pfDB["quests"]["deDE-tbc"] = {
  [1] = {
    ["D"] = "$Tpunk;!  Kill Kobold Vermin, 2 of em. NEW TEST AGAIN",
    ["O"] = "Tötet Koboldgezücht, und zwar 2 davon.",
    ["T"] = "Kanrethads Quest",
  },
  [2] = {
    ["D"] = "Der mächtige Hippogryph Scharfkralle wurde getötet und die Klaue der erschlagenen Bestie dient als Beweis für Euren Sieg.$B$BSenani Donnerherz in der Silberwindzuflucht wird zum Beweis Eurer Tat sicher gern diese Trophäe sehen wollen.",
    ["O"] = "Bringt Senani Donnerherz in der Silberwindzuflucht die Klaue von Scharfkralle.",
    ["T"] = "Klaue von Scharfkralle",
  },
  [5] = {
    ["D"] = "Ich verstecke mich jetzt schon seit Wochen in dieser Geisterstadt, und die ganze Zeit habe ich nichts gegessen außer Maden und Unkraut! Ich brauche was Anständiges zum Essen, und ich werde mich nicht lumpen lassen.$B$BBringt mir ein Festessen, und ich bezahle Euch gut dafür.$B$BIch habe gehört, dass Küchenchef Grual aus der Taverne \"Zum Roten Raben\" in Dunkelhain ganz ausgezeichnete schwärzliche Krebsküchlein macht...",
    ["O"] = "Sprecht mit Küchenchef Grual.",
    ["T"] = "Bibbers\' knurrender Magen",
  },
  [6] = {
    ["D"] = "Garrick Schleichfuß, ein Dieb und Halsabschneider, der schon seit Wochen unseren Bauern und Händlern das Leben schwer macht, ist bei einem Schuppen in der Nähe der Weinberge gesehen worden. Die Weinberge liegen im Osten der Abtei, auf der anderen Seite der Brücke. Bringt mir den Kopf des Schurken und verdient Euch das Kopfgeld, das auf ihn ausgesetzt ist!$B$BDoch seht Euch vor, $N. Garrick hat eine Bande von Rohlingen um sich versammelt, also werdet Ihr nicht leicht an ihn herankommen.",
    ["O"] = "Tötet Garrick Schleichfuß und bringt seinen Kopf zu Unteroffizier Willem in der Nähe der Abtei von Nordhain.",
    ["T"] = "Kopfgeld auf Garrick Schleichfuß",
  },
  [7] = {
    ["D"] = "Eure erste Aufgabe ist eine Säuberung, $N. Ein Koboldklan hat sich in den Wäldern im Norden eingenistet. Begebt Euch dorthin und bekämpft das Koboldgezücht, das Ihr dort finden werdet. Dezimiert es, damit wir die Kobolde eines Tages gänzlich aus Nordhain vertreiben können.",
    ["O"] = "Tötet 6 von dem Koboldgezücht und kehrt dann zu Marschall McBride zurück.",
    ["T"] = "Säuberung im Koboldlager",
  },
  [9] = {
    ["D"] = "Jetzt seht Euch nur an, was aus diesem Ort geworden ist! In dieser Gegend lebten einst ehrbare Bauersleute. Die verdammten Diebe haben sie alle vertrieben. Aber mich nicht! Jetzt sieht es allerdings so aus, als hätten einige Erntebehüter sich in den Feldern breitgemacht.$B$BWenn Ihr Euch dem gewachsen fühlt, dann geht hinaus und tötet dort draußen 20 von ihnen. Wenn Ihr fertig seid, kommt zurück und holt Euch Eure Bezahlung ab. Und wenn Ihr alle in meinem Feld erwischt habt, dann räumt auch in den angrenzenden Feldern auf.",
    ["O"] = "Bauer Saldean möchte, dass Ihr 20 Erntebehüter tötet.",
    ["T"] = "Tod auf den Feldern",
  },
  [10] = {
    ["D"] = "Oh, das ist einfach schrecklich! Ich habe meinen besten Gehilfen, Unterfeldmesser Kieselschliff, in die Wüste südöstlich von hier geschickt. Das ist ein Gebiet, das wir die Klaffende Schlucht nennen. Nun ja, er wird vermisst und in seinem letzten Bericht sprach er davon, dass es dort, wo er war, noch mehr dieser komischen Viecher gäbe. Könnte es sein, dass sie sich jetzt in zwei großen Gebieten der Wüste eingenistet haben?$B$BBegebt Euch bitte für mich zur Klaffenden Schlucht und sucht nach Kieselschliff! Das... oder findet wenigstens seine Feldmesserausrüstung. Ich fürchte, das ist alles, was von dem Armen übrig geblieben ist.",
    ["O"] = "Erforscht das Schicksal von Unterfeldmesser Kieselschliff und bringt entweder ihn oder seine Feldmesserausrüstung nach Gadgetzan zum Leitenden Feldmesser Rutenwünschels.",
    ["T"] = "Die Erlösung von Kieselschliff",
  },
  [11] = {
    ["D"] = "Gnolle, brutale Wesen, die in dieser Gegend nichts zu suchen haben, sind entlang der Grenzen des Waldes von Elwynn gesehen worden. Ein großes Rudel, viel zu viele, als dass wir allein mit ihnen fertigwerden könnten, hat sich in den Wäldern südlich von jenem Wachturm dort eingenistet. Eine weitere Gruppe hat sich im Gebiet nahe des Steinhügelsees im Osten eingenistet.$B$BDie Armee von Sturmwind wird jeden belobigen, der dabei hilft, die Gnolle zu töten. Bringt mir ihre bemalten Gnollarmbinden als Beweis für Eure Tat.",
    ["O"] = "Bringt Stellvertreter Rainer in der Kaserne 8 bemalte Gnollarmbinden.",
    ["T"] = "Kopfgeld auf die Flusspfotengnolle",
  },
  [12] = {
    ["D"] = "Die Volksmiliz hat nur ein einziges Ziel: Westfall zu verteidigen und den Frieden in dieses Land zurückzubringen. Doch leider muss der Frieden nur allzu oft mit Blut bezahlt werden. $B$BEiner meiner Späher hat uns Kunde davon gebracht, dass eine Fallenstellerbande der Defias hier in der Nähe verheerende Schäden anrichtet. Mir liegen Berichte vor, dass einige Fallensteller der Defias in der Nähe des Jangoschachts im Nordwesten gesichtet wurden, ebenso bei Molsens Hof und bei Brauenwirbels Kürbishof. Wenn Ihr unseren Reihen beizutreten wünscht, dann erschlagt 15 Fallensteller der Defias und 15 Schmuggler der Defias und kehrt zu mir zurück.",
    ["O"] = "Gryan Starkmantel möchte, dass Ihr 15 Fallensteller der Defias und 15 Schmuggler der Defias tötet und zu ihm auf die Späherkuppe zurückkehrt.",
    ["T"] = "Die Volksmiliz",
  },
  [13] = {
    ["D"] = "Eine Bande heimtückischer Plünderer der Defias ist dabei gesehen worden, wie sie den Goldküstensteinbruch, Mondbruch und Alexstons Bauernhof ausplünderten. Die Volksmiliz kann ein solches Verhalten nicht hinnehmen. Macht Euch unverzüglich auf den Weg, $N, und zeigt, dass das Licht in Westfall zugegen ist.$B$BDer Goldküstensteinbruch befindet sich in der Nähe der Küste, westlich vom Turm. Im nächsten Schritt Eurer Ausbildung müsst Ihr 15 dieser üblen Plünderer der Defias und 15 Ausplünderer der Defias töten.",
    ["O"] = "Gryan Starkmantel möchte, dass Ihr 15 Plünderer der Defias und 15 Ausplünderer der Defias tötet und zu ihm auf die Späherkuppe zurückkehrt.",
    ["T"] = "Die Volksmiliz",
  },
  [14] = {
    ["D"] = "Einige Defias konnten wir bislang nicht erwischen. Mein bester Späher berichtet, dass diese Defias in der ganzen Gegend auf Raubzug gehen und schon bis Süd-Westfall gekommen sind. Wir glauben, dass sie sich in den Dolchhügeln versteckt halten und dort ihren nächsten Schachzug planen. Schaltet diese Verbrecher im Namen der Volksmiliz aus.",
    ["O"] = "Gryan Starkmantel möchte, dass Ihr 15 Straßenräuber der Defias, 5 Pfadpirscher der Defias und 5 Knöchelhauer der Defias tötet und zu ihm auf die Späherkuppe zurückkehrt.",
    ["T"] = "Die Volksmiliz",
  },
  [15] = {
    ["D"] = "Tötet 8 Koboldtagelöhner und erstattet dann Marschall McBride Euren Bericht.",
    ["O"] = "Tötet 8 Koboldtagelöhner und erstattet dann Marschall McBride Euren Bericht.",
    ["T"] = "Überprüfung der Echokammmine",
  },
  [17] = {
    ["D"] = "Ich habe von einer neu entdeckten Pilzsorte gehört, den Magentafunguskappen. Sie wachsen in Kolonien bei der Uldaman-Grabungsstätte. Man findet sie häufig in den vorderen Tunnelabschnitten und auch tief in den unerforschten Regionen der Grabungsstätte. Ich möchte ihren potenziellen Nutzen für die Alchemie erforschen und dafür brauche ich Euch.$B$BFindet die Magentafunguskappen-Kolonien und bringt mir ein Dutzend Kappen. Aber seid gewarnt - die Pilze stoßen vielleicht Giftsporen aus, wenn man sie anstößt.$B$BWenn Ihr das für mich tut, dann braue ich Euch einen Schwung von meinem berühmten Regenerierungselixier!",
    ["O"] = "Bringt zwölf Magentafunguskappen nach Thelsamar zu Ghak Heilsegen.",
    ["T"] = "Reagenzsuche in Uldaman",
  },
  [18] = {
    ["D"] = "Seit Kurzem treibt sich eine neue Diebesbande in Nordhain herum. Sie nennen sich die Bruderschaft der Defias und sind auf der anderen Seite des Flusses im Osten gesehen worden.$B$BIch weiß nicht genau, was sie vorhaben, aber ich bin mir sicher, es ist nichts Gutes! Bringt mir die Kopftücher, die sie tragen, und ich werde Euch mit einer Waffe belohnen.",
    ["O"] = "Bringt 8 rote Sackleinenkopftücher zu Unteroffizier Willem in der Nähe der Abtei von Nordhain.",
    ["T"] = "Bruderschaft der Diebe",
  },
  [19] = {
    ["D"] = "Zwischen hier und Burg Steinwacht legen Kundschafter und Renegaten des Schwarzfels ständig Hinterhalte. Der Anführer der Kundschafter heißt Tharil\'zun und diesen Orc wollen wir haben. Bringt mir den Kopf von Tharil\'zun!",
    ["O"] = "Bringt den Kopf von Tharil\'zun ins Rotkammgebirge zu Marschall Marris.",
    ["T"] = "Tharil\'zun",
  },
  [20] = {
    ["D"] = "Die Orcs des Schwarzfelsklans haben die Brennende Steppe verlassen und Burg Steinwacht im Norden besetzt. Von dort aus ziehen sie marodierend durch das Gebiet nördlich von Seenhain, so dass dessen Bewohner sich nicht mehr aus der Stadt wagen und dort eingesperrt sind wie Vieh in einem zu engen Pferch.$B$BHelft uns, diese Bedrohung von Seenhain zu nehmen! Tötet diese Orcs des Schwarzfels und bringt mir 10 ihrer kampferprobten Äxte zum Beweis.$B$BUnd obwohl der Schwarzfelsklan uns vor allem aus dem Norden bedroht, haben wir auch gehört, dass sich noch weitere Schwarzfelsorcs in der südöstlichen Ecke des Rotkammgebirges aufhalten. Wenn Ihr dort welche findet, solltet Ihr die auch jagen.",
    ["O"] = "Bringt 10 kampferprobte Äxte nach Seenhain zu Marschall Marris.",
    ["T"] = "Bedrohung durch den Schwarzfelsklan",
  },
  [21] = {
    ["D"] = "Eure Untersuchungen beweisen, dass die Echokammmine gesäubert werden muss. Kehrt in die Mine zurück und helft dabei, sie von den Kobolden zu befreien.$B$BIhr dürft keine Zeit verlieren, $N. Je länger die Kobolde sich ungehindert in der Mine aufhalten können, umso festere Wurzeln fassen sie in Nordhain.",
    ["O"] = "Tötet 8 Koboldhilfsarbeiter und kehrt dann zu Marschall McBride in der Abtei von Nordhain zurück.",
    ["T"] = "Scharmützel in der Echokammmine",
  },
  [23] = {
    ["D"] = "Der mächtige Bär Ursangus ist nicht mehr!$B$BDas kleine halbmondförmige Emblem auf seiner Tatze bedeutet, dass das Tier von den Nachtelfen dazu abgerichtet wurde, den Wald zu durchstreifen und nach ihren Feinden zu durchsuchen. Niemand kann sagen, wie viele Eurer Hordebrüder und -schwestern diesem Geschöpf zum Opfer gefallen sind.$B$BIhr solltet die Tatze zu Tarkan in die Silberwindzuflucht bringen. Er wird sich freuen, zu erfahren, dass eine solche Waffe aus dem Arsenal des Feindes entfernt worden ist.",
    ["O"] = "Bringt Ursangus\' Tatze zu Hauptmann Tarkan in der Silberwindzuflucht.",
    ["T"] = "Ursangus\' Tatze",
  },
  [24] = {
    ["D"] = "Der einstmals so flinke Schattumbra liegt jetzt regungslos vor Euch und der Sieg ist Euer.$B$BDieses legendäre Geschöpf wird von den Nachtelfen verehrt. Sein Kopf dürfte eine beeindruckende Trophäe sein und als Zeichen für das Ende der Allianz im Eschental dienen.$B$BHauptmann Goggath in Höllschreis Wacht wird zweifellos hoch erfreut sein, den Kopf des erschlagenen Nachtsäblers entgegenzunehmen.",
    ["O"] = "Bringt Schattumbras Kopf zu Hauptmann Goggath in Höllschreis Wacht.",
    ["T"] = "Schattumbras Kopf",
  },
  [25] = {
    ["D"] = "Das hättet Ihr sehen sollen, $C! Die Elfen dachten, Silberwind wäre uneinnehmbar. Bei all ihren vielen Verteidigungsmaßnahmen und ihrer Armee von Wasserelementaren hat das auch beinahe gestimmt.$B$BBeinahe.$B$BEinem unserer Magier ist es gelungen, einen verderbten Elementar zu beschwören, der die anderen in wilde Raserei versetzte.$B$BAhh, Ihr hättet nur deren Gesichter sehen sollen!$B$B <Tarkan lacht.>$B$BLeider ist der Magier, der die Beschwörung durchgeführt hat, im Kampf gefallen. Jetzt benötige ich jemanden, der die Misere in Ordnung bringt.",
    ["O"] = "Hauptmann Tarkan in der Silberwindzuflucht möchte, dass Ihr Tideress sowie 12 beschmutzte Wasserelementare am Mystralsee erledigt.",
    ["T"] = "Und dann langsam abkühlen lassen",
  },
  [26] = {
    ["D"] = "$N, ich habe Euren Fortschritt mit großem Genuss verfolgt. Ihr entwickelt Euch definitiv zu einem wertvollen Beschützer der Natur.$B$BEs ist an der Zeit für Euch, mehr über einen der diversen Tieraspekte zu lernen, in die wir uns dank Cenarius\' Segen verwandeln können... einen, der Euch die Beherrschung des Wassers ermöglicht. Dazu müsst Ihr zur Mondlichtung in das Dorf Nachthafen reisen und mit Dendrite Sternenschauer vom Zirkel des Cenarius sprechen. Er wird Euch weiter anleiten.",
    ["O"] = "Sprecht mit Dendrite Sternenschauer im Dorf Nachthafen auf der Mondlichtung.",
    ["T"] = "Eine wertvolle Lektion",
  },
  [27] = {
    ["D"] = "$N, ich habe Euren Fortschritt mit großem Genuss verfolgt. Ihr entwickelt Euch definitiv zu einem wertvollen Beschützer der Natur.$B$BEs ist an der Zeit für Euch, mehr über einen der diversen Tieraspekte zu lernen, in die wir uns dank Cenarius\' Segen verwandeln können... einen, der Euch die Beherrschung des Wassers ermöglicht. Dazu müsst Ihr zur Mondlichtung in das Dorf Nachthafen reisen und mit Dendrite Sternenschauer vom Zirkel des Cenarius sprechen. Er wird Euch weiter anleiten.",
    ["O"] = "Sprecht mit Dendrite Sternenschauer im Dorf Nachthafen auf der Mondlichtung.",
    ["T"] = "Eine wertvolle Lektion",
  },
  [28] = {
    ["D"] = "Als Eure erste Prüfung müsst Ihr in den Tiefen des Sees von Elune\'ara nach einem Schreinschmuckstück suchen. Es zerfällt schnell, daher solltet Ihr Euch mit größter Eile zum Schrein von Remulos begeben. Benutzt das Schmuckstück am Schrein und sprecht dann dort mit Tajarri; sie ist eine der vertrauenswürdigsten Aufseherinnen der Mondlichtung.$B$BIhr habt nur eine gewisse Zeitspanne für diese Aufgabe zur Verfügung, und Ihr könntet in Konkurrenz zu anderen Druiden stehen, die ebenfalls nach Schmuckstücken suchen. Geistiges wie körperliches Geschick wird für Euren Erfolg unabdingbar sein.$B$BViel Glück, $N.",
    ["O"] = "Sucht ein Schreinschmuckstück im See von Elune\'ara und bringt es zum Schrein von Remulos im Nordwesten der Mondlichtung. Wenn Ihr dort seid, benutzt das Schreinschmuckstück.$B$BIhr müsst anschließend mit Tajarri am Schrein sprechen, um die Prüfung abzuschließen.",
    ["T"] = "Prüfung des Sees",
  },
  [29] = {
    ["D"] = "Als Eure erste Prüfung müsst Ihr in den Tiefen des Sees von Elune\'ara nach einem Schreinschmuckstück suchen. Es zerfällt schnell, daher solltet Ihr Euch mit größter Eile zum Schrein von Remulos begeben. Benutzt das Schmuckstück am Schrein und sprecht dann dort mit Tajarri; sie ist eine der vertrauenswürdigsten Aufseherinnen der Mondlichtung.$B$BIhr habt nur eine gewisse Zeitspanne für diese Aufgabe zur Verfügung, und Ihr könntet in Konkurrenz zu anderen Druiden stehen, die ebenfalls nach Schmuckstücken suchen. Geistiges wie körperliches Geschick wird für Euren Erfolg unabdingbar sein.$B$BViel Glück, $N.",
    ["O"] = "Sucht ein Schreinschmuckstück im See von Elune\'ara und bringt es zum Schrein von Remulos im Nordwesten der Mondlichtung. Wenn Ihr dort seid, benutzt das Schreinschmuckstück.$B$BIhr müsst anschließend mit Tajarri am Schrein sprechen, um die Prüfung abzuschließen.",
    ["T"] = "Prüfung des Sees",
  },
  [30] = {
    ["D"] = "Für die zweite Prüfung benötigt Ihr die zwei Hälften, aus denen der Anhänger des Seelöwen besteht. Die eine Hälfte bezieht ihre Kraft aus der Beweglichkeit des Seelöwen-Aspektes beim Gleiten durchs Wasser; die andere Hälfte bezieht ihre Kraft aus der unglaublichen Ausdauer des Seelöwen-Aspektes. Druiden bedienen sich dieser beiden Eigenschaften gleichermaßen, um den Aspekt ihrer Wassergestalt zu verkörpern.$B$BSprecht mit den Bewohnern der Mondlichtung, um herauszufinden, wo sich die Teile befinden könnten, und bringt beide hierher, um sie miteinander zu vereinen. Bringt den Anhänger zu Dendrite Sternenschauer, wenn Ihr ihn geschaffen habt.",
    ["O"] = "Sucht die Anhängerhälfte der Wasserbeweglichkeit und die Anhängerhälfte der Wasserbelastbarkeit. Sprecht mit den Einwohnern der Mondlichtung, um Hinweise darauf zu erhalten, wo sich diese Gegenstände befinden könnten.$B$BFormt den Anhänger des Seelöwen aus den beiden Anhängerhälften. Ihr müsst in der unmittelbaren Nähe des Schreins von Remulos sein, um dies zu tun.$B$BBringt den zusammengefügten Anhänger zu Dendrite Sternenschauer nach Nachthafen auf der Mondlichtung.",
    ["T"] = "Prüfung des Seelöwen",
  },
  [31] = {
    ["D"] = "Ihr habt die notwendigen Lektionen gemeistert, die alle jungen Druiden lernen, bevor sie einen Wasseraspekt annehmen können. Kehrt jetzt zu Eurem Lehrer Turak Runentotem nach Donnerfels zurück. Zeigt ihm Euren Anhänger und beweist ihm damit, dass Ihr bereit seid zu lernen, was er Euch lehren kann. Er wird Eure Ausbildung abschließen und Euch ermöglichen, eins mit dem Wasser zu werden.$B$BAuf Wiedersehen. Wir werden uns wieder sprechen.",
    ["O"] = "Kehrt nach Donnerfels zurück und zeigt Turak Runentotem den Anhänger des Seelöwen.",
    ["T"] = "Wassergestalt (Taurendruide)",
  },
  [32] = {
    ["D"] = "Hier ist Euer Bericht. Ihr solltet damit zu jemandem gehen, der ausreichend Kenntnis von solchen Dingen hat.$B$BIch kenne da einen Trollgelehrten mit Namen Zilzibin Tomtom. Er hat viel Zeit mit dem Studium der Lebensformen verbracht, die in der Ökologie von Azeroth einzigartig sind, und er ist noch dazu Historiker! Wenn Euch jemand helfen soll herauszufinden, was genau diese Bedrohung ausmacht, und wie sie zu stoppen ist, gebt ihm diesen Bericht. Hilfe können wir gar nicht genug haben.$B$BZil lebt in einem Haus auf der zweiten Ebene der Gasse in Orgrimmar.",
    ["O"] = "Bringt den Bericht nach Orgrimmar zu Zilzibin Tomtom.",
    ["T"] = "Aufstieg der Silithiden",
  },
  [33] = {
    ["D"] = "Bringt Eagan Pelzschneider, den Ihr vor der Abtei von Nordhain findet, 8 Pelze von erkrankten Wölfen.",
    ["O"] = "Bringt Eagan Pelzschneider, den Ihr vor der Abtei von Nordhain findet, 8 Pelze von erkrankten Wölfen.",
    ["T"] = "Wölfe an der Grenze",
  },
  [34] = {
    ["D"] = "Mein Garten ist schon wieder zertrampelt und verwüstet worden. Und ich weiß genau, was für ein Tier das getan hat. Das war dieser elende Eber, der, den Marschall Marris seit einer Weile immer Stopfwanst nennt.$B$BJetzt kann ich die Narzissen erst nächsten Herbst wieder anpflanzen. Hin! Alle hin, und das nur wegen dieses einen gefährlichen Ebers. Bereitet dieser Plage ein Ende! Zeigt mir seinen Hauer und ich werde Euch nur zu gern belohnen. Die meiste Zeit scheint er damit zu verbringen, in den Feldern südwestlich von Seenhain nach Futter zu suchen, doch gelegentlich verirrt er sich auch hierher und in meinen Garten.",
    ["O"] = "Martie Jainrose aus Seenhain möchte, dass Ihr Stopfwanst tötet. Bringt ihr den Hauer des Ebers zum Beweis.",
    ["T"] = "Ein ungebetener Gast",
  },
  [35] = {
    ["D"] = "Falls Ihr befürchtet, dass die Gerüchte über das Auftauchen von Murlocs wahr sein könnten, dann tut das Folgende: Begebt Euch zur östlichen Brücke von Elwynn und sprecht mit Wache Thomas. Er ist seit einer Woche an der Brücke stationiert und wird deswegen wissen, wie es in der Gegend aussieht.$B$BIch habe hier ein Streitross aus Sturmwind, das Euch zur Wache Thomas tragen wird. Lasst es mich wissen, wenn Ihr das Pferd benutzen wollt.",
    ["O"] = "Marschall Dughan möchte, dass Ihr mit Wache Thomas sprecht.",
    ["T"] = "Weitere Sorgen",
  },
  [36] = {
    ["D"] = "Ich hätte nie gedacht, dass jemals der Tag kommen würde, an dem ich den Hof verlasse. Aber in den Feldern wimmelt es nur so von Dieben und es ist hier für uns viel zu gefährlich geworden. Sobald Bauer Brauenwirbel den Wagen repariert hat, machen wir uns auf den Weg.$B$BKönntet Ihr mir vielleicht einen Gefallen tun? Ich schreibe Euch schnell mein Rezept für Westfalleintopf auf. Seid so nett und bringt es Salma Saldean; sie wohnt auf dem Hof dort hinten. Ihr findet den Hof der Saldeans gleich hinter der Gabelung der Straße.",
    ["O"] = "Verna Brauenwirbel möchte, dass Ihr ihr Rezept für Westfalleintopf bei Salma Saldean abliefert.",
    ["T"] = "Westfalleintopf",
  },
  [37] = {
    ["D"] = "Vor ein paar Tagen haben wir zwei Wachen, Rolf und Malakai, losgeschickt, damit sie entlang des Flusses Nachforschungen anstellen, aber bisher sind sie noch nicht zurückgekehrt. Damit ich meinen Bericht fertig schreiben kann, muss ich wissen, was den beiden Männern zugestoßen ist.$B$BSucht den Fluss in Richtung Norden ab und seht zu, dass Ihr die Wachen findet... oder ihre Überreste.",
    ["O"] = "Wache Thomas möchte, dass Ihr den Fluss in Richtung Norden nach Rolf und Malakai, den beiden vermissten Wachen, absucht.",
    ["T"] = "Suche nach den vermissten Wachen",
  },
  [38] = {
    ["D"] = "Wollt Ihr mir dabei helfen, Westfalleintopf zu kochen? Dann bringt mir die folgenden Zutaten: $B $B 3 Stücke sehniges Geierfleisch $B 3 Geiferzahnschnauzen $B 3 Murlocaugen $B 3 Okraschoten",
    ["O"] = "Salma Saldean möchte 3 Stücke sehniges Geierfleisch, 3 Geiferzahnschnauzen, 3 Murlocaugen und 3 Okraschoten.",
    ["T"] = "Westfalleintopf",
  },
  [39] = {
    ["D"] = "Berichtet Marschall Dughan, dass Malakai und Rolf ums Leben gekommen sind, und teilt ihm auch mit, dass wir die Murlocs in Ost-Elwynn mit unserer derzeitigen Truppenstärke hier nicht unter Kontrolle halten können.$B$BIch weiß, dass wir nicht viele überzählige Männer haben, aber vielleicht findet Dughan ja doch jemanden.",
    ["O"] = "Erstattet Marschall Dughan in Goldhain Bericht.",
    ["T"] = "Zustellung von Thomas\' Bericht",
  },
  [40] = {
    ["D"] = "Eine neue Gefahr bedroht den Wald von Elwynn, $N! Murlocs kommen die Bäche und Flüsse im östlichen Elwynn hinaufgeschwommen, vertreiben die Fische und greifen einfache Leute an!$B$BIch habe Marschall Dughan bereits gewarnt, aber der macht sich größere Sorgen wegen der Gnolle und der Banditen. Er ist nicht überzeugt davon, dass die Murlocs eine Gefahr darstellen.$B$BBitte sprecht mit Dughan, $N, und bringt ihn dazu, dass er Verstärkung in den Osten schickt!",
    ["O"] = "Remy \"Zweifach\" möchte, dass Ihr in Goldhain mit Marschall Dughan sprecht.",
    ["T"] = "Gefahr aus dem Wasser",
  },
  [46] = {
    ["D"] = "Die Armee von Sturmwind hat ein Kopfgeld auf die Murloclauerer und Murlocfutterwühler ausgesetzt, die sich in Elwynn herumtreiben. Erlegt sie und bringt Wache Thomas ihre zerrissenen Murlocflossen, dann wird die Armee von Sturmwind Euch ansehnlich belohnen.$B$BDie Murlocs haben am Steinhügelsee nördlich von hier ein Dorf errichtet und ein weiteres ihrer Dörfer befindet sich im Süden, wo der Fluss sich teilt.",
    ["O"] = "Bringt 8 zerrissene Murlocflossen zu Wache Thomas, den Ihr an der östlichen Brücke von Elwynn findet.",
    ["T"] = "Kopfgeld auf die Murlocs",
  },
  [48] = {
    ["D"] = "Weit im Süden, wo die Lianen sich ranken$BLiegt versteckt ein Brunnen, in dem fast versanken$BDie wertvollen Wasser, so rein und so helle - $BBringt mir Wasser von der Heiligen Quelle.",
    ["O"] = "Bringt Schnapsbart Donnerbräu Wasser von der Heiligen Quelle.",
    ["T"] = "Goldbrauner Samt",
  },
  [49] = {
    ["D"] = "Lang schon vermiss ich die Lieb meines Lebens$BGoldbraun und samtig, doch noch wart ich vergebens$BBringt mir, dann vergeht jeder Zorn$BJe einen Sack Gerste, Roggen und Korn.",
    ["O"] = "Schnapsbart Donnerbräu möchte einen Sack Gerste, einen Sack Roggen und einen Sack Korn haben.",
    ["T"] = "Goldbrauner Samt",
  },
  [50] = {
    ["D"] = "Die Maische anrühren, das ist mein Wille$BDoch fehlt mir dann auch noch eine Destille$BFür diese brauch ich ein starkes Metall$BBringt mir Echtsilber, das ist gut in dem Fall!",
    ["O"] = "Schnapsbart Donnerbräu braucht Echtsilber.",
    ["T"] = "Goldbrauner Samt",
  },
  [51] = {
    ["D"] = "Noch ist er nicht fertig, doch schon frisch gebrannt$BJetzt wird als Nächstes der Meiler bemannt$BUm den Whisky zu filtern. Drum bringt mir den Ast$BEines Bergahorns - wenn Ihr könnt, ohne Rast!",
    ["O"] = "Bringt Schnapsbart Donnerbräu den Ast eines Bergahorns.",
    ["T"] = "Goldbrauner Samt",
  },
  [52] = {
    ["D"] = "Seid gegrüßt, $N. Je weiter wir uns von Goldhain entfernen, umso reizbarer sind die wilden Tiere. Und das Holzfällerlager des Osttals wird beinahe ständig von Wölfen und Bären angegriffen.$B$BWir könnten hier draußen wirklich Eure Hilfe gebrauchen.",
    ["O"] = "Tötet 8 Streuner oder graue Forstwölfe und 5 junge Waldbären und kehrt dann zu Wache Thomas an der östlichen Brücke von Elwynn zurück.",
    ["T"] = "Grenzschutz",
  },
  [53] = {
    ["D"] = "Das Getränk, das ich braue, das reine und klare$BWird goldbraun und samtig im Laufe der Jahre$BDoch ehe er fertig, der Whisky, der weiche$BBraucht\'s noch ein Fass aus verkohlter Eiche.",
    ["O"] = "Bringt Schnapsbart Donnerbräu ein verkohltes Eichenbündel.",
    ["T"] = "Goldbrauner Samt",
  },
  [54] = {
    ["D"] = "$N, Ihr habt bewiesen, dass Ihr Euch um die Sicherheit von Nordhain sorgt. Nun erhaltet Ihr die Aufgabe, Euch auch dem Schutz des umliegenden Waldes von Elwynn zu widmen.$B$BWenn Ihr Euch dieser Aufgabe stellen wollt, dann nehmt diese Papiere, die bei Marschall Dughan in Goldhain abgeliefert werden müssen. Goldhain liegt an der südlichen Straße, hinter den Grenztoren.",
    ["O"] = "Liefert Marschall McBrides Unterlagen bei Marschall Dughan im Wald von Elwynn ab.",
    ["T"] = "Meldung in Goldhain",
  },
  [55] = {
    ["D"] = "Morbent Teufel hält sich in seinem Unterschlupf verborgen, dem Haus, das hoch oben auf dem Hügel im Osten über dem Friedhof von Rabenflucht thront. Doch seine Zeit in diesem Land neigt sich dem Ende entgegen...$B$BSetzt Morbents Bann gegen ihn ein. Das wird seine schützenden Zauber machtlos machen.$B$BErschlagt ihn. Erschlagt ihn und errettet uns von seiner Verderbtheit. Werdet zum Instrument meiner Rache und damit $Gzum Helden:zur Heldin; des Dämmerwalds!",
    ["O"] = "Verwendet Morbents Bann gegen Morbent Teufel, tötet Morbent Teufel und bringt Morbents Bann anschließend zu Sven in dessen Lager zurück.",
    ["T"] = "Morbent Teufel",
  },
  [56] = {
    ["D"] = "Als Kommandantin der Nachtwache habe ich geschworen, die Bürger von Dunkelhain zu beschützen. Sturmwind hat alle offiziell ernannten Wachen von ihren Posten hier abgezogen; damit sind wir uns selbst überlassen.$B$BWenn Ihr glaubt, der Aufgabe gewachsen zu sein, könnten wir Eure Hilfe wirklich gebrauchen! Helft der Nachtwache und wagt Euch in den Friedhof Stille Gärten, der ein Stückchen südlich von Dunkelhain liegt. Tötet dort 8 Skelettkrieger und 6 Skelettmagier. Der Bedrohung durch die Untoten muss ein Ende gemacht werden!",
    ["O"] = "Kommandantin Althea Schwarzhaupt aus Dunkelhain möchte, dass Ihr 8 Skelettkrieger und 6 Skelettmagier tötet.",
    ["T"] = "Die Nachtwache",
  },
  [57] = {
    ["D"] = "Ich habe berichtet bekommen, dass im Wald eine wahre Untotenplage ausgebrochen ist, $N. Wenn Ihr Euch der Aufgabe gewachsen fühlt, dann macht Euch auf den Weg zum Friedhof von Rabenflucht und erschlagt 15 dieser abscheulichen Skelettscheusale und 15 Skelettschrecken. Außerdem sollen im Friedhof Stille Gärten, der sich ein Stückchen südlich von Dunkelhain befindet, Skelette gesehen worden sein. Die Untoten müssen aus dem Dämmerwald vertrieben werden!",
    ["O"] = "Kommandantin Althea Schwarzhaupt aus Dunkelhain möchte, dass Ihr 15 Skelettscheusale und 15 Skelettschrecken tötet.",
    ["T"] = "Die Nachtwache",
  },
  [58] = {
    ["D"] = "Die Lage in Rabenflucht ist noch immer sehr bedenklich. Die Sicherheit von Dunkelhain liegt in Euren Händen, $N. Ich habe einfach nicht genug Behüter, um die Sicherheit in der Stadt zu gewährleisten. Deswegen müsst Ihr Euch erneut nach Rabenflucht aufmachen und das östliche Mausoleum von den Seuchenverbreitern säubern, die sich dort breitgemacht haben. Tötet 20 von ihnen!",
    ["O"] = "Kommandantin Althea Schwarzhaupt aus Dunkelhain möchte, dass Ihr im östlichen Mausoleum von Rabenflucht 20 Seuchenverbreiter tötet.",
    ["T"] = "Die Nachtwache",
  },
  [59] = {
    ["D"] = "Als Dank für Eure Gewitztheit und Euren Mut gebe ich Euch hier einen Gutschein, den Ihr gegen ein Rüstungsteil eintauschen könnt. Bringt den Gutschein ins Holzfällerlager des Osttals zu Sara Roderwald. Wenn Ihr ihr den Gutschein gebt, dann stellt sie die Rüstung für Euch her. Und wenn Ihr sie habt, tragt sie zur Verteidigung von Elwynn, $N.$B$BIhr findet das Holzfällerlager des Osttals östlich dieses Postens jenseits der Brücke.",
    ["O"] = "Gebt Sara Roderwald den Rüstungsgutschein der Armee von Sturmwind.",
    ["T"] = "Rüstung aus Tuch und aus Leder",
  },
  [60] = {
    ["D"] = "Guten Tag, $Gwerter Herr:werte Dame;! Hättet Ihr einen Moment Zeit für mich?$B$BMein Bruder und ich führen in Sturmwind eine Apotheke und ich bin hier, weil ich große Kerzen suche, ihres Wachses wegen. Könnt Ihr mir vielleicht helfen?$B$BGroße Kerzen könnt Ihr von Kobolden bekommen - und ich habe Gerüchte gehört, dass Kobolde sich in den Minen von Elwynn eingenistet haben: in der Tiefenschachtmine im Süden und in der Jaspismine im Osten. Ich schlage vor, dass Ihr Euch an einem dieser Orte auf die Suche nach Kerzen begebt.",
    ["O"] = "Bringt 8 große Kerzen zu William Pestle in Goldhain.",
    ["T"] = "Koboldkerzen",
  },
  [61] = {
    ["D"] = "In Sturmwind wartet mein Bruder Morgan auf die Lieferung der Kerzen. Ich habe selbst nicht die Zeit, um mich auf die Reise zu begeben, aber wenn Ihr ihm die Lieferung überbringen wollt, so wird er Euch gut bezahlen.$B$BIch habe die Kerzen schon fertig verpackt. Ihr findet Morgan in unserem Geschäft im Handelsdistrikt von Sturmwind. Es heißt Pestles Apotheke.",
    ["O"] = "Bringt Williams Lieferung in den Handelsdistrikt von Sturmwind zu Morgan Pestle.",
    ["T"] = "Lieferung nach Sturmwind",
  },
  [62] = {
    ["D"] = "Die Mine in Nordhain ist nicht die einzige, mit der es Probleme gibt. Berichten zufolge ist auch die Tiefenschachtmine im Wald von Elwynn zu einem Tummelplatz für Kobolde geworden.$B$BErforscht die Mine, bestätigt diese Berichte und kehrt dann zu mir zurück. Die Mine liegt fast genau südlich von Goldhain, zwischen den Höfen der Steinfelds und der Maclures.",
    ["O"] = "Erforscht die Tiefenschachtmine und kehrt dann nach Goldhain zu Marschall Dughan zurück.",
    ["T"] = "Die Tiefenschachtmine",
  },
  [63] = {
    ["D"] = "An der Küste des Silberwalds befindet sich westlich des Grabmals ein Schrein, der von der Seuche besudelt ist. Die Geister dort sprechen nicht länger zu unserem Volk und es ist jetzt an Euch, die Manifestation dort zu besiegen und den Teich zu säubern.$B$BNehmt die Armschienen des Elementars und gebt sie zusammen mit den verbleibenden Tropfen von Brines Trank in die Kohlenpfanne. Das sollte ausreichen, um einen reinen Geist zu beschwören, mit dem Ihr kommunizieren könnt.$B$BBringt mir einen Beweis dafür, dass Ihr die Tat vollbracht habt, dann belohne ich Euch mit Eurem Totem.",
    ["O"] = "Besiegt die verderbte Wassermanifestation und legt die Armschienen der verderbten Manifestation und die verbleibenden Tropfen reinsten Wassers im Silberwald auf die Kohlenpfanne von Immerquell.",
    ["T"] = "Ruf des Wassers",
  },
  [64] = {
    ["D"] = "Es war fürchterlich! Verna hat mich aufgeweckt, als sie draußen auf dem Feld so einen Radau gehört hat. Das ganze Feld war voller Rowdys! Wir sind in solcher Eile aufgebrochen und da hab ich vergessen, meine Taschenuhr einzupacken. Die Uhr hat mir Vernas Vater an unserem Hochzeitstag geschenkt und es wird mir fast schlecht, wenn ich dran denke, dass diese Diebe sie jetzt haben. Ich hab die Taschenuhr im Kleiderschrank in unserem Haus liegen lassen. Haltet nach dem Kürbisfeld im Westen Ausschau, dann kommt Ihr zu unserem Hof - Ihr könnt ihn gar nicht verfehlen. Wenn Ihr mir meine Uhr wiederbringen würdet, wäre ich Euch sehr dankbar.",
    ["O"] = "Bauer Brauenwirbel möchte, dass Ihr seine Taschenuhr holt. Sie befindet sich im Kleiderschrank des Bauernhauses auf der Kürbisfarm im Westen.",
    ["T"] = "Das vergessene Erbstück",
  },
  [65] = {
    ["D"] = "Die Bruderschaft der Defias nennt sich die Bande von Schuften, von der die braven Leute von Westfall aus dem Land vertrieben werden. Ihr müsst für mich diese Bande bespitzeln. Wir müssen erfahren, wer der Kopf der Gruppe ist und wo sie ihr Versteck hat. In Seenhain findet Ihr im Gasthaus einen Schurken namens Wiley, der mir noch einen Gefallen schuldet.$B$BBegebt Euch nach Seenhain im Rotkammgebirge, östlich von Elwynn, und findet heraus, was Ihr könnt.",
    ["O"] = "Gryan Starkmantel möchte, dass Ihr in Seenhain mit Wiley sprecht.",
    ["T"] = "Die Bruderschaft der Defias",
  },
  [66] = {
    ["D"] = "Letzte Nacht verspürte ich in meinem Geist eine schreckliche Erschütterung. Ich wusste, dass sich meine Enkelin Alyssa in großer Gefahr befand. Ich befragte die Karten und aus ihnen starrte mich die Fratze des Todes höhnisch an.$B$BIch begab mich auf eine lange Reise durch eine dunkle Trance und schließlich gelang es mir, einen Hinweis auf dieses schreckliche Geheimnis zu erhaschen. Ein Name erschloss sich mir, der Name Stalvan.$B$BSucht den Amtmann im Rathaus auf und schaut, ob Ihr nicht mehr über diese Person herausfinden könnt. Ich fürchte um unser aller Leben.",
    ["O"] = "Sucht Amtmann Daltry im Rathaus von Dunkelhain auf.",
    ["T"] = "Die Legende von Stalvan",
  },
  [67] = {
    ["D"] = "Stalvan, hm? Lasst mich im Stadtverzeichnis nachsehen.$B$BStalvan... Stalvan... mal sehen. Ah, hier ist es! Ich habe hier einen Eintrag über einen gewissen Stalvan Dunstmantel. Die letzte bekannte Adresse ist die Schule in Mondbruch. Liebe Güte, das nenne ich veraltet!$B$BWürdet Ihr mir einen Gefallen tun? Falls Ihr zufällig nach Mondbruch kommt, dann lasst es mich wissen, wenn Ihr an neue Informationen über diese Person geraten solltet. Ich habe meine Unterlagen gern auf dem neuesten Stand.",
    ["O"] = "Begebt Euch zur Schule in Mondbruch und bringt Amtmann Daltry alle neuen Informationen über Stalvan Dunstmantel.",
    ["T"] = "Die Legende von Stalvan",
  },
  [69] = {
    ["D"] = "Ach du liebes bisschen! Das ist mir beim ersten Mal ja glatt entgangen! Hier im Verzeichnis steht beim Eintrag für diesen Stalvan direkt unter der ersten Adresse noch eine weitere, die zum Teil durchgestrichen ist. Es scheint, als wäre er zum Gasthaus \"Zur Höhle des Löwen\" drüben in Goldhain unterwegs gewesen. Vielleicht solltet Ihr dort nachfragen, $N.",
    ["O"] = "Begebt Euch zum Gasthaus \"Zur Höhle des Löwen\" und fragt dort nach, ob jemand schon einmal den Namen Stalvan gehört hat.",
    ["T"] = "Die Legende von Stalvan",
  },
  [70] = {
    ["D"] = "Den Namen Stalvan hab ich schon mal gehört... Ach, jetzt fällt\'s mir wieder ein.$B$BVor vielen Jahren betrat an einem stürmischen Abend ein Bote dieses Haus, der Unterkunft für die Nacht suchte. Es war noch nicht ganz Mitternacht, da kam der Mann schreiend die Treppe heruntergerannt. Er war bleich wie der Tod vor Angst. Nur mit seinem Nachtgewand bekleidet verschwand er im Regen.$B$BIn der Eile vergaß er seine Briefe in der Truhe oben und kam nie zurück, um sie abzuholen. Es ist ein Brief dabei von diesem Stalvan, den Ihr erwähntet, adressiert an den Kanaldistrikt in Sturmwind. Ihr dürft ihn gern haben.",
    ["O"] = "Holt Stalvans niemals zugestellten Brief aus der Truhe und liefert ihn im Kanaldistrikt von Sturmwind ab.",
    ["T"] = "Die Legende von Stalvan",
  },
  [71] = {
    ["D"] = "Nun habt Ihr beide Medaillons. Bringt sie an die Brücke zu Wache Thomas, damit er erfährt, welches Schicksal seine beiden Kameraden erlitten haben.",
    ["O"] = "Bringt die Medaillons von Rolf und Malakai zu Wache Thomas an die östliche Brücke von Elwynn.",
    ["T"] = "Meldung an Thomas",
  },
  [72] = {
    ["D"] = "Mein Vater war der Verwalter des Besitzes, lange bevor ich es wurde. Nach dem Massaker war er derjenige, der das Blut aufwischen und aufräumen musste. Aber das tut nichts zur Sache.$B$BDie letzten Gelder aus dem Flintridge-Fonds sind versiegt und jetzt müssen die letzten Stücke aus dem Familienbesitz versteigert werden. Daran sind nur diese Steuereintreiber schuld. Aber wenn Ihr wirklich dringend mehr erfahren wollt, dann dürft Ihr diesen Kram hier gern durchsehen. Wer weiß, was Ihr findet.",
    ["O"] = "Durchsucht die verpackten Besitztümer nach Hinweisen.",
    ["T"] = "Die Legende von Stalvan",
  },
  [73] = "_",
  [75] = {
    ["D"] = "Als ich hier eingezogen bin, stand oben schon eine Truhe mit Pergamenten darin. Kurz nach meinem Einzug habe ich sie mir einmal angesehen, aber bald darauf konnte ich nur noch verschwommen sehen und da hab ich\'s dann gelassen. $B$BTut einem alten, halb blinden Mann einen Gefallen und seht Euch selbst mal in der Kiste oben um, ob Ihr vielleicht etwas findet, das Euch bei Eurer Suche nach diesem Stalvan weiterhelfen kann. Ich bin mir ziemlich sicher, dass da eine verblasste Tagebuchseite dabei ist, die Euch interessieren müsste. Bringt sie mir und ich werde sehen, was ich tun kann, um Euch zu helfen.",
    ["O"] = "Marschall Haggard möchte, dass Ihr in der Kiste oben nach der verblassten Tagebuchseite sucht.",
    ["T"] = "Die Legende von Stalvan",
  },
  [76] = {
    ["D"] = "Dank Euch wissen wir jetzt, dass Kobolde sich in der Tiefenschachtmine eingenistet haben. Jetzt brauchen wir noch einen Späher, der die weiter entfernte Jaspismine untersucht.$B$BErforscht die Jaspismine und bestätigt mir, ob sich dort tatsächlich Kobolde aufhalten. Die Mine erreicht Ihr, indem Ihr der Straße nach Osten folgt, bis Ihr zum Turm von Azora kommt. Wenn Ihr Euch am Turm nach Norden wendet, dann findet Ihr die Mine im Vorgebirge.",
    ["O"] = "Erforscht die Jaspismine und kehrt dann nach Goldhain zu Marschall Dughan zurück, um ihm Bericht zu erstatten.",
    ["T"] = "Die Jaspismine",
  },
  [77] = {
    ["D"] = "Ich besitze Originalverpackungsmaterialien für den Wein, den Dran Droffers will. Ich werde Euch dabei helfen, den Wein für Maltons ungehobelten Vater zu verpacken... für einen Unkostenbeitrag in Höhe zweier weiterer Flaschen besagten Weins. Sie befinden sich in Kisten, die Ihr in dem verlassenen Stützpunkt der Horde in der Nähe vom Lauerfels findet.$B$BDas heißt, dass es eine anstrengende Aufgabe wird. Ihr werdet bei den Ruinen stinkende Schleime und Schlamme vorfinden; angeblich werden sie von einer Art Oberschlamm geführt. Beschafft dessen ungeachtet den Wein und kehrt hierher zu mir zurück, um ihn zu verpacken.",
    ["O"] = "Holt 10 Flaschen Hinterländer Honigwein aus den Kisten, die sich rund um den verlassenen Stützpunkt der Horde am Lauerfels befinden.",
    ["T"] = "Eine schleimige Angelegenheit",
  },
  [78] = {
    ["D"] = "Ich kenne jemanden, der Euch vielleicht helfen könnte. Damals, als ich noch die Wachen von Sturmwind kommandierte, haben wir immer in der Taverne \"Zum Roten Raben\" in Dunkelhain gesessen und etwas getrunken. Smitts, der Gastwirt dort, kannte sich mit den örtlichen Lehren ziemlich gut aus. Zeigt ihm diese Seite, mal sehen, was er dazu zu sagen hat.",
    ["O"] = "Bringt die verblasste Tagebuchseite zu Schankwart Smitts nach Dunkelhain im Dämmerwald.",
    ["T"] = "Die Legende von Stalvan",
  },
  [79] = {
    ["D"] = "Ich habe die Geschichten um diesen Stalvan jahrelang verfolgt. Als vor ein paar Jahren diese Adligen, die hier zu Besuch waren, ermordet wurden, hab ich Haggard bei seinen Nachforschungen begleitet. Ich habe damals diese schlammverschmierten Seiten gefunden, aber es ist uns nie gelungen, die Handschrift mit diesem Verrückten in den Wäldern in Verbindung zu bringen. Eure Spur der Beweise zeigt aber, dass er schuldig ist. Bringt dies sofort zu Kommandant Schwarzhaupt und sagt ihr, was Ihr entdeckt habt!",
    ["O"] = "Bringt Kommandant Schwarzhaupt die schlammverschmierte Seite.",
    ["T"] = "Die Legende von Stalvan",
  },
  [80] = {
    ["D"] = "Ich beobachte diesen Widerling Stalvan jetzt schon eine ganze Weile. Aber wenn diese Seite tatsächlich von seiner Hand geschrieben wurde, dann beweist das seine Schuld ohne den letzten Zweifel.$B$BSo lange, wie Amtmann Daltry das Stadtverzeichnis jetzt schon verwaltet, ist er ein wahrer Experte darin geworden, Handschriften zu identifizieren. Zeigt ihm diese Seite und stellt fest, ob die Schrift dieselbe ist wie diejenige der Unterschrift im Verzeichnis.",
    ["O"] = "Zeigt Amtmann Daltry die blutbefleckte Tagebuchseite.",
    ["T"] = "Die Legende von Stalvan",
  },
  [81] = {
    ["D"] = "Ich habe zusätzliche Vorsichtsmaßnahmen getroffen und nicht nur Drans verpackten Wein in dem Fass sicher untergebracht, sondern auch die zusätzlichen Weinflaschen, die er klaut. Auch wenn ich nicht viel von Dran halte, so gibt es doch nichts Schlimmeres als ein durchgeschütteltes Weinbouquet. Bitte, bitte, keine Ursache.$B$BBringt jetzt Euer Fass zu Dran Droffers in seinem Kramladen in Orgrimmar. Entbietet meine besten Grüße bitte auch Malton und lasst ihn wissen, dass uns sein Besuch eine große Ehre wäre, falls es ihn jemals nach einem feinen Getränk und noch feineren Trinkkumpanen gelüstet.",
    ["O"] = "Liefert das Fass nach Orgrimmar an Dran Droffers.",
    ["T"] = "Weinlieferung",
  },
  [82] = {
    ["D"] = "Tja, diese Berichte über die Käfer... wir wissen, dass sie die Teiche trocken legen, die sich dort befanden, wo sich der Giftige Unterschlupf befindet. Was wir NICHT wissen, ist, na ja, alles andere.$B$BWir müssen als Erstes herausfinden, wer unser Feind ist! Begebt Euch bitte in den Giftigen Unterschlupf und besorgt ein paar Stücke dieser Tierwesen. Holt fünf von diesen Teilen von den dortigen Centipaar und bringt sie dem Alchemisten Stößelbruch zur Untersuchung. Wenn er sich damit beschäftigt, werden wir schon herausfinden, worum es sich handelt!",
    ["O"] = "Bringt fünf Centipaarinsektenteile vom Giftigen Unterschlupf nach Gadgetzan zum Alchemisten Stößelbruch.",
    ["T"] = "Erkundung des Giftigen Unterschlupfes",
  },
  [83] = {
    ["D"] = "Die Schläger in Elwynn tragen Leinenstoff, aus dem ich feine Leinenwaren herstellen kann.$B$BBringt mir Leinenfetzen und ich mache euch daraus etwas Nützliches.$B$BIhr findet diese gesetzlosen Verbrecher in kleinen Lagern überall in Elwynn.",
    ["O"] = "Bringt 6 rote Leinenkopftücher ins Holzfällerlager des Osttals zu Sara Roderwald.",
    ["T"] = "Feine Leinenwaren",
  },
  [86] = {
    ["D"] = "Wenn ich \'ne Pastete kriegen würde, dann könnte ich Euch vielleicht sagen, wer die Kette hat. Ach, und wisst Ihr was? Diese alte Dame Bernice auf dem anderen Hof da macht ganz tolle Schweinebauchpasteten. $B$BWenn Ihr ihr ein bisschen zartes Eberfleisch von den Ebern bringt, die immer um unsere Höfe herumstromern, und ihr sagt, dass es für einen guten Zweck ist, dann backt sie Euch vielleicht eine Pastete.",
    ["O"] = "Bringt 4 Portionen zartes Eberfleisch zu Tantchen Bernice Steinfeld auf dem Hof der Steinfelds.",
    ["T"] = "Pastete für Billy",
  },
  [87] = {
    ["D"] = "Ich hab in der Nähe von der Tiefenschachtmine gespielt und da hab ich, glaub ich, die Kette von der alten Dame fallenge... äääh, gesehen. Fragt mich nicht, wie die dahin gekommen ist, ich war\'s nicht!$B$BNa egal, jedenfalls hab ich diesen großen Kobold gesehen, der hatte einen Goldzahn und der hat die Kette aufgehoben und ist zurück in seinen Bau über der Mine gerannt. Wenn Ihr diesen Kobold findet, dann findet Ihr auch die Halskette, ehrlich! Ich schwör\'s!",
    ["O"] = "Bringt Berenices Halskette zu \"Tantchen\" Berenice Steinfeld auf dem Hof der Steinfelds.",
    ["T"] = "Goldzahn",
  },
  [88] = {
    ["D"] = "Die Brackbrunns haben eine preisgekrönte Sau namens Prinzessin. Dass dieses Tier so riesig geworden ist, das kommt nur daher, dass es immer hier herübergeschlichen kommt und mein Gemüse frisst!$B$BZufällig ist die Sau gerade hier. Sie muss sterben! Bringt mir ihr Halsband zum Beweis und ich werde Euch für Eure Mühen entlohnen.$B$BIhr findet Prinzessin mitten in unserem Wassermelonenbeet. Schnappt sie Euch!",
    ["O"] = "Tötet Prinzessin, nehmt ihr Halsband an Euch und bringt es zu Mutter Steinfeld auf dem Hof der Steinfelds.",
    ["T"] = "Prinzessin muss sterben!",
  },
  [89] = {
    ["D"] = "Während der letzten Invasion der Orcs waren wir gezwungen, unser Eisen einzuschmelzen, um Kugeln, Schwerter und Rüstungen herzustellen. Wir haben Sturmwind benachrichtigt, dass wir eine neue Lieferung Materialien benötigen. Aber eine Bande von Rotkammgnollen hat die Karawane überfallen und sich dann in die Hügel hinter Seenhain zurückgezogen.$B$BJetzt gehen uns langsam die Vorräte aus, die wir brauchen, um die Brücke wieder aufzubauen. Wenn Ihr mir 5 Eisenspitzen und 5 Eisennieten bringt, $N, dann sollt Ihr dafür belohnt werden. $B$BUnd jetzt an die Arbeit!",
    ["O"] = "Bringt 5 Eisenspitzen und 5 Eisennieten nach Seenhain zu Großknecht Oslow.",
    ["T"] = "Die Immerruhbrücke",
  },
  [90] = {
    ["D"] = "Gewürzter Wolfskebab ist eine Spezialität des Hauses! Natürlich kann ich Euch welchen machen. Aber ich brauche erst die Zutaten dafür. Bringt mir 10 magere Wolfflanken sowie Würzkräuter aus Sturmwind. Hier im Wald streifen so viele Wölfe herum, da werdet Ihr kein Problem haben, an die Wolfflanken zu kommen, da bin ich mir sicher. Und für die Kräuter sucht ihr am besten Felicia Gump in ihrem Blumenladen im Kanaldistrikt von Sturmwind auf.",
    ["O"] = "Beschafft Euch 10 magere Wolfflanken und Würzkräuter aus Sturmwind und kehrt dann zu Küchenchef Grual nach Dunkelhain zurück.",
    ["T"] = "Gewürzte Wolfkebabs",
  },
  [91] = {
    ["D"] = "Ein Feind Sturmwinds hat sich im östlichen Vorläufer des Rotkammgebirges niedergelassen. Dieser verderbte Schurke bedient sich des Übernatürlichen, um unsere Stadt und ihre Einwohner in Angst und Schrecken zu versetzen. Er betreibt sein übles Geschäft unter dem Namen Morganth.$B$BMorganth kontrolliert jetzt den Schattenfellklan. Die Gnolle tun alles, was er ihnen befiehlt. Wie es das Gesetz von Seenhain vorsieht, steht auf die Unterstützung eines im Königreich gesuchten Verbrechers die Todesstrafe.$B$BDiese abscheulichen Gnolle verdienen den Tod!$B$BGebt 10 Schattenfellanhänger bei mir ab, dann erhaltet Ihr eine Belohnung.",
    ["O"] = "Bringt 10 Schattenfellanhänger ins Rathaus von Seenhain zu Büttel Conacher.",
    ["T"] = "Das Gesetz des Solomon",
  },
  [92] = {
    ["D"] = "Ich würde liebend gern Rotkammgulasch für Euch kochen. Aber es gibt da ein kleines Problem. Erst kürzlich musste ich während des Orcaufstands eine ganze Brigade von Soldaten der Armee von Sturmwind verpflegen. Jetzt ist meine Vorratskammer leer. Aber wenn Ihr mir die Zutaten besorgt, dann mache ich das gern für Euch.$B$BBringt mir einfach 5 Stücke zähes Kondorfleisch, 5 große Geiferzahnschnauzen und 5 Portionen knuspriges Spinnenfleisch.",
    ["O"] = "Küchenchefin Breanna aus Seenhain möchte 5 Stücke zähes Kondorfleisch, 5 große Geiferzahnschnauzen und 5 Portionen knuspriges Spinnenfleisch.",
    ["T"] = "Rotkammgulasch",
  },
  [95] = {
    ["D"] = "Früher arbeitete ich auf einem der Höfe im Südosten... bis einmal in meiner Abwesenheit Dunkle Reiter vom Gebirgspass der Totenwinde herunterkamen, meinen Hof überfielen und meine Familie abschlachteten.$B$BAls ich zurückkehrte, sah ich eine schattenhafte Gestalt, die sich bei meiner Scheune herumtrieb und dort etwas vergrub. Sie floh, ehe ich sie erreichen konnte, und ich hatte keine Zeit, mich mit ihr aufzuhalten, da ich den Dunklen Reitern dicht auf den Fersen war. Und so habe ich nie entdeckt, was die Gestalt dort versteckte.$B$BWenn Ihr herausfinden könntet, was jene schattenhafte Gestalt dort vergrub, dann wäre ich Euch dankbar. Das Versteck befindet sich hinter dem alten Baumstumpf in der Nähe meiner Scheune.",
    ["O"] = "Begebt Euch zu Svens Hof und findet heraus, was hinter dem alten Baumstumpf vergraben wurde.",
    ["T"] = "Svens Rache",
  },
  [96] = {
    ["D"] = "Nehmt Splitter. Nehmt Splitter und gebt Leben.$B$BGebt Leben und versteht. Versteht, Wasser ist Leben... Verderbnis beendet Leben.$B$BIhr schützt und gebt Leben mit der Macht des Wassers.",
    ["O"] = "Bringt den Splitter des Wassers zu Islen Wasserdeuter am Tidustritt im Brachland.",
    ["T"] = "Ruf des Wassers",
  },
  [97] = {
    ["D"] = "Kommandant Schwarzhaupt muss unverzüglich erfahren, dass die Handschriften identisch sind, $N! Ihr Verdacht hat sich also bestätigt.",
    ["O"] = "Berichtet Kommandant Schwarzhaupt die Neuigkeiten.",
    ["T"] = "Die Legende von Stalvan",
  },
  [98] = {
    ["D"] = "Stalvan Dunstmantel hat ein Leben der Verworfenheit gelebt und unschuldige Opfer starben von seiner Hand. Zweifellos hat er noch zahllose andere Verbrechen begangen und nun wird Dunkelhain von diesem Wahnsinnigen bedroht. Das Licht allein weiß, welche schmutzigen Pläne er jetzt ausheckt. Deswegen brauche ich Eure Hilfe, $C. Begebt Euch zu seiner Hütte ein kleines Stück nördlich der Stadt und schaltet Stalvan ein für alle Mal aus.$B$BSobald die Tat vollbracht ist, begebt Euch zu Madame Eva und zeigt Ihr den Ring seiner Familie. Schließlich war es ihre Vorahnung, die zu dieser entsetzlichen Entdeckung führte. Doch jetzt kann ganz Dunkelhain dank ihr wieder ruhiger schlafen.",
    ["O"] = "Tötet Stalvan Dunstmantel und zeigt Madame Eva den Ring seiner Familie.",
    ["T"] = "Die Legende von Stalvan",
  },
  [99] = {
    ["D"] = "Je mehr ich über Arugals Magie erfahre, umso mehr wächst meine Verachtung für diesen jämmerlichen Narren. Bald werde ich meine Studien zu seiner so genannten \'Arznei\' beendet haben.$B$BMein Wissen wird vollständig sein, sobald ich herausgefunden habe, welche Verzauberung die seltsamen Zustände in Lohenscheit verursacht. Tagsüber scheinen die Arbeiter dort ganz gewöhnliche Menschen zu sein. Doch sobald die Sonne untergeht, verwandeln sich die Dorfbewohner in Mondzornworgs.$B$BIch muss Energie aus den verzauberten Fesseln ziehen, die Arugal auf sie gewirkt hat. Bringt mir 6 verzauberte Lohenscheitfesseln, $N.",
    ["O"] = "Bringt Dalar Morgenweber 6 Lohenscheitfesseln zum Grabmal.",
    ["T"] = "Arugals Torheit",
  },
  [100] = {
    ["D"] = "Noch während die Zauberformel, die Ihr gesprochen habt, verhallt, zuckt ein Meer von Lichtern um Euch herum auf. Das Zischen der Wassertropfen, die mit den Flammen in Berührung kommen, lässt nach und Eure überreizten Sinne spüren, wie die verderbten Geister in Eurer Nähe vernichtet werden.",
    ["O"] = "Sprecht mit der schwachen Wassermanifestation am Nördlichen Gezeitenstrom im Silberwald.",
    ["T"] = "Ruf des Wassers",
  },
  [101] = {
    ["D"] = "Nun, da übernatürliche Mächte den Wald durchziehen, besteht ganz zweifellos die Notwendigkeit, sich zu schützen, $N. Leise flüstern mir die Winde von einer großen Gefahr, die auf Euch lauert und der Ihr Euch in nicht allzu ferner Zukunft gegenübersehen werdet.$B$BAuch $Gein tapferer:eine tapfere:c; $C wie Ihr sollte sich schützen. Falls Ihr dies wünscht, so bringt mir 10 Ghulfangzähne, 10 Skelettfinger und 5 Phiolen Spinnengift. Dann stelle ich Euch ein Totem des Gegenschlages her und belege es mit einem Zauber, durch den es all denjenigen schadet, die gegen Euch Gewalt anwenden wollen.",
    ["O"] = "Bringt 10 Ghulfangzähne, 10 Skelettfinger und 5 Phiolen Spinnengift nach Dunkelhain zu Madame Eva.",
    ["T"] = "Das Totem des Gegenschlages",
  },
  [102] = {
    ["D"] = "Sturmwind hat uns im Stich gelassen. Der üble Gestank der Verderbtheit weht über die Ebenen von Westfall. Dies war und ist mein Heimatland und ich werde die Bürger, die sich zum Bleiben entschlossen haben, nicht verlassen. Wir, die wir früher Bauern waren, werden Widerstand leisten.$B$BEure Aufgabe, so Ihr sie denn annehmt, wird es sein, im Grasland von Westfall auf Patrouille zu gehen. Spürt die widerwärtigen Gnolle auf, die augenscheinlich mit den Dieben aus den Todesminen zusammenarbeiten, und tötet sie. Bringt mir 8 Gnolltatzen und ich werde Euren Mut zu belohnen wissen.",
    ["O"] = "Bringt 8 Gnolltatzen auf die Späherkuppe zu Hauptmann Danuvin.",
    ["T"] = "Auf Patrouille durch Westfall",
  },
  [103] = {
    ["D"] = "Die Nacht, in der die Familie des Leuchtturmwärters starb, war schrecklich. Hilflos musste ich mit ansehen, wie Trübauge der Alte den Angriff befehligte. Doch was geschehen ist, ist geschehen. Nun sorge ich mich vor allem um das Leben der Seeleute, die das Große Meer besegeln und deren Schiffe den trügerischen Felsen vor der Küste gefährlich nahe kommen. Da hier niemand mehr lebt, der die Flamme bewacht, obliegt die Verantwortung nun mir.$B$BHelft mir dabei, die Fackel am Leuchten zu erhalten, und bringt mir 5 Fläschchen Öl von den Erntemonstern.",
    ["O"] = "Bringt 5 Fläschchen Öl zu Kapitän Grausohn beim Leuchtturm von Westfall.",
    ["T"] = "Bewahrer der Flamme",
  },
  [104] = {
    ["D"] = "Als ich auf den Felsen mein Leben aushauchte, hatte ich keine Ahnung, was das Leben nach dem Tode für mich bereithalten würde. In jener Nacht war der Leuchtturm erloschen, weil Trübauge der Alte die Familie des Leuchtturmwärters vertrieben hatte. Die Familie kehrte zurück und zündete die Flamme wieder an, doch Trübauge brachte die willensschwächeren Murlocs dazu, den Leuchtturm noch einmal mit ihm zusammen anzugreifen. Beim zweiten Mal hatte die Familie nicht so viel Glück. Vor meinen Augen starben sie alle, ohne sich wehren zu können.$B$BTötet Trübauge den Alten, wenn Ihr ihn am Ufer seht, und bringt mir eine seiner Schuppen, dann werdet Ihr eine Belohnung erhalten.",
    ["O"] = "Bringt Kapitän Grausohn am Leuchtturm von Westfall eine Schuppe von Trübauge dem Alten.",
    ["T"] = "Bedrohung an der Küste",
  },
  [105] = {
    ["D"] = "Endlich ist die Zeit gekommen, Andorhal anzugreifen und den Lich, der die Stadt kontrolliert, zu vertreiben.$B$BIhr habt vielleicht einen Beschwörungskristall in den von Euch markierten Wachtürmen bemerkt. Wir glauben, dass diese dem Lich die Kraft geben, die er zur Kontrolle der Stadt benötigt. Ihr müsst alle vier ausschalten. Wird einer davon zerstört, dürfte das zweifellos weitere Truppen des Lichs auf den Plan rufen. Wenn alle vier vernichtet wurden, dürfte der Lich selbst auftauchen, um das Herz der Stadt zu verteidigen.$B$BVernichtet den Lich, $N, und bringt mir einen Splitter seines Phylakteriums als Beweis!",
    ["O"] = "Bringt den Splitter von Arajs Phylakterium zu Hochexekutor Derrington ins Bollwerk in den Westlichen Pestländern.",
    ["T"] = "Weh dir, Andorhal",
  },
  [106] = {
    ["D"] = "Oh, ein Fluch lastet auf mir! Mein Herz gehört Tommy Joe Steinfeld, aber unsere Familien sind aufs Ärgste miteinander verfeindet. Daher kann ich mich nicht mit ihm treffen, obwohl ich mich doch so sehr danach sehne, sein geliebtes Gesicht zu sehen!$B$BBitte nehmt diesen Brief und gebt ihn Tommy Joe. Normalerweise hält er sich am Fluss im Westen des Hofs der Steinfelds auf, das ist auch von hier aus genau westlich.",
    ["O"] = "Gebt Tommy Joe Steinfeld Maybells Liebesbrief.",
    ["T"] = "Junge Liebende",
  },
  [107] = {
    ["D"] = "Ich möchte wetten, William Pestle hat einen Trank, der unsere beiden Liebenden zusammenbringen kann.$B$BHier, bringt William diese Nachricht. Er wohnt im Gasthaus \"Zur Höhle des Löwen\" in Goldhain.",
    ["O"] = "Bringt Oma Steinfelds Nachricht zu William Pestle.",
    ["T"] = "Nachricht an William",
  },
  [108] = "_",
  [109] = {
    ["D"] = "Es scheint mir, $C, als hättet Ihr schon etliche Kämpfe überstanden. Falls Ihr das noch nicht getan habt, solltet Ihr Euch bei Gryan Starkmantel melden. Er ist der Anführer der Volksmiliz, die es sich zur Aufgabe gemacht hat, das Ackerland von Westfall zu beschützen. Ich möchte wetten, er könnte Eure Hilfe gebrauchen. Normalerweise findet man ihn im Steinturm auf der Späherkuppe, ein kleines Stückchen von der Straße weg, mitten in Westfall.",
    ["O"] = "Sprecht mit Gryan Starkmantel. Normalerweise findet man ihn im Steinturm auf der Späherkuppe, ein kleines Stückchen von der Straße weg, mitten in Westfall.",
    ["T"] = "Meldung bei Gryan Starkmantel",
  },
  [110] = {
    ["D"] = "Ich werde einige Zeit brauchen, um Kieselschliffs Feldmesserausrüstung durchzusehen... und seine persönlichen Angelegenheiten zu regeln. Mein Bauch sagt mir, dass dies nicht der letzte Verlust ist, den wir durch die Käfer hinnehmen müssen.$B$BGeht bitte zu Alchemist Stößelbruch, um zu sehen, ob er etwas mit den Insektenteilen anfangen konnte, die wir ihm geschickt haben. Wenn sie fertig sind, bringt sie mir.",
    ["O"] = "Begebt Euch nach Gadgetzan zu Alchemist Stößelbruch und erkundigt Euch nach der Analyse der Insektenteile.",
    ["T"] = "Insektenteil-Analyse",
  },
  [111] = {
    ["D"] = "Bitte sprecht mit meiner Großmutter, $N. Wenn irgendjemand einen Weg weiß, wie ich mit Maybell zusammen sein kann, dann ist sie das.$B$BIhr findet sie in unserem Haus, östlich von hier.",
    ["O"] = "Sprecht mit Oma Steinfeld.",
    ["T"] = "Gespräch mit Oma",
  },
  [113] = {
    ["D"] = "Der Leitende Feldmesser Rutenwünschels muss diesen Bericht sofort sehen. Eigentlich muss jeder, der lesen kann, diesen Bericht sehen. Wir stehen einer großen und ernsten Bedrohung gegenüber, die nicht nur Tanaris und Gadgetzan, sondern das ganze südliche Kalimdor betrifft, wenn wir nichts unternehmen. Möglicherweise ist sogar ganz Azeroth gefährdet. Dieses Ding macht nicht den Eindruck, als würde es das Interesse verlieren.$B$BLasst Rutenwünschels ein Duplikat des Berichts anfertigen, den Ihr dann an Eure Verbündeten verteilen könnt. Jeder muss von dieser Bedrohung erfahren!",
    ["O"] = "Bringt den Bericht nach Gadgetzan zum Leitenden Feldmesser Rutenwünschels. Lasst Euch von ihm eine Kopie des Berichts geben, wie Alchemist Stößelbruch gefordert hat.",
    ["T"] = "Insektenteil-Analyse",
  },
  [115] = {
    ["D"] = "Von den Orcs des Schwarzfelsklans wurden zur Unterstützung ihrer Angriffe auf das Rotkammgebirge Schattenzauberer rekrutiert, die von einer dunklen Macht erfüllte Vorrichtungen mit sich führten - Mitternachtskugeln. Diese Kugeln haben den Verteidigern vom Rotkammgebirge schwere Verluste zugefügt und es ist von äußerster Wichtigkeit, dass wir diese dämonbesudelten Gegenstände aus dem Konflikt entfernen.$B$BDurchsucht erschlagene Schattenzauberer des Schwarzfels nach Mitternachtskugeln und bringt sie mir. Ich werde die Kugeln dann vernichten lassen, denn ohne sie wäre die Welt ein wesentlich besserer Ort.",
    ["O"] = "Bringt 3 Mitternachtskugeln nach Seenhain zu Marschall Marris.",
    ["T"] = "Schattenmagie",
  },
  [116] = {
    ["D"] = "Mir werden hier gerade die Vorräte knapp, $N. Alle Flaschen sind fast leer, und die letzte Schnapslieferung hätte schon längst hier eintreffen sollen. Die Orcinvasion ist einfach die Hölle.$B$BVielleicht könnt Ihr Euch ja für mich an die Arbeit machen?$B$BIhr müsst für mich bei Schnapsbart Donnerbräu in den Hügeln von Westfall ein Fässchen Donnerbräu Lagerbier besorgen, ebenso ein Fässlein Merlot aus Sturmwind, eine Flasche Fusel aus Dunkelhain und einen Schlauch mit süßem Rum aus Goldhain. Bringt mir all diese Dinge, dann werde ich dafür sorgen, dass Ihr belohnt werdet.",
    ["O"] = "Barkeeper Daniels aus Seenhain braucht ein Fässchen Donnerbräu Lagerbier, ein Fässlein Merlot, eine Flasche Fusel und einen Schlauch mit süßem Rum.",
    ["T"] = "Trockene Zeiten",
  },
  [117] = {
    ["D"] = "Ihr müsst eilen, $Gmein Freund:Madame;, sollt nicht ruhen, noch rasten$BDenn für unser Lagerbier lohnt sich\'s zu hasten$BEs schmeckt allen Männern, es schmeckt allen Frauen$BDoch ich brauche noch Hopfen, um das Bier zu brauen.",
    ["O"] = "Bringt Schnapsbart Donnerbräu 5 Hopfen, damit er sein besonderes Bier brauen kann.",
    ["T"] = "Donnerbräu Lagerbier",
  },
  [118] = {
    ["D"] = "Ich habe so viel Eisen dafür verwenden müssen, die Wachen von Sturmwind auszurüsten, dass ich nicht mehr genug Eisen übrig habe, um die Pferde im Stall zu beschlagen!$B$BBringt diese Nachricht zu Schmied Argus nach Goldhain. Darin erkläre ich ihm mein Problem und bitte ihn um eine Lieferung Hufeisen.",
    ["O"] = "Bringt Verners Nachricht zu Schmied Argus nach Goldhain im Wald von Elwynn.",
    ["T"] = "Der Preis für Hufeisen",
  },
  [119] = {
    ["D"] = "Gut, er soll seine 50 Hufeisen haben.$B$BHier sind sie, schön in eine Kiste verpackt und bereit zum Abmarsch. Sagt Verner, dass es ein Vergnügen war, mit ihm Geschäfte zu machen, und sagt ihm, dass er unbedingt die Nachricht lesen soll, die ich ihm an die Kiste geheftet habe!",
    ["O"] = "Kehrt zu Verner Gutmark nach Seenhain im Rotkammgebirge zurück. Gebt ihm die Kiste mit Hufeisen.",
    ["T"] = "Zurück zu Verner",
  },
  [120] = {
    ["D"] = "Wir leben in schwierigen Zeiten, $C. Unsere Ortschaft ist einer ständigen Belagerung ausgesetzt. Wenn wir keine Verstärkung bekommen, ist unsere Niederlage so gut wie besiegelt. Die Botschaft, die ich Euch nun anvertrauen werde, ist von äußerster Wichtigkeit. Bringt diesen Bericht unverzüglich zu General Marcus Jonathan nach Sturmwind. Sobald Ihr die Nachricht abgeliefert habt, kehrt umgehend zu mir zurück und überbringt mir alle Neuigkeiten, ganz gleich, ob gut oder schlecht. $B$BDoch nun eilt!",
    ["O"] = "Magistrat Solomon hat Euch einen Bericht übergeben, der bei General Marcus Jonathan in Sturmwind abgeliefert werden muss. Der Richter möchte, dass Ihr zu ihm zurückkehrt, sobald Ihr die Nachricht zugestellt habt.",
    ["T"] = "Botschaft nach Sturmwind",
  },
  [121] = {
    ["D"] = "Während ich auf Antwort vom König warte, möchte ich, dass Ihr diesen Brief zu Magistrat Solomon bringt. $B$BWeggetreten, $C!",
    ["O"] = "Bringt General Marcus Jonathans Antwortschreiben nach Seenhain zu Magistrat Solomon.",
    ["T"] = "Botschaft nach Sturmwind",
  },
  [122] = {
    ["D"] = "Ich brauche einige Bauchschuppen von schwarzen Großdrachenwelpen, damit ich die Hufeisen bezahlen kann, die Argus mir aus Goldhain geschickt hat. Wenn Ihr mir 6 solcher Schuppen besorgt, dann kann ich mit einem Teil davon Argus bezahlen - und habe dann immer noch einen Rest übrig, aus dem ich etwas für Euch herstellen kann.$B$BSchwarze Großdrachenwelpen fliegen häufig über das Gebiet südlich von Seenhain, aber sie wandern auch gern herum. Haltet einfach die Augen offen und jagt sie, wenn Ihr sie seht.",
    ["O"] = "Sammelt 6 Welpenbauchschuppen von schwarzen Großdrachenwelpen ein und bringt sie ins Rotkammgebirge zu Verner Gutmark.",
    ["T"] = "Bauchschuppen",
  },
  [123] = {
    ["D"] = "Bei der Notiz handelt es sich um eine Tabelle mit Daten und Zeiten, zu denen jemand, der in der Notiz nur als \"der Sammler\" bezeichnet wird, je eine Lieferung Gold aus den Minen des Waldes von Elwynn erhalten soll.$B$BAus dem Plan geht hervor, dass sich der Sammler anscheinend in der Nähe von Brackbrunns Kürbisbeet in Ost-Elwynn aufhält.$B$BDas klingt, als sei es wichtig. Ihr solltet Marschall McCree im Holzfällerlager des Osttals davon berichten.",
    ["O"] = "Begebt Euch zu Marschall McCree ins Holzfällerlager des Osttals und gebt ihm den Zeitplan des Sammlers.",
    ["T"] = "Der Sammler",
  },
  [124] = {
    ["D"] = "Als ob Orcangriffe nicht genug wären! Jetzt schleichen auch noch Gnollschläger und Gnollmystiker auf dem Hügelkamm nördlich von meinem Stall herum und fallen meine Pferde an, wenn diese sich auch nur etwas zu weit vom Stall entfernen...$B$BWenn Ihr diese Gnolle beseitigen könntet, wären meine Pferde und ich Euch sehr dankbar.",
    ["O"] = "Tötet 10 Schläger der Rotkammgnolle und 8 Mystiker der Rotkammgnolle und kehrt anschließend zu Verner Gutmark zurück.",
    ["T"] = "Das Bellen der Gnolle",
  },
  [125] = {
    ["D"] = "Ich könnte hier wirklich etwas Hilfe gebrauchen, $N. Jetzt, wo die Stadt belagert wird, bekommen wir hier nur schwer Nachschub. Meine Werkzeuge sollten mit dem Karren von Goldhain aus angeliefert werden, aber die Brücke wurde in die Luft gejagt. Also legten wir die Werkzeuge in ein Boot, aber die Orcs beschossen es mit einem Katapult und trafen auch noch. Na, und bei meinem Pech ist mein Werkzeugkasten natürlich geradewegs bis auf den Grund des Sees gesunken.$B$BWenn Ihr mir meinen Werkzeugkasten wiederbeschafft, $N, dann soll es Euer Schaden nicht sein.",
    ["O"] = "Großknecht Oslow aus Seenhain möchte, dass Ihr seinen Werkzeugkasten vom Grunde des Immerruhsees heraufholt.",
    ["T"] = "Die verlorenen Werkzeuge",
  },
  [126] = {
    ["D"] = "Die Gnolle sind immer noch da draußen, $N. Ich kann sie bellen hören, wenn sie in den Hügeln oberhalb von Seenhain jagen. Und das Geheul eines Gnolls ist lauter als das aller anderen - das von Jauler, ihrem Anführer.$B$BTötet Jauler und vertreibt sein Rudel in alle vier Winde! Bringt mir seine Tatze zum Beweis.$B$BAber seid vorsichtig! Dieser Gnoll ist nicht umsonst der Anführer seines Rudels. Er ist kein einfacher Köter...",
    ["O"] = "Bringt Jaulers Tatze nach Seenhain zu Verner Gutmark.",
    ["T"] = "Geheul in den Hügeln",
  },
  [128] = {
    ["D"] = "Der Schwarzfelsklan muss vernichtet werden. Der Feind ist in unser Land eingedrungen. Diese Berge unterliegen der Oberhoheit des Königs von Sturmwind. Aus bestimmten Quellen haben wir erfahren können, dass die Anführer jeder Einheit im Klan Helden genannt werden. Ihr findet sie in ihrem Lager im Norden, ein kleines Stück westlich von der Straße, die in die Brennende Steppe führt.$B$BMagistrat Solomon möchte diese garstigen Bestien tot sehen. Erschlagt 15 Helden des Schwarzfels und kehrt zu mir zurück.",
    ["O"] = "Tötet 15 Helden des Schwarzfels, dann erhaltet Ihr von Wache Howe aus Seenhain eine Belohnung.",
    ["T"] = "Kopfgeld auf den Schwarzfelsklan",
  },
  [129] = {
    ["D"] = "Könntet Ihr mir einen Gefallen tun? Ich habe für Wache Parker ein Mittagessen gemacht, aber er ist gerade auf Patrouille unterwegs... Er ist groß und stark und noch dazu eine Wache von Sturmwind, also kann er auf sich selbst aufpassen, aber ich bin nur eine kleine Stadtbewohnerin - für mich ist das da draußen viel zu gefährlich!$B$BWenn Ihr ihm also sein Mittagessen bringt und anschließend wieder herkommt, dann bekommt Ihr auch ein Mittagessen von mir - umsonst! Parker patrouilliert die Straße, die in den Dämmerwald führt.",
    ["O"] = "Bringt Parkers Mittagessen zu Wache Parker. Er ist auf der Straße nach Dunkelhain auf Patrouille.",
    ["T"] = "Ein geschenktes Mittagessen",
  },
  [132] = {
    ["D"] = "Was ich Euch jetzt erzähle, könnte mich das Leben kosten. Die Bande der Defias plant irgendwas Großes. Das Letzte, was ich gehört habe, war, dass sie mit unterschiedlichen Gnollen, Kobolden und sogar Goblins zusammenarbeiten.$B$BBringt Starkmantel diese Nachricht. Darin steht alles, was ich zu dem Thema sagen kann.",
    ["O"] = "Bringt Wileys Nachricht nach Westfall zu Gryan Starkmantel.",
    ["T"] = "Die Bruderschaft der Defias",
  },
  [133] = {
    ["D"] = "Ich wohne so weit weg von der Stadt und dem Schutz, den sie bietet, dass es ein Wunder ist, dass die Ghule und die wandelnden Toten mich noch nicht aufgefressen haben! Tatsächlich hat erst gestern ein Rudel Knochenkauer gegen die Wände meines Hauses gehämmert.$B$BIch möchte ein Bildnis herstellen - eine Art \"Vogelscheuche\", nur für Ghule. Dafür brauche ich ein paar Ghulrippen. Die bekommt Ihr von Fleischfressern, Knochenkauern, Hirnfressern, Verfaulten und Seuchenverbreitern.$B$BWenn Ihr mir die Rippen beschafft, bezahle ich Euch in barer Münze.",
    ["O"] = "Sammelt 7 Ghulrippen und bringt sie zu Calvinius in seiner Hütte.",
    ["T"] = "Ein Ghulbildnis",
  },
  [134] = {
    ["D"] = "Vor ein paar Wochen wurde ich von einer Bande Ogern angegriffen, als ich draußen Kräuter sammelte, ziemlich weit von meinem Haus entfernt. Ich lief davon und musste eine Kiste mit wertvollen Werkzeugen und Kräutern zurücklassen.$B$BNachdem sie mich vertrieben hatten, stolzierten die Oger zurück zum Ogerhort im südlichen Dämmerwald. Ich bin mir sicher, dass meine Kiste irgendwo in der Nähe des Hügels sein muss.$B$BBitte, $N, beschafft mir diese Kiste wieder, denn sie fehlt mir sehr.",
    ["O"] = "Bringt Calvinius\' Kiste zu Calvinius.",
    ["T"] = "Ogerdiebe",
  },
  [135] = {
    ["D"] = "Ich möchte zu gern wissen, was Wiley meinte, als er die Steinmetze erwähnte. Vielleicht ist ihm da nur etwas rausgerutscht. Könnten die Defias irgendwas mit den Steinmetzen zu tun haben? Nur ein Mann weiß das mit Sicherheit: Mathias Shaw, Kopf von SI:7. Zeigt ihm Wileys Nachricht und stellt fest, ob er noch etwas zu diesem immer verzwickter werdenden Rätsel beitragen kann. Falls Ihr Probleme habt, Shaw zu finden, seht in der Kaserne in der Altstadt nach.",
    ["O"] = "Bringt Wileys Nachricht nach Sturmwind zu Mathias Shaw.",
    ["T"] = "Die Bruderschaft der Defias",
  },
  [136] = {
    ["D"] = "Wenn Ihr das hier lesen tut, dann heißt das, dass der alte Käpt\'n Sanders in den Fluten begraben is\'. Also tut mein Schatz jetz\' Euch gehör\'n, Ihr braucht bloß den Hinweisen folgen. $B$BErst müsst Ihr ma\' meine Schließkiste finden. Wahrscheinlich is\' die jetz\' da an der Westküste von Westfall in der Nähe von dem Schiffswrack schon halb im Sand verschwunden. \'s gibt da an der Küste massig Schiffswracks, aber bloß einen rostigen Anker. Wenn Ihr den Anker finden tut, dann habt Ihr auch meine Schließkiste gefunden. Da müsst ihr reingucken, denn da steht der nächste Hinweis drin.",
    ["O"] = "Findet Käpt\'n Sanders Schließkiste und durchsucht sie, um den nächsten Hinweis zu erhalten.",
    ["T"] = "Käpt\'n Sanders\' versteckter Schatz",
  },
  [137] = "_",
  [138] = {
    ["D"] = "Der Hinweis auf den Schatz lautet folgendermaßen: Gut gemacht, Kumpel! Jetz\' müsst Ihr Euch imma nach Osten halten. Im Osten die Steilhänge hoch, dann weiter nach Osten bis zur Straße. Schaut Euch nach so \'nem ollen verfallenen Kamin nahe bei der Straße um. Dort findet Ihr \'n altes Fass mit Eurem nächsten Hinweis drin.",
    ["O"] = "Findet das alte Fass in der Nähe des verfallenen Kamins und durchsucht es nach Eurem nächsten Hinweis.",
    ["T"] = "Käpt\'n Sanders\' versteckter Schatz",
  },
  [139] = {
    ["D"] = "Als Ihr das Fass durchsucht, findet Ihr ein weiteres Stück Pergament. Darauf steht: Also, von dem Fass dort müsst Ihr Euch nach Norden halten tun. Un\' dann geht Ihr immer der Nase nach schnurstracks geradeaus, bis Ihr den leeren Henkelkrug sehen tut, der da irgendwo bei so \'ner einzeln stehenden Windmühle oben auf dem Steilhang über dem Meer liegt. Wenn Ihr Euch den Henkelkrug mal genau anguckt, dann findet Ihr vielleicht sogar, was Ihr sucht.",
    ["O"] = "Durchsucht den leeren Henkelkrug neben der Windmühle nach dem nächsten Hinweis.",
    ["T"] = "Käpt\'n Sanders\' versteckter Schatz",
  },
  [140] = {
    ["D"] = "Findet Käpt\'n Sanders\' Truhe und öffnet sie. Darin befindet sich Eure Belohnung.",
    ["O"] = "Findet Käpt\'n Sanders\' Truhe und öffnet sie. Darin befindet sich Eure Belohnung.",
    ["T"] = "Käpt\'n Sanders\' versteckter Schatz",
  },
  [141] = {
    ["D"] = "Die Gilde der Steinmetze wurde von einem Mann namens Edwin van Cleef geführt. Van Cleef war dafür verantwortlich, dass Sturmwind wieder aufgebaut wurde, nachdem die Orcs es im Ersten Krieg dem Boden gleichgemacht hatten. Anscheinend waren van Cleef und seine Leute nicht zufrieden damit, wie der König sie behandelte, nachdem der Wiederaufbau vollendet war. Das könnte den einen oder anderen Sachverhalt erklären.$B$BIch habe Eurem Meister in Westfall einen ausführlicheren Bericht geschrieben. Bringt ihn sofort zu ihm!",
    ["O"] = "Bringt Shaws Bericht nach Westfall zu Gryan Starkmantel.",
    ["T"] = "Die Bruderschaft der Defias",
  },
  [142] = {
    ["D"] = "Wir müssen herausfinden, wo genau sich das Versteck der Defias befindet. $N, meine Späher berichten mir, dass ein Bote der Defias auf der Straße zwischen Mondbruch, dem Goldküstensteinbruch und dem Jangoschacht gesehen worden ist. Ich möchte, dass Ihr ihn fangt. Wenn er Widerstand leistet, tötet ihn und bringt mir alles, was er bei sich trägt.",
    ["O"] = "Spürt den Boten der Defias in Westfall auf und bringt die Nachricht, die er bei sich hat, zu Starkmantel.",
    ["T"] = "Die Bruderschaft der Defias",
  },
  [143] = {
    ["D"] = "Irgendetwas stimmt mit der Armee von Sturmwind nicht. Sie hätte längst mit einem größeren Aufgebot hier sein müssen. Doch Zeit ist ein Luxus, den wir nicht haben. Ich werde nicht dabeistehen und tatenlos zusehen, wie die Bewohner von Seenhain ihr Leben geben. Nein. Ich muss versuchen, von anderer Stelle her Hilfe zu gewinnen. Ich habe gehört, dass sich in Westfall eine Volksmiliz gebildet hat.$B$BBringt dieses Hilfegesuch ihrem Anführer, Gryan Starkmantel. Vielleicht können Starkmantels Männer uns hier unterstützen, bis der König Verstärkung schickt.",
    ["O"] = "Magistrat Solomon möchte, dass Ihr sein Hilfegesuch nach Westfall zu Gryan Starkmantel bringt.",
    ["T"] = "Botschaft nach Westfall",
  },
  [144] = {
    ["D"] = "Es schmerzt mich, Magistrat Solomons Schreiben zu lesen. Offensichtlich hat er von dem Krieg, der hier in Westfall tobt, keine Ahnung. Sonst wäre er nicht so naiv, Hilfe von der Miliz zu erwarten. Wenn Sturmwind nicht auch uns im Stich gelassen hätte, dann hätte es keinen Grund gegeben, die Miliz ins Leben zu rufen.$B$BBringt Eurem Herrn im Rotkammgebirge diese Antwort, $C. Und lasst ihn wissen, dass mir das Herz schwer wird, wenn ich an all die tapferen Leute denke, die ihr Leben verloren haben.",
    ["O"] = "Kehrt mit Gryan Starkmantels Antwort zu Magistrat Solomon zurück.",
    ["T"] = "Botschaft nach Westfall",
  },
  [145] = {
    ["D"] = "Ein weiteres Mal brauche ich Eure Hilfe. Ich habe an Lord Schwarzhaupt geschrieben und ihn gebeten, seine gut ausgebildete Nachtwache hierher zu schicken, damit diese bei der Verteidigung von Seenhain hilft. Es ist von ausnehmender Wichtigkeit, dass diese Nachricht ihn so bald wie möglich erreicht. Doch Ihr müsst äußerste Vorsicht walten lassen. Schwarzhaupt ist der Bürgermeister von Dunkelhain, und das liegt im Herzen des Dämmerwalds. Entfernt Euch nicht von der Straße.",
    ["O"] = "Magistrat Solomon möchte, dass Ihr einen Brief zu Lord Schwarzhaupt nach Dunkelhain bringt.",
    ["T"] = "Botschaft nach Dunkelhain",
  },
  [146] = {
    ["D"] = "Wenn mir Magistrat Solomon nicht persönlich bekannt wäre, würde ich sagen, der Mann hat den Verstand verloren. Die Nachtwache nach Seenhain schicken, weil Sturmwind die Hilfe verweigert? Die Nachtwache wurde gegründet, weil Sturmwind uns verließ, als diese elende Magie langsam das Land überzog. Wenn ich meine Nachtwache jetzt fortschicke, dann würde diese Stadt vom Bösen überrannt werden, noch ehe die Wachen den Waldrand erreicht hätten.$B$BBringt Eurem Meister in Seenhain dieses Antwortschreiben.",
    ["O"] = "Kehrt mit Lord Schwarzhaupts Antwortschreiben zu Magistrat Solomon zurück.",
    ["T"] = "Botschaft nach Dunkelhain",
  },
  [147] = {
    ["D"] = "Wenn der \"Sammler\" Gold aus unseren Minen entnimmt, dann bestiehlt er das Königreich! Führt den Sammler seiner gerechten Strafe zu und bringt mir den Ring, der in dem Abholplan, den Ihr mir gegeben habt, erwähnt wurde.$B$BIm Abholplan steht, dass der Sammler sich bei Brackbrunns Kürbisbeet versteckt hält. Dort solltet Ihr nach ihm suchen.",
    ["O"] = "Findet den \"Sammler\" und tötet ihn, dann kehrt mit dem Ring des Sammlers zu Marschall McCree zurück.",
    ["T"] = "Menschenjagd",
  },
  [150] = {
    ["D"] = "Die Murlocs werden zu einem Problem. Große Gruppen der Fischwesen werfen entlang der Ufer des Immerruhsees Anker und fischen, was das Zeug hält! Sie müssen verschwinden, ehe sie den ganzen See leer gefischt haben.$B$BGeht Murlocs jagen! Bringt mir 8 ihrer Flossen, dann habe ich vielleicht etwas für Euch...",
    ["O"] = "Bringt 8 Murlocflossen zu Dockmeister Baren in Seenhain.",
    ["T"] = "Murlocwilderer",
  },
  [151] = {
    ["D"] = "Die arme Graumähne! Das arme Pferd ist völlig entkräftet nach all der Arbeit, die wir ihm aufgebürdet haben. Ich habe es gefüttert, ehe wir den Hof verließen, aber wir haben natürlich nicht damit gerechnet, dass uns der Wagen kaputtgehen würde. Wenn Ihr der Stute ein paar Hände voll Hafer vom Feld bringen könntet, wäre ich Euch dankbar.$B$BIch wette, es lässt sich rund um die Höfe in Westfall etwas Hafer finden; Ihr müsst nur diesen entsetzlichen Maschinen aus dem Weg gehen, die hier alles übernommen haben. Es gibt einige Höfe südwestlich von hier.",
    ["O"] = "Verna Brauenwirbel in Westfall möchte, dass Ihr ihr 8 Hände voll Hafer bringt.",
    ["T"] = "Die arme Graumähne",
  },
  [152] = {
    ["D"] = "Die vielen Schiffswracks entlang der Küste sind Euch ja vermutlich schon aufgefallen. Das Große Meer ist überaus tückisch. Die Küste von Westfall muss freigehalten werden, damit die Seeleute sicher sind, wenn sie unsere Ufer erreichen. Aber die Murlocs sind ein Problem.$B$BTötet 7 Gezeitenjäger, 7 Krieger, 7 Orakel und 7 Küstenläufer, dann sorge ich dafür, dass Ihr belohnt werdet.",
    ["O"] = "Tötet 7 Gezeitenjäger, 7 Krieger, 7 Orakel sowie 7 Küstenläufer und kehrt dann zu Kapitän Grausohn beim Leuchtturm von Westfall zurück.",
    ["T"] = "Ärger an der Küste",
  },
  [153] = {
    ["D"] = "Die Defias sind ständig in Bewegung. Ich überwache ihre Aktivitäten jetzt schon seit einiger Zeit. Und dabei habe ich festgestellt, dass viele Bandenmitglieder an den roten Lederkopftüchern zu erkennen sind, die sie tragen.$B$BBringt mir 15 dieser Kopftücher, dann werde ich dafür sorgen, dass Ihr belohnt werdet.",
    ["O"] = "Bringt 15 rote Lederkopftücher auf die Späherkuppe zu Späher Galiaan.",
    ["T"] = "Rote Lederkopftücher",
  },
  [155] = {
    ["D"] = "Starkmantel denkt also wirklich, $Gein:eine:r; $R wie Ihr könne mich beschützen? Tja, Ihr werdet wohl reichen müssen. Bringt am besten ein paar Freunde mit. $B$BIhr wisst ja, wie\'s läuft, oder? Ihr haltet mir den Rücken frei und ich bringe Euch zum Versteck der Defias. Aber Ihr solltet besser in meiner Nähe bleiben, denn die Bande der Defias will jetzt meinen Kopf. Wenn die mich mit Euch sehen, werden sie versuchen, mich umzubringen.$B$BSagt mir einfach Bescheid, wenn Ihr und irgendwelche Freunde, die Ihr noch zusammentrommeln könnt, bereit seid loszuziehen.",
    ["O"] = "Eskortiert den Verräter der Defias zum Geheimversteck der Defiasbruderschaft. Sobald der Verräter der Defias Euch gezeigt hat, wo sich van Cleef und seine Männer aufhalten, kehrt Ihr mit der Information zu Gryan Starkmantel zurück.",
    ["T"] = "Die Bruderschaft der Defias",
  },
  [156] = {
    ["D"] = "Wenn Ihr mir ein paar Faulblüten bringt, dann kann ich Euch einen Kessel Zombiesaft brauen.$B$BFaulblüten wachsen an seltsamen Orten. Vor allem, habe ich gehört, findet man sie in den Schädeln von Skelettschrecken und Skelettscheusale - nicht, dass ich selbst schon mal nah genug dran war, um nachzusehen!$B$BDer Friedhof von Rabenflucht ist voll von diesen Skeletten. Wenn Ihr die auf Eurer Suche nach den Faulblüten auch noch dezimiert, dann schlagt Ihr zwei Fliegen mit einer Klappe, und ganz Dunkelhain wäre Euch dankbar.",
    ["O"] = "Sammelt 8 Faulblüten und bringt sie nach Dunkelhain zu Schankwart Smitts.",
    ["T"] = "Das Sammeln von Faulblüten",
  },
  [157] = {
    ["D"] = "Hier ist Euer Faden, $N. Man kann tausend Dinge damit machen, zum Beispiel böse Geister binden oder unbelebte Gegenstände mit Leben erfüllen.",
    ["O"] = "Bringt Calvinius den Geisterhaarfaden in seine Hütte nördlich des Friedhofs von Rabenflucht.",
    ["T"] = "Abliefern des Fadens",
  },
  [158] = {
    ["D"] = "Ich brauche eine Flasche eines bestimmten Schnapses, und zwar Zombiesaft. Schankwart Smitts aus der Taverne \"Zum Roten Raben\" in Dunkelhain sollte welchen haben.$B$BUnd bitte sputet Euch, $N. Die Nächte sind kalt und ich brauche den Schnaps, um meine Adern wieder mit Feuer zu erfüllen!",
    ["O"] = "Sprecht mit Schankwart Smitts.",
    ["T"] = "Zombiesaft",
  },
  [160] = {
    ["D"] = "Bringt diese Nachricht zu Ello Schwarzhaupt, dem Bürgermeister von Dunkelhain.$B$BEr und ich sind alte Freunde, und wenn er den Zettel liest und herausfindet, was Ihr für mich getan habt... dann freut er sich bestimmt genauso sehr, wie ich das tue! Hahahaha!",
    ["O"] = "Bringt Calvinius\' Brief zu Ello Schwarzhaupt, dem Bürgermeister von Dunkelhain.",
    ["T"] = "Ein Brief für den Bürgermeister",
  },
  [161] = {
    ["D"] = "Falls mein Verdacht sich bestätigt, ist dies eine Art Sprengpulver. Es muss von einem Sprengstoffexperten analysiert werden, damit wir genau wissen, womit wir es hier zu tun haben. $B$BEs gibt nur einen Mann für diese Aufgabe. Ashlan Schmunzelstein wurde eingeteilt, die Oger in der Festung Mo\'grosh im Osten zu beobachten. Begebt Euch dorthin und macht ihn ausfindig, sodass er Licht darauf werfen kann, worum es sich bei der Substanz handelt.",
    ["O"] = "Chefingenieur Hinderweir möchte, dass Ihr das sonderbar riechende Pulver zu Ashlan Schmunzelstein, dem Sprengstoffexperten am Mo\'grosh Lager bringt.",
    ["T"] = "Eine dunkle Bedrohung",
  },
  [162] = {
    ["D"] = "Ihr solltet mit diesem Bericht zu jemandem gehen, der ausreichend Kenntnis von solchen Dingen hat.$B$BIch kenne da diese Nachtelfengelehrte namens Gracina Glaubensmacht. Sie hat viel Zeit mit dem Studium der Lebensformen verbracht, die in der Ökologie von Azeroth einzigartig sind, und sie ist noch dazu Historikerin! Wenn Euch jemand dabei helfen soll herauszufinden, was genau diese Bedrohung ausmacht, und noch viel wichtiger, wie sie zu stoppen ist, gebt ihr den Bericht. Wir werden die Hilfe brauchen.$B$BDas Letzte, was ich hörte, war, dass sie im Tempel des Mondes in Darnassus lebt.",
    ["O"] = "Bringt den Bericht über Insektenanalyse nach Darnassus zu Gracina Glaubensmacht.",
    ["T"] = "Aufstieg der Silithiden",
  },
  [163] = {
    ["D"] = "Irgendetwas stimmt nicht in Rabenflucht, $N. Calor schwört, dass jemand - oder etwas - in den Gebäuden dort umgeht. Zwei Nächte in Folge hat er jetzt schon gesehen, wie sich die Schatten bewegten, und in der Nacht vorher habe er Licht in einem der Fenster gesehen, sagte er.$B$BWenn Euch, wie Ihr sagt, die Sicherheit des Dämmerwalds am Herzen liegt, dann findet heraus, welches Monster in Rabenflucht umgeht.",
    ["O"] = "Findet heraus, was in Rabenflucht umgeht.",
    ["T"] = "Rabenflucht",
  },
  [164] = {
    ["D"] = "Kennt Ihr Sven? Das ist mal ein Mann, der das Werk des Lichtes vollbringt! Er und seine Anhänger haben ein Lager am nordwestlichen Rand des Dämmerwalds.$B$BLeider sind sie so wenige, dass noch nicht einmal ein einziger Mann abkömmlich ist, um Vorräte aus der Stadt zu holen. Würdet Ihr diese Vorräte bei ihm abliefern?",
    ["O"] = "Liefert die Vorräte an Sven.",
    ["T"] = "Lieferung an Sven",
  },
  [166] = {
    ["D"] = "Jetzt gibt es für Euch nur noch eine Aufgabe zu erledigen. Edwin van Cleef muss getötet werden. Zwar stimmt es mich traurig, einen Menschen zum Tode zu verurteilen, doch es ist zum Besten des Volkes von Westfall, wenn van Cleef ein für alle Mal aus dem Verkehr gezogen wird. Bringt mir den Kopf des Schurken, sobald die Tat getan ist.",
    ["O"] = "Tötet Edwin van Cleef und bringt seinen Kopf zu Gryan Starkmantel.",
    ["T"] = "Die Bruderschaft der Defias",
  },
  [167] = {
    ["D"] = "Wir waren tief unten in einer großen Mine in Westfall, deren Eingang unter einer Scheune in Mondbruch versteckt lag. Keine Ahnung, wo dieses elende Diebesgesindel herkam. Egal, jedenfalls stürzte der Minentunnel über uns ein. Ich hab\'s nach draußen geschafft, aber die anderen... na ja... von den anderen hat seither niemand mehr etwas gehört.$B$BIhr seht aus, als könntet Ihr vielleicht etwas mehr Glück haben, wenn Ihr dort hineingeht. Wenn Ihr nach meinem Bruder suchen könntet, wäre ich Euch dankbar. Er trug immer sein Forscherliga-Abzeichen bei sich. Wenn Ihr ihn selbst nicht finden könnt, dann würde zumindest das Abzeichen mir etwas Seelenfrieden verschaffen.",
    ["O"] = "Bringt Großknecht Distelklettes Abzeichen der Forscherliga nach Sturmwind zu Wilder Distelklette.",
    ["T"] = "Oh Bruder...",
  },
  [168] = {
    ["D"] = "Viele meiner Freunde starben an jenem schrecklichen Tag, als der Minentunnel einstürzte. Wenn Eure Abenteuer Euch in jenen Abschnitt der Mine in Mondbruch führen sollten, dann haltet doch bitte die Augen offen nach jedem Zeichen, anhand dessen man sie identifizieren könnte. Wenn Ihr zum Beispiel ihre Gewerkschaftsausweise finden solltet, so bringt sie mir, dann sehe ich zu, dass ihre Familien informiert werden und damit wenigstens eine Art Schlussstrich unter diesen schrecklichen Unfall ziehen können.",
    ["O"] = "Beschafft 4 Gewerkschaftsausweise und bringt sie nach Sturmwind zu Wilder Distelklette.",
    ["T"] = "Die Suche nach Andenken",
  },
  [169] = {
    ["D"] = "Gesucht! Gath\'llzogg, Rädelsführer hinter den Angriffen des Schwarzfelsklans auf Burg Steinwacht und den Überfällen auf die Ortschaft Seenhain. $B$BGath\'llzogg ist äußerst gefährlich. Berichten zufolge hat er sich in der kürzlich eroberten Burg eingenistet und plant dort die weiteren Verbrechen des Schwarzfelsklans gegen die menschliche Bevölkerung. Die Orcplage muss um jeden Preis eingedämmt werden. Der tapferen Seele, die Magistrat Solomon den Kopf von Gath\'llzogg übergeben kann, winkt eine großzügige Belohnung.",
    ["O"] = "Erledigt Gath\'llzogg und bringt seinen Kopf zu Magistrat Solomon in Seenhain, um die Belohnung zu erhalten.",
    ["T"] = "GESUCHT: Gath\'Ilzogg",
  },
  [170] = {
    ["D"] = "Ich hoffe, Ihr seid hier, um uns zu helfen, $C. Nach dem letzten Angriff der Troggs können wir alle Hilfe gebrauchen, die wir kriegen können.$B$BIch habe gehört, die kleinen Biester sind überall im ganzen Land aufgetaucht, und wie es scheint, ist das Eisklammtal keine Ausnahme. Sie sind kreuz und quer in den Hügeln im Südosten und in der Nähe des gefrorenen Sees gesehen worden.$B$BUnd das ist noch nicht alles. Vor ein paar Nächten haben sie unser Lager im Westen angegriffen und überrannt! $B$BWir haben hier zu wenig Leute, $GJungchen:Mädel;, und wir brauchen ein paar starke Arme, die dabei helfen, die Troggs zurückzutreiben.",
    ["O"] = "Balir Frosthammer möchte, dass Ihr 6 Felsenkiefertroggs und 6 bullige Felsenkiefertroggs tötet.",
    ["T"] = "Eine neue Bedrohung",
  },
  [171] = {
    ["D"] = "Ihr wart die ganze Zeit so gut zu mir, $N. Nie hätte ich erwartet, während der Kinderwoche so viel Spaß zu haben! Danke schön.$B$BIch wünschte, all die anderen Waisenkinder hätten das Glück, so tolle Dinge zu unternehmen, wie wir. Meint Ihr, wir können Craggle Schlingerkreisel wiederfinden und bekommen einen Schaumstoffschwertständer von ihm? Ich glaube, er schiebt noch immer seinen Karren an den Kanälen rund um den Handelsdistrikt herum. So könnten alle anderen im Waisenhaus ihren Spaß haben!$B$BDanach wird es Zeit für mich, nach Hause zu gehen.",
    ["O"] = "Kauft einen Schaumstoffschwertständer von Craggle Schlingerkreisel und bringt dann Euren Schützling in das Waisenhaus von Sturmwind zurück, indem Ihr die Pfeife des Menschenwaisenkindes an Waisenmatrone Nachtigall im Kathedralenviertel übergebt.",
    ["T"] = "Tageseltern der Allianz",
  },
  [172] = {
    ["D"] = "Ihr wollt uns tatsächlich hier im Waisenhaus zur Hand gehen? Seid gesegnet, Freund. Eure Unterstützung in der Kinderwoche ist für uns von unschätzbarem Wert.$B$BNehmt diese Pfeife. Sie kann sieben Tage lang verwendet werden - über die ganze Dauer der Kinderwoche hinweg. Durch Benutzung der Pfeife, könnt Ihr mit dem Waisenkind sprechen, auf das Ihr aufpasst. Verwendet sie erneut, um das Kind wieder zu entlassen.$B$BDas Kind ist schon ganz aufgeregt und freut sich darauf Euch kennenzulernen. Worauf wartet Ihr noch $N, benutzt die Pfeife und trefft Euren Schützling.",
    ["O"] = "Benutzt die Pfeife des Orcwaisenkindes um während der Kinderwoche mit Eurem Schützling zu sprechen.",
    ["T"] = "Die Kinderwoche",
  },
  [173] = {
    ["D"] = "Es scheint, als würden dunkle Dinge unaufhaltsam in den Dämmerwald gezogen. Meister Treuwein hat sich der Aufgabe verschrieben, das Böse und die Ketzerei zu vertreiben. Dank unserer Anstrengungen lebt das Volk von Dunkelhain in Sicherheit.$B$BIhr glaubt, Ihr seid würdig, Euch unserer Gruppe anzuschließen?$B$BEinst dachte ich wie Ihr. Die Selbstzufriedenheit der Wache hatte mich meiner Illusionen beraubt und so schloss ich mich Meister Treuwein an. Wenn Ihr Euch beweisen möchtet, so kann dies nicht durch Worte geschehen.$B$BMesst Eure Fertigkeiten an den Schattenwirkern der Nachtheuler im Schattenhain und im Verrottenden Obstgarten.",
    ["O"] = "Tötet für Calor aus Dunkelhain 6 Schattenwirker der Nachtheuler.",
    ["T"] = "Worgen in den Wäldern",
  },
  [174] = {
    ["D"] = "Mein Freund Cog, der Gnom, hat mir versprochen, mir beim Bau eines Gerätes zu helfen, mit dem ich in die Sterne blicken kann. Jenseits der Magie existiert eine Wahrheit, mit deren Hilfe sich die seltsame Verwandlung des Waldes erklären lässt.$B$BBringt mir eine Bronzeröhre, die von einem geschickten Ingenieur hergestellt wurde. Wenn man nach Cogs Blaupause geht, ist das der erste Schritt.",
    ["O"] = "Viktori Prism\'Antras aus Dunkelhain möchte, dass Ihr ihm eine Bronzeröhre bringt.",
    ["T"] = "Der Blick zu den Sternen",
  },
  [176] = {
    ["D"] = "Gesucht: Hogger$B$BEin riesiger Gnoll, Hogger, streunt im südwestlichen Elwynn durch den Wald. Er hat bislang alle Versuche, ihn zu fangen, vereiteln können.$B$BDie Armee von Sturmwind hat ein großzügiges Kopfgeld auf den Gnoll ausgesetzt. Kopfgeldjäger, die sich die Belohnung verdienen möchten, sollten in die Wälder reisen und die Bestie beseitigen.",
    ["O"] = "Kümmert Euch um die \"Hogger-Situation\" und kehrt zu Marschall Dughan zurück.",
    ["T"] = "GESUCHT: \"Hogger\"",
  },
  [177] = {
    ["D"] = "Der Handspiegel war eines meiner wertvollsten Besitztümer bis zu... dem Vorfall. Danach warf ich den Spiegel hinter der Scheune fort. Es gab da einen irrsinnigen Ghul, der sich früher dort hinten herumgetrieben hat. Vielleicht hat der ihn aufgehoben und mitgenommen. Gerüchten zufolge haust die teuflische Bestie im Friedhof Stille Gärten.",
    ["O"] = "Nehmt dem irrsinnigen Ghul den Handspiegel der blinden Mary ab und gebt ihn Viktori in Dunkelhain.",
    ["T"] = "Der Blick zu den Sternen",
  },
  [178] = {
    ["D"] = "<Ihr hört eine körperlose Stimme:>$B$BIhr müsst nicht erschrecken. Ich bin Theocritus, Hochmagier des Turms von Azora im Wald von Elwynn. Der Anhänger, den Ihr in Händen haltet, ist ein Mittel zur Kommunikation zwischen den Schattenfellgnollen und Morganth, ihrem Meister.$B$BNach langen Monaten der Forschung glaube ich, dass nun auch ich durch diese Anhänger kommunizieren kann. Wenn Ihr diese Nachricht hören könnt, dann war mein Zauber erfolgreich.$B$BBringt mir diesen Anhänger und ich werde Euch für Eure Dienste belohnen.",
    ["O"] = "Bringt den verblassten Schattenfellanhänger zu Theocritus, dem Magier. Er befindet sich im Turm von Azora im Wald von Elwynn.",
    ["T"] = "Wiederbeschaffung für Theocritus",
  },
  [179] = {
    ["D"] = "Was haben wir denn hier? Ihr seht aus, als könntet Ihr was brauchen, was Euch die Hände warm hält, hm?$B$BIch sag Euch, was da helfen würde: ein paar schöne, warme Handschuhe. Und weil ich so ein netter Kerl bin, geb ich Euch doch gern so ein Paar. Unter einer Bedingung, versteht sich.$B$BIhr müsst mir etwas Wolfsfleisch besorgen. Ist doch \'n guter Handel, oder? Ihr bringt mir etwas Wolfsfleisch und ich sorge dafür, dass Ihr Euch keine Frostbeulen an den Fingern holt. Na, was sagt Ihr?",
    ["O"] = "Sten Starkarm hätte gern 8 Stücke zähes Wolfsfleisch.",
    ["T"] = "Zwergenausstatter",
  },
  [180] = {
    ["D"] = "Gesucht: Leutnant Fangor$B$BBekannter Anführer des Gnollklans der Schattenfelle; arbeitet für Morganth, den Feind der Stadt und des Königreiches. Auf Geheiß von Magistrat Solomon wird der Tod dieses üblen Verräters an Sturmwind gewünscht. Zeigt dem Magistraten seine Tatze zum Beweis, dass er tot ist.",
    ["O"] = "Tötet Leutnant Fangor und kehrt mit seiner Tatze zu Magistrat Solomon nach Seenhain zurück.",
    ["T"] = "GESUCHT: Leutnant Fangor",
  },
  [181] = {
    ["D"] = "Jetzt brauchen wir nur noch einen weiteren Gegenstand, um das Gerät fertig zu stellen. Laut Cogs Blaupause wird eine Linse oder etwas in der Art benötigt. Die einzige Linse, von der ich weiß, dass sie groß genug wäre, wird sehr schwierig zu beschaffen sein.$B$BEs gibt da einen Oger namens Zzarc\'Vul, der im Ogerhort im Süden des Dämmerwalds haust. Wenn Ihr könnt, bringt mir sein Monokel, dann werde ich das als unsere Linse verwenden.",
    ["O"] = "Findet Zzarc\'Vul im südlichen Ogerhort des Dämmerwalds und bringt sein Monokel zu Viktori nach Dunkelhain.",
    ["T"] = "Der Blick zu den Sternen",
  },
  [182] = {
    ["D"] = "Die Trolle benehmen sich seltsam. Mehr als normalerweise, meine ich. Wir haben sie ja schon vorher Kriegstruppen aufstellen sehen, aber diesmal ist es... anders.$B$BAm wichtigsten ist jetzt, dass wir Ambossar weiter beschützen. Ich habe nicht vor, diese Trolle Ambossar flankieren zu lassen, während es sich auf die Troggs konzentriert. Geht und dünnt ihre Reihen ein wenig aus. Sie müssen sehen, dass wir keinesfalls ohne Verteidigungen dastehen.",
    ["O"] = "Tötet 10 Trollwelpen der Frostmähnen.",
    ["T"] = "Die Trollbedrohung",
  },
  [183] = {
    ["D"] = "Es geht doch nichts über einen Tag auf der Eberjagd, wie?$B$BNa ja, hier im Eisklammtal gibt es so viele Eber, dass es fast schon keinen Spaß mehr macht. Man muss sie gar nicht mehr reizen. Sie sind schon aggressiv und angriffslustig, ohne dass man nachhilft. Um genau zu sein, streifen in letzter Zeit so viele Eber durch die Gegend, dass es gefährlich für mich geworden ist, auf die Jagd zu gehen.$B$BIch mach\'s kurz: Wenn Ihr mir dabei helfen könntet, einige der Eber zu töten, dann würde ich das zu schätzen wissen.",
    ["O"] = "Talin Kühnauge möchte, dass Ihr 12 kleine Klippeneber tötet.",
    ["T"] = "Der Eberjäger",
  },
  [184] = {
    ["D"] = "Dies ist eine Besitzurkunde für ein Stück Ackerland in Westfall. Es ist von einem gewissen Theodore Brauenwirbel und seiner Frau Verna unterzeichnet. Auf der Rückseite der Besitzurkunde stehen ein paar hastig hingekritzelte Worte:$B$B\"Wir haben Brauenwirbel etwas bearbeitet und ihm seine Besitzurkunde abgeluchst. Wäre vielleicht ganz nützlich, wenn Ihr Euch auch so eine fälschen wolltet.$B$BUm die Brauenwirbels müssen wir uns jedenfalls keine Sorgen machen. Zuletzt sah ich sie kurz hinter Westfall in einem Wagen mit gebrochener Achse.\"$B$BIhr denkt, die Brauenwirbels hätten ihre Besitzurkunde vielleicht gern zurück...",
    ["O"] = "Bringt Brauenwirbels Besitzurkunde zu Jansens Hof in Westfall.",
    ["T"] = "Brauenwirbels Urkunde",
  },
  [185] = {
    ["D"] = "Als ich noch ein kleines Mädchen war, brachte mein Vater mir die Kunst der Tigerjagd bei.$B$BIhr werdet feststellen, dass die jungen Katzen sich wesentlich leichter aufspüren und töten lassen. Man findet die jungen Tiger oftmals in der Nähe des Expeditionslagers.$B$BÜbt Euch im Fährtenlesen und seht zu, ob Ihr nicht einige der Wildtiere zur Strecke bringen könnt. Beginnt Eure Jagd im Süden in der Nähe unseres Lagers.",
    ["O"] = "Jagt 10 junge Schlingendorntiger.",
    ["T"] = "Beherrschen der Tigerjagd",
  },
  [186] = {
    ["D"] = "Junge Tiger sind das Eine. Ausgewachsene Tiger zu jagen ist aber eine ganz andere Angelegenheit. Die erwachsenen Schlingendorntiger zu erlegen sollte sich als eine noch größere Herausforderung erweisen.$B$BDie erwachsenen Katzen halten sich meistens im Süden von Kurzens Truppenlager auf der Ostseite des Flusses auf.",
    ["O"] = "Jagt 10 Schlingendorntiger.",
    ["T"] = "Beherrschen der Tigerjagd",
  },
  [187] = {
    ["D"] = "Alte Schlingendorntiger sind die stärksten Tiger, die es gibt. Viele Jäger vor Euch sind ihren rasiermesserscharfen Zähnen und schweren Krallen zum Opfer gefallen.$B$BAjeck wird sich freuen, wenn sie hört, dass Ihr gegen sie bestehen konntet. Das größte Rudel dieser Raubkatzen ist im Süden zu finden, in der Nähe der Zerschlagung an der Grenze zum Schlingendornkap.",
    ["O"] = "Jagt 5 Alte Schlingendorntiger.",
    ["T"] = "Beherrschen der Tigerjagd",
  },
  [188] = {
    ["D"] = "Legenden sprechen von einer schwer aufzuspürenden Tigerin... einer Raubkatze, deren Macht selbst die der ausgewachsenen Männchen in den Schatten stellt. Die Jäger von Nesingwarys Expedition haben ihr ohne Erfolg nachgestellt und viele von ihnen sind ihren reißenden Krallen zum Opfer gefallen. Die Leichen dieser Unglücklichen verwesen nun am Fuße ihres Hügels, nördlich von Grom\'gol.$B$BDer Name der Bestie lautet Sin\'dall.$B$BIhr entschließt Euch, das zu tun, was noch $Gkeinem:keiner:r; $R gelungen ist: Sin\'dall zu jagen, zu töten und ihre Tatze als Trophäe zu erbeuten.",
    ["O"] = "Jagt Sin\'Dall und kehrt mit ihrer Tatze zurück.",
    ["T"] = "Beherrschen der Tigerjagd",
  },
  [189] = {
    ["D"] = "Verdammte Trolle! Sie haben im Dschungel schon etliche unserer Agenten getötet und ich habe bereits einige meiner besten Wachen in Scharmützeln mit ihnen verloren. Was das alles kostet! Wie soll ich das nur Baron Revilgaz erklären?$B$BEntschieden handeln... Denk nach, Kebok... Du bist auf den Posten, den du heute hast, nicht durch Rumsitzen gekommen... Ha, ich hab\'s! Ihr... $N, richtig? Ihr helft mir doch, oder? Es gibt auch Geld zu verdienen!$B$BFolgender Handel... Ihr bringt mir die Ohren von 15 Trollen der Blutskalpe und ich bezahle Euch gut dafür.",
    ["O"] = "Verschafft Euch 15 Ohren der Blutskalpe und bringt sie Kebok in Beutebucht.",
    ["T"] = "Ohren der Blutskalpe",
  },
  [190] = {
    ["D"] = "Jagt 10 junge Panther.",
    ["O"] = "Jagt 10 junge Panther.",
    ["T"] = "Beherrschen der Pantherjagd",
  },
  [191] = {
    ["D"] = "Die jüngsten Panther sind für Euch eine leichte Beute gewesen. Panther, die aber schon herangewachsen sind, werden natürlich stärker, schneller und schwieriger zu besiegen sein.$B$BGrößere Pantherfährten führen nach Westen.",
    ["O"] = "Jagt 10 Panther.",
    ["T"] = "Beherrschen der Pantherjagd",
  },
  [192] = {
    ["D"] = "Jetzt kommt der schwierige Teil. Je älter ein Panther wird, desto gerissener wird er auch, wird eins mit seiner Umgebung und lässt sich nur noch schwer aufspüren. Diese verschlagenen Panther sind unter dem Namen \"Schattentatzenpanther\" bekannt, weil sie die Angewohnheit haben, plötzlich aus der Dunkelheit über einen herzufallen... und zwar mit einem hungrigen Maul voller Zähne.$B$BWenn Ihr Euch als ein wahrer Pantherjäger beweisen wollt, dann sind die Schattentatzenpanther der einzige Weg zum Ziel. Sie streifen im Südosten umher, am anderen Flussufer.",
    ["O"] = "Jagt 5 Schattentatzenpanther",
    ["T"] = "Beherrschen der Pantherjagd",
  },
  [193] = {
    ["D"] = "Ihr habt schon fast bewiesen, dass Ihr die Kunst der Pantherjagd meisterlich versteht. Schon seit längerem pirscht ein Panther namens Bhag\'thera durch den Dschungel. Bis jetzt ist er unserer Jagdgesellschaft immer ausgewichen. Seht zu, ob es Euch mit Euren Fähigkeiten gelingt, den großen Bhag\'thera zu töten.$B$BBringt mir den Fangzahn von Bhag\'thera, dann habt Ihr Euch meinen Respekt redlich verdient!",
    ["O"] = "Sire S. J. Erlgadin von Nesingwarys Expedition möchte, dass Ihr ihm den Fangzahn von Bhag\'thera bringt.",
    ["T"] = "Beherrschen der Pantherjagd",
  },
  [194] = {
    ["D"] = "Soso, Ihr glaubt also, Eure Jagdfertigkeiten sind erstklassig? Das lässt sich ganz leicht feststellen. Zieht hinaus in den Dschungel und tötet zehn Schlingendornraptoren. Dann werden wir ja sehen, wie gut Ihr als Großwildjäger wirklich seid.$B$BUnd nein, ich werde Euch nicht sagen, wo Ihr sie finden könnt! Die Viecher aufzuspüren ist Teil der Aufgabe.$B$BNa gut. Ihr müsst nach Westen.",
    ["O"] = "Jagt 10 Schlingendornraptoren.",
    ["T"] = "Beherrschen der Raptorenjagd",
  },
  [195] = {
    ["D"] = "Jetzt machen wir die Herausforderung ein wenig schwieriger. Begebt Euch in den Dschungel und erlegt 10 dieser Schmetterschwanzraptoren. Ihr werdet feststellen, dass diese stärker sind, als die Raptoren, die Ihr schon erlegt habt.$B$BDie Schmetterschwanzraptoren halten sich meistens südöstlich von hier auf, in der Nähe der Ruinen von Kal\'ai.",
    ["O"] = "Jagt 10 Schmetterschwanzraptoren.",
    ["T"] = "Beherrschen der Raptorenjagd",
  },
  [196] = {
    ["D"] = "Eine besonders gerissene Art von Raptor, die bei den Einheimischen als \"Dschungelpirscher\" bekannt ist, treibt sich südlich des Flusses im Wald herum. Diese sind viel schwieriger aufzuspüren und zu töten, als die anderen Arten, an denen Ihr Euch versucht habt.$B$BDiese Dschungelpirscher zu erlegen sollte mit Sicherheit eine gute Lagerfeuergeschichte abgeben.",
    ["O"] = "Jagt 10 Dschungelpirscher.",
    ["T"] = "Beherrschen der Raptorenjagd",
  },
  [197] = {
    ["D"] = "Bevor Ihr zu Nesingwarys Expedition zurückkehrt, müsst Ihr das Unmögliche wagen: macht Jagd auf den Raptor, den noch kein anderer Jäger fangen konnte. Dieses Tier ist unter dem Namen Tethis bekannt und versetzt die südöstlichen Gegenden des Nördlichen Schlingendorntals in Angst und Schrecken.$B$BDie Kralle von Tethis bei diesem Zwergenjäger abzuliefern sollte Beweis genug sein, dass Ihr $Gkein gewöhnlicher:keine gewöhnliche:r; $R seid.",
    ["O"] = "Jagt Tethis.",
    ["T"] = "Beherrschen der Raptorenjagd",
  },
  [198] = {
    ["D"] = "Hmm. Ja, es gibt tatsächlich etwas, das Ihr tun könntet. Leutnant Doren und seine Leute oben im Norden warten auf ihre turnusmäßige Vorratslieferung von uns.$B$BDie Vorräte wurden noch nicht bei ihnen abgeliefert und jetzt, wo die Trolle ständig angreifen, brauche ich jemanden, der das erledigt. Dorens Lager liegt im Nordwesten der Straße, dort, wo man vom Dämmerwald aus ins Schlingendorntal kommt. Gebt die Vorräte dem Gefreiten Thorsen. Wenn er nicht im Lager ist, dann patrouilliert er südlich davon. Er wird sie in Empfang nehmen und verstauen.$B$BAber dass eines klar ist: Wenn Ihr versagt, dann müsst Ihr mir Ersatz leisten.",
    ["O"] = "Liefert die verschiedenen Goblinvorräte bei dem Gefreiten Thorsen ab.",
    ["T"] = "Vorräte für den Gefreiten Thorsen",
  },
  [200] = {
    ["D"] = "Wenn Kurzen Thorsens Geheimnis kennt, dann haben wir einen Spion in unseren Reihen. Wir müssen herausfinden, wie das Geheimnis verraten werden konnte!$B$BThorsens Kontaktperson in Kurzens Lager war der Buchmacher Herod. Herod ist Kurzens Nachschubsoffizier und weiß alles über den Betrieb in Kurzens Truppenlager. Wenn Ihr Herods Unterlagen oder Aufzeichnungen beschaffen könntet, fänden wir vielleicht heraus, wie Thorsen entdeckt werden konnte.$B$BAls wir noch unter Kurzen dienten, war das Büro des Buchmachers im oberen Stock des einzigen zweistöckigen Gebäudes im Lager. Seine Aufzeichnungen sind vermutlich immer noch dort zu finden.",
    ["O"] = "Findet Buchmacher Herods Aufzeichnungen.",
    ["T"] = "Buchmacher Herod",
  },
  [201] = {
    ["D"] = "Lokalisiert das Jägerlager und teilt dann Krazek in Beutebucht mit, wo es sich befindet.",
    ["O"] = "Lokalisiert das Jägerlager und teilt dann Krazek in Beutebucht mit, wo es sich befindet.",
    ["T"] = "Untersuchung des Lagers",
  },
  [202] = {
    ["D"] = "Ihr habt Euren Wert wieder und wieder unter Beweis gestellt und Euer Einfallsreichtum scheint sich mit dem von Kurzen selbst messen zu können. Ihr könntet die Rettung für unseren Rebellentrupp sein... falls Ihr Euch gegen Kurzen stellen und siegen könnt!$B$BEr erteilt die Befehle an seine Männer von tief aus dem Vorratslager heraus. Ihr müsst Euch Zutritt zu dieser Höhle verschaffen, an seinen Verteidigern vorbei, und dann Kurzen und seinen Führungsoffizieren gegenübertreten.$B$BLebt wohl, $N. All meine Hoffnung, ebenso das Schicksal meiner Männer, ruht in Euren Händen.",
    ["O"] = "Tötet 6 von Kurzens Elitesoldaten, 4 von Kurzens Stabschefs und bringt Kurzens Kopf ins Rebellenlager zu Leutnant Doren.",
    ["T"] = "Colonel Kurzen",
  },
  [203] = {
    ["D"] = "Wir kämpfen einen aussichtslosen Kampf gegen Colonel Kurzen. Nicht nur, dass der Mann ein brillanter Taktiker ist, seine Männer sind uns auch noch zahlenmäßig mindestens 10 zu 1 überlegen! Um überleben zu können, müssen wir seine eigenen, auf Heimlichkeit basierenden Taktiken gegen ihn anwenden.$B$BBetretet sein Lager im Osten und tötet seine Leute. So viele Ihr könnt. Und verschwindet wieder, bevor seine Verstärkung Euch in die Zange nehmen kann.",
    ["O"] = "Tötet 15 von Kurzens Dschungelkämpfern.$B$BKehrt ins Rebellenlager zu Unteroffizier Yohwa zurück.",
    ["T"] = "Die zweite Rebellion",
  },
  [204] = {
    ["D"] = "Bringt Unteroffizier Yohwa im Rebellenlager 7 Dschungelarzneien und 1 Giftfarnextrakt.",
    ["O"] = "Bringt Unteroffizier Yohwa im Rebellenlager 7 Dschungelarzneien und 1 Giftfarnextrakt.",
    ["T"] = "Schlechte Medizin",
  },
  [205] = {
    ["D"] = "Während ich die Legenden studiere, die Ihr beschafft habt, sammelt bitte für mich die Gerätschaften der bösen Trollmagie. Begebt Euch in das Gebiet der Schädelspaltertrolle, eines zweiten Stammes weit entfernt im Süden, und beschafft Euch dort Fetische der Schädelspalter von ihren Hexendoktoren und Mystikern.",
    ["O"] = "Bringt Bruder Nimetz 4 Fetische der Schädelspalter ins Rebellenlager.",
    ["T"] = "Trollhexerei",
  },
  [208] = {
    ["D"] = "$N, Ihr seid $Gein Jäger, der:eine Jägerin, die; unserer Gesellschaft wahrlich würdig ist. Von den Schlachtfeldern von Lordaeron bis in den tiefsten Dschungel hinein habe ich selten jemanden gesehen, der so geschickt mit den Waffen umgegangen ist wie Ihr.$B$BIhr habt die furchterregende Sin\'Dall, den schwer fassbaren Bhag\'thera und die tückische Tethis erlegt. Doch die wahre Trophäe ist der Kopf von König Bangalash. Dieser weiße Tiger ist der Grund, warum ich hier bin. Und doch verspottet er mich von seinem Schlafplatz im Süden aus. Wenn Ihr König Bangalash tötet, dann steht ein für alle Mal fest, dass Euer jägerisches Können von niemandem übertroffen wird.",
    ["O"] = "Jagt nach dem Kopf von König Bangalash, dem großen weißen Tiger.",
    ["T"] = "Großwildjagd",
  },
  [209] = {
    ["D"] = "Es ist teuer und anstrengend, genug Tiger zu töten, um damit den Bedarf an Tigerzähnen für den Export auf die Inseln in den Südlichen Meeren decken zu können. Glücklicherweise haben wir eine gewisse Technik entwickelt, mit deren Hilfe wir aus einfachem hornähnlichen Material nicht nachweisbare Fälschungen herstellen können.$B$BErstaunlicherweise eignen sich die Hauer von Schädelspaltertrollen dafür am besten.$B$BUnd bevor Ihr irgendwas sagt - was der Käufer nicht weiß, macht ihn nicht heiß, hab ich nicht Recht? Bringt mir so viele Hauer wie möglich, damit wir mit der monatlichen Lieferung anfangen können!",
    ["O"] = "Besorgt 18 Hauer der Schädelspalter und bringt sie zu Kebok.",
    ["T"] = "Hauer der Schädelspalter",
  },
  [210] = {
    ["D"] = "Meistens bin ich es, der hier im Lager kocht. Und das war auch immer ganz schön viel Arbeit, bis ich von einem Goblinhändler in Beutebucht einen mechanischen Kochtopf gekauft habe. Der Topf ist einfach unglaublich. Er hat mir unzählige Stunden Arbeit erspart - bis er kaputtgegangen ist!$B$BKönnt Ihr den Topf dem Goblin zurückbringen, damit er ihn mir repariert? Der Händler heißt Krazek.",
    ["O"] = "Bringt Krazeks Spezialtopf zu Krazek, dem Goblin.",
    ["T"] = "Kochen mit Krazek",
  },
  [211] = {
    ["D"] = "Endlich ist die Zeit gekommen, Andorhal anzugreifen und den Lich, der die Stadt kontrolliert, zu vertreiben.$B$BIhr habt vielleicht einen Beschwörungskristall in den von Euch markierten Wachtürmen bemerkt. Wir glauben, dass diese dem Lich die Kraft geben, die er zur Kontrolle der Stadt benötigt. Ihr müsst alle vier ausschalten. Wird einer davon zerstört, dürfte das zweifellos weitere Truppen des Lichs auf den Plan rufen. Wenn alle vier vernichtet wurden, dürfte der Lich selbst auftauchen, um das Herz der Stadt zu verteidigen.$B$BVernichtet den Lich, $N, und bringt mir einen Splitter seines Phylakteriums als Beweis!",
    ["O"] = "Bringt den Splitter von Arajs Phylakterium zu Kommandant Ashlam Ehrenschlag auf der Zugwindspitze in den Westlichen Pestländern.",
    ["T"] = "Weh dir, Andorhal",
  },
  [212] = {
    ["D"] = "Eines meiner berühmtesten Gerichte ist gekühlter Basilisk an Limonenvinaigrette. Das Problem ist nur: Das Fleisch lässt sich unmöglich frisch halten und ich muss das Gericht heute Abend anlässlich des Geburtstags eines Edelmannes servieren!$B$BWenn Ihr mir also schnell einen gekühlten Basiliskenschenkel von einem frisch getöteten Kaltaugenbasilisken bringen könnt, dann schulde ich Euch ein Festessen, das eines Königs würdig wäre!$B$BKaltaugenbasilisken streifen durch die Stranddünen im Nordwesten des Schlingendorntals, also habt Ihr eine lange Reise vor Euch. Und macht schnell!",
    ["O"] = "Tötet einen Kaltaugenbasilisken, beschafft Euch einen gekühlten Basiliskenschenkel und bringt ihn Angus Stern im Blauen Eremiten.",
    ["T"] = "Eine kalte Mahlzeit",
  },
  [213] = {
    ["D"] = "Diesmal ist er zu weit gegangen! Schlimm genug, dass Gelriz dabei ist, die von den Handelsbaronen eingesetzten Moguln rauszudrängen, jetzt versucht er auch noch, den berüchtigtsten aller Piraten mit hineinzubringen!$B$BDas lässt Revilgaz sich nicht bieten und er hat mir aufgetragen, das Problem auf meine Weise zu lösen.$B$BWas meine Weise ist? Diebstahl, ganz einfach. Die Geologen der Venture Company zeigen ein auffälliges Interesse an den seltsamen blauen Kristallen, die sie bisweilen in den Minen finden. Bringt mir einige Proben dieser Steine von den Geologen; es ist mir völlig egal, was Ihr tun müsst, um an sie heranzukommen.",
    ["O"] = "Beschafft 8 gerommelte Kristalle und bringt sie nach Beutebucht zu Kebok.",
    ["T"] = "Feindliche Übernahme",
  },
  [214] = {
    ["D"] = "Es ist kein Geheimnis, dass das Markenzeichen der Defiasbande ihre roten Kopftücher sind. Aber wir haben herausgefunden, dass das Material, aus dem diese Kopftücher gemacht sind, den Rang des Bandenmitglieds bezeichnet.$B$BJetzt, wo wir das wissen, möchte ich, dass Ihr so viele hochrangige Mitglieder der Defias eliminiert, wie Ihr könnt. Die Bandenmitglieder mit der höchsten Position findet Ihr zweifellos in van Cleefs Geheimversteck. Wenn Ihr fertig seid, bringt mir 10 rote Seidenkopftücher zum Beweis, dass sie tot sind, dann werde ich Euch belohnen. Viel Glück, $N.",
    ["O"] = "Späherin Riell am Turm auf der Späherkuppe möchte, dass Ihr ihr 10 rote Seidenkopftücher bringt.",
    ["T"] = "Rote Seidenkopftücher",
  },
  [215] = {
    ["D"] = "Danke, dass Ihr mich gerettet habt, $N. Ich weiß, wie es aussieht, aber lasst mich bitte erklären!$B$BIch hatte von Leutnant Doren den Auftrag erhalten, mich wieder Kurzens Männern anzuschließen. Ich sollte sie ausspionieren und den Rebellen Informationen bringen, die sie bitter benötigen. Was Ihr hier miterlebt habt, war das, was ich für meine Chance hielt, Kurzens Truppenlager zu infiltrieren. Ich habe mich geirrt und ich muss Euch nochmals danken, dass Ihr mich gerettet habt.$B$BDiese Mission war streng geheim, Ihr dürft also niemandem davon erzählen. Aber wenn Ihr mit Doren sprecht, dann wird der Euch alles erklären.",
    ["O"] = "Sprecht im Rebellenlager mit Leutnant Doren.",
    ["T"] = "Geheimnisse im Dschungel",
  },
  [216] = {
    ["D"] = "Die Distelfellfurbolgs bedrohen unsere Expansion! Das Lager der Distelfelle westlich von uns blockiert den Weg zwischen uns hier und dem Splitterholzposten. Dieser Weg würde das Dorf Astranaar umgehen; ohne ihn überlassen wir der Allianz eine zusätzliche Möglichkeit der Ausbreitung.$B$B$N, wir dürfen keine Lücke in unseren Expansionspfaden erlauben!$B$BBegebt Euch ins Distelfelllager und dünnt die Furbolgs etwas aus. Ihr werdet für diese Aufgabe entschädigt werden, aber noch viel wichtiger ist, dass Ihr Eure Pflicht der Horde gegenüber tun werdet.",
    ["O"] = "Karang Amakkar in Höllschreis Wacht möchte, dass Ihr 15 beliebige Furbolgs der Distelfelle im Distelfelllager ausschaltet.",
    ["T"] = "Hart auf hart",
  },
  [217] = {
    ["D"] = "Mein Trick hat funktioniert. Wie es scheint, folgen diese Bestien am Loch Modan einem Häuptling namens Grawmug. Grawmug wird jedoch aufs Schwerste bewacht. Zwei seiner Elitewachen, Knirscher und Muskelprotz, weichen nie von seiner Seite.$B$BWenn Ihr Euch dem gewachsen fühlt, $N, dann möchte ich, dass Ihr das Attentat auf Grawmug und seine beiden Schläger hier in dieser Höhle anführt. Geht! Ich werde mich von diesen Fesseln befreien und in Kürze zu Euch aufschließen.",
    ["O"] = "Tötet den Anführer der Troggs, Grawmug, sowie seine beiden Wachen, Knirscher und Muskelprotz, und erstattet dann Hauptmann Rugelfuß in Thelsamar Bericht.",
    ["T"] = "Verteidigung der Länder des Königs",
  },
  [218] = {
    ["D"] = "Soren hat gut daran getan, Euch auf Erkundung zu schicken. Das sind wertvolle Informationen, die Ihr da bringt.$B$BDas Problem ist nur, dass uns das nur noch mehr Arbeit macht. So verwirrt die Trolle momentan zwar sind, würden sie die Zwerge noch immer am liebsten aus dem Eisklammtal hinauswerfen. Und ich werde ganz gewiss keinem dahergelaufenen Feuerdämon erlauben, unser verschneites Tal in einen Bergsee zu verwandeln.$B$BDer Trollanführer, seine Diener und die Feuerkreatur sollten alle in der Höhle im Südosten sein. Schaltet sie allesamt aus.",
    ["O"] = "Tötet Grik\'nir den Kalten, 10 von Grik\'nirs Dienern und den unberechenbaren Feuerelementar.",
    ["T"] = "Eis und Feuer",
  },
  [219] = {
    ["D"] = "Freunde der Allianz! Endlich!$B$BHelft... bitte... $B$BDas Letzte, an das ich mich erinnere, ist, dass diese abscheulichen Orcs unser Regiment überwältigten. Die meisten Männer wurden getötet; ich selbst erhielt einen Schlag an den Kopf und alles wurde schwarz. Jetzt werde ich in dieser Höhle gefangen gehalten. Bitte helft mir; ich bin schwer verletzt.$B$BIch brauche Hilfe auf dem Weg zurück in die Stadt... bitte... Ihr seid meine letzte Hoffnung...",
    ["O"] = "Beschützt Korporal Keeshan auf dem Rückweg ins Rotkammgebirge.",
    ["T"] = "Vermisst",
  },
  [220] = {
    ["D"] = "Islen hat noch eine weitere Aufgabe für Euch, ehe Ihr zur Belohnung Euer nächstes Totem erhaltet. Die Phiole mit Wasser, die ich Euch gegeben habe, wird für diese Aufgabe gebraucht, um was auch immer es sich dabei handeln mag.$B$BGeht nun und sucht sie auf. Wir werden uns eines Tages wieder begegnen.",
    ["O"] = "Bringt die Phiole mit reinstem Wasser zu Islen Wasserdeuter am Tidustritt im Brachland.",
    ["T"] = "Ruf des Wassers",
  },
  [221] = {
    ["D"] = "Habt Ihr vielleicht schon festgestellt, dass einige größere Worgen zusammen mit den Schattenwirkern im Wald herumziehen? Nach allem, was wir wissen, machen diese Dunkelläufer den Großteil der Worgen-Population aus.$B$BAuf meinen Wanderungen habe ich außerdem festgestellt, dass sie in den Verrottenden Obstgarten im Südwesten der Stadt eingefallen sind. Diese Worgen sind etwas zäher als die letzten, denen Ihr begegnet seid.$B$BAlso seht Euch vor.",
    ["O"] = "Tötet für Calor aus Dunkelhain 12 Dunkelläufer der Nachtheuler.",
    ["T"] = "Worgen in den Wäldern",
  },
  [222] = {
    ["D"] = "Eure früheren Leistungen haben mich überzeugt, dass Ihr in der Lage seid, Euch mit den zähesten Worgen zu messen, die sich in den Wäldern eingenistet haben.$B$BVon all den Worgen, die plötzlich hier zu finden sind, haben sich die Faulreißer und die Besudelten als die gefährlichsten herausgestellt. Sie haben sich in der Nähe einiger Höhlen und in der Mine im Süden niedergelassen.$B$BNoch aus weiter Entfernung sieht man die Lichter ihrer Feuer...",
    ["O"] = "Tötet für Calor aus Dunkelhain 8 Faulreißer der Nachtheuler und 8 besudelte Nachtheuler.",
    ["T"] = "Worgen in den Wäldern",
  },
  [223] = {
    ["D"] = "Hier, bitte schön, $N. Bringt Meister Treuwein diese Nachricht.$B$B<Schnell zieht er ein Stück verblasstes Pergament hervor und reicht es Euch.>$B$BNoch ein paar mehr wie Ihr, dann sind wir bald mehr Kämpfer als die Nachtwache! Vielleicht können wir dann das Werk vollenden, an dem wir wenigen uns heute noch vergebens abmühen.",
    ["O"] = "Bringt Calors Notiz zu Jonathan Treuwein.",
    ["T"] = "Worgen in den Wäldern",
  },
  [224] = {
    ["D"] = "Wir müssen den Loch beschützen, $N! Jetzt, wo so viele der Soldaten des Königs tapfer auf weit entfernten Schlachtfeldern kämpfen, sind wir nahe davor, an der Heimatfront überwältigt zu werden. Aus jedem Spalt graben sich Troggs an die Oberfläche! Die Troggplage stellt für Eisenschmiede die größte Gefahr dar. Diese widerwärtigen Mutanten müssen vernichtet werden.$B$BIhr, $Gtapferer Abenteurer:tapfere Abenteurerin;, müsst Euch für uns hinauswagen und der Bedrohung durch die Troggs ein Ende bereiten. Erschlagt 6 Splittersteintroggs und 6 Späher der Splittersteintroggs und erstattet dann wieder Bericht.",
    ["O"] = "Gebirgsjäger Feuerkiesel in Thelsamar möchte, dass Ihr 6 Splittersteintroggs und 6 Späher der Splittersteintroggs tötet.",
    ["T"] = "Verteidigung der Länder des Königs",
  },
  [225] = {
    ["D"] = "Auf dem verwitterten Grabstein steht einfach:$B$BMORGAN LADIMORE$B$B<Als Ihr den Ort weiter untersucht, stellt Ihr fest, dass die Erde über dem Grab kürzlich umgegraben wurde und dass ein guter Teil der Erde entfernt worden zu sein scheint...>",
    ["O"] = "Sprecht in Dunkelhain mit Sirra Von\'Indi.",
    ["T"] = "Das verwitterte Grab",
  },
  [226] = {
    ["D"] = "Sven und mir stehen jetzt gefährliche Tage ins Haus, wo doch der Totenbeschwörer im Osten sitzt und all das. Und weil wir hier draußen allein sind, müssen wir auch selbst jagen, wenn wir etwas essen wollen. Es kommt mir so vor, als würden jedes Mal, wenn ich mit etwas Fleisch zum Lager zurückgehe, verhungernde oder tollwütige Terrorwölfe aus dem Wald kommen, die auch etwas abhaben möchten. Ja, eines steht fest, das Leben hier draußen ist gefährlich!$B$BAber wenn Ihr einige der Wölfe für uns erledigen könntet, dann wäre das Leben für uns etwas leichter. Meist streunen sie nördlich und östlich von hier herum, in der Nähe des Flusses.",
    ["O"] = "Tötet 12 verhungernde Terrorwölfe und 8 tollwütige Terrorwölfe und kehrt dann zu Lars in Svens Lager an der westlichen Grenze des Dämmerwalds zurück.",
    ["T"] = "Wölfe sind uns auf den Fersen",
  },
  [227] = {
    ["D"] = "<Er sucht in den Regalen und kommt mit einem ledergebundenen Buch zurück.>$B$BWenn Ihr mehr wissen wollt, dann fragt Althea. Sie ist diejenige, die in letzter Zeit von dem... Ärger... mit ihm am meisten mitbekommen hat.",
    ["O"] = "Sprecht in Dunkelhain mit Kommandantin Althea Schwarzhaupt.",
    ["T"] = "Morgan Ladimore",
  },
  [228] = {
    ["D"] = "Meine Späher berichten mir, dass Mor\'Ladim durch den ganzen Dämmerwald wandert und auf dem Friedhof einen seltsamen, gewundenen Weg einschlägt. Wir haben ihn hinter dem Haus auf dem Hügel begraben, wisst Ihr, wo das ist?$B$BEs gibt nichts, was ich tun könnte, um Euch zu helfen, aber ich wünsche Euch viel Glück.",
    ["O"] = "Tötet Mor\'Ladim und bringt seinen Schädel nach Dunkelhain zu Kommandantin Althea Schwarzhaupt.",
    ["T"] = "Mor\'Ladim",
  },
  [229] = {
    ["D"] = "Ich hätte Euch dies schon früher sagen sollen...$B$BWie soll ich sagen...$B$BMorgan dachte zwar, dass seine ganze Familie umgekommen sei, aber seine Tochter Sarah Ladimore lebt noch: Sie ist jetzt eine Behüterin. Die... Umstände... unter denen ihr Vater zu Tode gekommen ist, haben sie immer sehr aufgewühlt. Vielleicht könntet Ihr zu ihr gehen und ihr mitteilen, was geschehen ist.",
    ["O"] = "Sprecht in Dunkelhain mit Behüterin Sarah Ladimore.",
    ["T"] = "Die überlebende Tochter",
  },
  [231] = {
    ["D"] = "Hier, nehmt dies und legt es auf sein Grab. Vielleicht... vielleicht weiß er dann irgendwie, dass es mir gut geht und dass keiner von uns ihn für das verantwortlich macht, was geschehen ist.$B$B<Jetzt hat sie Tränen in den Augen, als sie einen Ring von der rechten Hand zieht und ihn Euch übergibt.>$B$BUnd... danke, $N.",
    ["O"] = "Bringt Sarah Ladimores Ring zu Morgan Ladimores Grab.",
    ["T"] = "Die Liebe einer Tochter",
  },
  [232] = {
    ["D"] = "Die Königliche Apothekenvereinigung benötigt Eure Hilfe. Hört gut zu und wir können beide von meinem Plan profitieren.$B$BApotheker Keifer im nächsten Raum mag... begabt sein..., aber seine Forschungen könnten Früchte tragen, wenn sie von einem gewandten Forscher... oder einer gewandten Forscherin verfolgt würden. Zum Glück bin ich eine solche Forscherin. Bringt diese Bestellung zu Alessandro Luca, dem Eigentümer von Blaumond Dies-und-Das. Er wird Euch ein Testset geben. Wenn Ihr es habt, bringt es zu mir zurück und ich werde Euch Eure Aufgabe im Detail erläutern.",
    ["O"] = "Bringt die Bestellung nach Unterstadt zu Alessandro Luca.",
    ["T"] = "Kurierdienst für Apothekerin Zinge",
  },
  [233] = {
    ["D"] = "Hm, ich nehme nicht an, dass Ihr mir einen Gefallen tun würdet, $GJungchen:Mädchen;? Heute kam ein Stapel Briefe durch den Pass, aber ich habe nicht die Zeit, sie weiterzuleiten.$B$BSie sind alle an Talin Kühnauge adressiert. Ihr findet ihn ein Stück entlang der Straße im Westen. Er hat sein Lager am gefrorenen See aufgeschlagen.$B$BWas meint Ihr?",
    ["O"] = "Liefert den Stapel Briefe bei Talin Kühnauge im Eisklammtal ab.",
    ["T"] = "Postbeförderung im Eisklammtal",
  },
  [234] = {
    ["D"] = "Wenn ich mich recht erinnere, hat Grelin sein Lager im Südosten, ein Stück die Straße hinunter. Ich bin sicher, er wartet schon sehnsüchtig auf seine Post.",
    ["O"] = "Liefert den Brief bei Grelin Weißbart im Eisklammtal ab.",
    ["T"] = "Postbeförderung im Eisklammtal",
  },
  [235] = {
    ["D"] = "Achtung, junge Abenteurer! Die Wildnis des Eschentals erwartet Euch!$B$BDie Horde hat eine starke Präsenz in den Ländern nördlich des Brachlandes aufgebaut. Unsere beiden Außenposten - der Splitterholzposten und der Zoramstrandposten - wollen der Horde zu Ruhm gereichen! Diejenigen, die sich beweisen möchten, sollten dort nach Anleitung suchen. Wichtiger Hinweis: Senani Donnerherz im Splitterholzposten, direkt nördlich des Brachlandes, sucht Abenteurer, die an einer großen Jagd im Eschental teilnehmen wollen!",
    ["O"] = "Sprecht mit Senani Donnerherz im Splitterholzposten im Eschental.",
    ["T"] = "Die Jagd im Eschental",
  },
  [236] = "_",
  [237] = {
    ["D"] = "Gebirgsjäger Feuerkiesel hat nur Gutes über Euch zu berichten gehabt, $R. Daher werde ich Euch eine Mission von äußerster Wichtigkeit anvertrauen. Wir müssen den Druck auf die eindringenden Troggstreitkräfte aufrechterhalten, bis unsere Brüder, die Zwerge, von der Front der Allianz zurückkehren.$B$BBegebt Euch in die südlichen Hügel und tötet 10 Schädelhauer der Splittersteintroggs und 10 Seher der Splittersteintroggs. Eure Angriffe werden uns etwas Zeit verschaffen. Erstattet Bericht, sobald Ihr die Aufgabe erfüllt habt.",
    ["O"] = "Gebirgsjäger Kieselwut im südlichen Wachturm möchte, dass Ihr 10 Schädelhauer der Splittersteintroggs und 10 Seher der Splittersteintroggs tötet und ihm anschließend Bericht erstattet.",
    ["T"] = "Verteidigung der Länder des Königs",
  },
  [238] = {
    ["D"] = "Das Set ist fertig, $N. Die Apothekerin Zinge wird es zweifellos so schnell wie möglich haben wollen. Bringt es ihr ohne Verzögerung und lasst sie wissen, dass ich die Kosten für das Set mit auf die Rechnung der Gesellschaft schreiben werde. Richtet ihr bitte außerdem meine wärmsten Grüße aus.$B$BAlso los, steht nicht einfach so rum. So ist nun mal das Leben eines einfachen Kuriers, stimmt\'s?",
    ["O"] = "Bringt das Feldprobenset nach Unterstadt zu Apothekerin Zinge.",
    ["T"] = "Kurierdienst für Apothekerin Zinge",
  },
  [239] = {
    ["D"] = "Die Garnison an unserer westlichen Grenze berichtet von steigenden Aktivitäten der Gnolle und der Diebe. Sie bitten uns um die Entsendung weiterer Soldaten aus Sturmwind... aber wir können einfach keine entbehren!$B$BWenn Ihr helfen könntet - wir könnten die Hilfe wirklich gebrauchen. Geht, sprecht mit Stellvertreter Rainer in der Weststromgarnison und fragt ihn, was getan werden muss.$B$BDie Garnison liegt an der Straße im Westen. Wenn Ihr die Brücke über den kleinen Bach überquert habt, liegt sie auf der rechten Seite.",
    ["O"] = "Begebt Euch zur Weststromgarnison und sprecht mit Stellvertreter Rainer.",
    ["T"] = "Hilfe für die Weststromgarnison",
  },
  [240] = {
    ["D"] = "Und voilà - \"Krebs\"-Küchlein à la Grual!",
    ["O"] = "Kehrt mit den schwärzlichen Krebsküchlein zu Bibbers zurück.",
    ["T"] = "Rückkehr zu Bibbers",
  },
  [241] = "_",
  [242] = "_",
  [243] = {
    ["D"] = "Ihr werdet Euch in die Tanariswüste begeben und die Gewebebeschaffenheit der Basilisken, Skorpiden und Hyänen testen, die dort heimisch sind. Beschafft Gewebeproben und untersucht sie mit dem Testset; Ihr müsst davon ausgehen, dass eine Reihe der Proben unnütz sind. Ich benötige von jeder Kreaturenart acht akzeptable Proben.$B$BAußerdem verfügen die Goblins von Gadgetzan über eine Kraftquelle, die mit dieser Art von modifiziertem Set verwendet werden muss. Sprecht mit Chefingenieur Bilgenritzel und erwerbt eine für das Set.$B$BKehrt erst zu mir zurück, wenn Ihr erfolgreich wart.",
    ["O"] = "Sprecht in Gadgetzan mit Chefingenieur Bilgenritzel über eine Kraftquelle für das Testset.",
    ["T"] = "Auf ins Feld",
  },
  [244] = {
    ["D"] = "Seid gegrüßt, $C. Meine Aufgabe ist es, auf diesem Stück Straße zu patrouillieren. Obwohl die Straße im Augenblick sicher ist, habe ich nördlich und östlich von hier einige Lager von Gnollen gesehen.$B$BSeenhain muss erfahren, dass die Gnollstreitkräfte sich sammeln! Meldet Euch bei Stellvertreter Feldon in Seenhain und berichtet ihm von den Gnollen. Wenn Ihr dies tut, dann wird Feldon Euch bestimmt den Sold eines Spähers zahlen.$B$BGewöhnlich inspiziert Feldon das Gebiet südlich der Brücke nach Seenhain.",
    ["O"] = "Erstattet Stellvertreter Feldon Bericht. Zu finden ist er an der Brücke südlich von Seenhain im Rotkammgebirge.",
    ["T"] = "Eindringende Gnolle",
  },
  [245] = {
    ["D"] = "Seid gegrüßt, $C. Vielleicht habt Ihr die Spinnen gesehen, die die Grenze im Westen überrannt haben? Mit den achtbeinigen Monstern werden wir nicht fertig und der Kommandant hat nicht genug Leute, um sie so weit weg von Dunkelhain einsetzen zu können.$B$BIch bitte Euch wirklich nur sehr ungern darum, aber Ihr seht aus, als könntet Ihr mit ihnen fertigwerden. Ich kann Euch keine großen Schätze versprechen, aber ich kann Euch belohnen, wenn Ihr dabei helft, die dreckigen Biester auszurotten.$B$BAch, ein Ratschlag noch: Weicht ihrem Gift aus, wenn Ihr könnt.",
    ["O"] = "Erledigt 15 Zwerggiftweberspinnen und meldet Euch dann wieder bei Behüter Dodds im Dämmerwald.",
    ["T"] = "Gefahr auf acht Beinen",
  },
  [246] = {
    ["D"] = "Wir können keinen der Männer hier entbehren, aber mit diesen Gnollen, von denen Ihr mir berichtet habt, muss etwas passieren.$B$BSpäht das südliche Rotkammgebirge nach Gnollen aus. Bedrängt sie, tötet so viele Ihr könnt und erstattet mir dann Bericht über ihre Anzahl. Gebt mir auch eine Einschätzung der Gefahr, die sie darstellen. Möglicherweise findet Ihr Lager dieser Gnolle entlang der südlichen Straße im Rotkammgebirge.$B$BTut dies für die Armee von Sturmwind, $N, und Ihr erhaltet eine Belohnung.",
    ["O"] = "Tötet 10 Bastarde der Rotkammgnolle und 6 Wilderer der Rotkammgnolle und meldet Euch anschließend wieder bei Stellvertreter Feldon in Seenhain.",
    ["T"] = "Einschätzung der Gefahr",
  },
  [248] = {
    ["D"] = "Seit einer Weile schon beobachte ich den Turm von Ilgalar und ich habe herausgefunden, dass Morganth im Konflikt mit den Orcs von Burg Steinwacht liegt. Den Grund für ihren Streit kenne ich nicht, aber vielleicht könnt Ihr mir ja dabei helfen, diesem Rätsel auf die Spur zu kommen.$B$BNehmt eine weitere Glyphe von Azora und begebt Euch dann auf die Spitze des unversehrten Wachturms neben der Burg Steinwacht.$B$BFindet einen passenden Gegenstand, auf dem Ihr die Glyphe ablegen könnt. Eine Truhe oder ein Krug - irgendein Ort, an dem die Glyphe nicht auffällt - ist für meine Zwecke geeignet.",
    ["O"] = "Steigt in den unversehrten Wachturm neben der Burg Steinwacht im Rotkammgebirge hinauf.$B$BFindet einen passenden Behälter für die Glyphe von Azora und legt diese dort ab.",
    ["T"] = "Weitere Beobachtungen",
  },
  [249] = {
    ["D"] = "Nach langen Beobachtungen habe ich jetzt Morganths Pläne durchschaut.$B$BEr sucht nach der Sichel von Elune... und vielleicht hat er sie bereits gefunden! Wenn dem so ist, dann werden das Rotkammgebirge und das Königreich Sturmwind schon bald seine dunkle Macht zu spüren bekommen.$B$BIch selbst bin leider nicht bereit, mich gegen Morganth zu stellen, doch ich fürchte, er muss genau jetzt aufgehalten werden. Daher müsst Ihr Euch Zutritt zum Turm von Ilgalar verschaffen und Morganth entgegentreten.$B$BBesiegt ihn und bringt mir seinen Schattenanhänger. Wenn Ihr dies vermögt, dann schuldet das Königreich Euch viel.",
    ["O"] = "Besiegt Morganth.$B$BBringt seinen Schattenanhänger zu Theocritus am Turm von Azora im Wald von Elwynn.",
    ["T"] = "Morganth",
  },
  [250] = {
    ["D"] = "Den Sternen sei Dank, jemand sorgt sich um den Damm. Erst die Zerstörung des Thandolübergangs, dann die Plünderung von Dun Modr! Ich hege keinerlei Zweifel, dass der Damm das Ziel des nächsten Anschlags der Dunkeleisenzwerge sein wird. Der größte Teil meiner Sicherheitsleute wurde an die Front der Allianz versetzt, einschließlich meines besten Kontrolleurs.$B$BUnd deswegen brauche ich Euch, $N. Ich habe einige Pioniere der Dunkeleisenzwerge gesehen, die an der östlichen Rampe des Damms herumlungerten. Untersucht den Bereich und bringt einen Hinweis zurück.",
    ["O"] = "Untersucht den Bereich um die östliche Dammrampe und bringt Chefingenieur Hinderweir einen Hinweis, der Auskunft darüber geben könnte, was die Dunkeleisenzwerge vorhaben.",
    ["T"] = "Eine dunkle Bedrohung",
  },
  [252] = {
    ["D"] = "Oh je! Wenn das, was hier steht, wahr ist, dann hat Calvinius etwas... Unsägliches getan.$B$BBringt die übersetzte Notiz besser schnell zu Ello zurück.$B$BBeeilt Euch, denn ich fürchte, Unheil rollt bereits auf unsere Stadt zu...",
    ["O"] = "Bringt Lord Ello Schwarzhaupt den übersetzten Brief.",
    ["T"] = "Übersetzung für Ello",
  },
  [253] = {
    ["D"] = "Der Einbalsamierer... diesen Namen kennen wir aus einer Geschichte, die wir unseren Kindern erzählen. Er war ein freundlicher Alchemist, den der Tod seiner Frau Eliza in den Wahnsinn trieb. Er wollte sie wiederbeleben und so wandte er schwarze Magie an und pflanzte sein eigenes Herz in die Brust seiner toten Gattin ein.$B$BDies stellte Eliza wieder her, doch nun war sie verflucht... ein unstillbarer Hunger auf Menschenfleisch hatte sich ihrer bemächtigt und der Einbalsamierer musste sie in ihrem Grab liegen lassen.$B$BSeine Macht ruht in seinem Herzen. Findet das Grab seiner Frau, holt sein Herz und bringt es mir, solange es noch schlägt. Wenn Ihr dies tut, wird seine Macht gebrochen sein.",
    ["O"] = "Findet Elizas Grab. Beschafft das Herz des Einbalsamierers, das sie in sich trägt, und kehrt zu Ello Schwarzhaupt zurück.",
    ["T"] = "Braut des Einbalsamierers",
  },
  [255] = {
    ["D"] = "Tiere zu jagen wird nach einer Weile langweilig. Mir steht der Sinn nach größerer Beute...viel größerer. Oger, um genau zu sein.$B$BDie hiesigen Zwerge machen sich Sorgen über die Festung Mo\'grosh im Nordwesten, haben aber nicht die Truppenstärke, um viel zu unternehmen. Wenn Ihr bei meiner Jagd mitmachen wollt, könnt Ihr gern jeden Oger töten, dem Ihr begegnet. Bringt mir Kunde von Euren Taten und ich teile die Belohnung mit Euch, die ich mit dem Magistraten vereinbart habe.",
    ["O"] = "Kelt Thomasin vom Mo\'grosh Lager hat Euch angeheuert, 4 Oger der Mo\'grosh, 4 Schläger der Mo\'grosh und 4 Vollstrecker der Mo\'grosh zu töten.",
    ["T"] = "Söldner",
  },
  [256] = {
    ["D"] = "Auf Befehl des Magistrats von Thelsamar:$B$BDer Tod von Chok\'sul, dem Oger und mutmaßlichen Anführer und Kopf hinter den Angriffen auf die Stadt Thelsamar, den Steinwerkdamm und die Ausgrabungsstätte, wird unter allen Umständen gewünscht. Es darf zu jedem Mittel gegriffen werden, das notwendig ist.$B$BEine Barentschädigung kommt zur Auszahlung, wenn der Magistrat einen Beweis für Chok\'suls Tod erhält.$B$BZuletzt wurde Chok\'sul am Ogerlager im nordöstlichen Teil von Loch Modan gesehen.",
    ["O"] = "Tötet Chok\'sul und bringt Magistrat Stumpfnase seinen Kopf.",
    ["T"] = "GESUCHT: Baron Küstenschipper",
  },
  [257] = {
    ["D"] = "Ihr glaubt wohl, Ihr könnt Euch mit Daryl dem Kühnen messen, hm? Das will ich auch nicht hoffen! Aber Ihr dürft es natürlich gern versuchen, wenn Ihr denn meint... Hier habt Ihr eine Herausforderung, der Ihr bestimmt nicht gewachsen seid. Also nehmt\'s nicht zu schwer, wenn Ihr damit nicht fertigwerdet.$B$BEin Schwarm Bussarde nistet hier am Loch Modan. Warum versucht Ihr nicht, einige von denen zu erledigen? Ich sag Euch was. Wenn Ihr meine Herausforderung annehmt, dann gebe ich Euch meinen Bogen oder mein Gewehr.$B$B<Er mustert Euch abschätzig.>$B$BScheint ja nicht, als ob Ihr viel zu verlieren hättet.",
    ["O"] = "Tötet 8 Bergbussarde und kehrt zu Daryl dem Jüngling in die Jagdhütte der Weltenwanderer zurück.",
    ["T"] = "Das Prahlen eines Jägers",
  },
  [258] = {
    ["D"] = "$Nath, stimmt\'s? Ich seh schon, Ihr platzt fast vor Stolz, dass Ihr den ersten Test bestanden habt. Aber ich hab Euch ja auch schon gesagt, dass das keine große Tat war.$B$BIhr solltet Euch mal an der Eberjagd versuchen. Glaubt mir, das sind andere Eber als im Eisklammtal, also solltet Ihr Euch vorsehen.$B$BSeid nicht zu traurig, wenn Ihr versagt. Ich würde Euch mein letztes Hemd geben, wenn Ihr es schaffen würdet!$B$BHabe ich Euch schon mal erzählt, wie ich an meine berühmte Narbe gekommen bin? Nein? Also, das war vor zwei Jahren...",
    ["O"] = "Tötet 10 alte Bergeber und kehrt zu Daryl dem Jüngling in die Jagdhütte der Weltenwanderer zurück.",
    ["T"] = "Die Herausforderung eines Jägers",
  },
  [259] = "_",
  [260] = "_",
  [261] = {
    ["D"] = "Ich werde offen mit Euch sein. Wir sind im Krieg mit der Geißel. Sie ist ein Übel, das unser Volk verderbt und unser Land infiziert. Sie muss aufgehalten werden, bevor sie uns übermannt und unsere Welt mit Schatten überzieht. Wir vom Scharlachroten Kreuzzug haben geschworen, die Geißel mit Leib und Seele zu bekämpfen.$B$BWenn Ihr auf unserer Seite steht, dann nehmt Euren Mut zusammen und beweist Eure Zugehörigkeit – zerstört die untoten Verheerer von Desolace im südlich gelegenen Tal der Knochen.$B$BTut dies, und der Kreuzzug wird Euch mit offenen Armen aufnehmen.",
    ["O"] = "Vernichtet 30 untote Verheerer und kehrt dann zu Bruder Anton auf die Nijelspitze zurück.",
    ["T"] = "Auf dem Scharlachroten Pfad",
  },
  [262] = {
    ["D"] = "Ich muss wissen, wen ich in der Nacht, in der meine Familie ermordet wurde, in den Schatten habe herumschleichen sehen.$B$BBitte, $N, Ihr habt euren Einfallsreichtum bereits bewiesen. Bitte findet diese schattenhafte Gestalt. Ich muss wissen, was sie für Geheimnisse kennt!$B$BIch habe keine Hinweise, die Ihr nicht bereits hättet, aber hier - nehmt das Buch, das Ihr auf meinem Hof gefunden habt. Vielleicht können einige Stadtbewohner das Buch mit seinem Besitzer in Verbindung bringen.",
    ["O"] = "Nehmt das Buch, das Ihr auf Svens Hof gefunden habt, und zeigt es den Einwohnern von Dunkelhain.",
    ["T"] = "Die Schattengestalt",
  },
  [263] = {
    ["D"] = "Die Gebirgsjäger sagen mir, dass Ihr recht tapfer und fähig seid, $N. $GEin:Eine:r; $R wie Ihr wird hier draußen gebraucht. Wir bekommen das Troggproblem einfach nicht unter Kontrolle. Die Reserve wurde zur Front gerufen, und wir sind hier auf uns allein gestellt. Aber jetzt seid ja Ihr hier, $Gein erfahrener:eine erfahrene:c; $C, also wollen wir doch mal sehen, was Ihr ausrichten könnt.$B$BZieht los und tötet 8 Schamanen der Splittersteintroggs und 8 Knochenknacker der Splittersteintroggs. Wir werden sehen, ob Ihr Eurem Ruf gerecht werdet, $GTroggtöter:Troggtöterin;.",
    ["O"] = "Gebirgsjäger Wallbang im südlichen Wachturm möchte, dass Ihr 8 Schamanen der Splittersteintroggs und 8 Knochenknacker der Splittersteintroggs tötet.",
    ["T"] = "Verteidigung der Länder des Königs",
  },
  [264] = {
    ["D"] = "Dreckiger Abschaum! Lässt mich wegen seines verdammten Kreuzzugs hier sitzen. Nur, weil \"das Licht das Wichtigste ist, was wir gegen die Bedrohung durch die Untoten besitzen\".$B$BUnd was ist mit seinen Kindern?! Was ist mit MIR?! Nacht für Nacht hab ich hier gesessen und geduldig auf ihn gewartet... hab immer den zweiten Platz hinter seiner verfluchten Pflicht eingenommen!$B$BTja, hier könnt Ihr sehen, was Euch Gerechtigkeit einbringt, $N! Er ist tot und ich ertrage genau das, was er aufzuhalten versuchte!$B$BNehmt diesen Plunder und legt ihn auf seine Gruft im Grabmal. Ich will nichts mehr damit zu tun haben... oder mit ihm!",
    ["O"] = "Legt Clarices Anhänger auf Yurivs Grab im Silberwald.",
    ["T"] = "Bis dass der Tod uns scheidet",
  },
  [265] = {
    ["D"] = "Hier habt Ihr Euer Buch zurück, $N, und viel Glück noch bei Eurer Suche. Vielleicht können Euch ja andere in der Stadt mehr Hinweise geben, wenn Ihr sie fragt.$B$BIch habe jedenfalls noch einen für Euch - obwohl der Autor dieses Buches in Dunkelhain fremd ist, wurde das Buch selbst hier gekauft.",
    ["O"] = "Überprüft, ob andere Einwohner Hinweise auf die schattenhafte Gestalt haben.",
    ["T"] = "Die Schattensuche geht weiter",
  },
  [267] = {
    ["D"] = "$C, Ihr seid Euch vielleicht nicht unbedingt der Bedrohung durch die Troggs bewusst, die ihren Schatten über das Land der Zwerge wirft. Nachdem die Reserve von Eisenschmiede zur Front der Allianz gerufen wurde, bleibt uns nur ein Bruchteil der Verteidigungskräfte, die nötig wären, um dieses Land zu schützen. Meinem Regiment wurde aufgetragen, über das Tor hier zu wachen, und wir können unseren Posten aufgrund der Gefahr einer Invasion nicht verlassen.$B$BEs muss aber etwas Druck auf diese verdammten Troggs ausgeübt werden, die in den Hügeln lauern. Wenn Ihr Euch dem gewachsen fühlt, startet einen Angriff auf die Troggs. Bringt mir 8 Troggsteinzähne als Beweis.",
    ["O"] = "Bringt Hauptmann Rugelfuß im südlichen Wachturm 8 Troggsteinzähne.",
    ["T"] = "Die Bedrohung durch die Troggs",
  },
  [268] = {
    ["D"] = "Das Buch, das Ihr gefunden habt, war nur zur Hälfte fertig geschrieben. Ich musste es auf Svens Hof zurücklassen, als ich hierher floh... $B$BHier. Nehmt mein vollständiges Tagebuch und bringt es zu Sven. Es beschreibt wesentlich genauer, was auf seinem Hof passierte.$B$BObwohl dieses Tagebuch in den letzten Wochen meine einzige Gesellschaft war, gebe ich es gern auf, wenn ich mir dadurch erspare, Sven und seiner Wut zu begegnen.",
    ["O"] = "Kehrt mit Bibbers\' vollständigem Tagebuch zu Sven zurück.",
    ["T"] = "Rückkehr zu Sven",
  },
  [269] = {
    ["D"] = "Geht nach Sturmwind und sprecht dort mit Bischof Heller in der Kathedrale des Lichts. Gebt ihm Bibbers\' Tagebuch und sagt ihm, dass Ihr vorhabt, Euch Morbent Teufel zu stellen.$B$BAuch wenn Sturmwind Dunkelhain aufgegeben hat, ist Heller doch eine gute Seele und sehr weise. Er könnte Euch bei Eurem Versuch, uns zu retten, behilflich sein.$B$BEure Mission ist gefahrvoll, aber beeilt Euch dennoch, $N. Mit jeder verstreichenden Stunde verfinstern sich die Schatten über dem Dämmerwald mehr und mehr.",
    ["O"] = "Gebt Bischof Heller in Sturmwind Bibbers\' vollständiges Tagebuch.",
    ["T"] = "Auf der Suche nach Weisheit",
  },
  [270] = {
    ["D"] = "Geht zum Hafen von Menethil im Sumpfland und sprecht mit Glorin Stahlbraue.",
    ["O"] = "Geht zum Hafen von Menethil im Sumpfland und sprecht mit Glorin Stahlbraue.",
    ["T"] = "Die Verlorene Flotte",
  },
  [271] = {
    ["D"] = "Ihr seid nicht zufällig daran interessiert, es unserem ach so kühnen Kühnen ein wenig heimzuzahlen? Es schmerzt mich, das zu sagen, aber Daryl hat ein gewisses Talent.$B$BVor einem Jahr war ein großer Bär das Ziel der Jagdhütte der Weltenwanderer, aber keiner der Jäger hatte Glück mit ihm. Also dachte sich Daryl, er könnte sich mal an ihm versuchen. Das Einzige, was er nach seinem Versuch vorweisen konnte, war die hässliche Narbe über seinem Auge.$B$BErlegt den Bären und ich bin mir sicher, dass sogar Daryl mal sprachlos wäre.$B$BAlte Rußtatze, der Bär, hält sich irgendwo in der Nähe seines Baus nördlich von hier auf.",
    ["O"] = "Tötet alte Rußtatze und zeigt Daryl dem Jüngling in der Jagdhütte der Weltenwanderer dann Euer Werk.",
    ["T"] = "Vyrins Rache",
  },
  [272] = {
    ["D"] = "Für die zweite Prüfung benötigt Ihr die zwei Hälften, aus denen der Anhänger des Seelöwen besteht. Die eine Hälfte bezieht ihre Kraft aus der Beweglichkeit des Seelöwen-Aspektes beim Gleiten durchs Wasser; die andere Hälfte bezieht ihre Kraft aus der unglaublichen Ausdauer des Seelöwen-Aspektes. Druiden bedienen sich dieser beiden Eigenschaften gleichermaßen, um den Aspekt ihrer Wassergestalt zu verkörpern.$B$BSprecht mit den Bewohnern der Mondlichtung, um herauszufinden, wo sich die Teile befinden könnten, und bringt beide hierher, um sie miteinander zu vereinen. Bringt den Anhänger zu Dendrite Sternenschauer, wenn Ihr ihn geschaffen habt.",
    ["O"] = "Sucht die Anhängerhälfte der Wasserbeweglichkeit und die Anhängerhälfte der Wasserbelastbarkeit. Sprecht mit den Einwohnern der Mondlichtung, um Hinweise darauf zu erhalten, wo sich diese Gegenstände befinden könnten.$B$BFormt den Anhänger des Seelöwen aus den beiden Anhängerhälften. Ihr müsst in der unmittelbaren Nähe des Schreins von Remulos sein, um dies zu tun.$B$BBringt den zusammengefügten Anhänger zu Dendrite Sternenschauer nach Nachthafen auf der Mondlichtung.",
    ["T"] = "Prüfung des Seelöwen",
  },
  [273] = {
    ["D"] = "Es ist noch nicht zu spät, Ihr könnt gern losziehen und Huldar suchen. Er kann noch nicht sehr weit gekommen sein, denn so stark sie auch sein mögen, können Miran und Saean dennoch nicht allzu schnell laufen, wenn sie mit diesen Fässern vollgepackt sind.$B$BSie nehmen die übliche Route zur Ausgrabungsstätte, südwärts aus der Stadt raus und dann an der Krümmung des Lochs entlang.$B$BIhr solltet keine Probleme haben, sie zu finden, solange sie nicht bereits dort angekommen sind.",
    ["O"] = "Sprecht mit Huldar.",
    ["T"] = "Nachschub für die Ausgrabungsstätte",
  },
  [274] = {
    ["D"] = "Und jetzt bringt Ihr diese Mixtur schnell zum Chefingenieur zurück. Die Dunkeleisen werden keine Zeit verlieren, also werden wir das auch nicht.",
    ["O"] = "Ashlan Schmunzelstein möchte, dass Ihr mit der Entschärfungsmixtur zum Chefingenieur Hinderweir zurückkehrt.",
    ["T"] = "Eine dunkle Bedrohung",
  },
  [275] = {
    ["D"] = "Ich weiß nicht warum, aber irgendetwas schwärt im Sumpfland. Moorkrabbler haben sich dort ausgebreitet. Sie sind wie kranke, fiebrige Blasen auf dem Land, und um das Fieber zu besiegen, müssen sie erledigt und dem Sumpf wieder zugeführt werden.$B$BDie Moorkrabbler watscheln mitten in den Wasserwegen des Sumpflandes herum.",
    ["O"] = "Tötet 8 Moorkrabbler und kehrt dann zu Rethiel dem Sumpfhüter im Sumpfland zurück.",
    ["T"] = "Schwärendes Land",
  },
  [278] = {
    ["D"] = "Fangt gleich damit an, die Stoffe zum Entschärfen zusammenzusuchen, $N. Lauerergift kann von den einheimischen Spinnen gewonnen werden. Krokiliskentränen findet man ebenfalls hier im Loch. Ein Kristall der Mo\'grosh hingegen dürfte sehr schwierig zu bekommen sein. Die Oger im Nordosten bauen sie ab, aber die Kristalle sind ein sehr seltener Rohstoff.$B$BWir müssen einen Angriff der Dunkeleisenzwerge abwehren können! Kommt zurück, wenn Ihr alle erforderlichen Dinge zusammenhabt, und ich werde die Mixtur vorbereiten.",
    ["O"] = "Ashlan Schmunzelstein möchte, dass Ihr Lauerergift, einen Kristall der Mo\'grosh und eine Krokiliskenträne besorgt.",
    ["T"] = "Eine dunkle Bedrohung",
  },
  [279] = {
    ["D"] = "Murlocs kriechen aus den tiefen Wassern und bauen ihre Dörfer entlang der Küste. Sie überfallen unsere Fischer und Händler und müssen aufgehalten werden.$B$BEiner dieser Murlocs, Gobbler, streift mit den anderen Murlocs der Blaukiemen umher und plündert die Händler entlang der Straße aus, wobei sie sich danach immer in die Sicherheit der nahegelegenen Hütten zurückziehen. Unsere Händler sind in Gefahr, und wir sind bereit, für ihre Sicherheit zu zahlen.$B$BGobbler strolcht in der kleinen Bucht nördlich von hier herum. Findet ihn, erschlagt seine Artgenossen und bringt mir seinen Kopf.",
    ["O"] = "Tötet 12 Murlocs der Blaukiemen.$B$BErschlagt Gobbler und nehmt Euch seinen Kopf.$B$BBringt Gobblers Kopf zu Karl Boran in den Hafen von Menethil.",
    ["T"] = "Klauen aus der Tiefe",
  },
  [280] = {
    ["D"] = "Heute habe ich einige Aufrührer der Dunkeleisenzwerge gesehen, die mit einem großen Fass in Richtung Damm geschwommen sind. Es waren keine Gebirgsjäger zur Hand, um sie aufzuhalten! Ich fürchte, sie haben das Zephyrium am Fuße des Dammes deponiert, und es könnte jede Minute hochgehen!$B$BIhr müsst jetzt diese Mixtur mitnehmen und in das Fässchen einrühren, bevor es zu spät ist! Beeilt Euch!",
    ["O"] = "Chefingenieur Hinderweir will, dass Ihr zum Fuß des Dammes hinunterschwimmt, das Pulverfässchen ausfindig macht und die Entschärfungsmixtur hineinrührt, um eine Explosion zu verhindern.",
    ["T"] = "Eine dunkle Bedrohung",
  },
  [281] = {
    ["D"] = "Vor nicht allzu langer Zeit wurde ein Handelsschiff, die Blinde Prinzessin, von Murlocs überfallen. Obwohl das Schiff unversehrt entkam, wurde ein Großteil seiner Fracht von den Kreaturen über Bord gezogen, darunter eine äußerst wertvolle Statuette von Terenas Menethil. Wir möchten, dass die Statuette geborgen wird.$B$BDurchsucht die Hütten der Murlocs an der Küste nach Kisten, Fässern und anderen Spuren von verlorener Fracht. Findet die Statuette von Menethil und bringt sie mir wieder.",
    ["O"] = "Durchsucht die Kisten und Fässer in den Murloclagern an der Küste.",
    ["T"] = "Waren-Wiederbeschaffung",
  },
  [282] = {
    ["D"] = "Ich hatte ursprünglich vor, meinen Lehrling mit meinem Bericht zu meinem Bruder Senir zu schicken, aber ich würde mich viel besser fühlen, wenn er in etwas... verlässlicheren Händen wäre. Natürlich nur, wenn es Euch nichts ausmacht?$B$BHmm... Ihr müsstet den Tunnel nehmen, um nach Kharanos zu gelangen.$B$BSprecht mit Gebirgsjäger Thalos, bevor Ihr durch den Tunnel geht. Er ist mittlerweile vollständig von den Troggs überrannt.$B$BFolgt der Straße zurück nach Ambossar und geht dann weiter nach Osten zum Tunnel. Thalos ist ganz in seiner Nähe stationiert.",
    ["O"] = "Sprecht mit Gebirgsjäger Thalos.",
    ["T"] = "Senirs Beobachtungen",
  },
  [284] = {
    ["D"] = "Ihr durchsucht gründlich diese Ansammlung von Murlochütten, findet aber keine Spur der Statuette von Menethil.$B$BVielleicht gibt es in einer der beiden nahe gelegenen Hütten im Norden und Nordosten mehr Hinweise.",
    ["O"] = "Sucht nach der Statuette von Menethil.",
    ["T"] = "Die Suche geht weiter",
  },
  [285] = {
    ["D"] = "Dieses Grüppchen Murlochütten offenbart auch nach einer gründlichen Überprüfung nichts.$B$B... Aber Ihr seid Euch sicher, dass der nächste Anhaltspunkt, den Ihr findet, nützlich sein wird.$B$BEine der Hütten nördlich von hier muss die Statuette von Menethil enthalten!",
    ["O"] = "Sucht weiter in den Hütten der Murlocs nach der Statuette von Menethil.",
    ["T"] = "Suche in weiteren Hütten",
  },
  [286] = {
    ["D"] = "Nachdem Ihr das Fass aus dem Dreck gezogen und geöffnet habt, findet Ihr eine in dünnes Stroh eingepackte, silbrige Figur. Sie stellt einen jungen, langhaarigen Paladin mit erhobenem Hammer dar.$B$BDies kann nur die Statuette von Menethil sein! Ihr müsst sie nun zu Karl Boran in den Hafen von Menethil zurückbringen.",
    ["O"] = "Bringt Karl Boran im Hafen von Menethil im Sumpfland die Statuette von Menethil.",
    ["T"] = "Rückgabe der Statuette",
  },
  [287] = {
    ["D"] = "Ich bin mit meinem Bericht etwas im Rückstand und könnte Eure Hilfe brauchen, $N. Ich habe gerade herausgefunden, wo sich die Trolle verschanzt haben, aber ich habe so viele Trolle gesehen, dass ich mich nicht getraut habe, hineinzugehen.$B$BWas ich von Euch möchte, ist Folgendes: Geht runter zur Höhle, schaut Euch ein wenig darin um, tötet ein paar Trolle und kommt dann wieder hierher zurück.$B$BNehmt die Straße nach Norden, raus aus Kharanos. Sobald Ihr an der Brücke ankommt, folgt dem zugefrorenen Fluss westwärts, bis Ihr den Eiswellensee erreicht. Am linken Ufer werdet Ihr Bräuhall vorfinden. Die Höhle liegt südwestlich des Dorfes.",
    ["O"] = "Erforscht die Höhle der Frostmähnen und tötet 5 Kopfjäger der Frostmähnen für Senir Weißbart in Kharanos.",
    ["T"] = "Höhle der Frostmähnen",
  },
  [288] = {
    ["D"] = "Ich war einst Erster Maat auf der \'Herrin der Meere\', einem Linienschiff der Dritten Flotte von Kul Tiras, der Verlorenen Flotte. Verloren deshalb, weil die Flotte auf ihrer letzten Reise von roten Großdrachen angegriffen wurde. Großdrachen, die von Orcs beherrscht wurden!$B$BWir hatten keine Chance gegen das Drachenfeuer... und unsere Flotte ging just vor dieser Küste hier brennend unter. Ich bin der einzige Überlebende von diesen armen Schiffen.$B$B... Oh, mir brummt der Schädel! Ich werde einen frischen Drink brauchen, wenn ich mit diesem Kater klarkommen und meine Geschichte fortsetzen soll.",
    ["O"] = "Spendiert dem Ersten Maat Fitzsimmons einen Krug Met.",
    ["T"] = "Die Dritte Flotte",
  },
  [289] = {
    ["D"] = "<Gestärkt setzt Fitzsimmons seine Geschichte fort...>$B$BIch bin der einzige Überlebende der unglückseligen Dritten Flotte von Kul Tiras, die vor der Küste im Norden vernichtet wurde. Aber obwohl ich das einzige Mitglied der Flotte bin, das noch atmet, bin ich nicht der einzige, der noch herumläuft! Denn auf einem unserer Schiffe war ein großer und schrecklicher Schatz versteckt, und dessen unheimliche Macht hält die Seelen meiner Brüder in den verrottenden Wracks gefangen.$B$BWenn Ihr allerdings einige meiner Brüder befreien könnt, könnt Ihr vielleicht auch den Fluch brechen, der auf ihnen lastet.",
    ["O"] = "Vernichtet 13 verfluchte Seeleute, 5 verfluchte Marinesoldaten und den Ersten Maat Snellig. Bringt Snelligs Schnupftabakdose zum Ersten Maat Fitzsimmons im Hafen von Menethil.",
    ["T"] = "Die verfluchte Crew",
  },
  [290] = {
    ["D"] = "Um den Fluch zu brechen und alle meine Brüder zu befreien, müsst Ihr das... Ding..., das auf meinem Schiff verstaut ist, bergen und reinigen! Ich weiß nicht, was es war, aber ich weiß, dass es sicher in der Geldkassette des Schiffs verwahrt wurde, zu dem nur Kapitän Halyndor den Schlüssel hatte.$B$BMein alter Kapitän erscheint mir immer noch in meinen Träumen. Ich bin mir sicher, dass er auf meinem untergegangenen Schiff, der \'Kühnheit\', herumläuft. Besorgt Euch den Schlüssel von ihm und öffnet damit die Geldkassette der \'Kühnheit\'.$B$BAnschließend, wenn Ihr das verfluchte Ding habt, bringt es zu Glorin Stahlbraue, dem Priester, hier in der Stadt.",
    ["O"] = "Besorgt den Geldkassettenschlüssel der \'Kühnheit\' von Kapitän Halyndor.$B$BVerwendet den Schlüssel, um die Geldkassette der \'Kühnheit\' zu öffnen.",
    ["T"] = "Brechen des Fluchs",
  },
  [291] = {
    ["D"] = "Wunderbar! Überbringt Senator Barin Rotstein meinen Bericht. Er ist ein missmutiger Geselle, also stört Euch nicht an seinem wenig sonnigen Gemüt. Er ist in Eisenschmiede, in der Halle, in der König Magni Hof hält.$B$BIhr kennt den Weg nach Eisenschmiede nicht? Nehmt die Straße aus Kharanos heraus nach Norden, überquert die Brücke und folgt dann der Straße nach Osten den Berghang hinauf. Einige große Banner flankieren diese Straße.$B$BAch ja, und könntet Ihr es vielleicht vermeiden, zu erwähnen, dass Ihr mir geholfen habt? Ich kann die ja nicht glauben lassen, ich würde hier draußen nicht hart arbeiten, nicht wahr?",
    ["O"] = "Überbringt Senator Barin Rotstein in Eisenschmiede Senirs Bericht.",
    ["T"] = "Die Berichte",
  },
  [292] = {
    ["D"] = "In der offenen Truhe erblickt Ihr eine geschwärzte Kugel, die in einem gruseligen Licht pulsiert. Dies ist der Schatz, von dem Fitzsimmons gesprochen hat: ein verfluchtes Auge von Paleth.$B$BBringt das verfluchte Auge von Paleth zum Zwergenpriester Glorin Stahlbraue im Hafen von Menethil. Fragt ihn, wie man den Fluch des Auges brechen kann.",
    ["O"] = "Bringt Glorin Stahlbraue im Hafen von Menethil im Sumpfland das verfluchte Auge von Paleth.",
    ["T"] = "Das Auge von Paleth",
  },
  [293] = {
    ["D"] = "Ich kann den Fluch nicht von dem Auge von Paleth nehmen. Die Augen wurden von Menschenpriestern hergestellt, daher werdet Ihr nach Sturmwind gehen müssen, um jemanden zu finden, der Euch helfen kann. Sprecht mit Erzbischof Benedictus. Er steht der Kathedrale des Lichts in Sturmwind vor. Er kann helfen.$B$BDas hoffe ich jedenfalls, um Euretwillen.",
    ["O"] = "Bringt Erzbischof Benedictus das verfluchte Auge von Paleth. Ihr findet Benedictus in der Kathedrale des Lichts, in der Stadt Sturmwind.",
    ["T"] = "Die Reinigung des Auges",
  },
  [294] = {
    ["D"] = "Die Lage ist ernst, so viel ist sicher. Als wir diese Knochen freigelegt haben, hat das die Raptoren angelockt. Diese dreckigen Bestien haben meine Brüder getötet und mich, Merrin und den armen Ausgrabungsleiter hier festgesetzt.$B$BHelft mit, das Sumpfland von diesen Raptoren zu säubern, $N. Scheckige Raptoren und scheckige Kreischer befinden sich direkt westlich der Klippe hier. Tötet jeweils 10, wenn Ihr könnt.$B$BDas wäre ein guter Anfang für die Rache, die ich mir für sie ausgedacht habe.",
    ["O"] = "Ormer Eisenzopf von Whelgars Ausgrabungsstätte möchte, dass Ihr 10 scheckige Kreischer und 10 scheckige Raptoren tötet.",
    ["T"] = "Ormers Rache",
  },
  [295] = {
    ["D"] = "Es ist an der Zeit, diese abscheulichen Raptoren ihren Blutdurst bereuen zu lassen. Gleich da unten gibt es Dutzende von scheckigen Sensenklauen- und Scharfzahnraptoren. Lasst diese scheußlichen Kreaturen bezahlen, indem Ihr 10 von jeder Sorte erschlagt.",
    ["O"] = "Ormer Eisenzopf möchte, dass Ihr 10 scheckige Sensenklauen- und 10 scheckige Scharfzahnraptoren tötet und dann zu ihm zu Whelgars Ausgrabungsstätte zurückkommt.",
    ["T"] = "Ormers Rache",
  },
  [296] = {
    ["D"] = "Während Ihr da unten wart, ist mir aufgefallen, dass eines der Biester aus der Menge herausstach. Es war größer und bedrohlicher. Ich könnte wetten, das ist derjenige, der die anderen hierher geführt hat und für die Störung der Ausgrabungsstätte verantwortlich ist.$B$BIch erbitte eine letzte Aufgabe von Euch, $N. Sorgt dafür, dass Sarlatan seine gerechte Strafe erhält. Und wenn man die Schwere seiner Verbrechen bedenkt, bedeutet Gerechtigkeit in diesem Falle den Tod! Bringt mir eine seiner Krallen als Beweis, dass er tot ist.",
    ["O"] = "Ormer Eisenzopf von Whelgars Ausgrabungsstätte möchte, dass Ihr Sarlatan tötet und mit einer seiner Krallen zu ihm zurückkehrt, sobald Ihr die Aufgabe erledigt habt.",
    ["T"] = "Ormers Rache",
  },
  [297] = {
    ["D"] = "Vor einer Weile, kurz bevor die Troggs in der Stätte auftauchten, fanden wir eine große Anzahl merkwürdiger, verzierter Götzen Leider hatten wir keine Gelegenheit, sie zu untersuchen, denn kurz nach ihrer Entdeckung haben uns die Troggs aus den Ruinen verjagt! Und diese Götzen haben eine seltsame Wirkung auf die Troggs. Sie verfallen dadurch in Raserei!$B$BBringt mir 8 Götzen - ich möchte sie studieren, und ich will sie nicht mehr in den Händen der Troggs sehen! Ihr könnt die Götzen bei den Troggs finden, die die Stätte heimsuchen.",
    ["O"] = "Bringt Magmar Todesstreich an der Eisenband Ausgrabungsstätte 8 verzierte Steingötzen.",
    ["T"] = "Götzen sammeln",
  },
  [298] = {
    ["D"] = "Ich muss einen Fortschrittsbericht nach Thelsamar schicken - einen Fortschrittsbericht und eine Neufassung meiner Anfrage nach mehr Sprengpulver! Hier ist der Bericht. Bringt ihn Jern Hornhelm. Er ist unser örtlicher Kontaktmann in Thelsamar.$B$BKeine schwierige Aufgabe, aber sie muss erledigt werden.",
    ["O"] = "Bringt Eisenbands Fortschrittsbericht zu Jern Hornhelm in Thelsamar.",
    ["T"] = "Bericht über den Fortschritt der Ausgrabungen",
  },
  [299] = {
    ["D"] = "Kurz vor der Invasion habe ich eine große Schrifttafel gefunden, die man den Goazstein nennt. Der übersetzte Text reißt an 4 Stellen ab. Der Text spricht von einem \"göttlichen Plan\" und einer \"schicksalhaften Prophezeiung\".$B$BIch fürchte, uns läuft die Zeit davon. Ich bin zu alt, um hinunterzugehen und diesen Bestien zu trotzen. Aber Ihr seid stark.$B$BDurchkämmt die Ausgrabungsstätte und sucht die 4 fehlenden Tafelfragmente: Ados, Modr, Golm und Neru. Sucht sie in alten Artefakten oder dort, wo das Erdreich locker ist. Bringt sie mir, auf dass ich damit beginnen kann, ihre Geheimnisse zu enträtseln!",
    ["O"] = "Ausgrabungsleiter Whelgar wünscht, dass Ihr die Ausgrabungsstätte nach den 4 fehlenden Tafelfragmenten durchkämmt, Ados, Modr, Golm und Neru.",
    ["T"] = "Enthüllung der Vergangenheit",
  },
  [301] = {
    ["D"] = "Wir werden eine Bewilligung brauchen, um Eisenband mehr Sprengpulver zu beschaffen. Ihr müsst seinen Bericht zum Hauptquartier der Forscherliga in Eisenschmiede bringen.$B$BHier ist der Bericht. Gebt ihn Ausgrabungsleiter Sturmlanze und vergesst nicht, Euch zu ducken, nachdem Ihr ihn abgeliefert habt. Sturmlanze ist ein Hitzkopf, und es wird ihn nicht erfreuen, wenn er von Eisenbands langsamem Vorankommen an der Ausgrabungsstätte hört.$B$BIhr findet Sturmlanze in der Versammlungshalle der Forscher.",
    ["O"] = "Bringt Eisenbands Fortschrittsbericht zu Ausgrabungsleiter Sturmlanze.",
    ["T"] = "Meldung in Eisenschmiede",
  },
  [302] = {
    ["D"] = "Wenn Eisenband immer noch Sprengpulver braucht, dann, bei Magni, soll er es haben! Ich habe die Bewilligung bereits mit einem Boten nach Thelsamar zu Jern Hornhelm vorausgeschickt. Er wird das Pulver und andere Vorräte zum Transport zu Eisenband bereitstellen.$B$BAch, und noch was: Ich will, dass Ihr persönlich dafür Sorge tragt, dass diese Pulverlieferung ihren Bestimmungsort erreicht. Die letzte kam nicht an, also haltet Euren Verstand wach und Eure Augen offen.",
    ["O"] = "Sprecht in Thelsamar mit Jern Hornhelm.",
    ["T"] = "Pulver für Eisenband",
  },
  [303] = {
    ["D"] = "Der Krieg tobt daheim ebenso wie in der Fremde. Während mächtige Zwergenkrieger im Namen der Allianz in weit abgelegenen Gegenden ihr Leben lassen, fallen unsere eigenen Gebiete den bösen Taten der Dunkeleisenzwerge zum Opfer. Sie haben den Thandolübergang verwüstet. Dun Modr fiel kurz darauf.$B$BIhr müsst Kräfte sammeln und die Front zurückdrängen. Wenn Ihr 10 Dunkeleisenzwerge, 5 Tunnelgräber der Dunkeleisenzwerge, 5 Saboteure der Dunkeleisenzwerge und 5 Demolierer der Dunkeleisenzwerge töten könnt, dann kommen die Reserven vielleicht rechtzeitig, um den Vormarsch zu beenden.$B$BUnd jetzt beeilt Euch!",
    ["O"] = "Motley Garmason in Dun Modr möchte, dass Ihr 10 Dunkeleisenzwerge, 5 Tunnelgräber der Dunkeleisenzwerge, 5 Saboteure der Dunkeleisenzwerge und 5 Demolierer der Dunkeleisenzwerge tötet.",
    ["T"] = "Der Dunkeleisenkrieg",
  },
  [304] = {
    ["D"] = "Viele meiner Soldaten fielen in der Schlacht von Dun Modr. Mein eigener Bruder kam um, als die Westbrücke über dem Thandolübergang zerstört wurde. Jawohl, die Dunkeleisenzwerge sind eine teuflische Brut. Soweit Roggo das in Erfahrung bringen konnte, ist der Anführer dieses Verbrecherpacks im Sumpfland ein Hexenmeister namens Balgaras der Niederträchtige. Er ist allerdings ein ziemlicher Feigling. Er lässt seine Soldaten für sich kämpfen und versteckt sich selbst in seinem Lager auf dem Hügel der Düsterschmiede im Südosten.$B$BWenn Ihr diesen rückgratlosen Abschaum finden könnt, erschlagt ihn und bringt mir sein Ohr. Ich werde Euch dafür entlohnen, $C.",
    ["O"] = "Tötet Balgaras den Niederträchtigen und bringt Langzopf dem Grimmigen vor Dun Modr sein Ohr.",
    ["T"] = "Eine grausige Aufgabe",
  },
  [305] = {
    ["D"] = "Wie schön, Ihr müsst der Späher sein, den Langzopf zu schicken versprach. Ihr könnt Euch nicht vorstellen, wie besorgt ich war.$B$BMeine Frau, Merrin, hat mir im letzten Monat jede Woche Briefe geschickt. Sie gehört zu Whelgars Ausgrabungstrupp. Aber es sind fast zwei Wochen seit unserem letzten Briefwechsel vergangen. Irgendetwas ist bestimmt schief gelaufen!$B$BLangzopf versprach, dass er einen Späher schicken würde, der sich dort mal umsieht, wenn wir nicht bald etwas vom Trupp hören. Ich nehme mal an, dieser Späher seid Ihr, $N. Findet Merrin in der Ausgrabungsstätte im Osten!",
    ["O"] = "Tarrel Felsweber möchte, dass Ihr zur Ausgrabungsstätte reist und dort Kontakt mit Merrin Felsweber aufnehmt.",
    ["T"] = "Auf der Suche nach dem Ausgrabungstrupp",
  },
  [306] = {
    ["D"] = "Es war grauenhaft! Wir hatten einige seltene Artefakte enthüllt und fingen an, welche von diesen großen Knochen zu finden.$B$BBevor wir wussten, wie uns geschah, überrannten diese scheckigen Raptoren die Ausgrabungsstätte. Viele ließen ihr Leben.$B$BOrmer hier rettete - ganz tapfer - den Ausgrabungsleiter und mich. Er verteidigte uns, während wir Schutz suchten. Jetzt sind wir gefangen. Und unsere harte Arbeit liegt in Trümmern.$B$BHier, nehmt diese Nachricht mit zurück zu meinem süßen Tarrel. Sie wird ihm die Situation klarmachen.",
    ["O"] = "Kehrt mit Merrins Nachricht zu Tarrel Felsweber zurück. Zu finden ist er im Hafen von Menethil im Sumpfland.",
    ["T"] = "Auf der Suche nach dem Ausgrabungstrupp",
  },
  [307] = {
    ["D"] = "Die Silberbachmine im Osten hat sich schon vor langer Zeit erschöpft. Sie wurde vom Bund der Bergarbeiter in ein Lagerhaus umgewandelt, aber jetzt sind dort Kobolde eingezogen und befingern mit ihren schmutzigen Pfoten gute zwergische Werkzeuge!$B$BWir werden dieses Ungeziefer noch früh genug ausmerzen, aber der Bund wünscht, dass jemand ihre Gerätschaften aus der Mine schafft, bevor wir Krieger da reinstürmen und was kaputtmachen. Das wird ein harter Abstieg – Ihr solltet vielleicht Unterstützung mitnehmen.$B$BDie Ausstattung wird in Kisten des Bergarbeiterbunds über die Mine verteilt aufbewahrt. Viel Glück.",
    ["O"] = "Geht zur Silberbachmine und sucht 6 Sätze Minenarbeiterausstattung zusammen.$B$BKehrt dann zu Gebirgsjäger Sturmlanze zurück.",
    ["T"] = "Schmutzige Pfoten",
  },
  [309] = {
    ["D"] = "Wir müssen dieses Pulver zu Eisenband bringen, aber der Wagen fährt nirgendwo mehr hin. Es ist eine Menge zu schleppen und diese Gegend kann gefährlich sein; und wer weiß, was die Dunkeleisenzweige für uns auf Lager haben nun da Saean zu ihnen gestoßen ist.$B$BMir wäre viel leichter zumute, wenn Ihr mit mir Ausschau halten könntet. Miran kann die Pulverfässer im Lager abladen während Ihr und ich den Wagen bewachen.",
    ["O"] = "Beschützt Huldar und den Eisenbandkonvoi und erstattet anschließend Ausgrabungsleiter Eisenband Bericht.",
    ["T"] = "Beschützen der Fracht",
  },
  [310] = {
    ["D"] = "Ich muss zugeben, diese Donnerbräus stellen ein paar gute Getränke her. Aber sie müssen noch lernen, dass ihres nicht das einzige ordentliche Gebräu ist! Vielleicht könnt Ihr mir helfen, ihnen diese Lehre zu erteilen...$B$BHier, nehmt dieses Fass mit Gerstenbräu. Schleicht Euch in den Keller der Brauerei Donnerbräu in Kharanos und vertauscht es mit einem ihrer Fässer Donnerbräu Lager. Dann werden wir ja sehen, ob ihre Kundschaft mein Gebräu lieber mag als ihres!$B$BAch ja, und falls die Fässer bewacht sein sollten, müsstet Ihr eventuell den Wächter irgendwie ablenken...",
    ["O"] = "Vertauscht im Keller der Brauerei Donnerbräu in Kharanos ein Fass Donnerbräu Lager mit einem Fass Gerstenbräu.",
    ["T"] = "Bittere Rivalen",
  },
  [311] = {
    ["D"] = "Nachdem Ihr ein Fass von Marleths Gerstenbräu im Keller der Brauerei Donnerbräu abgestellt habt, schnappt Ihr Euch zum Beweis ein Fass Donnerbräu Lager...",
    ["O"] = "Bringt Marleth Gerstenbräu in Bräuhall das Fass mit Donnerbräu Lager.",
    ["T"] = "Rückkehr zu Marleth",
  },
  [312] = {
    ["D"] = "Ich hatte einen Monatsvorrat Trockenfleisch gesalzen und für die kalte Jahreszeit weggeschlossen. Weggeschlossen deshalb, damit die Bären nicht rankommen! Aber während ich unterwegs auf der Jagd war, hat sich diese Bestie, Eisbart der Alte, mit meiner Fleischtruhe davongemacht. Er wird allerdings nicht durch das Thorium kommen. Ich wiederum werde verhungern, wenn ich mein Fleisch nicht wiederbekomme.$B$BIhr seht gerissener aus als ich. Vielleicht könnt Ihr meinen Vorrat wiederbeschaffen? Ich würde es mit meinem steifen Bein niemals schnell genug in die Höhle hinein und wieder heraus schaffen.",
    ["O"] = "Holt Tundra MacGranns Trockenfleisch aus der gestohlenen Fleischtruhe in der Höhle von Eisbart dem Alten.",
    ["T"] = "Tundra MacGranns gestohlene Vorräte",
  },
  [313] = {
    ["D"] = "Der Kataklysmus hat die Troggs in Scharen an die Oberfläche getrieben, sodass die Frostmähnentrolle um ihr Leben fliehen mussten. Leider haben sie sich entschieden, sich direkt vor unserer Nase niederzulassen und uns Ärger zu bereiten.$B$BIch habe Gebirgsjäger Dunstan und seinen Trupp zum Graufelsbau südwestlich der Stadt geschickt, um dort ein Auge auf die Wendigos zu haben, jetzt aber brauchen wir sie hier in Kharanos. Macht sie ausfindig und sagt ihnen, sie sollen diese ferngesteuerten Beobachtungsbots aufstellen und dann nach Kharanos zurückkehren.",
    ["O"] = "Leitet Hauptmann Tharrans Befehle an Gebirgsjäger Dunstan, Gebirgsjäger Lewin und Gebirgsjäger Valgrum im Graufelsbau weiter.",
    ["T"] = "Zum Zusehen verdammt",
  },
  [314] = {
    ["D"] = "Wir haben mitten in der Nacht die Schreie gehört. Heute morgen fehlten der Herde dann tatsächlich zwei Widder. Diese heimtückische Bestie, Vagash genannt, hat sich über unsere Existenzgrundlage hergemacht. Da der Großteil von König Magnis Armee irgendwo in weit entfernten Landen an der Seite der Allianz kämpft, ist niemand mehr da, der Vagash in Schach halten könnte.$B$BVielleicht seid Ihr tapfer genug, die Bestie aufzuspüren und zu erschlagen. Bringt mir einen seiner Fangzähne, und ich werde Euch belohnen. Vagash lauert direkt oberhalb des Hofs hier, aber seid gewarnt, die Bestie ist tödlich.",
    ["O"] = "Erhaltet einen Fangzahn von Vagash.",
    ["T"] = "Die Herde beschützen",
  },
  [315] = {
    ["D"] = "Ich bin von einem Vorhaben besessen, dem Vorhaben, das perfekte Starkbier herzustellen. Ich weiß, dass ich es schaffen kann - das Brauen liegt mir im Blut. Ich muss nur das richtige Rezept finden...$B$BWeit oben in den Bergen im Nordwesten, auf dem Schimmergrat, bauen die Trolle der Frostmähnen eine Pflanze an, Schimmerkraut genannt. Sie verwenden sie in ihren seltsamen Stammesritualen. Wir Zwerge haben bisher keine wirkliche Verwendung dafür gefunden, aber sie hat einen einzigartigen Geschmack... und ich möchte damit in meinen Gebräuen herumexperimentieren.$B$BDas Schimmerkraut zu bekommen wird schwierig werden, aber ich werde Euch für jedes Schimmerkraut, das ihr aus den Körben im Trolldorf \"borgen\" könnt, gut bezahlen.",
    ["O"] = "Bringt Rejold Gerstenbräu in Kharanos 7 Schimmerkräuter.",
    ["T"] = "Das perfekte Starkbier",
  },
  [316] = "_",
  [317] = {
    ["D"] = "Ich bereite mich gerade darauf vor, eine Mission für die Belagerungsbrigade in Angriff zu nehmen. Sie wird ziemlich lange dauern, und ich muss Dampfstrahl mit Vorräten für einen ganzen Monat beladen. Könntet Ihr also nicht, während Stahlrost an meinem Panzer arbeitet, ein wenig für mich auf die Jagd gehen?$B$BIch werde ein paar Pelze als Bettzeug und etwas Eberfleisch zum Essen brauchen. Ihr könnt das Fleisch von Ebern und die Pelze von Bären erbeuten... Ihr findet beide in den Schneefeldern südlich des Graufelsbaus.",
    ["O"] = "Sammelt 4 Brocken Eberfleisch und 2 dicke Bärenfelle und liefert sie bei Pilot Bellfiz in Stahlrosts Depot ab.",
    ["T"] = "Vorräte für Dampfstrahl",
  },
  [318] = {
    ["D"] = "Rejold Gerstenbräu experimentiert mit seinen Gebräuen. Ein paar davon schmecken gut, einige schlecht, und einige... nun, einige hauen selbst den stärksten Zwerg um.$B$BEr lebt in Bräuhall, westlich von Kharanos, und ich will Immerschimmer, eines seiner Getränke. Es wird mich warmhalten, während ich in der Kälte unterwegs bin.$B$BAußerdem brennt das gesegnete Zeug so heiß, dass ich es in Dampfstrahls Brennofen kippen kann, um einen Energieschub zu erzeugen! Diese nette Taktik hat mich schon aus mehr als einer haarigen Situation gerettet.",
    ["O"] = "Besorgt von Rejold Gerstenbräu in Bräuhall ein Fässlein Immerschimmer.",
    ["T"] = "Immerschimmer",
  },
  [319] = {
    ["D"] = "Ich habe ein Fässlein mit Immerschimmer zur Hand, aber... könnt Ihr mir einen Gefallen tun? Bräuhall könnte ein wenig Hilfe gebrauchen gegen die wilden Tiere, die die nahegelegenen Schneefelder durchstreifen. Manchmal kommen sie sehr nah.$B$BWir haben keine Angst vor diesen Bären und Katzen und Ebern - kein Zwerg, der etwas auf sich hält, hätte das - aber wenn Ihr Euch für uns um diese Tiere kümmern könntet, hätten wir mehr Zeit für unser Handwerk und unsere Braukunst.",
    ["O"] = "Tötet 6 Eisklauenbären, 8 alte Klippeneber sowie 8 Schneeleoparden und kehrt dann zu Rejold Gerstenbräu nach Bräuhall zurück.",
    ["T"] = "Ein Gefallen für etwas Immerschimmer",
  },
  [320] = {
    ["D"] = "Hier habt Ihr Euer Fässlein mit Immerschimmer, $N. Und wie gesagt, vielen Dank für die Jagd kürzlich.",
    ["O"] = "Gebt Pilot Bellfiz in Stahlrosts Depot das Fässlein mit Immerschimmer.",
    ["T"] = "Rückkehr zu Bellfiz",
  },
  [321] = {
    ["D"] = "Diese Schiffswracks hier vor der Küste sind die verlorene Dritte Flotte von Kul Tiras, die während des letzten großen Krieges von roten Drachen verbrannt und versenkt wurde.$B$BDie Flotte stammte aus Lordaeron, bis auf ein Schiff, einen elfischen Zerstörer: die \'Fischadler\'. Es transportierte eine Ladung lichtgeschmiedetes Eisen und schloss sich der Dritten Flotte aus Sicherheitsgründen an. Welche Ironie, dass es von denselben Drachen versenkt wurde... $B$BGenug Geschichte. Die \'Fischadler\' liegt südlich vom Hafen von Menethil auf Grund. Wenn Ihr das lichtgeschmiedete Eisen darin finden könnt, bringt es zu mir.",
    ["O"] = "Durchsucht das Wrack der \'Fischadler\' im Sumpfland.",
    ["T"] = "Lichtgeschmiedetes Eisen",
  },
  [322] = {
    ["D"] = "Im Zwergendistrikt von Sturmwind gibt es einen zwergischen Waffenschmied, der lichtgeschmiedetes Eisen verarbeiten kann. Er heißt Grimand Elmore.$B$BIch habe das von Euch gefundene Eisen in eine Kiste gepackt. Bringt sie zu Grimand und erzählt ihm Eure Geschichte. Ich bin mir sicher, er wird jemandem, der auf einem Kreuzzug wie dem Euren ist, gefällig sein und Euch eine Waffe schmieden - eine Waffe, vor der sich sogar Morbent Teufel fürchten wird.",
    ["O"] = "Bringt Grimand Elmore in Sturmwind die Kiste mit Lichtschmiedeblöcken.",
    ["T"] = "Gesegnete Waffe",
  },
  [323] = {
    ["D"] = "Morbent Teufel ist ein Totenbeschwörer und ein Verbündeter der dunklen Reiter. Ich würde Euch ja erlauben, Euch der Macht von Morbent Teufel entgegenzustellen, aber ich werde Euch erst gegen dieses widerwärtige Scheusal kämpfen lassen, wenn ich Eure wahre Stärke kenne.$B$BIhr habt mir Eure Tapferkeit bereits bewiesen, aber wenn Ihr dem Totenbeschwörer wirklich trotzen wollt, dann beweist Euer Geschick zuerst an seinen Dienern.",
    ["O"] = "Vernichtet 15 Skeletträuber, 3 Skelettheiler sowie 3 Skelettwärter und kehrt danach zu Sven zurück.",
    ["T"] = "Bewährungsprobe",
  },
  [324] = {
    ["D"] = "Nachdem Ihr den einzelnen Lichtschmiedeblock aus der voll gelaufenen Kiste genommen habt, ist Euch klar, dass die Murlocs wohl den Rest gestohlen haben. Jagt die Murlocräuber in der Nähe des Wracks der \'Fischadler\', um an das verloren gegangene lichtgeschmiedete Eisen zu kommen.",
    ["O"] = "Sammelt 5 Lichtschmiedeblöcke und kehrt dann zu Glorin Stahlbraue im Sumpfland zurück.",
    ["T"] = "Die verlorenen Blöcke",
  },
  [325] = {
    ["D"] = "Hier ist die Waffe gegen das Scheusal, von dem Ihr spracht. Ihr seid bestens vorbereitet, $N. Viel Glück, und ich freue mich schon darauf, von Eurer Ruhmestat zu hören!",
    ["O"] = "Bringt Morbents Bann zu Sven im Dämmerwald.",
    ["T"] = "Bewaffnet und bereit",
  },
  [328] = {
    ["D"] = "In den Berichten wird kein Wort über den Gefreiten Thorsen verloren, jedoch im Einband eines von Herods Geschäftsbüchern findet sich ein kleiner Messingschlüssel.$B$BUnd nach genauerem Studium seiner Notizen finden sich in den verschiedenen Dokumenten einige Verweise auf eine \"sichere Geldkassette\". Die Geldkassette befindet sich irgendwo innerhalb des Vorratslagers - des Höhlenkomplexes weiter hinten in Kurzens Truppenlager.$B$BWenn diese sichere Geldkassette gefunden wird und der kleine Messingschlüssel ihn öffnen kann, dann kann das Geheimnis um Thorsens Entdeckung vielleicht offenbart werden.",
    ["O"] = "Verwendet den kleinen Messingschlüssel, um Buchmacher Herods Geldkassette zu öffnen.",
    ["T"] = "Der versteckte Schlüssel",
  },
  [329] = {
    ["D"] = "Unter den Wertgegenständen in der Geldkassette befindet sich eine schlammverschmierte, gekritzelte Notiz. Wenn sie auch kaum lesbar ist, so scheint es, als wäre es eine Abhandlung über den Gefreiten Thorsen. Sie beschreibt ausführlich seine Patrouillenrouten, mit wem er so spricht und wie er seine Zeit bei den Rebellen verbringt. Sie ist nur mit einem einzelnen Buchstaben unterschrieben: -M.$B$BDennoch muss, wer auch immer diese Notiz verfasst hat, stets in Thorsens Nähe gewesen sein. Wer auch immer diese Notiz verfasst hat, ist der Verräter im Rebellenlager.",
    ["O"] = "Bringt die schlammverschmierte Notiz zu Leutnant Doren im Rebellenlager.",
    ["T"] = "Der entlarvte Spion",
  },
  [330] = {
    ["D"] = "Die Notiz, die Ihr mir gegeben habt, ist mit einem \"-M\" unterzeichnet... Ich weiß nicht, was das heißen könnte.$B$BAber... die Notiz sagt auch aus, dass diese Person letzte Woche die gleichen Patrouillenrouten wie Thorsen hatte. Also muss, wer auch immer mit Thorsen letzte Woche auf Patrouille war, der Verräter sein!$B$BGeht zu Korporal Sethmann. Er kümmert sich um die Patrouillenpläne und wird Euch sagen können, wer mit Thorsen unterwegs war.",
    ["O"] = "Sprecht mit Korporal Sethmann.",
    ["T"] = "Patrouillen-Pläne",
  },
  [331] = {
    ["D"] = "Wollte Leutnant Doren, dass Ihr Euch mit diesen Patrouillen-Informationen wieder bei ihm meldet?",
    ["O"] = "Sprecht mit Leutnant Doren.",
    ["T"] = "Meldung bei Doren",
  },
  [332] = {
    ["D"] = "Wenn Ihr Euch mit irgendwem über Weine unterhaltet, werdet Ihr erfahren, dass wir Gallinas die besten Weine in ganz Sturmwind verkaufen. Wir sind auch nicht weit weg, immer noch innerhalb des Handelsdistrikts entlang der herrlichen Kanäle der Stadt.$B$BHier, nehmt dieses Flugblatt. Bringt es meiner Schwester Suzetta in unseren Laden, und Ihr erhaltet eine Gratisflasche unseres berühmten Dalaran Noir. Ihr werdet es nicht bereuen!",
    ["O"] = "Geht zum Weingut Gallina und gebt Suzetta Gallina den Weingutschein, um eine kostenlose Flasche Wein zu erhalten.",
    ["T"] = "Weinladen-Werbung",
  },
  [333] = {
    ["D"] = "Wir hatten hier in der letzten Zeit viel Betrieb. Es scheint so, als wollte wirklich jeder Rüstungen und robuste Kleidung kaufen. Als würden alle eine kalte, harte Jahreszeit erwarten... $B$BAber das sind alles Zukunftssorgen. Meine heutige Sorge ist eher, dass mir die gestrickten Kleidungsstücke ausgehen. Ich brauche eine weitere Fuhre von unserem Lieferanten.$B$BWenn Ihr diesen Auftrag zu Rema Schneider in die Schneiderei und Anpasserei am Kanal bringen könntet, wäre ich Euch sehr dankbar.",
    ["O"] = "Geht zur Schneiderei und Anpasserei am Kanal und bringt Rema Schneider Harlan Bagleys Stoffanforderung.",
    ["T"] = "Harlan braucht Nachschub",
  },
  [334] = {
    ["D"] = "Mein Sohn Thurman ist Lehrling beim Konfektionsgeschäft Larson im Magierviertel. Er hatte es heute etwas eilig und vergaß seine Schere und Nadeln. Ich weiß, $Gein großer:eine große:c; $C wie Ihr hat bestimmt wichtige Aufgaben zu erledigen, aber ohne sein Handwerkszeug kann er seine Lehrarbeiten nicht erledigen!$B$BBitte, $N. Könnt Ihr meinem Sohn sein Nähkästchen vorbeibringen? Das Konfektionsgeschäft Larson ist einer von zwei Kleiderläden im Magierviertel - es ist das, das etwas weiter drin liegt, ganz in der Nähe des Magierturms.",
    ["O"] = "Geht zum Konfektionsgeschäft Larson im Magierviertel von Sturmwind und gebt Thurman Schneider sein Nähkästchen.",
    ["T"] = "Eine Lieferung für Thurman",
  },
  [335] = {
    ["D"] = "Lord Wischock sorgt im Oberhaus ganz schön für Unruhe. Er wurde auf unseren kleinen Betrieb hier unten aufmerksam gemacht und propagiert jetzt eine genaue Untersuchung. Ich habe einen Plan, um Wischock... zu \"beschäftigen\"... aber ich benötige Eure Hilfe. $B$BEs gibt eine Blume, die in Stalvans Garten in den Außenbezirken von Dunkelhain wächst. Diese kleine weiße Blume ist als Tilloaträne bekannt. Ich werde außerdem eine Bisamwurzel brauchen, die man nur am Fuße des Wasserfalls tief unter dem Steinwerkdamm im Sumpfland findet.",
    ["O"] = "Zardeth der Schwarzen Klaue möchte, dass Ihr ihm eine Tilloaträne aus Dunkelhain und eine Bisamwurzel aus dem Sumpfland bringt.",
    ["T"] = "Ein nobles Bräu",
  },
  [336] = {
    ["D"] = "Lord Wischock hat, wie alle Menschen, seine Schwächen. Bei seiner handelt es sich um eine Vorliebe für alkoholische Getränke. Seine Scheinheiligkeit ist verblüffend. Dieser spießige Trottel verbringt seine Tage damit zu versuchen, unsere kleine \"Taverne\" schließen zu lassen, während er seine Nächte im Obergeschoss zubringt und dabei Rechnungen anschreiben lässt, die er niemals bezahlt. Na, mal sehen, wie Wischock dieses besondere Gebräu schmeckt, das ich für ihn vorbereitet habe: Schwarzklauenbier. Es haut ziemlich rein, $N.$B$BBringt es ihm und seht, wie es ihm schmeckt. Höchstwahrscheinlich treibt sich der alte Trottel in der Halle der Bittsteller herum.",
    ["O"] = "Bringt Lord Wischock in der Halle der Bittsteller das Schwarzklauenbier.",
    ["T"] = "Ein nobles Bräu",
  },
  [337] = {
    ["D"] = "Obwohl der Einband dieses Buches von Kratzern überzogen und von Schmutz verkrustet ist, kann man auf dem Buchrücken noch immer die Worte \"Die Geschichte von Sturmwind\" lesen. Wenn man das Buch aufmacht, kann man das Siegel der Bibliothek von Sturmwind erkennen, das deutlich auf der ersten Seite prangt.$B$BEs sieht so aus, als hätte jemand dieses Buch ausgeliehen, aber nie zurückgegeben.",
    ["O"] = "Bringt das Buch: Die Geschichte von Sturmwind in die Bibliothek von Sturmwind zurück.",
    ["T"] = "Ein altes Geschichtsbuch",
  },
  [338] = {
    ["D"] = "Meister Nesingwary wird furchtbar wütend sein, wenn er das herausfindet!$B$BEr vertraute mir das Manuskript für seinen Roman, Die Grünen Hügel des Schlingendorntals, zum Korrekturlesen an. Es lief auch alles glatt, aber dann wehte ein großer Windstoß durch das Lager und hat die Seiten überall verteilt.$B$BIch konnte die meisten Seiten wieder zusammensuchen, aber viele sind noch über den halben Dschungel verstreut. Sammelt die Seiten, mit denen man die vier Kapitel vervollständigen kann.$B$BIch werde Euch helfen, sie zu ordnen. Bringt mir die Kapitel zurück, wenn Ihr alle vier beisammen habt.",
    ["O"] = "Sammelt die fehlenden Seiten des Manuskripts von Die Grünen Hügel des Schlingendorntals. Wenn alle vier Kapitel vollständig sind, bringt sie zu Barnil zurück.",
    ["T"] = "Die Grünen Hügel des Schlingendorntals",
  },
  [339] = {
    ["D"] = "Kapitel I war ziemlich fesselnd! Meister Nesingwary baut den Hintergrund der Geschichte auf und erwähnt seine Jagdgefährten, mich eingeschlossen! $B$BAber das tut gerade nichts zur Sache. Lasst mich mal sehen, welche Seiten noch fehlen. So wie es aussieht sind die Seiten 1, 4, 6 und 8 immer noch irgendwo da draußen im Dschungel!",
    ["O"] = "Bringt Barnil Steinkrug die Seiten 1, 4, 6 und 8 von Nesingwarys Die Grünen Hügel des Schlingendorntals, um Kapitel I zu vervollständigen.",
    ["T"] = "Kapitel I",
  },
  [340] = {
    ["D"] = "Kapitel II war voll gestopft mit aufregenden Geschichten von der Panther- und Tigerjagd. Ich könnte mir dafür in den Hintern treten, dass ich die Seiten habe davonfliegen lassen. $B$BEs sieht aus, als hätte ich alle Seiten wiedergefunden, bis auf die Seiten 10, 11, 14 und 16.",
    ["O"] = "Bringt Barnil Steinkrug die Seiten 10, 11, 14 und 16 von Nesingwarys Die Grünen Hügel des Schlingendorntals, um Kapitel II zu vervollständigen.",
    ["T"] = "Kapitel II",
  },
  [341] = {
    ["D"] = "Kapitel III gefiel mir am besten. Es erzählt von Sire Erlgadins und Lady Rouacks erstem Blattschuss mit einem Zwergengewehr. Und am Ende von Kapitel III, Mann, solche eine Spannung! $B$BWenn ich das richtig sehe, brauchen wir noch die Seiten 18, 20, 21 und 24, um das Kapitel fertig zu stellen. Diese Seiten sind irgendwo da draußen...",
    ["O"] = "Bringt Barnil Steinkrug die Seiten 18, 20, 21 und 24 von Nesingwarys Die Grünen Hügel des Schlingendorntals, um Kapitel III zu vervollständigen.",
    ["T"] = "Kapitel III",
  },
  [342] = {
    ["D"] = "Ah, Kapitel IV; Das große Finale! Und was für ein Ende es war! $B$BUns fehlen nur drei Seiten aus diesem Kapitel. Wir brauchen die Seiten 25, 26 und 27, um Kapitel IV wieder zusammenzusetzen.",
    ["O"] = "Bringt Barnil Steinkrug die Seiten 25, 26 und 27 von Nesingwarys Die Grünen Hügel des Schlingendorntals, um Kapitel IV zu vervollständigen.",
    ["T"] = "Kapitel IV",
  },
  [343] = {
    ["D"] = "Ich werde eine Rede über die Kraft des Geistes halten, und ich würde dazu gern einige Auszüge aus einem bestimmten Buch über Metallurgie zitieren - \"Belastbarkeit von Eisen\". Eine Abschrift dieses Buches dürfte sich in der Königlichen Bibliothek der Burg Sturmwind finden. Könnt Ihr es für mich holen? Wenn dem so ist, dann mögen die Segnungen des Lichts mit Euch sein!$B$BDie Königliche Bibliothek ist sehr groß, aber der oberste Bibliothekar, Milton Garbenbund, kennt jedes Regal, als wäre es sein eigenes Kind. Sprecht mit ihm, und er wird Euch das Buch holen.",
    ["O"] = "Geht in die Königliche Bibliothek der Burg von Sturmwind und sprecht mit Milton Garbenbund. Er kann das Buch über Metallurgie für Euch finden, das Bruder Kristoff für seine Rede benötigt.",
    ["T"] = "Reden über Kraft",
  },
  [344] = {
    ["D"] = "Belastbarkeit von Eisen? Ja, ich kenne das Buch. Ziemlich gut, vor einigen Jahrzehnten von dem Zwergenminenarbeiter Margulf Blaggon geschrieben. Er verbrachte den Großteil seines Lebens tief in den Bergen von Khaz Modan, Erze schürfend. Kluger Kerl!$B$BAber wo war ich doch gleich? Ach ja, die Belastbarkeit von Eisen! Ich fürchte, das Buch wurde in die Abtei von Nordhain verlegt. Der Verwalter der Bibliothek der Abtei, Bruder Paxton, wollte eine Kopie des Buches für seine eigenen Regale anfertigen.$B$BDas war allerdings vor Monaten. Ich frage mich, warum er so lange dafür braucht...",
    ["O"] = "Sprecht mit Bruder Paxton in der Abtei von Nordhain.",
    ["T"] = "Bruder Paxton",
  },
  [345] = {
    ["D"] = "Sprecht mit Großknecht Oslow in Seenhain und fragt ihn nach Rethbaneisenerz.",
    ["O"] = "Sprecht mit Großknecht Oslow in Seenhain und fragt ihn nach Rethbaneisenerz.",
    ["T"] = "Tinten-Zutaten",
  },
  [346] = {
    ["D"] = "Nachdem ich jetzt mit der Abschrift fertig bin, könnt Ihr das Original von Belastbarkeit von Eisen haben. Ich danke Euch für Eure Geduld, und wenn Ihr Milton seht, bitte sagt ihm, die lange Verzögerung täte mir leid.",
    ["O"] = "Kehrt zu Bruder Kristoff auf den Kathedralenplatz zurück. Gebt ihm das Buch: \"Belastbarkeit von Eisen\".",
    ["T"] = "Rückkehr zu Kristoff",
  },
  [348] = {
    ["D"] = "Krank... so... furchtbar k-k-krank... $B$B... habe mich mit Schlingendornfieber angesteckt... tödliches V-v-virus... $B$B... einziges Heilmittel... wäre... wäre es... das Herz von M-m-m-mokk dem Wilden zu essen... schwer zu fassendes, weißes Wildtier... $B$B... Hexendoktor Unbagwa... Einziger, der es b-b-beschwören kann...$B$B... sucht Unbagwa... versteckte Höhle am südlichen Kap...$B$B... schnell... sterbe... sterbe...",
    ["O"] = "Sucht Hexendoktor Unbagwa und lasst ihn Mokk den Wilden beschwören. Bringt das Herz von Mokk zu Fin Fizrackett.",
    ["T"] = "Schlingendornfieber",
  },
  [350] = {
    ["D"] = "Marzon würde ein Mitglied der Defias kontaktieren und er hat Verbindungen zu Lescovar... $B$BDa van Cleef und Thredd tot sind, wäre es schwierig, einen Richtspruch gegen Lescovar zu erwirken, denn Eure Beweise sind wertlos ohne deren Zeugnis. Und dabei wird noch nicht einmal die Tatsache berücksichtigt, dass Lescovar ja ein Adliger ist und viele Beziehungen hat. Das stellt ihn gewissermaßen über das Gesetz.$B$BWir werden weitere Hilfe benötigen. Ich tu das nur äußerst ungern, aber ich kenne jemanden, der uns helfen wird. Ihr findet Trias im Handelsdistrikt der Stadt.",
    ["O"] = "Sprecht mit Elling Trias in Sturmwind.",
    ["T"] = "Auf einen alten Freund verlassen",
  },
  [351] = {
    ["D"] = "Ihr habt ein merkwürdiges Gerät aus Metall entdeckt, das die Form eines Eis hat. Als Ihr einen der Knöpfe berührt, erwacht das Ei zu Leben und verwandelt sich in eine Art Gnom-Roboter-Apparat! Aus dem Inneren des Roboteis erklingt eine rasselnde Stimme.$B$B\"Mein Name ist Glotz und mein Leitroboter ist verloren gegangen! Ich würde Euch belohnen, wenn Ihr ihn aufspürt! Bringt dem Roboter bitte diesen Signalgeber. Hervorragend, jetzt habe ich die Koordinaten für Euch berechnet! Er ist in der Nähe der Klaffenden Schlucht in Tanaris abgestürzt!\"",
    ["O"] = "Bringt das Notsignal zum Leitroboter von Glotz.",
    ["T"] = "Findet OOX-17/TN!",
  },
  [352] = "_",
  [353] = {
    ["D"] = "Die Sturmlanzen sind ein geachteter Zwergenklan und bekannt für ihren feinen und kritischen Geschmack. Daher ist es nicht verwunderlich, dass Gringer Sturmlanze, ein Gebirgsjäger aus Eisenschmiede, mich beauftragte, ihm eine Waffe zu schmieden.$B$BDie Waffe ist fertig, aber... Gebirgsjäger Sturmlanze ist weit weg, am fernen Loch Modan. Wenn Ihr ohnehin vorhabt, nordwärts zu reisen, könntet Ihr ihm dieses Päckchen überbringen?$B$BMeine letzte Nachricht von Gebirgsjäger Sturmlanze besagte, dass er im nördlichen Wachturm des Loch Modan stationiert ist.",
    ["O"] = "Bringt das Päckchen für Sturmlanze zu Gebirgsjäger Sturmlanze am Loch Modan.",
    ["T"] = "Sturmlanzes Lieferung",
  },
  [354] = {
    ["D"] = "Die Familie Agamand war die wohlhabendste in Tirisfal. Ich habe früher in ihrer Mühle gearbeitet... vor der Seuche.$B$BAls die Geißel das erste Mal auftauchte, befestigten die Agamands ihre Heimstatt und überredeten ihre Angestellten zu bleiben und bei der Verteidigung zu helfen. Wir waren Narren, aber wir waren loyale Narren.$B$BDie Agamands haben uns in ihrem Stolz zum Untod verdammt. Und heute sind sie Diener der Geißel!$B$BDient den Verlassenen, indem Ihr die Agamands besiegt, die der Seuche zum Opfer fielen. Dient mir, indem Ihr mir ihre Überreste bringt.",
    ["O"] = "Bringt Coleman Heller in Brill Gregors Überreste, Nissas Überreste und Thurmans Überreste.",
    ["T"] = "Todesfälle in der Familie",
  },
  [355] = {
    ["D"] = "Ihr habt mir die Überreste der Agamands gegeben und meinen Rachedurst gestillt. Aber Agamands Mühlen sind eine Bedrohung für alle Verlassenen, nicht nur für mich.$B$BIch habe mit Magistrat Sevren über Eure Heldentaten gegen die Agamands geredet, und er wünscht, mit Euch zu sprechen.",
    ["O"] = "Sprecht mit Magistrat Sevren in Brill.",
    ["T"] = "Sprecht mit Sevren",
  },
  [356] = {
    ["D"] = "Die Verteidiger auf dem Bollwerk, die Tirisfal vor den Pestländern schützen, müssen immer wachsam sein. Aber manchmal schafft es die Geißel, an ihnen vorbeizuschlüpfen.$B$BUnser Erfolg am Bollwerk hängt an einer Ein-Fronten-Schlacht - wir können keinen Angriff von hinten zulassen, und noch weniger können wir zulassen, dass die Versorgungslinie unserer Verteidiger unterbrochen wird.$B$BPatrouilliert ostwärts und erschlagt alle Untoten, die Ihr findet. Seid auf Balnirs Bauernhof im Osten besonders gründlich. Es hat sich zu einem Zufluchtsort für eindringende Untote entwickelt.",
    ["O"] = "Tötet 8 blutende Schrecken und 8 wandernde Geister.",
    ["T"] = "Patrouille zur Rückendeckung",
  },
  [357] = {
    ["D"] = "Es gibt einen Lich, der auf einer Insel im Blendwassersee im Norden haust. Obwohl er ein Verlassener mit freiem Willen ist, glaubt er, dass alle anderen Untoten Sklaven der Geißel sind, und hetzt seine Diener auf jeden, der sich ihm nähert.$B$BAber da er ein Verlassener ist, wird die Königin ihn haben wollen. Und wenn er in der Totenbeschwörung bewandert ist, dann wäre sein Wissen... recht nützlich für uns.$B$BUm etwas über seine Identität und sein Können herauszufinden, muss ich mir sein Zauberbuch ansehen. Stehlt Euch in sein Lager, verschafft es Euch und kehrt dann zu mir zurück.",
    ["O"] = "Bringt das Zauberbuch des Lichs zu Bethor Eismacht in Unterstadt.",
    ["T"] = "Die Identität des Lichs",
  },
  [361] = {
    ["D"] = "Dies ist ein Brief, der von Thurman, dem ältesten Sohn von Gregor Agamand, geschrieben wurde. Er ist an Yvette Heller adressiert, aber er scheint nicht bei ihr angekommen zu sein...",
    ["O"] = "Findet Yvette Heller und bringt Ihr den Brief von Thurman Agamand.",
    ["T"] = "Ein nie zugestellter Brief",
  },
  [362] = {
    ["D"] = "Devlin Agamand war der jüngere von zwei Söhnen der Familie Agamand und im Leben hätten sie unterschiedlicher nicht sein können. Thurman war groß und sanft, während sein jüngerer Bruder schwach und scharfzüngig war.$B$BAls Agamands Mühlen der Seuche anheim fielen, war ich nicht überrascht zu hören, dass Devlin ihr schnell erlag. Sein verrücktes Gebrabbel kann man heute noch nahe der Straße hören, die zu Agamands Mühlen führt.$B$BIch sammle die Überreste der Agamands und ich möchte den armen Devlin. Findet ihn, vernichtet ihn und bringt mir seine Knochen.",
    ["O"] = "Erschlagt Devlin Agamand und bringt Devlins Überreste zu Coleman Heller nach Brill.",
    ["T"] = "Die Spukmühlen",
  },
  [363] = {
    ["D"] = "About time you woke up. We were ready to toss you into the fire with the others, but it looks like you made it.$b$bI am Mordo, the caretaker of the crypt of Deathknell. And you are the Lich King\'s slave no more.$b$bSpeak with Shadow Priest Sarvis in the chapel at the base of the hill, he will tell you more of what you must know.",
    ["O"] = "Sprecht mit Schattenpriester Sarvis.",
    ["T"] = "Unsanftes Erwachen",
  },
  [364] = {
    ["D"] = "Seit der erste Verlassene aus seinem Grab gekrochen ist, stehen wir mit der Geißel, der Armee des Lichkönigs im Krieg: durch Nekromantie erweckte Armeen von Untoten, grausige Bestien des Nordens und gepeinigte Schreckgespenster.$B$BZwar sitzt der Lichkönig nicht länger auf dem Frostthron, dennoch ist der nördliche Teil des Dorfes noch immer von seinen Sklaven überrannt. Zerstört sie, zeigt ihnen keine Gnade, auch wenn sie vormals unsere Brüder und Schwestern waren.",
    ["O"] = "Tötet 5 hirnlose Zombies und 5 elende Ghule.",
    ["T"] = "Die Hirnlosen",
  },
  [365] = {
    ["D"] = "Was haben wir denn hier? Ihr seht wie ein Neuling aus, $C. Wenn Ihr darauf hofft, Euch der Dunklen Fürstin zu beweisen, dann müsst Ihr die Gebräuche der Verlassenen erlernen.$B$BIm Westen werdet Ihr einen Hof vorfinden. Menschen suchen das Land heim wie Schimmel eine verrottende Leiche. Und um es noch schlimmer zu machen, patrouilliert ganz in der Nähe der Scharlachrote Kreuzzug von seinem Turm aus. Erteilt diesem Abschaum eine Lektion und stehlt 10 ihrer kostbaren Kürbisse.$B$BSobald Ihr 10 beisammen habt, bringt sie zu Apotheker Johaan nach Brill.",
    ["O"] = "Stehlt 10 Kürbisse vom Gehöft im Westen, direkt nördlich von Todesend und bringt sie zu Apotheker Johann nach Brill.",
    ["T"] = "Felder des Kummers",
  },
  [366] = {
    ["D"] = "Vor langer Zeit waren Gunther und ich, wie auch der jetzt der Geißel dienende Totenbeschwörer Thule Rabenklaue, Freunde und Schüler beim Kirin Tor, einer Magiergesellschaft in Dalaran.$B$BGunther und ich fielen beide der Seuche anheim, aber ich dachte, er wäre entweder tot geblieben oder für die Armee des Lichkönigs erweckt worden. Es tut gut zu wissen, dass er frei ist.$B$BWir müssen ihn für uns gewinnen. Ich habe einen Zauber auf dieses Buch gelegt, der ihm, sobald er ihn sieht, zeigen wird, dass ich ebenfalls frei vom Einfluss des Lichkönigs bin. Ich hoffe, dass er sich uns anschließt, wenn er das erfährt.",
    ["O"] = "Bringt Gunthers Zauberbuch zu ihm auf die Gunthers Zufluchtsort genannte Insel zurück.",
    ["T"] = "Rückgabe des Buches",
  },
  [367] = {
    ["D"] = "Fürstin Sylvanas hat sich an die königliche Apothekervereinigung gewandt. Die Dunkle Fürstin glaubt, dass unser Wissen, gepaart mit der neuentdeckten Magie, der Schlüssel zu Arthas\' Untergang sein wird. Sie hat uns aufgefordert, eine neue Seuche zusammenzubrauen, eine Seuche, tödlicher als jedes Leiden in ganz Azeroth. Diese neue Krankheit wird Arthas\' Armee der Geißel ins Verderben stürzen.$B$BMeine Studien zeigen, dass das Blut von Bestien der Schlüssel sein könnte. Bringt mir 5 Phiolen Schattenhundblut, damit ich meine Theorie ausprobieren kann.",
    ["O"] = "Apotheker Johaan in der Stadt Brill möchte, dass Ihr 5 Phiolen Schattenhundblut sammelt.",
    ["T"] = "Eine neue Seuche",
  },
  [368] = {
    ["D"] = "Während Ihr unterwegs wart und Proben für mich gesammelt habt, haben meine Experimente ergeben, dass noch weitere Reagenzien benötigt werden, wenn sich diese Krankheit ordentlich ausbreiten soll. Ein paar unglückliche Opfer zu vergiften, ist eine Kleinigkeit. Eine ganze Welt erkranken zu lassen, erweist sich als etwas komplizierter.$B$BIch werde 5 Finsterflossenschuppen von den Murlocs in der Umgebung brauchen. Ihr findet die Kreaturen entlang der Küste im Norden oder Westen von hier.",
    ["O"] = "Apotheker Johaan in der Stadt Brill benötigt 5 Finsterflossenschuppen von den Murlocs in Tirisfal.",
    ["T"] = "Eine neue Seuche",
  },
  [369] = {
    ["D"] = "Während Ihr sammeln wart, habe ich einen alten Text in einem meiner Bücher entdeckt, der darauf hindeutet, dass eine uralte Seuche Tausende unschuldiger Opfer ausgelöscht hat. Man fand später heraus, dass der tödliche Erreger im Gift der Nachtweberspinnen erhalten blieb.$B$BBringt mir etwas Gift eines heimtückischen Nachtwebers, um dieses Experiment abzuschließen. Ich möchte sehen, ob das ansteckende Element des Giftes mit meinem neuen Gebräu harmoniert. Gerüchten zufolge leben die Spinnen im östlichen Tirisfal.",
    ["O"] = "Apotheker Johaan in der Stadt Brill möchte, dass Ihr ihm 4 Proben vom Gift eines heimtückischen Nachtwebers bringt.",
    ["T"] = "Eine neue Seuche",
  },
  [370] = {
    ["D"] = "Die strategischen Dokumente von Exekutor Arren enthalten alle Angaben, die wir brauchen, um die menschliche Plage aus den Ländern zu tilgen. Nach diesen Informationen hat der Scharlachrote Kreuzzug Hauptmann Perrine mit einer Brigade dem verfallenen Turm südwestlich von Brill zugeteilt.$B$BTötet Perrine sowie 3 Zeloten und 3 Missionare und erstattet mir dann Meldung.",
    ["O"] = "Exekutor Zygand in Brill möchte, dass Ihr Hauptmann Perrine, 3 Scharlachrote Zeloten und 3 Scharlachrote Missionare tötet.",
    ["T"] = "Im Krieg mit dem Scharlachroten Kreuzzug",
  },
  [371] = {
    ["D"] = "Wir befinden uns noch immer im Krieg, und der Scharlachrote Kreuzzug gewinnt an Stärke. Der Bericht, den mir Exekutor Arren schickte, zeigt auf, dass die Scharlachroten Kreuzzügler ihre Überfälle von dem verfallenen Turm im südöstlichen Tirisfal, nahe Balnirs Bauernhof, unter dem Kommando von Hauptmann Vachon ausgeführt haben.$B$BTötet Vachon, zusammen mit 5 Scharlachroten Ordensmitgliedern. Das sollte sich als vernichtender Schlag für den Kreuzzug erweisen!",
    ["O"] = "Exekutor Zygand in Brill hat Euch damit beauftragt, Hauptmann Vachon und 5 Scharlachrote Ordensmitglieder zu erschlagen.",
    ["T"] = "Im Krieg mit dem Scharlachroten Kreuzzug",
  },
  [372] = {
    ["D"] = "Scharlachrote Kreuzzügler haben vom verfallenen Turm im nördlichen Tirisfal, hinter Faols Ruheplatz, Überfälle ausgeführt. Nach den Informationen, die wir haben, führt ein unbarmherziger Kommandant mit Namen Hauptmann Melrache diese bösartige Truppe an.$B$BIch vertraue Euch eine besondere und gefahrvolle Mission an. Erschlagt Melrache und seine beiden Leibwachen im Namen der Dunklen Fürstin.",
    ["O"] = "Exekutor Zygand in der Stadt Brill möchte, dass Ihr Hauptmann Melrache und seine beiden Leibwachen ermordet.",
    ["T"] = "Im Krieg mit dem Scharlachroten Kreuzzug",
  },
  [373] = {
    ["D"] = "Als Ihr Edwin van Cleef durchsucht, findet Ihr unter anderem einen nie verschickten Brief. Er ist adressiert an Baros Alexston, Stadtarchitekt von Sturmwind, Rathaus, Kathedralenplatz.$B$BEr scheint erst vor Kurzem geschrieben und versiegelt worden zu sein.",
    ["O"] = "Bringt den Brief nach Sturmwind zum Stadtarchitekten Baros Alexston.",
    ["T"] = "Der nie verschickte Brief",
  },
  [374] = {
    ["D"] = "Exekutor Zygand sagt mir, dass Ihr Scharlachrote Kreuzzügler töten wart. In Tirisfal wimmelt es nur so von denen.$B$BVarimathras, Offizier der Dunklen Fürstin, kümmert sich persönlich darum, dass der Abschaum Azeroths, die Menschen, völlig ausgelöscht wird. Hier in Tirisfal stehen wir einer besonders lästigen Menschenbrut gegenüber: dem Scharlachroten Kreuzzug.$B$BBegebt Euch hinaus und führt eine Offensive gegen den Scharlachroten Kreuzzug, $C. Bringt mir 10 Scharlachrote Insignienringe zum Beweis Eurer Loyalität Varimathras und der Dunklen Fürstin gegenüber.",
    ["O"] = "Bringt Todeswache Burgess in Brill 10 Scharlachrote Insignienringe.",
    ["T"] = "Todesbeweis",
  },
  [375] = {
    ["D"] = "Es ist jetzt so kalt. Die Seuche des Untodes kriecht durch meine Adern wie eine eisige Schlange. Ich werde bald den Hirnlosen Zustand erreichen. Doch auch ein besiegeltes Schicksal wird mich nicht davon abhalten, unserer Dunklen Fürstin zu dienen. Als der Ruf ertönte, nähte ich Leichensäcke für die gefallenen Soldaten von Sylvanas\' mächtiger Armee.$B$BJetzt zittern meine Hände vor lauter Kälte. Wenn Ihr mir fünf Nachtsaugerpelze und etwas groben Faden bringen würdet, könnte ich mir eine Decke nähen. Helft mir, $N, auf dass ich der Sache weiter dienen kann.",
    ["O"] = "Bringt Gretchen Dedmar in Brill fünf Nachtsaugerpelze und etwas groben Faden.",
    ["T"] = "Die Kälte des Todes",
  },
  [376] = {
    ["D"] = "Meine Pflichten umfassen, die verwundeten Krieger zu versorgen, Rüstung und Kleidung zu schneidern und Schattenpriester Sarvis in jeder erdenklichen Weise zu unterstützen.$B$BEs sieht so aus, als würdet Ihr ebenfalls in seine Dienste aufgenommen... wahrscheinlich um die Hirnlosen zu jagen, wie ich ihn kenne. Tja, wenn Ihr dabei in einem Stück bleiben wollt - und ich bezweifle nicht, dass Ihr das wollt -, dann kann ich Euch vielleicht helfen. Mir gehen die Tatzen und Flügel aus, und wenn Ihr mir einige davon bringt, besorge ich Euch eine Rüstung. Ihr werdet im Norden eine Menge Wölfe und Fledermäuse finden.",
    ["O"] = "Novizin Elreth benötigt 4 Aasfressertatzen und 4 Nachtsaugerflügel.",
    ["T"] = "Die Verdammten",
  },
  [377] = {
    ["D"] = "Als wäre die Vernachlässigung der Einwohner des Dämmerwalds nicht schlimm genug, nein, die jüngste Entscheidung des Oberhauses, Dextren Ward in Sturmwind ins Gefängnis zu werfen, anstatt den Schurken - wie durch Lord Schwarzhaupts Urteil gefordert - zu enthaupten, ist ein Schlag ins Gesicht für den Rat von Dunkelhain.$B$BWard wurde dabei erwischt, wie er Leichen aus dem Friedhof an Morbent Teufel verkaufte, ein Verbrechen, auf das der Tod steht. Und dennoch fordert Sturmwind seine Gefangensetzung.$B$BErmordet Ward im Verlies von Sturmwind - bringt mir seine Hand - und ich werde Euch belohnen.",
    ["O"] = "Ratsherr Mühlenstein von Dunkelhain will, dass Ihr ihm die Hand von Dextren Ward bringt.",
    ["T"] = "Verbrechen und Strafe",
  },
  [378] = {
    ["D"] = "Ihr habt Eure Loyalität zu König Magni bewiesen, $C. Und Euer Hass auf den Dunkeleisenabschaum ist genauso groß wie mein eigener.$B$BEs gibt da eine Aufgabe, die ich selbst beenden möchte, aber ich bin durch meine Ehre verpflichtet, bei Langzopf zu bleiben. Jedoch hat Roggo Informationen gesammelt, die belegen, dass Kam Tiefenzorn einer der Verschwörer beim Angriff auf den Thandolübergang war. Durch Tiefenzorns Pläne verlor Langzopf seine Sippe.$B$BTiefenzorn wird aus politischen Gründen im Verlies von Sturmwind festgehalten. Ich will ihn tot sehen, $N. Für Langzopf! Bringt mir seinen Kopf!",
    ["O"] = "Motley Garmason in Dun Modr verlangt Kam Tiefenzorns Kopf.",
    ["T"] = "Tief empfundener Zorn",
  },
  [379] = {
    ["D"] = "Ihr möchtet eine Kraftquelle. Da kann ich Euch helfen. Wisst Ihr, was ich möchte? Ich möchte etwas zu Trinken! Bringt mir fünf Wasserbeutel eines Wüstenläufers und ich werde Euch die 4711-FTZ geben.$B$BUnd jetzt... ratet einmal, wo Ihr solche Wasserbeutel finden könnt! Warum geht Ihr nicht zu einem der Wüstenläufernomaden östlich von hier und bittet ihn um einen? Lasst mich wissen, ob das klappt.",
    ["O"] = "Bringt Chefingenieur Bilgenritzel in Gadgetzan 5 Wasserbeutel eines Wüstenläufers.",
    ["T"] = "Durstlöscher",
  },
  [380] = {
    ["D"] = "Eine unserer größten Schwierigkeiten besteht darin, die Rohstoffe zu besorgen, die wir zum Überleben brauchen. Gold war schon zu Hochzeiten der Macht der Allianz selten.$B$BEs gibt eine Goldmine im Nordwesten, die von Spinnen überrannt wurde. Wir brauchen das Gold dieser Mine, aber wir können es ja schlecht holen, während die Spinnen darin herumkrabbeln. Ich habe nicht viel Personal übrig für diese Aufgabe, also müssen wir sie Schritt für Schritt lösen.$B$BBegebt Euch dorthin und schaut mal, was Ihr für uns ausrichten könnt, $N.",
    ["O"] = "Exekutor Arren will, dass Ihr 8 junge Nachtweber und 5 Nachtweber tötet.",
    ["T"] = "Nachtweber",
  },
  [381] = {
    ["D"] = "Ihr werdet erfreut sein zu hören, dass wir anscheinend Fortschritte in der Mine machen. Der Dank dafür gebührt zu einem nicht unerheblichen Maß Euren Bemühungen. Nun können wir uns anderen Belangen zuwenden.$B$BMeine Späher haben berichtet, dass eine Abteilung des Scharlachroten Kreuzzugs südöstlich von hier ein Lager errichtet. Der Scharlachrote Kreuzzug ist eine verabscheuungswürdige Organisation, die uns jagt, und sie wird nicht ruhen, ehe nicht alle Untoten - ganz gleich, ob Teil der Geißel des Lichkönigs oder nicht - vernichtet sind. Wir müssen ihnen zuvorkommen!$B$BSeid vorsichtig, denn der unheilige Eifer dieser Leute macht sie zu gefährlichen Gegnern.",
    ["O"] = "Bringt Exekutor Arren insgesamt 12 Scharlachrote Armbinden von Scharlachroten Konvertiten und Scharlachroten Initianden.",
    ["T"] = "Der Scharlachrote Kreuzzug",
  },
  [382] = {
    ["D"] = "Berichte meiner Vorgesetzten in Brill weisen darauf hin, dass ein Agent des Scharlachroten Kreuzzugs vom Scharlachroten Kloster hierher nach Todesend entsendet wurde. Nach diesen Berichten führt dieser Bote wichtige Informationen mit sich. Ich kann nicht mit Einzelheiten dienen, aber was auch immer es ist, wir müssen es haben!$B$BIhr seid die qualifizierteste Person, die ich darauf ansetzen kann, $N. Findet diesen Boten und bringt mir alle Informationen, die er bei sich trägt.",
    ["O"] = "Tötet Meven Korgal, den Boten, im Lager der Kreuzzügler und kehrt danach mit allen Informationen, die Ihr findet, zu Exekutor Arren zurück.",
    ["T"] = "Der Rote Bote",
  },
  [383] = {
    ["D"] = "Das muss zu meinem Vorgesetzten, Exekutor Zygand, nach Brill gebracht werden. Mit diesen Informationen können wir dem Scharlachroten Kreuzzug einen entscheidenden Schlag versetzen. Eure Dienste waren hier in Todesend nützlich, aber ich benötige jemanden, der dies zu Zygand bringt, und ich denke, er wird angemessenere Arbeiten für Euch finden. $B$BNehmt die Straße nach Norden, hinaus aus Todesend. Kurz nachdem Ihr nach Tirisfal hineinkommt, trefft Ihr auf eine Kreuzung. Nehmt die Abzweigung nach Osten und behaltet diese Richtung dann bei. Ihr werdet unterwegs am Verlassenen Anwesen vorbeikommen.",
    ["O"] = "Bringt die Dokumente des Scharlachroten Kreuzzugs zu Exekutor Zygand nach Brill.",
    ["T"] = "Entscheidende Informationen",
  },
  [384] = {
    ["D"] = "Meine Tavernengäste mögen nichts so sehr wie meine Eberrippchen in Biersauce. Das einzige Problem dabei ist, dass der ortsansässige Fallensteller, der mich bisher mit den Zutaten versorgt hat, in die Armee des Königs eingetreten ist, um im Krieg an der Allianzfront zu helfen. $B$BVielleicht könnt Ihr mir helfen? Wenn Ihr mir ein paar Klippeneberrippchen bringt und ein Rhapsodiemalz von der Taverne hier besorgt, bekommt Ihr das Familienrezept für meine berühmten Eberrippchen in Biersauce und nicht zu vergessen natürlich eine Kostprobe! Das Geheimnis liegt im Malz!",
    ["O"] = "Ragnar Donnerbräu in Kharanos benötigt 4 Klippeneberrippchen und einen Krug Rhapsodiemalz.",
    ["T"] = "Eberrippchen in Biersauce",
  },
  [385] = {
    ["D"] = "So manchen Jäger zieht es an den Loch Modan, um unsere berühmten Krokis zu erlegen. Irgendein Händler sucht immer Krokiliskenhäute für seine Kleidungsstücke oder Rüstungen und es gibt auch einige Leute, denen ihr Fleisch mundet.$B$BWir handeln ein wenig damit, aber nicht wirklich viel, denn die Krokilisken sind wild und haben sich auf den Inseln im Loch verschanzt. Aber ich will Euch nicht davon abbringen, denn es ist ein unvergessliches Erlebnis, mit den Kiefern der Bestien zu ringen.$B$BAch ja, das eine Mal damals...",
    ["O"] = "Besorgt 5 Stücke Krokiliskenfleisch und 6 Krokiliskenhäute für Marek Eisenherz in der Jagdhütte der Weltenwanderer.",
    ["T"] = "Krokiliskenjagd",
  },
  [386] = {
    ["D"] = "Targorr der Schreckliche diente als oberster Exekutor unter Gath\'llzogg. Er ging grausam vor, selbst nach den Standards der dreckigen Orcs. Menschen, die tapfer gekämpft hatten, um das Königreich zu verteidigen, wurden nur zu seiner Belustigung gefoltert. Heute wird er im Verlies von Sturmwind gefangen gehalten und wurde zum Tode verurteilt. Dennoch stimmt irgendetwas nicht. Einer dieser bürokratischen Adeligen hat seine Exekution ausgesetzt. Ich bin mir sicher, hier wird eine Schandtat geplant.$B$BMacht Targorr dem Schrecklichen ein Ende, $N. Reist zum Verlies und enthauptet ihn, bevor man uns überlistet.",
    ["O"] = "Bringt Wache Berton in Seenhain den Kopf von Targorr dem Schrecklichen.",
    ["T"] = "Verbrechen lohnt sich nicht",
  },
  [387] = {
    ["D"] = "Der Aufstand muss niedergeschlagen werden! Ich brauche Eure Hilfe. Wenn jemand mitbekommt, dass das Gefängnis überrannt wurde, verliere ich meinen Job! Solch übles Spiel habe ich noch nie erlebt. Aber was jetzt zählt ist, die Gefangenen zur Sicherheit Sturmwinds niederzuringen.$B$BDie Strafe für Auflehnung lautet Tod. Tötet einige dieser Abweichler! Das sollte die Angelegenheit wieder unter Kontrolle bringen.",
    ["O"] = "Aufseher Thelwasser aus Sturmwind will, dass Ihr im Verlies 10 Gefangene, 8 Sträflinge und 8 Aufrührer tötet.",
    ["T"] = "Niederschlagung des Aufstandes",
  },
  [388] = {
    ["D"] = "Verbitterung fließt wie ein kalter Fluss durch meine Adern. Mein Enkel, Mac, war so ein guter Junge. Gerade mal 14 Jahre alt. Als die Armee an die Front geschickt wurde, griff Mac zu den Waffen, um die Stadt zu verteidigen. Er war eine Wache im Verlies, wo diese elenden Defias eingesperrt sind. Mac wies ein paar der Gefangenen dafür zurecht, dass sie ihre Wolldecken zerrissen, um Schals daraus zu machen, die ein Symbol ihrer Bande darstellen. Dafür wurde Mac hinterhältig erstochen.$B$BIch will Rache. Bringt mir 10 ihrer kostbaren Wollkopftücher. Sie haben die Farbe von Blut.",
    ["O"] = "Nikova Raskol von Sturmwind will, dass Ihr 10 rote Wollkopftücher für sie sammelt.",
    ["T"] = "Die Farbe von Blut",
  },
  [389] = {
    ["D"] = "Van Cleef und ich waren beide Mitglieder der Steinmetzgilde. Unser Hauptprojekt war der Wiederaufbau Sturmwinds nach dem Krieg.$B$BAls wir unsere Aufgabe erfüllt hatten, wurden wir betrogen. Die Adligen verweigerten uns die Bezahlung für unsere Arbeit. Einigen der älteren Steinmetze wurden Posten in der Regierung angeboten, aber van Cleef lehnte aus Loyalität zu den übrigen Steinmetzen ab. Er führte eine Revolte an und verließ, Rache schwörend, die Stadt.$B$BSein Helfer, Bazil Thredd, könnte vielleicht mehr über van Cleefs Pläne wissen. Er wird im Verlies gefangen gehalten.",
    ["O"] = "Sprecht mit Aufseher Thelwasser im Verlies.",
    ["T"] = "Bazil Thredd",
  },
  [390] = "_",
  [391] = {
    ["D"] = "Lasst uns eine Sache klarstellen, $N: Ich traue Euch nicht. Aber wie die Sache im Moment aussieht, werdet Ihr ohnehin keinen Unterschied machen.$B$BAlso wird das folgendermaßen ablaufen: Geht da runter und redet mit Thredd. Wenn Ihr wirklich auf unserer Seite seid, dann eliminiert ihn und bringt mir seinen Kopf, wenn Ihr fertig seid. Und falls sich herausstellt, dass Ihr auf seiner Seite seid und nicht herauskommt... werdet Ihr zusammen mit dem Rest dieser Würmer sterben, wenn wir Euch finden.",
    ["O"] = "Tötet Bazil Thredd und bringt seinen Kopf mit zurück zu Aufseher Thelwasser im Verlies.",
    ["T"] = "Aufstand im Verlies",
  },
  [392] = {
    ["D"] = "In den ersten Jahren von Thredds Gefangenschaft bekam er niemals Besuch. Ich ging davon aus, dass er für die Bruderschaft keinen Nutzen mehr hatte und sie ihn daher fallen ließ.$B$BAber vor einigen Monaten hat sich das alles geändert; er bekam regelmäßig Besuch... einmal, manchmal sogar zweimal pro Woche. Es war ein seltsamer Mann, so ein ruhiger Typ. Ich hatte da so meinen Verdacht, aber alle seine Papiere waren in Ordnung.$B$BLaut seinen Dokumenten war sein Name Maelik; hier habt Ihr seine Beschreibung. Ich glaube nicht, dass er noch einmal kommen wird, um Thredd zu besuchen.",
    ["O"] = "Bringt die versiegelte Beschreibung von Thredds Besucher nach Sturmwind zu Baros Alexston.",
    ["T"] = "Der seltsame Besucher",
  },
  [393] = {
    ["D"] = "Ich habe wirklich keine Ahnung, $N. Aber es gibt da jemand anderen, der Euch vielleicht helfen kann, und wie es der Zufall so will, schuldet dieser Jemand mir einen Gefallen, weil... egal, das brauchen wir jetzt nicht zu vertiefen. Ihr könnt Meister Mathias Shaw vom SI:7 in der Kaserne in der Altstadt finden. Shaw hat seine Finger in den meisten zwielichtigen oder heimlichen Geschäften der Stadt.$B$BWenn also jemand etwas weiß, dann er.",
    ["O"] = "Bringt die versiegelte Beschreibung von Thredds Besucher nach Sturmwind zu Meister Mathias Shaw.",
    ["T"] = "Schatten der Vergangenheit",
  },
  [394] = {
    ["D"] = "Sucht Shaw in der Altstadt. Teilt ihm mit, dass Ihr Euch um die Bedrohung durch Lescovar gekümmert habt, und berichtet ihm von Eurer Beteiligung an den Plänen des Lords gegen die Stadt. Ich bin sicher, er wird stolz sein, einen weiteren Helden unter den Bürgern Sturmwinds zu haben.$B$BEr wird Euch ganz bestimmt zu einem Treffen mit dem Prinzen oder einem anderen Adligen schicken. Lasst Euch das nicht zu Kopfe steigen, $N. Die Adligen können noch so sehr glauben, dass sie die Stadt beherrschen, aber einige Dinge bleiben für die im Dunklen.",
    ["O"] = "Sprecht mit Mathias Shaw in Sturmwind.",
    ["T"] = "Der Kopf des Ganzen",
  },
  [395] = {
    ["D"] = "Ach, das hätte ich fast vergessen. Alexston kam vorbei, als Ihr unterwegs wart, und wünschte mit Euch zu sprechen. Zweifellos möchte er den Adligen über die Ränkespiele der Defiasbruderschaft berichten.$B$BEin kleiner Rat, $N: Sollte Euch jemand fragen, würde ich zwar alle Details offen legen, aber dabei nicht erwähnen, dass es Eure Hand war, die Lescovar erschlug. Vertraut mir. Ich... kenne mich aus... mit solchen Dingen.$B$BUnd was Trias angeht, nun, sprecht mit niemandem über ihn... niemals... wenn Ihr es vermeiden könnt.",
    ["O"] = "Sprecht mit Baros Alexston in Sturmwind.",
    ["T"] = "Das Ende der Bruderschaft",
  },
  [396] = {
    ["D"] = "Ich habe einen Bericht an den König vorbereitet. Und Ihr sollt sein Überbringer sein, zusammen mit einer Auflistung Eurer Ergebnisse. Macht Euch keine Sorgen, er wird Euch zweifellos für die Erfüllung Eurer Pflicht gegenüber den Leuten von Sturmwind loben.$B$BEure Audienz findet in Kürze statt, also solltet Ihr Euch besser beeilen, denn es ist schon ein Stück Weges bis zur Burg Sturmwind.",
    ["O"] = "Liefert Baros Alexstons Bericht über die Bruderschaft der Defias bei König Varian Wrynn in der Burg Sturmwind ab.",
    ["T"] = "Eine Audienz beim König",
  },
  [397] = {
    ["D"] = "Wie ich höre, hat Lord Wischock sein Getränk genossen. $B$BIhr habt uns gut gedient, $N. Die Hexenmeister von Sturmwind werden nun, dank Euch, ohne Störung ihrem Tagewerk nachgehen können. Ich habe ein besonderes Geschenk vorbereitet, um Euch für Eure Mühen zu entlohnen. Sprecht mit meinem Diener, Zggi, um Eure Belohnung zu erhalten.",
    ["O"] = "Sprecht mit Zggi, dem Diener von Zardeth der Schwarzen Klaue, um Eure Belohnung zu erhalten.",
    ["T"] = "Ihr habt uns gut gedient",
  },
  [398] = {
    ["D"] = "Gesucht: Madenauge!$B$BDie selbst für einen Gnoll außerordentlich niederträchtige Kreatur wurde für ihre Verbrechen an den Verlassenen zum Tode verurteilt. Dieser verabscheuungswürdige Verbrecher hat Leichen für die Armee des Lichkönigs gestohlen. Madenauge wurde zuletzt gesehen, als er die Gegend nahe Garrens Schlupfwinkel nördlich von Brill plünderte.$B$BDie Stadt Brill bietet hiermit eine Belohnung für die fähige Seele, die die Exekution durchführt. Zeigt Exekutor Zygand die Tatze des Verbrechers, wenn die Tat getan ist.$B$BGnade ist etwas für die Schwachen.",
    ["O"] = "Tötet Madenauge und kehrt dann mit seiner Tatze zu Exekutor Zygand nach Brill zurück, um eine Belohnung zu erhalten.",
    ["T"] = "GESUCHT: Madenauge",
  },
  [399] = {
    ["D"] = "Es kommt mir so vor, als wäre es ewig her, dass ich als Junge in Westfall auf dem Hof gearbeitet habe. Man sagt ja, man kann niemals in der Zeit zurückgehen, und es stimmt wohl. In meinem Fall trifft das sogar gleich doppelt zu, denn das Heim meiner Familie wurde niedergebrannt und von Dieben besetzt.$B$BIch habe mit meinem Vater über den Verbleib einiger meiner Habseligkeiten gesprochen, darunter mein erster Kompass. Er konnte sie nicht retten. Allerdings meint er auch, dass sie auf dem Hof versteckt sein müssten.$B$BIhr findet Alexstons Bauernhof westlich der Späherkuppe. Könntet Ihr vielleicht dorthin gehen und mir meinen Kompass wiederbringen?",
    ["O"] = "Begebt Euch zu Baros Alexstons Haus in Westfall und sucht nach seinem Kompass. Übergebt ihm diesen dann am Kathedralenplatz von Sturmwind.",
    ["T"] = "Ein bescheidener Anfang",
  },
  [400] = {
    ["D"] = "Beldin Stahlrost ist der Besitzer der örtlichen Maschinenschlosserei, und er ist der beste Monteur von Belagerungsmaschinen, den es gibt! Allerdings geht er nicht sehr gnädig mit seinen Werkzeugen um. Ich schwöre Euch, er macht bald mehr Bogenlichtschraubenschlüssel kaputt, als wir ihm liefern können!$B$BWir haben gerade seine letzte Werkzeugbestellung fertig. Wenn Ihr sie ihm anliefert, wird er sich sicherlich erkenntlich zeigen.$B$BSein Geschäft, Stahlrosts Depot, liegt etwas nordöstlich von Kharanos. Und nebenbei, es handelt sich dort um einen örtlichen Treffpunkt für die Veteranen unter den Piloten der Belagerungsmaschinen, also hört Euch ruhig ein wenig um, während Ihr dort seid.",
    ["O"] = "Liefert Beldin Stahlrost seine Werkzeuge.",
    ["T"] = "Werkzeuge für Stahlrost",
  },
  [402] = {
    ["D"] = "Tut mir leid, aber bei dem Zustand, in dem die Stadt sich derzeit befindet, gibt es leider andere Dinge, die meine Aufmerksamkeit erfordern. Vielleicht kann ich diese Übersetzung später beenden, falls wir überleben.",
    ["O"] = "Schaut später noch einmal nach, ob Sirra immer noch beschäftigt ist.",
    ["T"] = "Sirra ist beschäftigt",
  },
  [405] = {
    ["D"] = "Bethor Eismacht, ein hochrangiger und mächtiger Magier aus Unterstadt im Süden, befiehlt mir, ihm einen Agenten zu schicken, der seinen Wert gegen die Geißel bereits bewiesen hat. Ihr, $N, werdet dieser Agent sein.$B$BZeigt Bethor diese Befehle. Er wird Euch daraufhin über Eure Mission aufklären. Ich kenne keine Details, aber es hat etwas damit zu tun, einen eigenwilligen Lich zu rekrutieren. Und es wird gefährlich sein.$B$BAlso macht Euch bereit, $C. Ihr dürft nicht scheitern.$B$BIhr dürftet Bethor im Magieviertel von Unterstadt finden.",
    ["O"] = "Zeigt Bethor Eismacht in Unterstadt Sevrens Befehle.",
    ["T"] = "Der abtrünnige Lich",
  },
  [406] = "_",
  [407] = {
    ["D"] = "Harmlose Kürbisse, oder? Zumindest sehen sie so aus. Wenn wir Arthas\' Vormarsch im Norden und die menschliche Plage im Süden stoppen wollen, müssen wir alle Möglichkeiten unseres untoten Daseins nutzen.$B$BMit etwas Einfallsreichtum wird sogar aus einem einfachen Kürbis ein Agent für unsere Dunkle Fürstin. Dieser Kürbis wird sich, wenn nach meiner jüngsten Formel behandelt, als eine besondere Leckerei erweisen.$B$BEs wurde schon wieder ein Scharlachroter Zelot gefangen genommen, der derzeit im Keller der Taverne Zur Galgenschlinge gefangen gehalten wird. Bringt dem Trottel diesen Kürbis.",
    ["O"] = "Bringt den getränkten Kürbis zum gefangenen Scharlachroten Zeloten, der im Keller der Taverne Zur Galgenschlinge gefangen gehalten wird.",
    ["T"] = "Felder des Kummers",
  },
  [408] = {
    ["D"] = "Die Gegend um Agamands Mühlen hat sich zu einem Außenposten der Geißel entwickelt. Am stärksten sind sie in der Gruft der Familie Agamand, wo ihr örtlicher militärischer Anführer, ein Skelett namens Hauptmann Dargol, sein Hauptquartier hat. Er hat die Vorfahren der Agamands durch Nekromantie wiedererweckt und hat vor, sie gegen uns einzusetzen.$B$BDies darf nicht geschehen, und Ihr müsst es verhindern. Reist zur Gruft der Familie Agamand und besiegt die erweckten Vorfahren der Agamands.$B$BUnd bringt mir den Schädel von Hauptmann Dargol.",
    ["O"] = "Tötet 8 Klagende Vorfahren und 8 Faulende Vorfahren.$B$BTötet Hauptmann Dargol und bringt seinen Schädel zu Magistrat Sevren nach Brill.",
    ["T"] = "Die Familiengruft",
  },
  [411] = {
    ["D"] = "Bringt meinem alten Kameraden Bethor diesen Netheredelstein. Er wird uns ermöglichen, miteinander zu kommunizieren... und ich habe ihm eine Menge zu erzählen.",
    ["O"] = "Bringt den Netheredelstein zu Bethor Eismacht nach Unterstadt.",
    ["T"] = "Die Rückkehr des abtrünnigen Lichs",
  },
  [412] = {
    ["D"] = "Ich sollte nach Gnomeregan zurückkehren und meine Forschungen für eine Heilung der verstrahlten Gnome vorantreiben.$B$BIch bin aber in Kharanos geblieben, weil es meine Heimatstadt ist und ich dabei helfen wollte, sie vor den Übergriffen der Frostmähnen zu schützen. Ich arbeite daran, die Dampfpanzer zu reparieren und in Ordnung zu halten.$B$BFragt mich nicht warum, aber die Strauchdiebe der Frostmähnen interessieren sich sehr für die gyromechanischen Getriebeteile, die ich für meine Reparaturen brauche. Sie treiben sich an der Frontlinie der Frostmähnen südöstlich der Stadt herum. Würdet Ihr mir dabei helfen, die Getriebeteile zurückzubekommen?",
    ["O"] = "Bringt Razzle Flinkspross 8 gyromechanische Getriebeteile.",
    ["T"] = "Operation Rekombobulationsgerät",
  },
  [415] = {
    ["D"] = "Rejold Gerstenbräu aus Bräuhall hat nach Euch gefragt.$B$BEr möchte mit Euch über ein neues Getränk reden, das er gebraut hat... er sagte etwas davon, dass Ihr ihm vielleicht dabei helfen könntet. Vielleicht wollt Ihr also wieder nach Bräuhall zurückkehren, um herauszufinden, worum es geht.$B$BEs könnte etwas Freibier für Euch drin sein...",
    ["O"] = "Sprecht mit Rejold Gerstenbräu.",
    ["T"] = "Rejolds neues Gebräu",
  },
  [416] = {
    ["D"] = "Wir haben hier in der Gegend ein Problem mit einer echten Plage. Kobolde der Tunnelratten sind ins Vorgebirge eingedrungen, haben dort ihre Baue eingerichtet und verdrecken unser Land mit ihrem Abfall!$B$BWir wollen, dass sie ausgelöscht werden. Jagt sie, bringt mir ihre Ohren und kassiert das Kopfgeld.$B$BIhr findet die größte Ansammlung von Kobolden der Tunnelratten im Osten in und um die Silberbachmine herum.",
    ["O"] = "Bringt Gebirgsjäger Sturmlanze an der Station Algaz 8 Tunnelrattenohren.",
    ["T"] = "Rattenfang",
  },
  [417] = {
    ["D"] = "Hangflug schrieb in sein Tagebuch, dass er von Marterklaue, einem \"gewaltigen Eisklauenbären\", angegriffen wurde. Er hat die Bestie verjagt, aber nicht, ohne vorher übel zugerichtet zu werden.$B$BWenn man sich den Zustand von Hangflugs Leiche so ansieht, muss Marterklaue später wiedergekommen sein und ihn getötet haben.$B$BJedenfalls schrieb Hangflug in sein Tagebuch einen letzten Wunsch:$B$BRache an Marterklaue.",
    ["O"] = "Tötet Marterklaue.$B$BBringt Pilot Hammerfuß am Südtoraußenposten die räudige Klaue des Bären und Hangflugs Tagebuch.",
    ["T"] = "Pilotenrache",
  },
  [418] = {
    ["D"] = "Es gibt hier in Thelsamar immer genug Mäuler zu stopfen, mit all den Kindern, die ein und aus gehen, und den Arbeitern von den Ausgrabungsstätten, die nach einem arbeitsreichen Tag hereinkommen. Wir sind berühmt für unsere Blutwurst, Ihr habt sie nicht zufällig schon einmal probiert?$B$BNein? Nun, hierzulande muss man für seine Mahlzeiten arbeiten, und glaubt nur nicht, dass Ihr eine Ausnahme seid, nur weil Ihr $Gein toller:eine tolle:c; $C seid.$B$BIch brauche Bärenfleisch, Ebergedärme für die Pelle und Spinnensekret als Würzmittel. Bringt mir das alles und überlasst dann Vidra das Kochen!",
    ["O"] = "Bringt Vidra Ofenglut in Thelsamar 8 Stück Bärenfleisch.",
    ["T"] = "Blutwurst aus Thelsamar",
  },
  [419] = {
    ["D"] = "Mein Freund Mori Hangflug, ebenfalls ein Pilot der Belagerungsmaschinen, hat sich in den Hügeln verirrt. Wir haben nach einem seltenen Erz gesucht, das man für hochwertiges Sprengpulver benötigt, und während unserer Suche hat er seine Maschine einen steilen Hügel hinaufgefahren und sie dabei kaputt gemacht!$B$BImmer noch überzeugt davon, dass es in diesen Bergen Erz gibt, hat er mich beauftragt, unsere Maschinen zu bewachen, während er zu Fuß weitersuchen wollte.$B$BDas ist jetzt mehrere Tage her, und ich habe des Nachts nördlich vom Helmsbettsee und westliches des Tunnels das Knurren von Raubtieren gehört. Mori ist ein zäher Bursche, aber ich mache mir Sorgen.$B$BBitte, $N. Sucht ihn.",
    ["O"] = "Pilot Hammerfuß am Südtoraußenposten möchte, dass Ihr den Piloten Hügelflug findet.",
    ["T"] = "Der vermisste Pilot",
  },
  [420] = {
    ["D"] = "Die Troggs im Tunnel sind extrem feindselig und scheinen nicht zu zögern, durch den Tunnel reisende Leute anzugreifen. Ihr seid aber offenbar hart genug im Nehmen, also solltet ihr wahrscheinlich sicher sein.$B$BEine Wegbeschreibung? Wenn Ihr mit Senir sprechen wollt, findet Ihr ihn in Kharanos.$B$BArbeitet Euch durch den Tunnel und folgt einfach weiter der Straße, wenn Ihr auf der anderen Seite herauskommt, denn sie führt direkt nach Kharanos.",
    ["O"] = "Überbringt Senir Weißbart in Kharanos Grelins Bericht.",
    ["T"] = "Senirs Beobachtungen",
  },
  [421] = {
    ["D"] = "Fürstin Sylvanas hat Varimathras damit beauftragt, die Gebiete der Menschen und Zwerge im Süden zu erobern.$B$BAber dieser Idiot Arugal - ein Scharlatan aus Dalaran und heutzutage eine verfluchte Kreatur der Burg Schattenfang - hat auf verantwortungslose Weise mit seiner Magie den strategischen Stützpunkt des Silberwaldes verheert.$B$BIch brauche einen geschickten Kämpfer, der dabei hilft, die Suppe auszulöffeln, die Arugal uns eingebrockt hat. Beweist Euch mir, indem Ihr 5 Weißskalps des Mondzorns tötet. Man findet die elenden Bestien oft nur ein kleines Stück abseits der Straße, am Fuße des Hügels dort unten.",
    ["O"] = "Dalar Morgenweber auf dem Grabmal will, dass Ihr 5 Weißskalps des Mondzorns tötet.",
    ["T"] = "Wertbeweis",
  },
  [422] = {
    ["D"] = "Wie Arugal in die Kirin Tor aufgenommen wurde, ist mir schleierhaft. Seine Zauberkunst schien so billig wie schlecht nachgemachter Schmuck.$B$BAm wichtigsten ist es erst einmal, herauszufinden, welche Art von Magie Arugal genau verwendet hat, damit ich sie gegen ihn verwenden und den Silberwald für die Dunkle Fürstin einnehmen kann.$B$BArugal hielt sich zuerst auf der Weizenfarm etwas nördlich der Brücke auf. Einer der Todespirscher berichtete, dass er einige Zauberbücher gesehen hätte, sie aber nicht beschaffen konnte. Beschafft mir den Zauber namens \'Arznei des Arugal\' aus diesen Büchern.",
    ["O"] = "Beschafft Dalar Morgenweber auf dem Grabmal die Arznei des Arugal.",
    ["T"] = "Arugals Torheit",
  },
  [423] = {
    ["D"] = "Bei der Untersuchung von Arugals Arbeit haben sich meine schlimmsten Befürchtungen bestätigt. Der alte Taugenichts war nicht einmal qualifiziert genug, um die Nachttöpfe in Dalaran zu leeren, davon, die Kirin Tor in ihrer schwersten Stunde zu repräsentieren, ganz zu schweigen. Narren!$B$BArugal benutzte verzauberte Gegenstände, um seine schwache Magie zu verstärken. Ich muss diese Gegenstände selbst untersuchen. Zieht los und erschlagt so lange Nimmersatts des Mondzorns und Dunkelseelen des Mondzorns, bis Ihr genug ihrer verzauberten Fesseln für meine Nachforschungen gesammelt habt. Man hat diese üblen Kreaturen im Norden und Osten gesehen.",
    ["O"] = "Bringt Dalar Morgenweber am Grabmal 6 Nimmersattfesseln und 3 Dunkelseelenfesseln.",
    ["T"] = "Arugals Torheit",
  },
  [424] = {
    ["D"] = "Ich werde tatsächlich länger brauchen, als ich gedacht hätte, die dunklen Geheimnisse hinter den Verzauberungen, die Arugal verwendete, zu entdecken.$B$BIn der Zwischenzeit müsst Ihr Euch um ein kleines Problem kümmern, das unsere Dunkelpirscher entdeckt haben. Es sieht so aus, als hätte Arugal seiner Magie erlaubt, sich bis zur Tiefenfelsmine in den Hügeln im Südosten auszubreiten.$B$BDiese Mine wäre eine ziemlich nützliche Ressource für Varimathras\' Vormarsch.$B$BIch will, dass Ihr Grimson den Blassen, den besudelten Großknecht der Mine, enthauptet. Mit seinem Tod sollte die Mine uns gehören.",
    ["O"] = "Tötet Grimson den Blassen und bringt seinen Kopf zu Dalar Morgenweber beim Grabmal.",
    ["T"] = "Arugals Torheit",
  },
  [425] = {
    ["D"] = "Mein Bruder Quinn und ich wurden beauftragt, den Silberwald auszukundschaften und Hochexekutor Hadrec Bericht zu erstatten. Aber wir kamen nicht weit - als wir über diese Farm gingen, wurden wir von einem Ghul namens Ivar der Verfaulte angegriffen.$B$BWir entkamen Ivar, aber er verletzte Quinn schwer. Dank des Trankes, den Ihr ihm gegeben habt, heilt er, aber Ivar könnte wiederkommen, bevor Quinn sich erholt hat.$B$BTötet Ivar, damit Quinn genug Zeit hat, sich zu regenerieren. Ivar hält sich üblicherweise in dem Schuppen da drüben auf und wartet darauf, dass meine Wachsamkeit nachlässt.",
    ["O"] = "Tötet Ivar den Verfaulten und bringt Ivars Kopf zu Rane Yorick auf dem Ivarfeld.",
    ["T"] = "Ivar der Verfaulte",
  },
  [427] = {
    ["D"] = "Die Dokumente, die Exekutor Arren uns zur Verfügung stellte, verschaffen uns genau die Atempause, die wir in unserem Kampf gegen den Scharlachroten Kreuzzug benötigen. Wir kennen jetzt dessen genaue Aufenthaltsorte überall in Tirisfal.$B$BAber leider haben die Todeswachen noch ganz andere Sorgen. Die Armee des Lichkönigs wächst jede Nacht an. Wir benötigen jemanden, der wie Ihr gern \"Initiative\" zeigt, um den Scharlachroten Kreuzzug ins Grab zu bringen.$B$BBeweist mir, dass Ihr der Dunklen Fürstin dienen könnt, indem Ihr nach Westen zum Turm hinter Sollidens Bauernhof reist und dort 10 Scharlachrote Krieger erschlagt.",
    ["O"] = "Exekutor Zygand von Brill verlangt, dass Ihr 10 Scharlachrote Krieger tötet.",
    ["T"] = "Im Krieg mit dem Scharlachroten Kreuzzug",
  },
  [430] = {
    ["D"] = "Hier habt Ihr den Trank, mit dem Ihr Quinn Yoricks Erholung beschleunigen könnt.$B$BWenn Ihr mich jetzt entschuldigen mögt, ich habe wirklich wichtige Dinge zu erledigen...",
    ["O"] = "Bringt Quinn Yorick auf dem Ivarfeld, nördlich des Grabmals, Quinns Trank.",
    ["T"] = "Rückkehr zu Quinn",
  },
  [432] = {
    ["D"] = "Diese vom Licht verfluchten Troggs verwandeln meinen Arbeitsplatz in eine Ruine. Schaut Euch das an, da geht schon wieder ein Pulverfass hoch.$B$BOh, ich wünschte ich könnte da runtergehen und allen einzeln ihre dürren Hälse umdrehen! Ich will sie alle töten!$B$BDas kann nicht gut für meine Gesundheit sein, all dieser Stress und diese Wut... Was haben sie mir gelassen? Nichts!$B$BIch würde sie persönlich umbringen, aber ich ziele nicht mehr so gut wie früher. Geht in den Steinbruch und tötet einige dieser dreckigen Mistviecher! Ich werde Euch bezahlen! Hauptsache, Ihr verletzt sie, tötet sie!",
    ["O"] = "Tötet im Steinbruch von Gol\'Bolar und in der Mine 8 Schädelhauer der Felsenkiefertroggs und 8 Knochenknacker der Felsenkiefertroggs.",
    ["T"] = "Diese verfluchten Troggs!",
  },
  [433] = {
    ["D"] = "Als Mitglied des Senats und der Forscherliga habe ich mir vorgenommen, die Zustände im Steinbruch zu inspizieren. Die Troggs sind außer Rand und Band!$B$BSie haben den Gol\'Bolar Steinbruch auf den Kopf gestellt. Als wir in die Tiefen des Erdreichs vorstießen, strömten sie ans Tageslicht, zerstörten unsere Ausrüstung und schlossen viele Bergarbeiter untertage ein.$B$BWir müssen sie von hier vertreiben. Ich habe Magier Überstunden machen lassen, damit diese Runen der Rückkehr verzaubert werden. Wenn Ihr sie in die Hände der gefangenen Minenarbeiter bekommen könnt, können sie unbeschadet zur Oberfläche zurückkehren.",
    ["O"] = "Verteilt Runen der Rückkehr an 7 gefangene Minenarbeiter in der Gol\'Bolarmine.",
    ["T"] = "Im Dienste des Volkes",
  },
  [434] = {
    ["D"] = "Nachdem ich dem Spionagebot die Verkleidung angelegt habe, begibt er sich hinein, um mit Lescovar zu sprechen. Die Wachen sollten ihn ohne Probleme durchlassen.$B$BWährend Ihr Material für mich gesammelt habt, habe ich Marzon darüber informiert, dass es für ihn und Lescovar an der Zeit sei, sich wieder zu treffen. Ich habe sogar einige falsche Informationen verlautbaren lassen.$B$BLescovar wird seine Wachen wegschicken und hier draußen im Garten auf Marzon warten. Tretet in Aktion, wenn Ihr etwas über die Defias hört. Ihr müsst sie beide eliminieren. Kapiert?$B$BWenn Ihr das erledigt habt, sprecht Ihr mit Trias.",
    ["O"] = "Bleibt im Garten, bis Lord Gregor Lescovar und Marzon die Stille Klinge ihr Gespräch beendet haben. Schaltet sie danach beide aus, bevor Ihr nach Sturmwind zu Elling Trias zurückkehrt.",
    ["T"] = "Der Angriff!",
  },
  [435] = {
    ["D"] = "Die Wölfe da draußen wollen nicht abhauen, und jedes Mal, wenn ich einen Schritt aus der Tür mache, schauen sie mich böse an. Sie müssen unglaublich hungrig sein, wenn sie mich für essenswert halten.$B$BIch muss mich bei meiner Kampfgefährtin, Todespirscherin Rane Yorick, melden. Und um zu Rane zu kommen, brauche ich Hilfe gegen diese Wölfe.$B$BBitte begleitet mich! Ach ja, da draußen sind eine Menge Wölfe, wir hätten also eine bessere Chance, wenn Ihr Freunde dabei hättet, die ebenfalls helfen können...",
    ["O"] = "Begleitet Erland durch die Wölfe hindurch zu Rane Yorick.",
    ["T"] = "Eskorte für Erland",
  },
  [436] = {
    ["D"] = "Ausgrabungsleiter Eisenband leitet die Ausgrabung einiger alter Ruinen östlich des Sees. Er hat zuletzt wenig Fortschritte gemacht, besonders, wenn man all den Nachschub bedenkt, den wir ihm geschickt haben.$B$BEisenband ist ein bodenständiger, ehrlicher Zwerg, der gern Resultate sieht, und daher mache ich mir Sorgen, dass da irgendwelche Kräfte gegen ihn arbeiten.$B$BGeht zu Eisenbands Ausgrabungsstätte und sprecht mit Magmar Todesstreich. Er kümmert sich um die Einzelheiten an der Stätte und wird wissen, warum es so langsam voran geht.$B$BUm zur Ausgrabungsstätte zu gelangen, müsst Ihr um die Südspitze des Sees herum und dann nach Osten gehen.",
    ["O"] = "Sprecht mit Forscher Todesstreich.",
    ["T"] = "Eisenbands Ausgrabungsstätte",
  },
  [437] = {
    ["D"] = "Unsere ursprünglichen Spähberichte über den Silberwald zeigen, dass die Moderfelle auf einer alten Farm im Norden, nahe dem Todesfeld, einen Stützpunkt haben. Wir haben kleine Angriffe auf sie gestartet, aber jedes Mal werden sie von der Banshee Nachtseele unterstützt, die ihre Magie einsetzt, um unsere Truppen in die Flucht zu schlagen.$B$BWir wollen Nachtseele vernichtet sehen.$B$BGeht ins Todesfeld und greift die Moderfelle an. Falls Ihr Nachtseele nicht seht, tötet so lange Moderfellgnolle, bis sie auftaucht, und besiegt sie dann.$B$BIhre Essenz wird zu Staub zerfallen. Bringt mir diesen Staub als Beweis.",
    ["O"] = "Tötet Gnolle der Moderfelle auf dem Todesfeld.$B$BSobald Nachtseele auftaucht, tötet sie ebenfalls.$B$BBringt Nachtseeles Essenz zu Hochexekutor Hadrec beim Grabmal.",
    ["T"] = "Das Todesfeld",
  },
  [438] = {
    ["D"] = "$N, wir haben ein neues Nest der Geißel entdeckt: Die verfallene Fähre. Die Fähre liegt östlich des Grabmals und wird von Moderfellgnollen und anderen Untoten schwer bewacht.$B$BEs muss einen Grund dafür geben, warum die Geißel dieses Gebiet besetzt hat. Geht zur verfallenen Fähre und findet heraus, warum die Geißel sich dort aufhält.",
    ["O"] = "Geht zur verfallenen Fähre.$B$BSucht nach dem Grund, warum die Geißel dort ist.",
    ["T"] = "Die verfallene Fähre",
  },
  [439] = {
    ["D"] = "Aus den Leichen ragt eine nicht eingewickelte Hand heraus. Es handelt sich um die Hand einer Frau, von der Seuche pockennarbig, aber relativ frisch. $B$BAn der Hand befindet sich ein Ring. Als Ihr den Ring entfernt und untersucht, entdeckt Ihr eine Gravur:$B$B\"Für Deliah.\"",
    ["O"] = "Bringt Deliahs Ring zu Hochexekutor Hadrec beim Grabmal.",
    ["T"] = "Auf den Spuren der Moderfelle",
  },
  [441] = {
    ["D"] = "Deliah hatte einen Ehemann, Raleigh, der nun in Unterstadt sein Zuhause hat. Wenn die Deliah, die in Brill wohnte, die ist, der dieser Ring gehörte, dann wird es Raleigh wissen.$B$BRaleigh ist im Handwerksviertel von Unterstadt.",
    ["O"] = "Bringt Deliahs Ring zu Raleigh.",
    ["T"] = "Raleigh und Unterstadt",
  },
  [442] = {
    ["D"] = "Es ist an der Zeit, den Standort der Geißel im Silberwald zu stürmen: die Insel Fenris.$B$BIhr müsst Euch noch einmal auf diese Insel begeben und Thule Rabenklaue vernichten. Ich will seinen Kopf!$B$BDiese Mission ist so gefahrvoll, wie sie entscheidend ist, $N. Ihr dürft nicht scheitern.",
    ["O"] = "Bringt Hochexekutor Hadrec auf dem Grabmal Thules Kopf.",
    ["T"] = "Sturm auf die Insel Fenris",
  },
  [443] = {
    ["D"] = "Bevor wir einen Angriff auf die Insel Fenris starten können, müssen wir erst herausfinden, wer hinter den Moderfellgnollen steht. Unsere Apotheker glauben, ihr Ursprung liegt in der seltsamen Flüssigkeit, die durch ihre Körper fließt.$B$BGeht zur Insel Fenris, östlich der verfallenen Fähre, und sammelt Moderfellsekret von den Gnollen dort. Bringt es zu Apotheker Renferrel, draußen vor dem Grabmal.",
    ["O"] = "Bringt 8 Flaschen Moderfellsekret zu Apotheker Renferrel beim Grabmal.",
    ["T"] = "Moderfellsekret",
  },
  [444] = {
    ["D"] = "Ich habe das Sekret, das Ihr mir gebracht habt, in seine Grundbestandteile aufgelöst. Meine Ergebnisse sind nicht sehr aufschlussreich, aber ich konnte ermitteln, dass Hexenmeister- und Totenbeschwörermagie zur Herstellung des Sekrets verwendet wurden.$B$BWährend ich weiter das Sekret untersuche, möchte ich, dass Ihr eine Probe davon zu Bethor Eismacht nach Unterstadt bringt. Bethor ist kein Apotheker, aber sein Wissen von der Magie könnte uns helfen, das Geheimnis um die Moderfelle zu lüften.$B$BGeht. Ihr dürftet Bethor im Magieviertel von Unterstadt finden.",
    ["O"] = "Bringt die Sekretprobe zu Bethor Eismacht nach Unterstadt.",
    ["T"] = "Die Ursprünge der Moderfelle",
  },
  [445] = {
    ["D"] = "Zeit ist ein Luxus, den sich diejenigen, die für Fürstin Sylvanas arbeiten, nicht leisten können. Aber das wisst Ihr inzwischen sicher selbst.$B$BAls Mitglied der königlichen Apothekervereinigung ist es meine Pflicht, mein Wissen mit meinen Kollegen zu teilen, auf dass unsere gemeinsamen Bemühungen der Dunklen Fürstin eines Tages die neue Seuche bescheren werden, die sie so ersehnt.$B$BBringt diese Erkenntnisse zu Apotheker Renferrel, der beim Grabmal im Silberwald stationiert ist. Folgt der Straße von Brill aus nach Süden. Geht dann nach Unterstadt südwestwärts.",
    ["O"] = "Bringt Apotheker Hollands Erkenntnisse zu Apotheker Renferrel im Silberwald.",
    ["T"] = "Lieferung in den Silberwald",
  },
  [446] = {
    ["D"] = "Dieses Sekret wurde von einem alten Kollegen von mir hergestellt: Thule Rabenklaue. Vor dem großen Krieg war er ein junger, aufstrebender Magier, aber als der Lichkönig immer mächtiger wurde, verriet er seine Brüder und verbündete sich mit der Geißel.$B$BIch hätte nicht gedacht, dass er noch lebt, geschweige denn, dass er die Moderfelle bei ihren Vorstößen gegen uns anführt.$B$BIch kenne viele seiner Zauber und kann dabei helfen, einen Trank zu brauen, der ihnen entgegenwirkt. Nehmt diese Spruchrolle mit zurück zu Apotheker Renferrel. Auf ihr stehen die Beschwörungsformeln, die man für den Trank braucht.",
    ["O"] = "Bringt Bethors Spruchrolle zu Apotheker Renferrel beim Grabmal im Silberwald.",
    ["T"] = "Thule Rabenklaue",
  },
  [447] = {
    ["D"] = "Arthas hat Truppen von schier endloser Zahl. Doch mit einer neuen Seuche könnten wir sowohl die Armee der Geißel als auch das menschliche Ungeziefer ein für alle Mal auslöschen.$B$BMeine Studien haben bewiesen, dass Spinnenblut, vermischt mit einem aus dem Herzen eines ergrauten Bären gewonnenen Toxin, zu einem tödlichen Elixier wird. Sammelt Proben von den Spinnen im Huschdunkel im Nordwesten und von den Bären, die im Silberwald umherstreifen. Bringt diese Reagenzien zu Apothekermeister Faranell von der königlichen Apothekervereinigung in Unterstadt.",
    ["O"] = "Sammelt 6 Herzen von ergrauten Bären und 6 Proben Huschendes Blut und bringt diese zu Apothekermeister Faranell nach Unterstadt.",
    ["T"] = "Rezept für den Tod",
  },
  [449] = {
    ["D"] = "Auch wenn Quinns Wunden heilen, möchte ich doch unseren Bericht so schnell wie möglich zu Hochexekutor Hadrec bringen. Er enthält Informationen, die für die Besetzung des Silberwaldes durch die Verlassenen von höchster Wichtigkeit sind.",
    ["O"] = "Bringt den Todespirscherbericht zu Hochexekutor Hadrec beim Grabmal.",
    ["T"] = "Der Bericht der Todespirscher",
  },
  [450] = {
    ["D"] = "Ihr könnt der königlichen Apothekervereinigung noch weiter zu Diensten sein, $N.$B$BAls Arugal seine elenden Zauber über den Wald gelegt hat, verfluchte er eines unserer vielversprechendsten Mitglieder, Apotheker Berard. Als hirnloser Diener von Arugal ist Berard jetzt für uns nutzlos. Aber seine Erkenntnisse könnten sich als wertvoll erweisen. Er untersuchte eine längst vergessene Seuche, die vor Ewigkeiten einmal alles Leben im Lordameresee getötet hat.$B$BBergt Berards Tagebuch aus seinem Studierzimmer im Gasthaus von Lohenscheit und bringt es zu Apotheker Renferrel.",
    ["O"] = "Bergt Apotheker Berards Tagebuch aus Lohenscheit und bringt es zu Apotheker Renferrel beim Grabmal.",
    ["T"] = "Rezept für den Tod",
  },
  [451] = {
    ["D"] = "Laut Berards Tagebuch könnte sich das Moos von Seeschleichern und Seekrabblern bei unserer Suche nach einer neuen Seuche als nützlich erweisen. Diese Bestien leben auf den Inseln im Lordameresee im Osten.$B$BEr erwähnt außerdem einen sehr seltenen verhärteten Tumor, den er bei einem Finsterflossenmurloc entdeckte. Anscheinend war der Tumor so selten, dass Berard nur einen einzigen auftreiben konnte.$B$BSammelt Proben von diesem Moos sowie von dem seltenen verhärteten Tumor und bringt beides zu Apothekermeister Faranell nach Unterstadt.",
    ["O"] = "Bringt 6 Proben Seeschleichermoos, 6 Proben Seekrabblermoos und einen verhärteten Tumor zu Apothekermeister Faranell nach Unterstadt.",
    ["T"] = "Rezept für den Tod",
  },
  [452] = {
    ["D"] = "Ihr kommt gerade rechtzeitig, $N!$B$BDer Rat von Lohenscheit wird jeden Moment zurückkehren. Arugal muss sich in der Burg um andere Dinge kümmern. Er benutzt den Rat als reine Schachfiguren, um die hirnlosen Diener von Lohenscheit unter seiner Kontrolle zu halten. Aber ich glaube, dass die Söhne von Arugal sich ohne die Führung durch den Rat von Lohenscheit gegen ihren Meister stellen werden.$B$BIch werde nicht allein mit ihnen allen fertig. Ihr müsst mir helfen. Gemeinsam können wir den Rat für Fürstin Sylvanas zerschlagen.",
    ["O"] = "Helft Todespirscher Faerleia, den Rat von Lohenscheit zu töten.",
    ["T"] = "Hinterhalt in Lohenscheit",
  },
  [453] = {
    ["D"] = "Viel Glück, $N. Ich hoffe, Ihr könnt den Kerl finden, und dass er dann noch am Leben ist.$B$BWenn er die Sicherheit von Dunkelhain hinter sich gelassen hat, könnte er leicht Wildtieren zum Opfer gefallen sein.",
    ["O"] = "Findet die Schattengestalt. Eure Hinweise:$B$BEr stammt nicht aus Dunkelhain.$B$BEr ist eine nervöse, hektische Person.$B$BEr verließ Dunkelhain und ging nach Westen.",
    ["T"] = "Auf der Suche nach der Schattengestalt",
  },
  [454] = {
    ["D"] = "Wir hatten vor dieser... Geschichte die Pulverfässer in mehreren Fuhren transportiert. Jetzt müssen wir die Arbeit aufteilen. Miran nimmt die Fässer, und ich passe auf die anderen auf.$B$BIch fühle mich nicht wohl dabei, Miran den Rest der Fracht ganz allein transportieren zu lassen. Vielleicht könntet Ihr ihn auf seiner nächsten Tour begleiten, wenn er fertig zur Abreise ist?",
    ["O"] = "Sprecht mit Miran.",
    ["T"] = "Nach dem Hinterhalt",
  },
  [455] = {
    ["D"] = "Orcs vom Drachenmalklan haben im Sumpfland eine Zufluchtstätte. Und jetzt haben sie sich nach Dun Algaz ausgebreitet. Wir können nicht zulassen, dass sie ihren Einfluss auf dieses Gebiet behalten, sonst wird der Loch Modan vom Hafen von Menethil, unserer Hafenfestung im Sumpfland, abgeschnitten.$B$BBrecht durch Dun Algaz in das dahinterliegende Sumpfland durch und tötet dabei ein paar Orcs. Meldet Euch bei Valstag Eisenkiefer im Hafen von Menethil.$B$BNach Menethil kommt Ihr, indem Ihr Dun Algaz durchquert, anschließend nordwärts der Straße folgt und Euch dann an der Biegung westwärts haltet.$B$BWir verlassen uns auf Euch, $N. Ihr dürft nicht scheitern.",
    ["O"] = "Durchquert Dun Algaz.$B$BTötet 8 Späher des Drachenmals und 6 Grunzer des Drachenmals.$B$BMeldet Euch bei Valstag Eisenkiefer im Hafen von Menethil.",
    ["T"] = "Der Algaz-Spießrutenlauf",
  },
  [456] = {
    ["D"] = "Seid gegrüßt, $N. Ich bin Konservator Ilthalaine. Meine Aufgabe im Laubschattental ist es, für den Erhalt des Gleichgewichts der Natur zu sorgen.$B$BDie Frühjahrsniederschläge waren dieses Jahr besonders heftig, was dafür sorgte, dass einige Tiere des Waldes gediehen, während andere darunter litten.$B$BUnglücklicherweise sind die Bestände an Nachtsäblern und Distelebern zu stark angewachsen. Das Laubschattental kann nur eine gewisse Menge Futter für diese Tiere erzeugen. Zieht los, $C, und dünnt die Eber- und Säblerpopulation etwas aus, auf dass die Harmonie der Natur erhalten bleibt.",
    ["O"] = "Tötet 3 junge Nachtsäbler sowie 3 junge Disteleber.",
    ["T"] = "Das Gesetz der Natur",
  },
  [457] = {
    ["D"] = "Die Jungbestände einiger Kreaturen hier im Laubschattental auszudünnen, war ein guter Anfang, $N, aber es gibt immer noch etwas zu tun.$B$BDie Ressourcen des Waldes werden sich zu schnell erschöpfen, wenn sich niemand um das Problem kümmert. Mutter Naturs Wildtiere zu töten, ist ein notwendiges Übel zum Besten von allen, die sich das Land teilen. Begebt Euch in den Nordosten des Waldes und erschlagt im Namen des Gleichgewichts räudige Nachtsäbler und Disteleber.",
    ["O"] = "Tötet 6 räudige Nachtsäbler und 6 Disteleber.",
    ["T"] = "Das Gesetz der Natur",
  },
  [458] = {
    ["D"] = "Was ein Glück, dass Ihr hier seid, $C. Vom Wispern der Waldgeister wurden mir seltsame Neuigkeiten zugetragen.$B$BDie geheimnisvolle Beschützerin des Waldlandes, Tarindrella, ist in das Laubschattental zurückgekehrt. Jahrelang war die Gegenwart der Dryade in den Wäldern Kalimdors nicht zu spüren. Sicherlich stimmt irgendetwas nicht, wenn sie jetzt wieder in diese Gegend zurückgekommen ist.$B$BSucht Tarindrella und findet heraus, was sie in unseren Hain führt. Eine der Schildwachen berichtete, sie südwestlich von Aldrassil gesehen zu haben.",
    ["O"] = "Sucht die Dryade, die als Tarindrella bekannt ist.",
    ["T"] = "Dämonische Diebe",
  },
  [459] = {
    ["D"] = "Teuflische kleine Grells! Die haben sich wieder an unseren Nahrungsvorräten bedient. $B$BWenn Ihr nichts dagegen hättet, uns zur Hand zu gehen: Was auch immer sie gestohlen haben, liegt sicherlich in den Lagern der Grells westlich von hier verstreut. Ich würde Euch mit Freuden belohnen, wenn ihr alles zu mir zurückbringen könntet.",
    ["O"] = "Bringt Dellylahs gestohlene Bohnen, Dellylahs gestohlenes Wasser und Dellylas gestohlene Beutel zurück.",
    ["T"] = "Dämonische Diebe",
  },
  [460] = {
    ["D"] = "Ihr seht nicht aus wie ein Moderfell. Gut. Ich hatte die Hoffnung schon aufgegeben...$B$BMein Name ist Alaric und ich war einst Thules oberster Diener und Leibwächter. Er nutzte seine Magie, um mich vor der Pest des Untodes zu \"schützen\", ein schöner Schutz... denn sein Zauber bürdete mir ein Unleben auf, dem ich nicht entrinnen kann. Und nachdem er diese verfluchten Moderfellgnolle erschaffen hatte, hat Thule mich geköpft... und meinen Kopf den Gnollen zum Spielen gegeben!$B$BBitte, bringt mich zu meinem Körper. Ich glaube, die Gnolle haben ihn hier in der Burg Fenris, nahe den Stallungen, in einem namenlosen Grab verscharrt.",
    ["O"] = "Bringt Alarics Kopf zu seinem Grab.",
    ["T"] = "In Stücken begraben",
  },
  [462] = "_",
  [463] = {
    ["D"] = "Ihr habt vom Sumpfhüter gehört? Ihr sucht doch nicht etwa nach ihm, oder? Nun, ich behaupte ja, Ihr seid verrückt, wenn dem so ist, aber wer bin ich, dass ich $Geinen Narren:eine Närrin; davon abhalte, in den Tod zu gehen?$B$BFalls Ihr ihn sucht - hmmm, ich habe gehört, er sei in den Marschen, ostwärts von dort, wo die Straße nach Dun Modr abzweigt. Er lungert dort bei den Krokis und Schlimmerem herum!$B$BAch, und lasst Euer Geld besser hier. Ihr werdet es dort, wo Ihr hingeht, nicht brauchen... und Ihr wollt doch sicher nicht, dass der alte Grünling sich an Eurem Gold einen Zahn ausbricht, wenn er Euch mittendurch beißt.",
    ["O"] = "Findet den Sumpfhüter.",
    ["T"] = "Der Sumpfhüter",
  },
  [464] = {
    ["D"] = "Der orcische Drachenmalklan war am Ende des zweiten Krieges so gut wie vernichtet, nachdem er die Kontrolle über ihre Schoßtierchen, die roten Drachen, verloren hatte. Die Drachen waren nicht sehr freundlich zu ihren früheren Herren und machten gnadenlos Jagd auf die Drachenmalorcs. Die wenigen, die überlebten, flohen aus Grim Batol in die Berge östlich von Whelgars Ausgrabungsstätte.$B$BJetzt haben sie an Stärke gewonnen und besitzen ein Lager nordöstlich von Menethil.$B$BWir wollen, dass Ihr dieses Lager angreift. Sammelt und bringt mir ihre Kriegsflaggen als Beweis.",
    ["O"] = "Bringt Hauptmann Starkfaust 8 Kriegsflaggen des Drachenmals.",
    ["T"] = "Kriegsflaggen",
  },
  [465] = {
    ["D"] = "Während Ihr die Orcs des Drachenmalklans bekämpft habt, haben wir mehr über deren Pläne herausgefunden.$B$BNek\'rosh hat Katapulte gebaut und beabsichtigt, Menethil zu belagern! Und dank der Kämpfe in Dun Algaz sind wir effektiv von Verstärkungstruppen abgeschnitten!$B$BIhr müsst diese Katapulte zerstören. Sie befinden sich im Lager des Drachenmalklans.$B$BHier, nehmt diesen Zunder. Kämpft Euch zu den Katapulten durch und lasst sie mit dem Zunder in Flammen aufgehen.$B$BUnd nachdem Ihr das getan habt, findet und tötet Nek\'rosh. Wir müssen dieser Drachenmalbestie den Kopf abschlagen.",
    ["O"] = "Zerstört die Katapulte des Drachenmals.",
    ["T"] = "Nek\'roshs Schachzug",
  },
  [466] = {
    ["D"] = "Ich habe gehört, im Sumpfland wurde in einer Höhle nordwestlich vom Algaz-Tor ein seltenes Erz namens Pyrophor gefunden. Pyrophor ist instabil und schwer anzuwenden, ohne sich dabei selbst in die Luft zu sprengen, aber ich bin mir sicher, wenn man es auf die richtige Art und Weise verhüttet, könnte man daraus Kanonenkugeln mit einer ziemlichen Durchschlagskraft herstellen.$B$BBesorgt mir Pyrophorerz und ich finde jemanden, der mutig genug ist, damit zu experimentieren.$B$BOh, und um dieses Erz aus einer Ader zu schürfen, müsst Ihr Euch mit Bergbau auskennen. Wenn dem nicht so ist, dann... nehmt einen Freund mit in diese Höhle, der es tut!",
    ["O"] = "Bringt 6 Ladungen Pyrophorerz zu Pilot Steinsegel nach Dun Morogh.",
    ["T"] = "Auf der Suche nach Pyrophor",
  },
  [467] = {
    ["D"] = "Pilot Steinsegel liebt seine Belagerungsmaschine, wie jeder Pilot es auch sollte! Und er ist immer begierig, neue Wege zu finden, die Feuerkraft seines Panzers zu vergrößern!$B$BEr hat Gerüchte über ein seltenes Erz gehört und sucht jetzt nach jemandem, der es für ihn auftreibt. Wenn es Euch also in den Fingern juckt, dann sucht Steinsegel in Stahlrosts Depot in Dun Morogh auf und schaut mal, was er will.",
    ["O"] = "Sprecht mit Pilot Steinsegel.",
    ["T"] = "Steinsegels Suche",
  },
  [468] = {
    ["D"] = "Gebirgsjäger Felsgar sucht nach Freiwilligen für eine Mission durch Dun Algaz und in das Sumpfland hinein. $B$BWenn Ihr also Lust habt, Euch Eure Stiefel in dieser sumpfigen Gegend im Norden schmutzig zu machen, dann sprecht mit Felsgar in Dun Algaz. Dun Algaz liegt weiter die Straße entlang im Norden, hinter dem Wachturm.",
    ["O"] = "Sprecht mit Gebirgsjäger Felsgar.",
    ["T"] = "Meldung bei Gebirgsjäger Felsgar",
  },
  [469] = {
    ["D"] = "Seid gegrüßt!$B$BIhr seid nicht zufällig in Richtung Menethil unterwegs? Falls nicht, würde ich Euch empfehlen, dort Halt zu machen, denn es ist ein guter Ort, um Waren zu kaufen oder etwas trinken zu gehen und sich auszuruhen.$B$BTja, wenn Ihr also zufälligerweise in diese Richtung unterwegs seid, dann habt Ihr doch sicher nichts dagegen, dieses Bündel Krokiliskenhäute zu James Halloran, dem Gerber, zu bringen?$B$BIch würde es ja selbst tun, aber meine Frau ist in letzter Zeit ziemlich launisch, und ich würde es gern so lange wie möglich aufschieben, ihr zu begegnen.",
    ["O"] = "Liefert das Bündel Krokiliskenhäute bei dem Gerber James Halloran im Hafen von Menethil ab.",
    ["T"] = "Tägliche Lieferung",
  },
  [470] = {
    ["D"] = "Bei meinem wöchentlichen Besuch an Eisenbarts Grabmal wurde ich von grässlichen, triefenden Schlammen angegriffen! Natürlich geriet ich in Panik.$B$BIch habe meine Tasche nach einem geworfen, aber das zeigte keinerlei Wirkung! Der Schlamm saugte sie einfach auf... Glücklicherweise gab mir das genug Zeit wegzulaufen.$B$BDie schlechte Nachricht ist, dass ich dringend den Inhalt meiner Tasche benötige!$B$BIch weiß nicht, nach welchem Schlamm ich meine Tasche geworfen habe; sie sehen alle gleich aus! Eisenbarts Grabmal liegt nordöstlich von hier, in der Nähe von Dun Modr. Ich danke Euch wirklich sehr!",
    ["O"] = "Ein Schlamm bei Eisenbarts Grabmal hat Sidas Tasche. Bergt sie und bringt sie ihr zurück in den Hafen von Menethil.",
    ["T"] = "Stochern im Schlamm",
  },
  [471] = {
    ["D"] = "Ich habe meinen Lehrling in die Marschen geschickt, um einige unserer berühmten riesigen Sumpflandkrokilisken zu häuten. Als er nicht zurückkam, bin ich losgezogen, um ihn zu suchen... Sagen wir einfach, es war kein schöner Anblick.$B$BDie Schwester sagt, er könnte durchkommen, aber derweil brauche ich immer noch meine Häute!$B$BUnd wenn Ihr noch ein paar von den Monstern töten wollt, hätte ich nichts dagegen, nach allem, was sie getan haben. Ihr findet die Krokis, wenn Ihr die Küste entlang nach Norden geht, hinter den Hütten der Murlocs.",
    ["O"] = "Sammelt 6 riesige Krokiliskenhäute und bringt sie zu James Halloran in den Hafen von Menethil.",
    ["T"] = "Lehrlingspflichten",
  },
  [472] = {
    ["D"] = "Dun Modr ist an die Dunkeleisenzwerge gefallen!$B$BMeine Wunden sind schwer, $C. Ein Großteil meines Regiments wurde getötet! Diese Rohlinge der Dunkeleisenzwerge haben uns angegriffen, bevor wir uns nach dem Überfall auf den Thandolübergang wieder sammeln konnten.$B$BUnser Anführer, Langzopf, blies zum Rückzug. Als wir die Stadt verließen, wurde ich von einer verirrten Axt in den Rücken getroffen und alles wurde schwarz um mich.$B$BIch wachte hier in Menethil auf, aber ich fürchte um meine Kameraden. Ich hege allerdings noch Hoffnung. Vielleicht ist Langzopf noch am Leben! Seht nach, ob Ihr ihn in der Nähe von Dun Modr finden könnt, $R!",
    ["O"] = "Sucht nahe der Stadt von Dun Modr nach Langzopf.",
    ["T"] = "Der Fall von Dun Modr",
  },
  [473] = {
    ["D"] = "$N. Der Drachenmalklan der Orcs zeigt nicht nur in Algaz Stärke. Hauptmann Starkfaust wünscht mit denen zu sprechen, die Hilfe gegen diese Orcs anbieten wollen. Er ist in der Burg, im Kriegsraum.",
    ["O"] = "Sprecht mit Hauptmann Starkfaust.",
    ["T"] = "Meldung bei Hauptmann Starkfaust",
  },
  [474] = {
    ["D"] = "Jetzt, nachdem die Katapulte abgebrannt wurden, sucht und tötet Häuptling Nek\'rosh.",
    ["O"] = "Tötet Häuptling Nek\'rosh.$B$BBringt Nek\'roshs Kopf zu Hauptmann Starkfaust.",
    ["T"] = "Sieg über Nek\'rosh",
  },
  [475] = {
    ["D"] = "Ein beunruhigender Wind weht durch den Wald.$B$BGaerolas Talvethren dient den schlafenden Druiden der Kralle in den Grabhügeln von Ban\'ethil als Großer Aufseher. Seine Pflicht als auserwählter Beschützer der Schlafenden ist es, deren Sicherheit zu gewährleisten, auf dass sie ihrem Pakt mit Ysera gerecht werden.$B$BAber es gab länger keine Nachricht von Gaerolas, und ich werde etwas nervös. Reist nach Osten, zum Dorf Sternenhauch, und bringt mir einen Bericht von Gaerolas, auf dass ich meine Sorgen begraben kann, da ich weiß, dass meine träumenden Brüder und Schwestern sicher schlummern.",
    ["O"] = "Sucht Gaerolas Talvethren im Dorf Sternenhauch auf.",
    ["T"] = "Ein beunruhigender Wind",
  },
  [476] = {
    ["D"] = "Der Knarzklauenstamm ist wieder verderbt worden!$B$BSie haben mich hinterrücks überfallen, als ich auf dem Weg zum Grabhügel von Ban\'ethil war und liefen weiter, um Sternenhauch zu plündern. Irgendetwas im Wald hat Ursal den Raufer, ihren Häuptling, verderbt und er benutzt die bösen Mächte des Teufelsmooses, um sie wahnsinnig werden zu lassen.$B$BIch bin zu schwer verwundet, um zu Athridas zurückkehren und ihm diese schlimme Nachricht überbringen zu können. $GJunger:Junge:c; $C, diese Aufgabe bleibt Euch überlassen. Wir können nur hoffen, dass die verrückten Knarzklauen noch nicht bis nach Ban\'ethil durchgedrungen sind!",
    ["O"] = "Kehrt zu Athridas Bärenfell in Dolanaar zurück.",
    ["T"] = "Die Verderbnis der Knarzklauen",
  },
  [477] = {
    ["D"] = "Wie Ihr vielleicht bereits wisst, die Stadt Mühlenbern ist weiterhin eine Quelle der menschlichen Opposition, die nötige Unterstützung erhalten sie meist durch die Hexer von Dalaran.$B$BIch weiß nicht, wie ihr Plan aussieht, aber ihr Interesse an einem Hinterwäldlerdorf wie Mühlenbern zeigt mir, dass es von größerer Bedeutung sein muss.$B$BVom Hügelland aus haben sie wagenweise Kisten geliefert. Viele dieser Vorräte werden in ein kleines Camp nördlich von Lohenscheit gebracht. Bringt mir den Inhalt einer dieser Kisten.",
    ["O"] = "Holt den Inhalt einer der Kisten von den Hexern aus Dalaran. Ihr findet Lohenscheit im Süden.",
    ["T"] = "Grenzübergänge",
  },
  [479] = {
    ["D"] = "Dalar versucht, die Quelle für die Zaubereien der Hexer zu finden. In der Zwischenzeit müssen wir ihren Fortschritt mit allen Mitteln aufhalten...$B$BAlle Herbeizauberer, Magier und Beschützer tragen mit Sicherheit ihre Anhänger. Nehmt sie ihnen ab und bringt sie hierher.$B$BFolgt der Hauptstraße in südlicher Richtung und nehmt dann die östliche Abzweigung nach Mühlenbern.",
    ["O"] = "Beschafft 8 Anhänger aus Dalaran für Schattenpriester Allister beim Grabmal.",
    ["T"] = "Nachforschungen in Mühlenbern",
  },
  [480] = {
    ["D"] = "Ein Erzmagier von großer Macht leitet die Reaktivierung des Leyenergieknotens. Angesichts ihres Fortschritts liegt unsere einzige Hoffnung, sie noch aufzuhalten, darin, ihn zu töten. Das ist eine schwierige Tat, denn ohne Zweifel wird er von anderen Magiern und Wärtern beschützt.$B$BIhr müsst ihn schnellstens erledigen. Bringt mir seinen Stab, sobald er tot ist. Geht jetzt.",
    ["O"] = "Tötet den Erzmagier von Dalaran und bringt seinen Stab zu Schattenpriester Allister in dem Grabmal.",
    ["T"] = "Der Wirker",
  },
  [481] = {
    ["D"] = "Bringt den Anhänger zu Dalar Morgenweber, vielleicht kann er Licht in das Dunkel der Hexerpläne bringen.",
    ["O"] = "Bringt den mit Runen gravierten Anhänger zu Dalar Morgenweber in dem Grabmal.",
    ["T"] = "Dalars Analyse",
  },
  [482] = {
    ["D"] = "Natürlich! Wie konnte ich das vergessen?$B$BMühlenbern war einer der Orte, die von den Kirin Tor gekennzeichnet wurden, weil dort ein ruhender Leyenergieknoten liegt. Mit Sicherheit haben die Hexer vor, diesen Knoten wiederzuerwecken und seine Energien für etwas Größeres zu nutzen.$B$BDas dürfen wir nicht zulassen...$B$BUm die Leyenergien des Gebietes zu aktivieren, bedarf es einer großen Macht. Wir können sie aufhalten, indem wir die Anhänger der Hexer an uns nehmen.$B$BBringt diese Information zu Allister, er wird dann über das weitere Vorgehen entscheiden.",
    ["O"] = "Sprecht mit dem Schattenpriester Allister in dem Grabmal.",
    ["T"] = "Dalaran Absichten",
  },
  [483] = {
    ["D"] = "Eindringlinge der Knarzklauen wurden beim Überfall auf die Grabhügel von Ban\'ethil im Westen gesehen.$B$BWenn wir ihnen nicht zu Hilfe kommen, werden die schlummernden Druiden auf ewig im Smaragdgrünen Traum gefangen gehalten, vollkommen ahnungslos ihrem Schicksal ausgeliefert. Das empfindliche Ritual des Großen Schlafes kann ohne die Relikte des Erwachens nicht unterbrochen werden.$B$BReist zum Höhlenbau und bringt den Rabenkrallentalisman, den schwarzen Federkiel, den Saphir des Himmels sowie die Rune des Nistens herbei. Die Druiden bewahren sie in heiligen Truhen auf. Bringt sie zu mir, damit ich das Aufweckungsritual vorbereiten kann.",
    ["O"] = "Bergt die Relikte des Erwachens.",
    ["T"] = "Die Relikte des Erwachens",
  },
  [484] = {
    ["D"] = "Ich bin James Halloran, der Gerber. Meine gegerbten Krokiliskenhäute sind nah und fern begehrt, Lieferungen gehen per Schiff zu allen Häfen der Welt.$B$BHabt Ihr Einar getroffen? Er ist einer der Jäger, die mir Krokiliskenhäute bringen.$B$BMir fehlen zurzeit weichere Häute, wie die von jungen Krokilisken.$B$BDa ich bald keine mehr habe, bin ich bereit, Euch einen Spitzenpreis für einen Stapel der Häute zu bezahlen. Ihr könnt die jungen Krokis in den Marschen gleich außerhalb der Stadt finden.",
    ["O"] = "Besorgt Euch 4 Häute junger Krokilisken für James Halloran im Hafen von Menethil.",
    ["T"] = "Häute junger Krokilisken",
  },
  [485] = {
    ["D"] = "Ihr habt ein merkwürdiges Gerät aus Metall entdeckt, das die Form eines Eis hat. Als Ihr einen der Knöpfe berührt, erwacht das Ei zu Leben und verwandelt sich in eine Art Gnom-Roboter-Apparat! Aus dem Inneren des Roboteis erklingt eine rasselnde Stimme.$B$B\"Mein Name ist Glotz Widrikus und mein Leitroboter ist abgestürzt! Ich werde Euch belohnen, wenn Ihr ihn aufspürt. Bringt dem Roboter bitte diesen Signalgeber!$B$BIch habe jetzt die Koordinaten des Roboters für Euch berechnet. Er ist in der Nähe vom Lauerfels abgestürzt, dort, wo der Fluss entspringt!\"",
    ["O"] = "Bringt das Notsignal zu Glotz\' Leitroboter an der Quelle des Flusses im Hinterland, in der Nähe vom Lauerfels.",
    ["T"] = "Sucht OOX-09/HL!",
  },
  [486] = {
    ["D"] = "Jetzt ist die Zeit gekommen, die Druiden der Kralle zu retten. $N, sollten wir jetzt versagen, sind sie für immer im Schlaf verloren.$B$BIch werde die Relikte des Erwachens vorbereiten und das Ritual durchführen. Damit meine Arbeit Wirkung zeigen kann, muss die verfluchte Bestie, die für diese schreckliche Situation verantwortlich ist, getötet werden. $B$BEs war Ursal der Raufer, der sich in die Berufung unserer Brüder eingemischt hat, und er ist es auch, der nun bezahlen muss, damit sie befreit werden können. Reist auf der Straße nach Nordwesten und macht den Pfad nach Norden ausfindig, der Euch in die Kluft führt. Dort werdet Ihr auf Ursals Höhle treffen.",
    ["O"] = "Tötet Ursal den Raufer.",
    ["T"] = "Ursal der Raufer",
  },
  [487] = {
    ["D"] = "Die Straße nach Darnassus muss sicher bleiben. Reisende, die von Dolanaar nach Darnassus fuhren, haben von ruchlosen Angriffen der verderbten Furbolgs des Knarzklauenstammes berichtet.$B$BJeden Tag gelangen auf diesem Weg wichtige Nachrichten und auch Waren von und nach Darnassus. Es darf einfach nicht sein, dass eine schurkische Bande von Heiden alle terrorisiert.$B$B$C, greift im Namen des Heiligen Waldes zu den Waffen. Ihr Bau liegt am Ende dieses nördlichen Pfades. Erschlagt die Wegelagerer der Knarzklauen und erstattet mir dann Bericht.",
    ["O"] = "Erschlagt 8 Wegelagerer der Knarzklauen.",
    ["T"] = "Die Straße nach Darnassus",
  },
  [488] = {
    ["D"] = "Ihr seid bereit zu arbeiten, wie ich sehe. Ihr habt Glück, denn es vergeht kein Tag, an dem ich mir nicht wünschte, $Gein junger:eine junge:c; $C wäre hier, $Gder:die:c; mir zur Hand geht.$B$BWisst Ihr, $N, ich kann Euch zu Eurem Glück verhelfen und mit Dingen versorgen, von denen Ihr nie zu träumen wagtet. Damit dies jedoch geschieht, müsst Ihr mir gewisse Dinge beschaffen.$B$BMeine Angelegenheiten im Wald erfordern oft gewisse... Reagenzien. Bringt mir Nachtsäblerfangzähne, Strigideulenfedern sowie Proben der Waldweberspinnenseide.$B$BDas soll unser kleines Geheimnis bleiben, $R.",
    ["O"] = "Erhaltet 2 Nachtsäblerfangzähne, 2 Strigideulenfedern und 2 Proben Waldweberspinnenseide.",
    ["T"] = "Zenns Aufträge",
  },
  [489] = {
    ["D"] = "Der Rat des Waldes hat die Meldung erhalten, dass Ihr Zenn Faulhuf behilflich gewesen seid. Der Satyr ist ein Feind des Waldes. Als $R müsstet Ihr eigentlich mehr Verstand haben, als den Wald zu entweihen, indem Ihr Kreaturen der Natur tötet.$B$BIhr müsst Eure Ehre in den Augen des Rates wiederherstellen, wenn Ihr weiterhin $Gein Freund:eine Freundin; von Teldrassil bleiben wollt.$B$BErteilt Faulhuf eine Lektion, dann habt ihr Eure Schuld beglichen. Teufelszapfen sind verderbte Samen, die von Bäumen fallen. Sie sondern grünen Rauch ab.$B$BGebt Faulhuf einige davon. Er wird denken, dass Ihr ihm eine harmlose Leckerei gebracht habt.",
    ["O"] = "Sammelt 3 Teufelszapfen.",
    ["T"] = "Sucht Befreiung!",
  },
  [490] = "_",
  [491] = {
    ["D"] = "Thule erzählte mir einmal, dass er und zwei Kollegen vor langer Zeit einen magischen Zauberstab herstellten, der aus den Lehrlingszauberstäben zusammengewoben wurde, den jeder Magier besaß. Dieser Zauberstab war ein Symbol ihrer Freundschaft, und obwohl Thule ihn hinterher verachtete, konnte er sich doch weder davon trennen noch ihn den anderen überlassen. Also stahl er den Zauberstab und versteckte ihn in Burg Fenris.$B$BHier. Bringt den Zauberstab zu Bethor Eismacht, einem Freund von Thule aus vergangenen Zeiten. Ich bin sicher, er möchte ihn haben.",
    ["O"] = "Bringt den gewebten Zauberstab zu Bethor Eismacht in das Magieviertel von Unterstadt.",
    ["T"] = "Zauberstab an Bethor",
  },
  [492] = {
    ["D"] = "Berichten der Todeswachen zufolge wurde gerade ein weiterer dieser dummen Zwergengebirgsjäger gefangen genommen. Die Todeswächter benutzen gern den Keller der Taverne Zur Galgenschlinge als Übergangszelle, bis man sich \"richtig\" um die Gefangenen kümmern kann.$B$BWarum... kümmert... Ihr Euch nicht um den gefangenen Gebirgsjäger und schaut, wie er das Spezialgetränk verträgt, das ich für ihn gemacht habe? Das ist nur ein kleiner Eindruck dessen, was die Dunkle Fürstin für den Rest von Azeroth geplant hat.",
    ["O"] = "Bringt Johaans Spezialgetränk zum gefangenen Gebirgsjäger.",
    ["T"] = "Eine neue Seuche",
  },
  [493] = {
    ["D"] = "Die Königliche Apothekervereinigung steht unter enormem Druck seitens der Dunklen Fürstin, eine neue Seuche zu entwickeln. Wir arbeiten sehr hart und haben bereits einige Fortschritte gemacht. Wir denken, dass der Erfolg sich umso schneller einstellen wird, wenn wir innerhalb der Vereinigung Informationen austauschen.$B$BAus diesem Grunde möchte ich, dass Ihr meine letzten Funde Apotheker Lydon in Tarrens Mühle überbringt, einem kleinen Städtchen, das sich im Vorgebirge des Hügellands befindet. Die Reise wird sehr lange dauern.$B$BBegebt Euch Richtung Süden und bleibt auf den Straßen. Beachtet die Schilder auf das Genaueste!",
    ["O"] = "Bringt die Funde von Apotheker Renferrel zu Apotheker Lydon nach Tarrens Mühle im Vorgebirge des Hügellands.",
    ["T"] = "Reise in das Vorgebirge des Hügellands",
  },
  [494] = {
    ["D"] = "Schweigt und passt auf, $C.$B$BIch wurde an diesen Ort geschickt, um die Stadt Hügellandhof zu sondieren. Es handelt sich um eine Aufklärungsmission. Dabei ist von äußerster Wichtigkeit, dass Ihr sofort die Hochexekutorin Darthalia in Tarrens Mühle benachrichtigt. Bestellt ihr, ich, Todespirscherin Lesch, lasse ihr folgende Nachricht ausrichten:$B$B\"Der Schrei des Raben locket vom Westen her.\"$B$BFolgt der Straße gen Osten und beachtet die Schilder genau. Nun aber schnell! Macht Euch auf den Weg nach Tarrens Mühle. Es ist Eile geboten!",
    ["O"] = "Reist nach Tarrens Mühle, um die Nachricht von Todespirscherin Lesch an Hochexekutorin Darthalia zu überbringen.",
    ["T"] = "Zeit zum Zuschlagen",
  },
  [495] = {
    ["D"] = "Mug\'Thol und seine Ogerkriegsmeute wurden nach Alterac geschickt, um die Krone des Willens für Fürstin Sylvanas zu holen. Anstatt sie zurückzubringen, hat der Idiot allerdings versucht, sie aufzusetzen... Oger können wohl einfach keinen Dingen widerstehen, die glitzern und schimmern, nehme ich an.$B$BSeine stümperhaften Aktionen sind allerdings zu einem Problem geworden. Die Krone des Willens hat ihm geholfen, der Besessenheit zu widerstehen, die ihm auferlegt wurde, damit er Fürstin Sylvanas dient.$B$BDie Dunkle Fürstin möchte ein Exempel statuieren. Begebt Euch nach Tarrens Mühle im Hügelland und meldet Euch bei Melisara, meinem dortigen Offizier.",
    ["O"] = "Meldet Euch bei Melisara in Tarrens Mühle im Vorgebirge des Hügellands.",
    ["T"] = "Die Krone des Willens",
  },
  [496] = {
    ["D"] = "Ah, und wieder ein ekelhafter Tag in Tarrens Mühle. Diese ganze gesunde Luft verdirbt mir gründlich die Laune, $N.$B$BJe schneller wir die Menschen hier erkranken lassen können, desto besser. Ich habe intensive Studien über mögliche tödliche Zutaten für meine Gebräue durchgeführt, aber ich habe nicht die Zeit, sie alle zu sammeln.$B$BWenn Ihr Euch nützlich machen wollt, dann besorgt mir Folgendes: 10 Graubärzungen sowie das sehr seltene und schwer zu findende Krabblersekret. Bären und Krabbler könnt Ihr gleich außerhalb von Tarrens Mühle finden.",
    ["O"] = "Apotheker Lydon aus Tarrens Mühle möchte 10 Graubärzungen und etwas Krabblersekret.",
    ["T"] = "Elixier des Leidens",
  },
  [497] = "_",
  [498] = {
    ["D"] = "Ich und einige andere Krieger wurden vom Kriegshäuptling geschickt, um ein Objekt aus Durnholde zu holen und dieses so genannte Syndikat zu untersuchen.$B$BAls wir nach Durnholde kamen, um uns umzusehen, wurden wir von einer großen Anzahl der Menschen überrascht. Wir haben mutig gekämpft, aber allein ihre Anzahl war so überwältigend groß, um uns zu besiegen. Sie haben einige von uns als Gefangene genommen, aber ich konnte entkommen.$B$BDurnholde befindet sich nicht weit südöstlich von uns, nur über den Fluss. Drull und Tog\'thar werden dort gefangen gehalten, Ihr müsst sie retten.",
    ["O"] = "Krusk in Tarrens Mühle braucht Euch, um Drull und Tog\'thar aus der Burg Durnholde zu befreien.",
    ["T"] = "Die Rettung",
  },
  [499] = {
    ["D"] = "Es wird einen Moment dauern, diese Komponenten zu vermengen, damit ein, wie ich es nenne, Elixier des Leidens entsteht.$B$BDann mal los! Ah, es gibt doch nichts Schöneres in diesem elendiglichen Vorgebirge, als ein wenig Leiden in die Welt zu bringen.$B$BLasst uns mal überprüfen, ob unser kleiner Trank auch seine gewünschte Wirkung hat.$B$BGebt meiner Assistentin Umpi einen kleinen Schluck von dem, was die Dunkle Fürstin sowohl für Menschen als auch für die Geißel geplant hat.",
    ["O"] = "Gebt Umpi das Elixier des Leidens.",
    ["T"] = "Elixier des Leidens",
  },
  [500] = {
    ["D"] = "Die Trümmergratoger haben einen Ogerhort im Alteracgebirge nahe der verfallenen Stadt Alterac gebaut. Meine Späher berichteten mir, dass sie auch die Ruinen da erobert haben.$B$BWir dürfen aber nicht zulassen, dass sie sich da fest einnisten. Falls sie sich dort zu sicher fühlen, werden sie bestimmt ins Vorgebirge eindringen und wären dann praktisch vor unserer Haustür!$B$BBegebt Euch nach Norden in das Alteracgebirge auf Ogerjagd. Bringt mir die schmutzigen Knöchelknochen, die sie tragen, und Ihr habt Euch ein hübsches Kopfgeld verdient.",
    ["O"] = "Sammelt 9 schmutzige Knöchelknochen von Trümmergratogern im Alteracgebirge. Bringt sie zu Marschall Rotpfad in Süderstade.",
    ["T"] = "Trümmergratkopfgeld",
  },
  [501] = {
    ["D"] = "Oh, $N..., im Hügelland blühen die Blumen und die Luft ist so klar und rein. Ich muss in diesem ganzen Elend baden. Ich sehe aus meinem verdreckten Fenster und sehne den Tag herbei, an dem unsere neue Seuche dieser Welt ihren wohlverdienten Tod bringt.$B$BSo gern ich auch den ganzen Tag hier herumsitzen und mit Grübeln verbringen würde, meine Pflicht gegenüber der Dunklen Fürstin ruft mich. Würdet Ihr mir helfen? Meine Forschungen führen mich zu der Annahme, dass sich aus Berglöwenblut ein vorzüglicher Todestrank herstellen lässt. Bringt mir etwas davon, dann können wir ein bisschen Spaß haben.",
    ["O"] = "Bringt 10 Phiolen mit Berglöwenblut zu Apotheker Lydon in Tarrens Mühle.",
    ["T"] = "Elixier der Schmerzen",
  },
  [502] = {
    ["D"] = "Ah, es geht doch nichts über den Geruch von frischem Blut im Vorgebirge!$B$BDann lasst uns eine Prise davon und ein wenig hiervon vermischen - und der einzig wahre Spaß kann beginnen! Die Kreation aus diesem Blut möchte ich Elixier der Schmerzen nennen. Wenn meine Berechnungen korrekt sind, sollte dieses Gebräu der Anfang von etwas Wunderbarem für Fürstin Sylvanas sein. Aber wir müssen erstmal einen Testlauf machen.$B$BBringt dieses Elixier zum nördlichen Hof in den Feldern des Hügellands im Südwesten. Lasst uns sehen, wie Stanley, dem kleinen Hund von Bauer Ray, dieses \'Leckerli\' so schmeckt.",
    ["O"] = "Füttert Stanley mit dem Elixier der Schmerzen.",
    ["T"] = "Elixier der Schmerzen",
  },
  [504] = {
    ["D"] = "Nun, da Ihr die Bekanntschaft der Trümmergratoger gemacht habt, möchte ich, dass Ihr ihnen so richtig zeigt, was eine Harke ist...$B$BGeht zu den Ruinen von Alterac und sucht die Kriegstreiber der Trümmergratoger. Ihr sollt eine große Anzahl von ihnen erschlagen - das ist der einzige Weg, um diese Schläger von dem Gebiet der Allianz fern zu halten.",
    ["O"] = "Erschlagt 10 Kriegstreiber der Trümmergratoger und kehrt dann zu Marschall Rotpfad in Süderstade zurück.",
    ["T"] = "Kriegstreiber der Trümmergratoger",
  },
  [505] = {
    ["D"] = "Ich bin der neue Magistrat von Süderstade. Ich wurde erst kürzlich ernannt, nachdem der vorherige Magistrat ermordet worden ist. Die Mörder wurden nie gefunden, aber unsere Nachforschungen haben ergeben, dass sie höchstwahrscheinlich vom Syndikat angeworben wurden - einer Gruppe von Dieben, angeführt von schurkischen Edlen des jetzt gefallenen Königsreichs von Alterac.$B$BDas Syndikat hat Lager bei Soferas Landspitze und Corrahns Dolch, nördlich und westlich des von der Horde besetzten Tarrens Mühle. Erschlagt die Syndikatsmitglieder, die Ihr dort vorfindet.",
    ["O"] = "Tötet 12 Schleicher des Syndikats und 8 Diebe des Syndikats, kehrt dann zurück zu Magistrat Henry Maleb nach Süderstade.",
    ["T"] = "Auftragsmörder des Syndikats",
  },
  [506] = {
    ["D"] = "Vor ein oder zwei Tagen erhielt ich Besuch vom Anführer dieses Syndikats. Sein Name war Aliden Perenolde und er nannte sich selbst Lord dieses Landes. Er vertraute mir seine Pläne an, die Orcs, die sich gegen ihn gewandt hatten, zu Sklaven zu machen, genau wie es einst auch sein Vater und Mentor, der geächtete Schwarzmoor, geplant hatte.$B$BAber viel wichtiger war, dass er seine Geliebte mitbrachte, ein zartes Mädchen namens Elysa. Um ihren Hals trug sie den Anhänger, den der Kriegshäuptling als sein Eigen wünscht.$B$BGeht jetzt, ich gehe meine eigenen Wege, nachdem ich meine Geschäfte hier erledigt habe.",
    ["O"] = "Bringt die von Gol\'dir gesammelten Informationen zu Krusk in Tarrens Mühle.",
    ["T"] = "Schwarzmoors Vermächtnis",
  },
  [507] = {
    ["D"] = "Es ist unumstritten, dass Perenolde getötet werden und seine Verderbtheit ausgelöscht werden muss. Sein Herrenhaus liegt weit nördlich von hier, an den Ufern des Lordameresees. $N, tut Eure Pflicht, holt danach den Anhänger, den der Kriegshäuptling zu haben wünscht, und kehrt dann hierher zurück.",
    ["O"] = "Tötet Lord Aliden Perenolde und befragt seine Geliebte Elysa nach Tarethas Anhänger.",
    ["T"] = "Lord Aliden Perenolde",
  },
  [508] = {
    ["D"] = "Den Anhänger? Aber natürlich könnt Ihr ihn haben... Nehmt ihn! Nichts soll mich an ihn erinnern... nur bitte geht, bitte geht in Frieden...",
    ["O"] = "Bringt Tarethas Anhänger zu Krusk nach Tarrens Mühle im Vorgebirge des Hügellands.",
    ["T"] = "Tarethas Geschenk",
  },
  [509] = {
    ["D"] = "Ein neuer Tag, ein neues Elixier. Wenn ich was zu sagen hätte, würde ich diese lächerlichen Menschen und die Geißel die Sache einfach unter sich ausmachen lassen. Sie scheinen ohnehin absolut darauf versessen zu sein, sich gegenseitig umzubringen.$B$BAber leider hat es sich unsere Dunkle Fürstin in den Kopf gesetzt, das Ganze ein wenig zu beschleunigen. Also warum diese düsteren Tage nicht dazu nutzen, um hier ein wenig Tod und Krankheit zu verbreiten?$B$B$N, das ist mein Plan: Ihr macht Euch auf den Weg zur Nethandersiedlung. Das ist südlich von Tarrens Mühle und östlich des Flusses und Süderstade. Sammelt in den Feldern dort 6 Morastschnauzenkappen für mein nächstes Elixier.",
    ["O"] = "Bringt 6 Morastschnauzenkappen zu Apotheker Lydon in Tarrens Mühle.",
    ["T"] = "Elixier der Pein",
  },
  [510] = {
    ["D"] = "Unter diesen Dokumenten befindet sich eine Liste der benötigten Vorräte. Das sind unter anderem ganz banale Dinge (Nahrung, Waffen, Kleidung) wie auch etwas exotischere Ausrüstungsgegenstände (Masken, Öl, Gift).$B$BAber am auffälligsten ist die Dringlichkeit dieses Auftrags - diese Vorräte werden schnellstens benötigt, um eine Unternehmung zu starten, die in dem Dokument als \'wichtige Mission gegen Süderstade\' beschrieben wird.",
    ["O"] = "Bringt die furchteinflößenden Pläne zu Magistrat Henry Maleb nach Süderstade.",
    ["T"] = "Furchteinflößende Pläne",
  },
  [511] = {
    ["D"] = "Einer der Briefe auf diesem Tisch ist verschlüsselt und nicht zu ergründen.",
    ["O"] = "Bringt den verschlüsselten Brief zum Meister der Lehren Dibbs nach Süderstade.",
    ["T"] = "Verschlüsselter Brief",
  },
  [512] = {
    ["D"] = "Ich fürchte, die Führung, die hinter dem Syndikat steht, will Süderstade unbedingt ruinieren. Wir müssen diese Anführer mit aller Kraft schlagen, bevor sie ihre Pläne in die Tat umsetzen können.$B$BGeht zu den Ruinen von Strahnbrad, im Alteracgebirge im Norden. Wir glauben, dass das Syndikat dieses Gebiet als Hauptquartier benutzt.$B$BWir wissen, dass diese Bande von Halsabschneidern von ehemaligen Adligen von Alterac angeführt wird. Sie mögen sich als normale Agenten des Syndikats verkleiden, aber wenn Ihr sie tötet, werdet Ihr einen Edlen an dem Siegelring erkennen, den er trägt.$B$BBringt mir diese Siegelringe.",
    ["O"] = "Bringt 7 Alteracsiegelringe zu Magistrat Henry Maleb nach Süderstade.",
    ["T"] = "Tod der Edlen",
  },
  [513] = {
    ["D"] = "Gerade dachte ich noch, ein weiterer Tag ohne jegliches Amüsement, und da kommt Ihr und bringt diese wunderbaren Morastschnauzenkappen.$B$BZuerst werde ich sie zu einem feinen Gemisch zusammenrühren. Dann werden wir Apothekermeister Faranell in Unterstadt benötigen, damit er seine Paste des Verfalls hinzufügt, und dann geht der Spaß erst richtig los!$B$BHier, $N, nehmt dieses Morastschnauzengemisch und bringt es schnell zu Meister Faranell.",
    ["O"] = "Bringt das Morastschnauzengemisch zu Apothekermeister Faranell in Unterstadt.",
    ["T"] = "Elixier der Pein",
  },
  [514] = {
    ["D"] = "Ich habe einen Kollegen in Eisenschmiede, der bei der Entschlüsselung dieses Briefes behilflich sein kann. Begebt Euch zur Bibliothek in der Halle der Forscher in Eisenschmiede und sprecht mit Ausgrabungsleiter Sturmlanze. Richtet ihm aus, dass ich Euch geschickt habe, und zeigt ihm diesen Brief. Wenn es jemanden nördlich der Schwarzfelsspitze gibt, der das hier lesen kann, dann ist er das.",
    ["O"] = "Bringt den geschickt verschlüsselten Brief zu Ausgrabungsleiter Sturmlanze nach Eisenschmiede.",
    ["T"] = "Brief an Sturmlanze",
  },
  [515] = {
    ["D"] = "Ein Morastschnauzengemisch? Das ist wirklich brillant! Warum ist mir das nicht eingefallen?$B$BLasst mich meine Paste des Verfalls diesem Teufelsgebräu hinzufügen.$B$BDamit die verunreinigenden Agenzien in dieser Morastschnauzenmixtur aktiviert werden, benötigt Lydon starken Trollbluttrank sowie einige Säbelflossenschuppen und Fetzenflossenaugen von der Südküste des Hügellands. Er wird wissen, was zu tun ist, sobald Ihr alle Reagenzien zusammengetragen habt. Ich kann es kaum abwarten zu erfahren, wie sein Experiment ablaufen wird!",
    ["O"] = "Bringt die Morastschnauzenmixtur, einen starken Trollbluttrank, 5 Säbelflossenschuppen und 5 Fetzenflossenaugen zu Apotheker Lydon nach Tarrens Mühle.",
    ["T"] = "Elixier der Pein",
  },
  [516] = {
    ["D"] = "Ich habe Berichte erhalten, dass eine Gruppe Untoter sich zusammenrottet, um einen Angriff gegen uns vorzubereiten. Mit diesen Informationen können wir den Spieß umdrehen und sie zuerst angreifen, damit wäre ihr Plan erledigt.$B$BLeider sind meine Informationen gelinde gesagt unvollständig. Laut Bericht verstecken sie sich an einem Ort, der als Berens List bekannt ist. Ihr genauer Standort ist unbekannt, aber es scheint eine Höhle in den Hügeln zu sein, nahe einem Nest der Hexer von Dalaran.$B$BIch vertraue auf Euren Einfallsreichtum. Findet sie und macht alles dem Erdboden gleich.",
    ["O"] = "Findet zuerst Berens List, tötet 6 Zwangsarbeiter von Rabenklaue sowie 6 Wächter von Rabenklaue und kehrt dann zu Schattenpriester Allister in das Grabmal zurück.",
    ["T"] = "Berens List",
  },
  [517] = {
    ["D"] = "Das neue Elixier ist endlich bereit für einen Test. Aber warum solch eine vollkommene, höchst ansteckende Seuche an einen einfachen Frosch oder Hund verschwenden? Wir sollten uns in Fürstin Sylvanas\' Namen eine kleine Freude erlauben.$B$BGeht nach Dun Garok. Ihr kennt doch die Kasernen, in denen diese widerlichen kleinen Zwerge umhersausen. Ihr stehlt ein Fässchen Kniegräberstarkbier für mich. Sobald ich dieses Fässchen habe, können wir beide, Ihr und ich, eine kleine Party feiern, $N.",
    ["O"] = "Bringt ein Fässchen mit Kniegräberstarkbier zu Apotheker Lydon nach Tarrens Mühle.",
    ["T"] = "Elixier der Pein",
  },
  [518] = {
    ["D"] = "Mug\'Thol und seine Trümmergratoger haben die Ruinen von Alterac überrannt. Ihm eine schwere Strafe zukommen zu lassen, ohne die Anzahl der Oger zu verringern, wird nicht einfach sein.$B$BWir machen sie erst weich und sehen dann weiter.$B$BVerstanden? Gut. Dann geht.",
    ["O"] = "Tötet 10 Raufer der Trümmergratoger für Melisara in Tarrens Mühle.",
    ["T"] = "Die Krone des Willens",
  },
  [519] = {
    ["D"] = "Targ, Muckrake und Glommus sind Mug\'Thols Offiziere. Sie sind ihm loyal ergeben, aber wer weiß schon, was einen Oger loyal sein lässt.$B$BBringt mir ihre Köpfe, $N, und dann muss nur noch Mug\'Thols Kopf, mitsamt Krone, rollen.",
    ["O"] = "Tötet Targ, Muckrake und Glommus und bringt ihre Köpfe zu Melisara nach Tarrens Mühle.",
    ["T"] = "Die Krone des Willens",
  },
  [520] = {
    ["D"] = "Nun, da Mug\'Thols Offiziere tot sind, muss nur noch Mug\'Thol selbst getötet und die Krone des Willens wieder zurückgebracht werden. Ich bin mir ziemlich sicher, dass er sie nur äußerst unwillig wieder hergeben wird. Außerdem wird er Euch wahrscheinlich bereits erwarten, in Anbetracht des... Ablebens... seiner Kumpanen.$B$BFindet Mug\'Thol und bringt mir seinen Kopf und die Krone. Der Widerstand der Trümmergratoger wird damit beendet sein.",
    ["O"] = "Tötet Mug\'Thol und bringt seinen Kopf mit der Krone des Willens zu Melisara nach Tarrens Mühle.",
    ["T"] = "Die Krone des Willens",
  },
  [521] = {
    ["D"] = "Es bleibt nur noch, die Krone des Willens zu Sharlindra nach Unterstadt zu bringen. Sie wird dafür sorgen, dass sie an Fürstin Sylvanas weitergeleitet wird. Ich warne Euch, auch nur daran zu denken, sie für Eure eigenen Zwecke zu verwenden, $N, Mug\'Thols Schicksal sollte Beispiel genug sein.",
    ["O"] = "Bringt die Krone des Willens zu Sharlindra in Unterstadt.",
    ["T"] = "Die Krone des Willens",
  },
  [522] = {
    ["D"] = "Dies ist ein Vertrag für die Ermordung des Magistrats Henry Maleb. Er wurde mit einem unverwechselbaren Siegelring in rotem Wachs gestempelt und versiegelt.",
    ["O"] = "Bringt den Auftragsmördervertrag zu Magistrat Maleb nach Süderstade.",
    ["T"] = "Auftragsmördervertrag",
  },
  [523] = {
    ["D"] = "Der Vertrag, den Ihr gebracht habt, wurde von Baron Vardus unterschrieben, einem Edlen aus dem ehemaligen Königreich von Alterac, und jemand der bekannt ist für seine Kaltherzigkeit und brutale Vorgehensweisen. Es überrascht mich nicht, dass jemand wie der Baron dahinter steckt.$B$BFindet diesen Mann. Findet ihn und führt ihn seiner gerechten Strafe zu. Er befindet sich sicherlich irgendwo in den Schlupfwinkeln des Syndikats - vielleicht in den Ruinen von Strahnbrad oder irgendwo in dem Tal im Norden davon.",
    ["O"] = "Bringt den Kopf von Baron Vardus zu Magistrat Maleb nach Süderstade.",
    ["T"] = "Ableben des Barons",
  },
  [524] = {
    ["D"] = "Nebenan halten die Todeswachen einige Bauern als Geiseln gefangen. Hochexekutorin Darthalia hatte vermutet, dass sich einer der Militärstrategen aus Sturmwind unter ihnen versteckte. Anscheinend wurde dieser Militärstratege gestern Nacht tot in den Pestländern gefunden. Darthalia denkt ja immer so nett an mich und hat entschieden, dass die Gefangenen mir zu überlassen sind.$B$BIch denke, es ist höchste Zeit, dass wir für diese Bauern eine kleine Party schmeißen. Hört sich doch nach einer tollen Zeit an!$B$BStellt dieses besudelte Fässchen auf den Teppich in ihrem Zimmer. Seuchenbräu für alle!",
    ["O"] = "Stellt das besudelte Fässchen auf den Teppich für die gefangenen Bauern.",
    ["T"] = "Elixier der Pein",
  },
  [525] = {
    ["D"] = "Ich habe den Brief dechiffriert, aber ich weiß nicht, was ich davon halten soll. Ihr habt ihn unter Dieben im Vorgebirge von Alterac gefunden? Sehr seltsam...$B$BIhr solltet ihn zum Magistrat nach Süderstade bringen. Auch wenn dieser Brief geheimnisvoll klingt, seine Stadt liegt Alterac am nächsten, wenn eine Bedrohung sich zusammenbraut, dann sollte gerade er davon erfahren.",
    ["O"] = "Bringt den entschlüsselten Brief zu Magistrat Maleb nach Süderstade.",
    ["T"] = "Weitere Geheimnisse",
  },
  [526] = {
    ["D"] = "Ihr mögt zwar einen Lichtschmiedeblock in dieser Truhe gefunden haben, aber das reicht nicht aus, um irgendetwas von Nutzen daraus zu schmieden.$B$BIhr solltet zurückgehen und nach weiteren suchen. Vielleicht tragen die Murlocs in der Nähe des Schiffswracks Lichtschmiedeblöcke mit sich herum.",
    ["O"] = "Sammelt 5 Lichtschmiedeblöcke und kehrt dann zu Glorin Stahlbraue im Sumpfland zurück.",
    ["T"] = "Lichtschmiedeblöcke",
  },
  [527] = {
    ["D"] = "Steht gefälligst stramm, wenn Ihr mit mir sprecht, $C.$B$BIch stehe unter direktem Kommando von Sylvanas. Wir sollen der menschlichen Plage Einhalt gebieten, bis unsere Apotheker eine neue Pest entwickeln können.$B$BUnseren Informationen zufolge bietet die Stadt Hügellandhof einen guten Angriffspunkt. Eure erste Aufgabe ist darauf gerichtet, die Nahrungsversorgung und Infrastruktur zu unterbrechen.$B$BBegebt Euch nach Westen. Überfallt die nördlichsten Höfe auf den Feldern des Hügellands. Und stellt sicher, dass die Landeigner, Bauer Ray und Bauer Getz, ebenfalls exekutiert werden.",
    ["O"] = "Tötet 6 Landarbeiter des Hügellands 6 Bauern des Hügellands, Bauer Ray und Bauer Getz und meldet Euch dann wieder bei Darthalia in Tarrens Mühle.",
    ["T"] = "Kampf um das Hügelland",
  },
  [528] = {
    ["D"] = "Die Berichte vom Angriff auf die nördlichen Höfe haben die Stadtbevölkerung in Panik versetzt. Vorzüglich.$B$BUnsere Todespirscher melden, dass Arbeiter in die Felder ins südliche Hügelland flüchten. Diese Narren glauben doch tatsächlich, dass sie sich in den Obsthainen verstecken und so ihrer Verdammnis entkommen können.$B$BBeweist ihnen das Gegenteil, $N. Kehrt zu den Feldern des Hügellands zurück und macht die feigen Arbeiter ausfindig. Vernichtet sie im Namen der Dunklen Fürstin.$B$BWeggetreten!",
    ["O"] = "Tötet 15 Arbeiter des Hügellands und erstattet Bericht bei Darthalia in Tarrens Mühle.",
    ["T"] = "Kampf um das Hügelland",
  },
  [529] = {
    ["D"] = "Die Menschen in Hügellandhof leisten unerwarteten Widerstand. Das Problem scheint darin zu bestehen, dass die Bewohner durch den örtlichen Schmied mit Waffen versorgt werden. Wir müssen alle Versorgungsmöglichkeiten abschneiden.$B$BEure Aufgabe ist es, Schmied Verringtan zu eliminieren. Tötet auch gleich seine Lehrlinge. Wenn Ihr schon dort seid, bringt auch gleich eine Eisenlieferung aus ihrem Vorratslager mit zurück. Nach der Eroberung der Stadt Hügellandhof werden diese Vorräte uns gelegen kommen, wenn wir mit der Verwüstung von Süderstade beginnen.",
    ["O"] = "Tötet Schmied Verringtan und 4 Schmiedelehrlinge des Hügellands.$B$BBesorgt eine Eisenlieferung und erstattet Bericht bei Darthalia in Tarrens Mühle.",
    ["T"] = "Kampf um das Hügelland",
  },
  [530] = {
    ["D"] = "Valdred, einst ein guter Freund von mir, ist der Mörder meiner Frau. Auch wenn ich keine Trauer empfinden kann, so sehne ich mich doch nach Rache. Wenn Ihr Valdred tötet und mir seine verfluchten Hände bringt, werde ich Euch belohnen.$B$BDas Letzte, was ich hörte, war, dass er sich im südlichen Silberwald beim Graumähnenwall aufhält und in das Königreich von Gilneas flüchten will.",
    ["O"] = "Tötet Valdred Moray.$B$BBringt seine Hände zu Raleigh Andrean nach Unterstadt.",
    ["T"] = "Rache eines Ehemanns",
  },
  [531] = {
    ["D"] = "Licht! Bringt solche Dinge nicht in die Jagdhütte... das ist unhygienisch, unaussprechlich, un... un... unhygienisch!$B$BBringt es bitte einfach nur raus hier, ja? Niemand will Eure kleine blutige Trophäe sehen, aber wirklich nicht!",
    ["O"] = "Bringt den Kopf der alten Rußtatze zu Vyrin Flinkwind in die Jagdhütte der Weltenwanderer.",
    ["T"] = "Vyrins Rache",
  },
  [532] = {
    ["D"] = "Hügellandhof ist ein einziges Chaos und wir sind der Meinung, dass die Stadt bald fallen wird. Nichtsdestotrotz müssen wir weiterhin an unserer Ruchlosigkeit festhalten bis der menschliche Abschaum ein für alle Mal von diesem Vorgebirge verschwunden ist.$B$BTrotz ihrer überwältigenden Verluste sammeln die Menschen sich weiterhin um ihre Führungsriege und kämpfen weiter. Wir müssen ihren Anführer, Magistrat Uferbach eliminieren und mit ihm den ganzen Stadtrat. Zerstört ihr heißgeliebtes politisches Dokument, die Proklamation des Hügellands, gleich mit. Und wenn Ihr schon dabei seid, stehlt das Stadtverzeichnis.",
    ["O"] = "Tötet Magistrat Uferbach und 4 Ratsherren des Hügellands. Zerstört die Proklamation des Hügellands. Stehlt das Stadtverzeichnis des Hügellands. Meldet Euch danach bei Darthalia in Tarrens Mühle.",
    ["T"] = "Kampf um das Hügelland",
  },
  [533] = {
    ["D"] = "Endlich haben wir eine Spur und können nach Hinweisen auf Gol\'dirs Aufenthaltsort suchen. Die Menschen haben ihn immer wieder umquartiert, aber wir konnten unsere Suche einengen und konzentrieren uns jetzt auf das große Syndikatlager nördlich von hier.$B$BBegebt Euch dorthin, $N, und beschafft alle Informationen, an die Ihr herankommen könnt.",
    ["O"] = "Holt Informationen über Gol\'dirs Aufenthaltsort für Krusk in Tarrens Mühle ein.",
    ["T"] = "Unterwanderung",
  },
  [534] = "_",
  [535] = {
    ["D"] = "Arrrgh... Verletzt, sterbend und den Feind direkt vor Augen...$B$BDa Ihr mich ja sowieso töten werdet, könntet Ihr mir da vielleicht zuvor ein Süderstader Starkbier bringen - Euresgleichen trinkt doch auch, oder? - und ich werde Euch sagen, was Ihr wissen wollt.",
    ["O"] = "Bringt einen Becher mit Süderstader Starkbier zu Valik in das Lager des Syndikats.",
    ["T"] = "Valik",
  },
  [536] = {
    ["D"] = "Von der Stadtwache von Sturmwind nach Süderstade versetzt, eine Stadt, die man gleich wieder verlassen sollte... wie\'s aussieht. Der Gestank vom Hafen Tag und Nacht.$B$BGanz zu schweigen vom alten Rotpfad... Will, dass ich mich um die Murlocs kümmere. Ha! $B$BKann mich kaum aufraffen, die Unterschlüpfe dieser übergroßen Fische unten am Weststrand aufzusuchen. Wenn Ihr für mich gehen und Euch um sie kümmern würdet, wäre ich Euch sehr dankbar.",
    ["O"] = "Tötet 10 Gezeitenjäger der Fetzenflossen und 10 Orakel der Fetzenflossen für Leutnant Farren Orinelle in Süderstade.",
    ["T"] = "Die Küste entlang",
  },
  [537] = {
    ["D"] = "Der Brief, den Ihr mir gebracht habt, wurde von einem Wesen geschrieben, das sich selbst Nagaz nennt, ein Mitglied der Arguswacht. Ich weiß nicht, was diese Arguswacht ist, aber wenn sie mit dem Syndikat zusammenarbeiten, dann sind sie eine Bedrohung.$B$BIch möchte, dass Ihr Nagaz und die Schattenmagier der Arguswacht, die in diesem Brief erwähnt werden, findet. Ihr sollt sie finden und dann töten.$B$BSucht im Oberland, wo das Syndikat am stärksten ist. Es liegt nördlich von Strahnbrad.$B$BMit etwas Glück könnt Ihr die Abgesandten der Arguswacht dort finden.",
    ["O"] = "Tötet 4 Schattenmagier der Arguswacht.$B$BBringt den Kopf von Nagaz zu Magistrat Maleb nach Süderstade.",
    ["T"] = "Der Dunkle Rat",
  },
  [538] = {
    ["D"] = "$N. Wenn Ihr einer langen Reise nicht abgeneigt seid, dann habe ich eine neue Aufgabe für Euch, Sturmwind erneut bei der Bestückung seiner Bibliothek behilflich zu sein.$B$BDas alte Königreich von Alterac ist nach dem großen Krieg gefallen und es besaß eine umfangreiche Büchersammlung. Es ist eine große Tragödie, dass die Stadt verfallen ist, aber es ist weitaus tragischer, dass all ihr Wissen verloren geht! Wir müssen retten, was zu retten ist.$B$BSüderstade liegt den Ruinen von Alterac am nächsten. Begebt Euch dorthin und sprecht mit dem Meister der Lehren Dibbs. Er kann Euch den Weg zu den Ruinen erklären und welche Bücher dort zu finden sind.",
    ["O"] = "Sprecht mit dem Meister der Lehren Dibbs in Süderstade.",
    ["T"] = "Süderstade",
  },
  [539] = {
    ["D"] = "Tötet Großknecht Bonds sowie 10 Minenarbeiter des Hügellands und erstattet Bericht bei Darthalia in Tarrens Mühle.",
    ["O"] = "Tötet Großknecht Bonds sowie 10 Minenarbeiter des Hügellands und erstattet Bericht bei Darthalia in Tarrens Mühle.",
    ["T"] = "Kampf um das Hügelland",
  },
  [540] = {
    ["D"] = "Diese barbarischen Oger haben sich in den Ruinen von Alterac ausgebreitet und es schüttelt mich bei dem Gedanken, was sie mit den wertvollen Büchern dort anstellen könnten. Ihr müsst retten, was immer Ihr könnt!$B$BGeht zu den Ruinen von Alterac und sucht dort nach Folianten, die die Oger gestohlen haben. Nehmt, was immer Ihr finden könnt, und bringt alles zu mir, aber sucht insbesondere ein ganz bestimmtes Buch: Der Arm von Gri\'lek. Es enthält alte Trolllehren, die ich erlernen muss. Die Ruinen von Alterac waren der letzte bekannte Ort, an dem es ein intaktes Exemplar dieses Buches geben soll.",
    ["O"] = "Bringt 5 geborgene Folianten und das abgenutzte Lederbuch, das \'Der Arm von Gri\'lek\' enthält, zum Meister der Lehren Dibbs nach Süderstade.",
    ["T"] = "Wissen bewahren",
  },
  [541] = {
    ["D"] = "Zwerge helfen Menschen? Diese verdammten, dreckigen kleinen Bestien.$B$BWir werden die Stärke dieser so genannten Allianz testen. Wir werden ja sehen, wie sehr diese kleinen Wadenbeißer den Menschen noch helfen wollen, nachdem sie unseren Zorn zu spüren bekommen haben. Wir wissen ganz genau, wo sie in diesem Gebiet ihre Hochburg haben.$B$BSie haben eine Kaserne namens Dun Garok im südöstlichen Hügelland entlang des Thoradin-Walls errichtet. Die letzte Order in diesem Kampf ist die Belagerung von Dun Garok, $C. Hauptmann Eisenberg, ihr Anführer muss sterben.",
    ["O"] = "Begebt Euch nach Dun Garok, tötet 8 Gebirgsjäger, 4 Scharfschützen, 2 Priester sowie Hauptmann Eisenberg und erstattet danach Bericht bei Darthalia in Tarrens Mühle.",
    ["T"] = "Kampf um das Hügelland",
  },
  [542] = {
    ["D"] = "Ich werde einige Zeit benötigen, um das Buch Der Arm von Gri\'lek zu studieren, das Ihr mir gebracht habt. Ich bin sicher, Milton kann es kaum erwarten, die anderen geborgenen Bücher in die Sammlung von Sturmwind zu integrieren.$B$BHier sind die Bücher, die Ihr aus den Ruinen von Alterac mitgebracht habt, verpackt und katalogisiert. Bringt sie zu Milton Garbenbund in Sturmwind und ich versichere Euch, dass er sehr dankbar sein wird.",
    ["O"] = "Bringt die Folianten von Alterac zu Milton Garbenbund nach Sturmwind.",
    ["T"] = "Rückkehr zu Milton",
  },
  [543] = {
    ["D"] = "Die Tiara von Perenolde ist ein altes Erbstück der \"Edlen\" von Perenolde - der Herrscherfamilie von Alterac, bevor das Königreich verfiel. Es heißt, dass sie von Meisterjuwelieren in Eisenschmiede hergestellt und mit den schönsten Smaragden besetzt wurde.$B$BAls Alterac zerstört wurde, ging die Tiara verloren, aber Gerüchten zufolge soll ein Ogermagier, Grel\'borg der Horter, die Tiara gefunden haben und jetzt in den Ruinen von Alterac auf der Suche nach weiteren Schätzen umherwandern.$B$BWir möchten, dass Ihr die Tiara für uns beschafft.",
    ["O"] = "Erlangt die Tiara von Perenolde zurück.",
    ["T"] = "Die Tiara von Perenolde",
  },
  [544] = {
    ["D"] = "Eigentlich kam ich zu Forschungszwecken nach Tarrens Mühle, aber jetzt muss ich eine Krise verhindern. Wisst Ihr, vier Verlassene sind vor einigen Monaten aus Unterstadt geflohen. Sie haben ihren Brüdern den Rücken zugekehrt, aber was viel schlimmer ist... sie haben die Dunkle Fürstin bestohlen.$B$BDiese Diebe sind in ein gesichertes Gewölbe eingebrochen und haben vier Artefakte gestohlen; Gegenstände, die unsere Apotheker für bestimmte Studien benötigen. Sylvanas will, dass diese Artefakte zurückgebracht werden.$B$BDie Diebe sind aus Unterstadt nach Dalaran geflohen und diese Hexer haben sie in Quarantäne in das Lordamere-Internierungslager gesteckt.",
    ["O"] = "Findet diese Verräter, sammelt ihre Artefakte ein und kehrt dann zu Magus Leermacht nach Tarrens Mühle zurück.",
    ["T"] = "Gefängniseinbruch",
  },
  [545] = {
    ["D"] = "Tötet 6 Beschwörer von Dalaran sowie 12 Elementarsklaven und kehrt dann zu Magus Leermacht nach Tarrens Mühle zurück.",
    ["O"] = "Tötet 6 Beschwörer von Dalaran sowie 12 Elementarsklaven und kehrt dann zu Magus Leermacht nach Tarrens Mühle zurück.",
    ["T"] = "Patrouillen von Dalaran",
  },
  [546] = {
    ["D"] = "Ich habe gehört, Ihr wurdet von Darthalia eingezogen, um Krieg gegen die Menschen des Hügellands zu führen. Ich bin ganz grün vor Neid...$B$BIhr habt den ganzen Spaß... Menschen erschlagen, die Stadt plündern, Unschuldige terrorisieren..., und ich stecke hier fest und darf in Tarrens Mühle Wache schieben.$B$BVielleicht habt Ihr ja etwas Mitleid mit einer alten Todeswache wie mir? Wisst Ihr, ich sammle nämlich menschliche Schädel. Und Ihr werdet eine recht lange Zeit gegen Menschen kämpfen. Bringt mir im Verlauf Eures langen Kampfes 30 Schädel, und ich werde Euch für Eure Mühe entlohnen.",
    ["O"] = "Todeswache Samsa aus Tarrens Mühle will 30 menschliche Schädel aus dem Hügelland.",
    ["T"] = "Andenken des Todes",
  },
  [547] = {
    ["D"] = "Hochexekutorin Darthalia hat mich hergeschickt, um Dun Garok aus der Ferne zu beobachten. Ich hatte mein Lager gerade außer Sichtweite aufgeschlagen. Am Morgen, als ich Wasser kochen wollte, ging ich Feuerholz sammeln.$B$BAls ich ins Lager zurückkam, entdeckte ich, dass meine Ausrüstung verschwunden war.$B$BAnscheinend waren ein paar Zwergenspäher von Dun Garok vorbeigekommen und hatten alles mitgenommen. Ich brauche mein Schwert! Wenn Ihr es für mich zurückholen könntet, wäre ich Euch sehr dankbar. So viele Zwerge hier... es wird ganz schön schwierig sein, den kleinen dreckigen Dieb zu finden, der es gestohlen hat.",
    ["O"] = "Bringt das Schwert von Todeswache Humbert aus Dun Garok zu ihm nach Tarrens Mühle zurück.",
    ["T"] = "Humberts Schwert",
  },
  [548] = "_",
  [549] = {
    ["D"] = "In Fürstin Sylvanas\' Namen sollen alle Mitglieder der Organisation, die sich das Syndikat nennt, getötet werden. Diejenigen, die bereits gefangen genommen wurden, werden gemeinschaftlich hingerichtet. Wir wissen, dass sie sich im Moment in den Ruinen der Burg Durnholde im Südosten von Tarrens Mühle sammeln.$B$BFür alle diejenigen, die Beweise ihrer Taten zu Hochexekutorin Darthalia bringen, ist eine reiche Belohnung ausgesetzt.",
    ["O"] = "Tötet 10 Schurken des Syndikats und 10 Wachmänner des Syndikats. Kehrt danach zu Hochexekutorin Darthalia nach Tarrens Mühle zurück.",
    ["T"] = "GESUCHT: Syndikatspersonal",
  },
  [550] = {
    ["D"] = "Bringt Darthalias versiegelte Belobigung zu Bragor Blutfaust nach Unterstadt.",
    ["O"] = "Bringt Darthalias versiegelte Belobigung zu Bragor Blutfaust nach Unterstadt.",
    ["T"] = "Kampf um das Hügelland",
  },
  [551] = {
    ["D"] = "Dieses Pergament ist mit einer Verzauberung versehen, die es unlesbar macht. Nur ein Wort oben am Blattanfang ist lesbar:$B$B Nagaz",
    ["O"] = "Bringt den verschlüsselten Brief zum Meister der Lehren Dibbs nach Süderstade im Vorgebirge des Hügellands.",
    ["T"] = "Das Verhexte Pergament",
  },
  [552] = {
    ["D"] = "Meister Helcular hatte seine Transformation in einen Lich fast vollständig abgeschlossen, als ein menschlicher Lynchmob seinen rituellen Unterschlupf in den Hügeln entdeckte und ihn brutal ermordete. Was die Angelegenheit noch schlimmer macht, ist, dass sie seinen Leichnam auf dem Friedhof von Süderstade begraben haben.$B$BDiese Missachtung eines großen Hexers ist einfach nicht akzeptabel. Als sein Lehrling werde ich dafür sorgen, dass der Regen der Rache auf Süderstade fallen wird.$B$BAber zuerst benötigen wir Helculars Rute. Yetis befinden sich jetzt in seinem rituellen Unterschlupf. Bestimmt hat sich eine dieser Bestien die Rute angeeignet.",
    ["O"] = "Holt Helculars Rute von dem Yeti und bringt sie zu Novize Thaivand nach Tarrens Mühle zurück.",
    ["T"] = "Helculars Rache",
  },
  [553] = {
    ["D"] = "Hier, nehmt die Rute von Helcular. Ihr werdet sie brauchen.$B$BHelcular stellte 3 zeremonielle Scheiterhaufen als Teil seines mächtigsten Zaubers auf. Die Flamme von Azel und die Flamme von Veraz liegen in den Vorgebirgshöhlen. Die Flamme von Uzel liegt in einer Höhle über diesen Höhlen, weiter nördlich in den Bergen.$B$BDie Rute von Helcular muss mit jeder der drei Flammen aufgeladen werden. Sobald das Ritual abgeschlossen ist, stoßt Ihr die Rute in Helculars Grab in Süderstade, der streng bewachten Menschenstadt im Süden. Die Rache wird unser sein!",
    ["O"] = "Ladet die Rute von Helcular mit der Macht der Flamme von Azel, der Flamme von Veraz und der Flamme von Uzel auf.$B$BStoßt die aufgeladene Rute in Helculars Grab in Süderstade.",
    ["T"] = "Helculars Rache",
  },
  [554] = {
    ["D"] = "Ausgrabungsleiter Sturmlanze ist ein Meister der Dechiffrierung und Entschlüsselung. Wenn Ihr das Pergament, das Ihr gefunden habt, zu ihm bringt, kann er mit etwas Glück herausfinden, was darin steht. Im Moment studiert Sturmlanze im Ausgrabungsleiterquartier in Eisenschmiede.$B$BWenn Ihr ihn seht, richtet ihm aus, dass Ihr mit mir gesprochen habt, - ich habe ihm in letzter Zeit einiges zu tun gegeben.",
    ["O"] = "Bringt das verhexte Pergament zu Ausgrabungsleiter Sturmlanze nach Eisenschmiede.",
    ["T"] = "Sturmlanzes Entschlüsselung",
  },
  [555] = {
    ["D"] = "Bringt 10 Stücke Schildkrötenfleisch zu Küchenchef Jessen nach Süderstade.",
    ["O"] = "Bringt 10 Stücke Schildkrötenfleisch zu Küchenchef Jessen nach Süderstade.",
    ["T"] = "Milde Schildkrötensuppe",
  },
  [556] = {
    ["D"] = "Ein bemerkenswertes Talent der Hexer von Dalaran ist ihre Vertrautheit mit den Elementaren: mächtige Kreaturen aus anderen Dimensionen. Die Hexer benutzen magische Marken als Mittel, diese Elementare zu kontrollieren. Magus Leermacht hätte gern einige dieser Marken, damit er sie studieren kann.$B$BBegebt Euch auf das Gebiet der Hexer von Dalaran, westlich der Stadt Hügellandhof, und sammelt die Marken ein.$B$BJeder Einwohner von Dalaran hat solche Marken, aber ich schlage vor, dass Ihr im Internierungslager auf die Suche geht. Dort ist es sicherer als in Dalaran selbst.",
    ["O"] = "Bringt 10 abgenutzte Steinmarken zu Bewahrer Bel\'varil nach Tarrens Mühle.",
    ["T"] = "Steinmarken",
  },
  [558] = {
    ["D"] = "Ich habe gehört, dass Lady Jaina Prachtmeer aus Theramore eine der größten Heldinnen ist, die die Allianz je gesehen hat. Wenn ich mal groß bin, möchte ich auch ein Held der Allianz werden!$B$BKennt Ihr Jaina, $N? Tut Ihr mir einen Gefallen? Kriege ich ein Autogramm von ihr? Ich weiß gar nicht, wie ich sie ansprechen soll, und wahrscheinlich ist sie sowieso viel zu beschäftigt, um mich zu beachten... aber was ist mit Euch? Ihr seid doch genau wie sie ein Abenteurer! Euch gibt sie bestimmt ein Autogramm!$B$BBitte, $N. Bitte...",
    ["O"] = "Reist nach Theramore und versucht für Euren Schützling ein Autogramm von Lady Jaina Prachtmeer zu ergattern.",
    ["T"] = "Jainas Autogramm",
  },
  [559] = {
    ["D"] = "Was haltet Ihr davon, $N? Rotpfad war nicht zufrieden mit den ganzen Murlocs, die wir getötet haben - seid nur nicht so bescheiden, Ihr wart ja auch dran beteiligt. Jetzt verlangt er von mir, dass ich ihren Tod beweise und kalte, schleimige Murlocköpfe abhacke und sie ihm bringe, um seine Anerkennung zu erlangen.",
    ["O"] = "Sammelt 10 Murlocköpfe von den Murlocs am Weststrand für Leutnant Farren Orinelle in Süderstade.",
    ["T"] = "Farrens Beweis",
  },
  [560] = {
    ["D"] = "Wisst Ihr, $N, ich würde die Köpfe am liebsten gar nicht anfassen... Da Ihr sie nun schon mal angefasst habt, würde es Euch sehr viel ausmachen, sie zu Rotpfad zu bringen?",
    ["O"] = "Bringt den Sack mit den Murlocköpfen zu Marschall Rotpfad nach Süderstade.",
    ["T"] = "Farrens Beweis",
  },
  [561] = {
    ["D"] = "Bitte informiert Leutnant Orinelle über meinen Wunsch, er möge seine Aufmerksamkeit erneut auf die Nagaplage am Oststrand richten. Sobald er Resultate erzielt hat, soll er persönlich bei mir Bericht erstatten.",
    ["O"] = "Sprecht mit Leutnant Farren Orinelle in Süderstade.",
    ["T"] = "Farrens Beweis",
  },
  [562] = {
    ["D"] = "Mein ehemaliger Mentor bei der Stadtwache von Sturmwind hat mir eine Nachricht zukommen lassen, dass sich das Oberkommando der Allianz langsam Sorgen macht um die Bedrohung, die von den Naga ausgeht. Er hat gehört, dass es gute Aussichten auf eine Beförderung - oder zumindest Versetzung - für jeden gibt, der sich in dieser Angelegenheit als hilfreich erweist.$B$BEs gibt da nur ein winzig kleines Problem... Ich schaff die Naga nicht, $N. Klar, ich kann eine Kneipenschlägerei auflösen, oder einen aggressiven Bettler wegschaffen, aber die Naga sind einfach zu viel für mich. Ihr müsst mir helfen, $N!",
    ["O"] = "Tötet 10 Küstenjäger der Säbelflossen und 10 Sirenen der Säbelflossen für Leutnant Farren Orinelle in Süderstade.",
    ["T"] = "Sturmwind Ho!",
  },
  [563] = {
    ["D"] = "Es dürfte kein Problem sein, meinen Bericht zu beachten. Der neue Hauptmann der Wachen, Major Samuelson, war mein Mentor, während ich in Sturmwind stationiert war. Der Bericht sollte ihm mehr als genug Grund geben, mich wieder in die Stadt zu versetzen. Aber ich brauche Euch, damit Ihr meinen Bericht zu ihm bringt... der alte Rotpfad soll nichts davon mitkriegen.",
    ["O"] = "Bringt Farrens Bericht zu Major Samuelson in die Burg Sturmwind.",
    ["T"] = "Versetzungen",
  },
  [564] = {
    ["D"] = "Nix macht mich wütender als wenn ich diese schönen Pferdchen verlieren tu. Und glaubt\'s mir, wir hab\'n \'ne ganze Menge verloren, an die eklichen untoten Unholde, das verdammte Syndikatsgesocks und all die anderen tunichtgute Schurken, die hier so rumlaufen tun.$B$BAber ich schwör beim Grab von mein alten Opa, \'ne größere Gefahr für uns\'re Pferdchen als die vonne Berglöwen in Alterac tut\'s nich\' geben tun. Komm\' alle Nase lang vonne Berge runner und machen mir dat Leben schwer.$B$BUnd Ihr seid also \'n Held? Dann geht mal und tut was dagegen unternehm\' tun!",
    ["O"] = "Darren Malve aus Süderstade will, dass Ihr 8 Berglöwen und 10 bullige Berglöwen tötet.",
    ["T"] = "Teure Bedrohung",
  },
  [565] = {
    ["D"] = "Ich, der große Bartolo, werde für Euch den weltberühmten, allianzweit bekannten Yetifellumhang anfertigen.$B$BIch wette mit Euch: Auch wenn Ihr die ganze Welt bereist, findet Ihr keinen besseren Umhang. Oh, wartet mal, was ist das, $Gkleiner:kleine:r; $R? \"Bartolo, es gibt keinen feineren Umhang!\" Ja, ja! Das habe ich schon öfter gehört.$B$BBartolo benötigt einen Wollstoffballen, einen Umhang des Hügelwächters von einem geschickten Lederer, feinen Faden und natürlich Yetifell. Bringt mir diese Dinge und Ihr werdet ein glücklicher Kunde!",
    ["O"] = "Bringt Bartolo Ginsetti in Süderstade einen Wollstoffballen, einen Umhang des Hügelwächters, feinen Faden und 10 Haufen Yetifell.",
    ["T"] = "Bartolos Yetifellumhang",
  },
  [566] = {
    ["D"] = "Laut Befehl der Hochexekutorin Darthalia wird der unter dem Namen Baron Vardus bekannte Mensch wegen Verbrechen gegen die Verlassenen gesucht. Vardus ist bekannt als hochrangiges Mitglied der Organisation, die sich das Syndikat nennt.$B$BZuletzt wurde er im Oberland des Alteracgebirges im Norden von Tarrens Mühle gesehen.$B$BEr soll getötet werden. Für den Erhalt der Belohnung müssen Beweise seines Todes zu Hochexekutorin Darthalia gebracht werden.",
    ["O"] = "Tötet Baron Vardus und bringt seinen Kopf zu Hochexekutorin Darthalia in Tarrens Mühle.",
    ["T"] = "GESUCHT: Baron Vardus",
  },
  [567] = {
    ["D"] = "Gefährlich!$B$BDie folgenden Menschen des Hügellands werden als gefährlich erachtet. Ein Kopfgeld ist von Hochexekutorin Darthalia auf sie ausgesetzt worden.$B$BAmtmann Horatio Weißross. Gesucht wegen Mordes an Todeswache Toma.$B$BBürger Wilkes. Gesucht wegen Mordes an Apotheker Eli.$B$BMinenarbeiter Hackett. Gesucht wegen Mordes an Todespirscher Fry.$B$BBäuerin Kalaba. Gesucht wegen hinterhältigem Überfall auf Vorräte von Unterstadt.$B$BJeder dieser Feinde hat sich versteckt und wird nur sehr schwer zu finden sein. Auf die Nachricht ihres Todes wird eine Belohnung gewährt.",
    ["O"] = "Hochexekutorin Darthalia in Tarrens Mühle hat ein Kopfgeld auf Amtmann Horatio Weißross, Bürger Wilkes, Minenarbeiter Hackett und Bäuerin Kalaba ausgesetzt.",
    ["T"] = "Gefährlich!",
  },
  [568] = {
    ["D"] = "Throm-ka, $C!$B$BEure Ankunft in Grom\'gol kommt wahrlich zur rechten Zeit. Als Kommandant des Kriegshäuptlingsstützpunkts hier im Dschungel bin ich durch meine Ehre gebunden und verpflichtet, die Sicherheit aller Mitglieder der Horde zu gewährleisten. Unsere Aufgabe, eine sichere Versorgungskette nach Steinard zu gewährleisten, wird durch einige der örtlichen Einwohner schwer behindert.$B$BIch gebe Euch den Auftrag, die Raptorpopulation außerhalb Grom\'gols zu reduzieren. Sobald Ihr nennenswerte Fortschritte erzielt habt, erstattet mir Bericht und Ihr erhaltet einen neuen Auftrag.",
    ["O"] = "Kommandant Aggro\'gosh vom Basislager von Grom\'gol will, dass Ihr 15 Schmetterschwanzraptoren tötet.",
    ["T"] = "Die Verteidigung von Grom\'gol",
  },
  [569] = {
    ["D"] = "Als wir in diesem Dschungel ankamen, hatten wir nicht vor, die hier ansässigen Stämme zu bekämpfen. Unsere Mission, Steinard zu versorgen, hat höchste Priorität. Aber es gibt Komplikationen. Eine hier ansässige Gruppe von Ogern hat die Ruinen von Mizjah im Südosten von Grom\'gol besetzt. Trotz unserer Warnungen überfallen sie weiterhin unsere Nachschubkarawanen.$B$BIch beauftrage Euch hiermit, Euch mit diesen unkooperativen Ogern auseinander zu setzen.$B$BReist zu den Ruinen und versucht, sie aus diesem Gebiet zu vertreiben. Das sollte ihnen zumindest ein eindeutiges Zeichen setzen.",
    ["O"] = "Kommandant Aggro\'gosh vom Basislager von Grom\'gol will, dass Ihr 10 Ogerschläger und 5 Ogerhexendoktoren tötet.",
    ["T"] = "Die Verteidigung von Grom\'gol",
  },
  [570] = {
    ["D"] = "Die Macht der Dschungelmagie muss erkannt und kann dann von der Horde genutzt werden. Meine Rituale waren bislang erfolgreich. Ich kann sehr mächtige Verzauberungen wirken. Für Euch, $Gmutiger:mutige:c; $C, werde ich einen wirklich ganz besonderen Gegenstand herstellen.$B$BBringt mir einige Schattentatzenpantherkrallen und den Fangzahn einer Tigerin. Es darf aber nicht irgendein Fangzahn sein. Er muss sich in einem makellosen Zustand befinden.$B$BDiese Bestien lauern im gesamten Dschungel, aber Ihr könnt einige ganz in der Nähe finden. Überquert den Fluss bei den Ruinen von Mizjah aus in südöstlicher Richtung.",
    ["O"] = "Scharfseher Mok\'thardin im Basislager von Grom\'gol will 8 Schattentatzenkrallen und einen makellosen Tigerinnenfangzahn.",
    ["T"] = "Mok\'Thardins Verzauberung",
  },
  [571] = {
    ["D"] = "Was ich nun von Euch benötige, ist sehr schwer zu bekommen.$B$BIch benötige ein magisches Band, um all diese Federn, Krallen und den makellosen Fangzahn zu befestigen. Nur die Sehne eines alten Gorillas ist dazu geeignet.$B$BEine solche könnt Ihr nur von einem alten Nebeltalgorilla bekommen. Sie sind oft auf Nahrungssuche im Osten von Beutebucht.$B$BDie Sehne muss in vollkommenem Zustand sein. Ihr werdet wahrscheinlich mehrere Tiere töten müssen, bis Ihr genau das erhaltet, was ich benötige, aber das ist es in jedem Fall wert.",
    ["O"] = "Bringt die Sehne eines alten Gorillas zu Scharfseher Mok\'thardin nach Grom\'gol.",
    ["T"] = "Mok\'Thardins Verzauberung",
  },
  [572] = {
    ["D"] = "Die Dinge, die Ihr von den katzenartigen Bestien des Tales gesammelt habt, sind nur die erste Voraussetzung für einen machtvoll verzauberten Gegenstand. Diese Dschungelornamente, in Verbindung mit meinem Schamanenritual, werden Euch mit einem äußerst nützlichen Werkzeug versehen, mit dessen Hilfe Ihr der Horde und dem Kriegshäuptling beistehen könnt.$B$BAber jetzt müsst Ihr für mich einige Dschungelpirscherfedern sammeln. Für gewöhnlich tragen die Bestien diese Federn an ihren Armbändern. Ihr findet einen Stamm dieser Raptoren direkt südlich von Grom\'gol, nicht weit von der großen Arena.",
    ["O"] = "Bringt 10 Dschungelpirscherfedern zu Scharfseher Mok\'thardin nach Grom\'gol.",
    ["T"] = "Mok\'Thardins Verzauberung",
  },
  [573] = {
    ["D"] = "Es fehlt nun nur noch ein Reagenz, um die Verzauberung zu vervollständigen.$B$BIm Süden, nahe der Westküste, liegt ein heiliger Quell, hoch über den Steilhängen. Dieser Quell ist der Stein des Anstoßes großer Konflikte zwischen religiösen und mystischen Gelehrten. Jede einzelne Gruppe in Azeroth vertritt die Auffassung, dass das Quellwasser nur für das eigene Volk göttlich gemacht wurde.$B$BMeine letzte Aufgabe für Euch: Bringt mir Wasser der Heiligen Quelle. Aber ich warne Euch: Eine Expedition von Forschern der Naga ist auf dem Weg zu der Quelle. Mit ihnen müsst Ihr Euch ebenfalls befassen.",
    ["O"] = "Scharfseher Mok\'thardin von Grom\'gol benötigt Wasser der Heiligen Quelle. Er will auch, dass Ihr 10 Forscher der Naga tötet.",
    ["T"] = "Mok\'Thardins Verzauberung",
  },
  [574] = {
    ["D"] = "Nun, da viele von seinen regulären Truppen gefallen sind, müsst Ihr es mit seinen hochtrainierten Streitkräften aufnehmen. Wenn sie nicht in der Wildnis unsere Rebellensoldaten erschlagen, kann man sie tief innerhalb von Kurzens Truppenlager finden, in einer Höhle, die sie das Vorratslager nennen.$B$BIch kenne einige von Kurzens Truppen persönlich und weiß, dass sie geschickt, loyal und grausam sind. Dies ist keine einfache Aufgabe, mit der ich Euch betraue, $N.$B$BNachdem Ihr sie besiegt habt, meldet Euch bei Leutnant Doren, meinem befehlshabenden Offizier. Er wird Euren Bericht aus erster Hand haben wollen.",
    ["O"] = "Tötet 10 von Kurzens Kommandosoldaten und 6 von Kurzens Kopfschrumpfer.$B$BErstattet Bericht bei Leutnant Doren im Rebellenlager.",
    ["T"] = "Spezialtruppe",
  },
  [575] = {
    ["D"] = "Der beste Ledermacher der Südlichen Meere und der Alten wie der Neuen Welt. So nennen sie mich. Mich! Drizzlik! Der beste Le... ähem. Nun ja.$B$BMeine Lederwaren sind nah und fern als absolut vorzüglich bekannt, erste Wahl der wahren Kenner!$B$BGerade habe ich eine Bestellung von Direktor Quizvox vom Tüftlerverband über ein Dutzend seiner Lieblingsstiefel aus der Exzelsiorlinie erhalten. Unsere Verkaufsschlager, ehrlich gesagt.$B$BFür die Grundform benötige ich Krokiliskenhäute von den Krokis entlang des Flusses im Norden des Schlingendorntals.",
    ["O"] = "Bringt 2 große Flusskrokiliskenhäute zu Drizzlik nach Beutebucht.",
    ["T"] = "Angebot und Nachfrage",
  },
  [576] = {
    ["D"] = "Haltet ein! Wer da?$B$BGrrr, ich bin so gut wie nutzlos ohne mein verzaubertes Spezialauge. Als ob es nich\' schon schlimm genug is\', dass ich meine Augen im Kampf verlor\'n hab! Aber zum Glück haben wir in Menethil angelegt und ein reisender Hexer hat \'n Spezialglasauge verzaubert, mit dem ich wieder was sehen kann.$B$BGestern Nacht hab\'n mir aber \'n paar von den Blutsegelbukanier\'n auf\'m Weg nach Haus aufgelauert. Die ham mir doch echt mein Auge geklaut! Scheint, als hätt\'n die Burschen Beutebucht gefunden.$B$BKönnt\'er \'nem alten Seebären helfen und mir mein Auge zurückholen?",
    ["O"] = "Torkel Einauge in Beutebucht möchte, dass Ihr sein Auge von den Blutsegelbukanieren zurückholt.",
    ["T"] = "Ein Auge offen halten",
  },
  [577] = {
    ["D"] = "Nun, ich habe die Grundform der Stiefel so weit und sie werden auch sehr gut, aber die Häute, die Ihr mir gebracht habt, werden leider nicht für die inneren Schichten ausreichen.$B$B\"Was sollen wir tun, Drizzlik?\" Diese Frage brennt Euch zweifelsohne auf den Lippen und Ihr habt wirklich Glück, dass Ihr mit Drizzlik sprecht, denn er wird Euch genauestens erklären, was wir zu tun gedenken!$B$BDie inneren Schichten sieht sowieso nie jemand, also werden wir uns zum Ausfüllen der Stiefel einige der billigeren, dickeren Häute der Krokis holen, die um den Nazferitisee leben.",
    ["O"] = "Bringt 5 Schnappkieferkrokiliskenhäute zu Drizzlik nach Beutebucht.",
    ["T"] = "Etwas Zusammenbau benötigt",
  },
  [578] = {
    ["D"] = "Wenn es wirklich einen Goblinmagier und Wasserelementare gibt, die auf dieser Insel ihr Unwesen treiben, dann heißt das, dass der Stein der Gezeiten wirklich mir gehören kann!$B$BIch will, dass Ihr diese Spukinsel findet und herausfindet, wie es dort zugeht. Oh, und falls Ihr mehr über den Stein der Gezeiten herausfinden wollt, bringt diesen Schein zur Bibliothek von Sturmwind, sie sollten dort ein Exemplar des Buches für Euch finden, in dem ich zuerst von der Legende gelesen habe.$B$BSollte es möglich sein, dass Gazban wirklich den Stein gefunden hat...?",
    ["O"] = "Findet die Spukinsel für Baron Revilgaz in Beutebucht.",
    ["T"] = "Der Stein der Gezeiten",
  },
  [579] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Bibliothek von Sturmwind",
  },
  [580] = {
    ["D"] = "Wir waren dabei, uns tüchtig einen hinter die Binde zu gießen, konnten \'s Hinterland schon sehen. Dachten schon, die See wollte uns ganz und gar verschlingen.$B$BOl\' Slim weiß alles über flüssigen Mut! Der Käpt\'n hatte eine Kiste mit Pupellyverbos\' Portwein, den wir auf\'m Weg nach Sturmwind von \'nem Freibeuter geholt hatten.$B$BAls der Sturm uns dann vom Hinterland aus getroffen hat, bin ich also in den Grog rein. Große Welle kam und hat die Flaschen über Bord gespült. Der Käpt\'n wird mich bestimmt mit der Katze streicheln, wenn Ihr mir nich\' helft, die wieder zurückzubringen!",
    ["O"] = "Klein Whiskey in Beutebucht möchte, dass Ihr ihm die Flaschen Pupellyverbos\' Portwein bringt, die er verloren hat.",
    ["T"] = "Klein Whiskeys verlorener Grog",
  },
  [581] = {
    ["D"] = "Unheil droht. Unheil für den jüngsten Sohn meines Häuptlings, Yenniku. Der Sitte unseres Volkes nach wurde er den Gurubashitrollen von Zul\'Gurub gegeben, aber nachdem mein Stamm, der Dunkelspeerstamm, mit dem Orc Thrall gegangen ist, war der arme Yenniku für uns verloren. Ich wurde aus dem fernen Orgrimmar zurück ins Schlingendorntal geschickt, um nach dem Sohn meines Häuptlings zu suchen.$B$BUnd ich glaube, dass der Blutskalpstamm ihn hat.$B$BWenn Ihr mir helfen wollt, dann jagt die Blutskalpe. Ihr könnt sie im Norden finden. Sammelt ihre Hauer und kehrt zu mir zurück.",
    ["O"] = "Bringt 9 Hauer der Blutskalpe zu Nimboya in das Basislager von Grom\'gol.",
    ["T"] = "Die Suche nach Yenniku",
  },
  [582] = {
    ["D"] = "Wir müssen herausfinden, ob Yenniku tot ist.$B$BErschlagt die Kopfjäger der Blutskalpe und nehmt ihnen ihre Schrumpfköpfe ab. Ihr müsst viele, viele Köpfe sammeln und ich werde sie überprüfen, um sicherzugehen, dass Yennikus Kopf nicht dabei ist.$B$BSollte Yenniku von den Blutskalpen getötet worden sein, möge ein Fluch für 600 Jahre auf den Geist ihres Häuptlings, Gan\'zulah, kommen!$B$BGeht, $N. Viele Kopfjäger könnt Ihr bei den Ruinen von Zuuldaia und Zul\'Kunda an der Küste im Norden finden.",
    ["O"] = "Bringt 20 Schrumpfköpfe zu Nimboya in das Basislager von Grom\'gol.",
    ["T"] = "Kopfjägerei",
  },
  [583] = {
    ["D"] = "Sucht Hemet Nesingwary Jr. auf und sprecht mit ihm.",
    ["O"] = "Sucht Hemet Nesingwary Jr. auf und sprecht mit ihm.",
    ["T"] = "Willkommen im Dschungel",
  },
  [584] = {
    ["D"] = "Yenniku war nicht unter den Köpfen, der Großen Schlange sei Dank. Aber wir sind noch weit entfernt davon, ihn zu finden.$B$BWir müssen die Wahrheit herausfinden, und ich fürchte, dass nur der Häuptling der Blutskalpe und sein Hexendoktor sie kennen. Sie sind Feinde des Dunkelspeerstammes und werden nicht freiwillig reden, also müssen wir die Wahrheit von ihren toten Lippen erzwingen!$B$BBringt mir die Köpfe von Gan\'zulah und Nezzliok dem Düsteren und legt sie in diesen Kessel.$B$BSie halten sich in den Tiefen von Zul\'Kunda auf. Bringt ihre Köpfe... wir werden sie schon zum Sprechen bringen.",
    ["O"] = "Bringt Gan\'zulahs Kopf und Nezzlioks Kopf zum blubbernden Kessel im Basislager von Grom\'gol.",
    ["T"] = "Köpfe des Blutskalpklans",
  },
  [585] = {
    ["D"] = "<Der Kopf von Nezzliok spricht.>$B$BEs stimmt. Ich weiß, wo das Kind der Dunkelspeere hingebracht wurde. Ihr habt mich besiegt und meinen Geist gebunden, aber ohne Gegenleistung sage ich nichts!$B$BDie Blutskalpe streiten mit den Schädelspaltern. Um mein Wohlwollen zu erhalten, stehlt 3 der Trophäenschädel von den Schädelspaltern und legt sie hierher zu uns, sodass mein Geist im Nether Sklaven zur Verfügung hat. Ein Schädel muss aus ihrem Trophäenstapel in Balia\'mah, der zweite von Ziata\'jai und der dritte von Zul\'Mamwe stammen.",
    ["O"] = "Bringt eine Trophäe von Balia\'mah, eine Trophäe von Ziata\'jai und eine Trophäe von Zul\'Mamwe zum blubbernden Kessel im Basislager von Grom\'gol.",
    ["T"] = "Mit Nezzliok sprechen",
  },
  [586] = {
    ["D"] = "<Der Kopf von Gan\'zulah spricht.>$B$BVerflucht seid Ihr! Und auch Eure Vorfahren!$B$BNur Blut und Rache können meine Wut beschwichtigen; wenn das Euer Wunsch ist, dann... tut Folgendes:$B$BWerft Euch in das Herz der Schädelspalter im Osten. Erschlagt die schrecklichsten unter ihnen, und wenn Ihr überleben solltet... dann tretet gegen ihren Häuptling, Ana\'thek den Grausamen, an. Ha! Bringt mir seine zerschmetterte Rüstung, wenn Ihr es vermögt!$B$BMöge er Eure Glieder ausreißen und Euch der Fäulnis ausliefern und den Geiern zum Fraß vorwerfen.",
    ["O"] = "Tötet 8 Jäger der Schädelspalter, 6 Kopfjäger der Schädelspalter und 4 Berserker der Schädelspalter.$B$BBringt die zerbrochene Rüstung von Ana\'thek zum blubbernden Kessel in das Basislager von Grom\'gol.",
    ["T"] = "Mit Gan\'zulah sprechen",
  },
  [587] = {
    ["D"] = "Ah, das Leben auf See. Ich sag nicht, dass es das wahre Leben ist, aber es ist mein Leben.$B$BWenn ich in den Hafen komme, such ich mir eine gute Flasche Grog und sehr guten Schnupftabak. Schlechte Angewohnheiten, ich weiß, aber irgendein Laster muss\'n Pirat ja nun mal haben, nich?$B$BAlso könnt Ihr Euch vielleicht vorstellen, wie niedergeschlagen ich war, als ich herausfand, dass der Kräuterkundige nichts von dem Zeug mehr da hatte. Erzählte mir, dass seine Lieferung von den verdammten Räubern der Blutsegelbukaniere geklaut worden ist.$B$BIch wette, $Gein furchtloser:eine furchtlose:c; $C wie Ihr könnte sie überreden, die Ware zu teilen, nich?",
    ["O"] = "Deeg in Beutebucht möchte, dass Ihr ihm 15 Prisen Schnupftabak bringt.",
    ["T"] = "Auf dem Schnupftabak",
  },
  [588] = {
    ["D"] = "<Nezzliok spricht.>$B$BIhr habt mich und meinen Meister beschwichtigt und ich werde Euch die Informationen geben, nach denen es Euch verlangt...$B$BDer junge Yenniku ist verloren.$B$BEr wurde von Hexendoktor Zanzil dem Ausgestoßenen entführt. Jetzt sind sein Geist und sein Körper in Zanzils Kontrolle und nur die stärkste aller Magien kann ihn davon befreien.$B$BMagie, die Euch nicht zur Verfügung steht, $C.$B$BVielleicht können Eure Verbündeten der Dunkelspeere in Bambala östlich von einen Hoffnungsschimmer zur Erlösung Yennikus darstellen. Aber das bezweifle ich.",
    ["O"] = "Sprecht mit Kin\'weelay.",
    ["T"] = "Yennikus Schicksal",
  },
  [590] = {
    ["D"] = "Was?$B$BWas?!$B$BIhr wollt Geld? Ich schulde Euch kein Geld.$B$BBrief? Was für ein Brief? Oh, Ihr wollt also kämpfen? Nun, gut Kumpel... dann lasst uns anfangen! Schulde Euch Geld...",
    ["O"] = "Besiegt Calvin Montague in Todesend.",
    ["T"] = "Ein Schurkenhandel",
  },
  [591] = {
    ["D"] = "Das Gedankenauge befindet sich bei einem Ogermagier, in den Tiefen des Ogerhügels der Mosh\'Ogg östlich von hier. Sein Name ist Mai\'Zoth. Ich kenne ihn noch von der Zeit, als sich die Dunkelspeere im Schlingendorntal aufhielten - er ist bösartig und seine Magie ist stark.$B$BUnd wenn er im Besitz des Gedankenauges ist, dann ist seine Magie noch stärker.$B$BIhr müsst gegen Mai\'Zoth antreten und ihm das Gedankenauge abnehmen. Es ist eine gefährliche Aufgabe, aber es ist die einzige Hoffnung, die wir haben, Yenniku zu retten.",
    ["O"] = "Bringt das Gedankenauge zu Kin\'weelay in das Basislager von Grom\'gol.",
    ["T"] = "Das Gedankenauge",
  },
  [594] = {
    ["D"] = "In der Notiz steht Folgendes in recht guter Handschrift:$B$BAn alle, die mir helfen können:$B$BBitte, ich benötige dringend Hilfe. Ein großer Himmelsmähnengorilla hält mich auf einer Insel in den Südlichen Meeren gefangen.$B$BDie Sternbilder am Nachthimmel sind mir nicht vertraut und von Dschungelbäumen verdeckt, aber an klaren Tagen kann ich die Wilden Ufer im Nordwesten erkennen und im Südwesten etwas, das große Segelschiffe sein könnten.$B$BIch flehe Euch an, mein möglicher Retter, bitte helft mir.",
    ["O"] = "Findet den Schreiber dieser Flaschenpost.",
    ["T"] = "Flaschenpost",
  },
  [595] = {
    ["D"] = "Ihr scheint ja sehr erpicht auf Arbeit zu sein, Kumpel. Bin nicht sicher, warum eine Landratte wie Ihr, $R, und $Gein:eine:c; $C noch dazu, den Schwarzmeerräubern Hilfe anbietet, aber ich werd Euch nicht fortschicken.$B$BUnser Schiff hat gerade hier in Beutebucht angedockt und es heißt, dass unsere Rivalen - diese verdammten, dreckigen Blutsegelbukaniere - sich im gesamten Süden des Schlingendorntals herumtreiben.$B$BMeine Jungs sind aber zu beschäftigt, sich einen anzusaufen und Mädchen nachzugaffen! Was ich brauch, ist ein Späher.$B$BBegebt Euch zur Bucht im Norden von Beutebucht an der Ungezähmten Küste und schaut, was Ihr so findet.",
    ["O"] = "Erster Maat Krazz will, dass Ihr die Bucht nördlich von Beutebucht überprüft.",
    ["T"] = "Die Blutsegelbukaniere",
  },
  [596] = {
    ["D"] = "Ich wurde nach Grom\'gol geschickt, um Nimboya auf seiner Suche zu begleiten, aber... Ich habe meine eigenen Gründe, hier zu sein.$B$BDer Blutskalpstamm ist der Feind unseres Stammes. Wenn wir Trophäen unserer Feinde sammeln, erhalten wir Macht über sie. Ihr könnt unserem Stamm behilflich sein, indem Ihr unsere Feinde jagt.$B$BSie leben im Norden. Erschlagt die Trolle der Blutskalpe und nehmt ihre blutigen Knochenhalsketten mit. Wenn Ihr viele davon sammelt, wird Eure Belohnung groß sein.",
    ["O"] = "Bringt 25 blutige Knochenhalsketten zu Kin\'weelay in das Basislager von Grom\'gol.",
    ["T"] = "Blutige Knochenhalskette",
  },
  [597] = {
    ["D"] = "Die Karte zeigt Beutebucht mit einem roten \"X\" genau an dem Punkt, wo Ihr gerade steht.$B$BDer hingekritzelte Text lautet:$B$B\"Leute, errichtet ein Lager an dieser Stelle und verhaltet euch schön unauffällig. Wartet auf das Signal vom südlichen Kommandoposten, bevor ihr angreift. Wenn sich jemand euch nähert, tötet ihr ihn.\"",
    ["O"] = "Berichtet dem Ersten Maat Krazz in Beutebucht von Euren Funden.",
    ["T"] = "Die Blutsegelbukaniere",
  },
  [598] = {
    ["D"] = "Nun, da Ihr Blutskalptrophäen gesammelt habt, habe ich eine weitere Aufgabe für Euch. Der Schädelspalterstamm der Trolle lebt im Südosten und wir bekämpfen ihn schon seit Jahren. Obwohl keine Dunkelspeere mehr im Schlingendorntal leben, sind wir immer noch dem Konflikt mit den Schädelspaltern verpflichtet.$B$BAlso, jagt sie. Sammelt ihre Spaltknochenhalsketten und kehrt zu mir zurück. Helft uns in unserem Krieg und Ihr werdet die Freundschaft der Dunkelspeere erlangen.",
    ["O"] = "Bringt 25 Spaltknochenhalsketten zu Kin\'weelay in das Basislager von Grom\'gol.",
    ["T"] = "Spaltknochenhalskette",
  },
  [599] = {
    ["D"] = "Neee, isses denn wahr! Also planen die Blutsegelbukaniere einen Angriff auf Beutebucht? Diese Schurken haben vielleicht Nerven. Beutebucht wird immer von den Schwarzmeerräubern beherrscht werden!$B$BDas ist ein größeres Unterfangen als ich dachte. Erstattet Flottenmeister Seehorn sofort Bericht über alles, was Ihr erfahren habt! Der alte Seebär ist irgendwo in der Stadt...",
    ["O"] = "Erstattet Flottenmeister Seehorn Bericht über den geplanten Angriff der Blutsegelbukaniere.",
    ["T"] = "Die Blutsegelbukaniere",
  },
  [600] = {
    ["D"] = "Die Venture Company hat ein paar Sachen im Schlingendorntal am Laufen, die hart arbeitende Goblins, wie ich es einer bin, davon abhalten, ehrliches Gold zu verdienen. Bitte, Ihr müsst mir helfen!$B$BDie Venture Company betreibt einen Stollen in der Nähe der Kristalladermine im Norden. Sie kommen nicht in die Mine hinein wegen der ganzen Basilisken, aber trotzdem sind sie in der Lage, singende Kristalle aus den umgebenden Hügeln herauszugraben.$B$BNehmt ihnen die Kristalle ab und zeigt ihnen, dass sie im Dschungel nichts zu melden haben.$B$BUnd... ähm... bringt mir diese Kristalle als Beweis!",
    ["O"] = "Bringt 10 singende blaue Kristalle zu Crank Zischelbub nach Beutebucht.",
    ["T"] = "Venture Company-Bergbau",
  },
  [601] = {
    ["D"] = "Die Wasserelementare wurden ohne Zweifel von Gazban, dem Goblinhexer beschworen, von dem man behauptet, er habe den Stein der Gezeiten entdeckt. Wie Ihr vielleicht wisst, verflucht der Stein der Gezeiten seinen Träger, sodass er ständig zwischen Sein und Nicht-Sein wandelt.$B$BVielleicht können wir einen Weg finden, dem Fluch entgegenzuwirken. Ein Magier von Dalaran kann die magischen Überreste einer der Armschienen des Wasserelementars für uns analysieren. Ihr müsst wieder zurück zur Insel und einige Exemplare davon herbringen.",
    ["O"] = "Holt 6 Wasserelementararmschienen für Baron Revilgaz in Beutebucht.",
    ["T"] = "Wasserelementare",
  },
  [602] = {
    ["D"] = "Bringt die Wasserelementararmschienen zu Erzmagier Ansirem Runenweber am Dalarankrater.",
    ["O"] = "Bringt die Wasserelementararmschienen zu Erzmagier Ansirem Runenweber am Dalarankrater.",
    ["T"] = "Magische Analyse",
  },
  [603] = {
    ["D"] = "Der Magier, der im Besitz dieses Steins ist, verstärkt seine Macht durch ein Zentrum... eine Art Altar. Ihr solltet in der Lage sein, dies zu unterbrechen, indem Ihr es überwältigt.$B$BWie es der Zufall will, weiß ich auch genau womit: ein alter Dolch, ein weiteres Trollrelikt. Es gibt da nur ein... winzig kleines Problem.$B$BWisst Ihr, ich fand den Dolch auf einer Forschungsreise im Schlingendorntal, aber ich gab ihn meiner eigenwilligen Tochter, Catelyn, die mir nach Beutebucht weglief, um Piratin zu werden.",
    ["O"] = "Sprecht mit Catelyn in Beutebucht.",
    ["T"] = "Ansirems Schlüssel",
  },
  [604] = {
    ["D"] = "Krazz lobt Eure Hingabe an die Schwarzmeerräuber in hohen Tönen. Ich werde Euch mit einer Mission betrauen, die für die Flotte von höchster Bedeutsamkeit ist.$B$BBespitzelt den so genannten südlichen Kommandoposten, den die Blutsegelbukaniere an den Wilden Ufern aufgebaut haben. Überfallt das Lager. Bringt mir ihre Karten zusammen mit ihren schriftlichen Befehlen. Wir werden ganz genau herausfinden, was dieser Abschaum so vorhat.",
    ["O"] = "Flottenmeister Seehorn in Beutebucht will, dass Ihr 10 Schwadroneure der Blutsegelbukaniere tötet und die Blutsegelkarten sowie die Blutsegelbefehle zurückbringt.",
    ["T"] = "Die Blutsegelbukaniere",
  },
  [605] = {
    ["D"] = "Die singenden Kristalle gibt es nur im Schlingendorntal und sie sind für gewisse Gruppierungen äußerst wertvoll. Ich kann diese Kristalle bewegen, aber die verfluchte Venture Company macht es einem ehrlichen Unternehmer wie mir fast unmöglich, überhaupt welche zu sammeln.$B$BIch würde Euch gern anheuern.$B$BDie Basilisken des Schlingendorntals essen die Kristalle. Dadurch erhalten sie ihre harte Haut und manchmal können Kristalle von ordentlicher Qualität von den Häuten abgesammelt werden.$B$BIhr könnt sie von jedem Basilisken sammeln, aber die nicht ganz so bösartigen leben an der Küste südlich von Zul\'Kunda, im Norden.",
    ["O"] = "Bringt 10 singende Kristallsplitter zu Crank Zischelbub.",
    ["T"] = "Singende blaue Splitter",
  },
  [606] = {
    ["D"] = "Wenn ich etwas nicht leiden kann, dann sind das Leute, die ihre Spielschulden nicht bezahlen! Und es gibt \'ne Menge Leute, die mir was schulden. Ob Ihr mir wohl beim Eintreiben helfen könnt?$B$BDa ist zuerst mal \"Zitterhand\" Phillipe unten bei den Docks. Er schuldet mir eine Menge nach dem Spiel letzte Woche und ich will mein Geld jetzt! Aber um da dranzukommen, müssen wir ihm gehörig Angst einjagen.$B$BVerlasst die Stadt und jagt alte Nebeltalgorillas. Sucht Euch ein paar nette Innereien heraus und bringt sie zu Zitterhand. Sagt ihm ja nicht, dass sie von einem Tier stammen, er soll ruhig das Schlimmste annehmen.",
    ["O"] = "Bringt 5 Nebeltalinnereien zu \"Zitterhand\" Phillipe nach Beutebucht.",
    ["T"] = "Zitterhand Angst einjagen",
  },
  [608] = {
    ["D"] = "Dieser verdammte Firallon! Könnte kein Boot in einem Stück über den Kristallsee bringen, ganz zu schweigen eine ganze Flotte gegen uns führen.$B$BDie Blutsegelbukaniere werden für ihre Sorglosigkeit teuer bezahlen. Jetzt, da wir ihren Angriffsplan kennen, können wir unseren Gegenschlag starten, bevor sie überhaupt merken, was los ist.$B$BAber warum sollten wir die ganze Flotte schicken, wenn eine mutige Gruppe von Abenteurern das auch allein schaffen kann? $N, nehmt einige von Euren geschicktesten Kameraden zu Firallons Ankerplatz und tötet die Führungskräfte der Blutsegelbukaniere. Ihre Flotte wird bald danach sinken...",
    ["O"] = "Flottenmeister Seehorn in Beutebucht will, dass Ihr Kapitän Stillwasser, Käpt\'n Kielhol und Flottenmeister Firallon tötet.",
    ["T"] = "Die Blutsegelbukaniere",
  },
  [609] = {
    ["D"] = "Meine Liste wird kürzer, aber da sind immer noch Leute, die mir was schulden.$B$BAls Nächste haben wir da Maury \"Klumpfuß\" Wilkins, Jon-Jon die Krähe und Chucky \"Zehn Daumen\".$B$BDie Schulden dieser räudigen Hunde stehen schon seit Monaten aus und ich dachte schon, dass sie die Stadt verlassen hätten, nur um nicht zahlen zu müssen! Später hab ich gehört, dass sie jetzt verflucht und verhext sind und in den Dschungelruinen ihr Unwesen treiben. Aber ihr Schicksal ist mir ziemlich egal - ich will nur, was mir zusteht!$B$BSie halten sich in den Ruinen von Aboraz und den Ruinen von Jubuwal nordöstlich von hier auf. Sucht sie und holt das Geld.",
    ["O"] = "Bringt Maurys Klumpfuß, Jon-Jons goldenes Handfernrohr und Chuckys gewaltigen Ring zu \"Seewolf\" MacKinley in Beutebucht.",
    ["T"] = "Voodooschulden",
  },
  [610] = {
    ["D"] = "Mein VATER hat Euch geschickt? Dachte, der alte Knacker hätte mich mittlerweile aufgegeben.$B$BEs war ja nicht unbedingt sein Fehler, dass ich weggerannt bin, er hätte einfach nicht erwarten dürfen, dass ich Lust auf ein langweiliges Leben hab, alte muffige Bücher lese und mich mit grauhaarigen, steifen, hochnäsigen Magiern abgebe!$B$BWenn Ihr meinen Dolch braucht, müsst Ihr ihn von \"Schönling\" Duncan wiederholen, einem der Blutsegelbukaniere. Rammt einfach was zwischen seine Schultern und er wird ihn sicherlich sofort rausrücken.",
    ["O"] = "Holt Catelyns Dolch von \"Schönling\" Duncan.",
    ["T"] = "Schönling Duncan",
  },
  [611] = {
    ["D"] = "Ihr sucht also nach einer Art Altar? Nun, ich bin zwar kein Experte für Trollruinen wie mein Vater, aber ich habe von einer versunkenen Trollstadt nah bei der Insel gehört, die Ihr sucht. Vielleicht solltet Ihr dort mal suchen.$B$BIhr habt mein Messer für mich wiedergeholt, also könnt Ihr es Euch auch borgen... Ich will es aber wiederhaben, wenn Ihr fertig seid!",
    ["O"] = "Zerstört den Altar der Gezeiten mit Catelyns Klinge, tötet Gazban und bringt dann den Stein der Gezeiten sowie Catelyns Klinge zurück zu Baron Revilgaz.",
    ["T"] = "Der Fluch der Gezeiten",
  },
  [613] = {
    ["D"] = "Verflucht noch eins! Dieser Blödmann Maury hat ein Schloss an seinem Fuß befestigt und ich kann es nicht öffnen! Seine Beute ist in dem Fuß, und das sind seine Schulden!$B$BWir müssen den Schlüssel finden, dann kriegen wir auch das Schloss auf. Maury hat mit den Ogern der Mosh\'Ogg gespielt, in ihrem Hügel im Nordosten. Als er noch lebte, erzählte er mir, dass sie ihn das letzte Mal von dort verjagt hätten, weil er zu viel gewonnen habe. Vielleicht haben die Oger ja seinen Schlüssel.$B$BWenn sie ihn haben sollten, müsstet Ihr eine ganze Menge von ihnen beseitigen, bevor Ihr den richtigen findet.",
    ["O"] = "Bringt Maurys Schlüssel zu \"Seewolf\" MacKinley in Beutebucht.",
    ["T"] = "Maurys Fuß aufbrechen",
  },
  [614] = {
    ["D"] = "Früher hab ich ein Schiff von hier bis ganz nach Ratschet, in Kalimdor gesegelt. Ich war ein erfolgreicher Kapitän mit einem einwandfreien Ruf.$B$BBis...$B$BDiese verfluchten Meeresriesen mich ruinierten! Sie haben mein Schiff zerschmettert, meine Crew umgebracht und mich in ein Rettungsboot nach Beutebucht gesteckt. Einer dieser Riesen, Gorlash, hat meine Kapitänstruhe gestohlen. Er sagte, er wolle sie als Schnupftabakdose. Diese Dreistigkeit!$B$BIch habe gehört, dass Gorlash an der Küste östlich von hier sein Unwesen treibt. Findet ihn und bringt mir meine Truhe zurück! Tut das, und Ihr habt Euch die Dankbarkeit eines Kapitäns verdient!",
    ["O"] = "Bringt Smotts\' Truhe zu Hecklebury Smotts nach Beutebucht.",
    ["T"] = "Die Truhe des Kapitäns",
  },
  [616] = {
    ["D"] = "Für Baron Revilgaz höre ich mich immer nach Gerüchten von seltsamen und ungewöhnlichen Dingen in Beutebucht und im Schlingendorntal um. Normalerweise gebe ich ja nicht viel auf Geistergeschichten, aber ich habe ein paar Seeleute in der Taverne über eine Insel nahe der Küste reden hören, auf der es spuken soll. Sie erzählten von Wasserelementaren und einem rasenden Goblingeist.$B$BWenn Ihr mit Revilgaz sprechen solltet, erzählt Ihr ihm davon?",
    ["O"] = "Sprecht mit Baron Revilgaz in Beutebucht.",
    ["T"] = "Die Spukinsel",
  },
  [617] = {
    ["D"] = "Sucht\'er Arbeit, $N? Hab vielleicht was für Euch, wenn\'er Euch was traut.$B$BMein Freund Groy verdient sich dumm un\' dämlich mit dem Verkauf von dem Zeuch, das die Naga Akirisschilfrohr nennen: is\' so \'ne Art wertvolles Meeresschilfrohr. Bin nich\' sicher, für was es gut is\', aber er bezahlt gut für Bündel von dem Zeuch.$B$BWenn Ihr mir 10 Stängel von dem Zeuch bringt, sind wa im Geschäft. Die Naga wurden zuletzt am Strand nördlich von hier gesehen.",
    ["O"] = "Bringt 10 Stängel Akirisschilfrohr zu Freibeuter Plust nach Beutebucht.",
    ["T"] = "Bündelweise Akiris",
  },
  [618] = {
    ["D"] = "Negolash ist ein hungriger Riese.$B$BAls die Riesen unser Schiff zerstörten, ging Negolash schnurstracks in meine Kombüse. Er hatte keinen Appetit auf die Crew wie die anderen Riesen. Negolash wollte Wein... und meine gegrillten Bussardflügel. Die sind meine Spezialität. Für das Rezept musste ich bis ins Ödland reisen!$B$BWenn Ihr den Riesen anlocken wollt, dann besorgt Euch etwas Wein und einen Haufen Bussardflügel. Legt sie in unser altes Rettungsboot, an der Küste südöstlich von hier. Sobald Negolash das Essen riecht, wird er bestimmt kommen!",
    ["O"] = "Bringt 10 gegrillte Bussardflügel und 5 Flaschen Dschungelrankenwein zu Käpt\'n Smotts Rettungsboot.$B$BTötet Negolash und bringt Smotts\' Entersäbel zu Kapitän Smotts ins Schlingendorntal.",
    ["T"] = "Negolash entgegentreten",
  },
  [620] = {
    ["D"] = "Auf dem Saum dieser feinen Schärpe sind die Buchstaben \"HS\" eingestickt.",
    ["O"] = "Bringt die Schärpe mit Monogramm zu ihrem Besitzer zurück.",
    ["T"] = "Die Schärpe mit Monogramm",
  },
  [621] = {
    ["D"] = "Zanzil der Ausgestoßene hält sich mit seinen Anhängern in den Ruinen von Aboraz und Jubuwal auf, nordöstlich von Beutebucht. Sein Stamm hat ihn verbannt. Warum? Nun...$B$BZanzil hat eine Methode, den Geist von anderen zu beugen, und seine Anführer fürchten ihn. Er benutzt eine Mixtur von Dschungelpflanzen, die den Willen seiner Anhänger unterdrücken und ihre Körper stärken.$B$BSeine Mixtur könnte sehr wertvoll für diejenigen sein, die andere zu kontrollieren wünschen.$B$BBringt mir Proben seiner Mixtur und ich werde einen Nutzen für sie finden.",
    ["O"] = "Bringt 12 Proben von Zanzils Mixtur zu Crank Zischelbub nach Beutebucht.",
    ["T"] = "Zanzils Geheimnis",
  },
  [622] = {
    ["D"] = "Während Ihr fort wart, habe ich den Topf, den Ihr mir brachtet, repariert. Ich habe sogar noch eine zusätzlich Dampfpfeife angebracht, und seht mal! Da ist ein neues Hackbeil am Boden, das kann Beinknochen durchtrennen.$B$BJa, ja, Krazeks Spezialtopf weiß, was Köche wünschen! Sein Besitzer wird bestimmt erfreut sein.",
    ["O"] = "Bringt Krazeks reparierten Topf zu Korporal Kaleb im Rebellenlager.",
    ["T"] = "Kehrt zu Korporal Kaleb zurück",
  },
  [623] = {
    ["D"] = "Ach ja, $N, fein dass Ihr hier seid. Während Eurer Abwesenheit hab ich \'ne Nachricht von Groy gekricht. Er sachte, dass er mit der letzt\'n Lieferung sehr zufrieden war und dass \'n Kunde von ihm noch mehr von dem Zeuch hab\'n will und das sofort.$B$BIch fahr erst inner Woche wieder nach Theramore, aber wenner den Weg nich\' scheut, dann könnter auch gleich das Zeuch für mich mitnehmen.$B$BAbenteuerlustich? Wissen tut er ja, wo die Düstermarschen sin\', nich\'? Ich wär aber vorsichtich, Theramore kann gefährlich sein, wenn man inne falschen Kreise kommt.",
    ["O"] = "Bringt ein Bündel Akirisschilfrohr zu Freibeuter Groy nach Theramore.",
    ["T"] = "Bündelweise Akiris",
  },
  [624] = {
    ["D"] = "Eine Brücke verziert und stark frequentiert$BUnter ihr werdet Ihr finden was Euch sehnlichst erwartet$BManche nennen es Hinweis andere Tipp$BEs liegt östlich der Schlucht der Totenwinde und südlich vom Rotkammgebirge$BFür unendliche Zeit genau dort wird es liegen$BIn den Sümpfen des Elends sich für immer wiegen$B$B",
    ["O"] = "Löst das Rätsel!",
    ["T"] = "Cortellos Rätsel",
  },
  [625] = {
    ["D"] = "Im fernen Kalimdor wartet ein Land$BDie Hoffnung hielt dort den Gefahren nicht stand$BDas Wetter östlich des Brachlandes ist harsch$BDunkel und Tod droh\'n in den Düstermarschen$BDoch in einer Höhle, Richtung Südwest$BLiegt ein Hinweis für alle, die wagen den Test.",
    ["O"] = "Löst das Rätsel!",
    ["T"] = "Cortellos Rätsel",
  },
  [626] = {
    ["D"] = "Nach Lordaeron! Schwerer Aufruhr dort wütet$BSoldatenmut wird mit dem Tode vergütet$BSucht Euren Weg durch den Aufstand dort$Bund Eure Such\' ist beendet an einem Ort$BAm Fuße der großen Fälle, entlang der Küste im Hinterland$BIm Wasser dort tief unten, nehmt Ihr Euren Schatz in die Hand!",
    ["O"] = "Löst das Rätsel!",
    ["T"] = "Cortellos Rätsel",
  },
  [627] = {
    ["D"] = "Es gibt eine Berghöhle in der Schlucht der Trockenstoppel, weit im Norden des Arathihochlands. In dieser Höhle findet sich ein seltenes Erz: Blutstein. Der Blutstein in dieser Höhle ist zwar nicht von höchster Qualität, aber auch Blutstein von minderer Qualität ist wertvoll... für diejenigen, die wissen, wie er zu nutzen ist.$B$BIch hätte gerne etwas davon.$B$BIch werde Euren Spezialtopf reparieren, aber während ich daran arbeite, möchte ich, dass Ihr mir einige Blutsteinproben bringt. Bringt mir genügend und Ihr erhaltet einen Bonus.$B$BOh, und falls Ihr nicht wisst, wie man ihn abbaut... findet jemanden, der es kann!",
    ["O"] = "Bringt 4 Ladungen geringes Blutsteinerz zu Krazek nach Beutebucht.",
    ["T"] = "Gefallen für Krazek",
  },
  [628] = {
    ["D"] = "Die Innenseiten sind fertig gestellt, jetzt fehlt nur noch die äußere Schicht. Dafür verwende ich nur die besten Häute der alten Krokilisken. Sie sind widerstandsfähiger und eine bessere Leinwand - wenn Ihr es so nennen wollt - für die Muster und Akzente, die Drizzliks Exzelsiorlinie ausmachen.$B$BEs ist allerdings äußerst schwierig, diese vollkommene Haut zu finden; die Einzige, die meinen Plänen gerecht wird, ist die eines alten Salzwasserkrokilisken. Die Alten kommen nur heraus, um die anderen Salzwasserkrokis zu verteidigen, also werdet Ihr wahrscheinlich einige töten müssen, um einen von der Sorte herauszulocken.",
    ["O"] = "Bringt die Haut eines alten Krokilisken zu Drizzlik nach Beutebucht.",
    ["T"] = "Exzelsior",
  },
  [629] = {
    ["D"] = "Vor langer Zeit, als ein großes Beben die Welt erschütterte, versank eine alte Trollstadt in den Wassern der Ungezähmten Küste im Nordwesten. Wir nennen diesen Ort das Finstere Riff, denn Murlocs bewohnen jetzt die Ruinen der Stadt und greifen jeden an, der sich ihnen nähert.$B$BIn diesen alten Ruinen liegt eine alte Schrifttafel, die die uralte Geschichte von Gri\'lek erzählt, einem Helden der Trolllegenden. Diese Geschichte ist dem Dunkelspeerstamm heilig und auch wenn die Tafel zu groß ist, um sie zu bewegen, so möchte ich doch einen Splitter davon, damit ich ihn in unserer neuen Heimat in Orgrimmar in einen Schrein legen kann.",
    ["O"] = "Bringt 1 Schrifttafelsplitter zu Kin\'weelay in das Basislager von Grom\'gol.",
    ["T"] = "Das Finstere Riff",
  },
  [630] = {
    ["D"] = "$N, ich setze meine ganze Hoffnung in Euch und jeden, den Ihr gewinnen könnt, um mich zu befreien. Der große Himmelsmähnenkönig Mukla hat mich gefangen genommen. Er befehligt die Bestien dieser Insel und sie halten mich mithilfe dieser Kette gegen meinen Willen hier fest. Wenn Ihr ihn töten und den Schlüssel für diese Fessel finden könnt, stehe ich tief in Eurer Schuld.$B$BIch habe ihn oft auf der südöstlichen Seite der Insel gehört, aber bitte seid vorsichtig: Man darf ihn nicht unterschätzen.",
    ["O"] = "Erschlagt König Mukla und kehrt mit dem Schlüssel zu Prinzessin Poobah zurück, um sie zu befreien.",
    ["T"] = "Flaschenpost",
  },
  [631] = {
    ["D"] = "Eine enorme Explosion donnerte durch die Hügel hinweg. \'s war furchtbar, sag ich Euch!$B$BGute Soldaten starben bei der Verteidigung des Thandolübergangs. Langzopf sagt, wir werden uns noch an den Dunkeleisenzwergen rächen.$B$BAber wir befinden uns in einer Welt voller Schmerz, und Verstärkung ist noch lange nicht in Sicht. Langzopf hat den alten Rostlocke als Späher zur östlichen Brücke geschickt, um herauszufinden, ob wir sie sichern können.$B$BDas ist aber schon Tage her. Und jetzt, wo Dun Modr belagert wird, haben wir nicht genug Leute, um nach ihm zu suchen.$B$BVielleicht könnt Ihr ihn finden...",
    ["O"] = "Rhag Garmason will, dass Ihr den alten Rostlocke findet.",
    ["T"] = "Der Thandolübergang",
  },
  [632] = {
    ["D"] = "Es sieht ganz so aus, als sei der arme alte Rostlocke in einen Hinterhalt der Dunkeleisenzwerge geraten. Ihr bemerkt ein Pergament neben seinen geballten Fäusten.$B$BErstattet Rhag Garmason Eure traurigen Neuigkeiten und zeigt ihm, was Ihr gefunden habt.",
    ["O"] = "Meldet Euch bei Rhag Garmason in Dun Modr im Sumpfland zurück und zeigt ihm das Pergament, das Ihr bei Rostlockes Leichnam gefunden habt.",
    ["T"] = "Der Thandolübergang",
  },
  [633] = {
    ["D"] = "Um Himmels willen, nein! Armer Rostlocke. Das ist wirklich eine fürchterliche Tragödie.$B$BSein Tod darf nicht vergebens sein. Das Pergament, das Ihr entdeckt habt, zeigt an, dass Kam Tiefenzorn, der hinter dem eigentlichen Angriff steckt, dafür gesorgt hat, dass ein Behälter mit Sprengstoff hinter dem Thandolübergang im Arathihochland versteckt wurde.$B$BWenn dieser Sprengstoff zur Brücke gelangt, wird unsere Hauptversorgungsverbindung in den Norden gekappt! Der Behälter muss zerstört werden, $N! Kehrt zu mir zurück, wenn Ihr Eure Mission erfüllt habt.$B$BIhr seid unsere einzige Hoffnung.",
    ["O"] = "Zerstört den Sprengstoffbehälter.",
    ["T"] = "Der Thandolübergang",
  },
  [634] = {
    ["D"] = "Ich bitte Euch noch um einen kleinen Gefallen, $C. Wir erwarten die Reserven von Eisenschmiede. Roggo hat jedoch von einem Lager der Allianz im Norden des Arathihochlands bei der Zuflucht erfahren.$B$BReist dorthin und macht Hauptmann Nials ausfindig. Fragt den guten Hauptmann, ob die Allianz einige Soldaten zu erübrigen bereit ist, die uns behilflich sein können, Dun Modr und den Thandolübergang zurückzuerobern.",
    ["O"] = "Macht Hauptmann Nials bei der Zuflucht im Arathihochland ausfindig.",
    ["T"] = "Hilfegesuch an die Allianz",
  },
  [636] = {
    ["D"] = "Dieses Buch erzählt die Geschichte einer Prinzessin der Elementare, Myzrael, und ihrer Gefangenschaft tief unterhalb des Arathihochlandes.$B$BEs erzählt auch von dem verborgenen Auftreten von Kristallen im Hochland. Diese Kristalle, die Splitter von Myzrael, sind Fenster zum Gefängnis der Prinzessin.$B$BWenn eine mutige Seele die Splitter von Myzrael fände, dann könnte $Ger:sie; vielleicht mit ihr sprechen...",
    ["O"] = "Findet die Splitter von Myzrael.",
    ["T"] = "Legenden der Erde <NYI>",
  },
  [637] = {
    ["D"] = "Der durchnässte und zerrissene Umschlag löst sich in Euren Händen auf und Euch bleiben nur einige Seiten eines Briefes, der mit viel Mühe geschrieben wurde.$B$BDer Brief ist adressiert an eine Frau Sara Balloo in Eisenschmiede und beginnt mit den Worten, \"Meine liebe Sara...\"",
    ["O"] = "Bringt Sully Balloos Brief zu Sara Balloo.",
    ["T"] = "Sully Balloos Brief",
  },
  [638] = {
    ["D"] = "Da Thoras Trollbann tot ist und Stromgarde in Schutt und Asche liegt, hat Vol\'jin Anweisungen gegeben, dass wir alles tun sollen, um das legendäre Schwert Trol\'kalar den menschlichen Händen zu entreißen, auf dass wir es in zukünftigen Kämpfen gegen die Trolle von Zul\'Gurub nutzen können.$B$BZengu, einer unserer erfahrenen Jäger, wurde nach Hammerfall im Nordosten des Arathihochlands entsandt, um seine Beschaffung zu leiten.",
    ["O"] = "Reist nach Hammerfall im Arathihochland und sprecht mit Zengu.",
    ["T"] = "Trollbann",
  },
  [639] = {
    ["D"] = "In den alten Zeiten kämpften Trolle gegen das Arathorianische Reich und unseren geschicktesten Feind, Lord Ignaeus von Strom, genannt Trollbann. Er ritt in die Schlacht und schwenkte dabei sein Schwert Trol\'kalar - was in ihrer alten Sprache Trolltöter heißt.$B$BAber jetzt sind die Söhne von Trollbann geschwächt, ihre Festung ist dem Verfall preisgegeben.$B$BDas Schwert wird von mehreren Siegelzeichen beschützt, die unter den Überlebenden von Stromgarde verteilt wurden. Wir müssen sie alle bekommen.$B$BDas Erste wurde von dem Syndikat mitgenommen, einer ihrer Männer in Stromgarde sollte es haben.",
    ["O"] = "Holt das Siegelzeichen von Strom für Zengu in Hammerfall.",
    ["T"] = "Siegelzeichen von Strom",
  },
  [640] = {
    ["D"] = "Thoras Trollbanns Grabmal wird von drei Siegeln beschützt - das erste davon habt Ihr dem Syndikat entwendet. Es wird allerdings schwieriger sein, an die anderen beiden heranzukommen, da sie sich bei der Miliz von Stromgarde befinden und diese sich gut verschanzt hat.$B$BDas Siegel von Thoradin wurde von Thoras\' Sohn, Galen, in fünf Teile aufgeteilt und unter den Verteidigern von Stromgarde verteilt. Holt die Stücke und bringt sie zu Tor\'gan, dem Trollschamanen, er kann sie wieder zum vollständigen Siegel binden.",
    ["O"] = "Holt die 5 Siegelfragmente von den Verteidigern in Stromgarde und bringt sie zu Tor\'gan in Hammerfall.",
    ["T"] = "Das zerbrochene Siegel",
  },
  [641] = {
    ["D"] = "Die Rekonstruktion ließ sich problemlos durchführen. Das Siegelzeichen kann nun benutzt werden, um das Siegel auf Trollbanns Grabmal aufzuschließen. Bringt es zu Zengu, er hat ohne Zweifel weitere Aufgaben, die Ihr erfüllen müsst.",
    ["O"] = "Bringt das wiederhergestellte Siegelzeichen von Thoradin zu Zengu in Hammerfall.",
    ["T"] = "Siegelzeichen des Thoradin",
  },
  [642] = {
    ["D"] = "Mein Name ist Myzrael. Ich bin eine Prinzessin der Erde und meine Häscher, die Riesen, halten mich tief unterhalb des Arathihochlandes fest. Diese Kristallsplitter sind meine einzige Möglichkeit, mit der oberen Welt zu sprechen.$B$BBitte helft mir! Kobolde der Trockenstoppel, die Verbündete der Riesen sind, haben einen Splitter wie diesen hier in ihrer Schlucht im Osten. Um die Gruppe zu aktivieren, müsst Ihr die Körner von Myzrael von den Kobolden holen und sie an der Gruppe anwenden.$B$BIch flehe Euch an, $N, helft mir!",
    ["O"] = "Sammelt 12 Körner von Myzrael und bringt sie zu den regenbogenfarbenen Splittern in der Schlucht der Trockenstoppel.",
    ["T"] = "Die gefangene Prinzessin",
  },
  [643] = {
    ["D"] = "Obwohl man kaum behaupten kann, dass sie Stromgarde kontrollieren, geschweige denn das gesamte Arathihochland, marschiert der menschliche Prinz trotzdem mit den Überbleibseln seiner Kavallerie durch das Land. Ich versteh ja nicht, warum er das tut, aber wer versteht schon, wie Menschen ticken, hm?$B$BWas auch immer seine Gründe sein mögen, er hat eines der Siegelzeichen Leutnant Heldenruf, dem Anführer seiner Kavallerie übergeben. Findet ihn und bringt mir sein Siegelzeichen zurück.",
    ["O"] = "Holt das Siegelzeichen von Arathor und bringt es Zengu in Hammerfall.",
    ["T"] = "Siegelzeichen von Arathor",
  },
  [644] = {
    ["D"] = "Das letzte Siegelzeichen, das benötigt wird, um das Siegel auf dem Grabmal von Thoras Trollbann zu öffnen, wird von seinem Sohn höchstpersönlich aufbewahrt. Er hält sich tief innerhalb Stromgardes auf, in einer Kapelle in den von der Miliz bewachten Distrikten. Prinz Galen wird gut bewacht und da er selbst ein ausgezeichneter Krieger ist, wird diese Aufgabe wahrscheinlich die schwierigste sein.",
    ["O"] = "Tötet Prinz Galen Trollbann und bringt das Siegelzeichen von Trollbann zu Zengu in Hammerfall.",
    ["T"] = "Siegelzeichen von Trollbann",
  },
  [645] = {
    ["D"] = "Was ist das? Die vier Siegelzeichen haben sich zu einem Siegelzeichen zusammengefügt. Ohne Zweifel ist dieses in der Lage, die Schutzzeichen zu zerbrechen, die sich auf Trollbanns Grabmal befinden.$B$BTrollbanns sterbliche Überreste sind im Sanktum hinter der Kapelle in Stromgarde zur Ruhe gebettet worden. Holt Trol\'kalar.",
    ["O"] = "Holt Trol\'kalar aus Trollbanns Grabmal in Stromgarde.",
    ["T"] = "Trol\'kalar",
  },
  [647] = {
    ["D"] = "Was hab ich gestern gesoffen! Langzopf würde mich sofort köpfen, wenn er wüsste, dass ich während meiner Wache eingepennt bin.$B$BMein Schädel brummt vielleicht. Fühlt sich an, als wäre eine ganze Brigade von Belagerungsmaschinen drübergerollt, dieses Gerumpel.$B$BOh weh, so spät schon! Hab Braumeister Bilger in Süderstade versprochen, dass ich meine Schulden mit ein wenig Fusel bei ihm bezahlen würde.$B$BUnd ich hab nur noch 15 Minuten! Könntet Ihr ihm diese Lieferung nicht bringen, bitte? Und beeilt Euch!",
    ["O"] = "Bringt MacKreels Fusel zu Braumeister Bilger nach Süderstade.",
    ["T"] = "MacKreels Fusel",
  },
  [648] = {
    ["D"] = "Bringt OOX-17/TN in Sicherheit und meldet Euch dann bei Glotz Widrikus in Beutebucht.",
    ["O"] = "Bringt OOX-17/TN in Sicherheit und meldet Euch dann bei Glotz Widrikus in Beutebucht.",
    ["T"] = "Rettet OOX-17/TN!",
  },
  [649] = {
    ["D"] = "Ich habe eine kleine Bergungsaktion für Euch, und falls Ihr ein Liebhaber von feinem Wein seid, so wie ich, dann werdet Ihr diese Aufgabe lieben. Wein!$B$BDas ist ein Getränk, Schafskopf.$B$BEin verlassener Außenposten der Horde verfügt über ein Lager mit Hinterländer Honigwein aus der Zeit, als sie noch im Geschäft waren. Solch feinen Wein bekommt Ihr sonst nirgendwo! Einer von Maltons Strohmännern weiß Bescheid, also begebt Ihr Euch dorthin und bringt mir eine Kiste verpackten Wein und zwei für meine eigene Sammlung! Sprecht mit Malton, er wird Euch die Einzelheiten mitteilen.",
    ["O"] = "Sprecht mit Malton Droffers in Orgrimmar.",
    ["T"] = "Weinbergung",
  },
  [650] = {
    ["D"] = "Der Elf, mit dem Ihr sprechen müsst, ist Gilveradin Sonnenjäger. Er erkennt jeden Wein, mit Jahrgang und allem, allein am Geruch! Er kann den Wein verpacken, den wir benötigen, daher müsst Ihr mit ihm sprechen. Sechs Flaschen müssen verpackt werden, zwei für meinen Vater, und so viele wie er selbst will... und glaubt mir, er wird welche wollen. Wenn Ihr den Wein und auch die zwei Flaschen für meinen Vater verpackt habt, dann kommt hierher zurück!$B$BGilveradins Lager befindet sich in der Nähe einer Hochelfenhütte im Hinterland, und diese Hochelfen führen nichts Gutes im Schilde. Glaubt mir.",
    ["O"] = "Sprecht mit Gilveradin Sonnenjäger; sein Lager befindet sich im Hinterland.",
    ["T"] = "Weinbergung",
  },
  [651] = {
    ["D"] = "Vier Steine der Bindung sind in ganz Arathi verteilt. Diese Steine halten mich unter der Erde fest.$B$BUm sie zu öffnen, müsst Ihr den stärksten öffnen: den Stein der Inneren Bindung. Um das tun zu können, müsst Ihr Schlüssel von den drei Übrigen sammeln.$B$BDiese Steine werden von Wächtern bewacht, aber wenn Ihr es geschickt anstellt oder Glück habt, findet Ihr vielleicht einen Weg, sie auszuschalten.",
    ["O"] = "Holt den brennenden Schlüssel, den schäumenden Schlüssel und den donnernden Schlüssel vom Stein der Westlichen Bindung, dem Stein der Östlichen Bindung und dem Stein der Äußeren Bindung.$B$BBringt sie zum Stein der Inneren Bindung.",
    ["T"] = "Steine der Bindung",
  },
  [652] = {
    ["D"] = "Das Öffnen der Steine der Bindung hat den Großteil meiner Fesseln gelöst, aber ein Schloss hält mich immer noch gefangen. Es ist der Hauptstein, den Ihr in diesem Kreis von Steinen sehen könnt. Und der Schlüssel wird von einem meiner Häscher bewahrt, dem Steinbehüter Fozruk.$B$BIhr müsst diesen Schlüssel, den die Steinbehüter die Rute der Ordnung nennen, von Fozruk in Besitz nehmen und mit ihm den Hauptstein öffnen.$B$B$N, Ihr habt schon so viel für mich getan. Nur noch eine kleine Hilfe... und meine Dankbarkeit wird die Erde in ihren Grundfesten erschüttern!",
    ["O"] = "Findet und tötet Fozruk. Bringt die Rute der Ordnung zum Hauptstein im Arathihochland.",
    ["T"] = "Den Hauptstein zerbrechen",
  },
  [653] = {
    ["D"] = "<Myzrael spricht.>$B$BFrei! ICH BIN FREI! Ich bin frei und kann meine Kräfte sammeln, verborgen vor denen, die mich gefangen nahmen. Denn wenn ich mich ihnen jetzt stellen müsste, würden sie mich sicherlich überwältigen und erneut einkerkern.$B$BAber bald werde ich mich den Riesen stellen und sie werden es bitter bereuen, mich als Gefangenen gehalten zu haben!$B$BIhr seid ein edelmütiger Verbündeter, $N. Ich werde Eure Hilfe bald wieder benötigen. Wenn ich bereit bin, müsst Ihr mich an die Oberfläche beschwören.$B$BSprecht mit Gerrig Knochengriff. Er befindet sich in Eisenschmiede in einem Geschäft im Düsteren Viertel. Er weiß, wie ich beschworen werden kann.",
    ["O"] = "Sprecht mit Gerrig Knochengriff im Düsteren Viertel von Eisenschmiede.",
    ["T"] = "Myzraels Verbündete",
  },
  [654] = {
    ["D"] = "Sammelt ungetestete Proben von 8 Basilisken, 8 Hyänen und 8 Skorpionen. Bringt das Testset zurück nach Gadgetzan zu Chefingenieur Bilgenritzel, bevor die Kraftquelle erschöpft ist.",
    ["O"] = "Sammelt ungetestete Proben von 8 Basilisken, 8 Hyänen und 8 Skorpionen. Bringt das Testset zurück nach Gadgetzan zu Chefingenieur Bilgenritzel, bevor die Kraftquelle erschöpft ist.",
    ["T"] = "Tanaris-Feldproben",
  },
  [655] = {
    ["D"] = "Ich erinnere mich, als wär es gestern gewesen... So viele Jahre ohne Hoffnung, mit Holz und Stahl, die mich an diesen fürchterlichen Ort banden. Und dann endlich, endlich! Da war der Kriegshäuptling. Schicksalshammer in seiner Mitternachtsrüstung, sein Kriegshammer hoch erhoben.$B$BEs war... zu schrecklich... Dass der Schlag eines Feiglings unseren größten Helden niederschlagen konnte. Er kam von hinten angestürmt auf seinem Pferd und dann rammte er seine Lanze einfach durch ihn hindurch... Ich fiel auf die Knie. Welche Hoffnung gab es noch? Welche Hoffnung gibt es noch...",
    ["O"] = "Sprecht mit Tor\'gan in Hammerfall.",
    ["T"] = "Hammerfall",
  },
  [657] = {
    ["D"] = "<Quae beginnt, den Ordner durchzusehen.>$B$BDas IST interessant. Es scheint, als habe Apotheker Jorell an einigen tödlichen Experimenten hier in Go\'Shek gearbeitet. Bin nicht sicher, was das mit dem Hügelland zu tun hat, aber Phin würde es nicht gefallen, wenn wir das einfach so laufen ließen.$B$BWährend Ihr fort wart, hatten wir ein Zusammentreffen mit ein paar Orcs und ich bin nicht in der geeigneten Verfassung, um Kinelory zu helfen. Wenn Ihr es vermögt... Sie wird Schutz gut gebrauchen können, wenn sie zum Hof geht, um zu stehlen, was Jorell noch nicht nach Tarrens Mühle geschickt hat.",
    ["O"] = "Sprecht mit Kinelory in der Nähe von Go\'Sheks Hof im Arathihochland.",
    ["T"] = "Hinweise auf eine neue Seuche?",
  },
  [658] = {
    ["D"] = "Phin hat Euch also geschickt, um nach uns zu sehen? Wundert mich nicht - er macht sich zu viele Sorgen.$B$BWir beobachten den Hof seit Tagen, und Kin sagt, es sei noch gar nicht so lange her, dass sie einen Kurier aus dem kleinen Haus da südlich hat kommen sehen.$B$BSie erzählt viel, aber ich kann ihrem Instinkt immer trauen, wenn wir uns auf gefährlichem Terrain befinden.$B$BIhr seid nicht zufällig ein geschickter Fährtenleser, oder? Wir glauben, dass der Kurier Notizen nach Tarrens Mühle bringen könnte. Wenn wir diese Dokumente in die Hände bekommen könnten, könnten wir vielleicht Näheres über die Vorgänge im Hügelland erfahren.",
    ["O"] = "Findet den Kurier der Verlassenen und bringt den versiegelten Ordner zu Quae in der Nähe von Go\'Sheks Hof.",
    ["T"] = "Hinweise auf eine neue Seuche?",
  },
  [659] = {
    ["D"] = "Seid mir gegrüßt, $N. Ich bin der Anführer einer Abenteurergruppe, die die seltsamen Vorgänge in diesem Gebiet untersucht.$B$BMein Cousin im Hügelland musste letztens seinen Hund Stanley einschläfern lassen, weil das Tier seltsamerweise auf eine immense Größe heranwuchs und jeden bösartig angegriffen hat, der ihm näher kam.$B$BIch glaube, dass die Untoten darin verwickelt sind, und wir suchen nach Anzeichen, die das bestätigen.$B$BZwei meiner Kameraden, Quae und Kinelory, haben sich zur Go\'Sheks Hof im Arathihochland aufgemacht und haben seit Tagen nichts von sich hören lassen. Könntet Ihr sie für mich finden? Ich mache mir langsam Sorgen.",
    ["O"] = "Findet Quae und Kinelory in der Nähe von Go\'Sheks Hof im Arathihochland.",
    ["T"] = "Hinweise auf eine neue Seuche?",
  },
  [660] = {
    ["D"] = "Nun gut, $N. Bereit, wenn Ihr es seid.$B$BSollen wir?",
    ["O"] = "Beschützt Kinelory, bis sie zu Quae zurückkehrt.",
    ["T"] = "Hinweise auf eine neue Seuche?",
  },
  [661] = {
    ["D"] = "Kinelory sah nach einer Nacht in der Taverne schon schlechter aus als jetzt. Ihr habt sie offensichtlich sehr gut beschützt.$B$BDiese Gegenstände, die sie von der Farm mitgebracht hat, sollten Phin zumindest einen Anhaltspunkt davon geben, was in Arathi so vor sich geht. Wir werden hier noch ein Weilchen bleiben, nur um sicherzugehen, dass nach Eurem Angriff kein Gegenschlag kommt. Wärt Ihr bereit, nach Süderstade zurückzukehren und mit Phin zu sprechen?",
    ["O"] = "Findet Phin Odelic in Süderstade im Vorgebirge des Hügellands.",
    ["T"] = "Hinweise auf eine neue Seuche?",
  },
  [662] = {
    ["D"] = "O\'Breen führte die Schiffe in die Bucht. Wir waren ziemlich sicher, dass der Schatz dort zu finden sei. Die Ambassadeur konnte noch sicher andocken, aber als die Tolle Minna und die Silbergeist näher kamen, senkte sich der Meeresspiegel plötzlich ab. So was Seltsames hab ich mein Lebtag nich\' gesehn!$B$BDiese beiden Schiffe sind auf irgendetwas aufgelaufen... eine Art Tempel im Riff. Sie sind irre schnell gesunken, eine echte Tragödie.$B$BWir müssen ihre Logbücher und auch ihre Karten wiederbekommen, bevor wir hier wieder weg sind. Wir dürfen diese Informationen nicht einfach für neugierige Augen zurücklassen.",
    ["O"] = "Der erste Maat Nilzlix will, dass Ihr die Karten und Logbücher von der TOLLEN MINNA und der SILBERGEIST zurückholt.",
    ["T"] = "Tiefsee-Bergung",
  },
  [663] = {
    ["D"] = "Lolo sieht was Winziges, es ist $Gein:eine:r; $R! Immerzu Ausschau halten, ja, ja, das tut Lolo!$B$BWillkommen in der Faldirbucht. Kapitän O\'Breen hat gesagt, dass wir nur für ein paar Stunden hier wären. Grad lang genug, um den Schatz einzusammeln und zurück nach Beutebucht zu segeln.$B$BAber Lolo denkt, dass wir noch sehr lange hier bleiben werden. Wir haben die anderen beiden Schiffe von unserer Formation verloren. Die arme Silbergeist und die arme Tolle Minna. Sie ruhen jetzt auf dem Grund des Meeres!$B$BLolo schlägt vor, dass Ihr mit Kapitän O\'Breen sprecht, wenn Ihr länger hier bleiben wollt.",
    ["O"] = "Sprecht mit Kapitän O\'Breen in der Faldirbucht im Arathihochland.",
    ["T"] = "Land in Sicht!",
  },
  [664] = {
    ["D"] = "Grr, \'s ist ein elendes Leben. Ich sollte in meinem nassen Grab liegen und nicht hier am Feuer sitzen und frische Luft atmen.$B$BDie Crew hat ihr Bestes gegeben um meine wunderschöne Tolle Minna zu retten. Gesegnet seien die Jungs, gesegnet ihre Herzen.$B$BO\'Breen hat es sicher ans Ufer geschafft, aber wir hatten nicht so viel Glück. Das Meer sank einfach auf uns herab, ich schwöre es!$B$BIn der einen Sekunde lagen wir auf dem Wasser und in der nächsten stürmte das Wasser auf uns ein. Und diese Bestien! Haben alle Überlebenden getötet. Elender Nagaabschaum. Mir gingen die Lichter aus und ich kam hier oben wieder zu mir, gerettet.$B$BIch will diese Biester tot sehen.",
    ["O"] = "Kapitän Stahlmagen in der Faldirbucht will, dass Ihr 10 Räuber der Säbelflossen und 3 Zauberinnen der Säbelflossen tötet.",
    ["T"] = "Ertrunkene Trauer",
  },
  [665] = {
    ["D"] = "Da wir jetzt richtige Schwarzmeerräuber sind, ist es nun unsere Aufgabe, Herrn O\'Breen bei der Suche nach dem verlorenen Elfenschatz behilflich zu sein.$B$BEs ist fast unmöglich, die Edelsteine ohne Hilfe im dunklen Meer zu finden. Der Doktor hat eine Schutzbrille konstruiert, die dabei hilft. Er muss die Schutzbrille mit der Energie aufladen, die von dem verzauberten Stein in der Höhle auf dem Hügel dort ausgeht.$B$BAber die Höhle ist verflucht! Sobald wir uns ihr nähern, werden wir überfallen. Wenn Ihr mich verteidigt, kann ich die Energie vom Stein in die Schutzbrille leiten.",
    ["O"] = "Begleitet Professor Prasseltrop zur Höhle und zurück.",
    ["T"] = "Versunkener Schatz",
  },
  [666] = {
    ["D"] = "Der Schatz befand sich so lange auf dem Meeresboden, dass die Edelsteine von einer dicken Kalkschicht umgeben sind. Aber die Macht, die in dieser Schutzbrille gefangen ist, wird Euch ermöglichen, sie leicht zu finden.$B$BEin wenig gnomischer Einfallsreichtum ist schon was Schönes!$B$BAlso nehmt Euch die Edelsteinjagdschutzbrille, $N, und versucht, ein wenig vom verlorenen Schatz für Kapitän O\'Breen einzusammeln.$B$BIch würde ja selbst hinuntertauchen, aber... ähem... ich, nun ich muss mich um äußerst wichtige wissenschaftliche Dinge kümmern, oben auf dem sicheren, trockenen Land... ähem, ja.",
    ["O"] = "Doktor Drechselknacks in der Faldirbucht will, dass Ihr 10 Elfenedelsteine einsammelt und die Edelsteinjagdschutzbrille zurückgebt, sobald Ihr fertig seid.",
    ["T"] = "Versunkener Schatz",
  },
  [667] = {
    ["D"] = "Das ist übel. Es scheint, als ob die Säbelflossen sich zum Angriff sammeln.$B$BNaga sind bekannt für ihre Ruchlosigkeit im Kampf. Sie werden so lange angreifen, bis ich tot bin.$B$BIch schätze mein eigenes Leben nicht höher als das meiner Crew, aber Ihr müsst helfen, mich zu verteidigen. Die Crew ist nicht in der Lage, ohne meine Kenntnisse des Meeres nach Beutebucht zurückzufinden.$B$BDie Naga werden vom Meer her kommen. Ihr müsst mit mir hier oben bleiben, um den Angriff abzuwehren. Bemannt die Kanone und treibt sie zurück.$B$BSeid Ihr bereit, $N?",
    ["O"] = "Beschützt Wank O\'Breen während des Angriffs.",
    ["T"] = "Tod von unten",
  },
  [668] = {
    ["D"] = "Wir sollten Kapitän O\'Breen nicht warten lassen. Er wird diese Edelsteine sofort sehen wollen. Darum sind wir ja schließlich hier!$B$BUnd nach ein paar Wochen Zusammenleben mit diesen Piraten sind der Professor und ich zum Schluss gekommen, dass es ganz und gar nicht gut für unsere Gesundheit ist, wenn sie denken, dass wir uns ihre Schätze einverleiben wollen.$B$BHier, $N, bringt das zu O\'Breen.",
    ["O"] = "Bringt die Elfenedelsteine zu Kapitän O\'Breen in die Faldirbucht im Arathihochland.",
    ["T"] = "Versunkener Schatz",
  },
  [669] = {
    ["D"] = "Flottenmeister Seehorn will über unseren Fund sofort informiert werden.$B$BAber, wie Ihr selbst sehen könnt, sind wir nicht unbedingt in der Lage, von hier wegzukommen. Es ist nicht nur der zu niedrige Wasserstand, sondern auch diese verdammten Viecher, die wir gestört haben, und die jetzt jede unserer Bewegungen aufs Genaueste beobachten.$B$BIhr könnt den Schwarzmeerräubern einen sehr großen Dienst erweisen, wenn Ihr Flottenmeister Seehorn in Beutebucht davon in Kenntnis setzen könntet, dass wir den Schatz gefunden haben und dabei sind, weitere Steine zu bergen. Bringt ihm diese Probe zum Beweis.",
    ["O"] = "Bringt die Probe zu Flottenmeister Seehorn nach Beutebucht im Schlingendorntal.",
    ["T"] = "Versunkener Schatz",
  },
  [670] = {
    ["D"] = "Sagt, $N, Ihr seht aus wie jemand, der einem Abenteuer nicht abgeneigt ist. Meine Flotte liegt hier in Beutebucht fest, bis wir unsere Vorräte auffüllen können.$B$BHier und auch außerhalb braut sich so einiges zusammen und ich muss ein paar äußerst geheime Nachrichten an Wank O\'Breen übermitteln, der vor der Arathiküste festsitzt.$B$BKann ich auf Euch vertrauen, dass Ihr diese Nachricht sicher und unberührt überbringt?",
    ["O"] = "Bringt Seehorns Brief zu Kapitän O\'Breen in die Faldirbucht im Arathihochland.",
    ["T"] = "Versunkener Schatz",
  },
  [671] = {
    ["D"] = "Ich fühlte etwas Fremdes in der Luft... ein Gefühl, das mein Blut köcheln lässt. Ich hatte auch schon einen Verdacht. Ich bin ausgezogen, um die Quelle meiner Unruhe aufzuspüren, und auf dem Nordhof im Westen fand ich restliche Spuren einer Dämonenbeschwörung. Als ich näher kam, begann mein Blut zu sieden und die Wut in mir wuchs.$B$BAuf dem Boden entdeckte ich ein Blutsteinamulett, das bei Dämonenbeschwörungen als Hilfe benutzt wird. Das Amulett, das ich fand, war jedoch entleert. Ich benötige ein weiteres, um es zu untersuchen, aber ich wage nicht, zum Anwesen zurückzukehren. Könnt Ihr mir ein paar bringen?",
    ["O"] = "Holt 10 Blutsteinamulette für Tor\'gan in Hammerfall.",
    ["T"] = "Elende Magien",
  },
  [672] = {
    ["D"] = "Wir haben versucht, es ihm gemütlich zu machen, ihn aufzumuntern, haben ihm Geschichten erzählt von unserer mächtigen Stadt Orgrimmar in Kalimdor, aber nichts, gar nichts hat ihn berührt.$B$BGeschichten von unserem jungen Kriegshäuptling, Thrall, der die Rüstung und den Hammer von Orgrim trägt... aber vielleicht... Vielleicht! Eine kleine Demonstration von unserer neu gefundenen Schamanen-Magie könnte ihm neue Hoffnung geben. Einen Versuch ist es wert!$B$BEin kleines Objekt könnten wir für ihn machen, ja... Bringt mir ein paar Raptoraugen und ich werde sehen, was ich tun kann.",
    ["O"] = "Bringt 10 Hochlandraptorenaugen von Hochlandschreitern und Hochlandhauern zu Tor\'gan in Hammerfall.",
    ["T"] = "Mut machen",
  },
  [673] = {
    ["D"] = "Ich habe keinerlei Zweifel, dass ein mächtiger Hexenmeister sich innerhalb der Mauern Stromgardes aufhält. Von Zeit zu Zeit hat dieser Hexenmeister mithilfe eines arkanen magischen Relikts abscheuliche Dämonen in großer Zahl herbeibeschworen. Wir müssen ihm die Quelle seiner Energie nehmen.$B$BSucht den Hexenmeister und tötet ihn. Bringt mir alle magischen Objekte, die Ihr bei ihm finden könnt, und ich werde die geeigneten Schritte unternehmen, sie zu zerstören, damit ihre Macht nicht mehr dazu benutzt wird, dieses Land weiterhin zu besudeln.",
    ["O"] = "Bergt Marez Schattenkuttes Blutsteinkugel und bringt sie zu Tor\'gan in Hammerfall.",
    ["T"] = "Elende Magien",
  },
  [674] = {
    ["D"] = "Hier, übergebt dieses Amulett Gor\'mul und beobachtet, ob die Kraft der Raptoren das Feuer des Kriegers, das einst in seinen Adern brannte, erneut erwecken kann.",
    ["O"] = "Gebt das Raptorkrallenamulett an Gor\'mul in Hammerfall im Arathihochland.",
    ["T"] = "Mut machen",
  },
  [675] = {
    ["D"] = "<Gor\'mul wirft das Amulett zu Boden.>$B$BTor\'gan hat Euch geschickt, oder? Pah! Warum er mir gegenüber Gnade empfindet, geht über meinen Verstand. Gnade wurde mir verweigert, als es mir nicht vergönnt war, mit meinem Kriegshäuptling im Kampf zu sterben. Das Schicksal hat mir einen harten Schlag versetzt...",
    ["O"] = "Sprecht mit Tor\'gan in Hammerfall im Arathihochland.",
    ["T"] = "Mut machen",
  },
  [676] = {
    ["D"] = "Schwächlinge... alle miteinander. Ihr da, $C, was schaut Ihr so dämlich? Denkt wohl, Ihr seid was Besonderes, was? Wollt Ihr\'s nicht mal beweisen?$B$BDer Außenposten von Hammerfall in Arathi wird angegriffen. Wenn wir der Besatzung nicht bald Hilfe schicken, steht ihr Ableben unmittelbar bevor.$B$BAber bevor Ihr jetzt einfach losrennt, möchtet Ihr vielleicht Eure Fähigkeiten erst testen. Nordöstlich von Stromgarde in Arathi befindet sich ein Ogerhort, der von den Felsfaustogern bewohnt wird. Wenn Ihr die Oger und Vollstrecker dort in den Griff bekommt, erstattet Drum Teufel in Hammerfall Bericht.",
    ["O"] = "Tötet 8 Oger der Felsfäuste und 10 Vollstrecker der Felsfäuste und sucht dann Drum Teufel im Außenposten von Hammerfall im Arathihochland auf.",
    ["T"] = "Der Hammer kann fallen",
  },
  [677] = {
    ["D"] = "Die Trolle der Bleichborken und die Felsfaustoger von Arathi arbeiten zusammen daran, uns von diesem Land zu vertreiben, aber unser Schicksal liegt weder in ihren noch in irgendwessen anderen Händen. Wir werden sie erledigen und ihnen zeigen, dass dies auch unsere Heimat ist. $B$BDie Trolle haben sich von uns abgewandt, die Oger würden uns als Nahrung und Bettvorleger benutzen, wenn wir es zuließen - das darf nicht sein.$B$BWir werden unseren Angriff auf die Trolle der Bleichborken südlich von hier beginnen. Erschlagt sie in Scharen und kehrt erst zurück, wenn ihre Lager dick mit Blut bedeckt sind.",
    ["O"] = "Erschlagt 10 Axtwerfer der Bleichborken, 10 Kopfjäger und 8 Hexendoktoren und kehrt zu Drum Teufel im Außenposten von Hammerfall zurück.",
    ["T"] = "Zu den Waffen",
  },
  [678] = {
    ["D"] = "Eure Kraft ist offensichtlich, $N. Dann zeigt mal, wie Ihr Euch mit echten Feinden schlagt.$B$BDie Felsfaustoger haben einen Unterschlupf südwestlich von hier, Richtung der Zwergenbrücke, und, wenn ich mich nicht täusche, werden die Raufer und Magi dort eine etwas größere Herausforderung darstellen - für uns waren sie das bislang zumindest.$B$BErschlagt so viele wie Ihr irgend könnt und kommt persönlich zu mir, wenn Ihr erfolgreich seid, oder erscheint mir als Geist, wenn Ihr versagt. Ich werde Eure Beharrlichkeit in der Schlacht in jedem Falle preisen.",
    ["O"] = "Tötet 10 Schläger der Felsfäuste und 4 Magi der Felsfäuste und sucht dann Drum Teufel im Außenposten von Hammerfall auf.",
    ["T"] = "Zu den Waffen",
  },
  [679] = {
    ["D"] = "Der Kopf der Bestie kann abgetrennt werden... mit einem mächtigen Schlag, $N. Ihr habt alle Zeichen dafür gezeigt, dass Ihr in der Lage seid, uns mit diesem letzten mächtigen Schlag zu helfen. Die Anführer dieser Angriffe haben ihren Unterschlupf innerhalb der Ruinen von Stromgarde gewählt, einem grellen Symbol menschlicher Schwachheit.$B$BFindet und erschlagt diese Lords der Felsfäuste und ihre speichelleckenden Schamanen, die versuchen, uns aus diesem Land zu vertreiben.",
    ["O"] = "Tötet 7 Schamanen der Felsfäuste und 3 Lords der Felsfäuste und kehrt zu Drum Teufel im Außenposten von Hammerfall im Arathihochland zurück.",
    ["T"] = "Zu den Waffen",
  },
  [680] = {
    ["D"] = "Ihr seid meinem Gemahl ein treuer Diener, $C, aber er weiß nicht, warum Hammerfall angegriffen wird.$B$BOr\'Kalar, Mugh\'thols Helfer in Stromgarde hasst meinen Gemahl aufs Äußerste, und er wünscht sich nichts mehr, als ihn tot zu sehen, da mein Gemahl ihn vor Jahren einmal besiegte.$B$BDie Vorzeichen verrieten mir, dass Or\'Kalar eine Falle für meinen Gemahl ausgelegt hat. Aus diesem Grund habe ich Hinweise auf den Aufenthaltsort des Ogers vor ihm versteckt.$B$BMein Gemahl wird Hals über Kopf losschlagen, sobald er vom Aufenthaltsort des Ogers erfährt, und ich wollte Eure Hilfe bei der Tötung der Kreatur erbitten.",
    ["O"] = "Erschlagt Or\'Kalar und bringt seinen Kopf zu Korin Teufel in den Außenposten von Hammerfall.",
    ["T"] = "Die echte Bedrohung",
  },
  [681] = {
    ["D"] = "Wir kämpfen einen Kampf um Stromgarde, und wir verlieren ihn. Die Oger, das Syndikat und wir haben unsere einst große Stadt geteilt und jede Fraktion bekämpft die andere bis zu einem blutigen Unentschieden.$B$BAber unsere Streitkräfte schwinden, während unsere Feinde immer stärker werden.$B$BWir brauchen Hilfe aus der Stadt Süderstade, aber Vorräte sind nur schwer zu bekommen, solange das Syndikat das Gebiet zwischen uns kontrolliert.$B$BBegebt Euch zum Syndikatsstützpunkt auf dem Nordhof und tötet sie alle. Dann wird der Druck auf unsere Versorgungszüge ein wenig nachlassen und ein größerer Teil wird durchkommen.",
    ["O"] = "Tötet 10 Straßenräuber des Syndikats und 6 Söldner des Syndikats.$B$BKehrt zu Hauptmann Nials bei der Zuflucht zurück.",
    ["T"] = "Nordhof",
  },
  [682] = {
    ["D"] = "Das Syndikat in Stromgarde bekämpft das Kontingent unserer Miliz dort. Um diesen Pöbel dazu zu bringen, sich mit unseren disziplinierteren Streitkräften zu schlagen, haben die Syndikatsanführer ein Kopfgeld auf unsere Soldaten ausgesetzt und belohnen jedes Abzeichen von Stromgarde, das toten Milizleuten abgenommen wurde, mit Gold.$B$BWir wollen diese Abzeichen zurück - wir dürfen es diesen Halsabschneidern nicht erlauben, Trophäen von unseren mutigen Gefallenen zu rauben!$B$BBegebt Euch in das vom Syndikat kontrollierte Gebiet von Stromgarde und jagt ihnen unsere Abzeichen ab.",
    ["O"] = "Bringt 7 Abzeichen von Stromgarde zu Hauptmann Nials bei der Zuflucht.",
    ["T"] = "Abzeichen von Stromgarde",
  },
  [683] = {
    ["D"] = "$N, ich habe nur eine kleine Bitte an Euch.$B$BBringt diese Mitteilung, die ich vorbereitet habe, zu unserem großen Monarchen von Eisenschmiede, König Magni Bronzebart. Es ist fast lächerlich, diese kleine Aufgabe von Euch zu verlangen, von jemandem, der für die Sache der Allianz bislang eine so große Unterstützung war.$B$BAber in meinem von Trauer übermannten Zustand vor den König zu treten, wäre meinem Anliegen nicht im Geringsten dienlich. Das versteht Ihr sicher.",
    ["O"] = "Bringt Sara Balloos Notiz zu König Magni Bronzebart von Eisenschmiede.",
    ["T"] = "Sara Balloos Hilfegesuch",
  },
  [684] = {
    ["D"] = "Es wurde ein Kopfgeld auf Marez Schattenkutte ausgesetzt, einer hochrangigen Beraterin und Agentin des Syndikats. Sie wurde zuletzt in dem vom Syndikat kontrollierten Gebiet von Stromgarde gesehen.$B$BDas Kopfgeld kann von Hauptmann Nials abgeholt werden.$B$BIch warne Euch: Marez ist eine geschickte und gerissene Hexenmeisterin und bekannt dafür, dass sie sich mit Dämonen abgibt. Bei ihrer Verfolgung ist äußerste Vorsicht geboten.",
    ["O"] = "Bringt Marez\' Kopf zu Hauptmann Nials bei der Zuflucht.",
    ["T"] = "GESUCHT: Marez Schattenkutte",
  },
  [685] = {
    ["D"] = "Die Miliz von Stromgarde hat Kopfgelder auf Lord Falkenstein und seine Leibwache Otto ausgesetzt. Falkenstein ist für die Bemühungen des Syndikats im Arathihochland verantwortlich und sein Tod wäre eine erhebliche Störung dieser Bemühungen.$B$BSeine Leibwache Otto ist zwar kein strategisch wichtiges Ziel, aber er ist ein starker Gegner und hat Dutzende unserer Verteidiger auf dem Gewissen.$B$BIhre Kopfgelder können bei Hauptmann Nials abgeholt werden.",
    ["O"] = "Bringt Ottos und Falkensteins Köpfe zu Hauptmann Nials bei der Zuflucht.",
    ["T"] = "GESUCHT: Falkenstein und Otto",
  },
  [686] = {
    ["D"] = "Ich erwarte nicht, dass Zwerge wie die gute Frau Balloo es verstehen, warum so ein Krieg für das größere Wohl unseres Volkes ertragen werden muss. Der Verlust ihres Gemahls und der Verlust jedes einzelnen Zwerges, der im Namen der Allianz kämpft, lastet schwer auf meiner Seele.$B$BAuch wenn ich nicht in der Lage bin, ihren Gemahl aus dem Wirbelnden Nether zurückzuholen, so kann ich ihm doch meine Ehrerbietung erweisen. $B$BIch werde einen Gedenkstein für Sully Balloo bei Großsteinmetz Marmorstein in Auftrag geben.$B$BBenachrichtigt ihn sofort, $C. Ihr werdet die Durchführung meines Auftrags beaufsichtigen.",
    ["O"] = "König Magni Bronzebart will, dass Ihr mit Großsteinmetz Marmorstein sprecht.",
    ["T"] = "Ehrerbietung von einem König",
  },
  [687] = {
    ["D"] = "Theldurin der Verirrte war einst ein Mitglied unseres Ordens, dem Schattenhammer. Aber er wurde verrückt und hat unsere Lehren verdammt. Bevor er uns davonlief, hat er die Rolle von Myzrael gestohlen! Wir konnten ihn nicht einfangen, aber Gerüchte besagen, dass er sich im Ödland versteckt, sabbelnd und wahnsinnig.$B$BIhr müsst die Rolle finden, denn nur so könnt Ihr die Fürstin zu uns zurückbringen. Und dafür müsst Ihr Theldurin finden.",
    ["O"] = "Findet Theldurin den Verirrten.",
    ["T"] = "Theldurin der Verirrte",
  },
  [688] = {
    ["D"] = "<Myzrael spricht.>$B$BFrei! ICH BIN FREI! Ich bin frei und kann meine Kräfte sammeln, verborgen vor denen, die mich gefangen nahmen. Denn wenn ich mich ihnen jetzt stellen müsste, würden sie mich sicherlich überwältigen und erneut einsperren.$B$BAber bald werde ich mich den Riesen stellen und sie werden es bitter bereuen, mich als Gefangene gehalten zu haben!$B$BIhr seid ein edelmütiger Verbündeter, $N. Ich werde Eure Hilfe bald wieder benötigen. Wenn ich bereit bin, werdet Ihr mich an die Oberfläche beschwören müssen.$B$BSprecht mit Zaruk in Hammerfall. Er weiß, wie ich beschworen werden kann.",
    ["O"] = "Sprecht mit Zaruk in Hammerfall im Arathihochland.",
    ["T"] = "Myzraels Verbündete",
  },
  [689] = {
    ["D"] = "Unser guter König will also, dass wir einen Gedenkstein zu Ehren eines Soldaten mit Namen Sully Balloo errichten? Und Ihr sagt, dass der arme Kerl bei der Tragödie am Thandolübergang gestorben sei?$B$BBah, wird denn jemals Frieden auf Azeroth herrschen?$B$BWie dem auch sei, ein Auftrag vom König hat die allerhöchste Priorität. Und um ein wahrhaft episches und bleibendes Werk zu schaffen, will ich mit dem besten Stein arbeiten, der verfügbar ist. Begebt Euch nach Darrohügel im Hügelland und besorgt mir etwas Alteracgranit.",
    ["O"] = "Großsteinmetz Marmorstein aus Eisenschmiede will 5 Stücke Alteracgranit.",
    ["T"] = "Ehrerbietung von einem König",
  },
  [690] = {
    ["D"] = "Seid gegrüßt, $N. Ihr seht aus wie ein Abenteurer und ich habe eine Aufgabe, die erledigt werden muss.$B$BErzmagier Trelane, mein früherer Meister, ist vor nicht allzu langer Zeit aus Stromgarde im Arathihochland geflohen und er hat mich gebeten, bei der Wiederbeschaffung einiger Gegenstände, die er zurückließ, behilflich zu sein.$B$BDie Stadt wird belagert und er hat versucht, den Turm von Arathor zu verteidigen, aber ohne Erfolg. Ich habe bereits Agenten im Hochland, aber sie haben mich gebeten, weitere Hilfe zu schicken. Seid Ihr interessiert?",
    ["O"] = "Sucht Skuerto bei der Zuflucht im Arathihochland auf.",
    ["T"] = "Malins Gesuch",
  },
  [691] = {
    ["D"] = "Erzmagier Malin schickte Skuerto und mich hierher, um für ihn ein paar Reagenzien zu besorgen. Ich hatte keine Ahnung, wie ich die Gegenstände beschaffen sollte, aber da Ihr jetzt hier seid, hat sich mein Problem wohl gelöst.$B$BIch brauche Folgendes: Hauer der Bleichborken, Medizinbeutel der Bleichborken und ein Schattenjägermesser.$B$BKönntet Ihr mir das besorgen? Die Bleichborken leben südöstlich von Arathi.",
    ["O"] = "Sucht für Lehrling Kryten bei der Zuflucht 10 Hauer der Bleichborken, 4 Medizinbeutel der Bleichborken und 1 Schattenjägermesser.",
    ["T"] = "Sein Gewicht in Gold",
  },
  [693] = {
    ["D"] = "He, $N. Tut mir leid, dass ich früher Euch gegenüber so kurz angebunden war... und nein, das sollte jetzt kein Wortspiel sein.$B$BIch habe herausgefunden, wo Trelanes erster Gegenstand gelandet ist: Kor\'gresh in der Halle der Felsfäuste hat ihn. Und so wie er seinen Leuten die Hölle heiß gemacht hat, damit sie nicht aus der Reihe tanzen, muss er wohl kurz davor sein herauszufinden, wie man seine gesamte Macht nutzen kann.$B$BDie Halle der Felsfäuste liegt südlich von hier und östlich vom Thandolübergang. Findet diesen Oger und holt den Zauberstab.$B$BWährend Ihr weg seid, werde ich versuchen herauszufinden, was wir wegen Stromgarde unternehmen können.",
    ["O"] = "Sucht Trelanes Zauberstab der Herbeirufung und bringt ihn Skuerto bei der Zuflucht.",
    ["T"] = "Zauberstab gegen Faust",
  },
  [695] = {
    ["D"] = "Bitte sehr, $N. Der Edelstein muss nur gegen eines der Podeste nahe dem Fuß des Turms gepresst werden, um dessen Verteidigungsanlage zu aktivieren. Benutzt ihn, wenn Ihr bereit seid, den Turm zu betreten und nach den restlichen Gegenständen Trelanes zu suchen. $B$BViel Glück. Ich warte hier im Lager auf Eure Rückkehr. Oh, und sprecht mit Skuerto, bevor Ihr geht. Er gibt Euch die restlichen Details, die Ihr braucht.",
    ["O"] = "Sprecht mit Skuerto in der Zuflucht im Arathihochland.",
    ["T"] = "Des Lehrlings Verzauberung",
  },
  [697] = {
    ["D"] = "Bringt diesen Brief nach Sturmwind und lasst Malin wissen, dass Kryten und ich bald wieder zu Hause sind. Ich zweifle nicht daran, dass er Euch reichlich für Eure Hilfe uns gegenüber belohnen wird, $N.$B$BSichere Reise.",
    ["O"] = "Bringt dem Erzmagier Malin im Magierviertel von Sturmwind den versiegelten Brief.",
    ["T"] = "Malins Gesuch",
  },
  [699] = {
    ["D"] = "Man hat uns wegen der Murlocplage entlang der Küste hier stationiert, um andere vor Gefahren jeglicher Art, die Steinard bedrohen könnten, zu beschützen - oder zu warnen, falls deren Streitkräfte zu groß werden. Das gibt uns nicht viel Zeit, um auszuruhen, und wir könnten weitere Hilfe Eurerseits gebrauchen, wenn Ihr gewillt seid.$B$BDie Sägezahnschnapperklauen geben erstklassige Waffen ab, wenn sie richtig vorbereitet werden. Bringt mir ein paar ihrer Klauen, dann werde ich zusehen, dass Ihr eine unserer Waffen als Dank für das Frischfleisch und die angenehme Gesellschaft bekommt.",
    ["O"] = "Bringt Tok\'Kar in den Sümpfen des Elends 6 Sägezahnschnapperklauen.",
    ["T"] = "Mangelnde Reserven",
  },
  [700] = {
    ["D"] = "Ah, welch edle Steine doch aus Lordaeron kommen. Was für eine Schande, dass in diesem Land ein solches Chaos herrscht.$B$BAber der Gedenkstein ist sehr schön geworden! Ich glaube, der König wird damit sehr zufrieden sein.$B$BWürdet Ihr so gütig sein und Seiner Majestät eine Nachricht senden, dass der Gedenkstein wie gefordert fertig gestellt wurde?",
    ["O"] = "Erstattet Meldung an König Magni Bronzebart.",
    ["T"] = "Ehrerbietung von einem König",
  },
  [701] = {
    ["D"] = "Mit ein paar weiteren Dingen kann ich einen Gegenstand schaffen, der Gor\'mul mit der Magie der Arglist des Raptors erfüllt! Wenn ihn die Macht des Raptors durchströmt, wird Gor\'mul unsere neue Macht erkennen und den Krieger neu entdecken, der in ihm schlummert!$B$BUm den Gegenstand und den Zauber zu schaffen, werden Raptorherzen benötigt.",
    ["O"] = "Besorgt für Tor\'gan 12 Raptorherzen von Hochlandfleischpirschern.",
    ["T"] = "Arglist des Raptors",
  },
  [702] = {
    ["D"] = "Nehmt diese Kugel und nutzt ihre Energie, um Gor\'mul die Essenz des Raptors zu spenden. Sie wird seinen Geist erfrischen und ihm einen neuen Lebensinhalt geben. Sobald er wieder normal ist, können wir ihn über die aktuelle Situation der Horde informieren und wir werden einen großen Krieger bei unseren unaufhörlichen Kämpfen an unserer Seite haben.",
    ["O"] = "Gebt Gor\'mul Tor\'gans Kugel in Hammerfall im Arathihochland.",
    ["T"] = "Arglist des Raptors",
  },
  [703] = {
    ["D"] = "Oh ja, meine gegrillten Bussardflügel sind ein Renner! Die Sauce ist gehaltvoll und das Fleisch würzig. Und der Duft lockt alle hungrigen Mäuler in einem Umkreis von Meilen an!$B$BIch habe alles, was ich brauche, um eine Menge zu kochen. Alles... außer Bussardflügel!$B$BWenn Ihr etwas davon haben möchtet, besorgt mir Bussardflügel. Bringt Ihr mir genügend davon, gebe ich Euch vielleicht sogar das Rezept dafür!",
    ["O"] = "Bringt Riggelfuzz 4 Bussardflügel.",
    ["T"] = "Gegrillte Bussardflügel",
  },
  [704] = {
    ["D"] = "Der letzte Bericht, den ich von Agmond erhalten habe, ist sehr beunruhigend, umso mehr, da er jetzt tot ist! Er erwähnte, dass seine Buddler an der Grabungsstätte vor Uldaman \"uralte Urnen\" entdeckt hätten. Ich fürchte, dass die dort gefundenen Urnen die Troggs erzürnt haben, ebenso wie die Götzen, die ich dort fand.$B$BIch möchte, dass Ihr uns die Urnen bringt, damit wir sie untersuchen können. Geht zur Grabungsstätte Uldaman, sammelt die Urnen ein und kehrt dann zu mir zurück.",
    ["O"] = "Bringt Ausgrabungsleiter Eisenband am Loch Modan 4 verzierte Steinurnen.",
    ["T"] = "Agmonds Schicksal",
  },
  [705] = {
    ["D"] = "Das Ödland ist eine unwirtliche Gegend voller heimtückischer Raubtiere und dreister Aasfresser. Unheimlich, besonders für einen so kleinen Goblin. Ich muss gewitzt sein, wenn ich überleben möchte!$B$BIch kenne das Rezept für Blitzbomben. Die benutze ich, um wilde Tiere zu verjagen. Leider geht mir langsam eine der Zutaten aus: zu Pulver zermahlene blaue Perlen. Bringt mir welche, dann soll es Euer Schaden nicht sein.$B$BHe, und ich hoffe, Ihr habt gute Stiefel an. Die blauen Perlen, die ich brauche, stammen von Muscheln am Finsteren Riff.$B$BGenau, dem Finsteren Riff im Schlingendorntal!",
    ["O"] = "Bringt Riggelfuzz im Ödland 9 blaue Perlen.",
    ["T"] = "Perlentauchen",
  },
  [706] = {
    ["D"] = "Als Lehrling Feranor Stahlzehes habe ich seine Spezialtechnik erlernt, wie man feurige Verzauberungen auf Waffen wirkt. Als ich das letzte Mal von ihm hörte, arbeitete er mit einer Jägergruppe in einer Schmiede, oben in Lordaeron.$B$BIch kann Euch nicht viel darüber sagen, an was genau er dort arbeitete, nur dass dazu Blut eines noch schlagenden Herzens eines seltenen Schwarzdrachenwelpen nötig ist. Ohne Neuigkeiten aus Eisenschmiede habe ich nicht viel zu tun. Wenn Ihr mir ein Herz eines Welpen aus der Lethlorklamm bringt, zeige ich Euch meine Fertigkeiten!",
    ["O"] = "Besorgt Sigrun Eisenhieb ein Schwarzdrachenherz.",
    ["T"] = "Feuerverzauberungen",
  },
  [707] = {
    ["D"] = "$N. Ich habe Berichte von Ausgrabungsleiter Eisenband von der Ausgrabungsstätte am Loch Modan erhalten - er braucht jemanden, der ihm die Rennerei auf einer unserer anderen Ausgrabungen abnimmt.$B$BIch habe keine weiteren Details, aber wenn Ihr Euch ein bisschen die Füße vertreten wollt, dann sprecht mit Eisenband.",
    ["O"] = "Sprecht auf Eisenbands Ausgrabungsstätte am Loch Modan mit Ausgrabungsleiter Eisenband.",
    ["T"] = "Eisenband sucht Euch!",
  },
  [710] = {
    ["D"] = "Ich habe die Arbeit an meinem neuesten Projekt hier im Ödland erstmal eingestellt: meine Fesseln der Elementarbindung.$B$BWenn sie vollendet sind, werden Magier noch mächtigere Elementare beschwören und sie in noch größerer Zahl nutzen können. Ich brauche zunächst kleine Felssplitter, um einige meiner neuesten Angriffszauber und ein paar der Dingsdas zu testen, die ich zusammengebastelt habe.$B$BDie Splitter kann man leicht von den kleinen Felselementaren westlich von hier sammeln. Würdet Ihr mir ein paar bringen?",
    ["O"] = "Sucht 10 kleine Felssplitter und bringt sie Lotwil Veriatus im Ödland.",
    ["T"] = "Element-Studie: Fels",
  },
  [711] = {
    ["D"] = "Ihr habt Euch als fähiger erwiesen als all meine Lehrlinge zusammen, $N.$B$BFür die nächsten Aktionen ist etwas mehr Krafteinsatz erforderlich. Wenn Ihr vorsichtig genug seid, könnt Ihr bei Felselementaren große Steinplatten finden. Ihre Körper sind kräftiger und größer und deshalb kommt man bei ihnen eher an Teile heran, die groß genug für meine Tests sind. Ihr findet sie weiter westlich von hier oder südöstlich entlang des Gebirgsrandes.$B$BLasst Euch Zeit, ich brauche eine Weile, um diese kleineren Stücke zu testen, die Ihr mir gebracht habt.",
    ["O"] = "Bringt Lotwil Veriatus im Ödland 3 große Steinplatten.",
    ["T"] = "Element-Studie: Fels",
  },
  [712] = {
    ["D"] = "$N, ich brauche noch ein weiteres Teil von den Elementaren hier im Ödland: die Armschienen der Steinbindung von den großen Felselementaren. Meinen Berechnungen nach sollten diese Armschienen ausreichen, um die Kraft meiner Fesseln zu beurteilen.$B$BBei meinen Wahrsageversuchen über die großen Felselementare sah ich eine Gruppe von ihnen südwestlich von hier, in der Nähe von ein paar Ogern, wenn ich mich recht erinnere.$B$BBringt mir 5 ihrer Armschienen, damit ich ein paar in Reserve habe. Servo ist berüchtigt dafür, meine Reagenzien und Forschungen zu verlegen.",
    ["O"] = "Bringt Lotwil Veriatus im Ödland 5 Armschienen der Steinbindung.",
    ["T"] = "Element-Studie: Fels",
  },
  [713] = {
    ["D"] = "Lasst mich Euch etwas über Frostöl erzählen: Nichts kann einen Motor oder ein Getriebe besser vor Überhitzung schützen.$B$BSeine Kühleigenschaften sind außerordentlich und seine Langlebigkeit als Schmiermittel ist ohnegleichen. Aber das ist nicht alles: Es trägt auch dazu bei, die Verzauberung magischer Gegenstände zu erhalten.$B$BIch brauche eine Phiole davon, um die magische Kapazität meiner Fesseln zu erhöhen und sie mächtig genug zu machen, um unseren beschworenen Freund davon abzuhalten, uns alle umzubringen.$B$BBro\'kin, einer meiner Goblinfreunde, kann Euch lehren, wie man es herstellt. Er wohnt in Alterac.",
    ["O"] = "Sucht Frostöl und bringt es Lotwil Veriatus im Ödland.",
    ["T"] = "Kühlaufsätze setzen sich durch",
  },
  [714] = {
    ["D"] = "Ihr habt Eure Arbeit überdurchschnittlich gut gemacht, $N.$B$B<Lotwil ist offensichtlich durch seine Arbeit abgelenkt.>$B$BFalls wir uns in Kharanos jemals wiedersehen, scheut Euch nicht, zu mir zu kommen und nach Arbeit zu fragen.$B$BUpps! Schnell, nehmt das... ja, das da. Danke.$B$BJetzt brauche ich nur noch eines. Es ist ein kleines Teil, aber leider ziemlich ausschlaggebend: ein Gyrochronatom.$B$BWenn Ihr irgendwo einen auftreiben und mir bringen würdet, könnte ich das hier fertig stellen.",
    ["O"] = "Bringt Lotwil Veriatus im Ödland einen Gyrochronatom.",
    ["T"] = "Gyro... Was?",
  },
  [715] = {
    ["D"] = "Ich glaube, die Gegenstände, die Ihr Lotwil besorgt habt, werden unsere geringste Sorge sein, $N. Andererseits habt Ihr Euch in den Wissenschaften, die Lotwil so heilig sind, mehr als talentiert gezeigt; also wie wäre es, wenn Ihr jetzt mir ein bisschen helft?$B$BIch brauche einen Unsichtbarkeitstrank und einen Heiltrank; beides könnte sich als nützlich erweisen, sobald er erst mit seinem Ding angefangen hat.$B$BDafür gebe ich Euch dieses Rezept für einen Trank, den ich entwickelt habe, als ich noch bei Lotwil in die Lehre ging. Er basiert auf einigen der Forschungen, die er hier im Ödland durchführte.",
    ["O"] = "Bringt Lucien Knobelzang im Ödland einen Heiltrank und einen geringen Unsichtbarkeitstrank.",
    ["T"] = "Flüssigstein",
  },
  [716] = {
    ["D"] = "Ich weiß, wie knickerig Lotwil sein kann, $N, also lasst mich etwas als Belohnung für all Eure Mühen fertigen. Vielleicht kann es Euch sogar vor der Scheußlichkeit schützen, die Lotwil gerade erfindet.$B$BIch habe alle zusätzlichen Steine verarbeitet, die Ihr für ihn gesammelt hattet, und kann Euch ein paar hübsche Armschienen machen, die Ihr möglicherweise gebrauchen könnt. Sie sind natürlich verzaubert.$B$BIch brauche nur noch gemusterte bronzene Armschienen, die ein Schmied hergestellt hat, um die fertig zu stellen, die ich für Euch mache.",
    ["O"] = "Bringt Lucien Knobelzang im Ödland ein Paar gemusterte bronzene Armschienen.",
    ["T"] = "Stein ist besser als Stoff",
  },
  [717] = {
    ["D"] = "Es ist offensichtlich, dass wir nicht mehr gewährleisten können, Schattenbringer und Hematus noch länger in Lethlor festzuhalten. Die schwarzen Großdrachen haben sich dort niedergelassen, und dass es den Ogern gelungen ist, das Zeichen zu stehlen, weist darauf hin, dass drastischere Maßnahmen ergriffen werden müssen.$B$BDas Gefängnis der Drachen wird von drei Runensteinen gebildet, die man nur mithilfe des Zeichens der Erde wegnehmen kann. Diese Runensteine können dann dazu verwendet werden, das Siegel zu zerbrechen, das sie gefangen hält.$B$B$N... sorgt dafür, dass sie süß träumen... für immer.",
    ["O"] = "Benutzt das Zeichen der Erde, um die Amethyst-, Opal- und Diamantsäulen zu aktivieren und die Runensteine zu holen.$B$BLegt die Runensteine auf das Siegel der Erde, um Schattenbringer und Hematus zu befreien.$B$BTötet sie und bringt dann Garek Schattenbringers Bindung, Hematus\' Ketten und das Zeichen der Erde.",
    ["T"] = "Erdbeben",
  },
  [718] = {
    ["D"] = "Wir hatten es so eilig, die Ausgrabungsstätte von Uldaman zu verlassen, dass wir uns gezwungen sahen, unterwegs viele Wagen und Nachschubkisten zurückzulassen. Die Oger von Camp Kosh haben sich auf unsere zurückgelassene Ausrüstung gestürzt wie Geier auf faulende Kadaver. Riechen auch nicht besser. Als die Kadaver, meine ich.$B$BWie dem auch sei, sie haben sich den Wagen mit unseren Waffen und anderem Zeug geschnappt und deshalb - tja, es ist ein bisschen peinlich - konnten wir uns nichts zum Essen beschaffen.$B$BWir stecken echt ganz schön in der Klemme. Könnt Ihr uns vielleicht helfen?",
    ["O"] = "Holt die Nachschubkiste für Sigrun Eisenhieb.",
    ["T"] = "Illusionen",
  },
  [719] = {
    ["D"] = "He, $N. Seid lieber vorsichtig. Es sind Dunkeleisenzwerge in der Nähe - scheinen vom Schattenschmiedeklan zu sein.$B$BSie haben gerade die Ausgrabungsstätte angegriffen, auf der ich arbeite - haben fast alle dort umgebracht, darunter meinen Boss Hammerzeh. Ich bin gerade noch lebendig weggekommen.$B$BDie Stätte liegt nördlich von hier und ich versuche, mir einen Plan auszudenken, wie wir einen Teil unserer Vorräte zurückbekommen könnten, insbesondere meine Glückshacke.$B$BMeint Ihr, Ihr könntet mir ein bisschen helfen? Einer von diesen verdammten Dunkeleisenzwergen muss meine Hacke haben! Es könnte jeder von denen sein.",
    ["O"] = "Sucht Roggendols Glückshacke und bringt sie Ausgrabungsleiter Roggendol in der Hammerzehs Grabungsstätte.",
    ["T"] = "Ein Zwerg und sein Werkzeug",
  },
  [720] = {
    ["D"] = "<Text wurde mit Kreide auf die Karte gekritzelt.>$B$B\"Roggendol, $BSie bringen mich nach Uldaman. Schickt Hilfe.$B- Hammerzeh\"",
    ["O"] = "Sucht Ausgrabungsleiter Roggendol und teilt ihm mit, dass Hammerzeh Grez noch am Leben ist.",
    ["T"] = "Ein Hoffnungsschimmer",
  },
  [721] = {
    ["D"] = "$N, geht Ihr ihn suchen? Sucht Ihr Hammerzeh in Uldaman?$B$BWenn diese dreckigen Zwerge der Schattenschmiede ihn als Geisel genommen haben, um herauszubekommen, was er über die Titanenausgrabungen weiß, könnte ganz Eisenschmiede in großer Gefahr sein.$B$BSeit langer Zeit schon trachten die Dunkeleisenzwerge nach Magie, um noch mächtigere Golems zu erschaffen. Hammerzeh hatte dasselbe vor, aber natürlich nur, um sie aufzuhalten.$B$BVielleicht lassen sie ihn am Leben, bis sie herausbekommen haben, was er weiß; Ihr müsst Euch beeilen, wenn Ihr ihn retten wollt!",
    ["O"] = "Sucht in Uldaman nach Hammerzeh Grez.",
    ["T"] = "Ein Hoffnungsschimmer",
  },
  [722] = {
    ["D"] = "Ihr seid mutig... <hustet>... dass Ihr Euch wegen mir nach Uldaman wagt, $C.$B$BWenn Ihr wirklich hier seid, um dem... ollen Hammerzeh zu helfen, dann... <hustet>... müsst Ihr nach... meinem Amulett suchen.$B$BGrubenschatten hat es... Magregan Grubenschatten. Muss hier irgendwo sein... in den Ruinen, das ist sicher. Das Amulett wird sie... <hustet>... genau zu den Rollen führen, wenn sie keiner aufhält. Es ist an Euch, $N. Bitte... holt das Amulett.$B$BDie Rollen werden es den Dunkeleisenzwergen ermöglichen, noch mächtigere Golems zu erschaffen. Ich weiß es einfach. Spür\'s in meinen Knochen.",
    ["O"] = "Sucht Hammerzehs Amulett und bringt es ihm nach Uldaman.",
    ["T"] = "Amulett der Geheimnisse",
  },
  [723] = {
    ["D"] = "<Hammerzeh fasst an seine Wunden und braucht einen Moment, um sich zu sammeln, bevor er weiterspricht.>$B$BFindet Roggendol, $N. Bringt ihm mein Amulett. <hustet> Er... <hustet>... wird wissen, was zu tun ist; er weiß, dass ich nach den Wurzeln unseres Volks suchte, und er... <hustet>... weiß, dass es meine Aufgabe war, den Schattenschmiedeklan aufzuhalten. Sie dürfen die Rollen nicht finden... <hustet>",
    ["O"] = "Bringt Ausgrabungsleiter Roggendol im Ödland Hammerzehs Amulett.",
    ["T"] = "Ein Funken Hoffnung",
  },
  [724] = {
    ["D"] = "Okay, okay... Wer könnte wissen, was zu tun ist?$B$BHistoriker Karnik! Genau!$B$BEr lebt in Eisenschmiede. Er kann helfen. Er und Hammerzeh haben zusammengearbeitet, um in Uldaman nach den verschollenen Artefakten zu suchen.$B$BSchätze, die Zwerge der Schattenschmiede haben herausgefunden, dass die beiden mehr wussten, als sie sagten.$B$BErzählt Karnik, was mit Hammerzeh geschehen ist, und gebt ihm das Amulett - er wird Euch dann sagen, was als Nächstes zu tun ist.",
    ["O"] = "Bringt Historiker Karnik in Eisenschmiede Hammerzehs Amulett.",
    ["T"] = "Ein Funken Hoffnung",
  },
  [725] = {
    ["D"] = "$N, lauft los und findet so schnell wie möglich Berater Belgrum; sagt ihm, was Ihr gesehen habt, und fragt ihn, wie wir weiter vorgehen sollen. Diese Notiz wird ihm alles Weitere erklären.$B$BIn der Zwischenzeit werde ich mir die Zeit nehmen, um mit Hammerzehs Geist zu sprechen. Vielleicht weiß er etwas, das uns helfen könnte, die Aktivitäten der Zwerge der Schattenschmiede hier im Ödland zu unterbinden. Wir können nur beten.",
    ["O"] = "Sucht Berater Belgrum und gebt ihm die Notiz.",
    ["T"] = "Kunde über die Bedrohung breitet sich aus",
  },
  [726] = {
    ["D"] = "<Berater Belgrum erreicht das Ende der Notiz und für einen Moment weiten sich seine Augen.>$B$BAch, das sind schlimme Nachrichten.$B$B$N, bitte kehrt zu Historiker Karnik zurück und sagt ihm, dass er im Auftrag des Königs schnell und entschlossen gemäß dieser Information handeln soll. Er muss die Aktivitäten der Zwerge der Schattenschmiede im Ödland unterbinden.",
    ["O"] = "Sprecht mit Historiker Karnik.",
    ["T"] = "Kunde über die Bedrohung breitet sich aus",
  },
  [727] = {
    ["D"] = "Die Schrifttafel erklärt vieles. Aber nicht genug! Nicht annähernd genug!$B$BEs gibt ein Buch, eine Zusammenstellung, die Yagyin vor Urzeiten verfasst hat. Die... Leute, vor denen ich geflohen bin... bewahren dieses Buch an vielen verschiedenen Orten auf. Und die Höchstgebildeten von ihnen studieren es. Es enthält riesiges Wissen. Wissen, das uns beschützen kann!$B$BGeht zu Gerrig Knochengriff. Er befindet sich in Eisenschmiede in einem Geschäft im Düsteren Viertel. Zeigt ihm dieses Siegelzeichen, dann wird er Euch für einen der ihren halten. Fragt ihn nach \'Yagyins Zusammenstellung\'... und bringt sie mir!",
    ["O"] = "Gebt Gerrig Knochengriff im Düsteren Viertel von Eisenschmiede das Siegelzeichen des Hammers.",
    ["T"] = "Auf nach Eisenschmiede zu \'Yagyins Zusammenstellung\'",
  },
  [728] = {
    ["D"] = "Die Schrifttafel erklärt vieles. Aber nicht genug! Nicht annähernd genug!$B$BEs gibt ein Buch, eine Zusammenstellung, die Yagyin vor Urzeiten verfasst hat. Die... Leute, vor denen ich geflohen bin... bewahren dieses Buch an vielen verschiedenen Orten auf. Und die Höchstgebildeten von ihnen studieren es. Es enthält riesiges Wissen. Wissen, das uns beschützen kann!$B$BSucht den Bewahrer Bel\'dugur in Unterstadt von Lordaeron auf. Zeigt ihm dieses Siegelzeichen, dann wird er Euch für einen der ihren halten. Fragt ihn nach \'Yagyins Zusammenstellung\'... und bringt sie mir!",
    ["O"] = "Bringt dem Bewahrer Bel\'dugur in Unterstadt das Siegelzeichen des Hammers.",
    ["T"] = "Auf nach Unterstadt zu \'Yagyins Zusammenstellung\'",
  },
  [729] = {
    ["D"] = "Was war es doch für eine Ehre, als Meister Grauflaum mir auftrug, unter der Führung des großen Ausgrabungsleiters Wirrbart zu arbeiten. Jeder an der Akademie in Eisenschmiede kannte Wirrbarts großartige Entdeckungen.$B$BAber der Ausgrabungsleiter ist sich nicht so recht... ähm... bewusst... was um ihn herum so vor sich geht.$B$BWir hatten Hinweise auf eine große Kultur entdeckt. Schreckliche Golems brachen aus dem Boden hervor und überrannten die Grabungsstätte. Wirrbart schien gar nichts bemerkt zu haben. Ich rannte nach Auberdine, um Hilfe zu holen.$B$BBitte reist nach Süden und seht nach, ob es dem Ausgrabungsleiter gut geht!",
    ["O"] = "Reist nach Süden und seht nach Ausgrabungsleiter Wirrbart.",
    ["T"] = "Der geistesabwesende Ausgrabungsleiter",
  },
  [730] = {
    ["D"] = "Wie schön, $Gein:eine:c; $C, $Gder:die; an den großartigen archäologischen Wundern unserer Welt interessiert ist.$B$BOftmals wird unser Wirken von unseren Freunden der Allianz nur als reines Hobby abgetan. Aber was viele nicht einsehen wollen ist, dass die jüngsten Entdeckungen in Khaz Modan bewiesen haben, dass eine große und mächtige Kraft ganz Azeroth bedroht, von Lordaeron bis nach Kalimdor.$B$BIch bin höchst besorgt um meine Mannschaft, die ich nach Dunkelküste geschickt habe. Meine Leute haben seit Wochen nichts mehr von sich hören lassen.$B$BReist nach Auberdine und sucht nach Hinweisen auf ihren Aufenthaltsort.",
    ["O"] = "Reist nach Auberdine und sucht nach Hinweisen auf das Zwergenausgrabungsteam.",
    ["T"] = "Ärger in Dunkelküste?",
  },
  [731] = {
    ["D"] = "Also dann, Hollee... ach ja, richtig, Ihr seid ja gar nicht Hollee. Wo ist Hollee?$B$BPah, das ist ja jetzt auch nebensächlich. Wir müssen diesen Handfeger finden... Ich meine, das rätselhafte Fossil! Wir müssen dieses Fossil finden. Die Forscherliga in Darnassus wird ein paar Hinweise darauf haben wollen, was ich hier die ganze Zeit getrieben habe.$B$BLasst es mich wissen, wenn Ihr bereit seid, dann machen wir uns auf die Jagd nach diesem Feger... Ich meine, Fossil! Wir sollten irgendeine Niederschrift finden, damit die Liga zufrieden ist.$B$BAlso, seid Ihr so weit?",
    ["O"] = "Beschützt Ausgrabungsleiter Wirrbart, während er nach dem rätselhaften Fossil sucht, und kehrt dann zur Archäologin Hollee in Auberdine zurück.",
    ["T"] = "Der geistesabwesende Ausgrabungsleiter",
  },
  [732] = {
    ["D"] = "Ich habe von Sigrun gehört, dass Ihr ihm eine große Hilfe gewesen seid. Vielleicht darf ich Eure Hilfe ja auch in Anspruch nehmen. Mein Meister, Krasus von den Kirin Tor, hat mich hergeschickt, um die Probleme zu lösen, die sich in der Lethlorklamm zusammenbrauen.$B$BIch darf nicht preisgeben, was genau meine Mission ist oder was ich unternehmen soll, aber seid versichert, dass es von großer Wichtigkeit ist.$B$BEine Bande nomadisierender Oger, die gelegentlich in der Nähe vom Camp Boff gesichtet wurden, hat einen Gegenstand gestohlen, das so genannte Zeichen der Erde, und ich muss es wiederhaben. Bringt es mir und ich verrate Euch mehr.",
    ["O"] = "Beschafft für Garek das Zeichen der Erde.",
    ["T"] = "Erdbeben",
  },
  [733] = {
    ["D"] = "Es wird hier ganz schön haarig werden, wenn es mir nicht gelingt, Material aufzutreiben, um ein paar Verteidigungslinien und andere Notwendigkeiten zu konstruieren. Mir ist aufgefallen, dass die Oger in diesem Gebiet über jede Menge Metallschrott verfügen. Vielleicht sind die Geschichten über Belagerungsmaschinen im Ödland doch gar nicht so weit hergeholt...$B$BWie dem auch sei, die Ausgrabungsleiter machen sich langsam Sorgen um die Lage in unserem kleinen Lager, deshalb würde ich gern, sobald es geht, mit der Arbeit beginnen.$B$BDas Hauptlager der Oger liegt bei der Staubspeiergrotte, eine ordentliche Strecke von hier aus Richtung Westen.",
    ["O"] = "Bringt Sigrun 7 Teile Metallschrott.",
    ["T"] = "Schnorrerei",
  },
  [734] = {
    ["D"] = "Wunderbar, wunderbar.$B$B<Lotwil sieht Euch einen Moment lang an, bevor er sich wieder seinen Fesseln zuwendet.>$B$BSprecht mit Lucien, $N. Ich brauche ein Zahnrad Nr. 5. Bringt es mir bitte, ja?",
    ["O"] = "Sprecht im Ödland mit Lucien Knobelzang.",
    ["T"] = "Das wird schwierig",
  },
  [735] = {
    ["D"] = "Yagyins Zusammenstellung\', eh? Wollt Ihr vielleicht irgendwo eine neue Ortsgruppe aufbauen? Egal, es steht mir nicht zu, Euch das zu fragen.$B$BIch werde Euch helfen, weil ich ein Exemplar davon besitze, das ich entbehren kann. Und eine Hand wäscht die andere, stimmt\'s? Daher werden wir groß.$B$BIch brauche drei mächtige Gegenstände. Bringt sie mir, dann gebe ich Euch die Zusammenstellung.$B$BIch werde ihre Namen nicht nennen. Man könnte uns belauschen. Aber wartet, ich schreibe sie auf.",
    ["O"] = "Bringt Gerrig Knochengriff in Eisenschmiede die Gegenstände in Knochengriffs Notiz.",
    ["T"] = "Der Stern, die Hand und das Herz",
  },
  [736] = {
    ["D"] = "Ihr braucht \'Yagyins Zusammenstellung\'? Dann wollt Ihr also eine neue Herde sammeln? Natürlich... und natürlich werde ich Euch helfen, $GBruder:Schwester;.$B$BAber Ihr müsst mir auch helfen.$B$BIch brauche drei mächtige Gegenstände. Bringt sie mir, dann gehört die Zusammenstellung Euch.$B$BIch darf die Namen dieser Gegenstände nicht aussprechen, aber ich werde sie aufschreiben.$B$BBringt mir die Gegenstände, die auf dieser Liste stehen. Wenn Ihr das schafft, seid Ihr wahrlich würdig, ein eigenes Kapitel über unseren Orden zu beginnen.",
    ["O"] = "Bringt dem Bewahrer Bel\'dugur in Unterstadt die Gegenstände von Bel\'dugurs Notiz.",
    ["T"] = "Der Stern, die Hand und das Herz",
  },
  [737] = {
    ["D"] = "Ich habe die Zusammenstellung für Euch, $N. Ich muss Euch wohl nicht sagen, dass Ihr sie sorgfältig behandeln solltet. Und dass Ihr sie nicht in unfreundliche Hände gelangen lassen solltet. $B$BDieses Buch enthält viele Wahrheiten...$B$B... und die Wahrheit kann manchmal wehtun.",
    ["O"] = "Bringt Theldurin \'Yagyins Zusammenstellung\'.",
    ["T"] = "Verbotenes Wissen",
  },
  [739] = {
    ["D"] = "Agmonds Spuren führen nach Westen. Anhand der Blutspuren auf den Felsen und im Sand ist unschwer zu erraten, dass sich der Zwerg ein ganzes Stück weit geschleppt haben muss.$B$BUnd hier ist er dann gestorben.$B$BBei näherer Betrachtung ist das Wort \"Murdaloc\" erkennbar, das grob aus Sandsteinen des Ödlands zusammengestückelt wurde.$B$BDirekt neben Agmonds toter Hand.",
    ["O"] = "Tötet Agmonds Mörder Murdaloc.$B$BTötet 12 Knochenknacker der Steingrufttroggs.$B$BErstattet Ausgrabungsleiter Eisenband am Loch Modan Bericht.",
    ["T"] = "Murdaloc",
  },
  [741] = {
    ["D"] = "Der Ausgrabungsleiter will also das geheimnisvolle Fossil zur Forscherliga nach Darnassus schicken? Es schmeckt mir gar nicht, Dunkelküste ohne ihn zu verlassen.$B$BHier, $N, bringt Ihr dem Chefarchäologen Grauflaum in Darnassus das rätselhafte Fossil.",
    ["O"] = "Bringt dem Chefarchäologen Grauflaum in Darnassus das rätselhafte Fossil.",
    ["T"] = "Der geistesabwesende Ausgrabungsleiter",
  },
  [742] = {
    ["D"] = "Achtung, junge Abenteurer! Die Wildnis des Eschentals erwartet Euch!$B$BDie Horde hat eine starke Präsenz in den Ländern nördlich des Brachlandes aufgebaut. Unsere beiden Außenposten - der Splitterholzposten und der Zoramstrandposten - wollen der Horde zu Ruhm gereichen! Diejenigen, die sich beweisen möchten, sollten dort nach Anleitung suchen. Wichtiger Hinweis: Senani Donnerherz im Splitterholzposten, direkt nördlich des Brachlandes, sucht Abenteurer, die an einer großen Jagd im Eschental teilnehmen wollen!",
    ["O"] = "Sprecht mit Senani Donnerherz im Splitterholzposten im Eschental.",
    ["T"] = "Die Jagd im Eschental",
  },
  [743] = {
    ["D"] = "Danke, dass Ihr Euch die Zeit genommen habt, mit mir zu sprechen, $N. Ich bin Ruul, Krieger und Lehrer.$B$BIhr habt offensichtlich ein Alter erreicht, in dem Ihr Euch auf Eure Aufgaben vorbereiten müsst, wenn Ihr auf Abenteuer weit außerhalb von Mulgore aus seid.$B$BWenn Ihr Eure Kraft erproben möchtet, versucht Euch zuerst an den Windfurienharpyien im Südosten. Sie treiben sich an den Bergrändern fernab der Straße herum.$B$BSie gehören mit zu unseren natürlichen Feinden hier in Mulgore und sind gut geeignet, um Eure Fertigkeiten einzuschätzen.",
    ["O"] = "Bringt Ruul Adlerklaue im Dorf der Bluthufe 8 Windfurienkrallen.",
    ["T"] = "Windfuriengefahr",
  },
  [744] = {
    ["D"] = "$N, mein Bruder wird schon bald vor Häuptling Bluthuf treten und es ist mir eine Ehre, einen Kopfputz für ihn anfertigen zu dürfen.$B$BIch möchte Euch um einen Gefallen bitten, während ich die letzten dieser Lederriemen gerbe. Ich habe keine Zeit mehr, um genügend Federn zu suchen, und ich habe mich gefragt, ob Ihr wohl noch ein paar für mich sammeln würdet?$B$BIhr könnt Federn in der richtigen Größe bei den Harpyien finden, die ganz weit im Norden und nordwestlich von Donnerfels leben. Ich brauche nur 6 azurblaue und 6 bronzene Federn, um die Ausführung zu vervollständigen.",
    ["O"] = "Sammelt 6 azurblaue und 6 bronzene Federn.",
    ["T"] = "Vorbereitungen auf die Zeremonie",
  },
  [745] = {
    ["D"] = "Viele Konflikte verwunden dieses Land, $N. Ich hoffe, dass Ihr nicht so viele wie ich ertragen müsst. Es gibt nur einen, bei dem ich um Eure Mithilfe bitten möchte. Er wird Euch deutlich machen, wie schrecklich sogar eine kleine Bedrohung für das Land sein kann, wenn man sie ignoriert.$B$BBleichmähnengnolle haben sich südlich vom Dorf der Bluthufe und in einer Höhle im Westen niedergelassen. Sie missachten unsere Versuche, mit ihnen zu kommunizieren, und vernichten ungezügelt die Tierwelt Mulgores.$B$BWorte allein genügen nicht mehr als Antwort.",
    ["O"] = "Tötet 10 Gerber der Bleichmähnen, 8 Kürschner der Bleichmähnen und 5 Wilderer der Bleichmähnen und kehrt dann zu Baine Bluthuf ins Dorf der Bluthufe zurück.",
    ["T"] = "Gemeinsame Nutzung des Landes",
  },
  [746] = {
    ["D"] = "Im Nordwesten bereiten Zwerge eine Grabungsstätte vor. Sie glauben, dass Geheimnisse in der Erde verborgen seien, und das stimmt auch; dies ist jedoch kein Grund, das Land bei der Suche nach diesen Lehren auszuhöhlen und zu besudeln.$B$BSammelt die Grabwerkzeuge der Zwerge ein, zerstört sie und kehrt mit den zerbrochenen Werkzeugen der Zwerge zu mir zurück.$B$BIhr könnt die Werkzeuge in einer Schmiede zerschlagen. Ihr findet eine in Donnerfels, aber jede Schmiede, sogar eine Schmiede im Zwergenlager, eignet sich dazu.",
    ["O"] = "Sammelt 5 Ausgrabungsleiterhacken ein.$B$BZerschlagt die Ausgrabungsleiterhacken in einer Schmiede, um zerbrochene Werkzeuge zu erhalten.$B$BBringt Baine Bluthuf im Dorf der Bluthufe 5 zerbrochene Werkzeuge.",
    ["T"] = "Zwergenbuddelei",
  },
  [747] = {
    ["D"] = "Ihr seht viel versprechend aus, und ich denke, Ihr werdet Euch dem Stamm gegenüber beweisen. Vielleicht wird man Euch eines Tages in der großen Stadt Donnerfels willkommen heißen. Bevor das jedoch möglich ist, müsst Ihr Euch meinem Vater, Häuptling Falkenwind, beweisen.$B$BWir hier oben auf der Hochwolkenebene sind stolz auf unsere hervorragenden Jagdfertigkeiten. Tauren jagen aus Notwendigkeit und als sportlichen Wettkampf. Unsere Fleischvorräte gehen zur Neige und wir brauchen Federn, um Kleidung herzustellen. Jagt die in der Nähe herumwandernden Ebenenschreiter und beweist Euch, indem Ihr für Nachschub für das Dorf sorgt.",
    ["O"] = "Grull Falkenwind im Camp Narache möchte, dass Ihr ihm 7 Ebenenschreiterfedern und 7 Stücke Ebenenschreiterfleisch bringt.",
    ["T"] = "Die Jagd beginnt",
  },
  [748] = {
    ["D"] = "Goblins und ihre Gefolgsleute haben unsere heiligen Brunnen besudelt! Das dürfen wir nicht zulassen.$B$BUm alle Brunnen zu säubern, muss ich ein Läuterungstotem herstellen und Ihr müsst dieses Totem dann zu den Brunnen bringen und ein Säuberungsritual durchführen.$B$BZuerst werden wir ein Läuterungtotem für den Brunnen der Winterhufe herstellen. Dabei muss uns das Land behilflich sein. Jagt Präriewölfe wegen ihrer Tatzen und ausgewachsene Ebenenschreiter wegen ihrer Krallen. Sie sind im Südwesten zu finden.$B$BKehrt mit den Tatzen und Krallen zu mir zurück, $N.",
    ["O"] = "Bringt Mull Donnerhorn im Dorf der Bluthufe 6 Präriewolftatzen und 4 Ebenenschreiterkrallen.",
    ["T"] = "Vergiftetes Wasser",
  },
  [749] = {
    ["D"] = "Vor ein paar Tagen haben wir am Nordufer des Steinbullensees eine bewachte Karawane der Venture Company entdeckt. Wir haben bei früheren Kämpfen mit ihnen zwei unserer Leute verloren, deshalb haben wir mit der Karawane kurzen Prozess gemacht.$B$BWir haben ihre Nachschubkisten zurückgelassen, weil wir dachten, unsere Fackeln würden alles Wertvolle zerstören, aber da man in der Nähe der ausgebrannten Wagen Bergungstrupps der Venture Company gesichtet hat, haben wir uns wohl geirrt.$B$BWürdet Ihr zur Karawane gehen und den Inhalt der Kisten untersuchen?",
    ["O"] = "Morin Wolkenpirscher möchte, dass Ihr den Inhalt einer Nachschubkiste der Überfallenen Karawane untersucht.",
    ["T"] = "Die Überfallene Karawane",
  },
  [750] = {
    ["D"] = "Ein Tauren, der in der Fertigkeit des Jagens bewandert ist, weiß, dass seine Beute nicht nur eine Trophäe ist. Die Wildtiere der Ebenen versorgen uns mit allem, was wir zum Überleben brauchen. Ihr würdet die Ältesten ziemlich beeindrucken, wenn Ihr ein paar hochgeschätzte Bergpumapelze erjagen würdet. Ihr findet die Wildtiere in den Hügeln im Süden.$B$BUnsere Kinder brauchen Kleidung und unsere Zelte müssen repariert werden.",
    ["O"] = "Grull Falkenwind im Camp Narache möchte, dass Ihr ihm 10 Bergpumapelze bringt.",
    ["T"] = "Die Jagd geht weiter",
  },
  [751] = {
    ["D"] = "Als Ihr den Deckel der Kiste aufstemmt, findet Ihr sie vollgestopft mit einem Sortiment aus Erzen und Mineralien, weitere Beweise für den Bergbau der Venture Company in Mulgore. Oben auf dem Erz und den Mineralien liegt ein fest verschnürtes Bündel Papiere, das Ihr an Euch nehmt.$B$BVom Vorgebirge im Osten her könnt Ihr schwach Arbeitsgeräusche hören und Ihr seht Rauch von einem großen Feuer aufsteigen.",
    ["O"] = "Bringt Morin Wolkenpirscher in der Nähe des Dorfs der Bluthufe die Dokumente der Venture Company.",
    ["T"] = "Die Überfallene Karawane",
  },
  [752] = {
    ["D"] = "Alle Mitglieder des Stammes teilen die Harmonie des Lebens miteinander. Wir leben und arbeiten zusammen.$B$BMit der Verpflichtung, füreinander da zu sein, haben wir eine große Verantwortung übernommen. Ich habe eine bescheidene Bitte an Euch.$B$BMeine Mutter ist heute Morgen fortgegangen, um Wasser aus dem Brunnen südöstlich von Narache zu holen. Das ist jetzt schon eine Weile her. Würdet Ihr wohl an meiner Stelle nach ihr sehen, während ich mich hier um Stammesangelegenheiten kümmere?",
    ["O"] = "Häuptling Falkenwind möchte, dass Ihr seine Mutter beim Brunnen südöstlich vom Camp Narache sucht.",
    ["T"] = "Eine bescheidene Bitte",
  },
  [753] = {
    ["D"] = "Ich habe in meinem Leben so viele Pfade beschritten und meinen alten Beinen mangelt es an der Energie, die sie einst hatten. Ich kann aber immer noch meine Pflichten innerhalb des Stammes erfüllen. Manchmal braucht eine alte Frau eben nur etwas länger dafür.$B$BAber Ihr scheint mir $Gein eifriger:eine eifrige:c; $C zu sein. Wir sollten Eure jugendliche Vitalität auf die Probe stellen. Holt eine Wasserkanne vom Brunnen und bringt sie meinem Sohn, dem Häuptling im Camp Narache.$B$BVergesst nicht, dass selbst die bescheidenste Tat die Anerkennung der Ahnen finden kann.",
    ["O"] = "Holt eine Wasserkanne vom Brunnen.$B$BBringt die Kanne zu Häuptling Falkenwind im Camp Narache, das nordwestlich des Brunnens liegt.",
    ["T"] = "Eine bescheidene Bitte",
  },
  [754] = {
    ["D"] = "Ich habe das Totem angefertigt, um den Brunnen der Winterhufe zu säubern. Jetzt müsst Ihr es zum Brunnen bringen und ein Säuberungsritual durchführen. Ihr findet ihn im Südosten, bewacht von elenden Goblins!$B$BDas wird keine leichte Aufgabe, aber Ihr müsst es tun, wenn wir jemals wieder sein Wasser trinken sollen.$B$BViel Glück, $N.",
    ["O"] = "Benutzt das Läuterungstotem der Winterhufe beim Brunnen der Winterhufe und kehrt dann zu Mull Donnerhorn zurück.",
    ["T"] = "Winterhufsäuberung",
  },
  [755] = {
    ["D"] = "Eure Bereitschaft, für die Tauren von Narache eine bescheidene Bitte zu erfüllen, und Euer Eifer zu lernen sind edle Charakterzüge, $N. Ich weiß, eines Tages wird man in Donnerfels von Euch als einer bedeutenden Person sprechen, $C.$B$BBevor es so weit ist, müsst Ihr jedoch die Riten der Erdenmutter bestehen, derer es drei gibt.$B$BDie erste Prüfung ist der Ritus der Kraft. Begebt Euch zu Seher Grauzunge und sagt ihm, dass Häuptling Falkenwind Euch schickt.$B$BIhr findet die Behausung des Sehers direkt südlich des Lagers Narache in den Hügeln verborgen.",
    ["O"] = "Begebt Euch zu Seher Grauzunge, der in den Hügeln direkt südlich des Lagers Narache lebt.",
    ["T"] = "Riten der Erdenmutter",
  },
  [756] = {
    ["D"] = "Um das Läuterungstotem der Donnerhörner zu erschaffen, benötigt Ihr die Klauen der Raubtiere der Mulgoreebene. Jagt Präriepirscher wegen ihrer Pirscherklauen und Flachlandpumas wegen ihrer Pumaklauen und kehrt dann zu mir zurück.$B$BIhr findet die Präriepirscher und Flachlandpumas im Osten und Westen.",
    ["O"] = "Bringt Mull Donnerhorn 6 Pirscherklauen und 6 Pumaklauen.",
    ["T"] = "Totem der Donnerhörner",
  },
  [757] = {
    ["D"] = "Die Riten der Erdenmutter sind die Schritte, die ein junger Tauren gehen muss, um sich in Donnerfels Respekt zu verschaffen.$B$BIhr müsst zuerst den Ritus der Kraft bestehen. Bei dieser Prüfung müsst Ihr Euren Mut beweisen, indem Ihr Feinde des Stammes tötet.$B$BDer Borstennackenstamm aus der Dornrankenklamm im Osten ist in unser Stammesterritorium eingedrungen. Sie überfallen unsere Jägergruppen und rauben in der Dunkelheit unsere Dörfer aus.$B$BZeigt Euren Wagemut, indem Ihr diese Bösewichte zur Strecke bringt und dem Häuptling des Lagers Narache ihre Gürtel zum Beweis Eurer Taten vorlegt.",
    ["O"] = "Tötet die Borstennacken in der Dornrankenklamm und bringt Häuptling Falkenwind im Camp Narache 12 Gürtel der Borstennacken.",
    ["T"] = "Ritus der Kraft",
  },
  [758] = {
    ["D"] = "Das Totem ist bereit, $N. Und wiederum ist es Eure Aufgabe, das Ritual der Läuterung durchzuführen.$B$BGeht zum Brunnen der Donnerhörner nördlich des Dorfes und benutzt das Läuterungstotem der Donnerhörner bei Eurem Ritual. Kehrt danach zu mir zurück.$B$BMögen Euch die Geister leiten.",
    ["O"] = "Benutzt das Läuterungstotem der Donnerhörner beim Brunnen der Donnerhörner und kehrt dann zu Mull zurück.",
    ["T"] = "Donnerhornsäuberung",
  },
  [759] = {
    ["D"] = "Es gibt nur noch einen Brunnen, der gesäubert werden muss; er trägt den Namen des Wildmähnenklans. Damit dieser Brunnen wieder rein wird, muss das Land die Zähne eines wilden Raubtiers opfern, die eines Präriewolfalphatiers. Findet die Alphatiere im Norden und kehrt dann zu mir zurück, wenn Ihr Eure Jagd beendet habt.$B$BAlphatiere streifen oft am Fuße unserer mächtigen Stadt Donnerfels umher.",
    ["O"] = "Bringt Mull Donnerhorn im Dorf der Bluthufe 8 Präriealphazähne.",
    ["T"] = "Totem der Wildmähnen",
  },
  [760] = {
    ["D"] = "Die Geister sprechen von Eurer Säuberung der Brunnen der Winterhufe und Donnerhörner, $Gjunger:junge; $N. Das Totem der Wildmähnen ist fertig und die letzte Aufgabe erwartet Euch.$B$BDer Brunnen der Wildmähnen liegt nördlich von Donnerfels. Führt das Ritual durch! Heilt das Land vom Gift der Goblins! Lasst das Wasser wieder klar und rein fließen!",
    ["O"] = "Benutzt das Läuterungstotem der Wildmähnen beim Brunnen der Wildmähnen.",
    ["T"] = "Wildmähnensäuberung",
  },
  [761] = {
    ["D"] = "Der Sturzflieger ist ein gewitzter Vogel, der schwer zu finden und zu jagen ist. Eine Sammlung Sturzfliegerkiele ist ein Zeichen der Geschicklichkeit und Entschlossenheit eines Jägers.$B$BWenn Ihr gewillt seid, diese Aufgabe anzunehmen, zieht in die Ebene hinaus und jagt die Sturzflieger.$B$BBringt mir ihre Kiele und macht Eurem Klan Ehre.$B$BSturzflieger findet man überall in Mulgore, aber Ihr müsst sehr scharfe Augen haben, um sie zu entdecken, und Ihr braucht flinke Hufe, um sie zu fangen.",
    ["O"] = "Bringt Harken Windtotem im Dorf der Bluthufe 8 Trophäensturzfliegerkiele.",
    ["T"] = "Sturzfliegerjagd",
  },
  [762] = {
    ["D"] = "Ragnaros persönlich schickte den Gesandten Infernus zur Festung Angor, damit er dort die Suche der Zwerge der Schattenschmiede nach in Uldaman verschollenen Artefakten organisiert.$B$BUnser Volk hat bereits einen enormen Verlust erlitten, aber in letzter Zeit hat sich die Lage offensichtlich noch verschlimmert. Ich wage kaum zu schätzen, wie viele Artefakte sie finden und gegen Eisenschmiede einsetzen könnten.$B$BUnser Ziel ist einfach, $C. Kehrt ins Ödland zurück und beseitigt den Botschafter Infernus. Sobald die Bedrohung aus der Welt geschafft ist, kehrt mit einem Beweis Eures Sieges zu Berater Belgrum zurück.$B$BEs liegt jetzt in Euren Händen.",
    ["O"] = "Tötet den Botschafter Infernus in der Festung Angor und bringt Berater Belgrum in Eisenschmiede einen Beweis, der bestätigt, dass der Botschafter tot ist.",
    ["T"] = "Ein Botschafter des Bösen",
  },
  [763] = {
    ["D"] = "Damit Ihr die Riten der Erdenmutter fortsetzen könnt, müsst Ihr noch zwei weitere Prüfungen bestehen. Es ist an der Zeit, Eure Erfahrungen zu erweitern, $N.$B$BReist ins Dorf der Bluthufe und sprecht mit Häuptling Baine Bluthuf, dem Sohn von Cairne. Dort könnt Ihr Eure Reise fortsetzen und Euch die Anerkennung der Ältesten von Donnerfels verdienen.$B$BBringt Baine dieses Totem. Er wird meine Schnitzereien erkennen und Euch helfen.$B$BFolgt der Straße, die aus dem Camp Narache herausführt, und beeilt Euch. Schweift nicht von der Straße ab, Ihr könntet Euch sonst verirren.",
    ["O"] = "Bringt Baine Bluthuf im Dorf der Bluthufe Falkenwinds Totem. Folgt der Straße, die aus dem Camp Narache herausführt.",
    ["T"] = "Riten der Erdenmutter",
  },
  [764] = {
    ["D"] = "Das erklärt auch die Aufstockung von Angestellten und Ausrüstung der Venture Company, die wir in Mulgore gesehen haben. Diese Goblins... ihre Firma wächst viel zu schnell, als dass es gut für sie wäre. Es heißt doch, zeigt man einem Goblin etwas, kann man gleich im Hintergrund die Registrierkasse klingeln hören.$B$BGierige kleine Teufel. Bedauerlich für ihre geschäftlichen Vorhaben, aber wir Tauren können es nicht zulassen, dass sie ihre Arbeiten hier in unserem Land durchführen. Begebt Euch zu ihrer Mine nordöstlich der Überfallenen Karawane und schickt ihnen eine Nachricht.",
    ["O"] = "Tötet für Morin Wolkenpirscher in der Nähe des Dorfs der Bluthufe 14 Tagelöhner der Venture Co. und 6 Vorsteher der Venture Co.",
    ["T"] = "Die Venture Company",
  },
  [765] = {
    ["D"] = "Laut der Dokumente, die Ihr von der Karawane geborgen habt, hat die Venture Company viele Pläne parat, um zu versuchen, uns aus unserer Heimat hier zu vertreiben und damit freie Bahn zur Ausbeutung des Landes zu haben.$B$BIch bin entsetzt und es stößt mich ab, auf welches Niveau man sich herablassen kann, um seine Ziele durchzusetzen. Ich will, dass der führende Kopf hinter ihren Plänen vernichtet wird. Er heißt Vorsteher Zischelspross, Ihr findet ihn in der Mine der Venture Co. östlich der Überfallenen Karawane. Bringt mir sein Klemmbrett, vielleicht finden wir damit ja etwas Nützliches heraus.",
    ["O"] = "Tötet Vorsteher Zischelspross und bringt Morin Wolkenpirscher in der Nähe des Dorfs der Bluthufe dessen Klemmbrett.",
    ["T"] = "Vorsteher Zischelspross",
  },
  [766] = {
    ["D"] = "Mein Großvater erzählte mir Geschichten von seinen Kämpfen gegen einen Schreiter namens Mazzranach. Er erzählte mir von seinen dämonischen roten Augen, rasiermesserscharfen Krallen und seinem giftigen Biss.$B$BWie der Zufall so spielt, stieß ich auf einer meiner Reisen durch die Ebene auf ihn und trug eine üble Bisswunde an meiner Schulter davon. Er war so Furcht erregend, wie ihn mein Großvater beschrieben hatte. Ich werde ein paar nur sehr schwer zu findende Körperteile von Tieren benötigen, um die Infektion durch seinen Biss zu säubern: ein Herz eines Präriewolfs, einen Knochen eines Flachlandpumas, eine Schuppe eines Ebenenschreiters und einen Muskelmagen eines Sturzfliegers.$B$BSchnell, Zeit ist hier entscheidend.",
    ["O"] = "Bringt Maur Regenrufer im Dorf der Bluthufe ein Herz eines Präriewolfs, einen Knochen eines Flachlandpumas, eine Schuppe eines Ebenenschreiters und einen Muskelmagen eines Sturzfliegers.",
    ["T"] = "Mazzranache",
  },
  [767] = {
    ["D"] = "Der Ritus der Vision, einer der Riten der Erdenmutter, wird Euch helfen, den Respekt der Ältesten von Donnerfels zu erlangen.$B$BUnser Volk hat gelernt, dass das Land unser allerheiligster Versorger ist. Um am Ritus der Vision teilzunehmen, müsst Ihr mit dem geistigen Anführer des Dorfes sprechen, Zarlman Zwillingsmond.",
    ["O"] = "Sprecht im Dorf der Bluthufe mit Zarlman Zwillingsmond.",
    ["T"] = "Ritus der Vision",
  },
  [768] = {
    ["D"] = "Seid gegrüßt. Ihr habt so einen Glanz in den Augen; Ihr brennt darauf, in die Ebene zurückzukehren, um zu jagen. Möget Ihr Eurem Klan Ehre machen!$B$BIch arbeite mit den Häuten von Wildtieren und fertige daraus Kleidung und Rüstung für die Leute von Donnerfels.$B$BWenn Ihr Wildtiere jagt und mir ihre Häute bringt, fertige ich Euch etwas daraus.",
    ["O"] = "Bringt Veren Weitschreiter in Donnerfels 12 Stücke leichtes Leder.",
    ["T"] = "Leder sammeln",
  },
  [769] = {
    ["D"] = "Die Kodowildtiere von Mulgore sind stark und robust - Charaktereigenschaften, die wir sehr schätzen. Wenn Ihr in der Lederverarbeitung bewandert seid und wissen möchtet, wie man eine Kodobalgtasche fertigt, bringt mir die Materialien, die ich für mein Handwerk brauche.$B$BDann teile ich mein Wissen mit Euch.",
    ["O"] = "Bringt Veren Weitschreiter in Donnerfels 4 Einheiten leichtes Leder und 4 grobe Fäden.",
    ["T"] = "Kodobalgtasche",
  },
  [770] = {
    ["D"] = "Der struppige Balg von Geistheuler trägt immer noch die schreckliche Wunde, die der Wolf im Kampf gegen die Brennende Legion davongetragen hat.$B$BVielleicht weiß jemand im Sonnenweidenlager, wie Geistheuler starb...",
    ["O"] = "Sucht jemanden, der etwas über Geistheuler weiß.",
    ["T"] = "Der Dämonennarbenumhang",
  },
  [771] = {
    ["D"] = "Um den Ritus der Vision zu bestehen, müsst Ihr die Zutaten sammeln, die für das Wasser der Seher benötigt werden:$B$BIch brauche Quellsteine, die man in der Nähe von Brunnen in Mulgore findet. Diese kombiniere ich mit Bernsteinkorn, das von den mächtigen Bäumen in unserem Land fällt.$B$BDiese Reagenzien enthalten magische Elemente, und wenn sie erhitzt und vor dem Stammesfeuer verzehrt werden, bekommt man eine Vision. Folgt ihr, dann wird sie Euch zum nächsten Schritt dieser heiligen Suche führen.",
    ["O"] = "Sammelt 2 Quellsteine und 2 Bernsteinkörner und bringt sie Zarlman Zwillingsmond ins Dorf der Bluthufe.",
    ["T"] = "Ritus der Vision",
  },
  [772] = {
    ["D"] = "Hier ist es nun, das Wasser der Seher.$B$BWenn Ihr bereit seid, trinkt das Wasser in der Nähe des Stammesfeuers. Nachdem Ihr das heilige Wasser getrunken habt, wird es einen Moment dauern, bis die Vision vor dem Feuer erscheint.$B$BDann liegt es bei Euch, ihr zu Eurer Bestimmung zu folgen...",
    ["O"] = "Trinkt vor dem Stammesfeuer im Dorf der Bluthufe das Wasser der Seher und folgt der Vision, sobald sie erscheint.",
    ["T"] = "Ritus der Vision",
  },
  [773] = {
    ["D"] = "Um die Anerkennung der Ältesten von Donnerfels zu verdienen, müsst Ihr als Nächstes den Ritus der Weisheit absolvieren.$B$BJetzt, da Ihr den Ritus der Vision bestanden habt, geben Euch die Geister der Ahnen der Teufelsfelsen den Segen unserer Vorfahren. Nur jene, die von dem Wasser der Seher getrunken haben, können diesen Segen erhalten.$B$BReist von Donnerfels gen Osten zu den Teufelsfelsen und sprecht mit dem Geist der Ahnen, $N.",
    ["O"] = "Reist von Donnerfels gen Osten zu den Teufelsfelsen und sprecht mit dem Geist der Ahnen.",
    ["T"] = "Ritus der Weisheit",
  },
  [774] = "_",
  [775] = {
    ["D"] = "Jetzt wird man Euch in unserer großen Stadt respektvoll gegenübertreten. $GEin:Eine:r; $R, $Gder:die; würdig ist, die Riten der Erdenmutter abzuschließen, hat diese Ehre verdient.$B$BSucht unseren edlen Anführer Cairne Bluthuf auf, um Eure letzte Aufgabe zu erhalten. Er residiert auf der höchsten Anhöhe in Donnerfels.",
    ["O"] = "Sprecht in Donnerfels mit Cairne Bluthuf.",
    ["T"] = "Reise nach Donnerfels",
  },
  [776] = {
    ["D"] = "Ihr habt die Riten der Erdenmutter absolviert und Euch einen Platz in Donnerfels verdient.$B$BUm den Platz zu verteidigen, den Ihr Euch so hart erarbeitet habt, müsst Ihr allerdings auch weiterhin Euren Wert für unser Volk beweisen.$B$BWir sind Jäger, $N. Unten in Mulgore gibt es einen mächtigen Kodo namens Arra\'chea. Zeigt mir Euer Können im Fährtenlesen und Jagen, indem Ihr mir das Horn von Arra\'chea bringt.",
    ["O"] = "Cairne Bluthuf in Donnerfels möchte, dass Ihr ihm das Horn von Arra\'chea bringt.",
    ["T"] = "Riten der Erdenmutter",
  },
  [778] = {
    ["D"] = "Haha! Fertig! Die Fesseln sind fertig!$B$BDer Moment ist gekommen, Lucien. Der Moment ist gekommen, Servo!$B$BSeid Ihr bereit, Zeuge der perfekten Verschmelzung von Alchemie, Ingenieurskunst und des Arkanen zu werden, $N?$B$BIch werde jetzt einen mächtigen Elementar beschwören. Das ist heute ein Bahn brechender Tag für uns alle.",
    ["O"] = "Besiegt den Wächter der Fam\'retor und bringt Lotwils Fesseln der Elementarbindung zu Lotwil Veriatus zurück.",
    ["T"] = "Das wird schwierig",
  },
  [780] = {
    ["D"] = "Die Kampfeber der Dornrankenklamm im Osten sind in unser Stammesjagdterritorium eingedrungen. Sie wurden von den Borstennackenstachelebern, mit denen wir uns im Krieg befinden, abgerichtet, niederträchtig zu sein.$B$BTötet diese elenden Kreaturen und bringt mir ein paar ihrer Schnauzen und Flanken, damit wir daraus einen Eintopf für unsere Jungen zubereiten können.",
    ["O"] = "Grull Falkenwind im Camp Narache möchte, dass Ihr für ihn Kampfeber tötet und ihm 8 Kampfeberschnauzen und 8 Kampfeberflanken bringt.",
    ["T"] = "Die Kampfeber",
  },
  [781] = {
    ["D"] = "Nachdem Ihr die Karte sorgfältig studiert habt, erkennt Ihr, dass Ihr die Pläne für eine ausgeweitete Belagerung von Camp Narache entdeckt habt, die von dem Kriegshäuptling der Borstennacken entworfen wurden.$B$BHäuptling Falkenwind kann mit dieser Information sicher einiges anfangen!",
    ["O"] = "Bringt die Angriffspläne der Borstennacken zu Häuptling Falkenwind in Camp Narache.",
    ["T"] = "Angriff auf Camp Narache",
  },
  [782] = {
    ["D"] = "Durch die Allianz der Horde mit dem Großdrachen Todesschwinge während des zweiten Krieges wissen wir, dass zwei ihrer Offiziere, Schattenbringer und Hematus, in der Lethlorklamm weit im Osten eingesperrt sind. Wir müssen unsere verderbten Verbündeten von damals zum Stillschweigen bringen, um die Ehre der Horde wiederherzustellen!$B$BWir hatten Tho\'grun und seine Gruppe Ogersöldner angeheuert, damit sie uns helfen, das Zeichen der Erde in die Hände zu bekommen, ein Schlüssel, mit dem das Gefängnis des Drachen geöffnet werden kann. Tho\'grun hat uns jedoch verraten, uns überrumpelt und ihn sich selbst angeeignet. Begebt Euch zum Camp Boff und holt ihn zurück!",
    ["O"] = "Tötet Tho\'grun und bringt Gorn in Kargath das Zeichen der Erde.",
    ["T"] = "Zerbrochene Allianzen",
  },
  [783] = {
    ["D"] = "Ich hoffe, Ihr habt Euren Gürtel festgezurrt, $C, denn es gibt hier in Nordhain viel Arbeit.$B$BUnd ich meine damit nicht Landarbeit.$B$BDie Wachen von Sturmwind haben alle Hände voll zu tun, um den Frieden zu bewahren; viele von uns weilen in fernen Ländern und viele Bedrohungen rücken immer näher. Deshalb rekrutieren wir jeden, der gewillt ist, seine Heimat zu verteidigen. Und die Allianz.$B$BFalls Ihr hier seid, um dem Aufruf zu folgen, sprecht mit meinem Vorgesetzten, Marschall McBride. Er ist in der Abtei hinter mir.",
    ["O"] = "Sprecht mit Marschall McBride.",
    ["T"] = "Die Bedrohung von innen",
  },
  [784] = {
    ["D"] = "Angeführt von Admiral Prachtmeer drangen die Menschen von Kul Tiras nach Durotar vor und brachen damit den Pakt, den der Kriegshäuptling vor Jahren mit Jaina Prachtmeer geschlossen hatte, um Archimonde zu besiegen.$B$BDie Menschenaggressoren wurden zurückgeschlagen und Burg Tiragarde fiel. Kürzlich jedoch hat die Reserve des Admirals unter der Führung von Leutnant Benedict die Burg zurückerobert und stellt erneut eine Bedrohung für unser Heimatland dar. Diese Menschen haben keinen Respekt vor Diplomatie.$B$BBeweist Eure Ehre und reist gen Süden zur Burg Tiragarde, um die Menscheneindringlinge zu eliminieren.",
    ["O"] = "Tötet 10 Matrosen von Kul Tiras, 8 Marinesoldaten von Kul Tiras sowie Leutnant Benedict und kehrt dann zu Gar\'Thok in Klingenhügel zurück.",
    ["T"] = "Vernichtet die Verräter",
  },
  [785] = {
    ["D"] = "Thrall ist ein äußerst weiser und edler Anführer. Seine Beharrlichkeit auf dem Schlachtfeld wird nur von seinem diplomatischen Feingefühl übertroffen.$B$BDie Trolle der Dunkelspeere haben sich als unschätzbare Verbündete für die Orcs erwiesen. Wir haben aus Loyalität und Ehrgefühl einen Schutzpakt mit ihnen geschlossen.$B$BDer Kriegshäuptling hat Befehle ausgegeben, dafür zu sorgen, dass unsere Dunkelspeerfreunde in Sen\'jin nicht unmittelbar in Gefahr sind. Einer ihrer verlässlichsten Späher, Lar Lauerzahn, hat ein wachsames Auge auf ihre Westflanke.$B$BSucht Lar und unterstützt ihn notwendigenfalls.",
    ["O"] = "Sucht Lar Lauerzahn an der Westflanke von Sen\'jin auf.",
    ["T"] = "Eine strategische Allianz",
  },
  [786] = {
    ["D"] = "Sprecht leiser, $C. Die Zentauren der Kolkar liegen gleich hinter jener Kuppe im Westen der Kolkarklippe.$B$BWährend ihres Angriffs letzte Nacht schlich ich mich in ihr Dorf und fand heraus, dass diese dreckigen Bestien einen dreistufigen Angriff auf die Trolle und Orcs von Durotar geplant hatten.$B$BIhre Invasion muss verhindert werden. Vielleicht könnt Ihr die Streitmacht zusammenziehen, die nötig ist, um die Kolkarklippe zu unterwandern und deren Angriffspläne zu vernichten.$B$BZuletzt sah ich, dass Sie sich auf drei ihrer Anführer verteilten.",
    ["O"] = "Lar Lauerzahn außerhalb von Sen\'jin möchte, dass Ihr die 3 Sets der Angriffspläne zerstört, die innerhalb der Kolkarklippe verwahrt werden.",
    ["T"] = "Vereitelung der Kolkaraggression",
  },
  [787] = {
    ["D"] = "Throm-ka, $C. Ich bin Etrigg, von Thrall beauftragt, die Ausbildung der Rekruten zu beaufsichtigen.$B$BDie Horde ist nicht mehr das, was sie einmal war. Es gab eine Zeit, da ich die Horde verließ, enttäuscht durch den wachsenden Einfluss der machthungrigen Marionetten der Brennenden Legion. Während meines Exils wurde ich von einer Gruppe Menschen gefangen genommen, aber dann von dem Kriegshäuptling gerettet. Er erzählte mir von seiner Vision einer Horde, frei von dämonischen Einflüssen, von den Schamanen geleitet, und so kehrte ich zurück.$B$BGornek wird weitere Anweisungen für Euch haben.",
    ["O"] = "Meldet Euch bei Gornek beim Höhlenbau.",
    ["T"] = "Die neue Horde",
  },
  [788] = {
    ["D"] = "Als Erstes werden wir Euer Rückgrat etwas kräftigen. Ich könnte Euch ins Brachland schicken, um Kodos zu jagen, aber mal ehrlich, lebend seid Ihr uns nützlicher als tot.$B$BIch glaube, die scheckigen Eber nördlich von hier wären genau das Richtige für Euch.",
    ["O"] = "Tötet 10 scheckige Eber und kehrt dann zu Gornek beim Höhlenbau zurück.",
    ["T"] = "Erste Erfahrungen",
  },
  [789] = {
    ["D"] = "Sowohl mächtige Krieger als auch tollpatschige Anfänger fallen dem Giftstachel der Skorpiden zum Opfer. Nordwestlich von hier findet Ihr eine größere Ansammlung Skorpiden. Bringt mir 10 ihrer Schwänze als Beweis für Eure Leistungsfähigkeit im Kampf.$B$BDas Gegengift für ihren Stich wird aus genau dem Gift, das aus ihren Stacheln extrahiert wurde, gewonnen. Wir haben immer einen großen Vorrat an Gegengift für dieses Skorpidgift, um Grünschnäbel wie Euch damit zu heilen...$B$BAber Ihr werdet das doch bestimmt nicht brauchen, oder?",
    ["O"] = "Bringt 10 Skorpidarbeiterschwänze zu Gornek im Höhlenbau.",
    ["T"] = "Stich des Skorpiden",
  },
  [790] = {
    ["D"] = "$C! Ich dachte schon, ich müsste hier draußen sterben und niemand würde es bemerken. Auf der Jagd nach den Skorpiden des Tals stieß ich auf ein besonders heimtückisches Exemplar. Ich stürzte mich auf ihn und konnte einer seiner Klauen einen ordentlichen Draufschlag versetzen, bevor sie sich um mein Bein schloss.$B$BIch hatte jedoch nicht mit seinem Stecher gerechnet, den er tief in meine Brust trieb, mit dem er mein Fleisch zerschnitt und mir einen großen Blutverlust verursachte. Bitte, Ihr müsst den Skorpid für mich töten! Meine Ehre darf nicht besudelt werden! Da oben auf dem Plateau im Süden habe ich gegen ihn gekämpft.",
    ["O"] = "Tötet Sarkoth und bringt seine Klaue zu Hana\'zua.",
    ["T"] = "Sarkoth",
  },
  [791] = {
    ["D"] = "Das Alter hat mich nutzlos für die Schlacht gemacht. Jetzt mache ich mich eben auf andere Weise nützlich.$B$BVon diesem Aussichtspunkt aus achte ich auf Eindringlinge. Während unsere Kraft hier zunimmt, muss ich mein Signalhorn immer seltener benutzen.$B$BUm mir die Zeit zu vertreiben, fertige ich Dinge an, die den jüngeren und fähigeren Kriegern bei der Verteidigung unserer Heimat helfen können.$B$BFür Euch könnte ich eine Tasche für Eure Habseligkeiten anfertigen. Falls ein solcher Gegenstand Euch nützlich sein könnte, bringt mir etwas Segeltuch, ein Material, das unter Menschen und Zentauren verbreitet ist.",
    ["O"] = "Furl Hohnbraue im Wachturm von Klingenhügel möchte 8 Segeltuchreste haben.",
    ["T"] = "Akzeptiert Euer Schicksal",
  },
  [792] = {
    ["D"] = "Ich denke, das Tal der Prüfungen wird Euch viel lehren, $C.$B$BMan hat mich eigentlich ins Tal geschickt, um Euch anzuleiten, aber ich habe dort eine sich ausbreitende Besudelung entdeckt...$B$BEine Gruppe, die sich die \'Brennende Klinge\' nennt, hat hier im Tal der Prüfungen einen Koven für ihre Mitglieder. Sie lungern im Nordosten in einer Höhle herum und ihre üblen Familiare kriechen daraus hervor, um Chaos anzurichten.$B$BAls erste Aktion gegen die Brennende Klinge bitte ich Euch, diese Familiare zu vernichten. Tötet so viele Ihr könnt, und falls Ihr überlebt, kehrt zu mir zurück.",
    ["O"] = "Tötet 12 üble Familiare.$B$BKehrt dann zu Zureetha Fernblick beim Höhlenbau zurück.",
    ["T"] = "Üble Familiare",
  },
  [793] = {
    ["D"] = "Das Siegel der Erde wird von drei Runensteinen gebildet, die in der Lethlorklamm auf Säulen aus Diamant, Opal und Amethyst liegen. Ihr könnt die Runensteine mithilfe des Zeichens der Erde entfernen und so das Siegel der Erde brechen.$B$BSeid jedoch vorsichtig, $N, die Schwarzdrachen Schattenbringer und Hematus sollte man nicht unterschätzen. Tötet sie und kehrt mit ihren Bindungen zu mir zurück.$B$BUm die Klamm zu erreichen, müsst Ihr in den östlichsten Teil des Ödlands reisen. Lasst jedoch Vorsicht walten, da die Klamm zahllose Drachen beherbergt.",
    ["O"] = "Benutzt das Zeichen der Erde, um die Säulen aus Diamant, Opal und Amethyst zu aktivieren und die Runensteine zu holen.$B$BLegt die Runensteine auf das Siegel der Erde, um Schattenbringer und Hematus zu befreien.$B$BTötet sie und bringt dann Gorn Schattenbringers Bindung, Hematus\' Ketten und das Zeichen der Erde.",
    ["T"] = "Zerbrochene Allianzen",
  },
  [794] = {
    ["D"] = "Durch meine hellseherischen Kräfte weiß ich, dass im Koven der Brennenden Klinge ein Gegenstand der Macht verborgen ist, der durch Bestien und schwarze Magie bewacht wird.$B$BEr wird das Medaillon der Brennenden Klinge genannt und Eure nächste Aufgabe besteht darin, es zu finden und aus dem Koven zu entfernen.$B$BSeid jedoch vorsichtig, denn das Medaillon könnte im Besitz eines Agenten der Brennenden Klinge sein, und falls dem so ist, ist die Macht dieses Agenten weitaus stärker als die der Familiare, auf die Ihr bereits gestoßen seid.$B$BGeht, $N. Ihr findet den Koven in einer Höhle im Norden und Westen.",
    ["O"] = "Bringt Zureetha Fernblick beim Höhlenbau das Medaillon der Brennenden Klinge.",
    ["T"] = "Medaillon der Brennenden Klinge",
  },
  [796] = "_",
  [797] = "_",
  [798] = "_",
  [799] = "_",
  [800] = "_",
  [801] = "_",
  [802] = "_",
  [803] = "_",
  [804] = {
    ["D"] = "Was Ihr für mich getan habt, gibt mir Zuversicht. Ich darf nicht einfach so gehen! Ich muss durchhalten!$B$BOhne Hilfe schaffe ich jedoch den Weg zurück zum Höhlenbau nicht. Bitte, $N, kehrt zum Höhlenbau zurück und berichtet Gornek von meiner Lage. Vielleicht kann er mir helfen.",
    ["O"] = "Informiert Gornek beim Höhlenbau über Hana\'zuas Notlage.",
    ["T"] = "Sarkoth",
  },
  [805] = {
    ["D"] = "Eure Prüfungen durch die Brennende Klinge sind beendet... jedenfalls hier im Tal. Ich möchte aber, dass Ihr von Euren Funden berichtet.$B$BBegebt Euch in das Trolldorf Sen\'jin und sucht nach Meister Gadrin. Sen\'jin liegt östlich hinter dem Tal und dann geht es an der Weggabelung nach rechts.$B$BBerichtet Gadrin von der Brennenden Klinge und dass sie das Tal der Prüfungen erreicht haben. Bringt von ihm in Erfahrung, ob sie Sen\'jin schon erreicht haben.$B$BGeht, $N, und beeilt Euch. Ich fürchte, dass das Böse, das wir im Koven der Brennenden Klinge entdeckt haben, nur der Vorbote eines viel größeren Übels ist...",
    ["O"] = "Sprecht mit Meister Gadrin in Sen\'jin.",
    ["T"] = "Bericht erstatten in Sen\'jin",
  },
  [806] = {
    ["D"] = "Wir können der Brennenden Klinge nicht erlauben, in Durotar Fuß zu fassen! Wir müssen sie vernichten, bevor das Böse zu schwären beginnt!$B$BIch habe selbst Nachforschungen unternommen und herausgefunden, dass ein Hexenmeister der Brennenden Klinge, der Goblin Zischel Düstersturm, nahe dem Donnergrat im Nordwesten kampiert. Er und seine Kultdiener verbreiten dort Chaos.$B$BSucht und besiegt Zischel und bringt mir seine Klaue!",
    ["O"] = "Bringt Zischels Klaue zu Orgnil Narbenseele in Klingenhügel.",
    ["T"] = "Dunkle Stürme",
  },
  [808] = {
    ["D"] = "Ich höre die Stimme meines Bruders Minshina, der in meinen Träumen zu mir spricht.$B$BEr wurde von Zalazane, dem Hexenmeister auf den Echoinseln im Osten, gefangen genommen. Und er ist tot.$B$BAber der Tod bedeutet nicht Freiheit für meinen Bruder. Minshinas Geist ist durch Zalazanes Magie in seinem eigenen Schädel gefangen. In meinen Träumen sehe ich ihn zusammen mit weiteren Schädeln in einem Kreis der Macht auf der Größten der Echoinseln. Solange er dort bleibt, ist die Seele meines Bruders verdammt.$B$BBitte, $N. Sucht den Kreis und bringt Minshinas Schädel zurück. Bringt ihn mir.$B$BBefreit ihn!",
    ["O"] = "Holt Minshinas Schädel aus dem Kreis der Macht auf den Echoinseln.$B$BBringt ihn Meister Gadrin in Sen\'jin.",
    ["T"] = "Minshinas Schädel",
  },
  [809] = {
    ["D"] = "Seit dem letzten großen Krieg, in dem die Brennende Legion besiegt wurde, suche ich nach den Quellen dämonischer Verderbnis innerhalb der orcischen Gesellschaft. Das Halsband, das Ihr mir brachtet, bestätigt meine Befürchtungen.$B$BEs gehört der Brennenden Klinge, einem Kult, der sich um einen Gegenstand dämonischer Macht schart. Er wird \'Dämonensaat\' genannt und im Brachland hoch über dem Glutnebelgipfel aufbewahrt. Er muss zerstört werden!$B$BGeht zur Fernwacht an der Grenze zum Brachland im Westen und sprecht dort mit meinem Assistenten Ak\'Zeloth. Er wird Euch weitere Anweisungen geben.",
    ["O"] = "Sprecht im Brachland mit Ak\'Zeloth.",
    ["T"] = "Ak\'Zeloth",
  },
  [810] = "_",
  [811] = "_",
  [812] = {
    ["D"] = "$N, Ihr hättet den Zeitpunkt nicht besser wählen können. Ich hoffe nur, dass ich Euch auch zu Eurer Eile beglückwünschen kann.$B$BWährend eines Kampfes mit ein paar Siechstachelskorpiden war ich unvorsichtig und ich wurde von einem von ihnen heftig gestochen. Während wir uns unterhalten, spüre ich, wie sein Gift mich schwächt. Bei dieser Geschwindigkeit bleibt mir vielleicht noch eine Stunde zu leben. Aber selbst dazu brauche ich Eure Hilfe...$B$BKor\'ghan in Orgrimmar weiß, wie man das Gegengift herstellt. Sucht ihn... und zwar schnell, $N. Ich werde nicht mehr lange durchhalten. Er sollte sich in der Kluft der Schatten befinden.",
    ["O"] = "Sucht Kor\'ghan in Orgrimmar und besorgt das Siechstachelgegengift. Bringt das Gegengift dann zu Rhinag an der nordwestlichen Grenze von Durotar.",
    ["T"] = "Heilmittel benötigt",
  },
  [813] = {
    ["D"] = "Siechstachelskorpide sind eine der tödlichsten Skorpidarten in ganz Durotar. Es war dumm von Rhinag, sie zu jagen, ohne etwas von dem Gegengift mit sich zu führen, aber momentan ist eine ordentliche Standpauke das Letzte, was er benötigt.$B$BBringt mir ein paar Giftbeutel von den Siechstachelskorpiden in der Nähe des Eingangs nach Orgrimmar, dann stelle ich ein Gegengift für ihn her.$B$BBeeilt Euch, $N. Einer der Unseren braucht Eure Hilfe.",
    ["O"] = "Bringt Kor\'ghan in Orgrimmar 4 Giftbeutel eines Siechstachelskorpiden.",
    ["T"] = "Suche nach dem Gegengift",
  },
  [814] = "_",
  [815] = {
    ["D"] = "Pah! Jetzt hätte ich doch beinahe vergessen, Frühstück zu machen.$B$B$N, setzt Euch in Bewegung und bringt mir ein paar Schwanzpeitschereier. Ich brauche mindestens drei davon, wenn ich genug für die morgige Mahlzeit haben möchte.$B$BDie Blutkrallenschwanzpeitscher können ganz schön wild sein und beschützen ihre Eier bis in den Tod. Manchmal vergraben sie ihre Eier allerdings zu tief in der Erde, um sie sicher ausgraben zu können, aber reist doch zu den Echoinseln südöstlich von Sen\'jin, dort findet man sie gewöhnlich überall auf den Inseln verteilt.",
    ["O"] = "Bringt dem Koch Torka in Klingenhügel 3 Schwanzpeitschereier.",
    ["T"] = "Zerbrecht ein paar Eier",
  },
  [816] = {
    ["D"] = "$N, bitte, könnt Ihr mir helfen? Mein Sohn Kron ist vor Tagen auf die Jagd gegangen und immer noch nicht zurück. Er wollte nach Westen in Richtung des Brachlandes, um dort am Ufer des Südstroms Krokilisken zu jagen. Ich fürchte das Schlimmste.$B$BSo stark wie er ist, hat ihn sein starrköpfiger Stolz doch immer wieder in Schwierigkeiten gebracht. Ich habe ihn gewarnt, dass die Krokilisken mächtig und heimtückisch sind. Er wurde wütend und stürmte davon.$B$BFalls Ihr ihn nicht finden könnt, so bringt mir doch wenigstens einen Hinweis darauf, was aus ihm geworden ist... und wenn Ihr dazu jedem einzelnen Krokilisken an den Ufern des Südstroms den Bauch aufschlitzen müsst.",
    ["O"] = "Bringt Misha Tor\'kren auf dem Bauernhof nordwestlich von Klingenhügel einen Hinweis auf Krons Schicksal.",
    ["T"] = "Verloren, aber nicht vergessen",
  },
  [817] = {
    ["D"] = "Viele der Bälge, die wir benutzen, stammen von den Durotartigern, $N. Decken, Rüstungen, Zelte: Es gibt viele Gründe, warum wir Jagd auf die Wildtiere machen, und ebenso viele Gründe, warum wir sie leben lassen.$B$BDie Zeit ist gekommen, dass wir den Bestand dezimieren. Wir werden immer mehr, und unser Bedarf übersteigt bald unsere Bestände. Ich brauche mehr Bälge, wenn ich geeignete Dinge für unser Volk anfertigen möchte.$B$BBringt mir 4 Durotartigerfelle, dann werde ich Euch belohnen. Ihr findet sie auf den Inseln südlich von hier.",
    ["O"] = "Bringt Vel\'rin Fang in Sen\'jin 4 Durotartigerfelle.",
    ["T"] = "Praktische Beute",
  },
  [818] = {
    ["D"] = "Meine Augen sind zwar nicht mehr so scharf, aber ich kann immer noch gut genug sehen. Immer öfter muss ich mich auf meine Alchemiefertigkeiten verlassen, um mir bei Dingen, die mir früher leicht fielen, nun mit Magie zu helfen. Ich weigere mich jedoch, einen Lehrling einzustellen - bislang ist mir noch kein Troll oder Orc begegnet, der sich als würdig genug erwiesen hätte.$B$BSeid Ihr dessen würdig? Ja, natürlich seid Ihr das... natürlich glaubt Ihr, dass Ihr es seid.$B$BIch brauche ein paar Dinge. Würdet Ihr sie für mich besorgen?$B$BIch brauche intakte Makruraaugen und Phiolen mit Kriechersekret. Ihr findet das überall in Durotar, wo es Makruras oder Kriecher gibt. Wir sehen uns bald wieder.",
    ["O"] = "Bringt Meister Vornal in Sen\'jin 4 intakte Makruraaugen und 8 Phiolen Kriechersekret.",
    ["T"] = "Ein geistvolles Getränk",
  },
  [819] = {
    ["D"] = "Auf einem kleinen Schild an einem Ende des Fässchens steht:$B$B\"Chen Sturmbräu -$B$BMöge es Euren Geist erheben und Euch Mut machen.\"",
    ["O"] = "Sucht jemanden, der etwas über Chens leeres Fässchen weiß.",
    ["T"] = "Chens leeres Fässchen",
  },
  [821] = {
    ["D"] = "Möchtet Ihr eine Kostprobe von Chens Namensvetter? Ahh, das Sturmbräu ist ein gehaltvolles Bier. Chen gab das Rezept an meinen Mentor weiter und mein Mentor gab es mir. Ich möchte, dass Ihr mir ein paar Dinge besorgt, aber eins möchte ich Euch sagen: Der Kick ist es wert.$B$BBringt mir 5 Savannenlöwenhauer von beliebigen Savannenlöwen, 5 Ebenenschreiternieren von beliebigen Ebenenschreitern und 1 Donnerechsenhorn von einer Sturmschnauze. Das sollte bestens funktionieren.$B$BIhr findet diese Zutaten überall im Brachland.",
    ["O"] = "Bringt Braumeister Drohn in Ratschet 5 Savannenlöwenhauer, 5 Ebenenschreiternieren und 1 Donnerechsenhorn.",
    ["T"] = "Chens leeres Fässchen",
  },
  [822] = {
    ["D"] = "Gutes Zeug, dieses Sturmbräu, was?$B$BIch habe noch ein Rezept, das ich von Chen gelernt habe. Hättet Ihr Interesse? Die Herstellung dauert nicht lange und es könnte Euch nützlich sein, wenn Ihr Eure Abenteuer fortsetzen wollt.$B$BBringt mir 5 Blitzschlagdrüsen einer beliebigen Sturmechse, 1 Donnerfalkenspeicheldrüse von großen Donnerfalken und eine Kodoleber einer beliebigen Kodoart des Brachlandes.$B$BWie ich sagte, das Zeug hat es in sich; das ist das Troggbier, das ich kürzlich erwähnte.",
    ["O"] = "Bringt Braumeister Drohn in Ratschet 5 Blitzschlagdrüsen, 1 Donnerfalkenspeicheldrüse und 1 Kodoleber.",
    ["T"] = "Chens leeres Fässchen",
  },
  [823] = {
    ["D"] = "Auch wir in Sen\'jin sind nicht ohne eigene Probleme und wir danken Euch für Eure Hilfe. Aber die Neuigkeiten, die Ihr über die Brennende Klinge bringt, können Probleme für jedermann bedeuten.$B$BIn Klingenhügel ist ein Orc stationiert, Orgnil Narbenseele, der Durotar vor solch Bösem wie der Brennenden Klinge bewacht! Geht zu Orgnil und berichtet ihm von unserer Not sowie von Euren Neuigkeiten über das Tal der Prüfungen. Er wird diese Dinge wissen wollen.$B$BKlingenhügel liegt im Norden.",
    ["O"] = "Sprecht mit Orgnil Narbenseele in Klingenhügel.",
    ["T"] = "Bericht an Orgnil",
  },
  [824] = {
    ["D"] = "Der Irdene Ring ist eine schamanistische Gruppe, die sich dem Studium und dem Erhalt der Elemente widmet. Sie hat bereits mehr über verrückte Alte Götter und uralte Legenden und Lehren vergessen, als die meisten Gelehrten jemals in Erfahrung bringen werden.$B$BZu Eurem Glück kenne ich jemanden hier im Eschental, der Mitglied des Irdenen Rings ist. Geht hinüber zum Außenposten von Zoram\'gar an der Küste des Zoramstrandes und zeigt Je\'neu Sancrea, was Ihr gefunden habt. Wenn überhaupt jemand die Bedeutung dieser Kugel herausfinden kann, dann er.",
    ["O"] = "Bringt die beschmutzte Wasserkugel zu Je\'neu Sancrea in der Tiefschwarzen Grotte.",
    ["T"] = "Je\'neu vom Irdenen Ring",
  },
  [825] = {
    ["D"] = "Einer meiner aufmerksamsten Späher hat mir Kunde gebracht, dass die Trümmer von Prachtmeers Flotte immer noch an der Küste Durotars liegen, gleich östlich von Burg Tiragarde.$B$BEs ist kein Geheimnis, dass die Menschen, die sich mit diesen elenden kleinen, als Gnome bekannte Kreaturen verbündet haben, über reichliches Wissen auf dem Gebiet der Mechanik verfügen. Wir müssen so viel wie möglich über unsere potenziellen Feinde in Erfahrung bringen. Dann kann unser Volk von diesem neuen Wissen profitieren.$B$BSchwimmt durch die Wrackteile, $N, und bringt mir die Werkzeuge der Allianz.",
    ["O"] = "Gar\'Thok von Klingenhügel möchte, dass Ihr 3 Gnomenwerkzeuge aus den Wrackteilen vor der Küste holt.",
    ["T"] = "Aus den Wrackteilen geborgen...",
  },
  [826] = {
    ["D"] = "Der Hexendoktor Zalazane lebt auf den Echoinseln im Osten. Das sind die Inseln, die wir einst Heimat nannten.$B$BVon dort aus entsendet er seine Trolle zum Festland, wo sie unser Volk verhexen und immer mehr von uns in ihren Bann ziehen.$B$BEr muss aufgehalten werden.$B$BBesiegt Zalazane und seine Diener - ehemalige Trolle der Dunkelspeere, die jetzt nicht mehr zu uns gehören. Bringt mir seinen Kopf, dann weiß ich, dass seine Schreckensherrschaft vorbei ist.",
    ["O"] = "Besiegt Zalazane.$B$BTötet 8 Voodootrolle und 8 verhexte Trolle.$B$BBringt Gadrin Zalazanes Kopf.",
    ["T"] = "Zalazane",
  },
  [827] = {
    ["D"] = "$N. Die Brennende Klinge hat die Knochenhöhle östlich von Orgrimmar besetzt. Sie führen in dieser Höhle schlimme Rituale durch und fügen sich durch Senghalsbänder selbst Brandwunden zu.$B$BIch glaube, indem sie diese Halsbänder tragen, bringen die Kultisten sich in Einklang mit dämonischen Mächten. Um das zu bestätigen, benötige ich jedoch ein paar dieser Halsbänder, um sie zu studieren.$B$BGeht zur Knochenhöhle und besorgt ein paar Senghalsbänder von den Kultisten, die Ihr dort findet. Bringt sie mir, dann werde ich ihre Geheimnisse enthüllen.",
    ["O"] = "Besorgt von den Kultisten an der Knochenhöhle ein paar Senghalsbänder.$B$BBringt sie Margoz in seinem Lager.",
    ["T"] = "Die Knochenhöhle",
  },
  [828] = {
    ["D"] = "Margoz, einer unserer Schamanen, weiß mehr über die Verderbnis der Brennenden Klinge. Er sprach von einer Höhle namens Knochenhöhle in den Bergen - gleich vor den Toren Orgrimmars! - dort soll sich eine große Gruppe von Kultisten der Brennenden Klinge eingenistet haben.$B$BBevor Ihr jedoch zur Knochenhöhle geht, sprecht mit Margoz. Er ist weise und sein Rat wird geschätzt.$B$BEr lagert im Nordosten zwischen der Küste und der Staubwindklamm.$B$BFolgt seinem Rat, aber was immer Margoz auch sagt, $N, ich möchte, dass Ihr diese Kultisten vernichtet!",
    ["O"] = "Sprecht mit Margoz.",
    ["T"] = "Margoz",
  },
  [829] = {
    ["D"] = "Die Senghalsbänder, die Ihr mir brachtet, sind mächtige, dämonische Werkzeuge. Ihre Herkunft zu bestimmen liegt jedoch leider außerhalb meiner Künste als Schamane. Wir werden einen Hexenmeister bitten müssen, sie zu studieren.$B$BBringt ein Senghalsband zu Neeru Feuerklinge. Er ist zwar ein talentierter Hexenmeister, doch nutzt er seine Mächte nur, um Dämonen zu bekämpfen, und er behauptet, dass seine Forschungen auf dem Gebiet des Okkultismus gutartiger Natur sind. Ob es stimmt oder nicht, wir brauchen dennoch seine Hilfe gegen den Dämonenkult in Durotar.$B$BIhr findet Neeru in Orgrimmar in der Kluft der Schatten.",
    ["O"] = "Bringt Neeru Feuerklinge in Orgrimmar ein Senghalsband-Muster.",
    ["T"] = "Neeru Feuerklinge",
  },
  [830] = {
    ["D"] = "Ihr öffnet den alten, verwitterten Umschlag und findet darin ein offiziell aussehendes Dokument. Ihr erkennt das Siegel Admiral Prachtmeers.$B$BEs sieht wichtig aus. Vielleicht wäre Gar\'Thok, Kommandant von Klingenhügel, an diesen Informationen interessiert.",
    ["O"] = "Bringt Gar\'Thok in Klingenhügel Admiral Prachtmeers Befehle.",
    ["T"] = "Die Befehle des Admirals",
  },
  [831] = {
    ["D"] = "Menschen kann man nicht trauen. Wir haben immer mit wachsamen Augen an ihrer Seite gekämpft, weil wir wussten, dass sie uns eines Tages verraten würden.$B$BLeider war Admiral Prachtmeers Tod nicht genug, um sein Vermächtnis des Betrugs aufzuhalten. Der Menschenabschaum hatte vor seinem Ableben alles gut durchgeplant.$B$BSeine Herrschaft würde, wie es scheint, nicht einmal mit Benedict enden. Wer weiß, wann die nächste Welle von Prachtmeers Männern an unseren Küsten landen wird.$B$BWir müssen diese Befehle sofort zu Nazgrel in Orgrimmar bringen! Man findet ihn in Thralls Kammer.",
    ["O"] = "Bringt Nazgrel in Thralls Kammer in Orgrimmar Admiral Prachtmeers Befehle.",
    ["T"] = "Die Befehle des Admirals",
  },
  [832] = {
    ["D"] = "<Die Stimme aus dem Anhänger erreicht direkt Euren Verstand.>$B$BGazz\'uz?$B$BGazz\'uz... Bericht!$B$BMan hat von Eurer Entdeckung in der Knochenhöhle gehört. Ihr müsst Euch auf einen Angriff vorbereiten!$B$BGazz\'uz, ich befehle Euch, mir zu antworten! Sagt etwas, sonst werde ich dafür sorgen, dass Neeru Feuerklinge von Eurer Anwesenheit erfährt, dann wird es Euch schlecht ergehen.$B$BVersucht nicht, Eure Fertigkeiten mit denen Neerus zu messen...$B$B... Gazz\'uz, seid Ihr da...?",
    ["O"] = "Bringt Neeru Feuerklinge in Orgrimmar dieses Auge.",
    ["T"] = "Brennende Schatten",
  },
  [833] = {
    ["D"] = "Nur die tapfersten Tauren werden bei den Teufelsfelsen, unserer heiligen Begräbnisstätte, bestattet. Es ist eine Ehre, die jenen großen Kriegern zuteil wird, die dazu beitrugen, Donnerfels zu gründen und zu verteidigen, und jenen, die ihr Leben für das Wohl ihres Stammes und ihrer Häuptlinge gegeben haben.$B$BNun sieht es jedoch so aus, als wäre es einer üblen Bedrohung gelungen, in unser heiliges Land einzudringen. Eine Gruppe Borstennacken verheert das Grabmal und ich bin zu alt und gebrechlich, um sie selbst zu vertreiben.$B$BSie müssen mit Gewalt von dort verjagt werden, $N.",
    ["O"] = "Tötet 8 Eindringlinge der Borstennacken bei den Teufelsfelsen.",
    ["T"] = "Eine heilige Bestattung",
  },
  [834] = {
    ["D"] = "Ich bin Rezlak, einer von Gazlowes Jungs. Boss hat mich geschickt, den Orcs hier in Durotar unter die Arme zu greifen. Soweit läuft alles klar, abgesehen von den Karawanen. Ich schaff\'s einfach nicht, sie zu sichern! Das erschwert mir meinen Job ganz schön, wisst Ihr?$B$BDie letzte Lieferung, meine Güte, die hatten garantiert, dass sie durchkommt! Aber sie wurde von den Staubschwingenharpyien von... wo war das noch... in der Klingenschlucht geklaut.$B$BIch muss dieses Zeug zurückhaben! Folgt dem großen Canyon nach Süden, dort findet Ihr eine Klamm direkt westlich und östlich der Wände.",
    ["O"] = "Beschafft 5 Vorratssäcke.",
    ["T"] = "Wind in der Wüste",
  },
  [835] = {
    ["D"] = "Gazlowe sagte mal zu mir: \"Rezlak. Eines musst du im Leben wissen: Wenn du was richtig gemacht haben willst, dann tu\'s selbst.\"$B$BIch kann es mir nicht leisten, all unsere Karawanen zu verlieren, also muss ich die Sache selbst in die Hand nehmen. Die Staubwindklamm liegt hinter dem Ostteil der Klingenschlucht. Wenn wir alle Harpyien dort töten, ist die Route wieder sicher.$B$BNatürlich gab\'s da noch was, das Gazlowe mir beigebracht hat, und das ist: \"Wenn die Kohle stimmt, kriegt man alles.\" Also, werdet Ihr mir helfen, $N?",
    ["O"] = "Tötet für Rezlak in der Nähe der Staubwindklamm 12 Wilde der Staubschwingen und 8 Sturmhexen der Staubschwingen.",
    ["T"] = "Sicherung der Linien",
  },
  [836] = {
    ["D"] = "Bringt OOX-09/HL in Sicherheit und meldet Euch dann bei Glotz Widrikus in Beutebucht.",
    ["O"] = "Bringt OOX-09/HL in Sicherheit und meldet Euch dann bei Glotz Widrikus in Beutebucht.",
    ["T"] = "Rettet OOX-09/HL!",
  },
  [837] = {
    ["D"] = "Als wir hier ankamen, war ein Großteil des Landes im Besitz der Klingenmähnenstacheleber, die sich als ein ziemlicher Dorn im Auge erwiesen. Durch unsere Anstrengungen konnten wir einen großen Teil von ihnen vertreiben, aber es gibt doch noch welche, die sich in einigen Gebieten verschanzt haben.$B$BDas geht jetzt jedoch schon viel zu lange so. Zu unserer eigenen Sicherheit dürfen wir nicht zulassen, dass sich die Klingenmähnen in unserem Land festsetzen. Ihre Lager befinden sich westlich von hier. Sucht nach dem Dornengestrüpp, dann findet Ihr sie. Heute vertreiben wir sie aus Durotar, morgen vielleicht schon aus Kalimdor.",
    ["O"] = "Tötet für Gar\'Thok in Klingenhügel 4 Stacheleber der Klingenmähnen, 4 Späher der Klingenmähnen, 4 Staubläufer der Klingenmähnen und 4 Schlachtwachen der Klingenmähnen.",
    ["T"] = "Übergriff",
  },
  [838] = {
    ["D"] = "Auf der Insel Darrowehr liegt die Scholomance - eine Schule, die als Festung für die Geißel dient. Unser Feind haust dort, wo er sicher Forschungen betreiben kann, die seine Macht stärken. Eine Tür versperrt den Weg, und ganz gleich was wir versuchen, der Zutritt ist nur mit dem richtigen Schlüssel möglich.$B$BApotheker Zitters weiß viel über die Scholomance und hat eine Idee, wie ein Schlüssel zu erlangen ist. Ihr habt mir Euren Wert als jemand bewiesen, der knifflige Dinge erledigen kann; ich vertraue Euch nun diese Aufgabe an. Sprecht mit ihm über diese Angelegenheit.",
    ["O"] = "Sprecht mit Apotheker Zitters am Bollwerk in den Westlichen Pestländer.",
    ["T"] = "Scholomance",
  },
  [839] = "_",
  [840] = {
    ["D"] = "Hmmm, Ihr seht mir recht stark aus.$B$BHört mal, mein guter Freund Kargal braucht ein paar neue Rekruten für den Dienst im Brachland. Ich weiß, dass Ihr immer nur das Richtige für die Horde tun wollt. Das ist jetzt Eure Chance.$B$BBringt Kargal diesen Anwerbungsbrief und seht, ob er ihn unterzeichnet.",
    ["O"] = "Gebt Kargal Schlachtnarbe im Brachlandaußenposten Euren Anwerbungsbrief.",
    ["T"] = "Freiwilliger der Horde",
  },
  [841] = {
    ["D"] = "Ich stelle mal eine Vermutung an - Ihr habt, welch weltbeendende, ruchlose Tat Ihr auch unternommen habt, diese Tat nicht rechtzeitig beendet. Nein, wartet, noch besser... Ihr habt die Sache in den Sand gesetzt! Nun, was auch immer der Grund sein mag, ich verfüge über eine weitere Kraftquelle, die ich Euch verkaufen werde. Dieses Mal wird sie Euch aber zehn, JAWOHL ZEHN Wasserbeutel eines Wüstenläufers kosten!$B$BDie Zeiten hier sind schwer, $N, und Kraftquellen wachsen nicht auf Bäumen! Hmm, aber was wäre wohl, wenn sie es täten... Vielleicht könnten wir einen Kraftquellenbaum MACHEN...",
    ["O"] = "Bringt Chefingenieur Bilgenritzel in Gadgetzan 10 Wasserbeutel eines Wüstenläufers im Austausch gegen eine weitere Kraftquelle.",
    ["T"] = "Eine weitere Kraftquelle?",
  },
  [842] = {
    ["D"] = "<Kargal holt ein lädiertes Einberufungsschreiben aus seiner Westentasche und macht sein Zeichen darauf. Feierlich überreicht er es Euch.>$B$BTja, Welpe, Ihr habt Euch als eine außerordentliche Bereicherung entpuppt. Betrachtet Euch als einberufen. Wir brauchen Euresgleichen am Wegekreuz, vorausgesetzt, ihr schafft es lebend dorthin. Folgt einfach der Straße westwärts und biegt dann nach Süden ab, sobald Ihr auf die Kreuzung trefft. Wenn Ihr wirklich Eurer neuen horde helfen wollt, dann sprecht mit Halga und schließt Euch unserer nächsten Karawane an. Liefert diesen Brief bei Sergra Dunkeldorn ab, wenn ihr dort angekommen seid.",
    ["O"] = "Liefert Euren unterschriebenen Anwerbungsbrief bei Sergra Dunkeldorn am Wegekreuz ab. Wenn Ihr es nicht schon getan habt, könnt Ihr einen Platz in der nächsten Karawane dorthin bei Halga Blutauge in der Fernwacht bekommen.",
    ["T"] = "Wegekreuzwehrpflicht",
  },
  [843] = {
    ["D"] = "In Einsamkeit wandere ich auf diesen Straßen.$B$BDie Zwerge Bael Modans haben keinen Respekt vor meinem Land. Entsetzliche Schläge reißen tiefe Wunden in den Boden und brüllende Maschinen zerfetzen die Hügel. Land, das meinem Stamm einst als Heimat und Versorger diente, ist jetzt von zerstörerischen Zwergen durchfurcht.$B$BAlle Versuche, zu einer diplomatischen Einigung zu kommen, sind fehlgeschlagen. Jetzt ist die Zeit für entschlossenes Handeln gekommen.$B$BVertreibt die Zwerge mit Gewalt von der Bael Modan-Ausgrabung und bringt mir das Tagebuch ihres Anführers, Ausgrabungsleiter Khazgorm.",
    ["O"] = "Gann Steinkeil möchte, dass Ihr 15 Ausgräber von Bael\'dun und 5 Großknechte von Bael\'dun tötet.$B$BBringt Gann Steinkeil dann Khazgorms Tagebuch.",
    ["T"] = "Ganns Rekultivierung",
  },
  [844] = {
    ["D"] = "Kennt Ihr die Ebenenschreiter? Nutzlose Viecher. Können nicht fliegen. Schmecken nicht. Gehirne so groß wie meine Knöchel. Zu blöd, einem fahrenden Karren auszuweichen. Ich habe mir an deren dicken Schädeln schon mehr Achsen gebrochen... Manchmal glaube ich, die Stacheleber scheuchen Schwärme von denen in die Bahn unserer Karawanen, um ihre Hinterhalte vorzubereiten.$B$BMacht mich stolz, $C. Schlagt Euch eine Bresche durch diese dreckigen Vögel, damit die Straßen frei bleiben und bringt mir ihre Schnäbel als Beweis.",
    ["O"] = "Sammelt 7 Ebenenschreiterschnäbel.",
    ["T"] = "Ebenenschreiterbedrohung",
  },
  [846] = {
    ["D"] = "Es ist klar, dass ein Ausgrabungsleiter von Sprengstoff und diesen lärmenden Fahrzeugen abhängig ist, um den Boden aufzureißen. Wir müssen damit rechnen, dass schon bald ein neuer Großknecht die Sache übernimmt.$B$BIch möchte den Zwergen ihre eigene Medizin zu schmecken geben. Wenn wir laut diesen Plänen Nitroglyzerin mit Holzbrei und Natriumnitrat mischen, erhalten wir eine Sprengladung. Es ist bekannt, dass die Scharfschützen, Soldaten und Offiziere in der Burg diese Dinge besitzen.$B$BHolt diese Sachen und bringt sie mir; ich werde daraus eine Sprengladung basteln, die die Zwerge so schnell nicht vergessen werden!",
    ["O"] = "Gann Steinkeil möchte, dass Ihr ihm 6 Phiolen Nitroglyzerin, 6 Teile Holzbrei und 6 Proben Natriumnitrat bringt.",
    ["T"] = "Ganns Rache",
  },
  [847] = {
    ["D"] = "Diese... diese Macht... Ist das die Macht der Schamanen, die Tor\'gan mir zeigen wollte? Ich muss sagen, das ist wirklich interessant. Sagt Tor\'gan, dass er mich überzeugt hat... jedenfalls für den Augenblick. Ich werde mir anhören, was er zu sagen hat.",
    ["O"] = "Sprecht mit Tor\'gan in Hammerfall im Arathihochland.",
    ["T"] = "Arglist des Raptors",
  },
  [848] = {
    ["D"] = "Die Oasen des Brachlandes verbergen ein Mysterium. In ihren Wassern fließt Lebensenergie, die alle Pflanzen und Wildtiere erquickt, die davon trinken.$B$BEs erquickt und verändert sie.$B$BBei den Oasen im Nordwesten wächst ein bestimmter Pilz. Seine Sporen besitzen Eigenschaften, die wir, die Apotheker Lordaerons, sehr nützlich finden.$B$BBringt mir diese Sporen, dann ist Euch unser Dank gewiss.",
    ["O"] = "Bringt dem Apotheker Helbrim in Crossroads 4 Pilzsporen.",
    ["T"] = "Pilzsporen",
  },
  [849] = {
    ["D"] = "Wenn wir uns an Khazgorms Pläne halten, sollte es eigentlich kein Problem sein, eine Sprengladung herzustellen.$B$BAh, fertig.$B$BAlso, $N, ich möchte Euch mit einer besonderen Aufgabe betrauen. Nehmt diese Dynamitstange und jagt damit den Flugapparat bei Bael Modan in die Luft.$B$BDieser Flugapparat ist nicht nur ein wichtiger Bestandteil der Infrastruktur der Grabungsstätte, sondern auch eine Kriegswaffe. Vor allem ist sie aber ein Symbol der Macht und eine Beleidigung des Andenkens an meinen Stamm.$B$BMacht mich stolz, indem Ihr diese heldenhafte Aufgabe erledigt, dann soll es Euer Schaden nicht sein.",
    ["O"] = "Zerstört den Flugapparat bei Bael Modan und kehrt dann zu Gann Steinkeil zurück.",
    ["T"] = "Ganns Rache",
  },
  [850] = {
    ["D"] = "Die Zentauren sind den Tauren schon seit Jahren ein Dorn im Auge. Die Kolkarzentauren des Brachlandes sind vor Kurzem zu einer echten Bedrohung geworden. Normalerweise sind sie in diesen Landen eher unorganisiert, aber es haben sich unter ihnen neue Anführer erhoben. Sie sammeln sich.$B$BWir müssen diese Zentaurenanführer vernichten, um unsere Position hier zu bewahren.$B$BBringt mir den Kopf von Barak Kodobann. Er lagert westlich der Vergessenen Teiche.$B$BBesiegt ihn und kehrt dann zu mir zurück.",
    ["O"] = "Bringt Regthar Deathgate westlich von Crossroads Baraks Kopf.",
    ["T"] = "Anführer der Kolkar",
  },
  [851] = {
    ["D"] = "Verog ist zwar schwer aufzuspüren, aber den Informationen zufolge, die Ihr gesammelt habt, ist seine Gefährtin Wyneth immer in seinem Kommandozelt westlich von hier zu finden. Wenn wir sie töten, wird Verog in Windeseile auftauchen.$B$BRegthar hat uns zum Auskundschaften hierher geschickt, aber er sagte auch, wir sollen jede Gelegenheit nutzen, die Befehlskette der Zentauren zu stören. Lockt Verog aus der Defensive und tötet ihn!",
    ["O"] = "Tötet Wyneth, um Verog den Derwisch hervorzulocken. Erschlagt ihn dann und nehmt seinen Kopf an Euch.",
    ["T"] = "Verog der Derwisch",
  },
  [852] = {
    ["D"] = "Hezrul Blutmal ist der Anführer der Kolkarzentauren im Brachland. Er ist wild, brutal und schlau. Könnte man ihn besiegen, würde das die Kolkar spalten und zerschlagen und die Bedrohung durch sie enorm entschärfen.$B$BAlso tötet ihn. Bringt mir Hezruls Kopf und ich werde ihn in diesem Außenposten aufspießen, damit sie Furcht kennenlernen.$B$BEr führt sein Volk von der Blühenden Oase im Süden aus an.",
    ["O"] = "Tötet Hezrul Blutmal und nehmt seinen Kopf an Euch.",
    ["T"] = "Hezrul Blutmal",
  },
  [853] = {
    ["D"] = "Ich habe die Sporen, die Ihr mir gabt, zu einer Emulsion verarbeitet. Die muss ich jetzt meiner Kollegin schicken, der Apothekerin Zamah. Wenn Ihr sie ihr bringen könntet, würde sie Euch bestimmt etwas aus ihrem Vorrat alchemistischer Waren anbieten.$B$BSie lebt bei den Teichen der Visionen in einer Höhle unter den Schamanen von Donnerfels. Der Pfad zu dieser Höhle ist gut versteckt, aber Ihr könnt ihn am Steilhang der Anhöhe der Geister finden.$B$BBeeilt Euch. Die Emulsion verliert nach einer Weile ihre Wirksamkeit und muss vorher luftdicht verschlossen und weiterverarbeitet sein.",
    ["O"] = "Bringt der Apothekerin Zamah in Donnerfels die weiterverarbeiteten Sporen, bevor die Zeit abgelaufen ist.",
    ["T"] = "Apothekerin Zamah",
  },
  [854] = {
    ["D"] = "Ich habe Eure Ankunft vorausgesehen, $Gjunger:junge:c; $C. Aber vielleicht seid Ihr ja gar nicht mehr so jung.$B$BIch glaube, dass es an der Zeit ist, dass Ihr Euch tiefer in dieses Land vorwagt. Ich fühle, dass Ihr zu Größerem berufen seid.$B$BDas Wegekreuz wartet auf Euch, denn die Orcs, mit denen wir eng verbunden sind, brauchen Unterstützung. Folgt dieser Straße weiter nach Osten und dann weiter nach Norden bis zum Wegekreuz. Sucht Thork in seinen Mauern, er hat den Segen der Erdenmutter.",
    ["O"] = "Sprecht mit Thork beim Wegekreuz im Brachland.",
    ["T"] = "Reise zum Wegekreuz",
  },
  [856] = "_",
  [857] = {
    ["D"] = "Feegly der Verbannte möchte, dass Ihr ihm die Träne der Monde zurückbringt.",
    ["O"] = "Feegly der Verbannte möchte, dass Ihr ihm die Träne der Monde zurückbringt.",
    ["T"] = "Die Träne der Monde",
  },
  [858] = {
    ["D"] = "Euch hat nicht etwa Stotterspritter geschickt? Ich stecke in der Klemme. Ich bin hier reingesprungen, ohne daran zu denken, dass ich einen Schlüssel brauche, um die Antriebswelle des Schredders aufzuschließen. Einer der anderen Schredderbediener fragte mich, ob alles in Ordnung sei, und da bin ich in Panik geraten! Statt ihm zu sagen, dass ich meinen Schlüssel nicht dabei hatte, sagte ich ihm, dass es irgendeine mechanische Störung gäbe.$B$BWir müssen hier weg, und zwar schnellstens! Durchsucht die oberste Ebene der Plattform, Vorsteher Lugwizz sollte einen Schlüssel für diesen Schredder haben.$B$BHelft mir hier raus!",
    ["O"] = "Holt den Zündschlüssel von Vorsteher Lugwizz.",
    ["T"] = "Zündung",
  },
  [860] = {
    ["D"] = "Wenn Ihr den Pfad der Jäger beschreiten möchtet, dann führt Euch Eure Reise ins Brachland. Die dortigen Wildtiere sind willensstark und unglaublich wild. Ihr werdet beim Jagen dort viel lernen und Euer Geist wird sich entfalten.$B$BSprecht mit Sergra Dunkeldorn. Sie wird im Brachland Eure Führerin sein.$B$BSergra ist beim Wegekreuz zu finden. Um zu ihr zu gelangen, müsst Ihr vom Dorf der Bluthufe aus gen Osten ins Brachland reisen und dann an der Weggabelung nach Norden abbiegen. Und seid vorsichtig auf dieser Reise - das Brachland hält für die Unvorbereiteten und Unachtsamen große Gefahren bereit.",
    ["O"] = "Sprecht beim Wegekreuz mit Sergra Dunkeldorn.",
    ["T"] = "Sergra Dunkeldorn",
  },
  [861] = {
    ["D"] = "Euch hat das Jagdfieber gepackt, wie ich sehe. Auch ich war einst wanderlustig...$B$BWollte wandern und jagen. Denn die Jagd ist die größte Ehre für einen Tauren.$B$BWenn Ihr ernsthaft das Leben eines Jägers führen wollt, dann beweist mir Eure Geschicklichkeit und Entschlossenheit. Bringt mir die Krallen der Flachlandstreuner in Mulgore. Sie sind zäh und gerissen - $Gein junger:eine junge:c; $C auf der Pirsch sollte sich mit ihnen messen können.",
    ["O"] = "Erhaltet 4 Flachlandstreunerklauen.",
    ["T"] = "Der Weg der Jäger",
  },
  [862] = {
    ["D"] = "$GEin:Eine:c; $C wie Ihr muss hier im Brachland doch ziemlich hungrig sein. Ich bin den ganzen Tag am Verhungern.$B$BIch sag Euch was, ich bereite Euch einen feinen Eintopf zu. Ich brauche nur ein paar Grubenratten.$B$BScheint so, als würden diese dreckigen Zwerge in Bael Modan von unser aller Lieblingsspeise überrannt. Also reist gen Süden nach Bael Modan und bringt mir ein paar Grubenratten.$B$BIhr dürft Euch erst einen erfahrenen Abenteurer nennen, wenn Ihr Euch ein bisschen mit der Rattenjagd beschäftigt habt! Ha!",
    ["O"] = "Bringt Grub östlich des Wegekreuzes 8 Grubenratten.",
    ["T"] = "Grubenratteneintopf",
  },
  [863] = {
    ["D"] = "Ich würde mal sagen, ich kann das nach und nach lernen... Kann ja nicht so schwer sein. Ein paar Knöpfchen hier, ein oder zwei Hebelchen da... Also, seid Ihr bereit zu gehen?",
    ["O"] = "Beschützt Kurbelwizz und den gestohlenen Goblinschredder auf dem Weg aus dem Schlickmoor heraus.",
    ["T"] = "Die Flucht",
  },
  [864] = {
    ["D"] = "\"Wir sind so weit, die Kraftquelle ist entfernt und die Daten sind gesichert. Also dann, sichere Reisen und all das. Ich hoffe, das ruchlose Wissenschaftsexperiment hat sich für Euch ausgezahlt.\"$B$BDer Goblin wendet sich von Euch ab, um sich den Belangen der Gadgetzan Water Company zu widmen. Ihr verstaut das versiegelte Testset sicher für die Reise zurück nach Unterstadt. Dort wartet Apothekerin Zinge auf Euch und das versiegelte Feldtestset.",
    ["O"] = "Bringt das versiegelte Feldtestset nach Unterstadt zu Apothekerin Zinge.",
    ["T"] = "Kehrt zu Apothekerin Zinge zurück",
  },
  [868] = {
    ["D"] = "He, $N. Man hat mich zum Wegekreuz geschickt, damit ich über das Land wache und meinen Meistern in Orgrimmar über eventuelle Vorkommnisse berichte.$B$BEines meiner Studienobjekte sind die insektenartigen Kreaturen, die man im Süden auf dem Feld der Riesen findet. Wir wissen nur wenig über diese Kreaturen, also haben wir es uns zur Aufgabe gemacht, mehr über sie zu erfahren. Sie scheinen intelligent zu sein, intelligenter als normale Tiere.$B$BNehmt diese Gräberklaue und grabt ein paar ihrer Eier aus den Hügeln aus, aber seid vorsichtig: einmal aufgeschreckt, werden sie Euch angreifen.",
    ["O"] = "Bringt Korran beim Wegekreuz 12 Silithideneier und die Gräberklaue.",
    ["T"] = "Eierjagd",
  },
  [869] = {
    ["D"] = "Vor Kurzem wurde eine Ladung Silber aus unserem Wachturm entwendet. Sie war als Sold für die Wachen des Wegekreuzes gedacht und wir wollen das Silber zurückhaben.$B$BDas Seltsamste daran ist... wir haben einen der Diebe in der Nacht des Raubes erwischt. Und... es war ein Raptor! Unglaublich!$B$BIch weiß nicht, wofür Raptoren Silber brauchen könnten. Aber das ist mir auch egal - ich will die Raptoren tot sehen, damit sie uns nicht wieder bestehlen!$B$BJagt Raptoren im Brachland. Sammelt ihre Köpfe für mich!",
    ["O"] = "Bringt Gazrog beim Wegekreuz 12 Raptorenköpfe.",
    ["T"] = "Raptorendiebe",
  },
  [870] = {
    ["D"] = "Vor langer Zeit war das Brachland ein üppiger Ort voller Leben. Aber Krieg und Katastrophen verheerten das Land, verbrannten es und jetzt ist es nur noch eine ausgedörrte Hülse. So ist es nun einmal und es macht mich traurig.$B$BIn den letzten Jahren jedoch sind Oasen im Brachland entstanden und das Leben blüht wieder auf. Und wir Druiden spüren eine Macht von ganz tief unten an die Oberfläche dringen.$B$BWir müssen ihre Quelle finden. Reist zu den vergessenen Teichen nordwestlich des Wegekreuzes. Sucht in den dortigen Gewässern nach einer Quelle der Macht und kommt dann hierher zurück.",
    ["O"] = "Berichtet Tonga Runentotem von Euren Entdeckungen.",
    ["T"] = "Die Vergessenen Teiche",
  },
  [871] = {
    ["D"] = "Alle Stacheleber sind unsere Feinde, $N. Manche sind nur lästiger als andere.$B$BDer Klingenmähnenstamm hat unsere Nachschublinien von Durotar aus angegriffen, was uns nicht wenig Ärger bereitet. Ich habe Späher ausgeschickt, um den Anführer dieser Überfälle zu finden, aber bis es so weit ist, könnte es uns schon helfen, wenn Ihr diese dreckigen Schweinemenschen etwas dezimieren würdet.$B$BBeginnt im Nordosten in Richtung Durotar. Ihr könnt ihre Baue immer an den riesigen, dornigen Ranken erkennen, die aus dem Boden wachsen. Sucht sie und erschlagt sie.",
    ["O"] = "Tötet 8 Wassersucher, 8 Dornwirker sowie 3 Jäger des Klingenmähnenstamms und kehrt dann zu Thork beim Wegekreuz zurück.",
    ["T"] = "Vereitelung der Angriffe",
  },
  [872] = {
    ["D"] = "Amoklaufende Schweinemenschen direkt an der Grenze zu Durotar? Nicht mit mir! Wie es so schön heißt, $N: Die beste Verteidigung ist alle zu zermalmen, die dir im Weg stehen.$B$BEiner meiner Späher ist den Stachelebern nach deren letztem Überfall auf unsere Karawane gefolgt und wir wissen nun, wo wir zuschlagen müssen. Kreenig Grantelschnauze ist der Drahtzieher hinter den Angriffen entlang des Flusses. Zerquetscht ihn, $C. Besiegt ihn und schlitzt seinen Truppen die Bäuche auf, so dass die Angriffe auf dieses Lager ein Ende haben.$B$BSucht nach einer mit Stachelebern verseuchten Höhle entlang des Flusses, südlich von unseren Ställen.",
    ["O"] = "Tötet 5 Verteidiger der Klingenmähnen, 5 Dornenwirker der Klingenmähnen und Kreenig Grantelschnauze. Nehmt Kreenig Grantelschnauzes Hauer an Euch.",
    ["T"] = "Der Angriff der Fernwacht",
  },
  [873] = {
    ["D"] = "Isha Awak ist der Herr über diese Gewässer. Groß ist seine Kraft und edel sein Stolz. Die Menschen an der Küste fürchten ihn, denn er hat großen Schaden unter ihnen angerichtet.$B$BIch jedoch fürchte ihn nicht. Ich bin froh, dass er hier ist. Er ist eine würdige Herausforderung und eine ehrenhafte Beute.$B$BWenn Ihr bereit seid, schwimmt hinaus und sucht nach Isha Awak, der Tiefsten Verdammnis.$B$BSein Geist wohnt in seinem Herzen, und dieses schlagen zu hören heißt, Euer Schicksal zu kennen.",
    ["O"] = "Bringt Mahren Himmelsdeuter das Herz von Isha Awak.",
    ["T"] = "Isha Awak",
  },
  [874] = {
    ["D"] = "Ihr habt die Wildtiere des Brachlandes gejagt. Euer Geist ist stark. Ein Jäger muss jedoch immer vorbereitet sein. Ein wahrer Jäger kann auf allen Pfaden seine Beute finden. Weder Berg noch Sumpf wird ihn wanken lassen.$B$BNicht einmal das Meer.$B$BSucht meine Schwester Mahren. Sie jagt die großen Wassertiere entlang der Küste. Sie wird Euch etwas über sie lehren.$B$BSeid jedoch vorsichtig, während Ihr nach ihr sucht, denn die Küste des Brachlandes ist in den Händen der Menschen.",
    ["O"] = "Sprecht mit Mahren Himmelsdeuter.",
    ["T"] = "Mahren Himmelsdeuter",
  },
  [875] = {
    ["D"] = "Hahaha, $N. Ihr wollt noch mehr? Gut, das hör ich gern.$B$BDieses Mal möchte ich, dass Ihr ein paar der Offiziere der Harpyien in den trockenen Hügeln meuchelt. Nur Töter der Hexenschwingen können Offiziere werden und Ihr erkennt sie an den Ringen, die sie tragen. Sie sind ein übler Haufen, aber sie führen ein paar der Unterlinge in diesem Gebiet an. Schaltet Ihr 6 davon aus, wird das ein Entscheidungsschlag gegen ihr Matriarchat sein.$B$BSorgt dafür, dass sie schmerzvoll sterben, $N. Wir möchten, dass diese Dirnen von Harpyien lernen, wie wahnwitzig es ist, sich mit der Horde anzulegen.",
    ["O"] = "Sammelt 6 Harpyienoffiziersringe von den Tötern der Hexenschwingen und kehrt dann zu Darsok Swiftdagger in Crossroads zurück.",
    ["T"] = "Offiziere der Harpyien",
  },
  [878] = {
    ["D"] = "Ihr müsst meine Feinde schlagen. <schnaubt> Ihr müsst Quetschzahn und dem Klingenmähnenstamm helfen, indem Ihr die Borstennacken schwächt. Beide Stämme sind sehr spirituell - gebunden an die Erde und ihre Kraft. <schnaubt> Sie gibt uns Stärke in allen Dingen... besonders im Krieg.$B$BWenn ihr herausfinden wollt, wer Thork <schnaubt> und der Horde wirklich solche Angst bereitet, dann helft Quetschzahn! Ihr greift die spirituellen Anführer der Borstennacken im Norden an und schwächt sie, sodass die Klingenmähnen sie besiegen und das Brachland beherrschen können.",
    ["O"] = "Erledigt 6 Wassersucher der Borstennacken, 12 Dornenwirker der Borstennacken sowie 12 Geomanten der Borstennacken und kehrt zu Quetschzahn im Camp Taurajo im Brachland zurück.",
    ["T"] = "Stämme im Krieg",
  },
  [879] = {
    ["D"] = "Drei meines Stammes kamen aus dem Kral, um die Überfälle gegen die Horde anzuführen, $R. <schnaubt> Sie sind ruchlos und schlau und wenn Ihr sie besiegt, dann wird das Wegekreuz und auch der Großteil des Brachlandes Frieden haben... <schnaubt> zumindest vor dem Klingenmähnenstamm. <schnaubt>$B$BSucht nach Nak, Kuz und Lok Orcfluch. Sie befinden sich weit im Süden des Brachlandes. Einer ist ein Zauberer, ein weiterer ein Fährtenleser und ihr Anführer, den sie Orcfluch nennen, <schnaubt> ist ein Krieger. Tötet sie, $R, so wie sie mich töteten.",
    ["O"] = "Tötet Nak, Kuz und Lok Orcfluch und bringt Quetschzahn im Camp Taurajo im Brachland ihre Schädel.",
    ["T"] = "Verräter in den eigenen Reihen",
  },
  [880] = {
    ["D"] = "Es wird wichtig sein, sowohl Flora als auch Fauna die von den Oasen und den ungewöhnlichen Energien, die Ihr entdeckt habt, betroffen sind, zu untersuchen. Die Brackige Oase zu unseren Füßen ist der Lebensraum einer Spezies von Schnappkieferschildkröten. In letzter Zeit hat sich ihre Population explosionsartig vergrößert. Außerdem werden diese einst so friedfertigen Tiere jetzt immer aggressiver, wie ich am eigenen Leib erfahren musste, bevor ich hier heraufgestiegen bin.$B$B<Jerrik hält einen bandagierten Finger hoch.>$B$BIch vermute, sie wurden von den Oasen verändert. Sammelt ihre Panzer und bringt sie zur weiteren Untersuchung zu Tonga am Wegekreuz.",
    ["O"] = "Sammelt 6 Schnappkieferpanzer von den Schnappkiefern in der Brackigen Oase.",
    ["T"] = "Veränderte Wesen",
  },
  [881] = {
    ["D"] = "Weißnebel, Echeyakee in der Sprache der Tauren, ist der König der Savannenkatzen. Er jagt mit solcher Verstohlenheit, dass man sagt, er sei wie ein dünner, weißer Nebel auf der Erde. Und er tötet so schnell, dass seine Beute weder Angst noch Schmerz verspürt.$B$BDie Tauren sagen, er sei sowohl Gnade als auch Tod.$B$B$N, ich schicke Euch nun auf die Jagd nach Echeyakee. Sein Versteck befindet sich nordwestlich von hier an der Nordseite eines kleinen Berges zwischen den Knochen riesiger Kodos.$B$BGeht. Stoßt in dieses Horn, wenn ihr seinen Unterschlupf erreicht. Blast das Horn und er wird erscheinen.",
    ["O"] = "Blast das Horn bei Echeyakees Unterschlupf, um ihn herbeizurufen. Tötet oder zähmt ihn.",
    ["T"] = "Echeyakee",
  },
  [882] = {
    ["D"] = "Ishamuhale - Speerzahn - ist der wildeste Sonnenschuppenraptor des Brachlandes. Er jagt weder zum Spaß noch für Nahrung. Er jagt, weil Jagen seine Leidenschaft ist. Er tötet, weil Töten seine Natur ist.$B$BUnd Ihr werdet Euch mit seiner Natur vertraut machen, $N, denn Euer Weg folgt jetzt den Krallenspuren Ishamuhales.$B$BBeginnt mit der Jagd. Erlegt seine Lieblingsbeute, ein Zhevra, und bringt den Kadaver zum toten Baum im Nordwesten von Ratschet. Ishamuhale wird den Kadaver riechen und von ihm angezogen werden.$B$BSeid bereit, wenn er kommt.",
    ["O"] = "Bringt Jorn Himmelsdeuter im Camp Taurajo Ishamuhales Fangzahn.",
    ["T"] = "Ishamuhale",
  },
  [883] = {
    ["D"] = "Lakota\'manis Huf ist ein Symbol für die Kraft der großen Kodos.",
    ["O"] = "Bringt Jorn Himmelsdeuter im Camp Taurajo Lakota\'manis Huf.",
    ["T"] = "Lakota\'mani",
  },
  [884] = {
    ["D"] = "Wenn man Owatankas Schwanzstachel berührt, stieben Funken auf.",
    ["O"] = "Bringt Jorn Himmelsdeuter im Camp Taurajo Owatankas Schwanzstachel.",
    ["T"] = "Owatanka",
  },
  [885] = {
    ["D"] = "Eine Berührung mit der Feder von Washte Pawne ist schmerzhaft.",
    ["O"] = "Bringt Jorn Himmelsdeuter im Camp Taurajo Washte Pawnes Feder.",
    ["T"] = "Washte Pawne",
  },
  [886] = {
    ["D"] = "Die Druiden von Donnerfels spüren eine große Macht, die östlich von Mulgore ins Brachland sickert.$B$BTonga Runentotem wurde entsandt, um die Quelle dieser Macht ausfindig zu machen, aber wir befürchten, dass er Hilfe brauchen wird.$B$BReist ins Brachland und sprecht mit Tonga. Er wird in der Siedlung des Wegekreuzes sein. Um dorthin zu gelangen, folgt der Straße vom Dorf der Bluthufe aus nach Osten. Betretet das Brachland und wendet Euch bei der Weggabelung weiter nach Norden. Geht weiter Richtung Norden, dann gelangt Ihr zum Wegekreuz.$B$BUnd haltet Euch immer auf der Straße, $N, denn das Brachland ist ein raues Land.",
    ["O"] = "Sprecht beim Wegekreuz mit Tonga Runentotem.",
    ["T"] = "Die Oasen des Brachlandes",
  },
  [887] = {
    ["D"] = "Ich wünschte, Vize-Admiral Grezzlik würde bessere Arbeit bei der Sicherung der Meere für unsere Handelsschiffe leisten. Bei all dem Gold, das die Handelsbarone in die Handelsflotten gesteckt haben, macht es mich rasend zu sehen, wie viele Piraten unbehelligt unterwegs sind, meine Schiffe überfallen und meine Waren stehlen!$B$BMir kam zu Ohren, dass die Südmeerfreibeuter südlich von hier ein Lager aufgeschlagen haben. Sie sind mir ein echter Dorn im Auge, und wenn sich Grezzlik nicht um sie kümmert, könnt Ihr mir vielleicht helfen, sie loszuwerden?",
    ["O"] = "Tötet für Gazlowe in Ratchet 12 Briganten der Südmeerfreibeuter und 6 Kanoniere der Südmeerfreibeuter.",
    ["T"] = "Südmeerfreibeuter",
  },
  [888] = {
    ["D"] = "Ich hätte zwar gern, dass Ihr mir all meine Waren zurückbringt, aber das dürfte wohl unmöglich sein. Es gibt jedoch ein paar Dinge, die ich unbedingt wiederhaben muss!$B$BWie Ihr seht, ist mein Observatorium fast einsatzbereit, aber es fehlt noch die kleinere der zwei Linsen. Die erste Linse ist mit der Karawane aus Durotar gekommen, aber die zweite wurde per Schiff verschickt und ist nie hier eingetroffen. Ebenso ist meine Stiefellieferung von Drizzlik aus Beutebucht nicht hier angekommen!$B$BBegebt Euch zum Piratenlager und seht mal nach, ob Ihr die Dinge dort finden könnt.",
    ["O"] = "Bringt Gazlowe in Ratschet die Stiefellieferung und die Teleskoplinse zurück.",
    ["T"] = "Gestohlene Beute",
  },
  [889] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Geist des Windes",
  },
  [890] = {
    ["D"] = "Ich warte schon eine Ewigkeit auf meine letzte Warenlieferung aus Beutebucht! Ich bin mir ziemlich sicher, dass die Seeräuber sie gestohlen haben; würdet Ihr wohl, nur um sicher zu sein, zu den Docks gehen und Flunkerblick fragen, ob meine Waren vielleicht ohne mein Wissen bereits ins Lagerhaus gebracht wurden?$B$BHier, nehmt mein Geschäftsbuch mit zu Flunkerblick, damit er meine Inventaraufzeichnungen mit seinen Büchern gegenprüft.",
    ["O"] = "Bringt Werftmeister Flunkerblick Gazlowes Geschäftsbuch.",
    ["T"] = "Die verloren gegangene Lieferung",
  },
  [891] = {
    ["D"] = "Wieder ist ein Schiff meiner Flotte diesen übereifrigen Menschen der Feste Northwatch in die Hände gefallen!$B$BMan muss sich um diese Bastarde kümmern. Ihr Verfolgungswahn hat sie in eine Art Raserei versetzt. Diese schießwütigen, rücksichtslosen Rüpel schießen auf alles, was sich bewegt.$B$BReist gen Süden entlang der Händlerküste zur Feste und belagert ihre Streitkräfte. Bringt mir die Medaillen ihrer Soldaten als Beweis. Und erschlagt um meiner Flotte willen Captain Hellstieg und ihre übereifrigen Kanoniere!",
    ["O"] = "Kapitän Thalo\'thas Brightsun aus Ratchet möchte, dass Ihr 10 Medaillen von Theramore sammelt und Captain Hellstieg, Kanonier Knoch und Kanonier Hecksler tötet.",
    ["T"] = "Die Kanonen von Northwatch",
  },
  [892] = {
    ["D"] = "Laut meinen Aufzeichnungen ist alles in Ordnung, $N. Geht zurück und sagt Gazlowe, dass er wohl Pech gehabt hat. Es würde mich nicht überraschen, wenn sich die Piraten seine Waren geschnappt hätten.$B$BIch kann leider nicht mehr für ihn tun. Oh... Ihr könnt ihm allerdings sagen, dass ich ein paar Dinge aus Lorenhall für ihn habe, falls er die Zeit findet, sie abzuholen.",
    ["O"] = "Bringt Gazlowe in Ratschet sein Geschäftsbuch zurück.",
    ["T"] = "Die verloren gegangene Lieferung",
  },
  [893] = {
    ["D"] = "Die Stacheleber des Klingenmähnenstamms im Süden, hinter dem Feld der Riesen, haben keine erfahrenen Schmiede, soweit ich weiß, aber Sie haben anscheinend begonnen, stabilere Waffen zu entwickeln. Ich hätte gern ein paar verschiedene Arten davon, um etwas über ihre Techniken zu erfahren, so möglich.$B$BWenn Ihr der Horde Ehre bringen wollt, dann bringt mir Exemplare von ihren Waffen. Besorgt mir einen Dolch von ihren Schleichern, einen Zauberstab von ihren Sehern und einen Schild von ihren Kriegsberserkern. Das sollte reichen!",
    ["O"] = "Bringt Tatternack Stahlformer im Camp Taurajo im Brachland einen Klingenmähnenrückenstecher, einen verkohlten Klingenmähnenzauberstab und einen Klingenmähnenkriegsschild.",
    ["T"] = "Ausgewählte Waffen",
  },
  [894] = {
    ["D"] = "Die Venture Company hat weit nördlich von hier, westlich des Schlickmoores, eine kleine Forschungseinrichtung errichtet. Was die Leute dort tun, weiß ich nicht genau, aber ich habe herausgefunden, dass sie dort mit etwas experimentieren, das \"Samophlang\" genannt wird.$B$BWas zum Teufel ist ein Samophlang? Naja, was immer es auch ist, ich möchte es untersuchen, also brauche ich jemanden, der eines beschafft.$B$BIch habe ein Exemplar der Bedienungsanleitung für ihr Steuersystem in die Finger bekommen; Ihr solltet also herausfinden können, wie man das Samophlang daraus entfernt.",
    ["O"] = "Verschafft Euch Zugang zur Steuerkonsole in der Forschungseinrichtung der Venture Company.",
    ["T"] = "Samophlang",
  },
  [895] = {
    ["D"] = "GESUCHT!$B$BBaron Küstenschipper, Kapitän der GNADENLOS$B$BCaptain Küstenschipper aus Gilneas befehligt Schiffe der Südmeerfreibeuter und wird wegen Piraterie gesucht. Seine Überreste müssen in noch erkennbarem Zustand sein!$B$B- Gazlowe",
    ["O"] = "Bringt Gazlowe in Ratchet Baron Küstenschippers Kopf.",
    ["T"] = "GESUCHT: Baron Küstenschipper",
  },
  [896] = {
    ["D"] = "Als Werftmeister eines so geschäftigen Hafens wie Ratchet habe ich mein Ohr immer am Puls der Zeit. Ich weiß alles über den Verkehr von Waren und Geld zwischen hier und Booty Bay.$B$BDie jüngste Neuigkeit, die ich erfahren habe, betrifft die Felsadermine der Venture Company nordöstlich des Schlickermoores. Einer der Minenarbeiter hat dort einen faustgroßen Smaragd gefunden. Ich kenne ein paar Käufer, die sich die Finger nach so etwas ablecken würden... und ich würde bei einem Verkauf Halbe-Halbe machen.",
    ["O"] = "Beschafft Euch von einem der Vorarbeiter oder Vollstrecker der Venture Company den Katzenaugensmaragd und bringt ihn zu Dizzywig in Ratchet.",
    ["T"] = "Des Minenarbeiters Vermögen",
  },
  [897] = {
    ["D"] = "Diese seltsame, insektenartige Kreatur scheint außerhalb der natürlichen Ordnung zu stehen, so als würde sie nicht ins Brachland gehören.",
    ["O"] = "Bringt den Ernterkopf zu Jorn Himmelsdeuter.",
    ["T"] = "Der Ernter",
  },
  [898] = {
    ["D"] = "Endlich! Jemand rettet mich!$B$BIch kann es nicht glauben, dass Ihr an den Wachen vorbeigekommen seid. Diese Fanatiker aus Theramore müssen verrückt sein. Sie haben unser Schiff versenkt und mich, den einzigen Überlebenden, eingesperrt. Sie nannten mich ihren Feind und eine Gefahr für die Allianz.$B$BIch! Eine Gefahr für diesen Riesenschwindel, den sie Allianz nennen? Ich war Decksmann auf einem Fuseltransporter zwischen Ratschet und Beutebucht.$B$BGenug geredet. Helft Ihr mir, zurückzukommen nach Ratschet, ja? Lasst es mich wissen, wenn Ihr bereit seid, hier auszubrechen.",
    ["O"] = "Begleitet Gilthares Feuerzweig sicher zurück zu Kapitän Blendsonn in Ratschet.",
    ["T"] = "Aus der Gefangenschaft befreit",
  },
  [899] = {
    ["D"] = "Vielleicht kennt Ihr diesen Schmerz der Ungewissheit, $C, vielleicht auch nicht. Doch wisset: Ich stehe hier jeden Tag, vom Aufgang der Sonne bis zu ihrem Untergang, und suche den Horizont nach diesen Monstern ab. Ich habe jeden Schweinemenschen getötet, den ich seitdem gesehen habe, doch der Durst nach ihrem Blut ist noch lange nicht gestillt. Vielleicht hätte ich mich nach Taurajo begeben sollen, statt nördlich zur Siedlung des Wegekreuzes. $B$BIhr, $C, könntet mir helfen.$B$BTötet sie. Tötet so viele Ihr könnt und bringt mir ihre Hauer, dann werden wir ihren Tod miteinander feiern.",
    ["O"] = "Bringt Mankrik beim Wegekreuz 60 Stacheleberhauer der Borstennacken.",
    ["T"] = "Von Hass zerfressen",
  },
  [901] = {
    ["D"] = "Da der Apparat jetzt ausgeschaltet ist, kann die Steuerkonsole geöffnet und das Samophlang entfernt werden. Alles andere sollte unangetastet bleiben und Ihr benötigt jetzt nur noch den Schlüssel, mit dem die Konsole geöffnet werden kann.",
    ["O"] = "Besorgt Euch bei Tüftler Kichers den Konsolenschlüssel.",
    ["T"] = "Samophlang",
  },
  [902] = {
    ["D"] = "Nach dem Öffnen der Front der Steuerkonsole zeigt sich ein dichtes Gewirr aus Kabeln, Schläuchen und anderen seltsamen, mechanischen Objekten - die meisten davon scheinen keinen praktischen Nutzen zu haben. Ihr schiebt sie beiseite und arbeitet Euch tiefer in die Konsole vor, bis Ihr schließlich das Samophlang gefunden habt. Mit einem Ruck zieht Ihr es aus der Halterung.",
    ["O"] = "Bringt das Samophlang zu Stotterspritter in Düsentopfs Außenposten im Nördlichen Brachland zurück.",
    ["T"] = "Samophlang",
  },
  [903] = {
    ["D"] = "Es ist nun an der Zeit, etwas zu jagen, das sich wirklich wehrt. Die Streuner des Brachlandes sind von zäher Art. Zäh, erfinderisch, listig... und tödlich. Ihr findet sie zwischen den hohen Gräsern. Ihr werdet viele südwestlich von Crossroads finden.$B$BSeid vorsichtig, wenn Ihr sie jagt, $N, oder Ihr werdet feststellen, dass Ihr nicht mehr Jäger, sondern Gejagter seid.",
    ["O"] = "Besorgt für Sergra Darkthorn in Crossroads 7 Streunerklauen von den Savannenstreunern.",
    ["T"] = "Streuner des Brachlandes",
  },
  [904] = "_",
  [905] = {
    ["D"] = "Tja, $N, die Jagd wird schwieriger. Jetzt müsst Ihr Eure Beute besiegen und dann Euren Weg zu ihrem Unterschlupf finden.$B$BJagt die Bluthornraptoren im Süden. Erledigt sie und nehmt Euch die Federn, die sie tragen. Legt die Federn in die Sensenklauennester südwestlich der brackigen Oase. Zeigt ihren Brüdern, dass Ihr sie nicht fürchtet!",
    ["O"] = "Tötet Bluthornraptoren und nehmt ihre Federn mit. Benutzt die Federn bei den drei Sonnenschuppennestern. Kehrt zu Sergra Darkthorn in Crossroads zurück.",
    ["T"] = "Die wütenden Sensenklauen",
  },
  [906] = {
    ["D"] = "Bringt Loks Kopf zu Thork am Wegekreuz, $R. <schnaubt> Er sollte ohne Worte wissen, was meinem Stamm zugestoßen ist - ha, nicht dass er denken würde, Quetschzahn hätte bei <schnaubt> solchen Dingen geholfen. Aber ich bin sicher, er wird Euch für das Ausführen einer solch großen Tat belohnen. <schnaubt>$B$BIch werde Euch auch weiterhin mit Agamaggans Macht segnen, solange ich in diesem Käfig verbleibe, $R. Bis dann, lebt wohl.",
    ["O"] = "Bringt Loks Kopf zu Thork beim Wegekreuz im Brachland.",
    ["T"] = "Verräter in den eigenen Reihen",
  },
  [907] = {
    ["D"] = "Nun denn, $N, jetzt müsst Ihr den großen Donnerechsen gegenübertreten. Diese Bestien sind so groß, dass der Boden erbebt, wenn sie über die Ebenen stampfen. Seid vorsichtig, wenn Ihr gegen sie kämpft, denn ein einziger falscher Schritt, und schon findet Ihr Euch unter ihren Füßen wieder.$B$BIhr findet die Donnerechsen im Südlichen Brachland. Man kennt sie als Sturmschnauzen, Sturmechsen und Donnerköpfe. Kehrt zu mir zurück, nachdem Ihr sie überwältigt habt.",
    ["O"] = "Bringt das Blut von 3 Donnerechsen ins Camp Taurajo zu Jorn Himmelsdeuter.",
    ["T"] = "Wütende Donnerechsen",
  },
  [908] = {
    ["D"] = "Die Anhänger des Schattenhammers sind in die Mondschreinruinen der Tiefschwarzen Grotte eingezogen. Ihre Gegenwart kann nur dazu dienen, die Elemente gegen uns aufzubringen. Wenn dem kein Einhalt geboten wird, gehört ihnen diese Region bald ganz und gar.$B$B$N, geht in die Tiefschwarze Grotte und findet den Tiefenstein der Ruinen. Er dürfte irgendwo in deren Nähe im Wasser liegen. Darin ist ein Tiefenkern - ein Gerät, das, wenn man es richtig abliest, eine Aufstellung über alle Aktivitäten von Elementaren liefert. Wenn ich es habe, können der Irdene Ring und ich vielleicht etwas tun, um sie aufzuhalten.",
    ["O"] = "Bringt den Tiefenkern zu Je\'neu Sancrea beim Außenposten von Zoram\'gar im Eschental.",
    ["T"] = "Inmitten der Ruinen",
  },
  [909] = {
    ["D"] = "Der besiegte Wasserelementar hat eine seltsame Wasserkugel zurückgelassen. Ihr vermutet, dass das Objekt die Kreatur irgendwie mit Energie versorgt hat. In der Kugel schwappt faulig aussehendes Wasser herum. Die Kugel selbst zeigt sich unbeeindruckt von jeder äußerlichen Krafteinwirkung.$B$BWenn irgendwer aus diesem Gegenstand schlau werden kann, ist das wohl Je\'neu Sancrea vom Irdenen Ring. Er befindet sich beim Außenposten von Zoram\'gar der Horde im Eschental.",
    ["O"] = "Bringt die seltsame Wasserkugel zu Je\'neu Sancrea beim Außenposten von Zoram\'gar im Eschental.",
    ["T"] = "OLD Baron Aquanis",
  },
  [912] = "_",
  [913] = {
    ["D"] = "Der Donnerfalke, $N, ist ein ungestümes Wildtier. Es ist an der Zeit für Euch, ihm gegenüberzutreten. Ihr müsst herausfinden, wo er umherstreift, und mir seine Flügel als Beweis für Eure erfolgreiche Jagd bringen.$B$BWenn Ihr das tut, wird Eure Zeit mit mir sich ihrem Ende nähern.",
    ["O"] = "Sucht und erschlagt einen Donnerfalken und bringt Jorn Himmelsdeuter in Taurajo seine Flügel.",
    ["T"] = "Der Ruf des Donnerfalken",
  },
  [914] = {
    ["D"] = "Die Druiden in den Höhlen des Wehklagens, die Druiden des Giftzahns, sind eine Verirrung der Natur. Einst gehörten sie einem Orden edler Druiden an, deren Plan es war, das Brachland zu heilen, doch jetzt trachten sie danach, das Land ihren eigenen verderbten Träumen gleichzumachen.$B$BDie Druiden des Giftzahns haben vier Anführer, und jeder besitzt einen Traumedelstein. Selbst jetzt noch verfolgen mich ihre Gesichter! Besiegt die Anführer und bringt mir ihre Edelsteine, dann wird das Brachland endlich wieder Frieden haben.$B$BGeht, $N. Sie halten sich tief in den Höhlen des Wehklagens verborgen.",
    ["O"] = "Bringt die Edelsteine von Kobrahn, Anacondra, Pythas und Serpentis nach Donnerfels zu Nara Wildmähne.",
    ["T"] = "Anführer der Giftzähne",
  },
  [915] = {
    ["D"] = "EISCREME! Oh, könnt Ihr mir bitte, bitte, bitte etwas Eiscreme besorgen? Am liebsten mag ich Erdbeere und es gibt einfach kein besseres Erdbeereis als das von Tigule und Foror. Das ist mein liebstes Eis auf der ganzen Welt!$B$BFrüher gab es das immer auf der Rennstrecke in der Schimmernden Ebene, aber ich habe mal gehört, dass man es jetzt auch in der Stadt kaufen kann. Kriege ich bitte eins? Bitte? Am liebsten Tigules und Forors Eiscreme.",
    ["O"] = "Besorgt für Euren Schützling etwas Erdbeereis. Der Kleine scheint die Marke \'Tigules und Forors Eiscreme\' zu bevorzugen.",
    ["T"] = "Lecker Eis",
  },
  [916] = {
    ["D"] = "Ich kam ins Laubschattental, um hier die Waldweberspinnen zu beobachten, die in der Schattenweberhöhle leben. Man sagte mir, dass sie mit Teldrassils jüngstem Segen weit weniger aggressiv sein sollten. Anscheinend wurde ich getäuscht.$B$BIch habe vor Kurzem ein paar ihrer Giftbeutel erhalten und musste eine sehr ungesunde Verderbnis in ihnen feststellen. Um es kurz zu machen, ich mache mir Sorgen darüber, was passieren könnte, wenn wir korrumpierte Tiere wie diese frei herumlaufen lassen.$B$BIch möchte, dass Ihr ihre Population verringert, damit wir etwas Zeit gewinnen.",
    ["O"] = "Tötet 12 Waldweberspinnen.",
    ["T"] = "Verderbnis der Waldweber",
  },
  [917] = {
    ["D"] = "Mir fällt gerade ein, dass Ihr mir vielleicht noch mit etwas Anderem helfen könnt. Ich bin hierhergekommen, um ein Waldweberei aus den Tiefen der Höhle zu besorgen, aber die Spinnen hatten etwas dagegen. Wenn Ihr ein Ei besorgen und es zurück zu Dirania bringen könntet, wären wir beide sehr dankbar.",
    ["O"] = "Erhaltet ein Waldweberei.",
    ["T"] = "Waldweberei",
  },
  [918] = {
    ["D"] = "Die Bäumlinge von Teldrassil sind Naturelementare. In ihnen spiegelt sich in gewisser Weise die natürliche Ordnung von Flora und Fauna unseres großen Baums wider.$B$BDaher ist es höchst beunruhigend zu sehen, dass die Bäumlinge so wütend geworden sind, besonders wenn man bedenkt, dass inzwischen alles heilen sollte.$B$BIch will dem auf den Grund gehen. Ich habe mich viel mit verschiedenen Düngemethoden für Pflanzen beschäftigt und würde sie gern an Bäumlingssamenkörnern ausprobieren. Würdet Ihr bitte in der Gegend des Al\'Amethsees ein paar Bäumlingssamenkörner sammeln und mir bringen?",
    ["O"] = "Erhaltet 6 Bäumlingssamenkörner.",
    ["T"] = "Bäumlingssamenkörner",
  },
  [919] = {
    ["D"] = "Kleine Bäumlinge sprießen rund um das Wasser des Al\'Amethsees. Ich fürchte, dass man diesen Schösslingen nicht mehr helfen kann - wir sollten versuchen, sie auszureißen, bevor sie groß genug sind, um Probleme zu bereiten.$B$BWenn Ihr auf Euren Wanderungen um den See Bäumlingssprosse seht, reißt sie bitte aus. Helft dabei, unser Land rein zu halten!",
    ["O"] = "Sammelt 10 Bäumlingssprosse.",
    ["T"] = "Bäumlingssprosse",
  },
  [920] = {
    ["D"] = "Ah, $N! Ich hörte, dass Tenaron nach Euch gefragt hat. Ihr findet ihn in dem am höchsten gelegenen Raum in Aldrassil.$B$BWir mögen zwar ein langes Leben haben, aber Ihr solltet ihn dennoch nicht allzu lang warten lassen.",
    ["O"] = "Sprecht im Laubschattental über den Dächern von Aldrassil mit Tenaron Sturmgewalt.",
    ["T"] = "Tenarons Einladung",
  },
  [921] = {
    ["D"] = "Es ist an der Zeit herauszufinden, was Euer Schicksal ist, $N. Doch bevor Ihr bereit seid, in die Welt hinter unseren verzauberten Wäldern hinauszuziehen, gibt es noch vieles, was Ihr über unsere jüngste Vergangenheit lernen müsst.$B$BEs gibt eine Aufgabe, der Ihr Euch stellen müsst. Geht zum Mondbrunnen nördlich von Aldrassil und entnehmt eine Phiole seines Wassers. Dort werdet Ihr Zeuge eines kleinen Eindruckes unserer Magien werden und etwas über unsere Vergangenheit erfahren.$B$BWenn Ihr dort fertig seid, bringt das Wasser zu Tenaron Sturmgewalt. Ihr werdet ihn auf der westlichen Rampe an der Spitze Aldrassils finden.",
    ["O"] = "Füllt die Kristallphiole.",
    ["T"] = "Teldrassil: Die Bürde der Kaldorei",
  },
  [922] = {
    ["D"] = "$N, würdet Ihr wohl eines der Samenkörner, die Ihr mir besorgt habt, meinem Freund Rellian Grünwipfel bringen? Er ist ein Druide, der sich bei der Hütte am Quell aufhält, und bei unserem letzten Gespräch machte er deutlich, dass er an meiner Arbeit mit den Bäumlingen interessiert sei. Er hatte selbst ein paar Ideen und würde sich über ein Exemplar dieser Samenkörner sicherlich freuen.$B$BDanke, $N. Ihr wart mir eine große Hilfe. Ich hoffe, dass ich Euch eines Tages die Früchte meiner Arbeit zeigen kann.$B$BDie Hütte am Quell ist an der Straße, die in westlicher Richtung aus Dolanaar hinausführt, auf dem Weg nach Darnassus.",
    ["O"] = "Bringt Rellian Grünwipfel bei der Hütte am Quell ein Bäumlingssamenkorn.",
    ["T"] = "Rellian Grünwipfel",
  },
  [923] = {
    ["D"] = "Bosheit wuchert in den Bäumlingen. Wir versuchen, die Ursache dafür zu finden, aber bis dahin... Aus Sorge um Teldrassils Sicherheit müssen wir alle Bäumlinge fällen, denen nicht mehr geholfen werden kann. Diejenigen, die die Gegend um den Quellsee im Norden durchstreifen, sind am schlimmsten betroffen. Sie müssen ausgemerzt werden!$B$BVernichtet alle Bäumlinge, die Ihr dort findet, und sammelt die moosartigen Tumore, die auf ihnen wachsen. Bringt mir die Tumore, damit ich sie verbrennen kann.",
    ["O"] = "Erhaltet 5 moosartige Tumore.",
    ["T"] = "Moosartige Tumore",
  },
  [925] = {
    ["D"] = "Ich habe gehört, dass Cairne Bluthuf einer der größten Helden ist, den die Horde je gesehen hat. Wenn ich mal groß bin, möchte ich auch ein Held der Horde werden!$B$BKennt Ihr Cairne, $N? Tut Ihr mir einen Gefallen? Kriege ich ein Autogramm von ihm? Ich weiß gar nicht, wie ich ihn ansprechen soll, und wahrscheinlich ist er sowieso viel zu beschäftigt, um mich zu beachten...aber was ist mit Euch? Ihr seid doch genau wie er ein Abenteurer! Euch gibt er bestimmt ein Autogramm!$B$BBitte, $N. Bitte...",
    ["O"] = "Reist nach Donnerfels und versucht für Euren Schützling einen Hufabdruck von Cairne Bluthuf zu ergattern.",
    ["T"] = "Cairnes Hufabdruck",
  },
  [928] = {
    ["D"] = "Ich könnte Euch zwar noch so viel mehr über die Mondbrunnen und Teldrassil erzählen, doch leider muss ich Euch fortschicken. Corithras Mondzorn erwartet Euch. Ich habe das Wasser der Phiole, die Ihr mir brachtet, in dieses Gefäß geschüttet, damit Ihr es ihm bringen könnt.$B$BFragt nach Corithras. Ihr findet ihn am Mondbrunnen in Dolanaar. Folgt der Straße südlich von Aldrassil, die aus dem Laubschattental hinausführt und dann weiter auf dem Kopfsteinpflaster in Richtung Westen.$B$BBleibt unbedingt immer auf der Straße, $N. Es gibt neuerdings ziemlich gefährliche Bestien in den Wäldern.",
    ["O"] = "Bringt Corithras Mondzorn in Dolanaar das teilweise gefüllte Gefäß.",
    ["T"] = "Teldrassil: Die Bürde der Kaldorei",
  },
  [929] = {
    ["D"] = "Lasst mich Euch zunächst ein bisschen mehr von der Aufgabe erzählen, die Euch erwartet. Die Druiden in Darnassus verbrauchen das Wasser der Mondbrunnen Teldrassils, und deshalb muss ihr Mondbrunnen von Zeit zu Zeit wieder aufgefüllt werden. Ihr könnt mit diesen speziell gefertigten Phiolen Wasser aus den Mondbrunnen schöpfen.$B$BNehmt dieses Gefäß zum Mondbrunnen außerhalb von Sternenhauch im Osten mit und füllt es mit Wasser. Beachtet die Lehren der Mondbrunnen, sonst nähren wir nur unsere Verfehlungen.",
    ["O"] = "Füllt die Jadephiole.",
    ["T"] = "Teldrassil: Die Bürde der Kaldorei",
  },
  [931] = {
    ["D"] = "Die Wedel der Pflanze schimmern im Dämmerlicht des Waldes und verleihen ihr eine wabernde, pulsierende Aura.$B$BIhr glaubt, dass Denalan bestimmt gern ein Exemplar davon hätte.",
    ["O"] = "Bringt Denalan einen der schimmernden Wedel. Er hält sich meistens am Al\'Amethsee auf, reist aber manchmal zur Hütte am Quell.",
    ["T"] = "Der schimmernde Wedel",
  },
  [932] = {
    ["D"] = "Ich muss Euch warnen, $N, das hier muss absolut unter uns bleiben. Die Satyrn bereiten uns schon genug Ungelegenheiten, aber dieser eine hier scheint sich zu einer echten Bedrohung zu entwickeln.$B$BLord Melenas ist sein Name. Er lebt in der nahegelegenen Teufelsfelshöhle, in der er ein großes Kontingent Grellkrieger um sich versammelt hat. Sein Herz ist so schwarz wie die Nacht und er führt etwas äußerst Übles im Schilde.$B$BIhr müsst ihn in der nahe gelegenen Höhle gleich nördlich von hier suchen und mir seinen Kopf bringen.",
    ["O"] = "Erhaltet den Kopf von Lord Melenas.",
    ["T"] = "Krankhafter Hass",
  },
  [933] = {
    ["D"] = "Ich möchte, dass Ihr noch einen letzten Mondbrunnen besucht. Er liegt direkt südlich von hier am Ufer der Teiche von Arlithrien. Seid vorsichtig, wenn Ihr Euch dem Brunnen nähert und haltet Eure Waffen bereit. Ich verspüre eine wachsende Verderbnis von den südlichen Ästen Teldrassils her.$B$BSobald Ihr das Wasser habt, möchte ich, dass Ihr es zu einem vertrauten Gesicht bringt, dem Ihr dort an den Teichen begegnen werdet. Sie möchte die Verderbnis bekämpfen und hat nach Euch persönlich gefragt.",
    ["O"] = "Füllt die Turmalinphiole.",
    ["T"] = "Teldrassil: Die Bürde der Kaldorei",
  },
  [934] = {
    ["D"] = "Ihr müsst nun den Mondbrunnen unter den Ästen des Orakelbaums im fernen Nordwesten aufsuchen. Er ist der erste und mächtigste unserer Brunnen und es heißt von seinem Wasser, es enthalte die Essenz von Elunes Macht.$B$BEntnehmt eine Phiole des Wassers und kehrt zu mir zurück. Ich werde euch an der Weggabelung vor Darnassus treffen, wenn Eure Aufgabe vollendet ist.",
    ["O"] = "Füllt die Amethystphiole.",
    ["T"] = "Krone der Erde",
  },
  [935] = {
    ["D"] = "Unsere Arbeit ist hier fürs Erste getan. Dieses Wasser hat sich als sehr bedeutsam herausgestellt, doch war es ursprünglich nicht für mich bestimmt. Geht nur und bringt es zu Corithras an der Wegkreuzung. Ich bin mir sicher, er möchte gern, dass Ihr es an seinen eigentlichen Bestimmungsort bringt.",
    ["O"] = "Bringt das gefüllte Gefäß zu Corithras Mondzorn an der Kreuzung vor Darnassus.",
    ["T"] = "Teldrassil: Die Bürde der Kaldorei",
  },
  [936] = {
    ["D"] = "Entschuldigung, $C, könnt Ihr einen Moment Eurer Zeit für mich erübrigen?$B$BDer Zirkel des Cenarius in Donnerfels ist sehr an erfahrenen Abenteurern wie Euch interessiert, die bei einem wichtigen Forschungsprojekt helfen können. Ich weiß zwar nichts Genaues, kann Euch aber sagen, dass kein Geringerer als Hamuul Runentotem an der Spitze dieses Projektes steht.$B$BBitte... wenn Ihr interessiert seid, sprecht direkt mit ihm auf der Anhöhe der Ältesten in Donnerfels.",
    ["O"] = "Sprecht in Donnerfels mit Erzdruide Hamuul Runentotem.",
    ["T"] = "Erzdruide Runentotem helfen",
  },
  [937] = {
    ["D"] = "Man hat mich mit einer kleinen Gruppe Schildwachen hierher beordert, um den Orakelbaum vor den Harpyien zu beschützen, die überall im Tal Nester gebaut haben. Wir versuchen, sie langsam aber sicher zurückzudrängen.$B$BBeim Versuch des Orakelbaums, einen Läufer mit einem Bericht nach Darnassus zu entsenden, wurde dieser von einer Gruppe eben jener Harpyien angegriffen und getötet.$B$BWenn Ihr glaubt, dieser Aufgabe gewachsen zu sein, begebt Euch zu ihren Nestern, erschlagt sie und bringt mir zum Beweis Eurer Taten ihre Gürtel.",
    ["O"] = "Erhaltet 6 Blutfedergürtel.",
    ["T"] = "Die verzauberte Lichtung",
  },
  [938] = {
    ["D"] = "Das Fell der Säblerkatze ist von einer unverwechselbaren grauen Farbe, die sich nur wenig vom sie umgebenden Wald abhebt. Wie sie da liegt, erkennt Ihr, dass sie schwer verletzt ist und tiefe Wunden an Rücken und Bauchseite aufweist.$B$BSie hebt ihren Kopf und starrt Euch an, ihre glänzenden blauen Augen voll wacher Intelligenz. Sie scheint Euch folgen zu wollen.",
    ["O"] = "Begleitet Nebel zur Schildwache Arynia Wolkenbruch beim Mondbrunnen in der Nähe des Orakelbaums.",
    ["T"] = "Nebel",
  },
  [939] = {
    ["D"] = "Ihr untersucht die einfache Holzflöte, die Ihr nach dem Sieg über Xavaric aufgehoben habt.$B$BDas knorrige Holz fühlt sich fest an, aber es scheint, als würden sich die Spiralen und Muster auf der Oberfläche bewegen und herumwirbeln, als Ihr sie in den Händen dreht. Plötzlich überkommt Euch ein Gefühl der Furcht...$B$BIhr schüttelt den Kopf, sicher, dass Euch Euer Gehirn einen Streich spielt.$B$BMöglicherweise tragen andere Satyrn Hinweise darüber mit sich, was Ihr da gefunden habt. Bringt Euren Fund zu dem Druiden Eridan Blauwind, der im südlichen Teil des Teufelswalds lebt.",
    ["O"] = "Bringt die Flöte von Xavaric und 5 Jadefeuerteufelsbinder zu Eridan Blauwind im südlichen Teil des Teufelswalds.",
    ["T"] = "Flöte des Xavaric",
  },
  [940] = {
    ["D"] = "Der Wald flüstert von Euren wagemutigen Heldentaten, $N. Sobald die Harpyien aus ihren Nestern vertrieben waren, spürte ich, wie sich große Ruhe im Wald verbreitete, und dass sich die Kreaturen des Waldes wieder sicher fühlten. Ich habe nicht gewagt, noch einen Boten zum Erzdruiden zu schicken, aber ich weiß, dass Ihr dafür sorgen werdet, dass ihn meine Nachricht sicher erreicht.$B$BBringt ihm dies und wisset, dass der Wald Euch auf all seinen Wegen beschützen wird, sodass Ihr den Wald aus Stein sicher erreicht.",
    ["O"] = "Überbringt dem Erzdruiden Fandral Hirschhaupt in Darnassus den Bericht des Orakelbaums.",
    ["T"] = "Teldrassil",
  },
  [941] = {
    ["D"] = "Ich habe das üble Moos von dem Herzen entfernt, aber es bleibt dennoch besudelt...$B$BVersucht, das Herz in meinem Blumentopf einzusetzen. Er ist mit einer nährstoffreichen Erde gefüllt, die das Herz säubern und heilen kann.",
    ["O"] = "Pflanzt das besudelte Herz in Denalans Blumentopf ein.",
    ["T"] = "Das Einpflanzen des Herzens",
  },
  [942] = {
    ["D"] = "Bringt das rätselhafte Fossil zum Archäologen Magenbitter im Hafen von Menethil.",
    ["O"] = "Bringt das rätselhafte Fossil zum Archäologen Magenbitter im Hafen von Menethil.",
    ["T"] = "Der geistesabwesende Ausgrabungsleiter",
  },
  [943] = {
    ["D"] = "Ausgrabungsleiter Whelgar ist jetzt im Besitz des Originalfundstücks. Er hält sich in seiner Ausgrabungsstätte im Sumpfland auf. Als ich den Stein von Relu entdeckte, glaubte ich, dass er der Schlüssel sei, mit dem das Rätsel des Fossils gelöst werden könne.$B$BAuf dem Weg zu Whelgar wurde ich dann von Raptoren angegriffen und das Relikt ging verloren. Ich weiß nicht, welche dieser scheckigen Bestien das Relikt verschlungen hat, aber falls es Euch gelingen sollte, es zurückzuholen, könnte ich die Macht dieser Artefakte freisetzen.$B$BBringt mir den Stein von Relu und das Originalfossil.",
    ["O"] = "Der Archäologe Magenbitter im Hafen von Menethil möchte, dass Ihr ihm den Stein von Relu und Magenbitters Fossil bringt.",
    ["T"] = "Der geistesabwesende Ausgrabungsleiter",
  },
  [944] = {
    ["D"] = "Wie seltsam, dass sich Naga hier herumtreiben, hier in Dunkelküste. Aber was noch seltsamer ist... es sind auch welche vom Klan des Schattenhammers da. Dieser Kult verehrt die alten, alten Fürsten der Erde. Fürsten, die schon vor Urzeiten besiegt wurden.$B$BDie Meistergleve im Süden ist ein Ort, an dem ein solcher Fürst einst gefallen ist.$B$BBegebt Euch dorthin und sucht nach weiteren Anhängern der Schattenhammerkultisten.$B$BNehmt diese Wahrsagephiole. Erschafft damit eine Wahrsageschale, nachdem Ihr die Meistergleve durchsucht habt. Sprecht dann mit mir mittels der Schale.",
    ["O"] = "Tragt Informationen zusammen und benutzt dann die Wahrsagephiole, um eine Wahrsageschale zu erschaffen. Benutzt die Schale, um mit Onu zu sprechen.",
    ["T"] = "Die Meistergleve",
  },
  [945] = {
    ["D"] = "Hilfe!$B$BDiese dreckigen Kultisten haben mich umzingelt, während ich durch die Meistergleve spazierte. Was für ein Glück, dass ich so gut im Verstecken bin!$B$BKönnt Ihr mir hier raushelfen? Ich sollte auch meiner Schwester Therysil Bescheid geben, dass es mir gut geht. Sie wohnt im Eschental im Süden, beim Schrein von Aessina.",
    ["O"] = "Verhelft Therylune zur Flucht und teilt dann Therysil beim Schrein von Aessina mit, dass ihre Schwester in Sicherheit ist.",
    ["T"] = "Therylunes Flucht",
  },
  [946] = "_",
  [947] = {
    ["D"] = "Ich entwickle gerade einen Trank, für den rare Pilze erforderlich sind. Pilze, die nur in einer bestimmten Höhle wachsen. Diese Höhle liegt hinter den Klippenquellfällen im Osten, leicht nördlich entlang der Berge.$B$BIch würde ja selbst dorthin gehen, aber der Hain der Uralten hat mir geraten, mich von diesem Ort fern zu halten. Unsere altehrwürdigen Verbündeten spüren, dass die Höhle ein Versteck für etwas Neues, Böses in Dunkelküste ist.$B$BBitte, $N, sammelt doch ein paar Pilze für mich, und wenn Ihr schon dabei seid, könntet Ihr auch gleich die Höhle erkunden, um die Befürchtungen der Urtume eventuell zu bestätigen.",
    ["O"] = "Bringt Barithras Mondschatten in Auberdine 5 Schorfstänglinge und 1 Todeskappe.",
    ["T"] = "Höhlenpilze",
  },
  [948] = {
    ["D"] = "Onu, ein Urtum der Lehren im Hain der Uralten, hat von Eurer Reise zu den Klippenquellfällen erfahren und möchte Euch sprechen. Der Hain liegt im Süden, im Schatten der Berge.$B$BDie Urtume sind geduldig und weise, $N. Aber wenn Onu Euch um Rat bezüglich dessen ersucht, was Ihr in den Fällen gesehen habt, fürchte ich, dass es wichtig ist.",
    ["O"] = "Sprecht im Hain der Uralten mit Onu.",
    ["T"] = "Onu",
  },
  [949] = {
    ["D"] = "Niemand weiß, was der Schattenhammer in der Meistergleve vorhat. Aber es muss etwas Böses sein.$B$BDurchsucht ihr Lager bei der Meistergleve... Sucht nach etwas, das uns ihre Absichten verraten könnte.$B$BWir müssen unbedingt wissen, was sie im Schilde führen, bevor wir einen Plan schmieden können.",
    ["O"] = "Sucht im Lager des Schattenhammers in der Meistergleve nach einem Hinweis.",
    ["T"] = "Das Lager des Schattenhammers",
  },
  [950] = {
    ["D"] = "Der Blick in den Schattenhammerfolianten kann einen wahnsinnig machen. Der Text verschwimmt und tanzt vor Euren Augen, kaum dass Ihr ihn einen Moment betrachtet habt, und die einst so wunderschönen Illustrationen verzerren sich zu grotesken Formen und Mustern.$B$BIhr könnt gerade noch eine Seite des irrsinnigen Gekritzels herausreißen, bevor Ihr ohnmächtig werdet.",
    ["O"] = "Bringt Onu das irrsinnige Gekritzel, das Ihr gefunden habt.",
    ["T"] = "Rückkehr zu Onu",
  },
  [951] = {
    ["D"] = "Das Pergament, das Ihr in der Meistergleve gefunden habt, ist eine Seite aus einem alten Text... geschrieben von Elfen! Es ist übelst verflucht und besudelt von der Verderbnis des Schattenhammers, aber es scheint, als würde der Kult alte Elfenmagie benutzen, um seine alten Meister in unsere Welt zurückzuholen.$B$BReist zu den Ruinen von Mathystra im Nordosten und sucht nach alten Relikten unter dem Gras und den zerbrochenen Steinen. Indem wir sie studieren, können wir vielleicht herausbekommen, auf welche Weise der Schattenhammer die Magie der Elfen nutzen will.",
    ["O"] = "Bringt 6 Relikte von Mathystra zu Onu in den Hain der Uralten.",
    ["T"] = "Relikte von Mathystra",
  },
  [952] = {
    ["D"] = "Wenn Ihr genug Zeit hattet, um für den Orakelbaum Nachrichten zu übermitteln, kann ich Euch sicherlich dazu bewegen, eine Nachricht in den Hain der Uralten in Dunkelküste zu bringen, südlich von Auberdine.$B$BIhr werdet höchstwahrscheinlich ein sicheres Transportmittel wie einen Hippogryphen benötigen, aber ich baue darauf, dass Ihr das regeln könnt. Bringt dies Onu, dem Urtum der Lehren. Er wartet auf eine Nachricht von mir, so wie ich auf Neuigkeiten aus der Lichtung des Orakels wartete.",
    ["O"] = "Bringt Fandrals Nachricht zu Onu in den Hain der Uralten in Dunkelküste, südlich von Auberdine.",
    ["T"] = "Der Hain der Uralten",
  },
  [953] = {
    ["D"] = "Im Osten findet Ihr die Ruinen von Ameth\'Aran. Sie werden heute von den ruhelosen Geistern der Hochgeborenen bewohnt, die einst dort lebten. Damals jedoch war es ein Ort, an dem die Diener Azsharas ihre mächtige Magie ungestört praktizieren konnten.$B$BMan hat mich geschickt, die Ruinen zu erkunden und ich stieß auf zwei große Schrifttafeln, auf denen die Geschichten Ameth\'Arans und seines Unterganges beschrieben werden. Während ich die Runen las, traten Geister an mich heran und ich floh.$B$BBitte, wenn es Euch möglich ist, begebt Euch zu den Ruinen und entziffert an meiner statt die Schrifttafeln.",
    ["O"] = "Studiert die Schrifttafeln, die von Ameth\'Aran und ihrem Untergang erzählen, und kehrt dann zur Schildwache Tysha Mondklinge in Dunkelküste zurück.",
    ["T"] = "Der Untergang von Ameth\'Aran",
  },
  [954] = {
    ["D"] = "Die Ruinen von Bashal\'Aran im Osten sind von dämonischen Dienern überrannt worden. Die Grimmlinge und Satyrn haben sich in dem Gebiet niedergelassen und nähren sich von den magischen Energien dort. Ihre Macht wächst, je länger sie sich dort aufhalten.$B$BDennoch habe ich festgestellt, dass es dort einen Schrein gibt, dem sie nicht zu nahe kommen wollen. An der Westseite der Ruinen auf einem kleinen Steilhang entspringt eine seltsame blaue Aura... Das muss eine Erklärung für das Widerstreben der Dämonen sein.$B$BIch hätte gern, dass Ihr das untersucht.",
    ["O"] = "Sucht in den Ruinen von Bashal\'Aran nach der Quelle der seltsamen blauen Aura.",
    ["T"] = "Bashal\'Aran",
  },
  [955] = {
    ["D"] = "Sollte ich jemals gehalten sein, die Geschichte meines Lebens zu erzählen, würde dies zweifellos die Grenzen Eurer Geduld überstrapazieren. Sagen wir einfach, dass mein Leben lang und schmerzvoll war, und dass diese meine spektrale Gestalt die schlimmste Pein von allem ist.$B$BIch werde durch Magie hier festgehalten. Mögen meine Worte auch unaufrichtig erscheinen, so versichere ich Euch doch, dass ich über die Maßen dankbar wäre, würdet Ihr mir helfen, den Grund für meine Gefangenschaft herauszufinden. Ein Siegel bindet mich, und vielleicht finde ich einen Hinweis darauf, wenn ich den Ohrschmuck der Grimmlinge und Grells nur näher betrachten könnte.",
    ["O"] = "Beschafft für Asterion in Bashal\'Aran 8 Grellohrringe.",
    ["T"] = "Bashal\'Aran",
  },
  [956] = {
    ["D"] = "Wenn die Grells erst kürzlich mit dem Siegel in Berührung gekommen sind, das mich an dieses ewige Gefängnis bindet, glaube ich, den Grund dafür zu wissen. Zweifellos ist das Siegel in den Besitz des Satyrs gelangt, der sie anführt.$B$BIch bin mir ganz sicher, dass es so ist, $N. Einer der Satyrn muss es besitzen. Wenn Ihr es an Euch bringen könntet, würde es mich der Überwindung der Gitterstäbe meines Gefängnisses so nahe bringen, dass es mich zu Tränen rühren würde.",
    ["O"] = "Beschafft das alte Mondsteinsiegel und bringt es Asterion in Bashal\'Aran.",
    ["T"] = "Bashal\'Aran",
  },
  [957] = {
    ["D"] = "Es war die Kunst eines der mächtigsten Hochgeborenen, der das Siegel erschuf, das mein Gefängnis war. In Ameth\'Aran, den Ruinen im Süden, die diesen hier aufs Haar gleichen, gibt es sogar heute noch eine uralte Flamme, blau ist ihre Farbe. In dieser Flamme könnte man dieses Siegel zerstören.$B$BAchtet auf die Ruinen, $N!",
    ["O"] = "Zerstört das alte Mondsteinsiegel in der uralten Flamme in Ameth\'Aran und kehrt dann zu Asterion in Bashal\'Aran zurück.",
    ["T"] = "Bashal\'Aran",
  },
  [958] = {
    ["D"] = "$C, ich möchte Euch um etwas bitten.$B$BHabt Ihr die Ruinen von Ameth\'Aran gesehen? Falls nicht, findet Ihr sie auf der Ostseite der Hauptstraße, ein paar Straßen weiter südlich. Einst waren sie die Heimat vieler mächtiger Hochgeborener, doch heute sind sie Zeugnis der Zerstörung, die sie mit ihrem Dilettantismus angerichtet haben.$B$BDie Schildwachen sagten mir, dass die Geister der Hochgeborenen immer noch umgehen und dass sie ihre alten, magischen Utensilien immer noch benutzen. Wir müssen uns diese Relikte aneignen, damit wir sie zerstören können.",
    ["O"] = "Sucht sieben Hochgeborenenrelikte und bringt sie Thundris Windwirker in Auberdine.",
    ["T"] = "Werkzeuge der Hochgeborenen",
  },
  [959] = {
    ["D"] = "Upps!$B$BWie Ihr seht, ist mir ein kleines Malheur passiert! Die Morgenlieferung aus Beutebucht war umfangreich. Wirklich gewaltig! Noch nie habe ich so viele Kisten gesehen. Tja, ich wollte die Sache etwas beschleunigen, deshalb habe ich begonnen mehr abzuladen, als ich auf einmal bewältigen konnte. Und dann ist alles zusammengebrochen.$B$BBevor ich wusste, was überhaupt los war, sah ich diese Nervensäge Zausel den Verrückten mit einer Flasche 99-jährigem Portwein wegrennen. Hab ihn bis zu den Höhlen des Wehklagens verfolgt. Da wollte ich auf keinen Fall rein.$B$BVielleicht seid Ihr ja mutig genug, um nach ihm zu suchen?",
    ["O"] = "Kranführer Moppelfuzz in Ratschet möchte, dass Ihr Zausel dem Verrückten, der sich in den Höhlen des Wehklagens versteckt, die Flasche mit 99-jährigem Portwein wieder abnehmt.",
    ["T"] = "Ärger auf den Docks",
  },
  [962] = {
    ["D"] = "Die Königliche Apothekervereinigung, die in der großen Unterstadt Lordaerons ansässig ist, hat mich geschickt, um eine ganz besondere Aufgabe zu erledigen, $N. Vielleicht möchtet Ihr mich und damit auch die Dunkle Fürstin bei unseren Bemühungen, die Verlassenen weiterzubringen, unterstützen.$B$BIch studiere seit Kurzem eine rare Pflanzenart namens Schlangenflaum. Ich denke, dass dieses Kraut in größeren Mengen außerordentliches Potenzial besitzt.$B$BLeider findet man Schlangenflaum nur in den dunkelsten Winkeln der Höhlen des Wehklagens, einem gefährlichen Höhlensystem im Brachland.",
    ["O"] = "Die Apothekerin Zamah in Donnerfels möchte, dass Ihr zehn Schlangenflaum für sie sammelt.",
    ["T"] = "Schlangenflaum",
  },
  [963] = {
    ["D"] = "Nach den Schlachten um den Brunnen der Ewigkeit hörte ich, dass Ameth\'Aran zerstört wurde und alle Bewohner ums Leben kamen, darunter auch meine geliebte Anaya.$B$BIch hätte nie gedacht, dass mich noch heute, Tausende von Jahren später, die Erinnerungen an Anaya um den Schlaf bringen würden. Ich wanderte wie in Trance durch die Wälder von Dunkelküste und fand mich schließlich in den Ruinen von Ameth\'Aran wieder... wo ich den gequälten Geist meiner Geliebten sah.$B$BSie muss befreit werden, aber ich kann es nicht über mich bringen, dies zu tun. Ihr Geist muss zerstört werden.",
    ["O"] = "Befreit den Geist von Anaya Dämmerflucht und bringt ihren Anhänger zu Cerellean Weißklaue in Auberdine.",
    ["T"] = "In ewiger Liebe",
  },
  [964] = {
    ["D"] = "Um in die Scholomance zu gelangen, benötigt ihr einen Dietrich. Er muss aus den Überresten eines Skeletts geschmiedet werden- eigentlich aus mehreren - und er darf nur durch die stärksten Metalle in einer geeigneten Form gehärtet werden. Ein Signet der Macht von einem Wesen, das das Portal zur Scholomance natürlich öffnen kann, gibt dem Schlüssel seine endgültige Funktion.$B$BAber eines nach dem anderen, $N. Wir benötigen Skelettfragmente für das Schmieden des Schlüssels. Skelette innerhalb von Andorhal sollten den gewünschten Effekt bringen, aber die außerhalb der Mauern könnten vielleicht auch ausreichen.",
    ["O"] = "Bringt 15 Skelettfragmente zu Apotheker Zitters am Bollwerk in Tirisfal.",
    ["T"] = "Skelettfragmente",
  },
  [965] = {
    ["D"] = "Grüße, $R. Ich bin Elissa Sternenhauch und es ist meine Aufgabe, Auberdine vor Schaden zu schützen.$B$BAus diesem Grund habe ich Balthule Schattenhieb ausgeschickt, um die seltsamen Vorkommnisse rund um den Turm von Althalaxx im Nordosten zu beobachten.$B$BEr hätte eigentlich schon längst wieder zurück sein sollen. Daher fürchte ich, dass er im Wald auf eine unvorhersehbare Gefahr gestoßen ist. Ich würde es außerordentlich zu schätzen wissen, wenn Ihr nach ihm suchen und dafür sorgen würdet, dass es ihm gut geht.",
    ["O"] = "Sucht Balthule Schattenhieb in der Nähe des Turms von Althalaxx in Dunkelküste.",
    ["T"] = "Der Turm von Althalaxx",
  },
  [966] = {
    ["D"] = "Eine Gruppe Hexenmeister hat sich in und um den Turm herum niedergelassen. Ich wollte ja schon früher nach Auberdine zurückkehren, um Elissa Bericht zu erstatten, aber ich befürchtete, hier etwas zu verpassen, solange ich fort wäre.$B$BDelgren vermutete, dass sich eine solche Gesellschaft beim Turm versammeln würde, aber er wusste nicht, warum.$B$BEin paar Pergamentfetzen, die die Hexenmeister bei sich führten, sind mir in die Hände gefallen, aber ich brauche noch mehr, um das Rätsel zu lösen. Ich warne Euch jedoch, geht lieber nicht in den Turm hinein, denn die Hexenmeister dort sind noch viel mächtiger!",
    ["O"] = "Sammelt 4 abgenutzte Pergamentfetzen für Balthule Schattenhieb in der Nähe des Turms von Althalaxx.",
    ["T"] = "Der Turm von Althalaxx",
  },
  [967] = {
    ["D"] = "Mein Meister, Delgren der Läuterer, ist ein Paladin, der gütigerweise seine Unterstützung bei der Verteidigung unseres Waldes vor den Streitkräften der Dämonen und Untoten angeboten hat. Er hat mich viel über das Heilige Licht und die Kampfkunst gelehrt.$B$BDelgren muss sofort über die Machenschaften des Kults informiert werden.$B$BIhr findet ihn ganz weit südlich von hier in Maestras Posten im Eschental. Beeilt Euch, ich fürchte nämlich, dass die Bedrohung durch den Dunklen Strang stündlich größer wird.",
    ["O"] = "Überbringt Delgren dem Läuterer im Eschental Balthules Brief.",
    ["T"] = "Der Turm von Althalaxx",
  },
  [968] = {
    ["D"] = "Obwohl das Buch ziemlich abgenutzt und zerfleddert ist, ist der Titel auf dem Buchdeckel noch gut lesbar:$B$B\'Die Macht in der Tiefe\'$B$BIn diesem Buch findet sich eine Auflistung unaussprechlicher Namen mit viel sagenden Betitelungen, Verehrungsmethoden und bevorzugten Opferritualen... bei denen sehr oft lebende, humanoide Kreaturen verwendet werden.$B$BAuf der Innenseite des Einbands stehen die Worte: \'Knochengriffs Runen und Verhängnisse, Düsteres Viertel, Eisenschmiede. Inhaber: Gerrig Knochengriff.\'$B$BWenn Knochengriff derartige Bücher verkauft, nimmt er dieses hier vielleicht zurück.",
    ["O"] = "Bringt das Buch: \'Die Macht in der Tiefe\' zu Gerrig Knochengriff im Düsteren Viertel in Eisenschmiede.",
    ["T"] = "Die Macht in der Tiefe",
  },
  [969] = {
    ["D"] = "Ah, ich seh\'s Euch direkt an: Ihr möchtet, dass ich Euch einen Glücksbringer anfertige. Mau\'ari weiß immer Bescheid!$B$BIm Süden findet Ihr eine Schlucht und Riesen, die komplett aus Eis bestehen. Die Riesen tragen die Splitter manchmal bei sich, aber Ihr könnt sie auch selbst in der Schlucht sammeln. Diese Splitter haben mächtige reflektierende Eigenschaften.$B$BBringt mir 4 Frosthagelsplitter und mein Glücksbringer wird Euch gehören.",
    ["O"] = "Sammelt 4 Frosthagelsplitter für die Hexendoktorin Mau\'ari in der Ewigen Warte.",
    ["T"] = "Das Glück sei mit Euch",
  },
  [970] = {
    ["D"] = "Balthules Brief verkündet Unheil. Dieser Kult des Dunklen Strangs ist mir ein Dorn im Auge, der entfernt werden muss. Ich hatte nordöstlich von hier in Ordil\'Aran schon einmal mit dem Abschaum des Dunklen Strangs zu tun. Eines seiner Mitglieder besitzt einen Seelenedelstein, der, wie ich glaube, das Geheimnis der Macht des Kults enthält.$B$BBringt ihn mir, dann kann ich die darin zu findenden Geheimnisse entschlüsseln.",
    ["O"] = "Beschafft einen leuchtenden Seelenedelstein und bringt ihn Delgren dem Läuterer in Maestras Posten.",
    ["T"] = "Der Turm von Althalaxx",
  },
  [971] = {
    ["D"] = "Wie Ihr vielleicht wisst, trage ich Lehren zusammen. Alte Lehren. Mächtige Lehren. Lehren, die Portale öffnen, und Lehren, die die schlafenden Alten Götter erwecken können.$B$BEs kursieren Gerüchte um einen alten Text, das \'Manuskript von Lorgalis\', das sich tief unten in der Tiefschwarzen Grotte im Westen. Dieser Ort ist die ehemalige Heimat unserer längst verstorbenen Ahnen. Elfen, die über großes Wissen verfügten, bevor ihre Stadt zerfiel.$B$BSucht in der Tiefschwarzen Grotte nach dem Manuskript. Wenn Ihr das tut, werde ich Euch das nie vergessen. Auch nicht... nach dem Ende aller Tage.",
    ["O"] = "Bringt das \'Manuskript von Lorgalis\' zu Schildwachenschülerin Issara zum Stützpunkt an der Grotte.",
    ["T"] = "Wissen in der Tiefe",
  },
  [973] = {
    ["D"] = "Ilkrud Magskluve. Ja, ich kenne ihn. Er ist ein mächtiger orcischer Hexenmeister, der im Schrein des Sengenden Feuers lebt, einem äußerst bösen Ort im südwestlichen Eschental.$B$BBislang ist er nur ein geringes Ärgernis und ich hatte noch nichts mit ihm zu tun, aber es sieht so aus, als wäre jetzt seine Zeit gekommen.",
    ["O"] = "Bringt Delgren dem Läuterer in Maestras Posten Ilkrud Magskluves Folianten.",
    ["T"] = "Der Turm von Althalaxx",
  },
  [974] = {
    ["D"] = "Die heißen Quellen hier sind eine Quelle der Geheimnisse für mich. Woher kommt die Hitze?$B$BWenn ich mir überlege, wie nah diese Teiche beim Feuersäulengrat sind, frage ich mich wirklich, ob der Vulkan etwas damit zu tun hat. Ich habe ein Thermometer entwickelt, um die Temperaturen am Vulkan zu messen, und ich würde gern die heißeste Stelle finden. Es zeigt die Messungen in Grad Kraklenheit an!$B$BHier, nehmt es und messt die Temperaturen am Grat, wann immer Ihr eine heiße Stelle seht!",
    ["O"] = "Krakle im Krater von Un\'Goro möchte, dass Ihr den heißesten Bereich des Feuersäulengrats findet.$B$BImmer, wenn Ihr eine heiße Stelle findet, klickt Ihr das Thermometer mit der rechten Maustaste an, um die Temperatur zu überprüfen. Sucht, bis Ihr die heißeste Stelle gefunden habt.",
    ["T"] = "Die Quelle finden",
  },
  [975] = {
    ["D"] = "Also, bevor ich Euch dies\'n Glücksbringer geb, erklär ich Euch besser, wie er zu verwenden is\'.$B$BWenn Ihr den Glücksbringer bei Euch tragt, werdet Ihr feststell\'n, dass die Kreaturen, gegen die Ihr kämpft, bestimmte Gegenstände fallenlassen. Wenn Ihr den Glücksbringer nich\' dabei habt, werdet Ihr sie nich\' find\'n. Kapiert?$B$BIhr erkennt diese speziellen Gegenstände am Namen - sie werden E\'ko genannt. Wenn Ihr solche Gegenstände findet, bringt Ihr sie einfach der alten Mau\'ari, und sie hilft Euch.$B$BWartet hier, während ich Euren Glücksbringer anfertige.",
    ["O"] = "Wartet einen Moment und sprecht dann erneut mit Hexendoktorin Mau\'ari, um den Glücksbringer zu erhalten.",
    ["T"] = "Behälter von Mau\'ari",
  },
  [976] = {
    ["D"] = "Delgren hat mich gebeten, der Schildwache Sternenhauch in Auberdine Nachrichten und Nachschub zu bringen, aber es schmeckt mir gar nicht, allein dorthin zu gehen. Im Wald sind gefährliche Feinde zugange und ich möchte nicht riskieren, dass der Nachschub und die Informationen in die falschen Hände geraten.$B$BWenn Ihr und ein paar andere als Wachen mit mir kämen, könnte ich sofort gehen. Mit Eurem Schutz zweifle ich nicht daran, dass wir diese Dinge sicher nach Auberdine bringen können.",
    ["O"] = "Sprecht mit Delgren dem Läuterer bei Maestras Posten, nachdem Ihr Feero sicher durch das Eschental geführt habt.",
    ["T"] = "Nachschub für Auberdine",
  },
  [977] = {
    ["D"] = "Nachdem ich jetzt alles habe, was ich benötige, um die mechanischen beweglichen Teile abzudecken, benötige ich nur noch eine weitere Sache. Na ja, eigentlich zwei. Zwei Hörner!$B$BDie Eisdistelpatriarchen und -matriarchinnen haben die größten Hörner, also will ich natürlich diese. Diese Yetiart bewohnt eine Höhle im Südosten. Es gibt viele von ihnen; Ihr solltet keine Probleme haben, sie zu finden.$B$BDenkt daran, ich will nur die bestaussehenden - keine beschädigten oder zerbrochenen Hörner, bitte!",
    ["O"] = "Sammelt 2 makellose Yetihörner für Umi Rumpelkicher in der Ewigen Warte.",
    ["T"] = "Sind wir endlich da, Yeti?",
  },
  [978] = {
    ["D"] = "Wildekinfedern aus dem Hinterland scheinen Spuren magischer Fähigkeiten zu enthalten. Diese Kräfte... sie scheinen von den Kreaturen nicht bewusst eingesetzt zu werden, es scheint eher, als wären sie der Art angeboren; ein Teil ihres innersten Wesens.$B$BWährend meiner Studien habe ich gehört, dass eine große Population von Wildekin die schneebedeckten Länder von Winterquell bewohnt. Könnten diese Kreaturen über die gleichen Kräfte verfügen? Helft mir bitte, eine Antwort zu finden, $N. Begebt Euch nach Winterquell und sammelt die auf dem Boden gefallenen Federn ein. Kehrt dann zu mir zurück.",
    ["O"] = "Sammelt 10 mondbestrahlte Federn aus Winterquell und kehrt dann zu Erelas Himmelsbern in Rut\'theran zurück.",
    ["T"] = "Mondbestrahltes Wildekin",
  },
  [979] = {
    ["D"] = "Ja, ja, diese Federn scheinen die gleiche magische Verzauberung zu enthalten, die mir schon zuvor auffiel... Auch wenn ich mir nicht sicher bin, was wir vorfinden werden, solltet Ihr weiterhin nach Antworten suchen, $N.$B$BEine meiner Kolleginnen reiste kürzlich nach Winterquell, um die Wildekin dort in ihrer heimischen Umgebung zu beobachten. Warum sucht Ihr sie nicht auf und seht, was sie herausgefunden hat?",
    ["O"] = "Sucht nach Ranshalla im östlichen Winterquell.",
    ["T"] = "Ranshalla suchen",
  },
  [980] = {
    ["D"] = "Tja, ich habe von anderen heißen Quellen an einem Ort namens Winterquell gehört, von hier aus gesehen weit im Norden. Das Merkwürdige ist, dass es dort in der Nähe keine Vulkane gibt. Interessant, ich weiß.$B$BAlso, wenn Ihr mehr herausfinden möchtet, solltet Ihr Euch nach Winterquell begeben und mit einer Freundin von mir sprechen. Ihr Name ist Donova Schneeweh und sie hält sich derzeit bei den heißen Quellen dort auf.$B$BIch danke Euch erneut, dass Ihr mir mit meinem Thermometer geholfen habt! Auf dann!",
    ["O"] = "Reist nach Winterquell und sprecht mit Donova Schneeweh.",
    ["T"] = "Die neuen Quellen",
  },
  [982] = {
    ["D"] = "Nahe der Ufer von Dunkelküste im Norden liegen zwei havarierte Schiffe - die Silbermorgen und die Nebelschleier. Vor einiger Zeit kollidierten die beiden Schiffe wegen der verfluchten Murlocs, als sie über das weite Meer nach Auberdine segelten. Jetzt liegen sie auf dem Grund des Ozeans als Trophäen für diese Scheusale.$B$BBeide Kapitäne haben es in dieser Nacht nicht geschafft, ihre Schiffe zu verlassen. Ihre Logbücher und anderen Habseligkeiten befinden sich immer noch dort unten in Schließkassetten. Ich möchte Euch bitten, sie für uns zu beschaffen; es würde den Mannschaftsmitgliedern, die sich noch hier aufhalten, viel bedeuten.",
    ["O"] = "Beschafft die Schließkassette der Silbermorgen und die Schließkassette der Nebelschleier für Gorbold Stahlhand in Auberdine. Beide Gegenstände findet Ihr an Bord der Schiffswracks nördlich des Dorfes.",
    ["T"] = "Tiefer Ozean, weites Meer",
  },
  [983] = {
    ["D"] = "He! Helft mir bei meiner neuesten Erfindung, der Dröhnkiste. Man kann damit mit Leuten sprechen, die weit weg sind!$B$BVielleicht habt Ihr sie schon mal gesehen, das sind kleine Kästchen mit Hebeln daran. Ihr einziges Problem ist, dass sie ständig gewartet werden müssen.$B$BJede hat ein anderes Problem, aber ich hab mir etwas ziemlich Schlaues ausgedacht. Ich habe alle in der Nähe von Kreaturen platziert, die über die passenden Teile verfügen, um die jeweilige Maschine zu reparieren. Momentan hat Dröhnkiste 827 den Geist aufgegeben. Sie steht südlich von Auberdine, also gleich in der Nähe. Es werden sechs Kriecherbeine benötigt, um sie zu reparieren. Ich bezahl Euch auch dafür...",
    ["O"] = "Sammelt 6 Kriecherbeine und baut sie in Dröhnkiste 827 ein.",
    ["T"] = "Dröhnkiste 827",
  },
  [985] = {
    ["D"] = "Ihr habt Euer Talent im Ausspähen unserer Feinde bereits unter Beweis gestellt, $N. Aber habt Ihr auch das Zeug dazu, sie zu bekämpfen?$B$BNicht alle Abenteurer ziehen ein offenes Gefecht der Kunst der Verstohlenheit und des Ausweichens vor.$B$BWenn Ihr glaubt, der Aufgabe gewachsen zu sein, solltet Ihr Euch das Furbolglager südlich von Auberdine vornehmen. Das stellt momentan die größte Bedrohung für unser Volk dar. Ihr werdet dort ein paar Mitglieder des Schwarzfellstamms finden. Tötet 8 Pfadfinder und 5 Windsprecher und kommt dann zu mir zurück.",
    ["O"] = "Tötet 8 Pfadfinder der Schwarzfelle und 5 Windsprecher und kehrt dann zu Terenthis in Auberdine zurück.",
    ["T"] = "Wie groß ist die Bedrohung?",
  },
  [986] = {
    ["D"] = "$N, Eure Fertigkeiten haben mir bei meinen Bemühungen sehr geholfen. Kann ich es wagen, Euch dennoch erneut zu bitten, Grimmklaue und seinem Meister Volcor zu helfen? Ich könnte Euch dazu einen magischen Umhang anfertigen, mit dem Ihr ungehindert an den Kreaturen Dunkelküstes vorbeigelangt, während Ihr nach ihm sucht.$B$BUm den Umhang anzufertigen, benötige ich fünf Mondpirscherpelze von einer Mondpirschermatriarchin oder einem Mondpirscherpatriarchen, um genügend Material zur Verfügung zu haben. Die Katzen findet Ihr südlich von hier, in der Nähe der Wildschnellen, oder noch weiter im Süden, in der Nähe des Eschentals.",
    ["O"] = "Sucht 5 feine Mondpirscherpelze und bringt sie Terenthis in Auberdine.",
    ["T"] = "Ein verirrter Meister",
  },
  [987] = "_",
  [990] = {
    ["D"] = "$N, meine Herrin Raene Wolfsläufer erwartet Euch in Astranaar im Eschental. Mit Eurer Hilfe könnten wir vielleicht wenigstens eines Teils der Verderbnis Herr werden, die sich dort breitgemacht hat.$B$BGeht von hier aus nach Süden und haltet Euch dicht an der Straße. Wenn Ihr Euch an diese Richtungsangaben haltet, erreicht Ihr problemlos Euer Ziel.",
    ["O"] = "Sucht Raene Wolfsläufer im Eschental auf.",
    ["T"] = "Marsch ins Eschental",
  },
  [991] = {
    ["D"] = "Eine Freundin von mir aus Astranaar, Raene Wolfsläufer, hat mich gebeten, einen Kundschafter zum Falathimsee im Westen zu schicken. Teronis ist vor Tagen aufgebrochen, kurz bevor die Horde angegriffen hat.$B$BEr war auf der Suche nach einem Gegenstand, von dem er dachte, dass er die Furbolgangriffe auf unser Volk mildern könnte: eine Rute, die von einem mittlerweile toten, bösen Zauberer.$B$BBevor er aufbrach, erwähnte er einen Edelstein für die Rute, von dem er wusste, dass er in einem Schrein auf einer Insel im See ruht.$B$BLeider wissen wir, dass der See von einer bösen Macht heimgesucht worden ist. Bitte, $N, macht meinen Freund ausfindig.",
    ["O"] = "Sucht am Falathimsee im Eschental nach Teronis.",
    ["T"] = "Findet Teronis",
  },
  [992] = {
    ["D"] = "Wenn Ihr interessiert seid, hätten wir einen offenen Posten im Rang eines temporären Unterfeldmessers in der Gadgetzan Water Company.$B$BDa die Nomaden unsere Brunnen erobern, müssen wir weitere freie Wasserquellen ausschöpfen! Es gibt Wasser in der Wüste, aber nur für die, die klug genug sind, sie erst zu erforschen.$B$BNehmt diesen Wünschelrutenapparat und zapft damit eine Wasserprobe vom Teich in der Nähe der Sandmarterwache. Er befindet sich direkt in Sichtweite der Trolle dort. Bringt mir den angezapften Apparat zurück, wenn Ihr fertig seid!",
    ["O"] = "Benutzt den unangezapften Wünschelrutenapparat in der Nähe des Teichs bei der Sandmarterwache. Bringt den angezapften Wünschelrutenapparat zum Leitenden Feldmesser Rutenwünschels nach Gadgetzan zurück, wenn Ihr die Probe entnommen habt.",
    ["T"] = "Gadgetzan Wassersondierung",
  },
  [993] = {
    ["D"] = "Der Umhang ist fertig, $N. Jetzt ist es an der Zeit, Volcor zu suchen. Ich hoffe nur, dass wir es noch rechtzeitig schaffen.$B$BDie Magie des Umhangs hält nicht lang an, nachdem sie herbeigerufen wurde - etwa fünf Minuten. Es liegt an Euch zu entscheiden, wann Ihr sie benutzt: Ich schlage vor, dass Ihr damit wartet, bis Ihr Grimmklaue gefunden habt.$B$BFalls es wahr ist, was er mir erzählte, findet Ihr seinen Meister in einer Höhle südlich von hier. Grimmklaue wird an der Straße auf Euch warten. Gebt ihm ein Signal, indem Ihr winkt, sobald Ihr ihn gesehen habt, dann wird er Euch den Weg zu seinem Meister weisen.",
    ["O"] = "Sucht Volcor in Dunkelküste und gebt ihm den verzauberten Mondpirscherumhang.",
    ["T"] = "Ein verirrter Meister",
  },
  [994] = {
    ["D"] = "Gut, $N, dann sei es Gewalt. Lasst uns hier verschwinden, sobald Ihr bereit seid. Wir werden uns unseren Weg hinunter zur Straße erkämpfen - Grimmklaue dürfte immer noch dort sein. Sobald wir sie erreicht haben, können wir uns trennen.$B$BTerenthis wird sofort Eure Version der Geschichte hören wollen, also geht nach Auberdine, sobald wir es hier herausgeschafft haben.",
    ["O"] = "Beschützt Volcor, bis Ihr die Straße erreicht habt und sprecht dann mit Terenthis in Auberdine.",
    ["T"] = "Flucht durch Gewalt",
  },
  [997] = {
    ["D"] = "Seid Ihr nach Süden unterwegs? Zum Al\'Amethsee? Wenn dem so ist, möchte ich Euch um einen Gefallen bitten...$B$BMein Kollege Denalan hat am Ostende des Sees ein Lager aufgeschlagen, in dem er die Pflanzenwelt Teldrassils studiert und Experimente betreibt. Er hat in Darnassus ein Päckchen seltener Erden bestellt, und die Lieferung kam erst kürzlich mit Verspätung hier in Dolanaar an.$B$BWürdet Ihr ihm dieses Päckchen wohl bringen?",
    ["O"] = "Bringt Denalan am Al\'Amethsee das Päckchen seltener Erden.",
    ["T"] = "Denalans Erde",
  },
  [999] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Wenn Träume zu Alpträumen werden",
  },
  [1000] = {
    ["D"] = "Aufgemerkt, Helden!$B$BDer Zirkel des Cenarius sucht nach einsatzfähigen Individuen, die dazu beitragen möchten, ihn bei der Erforschung der riesigen neuen Grenzgebiete Kalimdors zu unterstützen! Weit im Westen von Tanaris und Gadgetzan liegt eine abschreckende Ödnis voller unvorstellbarer Gefahren, und der Zirkel braucht ehrbare und gewillte Vertreter, die an seiner statt agieren.$B$BInteressenten mögen bitte zwecks einer Audienz beim Zirkel des Cenarius vorstellig werden. Weitere Informationen erhaltet Ihr beim Erzdruiden Hamuul Runentotem auf der Anhöhe der Ältesten in Donnerfels.",
    ["O"] = "Sprecht auf der Anhöhe der Ältesten in Donnerfels mit dem Erzdruiden Hamuul Runentotem über den Aufruf des Zirkels des Cenarius zur Erforschung der Grenzgebiete von Kalimdor.",
    ["T"] = "Die neue Grenze",
  },
  [1001] = {
    ["D"] = "Aus dem Inneren der Maschine ist eine leise Stimme zu hören.$B$B\"Hier spricht Hexknall! Die nächste Dröhnkiste befindet sich nördlich von Auberdine, am Strand.$B$BAlso, es ist die Dröhnkiste mit der Nummer 411 und es werden 3 Drescheraugen benötigt, um sie zu reparieren. Sie befinden sich direkt vor der Küste! Die Dunkelküstendrescher... *Hick*... Tut mir leid...$B$BÜbrigens, wie beim letzten Mal wird die Dröhnkiste Eure Belohnung ausspucken, sobald Ihr die Sachen in die Maschine eingebaut habt. *Hick*... Was? Nein, mir geht\'s gut!\"",
    ["O"] = "Besorgt Euch 3 Drescheraugen von den Dunkelküstendreschern im tiefen Meer in der Nähe der Dröhnkiste 411.",
    ["T"] = "Dröhnkiste 411",
  },
  [1002] = {
    ["D"] = "Die Dröhnkiste gibt statische Geräusche von sich, als Hexknall zu sprechen beginnt.$B$B\"Die nächste Dröhnkiste ist Nummer 323. Schteht nördlich von Auberdine, aber... wo\'s das Bier? Wa...? Oh, schteht direkt an der Straße, kann man gar nicht verfehlen, gleich neben der Brücke.\"$B$BHexknall murmelt wirres Zeug vor sich hin, bevor Ihr ein lautes Schluckgeräusch hört.$B$B\"Der da fehlen Mondpirscherfangzähne... sechs davon.\"",
    ["O"] = "Sammelt 6 Mondpirscherfangzähne und baut sie in Dröhnkiste 323 ein.",
    ["T"] = "Dröhnkiste 323",
  },
  [1003] = {
    ["D"] = "Hexknalls zitternde Stimme übertönt die Blubber- und Tropfgeräusche, die aus dem Inneren der Maschine dringen.$B$B\"Näxte is\' die Straße weiter runter nach Süden. Is\' leicht zu find\'n... direkt am Straßenrand... neben ei\'m Sssaun. Glaub, ich hab sie hinner\'n paar Büscheln verschteckt... Büschen. Hehehe... Jaaa, Dröhnkiste 525 fehlen ergraute Skalps von dies\'n grauhaarigen Dischtelbären. Mann, das schpricht sich blöd. Dissstelbär... Hehehe...\"$B$BIhr hört weitere Schluckgeräusche, dann schaltet sich die Maschine aus. Plötzlich erwacht sie jedoch wieder zum Leben.$B$B\"4!\"",
    ["O"] = "Sammelt 4 ergraute Skalps von ergrauten Distelbären südlich von Auberdine und legt sie in die Dröhnkiste 525.",
    ["T"] = "Dröhnkiste 525",
  },
  [1004] = {
    ["D"] = "Aufgemerkt, Helden!$B$BDer Zirkel des Cenarius sucht nach einsatzfähigen Individuen, die dazu beitragen möchten, ihn bei der Erforschung der riesigen neuen Grenzgebiete Kalimdors zu unterstützen! Weit im Westen von Tanaris und Gadgetzan liegt eine abschreckende Ödnis voller unvorstellbarer Gefahren, und der Zirkel braucht ehrbare und gewillte Vertreter, die an seiner statt agieren.$B$BInteressenten mögen bitte zwecks einer Audienz beim Zirkel des Cenarius vorstellig werden. Weitere Informationen erhaltet Ihr beim Erzdruiden Hamuul Runentotem auf der Anhöhe der Ältesten in Donnerfels.",
    ["O"] = "Sprecht auf der Anhöhe der Ältesten in Donnerfels mit dem Erzdruiden Hamuul Runentotem über den Aufruf des Zirkels des Cenarius zur Erforschung der Grenzgebiete von Kalimdor.",
    ["T"] = "Die neue Grenze",
  },
  [1005] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Was dahinter lauert",
  },
  [1006] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Was dahinter liegt",
  },
  [1007] = {
    ["D"] = "Ich würde Euch gern eine Aufgabe übertragen, $N.$B$BIch war auf meinem kleinen Schiff und kreuzte über den versunkenen Ruinen von Zoram, als ich plötzlich von Naga angegriffen wurde, die aus dem Wasser stießen und mich mit ihren Klauen beharkten! Ich floh und der Rest ist, wie man so schön sagt, Geschichte.$B$BIch bin eine Art Archäologe und versuche die Artefakte der versunkenen Stadt zu bergen.$B$BWürdet Ihr Euch zum Zoramstrand im Südwesten aufmachen und alle antiken Statuetten bergen, die Ihr finden könnt? Sie sind der Grund, warum ich die Gefahren des Zoramstands auf mich genommen habe.",
    ["O"] = "Sammelt 10 Antike Statuetten.",
    ["T"] = "Die antiken Statuetten",
  },
  [1008] = {
    ["D"] = "Böses lauert an unserer nordwestlichen Küste, die als Zoramstrand bekannt ist.$B$BEs ist die Ruhestätte der untergegangenen Stadt Zoram, die während der Großen Zerschlagung zerstört wurde und im Meer versank. Die Nachtelfen verloren sie vor Urzeiten. Sie war verloren und fast vergessen.$B$BJetzt allerdings sind die Naga zurückgekehrt, aber warum, wissen wir nicht. Es ist auch unwichtig. Wir müssen diese Scheusale vernichten und zurück in die Tiefe treiben!$B$BKehrt zu mir zurück, wenn Ihr Eure Mission abgeschlossen habt.",
    ["O"] = "Bringt Shindrell Feuerflink in Astranaar 20 Köpfe von Rächerflossen.",
    ["T"] = "Der Zoramstrand",
  },
  [1009] = {
    ["D"] = "Ich fürchte, die Geheimnisse der antiken Statuetten werden für immer verborgen bleiben, wenn Ihr nicht den Schlüssel zu ihren antiken Schlössern findet.$B$BDieser Schlüssel ist ein Ring, der Ring von Zoram. Er gehörte einst den Herrschern dieser Stadt, bevor sie in den Wellen der Großen Zerschlagung unterging.$B$BÜber Jahre hinweg war der Ring verloren, aber die Naga haben ihn vor Kurzem gefunden.$B$BRuuzel, ihre Anführerin, haust auf einer Insel am nördlichen Ende des Zoramstrands. Bitte, $N, beschafft mir den Ring!",
    ["O"] = "Nehmt den Ring von Zoram an Euch.",
    ["T"] = "Ruuzel",
  },
  [1010] = {
    ["D"] = "Es gibt eine Pflanze, die in den alten Ruinen von Bathrans Schlupfwinkel im Osten wächst. Die Planze heißt Bathranshaar und es heißt, sie könne Erkrankungen des Geistes heilen.$B$BDieser Tage ist die Horde dort damit beschäftigt, die Pflanzen für ihre eigenen finsteren Pläne zu sammeln.$B$BEs gibt ein krankes Kind in Astranaar und wir glauben, dass ihre Krankheit mehr als nur körperlicher Natur ist. Ich habe meinem Meister versprochen, dass ich nach einem Heilmittel suchen würde, nur bin ich kein Kämpfer.$B$BWürdet Ihr die Pflanzen für mich aus Bathrans Schlupfwinkel besorgen?",
    ["O"] = "Sammelt 8 Bathranshaar.",
    ["T"] = "Bathranshaar",
  },
  [1011] = {
    ["D"] = "Die Verlassenen, der untote Arm der Horde, haben sich ins Eschental eingeschlichen und treiben sich in unseren Ruinen und Grabhügeln herum. Wir glauben, dass sie die Krankheit im Land der Nachtelfen verbreiten wollen. Wir müssen das genau wissen!$B$BDie Verlassenen haben südlich von hier, in der Nähe des Grabhügels von Dor\'Danil, ein Lager, in dem sie Gifte und Krankheiten entwickeln und in Flaschen füllen. Kämpft Euch in das Lager hinein und stehlt eine Krankheitsflasche. Bringt mir eine solche Flasche. Ich werde sie testen und herausfinden, was sie bewirkt.",
    ["O"] = "Bringt Kayneth Stillwind in Waldeslied eine Krankheitsflasche.",
    ["T"] = "Verlassenenkrankheit",
  },
  [1012] = {
    ["D"] = "Die Druiden sind tot, von ihren Anführern ermordet, die wegen der Verunreinigung des Herzen des Waldes durch die Horde in den Wahnsinn getrieben wurden! Ich kann die Verderbnis sogar hier spüren und wage mich nicht näher heran.$B$BZwar bin ich zu sehr im Einklang mit dem Eschental und dem Herz des Waldes, Ihr jedoch... Ihr könntet in den Grabhügel hinabsteigen und tun, was getan werden muss. Ich habe im Inneren eine Spur für Euch gelegt.$B$BAuch wenn es mir das Herz zerbricht, muss ich Euch bitten, die einst so großen Anführer der Druiden zur letzten Ruhe zu betten: Taneel Finsterwald, Uthil Mondweise, Mavoris Wolkenbruch.",
    ["O"] = "Tötet die wahnsinnigen Druiden Taneel Finsterwald, Uthil Mondweise und Mavoris Wolkenbruch.",
    ["T"] = "Irrsinnige Druiden",
  },
  [1013] = {
    ["D"] = "$N, in der Burg Schattenfang gibt es ein Buch, das Buch von Ur, das ich unbedingt in meiner Sammlung haben möchte. Ur war einst ein großer Magier aus Dalaran bevor die Geißel kam. Seine Studien anderer Welten sind von großem Wert für... bestimmte Kreise der Verlassenen.$B$BBetretet die Burg Schattenfang und sucht das Buch. Bringt es mir, dann kann ich unserer Dunklen Fürstin von Euren Diensten berichten...",
    ["O"] = "Bringt dem Bewahrer Bel\'dugur im Apothekarium in Unterstadt das Buch von Ur.",
    ["T"] = "Das Buch von Ur",
  },
  [1014] = {
    ["D"] = "Arugal lebt immer noch in der Burg Schattenfang. Wir können Silberwald erst dann als strategische Festung für die Dunkle Fürstin beanspruchen, wenn Arugal erschlagen ist.$B$BIch werde mich darum kümmern, seine Magie zu neutralisieren, $N. Ich überlasse es jedoch Euch, für Arugals Tod zu sorgen, den er mehr als verdient.$B$BReist zur Burg Schattenfang und setzt Arugals üblen Zaubern ein für alle Mal ein Ende. Bringt mir den Kopf dieses niederträchtigen Hexers!",
    ["O"] = "Tötet Arugal und bringt Dalar Morgenweber in dem Grabmal seinen Kopf.",
    ["T"] = "Arugal muss sterben",
  },
  [1015] = {
    ["D"] = "Aufgemerkt, Helden!$B$BDer Zirkel des Cenarius sucht nach einsatzfähigen Individuen, die dazu beitragen möchten, ihn bei der Erforschung der riesigen neuen Grenzgebiete Kalimdors zu unterstützen! Weit im Westen von Tanaris und Gadgetzan liegt eine abschreckende Ödnis voller unvorstellbarer Gefahren, und der Zirkel braucht ehrbare und gewillte Vertreter, die an seiner statt agieren.$B$BInteressenten mögen bitte zwecks einer Audienz beim Zirkel des Cenarius vorstellig werden. Sprecht in Darnassus mit dem Erzdruiden Fandral Hirschhaupt, er hat weitere Informationen!",
    ["O"] = "Sprecht in der Enklave des Cenarius in Darnassus mit dem Erzdruiden Fandral Hirschhaupt über den Aufruf des Zirkels des Cenarius zur Erforschung der Grenzgebiete von Kalimdor.",
    ["T"] = "Die neue Grenze",
  },
  [1016] = {
    ["D"] = "Wir hätten die Silberwindzuflucht ohne Weiteres gegen den Ansturm der Horde halten können, wenn sie uns nicht die Kontrolle über unsere Wasserelementarverteidiger im Mystralsee entrissen hätten.$B$BEiner ihrer Schamanen hat Tideress heraufbeschworen, einen alten, wütenden Wassergeist des Waldes, der unsere Elementare besudelt und gegen uns aufgebracht hat. Da sowohl der See als auch die Horde über uns hereingebrochen sind, hatten wir keine andere Wahl, als uns hierher zur Spitze zurückzuziehen.$B$BBegebt Euch nach Osten. Erobert den See zurück, sodass eine Chance besteht, dass wir eines Tages die Zuflucht wieder einnehmen können.",
    ["O"] = "Tötet 12 beschmutzte Wasserelementare und Tideress.",
    ["T"] = "Die Rückeroberung des Mystralsees",
  },
  [1017] = {
    ["D"] = "Es gibt nur einen untoten Magier im Brachland, der in der Lage wäre, so viele Wasserelementare zu entweihen. Er ist auch der Einzige, der dumm genug wäre, es zu versuchen. Sarilus Faulmut sitzt wahrscheinlich jetzt gerade oben auf dem Glutnebelgipfel und lacht mich aus.$B$B$N, Ihr müsst Euch in Feindesland begeben und diesen Übeltäter vernichten. Ich habe genug von seinem Schwachsinn und wir müssen ihm zeigen, was mit jenen geschieht, die die Schildwachen verärgern.$B$BVernichtet ihn und bringt mir seinen Kopf. Vorsicht allerdings, die Horde könnte nach Euch Ausschau halten.",
    ["O"] = "Erschlagt Sarilus Faulmut und bringt Schildwache Velene Sternenschlag in der Silberwindzuflucht seinen Kopf.",
    ["T"] = "Magier-Beschwörer",
  },
  [1018] = {
    ["D"] = "Aufgemerkt, Helden!$B$BDer Zirkel des Cenarius sucht nach einsatzfähigen Individuen, die dazu beitragen möchten, ihn bei der Erforschung der riesigen neuen Grenzgebiete Kalimdors zu unterstützen! Weit im Westen von Tanaris und Gadgetzan liegt eine abschreckende Ödnis voller unvorstellbarer Gefahren, und der Zirkel braucht ehrbare und gewillte Vertreter, die an seiner statt agieren.$B$BInteressenten mögen bitte zwecks einer Audienz beim Zirkel des Cenarius vorstellig werden. Weitere Informationen erhaltet Ihr beim Erzdruiden Hamuul Runentotem auf der Anhöhe der Ältesten in Donnerfels.",
    ["O"] = "Sprecht auf der Anhöhe der Ältesten in Donnerfels mit dem Erzdruiden Hamuul Runentotem über den Aufruf des Zirkels des Cenarius zur Erforschung der Grenzgebiete von Kalimdor.",
    ["T"] = "Die neue Grenze",
  },
  [1019] = {
    ["D"] = "Aufgemerkt, Helden!$B$BDer Zirkel des Cenarius sucht nach einsatzfähigen Individuen, die dazu beitragen möchten, ihn bei der Erforschung der riesigen neuen Grenzgebiete Kalimdors zu unterstützen! Weit im Westen von Tanaris und Gadgetzan liegt eine abschreckende Ödnis voller unvorstellbarer Gefahren, und der Zirkel braucht ehrbare und gewillte Vertreter, die an seiner statt agieren.$B$BInteressenten mögen bitte zwecks einer Audienz beim Zirkel des Cenarius vorstellig werden. Sprecht in Darnassus mit dem Erzdruiden Fandral Hirschhaupt, er hat weitere Informationen!",
    ["O"] = "Sprecht in der Enklave des Cenarius in Darnassus mit dem Erzdruiden Fandral Hirschhaupt über den Aufruf des Zirkels des Cenarius zur Erforschung der Grenzgebiete von Kalimdor.",
    ["T"] = "Die neue Grenze",
  },
  [1020] = {
    ["D"] = "$N, nun da es von Elunes gesegnetem Licht durchflutet wurde, glaube ich, dass das Gebräu dem kranken Kind helfen wird. Bitte bringt es nach Astranaar und gebt es dem Vater des Kindes, Pelturas Weißmond.$B$BUm nach Astranaar zu gelangen, folgt ihr der Straße nach Süden und dann nach Osten. Wenn Ihr aber schneller dorthin gelangen wollt, sprecht mit Schildwache Avana.",
    ["O"] = "Bringt Orendils Heilung zu Pelturas Weißmond in Astranaar im Eschental.",
    ["T"] = "Orendils Heilung",
  },
  [1021] = {
    ["D"] = "Ich weiß zwar nicht, wer Ihr seid, aber ich brauche Eure Hilfe. Meine Schwestern wollten den Satyrn den Zweig des Cenarius abnehmen. Sie sagten, dass sie bald zurück sein werden, aber das war vor zwei vollen Tagen!$B$B$N, Ihr müsst sie finden! Sie wollten nach den Ruinen eines Nachtelfentempels in Xavian im Nordwesten suchen. Ich weiß nicht, was ich davon halten soll. Sucht nach Anilia, sie wollte sie bei der Suche führen.",
    ["O"] = "Sucht in Xavian im Eschental nach Anilia.",
    ["T"] = "Elende Satyrn! Die Dryaden sind in Gefahr!",
  },
  [1022] = {
    ["D"] = "Obwohl wir viele Mittel und große Bemühungen investiert haben, um auch die letzten Dämonen aus dem Teufelswald im Norden zu vertreiben, hatten wir nur wenig Erfolg damit. Wir konnten jedoch einen Großteil der Dämonen vom Eschental fern halten.$B$BIm Norden, nahe der Grenze zum Teufelswald, wurde der verfallene Mel\'Thandris-Schrein von rätselhaften Wolfmenschen übernommen. Ihre Schreie, die einem das Blut in den Adern gerinnen lassen, haben dem als Das Heulende Tal bekannten Gebiet seinen Namen gegeben. Der Foliant von Mel\'Thandris, der im Schrein aufbewahrt wird, könnte etwas Licht darauf werfen, warum die Wolfmenschen hier sind.",
    ["O"] = "Begebt Euch ins Heulende Tal, studiert den Folianten von Mel\'Thandris und kehrt dann zur Schildwache Melyria Frostschatten in Waldeslied zurück.",
    ["T"] = "Das Heulende Tal",
  },
  [1023] = {
    ["D"] = "Während Ihr Teronis Habseligkeiten durchstöbert, stoßt Ihr auf ein Tagebuch, das noch in gutem Zustand ist.$B$BIhr nehmt das Tagebuch und lasst Teronis\' Kadaver so wie er ist zurück.$B$BJetzt müsst Ihr nur noch eines tun: den Edelstein finden, den Teronis suchte. Orendil deutete an, dass er sich in diesem Gebiet befinden würde. Vielleicht haben ihn die Ranken...",
    ["O"] = "Findet den leuchtenden Edelstein.",
    ["T"] = "Findet Teronis",
  },
  [1024] = {
    ["D"] = "Ah, da seid Ihr ja, $N. Ich habe mich in die Anmerkungen vertieft, die Teronis gemacht hat, und es sieht so aus, als ist Euer nächster Schritt, die Teile von Dartols Rute zu finden.$B$BIch würde zunächst nach Osten reisen, zum Rajenbaumturm.$B$BDort findet Ihr eine Dryade namens Shael\'dryn, die Euch eine bessere Führerin sein wird. Sucht sie auf.$B$BBehaltet den Edelstein bei Euch - Shael\'dryn wird ihn brauchen, um Euch bei der Wiederherstellung der Rute zu helfen.",
    ["O"] = "Sucht Shael\'dryn am Rajenbaumturm im Eschental auf.",
    ["T"] = "Findet Teronis",
  },
  [1025] = {
    ["D"] = "Bevor Ihr zum Mondbrunnen aufbrecht, habe ich noch eine andere Aufgabe für Euch, wenn Ihr gern Eure Fertigkeiten auf die Probe stellen und dem Volk des Eschentals helfen würdet.$B$BEntlang der Straße, die nach Osten führt, haben Furbolgs kurz vor dem Falfarren Reisende angegriffen. Ihre gequälten Seelen nähren sich von Angst und sie stillen ihren Appetit mit dem Blut Unschuldiger.$B$BHelft den Schildwachen, das Land zu schützen, indem Ihr diesen Monstern dieselbe Gnade zeigt, die sie uns erwiesen haben.$B$BIch glaube, ihre Lager liegen südlich der Straße... seid vorsichtig.",
    ["O"] = "Tötet 1 Höhlenbehüter, 2 Ursa, 10 Totemiker sowie 12 Krieger der Fäulnisklauen und kehrt dann zu Raene Wolfsläufer in Astranaar zurück.",
    ["T"] = "Eine aggressive Verteidigung",
  },
  [1026] = {
    ["D"] = "Ich wünschte, Ihr hättet bessere Nachrichten.$B$BRaene hat Recht, ich weiß von Dartols Werk und den Treants, den Baumwesen in diesem Gebiet - zumindest wusste ich es einst. Heute sind sie verderbt, ihre Natur verzerrt.$B$BFalls es Euer Ziel ist, das nächste Teil der Rute zu finden, solltet Ihr zunächst den Schlüssel suchen, mit dem die Truhe geöffnet werden kann, in der es sich befindet.$B$BEiner der Treants nordöstlich von hier in der Nähe des Teufelswalds hat ihn. Sobald Ihr ihn habt, sucht nach einem kleinen Tal westlich der Straße nahe der Grenze zum Teufelswald am Heulenden Tal. Wenn Ihr es gefunden habt, sucht nach einer kleinen Lichtung hinter dem Tunnel in diesem Tal. Die Truhe ist dort irgendwo versteckt.",
    ["O"] = "Sucht den hölzernen Schlüssel und benutzt ihn, um den Eisenschaft von Dartols Rute zu finden.",
    ["T"] = "Findet Teronis",
  },
  [1027] = {
    ["D"] = "Das letzte Stück der Rute ist im Besitz der Druiden der Klaue. Ich habe ihren Anführer, Oso Dornennarbe, gebeten, es mir auszuhändigen, aber er und seine Wächter hätten mich beinahe umgebracht! Offenbar hat die Verderbnis des Herzens des Waldes seitens der Horde auch die Druiden betroffen.$B$BAber ich habe eine Idee, wie Ihr in Osos Höhle gelangen und den Knauf an Euch nehmen könnt.$B$BSchon als kleiner Faun war ich immer ziemlich gut darin, mich tot zu stellen. Das funktioniert wunderbar gegen Bären! Ich kann es Euch beibringen! Geht nach Osten zum Schattenwinkel und wenn sie versuchen, Euch anzugreifen, probiert einfach meine Taktik aus.",
    ["O"] = "Erhaltet das letzte Teil von Dartols Rute, den Eisenschwertknauf.",
    ["T"] = "Findet Teronis",
  },
  [1028] = {
    ["D"] = "Hier habt Ihr die vollständige Rute, $N. Es war nicht schwer, sie wieder zusammenzusetzen... Den Schrein zu finden, der die Kraft hat, sie wieder aufzuladen, ist der schwierige Teil.$B$BMit etwas Konzentration und Einsatz sollte es für jemanden wie Euch, der bereits so viel erreicht hat, allerdings nicht zu schwer sein.$B$BGeht von hier aus nach Südosten - es gibt nur wenige Wege, also seid vorsichtig - und behaltet immer den Norden im Auge. Der Schrein liegt in den Bergen verborgen, die dieses Tal umgeben.$B$BDort angekommen legt Ihr die Rute in den Schrein... der Rest ist Magie. Hehe.",
    ["O"] = "Sucht den verborgenen Schrein und verzaubert Dartols Rute erneut.",
    ["T"] = "Findet Teronis",
  },
  [1029] = {
    ["D"] = "Ich schlage vor, dass Ihr Raene aufsucht und sie wissen lasst, dass die Rute fertig gestellt ist. Sie weiß bestimmt, was Ihr als Nächstes tun solltet.$B$BSeid vorsichtig damit... ich spüre eine große Macht darin, zum Guten wie auch zum Bösen. Ich hoffe nur, dass Raene und Ihr wisst, was Ihr tut.$B$BViel Glück, $N.",
    ["O"] = "Liefert Dartols Rute bei Raene Wolfsläufer in Astranaar im Eschental ab.",
    ["T"] = "Findet Teronis",
  },
  [1030] = {
    ["D"] = "Leider bin ich mir nicht ganz sicher, was er als Nächstes vorhatte.$B$BWenn es wahr ist, was er über die Macht der Rute sagte, könnt Ihr mit ihrer Hilfe eine völlig andere Gestalt annehmen, vermutlich die eines Furbolgs.$B$BVielleicht wollte er ihre Kräfte nutzen, um näher an die besudelten Furbolgs heranzukommen, aber selbst die nicht verderbten Kreaturen hätten ihn getötet, wäre er nicht vorsichtig gewesen. Durch die Rute verwandelt, hätte Dartol wahrscheinlich mit einem Furbolg gesprochen, der noch unverderbt war.$B$BIch würde meine Suche bei dem Furbolg beginnen, der als Krolg bekannt ist. Sucht nach seiner Hütte südöstlich vom Mystralsee.",
    ["O"] = "Benutzt Dartols Rute, um mit Krolg bei Krolgs Hütte im Eschental kommunizieren zu können.",
    ["T"] = "Findet Teronis",
  },
  [1031] = {
    ["D"] = "Anilia legt Euch die Hände auf die Schultern und zieht Euch zu sich heran. Nach Luft ringend zeigt sie in Richtung des Bogens im Norden, bevor sich ihre Augen schließen und sie einen letzten, keuchenden Atemzug ausstößt. \"Er hat ihn... Geltharis...\" In Eurem Herzen wisst Ihr, dass es Anilias letzter Wunsch war, den Zweig des Cenarius wieder in den Händen der Dryaden zu sehen.",
    ["O"] = "Erhaltet den Zweig des Cenarius.",
    ["T"] = "Der Zweig des Cenarius",
  },
  [1032] = {
    ["D"] = "Ich kann nicht glauben, dass Anilia von uns gegangen ist... Wir haben doch immer zusammen in den Wäldern gespielt. Ich fühle beinahe so etwas wie... Trauer.$B$BEs gibt aber etwas, das mich vielleicht aufheitern könnte! Bitte, erschlagt die Satyrn, die mir Anilia genommen haben! Bringt mir ihre Hörner als Beweis Eurer Taten und Ihr könnt Euch auf ewig meines Dankes gewiss sein.",
    ["O"] = "Erhaltet 16 Satyrhörner.",
    ["T"] = "Satyrn schlachten!",
  },
  [1033] = {
    ["D"] = "Ich hatte gehofft, Orendils Heilmittel würde Relara retten, aber ich fürchte, es hat die Krankheit, die in meinem Kind wütet, nur gedämpft. Wir müssen eine stärkere Medizin finden! Zum Glück hat mir Orendil von weiteren Arzneien erzählt, sollte seine Erste versagen.$B$BElunes Träne ist eine Gesteinsart, die nur an einem Ort vorkommt: den Überresten des Irissees, der durch den Ausbruch des Donnergipfels nordöstlich von Astranaar zerstört worden ist. Orendil sagte, der Stein könne Verderbnis wegwaschen.$B$BBitte, $N, bringt mir eine Träne Elunes und betet, dass sich die Erkrankung meiner Tochter damit heilen lässt!",
    ["O"] = "Erhaltet eine Träne Elunes.",
    ["T"] = "Elunes Träne",
  },
  [1034] = {
    ["D"] = "Elunes Träne hat einen Teil der Erkrankung meiner Tochter weggewaschen. Ihre Alpträume haben nachgelassen und ich danke der Göttin für ihren Segen. Aber... immer noch wütet das Fieber in ihr.$B$BOrendil erzählte mir von einem gefrorenen Staub, den man in den Sternenstaubruinen findet. Vielleicht kann man damit Relaras Fieber senken. Sammelt etwas von diesem Staub und kommt dann bitte wieder zu mir zurück.$B$BDie Sternenstaubruinen liegen im Süden, $N. Möge Elune Eure Reise beschleunigen.",
    ["O"] = "Bringt Pelturas in Astranaar 5 Handvoll Sternenstaub.",
    ["T"] = "Die Sternenstaubruinen",
  },
  [1035] = {
    ["D"] = "Relara hat kein Fieber mehr, aber sie ist immer noch sehr schwach. Ich glaube nicht, dass sie wieder zu Kräften kommt, wenn wir einfach nur abwarten. Ich glaube, dazu bedarf es der Kraft eines Mondsteins.$B$BWir haben Mondsteine früher immer beim Himmelssturzsee im Südosten gesammelt... bis die Schattendickichtbäumlinge kamen.$B$BIhr Anführer, das Schattendickichtorakel, hat alle gefallenen Mondsteine eingesammelt und hortet sie. Jetzt müsst Ihr Euch einen Mondstein von dem Orakel holen.$B$BUnd dazu werdet Ihr ihn wohl erschlagen müssen, fürchte ich.",
    ["O"] = "Holt von dem Schattendickichtorakel einen gefallenen Mondstein und bringt ihn Pelturas in Astranaar.",
    ["T"] = "Der Himmelssturzsee",
  },
  [1036] = {
    ["D"] = "$N... Ja, ich habe von Euch gehört. Ich habe gehört, dass Ihr kein Freund von Beutebucht seid - genau wie wir Blutsegelbukaniere. Flottenmeister Firallon sucht nach gesunden und tüchtigen $GMännern:Frauen; wie Euch für eine spezielle Mission.$B$BWenn Ihr interessiert seid, begebt Euch zur Purpurschleier und sprecht mit ihm unter Deck. Sie ankert vor der wilden Küste östlich von hier, bei der Springflut und der Maid im Glück.",
    ["O"] = "Sprecht mit Flottenmeister Firallon an Bord der Purpurschleier vor der Küste des Schlingendorntals.",
    ["T"] = "Hierher, Taugenichts",
  },
  [1037] = {
    ["D"] = "Velinde Sternensang war hier im Eschental meine Vorgängerin. Zunächst schien es, als hätte sie die Lage im Teufelswald unter Kontrolle, aber nach und nach ließen ihre Bemühungen nach. Eines Tages verschwand sie einfach.$B$BMan schickte mich hierher, um ihre Arbeit fortzusetzen. Ich fürchte jedoch, dass ich nichts über die Priesterin weiß. Vielleicht weiß Thyn\'tel Klingenweber, eine der Kommandantinnen der Schildwache, mehr über ihr Verschwinden, das ich nicht miterlebt habe.$B$BSie wird sicherlich wissen, wie wichtig diese Informationen sind.",
    ["O"] = "Sprecht auf der Terrasse der Krieger in Darnassus mit Thyn\'tel Klingenweber.",
    ["T"] = "Velinde Sternensang",
  },
  [1038] = {
    ["D"] = "Der Foliant von Mel\'Thandris hat Euch das gezeigt? Dann könnte es wohl nicht schaden, wenn ich Euch erlaubte, ihre Habseligkeiten durchzusehen. Mit diesem Schlüssel könnt Ihr die Truhe öffnen, in der wir ihre Sachen in der Schildwachenbaracke aufbewahren. Sie führte ein Tagebuch über ihre Pflichten und wenn es etwas zu erfahren gibt, dann daraus.$B$BIch sollte Euch noch sagen, dass die Schildwachen denken, dass sie Gründe für ihr Verschwinden hatte und damit rechnen, dass sie jederzeit wieder auftaucht. Die Priesterin hat in der Vergangenheit viel geleistet und unser Vertrauen verdient.",
    ["O"] = "Durchsucht Velindes Truhe nach ihrem Tagebuch und bringt es zusammen mit dem Schlüssel nach Darnassus zu Thyn\'tel Klingenweber.",
    ["T"] = "Velindes Besitztümer",
  },
  [1039] = {
    ["D"] = "Ratschet ist der einzige Hafen des Brachlandes. Höchstwahrscheinlich hat Velinde in Ratschet ein Handelsschiff gefunden, das sie zur Schwarzmeerbucht in Azeroth brachte. Wir haben nur wenig mit den Goblins zu tun, die den Hafen leiten, aber der Dockmeister sollte über das Kommen und Gehen von Schiffspassagieren informiert sein.$B$BFolgt der Straße in südöstlicher Richtung durch das Eschental, dann gelangt Ihr irgendwann ins Brachland. Seid auf der Hut, $N, Krieger der Horde streifen durch das Land. Im Hafen werdet Ihr jedoch sicher sein.",
    ["O"] = "Sprecht in Ratschet mit Werftmeister Flunkerblick.",
    ["T"] = "Der Brachlandhafen",
  },
  [1040] = {
    ["D"] = "Ah ja, da steht\'s ja. Hättet mir sagen sollen, dass sie vor so langer Zeit hier durchgekommen ist. Mal sehen.$B$BVelinde. Hat eine Passage auf der BLACK OSPREY nach Beutebucht gebucht. Da hier nichts anderes steht, nehme ich an, dass sie den Hafen dort sicher erreicht hat.$B$BViel mehr Hilfe kann ich Euch nicht anbieten, aber sie hat mich nach einem Überlandtransport zur anderen Seite der Welt gefragt und ich habe ihr Ruzzgot vorgeschlagen, einen Karawanenführer aus Beutebucht. Kann sein, dass Velinde mit ihm gereist ist.$B$BUnd jetzt fort mit Euch. Ich hab zu tun.",
    ["O"] = "Nehmt ein Schiff nach Beutebucht und sprecht dort mit dem Karawanenführer Ruzzgot.",
    ["T"] = "Passage nach Beutebucht",
  },
  [1041] = {
    ["D"] = "Da hab ich mich wohl in Euch getäuscht und das passiert mir nicht alle Tage.$B$BZufällig erinnere ich mich an diese Velinde, nach der Ihr sucht. Kommt nicht jeden Tag vor, dass eine Nachtelfenpriesterin mit einer schmutzigen alten - aber alle Transportwünsche erfüllenden - Karawane wie der meinigen reisen will.$B$BAuf dem Weg nach Norden trennten wir uns, sie wollte weiter nach Dunkelhain. Der Amtmann dort macht alle möglichen Aufzeichnungen. Kann sein, dass er etwas Nützliches weiß.$B$BSeid im Dschungel vorsichtig, der ist selbst in günstigsten Zeiten tödlich.",
    ["O"] = "Sprecht in Dunkelhain mit Amtmann Daltry.",
    ["T"] = "Die Karawanenstraße",
  },
  [1042] = {
    ["D"] = "Nein, ich habe keinen Eintrag über eine Velinde Sternensang, die sich in Dunkelhain aufgehalten haben soll... Wenn Ihr erlaubt, ich kann mir auch nicht vorstellen, dass eine Nachtelfenpriesterin sich in einem Gasthaus einquartiert, wenn Ihr wisst, was ich meine.$B$BDiese Wolfmenschen, die Ihr erwähntet, von denen hab ich schon mal gehört. Erst kürzlich kam Calor mit einer ganzen Reihe Köpfe von denen in die Stadt zurück. Er arbeitet für die Familie Treuwein. Sie jagen Dämonen, Untote und andere Monster. Sprecht mit Jonathan, er ist der Haushaltsvorstand.",
    ["O"] = "Sprecht mit Jonathan Treuwein in Dunkelhain.",
    ["T"] = "Die Familie Treuwein",
  },
  [1043] = {
    ["D"] = "Eure Geschichte klingt glaubhaft... Ich habe Eure Beweggründe zwar noch nicht ganz begriffen, aber wenn es zu Euren Angelegenheiten hier im Dämmerwald gehört, die Worgen im Wald auszurotten, dann können mir Eure Beweggründe auch herzlich egal sein, solange das Ergebnis stimmt.$B$BIm Süden gibt es eine Mine, die von Worgen überrannt wurde... Sie kamen aus dem Nichts, aber nach allem, was wir wissen, hat man sie dort zuerst gesehen.$B$BGeht Ihr nur Euren Angelegenheiten nach, aber ich möchte Euch bitten, dass Ihr mir Bescheid gebt, sobald Ihr etwas Wichtiges entdeckt. Wir sind für jede Hilfe gegen das Böse dankbar.",
    ["O"] = "Sucht nach Hinweisen auf die Sichel von Elune und kehrt dann zu Jonathan Treuwein in Dunkelhain zurück.",
    ["T"] = "Die Sichel von Elune",
  },
  [1044] = {
    ["D"] = "Wir werden das Worgproblem in den Griff bekommen, keine Frage. Das Böse, das Eure Freundin in unseren Wald gebracht hat, wird eingedämmt werden und ich nehme ihr ihre Handlungen nicht übel. In letzter Zeit gehen seltsame Dinge vor sich, $N, und die Dunkelheit kennt keine Atempausen.$B$BIch will Euch nicht länger aufhalten. Mir scheint, dass auch andere erfahren sollten, was Ihr in den Minen des Dämmerwalds entdeckt habt.",
    ["O"] = "Kehrt nach Darnassus zu Thyn\'tel Klingenweber zurück.",
    ["T"] = "Beantwortete Fragen",
  },
  [1045] = {
    ["D"] = "Ich bin mir sicher, dass der seit langem tote Zauberer Dartol diese Rute dazu benutzen wollte, uns zu unterwerfen und zu zerstören, genauso wie es die Nachtelfen und Orcs versucht haben. Jetzt aber werden wir dieses Ding einem unvorhergesehenen Zweck zuführen.$B$BRan Blutreißer ist König über alle Furbolgs im Eschental. Sein Stamm, die Blutreißer, sind am mächtigsten und grenzen sich in einem Lager im Osten ab.$B$BTötet sie alle und bringt mir seinen Schädel. Wollen wir doch einmal sehen, ob wir nicht einen guten Verwendungszweck dafür finden können.",
    ["O"] = "Erhaltet Ran Blutreißers Schädel und Dartols Rute.",
    ["T"] = "Findet Teronis",
  },
  [1046] = {
    ["D"] = "Nehmt jetzt die Rute und sprecht, während Ihr unter ihrem Effekt steht, mit meinem Bruder Ota Wen.$B$BOta ist bei den anderen Furbolgs, die von den Orcs gefangen genommen und in den riesigen Käfig nördlich der Silberwindzuflucht gesperrt worden sind. Die Rute wird es Euch erlauben, Ota und die anderen Furbolgs dazu zu bringen, sich gegen die Horde aufzulehnen.$B$BDanach dürft Ihr Eures Weges ziehen. Wisst nur, dass Ihr mit Euren Taten einen neuen Freund gefunden habt.",
    ["O"] = "Benutzt Dartols Rute und sprecht mit Ota Wen. Überzeugt ihn, sich gegen die Unterdrücker der Horde aufzulehnen.",
    ["T"] = "Findet Teronis",
  },
  [1047] = {
    ["D"] = "Aufgemerkt, Helden!$B$BDer Zirkel des Cenarius sucht nach einsatzfähigen Individuen, die dazu beitragen möchten, ihn bei der Erforschung der riesigen neuen Grenzgebiete Kalimdors zu unterstützen! Weit im Westen von Tanaris und Gadgetzan liegt eine abschreckende Ödnis voller unvorstellbarer Gefahren, und der Zirkel braucht ehrbare und gewillte Vertreter, die an seiner statt agieren.$B$BInteressenten mögen bitte zwecks einer Audienz beim Zirkel des Cenarius vorstellig werden. Sprecht in Darnassus mit dem Erzdruiden Fandral Hirschhaupt, er hat weitere Informationen!",
    ["O"] = "Sprecht in der Enklave des Cenarius in Darnassus mit dem Erzdruiden Fandral Hirschhaupt über den Aufruf des Zirkels des Cenarius zur Erforschung der Grenzgebiete von Kalimdor.",
    ["T"] = "Die neue Grenze",
  },
  [1048] = {
    ["D"] = "Während die Dunkle Fürstin sich um die dringende Angelegenheit des Vormarsches des Lichkönigs im Norden kümmert, sorge ich dafür, dass Dinge, die uns unmittelbar betreffen, entsprechend geregelt werden.$B$BDie Menschenplage, die sich selbst Scharlachroter Kreuzzug nennt, hat sich als der schärfste Dorn in unserem Fleisch erwiesen. Tirisfal wimmelt förmlich von diesen kleinen roten Ameisen.$B$BBegebt Euch zum Kloster, $C, und vernichtet Hochinquisitorin Weißsträhne und ihre obersten Offiziere.",
    ["O"] = "Tötet Hochinquisitorin Weißsträhne, den Scharlachroten Kommandant Mograine, Herod, den Scharlachroten Helden sowie den Hundemeister Loksey und meldet Euch dann wieder bei Varimathras in Unterstadt.",
    ["T"] = "In das Scharlachrote Kloster",
  },
  [1049] = {
    ["D"] = "Der Irdene Ring hatte nur die besten Absichten, was unsere geplagten Brüder betraf. Aber wer weiß schon, welche Absichten jene verfolgen, die dem Ältestenrat Dinge einflüstern?$B$BDie Geschichte der Verlassenen, mit denen wir uns verbündet haben, ist von Lug und Trug durchzogen. Zu übereilt gingen wir unseren Pakt ein. Vielleicht hätte Cairne gut daran getan, die Warnungen aus Orgrimmar zu beherzigen.$B$BTief im Inneren des Scharlachroten Klosters liegt das \'Kompendium der Gefallenen\' verborgen, das von irren menschlichen Zeloten bewacht wird. Ihr Tun ist von ihrem Wahnsinn geprägt, aber ihre Forschungen könnten nützlich für uns sein.",
    ["O"] = "Holt das \'Kompendium der Gefallenen\' aus dem Kloster in Tirisfal und bringt es zu dem Weisen Wahrspruch in Donnerfels.",
    ["T"] = "Kompendium der Gefallenen",
  },
  [1050] = {
    ["D"] = "Die Zukunft ist unausweichlich! Wir stürzen ihr entgegen wie Regentropfen, die auf das Große Meer prasseln.$B$BAber vielleicht erfahren wir, indem wir die Vergangenheit studieren, was uns erwartet, und könnten uns besser auf das Unbekannte vorbereiten.$B$BBevor Lordaeron von all diesen schrecklichen Dingen heimgesucht wurde, gab es eine Bastion des Wissens, die in Tirisfal versteckt lag. Gerüchten zufolge soll es in der Bibliothek des alten Klosters ein Buch mit dem Titel \'Mythologie der Titanen\' gegeben haben.$B$BBringt es mir, dann können wir vielleicht gemeinsam die Geheimnisse der Vergangenheit entschlüsseln!",
    ["O"] = "Holt die \'Mythologie der Titanen\' aus dem Kloster und bringt die der Bibliothekarin Mae Bleichstaub in Eisenschmiede.",
    ["T"] = "Mythologie der Titanen",
  },
  [1051] = {
    ["D"] = "Bringt Monika Sengutz in Tarrens Mühle den Ehering von Vorrel Sengutz.",
    ["O"] = "Bringt Monika Sengutz in Tarrens Mühle den Ehering von Vorrel Sengutz.",
    ["T"] = "Vorrels Rache",
  },
  [1052] = {
    ["D"] = "Wir vom Scharlachroten Kreuzzug erheben Anspruch auf die Festungen zwischen Herdweiler und Tirisfal. Wir sind recht stolz auf unsere Bastionen der Säuberung in ganz Lordaeron.$B$BIhr habt Euch gegen die Untoten in Desolace bewiesen. Doch die wahre Bedrohung durch die Seuche liegt in Lordaeron.$B$BBegebt Euch nach Süderstade in den Östlichen Königreichen. Sucht dort nach einem Kreuzzügler namens Raleigh der Andächtige. Gebt ihm dieses Empfehlungsschreiben, das mein Siegel trägt, dann wird er Euch zu einem ehrwürdigen Ort in unserem Scharlachroten Kloster führen.",
    ["O"] = "Bringt Raleigh dem Andächtigen in Süderstade Bruder Antons Empfehlungsschreiben.",
    ["T"] = "Auf dem Scharlachroten Pfad",
  },
  [1053] = {
    ["D"] = "Einst diente ich dem Scharlachroten Kreuzzug mit Ehre, Treue und Stolz. Ich hielt das Ziel, das sie verfolgten, für edel: Azeroth von den Untoten befreien.$B$BAls ich jedoch im Kloster in Tirisfal weilte, erkannte ich, dass sie langsam den Bezug zur Wirklichkeit verloren. Jetzt halten sie jeden für verseucht, der nicht das Wams des Kreuzzuges trägt. Unschuldige Männer und Frauen wurden gefoltert, weil sie angeblich verseucht waren. $B$BDer Scharlachrote Kreuzzug muss im Namen des Lichts zerschlagen werden. Ihr, $N, müsst ihr wahnwitziges Regime beenden.",
    ["O"] = "Tötet Hochinquisitorin Weißsträhne, den Scharlachroten Kommandant Mograine, Herod, den Scharlachroten Helden sowie den Hundemeister Loksey und meldet Euch dann wieder bei Raleigh dem Andächtigen in Süderstade.",
    ["T"] = "Im Namen des Lichts",
  },
  [1054] = {
    ["D"] = "Direkt im Norden Astranaars leben die Furbolgs vom Distelfellstamm. Meine Späher berichten, dass ihre Zahl sich in den letzten Monaten erhöht hat. Sollten es zu viele werden, könnten sie den Mut finden, Astranaar direkt anzugreifen.$B$BTötet so viele von ihnen, wie Ihr könnt, aber bringt mir den Schädel ihres Häuptlings zum Beweis dafür, dass ihr Bemühen im Keim erstickt wurde... vorläufig.$B$BIch bleibe hier in Astranaar und warte auf Kunde von Euch. Elune sei mit Euch.",
    ["O"] = "Bringt Raene Wolfsläufer in Astranaar Dal Blutklaues Schädel.",
    ["T"] = "Die Auswahl der Bedrohung",
  },
  [1055] = {
    ["D"] = "Die Rute, jetzt wieder heil, liegt in Euren Händen. Shael\'dryn wird von Eurem Erfolg hören wollen.",
    ["O"] = "Kehrt zu Shael\'dryn beim Mondbrunnen zurück.",
    ["T"] = "Findet Teronis",
  },
  [1056] = {
    ["D"] = "Die Waldgeister sagten mir, dass Ihr mutig und zu reisen gewillt seid.$B$BIm Süden, nicht weit vom Mystralsee entfernt, liegt ein Tunnel namens Steinkrallenpfad. Durch diesen Tunnel gelangt Ihr in ein Gebiet, das im Steinkrallengebirge als Scherwindklippe bekannt ist. Dort angelangt reist südwestlich weiter, am Felskesselsee vorbei und dann nach Norden, den steilen Abhang hinauf, bis Ihr den Steinkrallengipfel erreicht.$B$BDort werdet Ihr von Hüter Albagorm erwartet. Tut, was er sagt, $R.$B$BDie Reise wird gefährlich.",
    ["O"] = "Reist zu Bewahrer Albagorm auf dem Steinkrallengipfel.",
    ["T"] = "Reise zum Steinkrallengipfel",
  },
  [1057] = {
    ["D"] = "Als Beschützer der Waldgeister könnt Ihr sicher unschwer erraten, warum ich beunruhigt bin.$B$BIhr seid durch diese Berge gereist, um diesen Gipfel zu erreichen. Sicherlich habt Ihr das Zerrbild gesehen, zu dem das Land geworden ist. Gier hat zur Zerstörung dieser heiligen Hügel geführt.$B$BDie gierige motorisierte Armee der Industriellen hat das Verbrannte Tal im Süden in Brand gesteckt. Was noch schlimmer ist, die Blutfurien, die in unser Land eingedrungen sind, lassen uns nicht dorthin, um mit der Neuanpflanzung zu beginnen.$B$BVertreibt diese elenden Harpyien, $N!",
    ["O"] = "Bewahrer Albagorm auf dem Steinkrallengipfel möchte, dass Ihr 7 Harpyien der Blutfurien, 7 Wegelagerinnen der Blutfurien, 7 Töterinnen der Blutfurien und 7 Wildfedern der Blutfurien erschlagt.",
    ["T"] = "Die Rückeroberung des Verbrannten Tals",
  },
  [1058] = {
    ["D"] = "Ich bin ja so aufgeregt!$B$BDrei kleine Käfige mit drei kleinen Elfen! Aber was tun wir nur mit unseren süßen Baumschützer-Freunden? Na, lassen wir sie doch... wie nennen sie das doch gleich... Waldmagie schmecken!$B$BIch brauche einen ordentlichen Schuss Steinkrallensaft. Dann brauche ich ein paar Mondlichtläuferschnurrhaare von diesen schrecklichen Katzen. Das schreit nach einer Menge Renneraugen – man kann nie genug Augen haben. Oh, und bevor wir es vergessen, die wertvolle Schuppe eines Feendrachen.$B$BSchnell zum Steinkrallengipfel, $N, damit ich etwas Waldmagie zusammenbrauen kann!",
    ["O"] = "Hexendoktor Jin\'Zil in Malaka\'jin möchte 5 Portionen Steinkrallensaft, 5 Schnurrhaare eines Mondlichtläufers, 30 Renneraugen und die Schuppe eines Feendrachen.",
    ["T"] = "Jin\'Zils Waldmagie",
  },
  [1059] = {
    ["D"] = "Die Blutfurien wurden besiegt. Sie werden sich jedoch schon bald wieder neu gruppieren, wenn wir nichts unternehmen.$B$BGanz weit im Süden des Brachlandes liegt ein Land, das als Tausend Nadeln bekannt ist. Wenn Ihr in Tausend Nadeln nach Westen reist bis zum üppigen Waldrand von Feralas, stoßt Ihr auf einen Druiden namens Falfindel Wegeshut.$B$BDieser große und weise Druide kann große Waldgeister herbeirufen, die bei der Rekultivierung des Verbrannten Tals helfen können.$B$BErzählt ihm von unserer Notlage! Sucht ihn sofort auf!",
    ["O"] = "Reist zu Falfindel Wegeshut an der Grenze zwischen Tausend Nadeln und Feralas.",
    ["T"] = "Die Rückeroberung des Verbrannten Tals",
  },
  [1060] = {
    ["D"] = "Ihr habt mir sehr geholfen, $N. Ihr seid ein echter Glücksbringer, wie eine Hasenpfote, und ich möchte, dass Ihr Euch das hier einmal anseht.$B$BDie Harpyien stammen nicht aus dem Brachland. Sie kommen aus einem Ort namens Steinkrallengebirge, ganz weit nordwestlich von hier. Dort könnte es möglicherweise gefährlich für Euch sein, aber dieser Brief muss an einen alten Freund von mir überbracht werden, der dort lebt, ein Hexendoktor mit dem Namen Jin\'Zil.$B$BEr wird sich freuen zu hören, dass Serena Blutfeder tot ist, und ich glaube, Ihr werdet das Steinkrallengebirge sehr interessant finden.",
    ["O"] = "Überbringt Jin\'Zil in seiner Höhle im Steinkrallengebirge Darsoks Brief.",
    ["T"] = "Brief an Jin\'Zil",
  },
  [1061] = {
    ["D"] = "Die Geister des Steinkrallengebirges sind verärgert, weil Goblins und ihre Gefolgsleute das Land ausplündern und brandschatzen. Das Land rief um Hilfe, und jene, die auf so etwas achten... haben es gehört.$B$BSeereth Bruchstein, eine aufstrebende Schamanin der Horde, wurde in das Steinkrallengebirge geschickt. Sie berichtet von umfangreichen Rodungen durch die Goblins und bittet um Hilfe.$B$BTrefft Euch im Steinkrallengebirge mit Seereth. Um zu ihr zu gelangen, folgt der Straße westlich aus der Siedlung des Wegekreuzes im Brachland. Sie hat in der Nähe des Hochwipfeltals ein Lager aufgeschlagen.",
    ["O"] = "Sprecht mit Seereth Bruchstein an der Grenze zwischen dem Steinkrallengebirge und dem Brachland.",
    ["T"] = "Die Geister des Steinkrallengebirges",
  },
  [1062] = {
    ["D"] = "Die von Goblins geleitete Venture Company ist in das Steinkrallengebirge umgezogen, um dort Bäume zu fällen und ganze Waldflächen zu brandroden. Die Geister dort sind außer sich vor Schmerz und Wut. Wir müssen der Venture Company Einhalt gebieten!$B$BBegebt Euch nach Nordwesten, jenseits des Hochwipfeltals, und zur Scherwindklippe. Ihr werdet dort die Goblins und ihre Gefolgsleute bei der Arbeit vorfinden - zeigt ihnen, dass die Horde eine Ausbeutung des Steinkrallengebirges nicht billigt. Zeigt es ihnen in der Sprache, die sie am besten verstehen...$B$BGewalt.",
    ["O"] = "Tötet 15 Holzfäller der Venture Co. und kehrt dann zu Seereth Bruchstein an der Grenze des Steinkrallengebirges und dem Brachland zurück.",
    ["T"] = "Goblineindringlinge",
  },
  [1063] = {
    ["D"] = "Um die Geister des Steinkrallengebirges wirklich zu beschwichtigen, müssen wir uns an die Taurenältesten wenden.$B$BIch kenne nicht viele ihrer Anführer, aber einen Namen kenne ich: Magatha. Ich habe gehört, dass sie sehr alt sein soll und sehr viel weiß. Wenn jemand die Tauren um geistige Hilfe bitten kann, dann sie.$B$BMagatha lebt in der Taurenhauptstadt Donnerfels auf der Anhöhe der Ältesten - dem Steilhang im Nordosten der Stadt. Sprecht mit Magatha und berichtet ihr von der Not im Steinkrallengebirge.",
    ["O"] = "Sprecht in Donnerfels mit Magatha.",
    ["T"] = "Die Ältestengreisin",
  },
  [1065] = {
    ["D"] = "Um die Venture Company aus dem Steinkrallengebirge zu vertreiben, müssen wir extreme Maßnahmen ergreifen. Extrem, aber ohne Blutvergießen.$B$BEiner meiner Kollegen hat vielleicht genau das Werkzeug, das wir benötigen. Er heißt Lydon und wohnt in Tarrens Mühle im Vorgebirge des Hügellands, das im weit entfernten Lordaeron liegt.$B$BUm ins Hügelland zu gelangen, reist mit dem Zeppelin in unsere Hauptstadt, nach Unterstadt. Geht von dort aus nach Norden nach Tirisfal, dann südwestlich nach Silberwald und südlich ins Hügelland.$B$BSucht Lydon auf und gebt ihm diese Notiz. Sie wird ihm alles erklären.",
    ["O"] = "Bringt Apotheker Lydon in Tarrens Mühle Zamahs Notiz.",
    ["T"] = "Reise nach Tarrens Mühle",
  },
  [1066] = {
    ["D"] = "Ich werde ein Toxin herstellen, mit dem wir der Venture Company die Gewalt entziehen und sie schwach und sanftmütig werden lassen. Das sollte das Problem der Horde im Steinkrallengebirge lösen, meint Ihr nicht?$B$BUm das Toxin herzustellen, benötige ich jedoch das Blut Unschuldiger, eine Zutat, an die man nur schwer herankommt.$B$BGlücklicherweise beschäftigt das Syndikat, das ist eine Gruppe menschlicher Briganten in dieser Region, Schattenmagier... und diese Magier beschaffen sich das Blut Unschuldiger für ihre eigenen unappetitlichen Machenschaften.$B$BIhr findet die Schattenmagier des Syndikats in der Burg Durnholde im Südosten.",
    ["O"] = "Bringt Apotheker Lydon in Tarrens Mühle 5 Phiolen mit dem Blut Unschuldiger.",
    ["T"] = "Das Blut Unschuldiger",
  },
  [1067] = {
    ["D"] = "Hier habt Ihr das Toxin. Wenn Ihr es gegen die Venture Company einsetzt, sollte es positive, vielleicht sogar ansteckende Ergebnisse erzielen. Ich muss sagen, es ist ziemlich aufregend zu sehen, dass mein Werk tatsächlich eingesetzt wird!$B$BBringt es aber erst der Apothekerin Zamah, bevor Ihr es einsetzt. Sie muss eine Anwendungsmethode für das Toxin entwickeln, um einen größtmöglichen Effekt zu erzielen.",
    ["O"] = "Bringt Apothekerin Zamah in Donnerfels Lydons Toxin.",
    ["T"] = "Rückkehr nach Donnerfels",
  },
  [1068] = {
    ["D"] = "Es gibt eine Verbindung zwischen der spirituellen Magie der Schamanen und der natürlichen Magie der Druiden. Deshalb können die Schamanen auch das Flehen der Geister des Steinkrallengebirges hören. Vieles, das sie flüstern, verstehe ich nicht, aber eine Nachricht, eine Vision, ist eindeutig...$B$BIch hatte die Vision zweier menschenähnlicher Maschinen, die auf der Scherwindklippe nordwestlich des Hochwipfeltals mit ungeheuerlicher Geschwindigkeit Bäume fällten. Diese Metallbestien müssen aufgehalten werden!$B$BIn meiner Vision trugen diese Maschinen seltsame Symbole:$B$BXT:4 und XT:9.",
    ["O"] = "Vernichtet die Schredder XT:4 und XT:9 und kehrt dann zu Seereth Bruchstein an der Grenze zwischen dem Steinkrallengebirge und dem Brachland zurück.",
    ["T"] = "Schreddermaschinen",
  },
  [1069] = {
    ["D"] = "Omeletts aus Spinneneiern sind der letzte Schrei in Beutebucht! Das Problem ist nur... dass es in Beutebucht keine Eier gibt.$B$BDas riecht nach einer Geschäftsidee!$B$BAn der Scherwindklippe und im Sishircanyon nordwestlich im Steinkrallengebirge, lebt die Kluftmoosspinne. Bringt mir ihre Eier, dann werde ich Euch reichlich entlohnen!$B$BAn der Scherwindklippe sind die Eihaufen unter den noch verbliebenen Bäumen zu finden.$B$BIm Sishircanyon... tja, wenn Ihr einen schwachen Magen habt, solltet Ihr vielleicht nur die Eier an der Scherwindklippe sammeln...",
    ["O"] = "Bringt Mebok Mizzyrix in Ratschet 15 Kluftmooseier.",
    ["T"] = "Eier der Kluftmoosspinnen",
  },
  [1070] = {
    ["D"] = "Hallo, $N.$B$BSo wie Ihr ausseht, scheint Ihr voller Reiselust zu sein. Wart Ihr schon mal im Steinkrallengebirge? Es ist ein Land, das von der Venture Company und anderen Feinden der Nachtelfen heimgesucht wird.$B$BErst neulich schickte Schildwache Wolfsläufer meine Freundin Kaela Schattenspeer dorthin, um zu untersuchen, was mit dem Land geschehen ist, aber ich habe seitdem nichts mehr von ihr gehört. Was mir noch mehr Sorgen macht ist, dass die Allianz sie zusammen mit einem Gnomenmagier losgeschickt hat... das deutet definitiv auf Probleme hin.$B$BWürdet Ihr sie suchen und mir dann Bericht erstatten?",
    ["O"] = "Sucht Kaela Schattenspeer, die auf der Scherwindklippe im Steinkrallengebirge den Taleingang beobachtet.",
    ["T"] = "Wache halten im Steinkrallengebirge",
  },
  [1071] = {
    ["D"] = "Wisst Ihr, $N, ich bin Mitglied der Erleuchteten Vereinigung der Arkanologie, Alchemie und Ingenieurwissenschaften, und es ist mein Ziel... nein, meine PFLICHT!, den Nachtelfen zu zeigen, dass eine Mischung aus Magie, Mixturen und Mechanismen dazu beitragen kann, ihren Wald zu retten.$B$BEs ist jedoch ein Kampf gegen Windmühlen, und die Goblins vermehren sich wie die Kaninchen, wie es scheint. Ich hatte sogar nicht mal Zeit, um meine neuesten Erfindungen zu bauen!$B$BIch brauche etwas Zeit, um zu planen und zu organisieren. Dazu brauche ich jedoch Eure Hilfe bei der Eindämmung der Goblins auf der Scherwindklippe im Osten...",
    ["O"] = "Tötet 10 Holzfäller der Venture Co. und 10 Roder der der Venture Co. und kehrt dann zu Gaxim Rostknirsch im Steinkrallengebirge zurück.",
    ["T"] = "Der Aufschub des Gnoms",
  },
  [1072] = {
    ["D"] = "Das Gerät, das mir vorschwebt, wird meine am weitesten entwickelte Erfindung sein. Aber wir brauchen einen speziellen Trank, wenn es funktionieren soll. Ich glaube, wir sollten uns gleich an das gute Zeug halten, immerhin könnte das unsere letzte Mission sein.$B$BUnd deshalb brauchen wir einen hochwirksamen Sprengstoff: Nitromirglyceronium.$B$BDie einzige Person, die NG-5 herstellen kann, ist ein alter Freund von mir: Lomac Ritzeldreh. Sucht in Tüftlerstadt nach ihm.$B$BWenn Ihr ihn überreden könnt, uns etwas NG-5 herzustellen, kann ich mich an die Platzierung meiner Geräte machen.",
    ["O"] = "Sprecht mit Lomac Ritzeldreh in Eisenschmiede.",
    ["T"] = "Ein alter Kollege",
  },
  [1073] = {
    ["D"] = "Hahaha... Ihr braucht Nitromirglyceronium, um Goblins daran zu hindern, einen Wald zu zerstören?!$B$BEs ist mir egal, ob Ihr Gaxim kennt oder nicht... Ich würde Euch das Zeug allein schon deshalb geben, weil ich sehen will, wie Ihr oder sie in klitzekleine Fetzen explodieren.$B$BEs gibt allerdings ein Problem: Ich hab nichts mehr.$B$BAber ich sag Euch was: Bringt mir die richtigen Tränke, dann stelle ich sofort etwas für Euch her.$B$BDas Mindeste, was Ihr tun könnt, ist, mir ein paar Eurer alchemistischen Fertigkeiten vorzuführen, bevor ich Euch eine Kostprobe meiner größten Erfindung gebe.",
    ["O"] = "Bringt Lomac Ritzeldreh in Eisenschmiede 4 schwache Manatränke und 2 Elixiere der schwachen Seelenstärke.",
    ["T"] = "Stümperhaftigkeit + Chemikalien = Riesenspaß!",
  },
  [1074] = {
    ["D"] = "Bitte sehr, Fremdling. Seid jedoch vorsichtig damit... das Zeug ist hochexplosiv.$B$BFalls Ihr wirklich ein Freund von Gaxim seid, grüßt ihn von mir. Falls nicht, dann viel Spaß beim Selbst-in-die-Luft-jagen.",
    ["O"] = "Bringt Gaxim Rostknirsch im Steinkrallengebirge das Nitromirglyceronium.",
    ["T"] = "Stümperhaftigkeit + Chemikalien = Riesenspaß!",
  },
  [1075] = {
    ["D"] = "Gut, wenden wir uns jetzt dem Teil meines Planes mit der Fernzündung zu... oh, das hab ich ja noch gar nicht erklärt, stimmt\'s?$B$BAlso dann mal die Kurzversion: Mit etwas Magie kann ich dafür sorgen, dass der Sprengstoff hochgeht, ohne dass Ihr dabei sein müsst. Ich bastele ein Gerät zusammen, bei dem Ihr nur einen Knopf drücken müsst, damit das NG-5 explodiert, wenn Ihr bereit seid.$B$BIch brauche jedoch einen Zauber von einem Freund in Sturmwind - er heißt Collin Mauren. Ihr findet ihn im dortigen Magierviertel. Er ist ein Mensch, daher solltet Ihr ihn kaum verfehlen können.",
    ["O"] = "Sucht Collin Mauren in Sturmwind auf.",
    ["T"] = "Eine Rolle von Mauren",
  },
  [1076] = {
    ["D"] = "Ah, ich glaube, ich hab\'s. Ein Nachrichtenzauber... Er dürfte den Effekt haben, nach dem er sucht.$B$BIch muss eine Rolle herstellen, auf die der Zauber geprägt ist. Dazu benötige ich die Überreste eines Staubteufels.$B$BWarum geht Ihr nicht nach Westfall und sucht eine dieser elenden Kreaturen, während ich den Rest der Komponenten sammle? Sie sind gefährlich, aber ich bin mir sicher, dass Ihr schon damit klarkommt, zumal Ihr sogar gewillt seid, an einem von Gaxims Experimenten teilzunehmen.$B$BIch werde hier sein, wenn Ihr zurückkommt.",
    ["O"] = "Bringt Collin Mauren in Sturmwind die Überreste eines Staubteufels.",
    ["T"] = "Teufel in Westfall",
  },
  [1077] = {
    ["D"] = "Bitte sehr, $N. Das sollte funktionieren.$B$BBitte grüßt Gaxim von mir, wenn Ihr ihn das nächste Mal seht. Ich freue mich schon so darauf, von diesem Abenteuer zu hören, wenn Ihr es überstanden habt.$B$BLebt wohl.",
    ["O"] = "Bringt Gaxim Rostknirsch im Steinkrallengebirge die Rolle der Nachrichtenübermittlung.",
    ["T"] = "Speziallieferung für Gaxim",
  },
  [1078] = {
    ["D"] = "Immer wieder fragen mich Reisende über das Steinkrallengebirge aus. Scheint ein beliebter Ort unter Abenteurern zu sein; spielt keine Rolle, ob man Wyvern oder Elementare sucht, oder ob man Geschäfte mit der Venture Company machen will.$B$BIm Verbrannten Tal tief im Steinkrallengebirge gab es mal eine Basiliskenart, deren Schuppen, wenn man sie zu Staub zermahlte, ein wunderbares Reagenz für einige der Zauber ergab, die ich ersonnen habe.$B$BWenn diese Basilisken noch leben, hätte ich liebend gern ein paar ihrer Schuppen.$B$BLasst Euch Zeit, ich hab keine Eile, aber ich kann Euch gut bezahlen.",
    ["O"] = "Bringt Collin Mauren in Sturmwind 8 kristallisierte Schuppen.",
    ["T"] = "Suche für Mauren",
  },
  [1079] = {
    ["D"] = "Das ist es, $N. Seht genau hin. Die Geräte sind fertig und einsatzbereit.$B$BNehmt diese Blätter - sie erklären mehr über die Zünder und den Sprengstoff und wo Ihr ihn platzieren solltet.$B$BDenkt daran, der Erfolg Eurer Mission hängt nicht davon ab, wie viel Schaden Ihr anrichtet. Der Erfolg von Plan Alpha hängt davon ab, dass Ihr die Ingenieurspläne der Venture Company aus dem Sägewerk südlich des Felskesselsees holt.",
    ["O"] = "Holt die Ingenieurspläne der Venture Company aus dem Sägewerk auf der Scherwindklippe.",
    ["T"] = "Geheimoperation Alpha",
  },
  [1080] = {
    ["D"] = "Plan Beta funktioniert genau wie Plan Alpha, $N.$B$BWir stiften Verwirrung und stehlen dann ihre wertvollen Dokumente.$B$BWir müssen unbedingt diese Dokumente haben... vergesst das nicht.$B$BDie Blätter, die Ihr bereits habt, zeigen Euch, wo Ihr den Sprengstoff platzieren müsst. Kommt mir nur heil und mit diesen Dokumenten zurück...",
    ["O"] = "Holt die Dokumente der Venture Company aus der Scherwindmine am Ostende der Scherwindklippe.",
    ["T"] = "Geheimoperation Beta",
  },
  [1081] = {
    ["D"] = "$N, ich bin mir sicher, dass Tyrande Wisperwind Euch persönlich für Eure Teilnahme an dieser Mission danken wollen wird.$B$BEs mag auf den ersten Blick zwar nicht so aussehen, aber Ihr habt dazu beigetragen, etwas in Gang zu setzen, das unsere Position im Steinkrallengebirge erheblich stärken wird.$B$BFalls Ihr Euch als unseren Verbündeten im Kampf gegen diese Gräueltaten betrachtet, sucht Tyrande im Tempel des Mondes in Darnassus auf.",
    ["O"] = "Sucht Tyrande Wisperwind in Darnassus auf.",
    ["T"] = "Tyrandes Empfang",
  },
  [1082] = {
    ["D"] = "$N, Ihr habt Großes geleistet, indem Ihr uns hier geholfen habt.$B$BDie Schildwachen des Eschentals werden Euch reichlich belohnen, wenn Ihr ihnen mitteilt, dass Gaxim und ich noch am Leben sind. Sagt ihnen, dass wir beim Aufhalten der Venture Company und der Heilung des Landes gute Fortschritte machen.$B$BSucht Tara Thenysil in Astranaar auf. Sie wird sich freuen, dass Ihr mit guten Neuigkeiten kommt.",
    ["O"] = "Kehrt zu Schildwache Thenysil im Eschental zurück.",
    ["T"] = "Neuigkeiten für Schildwache Thenysil",
  },
  [1083] = {
    ["D"] = "Ich weiß nicht genau, was die Venture Company dem Tal angetan hat, aber es ist offensichtlich, dass es von den dortigen Kreaturen überwältigt wurde.$B$BSie steckten den Wald in Brand und benutzten ihre zerstörerischen Erfindungen, um so viel wie möglich des Tals zu zerstören.$B$BIn ihrer Dummheit haben sie jedoch auch noch etwas anderes entfesselt: brennende Geister, Elementare der Flammen, der Steine und der Wut.$B$BDas Land wird Elune nicht mehr lächeln sehen, solange diese Kreaturen frei herumstreifen.$B$BBringt mir ihre glimmenden Überreste, um mir zu zeigen, dass Ihr sie dezimiert habt.",
    ["O"] = "Bringt Kaela Schattenspeer im Steinkrallengebirge 8 glimmende Überreste der Feuerelementare des Verbrannten Tals.",
    ["T"] = "Wütende Geister",
  },
  [1084] = {
    ["D"] = "Es scheint mir offensichtlich, dass die Beschützer des Tales - die Treants - irgendetwas damit zu tun hatten, dass die Venture Company zurückgeschlagen wurde. Leider wurden selbst diese einst so großen Wächter jetzt verderbt.$B$BGaxim glaubt, dass ihre Verderbnis irgendwie mit den Waffen zusammenhängt, die die Goblins während ihres Rückzuges benutzten.$B$BIch bat ihn, eine Probe von einem verwundeten Treant zu nehmen und sie zu untersuchen, um herauszufinden, ob das wirklich stimmt.$B$BBringt mir ein paar Späne, damit der Gnom mehr darüber herausfinden kann...",
    ["O"] = "Bringt Kaela Schattenspeer im Steinkrallengebirge ein Stück eines zerfressenen Spans.",
    ["T"] = "Verletzte Urtume",
  },
  [1085] = {
    ["D"] = "Die Lage hier im Steinkrallengebirge ist offensichtlich düster, wenn Nachtelfen und Gnome zusammenarbeiten.$B$BDie Venture Company hat das Land verwüstet und ich habe einen Großteil meines Leben damit zugebracht, der Zerstörung ein Ende zu setzen.$B$BMan sollte Gaxim nicht vorschnell beurteilen; er versteht die Notwendigkeit, warum wir zusammenarbeiten müssen.$B$BWenn Ihr uns helfen wollt, sprecht zunächst mit Gaxim.$B$BEs tut mir leid, dass ich Euch jetzt noch nicht mit guten Neuigkeiten zu Tara zurückschicken kann. Aber vielleicht schon bald.",
    ["O"] = "Sprecht mit Gaxim im Steinkrallengebirge.",
    ["T"] = "Wache halten im Steinkrallengebirge",
  },
  [1086] = {
    ["D"] = "Das Toxin ist bereit. Jetzt muss es nur noch ausgeliefert werden. Ihr, $N, müsst es ausliefern.$B$BDie Venture Company muss irgendwelche Mittel und Wege haben, Nachrichten und leichte Fracht zwischen ihrem Hauptquartier und dem Betrieb im Steinkrallengebirge hin- und herzuschicken. Es könnte ein kleiner Flugplatz sein - nicht groß genug für einen Zeppelin, aber vielleicht für einen Flugapparat. Ihr müsst die Gebiete, die von der Venture Company kontrolliert werden, durchsuchen und diesen Flugplatz finden, falls er existiert. Solltet Ihr ihn finden, platziert das Toxin dort.$B$BPlatziert es und lauft, was Ihr könnt.",
    ["O"] = "Bringt den Toxinvernebler zum Flugplatz der Venture Company und kehrt dann zu Apothekerin Zamah zurück.",
    ["T"] = "Der Flugapparathafen",
  },
  [1087] = {
    ["D"] = "Ihr könnt Euch nicht vorstellen, wie groß mein Hass auf dieses elende Land ist. Solche Leute wie Cenarius und die Kaldoreidruiden auf dem Gipfel benutzen ihre lächerlichen Mächte, um den Magiefluss zu stoppen, sogar noch so weit von ihrem lächerlichen Besitz entfernt... ihren geliebten Wäldern.$B$BIch wünschte, ich könnte nach Jaedenar zurückkehren und im Fluss der Magie baden, aber mein Meister erlaubt mir erst dorthin zurückzukehren, wenn meine Arbeit getan ist.$B$BZweifellos liebt Ihr die Nachtelfen und ihre Verbündeten genauso wenig wie ich. Helft mir, dann haben wir vielleicht beide etwas davon.",
    ["O"] = "Tötet für Brealyn Feuerhand in Sonnenfels 4 Söhne des Cenarius, 4 Töchter des Cenarius und 4 cenarische Botaniker.",
    ["T"] = "Cenarius\' Vermächtnis",
  },
  [1088] = {
    ["D"] = "Sie dachten, dass man den Fluss der Magie zum Erliegen bringen könnte. Sie haben sich geirrt. Sie dachten, dass sie Ordanus verstecken könnten, aber ich will seinen Kopf!$B$BDie Magie gewährt mir Durchblick. Ja... da ist er. Ein Scheideweg... auf dem Wipfel eines großen Baumes im Eschental. Ein Name... der Rajenbaum. Einer der ältesten Söhne des Waldgottes. Sie haben ihn aus dem Wipfel geschleust, bevor ich ihn finden konnte, aber ihm läuft langsam die Zeit davon.$B$BBringt ihn mir, dann teile ich ein großes Geschenk mit Euch.",
    ["O"] = "Bringt Braelyn Feuerhand in der Nähe von Sonnenfels Ordanus\' Kopf.",
    ["T"] = "Ordanus",
  },
  [1089] = {
    ["D"] = "Die Druiden errichteten den Krallenbau auf dem Steinkrallengipfel, in dem sie viele mächtige Gegenstände aufbewahren.$B$BSie haben sich ein komplexes Schema ausgedacht, damit sie nicht in die falschen Hände gelangen.$B$BGlücklicherweise konnte ich einem von ihnen... ein paar Details entlocken.$B$BVersteckt in einer kleinen Laterne am Eingang des Baus werdet Ihr den Torwächterschlüssel finden. Damit könnt Ihr die Behälter öffnen, die über den ganzen Bau verteilt sind. Mit den Gegenständen aus diesen Behältern wiederum könnt Ihr ihren Hort öffnen, der sich in einem der zentralen Bereiche befindet.",
    ["O"] = "Begebt Euch zum Bau auf den Steinkrallengipfel. Benutzt den Torwächterschlüssel, um Euch die versteckten Gegenstände der Druiden zu besorgen. Benutzt diese Gegenstände, um den Hort des Krallenbaus zu öffnen.",
    ["T"] = "Der Höhlenbau",
  },
  [1090] = {
    ["D"] = "Wisst Ihr, ich war hier einst die zweitwichtigste Person. Das hat sich alles geändert, als Gerenzo hier übernahm. Hat meinen Boss Ziz Fizziks gefeuert und mich zum Arbeiten nach unten in die Minen geschickt. Ziz sagte mir aber, dass ich hier bleiben und Informationen sammeln solle. Sein neuer Boss wird uns gut bezahlen, wenn wir rausfinden, was Gerenzo plant.$B$BEgal, was ich weiß ist, dass Gerenzo befohlen hat, dass Riesenmengen von diesem gold-grünen Erz abgebaut werden sollen. Ich hol Euch eine Probe, damit Ihr sie Ziz bringen könnt, aber Ihr müsst Euch vor Patrouillen hüten, während ich es hole.",
    ["O"] = "Nehmt das rätselhafte Erz, nachdem Piznik es fertig abgebaut hat.",
    ["T"] = "Gerenzos Befehle",
  },
  [1091] = {
    ["D"] = "$N, bringt Kaela diese Briefe. Ich bin mir sicher, dass sie das interessiert. Oder zumindest die Schildwachen.$B$BErzählt Ihr unbedingt, wie erfolgreich wir waren und dass wir es ohne ein bisschen Magie nie geschafft hätten.",
    ["O"] = "Bringt Kaela Schattenspeer im Steinkrallengebirge die Dokumente der Venture Company.",
    ["T"] = "Kaelas Update",
  },
  [1092] = {
    ["D"] = "Ich glaube, ich sollte auch langsam darüber nachdenken, hier zu verschwinden. Es wird nicht lange dauern, bis die da oben erfahren, was hier gelaufen ist, und ich bin sicher, dass die keinen großen Wert darauf legen, mich wiederzusehen.$B$BIch schleich mich raus und treffe mich mit Ziz. Hoffentlich kann er mir einen neuen Job besorgen!$B$BIch werde versuchen, hier ein bisschen Ordnung zu schaffen, aber Ihr solltet die Erzprobe sofort zu Ziz bringen. Er sagte mir, dass er irgendwo an der Westseite der Scherwindklippe sein Lager aufgeschlagen hat. Viel Glück.",
    ["O"] = "Bringt Ziz Fizziks im Steinkrallengebirge das unbekannte Erz.",
    ["T"] = "Gerenzos Befehle",
  },
  [1093] = {
    ["D"] = "Schrecklich, was die Venture Company hier getan hat, was? Was haben die sich dabei gedacht? Eine Goblinfirma, die einen Gnom anheuert!$B$BEin Glück für mich, dass Stotterspritter ein Genie erkennt, wenn es vor ihm steht. Hat mich eingestellt, gleich nachdem ich gefeuert wurde!$B$BSagte mir, sie wollten eine riesige Holzfällmaschine bauen, und Sput möchte, dass ich für ihn die Pläne dazu stehle. Leider wird Gerenzo - dieser verdammte Gnom - mich umlegen, sobald er mich sieht... würdet Ihr vielleicht die Pläne für mich stehlen? Ihr solltet sie bei einem ihrer Maschinenführer finden.",
    ["O"] = "Besorgt für Ziz Fizziks im Steinkrallengebirge die Blaupausen für den Superschnitter 6000.",
    ["T"] = "Superschnitter 6000",
  },
  [1094] = {
    ["D"] = "In diesem Umschlag befinden sich alle Informationen, die ich mir schnappen konnte, bevor sie mich vor die Tür setzten... Interne Dokumente der Venture Company, ein paar Handbücher, technisches Zeugs... Oh, und natürlich die Blaupausen für den Superschnitter 6000.$B$BStotterspritter hat sich bei Ratschet im Brachland niedergelassen. Sollte nicht schwer sein, ihn zu finden. Er ist ein kleiner, grüner Kerl.",
    ["O"] = "Bringt Stotterspritter in Ratschet im Brachland den versiegelten Umschlag.",
    ["T"] = "Weitere Instruktionen",
  },
  [1095] = {
    ["D"] = "Die Tatsache, dass die Venture Company diesen Gnom angestellt hat, hat in Lorenhall für einiges Aufsehen gesorgt. Die Handelsbarone finden es nicht gut, dass Razdunk mit Gerenzo gemeinsame Sache macht.$B$BQuizvox hat mich gebeten, mich um das Problem zu kümmern und ich bin mir sicher, dass einer oder zwei dieser Handelsbarone ihn dazu angewiesen haben. Natürlich hat mir der Direktor das nicht erzählt. Sagen wir einfach, ich hätte so eine Ahnung.$B$BWie dem auch sei, Gerenzo muss entfernt werden und es wäre eine passende Prüfung, das Fizzik erledigen zu lassen.",
    ["O"] = "Bringt Ziz Fizziks im Steinkrallengebirge die neuen Befehle.",
    ["T"] = "Weitere Instruktionen",
  },
  [1096] = {
    ["D"] = "Ich habe Gerenzo einmal getroffen, und lasst mich Euch eines sagen, er ist nicht gerade eine Schönheit. Sein Fleisch sieht aus, als würde es faulen. Dazu hat er noch überall an seinem Körper Metallteile und Zeugs angeflanscht und sogar einige seiner Körperteile durch mechanische ersetzt.$B$BNordöstlich von hier, weit oberhalb des Felskesselsees in der Nähe des Wasserrades beaufsichtigt Gerenzo die gesamte Stätte. Ich könnte mich unmöglich bis da hinauf durchkämpfen und schon gar nicht mich mit Gerenzo anlegen...Aber Ihr seht aus, als könntet Ihr es schaffen.$B$BWenn Ihr ihn für mich tötet, soll es Euer Schaden nicht sein!",
    ["O"] = "Bringt Ziz Fizziks im Steinkrallengebirge Gerenzo Pfeifzangs mechanischen Arm.",
    ["T"] = "Gerenzo Pfeifzang",
  },
  [1097] = {
    ["D"] = "In Sturmwind gibt es einen zwergischen Waffenschmied namens Grimand Elmore, der mal angedeutet hat, dass er Hilfe bei einer Lieferung bräuchte. Ich glaube, er möchte ein Paket in seine Heimat im Norden schicken.$B$BIhr habt da ein paar schöne kräftige Beine! Falls Ihr also an ein wenig Beinarbeit interessiert seid, sprecht mit Grimand. Wir könnten Euch hier unten brauchen, aber wir müssen auch unsere Bande mit den Zwergen stärken.$B$BIhr findet Grimand Elmore im Waffenladen im Zwergendistrikt von Sturmwind, im nordöstlichen Teil der Stadt.",
    ["O"] = "Sprecht mit Grimand Elmore in Sturmwind.",
    ["T"] = "Elmores Aufgabe",
  },
  [1098] = {
    ["D"] = "Als Vorbereitung auf einen Überfall auf Schattenfang wurden zwei Todespirscher in die Burg geschickt, um Informationen zu sammeln. Das war vor mehreren Tagen und sie sind noch nicht wieder zurück, aber wir brauchen ihren Bericht, wenn wir auf einen erfolgreichen Angriff hoffen wollen!$B$BDas ist Eure Aufgabe, $N. Geht zu Burg Schattenfang im Süden und sucht die Todespirscher Adamant und Vincent. Findet sie und helft ihnen, falls es erforderlich ist.$B$BDiese Mission ist nicht nur gefährlich, sondern auch lebenswichtig, $N. Versagt bitte nicht.",
    ["O"] = "Sucht die Todespirscher Adamant und Vincent.",
    ["T"] = "Todespirscher in Burg Schattenfang",
  },
  [1099] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Die Goblins haben gewonnen!",
  },
  [1100] = {
    ["D"] = "Ihr öffnet langsam das verwitterte Tagebuch des Henrig Einbraue...",
    ["O"] = "Lest Henrig Einbraues Tagebuch.",
    ["T"] = "Einbraues Tagebuch",
  },
  [1101] = {
    ["D"] = "Armer Heralath! Und dieser Zwerg... Einbraue. Was war er doch für eine mutige Seele.$B$BSo viel Heldenmut darf nicht umsonst gewesen sein. Diese Greisin... Charlga Klingenflanke... muss aufgehalten werden.$B$BSicher, es wird gefährlich werden, den Kral zu bespitzeln. Wir haben keine Zeit, um Darnassus zu informieren. $N, stellt eine Gruppe zusammen, um die Greisin zu erschlagen.$B$BBringt mir Klingenflankes Medaillon zum Beweis ihres Ablebens.",
    ["O"] = "Bringt Falfindel Wegeshut in Thalanaar Klingenflankes Medaillon.",
    ["T"] = "Die Greisin des Krals",
  },
  [1102] = {
    ["D"] = "Es war edel von unserem Anführer Cairne, unser Volk hier in Donnerfels zu vereinen.$B$BIch kann jenen jedoch nicht vergeben, die uns so leicht aus dem Land unserer Vorfahren vertrieben haben. Jahrhunderte lang lebte mein Volk im Südlichen Brachland. Das Land war uns heilig. Aber wir wurden von zahllosen Feinden vertrieben.$B$BEin schreckliches Schicksal erwartet die Greisin Charlga Klingenflanke, die im Kral der Klingenhauer eine abscheuliche Armee zusammenzieht.$B$BBringt mir Klingenflankes Herz, damit ich in Frieden sterben kann, $N.",
    ["O"] = "Bringt Auld Steinkeil in Donnerfels Klingenflankes Herz.",
    ["T"] = "Ein schreckliches Schicksal",
  },
  [1103] = {
    ["D"] = "Seid gegrüßt, $G Bruda:Schwesta;! Seid auch Ihr gekommen, um das Wasser hier zu läutern und Islen Euer Wissen zu beweisen?$B$BIch habe den verderbten Wassergeist gerade selbst bezwungen und bin grad dabei, die Armschienen un\' verbleibenden Wassertropfen in die Kohlenpfanne zu geben.$B$BWas\'n das? Ahhh. Tiev versteht nur zu gut, wenn wat falsch läuft. Tja, Tiev kann Euch echt helfen, wenn Ihr den Wassergeist wieda seh\'n wollt.$B$BBringt mir \'in Wassersapta von Islen als Beweis, dass Ihr bereit seid, un\' ich bin bereit, meinen Teil beizutrag\'n.",
    ["O"] = "Bringt Tiev Mordune im Silberwald ein Wassersapta, wenn Ihr nicht mit der schwachen Wassermanifestation gesprochen habt.",
    ["T"] = "Ruf des Wassers",
  },
  [1104] = {
    ["D"] = "Ich habe so viele Pläne für unseren Raketenwagen! So viele! Und ich muss sie alle ausprobieren!!!$B$BIch arbeite gerade an einem neuen Treibstoff; an einem, der wirklich, wirklich, wirklich heiß ist! Die Mixtur stimmt zwar noch nicht ganz, aber ich glaube, ich weiß, was noch fehlt...$B$BDie Skorpide, die die Schimmernde Ebene durchwandern, haben ein Gift mit Unmengen Salz darin. Es ist einzigartig! Und es ist genau das, was ich brauche!$B$BBringt mir das Gift und vertraut mir: unser Wagen wird fliegen!",
    ["O"] = "Bringt Fizzel Kupferbolz in der Schimmernden Ebene 6 Einheiten salziges Skorpidgift.",
    ["T"] = "Salziges Gift",
  },
  [1105] = {
    ["D"] = "Mein Bruder arbeitet an einem neuen Treibstoff für seinen Raketenwagen. Er ist brillant, ehrlich. So brillant, dass... Ich glaube, der neue Treibstoff, den er da entwickelt, ist stark genug, um sich durch das Chassis des Wagens zu fressen!$B$BIch würde gern die Treibstofftanks verstärken, bevor das passiert.$B$BDie Schildkröten draußen auf der Ebene haben sehr starke Panzer. Ich möchte daraus neue Tanks für unseren Raketenwagen bauen. Tanks, die den neuen Treibstoff meines Bruders aushalten.$B$BIhr seht mir verantwortungsbewusst genug aus. Holt Ihr mir solche Panzer?",
    ["O"] = "Bringt Wizzel Kupferbolz in der Schimmernden Ebene 9 gehärtete Schildkrötenpanzer.",
    ["T"] = "Gehärtete Schalen",
  },
  [1106] = {
    ["D"] = "Ich entwickle einen neuen Motor, mit dem der Wagen so schnell wie der Wind wird! Schnell genug, um zu fliegen, ich weiß es! Ich muss nur ein paar Kolben herstellen, die sehr, sehr große Belastungen aushalten. All meine bisherigen Versuche sind fehlgeschlagen, weil ich die Kolben einfach nicht robust genug herstellen kann!$B$BAber es gibt jemanden, der wissen könnte, wie es geht.$B$BSein Name ist Martek der Verbannte. Er ist ein großartiger Schmied und weiß mehr über Metall als alle anderen. Bringt ihm diesen Brief, ich weiß, dass er helfen kann!$B$BAber er lebt weit, weit weg - in Azeroth, im Ödland, in einem Lager, zusammen mit einem Goblin!$B$BSucht ihn!",
    ["O"] = "Bringt Martek dem Verbannten im Ödland Fizzel Kupferbolz\' Brief.",
    ["T"] = "Martek der Verbannte",
  },
  [1107] = {
    ["D"] = "Unser Raketenwagen ist das schnellste, was es auf der Ebene gibt! So schnell, dass... ich fürchte, die Reibung reißt ihn auseinander!$B$BUm das zu verhindern, muss ich einen Stoff entwickeln, der die beweglichen Teile des Wagens überzieht. Er muss hart sein, aber nicht spröde, und er muss resistent gegen Salzkorrosion sein, sonst wird der Wagen, während er sich in der Schimmernden Ebene befindet, zerfressen.$B$BHabt Ihr schon mal vom Finsteren Riff gehört, $N? Es liegt vor der Küste des Schlingendorntals. Dort leben Salzschuppenmurlocs, deren Schwanzflossen unabdingbare Zutaten zu meinem Stoff sind.",
    ["O"] = "Bringt Wizzel Kupferbolz in der Schimmernden Ebene 10 verkrustete Schwanzflossen.",
    ["T"] = "Verkrustete Schwanzflossen",
  },
  [1108] = {
    ["D"] = "Ich bin nicht hier draußen, weil ich mich vor Leuten verstecken will. Das Ödland ist reich an Metall, $N, wenn man den Mut hat, es sich zu holen.$B$BDas Metall heißt Indurium, und wie das Schicksal so spielt, soll es angeblich hohen Widerstand gegen Hitze und Belastung bieten. Das könnte genau das Richtige für Zischels Wagen sein.$B$BAber wir sollten sichergehen. Bringt mir eine Indurium-Probe, dann kann ich seine Eigenschaften testen.$B$BEchtes Induriumerz liegt tief drinnen in Uldaman, aber die Steingrufttroggs des Ödlands tragen manchmal Späne davon mit sich herum. Bringt mir diese Späne.",
    ["O"] = "Bringt Martek dem Verbannten im Ödland 10 Induriumspäne.",
    ["T"] = "Indurium",
  },
  [1109] = {
    ["D"] = "Wie Ihr seht, $N, befassen wir uns mit einigen sehr... interessanten Experimenten in der Königlichen Apothekervereinigung.$B$BAls Meister hier ist es meine Aufgabe, die anspruchsvollsten unserer alchemistischen Versuche zu überwachen. So viel Arbeit und nur so wenig Zeit!$B$BIhr seht weit gereist aus, $C. Vielleicht könnt Ihr mir helfen.$B$BIch brauche eine rare Substanz. Es ist dieses widerliche Zeug, das von einer raren Fledermausart stammt, die man nur in einem Dreckloch namens Kral der Klingenhauer findet. Bringt mir den Guano der Kralfledermäuse, damit wir mit unserer Arbeit beginnen können...",
    ["O"] = "Bringt dem Apothekermeister Faranell in Unterstadt 1 Häufchen Kralguano.",
    ["T"] = "Go, Go, Guano!",
  },
  [1110] = {
    ["D"] = "Hi-hi, bei den vielen Rennen, die hier stattfinden, ist es nicht verwunderlich, wenn dabei ein paar Unglücksfälle passieren. Wenn man sich in der Schimmernden Ebene umschaut, sieht man ständig Spuren vergangener Unfälle - überall liegen Teile von Raketenwagen herum.$B$BUnd diese Teile bedeuten für die Gnome und Goblins bares Geld. Sie suchen ständig nach weiteren neumodischen Apparaten, um sie an ihre Autos anzubauen.$B$BAlso zieht los und bringt mir die Teile. Bringt mir einen Haufen und ich werde Euch gut bezahlen.",
    ["O"] = "Bringt 30 Raketenwagenteile zu Kravel Kohlebart in der Schimmernden Ebene.",
    ["T"] = "Raketenwagenteile",
  },
  [1111] = {
    ["D"] = "Mein Job ist es, den Leuten zu beschaffen, was sie möchten, und diese Gnome da drüben möchten Teile bestellen. Ihre Teile wurden vor einer Woche nach Ratschet geschickt, aber es war mir nicht möglich, dorthin zu reisen.$B$BAlso, seid Ihr bereit, mir die Kleinarbeit abzunehmen? Geht nach Ratschet und gebt diese Teilebestellung an Werftmeister Flunkerblick - er wird wahrscheinlich bei den Docks sein.$B$BWenn Ihr das macht, habe ich weitere Aufträge für Euch.$B$BAufträge, bei denen eine Menge Profit rausspringen kann...",
    ["O"] = "Bringt Kravels Teilebestellung zu Werftmeister Flunkerblick nach Ratschet im Brachland.",
    ["T"] = "Werftmeister Flunkerblick",
  },
  [1112] = {
    ["D"] = "Hier ist Kravels Paket, $N. Und wenn Ihr es zustellt, sagt ihm bitte... wenn er weitere spezielle Lieferungen möchte, dann muss er bezahlen!",
    ["O"] = "Bringt Kravels Teile zu Kravel Kohlebart. Zu finden ist er in der Schimmernden Ebene in Tausend Nadeln.",
    ["T"] = "Teile für Kravel",
  },
  [1113] = {
    ["D"] = "Warum ich eine rare Art von Guano benötige, fragt Ihr Euch? Also bei diesem Experiment möchte ich wissen, wie zwei diametral entgegengesetzte chemische Substanzen aufeinander wirken, besonders wenn beide mit einem höchst toxischen Agens kombiniert werden, um die...garstigsten Resultate zu erzielen.$B$BIn der Nordostspitze von Tirisfal liegt das Scharlachrote Kloster. Innerhalb und außerhalb seiner Hallen versammeln sich die wahnsinnigen Jünger des Scharlachroten Kreuzzuges. Es ist völlig egal, wen Ihr tötet, jeder einzelne ist möglicherweise im Besitz des von mir gesuchten Herzens.",
    ["O"] = "Apothekermeister Faranell in Unterstadt möchte 20 Herzen des Eifers.",
    ["T"] = "Herzen des Eifers",
  },
  [1114] = {
    ["D"] = "Jetzt, wo ich habe, was ich brauche, gebt diese Teile Fizzel Kupferbolz. Er wartet schon lange darauf - ich bin sicher, das kleine Teil, das ich behalten habe, wird er nicht vermissen...$B$BUnd ich würde ihn auch nicht darauf hinweisen. Es war klein und wertlos.$B$BGlaubt mir.",
    ["O"] = "Bringt die empfindlichen Autoteile zu Fizzel Kupferbolz.",
    ["T"] = "Lieferung für die Gnome",
  },
  [1115] = {
    ["D"] = "Jetzt, da wir diesen Dienst für die Gnome geleistet haben, wird es Zeit, den Goblins einen Gefallen zu tun! $B$BIch habe gehört, dass der Goblinrennfahrer Nazz Kesseldampf diesen Namen nicht immer getragen hat. Ich frage mich, weshalb er ihn änderte. Ob es wohl ein Geheimnis ist und... wie viel ihm dieses Geheimnis wert ist.$B$BWenn jemand die Wahrheit kennt, dann Krazek aus Beutebucht an der Südspitze des Schlingendorntals. Sprecht mit Krazek und fragt ihn nach unserem Goblinrennfahrer.$B$BWenn Ihr nützliche Informationen aus ihm herausbekommt, springt vielleicht Profit für uns raus.",
    ["O"] = "Sprecht mit Krazek in Beutebucht im Schlingendorntal.",
    ["T"] = "Der Gerüchteverbreiter",
  },
  [1116] = {
    ["D"] = "Es ist ein großer Gefallen, diese Informationen über Nazz zu beschaffen, $N. Ja, ein großer Gefallen. Aber Ihr habt den weiten Weg von der Schimmernden Ebene auf Euch genommen... seid Ihr bereit, noch etwas weiter zu reisen?$B$BIhr könntet doch etwas für mich besorgen, während ich Kontakt mit meinen Informanten aufnehme, hm?$B$BHmm... was könnte es denn sein? Wie wäre es mit Traumstaub von den Großdrachenwelpen und Wyrmkin aus den Sümpfen des Elends? Ja, ich hätte gern etwas Traumstaub für meinen Schnupftabakbeutel!$B$BBringt ihn mir, $N, und ich gebe Euch die gewünschten Informationen.",
    ["O"] = "Bringt 10 Körnchen Traumstaub zu Krazek in Beutebucht.",
    ["T"] = "Traumstaub im Sumpf",
  },
  [1117] = {
    ["D"] = "Ich musste einige Gefälligkeiten erweisen, eine rare Flasche Elfenwein herschenken und mir selbst einiges zusammenreimen! Aber ich habe die Informationen, die Ihr sucht.$B$BHier, ich habe einen Brief mit einigen sehr interessanten Infos über Euren Freund Nazz Kesseldampf verfasst. Macht damit, was Ihr wollt - für mich wäre es zu gefährlich, ihn zu benutzen.$B$BSagt nur keinem, woher Ihr das habt!",
    ["O"] = "Bringt die Goblingerüchte zu Kravel Kohlebart. Zu finden ist er in der Schimmernden Ebene in Tausend Nadeln.",
    ["T"] = "Gerüchte für Kravel",
  },
  [1118] = {
    ["D"] = "$N, Ihr seid $Gein:eine:c; $C, $Gdem:der:c; ich trauen kann. Ihr werdet den Auftrag erledigen und die Klappe halten.$B$BIch habe einen neuen Plan geschmiedet. Er ist riskant, aber wenn er funktioniert, könnten wir beide gut absahnen! Und ich glaube, Ihr seid der Richtige, um das durchzuziehen.$B$BAls Erstes müsst Ihr nach Beutebucht zurückkehren. Nun, ich bitte um Verzeihung, $N. Ich weiß, meine letzte Aufgabe führte Euch dorthin. Aber sprecht diesmal mit Crank Zischelbub.$B$BGebt ihm diesen Brief. Und wenn er um Unterstützung bittet, dann tut, was Ihr könnt, um ihm zu helfen. Es wird sich lohnen.",
    ["O"] = "Bringt Kravels Plan zu Crank Zischelbub nach Beutebucht im Schlingendorntal.",
    ["T"] = "Zurück nach Beutebucht",
  },
  [1119] = {
    ["D"] = "Ich habe herausgefunden, wie man aus Zanzils Mixtur mit Starkbier ein mächtiges Getränk macht. Fools Starkbier. Und ich glaube, genau das braucht Kravel. Probiert es einfach mal aus und lasst mich wissen, wie es wirkt.$B$BUnd behaltet das Zeug nicht zu lange unbenutzt, $N. Zanzils Mixtur hat eine seltsame Wirkung auf die Hefe des Starkbiers und regt sie heftig an. Sie bleibt nicht ewig frisch.",
    ["O"] = "Bringt Fools Starkbier zu Kravel zurück. Zu finden ist er in der Schimmernden Ebene in Tausend Nadeln.",
    ["T"] = "Zanzils Mixtur und Fools Starkbier",
  },
  [1120] = {
    ["D"] = "Wir müssen Fools Starkbier an einem aus der Boxencrew ausprobieren. Wenn Ihr es an den Gnomen erproben wollt, hier - nehmt das Starkbier und gebt es dem Gnomenboxenboss.$B$BUnd wartet nicht zu lange. Das Starkbier wird mit der Zeit schlecht.",
    ["O"] = "Bringt Fools Starkbier zum Gnomenboxenboss bei der Illusionenrennbahn in Tausend Nadeln.",
    ["T"] = "Die Gnome betrunken machen",
  },
  [1121] = {
    ["D"] = "Wir müssen Fools Starkbier an einem aus der Boxencrew ausprobieren. Wenn Ihr es an den Goblins erproben wollt, hier - nehmt das Starkbier und gebt es dem Goblinboxenboss.$B$BUnd wartet nicht zu lange. Das Starkbier wird mit der Zeit schlecht.",
    ["O"] = "Bringt Fools Starkbier zum Goblinboxenboss bei der Illusionenrennbahn in Tausend Nadeln.",
    ["T"] = "Die Goblins betrunken machen",
  },
  [1122] = {
    ["D"] = "Fools Starkbier hat astrein gewirkt! Ich habe beim nächsten Rennen gesetzt... hoffen wir, das Team, das Ihr betrunken gemacht habt, hat sein Auto nummeriert!$B$BBleibt nur noch eine Aufgabe für Euch - bringt diese Notiz zu Crank Zischelbub in Beutebucht. Er wird von dem erfolgreichen Test wissen wollen.$B$BHoffentlich bekommen wir mehr von Fools Starkbier für künftige Rennen!",
    ["O"] = "Bringt den Bericht über Fools Starkbier zu Crank Zischelbub nach Beutebucht im Schlingendorntal.",
    ["T"] = "Meldet Euch bei Zischelbub",
  },
  [1123] = {
    ["D"] = "Für diese Aufgabe sucht Majordomus Rabine Saturna für Bewahrer Remulos auf der Mondlichtung Unterstützung bei der Erforschung des riesigen Ödlands weit im Westen von Tanaris und Un\'Goro. Er lebt in Nachthafen, dem Hauptort der Mondlichtung.$B$BVorher möchte ich Euch jedoch noch von der Wichtigkeit Eurer Anwesenheit in Kenntnis setzen. Es gibt zunehmend Spannungen zwischen den Nachtelfen von Darnassus und dem Zirkel des Cenarius bei der Mondlichtung. Lasst Euch nicht von völkischer Engstirnigkeit beirren und verlasst Euch auf die Weisheit Eurer Vorfahren, wenn Ihr dort mit dem Zirkel zu tun habt, $N. Viel Glück.",
    ["O"] = "Sprecht mit Rabine Saturna im Dorf Nachthafen auf der Mondlichtung. Die Mondlichtung liegt zwischen dem Teufelswald und Winterquell, und Ihr erreicht es über den Pfad, der aus der Holzschlundfeste herausführt.",
    ["T"] = "Rabine Saturna",
  },
  [1124] = {
    ["D"] = "Westlich des Kraters von Un\'Goro entlang des nordwestlichen Grats liegt ein raues Ödland - Silithus. Nur wenige wissen, dass sich hier vor Urzeiten ein Krieg zwischen den Nachtelfen und einer bösartigen, außerirdischen Macht abgespielt hat. Das Böse wurde versiegelt, aber jetzt befürchten wir, dass Kräfte am Werk sind, um diesen... unaussprechlichen Horror wieder zu entfesseln.$B$BSprecht mit Layo Sternenschlag, einem unserer Agenten in Silithus. Sucht nach ihm in Heldenwacht, der letzten Ruhestätte der Gefallenen des Ödlands. Gebt ihm diesen Brief, damit erwerbt Ihr sein Vertrauen.",
    ["O"] = "Sprecht in der Nähe des Friedhofs Heldenwacht in Silithus mit Layo Sternenschlag und zeigt ihm Rabines Brief.",
    ["T"] = "Einöde",
  },
  [1125] = {
    ["D"] = "Südwestlich von uns liegt ein tragischer Schauplatz – die Ruinen des Südwindpostens. Dort sollten wir mit unserer Arbeit beginnen, $N.$B$BNach allem, was wir wissen, war dieses Dorf einst ein Stützpunkt der Nachtelfen für Operationen in Silithus. In der Zwischenzeit wurde es längst von den Silithidenschwärmen überrannt, die die Wüsten durchziehen. Ich vermag mir den Gräuel kaum vorzustellen, als die Zerstörung des Dorfs begann...$B$BDie gequälten Geister gefallener Druiden und Schildwachen wandern ziellos durch die Ruinen. Erforscht diesen Ort und befreit die Seelen dieser unglücklichen Kreaturen, wo immer es erforderlich ist.",
    ["O"] = "Befreit die Geister von 8 gequälten Druiden und 8 gequälten Schildwachen beim Südwindposten und kehrt dann zu Layo Sternenschlag auf den Friedhof Heldenwacht in Silithus zurück.",
    ["T"] = "Die Geister vom Südwindposten",
  },
  [1126] = {
    ["D"] = "Wir müssen uns um den Schwarmbau beim Südwindposten kümmern, und ich glaube, ich weiß auch schon, wie wir an ihn herankommen. Meiner Erfahrung nach reagieren die Schwarmbauten der Silithiden empfindlich auf ihre Umgebung. Das erklärt auch ihre Angriffe, als sie die Todesschreie der Geister hörten.$B$BIm Turm sind die Silithiden am stärksten - dort, wo massenweise Wespen herumschwirren. Besteigt den Turm und scheucht den Schwarmbau auf. Meldet Euch zurück, sobald Ihr etwas erfahren habt, aber seid vorsichtig! Wenn Ihr irgendetwas Hilfreiches entdeckt, bringt es zu mir.",
    ["O"] = "Besteigt den Turm beim Südwindposten und findet eine Möglichkeit, den Schwarmbau der Silithiden aufzuscheuchen. Bringt alles Ungewöhnliche, das Ihr dabei entdeckt, zu Layo Sternenschlag auf den Friedhof Heldenwacht in Silithus.",
    ["T"] = "Schwarmbau im Turm",
  },
  [1127] = {
    ["D"] = "Ihr möchtet also etwas von Fools Starkbier, hm? Denkt Ihr daran, wieder die Rennen zu besuchen...?$B$BGut, bringt mir mehr von Zanzils Mixtur und ich braue noch etwas mehr Starkbier für Euch.",
    ["O"] = "Bringt 12 von Zanzils Mixturen zu Crank Zischelbub in Beutebucht.",
    ["T"] = "Fools Starkbier",
  },
  [1130] = {
    ["D"] = "$N, Melor Steinhuf hat von Euren Taten gehört und Euch einen Weg geebnet.$B$BWenn Ihr ihn einschlagen möchtet, dann geht nach Donnerfels und sprecht mit Melor. Ihr findet ihn auf der Anhöhe der Jäger.",
    ["O"] = "Sprecht mit Melor Steinhuf in Donnerfels.",
    ["T"] = "Melor schickt Kunde",
  },
  [1131] = {
    ["D"] = "Manchmal jagen wir wegen Essen. Manchmal jagen wir wegen der Ehre. Und manchmal jagen wir, um der Lehren der Erdenmutter teilhaftig zu werden.$B$BDoch die Jagd, auf die ich Euch jetzt schicke, hat damit nichts zu tun. Es geht darum, eine Kreatur zu töten, die nicht unter dem Segen der Erdenmutter steht.$B$BDie Hyäne Stahlbiss streift südlich des Brachlandes durch Tausend Nadeln. Sie ist eine Tyrannin, die andere Hyänen anlockt und Furcht und Blutvergießen unter den friedlicheren Wildtieren verbreitet.$B$BSucht Stahlbiss, $N. Sucht und besiegt sie.",
    ["O"] = "Bringt Stahlbiss\' Rippe zu Melor Steinhuf in Donnerfels.",
    ["T"] = "Stahlbiss",
  },
  [1132] = {
    ["D"] = "Sprecht mit Fiora Langohr. Sie befindet sich an den Docks von Auberdine in Dunkelküste.",
    ["O"] = "Sprecht mit Fiora Langohr. Sie befindet sich an den Docks von Auberdine in Dunkelküste.",
    ["T"] = "Fiora Langohr",
  },
  [1133] = {
    ["D"] = "Sprecht mit Shindrell Feuerflink in Astranaar.",
    ["O"] = "Sprecht mit Shindrell Feuerflink in Astranaar.",
    ["T"] = "Reise nach Astranaar",
  },
  [1134] = {
    ["D"] = "Wir führen keinen offenen Krieg mit der Horde, aber der Frieden ist brüchig. Ihre Agenten gehen häufig gegen uns vor, aber stets heimlich.$B$BEiner dieser Feldzüge wurde uns durch geheime Informanten in Theramore enthüllt, die behaupten, dass die Orcs Gift der Prachtschwingen beim Mirkfallonsee im Steinkrallengebirge im Süden sammeln. Dann benutzen orcische Auftragsmörder das Gift bei ihren geheimen Feldzügen gegen die Allianz!$B$BWenn das stimmt, dann müssen wir sie aufhalten, $N. Wir müssen ihnen den Zugang zu dem Gift versperren.",
    ["O"] = "Bringt 12 Prachtschwingengiftbeutel zu Shindrell Feuerflink in Astranaar.",
    ["T"] = "Prachtschwingen des Steinkrallengebirges",
  },
  [1135] = {
    ["D"] = "Wie Ihr sicher wisst, werden Wyvern oft von Orcs als Reittiere gezähmt. Weniger bekannt indessen ist, dass das Gift eines Wyvern in manchen Kreisen höher geschätzt wird als die Bestie selbst. Falls Ihr also keine Angst vor einer kleinen Wyvernjagd habt... habe ich einen Job für Euch.$B$BEs gibt einen Ort im Land Tausend Nadeln, er liegt westlich von hier, welcher als der Steilhang bekannt ist. Die dort lebenden Steilhangwyvern haben ein starkes Gift. Stark und kostbar.",
    ["O"] = "Bringt 10 Steilhanggiftbeutel zu Fiora Langohr in Theramore.",
    ["T"] = "Steilhanggift",
  },
  [1136] = {
    ["D"] = "$N, jetzt ist es Zeit, weit entfernte Länder zu bereisen. Ihr seid bereit.$B$BIhr jagt jetzt Frostrachen, einen Yeti im Alteracgebirge. Er ist schwer zu erkennen, da sein weißes Fell mit den Schneefeldern seiner Bergheimat verschmilzt. Man bekommt ihn kaum je zu sehen.$B$BAber ich zeige Euch, wie man eines solchen Geschöpfes habhaft wird. Hier, nehmt diese Rolle. Ich habe darauf vermerkt, wie man Frostrachen anlocken kann.$B$BGeht jetzt, $N. Eure Reise ist lang und viele Herausforderungen harren Eurer. Erst wenn Ihr mit Frostrachens Mähne zurückkehrt, ist Eure Aufgabe abgeschlossen.",
    ["O"] = "Bringt Frostrachens Mähne zu Melor Steinhuf in Donnerfels.",
    ["T"] = "Frostrachen",
  },
  [1137] = {
    ["D"] = "Indurium besitzt einen erstaunlichen Hitzewiderstand! Ich bin sicher, es taugt für Fizzels Kolben. Aber er braucht echtes Indurium, nicht nur die Späne.$B$BDas Erz muss tief in den Minen von Uldaman abgebaut werden, an der Nordgrenze des Ödlands. Das ist die wahre Quelle von Indurium.$B$BBestellt Fizzel Kupferbolz Grüße von mir und sagt ihm, was ich Euch gesagt habe.$B$BAngenehme Reise, $N. Ich wäre nicht überrascht, wenn wir uns wiedersehen - wenn Fizzel von dem Indurium erfährt, schickt er Euch wahrscheinlich nach Uldaman, um es zu holen!",
    ["O"] = "Sprecht mit Fizzel Kupferbolz in der Schimmernden Ebene in Tausend Nadeln.",
    ["T"] = "Neuigkeiten für Fizzel",
  },
  [1138] = {
    ["D"] = "Ich liebe Krebse. Krebse sind die Früchte des Meeres! Man kann sie backen, grillen, kochen und braten. Man kann sie in der Pfanne braten, frittieren und kurzbraten. Es gibt Krebssalat, Krebssuppe, Krebseintopf, Pfefferkrebs, Zitronenkrebs, Peitscherwurzelkrebs und Eisenschmiedekrebs à la Surprise. Das...das war\'s eigentlich.$B$BIch angle hier gerade und daher kann ich mir keine feinen Krebsbrocken besorgen. Man kann sie aus Riffkriechern und verkrusteten Gezeitenkriechern kratzen. Man kann viel mit ihnen anstellen. Man kann sie backen, grillen, kochen...",
    ["O"] = "Sammelt 6 feine Krebsbrocken für Gubber Blump in Auberdine.",
    ["T"] = "Meeresfrüchte",
  },
  [1139] = {
    ["D"] = "Wir haben den Schattenschmiedeklan ausgebremst, indem wir ihn von der Schwarzfelsspitze abgeschnitten haben, doch Hammerzehs Mission wurde noch nicht erfüllt. Sein Tod darf nicht umsonst gewesen sein. Er träumte davon, die Schrifttafel des Willens zu finden: eine Steintafel, graviert mit Magie durch die Titanen selbst, die helfen, ihren Schöpfungen Leben einzuhauchen und ihnen damit einen eigenen Willen ermöglichen. So jedenfalls heißt es in den Legenden.$B$BDennoch müssen wir die Tafel bergen, ehe der Schattenschmiedeklan sie finden kann.$B$BKehrt nach Uldaman zurück, sucht die Tafel und kommt unverzüglich wieder her.",
    ["O"] = "Sucht die Tafel des Willens und bringt sie zu Berater Belgrum in Eisenschmiede.",
    ["T"] = "Die verlorene Tafel des Willens",
  },
  [1140] = {
    ["D"] = "Die Schrift der Orcs ist kaum mehr als das Gekritzel eines Kindes. Viele meines Ordens spotteten, als ich ihre Sprache lernen wollte, aber ich fand es hilfreich, meine Feinde besser zu verstehen.$B$BIlkruds Gekritzel zufolge scheint Athrikus immer noch zwei Seelenedelsteine zu besitzen, und diese werden in der Nachtflucht und Satyrnaar aufbewahrt, Satyrgebiete im nordöstlichen Eschental.$B$BSeid vorsichtig da draußen, $N, die bluttriefenden Tempel der Satyrn können selbst für abgebrühte Helden wie Euch beängstigend sein.",
    ["O"] = "Befreit die Hochgeborenenseelen in der Nachtflucht und Satyrnaar und kehrt zu Delgren dem Läuterer in Maestras Posten zurück.",
    ["T"] = "Der Turm von Althalaxx",
  },
  [1141] = {
    ["D"] = "Ich liebe das Angeln. Die Familie Blump ist berühmt fürs Angeln. Mein Name ist Gubber Blump; ich angle.$B$BEs gibt eine Fischart, die ich hier in der Gegend gern fange. Sie heißt Dunkelküstenbarsch. Früher fuhr ich mit einem Boot hinaus, um Barsche zu angeln, aber seitdem die gemeinen Murlocs gekommen sind, fahren keine Boote mehr raus. Ich wette, dass sie auch alle Barsche aufgefressen haben.$B$BSagt, wollt Ihr mir helfen, Dunkelküstenbarsche zu fangen? Ich würde Euch eine echte Angelrute der Familie Blump geben, wenn Ihr es tun würdet! Es ist eine wirklich gute Angelrute, um damit Fische zu fangen.",
    ["O"] = "Fangt 6 Dunkelküstenbarsche für Gubber Blump in Auberdine.",
    ["T"] = "Die Familie und die Angelrute",
  },
  [1142] = {
    ["D"] = "Ich habe nicht mehr lange zu leben... so... so... schwach.$B$B... der t-t-tapfere Zwerg Einbraue hat jemanden geschickt, Falfindel zu w-w-warnen...$B$BAber bevor ich... s-s-sterbe...$B$B... meine Frau Treshala... ihr Anhänger des Bundes mit mir... gestohlen... von einem der n-n-niederträchtigen Angreifer...$B$BB-B-Bitte sucht ihn... und bringt ihn Treshala in Darnassus zurück...$B$B... mit der Nachricht meines einsamen... Todes.",
    ["O"] = "Sucht und bringt Treshalas Anhänger zu Treshala Bachquell in Darnassus.",
    ["T"] = "Die Sterblichkeit schwindet",
  },
  [1143] = {
    ["D"] = "Verstehe. Also wenn das stimmt, was Ihr sagt, dann ist jetzt die Zeit gekommen, endlich zuzuschlagen. Ich glaube, Athrikus residiert in der Turmspitze. Aber seid vorsichtig, er ist von vielen mächtigen Hexenmeistern umgeben, die tödlicher sind als Eure bisherigen Gegner.",
    ["O"] = "Tötet Athrikus Narassin und bringt seinen Kopf zu Balthule Schattenhieb nahe des Turms von Althalaxx.",
    ["T"] = "Der Turm von Althalaxx",
  },
  [1144] = {
    ["D"] = "Führt Willix den Importeur aus dem Kral der Klingenhauer hinaus.",
    ["O"] = "Führt Willix den Importeur aus dem Kral der Klingenhauer hinaus.",
    ["T"] = "Willix der Importeur",
  },
  [1145] = {
    ["D"] = "Viele Reisende, die zum Wegekreuz kamen, halfen mir, Eier vom Feld der Riesen zu sammeln, und viele können Geschichten erzählen, wie tödlich diese Kreaturen sind. Ich habe genug gehört, um diese Insekten als größere Bedrohung einzustufen als meine Meister zuerst dachten.$B$BIch brauche einen Kurier, der nach Orgrimmar reist und sich mit Belgrom Felshammer trifft. Das dürfte keine Kleinigkeit werden und es sollte nur jemand gehen, der die Aufgabe ernst nimmt.$B$BBelgrom ist für gewöhnlich vor der Halle der Kriegerhelden zu finden.",
    ["O"] = "Stellt Korrans versiegelte Notiz Belgrom Felshammer in Orgrimmar zu.",
    ["T"] = "Der Schwarm wächst",
  },
  [1147] = {
    ["D"] = "Hmm. Offenkundig hält Belgrom die Monster für eine Bedrohung. Er möchte, dass sie in die Tiefen zurückgedrängt werden, aus denen sie gekommen sind.$B$BDie Zwerge zu töten, die von der Forscherliga geschickt wurden, genügte wohl nicht - jetzt muss ich eine Gruppe halbtoter Krieger in einen neuen Kampf führen. Aber selbst können wir das nicht; nicht unter diesen Bedingungen. Die Grabungsstätte liegt südlich von hier. Es sollten keine Zwerge mehr in der Gegend sein. Habt Ihr Lust, Klinge oder Stab in die Hand zu nehmen und uns zu unterstützen?",
    ["O"] = "Tötet 5 Silithidsucher, 5 Silithidschwarmdrohnen sowie 5 Silithideindringlinge und kehrt zu Moktar Krin in Tausend Nadeln zurück.",
    ["T"] = "Der Schwarm wächst",
  },
  [1148] = {
    ["D"] = "Ihr untersucht den Knochenpanzer gründlich. Als Ihr ihn in den Händen dreht, stellt Ihr fest, dass die Chitinplatten derart verflochten sind, dass er dadurch unglaublich hart wird. So etwas habt Ihr noch nie gesehen. Ihr untersucht den Rest des Kadavers und kommt zu dem Schluss, dass diese Kreaturen sehr kräftig und hochentwickelt sind.$B$BAls ihr einen weiteren Blick auf den Knochenpanzer werft, wird Euch klar, dass Korran diese Kreaturen gewiss gern studieren würde. Er könnte dadurch herausfinden, was diese Kreaturen sind und woher sie kommen.",
    ["O"] = "Bringt 1 Silithidenherz, 5 Silithidenkrallen und 3 intakte Silithidenknochenpanzer zu Korran beim Wegekreuz.",
    ["T"] = "Teile des Schwarms",
  },
  [1150] = {
    ["D"] = "Ich sehe mit Freuden, dass Euer Mut im Lichte Eurer bisherigen Taten unerschütterlich bleibt. Da Euer Glaube stark ist, wird es Zeit, Euren Körper zu testen.$B$BAls Erstes ein Test der Belastbarkeit. Geht zum Bau der Wildfedern westlich vom Freiwindposten. Steigt in die Tiefen ihres Unterschlupfes hinab und zerstört ihre Nahrungsvorräte. Damit solltet Ihr deren Zorn angestachelt haben. Wenn Ihr lange genug überlebt, wird Grenka Blutkreischer Euch persönlich heimsuchen. Tötet sie und bringt mir ihre Klaue, um den Test abzuschließen.",
    ["O"] = "Bringt Grenkas Klaue zu Dorn Ebenenpirscher in Tausend Nadeln.",
    ["T"] = "Test der Belastbarkeit",
  },
  [1151] = {
    ["D"] = "Zu Zeiten der Alten Götter bewohnten Elementare einen Großteil dieser Welt. Sie beteten die Alten Götter an, und die Götter fesselten die Elementare durch ihre Macht an diese Welt. Als die Alten Götter verbannt wurden, verblassten die Elementare in unserer Welt und kehren jetzt auf Geheiß mächtiger Magier zurück.$B$BAber es gibt Ausnahmen. Einige Elementare waren mächtig genug, aus eigenen Stücken hier zu bleiben. Solch ein Elementar ist Rok\'Alim.$B$BSucht Rok\'Alim in Tausend Nadeln und tötet ihn, um Eure Kraft zu beweisen.",
    ["O"] = "Bringt Fragmente von Rok\'Alim zu Dorn Ebenenpirscher in Tausend Nadeln.",
    ["T"] = "Test der Kraft",
  },
  [1152] = {
    ["D"] = "Eure Zeit bei mir neigt sich dem Ende zu. Ich bin wissend, aber den Test der Lehre kann ich Euch nicht stellen. Es gilt, Lektionen zu erlernen und Orte zu besuchen, die in der Zuständigkeit anderer liegen.$B$BWenn Ihr bereit seid, dann sucht Braug Dämmergeist auf - er ist ein höchst weiser Schamane. Er wird Euch weiter testen, also achtet auf seine Worte, $N.$B$BWenn Ihr bereit seid, dann sucht den Tunnel, der das Steinkrallengebirge mit dem Eschental verbindet. Dort in der Nähe lebt Braug. Und seid vorsichtig, $N, die Nachtelfen versuchen vielleicht, Euren Test zu behindern.",
    ["O"] = "Sucht Braug Dämmergeist in der Nähe des Eingangs zum Steinkrallenpfad im Steinkrallengebirge.",
    ["T"] = "Test der Lehre",
  },
  [1153] = {
    ["D"] = "$C, Ihr habt mir auch früher schon geholfen. Der Kriegshäuptling ist mit meinen bisherigen Leistungen zufrieden und bittet mich, mit neuer Entschlossenheit fortzufahren. Meine Ehre gebietet, dass ich den Kriegshäuptling wissen lasse, was Ihr alles vollbracht habt, doch zuvor möchte ich Euch abermals um Hilfe bitten.$B$BJüngst kamen Reisende aus dem Süden von einem Ort namens Tausend Nadeln. Dort töteten sie einen Kobold, der eine außergewöhnliche Erzprobe bei sich hatte, wollten sie aber nicht verkaufen. Ich möchte eine Probe dieses Erzes untersuchen.",
    ["O"] = "Sucht eine unbearbeitete Erzprobe bei einem Buddler oder Feldmesser der Bruchschnauzen und bringt sie zu Tatternack Stahlformer im Camp Taurajo im Brachland.",
    ["T"] = "Eine neue Erzprobe",
  },
  [1154] = {
    ["D"] = "Ihr habt Dorns Tests bestanden und wollt jetzt Eure nächste Prüfung absolvieren. Sehr gut. Bisher sind alle Eure Herausforderungen kindisch gewesen. Es ist leicht, Euren Körper zu testen - bei Eurem Verstand sieht das schon anders aus.$B$BIch werde es mit einfachen Worten erklären: sucht das Buch, das ich Euch nenne, lest es und beantwortet meine Fragen nach seinem Inhalt.$B$BWas für ein Buch, werdet Ihr fragen? Das \'Vermächtnis der Aspekte\'. Man findet es in den Tiefen des Grabhügels von Dor\'Danil im südlichen Eschental, wo es von den toten Druiden und deren Tötern bewacht wird.",
    ["O"] = "Sucht das \'Vermächtnis der Aspekte\' und bringt es zu Braug Dämmergeist in der Nähe des Eingangs zum Steinkrallenpfad im Steinkrallengebirge zurück.",
    ["T"] = "Test der Lehre",
  },
  [1155] = "_",
  [1156] = "_",
  [1157] = "_",
  [1158] = "_",
  [1159] = {
    ["D"] = "Gut gemacht, $N. Ihr habt den ersten Teil des Tests der Lehren bestanden, aber es bleibt noch eine Frage zu beantworten. Aber es ist nicht an mir, diese Frage zu stellen.$B$BBesucht Parqual Fintallas in Lordaeron. Er war ein bedeutender Historiker der Menschen, bevor die Seuche ihn erwischte. Jetzt sucht er immer noch Erleuchtung, freilich einer ganz anderen Art.$B$BEr wird eine weitere Forderung an Euch stellen, bevor er Euch etwas fragt, aber Ihr habt Euch bisher meisterhaft geschlagen - ich bin sicher, Ihr werdet dieses Hindernis überwinden.",
    ["O"] = "Sucht Parqual Fintallas in Unterstadt.",
    ["T"] = "Test der Lehre",
  },
  [1160] = {
    ["D"] = "Ich sage Euch, was Ihr suchen müsst und werde Euch sogar meine Frage stellen, aber nicht, weil Ihr Euch beweisen wollt. Wissen ist weit wichtiger als Kraft. Auch wenn andere Euch in ihren Fähigkeiten überlegen sein mögen, Euer Wissen über die Welt ist wertvoller.$B$BGeht zu dem Kloster im Norden Tirisfals. In der dortigen Bibliothek steht ein Buch mit dem Titel Die Anfänge der Bedrohung durch die Untoten. Es wurde von den jetzt verderbten Paladinen benutzt, um sie in meiner Art des Kampfes auszubilden.$B$BBringt es mir.",
    ["O"] = "Sucht Die Anfänge der Bedrohung durch die Untoten und bringt es zu Parqual Fintallas in Unterstadt.",
    ["T"] = "Test der Lehre",
  },
  [1161] = "_",
  [1162] = "_",
  [1163] = "_",
  [1164] = {
    ["D"] = "Die Hure! Das Flittchen! Kenata lebt noch; ihre Familie blüht und gedeiht, während ich leide. Verlassene, wahrhaftig!$B$BMeine Familie wurde Opfer der Seuche. Unser Besitz ebenfalls dahin, während des Kriegs geplündert und niedergebrannt. Und nach alledem haben sie und ihre lausigen Kinder die Stirn, das einzig Kostbare zu stehlen, das ich noch hatte.$B$BMir sind die Sachen, die sie gestohlen haben, längst einerlei. Ich möchte ihre Köpfe!$B$BTötet sie für mich! Geht zum Bauernhof der Dabyries in Arathi, nordöstlich der Zuflucht.",
    ["O"] = "Bringt Kenata, Fardel und Marcel Dabyries Köpfe zu Genavie Kahler in Unterstadt.",
    ["T"] = "Von Dieben stehlen",
  },
  [1166] = {
    ["D"] = "Verbeugen Euch vor Oberanführer Mok\'Morokk!$B$BRexxar mich machen zu mächtigem Anführer von Steinbrecherklan!$B$BIch gut Anführer. Ich Klan sicher machen.$B$BGroße Drachen kommen und machen Feuer in alte Heimat. Ich klug Oger. Finden neu Heimat für Klan hier.$B$BAlte Heimat so schnell verlassen, ich Sachen vergessen. Winzig $R, gehen mein Sachen holen. Gehen nach Süden und suchen mein lecker Grog, mein Schnupftabak und mein Geldkassette. Ich Euch mögen, wenn Ihr das tun. Ihr in Flammenbau und Ruinen der Steinbrecher schauen... sie dort sein, ich sicher!",
    ["O"] = "Oberanführer Mok\'Morokk in Brackenwall möchte, dass Ihr seinen Grog, seinen Schnupftabak und seine Geldkassette aus dem Sumpf der Düstermarschen holt.",
    ["T"] = "Oberanführer Mok\'Morokks Sorgen",
  },
  [1167] = {
    ["D"] = "Bevor Ihr Athrikus konfrontiert, wäre es vermutlich klug, Balthule zu konsultieren. Zweifellos ist er bei seiner Wacht über den Turm sehr aufmerksam gewesen und könnte Informationen über jüngste Entwicklungen haben.",
    ["O"] = "Sprecht mit Balthule Schattenhieb beim Turm von Althalaxx.",
    ["T"] = "Der Turm von Althalaxx",
  },
  [1168] = {
    ["D"] = "Mok\'Morokk wurde von Rexxar zum Oberanführer gemacht. Ihm liegt mehr an Macht als an den Ogern.$B$BSchwarze Großdrachen töten viele Oger. Töten Thargs Frau. Mok\'Morokk sagt uns, wir müssen fliehen. Lässt uns hierher kommen.$B$BOger sollten kämpfen. Oger nicht gut im Weglaufen. Mok\'Morokk schert sich nur um Mok\'Morokk.$B$BFür mich die Ruinen der Steinbrecher Heimat. Mir gefallen im Süden. Dieser Ort nicht Heimat.$B$BHelft Ogern, Armee der schwarzen Großdrachen zu bekämpfen. Ich wollen Rache. Ich wollen nach Hause.",
    ["O"] = "Tharg in Brackenwall möchte, dass Ihr 10 Späher der Feuermähnen, 10 Aschenschweife der Feuermähnen und 5 Bannschuppen der Feuermähnen tötet.",
    ["T"] = "Armee des schwarzen Großdrachen",
  },
  [1169] = {
    ["D"] = "Während der törichte Narr Mok\'Morokk seine Macht genießt und Tharg mit den Dämonen des Kummers und der Rache ringt und um die Führerschaft des Klans buhlt, scheine ich der Einzige zu sein, der die Ursache der Aggression in unserer alten Heimat herausfinden möchte.$B$BWarum der verwirrte Blick, $N? Erwartet Ihr, dass ich wie ein tumber Grobian spreche, nur weil ich ein Oger bin?$B$BDoch zurück zum Geschäft, bringt mir die Herzen und Zungen der Welpen und Jungtiere. Ich werde die Wurzel dieses Übels finden...",
    ["O"] = "Draz\'Zilb in Brackenwall hätte gern, dass Ihr ihm 7 sengende Zungen und 7 sengende Herzen bringt.",
    ["T"] = "Die Brut identifizieren",
  },
  [1170] = {
    ["D"] = "Das Dorf der Steinbrecher wurde von der Brut von Onyxia überrannt. Aber warum sollte die Tochter des schwarzen Drachenlords Todesschwinge über unser Land herfallen?$B$BDas ist sehr beunruhigend. Gewiss wurde Onyxia aus einem anderen Grund hergeschickt, als eine kleine Schar von Ogern zu belagern.$B$BVerständigt unverzüglich Mok\'Morokk! Es muss gehandelt werden.",
    ["O"] = "Sprecht mit Oberanführer Mok\'Morokk in Brackenwall.",
    ["T"] = "Die Brut von Onyxia",
  },
  [1172] = {
    ["D"] = "Onyxias Brut wurde über das Drachendüster verstreut. Es dürfen auf keinen Fall weitere Welpen schlüpfen.$B$BBegebt Euch eilig zum Drachensumpf in den Düstermarschen. Gewiss hat sie dort einen Hort angelegt. Sucht die Eier der bösen Großdrachen und vernichtet sie.$B$BWir können das Dorf der Steinbrecher nie zurückerobern, wenn das Umland eine Brutstätte bleibt.$B$BWas Mok\'Morokk angeht... für den Jammerlappen habe ich andere Pläne.",
    ["O"] = "Draz\'Zilb in Brackenwall möchte, dass Ihr 5 Eier von Onyxia zerstört.",
    ["T"] = "Die Brut von Onyxia",
  },
  [1173] = {
    ["D"] = "Ihr wagt es, Oberanführer Mok\'Morokk herauszufordern?$B$BHa!$B$BIch zerschmettern einen Winzling von $R!$B$BIhr mir sagen, wenn bereit für ordentliche Tracht Prügel.",
    ["O"] = "Besiegt Mok\'Morokk und bringt die Nachricht zu Draz\'Zilb in Brackenwall.",
    ["T"] = "Fordert Oberanführer Mok\'Morokk heraus",
  },
  [1174] = "_",
  [1175] = {
    ["D"] = "Nach dem schweren Zusammenstoß des Red-Thunder-Rennwagen der Goblins und einem Basilisken hat Kronkreiter mich angeheuert, um die Strecke zu warten. Keine Ahnung, was die Tiere in der Ebene fressen, dass sie so groß werden, aber wir müssen sie dezimieren, damit sie nicht auf die Fahrbahn laufen, wenn ein Rennen stattfindet.$B$BIch sag Euch was. Wenn ihr ein wenig Profit machen wollt, zieht los und entfernt die Basilisken aus der Gegend.",
    ["O"] = "Tötet 10 Salzsteinbasilisken, 10 Salzsteinkristallhäute und 6 Salzsteinstarrer für Rennsteckenleiter Zherin in der Schimmernden Ebene.",
    ["T"] = "Eine Straßenunebenheit",
  },
  [1176] = {
    ["D"] = "Wenn es um Rennen geht, zählt jede Kleinigkeit! Ob es sich nun um ein leises Quietschen der Kraftübertragung, eine geringfügige Schwäche der Stoßdämpfer oder ein leichteres Gewicht der Karosserie handelt, man muss alles berücksichtigen.$B$BWir benutzen Geierknochen für kleinere Modifikationen am Chassis unserer Rennwagen, die das perfekte Gleichgewicht zwischen Stärke und Gewicht haben. Und sie sind, was noch besser ist, leicht zu beschaffen, da es in der Salzwüste von Geiern wimmelt.$B$BBringt mir ein paar und ich zahle einen guten Preis dafür.",
    ["O"] = "Beschafft 10 hohle Geierknochen für Pozzik in der Schimmernden Ebene.",
    ["T"] = "Gewicht reduzieren",
  },
  [1177] = {
    ["D"] = "Patsch großer Oger.$B$BIch hungrig.$B$BGeht Fische hauen. Bringt Patsch Essen.$B$BIhr füttert Patsch oder Patsch frisst Euch.",
    ["O"] = "Schlammpatsch Dreckfuß in den nördlichen Düstermarschen möchte 8 Schlammflossenköpfe.",
    ["T"] = "Hunger!",
  },
  [1178] = {
    ["D"] = "Unser Rennfahrerteam ist der Stolz aller Goblins. Ihr wärt überrascht, welche Leute unser Team auf die eine oder andere Weise unterstützen.$B$BKennt Ihr Gazlowe? Er treibt auf der ganzen Welt Ersatzteile für uns auf. Er kam sogar mal her, um an einer unserer Aufhängungen zu arbeiten!$B$BVor ein paar Wochen wollte er uns den Prototyp einer neuen Treibstoffeinspritzpumpe schicken, aber sie ist noch nicht eingetroffen. Sonst ist er immer zuverlässig...Würde es Euch vielleicht etwas ausmachen, nach Ratschet zu gehen und ihn danach zu fragen? Ich will ja nicht drängen, aber die Gnome holen langsam auf!",
    ["O"] = "Sprecht mit Gazlowe in Ratschet im Brachland.",
    ["T"] = "Goblinsponsoring",
  },
  [1179] = {
    ["D"] = "Schon von der Schimmernden Ebene gehört, ja? Das ist eine Salzwüste drüben in Kalimdor, direkt südlich vom Canyon der Tausend Nadeln. Der ideale Austragungsort für Rennen, weil es da so flach ist!$B$BDa draußen sind zwei Gnomenbrüder, Freunde von mir, die ihren neuen Raketenwagen erproben, und soweit ich gehört habe, ist er schnell! Hey, so schnell, dass die Fahrer immer verunglücken. Und Gehirnerschütterungen bekommen.$B$BDarum baten sie mich um ein paar Helme für Belagerungsmaschinen, aber ich kann hier nicht weg, meine Pflicht fesselt mich an diesen Ort. Ich kann die Reise also nicht machen!$B$BKönnt Ihr es?",
    ["O"] = "Bringt die Kiste mit Schutzhelmen zu Wizzel Kupferbolz. Zu finden ist er an der Illusionenrennbahn in Tausend Nadeln.",
    ["T"] = "Die Brüder Kupferbolz",
  },
  [1180] = {
    ["D"] = "Zurück zum Grund Eures Hierseins. Ich weiß, ich habe Pozzik versprochen, dass ich ihm eine neue Treibstoffeinspritzpumpe schicke, aber ich habe sie selbst noch nicht bekommen. Revilgaz drüben in Beutebucht sagte, er würde sie herschicken, aber irgendwie scheint da was schief gelaufen zu sein.$B$BWenn das stimmt, können wir wenig dagegen tun. Aber wir können wenigstens herausfinden, wann sie von Beutebucht abgeschickt wurde.$B$BFragt drüben bei Werftmeister Lozgil nach, er müsste die Frachtbriefe überprüfen können.",
    ["O"] = "Sprecht mit Werftmeister Lozgil in Beutebucht.",
    ["T"] = "Goblinsponsoring",
  },
  [1181] = {
    ["D"] = "Ich fürchte, ich kann Euch nicht weiterhelfen, $N. Wenn Ihr der Sache weiter nachgehen möchtet, würde ich vorschlagen, Ihr geht zum Baron persönlich. Wenn jemand über die Geschäfte von Revilgaz Bescheid weiß, dann Revilgaz selbst.$B$BIhr findet ihn in der Taverne jenseits des Hafens, Obergeschoss.",
    ["O"] = "Sprecht mit Baron Revilgaz in Beutebucht.",
    ["T"] = "Goblinsponsoring",
  },
  [1182] = {
    ["D"] = "Wisst Ihr, die Schredder der Venture Company draußen am Nazferitisee (im Norden des Schlingendorntals) wurden mit einer neuartigen Treibstoffeinspritzpumpe ausgerüstet. Ich dachte mir, Pozzik könnte Kapital daraus schlagen, wenn er einige Einzelheiten erfahren könnte. Leider scheint Cozzle, der dortige Großknecht, etwas schlauer zu sein als ich ihm zugestehen wollte.$B$BAber Ihr seht aus, als könntet Ihr es schaffen, die Blaupausen zu finden. Ich weiß, dass er sie in seinem Haus aufbewahrt, aber mit Sicherheit verschlossen und verriegelt.",
    ["O"] = "Bringt die Treibstoffreglerblaupausen zu Baron Revilgaz in Beutebucht.",
    ["T"] = "Goblinsponsoring",
  },
  [1183] = {
    ["D"] = "Ihr solltet zum Aufbruch bereit sein. Bestellt Pozzik schöne Grüße und sagt ihm, wenn er etwas braucht, soll er keine Hemmungen haben, zu fragen. Ich warte auf die baldige Nachricht, dass er den Streckenrekord gebrochen hat!",
    ["O"] = "Bringt die Blaupausen der Treibstoffeinspritzpumpe zu Pozzik in der Schimmernden Ebene.",
    ["T"] = "Goblinsponsoring",
  },
  [1184] = {
    ["D"] = "Geht zurück zu Belgrom in Orgrimmar und sagt ihm, dass Moktar in Tausend Nadeln ganz in seinem Sinne handelt. Sagt ihm auch, dass meine Studien voranschreiten und ich hoffe, dass ich bald mehr gelernt habe. Das sollte ihn freuen.",
    ["O"] = "Kehrt zu Belgrom Felshammer in Orgrimmar zurück.",
    ["T"] = "Teile des Schwarms",
  },
  [1186] = {
    ["D"] = "Ich werde damit beschäftigt sein, diese neue Treibstoffeinspritzpumpe in unsere Rennwagen einzubauen, aber wenn Ihr dem Team noch weiter helfen möchtet, sprecht mit Razzeric. Er ist unser Fahrer - unser achtzehnter Fahrer, um genau zu sein -, und er nimmt selbst gern Veränderungen an den Rennwagen vor.$B$BAber er ist ein wenig daneben, bemüht Euch daher, nicht zu überrascht zu wirken.",
    ["O"] = "Sprecht mit Razzeric in der Schimmernden Ebene in Tausend Nadeln.",
    ["T"] = "Der achtzehnte Rennfahrer",
  },
  [1187] = {
    ["D"] = "Pozzik ist ein toller Mechaniker, das weiß ich, aber er hat Angst davor, einfach was mit viel Power einzubauen und dann loszudüsen. Aber ich nicht.$B$BWenn Pozzik mit einem Rennwagen fertig ist, nehme ich mir die Zeit und baue eigene Veränderungen ein - Ihr wisst schon, die Steuerung lockern, Teile des Rahmens entfernen und ihm mehr Saft geben!$B$BIch habe einen Zephyriumnachbrenner von Kainer im Gizmorium in Gadgetzan bestellt, aber offenbar musste der Zeppelin, mit dem er geschickt wurde, in den Düstermarschen notlanden! Ich muss das Ding haben, $N!",
    ["O"] = "Bergt den Zephyriumnachbrenner für Razzeric in der Schimmernden Ebene.",
    ["T"] = "Razzerics Basteln",
  },
  [1188] = {
    ["D"] = "Doch wartet, was ist das? Kainer hat die Sicherung aktiviert gelassen. Das reduziert die Menge Energie, die ich aus dem Nachbrenner rausholen kann. Ich sagte ihm, ich wollte sie deaktiviert haben! Das ist nicht gut.$B$BIhr müsst das ins Gadgetzan-Gizmorium in der Tanariswüste bringen und es von Kainer entfernen lassen.",
    ["O"] = "Bringt den Zephyriumnachbrenner zu Kainer nach Gadgetzan in Tanaris.",
    ["T"] = "Sicherheit zuerst",
  },
  [1189] = {
    ["D"] = "So, ich habe getan, was Razzeric wollte. Sagt ihm nur, er soll vorsichtig sein, wenn er das zündet. Wenn er den kleinsten Fehler macht, fährt er nicht los, sondern ist nur noch ein Brandfleck auf der Rennstrecke.$B$BWäre doch ein Jammer, wenn das passiert. Wer weiß, ob unser neunzehnter Fahrer genauso gut wäre?",
    ["O"] = "Bringt den umgebauten Zephyriumnachbrenner zu Razzeric. Zu finden ist er in der Schimmernden Ebene in Tausend Nadeln.",
    ["T"] = "Sicherheit zuerst",
  },
  [1190] = {
    ["D"] = "Die Kupferbolz-Brüder haben einen neuen Baustoff entdeckt, das ist die einzig logische Erklärung. Ihre Rennwagen fahren schneller als vorher, aber Design und alle anderen Daten ihrer Probefahrten sind noch dieselben.$B$BWir wissen, dass Rizzel Kupferbolz sich fast ununterbrochen mit den Plänen seiner neuen Entdeckung befasst hat.$B$BZamek kann ein Ablenkungsmanöver inszenieren, damit Ihr diese Pläne von ihnen stehlen könnt.",
    ["O"] = "Lasst Zamek ein Ablenkungsmanöver inszenieren und stehlt dann Rizzels Pläne für Pozzik in der Schimmernden Ebene in Tausend Nadeln.",
    ["T"] = "Tempo halten",
  },
  [1194] = {
    ["D"] = "Rizzels Baupläne beschreiben den Vorgang der Veredelung von Induriumerz. Man kann dem Gekritzel entnehmen, dass Induriumerz an der Ausgrabungsstätte Uldaman der Zwerge im Ödland zu finden ist.$B$BDie anderen Notizen behandeln fast ausschließlich seinen Hitzewiderstand.$B$BAls Briefbeschwerer der Pläne dient ein Klumpen, der wie unbearbeitetes Induriumerz aussieht.",
    ["O"] = "Bringt die Induriumerzprobe zu Pozzik. Zu finden ist er in der Schimmernden Ebene in Tausend Nadeln.",
    ["T"] = "Rizzels Baupläne",
  },
  [1195] = {
    ["D"] = "Die Galakzentauren von Tausend Nadeln beschützen ein Artefakt aus der Zeit der Erschaffung der Zentauren.$B$BWir würden es gern bergen, benötigen aber eine Phiole Wasser aus einem der Mondbrunnen der Nachtelfen.$B$BUm dieses Wasser zu beschaffen, müsst Ihr zuerst eine Phiole besorgen, wie sie die Dryaden beim Rajenbaum im Herzen des Eschentals besitzen. Am Westufer des Falfarren, südlich der Hauptstraße, befindet sich ein Mondbrunnen, in dem Ihr die Phiole füllen könnt.",
    ["O"] = "Bringt eine gefüllte geätzte Phiole zu Zangen Steinhuf in Donnerfels.",
    ["T"] = "Die heilige Flamme",
  },
  [1196] = {
    ["D"] = "Bringt die gefüllte Phiole zu Rau Klippenläufer beim Freiwindposten im Südosten von Tausend Nadeln. Ihr gelangt nach Tausend Nadeln, wenn Ihr den Großen Aufzug am Südrand des Brachlandes benutzt.$B$BIch wäre nicht überrascht, wenn Ihr uns weiter behilflich sein könntet.",
    ["O"] = "Bringt die gefüllte geätzte Phiole zu Rau Klippenläufer im Freiwindposten.",
    ["T"] = "Die heilige Flamme",
  },
  [1197] = {
    ["D"] = "Wenn Ihr das Wasser benutzt, das Ihr mitgebracht habt, könnt Ihr vorübergehend die Flamme löschen, die das Zentaurenrelikt schützt. Aber der Effekt ist nicht von Dauer, daher müsst Ihr rasch handeln.$B$BIhr findet die Spalthufklippe, die Festung der Zentauren, nördlich von hier. Die Flamme wird in der Spalthufhöhle bewahrt, einem schwer bewachten Teil der Klippe.$B$BAber seid vorsichtig, die Zentauren der Galak sind dort in großer Zahl zugegen und dürften etwas, das ihnen so teuer ist, nicht freiwillig hergeben.",
    ["O"] = "Holt den gespaltenen Huf für Rau Klippenläufer im Freiwindposten.",
    ["T"] = "Die heilige Flamme",
  },
  [1198] = {
    ["D"] = "Heutzutage kursieren viele Gerüchte über die Vereinigung des Schattenhammers mit den Naga in der Tiefschwarzen Grotte im Westen.$B$BMeine Rolle hier besteht darin, so viele Informationen wie möglich zu sammeln, damit wir wissen, wann und wo Hilfe erforderlich ist.$B$BEs ist jetzt Wochen her, seit ich einen Späher losschickte, um die Aktivitäten in der Tiefschwarzen Grotte auszuspähen. Späher Thaelrid hat sich noch nicht zurückgemeldet.$B$BMeine anderen Späher sind mit dringenden Angelegenheiten beschäftigt.$B$BSucht nach Thaelrid für mich, $N.",
    ["O"] = "Sucht Späher Thaelrid in der Tiefschwarzen Grotte auf.",
    ["T"] = "Auf der Suche nach Thaelrid",
  },
  [1199] = {
    ["D"] = "Mit der Macht der uralten Rituale der chaotischen Alten Götter versuchen die Mitglieder des Schattenhammers, ihre dunklen Schutzherren zurückzuholen und Tod und Schrecken in der Welt zu verbreiten.$B$BWir glauben, dass der Schattenhammer sich mit den Naga verbündet hat und sich ihre Anhänger in der Tiefschwarzen Grotte im Westen zusammenrotten.$B$BWelch schändliche Pläne in den Tiefen des alten Tempels auch immer geschmiedet werden, wir müssen sie vereiteln.$B$BFührt Krieg gegen die Anhänger des Schattenhammers und bringt Beweise Eurer Taten mit. Eure Bemühungen sollen reich belohnt werden.",
    ["O"] = "Bringt 10 Zwielichtanhänger zu Schildwache Aluwyn im Stützpunkt an der Grotte.",
    ["T"] = "Die Stunde der Finsternis",
  },
  [1200] = {
    ["D"] = "Die Kraft hat mich verlassen. Ich benötige Eure Hilfe!$B$BDiese Stätte war einst ein großer Tempel von Elune. Doch das Unglück führte zum Untergang, als die Verderbnis eines Alten Gottes unter der Erde emporwallte und den heiligen Mondbrunnen besudelte.$B$BAku\'mai, die Dienerin der Alten Götter, erhob sich aus den Wassern.$B$BDie Kultisten des Schattenhammers haben sich mit den Naga verschworen, um diese Anlage zu besetzen. Die Kultisten opfern unter Führung von Kelris Unschuldige für Aku\'mai, um Macht zu erlangen.$B$BBitte, erschlagt Kelris und bringt Ashelan im Stützpunkt an der Grotte seinen Kopf, $N.",
    ["O"] = "Bringt den Kopf von Zwielichtfürst Kelris zu Ashelan Nordwald im Stützpunkt an der Grotte.",
    ["T"] = "Schurkerei in der Tiefschwarzen Grotte",
  },
  [1201] = {
    ["D"] = "Die Horde und die Allianz wahren einen brüchigen Waffenstillstand. Beide Seiten liefern sich Scharmützel und heimliche Angriffe. Wir müssen dafür sorgen, dass die Horde stets die Oberhand behält!$B$BJetzt haben wir die Chance, genau das zu tun.$B$BSpitzel der Allianz haben Stellungen östlich des Dorfs Brackenwall bezogen. Sie spionieren das Dorf aus und melden unsere Bewegungen Theramore, was wir nicht dulden können.$B$BJagt die Spitzel und schaltet sie aus. Schickt eine deutliche Botschaft an ihre Vorgesetzten, dass wir die Anwesenheit ihrer Spione nicht dulden!",
    ["O"] = "Tötet 9 Spitzel von Theramore und kehrt dann zu Nazeer Blutlanze in Brackenwall zurück.",
    ["T"] = "Theramores Spione",
  },
  [1202] = {
    ["D"] = "$N, zwei unserer Spione wurden ausgeschickt, um Dokumente von einem Schiff bei den Docks von Theramore zu stehlen. Sie bekamen die Papiere, wurden aber entdeckt und waren gezwungen, die Dokumente ins Meer zu werfen.$B$BSpäter konnte ein Spion entkommen, die Beute aber nicht bergen. Er informierte mich über sein Scheitern und jetzt gebe ich Euch die Aufgabe.$B$BDie Dokumente sind in einer Schließkiste irgendwo im Wasser unter den Docks von Theramore. Sucht diese Dokumente, $N, und bringt sie mir.",
    ["O"] = "Bringt die Dokumente des Kapitäns zu Nazeer Blutlanze in Brackenwall.",
    ["T"] = "Die Docks von Theramore",
  },
  [1203] = {
    ["D"] = "Ich habe zu schneiden. Ich kann Euch nicht sagen, was ich schneiden werde, aber... Ich brauche eine Klinge. Eine ganz bestimmte Klinge...$B$BManche Leute sprechen von einem Schwert. Ich nenne es eine Schwingklinge.$B$BHelft Ihr mir? Diese Fischmenschen, Murlocs, oder wie auch immer die heißen... Einer von ihnen wird Messerwirbel genannt, hat ständig so ein großes Schwert bei sich, lebt nördlich von hier am Strand. Ich brauche sein Schwert!$B$BAber schlaft nicht noch darüber. Was ich schneide, das hält nicht ewig...",
    ["O"] = "Bringt Messerwirbels Schwert zu \"Sumpfauge\" Jarl im Irrlichtanwesen.",
    ["T"] = "Jarl braucht eine Klinge",
  },
  [1204] = {
    ["D"] = "Ich arbeite an einem Gericht namens \"Schlammpanzersuppe mit Käfern\". Die wichtigsten Zutaten fehlen mir leider noch immer... aber ich glaube nicht, dass ich einen Fußmarsch durch die Düstermarschen durchhalten könnte.$B$BNun denn, $N, könntet Ihr für mich auf die Jagd gehen? Als Erstes brauche ich die Zungen von Schlammpanzerschildkröten. Ihr findet die Schlammpanzer entlang der Küste, hauptsächlich im Bereich der Bucht.$B$BAber ich will nicht jede beliebige Zunge! Ich habe gehört, dass die Zungen von einigen Schlammpanzerschildkröten gespalten sind...$B$BMan sagt, diese gespaltenen Zungen hätten einen charakteristischen Geschmack, und den möchte ich in meinem Gericht...",
    ["O"] = "Bringt Morgan Stern in Theramore 8 gespaltene Schlammpanzerzungen.",
    ["T"] = "Schlammpanzersuppe mit Käfern",
  },
  [1205] = {
    ["D"] = "Schlammwühler ist ein uralter Krokilisk in den Düstermarschen. Und seine Zeit zum Sterben ist gekommen.$B$BAlte Knochen knirschen, wenn er seinen gewaltigen Leib durch den Sumpf schleppt, und auch wenn er sich noch mit der Kraft und dem Tempo der Jugend bewegt, martert sein alter Körper den großen innewohnenden Geist, ein Geist, dessen Flamme nicht erlischt.$B$BHeutzutage jedoch leidet er unablässig qualvolle Schmerzen. Ihr müsst das Leben dieser edlen Kreatur beenden, $N. Ihr müsst Schlammwühler Frieden geben.",
    ["O"] = "Bringt Schlammwühlers Zahn zu Melor in Donnerfels.",
    ["T"] = "Schlammwühler",
  },
  [1206] = {
    ["D"] = "Augensaft ist ein leckeres Getränk. Leider haben die meisten Tierwesen nur zwei Augen.$B$BAber Spinnen haben eine Menge Augen. Jede Menge. Und Graunebelaugensaft ist lecker. Samtig und salzig! Unzerplatzte Spinnenaugen... Ich schmecke sie schon förmlich.$B$BGraunebelspinnen leben westlich von hier in den Graunebelhöhlen. Glaubt Ihr, die Spinnen wurden nach der Höhle benannt... oder ist es umgekehrt?",
    ["O"] = "Bringt 20 unzerplatzte Graunebelaugen zu \"Sumpfauge\" Jarl im Irrlichtanwesen.",
    ["T"] = "Jarl braucht Augen",
  },
  [1218] = {
    ["D"] = "Ich koche gern. Leckere Sumpfküche. Frittierte grüne Froschschenkel und dergleichen.$B$BLeider habe ich keine Froschschenkel mehr. Beim Anwesen wimmelt es nur so von Fröschen, riesigen Marschenfröschen. Könntet Ihr mir vielleicht ein paar dieser Schenkel bringen? Das ist halb so wild, ehrlich. Haut ihnen einfach auf den Kopf und bringt mir die Schenkel.$B$BDann mache ich einen Eintopf und topfe ihn ein und schlabber ihn aus und geb Euch auch was ab.$B$BMögt Ihr Eintopf?",
    ["O"] = "Bringt 10 Marschenfroschschenkel zu \"Sumpfauge\" Jarl in den Düstermarschen.",
    ["T"] = "Marschenfroschschenkel",
  },
  [1219] = {
    ["D"] = "Beim Durchsuchen der lockeren Erde findet sich die abgetrennte Hand eines Orcs. Die Hand hält eine aus Knochen geschnitzte Röhre umklammert. Und in der Röhre befindet sich ein fleckiges Pergament.$B$BDas Pergament ist in der Sprache der Orcs verfasst... und sieht wichtig aus.",
    ["O"] = "Bringt den Bericht des Spions zu Leutnant Aden in Theramore.",
    ["T"] = "Der Orcbericht",
  },
  [1220] = {
    ["D"] = "Hauptmann Mumm sollte den Orcbericht sehen, den Ihr gefunden habt, $N. Er kennt die Sprache der Orcs besser als die meisten. Und er sollte wissen, dass sich Spione der Horde bei Theramore aufhalten!$B$BDer Hauptmann befindet sich in der Kaserne auf der nordöstlichen Seite der Insel Theramore.",
    ["O"] = "Bringt den Bericht des Spions zu Hauptmann Garran Mumm.",
    ["T"] = "Hauptmann Mumm",
  },
  [1221] = {
    ["D"] = "Blaulaubknollen gelten auf der ganzen Welt als Delikatesse! Aber sie sind rar, sehr rar. Man findet sie nur hier im Brachland, tief in der Erde des Krals der Klingenhauer.$B$BUnd selbst dann sind sie fast nicht zu finden, wenn man nicht weiß, wo man suchen muss! Darum habe ich diese Schnüffelnasenziesel abgerichtet, damit sie sie für mich aufstöbern. Sie haben unheimlich empfindliche Nasen und riechen eine Knolle aus fünfzig Schritt Entfernung.$B$BEs wird nicht einfach sein, aber wenn Ihr mir ein paar Knollen bringt, bezahle ich Euch gut dafür.",
    ["O"] = "Schnappt Euch eine Kiste mit Löchern.$BSchnappt Euch einen Schnüffelnasenleitstecken.$BSchnappt Euch das Handbuch für Schnüffelnasenbesitzer und lest es.$B$BBenutzt im Kral der Klingenhauer die Kiste mit Löchern, um ein Schnüffelnasenziesel zu beschwören, und benutzt den Leitstecken bei dem Ziesel, damit es nach Knollen sucht.$B$BBringt 6 Blaulaubknollen, den Schnüffelnasenleitstecken und die Kiste mit Löchern zu Mebok Mizzyrix in Ratschet.",
    ["T"] = "Blaulaubknollen",
  },
  [1222] = {
    ["D"] = "Klasse, endlich etwas, das mich nicht essen möchte!$B$BIch brauche Hilfe mit den Tierwesen hier, und Ihr kommt gerade recht, $GJungchen:Lady;.$B$BDer Koch Morgan Stern arbeitet an einem neuen Rezept und hat mich hierher geschickt, um Sumpfbohnenblätter zu sammeln. Ich glaube, er findet sie lecker.$B$BWie auch immer, während ich nach einer Sumpfbohnenpflanze suchte... wurde ich umzingelt! Könnt Ihr mir hier raus helfen und auf mich aufpassen, während ich nach den Sumpfbohnen suche?",
    ["O"] = "Begleitet \"Stinki\" Ignatz und sprecht dann mit Morgan Stern in Theramore.",
    ["T"] = "Stinkis Flucht",
  },
  [1238] = {
    ["D"] = "Beim Durchsuchen der lockeren Erde wird die abgetrennte Hand eines Orcs gefunden. Die Hand hält eine aus Knochen geschnitzte Röhre umklammert. Und in der Röhre befindet sich ein fleckiges Pergament.$B$BDas Pergament ist ein Bericht von einem Orc, der die Stadt Theramore über Wochen ausspionierte.$B$BAls Ihr das Pergament überflogen habt, ist klar, dass es zum orcischen Spionagemeister in Brackenwall gebracht werden muss.",
    ["O"] = "Bringt den Bericht des Spions zu Nazeer Blutlanze in Brackenwall.",
    ["T"] = "Der verlorene Bericht",
  },
  [1239] = {
    ["D"] = "Ausgiebiges Graben fördert den Kopf eines männlichen Orcs zu Tage, der am Hals abgesägt worden war. Seine Lippen sind zu einer höhnischen Grimasse verzerrt, seine dunklen Augen blicken trotzig.",
    ["O"] = "Bringt den Kopf des aufsässigen Orcs zu Nazeer Blutlanze in Brackenwall.",
    ["T"] = "Der abgetrennte Kopf",
  },
  [1240] = {
    ["D"] = "Wir müssen die Umstände von Margs Tod erfahren! Und das können wir nur, $N, durch die Hexenmagie der Trolle.$B$BBringt Margs Kopf zu Kin\'weelay im Schlingendorntal im Basislager von Grom\'gol.$B$BKin\'weelay ist der mächtigste Hexendoktor der Dunkelspeertrolle. Wenn jemand mit Marg Nachtauges Geist sprechen kann, dann er.",
    ["O"] = "Bringt den Kopf des aufsässigen Orcs zu Kin\'weelay im Schlingendorntal im Basislager von Grom\'gol.",
    ["T"] = "Der Trollhexendoktor",
  },
  [1241] = {
    ["D"] = "Kürzlich wurde ein Diplomat nach Theramore geschickt, um sich mit Jaina Prachtmeer zu treffen. Dieser Diplomat kam nie an.$B$BIch glaube, die Defias sind in diese Verschwörung verwickelt, weiß aber nicht, wie. Das Verschwinden des Diplomaten wurde noch nicht publik, aber lange kann das nicht mehr so bleiben.$B$BAgenten des Königs durchkämmen die Stadt bereits nach Hinweisen, aber ich habe eigene Verbindungsleute, die ich gern einschalten würde.$B$BIm Tal der Helden lebt ein alter Freund von mir namens Jorgen. Sucht ihn, gebt ihm diese Notiz und folgt seinen Anweisungen.",
    ["O"] = "Sucht Jorgen in der Stadt Sturmwind.",
    ["T"] = "Der vermisste Diplomat",
  },
  [1242] = {
    ["D"] = "Bringt diese Notiz zu Elling Trias. Sagt sonst nichts zu ihm. Seid nur höflich und wartet auf seine Antwort. Und keine Bange, er wird antworten.$B$BIch sage Euch das nur noch einmal, $N: nehmt diese Sache ernst und haltet die Klappe.$B$BIhr findet Trias in seinem Käseladen, Trias\' Käse, im Handelsdistrikt. Viel Glück.",
    ["O"] = "Bringt Jorgens versiegelte Notiz zu Elling Trias in der Stadt Sturmwind.",
    ["T"] = "Der vermisste Diplomat",
  },
  [1243] = {
    ["D"] = "Jorgen, alter Pickel! Aha, so soll es also sein, hm?$B$BNa gut, damit kann ich leben. Ich hoffe, er findet den Wirbelnden Nether umso früher!$B$BSo wird es ablaufen, $N. Ich werde meine Verbindungsleute hier kontaktieren, aber Ihr werdet den Großteil der Beinarbeit erledigen, damit ich ein wenig spare.$B$BGeht nach Dunkelhain im Dämmerwald und sucht Behüter Backus - er patrouilliert meist auf der Nordstraße vor der Stadt. Sagt ihm nur, dass Ihr für mich die Aktivitäten der Defias untersucht, dann wird er Euch mit allen verfügbaren Informationen weiterhelfen.",
    ["O"] = "Gebt Behüter Backus im Dämmerwald die versiegelte Notiz.",
    ["T"] = "Der vermisste Diplomat",
  },
  [1245] = {
    ["D"] = "Boah, das sieht zu komplex aus, um Euch nur auf eine falsche Fährte zu locken. Ich sollte das nicht einmal ansehen. Sprecht mit Trias - er kann es bestimmt verstehen. Und lasst Euch durch NICHTS aufhalten. Dies ist wichtiger als ich, vielleicht sogar wichtiger als er, aber wir werden sehen.$B$BUnd tut mir einen Gefallen, $N, sagt keinem, dass ich Euch geholfen habe. Wenn meine Vorgesetzten erfahren, dass ich davon wusste und sie nicht informiert habe, hängen sie mich wahrscheinlich.",
    ["O"] = "Bringt die Liste der Defias zu Elling Trias in Sturmwind.",
    ["T"] = "Der vermisste Diplomat",
  },
  [1246] = {
    ["D"] = "Ja, jetzt, wo ich darüber nachdenke... Dashel... Dashel irgendwas... Wie war sein Name? FAUST! Dashel Steinfaust. In manchen Kreisen nennen sie ihn Faust - wahrscheinlich, weil er ein Raufbold ist.$B$BIhr könnt ihn normalerweise in der Altstadt finden - direkt mitten im Zentrum. Redet mit ihm. Wenn es sein muss, dann benutzt ein Schwert oder etwas Schweres und Stumpfes, um ihn zum Reden zu bringen. Und zögert nicht. Wenn er so tief drinsteckt, wie diese Papiere andeuten, wird er Euch freiwillig keine Informationen geben.",
    ["O"] = "Sprecht mit Dashel Steinfaust in Sturmwind.",
    ["T"] = "Der vermisste Diplomat",
  },
  [1247] = {
    ["D"] = "Ja, also... hm... Vielleicht weiß ich was über dieses Treffen in Addles Siedlung. Das war, glaube ich, vor ein paar Wochen. Ich hatte wirklich nichts damit zu tun. Meine Rolle war vorbei, als Plan A schief ging... verdammte Abenteurer.$B$BDer Kerl, den wir für Plan B hatten, kam aus Menethil; ein Typ namens Slim. Mehr weiß ich nicht über ihren Ersatzplan.",
    ["O"] = "Sprecht mit Elling Trias in Sturmwind.",
    ["T"] = "Der vermisste Diplomat",
  },
  [1248] = {
    ["D"] = "Ein Verbindungsmann von mir verbringt seinen Ruhestand in der Hafenstadt Menethil im Sumpfland. Er sollte Euch auf Euer Wort hin helfen, wenn Ihr ihm sagt, dass Ihr für mich arbeitet. Sein Name ist Mikhail. Bevor er Sturmwind vor ein paar Jahren verließ, erwähnte er, dass er Schankkellner werden wollte.$B$BSprecht mit ihm, sucht diesen Slim und findet heraus, ob es noch etwas über diesen entführten Diplomaten zu wissen gibt. Wir müssen herausfinden, wohin sie ihn gebracht haben, oder wenigstens, wer er ist. In der Liste steht nichts von ihren Plänen nach seiner Entführung.",
    ["O"] = "Sprecht mit Mikhail im Sumpfland.",
    ["T"] = "Der vermisste Diplomat",
  },
  [1249] = {
    ["D"] = "Ihr sagt, Elling hat Euch geschickt? Dann soll Euch meine Gastfreundschaft zehnfach zuteilwerden. Wenn Ihr etwas braucht, fragt nur. Ich tue mein Bestes, Euch zu unterstützen.$B$BHmm, Slim, sagt Ihr? Bei dem Namen klingelt nichts, aber ich werde nach ihm Ausschau halten.$B$BSchaut jetzt nicht hin, $N, aber der Herr dort am Ausgang sieht herüber. Er folgt unserer Unterhaltung etwas zu intensiv, wenn Ihr versteht, was ich meine. Ich an Eurer Stelle würde tun, was ich kann, um ihn zu schnappen, bevor er zur Tür flieht.",
    ["O"] = "Schnappt Tapoke Slim Jahn, bevor er entkommt, und kehrt dann zu Mikhail in Menethil zurück.",
    ["T"] = "Der vermisste Diplomat",
  },
  [1250] = {
    ["D"] = "Die Defias zahlten mir nicht genug, um für diese Sache zu sterben. Sie entführten den König. Ja, den König von Sturmwind, keinen kleinen Diplomaten. Er war auf dem Weg nach Theramore, um sich mit Jaina Prachtmeer zu treffen - offenbar eine große Sache und streng geheim. Aber es sind nicht nur die Defias darin verwickelt. Sie arbeiten mit jemandem zusammen. Ich glaube, sie wussten, dass das ihre Kräfte übersteigt.$B$BMeine Aufgabe war es, ihren Mann an Bord des königlichen Schiffes zu bringen, als es im Hafen lag. Der Typ hieß Hendel. Mehr weiß ich nicht. Ich schwöre es!",
    ["O"] = "Sprecht mit Mikhail im Sumpfland.",
    ["T"] = "Der vermisste Diplomat",
  },
  [1251] = {
    ["D"] = "Ein Eisenschild, vom Feuer geschwärzt, das in dem Gasthaus wütete, ist an dem verfallenen Kamin befestigt, einer der wenigen erhaltenen Teile des Gebäudes, das einmal das Gasthaus \"Zur Süßen Ruh\" war.$B$BDer Schild lässt sich von den Mauersteinen des Kamins ablösen.",
    ["O"] = "Bringt den geschwärzten Eisenschild zu Krog in Brackenwall.",
    ["T"] = "Der schwarze Schild",
  },
  [1252] = {
    ["D"] = "Unter dem Geröll sticht Euch ein Funkeln ins Auge. Kaum sind Schmutz und Asche von dem Objekt gewischt, kommt das Emblem eines goldenen Ankers auf einer weißen Emailleoberfläche zum Vorschein: das Zeichen von Theramore.$B$BUnter dem Anker ist der Name \"Leutnant Paval Reethe\" eingeprägt.",
    ["O"] = "Bringt Reethes Abzeichen zu Hauptmann Garran Mumm in Theramore.",
    ["T"] = "Leutnant Paval Reethe",
  },
  [1253] = {
    ["D"] = "Ein Eisenschild, vom Feuer geschwärzt, das in dem Gasthaus wütete, ist an dem verfallenen Kamin befestigt, einer der wenigen erhaltenen Teile des Gebäudes, das einmal das Gasthaus \"Zur Süßen Ruh\" war.$B$BDer Schild lässt sich von den Mauersteinen des Kamins ablösen.",
    ["O"] = "Bringt den geschwärzten Eisenschild zu Hauptmann Garran Mumm in Theramore.",
    ["T"] = "Der schwarze Schild",
  },
  [1258] = {
    ["D"] = "Eine weitere Komponente meines neuen Rezepts ist das gepanzerte Bein eines Riesenkrebses. Und die Krebse, die sie in den Düstermarschen haben - stachelige Steinkrabbe nennen sie sie -, sollten sich hervorragend eignen!$B$BDie Schale des Krebsbeins ist das wichtigste Element der Präsentation des Gerichts, daher brauche ich makellose, unverletzte Beine. Ein ausgewachsener Krebs hat möglicherweise nur ein oder zwei Beine, die in Frage kommen, aber ich brauche viel mehr für mein Gericht.$B$BMacht Euch auf den Weg zu den Felsen rund um Theramore und besorgt mir diese Beine.",
    ["O"] = "Bringt 12 makellose Kriecherbeine zu Morgan Stern in Theramore.",
    ["T"] = "...und Käfer",
  },
  [1259] = {
    ["D"] = "Ich kenne den Namen Leutnant Paval Reethe nicht. Am besten erkundigt Ihr Euch bei Adjutant Tesoran. Er hat alle Aufzeichnungen über die Wachen von Theramore, vom Personal bis hin zur Ausrüstung.$B$BEr sollte Euch nach einem Blick in seine Bücher sagen können, wer dieser Reethe ist.",
    ["O"] = "Findet bei Adjudant Tesoran in Theramore mehr über Reethe heraus.",
    ["T"] = "Leutnant Paval Reethe",
  },
  [1260] = {
    ["D"] = "Ich entwickle ein neues Menü für das Restaurant. Es soll aus regionalen Gerichten mit einem Hauch von \"Bayou\" bestehen. Ich glaube, meine Kunden im Blauen Eremiten werden es lieben!$B$BIch schickte meinen Neffen Morgan nach Theramore, einer Stadt am Rand der Düstermarschen. Er sammelt die Rezepte der Einheimischen und ich muss wissen, was er bisher gefunden hat.$B$BDie Reise nach Theramore ist lang. Zu lang für mich. Aber ich wette, $Gein:eine:c; $C wie Ihr $Geiner:eine:c; seid, nutzt jede Chance für eine Reise, ja?",
    ["O"] = "Sprecht mit Morgan Stern in Theramore.",
    ["T"] = "Morgan Stern",
  },
  [1261] = {
    ["D"] = "Verfluchter Matschpanzer! Man kann Marg nicht töten! Ah, aber ich bin tot. Also muss er mich getötet haben...$B$BAber meine Mission! Meine Mission! Ihr, $N, müsst sie beenden.$B$BIn der Nähe von Theramore sah ich eine Gruppe Soldaten der Allianz am Ufer kampieren. Ein Soldat trug einen Anhänger... einen Anhänger mit mehr Edelsteinen, als ihn sich ein Soldat leisten könnte!$B$BIn jener Nacht schlich ich in ihr Lager und stahl den Anhänger, aber nach meiner Rückkehr zum Stützpunkt tötete mich ein Matschpanzerkrebsmann!$B$B$N, Ihr müsst diesen Matschpanzer finden und den Anhänger retten!",
    ["O"] = "Bringt den juwelenbesetzten Anhänger zu Nazeer in Brackenwall.",
    ["T"] = "Marg spricht",
  },
  [1262] = {
    ["D"] = "Wie seltsam, dass sich der Anhänger, den Marg stahl, in den Händen eines gemeinen Soldaten befand. Vielleicht hatte sich sein Besitzer verkleidet, oder die Soldaten selbst hatten ihn gestohlen!$B$BWas auch immer, dies ist der Anhänger eines Adligen und wir müssen seinen wahren Besitzer finden, wenn wir das Rätsel lösen wollen.$B$BBringt den Anhänger zu Zor Einbaum in Orgrimmar. Er ist mit der Politik der Allianz vertraut und wird wissen, ob dieser Anhänger etwas mit der Geschichte der Adligen von Sturmwind zu tun hat.",
    ["O"] = "Bringt den Edelsteinanhänger zu Zor Einbaum in Orgrimmar.",
    ["T"] = "Bei Zor melden",
  },
  [1263] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Das brennende Gasthaus &lt;ZU TRATSCH WECHSELN&gt;",
  },
  [1264] = {
    ["D"] = "Am Anfang jedes Monats werden die Marinesoldaten von Kul Tiras bezahlt und verlassen Theramore meist mit anderen Zielen. Viele kommen hierher, um Frauen aufzureißen, zu zechen und ihren Spaß zu haben. Eine dieser Gruppen betrachte ich als Stammgäste der Taverne, und ich glaube, dieser Hendel ist dabei.$B$BKommandant Samaul in Theramore könnte Euch helfen, ihn zu finden, wenn Hendel noch hier stationiert ist. Er ist Prachtmeer treu ergeben, aber wenn Ihr Ellings Namen erwähnt, dürfte er Euch helfen... diskret.",
    ["O"] = "Sucht Kommandant Samaul in den Düstermarschen.",
    ["T"] = "Der vermisste Diplomat",
  },
  [1266] = {
    ["D"] = "Der Gefreite Hendel hält sich westlich von hier auf. Im Augenblick ist er auf Wache und beschützt einige von Theramores Spitzeln, während sie ausruhen. Sie behalten Brackenwall, den Stützpunkt der Horde, im Auge und suchen nach allem, das unsere Grenzen bedrohen könnte.$B$BIhr müsstet ihn problemlos finden können; sucht einfach nach Zelten am Straßenrand.$B$BIch muss mich um andere Dinge kümmern, stoße aber bestimmt bald zu Euch. Ihr könnt mich immer im Turm von Lady Prachtmeer in Theramore finden, falls Ihr Probleme habt.",
    ["O"] = "Sucht den Gefreiten Hendel in den Düstermarschen.",
    ["T"] = "Der vermisste Diplomat",
  },
  [1267] = {
    ["D"] = "",
    ["O"] = "Sprecht mit Jaina Prachtmeer im Sumpf der Düstermarschen.",
    ["T"] = "Der vermisste Diplomat",
  },
  [1268] = {
    ["D"] = "Reihen von Hufspuren führen von den noch rauchenden Überresten des Gasthauses \"Zur Süßen Ruh\" weg. Wenigstens einige Abdrücke sind klar erkennbar, weitere Reihen sind im weichen Schlamm des Sumpfs kaum zu sehen.",
    ["O"] = "Berichtet Krog in Brackenwall von den verdächtigen Abdrücken beim Gasthaus \"Zur Süßen Ruh\".",
    ["T"] = "Verdächtige Hufabdrücke",
  },
  [1269] = {
    ["D"] = "Unter dem Geröll sticht Euch ein Funkeln ins Auge. Kaum sind Schmutz und Asche von dem Objekt gewischt, kommt das Emblem eines goldenen Ankers auf einer weißen Emailleoberfläche zum Vorschein: das Zeichen von Theramore.$B$BUnter dem Anker ist der Name \"Leutnant Paval Reethe\" eingeprägt.",
    ["O"] = "Bringt Reethes Abzeichen zu Krog in Brackenwall.",
    ["T"] = "Leutnant Paval Reethe",
  },
  [1270] = {
    ["D"] = "Klasse, endlich etwas, das mich nicht essen will!!!$B$BIch brauche Hilfe mit den Tierwesen hier, und Ihr kommt gerade recht, $GJungchen:Mädel;.$B$BEin Goblin aus Ratschet namens Mebok Mizzyrix braucht ein paar Sumpfbohnenblätter für einen Trank oder so... Mir ist ganz ehrlich einerlei, warum - er hat gut bezahlt und ich kenne diesen Sumpf, also bin ich hier.$B$BZu dumm, dass ich umzingelt wurde, bevor ich die Sumpfbohnen fand! Könnt Ihr mir hier raushelfen und auf mich aufpassen, während ich nach Meboks Pflanze suche?",
    ["O"] = "Begleitet \"Stinki\" Ignatz und sprecht dann mit Mebok Mizzyrix in Ratschet.",
    ["T"] = "Stinkis Flucht",
  },
  [1272] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Sucht Reethe &lt;ZU TRATSCH WECHSELN&gt;",
  },
  [1273] = {
    ["D"] = "Es hat lange gedauert, aber ich fand Reethe. Für einen Menschen hatte er sich gut versteckt.$B$BOgron hatte Angst, nach der langen Zeit im Sumpf könnte er verrückt sein. Ihr kommt mit mir, denn wir brauchen Antworten von ihm.",
    ["O"] = "Geht mit Ogron und sprecht mit Reethe, dann kehrt zu Krog in Brackenwall zurück.",
    ["T"] = "Befragt Reethe",
  },
  [1274] = {
    ["D"] = "Pardon, $Gwerter Herr:holde Dame;. Bischof DeLavey bat mich, Abenteurer zu suchen, die ihm in einer delikaten Angelegenheit helfen könnten.$B$BWenn Ihr so gut sein könntet, heimlich zur Burg Sturmwind zu reiten und mit ihm zu sprechen, sobald es Euch genehm ist - er wäre Euch sicherlich für Eure Hilfe dankbar. Und nochmals, bitte seid diskret. Es ist eine wichtige Angelegenheit.",
    ["O"] = "Sucht Bischof DeLavey in der Burg Sturmwind.",
    ["T"] = "Der vermisste Diplomat",
  },
  [1275] = {
    ["D"] = "Törichte Blutelfen spielen mit Dämonenmagie. Wurden sie nicht Zeugen, was mit den Naga und den Satyrn von Kalimdor geschehen ist?$B$BIch fürchte, die Blutelfen werden auf ähnliche Weise deformiert. Azeroth darf kein weiteres Volk abscheulicher Monster hervorbringen.$B$BIch möchte die Hirnstämme der Satyrn und Naga untersuchen, damit ich daraus folgern kann, welches Schicksal die Elfen erwartet. In der Tiefschwarzen Grotte unweit von hier im Westen werdet Ihr sowohl Satyrn als auch Naga finden.",
    ["O"] = "Relwyn Schattenstern am Stützpunkt an der Grotte benötigt 8 verderbte Gehinstämme.",
    ["T"] = "Erforschung der Verderbnis",
  },
  [1276] = {
    ["D"] = "Ah, jetzt erinnere ich mich! Mosarn ist ein angesehener Schmied in Donnerfels. Ich wusste, ich habe den Namen schon einmal gehört.$B$BAls Dank belohnte mich der Irdene Ring mit einem von Mosarn hergestellten Satz Rüstungen. Sehr gute Arbeit.$B$BVielleicht kann er Euch mehr über den Schild sagen, den er anfertigte.$B$BEs ist nicht viel, aber da wir aus Reethe nichts Nützliches herausbekamen, bleibt uns wohl nichts anderes übrig.",
    ["O"] = "Bringt den geschwärzten Eisenschild zu Mosarn in Donnerfels.",
    ["T"] = "Der schwarze Schild",
  },
  [1277] = "_",
  [1278] = "_",
  [1279] = "_",
  [1280] = "_",
  [1281] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Jims Lied &lt;ZU TRATSCH WECHSELN&gt;",
  },
  [1282] = {
    ["D"] = "Ich kann nicht anders, der alte Jim da tut mir leid. Er war nicht immer so. Hatte eine Zeitlang ein Gasthaus im Sumpf, wunderbares Lokal.$B$BHört zu, ich will wirklich nicht darüber sprechen. Besprecht es mit Hauptmann Mumm, wenn es sein muss. Ihr findet ihn auf der obersten Etage der Wehrzitadelle, der großen Kaserne östlich von Lady Jainas Turm.",
    ["O"] = "Sprecht mit Hauptmann Garran Mumm auf der obersten Etage der Wehrzitadelle in Theramore.",
    ["T"] = "Sie nennen ihn den lächelnden Jim",
  },
  [1283] = "_",
  [1284] = {
    ["D"] = "Reihen von Hufspuren führen von den noch rauchenden Überresten des Gasthauses \"Zur Süßen Ruh\" weg. Wenigstens einige Abdrücke sind klar erkennbar, weitere Reihen sind im weichen Schlamm des Sumpfs kaum zu sehen.",
    ["O"] = "Berichtet Hauptmann Garran Mumm in Theramore von den verdächtigen Abdrücken beim Gasthaus \"Zur Süßen Ruh\".",
    ["T"] = "Verdächtige Hufabdrücke",
  },
  [1285] = {
    ["D"] = "Reddon... Oh, da haben wir ihn! Leutnant Paval Reethe.$B$BTrat den Wachen von Theramore zusammen mit den meisten Rekruten bei, die mit Lady Prachtmeer über das Meer kamen. Trat mit sechzehn Jahren den Marinesoldaten von Kul Tiras bei. Herausragende Dienste, Beförderungen...$B$BIst als vermisst eingetragen.",
    ["O"] = "Kehrt mit den Informationen über Reethe zu Hauptmann Garran Mumm in Theramore zurück.",
    ["T"] = "Daelins Männer",
  },
  [1286] = {
    ["D"] = "Es war ein ständiger Kampf, in den Marschen präsent zu bleiben, aber durch die Kraft unserer Entschlossenheit konnten wir mehrere Wachtürme in diesem Gebiet zur Verteidigung halten.$B$BDie aktuellsten Informationen und Späherberichte, die ich bekam, deuten darauf hin, dass eine Gruppe Deserteure sich in unserem alten Turm bei der Verlassenen Wacht, südöstlich des Gasthauses, eingenistet hat.$B$BDer örtliche Anführer der Deserteure ist ein Mann namens Balos Jacken. Seht zu, ob Ihr Informationen über ihn bekommt.$B$BSeid gewarnt, $N. Er könnte sich als harte Nuss erweisen.",
    ["O"] = "Sammelt Informationen über Reethe von Balos Jacken und den Deserteuren in den Düstermarschen.",
    ["T"] = "Die Deserteure",
  },
  [1287] = {
    ["D"] = "Reethe? Dieser Feigling?$B$BWir warfen ihn aus dem Lager hinaus, weil er dauernd die Vorräte der Oger in Brackenwall plündern wollte. So viele sind wir nicht, dass wir es mit diesen hirnlosen Schlägern aufnehmen könnten.$B$BEr hat das Fass zum Überlaufen gebracht, daher haben wir ihn sich selbst überlassen.$B$BAlso ich habe Euch geholfen... Warum helft Ihr jetzt nicht uns und vergesst, dass Ihr uns gefunden habt?",
    ["O"] = "Sprecht mit Hauptmann Garran Mumm in Theramore über Reethe.",
    ["T"] = "Die Deserteure",
  },
  [1288] = {
    ["D"] = "Ihr habt gute Arbeit geleistet, $N, die Beste, die ich je von einem Amateur sah.$B$BLady Prachtmeer bat mich, sie über diese delikate Situation auf dem Laufenden zu halten, aber ich habe eine Weile nicht mit ihr gesprochen.$B$BIch wollte ihr meinen Bericht später bringen, aber es scheint mir passender, wenn Ihr mit ihr darüber sprecht. Ihr könnt ihre Fragen wahrscheinlich besser beantworten als ich.",
    ["O"] = "Bringt Mumms Bericht zu Lady Jaina Prachtmeer in Theramore.",
    ["T"] = "Mumms Bericht",
  },
  [1289] = {
    ["D"] = "Well. judging from Captain Vimes\'s report. you\'ve been a huge help to his investigation. and for that I thank you.$B$BI can put my mind more at ease knowing that the matter is in such capable hands. If you would. I would request that you return to Captain Vimes and help him in getting to the bottom of this mystery.",
    ["O"] = "Kehrt zu Hauptmann Garran Mumm in Theramore zurück.",
    ["T"] = "&lt;nyi&gt; Mumms Bericht",
  },
  [1293] = "_",
  [1294] = "_",
  [1295] = "_",
  [1296] = "_",
  [1297] = "_",
  [1299] = "_",
  [1300] = "_",
  [1301] = {
    ["D"] = "Bevor ich hier im Blauen Eremiten arbeitete, wurde ich von einem der besten Küchenchefs und Gastwirte ausgebildet, die ich kenne. Sein Name war James Hyal. Alles, was ich besitze und im Leben erreicht habe, verdanke ich ihm.$B$BIch frage mich, was er wohl so treibt... Sein Bruder Vincent und er wollten in einer Taverne im Hafen von Menethil arbeiten, aber ich habe lange nichts von ihm gehört.$B$BWenn Ihr jemals in diese Gegend kommen solltet, könntet Ihr ihm dann Grüße von mir bestellen?",
    ["O"] = "Sprecht mit Vincent Hyal im Hafen von Menethil.",
    ["T"] = "James Hyal",
  },
  [1302] = {
    ["D"] = "Mein Bruder James fuhr mit Lady Prachtmeer über das Meer. Als Letztes hörte ich, dass die Kämpfe dort drüben beigelegt waren und James irgendwo auf dem Kontinent ein Gasthaus eröffnete. Wo immer er sein mag, ich bin sicher, seine Geschäfte blühen. Er war ein verdammt guter Koch.$B$BAber ich habe eine Weile nichts mehr von ihm gehört - zu sehr mit seinem Gasthaus beschäftigt, denke ich. Der Amtmann in Theramore könnte Euch vielleicht zu ihm führen. Sucht ihn auf der obersten Etage dieser Zitadelle, die in der Mitte der Stadt steht. Ich glaube, sie nennen sie die Wehrzitadelle.",
    ["O"] = "Sprecht mit Amtmann Lendry bei der Wehrzitadelle in Theramore.",
    ["T"] = "James Hyal",
  },
  [1318] = {
    ["D"] = "Nun Ihr de\' König sein, vielleicht Ihr kriegen zurück wertvolles Artefakt der Gordok von gruseligen Elfen, der sich selbst nennen Prinz! König vor Euch hat versagt, so wie alle Könige seit Tortheldrin - de\' gruselige Elf - es gestohlen hat, von damaligen Gordokkönig!$B$BArtefakt heißen Stulpen der Gordokmacht. Alte Zeit Geschichten sagen, nur de\' wahre König de\' Gordok kann sie zurückholen. Alte Zeit Geschichten sagen auch, de\' gruselige Elf ist immer noch hier - Ihr finden es und es werden Eure mächtigen Stulpen, für immer! Yar, ich zeigen!",
    ["O"] = "Findet die Stulpen der Gordokmacht und bringt sie zu Hauptmann Krombruch in Düsterbruch.$B$BKrombruch zufolge sagen die \"Alte Zeit Geschichten\", dass Tortheldrin - ein \"gruseliger\" Elf, der sich selbst als Prinz bezeichnet- sie einem der Gordokkönige gestohlen hat.",
    ["T"] = "Die offene Rechnung der Gordok",
  },
  [1319] = {
    ["D"] = "Ich kann Euch leider nicht weiterhelfen, $N, schicke Euch aber zu jemandem, der es kann. Caz Doppelspross, einer unserer besten Schmiede, arbeitet in der Schmiede.$B$BBringt ihm den Schild und hört Euch an, was er zu sagen hat.",
    ["O"] = "Zeigt den geschwärzten Eisenschild Caz Doppelspross in Theramore.",
    ["T"] = "Der schwarze Schild",
  },
  [1320] = {
    ["D"] = "Ah, das hätte ich fast übersehen... Der Ledergurt an der Rückseite muss verbrannt sein, aber er war offenbar für eine große Person gemacht. Ich schätze, für einen Tauren.$B$BMehr kann ich Euch nicht sagen. Sagt Hauptmann Mumm, es tut mir leid, dass ich nicht mehr helfen konnte.",
    ["O"] = "Sprecht mit Hauptmann Garran Mumm in Theramore.",
    ["T"] = "Der schwarze Schild",
  },
  [1321] = {
    ["D"] = "Hmm... Ich sehe nichts Ungewöhnliches an diesem Schild. Do\'gol, der Schmied, sollte ihn sich ansehen. Vielleicht kann er uns mehr sagen, aber große Hoffnungen würde ich mir nicht machen...",
    ["O"] = "Zeigt den geschwärzten Eisenschild Do\'gol in Brackenwall.",
    ["T"] = "Der schwarze Schild",
  },
  [1322] = {
    ["D"] = "Hmmm, hier am Rand des Schildes steht etwas geschrieben, aber durch die Brandspuren kann man es kaum lesen.$B$BIch brauche Säure, um ihn zu reinigen. Das Gift der Graunebelspinnen sollte dafür ausreichen. Die Spinnen haben sich in einer alten Mine in den Graunebelhöhlen nordwestlich der Siedlung eingenistet. Bringt mir ihre Giftsäcke und ich reinige diesen Schild.",
    ["O"] = "Bringt 5 säurehaltige Giftbeutel zu Do\'gol in Brackenwall.",
    ["T"] = "Der schwarze Schild",
  },
  [1323] = {
    ["D"] = "Oh, ich glaube, ich habe zu viel Säure auf den Schild geschüttet. Lasst mich ein wenig rubbeln...$B$BHm, schwer zu lesen, sieht aber wie der Name Mosarn aus. Nie von ihm gehört. Vielleicht weiß Krog etwas. Krog kennt viele Leute.",
    ["O"] = "Sprecht mit Krog in Brackenwall.",
    ["T"] = "Der schwarze Schild",
  },
  [1324] = {
    ["D"] = "Was?! Ihr kommt hierher und werft mir vor, ich mache mit den Defias gemeinsame Sache und glaubt, ich höre mir diese Beleidigungen an? Etwas sagt mir, dass Ihr das schon lange verdient habt.",
    ["O"] = "Besiegt den Gefreiten Hendel in den Düstermarschen.",
    ["T"] = "Der vermisste Diplomat",
  },
  [1338] = {
    ["D"] = "Es gibt einen Rüstungsschmied bei den Zwergen, dessen Arbeit ich sehr bewundere. Sein Name ist Furen Langbart und seine Fertigkeit unübertroffen. Ich brauche einen neuen Schild und muss einen von ihm haben!$B$BDas Problem ist... Furen lebt weit im Süden im Zwergendistrikt von Sturmwind. Im Menschenland! Ich kann mir nicht vorstellen, warum er dort lebt, so weit weg von Eisenschmiede. Die Menschen müssen ihn fürstlich bezahlen, damit er dort bleibt!$B$BWenn Ihr reisen wollt und nichts gegen das Geldverdienen habt, würdet Ihr Furen dann meine Bitte nach einem Schild überbringen?",
    ["O"] = "Bringt Sturmlanzes Anforderung zu Furen Langbart in Sturmwind.",
    ["T"] = "Sturmlanzes Bestellung",
  },
  [1339] = {
    ["D"] = "Ich hörte, Gebirgsjäger Sturmlanze sucht nach einem Läufer. Jemand, der eine Reise für ihn unternimmt. Wie wäre es? Seid Ihr $Gein:eine:r; $R, $Gder:die:r; für den Job geeignet ist?$B$BWenn ja, findet Ihr Sturmlanze auf der Spitze des nördlichen Wachturms.",
    ["O"] = "Sprecht mit Gebirgsjäger Sturmlanze.",
    ["T"] = "Gebirgsjäger Sturmlanzes Aufgabe",
  },
  [1358] = {
    ["D"] = "Ein Kollege in Kalimdor, Apotheker Helbrim, untersucht die Auswirkungen von Lordaerons Toxinen auf die Wildtiere von Kalimdor. Er wäre an einer Probe der Wolfherzen interessiert, die Renferrel mir geschickt hat.$B$BHelbrim lebt im Brachland in einem Dorf am Wegekreuz. Um ihn zu erreichen, müsst Ihr per Zeppelin zur Hauptstadt der Orcs, Orgrimmar, reisen und dann zu Fuß Richtung Süden nach Durotar. Geht durch das Brachland nach Westen und Ihr erreicht bald die Siedlung am Wegekreuz.",
    ["O"] = "Bringt die Wolfherzprobe zu Apotheker Helbrim ins Brachland.",
    ["T"] = "Probe für Helbrim",
  },
  [1359] = {
    ["D"] = "Die Wolfherzen, die Ihr beschafft habt, verfügen über ein Merkmal, das es in ähnlichen Proben nicht gibt. Sie verfügen über eine Giftigkeit, die für genau die Wölfe tödlich sein müsste, in denen die Herzen schlagen!$B$BEs ist unglaublich und wir müssen das weiter untersuchen. Ich habe einige Proben der gewonnenen Herzen aufbewahrt. Bringt sie zu meiner Kollegin, Apothekerin Zinge. Ihr findet sie in unserem Hauptquartier in Unterstadt.",
    ["O"] = "Bringt die Wolfherzproben zu Apothekerin Zinge nach Unterstadt.",
    ["T"] = "Zinges Lieferung",
  },
  [1360] = {
    ["D"] = "Ich hatte selbst eine gute Arbeitsstelle als Buddler an der Uldaman-Ausgrabungsstätte im Ödland... das heißt, bis diese verfluchten Troggs auftauchten. Ich konnte gerade noch mit heiler Haut entkommen, das war alles. In dem Chaos habe ich meinen wertvollsten Besitz zurückgelassen. Natürlich ist er in meiner Truhe sicher vor den Troggs, aber die befindet sich mitten unter ihnen.$B$BIch wäre in Eurer Schuld, sowohl in Ehre als auch in Münze, wenn Ihr sie für mich besorgen könntet! Eine Truhe mit meinem Namen darauf befindet sich am Ende der Nördlichen Bankenhalle. Dorthin müsst Ihr gelangen.",
    ["O"] = "Holt Krom Starkarms wertvollen Besitz aus seiner Truhe in der Nördlichen Bankenhalle von Uldaman und bringt den Schatz zu ihm nach Eisenschmiede.",
    ["T"] = "Wiederbeschaffte Schätze",
  },
  [1361] = {
    ["D"] = "Ein Krieger der Horde, Regthar Totenfurt, rekrutiert Agenten für einen Einsatz im Land Desolace. Sprecht mit ihm, wenn Ihr Euch freiwillig melden wollt. Er lebt im Brachland in einer Reihe von Bunkern westlich des Wegekreuzes.$B$BUnd schärft Eure Waffen. Desolace ist ein vom Krieg zerrissener Ort.",
    ["O"] = "Sprecht mit Regthar Totenfurt.",
    ["T"] = "Regthar Totenfurt",
  },
  [1362] = {
    ["D"] = "Die Kolkarzentauren leben in Desolace. Ihre Truppen sind in das Brachland vorgestoßen. Das ist bekannt. Agenten der Horde kümmern sich dort um sie.$B$BAber so lange die Zentauren in Desolace so stark sind, werden sie immer eine Bedrohung sein. Es wird Zeit, dass wir uns diese Bedrohung vom Hals schaffen.$B$BSprecht mit Felgur Zweischneid beim Geistwandlerposten in Desolace. Seine Aufgabe war es, Informationen über die Zentauren zu sammeln. Er weiß bestimmt, wie man am besten mit den Kolkar von Desolace fertig wird.",
    ["O"] = "Sprecht mit Felgur Zweischneid beim Geistwandlerposten in Desolace.",
    ["T"] = "Die Kolkar von Desolace",
  },
  [1363] = {
    ["D"] = "Als Verwalter der Arbeit hier in der Bibliothek der Akademie der Arkanen Künste und Wissenschaften liegt es in meiner Verantwortung, dafür zu sorgen, dass alle magischen Folianten und Rollen stets erfasst sind. Wer weiß, was Schlimmes geschehen würde, fielen einige dieser verzauberten Texte in die falschen Hände?$B$BDabei fällt mir ein...mein Assistent Dellis machte letzte Woche einen schrecklichen Fehler. In der Bibliothek ist er recht tüchtig, aber inkompetent, wenn er die Tore von Sturmwind hinter sich lässt. Seid ein Held und helft dem alten Dellis, ja?",
    ["O"] = "Sprecht mit Akolyth Dellis in Sturmwind.",
    ["T"] = "Mazens Befehl",
  },
  [1364] = {
    ["D"] = "Meister Mazens Anweisungen waren ganz klar: Bringt einen sehr wichtigen Essay zu Behüter Mahar Ba in der Burg Nethergarde.$B$BAber ich bin noch nie in den Verwüsteten Landen gewesen. Die Reise war beängstigend!$B$BAls ich an die Sümpfe des Elends kam, bog ich falsch ab und war plötzlich von grässlichen pflanzenartigen Sumpfwesen umzingelt. Sie schienen mich angreifen zu wollen, also lief ich weg. Doch in meiner Hast ließ ich den Essay fallen. Ich bin sicher, eine der Bestien hat ihn genommen.$B$BKönntet Ihr bitte versuchen, ihn zu holen und zu Mahar Ba zu bringen?",
    ["O"] = "Rettet Khadgars \'Abhandlungen über dimensionale Konvergenz\' und bringt sie zu Behüter Mahar Ba in der Burg Nethergarde.",
    ["T"] = "Mazens Befehl",
  },
  [1365] = {
    ["D"] = "Die Kolkar sind nicht die einzigen Zentauren von Desolace. Viele Klans ziehen durch diese trockenen Ebenen, bekämpfen einander und sterben. Und es ist gut, dass sie sich untereinander bekämpfen, denn sie sind stark und brutal. Vereinten sich die Zentauren gegen uns, wären sie ein schrecklicher Gegner.$B$BWir müssen sie erledigen, bevor sie sich zusammentun.$B$BEuer erster Schritt ist, den Kolkarklan zu schwächen. Macht das, indem Ihr den Anführer Khan Dez\'hepah besiegt.$B$BDas Dorf Kolkar liegt nordöstlich von hier. Geht, und möge Euch das Glück hold sein.",
    ["O"] = "Bringt Khan Dez\'hepahs Kopf zu Felgur Zweischneid in Desolace.",
    ["T"] = "Khan Dez\'hepah",
  },
  [1366] = {
    ["D"] = "Da Ihr die Zentaurenklans jetzt eingeschüchtert habt, wird es Zeit, sie bluten zu lassen. Wenn Ihr durch Desolace reist, jagt Zentauren. Mir ist gleich, welchen Klan Ihr jagt. Sammelt nur Ohren. Bergeweise Ohren.$B$BWenn Ihr ihre Zahl dezimiert, schwächt Ihr ihre Entschlossenheit und macht alle Gedanken an Expansion zunichte.$B$BGute Jagd, $N.",
    ["O"] = "Bringt 15 Zentaurenohren zu Felgur Zweischneid in Desolace.",
    ["T"] = "Zentaurenkopfgeld",
  },
  [1368] = {
    ["D"] = "Um die Zentauren zu schlagen, müssen wir ihren Hass gegen sie wenden. Wir verbünden uns mit einem Klan und lernen durch ihn, wie wir alle Klans besiegen können.$B$BWenn Ihr Euch mit den Gelkis verbünden wollt, dann tötet ihre Gegner, die Magram. Wenn Ihr Euch den Respekt der Gelkis verdient habt, dann trefft Euch mit der Schamanin der Gelkis, Uthek der Weisen, am Rand des Dorfs der Gelkis im Südwesten von Desolace.",
    ["O"] = "Baut Euch einen freundlichen Ruf bei den Gelkis auf und sprecht dann mit Uthek der Weisen.",
    ["T"] = "Allianz mit den Gelkis",
  },
  [1370] = {
    ["D"] = "Wenn wir nicht im Krieg sind, jagen wir wegen Fleisch. Fleisch gibt uns Kraft und volle Bäuche kämpfen besser als leere.$B$BSo ist es bei den Magram. Sie lagern überschüssiges Fleisch ein, damit sie zwischen den Jagdausflügen länger kämpfen können. Wenn man ihr Dorf im Osten überfällt und ihre Fleischvorräte raubt, wird ihre Kraft schwinden.",
    ["O"] = "Bringt 6 Taschen primitiv getrocknetes Fleisch zu Uthek der Weisen im Dorf der Gelkis.",
    ["T"] = "Diebstahl von Vorräten",
  },
  [1371] = {
    ["D"] = "Vor langer Zeit kam ein kleiner Gnom nach Desolace. Er hatte Pferde und einen Karren, und auf dem Karren viele seltsame Dinge. Komische Dinge. Dinge, die in verschiedenen Farben brannten. Dinge, die sich bewegten und doch nicht lebten. Ich tötete den kleinen Gnom, aber vorher...brachte er mich zum Lachen.$B$BIch behielt mein liebstes Ding von dem Wagen. Es war ein Mensch aus Holz, der mir zuwinkte, bis ich ihn schlug und zerbrach.$B$BIch will einen Neuen. Besorgt mir einen Neuen!",
    ["O"] = "Wahrt Euren Ruf bei den Magram und bringt eine hochentwickelte Zielattrappe zu Warug im Dorf Magram in Desolace.",
    ["T"] = "Gizmo für Warug",
  },
  [1372] = {
    ["D"] = "Auf Befehl von Varimathras wurde unsere Expeditionsstreitmacht ausgeschickt, um das untere Azeroth zu vermessen.$B$BBeim Spähen bemerkte ich einen Spitzel der Menschen, der den Bettlerschlupfwinkel ausspionierte. Ehe ich ihn festnehmen konnte, schnappten ihn einige unserer...Orcbrüder...und brachten ihn nach Steinard, ihrer Festung in den Sümpfen des Elends.$B$BIch möchte unseren Orcverbündeten...helfen. Sie sahen unser Lager hier nicht, dennoch wollte ich sie unterstützen.$B$BIch arbeitete hier mit Faustin, um ein...Wahrheitsserum für unseren Freund in Steinard zu entwickeln.",
    ["O"] = "Sprecht mit Apotheker Faustin im Bettlerschlupfwinkel.",
    ["T"] = "Nichts als die Wahrheit",
  },
  [1373] = {
    ["D"] = "Ihr müsst noch mehr beweisen und Eure nächste Aufgabe führt Euch weit weg, daher werden die Gelkis die Magram vorerst ohne Euch bekämpfen.$B$BDie Erde flüstert jenen, die zuhören, viele Geheimnisse zu. Ein Geheimnis, von dem ich höre, betrifft einen roten Kristall. Er heißt Draenethyst und ist neu auf unserer Erde. Er stammt von einem Ort, wo Berge fliegen und Dämonen herrschen.$B$BBringt mir einen Splitter Draenethyst; er macht meine Magie stark! Der Splitter befindet sich an einem Ort namens Sümpfe des Elends und ist im Besitz von Ongeku, dem Verirrten.",
    ["O"] = "Wahrt Euren Ruf bei den Gelkis und bringt einen Draenethystsplitter zu Uthek der Weisen im Dorf der Gelkis in Desolace.",
    ["T"] = "Ongeku",
  },
  [1374] = {
    ["D"] = "Die Erde spricht zu mir und sagt, dass die Magram schwach werden. Jetzt ist es Zeit, ihren Khan zu töten!$B$BBringt mir den Kopf des Magramkahns Jehn!",
    ["O"] = "Bringt Khan Jehns Kopf zu Uthek der Weisen im Dorf der Gelkis in Desolace.",
    ["T"] = "Khan Jehn",
  },
  [1380] = {
    ["D"] = "$N, Ihr habt uns gegen die Magram geholfen. Jetzt helft uns dabei, die Maraudine zu schlagen! Tötet den Anführer ihres Klans, Khan Hratha!$B$BUm ihn herauszufordern, blast das Kriegshorn der Maraudine. Es befindet sich auf einem hohen Steilhang im Tal der Speere.$B$BDoch um das Horn zu blasen, müsst Ihr erst ein Kriegshornmundstück finden. Diese Teile sind selten und sind ausschließlich im Besitz der vertrauenswürdigsten Maraudinezentauren.$B$BNachdem das Horn geblasen wurde, werden Zentauren kommen. Tötet sie, bis Hratha auftaucht.$B$BKhan Hratha besitzt ein Stück eines... Schlüssels. Bringt es mir...",
    ["O"] = "Wahrt Euren Ruf bei den Gelkis und bringt das Schlüsselfragment der Maraudine zu Uthek der Weisen im Dorf der Gelkis.",
    ["T"] = "Khan Hratha",
  },
  [1381] = {
    ["D"] = "Der Khan der Gelkis ist tot, und jetzt werden die Maraudine fallen! Tötet Hratha, den Khan der Maraudine!$B$BUm ihn herauszufordern, blast das Horn auf dem hohen Steilhang im Tal der Speere. Doch um das Horn zu blasen, braucht Ihr erst ein Kriegshornmundstück. Um ein Mundstück zu finden, müsst Ihr Zentauren der Maraudine töten. Tötet sie so lange, bis Ihr ein Mundstück habt. Dann blast das Horn.$B$BTut dies, und Zentauren der Maraudine werden zum Turm kommen. Tötet sie, bis Khan Hratha auftaucht. Khan Hratha besitzt ein Stück eines Schlüssels. Gebt mir dieses Stück...",
    ["O"] = "Wahrt Euren Ruf bei den Magram und bringt das Schlüsselfragment der Maraudine zu Warug im Dorf Magram in Desolace.",
    ["T"] = "Khan Hratha",
  },
  [1382] = {
    ["D"] = "Wir können von Glück reden, dass die Zentauren sich untereinander bekämpfen, denn als vereinigte Kraft könnte sie nichts aufhalten. Ja, das kommt uns zugute und wir sollten unseren Vorteil ausbauen.$B$BEiner der Zentaurenklans, der Gelkisklan, führt einen brutalen Krieg gegen den Magramklan. Wenn wir die Magram angreifen, können wir vielleicht eine Allianz mit den Gelkis schmieden.$B$BSprecht mit Uthek der Weisen im Randbezirk der Gelkislager im Südwesten von Desolace. Durch sie könnten wir eine Allianz mit den Gelkis schmieden.",
    ["O"] = "Baut Euch einen freundlichen Ruf bei den Gelkis auf und sprecht dann mit Uthek der Weisen.",
    ["T"] = "Seltsame Allianz",
  },
  [1383] = {
    ["D"] = "Apotheker Faustin im Bettlerschlupfwinkel braucht 5 Schattenpantherherzen, Morastlordfungus und etwas Blut eines grünen Welpen.",
    ["O"] = "Apotheker Faustin im Bettlerschlupfwinkel braucht 5 Schattenpantherherzen, Morastlordfungus und etwas Blut eines grünen Welpen.",
    ["T"] = "Nichts als die Wahrheit",
  },
  [1384] = {
    ["D"] = "Die Kolkarzentauren glauben, ihre Magie sei stark. Die wissen nicht, was Magie ist! Nie und nimmer würden die richtige Magie überhaupt erkennen!$B$BIhr werdet ihnen zeigen, dass ihre Magie schwach ist. Geht zu den Kolkar im Nordosten und tötet sie. Nehmt ihre Glücksbringer. Bringt mir die Glücksbringer und ich werfe sie ins Feuer und lache!",
    ["O"] = "Wahrt Euren Ruf bei den Gelkis und bringt 10 kunstlose Glücksbringer zu Uthek der Weisen im Dorf der Gelkis in Desolace.",
    ["T"] = "Überfall auf die Kolkar",
  },
  [1385] = {
    ["D"] = "Desolace ist ein Land im Krieg. Die Zentauren führen Krieg untereinander und wir glauben, der Sieger wird ausrücken und unsere Festungen bedrohen. Das können wir nicht dulden.$B$BWir müssen lernen, wie man die Zentauren schlägt, und dazu... müssen wir uns mit einem der Klans verbünden. Die Zentauren der Magram führen Krieg mit den Gelkis. Wenn Ihr gegen die Gelkis kämpft, dann erwägen die Magram vielleicht eine Allianz.$B$BErringt die Gunst der Magram und sprecht dann mit Warug im Dorf Magram im Südosten von Desolace.",
    ["O"] = "Baut Euch einen freundlichen Ruf bei den Magram auf und sprecht dann mit Warug.",
    ["T"] = "Brutale Politik",
  },
  [1387] = {
    ["D"] = "Viele Klans bilden das Volk der Zentauren. Jeder hat seinen Anführer und eine Agenda, aber alle Klans haben eines gemeinsam: Grausamkeit.$B$BWenn wir je Frieden finden wollen, müssen die Zentauren entfernt werden. Und ich übertrage Euch die Aufgabe, genau das zu tun.$B$BWenn Ihr durch Desolace zieht, jagt Zentauren und sammelt ihre Ohren. Bringt mir viele, viele Ohren.$B$BMacht das im Dienst der Allianz, $N, und wir sind dem Ende des Krieges einen Schritt näher.",
    ["O"] = "Bringt 15 Zentaurenohren zu Korporal Melkins bei der Nijelspitze in Desolace.",
    ["T"] = "Zentaurenkopfgeld",
  },
  [1388] = {
    ["D"] = "Alles läuft genau nach Plan, $N. Ich möchte, dass Ihr dieses Wahrheitsserum dem Todespirscher Zraedus gebt. Er wird Euch zeigen, wie man es richtig... anwendet.$B$BHa-ha!",
    ["O"] = "Gebt Faustins Wahrheitsserum dem Todespirscher Zraedus im Bettlerschlupfwinkel.",
    ["T"] = "Nichts als die Wahrheit",
  },
  [1389] = {
    ["D"] = "Die Verirrten, die in der Zuflucht der Verirrten leben, sehen aus wie wir, aber sie sind Monster... die üblen Unbilden auf unserem Treck nach Azeroth haben sie wahnsinnig gemacht. Einst waren sie unsere Brüder, doch nun leben sie in der Zuflucht der Verirrten im Osten und fangen oder töten alle, die sich in die Nähe wagen.$B$BUnd sie haben uns etwas Heiliges gestohlen - Draenethystkristalle.$B$BUns fehlt die Kraft, um die Zuflucht der Verirrten zu betreten und unseren gestohlenen Draenethyst von ihren Hütten und Lagerfeuern zu holen, aber ohne diese Kristalle... könnten auch wir vom rechten Weg abkommen.",
    ["O"] = "Bringt 6 Draenethystkristalle zu Magtoor im Sicheren Hafen in den Sümpfen des Elends.",
    ["T"] = "Draenethystkristalle",
  },
  [1390] = {
    ["D"] = "Missing text",
    ["O"] = "Missing text",
    ["T"] = "&lt;nyi&gt; Upps, wir haben sie schon wieder ins Jenseits geschickt.",
  },
  [1391] = {
    ["D"] = "Wir müssen mit einer gewissen Diskretion vorgehen. Immerhin werdet Ihr nicht da reinspazieren und den Narren überzeugen, Faustins Gebräu zu trinken, richtig?$B$BAber wenn es eines gibt, zu dem die Menschen nicht nein sagen können, dann ist es Fusel. Ich nehme Faustins Serum und mische es in dieses Bier. Der arme Kerl wird garantiert keine Hemmungen haben, wenn es darum geht, ein Gebräu zu kippen.$B$BGeht hastig nach Steinard in den Sümpfen des Elends, $N. Und schweigt schön stille über unsere kleine Mission...",
    ["O"] = "Bringt Zraedus\' Bräu zu Spitzel Marksen in Steinard.",
    ["T"] = "Nichts als die Wahrheit",
  },
  [1392] = {
    ["D"] = "Noboru plagt die Draenei des Sicheren Hafens seit Monaten; sein Knüppel ist ein Symbol der Brutalität, die sie sich von den Verirrten gefallen lassen mussten.",
    ["O"] = "Bringt Noborus Knüppel zum Anführer des Sicheren Hafens in den Sümpfen des Elends.",
    ["T"] = "Noboru der Knüppler",
  },
  [1393] = {
    ["D"] = "Bitte helft! Diese Kreaturen werden mich fressen!$B$BHelft mir hier raus und ich gebe Euch einen großen Schatz! Er ist in meinem Lager im Sumpf im Norden von Steinard, aber in einer Geldkassette eingeschlossen, die man mit einem Trick öffnet, den nur ich beherrsche.$B$BWenn Ihr mir zur Flucht verhelft, zeige ich ihn Euch.",
    ["O"] = "Eskortiert Galen, bis er außer Gefahr ist, sucht dann sein Lager in den Sümpfen des Elends und öffnet seine Geldkassette.",
    ["T"] = "Galens Flucht",
  },
  [1394] = {
    ["D"] = "Ihr habt Eure Sache gut gemacht, $N. Ihr habt meinen Test und die Tests vor meinem bestanden. Kehrt zu Dorn in Tausend Nadeln zurück. Er wird die Veränderung in Euch spüren und sehen, dass Euer Verstand, Körper und Geist stark genug sind, dass Ihr für Eure Bemühungen belohnt werden solltet.$B$BBleibt dem Gleichgewicht treu, das Ihr während all dieser Prüfungen gezeigt habt, $N. Wenn Ihr das tut, wird Dorn Euch irgendwann noch einmal prüfen.",
    ["O"] = "Sprecht mit Dorn Ebenenpirscher in Tausend Nadeln.",
    ["T"] = "Letzte Überfahrt",
  },
  [1395] = {
    ["D"] = "Diese Kiste mit Kraftsteinen muss in die Burg Nethergarde in den Verwüsteten Landen. Sie werden ihre Macht nicht lange halten, daher können wir sie nicht mit der nächsten Karawane aufgeben.$B$BIhr müsst sie nehmen, und zwar schnell.$B$BBringt sie zu Rüstmeister Lungertz in der Burg Nethergarde. Um die Burg zu erreichen, müsst Ihr nach Osten über den Gebirgspass der Totenwinde, dann nach Osten durch die Sümpfe des Elends und dann nach Süden durch die Verwüsteten Lande.$B$BSeid vorsichtig, $N. Ihr müsst durch gefährliche Länder und dürft doch nicht verzagen.",
    ["O"] = "Bringt die Kiste mit den Kraftsteinen zu Rüstmeister Lungertz, bevor die Kraft verbraucht ist.",
    ["T"] = "Vorräte für Nethergarde",
  },
  [1398] = {
    ["D"] = "Ich stehe tief in Eurer Schuld, $N. Ein ehrenhaftes Herz schlägt in Eurer Brust. Und jetzt habe ich eine andere Aufgabe, für die ich demütig um Eure Mithilfe bitte.$B$BWenn ich Nethergarde erreichen soll, dann muss ich diesen Wagen reparieren. Und dafür...brauche ich Holz. Starkes, trockenes Holz - nicht das schimmelige, krumme Holz der Sumpfbäume.$B$B$N, geht nach Osten zur Küste und sucht nach in der Sonne getrocknetem Treibholz. Bringt es mir.",
    ["O"] = "Bringt 8 Stücke sonnengetrocknetes Treibholz zu Behüter Biggs in den Sümpfen des Elends.",
    ["T"] = "Treibholz",
  },
  [1418] = {
    ["D"] = "Das Ödland ist unwirtlich und liegt nahe am Land des Loch Modan, das von der Allianz beherrscht wird, daher wird die Kommunikation zwischen Steinard und den Ödland-Außenposten von Kargath oft unterbrochen.$B$BBeweist Euch, $N. Reist ins Ödland und sprecht mit Neeka Blutnarbe, der obersten Späherin des Kargath-Außenpostens. Sprecht mit ihr und bringt mir ihren Bericht.",
    ["O"] = "Sprecht mit Neeka Blutnarbe.",
    ["T"] = "Neeka Blutnarbe",
  },
  [1419] = {
    ["D"] = "Habt Ihr viele Tage im Ödland verbracht, $C? Habt Ihr sein Herz gespürt? Habt Ihr den Bestien getrotzt, die durch die staubigen Ebenen schleichen? Dann wird es aber höchste Zeit.$B$BKlippenkojoten sind zähe Burschen. Zäh und tollkühn genug, dass sie sich nach Kargath schleichen und unsere Essensvorräte stehlen. Helft mit, den Außenposten von diesen Dieben zu befreien!$B$BJagt Klippenkojoten, $N. Jagt sie und bringt mir ihre Kieferknochen. Bringt mir genügend und ich glaube Euch vielleicht, wenn Ihr sagt, dass Ihr das Ödland kennt.",
    ["O"] = "Bringt 30 Kojotenkieferknochen zu Neeka Blutnarbe in Kargath.",
    ["T"] = "Kojoten-Diebe",
  },
  [1420] = {
    ["D"] = "Ich schrieb einen neuen Bericht für Helgrum in Steinard. Bringt ihn zu ihm.$B$BUnd wenn Ihr Euch als Kundschafter betrachtet, kommt nicht vom Weg ab. Oder fallt einem Feind in die Hände.",
    ["O"] = "Bringt Neekas Bericht zu Helgrum dem Hurtigen in Steinard.",
    ["T"] = "Bericht für Helgrum",
  },
  [1421] = {
    ["D"] = "Ich wurde während des Angriffs der Verirrten betäubt, aber bevor mir schwarz vor Augen wurde, sah ich einen unserer Wagen, der zur Zuflucht der Verirrten gezerrt wurde, das Dorf der Verirrten im Norden.$B$BEine Karawanentruhe mit wertvollen Reagenzien war in diesem Wagen, Reagenzien, die unabdingbar für die Studien der Hexer der Burg Nethergarde sind. Sucht die Truhe und holt diese Reagenzien, $N. Holt sie und bringt sie mir.",
    ["O"] = "Sucht die Karawanentruhe in der Zuflucht der Verirrten, beschafft Euch die Reagenzien der Hexer und bringt sie zu Behüter Biggs.",
    ["T"] = "Die verlorene Karawane",
  },
  [1424] = {
    ["D"] = "Der Kriegshäuptling schickt uns, die seltsamen Vorkommnisse im Tempel von Atal\'Hakkar zu untersuchen.$B$BDie Trolle des Stammes der Atal\'ai sind ein Stamm dunkler Zauberkundiger, die dafür bekannt sind, verbotene Magie auszuüben. Ihr könnt Euch vorstellen, wie erstaunt wir waren, als wir hier eintrafen und feststellten, dass der Tempel versunken war.$B$BDie grünen Großdrachen hüten nun die Stelle und spielen sich als Beschützer von Azeroth auf.$B$BWir müssen herausfinden, was geschehen ist. Forscht rund um den Tränenteich und sammelt Artefakte der Atal\'ai. Damit kann ich dieses Rätsel lösen.",
    ["O"] = "Fel\'Zerul in Steinard möchte, dass Ihr 5 Artefakte der Atal\'ai sammelt.",
    ["T"] = "Tränenteich",
  },
  [1425] = {
    ["D"] = "Während ich diesen Wagen repariere, $N, habe ich eine letzte Aufgabe für Euch.$B$BIch habe die wichtigsten Vorräte eingepackt, die ich vor den Angriffen der Oger retten konnte. Bringt sie zur Burg Nethergarde. Die Burg schmiegt sich an die nördlichen Hügel der Verwüsteten Lande und wacht über das Dunkle Portal.$B$BFolgt der Straße nach Osten, um Nethergarde zu erreichen. Geht an der Gabelung nach Süden in die Verwüsteten Lande und bei der nächsten Gabelung nach links. Folgt der Straße, $N, und seid wachsam.",
    ["O"] = "Bringt die Ladung für Nethergarde zu Rüstmeister Lungertz in der Burg Nethergarde.",
    ["T"] = "Überbringen der Lieferung",
  },
  [1426] = {
    ["D"] = "Ich mag weder das Meer noch die Kreaturen, die ich aus seiner Tiefe kommen sah. Es ist eine mächtige, aber blinde Herrin; launisch und brutal. Es wäre mir ein Vergnügen, die Fischmenschen zu Hunderten zu töten, wenn es in meiner Macht stünde. Eine kleine Freude ist es mir wenigstens, sie durch die Hände all derer sterben zu sehen, die den Willen der Horde erfüllen.$B$BUnd damit sind wir bei Euch, $C. Wie viele könnt Ihr besiegen? Würdet Ihr Euch mit dem Mächtigsten ihrer Art messen?",
    ["O"] = "Tötet 10 Marschenmurlocs, 10 Marschentintenspucker und 10 Marschenfleischfresser und kehrt dann zu Katar in den Sümpfen des Elends zurück.",
    ["T"] = "Gefahr aus dem Meer",
  },
  [1427] = {
    ["D"] = "Sprecht wieder mit Tok\'Kar, bevor Ihr Euch ausruht. Während Ihr fort wart, hat er, glaube ich, ein paar Waffen vollendet. Für Eure Hilfe würde er Euch eine davon anbieten. Ihr tätet gut daran, ihre Kraft an den Fischmenschen zu erproben, wenn Ihr bereit seid...aber nur, um mich weiterhin zu erfreuen.",
    ["O"] = "Sprecht mit Tok\'Kar in den Sümpfen des Elends.",
    ["T"] = "Gefahr aus dem Meer",
  },
  [1429] = {
    ["D"] = "Während Ihr den Gefahren des Tränenteichs getrotzt habt, kehrte einer meiner Späher mit einer in Blut und auf getrocknetem Fleisch geschriebenen Rolle zurück. Es scheint sich um ein Urteil gegen einen der Priester des Stammes der Atal\'ai zu handeln.$B$BDer Priester wurde zu etwas Schlimmerem als dem Tod bei den Atal\'ai verurteilt.$B$BEr wurde ins Hinterland zu den Trollen der Bleichborken verbannt.$B$BIch bündle diese Artefakte. Ihr müsst den Verbannten suchen und ihren wahren Verwendungszweck herausfinden. Kommt wieder zu mir, wenn Eure Reise beendet ist.",
    ["O"] = "Bringt das Artefaktbündel der Atal\'ai zu dem Verbannten der Atal\'ai ins Hinterland.",
    ["T"] = "Der Verbannte der Atal\'ai",
  },
  [1431] = {
    ["D"] = "Seid still, $N, und lasst mich Euch etwas Wichtiges über unser Volk sagen, das nicht auf der Hand liegt.$B$BWir sind ein stolzes Volk und der Krieg gegen die Menschen hat selbst Thralls große Geduld auf die Probe gestellt. Wisst Ihr, unser Kriegshäuptling ist weise und umsichtig und sein Volk kommt stets an erster Stelle. Letztendlich ist er ein Diplomat und hat in seiner Zeit bei den Menschen viel von ihrer Art gelernt.$B$BWenn die Geschichte, die ich begonnen habe, Euer Interesse weckt, dann geht zu Keldran beim westlichen Ausgang der Stadt. Dort steht seine Hütte.",
    ["O"] = "Sprecht mit Keldran in Orgrimmar.",
    ["T"] = "Allianzbeziehungen",
  },
  [1432] = {
    ["D"] = "Wenn die Allianz immer tiefer nach Kalimdor vorstößt, könnten falsche Schlussfolgerungen über mögliche Bündnisse der Horde mit der Brennenden Legion gezogen werden. Dazu darf es der Kriegshäuptling nicht kommen lassen. Und mit seinem Segen übernehme ich die Aufsicht darüber, dass alle Missverständnisse ausgeräumt werden.$B$BIch habe Agenten in der Gegend von Desolace am Geistwandlerposten nördlich des großen Kodofriedhofs. Wenn Ihr vom Verbrannten Tal im Steinkrallengebirge aus südwärts geht, solltet Ihr dort vorbeikommen. Wenn Ihr eintrefft, sucht Takata Stahlklinge auf.",
    ["O"] = "Sprecht mit Takata Stahlklinge in Desolace.",
    ["T"] = "Allianzbeziehungen",
  },
  [1433] = {
    ["D"] = "Die Brennende Klinge verfolgt eigene Ziele; doch wisst, dass sie bis ins Mark böse sind und ihr Geist verderbt wurde. Sie zu vernichten ist die einzige Möglichkeit, ihnen Einhalt zu gebieten.$B$BKeldran schickte Maurin mit mir, weil er die Klinge und die Arglist ihrer Hexenmeister kennt.$B$BEr leitet unsere Verschwörungen gegen die Klinge, während ich mich um die anderen dämonischen Bedrohungen in Desolace kümmere.$B$BSprecht mit ihm und er wird Euch in seine Pläne einweihen.",
    ["O"] = "Sprecht mit Maurin Knochenspalter in Desolace.",
    ["T"] = "Allianzbeziehungen",
  },
  [1434] = {
    ["D"] = "Eine Dämonenplage geschieht nicht ohne andere Einflüsse - etwas anderes ermöglicht dem Bösen, Fuß zu fassen.$B$BEs mag mit einem schwachen Geist beginnen, einer unnötigen Gier nach Macht, möglicherweise Rachedurst oder der Verlockung nach höchster Gerechtigkeit. Wenn der Schmerz uns übermannt, dann nutzt uns das Böse aus - das Böse wie die Satyrn.$B$BBande wurden zwischen ihnen und der Brennenden Klinge geknüpft und Thrall hätte sie getötet. Sucht sie nordöstlich von hier und kommt zu mir zurück, wenn Ihr sie zurückgeschlagen habt.",
    ["O"] = "Besiegt 7 Schurken der Zornsäer, 7 Teufelsanbeter der Zornsäer, 7 Verräter der Zornsäer sowie 7 Höllenrufer der Zornsäer und kehrt zu Takata Stahlklinge in Desolace zurück.",
    ["T"] = "Beschmutzt durch Satyrn",
  },
  [1435] = {
    ["D"] = "Für diese abscheulichen Kreaturen, die ihrem Lehen aus so egoistischen Gründen den Rücken kehren, ist der Tod höchst wünschenswert. Doch wenn man es mit denen zu tun hat, die sich der Dunkelheit verschrieben haben, reicht der Tod allein nicht aus.$B$BNehmt diesen Edelstein. Wenn einer der Brennenden Klinge dem Tode nahe ist, aktiviert seine Magie. Der Zauber wird den verderbten Geist aus seinem Opfer herausziehen und einen damit erfüllten Edelstein hinterlassen.$B$BBringt mir diese erfüllten Edelsteine, damit ich sie angemessen entsorgen kann.$B$BGeht jetzt, und möge Euch der Tod meiden.",
    ["O"] = "Bringt den brennenden Edelstein und 15 erfüllte brennende Edelsteine nach Desolace zu Maurin Knochenspalter.",
    ["T"] = "Brennender Geist",
  },
  [1436] = {
    ["D"] = "Der Kampf läuft gut, $N. Vielleicht lässt uns der Kriegshäuptling, bevor die Jahreszeiten wechseln, in unsere Heimat zurückkehren, wenn der Kampf gegen die Dämonen und ihre Unterlinge weiterhin so gute Fortschritte macht.$B$BKehrt zu Keldran zurück und lasst ihn wissen, was wir getan haben. Ich bin sicher, er belohnt Euch für Eure Taten hier. Seine Sache ist ihm wichtig und es wird ihn mit Stolz erfüllen, wenn er weiß, dass der Kriegshäuptling seine Sorgen respektiert.",
    ["O"] = "Sprecht mit Keldran in Orgrimmar.",
    ["T"] = "Allianzbeziehungen",
  },
  [1437] = {
    ["D"] = "Ich bin Vahlarriel, $N. Ich kam her, um mich nach dem Befinden zweier vermisster Adliger einer Kaufmannsfamilie aus Darnassus zu erkundigen - Haus Malem. Der Vater des Hauses fürchtet das Schlimmste. Ich soll tun, was ich kann, um seine vermissten Kinder zu finden. Ich habe noch nicht mit der Suche nach Tyranis und Dalinda begonnen, hoffe aber, ich werde mich nicht lange hier aufhalten.$B$BZuletzt hörte man, dass sie vor wenigen Wochen durch das Steinkrallengebirge gekommen sein sollen.$B$BFalls Ihr ihren Wagen findet, sucht mich bitte unverzüglich auf.",
    ["O"] = "Sucht und findet den Wagen von Tyranis und Dalinda Malem in Desolace.",
    ["T"] = "Vahlarriels Suche",
  },
  [1438] = {
    ["D"] = "Sucht Dalinda und Tyranis, wenn Ihr könnt, und berichtet mir von Euren Funden. Beginnt Eure Suche in dieser Festung. Ich setze mich sofort mit meinen Vorgesetzten in Darnassus in Verbindung und informiere sie über die Situation.$B$B$N, ich möchte Euch noch etwas wissen lassen. Meine Vorgesetzten haben Besorgnis wegen Tyranis und seinen Motiven für diese Reise geäußert. Es ist ihnen wichtiger, dass Dalinda gefunden und wohlbehalten zurückgebracht wird. Tyranis soll... überprüft werden, ehe man ihn nach Hause bringt.",
    ["O"] = "Sucht Dalinda Malem in Desolace.",
    ["T"] = "Vahlarriels Suche",
  },
  [1439] = {
    ["D"] = "Wir wurden hierher gebracht, nachdem die Orcs alle unsere Wachen und Diener abgeschlachtet hatten. Oh, was haben sie ihnen Schreckliches angetan! Ich dachte, sie würden uns auf der Stelle töten, doch dann wurde deutlich, dass sie andere Pläne hatten.$B$BTyranis versuchte, mit ihnen zu reden. Ich glaube, er wollte sie mit seinem arkanen Wissen beeindrucken...Ich bin nicht sicher, warum.$B$BBitte, ich kann hier nicht weg, bevor ich weiß, was meinem Bruder zugestoßen ist. Sucht ihn und kommt zu mir, wenn Ihr sein Schicksal kennt.",
    ["O"] = "Sucht Tyranis Malem in Desolace.",
    ["T"] = "Suche nach Tyranis",
  },
  [1440] = {
    ["D"] = "Also ist die Zeit gekommen? Ich weiß Eure Bemühungen zu schätzen, und wenn auch die Situation dafür nicht geeignet ist, lasst mich Euch jetzt für alles danken, was Ihr für mich und meine Familie getan habt. Wenn Ihr bereit seid, hier hält mich nichts mehr - wir können aufbrechen, falls es Euch passend erscheint.",
    ["O"] = "Begleitet Dalinda Malem aus der Festung Donneraxt und kehrt zu Vahlarriel Dämonenjäger nach Desolace zurück.",
    ["T"] = "Rückkehr zu Vahlarriel",
  },
  [1441] = "_",
  [1442] = {
    ["D"] = "Seid gegrüßt, $N. Was kann ich für einen tun, der dem heiligen Weg folgt?$B$BAh ja, Stillbrunn, der Schmied... das war vor langer Zeit. Ich erinnere mich gut an ihn, doch ich habe seinen Namen schon eine ganze Weile nicht mehr gehört.$B$BEr schien mir nie ein habgieriger Mann zu sein, daher kann ich nur davon ausgehen, dass sein Wunsch nach einem Koredelstein edle Motive hat.$B$BWenn Ihr wirklich so einen Edelstein möchtet, dann müsst Ihr in den Nordwesten des Eschentals. Dort findet Ihr die Tiefschwarze Grotte mit einigen alten Ruinen. Die Naga, die diese Edelsteine noch immer benutzen, sind dort unten. Geht nicht allein, $C.",
    ["O"] = "Bringt einen Koredelstein zu Thundris Windwirker in Dunkelküste.",
    ["T"] = "Suche nach dem Koredelstein",
  },
  [1444] = {
    ["D"] = "Jammal\'an der Prophet verbannte mich aus dem Tempel von Atal\'Hakkar. Einst empfand ich den Hohepriester als würdigen Anführer.$B$BSein unerschütterlicher Glaube an seine Prophezeiung wird den Untergang der Atal\'ai herbeiführen. Wenn es nicht schon geschehen ist.$B$BAls ich meiner Besorgnis Ausdruck verlieh, hieß man mich einen Ungläubigen und verstieß mich aus dem Tempel.$B$BDie Artefakte, die Ihr zu mir brachtet, sind harmlos. Aber wenn Jammal\'an die richtige Fetischverzauberung für das Beschwörungsritual gefunden hat, droht der Welt große Gefahr...",
    ["O"] = "Kehrt zu Fel\'Zerul in Steinard zurück.",
    ["T"] = "Rückkehr zu Fel\'Zerul",
  },
  [1445] = {
    ["D"] = "Also sprach er von Jammal\'ans Prophezeiung?$B$BDer spirituelle Anführer der Atal\'ai glaubt, wenn Hakkar aus dem Nether nach Azeroth zurückkehrt, wird der Gott dem Stamm der Atal\'ai Unsterblichkeit gewähren.$B$BTörichte Arglist, um den Jüngsten Tag schneller herbeizuführen, wenn Ihr mich fragt.$B$BDoch Ihr sagt, der Verbannte sprach von verzauberten Fetischen? Das erfüllt mich mit großer Sorge. Wenn sie wahrhaftig der Schlüssel zu dem Ritual der Atal\'ai sind, das dieses Durcheinander verursachte, müssen wir die Mächte verstehen, die in ihnen wohnen.$B$BBegebt Euch in den Tempel und werdet dieser Fetische habhaft!",
    ["O"] = "Sammelt 20 Fetische von Hakkar und bringt sie zu Fel\'Zerul in Steinard.",
    ["T"] = "Der Tempel von Atal\'Hakkar",
  },
  [1446] = {
    ["D"] = "Meine Bleichborkenbegleiter mögen keine Fremden in ihrer Nähe.$B$BIch? Ich habe keine Heimat mehr. Mein Volk, der große Stamm der Atal\'ai aus den Sümpfen des Elends, hat mich verbannt.$B$BJammal\'an, unser spiritueller Anführer, hatte eine, wie wir uns ausdrücken, Prophezeiung. Er glaubt, die Beschwörung des Gottes Hakkar bringt den Atal\'ai Unsterblichkeit.$B$BIch jedoch drängte auf Vorsicht. Wenn die Prophezeiung nun nichts weiter als Manipulation war?$B$BIch will Rache für meine Verbannung. Bringt mir Jammal\'ans Kopf. Vielleicht ist mein Volk dann frei.",
    ["O"] = "Der Verbannte der Atal\'ai im Hinterland möchte den Kopf von Jammal\'an.",
    ["T"] = "Jammal\'an der Prophet",
  },
  [1447] = {
    ["D"] = "Hm, Ihr seid offenkundig nicht $Geiner:eine; der Klügsten Eures Handwerks, $Gder:die; je nach Sturmwind kam, aber $Geiner:eine; der Dümmsten, wenn Ihr nicht wieder fortgeht.$B$BHabt Ihr meine Freunde getroffen?",
    ["O"] = "Besiegt Dashel Steinfaust in Sturmwind.",
    ["T"] = "Der vermisste Diplomat",
  },
  [1448] = {
    ["D"] = "Es gab eine Zeit, da war ich jung und voller Kraft, wie Ihr, $C.$B$BDoch jetzt bin ich alt und vermag die Welt nicht mehr zu erforschen wie einst. Ach, was habe ich nicht alles gesehen!$B$BAber ein Geheimnis entzog sich mir stets. Als meinen letzten Auftrag schickte mich die Forscherliga auf die Suche nach dem Tempel von Atal\'Hakkar. Er sollte Gerüchten zufolge in den Sümpfen des Elends liegen.$B$BMeines gebrechlichen Zustands wegen reiste ich auf einem Greif durch die Lüfte, fand den Tempel aber nie.$B$BHelft Ihr einem alten Zwerg? Vielleicht habt Ihr zu Fuß mehr Glück.",
    ["O"] = "Brohann Fassbauch in Sturmwind möchte, dass Ihr den Tempel von Atal\'Hakkar sucht.",
    ["T"] = "Auf der Suche nach dem Tempel",
  },
  [1449] = {
    ["D"] = "Wie ich schon früher sagte, versuchte ich selbst, den Tempel aus der Luft zu finden. Ganz allein war ich freilich nicht.$B$BIch reiste zu dem berühmten Greifenhorst beim Nistgipfel im Hinterland. Dort versicherte ich mich der Hilfe eines ihrer besten Piloten. Der Pilot fiel von seinem Reittier in den Sumpf...$B$BDen Namen des armen Burschen habe ich im Alter vergessen. Der Meister des Horsts kennt ihn zweifellos noch.$B$BBegebt Euch dorthin und sucht nach Falstad Wildhammer.$B$BWir müssen herausfinden, was dem Piloten zugestoßen ist.",
    ["O"] = "Sprecht mit dem obersten Than, Falstad Wildhammer, am Nistgipfel im Hinterland.",
    ["T"] = "Ins Hinterland",
  },
  [1450] = {
    ["D"] = "Ihr seid also nur ein $Gbesserer Botenjunge:besseres Botenmädchen; für einen alten Tattergreis der Forscherliga, hm?$B$BHört mir gut zu, $C. Ich habe keine Ahnung, mit wem Euer alter Kumpel da unten in Azeroth rumgeflogen ist. Seh ich etwa wie der Greifenmeister Krallenaxt aus? Ja?$B$BWenn Ihr Euch für die Flugpläne interessiert, warum fragt Ihr dann nicht den Greifenmeister? Wenn Ihr noch länger untätig hier herumhängt, werde ich Euch richtige Arbeit geben.$B$BUnd jetzt trollt Euch.",
    ["O"] = "Sprecht mit Greifenmeister Krallenaxt im Horst beim Nistgipfel.",
    ["T"] = "Greifenmeister Krallenaxt",
  },
  [1451] = {
    ["D"] = "Rhapsodie fand Gefallen am Schnaps. Versteht mich nicht falsch - alle Zwerge hier im Horst wissen ein gutes Bier zur passenden Stunde zu schätzen.$B$BAber der alte Kniegräber brauchte den Fusel wie ein Greif den Himmel. Und das war echt ein Jammer. Niemand konnte so fliegen wie Rhapsodie.$B$BDie ersten Probleme gab es, als er mit Eurem Freund Brohann von dieser Mission zurückkehrte. Danach ging es rapide bergab. Musste ihn rauswerfen.$B$BZuletzt hörte ich, er habe östlich von hier eine Brauerei eröffnet...",
    ["O"] = "Sucht Rhapsodie Kniegräber im Hinterland.",
    ["T"] = "Rhapsodie Kniegräber",
  },
  [1452] = {
    ["D"] = "Hicks!$B$BDieser irre Narr Brohann schickt Euch also? Wir haben ein tolles Abenteuer zusammen erlebt... Hicks!$B$BWenn ich Euch erzählen soll, was da unten im Sumpf passiert ist, brauch ich was Ordentliches zu trinken. Das war echt kein schöner Anblick. Hicks!$B$BIch sag Euch was... bringt mir, was ich brauche, um einen Kalimdor-Cocktail zu mixen, dann erzähl ich Euch davon.$B$BMan braucht dazu den Muskelmagen eines Roc aus Tanaris... etwas Groddoc- und Eisenfellleber aus Feralas...$B$BEin schöner kräftiger Kalimdor-Cocktail... dann kann ich plaudern!",
    ["O"] = "Rhapsodie Kniegräber im Hinterland möchte, dass Ihr ihm 3 Muskelmägen eines Roc, 3 Groddoclebern und 3 Eisenfelllebern bringt.",
    ["T"] = "Rhapsodies Kalimdor-Cocktail",
  },
  [1453] = {
    ["D"] = "Es gibt Arbeit in Desolace, $N. Das ist ein grässlicher Ort, beschmutzt von Zentauren und anderer Unbill. Aber wenn Ihr eine Woche ehrlicher Arbeit sucht, dann geht dorthin, besucht Kreldig Ungor und sagt ihm, ich hab Euch geschickt.$B$BIhr findet ihn an der Nijelspitze - dem gemütlichen kleinen Lager in den Bergen des nördlichen Desolace. Haltet Euch einfach ostwärts, wenn Ihr vom Steinkrallengebirge kommt. Man übersieht es leicht, also seid wachsam.",
    ["O"] = "Sprecht mit Kreldig Ungor in Desolace.",
    ["T"] = "Die Geschäfte der Wiederholer in Desolace",
  },
  [1454] = {
    ["D"] = "Seid gegrüßt, $Gmein Freund:meine Freundin;. Nehmt doch Platz...$B$BMein erster Auftrag betrifft den Magier Karnitol - eine Art Edelmann in Sturmwind. Offenbar wurde sein Schiff vor der nordwestlichen Küste von Desolace von Meeresriesen angegriffen. Er und seine Gefolgsleute versuchten, eine seiner wertvolleren Truhen an Land zu bringen, aber als die Meeresriesen in Schwärmen angriffen, beschloss er, sie aufzugeben und sich und seine Gefolgsleute in Sicherheit zu teleportieren.$B$BEr möchte, dass wir die Truhe samt ihrem Inhalt finden und zu ihm nach Sturmwind bringen.",
    ["O"] = "Sucht Karnitols Truhe in Desolace.",
    ["T"] = "Karnitols Schiffbruch",
  },
  [1457] = {
    ["D"] = "Bringt Karnitols Sachen zu Roetten in Eisenschmiede, er wird Euch die gute Arbeit wohl vergelten.$B$BHaha, wenn Ihr nicht aufpasst, wird er so zufrieden sein, dass er Euch bittet, als Festangestellter bei uns zu bleiben.$B$BWieder einmal gut gemacht, $N.$B$BSeid auf dem Rückweg vorsichtig und lasst Euch nicht zu viel Zeit - Karnitol ist nicht der Geduldigste, aber dennoch höchst großzügig.",
    ["O"] = "Bringt Karnitols Ranzen zu Roetten Steinhammer in Eisenschmiede.",
    ["T"] = "Karnitols Schiffbruch",
  },
  [1458] = {
    ["D"] = "Zu meinen Pflichten hier gehört, Reagenzien für Linkstatsch zu beschaffen, einen Gnomenmagier in Kharanos. Roetten glaubt, das sei ein Kinderspiel, da wir ohnehin schon hier draußen sind. Ihm ist es gleich, dass wir auch so schon unterbesetzt sind.$B$BDie ersten paar Gegenstände auf seiner Liste sollten leicht zu beschaffen sein, und für alle, die mir helfen, springt eine satte Belohnung raus.$B$BIch brauche einige Hörner und Klauen von den Satyrn der Zornsäer östlich von hier. Bringt sie mir, dann nehmen wir uns den Rest der Liste vor.",
    ["O"] = "Bringt 10 Zornsäerklauen und 10 Zornsäerhörner zu Kreldig Ungor in Desolace.",
    ["T"] = "Reagenzien für die Wiederholer AG",
  },
  [1459] = {
    ["D"] = "Als Nächstes steht auf Linkstatschs Liste... mal sehen... hmm. Oh, da haben wir es.$B$BRecht einfach, aber mit sehr expliziten Anweisungen. Er braucht Gift von den Skorpashi und Bälge von Kodos. Im Kleingedruckten steht, beide müssen von bestimmten Kreaturen sein: Das Gift soll von irgendeinem Skorpashi stammen und der Balg muss von einem gealterten Kodo sein, aber keinem sterbenden oder uralten.$B$BMagier, ich sage Euch, was die so alles wollen... abartig.",
    ["O"] = "Bringt 7 Phiolen Skorpashigift und 3 Bälge von gealterten Kodos zu Kreldig Ungor in Desolace.",
    ["T"] = "Reagenzien für die Wiederholer AG",
  },
  [1460] = "_",
  [1461] = "_",
  [1462] = {
    ["D"] = "",
    ["O"] = "Sprecht mit Seherin Rabenfeder wegen eines weiteren Erdsaptas.",
    ["T"] = "Erdsapta",
  },
  [1463] = {
    ["D"] = "",
    ["O"] = "Sprecht mit Canaga Erdenrufer wegen eines weiteren Erdsaptas.",
    ["T"] = "Erdsapta",
  },
  [1465] = {
    ["D"] = "Als Ihr Eure Suche nach dem Wagen fortsetzt, findet Ihr nicht nur einen Anhänger, sondern auch Fußspuren, die in Richtung Süden führen. In der Ferne seht Ihr Rauch von einem großen Fort aufsteigen. Der Rauch stammt von einem Lagerfeuer, nicht von brennenden Gebäuden. Euer Instinkt sagt Euch, dass die Malems, wenn sie noch leben, in diese Richtung gebracht wurden.$B$BIhr seid der Ansicht, dass Vahlarriel an der Nijelspitze nicht nur den Anhänger sehen sollte, den Ihr gefunden habt, sondern auch von den Lebenszeichen im Süden wissen sollte.",
    ["O"] = "Kehrt mit Malems Anhänger zu Vahlarriel Dämonenjäger in Desolace zurück.",
    ["T"] = "Vahlarriels Suche",
  },
  [1466] = {
    ["D"] = "Das ist der letzte Gegenstand und ganz ehrlich, er ist nicht schön. Ich gehe auch davon aus, dass Ihr dafür etwas länger braucht als für die anderen Gegenstände.$B$BDoch genug der Warnungen, fangen wir an. Linkstatsch sagt, er braucht Folgendes: zehn Gehirne, entweder von Manafressern, Magierjägern oder Leyjägern, zehn Netherflügel von einer Nethermaid, -schwester oder -zauberin. Und... zehn Phiolen Verdammniswächterblut von einem Hauptmann oder Lord der Verdammniswächter.$B$BGanz einfach, hm? Tja... ich hätte Verständnis dafür, wenn Ihr dies ausschlagen wollt.",
    ["O"] = "Bringt 10 Teufelshundgehirne, 10 Netherflügel und 10 Phiolen Verdammniswächterblut zu Kreldig Ungor in Desolace.",
    ["T"] = "Reagenzien für die Wiederholer AG",
  },
  [1467] = {
    ["D"] = "Wenn Ihr Eure Bezahlung wollt, müsst Ihr nach Eisenschmiede zurück und persönlich mit Roetten sprechen. Er dürfte froh sein, wenn er Linkstatschs Päckchen vom Hals hat. Gebt ihm das, es müsste genügen, damit Ihr Euren Anteil bekommt.$B$BKeine Bange, ich werde trotzdem ein gutes Wort für Euch einlegen, wenn ich zurück bin. Ich sollte nicht mehr lange in Desolace bleiben müssen.",
    ["O"] = "Bringt Linkstatschs Päckchen zu Roetten Steinhammer in Eisenschmiede zurück.",
    ["T"] = "Reagenzien für die Wiederholer AG",
  },
  [1468] = {
    ["D"] = "Ihr wollt uns tatsächlich hier im Waisenhaus zur Hand gehen? Seid gesegnet, Freund. Eure Unterstützung in der Kinderwoche ist für uns von unschätzbarem Wert.$B$BNehmt diese Pfeife. Sie kann sieben Tage lang verwendet werden - über die ganze Dauer der Kinderwoche hinweg. Durch Benutzung der Pfeife, könnt Ihr mit dem Waisenkind sprechen, auf das Ihr aufpasst. Verwendet sie erneut, um das Kind wieder zu entlassen.$B$BDas Kind ist schon ganz aufgeregt und freut sich darauf Euch kennenzulernen. Worauf wartet Ihr noch $N, benutzt die Pfeife und trefft Euren Schützling.",
    ["O"] = "Benutzt die Pfeife des Menschenwaisenkindes um während der Kinderwoche mit Eurem Schützling zu sprechen.",
    ["T"] = "Die Kinderwoche",
  },
  [1469] = {
    ["D"] = "Vor dem Flug trank ich ein ganzes Fass Starkbier. Hicks!$B$BUnd was für ein Flug das war. Ich sauste herum und Brohann folgte mir. Aber ich hab keinen Tempel nicht gesehen. Hicks!$B$BJedenfalls sah ich keinen Tempel, bis mir schwindlig wurde und ich von meinem Reittier fiel. Ich fiel kopfüber ins Wasser und wurde in die Tiefe gesogen. Dort sah ich mich von einer Schar unheimlicher Trolle umringt. Ich schwamm, was das Zeug hielt! Und schaffte es sogar mit diesem Trollsouvenir da raus. Hier, bringt es Brohann und lasst ihn wissen, dass der Tempel tatsächlich da unten ist. Hicks!",
    ["O"] = "Bringt das Schrifttafelfragment der Atal\'ai zu Brohann Fassbauch in Sturmwind.",
    ["T"] = "Rhapsodies Geschichte",
  },
  [1470] = {
    ["D"] = "Seid gegrüßt, Kind. Ich sehe, dass Ihr noch unerfahren in den Dingen der Hexenmeister seid, dass sich die Macht noch Eurem Griff entzieht. Doch ich spüre Euren Eifer zu lernen.$B$BUnd Ihr werdet lernen.$B$BDurch Ignoranz kann man keine Stärke finden. Wissen ist unsere größte Macht. Indem wir es anwenden, können wir die chaotische Magie und die Wesen der Leere kontrollieren und unserem Willen beugen.$B$BIch zeige Euch, wie Ihr einen Diener an Euren Willen bindet, doch zuerst müsst Ihr mir drei Schädel der klappernden Skelette nahe der verlassenen Schmiede bringen.",
    ["O"] = "Bringt Venya Marthand 3 Schädel eines klappernden Skeletts.",
    ["T"] = "Der zerrissene Schleier",
  },
  [1471] = {
    ["D"] = "Gewiss habt Ihr den Kreis der Beschwörung unter uns bemerkt. Nur dort könnt Ihr einen ungebundenen Leerwandler herbeirufen.$B$BMit diesen Runen könnt Ihr ihn herbeirufen, dann müsst Ihr ihn durch die Kraft von Waffen und Magie beherrschen.$B$BWenn er unterworfen wurde, wird er Euch ergeben sein. Kommt dann zu mir und ich werde den Beschwörungszauber erklären, damit er Euch in jeder Hinsicht zu Willen ist.",
    ["O"] = "Beschwört mithilfe der Runen der Beschwörung einen Leerwandler, unterwerft ihn und bringt die Runen der Beschwörung dann zu Carendin Halger im Magieviertel von Unterstadt zurück.",
    ["T"] = "Die Bindung",
  },
  [1472] = {
    ["D"] = "Ein Sukkubus ist ein Verschlinger von Seelen, Zerstörer von Herzen, Versucher der Menschen. Eine durch und durch böse und engstirnige Kreatur.$B$BOhne Anreiz kann er nicht in unsere Welt gebracht werden.$B$BDoch wir wenden seine eigene Kraft gegen ihn an. Den Herzen guter, reiner oder liebender Männer kann der Sukkubus nicht widerstehen und tritt in unsere Welt herüber. Dann könnt Ihr ihn beherrschen und zu Eurem Eigen machen.$B$BSprecht mit meinem Assistenten Godrick Farsan im Tempel. Er hilft Euch bei der Suche nach einem Köder.",
    ["O"] = "Sprecht mit Godrick Farsan im Tempel der Verdammten.",
    ["T"] = "Verschlinger der Seelen",
  },
  [1473] = {
    ["D"] = "Alle scheinen sich einig, dass Ihr nunmehr bereit seid zu lernen, wie man einen Leerwandler beschwört.$B$BEin aus dem jenseitigen Chaos erschaffenes Wesen - wenn Ihr eines beschwört, es aber nicht kontrollieren könnt, zerreißt es Euch.$B$BEgalin beschwor den ersten Leerwandler und hielt in seinem Zauberfoliant fest, wie es bewerkstelligt wird.$B$BLeider wurde er uns von einem Agenten des Scharlachroten Kreuzzugs gestohlen, der ihn in seinem Stützpunkt in einem Turm im Westen versteckte. Bringt ihn mir und ich lehre Euch das Wissen.",
    ["O"] = "Beschafft Egalins Zauberfolianten und bringt ihn zu Carendin Halgar in den Tempel der Verdammten.",
    ["T"] = "Kreatur der Leere",
  },
  [1474] = {
    ["D"] = "Benutzt diese Herzen und wirkt einen Zauber am Beschwörungskreis, um eine Tür zu öffnen, durch die der Sukkubus kommen kann. Eure Mächte sind jetzt größer und Ihr werdet sie alle brauchen, um den Sukkubus zu besiegen. $B$BHütet Euch vor seinen Bezauberungen, $N, manch starker Geist wurde schon seinem Willen unterworfen. Wenn er überwunden wurde, kehrt der Sukkubus in die Leere zurück, dann lehre ich Euch den Zauber, um ihn nach Eurem Gutdünken zurückzurufen.",
    ["O"] = "Beschwört mithilfe der reinen Herzen einen Sukkubus, unterwerft ihn und bringt die reinen Herzen dann zu Carendin Halger im Magieviertel von Unterstadt zurück.",
    ["T"] = "Die Bindung",
  },
  [1475] = {
    ["D"] = "Der Legende zufolge ist der Tempel von Atal\'Hakkar ein heiliger Schrein, der dem alten Gott Hakkar dem Seelenschinder geweiht ist. Der Tempel, der von einem tückischen Trollstamm namens Atal\'ai unterhalten wird, soll angeblich uneinnehmbar sein.$B$BDa der Tempel jetzt im Tränenteich versunken ist, dürfte es noch schwieriger sein, Artefakte zu bergen. Aber genau das müsst Ihr tun, $N.$B$BHelft mir dabei, indem Ihr die intakten Schrifttafeln der Atal\'ai sammelt. Bringt sie mir und ich schicke der Forschergilde einen Helden zurück. Wir werden uns den Ruhm teilen.",
    ["O"] = "Sammelt 10 Schrifttafeln der Atal\'ai für Brohann Fassbauch in Sturmwind.",
    ["T"] = "Im Tempel von Atal\'Hakkar",
  },
  [1476] = {
    ["D"] = "Männer mit Ehre und Güte? Existiert so ein Mann?$B$BViele Recherchen waren erforderlich, doch durch Geschichten, die ich hörte, konnte ich zwei Männer finden, die genügen sollten.$B$BDer Erste ist Dalin Schmiedevater, der sich der Fürsorge für die Flüchtlinge von Lordaeron verschrieben hat, die von ihresgleichen am Graumähnenwall verstoßen werden.$B$BDer andere ist Comar Villard, ein Mann, der seiner verlorenen Liebsten treu blieb und im Sumpfland nach Spuren seiner Frau sucht.$B$BBringt ihre Herzen zu Carendin. Doch zuvor möchte ich Euch Eure Opfer zeigen.",
    ["O"] = "Bringt die Herzen von Dalin Schmiedevater und Comar Villard zu Carendin Halgar im Tempel der Verdammten.",
    ["T"] = "Herzen der Reinen",
  },
  [1477] = {
    ["D"] = "Unsere Magier führen Forschungen in der Burg Nethergarde in den Verwüsteten Landen durch, die entscheidend für unser Verständnis der Dunklen Portale sind. Sie benötigen fast ununterbrochen Nachschub, doch unsere Nachschubwege nach Nethergarde sind dürftig.$B$BIhr müsst helfen sicherzustellen, dass der Nachschub nach Nethergarde gelangt. Meldet Euch bei Wachhauptmann Sorigal in Dunkelhain und tut, was er verlangt. Diese Aufgabe führt Euch in Feindesland, $N. Macht Euch bereit.",
    ["O"] = "Sprecht mit Wachhauptmann Sorigal in Dunkelhain im Dämmerwald.",
    ["T"] = "Überaus wichtige Vorräte",
  },
  [1478] = {
    ["D"] = "Ah, $N! Carendin Halgar ließ mich soeben wissen, dass er Euch für weitere Ausbildung sehen möchte.$B$BIhr solltet stolz auf Euch sein, $N Es kommt nicht jeden Tag vor, dass Meister Halgar persönlich einen jungen Hexenmeister unterrichtet.$B$BIhr findet ihn im Magieviertel von Unterstadt nahe des Tempels der Verdammten.",
    ["O"] = "Sprecht mit Carendin Halgar in Unterstadt.",
    ["T"] = "Halgars Beschwörung",
  },
  [1480] = {
    ["D"] = "Die zerrissene Haut riecht verfault und ist erstaunlich geschmeidig für etwas, das so trocken und zerbrechlich wirkt.$B$BAls Ihr sie aufrollt, könnt Ihr deutlich die roten Zeichen einer Art Sprache auf der einen Seite erkennen. Am unteren Ende der Haut befindet sich ein sehr deutliches Symbol, das Ihr für eine Art Unterschrift haltet.$B$BVielleicht könnte Maurin Euch mehr darüber erzählen?",
    ["O"] = "Bringt die geschundene Dämonenhaut nach Desolace zu Maurin Knochenspalter.",
    ["T"] = "Der Verderber",
  },
  [1481] = {
    ["D"] = "Ich denke, mit den entsprechenden Reagenzien kann ich diesen Dämonenlord finden.$B$BWenn er wirklich so mächtig ist, wie ich vermute, brauche ich gesammelte Gegenstände von tödlichen Kreaturen. Doch Ihr handelt im Namen des Kriegshäuptlings, daher werdet Ihr die Aufgabe gewiss übernehmen.$B$BAls Erstes muss ich jeden Schutzzauber brechen, den er gegen Wahrsagen hat. Daher brauche ich den Skalp eines Satyrs... aber nicht den eines beliebigen Satyrs. Der Skalp muss von einem Schattenpirscher der Zornsäer stammen.$B$BSucht in den antiken Ruinen nordöstlich von Desolace nach ihnen.",
    ["O"] = "Bringt Maurin Knochenspalter in Desolace einen Schattenpirscherskalp.",
    ["T"] = "Der Verderber",
  },
  [1482] = {
    ["D"] = "Ein Orakelkristall wäre genau richtig. Die Naga stellen sie her, damit ihre Orakel sie benutzen können.$B$BNordwestlich von hier liegt ein großes Korallenriff vor der Küste, das die Naga als ihre Heimat nutzen, da es so nahe am Ufer liegt. Dort könnt Ihr ihre Orakel finden. Die Zackenkammnaga sind tödlich und reagieren ungnädig auf Euer Eindringen, seid daher vorsichtig und eilt Euch.$B$BWenn Ihr zurückkehrt, suchen wir diesen Dämonenlord Azrethoc.",
    ["O"] = "Bringt Maurin Knochenspalter in Desolace einen Orakelkristall.",
    ["T"] = "Der Verderber",
  },
  [1483] = {
    ["D"] = "Die Venture Company stahl viele meiner besten Goblindenker, aber von Zeit zu Zeit bekommen wir Gelegenheit, einige zurückzustehlen.$B$BVor einiger Zeit stellte die Filiale der Venture Company im Steinkrallengebirge Gerenzo Pfeifzang ein... einen Gnom! Ihr anderer Ingenieur, Ziz Fizziks, war so erschüttert davon, dass er mich anflehte, ihm eine neue Stelle beim Tüftlerverband zu besorgen. Ich gab ihm einige Aufgaben, aber möglicherweise braucht er dabei Hilfe.$B$BVielleicht könntet Ihr mal nach dem Rechten sehen? Er ist am Westrand der Scherwindklippe stationiert.",
    ["O"] = "Sprecht mit Ziz Fizziks bei der Scherwindklippe.",
    ["T"] = "Ziz Fizziks",
  },
  [1484] = {
    ["D"] = "Diese Kreatur ist weitaus gefährlicher als ich dachte. Zweifellos stellt Takata in Frage, dass ich so einer Kreatur gewachsen bin, und es würde ihn in seiner Ehre kränken, wenn er nicht wüsste, was gerade passierte.$B$BSprecht mit dem Schwertkämpfer, sagt ihm, Ihr seid bereit, den Dämon zu erschlagen, wenn er es erlaubt, aber bedrängt ihn nicht zu sehr. Es ist besser, wenn er denkt, dass es seine Idee war.",
    ["O"] = "Sprecht mit Takata Stahlklinge in Desolace.",
    ["T"] = "Der Verderber",
  },
  [1485] = {
    ["D"] = "Die Hexenmeister, die vor uns kamen, weckten das Misstrauen der Horde. Man toleriert uns zwar, aber man liebt uns nicht.$B$BDas ließ die Hexenmeister freilich nicht näher zusammenrücken, da viele in Abgeschiedenheit leben und ihre Magie allein erforschen. Ich will Euch nicht auf den einen oder anderen Weg führen, sondern nur helfen, reifer zu werden.$B$BBevor ich Euch die Kunst des Beschwörens lehre, bringt mir sechs Köpfe der schändlichen Familiare aus der Höhle im Nordosten.",
    ["O"] = "Bringt 6 Köpfe schändlicher Familiare zu Ruzan.",
    ["T"] = "Üble Familiare",
  },
  [1486] = {
    ["D"] = "Als Naralex tiefer in seinen Alptraum vorstieß, stieg eine seltsame Schar von Bestien unter dem Brachland in die Höhlen des Wehklagens vor.$B$BDiese Deviatkreaturen haben seltsame, überirdische Eigenschaften. Sie sind zwar von Natur aus böse, doch bin ich der Meinung, dass ihre Existenz hier in Kalimdor auch etwas Gutes haben kann. Ich glaube, ihre Bälge sind besonders gut für die Lederverarbeitung geeignet.$B$BWenn Ihr Euch der Aufgabe gewachsen fühlt, steigt in die Höhlen hinab und holt einige Deviatbälge.",
    ["O"] = "Nalpak in den Höhlen des Wehklagens möchte 20 Deviatbälge.",
    ["T"] = "Deviatbälge",
  },
  [1487] = {
    ["D"] = "Naralex hatte ein edles Ziel.$B$BUnser großer Anführer trachtete danach, in den Smaragdgrünen Traum zu gelangen und dabei zu helfen, aus diesem kargen Land wieder den üppigen Wald zu machen, der hier einst wuchs. Aber etwas ist furchtbar schief gegangen.$B$BNaralex\' Traum wurde zum Alptraum. Verderbte Kreaturen siedelten sich in den Höhlen an.$B$BWährend einige Jünger von Naralex versuchen, unseren Meister zu wecken, ist es mein Trachten, die Höhlen von diesen bösen Bestien zu befreien.$B$BWagt Euch in die Höhlen, $N, und löscht die Deviatbrut aus.",
    ["O"] = "Ebru in den Höhlen des Wehklagens möchte, dass Ihr 7 Deviatverheerer, 7 Deviatvipern, 7 Deviatschlurfer und 7 Deviatschreckensfange tötet.",
    ["T"] = "Ausrottung der Deviat",
  },
  [1488] = {
    ["D"] = "Wiewohl ich nichts über diesen Lord Azrethoc weiß, stimme ich mit Maurin überein, er muss aufgehalten werden. Besonders wenn er versucht, Einfluss über die Brennende Klinge zu bekommen, um seine Ziele voranzutreiben.$B$BIhr habt schon gezeigt, dass Ihr den Kriegshäuptling unterstützen möchtet. Ich verlasse mich darauf, dass Ihr eine Truppe zusammenstellt, die den Dämonenlord und seinen Hexenmeisterdiener besiegt.$B$BWenn Ihr bereit seid, geht direkt nach Süden und kehrt zu mir zurück, wenn die Bedrohung beseitigt wurde.",
    ["O"] = "Besiegt Lord Azrethoc und Jugkar Grimm\'rod. Kehrt dann zu Takata Stahlklinge in Desolace zurück.",
    ["T"] = "Der Verderber",
  },
  [1489] = {
    ["D"] = "Die Schalen, die Ihr mir gebracht habt, haben eine Besudelung, eine, die ich nie zuvor gesehen habe. Ich bin mir sicher, dass das Wasser der Oasen der Grund dafür ist. Ich spüre die Verwirrung, die sie über das Land bringen. Es ist, als würde das Land träumen und nicht erwachen können.$B$BDie Höhlen des Wehklagens müssen die Quelle dieser Besudelung sein. Doch ehe Ihr dorthin geht, solltet Ihr mit dem Druidenältesten Hamuul Runentotem sprechen. Ich ließ ihn über die Vorfälle im Brachland informieren. Er ist weise und kann Euch Rat geben.$B$BIhr findet ihn in Donnerfels auf der Anhöhe der Ältesten.",
    ["O"] = "Sprecht mit Hamuul Runentotem.",
    ["T"] = "Hamuul Runentotem",
  },
  [1490] = {
    ["D"] = "Ich betrat das Land der Träume und träumte von einem Druidenkult, der in den Höhlen des Wehklagens haust. Ich sah, dass ihre Ziele einst edel waren, sie jedoch vom rechten Weg abkamen. Sie sind jetzt Diener der Besudelung, die das Brachland befallen hat.$B$BIch bin alt und meine Kraft verlässt mich. Ich konnte nicht lange an diesem Traumort verweilen und weiß wenig mehr von den besudelten Druiden. Aber Nara ist jung und starken Willens. Sie blieb in dem Traum zurück, als ich floh.$B$BSprecht mit Nara. Sie ist in diesem Zelt da drüben.",
    ["O"] = "Sprecht mit Nara Wildmähne.",
    ["T"] = "Nara Wildmähne",
  },
  [1491] = {
    ["D"] = "Nun gut, die Raptorenhörner haben nicht funktioniert. Aber das macht nichts... nicht alles klappt beim ersten Mal! Außerdem habe ich wohl etwas gefunden, das in meinen Klugheitstränken noch besser funktioniert!$B$BEs gibt eine Substanz, die Klageessenz genannt wird. Ich glaube, sie ist die Ursache für die seltsamen Pflanzen und Tiere im Brachland. Bringt mir etwas davon, dann erprobe ich es in meinem Trank!$B$BIch hörte, die Essenz reichert sich in Ektoplasmakreaturen in den Höhlen des Wehklagens im Westen an. Jagt diese Kreaturen und sammelt die Essenz.",
    ["O"] = "Sammelt 6 Portionen Klageessenz.",
    ["T"] = "Klugheitstränke",
  },
  [1492] = {
    ["D"] = "Ich muss eine Kiste mit Tränken, Reagenzien und anderen Gegenständen zu meinen Geschäftspartnern in der alten Welt schaffen. Ich habe kaum Zeit, sie selbst zum Dock von Ratschet zu bringen, und die letzte Karawane ist schon aufgebrochen.$B$BKönntet Ihr sie bitte freundlichst zu Werftmeister Flunkerblick bringen? Er wird dafür sorgen, dass sie an Bord des nächsten Schiffes zur Schwarzmeerbucht kommt.",
    ["O"] = "Bringt die gesicherte Kiste zu Werftmeister Flunkerblick in Ratschet.",
    ["T"] = "Werftmeister Flunkerblick",
  },
  [1498] = {
    ["D"] = "Als Krieger ist eine unserer wichtigsten Aufgaben... der Schutz. Wir sind der Schild der Horde und sorgen für die Sicherheit unserer schwächeren Brüder. Wenn Ihr Euch bei uns einreihen möchtet, müsst Ihr mir Eure Tauglichkeit beweisen.$B$BGeht zum Donnergrat in Durotar, wo die Blitzechsen und Donnerechsen hausen. Jagt die Bestien! Zeigt mir, dass Ihr härter als sie seid! Trotzt ihren Angriffen und bringt mir einen Stapel ihrer versengten Schuppen als Beweis.$B$BGelingt Euch das, unterrichte ich Euch.",
    ["O"] = "Bringt 5 versengte Schuppen zu Uzzek bei der Fernwacht im Brachland.",
    ["T"] = "Weg der Verteidigung",
  },
  [1499] = {
    ["D"] = "Zureetha hat Novizen wie Euch geschickt, damit sie helfen, die Zahl der Kreaturen in der Höhle einzudämmen, in deren Nähe Ihr gegen die schändlichen Familiare gekämpft habt.$B$BIch finde, es wäre gut für Euch, wenn Ihr Erfahrung sammelt, indem Ihr ihr helft. Erwähnt unbedingt, dass Ihr mitgeholfen habt, das Wichtel-Problem zu lösen, womöglich hat sie eine Belohnung für Euch.",
    ["O"] = "Sprecht mit Zureetha Fernblick.",
    ["T"] = "Üble Familiare",
  },
  [1500] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Die Erweckung von Naralex",
  },
  [1501] = {
    ["D"] = "So wenige Hexenmeister sind übrig... Wir riskieren viel, doch die Risiken sind gerechtfertigt. Ihr müsst bald Reife in Eurer Fähigkeit erlangen, wenn Ihr der Horde und dem Kriegshäuptling nützlich sein wollt.$B$BEs ist Zeit, dass Ihr einen neuen Beschwörungszauber lernt.$B$BAls Erstes müsst Ihr die Schrifttafel der Verga wiederbeschaffen, die von Hexenmeistern der Brennenden Klinge gestohlen wurde und nun in ihrem Unterschlupf in der Knochenhöhle verwahrt wird. Mit der Tafel zeige ich Euch die Runen, mit denen ein Leerwandler beschworen wird.",
    ["O"] = "Holt die Schrifttafel der Verga für Gan\'rul Blutauge in Orgrimmar.",
    ["T"] = "Kreatur der Leere",
  },
  [1502] = {
    ["D"] = "Es ist wichtig, dass ein Krieger eine hochwertige Waffe hat. Und wenn es einen Orc gibt, der hochwertige Waffen herstellen kann, dann ist das Thun\'grim Brandblick. Er ist ein Einsiedler, der auf den Hügeln östlich des Wegekreuzes haust.$B$BWenn Ihr eine Waffe sucht, Krieger, dann solltet Ihr Euch an Thun\'grim wenden. Um ihn zu finden reist Ihr zuerst zum Wegekreuz im Westen und wendet Euch dann nach Osten in die Berge. Sein Lager liegt hoch auf einem Berggipfel.",
    ["O"] = "Sprecht mit Thun\'grim Brandblick im Brachland.",
    ["T"] = "Thun\'grim Brandblick",
  },
  [1503] = {
    ["D"] = "Ich gebe Euch eine Waffe, $N, doch vorher müsst Ihr mir helfen. Vor einigen Nächten kamen die Klingenmähnen von Nordwesten in mein Lager. Sie wollten nicht mit mir kämpfen, wissen aber, wie schlecht es um mein Augenlicht bestellt ist. Einige lenkten mich durch Schnauben ab, während die anderen mich bestahlen. Diese Feiglinge!$B$BSie nahmen eine Eisentruhe mit, in der sich Schmiedestahlbarren befanden. Wenn Ihr sie wiederbeschaffen könnt, bekommt Ihr die Waffe, die Ihr sucht.$B$BSucht im Stacheleberlager nach der Eisentruhe und bringt die Schmiedestahlbarren zurück.",
    ["O"] = "Bringt die Schmiedestahlbarren zu Thun\'grim Brandblick im Brachland.",
    ["T"] = "Geschmiedeter Stahl",
  },
  [1504] = {
    ["D"] = "Nun studiert die Schrifttafel der Verga, denn sie enthält die Glyphen, die Ihr für die Beschwörung braucht. Bringt sie zu dem Kreis in Neeru Feuerklinges Zelt auf der anderen Seite der Höhle. Dort könnt Ihr den Leerwandler herbeirufen.$B$BIhr müsst ihn mit allen körperlichen und magischen Mitteln besiegen, die Euch zur Verfügung stehen. Wenn das vollendet ist, kommt mit den Glyphen der Beschwörung zu mir zurück und ich führe Euch vor, wie Ihr die Kreatur an Euren Willen bindet.",
    ["O"] = "Beschwört mithilfe der Glyphen der Beschwörung einen Leerwandler, unterwerft ihn und bringt dann die Glyphen der Beschwörung zu Gan\'rul Blutauge in Orgrimmar zurück.",
    ["T"] = "Die Bindung",
  },
  [1505] = {
    ["D"] = "Die Horde ehrt ihre Krieger, da ihre Kraft außer Frage steht. Doch die wahrhaft großen Helden sind auch große Schüler.$B$BSucht nach Uzzek. Er ist ein Veteran zahlreicher Kämpfe und unterrichtet jetzt junge Krieger in der Gefechtskunst. Ihr findet ihn südlich der Fernwacht, der Orcgarnison an der Grenze von Brachland und Durotar.",
    ["O"] = "Sprecht mit Uzzek bei der Fernwacht.",
    ["T"] = "Veteran Uzzek",
  },
  [1506] = {
    ["D"] = "Ihr da. $N, richtig?$B$BIch hoffe, Ihr seid nicht zu sehr beschäftigt, denn Gan\'rul möchte Euch unverzüglich sprechen. Ihr findet ihn mit den Hexenmeisterältesten in der Kluft der Schatten.$B$BMeinen früheren Erfahrungen mit Gan\'rul nach zu urteilen, bedeutet Eure Einbestellung entweder etwas sehr Gutes oder etwas sehr Schlechtes.",
    ["O"] = "Sprecht mit Gan\'rul Blutauge in Orgrimmar.",
    ["T"] = "Gan\'ruls Beschwörung",
  },
  [1507] = {
    ["D"] = "Eure Zeit ist gekommen, mehr über die Beschwörungsmagien zu lernen. Ihr werdet lernen, einen Sukkubus zu beschwören, eine Kreatur mit unglaublichen geistigen Mächten. Er kann sich schwache Geister zu Diensten machen... doch seid unbesorgt! Ich bin sicher, Ihr seid der Herausforderung gewachsen.$B$BEin Sukkubus kann nur beschworen werden, wenn man ihn mit einem Gegenstand ködert, der große Hingabe und Liebe bedeutet. Ich überlasse es Euch, so ein Objekt zu finden.$B$BEin Vorschlag? Sprecht mit Cazul... Es scheint wenig zu geben, das der alte Hexenmeister nicht weiß.",
    ["O"] = "Sprecht mit Cazul in der Kluft der Schatten.",
    ["T"] = "Verschlinger der Seelen",
  },
  [1508] = {
    ["D"] = "Hmmm... Aber natürlich seid Ihr nicht gekommen, um deshalb mit mir zu sprechen.$B$BWenn Gan\'rul Euch zu mir geschickt hat, dann gewiss, weil Ihr einen Sukkubus beschwören sollt und einen Gegenstand braucht, um ihn anzulocken.$B$BHmm... Wenn Ihr Euch von einem Blinden führen lassen möchtet, $N, dann hört zu. In den Hängen von Orgrimmar, westlich des Himmelsturms im Tal der Stärke, lebt Zankaja. Sie wartet darauf, dass ihr Lebensgefährte zurückkehrt.$B$BGeht zu ihr.",
    ["O"] = "Sprecht mit Zankaja in Orgrimmar.",
    ["T"] = "Der blinde Cazul",
  },
  [1509] = {
    ["D"] = "Dogran, mein Gefährte, wurde jüngst zum Dienst am Wegekreuz abkommandiert. Es war eine Beförderung und er war sehr stolz.$B$BIch war natürlich traurig, ließ es ihn aber nicht wissen. Und auch besorgt. Das Brachland ist gefährlicher als Orgrimmar, daher fürchtete ich das Schlimmste.$B$BSeit einer Woche sollte er nun schon zu Hause sein und ich habe kein Wort von ihm gehört...$B$BVielleicht könntet Ihr zum Wegekreuz gehen und nachfragen, ob es Neuigkeiten gibt? Er arbeitete mit Gazrog, der noch dort stationiert sein sollte.",
    ["O"] = "Sprecht mit Gazrog beim Wegekreuz.",
    ["T"] = "Neuigkeiten von Dogran",
  },
  [1510] = {
    ["D"] = "Er war im Camp Taurajo stationiert, wo wir versuchten, die Stacheleber auszurotten. Er führte ein paar Grunzer zu einem ihrer Lager, wo er schwer verwundet wurde. Die anderen schafften es zurück und versuchten, ihn zu retten. Er war in einem schlimmen Zustand, seine Wunden eiterten, daher wollten sie ihn nicht mitnehmen, ohne ihn erst zu versorgen.$B$BSie schickten eine Nachricht hierher und baten um ein Heilgebräu von den Trollen der Malaka\'jin im Steinkrallengebirge. Wir schickten Leute, um eines zu holen, aber diese sind noch nicht zurückgekehrt.",
    ["O"] = "Sprecht mit Ken\'zigla am Malaka\'jin.",
    ["T"] = "Neuigkeiten von Dogran",
  },
  [1511] = {
    ["D"] = "Ich dachte mir, dass ein anderer kommen würde, um den Trunk zu holen, daher habe ich ihn dennoch vorbereitet.$B$BDa habt Ihr ihn, $N. Aber denkt daran, Ihr solltet ihm nur die Hälfte geben! Dadurch bekommt er genug Kraft, sich zu bewegen, ohne dass er verblutet. Wartet eine Nacht und gebt ihm dann die andere Hälfte.$B$BWie auch immer, Ihr solltet dies zu Logmar im Camp Taurajo bringen. Nach allem, was er mir gesagt hat, sollte er wissen, wo dieser Dogran ist.",
    ["O"] = "Bringt Ken\'ziglas Trunk zu Grunzer Logmar im Camp Taurajo.",
    ["T"] = "Ken\'ziglas Trunk",
  },
  [1512] = {
    ["D"] = "Zankaja ist jung und wunderschön. An einen Invaliden gekettet zu sein... Ich dulde nicht, dass ihr diese Ungerechtigkeit widerfährt.$B$BLasst mich allein, $N. Und habt Dank. Vielleicht beende ich mein Leben im ruhmreichen Kampf statt als leblose Hülle.$B$BUnd bitte erzählt meiner Gefährtin nichts davon... Ich möchte nicht, dass sie sich Schuld auflädt.",
    ["O"] = "Bringt den schmutzverkrusteten Anhänger zu Gan\'rul Blutauge in Orgrimmar.",
    ["T"] = "Die Liebesgabe",
  },
  [1513] = {
    ["D"] = "Bringt dies zum Beschwörungskreis in Neerus Zelt, $N. Beschwört einen schändlichen Sukkubus und zeigt ihm Eure Überlegenheit. Ich glaube an die Kraft Eurer Magie. Aber seid wie stets wachsam, denn Stolz und Selbstüberschätzung wurden manchem viel versprechenden Hexenmeister zum Verhängnis.$B$BWenn Ihr ihn besiegt habt, bringt den Anhänger zu mir, damit ich Zankaja die Neuigkeit überbringen kann.",
    ["O"] = "Beschwört mithilfe von Dograns Anhänger einen Sukkubus, unterwerft ihn und bringt dann Dograns Anhänger zu Gan\'rul Blutauge in Orgrimmar zurück.",
    ["T"] = "Die Bindung",
  },
  [1515] = {
    ["D"] = "Ihr habt es bis hierher geschafft, wenn Ihr es bis zu Ende durchstehen möchtet, werde ich Euch nicht aufhalten. Aber ich kann keine Leute erübrigen, Ihr seid also auf Euch allein gestellt.$B$BDogran wird in einem Lager der Stacheleber nördlich von hier festgehalten. Sucht nach zwei nebeneinander stehenden Hütten. Er wird schwer bewacht... Das heißt, wenn sie ihn noch nicht getötet haben oder er seinen Verletzungen erlegen ist.$B$BViel Glück.",
    ["O"] = "Bringt Ken\'ziglas Trunk zu Dogran im Brachland.",
    ["T"] = "Dograns Gefangenschaft",
  },
  [1516] = {
    ["D"] = "Die Zeit ist gekommen, junger $C. Ihr seid stark geworden und Euer Geist ist ausdauernd wie die Erde. Dass Ihr dem Pfad des Schamanen folgt, zeigt, dass Ihr Weisheit besitzt, sogar bevor Ihr Euch selbst beweist.$B$BDas Element der Erde wird über Euer Schicksal wachen und Teil von Euch werden, wenn Ihr bereit seid. Doch Ihr müsst vor der Erde selbst bestehen. Wenn Ihr bereit seid, werdet Ihr Dinge sehen, von denen nur Schamanen wissen.$B$BSucht die Teufelspirscher im Norden und beschafft Euch zwei ihrer Hufe. Kehrt dann zu mir zurück und wir sprechen weiter über Eure Zukunft.",
    ["O"] = "Bringt 2 Teufelspirscherhufe zu Canaga Erdenrufer im Tal der Prüfungen.",
    ["T"] = "Ruf der Erde",
  },
  [1517] = {
    ["D"] = "Ein Sapta ist ein Getränk, mit dem unsere Seelen an die Elemente gebunden werden. Mit Magie gestärkt lässt dieser Trank die Willensstarken die Elemente sehen, wie es sonst niemand kann. Euer Geist wird mit dem Element verbunden, für das das Sapta geschaffen wurde. Nur wenige auserwählte Schamanen kennen das Rezept.$B$BDas Sapta wird stets an einem heiligen Ort getrunken, niemals anderswo...vergesst das nicht.$B$BBesucht den Geisterfelsen südwestlich von hier...er befindet sich am Ende des Verborgenen Pfades. Trinkt Euer Sapta dort und...tja, Ihr werdet schon sehen.",
    ["O"] = "Sucht den Geisterfelsen und trinkt das Erdsapta.",
    ["T"] = "Ruf der Erde",
  },
  [1518] = {
    ["D"] = "Nehmt diesen Rohquarz von mir und bringt ihn als Beweis für unsere Begegnung zu Canaga Erdenrufer. Er wird ein Totem für Euch anfertigen, dessen Herzstück dieser Kiesel sein soll. So winzig er scheinen mag, Ihr werdet feststellen, dass es nicht auf die Größe ankommt und selbst die kleinsten Dinge viele anderen überdauern können.$B$BHabt Geduld, $N. Bleibt stark und sucht Weisheit. Dies verlangt die Erde von Euch. Am Ende solltet Ihr eins mit der Erde werden... respektiert sie so, wie sie Euch respektiert.",
    ["O"] = "Bringt den Rohquarz zu Canaga Erdenrufer im Tal der Prüfungen.",
    ["T"] = "Ruf der Erde",
  },
  [1519] = {
    ["D"] = "Die Zeit ist gekommen, $C. Euer Körper ist ausgereift und stark geworden und Euer Geist ist ausdauernd wie die Erde. Ich sehe es Euch an... Ihr besitzt bereits Weisheit.$B$BDie Erde leitet unser Volk und spricht zu uns... und bald auch zu Euch. Aber Ihr müsst geprüft werden. Wenn Ihr bereit seid, werdet Ihr Dinge sehen, von denen nur unsere Art weiß.$B$BSucht die Schamanen der Borstennacken in ihrer Klamm im Osten auf und beschafft Euch eine ihrer Salben, die sie für ihre Rituale verwenden. Kehrt erst zu mir zurück, wenn Ihr genügend davon für Euren eigenen Trank habt.",
    ["O"] = "Bringt 2 Dosen Ritualsalbe zu Seherin Rabenfeder im Camp Narache.",
    ["T"] = "Ruf der Erde",
  },
  [1521] = {
    ["D"] = "Nehmt diesen Rohquarz von mir und bringt ihn als Beweis für unsere Begegnung zu Seherin Rabenfeder. Sie wird ein Totem für Euch anfertigen, dessen Herzstück dieser Kiesel sein soll. So winzig er scheinen mag, Ihr werdet feststellen, dass es nicht auf die Größe ankommt und selbst die kleinsten Dinge Berge überdauern können.$B$BHabt Geduld, $N. Bleibt stark und sucht Weisheit. Dies verlangt die Erde von Euch. Am Ende solltet Ihr eins mit der Erde werden... respektiert sie so, wie sie Euch respektiert.",
    ["O"] = "Bringt den Rohquarz zu Seherin Rabenfeder im Camp Narache.",
    ["T"] = "Ruf der Erde",
  },
  [1522] = {
    ["D"] = "Feuer sei mit Euch, Mann. Ich sehe, Ihr seid schon bewandert in den Wegen der Erde und scheint mir bereit, mit Kranal zu sprechen.$B$BKranal lebt nördlich des Wegekreuzes im Brachland. Er kennt sich mit den Elementen aus und kann Euch was übers Feuer beibringen...wenn Ihr denkt, dass Ihr bereit seid.$B$BEr ist leicht zu finden, geht nur nicht zu weit. Wenn Ihr zu weit geht, seid Ihr plötzlich im Elfenland.",
    ["O"] = "Sucht Kranal Fiss im Brachland.",
    ["T"] = "Ruf des Feuers",
  },
  [1523] = {
    ["D"] = "Es ist lange her, seit Ihr zuletzt geprüft wurdet, $N. Bislang habt Ihr bei Euren Studien Geduld bewiesen, doch die Zeit ist gekommen, da Ihr mehr lernen müsst. Die Zeit ist gekommen, mehr über Zerstörung zu lernen... und über Chaos. Ihr könnt das Element des Feuers verstehen lernen, wenn Ihr bereit seid, dafür zu bezahlen.$B$BSucht nach Kranal Fiss im Brachland. Er lebt in einer kleinen Siedlung nördlich des Wegekreuzes, aber gebt Acht, wenn Ihr das Land der Nachtelfen erreicht, seid Ihr zu weit gegangen.",
    ["O"] = "Sucht Kranal Fiss im Brachland.",
    ["T"] = "Ruf des Feuers",
  },
  [1524] = {
    ["D"] = "Ihr seid mächtig genug, das Totem des Feuers zu tragen, doch bedeutet das nicht, dass Ihr bereit seid.$B$BAm Südstromfluss in Durotar, vor dem Großen Meer, liegt ein verborgener Pfad zu einem der höchsten Gipfel des Landes. Sucht einen Weg nach oben. Dann werdet Ihr einen unserer heiligen Schreine finden, den Telf Joolam bewacht... den Schrein der Schlafenden Flamme.$B$BBringt ihm diese Fackel... sie zeigt, dass Ihr versucht, das Feuer zu bändigen. Bringt sie mir zurück, wenn Ihr sie an der Kohlenpfanne des Schreins entzündet habt, und ich sorge dafür, dass Ihr das Totem des Feuers bekommt.",
    ["O"] = "Bringt die Fackel der schlafenden Flamme zu Telf Joolam in Durotar.",
    ["T"] = "Ruf des Feuers",
  },
  [1525] = {
    ["D"] = "Aber Ihr seid doch nicht zum Geschichtsunterricht hergekommen, oder? Ihr seid wegen der ewigen Flamme und einem Feuertotem gekommen.$B$BZunächst einmal müsst Ihr ein Sapta anfertigen und Euch selbst an das Feuer binden.$B$BIch kann das Feuersapta für Euch herstellen, wenn Ihr mir zwei Gegenstände bringt. Der Erste ist Feuerteer, ein einfacher Gegenstand, den normalerweise die Zauberer der Klingenmähnen im östlichen Brachland mit sich führen.$B$BDer zweite Gegenstand dürfte gefährlicher zu bekommen sein: ein Reagenzienbeutel von einem Kultisten der Brennenden Klinge. Ihr findet sie in einer Höhle über den Klammen nordöstlich von Klingenhügel.",
    ["O"] = "Bringt 1 Feuerteerstück und 1 Reagenzienbeutel zu Telf Joolam in Durotar.",
    ["T"] = "Ruf des Feuers",
  },
  [1526] = {
    ["D"] = "Das Feuersapta ist fertig. Die Zeit ist gekommen, dass Ihr Euch der Flamme stellt. Benutzt das Sapta am Schrein und geht zum Berggipfel, um Euch dort der Manifestation zu stellen. Er wird Euch erst angreifen, wenn Ihr bereit seid, wird aber wissen, dass Ihr ihn sehen könnt.$B$BSchlagt ihn schnellstens nieder, $N, denn seine Macht wächst, je länger Ihr braucht, um ihn zu besiegen. Wenn er bezwungen wurde, legt seine Glut in die Kohlenpfanne und entzündet die Fackel.",
    ["O"] = "Besiegt die geringe Feuermanifestation und legt Glut in die Kohlenpfanne auf dem Schrein der ewigen Flamme.",
    ["T"] = "Ruf des Feuers",
  },
  [1528] = {
    ["D"] = "Wasser sei in Eurer Zukunft, Mann. Ich sehe, Ihr seid schon bewandert in den Wegen des Feuers und scheint mir bereit, mit Islen zu sprechen.$B$BIslen lebt an der Küste des Brachlandes. Wenn Ihr am Ufer des Südstroms nach Süden geht, könnt Ihr sie gar nicht verfehlen...achtet auf den großen Hai! Sie kennt sich mit den Elementen aus und kann Euch etwas übers Wasser beibringen...wenn Ihr denkt, dass Ihr bereit seid.$B$BSie ist leicht zu finden, geht nur nicht zu weit nach Süden...sie ist gleich hinter Ratschet.",
    ["O"] = "Sucht Islen Wasserdeuter im Brachland.",
    ["T"] = "Ruf des Wassers",
  },
  [1529] = {
    ["D"] = "Es ist lange her, seit Ihr zuletzt geprüft wurdet, $N. Bislang habt Ihr bei Euren Studien Geduld bewiesen, doch die Zeit ist gekommen, da Ihr mehr lernen müsst. Die Zeit ist gekommen, mehr über die Reinheit des Wassers zu lernen.$B$BSucht nach Islen Wasserdeuter im Brachland. Sie befindet sich an der Ostküste, südlich von Ratschet und dem Südstrom. Ihr findet sie in ihrer Fischerhütte. Viel Glück, $N.",
    ["O"] = "Sucht Islen Wasserdeuter im Brachland.",
    ["T"] = "Ruf des Wassers",
  },
  [1530] = {
    ["D"] = "Wenn Ihr eine Lektion über Wasser lernen müsst, dann diese: Wasser bedeutet Wiedergeburt.$B$BSeine Macht fließt, erodiert und reinigt alle Arten von Dingen. Seine Fähigkeit zu heilen und zu regenerieren ist unvergleichlich, jedoch nur, wenn es rein ist.$B$BWenn Ihr begreift, dass verunreinigtes Wasser alles verheeren kann, was es berührt, werdet Ihr schätzen lernen, wie lebenswichtig dieser Rohstoff ist.$B$BWenn Ihr das Wassersapta möchtet, sucht Brine tief im Südlichen Brachland. Ihr Heim wird unablässig von den dortigen Stachelebern heimgesucht.",
    ["O"] = "Sucht Brine beim Schwarzdorngrat im Südlichen Brachland.",
    ["T"] = "Ruf des Wassers",
  },
  [1531] = {
    ["D"] = "Macht Euch auf den Weg, Mann. Ihr seid bereit, Euch des Lufttotems würdig zu erweisen, aber zuerst müsst Ihr Prate Wolkendeuter in Tausend Nadeln finden. Wenn Ihr sie mit ausreichend Respekt behandelt, wird sie bereit sein, Euch zu sagen, wie Ihr das Lufttotem bekommen könnt.$B$BGeht durch das Brachland nach Süden und weiter nach Osten, wenn Ihr Tausend Nadeln erreicht habt. Ihr werdet sie verborgen in einem Erdloch mit ihrem Liebsten finden - haha, sagt ihr nicht, dass ich schlecht über sie geredet habe - das mag sie nicht sonderlich.",
    ["O"] = "Sucht Prate Wolkendeuter in Tausend Nadeln.",
    ["T"] = "Ruf der Luft",
  },
  [1532] = {
    ["D"] = "Die Winde von Kalimdor wehen Euch entgegen und Ihr sollt zu den Mächtigsten unserer $GBrüder:Schwestern; gehören. Ich sehe mit Stolz, dass Ihr so weit gekommen seid und so eine viel versprechende Zukunft habt.$B$BDas Luftelement ist schwer zu kontrollieren, doch ich vertraue darauf, dass Eure Fertigkeiten alle Hindernisse überwinden, die Euch den Weg versperren.$B$BGeht zum äußersten Südrand des Brachlandes und betretet Tausend Nadeln. Dort, weit im Osten, werdet Ihr Prate Wolkendeuter finden. Sie wird Euch mehr beibringen.",
    ["O"] = "Sucht Prate Wolkendeuter in Tausend Nadeln.",
    ["T"] = "Ruf der Luft",
  },
  [1534] = {
    ["D"] = "Nehmt diesen Wasserschlauch und begebt Euch tief in die dichten Wälder, an den Ort, den die Nachtelfen Eschental nennen. Sie liegen weit nördlich des Brachlandes.$B$BWenn Ihr den Wald betreten habt, reist nach Westen, an den Bergen entlang, die das Steinkrallengebirge und das Eschental trennen. Jenseits des Mystralsees findet Ihr einen heiligen Ort: die Sternenstaubruinen. In der Mitte des Sees findet Ihr einen kleinen Brunnen.$B$BBringt mir eine Probe des Brunnenwassers. Das ist die letzte Komponente, die Ihr braucht, ehe ich Euch zu Islen zurückschicke.",
    ["O"] = "Füllt den leeren blauen Wasserschlauch bei den Sternenstaubruinen im Eschental und kehrt zu Brine im Brachland zurück.",
    ["T"] = "Ruf des Wassers",
  },
  [1536] = {
    ["D"] = "Vor langer Zeit folgte ich einigen Gefährten ins Vorgebirge des Hügellands. Wir verteidigten uns gegen einige aufmüpfige Menschen, die dort beheimatet sind, und kamen in eine kleine Stadt, die die Verlassenen übernommen hatten: Tarrens Mühle.$B$BDort fand ich etwas höchst Eigentümliches: In der Mitte der Stadt befand sich ein kleiner Brunnen mit frischem Quellwasser. Ich bin nicht sicher, ob die gebeutelten Bewohner der Stadt seinen Wert überhaupt kennen... er ist eine kleine heilige Insel in einem unheiligen Meer.$B$BBringt mir eine Probe dieses Wassers in diesem Wasserschlauch.",
    ["O"] = "Füllt den leeren roten Wasserschlauch am Brunnen in Tarrens Mühle und kehrt zu Brine im Brachland zurück.",
    ["T"] = "Ruf des Wassers",
  },
  [1559] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Blitzbombenanleitung",
  },
  [1560] = {
    ["D"] = "Ich habe mich vollkommen verirrt!$B$BTorta, meine Frau, schickte mich Essen holen, aber ich muss irgendwo falsch abgebogen sein.$B$BWürdet Ihr so freundlich sein, mich zu Torta zurückzuführen? Sie wartet südlich vom Dampfdruckpier auf mich.",
    ["O"] = "Zeigt Tooga den Weg zurück zu seiner Frau Torta.",
    ["T"] = "Toogas Bitte",
  },
  [1578] = {
    ["D"] = "Die Gebirgsjäger vom Loch Modan kämpfen gegen Troggs und sind damit schwer beschäftigt! Sie benötigen konstanten Nachschub an neuen Rüstungen und Waffen, wenn wir diese Troggs je bezwingen wollen.$B$BDarum ist mein Kollege Thorvald hier und versucht, die Gebirgsjäger gut ausgerüstet zu halten. Wenn Ihr einige Kupferäxte und Kettengürtel aus Kupfer herstellen und sie Thorvald bringen könntet, wäre er Euch sehr verbunden. Vielleicht weiht er Euch sogar in sein Wissen ein.$B$BThorvald ist im südlichem Wachturm von Loch Modan.",
    ["O"] = "Bringt 6 Kupferäxte und 6 kupferne Kettengürtel zu Thorvald im südlichen Wachturm von Loch Modan.",
    ["T"] = "Nachschub für die Front",
  },
  [1579] = {
    ["D"] = "Ich kam nach Auberdine wegen der Ruhe. Ich möchte meine Erfindungen machen, wo es ruhig ist, denn nach einer durchzechten Nacht ertrage ich den Lärm von Eisenschmiede nicht!$B$BDaher nahm ich ein Schiff nach Auberdine, trank aber unterwegs ein bisschen viel. Ich ließ meine Kiste mit Klemmmuffen über Bord fallen! Ich brauche meine Klemmmuffen!$B$BIch weiß nicht genau, wo ich sie fallen ließ, aber vielleicht habt Ihr ja Glück, wenn Ihr im Meer danach fischt...",
    ["O"] = "Bringt 8 Klemmmuffen zu Hexknall Kurbeldreh in Auberdine.",
    ["T"] = "Klemmmuffen",
  },
  [1580] = {
    ["D"] = "Wir wissen, dass Ihr Glück habt, aber... wie viel Glück genau, $N?$B$BEines Nachts schlenderte ich durch die Straßen von Dunkelküste. Ich suchte nach einem geeigneten Fluss oder See, um meine neuen propellerbetriebenen explodierenden Entenattrappen auszuprobieren. Ich muss wohl etwas beschwipst gewesen sein, denn Stunden später wachte ich in Auberdine auf. Mein Haar war versengt und meine Tasche mit den Elektropellern war weg! Sie müssen mir ins Wasser gefallen sein!$B$BIch brauche diese Elektropeller! Vielleicht könnt Ihr sie aus einem See oder Bach in Dunkelküste fischen...",
    ["O"] = "Bringt 12 Elektropeller zu Hexknall Kurbeldreh in Auberdine.",
    ["T"] = "Elektropeller",
  },
  [1581] = {
    ["D"] = "Unser Kräuter- und Alchemieladen floriert, aber mein Mann verbringt so viel Zeit damit, Tränke zu mischen, dass wir nie Zeit füreinander haben.$B$BKönnt Ihr uns helfen?$B$BWenn Ihr mir einen Vorrat an vorgefertigten Elixieren bringt, würde ich mit Freuden mit Euch tauschen. Ich besitze gewisse seltene Kräuter, die Euch nützlich sein könnten.",
    ["O"] = "Bringt 6 Elixiere der Löwenstärke und 2 Elixiere der schwachen Verteidigung zu Syral Messerblatt in Dolanaar.",
    ["T"] = "Elixiere für die Messerblatts",
  },
  [1582] = {
    ["D"] = "Wir in Darnassus, besonders wir Handwerker, sind sehr beschäftigt. Junge Abenteurer brennen darauf, sich in der Wildnis zu beweisen und benötigen unablässig Nachschub.$B$BWenn Ihr mir helft, meine Bestellungen an Lederwaren zu erfüllen, kann ich Euch vielleicht eine hochentwickelte Technik der Lederverarbeitung beibringen.",
    ["O"] = "Bringt 1 Paar geprägte Lederhandschuhe, 5 Paar geprägte Lederstiefel und 5 geprägte Lederumhänge zu Lotherias in Darnassus.",
    ["T"] = "Mondscheinweste",
  },
  [1598] = {
    ["D"] = "Ihr da! $N, richtig?$B$BIch hoffe, Ihr seid sicher, dass Ihr ein Hexenmeister werden möchtet, denn wenn Ihr nicht allein besteht, werdet Ihr gewiss untergehen.$B$BIch werde Euch unterrichten, muss aber zuerst sicher sein, dass Ihr stark genug seid.$B$BEin Novize, der an der Abtei studierte, lief weg und schloss sich der Bruderschaft der Defias an. Warum mich das kümmert? Weil er einige wichtige Bücher gestohlen hat.$B$BEines davon war ein Buch, das ich selbst wollte, Die Macht der Leere. Stehlt es für mich, sie haben es in ihrem Zelt in den Weinbergen versteckt.",
    ["O"] = "Holt Die Macht der Leere für Drusilla La Salle zurück.",
    ["T"] = "Der gestohlene Foliant",
  },
  [1599] = {
    ["D"] = "Ihr müsst der neue Hexenmeister sein, über den alle tuscheln. Jemand muss Euch sehr mögen, wenn man mich bittet, meine Forschungen aufzuschieben und Eure Ausbildung zu übernehmen.$B$BKeine Bange, ich mache Euch keinen Vorwurf.$B$BDer einfachste Beschwörungszauber, den Ihr lernen werdet, ist der für einen Wichtel. Doch bevor ich ihn Euch beibringe, müsst Ihr beweisen, dass Ihr die notwendigen magischen und körperlichen Fähigkeiten besitzt.$B$BBringt mir drei Federglücksbringer von den Novizen der Frostmähnen aus der Höhle im Südwesten.",
    ["O"] = "Bringt 3 Federglücksbringer zu Alamar Grimm in Ambossar.",
    ["T"] = "Anfänge",
  },
  [1618] = {
    ["D"] = "Wir Zwerge sind nicht die Einzigen, die kämpfen. Unsere menschlichen Verbündeten haben auch alle Hände voll zu tun!$B$BDas Rotkammgebirge wird immer wieder von den Schwarzfelsorcs angegriffen und der Vorrat an Ausrüstung ist stets knapp. Ich bin sicher, wenn Ihr ihnen helft, wird der ansässige Schmied, Verner Gutmark, Euch den einen oder anderen Trick beibringen.",
    ["O"] = "Tormus Tiefenschmied möchte, dass Ihr 4 runenverzierte Kupfergürtel und 4 schwere Kupferschlägel zu Verner Gutmark ins Rotkammgebirge bringt.",
    ["T"] = "Die Aufrüstung im Rotkammgebirge",
  },
  [1638] = {
    ["D"] = "Ihr habt Euch als fähiger Krieger erwiesen, $N. Aber Ihr seid noch jung und müsst noch viel lernen.$B$BSprecht mit Harry Burlwacht... er ist meist in der Taverne \"Zum Pfeifenden Schwein\" in der Altstadt von Sturmwind. Harry ist ein alter Veteran und guter Lehrer. Er kann Euch einige Kampftechniken zeigen, die Euer Leben verlängern.",
    ["O"] = "Sprecht mit Harry Burlwacht in Sturmwind.",
    ["T"] = "Die Ausbildung eines Kriegers",
  },
  [1642] = {
    ["D"] = "Ein Paladin muss in allen Dingen das Licht widerspiegeln, das uns Kraft gibt. Wenn einer unserer Art nach dem Göttlichen strebt, bedeutet das nicht, dass er ein Gott sein möchte... wir trachten nur danach, in allem nur Gutes zu tun.$B$BAuch wenn Ihr in diesen schweren Zeiten berufen seid, das Böse zu zerschmettern, dürft Ihr nicht vergessen, dass die Unterstützung anderer Euch wahrhaft von den Bürgern von Azeroth unterscheidet. Mitgefühl, Geduld, Tapferkeit... das alles bedeutet einem Paladin so viel wie Kraft im Kampf.$B$BBedenket das und vergesst es niemals.",
    ["O"] = "Sprecht mit Duthorian Rall in Sturmwind.",
    ["T"] = "Der Foliant der Offenbarung",
  },
  [1643] = {
    ["D"] = "Eure Aufgabe, $N, besteht darin, jemanden hier in Sturmwind zu finden, der der Hilfe bedarf. Jemanden mit reinem Herzen und edlen Motiven. Vielleicht jemanden, der anderen hilft. Vielleicht jemanden, der einen Führer braucht. Was auch immer, die Person muss edle Absichten haben und auf Eure Güte angewiesen sein.$B$BMacht das und kommt zu mir zurück, wenn Ihr fertig seid. Dann sprechen wir weiter über Euren Weg und was es bedeutet, ein Paladin des Lichts zu sein.",
    ["O"] = "Sucht im Handelsdistrikt von Sturmwind nach Stephanie Turner.",
    ["T"] = "Der Foliant der Offenbarung",
  },
  [1644] = {
    ["D"] = "Die meisten der guten Seelen haben keine Familien mehr aufgrund der Angriffe der Geißel im letzten Jahrzehnt.$B$BMein Mann John sammelt die Materialien, die ich brauche, um Hemden und Hosen für die Kinder zu machen, aber nicht einmal dafür hatte ich Zeit. Ich war zu sehr damit beschäftigt, ihnen Essen auf ihre Teller und ein Dach über ihre Köpfe zu geben.$B$BWie war das? Ihr wärt bereit, mir zu helfen? Oh, dem Licht sei Dank und Euch ebenfalls, $N.$B$BWenn Ihr mir etwas Leinen bringen könntet, damit ich den Kindern Kleidung nähen kann, wäre mir das eine große Hilfe.",
    ["O"] = "Bringt 10 Stück Leinenstoff zu Stephanie Turner in Sturmwind.",
    ["T"] = "Der Foliant der Offenbarung",
  },
  [1646] = {
    ["D"] = "Ein Paladin muss in allen Dingen das Licht widerspiegeln, das uns Kraft gibt. Wenn einer unserer Art nach dem Göttlichen strebt, bedeutet das nicht, dass er ein Gott sein möchte... wir trachten nur danach, in allem nur Gutes zu tun.$B$BAuch wenn Ihr in diesen schweren Zeiten berufen seid, das Böse zu zerschmettern, dürft Ihr nicht vergessen, dass die Unterstützung anderer Euch wahrhaft von den Bürgern von Azeroth unterscheidet. Mitgefühl, Geduld, Tapferkeit... das alles bedeutet einem Paladin so viel wie Kraft im Kampf.$B$BBedenket das und vergesst es niemals.",
    ["O"] = "Sprecht mit Tiza Kriegshammer in Eisenschmiede.",
    ["T"] = "Der Foliant der Offenbarung",
  },
  [1647] = {
    ["D"] = "Eure Aufgabe, $N, ist es, jemanden hier in Eisenschmiede zu finden, der der Hilfe bedarf. Jemand mit reinem Herzen und edlen Motiven. Vielleicht jemanden, der anderen hilft. Vielleicht jemanden, der einen Führer braucht. Was auch immer, die Person muss edle Absichten haben und auf Eure Güte angewiesen sein.$B$BMacht das und kommt zu mir zurück, wenn Ihr fertig seid. Dann sprechen wir weiter über Euren Weg und was es bedeutet, ein Paladin des Lichts zu sein. Ich warte hier im Tempel auf Eure Rückkehr. Auch andere Paladine brauchen meine Unterstützung.",
    ["O"] = "Sucht im Äußeren Ring von Eisenschmiede nach John Turner.",
    ["T"] = "Der Foliant der Offenbarung",
  },
  [1648] = {
    ["D"] = "Meine Frau und ich führen das Waisenhaus in Sturmwind, das überwiegend Kindern vorbehalten ist, die im letzten Jahrzehnt ihre Eltern bei Angriffen der Geißel verloren haben. Ich komme etwa einmal im Monat nach Eisenschmiede und versuche, bei den Bürgern hier mehr Vorräte zu sammeln und mit den verschiedenen Gruppen zu reden, ob sie irgendwie helfen können.$B$BDieser Ausflug war schrecklich langwierig und nicht sehr ergiebig.$B$BKönnte ich doch nur noch etwas mehr Leinen bekommen, damit meine Frau Kleidungsstücke nähen kann, dann könnte ich endlich eine Ladung nach Hause schicken.",
    ["O"] = "Bringt 10 Stück Leinenstoff zu John Turner in Eisenschmiede.",
    ["T"] = "Der Foliant der Offenbarung",
  },
  [1649] = {
    ["D"] = "Ehre, und alle Tugenden, die damit einhergehen, sind wie rare Mineralien: man muss sie ihres Wertes wegen bewundern, wenn man sie findet, aber man muss sich auch die Zeit nehmen, sie aufzuheben... zu veredeln. Mischt man sie richtig, entsteht eine starke Legierung, die manchmal selbst die heftigsten Hiebe nicht zerbrechen können.$B$BDiese Kraft, Euer Glauben, Eure Tapferkeit, müssen geprüft werden. Ihr müsst lernen, niemals den Glauben zu verlieren und erkennen, dass Ihr mit der Macht des Lichts Dinge überwinden könnt, vor denen andere klein beigeben müssen.",
    ["O"] = "Sprecht mit Duthorian Rall in Sturmwind.",
    ["T"] = "Der Foliant der Tapferkeit",
  },
  [1650] = {
    ["D"] = "Gut! Da Ihr nicht zögert, seid Ihr gewiss bereit und brennt darauf, Euch zu beweisen. So sei es.$B$BAm äußersten Ende der Endlosen Küste in Westfall steht ein kleines Haus über dem Ozean. Dort lebt ein Paar, die Stillbrunns. Jordan ist ein unglaublicher Schmied und für einige Zeit nach Eisenschmiede gegangen, um sich mit den Zwergen zu treffen. Dadurch ist seine Frau Daphne allein und schutzlos.$B$BEs wird berichtet, dass die Defias fast das gesamte Gebiet übernommen haben. Gut möglich, dass Daphne Schutz vor den Bösewichtern braucht. Geht dorthin.",
    ["O"] = "Sucht Daphne Stillbrunn in Westfall.",
    ["T"] = "Der Foliant der Tapferkeit",
  },
  [1651] = {
    ["D"] = "Ich wette, die kommen bald wieder, also macht Euch bereit.$B$BIch habe eine Schusswaffe im Haus. Ich hole sie und helfe Euch gern, aber Ihr werdet weitgehend auf Euch gestellt sein.$B$BIch habe keine Ahnung, wie viele sie schicken, aber es werden sicher mehr sein als die paar beim letzten Mal.$B$BBittet das Licht um jeden erdenklichen Segen, denn...habt Ihr das gehört? Ich glaube, sie kommen.$B$BWir beziehen Position beim Haus!",
    ["O"] = "Verteidigt Daphne Stillbrunn vor dem Angriff der Defias.$B$BKeiner Eurer Geister darf aus seinen irdischen Fesseln befreit werden, wenn Ihr erfolgreich sein wollt.$B$BSprecht erneut mit Daphne Stillbrunn, wenn Ihr erfolgreich wart.",
    ["T"] = "Der Foliant der Tapferkeit",
  },
  [1652] = {
    ["D"] = "Kehrt bitte zu Duthorian zurück und lasst ihn wissen, dass ich hier vorerst sicher bin. Dafür muss ich Euch und der Kirche danken.$B$BEr wird eine Nachricht nach Eisenschmiede schicken und meinen Jordan wissen lassen, dass er keinen Grund zur Furcht hat.$B$BOh, ich vermisse meinen Mann so sehr. Ich kann es kaum erwarten, bis er wieder da ist.",
    ["O"] = "Sprecht mit Duthorian Rall in Sturmwind.",
    ["T"] = "Der Foliant der Tapferkeit",
  },
  [1653] = {
    ["D"] = "Ich zweifle nicht daran, dass Jordan Euch persönlich danken möchte, und da ich einen Boten brauche, der ihn wissen lässt, dass es seiner Frau gut geht, scheint Ihr mir die perfekte Wahl zu sein.$B$BJordan sollte ein Paladin des Lichts sein, bevor die Liebe zum Beruf seines Vaters ihn einen anderen Weg einschlagen ließ - er ist innig mit unserer Lebensweise vertraut und wird zu unseren Getreuesten gerechnet.$B$BEr müsste noch in Eisenschmiede sein - tagsüber arbeitet er gern vor den Toren, wohl wegen der kühlen Bergluft.",
    ["O"] = "Sprecht mit Jordan Stillbrunn in Eisenschmiede.",
    ["T"] = "Die Prüfung der Rechtschaffenheit",
  },
  [1654] = {
    ["D"] = "Ich würde Euch gern eine Belohnung geben, wenn Ihr bereit seid, eine anzunehmen. Missversteht mich nicht, meine Großzügigkeit hängt davon ab, wie hilfreich Ihr sein könnt, aber ich denke, wir werden beide von einer derartigen Allianz profitieren.$B$BIch werde Euch eine Waffe schmieden, die Eurer Leistung würdig ist, ein Symbol der Paladinschaft und des Lichts, doch zuvor müsst Ihr die Gegenstände sammeln, die ich zu seiner Herstellung brauche. Ich habe schon angefangen, mir Notizen zu den Gegenständen zu machen, und brauche nur jemanden, der tapfer und geschickt genug ist, sie zu beschaffen. Was sagt Ihr dazu, $C?",
    ["O"] = "Sucht mit Jordans Waffennotizen etwas Weißsteineichenholz, Bailors aufbereitete Erzlieferung, Jordans Schmiedehammer und einen Koredelstein und bringt alles zusammen zu Jordan Stillbrunn in Eisenschmiede.",
    ["T"] = "Die Prüfung der Rechtschaffenheit",
  },
  [1655] = {
    ["D"] = "Ach, Ihr habt mich erschreckt, $G Mann:Frau;! Seht Ihr nicht, dass ich nachdenke?$B$BWie war das? Jordans Lieferung... für Eisenschmiede. Ahhh, ja, ich weiß, wovon Ihr sprecht. Oger der Mo\'grosh haben die Erzlieferung meines alten Freundes gestohlen. Die Karawane war auf dem Weg vom Sumpfland hierher und geriet in einen Hinterhalt. Die Wache, die den Angriff überlebte, starb vor wenigen Tagen - der Heiler kam nicht schnell genug hin.$B$BWenn Ihr die Lieferung wollt, müsst Ihr über den Loch nach Nordosten. Dort muss sie irgendwo sein.",
    ["O"] = "Bringt Jordans Erzlieferung zu Bailor Steinhand in Loch Modan.",
    ["T"] = "Bailors Erzlieferung",
  },
  [1656] = {
    ["D"] = "$N, bitte, ich flehe Euch an, könnt Ihr mir helfen? Ich habe meine Aufgaben hier auf der Ebene noch nicht beendet - ich bin mitten in meinem Ritual der Kraft - und mein Vater bat mich, diese Felle im Gasthaus der Bluthufe abzuliefern.$B$BIch bin längst nicht erholt genug, mein Ritual und diese Aufgabe für meinen Vater zu beenden. Würdet Ihr an meiner statt gehen?$B$BDas Gasthaus ist angenehm und eine perfekte Oase der Ruhe auf langen Reisen. Ich bin sicher, Ihr stimmt mir zu, wenn Ihr es seht.",
    ["O"] = "Bringt das Bündel Felle zu Gastwirt Kauth ins Dorf der Bluthufe.",
    ["T"] = "Eine unvollendete Aufgabe",
  },
  [1657] = {
    ["D"] = "Feiert die Schlotternächte mit Stil... bereitet unseren Feinden in Süderstade Kummer!$B$BNehmt eine dieser speziell angefertigten Stinkbomben. Sie sind mit einem derartig furchtbaren Schrecken gefüllt, dessen Geruch kein Mensch - oder anderes Wesen mit schwachem Gemüt - ertragen kann. Ihr müsst die Stinkbombe direkt ins Herz von Süderstade werfen, damit sie richtig wirken kann. Seid also auf eine Auseinandersetzung mit der Allianz gefasst!$B$BSolltet Ihr diese Aufgabe bewältigt haben, kehrt zu mir zurück... es ist ein Beitrag zu unserer Befreiung!",
    ["O"] = "Werft drei Stinkbomben der Verlassenen ins Herz von Süderstade im Hügelland und kehrt zu Ruferin der Dunkelheit Yanka beim Fest des Weidenmanns in Tirisfal zurück.",
    ["T"] = "Die Verpestung von Süderstade",
  },
  [1658] = {
    ["D"] = "Macht das Fest des Weidenmanns der Verlassenen in Tirisfal ausfindig. Kehrt zu Unteroffizier Hartman in Süderstade zurück, sobald Ihr Eure Mission erfüllt habt.",
    ["O"] = "Macht das Fest des Weidenmanns der Verlassenen in Tirisfal ausfindig. Kehrt zu Unteroffizier Hartman in Süderstade zurück, sobald Ihr Eure Mission erfüllt habt.",
    ["T"] = "Bespitzelt das Fest des Weidenmanns",
  },
  [1659] = "_",
  [1660] = "_",
  [1661] = {
    ["D"] = "Ich grüße Euch erneut, $N. Ich bin stolzer als jemals zuvor, Euch $G Bruder:Schwester; nennen zu können - Euer Engagement ist ein Tribut an uns alle.$B$BDie Zeit ist für mich gekommen, Euch ein wunderbares Geschenk zu übergeben. Wisset aber, dass ich es Euch fürs Erste zwar frei gebe, später jedoch, falls die Zeit es erlaubt, wird dies eine viel größere Herausforderung für alle Paladine sein, die sich dazu entscheiden, meinen Auftrag anzunehmen.$B$BSprecht erneut mit mir und ich werde Euch einen großen Segen gewähren.",
    ["O"] = "Sprecht mit Duthorian Rall in Sturmwind.",
    ["T"] = "Der Foliant des Adels",
  },
  [1665] = {
    ["D"] = "Okay, hier ist mein Becher. Wartet! Lasst mich zuerst austrinken... da haben wir es.",
    ["O"] = "Bringt Bartlebys Becher zu Burlwacht.",
    ["T"] = "Bartlebys Becher",
  },
  [1666] = {
    ["D"] = "Ihr habt einige Kampftechniken gelernt, braucht aber noch eine anständige Waffe. Ich wette, Marschall Haggard könnte Euch helfen, wenn Ihr mit ihm sprecht.$B$BMarschall Haggard lebt in einem Anwesen im Wald von Elwynn, beim Holzfällerlager des Osttals. Er ist jetzt im Ruhestand, hat aber in seiner Laufbahn eine Menge Ausrüstung gesammelt.$B$BLasst ihn wissen, dass ich Euch geschickt habe, und mit etwas Glück hat er eine Waffe für Euch übrig.",
    ["O"] = "Sprecht mit Marschall Haggard.",
    ["T"] = "Marschall Haggard",
  },
  [1667] = {
    ["D"] = "Burlwacht hat Euch geschickt, nicht? Ja, ich habe Waffen übrig, aber zuerst habe ich eine Aufgabe für Euch...$B$BVor einer Weile überlistete mich ein Bösewicht der Defias, Stumpfzahn Jack. Es war dunkel und er trug einen Wappenrock von Sturmwind, daher dachte ich, er sei Marschall Dughan. Er sagte, er braucht meinen alten Marschallstern aus Sturmwind und ich gab ihm diesen... dann lachte er und lief weg, ehe ich ihn packen konnte!$B$BBringt mir meinen Stern! Stumpfzahn Jack hat ihn wahrscheinlich in seinem Lager im Süden versteckt.",
    ["O"] = "Holt Marschall Haggards Abzeichen aus Stumpfzahn Jacks Lager und kehrt zu Marschall Haggard zurück.",
    ["T"] = "Stumpfzahn Jack",
  },
  [1678] = {
    ["D"] = "Bringt Vejreks Kopf zu Muren Sturmlanze in Eisenschmiede.",
    ["O"] = "Bringt Vejreks Kopf zu Muren Sturmlanze in Eisenschmiede.",
    ["T"] = "Vejrek",
  },
  [1679] = {
    ["D"] = "Hallo, Krieger! Ihr seid viel versprechend, aber dennoch müsst Ihr ein paar Tricks lernen, und Muren Sturmlanze ist genau der Zwerg, der sie Euch beibringen kann.$B$BIhr findet Muren in der Halle der Waffen im Militärviertel von Eisenschmiede. Sprecht mit ihm, wenn Ihr bereit seid zu lernen, was es heißt, ein Krieger zu sein.",
    ["O"] = "Sprecht mit Muren Sturmlanze.",
    ["T"] = "Muren Sturmlanze",
  },
  [1680] = {
    ["D"] = "Jetzt, da Ihr einige Kampftechniken gelernt habt, wird es Zeit, Euch eine anständige Waffe zu beschaffen. Sprecht mit Tormus Tiefenschmied. Er kann Euch eine Waffe machen, die Euch sicher gefallen wird.$B$BIhr findet Tormus hier in Eisenschmiede bei der Großen Schmiede.",
    ["O"] = "Sprecht mit Tormus Tiefenschmied.",
    ["T"] = "Tormus Tiefenschmied",
  },
  [1681] = {
    ["D"] = "Mein Mentor Sturgy Eisenband hatte ein Lager südlich des Helmsbettsees im östlichen Dun Morogh. Dort lehrte er die Schmiedekunst und er war der Beste.$B$BAber eines Tages brachte ihm einer seiner Schüler eine Ladung mit seltsamem Erz. Meister Eisenband nannte es Umbralerz und konnte es kaum erwarten, damit zu arbeiten. Doch kurz nach der Lieferung griff eine Bande Dunkeleisenzwerge sein Lager an. Sie töteten alle!$B$BBegebt Euch zu Eisenbands Lager, $N, und rächt den Tod meines Meisters. Und bringt mir die Ladung Umbralerz, die all das auslöste.",
    ["O"] = "Bringt eine Ladung Umbralerz zu Tormus Tiefenschmied in Eisenschmiede.",
    ["T"] = "Eisenbands Truppenlager",
  },
  [1683] = {
    ["D"] = "Bevor ich Euch unterrichten kann, müsst Ihr mir Eure Entschlossenheit beweisen.$B$BDer Satyr Vorlus Elendshuf hat einen unserer entlegenen Mondbrunnen südöstlich der Grabhügel von Ban\'ethil gefunden. Nun planscht er darin und verseucht das reine Wasser mit seinem Schmutz.$B$BSucht Vorlus beim Mondbrunnen und vernichtet ihn. Bringt mir sein Horn als Beweis, dann beginne ich Eure Fortgeschrittenen-Ausbildung zum Krieger.$B$BDer Weg zum Mondbrunnen ist gut verborgen, $N, doch wenn Ihr konzentriert und wachsam bleibt, werdet Ihr ihn finden.",
    ["O"] = "Bringt das Horn von Vorlus zu Elanaria in Darnassus.",
    ["T"] = "Vorlus Elendshuf",
  },
  [1685] = {
    ["D"] = "Läuft es nicht immer so? Ich habe den ganzen Tag nach Euch gesucht und konnte Euch nicht finden, und jetzt, da ich es nicht erwarte, kommt Ihr einfach zu mir spaziert.$B$BIch habe genug Zeit mit der Suche nach Euch vergeudet, daher möchte ich gleich zur Sache kommen. Meister Gakin hat Euch zu weiterer Ausbildung gerufen. Mir scheint, Ihr solltet ihn so rasch wie möglich aufsuchen.$B$BIhr findet ihn im Keller der Taverne \'Zum Geschlachteten Lamm\'. Übrigens solltet Ihr diesen Treffpunkt nicht an die große Glocke hängen. Muss ja nicht jeder wissen.",
    ["O"] = "Meldet Euch bei Gakin dem Dunkelbinder im Magierviertel von Sturmwind.",
    ["T"] = "Gakins Beschwörung",
  },
  [1686] = {
    ["D"] = "Ihr habt viel gelernt, $N. Jetzt fertigen wir Euch eine Waffe an. Die Waffe eines Kriegers.$B$BDazu müsst Ihr nach Dunkelküste reisen.$B$BVor langer Zeit zerschellte ein Schiff auf den Felsen beim Leuchtturm von Auberdine. Viele Kisten Eluniterz sanken auf den Meeresgrund.$B$BAls Erstes müsst Ihr den Wächter des Elunits besiegen, Eluras Schemen. Sie war einst Kapitän des Schiffes und verfluchte sich selbst dazu, für alle Zeiten in der Tiefe zu verweilen und ihre Schiffsladung zu schützen. Besiegt sie, nehmt Ihr das Medaillon ab und sammelt die verlorenen Elunitkisten ein.",
    ["O"] = "Bringt 8 Ladungen Eluniterz und das Medaillon von Elura zu Elanaria in Darnassus.",
    ["T"] = "Eluras Schemen",
  },
  [1688] = {
    ["D"] = "Ich hatte eine Studentin namens Surena Caledon. Sie stand da, wo Ihr jetzt steht, und brannte darauf, die Hexenmeistermagie zu lernen, und ihr Talent war nicht von schlechten Eltern. Mehr als das, sie war jung und eine wahre Augenweide. Hätte ich sie doch nur damals schon durchschaut, die falsche Schlange!$B$BDas diebische Weib brannte mit einem Schläger durch, Erlan Tragmehr. Ihr Verlust ist unbedeutend, aber ich schenkte ihr einen Blutsteinhalsschmuck, den ich wiederhaben muss.$B$BIhr Leben hat keine Bedeutung mehr für mich. Ihr findet sie bei Brackbrunns Kürbisbeet. Bringt wieder, was mir gehört.",
    ["O"] = "Beschafft Surenas Halsreif für Gakin den Dunkelbinder in Sturmwind.",
    ["T"] = "Surena Caledon",
  },
  [1689] = {
    ["D"] = "Ihr habt meinen Halsreif zurückgebracht und Surena bezwungen und damit gezeigt, dass Ihr mindestens ebenso begabt seid, wie sie es war.$B$BNichtsdestoweniger kann ich Euch versichern, dass Ihr dringend Ausbildung benötigt. Ihr meistert den Fluss der Magie, ja, aber zu einem Hexenmeister gehört viel mehr als das.$B$BIch zeige Euch, wie man einen Leerwandler beherrscht, aber bevor ich das kann, müsst Ihr einen am Beschwörungskreis herbeirufen und mit allen Euch zur Verfügung stehenden Waffen unterwerfen. Benutzt die Magie dieses Halsschmuckes unten am Beschwörungskreis.",
    ["O"] = "Beschwört mithilfe des Blutsteinhalsreifs einen Leerwandler, unterwerft ihn und bringt dann den Blutsteinhalsreif zu Gakin dem Dunkelbinder in Sturmwind zurück.",
    ["T"] = "Die Bindung",
  },
  [1690] = {
    ["D"] = "Ihr da! Für Euch ist es an der Zeit, ein großer Helfer der Gadgetzan Water Company zu sein.$B$BDie Wüstenläufernomaden draußen in der Tanariswüste haben fast alle Brunnen erobert! Ohne Zugang zu den Brunnen wird unser Wasservorrat im Handumdrehen schwinden. Wenn Ihr Euch unsere Dankbarkeit verdienen wollt, dann helft uns, diese Nomaden der Gerechtigkeit zuzuführen!$B$BBegebt Euch von hier Richtung Osten und schaltet zehn Banditen und Diebe der Wüstenläufer aus und meldet Euch dann umgehend wieder bei mir. Gerechtigkeit wartet auf keinen Mann... keinen Goblin... ach, legt einfach los!",
    ["O"] = "Erledigt 10 Banditen und Diebe der Wüstenläufer östlich von Gadgetzan und meldet Euch dann wieder bei Chefingenieur Bilgenritzel.",
    ["T"] = "Wüstenläufergerechtigkeit",
  },
  [1691] = {
    ["D"] = "Wir müssen uns jetzt die stärkeren Ränge der Wüstenläufernomaden vornehmen. Ihr habt bisher gute Arbeit geleistet, aber wenn wir diese Brunnen zurückerobern wollen, dann wäre es gut, wenn uns dabei so wenig Nomaden wie möglich gegenüberstünden!$B$BDieses Mal müsst Ihr ihre Schurken, Auftragsmörder und Schattenmagier erledigen. Ihr werdet sie östlich und südöstlich von hier finden.$B$BIch werde Euch nichts vormachen - das ist eine schlimme Bande. Tut dies für uns, dann sind wir auf dem besten Weg, uns das zurückzuholen, was rechtmäßig uns gehört!",
    ["O"] = "Erledigt 10 Schattenmagier der Wüstenläufer, 8 Schurken der Wüstenläufer und 6 Auftragsmörder der Wüstenläufer für Chefingenieur Bilgenritzel in Gadgetzan.",
    ["T"] = "Noch mehr Wüstenläufergerechtigkeit",
  },
  [1699] = {
    ["D"] = "Ich nenne meine Herausforderung den Rethbanspießrutenlauf. Um die Prüfung zu bestehen, müsst Ihr lediglich die Rethbanhöhlen betreten, bis zur ersten Gabelung gelangen und hierher zurückkehren. Ganz einfach! Aber es gibt da ein paar Hürden...$B$BIhr müsst binnen einer Stunde zurück sein.$B$BGanze Heerscharen von Rotkammgnollen versperren den Weg.$B$BVor dem Start müsst Ihr einen Schluck des Stammgetränks meiner Familie zu Euch nehmen: Gerstenbräu!$B$BWas sagt Ihr dazu? Die Rethbanhöhlen liegen im Vorgebirge nördlich von Seenhain. Seid Ihr bereit, am Rethbanspießrutenlauf teilzunehmen?",
    ["O"] = "Betretet die Rethbanhöhlen, geht bis zur ersten Gabelung und kehrt innerhalb der zulässigen Zeit zu Yorus Gerstenbräu zurück.$B$BIhr dürft nicht sterben und Euren Geist befreien.",
    ["T"] = "Der Rethbanspießrutenlauf",
  },
  [1700] = {
    ["D"] = "Ich fertigte eine Kopie meiner Notizen über die Techniken an, die ich lernte, während ich Eure Rüstung herstellte. Sie muss zu Grimand Elmore geschickt werden. Er ist hier im Zwergendistrikt, im Waffenladen.$B$BUnd duckt Euch, wenn Ihr ihm die gebt. Er bildet sich ungeheuer viel auf seine Fertigkeiten ein. Es wird ihm nicht gefallen, etwas von mir zu lernen...",
    ["O"] = "Bringt Furens Notizen zu Grimand Elmore.",
    ["T"] = "Grimand Elmore",
  },
  [1701] = {
    ["D"] = "Ich gab Euch einen guten Schild, aber dabei muss es nicht bleiben. Ich kenne das Geheimnis, wie man extrem harte Panzerrüstungen herstellt. Es ist nicht leicht und erfordert sehr rare Materialien, aber wenn Ihr bereit seid, die Kleinarbeit zu machen, fertige ich ein Teil für Euch an.$B$BWas sagt Ihr dazu, $N? Wenn Ihr Euch der Herausforderung gewachsen fühlt, gebe ich Euch eine Liste, was Ihr beschaffen müsst und wo Ihr es bekommt.",
    ["O"] = "Sammelt die Materialien, die Furen Langbart benötigt, und bringt sie zu ihm nach Sturmwind.",
    ["T"] = "Feuergehärteter Panzer",
  },
  [1702] = {
    ["D"] = "Ihr habt echt gezeigt, aus welchem Holz Ihr geschnitzt seid, als Ihr am Rethbanspießrutenlauf teilgenommen habt, $N. Und ich stehe zu meinem Wort. Ihr bekommt die versprochene Belohnung...$B$BAber sie ist in Sturmwind.$B$BSprecht mit meinem Freund Furen Langbart im Zwergendistrikt von Sturmwind. Er ist der beste Schildschmied, den Ihr je gesehen habt, und er schuldet mir einen großen Gefallen.$B$BHier, bringt Furen dieses Fässlein mit Gerstenbräu und bittet ihn um einen Schild. Er wird Euch einen schönen geben.",
    ["O"] = "Bringt das Fässlein mit Gerstenbräu zu Furen Langbart.",
    ["T"] = "Der Schildschmied",
  },
  [1704] = {
    ["D"] = "Ich fertigte eine Kopie meiner Notizen über die Techniken an, die ich lernte, während ich Eure Rüstung herstellte, und brauche jemanden, der sie zu Klockmort Spannsplint bringt. Er ist ein gnomischer Schmied in Eisenschmiede, und wenn er herausfindet, was ich herausgefunden habe...Ich wette, er reißt sich vor Aufregung selbst den Bart aus.",
    ["O"] = "Bringt Furens Notizen zu Klockmort Spannsplint.",
    ["T"] = "Klockmort Spannersplint",
  },
  [1705] = {
    ["D"] = "Ich gebe nur ungern zu, dass Furen tatsächlich etwas gefunden haben könnte. Aber um ganz sicher zu sein, muss ich seine Techniken an einem neuen Rüstungsteil erproben. Wenn Ihr mir mit den Materialien helft, $N, könnt Ihr die Rüstung haben.$B$BUm die Rüstung herzustellen benötige ich Proben von brennendem Blut und brennendem Felsen.$B$BDie findet man meines Wissens nur im Dämmerwald. Die Worgs der Nachtheuler in der Mine von Rolands Verdammnis haben brennendes Blut, und Bauchreißer, ihr Anführer, wird den Fels haben.$B$BMöge das Schicksal Euch Schnelligkeit gewähren $N.",
    ["O"] = "Bringt 20 Phiolen brennendes Blut und 1 brennenden Fels zu Grimand Elmore in Sturmwind.",
    ["T"] = "Brennendes Blut",
  },
  [1707] = {
    ["D"] = "Seid gegrüßt, $N, mein Name ist Lochbeutel, und wir haben einen wichtigen Auftrag für Euch, wenn Ihr bereit seid! Hussah!$B$BNichts würde mir als altgedientem Leitungshahnmaschinisten mehr gefallen, als Euch jeden Wasserbeutel eines Wüstenläufers abzunehmen, den Ihr draußen in der Wüste findet. Das ist ja ohnehin unser Wasser - die Nomaden haben es einfach nur gestohlen! Die Gadgetzan Water Company bietet Euch ein offizielles Überlebenspaket für jeweils fünf, die Ihr abliefert. Am wichtigsten ist aber, dass Ihr Gadgetzan selbst helft!",
    ["O"] = "Bringt Leitungshahnmaschinist Lochbeutel in Gadgetzan 5 Wasserbeutel eines Wüstenläufers.",
    ["T"] = "Wasserbeutelkopfgeld",
  },
  [1708] = {
    ["D"] = "Ich möchte Furen Langbarts neue Techniken an einer eigenen Rüstung erproben. Und weil Ihr Furen geholfen habt und er mir, sollt Ihr die Rüstung bekommen, wenn sie fertig ist!$B$BUm das Teil zu beenden, brauche ich eine spezielle Koralle, Sengkoralle. Sie wächst nur an einem Ort - an der Küste südlich des Hafens von Menethil im Sumpfland.$B$BBringt mir genügend Sengkoralle und ich mache mich gleich an die Arbeit. Und beeilt Euch, $N. Ich brenne so sehr darauf, endlich anzufangen, dass meine Zehen jucken!",
    ["O"] = "Bringt 20 Ladungen Sengkoralle zu Klockmort Spannsplint in Eisenschmiede.",
    ["T"] = "Eisenkoralle",
  },
  [1710] = {
    ["D"] = "Mein Kollege, Furen Langbart, hat eine Entdeckung gemacht, die ich schnellstens ausprobieren möchte. Ich möchte sie an einer neuen Rüstung erproben, an der ich forsche, denn ich glaube, Furens Techniken werden grandiose Ergebnisse erzielen.$B$BWerdet Ihr mir helfen? Wenn Ihr sammelt, was ich zur Herstellung der Rüstung brauche, fertige ich für Euch auch eine an.$B$BIch brauche sonnenverbrannte Eierschalen der Steilhangwyvern, die am Steilhang in Tausend Nadeln hausen. Sucht unter den Nestern nach den Schalen und kommt zu mir zurück, wenn Ihr eine ausreichende Menge habt.",
    ["O"] = "Bringt 20 sonnenverbrannte Schalen zu Mathiel in Darnassus.",
    ["T"] = "Sonnenverbrannte Schalen",
  },
  [1713] = {
    ["D"] = "Ich bin bereit, das Ritual zur Beschwörung Cyclonians durchzuführen. Wenn er beschworen ist, müsst Ihr ihn besiegen und sein Wirbelherz nehmen. Ich werde es benutzen, um eine Waffe mit Cyclonians Macht zu stärken.$B$B$N, seid Ihr bereit, dem Windlord zu trotzen?",
    ["O"] = "Bringt das Wirbelherz zu Bath\'rah dem Windbehüter.",
    ["T"] = "Die Beschwörung",
  },
  [1715] = {
    ["D"] = "Hmph. Früher wurden alle Hexenmeister in Khaz Modan unter der Aufsicht der Meister von Gnomeregan ausgebildet, doch jetzt, wo wir gezwungen wurden, in dieses Rattenloch in Eisenschmiede zu ziehen und Magni beschloss, uns genau zu beobachten, sind wir gezwungen, unsere Novizen zur Ausbildung nach Sturmwind zu schicken.$B$BZum Glück ist Gakin kein Tölpel, daher werdet Ihr lernen, was Ihr wissen müsst. Dennoch bringt es mein Blut zum Kochen!$B$BWie auch immer, Ihr findet Gakin im Keller des Geschlachteten Lamms in Sturmwind.",
    ["O"] = "Meldet Euch bei Gakin dem Dunkelbinder im Magierviertel von Sturmwind.",
    ["T"] = "Zum Geschlachteten Lamm",
  },
  [1716] = {
    ["D"] = "Ein Sukkubus ist ein Verschlinger der Seelen, Zerstörer der Herzen, Bezwinger des Geistes. Er kennt nur das Böse.$B$BDies ist die Kreatur, die Ihr beschwören und beherrschen lernt, doch ohne Anreiz kann sie nicht in unsere Welt gebracht werden.$B$BMit einem Symbol der Liebe als Köder wird er gewiss auf Euren Ruf reagieren. Dann könnt Ihr ihn besiegen und zu Eurem Werkzeug machen.$B$BEs wird allerdings schwer sein, etwas dieser Art zu finden. Ihr solltet mit dem Seher Takar sprechen. Ihr findet ihn im Brachland von Kalimdor nahe dem Camp Taurajo.",
    ["O"] = "Sprecht mit Takar dem Seher im Brachland.",
    ["T"] = "Verschlinger der Seelen",
  },
  [1717] = {
    ["D"] = "$N! Ich hätte nicht erwartet, Euch in der Gegend zu sehen, wurde aber angewiesen, nach Euch Ausschau zu halten.$B$BHöchste Zeit, dass Eure Ausbildung fortgesetzt wird. Beim nächsten Besuch in Sturmwind solltet Ihr bei Gakin reinschauen. Er wird Euch erwarten.",
    ["O"] = "Meldet Euch bei Gakin dem Dunkelbinder im Magierviertel von Sturmwind.",
    ["T"] = "Gakins Beschwörung",
  },
  [1718] = {
    ["D"] = "Euer Ruf als Krieger wächst, $N. Jetzt ist es Zeit, Euch mit Euresgleichen zu messen und festzustellen, was Ihr könnt.$B$BVor der Küste des Brachlandes, südlich von Ratschet, liegt eine Insel. Sie heißt Prügeleiland und ist ein Treffpunkt für Krieger.$B$BGeht dorthin und kämpft.$B$BSprecht mit Klannoc Macleod, dem obersten Krieger der Insel. Wenn er Euch für würdig erachtet, wird Klannoc Euch weiter auf dem Pfad des Kriegers führen.$B$BUm das Prügeleiland zu erreichen, müsst Ihr nach den Sandbankuntiefen entlang der Händlerküste suchen.",
    ["O"] = "Sprecht mit Klannoc Macleod.",
    ["T"] = "Der Eiländer",
  },
  [1719] = {
    ["D"] = "Wenn Ihr von mir lernen wollt, müsst Ihr zuerst den Ringkampf absolvieren. Das ist eine Herausforderung hier auf dem Prügeleiland, an der wir aufstrebende Krieger teilnehmen lassen, die sich für gut genug halten. Wenn Ihr gewinnt, dann weiß ich, dass Ihr es tatsächlich seid.$B$BGeht als Erstes mitten in diese Menge dort und stellt Euch auf den Rost. Dadurch weiß Twiggy Flachkopf, dass Ihr bereit seid, und er schickt Euch Herausforderer.$B$BWenn Ihr alle Herausforderer besiegt habt, ruft er Big Will. Tötet Big Will und kehrt innerhalb der von uns gesetzten Frist zu mir zurück.",
    ["O"] = "Tötet Big Will und sprecht mit Klannoc Macleod auf dem Prügeleiland.",
    ["T"] = "Der Ringkampf",
  },
  [1738] = {
    ["D"] = "Als Vorbereitung auf Eure Ankunft nutzte ich mein zweites Gesicht, um über die Welt und hinter die Nebel der Zeit zu schauen. Ich habe herausgefunden, wonach Ihr sucht.$B$BIm Krieg der Ahnen fielen zwei Liebende gemeinsam im Kampf. Wo sie ihren letzten Seufzer taten, wuchs ein als Herzholz bekannter Baum als Symbol ihrer unsterblichen Liebe.$B$BEr sollte genügen, um einen Sukkubus anzulocken. Ihr findet ihn in den Ruinen von Ordil\'Aran im nordwestlichen Teil des Waldes vom Eschental.",
    ["O"] = "Holt das Herzholz aus dem Eschental und bringt es zu Gakin dem Dunkelbinder im Magierviertel von Sturmwind.",
    ["T"] = "Herzholz",
  },
  [1739] = {
    ["D"] = "Mit dem Herzholzkern könnt Ihr einen Zauber am Beschwörungskreis unten in der Gruft wirken und ein Portal öffnen, durch das der Sukkubus kommen kann.$B$BSeid vorsichtig, $N, Sukkuben haben schon den stärksten Geist unterjocht.$B$BWenn Ihr ihn bezwungen und in die Leere zurückgeschickt habt, kommt wieder zu mir, dann zeige ich Euch, wie Ihr ihn rufen und kontrollieren könnt.",
    ["O"] = "Beschwört mithilfe des Herzholzkerns einen Sukkubus, unterwerft ihn und bringt dann den Herzholzkern zu Gakin dem Dunkelbinder im Geschlachteten Lamm zurück.",
    ["T"] = "Die Bindung",
  },
  [1740] = {
    ["D"] = "Wenn Dämonen der Legion in Ungnade fallen, wird ihr Geist in Kugeln gefangen und an ihre Magier und Hexenmeister verteilt.$B$BViele wurden von ihren Bediensteten in Azeroth getragen. Selbst als Bruchstücke besitzen sie die Macht der darin gefangenen Dämonen.$B$BDie Kugel von Soran\'ruk wurde in zwei Teile zerbrochen. Eines davon wurde abermals zerbrochen und von den Akolythen des Schattenhammers in der Tiefschwarzen Grotte benutzt. Das andere, größere Teil besaß ein Hexer in der Burg Schattenfang. Sucht sie, dann kann ich sie zusammenfügen...",
    ["O"] = "Sucht 3 Fragmente von Soran\'ruk und 1 großes Fragment von Soran\'ruk und bringt sie zu Doan Karhan im Brachland.",
    ["T"] = "Die Kugel von Soran\'ruk",
  },
  [1758] = {
    ["D"] = "Mehr als ein einfacher Beschwörungskreis wird erforderlich sein, um einen Teufelsjäger als Herausforderer für Euch herzubringen.$B$BMeine drei Akolythen und ich helfen, die magischen Kreise zu erschaffen. Ihr benötigt eine Kopie des Folianten der Kabale, der seit geraumer Zeit als verschollen gilt. Er wurde von der ersten Gruppe Hexenmeister geschrieben, die ohne Unterstützung einen Teufelsjäger beschwörten.$B$BVielleicht kann Krom Starkarm Euch dabei helfen. Heute ist er ein fahrender Hexenmeister, hortet alte Schriften und hängt in der Bibliothek von Eisenschmiede herum.",
    ["O"] = "Sprecht mit Krom Starkarm in Eisenschmiede.",
    ["T"] = "Foliant der Kabale",
  },
  [1778] = {
    ["D"] = "Ich werde bald Pläne für meine Heimkehr machen. Hoffentlich geht es Stephanie gut in Sturmwind. Ich kann es kaum erwarten, sie wiederzusehen. Auf bald, $C.",
    ["O"] = "Sprecht mit Tiza Kriegshammer in Eisenschmiede.",
    ["T"] = "Der Foliant der Offenbarung",
  },
  [1779] = {
    ["D"] = "Ich möchte, dass Ihr das nehmt, $N. Es heißt Symbol des Lebens.$B$BIhr habt bewiesen, dass Ihr barmherzig und geduldig seid. Jetzt sollt Ihr mir zeigen, wie versiert Ihr geworden seid, was Gefecht und Macht angeht.$B$BMein Mann Muiredon kehrte jüngst von einer Begegnung mit Dunkeleisenzwergen zurück und kam gerade noch mit dem Leben davon - er ist da drüben und einer unserer Priester heilt schon seine Wunden.$B$BNehmt das Symbol und redet mit ihm. Wenn Ihr ihm helfen könnt, dann sprechen wir uns bald wieder. Möge das Licht auf Euch scheinen.",
    ["O"] = "Nehmt das Symbol des Lebens und sprecht mit Muiredon Kriegshammer in Eisenschmiede.",
    ["T"] = "Der Foliant der Offenbarung",
  },
  [1780] = {
    ["D"] = "Ich bringe das noch heute zu den Kindern. Bitte bleibt unversehrt, $C. Es kommt nicht oft vor, dass $GMänner:Frauen; Eures Charakters in der Stadt auftauchen, doch Ihr habt bewiesen, dass die Paladine in Sturmwind die Bedürftigen nicht im Stich lassen.$B$BLebt wohl.",
    ["O"] = "Sprecht mit Duthorian Rall in Sturmwind.",
    ["T"] = "Der Foliant der Offenbarung",
  },
  [1781] = {
    ["D"] = "Nehmt das, $N. Es heißt Symbol des Lebens.$B$BIhr habt bewiesen, dass Ihr barmherzig und geduldig seid. Jetzt ist die Zeit gekommen, um festzustellen, ob Ihr stark genug seid für die größte Gabe des Lichts.$B$BMein Freund Gazin kehrte jüngst von einer Begegnung mit Defias in Elwynn nahe Heldenwache zurück - er kam gerade so mit dem Leben davon. Er ist in der Nähe des Altars, wo unsere Priester seine Wunden heilen.$B$BNehmt das Symbol und sprecht mit ihm. Wenn Ihr ihm helfen könnt, dann sprechen wir uns bald wieder. Möget Ihr Euch im Schein des Lichts sonnen.",
    ["O"] = "Bringt das Symbol des Lebens zu Gazin Tenorm in Sturmwind.",
    ["T"] = "Der Foliant der Offenbarung",
  },
  [1783] = {
    ["D"] = "Er spionierte einigen Dunkeleisenzwergen nach, die sich südlich des Helmsbettsees eingebunkert hatten. Wir wussten, dass sie nichts Gutes im Schilde führten, daher dachten Narm und ich, wir sollten der Sache nachgehen - wir sahen, wie ihr Anführer seinen Männern Befehle gab.$B$BNarm und ich wollten einige töten, aber sie waren stärker als wir dachten und es wurden immer mehr. Narm sagte mir, ich solle Verstärkung holen, und hier bin ich.$B$BNarm braucht Hilfe und ich werde erst ruhen, wenn er sie bekommt.$B$BKönnt Ihr helfen, $N?",
    ["O"] = "Nehmt das Symbol des Lebens und erweckt Narm Faulk in Dun Morogh wieder zum Leben.",
    ["T"] = "Der Foliant der Offenbarung",
  },
  [1784] = {
    ["D"] = "Bevor ich im Kampf fiel, hatten Muiredon und ich fast einen der Spione der Dunkeleisenzwerge in der Nähe dieses kleinen Hauses besiegt. Wir wurden Zeugen, wie ihr Hauptmann ihnen Befehle weitergab, und wollten sie töten und die Befehle an uns nehmen.$B$BEinige im Rat von Eisenschmiede glauben nicht, dass sich Dunkeleisenzwerge in Dun Morogh aufhalten. Bekämen wir eines dieser Manuskripte, hätten wir nicht nur den Beweis für ihre Tätigkeiten, sondern möglicherweise auch eine Vorstellung dessen, was sie planen.$B$BBeschafft eines dieser Manuskripte und bringt es zu Muiredon, $N.",
    ["O"] = "Beschafft ein Dunkeleisenmanuskript von den Dunkeleisenzwergen beim Helmsbettsee und kehrt zu Muiredon Kriegshammer in Eisenschmiede zurück.",
    ["T"] = "Der Foliant der Offenbarung",
  },
  [1785] = {
    ["D"] = "Ich sehe, meine Frau steht schon da drüben und platzt vor Stolz. Schleppt Euch besser mal hier rüber, aber pronto. Könnte mir denken, dass sie Euch was zu sagen hat.$B$B\'s Licht soll mit Euch sein, $C. Wenn die Zeit reif ist, such ich nach Euch auf den Schlachtfeldern, wenn es gegen die Geißel und die Dunkeleisenzwerge geht.",
    ["O"] = "Sprecht mit Tiza Kriegshammer in Eisenschmiede.",
    ["T"] = "Der Foliant der Offenbarung",
  },
  [1786] = {
    ["D"] = "Henze und ich schafften es nach Heldenwache, einer Insel inmitten des Steinhügelsees im nordöstlichen Elwynn. Wir hörten, dass Defias unsere Grenzen unterwandert hatten und wollten einen Beweis für die Berater des Königs.$B$BAls wir eine Bande dieses Abschaums sahen, die Dokumente durchlasen, griffen wir an. Leider wurden wir recht schnell durch ihre Magier überwältigt.$B$BHenze wehrte sie ab, derweil ich entkam...der elende Narr muss immer den Helden spielen.$B$BEr ist jetzt da draußen, der gute Henze. Könnt Ihr uns helfen, $N?",
    ["O"] = "Nehmt das Symbol des Lebens und erweckt Henze Faulk in Elwynn wieder zum Leben.",
    ["T"] = "Der Foliant der Offenbarung",
  },
  [1787] = {
    ["D"] = "Wir müssen uns später ausgiebiger über das Symbol unterhalten. Wichtig ist jetzt, dass wir diese Papiere bekommen, die die Defias studierten. Wenn wir sie in die Finger bekommen, können wir beweisen, dass die Defias Elwynn unterwandert haben und hoffentlich Einblick in ihre weiteren Pläne erhalten.$B$BMomentan bin ich nicht in der Verfassung zu kämpfen, $N. Glaubt Ihr, Ihr seid stark genug, den Defias hier auf der Insel diese Papiere abzunehmen?$B$BWenn Ihr sie beschaffen könnt, bringt sie Gazin und lasst ihn wissen, dass ich wohlauf bin.",
    ["O"] = "Beschafft ein Manuskript der Defias von den abtrünnigen Hexern der Defias in Elwynn und kehrt zu Gazin Tenorm in Sturmwind zurück.",
    ["T"] = "Der Foliant der Offenbarung",
  },
  [1788] = {
    ["D"] = "Ich bringe das Manuskript zu den Beratern des Königs, nachdem Duthorian einen Blick darauf geworfen hat. In der Zwischenzeit solltet Ihr mit Duthorian sprechen. Er wird Euch ganz sicher auf die Schulter klopfen wollen!$B$BNochmals danke, $N. Ich hoffe, wir sehen uns wieder. Ihr seid ein Verbündeter, den ich voller Stolz meinen Freund nenne.",
    ["O"] = "Sprecht mit Duthorian Rall in Sturmwind.",
    ["T"] = "Der Foliant der Offenbarung",
  },
  [1791] = {
    ["D"] = "Jetzt, da Ihr Eure Berserkerhaltung gelernt habt, wollt Ihr bestimmt auch eine hübsche Waffe dazu.$B$BSprecht mit Bath\'rah dem Windbehüter. Das ist ein Trolleinsiedler, der zwischen dem Alteracgebirge und den Westlichen Pestländern lebt. Er ist ein geschickter Waffenmacher und mächtiger Schamane.$B$BUm Bath\'rah zu finden, solltet Ihr zu dem Fluss gehen, der an der Ostflanke von Tarrens Mühle entlangfließt. Folgt ihm nach Norden - Bath\'rah hat sein Lager nahe den Ruinen am Ostufer des Flusses aufgeschlagen.",
    ["O"] = "Sprecht mit Bath\'rah dem Windbehüter.",
    ["T"] = "Der Windbehüter",
  },
  [1793] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Der Foliant der Tapferkeit",
  },
  [1794] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Der Foliant der Tapferkeit",
  },
  [1795] = {
    ["D"] = "Wenn Ihr hinreichend vorbereitet seid, sollten wir beginnen. Lasst mich Euch den Vorgang erklären.$B$BIch beginne damit, dass ich den Standard-Beschwörungskreis erstelle. Wenn das geschehen ist, werden meine Akolythen mit den Kanalisierungsruten den größeren Kreis erstellen.$B$BIst das abgeschlossen, lest Ihr die Zauberformel aus dem Folianten der Kabale und der Teufelsjäger erscheint. Seid vorsichtig, $N. Ein Teufelsjäger ist ein mächtiger Widersacher. Ich möchte nicht, dass er Euch einen Kopf kürzer macht.",
    ["O"] = "Beschwört mithilfe des \'Folianten der Kabale\' einen Teufelsjäger, unterwerft ihn und bringt den \'Folianten der Kabale\' zu Strahad Farsan in Ratschet zurück.",
    ["T"] = "Die Bindung",
  },
  [1796] = {
    ["D"] = "Der erste Gegenstand, bei dessen Erschaffung ich Euch helfen werde, ist eine Robe. Ihr werdet die Gegenstände für mich sammeln und wir werden sie zusammensetzen. Vorausgesetzt Ihr seid erfolgreich, werdet Ihr danach den ersten von vielen Gegenständen haben, die Euch mächtiger machen und Euch bei Eurem Kampf helfen werden.$B$BFür den Anfang müsst Ihr in den Besitz der Roben von Arcana kommen. Ein Schneider kann sie für Euch machen oder vielleicht könnt Ihr eine von jemandem kaufen... am meisten würde es mich beeindrucken, wenn Ihr sie selbst herstellen könntet... aber wie auch immer, bringt mir die Roben und dann machen wir weiter.",
    ["O"] = "Bringt die Roben von Arcana zu Menara Leerbringer im Brachland.",
    ["T"] = "Komponenten für die verzauberte Goldblutrobe",
  },
  [1798] = {
    ["D"] = "Es ist Zeit, dass Ihr die Beschwörung eines Teufelsjägers lernt, $N. Ich habe nicht die erforderlichen Reagenzien und Gegenstände, um Euch hier zu unterrichten, daher müsst Ihr einen anderen aus unserem inneren Kreis aufsuchen, nämlich Strahad Farsan.$B$BEr hat sich im Goblinhafen von Ratschet niedergelassen. Ich werde ihn von Eurer Ankunft in Kenntnis setzen lassen.",
    ["O"] = "Sprecht mit Strahad Farsan im Brachland.",
    ["T"] = "Die Suche nach Strahad",
  },
  [1799] = {
    ["D"] = "Gut, dann können wir beginnen.$B$BIhr müsst als Erstes mit meinen Akolythen im Inneren sprechen. Wählt einen ihrer Wege und folgt ihm - sie werden Euch alles erklären.$B$BAls Zweites müsst Ihr nach Desolace reisen und eine Höllenkugel von einem der Hexenmeister beim Zirkel der Mannoroc holen. Diese Kugel werdet Ihr mit der Hilfe einer Magierin namens Tabetha tief in den südlichen Düstermarschen reinigen. Sie wird Euch aufgrund unserer gemeinsamen Vergangenheit helfen.$B$BKommt erst zurück, wenn Ihr beide Dinge erledigt habt, um die ich Euch gebeten habe.",
    ["O"] = "Sprecht mit Menaras Akolythen im Inneren des Turms über Ratschet und wählt einen ihrer Wege, um ihm zu folgen.$B$BBringt danach eine Höllenkugel zu Tabetha in den Düstermarschen.",
    ["T"] = "Fragmente der Kugel von Orahil",
  },
  [1800] = {
    ["D"] = "Wenn wir brav sind und die Hausarbeiten ordentlich machen, erzählen uns die Waisenmatronen oft Geschichten von großen Schlachten und darüber, wie Lordaeron fiel und wie der böse Arthas seinen eigenen Vater umbrachte, nur um König zu werden.$B$BEr hatte keine Ehre, $N! Wenn ich erst einmal groß und ein mächtiger Krieger der Horde bin, wird mein Schwert durch die Ehre geleitet werden; niemals durch das Böse!$B$BWürdet Ihr mit mir den Thronsaal besuchen, bitte? Er befindet sich kurz vor dem Abgang, über den man nach Unterstadt heruntergeht.",
    ["O"] = "Besucht mit Eurem Schützling den alten Thronsaal in Lordaeron. Er befindet sich kurz vor dem Abgang nach Unterstadt.",
    ["T"] = "Der Thronsaal von Lordaeron",
  },
  [1801] = {
    ["D"] = "Mehr als ein einfacher Beschwörungskreis wird erforderlich sein, um einen Teufelsjäger als Herausforderer für Euch herzubringen.$B$BMeine drei Akolythen und ich helfen, die magischen Kreise zu erschaffen. Ihr benötigt eine Kopie des Folianten der Kabale, der seit geraumer Zeit als verschollen gilt. Er wurde von der ersten Gruppe Hexenmeister geschrieben, die ohne Unterstützung einen Teufelsjäger beschwörten.$B$BVielleicht kann Jorah Annison Euch dabei helfen. Heute arbeitet er als Kartograf in Unterstadt, weiß aber dennoch mehr über alte Schriften als ich.",
    ["O"] = "Sprecht mit Jorah Annison in Unterstadt.",
    ["T"] = "Foliant der Kabale",
  },
  [1802] = {
    ["D"] = "Er ist verschollen, aber man glaubt zu wissen, wo man ihn finden könnte.$B$BNur eine Kopie wurde jemals angefertigt, und die wurde einst beschädigt, eine Vielzahl von Seiten herausgerissen. Man munkelt, dass er irgendwie den Zentauren von Tausend Nadeln in die Hände gefallen sein soll.$B$BDer Rest ging an der Küste westlich von Süderstade verloren, als er per Schiff zum Hafen von Menethil hätte transportiert werden sollen. Er kam nie an.$B$BAls Gegenleistung für dieses Wissen verlange ich nur, dass Ihr ihn findet und mir zur Ansicht bringt.",
    ["O"] = "Beschafft den schimmeligen Folianten und das zerlumpte Manuskript für Krom Starkarm in Eisenschmiede.",
    ["T"] = "Foliant der Kabale",
  },
  [1803] = {
    ["D"] = "Ah! Ich sehe, Ihr seid ein Hexenmeister, der meinen Geschmack teilt. Den Folianten der Kabale zu beschaffen ist ein würdiges Ziel. Habt Ihr gewusst, dass er sich einst innerhalb der Mauern von Lordaeron befand? Aber er wurde beschädigt, fast die Hälfte der Seiten herausgerissen. Wer würde so eine abscheuliche Tat begehen?$B$BDiese Hälfte ist verschollen, doch man munkelt, sie soll den Zentauren von Tausend Nadeln in die Hände gefallen sein.$B$BDer Rest des Textes sollte nach Eisenschmiede gebracht werden, ging aber während der Überfahrt an der Westküste von Süderstade verloren.",
    ["O"] = "Beschafft den schimmeligen Folianten und das zerlumpte Manuskript für Jorah Annison in Unterstadt.",
    ["T"] = "Foliant der Kabale",
  },
  [1804] = {
    ["D"] = "Ach ja, es ist recht schwer, ihn aus der Hand zu geben. Aber Ihr habt ihn gefunden und natürlich Wichtigeres damit zu tun. Vergesst nur nicht, Strahad zu sagen, dass ich eine Kopie haben möchte.$B$BWährend Ihr fort wart, hat er eine Nachricht für Euch geschickt. Er sagte mir, damit seine Akolythen bei der Beschwörung helfen können, müsst Ihr drei Kanalisierungsruten beschaffen.$B$BEr weiß vielleicht nicht, wo man die findet, aber ich! Sie wurden zuletzt von den Orcs des Drachenmalklans im Sumpfland verwendet. Ich würde mich mal mit ihren Zauberern in Verbindung setzen!",
    ["O"] = "Bringt den rekonstruierten Folianten und 3 Ruten des Kanalisierens zu Strahad Farsan in Ratschet.",
    ["T"] = "Foliant der Kabale",
  },
  [1805] = {
    ["D"] = "Das ist einer der interessantesten Texte, die ich in den letzten Jahren gelesen habe. Die Auswahl hier ist nicht sonderlich groß - zum Großteil sind es Bücher über Geografie - und ich habe sie schon alle gelesen. Hoffentlich schickt mir Strahad eine Kopie dieses Folianten, wenn er die Chance dazu hat.$B$BDa wir gerade von ihm sprechen: während Ihr fort wart, habe ich eine Nachricht von ihm erhalten. Für Eure Aufgabe müsst Ihr drei Ruten des Kanalisierens beschaffen.$B$BWeil Ihr mir diesen Text gezeigt habt, weihe ich Euch in ein Geheimnis ein: Ihr findet die Ruten bei den Orcs des Drachenmalklans im Sumpfland.",
    ["O"] = "Bringt den rekonstruierten Folianten und 3 Ruten des Kanalisierens zu Strahad Farsan in Ratschet.",
    ["T"] = "Foliant der Kabale",
  },
  [1806] = {
    ["D"] = "Warum gehen wir nicht rüber zur Schmiede und fangen damit an? Ich treffe schon seit einiger Zeit Vorbereitungen für Eure Ankunft, daher dürfte es nicht allzu lange dauern.$B$BWenn wir fertig sind, werdet Ihr viel besser gerüstet sein, das Licht zu verteidigen, als bisher, das schwöre ich Euch.$B$BIch will ehrlich sein, $N, wenn ich die Ergebnisse Eurer Taten sehe, erfüllt mich ein gewisser Neid. Doch das Wissen, dass ich auf meine bedeutende Weise zu Euren Abenteuern beitragen kann, erfüllt mich mit Freude.",
    ["O"] = "Wartet, bis Jordan Stillbrunn eine Waffe für Euch geschmiedet hat.",
    ["T"] = "Die Prüfung der Rechtschaffenheit",
  },
  [1819] = {
    ["D"] = "Als die Geißel durch Tirisfal zog, konnten wir einen ihrer Angehörigen einfangen, um ihn zu studieren. Er hatte einen freien Geist und nannte sich Ulag der Spalter. Doch obwohl er einen freien Willen besaß, stand er noch immer unter dem Bann des Lichkönigs.$B$BWir hofften, wir könnten den Einfluss des Königs auf Ulag brechen, doch es gelang uns nicht. Jetzt ist die Zeit gekommen, ihn zu befreien. $B$BBefreit und tötet ihn.$B$BAktiviert den Schädelsteinauslöser beim Mausoleum im Norden. Damit wird das magische Siegel entfernt, das ihn bindet.$B$BUnd dann... seid bereit, $N.",
    ["O"] = "Tötet Ulag den Spalter und sprecht dann mit Todeswache Dillinger.",
    ["T"] = "Ulag der Spalter",
  },
  [1820] = {
    ["D"] = "Ihr habt Euren Wert für die Dunkle Fürstin bewiesen, vielleicht könnt Ihr ihn nun an einem ihrer Untertanen beweisen.$B$BColeman Heller wartet auf einen Krieger, der bereit ist, sein Instrument der Rache gegen die Feinde der Verlassenen zu sein.$B$BIhr findet ihn in der Taverne Zur Galgenschlinge im Osten von Brill.",
    ["O"] = "Sprecht mit Coleman Heller.",
    ["T"] = "Gespräch mit Coleman",
  },
  [1821] = {
    ["D"] = "Zu Lebzeiten war die Familie Agamand mein Arbeitgeber und sie war die Ursache für meinen Tod. Es ist nur recht und billig, dass die Agamands jetzt hirnlose Diener des Lichkönigs sind.$B$BUnd jetzt möchte ich ihnen den letzten Rest Ehre nehmen. Geht in ihre Familiengruft und stehlt ihnen die Familienerbstücke: ein Schwert, eine Axt, einen Dolch und einen Streitkolben. Bringt sie mir!$B$BDie Gruft der Familie Agamand liegt am nördlichen Ende von Agamands Mühlen. Der Ort wird durch lauernde Angehörige der Geißel und tote Agamands bewacht.",
    ["O"] = "Bringt Coleman Heller die Axt, das Schwert, den Streitkolben und den Dolch der Familie Agamand.",
    ["T"] = "Agamand-Erbstücke",
  },
  [1823] = {
    ["D"] = "Ihr seid ein Krieger mit Fertigkeiten und wachsendem Ansehen. Vielleicht seid Ihr bereit für Ruga Rachtotems Prüfung. Wenn ja, findet Ihr Ruga im Camp Taurajo im Brachland.$B$BEr wartet auf Krieger, die seinem Ruf folgen.",
    ["O"] = "Sprecht mit Ruga Rachtotem.",
    ["T"] = "Gespräch mit Ruga",
  },
  [1824] = {
    ["D"] = "Um diese Prüfung zu bestehen, müsst Ihr nach Süden zum Feld der Riesen reisen. Dort findet Ihr einen Schwarm krabbelnder Insektenwesen. Sie sind neu im Brachland und mir gefällt nicht, was sie ankündigen.$B$BBringt mir ihre noch zuckenden Fühler und vergeudet keine Zeit, $N, denn abgetrennte Fühler zucken nicht lange. Bringt mir die Fühler in der genannten Zeit und Ihr werdet die Prüfung bestehen.",
    ["O"] = "Bringt 5 zuckende Fühler zu Ruga Rachtotem im Camp Taurajo.",
    ["T"] = "Prüfung auf dem Feld der Riesen",
  },
  [1825] = {
    ["D"] = "Thun\'grim Brandblick, ein berühmter orcischer Schmied, hat von Euren fortgesetzten Heldentaten im Dienst der Horde gehört. Er möchte eine Rüstung für Euch anfertigen und bittet, dass Ihr umgehend mit ihm sprecht.$B$BThun\'grims Lager liegt östlich des Wegekreuzes im Brachland.",
    ["O"] = "Sprecht mit Thun\'grim Brandblick im Brachland.",
    ["T"] = "Gespräch mit Thun\'grim",
  },
  [1838] = {
    ["D"] = "Ich habe Geschichten über Eure Kraft und Tapferkeit gehört und möchte eine Rüstung für Euch anfertigen, eine Unterstützung für Euch, während die Geschichten über Eure Ruhmestaten wachsen.$B$BAber die Herstellung dieser Rüstung wird eine Aufgabe für sich sein, $N. Ich werde Eure Hilfe benötigen und die Hilfe der anderen Schmiede der Horde.$B$BHier, nehmt diese Rolle. In vielen Nächten habe ich mit fast blinden Augen aufgeschrieben, was ich für die Rüstung benötige. Bringt mir die Gegenstände auf dieser Rolle und ich fertige das erste Teil der Rüstung für Euch an.",
    ["O"] = "Bringt Thun\'grim Brandblick 15 rauchige Eisenblöcke, 10 pulverisierte Azurite, 10 Eisenbarren und 1 Phiole Phlogiston.",
    ["T"] = "Brutale Rüstung",
  },
  [1839] = {
    ["D"] = "Der Trollschmied Ula\'elek wird die brutalen Stulpen für Euch anfertigen. Er wohnt mit seinen Dunkelspeerbrüdern in Durotar in Sen\'jin und erwartet Eure Ankunft.",
    ["O"] = "Sprecht mit Ula\'elek in Durotar.",
    ["T"] = "Ula\'elek und die brutalen Stulpen",
  },
  [1840] = {
    ["D"] = "Orm Steinhuf wird ob seiner Fertigkeiten hoch geachtet. Er wird den brutalen Helm für Euch machen.$B$BSucht ihn in Donnerfels beim Teich nahe dem großen, zentralen Totem der Stadt.",
    ["O"] = "Sprecht mit Orm Steinhuf in Donnerfels.",
    ["T"] = "Orm Steinhuf und der brutale Helm",
  },
  [1841] = {
    ["D"] = "Es heißt, vor ihrem Tod hätte Velora Nachtens zu den besten Rüstungsschmieden von Lordaeron gehört. Offenbar konnte der Tod ihrem Talent nichts anhaben. Sprecht mit Velora - sie wird die brutalen Beinschützer für Euch machen.$B$BVelora geht ihrem Gewerbe in Unterstadt unterhalb von Lordaeron nach, im Handwerksviertel.",
    ["O"] = "Sprecht in Unterstadt mit Velora Nachtens.",
    ["T"] = "Velora Nachtens und die brutalen Beinschützer",
  },
  [1842] = {
    ["D"] = "Ich kann Euch ein Paar brutale Stulpen machen, denen große Magie innewohnen wird. Doch vorher müsst Ihr diese Magie sammeln und mir bringen.$B$BDie Satyrn halten sich im nordöstlichen Teil des Eschentals auf wegen der Macht, die dem Wald innewohnt. Sie nähren sich an seiner Reinheit, saugen sie ab und verderben sie. An den geschwollenen, ungespaltenen Hufen erkennt man, welche Satyrn die meiste Magie des Eschentals aufgenommen haben.$B$BDiese Hufe besitzen große Macht. Ich brauche sie, um Eure Stulpen anzufertigen.",
    ["O"] = "Bringt 7 ungespaltene Satyrhufe zu Ula\'elek in Sen\'jin in Durotar.",
    ["T"] = "Satyrhufe",
  },
  [1844] = {
    ["D"] = "Bringt ein galvanisiertes Horn zu Orm Steinhuf in Donnerfels.",
    ["O"] = "Bringt ein galvanisiertes Horn zu Orm Steinhuf in Donnerfels.",
    ["T"] = "Schimärenhorn",
  },
  [1846] = {
    ["D"] = "Um brutale Beinschützer zu machen, brauche ich eine Stütze aus stabilem Schienbeinknochen. Die Orcs des Drachenmalklans im Sumpfland haben hübsche, dicke Schienbeinknochen. Die brauche ich.$B$BAber sie müssen stabil sein. Prüft die Schienbeinknochen unbedingt, wenn Ihr sie einsammelt. Wenn Ihr sie mit bloßen Händen zerbrechen könnt, sind sie nicht stabil genug für mich.",
    ["O"] = "Bringt 8 stabile Schienbeinknochen eines Drachenmalorcs zu Velora Nachtens in Unterstadt.",
    ["T"] = "Schienbeinknochen der Drachenmalorcs",
  },
  [1858] = {
    ["D"] = "Ich überlasse es Euch, Tazans Ranzen zu öffnen und mir seinen Inhalt zu bringen. Der Schlossmechanismus ist komplex, daher wäre es wohl das Beste, wenn Ihr Euch den Schlüssel beschafft.$B$BDa Ihr ihn bei Tazans Leichnam nicht gefunden habt, kann ich nur annehmen, dass er ihn jemandem in Obhut gegeben hat. Ah, ja, ganz recht! Er hatte einen Assistenten namens Gamon. Ein Trunkenbold, vermutlich findet Ihr ihn in der Taverne. Vielleicht könnt Ihr ihm den Schlüssel heimlich abnehmen.",
    ["O"] = "Stehlt Tazans Schlüssel, öffnet seinen Ranzen damit und bringt den Inhalt zu Therzok in der Kluft der Schatten in Orgrimmar.",
    ["T"] = "Die Zerschmetterte Hand",
  },
  [1859] = {
    ["D"] = "Sprecht leiser, $N. Ja, ich kenne Euren Namen. Mir wurde gesagt, ich soll nach einem Orc Ausschau halten, auf den Eure Beschreibung passt.$B$BSoweit wir gehört haben, könnt Ihr im Gefecht bestehen und habt nichts gegen ein wenig Heimlichtuerei von Zeit zu Zeit einzuwenden.$B$BHabe ich Recht?$B$BWie auch immer, Ihr solltet Therzok in Orgrimmar aufsuchen. Er hat ein Angebot, das Ihr Euch anhören solltet.",
    ["O"] = "Sprecht mit Therzok in der Kluft der Schatten in Orgrimmar.",
    ["T"] = "Therzok",
  },
  [1860] = {
    ["D"] = "Wir Magier haben eine Aufgabe, die erledigt werden muss. Sie ist für die Sicherheit von Sturmwind und dem Wald von Elwynn von entscheidender Bedeutung.$B$BJennea Haubitz kennt die Einzelheiten. Sie studiert im Magiersanktum im Magierviertel von Sturmwind. Ihr solltet Euch eiligst zu ihr begeben.",
    ["O"] = "Sprecht in Sturmwind mit Jennea Haubitz.",
    ["T"] = "Gespräch mit Jennea",
  },
  [1861] = {
    ["D"] = "Diese Welt ist gewaltigen mystischen Kämpfen zum Opfer gefallen, die magische Ströme aus ihren einst stabilen Kanälen rissen. Wir haben Mühe, die Auswirkungen dieser Veränderungen herauszufinden. Einige von uns fürchten das Schlimmste.$B$BAber davon werde ich Euch später mehr erzählen. Im Augenblick müsst Ihr Informationen für uns sammeln.$B$BHier, nehmt dieses Fläschchen. Geht zum Wasserfall des Spiegelsees, südwestlich der Tore von Sturmwind, und holt dort eine Probe des Wassers. Bringt sie mir, damit ich die magische Besudelung messen kann.",
    ["O"] = "Bringt eine Probe des Spiegelseewassers zu Jennea Haubitz in Sturmwind.",
    ["T"] = "Spiegelsee",
  },
  [1879] = {
    ["D"] = "$N! Bink, meine gnomische Kollegin in Eisenschmiede, hat sich gemeldet. Sie hat eine Aufgabe zu erledigen und will sich dabei nur auf einen Magier verlassen! Seid Ihr $Gein Magier:eine Magierin;, $N?$B$BWenn ja, sprecht mit Bink. Sie verbringt ihre Zeit in Eisenschmiede in der Halle der Mysterien im mystischen Krieg.",
    ["O"] = "Sprecht mit Bink in Eisenschmiede.",
    ["T"] = "Gespräch mit Bink",
  },
  [1880] = {
    ["D"] = "Wir Magier machen mehr als nur mit den Fingern schnippen und hübsche Funken regnen lassen. Wir kontrollieren die Magie, die Grundsubstanz unserer Welt! Das erfordert große Umsicht und Entschlossenheit. Und Wissen... viel, viel Wissen!$B$BEines meiner Geräte, der magitastische Gizmonitor, sammelt und speichert dieses Wissen in riesigen Mengen! Aber ich verlor ihn! Als wir aus unserer Heimat Gnomeregan flohen, ließ ich ihn in einer Truhe in einer unserer Hütten vor der Stadt, und jetzt wimmelt es dort von Lepragnomen!$B$BBitte, $N, findet meinen Gizmonitor wieder!",
    ["O"] = "Bringt Bink ihren magitastischen Gizmonitor.",
    ["T"] = "Magitastischer Gizmonitor",
  },
  [1881] = {
    ["D"] = "$N, Anastasia Hirschbrunn hat eine Aufgabe für jemanden mit Euren Talenten. Sprecht mit ihr im Magieviertel von Unterstadt und bereitet Euch auf den ersten Auftrag der Magier der Verlassenen vor.",
    ["O"] = "Sprecht mit Anastasia Hirschbrunn.",
    ["T"] = "Gespräch mit Anastasia",
  },
  [1882] = {
    ["D"] = "Die Pestländer im Osten sind entartet. Der Lichkönig hat sie ersonnen, und jetzt breitet sich ihre Magie im Erdboden aus und verderbt und tötet alles Leben. Die zerstörerischen Kräfte der Seuche haben zwar eine gewisse Bezauberung, doch wir, die Magier der Verlassenen, sind der Meinung, sie ist ungehörig und muss ausgemerzt werden.$B$BDoch zuerst müssen wir herausfinden, wie weit sie sich nach Tirisfal ausgebreitet hat. Geht zu Balnirs Bauernhof im Osten von Tirisfal. Im Garten blühen Blumen – Löwenmäulchen. Sammelt sie und kehrt zurück.",
    ["O"] = "Bringt Balnirlöwenmäulchen zu Anastasia Hirschbrunn im Magierviertel von Unterstadt.",
    ["T"] = "Balnirs Bauernhof",
  },
  [1884] = {
    ["D"] = "Der Hexenmeister Zalazane von den Echoinseln hat den normalen Fluss der Magie in Durotar gestört. Das vollbringt er mit besudelten Fetischen, Jujuhaufen, die er auf der Echohauptinsel in seinen Hütten aufgeschichtet hat.$B$BDiese Haufen haben unser Ökosystem verdorben und Hexenmeistern und Höllenkreaturen, die in den dunklen Winkeln des Landes hausen, Macht gegeben.$B$BIhr müsst diese Jujuhaufen zerstören, $N, bevor der Fluss der Magie für immer verändert ist.",
    ["O"] = "Zerstört 4 Jujuhaufen und kehrt dann zu Un\'thuwa in Sen\'jin zurück.",
    ["T"] = "Jujuhaufen",
  },
  [1885] = {
    ["D"] = "$N? Nicht ganz das, was ich erwartet hatte, wenn man bedenkt, dass Carkad selbst Interesse an Euch gezeigt hat. Aber das Aussehen kann täuschen, nicht?$B$BSo oder so, Ihr solltet ihn schnellstmöglich aufsuchen. Ihr könnt Euch nicht vorstellen, was aus den Letzten geworden ist, die seinem Ruf nicht Folge leisteten.",
    ["O"] = "Sprecht mit Mennet Carkad im Schurkenviertel von Unterstadt.",
    ["T"] = "Mennet Carkad",
  },
  [1886] = {
    ["D"] = "Wenn Ihr Euch den Todespirschern anschließen möchtet, der Elitewache von Lord Varimathras, müsst Ihr Euren Wert beweisen.$B$BIch will Euch nichts vormachen, $N, diese Aufgabe könnte Euren Tod bedeuten.$B$BIch habe ein paar Angelegenheiten, die geregelt werden müssen. Ich benötige die persönlichen Habseligkeiten von Astor Hadren, einem Boten, der zwischen Silberwald und Unterstadt pendelt. Die Beschaffungsmethode überlasse ich ganz Euch.$B$BJe weniger Ihr über diese Angelegenheit Bescheid wisst, desto besser. Wenn Ihr Erfolg habt, sage ich Euch mehr.",
    ["O"] = "Beschafft Astors Vorstellungsschreiben und bringt es zu Mennet Carkad im Schurkenviertel.",
    ["T"] = "Die Todespirscher",
  },
  [1898] = {
    ["D"] = "Das Empfehlungsschreiben sollte Andron überzeugen, dass Ihr der Bote seid, auf den er gewartet hat. Ich kenne Andron, er ist nicht so helle im Kopf, dass er Verdacht schöpfen würde.$B$BLord Varimathras vermutet, dass Andron einer anderen Gruppe Unterstützung zuteilwerden ließ. Er möchte wissen, was das für eine Organisation ist, und braucht einen Beweis.$B$BIch möchte, dass Ihr mit Andron sprecht und diese Information bekommt. Ihr findet ihn in seinem Geschäft nahe des Apothekariums.",
    ["O"] = "Sprecht mit Andron Gant in Unterstadt.",
    ["T"] = "Die Todespirscher",
  },
  [1899] = {
    ["D"] = "Ihr findet das Geschäftsbuch mit den Informationen, die Eure Auftraggeber suchen, auf dem Bücherregal hinter mir. Überbringt es mit meinen besten Wünschen und sagt ihnen, dass ich es bevorzugen würde, wenn sie Euch schicken, sollte unsere Übereinkunft ihnen so lieb und teuer sein, dass sie sie fortsetzen möchten.",
    ["O"] = "Bringt Androns Geschäftsbuch zu Mennet Carkad im Schurkenviertel von Unterstadt.",
    ["T"] = "Die Todespirscher",
  },
  [1918] = {
    ["D"] = "Der erschlagene Elementar hat eine intensiv glühende Kugel aus Wasser zurückgelassen. Der Inhalt dieser Kugel ist bestenfalls als abstoßend zu bezeichnen. Es ist eine sichere Annahme, dass was auch immer das Wasser des Mystralsees beschmutzt hat, höchstwahrscheinlich im Wasser dieser Kugel enthalten ist.$B$BEs ist nicht gerade allgemein bekannt, wer sich für solch einen Fund interessieren könnte; vielleicht hat ja Mastok Wutfauch im Splitterholzposten eine Idee.",
    ["O"] = "Bringt die beschmutzte Wasserkugel zu Mastok Wutfauch im Splitterholzposten im Eschental.",
    ["T"] = "Das beschmutzte Element",
  },
  [1919] = {
    ["D"] = "Euer Wissen über das Handwerk wächst, $N. Seid Ihr jetzt bereit, die wahren Lektionen zu beginnen? Seid Ihr bereit, Euren Platz in dem geheimen Krieg einzunehmen, den wir führen?$B$BWenn ja, sprecht mit Jennea Haubitz im Magiersanktum im Magierviertel von Sturmwind. Sie hat eine Aufgabe für Euch, die Euer erstes Fenster in die Welt des Unsichtbaren öffnen wird.",
    ["O"] = "Sprecht in Sturmwind mit Jennea Haubitz.",
    ["T"] = "Meldung bei Jennea",
  },
  [1920] = {
    ["D"] = "Es wurde von einer Störung in der Taverne \'Zum blauen Eremiten\' im Magierviertel berichtet. Jetzt wandern seltsame unsichtbare Kreaturen durch die Taverne. Ich fürchte, dort hat sich ein Manariss gebildet.$B$BIch möchte, dass Ihr die Taverne untersucht und diese Kreaturen fangt. Benutzt die Inkantation der Manifestation, um die Kreaturen in der Taverne sichtbar zu machen, überwältigt sie und fangt sie dann mit einem Verwahrungskasten ein. Wenn Ihr genug habt, kommt mit den gefüllten Kästen, den leeren Kästen und der Anrufung zu mir zurück.",
    ["O"] = "Nehmt Euch die Inkantation der Manifestation und die Truhe mit Verwahrungskästen vom Tisch, hinter Jennea Haubitz. Bringt 3 gefüllte Verwahrungskästen zu Jennea im Magiersanktum.",
    ["T"] = "Untersuchungen im Blauen Eremiten",
  },
  [1921] = {
    ["D"] = "Ihr habt Euren Magierkollegen gut gedient, jetzt wird es Zeit, die Hexerrobe anzuziehen. Wynne Larson kann Eure Robe nähen, aber Ihr müsst ihr die erforderlichen Materialien bringen. $B$BEure Robe wird aus Leinen bestehen, das leicht zu beschaffen ist, aber Ihr müsst auch aufgeladene Rissedelsteine finden, die nur in der Silberbachmine vom Loch Modan zu finden sind, wo die Minenarbeiterliga der Zwerge sie in Kisten zurückgelassen hat.$B$BBeschafft das Leinen und die Edelsteine und bringt sie zu Wynne Larson in das Konfektionsgeschäft Larson im Magierviertel.",
    ["O"] = "Bringt 10 Leinenstoff und 6 aufgeladene Rissedelsteine zu Wynne Larson in Sturmwind.",
    ["T"] = "Materialsammlung",
  },
  [1938] = {
    ["D"] = "Vor langer Zeit studierten drei Magier in diesem Turm, bis einer den dunklen Künsten verfiel. Man bedrängte ihn, von dieser bösen Magie zu lassen, aber er konnte nicht widerstehen. Und so wurde er verbannt.$B$BDas neue Domizil des gefallenen Magiers ist der Turm von Ilgalar im Rotkammgebirge. Er hat ein mächtiges Buch dorthin mitgenommen -\'Urs Abhandlung über Schattenmagie\'.$B$BWir, die Magier von Sturmwind, möchten dieses Buch zurück. Begebt Euch in den Turm von Ilgalar, sucht den Folianten in seiner Bibliothek und bringt ihn mir.",
    ["O"] = "Bringt \'Urs Abhandlung über Schattenmagie\' zu Zaubermeister Andromath in Sturmwind.",
    ["T"] = "Urs Abhandlung über Schattenmagie",
  },
  [1939] = {
    ["D"] = "Der Zaubermeister Andromath wünscht Euch zu sprechen, $N. Er ist oben im Magiersanktum im Magierviertel von Sturmwind.$B$BEs wäre klug, mit ihm zu sprechen, denn Andromath ist einer der mächtigsten Magier, die nicht in Dalaran eingesperrt sind. Ein Rat von ihm ist in jedem Fall ein guter Rat.",
    ["O"] = "Sprecht mit Zaubermeister Andromath.",
    ["T"] = "Zaubermeister Andromath",
  },
  [1940] = {
    ["D"] = "Eure Treue ist unerschütterlich, $N. Ich glaube, es ist an der Zeit, dass Ihr ein besseres Kleidungsstück bekommt, das einem geschickten Magier von Eurem Rang angemessener ist. Wenn Ihr die erforderlichen Materialien sammeln könnt, werde ich veranlassen, dass die Robe angefertigt wird.$B$BIhr müsst in den Dämmerwald und Aaskrabblerspinnen jagen. Extrahiert ihre Seide, aber nur die feinste und makelloseste. Wenn Ihr genug davon habt, bringt sie zu Wynne Larson ins Konfektionsgeschäft Larson hier im Magierviertel. Sie wird Eure Robe anfertigen.",
    ["O"] = "Bringt 8 Rollen makelloser Spinnenseide zu Wynne Larson in Sturmwind.",
    ["T"] = "Makellose Spinnenseide",
  },
  [1943] = {
    ["D"] = "Die Trollmagierin Deino bedarf Eurer Unterstützung, $N. Sie residiert in Orgrimmar im Dunkeldornenlodge.",
    ["O"] = "Sprecht mit Deino.",
    ["T"] = "Gespräch mit Deino",
  },
  [1944] = {
    ["D"] = "Die legendäre Stadt Xavian liegt tief im östlichen Teil des Eschentals. Die Geschichten über ihr Wissen aus längst vergangener Zeit sind den Gebildeten wohl bekannt. Man sagt, die Wasser von Xavian bergen enorme magische Kräfte.$B$BBringt mir eine Probe dieses Wassers direkt von der Quelle.$B$BNehmt dieses Fläschchen und reist nach Xavian. Füllt Wasser am höchsten Wasserfall ab und kehrt zu mir zurück. Aber seid vorsichtig in der uralten Stadt - heute hausen Satyrn in den Ruinen, die gar nicht mögen, wenn sich Magier in ihre Angelegenheiten einmischen.",
    ["O"] = "Bringt die Probe des Wassers von Xavian zu Deino in Orgrimmar.",
    ["T"] = "Wasser von Xavian",
  },
  [1945] = {
    ["D"] = "Als Dank für Eure Dienste habe ich veranlasst, dass Euch neue Roben angefertigt werden, sofern Ihr die erforderlichen Materialien beschaffen könnt...$B$BFür diese Roben müsst Ihr ins Eschental zurückkehren und Locken vom Haar der lachenden Schwestern beschaffen - Dryaden, die sich in der Nähe des Rajenbaums tummeln.$B$BWenn Ihr das Haar habt, bringt es zu Kil\'hala, einem Trollschneider in der Siedlung des Wegekreuzes im Brachland. Er wird Euch erwarten und Eure Roben zuschneiden.",
    ["O"] = "Bringt 12 Haarlocken der lachenden Schwestern zu Kil\'hala beim Wegekreuz.",
    ["T"] = "Lachende Schwestern",
  },
  [1947] = {
    ["D"] = "Jetzt ist es an der Zeit, Euch Euren Magierzauberstab zu verdienen. Um diese Aufgabe zu beginnen, solltet Ihr mit der Menscheneinsiedlerin Tabetha in den Düstermarschen sprechen. Ihr findet ihre Hütte tief in den Marschen, westlich von Theramore.$B$BSprecht mit ihr, denn sie verfügt über umfangreiches Wissen. Tabethas Hütte befindet sich westlich von Theramore und direkt nördlich der Ruinen der Steinbrecher.",
    ["O"] = "Sprecht mit Tabetha.",
    ["T"] = "Reise in die Marschen",
  },
  [1948] = {
    ["D"] = "Es ist nicht leicht, einen Magierzauberstab anzufertigen. Es sind rare Substanzen erforderlich und ein Gefäß muss gemacht werden, das große Mengen magischer Energie speichern kann. Ihr müsst diese Sachen sammeln und mir bringen.$B$BIch habe auf dieses Pergament geschrieben, was Ihr braucht und wie Ihr es bekommt. Bringt mir exakt das, was auf der Liste steht und folgt meinen schriftlichen Anweisungen auf den Buchstaben genau, denn wenn Ihr das nicht macht, geht bei der Herstellung Eures Zauberstabes vielleicht etwas...schief.",
    ["O"] = "Bringt 1 Stück Jade und den vom Blitz aufgeladenen Dornbusch zu Tabetha in den Düstermarschen.",
    ["T"] = "Gegenstände der Macht",
  },
  [1949] = {
    ["D"] = "Um Euren Zauberstab herzustellen, muss ich einige wichtige Stellen in einem bestimmten Buch lesen. Dieses Buch wurde von dem Magus Tirth geschrieben und heißt \'Rituale der Macht\'.$B$BIch besitze kein Exemplar dieses Buchs und es ist in keiner der großen Bibliotheken der Welt zu finden. Daher müsst Ihr mit Tirth sprechen und es von ihm bekommen.$B$BViel Glück. Tirth war ein großer Magier und Gelehrter, aber heute verbringt er seine Zeit bei den Gnomen- und Goblinrennen in der Schimmernden Ebene und trinkt und spielt.",
    ["O"] = "Sprecht mit Magus Tirth in der Schimmernden Ebene.",
    ["T"] = "Verborgene Geheimnisse",
  },
  [1951] = {
    ["D"] = "Hm, wenn ich so darüber nachdenke, ich habe mein Buch nicht! Wisst Ihr, ich hatte Spielschulden, und um sie zu bezahlen, verkaufte ich das letzte Exemplar von \'Rituale der Macht\' an ein Mitglied der Kleriker...$B$BEin Mitglied der Scharlachroten Bruderschaft. Er muss es zur Bibliothek im Scharlachroten Kloster in Tirisfal gebracht haben!$B$BWenn Ihr das Buch wollt, müsst Ihr in das Kloster gehen und es suchen. Und entschuldigt den ganzen Ärger. Ich bin neuerdings nicht mehr ich selbst...",
    ["O"] = "Bringt das Buch \'Rituale der Macht\' zu Tabetha in den Düstermarschen.",
    ["T"] = "Rituale der Macht",
  },
  [1953] = {
    ["D"] = "$N, es wird Zeit, dass Ihr mit Tabetha sprecht... der Einsiedlerin in den Düstermarschen. Sie wird Euch in der Herstellung einer mächtigen Magiergerätschaft unterweisen.$B$BTabethas Hütte liegt tief in den Düstermarschen, westlich von Theramore.",
    ["O"] = "Sprecht mit Tabetha.",
    ["T"] = "Rückkehr in die Marschen",
  },
  [1954] = {
    ["D"] = "Als ersten Schritt müsst Ihr eine Kugel voll Macht beschaffen. Die Hexenmeister der Brennenden Klinge haben hübsche, hochwertige Kugeln... zu dumm, dass sie Dämonen darin eingebunden haben! Aber das können wir ändern, ja.$B$BGeht nach Desolace und nehmt einem Beschwörer der Brennenden Klinge eine Höllenkugel ab. Beschwörer findet Ihr beim Zirkel der Mannoroc.$B$BKommt zu mir zurück, wenn Ihr die Kugel habt.",
    ["O"] = "Bringt eine Höllenkugel zu Tabetha in den Düstermarschen.",
    ["T"] = "Die Höllenkugel",
  },
  [1956] = {
    ["D"] = "Jetzt, nachdem der Dämon entfernt wurde, müssen wir eine Kraftquelle finden. Leider sind die besten Kraftquellen meistens die ältesten und die, an die man am schwersten rankommt. Ist schon lustig, wie einem das Schicksal manchmal mitspielt, wie?$B$BAlso müsst Ihr nach Uldaman und dort den Obsidianwächter besiegen. Der Kerl ist groß, er ist zäh und seine Kraftquelle hat ganz schön Pep.$B$BIch wünsche Euch viel Glück, $N. Falls Ihr kein Glück haben solltet, tja... dann war es schön, Euch gekannt zu haben.",
    ["O"] = "Beschafft Euch eine Obsidiankraftquelle und bringt sie in die Düstermarschen zu Tabetha.",
    ["T"] = "Kraft in Uldaman",
  },
  [1957] = {
    ["D"] = "Wir sind fast fertig, aber dieser letzte Schritt ist der gefährlichste, $N.$B$BIch habe die leere Beschwörerkugel und bin bereit, sie zu ermächtigen. Das machen wir, indem wir die Energie eines Manarisses anzapfen. Ich wirke einen Zauber, der vorübergehend einen Riss auftut, aus dem Kreaturen, Manawogen, herauskommen. Ihr müsst die Manawogen vernichten, damit ihre Macht freigesetzt wird, die ich dann einsammle.$B$BZerschlagt genug Manawogen, bevor sich der Riss schließt, und ich versiegle ihre gesammelte Energie in Eurer Kugel.",
    ["O"] = "Vernichtet 12 Manawogen innerhalb des Zeitlimits.",
    ["T"] = "Manawogen",
  },
  [1959] = {
    ["D"] = "Die Magier von Unterstadt haben eine magische Störung in der Stadt bemerkt und die Magier der Horde um Untersuchung gebeten.$B$BAnastasia Hirschbrunn kann Euch mehr sagen. Sprecht mit Ihr im Magieviertel in Unterstadt.",
    ["O"] = "Sprecht mit Anastasia Hirschbrunn.",
    ["T"] = "Meldung bei Anastasia",
  },
  [1960] = {
    ["D"] = "Algernons Alchemistenladen im Apothekarium ist das Zentrum einer seltsamen Störung. Unsichtbare, aus reiner Magie bestehende Kreaturen halten sich in dem Laden auf - dafür muss es einen Grund geben, und den müssen wir herausfinden!$B$BUntersucht die Störung und fangt diese Kreaturen. Nehmt Euch von dort hinten eine Truhe mit Kästen und eine Rolle. Benutzt im Laden die Rolle, um die Kreaturen sichtbar zu machen, überwältigt sie mit Euren Zaubern und fangt sie dann mit einem Kasten ein. Kommt mit den gefüllten Kästen, den leeren Kästen und der Anrufung zu mir zurück.",
    ["O"] = "Besorgt eine Inkantation der Manifestation und eine Truhe mit Verwahrungskästen von Anastasia Hirschbrunn. Bringt 3 gefüllte Verwahrungskästen, die Truhe mit Verwahrungskästen sowie die Inkantation der Manifestation zu Anastasia in Unterstadt.",
    ["T"] = "Untersuchungen im Alchemistenladen",
  },
  [1961] = {
    ["D"] = "Jetzt ist es Zeit, Eure erste Magierrobe anzulegen, $N. Sie ist ein Zeichen Eures Ranges und ein Zeugnis für Euren Wert.$B$BUm die Robe zu bekommen, müsst Ihr Manaedelsteine von den Magiern von Dalaran im Silberwald sammeln. Ihre Überzeugungen ähneln den unseren, daher ist es ein wenig geschmacklos, sie zu töten, aber wenn sie keine Kraft haben, uns Widerstand zu leisten, verdienen sie es nicht, zu leben.$B$BBringt die Edelsteine und das Leinen zu Josef Gregorian in der Schneiderei im Magieviertel.",
    ["O"] = "Bringt 10 Leinenstoff und 6 Manaedelsteine aus Dalaran zu Josef Gregorian.",
    ["T"] = "Materialsammlung",
  },
  [1963] = {
    ["D"] = "Die Methoden des Kriegshäuptlings sind zu zimperlich. Er wagt nicht, die Macht der Horde zu entfesseln. Einst waren wir stärker, allesamt mächtige Eroberer. Diesen Stolz müssen wir zurückerlangen, und der Klan der Zerschmetterten Hand wird dies bewerkstelligen.$B$BWir sammeln Kraft aus den Schatten und schlagen im Verborgenen gegen exponierte Feinde zu. Eine Aufgabe müsst Ihr für mich erledigen, um Euren Wert zu beweisen.$B$BDer Troll Tazan treibt Handel mit den Piraten bei Ratschet. Er hat Informationen, die ich haben muss. Tötet ihn und bringt mir seinen Ranzen.",
    ["O"] = "Tötet Tazan und bringt seinen Ranzen zu Therzok in der Kluft der Schatten in Orgrimmar.",
    ["T"] = "Die Zerschmetterte Hand",
  },
  [1978] = {
    ["D"] = "Das Syndikat? Darauf wäre ich nie gekommen. Offenbar sind die Lords von Alterac in den Jahren seit ihrem Verrat tief gesunken.$B$BLord Varimathras wird es mit Freuden hören, und da wir diese Entdeckung weitgehend Euch verdanken, ist es nur recht und billig, dass Ihr ihm die Nachricht überbringt.$B$BAh, und wie konnte ich das vergessen?$B$BWillkommen bei den Todespirschern.",
    ["O"] = "Bringt Androns Geschäftsbuch zu Varimathras in der Halle der Dunklen Fürstin in Unterstadt.",
    ["T"] = "Die Todespirscher",
  },
  [1998] = {
    ["D"] = "Lord Varimathras hat mir soeben in einer Botschaft über eine Gefahr für die Verlassenen berichtet, die unverzüglich ausgeschaltet werden muss. Sein Name ist Fenwick Thatros. Lord Varimathras glaubt, dass er einer der Anführer der Untoten sei, die sich im Silberwald angesiedelt haben.$B$BMir kam sofort Euer Name in den Sinn, $N. Ich habe Vertrauen in Eure Fähigkeiten und glaube, Ihr werdet diese Mission fehlerfrei abschließen.$B$BDie letzte Meldung von den Todespirschern im Silberwald deutet darauf hin, dass Thatros nahe der verfallenen Fähre am Lordameresee zu finden ist.",
    ["O"] = "Tötet Fenwick Thatros und bringt seinen Kopf zu Mennet Carkad im Schurkenviertel von Unterstadt.",
    ["T"] = "Fenwick Thatros",
  },
  [1999] = {
    ["D"] = "Die Hexer der Kirin Tor bei Mühlenbern haben wichtige Dokumente eingelagert, die den aktuellen Zustand von Dalaran betreffen. Das wissen wir durch die Aussagen eines unserer Gefangenen. Lord Varimathras hat die Aufgabe, diese zu beschaffen, den Todespirschern übertragen.$B$BDas ist eine Arbeit für Euch, $N. Ihr braucht allerdings etwas Ausrüstung und Kenntnisse im Schlossknacken. Sprecht mit Estelle Gendry, unserer Lageristin, und bittet sie um ein Werkzeugset, bevor Ihr aufbrecht.",
    ["O"] = "Sucht den Statusbericht von Dalaran und bringt ihn zu Mennet Carkad im Schurkenviertel von Unterstadt.",
    ["T"] = "Werkzeuge des Handwerks",
  },
  [2000] = {
    ["D"] = "Ihr habt zu viel Müßiggang, $N. Es ist Zeit, dass Ihr mehr über unser Handwerk lernt. Erst gestern Nacht fand man fünf von uns tot in einer der Spalten in Orgrimmar, aber kaum Hinweise. Es gibt Widerständler gegen uns, die uns mit unseren eigenen Waffen schlagen möchten.$B$BDaher habe ich beschlossen, dass Ihr die Kunst des Schlossknackens lernen sollt.$B$BLeider ist das eine Fertigkeit, für die meine Hände nicht geschaffen sind. Ihr solltet Rokar aufsuchen, der in den Hügeln bei Durotar lebt. Ich glaube, er hat es trotz seines hohen Alters noch drauf.",
    ["O"] = "Sprecht mit Rokar Klingenschatten in Durotar.",
    ["T"] = "Rokar Klingenschatten",
  },
  [2018] = {
    ["D"] = "Ah, also schickt mir Therzok jetzt schon seine Studenten-Grünschnäbel, hm? Ja, ich denke, ich kann Euch das eine oder andere beibringen. Nicht, dass mir viel an Eurer Sache gelegen wäre, aber ich möchte mein Gewissen nicht damit belasten, dass ich Euch in den Tod schicke.$B$BAber zuerst müsst Ihr Euch als würdig erweisen.$B$BIch sage es offen und ehrlich, $N. Ich mag diese Venture Company nicht besonders. Dieses Land erinnert mich an Draenor und mir gefällt die Vorstellung nicht, dass diese Goblins es zerstören. Wenn ihr Vorarbeiter am Schlickmoor getötet wird, wird das Unternehmen lahm gelegt sein.",
    ["O"] = "Tötet Vorarbeiter Glibby und bringt seine Pfeife zu Rokar Klingenschatten in Durotar.",
    ["T"] = "Rokars Test",
  },
  [2019] = {
    ["D"] = "Da habt Ihr sie, $N. Sie sollten Euch nützlich sein. Ich habe sie eigenhändig gefertigt und schwöre, Ihr findet keine besseren.$B$BAber sagt mir nichts von Euren Plänen. Therzoks Ränke interessieren mich nicht, ich möchte nur in Frieden meinen Ruhestand genießen.$B$BSolltet Ihr je ein anderes Werkzeugset brauchen, kommt zu mir und ich fertige Euch ein neues an. Aber geht sorgfältig damit um!",
    ["O"] = "Kehrt zu Therzok in der Kluft der Schatten in Orgrimmar zurück.",
    ["T"] = "Werkzeuge des Handwerks",
  },
  [2038] = {
    ["D"] = "Als wäre es nicht schlimm genug gewesen, in den Loch zu stürzen, haben mich auch noch Troggs von der nahe gelegenen Insel angegriffen und mir mein Werkzeug und meinen speziellen Sprengstoff Superspreng aus der Blastenheimer-Sprengstoffstube gestohlen. Ohne Gyrokopter und Sprengstoff hat die Bewegung keine Chance!$B$BBeschafft mir meine Werkzeuge wieder und findet mein Superspreng aus der Sprengstoffstube, $N. Für Gnomeregan!",
    ["O"] = "Sucht Bingles\' Werkzeuge und bringt sie ihm zurück. Es sind:$B$BBingles\' Schraubenschlüssel, Bingles\' Schraubendreher, Bingles\' Hammer und Bingles\' Sprengstofftube.",
    ["T"] = "Bingles\' verschwundene Werkzeuge",
  },
  [2039] = {
    ["D"] = "Wir haben den Kontakt mit Bingles verloren! Er wurde zu einer Aufklärungsmission über Gnomeregan geschickt und hat sich seit mehr als einer Woche nicht mehr gemeldet. Ich weiß, er hat sich selbst in die Luft gejagt.$B$BZuletzt sah man ihn über dem Loch fliegen. Vielleicht könnt Ihr dies untersuchen, $N?",
    ["O"] = "Sucht Bingles Blastenheimer am Loch Modan.",
    ["T"] = "Sucht Bingles",
  },
  [2040] = {
    ["D"] = "Gnomeregan ist diesen elendigen Troggs in die Hände gefallen! Die Situation ist ernst, aber vielleicht könnt Ihr helfen, $N.$B$BTief in den Todesminen gibt es einen funktionierenden Goblinschredder. Sucht diesen Schredder und bringt die intakte Energieversorgung mit. Mit der Energieversorgung des Schredders können wir unseren gyrobohrmatischen Exkavationatoren die erforderliche Energie zuführen, um durch die felsigen, unterirdischen Grenzen von Gnomeregan zu brechen und einem Gnomenangriff den Weg zu ebnen!",
    ["O"] = "Holt das Gnoamsprenkelspross aus den Todesminen und bringt es Shoni der Schtillen in Sturmwind.",
    ["T"] = "Unterirdischer Angriff",
  },
  [2041] = {
    ["D"] = "Vielleicht... Vielleicht könnt Ihr uns im Kampf um Gnomeregan unterstützen.$B$BIn Sturmwind findet Ihr den Kommandant unserer Untergrundkampftruppe, Shoni die Schtille. Shoni braucht Hilfe für ihre gyrobohrmatischen Exkavationatoren.$B$BIhr findet sie wahrscheinlich bei den Zwergen in Sturmwind.$B$BViel Glück, $N.",
    ["O"] = "Sprecht mit Shoni der Schtillen im Zwergendistrikt von Sturmwind.",
    ["T"] = "Sprecht mit Shoni",
  },
  [2078] = {
    ["D"] = "Landratte, Ihr müsst meinen Ersten Maat finden.$B$BNehmt diesen Schlüssel, steckt ihn in sein\' Gulliver und schafft ihn her, dasser seim Käpt\'n dient.$B$BDiese Murlocs und ihr Häuptling werden nich\' so froh sein, wenn der Erste Maat sie zu fassen kriegt!",
    ["O"] = "Sucht Gelkaks Ersten Maat, den Dreschonator 4100, und führt ihn zu Gelkak zurück.",
    ["T"] = "Gyromasts Rache",
  },
  [2118] = {
    ["D"] = "Eine Erkrankung breitet sich in Dunkelküste aus und befällt Körper und Geist aller, die damit in Kontakt kommen.$B$BDie Distelbären wurden am schwersten von der Seuche getroffen. Die einst edlen Tiere sind jetzt ein Instrument der Zerstörung - tollwütig und rasend. Ich habe möglicherweise ein Heilmittel für dieses Leiden.$B$BGeht mit dieser Falle in den Wald und legt sie auf den Boden. Jeder tollwütige Distelbär, der das Licht kreuzt, wird für kurze Zeit friedfertig. Wenn der Bär erst einmal friedfertig ist, wird er Euch folgen. Führt ihn hierher, $N.",
    ["O"] = "Fangt einen lebenden tollwütigen Distelbären und bringt ihn zu Tharnariun.$B$BWendet Euch an Tharnariun Baumwahrer, falls Ihr fehlschlagen und Eure Falle verlieren solltet.",
    ["T"] = "Verseuchtes Land",
  },
  [2138] = {
    ["D"] = "Wie ich befürchtete, das Heilmittel wirkt nicht.$B$BEs stimmt mich traurig, Geschöpfe der Natur zum Tode zu verurteilen, aber die kranken und besudelten Distelbären müssen ausgerottet werden.$B$BKehrt in den Wald zurück und vernichtet 20 tollwütige Distelbären, $N. Das wird die Seuche sicher nicht stoppen, aber den Schaden, den die Tiere unserem Wald und seinen Bewohnern zufügen, zumindest für kurze Zeit verringern.",
    ["O"] = "Befreit den Wald von 20 tollwütigen Distelbären und kehrt dann zu Tharnariun Baumwahrer in Dunkelküste zurück.",
    ["T"] = "Ausrottung der Infizierten",
  },
  [2139] = {
    ["D"] = "Das Überleben unseres Volkes und unserer Länder ist mit bedauerlichen Kosten verbunden, $N.$B$BIhr habt das Blut der Diener der Natur vergossen; viele kranke Tiere habt Ihr erledigt. Das Volk von Auberdine steht in Eurer Schuld, denn die Qualen des Waldes sind gelindert - wenn auch nur vorübergehend.$B$BIch habe eine letzte Aufgabe für Euch, doch seid gewarnt, Ihr werdet wieder töten müssen. Dies wird hoffentlich das letzte Mal sein, dass ich Euch rufen muss, $N.$B$BIn einer Höhle im Nordosten, nahe Bashal\'Aran, haust die Höhlenmutter. Tötet sie.",
    ["O"] = "Sucht und tötet die Höhlenmutter.",
    ["T"] = "Tharnariuns Hoffnung",
  },
  [2158] = {
    ["D"] = "Jeder Abenteurer sollte ausruhen, wenn ihn die Erschöpfung überkommt - und es gibt keinen besseren Platz für Ruhe und Entspannung als das Gasthaus \"Zur Höhle des Löwen\"!$B$BGastwirt Farley, der Inhaber der \"Höhle des Löwen\", ist mein bester Freund. Wenn Ihr ihm sagt, dass ich Euch geschickt habe, dann gewährt er Euch vielleicht einen Spezialrabatt auf Essen und Trinken.$B$BDas Gasthaus \"Zur Höhle des Löwen\" findet Ihr, wenn Ihr auf der Straße nach Süden reist - Ihr könnt es nicht verfehlen!",
    ["O"] = "Sprecht im Gasthaus \"Zur Höhle des Löwen\" mit Gastwirt Farley.",
    ["T"] = "Ruhe und Entspannung",
  },
  [2159] = {
    ["D"] = "Seid mir gegrüßt, $C. Könnt Ihr mir helfen? Ich habe ein Päckchen Kräuter, das ich in die Stadt Dolanaar liefern muss. Aber ich habe noch etwas mit den Druiden des Laubschattentals zu regeln und kann nicht weg.$B$BKönntet Ihr das Päckchen für mich zustellen? Es muss zu Gastwirt Keldamyr im Gasthaus von Dolanaar. Das liegt an der Straße nach Süden.",
    ["O"] = "Bringt die Lieferung für Dolanaar zu Gastwirt Keldamyr.",
    ["T"] = "Lieferung für Dolanaar",
  },
  [2160] = {
    ["D"] = "He! Ihr seht wie ein beherzter Abenteurer aus. Wenn Ihr vorhabt, den Pass zu überqueren, könntet Ihr da wohl ein Päckchen ins Gasthaus von Kharanos bringen?$B$BIhr hattet doch vor, im Gasthaus abzusteigen, nicht? Wenn Ihr den Pass bezwungen habt, werdet Ihr Euch dort ganz bestimmt etwas ausruhen wollen.$B$BWie auch immer, bringt das zu Tannok Frosthammer, dem Gehilfen des Gastwirts. Ich selbst kann nicht über den Pass und es dauert Tage, bis wieder eine Gebirgsjägereskorte vorbeikommt!",
    ["O"] = "Bringt die Kiste mit Gasthausvorräten zu Tannok Frosthammer in Kharanos.",
    ["T"] = "Vorräte für Tannok",
  },
  [2161] = {
    ["D"] = "Entschuldigung? Könntet Ihr mir helfen?$B$BIch habe hier eine Ladung Essen. Ich bin die Straße entlanggelaufen und habe Skorpiden und Spinnen und Schlimmerem getrotzt! Ich habe das Essen ins Tal der Prüfungen gebracht, weil ich dachte, sie könnten es brauchen und weil ich gern behilflich bin. Aber sie brauchen kein Essen! Jetzt muss ich es nach Klingenhügel zurückbringen und fürchte mich vor den ganzen Bestien unterwegs...$B$BKönnt Ihr es für mich hinbringen? Ich bin nur ein Peon, Ihr aber seid $Gein Held:eine Heldin;. Ihr fürchtet nichts!$B$BBringt es zu Gastwirt Grosk im Gasthaus von Klingenhügel. Danke!",
    ["O"] = "Bringt Ukors Bürde zu Gastwirt Grosk in Klingenhügel.",
    ["T"] = "Die Bürde eines Peons",
  },
  [2178] = {
    ["D"] = "Riecht Ihr dieses köstliche Aroma? Das ist mein Geheimrezept, $N - frischer Schreitereintopf. Köstlich!$B$BVielleicht möchtet Ihr das Rezept ja wissen?$B$BBevor ich Euch das Rezept verrate, müsst Ihr beweisen, dass Ihr den Wildtieren gewachsen seid.$B$BIch erwarte nicht, dass Ihr loszieht und eines zähmt und darauf zu mir zurückgeritten kommt, aber wenn Ihr einige Wildtiere töten könnt und mir etwas von ihrem saftigen Fleisch bringt, verrate ich Euch vielleicht das Schreiterrezept.",
    ["O"] = "Bringt 5 Stücke Schreiterfleisch zu Alanndarian Nachtweise in Auberdine.",
    ["T"] = "Schreitereintopf",
  },
  [2198] = {
    ["D"] = "Ihr habt eine angelaufene Halskette gefunden, doch unter der Stumpfheit des Metalls lässt sich noch die feine und magische Handarbeit erkennen. Die Halskette ist beschädigt, in ihrer Fassung scheinen drei Edelsteine zu fehlen. Auf der Unterseite der Halskette kann man vage den Stempel des Stadtsiegels von Eisenschmiede erkennen. Unter dem Siegel befinden sich drei winzige eingravierte Buchstaben: \"TdK\".$B$BWenn Ihr etwas über die Halskette in Erfahrung bringen wollt, müsst Ihr herausfinden, woher sie stammt. Vielleicht sind das Siegel und die Initialen ein Anfang...",
    ["O"] = "Sucht nach dem Erschaffer der zerrissenen Halskette, um etwas über ihren möglichen Wert zu erfahren.",
    ["T"] = "Die zerrissene Halskette",
  },
  [2199] = {
    ["D"] = "Ich genieße als Juwelier hier in Eisenschmiede ein hohes Ansehen! Oder sagen wir besser: Ich genoss es einmal. Dank der gemeinen Tricks eines Konkurrenten mache ich gerade eine schwere Zeit durch. In dieser Branche bedeutet ein schlechter Ruf das finanzielle Desaster. Und so muss ich feststellen, dass mein guter, wenn auch momentan leicht beschmutzter Name keinen roten Heller mehr wert ist.$B$BIch möchte, dass Ihr mir fünf Silberbarren beschafft, die ich für einen Auftrag brauche - den ersten seit einer Ewigkeit.$B$BBringt mir die fünf Silberbarren, dann beantworte ich Eure Fragen über die Halskette.",
    ["O"] = "Bringt fünf Silberbarren zu Talvash del Kissel in Eisenschmiede.",
    ["T"] = "Lehren haben ihren Preis",
  },
  [2200] = {
    ["D"] = "Einer meiner Konkurrenten brachte das Gerücht in Umlauf, meine Halskette habe den Paladin verflucht. Und natürlich kauft einem Juwelier niemand verfluchte Gegenstände ab.$B$BIch schlag Euch etwas vor, $N: Ihr findet die Edelsteine und ich repariere die Halskette - aber nur, wenn Ihr herumerzählt, dass ich KEINE verfluchten Gegenstände herstelle!$B$BFangt am besten an, in Uldaman nach Hinweisen zu suchen; vielleicht liegt der Paladin dort begraben, von ihm wissen wir, dass er die Kette als Letzter hatte! Wenn Ihr die Edelsteine gefunden habt, solltet Ihr dies hier benutzen; dadurch könnt Ihr mit mir in Eisenschmiede Kontakt aufnehmen.",
    ["O"] = "Sucht in Uldaman nach Hinweisen auf den momentanen Zustand von Talvashs Halskette. Der getötete Paladin, den Talvash erwähnte, hatte die Kette zuletzt.",
    ["T"] = "Rückkehr nach Uldaman",
  },
  [2201] = {
    ["D"] = "Das Tagebuch des Paladins liefert Euch die vielleicht einzigen Hinweise darauf, wo sich die Edelsteine aus der Halskette befinden könnten. Wenn der Paladin alles korrekt aufgezeichnet hat, dann müsst Ihr Euch um die Zwerge der Schattenschmiede und die Troggs kümmern, die um die Vorherrschaft über die Grabungsstätte konkurrieren.$B$BMit diesem Wissen bewaffnet, könnt Ihr Eure Suche nach den Edelsteinen jetzt ernsthaft beginnen. Sobald Ihr Euch alle Edelsteine angeeignet habt, solltet Ihr gemäß seinen Anweisungen Talvash in Eisenschmiede mithilfe der Wahrsagephiole informieren",
    ["O"] = "Findet den Rubin, den Saphir und den Topas, die in ganz Uldaman verstreut sind. Wenn Ihr sie habt, wendet Euch aus der Ferne an Talvash del Kissel, indem Ihr die Wahrsagephiole nutzt, die er Euch zuvor gegeben hat.$B$BAus dem Tagebuch, wisst Ihr...$B* Der Rubin wurde in einer Bastion der Zwerge der Schattenschmiede versteckt.$B* Der Topas steckt in einer Urne in einem der Trogggebiete, in der Nähe einiger Zwerge der Allianz.$B$B* Der Saphir wurde von Grimlok, dem Anführer der Troggs, mitgenommen.",
    ["T"] = "Suche nach den Edelsteinen",
  },
  [2202] = {
    ["D"] = "Ich habe von einer neu entdeckten Pilzsorte gehört, den Magentafunguskappen. Sie wachsen in Kolonien bei der Uldaman-Grabungsstätte. Man findet sie häufig in den vorderen Tunnelabschnitten und auch tief in den unerforschten Regionen der Grabungsstätte. Ich möchte ihren potenziellen Nutzen für die Alchemie erforschen und dafür brauche ich Euch.$B$BFindet die Magentafunguskappen-Kolonien und bringt mir ein Dutzend Kappen. Aber seid gewarnt - die Pilze stoßen vielleicht Giftsporen aus, wenn man sie anstößt.$B$BWenn Ihr das für mich tut, dann braue ich Euch einen Schwung von meinem berühmten Regenerierungselixier!",
    ["O"] = "Bringt 12 Magentafunguskappen nach Kargath zu Jarkal Moosblut.",
    ["T"] = "Reagenzsuche in Uldaman",
  },
  [2203] = {
    ["D"] = "Ihr seid mir eine große Hilfe gewesen, $C. Ich würde Euch ja das Rezept des Gebräus geben, das Ihr vorhin gekostet habt... doch zuvor muss ich Euch um einen gefährlichen Gefallen bitten. Hört meine Bitte.$B$BDiese Schließkassette enthält drei leere thaumaturgische Gefäße; sie sind mit einer Aura der Einstimmung magieerfüllt, die einem versengten Behütergroßdrachen das Blut aussaugt. Wenn Ihr sie gegen die Bestie benutzt, wird sie höchst erbost reagieren, also seid gewarnt. Bringt sie mir, wenn Ihr alle drei gefüllt habt.$B$BGelingt Euch das, gehört das Rezept Euch.",
    ["O"] = "Benutzt die leeren thaumaturgischen Gefäße bei versengten Wächtergroßdrachen im Ödland. Wenn Ihr sie gefüllt habt, bringt sie zu Jarkal Moosblut in Kargath.",
    ["T"] = "Reagenziensuche im Ödland II",
  },
  [2204] = {
    ["D"] = "Ihr müsst etwas \"Wumms\" für die Halskette finden und wenn ich es mir recht überlege, dann ist Uldaman sogar der beste Ort dafür. Ich vermute mal, da drinnen laufen eine Menge Konstrukte rum, hab ich Recht? Gut, dann sucht Euch das größte und böseste Konstrukt, das Ihr finden könnt, macht es fertig und schnappt Euch seine Kraftquelle! Ich bin mir ziemlich sicher, dass ich die Kraftquelle mit der Halskette verbinden kann.$B$BOhne die Kraftquelle wird es unmöglich sein, die Halskette zu reparieren! Kommt wieder zu mir nach Eisenschmiede zurück, wenn Ihr die Kraftquelle habt, dann repariere ich die Halskette für Euch.",
    ["O"] = "Besorgt Euch eine Kraftquelle vom mächtigsten Konstrukt, das Ihr in Uldaman finden könnt, und liefert sie bei Talvash del Kissel in Eisenschmiede ab.",
    ["T"] = "Restaurierung der Halskette",
  },
  [2205] = {
    ["D"] = "Liefert Keryn Sylvius\' Päckchen an Meister Mathias Shaw in der Kaserne von Sturmwind.",
    ["O"] = "Liefert Keryn Sylvius\' Päckchen an Meister Mathias Shaw in der Kaserne von Sturmwind.",
    ["T"] = "Aufsuchen des SI:7",
  },
  [2206] = {
    ["D"] = "Unsere Agenten vor Ort melden starke Banditenaktivitäten südlich von Goldhain. Die Berichte deuten an, dass die Banditen Jerods Anlegestelle erobert haben und sie nun benutzen, um große Mengen Hehlerware vom Rotkammgebirge nach Westfall zu schmuggeln.$B$BIhr müsst Euch in das Dockhaus einschleichen und mir den Lieferzeitplan beschaffen, $N. Ihr findet ihn wahrscheinlich tief in den Taschen des Dockmeisters.$B$B... und, $N, solltet Ihr entdeckt werden, wird der SI:7 strikt leugnen, dass er Euch kennt.",
    ["O"] = "Sucht den Dockmeister der Defias und beschafft den Lieferzeitplan für Meister Mathias Shaw.",
    ["T"] = "Schneller Eingriff",
  },
  [2218] = {
    ["D"] = "Sprecht mit Hulfdan Schwarzbart in Eisenschmiede.",
    ["O"] = "Sprecht mit Hulfdan Schwarzbart in Eisenschmiede.",
    ["T"] = "Straße der Rettung",
  },
  [2238] = {
    ["D"] = "Ihr seht\'s ja, der König ist dabei, im Steinbruch nach Erz und Edelsteinen graben zu lassen. Mehr Reichtümer, als ein Zwerg normalerweise in zehn Leben zu sehen bekommen würde.$B$BDer Verborgene Kreis hat auch Werte, $N. Wertsachen! Versteht Ihr, was ich meine?$B$BWir finden, der König sollte uns was von der Beute abgeben, aber der alte Stinkebart soll nicht erfahren, dass er was abgibt - ich hoffe, Ihr könnt mir folgen.$B$BIhr müsst mit Onin MacHammar drüben bei Gnomeregan reden und \'nen Bericht zurückbringen. Der listige Zwerg hat sich bei den Lepragnomen versteckt. Seid vorsichtig.",
    ["O"] = "Sucht Onin MacHammar bei Gnomeregan.",
    ["T"] = "Einfache List",
  },
  [2239] = {
    ["D"] = "Bringt Onins Bericht zu Hulfdan in Eisenschmiede.",
    ["O"] = "Bringt Onins Bericht zu Hulfdan in Eisenschmiede.",
    ["T"] = "Onins Bericht",
  },
  [2240] = {
    ["D"] = "Jetzt zur anstehenden Aufgabe:$B$BEs gibt eine geheime Kammer, die Kammer von Khaz\'mul, die erforscht werden muss. Das Problem dabei ist, dass die Kammer hinter einer riesigen verschlossenen Tür liegt! Glücklicherweise weiß ich, wie man sie öffnet.$B$BIch habe alles in dem Tagebuch hier auf dem Tisch beschrieben. Meine Leute und ich sitzen hier zurzeit fest...also müsst Ihr die Kammer von Khaz\'mul für uns finden, $N.$B$BLest mein Tagebuch, erforscht die Kammer und meldet Euch dann bei Ausgrabungsleiter Sturmlanze in der Forscherliga in Eisenschmiede.",
    ["O"] = "Lest Baelogs Tagebuch, erforscht die geheime Kammer und erstattet dann Ausgrabungsleiter Sturmlanze Bericht.",
    ["T"] = "Die geheime Kammer",
  },
  [2242] = {
    ["D"] = "Sucht Sethir den Uralten und bringt alle Hinweise, die Ihr finden könnt, zu Syurna.",
    ["O"] = "Sucht Sethir den Uralten und bringt alle Hinweise, die Ihr finden könnt, zu Syurna.",
    ["T"] = "Das Schicksal ruft",
  },
  [2258] = {
    ["D"] = "$N, ich könnte Eure Hilfe gebrauchen, wenn Ihr Euch dazu in der Lage fühlt. Ich bin ein angesehener Alchemist und benötige Eure Unterstützung, um einige Reagenzien zu beschaffen, die im Ödland zu finden sind.$B$BIch brauche folgende Gegenstände, um meine Vorräte aufzufüllen: 5 Bussardmuskelmägen, 10 Klippenkojotenfangzähne und 5 Felselementarsplitter. Natürlich könnt Ihr diese Reagenzien direkt von den lieben Tieren selbst bekommen; aber wenn Ihr keine Kämpfernatur seid, dann sucht Euch besser Freunde, die kämpfen können.$B$BBringt mir diese Gegenstände, dann springen ein paar Münzen für Euch dabei raus. Was sagt Ihr?",
    ["O"] = "Bringt Jarkal Moosblut in Kargath im Ödland 5 Bussardmuskelmägen, 10 Klippenkojotenfangzähne und 5 Felselementarsplitter.",
    ["T"] = "Reagenziensuche im Ödland",
  },
  [2259] = {
    ["D"] = "Keine Bange, $N, ich schicke Euch nicht zu Syurna. Erion verlangt nach Eurer Anwesenheit in der Enklave des Cenarius. Beeilung!",
    ["O"] = "Kontaktiert Erion Schattenflüsterer in Darnassus.",
    ["T"] = "Erion Schattenflüsterer",
  },
  [2260] = {
    ["D"] = "Reist nach Sturmwind und meldet Euch bei Renzik \"Das Messer\".",
    ["O"] = "Reist nach Sturmwind und meldet Euch bei Renzik \"Das Messer\".",
    ["T"] = "Erions Befehl",
  },
  [2279] = {
    ["D"] = "Als Ihr die Scheiben von Norgannon ein weiteres Mal berührt, kribbelt Eure Hand erneut. Jetzt aber ist aus dem Inneren der Scheiben ein seltsames, knirschendes Geräusch zu hören.$B$BPlötzlich ist Euer Rucksack schwerer als noch eine Sekunde vorher. In seinem Inneren findet Ihr eine Miniaturausgabe der Scheiben!$B$BZweifellos wäre ein solcher Gegenstand von unschätzbarem Wert für jeden, der sich für die Frühgeschichte von Azeroth interessiert. Die Forscherliga in Eisenschmiede beispielsweise ist eine solche Gruppe und sie ist in der ganzen Allianz bekannt - vielleicht können die etwas mit diesem Fund anfangen.",
    ["O"] = "Bringt die Miniaturausgabe der Scheiben von Norgannon zur Forscherliga nach Eisenschmiede.",
    ["T"] = "Die Platinscheiben",
  },
  [2280] = {
    ["D"] = "Als Ihr die Scheiben von Norgannon ein weiteres Mal berührt, kribbelt Eure Hand erneut. Jetzt aber ist aus dem Inneren der Scheiben ein seltsames, knirschendes Geräusch zu hören.$B$BPlötzlich ist Euer Rucksack schwerer als noch eine Sekunde vorher. In seinem Inneren findet Ihr eine Miniaturausgabe der Scheiben!$B$BZweifellos wäre ein solcher Gegenstand von unschätzbarem Wert für jeden, der sich für die Frühgeschichte von Azeroth interessiert. Irgendjemand wird sich mit Sicherheit dafür interessieren!",
    ["O"] = "Bringt die Miniaturausgabe der Scheiben von Norgannon zu jemandem, der sich brennend dafür interessiert.",
    ["T"] = "Die Platinscheiben",
  },
  [2281] = {
    ["D"] = "Euch jetzt in den Einsatz zu schicken wäre Selbstmord. Renzik braucht nicht noch einen toten Schurken. Aber keine Sorge, $N... Renzik wird Euch schon Beine machen.$B$BGeht ins Rotkammgebirge und sprecht mit einem Mitglied des SI:7 namens Lucius. Ihr werdet ihn auf den Docks von Seenhain finden, wo er mit den anderen halbseidenen Taugenichtsen herumhängt.",
    ["O"] = "Geht nach Seenhain im Rotkammgebirge und sprecht mit Lucius.",
    ["T"] = "Rendezvous im Rotkammgebirge",
  },
  [2298] = {
    ["D"] = "Wir war\'n also grade dabei, Phase III zu planen, da platzen \'n paar von Magnis Holzköpfen hier rein, schwafeln was von \'ner Art Razzia und verhaften ein paar vom Verborgenen Kreis.$B$BSo springt keiner mit dem alten Schwarzbart um! Die Diskussion wurde immer hitziger und irgendwann fiel Euer Name!$B$BMein Rat? Nehmt den ersten Greif aus Eisenschmiede raus und geht nach Sturmwind. Geht in die Altstadt und sprecht dort mit einem alten Freund von mir, der Renzik \"Das Messer\" genannt wird.",
    ["O"] = "Reist nach Sturmwind und sucht Renzik \"Das Messer\" auf.",
    ["T"] = "Königliche Erpressung",
  },
  [2299] = {
    ["D"] = "Wo seid Ihr gewesen, $N? Hulfdan sucht schon überall nach Euch. Wir dachten schon, der König hätte Euch in Eisen legen lassen.$B$BGeht so schnell wie möglich nach Eisenschmiede und sprecht mit Hulfdan!",
    ["O"] = "Reist nach Eisenschmiede und sprecht mit Hulfdan Schwarzbart.",
    ["T"] = "Auf Hulfdan!",
  },
  [2300] = {
    ["D"] = "Reist nach Sturmwind und sucht Renzik \"Das Messer\" auf.",
    ["O"] = "Reist nach Sturmwind und sucht Renzik \"Das Messer\" auf.",
    ["T"] = "SI:7",
  },
  [2318] = {
    ["D"] = "Sucht jemanden, der das Tagebuch des Paladins übersetzen kann. Der nächstgelegene Ort, wo Ihr so jemanden finden könntet, ist Kargath im Ödland.",
    ["O"] = "Sucht jemanden, der das Tagebuch des Paladins übersetzen kann. Der nächstgelegene Ort, wo Ihr so jemanden finden könntet, ist Kargath im Ödland.",
    ["T"] = "Übersetzung des Tagebuchs",
  },
  [2339] = {
    ["D"] = "Hier ist das Tagebuch, übersetzt, sodass Ihr es lesen könnt. Kurz gesagt steht darin, dass Zwerge den Rubin versteckt haben, Troggs versteckten den Topas und ein Trogg namens Grimlok hat den Saphir.$B$BEs ist so, Ihr braucht eine Kraftquelle, um die Halskette wieder funktionstüchtig zu bekommen. Dran Droffers weiß das nicht. Um eine Kraftquelle zu finden, würde ich Folgendes tun: Begebt Euch so tief wie möglich nach Uldaman hinein und tötet das stärkste Konstrukt, das Ihr finden könnt. Ich zweifle nicht daran, dass das stärkste Konstrukt dort auch dasjenige ist, das die Kraftquelle hat.$B$BWenn Ihr alles zusammenhabt, dann kommt wieder hierher!",
    ["O"] = "Beschafft in Uldaman alle drei Edelsteine sowie eine Kraftquelle für die Halskette und bringt sie anschließend zu Jarkal Moosblut nach Kargath. Jarkal glaubt, dass sich eine Kraftquelle vielleicht im stärksten Konstrukt in Uldaman findet.$B$BAus dem Tagebuch, wisst Ihr...$B* Der Rubin wurde in einer Bastion der Zwerge der Schattenschmiede versteckt.$B* Der Topas steckt in einer Urne in einem der Trogggebiete, in der Nähe einiger Zwerge der Allianz.$B* Der Saphir wurde von Grimlok, dem Anführer der Troggs, mitgenommen.",
    ["T"] = "Findet die Edelsteine und die Kraftquelle",
  },
  [2341] = {
    ["D"] = "Als Dran und Malton Droffers anfangen, sich um ihr neues Bergegut zu zanken, ist die Hälfte des Geschäftes bereits erledigt. Jetzt steht noch eine Reise ins Ödland an, um dort die Sache zum Abschluss zu bringen, denn Jarkal Moosblut aus Kargath hat eine besondere Belohnung für Euch in petto - oder vielleicht auch nicht.",
    ["O"] = "Besucht Jarkal Moosblut in Kargath, um festzustellen, ob er Erfolg gehabt hat.",
    ["T"] = "Wiederbeschaffung der Halskette, Teil 3",
  },
  [2342] = {
    ["D"] = "Die Fledermausgehege mit diesem Besen auszukehren, das ist keine Aufgabe, die eines Garretts würdig wäre! Aber ich habe mit diesem verfluchten Job ein wenig Geld verdient und ich bin bereit, es für Euch auszugeben, wenn ich damit nur meine Situation verbessern kann.$B$BDie Lieblingswaffe der Familie Garrett liegt weggeschlossen in unserer Truhe in der Südlichen Bankenhalle von Uldaman. Das rührt noch von den Zeiten her, da wir Garretts dort als Buddler schufteten. Ohne sie werde ich für alle Zeiten hier festsitzen und im wahrsten Sinne des Wortes diesen höllischen Fledermäusen hinterherkehren.$B$BBeschafft den Schatz meiner Familie wieder und bringt ihn mir! Helft mir dabei, mein Geburtsrecht einzufordern!",
    ["O"] = "Holt Patrick Garretts Familienschatz aus der Truhe der Familie in der Südlichen Bankenhalle von Uldaman und bringt diesen zu ihm nach Unterstadt.",
    ["T"] = "Wiederbeschaffte Schätze",
  },
  [2358] = {
    ["D"] = "Vor vielen Jahren wurden die Hörner von Nez\'ra aus dem Rabenholdtanwesen gestohlen. Über Ozeane und Kontinente hinweg habe ich nach ihnen gesucht, nur um schließlich zu erfahren, dass diese unbedeutenden Bestien sie die ganze Zeit über im Besitz hatten.$B$BErschlagt jeden Schwarzfelsorc, auf den Ihr trefft, bis Ihr die Hörner von Nez\'ra wiedererlangt habt. Solltet Ihr die Hörner in Euren Besitz bekommen, dann sucht mich in Seenhain auf.$B$BWenn Ihr dies für mich tut, soll Euer Name in Rabenholdt Bekanntheit erlangen, $N.",
    ["O"] = "Bringt die Hörner von Nez\'ra zu Arantir nach Seenhain.",
    ["T"] = "Hörner von Nez\'ra",
  },
  [2359] = {
    ["D"] = "Ich überwache diesen Turm jetzt schon seit Wochen, $N. Kürzlich kam eine größere Lieferung aus dem Dämmerwald an und mit dieser Lieferung kamen auch diese Wachen.$B$BWas sie sind? Das weiß ich genauso wenig wie Ihr. Eines kann ich Euch aber sagen: Menschen sind das nicht.$B$BWie dem auch sei, bei der Lieferung war eine Truhe dabei und ich vermute mal, das ist es, was diese Monstrositäten bewachen. Die Kreatur mit den Stacheln hat einen Schlüssel, den wir brauchen.$B$BBeschafft Euch den Schlüssel, dringt in den Turm ein und stellt fest, was in dieser Truhe ist. Berichtet dann Mathias, was Ihr herausgefunden habt.",
    ["O"] = "Stehlt den Turmschlüssel der Defias, brecht in den Turm der Defias ein und stellt fest, was sich in der Dämmerwaldtruhe befindet. Bringt Mathias Shaw in Sturmwind dann alle Informationen über das, was Ihr herausgefunden habt. Lest Kearnens Tagebuch, um neue Erkenntnisse über den Turm zu erlangen.",
    ["T"] = "Klavens Turm",
  },
  [2360] = {
    ["D"] = "Keinen Moment zu früh, $N. Spähtrupps des SI:7 berichten von ungewöhnlich intensiven Aktivitäten der Defias in Westfall.$B$BEs sieht so aus, als hätten die Defias einen verlassenen Wachturm der Allianz südlich der Späherkuppe übernommen und verwendeten diesen als Operationsbasis, um Güter von der Lieferroute ins Rotkammgebirge hereinzuschmuggeln.$B$BBegebt Euch nach Westfall und kontaktiert Agentin Kearnen. Sie wird Euch Bericht erstatten. Sie hält sich in der Nähe des Turms versteckt; dort findet Ihr sie.$B$B... und, $N, solltet Ihr entdeckt werden, wird der SI:7 strikt leugnen, dass er Euch kennt.",
    ["O"] = "Reist nach Westfall und sucht Agentin Kearnen. Sie wird Euch Bericht erstatten.",
    ["T"] = "Mathias und die Defias",
  },
  [2378] = {
    ["D"] = "Reist nach Orgrimmar und sprecht an der Kluft der Schatten mit Shenthul.",
    ["O"] = "Reist nach Orgrimmar und sprecht an der Kluft der Schatten mit Shenthul.",
    ["T"] = "Suche nach der Zerschmetterten Hand",
  },
  [2379] = {
    ["D"] = "Sprecht an der Kluft der Schatten mit Zando\'zan.",
    ["O"] = "Sprecht an der Kluft der Schatten mit Zando\'zan.",
    ["T"] = "Zando\'zan",
  },
  [2380] = {
    ["D"] = "Sprecht an der Kluft der Schatten in Orgrimmar mit Shenthul.",
    ["O"] = "Sprecht an der Kluft der Schatten in Orgrimmar mit Shenthul.",
    ["T"] = "Nach Orgrimmar!",
  },
  [2381] = {
    ["D"] = "Bringt den Schatz der Südmeerfreibeuter zu Wrenix dem Jämmerlichen in Ratschet. Vergesst nicht, einen R.C.V.K. und Diebeswerkzeug von Wrenixs Elektrodingsda-Apparat zu bringen. Ihr werdet diese Dinge brauchen, um Eure Mission zu beenden.$B$BWenn Ihr von ungewöhnlich feindlichen Papageien angegriffen werdet, benutzt Euren R.C.V.K.!",
    ["O"] = "Bringt den Schatz der Südmeerfreibeuter zu Wrenix dem Jämmerlichen in Ratschet. Vergesst nicht, einen R.C.V.K. und Diebeswerkzeug von Wrenixs Elektrodingsda-Apparat zu bringen. Ihr werdet diese Dinge brauchen, um Eure Mission zu beenden.$B$BWenn Ihr von ungewöhnlich feindlichen Papageien angegriffen werdet, benutzt Euren R.C.V.K.!",
    ["T"] = "Das Ausplündern der Plünderer",
  },
  [2382] = {
    ["D"] = "Reist nach Ratschet im Brachland und sprecht mit Wrenix dem Jämmerlichen.",
    ["O"] = "Reist nach Ratschet im Brachland und sprecht mit Wrenix dem Jämmerlichen.",
    ["T"] = "Wrenix aus Ratschet",
  },
  [2383] = {
    ["D"] = "Ah, während Ihr fort wart, kam ein Pergament für Euch, $N.$B$BLest es, wenn Ihr Zeit habt. Wenn ich mich nicht irre, kam es vom Kriegerlehrer Frang. Er würde gern mit Euch sprechen, wenn Ihr bereit seid. Ihr findet ihn außerhalb des Höhlenbaus, wo er sich in den Schatten verbirgt.",
    ["O"] = "Lest das einfache Pergament und sprecht mit Frang im Tal der Prüfungen.",
    ["T"] = "Einfaches Pergament",
  },
  [2398] = {
    ["D"] = "Die Grabungsstätte von Uldaman war nicht immer so überlaufen. Wir Zwerge von Eisenschmiede kontrollierten früher das Gebiet, aber... das war, bevor die Troggs kamen. Sie kamen so schnell und waren so zahlreich, dass nicht einmal die Macht der Zwerge sie aufhalten konnte.$B$BUm ehrlich zu sein, kam ihr Angriff so plötzlich, dass ich fürchte, dass vielleicht einige unserer Leute hinter den feindlichen Linien festsitzen.$B$BMein Freund Baelog hatte eine kleine Gruppe Zwerge tief hinunter in die Grabungsstätte geführt, kurz bevor die Troggs auftauchten. Wenn er und seine Gefährten noch leben, dann möchte ich, dass Ihr sie findet.",
    ["O"] = "Sucht in Uldaman nach Baelog.",
    ["T"] = "Die verschollenen Zwerge",
  },
  [2418] = {
    ["D"] = "Vor einiger Zeit hatte ich einen kleinen Zwischenfall mit den Zwergen der Schattenschmiede. Sie brachten mich in die Festung Angor, um dort zwei Arten von seltsamen Steinen zu untersuchen, wie sie in Uldaman vorkommen. Ich habe entdeckt, dass die Steine langlebige und stabile Kraftquellen darstellen, wenn sie richtig behandelt werden. Es ist schon erstaunlich, was man alles in dieser alten Grabungsstätte finden kann!$B$BJedenfalls warfen sie mich aus der Festung Angor raus, nachdem ich ihnen das Geheimnis der Steine verraten hatte, und begannen, in Uldaman nach weiteren Steinen zu graben.$B$BIch will diese Steine für mich! Bringt mir beide Arten von Steinen und ich bezahle Euch gut dafür!",
    ["O"] = "Bringt Riggelfuzz im Ödland 8 Kraftsteine aus Dentrium und 8 Kraftsteine aus An\'Alleum.",
    ["T"] = "Kraftsteine",
  },
  [2438] = {
    ["D"] = "Einst erhielt ich von Gaerolas Talvethren, dem Bewacher der Druiden im Grabhügel von Ban\'ethil, einen smaragdgrünen Traumfänger. Dieses mächtige Amulett ist in der Lage, Energie aus dem Smaragdgrünen Traum abzuziehen und damit denen, die es tragen, Glück zu bringen.$B$BLeider habe ich den Traumfänger nicht mehr aus meiner Kommode in Sternenhauch holen können. Sternenhauch war einst ein ruhiger Ort, aber jetzt ist er durch die Furbolgs, die sich dort aufhalten, der Verderbnis anheim gefallen.$B$BWärt Ihr vielleicht bereit, mir meinen Traumfänger wiederzubeschaffen, $C?",
    ["O"] = "Bringt den smaragdgrünen Traumfänger an Euch.",
    ["T"] = "Der smaragdgrüne Traumfänger",
  },
  [2439] = {
    ["D"] = "Dieser Gegenstand ist ein unglaublicher Fund. Die alten Titanen oder \'Schöpfer\', wie sie auf diesen Scheiben genannt werden, sind die Verbindung zu unserer verzauberten Erschaffung, wenn das hierin gespeicherte Wissen korrekt ist! Wir werden uns so eingehend wie möglich mit diesen Scheiben beschäftigen.$B$BWas Euch betrifft, $C: Bringt diesen Gutschein zu Dinita Zasterstein in dem Gewölbe. Für ihn erhaltet Ihr etwas, das meiner Meinung nach eines solch unglaublichen Fundes würdig ist.$B$BKehrt umgehend zu mir zurück, wenn Ihr fertig seid - es gibt noch viel mehr für die Forscherliga zu tun!",
    ["O"] = "Bringt Euren Belohnungsgutschein zu Dinita Zasterstein im Tresor von Eisenschmiede.",
    ["T"] = "Die Platinscheiben",
  },
  [2440] = {
    ["D"] = "Ich werde all meine Energien auf diese Scheiben fokussieren; wenn die Zeit gekommen ist, werden sie mir ihre Funktionsweise schon verraten.$B$BWas Euch betrifft, $N, nun, Ihr sollt dafür belohnt werden, dass Ihr mir diese Sache kundgetan habt. Bringt den Belohnungsgutschein zu Bena Winterhuf, der Alchemielehrerin in Donnerfels. Sie wird Euch einen speziellen Gegenstand geben, der Euch bestimmt gefällt, und außerdem erhaltet Ihr von ihr eine Auswahl an Tränken aus meinem persönlichen Vorrat.$B$BKehrt bald zu mir zurück... ich spüre, dass wir in Bezug auf diesen Fund noch viel mehr zu tun haben.",
    ["O"] = "Bringt den Belohnungsgutschein zu Bena Winterhuf nach Donnerfels.",
    ["T"] = "Die Platinscheiben",
  },
  [2458] = {
    ["D"] = "Reist zum Turm der Venture Company nördlich des Schlickmoores und kontaktiert dort den Agenten der Zerschmetterten Hand, Zuchtmeister Fitzel.$B$BSobald der Turm in Sichtweite ist, verwendet die Leuchtfeuerwaffe, um Zuchtmeister Fitzel Eure Ankunft anzuzeigen. Feuert ZWEI Leuchtfeuergeschosse ab, nähert Euch dann Zuchtmeister Fitzel und zeigt den Gruß der Zerschmetterten Hand.$B$BEs ist wichtig, dass Ihr diese Aktionen in der richtigen Reihenfolge durchführt, da Fitzel jede Person, die er nicht erkennt, angreift und eliminiert.",
    ["O"] = "Reist zum Turm der Venture Company nördlich des Schlickmoores und kontaktiert dort den Agenten der Zerschmetterten Hand, Zuchtmeister Fitzel.$B$BSobald der Turm in Sichtweite ist, verwendet die Leuchtfeuerwaffe, um Zuchtmeister Fitzel Eure Ankunft anzuzeigen. Feuert ZWEI Leuchtfeuergeschosse ab, nähert Euch dann Zuchtmeister Fitzel und zeigt den Gruß der Zerschmetterten Hand.$B$BEs ist wichtig, dass Ihr diese Aktionen in der richtigen Reihenfolge durchführt, da Fitzel jede Person, die er nicht erkennt, angreift und eliminiert.",
    ["T"] = "Undercover",
  },
  [2459] = {
    ["D"] = "Der Smaragd... Er ist weg! Mein Traumfänger ist beschädigt!$B$BEine Gruppe von Knarzklauenmystikern lebt im Norden von Sternenhauch. Ich habe gehört, dass ihr Anführer, Ferocitas der Traumfresser, seit einiger Zeit eine Halskette trägt, die des Nachts grün leuchtet. Jetzt, wo ich meinen Traumfänger sehe, bin ich mir sicher, dass er meinen Smaragd gestohlen hat... Er würde nie verstehen, dass dessen Macht völlig nutzlos für ihn ist.$B$BFindet das verlorene Juwel, $N. Und wenn Ihr schon mal dort seid, erledigt auch gleich einige dieser verderbten Mystiker.",
    ["O"] = "Tötet 7 Mystiker der Knarzklauen und findet das verlorene Juwel.",
    ["T"] = "Ferocitas der Traumfresser",
  },
  [2460] = {
    ["D"] = "Als ein Schurke der Zerschmetterten Hand müsst Ihr unbedingt den geheimen Gruß erlernen. Dies ist die einzige Methode, mit der man ein anderes Mitglied der Hand zweifelsfrei identifizieren kann.$B$BSchaut zu, lernt, wiederholt.",
    ["O"] = "Führt Shenthul gegenüber den Gruß der Zerschmetterten Hand aus.",
    ["T"] = "Der Gruß der Zerschmetterten Hand",
  },
  [2478] = {
    ["D"] = "Ihr müsst den Turmschlüssel von Großknecht Silixiz stehlen, $N. Sobald sich der Schlüssel in Eurem Besitz befindet, haltet ihn gut fest - ich könnte ihn später brauchen.$B$BVergesst nicht, das Gift hat alles in diesem Turm geschwächt. Wendet Eure Fähigkeiten an, um unter seinen Bewohnern Chaos und Zerstörung zu verbreiten. Erschlagt sie alle - zeigt keine Gnade!$B$BSobald sich dort drinnen nichts mehr rührt, knackt Ihr das Schloss von Gallywix\' Schließkassette und stehlt den Vorrat an Zanzils Mixtur, der sich darin befindet. Bringt Shenthul in Orgrimmar sowohl Gallywix\' Kopf als auch die Mixtur.",
    ["O"] = "Stehlt Silixiz\' Turmschlüssel von Großknecht Silixiz. Tötet dann 2 mutierte Drohnen der Venture Co., 2 Patrouillen der Venture Co. und 2 Ausgucker der Venture Co.$B$BEliminiert den obersten Großknecht Gallywix und nehmt seinen Kopf. Knackt dann das Schloss von Gallywix\' Schließkassette und stehlt den Vorrat an Zanzils veränderter Mixtur. Bringt den Vorrat und Gallywix\' Kopf zu Shentul nach Orgrimmar; er wird Euch weitere Anweisungen geben.",
    ["T"] = "Mission: möglich, aber nicht sehr wahrscheinlich",
  },
  [2479] = {
    ["D"] = "Bringt eine Probe der Mixtur zu Serge Hinott nach Tarrens Mühle.$B$BWenn Euch überhaupt irgendjemand heilen kann, dann ist das Serge. Falls Ihr lange genug lebt, kann Serge Euch in der Kunst der Herstellung und Anwendung von Giften unterweisen.",
    ["O"] = "Reist nach Tarrens Mühle im Vorgebirge des Hügellands und liefert die Probe von Zanzils Mixtur bei Serge Hinott ab.$B$BUm nach Tarrens Mühle zu kommen, müsst Ihr den Zeppelin nach Unterstadt nehmen und dann auf der Straße nach Süden durch den Silberwald in Richtung Hügellandhof reisen. Folgt den Schildern!",
    ["T"] = "Hinotts Hilfe",
  },
  [2480] = {
    ["D"] = "Noch nie habe ich gesehen, dass ein Gift so wundervolle Auswirkungen hatte! Zanzil, sagt Ihr? Nie von ihm gehört. Aber ich muss ihm zur Kreation dieses Gebräus gratulieren.$B$BTja, ich soll Euch also hiervon heilen und Euch dann beibringen, wie man das Gift anwendet?$B$BGebt mir einen Moment Zeit, damit ich das Toxin untersuchen kann, $N.",
    ["O"] = "Wartet auf Serge Hinott, um das Heilmittel fertig zu stellen.",
    ["T"] = "Hinotts Hilfe",
  },
  [2498] = {
    ["D"] = "Ich habe gerade erfahren, dass Denalan am Al\'Amethsee herausgefunden hat, was der Grund für die Tumore sein könnte, von denen die Bäumlinge befallen sind. Bitte sprecht mit ihm und sagt ihm, dass ich Euch geschickt habe.$B$BSputet Euch, $N; er braucht unsere Hilfe.",
    ["O"] = "Rellian Grünwipfel möchte, dass Ihr am Al\'Amethsee mit Denalan sprecht.",
    ["T"] = "Rückkehr zu Denalan",
  },
  [2499] = {
    ["D"] = "Ich habe es! Ich musste einfach hierher eilen, um Euch zu finden! Es ist eigentlich so einfach... die Verderbnis hat sich von Bäumling zu Bäumling verbreitet, doch ein einziger von ihnen ist die Quelle.$B$BGrimmeiche wird von dem größten Tumor vergiftet, den ich je gesehen habe... ich würde ihn sogar als riesengroß beschreiben.$B$BGrimmeiche lebte früher in einer Höhle in der Nähe meines Sees, hat aber seitdem einen Schlupfwinkel im Osten des Quellsees gefunden. $N, macht Grimmeiche ausfindig und sammelt seinen Tumor ein; entfernt den Ursprung dieser Verderbnis aus meiner Heimat.",
    ["O"] = "Erhaltet den riesengroßen Tumor von Grimmeiche.",
    ["T"] = "Grimmeiche",
  },
  [2500] = {
    ["D"] = "$C, ich möchte Eure Dienste in Anspruch nehmen. Ich bin ein angesehener Alchemist und benötige Eure Unterstützung, um einige Reagenzien zu beschaffen, die im Ödland zu finden sind.$B$BIch brauche folgende Gegenstände, um meine Vorräte aufzufüllen: 5 Bussardmuskelmägen, 10 Klippenkojotenfangzähne und 5 Felselementarsplitter. Natürlich könnt Ihr Euch diese Reagenzien von den Viechern selbst holen; aber wenn Ihr keine Kämpfernatur seid, dann sucht Euch besser Freunde, die kämpfen können.$B$BBringt mir diese Gegenstände, dann springen ein paar Münzen für Euch dabei heraus.$B$BAlso, was sagt Ihr?",
    ["O"] = "Beschafft die Reagenzien, die Ghak Heilsegen aus dem Ödland braucht, und bringt sie ihm nach Thelsamar.",
    ["T"] = "Reagenziensuche im Ödland",
  },
  [2501] = {
    ["D"] = "Ihr seid mir eine große Hilfe gewesen, $N. Ich würde Euch gern das Rezept des Gebräus geben, das Ihr vorhin gekostet habt... doch zuvor muss ich Euch um einen recht gefährlichen Gefallen bitten.$B$BDiese Schließkassette enthält drei leere thaumaturgische Gefäße; sie sind mit einer Einstellungs-Aura magieerfüllt, die einem versengten Wächtergroßdrachen das Blut aussaugt. Wenn Ihr ein solches Gefäß gegen die Bestie einsetzt, wird sie höchst erbost reagieren, also seid vorsichtig. Bringt sie mir, wenn Ihr alle drei gefüllt habt.$B$BGelingt Euch das, gehört das Rezept Euch.",
    ["O"] = "Benutzt die leeren thaumaturgischen Gefäße bei versengten Wächtergroßdrachen im Ödland. Wenn Ihr sie gefüllt habt, bringt sie zu Ghak Heilsegen in Thelsamar.",
    ["T"] = "Reagenziensuche im Ödland II",
  },
  [2518] = {
    ["D"] = "Einst wurde Lady Sathrah von Elune geliebt. Grazil und rein spann die Spinne ihre silbernen Fäden im Mondlicht, wo sie den Abendnebel einfingen. Der silbrige Tau hatte starke Heilkräfte und wurde darum hier im Tempel aufbewahrt.$B$BDoch in jüngster Zeit ist Sathrah dem Wahnsinn verfallen. Auch die ihr nachfolgenden Generationen sind nun gefährdet.$B$BFindet Lady Sathrah, $N, und beendet ihr Leiden. Sie hält sich nordöstlich von hier am Fluss auf. Sammelt ihre silbrigen Spinndrüsen und bringt sie mir.",
    ["O"] = "Erhaltet Lady Sathrahs silbrige Spinndrüsen.",
    ["T"] = "Tränen des Mondes",
  },
  [2521] = {
    ["D"] = "$R, kann Euer winziges Gehirn überhaupt verstehen, wie selten eine völlig fehlerlose Draenethystsphäre ist? Viele Draenei suchten ein Leben lang nach einem dieser Kristalle, doch war ihnen kein Erfolg beschert und sie alle endeten auf tragische Weise.$B$BUnd nun steht Ihr vor Kum\'isha, wissend, was Kum\'isha wünscht, und Ihr möchtet dennoch behilflich sein? Nun gut, $R... Solltet Ihr auf Euren Reisen durch die Lande irgendwann auf eine fehlerlose Draenethystsphäre stoßen, dann bringt sie mir, und ich belohne Euch mit Reichtümern, wie Ihr sie Euch niemals erträumen könnt.",
    ["O"] = "Falls Ihr auf Euren Reisen durch die Verwüsteten Lande je auf eine fehlerlose Draenethystsphäre stoßen solltet, bringt sie zu Kum\'isha dem Sammler.$B$BDoch seid gewarnt. Der Edelstein ist einer der rarsten Kristalle von ganz Azeroth. Jedes Wesen in diesem Land könnte eine fehlerlose Draenethystsphäre bei sich haben.$B$BIhr erhaltet eine Belohnung für jede fehlerlose Draenethystsphäre, die Ihr beschaffen könnt.",
    ["T"] = "Ein Dienst für Kum\'isha",
  },
  [2541] = {
    ["D"] = "Die hier lebenden Schamanen der Knarzklauen haben einen Weg entdeckt, wie man den Geist eines schlafenden Druiden von seinem Körper trennen kann. Die Furbolgs haben meinen physischen Körper reanimiert und lassen ihn jeden angreifen, der versucht, den Grabhügel von Ban\'ethil zu erforschen. Ich selbst bin im Smaragdgrünen Traum gefangen und kann diesem Treiben keinen Einhalt gebieten.$B$BIhr müsst mir helfen. Die Knarzklauenschamanen tragen einen seltsamen Glücksbringer bei sich, mit dem dieses Ritual durchgeführt wird, und ich würde einen solchen Glücksbringer gern untersuchen. Bitte, $N, bringt mir einen.",
    ["O"] = "Bringt einen Voodooglücksbringer des Schamanen zu Oben Zornklaue in den Grabhügel von Ban\'ethil.",
    ["T"] = "Der schlafende Druide",
  },
  [2561] = {
    ["D"] = "Nachdem ich den Glücksbringer untersucht habe, weiß ich jetzt, was zu tun ist, $N. Bitte nehmt ihn und führt aus, worum ich Euch bitte.$B$BIhr müsst Euch der Tür zu der nahegelegenen Kammer nähern. Dahinter findet Ihr meinen seelenlosen Körper. Und obwohl es mich sehr schmerzt, Euch das jetzt zu sagen, sehe ich keine andere Möglichkeit, wie ich mich der Kontrolle der Knarzklauen entziehen kann.$B$BDamit ich mich von ihnen befreien kann, müsst Ihr meinen physischen Körper töten. Sobald das getan ist, wendet den Voodooglücksbringer auf meinen gefallenen Körper an. Und wenn Ihr diese Aufgabe erfüllt habt, kommt zu mir zurück.",
    ["O"] = "Tötet den seelenlosen Körper von Oben Zornklaue und benutzt dann den Voodooglücksbringer.",
    ["T"] = "Druide der Klaue",
  },
  [2582] = {
    ["D"] = "Ah, $N, willkommen. Es scheint, als habe es Euch gefallen, so von Kraft erfüllt und handlungsfähig zu sein, wie es durch die Wirkung der Kicherfangdroge möglich wurde. Doch seid gewarnt: Zwar zeigt sich unmittelbar nach der Einnahme des Präparates ein gigantischer Stärkeschub, doch auf lange Sicht gesehen könnte das Mittel nachteilige Auswirkungen auf Euren Geist und Eure Intelligenz haben.$B$BUnd jetzt, wo Ihr dies wisst - braucht Ihr noch mehr? Falls ja, dann bringt mir drei weitere Kicherfangunterkiefer, zwei feuerverbrannte Eberlungen und eine Skorpokzange.",
    ["O"] = "Bringt Blutmagier Drazial 3 Kicherfangunterkiefer, 2 feuerverbrannte Eberlungen und 1 Skorpokzange.",
    ["T"] = "Wut der Zeiten",
  },
  [2583] = {
    ["D"] = "Hier in den Verwüsteten Landen leben zwei Arten von Ebern: der Aschmähneneber und der Hölleneber.$B$BEs scheint, als mache die Ausdauer dieser Wildtiere sie so gut wie völlig immun gegen Schaden jeglicher Art, da die anderen Tiere zu große Schwierigkeiten haben, sie zu töten, und daher in Ruhe lassen.$B$BWir müssen weitere Analysen durchführen, $N. Wenn Ihr mir drei feuerverbrannte Eberlungen, zwei Skorpokzangen und ein Basiliskengehirn bringt, dann werde ich aus den Überresten der Inhaltsstoffprobe ein himmlisches Elixier kreieren. Und Ihr seid mein erstes Versuchsobjekt!",
    ["O"] = "Bringt Blutmagier Drazial 3 feuerverbrannte Eberlungen, 2 Skorpokzangen und 1 Basiliskengehirn.",
    ["T"] = "Lebenskraft des Ebers",
  },
  [2584] = {
    ["D"] = "Schon als ich das Elixier zum ersten Mal einsetzte, konnte ich sehen, dass eine oder zwei Anwendungen Euer Verlangen danach nicht stillen würden, $N. Selbst jetzt bleibt mir die Gier in Euren Augen nicht verborgen. Das Gebräu hat suchterzeugende Eigenschaften, denen, so scheint es, noch nicht einmal jemand mit äußerst starkem Willen widerstehen kann.$B$BSo sei es denn! Solltet Ihr mehr von dem Lungensaftcocktail benötigen, dann wagt Euch wieder hinaus ins Ödland und besorgt mir drei feuerverbrannte Eberlungen, zwei Skorpokzangen und ein Basiliskengehirn.",
    ["O"] = "Bringt Blutmagier Drazial 3 feuerverbrannte Eberlungen, 2 Skorpokzangen und 1 Basiliskengehirn.",
    ["T"] = "Geist des Ebers",
  },
  [2585] = {
    ["D"] = "Habt Ihr jemals gesehen, wie sie kämpfen, $N? Die Skorpoks, meine ich. Habt Ihr jemals die Anmut und Gewandtheit ihrer Bewegungen gesehen? Gesehen, wie entschlossen sie zuschlagen?$B$BWenn wir nur irgendwie diese Beweglichkeit einfangen und für uns nutzbar machen könnten, dann würden wir so reich werden, wie... ähem, dann wäre das ein unschätzbarer Segen für die wissenschaftliche Gemeinschaft, meine ich natürlich.$B$BHelft mir, $N, dann lasse ich Euch auch von der fertigen Inhaltsstoffprobe kosten.$B$BUm eine reaktionsstabile Probe herzustellen, benötige ich drei Skorpokzangen, zwei Geiermuskelmägen und eine feuerverbrannte Eberlunge.",
    ["O"] = "Bringt Blutmagier Drazial 3 Skorpokzangen, 2 Geiermuskelmägen und 1 feuerverbrannte Eberlunge.",
    ["T"] = "Der Entscheidungsschlag",
  },
  [2586] = {
    ["D"] = "Ihr lebt noch! Das sind wahrlich gute Neuigkeiten. Ich vermute mal, Ihr seid wieder da, weil Ihr mehr von der Skorpokinhaltsstoffprobe möchtet?$B$BTja, $N, dann mal ab ins Ödland mit Euch, und kommt erst zurück, wenn Ihr drei Skorpokzangen, zwei Geiermuskelmägen und eine feuerverbrannte Eberlunge eingesammelt habt.",
    ["O"] = "Bringt Blutmagier Drazial 3 Skorpokzangen, 2 Geiermuskelmägen und 1 feuerverbrannte Eberlunge.",
    ["T"] = "Salz der Skorpok",
  },
  [2601] = {
    ["D"] = "In der Einöde wimmelt es nur so von Rotsteinbasilisken, $N. Diese Basilisken haben sich an ihre Umgebung angepasst und sind in ihr gediehen, mehr als jede andere wilde Tierart in diesem Gebiet.$B$BIch habe sie schon oft von weitem beobachtet, wenn sie jagen und ihren täglichen Aktivitäten nachgehen: Diese Wildtiere zeigen Denkprozesse, die komplexer sind als die mancher Humanoiden!$B$BIch muss ihre Gehirne studieren!$B$BBringt mir zehn ihrer Gehirne, $N. Doch für das Präparat benötige ich auch zwei Geiermuskelmägen. Diese bilden die Grundlage der Lösung.",
    ["O"] = "Bringt Blutmagierin Lynnore 10 Basiliskengehirne und 2 Geiermuskelmägen.",
    ["T"] = "Der Biss des Basilisken",
  },
  [2602] = {
    ["D"] = "Bringt Blutmagierin Lynnore 10 Basiliskengehirne und 2 Geiermuskelmägen.",
    ["O"] = "Bringt Blutmagierin Lynnore 10 Basiliskengehirne und 2 Geiermuskelmägen.",
    ["T"] = "Unfehlbare Gedanken",
  },
  [2603] = {
    ["D"] = "Hier in den Verwüsteten Landen besitzt der Tod tatsächlich Flügel, $R. Ich habe gesehen, wie die schwarzen Töter und die Knochenpflücker sich am Kadaver so manch eines gefallenen Forschers gütlich taten - und manchmal fraßen sie sogar die übelsten, von Seuchen zerfressenen Leichen auf. Da ist es schon ein Wunder, wie ihre inneren Organe die Pestilenz überwinden, die in diesen Landen wütet.$B$BHelft mir bei meinen Studien, $N! Ich brauche Muskelmägen! Aber ich benötige auch zwei Kicherfangunterkiefer. Diese stellen die Grundlage für das Gebräu dar.$B$BFür Eure Anstrengungen erhaltet Ihr eine doppelte Belohnung.",
    ["O"] = "Bringt Blutmagierin Lynnore 10 Geiermuskelmägen und 2 Kicherfangunterkiefer.",
    ["T"] = "Lebensenergie des Geiers",
  },
  [2604] = {
    ["D"] = "Wie ich sehe, ist Euer inneres Feuer erloschen. Was meint Ihr, hat das geistige Hochgefühl etwa eine Stunde angedauert?$B$BFalls Ihr mehr von der Muskelmagenkaumasse benötigt, dann benötige ich mehr Muskelmägen. Zurück in die Einöde mit Euch, $N. Kommt erst wieder, wenn Ihr zehn Muskelmägen und zwei Unterkiefer beschafft habt.",
    ["O"] = "Bringt Blutmagierin Lynnore 10 Geiermuskelmägen und 2 Kicherfangunterkiefer.",
    ["T"] = "Geistige Beherrschung",
  },
  [2605] = {
    ["D"] = "Ich hab da so eine Idee. Ich habe von einigen seltsamen Kreaturen namens \'Tausammler\' gehört, die im Südwesten leben. Angeblich können sie in speziellen Drüsen Wasser speichern.$B$BIch glaube, wenn ich eine von diesen Drüsen bekäme, dann könnte ich ein Getränk kreieren, das meinen schrecklichen Durst löschen würde. Könntet Ihr mir vielleicht helfen, $N?$B$BSucht bei den Kakteen, $N: Dort findet Ihr die Tausammler.",
    ["O"] = "Beschafft Euch eine volle Taudrüse und bringt sie nach Gadgetzan zu Marin Noggenfogger.",
    ["T"] = "Der durstige Goblin",
  },
  [2606] = {
    ["D"] = "Bäh! Igitt! Das Zeug schmeckt ja eklig! So viel dazu...$B$BBitte, was meint Ihr? Ja, ich weiß schon, Ihr findet, ich sollte nicht so schnell die Flinte ins Korn werfen. Na dann.$B$BÄhm, da fällt mir was ein! Das ist genial! Bringt diesen Tau meiner Freundin Sprinkel - sie ist eine tolle Küchenchefin. Sagt ihr, was wir vorhaben, und dass wir das Zeug etwas aufpeppen wollen.$B$BIch meine, wenn es schon so fies schmeckt, dann sollte es wenigstens einen kleinen... Kick haben. Und davon kann ich nichts bemerken...",
    ["O"] = "Marin Noggenfogger möchte, dass Ihr in Gadgetzan mit Sprinkel sprecht.",
    ["T"] = "Dem guten Geschmacke",
  },
  [2607] = {
    ["D"] = "Es scheint, als hättet Ihr mehr Informationen bekommen, als selbst ich erwartet hätte. Es ist schon seltsam, wie das Schicksal manchmal spielt, $N.$B$BEin Heilmittel? Hmm... Geht hinunter in den Keller und sprecht mit Dok Mixilpixil: Vielleicht kann er Euch helfen.",
    ["O"] = "Sprecht im Keller der Kaserne mit Dok Mixilpixil.",
    ["T"] = "Die Berührung von Zanzil",
  },
  [2608] = {
    ["D"] = "Meine Güte, Ihr seht ja aus wie der Tod. Nein, falsch. Ich habe den Tod schon gesehen - Ihr seht schlimmer aus. Ihr seht aus wie das, was der Tod bei seiner letzten Vorsorgeuntersuchung ausgespuckt hat.$B$BHa! Habt Ihr das gehört, Noarm? Schreibt das auf!$B$B<hustet> Ähem, ja, damit ich Euch helfen kann, muss ich Euch Blut abnehmen. Und keine Sorge, es tut nur die ersten... fünfzig Male oder so weh... Wenn Ihr bereit für die Behandlung seid, $N, solltet Ihr Euch bitte /hinlegen.",
    ["O"] = "Ihr solltet Euch /hinlegen, damit Dok Mixilpixil Euch untersuchen kann. Denn wie soll er ein Heilmittel finden, wenn er die Ursache nicht finden kann?",
    ["T"] = "Die Berührung von Zanzil",
  },
  [2609] = {
    ["D"] = "Wir sind einstimmig zu folgendem Schluss gekommen, $N... Ihr habt eine \'Itis\'.$B$BZum Glück kann ich die \'Itis\' heilen.$B$BUnd das brauche ich dafür: ein Bündel einfache Wildblumen, eine verbleite Phiole, eine Bronzeröhre und schließlich und endlich noch eine Rolle blassgelben Seidenfaden. Die alte Emma hat in \'Das feinste Band\' eine Rolle davon für mich hinterlassen. Ihr findet den Laden an einem der Kanäle.$B$BBeeilt Euch jetzt, $N, und vermeidet es möglichst, jemanden anzufassen, bis Ihr geheilt seid. Dr. Montgomery meint übrigens, Ihr könntet eine Dusche vertragen - das hat er gesagt, nicht ich.",
    ["O"] = "Bringt Dok Mixilpixil 1 Bündel einfache Wildblumen, 1 verbleite Phiole, 1 Bronzeröhre und 1 Rolle blassgelben Seidenfaden. So eine \'Itis\' heilt sich nicht von selbst, $Gjunger Herr:junge Dame;.",
    ["T"] = "Die Berührung von Zanzil",
  },
  [2621] = {
    ["D"] = "Sprecht in Steinard in den Sümpfen des Elends mit Zentralenkommandant Ruag.",
    ["O"] = "Sprecht in Steinard in den Sümpfen des Elends mit Zentralenkommandant Ruag.",
    ["T"] = "Der Entehrte",
  },
  [2622] = {
    ["D"] = "Die Befehle? Jetzt sagt mir doch mal, was Ihr mit diesen Befehlen wollt!$B$BWas wollt Ihr damit andeuten, $N? Natürlich habe ich die Befehle weitergegeben! Die Soldaten, denen ich die Befehle mitgab, wurden von Murlocs überfallen und auf brutalste Weise misshandelt. Nur ein Einziger von ihnen hat überlebt... wenn man das überleben nennen kann. Er kann kaum sprechen, kann nicht mehr laufen und muss durch einen Schlauch ernährt werden. Wenn Ihr wissen wollt, was mit diesen Befehlen passiert ist, dann sprecht mit Bengor. Er treibt sich hier mit Sicherheit irgendwo herum... und, $N, seid vorsichtig, in was Ihr Eure Nase steckt. Nicht, dass sie Euch abgebissen wird!",
    ["O"] = "Sprecht mit Bengor in Steinard in den Sümpfen des Elends.",
    ["T"] = "Die fehlenden Befehle",
  },
  [2623] = {
    ["D"] = "Beschafft die Befehle des Kriegshäuptlings wieder und gebt sie dem gefallenen Helden der Horde zurück.",
    ["O"] = "Beschafft die Befehle des Kriegshäuptlings wieder und gebt sie dem gefallenen Helden der Horde zurück.",
    ["T"] = "Der Sumpfredner",
  },
  [2641] = {
    ["D"] = "Als ich noch ein kleines Mädchen war, hat meine Schwester immer gekocht. Es war grauenhaft! Manche ihrer \'Gerichte\' konnte ich kaum runterwürgen - bis ich irgendwann meine \'Geheimzutat\' entdeckt habe! Egal, zu was man sie hinzufügt... im Nu schmeckt alles toll!$B$BUm was es sich dabei handelt, wollt Ihr wissen? Es ist ein Pilz namens violetter Tragan. Wenn Ihr wollt, dass ich dieses fürchterlich schmeckende Gebräu in etwas Genießbares verwandle, dann begebt Euch ins Hinterland und sucht dort nach einem kleinen See.$B$BUnd vergesst nicht: Violette Tragane findet man nur unter Wasser.",
    ["O"] = "Sprinkel in Gadgetzan möchte, dass Ihr einen violetten Tragan sucht und ihr bringt.",
    ["T"] = "Sprinkels Geheimzutat",
  },
  [2661] = {
    ["D"] = "Hier habt Ihr das violette Pulver, $N. Bringt es zu Marin und sagt ihm, er schuldet mir was!",
    ["O"] = "Sprinkel möchte, dass Ihr das violette Pulver nach Gadgetzan zu Marin Noggenfogger bringt.",
    ["T"] = "Lieferung für Marin",
  },
  [2662] = {
    ["D"] = "So, jetzt mische ich das Pulver unter den Tau.$B$BSprecht mich in einer Minute noch mal an... wenn ich Noggenfoggers fabelhaftes Elixier perfektioniert habe!",
    ["O"] = "Marin Noggenfogger möchte, dass Ihr ihn nochmals ansprecht, wenn er sein Elixier fertiggestellt hat.",
    ["T"] = "Noggenfoggers Elixier",
  },
  [2681] = {
    ["D"] = "Allein wird es Euch nicht gelingen, die Streitmacht von Razelikh und seinen Untergebenen zu überwinden.$B$BSammelt eine Gruppe von geeigneten Abenteurern um Euch und befreit meine Männer aus der ewigen Qual! Achtzehn Steine müsst Ihr zerschmettern.$B$BKämpft hart und sterbt ehrenvoll, $N.",
    ["O"] = "Befreit 9 Diener von Razelikh, 3 Diener von Sevine, 3 Diener von Allistarj und 3 Diener von Grol. Kehrt zum gefallenen Helden zurück, sobald Ihr die Aufgabe erfüllt habt. Ihr müsst nahe bei den Steinen bleiben, sonst habt Ihr keinen Erfolg.",
    ["T"] = "Die Steine, die uns binden",
  },
  [2701] = {
    ["D"] = "Öffnet die Truhe und nehmt Euch Eure Belohnung.",
    ["O"] = "Öffnet die Truhe und nehmt Euch Eure Belohnung.",
    ["T"] = "Helden der Vergangenheit",
  },
  [2702] = {
    ["D"] = "Mit Eurem Edelmut und Eurer Heldenhaftigkeit erweist Ihr mir Ehre, $N.$B$BUnd auch ihnen habt Ihr Ehre erwiesen...",
    ["O"] = "Sprecht mit Korporal Thund Spalthuf.",
    ["T"] = "Helden der Vergangenheit",
  },
  [2721] = {
    ["D"] = "Ein Kapitel Eurer Abenteuer mag jetzt beendet sein, doch ein weiteres Kapitel beginnt gerade, $N.$B$BEiner meiner Männer ist noch immer verschollen, irgendwo in den Verwüsteten Landen verschwunden. Ich spreche natürlich von Leutnant Kirith.$B$BWie ich vorhin schon sagte, wurden wir beide von Allistarj gefangen genommen und gefoltert, aber ich fürchte, dass der arme Kirith nicht durchhielt und schwach wurde. Aus meinem Käfig heraus konnte ich die Experimente hören: Die gequälten Schreie hallten in der ganzen Höhle wider. Das war das Letzte, was ich von Kirith gesehen oder gehört habe.",
    ["O"] = "Findet heraus, was aus Leutnant Kirith geworden ist.",
    ["T"] = "Kirith",
  },
  [2742] = {
    ["D"] = "He, $R!$B$BBitte, helft Rin\'ji flüchten. Ich weiß, Ihr seid $R, mögt Rin\'jis Volk nicht, aber Rin\'ji bittet und bettelt! Muss hier raus!$B$BHab auch was Schönes für $R, wenn $R nett ist und Rin\'ji hier raushilft...$B$BRin\'ji hat was Geheimes bei den Aussichtsklippen versteckt.",
    ["O"] = "Eskortiert Rin\'ji aus der Jagdhütte von Quel\'Danil heraus und findet dann das, was er bei den Aussichtsklippen im Osten versteckt hat.",
    ["T"] = "Rin\'ji steckt in der Falle!",
  },
  [2744] = {
    ["D"] = "Das Dreigestirn wird von Razelikh beschützt.$B$BAls Lohn für ihre Dienste hat Razelikh jeden von ihnen mit schier unbegrenzter Macht in den Verwüsteten Landen ausgestattet. Die Teile des Amuletts sind auf die Lebenskraft ihrer jeweiligen Besitzer eingestellt und verleihen diesen Unsterblichkeit. Solange die sich in den Verwüsteten Landen aufhalten, können sie nicht getötet werden, und auch ihre Lebensspanne ist bis zum Ende aller Tage ausgedehnt.$B$BDoch einen gibt es, der Euch vielleicht weiterhelfen kann, $N. Reist nach Azshara und sucht dort nach dem Dämonenjäger Loramus Thalipedes. Ihr findet ihn vermutlich auf den Inseln.",
    ["O"] = "Sprecht mit Loramus Thalipedes in Azshara.",
    ["T"] = "Der Dämonenjäger",
  },
  [2745] = {
    ["D"] = "Hmm, eine interessante Geschichte und eine, die man nicht auf die leichte Schulter nehmen sollte. Ja, ich kann Euch helfen, aber wenn Ihr das wirklich bis zum Ende durchziehen wollt, dann müsst Ihr andere Saiten aufziehen, die... nicht ganz legal sind.$B$BLescovar und Marzon müssen erledigt werden, und zwar schnell. Glaubt Ihr, Ihr seid zu so was fähig?$B$BDie Entscheidung liegt bei Euch. Falls ja, dann geht so schnell wie möglich ins Schloss und redet dort mit einem Freund von mir, einem Gnom namens Tyrion. Er beobachtet Lescovar jetzt schon seit ein paar Wochen. Er sollte Euch dabei helfen können, die Sache zu Ende zu bringen.",
    ["O"] = "Sprecht in Schloss Sturmwind mit Tyrion.",
    ["T"] = "Eindringen ins Schloss",
  },
  [2746] = {
    ["D"] = "Trias will also alles ein bisschen beschleunigen, wie? Na gut, können wir zwar machen, aber ich bin noch nicht bereit. Und damit eines mal klar ist: Ich hab den anstrengenderen Job, aber Ihr seid $Gderjenige, der:diejenige, die; sich die Hände schmutzig macht, verstanden? Gut.$B$BAlso, Ihr müsst mir ein paar Sachen besorgen, bevor ich meinen Plan in die Tat umsetzen kann. Ich hab schon die perfekte Verkleidung, um den Spionagebot an den Wachen vorbeizubekommen.$B$BBringt mir etwas Seide und ein paar Äpfel. Wo Ihr die Seide herkriegt, ist Eure Sache, aber auf Claras kleinem Hof draußen vor der Stadt gibt es Äpfel in genau der richtigen Größe.",
    ["O"] = "Bringt 3 Stück Seidenstoff und 2 frische Äpfel von Clara nach Sturmwind zu Tyrion.",
    ["T"] = "Nicht unwichtige Gegenstände",
  },
  [2751] = {
    ["D"] = "Zwanzig Generationen von Omoshs haben diesen Hammer bereits in Händen gehalten, $N. Hunderttausend Tonnen Metall haben wir geformt. Doch leider, leider endet alles mit Orokk, dem Fehlschlag der Omosh: ohne Verwandte und nicht in der Lage, sich fortzupflanzen.$B$BIch kann nicht zulassen, dass die geheimen Rezepturen der Omosh mit mir sterben! Jemand muss das Werk fortführen, ganz gleich, ob derjenige ein Omosh ist oder nicht!$B$BBeweist Orokk, dass Ihr fähig seid, $N. Bringt Orokk die folgenden Dinge: zwei gemusterte bronzene Armschienen, zwei bronzene Großschwerter und zwei scharfe Klauen. Bringt sie Orokk, dann machen wir weiter.",
    ["O"] = "Bringt 2 gemusterte bronzene Armschienen, 2 bronzene Großschwerter und 2 scharfe Klauen zu Orokk Omosh in Orgrimmar.",
    ["T"] = "Barbarische Waffenschmiedekunst",
  },
  [2752] = {
    ["D"] = "Orokk sieht den Eifer in Euren Augen, $N. Wie ein Schwamm saugt Ihr das Wissen der Omoshs auf.$B$BDrei Lektionen werde ich Euch lehren.$B$BKehrt zum Amboss zurück, schwitzt in der Hitze der Schmiede und stellt für Orokk vier bronzene Streitäxte und vier bronzene Kriegshämmer her. Erst dann könnt Ihr etwas über Eisenschulterstücke lernen.",
    ["O"] = "Bringt 4 bronzene Streitäxte und 4 bronzene Kriegshämmer zu Orokk Omosh in Orgrimmar.",
    ["T"] = "Über Eisenschulterstücke",
  },
  [2753] = {
    ["D"] = "Nur zwei Lektionen noch hat Orokk für Euch, $N. Eure Muskeln schmerzen, ich weiß, doch macht weiter - für die Omoshs!$B$BBringt Orokk vier grüne Eisenhelme, vier grüne Eisenarmschienen und zwei grüne Eisengamaschen - zur Belohnung werdet Ihr lernen und Eure Feinde werden leiden!",
    ["O"] = "Bringt 4 grüne Eisenhelme, 4 grüne Eisenarmschienen und 2 grüne Eisengamaschen zu Orokk Omosh in Orgrimmar.",
    ["T"] = "In den Boden getrampelt",
  },
  [2754] = {
    ["D"] = "Bringt 2 robuste Eisenschlägel, 2 bronzene Stiefel mit Versilberung und 2 bronzene Stulpen mit Versilberung zu Orokk Omosh in Orgrimmar.",
    ["O"] = "Bringt 2 robuste Eisenschlägel, 2 bronzene Stiefel mit Versilberung und 2 bronzene Stulpen mit Versilberung zu Orokk Omosh in Orgrimmar.",
    ["T"] = "Hörner der Raserei",
  },
  [2755] = {
    ["D"] = "Seht zu und lernt den Freudentanz der Omoshs.",
    ["O"] = "Seht zu und lernt den Freudentanz der Omoshs.",
    ["T"] = "Freude der Omoshs",
  },
  [2757] = {
    ["D"] = "Hört mal, wenn Ihr hier herumhängt, lernt Ihr nix.$B$BIch kenn da so \'n Typen, der \'n Typen kennt. Der treibt sich bei der Schmiede in Beutebucht rum. Wenn Ihr ihm das Insigne hier zeigt, dann erzählt er Euch alles über Mithril.$B$BAlso, was sagt Ihr? Überlasst diesen Babykram den Kleinkindern, $N - für Euch ist es Zeit weiterzuziehen.$B$BFalls Ihr Interesse haben solltet, der Typ heißt McGavan. Zeigt ihm das hier, dann wird er sehen, dass es Euch ernst ist.",
    ["O"] = "Sprecht in Beutebucht mit McGavan.",
    ["T"] = "Auf nach Beutebucht!",
  },
  [2758] = {
    ["D"] = "Als ich etwa so alt war wie Ihr, nahm mich ein Zwerg namens Galvan unter seine Fittiche und bildete mich in der Kunst des Rüstungsschmiedens aus. Bevor er sich nach Beutebucht aufmachte, nahm er mir das Versprechen ab, dass ich all denjenigen ein Lehrer sein würde, die den Wunsch hätten und entschlossen genug wären, selbst eine Schmiedelaufbahn einschlagen zu wollen. Und hier bin ich nun - all die Jahre später.$B$BAlso, es ist jetzt Zeit für Euch, ein bisschen was zu lernen. Stellt mir sechs goldene Schuppenarmschienen her, dann zeige ich Euch, was echte Schmiedekunst ist, $N.",
    ["O"] = "Bringt 6 goldene Schuppenarmschienen zu Hank dem Hammer in Sturmwind.",
    ["T"] = "Die Herkunft der Schmiedekunst",
  },
  [2759] = {
    ["D"] = "Obwohl Galvan Beutebucht schon vor vielen Jahren verlassen hat, leben noch immer Mitglieder des Ordens in der Stadt und hoffen, dass sie eines Tages jemanden finden werden, der der Lehre von Galvan würdig ist.$B$BNehmt dieses Insigne und zeigt es McGavan in Beutebucht.$B$BSolltet Ihr es tatsächlich bis dorthin schaffen, dann weiß er, dass Ihr würdig seid. Ich glaube an Euch, $N!",
    ["O"] = "Sprecht in Beutebucht mit McGavan.",
    ["T"] = "Auf der Suche nach Galvan",
  },
  [2760] = {
    ["D"] = "Sprecht mit Galvan dem Alten im Schlingendorntal.",
    ["O"] = "Sprecht mit Galvan dem Alten im Schlingendorntal.",
    ["T"] = "Der Mithrilorden",
  },
  [2761] = {
    ["D"] = "Viele Schmiede glauben, der Bergbau und die Schmiedekunst seien zwei völlig getrennte, voneinander unabhängige Dinge, aber wie kann es Schmiedekunst geben ohne Bergbau? Genau, das ist völlig unmöglich! Und daher muss der vollendete Schmied auch ein tüchtiger Minenarbeiter sein.$B$BZeigt Galvan, dass Ihr fähig seid, $N. Bringt mir vierzig Barren Eisen und vierzig Barren Mithril. Dafür sagt Galvan Euch dann alles über verschnörkelte Mithrilhosen!",
    ["O"] = "Bringt Galvan dem Alten im Schlingendorntal 40 Mithrilbarren und 40 Eisenbarren.",
    ["T"] = "Hier verhütten, da verhütten",
  },
  [2762] = {
    ["D"] = "Es heißt, dass Echtsilber noch stärker und haltbarer sei als Mithril. Galvan widerspricht dieser Einschätzung der Mineralien zwar nicht, aber Echtsilber trägt seinen Namen zu Unrecht. Echtsilber ist nicht echt! Mithril ist ein wesentlich edleres Mineral! HA! Aber wie dem auch sei...für diese Lektion müsst Ihr den silbernen Betrüger finden, $N.$B$BBringt mir vierzig Barren Mithril und fünf Barren Echtsilber. Im Gegenzug werdet Ihr in das Geheimnis der verschnörkelten Mithrilhandschuhe eingeführt, und, was noch wichtiger ist, Ihr werdet lernen, dass Echtsilber lügt!",
    ["O"] = "Bringt Galvan dem Alten im Schlingendorntal 40 Mithrilbarren und 5 Echtsilberbarren.",
    ["T"] = "Der große silberne Betrüger",
  },
  [2763] = {
    ["D"] = "Der Abbau von wertvollen Edelsteinen ist für unsere Zunft von äußerster Wichtigkeit, $N. Und nur wenige Edelsteine sind wertvoller als der Citrin. Die magischen Eigenschaften, mit denen wir unsere Waren ausstatten, werden deutlich verstärkt, wenn Citrine im Spiel sind.$B$BIn dieser Lektion verstärken wir vierzig Barren Mithril mit vier Citrinen. So bekommt Ihr ein besseres Verständnis für die magische Edelsteinkunde und lernt außerdem, wie man verschnörkelte Mithrilschultern herstellt.",
    ["O"] = "Bringt Galvan dem Alten im Schlingendorntal 40 Mithrilbarren und 4 Citrine.",
    ["T"] = "Die Kunst der Magieerfüllung",
  },
  [2764] = {
    ["D"] = "Wenn Ihr bereit seid, Eure Ausbildung bei Galvan abzuschließen, dann solltet Ihr nach Gadgetzan reisen.$B$BDort findet Ihr meinen besten Schüler, Trenton Lichthammer. Er kann Euch viel über verschnörkeltes Mithril beibringen. Ansonsten bleibt hier und lernt weiter!$B$BAch ja, Gadgetzan liegt natürlich in Tanaris!",
    ["O"] = "Sprecht in Gadgetzan mit Trenton Lichthammer.",
    ["T"] = "Galvans bester Schüler",
  },
  [2765] = {
    ["D"] = "Schon als Ihr das erste Mal zu mir kamt, wusste ich, dass Ihr Euch als einer meiner besten Schüler herausstellen würdet! Ohne zu zögern trenntet Ihr Euch von einem nicht recht mächtigen Gegenstand und gabt ihn McGavan. Ihr tatet dies, nur damit Ihr mich treffen konntet. Solch blindes Vertrauen und Hingabe an unser Handwerk soll nicht unbelohnt bleiben.$B$BIhr seid zu einem wahren Experten der Schmiedekunst geworden, $N. Nun erntet den Lohn dafür!",
    ["O"] = "Fordert Eure Belohnung von Galvan dem Alten ein!",
    ["T"] = "Schmiedkunstexperte!",
  },
  [2766] = {
    ["D"] = "Ihr habt ein merkwürdiges Gerät aus Metall entdeckt, das die Form eines Eis hat. Als Ihr einen der Knöpfe berührt, erwacht das Ei zum Leben und verwandelt sich in eine Art Gnom-Roboter-Apparat! Aus dem Inneren des Roboteis erklingt eine rasselnde Stimme.$B$B\"Mein Name ist Glotz Widrikus und mein Leitroboter ist abgestürzt! Ich werde Euch belohnen, wenn Ihr ihn aufspürt. Bringt dem Roboter bitte diesen Signalgeber!\"$B$B\"Ich habe jetzt die Koordinaten des Roboters für Euch berechnet. Er ist in der Nähe des Wildschrammtals abgestürzt!\"",
    ["O"] = "Bringt das Notrufsignal zu Glotz\' Leitroboter in einer Höhle in der Nähe des Wildschrammtals.",
    ["T"] = "Findet OOX-22/FE!",
  },
  [2767] = {
    ["D"] = "Bringt OOX-22/FE in Sicherheit und meldet Euch dann bei Glotz Widrikus in Beutebucht.",
    ["O"] = "Bringt OOX-22/FE in Sicherheit und meldet Euch dann bei Glotz Widrikus in Beutebucht.",
    ["T"] = "Rettet OOX-22/FE",
  },
  [2768] = {
    ["D"] = "Unteroffizier Bly hat mich bestohlen! Er sagte, er wolle sie sich nur ausleihen, aber er hat meine geliebte Wünschel-mato-Rute gestohlen! Wo soll ich denn bitte ohne diese Rute wissen, wo ich neue Wasserlöcher graben muss?$B$BFindet Bly und bringt mir meine Rute! Ich habe gehört, er hätte seine Bande von zwielichtigen Abenteurern auf eine Schatzsuche nach Zul\'Farrak im Westen geführt. Ich möchte wetten, dort findet Ihr ihn.$B$BHoffen wir, dass die Trolle ihn schon erledigt haben, denn falls Ihr Euch wegen der Rute mit ihm anlegen müsst, dann steht Euch ein schwerer Kampf bevor.",
    ["O"] = "Bringt die Wünschel-mato-Rute nach Gadgetzan zu Chefingenieur Bilgenritzel.",
    ["T"] = "Wünschel-mato-Rute",
  },
  [2769] = {
    ["D"] = "Seid Ihr den Gebrüdern Kupferbolz schon mal begegnet? Sie sind drüben in der Schimmernden Ebene, in Kalimdor, um genau zu sein, und treten dort in Raketenwagenrennen gegen die Goblins an. Normalerweise gewinnen sie, aber das ist keine große Überraschung - der Wagen der Goblins fliegt so oft in die Luft, dass er es kaum jemals über die Ziellinie schafft!$B$BAber trotzdem suchen die Kupferbolz-Brüder ständig nach neuen Möglichkeiten, ihren Wagen noch schneller zu machen, und Wizzel hat mir gesagt, dass er für sein nächstes Experiment noch eine sehr seltene Komponente benötigt.$B$BWahrscheinlich kommt er an dieses Ersatzteil nicht allein ran. Sprecht mit ihm, dann erfahrt Ihr mehr darüber.",
    ["O"] = "Sprecht mit Wizzel Kupferbolz in der Schimmernden Ebene.",
    ["T"] = "Die Brüder Kupferbolz",
  },
  [2770] = {
    ["D"] = "Tief im Inneren von Zul\'Farrak, der Stadt der Sandtrolle in Tanaris, liegt ein hochheiliger Teich. An diesem Teich beschwören die Trolle eine gewaltige Bestie, Gahz\'rilla! Der ist so wild, dass sogar seine Schuppen vor Energie knistern. Und genau diese Energie will ich in meinem Wagen einsetzen.$B$BBringt mir die energiegeladene Schuppe von Gahz\'rilla!$B$BWie man Gahz\'rilla allerdings beschwört, das verraten die Trolle niemandem. Wenn Ihr ihm begegnen wollt, müsst Ihr daher erst den Trollen das Geheimnis entreißen.",
    ["O"] = "Bringt Wizzel Kupferbolz in der Schimmernden Ebene Gahz\'rillas energiegeladene Schuppe.",
    ["T"] = "Gahz\'rilla",
  },
  [2771] = {
    ["D"] = "Es scheint, als habe der Meister dies von Anfang an geplant. Ich habe nicht gelernt, solche Gegenstände herzustellen, wie Ihr es könnt, und Ihr habt nicht gelernt, solche Gegenstände herzustellen, wie ich es kann.$B$BDer Meister ist klug, $N. Wir werden uns gegenseitig etwas beibringen!$B$BZeigt mir, wie man eine Mithrilhelmkappe und verschnörkelte Mithrilschultern herstellt, dann zeige ich Euch, wie man einen verschnörkelten Mithrilhelm macht.",
    ["O"] = "Bringt Trenton Lichthammer 2 Mithrilhelmkappen und 1 verschnörkelte Mithrilschulter.",
    ["T"] = "Ein schlauer Kopf auf den Schultern",
  },
  [2772] = {
    ["D"] = "Bei der Talmeisterschaft der Schmiedekunst letztes Jahr mussten alle, die zusahen, erleben, wie mir so ein feiger Hund von der Gießerei der Venture Company die Beine unter dem Körper wegzog. Aber den Grund, warum ich weitermachen und die Meisterschaft am Ende gewinnen konnte, den wusste niemand. Genau, verschnörkelte Mithrilstiefel!$B$BAber für die Meisterschaft dieses Jahr muss ich lernen, wie man verschnörkelte Mithrilhosen fertigt, denn ich muss gegen einen Schredder der Venture Co. antreten! Bringt mir bei, wie man die Hosen anfertigt, und lernt, was es mit den Stiefeln auf sich hat!",
    ["O"] = "Bringt Trenton Lichthammer 2 schwere Mithrilstiefel und 1 verschnörkelte Mithrilhose.",
    ["T"] = "Die Welt zu Euren Füßen",
  },
  [2773] = {
    ["D"] = "Hier in der Gegend bin ich als Mithril-Kid bekannt. Ich beliefere Abenteurer aus aller Welt mit verschnörkeltem Mithril.$B$B<Trenton flüstert.>$B$BDie wissen aber nicht, dass ich bisher noch keine verschnörkelten Mithrilhandschuhe herstellen kann. Psst! Das bleibt unser kleines Geheimnis, ja?$B$BBringt mir bei, wie man verschnörkelte Mithrilhandschuhe fertigt, $N, dann zeige ich Euch im Gegenzug den Höhepunkt meiner Ausbildung unter Galvan: die verschnörkelte Mithrilbrustplatte.",
    ["O"] = "Bringt Trenton Lichthammer 2 schwere Mithrilbrustplatten und 1 verschnörkelten Mithrilhandschuh.",
    ["T"] = "Mithril-Kid",
  },
  [2781] = {
    ["D"] = "GESUCHT: Kalif Skorpidstich!$B$BDie Gadgetzan Water Company aus Gadgetzan in Tanaris ist bereit, eine hohe Belohnung für den Kopf von Kalif Skorpidstich, dem Anführer der Wüstenläufernomaden, zu zahlen. Seine Verbrechen belaufen sich auf:$B$B1. Ermordung von Angestellten der Gadgetzan Water Company!$B2. Stehlen von Rohstoffen der Gadgetzan Water Company!$B3. Besetzen von Eigentum der Gadgetzan Water Company!$B$BBringt Beweise für Kalif Skorpidstichs Untergang zu Chefingenieur Bilgenritzel und Ihr werdet umgehend belohnt!",
    ["O"] = "Bringt Chefingenieur Bilgenritzel in Gadgetzan den Kopf von Kalif Skorpidstich.",
    ["T"] = "GESUCHT: Kalif Skorpidstich",
  },
  [2782] = {
    ["D"] = "Die Tafel ist mit Schriftzeichen der Bleichborkentrolle bedeckt. Rin\'jis Geheimnis ist wirklich ein Mysterium... $B$BGut möglich, dass Ihr hier etwas ziemlich Wertvolles in Händen haltet. Andererseits könnte ebenso gut das Gegenteil der Fall sein. Ein Troll der Bleichborken in Not würde wahrscheinlich alles tun, um einen Ausweg aus seinen Problemen zu finden...",
    ["O"] = "Oran Schlangenfuß, eine bekannte Gelehrte auf dem Gebiet der Bleichborkentrolle, könnte die Tafel vielleicht übersetzen. Normalerweise hält sie sich im Magierviertel von Unterstadt auf.",
    ["T"] = "Rin\'jis Geheimnis",
  },
  [2783] = {
    ["D"] = "Wir bekämpfen den falschen Feind, $N.$B$BDie wahre Macht in diesem Land wird zahlen- und kräftemäßig immer stärker, während wir uns mit den Orcs über verbrannte Erde zanken.$B$BSollen wir wirklich in diesem Land einen Krieg gegen die Horde führen, nur um eines Tages von den Armeen der Brennenden Legion hinweggefegt zu werden? Es ist sinnlos.$B$BBegrabt Eure Streitigkeiten, $N, und sprecht mit dem gefallenen Helden der Horde. Er kennt die Wahrheit über die Verwüsteten Lande.",
    ["O"] = "Sprecht mit dem gefallenen Helden der Horde im Grenzgebiet zwischen den Verwüsteten Landen und dem Sumpf.",
    ["T"] = "Kleinliches Gezänk",
  },
  [2801] = {
    ["D"] = "Bevor wir fortfahren können, müsst Ihr meine Geschichte hören.",
    ["O"] = "Lauscht der Geschichte des gefallenen Helden der Horde.",
    ["T"] = "Eine Geschichte voller Elend",
  },
  [2821] = {
    ["D"] = "Meine Einstellung zur Lederverarbeitung verlangt, dass die Arbeit von bestmöglicher Qualität zu sein hat - ohne Kompromisse. Jeder, der behauptet, Qualität sei unwichtig, wird auch versuchen, Euch ein Küstengrundstück in Dun Morogh zu verkaufen!$B$BAuf meiner jüngsten Suche nach hochwertigem Material fand ich dicke Yetibälge. Die Yetis sind in Feralas zuhause und viele findet man im Wildschrammtal. Ich hätte gern welche, und wenn Ihr mir zehn davon bringt, dürft Ihr Euch ein beliebiges Stück meiner Lederwaren aussuchen. Haltet Ihr das für angemessen? Dann sind wir im Geschäft.",
    ["O"] = "Bringt Pratt McGrubben in der Mondfederfeste 10 dicke Yetibälge.",
    ["T"] = "Das Zeichen der Qualität",
  },
  [2822] = {
    ["D"] = "Ich bin stolz auf meine Lederarbeiten und ich glaube, das sieht man ihnen an; Qualität sollte niemals zweitrangig sein. Jeder, der behauptet, Qualität sei unwichtig, wird Euch wahrscheinlich auch glauben machen wollen, dass die Erde keine Geheimnisse enthält!$B$BAuf meiner jüngsten Suche nach hochwertigem Material habe ich dicke Yetibälge entdeckt. Die Yetis sind in Feralas beheimatet und man findet sie häufig westlich des Wildschrammtals. Ihr dürft Euch ein beliebiges Stück meiner Lederwaren aussuchen für einen Zehnerstapel Yetibälge. Wenn das für Euch akzeptabel ist, dann sind wir im Geschäft!",
    ["O"] = "Bringt Jangdor Flinkschreiter im Camp Mojache 10 dicke Yetibälge.",
    ["T"] = "Das Zeichen der Qualität",
  },
  [2841] = {
    ["D"] = "Besorgt die Maschinenblaupausen und Thermadrahts Safekombination aus Gnomeregan und bringt sie zu Nogg nach Orgrimmar.",
    ["O"] = "Besorgt die Maschinenblaupausen und Thermadrahts Safekombination aus Gnomeregan und bringt sie zu Nogg nach Orgrimmar.",
    ["T"] = "Maschinenkriege",
  },
  [2842] = {
    ["D"] = "In dem Moment, als wir durch Kernobee von der neuen Gnomenmaschine hörten, haben Scooty und ich mit der Arbeit am Transpolyporter 6000 begonnen: dem schnellsten und verlässlichsten Weg nach Gnomeregan.$B$BEndlich ist er bereit, Passagiere aufzunehmen, $N. Wenn Ihr es einmal versuchen wollt, geht nach Beutebucht und redet mit Scooty am Windreiterhorst. Um nach Beutebucht zu gelangen, nehmt das Boot von Ratschet aus.$B$BNatürlich könnt Ihr auch versuchen, Euch durch die von Zwergen verseuchten Berge von Dun Morogh nach Gnomeregan durchzuschlagen!",
    ["O"] = "Sprecht mit Scooty in Beutebucht.",
    ["T"] = "Chefingenieur Scooty",
  },
  [2843] = {
    ["D"] = "Wartet, bis Scooty den Goblintransponder kalibriert hat.",
    ["O"] = "Wartet, bis Scooty den Goblintransponder kalibriert hat.",
    ["T"] = "Gnomer-weeeeg!",
  },
  [2844] = {
    ["D"] = "Ich bin so unfähig, $N. Ich sollt\' auf meine kleine Freundin, Shay, aufpassen, aber irgendwie ist sie mir verlor\'n gegangen. Ich kann nicht genau sagen, was passiert ist, ehrlich, aber als ich sie das letzte Mal gesehen habe, lief\'n wir durch die Ruinen direkt nördlich von hier.$B$BAlles war wunderbar, aber dann hab ich mich umgedreht, un\'... sie war weg.$B$BUnd jetz\' hab ich keine Ahnung, was ich tun soll. Habt Ihr \'ne Idee, $N? Ich mache mir furchtbare Sorgen.$B$BHey, wie wär\'s, wenn Ihr nach Shay suchen würdet? Eure Beine sind viel schneller als meine.",
    ["O"] = "Findet das verlorene Nachtelfenmädchen.",
    ["T"] = "Der Riesenwächter",
  },
  [2845] = {
    ["D"] = "Glaubt Ihr, Ihr könnt mir helfen, meinen Weg zurück zu meinem Freund, Felsbeißer, zu finden? Er sollte auf mich aufpassen... Aber ich neige dazu, gelegentlich davonzuschlendern. Es gibt immer so viele Dinge zu sehen und ich lasse mich dann ablenken.$B$BEs gibt allerdings etwas, was immer meine Aufmerksamkeit erregt.$B$BIn dieser Truhe findet Ihr eine Glocke, die mir sehr teuer ist. Ich liebe einfach ihren Ton. Ihr habt mein Wort: Wenn ich davonschlendere, müsst Ihr lediglich damit klingeln und ich komme angelaufen.",
    ["O"] = "Nehmt Shays Glocke aus der Truhe.$B$BBegleitet Shay Laubschleicher zu Felsbeißers Lager.",
    ["T"] = "Streunende Shay",
  },
  [2846] = {
    ["D"] = "Vor langer Zeit besaß ich ein wunderschönes Schmuckstück, die Tiara der Tiefen. Diese war nicht nur schön, sondern enthielt auch große Macht für diejenigen, die damit umzugehen wussten.$B$BAls nun die Kunde von der Tiara die Hydromantin Velratha erreichte, musste sie sie haben. Sie schickte Handlanger in mein Haus und die stahlen sie, während ich weg war. Diese Diebe!$B$BIch will meine Tiara wiederhaben! Geht nach Zul\'Farrak, der Trollstadt in Tanaris, findet Velratha und ringt ihr die Tiara ab. Bringt sie mir zurück und Ihr steht in meiner Gunst.",
    ["O"] = "Bringt die Tiara der Tiefen zu Tabetha in den Düstermarschen.",
    ["T"] = "Tiara der Tiefen",
  },
  [2847] = {
    ["D"] = "$N, Ihr werdet ziemlich gut als Lederer! Ich kenne da einige Muster, an denen Ihr vielleicht interessiert seid... Wildlederrüstung! Wildleder zieht Kraft aus den mächtigen und chaotischen Eigenschaften von Wildranken, die man in Gegenden mit üppiger Vegetation auf einigen der Kreaturen dort findet. Die daraus gefertigte Rüstung ist garantiert das Beste, was jemand Eures Geschicks herstellen kann.$B$BIch werde es Euch beibringen, aber Ihr werdet dafür eine Weile für mich arbeiten. Das Lehrgeld beträgt nur 10 Stücke dickes Leder. Danach reden wir über Details.",
    ["O"] = "Bringt Pratt McGrubben in der Mondfederfeste 10 Stücke dickes Leder.",
    ["T"] = "Wildlederrüstung",
  },
  [2848] = {
    ["D"] = "Wildlederschultern verstärken den Körper des Trägers unglaublich gut und bieten dabei erstklassigen Schutz für die Oberarme und die Schultern. Wie bei allen Wildlederrüstungen wird auch hier eine mächtige, allerdings zufällige Verbesserung erzeugt, wenn die Wildranke eingearbeitet wird.$B$BFür dieses Muster werde ich 6 dicke Rüstungssets und eine Wildranke benötigen. Das sollte für Euch kein allzu schwer zu erfüllender Auftrag sein.",
    ["O"] = "Bringt Pratt McGrubben in der Mondfederfeste 6 dicke Rüstungssets und eine Wildranke.",
    ["T"] = "Wildlederschultern",
  },
  [2849] = {
    ["D"] = "Wildlederwesten sind die Grundlage aller Wildlederrüstungssets. Das Gerüst der Weste verbindet sich mit der Wildranke, um ein Produkt zu erzeugen, das sowohl maximalen Schutz für den Torso bietet als auch der mächtigen Magie erlaubt, sich in dem Gewand auszubreiten.$B$BFür dieses Muster werde ich 2 Schildkrötenschuppenbrustplatten, 2 Paar Schildkrötenschuppenhandschuhe und eine Wildranke benötigen. Das ist ein großer Auftrag, aber sicher keiner, bei dessen Erfüllung Ihr Schwierigkeiten haben werdet.",
    ["O"] = "Bringt Pratt McGrubben in der Mondfederfeste 2 Schildkrötenschuppenbrustplatten, 2 Paar Schildkrötenschuppenhandschuhe und eine Wildranke.",
    ["T"] = "Wildlederweste",
  },
  [2850] = {
    ["D"] = "Ein Wildlederhelm bietet dem Träger verbesserten Kopfschutz, ohne dabei an der Bequemlichkeit zu sparen. Wie bei allen Wildlederrüstungen erzeugt die eingearbeitete Wildranke eine mächtige, aber zufällige Verbesserung der Struktur des Helms. Lederträger werden dieses Stück auf jeden Fall haben wollen!$B$BFür dieses Muster brauche ich 2 Tuniken des Nachtschleichers, 2 Stirnbänder des Nachtschleichers und eine Wildranke. Erfüllt diesen Auftrag und das Muster gehört Euch!",
    ["O"] = "Bringt Pratt McGrubben in der Mondfederfeste 2 Tuniken des Nachtschleichers, 2 Stirnbänder des Nachtschleichers und eine Wildranke.",
    ["T"] = "Wildlederhelm",
  },
  [2851] = {
    ["D"] = "Ihr habt schon eine ganze Menge gelernt, $N; dennoch gibt es noch mehr für Euch zu lernen. Als Nächstes sind Wildlederstiefel an der Reihe, der Grundstein eines jeden Sets.$B$BDiese Stiefel werden so gefertigt, dass sie den Fuß in Bequemlichkeit hüllen, während sie ihn gleichzeitig gegen die Gefahren des Abenteuerlebens wappnen. Die weichen Sohlen sind ein Segen für diejenigen, die bei ihrer Fußbekleidung auf leise Bewegung Wert legen.$B$BFür dieses Muster müsst Ihr mir 2 Hosen des Nachtschleichers und 2 Stiefel des Nachtschleichers besorgen. Ich werde außerdem zwei Wildranken benötigen, nicht nur eine. Wenn Ihr all das beisammen habt, gebt mir Bescheid.",
    ["O"] = "Bringt Pratt McGrubben in der Mondfederfeste 2 Hosen des Nachtschleichers, 2 Stiefel des Nachtschleichers und 2 Wildranken.",
    ["T"] = "Wildlederstiefel",
  },
  [2852] = {
    ["D"] = "Jetzt, nachdem Ihr die Grundlagen beherrscht, seid Ihr bereit, Euch komplexeren Dingen zuzuwenden, $N. Wildledergamaschen gehören zur robustesten und stärksten Lederausrüstung überhaupt. Bauch, Hüften, Schenkel und Solarplexus werden ungeheuer gut geschützt. Und wenn dann noch die Verstärkung hinzukommt, die die Wildranke bewirkt, werden Euch die Abenteurer, die diese Rüstung besitzen, jedes Mal danken, wenn sie Euch sehen.$B$BFür dieses Muster müsst Ihr mir 2 Schildkrötenschuppenhelme, 2 Schildkrötenschuppenarmschienen und 2 Wildranken besorgen.",
    ["O"] = "Bringt Pratt McGrubben in der Mondfederfeste 2 Schildkrötenschuppenhelme, 2 Schildkrötenschuppenarmschienen und 2 Wildranken.",
    ["T"] = "Wildledergamaschen",
  },
  [2853] = {
    ["D"] = "Nun, $N, Ihr gebt dem Wort Sorgfalt eine neue Bedeutung. Es steht nur noch ein Wildlederrüstungsmuster aus, denn Ihr habt alle anderen gemeistert. Ich werde nicht von Euch verlangen, Euch unnötig für dieses Muster abzuplagen. Ihr habt es Euch bereits mehr als verdient... Aber ich kann es Euch nicht selbst geben. Mein Lehrer, derjenige, der mich in dieses Handwerk einführte, muss Euch belohnen.$B$BBringt diesen Brief zu Telonis. Er lehrt Lederverarbeitung in Darnassus. Zeigt ihm den Brief und lasst Euch von ihm als Gleichgestellten anerkennen, so wie ich das auch tue.",
    ["O"] = "Gebt Telonis, dem Lederverarbeitungsmeister von Darnassus, Pratts Brief.",
    ["T"] = "Meister des Wildleders",
  },
  [2854] = {
    ["D"] = "$N, Euer Ruf als Lederer eilt Euch voraus. Ich besitze Wissen über die Lederverarbeitung, das zu erwerben weise von Euch wäre... Wildlederrüstung! Wildleder zieht Kraft aus den mächtigen und chaotischen Eigenschaften von Wildranken, die man in Gegenden mit üppiger Vegetation auf einigen der Kreaturen dort findet. Die daraus gefertigte Rüstung ist garantiert das Beste, was ein Handwerker Eures Geschicks herstellen kann.$B$BIch werde es Euch beibringen, aber Ihr werdet dafür eine Weile für mich arbeiten. Das Lehrgeld beträgt nur 10 Stücke dickes Leder. Danach reden wir über Details.",
    ["O"] = "Bringt Jangdor Flinkschreiter im Camp Mojache 10 Stücke dickes Leder.",
    ["T"] = "Wildlederrüstung",
  },
  [2855] = {
    ["D"] = "Wildlederschultern verstärken den Körper des Trägers unglaublich gut und bieten dabei besten Schutz für Oberarme und Schultern. Wie bei allen Wildlederrüstungen wird auch hier eine zufällige, aber magieerfüllte Verbesserung erzeugt, wenn die Wildranke eingearbeitet wird.$B$BFür dieses Muster werde ich 6 dicke Rüstungssets und eine Wildranke benötigen. Das sollte für Euch kein allzu schwer zu erfüllender Auftrag sein.",
    ["O"] = "Bringt Jangdor Flinkschreiter im Camp Mojache 6 dicke Rüstungssets und eine Wildranke.",
    ["T"] = "Wildlederschultern",
  },
  [2856] = {
    ["D"] = "Wildlederwesten sind die Grundlage jeglicher Wildlederrüstungssets. Die Struktur der Weste und das Wildrankengeflecht darin erzeugen ein Produkt, das sowohl maximalen Schutz für den Torso bietet, als auch der mächtigen Magie erlaubt, sich in dem Gewand auszubreiten.$B$BFür dieses Muster werde ich 2 Schildkrötenschuppenbrustplatten, 2 Paar Schildkrötenschuppenhandschuhe und eine Wildranke benötigen. Dies ist keine einfache Lektion, aber eine, für die Ihr sicherlich das nötige Geschick habt.",
    ["O"] = "Bringt Jangdor Flinkschreiter im Camp Mojache 2 Schildkrötenschuppenbrustplatten, 2 Schildkrötenschuppenhandschuhe und eine Wildranke.",
    ["T"] = "Wildlederweste",
  },
  [2857] = {
    ["D"] = "Ein Wildlederhelm bietet dem Träger verbesserten Kopfschutz, ohne dabei an der Bequemlichkeit zu sparen. Wie bei allen Wildlederrüstungen erzeugt die eingearbeitete Wildranke eine mächtige, aber zufällige magische Verstärkung der Struktur des Helms. Lederträger werden dieses Stück auf jeden Fall haben wollen!$B$BFür dieses Muster brauche ich 2 Tuniken des Nachtschleichers, 2 Stirnbänder des Nachtschleichers und eine Wildranke. Schließt diese Lektion erfolgreich ab und das Muster gehört Euch!",
    ["O"] = "Bringt Jangdor Flinkschreiter im Camp Mojache 2 Tuniken des Nachtschleichers, 2 Stirnbänder des Nachtschleichers und eine Wildranke.",
    ["T"] = "Wildlederhelm",
  },
  [2858] = {
    ["D"] = "Euer Wissen über die Herstellung von Wildlederrüstungen wächst, $N, aber trotzdem habt Ihr noch einiges zu lernen.$B$BWildlederstiefel sind die Grundlage eines jeden Sets. Diese Stiefel werden so gefertigt, dass sie den Fuß in Bequemlichkeit hüllen, während sie ihn gleichzeitig gegen die Gefahren des Abenteurerlebens wappnen. Die weichen Sohlen sind ein Segen für diejenigen, die bei ihrer Fußbekleidung auf leise Bewegung Wert legen.$B$BFür dieses Muster müsst Ihr mir 2 Hosen des Nachtschleichers und 2 Stiefel des Nachtschleichers herstellen. Ich werde außerdem zwei Wildranken benötigen, nicht nur eine. Wenn Ihr diese Dinge habt, werden wir weitermachen.",
    ["O"] = "Bringt Jangdor Flinkschreiter im Camp Mojache 2 Hosen des Nachtschleichers, 2 Stiefel des Nachtschleichers und 2 Wildranken.",
    ["T"] = "Wildlederstiefel",
  },
  [2859] = {
    ["D"] = "Ihr seid so weit, komplexere Lektionen zu meistern, $N. Wildledergamaschen gehören zur robustesten und stärksten Lederausrüstung überhaupt. Bauch, Hüften, Schenkel und Solarplexus werden ungeheuer gut geschützt. Und wenn dann noch die Verstärkung hinzukommt, die die Wildranke bewirkt, werden Euch die Abenteurer, die diese Rüstung besitzen, jedes Mal danken, wenn sie Euch sehen.$B$BUm Euch dieses Muster zu geben, benötige ich 2 Schildkrötenschuppenhelme, 2 Schildkrötenschuppenarmschienen und 2 Wildranken.",
    ["O"] = "Bringt Jangdor Flinkschreiter im Camp Mojache 2 Schildkrötenschuppenhelme, 2 Schildkrötenschuppenarmschienen und 2 Wildranken.",
    ["T"] = "Wildledergamaschen",
  },
  [2860] = {
    ["D"] = "$N, Ihr habt alles erlernt, was ich Euch lehren kann. Es gibt allerdings noch ein letztes Wildlederrüstungsmuster. Ich werde nicht von Euch verlangen, Euch unnötig dafür abzuplagen. Ihr habt es Euch bereits mehr als verdient. Jedoch kann ich dieses Muster nicht einfach mit Euch teilen.$B$BMein Lehrer, derjenige, der mich in dieses Handwerk einführte, muss Euch belohnen. Bringt diese Notiz zu Una. Sie lehrt Lederverarbeitung in Donnerfels. Zeigt ihr den Brief und lasst Euch von ihr als Gleichgestellten anerkennen, so wie ich das auch tue.",
    ["O"] = "Gebt Una, der Lederverarbeitungsmeisterin von Donnerfels, Jangdors Schreiben.",
    ["T"] = "Meister des Wildleders",
  },
  [2861] = {
    ["D"] = "Gerüchte besagen, dass Tabetha, eine Magierkollegin, die tief in den Düstermarschen lebt, ein Hühnchen mit einem gewissen Sandtroll in Zul\'Farrak zu rupfen hat. Wenn Ihr mit Tabetha sprecht, kann sie Euch sicher mehr dazu sagen.$B$BUnd nehmt ein paar Freunde mit, $N. Falls Tabetha Euch nach Zul\'Farrak schickt, wollt Ihr sicher nicht allein hingehen.$B$BIhr findet Tabethas Hütte westlich von Theramore und direkt nördlich der Ruinen der Steinbrecher.",
    ["O"] = "Sprecht mit Tabetha in den Düstermarschen.",
    ["T"] = "Tabethas Aufgabe",
  },
  [2862] = {
    ["D"] = "Die Gnolle... sie sind mehr als nur ein Ärgernis. Sie sind eine konstante Bedrohung für die Existenz dieses gesamten Lagers! Es ist unwichtig, ob sie Eingeborene des Feralas sind oder nicht. Wenn wir uns nicht gegen sie wappnen, werden wir uns eines Tages aus dem gesamten Gebiet vertrieben sehen.$B$BDie Gnollangriffe auf das Lager wurden in letzter Zeit heftiger. Wir brauchen Eure Hilfe dabei, ihre Anzahl zu reduzieren. Bringt mir zehn ihrer Mähnen und ich werde Euch stattlich für Eure Mühe entlohnen.$B$BLos!",
    ["O"] = "Bringt Hadoken Flinkschreiter im Camp Mojache 10 Mähnen eines Waldpfotengnolls.",
    ["T"] = "Krieg gegen die Waldpfoten",
  },
  [2863] = {
    ["D"] = "Der Plan besteht darin, verschiedene Anführer der Gnolle - Alphas, wie sie sich selbst nennen - in schneller Folge auszuschalten. Wenn wir einen solch schnellen und präzisen Schlag gegen sie erzielen können, würde es in ihren Rängen zu Chaos führen. Vielleicht würde es dann endlich auch in ihren dicken, von Flöhen zerbissenen Köpfen klar, dass wir sie vernichten können und werden!$B$BIch möchte, dass Ihr der Ausführende meines Willens seid. Eliminiert innerhalb einer Stunde fünf Alphas, wenn das klappen soll, $N, sonst bleiben wir in dieser Pattsituation!",
    ["O"] = "Tötet 5 Alphas der Waldpfoten und kehrt innerhalb einer Stunde zu Hadoken Flinkschreiter zurück.",
    ["T"] = "Schlag gegen die Alphas",
  },
  [2864] = {
    ["D"] = "Mein Vetter Tran\'rek ist in Schwierigkeiten. Er versprach mir vor einer Woche eine Fuhre Skarabäuspanzerschalen und hat bisher nicht geliefert. Er ist mein Vetter, also würde ich es wirklich hassen, wenn ich jemanden nach Gadgetzan schicken müsste, um ihm etwas zu brechen.$B$BKönnt Ihr für mich mit Tran\'rek sprechen? Lasst ihn wissen, dass er diese Lieferung zusammenbekommen muss, bevor ich irgendetwas Unüberlegtes mache.$B$BGadgetzan liegt im nördlichen Tanaris, in Kalimdor.",
    ["O"] = "Sprecht mit Tran\'rek in Gadgetzan.",
    ["T"] = "Tran\'rek",
  },
  [2865] = {
    ["D"] = "Die Skarabäen von Tanaris haben sehr harte Panzerschalen! Hart genug, um sie als Baumaterial für eine Menge Dinge zu verwenden. So viele Dinge!$B$BTatsächlich sind diese Panzer so nützlich... dass die Skarabäen beinahe bis zur Ausrottung gejagt wurden.$B$BIch weiß, wo es noch mehr Skarabäen gibt, und wenn Ihr mir versprecht, mir ihre Panzerschalen zu bringen, sage ich Euch, wo sie sind. Versprochen?$B$BO.k., die Skarabäen haben eine Kolonie in Zul\'Farrak. Ich schätze, die Trolle jagen sie nicht wegen ihrer Panzerschalen.$B$BAber Ihr könnt das tun! Geht nach Zul\'Farrak und besorgt mir unbeschädigte Skarabäuspanzerschalen!",
    ["O"] = "Bringt Tran\'rek in Gadgetzan 5 unbeschädigte Skarabäuspanzerschalen.",
    ["T"] = "Skarabäuspanzerschalen",
  },
  [2866] = {
    ["D"] = "Südlich von uns befinden sich die Ruinen von Solarsal. Dies ist eine Gegend aus längst vergangener Zeit. Wir haben die Ruinen ursprünglich erforscht und nichts Besonderes darin gefunden. Daher sind wir friedlich wieder abgezogen.$B$BVor Kurzem haben die Hasskammnaga die Ruinen in alarmierender Anzahl besetzt. Zuerst hielten wir das für die Vorbereitungen auf einen Angriff. Obwohl diese Bedrohung immer noch gegeben ist, glauben wir, dass dort etwas anderes vorgeht.$B$BGeht dorthin, erforscht die Ruinen und achtet auf alles, was Euch ungewöhnlich vorkommt. Viel Glück, $N.",
    ["O"] = "Erforscht die Ruinen von Solarsal und findet etwas über die Anwesenheit der Naga dort heraus.",
    ["T"] = "Die Ruinen von Solarsal",
  },
  [2867] = {
    ["D"] = "Mit der Entdeckung des seltsamen Pavillons im Herzen der Ruinen von Solarsal ist es an der Zeit, zur Mondfederfeste zurückzukehren und General Shandris Mondfeder zu berichten, was hier entdeckt wurde.",
    ["O"] = "Kehrt zu Shandris Mondfeder in der Mondfederfeste zurück und berichtet von Euren Entdeckungen.",
    ["T"] = "Kehrt zur Mondfederfeste zurück",
  },
  [2868] = "_",
  [2869] = {
    ["D"] = "Es ist derzeit die beste Handlungsweise, davon auszugehen, dass die Naga, was auch immer sie vorhaben, langfristig versuchen werden, die Insel für sich zu beanspruchen. Wir müssen aktive Schritte unternehmen, um diese Bedrohung auszuschalten, bevor es zu spät ist.$B$BBringt mir 10 Schuppen der Hasskämme in gutem Zustand. Es dürfte offensichtlich sein, wie man an diese herankommt. Ihr werdet Euch an den Naga das erste Mal Eure Sporen verdienen und wir haben dann eine geschwächte Nagapräsenz auf der Sardor-Insel - beides sind Aussichten, die uns zum Vorteil gereichen.",
    ["O"] = "Bringt Latronicus Mondspeer in der Mondfederfeste 10 Nagaschuppen der Hasskämme.",
    ["T"] = "Gegen die Hasskämme",
  },
  [2870] = {
    ["D"] = "$N, unsere neuesten Spähberichte haben uns einen guten Einblick in die Kommandokette der Naga erlaubt. In ihren Truppen gibt es viele Adlige und die sind es, die den Ton angeben.$B$BUm genau zu sein, wir haben einen der grausameren Adligen als Lord Shalzaru identifiziert. Späher berichten, dass er höchstpersönlich bei einer kürzlich durchgeführten Nagaausgrabung in einer Höhle südlich der eigentlichen Ruinen, jenseits des Wassers, auf der Insel des Schreckens, ein seltsames Relikt gefunden hat.$B$BMacht dem ein Ende, indem Ihr ihm ein Ende macht, und bringt mir dieses Relikt, damit wir es untersuchen können.",
    ["O"] = "Vernichtet Lord Shalzaru und bringt dann Latronicus Mondspeer in der Mondfederfeste das geheimnisvolle Relikt, das er entdeckt hat.",
    ["T"] = "Gegen Lord Shalzaru",
  },
  [2871] = {
    ["D"] = "Wenn Ihr so nett wärt, das Relikt zu meiner Frau Vestia zu bringen, wüsste ich das zu schätzen. Sie ist nicht nur eine gute Priesterin für unsere Gemeinde, sondern auch eine der versiertesten Gelehrten, die es gibt. Ich könnte wahrscheinlich nicht einmal dann herausfinden, wo an diesem Ding oben oder unten ist, wenn mein Leben davon abhinge. Meine Frau wiederum... Also wenn irgendwer herausfinden kann, was das ist, dann sie... Und wir müssen es wissen!$B$BSie befindet sich gleich um die Ecke, ohne Zweifel am Studieren. Danke, $N.",
    ["O"] = "Bringt das geheimnisvolle Relikt zu Vestia Mondspeer in der Mondfederfeste.",
    ["T"] = "Überbringung des Relikts",
  },
  [2872] = {
    ["D"] = "Mein alter Kumpel Stoley schuldet mir eine ganz bestimmte Flasche Rum. Er schmeißt die Bar am Dampfdruckpier in Tanaris, Kalimdor, und er versprach mir, dass der Rum dort stärker ist als alles, was man in Beutebucht finden kann. Er ist so davon überzeugt, dass er sagt, wenn ich diesen Rum probiere und ihm nicht zustimme, würde er das Geld, das er mir schuldet, verdoppeln!$B$BNun, ich glaube, er ist nicht mehr so zuversichtlich wie damals, als wir zuletzt miteinander geredet haben, und hat jetzt wohl Angst, mir diesen Rum zu geben. Ich lasse eine Wette aber nicht auf sich beruhen, also wie wäre es, wenn Ihr für mich mit ihm redet?",
    ["O"] = "Sprecht mit Stoley beim Dampfdruckpier.",
    ["T"] = "Stoleys Schuld",
  },
  [2873] = {
    ["D"] = "Ich schulde \"Seewolf\" MacKinley in Beutebucht etwas Rum, um eine Wette zu begleichen, aber mir ist der Rum ausgegangen! Kapitän Cuergo hat meine letzte Lieferung gestohlen und lagert sie in dieser ummauerten Piratenstadt im Süden.$B$BWenn Ihr meine Rumlieferung finden und zurückbringen könnt, dann kann ich MacKinley beweisen, dass Kalimdors Drinks die aus Beutebucht um Längen schlagen.",
    ["O"] = "Bringt Stoleys Lieferung zu Stoley beim Dampfdruckpier.",
    ["T"] = "Stoleys Lieferung",
  },
  [2874] = {
    ["D"] = "Danke nochmals für Eure Hilfe, $N. Hier habt Ihr eine Flasche Dampfdruckrum. Bringt sie zu MacKinley und er wird endlich seine verrückten Ansichten über Drinks aus Beutebucht über Bord werfen können.",
    ["O"] = "Bringt Stoleys Flasche zu \"Seewolf\" MacKinley in Beutebucht.",
    ["T"] = "Lieferung für MacKinley",
  },
  [2875] = {
    ["D"] = "Der niederträchtige Pirat Andre Feuerbart wird aufgrund seiner gegen die Einwohner des Dampfdruckpiers und Gadgetzan gerichteten Verbrechen gesucht.$B$BDurch Erlass der Ratsregierung ist hiermit jeder, der dies liest, zu tödlicher Gewaltanwendung bei der Festnahme von Andre und seinen Spießgesellen autorisiert.$B$BDes Weiteren gilt durch zusätzlichen Erlass, dass, sollte Andre verstorben oder anderweitig während seiner Anhörung unpässlich sein, sein Kopf als Ersatz gilt und die Aushändigung dieses Kopfes an Sicherheitschef Bilgenritzel als vertragsbindend angesehen wird.",
    ["O"] = "Bringt Sicherheitschef Bilgenritzel beim Dampfdruckpier Andres Kopf.",
    ["T"] = "GESUCHT: Andre Feuerbart",
  },
  [2876] = {
    ["D"] = "Auf dieser Schriftrolle sind die Zeitpläne für Lieferungen zwischen dem Dampfdruckpier und Beutebucht verzeichnet. Sowohl vergangene als auch zukünftige Lieferungen sind aufgeführt sowie Schiffsnamen. Große rote Kreuze befinden sich neben einigen der Schiffsreisen und Anmerkungen wie \"Große Beute\" oder \"Harter Kampf\" neben anderen.$B$BJemand beim Dampfdruckpier wird dieses Dokument sicher sehen wollen.",
    ["O"] = "Berichtet den Behörden des Dampfdruckpiers von den Schiffzeitplänen.",
    ["T"] = "Schiffzeitpläne",
  },
  [2877] = {
    ["D"] = "Wir Wildhämmer haben die Vergangenheit besser in Erinnerung als unsere Brüder in Eisenschmiede. Wenn Ihr Euch hier beliebt machen wollt, dann müsst Ihr Euch schon Eure Hände für uns schmutzig machen!$B$BDie Horde hat ihre Stellungen im Hinterland beim Lauerfels aufgegeben. Jetzt wimmelt es dort von üblen Schlicken und Schlammen. Wir haben Pläne für dieses Gebiet, aber zuerst muss uns eine Aufräummannschaft dort eine Bresche hineinschlagen.$B$BUnd hier kommt Ihr ins Spiel, Sonnenschein! Geht da runter und erledigt 10 grüne Schlicke und 10 jadegrüne Schlamme und meldet Euch dann wieder bei mir. Zieht los!",
    ["O"] = "Tötet 10 grüne Schlicke sowie 10 jadegrüne Schlamme und meldet Euch dann wieder bei Fraggar Donnermantel beim Nistgipfel.",
    ["T"] = "Den Lauerfels aufräumen",
  },
  [2879] = {
    ["D"] = "Es sieht so aus, als könnte dieser Stock der Stock von Equinex sein. Der Stock von Equinex ist in Wirklichkeit ein Schlüssel. Mit ihm kann man den Monolithen von Equinex öffnen, der immer noch in den nördlichen Ruinen von Rabenwind steht.$B$BFindet die vier Feuer, die noch immer in den Ruinen brennen: Samha, Imbel, Byltan und Lahassa. Beschafft Euch deren Essenz. Und dann benutzt diese Essenz, während Ihr vor dem Monolithen von Equinex steht, um den Stock zu aktivieren. Wenn dies wirklich der Stock von Equinex ist, dann könnt Ihr den Monolithen öffnen und ein hochheiliges Artefakt daraus bergen.",
    ["O"] = "Aktiviert Troyas Stock und findet den Monolithen von Equinex.",
    ["T"] = "Der Stock von Equinex",
  },
  [2880] = {
    ["D"] = "Seid Euch darüber im Klaren, $R - wir sind NICHT Teil der Allianz! Die Wildhämmer sind unabhängig. Um den Wert einer Person zu beweisen, verlassen sie sich lieber auf Taten und hören nicht auf das Geschwätz derer, die mit ihrem Gequassel versuchen, ihre Untätigkeit zu überspielen.$B$BWenn Ihr hier Freunde sucht, beweist uns erst Euren Wert! Die üblen Trolle, die die Hänge des Hinterlands überrennen, tragen manchmal Stammeshalsketten. Bringt mir 5 davon und wir werden das als einen Schritt in die richtige Richtung für Euch verbuchen.",
    ["O"] = "Bringt Fraggar Donnermantel beim Nistgipfel 5 Trollstammeshalsketten.",
    ["T"] = "Kopfgeld für Trollhalsketten",
  },
  [2882] = {
    ["D"] = "Diese Karte zeigt ein Stück Strand an einer Südküste. Unter der Karte stehen elegant geschriebene Worte:$B$BSüdlich des Piers$BDürft Ihr drauf hoffen$BFindet Flagge, Mast und Knochen$BGrabt dort, wenn Ihr tapfer sein wollt$BGrabt und erbt Cuergos Gold!$BEinen Schlüssel werdet Ihr brauchen, einen Schlüssel werdet Ihr sehen$BDie Mannen dort woll\'n als Bezahlung Euer Blut Euch nehm\'n.",
    ["O"] = "Findet Cuergos Gold!",
    ["T"] = "Cuergos Gold",
  },
  [2902] = {
    ["D"] = "Gnolle sind zwar nicht die Hellsten, aber sie werden bestimmt die Strategie ihrer jüngsten Aktivitäten irgendwo dokumentiert haben. Wenn wir diese Pläne finden würden, hätten wir eine bessere Vorstellung davon, was sie so aufbringt.$B$BIch möchte, dass Ihr zu den Waldpfoten zurückgeht und nach allem sucht, was Ähnlichkeit mit ihren Schlachtplänen haben könnte. Irgendwas muss es geben, ihre Angriffe waren einfach zu gut koordiniert. Findet diese Schlachtpläne und kommt zu mir zurück, sobald Ihr sie habt.",
    ["O"] = "Findet die Schlachtpläne der Gnolle irgendwo in den Gnoll-Lagern südlich vom Camp Mojache.",
    ["T"] = "Waldpfotennachforschung",
  },
  [2903] = {
    ["D"] = "Jetzt, nachdem die Schlachtpläne der Gnolle entdeckt wurden, bleibt nur noch, die Pläne zu Hadoken Flinkschreiter ins Camp Mojache zurückzubringen. Vielleicht kann er oder irgendjemand dort besser darauf schließen, was die Gnolle vorhaben.",
    ["O"] = "Bringt die Schlachtpläne der Waldpfoten zu Hadoken Flinkschreiter ins Camp Mojache.",
    ["T"] = "Die Schlachtpläne",
  },
  [2904] = {
    ["D"] = "<Kernobee stöhnt.> Was... Was ist passiert?$B$BDas Letzte, an das ich mich erinnere, ist, wie ich von einer Gruppe Dunkeleisenzwerge gefangen, halb tot geprügelt und dann hier zu diesen schmutzigen Troggs geworfen wurde.$B$BHolt mich hier raus, $N!$B$BSo ist der Plan: Bringt mich zum Ausgang; Wenn wir es lebend hier herausschaffen, geht nach Beutebucht und lasst Scooty wissen, dass ich nach Ratschet gegangen bin, um Tüftelwiz zu warnen, dass die Dunkeleisenzwerge in die Herstellung dieser Supermaschine verwickelt sind.$B$BGehen wir!",
    ["O"] = "Begleitet Kernobee zur Uhrwerkgasse und meldet Euch dann wieder bei Scooty in Beutebucht.",
    ["T"] = "Eine schöne Bescherung",
  },
  [2922] = {
    ["D"] = "Als unsere geliebte Stadt Gnomeregan fiel, muss ein verirrter Funke mein armes Geschöpf, Techbot, völlig durcheinander gebracht haben. Früher half er mir und meinen Kollegen mit seinen zahllosen Dingsdas und seiner endlosen Speicherkapazität, aber sein positronisches Gehirn wurde polarisiert. Es ist jetzt negatronisch... und Techbots Verhalten wurde umgekehrt. Er wütet jetzt in ganz Gnomeregan!$B$BDoch vielleicht kann man ihn noch retten. Wenn Ihr seinen Speicherkern auffinden und zu mir bringen könnt, kann ich die Ursache der Polarisierung möglicherweise finden und meinen armen Techbot reparieren.$B$B",
    ["O"] = "Bringt Techbots Speicherkern zu Tüftlermeister Oberfunks nach Eisenschmiede.",
    ["T"] = "Rettet Techbots Hirn!",
  },
  [2923] = {
    ["D"] = "Tüftlermeister Oberfunks ist ob des Schicksals seiner mechanischen Schöpfung, Techbot, ziemlich bestürzt. Ich erinnere mich an Techbot als ein freundliches und hilfsbereites Werkzeug der Gnome, aber Oberfunks erzählte mir, er hätte eine Fehlfunktion.$B$BWirklich traurig. Aber mit etwas Glück kann er vielleicht repariert werden!$B$BSprecht mit Tüftlermeister Oberfunks in Tüftlerstadt, Eisenschmiede. Er würde sich über Eure Hilfe freuen.",
    ["O"] = "Sprecht mit Tüftlermeister Oberfunks in Eisenschmiede.",
    ["T"] = "Tüftlermeister Oberfunks",
  },
  [2924] = {
    ["D"] = "Ein beinahe universell einsetzbares mechanisches Bauteil - das grundlegende Artifix - war den Tüftlern und Schmieden von Gnomeregan von großem Nutzen. Doch während der panischen Flucht aus unserer Stadt hat niemand daran gedacht, einige von diesen Geräten mitzunehmen! Und ich brauche eine ganze Menge davon für eine Arbeit, die ich in Kürze in Angriff nehmen werde.$B$BBitte, geht nach Gnomeregan und bringt mir einige grundlegende Artifixe. Ihr werdet sie in den tieferen Bereichen unserer Stadt finden, in Künstliche Extrapolatoren genannten Behältern.$B$BDanke, $N, und bitte beeilt Euch!",
    ["O"] = "Bringt Klockmort Spannsplint in Eisenschmiede 12 grundlegende Artifixe.",
    ["T"] = "Grundlegende Artifixe",
  },
  [2925] = {
    ["D"] = "Ein Gnomenfreund, Klockmort Spannsplint, hat mich aus Eisenschmiede kontaktiert. Er teilt mir mit, dass er Hilfe bei einer gefährlichen, aber unverzichtbaren Mission nach Gnomeregan braucht, der einst so großen Gnomenstadt, die jetzt verseucht und voller verrückter Gnome und ihrer Gerätschaften ist.$B$BIch muss hier bleiben, aber mir blutet das Herz, wenn ich meinem Freund nicht helfen kann. Wenn Ihr an meiner Stelle gehen könnt, dann stünde ich tief in Eurer Schuld.$B$BIhr könnt Klockmort in Tüftlerstadt in Eisenschmiede finden.",
    ["O"] = "Sprecht mit Klockmort Spannsplint in Eisenschmiede.",
    ["T"] = "Klockmorts Grundlagen",
  },
  [2926] = {
    ["D"] = "Seit dem Unfall redet Gnoarn nur noch davon, Gnomeregan vom Troggbefall zu befreien und ein Heilmittel für die Erkrankung, die die Stadt verseucht, zu finden, $N.$B$BZum Glück sind Razzle und ich gerade dabei, ein solches Heilmittel zu entwickeln... oder vielleicht auch ein Rezept für wirklich starken Rum. Genau wissen wir das aber erst, wenn wir es ausprobiert haben! Logischerweise benötigen wir zum Test unserer Theorie ein paar Dinge, vor allem Ablagerungen von den bestrahlten Troggs in Gnomeregan. Füllt diese Phiole mit dem grünen Leuchten und bringt sie zurück!",
    ["O"] = "Sammelt mit der leeren bleiernen Sammelphiole radioaktive Ablagerungen bestrahlter Eindringlinge oder Plünderer. Sobald sie voll ist, bringt Ihr sie zu Ozzie Wechselvolt nach Kharanos zurück.",
    ["T"] = "Gnogaine",
  },
  [2927] = {
    ["D"] = "Ihr glaubt doch wohl nicht, dass ich schon immer so kahlköpfig war, oder? Nein, $Gwerter Herr:holde Dame;! Das Strahlungsleck in Gnomeregan rief diesen tragischen Haarverlust hervor. Vor jenem Tag hatte ich den einzigen Gnomenafro in ganz Azeroth.$B$B<Gnoarn schüttelt drohend die Faust.>$B$BVerdammt seien diese verdammten dreckigen Troggs, verdammt seien sie alle!$B$BVielleicht ist es noch nicht zu spät, $N. Wir können sie vielleicht immer noch retten - die Stadt, meine ich! Sprecht mit Ozzie Wechselvolt in Kharanos. Er und sein Partner Razzle arbeiten an einer Lösung.",
    ["O"] = "Sprecht mit Ozzie Wechselvolt in Kharanos in Dun Morogh.",
    ["T"] = "Der Tag danach",
  },
  [2928] = {
    ["D"] = "Bringt 24 robomechanische Innereien zu Shoni nach Sturmwind.",
    ["O"] = "Bringt 24 robomechanische Innereien zu Shoni nach Sturmwind.",
    ["T"] = "Gyrobohrmatische Exkavation",
  },
  [2929] = {
    ["D"] = "Ich habe Thermadraht vertraut, $R. Ich hätte nie vermutet, dass er mich und das ganze Gnomenvolk verraten würde. Und wofür? Macht? Reichtum? Alles Dinge, die er mit der Zeit auch so erlangt hätte. Jetzt sind wir aus unserer Heimat verdrängt worden und dieser Wahnsinnige ist dafür verantwortlich. Der König des Nichts...$B$BWir werden Gnomeregan zurückerobern, $N. Wir werden nicht ruhen, bis die Stadt wieder unter unserer Kontrolle steht. Wenn Ihr Euch unserem Kampf anschließen wollt, habe ich eine einfache Bitte an Euch: Tötet den Verräter. Vernichtet Robogenieur Thermadraht.",
    ["O"] = "Reist nach Gnomeregan und tötet Robogenieur Thermadraht. Kehrt zu Hochtüftler Mekkadrill zurück, wenn der Auftrag ausgeführt ist.",
    ["T"] = "Der große Verrat",
  },
  [2930] = {
    ["D"] = "Als wir aus Gnomeregan flohen, haben wir so viele Daten zurückgelassen! Lebenswichtige Informationen!$B$BIch brauche die Daten auf einer Prismalochkarte aus einem Matrix-Prägographen 3005-D, einem Hochsicherheitsterminal, tief in Gnomeregan. Ihr müsst auf dieses Terminal zugreifen, aber dafür braucht Ihr die Zugangsberechtigung...und um die zu erhalten, müsst Ihr erst Zugang zu den Terminals niedrigerer Sicherheitsstufen - Modelle A, B und C - haben. DAFÜR wiederum braucht Ihr Sicherheitslochkarten, angefangen mit einer weißen...Und wir haben alle Lochkarten in der Stadt vergessen.$B$BDas ist eine Katastrophe! Könnt Ihr helfen?",
    ["O"] = "Bringt Mechanikermeister Gussmuff in Eisenschmiede eine Prismalochkarte.",
    ["T"] = "Datenrettung",
  },
  [2931] = {
    ["D"] = "Mechanikermeister Gussmuff in Eisenschmiede sucht Abenteurer! Er organisiert Teams, um Gnomeregan auf einer Datensicherungsmission höchster Sicherheitsstufe und Wichtigkeit zu betreten.$B$BGussmuff mag zwar nicht wie ich ein Mitglied der Erleuchteten Vereinigung für Arkanologie, Alchemie und Ingenieurwissenschaften sein, aber er ist dennoch ein kluger Gnom, und wenn er es für wichtig hält, dann besteht nach meinen Berechnungen eine vierundachtzigprozentige Wahrscheinlichkeit, dass es wirklich wichtig ist.$B$BDas ist eine gute Chance!",
    ["O"] = "Sprecht mit Mechanikermeister Gussmuff in Eisenschmiede.",
    ["T"] = "Gussmuffs Auftrag",
  },
  [2932] = {
    ["D"] = "Die Trolle der Bleichborken waren einst Verbündete der Horde, aber sie haben Thrall hintergangen... und das muss bestraft werden!$B$BHier, nehmt meine Pike und geht weit gen Norden ins Hinterland. Ihr werdet es nordöstlich von Tarrens Mühle vorfinden, einem Dorf, das von unseren Verbündeten, den Verlassenen, kontrolliert wird.$B$BWenn Ihr ins Hinterland kommt, jagt Trolle der Bleichborken. Sammelt ihre Schädel und rammt meine Pike in einem ihrer kleineren Dörfer in den Boden: in Hiri\'watha oder Zun\'watha.$B$BZeigt ihnen, dass ihre heimtückischen Taten Konsequenzen nach sich ziehen!",
    ["O"] = "Sammelt Schädel der Bleichborken und hängt sie an Nimboyas Pike. Platziert Nimboyas behängte Pike in einem der Bleichborkendörfer im Hinterland und kehrt dann zu Nimboya ins Schlingendorntal zurück.",
    ["T"] = "Grausige Botschaft",
  },
  [2933] = {
    ["D"] = "Diese Flaschen enthalten ein übles grünes Gift.$B$BWenn man die Anzahl der Flaschen in diesem Dorf bedenkt, müssen die Bleichborken es für sehr wichtig halten. Vielleicht will einer der Apotheker im nahe gelegenen Tarrens Mühle eine Probe davon.",
    ["O"] = "Bringt eine Giftflasche zu einem Apotheker nach Tarrens Mühle.",
    ["T"] = "Giftflaschen",
  },
  [2934] = {
    ["D"] = "Ich benötige eine frische Probe von dem Gift, das Ihr mir gebracht habt. Die Spinnen, von denen es gesammelt wurde, dürften im Hinterland leben. Jagt sie, bis Ihr einen unbeschädigten Giftbeutel findet, und bringt ihn mir. Ihr findet viele Spinnen nahe der Trollstadt Shadra\'Alor.$B$BUnd trödelt nicht rum, $N, denn der Giftbeutel muss frisch sein, wenn Ihr ihn abliefert.",
    ["O"] = "Bringt Apotheker Lydon in Tarrens Mühle einen unbeschädigten Giftbeutel.",
    ["T"] = "Unbeschädigter Giftbeutel",
  },
  [2935] = {
    ["D"] = "Das Gift der Brutwachen war nicht so potent wie das Gift, das Ihr mir ursprünglich gebracht habt. Es muss von einer anderen Spinne gewesen sein.$B$BIch will eine frische Probe dieses Gifts. Um herauszufinden, woher es kam, müsst Ihr Euch mit einem Troll unterhalten, der die Bleichborken gut kennt – Meister Gadrin im Dorf Sen\'jin. Reist nach Durotar und sprecht mit Gadrin. Wenn wir Glück haben, weiß er, woher die Bleichborken ihr Gift bekommen.",
    ["O"] = "Sprecht mit Meister Gadrin in Sen\'jin.",
    ["T"] = "Konsultiert Meister Gadrin",
  },
  [2936] = {
    ["D"] = "Die Bleichborken verehren einen Spinnengott und von ihm bekommen sie ihr wirkungsvolles Gift. Wenn Ihr also ihr Gift wollt, müsst Ihr ihren Gott beschwören!$B$BUm das zu tun, müsst Ihr seinen wahren Namen wissen und dieser Name steht auf einer Tafel, die in Tanaris, in der Trollstadt von Zul\'Farrak, in einer Gruft vergraben ist, die von Theka dem Märtyrer bewacht wird.$B$BBesiegt Theka und findet und lest diese Tafel, um den Namen des Spinnengottes herauszufinden. Wenn Ihr ihn kennt, kehrt zu mir zurück.",
    ["O"] = "Lest von der Schrifttafel des Theka, um den Namen des Spinnengottes der Bleichborken zu erfahren, und kehrt dann zu Meister Gadrin zurück.",
    ["T"] = "Der Spinnengott",
  },
  [2937] = {
    ["D"] = "Nehmt dieses Pergament und verlest es am Altar von Shadra\'Alor tief in der Welt der Bleichborken im Hinterland. Dies wird Shadra, den Spinnengott, beschwören.",
    ["O"] = "Besiegt Shadra am Altar von Shadra\'Alor und bringt ihr Gift zu Apotheker Lydon nach Tarrens Mühle.",
    ["T"] = "Die Beschwörung von Shadra",
  },
  [2938] = {
    ["D"] = "Das Gift, das Ihr vom Spinnengott der Bleichborken beschafft habt, ist ein großer Fund! Ich konnte mit nur einem Tropfen von dem Zeug fast zwei Dutzend Hasen töten! Na gut, einige dieser Hasen waren sehr jung und klein genug, um auf eine Handfläche zu passen, und daher war ihre Widerstandskraft gegen Toxine bestimmt niedrig, aber man kann nicht leugnen, dass dieses Gift mächtig ist!$B$BIch würde gern eine Probe zu meinem Vorgesetzten, Apotheker Faranell, in das Apothekarium von Unterstadt entsenden. Ich bin sicher, er würde es gern selbst testen.",
    ["O"] = "Bringt Faranells Päckchen zu Apothekermeister Faranell nach Unterstadt.",
    ["T"] = "Gift für Unterstadt",
  },
  [2939] = {
    ["D"] = "Auch wenn die Ruinen von Feralas ziemlich gefährlich sein können, so erzählen sie doch viel darüber, was hier passiert ist...$B$BAls ich vor einigen Tagen das Geröll südlich von hier durchstöberte, fand ich etwas, das wie ein gewöhnlicher Stock aussah. Allerdings werde ich das Gefühl nicht los, dass es mehr damit auf sich hat. Angelas und ich haben über unseren Büchern hier gebrütet, aber wir konnten nichts darüber herausfinden.$B$BIch habe aber eine Kollegin in Darnassus, die uns vielleicht erzählen kann, was das ist, $N. Warum sprecht Ihr nicht mit ihr und seht, ob sie uns helfen kann?",
    ["O"] = "Sprecht mit Daryn Leuchtwind im Dorf Rut\'theran.",
    ["T"] = "Auf der Suche nach Wissen",
  },
  [2940] = {
    ["D"] = "Es sieht so aus, als sei dieses Buch seit langer Zeit von niemandem mehr geöffnet worden. Sein Einband ist ziemlich abgenutzt und seine Seiten sind vergilbt, aber nach einer kurzen Untersuchung stellt Ihr fest, dass es genau das sein könnte, was Troyas sucht.$B$BIhr steckt es ein, aber dann fällt Euch ein, dass Ihr vielleicht vorher fragen solltet, bevor Ihr es ausleiht.",
    ["O"] = "Fragt Daryn Leuchtwind, ob Ihr Euch ihr Buch ausleihen dürft.",
    ["T"] = "Feralas: Ein historischer Abriss",
  },
  [2941] = {
    ["D"] = "Ich habe in meinem Leben viele Dinge studiert und meine jüngste Leidenschaft sind die Schnappkiefer, die den Strand im Hinterland bevölkern. Es gibt einen bestimmten, den ich gern sehen würde, einen Riesenschnappkiefer namens Gammerita. Ich würde gern selbst hingehen, aber meine Forschungen halten mich hier. Ein Bild von Gammerita wäre wohl die nächstbessere Lösung.$B$BBringt diesen Brief zu Curgle Kurbelhüpf nach Tanaris. Sie hat etwas für mich erfunden, das Bilder einfangen kann. Ich glaube, sie nannte es \"Schnappschuss\"...",
    ["O"] = "Bringt den Brief zu Curgle Kurbelhüpf nach Tanaris.",
    ["T"] = "Der Borger",
  },
  [2942] = {
    ["D"] = "In dem Moment, in dem das Artefakt vom Monolithen getrennt wird, fühlt Ihr, wie die Energie im Stock von Equinex sich zu verflüchtigen beginnt.",
    ["O"] = "Bringt den funkelnden Stein und den Stock von Equinex zu Troyas Mondhauch in die Mondfederfeste.",
    ["T"] = "Der Morgenstein",
  },
  [2943] = {
    ["D"] = "Hier, $N. Bitte, passt auf mein Buch auf.$B$BNun aber schnell. Troyas wartet bestimmt schon ganz ungeduldig auf Eure Rückkehr.",
    ["O"] = "Liefert das Buch bei Troyas Mondhauch in der Mondfederfeste ab.",
    ["T"] = "Rückkehr zu Troyas",
  },
  [2944] = {
    ["D"] = "Werft erst mal einen Blick auf meine neue Erfindung, $N. Visiert einfach an, wovon Ihr ein Bild haben möchtet, und drückt dann auf den Knopf.$B$BWas war das für eine Kreatur aus dem Hinterland, die Ihr erwähnt habt? Gammerita? Tja, viel Erfolg bei der Suche nach ihr. Ich bin sicher, Daryn wird mit dem Schnappschuss, den Ihr ihr zurückbringt, sehr zufrieden sein.$B$BWie auch immer, hier habt Ihr den Superschnapper. Viel Spaß!",
    ["O"] = "Benutzt den Superschnapper FX, um einen Schnappschuss von Gammerita zu machen, und kehrt dann zu Daryn Leuchtwind nach Rut\'theran zurück.",
    ["T"] = "Der Superschnapper FX",
  },
  [2946] = {
    ["D"] = "Wir möchten, dass Ihr die Mininaturscheiben, die Ihr gefunden habt, zu den versiegelten Toren von Uldum bringt. Wir... wir sind uns nicht ganz sicher, was dann passieren wird.$B$BEs klingt ein bisschen riskant, sicher, das geben wir auch zu. Es könnte allerdings auch sein, dass überhaupt nichts passiert. Vielleicht müsst Ihr mit den Scheiben irgendetwas anstellen. Vielleicht öffnen sich die Türen, wenn Ihr die Scheiben bei Euch habt. Versucht alles Mögliche und seid offen für alles, was passieren könnte.$B$BKehrt nach Eisenschmiede zurück, wenn Ihr herausgefunden habt, was die Scheiben bewirken. Es wird einiges zu untersuchen geben, wenn Ihr das erledigt habt!",
    ["O"] = "Bringt die Miniaturplatinscheiben zu den Toren von Uldum in Tanaris und sucht nach Hinweisen auf die Verbindung zwischen beidem.",
    ["T"] = "Mal sehen, was passiert...",
  },
  [2947] = {
    ["D"] = "Ihr öffnet die Schachtel, die der Funkelmat 5200 ausgegeben hat, und entdeckt, was Ihr vermutet hattet - einen glänzenden Ring! Beim Anstecken des Rings fühlt Ihr magische Macht durch Euren Körper fließen.$B$BAuf der Innenseite des Rings befindet sich eine kleine, aber dennoch klar erkennbare Gravur des Siegels von Eisenschmiede. Daneben sind drei kleine Initialen eingraviert: \"TdK\". Dies könnten entweder die Initialen des ursprünglichen Besitzers oder die des Herstellers des Rings sein. Den Ring behalten...oder ihn dieser Person zurückgeben? Entscheidungen über Entscheidungen...",
    ["O"] = "Ihr könnt den Ring entweder behalten oder die Person finden, die für die Prägung und Gravuren auf der Innenseite des Rings verantwortlich ist.",
    ["T"] = "Die Rückkehr des Rings",
  },
  [2948] = {
    ["D"] = "Hört mir zu, $N. Dieser Ring gehört Euch, das ist schon richtig... aber ich kann Euch einen besseren machen! Das Design ist, wie ich bereits sagte, wirklich einfach. Ich bin heutzutage wesentlich geschickter und für die läppische Summe von dreißig Silbermünzen werde ich ihn für Euch aufpeppen! Na ja, eigentlich etwas mehr als dreißig...$B$BIch bin derzeit in einer misslichen Lage. Mein ganzer Materialvorrat ist aufgebraucht. Wenn Ihr mir einen Barren Silber und einen Moosachat bringt, verziere ich Euch den Ring damit und peppe ihn richtig schön auf!!$B$BVertraut mir!",
    ["O"] = "Bringt Talvash del Kissel in Eisenschmiede den glänzenden Goldring, einen Silberbarren, einen Moosachat und 30 Silbermünzen.",
    ["T"] = "Gnomenverbesserungen",
  },
  [2949] = {
    ["D"] = "Ihr öffnet die Schachtel, die der Funkelmat 5200 ausgegeben hat, und entdeckt einen glänzenden Ring! Beim Anstecken des Rings fühlt Ihr magische Macht durch Euren Körper fließen.$B$BAuf der Innenseite des Rings befindet sich eine kleine, aber dennoch klar erkennbare Gravur des Siegels von Orgrimmar. Daneben sind Buchstaben eingraviert, aber selbst nach der Säuberung sind nur die ersten drei zu erkennen: \'NOG...\'$B$BDies könnten entweder die Initialen des ursprünglichen Besitzers oder die des Herstellers des Rings sein. Den Ring behalten...oder \'NOG\' suchen - Entscheidungen, Entscheidungen...",
    ["O"] = "Ihr könnt den Ring entweder behalten oder die Person finden, die für die Prägung und Gravuren auf der Innenseite des Rings verantwortlich ist.",
    ["T"] = "Die Rückkehr des Rings",
  },
  [2950] = {
    ["D"] = "Nein, nein, nein - ich behaupte nicht plötzlich, der Ring sei meiner. Normalerweise mache ich so etwas nicht, aber Eure Information über die Dunkeleisenzwerge war sehr nützlich. Ich werde diesen Ring für Euch so verbessern, dass selbst der, der dieses einfache kleine Ding hergestellt hat, es nicht hätte besser machen können. Wahrscheinlich war es irgendein Gnom...$B$BWenn Ihr mir einen Barren Silber, einen Moosachat und dreißig Silbermünzen bringt, verziere ich den Ring damit derart, dass seine Magie noch verstärkt wird. Interessiert?",
    ["O"] = "Bringt Nogg in Orgrimmar den glänzenden Goldring, einen Silberbarren, einen Moosachat und 30 Silbermünzen.",
    ["T"] = "Noggs Ringerneuerung",
  },
  [2953] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Funkelmatastisch",
  },
  [2954] = {
    ["D"] = "Willkommen - Ich bin der Wächter des Eingangs. Ich werde Euch den Zugang zum Areal von Uldum nur gewähren, wenn Ihr die Platten von Uldum in Euren Scheibensatz integriert habt.$B$BDas Erscheinen des Steinbehüters von Norgannon muss die Reaktion sein, die man sich davon erhoffte, als man Euch mit den Platinscheiben in die Tanariswüste schickte. Wie Euch aufgetragen, werdet Ihr die Geschichte, die hinter dem Steinbehüter steckt, herausfinden müssen, bevor Ihr weitermachen könnt.",
    ["O"] = "Findet den Zweck des Steinbehüters von Norgannon heraus und interagiert dann mit dem Podest von Uldum in der Tanariswüste.",
    ["T"] = "Der Steinbehüter",
  },
  [2962] = {
    ["D"] = "Eure Probe war nicht nur von unzureichender Menge, sondern auch noch verunreinigt.$B$BWas wir brauchen, ist mehr von dem grünen Leuchten. Die Troggs sind offensichtlich nicht stark genug verstrahlt, um unseren Ansprüchen zu genügen. Ihr werdet nach Gnomeregan gehen und etwas der radioaktiven Ablagerung direkt von der Quelle besorgen müssen. Ich spreche von den bestrahlten Schleimen, Lauerern und Schrecken. Seid gewarnt, diese Art von Ablagerung ist schwer abzufüllen. Es könnte Euch mehrere Versuche mit der Phiole kosten, bis Ihr eine geeignete Probe bergen könnt.",
    ["O"] = "Reist nach Gnomeregan und bringt etwas von der hoch konzentrierten radioaktiven Ablagerung zurück. Seid gewarnt, die Ablagerung ist instabil und wird ziemlich schnell zerfallen.$B$BOzzie wird außerdem Eure schwere bleierne Phiole benötigen, nachdem die Aufgabe erledigt ist.",
    ["T"] = "Das einzige Heilmittel ist mehr grünes Leuchten",
  },
  [2963] = {
    ["D"] = "Seid mir erneut gegrüßt, $N! Seit Eurer Entdeckung in Uldaman ist die Forscherliga voller Aufregung. Die Liga hat daran gearbeitet, die Geheimnisse der Miniaturplatinscheiben zu enträtseln, die Ihr gefunden habt. Euer treffliches Erscheinen hier in der Halle erlaubt es mir, Euch mitzuteilen, dass wir ziemlich wahrscheinlich eine Spur haben!$B$BHistoriker Karnik hat einige wichtige Hinweise auf den wahren Ursprung des Tals der Behüter in der Wüste von Tanaris gefunden. Er möchte so schnell wie möglich mit Euch sprechen!",
    ["O"] = "Sprecht mit Historiker Karnik in der Halle der Forscher in Eisenschmiede.",
    ["T"] = "Omen von Uldum",
  },
  [2964] = {
    ["D"] = "Nun, $N... Wir scheinen derzeit in einer temporären Sackgasse zu stecken. Wir wissen, was wir brauchen - die Platten von Uldum -, aber wir haben keinen Schimmer, wo diese sein könnten. Wir müssen eine Menge Nachforschungen anstellen, um einen guten Einsatzplan zusammenzustellen. Ich habe ein paar Ideen, wo man mit der Suche anfangen könnte. Ich hege große Hoffnungen, dass wir Euch bald bitten werden, loszuziehen und sie zu bergen.$B$BDer Hochforscher muss hiervon in Kenntnis gesetzt werden, $N. Zweifellos wird er Euch ebenfalls persönlich dafür danken wollen, dass Ihr der Liga wieder einmal geholfen habt.",
    ["O"] = "Sprecht mit Hochforscher Magellas in Eisenschmiede.",
    ["T"] = "Eine zukünftige Aufgabe",
  },
  [2965] = {
    ["D"] = "Ah, $N, es ist vortrefflich, dass Ihr erneut zu mir gekommen seid. Eure Funde in der Grabungsstätte von Uldaman haben uns Gelehrten sicherlich einigen Gesprächsstoff geliefert. Ich unterhielt mich also gerade mit einer der Druiden, Nara Wildmähne, als mir dämmerte, dass ihre früheren Erfahrungen als Abenteurer von großer Wichtigkeit für unseren nächsten Schritt sein könnten.$B$BIch möchte, dass Ihr mit ihr über ihre Reisen ins Tal der Behüter in der Wüste von Tanaris sprecht. Sie erwartet Euch auf der Anhöhe der Ältesten.",
    ["O"] = "Sprecht mit Nara Wildmähne auf der Anhöhe der Ältesten in Donnerfels.",
    ["T"] = "Omen von Uldum",
  },
  [2966] = {
    ["D"] = "Weiser Wahrspruch und ich würden Euch bitten, die Miniaturscheiben, die Ihr gefunden habt, zu den versiegelten Toren von Uldum zu bringen. Wir... wir sind uns nicht ganz sicher, was dann passieren wird.$B$BDas klingt ein wenig riskant, sicher, das geben wir auch zu. Es könnte allerdings auch gar nichts passieren. Vielleicht müsst Ihr die Scheiben mit etwas benutzen. Vielleicht öffnen sich die Türen, wenn Ihr die Scheiben bei Euch habt. Versucht alles Mögliche und seid bereit für alles, was passieren könnte.$B$BFindet alles heraus, was Ihr könnt, und kehrt dann zu mir zurück. Wir werden dann viel zu bereden haben.",
    ["O"] = "Bringt die Miniaturplatinscheiben zu den Toren von Uldum in Tanaris und sucht nach Hinweisen auf die Verbindung zwischen beidem.",
    ["T"] = "Mal sehen, was passiert...",
  },
  [2967] = {
    ["D"] = "Ihr greift erneut auf das Podest zu und die Platinscheiben erscheinen plötzlich wieder in Eurem Rucksack.$B$BNachdem Ihr das Wissen des Steinbehüters verinnerlicht habt und die Scheiben wieder in Eurem Besitz sind, ist es jetzt an der Zeit, nach Donnerfels zurückzukehren und Nara Wildmähne von Eurer Entdeckung in Kenntnis zu setzen.",
    ["O"] = "Sprecht mit Nara Wildmähne in Donnerfels.",
    ["T"] = "Rückkehr nach Donnerfels",
  },
  [2968] = {
    ["D"] = "Nun, $N... Wir scheinen unglücklicherweise in einer Sackgasse zu stecken. Wir wissen, was wir brauchen - die Platten von Uldum -, aber wir haben keinen Schimmer, wo diese sein könnten. Wir werden Nachforschungen darüber anstellen und dann einen soliden Einsatzplan entwickeln. Ich habe ein paar Ideen, wo man mit der Suche anfangen könnte; Ich hege große Hoffnungen, dass wir Euch bald bitten werden, Euch wieder unseren Bemühungen um die Entdeckung der Wahrheit anzuschließen.$B$BWeiser Wahrspruch muss hiervon in Kenntnis gesetzt werden, $N; zweifellos wird er Euch ebenfalls persönlich dafür danken wollen, dass Ihr noch einmal dem Ruf gefolgt seid.",
    ["O"] = "Sprecht mit dem Weisen Wahrspruch in Donnerfels.",
    ["T"] = "Eine zukünftige Aufgabe",
  },
  [2969] = {
    ["D"] = "Der dreckige Grimmtotemklan fängt Feendrachen ein, $N. Jer\'kai und ich haben keine Ahnung, was sie mit ihnen vorhaben, aber es ist bestimmt nichts Gutes.$B$BWir sind nicht stark genug, um uns durch das Lager zu kämpfen und die gefangenen Drachen zu befreien, aber mit Eurer Hilfe...$B$BWenn Ihr willens seid und ein paar starke Freunde habt, dann befreit mit diesem Schlüssel die Kreaturen aus ihrem Käfig und stellt sicher, dass sie mit ihrem Leben davonkommen. Ich werde die ganze Zeit an Eurer Seite sein. Jer\'kai wird hier bleiben.",
    ["O"] = "Befreit mit dem Bambuskäfigschlüssel die Grimmlingflitzer aus dem Pferch im Grimmtotemlager. Kindal Mondweber sowie mindestens 6 Grimmlingflitzer müssen überleben.$B$BIhr habt 6 Minuten, bis Kindal beschließt, dass es Zeit zum Rückzug und Eure Aufgabe gescheitert ist.",
    ["T"] = "Freiheit für alle Kreaturen",
  },
  [2970] = {
    ["D"] = "Wie sie Euch ja schon mitgeteilt hat, tragen Kindal und ich den Kampf jetzt zu den Grimmtotem. Gemeinsam sollten wir in der Lage sein, einige der Tauren ohne große Gefahr für uns selbst auszuschalten, aber wir würden etwas Hilfe sehr begrüßen, wenn Ihr dazu bereit wärt.$B$BIhr habt das Lager unterhalb von uns ja schon gesehen, aber es gibt mehr davon im Osten. Verwendet Eure eigenen Taktiken, um den Tauren so viel Schaden wie möglich zuzufügen. Wir können uns alle wieder hier treffen, nachdem sie Kindals Pfeile und Eure Waffen geschmeckt haben!",
    ["O"] = "Tötet 12 Naturalisten der Grimmtotem, 10 Räuber der Grimmtotem sowie 6 Schamanen der Grimmtotem und kehrt dann zu Jer\'kai Mondweber nach Feralas zurück.",
    ["T"] = "Gerechtigkeit üben",
  },
  [2971] = "_",
  [2972] = {
    ["D"] = "Hier draußen in der Wildnis haben Kindal und ich nur geringe Mittel, um Euch für Eure Hilfe zu entlohnen, aber wir wollen nicht, dass Ihr Euch nicht wertgeschätzt fühlt. Nehmt meinen Siegelring. Solltet Ihr Euch jemals in Teldrassil wiederfinden, geht zum Tempel des Mondes im Herzen von Darnassus. Dort werdet Ihr meine Hohepriesterin vorfinden, Tyrande Wisperwind. Der Ring wird sie wissen lassen, dass es uns gut geht, und Eure Geschichte untermauern.$B$BWir beide danken Euch, $N.",
    ["O"] = "Bringt Jer\'kais Siegelring zu Hohepriesterin Tyrande Wisperwind nach Darnassus.",
    ["T"] = "Gerechtigkeit üben",
  },
  [2973] = {
    ["D"] = "Grüße, $C. Ich bin mit meinen Gefährten auf einer Mission für unsere Meister aus Orgrimmar hinunter nach Feralas gereist.$B$BMeine Lebensgefährtin Korrin liebt neue Kleider und daher habe ich vor, ihr einen neuen Umhang zu fertigen, der mit den hauchzarten Flügeln von Feendrachen geschmückt ist. Seid Ihr vielleicht in der Lage, mir zu helfen? Grimmlingflitzer mit prachtvollen Flügeln, wie ich sie will, gibt es westlich vom Camp Mojache.",
    ["O"] = "Bringt 10 regenbogenfarbene Grimmlingflitzerflügel zu Krueg Schädelspalter nach Camp Mojache.",
    ["T"] = "Schimmer für einen neuen Umhang",
  },
  [2974] = {
    ["D"] = "Was!? Ihr seid in dieser Gegend auf Tauren getroffen? Ihr macht Witze! Wie sahen sie aus?$B$BDer Grimmtotemklan! Ihr habt einige von denen gefunden? Belgrom, einer unserer Meister, interessiert sich für diese Sippe... Er hat sogar ein Kopfgeld auf sie ausgesetzt.$B$BIhr müsst wieder zurückgehen, $C. Geht zurück und bringt mir ihre Hörner... haufenweise, wenn Ihr könnt. Mit den Grimmtotem ist nicht zu spaßen und man sollte sie nicht unterschätzen. Geht zurück und erschlagt sie; lasst die Flüsse in Feralas rot werden von ihrem dunklen Blut.",
    ["O"] = "Bringt 20 Grimmtotemhörner zu Krueg Schädelspalter in Camp Mojache.",
    ["T"] = "Eine grausige Entdeckung",
  },
  [2975] = {
    ["D"] = "Der Einfluss des Ogerstamms der Gordunni ist in diesem Gebiet kaum zu übersehen. Schaut Euch mal um, $N. Seine bloße Anwesenheit zeichnet das Land. Wir müssen die Gordunni zurückdrängen, bevor sie sich in Feralas weiter ausbreiten können.$B$BFolgt dem Fluss nach Norden und Ihr werdet ihren Außenposten finden. Verringert ihre Zahl, $N. Wir müssen ihnen klar machen, dass Ihresgleichen hier nicht willkommen ist.",
    ["O"] = "Rok Orhan im Camp Mojache will, dass Ihr 10 Oger der Gordunni, 10 Magier der Gordunni und 5 Schläger der Gordunni tötet.",
    ["T"] = "Die Oger von Feralas",
  },
  [2976] = {
    ["D"] = "Was?!$B$BHm, ich hab wohl wirklich ein Kopfgeld erwähnt, nicht wahr? Gut, das wird mich lehren, meinen Mund nicht zu weit aufzureißen, oder?$B$BIhr habt die Arbeit gemacht, also ist es wohl nur gerecht, wenn Ihr den größten Lohn einheimst. Nehmt die Hörner, ich lasse sie für Euch in eine Kiste packen. Findet Belgrom Felshammer im Tal der Ehre. Er wird wissen wollen, was Ihr gesehen habt, und Euch für Eure harte Arbeit belohnen.$B$BMacht es gut, $C, und danke noch einmal für alles, was Ihr getan habt.",
    ["O"] = "Bringt die Kiste mit Grimmtotemhörnern zu Belgrom Felshammer nach Orgrimmar.",
    ["T"] = "Eine grausige Entdeckung",
  },
  [2977] = {
    ["D"] = "Ihr greift erneut auf das Podest zu und die Platinscheiben erscheinen plötzlich wieder in Eurem Rucksack.$B$BNachdem Ihr das Wissen des Steinbehüters verinnerlicht habt und die Scheiben wieder in Eurem Besitz sind, ist es jetzt an der Zeit, nach Eisenschmiede zurückzukehren und die Forscherliga von Eurer Entdeckung in Kenntnis zu setzen.",
    ["O"] = "Sprecht mit Historiker Karnik in Eisenschmiede.",
    ["T"] = "Rückkehr nach Eisenschmiede",
  },
  [2978] = {
    ["D"] = "Ihr hebt die verschmutzte Schriftrolle auf und bemerkt, dass sie eine Art Versammlung oder Zeremonie darstellt. Rok Orhan wird sich für diese Schriftrolle, und was sie für die Gordunni bedeutet, interessieren.",
    ["O"] = "Bringt die Gordunnischriftrolle zu Rok Orhan ins Camp Mojache.",
    ["T"] = "Die Gordunnischriftrolle",
  },
  [2979] = {
    ["D"] = "Dies scheinen Pläne für eine Art dunkle Zeremonie zu sein, $N. Soweit ich das erkennen kann, wird dieses Ritual von den Magierlords der Gordunni ausgeführt.$B$BFindet einen dieser Magierlords und sammelt eine Kugel von ihm.$B$BDie Kugel eines Magiers enthält große Macht und wird uns dabei helfen, mehr über diese Zeremonie und die Magie, die die Gordunni beherrschen, herauszufinden.",
    ["O"] = "Rok Orhan im Camp Mojache will, dass Ihr eine Gordunnikugel findet.",
    ["T"] = "Dunkle Zeremonie",
  },
  [2980] = {
    ["D"] = "Jetzt, da Ihr bewiesen habt, dass Ihr ein wahrer Kämpfer seid, vertraue ich darauf, dass Ihr den Mut besitzt, mit der vorliegenden Aufgabe weiterzumachen.$B$BReist in die Obere Wildnis südwestlich von hier. Dort gibt es einige verlassene und zerstörte Gebäude auf dem Land, das einst den Nachtelfen gehörte. Sucht nach diesen Ruinen, und wenn Ihr verwelkte Bäume und entweihtes Gelände seht, dann werdet Ihr die Gordunni finden. Drängt sie zurück.$B$BDiese Oger sind viel stärker als diejenigen, denen Ihr bisher entgegengetreten seid, $N. Seht Euch vor.",
    ["O"] = "Rok Orhan im Camp Mojache will, dass Ihr 10 Schamanen der Gordunni, 10 Hexenmeister der Gordunni und 5 Raufer der Gordunni tötet.",
    ["T"] = "Die Oger von Feralas",
  },
  [2981] = {
    ["D"] = "Feralas ist der ideale Ort für jemanden mit Eurer Erfahrung, $C, der sich selbst beweisen will. Ein Stamm von Ogern, die sich Gordunni nennen, ist die vorherrschende Militärmacht dort, und ihre Zahl wächst. Sie müssen aufgehalten werden, bevor sie unsere Anwesenheit in Feralas bedrohen.$B$BEine mir bekannte Kriegerin mit Namen Rok Orhan lebt im Camp Mojache in Feralas; geht zu ihr. Sie wird Euch mehr über die Situation erzählen, wenn Ihr dort ankommt.",
    ["O"] = "Belgrom Felshammer will, dass Ihr mit Rok Orhan im Camp Mojache sprecht.",
    ["T"] = "Eine Bedrohung in Feralas",
  },
  [2982] = {
    ["D"] = "Obwohl es gefährlich ist, haben Troyas und ich die Ruinen erforscht, die in den Wäldern von Feralas zu finden sind.$B$BIn jüngster Zeit ist Troyas jedoch oft allein in die Obere Wildnis gegangen und ich mache mir Sorgen um ihn. Die Oger der Gordunni haben dieses Gebiet besetzt und sie reagieren nicht sehr freundlich auf jemanden, der ihr Territorium betritt.$B$BIch hoffte, dass Ihr vielleicht zu den Ruinen im Südosten reisen und einige der Oger beseitigen würdet. Macht es ein wenig sicherer für meinen Troyas und ich werde Euch sehr dankbar sein.",
    ["O"] = "Angelas Mondhauch will, dass Ihr 8 Hexenmeister der Gordunni, 8 Schamanen der Gordunni und 8 Schläger der Gordunni tötet.",
    ["T"] = "Die Obere Wildnis",
  },
  [2983] = {
    ["D"] = "Es ist eine Weile her, seit Ihr zuletzt geprüft wurdet, $N. Ihr wart in Euren Studien bisher sehr geduldig, doch es ist an der Zeit, dass Ihr etwas über größere Mächte erlernt... Es ist an der Zeit, dass Ihr wahre Macht erfahrt. Das Element des Feuers ist Euer, wenn Ihr bereit seid, dafür zu bezahlen.$B$BSucht nach Kranal Fiss im Brachland. Er lebt in einer kleinen Siedlung nördlich des Wegekreuzes, aber gebt Acht... wenn Ihr das Land der Nachtelfen erreicht, seid Ihr zu weit gegangen.",
    ["O"] = "Sucht Kranal Fiss im Brachland.",
    ["T"] = "Ruf des Feuers",
  },
  [2984] = {
    ["D"] = "Es ist lange her, seit Ihr zuletzt geprüft wurdet, $N. Bislang habt Ihr bei Euren Studien Geduld bewiesen, doch die Zeit ist gekommen, da Ihr mehr lernen müsst. Die Zeit ist gekommen, mehr über Zerstörung zu lernen... und über Chaos. Ihr könnt das Element des Feuers verstehen lernen, wenn Ihr bereit seid, dafür zu bezahlen.$B$BSucht nach Kranal Fiss im Brachland. Er lebt in einer kleinen Siedlung nördlich des Wegekreuzes, aber gebt Acht, wenn Ihr das Land der Nachtelfen erreicht, seid Ihr zu weit gegangen.",
    ["O"] = "Sucht Kranal Fiss im Brachland.",
    ["T"] = "Ruf des Feuers",
  },
  [2985] = {
    ["D"] = "Ihr seht aus, als wäret Ihr bereit für die nächste Prüfung, $N. Ihr wart bislang geduldig in Euren Studien, doch die Zeit ist gekommen, da Ihr mehr erlernen müsst. Die Zeit ist gekommen, mehr über die Reinheit des Wassers zu erlernen.$B$BSucht nach Islen Wasserdeuter im Brachland. Sie befindet sich an der Ostküste, südlich von Ratschet und dem Südstrom. Ihr findet sie in ihrer Angelhütte. Viel Glück, $N.",
    ["O"] = "Sucht Islen Wasserdeuter im Brachland.",
    ["T"] = "Ruf des Wassers",
  },
  [2986] = {
    ["D"] = "Es ist lange her, seit Ihr zuletzt geprüft wurdet, $N. Bislang habt Ihr bei Euren Studien Geduld bewiesen, doch die Zeit ist gekommen, da Ihr mehr lernen müsst. Die Zeit ist gekommen, mehr über die Reinheit des Wassers zu lernen.$B$BSucht nach Islen Wasserdeuter im Brachland. Sie befindet sich an der Ostküste, südlich von Ratschet und dem Südstrom. Ihr findet sie in ihrer Fischerhütte. Viel Glück, $N.",
    ["O"] = "Sucht Islen Wasserdeuter im Brachland.",
    ["T"] = "Ruf des Wassers",
  },
  [2988] = {
    ["D"] = "Der Sohn unseres mächtigsten Greifen ist verschwunden! Sturmschwinges Erbe, Scharfschnabel, ging vor ein paar Tagen bei der Jagd mit seinen Eltern verloren und wir glauben, dass die Trolle ihn geschnappt haben.$B$BIhr müsst uns helfen! Durchsucht die Käfige in den beiden Dörfern der Bleichborkentrolle, um herauszufinden, ob Scharfschnabel dort ist.",
    ["O"] = "Durchsucht die Käfige in den beiden Dörfern der Bleichborken und kehrt dann zu Greifenmeister Krallenaxt zurück.",
    ["T"] = "Käfige der Bleichborken",
  },
  [2989] = {
    ["D"] = "Scharfschnabel war nicht bei den Bleichborkentrollen. Vielleicht haben ihn die Blutfratzentrolle geholt. Geht zum Altar von Zul am Westrand des Territoriums der Blutfratzen und sucht nach dem Greifenjungen.",
    ["O"] = "Überprüft die Oberfläche des Altars von Zul und kehrt dann zu Greifenmeister Krallenaxt zurück.",
    ["T"] = "Der Altar von Zul",
  },
  [2990] = {
    ["D"] = "Bringt diese Feder zum Rutengänger Thadius Grimmschatten. Er verließ vor Jahren den Nistgipfel, um sich dunklerem Wissen zuzuwenden, aber in seinem Herzen ist er immer noch ein Wildhammer!$B$BUnd vielleicht können uns seine Talente dabei helfen, Scharfschnabel zu finden.$B$BDiese Feder stammt aus dem Nest, in dem das Greifenjunge schläft. Wenn Ihr sie Thadius gebt, kann er mit etwas Glück seine Rutengangmagie verwenden, um Scharfschnabel zu finden.$B$BViel Glück, $N. Ihr werdet Thadius in den Verwüsteten Landen finden, im Magierturm der Burg Nethergarde.",
    ["O"] = "Bringt Scharfschnabels Feder zu Thadius Grimmschatten in die Verwüsteten Lande.",
    ["T"] = "Thadius Grimmschatten",
  },
  [2991] = {
    ["D"] = "Ich werde herausfinden, wo Euer Greif ist, aber erst müsst Ihr etwas für mich tun. Es gibt da einen Troll in Zul\'Farrak, Nekrum der Ausweider, der ein Medaillon besitzt, das ich wirklich gern studieren würde. Bringt mir das Medaillon und ich werde meine Kräfte dafür verwenden, Scharfschnabel zu finden.$B$BZul\'Farrak ist jenseits des Ozeans, im Westen, in der Wüste von Tanaris.",
    ["O"] = "Bringt Thadius Grimmschatten in den Verwüsteten Landen Nekrums Medaillon.",
    ["T"] = "Nekrums Medaillon",
  },
  [2992] = {
    ["D"] = "Lasst mir etwas Platz, während ich ein Rutengangritual durchführe. Ich werde die Verbindung zwischen der Feder, die Ihr mir gebracht habt, und ihrem Besitzer herstellen und dann selbst dieser Verbindung zu Scharfschnabel folgen.$B$BZumindest... wenn alles nach Plan verläuft. Wenn nicht, nun... dann schließt Ihr besser die Augen.",
    ["O"] = "Wartet, bis Grimmschatten das Rutengangritual durchgeführt hat.",
    ["T"] = "Die Weissagung",
  },
  [2993] = {
    ["D"] = "Scharfschnabel wird in einem Käfig hoch oben auf einer Klippe am Gipfel des Jintha\'Alor im Hinterland gefangen gehalten. Der Schlüssel zu diesem Käfig ist im Besitz der Hohepriesterin der Blutfratzentrolle, der üblen Priesterin Hexx.$B$BDa. Ich habe meinen Teil erfüllt. Ich wünsche Euch Glück, $N.",
    ["O"] = "Sprecht mit Greifenmeister Krallenaxt.",
    ["T"] = "Rückkehr ins Hinterland",
  },
  [2994] = {
    ["D"] = "Wir müssen Scharfschnabel retten!$B$BKämpft Euch bis zum Gipfel von Jintha\'Alor im Herzen des Blutfratzenterritoriums vor, holt Euch den Schlüssel zu Scharfschnabels Käfig von der üblen Priesterin Hexx und dann sucht und befreit Scharfschnabel.",
    ["O"] = "Kämpft Euch bis zum Gipfel von Jintha\'Alor durch, tötet die üble Priesterin Hexx, um an den Schlüssel zu Scharfschnabels Käfig zu kommen, und rettet Scharfschnabel.",
    ["T"] = "Scharfschnabels Rettung",
  },
  [2995] = {
    ["D"] = "Die Jagdhütte von Quel\'Danil ist ein Zentrum der Kommunikation für die Hochelfen des Hinterlands. Ihre Botschafter sind damit beschäftigt, Beziehungen zu anderen Völkern zu knüpfen, mit denen sie ihr Wissen über die Trolle der Bleichborken teilen, die das Gebiet im südlichen Hinterland bewohnen. Alle Aufzeichnungen betreffs dieses Nachrichtenaustauschs werden in der Hütte aufbewahrt.$B$BWir können es nicht brauchen, wenn die Hochtalelfen Allianzen mit anderen schließen, $N.$B$BVernichtet die Hochtaldokumente. Wir müssen ihre Nachrichtenwege niederbrennen... im wahrsten Sinne des Wortes.",
    ["O"] = "Verbrennt die Hochtalaufzeichnungen, Hochtalberichte und Hochtalnotizen.",
    ["T"] = "Nachrichtenwege",
  },
  [2996] = {
    ["D"] = "Ich habe Euch in der Vergangenheit viele Fertigkeiten beigebracht, aber es ist an der Zeit, dass Euch ein anderer lehrt. Er lebt in Ratschet und sein Name ist Strahad. Er ist ein Mensch, aber lasst Euch deswegen nicht dazu hinreißen, ihn für schwach zu halten. Er mag klein sein, aber er ist auch mächtig. Er kann Euch mächtigere Beschwörungen lehren - ich fühle, dass Ihr dazu bereit seid. Geht nun zu ihm. Er ist in Ratschet... Über der Hafenstadt gleich bei seinem Turm.",
    ["O"] = "Sprecht mit Strahad Farsan in Ratschet.",
    ["T"] = "Die Suche nach Strahad",
  },
  [2997] = {
    ["D"] = "Hallo, $N. Ich weiß, Ihr seid damit beschäftigt, den Leuten von Dun Morogh zu helfen, aber wenn Ihr etwas Zeit habt, könntet Ihr Euch auf den Weg nach Eisenschmiede hinauf machen, um mit einer unserer am meisten Respektierten zu reden: Tiza Kriegshammer. Zu ihr kommen die jüngeren Paladine, wenn es an der Zeit für sie ist, mehr über das Licht und darüber zu erfahren, was von einem erwartet wird.$B$BIhr könnt sie oben in der Halle der Mysterien finden. Falls Ihr noch nie dort wart, das ist im Mystikerviertel. Viel Glück, $N.",
    ["O"] = "Sprecht mit Tiza Kriegshammer in Eisenschmiede.",
    ["T"] = "Foliant der Offenbarung",
  },
  [2998] = {
    ["D"] = "Seid gegrüßt, $GBruder:Schwester;. Duthorian Rall, einer unserer einflussreichsten Paladine, hat kürzlich von Euch gesprochen. Ich habe mitbekommen, wie er darüber sprach, dass es an der Zeit sei, dass Ihr ein wenig getestet würdet... Dabei fielen auch einige Komplimente darüber, wie gut Ihr bisher abgeschnitten habt.$B$BWenn ich Ihr wäre, würde ich nach Sturmwind gehen und mit ihm sprechen, sobald Ihr Zeit habt. Ich bin sicher, er ist in der Kathedrale des Lichts - dort verbringt er einen Großteil seiner Zeit.",
    ["O"] = "Sprecht mit Duthorian Rall in Sturmwind.",
    ["T"] = "Foliant der Offenbarung",
  },
  [2999] = {
    ["D"] = "Hallo, $N. Ich weiß, Ihr seid damit beschäftigt, den Leuten von Dun Morogh zu helfen, aber wenn Ihr etwas Zeit habt, könntet Ihr nach oben gehen, um mit einer unserer am meisten Respektierten zu reden: Tiza Kriegshammer. Zu ihr kommen die jüngeren Paladine, wenn es an der Zeit für sie ist, mehr über das Licht und darüber zu erfahren, was von einem erwartet wird.",
    ["O"] = "Sprecht mit Tiza Kriegshammer in Eisenschmiede.",
    ["T"] = "Foliant der Offenbarung",
  },
  [3000] = {
    ["D"] = "Hallo, $N. Ich weiß, Ihr seid damit beschäftigt, den Völkern Azeroths zu helfen, aber wenn Ihr etwas Zeit habt, solltet Ihr nach Eisenschmiede zurückkehren, um mit einem der am meisten respektierten Paladine Eurer Heimat zu reden: Tiza Kriegshammer. Zu ihr kommen die jüngeren Zwergenpaladine, wenn es an der Zeit für sie ist, mehr über das Licht und darüber zu erfahren, was von uns allen erwartet wird.$B$BIhr findet sie im Obergeschoss der Halle der Mysterien, die sich im Mystikerviertel befindet.",
    ["O"] = "Sprecht mit Tiza Kriegshammer in Eisenschmiede.",
    ["T"] = "Foliant der Offenbarung",
  },
  [3001] = {
    ["D"] = "Ich habe Euch in der Vergangenheit viele Fertigkeiten beigebracht, aber es ist an der Zeit, dass Euch ein anderer lehrt. Er lebt in Ratschet und sein Name ist Strahad. Er ist ein Mensch, aber lasst Euch deswegen nicht dazu hinreißen, ihn für schwach zu halten oder als Euren Feind anzusehen - zumindest vorerst. Er ist mächtig und sollte respektiert werden. Er kann Euch große Beschwörungen lehren, Beschwörungen, für die Ihr, glaube ich, bereit seid. Geht nun zu ihm.$B$BEr ist in Ratschet... Über der Hafenstadt, gleich bei dem Turm dort.",
    ["O"] = "Findet Strahad Farsan in Ratschet.",
    ["T"] = "Die Suche nach Strahad",
  },
  [3002] = {
    ["D"] = "Obwohl ich meinen Feind genau kenne, bin ich ein Krieger, $N. Ich kann Euch nicht sagen, über welche Fähigkeiten diese Kugel verfügt.$B$BIch kenne einen Magier in Orgrimmar, der uns mehr über die Kugel sagen können wird. Bringt sie zu ihm.",
    ["O"] = "Bringt die Gordunnikugel zu Uthel\'nay in die Dunkeldornenlodge in Orgrimmar.",
    ["T"] = "Die Gordunnikugel",
  },
  [3022] = {
    ["D"] = "$N, ich sehe deutlich, dass Ihr Euch liebend gern meiner noblen Sache zuwenden möchtet. Ja, natürlich könnt Ihr etwas für mich tun!$B$BBringt diese Kiste mit Eiern zu Erelas Himmelsbern. Das ist ein Druide, der sich um diese Eier kümmert. Er bebrütet sie und bildet dann die jungen Hippogryphen aus. Ihr findet ihn in Teldrassil, in einem kleinen Dorf im Süden namens Rut\'theran.",
    ["O"] = "Bringt die sorgfältig verpackte Kiste zu Erelas Himmelsbern in das Dorf Rut\'theran.",
    ["T"] = "Vorsichtig behandeln.",
  },
  [3042] = {
    ["D"] = "Die Trolle der Sandwüter von Zul\'Farrak stellen ein Aushärtungsmittel aus Sandstein her, und ich kann dies verwenden, um die von mir hergestellten Waren auszuhärten, ja, so ist es! Von Kennern wird es hoch gepriesen; wenn Ihr mir also einen guten Vorrat dieses Härters bringt, werde ich Euch reich belohnen.$B$BZul\'Farrak liegt nordwestlich von Gadgetzan. Viel Glück.",
    ["O"] = "Bringt 20 Phiolen Trollaushärter zu Trenton Lichthammer in Gadgetzan.",
    ["T"] = "Trollaushärter",
  },
  [3062] = {
    ["D"] = "Edana Hasskralle ist die Königin der Nordschwingen, die in den Ruinen im Westen zu Hause sind. Sie ist das vollkommen Böse; ich habe gehört, dass sie ein Herz aus Kristall hat. Sie ist bar jeglicher Emotionen außer dem Hass, den sie für alle anderen fühlt. Ihr müsst Euch Edana stellen und mir ihr dunkles Herz zurückbringen.$B$BIch habe von einem Horn gehört, das die Harpyien tragen - wenn es bei den Hasskrallensteinen, im nördlichen Teil der Ruinen, geblasen wird, wird die Königin beschworen.$B$BStellt Euch ihre Überraschung vor, wenn es nicht die ihren sind, die auf sie warten, sondern ihr Tod.",
    ["O"] = "Talo Dornhuf im Camp Mojache will das dunkle Herz von Edana Hasskralle.",
    ["T"] = "Dunkles Herz",
  },
  [3063] = {
    ["D"] = "Zu meiner Zeit war ich ein erfahrener Jäger. Es gab keine Kreatur, die ich fürchtete. Bis...$B$BIch war draußen im Wald, um nach einem heftigen Kampf meine Klinge zu schärfen, als ich in einen Hinterhalt geriet. Harpyien umzingelten mich, und ich schaffte es kaum, mich in Sicherheit zu bringen.$B$BSelbst heute noch kann ich das Echo ihrer Schreie vernehmen...$B$BDanach war ich nicht mehr derselbe. Aber der Hass kocht in meinem Inneren - ich will Rache, auch noch nach all diesen Jahren.$B$BDie Harpyien der Nordschwingen leben westlich von hier. $N, rächt mich.",
    ["O"] = "Talo Dornhuf möchte, dass Ihr 4 Harpyien der Nordschwingen, 4 Wildfedern der Nordschwingen, 4 Windruferinnen der Nordschwingen und 4 Töter der Nordschwingen erschlagt.",
    ["T"] = "Rache an den Nordschwingen",
  },
  [3064] = "_",
  [3065] = {
    ["D"] = "Ah, während Ihr fort wart, kam ein Pergament für Euch, $N.$B$BLest es, wenn Ihr Zeit habt. Wenn ich mich nicht irre, kam es vom Kriegerlehrer Frang. Er würde gern mit Euch sprechen, wenn Ihr bereit seid. Ihr findet ihn außerhalb des Höhlenbaus, wo er sich in den Schatten verbirgt.",
    ["O"] = "Lest die einfache Schrifttafel und sprecht mit Frang im Tal der Prüfungen.",
    ["T"] = "Einfache Schrifttafel",
  },
  [3082] = {
    ["D"] = "Ah, während Ihr fort wart, kam eine Schrifttafel für Euch, $N.$B$BLest sie, wenn Ihr Zeit habt. Wenn ich mich nicht irre, kam sie von der Jägerlehrerin Jen\'shan. Sie würde gern mit Euch sprechen, wenn Ihr bereit seid.",
    ["O"] = "Sprecht mit Jen\'shan im Tal der Prüfungen.",
    ["T"] = "Geätzte Schrifttafel",
  },
  [3083] = {
    ["D"] = "Ah, während Ihr fort wart, kam eine Schrifttafel für Euch, $N.$B$BLest sie, wenn Ihr Zeit habt. Wenn ich mich nicht irre, kam sie vom Schurkenlehrer Rwag. Er würde gern mit Euch sprechen, wenn Ihr bereit seid.",
    ["O"] = "Lest die verschlüsselte Schrifttafel und sprecht mit Rwag im Tal der Prüfungen.",
    ["T"] = "Verschlüsselte Schrifttafel",
  },
  [3084] = {
    ["D"] = "Ah, während Ihr fort wart, kam eine Schrifttafel für Euch, $N.$B$BLest sie, wenn Ihr Zeit habt. Wenn ich mich nicht irre, kam sie von der Schamanenlehrerin Shikrik. Sie würde gern mit Euch sprechen, wenn Ihr bereit seid.",
    ["O"] = "Sprecht mit Shikrik im Tal der Prüfungen.",
    ["T"] = "Mit Runen beschriftete Schrifttafel",
  },
  [3085] = {
    ["D"] = "Ah, während Ihr fort wart, kam eine Schrifttafel für Euch, $N.$B$BLest sie, wenn Ihr Zeit habt. Wenn ich mich nicht irre, kam sie vom Priesterlehrer Ken\'jai. Er würde gern mit Euch sprechen, wenn Ihr bereit seid.",
    ["O"] = "Lest die geheiligte Schrifttafel und sprecht mit Ken\'jai außerhalb des Höhlenbaus im Tal der Prüfungen.",
    ["T"] = "Geheiligte Schrifttafel",
  },
  [3086] = {
    ["D"] = "Ah, während Ihr fort wart, kam eine Schrifttafel für Euch, $N.$B$BLest sie, wenn Ihr Zeit habt. Wenn ich mich nicht irre, kam sie von der Magierlehrerin Mai\'ah. Sie würde gern mit Euch sprechen, wenn Ihr bereit seid.",
    ["O"] = "Lest die mit Glyphen versehene Schrifttafel und sprecht mit Mai\'ah beim Eingang zum Höhlenbau im Tal der Prüfungen.",
    ["T"] = "Glyphenschrifttafel",
  },
  [3087] = {
    ["D"] = "Ah, während Ihr fort wart, kam ein Pergament für Euch, $N.$B$BLest es, wenn Ihr Zeit habt. Wenn ich mich nicht irre, kam es von der Jägerlehrerin Karranisha. Sie würde gern mit Euch sprechen, wenn Ihr bereit seid.",
    ["O"] = "Lest das geätzte Pergament und sprecht mit Karranisha im Tal der Prüfungen.",
    ["T"] = "Geätztes Pergament",
  },
  [3088] = {
    ["D"] = "Ah, während Ihr fort wart, kam ein Pergament für Euch, $N.$B$BLest es, wenn Ihr Zeit habt. Wenn ich mich nicht irre, kam es vom Schurkenlehrer Rwag. Er würde gern mit Euch sprechen, wenn Ihr bereit seid.",
    ["O"] = "Lest das verschlüsselte Pergament und sprecht mit Rwag im Tal der Prüfungen.",
    ["T"] = "Verschlüsseltes Pergament",
  },
  [3089] = {
    ["D"] = "Ah, während Ihr fort wart, kam ein Pergament für Euch, $N.$B$BLest es, wenn Ihr Zeit habt. Wenn ich mich nicht irre, kam es von der Schamanenlehrerin Shikrik. Sie würde gern mit Euch sprechen, wenn Ihr bereit seid.",
    ["O"] = "Lest das mit Runen beschriebene Pergament und sprecht mit Shikrik im Tal der Prüfungen.",
    ["T"] = "Mit Runen beschriftetes Pergament",
  },
  [3090] = {
    ["D"] = "Ah, während Ihr fort wart, kam ein Pergament für Euch, $N.$B$BIch versuche normalerweise, jedweden Kontakt mit Hexenmeistern zu vermeiden, aber ich glaube, Ihr solltet es lesen, wenn Ihr Zeit habt. Wenn ich mich nicht irre, kam es von Eurem neuen Lehrer Nartok. Er würde gern mit Euch sprechen, wenn Ihr bereit seid.",
    ["O"] = "Lest das besudelte Pergament und sprecht mit Nartok im Tal der Prüfungen.",
    ["T"] = "Besudeltes Pergament",
  },
  [3091] = {
    ["D"] = "Lest die einfache Nachricht und sprecht mit Harutt Donnerhorn im Camp Narache.",
    ["O"] = "Lest die einfache Nachricht und sprecht mit Harutt Donnerhorn im Camp Narache.",
    ["T"] = "Einfache Notiz",
  },
  [3092] = {
    ["D"] = "Lest die geätzte Nachricht und sprecht mit Lanka Weitschuss in Camp Narache.",
    ["O"] = "Lest die geätzte Nachricht und sprecht mit Lanka Weitschuss in Camp Narache.",
    ["T"] = "Geätzte Notiz",
  },
  [3093] = {
    ["D"] = "Lest die mit Runen beschriftete Nachricht und sprecht mit Meela Morgenwandler im Camp Narache.",
    ["O"] = "Lest die mit Runen beschriftete Nachricht und sprecht mit Meela Morgenwandler im Camp Narache.",
    ["T"] = "Mit Runen beschriftete Notiz",
  },
  [3094] = {
    ["D"] = "Lest die tiefgrüne Notiz und sprecht mit Gart Nebelläufer im Camp Narache.",
    ["O"] = "Lest die tiefgrüne Notiz und sprecht mit Gart Nebelläufer im Camp Narache.",
    ["T"] = "Tiefgrüne Notiz",
  },
  [3095] = {
    ["D"] = "Ach ja, während Ihr unterwegs wart, um Euch um die hirnlose Geißel zu kümmern, kam diese Schriftrolle für Euch an. Ich würde meinen, dass es sich um etwas Wichtiges handelt, denn sie scheint das Siegel des Kriegerlehrers Dannal zu tragen. Ich würde mir die Zeit nehmen, sie zu lesen, bevor ich wieder losziehe.",
    ["O"] = "Lest die einfache Schriftrolle und sprecht mit Dannal Stern in Todesend.",
    ["T"] = "Einfache Rolle",
  },
  [3096] = {
    ["D"] = "Ach ja, während Ihr unterwegs wart, um Euch um die hirnlose Geißel zu kümmern, kam diese Schriftrolle für Euch an. Ich würde meinen, dass es sich um etwas Wichtiges handelt, denn sie scheint das Siegel des Schurkenlehrers David Trias zu tragen. Ich würde mir die Zeit nehmen, sie zu lesen, bevor ich wieder losziehe.",
    ["O"] = "Lest die verschlüsselte Schriftrolle und sprecht mit David Trias in Todesend.",
    ["T"] = "Verschlüsselte Rolle",
  },
  [3097] = {
    ["D"] = "Ach ja, während Ihr unterwegs wart, um Euch um die hirnlose Geißel zu kümmern, kam diese Schriftrolle für Euch an. Ich würde meinen, dass es sich um etwas Wichtiges handelt, denn sie scheint das Siegel des Priesterlehrers Duesten zu tragen. Ich würde mir die Zeit nehmen, sie zu lesen, bevor ich wieder losziehe.",
    ["O"] = "Lest die geheiligte Schriftrolle und sprecht mit dem dunklen Kleriker Duesten in der Kirche von Todesend.",
    ["T"] = "Geheiligte Rolle",
  },
  [3098] = {
    ["D"] = "Ach ja, während Ihr unterwegs wart, um Euch um die hirnlose Geißel zu kümmern, kam diese Schriftrolle für Euch an. Ich würde meinen, dass es sich um etwas Wichtiges handelt, denn sie scheint das Siegel der Magierlehrerin Isabella zu tragen. Ich würde mir die Zeit nehmen, sie zu lesen, bevor ich wieder losziehe.",
    ["O"] = "Lest die mit Glyphen versehene Schriftrolle und sprecht mit Isabella in der Kirche von Todesend.",
    ["T"] = "Glyphenrolle",
  },
  [3099] = {
    ["D"] = "Ach ja, während Ihr unterwegs wart, um Euch um die hirnlose Geißel zu kümmern, kam diese Schriftrolle für Euch an. Ich würde meinen, dass es sich um etwas Wichtiges handelt, denn sie scheint das Siegel des Hexenmeisterlehrers Maximillion zu tragen. Ich würde mir die Zeit nehmen, sie zu lesen, bevor ich wieder losziehe.",
    ["O"] = "Lest die besudelte Schriftrolle und sprecht mit Maximillion in Todesend.",
    ["T"] = "Besudelte Rolle",
  },
  [3100] = {
    ["D"] = "Ich wurde darum gebeten, Euch hierauf aufmerksam zu machen, sobald Ihr mit den Schwarzfelsspionen fertiggeworden seid, $N. Es scheint sich um einen versiegelten Brief mit den Insignien von Llane, unserem örtlichen Kriegerlehrer, zu handeln. Ich würde nicht zögern, ihn zu lesen, bevor Ihr irgendwelchen anderen Geschäften hier in der Abtei nachgeht.",
    ["O"] = "Lest den einfachen Brief und sprecht mit Llane Beshere in der Abtei von Nordhain.",
    ["T"] = "Einfacher Brief",
  },
  [3101] = {
    ["D"] = "Ich wurde darum gebeten, Euch hierauf aufmerksam zu machen, sobald Ihr mit den Schwarzfelsspionen fertiggeworden seid, $N. Es scheint sich um einen versiegelten Brief mit den Insignien von Bruder Sammuel, unserem örtlichen Paladinlehrer, zu handeln. Ich würde nicht zögern, ihn zu lesen, bevor Ihr irgendwelchen anderen Geschäften hier in der Abtei nachgeht.",
    ["O"] = "Lest den geweihten Brief und sprecht mit Bruder Sammuel in der Abtei von Nordhain.",
    ["T"] = "Geweihter Brief",
  },
  [3102] = {
    ["D"] = "Ich wurde darum gebeten, Euch hierauf aufmerksam zu machen, sobald Ihr mit den Schwarzfelsspionen fertiggeworden seid, $N. Es scheint sich um einen versiegelten Brief mit den Insignien von Jorik, einem unserer örtlichen Ausbilder, zu handeln. Ich würde nicht zögern, ihn zu lesen, bevor Ihr irgendwelchen anderen Geschäften hier in der Abtei nachgeht.",
    ["O"] = "Lest den verschlüsselten Brief und sprecht mit Jorik Kerridan im Stall hinter der Abtei von Nordhain.",
    ["T"] = "Verschlüsselter Brief",
