  [3661] = {
    ["D"] = "Wildekin sind unberechenbare Kreaturen, $N. Sie sind zu einer außerordentlichen Zärtlichkeit fähig, aber diese Kreaturen können auch eine enorme Wildheit an den Tag legen, wenn etwas für sie sehr Wichtiges bedroht wird.$B$BDiese Kreaturen wurden angeblich von Elune geschaffen und ich bin daran interessiert herauszufinden, ob dies wirklich der Wahrheit entspricht.$B$BVor Kurzem habe ich etwas von einer Population von Wildekin gehört, die im Hinterland leben - heimtückische, primitive und wilde Eulenbestien. Man findet sie unter den wild lebenden Tieren dort. Würdet Ihr einige Wildekinfedern für mich sammeln?",
    ["O"] = "Sammelt 15 Wildekinfedern aus dem Hinterland für Erelas Himmelsbern in Rut\'theran.",
    ["T"] = "Elunes Lieblinge?",
  },
  [3681] = {
    ["D"] = "Verzeiht, dass wir Eure Reise unterbrechen, $R, aber ich dachte, Ihr solltet wissen, dass uns vor Kurzem eine Meldung aus Sturmwind erreicht hat, dass sich Paladine zum Training dort melden sollen.$B$BWenn ich es nicht besser wüsste, würde ich sagen, dass bald etwas Besonderes geschehen wird und es für Euch an der Zeit ist, die Folianten zu studieren.$B$BSucht Duthorian Rall in Sturmwind auf. Er trainiert für gewöhnlich Paladine in der Kathedrale des Lichts.",
    ["O"] = "Sprecht mit Duthorian Rall in Sturmwind.",
    ["T"] = "Foliant der Offenbarung",
  },
  [3701] = {
    ["D"] = "Begebt Euch zu den glimmenden Ruinen von Thaurissan in der Brennenden Steppe und sammelt so viele Informationen wie möglich.$B$BEs heißt, dass geheimnisvolle Relikte in den Ruinen verstreut seien. Vielleicht solltet Ihr Eure Suche dort beginnen, $N. Nehmt Euch vor den thaurissanischen Wächtern in Acht: Es sind Auftragsmörder, die die Ruinen bewachen und die dort möglicherweise noch immer begrabenen Geheimnisse beschützen, wie auch immer sie aussehen mögen.",
    ["O"] = "Begebt Euch zu den Ruinen von Thaurissan in der Brennenden Steppe und sammelt Informationen über die Relikte von Thaurissan. Kehrt zur königlichen Historikerin Archesonus zurück, wenn Ihr die Informationen besorgt habt.",
    ["T"] = "Die glimmenden Ruinen von Thaurissan",
  },
  [3761] = {
    ["D"] = "Die Erde des Kraters von Un\'Goro ist angeblich reich an starken magischen Eigenschaften. Der Zirkel des Cenarius möchte diese Erde in großen Mengen untersuchen; zieht daher in die Wildnis von Un\'Goro und sammelt genug davon für unsere ständig laufenden Forschungen.$B$BDie angereicherte Erde ist manchmal zu Haufen aufgeworfen und einige der Wildtiere könnten vielleicht nützliche Proben bei sich haben. Bringt sie raus zu Ghede; sucht einfach nach den Erdhaufen und dem Tauren, der Befehle brüllt. Er ist kein schlechter Kerl, aber verärgert ihn lieber nicht!",
    ["O"] = "Bringt 20 Erdproben von Un\'Goro zu Ghede in Donnerfels.",
    ["T"] = "Erde von Un\'Goro",
  },
  [3762] = {
    ["D"] = "Entschuldigung, $C, könnt Ihr einen Moment Eurer Zeit für mich erübrigen?$B$BDer Zirkel des Cenarius in Donnerfels ist sehr an erfahrenen Abenteurern wie Euch interessiert, die bei einem wichtigen Forschungsprojekt helfen können. Ich weiß zwar nichts Genaues, kann Euch aber sagen, dass kein Geringerer als Hamuul Runentotem an der Spitze dieses Projektes steht.$B$BBitte... wenn Ihr interessiert seid, sprecht direkt mit ihm auf der Anhöhe der Ältesten in Donnerfels.",
    ["O"] = "Sprecht in Donnerfels mit Erzdruide Hamuul Runentotem.",
    ["T"] = "Erzdruide Runentotem helfen",
  },
  [3763] = {
    ["D"] = "Entschuldigung, $C, könnt Ihr einen Moment Eurer Zeit für mich erübrigen?$B$BDer Zirkel des Cenarius in Darnassus sucht nach erfahrenen Abenteurern wie Euch, die bei einem wichtigen Forschungsprojekt helfen können. Ich weiß zwar nichts Genaues, kann Euch aber sagen, dass kein geringerer als Erzdruide Fandral Hirschhaupt an der Spitze dieses Projektes steht.$B$BBitte... wenn Ihr interessiert seid, sprecht direkt mit ihm in der Enklave des Cenarius in Darnassus.",
    ["O"] = "Sprecht mit Erzdruide Fandral Hirschhaupt in Darnassus.",
    ["T"] = "Erzdruide Hirschhaupt helfen",
  },
  [3764] = {
    ["D"] = "Die Erde des Kraters von Un\'Goro ist angeblich reich an starken magischen Eigenschaften. Ich möchte Wahrheit von Unwahrheit unterscheiden; begebt Euch daher in die Wildnis von Un\'Goro und sammelt ausreichend hochwertige Erdproben für unsere ständig laufenden Studien.$B$BIhr glaubt möglicherweise, dass die Aufgabe, etwas zu sammeln, unter Eurer Würde sei; doch mit solch einer Einstellung macht Ihr Euch weder bei mir noch beim Zirkel des Cenarius beliebt. Bringt die Proben zu Jenal, der zweifellos draußen hinter diesem Baum müßig herumlungert; sucht einfach nach den Erdhaufen. Er wird sich um die Logistik kümmern, nicht ich.",
    ["O"] = "Bringt 20 Erdproben von Un\'Goro zu Jenal in Darnassus.",
    ["T"] = "Erde von Un\'Goro",
  },
  [3765] = {
    ["D"] = "Sturmwind - geschäftiger Dreh- und Angelpunkt der Aktivität.$B$BIch bin weit gereist, um Hilfe zu suchen. Man hört häufig, dass mutige Abenteurer durch die Straßen dieser Stadt ziehen.$B$BMein Bruder Gershala benötigt Hilfe in Dunkelküste. Er lebt in der Küstenstadt Auberdine.$B$BDie Reise ist lang und abenteuerlich. Daher suche ich nach Hilfe. Es sollte $Gein erfahrener:eine erfahrene:c; $C sein, so wie Ihr.",
    ["O"] = "Reist zu Gershala Nachtraunen in Auberdine.",
    ["T"] = "Verderbnis in der Fremde",
  },
  [3781] = {
    ["D"] = "Mit dem, was wir einen Immergrünbeutel nennen, haben wir Tharlendrissamenkörner in der Erde des Kraters von Un\'Goro kultiviert. Diese Samenkörner sind zu einer Ansammlung wirksamer Kräuter erblüht. Ein Ergebnis ist das Morgenkorn, ein rätselhaftes Kraut, von dem wir wenig wissen; ich habe vor, dieses Geheimnis zu enthüllen.$B$BBringt das nach unten zu Mathrengyl, und er wird Euch einige Samenkörner geben, mit denen Ihr beginnen könnt. Wenn Ihr diese verbraucht habt, müsst Ihr weitere von ihm kaufen.",
    ["O"] = "Bringt den Samenkorngutschein des Erzdruiden zu Mathrengyl Bärenfährte in der Enklave des Cenarius von Darnassus.",
    ["T"] = "Morgenkornforschung",
  },
  [3782] = {
    ["D"] = "Mit dem, was wir einen Immergrünbeutel nennen, haben wir Tharlendrissamenkörner in der Erde des Kraters von Un\'Goro kultiviert. Diese Samenkörner sind zu einer Ansammlung wirksamer Kräuter erblüht. Ein Ergebnis ist das Morgenkorn, ein rätselhaftes Kraut, von dem wir wenig wissen; ich hoffe, dieses Geheimnis für den Zirkel zu enthüllen.$B$BBringt diesen Gutschein zu meiner Tochter Bashana, und sie wird Euch einige Samenkörner geben, damit Ihr uns helfen könnt. Wenn Ihr diese verbraucht habt, müsst Ihr weitere von ihr kaufen.",
    ["O"] = "Bringt den Samenkorngutschein zu Bashana Runentotem in Donnerfels.",
    ["T"] = "Morgenkornforschung",
  },
  [3783] = {
    ["D"] = "Meine Freunde hatten immerzu tolle Ideen. Ich hatte nie das Gefühl mit ihnen mithalten zu können... bis heute! Gerade habe ich die Pläne für etwas Großes angefertigt! Wenn Ihr versprecht, es nicht zu erzählen, weihe ich Euch in das Geheimnis ein... $B$BIch werde einen mechanischen Yeti herstellen! Meine Freunde werden ganz bestimmt durchdrehen, wenn sie ihn sehen, aber wenn sie darüber hinweg sind, bin ich mir sicher, dass sie beeindruckt sein werden!$B$BFür den Anfang benötige ich dickes Yetifell von Eisdistelyetis. Werdet Ihr helfen? Ihr könnt sie im Südosten finden.",
    ["O"] = "Sammelt 10 dicke Yetifelle für Umi Rumpelkicher in der Ewigen Warte.",
    ["T"] = "Sind wir endlich da, Yeti?",
  },
  [3784] = {
    ["D"] = "Entschuldigung, $C, könnt Ihr einen Moment Eurer Zeit für mich erübrigen?$B$BDer Zirkel des Cenarius in Donnerfels ist sehr an erfahrenen Abenteurern wie Euch interessiert, die bei einem wichtigen Forschungsprojekt helfen können. Ich weiß zwar nichts Genaues, kann Euch aber sagen, dass kein Geringerer als Hamuul Runentotem an der Spitze dieses Projektes steht.$B$BBitte... wenn Ihr interessiert seid, sprecht direkt mit ihm auf der Anhöhe der Ältesten in Donnerfels.",
    ["O"] = "Sprecht in Donnerfels mit Erzdruide Hamuul Runentotem.",
    ["T"] = "Erzdruide Runentotem helfen",
  },
  [3785] = {
    ["D"] = "Hier ist Euer Immergrünbeutel, den Ihr für die Nutzung der Tharlendrissamenkörner benötigen werdet. Ihr benötigt außerdem noch Erdproben von Un\'Goro als Dünger für die Samenkörner. Verwendet einfach den Beutel, wenn Ihr alles habt; der Beutel wird den Rest erledigen.$B$BWenn Ihr genug Einheiten Morgenkorn habt, bringt sie mir hierher in die Enklave. Und belästigt bitte nicht den Erzdruiden; er ist ein sehr beschäftigter Mann und muss nicht jedes Mal gestört werden, wenn jemand mit einer Ladung Morgenkorn zurückkehrt!",
    ["O"] = "Benutzt einen Immergrünbeutel mit einem Paket Tharlendrissamenkörner sowie zwei Erdproben von Un\'Goro, um Morgenkorn zu kultivieren.$B$BBringt 10 Einheiten Morgenkorn zu Mathrengyl Bärenfährte in Darnassus.",
    ["T"] = "Morgenkornforschung",
  },
  [3786] = {
    ["D"] = "Hier ist Euer Immergrünbeutel, den Ihr für die Nutzung der Tharlendrissamenkörner benötigen werdet. Ihr benötigt außerdem noch Erdproben von Un\'Goro als Dünger für die Samenkörner. Verwendet einfach den Beutel, wenn Ihr alles habt; der Beutel wird den Rest erledigen.$B$BWenn Ihr genug Einheiten Morgenkorn habt, bringt sie zu mir hierher auf die Anhöhe der Ältesten. Belästigt bitte nicht den Erzdruiden; er wird sehr mit den Forschungsarbeiten beschäftigt sein sowie mit den tausend anderen Sorgen, die er hat!",
    ["O"] = "Benutzt einen Immergrünbeutel mit einem Paket Tharlendrissamenkörner sowie zwei Erdproben von Un\'Goro, um Morgenkorn zu kultivieren.$B$BBringt 10 Einheiten Morgenkorn zu Bashana Runentotem in Donnerfels.",
    ["T"] = "Morgenkornforschung",
  },
  [3787] = {
    ["D"] = "Entschuldigung...$B$BEin Kollege von mir würde gern mit Euch über die Forschungsarbeiten sprechen, die Ihr zusammen mit dem Zirkel des Cenarius durchführt. Er ist ein Druide namens Quintis Lebenshand und lebt in der Mondfederfeste in Feralas. Er würde gern mit Euch über Morgenkorn reden... und er würde es vorziehen, wenn unser Gespräch dem Erzdruiden nicht zu Ohren kommt.$B$BFalls Ihr interessiert seid, dann sprecht mit ihm; zweifellos wird alles klargelegt, wenn Ihr es tut.",
    ["O"] = "Sprecht mit Quintis Lebenshand in der Mondfederfeste.",
    ["T"] = "Lebenshands Bitte",
  },
  [3788] = {
    ["D"] = "Entschuldigung...$B$BEin Kollege von mir würde gern mit Euch über die Forschungsarbeiten sprechen, die Ihr zusammen mit dem Zirkel des Cenarius durchführt. Er ist ein Druide namens Quintis Lebenshand und lebt hier in der Mondfederfeste. Er würde gern mit Euch über Morgenkorn reden... und er würde es vorziehen, wenn unser Gespräch dem Erzdruiden nicht zu Ohren kommt.$B$BFalls Ihr interessiert seid, dann sprecht mit ihm; zweifellos wird alles klargelegt, wenn Ihr es tut.",
    ["O"] = "Sprecht mit Quintis Lebenshand in der Mondfederfeste.",
    ["T"] = "Lebenshands Bitte",
  },
  [3789] = {
    ["D"] = "Entschuldigung, $C, könnt Ihr einen Moment Eurer Zeit für mich erübrigen?$B$BDer Zirkel des Cenarius in Darnassus sucht nach erfahrenen Abenteurern wie Euch, die bei einem wichtigen Forschungsprojekt helfen können. Ich weiß zwar nichts Genaues, kann Euch aber sagen, dass kein geringerer als Erzdruide Fandral Hirschhaupt an der Spitze dieses Projektes steht.$B$BBitte... wenn Ihr interessiert seid, sprecht direkt mit ihm in der Enklave des Cenarius in Darnassus.",
    ["O"] = "Sprecht mit Erzdruide Fandral Hirschhaupt in Darnassus.",
    ["T"] = "Erzdruide Hirschhaupt helfen",
  },
  [3790] = {
    ["D"] = "Entschuldigung, $C, könnt Ihr einen Moment Eurer Zeit für mich erübrigen?$B$BDer Zirkel des Cenarius in Darnassus sucht nach erfahrenen Abenteurern wie Euch, die bei einem wichtigen Forschungsprojekt helfen können. Ich weiß zwar nichts Genaues, kann Euch aber sagen, dass kein geringerer als Erzdruide Fandral Hirschhaupt an der Spitze dieses Projektes steht.$B$BBitte... wenn Ihr interessiert seid, sprecht direkt mit ihm in der Enklave des Cenarius in Darnassus.",
    ["O"] = "Sprecht mit Erzdruide Fandral Hirschhaupt in Darnassus.",
    ["T"] = "Erzdruide Hirschhaupt helfen",
  },
  [3791] = {
    ["D"] = "Lasst mich offen mit Euch sein, $N. Ich bin interessiert zu wissen - nein, ich muss es unbedingt wissen, warum der Erzdruide in Darnassus den Namen des Zirkels des Cenarius sowie dessen Mittel benutzt, um große Mengen Morgenkorn zu beschaffen. Ich bin ein Forscher - Flüche sind meine Spezialität. Ich habe gehört, dass Morgenkorn unter den richtigen Bedingungen Fähigkeiten verströmt wie andere Kräuter, die in primitiven Flüchen eingesetzt werden.$B$BIch möchte mehr darüber wissen und bitte Euch um Eure Hilfe. Bringt mir 10 Einheiten Morgenkorn und ich werde mich um Euch kümmern.",
    ["O"] = "Bringt 10 Einheiten Morgenkorn zu Quintis Lebenshand in der Mondfederfeste.",
    ["T"] = "Das Geheimnis des Morgenkorns",
  },
  [3792] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Morgenkorn für die Mondfederfeste",
  },
  [3801] = {
    ["D"] = "Seid gegrüßt, $N. Ich bin Franclorn Schmiedevater. Ja, der Franclorn Schmiedevater, der für die Steinwerkmethode der Architektur verantwortlich ist. Ihr habt zweifellos während Eurer Abenteuer in unserer vom Krieg zerrissenen Welt eines meiner Werke durchquert oder habt auf einem gestanden.$B$BIhr fragt Euch wahrscheinlich, warum ich hier bin und was ich von Euch möchte - beides berechtigte Fragen.$B$BIch brauche natürlich Eure Hilfe. Im Austausch für Eure Hilfe gewähre ich Euch den Schlüssel zu den Tiefen meiner verfluchten Stadt.",
    ["O"] = "Sprecht mit Franclorn Schmiedevater bei Schmiedevaters Grabmal im Schwarzfels, wenn Ihr daran interessiert seid, einen Schlüssel für die Hauptstadt zu erhalten.",
    ["T"] = "Dunkeleisenerbe",
  },
  [3802] = {
    ["D"] = "Ihr findet Fineous Dunkelader jenseits des Rings des Gesetzes in der Halle des Handwerks. Tötet den elenden Wicht und nehmt Eisenhölle an Euch.$B$BBringt Eisenhölle zum Schrein von Thaurissan und legt den Hammer an seinen rechtmäßigen Ort: in die Hände der Statue, die mir zu Ehren aufgestellt wurde.$B$BSobald dieses getan ist, öffnet sich das Fach, in dem ich den Hauptschlüssel verborgen habe. Wichtiger jedoch ist: Eisenhölle wird für alle Zeiten in meinen Händen bleiben. Sollte jemand versuchen, den Hammer zu entfernen, werden Statue und Hammer bersten und für immer verloren sein.",
    ["O"] = "Erschlagt Fineous Dunkelader und bergt den großen Hammer Eisenhölle. Bringt Eisenhölle zum Schrein von Thaurissan und legt ihn auf die Statue von Franclorn Schmiedevater.",
    ["T"] = "Dunkeleisenerbe",
  },
  [3804] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Morgenkorn nach Donnerfels",
  },
  [3821] = {
    ["D"] = "Sha\'ni wurde ausgeschickt, um Berichte über merkwürdige rituelle Aktivitäten zu untersuchen, die vor mehr als drei Wochen von den Feuermagenogern der Brennenden Steppe kamen.$B$BBluthauer sagt mir, dass er noch nichts wieder von dieser Gruppe gehört hätte und dass es am besten sei, wenn ich einfach weitermache und mich auf meine Aufgabe konzentriere. Könnt Ihr Euch das vorstellen? Das ist doch meine Frau!$B$BIch kann meinen Posten nicht verlassen, $N, aber ich muss wissen, was aus Sha\'ni geworden ist. Reist in die Brennende Steppe und sucht nach ihr.",
    ["O"] = "Begebt Euch in die Brennende Steppe und findet heraus, was aus Sha\'ni Ehrenhauer geworden ist.",
    ["T"] = "Schreckensfels",
  },
  [3822] = {
    ["D"] = "Meine ganze Gruppe wurde gefangen genommen, $N. Alle wurden getötet - alle, außer mir.$B$BIhr Anführer Krom\'Grul wollte mich für irgendein Ritual aufsparen.$B$BIch wurde von dem Biest an diesen Altar gekettet und brutal misshandelt. Mein körperliches Sein gab nach einer Stunde dieser Folter auf. Die Bestie riss mir dann meinen Ehenasenring aus dem Gesicht und steckte ihn an seinen Finger. Noch niemals habe ich solch eine Rohheit gesehen!$B$BFindet Krom\'Grul und erledigt ihn! Bringt meinen Ring zurück zu Thal\'trak.$B$BUnd, $N, sagt ihm, dass wir uns wiedersehen werden...in einem anderen Leben.",
    ["O"] = "Holt Sha\'nis Nasenring von Krom\'Grul zurück und bringt ihn zu Thal\'trak in Kargath. Durchsucht die verschiedenen Höhlen und Minen im Schreckensfels.",
    ["T"] = "Krom\'Grul",
  },
  [3823] = {
    ["D"] = "Wir müssen unsere Feinde schlagen, bevor sie uns schlagen!$B$BIch werde es kurz machen, Soldat.$B$BDie Feuermagenoger der Brennenden Steppe sind Verbündete der Orcs des Schwarzfelsklans. Sie arbeiten eng mit diesen üblen Orcs zusammen, bewachen den mineralreichen Schreckensfels und halten die Nachschublinien ständig offen.$B$BHinterlasst Euer Zeichen, $N. Legt ihre Streitkräfte lahm und kehrt zu mir zurück, wenn der Auftrag erledigt ist.$B$B<Oralius greift in die Luft und macht eine erwürgende Bewegung.>$B$BICH BRING DICH UM, WINKY!",
    ["O"] = "Tötet 15 Ogermagier der Feuermägen, 7 Oger der Feuermägen sowie 7 Schläger der Feuermägen.",
    ["T"] = "Die Feuermagenoger auslöschen",
  },
  [3824] = {
    ["D"] = "Sie sind gelähmt, ich sag\'s Euch! Unser nächster Schlag muss entscheidend sein.$B$BDer Lord dieser Bestien, Gor\'tesh, muss eliminiert werden. Wichtiger noch, sein Kopf muss zu mir zurückgebracht werden, damit ich die dritte Phase unseres Angriffs auf die Feuermagenoger vorbereiten kann.$B$BSeid gewarnt, Soldat, Gor\'tesh regiert nicht vom Schreckensfelsen aus. Stattdessen befindet er sich in einem Gebäude der Schwarzfelsorcs westlich vom Schreckensfelsen.$B$BEure Befehle sind daher einfach. Sucht Gor\'tesh und hackt ihm seinen fetten Kopf ab. Kommt zurück, wenn Eure Aufgabe erledigt ist.$B$BKLAPPE HALTEN, WINKY!",
    ["O"] = "Beschafft Euch Gor\'teshs abgehackten Kopf.",
    ["T"] = "Gor\'tesh der Schläger-Lord",
  },
  [3841] = {
    ["D"] = "Ach, hallo, $N. Ich freue mich sehr, Euch wiederzusehen. Wie war Eure Reise nach Darnassus?$B$BNa, Ihr seid bestimmt froh darüber, dass wir während Eurer Abwesenheit ziemliche Fortschritte gemacht haben. Wir haben sogar ein ganzes Nest voller Eier gerettet. Leider ist die Mutter gestorben, ehe wir sie gefunden haben.$B$BSagt mal, wollt Ihr nicht vielleicht so ein Tierchen haben? Es sieht aus, als würden sie bald schlüpfen, und dann brauchen sie $Geinen guten Vater:eine gute Mutter;. Was meint Ihr?$B$BWenn Ihr wirklich Interesse habt, dann bringt eines der Eier zu Quentin. Ihr findet ihn an der Rennbahn in der Schimmernden Ebene. Er kann Euch mehr darüber sagen.",
    ["O"] = "Sprecht mit Quentin in Tausend Nadeln.",
    ["T"] = "Waise sucht Zuhause",
  },
  [3842] = {
    ["D"] = "Die Mondwebers, wie? Überaus interessant. Schade, dass die Grimmlingflitzer nicht schätzen können, was es bedeutet, wenn diese beiden sich um sie kümmern. Beide sind überaus fähig und gut.$B$BSoso, sie haben Euch das Ei also geschenkt. Tja, dann solltet Ihr auch so lange dafür verantwortlich sein, wie Ihr dies wünscht. Das Junge scheint wohl schon bald zu schlüpfen, aber wir sollten trotzdem sichergehen, dass es gesund ist und keinen Schaden genommen hat.$B$BIch brauche lediglich zwei Elixiere der Seelenstärke für einen kleinen Zauber, der das Ei beschützen wird. Die übrigen Komponenten habe ich bereits hier.",
    ["O"] = "Bringt 2 Elixiere der Seelenstärke zu Quentin in Tausend Nadeln.",
    ["T"] = "Kurz vor dem Schlüpfen",
  },
  [3843] = {
    ["D"] = "Ein prachtvolles Exemplar, $N. Wenn dieses Geschöpf das Heranreifen übersteht, dann wird es ein wahrhaft gesundes Tier sein!$B$BAber eines würde ich Euch noch vorschlagen, wenn Ihr es damit wirklich ernst meint - geht zum Nistgipfel im Hinterland. Sprecht dort mit einem Zwerg namens Agnar Tiermeister. Er ist ein Tierführermeister und kann Euch bestimmt bei den letzten Stadien der Brutzeit behilflich sein.$B$BAber eilt Euch, $N, das Ei ist empfindlich und wird sich ohne die richtige Pflege nicht lange halten.",
    ["O"] = "Bringt Euer empfindliches Grimmlingflitzerei ins Hinterland und sprecht mit Agnar Tiermeister, aber beeilt Euch. Ihr habt nur eine Stunde Zeit, ihn zu finden. Dann stirbt das Jungtier in dem Ei.",
    ["T"] = "Das neueste Mitglied der Familie",
  },
  [3844] = {
    ["D"] = "In Ufernähe liegt ein zerstörtes Floß. Das hier ist nur ein kleiner Tümpel, deswegen ist Euch nicht so ganz klar, wie das Floß hier landen konnte. Aber ein Blick auf die Bäume verrät Euch, dass der Wasserstand im Teich nicht unbedingt immer derselbe sein muss.$B$BVielleicht findet Ihr ja mehr heraus, wenn Ihr Euch ein bisschen in der Gegend umschaut...",
    ["O"] = "Sucht in dem Tümpel nach einem Hinweis darauf, wem dieses havarierte Floß gehören könnte.",
    ["T"] = "Es ist für jeden ein Geheimnis",
  },
  [3845] = {
    ["D"] = "Ihr greift nach dem Bündel und zieht es an Land. Vielleicht findet Ihr heraus, wem es gehört, wenn Ihr die Gegenstände darin untersucht.$B$BIn Marschalls Zuflucht halten sich ein paar vereinzelte Reisende auf. Vielleicht lässt sich dort ja mehr herausfinden.",
    ["O"] = "Untersucht die Gegenstände in dem Bündel und bringt sie zu ihrem Besitzer.",
    ["T"] = "Es ist für jeden ein Geheimnis",
  },
  [3861] = {
    ["D"] = "Gack... gack... gack... GAGGACK!$B$BGack... gack... gack.$B$BDas Huhn sieht zu Euch hinauf und beginnt dann, mit seinen Krallen am Boden zu scharren. Ihr glaubt, dass es ein Wort schreibt, aber Ihr seid Euch nicht sicher, was es ist. Könnte es das sein... S-A-L-D-E-A-N? Bevor Ihr fragen könnt, dreht sich das Huhn weg und kümmert sich um seine Angelegenheiten.",
    ["O"] = "Beschafft Euch etwas Spezialhühnerfutter und kehrt zu dem Huhn zurück, das sich mit Euch angefreundet hat. Wenn Ihr es gefunden habt, dann benutzt erst /cheer, ehe Ihr dem Huhn das Spezialhühnerfutter gebt.",
    ["T"] = "GLUCK!",
  },
  [3881] = {
    ["D"] = "Ich weiß zwar nicht, wie Ihr es bis hierher geschafft habt, aber Ihr seid Balsam für meine Augen! Ich heiße Williden Marschall und bin der Leiter eines neutralen Expeditionstrupps. Unsere jüngste und größte Unternehmung wurde von zahllosen Katastrophen begleitet. Die schlimmste davon war, dass wir die Wildheit des Kraters von Un\'Goro unterschätzt hatten.$B$BAls wir aus unseren Stützpunkten im südwestlichen und nordöstlichen Teil des Kraters flohen, ließen wir lebenswichtige Vorräte zurück. Ihr seht aus, als wärt Ihr recht stark und geschickt... Könnt Ihr uns vielleicht dabei helfen, sie zurückzubekommen?",
    ["O"] = "Bringt eine Kiste mit Nahrungsmitteln und Forscherausrüstung zu Williden Marschall im Krater von Un\'Goro.",
    ["T"] = "Rettungsexpedition",
  },
  [3882] = {
    ["D"] = "Man sollte meinen, dass ich gern grabe, nur weil ich der Oberbuddler von Marschalls Expeditionen bin. Es könnte schlimmere Dinge in der Welt geben... auch wenn mir gerade keines einfällt.$B$BJedenfalls sollten wir eigentlich in der Terrorflucht - PHANTASTISCHER Name übrigens - nach toten Stegodonten und Dimetrodonten graben. Na ja, es treiben sich von der Sorte auch noch eine Menge lebende Viecher dort herum! Wil und Hol beschäftigen sich immer mit dieser Todesfalle, daher benötigen wir diese Dinosaurierknochen. Obwohl, unter uns gesagt, wenn Ihr mir welche von einem lebenden Exemplar bringt, dann ist das genauso gut.",
    ["O"] = "Bringt 8 Dinosaurierknochen zu Funks Nilminer im Krater von Un\'Goro.",
    ["T"] = "Knochenklappern",
  },
  [3883] = {
    ["D"] = "Nur weil wir einen Rückschlag erlitten haben, heißt das noch lange nicht, dass unsere Forschungen ein Ende finden müssen! Bevor wir hier gelandet sind, untersuchten wir die Schwarmbauten der Gorishiinsekten im südlichen Teil des Kraters. Sie sind fremdartig und passen nicht zur ursprünglichen Ökologie des Un\'Goro und wir wollen herausfinden, warum das so ist.$B$BIn ihrem Schwarmbauverbund muss es Larvenbrutstätten geben. Ihr könnt diesen Ort an der hängenden Raupenbrut erkennen. Hier, nehmt mit dieser Phiole eine Probe von der Schwarmbauwand in der Nähe der Brutstätten. Bringt sie dann zu mir zurück, damit ich sie untersuchen kann, aber seid vorsichtig!",
    ["O"] = "Verwendet die Kratzphiole, um damit eine Schwarmbauwandprobe von einer der Schwarmbrutstätten der Gorishi im Krater von Un\'Goro zu holen. Sucht nach den Kammern mit der hängenden Larvenbrut.$B$BBringt die Schwarmbauwandprobe zu Hol\'anyee Marschall im Krater von Un\'Goro.",
    ["T"] = "Eine fremdartige Ökologie",
  },
  [3884] = {
    ["D"] = "Ihr habt ein zerfleddertes Tagebuch zu Tage gefördert. Ein Teil davon ist noch lesbar; es scheint von einem gewissen Williden Marschall verfasst worden zu sein. Wenn das Tagebuch stimmt, dann muss Williden irgendwo im nördlichen Teil des Kraters zu finden sein. Dem Tagebuch zufolge haben er und seine Gefährten in den höheren Lagen des Kraters Schutz gesucht, nachdem ihre tiefer gelegenen Stützpunkte von den wilden Bestien überrannt wurden.",
    ["O"] = "Bringt das Tagebuch zu Williden Marschall zurück. Er befindet sich irgendwo im Krater von Un\'Goro.",
    ["T"] = "Willidens Tagebuch",
  },
  [3885] = "_",
  [3901] = {
    ["D"] = "Ihr habt den Verlassenen Euer Potenzial unter normalen Umständen gezeigt, $N. Jetzt wollen wir mal sehen, wie es damit bestellt ist, wenn Ihr unter Druck steht.$B$BDie klappernden Skelette, weitere hirnlose Landplagen, sind zähere Feinde als die Zombies, mit denen Ihr es bisher zu tun hattet. Auch hier heißt es wieder: Dünnt ihre Reihen aus und beweist den Verlassenen, dass Ihr der Aufgabe gewachsen seid. Frisch ans Werk...und sprecht wieder mit mir, wenn Ihr fertig seid.",
    ["O"] = "Tötet 8 klappernde Skelette.",
    ["T"] = "Klappernde Skelette",
  },
  [3902] = {
    ["D"] = "Ihr da! Wenn Ihr Euch irgendwie nützlich machen wollt, dann hört zu!$B$BWir benötigen frisch erweckte Rekruten, die sich nach Todesend begeben und dort nach allem suchen, was wir irgendwie gebrauchen können. Die Dinge werden sich höchstwahrscheinlich in gestapelten Kästen befinden. Wir rechnen damit, dass sich in Kürze weitere Rekruten erheben werden, und wenn wir nicht wollen, dass die nackt herumtappen, dann müssen wir wohl oder übel was für sie suchen.$B$BAlso, macht Euch an die Arbeit, elendes Knochengestell! Und ohne ein bisschen Eile gibt es auch keine Belohnung.",
    ["O"] = "Durchsucht Todesend und Umgebung nach 6 Stücken aufgestöberter Ware und bringt sie zu Todeswache Saltain zurück.",
    ["T"] = "Beutesuche in Todesend",
  },
  [3903] = {
    ["D"] = "Ihr habt Euch als verlässlich erwiesen, $N. Verlässlich und ohne Angst, Euch auch mal die Hände schmutzig zu machen, wie?$B$BTja, eine Freundin von mir, Milly Geltmark, hat ein paar Probleme. Sie ist mit ihrem Wagen auf der anderen Seite der Abtei, in der Nähe des Stalls. Ich bin mir sicher, sie könnte Eure Hilfe gut gebrauchen.",
    ["O"] = "Sprecht mit Milly Geltmark.",
    ["T"] = "Milly Osworth",
  },
  [3904] = {
    ["D"] = "Eine Bande von Briganten, die Defias, sind in das Weingut von Nordhain eingedrungen, als ich gerade bei der Weinlese war! Ich habe es den Wachen von Nordhain gemeldet und sie haben mir auch versprochen, dass sie sich darum kümmern würden, aber... Ich habe Angst um meine Traubenernte! Selbst wenn die Defias sie nicht stehlen, befürchte ich, dass unsere Wachen beim Vertreiben der Rohlinge alles zertrampeln.$B$BBitte, Ihr müsst mir helfen! Die meisten Trauben sind schon in Eimern gesammelt, aber ich habe sie bei den Weinstöcken im Südosten zurückgelassen.$B$BBringt mir diese Kübel! Rettet meine Ernte!",
    ["O"] = "Bringt 8 Kisten mit Millys Ernte zu Milly Geltmark in der Nähe der Abtei von Nordhain.",
    ["T"] = "Millys Ernte",
  },
  [3906] = {
    ["D"] = "Die Ältesten haben eine Störung der Elemente gespürt. Die Dunkeleisenzwerge haben einen üblen Geist in unsere Welt beschworen, durch den unser Land von einer ewigen Dürre heimgesucht werden könnte. Die Disharmonie der Flamme muss untersucht werden.$B$BBegebt Euch zum Schwarzfels und dann durch die Flammenschlucht in den Steinbruch. Dort werdet Ihr diesen Herrn des Feuers finden: Übermeister Pyron.$B$BMacht der Störung ein Ende und kehrt dann zu mir zurück.",
    ["O"] = "Reist zum Steinbruch im Schwarzfels und tötet Übermeister Pyron. Kehrt zu Donnerherz zurück, sobald Ihr den Auftrag erledigt habt.",
    ["T"] = "Disharmonie der Flamme",
  },
  [3907] = {
    ["D"] = "Betretet die Schwarzfelstiefen und spürt Lord Incendius auf. Tötet ihn und bringt jegliche Informationsquelle, die Ihr finden könnt, zu Donnerherz.",
    ["O"] = "Betretet die Schwarzfelstiefen und spürt Lord Incendius auf. Tötet ihn und bringt jegliche Informationsquelle, die Ihr finden könnt, zu Donnerherz.",
    ["T"] = "Disharmonie des Feuers",
  },
  [3908] = {
    ["D"] = "Seht, ich habe keine Ahnung, wie ich hierher kam. Niemand weiß das - niemand versteht es! Ich kann mich einfach an nichts erinnern...$B$BAber diese Gegenstände, die Ihr mir gebracht habt... Ich erinnere mich an etwas.$B$BMein Schwert. Ich soll es irgendwie stärken, aber ich weiß beim besten Willen nicht, warum. Es tut mir leid, dass ich Euch nicht mehr sagen kann, aber ich kann mich einfach nicht erinnern!$B$BNa, egal, ich weiß aber, wo Ihr es hinbringen könnt: zu Donova Schneeweh in Winterquell. Ihr findet sie bei den Thermalquellen. Sie kann das Schwert an den Teichen dort aushärten.",
    ["O"] = "Bringt Linkens Schwert zu Donova Schneeweh in Winterquell.",
    ["T"] = "Es ist für jeden ein Geheimnis",
  },
  [3909] = {
    ["D"] = "Was sagt Ihr da? Linken... HAT ES VERGESSEN?$B$BAh, verstehe... Hat sein Gedächtnis verloren...$B$BNa gut, hier ist sein Schwert. Es ist aber noch nicht fertig. Ich habe von jemandem gehört, der es fertig stellen kann, aber... tja, es könnte etwas schwierig werden, mit ihm zu reden.$B$BHört mal, ich weiß jemanden, der Euch bestimmt helfen kann. Ich kann Euch jetzt nicht alles erklären, das würde zu lange dauern, aber Ihr müsst Euch irgendwie ein Videre-Elixier beschaffen. Der Einzige, von dem ich weiß, dass er Euch dabei helfen kann, ist Gregan Hopfenspei. Er ist ziemlich schwer aufzuspüren, aber zuletzt hatte er ein Lager in der Wildnis von Feralas.",
    ["O"] = "Sucht nach Gregan Hopfenspei im nördlichen Feralas. Lasst Euch von ihm erklären, wie Ihr Euch das Videre-Elixier beschaffen könnt, und kehrt dann zu Donova Schneeweh nach Winterquell zurück.",
    ["T"] = "Das Videre-Elixier",
  },
  [3911] = {
    ["D"] = "Es gibt Arbeit für diejenigen, die sich in die Tiefen wagen, $N.$B$BDen Dunkeleisenzwergen ist es gelungen, extrem mächtige Golems zu erschaffen.$B$BErsten Berichten unserer Spione zufolge verwenden die Zwerge eine einzigartige Kraftquelle, die ihren Schöpfungen eine unvergleichliche Kraft zum Töten gibt.$B$BStellt Euch nur einmal vor, was wir mit unseren Monstrositäten tun könnten, wenn wir diese Essenz der Elemente in die Hände bekommen könnten! Stellt die ganze Stadt auf den Kopf, wenn es sein muss, aber kommt nicht wieder, ehe Ihr nicht die Essenz gefunden habt! Die Bezahlung ist es wert, das Risiko einzugehen.",
    ["O"] = "Begebt Euch in die Schwarzfelstiefen und beschafft 10 Essenzen der Elemente. Euer erster Gedanke ist, die Golems und die Schöpfer der Golems zu suchen. Doch Ihr erinnert Euch, dass Vivian Lagrave auch etwas von Elementaren vor sich hingemurmelt hat.",
    ["T"] = "Das letzte Element",
  },
  [3913] = {
    ["D"] = "Wenn Ihr zum Friedhof zurückkehrt, $N, findet Ihr dort einen Grabstein... Nicht einfach nur irgendeinen Grabstein... Dieser hebt sich von all den anderen ab. Geheimnisse liegen darin verborgen.$B$BDrückt gegen den Stein, dann erfahrt Ihr von diesen Geheimnissen...",
    ["O"] = "Wenn Ihr auferstanden seid, sucht nach dem Grabstein, von dem Gaeriyan Euch erzählt hat.",
    ["T"] = "Grabesstille",
  },
  [3914] = {
    ["D"] = "Ein greller Lichtblitz blendet Euch, dann könnt Ihr das Schwert wieder aufheben. Es hat sich verwandelt!",
    ["O"] = "Liefert Linkens überragendes Schwert bei Linken im Krater von Un\'Goro ab.",
    ["T"] = "Linkens Schwert",
  },
  [3921] = {
    ["D"] = "Ich glaube, ich habe das Ding wirklich kaputt gemacht: sieht so aus, als hätte ich einen der tragenden Fluxoren in die Speichen des Deregulatorgetriebes gehämmert. Aber das können wir ganz einfach beheben. Ich kenne eine Expertin für 3-fach Expansionskolbenantriebe, die liebend gern ein Samophlang in die Finger bekommen würde. Bringt das zerbrochene Samophlang zu Wenikee Kübelbolz südlich des Schutzwalls von Mor\'shan und sie wird es schon zusammenflicken.$B$BHe, Ihr müsst noch nicht einmal zu Fuß gehen. Das ist die perfekte Gelegenheit, meinen persönlichen Personenporter zu testen!",
    ["O"] = "Bringt das zerbrochene und ramponierte Samophlang zu Wenikee Kübelbolz. Sprecht mit Brak, um direkt zu ihr teleportiert zu werden.",
    ["T"] = "Wenikee Kübelbolz",
  },
  [3922] = {
    ["D"] = "Ich möchte das Samophlang reparieren, aber dazu brauche ich ein paar Dinge. Ich habe keine 17,5er Schraubnieten mehr, und davon werde ich für meine Arbeit hier eine ganze Menge brauchen.$B$BDer nächstgelegene Ort, an dem man sie bekommen kann, ist die in der Nähe befindliche Forschungsstation und das Schlickmoor im Osten. Die Leute von der Venture Company bewahren sie normalerweise in ihren Werkzeugkisten auf. Schaut in den Werkzeugkisten an beiden Orten nach und kommt zu mir zurück, sobald Ihr genug Schraubnieten gesammelt habt.",
    ["O"] = "Sammelt 10 Schraubnieten aus den Werkzeugkisten rund ums Schlickmoor.",
    ["T"] = "Schraubnieten",
  },
  [3923] = {
    ["D"] = "Tja, das hat schon mal nicht funktioniert. Ich habe an dem Samophlang ein paar zusätzliche Messgeräte angebracht, um seine strukturelle Integrität zu messen, und laut der Anzeige war die Integrität am absoluten Tiefpunkt. Ich könnte natürlich noch ein paar Skalen und vielleicht auch den einen oder anderen Hebel anbringen, aber...ich glaube, das würde alles nur noch schlimmer machen.$B$BIhr solltet das Samophlang zu Rilli Schmierklump bringen. Er hat mal für die Venture Company gearbeitet. Wenn irgendjemand weiß, wie man das Ding reparieren kann, dann Rilli.$B$BIhr findet ihn bei \'Noggs Meistermaschinen\' in Orgrimmar, im Tal der Ehre.",
    ["O"] = "Bringt das zerbrochene Samophlang nach Orgrimmar zu Rilli Schmierklump.",
    ["T"] = "Rilli Schmierklump",
  },
  [3924] = {
    ["D"] = "Ich glaube, ich kann das Samophlang reparieren, aber dazu brauche ich erst einmal das Handbuch. Als ich noch für die Venture Company arbeitete, hatte ich einen Konkurrenten, Boss Kupferdraht. Der hat das Geheimnis des Samophlangs gehütet wie seinen Augapfel und immer nur kleine Fitzelchen an Informationen an diejenigen verteilt, denen er traute. Zu dieser kleinen, ausgewählten Gruppe habe ich leider nicht gehört...$B$BBoss Kupferdraht war früher immer in der Felsadermine im Nordosten des Brachlandes. Beschafft Euch das Handbuch von ihm, und falls er irgendjemandem einzelne Seiten davon gegeben hat, dann müsst Ihr diese ebenfalls einsammeln.",
    ["O"] = "Bringt das Samophlanghandbuch nach Orgrimmar zu Rilli Schmierklump.",
    ["T"] = "Samophlanghandbuch",
  },
  [3941] = {
    ["D"] = "Ich denke... Ich denke... Ich weiß, dass ich aus irgendeinem wichtigen Grund hergekommen bin...$B$BIch hab da eine Idee, $N. In der Höhle dort drüben wohnt eine Gnomin namens J.D. Sie war sehr freundlich zu mir, als ich hier ankam. Vielleicht habe ich in meiner Verwirrung ihr gegenüber irgendwas erwähnt.$B$BWarum fragt Ihr sie nicht, ob sie etwas weiß? Oh, aber seid vorsichtig: Sie spielt immer mit diesen verrückten, leuchtenden Kristallen herum, die ich im Krater gesehen habe. Ich habe keine Ahnung, was sie da drinnen treibt.",
    ["O"] = "Sucht J.D. Collie in der Höhle in Marschalls Zuflucht auf.",
    ["T"] = "Die Hilfe des Gnoms",
  },
  [3942] = {
    ["D"] = "Oh... Linken? Jaaa, der ist echt witzig. Als er hier ankam, murmelte er ständig irgendwelche seltsamen Dinge. Hat wahrscheinlich einen ziemlich heftigen Schlag auf den Kopf bekommen, würde ich mal sagen.$B$BAber etwas, das er gesagt hat, ist bei mir irgendwie hängen geblieben - dass er dabei war, eine Art Totem herzustellen; er sagte, er müsse mit jemandem namens Eridan Blauwind sprechen... an einem Ort namens Teufelswald, glaube ich.$B$BAber ich bezweifle, dass Linken Euch jetzt noch viel darüber sagen kann. Wenn Ihr mehr wissen wollt, solltet Ihr diesen Eridan vielleicht selbst suchen gehen...",
    ["O"] = "Sucht Eridan Blauwind im südlichen Teil des Teufelswalds.",
    ["T"] = "Linkens Andenken",
  },
  [3961] = {
    ["D"] = "Wenn man sich überlegt, dass Linken nur hergekommen ist, um uns von Sturmflamme zu befreien... Er scheint so gar nichts Außergewöhnliches an sich zu haben, aber mir ist aufgefallen, dass er sich überhaupt nicht für meine Experimente oder Geräte interessiert hat, wie das bei den meisten Gnomen sonst der Fall ist. Er muss im Herzen ein echter Krieger sein.$B$BBringt ihm das Totem. Vielleicht hilft ihm das, sich zu erinnern, warum er hergekommen ist.",
    ["O"] = "Bringt das Silbertotem von Aquementas zu Linken in Marschalls Zuflucht.",
    ["T"] = "Linkens Abenteuer",
  },
  [3962] = {
    ["D"] = "$N, ich fange endlich an, mich zu erinnern! Ich bin hergekommen, um gegen Sturmflamme zu kämpfen. Ich war auf einer Reise und das hätte der letzte Schritt sein sollen.$B$BAber ich bin kein Held mehr... sondern Ihr. Alles, was Ihr getan habt... Ihr müsst dies jetzt zu Ende bringen, nicht ich.$B$BFindet Sturmflamme und besiegt ihn. Er bewacht ein Artefakt, das die Goldene Flamme genannt wird. Es verleiht mehr Macht als alles, was ich je zuvor gesehen habe, und in den Händen von jemandem, dessen Herz böse ist, ist es äußerst gefährlich.$B$BIhr solltet aber unbedingt Verstärkung mitnehmen - es ist gefährlich, allein zu gehen.",
    ["O"] = "Begebt Euch zum Feuersäulengrat südlich von Marschalls Zuflucht.$B$BSucht Sturmflamme und verwendet das Totem, um die schützende Aura, die ihn umgibt, zu entfernen. Sobald er besiegt ist, holt die Goldene Flamme aus der Höhle.",
    ["T"] = "Allein ist es gefährlich",
  },
  [3981] = {
    ["D"] = "Sucht Kommandant Gor\'shak in den Schwarzfelstiefen.$B$BIhr erinnert Euch, dass auf dem primitiv gezeichneten Bild des Orcs auch Gitter vor dem Gesicht zu sehen waren. Vielleicht solltet Ihr nach einer Art Gefängnis suchen.",
    ["O"] = "Sucht Kommandant Gor\'shak in den Schwarzfelstiefen.$B$BIhr erinnert Euch, dass auf dem primitiv gezeichneten Bild des Orcs auch Gitter vor dem Gesicht zu sehen waren. Vielleicht solltet Ihr nach einer Art Gefängnis suchen.",
    ["T"] = "Kommandant Gor\'shak",
  },
  [3982] = {
    ["D"] = "Ich wurde unter der Schirmherrschaft von Thrall hierher geschickt. Glaubt Ihr wirklich, diese Clowns könnten mich fangen? Ich habe mich fangen lassen, um so Informationen sammeln zu können.$B$B<Gor\'shak flüstert.>$B$BDie Prinzessin von Eisenschmiede wurde von Imperator Thaurissan entführt. Prinzessin Bronzebart befindet sich in dieser Stadt! Ich wette, dem alten Magni ist fast das Herz stehen geblieben, als er diese Neuigkeiten gehört hat.$B$B<Gor\'shak lacht.>$B$BPssst! Hört Ihr das? Es kommen Wachen! VERTEIDIGT EUCH!",
    ["O"] = "Verteidigt Gor\'shak.",
    ["T"] = "Was ist los?",
  },
  [4001] = {
    ["D"] = "Sprecht mit Kharan Hammermacht und sammelt Informationen über die Entführung von Prinzessin Moira Bronzebart. Bringt diese Informationen dann zu Thrall nach Orgrimmar.$B$BIhr erinnert Euch, dass Gor\'shak gesagt hat, Kharan werde in einer Zelle in seiner Nähe festgehalten.",
    ["O"] = "Sprecht mit Kharan Hammermacht und sammelt Informationen über die Entführung von Prinzessin Moira Bronzebart. Bringt diese Informationen dann zu Thrall nach Orgrimmar.$B$BIhr erinnert Euch, dass Gor\'shak gesagt hat, Kharan werde in einer Zelle in seiner Nähe festgehalten.",
    ["T"] = "Was ist los?",
  },
  [4002] = {
    ["D"] = "Ihr bringt ganz ausgezeichnete Nachrichten, $N.$B$BAls Kriegshäuptling ist es meine Aufgabe, das zu tun, was das Beste für die Horde ist. Zu garantieren, dass die nachfolgenden Generationen nicht in einer hoffnungslosen Existenz gefangen sein werden.$B$BJetzt ist uns die einzigartige Chance, die Allianzen der Zukunft zu formen, in den Schoß gefallen. Wenn wir jetzt den Zwergen helfen, könnte dies unsere Beziehungen zu den Östlichen Königreichen enorm verbessern. Und aus genau diesem Grund dürft Ihr nicht versagen!$B$BSeid Ihr bereit?",
    ["O"] = "Sprecht mit Thrall, wenn Ihr bereit seid, die von ihm geplante Mission zu übernehmen.",
    ["T"] = "Die Östlichen Königreiche",
  },
  [4003] = {
    ["D"] = "Der Schlag gegen die Dunkeleisenzwerge wird, wenn er denn erfolgreich verläuft, eine Schockwelle durch ihr ganzes Königreich jagen.$B$BPrinzessin Bronzebart ist unter der Kontrolle von Imperator Thaurissan. Um sie zu befreien, müsst Ihr den Imperator töten. Doch seid gewarnt, denn Eure Aufgabe ist doppelt gefährlich. Zweifellos wird Euch Prinzessin Bronzebart angreifen, solange sie noch der Kontrolle des Imperators unterliegt.$B$BIHR DÜRFT IHR KEIN LEID ZUFÜGEN!",
    ["O"] = "Tötet Imperator Dagran Thaurissan und befreit Prinzessin Moira Bronzebart von seinem bösen Zauber.",
    ["T"] = "Die königliche Rettung",
  },
  [4004] = {
    ["D"] = "Kehrt zu Thrall in der Feste Grommash in Orgrimmar zurück.",
    ["O"] = "Kehrt zu Thrall in der Feste Grommash in Orgrimmar zurück.",
    ["T"] = "Ist die Prinzessin gerettet?",
  },
  [4005] = {
    ["D"] = "Nehmt diese Tasche mit Vorräten mit Euch nach Tanaris. Begebt Euch zur Mast- und Schotbucht und sucht dort einen Kreis aus Steinen.$B$BIn der östlichsten Halbinsel liegt das Geheimnis...$B$BLest aus dem Buch von Aquor vor - aber nur innerhalb dieses Kreises - und beschwört das einzige Wesen, das die Gegenstände, die Ihr gesammelt habt, in ein Totem verwandeln kann, mit dem Ihr Sturmflamme bekämpfen könnt.$B$BEs wird Aquementas nicht gefallen, dass Ihr ihn aufweckt, doch vergesst nicht: Ihr müsst unbedingt in dem Kreis bleiben, damit er die Gegenstände transformieren kann.",
    ["O"] = "Bringt das Buch von Aquor, die silbrigen Klauen und das Eisenstammherz nach Tanaris.$B$BBenutzt das Buch von Aquor, um Aquementas zu beschwören.$B$BWenn Ihr das vollständige Totem habt, bringt es zu J.D. Collie in Marschalls Zuflucht zurück.",
    ["T"] = "Aquementas",
  },
  [4021] = {
    ["D"] = "Die Horde lobt Euch für Eure Erfolge gegen die Kolkar, doch die Kolkar kochen vor Wut. Ich habe Berichte erhalten, denen zufolge ein Kontingent Kolkar ihr Zuhause in Desolace verlassen hat und ins Brachland gezogen ist, um dort Rache zu üben. Den letzten Berichten nach, rücken Sie in diesem Moment von Westen her gegen diese Bunker vor!$B$BStellt Euch den Eindringlingen der Kolkar und bereitet der Bedrohung, die sie für das Brachland darstellen, ein Ende. Tötet sie, bis ihr Anführer, Kriegsherr Krom\'zar, erscheint. Besiegt ihn und bringt mir ein Teil seiner Flagge als Beweis.$B$BWir zählen auf Euch, $N.",
    ["O"] = "Tötet Kolkarzentauren bis Krom\'zar auftaucht, dann tötet ihn und nehmt einen Teil von Krom\'zars Flagge an Euch.",
    ["T"] = "Gegenangriff!",
  },
  [4022] = {
    ["D"] = "Der Klan ist sich Eurer Arbeit in der Sengenden Schlucht bewusst.$B$BZeigt sie mir, $R. Die abgestreifte Drachenhaut, Dummkopf!",
    ["O"] = "Zeigt Cyrus Therepentous die abgestreifte Haut des schwarzen Drachenschwarms, die Ihr von Kalaran Windklinge erhalten habt.",
    ["T"] = "Eine Kostprobe der Flamme",
  },
  [4024] = {
    ["D"] = "Ich möchte die sengenden Flammen des Feuerberges schmecken. Die flüssigen Feuer des Schwarzfels über mich strömen lassen. Diese Essenz, diese sengende Hitze ist es, nach der ich mich sehne.$B$BIn den Tiefen des Schwarzfels haust Bael\'Gar: ein Wesen aus reinstem Feuer, geboren aus den Flammen des Ragnaros. Tötet Bael\'Gar und bringt mir aus seinen glimmenden Überresten einen Teil seines inneren Feuers.$B$BVerwendet die Drachenhaut für seine Überreste, um die innere Flamme sicher entnehmen zu können.$B$BSeid erfolgreich und Ihr werdet belohnt.",
    ["O"] = "Begebt Euch in die Schwarzfelstiefen und tötet Bael\'Gar.$B$BIhr wisst lediglich, dass der Riese irgendwo in den Schwarzfelstiefen haust. Vergesst nicht, die veränderte Haut des schwarzen Drachenschwarms mit Bael\'Gars Überresten zu benutzen und damit die feurige Essenz einzufangen.$B$BBringt die eingeschlossene feurige Essenz zu Cyrus Therepentous zurück.",
    ["T"] = "Eine Kostprobe der Flamme",
  },
  [4061] = {
    ["D"] = "Begebt Euch in die Brennende Steppe und beschafft für Theodora Mulvadania 10 gerissene Elementarsplitter.$B$BEuch fällt ein, dass Theodora gesagt hat, die Golems und Elementare in der Gegend seien eine gute Quelle für solche Splitter.",
    ["O"] = "Begebt Euch in die Brennende Steppe und beschafft für Theodora Mulvadania 10 gerissene Elementarsplitter.$B$BEuch fällt ein, dass Theodora gesagt hat, die Golems und Elementare in der Gegend seien eine gute Quelle für solche Splitter.",
    ["T"] = "Aufstieg der Maschinen",
  },
  [4063] = {
    ["D"] = "Das kann doch nicht Argelmach sein... Argelmach wurde vor zehn Jahren getötet. Warum ich das so genau weiß? Weil ich derjenige war, der ihn getötet hat.$B$BEr war ein abscheulicher Zwerg und wild entschlossen, mein Lebenswerk zu verderben und es für seine teuflischen Zwecke zu benutzen. Wie es scheint, finden die Tückischen immer einen Weg, ihrer gerechten Strafe zu entgehen.$B$BWenn dies Argelmachs Werk ist, dann muss er zerstört werden. Ich brauche Proben seiner jüngsten Schöpfungen. Mit den richtigen Proben kann ich vielleicht diesem Aufstand ein Ende machen. Die Schöpfungen bewachen Argelmach in den Schwarzfelstiefen.",
    ["O"] = "Sucht und tötet Golemlord Argelmach. Bringt Lotwil seinen Kopf. Außerdem müsst Ihr 10 intakte Elementarkerne von den Wuthäschergolems und Kriegshetzerkonstrukten, die Argelmach beschützen, beschaffen. Das wisst Ihr, weil Ihr übernatürliche Fähigkeiten habt.",
    ["T"] = "Aufstieg der Maschinen",
  },
  [4081] = {
    ["D"] = "Auf Befehl von Kriegsherr Bluthauer, Kommandant des Expeditionskorps von Kargath:$B$BAlle Streitkräfte, über die General Zornesschmied in den Schwarzfelstiefen verfügt, müssen ausgelöscht werden.$B$BZornesschmieds Männer sind rücksichtslose Killer und verantwortlich für den Massenmord an der 109. Division des Expeditionskorps von Kargath. Alle Helden, die diesen gefühllosen Bestien gegenübertreten wollen, werden aufgefordert, extreme Vorsicht walten zu lassen.$B$BJede Person, die die erste Kampfreihe von General Zornesschmieds Streitkräften in den Schwarzfelstiefen vernichtet, wird einen Tribut erhalten.",
    ["O"] = "Begebt Euch in die Schwarzfelstiefen und vernichtet die üblen Aggressoren!$B$BKriegsherr Bluthauer möchte, dass Ihr 15 Gardisten der Zorneshämmer, 10 Aufseher der Zorneshämmer und 5 Fußsoldaten der Zorneshämmer tötet. Kehrt zu ihm zurück, sobald Ihr die Aufgabe erfüllt habt.",
    ["T"] = "SOFORT TÖTEN: Dunkeleisenzwerge",
  },
  [4082] = {
    ["D"] = "Begebt Euch in die Schwarzfelstiefen und vernichtet die üblen Aggressoren!$B$BKriegsherr Bluthauer möchte, dass Ihr 10 Sanitäter der Zorneshämmer, 10 Soldaten der Zorneshämmer und 10 Offiziere der Zorneshämmer tötet. Kehrt zu ihm zurück, sobald Ihr die Aufgabe erfüllt habt.",
    ["O"] = "Begebt Euch in die Schwarzfelstiefen und vernichtet die üblen Aggressoren!$B$BKriegsherr Bluthauer möchte, dass Ihr 10 Sanitäter der Zorneshämmer, 10 Soldaten der Zorneshämmer und 10 Offiziere der Zorneshämmer tötet. Kehrt zu ihm zurück, sobald Ihr die Aufgabe erfüllt habt.",
    ["T"] = "SOFORT TÖTEN: Hochrangige Führungskräfte der Dunkeleisenzwerge",
  },
  [4084] = {
    ["D"] = "Ich kenne denjenigen nicht, von dem Ihr sprecht, $N.$B$BDoch keine Sorge, wenn Euer Freund auf der Suche nach mir war, dann weiß ich, was er von mir wollte.$B$BIm Herzen des Kraters von Un\'Goro lebt das Böse in seiner reinsten Form. Dort manifestiert es sich in der Gestalt eines Elementars namens Sturmflamme. Er wird von einer Aura beschützt, die niemand durchdringen kann.$B$BNiemand... außer denen, die über genügend Macht verfügen. Diese Macht kann ich Euch lehren, $N.$B$BDoch zunächst müsst Ihr mir eine Bitte erfüllen. Beschafft 11 silbrige Klauen und 1 Eisenstammherz von den Kreaturen im Teufelswald und kommt dann zu mir zurück.",
    ["O"] = "Beschafft 11 silbrige Klauen und 1 Eisenstammherz für Eridan Blauwind im Teufelswald.",
    ["T"] = "Silberherz",
  },
  [4101] = {
    ["D"] = "Die Pflanzen schenken dem Teufelswald Leben, doch sogar sie leiden unter der Besudelung der Verderbnis. Ich kenne Wege, um sie zu säubern, doch dieses Wissen kann ich an niemanden weitergeben, den ich nicht kenne oder dem ich nicht vertraue. Beweist mir, dass Ihr der Sache des Zirkels des Cenarius wirklich helfen wollt!$B$BNördlich von hier liegen die Eisenstammhöhlen. Für die Wucherborkenelementare, die sich dort aufhalten, gibt es keine Hoffnung mehr auf Säuberung. Ich bitte Euch, bringt mir ihren Saft, er wird Blutharz genannt, damit ich ihn studieren kann. Vielleicht gelingt es mir so, die anderen Lebensformen hier zu retten.",
    ["O"] = "Bringt 15 Einheiten Blutharz zu Arathandris Silberwolke im Teufelswald.",
    ["T"] = "Den Teufelswald säubern",
  },
  [4102] = {
    ["D"] = "Die Pflanzen schenken dem Teufelswald Leben, doch sogar sie leiden unter der Besudelung der Verderbnis. Ich kenne Wege, um sie zu säubern, doch dieses Wissen kann ich an niemanden weitergeben, den ich nicht kenne oder dem ich nicht vertraue. Beweist mir, dass Ihr der Sache des Zirkels des Cenarius wirklich helfen wollt!$B$BNördlich von hier liegen die Eisenstammhöhlen. Für die Wucherborkenelementare, die sich dort aufhalten, gibt es keine Hoffnung mehr auf Säuberung. Ich bitte Euch, bringt mir ihren Saft, er wird Blutharz genannt, damit ich ihn studieren kann. Vielleicht gelingt es mir so, die anderen Lebensformen hier zu retten.",
    ["O"] = "Bringt 15 Einheiten Blutharz zu Maybess Flussbrise im Teufelswald.",
    ["T"] = "Den Teufelswald säubern",
  },
  [4120] = {
    ["D"] = "Als Jäger strebe ich danach, die Stärken nachzuahmen, die ich bei meinen Gegnern sehe. Man kann den Kampf zu seinen Gunsten wenden, indem man einfach seinen Feind gut kennt, $N.$B$BAn einem Ort namens Teufelswald, nördlich des Eschentals, wurden die Kreaturen durch dämonische Energien verderbt. Die Bären und Wölfe wurden von ihnen verrückt, allerdings glaube ich, dass sie dadurch auch noch stärker geworden sind.$B$BGeht, $N. Reist zum Teufelswald und tretet den Bestien gegenüber. Erfahrt selbst, ob sie wirklich so stark sind, wie ich glaube.",
    ["O"] = "Talo Dornhuf im Camp Mojache in Feralas möchte, dass Ihr 12 Wutklauengrizzlys und 12 Teufelspfotenverheerer im Teufelswald erledigt.",
    ["T"] = "Die Kraft der Verderbnis",
  },
  [4121] = {
    ["D"] = "Eskortiert Euren Gefangenen, Grark Lorkrub, durch die Brennende Steppe und durch den Schwarzfels in die Sengende Schlucht.$B$BEuch fällt ein, dass Lexlort gesagt hat, seine Männer würden auf der anderen Seite warten, um Grark in Gewahrsam zu nehmen.$B$BIhr werdet außerdem Eure Thoriumfesseln an Lexlort übergeben müssen.",
    ["O"] = "Eskortiert Euren Gefangenen, Grark Lorkrub, durch die Brennende Steppe und durch den Schwarzfels in die Sengende Schlucht.$B$BEuch fällt ein, dass Lexlort gesagt hat, seine Männer würden auf der anderen Seite warten, um Grark in Gewahrsam zu nehmen.$B$BIhr werdet außerdem Eure Thoriumfesseln an Lexlort übergeben müssen.",
    ["T"] = "Gefährliche Zwickmühle",
  },
  [4122] = {
    ["D"] = "Begebt Euch in die Brennende Steppe und sucht Grark Lorkrub. Ihr erinnert Euch, dass Lexlort gesagt hat, er sei zuletzt in einer großen Festung des Schwarzfelsklans gesehen worden.$B$BWenn Ihr Grark Lorkrub gefunden habt, verwendet die Thoriumfesseln, um ihn zu binden, und bringt ihn dann über den Schwarzfels in die Sengende Schlucht zurück. Lexlorts Männer werden auf der anderen Seite warten, um Grark in Gewahrsam zu nehmen.",
    ["O"] = "Begebt Euch in die Brennende Steppe und sucht Grark Lorkrub. Ihr erinnert Euch, dass Lexlort gesagt hat, er sei zuletzt in einer großen Festung des Schwarzfelsklans gesehen worden.$B$BWenn Ihr Grark Lorkrub gefunden habt, verwendet die Thoriumfesseln, um ihn zu binden, und bringt ihn dann über den Schwarzfels in die Sengende Schlucht zurück. Lexlorts Männer werden auf der anderen Seite warten, um Grark in Gewahrsam zu nehmen.",
    ["T"] = "Grark Lorkrub",
  },
  [4123] = {
    ["D"] = "Schon seit Jahren suche ich einen ganz bestimmten Edelstein. Er wird das \'Herz des Berges\' genannt und ist so groß wie Eure Faust! Die Dunkeleisenzwerge haben den Stein in ihrem Gewölbe weggeschlossen und ich kann machen, was ich will, sie wollen ihn mir einfach nicht verkaufen!$B$BAlso muss ich Gewalt anwenden.$B$BKämpft Euch zum unteren Gewölbe der Schwarzfelstiefen durch, brecht in den geheimen Safe dort ein und holt Euch das Herz. Dazu müsst Ihr den Wachmann Stahlgriff besiegen, aber der taucht erst auf, wenn Ihr jeden Reliktkasten im Gewölbe geöffnet habt!$B$BViel Glück.",
    ["O"] = "Bringt das \'Herz des Berges\' zu Maxwort Funkelglanz in der Brennenden Steppe.",
    ["T"] = "Das Herz des Berges",
  },
  [4124] = {
    ["D"] = "Einer meiner Kollegen, Ginro Herdfeuer, braucht Hilfe und Informationen über den Aufenthaltsort eines Kuriers und der Pakete, die er bei sich hatte. Es ist etwa eine Woche her, dass der Kurier verschwunden ist; unsere eigene Suche hat nichts ergeben und wir wissen nicht, was wir noch tun sollen. Leider musste ich den Kurier für vermisst erklären.$B$BWenn Ihr freie Zeit und Kapazitäten habt, dann sprecht doch bitte mit Ginro. Er sollte irgendwo hier in der Festung sein, vielleicht in seinem Quartier.",
    ["O"] = "Sprecht mit Ginro Herdfeuer in der Mondfederfeste.",
    ["T"] = "Der vermisste Kurier",
  },
  [4125] = {
    ["D"] = "Es ist etwa eine Woche her, seit der Kurier Raschal sich nach Thalanaar aufgemacht hat. Er kennt Feralas gut; er ist schon bei uns, seit General Mondfeder das Expeditionskorps hergeführt hat. Ich weiß, dass er vorhatte, mit einem Ruderboot die Küste entlangzufahren und Feralas im Süden zu durchqueren, um das Lager der Horde zu umgehen.$B$BIch konnte sein Boot nicht finden, aber vielleicht findet Ihr es ja. Vielleicht liegt es irgendwo versteckt...oder vielleicht wurde es auch versenkt. Wie dem auch sei, möglicherweise enthält es einen Hinweis darauf, wo Raschal ist. Wenn Ihr irgendetwas entdeckt, lasst es mich bitte wissen.",
    ["O"] = "Sucht das Boot des Kuriers irgendwo entlang der Küste von Feralas.",
    ["T"] = "Der vermisste Kurier",
  },
  [4126] = {
    ["D"] = "Die Dunkeleisenzwerge, angeführt von dem Bösewicht Hurley Pestatem, haben eines der besten Rezepte der Donnerbräus gestohlen, jenes für das Donnerbräu Lagerbier. Diese Schurken! Sie verdienen ein solch edles Gebräu nicht!$B$B$N, ich habe eine schwierige Aufgabe für Euch. Geht zu den Schwarzfelstiefen in der Brennenden Steppe, zerstört alle Fässer mit Donnerbräu Lagerbier, die Ihr finden könnt, und bringt unser Donnerbräurezept zurück!$B$BBitte, bringt das Rezept unbedingt zurück, und wenn Ihr dafür die ganzen Schwarzfelstiefen umkrempeln müsstet!",
    ["O"] = "Bringt Ragnar Donnerbräu in Kharanos das gestohlene Donnerbräurezept.",
    ["T"] = "Hurley Pestatem",
  },
  [4127] = {
    ["D"] = "Als Ihr mit der Hand durch den Matsch fahrt, fühlt Ihr plötzlich den Griff eines Messers unter Euren Fingern. Es sieht gut gepflegt aus und dürfte mit Sicherheit jemandem gehört haben, der auf dem Boot gewesen ist. Vielleicht hat das große Stück des verschwundenen Bootes irgendetwas mit seinem Verschwinden zu tun.$B$BIhr nehmt das Messer an Euch, denn Ginro Herdfeuer in der Mondfederfeste hatte Euch angewiesen, ihm jegliche Hinweise sofort zu bringen. Allerdings werdet Ihr das ungute Gefühl nicht los, dass Ihr in diesem Gewässer nicht allein seid...",
    ["O"] = "Kehrt zur Mondfederfeste zurück und zeigt Ginro Herdfeuer das gefundene Messer.",
    ["T"] = "Havarie",
  },
  [4128] = {
    ["D"] = "Hallo, $N. Wie ich hörte, soll Ragnar Donnerbräu außer sich vor Wut sein, dass jemand sein Bier gestohlen hat. Er sucht nach wackeren Abenteurern und ist gewillt, sie reichlich zu entlohnen.$B$BSprecht mit Ragnar, wenn Ihr Interesse habt. Ihr findet ihn in der Brauerei Donnerbräu in Kharanos in Dun Morogh.",
    ["O"] = "Sprecht mit Ragnar Donnerbräu.",
    ["T"] = "Ragnar Donnerbräu",
  },
  [4129] = {
    ["D"] = "Der Hausdruide der Festung, Quintis Lebenshand, verfügt über ein paar... ungewöhnliche Fertigkeiten. Eine davon ist die Psychometrie - die Fähigkeit, durch Berührung eines Objekts etwas über seine Geschichte zu erfahren. Fragt mich nicht, wie er das bewerkstelligt. Es muss etwas mit Magie zu tun haben, wovon ich nichts verstehe und womit ich auch nichts zu tun haben will. Zwei Dinge weiß ich jedoch: Erstens, ich habe gesehen, dass es funktioniert, und zweitens, der Gauner schuldet mir etwas.$B$BQuint lebt in einem kleinen Baumhaus. Bringt ihm das Messer und richtet ihm von mir aus, dass seine Kalimdor-Poker-Schulden bei mir beglichen sind, wenn er seinen Hokuspokus bei dem Messer wirkt.",
    ["O"] = "Bringt Quintis Lebenshand in der Mondfederfeste das gefundene Messer.",
    ["T"] = "Das wieder aufgetauchte Messer",
  },
  [4130] = {
    ["D"] = "Entschuldigt bitte, mir ist immer noch ein bisschen schwummrig. Wie dem auch sei, ich hatte ein paar nützliche Visionen.$B$BRaschal nimmt zwei lederne Rucksäcke aus seinem Boot und geht landeinwärts. Dann jedoch kehrt er zu seinem Boot zurück. Ich sehe Gnollskalps... er ist auf ein paar Gnolle gestoßen - Waldpfoten nennt man sie, glaube ich. Er ist sehr besorgt - fast schon panisch - wegen etwas. Und das so sehr, dass er nicht bemerkt, wie sich Wasserelementare hinter seinem Rücken manifestieren. Hier enden die Visionen.$B$BIch muss mich ausruhen, tut mir leid, $N. Erzählt Gin, was ich gesehen habe. Er muss das wissen.",
    ["O"] = "Sprecht mit Ginro Herdfeuer in der Mondfederfeste.",
    ["T"] = "Psychometrie-Sitzung",
  },
  [4131] = {
    ["D"] = "Unsere \'Freunde\', die Waldpfoten, mögen Raschal am Ende vielleicht zwar erwischt haben, aber irgendwie habe ich das Gefühl, als hätte sich da etwas anderes ereignet. Wie dem auch sei, es dürfte allerdings der beste Anhaltspunkt sein, um mit der Suche zu beginnen.$B$BIch wünschte nur, ich wüsste genauer, was zu tun ist, aber momentan kann ich lediglich sagen: \'Durchsucht die Lager der Waldpfoten. Macht Euch auf den Weg.\' Ich weiß, dass Gnolle Gegenstände, die sie ergattern, behalten und um sie zanken. Vielleicht haben sie ja auch die Rucksäcke gefunden, die Quint erwähnte. Ohne Quints Visionen hätte ich bei früheren Suchaktionen niemals eine direkte Konfrontation mit ihnen gesucht, aber jetzt würde ich sagen: Auf sie mit Gebrüll!",
    ["O"] = "Durchsucht die Lager der Waldpfotengnolle im Süden von Feralas nach Hinweisen auf den Verbleib des Kuriers oder seiner Habseligkeiten.",
    ["T"] = "Die Waldpfotengnolle",
  },
  [4132] = {
    ["D"] = "Begebt Euch zu den Schwarzfelstiefen und eliminiert General Zornesschmied! Kehrt zum Kriegsherrn Bluthauer zurück, sobald Ihr diese Aufgabe erledigt habt.",
    ["O"] = "Begebt Euch zu den Schwarzfelstiefen und eliminiert General Zornesschmied! Kehrt zum Kriegsherrn Bluthauer zurück, sobald Ihr diese Aufgabe erledigt habt.",
    ["T"] = "Operation: Tod dem Zornesschmied",
  },
  [4133] = {
    ["D"] = "Schattenmeisterin Vivian Lagrave benötigt Eure Dienste. Sie lebt im Ödland, im orcischen Außenposten Kargath, wo sie dem Gerücht nachgeht, nach dem die Dunkeleisenzwerge der Schwarzfelstiefen angeblich ein bestimmtes Gebräu herstellen.$B$BSprecht mit Vivian. Sie erwartet Euch.",
    ["O"] = "Sprecht mit Schattenmeisterin Vivian Lagrave in Kargath.",
    ["T"] = "Vivian Lagrave",
  },
  [4134] = {
    ["D"] = "Es geht das Gerücht umher, dass der Dunkeleisenzwerg Hurley Pestatem ein Rezept zum Brauen von Bier gestohlen habe. Dieses Bier, das Donnerbräu Lagerbier, soll angeblich jeden, der es trinkt, mit großer Kraft und Mut erfüllen. Wir möchten dieses Getränk gern untersuchen. Vielleicht finden wir ja noch andere Anwendungsgebiete für seine Vorzüge... Anwendungsgebiete, die eher im Einklang mit den Zielen der Verlassenen sind.$B$BDurchforstet die Schwarzfelstiefen, findet Hurley, nehmt das Donnerbräurezept von ihm und bringt es mir.$B$BUnd um ihn zu finden, müsst Ihr ihn möglicherweise locken, indem Ihr sein wertvolles Bier bedroht.",
    ["O"] = "Bringt Vivian Lagrave in Kargath das gestohlene Donnerbräurezept.",
    ["T"] = "Verlorenes Donnerbräurezept",
  },
  [4135] = {
    ["D"] = "In den Rucksäcken befinden sich zwei Gegenstände: Der erste ist ein für Thalanaar bestimmtes Paket mit Waren, der zweite eine hastig hingekritzelte Notiz, die an einen unbekannten Empfänger gerichtet ist.$B$BDie Notiz spricht von der Entdeckung heimtückischer käferartiger Kreaturen südlich des Gnoll-Lagers an einem Ort, der Gewundene Tiefe genannt wird. Der Verfasser wollte die Sache persönlich untersuchen, da er dies als ernsthafte Bedrohung für ganz Feralas befand. Die Notiz ist mit \'Raschal von Mondfeder\' unterzeichnet.$B$BOffenbar habt Ihr jetzt herausgefunden, wohin Raschal letztlich verschwunden ist.",
    ["O"] = "Da Ihr jetzt Raschals letzten bekannten Aufenthaltsort in Erfahrung gebracht habt, setzt Ihr die Suche nach ihm oder seinen sterblichen Überresten in der Gewundenen Tiefe fort. Laut Notiz soll dieser Ort südlich der Lager der Waldpfotengnolle liegen.",
    ["T"] = "Die Gewundene Tiefe",
  },
  [4136] = {
    ["D"] = "Mein Bruder Ribbly hat unserer Familie schon immer auf der Tasche gelegen, unser Geld genommen und es für eine Dummheit nach der anderen ausgegeben.$B$BTja, seine letzte Dummheit muss wohl seine bislang schlimmste gewesen sein, denn Baron Revilgaz von Beutebucht hat ein Kopfgeld auf den armen Ribbly ausgesetzt. Ich kann Euch gar nicht sagen, wie glücklich das die Schraubstutzs macht! Endlich ist unser kleiner Ribbly mal etwas wert!$B$BEr versteckt sich jetzt allerdings ganz weit unten in den Schwarzfelstiefen. Sucht ihn doch bitte und bringt ihn zu mir!$B$BUnd falls er nicht mitkommen will, ist das auch kein Problem...bringt mir einfach seinen Kopf.",
    ["O"] = "Bringt Yuka Schraubstutz in der Brennenden Steppe Ribblys Kopf.",
    ["T"] = "Ribbly Schraubstutz",
  },
  [4142] = {
    ["D"] = "Ich weiß nicht so recht, was ich davon halten soll, $N. Die Pflanzen... sie scheinen ein gewisses Maß an Intelligenz zu besitzen.$B$BAllerdings bin ich auf diesem Gebiet kein Experte. Aber ich kenne einen!$B$BGeht nach Nord-Feralas und sucht einen Freund von mir auf, er heißt Gregan Hopfenspei. Er kennt sich mit solchen Dingen bestens aus.$B$BGregan kann uns bestimmt weiterhelfen. Ihr findet ihn draußen in der Wildnis, denn für das Großstadtleben hat er nicht viel übrig.",
    ["O"] = "Bringt Gregan Hopfenspei in Feralas eine Blutblüte.",
    ["T"] = "Ein Besuch bei Gregan",
  },
  [4143] = {
    ["D"] = "Um ehrlich zu sein, $N, solche Pflanzen habe ich auf meinen Reisen noch nie gesehen. Vielleicht sollte ich mal einen Ausflug zu diesem Krater machen...$B$BEgal, ich weiß vermutlich, was Muigin benötigt - Dunst der Atal\'ai. Das ist ein abscheuliches Zeug, sehr schwer zu sammeln, aber es ist genau das, was er benötigt, um diese kleinen Monster unter Kontrolle zu bringen.$B$BIhr müsst zum Tempel von Atal\'Hakkar in den Sümpfen des Elends gehen. Den Dunst der Atal\'ai bekommt Ihr von Tieflauerern, Düsterwürmern oder Schlammen, die man dort findet. Viel Glück, $N!",
    ["O"] = "Sammelt 5 Proben Dunst der Atal\'ai und bringt sie Muigin im Krater von Un\'Goro.",
    ["T"] = "Der Dunst des Bösen",
  },
  [4145] = {
    ["D"] = "Seht Ihr Muigin da drüben? Es ist SEINE Schuld, dass wir uns verirrt haben!$B$BIch kannte den richtigen Weg, aber er bestand darauf, eine Abkürzung zu nehmen, und so sind wir hier gelandet!$B$BUnd um die Lage noch zu verschlimmern, schickt er mir jetzt auch noch seine verrückten Pflanzen, um mich zu ärgern. Ich bin so wütend, dass ich bis auf weiteres kein Wort mehr mit ihm reden werde, aber diese Quälgeister sind wirklich unerträglich.$B$BWarum versuchen wir nicht als Erstes, seinen Bestand ein bisschen zu dezimieren? Geht hinaus und jagt einige Blutblüten und kehrt dann zu mir zurück.",
    ["O"] = "Jagt 5 Blutblütenpeitscher, 5 Blutblütendrescher, 5 Blutblütenschinder und 5 Blutblütenfallen für Larion in Marschalls Zuflucht.",
    ["T"] = "Larion und Muigin",
  },
  [4146] = {
    ["D"] = "Ihr habt Glück, $N! Während Marvon fort war, habe ich an einigen meiner eigenen Experimente gearbeitet, eines davon ist ein Unkrautvernichter mit kurzer Reichweite. Nur noch ein paar kleine Änderungen, dann ist er perfekt, um damit diese Blutblütenplagen zu beseitigen.$B$BIhr könnt gern einen meiner Prototypen haben, aber Ihr benötigt noch etwas Treibstoff dafür. Den für den Vernichter benötigten Dunst der Atal\'ai findet Ihr im Tempel von Atal\'Hakkar. Ihr bekommt ihn von Tieflauerern, Düsterwürmern oder Schlammen.",
    ["O"] = "Bringt Larion in Marschalls Zuflucht den ungeladenen Unkrautvernichter und 5 Proben Dunst der Atal\'ai.",
    ["T"] = "Treibstoff für die Unkrautvernichtung",
  },
  [4147] = {
    ["D"] = "Ich habe herausgefunden, was Muigin so treibt. Er züchtet da drüben doch tatsächlich diese verdammten Dinger!$B$BIch glaube, die einzige Lösung des Problems besteht darin, mit... nein, nicht mit ihm zu sprechen... Was für ein Blödsinn! Die Lösung ist zurückzuschlagen!$B$BWir brauchen die Hilfe meines Freundes Marvon, der in Ratschet eine Werkstatt hat. Er kann bestimmt irgendein Gerät zusammenbasteln, das uns helfen wird.",
    ["O"] = "Reist zu Marvon Nietensuchers Werkstatt in Ratschet.",
    ["T"] = "Marvons Werkstatt",
  },
  [4148] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Blutblütenvernichter",
  },
  [4161] = {
    ["D"] = "Vor langer Zeit nannte man die Nachtelfen \'Kaldorei\', was übersetzt \'Kinder der Sterne\' bedeutet. Für Euch als $C ist es besonders wichtig, etwas über die Vergangenheit zu erfahren, also hört gut zu, was ich Euch zu sagen habe, $N.$B$BDie Kaldorei waren schon immer stolz auf ihre harmonische Beziehung zur Natur. Das bedeutet, dass wir von der Natur nur nehmen, was notwendig ist, und es in gleichem Maße vergelten. Dieses Gleichgewicht bedeutet uns viel, $N.$B$BGeht und sammelt sieben kleine Spinnenbeine - und nicht mehr, und kommt dann zu mir zurück.",
    ["O"] = "Sammelt 7 kleine Spinnenbeine.",
    ["T"] = "Rezept der Kaldorei",
  },
  [4181] = {
    ["D"] = "Bei der Experteningenieurskunst gibt es zwei unterschiedliche Disziplinen: Gnom und Goblin. Ich erwähne die Ingenieurskunst der Goblins nur, weil ich ein gerechter und unvoreingenommener Lehrer bin. Sie machen gern Dinge, die in die Luft gehen und es ist ihnen dabei meistens egal, ob dabei was im Weg herumsteht - auch wenn sie es selbst sind.$B$BLest dieses Handbuch, $N. Wenn Ihr lernen wollt, wie Ihr Euch selbst in die Luft jagt, dann bringt es zu Nixx Sprossfeder nach Gadgetzan. Denkt daran - das ist eine dauerhafte Mitgliedschaft. Es ist also nicht möglich, auch in die andere Disziplin einzutreten, daher solltet Ihr genau wissen, was Ihr wollt, bevor Ihr seine Aufgabe ausführt.",
    ["O"] = "Wenn Ihr mehr über die Gobliningenieurskunst wissen wollt, dann bringt das Handbuch der Ingenieurskunstdisziplinen zu Nixx Sprossfeder nach Gadgetzan.",
    ["T"] = "Gobliningenieurskunst",
  },
  [4182] = {
    ["D"] = "Erschlagt 15 schwarze Brutlinge, 10 Exemplare der schwarzen Großdrachenbrut, 4 schwarze Wyrmkin und 1 Schwarzdrachen.",
    ["O"] = "Erschlagt 15 schwarze Brutlinge, 10 Exemplare der schwarzen Großdrachenbrut, 4 schwarze Wyrmkin und 1 Schwarzdrachen.",
    ["T"] = "Drachkin-Bedrohung",
  },
  [4183] = {
    ["D"] = "Bislang nahmen wir an, dass die Schwarzfelsorcs das Ergebnis ihrer primitiven Erziehung seien. Was wir jedoch hier, in ihrem Heimatland, entdeckt haben, erzählt uns eine ganz andere Geschichte.$B$BAngetrieben werden sie zwar tatsächlich von Kampfrausch und Streitlust, aber der schwarze Drachenschwarm ist es, der die Orcs des Schwarzfels beherrscht. Wir vermuten, dass es auch der schwarze Drachenschwarm ist, der ihre Wut und Feindseligkeit schürt.$B$BBringt diesen Brief zu Magistrat Solomon. Er wird wissen, was zu tun ist.$B$B<Helendis gibt Euch einen versiegelten Brief.>",
    ["O"] = "Reist nach Seenhain im Rotkammgebirge und händigt Magistrat Solomon Helendis Flusshorns Brief aus.",
    ["T"] = "Die wahren Meister",
  },
  [4184] = {
    ["D"] = "Reist nach Sturmwind und tragt Solomons Hilfegesuch dem König Varian Wrynn vor.$B$BDer König residiert in Burg Sturmwind.",
    ["O"] = "Reist nach Sturmwind und tragt Solomons Hilfegesuch dem König Varian Wrynn vor.$B$BDer König residiert in Burg Sturmwind.",
    ["T"] = "Die wahren Meister",
  },
  [4185] = {
    ["D"] = "Sprecht zuerst mit Lady Katrana Prestor, dann mit Hochlord Bolvar Fordragon.",
    ["O"] = "Sprecht zuerst mit Lady Katrana Prestor, dann mit Hochlord Bolvar Fordragon.",
    ["T"] = "Die wahren Meister",
  },
  [4186] = {
    ["D"] = "Ich ernenne Euch hiermit zum kommissarischen Stellvertreter von Sturmwind, $N.$B$BKehrt zu Magistrat Solomon in Seenhain zurück und übergebt ihm diesen Erlass.",
    ["O"] = "Händigt Wrynns Erlass an Magistrat Solomon in Seenhain aus.",
    ["T"] = "Die wahren Meister",
  },
  [4201] = {
    ["D"] = "Oh, wie schmerzhaft doch die Liebe ist! Mein geliebter Gefreiter Rocknot ist immun gegen meinen Charme. Er verschmäht mich! Es ist zum Verrücktwerden!$B$BIhr müsst mir helfen! Wir Sukkuben wissen viel über die Liebe und ich kann einen Trank herstellen, der den eisernen Panzer um Rocknots Herz erweichen könnte. Dazu benötige ich folgende Dinge: das Kraut Gromsblut, Riesensilbervenen von den Klippenriesen Azsharas sowie Wasser aus der Quelle des Golakkakraters in Un\'Goro. Ihr könnt das Wasser in diese Phiole füllen.$B$BBitte, $N, verhelft einer armen Seele zum Glück!",
    ["O"] = "Bringt 4 Gromsblut-Kräuter, 10 Riesensilbervenen und Nagmaras gefüllte Phiole zu Herrin Nagmara in den Schwarzfelstiefen.",
    ["T"] = "Der Liebestrank",
  },
  [4223] = {
    ["D"] = "<Magistrat Solomon seufzt.>$B$BIhr müsst in die Brennende Steppe zurückkehren, $N. Als Stellvertreter von Sturmwind erwartet man von Euch, dass Ihr Euch bei Marschall Maxwell meldet. Viel Glück.",
    ["O"] = "Sprecht mit Marschall Maxwell in der Brennenden Steppe.",
    ["T"] = "Die wahren Meister",
  },
  [4224] = {
    ["D"] = "Was? Ihr dachtet, Ihr wärt der Erste? Wie viele Soldaten müssen wir noch verlieren, bevor Sturmwind handelt?$B$BWir hatten riesige Mengen an Informationen über die Schwarzfelsbedrohung zusammengetragen, doch dann verloren wir unseren befehlshabenden Offizier, Marschall Windsor. Zusammen mit Marschall Windsor gingen auch all unsere wertvollen Informationen verloren.$B$BWas wir brauchen, ist diese Dokumentation. Sprecht mit dem struppigen John, $N. Er war mit Windsor zusammen, als sie von einem Stoßtrupp der Schwarzfelsorcs überfallen wurden. Er wurde zuletzt in einer Höhle im Norden gesehen.",
    ["O"] = "Sprecht mit dem struppigen John.",
    ["T"] = "Die wahren Meister",
  },
  [4241] = {
    ["D"] = "Dieser nichtsnutzige Feigling behauptet also, Windsor sei noch am Leben, was?$B$BMacht Euch bereit, Stellvertreter, Ihr werdet ausziehen, um ihn zu suchen. Falls Windsor noch am Leben ist, müsst Ihr ihn zurückholen. Sollte er tot sein, brauche ich einen Beweis dafür.$B$B<Maxwell lacht.>$B$BIch wette, das könnt Ihr langsam schon nicht mehr hören, was?",
    ["O"] = "Reist zum Schwarzfels im Nordwesten und dann weiter zu den Schwarzfelstiefen. Findet heraus, was aus Marschall Windsor geworden ist.$B$BIhr erinnert Euch daran, dass der struppige John sagte, man hätte Windsor in ein Gefängnis verschleppt.",
    ["T"] = "Marschall Windsor",
  },
  [4242] = {
    ["D"] = "Bolvar ist ein Dummkopf. Ich war gerade dabei, ihm seinen kostbaren \'Beweis\' zu beschaffen, um ihm diesen in den Hals stopfen zu können.$B$BEr ist ein blinder Blödmann. Selbst wenn der Beweis ihn in die Nase beißt, würde er ihn nicht sehen.$B$BWas meine Informationen angeht, die sind verloren. Unwiderbringlich.$B$BSollte ich Eisenfeind jemals finden und es aus diesem Labyrinth herausschaffen, wird mein erster Halt Sturmwind sein, damit ich diesem Reptil meinen Hammer über den Schädel ziehen kann.$B$BGeht jetzt, $N.",
    ["O"] = "Überbringt Marschall Maxwell bei Morgans Wacht in der Brennenden Steppe die schlechten Neuigkeiten.",
    ["T"] = "Verlorene Hoffnung",
  },
  [4243] = {
    ["D"] = "Irgendetwas muss meiner bislang besten Erfindung zugestoßen sein. Ihr habt doch schon mal mechanische Eichhörnchen gesehen, richtig? Tja, um näher an die Affen des Kraters von Un\'Goro heranzukommen, habe ich eine mechanische Äffin geschaffen, die sich unbemerkt unter sie mischen kann - ich nenne sie A-Me 01.$B$BSie hat Wahrsagesteine als Augen, so konnte ich alles sehen, was auch sie sah. Den Fungusfels hat sie wohlbehalten erreicht - das ist das Versteck der Affen, gleich nördlich von hier. Und dort habe ich dann den Kontakt zu ihr verloren. Das Letzte, was ich sah, war eines der Alphamännchen, das auf sie zugestürmt kam, um sie zu begrüßen!$B$BKönntet Ihr mir wohl helfen, sie zu finden?",
    ["O"] = "Sucht im Krater von Un\'Goro nach A-Me 01.",
    ["T"] = "Chasing A-Me 01",
  },
  [4244] = {
    ["D"] = "Ihr setzt Eure Untersuchungen fort und findet eine Abdeckung, die sich aufschieben lässt. Oberhalb der Verkabelung steht in gut lesbarer Schrift geschrieben: \'Mithrilgehäuse ersetzen und Strom erst ausschalten, dann einschalten.\'$B$BEs dauert einen Moment, bis Ihr die Abdeckung wieder angebracht habt, und Ihr fragt Euch, ob es wirklich so einfach ist, A-Me 01 zu reparieren und zurück zu Karna Wirrbart in der Nähe von Marschalls Zuflucht zu bringen.$B$BTja, probieren geht über studieren...",
    ["O"] = "Sucht ein Mithrilgehäuse und bringt es zu A-Me 01 im Krater von Un\'Goro.",
    ["T"] = "Chasing A-Me 01",
  },
  [4245] = {
    ["D"] = "Nachdem Ihr Euch vergewissert habt, dass Ihr bereit seid, A-Me 01 zu Karna Wirrbart zurückzubringen, knipst Ihr den Schalter auf \'Aus\'. Ihr seht Euch nochmals um, um sicherzugehen, dass sich keine Gorillas nähern, und fragt Euch, was wohl geschieht, wenn Ihr den Schalter wieder auf die \'Ein\'-Position stellt.$B$BDas ist Eure letzte Chance...wollt Ihr das wirklich tun?",
    ["O"] = "Begleitet A-Me 01 zu Karna Wirrbart im Krater von Un\'Goro.",
    ["T"] = "Chasing A-Me 01",
  },
  [4261] = {
    ["D"] = "Der Teufelswald war einst ein friedliches und ruhiges Land... bis die Legion hier eingefallen ist. Die Höllenbestien, Zerstörung, Chaos...$B$BDer Schädel des Gul\'dan hat dafür gesorgt, dass hier niemand ungeschoren davonkam. Alles fiel der Verderbnis zum Opfer.$B$BSogar jetzt, da ich ein Geist bin, bin ich immer noch gefangen und muss die Leiden dieses Landes ertragen. Helft mir, den Weg nach Hause zu finden. Bitte, $N, sagt mir, dass Ihr einen alten Geist weg von hier führen werdet, dorthin, wo ich endlich ruhen kann.$B$BSobald ich frei sein werde, solltet Ihr Kayneth Stillwind aufsuchen, einen Druiden, der in Waldeslied im Osten lebt.",
    ["O"] = "Bringt Arei in Sicherheit und sprecht dann mit Kayneth Stillwind in Waldeslied, im Osten des Eschentals.",
    ["T"] = "Alter Geist",
  },
  [4262] = {
    ["D"] = "Die letzten fünf meiner Magier, die ausgezogen waren, um sich auf dem Schwarzfels umzusehen, kamen knusprig geröstet zurück, mit einem dauerhaften orangefarbenen Gnomenafro - oder noch schlimmer.$B$B<Jalinda nimmt etwas Asche vom Boden auf und lässt sie vom Wind davontragen.>$B$BEin besonders mieses Individuum, Übermeister Pyron, scheint hier eine Menge Ärger zu verursachen. Dieser Elementar muss vernichtet werden, damit unser Expeditionsteam in die Schwarzfelstiefen reisen kann!$B$BEr bewacht den Eingang am Schwarzfelssteinbruch.",
    ["O"] = "Erschlagt Übermeister Pyron.$B$BIhr erinnert Euch an Jalindas Worte, dass Pyron den Steinbruch bewachen würde. Vielleicht solltet Ihr dort nachsehen?",
    ["T"] = "Übermeister Pyron",
  },
  [4263] = {
    ["D"] = "Sucht Lord Incendius in den Schwarzfelstiefen und vernichtet ihn!",
    ["O"] = "Sucht Lord Incendius in den Schwarzfelstiefen und vernichtet ihn!",
    ["T"] = "Incendius!",
  },
  [4264] = {
    ["D"] = "Soeben seid Ihr auf etwas gestoßen, das Marschall Windsor mit Sicherheit sehr interessiert. Vielleicht besteht ja doch noch Hoffnung.",
    ["O"] = "Soeben seid Ihr auf etwas gestoßen, das Marschall Windsor mit Sicherheit sehr interessiert. Vielleicht besteht ja doch noch Hoffnung.",
    ["T"] = "Eine zusammengeknüllte Notiz",
  },
  [4265] = {
    ["D"] = "Die Kapsel platzt auf, als Ihr sie berührt, und zum Vorschein kommt ein Nachtelf, der überraschenderweise noch am Leben ist. Er scheint bei guter Gesundheit zu sein, ist jedoch sehr benommen. Als Ihr ihm aus der Kapsel heraushelft, regt sich Leben in ihm.$B$B\"Elune sei Dank! Ihr habt mich gerettet! Ich... Mein Name ist Raschal. Ich erwachte völlig gelähmt in dieser Kapsel, nachdem mich diese Käfer überwältigt hatten. Wurdet Ihr von der Festung geschickt?\"$B$BAnscheinend habt Ihr den vermissten Kurier gefunden!",
    ["O"] = "Begleitet Raschal sicher aus dem Schwarmbau der Zukk\'ash hinaus.$B$BKehrt zu Ginro Herdfeuer in der Mondfederfeste zurück und lasst ihn wissen, dass Raschal am Leben ist und dass es ihm gut geht.",
    ["T"] = "Flucht aus dem Schwarmbau",
  },
  [4266] = {
    ["D"] = "$N, General Shandris Mondfeder möchte unter vier Augen mit Euch sprechen. Sie möchte Euch bestimmt für Eure Dienste zum Wohle der Festung danken, aber ich weiß auch, dass sie außerdem mit Euch über diese Insektenwesen sprechen will, auf die Ihr und Raschal im Süden von Feralas gestoßen seid. Die Berichte, die diese Insekten der Zukk\'ash betreffen, sind...mehr als beängstigend.$B$BGeneral Mondfeder wartet im Haupttruppenlager auf Euch.",
    ["O"] = "Sprecht mit Shandris Mondfeder in der Mondfederfeste.",
    ["T"] = "Der Empfang eines Helden",
  },
  [4267] = {
    ["D"] = "Ich vertraue Euch eine Kopie des Berichts an, den Raschal über die Plage im Südosten Feralas\' angefertigt hat. Der eigentliche Bericht befindet sich in den Akten in Darnassus, aber ich möchte, dass Ihr diese Kopie an eine Vertraute des Protektorats überbringt. Ihr Name lautet Gracina Glaubensmacht. Sie ist eine Priesterin, die im Tempel des Mondes in Darnassus lebt. Sie verfügt über großes Wissen, solche Kreaturen betreffend, und wenn sie diesen Bericht erhalten würde, käme es uns allen sehr zugute.$B$BMacht Euch auf den Weg, und viel Glück, $N. Möge Elune über uns alle wachen.",
    ["O"] = "Bringt Raschals Bericht zu Gracina Glaubensmacht in Darnassus.",
    ["T"] = "Aufstieg der Silithiden",
  },
  [4281] = {
    ["D"] = "Dieses Paket mit Waren, das Ihr in den Lagern der Waldpfotengnolle gefunden habt, war für den kleinen Streckenposten Thalanaar im Südosten Feralas\' bestimmt. Das Paket, ungeachtet des Schicksals des vermissten Kuriers, harrt immer noch der Auslieferung.$B$BIhr untersucht das Paket genauer und entdeckt den Namen \'Falfindel Wegeshut\' handschriftlich auf der Verpackung vermerkt. Vielleicht ist er der eigentliche Empfänger des Pakets.",
    ["O"] = "Bringt das nicht ausgelieferte Paket zu Falfindel Wegeshut in Thalanaar.",
    ["T"] = "Thalanaar-Lieferung",
  },
  [4282] = {
    ["D"] = "<Marschall Windsor wedelt aufgeregt mit der Notiz herum.>$B$BWisst Ihr, was das bedeutet, $N?$B$BVielleicht ist doch noch nicht alles verloren! Soweit ich das erkennen kann, wird die Information von zwei Dunkeleisenzwergen verwahrt: General Zornesschmied und dem Golemlord Argelmach. Ich habe das Gefühl, als würden sie die Information nicht einfach so herausrücken, wenn wir sie freundlich darum bitten.$B$BAlso hoffe ich, dass Ihr auf einen Kampf vorbereitet seid.$B$BZieht aus und findet die beiden Bösewichte! Vernichtet sie und bringt mir meine verloren gegangenen Informationen zurück.",
    ["O"] = "Holt Marschall Windsors verloren gegangene Informationen zurück.$B$BMarschall Windsor glaubt, dass sich die Informationen in den Händen des Golemlords Argelmach und des Generals Zornesschmied befinden.",
    ["T"] = "Ein Funken Hoffnung",
  },
  [4283] = {
    ["D"] = "Ich werde Euch ein kleines Geheimnis verraten, Soldat: Kommt näher.$B$BICH BIN HIER DER EINZIGE, DER NOCH BEI VERSTAND IST!$B$BSeht Euch nur all diese Leute an! Ist es zu fassen, dass sie alle irre sind!? Oh ja, das sind sie! DURCHGEDREHT! WAHNSINNIG!$B$B<Oralius senkt die Stimme.>$B$BDas bleibt aber unter uns: mir, Euch und meinem Kumpel Käpt\'n Winky.$B$B<Oralius greift in die Luft und drückt zu.>$B$BWie auch immer...Ich sammle Abzeichen, die gut zu meiner Sammlung Ogerohren passen. Orcs sind der letzte Schrei. Fünfzig. LOS!$B$B<Oralius stößt in ein Horn.>$B$BAB DAMIT!",
    ["O"] = "Beschafft Euch 50 Medaillons des Schwarzfelsklans.",
    ["T"] = "FÜNFZIG! Jawoll!",
  },
  [4284] = {
    ["D"] = "Ich bin sicher, Ihr habt sie bemerkt, $N. Sie sind überall!$B$BWovon ich rede? Na, von den Kristallen natürlich!$B$BDie Kristalle... Sie liegen überall im Krater herum. Die meisten Leute ignorieren sie, aber ich habe das seltsame Gefühl, als besäßen sie eine gewisse Macht. Ich habe eine Weile mit ihnen herumexperimentiert, aber ich habe noch nichts herausgefunden. Tja, bis jetzt jedenfalls.$B$BAber hört mal, mir gehen langsam die Kristalle aus - meint Ihr, Ihr könntet noch ein paar für mich sammeln?",
    ["O"] = "Sammelt 7 Machtkristalle von jeder Farbe: rot, blau, gelb und grün. Bringt sie zu J. D. Collie in Marschalls Zuflucht.",
    ["T"] = "Machtkristalle",
  },
  [4285] = {
    ["D"] = "Wofür die wohl gut sind? Ich muss ständig daran denken!$B$BEs macht mich verrückt, dass all die Gerüchte über die Kristallpylonen wahr sind. Nennt mich leichtgläubig, aber ich glaube gewöhnlich, was mir die Leute erzählen.$B$BWie dem auch sei, ich habe gehört, dass sich einer dieser Kristallpylonen hier ganz in der Nähe befinden soll. Irgendwo im Norden... Sucht ihn doch bitte, $N. Ich weiß genau, dass es die Dinger gibt.",
    ["O"] = "Findet den Standort des nördlichen Kristallpylonen im Krater von Un\'Goro.",
    ["T"] = "Der Nordpylon",
  },
  [4286] = {
    ["D"] = "Beschafft Euch 20 Dunkeleisengürteltaschen.",
    ["O"] = "Beschafft Euch 20 Dunkeleisengürteltaschen.",
    ["T"] = "Das gute Zeug",
  },
  [4287] = {
    ["D"] = "Während meines Aufenthaltes hier sind mir seltsame Gerüchte über rätselhafte Pylonen zu Ohren gekommen, die es angeblich draußen in der Wildnis des Kraters geben soll, $N. Ich nehme an, sie sind ziemlich groß, mit Kristallen an den Innenseiten.$B$BIch habe zwar noch nie welche gesehen, aber ich komme nicht umhin zu glauben, dass die Gerüchte wahr sind - und dass die Pylonen irgendetwas mit all den Kristallen zu tun haben, die wir gefunden haben.$B$BAlso, warum helft Ihr mir nicht, meine Neugier zu stillen? Angeblich soll es einen im östlichen Teil des Kraters geben. Meint Ihr, Ihr könntet ihn finden?",
    ["O"] = "Findet den Standort des östlichen Kristallpylonen im Krater von Un\'Goro.",
    ["T"] = "Der Ostpylon",
  },
  [4288] = {
    ["D"] = "Meine Experimente haben bis jetzt noch keine positiven Ergebnisse erzielt, $N. Ich kann nur hoffen, dass Ihr eine Lösung findet, indem Ihr einen der Pylonen entdeckt, von denen ich gehört habe.$B$BWas, wenn wir bestimmte Farben mit ihnen kombinieren würden... ach, eines nach dem anderen. Zuerst müssen wir sie finden!$B$B$N, sucht im westlichen Teil des Kraters. In diesem Gebiet soll sich irgendwo ein Pylon befinden.",
    ["O"] = "Findet den Standort des westlichen Kristallpylonen.",
    ["T"] = "Der Westpylon",
  },
  [4289] = {
    ["D"] = "In diesem Krater gibt es viele seltsame Kreaturen - sie scheinen edler zu sein als ihre Verwandten, die man überall in meinem Heimatland und jenseits davon findet. Ich habe keine Erklärung dafür, aber dieser Ort trägt die Handschrift der Erdenmutter. Immer, wenn ich auf eines ihrer wundervollen Geschöpfe in diesem Krater stoße, erweise ich ihr meine Ehrerbietung.$B$BWenn auch Ihr diese Erleuchtung spüren möchtet, die eine solche Tat begleitet, dann geht nach Norden. Dort findet Ihr große Affen, die die Kraft eines jeden Sterblichen auf eine harte Probe stellen. Bringt mir ihre Pelze...beweist mir Eure Kraft.",
    ["O"] = "Bringt Torwa Pfadfinder am Eingang zum Krater von Un\'Goro 2 Gorillapelze von Un\'Goro, 2 Stampferpelze von Un\'Goro und 2 Donnererpelze von Un\'Goro.",
    ["T"] = "Die Affen von Un\'Goro",
  },
  [4290] = {
    ["D"] = "Einst, vor sehr langer Zeit, stahl einer meiner Brüder ein Stück Fleisch aus Lar\'korwis Jagdbeute. Damals wusste er es nicht, aber Lar\'korwi hat ihn vom Wald aus beobachtet. Lar\'korwi folgte meinem Bruder tagelang und vernichtete alles, was er berührte. Mein Bruder, von Angst erfüllt, entschloss sich schließlich, aus dem Krater zu fliehen, um Lar\'korwi zu entkommen.$B$BLar\'korwi tötete ihn, nicht weit von dieser Stelle entfernt.$B$BWenn Ihr Lar\'korwi provozieren wollt, sucht nach seiner letzten Jagdbeute und stehlt ihm etwas Fleisch.$B$BKommt dann zu mir zurück.",
    ["O"] = "Sucht das Gerippe von Lar\'korwis letzter Jagdbeute und stehlt einen Teil eines Dreschadongerippes; kehrt dann zu Torwa Pfadfinder im Krater von Un\'Goro zurück.",
    ["T"] = "Lar\'korwis Mahl",
  },
  [4291] = {
    ["D"] = "Ich bin wirklich erstaunt, dass er noch nicht hinter Euch her ist, $N. Alles läuft jedoch gut, denn ich habe einen letzten Plan, mit dem wir das Monster zu uns locken können.$B$BAls dominantem Männchen steht es Lar\'korwi zu, sich mit jedem Weibchen zu paaren, das ihm gefällt - und ihm gefallen viele. Westlich von hier werdet Ihr eine Menge Ravasauruseier finden. Tretet dicht an sie heran, um damit einen Angriff seiner Artgenossen zu provozieren. Die Weibchen produzieren in einer Drüse an ihrem Kopf einen abscheulichen Gestank, der die Männchen wissen lässt, dass sie paarungsbereit sind. Bringt mir zwei dieser Drüsen.",
    ["O"] = "Bringt Torwa Pfadfinder in der Nähe des Eingangs zum Krater von Un\'Goro 2 Pheromondrüsen eines Ravasaurus.",
    ["T"] = "Lar\'korwis Fährte",
  },
  [4292] = {
    ["D"] = "Das hier wird der schwierigste Teil werden, denn der Plan ist etwas kompliziert. Nördlich von hier findet Ihr einen kleinen Pfad, der in die Berge hinaufführt. Dort habe ich Lar\'korwi zuletzt gesehen. Ihr werdet wissen, dass Ihr im richtigen Gebiet seid, wenn Ihr all die Knochen der großen Kreaturen findet, die es dort einst gab. Nehmt das Fleisch, das Ihr Euch besorgt habt, und legt es dort ab. Reibt das Fleisch dann mit der Drüse ein. Wenn es mit beiden Lockmitteln zusammen nicht gelingt, Lar\'korwi zu Euch zu locken, dann weiß ich auch nicht weiter.",
    ["O"] = "Beschwört Lar\'korwi mit dem Inhalt von Torwas Beutel und besiegt ihn. Bringt dann Lar\'korwis Kopf zu Torwa Pfadfinder in der Nähe des Eingangs zum Krater von Un\'Goro.",
    ["T"] = "Ein Köder für Lar\'korwi",
  },
  [4293] = {
    ["D"] = "Der Teufelswald ist ein Wald weit im Norden des Brachlandes, sogar noch weiter weg als das Eschental. Um die dortigen Teiche herum, die durch dämonische Energie gesegnet wurden, befinden sich überall Schlamme. Die Geißel hat mit den Angriffen von Höllenbestien und anderer Dämonenmagie die Gegend stark verwüstet - die Schleime sind angeblich während dieser Angriffe aus der Erde an die Oberfläche gekrochen.$B$BSammelt einige Proben von Teufelswaldschleimen, dann verwenden wir die Testausrüstung hier neben mir, um eine Kontrollgruppe zusammenzustellen, mit der wir die anderen Proben, die Ihr eingesammelt habt, vergleichen können.",
    ["O"] = "Sammelt im Teufelswald Proben von verschiedenen Schleimen. Verwendet dann die Testausrüstung neben Chemiker Brenner in Unterstadt, um zu sehen, welche Eurer Proben am verderbtesten sind. Bringt 5 der verderbten Proben aus dem Teufelswald, die Ihr findet, zu Chemiker Brenner.",
    ["T"] = "Eine Schleimprobe...",
  },
  [4294] = {
    ["D"] = "Habt Ihr schon einmal vom Krater von Un\'Goro gehört, $N? Er liegt in der Nähe der Tanariswüste und es ist ein großartiger Ort: üppig, von Leben sprühend, voller Kreaturen und Pflanzen.$B$BDorthin müsst Ihr Euch begeben. Ich habe mir sagen lassen, dass der Krater noch völlig unbesudelt und frei von Einflüssen von außen sei. Erst jetzt beginnen Forscher, seine Geheimnisse zu entdecken.$B$BIch würde die dortigen Schleime und Schlamme gern testen. Wenn es stimmt, dass sie nicht verderbt sind, dann würde ich gern wissen, wie sich solche Kreaturen entwickelt haben.",
    ["O"] = "Sammelt im Un\'Goro Proben von verschiedenen Schleimen. Verwendet dann die Testausrüstung neben Chemiker Brenner in Unterstadt, um zu sehen, welche Eurer Proben am reinsten sind. Bringt 5 der reinen Proben aus dem Un\'Goro, die Ihr findet, zu Chemiker Brenner.",
    ["T"] = "...und eine Menge Schlamm",
  },
  [4296] = {
    ["D"] = "Die Legenden der Sieben führten mich in die Brennende Steppe. Sie waren hoch gebildete und mächtige Zwerge, und wenn man über Wissen und Macht verfügt, so ist das der Schlüssel zum Reichtum.$B$BUnd genau das ist es, was ich will. Reichtum!$B$BWenn wir mehr über die Sieben in Erfahrung bringen könnten, würde das uns beide vielleicht reich machen. In den Ruinen von Thaurissan soll sich angeblich die Schrifttafel der Sieben befinden, auf der das Wissen über die alten Zwerge geschrieben steht. Sucht diese Schrifttafel, kopiert den Text und bringt ihn mir. Lasst uns deren Geheimnisse gemeinsam entschlüsseln!",
    ["O"] = "Beschafft Euch die Abschrift der Schrifttafel.",
    ["T"] = "Schrifttafel der Sieben",
  },
  [4297] = {
    ["D"] = "Grimmlingflitzer sind Allesfresser - das bedeutet, dass sie sowohl Fleisch als auch Grünzeug fressen. Wenn dieses Junge hier schlüpft, sollten wir vorbereitet sein. Es ist kaum zu glauben, aber diese kleinen Kerlchen ernährt man am besten mit Fleischstücken, roh und saftig. Ich habe nie herausgefunden, warum das so ist, aber es muss wohl etwas mit Elunes Plan für die Elfen zu tun haben... vielleicht waren sie als Beschützer oder so etwas vorgesehen, ich weiß es nicht.$B$BIch kümmere mich vorerst um das Ei. Warum besorgt Ihr nicht ein paar Silbermähnenpirscherflanken, mit denen wir das Junge füttern können, sobald es geschlüpft ist?",
    ["O"] = "Bringt Agnar Tiermeister im Hinterland 5 Silbermähnenpirscherflanken.",
    ["T"] = "Essen für das Baby",
  },
  [4298] = {
    ["D"] = "He, es war wohl schon weiter, als ich dachte...",
    ["O"] = "Sprecht mit Agnar Tiermeister im Hinterland.",
    ["T"] = "Elternfreuden",
  },
  [4299] = "_",
  [4300] = {
    ["D"] = "Hmm, jetzt, wo ich darüber nachdenke, könnte es doch etwas geben, das Ihr für die Hand erledigen könntet. Ja, Jes\'rimon könnte bestimmt Eure Hilfe brauchen.$B$BIm Krater von Un\'Goro gibt es Raptoren mit tödlichen Klauen. Manche dieser Raptoren haben weiße Klauen statt schwarze wie der Tod. Die Mitglieder der Hand haben...Kunden..., denen aus diesen Klauen gefertigte Waffen gefallen. Bringt mir reichlich von diesen weißen Klauen, dann werde ich Euch gut entlohnen. Wenn Ihr das schnell genug tut, dann kann ich Euch vielleicht noch auf andere Weise helfen.",
    ["O"] = "Bringt Jes\'rimon in Orgrimmar 8 weiße Ravasaurusklauen.",
    ["T"] = "Knochenklingenwaffen",
  },
  [4301] = {
    ["D"] = "Während Ihr Un\'Goro erkundet habt, sind mir ein paar andere Dinge aufgefallen, die einem mächtigen Jäger wie Euch sicher gefallen würden. Zum Beispiel gibt es da einen gewaltigen Affen unter den Gorillas im Norden. Ihr seid schon einmal in dieser Höhle gewesen, aber dieses Mal frage ich mich doch, ob Ihr die Begegnung mit ihm wohl überleben würdet, solltet Ihr meine Aufgabe annehmen.$B$BIch nenne ihn U\'cha und er ist einer der größten Söhne der Erdenmutter, da könnt Ihr sicher sein. Besiegt U\'cha und bringt mir seinen Pelz. Wenn Euch das gelingt, habt Ihr das Recht darauf, ein großer Jäger genannt zu werden.",
    ["O"] = "Bringt U\'chas Pelz zu Torwa Pfadfinder am Eingang des Kraters von Un\'Goro.",
    ["T"] = "Der mächtige U\'cha",
  },
  [4321] = {
    ["D"] = "Die Information über die Pylonen, die Ihr mir gabt, ist sehr interessant...$B$BGebt mir einen Moment Zeit, um ein paar Berechnungen anzustellen, $N. Sprecht mich demnächst noch einmal an, dann sollte ich Euch die Antwort geben können, nach der wir gesucht haben.",
    ["O"] = "Sprecht demnächst mit J. D. Collie.",
    ["T"] = "Die Sache verstehen",
  },
  [4322] = {
    ["D"] = "Wir müssen hier weg, $N. Helft mir, meine Ausrüstung aus dem Vorratsbereich zu holen und meine Freunde zu befreien. Falls wir hier rauskommen, dann müsst Ihr Euch weiter nach Morgans Wacht durchschlagen und mit Marschall Maxwell sprechen. Er wird Euch weitere Anweisungen geben.",
    ["O"] = "Helft Marschall Windsor, seine Ausrüstung zurückzuholen und seine Freunde zu befreien. Kehrt zu Marschall Maxwell zurück, wenn Ihr Erfolg hattet.",
    ["T"] = "Gefängnisausbruch!",
  },
  [4323] = "_",
  [4324] = {
    ["D"] = "Mein Bruder Ribbly war sein ganzes Leben lang zu nichts nütze; aber jetzt, glaube ich, hat er endlich einen gewissen Wert! Aber... wir werden Eure Hilfe brauchen, um aus Ribblys Wert Kapital zu schlagen.$B$BMeine Schwester Yuka hat ihn wochenlang gesucht und endlich in der Brennenden Steppe gefunden. Sie hat mich wissen lassen, dass sie ein paar Helfer brauchen könnte.$B$BAlso, was meint Ihr? Wollt Ihr uns helfen? Falls ja, sprecht mit meiner Schwester. Ihr findet sie im Flammenkamm versteckt, in einer Höhle nordwestlich des Schreckensfelsens.",
    ["O"] = "Sprecht mit Yuka Schraubstutz in der Brennenden Steppe.",
    ["T"] = "Yuka Schraubstutz",
  },
  [4341] = {
    ["D"] = "Seid Ihr in den rauchenden Ruinen von Thaurissan gewesen? Seid Ihr dort zufällig einem erbärmlichen Tölpel namens Kharan Hammermacht begegnet? Dem Zwerg, der eigentlich über mein kleines Mädchen wachen sollte?$B$B<Der König schluchzt.>$B$BUnd jetzt ist sie verschwunden, $N!$B$BIch habe meine Leute die gesamte Steppe durchkämmen lassen. Doch der einzige Hinweis, den sie über den Verbleib meiner Tochter finden konnten, war, dass sie vielleicht in den Tiefen ist. Möglicherweise ist sie sogar schon tot!$B$BAngeblich wird Kharan dort als Gefangener festgehalten...findet ihn! Ich will Antworten!",
    ["O"] = "Begebt Euch in die Schwarzfelstiefen und findet Kharan Hammermacht.$B$BDer König erwähnte, dass Kharan dort gefangen sei - vielleicht solltet Ihr nach einem Gefängnis Ausschau halten.",
    ["T"] = "Kharan Hammermacht",
  },
  [4342] = {
    ["D"] = "Lasst Kharan Hammermacht seine Geschichte erzählen.",
    ["O"] = "Lasst Kharan Hammermacht seine Geschichte erzählen.",
    ["T"] = "Kharans Geschichte",
  },
  [4361] = {
    ["D"] = "Kehrt nach Eisenschmiede zurück und überbringt König Magni Bronzebart die schlechten Nachrichten.",
    ["O"] = "Kehrt nach Eisenschmiede zurück und überbringt König Magni Bronzebart die schlechten Nachrichten.",
    ["T"] = "Der Überbringer schlechter Botschaften...",
  },
  [4362] = {
    ["D"] = "Es scheint, als habe mein alter Widersacher, Dagran Thaurissan, mich und das ganze Königreich Eisenschmiede in seiner Gewalt.$B$BVielleicht seid Ihr meine letzte Hoffnung, $N. Ihr müsst meine Tochter Moira retten!$B$BEs gibt nur einen Weg, wie der Zauber, mit dem Thaurissan Moira belegt hat, wirklich zuverlässig gebrochen werden kann: Ihr müsst ihn töten.$B$BAber, $N, meiner Tochter dürft Ihr kein Leid zufügen! Vergesst nicht, dass sie Thaurissans Kontrolle unterliegt! Alles, was sie vielleicht sagt oder tut, geschieht aufgrund von Thaurissans bösem Zauber!",
    ["O"] = "Kehrt in die Schwarzfelstiefen zurück und rettet Prinzessin Moira Bronzebart aus den Fängen des bösen Imperators Dagran Thaurissan.",
    ["T"] = "Das Schicksal des Königreichs",
  },
  [4363] = {
    ["D"] = "Thaurissan war ein großer und ehrenhafter Zwerg. Ihr habt mein Leben zerstört - meines und das meines ungeborenen Kindes!$B$B<Ihr habt den Eindruck, als leide Prinzessin Bronzebart noch immer unter den letzten Auswirkungen des vom Imperator gewirkten Zaubers.>$B$BKehrt nach Eisenschmiede zurück und sagt meinem Vater, dass der Erbe des Königreichs Eisenschmiede ein Dunkeleisenzwerg sein wird.$B$BOb er dies billigt oder nicht, so wird es sein.",
    ["O"] = "Kehrt nach Eisenschmiede zurück und sprecht mit König Magni Bronzebart.",
    ["T"] = "Die Überraschung der Prinzessin",
  },
  [4402] = {
    ["D"] = "Hier im Tal der Prüfungen wird es ganz schön heiß.$B$B<Galgar wischt sich den Schweiß von der Stirn.>$B$BWenn ich doch nur ein paar Kaktusäpfel hätte, dann könnte ich meinen berühmten Kaktusapfel Surprise machen! Nichts kühlt einen schneller ab als ein Stück dieser leckeren Spezialität.$B$BIch sag Euch was, $N. Wenn Ihr mir 10 Kaktusäpfel bringt, dann mache ich Euch ein paar Portionen Kaktusapfel Surprise, die Ihr mit auf Eure Abenteuer nehmen könnt. Falls Ihr daran interessiert seid, findet Ihr Kaktusäpfel bei den Kaktuspflanzen, die hier in der Gegend wachsen.",
    ["O"] = "Bringt Galgar 10 Kaktusäpfel. Ihr erinnert Euch doch, dass er gesagt hat, man fände sie in der Nähe von Kakteen?",
    ["T"] = "Galgars Kaktusapfel Surprise",
  },
  [4421] = {
    ["D"] = "Nicht viele kommen hierher und suchen das Abenteuer... Die Verderbnis hat sich im Teufelswald überall ausgebreitet und macht vor nichts Halt.$B$BAls Mitglied des Smaragdkreises trachte ich danach, dieses Land zu heilen. Doch es gibt andere, die wünschen, dass es weiter schwärt.$B$BIch spreche von den Jadefeuersatyrn, die sich nordwestlich von hier aufhalten, bei den Ruinen von Constellas. Ihr Anführer ist ein besonders grausamer Satyr namens Xavathras und unter seiner Führung übertragen sie die Krankheit auf alle Wesen, denen sie begegnen.$B$BWir müssen dieses Land, das einst das unsrige war, verteidigen. Besiegt Xavathras und vernichtet seine Diener.",
    ["O"] = "Tötet Xavathras sowie 11 Jadefeuerteufelsanbeter, 9 Jadefeuerschattenpirscher und 9 Jadefeuerschurken. Kehrt zu Eridan Blauwind im Teufelswald zurück, sobald die Aufgabe erledigt ist.",
    ["T"] = "Die Verderbnis der Jadefeuer",
  },
  [4441] = {
    ["D"] = "Die Flöte, die Ihr gefunden habt, wurde aus einem Urtumbeschützer geschnitzt, den Treantverbündeten, die schon immer treu über die Nachtelfen gewacht haben.$B$BEs ist den Satyrn gelungen, alles zu verderben, mit dem sie in Kontakt kommen, einschließlich der Urtume, die im Teufelswald lebten. Mithilfe der teuflischen Verderbnis wurde der Geist des Urtums gefangen und gefoltert und nie wieder völlig freigegeben. Der Teufelsbinder, den Ihr gefunden habt, hängt ganz ohne Zweifel damit zusammen.$B$BWir müssen versuchen, die Flöte zu läutern, $N. Bringt mir gesegnetes Wasser vom Tempel des Mondes in Darnassus.",
    ["O"] = "Begebt Euch nach Darnassus und verwendet Eridans Phiole, um Euch im Tempel des Mondes eine Phiole mit gesegnetem Wasser zu holen. Kehrt mit der gefüllten Phiole zu Eridan zurück.",
    ["T"] = "Gebundene Urtume",
  },
  [4449] = {
    ["D"] = "Tötet 8 Geologen der Dunkeleisenzwerge und bringt der Person, die im Plumpsklo in der Sengenden Schlucht eingesperrt ist, 15 Stücke Seidenstoff.",
    ["O"] = "Tötet 8 Geologen der Dunkeleisenzwerge und bringt der Person, die im Plumpsklo in der Sengenden Schlucht eingesperrt ist, 15 Stücke Seidenstoff.",
    ["T"] = "Eingesperrt!",
  },
  [4450] = {
    ["D"] = "Ach, wisst Ihr was? Da fällt mir grade was ein.$B$BWo ich doch immer noch hier drin festsitz: Wollt Ihr nich\' vielleicht \'n kleinen Job für mich fertig machen, den ich angenommen hab, als ich in Tanaris war? Is\' auch ganz einfach... na ja, wenn man nich\' grade hier drin sitzt, jedenfalls.$B$BKrinkle Goldstahl in Gadgetzan war scharf auf so\'n paar Sachen, die hier in der Sengenden Schlucht und an \'n paar anderen Orten gefunden wurden. Vielleicht könnt Ihr Euch ja die Liste einmal ansehen und ihm den Kram dann bringen?$B$BIch schieb Euch sein Geschäftsbuch unter der Tür durch, wenn Ihr interessiert seid. Bringt es ihm zusammen mit dem Zeug, das er will, nachdem Ihr den ganzen Kram eingesammelt habt.",
    ["O"] = "Nehmt die Abschrift von Goldstahls Geschäftsbuch und beschafft Euch dann die darin aufgeführten Gegenstände, ehe Ihr Krinkle Goldstahl in Tanaris aufsucht.",
    ["T"] = "Geschäftsbuch aus Tanaris",
  },
  [4451] = {
    ["D"] = "Der kleine Messingschlüssel sieht recht einfach aus. Das Einzige, was ihn von anderen seiner Art unterscheidet, sind einige Worte, die darauf eingeritzt sind:$B$B\"Dunkeleisenplumpsklo - Nicht duplizieren.\"$B$BIhr habt absolut keine Ahnung, was das heißen soll oder wofür der Schlüssel gut ist.",
    ["O"] = "Sucht in der Sengenden Schlucht, bis Ihr etwas findet, wofür man den Schlüssel für Rußschlacks Schuppen verwenden kann, den Ihr gefunden habt.",
    ["T"] = "Der Schlüssel zur Freiheit",
  },
  [4483] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Buchband der Abhärtung",
  },
  [4485] = {
    ["D"] = "Hallo, $N. Wie ich sehe, ist Eure Hingabe weiterhin stark, ebenso wie Euer Glaube.$B$BIch denke, es ist an der Zeit, dass Ihr mit Duthorian Rall in Sturmwind sprecht. Es gibt noch einige Lektionen, die Ihr lernen müsst, und ich spüre, dass Ihr dazu bereit seid.$B$BSucht ihn in der Kathedrale des Lichts auf... und, $N, nehmt diese Lektionen ernst! Ihr habt Euren derzeitigen Rang durch harte Arbeit und Demut erreicht, enttäuscht uns jetzt nicht, indem Ihr überheblich werdet.$B$BGehabt Euch wohl.",
    ["O"] = "Sprecht mit Duthorian Rall in Sturmwind.",
    ["T"] = "Der Foliant des Adels",
  },
  [4486] = {
    ["D"] = "Nee, nee, nee. Jetzt sieh sich nur mal einer den mächtigen Paladin in all seinem Glanz an.$B$BTut mir leid, $N, war nicht böse gemeint... Ich werde nur manchmal ein bisschen neidisch, wenn ich sehe, was Ihr für tolle Taten vollbracht habt. Tiza hat gesagt, das sei meine Schwäche, und ich habe ihr geschworen, dass ich daran arbeiten würde. Aber manchmal ist das einfach schwer, wisst Ihr.$B$BAber der Grund, warum ich mit Euch sprechen wollte, war, dass Duthorian Rall in Sturmwind gesagt hat, Ihr solltet ihn in der Kathedrale des Lichts treffen, wenn Ihr bereit seid. Klang ziemlich wichtig. Klang, als sei irgendwas los.",
    ["O"] = "Sprecht mit Duthorian Rall in Sturmwind.",
    ["T"] = "Der Foliant des Adels",
  },
  [4487] = {
    ["D"] = "Ah, gut, da seid Ihr ja. Ich habe Euch $Geinen Sukkubus:eine Höllenbestie; hinterhergeschickt, um Eure Aufmerksamkeit zu erregen, aber es scheint, als sei es Euch gelungen herzukommen, ehe $Ger:sie; Euch gefunden hat. Einerlei.$B$BIn letzter Zeit gab es einiges an Gerede über Eure wachsende Macht - das ist gut für Euch und möglicherweise schlecht für andere. Doch das sind alles Nebensächlichkeiten. Wichtig ist nur, dass ich denke, dass Ihr Euch bewiesen habt und deswegen... hihi... einen gewissen Segen verdient.$B$BOberhalb von Ratschet im Brachland findet Ihr einen Mann namens Strahad. Ich glaube, Eure Wege haben sich bereits gekreuzt.",
    ["O"] = "Sprecht mit Strahad Farsan in Ratschet.",
    ["T"] = "Teufelsross beschwören",
  },
  [4488] = {
    ["D"] = "Ah, $N, wie ich sehe, beehrt Ihr uns mit Eurer Anwesenheit. Wie wunderbar!$B$BIch hoffe, Ihr erwartet nicht auch noch, dass wir Euch von Kopf bis Fuß bedienen. Ihr mögt ja vielleicht schnell an Macht gewinnen, doch Ihr seid noch immer ein Kleinkind, was das wahre Wissen und das wahre Verständnis angeht. Wenn Ihr nicht aufpasst, wird die Verderbnis Euch und Euren schwachen Willen überwältigen.$B$BAber ich sollte zum Thema kommen: Strahad möchte mit Euch sprechen. Er wohnt noch immer im Brachland in seinem Turm, der Ratschet überblickt. Ich schlage vor, Ihr säumt nicht zu lange.",
    ["O"] = "Sprecht mit Strahad Farsan in Ratschet.",
    ["T"] = "Teufelsross beschwören",
  },
  [4489] = {
    ["D"] = "Ihr da, $R! Kommt her!$B$BIhr werdet $N gerufen, nicht wahr?$B$BDas habe ich mir gedacht. Ich fasse mich kurz: Ihr gewinnt an Macht und das ist nicht unbeobachtet geblieben. Und nun ist die Zeit gekommen, dass Ihr für Eure Anstrengungen belohnt werdet.$B$BBegebt Euch ins Brachland. Oberhalb von Ratschet findet Ihr dort Strahad Farsan - vielleicht erinnert Ihr Euch an ihn? Er möchte mit Euch sprechen. Ich würde nicht säumen.",
    ["O"] = "Sprecht mit Strahad Farsan in Ratschet.",
    ["T"] = "Teufelsross beschwören",
  },
  [4490] = {
    ["D"] = "Ich möchte Euch eine Belohnung für Eure Geduld und Eure harte Arbeit geben. Doch wisset, dass sich die Fähigkeit, ein Teufelsross zu beschwören, in Zukunft wesentlich schwieriger gestalten wird. Ihr müsst sie Euch verdienen - und es wird nicht leicht sein.$B$BDoch geht nun fürs Erste, nehmt die Macht mit Euch, eine solche Kreatur zu beschwören, und verwendet diese Macht, wie Ihr es für richtig haltet.",
    ["O"] = "Sprecht in Ratschet mit Strahad Farsan, um zu lernen, wie man ein Teufelsross beschwört.",
    ["T"] = "Teufelsross beschwören",
  },
  [4491] = {
    ["D"] = "Argh... es ist so heiß...$B$BIch war gerade dabei, die Gegend zu erkunden, als die Hitze plötzlich unerträglich wurde. Ich muss wohl einen Hitzschlag haben.$B$BBitte, bringt mich zu meiner Freundin Spraggel in Marschalls Zuflucht zurück. Ich glaube, es geht mir noch gut genug, um Euch dorthin zu folgen.$B$BAber ich fühle mich ziemlich schwach. Wenn ich also noch mal in Ohnmacht fallen sollte, dann bespritzt mich einfach mit etwas Wasser aus Spraggles Feldflasche. Das sollte helfen, hoffe ich...",
    ["O"] = "Führt Ringo zu Spraggel Frock in Marschalls Zuflucht.$B$BFalls Ringo ohnmächtig werden sollte, verwendet Spraggels Feldflasche, um ihn zu beleben.",
    ["T"] = "Etwas Hilfe von guten Freunden",
  },
  [4492] = {
    ["D"] = "Ihr müsst mir helfen, $N! Mein Freund Ringo ist vor einer Weile aufgebrochen, um den Vulkan südlich von hier zu erforschen, und er ist schon viel zu lange weg. Ich mache mir große Sorgen um ihn.$B$BMeint Ihr, Ihr könnt ihn suchen? Nehmt doch meine Feldflasche mit - wenn Ihr Ringo tatsächlich findet, dann hat er sie bestimmt nötig!",
    ["O"] = "Sucht Ringo am Feuersäulengrat.",
    ["T"] = "Verloren!",
  },
  [4493] = {
    ["D"] = "Es scheint, als sei es die Absicht der Silithiden – nein, ihr ganzer Lebenszweck! –, alles zu verschlingen, was ihnen im Weg steht und was nicht zu ihrer eigenen Ökologie gehört. Nur darauf konzentrieren sie sich und darum stellen sie eine große Gefahr für alles Leben auf Azeroth dar.$B$BBis jetzt haben wir nur die Anfänge gesehen. Ich vermute, dass sie ihre Kraft aus einem Ort tiefer drinnen in Kalimdor beziehen: aus dem Krater von Un\'Goro. Geht nach Gadgetzan in der Tanariswüste und sprecht mit Alchemist Stößelbruch. Erzählt ihm von meiner Theorie; er wird uns bestimmt dabei helfen, die Silithiden zu bekämpfen.",
    ["O"] = "Sprecht in Gadgetzan mit Alchemist Stößelbruch.",
    ["T"] = "Der Marsch der Silithiden",
  },
  [4494] = {
    ["D"] = "Es scheint, als sei es die Absicht der Silithiden – nein, ihr ganzer Lebenszweck! –, alles zu verschlingen, was ihnen im Weg steht und was nicht zu ihrer eigenen Ökologie gehört. Nur darauf konzentrieren sie sich und darum stellen sie eine große Gefahr für alles Leben auf Azeroth dar.$B$BBis jetzt haben wir nur die Anfänge gesehen. Ich vermute, dass sie ihre Kraft aus einem Ort tiefer drinnen in Kalimdor beziehen: aus dem Krater von Un\'Goro. Geht nach Gadgetzan in der Tanariswüste und sprecht mit Alchemist Stößelbruch. Erzählt ihm von meiner Theorie; er wird uns bestimmt dabei helfen, die Silithiden zu bekämpfen.",
    ["O"] = "Sprecht in Gadgetzan mit Alchemist Stößelbruch.",
    ["T"] = "Der Marsch der Silithiden",
  },
  [4495] = {
    ["D"] = "Ich habe einen Freund, Iverron, der mich normalerweise jeden Tag um dieselbe Zeit besucht. Komischerweise ist er heute noch nicht da gewesen - genau genommen ist er sogar schon etliche Stunden über seiner sonstigen Zeit.$B$BIch muss zugeben, ich bin etwas besorgt, $N. Iverron verbringt viel Zeit bei der Spinnenhöhle oben im Norden und ich bin sicher, Ihr wisst, wie gefährlich es dort ist - überall Spinnen!$B$BFalls Ihr zufällig dort vorbeikommen solltet, könntet Ihr dann nach ihm Ausschau halten?",
    ["O"] = "Sucht Iverron an der Höhle im Norden.",
    ["T"] = "Ein guter Freund",
  },
  [4496] = {
    ["D"] = "Meine Forschungen haben ergeben, dass die Silithiden den Bienen ähneln: Wenn man die Königin des Schwarmbaus tötet, dann sollte das den Rest in völliges Chaos stürzen. Ich werde einen Köder brauen, mit dem wir die Königin herauslocken können, und sobald sie draußen ist, streckt Ihr sie nieder.$B$BDoch zuerst brauchen wir einige Reagenzien; wir wollen doch nicht, dass Ihr völlig unvorbereitet im Dschungel umherstolpert. Geht nach Westen, sucht den Schwarmbau der Silithiden im Krater von Un\'Goro und beschafft Euch eine Duftdrüse von einem der Viecher. Außerdem brauche ich einige Erdproben aus Un\'Goro.",
    ["O"] = "Bringt eine Gorishiduftdrüse und 5 Erdproben von Un\'Goro zu Alchemist Stößelbruch in Gadgetzan.",
    ["T"] = "Dschungelei",
  },
  [4501] = {
    ["D"] = "Tötet 10 rasende Pterrordax und sprecht dann mit Spraggel Frock in Marschalls Zuflucht.",
    ["O"] = "Tötet 10 rasende Pterrordax und sprecht dann mit Spraggel Frock in Marschalls Zuflucht.",
    ["T"] = "Vorsicht, Pterrordax!",
  },
  [4502] = {
    ["D"] = "Als Lehrling von Marvon Nietensucher habe ich unglaublich viel gelernt. Aber er ist jetzt schon sehr lange fort. Daher denke ich, werde ich an ein paar eigenen Experimenten arbeiten.$B$BIch habe kürzlich etwas über den Krater von Un\'Goro gelesen und über die eigentümliche Vulkanaktivität, die in der Mitte des Kraters zu erkennen ist. Ich glaube, ich könnte eine Menge lernen, wenn ich nur etwas Asche von den Elementaren dort haben könnte.$B$BWürde es Euch etwas ausmachen, etwas Asche für mich einzusammeln, falls Ihr dort entlangkommen solltet?",
    ["O"] = "Sammelt 9 Proben der Asche von Un\'Goro von den Feuerelementaren am Vulkan im Krater von Un\'Goro ein und bringt die Proben zu Liv Ritzelflick in Ratschet.",
    ["T"] = "Vulkanische Aktivität",
  },
  [4503] = {
    ["D"] = "Ich baue einen Flugapparat!$B$BIch bin hier gestrandet und habe leider absolut überhaupt keinen Orientierungssinn. Ich würde ja einen Greifen oder Windreiter zurück nach Gadgetzan nehmen, aber diese Viecher jagen mir einfach eine höllische Angst ein.$B$BAlso, werdet Ihr mir helfen? Ich muss nur noch die Flügel bauen und ich glaube, dass sich die eingesponnenen Schuppen von Pterrordax und Dimetrodonten perfekt dafür eignen würden. Ich werde mich zwar garantiert nicht mit denen anlegen, aber Ihr seht ziemlich zäh aus. Wollt Ihr es nicht versuchen?",
    ["O"] = "Sammelt 8 eingesponnene Dimetrodonschuppen und 8 eingesponnene Pterrordaxschuppen für Sappalot in Marschalls Zuflucht.",
    ["T"] = "Sappalots Flieger",
  },
  [4504] = {
    ["D"] = "Ich bin immer auf der Suche nach neuen Methoden, um schnelles Gold zu verdienen, $N. Wenn Ihr mir bei meiner neuesten Idee helft, dann gebe ich Euch ein bisschen was vom Gewinn ab. Na, was sagt Ihr dazu?$B$BDas hab ich mir gedacht!$B$BGeht zu den Teergruben im Norden des Kraters von Un\'Goro und beschafft mir etwas Teer. Aber nicht jeden x-beliebigen Teer, versteht Ihr? Ihr müsst den von den Kreaturen nehmen, die an den Teergruben leben. Normaler Teer ist einfach nicht klebrig genug.$B$BDie pflanzenartigen Kreaturen jedoch haben Chlorophyll in ihrer Haut, und wenn man das mit dem Teer mischt, dann wird das superklebrig!",
    ["O"] = "Sammelt 12 Proben superklebrigen Teer für Tran\'rek in Gadgetzan.",
    ["T"] = "Superkleber",
  },
  [4505] = {
    ["D"] = "Manche Leute sagen vielleicht, ich sei außergewöhnlich grausam... Aber in Wirklichkeit bin ich doch nur von dem Wunsch beseelt zu sehen, wie die Horde einen Vorteil gegenüber der Allianz erringt, und zwar auf jede nur mögliche Weise.$B$BSeit Kurzem interessiere ich mich für einen Mondbrunnen in den Ruinen von Constellas. Er soll südlich von hier liegen und von den Jadefeuersatyrn gepflegt werden. Der Mondbrunnen, der einst ein Symbol der Erneuerung für die unerträglichen Nachtelfen war, ist jetzt verderbt und wird dazu benutzt, neue Satyrn hervorzubringen.$B$BIch habe da eine Idee, $N, und ich hätte gern eine Probe von dem Wasser... für meinen kleinen Katzenfreund hier.",
    ["O"] = "Beschafft eine Probe verderbten Wassers vom Mondbrunnen der Jadefeuersatyrn und bringt sie zu Winna Hazzard am Blutgiftposten.",
    ["T"] = "Brunnen der Verderbnis",
  },
  [4506] = {
    ["D"] = "Dieses verderbte Wasser können wir zu unserem Vorteil verwenden, $N. Mit nur einer kleinen Menge Wasser wurde mein Kätzchen doppelt so groß und scheint jetzt irgendwie... stärker als vorher zu sein. Solche Katzen könnten sich noch als nützlich erweisen...$B$BHier, $N. Nehmt diesen Katzentransportkorb und lasst das Kätzchen darin neben dem Mondbrunnen in den Ruinen von Constellas frei. Hi, hi... Ich bin mir sicher, das Ergebnis wird Euch gefallen!$B$BBringt mir danach die Katze zurück, dann werde ich mich für Eure Zeit und Mühe erkenntlich zeigen.",
    ["O"] = "Bringt Winnas Kätzchen zum verderbten Mondbrunnen, lasst es dort frei und bringt es dann wieder zu Winna zurück.$B$BSobald Ihr wieder bei Winna seid, klickt auf die Katze, um sie Winna zu übergeben.",
    ["T"] = "Verderbte Säbler",
  },
  [4507] = {
    ["D"] = "Hier ist der Köder. Im Schwarmbau sollte sich ein Kristall befinden. Wir glauben, dass die Silithiden den Kristall als eine Art Einstellungsgerät verwenden, das den ganzen Schwarmbau verbindet. Wo sich dieser Kristall befinden könnte, wollt Ihr wissen? Ich würde vorschlagen, Ihr versucht es in der Brutstätte innerhalb des Schwarmbaus.$B$BWenn Ihr den Köder bei dem Kristall benutzt, sollte das die Königin aufscheuchen, aber geht acht – sie ist wahrscheinlich von mehreren Wachen umgeben. Schaltet die Königin aus und entfernt ihr Gehirn. Ich weiß, das klingt eklig, aber wir müssen es unbedingt untersuchen. Viel Glück, $N!",
    ["O"] = "Besiegt die Schwarmkönigin der Gorishi, sobald sie erscheint.$B$BBringt das Gehirn der Schwarmkönigin zu Alchemist Stößelbruch nach Gadgetzan.",
    ["T"] = "Bauer schlägt Dame",
  },
  [4508] = {
    ["D"] = "Meine jüngsten Ergebnisse und das Wissen, das wir heute hier gesammelt haben, müssen in die Hände von Leuten gelangen, die etwas damit anfangen können. So einfallsreich wir hier in Gadgetzan auch sein mögen, das hier können wir nicht allein schaffen.$B$BBringt meinen jüngsten Bericht zu Gracina Glaubensmacht nach Darnassus; sie hält sich doch immer noch im Tempel des Mondes auf, oder? Sie hat Freunde, und zwar ziemlich hohe Tiere innerhalb der Allianz; wenn wir die in diese Sache mit hineinziehen können, $N, dann haben wir vielleicht eine Chance, dies alles zu überleben.",
    ["O"] = "Bringt Stößelbruchs Bericht von Un\'Goro zu Gracina Glaubensmacht in Darnassus.",
    ["T"] = "Die Ruhe vor dem Sturm",
  },
  [4509] = {
    ["D"] = "Meine jüngsten Ergebnisse und das Wissen, das wir heute hier gesammelt haben, müssen in die Hände von Leuten gelangen, die etwas damit anfangen können. So einfallsreich wir hier in Gadgetzan auch sein mögen, das hier können wir nicht allein schaffen.$B$BBringt meinen jüngsten Bericht zu Zilzibin Tomtom nach Orgrimmar; er lebt doch immer noch im oberen Teil der Gasse, oder? Er hat Freunde ziemlich weit oben in der Horde; wenn wir die mit in diese Sache hineinziehen können, $N, dann haben wir vielleicht eine Chance, dies alles zu überleben.",
    ["O"] = "Bringt Stößelbruchs Bericht von Un\'Goro zu Zilzibin Tomtom in Orgrimmar.",
    ["T"] = "Die Ruhe vor dem Sturm",
  },
  [4510] = {
    ["D"] = "$N, Eure Hilfe war wirklich unschätzbar wertvoll. Es wird zweifellos der Moment kommen, an dem ich erneut um Euren Beistand im Kampf gegen die Silithiden bitten muss. Wir müssen erst verarbeiten, was wir im Krater von Un\'Goro gelernt haben - ich fürchte, dass meine ursprüngliche Annahme, die Silithiden kämen aus dem Un\'Goro, falsch war... dass an einem anderen Ort tatsächlich eine wesentlich stärkere Präsenz existiert.$B$BBringt diese Nachricht zu Idriana in der Bank. Sie wird Euch etwas aus meinem Gewölbe geben, das Ihr als Zeichen meiner Dankbarkeit ansehen sollt. Ich danke Euch für Eure mutige Unterstützung, $C.",
    ["O"] = "Bringt den Bankgutschein zu Idriana in der Bank von Darnassus.",
    ["T"] = "Die Ruhe vor dem Sturm",
  },
  [4511] = {
    ["D"] = "$N, ohne Euch wären wir niemals so weit gekommen. Es wird zweifellos der Moment kommen, an dem ich erneut um Eure Hilfe im Kampf gegen die Silithiden bitten muss. Wir müssen erst verarbeiten, was wir im Krater von Un\'Goro gelernt haben - ich fürchte, dass meine ursprüngliche Annahme, die Silithiden kämen aus dem Un\'Goro, falsch war... dass an einem anderen Ort tatsächlich eine wesentlich stärkere Präsenz existiert.$B$BBringt diese Nachricht zu Karus in der Bank. Er wird Euch etwas aus meinem Gewölbe geben, das Ihr als Zeichen meiner Dankbarkeit ansehen sollt. Danke für Eure tatkräftige Unterstützung, $C.",
    ["O"] = "Bringt den Bankgutschein zu Karus in der Bank von Orgrimmar.",
    ["T"] = "Die Ruhe vor dem Sturm",
  },
  [4512] = {
    ["D"] = "Aber womit ich mich am meisten beschäftigt habe, das sind Schlamme. Ich weiß, das klingt seltsam, aber die sind wirklich überall!$B$BVor Kurzem habe ich einer Dame in Menethil dabei geholfen, ihre Tasche wiederzufinden - ein Schlamm hatte sie gefressen. Ich habe auch gesehen, wie Mitglieder der Königlichen Apothekervereinigung aus Unterstadt Proben von dem Zeug sammelten. Ich möchte wissen, wieso! Wo kommt es her?$B$BIch habe auch gehört, dass es im Teufelswald noch mehr davon geben soll. Ich brauche einige Proben von den Schlammen dort, damit ich meine Forschungen weiter vorantreiben kann. Ich gebe Euch einige Behälter mit; verwendet diese, um eine Probe von den toten Kreaturen einzusammeln.",
    ["O"] = "Bringt 6 mit verfluchtem Schlamm gefüllte Gläser und 6 mit besudeltem Schlamm gefüllte Gläser zu Laris Bummelritzel in Eisenschmiede.",
    ["T"] = "Schlamm auf Abwegen",
  },
  [4513] = {
    ["D"] = "Ich brauche noch mehr Proben, $N. Aber - und ich weiß nicht so recht, wie ich das ausdrücken soll - es müssen reine Proben sein. Ich weiß, ich weiß. \"Was meint Ihr mit \'rein\', Laris?\", fragt Ihr. Ich kann es nicht genau sagen. Ich weiß, dass die hier verderbt sind... genau wie im Teufelswald. Und genau deswegen ist meine Erkenntnis so wichtig. Die Schlamme scheinen sich anzupassen... die Eigenschaften der Gegend anzunehmen, in der sie sich befinden. Ich muss Proben finden, die nicht verunreinigt sind. Aber wo? Wo?!$B$BPah! $N, nehmt diese Behälter einfach mit, nur für den Fall, dass Ihr irgendwo welche finden solltet.",
    ["O"] = "Bringt 10 gefüllte reine Probengläser zu Laris Bummelritzel in Eisenschmiede.$B$BFalls Ihr Probleme habt, fündig zu werden, dann kann Laris Euch vielleicht noch den einen oder anderen Hinweis geben.",
    ["T"] = "Schlamm auf Abwegen",
  },
  [4521] = {
    ["D"] = "Winterquell liegt gleich hinter der Holzschlundfeste, im Nordosten des Teufelswalds. Seid Ihr schon mal dort gewesen?$B$BIch habe gehört, dass es in jener Region nur so von Kreaturen wimmeln soll, die Wildekin genannt werden. Diese Bestien sind dafür bekannt, dass sie Geheimnisse bewachen, und zwar Geheimnisse der Nachtelfen.$B$BWir müssen herausfinden, was in Winterquell verborgen liegt.$B$BIch habe gehört, die struppigen Eulenbestien findet man südlich von der Straße, und außerdem soll es noch so genannte tobende Eulenbestien geben, die im Zentrum von Winterquell zu finden sind. Räumt sie aus dem Weg und verschafft Euch so Zutritt zu dem Gebiet.",
    ["O"] = "Trull Scheiterbann im Teufelswald möchte, dass Ihr 15 tobende Eulenbestien und 15 struppige Eulenbestien tötet.",
    ["T"] = "Wilde Wächter",
  },
  [4542] = {
    ["D"] = "Bringt Klippenbehüter Langhorn am Freiwindposten die dringende Nachricht.",
    ["O"] = "Bringt Klippenbehüter Langhorn am Freiwindposten die dringende Nachricht.",
    ["T"] = "Nachricht zum Freiwindposten",
  },
  [4561] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Test auf Unreinheiten - Krater von Un\'Goro",
  },
  [4581] = {
    ["D"] = "Der Druide Kayneth Stillwind hat sich bei mir gemeldet. Er hätte gern einen Bericht über das, was an unserer Küste und in den Wäldern um Astranaar vorgeht. Er befürchtet, dass eine Verderbnis durch das Eschental zieht... und vielleicht hat er Recht damit.$B$BHier, bringt ihm meinen Bericht. Ihr werdet ihn im Schrein von Waldeslied finden, im Nachtweisenwald im Osten.",
    ["O"] = "Bringt Shindrells Notiz zu Kayneth Stillwind in Waldeslied.",
    ["T"] = "Kayneth Stillwind",
  },
  [4621] = {
    ["D"] = "Jetz\' hört mal zu, $Goller Seebär:olle Piratenbraut;... Wegen der Machthaber in Beutebucht könn\'n wir Blutsegel nich\' hinsegeln, wo wir woll\'n, und uns nich\' das nehm\'n, was wir als Beute ha\'m woll\'n. Tätet Ihr aber Flottenmeister Seehorn \'n Messer verpass\'n, dann würd\'n wir nie wieder mit \'nem Schiff aus Beutebucht aneinander geraten tun. Tätet Ihr Baron Revilgaz \'n Messer verpass\'n, dann müsst\'n wir uns nie wieder denen ihrem Gesetz stellen.$B$BWenn Ihr also \'n Admiral ehrenhalber bei uns werd\'n wollt - ja, ADMIRAL hab ich gesagt! - dann verpasst den beiden \'n Messer, wie ich\'s gesagt hab. Un\' jetz\' geht, $GJungchen:Mädel;, un\' kommt wieder, sobald Ihr\'s gemacht habt.",
    ["O"] = "Tötet Flottenmeister Seehorn und Baron Revilgaz aus Beutebucht und kehrt dann zu Flottenmeister Firallon zurück. Er befindet sich auf der Purpurschleier vor der Küste des Schlingendorntals.",
    ["T"] = "Segel setzen, Admiral!",
  },
  [4641] = {
    ["D"] = "Endlich seid Ihr alt genug, $N... alt genug, um im Namen der Horde zu kämpfen. Um für den Ruhm des Kriegshäuptlings Siege zu erringen.$B$BJa...$B$B<Kaltunk mustert Euch.>$B$BDoch, doch, Ihr eignet Euch.$B$BZweifellos wollt Ihr Euch einen mächtigen Drachen oder einen Dämon suchen und ihn mit bloßen Händen erwürgen, aber vielleicht ist es vernünftiger, wenn Ihr mit etwas weniger... Gefährlichem anfinget.$B$B<Kaltunk lacht.>$B$BMeldet Euch bei Gornek; der sollte Euch eine Aufgabe zuweisen können, die zu einem wie Euch passt, $C. Ihr findet Gornek im Höhlenbau im Westen.",
    ["O"] = "Sprecht mit Gornek. Euch fällt ein, dass Kaltunk auf Eurer Karte markiert hat, wo er sich befindet. Außerdem erwähnte er, dass Gornek sich im Höhlenbau aufhielte, einem Gebäude im Westen.",
    ["T"] = "Euer Platz in der Welt",
  },
  [4642] = {
    ["D"] = "Hier drin eingeschlossen ist einer von den Schlammen, die Ihr im Teufelswald eingesammelt habt. Bringt das Behältnis zum Krater von Un\'Goro und sucht dort einen reinen Schlamm - einer von den Urzeitschlammen wird\'s tun, denke ich. Sobald Ihr nahe genug heran seid, lasst den Schlamm aus dem Behältnis frei, und dann wollen wir doch mal sehen, was die beiden voneinander halten.$B$BBringt mir nach dem Ende des Experiments eine Probe von dem, was anschließend noch übrig ist. Ich erwarte Euch hier im Apothekarium.",
    ["O"] = "Bringt eine Probe verschmolzenem Schlamms zum Chemiker Brenner in Unterstadt.",
    ["T"] = "Vereinigung von Einflüssen",
  },
  [4701] = {
    ["D"] = "Begebt Euch zur Schwarzfelsspitze und vernichtet die Quelle der Bedrohung durch die Worgs. Als Ihr Helendis verlasst, ruft er Euch noch einen Namen hinterher: Halycon. Darauf beziehen sich die Orcs im Zusammenhang mit den Worgs.",
    ["O"] = "Begebt Euch zur Schwarzfelsspitze und vernichtet die Quelle der Bedrohung durch die Worgs. Als Ihr Helendis verlasst, ruft er Euch noch einen Namen hinterher: Halycon. Darauf beziehen sich die Orcs im Zusammenhang mit den Worgs.",
    ["T"] = "Stellt sie ab",
  },
  [4721] = {
    ["D"] = "Es sind Läufer aus Winterquell zurückgekehrt, die berichten, dass es im Süden von Winterquell eine Reihe kleiner Höhlen gibt. Es scheint, als würden sich im Gebiet um diese Höhlen herum Wildekin aufhalten. Falls sie wirklich irgendetwas Wertvolles bewachen, so ist das der Ort, an dem wir es finden werden, denke ich.$B$BIhr werdet feststellen, dass diese Wildekin eine etwas größere Herausforderung sind als die, mit denen Ihr Euch bisher herumgeschlagen habt, $N. Es scheint, als hätte genau das, was diese Kreaturen beschützen wollen, sie in den Wahnsinn getrieben.",
    ["O"] = "Trull Scheiterbann im Teufelswald möchte, dass Ihr in Winterquell 10 Berserkereulenbestien jagt.",
    ["T"] = "Wilde Wächter",
  },
  [4724] = {
    ["D"] = "Manche sagen, die vom Schwarzfelsklan kontrollierten Worgs seien durch eine übernatürliche Macht erschaffen worden. Andere wiederum sagen, es seien gar keine echten Worgs, sondern eine Art dämonische Hunde, die den Orcs von einem gefallenen Grubenlord überlassen wurden.$B$BDie Wahrheit ist wesentlich weniger weit hergeholt, aber nicht weniger erschreckend. Die Worgs des Schwarzfels stammen von Halycon und ihrem Partner. Halycon ist die gewaltige Höhlenmutter der Worgs. Und ihr Partner? Es hat noch niemand überlebt, der etwas über diesen Partner sagen konnte.$B$BTrefft die Worgs dort, wo es ihnen am meisten wehtut. Vernichtet Halycon...aber, $N, seid auf der Hut.",
    ["O"] = "Erschlagt Halycon, die Rudelführerin der Worgs der Blutäxte.",
    ["T"] = "Die Herrin der Meute",
  },
  [4726] = {
    ["D"] = "Schon seit Wochen studiere ich die Drachkin der Brennenden Steppe und ich glaube, mir ist endlich ein Durchbruch gelungen! Ich habe ein Gerät gebaut - ich habe es Draco-Incarcinatrix 900 getauft -, mit dem ich die Essenz von Großdrachenwelpenbrutlingen einfangen kann!$B$BOder zumindest glaube ich das.$B$BHier, nehmt das Gerät und zappt damit einen Brutling in der Brennenden Steppe. Wenn Ihr den Brutling dann besiegt, während er sich noch unter dem Einfluss des Geräts befindet, dann sollte eigentlich seine Essenz freigesetzt werden.$B$BWenn dem so ist, dann prima! Bringt mir ein paar Proben zurück, damit ich sie studieren kann.",
    ["O"] = "Bringt 8 Brutlingessenzen sowie den Draco-Incarcinatrix 900 zu Tinkee Kesseldampf auf dem Flammenkamm in der Brennenden Steppe.",
    ["T"] = "Brutlingessenz",
  },
  [4729] = {
    ["D"] = "Begebt Euch zur Schwarzfelsspitze und sucht Worgwelpen der Blutäxte. Benutzt den Käfig, um die wilden kleinen Bestien zu transportieren. Bringt einen eingesperrten Worgwelpen zu Kibler.",
    ["O"] = "Begebt Euch zur Schwarzfelsspitze und sucht Worgwelpen der Blutäxte. Benutzt den Käfig, um die wilden kleinen Bestien zu transportieren. Bringt einen eingesperrten Worgwelpen zu Kibler.",
    ["T"] = "Kiblers Exotische Tiere",
  },
  [4734] = {
    ["D"] = "Ich habe hart an einem neuen Gerät gearbeitet, $N - dem Eiszilloskop! Es ist so konstruiert, dass es einen Strahl kalter Energie auf das Ei eines schwarzen Großdrachen abgibt und es einfriert! Könnt Ihr das für mich ausprobieren?$B$BIch habe gehört, dass die Großdrachen weit oben auf der Schwarzfelsspitze einen Horst haben, wo sie ihre Eier ausbrüten. Da müsst Ihr hin, um das Eiszilloskop zu testen, und dann müsst Ihr mir sagen, wie es funktioniert hat!$B$BUnd seid unbesorgt. Gefrorene Dracheneier sollten harmlos sein. Bloß bei den ungefrorenen müsst Ihr Euch Sorgen machen!",
    ["O"] = "Benutzt den Prototyp des Eiszilloskops an einem Ei im Horst der Schwarzfelsspitze.",
    ["T"] = "Ei-Frosten",
  },
  [4735] = {
    ["D"] = "$N, ich bin endlich bereit für echte Dracheneier! Hier, nehmt dieses kollektronische Modul. Es ist so konstruiert, dass es ein Drachenei extrahiert und für den Transport vorbereitet. Kehrt zum Horst zurück und verwendet das Modul, um Eier einzusammeln.$B$BAber vergesst nicht, sie vorher einzufrieren, sonst schlüpfen sie vielleicht aus, ehe Ihr sie einsammeln könnt!$B$BWenn Ihr genug Eier habt, dann bringt sie mir.",
    ["O"] = "Bringt 8 eingesammelte Dracheneier sowie das kollektronische Modul zu Tinkee Kesseldampf auf dem Flammenkamm in der Brennenden Steppe.",
    ["T"] = "Eiersammlung",
  },
  [4736] = {
    ["D"] = "Ah, hallo, $N. Ihr macht bei Eurer Ausbildung gute Fortschritte. So gute Fortschritte, um genau zu sein, dass ich finde, Ihr solltet für Eure harte Arbeit belohnt werden - und dafür, dass Euch die arkanen Künste noch nicht verderbt haben.$B$BFalls Ihr Interesse habt, sucht Menara Leerbringer in Ratschet auf. Momentan wohnt sie in Strahad Farsans Turm über der Stadt.$B$BSie ist nicht sehr umgänglich, $N, also erwartet keine Lobeshymnen von ihr. Aber wenn Ihr hart arbeitet, dann wird sie sich gut um Euch kümmern.",
    ["O"] = "Sprecht mit Menara Leerbringer im Brachland.",
    ["T"] = "Auf der Suche nach Menara Leerbringer",
  },
  [4737] = {
    ["D"] = "Hmpf. Noch immer wollt Ihr mächtiger werden. Ich sage, es ist nur eine Frage der Zeit, ehe die Verderbnis und die Sucht sich Eurer bemächtigen und Ihr ein Sklave dessen werdet, über das Ihr Macht gewinnen wollt.$B$BPah! Ihr seid ein Dummkopf! Die Zeit wird mir Recht geben - nichts anderes, nur die Zeit.$B$BDoch es scheint, dass nicht alle derselben Meinung sind wie Zevrost. Es scheint, als wäre Menara bereit, Instrumente für Euch herzustellen, mit denen Ihr mächtiger werden und Eure Kräfte besser nutzen könnt. Falls Ihr Interesse habt, dann begebt Euch nach Ratschet im Brachland. Sie befindet sich dort über der Stadt.",
    ["O"] = "Sprecht mit Menara Leerbringer im Brachland.",
    ["T"] = "Auf der Suche nach Menara Leerbringer",
  },
  [4738] = {
    ["D"] = "Lasst uns eine Sache klarstellen, $N: Ich finde nicht, dass Ihr für eine solche Verantwortung schon bereit seid. Ich glaube nicht, dass ein Neuling bei der Erschaffung irgendeines Gegenstands der Macht helfen sollte, und schon gar nicht jemand, der noch so unerfahren ist.$B$BDoch diese Entscheidung liegt nicht bei mir. Diese Entscheidung treffen die maßgeblichen Stellen oder zumindest diejenigen, die Euch eine solche Gabe zukommen lassen wollen.$B$BFalls Ihr mehr darüber wissen wollt, dann schlage ich vor, Ihr sucht Menara Leerbringer in Ratschet auf. Für gewöhnlich hält sie sich in dem Turm über der Hafenstadt auf.",
    ["O"] = "Sprecht im Brachland mit Menara Leerbringer.",
    ["T"] = "Auf der Suche nach Menara Leerbringer",
  },
  [4739] = {
    ["D"] = "$C! Ja, Ihr!$B$BIhr habt noch nicht alles Handwerkszeug zusammen, aber es ist deutlich, dass Ihr bereit seid - Euch umgibt eine Aura größeren Selbstvertrauens. Ja, das erkenne ich jetzt.$B$BMenara Leerbringer kann Euch ein paar Dinge beibringen, wenn Ihr denkt, dass Ihr bereit seid. Sie unterrichtet jüngere Schüler in den arkanen Künsten. Sie macht denen, die ihre Prüfungen bestehen, sogar Geschenke.$B$BGeht nach Ratschet ins Brachland, wenn Ihr Interesse habt. Sie ist in Strahads Turm über der Stadt.",
    ["O"] = "Sprecht mit Menara Leerbringer im Brachland.",
    ["T"] = "Auf der Suche nach Menara Leerbringer",
  },
  [4740] = {
    ["D"] = "Achtung!$B$BEine Belohnung wird ausgesetzt für den Tod des Murlocs \"Gurgelbacke\". Diese üble Bestie ist nachgewiesenermaßen verantwortlich für den Tod mindestens einer Schildwache und wird verdächtigt, den Untergang von mindestens zwei Frachtschiffen in den Gewässern vor Dunkelküste verursacht zu haben!$B$BZuletzt wurde Gurgelbacke an einem Barackenlager der Murlocs südlich von Auberdine gesehen. Es wird angenommen, dass er die Hütten dort verteidigt. Das Kopfgeld kann bei Schildwache Glynda Nal\'Shea in Auberdine eingefordert werden.$B$BDer Dorfrat von Auberdine",
    ["O"] = "Sucht und tötet den Murloc namens Gurgelbacke. Man nimmt an, dass die Kreatur die Hütten der Murlocs am Strand südlich von Auberdine beschützt.$B$BInformiert Schildwache Glynda Nal\'Shea über Gurgelbackes Tod.",
    ["T"] = "GESUCHT: Gurgelbacke!",
  },
  [4741] = {
    ["D"] = "Die Wildekin in den nördlichen Bereichen von Winterquell werden immer aggressiver. Wir müssen unsere Aktivitäten dort fortsetzen.$B$BJagt die mondbestrahlten Eulenbestien von Winterquell und untersucht das Gebiet, das sie bewohnen.$B$BDie Gegend dort ist gefährlich, aber ich weiß, dass Ihr der Aufgabe gewachsen seid. Kehrt zu mir zurück, sobald Ihr 13 der Wildekin getötet habt.",
    ["O"] = "Trull Scheiterbann im Teufelswald möchte, dass Ihr in Winterquell 13 mondbestrahlte Eulenbestien jagt.",
    ["T"] = "Wilde Wächter",
  },
  [4743] = {
    ["D"] = "Nur wenige der schwarzen Drachkin außerhalb der oberen Zitadelle sind in der Lage, das Siegel mit ihrem eigenen Flammenatem zu schmieden. Wyrmthalak ist ein solcher Drachkin, doch sein Wille kann nicht gebrochen werden.$B$BIhr müsst zum Drachensumpf in den Düstermarschen reisen. Dort werdet Ihr einen alten Drachen namens Aschenschwinge finden. Seinen Willen müsst Ihr brechen, $N. Brecht seinen Willen und verwendet diese Kugel, sobald er geschwächt ist. Doch Ihr werdet nur wenige Sekunden haben, in denen Ihr seinen Geist kontrollieren und die Flammen anfachen könnt, mit denen das Siegel geschmiedet wird.",
    ["O"] = "Reist zum Drachensumpf in den Düstermarschen. Sucht den alten Drachen Aschenschwinge auf und bekämpft ihn ohne Gnade, bis sein Wille gebrochen ist.$B$BIn genau diesem Moment müsst Ihr das ungeschmiedete Siegel des Aufstiegs vor der großen Bestie hinlegen. Dann müsst Ihr schnell die Kugel der drachischen Energie auf seine geschwächte Gestalt anwenden und die Kontrolle über seine geistigen Kräfte übernehmen. Kontrolliert die Bestie und zwingt die Flammen des schwarzen Drachenschwarms auf das ungeschmiedete Siegel des Aufstiegs!",
    ["T"] = "Siegel des Aufstiegs",
  },
  [4762] = {
    ["D"] = "Der Klippenquell wird immer verderbter. Er mündet in Nebelrand und ich fürchte, dass sich das verunreinigte Wasser bald auf Auberdine auswirken wird. Ich halte die Furbolgs der Schwarzfelle flussaufwärts für den Auslöser der Besudelung, aber ich vermute auch, dass sie nicht die wahre Ursache sind.$B$BNehmt diesen Probenbehälter und begebt Euch zur Mündung des Flusses nördlich von hier. Geht weiter landeinwärts bis zum ersten Wasserfall und entnehmt dem Teich dort eine Probe. Oberhalb davon seht Ihr eine Brücke. Kommt zu mir nach Auberdine zurück, sobald Ihr die Probe habt.",
    ["O"] = "Geht von Auberdine aus nach Norden zum ersten Wasserfall des Klippenquell und entnehmt aus dem Teich dort eine Probe.$B$BKehrt mit der Probe des Klippenquells zu Thundris Windwirker nach Auberdine zurück.",
    ["T"] = "Der Klippenquell",
  },
  [4763] = {
    ["D"] = "Wir haben herausgefunden, dass die Satyrn ein Grund für die Verderbnis der Furbolgs sind. Sie sollen ihre Macht durch Talismane ausüben, mit denen sie ihre Magie kanalisieren können. Damit die Furbolgs Aussicht auf Rettung haben, müssen wir den Satyrverderber aus seinem Versteck locken und ihm diesen Talisman abnehmen!$B$BFüllt diese Schale an unserem Mondbrunnen und holt aus dem Lager der Furbolgs im Norden ein paar Proben ihrer Lebensmittel. Mischt alles zusammen und stellt die Schale in die Nähe des Freudenfeuers am Fluss. Sobald die Furbolgs davon essen, werden sie lang genug gesäubert, um den Satyrverderber herauszulocken... Den Ihr dann töten müsst!",
    ["O"] = "Füllt die leere Säuberungsschale am Mondbrunnen in Auberdine.$B$BHolt jeweils eine Probe von Früchten, Nüssen und Getreide aus dem Lager der Schwarzfellfurbolgs im Norden.$B$BMischt alles in der Schale und stellt diese bei dem Freudenfeuer neben dem Klippenquell auf Höhe des nördlichen Lagers ab und lockt dadurch den Satyrverderber an.$B$BNehmt den Talisman der Verderbnis und bringt ihn zu Thundris Windwirker in Auberdine.",
    ["T"] = "Die verderbten Schwarzfelle",
  },
  [4764] = {
    ["D"] = "Hoch droben auf der Schwarzfelsspitze gibt es eine Kammer, die einst \'Halle des Glücks\' genannt wurde. Dort bewahrten die Dunkeleisenzwerge ihre Relikte, Trophäen und Kunstobjekte auf.$B$BDoch das ist schon lange her. Man sagt, dass die Halle eingestürzt sei und jetzt für... weniger hehre Zwecke genutzt wird. Ich glaube jedoch, dass man dort immer noch so manch altes Zwergenrelikt finden kann.$B$BEines dieser Relikte ist Doomriggers Schnalle, deren Oberfläche so reich mit seltenen Edelsteinen verziert ist, dass angeblich jeder Zwerg bei ihrem Anblick in Verzückung geraten soll.$B$BDiese Schnalle ist Euer Ziel, $N.",
    ["O"] = "Bringt Mayara Wolkenglanz in der Brennenden Steppe Doomriggers Schnalle.",
    ["T"] = "Doomriggers Schnalle",
  },
  [4765] = {
    ["D"] = "Ich habe Doomriggers Schnalle in eine Kiste gepackt, damit sie nach Sturmwind geschickt werden kann. $N, Ihr wart wirklich eine große Hilfe - darf ich Euch noch um einen letzten Gefallen bitten?$B$BBringt Graf Remington Kronenbrunn die Kiste. Ihr findet ihn in der Burg von Sturmwind. Ich bin sicher, dass er sich sehr über das freuen wird, was Ihr ihm bringt.",
    ["O"] = "Bringt Kronenbrunns Kiste zu Graf Remington Kronenbrunn nach Burg Sturmwind in Sturmwind.",
    ["T"] = "Lieferung an Kronenbrunn",
  },
  [4766] = {
    ["D"] = "Eine meiner Agentinnen, Mayara Wolkenglanz, wurde in die Brennende Steppe entsandt, um die in Ruinen liegende Stadt Thaurissan zu erkunden. Diese zerstörte Stadt der Dunkeleisenzwerge könnte sehr wertvolle Objekte bergen... Objekte, die gar trefflich in unser Familienmuseum passen würden.$B$BMayara sagt, dass sie etwas gefunden hätte und nun Hilfe bräuchte, um es zu holen, allerdings befindet es sich nicht in Thaurissan. Stattdessen ist eine Exkursion zur Schwarzfelsspitze nötig.$B$BIch weiß nichts Genaues, aber sprecht doch mit Mayara in der Brennenden Steppe, falls Ihr interessiert seid.",
    ["O"] = "Sprecht mit Mayara Wolkenglanz in Morgans Wacht in der Brennenden Steppe.",
    ["T"] = "Mayara Wolkenglanz",
  },
  [4767] = {
    ["D"] = "Mir wurde aufgetragen, weitere Wyvern für die Meisterwindreiter zu rekrutieren. Sie werden immer mehr und ich hoffe, selbst eines Tages ein Windreiter zu werden. Wie dem auch sei, wir können gar nicht genug Wyvern ausbilden, um der Nachfrage gerecht zu werden. Vielleicht könnt Ihr mir helfen, $N.$B$BIch möchte, dass Ihr zum Steilhang reist, um dort ein paar Wyverneier zu sammeln. Geht von hier aus nach Nordwesten entlang der Talwände, bis zu einer Rampe, die in das Gebiet des Steilhangs hineinführt. Seid vorsichtig, Wyvern haben einen starken Beschützerinstinkt, was ihre Eier angeht, und schätzen keine Eindringlinge.",
    ["O"] = "Bringt Elu am Freiwindposten 10 Eier von Steilhangwyvern.",
    ["T"] = "Windreiter",
  },
  [4768] = {
    ["D"] = "Die Dunkelsteinschrifttafel enthält die Niederschriften des vor langer Zeit verstorbenen Zwergs Urheld Dunkelstein. Er war ein äußerst fähiger Alchemist, den seine Zeitgenossen für verrückt hielten und der seine Forschungen daher für sich behielt.$B$BEr hielt jedoch alles auf Schrifttafeln fest und ich glaube, dass eine dieser Schrifttafeln auf der Schwarzfelsspitze zu finden ist, in einer Kammer, die einst die \'Halle des Glücks\' genannt wurde. Ich weiß nicht, wie man sie heute bezeichnet, denn nur wenige reisen zum Schwarzfels und kehren heil zurück.$B$BFalls Ihr jedoch gewillt seid, Euch den Gefahren dort zu stellen, und mir diese Schrifttafel bringt, erwartet Euch eine große Belohnung.",
    ["O"] = "Bringt der Schattenmagierin Vivian Lagrave in Kargath die Dunkelsteinschrifttafel.",
    ["T"] = "Die Dunkelsteinschrifttafel",
  },
  [4769] = {
    ["D"] = "Vivian Lagrave aus Kargath im Ödland hat sich an mich gewandt. Aufgrund ihrer Studien ist sie davon überzeugt, dass es irgendwo auf der Schwarzfelsspitze eine alte Niederschrift gibt, die nützlich für unsere Forschung sein könnte. Um dies zu bestätigen, benötigt sie jemanden, der diesen Text von der Schwarzfelsspitze holt. Das ist keine leichte Aufgabe.$B$BFalls Ihr interessiert seid, sprecht in Kargath mit Vivian.",
    ["O"] = "Sprecht mit Schattenmagierin Vivian Lagrave.",
    ["T"] = "Vivian Lagrave und die Dunkelsteinschrifttafel",
  },
  [4770] = {
    ["D"] = "Bitte helft mir, $C! Ich verstecke mich jetzt schon eine ganze Weile hier am Steilhang und seit Tagen habe ich kein freundliches Gesicht mehr gesehen. Ich kam hierher, um die Wyvern zu studieren, und bin dabei wohl etwas zu dicht herangekommen. Nachdem ich von einem der Nester verjagt wurde, habe ich mich verlaufen.$B$BIch muss zum Weißgipfelposten zurückkehren! Motega weiß nicht, dass ich vermisst werde, und er wird wütend sein, wenn er hört, dass ich allein zum Steilhang gegangen bin.$B$BDer Weißgipfelposten liegt direkt östlich von hier... bringt mich bitte dorthin!",
    ["O"] = "Begleitet Pao\'ka Bergrausch aus dem Steilhang heraus und sprecht dann am Weißgipfelposten mit Motega Feuermähne.",
    ["T"] = "Auf der Heimreise",
  },
  [4771] = {
    ["D"] = "Ich habe die Dämmerungstrickfalle in den Behälter gelegt, den Ihr uns gebracht habt, denjenigen, in dem sich die Dracheneier befanden. Euer Auftrag besteht darin, sie tief nach Scholomance hineinzubringen.$B$BDer untote Magier Vectus führt ein Team von Gelehrten der Geißel an... legt die Dämmerungstrickfalle in ihre Kammer, genannt der \"Vorführraum\". Sie wird dann mit den dort gefundenen Untoten reagieren und sie mit ein wenig Glück vernichten.$B$BDanach sollte es kein Problem sein, Vectus zu besiegen. Nicht wahr?",
    ["O"] = "Legt die Dämmerungstrickfalle in den Vorführraum von Scholomance. Besiegt Vectus und kehrt dann zu Betina Moppelzink zurück.",
    ["T"] = "Dämmerungstrickfalle",
  },
  [4781] = {
    ["D"] = "Eure nächste Aufgabe ist so einfach wie die erste, allerdings werdet Ihr jetzt Hilfe benötigen.$B$BIch möchte einen Umhang besticken und benötige dazu Goldfaden, allerdings ist dieses Garn recht schwer zu beschaffen, und nur wenige wissen, wie man es herstellt. Glücklicherweise kennt jemand, der unsere Sache unterstützt, dieses Geheimnis und ist gewillt, uns zu helfen.$B$BBringt Xizk Gutstich in Beutebucht einen Goldbarren. Er ist ein Schneidermeister und wird mit Freuden das Garn für Euch spinnen. Goldbarren könnt Ihr von geschickten Minenarbeitern einschmelzen lassen. Bittet einfach einen Minenarbeiter um Hilfe.",
    ["O"] = "Bringt Xizk Gutstich im Schlingendorntal einen Goldbarren.",
    ["T"] = "Komponenten für die verzauberte Goldblutrobe",
  },
  [4782] = {
    ["D"] = "Kehrt zu Menara zurück und sagt Ihr, dass es eine Weile dauern wird, aber ich werde auf jeden Fall auf sie... und Euch zurückkommen. Sagt Ihr, dass Ihr zunächst noch die restlichen Komponenten zusammentragen müsst - bis dahin sollte ich das Garn fertig haben. Ihr könnt dann wieder herkommen und es mitnehmen.$B$BViel Glück, $N. Oh, und grüßt Strahad von mir, wenn Ihr schon mal dort seid. Der alte Zausel... hat doch wirklich kaum einen Steinwurf von Ratschet einen Turm errichtet... verrückt.",
    ["O"] = "Kehrt zu Menara Leerbringer im Brachland zurück.",
    ["T"] = "Komponenten für die verzauberte Goldblutrobe",
  },
  [4783] = {
    ["D"] = "Die nächsten Komponenten findet Ihr in Desolace, weit westlich des Brachlandes und sogar des Steinkrallengebirges.$B$BNach der Zerschlagung herrscht dort noch immer das reine Chaos. Sowohl Eure Kraft als auch Eure Schlauheit werden dort auf die Probe gestellt werden - Ihr werdet Euch Kreaturen voller Hass und sogar der Brennenden Legion stellen müssen.$B$BBringt mir etwas Blut von den Satyrn der Zornsäer im Nordosten. Und dann bringt mir noch einen geringen Höllenstein von den geringen Höllenbestien beim Zirkel der Mannoroc.$B$BWir brauchen beides, um daraus eine Robe für Euch zu fertigen.",
    ["O"] = "Bringt Menara Leerbringer im Brachland 10 Phiolen Zornsäerblut und 1 geringen Höllenstein.",
    ["T"] = "Komponenten für die verzauberte Goldblutrobe",
  },
  [4784] = {
    ["D"] = "Ich brauche jetzt nur noch drei Dinge, $N, dann kann ich Eure Robe fertig stellen.$B$BZuerst brauche ich das feine Goldgarn, das Xizk für Euch gesponnen hat. Kehrt zu ihm nach Beutebucht zurück und holt es.$B$BAls Zweites brauche ich ein paar glimmende Kohlen von den Feuerelementaren im Arathihochland. Wenn ich mich recht erinnere, leben sie im Westen in der Nähe des Thoradinswalls.$B$BAls Letztes benötige ich dann noch einen von Euch geschaffenen Seelensplitter.$B$BKommt hierher zum Turm zurück, sobald Ihr alle Komponenten habt, dann stelle ich Eure Robe fertig.",
    ["O"] = "Bringt Menara Leerbringer im Brachland Goldgarn, 2 glimmende Kohlen und einen Seelensplitter.",
    ["T"] = "Komponenten für die verzauberte Goldblutrobe",
  },
  [4786] = {
    ["D"] = "Jetzt schneidere ich die Robe für Euch. Es dauert nur einen Augenblick. Geht nicht zu weit fort, ja?",
    ["O"] = "Wartet, bis Menara Leerbringer Eure Robe fertiggestellt hat, und sprecht dann noch einmal mit ihr.",
    ["T"] = "Die fertiggestellte Robe",
  },
  [4787] = {
    ["D"] = "Mosh\'arus Prophezeiung spricht von einem uralten Ei. Es ist ein Relikt aus einer Zeit, als die Trolle noch über riesige Reiche herrschten, und es hat die Kraft, die Essenz von Hakkar zu binden. Möglicherweise ist es das einzige Mittel, um unsere Welt vor diesem Bösen zu schützen.$B$BBringt mir das uralte Ei, $N. Man sagt, dass es tief verborgen in Jintha\'Alor im Hinterland liegt, in einer Höhle hinter dem Amphitheater am obersten Punkt der Stadt.",
    ["O"] = "Bringt das uralte Ei zu Yeh\'kinya nach Tanaris.",
    ["T"] = "Das uralte Ei",
  },
  [4788] = {
    ["D"] = "Während ich die dritte und vierte Schrifttafel von Mosh\'aru überprüfe, müsst ihr die fünfte und sechste finden! Es wird nicht einfach sein, da sie in den Händen der Trolle des Gluthauerstamms auf der Schwarzfelsspitze sind. Die Spitze ist ein dunkler Berg aus Feuer zwischen der Brennenden Steppe und der Sengenden Schlucht.$B$BViel Glück, $N. Die Schwarzfelsspitze ist kein Ort für anständige Leute...",
    ["O"] = "Bringt Ausgrabungsleiter Eisenschuh in Tanaris die fünfte und sechste Schrifttafel von Mosh\'aru.",
    ["T"] = "Die letzten Schrifttafeln",
  },
  [4801] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "E\'ko eines Frostsäblers",
  },
  [4804] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "E\'ko einer Eiswindschimäre",
  },
  [4805] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "E\'ko eines Eisdistelyetis",
  },
  [4807] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "E\'ko eines Wildekins",
  },
  [4808] = {
    ["D"] = "Ich habe noch eine weitere kleine Bitte an Euch.$B$BMeine anfänglichen Studien bezüglich der Essenz, die Ihr gesammelt habt, haben ergeben, dass es wohl möglich ist, die Eier der Großdrachenwelpen einzufrieren und so zu konservieren! Ich möchte diese Theorie überprüfen, aber zuerst benötige ich speziell angefertigte Komponenten der Ingenieurskunst. Mein Kollege Felnok Stahlfeder kann sie herstellen.$B$BHier, bringt Felnok diesen Brief. Darin steht, was ich benötige. Ihr findet Felnok in Winterquell in der Ewigen Warte.",
    ["O"] = "Gebt Felnok Stahlfeder in der Ewigen Warte in Winterquell Tinkees Brief.",
    ["T"] = "Felnok Stahlfeder",
  },
  [4809] = {
    ["D"] = "Ich habe fast alles, was Tinkee benötigt, aber mir fehlt eine wichtige Komponente, und das sind Eiswindschimärenhörner. Die Hörner der Eiswindschimären von Winterquell sind eine konzentrierte Kältequelle, aber man kann nur makellose, unbeschädigte Hörner verwenden! Ich brauche ein paar davon, um Tinkees Auftrag zu erfüllen.$B$BBringt mir unbeschädigte Hörner der Eiswindschimären, dann schnüre ich ein Päckchen für Tinkee zusammen.",
    ["O"] = "Beschafft Euch 8 unbeschädigte Hörner.",
    ["T"] = "Hörner der Eiswindschimären",
  },
  [4810] = {
    ["D"] = "Gut, alles ist bereit und verpackt und transportfertig! Viel Glück auf Eurer Reise und richtet Tinkee aus, dass sie mir was schuldet!",
    ["O"] = "Bringt Tinkee Kesseldampf auf dem Flammenkamm in der Brennenden Steppe Felnoks Päckchen.",
    ["T"] = "Rückkehr zu Tinkee",
  },
  [4811] = {
    ["D"] = "Im Osten streifen immer mehr feindliche Mondkin umher. Man dachte einst, dass sie sanftmütige, fast mystische Kreaturen wären. Wenn auch viele ihnen weiterhin Ehrfurcht entgegenbringen, zwingt mich die Sicherheit Auberdines, die Sache realistischer zu sehen.$B$BIch habe Berichte erhalten, dass es die Mondkin zu einem großen roten Kristall an der östlichen Bergkette von Dunkelküste zieht. Niemand weiß, was dieser Kristall ist oder ob er wirklich existiert. Ich möchte, dass Ihr ihn dort sucht und mir meldet, was Ihr vorgefunden habt.",
    ["O"] = "Reist von Auberdine nach Osten und sucht nach einem großen, roten Kristall entlang der östlichen Bergkette von Dunkelküste. Informiert Schildwache Glynda Nal\'Shea in Auberdine über Eure Erkenntnisse.",
    ["T"] = "Der rote Kristall",
  },
  [4821] = {
    ["D"] = "Es wurde ein Gerücht über ein fremdartiges Ei hier in Tausend Nadeln in Umlauf gebracht. Diejenigen, die dieses Ei gesehen haben, konnten aber nicht nahe genug herankommen, um es ausführlich zu untersuchen. Schlangen bewachen das Ei, als wäre es eines ihrer eigenen.$B$BIch möchte, dass Ihr dieses fremdartige Ei sucht und es mir bringt, damit ich es untersuchen kann. Berichten zufolge soll sich das Ei in einem Schlangenbau befinden, aber es gibt mehrere Schlangenbaue entlang des Fußes der Klippenwand südlich und nordöstlich des Freiwindpostens.",
    ["O"] = "Bringt Hagar Blitzhuf am Freiwindposten das fremdartige Ei.",
    ["T"] = "Fremdartiges Ei",
  },
  [4841] = {
    ["D"] = "Tötet 12 Späher der Galak, 10 Zänker der Galak sowie 6 Windjäger der Galak und kehrt dann zu Klippenbehüter Langhorn am Freiwindposten zurück.",
    ["O"] = "Tötet 12 Späher der Galak, 10 Zänker der Galak sowie 6 Windjäger der Galak und kehrt dann zu Klippenbehüter Langhorn am Freiwindposten zurück.",
    ["T"] = "Befriedung der Zentauren",
  },
  [4842] = {
    ["D"] = "Also mal ehrlich, es gibt hier in Winterquell zwar keine Vulkane, aber ich glaube, dass es ein paar viel gefährlichere Ursachen für diese heißen Quellen gibt...$B$BTja, Ihr möchtet Euch das bestimmt lieber mit eigenen Augen ansehen. Ihr müsst dazu ganz Winterquell durchqueren und ganz nach Süden reisen. Wenn die Luft irgendwie stickig und schwül wird und sich der Himmel verdunkelt, habt Ihr gefunden, was ich für die Ursache der heißen Quellen halte.$B$BSeht es Euch an. Ich bin sicher, dass Ihr mit einer Menge Fragen zurückkehren werdet.",
    ["O"] = "Befolgt Donova Schneewehs Anweisungen und meldet Euch dann wieder bei ihr.",
    ["T"] = "Sonderbare Quellen",
  },
  [4861] = {
    ["D"] = "Hallo, $GBursche:Mädchen;! Ein paar blutrünstige Wildekin haben mich aus meinem Lager im südlichen Winterquell vertrieben! Nun, ich schätze, ich bin irgendetwas zu nahe gekommen, das die Wildekin bewachen.$B$BIch hab es gerade noch geschafft, mit heiler Haut davonzukommen, aber ich fürchte, einer meiner Freunde hatte nicht so viel Glück. Würde es Euch wohl etwas ausmachen, Euch der Gefahr durch die Wildekin zu stellen und zum Lager zu gehen? Ich brauche unbedingt meine Vorräte und hätte auch gern das Amulett zurück, das ich gestern an der Ausgrabungsstätte zu Tage gefördert habe.",
    ["O"] = "Findet Jaron Steinschleifers ruiniertes Lager samt dessen Vorräten im südlichen Winterquell.",
    ["T"] = "Wütende Wildekin",
  },
  [4862] = {
    ["D"] = "Ob Ihr es glaubt oder nicht, da draußen gibt es Leute, die gutes Geld selbst für die übelsten der abgerichteten Bestien ausgeben würden!$B$BIch würde ja Bijou zur Spitze schicken, um ein paar dieser Spitzenspinneneier zu sammeln, aber ich habe seit Wochen nichts mehr von ihr gehört. Vielleicht könnt Ihr mir helfen?$B$BIch sag Euch was, $N. Geht zur Schwarzfelsspitze und sammelt ein paar Spitzenspinneneier für mich. Bringt sie mir, dann werde ich Euch nicht nur etwas Bargeld geben, sondern auch eines der Viecher für Euch abrichten!",
    ["O"] = "Reist zur Schwarzfelsspitze und sammelt 15 Spitzenspinneneier für Kibler.$B$BLogischerweise müsstet Ihr diese Eier in der Nähe von Spinnen finden.",
    ["T"] = "Be-Öh-Es-Eh",
  },
  [4863] = {
    ["D"] = "Ihr seht Euch um und könnt nichts entdecken, was Jaron wohl noch brauchen könnte. Ihr stellt jedoch fest, dass die Kiste, die Ihr fandet, aufgebrochen wurde, als ob es die Wildekin darauf abgesehen hatten...$B$BAls Ihr nach Osten blickt, erkennt Ihr den vagen Umriss eines zurückgelassenen Wagens. Vielleicht findet Ihr Jarons Sachen ja dort. Nachsehen kann nicht schaden.",
    ["O"] = "Sucht Jaron Steinschleifers zerstörten Wagen.",
    ["T"] = "Wütende Wildekin",
  },
  [4864] = {
    ["D"] = "Nachdem Ihr den Inhalt des Kastens inspiziert habt, befindet Ihr, dass Jaron die Werkzeuge und anderen Gegenstände darin sicher gebrauchen könnte. Von dem Amulett fehlt jedoch jede Spur.$B$BDie Wildekin scheinen die Landstriche nördlich und östlich von hier zu bewohnen, also beschließt Ihr, dass Euer nächster Schritt sein wird, die Kreaturen aufzustöbern und das Amulett von ihnen einzufordern.",
    ["O"] = "Klaubt Jarons Vorräte aus dem Schnee und sucht dann die Wildekin, die das Amulett gestohlen haben, von dem Jaron gesprochen hatte.$B$BBringt Jaron Steinschleifer in der Lodge in Winterquell dessen Vorräte und das blaugefiederte Amulett.",
    ["T"] = "Wütende Wildekin",
  },
  [4865] = {
    ["D"] = "Wenn ich es nicht mit eigenen Augen gesehen hätte, würde ich es nie für möglich gehalten haben. Die Rache kam nach Tausend Nadeln!$B$B<Hagar wischt sich den Schweiß von der Stirn.>$B$B$N, Ihr müsst Euch beeilen! Geht sofort los und sucht Motega Feuermähne auf. Ihr findet ihn im Weißgipfelposten nordwestlich des Freiwindpostens, entlang der Straße. Er wird wissen, was zu tun ist!",
    ["O"] = "Berichtet Motega Feuermähne von Euren Entdeckungen. Zu finden ist er im Weißgipfelposten in Tausend Nadeln.",
    ["T"] = "Wilde Schlange",
  },
  [4866] = {
    ["D"] = "Das dürfte eines der stärksten Gifte der Welt sein, $R!$B$BIch habe bei meinen Ausflügen zur Spitze immer wieder gehört, wie die Schwarzfelsorcs davon sprachen, wie tödlich es sei. Sie nennen es \'Muttermilch\', benannt nach Mutter Glimmernetz, der großen, grausamen Spinne in der Listspinnerhöhle.$B$BAlso, das müsst Ihr mir besorgen: eine frische Probe dieser Milch, die durch EURE Adern fließt. Mir einfach nur die Giftdrüse zu bringen, wäre nicht genug! Bringt sie dazu, Euch zu vergiften, und kommt dann zu mir zurück!$B$BDann... kann ich Euch melken!$B$B<Reibt sich voller Vorfreude die Hände.>",
    ["O"] = "Ihr findet Mutter Glimmernetz im Herzen der Schwarzfelsspitze. Kämpft mit ihr und bringt sie dazu, Euch zu vergiften. Es kann gut sein, dass Ihr sie sogar töten müsst. Kehrt zum struppigen John zurück, sobald Ihr vergiftet seid, damit er Euch \'melken\' kann.",
    ["T"] = "Muttermilch",
  },
  [4881] = {
    ["D"] = "Warnt Kanati Grauwolke am Weißgipfelposten, dass man vorhat, ihn zu töten.",
    ["O"] = "Warnt Kanati Grauwolke am Weißgipfelposten, dass man vorhat, ihn zu töten.",
    ["T"] = "Auftragsmordplan",
  },
  [4882] = {
    ["D"] = "Ihr seid Euch unsicher bezüglich der Bedeutung der Halskette, die vom Hals des gerade erschlagenen Wildekin stammt. Sie glüht hell, verblasst und wird wieder hell.$B$BTrull Scheiterbann kann Euch vielleicht mehr darüber sagen, da er über sehr großes Wissen über die Wildekin verfügt. Vielleicht solltet Ihr ihn darüber befragen.",
    ["O"] = "Bringt Trull Scheiterbann beim Blutgiftposten das blaugefiederte Amulett.",
    ["T"] = "Geheimnisse bewahren",
  },
  [4883] = {
    ["D"] = "Auch wenn ich viel über die Eulenbestien weiß, die Ihr in Winterquell erledigt habt, bin ich in der Magie, die sie wirken, nicht wirklich bewandert. Aber ich glaube, dass ich jemanden kenne, der die Kette untersuchen und Euch sagen könnte, was es ist.$B$BNara Wildmähne in Donnerfels ist eine Expertin auf diesem Gebiet; trefft Euch mit ihr. Viel Glück, $N.",
    ["O"] = "Bringt Nara Wildmähne das blaugefiederte Amulett.",
    ["T"] = "Geheimnisse bewahren",
  },
  [4901] = {
    ["D"] = "Ganz oben auf dem Grat liegt das, was ich für einen Altar der Göttin Elune halte. Soweit ich das sagen kann, müssen wir in jeder Höhle fünf Fackeln anzünden. Dann können wir den eigentlichen Altar erreichen.$B$BIch habe einige der Zauberformeln übersetzt, die in den Stein der alten Ruinen von Kel\'Theril geritzt sind. Meiner Meinung nach sind sie der Schlüssel, mit dem man den Altar freigeben kann. Ich brauche Eure Hilfe dabei, alle Fackeln anzuzünden, während ich den Zauber rezitiere.$B$BWenn wir Erfolg haben, könnt Ihr Erelas unseren Fund melden!",
    ["O"] = "Schützt Ranshalla, während sie versucht, den Altar von Elune wieder zu aktivieren. Berichtet Erelas Himmelsbern in Rut\'theran von Eurem Fund.",
    ["T"] = "Wächter des Altars",
  },
  [4902] = {
    ["D"] = "Das ist faszinierend!$B$BIch habe mich immer gefragt, was die Wahrheit ist... Nun, Ihr müsst dieses Wissen auf alle Fälle teilen! Geht sofort zum Erzdruiden Fandral Hirschhaupt. Ihr werdet ihn in Darnassus im Zirkel des Cenarius antreffen. Erklärt ihm, was passiert ist, genau so wie Ihr es mir erzählt habt. Ich bin sicher, es wird ihn ebenso interessieren!$B$BDas sind wichtige Informationen, $N!",
    ["O"] = "Sprecht mit Erzdruide Fandral Hirschhaupt in Darnassus.",
    ["T"] = "Wildekin von Elune",
  },
  [4903] = {
    ["D"] = "Auf Befehl von Kriegsherr Bluthauer müssen die nachfolgenden Bewohner der Schwarzfelsspitze eliminiert werden:$B$BDie aufgeblasene Bedrohung, Hochlord Omokk.$B$BDer grausame und erbarmungslose Troll Kriegsmeister Voone.$B$BOberanführer Wyrmthalak, Zuchtmeister der geringen Stadt.$B$BDes Weiteren müsst Ihr alle wichtigen Dokumente zurückbringen, die Ihr möglicherweise findet.$B$BSeid erfolgreich und macht Euch Ehre.$B$BVersagt und seid vergessen.",
    ["O"] = "Tötet Hochlord Omokk, Kriegsmeister Voone und Oberanführer Wyrmthalak. Findet die wichtigen Schwarzfelsdokumente. Kehrt zum Kriegsherrn Bluthauer nach Kargath zurück, sobald Ihr diese Mission erledigt habt.",
    ["T"] = "Befehl des Kriegsherren Bluthauer",
  },
  [4904] = {
    ["D"] = "Arnak glaubt, dass wir heiraten werden… aber nachdem ich seinen Antrag abgelehnt hatte, kehrte er in der Nacht zurück und entführte mich. Mein Bruder wollte ihn aufhalten, aber Arnak war einfach zu stark. Er... er tötete meinen Bruder, dann brachte er mich hierher. Mein armer Bruder...$B$B$N, würdet Ihr mich wohl vom Düsterwolkengipfel hinab geleiten? Bitte... ich muss zu Thalia zurückkehren! Sie macht sich bestimmt große Sorgen um mich.",
    ["O"] = "Geleitet Lakota Windweise vom Düsterwolkengipfel hinab und sprecht dann im Freiwindposten mit Thalia Bernhaut.",
    ["T"] = "Endlich frei",
  },
  [4905] = "_",
  [4906] = {
    ["D"] = "Ein weiterer Satyrstützpunkt unter der Leitung des grausamen Xavaric bedroht den Teufelswald im Norden. Die elenden Verräter und Schwindler zehren von der Verderbnis der im Teufelswald heimischen Kreaturen.$B$BXavathras und seine Diener waren nur Teil eines größeren Plans - ein Mondbrunnen sollte verderbt werden und Xavaric sollte dann die Beute bekommen.$B$BDa Xavathras jetzt beseitigt ist, solltet Ihr diese Aufgabe beenden, $N. Geht nach Norden und besiegt Xavaric.",
    ["O"] = "Eridan Blauwind im Teufelswald möchte, dass Ihr Xavaric sowie 8 Jadefeuerhöllenrufer, 8 Jadefeuerverräter und 8 Jadefeuerschwindler tötet. Kehrt zu Eridan zurück, sobald Ihr die Aufgabe erfüllt habt.",
    ["T"] = "Weitere Verderbnis",
  },
  [4907] = {
    ["D"] = "Hallo, $N. Erinnert Ihr Euch an Tinkee Kesseldampf? Tja, sie erinnert sich jedenfalls an Euch! Sie schickte mir eine Nachricht, dass ich nach Euch Ausschau halten soll... sie braucht nämlich nochmals Eure Hilfe. Ich glaube, es hat was mit ihren Drachenstudien zu tun.$B$BIhr solltet mit ihr sprechen. Sie ist in der Brennenden Steppe, beim Flammenkamm.",
    ["O"] = "Sprecht mit Tinkee Kesseldampf auf dem Flammenkamm in der Brennenden Steppe.",
    ["T"] = "Tinkee Kesseldampf",
  },
  [4921] = {
    ["D"] = "Wir wurden getrennt, als wir in einem kleinen Taurenlager gemeinsam kämpften - sie hat drei Borstennacken im Alleingang bezwungen. Aber dann waren sie plötzlich in der Überzahl. Ich lockte ein paar fort, dann sah ich, wie sie von Neuankömmlingen überwältigt wurde. Rasend vor Zorn stellte ich mich meinen Feinden, aber da es derer so viele waren, ging ich schon bald zu Boden.$B$BIch kam im Zelt eines Taurendruiden zu mir, der meine Wunden versorgte - er kam zufällig die Goldstraße entlang, als ich fiel.$B$BBitte, $C, sucht nach einem Lebenszeichen von meiner Frau.",
    ["O"] = "Sucht Mankriks Frau und kehrt dann zu ihm in die Siedlung beim Wegekreuz zurück.",
    ["T"] = "In der Schlacht vermisst",
  },
  [4941] = {
    ["D"] = "Rend lebt?$B$BUnmöglich!$B$BEr soll doch schon vor Jahrzehnten getötet worden sein.$B$BBittet Etrigg, seine Weisheit mit Euch zu teilen, $N. Niemand kennt den Schwarzfelsklan besser als er, und wenn es wahr ist, was hier geschrieben steht, muss Etrigg davon in Kenntnis gesetzt werden. Niemandem sollte das Recht auf Vergeltung versagt werden.$B$BIhr findet ihn in Orgrimmar.$B$BWenn Ihr mit Etrigg gesprochen habt, beratet Euch mit dem Kriegshäuptling, um von ihm zu erfahren, was er bezüglich dieses Problems zu unternehmen gedenkt.",
    ["O"] = "Sprecht mit Etrigg in Orgrimmar. Wenn Ihr die Angelegenheit mit Etrigg besprochen habt, bittet Thrall um Rat.$B$BIhr erinnert Euch, dass Ihr Etrigg in Thralls Gemächern gesehen habt.",
    ["T"] = "Etriggs Weisheit",
  },
  [4961] = {
    ["D"] = "Ah, eine Beschwörerkugel - sie muss die Quelle der Besudelung sein. Wir müssen erst den Dämon darin exorzieren, wenn Ihr sie benutzen wollt. Das ist ein Klacks für mich.$B$BIhr habt die schwere Aufgabe. Ihr müsst den Dämon töten, sobald ich ihn freigesetzt habe.$B$BUnd Ihr müsst Euch beeilen. Wenn Ihr zu lange braucht, kehrt er in die Kugel zurück und wir müssen von vorn anfangen!$B$BAlso macht Euch bereit, $N. Bereitet Eure stärksten Zauber vor, habt Eure Tränke parat und holt einen Freund, wenn Ihr einen habt. Denn das hier wird nicht angenehm.",
    ["O"] = "Tötet den Dämon der Kugel und sprecht dann mit Tabetha.",
    ["T"] = "Die Säuberung der Kugel von Orahil",
  },
  [4962] = {
    ["D"] = "Ihr seid also entschlossen, die Seele eines Teufelshundes zu fangen, gut.$B$BNehmt diesen Rubin - mit ihm könnt Ihr die Seele der Kreatur einfangen. Er funktioniert auf ähnliche Weise wie die Zauber, die Ihr bereits kennt: Wenn die Kreatur dem Tode nahe ist, benutzt den Edelstein, er sollte dann den Rest für Euch erledigen.$B$BIch schlage vor, dass Ihr nach Desolace geht und versucht, dort eines Teufelshundes habhaft zu werden. Die sollten geeignet sein.$B$BWenn Ihr Erfolg hattet, bringt den gefangenen Geist und Euren Rubin zu Menara.",
    ["O"] = "Nehmt den Teufelsrubin und benutzt ihn bei einem der Teufelshunde von Desolace. Wenn Ihr Erfolg hattet, bringt Menara Leerbringer im Brachland den eingekerkerten Teufelshundgeist und den Teufelsrubin.",
    ["T"] = "Splitter eines Teufelshundes",
  },
  [4963] = {
    ["D"] = "Ihr seid also entschlossen, die Seele einer Höllenbestie zu fangen, gut.$B$BNehmt diesen Smaragd - mit ihm könnt Ihr die Seele der Kreatur einfangen. Er funktioniert auf ähnliche Weise wie die Zauber, die Ihr bereits kennt: Wenn die Kreatur dem Tode nahe ist, benutzt den Edelstein, er sollte dann den Rest für Euch erledigen.$B$BIch schlage vor, dass Ihr nach Desolace geht und versucht, dort einer Höllenbestie habhaft zu werden. Die sollten geeignet sein.$B$BWenn Ihr Erfolg hattet, bringt den gefangenen Geist und Euren Smaragd zu Menara.",
    ["O"] = "Nehmt den Infussmaragd und benutzt ihn bei einer der Höllenbestien von Desolace. Wenn Ihr Erfolg hattet, bringt Menara Leerbringer im Brachland den eingekerkerten Höllenbestiengeist und den Infussmaragd.",
    ["T"] = "Splitter einer Höllenbestie",
  },
  [4964] = {
    ["D"] = "Ich werde sofort mit der Arbeit an Eurer Kugel beginnen und Euren neuen Dämon darin einschließen. Wenn ich damit fertig bin, könnt Ihr entscheiden, ob Ihr ihn lieber in Form eines Stabs oder einer Kugel hättet. Ich fand es schon immer wichtig, dafür zu sorgen, dass jeder Hexenmeister die richtige Waffe bekommt...Ich wüsste nicht, warum ich jetzt mit dieser Praxis aufhören sollte.",
    ["O"] = "Wartet, bis Menara Leerbringer die Kugel von Dar\'Orahil fertig gestellt hat, und sprecht dann noch einmal mit ihr.",
    ["T"] = "Die fertiggestellte Kugel von Dar\'Orahil",
  },
  [4965] = {
    ["D"] = "Schön, schön, schön... Es ist eine Freude zu sehen, wie Eure Macht wächst, $N. Ich würde sagen, dass Ihr bald bereit seid, im Brachland mit Menara Leerbringer zu sprechen. Falls Ihr noch nie mit ihr gesprochen habt, dann lasst mich Euch sagen, dass ich wohl keinen zweiten Experten wie sie kenne, der so geschickt im Anfertigen von Gegenständen - insbesondere Kugeln - ist, in denen man sogar einen Dämon einkerkern kann, damit er einem einfach und bequem zu Diensten ist.$B$BFalls Ihr interessiert seid, begebt Euch zu Strahads Turm oberhalb von Ratschet im Brachland, dort findet Ihr sie.",
    ["O"] = "Sprecht mit Menara Leerbringer im Brachland.",
    ["T"] = "Wissen der Kugel von Orahil",
  },
  [4966] = {
    ["D"] = "Es könnte allerdings noch viel mehr Zentauren geben, als ich dachte, deshalb bitte ich Euch, $N, zusammen mit mir gegen diese gemeinen Auftragsmörder zu kämpfen.",
    ["O"] = "Beschützt Kanati Grauwolke vor den Angriffen der Zentauren.",
    ["T"] = "Kanati Grauwolke schützen",
  },
  [4967] = {
    ["D"] = "Immer noch derselbe Dummkopf... und immer noch auf der Suche nach Dingen der Sterblichen. Ja, es mag Euch arrogant erscheinen, wie ich zu Euch spreche. Ihr steht vor mir und ich muss lachen. Ihr seid jämmerlich!$B$BAber was ich denke, zählt nicht. Es zählt nur, was jene denken, die gewillt sind, sich mit Euch abzugeben! Und obwohl ich anderer Ansicht bin, sind jene, die über ein gewisses Maß an Macht gebieten, der Meinung, dass Ihr mehr verdient.$B$BWenn es das ist, wonach Ihr trachtet, solltet Ihr mit Menara Leerbringer im Turm oberhalb von Ratschet sprechen. Ich glaube, Ihr habt sie vor geraumer Zeit schon einmal getroffen.",
    ["O"] = "Sprecht mit Menara Leerbringer im Brachland.",
    ["T"] = "Wissen der Kugel von Orahil",
  },
  [4968] = {
    ["D"] = "Hmm, Ihr habt es also bis hierher geschafft... das ist wahrhaft erstaunlich. Ich dachte, die Verderbnis durch die Kanalisierung des Arkanen hätte sich mittlerweile in Eure Seele geschlichen und Euch in den Wahnsinn getrieben. Sollen wir versuchen, Euch noch ein bisschen weiter aufzubauen?$B$BWenn Ihr Eure Fertigkeiten auf die Probe stellen wollt, sprecht mit Menara Leerbringer. Sie ist in Strahads Turm oberhalb von Ratschet zu finden. Sie wird Euch leiten.",
    ["O"] = "Sprecht mit Menara Leerbringer im Brachland.",
    ["T"] = "Wissen der Kugel von Orahil",
  },
  [4969] = {
    ["D"] = "Haha! Das habt Ihr sehr gut gemacht, $C.$B$BFrüher oder später werden sich selbst Höllenbestien und Schreckenslords vor Euch in den Staub werfen!$B$BAber bis dahin müsst Ihr Euch noch auf die Waffen verlassen, die von Sterblichen geschaffen wurden.$B$BIhr scheint bereit zu sein für ein weiteres solches Werk, und wenn Ihr möchtet, würde Menara Leerbringer mit Euch sprechen! Sie ist in der Nähe von Strahads Turm zu finden, der oberhalb der Hafenstadt Ratschet steht. Geht dorthin, wenn Ihr bereit seid.",
    ["O"] = "Sprecht mit Menara Leerbringer im Brachland.",
    ["T"] = "Wissen der Kugel von Orahil",
  },
  [4970] = {
    ["D"] = "Wenn Ihr Euch beweisen wollt, könnt Ihr eine sehr einfache, aber sehr wichtige Aufgabe erledigen.$B$BSammelt Fleisch von den Schimären und den Splitterzahnbären in diesem Gebiet. Bringt es mir, dann kann ich damit die Frostsäbler füttern, die gerade ausgebildet werden.",
    ["O"] = "Sammelt 5 Splitterzahnfleischstücke und 5 Fleischstücke einer Eiswindschimäre für Rivern Frostwind in Winterquell.",
    ["T"] = "Frostsäblerverpflegung",
  },
  [4971] = {
    ["D"] = "Benutzt den Temporalversetzer in der Nähe eines der Silos von Andorhal, um die temporalen Parasiten aufzuspüren.$B$BTötet 10 temporale Parasiten und bringt dann den Temporalversetzer zu Chromie im Gasthaus Andorhal in den Westlichen Pestländern.",
    ["O"] = "Benutzt den Temporalversetzer in der Nähe eines der Silos von Andorhal, um die temporalen Parasiten aufzuspüren.$B$BTötet 10 temporale Parasiten und bringt dann den Temporalversetzer zu Chromie im Gasthaus Andorhal in den Westlichen Pestländern.",
    ["T"] = "Eine Frage der Zeit",
  },
  [4972] = {
    ["D"] = "Ihr müsst für mich in die jenseits dieses Raumes liegende Welt gelangen, $N. Lasst es mich Euch erklären.$B$BIhr seht, dass dieser Raum hier anders ist als der Rest von Andorhal. Mein Drachenschwarm agiert auf verschiedene Weise und in vielen verschiedenen Zeitlinien. Um den in Andorhal angerichteten Schaden zu beheben, handle ich jetzt von einer anderen Zeitlinie aus, um sie nicht allzu sehr zu kontaminieren. Um diese Arbeit fortzusetzen, benötige ich Zeitobjekte, die aus einer Zeit stammen, bevor die Seuche auftrat. Ihr findet sie in Schließkassetten in den Ruinen der Gebäude, die einst diese stolze Stadt waren.",
    ["O"] = "Sucht 5 Uhren von Andorhal, die in Schließkassetten überall im Schutt der Stadt zu finden sind. Kehrt mit ihnen zu Chromie ins Gasthaus Andorhal in den Westlichen Pestländern zurück.",
    ["T"] = "Tempus fugit",
  },
  [4973] = {
    ["D"] = "Falls Ihr gewillt seid, mir noch einmal zu helfen, würde ich Eure Unterstützung dankbar annehmen. Ich nehme Euch gern weitere Uhren ab, die Ihr in den Ruinen von Andorhal findet. Sie helfen mir sicherlich dabei, das Gleichgewicht gegen die unbekannte bösartige Kraft zu erhalten, die hier mit der Zeitlinie herumspielt.$B$BIm Austausch für diese Uhren werde ich Euch einen anderen Gegenstand geben, der sich, wie ich vermute, in der Zukunft für Euch als von großem Wert erweisen wird.",
    ["O"] = "Sucht 5 Uhren von Andorhal in den Ruinen der Stadt und bringt sie Chromie im Gasthaus Andorhal in den Westlichen Pestländern.",
    ["T"] = "Tempus fugit",
  },
  [4974] = {
    ["D"] = "Nur weil der schwarze Drachenschwarm hinter ihm steht, wagt Rend es, solche Ansprüche zu stellen.$B$BIhr, $N, müsst einen Weg finden, die Hallen des Aufstiegs zu durchqueren. Dahinter findet Ihr den \'Kriegshäuptling\' Rend Schwarzfaust. Ihr müsst ihn vernichten... FÜR DIE HORDE!$B$B<Thrall schlägt mit der Faust auf seinen Thron.>$B$BWenn Ihr meine Gemächer das nächste Mal betretet, werdet Ihr seinen Kopf triumphierend in die Höhe halten und ihn Eurem Kriegshäuptling präsentieren. Gelingt Euch das, wird man Euch als Held der Horde ehren.",
    ["O"] = "Begebt Euch zur Schwarzfelsspitze und tötet den Kriegshäuptling Rend Schwarzfaust. Nehmt seinen Kopf und kehrt nach Orgrimmar zurück.",
    ["T"] = "Für die Horde!",
  },
  [4975] = {
    ["D"] = "Ich werde sofort mit der Arbeit an Eurer Kugel beginnen und Euren neuen Dämon darin einschließen. Wenn ich damit fertig bin, könnt Ihr entscheiden, ob Ihr ihn lieber in Form eines Stabs oder einer Kugel hättet. Ich fand es schon immer wichtig, dafür zu sorgen, dass jeder Hexenmeister die richtige Waffe bekommt...Ich wüsste nicht, warum ich jetzt mit dieser Praxis aufhören sollte.",
    ["O"] = "Wartet, bis Menara Leerbringer die Kugel von Noh\'Orahil fertig gestellt hat, und sprecht dann noch einmal mit ihr.",
    ["T"] = "Die fertiggestellte Kugel von Noh\'Orahil",
  },
  [4976] = {
    ["D"] = "Bringt die gesäuberte Kugel zurück zu Menara nach Ratschet, $N. Grüßt sie von mir und passt auf Euch auf. Ich hoffe wirklich, dass Ihr der Versuchung und der Verderbnis widerstehen könnt, die Euch auf Eurem Weg sicherlich begleiten werden.$B$BViel Glück.",
    ["O"] = "Bringt die gesäuberte Höllenkugel zu Menara Leerbringer ins Brachland.",
    ["T"] = "Ablieferung der gesäuberten Kugel",
  },
  [4981] = {
    ["D"] = "Trau niemals einem Goblin. Darauf läuft es doch hinaus, $N.$B$BSie heißt Bijou und ist eine unserer unzuverlässigsten Agentinnen. Ich habe herausgefunden, dass sie für irgendeinen zwielichtigen Tierhändler namens Kibler außerhalb der Brennenden Steppe gearbeitet hat - sie hat uns schlicht und ergreifend hintergangen. Und was noch schlimmer ist, sie hat Gelder der Horde veruntreut, um ihre Machenschaften zu finanzieren.$B$BIch will, dass man sie mir lebend bringt!$B$BZuletzt wurde sie beim Betreten der Schwarzfelsspitze gesehen. Findet sie und bringt sie her.$B$BUnd lasst Euch bloß nicht von ihr umgarnen, $N.",
    ["O"] = "Begebt Euch zur Schwarzfelsspitze und findet heraus, was aus Bijou geworden ist.",
    ["T"] = "Agentin Bijou",
  },
  [4983] = {
    ["D"] = "Bringt Bijous Aufklärungsbericht zu Großmeister Lexlort nach Kargath.",
    ["O"] = "Bringt Bijous Aufklärungsbericht zu Großmeister Lexlort nach Kargath.",
    ["T"] = "Bijous Aufklärungsbericht",
  },
  [4984] = {
    ["D"] = "Allein konnte ich nicht viel tun, um die Geißel zu bezwingen. Aber ich kam dennoch hierher, um zu versuchen, den an der Natur angerichteten Schaden zu lindern. Die Tiere leiden unter der Seuche, die das Land noch immer plagt, und finden weder Gnade noch Frieden. Ich bitte Euch fortzusetzen, was ich hier nicht beenden konnte.$B$BEs gibt für diese leidenden Tiere keine Rettung, nur eine gnädige und sanfte Erlösung. Sucht die kranken Wölfe auf, die sich hier in der Nähe befinden, und tötet sie. Bitte, $N, macht ihrem Leiden ein Ende - Ihr würdet damit auch meinen Kummer lindern.",
    ["O"] = "Tötet 8 kranke Wölfe und kehrt dann zu Mulgris Tiefenfurt auf dem Trostlosen Feld in den Westlichen Pestländern zurück.",
    ["T"] = "Die Tierwelt leidet auch",
  },
  [4985] = {
    ["D"] = "Östlich von hier, etwa da, wo die Östlichen Pestländer beginnen, streiften einst stolze und herrliche Grizzlys durch das Land. Die von der Geißel freigesetzte Seuche hat auch auf diese Wildtiere übergegriffen. Jetzt ziehen sie durchs Land und leiden sehr, während ihre Muskeln steif werden und ihr Fell und Fleisch langsam verrotten.$B$BIch bitte Euch, auch sie von ihrem Elend zu erlösen. Das einzige Heilmittel für sie ist ein schneller Tod, beigebracht durch Eure Hand.",
    ["O"] = "Tötet 8 kranke Grizzlys und kehrt dann zu Mulgris Tiefenfurt auf dem Trostlosen Feld in den Westlichen Pestländern zurück.",
    ["T"] = "Die Tierwelt leidet auch",
  },
  [4986] = {
    ["D"] = "Die leblose Hand des Tauren hält einen erstaunlich gesund aussehenden Eichenast umklammert. Als Ihr den Ast untersucht, entdeckt Ihr Glyphen druidischer Art, die in die Rinde geritzt sind.$B$BIhr nehmt den Ast und stellt fest, dass er sich warm anfühlt. Viel wärmer, als er sich anfühlen sollte. Vor Eurem inneren Auge erscheint das Bild des Baums von Teldrassil - genau gesagt von Darnassus - und die Worte \"Zirkel des Cenarius\" liegen Euch auf der Zunge. Es scheint, als hätte man dem Ast die Information mitgegeben wohin er zu bringen sei.",
    ["O"] = "Nach der Magie zu urteilen, mit der der glyphenverzierte Eichenast erfüllt ist, hatte der Taurendruide wohl gewollt, dass Ihr den Ast zum Zirkel des Cenarius nach Darnassus bringt. Bittet einen der Druiden dort um Hilfe.",
    ["T"] = "Glyphenverzierter Eichenast",
  },
  [4987] = {
    ["D"] = "Die leblose Hand des Tauren hält einen erstaunlich gesund aussehenden Eichenast umklammert. Als Ihr den Ast untersucht, entdeckt Ihr Glyphen druidischer Art, die in die Rinde geritzt sind.$B$BIhr nehmt den Ast und stellt fest, dass er sich warm anfühlt. Viel wärmer, als er sich anfühlen sollte. Vor Eurem inneren Auge erscheint das Bild von Donnerfels in Mulgore - genau gesagt von der Anhöhe der Ältesten - und die Worte \"Zirkel des Cenarius\" liegen Euch auf der Zunge. Es scheint, als hätte man dem Ast die Information mitgegeben, wohin er zu bringen sei.",
    ["O"] = "Nach der Magie zu urteilen, mit der der glyphenverzierte Eichenast erfüllt ist, hatte der Taurendruide wohl gewollt, dass Ihr den Ast zum Zirkel des Cenarius nach Donnerfels bringt. Bittet einen der Druiden dort um Hilfe.",
    ["T"] = "Glyphenverzierter Eichenast",
  },
  [5001] = {
    ["D"] = "Normalerweise gebe ich mich nicht mit Euresgleichen ab, $R, aber mir scheint, ich stecke hier in einer Zwickmühle. Wisst Ihr, meine Mission hier ist für die Horde von allergrößter Wichtigkeit. Die Informationen, die ich hier gesammelt habe, betreffen jedoch auch das Wohlergehen der Allianz. Ihr könnt mir glauben, ich bin wahrlich kein Freund der Allianz, aber die Gefahr durch den Schwarzfelsklan ist größer als nur ein Haufen Orcs.$B$BIhr helft mir, meine Habseligkeiten zu finden, dann helfe ich Euch mit ein paar Informationen aus, die sich für Euresgleichen als nützlich erweisen könnten.$B$BAbgemacht?",
    ["O"] = "Sucht Bijous Habseligkeiten und bringt sie ihr. Viel Glück!",
    ["T"] = "Bijous Habseligkeiten",
  },
  [5002] = {
    ["D"] = "Ich bin ein Goblin, der zu seinem Wort steht, $R. Und jetzt hört gut zu.$B$BDiese Orcs werden vom schwarzen Drachenschwarm vor allem als Packesel und Kanonenfutter missbraucht!$B$BIhr wahrer Anführer ist kein Geringerer als Nefarian, die Brut von Todessschwinge. Ich habe auch den Aufenthaltsort der Untergebenen von Nefarian in diesem Teil der Zitadelle entdeckt.$B$BBringt diese Karte zu Marschall Maxwell in der Brennenden Steppe. Auf ihr ist der Aufenthaltsort dieser Teufelskreaturen eingezeichnet, um es einfacher zu machen.",
    ["O"] = "Begebt Euch in die Brennende Steppe und leitet Bijous Informationen an Marschall Maxwell weiter.",
    ["T"] = "Nachricht an Maxwell",
  },
  [5021] = {
    ["D"] = "\"Das Paket muss ausgeliefert werden... sonst bin ich hier gefangen!\"$B$BWie ein Flüstern zieht das Wehklagen einer verängstigten Frau durch das Haus. In einer Ecke im oberen Stockwerk kauert eine weibliche, geisterhafte Gestalt. Sie scheint sich ihrer Umgebung nicht ganz gewahr zu sein und ist sich Eurer nur vage bewusst.$B$B\"Seid Ihr hier, um mein Paket auszuliefern? Bitte, bevor es noch viel schlimmer wird! Ich wollte hier wegreiten. Es sollte immer noch bei meinem Pferd sein... oder komme ich zu spät?\"",
    ["O"] = "Das wilde Gebrabbel der Geisterfrau deutete darauf hin, dass sie ein Paket ausliefern musste. Sie behauptete, dass es dort war, wo auch ihr Pferd war. Darüber, wo das Pferd jetzt ist oder wohin das Paket ausgeliefert werden muss, sagt der Geist nichts.",
    ["T"] = "Besser spät als nie",
  },
  [5022] = {
    ["D"] = "Ihr hebt das Paket auf und wischt eine dünne Schicht aus Staub und Schmutz ab, mit der es bedeckt ist. Es ist mit hastig hingekritzelter Schrift an eine Emma Teufelsstein in Sturmwind adressiert; die Auslieferung des Pakets war für einen Tag vor über vier Jahren geplant.$B$BOb Emma Teufelsstein überhaupt noch lebt, ist nicht klar. Aber man weiß, dass im Rathaus von Sturmwind Unterlagen über die Bevölkerung der Stadt aufbewahrt werden. Sie werden von den königlichen Disponenten des Volkszählungsbüros von Sturmwind verwaltet.",
    ["O"] = "Wendet Euch an die königlichen Disponenten von Sturmwind, um etwas über den Aufenthaltsort von Emma Teufelsstein herauszufinden. Normalerweise findet sich ein Volkszählungsbeamter im Rathaus.",
    ["T"] = "Besser spät als nie",
  },
  [5023] = {
    ["D"] = "Ihr hebt das Paket auf und wischt eine dünne Schicht aus Staub und Schmutz ab, mit der es bedeckt ist. Es ist mit hastig hingekritzelter Schrift an einen Jeremiah Teufelsstein aus Lordaeron adressiert; die Auslieferung des Pakets war für einen Tag vor über vier Jahren geplant.$B$BOb Jeremiah Teufelsstein überhaupt noch lebt, ist nicht klar. Aber man weiß, dass Unterstadt, früher bekannt als Lordaeron, Unterlagen über die Bevölkerung der Stadt aufbewahrt. Sie werden von den königlichen Vorarbeitern des Volkszählungsbüros von Unterstadt verwaltet.",
    ["O"] = "Wendet Euch an die königlichen Vorarbeiter von Unterstadt, um etwas über den Aufenthaltsort von Jeremiah Teufelsstein herauszufinden. Normalerweise findet sich ein Volkszählungsbeamter in der Nähe der Gilden- und Wappenrock-Registrierung.",
    ["T"] = "Besser spät als nie",
  },
  [5041] = {
    ["D"] = "Die Karawane, die angegriffen wurde, führte Vorräte mit sich, die wir dringend benötigen.$B$BIhr wart bisher tapfer genug, den Klingenmähnenstamm zu bekämpfen, und ich werde Euch bitten, es erneut zu tun. Dieses Mal wüsste ich aber gern, ob ich Euch bitten könnte, nach unseren verlorenen Vorräten Ausschau zu halten. Ihr könnt sie wahrscheinlich in ihren Lagern finden; die Stacheleber werden durch ihre schändlichen Taten fett.$B$BDie Einwohner von Crossroads wären in Eurer Schuld.",
    ["O"] = "Findet die Crossroads-Vorratskisten und bringt sie zu Thork im Brachland zurück.",
    ["T"] = "Vorräte für das Wegekreuz",
  },
  [5042] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Agamaggans Kraft",
  },
  [5043] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Agamaggans Beweglichkeit",
  },
  [5044] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Weisheit von Agamaggan",
  },
  [5047] = {
    ["D"] = "Ihr findet Malyfous Düsterhammer in der Ewigen Warte, $C. Sagt ihm, dass Finkle absolut in Ordnung ist und dass der Anzug perfekt funktioniert.$B$BDann gebt ihm dies:$B$B<Finkle gibt Euch ein leuchtendes Fleischstück.>$B$BIch riss es aus den Innereien des Viechs während meines \'Urlaubs.\'$B$BIch wette Malyfous könnte Euch eine recht genaue Einschätzung dessen geben, was er mit Teilen eines solchen Monsters tun kann!$B$BOh, ich schätze Ihr wisst nicht, wo die Ewige Warte ist, was? Geht nach Winterquell, $GBursche:meine Dame;!",
    ["O"] = "Sprecht mit Malyfous Düsterhammer in der Ewigen Warte.",
    ["T"] = "Finkle Einhorn, zu Euren Diensten!",
  },
  [5048] = {
    ["D"] = "Sie ist ein bisschen zänkisch und vielleicht nicht so ganz richtig im Kopf... aber diese Akten deuten darauf hin, dass die alte Emma zu den Informationen passt, die wir über Emma Teufelsstein haben.$B$BVielleicht habt Ihr die alte Emma durch die Stadt streifen sehen... sie redet mit sich selbst, meistens sinnloses Geplapper. Sie tut aber nichts und niemandem weh, also lassen die Leute sie meist gewähren.$B$BAlles in allem würde ich sagen, dass die alte Emma tatsächlich Emma Teufelsstein ist. Ihr könntet sie fragen, ob ihr Nachname Teufelsstein ist.",
    ["O"] = "Sucht die alte Emma in Sturmwind und findet heraus, ob sie tatsächlich Emma Teufelsstein ist. Wenn sie es ist, dann möchte sie vielleicht das Paket haben, das Janice Teufelsstein für sie gepackt hat.",
    ["T"] = "Emma die Nette",
  },
  [5049] = {
    ["D"] = "Nach den Informationen, die ich finden konnte, ist die Person, die Ihr sucht - und der vorgesehene Empfänger des Pakets -, Jeremiah Payson, der Kakerlakenverkäufer von Unterstadt.$B$BIch war immer der Meinung, dass es einer besonderen Gabe bedarf, um... Kakerlaken zu verkaufen. Zweifellos seid Ihr jetzt auf einer recht epischen Reise, $C. Ich werde Euch Euren weltverändernden Bestrebungen überlassen. Erlaubt mir aber, ein Teil Eures Meisterwerks zu sein und zu enthüllen, dass er sich in der Nähe der Bank aufhält und unter einem der Gehwege herumkriecht.",
    ["O"] = "Sucht Jeremiah Payson in Unterstadt und findet heraus, ob er tatsächlich Jeremiah Teufelsstein ist. Wenn er es ist, dann möchte er vielleicht das Paket haben, das Janice Teufelsstein für ihn gepackt hat.",
    ["T"] = "Der Jammer des Jeremiah",
  },
  [5050] = {
    ["D"] = "Es scheint, dass ich bei Euch falsch lag, $N, und dafür schulde ich Euch eine Entschuldigung. Meine Schwester hat einige persönliche Dinge geschickt, einschließlich, was witzig genug ist, die Urkunde zum Teufelssteinfeld. Ich denke, ich werde im Moment darauf verzichten, Anspruch darauf geltend zu machen.$B$BWenn der Geist meiner Schwester weiterhin an den Hof gebunden ist und keine Ruhe finden kann, bitte... könntet Ihr dies wohl zu ihr bringen? Es ist die Hälfte eines Glücksbringers, den sie einst hatte. Die andere Hälfte hatte ihr Ehemann.$B$BVielleicht wird sie ihren Frieden finden, wenn sie weiß, dass ich diese Dinge habe.",
    ["O"] = "Bringt Janice Teufelsstein in den Westlichen Pestländern die Hälfte ihres Glücksbringers zurück.",
    ["T"] = "Glücksbringer",
  },
  [5051] = {
    ["D"] = "Der arme Geist plappert weiter:$B$B\"Mir ist so kalt, John. Bring mir deinen Glücksbringer und wir werden vereint sein! Schnell, John, bevor die Seuche uns in grauenhafte Bestien verwandelt! Ich kann dich nicht sehen, John... aber ich weiß, dass du nah bist. Ich fühle dich in meiner Nähe.\"$B$BVielleicht solltet Ihr die zweite Hälfte des Glücksbringers suchen und das Amulett wieder zusammensetzen... es könnte sein, dass der Geist von Janice Teufelsstein genau das sucht.",
    ["O"] = "Sucht auf dem Teufelssteinfeld in den Westlichen Pestländern nach der anderen Hälfte des Glücksbringers und setzt ihn wieder zusammen.$B$BGebt den Glücksbringer Janice Teufelsstein auf dem Teufelssteinfeld in den Westlichen Pestländern.",
    ["T"] = "Zwei Hälften vereint",
  },
  [5052] = {
    ["D"] = "Zusammen mit diesen Dornen wirkte sich das Blut <schnaubt> auf andere Aspekte der Erde aus: blutrote Steine gemischt mit anderen Mineralien. Die Borstennacken graben diese Splitter aus und tragen sie als Totems <schnaubt> für Kraft im Kampf und als Totems für ihre Magie. <schnaubt>$B$BIch kenne die Geheimnisse dieser Magie.$B$BBringt Quetschzahn einen dieser Blutsplitter, um zu zeigen, dass Ihr verstanden habt. Bringt Quetschzahn viele dieser Splitter und er wird Euch mit Stachelebermagie segnen - der Magie von Agamaggan! <schnaubt>",
    ["O"] = "Bringt Quetschzahn im Camp Taurajo im Brachland einen Blutsplitter.$B$BSammelt weitere Blutsplitter, wenn Ihr von ihm mit Agamaggans Macht gesegnet werden möchtet.",
    ["T"] = "Blutsplitter von Agamaggan",
  },
  [5053] = "_",
  [5054] = {
    ["D"] = "In den schneebedeckten Bergen von Winterquell pirscht sich ein geschickter Jäger an seine Beute an... Ich spreche vom Splitterzahnbären Ursius, einer flinken und schlauen Bestie.$B$BIch schicke Euch nun gegen ihn. Er zieht durch die wogenden Hügel im Nordwesten der Ewigen Warte. Findet und jagt ihn, bevor er Eure Absicht erkennt.$B$BAlle, die bisher gegen Ursius gezogen sind, haben schnell herausgefunden, dass sie die Beute waren und er der Jäger. Also seht Euch vor.",
    ["O"] = "Storm Schattenhuf in der Ewigen Warte in Winterquell möchte, dass Ihr Ursius tötet.",
    ["T"] = "Ursius von den Splitterzahnbären",
  },
  [5055] = {
    ["D"] = "Geschickt und schlau gleitet Brumeran leise über die schneebedeckte Landschaft des südlichen Winterquell. Lasst Euch aber nicht von Ihrem graziösen Auftreten verleiten zu glauben, dass sie harmlos sei. Alle, die ihren Weg kreuzen, bezahlen einen hohen Preis dafür und häufig mit ihrem eigenen Leben.$B$BSucht Brumeran von den Eiswindschimären. Besiegt sie und kehrt dann wieder zu mir zurück.",
    ["O"] = "Storm Schattenhuf in der Ewigen Warte in Winterquell möchte, dass Ihr Brumeran tötet.",
    ["T"] = "Brumeran von den Eiswindschimären",
  },
  [5056] = {
    ["D"] = "Im nördlichen Winterquell sammeln sich große Katzen rund um einen großen Orientierungspunkt, dem Frostsäblerfelsen. Die Anführerin dieser Katzen ist unter dem Namen Shy-Rotam bekannt. Wenn Ihr die Matriarchin der Frostsäbler besiegen würdet, würde Euch das großen Respekt in den Augen der anderen einbringen, $R.$B$BGeht erneut nach Winterquell und sammelt dort hochheiliges Fleisch von den Frostsäblern. Sucht den Stein von Shy-Rotam und legt das Fleisch dort ab. Wenn Ihr es dort platziert, ruft das Shy-Rotam herbei... und sie wird kochen vor Wut.$B$BWappnet Euch vor ihrem Zorn und besiegt sie.",
    ["O"] = "Storm Schattenhuf in der Ewigen Warte in Winterquell möchte, dass Ihr Shy-Rotam besiegt.",
    ["T"] = "Shy-Rotam",
  },
  [5057] = {
    ["D"] = "Schon bevor Ihr mit mir spracht, $N, habe ich von Euren Abenteuern gehört... dass Ihr den unerschütterlichen Willen habt, Euch selbst zu beweisen, und dass Ihr als $C außerordentliche Fähigkeiten besitzt. Und nun weiß ich, dass diese Dinge wahr sind.$B$BWer wie Ihr herausragt, ist der Anerkennung würdig. Bringt diesen Gutschein zu einem Freund von mir in Donnerfels namens Melor Steinhuf. Ich bin sicher, dass er sich an Euch und das, was Ihr vor so langer Zeit erreicht habt, erinnern wird. Sprecht mit ihm über Eure jüngsten Siege und vergesst nicht, was Ihr heute erreicht habt.",
    ["O"] = "Bringt Storm Schattenhufs Gutschein zu Melor Steinhuf in Donnerfels.",
    ["T"] = "Frühere Abenteuer",
  },
  [5058] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Frau Dalsons Tagebuch",
  },
  [5061] = {
    ["D"] = "Ihr habt die notwendigen Lektionen gemeistert, die alle jungen Druiden lernen, bevor sie einen Wasseraspekt annehmen können. Kehrt jetzt zu Eurem Lehrer Mathrengyl Bärenfährte nach Darnassus zurück. Zeigt ihm Euren Anhänger und beweist ihm damit, dass Ihr bereit seid zu lernen, was er Euch lehren kann. Er wird Eure Ausbildung abschließen und Euch ermöglichen, eins mit dem Wasser zu werden.$B$BAuf Wiedersehen. Wir werden uns wieder sprechen.",
    ["O"] = "Kehrt nach Darnassus zurück und zeigt Mathrengyl Bärenfährte den Anhänger des Seelöwen.",
    ["T"] = "Wassergestalt (Taurendruide)",
  },
  [5062] = {
    ["D"] = "Arikara ist eine tödliche Kreatur, die schnell beseitigt werden muss. Um sie zur Strecke zu bringen, müsst Ihr das hochheilige Feuer des Lebens anzünden - so wird Arikara beschworen.$B$BGeht nun und holt eine seltene Pflanze, die Brandpulveragave. Wenn Ihr genug Agaven gesammelt habt, sucht den Rat von Magatha Grimmtotem auf der Anhöhe der Ältesten in Donnerfels. Sie ist eine mächtige Schamanin, die die Agavenpflanze verzaubern kann, um ein Pulver zu erzeugen, das das Feuer entzünden kann.$B$BReist in Richtung Osten zum Siedenden Teich und sammelt Brandpulveragaven.",
    ["O"] = "Sammelt 10 Scheffel Brandpulveragaven und beratet Euch dann mit Magatha Grimmtotem auf der Anhöhe der Ältesten in Donnerfels.",
    ["T"] = "Hochheiliges Feuer",
  },
  [5063] = {
    ["D"] = "Eine feinere Kappe werdet Ihr niemals sehen. Aus dem makellosen Balg der Bestie werden unsere Experten den phänomenalsten Hut schaffen, den ein Gaukler jemals zu tragen träumte!$B$BFünfzig Goldstücke zusammen mit den aufgeführten Gegenständen und Euer Kopf wird von der Kappe des Scharlachroten Wissenden geziert:$B$B*Einen makellosen Balg der Bestie.$B$B*Ausgefranste Monstrositätenstickereien.$B$B*Arkankristalle.$B$B*Verzauberter scharlachroter Faden.$B$BMERKT AUF!",
    ["O"] = "Um die Kappe des scharlachroten Wissenden herzustellen, müsst Ihr die folgenden Dinge zu Malyfous Düsterhammer bringen: 1 makelloser Balg der Bestie; 5 ausgefranste Monstrositätenstickereien; 8 Arkankristalle; 5 verzauberte scharlachrote Fäden.$B$BIhr müsst außerdem 50 Goldstücke für die Herstellung des Gegenstandes zahlen.",
    ["T"] = "Kappe des Scharlachroten Wissenden",
  },
  [5064] = {
    ["D"] = "Sucht die drei geheimen Notizen im Düsterwolkengipfel und bringt sie zurück.",
    ["O"] = "Sucht die drei geheimen Notizen im Düsterwolkengipfel und bringt sie zurück.",
    ["T"] = "Grimmtotemspionage",
  },
  [5065] = {
    ["D"] = "Der Troll, mit dem Ihr gesprochen habt, hat Euch nicht die Wahrheit gesagt. Die beiden Schrifttafeln, die Ihr für ihn erhalten habt, sprachen davon, die Essenz von Hakkar zu bewahren, aber an dieser Legende ist noch mehr. Ich habe sie lange studiert und herausgefunden, dass es sechs Schrifttafeln von Mosh\'aru gibt, nicht zwei! Sechs!$B$BUnd ich fürchte, dass uns die Geheimnisse, die die anderen enthalten, nicht gefallen werden.$B$BSucht die dritte und vierte Schrifttafel von Mosh\'aru. Sie befinden sich in den Östlichen Pestländern, im Besitz der Moosschindertrolle. Sucht sie und bringt sie mir.",
    ["O"] = "Bringt Ausgrabungsleiter Eisenschuh in Tanaris die dritte und vierte Schrifttafel von Mosh\'aru.",
    ["T"] = "Die verlorenen Schrifttafeln von Mosh\'aru",
  },
  [5066] = {
    ["D"] = "Hört her, hört her! Alle Helden werden ersucht, aufzumerken!$B$BDas Königreich Sturmwind ruft zu den Waffen! Alle einsatzfähigen Individuen sind hiermit angehalten, gegen die dunkle Bedrohung durch die Geißel in den nördlichen Ländern der Östlichen Königreiche zu den Waffen zu greifen! Es gibt Gerüchte über neue Bedrohungen, die sich aus den Ruinen der Pestländer erheben!$B$BHelden der Welt... Sucht Kommandant Ashlam Ehrenschlag auf, der seinen Stützpunkt an der Zugwindspitze errichtet hat, nördlich des Alteracgebirges!",
    ["O"] = "Sucht Kommandant Ashlam Ehrenschlag auf. Sein Stützpunkt befindet sich an der Zugwindspitze, nördlich des Alteracgebirges.",
    ["T"] = "Der Ruf zu den Waffen: Die Pestländer!",
  },
  [5067] = {
    ["D"] = "Wenn die Herstellung eines solchen Gegenstandes auch noch nie erfolgreich bewerkstelligt wurde, so wird sie doch für möglich erachtet - unter den richtigen Umständen.$B$BSollte der Sucher des Arkanums wünschen, Gamaschen mit immenser Kraft magiezuerfüllen, so muss er Folgendes suchen:$B$B*Einen makellosen Balg der Bestie.$B$B*Ausgefranste Monstrositätenstickereien.$B$B*Arkanitbarren.$B$B*Frostraunens Balsamierungsflüssigkeit.$B$BMERKT AUF!",
    ["O"] = "Um die Gamaschen von Arcana herzustellen, müsst Ihr die folgenden Dinge zu Malyfous Düsterhammer bringen: 1 makelloser Balg der Bestie; 5 ausgefranste Monstrositätenstickereien; 5 Arkanitbarren; 5 Einheiten Frostraunens Balsamierungsflüssigkeit.$B$BIhr müsst außerdem 50 Goldstücke für die Herstellung des Gegenstandes zahlen.",
    ["T"] = "Gamaschen von Arcana",
  },
  [5068] = {
    ["D"] = "Es heißt, dass nur eine einzige Brustplatte des Blutdurstes jemals geschaffen wurde und dass sie für die legendäre Auftragsmörderin Garona gemacht wurde. Ob diese Welt jemals eine weitere sehen wird, ist zu bezweifeln.$B$BWie dem auch sei! Unsere meisterlichen Handwerker können den Gegenstand reproduzieren, wenn die notwendigen Komponenten vorhanden sind:$B$B*Ein makelloser Balg der Bestie.$B$B*Ausgefranste Monstrositätenstickereien.$B$B*Arkanitbarren.$B$B*Die Haut des Schattens.$B$BMERKT AUF!",
    ["O"] = "Um die Brustplatte des Blutdurstes herzustellen, müsst Ihr die folgenden Dinge zu Malyfous Düsterhammer bringen: 1 makelloser Balg der Bestie; 10 ausgefranste Monstrositätenstickereien; 5 Arkanitbarren; 5 Häute des Schattens.$B$BIhr müsst außerdem 50 Goldstücke für die Herstellung des Gegenstandes zahlen.",
    ["T"] = "Brustplatte des Blutdurstes",
  },
  [5081] = {
    ["D"] = "Diese Informationen füllen die Lücken in unseren sonst einwandfreien Geheimdienstdaten über die Schwarzfelsspitze.$B$BDer Angriff muss entscheidend sein, $N.$B$BZuerst muss ihr Kriegsmeister, der Troll Voone, vernichtet werden. Ohne seine Kampfpläne werden die Schwarzfelsorcs im Chaos versinken.$B$BDann müsst Ihr den Ogerhochlord Omokk ausschalten. Die feigen Oger des Schwarzfels sind ohne seine Führungskraft machtlos.$B$BSchließlich noch der Oberanführer der Zitadelle: Wyrmthalak. Wenn Wyrmthalak tot ist, ist ihre zentrale Kommandoeinheit ohne Leitung.",
    ["O"] = "Reist zur Schwarzfelsspitze und schaltet Kriegsmeister Voone, Hochlord Omokk und Oberanführer Wyrmthalak aus.",
    ["T"] = "Maxwells Mission",
  },
  [5082] = {
    ["D"] = "Ich kam nach Winterquell, um die Frostfeuerquellen zu untersuchen. Mir ist aufgefallen, dass das Wasser... merkwürdige Eigenschaften zu haben scheint. Ich konnte die Quelle aber noch nicht entdecken, und um die Wahrheit zu sagen, hatte ich in letzter Zeit durch die Furbolgs der Winterfelle einige Schwierigkeiten.$B$BSie scheinen von den Quellen angezogen zu werden, genau wie ich. Mittlerweile wachen sie extrem eifersüchtig über das Wasser und lassen mich nicht einmal in die Nähe der beiden größeren Quellen, die von hier aus im Norden liegen.$B$BIch würde meine Studien aber gern fortsetzen. Könntet Ihr mir wohl helfen?",
    ["O"] = "Donova Schneeweh in Winterquell möchte, dass Ihr 8 Pfadfinder der Winterfelle, 8 Höhlenbehüter der Winterfelle und 8 Totemiker der Winterfelle erledigt.",
    ["T"] = "Bedrohung durch die Winterfelle",
  },
  [5083] = {
    ["D"] = "Diese Phiole, die Ihr bei den Winterfellfurbolgs aufgehoben habt, ist leer, aber Ihr könnt im Inneren ein paar Tropfen einer merkwürdigen grünen Flüssigkeit sehen; sie scheint fast zu glühen.$B$BIst es etwas Wichtiges?$B$BDonova Schneeweh könnte Euch wahrscheinlich mehr sagen, da sie nun schon einige Zeit bei den heißen Quellen in Winterquell lagert.",
    ["O"] = "Bringt das leere Feuerwasserfläschchen zu Donova Schneeweh in Winterquell.",
    ["T"] = "Feuerwasser der Winterfelle",
  },
  [5084] = {
    ["D"] = "Diese grünliche Flüssigkeit... sie erinnert mich ein wenig an den Schlamm, den ich bei meiner Reise durch den Teufelswald sah. Und ich habe einige Läufer der Winterfelle gesehen, die die Furbolgs der Holzschlundfeste passierten... Vielleicht haben diese Dinge etwas mit dem Fläschchen zu tun, das Ihr gefunden habt, $N.$B$BEs gibt eine große Gruppe Furbolgs direkt hinter der Holzschlundfeste im Teufelswald. Es ist der so genannte Stamm der Totenwaldfelle. Er wurde durch teuflische Energien verderbt und in den Wahnsinn getrieben. Warum seht Ihr Euch dort nicht um und schaut, ob Ihr Beweise für irgendwelche Beziehungen zwischen den Winterfellfurbolgs und den Totenwaldfellen findet?",
    ["O"] = "Durchsucht das Furbolglager der Totenwaldfelle im nördlichen Teil des Teufelswalds nach einem Hinweis.",
    ["T"] = "Der Verderbnis erliegen",
  },
  [5085] = {
    ["D"] = "Die grünliche Substanz in dem Kessel scheint der zu ähneln, die Ihr in dem Fläschchen gefunden habt. Dieses merkwürdige zähflüssige Zeug könnte genauso verderbt sein wie die Furbolgs, die es geschaffen haben...$B$BFalls die Winterfellfurbolgs es verwenden, bedeutet das, dass sie bald genauso verderbt sein werden wie der Stamm der Totenwaldfelle?$B$BEs wäre das Beste, Donova von Euren Funden zu berichten und ihr eine Probe aus dem Kessel mitzubringen.",
    ["O"] = "Berichtet Donova Schneeweh von Euren Entdeckungen.",
    ["T"] = "Geheimnisvoller Glibber",
  },
  [5086] = {
    ["D"] = "Ich habe die Probe untersucht, die Ihr mitgebracht habt. Ganz sicher bin ich mir nicht, aber ich glaube doch, dass sie Spuren von toxischen Elementen enthält. Die einzige Möglichkeit, das zu bestätigen, besteht darin, einige Proben als Vergleich zu sammeln.$B$BIch glaube, dass sich die Furbolgs der Totenwaldfelle diese Elemente möglicherweise von anderen verderbten Kreaturen im Teufelswald holen, vor allem von den Elementaren, die im Eisenwald leben und Toxinschrecken genannt werden.$B$BSammelt einige Proben zum Vergleich für mich. Aber seid vorsichtig: Es ist dort sehr gefährlich!",
    ["O"] = "Sammelt 3 Toxinschreckentropfen für Donova Schneeweh in Winterquell.",
    ["T"] = "Toxinschrecken",
  },
  [5087] = {
    ["D"] = "Sehr interessant, $N...$B$BWenn diese toxische Substanz mit dem Wasser aus den heißen Quellen gemischt wird, verliert sie ihre Toxizität und behält lediglich die Fähigkeit zurück, Kreaturen vorübergehend zu beeinflussen, die sie einnehmen. Darum verteidigen die Winterfelle die heißen Quellen wohl so vehement - sie brauchen sie, um ihr Feuerwasser zu läutern.$B$BWir müssen das hier stoppen... den Kontakt zwischen den Winterfellen und den Totenwaldfellen abschneiden. Eine Gruppe von Läufern ist zwischen ihrem Dorf und dem Teufelswald unterwegs. Sucht und besiegt sie und stehlt ihre Lieferung.",
    ["O"] = "Sucht die Läufer der Winterfelle und hindert sie daran, ihre Lieferung zum Lager der Winterfelle zu bringen.",
    ["T"] = "Läufer der Winterfelle",
  },
  [5088] = {
    ["D"] = "Bringt dieses aufbereitete und geläuterte Brandpulver nach Tausend Nadeln und werft es in das hochheilige Feuer des Lebens. Es befindet sich beim Düsterwolkengipfel auf einer isolierten Nadel, wo sie ihre Toten beerdigen.$B$BDie Verzauberung, die ich in das Brandpulver eingewoben habe, wird Arikara für Euch beschwören, $N.$B$BIch fürchte, dass sie Cairne Bluthuf bedrohen könnte; die Lehren der Tauren haben sich zu sehr verändert und möglicherweise ist Arikara darüber erzürnt. Tut schnell etwas, bevor alles verloren ist, $C!",
    ["O"] = "Erschlagt Arikara. Bringt ihre Überreste und das Brandpulver zu Motega Feuermähne im Weißgipfelposten als Beweis für Eure Tat.",
    ["T"] = "Arikara",
  },
  [5089] = {
    ["D"] = "Dem Kadaver der besiegten Schlange entnehmt Ihr etwas, das wie ein Schreiben von einiger Bedeutung aussieht. Es ist ein Brief des Vorgesetzten, General Drakkisath, bezüglich des Protokolls.$B$BEuer Instinkt sagt Euch, dass Ihr ihn zu Marschall Maxwell bringen solltet.",
    ["O"] = "Bringt den Befehl von General Drakkisath zu Marschall Maxwell in der Brennenden Steppe.",
    ["T"] = "General Drakkisaths Befehl",
  },
  [5090] = {
    ["D"] = "Hört her, hört her! Alle Helden werden ersucht, aufzumerken!$B$BDas Königreich Sturmwind ruft zu den Waffen! Alle einsatzfähigen Individuen sind hiermit angehalten, gegen die dunkle Bedrohung durch die Geißel in den nördlichen Ländern der Östlichen Königreiche zu den Waffen zu greifen! Es gibt Gerüchte über neue Bedrohungen, die sich aus den Ruinen der Pestländer erheben!$B$BHelden der Welt... Sucht Kommandant Ashlam Ehrenschlag auf, der seinen Stützpunkt an der Zugwindspitze errichtet hat, nördlich des Alteracgebirges!",
    ["O"] = "Sucht Kommandant Ashlam Ehrenschlag auf. Sein Stützpunkt befindet sich an der Zugwindspitze, nördlich des Alteracgebirges.",
    ["T"] = "Der Ruf zu den Waffen: Die Pestländer!",
  },
  [5091] = {
    ["D"] = "Hört her, hört her! Alle Helden werden ersucht, aufzumerken!$B$BDas Königreich Sturmwind ruft zu den Waffen! Alle einsatzfähigen Individuen sind hiermit angehalten, gegen die dunkle Bedrohung durch die Geißel in den nördlichen Ländern der Östlichen Königreiche zu den Waffen zu greifen! Es gibt Gerüchte über neue Bedrohungen, die sich aus den Ruinen der Pestländer erheben!$B$BHelden der Welt... Sucht Kommandant Ashlam Ehrenschlag auf, der seinen Stützpunkt an der Zugwindspitze errichtet hat, nördlich des Alteracgebirges!",
    ["O"] = "Sucht Kommandant Ashlam Ehrenschlag auf. Sein Stützpunkt befindet sich an der Zugwindspitze, nördlich des Alteracgebirges.",
    ["T"] = "Der Ruf zu den Waffen: Die Pestländer!",
  },
  [5092] = {
    ["D"] = "Da die westlichen und nördlichen Straßen in die Stadt von Andorhal gut befestigt sind, habe ich vor, über die weniger gut verteidigte östliche Straße anzugreifen. Die Geißel verlässt sich auf den Puffer von Untoten, die Trauerhügel heimsuchen, als erste Verteidigungslinie. Wenn wir nach Andorhal vordringen wollen, müssen wir es über Trauerhügel tun.$B$BEure erste Aufgabe besteht darin, den Weg durch Trauerhügel zu säubern. Dünnt die Masse der Skelette und Ghule aus, die das Gebiet heimsuchen. Meldet Euch wieder bei mir, wenn das erledigt ist!",
    ["O"] = "Tötet 10 Skelettschinder und 10 sabbernde Ghule in Trauerhügel.$B$BKehrt zu Kommandant Ashlam Ehrenschlag an der Zugwindspitze in den Westlichen Pestländern zurück.",
    ["T"] = "Den Weg räumen",
  },
  [5093] = {
    ["D"] = "Helden der Welt, höret mich - die Horde ruft zum schnellen Aufmarsch!$B$BAlle einsatzfähigen Personen werden zu den Waffen gerufen, um gegen die Geißel zu ziehen! Es gibt Gerüchte über neue Bedrohungen, die sich aus den Ruinen der Östlichen Königreiche erheben! Sie lauern östlich von Unterstadt, in den nördlichen Ausläufern der Länder, die als Pestländer bekannt sind!$B$BHelden der Welt - sucht Hochexekutor Derrington auf. Er hat seinen Stützpunkt am Bollwerk östlich von Tirisfal aufgeschlagen!",
    ["O"] = "Sucht Hochexekutor Derrington auf. Sein Stützpunk befindet sich am Bollwerk, östlich von Tirisfal und Unterstadt.",
    ["T"] = "Der Ruf zu den Waffen: Die Pestländer!",
  },
  [5094] = {
    ["D"] = "Helden der Welt, höret mich - die Horde ruft zum schnellen Aufmarsch!$B$BAlle einsatzfähigen Personen werden zu den Waffen gerufen, um gegen die Geißel zu ziehen! Es gibt Gerüchte über neue Bedrohungen, die sich aus den Ruinen der Östlichen Königreiche erheben! Sie lauern östlich von Unterstadt, in den nördlichen Ausläufern der Länder, die als Pestländer bekannt sind!$B$BHelden der Welt - sucht Hochexekutor Derrington auf. Er hat seinen Stützpunkt am Bollwerk östlich von Tirisfal aufgeschlagen!",
    ["O"] = "Sucht Hochexekutor Derrington auf. Sein Stützpunk befindet sich am Bollwerk, östlich von Tirisfal und Unterstadt.",
    ["T"] = "Der Ruf zu den Waffen: Die Pestländer!",
  },
  [5095] = {
    ["D"] = "Ihr Helden dieser Welt, höret mich - die Horde ruft zum schnellen Aufmarsch!$B$BAlle einsatzfähigen Personen werden zu den Waffen gerufen, um gegen die Geißel zu ziehen! Es gibt Gerüchte über neue Bedrohungen, die sich aus den Ruinen der Östlichen Königreiche erheben! Sie lauern östlich von Unterstadt, in den nördlichen Ausläufern der Länder, die als Pestländer bekannt sind!$B$BZögert nicht lange, ihr Helden! Sucht Hochexekutor Derrington auf! Er hat seinen Stützpunkt am Bollwerk östlich von Tirisfal aufgeschlagen.",
    ["O"] = "Sucht Hochexekutor Derrington auf. Sein Stützpunkt befindet sich am Bollwerk, östlich von Tirisfal und Unterstadt.",
    ["T"] = "Der Ruf zu den Waffen: Die Pestländer!",
  },
  [5096] = {
    ["D"] = "Vor Andorhal müssen wir die Bedrohung durch den anrückenden Scharlachroten Kreuzzug von Herdweiler niederschlagen. Dessen Lager wurde zwischen dem Teufelssteinfeld und Dalsons Tränenfeld aufgeschlagen, wodurch wir gleichzeitig einen Konflikt mit der Geißel und dem Kreuzzug riskieren müssen.$B$BMein Plan ist, sie gegeneinander auszuspielen, indem wir Euch schicken, um das Kommandozelt zu zerstören, das sie errichtet haben. Benutzt dazu die Flaschenflammen aus dieser Kiste mit Brandsätzen. Sobald es vernichtet ist, richtet Ihr dieses Banner der Geißel bei dem Zelt auf. Mit etwas Glück werden sie uns ignorieren und sich auf Rache gegen die Geißel konzentrieren.",
    ["O"] = "Zieht zum Basislager des Scharlachroten Kreuzzugs zwischen dem Teufelssteinfeld und Dalsons Tränenfeld und zerstört sein Kommandozelt.$B$BLasst das Banner der Geißel im Lager wehen und kehrt dann zu Hochexekutor Derrington am Bollwerk in den Westlichen Pestländern zurück.",
    ["T"] = "Scharlachrote Ablenkung",
  },
  [5097] = {
    ["D"] = "Durch die Verwendung eines magischen Gegenstandes, einer Signalfackel, können wir Ziele unserer Wahl für künftige Aktionen durch unsere Truppen markieren. Was das angeht, glauben wir, dass die Geißel die Wachtürme von Andorhal nutzt, um die Bewegungen ihrer Truppen zu kontrollieren.$B$BEure Mission: Durchbrecht die Verteidigung von Andorhal, um Signalfackeln an allen vier Wachtürmen zu platzieren. Ihr müsst im Eingang eines jeden Turmes stehen, um das Signal zu platzieren; es wird dann den Turm markieren. Die Signalfackel hat nur fünf Ladungen, nutzt sie also weise!",
    ["O"] = "Markiert mit der Signalfackel jeden Turm in Andorhal; Ihr müsst im Eingang jedes Turmes stehen, um ihn erfolgreich zu markieren.$B$BWenn alle vier Türme markiert sind, bringt Ihr die Signalfackel zu Kommandant Ashlam Ehrenschlag an der Zugwindspitze in den Westlichen Pestländern zurück.",
    ["T"] = "Turm um Turm",
  },
  [5098] = {
    ["D"] = "Durch die Verwendung eines magischen Gegenstandes, einer Signalfackel, können wir Ziele unserer Wahl für künftige Aktionen durch unsere Truppen markieren. Was das angeht, glauben wir, dass die Geißel die Wachtürme von Andorhal nutzt, um die Bewegungen ihrer Truppen zu kontrollieren.$B$BEure Mission: Durchbrecht die Verteidigung von Andorhal, um Signalfackeln an allen vier Wachtürmen zu platzieren. Ihr müsst im Eingang eines jeden Turmes stehen, um das Signal zu platzieren; es wird dann den Turm markieren. Die Signalfackel hat nur fünf Ladungen, nutzt sie also weise!",
    ["O"] = "Markiert mit der Signalfackel jeden Turm in Andorhal; Ihr müsst im Eingang jedes Turmes stehen, um ihn erfolgreich zu markieren.$B$BWenn alle vier Türme markiert sind, bringt Ihr die Signalfackel zu Hochexekutor Derrington am Bollwerk in den Westlichen Pestländern, zurück.",
    ["T"] = "Turm um Turm",
  },
  [5101] = {
    ["D"] = "Pah! Was glaubt Ihr\'n, was das is\', \'ne Art Test-Quest? Ich wett, dass da sogah Tippfehler drinne sin\'!$B$BUn\' wenn Ihr denkt, dass mein Quest einfach sein würd, denn irrt Ihr Euch abba gewaltich!$B$BMacht mir ma\' zehn Schafe (1933) alle und kommt erst zurück, wenn Ihr\'s getan habt!",
    ["O"] = "Tötet 6 Schafe für Benny... und dann nehmt die Füße in die Hand und rennt weg, was das Zeug hält!$B$BWar nur \'n Scherz... was das Rennen angeht.",
    ["T"] = "Lees ultimativer Test-Quest... der Verdammnis!",
  },
  [5102] = {
    ["D"] = "Wir kämpfen an zwei Fronten gegen diese Biester: Von unserer Hauptstadt Sturmwind aus bekämpfen wir sie von innen, und hier, in der Brennenden Steppe, tragen wir den Kampf an ihre Türschwelle. Wir müssen wachsam sein, $N. Niemand soll von unserem Zorn verschont werden, da keiner der Unseren verschont wurde, als der Feind bei unseren Heimen und Familien zuschlug.$B$BFindet diesen General Drakkisath und erledigt ihn!$B$BDie Aufgabe ist sicherlich nicht einfach, aber die Belohnungen werden großartig sein.",
    ["O"] = "Begebt Euch zur Schwarzfelsspitze und schaltet General Drakkisath aus.",
    ["T"] = "General Drakkisaths Niedergang",
  },
  [5121] = {
    ["D"] = "Wir wissen leider immer noch nicht, warum die Furbolgs diese merkwürdige Substanz einnehmen. Aber ich glaube, es wäre das Beste, das zu unterbinden, bevor es ausufert.$B$BDie Furbolgs der Winterfelle sind normalerweise kein aggressiver Stamm und diese Änderung ihres Verhaltens führt mich zu der Annahme, dass sie Befehle ihres Häuptlings ausführen. Wir werden wohl den Schaden, der bereits angerichtet ist, nicht mehr beheben können, aber ich denke, wenn ihr Anführer erledigt ist, wird es zumindest nicht schlimmer werden.$B$BBegebt Euch nach Osten zum Lager der Winterfelle.",
    ["O"] = "Donova Schneeweh in Winterquell möchte, dass Ihr den Oberhäuptling der Winterfelle besiegt.",
    ["T"] = "Oberhäuptling der Winterfelle",
  },
  [5123] = {
    ["D"] = "Das Logbuch, das Ihr bei dem Oberhäuptling gefunden habt, scheint recht ausführlich zu sein, aber nachdem Ihr einige Seiten durchgesehen habt, stellt Ihr fest, dass Ihr den Text nicht lesen könnt. Die Zeichen sind so kunstlos und schlecht geformt, dass Ihr nicht sicher seid, ob überhaupt jemand sie lesen kann.$B$BAber vielleicht kann Donova Euch sagen, was sie bedeuten...",
    ["O"] = "Bringt das kunstlos geschriebene Logbuch zu Donova Schneeweh in Winterquell.",
    ["T"] = "Das letzte Stück",
  },
  [5124] = {
    ["D"] = "Bringt Malyfous Düsterhammer 6 verzauberte Thoriumbarren, 2 Essenzen des Feuers und 4 Sternrubine. Ihr müsst außerdem Eure ungebrannten Plattenstulpen abgeben.",
    ["O"] = "Bringt Malyfous Düsterhammer 6 verzauberte Thoriumbarren, 2 Essenzen des Feuers und 4 Sternrubine. Ihr müsst außerdem Eure ungebrannten Plattenstulpen abgeben.",
    ["T"] = "Feurige Plattenstulpen",
  },
  [5127] = {
    ["D"] = "Lorax macht Geschäfte, aber er akzeptiert sie nicht von unbedeutenden Sterblichen.$B$BEinen Handel kann ich aber mit Euch machen... nehmt diese Pike, sie ist mit mächtigen Fel-Energien getränkt. Wenn Ihr Goraluk Hammerbruch findet, erschlagt ihn und treibt diese Pike durch sein sterbendes Herz. Er hat seine Seele verschachert und daher werde ich seine Seele bekommen. Bringt die Pike und die Brustplatte zurück, die der Dieb gestohlen hat, und ich werde Euch das beibringen, was er lernen sollte.",
    ["O"] = "Begebt Euch zur Schwarzfelsspitze und sucht Goraluk Hammerbruch. Erschlagt ihn und wendet dann die blutbefleckte Pike auf seine Leiche an. Nachdem seine Seele abgesaugt wurde, wird die Pike seelenbefleckt sein.$B$BIhr müsst außerdem die ungeschmiedete runenbedeckte Brustplatte finden.$B$BBringt die seelenbefleckte Pike und die ungeschmiedete runenbedeckte Brustplate zu Lorax in Winterquell.",
    ["T"] = "Die Dämonenschmiede",
  },
  [5128] = {
    ["D"] = "Die Winterfelle sind für mich keine Bedrohung mehr, aber wenn Ihr immer noch herausfinden möchtet, was die Furbolgs dazu brachte, dieses merkwürdige Feuerwasser herzustellen und zu konsumieren, solltet Ihr Kelek Himmelshüter aufsuchen.$B$BEr gehört zu einer Gruppe, die als Smaragdkreis bekannt ist, Druiden, die sich ihrer Arbeit im Teufelswald widmen und das Land von der Verderbnis heilen.$B$BKelek hat viel Erfahrung mit den Furbolgs im Teufelswald und kann Euch vielleicht mehr über das Logbuch sagen.$B$BNochmals danke, $N.",
    ["O"] = "Bringt das kunstlos geschriebene Logbuch zu Kelek Himmelshüter im südlichen Teil des Teufelswalds.",
    ["T"] = "Worte des Oberhäuptlings",
  },
  [5142] = {
    ["D"] = "So viele von uns sind im Kampf von Darroheim gefallen. So viele starben... und noch Schlimmeres.$B$BAls die Schlacht begann, versteckte ich meine Nichte Pamela, und ich weiß nicht, was mit ihr geschehen ist. Ich bin nur ein wandernder Geist, aber mein Herz möchte dennoch Pamelas Schicksal kennen.$B$BBitte, $C. Könnt Ihr nach Darroheim gehen und nach ihr suchen? Es liegt im Osten, hinter Gahrrons Trauerfeld.",
    ["O"] = "Sucht in Darroheim nach Pamela Rotpfad.",
    ["T"] = "Klein-Pamela",
  },
  [5143] = {
    ["D"] = "Um die Kunst der Stammeslederverarbeitung zu erlernen, müsst Ihr verstehen, wie urzeitlich die Natur wirklich ist; ich weiß, dass Ihr bereits die Kunst der Herstellung von Wildlederrüstungen gemeistert habt und ich bin bereit, Eure Ausbildung abzuschließen.$B$BDoch wisset dies, $C: Durch die Wahl dieses Weges erklärt Ihr Euch einverstanden, niemals die Kunst der Drachenlederverarbeitung oder der Elementarlederverarbeitung zu erlernen. Ihr könnt nur eine dieser drei Künste erlernen. Bringt mir des Weiteren die feinsten Proben Eurer Wildlederrüstungen. Von diesem Punkt aus werden wir dann beginnen, an Eurem Können zu arbeiten.",
    ["O"] = "Bringt eine Wildlederweste und einen Wildlederhelm zu Caryssia Jägermond in Feralas.$B$BWenn Ihr diese Aufgabe abschließt, werdet Ihr Zugang zur Kunst der Stammeslederverarbeitung erhalten.$B$BWenn Ihr diese Aufgabe abschließt, werdet Ihr allerdings weder Drachenlederverarbeitung noch Elementarlederverarbeitung erlernen können; seid also vorher sicher, dass dies der Weg ist, dem Ihr folgen wollt.",
    ["T"] = "Stammeslederverarbeitung",
  },
  [5144] = {
    ["D"] = "Elementarlederverarbeitung ist, was ich kann und wenn Ihr bereit seid, Eure Gedanken den größeren Mächten der Substanz zu öffnen, werde ich mit Euch alles teilen, was es zu lernen gibt.$B$BDoch wisset dies, $C: Durch die Wahl dieses Weges erklärt Ihr Euch einverstanden, niemals die Kunst der Drachenlederverarbeitung oder der Stammeslederverarbeitung zu erlernen. Ihr könnt nur eine dieser drei Künste erlernen. Des Weiteren benötige ich einen angemessenen Beweis, dass Ihr im Einklang mit den Elementen der Natur steht. Bringt mir dafür die reinsten Essenzen von Feuer, Wasser, Erde und Wind.",
    ["O"] = "Bringt 2 Herzen des Feuers, 2 Kugeln des Wassers, 2 Erdenkerne und 2 Odem des Windes zu Sarah Gerber in die Sengende Schlucht.$B$BWenn Ihr diese Aufgabe abschließt, werdet Ihr Zugang zur Kunst der Elementarlederverarbeitung erhalten.$B$BWenn Ihr diese Aufgabe abschließt, werdet Ihr allerdings weder Drachenlederverarbeitung noch Stammeslederverarbeitung erlernen können; seid also vorher sicher, dass dies der Weg ist, dem Ihr folgen wollt.",
    ["T"] = "Elementarlederverarbeitung",
  },
  [5145] = {
    ["D"] = "Drachenlederverarbeitung ist nicht einfach, aber sie ist sehr einträglich. Ich werde Euch beibringen, wie man diese feine Panzerrüstung fertigt, aber nur, wenn Ihr bestimmten Bedingungen zustimmt.$B$BMit der Wahl dieses Weges erklärt Ihr Euch einverstanden, niemals die Kunst der Elementarlederverarbeitung oder der Stammeslederverarbeitung zu erlernen. An der Spitze des Handwerks ist nur Platz für eine der drei. Des Weiteren werde ich eine Probe Eurer besten Arbeit aus dem Bereich der Skorpidrüstung benötigen. Schließlich benötige ich noch einen Beweis für Eure Fähigkeit, die Hauptzutat des Handwerks zu erlangen: Schuppen der Drachkin.",
    ["O"] = "Bringt 2 feste Skorpidbrustplatten, 2 feste Skorpidhandschuhe und 10 Drachenschuppen zu Thorkaf Drachenaug im Ödland.$B$BWenn Ihr diese Aufgabe abschließt, werdet Ihr Zugang zur Kunst der Drachenlederverarbeitung erhalten.$B$BIhr könnt dann allerdings weder Elementarlederverarbeitung noch Stammeslederverarbeitung erlernen; seid also vorher sicher, dass dies der Weg ist, dem Ihr folgen wollt.",
    ["T"] = "Drachenlederverarbeitung",
  },
  [5146] = {
    ["D"] = "Elementarlederverarbeitung ist, was ich kann und wenn Ihr bereit seid, Eure Gedanken den größeren Mächten der Substanz zu öffnen, werde ich mit Euch alles teilen, was es zu lernen gibt.$B$BDoch wisset dies, $C: Durch die Wahl dieses Weges erklärt Ihr Euch einverstanden, niemals die Kunst der Drachenlederverarbeitung oder der Stammeslederverarbeitung zu erlernen. Ihr könnt nur eine dieser drei Künste erlernen. Des Weiteren benötige ich einen angemessenen Beweis, dass Ihr im Einklang mit den Elementen der Natur steht. Bringt mir dafür die reinsten Essenzen von Feuer, Wasser, Erde und Wind.",
    ["O"] = "Bringt 2 Herzen des Feuers, 2 Kugeln des Wassers, 2 Erdenkerne und 2 Odem des Windes zu Brumn Winterhuf in den Arathibergen.$B$BWenn Ihr diese Aufgabe abschließt, werdet Ihr Zugang zur Kunst der Elementarlederverarbeitung erhalten.$B$BWenn Ihr diese Aufgabe abschließt, werdet Ihr allerdings weder Drachenlederverarbeitung noch Stammeslederverarbeitung erlernen können; seid also vorher sicher, dass dies der Weg ist, dem Ihr folgen wollt.",
    ["T"] = "Elementarlederverarbeitung",
  },
  [5147] = {
    ["D"] = "Gesucht: Arnak Grimmtotem$B$BArnak der Ausgestoßene wird wegen ruchloser Verbrechen des Mordes und der Entführung gesucht. Ansehnliche Belohnungen winken dem, der mutig genug ist, diesen Verbrecher der Gerechtigkeit zuzuführen.$B$BArnak wurde zuletzt auf den Klippen in der Nähe des Düsterwolkengipfels gesehen. Zeigt dem Klippenbehüter Langhorn im Freiwindposten den Beweis Eurer Tat, um die Belohnung zu beanspruchen.",
    ["O"] = "Erschlagt Arnak Grimmtotem und bringt den Beweis Eurer Tat zu Klippenbehüter Langhorn im Freiwindposten.",
    ["T"] = "GESUCHT: Arnak Grimmtotem",
  },
  [5148] = {
    ["D"] = "Um die Kunst der Stammeslederverarbeitung zu erlernen, müsst Ihr verstehen, wie urzeitlich die Natur wirklich ist; ich weiß, dass Ihr bereits die Kunst der Herstellung von Wildlederrüstungen gemeistert habt und ich bin bereit, Eure Ausbildung abzuschließen.$B$BDoch wisset dies, $C: Durch die Wahl dieses Weges erklärt Ihr Euch einverstanden, niemals die Kunst der Drachenlederverarbeitung oder der Elementarlederverarbeitung zu erlernen. Ihr könnt nur eine dieser drei Künste erlernen. Bringt mir des Weiteren die feinsten Proben Eurer Wildlederrüstungen. Von diesem Punkt aus werden wir dann beginnen, an Eurem Können zu arbeiten.",
    ["O"] = "Bringt eine Wildlederweste und einen Wildlederhelm zu Se\'Jib im Schlingendorntal.$B$BWenn Ihr diese Aufgabe abschließt, werdet Ihr Zugang zur Kunst der Stammeslederverarbeitung erhalten.$B$BWenn Ihr diese Aufgabe abschließt, werdet Ihr allerdings weder Drachenlederverarbeitung noch Elementarlederverarbeitung erlernen können; seid also vorher sicher, dass dies der Weg ist, dem Ihr folgen wollt.",
    ["T"] = "Stammeslederverarbeitung",
  },
  [5151] = {
    ["D"] = "Erschlagt die Bestie und bringt den Hyperkondensator sowie den Pantherkäfigschlüssel zu Wizlo Lagerputzer zurück.",
    ["O"] = "Erschlagt die Bestie und bringt den Hyperkondensator sowie den Pantherkäfigschlüssel zu Wizlo Lagerputzer zurück.",
    ["T"] = "Hyperkondensator-Dingsda",
  },
  [5152] = {
    ["D"] = "Oh, ich bin ja so einsam. Wo ist meine Familie? Tante Marlene hat mir gesagt, ich soll hier bleiben, aber dann ist sie weggegangen und nicht wiedergekommen! Und mein Papa... habt Ihr ihn gesehen?$B$BVielleicht weiß Tante Marlene, wo er ist. Sie lebt in der Nähe von Andorhal, in einem Haus im Süden. Ich glaub, es war auf einem Hügel... sie sagt, es wäre auf einem Hügel.$B$BBitte, $N. Wir sind doch jetzt Freunde! Könnt Ihr für mich mit meiner Tante sprechen?",
    ["O"] = "Sprecht mit Marlene, südlich der Ruinen von Andorhal.",
    ["T"] = "Tante Marlene",
  },
  [5153] = {
    ["D"] = "Ich wünschte, Joseph könnte wieder bei seiner Tochter sein, aber das geht nicht. Seine Seele wurde von der Geißel verdreht und er wurde zu einem Monster. Oh, er ist verloren!$B$BAber vielleicht können wir sein Schicksal ändern.$B$BDurchsucht die Gräber draußen nach Josephs Denkmal. Sein Körper ist nicht dort, da er vor Jahren zertrampelt und zerschmettert wurde, aber unter dem Denkmal liegt sein Ehering. Bringt den Ring zu Chromie...das ist eine merkwürdige Gnomin mit sehr merkwürdigen Kräften.$B$BChromie hält sich in dem zerstörten Gasthaus im nordwestlichen Teil von Andorhal auf.",
    ["O"] = "Bringt Josephs Ehering zu Chromie.",
    ["T"] = "Ein seltsamer Historiker",
  },
  [5154] = {
    ["D"] = "Um Joseph Rotpfad zu retten, müssen wir zuerst seine Vergangenheit enthüllen. Wir können sie den \'Annalen von Darroheim\' entnehmen, einem Buch, das im Rathaus von Andorhal aufbewahrt wird.$B$BBringt mir dieses Buch. Wir können etwas über Josephs Schicksal erfahren...und es mit etwas Glück...ändern!",
    ["O"] = "Bringt die \'Annalen von Darroheim\' zu Chromie in Andorhal.",
    ["T"] = "Die Annalen von Darroheim",
  },
  [5155] = {
    ["D"] = "Wenn ich Euch in dieser Angelegenheit vertrauen soll, müsst Ihr Euch zuerst als vertrauenswürdig erweisen.$B$BIch muss sicher sein, dass Ihr ein Feind des Schattenrates seid - seine Mitglieder sind für viele böse Taten nah und fern verantwortlich.$B$BReist tiefer in den Teufelswald hinein. Im Westen werdet Ihr einen Pfad sehen, der nach Jaedenar führt. Dort schmiedet der Rat Pläne zusammen mit der Brennenden Legion.$B$BBetretet die Tiefen eines der von ihnen bewohnten verderbten Grabhügel und macht sie nieder. Kehrt zu mir zurück, wenn Ihr dies erledigt habt. Dann weiß ich, dass Ihr uns wirklich helfen wollt.",
    ["O"] = "Betretet Jaedenar und erschlagt 4 Hunde von Jaedenar, 4 Gardisten von Jaedenar, 6 Adepten von Jaedenar und 6 Kultisten von Jaedenar, bevor Ihr zu Greta Mooshuf im Teufelswald zurückkehrt.",
    ["T"] = "Streitmacht von Jaedenar",
  },
  [5156] = {
    ["D"] = "Im Norden, entlang der östlichen Straße im so genannten Narbengrund, liegt ein Bereich, wo im letzten großen Krieg Höllenbestien herbeigerufen wurden; die Krater zernarben immer noch das Land, so wie die Höllenbestien selbst.$B$BIch möchte gern zwei Dinge wissen. Erstens, durchstreifen immer noch entropische Elementare das Gebiet? Und zweitens, sind die Krater immer noch mit dem verderbten, stinkenden Wasser gefüllt, das sich dort vor Jahren gesammelt hat? Würdet Ihr das für mich überprüfen, $N? Seid aber vorsichtig, die Höllenbestien zerstören alles, was ihren Weg kreuzt.",
    ["O"] = "Sucht und zerstört 2 entropische Bestien und 2 entropische Schrecken, während Ihr den Narbengrund erforscht. Nachdem Ihr überprüft habt, ob die Krater immer noch mit verderbtem Wasser gefüllt sind, kehrt zu Taronn Rotfeder im Teufelswald zurück.",
    ["T"] = "Die Verderbnis überprüfen",
  },
  [5157] = {
    ["D"] = "Tief in Jaedenar liegt die Schattenfeste - die Hauptfestung der Verderbnis und des Bösen.$B$BDer Schattenrat verfügt über Schutzzauber in Form von Kohlenpfannen, die seinen Unterschlupf umgeben. Wenn ich durch Wahrsagen noch weitere Informationen über unseren Feind herausfinden soll, müssen diese Kohlenpfannen gelöscht werden. Dann sollte ich den Rat ausspionieren und herausfinden können, wer ihn anführt.$B$BBitte, nehmt diese Feldflasche und füllt sie am Mondbrunnen im Zentrum von Jaedenar, wenn Euch dies möglich ist. Kommt dann zu mir zurück.",
    ["O"] = "Bringt die leere Feldflasche zum Mondbrunnen im Zentrum von Jaedenar und füllt sie, bevor Ihr zu Greta Mooshuf im Teufelswald zurückkehrt.",
    ["T"] = "Verderbtes Wasser sammeln",
  },
  [5158] = {
    ["D"] = "Bringt das verderbte Wasser ins Brachland, $N. Südlich von Ratschet, versteckt entlang des Ufers, hält sich eine alte Freundin von mir auf, eine Schamanin namens Islen Wasserdeuter. Sie ist eine mächtige Schamanin, die uns helfen wird. Ihre Verbindung zu den Geistern des Wassers war immer stark. Dass unsere Aufgabe edel ist, sollte ausreichen, um sie und die Geister des Wassers dazu zu bringen, bei dieser Aufgabe zu helfen.$B$BIhr dürftet sie in einem kleinen Lager direkt am Wasser finden. Ach ja, $N, ignoriert die Piraten...sie sind nur eine Plage und keine Bedrohung für Euch.",
    ["O"] = "Bringt das verderbte Mondbrunnenwasser in das Brachland zu Islen Wasserdeuter.",
    ["T"] = "Spirituelle Hilfe suchen",
  },
  [5159] = {
    ["D"] = "Dankt den Geistern, $N. Sie sind diejenigen, die Euch zu Hilfe kamen, nicht ich.$B$BIch gebe Euch nun dieses geläuterte Wasser. Richtet Greta bitte meine Grüße und meine besten Wünsche aus. Sie kämpft mit etwas viel Größerem als die meisten von uns... wie sie sicherlich bald herausfinden wird.$B$BDer Schattenrat ist keine Kraft, die man unterschätzen sollte, $N. Vergesst das nicht.$B$BGeht jetzt. Nehmt das Wasser und kehrt zu Greta zurück.",
    ["O"] = "Bringt das geläuterte Mondbrunnenwasser zu Greta Mooshuf im Teufelswald.",
    ["T"] = "Gesäubertes Wasser wird zum Teufelswald gebracht",
  },
  [5160] = {
    ["D"] = "Reist nach Kalimdor, $R. Unser Schwarm ist in der frostigen Landschaft von Winterquell zu Hause. Wenn Ihr Winterquell erreicht habt, sucht die Höhlen von Mazthoril.$B$BAuf den eisigen Böden von Mazthoril werdet ihr Kobaltrunen finden. Haltet eine meiner Schuppen in der Hand und stellt Euch auf eine Rune. Ihr werdet dann zu Haleh transportiert, unserer obersten Beschützerin.$B$BGebt ihr die Schuppe, damit sie weissagen kann, was ich hier an diesem verlorenen Ort gesehen und gefühlt habe.",
    ["O"] = "Begebt Euch nach Winterquell und sucht Haleh. Gebt ihr Awbees Schuppe.",
    ["T"] = "Die oberste Beschützerin",
  },
  [5161] = {
    ["D"] = "Wir verstehen, was die verfluchte Brut von Todesschwinge zu tun versucht, und wir wissen, dass das unterbunden werden muss.$B$BDurch den Schutz, den der Schwarzfels Nefarian bietet, wird es für uns immer schwieriger zu intervenieren.$B$BWir können aber Euch, sterbliches Wesen, ermächtigen - solltet Ihr Euch dafür entscheiden.$B$BDer Weg, der vor Euch liegt, ist voller Gefahren. Ihr müsst Euch entscheiden, ob Ihr unsere Hilfe annehmt. Denkt darüber nach und sprecht erneut mit mir, wenn Ihr eine Entscheidung getroffen habt.",
    ["O"] = "Sprecht mit Haleh, wenn Ihr weitermachen wollt.",
    ["T"] = "Der Zorn des blauen Drachenschwarms",
  },
  [5162] = {
    ["D"] = "Sprecht mit Jeziba in den Pestländern. Er wohnt in Andorhal.",
    ["O"] = "Sprecht mit Jeziba in den Pestländern. Er wohnt in Andorhal.",
    ["T"] = "Der Zorn des blauen Drachenschwarms",
  },
  [5163] = {
    ["D"] = "Es ist alles bereit, $N!$B$BAlso, ich habe meinen Freunden bereits eine Nachricht geschickt und ihnen gesagt, dass ich eine Überraschung für sie habe. Und hier kommt Ihr ins Spiel!$B$BIch werde Euch meinen mechanischen Yeti geben und Ihr müsst sie überraschen, indem Ihr jeden meiner Freunde aufsucht, den Yeti einschaltet und zu ihnen schickt.$B$BNachdem Ihr ihn Legacki in der Ewigen Warte, Sprinkel in Gadgetzan und Quixxil in Marschalls Zuflucht gezeigt habt, kehrt zurück und berichtet mir, was geschehen ist!$B$BBitte bringt mir meinen Yeti dann wieder zurück.",
    ["O"] = "Nehmt Umis mechanischen Yeti und erschreckt ihre Freunde damit:$B$BLegacki in der Ewigen Warte (Winterquell)$BSprinkel in Gadgetzan (Tanaris)$BQuixxil in Marschalls Zuflucht (Krater von Un\'Goro)$B$BWenn Ihr damit fertig seid, bringt den mechanischen Yeti zu Umi zurück.",
    ["T"] = "Sind wir endlich da, Yeti?",
  },
  [5165] = {
    ["D"] = "Jetzt müsst Ihr nach Jaedenar zurückkehren. Die Kohlenpfannen sind wahrscheinlich über die ganze Schattenfeste verteilt. Übergießt jede ihrer Flammen, und der Schutz, den der Rat eingerichtet hat, wird zumindest für eine kurze Zeit erlöschen. Ich werde warten, dass dies geschieht. Wenn es so weit ist, werde ich einen tiefen Blick in die Feste werfen und versuchen herauszufinden, wer der Kopf dieser Bestie ist. Dann, und nur dann, werden wir sehen, ob wir sie direkt angreifen.",
    ["O"] = "Übergießt die Flammen der vier Kohlenpfannen des Schutzes in der Schattenfeste im Teufelswald mit dem geläuterten Mondbrunnenwasser und kehrt danach zu Greta Mooshuf im Smaragdrefugium zurück.",
    ["T"] = "Übergießen der Flammen des Schutzes",
  },
  [5166] = {
    ["D"] = "Das Schmieden einer mächtigen Brustplatte zum Schutz gegen die unerbittlichen Angriffe des chromatischen Drachenschwarms ist eigentlich recht ironisch.$B$BDie Schuppen der Bestien, die wir bekämpfen, müssen an einen makellosen Knochenpanzer eines alten chromatischen Drachen oder Großdrachen genäht werden. Derzeit ist nicht bekannt, ob es überhaupt welche gibt, und wir sollten hoffen, dass es niemals welche geben wird.$B$BUnd dann ist da noch die vielleicht schwierigste aller Komponenten: das Blut von Helden.$B$BDer Bildhauer muss diese Kreation dann schmieden.",
    ["O"] = "Um die Brustplatte des chromatischen Drachenschwarms zu schmieden, müsst Ihr die folgenden Gegenstände zu Jeziba dem \'Bildhauer\' bringen:$B$B1 chromatischen Knochenpanzer.$B$B10 glänzende chromatische Schuppen.$B$B10 Einheiten Blut von Helden.$B$B10 ausgefranste Monstrositätenstickereien.",
    ["T"] = "Brustplatte des chromatischen Drachenschwarms",
  },
  [5168] = {
    ["D"] = "Die Helden von Darroheim haben einen Platz in meinem Herzen und das Buch, das Ihr mir gebracht habt, die \'Annalen von Darroheim\', erzählt ihre Geschichten.$B$BEs berichtet von dem Paladin Davil Lichterschein und davon, wie der Kreuzzug seinen Buchband zum Rathaus von Herdweiler brachte.$B$BEinige Passagen handeln auch von meinem Bruder, Hauptmann Rotpfad. Nach seinem Verrat wurde sein Schild zerschmettert und weggeworfen und liegt nun am unwahrscheinlichsten aller Orte - in der Nähe von Gahrrons Trauerfeld.$B$BSammelt diese Relikte, $N. Denn Verhängnis und Tragik umgeben sie.",
    ["O"] = "Bringt Davils Buchband und Rotpfads Schild zu Carlin Rotpfad.",
    ["T"] = "Helden von Darroheim",
  },
  [5181] = {
    ["D"] = "Die \'Annalen von Darroheim\' berichten von den Bösewichten dieser Schlacht. Sie berichten, wie der Ghulfürst Horgus der Verheerer von Kräften der Allianz während der Schlacht vernichtet wurde. Seine Leiche wurde in einen See geworfen, nordwestlich von Corins Kreuzung, und sein Schädel ist noch dort.$B$BMarduk der Schwarze wurde nie besiegt, aber sein sagenhaftes Schwert wurde zertrümmert und ging verloren. Es liegt auf dem Grund der Schlucht westlich von Corins Kreuzung.$B$BBringt diese Gegenstände zurück, $N. Ich weiß nicht, warum, aber ich bin sicher, dass ihr Schicksal in Euren Händen liegt.",
    ["O"] = "Bringt den Schädel von Horgus und das zertrümmerte Schwert von Marduk zu Carlin Rotpfad in der Kapelle des Hoffnungsvollen Lichts.",
    ["T"] = "Bösewichte von Darroheim",
  },
  [5201] = {
    ["D"] = "Meine Anwesenheit bleibt den Meisten verborgen, $N. Es gibt aber eine Störung, die hier in Winterquell fest verankert zu sein scheint: die Winterfellfurbolgs.$B$BObwohl ich die Furbolgs nie direkt angegriffen habe, scheinen sie wahnsinnig zu sein, alles, was in ihrem Weg steht, scheint sie zu ängstigen. Ich habe beobachtet, wie sie zahllose Kreaturen erschlagen haben, die das Pech hatten, einfach zur falschen Zeit am falschen Ort zu sein.$B$BIch bin ihrer Einmischungen müde... helft mir bitte, indem Ihr sie bekämpft. Erschlagt die Furbolgs und haltet sie vom Frostsäblerfelsen fern.",
    ["O"] = "Rivern Frostwind möchte, dass Ihr 5 Schamanen der Winterfelle und 5 Ursa der Winterfelle erschlagt.",
    ["T"] = "Eindringlinge der Winterfelle",
  },
  [5203] = {
    ["D"] = "Aber so sehr es mich auch schmerzt, es gibt eine Sache, die zuerst erledigt werden muss. Ich muss Treys Schwert Lichtschmied finden und aus der Schattenfeste entkommen, um meinen Vorgesetzten zu berichten, was hier geschieht. Könntet Ihr mir helfen, $N?$B$BUnsere Ausrüstung ist nicht weit von hier und Ihr habt bereits bewiesen, dass Ihr den Weg an die Spitze überleben könnt.$B$BNachdem wir entkommen sind, werde ich nach Darnassus gehen, und Euch würde ich bitten, meine Freundin Jessir in der Nähe der Grenze zum Eschental aufzusuchen. Sie sollte sich eigentlich im Lager des Smaragdkreises hier in der Nähe mit uns treffen.$B$BWas sagt Ihr dazu, $N? Würdet Ihr mir nochmals helfen?",
    ["O"] = "Bringt Arko\'narin dorthin, wo Lichtschmied und ihre Ausrüstung aufbewahrt werden, und beschützt sie dann bei eurer Flucht aus der Schattenfeste. Sucht danach Jessir Mondsichel im Teufelswald auf und lasst sie wissen, was mit ihren Freunden geschehen ist.",
    ["T"] = "Rettung aus Jaedenar",
  },
  [5204] = {
    ["D"] = "Sie möchte so tun, als ob es ihr gut ginge. Sie möchte gern, dass ich denke, sie sei stark genug, um mit alledem selbst fertigzuwerden, aber ich weiß es besser. Treys Tod und zu hören, welche Folter er erdulden musste, wird sie lange Zeit verfolgen und es gibt nichts, was wir tun können, um das zu stoppen. Aber wir können ihr ein wenig Frieden geben!$B$B$N, kehrt zur Schattenfeste zurück, tötet Treys Folterer und sucht seine Überreste. Es gibt keinen Grund, dass wir nicht all diese Dinge zu Ende bringen können, während Arko\'narin auf ihre eigene Art trauert.",
    ["O"] = "Kehrt nach Jaedenar zurück und tötet den Sukkubus Rakaiah, bevor Ihr die Überreste von Trey Lichtschmied sucht.",
    ["T"] = "Vergeltung des Lichts",
  },
  [5205] = "_",
  [5206] = {
    ["D"] = "Chromie sagte, dass die von Euch gesammelten Relikte Reagenzien für einen mächtigen Zauber seien und dass noch eines fehle: Schädel von Angehörigen der Geißel, die an der Schlacht von Darroheim teilnahmen.$B$BNehmt diesen Kristall und jagt um ihrer Schädel willen Champions der Geißel. Der Kristall ist verzaubert: Wenn ein Held an der Schlacht teilnahm, dann wird er, wenn Ihr ihn über dessen Schädel haltet, widerhallen. Bringt mir diese widerhallenden Schädel.$B$BGeht, $N. Ihr könnt Champions der Geißel im Giftigen Tal im Norden und im Fungustal im Westen finden.",
    ["O"] = "Bringt 5 widerhallende Schädel und den mystischen Kristall zu Carlin Rotpfad in den Östlichen Pestländern.",
    ["T"] = "Marodeure von Darroheim",
  },
  [5207] = "_",
  [5208] = "_",
  [5209] = "_",
  [5210] = {
    ["D"] = "Die \'Annalen von Darroheim\' haben mithilfe meiner Magie an ihrem Ende Seiten hinzugewonnen, und nun erstreckt sich die Geschichte auch über den Kampf hinaus. Eine der zusätzlichen Passagen berichtet von einem anderen Rotpfad, Carlin, der Josephs Verrat überlebt hat. Carlin befindet sich jetzt in den Pestländern im Osten, bei der Kapelle des Hoffnungsvollen Lichts.$B$BNehmt das Buch und sprecht mit ihm.",
    ["O"] = "Bringt die erweiterten \'Annalen von Darroheim\' zu Carlin Rotpfad in den Östlichen Pestländern.",
    ["T"] = "Bruder Carlin",
  },
  [5211] = {
    ["D"] = "Viele der Verteidiger fielen in der Schlacht von Darroheim. Und viele ziehen erneut umher als grässliche Diener der Geißel! Sucht diese armen Seelen und beendet ihr Elend!$B$BJagt Menschenfresserghule, jammernde Ghule und kranke Schinder. Viele dieser Scheusale waren einst gute Leute aus Darroheim und die Zerstörung ihrer Formen wird ihre Seelen befreien.$B$BIhr werdet sie im Pestwald und bei Corins Kreuzung finden, ebenso wie in anderen Geißelfestungen in den Östlichen Pestländern.",
    ["O"] = "Befreit 15 Geister von Darroheim und kehrt dann zu Carlin Rotpfad zurück.",
    ["T"] = "Verteidiger von Darroheim",
  },
  [5212] = {
    ["D"] = "Sammelt 10 verseuchte Fleischproben in Stratholme und bringt sie zu Betina Moppelzink zurück. Ihr vermutet, dass Ihr besagte Fleischproben bei jeder Kreatur in Stratholme finden könnt.",
    ["O"] = "Sammelt 10 verseuchte Fleischproben in Stratholme und bringt sie zu Betina Moppelzink zurück. Ihr vermutet, dass Ihr besagte Fleischproben bei jeder Kreatur in Stratholme finden könnt.",
    ["T"] = "Das Fleisch lügt nicht",
  },
  [5213] = {
    ["D"] = "Reist nach Stratholme und durchsucht die Ziggurats. Sucht neue Geißeldaten und bringt sie zu Betina Moppelzink zurück.",
    ["O"] = "Reist nach Stratholme und durchsucht die Ziggurats. Sucht neue Geißeldaten und bringt sie zu Betina Moppelzink zurück.",
    ["T"] = "Der aktive Wirkstoff",
  },
  [5214] = {
    ["D"] = "Schrecklich... was mit Fras geschehen ist. Wisst Ihr, die Stadt wurde geplündert und Fras wollte seine wertvollen Dinge nicht zurücklassen. Das Letzte, woran ich mich erinnere, ist, dass er sagte: \'Ich werde jede einzelne Seite verbrennen, bevor ich zulasse, dass sie sie in ihre verdammten, schmutzigen Pfoten kriegen!\'$B$BIch rauche ja auch ganz gern mal eine Zigarre, aber ich bin kein Idiot. Ich habe Fras und sein reizendes Geschäft an dem Tag verlassen. Ich nehme an, dass er in den Flammen unterging, aber wenn es eine Chance gibt, dass auch nur ein Kasten seines berühmten Tabaks noch intakt ist...$B$BWas sagt Ihr dazu, $N? Sucht Ihr einen Kasten für den alten Smokey?",
    ["O"] = "Sucht Fras Siabis Raucherladen in Stratholme und bergt einen Kasten von Siabis Tollem Tabak. Kehrt zu Smokey LaRue zurück, wenn Eure Aufgabe erledigt ist.",
    ["T"] = "Der große Fras Siabi",
  },
  [5215] = {
    ["D"] = "In den Ländern des ehemaligen Lordaeron befinden sich acht Kessel - vier hier im Westen und vier weitere versteckt im Osten. Diese Kessel speien Gifte in die Luft und verbreiten die Seuche für die Geißel im ganzen Land.$B$BDie nekromantische Macht von Kel\'Thuzad ist zu stark, um diese Kessel gänzlich zu schließen, aber wir glauben zu wissen, wie wir sie zu unserem Vorteil nutzen könnten. Meine rechte Hand, die Hohepriesterin MacDonnell, wird Eure Kontaktperson für diesen Auftrag sein; sie wird die Einweisung fortsetzen.",
    ["O"] = "Sprecht mit der Hohepriesterin MacDonnell an der Zugwindspitze in den Westlichen Pestländern.",
    ["T"] = "Die Geißelkessel",
  },
  [5216] = {
    ["D"] = "Von den vier Kesseln, die sich hier im Westen befinden, ist derjenige, der sich auf dem Teufelssteinfeld befindet, wohl der, zu dem der Zugang am einfachsten ist. Die Geißel hat hier Truppen mit weniger Erfahrung positioniert, sie sollten aber trotzdem nicht unterschätzt werden.$B$BGeht zum Teufelssteinfeld, nordwestlich von Andorhal, und befasst Euch dort mit dem Kessellord. Wir glauben, dass er einen Schlüssel hat, der den Zugang zu dem Kessel freigeben wird. Wenn er frei ist, benutzt diese Flasche, um dem Kessel eine Probe des Giftes zu entnehmen; wir benötigen sie für die Entwicklung eines Gegenwirkstoffs.",
    ["O"] = "Geht zum Teufelssteinfeld in den Westlichen Pestländern. Sucht nach dem dortigen Kessellord und besiegt ihn. Er könnte einen Schlüssel haben, der den Zugang zu dem Kessel ermöglicht. Ihr müsst die leere Flasche: Teufelssteinfeld bei Euch haben, um eine Probe des Giftes zu entnehmen, das sich in dem Kessel findet.",
    ["T"] = "Ziel: Teufelssteinfeld",
  },
  [5217] = {
    ["D"] = "Ihr füllt die Flasche mit einer ausreichenden Menge des giftigen Gebräus aus dem Kessel, sodass es für eine Probe zur Untersuchung reicht.$B$BMit dem Gift in der Hand ist es Zeit, zur Hohepriesterin MacDonnell an der Zugwindspitze zurückzukehren, um die Mission abzuschließen.",
    ["O"] = "Bringt die gefüllte Flasche: Teufelssteinfeld zur Hohepriesterin MacDonnell an der Zugwindspitze in den Westlichen Pestländern.",
    ["T"] = "Rückkehr zum Zugwindlager",
  },
  [5220] = {
    ["D"] = "Vorsichtig füllt Ihr so viel, wie Ihr für eine sinnvolle Probe für nötig erachtet, von dem Sekret aus dem Kessel in die Flasche.$B$BMit dem Gift in der Hand ist es Zeit, zur Hohepriesterin MacDonnell an der Zugwindspitze zurückzukehren, um die Mission abzuschließen.",
    ["O"] = "Bringt die gefüllte Flasche: Dalsons Tränenfeld zur Hohepriesterin MacDonnell an der Zugwindspitze in den Westlichen Pestländern.",
    ["T"] = "Rückkehr zum Zugwindlager",
  },
  [5222] = {
    ["D"] = "Zwei Kessel wurden erfolgreich geöffnet. Auf diese könnt Ihr jetzt zwar unbegrenzt zugreifen, aber wir möchten immer noch, dass Ihr die beiden verbleibenden öffnet. Damit erhielten wir weitere Möglichkeiten, den Gegenwirkstoff einzusetzen!$B$BDieses Mal werdet Ihr Euch um den Kessel des Trostlosen Felds kümmern. Er befindet sich ziemlich genau östlich von Andorhal, relativ nahe bei den zerstörten Stadtmauern. Eliminiert auch dort den Kessellord, um an den Schlüssel zu gelangen. Benutzt diese Flasche, um eine Probe aus dem Kessel zu entnehmen.$B$BGute Jagd, $N!",
    ["O"] = "Begebt Euch zum Trostlosen Feld in den Westlichen Pestländern, um den dortigen Kessellord zu suchen und zu besiegen. Verwendet dann seinen Schlüssel, um Zugang zum Kessel zu erhalten. Ihr müsst die leere Flasche: Das Trostlose Feld bei Euch haben, um eine Probe des Giftes zu entnehmen, das sich in dem Kessel findet.",
    ["T"] = "Ziel: Das Trostlose Feld",
  },
  [5223] = {
    ["D"] = "Vorsichtig füllt Ihr die leere Flasche. Jetzt schwappt eine Probe der stinkenden Substanz, die unter der Plattform des Kessels brodelt, in dem zerbrechlichen Glasbehälter herum.$B$BMit dem Gift in der Hand ist es Zeit, zur Hohepriesterin MacDonnell an der Zugwindspitze zurückzukehren, um die Mission abzuschließen.",
    ["O"] = "Bringt die gefüllte Flasche: Das Trostlose Feld zur Hohepriesterin MacDonnell an der Zugwindspitze in den Westlichen Pestländern.",
    ["T"] = "Rückkehr zum Zugwindlager",
  },
  [5225] = {
    ["D"] = "Der letzte verbleibende Kessel befindet sich auf einem Hof, der jetzt Gahrrons Trauerfeld genannt wird. Er wird durch einige der stärkeren Diener der Geißel bewacht... um genau zu sein durch Gespenster und Geisterscheinungen. Die darf man nicht unterschätzen, $N.$B$BGahrrons Trauerfeld liegt von unserer Position hier aus im Nordosten, etwa dort, wo die Östlichen Pestländer beginnen. Eliminiert auch dort den Kessellord, um an den Schlüssel zu gelangen. Hier ist Eure leere Probenflasche; bringt sie mir gefüllt zurück. Möge das Licht Euch bei dieser gefährlichen Mission schützen, $C.",
    ["O"] = "Begebt Euch zu Gahrrons Trauerfeld in den Westlichen Pestländern, um den dortigen Kessellord zu suchen und zu besiegen. Verwendet dann seinen Schlüssel, um Zugang zum Kessel zu erhalten. Ihr müsst die leere Flasche: Gahrrons Trauerfeld bei Euch haben, um eine Probe des Giftes zu entnehmen, das sich in dem Kessel findet.",
    ["T"] = "Ziel: Gahrrons Trauerfeld",
  },
  [5226] = {
    ["D"] = "Ihr schließt sorgfältig den Hahn in der Zugangsklappe und beobachtet, wie das faulige Sekret des Kessels in die leere Flasche tropft.$B$BMit dem Gift in der Hand ist es Zeit, zur Hohepriesterin MacDonnell an der Zugwindspitze zurückzukehren, um die Mission abzuschließen.",
    ["O"] = "Bringt die gefüllte Flasche: Gahrrons Trauerfeld zur Hohepriesterin MacDonnell an der Zugwindspitze in den Westlichen Pestländern.",
    ["T"] = "Rückkehr zum Zugwindlager",
  },
  [5228] = {
    ["D"] = "In den Ländern des ehemaligen Lordaeron befinden sich acht Kessel - vier im Westen und vier weitere verschanzt im Osten. Diese Kessel speien Gifte in die Luft und verbreiten die Seuche für die Geißel im ganzen Land.$B$BDie nekromantische Macht von Kel\'Thuzad ist zu stark, um diese Kessel gänzlich zu schließen, aber wir glauben zu wissen, wie wir sie zu unserem Vorteil nutzen könnten. Meine rechte Hand, die Schattenpriesterin Vandis wird Eure Kontaktperson für diesen Auftrag sein; sie wird die Einweisung fortsetzen.",
    ["O"] = "Sprecht mit der Schattenpriesterin Vandis am Bollwerk in den Westlichen Pestländern.",
    ["T"] = "Die Geißelkessel",
  },
  [5229] = {
    ["D"] = "Von den vier Kesseln, die sich in den Westlichen Pestländern befinden, ist derjenige, der sich im Teufelssteinfeld befindet, wohl der, zu dem der Zugang am einfachsten ist. Die Geißel hat hier Truppen mit weniger Erfahrung positioniert, sie sollten aber trotzdem nicht unterschätzt werden.$B$BGeht zum Teufelssteinfeld, direkt östlich von hier und nordwestlich von Andorhal, und befasst Euch dort mit dem Kessellord. Er hat einen Schlüssel bei sich, der den Zugang zu dem Kessel freigeben wird. Wenn er frei ist, benutzt diese Flasche, um dem Kessel eine Probe des Giftes zu entnehmen; wir benötigen sie für die Entwicklung eines Gegenwirkstoffs.",
    ["O"] = "Geht zum Teufelssteinfeld in den Westlichen Pestländern. Sucht nach dem dortigen Kessellord und besiegt ihn. Er könnte einen Schlüssel haben, der den Zugang zu dem Kessel ermöglicht. Ihr müsst die leere Flasche: Teufelssteinfeld bei Euch haben, um eine Probe des Giftes zu entnehmen, das sich in dem Kessel findet.",
    ["T"] = "Ziel: Teufelssteinfeld",
  },
  [5230] = {
    ["D"] = "Ihr füllt die Flasche mit einer ausreichenden Menge des giftigen Gebräus aus dem Kessel, sodass es für eine Probe zur Untersuchung reicht.$B$BMit dem Gift in der Hand ist es Zeit, zur Schattenpriesterin Vandis am Bollwerk zurückzukehren, um die Mission abzuschließen.",
    ["O"] = "Bringt die gefüllte Flasche: Teufelssteinfeld zur Schattenpriesterin Vandis am Bollwerk in den Westlichen Pestländern.",
    ["T"] = "Rückkehr zum Bollwerk",
  },
  [5232] = {
    ["D"] = "Vorsichtig füllt Ihr so viel, wie Ihr für eine sinnvolle Probe für nötig erachtet, von dem Sekret aus dem Kessel in die Flasche.$B$BMit dem Gift in der Hand ist es Zeit, zur Schattenpriesterin Vandis am Bollwerk zurückzukehren, um die Mission abzuschließen.",
