  [5844] = {
    ["D"] = "Bringt Vorn Himmelsdeuter im Dorf der Bluthufe den Geschenkgutschein der Bluthufe.",
    ["O"] = "Bringt Vorn Himmelsdeuter im Dorf der Bluthufe den Geschenkgutschein der Bluthufe.",
    ["T"] = "Willkommen!",
  },
  [5845] = {
    ["D"] = "Der Orden der Silbernen Hand wurde gar sehr geschwächt, als Uther sein gewaltsames Ende fand.$B$BDer Junge hat durchgehalten, solange er konnte. Er wurde bis zum vom Krieg zerrissenen Weiler Nordtal zurückgetrieben, und dort stellte er sich zu seinem letzten Kampf.$B$BWar noch irgendjemand aus dem Orden am Leben, fragte er sich - und spielte das überhaupt noch eine Rolle?$B$BMit diesem Gedanken im Kopf schmetterte Taelan die Standarte des Ordens zu Boden und entsagte allem, was er gelernt hatte. Auf dem blutgetränkten Boden des Nordtals ließ er seine Ehre zurück.$B$BIhr müsst ins Nordtal reisen und das Symbol der verlorenen Ehre finden.",
    ["O"] = "Reist ins Nordtal im nordöstlichsten Gebiet der Östlichen Pestländer und sucht nach dem Symbol der verlorenen Ehre. Kehrt zu Tirion Fordring zurück, sobald Ihr Eure Mission abgeschlossen habt.",
    ["T"] = "Von verlorener Ehre",
  },
  [5846] = {
    ["D"] = "Als Taelan noch ein Kind war, unternahmen wir mit der Familie oft Ausflüge nach Darrowehr. Bei unserem letzten Besuch malte eine Künstlerin namens Renfray ein Porträt von uns, wie wir am Strand posierten. Das ist die schönste Erinnerung, die ich an Taelan und Karandra habe. Denn es war just dieser Moment, wo ich meine Frau und meinen Sohn in den Armen hielt, dass ich eine Liebe und Verbundenheit mit meiner Familie spürte, die ich seitdem nie wieder erlebt habe.$B$BFalls dieses Gemälde noch existiert, müsst Ihr es finden.$B$BBegebt Euch zur in Trümmern liegenden Insel Darrowehr und seht nach, ob das Gemälde der Künstlerin noch da ist.",
    ["O"] = "Begebt Euch zur Insel Darrowehr im südlich-zentralen Bereich der Pestländer und sucht nach Hinweisen, wo sich das Gemälde befinden könnte.",
    ["T"] = "Von Liebe und Familie",
  },
  [5847] = {
    ["D"] = "Bringt Claire Weidner in Brill den Geschenkgutschein von Brill.",
    ["O"] = "Bringt Claire Weidner in Brill den Geschenkgutschein von Brill.",
    ["T"] = "Willkommen!",
  },
  [5861] = {
    ["D"] = "Ihr habt alles getan, um das ich Euch bislang bat, $N. Jetzt fehlt nur noch ein Schritt auf Eurem Weg zur Erlösung.$B$BIhr müsst die gesammelten Gegenstände Taelan aushändigen. Leider werden Euch Taelan und seine Scharlachroten Kreuzzügler angreifen, sobald sie Euer ansichtig werden.$B$BEs gibt nur eine Möglichkeit, meine Nachricht zu übermitteln, und zwar durch Täuschung.$B$BIm Süden findet Ihr Uthers Grabmal. Myranda, eine alte und getreue Vertraute von mir, lebt jetzt dort - geht zu ihr. Zeigt ihr die Gegenstände, dann wird sie Euch helfen.",
    ["O"] = "Begebt Euch zu Uthers Grabmal in den Südlichen Pestländern. Zeigt Myranda der Vettel Tirions Geschenk.",
    ["T"] = "Myranda suchen",
  },
  [5862] = {
    ["D"] = "Ich bin eine Illusionistin, wie Ihr das nennen würdet. Ich könnte zwar eine Illusion erschaffen, die es Euch ermöglichen würde, Herdweiler zu betreten, aber seid gewarnt, meine Kräfte haben Grenzen.$B$BSolltet Ihr Euch zu weit aus diesem Land entfernen, lassen die Effekte dieser Illusion nach. Der Zauber selbst bedarf Unmengen an Konzentration und Kraft meinerseits, und deshalb kann ich den Effekt nur für kurze Zeit aufrechterhalten.$B$BKommt wieder zu mir, wenn Ihr bereit seid.",
    ["O"] = "Sprecht mit Myranda, um die Scharlachrote Illusion zu erhalten. Begebt Euch nach Herdweiler, während Ihr unter dem Einfluss der Scharlachroten Illusion steht, und übergebt dem Hochlord Taelan Fordring Tirions Geschenk.$B$BHerdweiler liegt im Norden, Taelan dürfte sich in der Burg Mardenholde aufhalten.",
    ["T"] = "Scharlachrote Täuschung",
  },
  [5863] = {
    ["D"] = "Erst kürzlich ist eine große Gruppe nomadischer Oger in Tanaris eingetroffen. Südlich von hier haben sie einen großen Bereich der Wüste in Besitz genommen und ein Truppenlager aufgeschlagen. Mehrere kleinere Gruppen durchstreifen momentan die Wüste, einige davon sind in die alten Trollruinen eingedrungen.$B$BDas Truppenlager der Dünenbrecher muss zerstört werden! Wir dürfen nicht zulassen, dass diese Oger zu einer Streitmacht heranwachsen.$B$BDer Anführer der Gruppe heißt Gor\'marok der Verheerer. Ich bin mir sicher, dass es den Anfang vom Ende für die Oger bedeuten würde, wenn man ihnen den Anführer nimmt.",
    ["O"] = "Andi Lynn in Gadgetzan möchte, dass Ihr das Truppenlager der Dünenbrecher vernichtet, indem Ihr 10 Schläger der Dünenbrecher 10 Vollstrecker der Dünenbrecher sowie Gor\'marok den Verheerer tötet.",
    ["T"] = "Das Truppenlager der Dünenbrecher",
  },
  [5881] = {
    ["D"] = "Die Horde kümmert sich gut um ihre Truppen... seit dem großen Krieg ging es uns hier in Kalimdor sehr gut.$B$BIhr müsst mir einen Gefallen tun. Ich lasse einen Jungen namens Grish Fernläufer den Großen Aufzug im Südlichen Brachland bewachen. Gebt ihm diesen Brief, er wird ihn wissen lassen, dass ich einen Ersatzmann angefordert habe, um ihn von seinem Dienst zu entbinden, damit er seine Familie besuchen kann. Er bat mich vor einer Weile darum und wird sich über diese Nachricht freuen.",
    ["O"] = "Maggran möchte, dass Ihr Grish Fernläufer am Großen Aufzug im Brachland einen Brief zustellt.",
    ["T"] = "Hinzurufen der Reserve",
  },
  [5892] = {
    ["D"] = "Bevor die Schlacht begann, lagerten wir Vorräte für Dun Baldar in der Eisenschachtmine. Die Troggs, die dort hausten, waren kein Problem solange wir Ersatztruppen hatten, die die Mine bewachen konnten, aber da wir nun gegen die Horde kämpfen, können wir sie nicht mehr kontrollieren.$B$BWir brauchen die Vorräte in der Eisenschachtmine, $N. Betretet die Mine und bring mir eine Ladung.",
    ["O"] = "Bringt 10 Vorräte der Eisenschachtmine zum Rüstmeister der Allianz in Dun Baldar.",
    ["T"] = "Vorräte der Eisenschachtmine",
  },
  [5893] = {
    ["D"] = "Die Eisbeißermine im Norden war einst ein idealer Ort, um zusätzliche Vorräte zu lagern, aber da die Kämpfe mit den Sturmlanzen nun härter werden, haben wir nicht immer die notwendigen Soldaten über, um sie an der Mine zu positionieren und diese zu bewachen. Daher fällt die Kontrolle über diese Mine oft an die Allianz oder an Untergrundvölker...$B$BAber wir benötigen dennoch unsere Vorräte, $N! Geht zu der Eisbeißermine und bringt mir eine Ladung. Seid wachsam, was unsere Feinde anbelangt, die Übermacht in der Schlacht kann sich an jenem Ort schnell ändern...",
    ["O"] = "Bringt 10 Vorräte der Eisbeißermine zum Rüstmeister der Horde in die Burg Frostwolf.",
    ["T"] = "Vorräte der Eisbeißermine",
  },
  [5901] = {
    ["D"] = "Also, um Schmerz und Unfrieden über den Scharlachroten Kreuzzug zu bringen, möchte ich ihnen ihr wertvolles Sägewerk wegnehmen. Ich bin mir sicher, der Verlust einer solchen Ressource wird ihnen ziemlichen Schaden in Bezug auf Verteidigung und Wiederaufbau der Stadt für ihre Zwecke zufügen. Werdet Ihr mir helfen, $N?$B$BFangt an, indem Ihr diesen Krug zum Pestwald in den Östlichen Pestländern bringt. Füllt ihn mit den Termiten, die Ihr in ihren Hügeln überall im Wald findet. Kommt wieder, wenn Ihr eine ausreichende Menge habt.",
    ["O"] = "Wendet den leeren Termitenkrug auf die Termitenhügel in den Östlichen Pestländern an. Sobald Ihr 100 Pestlandtermiten gesammelt habt, kehrt zu Mickey Levine am Bollwerk in Tirisfal zurück.",
    ["T"] = "Eine Plage komme über Euch",
  },
  [5902] = {
    ["D"] = "Da wir jetzt jede Menge Termiten haben, bringt dieses stahlverstärkte Fass zum Sägewerk des Nordkamms südlich von Herdweiler. Ihr werdet es westlich der Straße finden, die Euch in die Stadt hinauf führt.$B$BWenn Ihr dort seid, stellt Ihr das Fass in die Mitte des Sägewerkes, irgendwo weit oben, vielleicht auf eine Kiste oder so was.$B$BNachdem Ihr es geöffnet habt, sollte sich der Rest von selbst erledigen. Kommt einfach hierher zurück und ich werde Euch dafür belohnen, dass Ihr mir geholfen habt.",
    ["O"] = "Sucht Euch einen geeigneten Ort in der Mitte des Sägewerk des Nordkamms in den Westlichen Pestländern, um das Fass mit den Pestlandtermiten abzustellen.",
    ["T"] = "Eine Plage komme über Euch",
  },
  [5903] = {
    ["D"] = "Ich habe bereits viele Male versucht, den Scharlachroten Kreuzzug allein mit Gewalt auszulöschen, doch ohne Erfolg. Daher habe ich mich entschlossen, ihnen das Leben schwer zu machen.$B$BWenn Ihr bereit seid zu helfen, möchte ich, dass Ihr dieses Sägewerk mit der übelsten Termitenart verseucht, die je gefunden wurde.$B$BIm Pestwald, nahe dem einst großen Stratholme, gibt es eine Spezies, die die Seuche und auch die Geißel überlebt hat. Sucht ihre Hügel und füllt diesen Krug mit ihnen, damit wir sie darauf vorbereiten können, auf den Kreuzzug losgelassen zu werden.",
    ["O"] = "Wendet den leeren Termitenkrug auf die Termitenhügel in den Östlichen Pestländern an. Sobald Ihr 100 Pestlandtermiten gesammelt habt, kehrt zu Nathaniel Dumah im Zugwindlager in den Westlichen Pestländern zurück.",
    ["T"] = "Eine Plage komme über Euch",
  },
  [5904] = {
    ["D"] = "Da wir jetzt die Termiten haben, bringt dieses stahlverstärkte Fass zum Sägewerk des Nordkamms südlich von Herdweiler. Ihr werdet es westlich der Straße finden, die Euch in die Stadt hinauf führt.$B$BWenn Ihr dort seid, stellt Ihr das Fass in die Mitte des Sägewerkes. Findet einen geeigneten Ort, vielleicht eine Kiste, und öffnet es dann. Die Termiten dürften dann ihre Arbeit tun und die Mühle für den Kreuzzug unbrauchbar machen.",
    ["O"] = "Sucht Euch einen geeigneten Ort in der Mitte des Sägewerk des Nordkamms in den Westlichen Pestländern, um das Fass mit den Pestlandtermiten abzustellen.",
    ["T"] = "Eine Plage komme über Euch",
  },
  [5921] = {
    ["D"] = "Alle Druiden sind in den hochheiligen Landen der Mondlichtung willkommen, ungeachtet der derzeitigen Spannungen im Zirkel des Cenarius zwischen dem Erzdruiden Hirschhaupt und dem Bewahrer Remulos, dem Wächter der Mondlichtung. Jetzt, da Ihr den Weg der Klaue lernt, bringe ich Euch den Zauber bei, der es Euch ermöglicht, Euch selbst dorthin zu befördern. Wirkt ihn, wann immer Ihr das Wissen der Mondlichtung oder Zuflucht sucht.$B$BAls Eure erste Lektion müsst Ihr zur Mondlichtung reisen und mit Dendrite Sternenschauer in Nachthafen über den Pfad sprechen, auf dem Ihr jetzt wandelt. Viel Glück, $Gjunger Druide:junge Druidin;.",
    ["O"] = "Benutzt den Zauber \'Teleportation: Mondlichtung\', um zur Mondlichtung zu reisen. Wenn Ihr dort ankommt, sprecht mit Dendrite Sternenschauer in Nachthafen.",
    ["T"] = "Mondlichtung",
  },
  [5922] = {
    ["D"] = "Alle Druiden sind in den hochheiligen Landen der Mondlichtung willkommen, Tauren wie Nachtelfen. Bewahrer Remulos - der Wächter der Mondlichtung - wacht über alle Druiden, die hier ihren Winterschlaf halten. Jetzt, da Ihr den Weg der Klaue lernt, bringe ich Euch den Zauber bei, der es Euch ermöglicht, Euch selbst dorthin zu befördern. Wirkt ihn, wann immer Ihr das Wissen der Mondlichtung oder Zuflucht sucht.$B$BAls Eure erste Lektion müsst Ihr zur Mondlichtung reisen und mit Dendrite Sternenschauer in Nachthafen über den Pfad sprechen, auf dem Ihr jetzt wandelt. Viel Glück, $Gjunger Druide:junge Druidin;.",
    ["O"] = "Benutzt den Zauber \'Teleportation: Mondlichtung\', um zur Mondlichtung zu reisen. Wenn Ihr dort ankommt, sprecht mit Dendrite Sternenschauer in Nachthafen.",
    ["T"] = "Mondlichtung",
  },
  [5923] = {
    ["D"] = "Entschuldigung...$B$BEs ist mir zu Ohren gekommen, dass Mathrengyl Bärenfährte bekannt gegeben hat, dass er mit Euch sprechen möchte. Es scheint, dass Eure Ausbildung als Druide ein neues Gesicht bekommen soll und dass er Euch persönlich ausbilden will. Mathrengyl ist der direkte Assistent des Erzdruiden selbst und einer der beflissensten und erhabensten Druiden, die Ihr in Teldrassil finden könnt.$B$BEr befindet sich in der Enklave des Cenarius von Darnassus, im zweiten Stock von Fandral Hirschhaupts Baum.",
    ["O"] = "Sprecht mit Mathrengyl Bärenfährte in der Enklave von Cenarius in Darnassus.",
    ["T"] = "Dem Ruf folgen",
  },
  [5924] = {
    ["D"] = "Entschuldigung...$B$BEs ist mir zu Ohren gekommen, dass Mathrengyl Bärenfährte bekannt gegeben hat, dass er mit Euch sprechen möchte. Es scheint, dass Eure Ausbildung als Druide ein neues Gesicht bekommen soll und dass er Euch persönlich ausbilden will. Mathrengyl ist der direkte Assistent des Erzdruiden selbst und einer der beflissensten und erhabensten Druiden, die Ihr in Teldrassil finden könnt.$B$BEr befindet sich in der Enklave des Cenarius von Darnassus, im zweiten Stock von Fandral Hirschhaupts Baum.",
    ["O"] = "Sprecht mit Mathrengyl Bärenfährte in der Enklave von Cenarius in Darnassus.",
    ["T"] = "Dem Ruf folgen",
  },
  [5925] = {
    ["D"] = "Entschuldigung...$B$BEs ist mir zu Ohren gekommen, dass Mathrengyl Bärenfährte bekannt gegeben hat, dass er mit Euch sprechen möchte. Es scheint, dass Eure Ausbildung als Druide ein neues Gesicht bekommen soll und dass er Euch persönlich ausbilden will. Mathrengyl ist der direkte Assistent des Erzdruiden selbst und einer der beflissensten und erhabensten Druiden, die Ihr in Teldrassil finden könnt.$B$BEr befindet sich in der Enklave des Cenarius von Darnassus, im zweiten Stock von Fandral Hirschhaupts Baum.",
    ["O"] = "Sprecht mit Mathrengyl Bärenfährte in der Enklave von Cenarius in Darnassus.",
    ["T"] = "Dem Ruf folgen",
  },
  [5926] = {
    ["D"] = "Entschuldigung...$B$BEs ist mir zu Ohren gekommen, dass Turak Runentotem bekannt gegeben hat, dass er mit Euch sprechen möchte. Es scheint, dass Eure Ausbildung als Druide ein neues Gesicht bekommen soll und dass er Euch persönlich ausbilden will. Turak ist einer der Assistenten des Erzdruiden. Er ist als weiser und geduldiger Druide bekannt und wird Euch gut anleiten.$B$BEr befindet sich in Donnerfels, im Hauptzelt des Zirkels des Cenarius auf der Anhöhe der Ältesten.",
    ["O"] = "Sprecht mit Turak Runentotem auf der Anhöhe der Ältesten in Donnerfels.",
    ["T"] = "Dem Ruf folgen",
  },
  [5927] = {
    ["D"] = "Entschuldigung...$B$BEs ist mir zu Ohren gekommen, dass Turak Runentotem bekannt gegeben hat, dass er mit Euch sprechen möchte. Es scheint, dass Eure Ausbildung als Druide ein neues Gesicht bekommen soll und dass er Euch persönlich ausbilden will. Turak ist einer der Assistenten des Erzdruiden. Er ist als weiser und geduldiger Druide bekannt und wird Euch gut anleiten.$B$BEr befindet sich in Donnerfels, im Hauptzelt des Zirkels des Cenarius auf der Anhöhe der Ältesten.",
    ["O"] = "Sprecht mit Turak Runentotem auf der Anhöhe der Ältesten in Donnerfels.",
    ["T"] = "Dem Ruf folgen",
  },
  [5928] = {
    ["D"] = "Entschuldigung...$B$BEs ist mir zu Ohren gekommen, dass Turak Runentotem bekannt gegeben hat, dass er mit Euch sprechen möchte. Es scheint, dass Eure Ausbildung als Druide ein neues Gesicht bekommen soll und dass er Euch persönlich ausbilden will. Turak ist einer der Assistenten des Erzdruiden. Er ist als weiser und geduldiger Druide bekannt und wird Euch gut anleiten.$B$BEr befindet sich in Donnerfels, im Hauptzelt des Zirkels des Cenarius auf der Anhöhe der Ältesten.",
    ["O"] = "Sprecht mit Turak Runentotem auf der Anhöhe der Ältesten in Donnerfels.",
    ["T"] = "Dem Ruf folgen",
  },
  [5929] = {
    ["D"] = "Im nordwestlichen Teil der Mondlichtung liegt das Zuhause eines weisen und edlen Wesens, das wir den großen Bärengeist nennen. Er hat allen Druiden als Tutor und Mentor zur Seite gestanden, die das erste Mal wahres und natürliches Verständnis für den Bären entwickeln wollten. Alle Druiden suchen an irgendeinem Punkt ihres Lebens die Weisheit des Geistes und auch Ihr werdet keine Ausnahme sein.$B$BGeht nun zum großen Bärengeist und lernt, was er Euch beibringen wird. Wenn er mit Euch zufrieden ist, kehrt zu mir hier nach Nachthafen zurück. Wir werden dann sehen, ob Ihr die Lektionen, die er Euch gelehrt hat, wirklich verinnerlicht habt.",
    ["O"] = "Sucht den großen Bärengeist im nordwestlichen Teil der Mondlichtung auf und lernt alles, was er Euch über die Natur des Bären mitzuteilen hat. Wenn Ihr das erledigt habt, kehrt zu Dendrite Sternenschauer nach Nachthafen auf der Mondlichtung zurück.",
    ["T"] = "Großer Bärengeist",
  },
  [5930] = {
    ["D"] = "Im nordwestlichen Teil der Mondlichtung liegt das Zuhause eines weisen und edlen Wesens, das wir den großen Bärengeist nennen. Er hat allen Druiden als Tutor und Mentor zur Seite gestanden, die das erste Mal wahres und natürliches Verständnis für den Bären entwickeln wollten. Alle Druiden suchen an irgendeinem Punkt ihres Lebens die Weisheit des Geistes und auch Ihr werdet keine Ausnahme sein.$B$BGeht nun zum großen Bärengeist und lernt, was er Euch beibringen wird. Wenn er mit Euch zufrieden ist, kehrt zu mir hier nach Nachthafen zurück. Wir werden dann sehen, ob Ihr die Lektionen, die er Euch gelehrt hat, wirklich verinnerlicht habt.",
    ["O"] = "Sucht den großen Bärengeist im nordwestlichen Teil der Mondlichtung auf und lernt alles, was er Euch über die Natur des Bären mitzuteilen hat. Wenn Ihr das erledigt habt, kehrt zu Dendrite Sternenschauer nach Nachthafen auf der Mondlichtung zurück.",
    ["T"] = "Großer Bärengeist",
  },
  [5931] = {
    ["D"] = "Es ist an der Zeit für uns, getrennte Wege zu gehen, denn die Anwendung Eurer Lektionen bringt Euch zurück zu demjenigen, der Euch zu mir geschickt hat. Mathrengyl Bärenfährte bewegt sich wirklich im Einklang mit der Natur, selbst wenn er gefährlich nahe der Orte ist, an denen das Gleichgewicht zusammengebrochen ist. Achtet auf seine Anweisungen und Ihr werdet bald die ersten Lektionen Eurer Rolle als Beschützer des Gleichgewichts beenden.$B$BGeht jetzt. Wir sehen uns wieder.",
    ["O"] = "Kehrt nach Darnassus zurück und sprecht mit Mathrengyl Bärenfährte in der Enklave von Cenarius.",
    ["T"] = "Zurück nach Darnassus",
  },
  [5932] = {
    ["D"] = "Es ist an der Zeit für uns, getrennte Wege zu gehen, denn die Anwendung Eurer Lektionen bringt Euch zurück zu demjenigen, der Euch zu mir geschickt hat. Auch wenn die Tauren auf den Wegen des Cenarius als jung angesehen werden, wandelt Turak Runentotem doch mit großem Gleichgewicht in Gegenwart der Natur. Achtet auf seine Anweisungen und Ihr werdet bald die ersten Lektionen Eurer Rolle als Beschützer des Gleichgewichts beenden.$B$BGeht jetzt. Wir sehen uns wieder.",
    ["O"] = "Kehrt nach Donnerfels zurück und sprecht mit Turak Runentotem auf der Anhöhe der Ältesten.",
    ["T"] = "Zurück nach Donnerfels",
  },
  [5941] = {
    ["D"] = "Ihr habt viele Relikte der Schlacht von Darroheim gesammelt. Jedes Mal, wenn Ihr mit weiteren zurückgekehrt seid, sehnte sich mein Herz nach meinem Dorf... sehnte es sich danach, es vor seinem Schicksal zu bewahren.$B$BIch weiß, dass das nicht sein kann, aber Chromie, diese seltsame Gnomin in Andorhal, behauptet, es wäre möglich. Sie sagt, dass Ihr Darroheim retten könnt!$B$BSie hat mich gebeten, die Relikte zu sammeln und sie von Euch zu ihr bringen zu lassen. Sie sagte, dass sie Euch im zerstörten Gasthaus von Andorhal erwartet.",
    ["O"] = "Bringt das Reliktbündel zu Chromie nach Andorhal.",
    ["T"] = "Rückkehr zu Chromie",
  },
  [5942] = {
    ["D"] = "Papa sagte, ich solle Euch diesen Schlüssel geben. Er sagte, er öffnet eine Truhe draußen hinter dem Haus.$B$BEr wollte außerdem, dass ich Euch danke. Habt Ihr ihm einen Gefallen getan? Habt Ihr ihm gesagt, dass Ihr meine Puppe gefunden habt?$B$BNun, hier ist jedenfalls der Schlüssel. Und habt Dank, $N. Danke! Ihr seid $Gein sehr netter Mann:eine sehr nette Dame;!",
    ["O"] = "Bringt Josephs Schlüssel zu Josephs Truhe.",
    ["T"] = "Versteckte Schätze",
  },
  [5943] = {
    ["D"] = "Ihr seht fähig aus, $R. Vielleicht wollt Ihr Euch etwas Geld verdienen? Cork und ich haben mit dieser Karawane angefangen, um einen Haufen Geld zu scheffeln. Doch wir hatten keine Ahnung von den Gefahren, die das schnelle Geld mit sich bringen würde!$B$BDa vor uns liegt der Zirkel der Mannoroc...normalerweise ignorieren die Dämonen uns, aber dieses Mal verängstigt irgendetwas die Kodos. Ich werde Euch dafür bezahlen, wenn Ihr die Karawane auf dem Weg am Zirkel der Mannoroc vorbei beschützt. Sobald wir sicher daran vorbei sind, könnt Ihr Euch Eure Belohnung bei unserem Geschäftspartner Smeed in Schraubenbuddels Lager abholen.",
    ["O"] = "Geleitet Gizeltons Karawane am Zirkel der Mannoroc vorbei. Sprecht in Schraubenbuddels Lager mit Smeed, um Eure Belohnung zu erhalten.",
    ["T"] = "Gizeltons Karawane",
  },
  [5944] = {
    ["D"] = "Über so lange Zeit war ich eine Marionette des Obersten Kreuzfahrers. Welchen Grund gab es, gegen das anzukämpfen, was aus dem Scharlachroten Kreuzzug geworden ist? Es ist Jahrzehnte her, doch die Erinnerungen an meinen Vater - diese kostbaren Erinnerungen - waren es, die mich am Leben erhielten.$B$BIch habe Träume, $GFremder:Fremde;. In diesen Träumen ist mein Vater bei mir. Er steht stolz an meiner Seite, während ich in den Orden aufgenommen werde. Wir bekämpfen Legionen der Geißel, Seite an Seite. Wir bringen der Allianz und Lordaeron Ehre.$B$BIch möchte nicht mehr träumen.$B$BBringt mich zu ihm.",
    ["O"] = "Geleitet Taelan Fordring aus Herdweiler hinaus.",
    ["T"] = "In den Träumen",
  },
  [5961] = {
    ["D"] = "Es freut mich sehr, dass sich die Kampferprobten unserer Sache anschließen. Unsere Schar gewinnt mit jedem Tag an Größe und Stärke. Vielleicht seid Ihr bereit, Euren Mut auf die Probe zu stellen, $C?$B$BZu den ersten meiner Helden gehörte Nathanos, der heute Pestrufer genannt wird. In unseren Reihen gibt es kaum einen mit mehr Entschlossenheit oder Eifer.$B$BSucht in den Östlichen Pestländern nach ihm. Er wohnt in Marris Siedlung; ab und an streift er durch die dezimierten Ödländer und tötet jeden Abschaum der Geißel und der Allianz, den er aufspüren kann.",
    ["O"] = "Sprecht mit Nathanos Pestrufer in den Östlichen Pestländern. Es heißt, dass er sich in Marris Siedlung niedergelassen hätte.",
    ["T"] = "Der Champion der Bansheekönigin",
  },
  [5981] = {
    ["D"] = "Vor rund 4.000 Jahren rettete Tyrande Wisperwind die Frostsäblerkönigin aus den Händen tobender Riesen. Seither sind die Frostsäbler Verbündete der Nachtelfen.$B$BDoch die Riesen, die einst eine Gefahr für die Frostsäbler waren, gibt es noch immer, und das nicht allzu weit von hier entfernt. Man nennt sie Frosthagelriesen und sie haben das große Tal südlich von hier für sich beansprucht.$B$BBeweist Eure Treue zu den Frostsäblern. Tötet diese Riesen im Namen der Frostsäblerkönigin Shy-Rotam!",
    ["O"] = "Rivern Frostwind möchte, dass Ihr 4 Frosthagelriesen und 4 Frosthagelbewahrer besiegt.",
    ["T"] = "Tobende Riesen",
  },
  [6000] = "_",
  [6001] = {
    ["D"] = "Unmittelbar östlich von Auberdine liegen viele Mondkinhöhlen. In der Auberdine am nächsten gelegenen Höhle befindet sich ein Mondkinstein. Mondklaue, der Verteidiger des Steins, stellt sich Euch nur, wenn Mondstaub des Cenarius darauf angewendet wird. Mondklaue besitzt eine innere Stärke, die Ihr als einer der Klaue benutzen müsst.$B$BIhr habt Euch das Recht verdient, diesen Mondstaub des Cenarius zu benutzen, $N... jetzt zeigt mir, dass Ihr Euch das Recht verdient habt, die Kraft des Körpers und des Herzens zu besitzen, die man als Druide braucht!",
    ["O"] = "Benutzt den Mondstaub des Cenarius mit dem Mondkinstein, um Mondklaue herauszulocken. Danach müsst Ihr Euch Mondklaue stellen und Euch die Kraft des Körpers und des Herzens verdienen, die er besitzt.$B$BSprecht mit Mathrengyl Bärenfährte in Darnassus, wenn Ihr fertig seid.",
    ["T"] = "Körper und Herz",
  },
  [6002] = {
    ["D"] = "Im Land westlich von Taurajo im Brachland - kurz bevor es in Mulgore übergeht - liegt ein Mondkinstein. Mondkin sind besondere Tiere, die nicht in diesen Breiten heimisch sind. Mondklaue, der Verteidiger des Steins, stellt sich Euch, wenn Lunarstaub des Cenarius darauf angewendet wird. Mondklaue besitzt eine innere Stärke, die Ihr als einer der Klaue benutzen müsst.$B$BIhr habt Euch das Recht verdient, diesen Lunarstaub des Cenarius zu benutzen, $N... jetzt zeigt mir, dass Ihr Euch das Recht verdient habt, die Kraft des Körpers und des Herzens zu besitzen, die man als Druide braucht!",
    ["O"] = "Benutzt den Lunarstaub des Cenarius mit dem Mondkinstein, um Mondklaue herauszulocken. Danach müsst Ihr Euch Mondklaue stellen und Euch die Kraft des Körpers und des Herzens verdienen, die er besitzt.$B$BSprecht mit Turak Runentotem in Donnerfels, wenn Ihr fertig seid.",
    ["T"] = "Körper und Herz",
  },
  [6004] = {
    ["D"] = "Ich wurde als Späher ausgeschickt, um etwas Schaden anzurichten und die Kraft von Herdweilers Verteidigung auszukundschaften. Einige Leute der Allianz sind der Meinung, die kleine Stadt habe mehr als nur eine historische Bedeutung. Meine Aufgabe ist es nicht, diese Bedeutung in Frage zu stellen, aber dennoch sollte ich mich in meiner Verfassung erst erholen, bevor ich weitermache.$B$BWenn Ihr wollt, zahle ich Euch ein hübsches Sümmchen für Eure Hilfe. Fangt südlich von hier in dem Lager an, das sie in der Nähe des kleinen Bollwerks aufgeschlagen haben. Wenn Ihr stark genug seid, könnt Ihr mir vielleicht bei meinem vordringlichen Ziel helfen.",
    ["O"] = "Tötet 2 Scharlachrote Sanitäter, 2 Scharlachrote Jäger, 2 Scharlachrote Magier und 2 Scharlachrote Ritter, bevor Ihr zu Kirsta Grubenschatten in den Westlichen Pestländern zurückkehrt.",
    ["T"] = "Offene Rechnungen",
  },
  [6021] = {
    ["D"] = "Könnt Ihr Euch vorstellen, dass wir uns nach dem ganzen Ärger in den Pestländern... jetzt auch noch um Diebe sorgen müssen? Nicht einfach gewöhnliche Diebe, Trolldiebe.$B$BUnd nicht einfach gewöhnliche Trolldiebe. Tote Trolldiebe!$B$BSie werden von diesem großen toten Troll namens Zaeldarr dem Ausgestoßenen angeführt, kommen nachts hierher und stehlen Leichen aus der Grube unten. Dann schaffen sie sie zu ihrem üblen Versteck für tote Trolle - dem Tiefgewölbe im Westen.$B$BGeht dorthin und schaltet Zaeldarr aus, $N. Ich habe seine Schandtaten satt!",
    ["O"] = "Bringt Zaeldarrs Kopf zu Verwalter Alen in der Kapelle des Hoffnungsvollen Lichts.",
    ["T"] = "Zaeldarr der Ausgestoßene",
  },
  [6023] = {
    ["D"] = "Meine wahre Mission bestand darin, die äußeren Kräfte des Scharlachroten Kreuzzugs zu schwächen und dann Herdweiler zu infiltrieren, damit ich ihre Zahl abschätzen kann. In meiner Verfassung kann ich das immer noch nicht allein, vielleicht dürfte ich Euch ja erneut um Eure Unterstützung bitten, $N? Bevor ich entdeckt wurde, konnte ich die Informationen sammeln, dass der Kreuzzug zwei wichtige Mitglieder hat, die das Rückgrat ihrer Streitkräfte außerhalb der Stadt bilden: Jägerin Radley und Dragoner Durgen. Tötet sie, das bringt uns schon weiter. Sie müssten am Pass von Herdweiler außerhalb der Stadtgrenze sein.",
    ["O"] = "Besiegt Jägerin Radley und Dragoner Durgen, bevor Ihr zu Kirsta Grubenschatten in den Westlichen Pestländern zurückkehrt.",
    ["T"] = "Offene Rechnungen",
  },
  [6025] = {
    ["D"] = "Was? Wirklich? Ihr möchtet noch mehr wissen? Aber Ihr habt bereits so viel für mich getan, $N. Ich kann Euch unmöglich darum bitten, an meiner statt nach Herdweiler zu gehen.$B$BSeid Ihr sicher, $N? Herdweiler ist viel gefährlicher als der Pass - ich kann Euch weder Schutz noch Informationen geben, was Ihr da vorfinden könntet.$B$BAlso, wenn Ihr wirklich bereit seid, mir zu helfen, dann geht in die Stadt und sucht eine erhöhte Stelle, von der aus Ihr einen guten Überblick habt. Dort könnt Ihr ihre Zahl abschätzen und mir dann Bericht erstatten. Aber seid vorsichtig...",
    ["O"] = "Geht nach Herdweiler, sucht einen hohen Aussichtspunkt, von dem aus Ihr die Stadt überblicken könnt, schätzt die Streitkräfte des Scharlachroten Kreuzzugs und kehrt dann zu Kirsta Grubenschatten in den Westlichen Pestländern zurück.",
    ["T"] = "Offene Rechnungen",
  },
  [6027] = {
    ["D"] = "$N, ich vertraue Euch den Edelstein der Schlange an. Legt diesen Edelstein auf der Insel Ranazjar nordwestlich von hier in die Hand der Schlangenstatue. Dadurch wird jener gerufen, der das \'Buch der Uralten\' beschützt. Dieses Buch birgt die Geheimnisse der Naga. Geheimnisse, die seit Jahrhunderten gewahrt werden. Bringt mir dieses Buch, dann werden wir mehr über unsere schuppigen Widersacher erfahren!",
    ["O"] = "Beschafft das \'Buch der Uralten\' und kehrt zu Azore Aldamort im Turm von Ethel Rethor zurück.",
    ["T"] = "Buch der Uralten",
  },
  [6028] = {
    ["D"] = "Ich suche ein verantwortungsbewusstes Individuum, das mir in einer kleinen, aber dennoch wichtigen Angelegenheit hilft. Ich bin hier als Vertreter der Argentumdämmerung und ihrer Interessen in der Lokalverwaltung der Ewigen Warte. Ich muss Argentumoffizier Reinherz im Zugwindlager einen Fortschrittsbericht zukommen lassen. Das ist weit entfernt von hier am südlichen Rand der Westlichen Pestländer.$B$BWenn ich so kühn sein darf - dies wäre eine perfekte Möglichkeit, Euch meiner Vorgesetzten vorzustellen und vielleicht etwas Arbeit von ihr zu bekommen!",
    ["O"] = "Bringt den Bericht der Ewigen Warte zu Argentumoffizier Reinherz im Zugwindlager in den Westlichen Pestländern.",
    ["T"] = "Der Bericht der Ewigen Warte",
  },
  [6029] = {
    ["D"] = "Ich suche ein verantwortungsbewusstes Individuum, das mir in einer kleinen, aber dennoch wichtigen Angelegenheit hilft. Ich bin hier als Vertreter der Argentumdämmerung und ihrer Interessen in der Lokalverwaltung der Ewigen Warte. Ich muss Argentumoffizier Garush beim Bollwerk einen Fortschrittsbericht zukommen lassen; das ist weit entfernt von hier am westlichen Rand der Westlichen Pestländer, östlich von Tirisfal.$B$BWenn ich so kühn sein darf - dies wäre eine perfekte Möglichkeit, Euch meinem Vorgesetzten vorzustellen und vielleicht etwas Arbeit von ihm zu bekommen!",
    ["O"] = "Bringt den Bericht der Ewigen Warte zu Argentumoffizier Garush im Bollwerk in den Westlichen Pestländern.",
    ["T"] = "Der Bericht der Ewigen Warte",
  },
  [6030] = {
    ["D"] = "Ein einsiedlerisches Medium namens Umbranse, das in dieser Gegend ansässig ist, hat der Argentumdämmerung ein literarisches Meistwerk geschenkt... es handelt sich vermutlich um die größte Sammlung mündlicher Überlieferungen, die je zu Papier gebracht wurde.$B$BFürst Nicholas Zverenhoff, einer der angesehensten Anführer der Dämmerung, ist ein brillanter Sprachwissenschaftler. Könntet Ihr ihm das für mich bringen - natürlich gegen eine Belohnung? Bei der Kapelle des Hoffnungsvollen Lichts, tief im östlichen Teil der Östlichen Pestländer - da hält er sich gerade auf.",
    ["O"] = "Bringt das Buch \'Studien über das Geistsprechen\' zu Fürst Nicholas Zverenhoff in der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern.",
    ["T"] = "Fürst Nicholas Zverenhoff",
  },
  [6041] = {
    ["D"] = "Während Ihr die Vorräte gesammelt habt, habe ich einige meiner Leute die Geißelbauwerke kennzeichnen lassen, die wir abreißen müssen.$B$BSo ist der Plan: Ich gebe Euch zehn Stecken meines speziellen Präparats. Ihr bringt sie rüber in den Pestwald und platziert sie in den Geißelbauwerken, die für die Sprengung markiert wurden.$B$B<Smokey schnippt mit den Fingern.>$B$BBing! So einfach ist das.",
    ["O"] = "Reist in den Pestwald, nordwestlich der Kapelle des Hoffnungsvollen Lichts. Zerstört 8 Geißelbauwerke, indem Ihr Smokeys spezielles Präparat am Mal der Zerstörung in jedem Bauwerk benutzt. Smokey hat die Ziggurats und Schlachthäuser markieren lassen.$B$BWenn Ihr die Bombe gelegt habt, dann verlasst das Bauwerk und staunt über Euer Geschick.$B$BÜbrigens, Smokeys spezielles Präparat ist recht...instabil.",
    ["T"] = "When Smokey Sings, I Get Violent",
  },
  [6042] = {
    ["D"] = "Man kann keine zwei Schritte von diesem Hof runter, ohne auf eine dieser grässlichen Fledermäuse zu stoßen. Ich möchte die Hunde ausführen und die Geißel abschlachten können, ohne dass mich diese elenden, schmutzigen Fledermäuse behelligen. Ich habe Wichtigeres mit meiner Zeit anzufangen, als tagein, tagaus sinnlos Fledermäuse zu töten. Ihr allerdings nicht.$B$BWas? Eure Ausbildung? Das ist Eure Ausbildung! Jetzt geht mir aus den Augen und kommt erst zurück, wenn Ihr eine ordentliche Menge dieser geflügelten Dämonen getötet habt! Enttäuscht mich nicht.",
    ["O"] = "Erschlagt 20 giftige Seuchenfledermäuse und 10 monströse Seuchenfledermäuse. Kehrt zu Nathanos Pestrufer zurück, wenn Ihr fertig seid.",
    ["T"] = "Die kleinen Ärgernisse des Un-Lebens",
  },
  [6061] = {
    ["D"] = "Für uns als Tauren ist es wichtig, dass wir den Traditionen unserer Vorfahren folgen und gegen mächtige Kreaturen kämpfen. Wenn wir diese Kreaturen bezwingen, können wir ihre Geister aufnehmen und näher bei der Erdenmutter sein.$B$BAuf dieselbe Weise hilft es uns, eins mit der Natur zu werden, wenn wir ein Tier zähmen, damit es an unserer Seite kämpft. Ihr werdet auch irgendwann lernen, ein Tier Eurer Wahl zu zähmen. Aber alles erfordert Übung und Ihr müsst noch viel lernen. Nehmt als erstes diese Zähmrute. Ihr müsst einen ausgewachsenen Ebenenschreiter zähmen.",
    ["O"] = "Benutzt die Zähmrute, um einen ausgewachsenen Ebenenschreiter zu zähmen. Übt Eure Fertigkeiten und bringt die Zähmrute dann zu Yaw Scharfmähne ins Dorf der Bluthufe.",
    ["T"] = "Die Zähmung der Bestie",
  },
  [6063] = {
    ["D"] = "Als Jäger dürft Ihr niemals die Verantwortung vergessen, die Ihr tragt. Die Natur ist eine Kraft, die sich nicht Euren Wünschen beugt. Man muss sie respektieren und anerkennen als etwas Stärkeres als die eigene Existenz.$B$B$N, Ihr müsst beweisen, dass Ihr das verstanden habt, bevor Ihr die Fähigkeit erwerben könnt, Euch ein Tier als Gefährten zu zähmen.$B$BEure erste Aufgabe ist es, diese Zähmrute zu nehmen. Mit ihr könnt Ihr einen Waldweberlauerer zähmen. Zähmt ihn und übt Eure Fertigkeiten als Jäger.",
    ["O"] = "Benutzt die Zähmrute, um einen Waldweberlauerer zu zähmen. Übt Eure Fertigkeiten und bringt die Zähmrute dann zu Dazalar in Dolanaar.",
    ["T"] = "Die Zähmung der Bestie",
  },
  [6064] = {
    ["D"] = "Hallo, $GBursche:Mädchen;! Ihr wollt also wissen, wie Ihr ein eigenes Tier bekommen könnt?$B$BGut, dann lasst uns anfangen. Wenn es um das Jagen geht, dann ist nichts wichtiger als Eure Ausrüstung, aber wir können ein andermal über meinen neuen Schrapnell-Blaster sprechen. Konzentrieren wir uns vorerst auf etwas, das Euch gewiss interessiert: ein Tier!$B$BAm besten lernt Ihr, wenn Ihr verschiedene Kreaturen als Gefährten ausprobiert und herausfindet, welche Ihr mögt. Sucht also einen großen Klippeneber und benutzt dann diese Zähmrute, um ihn zu zähmen.",
    ["O"] = "Benutzt die Zähmrute, um einen großen Klippeneber zu zähmen. Übt Eure Fertigkeiten und bringt die Zähmrute dann zu Grif Wildherz in Kharanos.",
    ["T"] = "Die Zähmung der Bestie",
  },
  [6065] = {
    ["D"] = "Hallo, junger Jäger. Ihr seht mir aus, als wärt Ihr geschickt genug, ein Tier zu Eurem Gefährten auszubilden.$B$BIhr müsst Yaw Scharfmähne im Dorf der Bluthufe aufsuchen. Er kann Euch auf den Weg zu einem eigenen Tier führen.",
    ["O"] = "Sprecht mit Yaw Scharfmähne im Dorf der Bluthufe in Mulgore.",
    ["T"] = "Der Weg des Jägers",
