  [8195] = {
    ["D"] = "I seek the Paragons of Power known as the Coins of the Tribes.  They are the currency used by the various denizens of Zul\'Gurub, and each is imbued with subtle but powerful mojo.$B$BThere are nine distinct kinds to be found.  Some are sought after by my compatriots for various armors they offer Zandalar heroes.  Should you have any extra, I will trade you one of our Honor Tokens for a set of three.  You may use the Tokens with Rin\'wosho the Trader; he offers our heroes special items for them.",
    ["O"] = "",
    ["T"] = "Münzen der Razzashi, Hakkari und zulianische Münzen",
  },
  [8201] = {
    ["D"] = "Es ist wahr. Hakkar hat sich in Zul\'Gurub eingenistet und badet in den Kräften unserer Götter. Von ihm kontrollierte Priester haben damit begonnen, die Energien unserer heiligsten Gottheiten auf den Seelenschinder zu kanalisieren. Seine Macht wird schon bald die aller Wesen in Azeroth übersteigen. Er muss aufgehalten werden!$B$BHier, nehmt diese heilige Kordel. Findet die Priester, die es wagen die Energien unserer Götter zu kanalisieren, und reiht ihre Köpfe auf dieser Kordel aneinander.$B$BKehrt anschließend zu mir zurück.",
    ["O"] = "Reiht die Köpfe der 5 Kanalisierer auf der heiligen Kordel aneinander. Bringt dann die Trollschädelsammlung zu Exzhal auf der Insel Yojamba.",
    ["T"] = "Die Schädelsammlung",
  },
  [8221] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Besonderer Fisch - Kiefers Engelfisch",
  },
  [8225] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Besonderer Fisch - Braunells blaugestreifter Flitzerfisch",
  },
  [8230] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Collins Test Quest",
  },
  [8231] = {
    ["D"] = "Der Wellenhauer ist ein amphibisches Wildtier, das nur hier in Azshara vorkommt. Er ist gewandt und zäh.$B$BDie robusten, reflektierenden Schuppen eines Wellenhauers spiegeln die Stärke des Jägers wider.$B$BSpürt Wellenhauer entlang der Küste Azsharas auf, jagt sie und bringt mir ihre Schuppen.",
    ["O"] = "Bringt 6 Wellenhauerschuppen zu Ogtinc in Azshara. Ogtinc wohnt oberhalb des Kliffs, nordöstlich der Ruinen von Eldarath.",
    ["T"] = "Wellenjagd",
  },
  [8232] = {
    ["D"] = "Ich entsende Euch nun auf eine gefährliche Jagd. Es gilt den grünen Drachen, Morphaz, zu bezwingen und mir seinen Zahn zu bringen. Morphaz haust tief im Tempel von Atal\'Hakkar, dessen Eingang in den Sümpfen des Elends liegt.$B$BWenn Ihr ihn besiegen möchtet, solltet Ihr nicht alleine gehen. Dieser Drache ist der Feind von vielen - sucht nach anderen, die ihn ebenfalls zu vernichten suchen.$B$BDer Zahn des Drachen steht für die Macht, den ein Jäger in der Gesellschaft anderer findet. Bringt ihn zu mir und Ihr werdet viel gelernt haben.",
    ["O"] = "Bringt Morphaz\' Zahn zu Ogtinc in Azshara. Ogtinc wohnt oberhalb des Kliffs, nordöstlich der Ruinen von Eldarath.",
    ["T"] = "Der grüne Drache",
  },
  [8233] = {
    ["D"] = "Lord Jorach Rabenholdt hat nach Euch rufen lassen. Die Angelegenheit ist geheim und ich kann auch nicht mehr dazu sagen.$B$BDas Rabenholdtanwesen liegt im Vorgebirge des Hügellands - haltet nach einem Pfad im Gebirge nordwestlich von Burg Durnholde Ausschau.",
    ["O"] = "Sprecht mit Lord Jorach Rabenholdt im Rabenholdtanwesen.",
    ["T"] = "Ein simples Anliegen",
  },
  [8234] = {
    ["D"] = "Eins nach dem anderen. Ich habe eine kleine blaue Tasche verloren, deren Inhalt von privater Natur ist.$B$BWie dem auch sei, versucht erst gar nicht den Dieben nachzustellen, denn sie sind bereits tot. Wir wissen jedoch, dass diese Narren die Tasche nicht öffnen konnten. Sie verkauften sie also an einen Schamanen der Holzschlundfeste in Azshara als heiliges Relikt. Raffiniert.$B$BIch möchte, dass Ihr nach Azshara reist und die Tasche von dem Schamanen durch Geschick... oder mittels Kraft wiedererlangt. Ich glaube mit ersterer Methode dürftet Ihr mehr Glück haben. Habt Ihr sie, bringt sie zu Erzmagier Xylem in Azshara.",
    ["O"] = "Erlangt die versiegelte azurblaue Tasche von dem Schamanen der Holzschlundfeste in Azshara zurück. Bringt sie anschließend zum Erzmagier Xylem, der ebenfalls in Azshara zu finden ist.",
    ["T"] = "Versiegelte azurblaue Tasche",
  },
  [8237] = "_",
  [8238] = {
    ["D"] = "I seek the Paragons of Power known as the Coins of the Tribes.  They are the currency used by the various denizens of Zul\'Gurub, and each is imbued with subtle but powerful mojo.$B$BThere are nine distinct kinds to be found.  Some are sought after by my compatriots for various armors they offer Zandalar heroes.  Should you have any extra, I will trade you one of our Honor Tokens for a set of three.  You may use the Tokens with Rin\'wosho the Trader; he offers our heroes special items for them.",
    ["O"] = "",
    ["T"] = "Münzen der Gurubashi, Blutfratzen und Bleichborken",
  },
  [8239] = {
    ["D"] = "I seek the Paragons of Power known as the Coins of the Tribes.  They are the currency used by the various denizens of Zul\'Gurub, and each is imbued with subtle but powerful mojo.$B$BThere are nine distinct kinds to be found.  Some are sought after by my compatriots for various armors they offer Zandalar heroes.  Should you have any extra, I will trade you one of our Honor Tokens for a set of three.  You may use the Tokens with Rin\'wosho the Trader; he offers our heroes special items for them.",
    ["O"] = "",
    ["T"] = "Münzen der Sandwüter, Schädelspalter und Blutskalpe",
  },
  [8250] = {
    ["D"] = "Erzmagier Xylem hat seit kurzer Zeit begonnen sich umzuhören... nach jemanden mit Eurer Erfahrung.$B$BFalls Ihr ihn aufsuchen möchtet, dann könnt Ihr ihn in Azshara antreffen. Sein Turm steht hoch über den Klippen und der einzige Weg, der dort hinaufführt, ist ein Teleportationszauber, der von seinem arroganten Diener, Sanath Lim-yo, gewirkt wird.$B$BSanath kampiert unterhalb des Turms. Um ihn zu finden, müsst Ihr zu der Stelle reisen, an der sich Azsharas Hauptstraße beim Eingang zu den Ruinen von Eldarath gabelt. Geht von dort aus direkt nach Norden bis Ihr zu den Klippen kommt.",
    ["O"] = "Sucht nach Sanath Lim-yo und verschafft Euch Zugang zu Erzmagier Xylem.",
    ["T"] = "Geschick der Magier",
  },
  [8251] = {
    ["D"] = "Die Blutelfen in Azshara haben eine Methode entwickelt, um magische Gegenstände, die sie erhalten, zu Staub zu pulverisieren. Dieser glitzernde Staub, der so produziert wird, gleicht nicht jenem, der sich durch eine übliche Entzauberung herstellen lässt.$B$BIch habe bereits meinen Lehrling entsendet, um durch Handel diesen Staub zu erlangen, doch er wurde ohne Umschweife grausam ermordet. Dafür müssen sie bezahlen.$B$BDie Zeiten der Konversation sind vorbei, $N. Sucht nach den Blutelfen entlang der Klippen von Azshara und wendet \"überzeugendere\" Mittel an, um an den glitzernden Staub zu kommen.",
    ["O"] = "Bringt Erzmagier Xylem in Azshara 10 glitzernde Staubproben.",
    ["T"] = "Magischer Staub",
  },
  [8254] = {
    ["D"] = "Es ist der Weg des Göttlichen, mit dem denen geholfen wird, die in Not sind. Das wie ist unbedeutend - allein das warum zählt.$B$BDer Zirkel des Cenarius ist in derartigen Nöten. Sprecht mit Ogtinc, um ihnen in ihrer Notlage auszuhelfen. Er wohnt oberhalb des Kliffs, nordöstlich der Ruinen von Eldarath.",
    ["O"] = "Sucht Ogtinc in Azshara auf.",
    ["T"] = "Cenarische Hilfe",
  },
  [8258] = {
    ["D"] = "Der Todesritter Schattensichel herrscht über das Große Ossuarium in Scholomance. Er droht die spirituelle Ebene zu seinen eigenen Zwecken zu missbrauchen... Er muss gestoppt werden! Dabei kommt das Orakel der Anrufung, bei dessen Herstellung Ihr behilflich wart, zum Einsatz.$B$BSetzt das Orakel inmitten des Kellergewölbes des Großen Ossuariums ein, um Schattensichels Geistergefolge herbeizurufen. Besiegt die Geister und der Todesritter selbst wird erscheinen. $N, bringt mir seinen Kopf! Lediglich seine Niederlage wird den Schaden, den er der spirituellen Ebene zugefügt hat, wieder umkehren können!",
    ["O"] = "Wendet das Orakel der Anrufung inmitten des Kellergewölbes des Großen Ossuariums in Scholomance an. Ihr werdet damit Geister herbeirufen, die es zu bekämpfen gilt. Nach Vernichtung dieser Geister wird der Todesritter Schattensichel auftauchen; besiegt ihn.$B$BBringt Schattensichels Kopf zu Sagorne Gratläufer im Tal der Weisheit in Orgrimmar.",
    ["T"] = "Die Bedrohung durch Schattensichel",
  },
  [8259] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Eine angemessenere Belohnung",
  },
  [8266] = {
    ["D"] = "I understand that it can be difficult at times to come out on top in Warsong Gulch. Still, your effort on our behalf - even when victory is not achieved - is important.$B$BShould you complete one of the trials inside Warsong Gulch and not achieve victory, you will still receive a Ribbons of Sacrifice. Bring it to me so that the Outriders may reward you for acting on our behalf... even if you weren\'t able to win this time.",
    ["O"] = "Bring the Ribbons of Sacrifice to Captain Shatterskull so that the Outriders may reward you for acting on our behalf.",
    ["T"] = "Aufopferungsabzeichen",
  },
  [8268] = {
    ["D"] = "I understand that it can be difficult at times to come out on top in Warsong Gulch. Still, your effort on our behalf - even when victory is not achieved - is important.$B$BShould you complete one of the trials inside Warsong Gulch and not achieve victory, you will still receive a Ribbons of Sacrifice. Bring it to me so that the Sentinels may reward you for acting on our behalf... even if you weren\'t able to win this time.",
    ["O"] = "Bring the Ribbons of Sacrifice to Sentinel Farsong so that the Sentinels may reward you for acting on our behalf.",
    ["T"] = "Aufopferungsabzeichen",
  },
  [8274] = "_",
  [8275] = {
    ["D"] = "Ihr Helden der Allianz, horcht auf! Die Bedrohung durch die Silithiden blieb zu lange unbeachtet. Ohne einen wirklichen Gegner, der sie bekämpft, haben sie sich die ganze Zeit über auf eine großangelegte Invasion vorbereitet.$B$BDer Zirkel des Cenarius sammelt seine Streitkräfte in Silithus, um diesen verachtungswürdigen Kreaturen den Krieg zu erklären. Alle kampftauglichen Abenteurer werden hiermit aufgerufen, sich uns anzuschließen!$B$BWartet nicht, bis es zu spät ist! Helft uns bei der Verteidigung von Azeroth! Meldet Euch für genauere Informationen bei Windruferin Prachthorn auf Burg Cenarius.",
    ["O"] = "Sprecht mit Windruferin Prachthorn auf Burg Cenarius in Silithus.",
    ["T"] = "Die Rückeroberung von Silithus",
  },
  [8276] = {
    ["D"] = "Ihr Helden der Horde, horcht auf! Es gibt eine Bedrohung dort draußen, die wir zu lange ignoriert haben. Erderschütterungen in der Nähe von Silithidennestern, verursacht durch massive Wanderungen dieser Biester, haben unsere Aufmerksamkeit erregt. Es scheintn als bereiten sie ihre weitere Expansion vor.$B$BDer Zirkel des Cenarius sammelt seine Streitkräfte in Silithus, um diesen Silithiden den Krieg zu erklären, bevor es zu spät ist. Niemals wieder sollen diese Biester unsere Ländereien entweihen!$B$BAlle kampftauglichen Abenteurer sollten sich umgehend bei Windruferin Prachthorn auf Burg Cenarius melden. Mögen die Silithiden endgültig besiegt werden!",
    ["O"] = "Sprecht mit Windruferin Prachthorn auf Burg Cenarius in Silithus.",
    ["T"] = "Die Rückeroberung von Silithus",
  },
  [8277] = {
    ["D"] = "Beetix Wankelhupf ist Euch zu Diensten! Mein Cousin Noggle hat es sich zum Lebensinhalt gemacht, seltene Kräuter für die Alchemisten der Burg Cenarius zu sammeln. Oder hatte es zumindest, egal. Er kam von seinem letzten Ausflug mit einer starken Vergiftung zurück.$B$BIch habe es geschafft die Wirkung des Gifts zu verlangsamen, aber um es vollständig neutralisieren zu können, benötige ich Giftproben von was auch immer ihn gestochen haben mag.$B$BNoggle kam aus dem Norden, daher verwette ich mein letztes Hemd darauf, dass es Steinpanzerskorpide oder Sandhuscher waren. Bringt mir ein paar Stachel und Giftzähne und ich werde versuchen ein Gegengift für ihn zu erstellen.",
    ["O"] = "Beetix Wankelhupf von der Burg Cenarius in Silithus möchte, dass Ihr 8 Stachel von Steinpanzerskorpiden und 8 Giftzähne von Sandhuschern für sie sammelt. Kehrt zu ihr zurück, sobald Ihr diese Aufgabe erfüllt habt.",
    ["T"] = "Tödliches Wüstengift",
  },
  [8278] = {
    ["D"] = "Verflucht! Das Gegengift hat nicht gewirkt. Noggle muss von einer der stärkeren Kreaturen im fernen Süden gestochen worden sein.$B$BIch werde Euch brauchen, um dorthin zu gehen und mir noch mehr Proben zu besorgen, $N. Das Gute dabei - da ihr Gift stärker ist, werde ich nicht so viele brauchen. Der Haken ist allerdings, dass in der südlichen Gegend mehrere Arten giftiger Kreaturen herumfleuchen und Ihr daher verschiedene Proben besorgen müsst.",
    ["O"] = "Beetix Wankelhupf von der Burg Cenarius in Silithus möchte, dass Ihr je 3 Stachel von Steinpanzerzangen und Steinpanzerschindern sowie 3 Felspirschergiftzähne für sie sammelt.",
    ["T"] = "Noggles letzte Hoffnung",
  },
  [8279] = {
    ["D"] = "Bringt Ortell dem Einsiedler in Silithus die drei Kapitel des Zwielichtlexikons.",
    ["O"] = "Bringt Ortell dem Einsiedler in Silithus die drei Kapitel des Zwielichtlexikons.",
    ["T"] = "Das Zwielichtlexikon",
  },
  [8280] = {
    ["D"] = "Burg Cenarius ist von außerordentlicher strategischer Bedeutung. Ich bin davon überzeugt, dass unser Sieg über die Silithiden feststeht, solange wir sie halten können. Natürlich unter der Bedingung, dass wir die Versorgung mit Nachschubgütern aufrechterhalten können.$B$BDie Sandwürmer in dieser Gegend stellen für unsere Nachschubkarawanen eine noch größere Gefahr dar als die Silithiden selbst. Erst heute Morgen verloren wir eine ganze Morgenkornlieferung an eine Gruppe Baggerschläger. Ich brauche Eure Hilfe, um die Bestände dieser Baggerschläger auszudünnen und um sicherzustellen, dass unsere Karawanen vor weiteren Überfällen sicher sind.",
    ["O"] = "Windruferin Prachthorn auf Burg Cenarius in Silithus möchte, dass Ihr 15 Baggerschläger tötet.",
    ["T"] = "Sicherung der Versorgungslinien",
  },
  [8281] = {
    ["D"] = "Eure bisherigen Leistungen sind makellos, $N. Dennoch, sind unsere Versorgungslinien noch lange nicht sicher. In Abwesenheit der Baggerschläger, werden ihre südlichen Artgenossen nach Norden drängen, um ihren Platz einzunehmen.$B$BIhr werdet eine ganze Reihe Baggerbrecher töten müssen, um diese Biester von unseren Versorgungslinien fernzuhalten.",
    ["O"] = "Windruferin Prachthorn auf Burg Cenarius in Silithus möchte, dass Ihr 20 Baggerbrecher tötet.",
    ["T"] = "Verstärkte Sicherheit",
  },
  [8283] = {
    ["D"] = "Man sagt, dass sich der grausame Skorpid Totenstachel nahe des südlichen Gebirgszuges wegen erhöhter Silithidenaktivität zurückgezogen hat. Dies stellt eine große Bedrohung für Patrouillen und Expeditionen in der südlichen Gegend dar. Wer sich an dessen Vernichtung beteiligt, soll reichlich belohnt werden. Fragt bei Hauptmann Vish Kozus, ganz oben auf dem Wachturm der Burg Cenarius, wegen der Belohnung nach.",
    ["O"] = "Bringt die Zange Totenstachels zu Vish Kozus, Hauptmann der Wache.",
    ["T"] = "GESUCHT: Totenstachel, Terror der Wüste",
  },
  [8284] = {
    ["D"] = "Einst existierte nordwestlich von hier ein florierender Außenposten des Schattenhammers. Wenn Ihr heutzutage in diese Gegend reist, werdet Ihr dort nur Schwärme von Elementaren antreffen. Verrottete Holzpfosten und alte Zelttuchfetzen sind das Einzige, was noch auf den Standort des Lagers hinweist.$B$BVor einiger Zeit fand ich dort die Fragmente einer Tontafel, allerdings nicht genug Teile, um sie zu irgendetwas Brauchbarem zusammenzusetzen. Bringt mir die fehlenden Fragmente der Schrifttafel. Vielleicht können wir so etwas Licht in die Sache bringen und herausfinden, was die Anhänger des Schattenhammers in Silithus gesucht haben.",
    ["O"] = "Geologin Lerchenbann auf Burg Cenarius möchte, dass Ihr ihr 8 Zwielichtschrifttafelfragmente bringt.",
    ["T"] = "Das Geheimnis des Zwielichts",
  },
  [8290] = {
    ["D"] = "Jenseits dieses Tunnels werdet Ihr ein Feld des Kampfes und der Aufruhr finden, $Gjunger:junge:c; $C. Die Horde hört nicht auf, unseren heiligen Wald zu roden und benutzt unser immergrünes Blattwerk, um ihre Maschinen anzutreiben.$B$BBetretet die Kriegshymnenschlucht und verteidigt die Feste der Silberschwingen. Schlagt die eingefallenen Kräfte der Horde zurück.$B$BEin Erfolg würde Euch ein Verdienstabzeichen einbringen. Bringt mir eines dieser Abzeichen, $N und ich werde Euch belohnen.",
    ["O"] = "Betretet die Kriegshymnenschlucht und besiegt die Streitkräfte der Horde. Verdient Euch ein Ehrenabzeichen der Kriegshymnenschlucht und kehrt zur Schildwache Weltensang am Hain der Silberschwingen zurück.",
    ["T"] = "Besiegt die Eindringlinge!",
  },
  [8291] = {
    ["D"] = "Jenseits dieses Tunnels werdet Ihr ein Feld des Kampfes und der Aufruhr finden, $Gjunger:junge:c; $C. Die Horde hört nicht auf, unseren heiligen Wald zu roden und benutzt unser immergrünes Blattwerk, um ihre Maschinen anzutreiben.$B$BBetretet die Kriegshymnenschlucht und verteidigt die Feste der Silberschwingen. Schlagt die eingefallenen Kräfte der Horde zurück.$B$BEin Erfolg würde Euch ein Verdienstabzeichen einbringen. Bringt mir eines dieser Abzeichen, $N und ich werde Euch belohnen.",
    ["O"] = "Betretet die Kriegshymnenschlucht und besiegt die Streitkräfte der Horde. Verdient Euch ein Ehrenabzeichen der Kriegshymnenschlucht und kehrt zur Schildwache Weltensang am Hain der Silberschwingen zurück.",
    ["T"] = "Besiegt die Eindringlinge!",
  },
  [8292] = {
    ["D"] = "You have proven your value to our efforts in Warsong Gulch. Continue to aid the cause and bring me more talismans of merit. Do this, and you will earn even more of our trust.",
    ["O"] = "You obtained a Silverwing Talisman of Merit for your last task, talk to me again, and you gain your reward.",
    ["T"] = "Verdienstabzeichen",
  },
  [8293] = {
    ["D"] = "You have proven your value to our efforts in Warsong Gulch. Continue to rage against the Silverwing Sentinels! Bring me more marks of honor!",
    ["O"] = "You obtained a Warsong Mark of Honor for your last task, talk to me again, and you gain your reward.",
    ["T"] = "Ehrenabzeichen",
  },
  [8294] = {
    ["D"] = "Die Wildnis des Eschentals wird sich der Macht der Horde ergeben, $R. Nichts, was die Silberschwingen tun oder sagen, wird uns daran hindern, unseren territorialen Herrschaftsanspruch durchzusetzen. Kalimdor gehört der Horde. Wie können sie es wagen, uns davon abzuhalten, das zu ernten, was rechtens unser ist!$B$BLasst Eure Brust vor Stolz erbeben, während Ihr ihre schwachen Versuche, unseren Fortschritt zu behindern, zunichtemacht. Zerstört die Schildwachen der Silberschwingen und erlangt ein Ehrenabzeichen des Kriegshymnenklans. Kehrt zu mir zurück, $N, wenn Ihr im Besitz eines Ehrenabzeichens seid, und ich werde Euch belohnen.",
    ["O"] = "Betretet die Kriegshymnenschlucht und besiegt die Streitkräfte der Allianz. Erwerbt ein Ehrenabzeichen der Kriegshymnenschlucht und kehrt anschließend zu Hauptmann Schädelbrecher im Stützpunkt Mor\'shan zurück.",
    ["T"] = "Bezwingt die Eindringlinge der Silberschwingen",
  },
  [8295] = {
    ["D"] = "Die Wildnis des Eschentals wird sich der Macht der Horde ergeben, $R. Nichts, was die Silberschwingen tun oder sagen, wird uns daran hindern, unseren territorialen Herrschaftsanspruch durchzusetzen. Kalimdor gehört der Horde. Wie können sie es wagen, uns davon abzuhalten, das zu ernten, was rechtens unser ist!$B$BLasst Eure Brust vor Stolz erbeben, während Ihr ihre schwachen Versuche, unseren Fortschritt zu behindern, zunichtemacht. Zerstört die Schildwachen der Silberschwingen und erlangt ein Ehrenabzeichen des Kriegshymnenklans. Kehrt zu mir zurück, $N, wenn Ihr im Besitz eines Ehrenabzeichens seid, und ich werde Euch belohnen.",
    ["O"] = "Betretet die Kriegshymnenschlucht und besiegt die Streitkräfte der Allianz. Erwerbt ein Ehrenabzeichen der Kriegshymnenschlucht und kehrt anschließend zu Hauptmann Schädelbrecher im Stützpunkt Mor\'shan zurück.",
    ["T"] = "Bezwingt die Eindringlinge der Silberschwingen",
  },
  [8297] = {
    ["D"] = "Eine Schlacht muss mit Soldaten gewonnen werden, das wird Euch jeder Soldat gerne bestätigen. Man sagt auch, dass eine Schlacht auf leeren Magen und mit schlechter Ausrüstung bereits eine verlorene Schlacht ist!$B$BDeshalb ist das Arathibecken so wichtig. Es gibt dort Schlüsselregionen, die essentielle Ressourcen bergen. Metalle, Waffen, Holz, ja, alles wird gebraucht und kann dort gefunden werden.$B$BDaher brauche ich Euch, $N. Begebt Euch ins Arathibecken und entscheidet die Schlacht für Euch, indem Ihr mehr Stützpunkte haltet als der Feind. Bringt mir als Beweis Eurer Taten eine Vorratskiste.",
    ["O"] = "Gewinnt die Schlacht um das Arathibecken, raubt eine Arathivorratskiste und bringt sie Unteroffizier Maclear in der Zuflucht.",
    ["T"] = "Die Ressourcen des Arathibeckens",
  },
  [8298] = {
    ["D"] = "Eine Schlacht muss mit Soldaten gewonnen werden, das wird Euch jeder Soldat gerne bestätigen. Man sagt auch, dass eine Schlacht auf leeren Magen und mit schlechter Ausrüstung bereits eine verlorene Schlacht ist!$B$BDeshalb ist das Arathibecken so wichtig. Es gibt dort Schlüsselregionen, die essentielle Ressourcen bergen. Metalle, Waffen, Holz, ja, alles wird gebraucht und kann dort gefunden werden.$B$BDaher brauche ich Euch, $N. Begebt Euch ins Arathibecken und entscheidet die Schlacht für Euch, indem Ihr mehr Stützpunkte haltet als der Feind. Bringt mir als Beweis Eurer Taten eine Vorratskiste.",
    ["O"] = "Gewinnt die Schlacht um das Arathibecken, raubt eine Arathivorratskiste und bringt sie Unteroffizier Maclear in der Zuflucht.",
    ["T"] = "Noch mehr Vorratskisten",
  },
  [8299] = {
    ["D"] = "Der Bund von Arathor scheut keine Mühen, um Arathi sein Eigen zu nennen. Sie und all die anderen Völker der Allianz strömen nun in diese entfernte Region, versessen darauf, ihren Gebietsanspruch von der Zuflucht aus zu erweitern. Wir müssen sie aufhalten, und das beste Mittel dies zu tun, ist ihre Vorräte zu stehlen.$B$BIhr könnt uns helfen, $N. Betretet das Arathibecken, das für seine reichen Minen, sein fruchtbares Land und seine geschickten Handwerker bekannt ist, und erobert jeden Stützpunkt, den Ihr könnt. Entscheidet die Schlacht auf diese Weise für Euch und kehrt dann mit den Ressourcen, die Ihr erbeuten konntet, zu mir zurück.",
    ["O"] = "Entscheidet die Schlacht um das Arathibecken für Euch, erbeutet eine Arathivorratskiste und bringt sie Todespirscher Mortis in Hammerfall.",
    ["T"] = "Zerschlagt die Versorgungslinien Arathors",
  },
  [8300] = {
    ["D"] = "Der Bund von Arathor scheut keine Mühen, um Arathi sein Eigen zu nennen. Sie und all die anderen Völker der Allianz strömen nun in diese entfernte Region, versessen darauf, ihren Gebietsanspruch von der Zuflucht aus zu erweitern. Wir müssen sie aufhalten, und das beste Mittel dies zu tun, ist ihre Vorräte zu stehlen.$B$BIhr könnt uns helfen, $N. Betretet das Arathibecken, das für seine reichen Minen, sein fruchtbares Land und seine geschickten Handwerker bekannt ist, und erobert jeden Stützpunkt, den Ihr könnt. Entscheidet die Schlacht auf diese Weise für Euch und kehrt dann mit den Ressourcen, die Ihr erbeuten konntet, zu mir zurück.",
    ["O"] = "Entscheidet die Schlacht um das Arathibecken für Euch, erbeutet eine Arathivorratskiste und bringt sie Todespirscher Mortis in Hammerfall.",
    ["T"] = "Noch mehr Vorratskisten",
  },
  [8303] = {
    ["D"] = "Es ist an der Zeit, $N.$B$BIch muss mich dafür entschuldigen, so viel vor Euch geheim zu halten. Die Feinde sind überall, selbst unter uns. Bitte, lest aus diesem Folianten und lernt. Nutzt das Wissen, das ich Euch preisgebe, und sucht nach unserem Meister.",
    ["O"] = "Sucht nach Anachronos bei den Höhlen der Zeit in Tanaris.",
    ["T"] = "Anachronos",
  },
  [8304] = {
    ["D"] = "Meine geliebte Natalia ist verschwunden.$B$BUm die Wahrheit zu sagen, sie hat sich schon Wochen vor ihrem Verschwinden äußerst seltsam benommen. Ich habe sie mehr als einmal dabei erwischt, wie sie zu sich selber sprach, obwohl niemand in der Nähe war. Doch sie behauptete felsenfest, dass mit ihr alles in Ordnung sei und sie ihre Forschungen unbedingt fortsetzen müsste.$B$BDas letzte Mal hatte man sie gesehen, wie sie nach Süden, in Richtung Bronzebarts Lager verschwand.$B$BLeider stehen mir zu wenige Soldaten für weitere Nachforschungen zur Verfügung, $N. Ich brauche Eure Hilfe, um die Zwerge in Bronzebarts Lager nach ihrem Verbleib zu befragen.",
    ["O"] = "Befragt Frankal und Rutgar in Bronzebarts Lager über den Aufenthaltsort von Natalia Mar\'alith.",
    ["T"] = "Liebste Natalia",
  },
  [8305] = {
    ["D"] = "Sucht die Kristallträne und blickt in ihr Innerstes.",
    ["O"] = "Sucht die Kristallträne und blickt in ihr Innerstes.",
    ["T"] = "Lang Vergessenes",
  },
  [8306] = {
    ["D"] = "Kommandant Mar\'alith auf Burg Cenarius in Silithus möchte, dass Ihr seine geliebte Natalia wiederfindet. Die von Euch gesammelten Informationen deuten darauf hin, dass Ihr sie im Bau des Regalschwarms im Süden finden könntet.$B$BSprecht noch einmal mit den Zwergen in Bronzebarts Lager, bevor Ihr Euch in den Schwarmbau begebt. Ihr Wissen könnte sich als hilfreich erweisen und sicherlich, haben sie noch einige Aufgaben für Euch.$B$BUnd $N, denkt an die Worte des Kommandanten: \"Tut, was Ihr tun müsst...\"",
    ["O"] = "Kommandant Mar\'alith auf Burg Cenarius in Silithus möchte, dass Ihr seine geliebte Natalia wiederfindet. Die von Euch gesammelten Informationen deuten darauf hin, dass Ihr sie im Bau des Regalschwarms im Süden finden könntet.$B$BSprecht noch einmal mit den Zwergen in Bronzebarts Lager, bevor Ihr Euch in den Schwarmbau begebt. Ihr Wissen könnte sich als hilfreich erweisen und sicherlich, haben sie noch einige Aufgaben für Euch.$B$BUnd $N, denkt an die Worte des Kommandanten: \"Tut, was Ihr tun müsst...\"",
    ["T"] = "Der Schlund des Wahnsinns",
  },
  [8308] = {
    ["D"] = "An den Finder dieses Briefs,$B$Bwenn Ihr dies lest, bin ich wahrscheinlich bereits tot oder werde es in Kürze sein. Die meisten meiner Männer sind schon tot.$B$BIch habe Informationen über einen Titanen entdeckt, über eine große Schlacht zwischen ihm und einem Alten Gott. Ja, ein verdammter Alter Gott. Wir haben einen uralten Tunnel nach Ahn\'Qiraj entdeckt, dem ich jetzt folge.$B$BBringt diesen Brief in mein altes Lager. Kümmert Euch bitte um meinen Affen und gebt ihm ausreichend Wasser, Nahrung und die eine oder andere Umarmung.$B$B- Brann Bronzebart, Extremforscher$B$BP.S. Achtet auf die verrückten nachtelfischen Anbeter des alten Gottes.",
    ["O"] = "Liefert Brann Bronzebarts verlorenen Brief in Bronzebarts Lager im südlichen Silithus ab.",
    ["T"] = "Brann Bronzebarts verlorener Brief",
  },
  [8309] = {
    ["D"] = "Nach unseren ersten Streifzügen in die Schwarmbauten und dem darauf folgenden Wahnsinn der Herrin Mar\'alith, haben die drei Schwarmbauten damit begonnen hunderte von Insektenkriegern auszuspucken. Die erhöhte Aktivität könnte mit den Glyphen in Zusammenhang stehen, die diese merkwürdigen Kristalle in den drei Bauten der Schwärme bedecken.$B$BWir müssen unbedingt Abpausbilder von diesen Glyphen anfertigen. Mithilfe der Abpausbilder gelingt es uns vielleicht, hinter das Geheimnis dieses Wahnsinns zu kommen.$B$BHier $N, nehmt dieses Abschriftenset und begebt Euch in den Bau des Regalschwarms im Osten. Das ist das letzte Abpausbild, das ich brauche.",
    ["O"] = "Benutzt das Abschriftenset für Geologen, um ein Abpausbild des glyphenverzierten Kristalls des Regalschwarms anzufertigen.",
    ["T"] = "Glyphenjagd",
  },
  [8311] = {
    ["D"] = "Ich darf nicht zum Süßigkeitensammeln gehen. Nach Todesschwinges Angriff habe ich schon Glück, dass die Waisenmatrone Nachtigall mich so weit hinaus lässt.$B$BWenn Ihr für mich loszieht, dann werde ich Euch Eure gesammelten Süßigkeiten gegen welche eintauschen, die ich bereits hier habe!$B$BAlle Gastwirte in den größeren Städten verteilen während der Schlotternächte Süßigkeiten - sprecht mit ihnen und führt ihnen vor, nach was sie Euch fragen! In Eisenschmiede verteilt auch ein kleiner Gnom namens Talvash Süßes. Sobald Ihr die Süßigkeiten habt, kommt hierher zu mir zurück!",
    ["O"] = "Sprecht mit den Gastwirten in Sturmwind, Eisenschmiede und Darnassus sowie mit Talvash del Kissel in Eisenschmiede. Führt ihnen vor, nach was sie im Gegenzug für die Süßigkeiten verlangen.$B$BKehrt zu Jesper außerhalb von Sturmwind mit einem Marzipan aus Darnassus, einer Kaugummikugel aus Gnomeregan, Nugat aus Sturmwind und einem Pfefferminzbonbon aus Eisenschmiede zurück.",
    ["T"] = "Schlotternachtssüßigkeiten für Jesper!",
  },
  [8312] = {
    ["D"] = "Ich würde gerne während der Schlotternächte zum Süßigkeitensammeln gehen, aber ich kann nicht, weil die Matrone mich nicht lässt. Wenn Ihr für mich loszieht, dann werde ich Euch Eure gesammelten Süßigkeiten gegen welche eintauschen, die ich bereits hier habe! Ich glaube Ihr werdet sie mögen - sie verwandeln Euch entweder in etwas Lustiges oder Gruseliges!$B$BAlle Gastwirte in den größeren Städten verteilen während der Schlotternächte Süßigkeiten - sprecht mit ihnen und führt ihnen vor, nach was sie Euch fragen! In Orgrimmar verteilt auch ein Troll namens Kali Remik Süßes. Sobald Ihr die Süßigkeiten habt, kommt hierher zu mir zurück!",
    ["O"] = "Sprecht mit den Gastwirten in Orgrimmar, Unterstadt und Donnerfels sowie mit Kali Remik im Tal der Geister in Orgrimmar. Führt ihnen vor, nach was sie im Gegenzug für die Süßigkeiten verlangen.$B$BKehrt zu Spuks in den Ruinen von Lordaeron mit einem Marzipan aus Donnerfels, einer Kaugummikugel der Dunkelspeere, Nugat aus Orgrimmar und einem Pfefferminzbonbon aus Unterstadt zurück.",
    ["T"] = "Schlotternachtssüßigkeiten für Spuks!",
  },
  [8314] = {
    ["D"] = "Wir stehen kurz vor einem Durchbruch, $N. Dank der von Euch beschafften Abpausbilder ist es mir gelungen, die Glyphen auf diesem Kristallprisma nachzubilden. Es sieht so aus, als würden sie den Qiraji und anderen unbekannten Wesen dazu dienen, mit den Silithiden zu kommunizieren.$B$BLeider ist das alles, was ich aus diesen Glyphen herauskriegen konnte. Wir müssen eine Möglichkeit finden, wie wir die in den Kristallen versteckten Botschaften freilegen können.$B$BNehmt dieses glyphenverzierte Kristallprisma und bringt es Geologin Lerchenbann auf Burg Cenarius. Sie müsste uns weiterhelfen können.",
    ["O"] = "Bringt das glyphenverzierte Kristallprisma zu Geologin Lerchenbann auf Burg Cenarius in Silithus.",
    ["T"] = "Des Rätsels Lösung",
  },
  [8315] = {
    ["D"] = "Die Glyphen zeigen einen Abgesandten der Qiraji, wie er sich mit drei Silithiden vor den Knochen eines großen Drachen trifft. Über den Glyphen steht, soweit ich dass beurteilen kann, eine Art Kode. Es könnte gut sein, dass dieser Kode der Schlüssel zur Beschwörung des Abgesandten ist.$B$BIch werde eine Kopie des Glyphenkodes anfertigen, den Ihr vor den Knochen von Grakkarond in den Sand schreiben müsst. Wenn ich Recht habe, werden die Oberanführer in dem Glauben dort auftauchen, dass neue Informationen zur Übertragung bereit stehen. Streckt sie nieder und konfrontiert den Abgesandten. Er besitzt den Schlüssel für die Kristalle...",
    ["O"] = "Geologin Lerchenbann auf Burg Cenarius in Silithus möchte, dass Ihr ihr den Kristallentriegelungsmechanismus vom Abgesandten der Qiraji beschafft.$B$BNehmt die Glyphen der Anrufung, begebt Euch zu den Knochen von Grakkarond südlich von Burg Cenarius und zeichnet sie dort in den Sand. Sollte der Abgesandte der Qiraji erscheinen, tötet ihn und nehmt den Kristallentriegelungsmechanismus an Euch. Kehrt anschließend zu Geologin Lerchenbann zurück.$B$BFür diese Aufgabe werdet Ihr eine kleine Armee benötigen, $N!",
    ["T"] = "Die Anrufung",
  },
  [8318] = {
    ["D"] = "Einige Schattenhammerkultisten tragen mit Kauderwelsch beschriftete Papiere bei sich. Ich möchte, dass Ihr mir ein paar dieser Texte bringt, $C.$B$BIch habe zwar keine Möglichkeit, dieses Geschreibsel zu verstehen, doch das ist nicht der springende Punkt! Mithilfe dieser Papiere tauschen die verschiedenen Gruppierungen des Kults untereinander geheime Nachrichten aus. Mit jeder Nachricht, die wir aus dem Verkehr ziehen, stören wir ihre Kommunikation untereinander!",
    ["O"] = "Bringt Bor Wildmähne auf Burg Cenarius 10 verschlüsselte Schattenhammertexte.",
    ["T"] = "Geheime Nachrichten",
  },
  [8321] = {
    ["D"] = "Wir konnten eine weitere Operationsbasis des Schattenhammers ausfindig machen. Es ist eine gut versteckte Höhle in der nordöstlichen Ecke von Silithus, bekannt als die Kavernen des Schattenhammers. Der örtliche Anführer des Kultes, Vyral der Üble, residiert dort. Er ist Euer nächstes Ziel. Besiegt ihn und bringt mir seinen Siegelring.$B$BDie Kavernen des Schattenhammers beherbergen furchterregende Schrecken, also passt auf Euch auf! Ich wünsche Euch alles gute, $N.",
    ["O"] = "Tötet Vyral den Üblen und erhaltet Vyrals Siegelring.",
    ["T"] = "Vyral der Üble",
  },
  [8322] = {
    ["D"] = "Die Menschen von Süderstade lieben ihr Bier und deshalb schätzen sie das Bier, das im Gasthaus von Süderstade serviert wird, auch besonders. Wir können von dem Chaos während der Schlotternächte profitieren und ihre letzte Ladung Bier für Wochen - wenn nicht sogar Monate, verderben!$B$BNehmt diese faulen Eier und werft sie in die großen Braufässchen im Gasthaus von Süderstade. Allein Eure Anwesenheit wird für Unruhe sorgen, Ihr solltet Euch also zu verteidigen wissen!$B$BWenn Ihr Süderstade dieses unvergessliche Geschmackserlebnis überbracht habt, kommt hierher zu mir aufs Fest zurück!",
    ["O"] = "Werft die faulen Eier in die großen Braufässchen im Gasthaus von Süderstade.",
    ["T"] = "Faule Eier",
  },
  [8325] = {
    ["D"] = "Je früher Ihr mit Eurer Ausbildung anfangt, $N, umso besser für uns alle. Wir können uns keine Fehler erlauben, also hört gut zu.$B$BDie brennenden Kristalle - die grünen, schwebenden Objekte westlich des Sonnenturms hier - wurden lange Zeit als Energiequelle für die Experimente der Insel verwendet. Die Manawyrms waren ihre Wächter, aber der Einmarsch der Geißel in Quel\'Thalas hat sie auf Grund unserer mangelnden magischen Kontrolle über sie fehlgeleitet.$B$BUns bleibt keine andere Wahl, als sie für die Wiedereroberung der Insel in ihrer Zahl zu reduzieren. Kehrt nach Beendigung Eurer Arbeit zu mir zurück.",
    ["O"] = "Kill 8 Mana Wyrms; they are located close to the Burning Crystals that are adjacent to the tower and gazebo.  Return to Magistrix Erona at the tower on Sunstrider Isle when you are done.",
    ["T"] = "Rückeroberung der Insel der Sonnenwanderer",
  },
  [8326] = {
    ["D"] = "Eure Bemühungen haben uns etwas erkennen lassen, von dem ich hoffte, es wäre nicht wahr. Die unkontrollierte Macht der brennenden Kristalle hat einen viel größeren Teil des natürlichen Gleichgewichts der Insel durcheinander gebracht als ich vermutet habe. Wir müssen jetzt leider bedauerliche Maßnahmen ergreifen, um die Kontrolle wiederzuerlangen.$B$BDie Luchse aus der Umgebung sind dem Einfluss der Kristalle erlegen und müssen bekämpft werden. Bringt mir ihre Halsbänder, $N. Noch kann ich vielleicht einen Magieschutz anfertigen, der manche wieder dem Einfluss entziehen kann.",
    ["O"] = "Sammelt 8 Luchshalsbänder von erlegten Bachtatzenluchsen und Bachtatzenjungen ein. Kehrt zu Magistrix Erona auf der Insel der Sonnenwanderer zurück, sobald Ihr Eure Aufgabe erfüllt habt.",
    ["T"] = "Bedauerliche Maßnahmen",
  },
  [8327] = {
    ["D"] = "Erstattet meinem Assistenten Lanthan Perilon Bericht. Er wird Euch sagen, was es als Nächstes zu tun gilt. Während wir hier am Sonnenturm weiterhin über die brennenden Kristalle walten, werdet Ihr Euch auf eine dringendere Angelegenheit bezüglich der Akademie von Falthrien konzentrieren. Lanthan wird Euch alles erklären, wenn Ihr mit ihm sprecht.$B$BGeht einfach von hier aus gen Westen entlang des Pfads, Ihr solltet ihn in der Nähe antreffen.$B$BViel Glück, $N - jeglicher Erfolg soll belohnt werden.",
    ["O"] = "Sprecht mit Lanthan Perilon auf der Insel der Sonnenwanderer. Er steht in der Nähe des Pfads, der vom Sonnenturm Richtung Westen verläuft.",
    ["T"] = "Berichterstattung an Lanthan Perilon",
  },
  [8328] = {
    ["D"] = "Während Ihr in Eurer Klasse weiter aufsteigt, werdet Ihr sicher die Ausbildung, die Euch der Lehrer Eurer Klasse anbietet, nutzen wollen. Die Lehrerin für junge Magier hier auf der Insel der Sonnenwanderer ist Julia Sonnensturm. Sprecht mit Julia, um herauszufinden, welche Ausbildung sie Euch anbieten kann. Die Ausbildung hat ihren Preis, bringt also ein paar Münzen mit Euch.$B$BJulia - wie alle anderen Lehrer auf der Insel der Sonnenwanderer auch - ist innerhalb des Sonnenturms, auf der unteren Ebene, anzutreffen.",
    ["O"] = "Sprecht mit Julia Sonnensturm - der Magierlehrerin - innerhalb des Sonnenturms auf der Insel der Sonnenwanderer.",
    ["T"] = "Ausbildung zum Magier",
  },
  [8329] = {
    ["D"] = "Während Ihr in Eurer Klasse weiter aufsteigt, werdet Ihr sicher die Ausbildung, die Euch der Lehrer Eurer Klasse anbietet, nutzen wollen. Der Lehrer für junge Krieger hier auf der Insel der Sonnenwanderer ist Delios. Sprecht mit ihm, um herauszufinden, welche Ausbildung er Euch anbieten kann. Die Ausbildung hat ihren Preis, habt also ein paar Münzen bei Euch.$B$BDelios - wie alle anderen Lehrer auf der Insel der Sonnenwanderer auch - ist innerhalb des Sonnenturms auf der unteren Ebene anzutreffen.",
    ["O"] = "Sprecht mit Delios - dem Kriegerlehrer - innerhalb des Sonnenturms auf der Insel der Sonnenwanderer.",
    ["T"] = "Ausbildung zum Krieger",
  },
  [8330] = {
    ["D"] = "Bei dem Chaos hier beim Sonnenturm, habe ich es nicht geschafft, all mein Hab und Gut zusammenzusammeln, das ich an verschiedenen Plätzen auf der Insel gelassen habe. Ich muss hier beim Sonnenbrunnen weiterhin Wache halten, aber könntet Ihr vielleicht meine Sachen an meiner Stelle suchen gehen?$B$BIch brauche meine Seherkugel, meine Rolle der Geißelmagie und mein Tagebuch. Nutzt diesen Ranzen für zusätzliches Gepäck, da die Dinge etwas sperrig sind. Bringt sie zu mir zurück und ich gebe Euch dafür etwas im Austausch - für Eure Mühe, versteht Ihr? Den Ranzen könnt Ihr dann auch behalten!",
    ["O"] = "Sammelt Brunnenbehüter Solanians Seherkugel, seine Rolle der Geißelmagie und sein Tagebuch ein. Ihr findet die Gegenstände auf der Insel der Sonnenwanderer bei dem Tümpel, dem Brunnen und bei einem der brennenden Kristalle. Bringt sie zu dem Brunnenbehüter oben im Sonnenturm auf der Insel der Sonnenwanderer, sobald Ihr alle gefunden habt.",
    ["T"] = "Solanians Habe",
  },
  [8331] = {
    ["D"] = "$N, wir hatten angenommen, dass wir die Operationen des Schattenhammers in Silithus verlangsamt haben… bedauerlicherweise lagen wir falsch. Durch verschiedene Unternehmungen gegen den Kult haben wir herausgefunden, dass eine noch viel dunklere Macht hinter dem Schattenhammer steht, der sogenannte Abyssische Rat. Er kontrolliert den Kult über die arkanen Windsteine, die in den Lagern der Schattenhammerkultisten postiert sind. Daher, $N, werdet Ihr als Nächstes gegen den abyssischen Rat ins Feld ziehen.$B$BSprecht mit Aurel Goldblatt im östlichen Teil von Burg Cenarius. Für unseren nächsten Schachzug werden wir ihre Hilfe benötigen.",
    ["O"] = "Sprecht mit Aurel Goldblatt auf Burg Cenarius.",
    ["T"] = "Aurel Goldblatt",
  },
  [8334] = {
    ["D"] = "Bevor ich Euch zur Akademie aussende, möchte ich, dass Ihr Euch um eine andere Aufgabe kümmert, die es sofort zu erledigen gilt. Einst halfen uns die Borklinge, die Pflanzenwelt der Insel der Sonnenwanderer zu erhalten, aber jetzt sind uns diese Bestien außer Kontrolle geraten. Helft uns so schnell wie möglich bei ihrer Vernichtung, aber seid gewarnt - der Verlust ihrer Kontrolle hat sie äußerst aggressiv werden lassen. Es ist diese Angriffslust, die uns zwingt, diese einst friedlichen Untergebenen zu zerschlagen.$B$BEs bereitet mir keine Freude, Euch um diesen Gefallen zu bitten, aber es geht nun mal ums Überleben. Ich werde später trauern.",
    ["O"] = "Tötet 7 Borklinge und 7 wilde Borklinge. Kehrt dann zu Lanthan Perilon auf der Insel der Sonnenwanderer zurück.",
    ["T"] = "Aggressivität",
  },
  [8335] = {
    ["D"] = "Einer unserer Verräter haust oben in der Akademie von Falthrien.$B$BFelendren ist sein Name. Er wurde aus dem Reich der Blutelfen verbannt, da er die Warnungen unserer Lehrer und Ältesten missachtet hat. Er ist der Schlimmste unserer Art, da er nur nach der Befriedigung seines unstillbaren Magieverlangens strebt. Er hat sich der Kontrolle verweigert und ist nur noch ein Schatten seines einstigen Selbsts - ein Getriebener - und er stellt eine Bedrohung für den Sonnenturm dar.$B$BVernichtet ihn und die Gespenster, die er sich als Diener zu Nutzen macht.",
    ["O"] = "Tötet 8 Arkangespenster, 2 besudelte Arkangespenster und Felendren den Verbannten. Ihr findet sie in der Akademie von Falthrien. Bringt Felendrens Kopf zu Lanthan Perilon auf der Insel der Sonnenwanderer.",
    ["T"] = "Felendren der Verbannte",
  },
  [8336] = {
    ["D"] = "Es ist eine Schande, dass wir die Kontrolle über so viele Kreaturen hier auf der Insel verloren haben. Es war einst ein ruhiger Ort der Studien und Forschungsarbeiten. Jetzt können wir uns nur noch hüten, damit wir von unseren eigenen Kreationen nicht angegriffen werden!$B$BIch biete Euch einen magischen Segen an, wenn Ihr für mich etwas einsammelt. Bringt mir eine Handvoll Arkanspäne von den Mana benutzenden Kreaturen der Insel der Sonnenwanderer und ich wirke einen Zauber auf Euch, welcher Eure weiteren Abenteuer immens erleichtern wird.",
    ["O"] = "Sammelt 6 Arkanspäne und bringt sie zu Arkanist Ithanas auf der Insel der Sonnenwanderer. Derartige Fragmente findet man bei allen Mana benutzenden Kreaturen auf der Insel.",
    ["T"] = "Für eine Handvoll Fragmente",
  },
  [8338] = {
    ["D"] = "Das Fragment kristallisierten Manas, das Ihr bei dem Körper des besudelten Arkangespensts gefunden habt, ist äußerst sonderbar. Es ist anders als die Fragmente, die Ihr vielleicht sonst gesehen habt. Es sieht so dunkel und unheimlich wie das Gespenst, von dem Ihr es habt, aus. Hält man es in der Hand, spürt man bereits etwas unnatürlich Bedrohliches.$B$BArkanist Helion beim Vorbau des Sonnenturms auf der Insel der Sonnenwanderer könnte für das Fragment vielleicht Verwendung finden.",
    ["O"] = "Bringt den besudelten Arkanspan zu Arkanist Helion beim Vorbau des Sonnenturms auf der Insel der Sonnenwanderer.",
    ["T"] = "Besudelter Arkanspan",
  },
  [8339] = {
    ["D"] = "Um einen Siegelring eines Schattenhammerkultisten herzustellen, brauche ich ein paar Dinge.",
    ["O"] = "Bringt die Gegenstände zu Aurel Goldblatt in der Burg Cenarius.",
    ["T"] = "Belohnung des Rats",
  },
  [8340] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Siegelring des Schattenhammers",
  },
  [8343] = {
    ["D"] = "$N, Aurel Goldblatt glaubt einen Weg gefunden zu haben, eines der führenden Mitglieder des Abyssischen Rates zu beschwören und zu besiegen. Diese Aufgabe wird äußerst gefährlich werden und Ihr genießt den größten Respekt innerhalb des Zirkels des Cenarius. Daher glauben wir, dass wenn es jemanden gibt, der diese schwere Prüfung meistern kann, dann seid Ihr es.$B$BMöge Euch das Schicksal gnädig sein, $N! Sprecht mit Aurel, wenn Ihr bereit seid.",
    ["O"] = "Sprecht mit Aurel Goldblatt auf Burg Cenarius.",
    ["T"] = "Goldblatts Entdeckung",
  },
  [8344] = {
    ["D"] = "Erhört meine Worte, Kind - diese Insel ist von einem Makel behaftet, der mächtiger als die Überbleibsel der Geißel ist.$B$BIch glaube, dass auf der Insel eine wirklich dämonische Präsenz vorherrscht.$B$BWestlich von hier liegen die Türme der Akademie von Falthrien. Die Arkangespenster, die dort hausen, sind der Schlüssel zu der dämonischen Machtquelle. Bringt mir Essenzen von besudelten und unbesudelten Gespenstern.$B$BIm Austausch für Euren Gehorsam, werde ich Euch lehren, wie man einen Wichtel als Begleiter beschwören kann - Euren ersten von vielen als Hexenmeister.",
    ["O"] = "Bring 4 Essenzen eines Gespenstes und 1 Essenz eines besudelten Gespenstes zu Beschwörer Teli\'Larien beim Sonnenturm auf der Insel der Sonnenwanderer.",
    ["T"] = "Schlüssel zur Macht",
  },
  [8345] = {
    ["D"] = "Tag für Tag stehe ich hier, beobachte, warte. Man hat mich beschuldigt, zu tief in unserer Vergangenheit zu graben, während mein Blick zum Horizont gerichtet ist. Ich glaube jedoch fest daran, dass jeder Besucher dieser Insel diejenigen ehren sollte, die alles dafür geopfert haben, damit andere fortfahren können.$B$BDath\'Remar Sonnenwanderer war unser erster König. Er führte uns durch den Mahlstrom von Kalimdor hierher.$B$BSucht seinen Schrein im Westen auf und kehrt erst dann zu mir zurück, wenn Ihr die dort angebrachte Tafel zu seiner Ehre gelesen habt.",
    ["O"] = "Lest die Tafel am Schrein von Dath\'Remar und kehrt anschließend zu Brunnenbehüter Solanian beim Sonnenturm auf der Insel der Sonnenwanderer zurück.",
    ["T"] = "Der Schrein von Dath\'Remar",
  },
  [8346] = {
    ["D"] = "If there is only one lesson you deign to remember from your time on Sunstrider Isle, let it be this - control your thirst for magic.  It is a thirst unending, $N - you must absorb energy to survive via Mana Tap, and you must control how you release it via Arcane Torrent.  Failure is to become one of the Wretched... hopelessly addicted and insane.$B$BSeek out creatures on the isle that have mana and Mana Tap them.  Learn to master your cravings of power.  When you have sufficiently fed, return to me.",
    ["O"] = "Use your Mana Tap racial ability on creatures with mana six times, then return to Arcanist Helion.",
    ["T"] = "Unstillbarer Durst",
  },
  [8347] = {
    ["D"] = "Magistrix Erona möchte, dass Ihr Euch nach Silbermond begebt, um die dortigen Bestrebungen zur Wiedereroberung zu unterstützen... allerdings gilt es vorher noch, etwas bei der Akademie von Falthrien zu erledigen. Ihr habt uns nicht enttäuscht, $N.$B$BAuf Eurem Weg zum Falkenplatz südlich von hier solltet Ihr mit Kundschafterin Alarion sprechen. Sie ist nicht weit der Brücke.$B$BDie Kundschafter kümmern sich um den Warenverkehr zwischen hier und dem Landesinneren der Insel, und da Ihr Eure Tatkräftigkeit eindrucksvoll unter Beweis gestellt habt, denke ich, dass sie Eure Hilfe sicherlich sehr begrüßen werden.",
    ["O"] = "Sprecht mit Kundschafterin Alarion nahe der Brücke, die in den Süden der Insel der Sonnenwanderer führt.",
    ["T"] = "Unterstützung für die Kundschafter",
  },
  [8349] = {
    ["D"] = "Nun, da Ihr ein Medaillon habt und in der Lage seid, einen abyssischen Fürsten herbeizurufen, sprecht mit Bor Wildmähne. Er wird Euch mit einer Aufgabe, die sich gegen den Abyssischen Rat richtet, beauftragen.$B$BViel Glück, $N. Falls Ihr weitere Medaillons braucht, kehrt zu mir zurück.",
    ["O"] = "Sprecht mit Bor Wildmähne auf Burg Cenarius.",
    ["T"] = "Bor Wildmähne",
  },
  [8350] = {
    ["D"] = "Der Tod eines weiteren Kundschafters ist eine wahre Tragödie, aber es ist ein Risiko, das wir alle in Kauf nehmen, wenn wir einen Auftrag annehmen. Magistrix Erona beim Sonnenturm möchte, dass dieses Päckchen schnellstmöglich zum Gasthaus beim Falkenplatz geliefert wird.$B$BWir haben nur noch wenige Leute und niemand könnte jetzt für uns einspringen.$B$BIch möchte Euch nichts vormachen, Ihr habt selbst gesehen, wie gefährlich die Straße zum Falkenplatz sein kann, sobald man vom Dämmerweg abkommt. Fühlt Ihr Euch bereit für diese Aufgabe, $N?",
    ["O"] = "Bringt Eronas Päckchen zu Gastwirtin Delaniel im Gasthaus beim Falkenplatz im Immersangwald.",
    ["T"] = "Erfolgreiche Lieferung",
  },
  [8354] = {
    ["D"] = "Spielt Gastwirt Norman ein Huhn vor, er wird Euch dafür ein Pfefferminzbonbon aus Unterstadt geben!",
    ["O"] = "Spielt Gastwirt Norman ein Huhn vor, er wird Euch dafür ein Pfefferminzbonbon aus Unterstadt geben!",
    ["T"] = "Hühnchengegacker für ein Pfefferminzbonbon",
  },
  [8356] = {
    ["D"] = "Das ist ein... interessantes Kostüm, welches Ihr da tragt. Auch wenn Ihr nett seid und einem Kind dieses Jahr beim Süßigkeitensammeln helft, so fürchte ich doch, dass ich Euch um eine kleine Vorstellung bitten muss!$B$BMal sehen... Ihr seht in diesem... Kostüm stark und muskulös aus... jetzt gebt doch mal ein bisschen an! Ja, lasst Eure Muskeln für mich spielen, $G starker Mann : starke Frau;!$B$BWenn ich Euch angeben sehe, und auch nur dann, werde ich Euch etwas von dem leckeren Nugat aus Sturmwind geben. Es ist eine großartige Schlotternachtssüßigkeit.",
    ["O"] = "Ihr müsst vor Gastwirtin Allison angeben, damit sie Euch im Gegenzug etwas vom Nugat aus Sturmwind gibt!",
    ["T"] = "Muskelspiel für Nugat",
  },
  [8358] = {
    ["D"] = "A trick-or-treater!  Yeah mon!$B$BWell now, even though you\'re doing something noble and collecting candies for a sick child, I can\'t let you off the hook that easily.  I have a yummy Darkspear Gumdrop to give to the sick child... if you make a train noise for me!  Haha, yes, that\'ll be the trick!  The price of this gumdrop is one train noise!$B$BChugga-chugga, $c!",
    ["O"] = "Ahmt für Kali Remik einen Zug nach und sie wird Euch dafür eine Kaugummikugel der Dunkelspeere geben!",
    ["T"] = "Kaugummikugelexpress",
  },
  [8359] = {
    ["D"] = "Ihr müsst vor Gastwirtin Gryshka angeben, damit sie Euch im Gegenzug etwas vom Nugat aus Orgrimmar gibt!",
    ["O"] = "Ihr müsst vor Gastwirtin Gryshka angeben, damit sie Euch im Gegenzug etwas vom Nugat aus Orgrimmar gibt!",
    ["T"] = "Muskelspiel für Nugat",
  },
  [8365] = {
    ["D"] = "Ihr seid die Hilfe, die das Kartell entsandt hat? Ach, ich denke, Ihr werdet reichen. Hört mir genau zu, $C. Wie Ihr wisst, bin ich die Lieferantin für modische Kleidung, die in den höchsten Kreisen der Gesellschaft Azeroths gefragt ist. Könige und Königinnen, Kriegshäuptlinge und... naja wie auch immer die Horde ihre Königinnen bezeichnet, all jene tragen, was ich als modisch deklariere. Und ich verdiene mir an ihnen eine goldene Nase, was natürlich das Beste am Ganzen ist.$B$BAlso hierfür benötige ich Eure Hilfe. Lauft hinunter zur Mast- und Schotbucht und beschafft mir einen Stapel Piratenhüte, koste es was es wolle. Glaubt Ihr, Ihr schafft das, hmm?",
    ["O"] = "Sammelt 20 Südmeerpiratenhüte und bringt sie zurück zur eingebildeten Modeschöpferin beim Dampfdruckpier in Tanaris.",
    ["T"] = "Piratenhüte Ahoy!",
  },
  [8366] = {
    ["D"] = "Argh! Sie verlangen das Unmögliche von mir!$B$BDas Dampfdruckkartell möchte, dass ich den Südmeerpiraten eine Lektion erteile. Ich kann doch aber nicht an zwei Orten gleichzeitig sein; Ich muss hierbleiben, um zumindest hier alles ruhig zu halten.$B$BSeit neuestem hält nicht mal mehr das Schutzgeld, dass das Kartell regelmäßig auszahlt, die Piraten auf. Daher möchte ich Euch zeitweilig abordnen, mal dort runter zu gehen und denen Saures zu geben.$B$BIhr findet die Piraten in der Mast- und Schotbucht, die hier gleich in der Nähe gen Süden ist. Besten Dank, $N, ich schulde Euch was!",
    ["O"] = "Sicherheitschef Bilgenritzel beim Dampfdruckpier in Tanaris beauftragt Euch, 10 Piraten der Südmeerfreibeuter, 10 Seeräuber der Südmeerfreibeuter, 10 Dockarbeiter der Südmeerfreibeuter und 10 Schwadroneure der Südseefreibeuter zu töten.",
    ["T"] = "Gaunerei und Erpressung im Südmeer",
  },
  [8367] = {
    ["D"] = "Bringt ein Ehrenabzeichen jedes Schlachtfelds zu einem Kriegshetzer der Horde in einer der Hauptstädte oder in Shattrath.",
    ["O"] = "Bringt ein Ehrenabzeichen jedes Schlachtfelds zu einem Kriegshetzer der Horde in einer der Hauptstädte oder in Shattrath.",
    ["T"] = "Große Ehre",
  },
  [8368] = {
    ["D"] = "Bringt 3 Ehrenabzeichen des Kriegshymnenklans zu einem Kriegshetzer der Horde, außerhalb der Schlachtfelder.",
    ["O"] = "Bringt 3 Ehrenabzeichen des Kriegshymnenklans zu einem Kriegshetzer der Horde, außerhalb der Schlachtfelder.",
    ["T"] = "Schlacht der Kriegshymnenschlucht",
  },
  [8369] = {
    ["D"] = "Hört mir genau zu, $N. Egal, was auch immer Ihr über die noch immer tobende Schlacht im Alteractal gehört habt, etwas davon ist wahr: Es war die Expeditionsarmee der Sturmlanzen, die diesen Konflikt durch die Besetzung des Territoriums der Frostwölfe entfachte. Der Tag, an dem wir es der Allianz erlauben, Gebiete der Horde zu erobern, ohne militärisch zurückzuschlagen, wird der Tag sein, an dem ich meine Axt an den Nagel hänge, um den Rest meines Lebens in Schande zu verbringen. Geht jetzt und schlagt die Invasoren zurück!",
    ["O"] = "Bringt 3 Ehrenabzeichen des Alteractals zu einem der Kriegshetzer der Horde, außerhalb der Schlachtfelder.",
    ["T"] = "Die Invasoren des Alteractals",
  },
  [8370] = {
    ["D"] = "Unser Konflikt mit der Allianz hat sich zu einem richtigen Krieg entwickelt. Es ist nicht länger notwendig unter falschen Vorwänden in die Schlacht zu ziehen, keine Seite erwartet dies noch länger.$B$BIn der Schlacht um das Arathibecken geht es nur um eines, Ressourcen. Nicht mehr und nicht weniger. Entweder die Allianz erobert diese Ressourcen und benutzt sie gegen uns, oder wir kommen ihnen zuvor und verwenden die dortigen Ressourcenquellen für unsere Kriegspläne. $N, helft den Entweihten dabei, die von uns benötigten Ressourcenlieferungen aufrechtzuerhalten!",
    ["O"] = "Bringt 3 Ehrenabzeichen des Arathibeckens zu einem Kriegshetzer der Horde, außerhalb der Schlachtfelder.",
    ["T"] = "Eroberung des Arathibeckens",
  },
  [8371] = {
    ["D"] = "Bringt ein Ehrenabzeichen jedes Schlachtfelds zu einem Brigadegeneral der Allianz in einer der Hauptstädte, Dalaran oder Shattrath.",
    ["O"] = "Bringt ein Ehrenabzeichen jedes Schlachtfelds zu einem Kriegshetzer der Horde in einer der Hauptstädte oder in Shattrath.",
    ["T"] = "Gemeinsames Bestreben",
  },
  [8372] = {
    ["D"] = "Die Schildwachen der Silberschwingen sind auf Grund der Zerstörung, die die Orcs im Wald anrichten, im Krieg mit der Vorhut des Kriegshymnenklans. Allerdings gibt es noch andere Gründe, als die bloße Liebe zu der Natur, für die Verteidigung von ausgerechnet diesem Wald.$B$BDer Wald bildet eine strategische Barriere, die es einfach macht, das Eschental gegen jegliche Art von Großangriffen zu verteidigen. Ohne den Wald, würde Astranaar lediglich ein oder zwei Tage standhalten, bevor es vom Brachland einverleibt werden würde.$B$BSeid tapfer und schlagt Euch gegen die Vorhut des Kriegshymnenklans, $N! Für die Allianz!",
    ["O"] = "Bringt 3 Ehrenabzeichen der Kriegshymnenschlucht zu einem Brigadegeneral der Allianz, außerhalb der Schlachtfelder.",
    ["T"] = "Kampf um die Kriegshymnenschlucht",
  },
  [8373] = {
    ["D"] = "Was ich während der Schlotternächte am meisten hasse, sind die Stinkbomben, die die Verlassenen hier inmitten von Süderstade loslassen. Wer hätte gedacht, dass eine Gedenkfeier zur Freilassung mit einer so stinkigen Sache einhergehen würde?$B$BVerwendet diesen Stinkbombenreiniger während der Schlotternächte, um die scheußlichen Stinkbomben, die die Horde womöglich hier in der Stadt loslässt, zu vernichten. Er bekämpft, dank der Kraft von Pinien, schreckliche Gerüche.$B$BHelft mit Süderstade sauber zu halten und ich werde Euch dafür ein paar Schlotternachtssüßigkeiten geben, die Ihr sicherlich mögen werdet. Abgemacht?",
    ["O"] = "Verwendet einen Stinkbombenreiniger, um alle Stinkbomben der Verlassenen zu vernichten, die hier in Süderstade losgelassen wurden. Kehrt zu Unteroffizier Hartman in Süderstade zurück, wenn Ihr fertig seid.",
    ["T"] = "Die Kraft der Pinie",
  },
  [8375] = {
    ["D"] = "Ihr liegt vollkommen richtig, $N. Die Horde verkündet die Wahrheit, mit ihrer Behauptung, das Alteractal gehöre zum Territorium der Frostwölfe. Die Sturmlanzen kamen, auf der Suche nach Erzvorkommen und Relikten, mit friedlichen Absichten in das Tal. Die Frostwölfe jedoch, reagierten auf die Anwesenheit der Sturmlanzen mit einer, in der Allianz, bis dato unbekannten Brutalität und Aggressivität.$B$BNiemals dürfen wir all die Zwerge vergessen, die während des folgenden feigen und unangekündigten Angriffs zu Tode kamen. Geht nun, $N, und tragt Euren Teil zur Schlacht im Alteractal bei. Für die Allianz!",
    ["O"] = "Bringt 3 Ehrenabzeichen des Alteractals zu einem der Brigadegeneräle der Allianz, die außerhalb der Schlachtfelder stehen.",
    ["T"] = "Vergesst das Alteractal nicht!",
  },
  [8383] = {
    ["D"] = "Bringt 3 Ehrenabzeichen des Alteractals zu einem der Brigadegeneräle der Allianz, die außerhalb der Schlachtfelder stehen.",
    ["O"] = "Bringt 3 Ehrenabzeichen des Alteractals zu einem der Brigadegeneräle der Allianz, die außerhalb der Schlachtfelder stehen.",
    ["T"] = "Vergesst das Alteractal nicht!",
