  [8444] = "_",
  [8445] = "_",
  [8447] = {
    ["D"] = "Das Wasser des Sees von Elune\'ara wird die Alpträume auflösen, die den Gegenstand umgeben. Bitte, gebt mir einen Moment, um die Reinigung durchzuführen, $N.",
    ["O"] = "Wartet, bis Bewahrer Remulos bei der Mondlichtung den von Alpträumen eingehüllten Gegenstand gereinigt hat.",
    ["T"] = "Legenden erwachen",
  },
  [8458] = "_",
  [8459] = "_",
  [8461] = {
    ["D"] = "Direkt südwestlich von hier liegt ein Furbolglager der Totenwaldfelle. Ich bin nicht sicher, was sie vorhaben, aber ich würde dem gern einen Riegel vorschieben, ehe es zum Problem wird.$B$BDer Stamm der Totenwaldfelle ist böse, aber die Verderbnis ist es, die ihre Hirne verseucht. Ihre Feindseligkeit ist keine angeborene Eigenschaft, doch da kann man nichts machen. Aus diesem Grund stimmt es mich zutiefst traurig, dass ich gegen meine Geschwister kämpfen muss.$B$BBitte, $N, tut, was getan werden muss, aber erspart mir die Einzelheiten. Sie beunruhigen mich zu sehr.",
    ["O"] = "Nafien möchte, dass Ihr 6 Höhlenbehüter der Totenwaldfelle, 6 Rächer der Totenwaldfelle und 6 Schamanen der Totenwaldfelle besiegt. Kehrt danach zu ihm in den Norden des Teufelswalds nahe des Eingangs der Holzschlundfeste zurück.",
    ["T"] = "Totenwaldfelle des Nordens",
  },
  [8462] = {
    ["D"] = "Vielleicht seid Ihr stark genug, um Euch einer noch größeren Bedrohung entgegenzustellen. Die Furbolgs der Holzschlundfeste brauchen Eure Hilfe und ich denke, Ihr habt hier bereits Eure Tauglichkeit im Kampf ausreichend bewiesen.$B$BReist auf dieser Straße in Richtung Norden, bis zum Eingang der Holzschlundfeste, wo einer meiner Brüder Wache steht. Sein Name ist Nafien. Im ersten Augenblick mag er vielleicht wie ein ungezügeltes, wildes Tier wirken, doch braucht Ihr ihn nicht zu fürchten. Er ist auf der Suche nach tapferen Abenteurern, die ihm im Kampf gegen die dunkle Verderbnis unterstützen, welche unsere Heimat bedroht.",
    ["O"] = "Reist nach Norden entlang der Hauptstraße des Teufelswalds und sprecht mit einem Furbolg namens Nafien. Er hält außerhalb des Eingangs zur Holzschlundfeste Wache.",
    ["T"] = "Sprecht mit Nafien",
  },
  [8463] = {
    ["D"] = "Eine Gruppe Getriebener hat kürzlich einen Vorrat instabiler Manakristalle aus einem unserer arkanen Sankten gestohlen. Wenn man mit den Kristallen nicht umzugehen vermag, können sie mit einer Kraft explodieren, die ausreicht, um ein Loch in die Stadtmauern zu reißen. Nun, $N, mir kann es egal sein, ob sich diese Narren beim Einverleiben instabiler arkaner Quellen selbst in die Luft sprengen, doch ein solcher Vorfall würde meine Männer schlecht dastehen lassen.$B$BBringt mir alle verbliebenen Kristalle, die Ihr finden könnt; wahrscheinlich lagern sie immer noch in ihren ursprünglichen Kisten.",
    ["O"] = "Bringt Aeldon Sonnenmal 6 instabile Manakristalle.",
    ["T"] = "Instabile Manakristalle",
  },
  [8464] = {
    ["D"] = "Die Furbolgs der Holzschlundfeste bleiben meist unter sich, $N. Wir gewähren allen, die ihre Vertrauenswürdigkeit unter Beweis gestellt haben, freies Geleit, meiden aber, wenn möglich, andere Furbolgstämme.$B$BIn letzter Zeit ist mir aufgefallen, dass der Winterfellstamm uns gegenüber immer größere Feindseligkeit zeigt. Sie scheinen in Rage zu sein, und ganz von ihrem Hass auf alles beseelt, das sie nicht verstehen.$B$BEs stimmt mich zwar zutiefst traurig, dass ich gegen meine Geschwister kämpfen muss, trotzdem möchte ich Euch bitten, dass Ihr einige von ihnen ausmerzt, um die Bedrohung, die von ihnen ausgeht, zu verringern. Ihr findet sie im Lager der Winterfelle.",
    ["O"] = "Salfa möchte, dass Ihr 8 Schamanen der Winterfelle, 8 Höhlenbehüter der Winterfelle und 8 Ursa der Winterfelle erledigt. Salfa befindet sich genau außerhalb des Eingangs zur Holzschlundfeste in Winterquell.",
    ["T"] = "Aktivität der Winterfelle",
  },
  [8465] = {
    ["D"] = "Die Verderbnis bedroht uns nicht nur in Form der Totenwaldfelle vom Süden her, sondern auch durch die Winterfellfurbolgs im Osten. Wenn Ihr uns auch weiterhin helfen wollt, sprecht mit Salfa auf der anderen Seite der Festung. Ihr findet sie nahe des Eingangs zu Winterquell. Folgt einfach dem linken Pfad, um zu ihr zu gelangen.$B$BDoch seid gewarnt! Sollten meinesgleichen Euch gegenüber hasserfüllt oder gar feindlich eingestellt sein, werden sie Euch angreifen. Sollte dies der Fall sein, durchquert das Gebiet schnellstmöglich und vermeidet alle Kämpfe mit ihnen. Einen Furbolg der Holzschlundfeste zu töten und sei es nur zur Selbstverteidigung, wird Euren Ruf in der Holzschlundfeste nur weiter verschlechtern.",
    ["O"] = "Durchquert die Holzschlundfeste und verlasst sie in Richtung Winterquell. Sprecht mit Salfa, die außerhalb des Eingangs zur Holzschlundfeste Wache steht.",
    ["T"] = "Sprecht mit Salfa",
  },
  [8466] = {
    ["D"] = "",
    ["O"] = "Bringt alle Federn, die ihr findet, zu Grazle.",
    ["T"] = "Federn für Grazle",
  },
  [8467] = {
    ["D"] = "",
    ["O"] = "Bringt alle Federn, die ihr findet, zu Grazle.",
    ["T"] = "Federn für Nafien",
  },
  [8468] = {
    ["D"] = "Hiermit setzt die Stadt Silbermond eine Belohnung auf den Kopf von Thaelis dem Manadurstigen aus, einem notorischen Aufwiegler und Rädelsführer getriebener Blutelfen. Ihm werden verschiedene Verbrechen gegen die Einwohner von Silbermond zur Last gelegt, einschließlich der Ermordung zweier Stadtwachen.$B$BMeldet Euch bei Unteroffizier Kan\'ren, um die Belohnung zu erhalten.",
    ["O"] = "Bringt Thaelis Kopf zu Unteroffizier Kan\'ren beim Falkenplatz im Immersangwald.",
    ["T"] = "Gesucht: Thaelis der Manadurstige",
  },
  [8469] = {
    ["D"] = "",
    ["O"] = "Bringt die Geisterperlen, die Ihr findet, zu Salsa.",
    ["T"] = "Perlen für Salfa",
  },
  [8470] = {
    ["D"] = "Bringt das Ritualtotem in die Holzschlundfeste und findet heraus, ob einer der dortigen Furbolgs eine Verwendung für diesen Gegenstand hat. Die Furbolgs der Holzschlundfeste werden solange nicht mit Euch sprechen, bis Euer Ruf bei ihnen neutral oder höher ist.",
    ["O"] = "Bringt das Ritualtotem in die Holzschlundfeste und findet heraus, ob einer der dortigen Furbolgs eine Verwendung für diesen Gegenstand hat. Die Furbolgs der Holzschlundfeste werden solange nicht mit Euch sprechen, bis Euer Ruf bei ihnen neutral oder höher ist.",
    ["T"] = "Ritualtotem der Totenwaldfelle",
  },
  [8471] = {
    ["D"] = "Ihr habt ein Ritualtotem der Winterfellfurbolgs gefunden. Das Totem selbst ist von unbestimmbarer Machart, aber es geht eine beinahe greifbare Aura der Furcht und Verzweiflung von ihm aus. Sollten tatsächlich alle Furbolgs der Winterfelle ein solches Totem bei sich tragen, würde dies ihren momentanen Zustand sicherlich erklären.$B$BEiner der Furbolgs innerhalb der Holzschlundfeste könnte vielleicht eine Verwendung für diesen Gegenstand haben... gesetztenfalls Ihr habt Euch genug Vertrauen verdient, um mit ihnen zu sprechen.",
    ["O"] = "Bringt das Ritualtotem in die Holzschlundfeste und findet heraus, ob einer der dortigen Furbolgs eine Verwendung für diesen Gegenstand hat. Die Furbolgs der Holzschlundfeste werden solange nicht mit Euch sprechen, bis Euer Ruf bei ihnen neutral oder höher ist.",
    ["T"] = "Ritualtotem der Winterfelle",
  },
  [8472] = {
    ["D"] = "Die Arkanpatrouillen, einst von unseren Vorvätern zum Schutze dieser Stadt erschaffen, befinden sich in einem gefährlichen Zustand des Verfalls. Sie erkennen uns nicht länger als ihre Herren und greifen uns ohne Vorwarnung an!$B$BObwohl sie schon vor Jahrzehnten konstruiert wurden, sind die Arkankerne, die sie antreiben, für uns immer noch von großem Wert. Ich möchte, dass Ihr einige dieser Kerne sammelt und sie mir bringt, damit wir sie anderweitig sinnvoll einsetzen können. Ihr findet die Patrouillen im zerstörten Teil von Silbermond, im Nordwesten.",
    ["O"] = "Sammelt 6 Arkankerne von den Arkanpatrouillen, die in den Ruinen von Silbermond umherstreifen, und bringt sie Magister Jaronis beim Falkenplatz im Immersangwald.",
    ["T"] = "Schwere Fehlfunktion",
  },
  [8473] = {
    ["D"] = "Das sind schwierige Zeiten, $N. Es war eine schwere Entscheidung, die Wälder am Rande der Geisterlande niederzubrennen, um die weitere Expansion der Geißel zu stoppen.$B$BDie Treants, seit Jahrzehnten unsere Verbündeten, versuchen den niedergebrannten Wald am Versengten Hain wieder aufzuforsten.$B$BEs tut mir in der Seele weh, Euch um diesen Gefallen zu bitten, $C, doch wir konnten unsere Verbündeten nicht davon abhalten, den Wald zu erneuern. Ihr müsst sie und ihr Vorhaben mit dem einzig noch verbleibenden Mittel beenden: Gewalt.",
    ["O"] = "Tötet 10 siechende Waldbehüter beim Versengten Hain und meldet Euch anschließend bei Larianna Flusswind, die sich im Turm nordwestlich des Versengten Hains im Immersangwald aufhält.",
    ["T"] = "Eine traurige Aufgabe",
  },
  [8474] = {
    ["D"] = "Auf dem Anhänger sind die eingravierten Initialen L.F. lesbar. Vielleicht weisen diese Anfangsbuchstaben ja auf die Person hin, die den Anhänger angefertigt hat.",
    ["O"] = "Macht die Person ausfindig, die Weißborkes Anhänger angefertigt haben könnte. Ihr könntet Hinweise über den Gegenstand erfahren.",
    ["T"] = "Weißborkes Anhänger",
  },
  [8475] = {
    ["D"] = "Die schwarze Schneise aus verbrannter Erde, welche sich durch den Immersangwald zieht, ist eine ständige Erinnerung an den dunklen Tag, als Arthas die Geißel in unsere geliebte Stadt führte.$B$BDieser Schandfleck ist mehr als nur ein Makel! Selbst heute noch dringen über diesen dunklen Pfad Kreaturen aus den Geisterlanden bis nach Silbermond vor.$B$BGeht, $N, und reicht meinen Männern Eure helfende Hand, indem Ihr die geistlosen Untoten tötet, welche in der Todesschneise umherstreifen.",
    ["O"] = "Tötet 8 Seuchenknochenplünderer und kehrt anschließend zu Waldläuferin Jaela in der Todesschneise im Immersangwald zurück.",
    ["T"] = "Die Todesschneise",
  },
  [8476] = {
    ["D"] = "Wir herrschen nicht über solch riesige Königreiche wie die Menschen oder die Orcs. Beinahe alles, was uns noch von unseren Ländereien in Azeroth geblieben ist, seht Ihr hier vor Euch, $N.$B$BBis wir die Scherbenwelt erreichen können, um ein neues Zuhause zu finden, müssen wir das Wenige, das uns geblieben ist, um jeden Preis verteidigen.$B$BUnsere unzivilisierten Nachbarn, die Amanitrolle, haben allerdings eine gänzlich andere Auffassung darüber, wem dieses Land gehört. Ihre täglichen Angriffe werden wir nicht länger tolerieren. Begebt Euch zu ihren Siedlungen, Zeb\'Watha und Tor\'Watha, und erteilt ihnen eine Lektion.",
    ["O"] = "Leutnant Dämmerflucht bei der Zuflucht der Weltenwanderer im Immersangwald möchte, dass Ihr 5 Berserker der Amani und 5 Axtwerfer der Amani tötet.",
    ["T"] = "Amaniübergriff",
  },
  [8477] = {
    ["D"] = "Die Amani und die Sin\'dorei pflegen seit langem eine gegenseitige Ablehnung und offenkundige Feindseligkeit. Das hält mich jedoch nicht von der Bewunderung der Arbeit eines Handwerkskollegen ab, $N.$B$BIch bin auf Stücke von Amaniwaffen gestoßen und muss zugeben, dass ich von der Qualität ihrer Arbeit beeindruckt bin. Man munkelt, dass Speermacher Otembe mit einem magischen Hammer arbeitet, der so leicht und gut ausbalanciert ist, dass er mit großem Geschick und höchst effizient ans Werk gehen kann.$B$BWenn Ihr mir dieses Werkzeug bringt, werde ich Euch damit eine Waffe anfertigen.",
    ["O"] = "Sucht in Tor\'Watha, südöstlich der Zuflucht der Weltenwanderer, nach Speermacher Otembe und bringt Otembes Hammer zu Arathel Sonnenschmied bei der Zuflucht der Weltenwanderer im Immersangwald.",
    ["T"] = "Der Hammer des Speermachers",
  },
  [8478] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Wählt Eure Waffe",
  },
  [8479] = {
    ["D"] = "Kommt näher, Mann. Keine Angst, Ven\'jashi wird Euch nicht wehtun.$B$BWir Dunkelspeere sind, genau wie Ihr, Erzfeinde der Amani. Ihr Anführer Zul\'Marosh ist \'n bösartiger Troll. Er gab mir Gift und ließ mich zum Sterben in diesem Käfig zurück.$B$BIch hab\' keine Angst vorm Sterben, Mann. Ich sehe dem Tod ins Auge und lache über ihn.$B$BDoch noch kann ich nicht in Frieden sterben, Mann. Nich\' bevor Zul\'Marosh tot ist. Sucht ihn jenseits des Sees in Zeb\'Watha. Ich werd\' das Gift ertragen, bis Ihr mir seinen dicken Kopf gebracht habt, Mann. Erst dann kann ich friedlich sterben.",
    ["O"] = "Ven\'jashi, der gefangene Troll in Tor\'Watha möchte, dass Ihr ihm Häuptling Zul\'Maroshs Kopf bringt. Häuptling Zul\'Marosh befindet sich in Zeb\'Watha, jenseits des Elrendarsees.",
    ["T"] = "Zul\'Marosh",
  },
  [8480] = {
    ["D"] = "Die Getriebenen werden mit jedem Tag dreister, $N. Sie sind mittlerweile gut genug organisiert, um einen Angriff auf den Ankerplatz der Sonnensegel zu verüben.$B$BAls wäre das nicht schon peinlich genug, so hat man bei ihrem Angriff all unsere Waffen zurückgelassen. Bringt mir unsere Waffen zurück, damit wir diesen Halunken zeigen können, wer hier das Sagen hat.",
    ["O"] = "Bringt 8 Waffen der Sin\'dorei zu Velendris Weißflor im Immersangwald.",
    ["T"] = "Zurückgelassene Waffen",
  },
  [8481] = {
    ["D"] = "$N, dank Euch haben wir eine Möglichkeit ausfindig gemacht, wie wir die Wurzel alles Bösen der Winterfelle beschwören können - den Dämon der Verderbnis, der sie beherrscht!$B$BBringt diese Fackel zum Eingang von Grolnars persönlicher Höhle und stellt sie dort auf. Der Dämon wird sich dadurch zu erkennen geben und nachforschen müssen. Das ist der Moment, in dem Ihr und Eure Verbündeten zuschlagen müsst! Ausschließlich die Essenz des gefallenen Dämonen wird den langwierigen Heilungsprozess des Winterfellstammes in Gang setzen... und die Erlösung der Holzschlundfeste und ihrer Einwohner.",
    ["O"] = "Stellt die Fackel zur Dämonenbeschwörung im Eingang von Grolnars Höhle, im Lager der Winterfellfurbolgs auf. Besiegt den Dämon und erlangt die Essenz von Xandivious für Gorn Einauge in der Holzschlundfeste.",
    ["T"] = "Die Wurzel alles Bösen",
  },
  [8482] = {
    ["D"] = "Die Dokumente enthalten detaillierte Karten von strategisch wichtigen Gebäuden im Immersangwald. Ihr entdeckt auch die ausführliche Darstellung eines arkanen Sanktums. Überall finden sich Randnotizen in einer Euch unbekannten Sprache.$B$BDa Ihr diese Dokumente bei einem darnassischen Spion gefunden habt, solltet Ihr sie besser zum Hauptmann der Wache bringen - Aeldon Sonnenmal.",
    ["O"] = "Bringt die belastenden Dokumente zu Aeldon Sonnenmal auf dem Falkenplatz im Immersangwald.",
    ["T"] = "Belastende Dokumente",
  },
  [8483] = {
    ["D"] = "Hört mir genau zu, $N. Das ist eine sehr delikate Angelegenheit; obwohl wir den Spion eindeutig identifiziert haben, können wir ihn nicht einfach in aller Öffentlichkeit töten. Eine Gefangennahme kommt auch nicht in Frage, das Risiko einer Flucht wäre einfach zu hoch.$B$BIch möchte, dass Ihr Ausgrabungsleiter Ambossel am Sanktum des Nordens aufsucht und ihn tötet. Geht dabei möglichst unauffällig vor, es soll nicht das Gerücht entstehen, wir hätten einem Spion Zugang zu unserer Stadt gewährt.$B$BBringt mir seinen Kopf als Beweis. Eisenschmiede wird schnell lernen, was es heißt, sich mit den Sin\'dorei anzulegen.",
    ["O"] = "Bringt den Kopf von Ausgrabungsleiter Ambossel zu Aeldon Sonnenmal beim Falkenplatz im Immersangwald.",
    ["T"] = "Der Zwergenspion",
  },
  [8484] = {
    ["D"] = "Eure Taten gehen weit über die Geschichte eines einfachen Helden hinaus, $N. Solltet Ihr der Vertreter Eures Volkes sein, dann sind dies diejenigen, mit denen wir, dem Frieden zu Willen, gerne zusammenarbeiten würden.$B$BBitte - bringt dies als Zeichen unseres Friedens zu dem Zwergenkönig, der in einem Berg aus Eisen lebt; ich glaube man nennt diese Stadt Eisenschmiede. In vergangenen Zeiten hatte er im Namen seiner Allianz versucht, mit uns Kontakt aufzunehmen. Es bietet sich jetzt also an, Euch als Schlüssel hinsichtlich der Friedensbemühungen Eurer Allianz zu betrachten.",
    ["O"] = "Bringt das Friedensangebot der Holzschlundfeste zu König Magni Bronzebart in Eisenschmiede.",
    ["T"] = "Das Bemühen um Frieden",
  },
  [8485] = {
    ["D"] = "Eure Taten gehen weit über die Geschichte eines einfachen Helden hinaus, $N. Solltet Ihr der Vertreter Eures Volkes sein, dann sind dies diejenigen, mit denen wir, dem Frieden zu Willen, gerne zusammenarbeiten würden.$B$BBitte, bringt dies als Zeichen unseres Friedens zu dem Kriegshäuptling der Orcs, der in den Landen von Durotar lebt; ich glaube man nennt die Stadt Orgrimmar. In vergangenen Zeiten hatte er im Namen seiner Horde versucht, mit uns Kontakt aufzunehmen. Es bietet sich jetzt also an, Euch als Schlüssel hinsichtlich der Friedensbemühungen Eurer Horde zu betrachten.",
    ["O"] = "Bringt das Friedensangebot der Holzschlundfeste zu Shok Narnes in Orgrimmar.",
    ["T"] = "Das Bemühen um Frieden",
  },
  [8486] = {
    ["D"] = "Es wurde Zeit, dass dieser Schwachkopf Caidanis endlich jemanden hierher gesandt hat.$B$BIch habe davor gewarnt, die Ladungen beim Sanktum des Westens zu erhöhen! Jetzt ist einer unserer Energiewandler zerstört und Arkangespenster strömen aus dem Sanktum!$B$BIhr müsst Euch um diese Kreaturen kümmern, bevor wir überhaupt jemanden für die Reparaturarbeiten hinein lassen können. Beeilt Euch!",
    ["O"] = "Leybewahrerin Velania möchte, dass Ihr 5 Managespenster und 5 Manapirscher tötet.",
    ["T"] = "Arkane Instabilität",
  },
  [8487] = {
    ["D"] = "Bevor Arthas und seine Armee der Geißel auf die Idee kamen, auf ihrem Weg nach Silbermond hier durchzumarschieren, war dies ein voll funktionstüchtiges arkanes Sanktum.$B$BJetzt hat es in etwa den gleichen Nutzen wie der Rest, der zur Todesschneise gehört. Der Grund ist besudelt, Untote werden davon angezogen und die Energie der Leylinien wurde komplett unterbrochen.$B$BWas ich jetzt herausfinden will, $N, ist, ob man diese Besudelung wieder umkehren kann oder nicht. Sammelt Erdproben dieser Gegend für mich, damit ich sie untersuchen kann. Vielleicht können wir mit der Zeit den normalen Zustand wieder herstellen.",
    ["O"] = "Lehrling Mirveda beim Sanktum des Ostens im Immersangwald möchte, dass Ihr 8 besudelte Erdproben einsammelt.",
    ["T"] = "Besudelte Erde",
  },
  [8488] = {
    ["D"] = "Irgendetwas ist faul, $N. Ich spüre die Gegenwart der Geißel. Macht Euch zum Kampf bereit!",
    ["O"] = "Beschützt Lehrling Mirveda vor dem Angriff der Geißel.",
    ["T"] = "Ungewöhnliche Ergebnisse",
  },
  [8489] = {
    ["D"] = "Dieser Gegenstand scheint einer der Arkanwandler zu sein, von denen Leybewahrerin Velania sprach. Er blieb vom Feuer verschont, vielleicht kann sie ja etwas damit anfangen.",
    ["O"] = "Bringt den intakten Arkanwandler zu Leybewahrerin Velania.",
    ["T"] = "Ein intakter Wandler",
  },
  [8490] = {
    ["D"] = "Uralte Runensteine hielten einst eine Schutzbarriere an der Grenze des Immersangs aufrecht.Viele wurden während Arthas\' Angriff zerstört, andere wurden gestohlen.$B$BVon den drei, die übrig geblieben sind, konnte nur dieser seine Macht bewahren. Der Runenstein des Ostens könnte jedoch noch gerettet werden.$B$BNehmt diesen Kristall und flößt dem Runensteins des Ostens seine Energie ein. Der Vorgang sollte nur einen Moment benötigen. Doch Vorsicht, die Energien werden auch in der Umgebung befindliche Kreaturen der Geißel anziehen. Sie dürfen den Runenstein auf keinen Fall zerstören!",
    ["O"] = "Platziert den energieerfüllten Kristall beim Runenstein des Ostens und verteidigt ihn 1 Minute lang vor der Geißel. Bringt den energieerfüllten Kristall anschließend zu Runenwächter Deryan im Immersangwald, um Eure Belohnung zu erhalten.",
    ["T"] = "Wiederaufladen der Verteidigung",
  },
  [8491] = {
    ["D"] = "Die Bachtatzen in dieser Region sind für ihre hochwertigen Pelze bekannt. Selbst Nachtelfenabschaum dringt in unsere Länder ein, um hier zu wildern.$B$BDie andauernden Scharmützel mit den Amani haben unsere Versorgung mit Ausrüstung stark strapaziert. Leider finden unsere Waldläufer aufgrund dessen kaum Zeit für die Pelzjagd. Vielleicht möchtet Ihr uns eine helfende Hand reichen und einige Bachtatzenfelle beschaffen, eine handgefertigte Rüstung wäre Euer Lohn. Sucht in den Wäldern nördlich von hier nach Bachtatzenpirschern.",
    ["O"] = "Velan Lichteiche in Morgenluft im Immersangwald möchte, dass Ihr ihm 6 Bachtatzenfelle bringt.",
    ["T"] = "Pelzjagd",
  },
  [8492] = {
    ["D"] = "Hey, $C, Ihr seht tauglich genug aus, um zu den Kriegsanstrengungen beizutragen! Wir brauchen jede Hilfe, die wir bekommen können, um die benötigten Vorräte für den Krieg in Ahn\'Qiraj zusammenzutragen. Heute ist Euer Glückstag.$B$BIch sammle Kupferbarren in Stapeln zu je 20 Stück. Der Zweck ist die Mühe wert, was denkt Ihr? Sicherlich wollt Ihr die Allianz unterstützen!",
    ["O"] = "Bringt 20 Kupferbarren zu Unteroffizier Steinbraue im Militärviertel in Eisenschmiede.",
    ["T"] = "Die Allianz braucht Kupferbarren!",
  },
  [8494] = {
    ["D"] = "Ihr da, seid gegrüßt, $G Herr Modenarr: Frau Modenärrin;, was für eine Augenweide Ihr doch seid. Solltet Ihr es, während Ihr auf \"Abenteuern\" wart, noch nicht gehört haben: die Allianz macht sich für den Krieg in Ahn\'Qiraj bereit und braucht dringend Eisenbarren... eine ganze Menge davon. Bringt sie in Stapeln zu je 20 Stück zu mir. Wir können doch auf Euch zählen, oder, $C?",
    ["O"] = "Bringt 20 Eisenbarren zu Korporal Schwarz im Militärviertel in Eisenschmiede.",
    ["T"] = "Die Allianz braucht Eisenbarren!",
  },
  [8498] = {
    ["D"] = "Unsere Späher haben berichtet, dass die Anführer des Schattenhammers neue Schlachtbefehle ausgegeben haben. Die Befehle wurden von Propheten des Schattenhammers unter schwerem Geleitschutz verteilt. Fangt die Schlachtbefehle ab und bringt sie zu Kommandant Mar\'alith.",
    ["O"] = "Beschafft Euch die Schlachtbefehle des Schattenhammers und bringt sie zu Kommandant Mar\'alith auf Burg Cenarius in Silithus. Ihr benötigt auch den taktischen Einsatz X, um diese Aufgabe abschließen zu können.",
    ["T"] = "Schlachtordnungen des Schattenhammerklans",
  },
  [8499] = {
    ["D"] = "Es ist äußerst freundlich, dass Ihr Euch nach den Bedürfnissen der Allianz erkundigt. Zusätzlich zu all den anderen Sammelaktionen, die gerade im Gange sind, benötigen wir noch jede Menge Thoriumbarren. Damit wurde ich beauftragt. Thorium wird der Herstellung von vielerlei Kriegsmaterialien dienen, vor allem aber dem Aufbau eines Bataillons von Dampfpanzern, die nach ihrer Fertigstellung per Lufttransport nach Silithus gebracht werden sollen. Könntet Ihr uns bei diesem Unterfangen behilflich sein, $C?",
    ["O"] = "Bringt 20 Thoriumbarren zu Dame Doppelzopf im Militärviertel in Eisenschmiede.",
    ["T"] = "Die Allianz braucht Thoriumbarren!",
  },
  [8500] = {
    ["D"] = "Schön, Euch wiederzusehen, $N. Ich hoffe es ist Euch gut ergangen. Es stimmt, wir benötigen noch immer Thoriumbarren. Solltet Ihr welche entbehren können, ich sammle sie hier für die Kriegsvorbereitungen von Ahn\'Qiraj.",
    ["O"] = "Bringt 20 Thoriumbarren zu Dame Doppelzopf beim Landeplatz in Dun Morogh.",
    ["T"] = "Die Allianz braucht mehr Thoriumbarren!",
  },
  [8503] = {
    ["D"] = "So $C, habe ich Euch schon von all den wunderbaren Verwendungszwecken erzählt, die Würgetang bietet? Ja? Oh, na dann... wir brauchen auf jeden Fall eine Menge davon, und wenn ich eine Menge sage, dann meine ich eine große Menge! Meint Ihr, dass Ihr uns helfen könntet? Ich weiß, dass das bedeutet, dass Ihr daraus nichts Tolles für Euch selbst herstellen könnt, aber es ist für einen guten Zweck!",
    ["O"] = "Bringt 20 Stück Würgetang zu dem Gefreiten Drechselknacks im Militärviertel in Eisenschmiede.",
    ["T"] = "Die Allianz braucht Würgetang!",
  },
  [8504] = {
    ["D"] = "Hallo Ihr... warum seid Ihr nochmal hier? Ah... natürlich, $N! Ihr seid gekommen, um nachzusehen, ob ich noch mehr Würgetang brauche, oder? Na dann ist heute Euer Glückstag, denn die Antwort auf diese wichtige Frage lautet eindeutig JA! Könntet Ihr vielleicht noch einmal zwanzig mehr sammeln?",
    ["O"] = "Bringt 20 Stück Würgetang zu dem Gefreiten Drechselknacks beim Landeplatz in Dun Morogh.",
    ["T"] = "Die Allianz braucht mehr Würgetang!",
  },
  [8505] = {
    ["D"] = "$C, lila Lotus ist eine Pflanze, die vielfach verwendet werden kann. Ich bin hier im Auftrag der Allianz und verantwortlich dafür, dass die gesammelten Pflanzen sicher aufbewahrt und ihrem vorgesehenen Verwendungszweck im Rahmen unserer Kriegsbemühungen zugeführt werden.$B$BIch würde Euch gerne damit beauftragen durch die Welt zu ziehen und zu mir zurückzukehren, sobald Ihr einige dieser Pflanzen gesammelt habt. Wäre das machbar?",
    ["O"] = "Bringt 20 lila Lotus zu Meister Nachtweise im Militärviertel in Eisenschmiede.",
    ["T"] = "Die Allianz braucht lila Lotus!",
  },
  [8507] = {
    ["D"] = "Seid gegrüßt, $N. Bevor ich Euch mit umfassenderen Aufgaben in diesem Krieg beauftrage, müsst Ihr Euren Anteil am Felddienst leisten.$B$BIhr könnt die Eisenschmiedebrigade von Hauptmann Rußschmied außerhalb des Zoraschwarmbaus finden.$B$BErstattet ihm oder seinem Leutnant, Janela Starkhammer, Bericht und kommt dann wieder zu mir. Ich werde wichtigere Anweisungen für Euch bereithalten.$B$BOh, bitte erweist uns den Gefallen und haltet Euch von der Orgrimmarlegion fern. Wir sind gezwungen die beiden Streitkräfte aufgrund gegenseitiger Feindseligkeit auseinander zu halten.",
    ["O"] = "Erstattet am Posten der Eisenschmiedebrigade beim Zoraschwarmbau Bericht. Bereitet Eure nicht unterzeichneten Felddienstpapiere vor, lasst sie von Hauptmann Rußschmied unterzeichnen und kehrt zu Windrufer Kaldon auf Burg Cenarius in Silithus zurück.$B$BHinweis: das Heilen oder Wirken von begünstigenden Zaubern auf ein Mitglied der Eisenschmiedebrigade wird Euch für den PvP-Kampf markieren.",
    ["T"] = "Felddienst",
  },
  [8509] = {
    ["D"] = "Ich möchte nicht lange darum herum reden, $C. Die Allianz benötigt eine große Menge an Arthas\' Tränen für die Kriegsanstrengungen von Ahn\'Qiraj. Fragt mich nicht für was; ich befolge nur Anweisungen. Trotzdem würde ich Euch gerne bitten, so viele dieser Pflanzen wie möglich zu sammeln und sie anschließend hierher zu mir zu bringen. Würdet Ihr das tun?",
    ["O"] = "Bringt 20 Arthas\' Tränen zu Stabsfeldwebel Germaine im Militärviertel in Eisenschmiede.",
    ["T"] = "Die Allianz braucht Arthas\' Tränen!",
  },
  [8511] = {
    ["D"] = "Wir Steinschinders können auf eine lange Geschichte der Kürschnerei zurückblicken, $C. Deshalb hat die Allianz auch meine Familie damit beauftragt, bei der Sammlung von leichtem Leder für die Kriegsanstrengungen von Ahn\'Qiraj mitzuhelfen. Ich achte darauf, dass das Leder vernünftig gegerbt wird. Was danach daraus gemacht wird, weiß ich nicht. Wir brauchen jedoch Eure Hilfe.$B$BDa ich wegen meiner Arbeit hier bleiben muss, brauche ich Euch, um die Häute zu beschaffen und sie anschließend zu mir zu bringen. Was meint Ihr $G Junge : Mädel;, seid Ihr dabei?",
    ["O"] = "Bringt 10 Einheiten leichtes Leder zu Bonnie Steinschinder im Militärviertel in Eisenschmiede.",
    ["T"] = "Die Allianz braucht leichtes Leder!",
  },
  [8512] = {
    ["D"] = "Schön, Euch wiederzusehen, $N. Es scheint, als würden wir noch mehr leichtes Leder für die Herstellung verschiedenster Kriegsmaterialien benötigen. Es wäre großartig, wenn ihr mir noch einmal einen Stapel mit zehn Stücken bringen könntet.",
    ["O"] = "Bringt 10 Einheiten leichtes Leder zu Bonnie Steinschinder beim Landeplatz in Dun Morogh.",
    ["T"] = "Die Allianz braucht mehr leichtes Leder!",
  },
  [8513] = {
    ["D"] = "Ja, $Gmein Herr:meine Dame;, ich wurde mit der Sammlung und Aufbewahrung des mittleren Leders für die Kriegsvorbereitungen beauftragt. Solltet Ihr über die Mittel verfügen, um mittleres Leder besorgen zu können, woher auch immer, dann bringt es bitte zu mir. Ich könnte mit Sicherheit etwas Unterstützung gebrauchen, wenn Ihr Zeit habt.",
    ["O"] = "Bringt 10 Einheiten mittleres Leder zu dem Gefreiten Porter im Militärviertel in Eisenschmiede.",
    ["T"] = "Die Allianz braucht mittleres Leder!",
  },
  [8514] = {
    ["D"] = "Ja, $Gmein Herr:meine Dame;, es sieht aus als hätten wir bis zum heutigen Tag $2063w Stücke mittleren Leders gesammelt. Ich weiß es ist viel verlangt, aber wenn Ihr noch immer gewillt seid, dann könnte ich Eure Hilfe beim Zusammentragen des restlichen Leders noch einmal gebrauchen.",
    ["O"] = "Bringt 10 Einheiten mittleres Leder zu dem Gefreiten Porter beim Landeplatz in Dun Morogh.",
    ["T"] = "Die Allianz braucht mehr mittleres Leder!",
  },
  [8515] = {
    ["D"] = "Junge, Junge, das wird großartig! $C, die Vorbereitungen für den Krieg in Ahn\'Qiraj sind in vollem Gange und man hat mich damit beauftragt sämtliche Ladungen an dickem Leder zu überwachen, die hier abgeliefert werden. Wir fertigen zahlreiche Dinge daraus an, aber zuallererst brauchen wir eine große Menge davon. Eine gewaltige Menge! Jetzt zu Euch.$B$BGlaubt Ihr, dass Ihr losziehen und mir einen fetten Stapel dickes Leder beschaffen könntet? Ja, wirklich?!",
    ["O"] = "Bringt 10 Einheiten dickes Leder zu Marta Feinspindel im Militärviertel in Eisenschmiede.",
    ["T"] = "Die Allianz braucht dickes Leder!",
  },
  [8516] = {
    ["D"] = "Tja, der Stapel ist noch nicht hoch genug. $N, wir haben noch immer nicht unsere Anforderung von $2071w Einheiten dicken Leders erreicht. Denkt nur an all die Dinge, die wir aus dem benötigten Leder noch herstellen können! Allerlei Rüstungen und Waffen. Sachen für das Innere der Dampfpanzer und Zubehör für Gewehre! Ingenieursbrillen und am Ende, wenn wir tatsächlich zu viel gesammelt haben, dient es auch noch als Material für die Zeppeline der Horde!$B$BBringt Ihr mir noch mehr dickes Leder?",
    ["O"] = "Bringt 10 Einheiten dickes Leder zu Marta Feinspindel beim Landeplatz in Dun Morogh.",
    ["T"] = "Die Allianz braucht mehr dickes Leder!",
  },
  [8517] = {
    ["D"] = "Der Krieg in Ahn\'Qiraj wird lange und blutig sein. Es ist sehr weise, dass die Allianz Eure Hilfe und die von anderen Euresgleichen in Anspruch nimmt, um vorerst die benötigten Mittel zu erstellen, anstelle sich sofort in den Konflikt zu stürzen.$B$BIch wurde mit dem Sammeln von Leinenverbänden für den Krieg beauftragt und hoffe nun, dass Ihr mir bei dieser Aufgabe zu Hand gehen werdet. Wir brauchen so viele Verbände wie möglich. Es ist keine leichte Aufagbe, ich weiß, aber wenigstens lassen sich Leinenverbände am einfachsten herstellen. Seid Ihr dabei, $C?",
    ["O"] = "Bringt 20 Leinenverbände zu Schildwache Silberwolke im Militärviertel in Eisenschmiede.",
    ["T"] = "Die Allianz braucht Leinenverbände!",
  },
  [8520] = {
    ["D"] = "Mit dem immer näherrückenden Krieg in Ahn\'Qiraj gilt es alles erdenkliche zu tun, um unsere Soladten mit allem zu versorgen, das sie brauchen werden. Wir kümmern uns hier genau um diese Sache. Ich bin verantwortlich für das Ansammeln von Seidenstoffverbänden.$B$BGlaubt Ihr, dass Ihr mir dabei helfen könntet, $C?",
    ["O"] = "Bringt 20 Seidenverbände zu Schwester Steinfeld im Militärviertel in Eisenschmiede.",
    ["T"] = "Die Allianz braucht Seidenverbände!",
  },
  [8522] = {
    ["D"] = "$C, stellt die Verdienste des Krieges in Frage, wenn Ihr wollt, er hat jedoch schon längst begonnen. Seht Euch bei Eurer Familie oder Euren Freunden um und fragt Euch dann, was es ist, das Ihr tun könnt, um im Fall der Fälle zu ihrem Überleben beizutragen.$B$BIch sammle hier Runenstoffverbände jeglicher Herkunft. Würdet Ihr gerne welche spenden?",
    ["O"] = "Bringt 20 Runenstoffverbände zu Bewahrer Mondschatten im Militärviertel in Eisenschmiede.",
    ["T"] = "Die Allianz braucht Runenstoffverbände!",
  },
  [8523] = {
    ["D"] = "Wir treffen also erneut aufeinander, um wichtige Angelegenheiten zu besprechen, $C. Ich möchte Euch noch einmal für Eure bisherigen Bemühungen danken; nicht jeder ist so selbstlos wie Ihr. Es gibt jedoch noch mehr zu tun.$B$BWürdet Ihr noch einmal Runenstoffverbände für mich sammeln gehen und sie dann zu mir bringen, $N?",
    ["O"] = "Bringt 20 Runenstoffverbände zu Bewahrer Mondschatten beim Landeplatz in Dun Morogh.",
    ["T"] = "Die Allianz braucht mehr Runenstoffverbände!",
  },
  [8524] = {
    ["D"] = "Hey, Ihr da! Wenn ich zu Euch sagen würde \'Sollten Soldaten nicht etwas zu essen haben, wie jeder andere auch?\', dann würdet Ihr natürlich \'Ja!\' sagen. Da stimme ich 110%mit Euch überein! Schön, dass wir uns einig sind. Es gibt nur eine Sache, um die ich Euch gerne bitten würde: zieht los, fischt eine ordentliche Menge Regenbogenflossenthunfische, kocht diese dann und bringt sie zu mir zurück. Mmm, mmm, ich kann es bereits schmecken.",
    ["O"] = "Bringt 20 Regenbogenflossenthunfische zu Gastronom Glitschko im Militärviertel in Eisenschmiede.",
    ["T"] = "Die Allianz braucht Regenbogenflossenthunfisch!",
  },
  [8526] = {
    ["D"] = "Oh, wie geht\'s, $C? Ich sehe, dass Ihr eventuell beim Sammeln von Lebensmittelvorräten für den aufziehenden Krieg in Ahn\'Qiraj mithelfen möchtet. Mein Vater hat sich wohl gedacht, dass es eine gute Lernerfahrung für mich wäre, wenn ich mich als freiwillige Aushilfe hier melde. Ein großer Fehler. Ich esse noch nicht einmal Fleisch.$B$BEgal, wenn Euch danach ist, dann könntet Ihr mir eine Menge gerösteter Raptoren bringen. Anscheinend ist das genau das, wonach Soldaten verlangen.",
    ["O"] = "Bringt 20 geröstete Raptoren zu Sarah Sorgenpfeifer im Militärviertel in Eisenschmiede.",
    ["T"] = "Die Allianz braucht geröstete Raptoren!",
  },
  [8528] = {
    ["D"] = "Das Sammeln von gekochtem Fisch mag zwar nicht sehr heldenhaft erscheinen, aber Ihr solltet die Wichtigkeit von guten Vorbereitungen nicht unterschätzen, $C. Soldaten müssen eben essen, wie jeder andere auch. Wir leisten hierfür unseren Teil, um sicherzustellen, dass sie mit allem versorgt sind, das sie brauchen, sobald der Krieg in Ahn\'Qiraj beginnt.$B$BFolglich sorge ich hier für die Einlagerung von Tüpfelgelbschwänzen, eine vielseitige Nahrungsressource, die sich gut lagern lässt und auf verschiedenste Art und Weise zubereitet werden kann. Der Geruch ähnelt zudem nicht dem von anderen Salzwasserfischen. Wollt Ihr mir bei dieser Unternehmung behilflich sein?",
    ["O"] = "Bringt 20 Tüpfelgelbschwänze zu Jägerin Flinkbach im Militärviertel in Eisenschmiede.",
    ["T"] = "Die Allianz braucht Tüpfelgelbschwänze!",
  },
  [8529] = {
    ["D"] = "Wenn Ihr noch immer gewillt seid, dann würde ich Euch gerne aussenden, um einen weiteren Schwung Tüpfelgelbschwänze zu beschaffen, $N. Ich vermute, dass es nach der Erfahrung, die Ihr beim ersten Mal gesammelt habt, diesmal wesentlich schneller und einfacher für Euch sein wird. Seid Ihr dabei?",
    ["O"] = "Bringt 20 Tüpfelgelbschwänze zu Jägerin Flinkbach beim Landeplatz in Dun Morogh.",
    ["T"] = "Die Allianz braucht mehr Tüpfelgelbschwänze!",
  },
  [8530] = {
    ["D"] = "$C, ich brauche Eure Hilfe. Im Geschmolzenen Kern gibt es diese Steine, sengende Kernsteine, um genau zu sein, die wir für die Kriegsanstrengungen von Ahn\'Qiraj einsammeln müssen. Mir wurde gesagt, dass unsere Schmiede und Ingenieure eine gute Verwendung für sie haben. Jetzt kommt Ihr ins Spiel. Ich brauche Euch, damit Ihr zum Geschmolzenen Kern geht und so viele dieser Kernsteine wie Ihr könnt einsammelt. Natürlich werdet Ihr dafür neununddreißig Eurer engsten Freunde mit Euch nehmen wollen.",
    ["O"] = "Bringt 50 sengende Kernsteine zu Kommandant Hammerkraft beim Landeplatz in Dun Morogh.",
    ["T"] = "Die Allianz braucht sengende Kernsteine!",
  },
  [8532] = {
    ["D"] = "Ich bin froh, dass Ihr Euch entschieden habt hier auszuhelfen. Nicht jeder würde das tun, deshalb ist es auch wichtig, dass Ihr wirklich versteht, was ich hier brauche. Kupferbarren, nicht Kupfererz. Wir haben weder die Zeit, noch genügend Leute, um das Material hier selbst zu verhütten, wenn Ihr also so freundlich wäret und diesen Vorgang für uns übernehmen würdet. Man würde Eure Mühen an den richtigen Stellen zur Kenntnis nehmen. Was sagt Ihr dazu, $C?",
    ["O"] = "Bringt 20 Kupferbarren zu Minenarbeiter Kromwell im Tal der Geister in Orgrimmar.",
    ["T"] = "Die Horde braucht Kupferbarren!",
  },
  [8534] = {
    ["D"] = "Wir haben unsere besten Späher bis weit in die Schwarmbauten der Silithiden ausgesendet. Späherin Azenel hat die Aktivitäten der Silithiden im Zoraschwarm nun schon seit Tagen beobachtet. Findet sie und besorgt Euch einen schriftlichen Bericht. Wir müssen so schnell wie möglich auf diese Informationen reagieren können, $N.",
    ["O"] = "Setzt Euch mit der cenarischen Späherin Azenel innerhalb des Zoraschwarmbaus in Verbindung und bringt den Späherbericht des Zoraschwarms zu Windruferin Prachthorn auf Burg Cenarius. Ihr benötigt auch den taktischen Einsatz VI, um diese Aufgabe abschließen zu können.",
    ["T"] = "Späherbericht des Zoraschwarms",
  },
  [8535] = {
    ["D"] = "Beschwört und vernichtet einen weißgrauen Templer. Erstattet anschließend Bericht bei Bor Wildmähne auf Burg Cenarius. Ihr benötigt auch den taktischen Einsatz IV, um diese Aufgabe abschließen zu können.",
    ["O"] = "Beschwört und vernichtet einen weißgrauen Templer. Erstattet anschließend Bericht bei Bor Wildmähne auf Burg Cenarius. Ihr benötigt auch den taktischen Einsatz IV, um diese Aufgabe abschließen zu können.",
    ["T"] = "Weißgrauer Templer",
  },
  [8542] = {
    ["D"] = "Bringt 20 Zinnbarren zu Grunzer Maug im Tal der Geister in Orgrimmar.",
    ["O"] = "Bringt 20 Zinnbarren zu Grunzer Maug im Tal der Geister in Orgrimmar.",
    ["T"] = "Die Horde braucht Zinnbarren!",
  },
  [8545] = {
    ["D"] = "$C, der Krieg rückt näher und das Einzige, was wir tun können, ist uns darauf vorzubereiten. Hierbei können wir uns gegenseitig helfen. Seht Ihr, ich wurde mit der Sammlung von Mithrilbarren beauftragt, die wir zur Herstellung von allerlei Kriegsbedarf benötigen. Es fehlt noch eine ganze Menge!$B$BSo, ich hoffe, Ihr zieht jetzt los, baut so viel Mithrilerz ab, wie Ihr könnt und verhüttet es zu Mithrilbarren. Könntet Ihr das für uns tun?",
    ["O"] = "Bringt 20 Mithrilbarren zu Schlachtrufer T\'kelah im Tal der Geister in Orgrimmar.",
    ["T"] = "Die Horde braucht Mithrilbarren!",
  },
  [8546] = {
    ["D"] = "Bringt 20 Mithrilbarren zu Schlachtrufer T\'kelah im Tal der Geister in Orgrimmar.",
    ["O"] = "Bringt 20 Mithrilbarren zu Schlachtrufer T\'kelah im Tal der Geister in Orgrimmar.",
    ["T"] = "Die Horde braucht mehr Mithrilbarren!",
  },
  [8547] = {
    ["D"] = "Welcome to the World of Warcraft!$B$BAs special thanks for purchasing the World of Warcraft Collector\'s Edition, turn in this gift voucher to Marsilla Dawnstar at the Sunspire on Sunstrider Isle.  You\'ll then be given a gift: a little companion to join you on your quest for adventure and glory.$B$BThanks again, and enjoy your stay in the World of Warcraft!",
    ["O"] = "Bring the Sunstrider Isle Gift Voucher to Marsilla Dawnstar at the Sunspire on Sunstrider Isle.",
    ["T"] = "Willkommen!",
  },
  [8548] = {
    ["D"] = "Die Abzeichen, die Ihr für Eure Erfüllung von Kampf-, Logistik- und taktischen Aufträgen für Burg Cenarius erhalten habt, dienen zu weit mehr als der Angeberei, $N.$B$BIhr solltet deren Wert besser zu schätzen wissen. Da Ihr gegenüber unserer Organisation Loyalität bewiesen habt, kann ich Euch bestimmte Ausrüstungsgegenstände anbieten, die wir lediglich für unsere engsten Verbündeten im Austausch gegen diese Abzeichen bereithalten.",
    ["O"] = "Bringt 5 cenarische Gefechtsabzeichen, 3 cenarische Versorgungsabzeichen und 7 cenarische Strategieabzeichen zu Vargus auf Burg Cenarius in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen freundlichen Ruf beim Zirkel des Cenarius.",
    ["T"] = "Schlachtrüstung des Kriegsfreiwilligen",
  },
  [8549] = {
    ["D"] = "Man sagt, dass sich jede Generation einer bestimmten Prüfung stellen muss. Ich fürchte, dass der Krieg von Ahn\'Qiraj genau das Ereignis ist, in dem unser Eifer auf die Probe gestellt wird. Wir bereiten uns deshalb vor, und finden dabei heraus, was uns wirklich ausmacht.$B$BIch habe mich freiwillig gemeldet, um der Horde beim Sammeln von so vielen Friedensblumen wie möglich zu helfen. Diese können dann in Tränke für den Kriegseinsatz verarbeitet werden. Ich hoffe, dass Ihr hier seid, um mir behilflich zu sein. Wenn ja, dann bringt mir eine gute Menge dieser Pflanzen. Werdet Ihr mich unterstützen, $C?",
    ["O"] = "Bringt 20 Friedensblumen zu der Kräuterkundigen Stolzfeder im Tal der Geister in Orgrimmar.",
    ["T"] = "Die Horde braucht Friedensblumen!",
  },
  [8550] = {
    ["D"] = "Bringt 20 Friedensblumen zu der Kräuterkundigen Stolzfeder im Tal der Geister in Orgrimmar.",
    ["O"] = "Bringt 20 Friedensblumen zu der Kräuterkundigen Stolzfeder im Tal der Geister in Orgrimmar.",
    ["T"] = "Die Horde braucht mehr Friedensblumen!",
  },
  [8551] = {
    ["D"] = "Früher hab ich ein Schiff von hier bis ganz nach Ratschet, in Kalimdor gesegelt. Ich war ein erfolgreicher Kapitän mit einem einwandfreien Ruf.$B$BBis...$B$BDiese verfluchten Meeresriesen mich ruinierten! Sie haben mein Schiff zerschmettert, meine Crew umgebracht und mich in ein Rettungsboot nach Beutebucht gesteckt. Einer dieser Riesen, Gorlash, hat meine Kapitänstruhe gestohlen. Er sagte, er wolle sie als Schnupftabakdose. Diese Dreistigkeit!$B$BIch habe gehört, dass Gorlash an der Küste östlich von hier sein Unwesen treibt. Findet ihn und bringt mir meine Truhe zurück! Tut das, und Ihr habt Euch die Dankbarkeit eines Kapitäns verdient!",
    ["O"] = "Bringt Smotts\' Truhe zu Hecklebury Smotts nach Beutebucht.",
    ["T"] = "Die Truhe des Kapitäns",
  },
  [8553] = {
    ["D"] = "Nachdem ich mein Schiff an diese Riesen verlor, kaufte ich mir ein neues. Ich hab es SMOTTS\' REVENGE genannt, Vorräte gekauft, eine Crew angeheuert und Segel gesetzt, um diese Schurken ausfindig zu machen. Ich habe sie auch gefunden, aber... sie haben mich besiegt. Sie haben die SMOTTS\' REVENGE zerschmettert, meine zweite Crew umgebracht und mich in einem weiteren Rettungsboot ausgesetzt.$B$BDieses Mal hat ein anderer Riese, Negolash, meinen Entersäbel gestohlen.$B$BStellt Euch Negolash und bringt mir meinen Entersäbel!$B$BSprecht mit Knirpsens. Er war mein Smutje auf der SMOTTS\' REVENGE und hat den letzten Angriff überlebt. Er kann Euch helfen, den Riesen zu finden.",
    ["O"] = "Sprecht mit Knirpsens.",
    ["T"] = "Der Entersäbel des Kapitäns",
  },
  [8554] = {
    ["D"] = "Negolash ist ein hungriger Riese.$B$BAls die Riesen unser Schiff zerstörten, ging Negolash schnurstracks in meine Kombüse. Er hatte keinen Appetit auf die Crew wie die anderen Riesen. Negolash wollte Wein... und meine gegrillten Bussardflügel. Die sind meine Spezialität. Für das Rezept musste ich bis ins Ödland reisen!$B$BWenn Ihr den Riesen anlocken wollt, dann besorgt Euch etwas Wein und einen Haufen Bussardflügel. Legt sie in unser altes Rettungsboot, an der Küste südöstlich von hier. Sobald Negolash das Essen riecht, wird er bestimmt kommen!",
    ["O"] = "Bringt 10 gegrillte Bussardflügel und 5 Flaschen Dschungelrankenwein zu Käpt\'n Smotts Rettungsboot.$B$BTötet Negolash und bringt Smotts\' Entersäbel zu Kapitän Smotts ins Schlingendorntal.",
    ["T"] = "Negolash entgegentreten",
  },
  [8557] = {
    ["D"] = "Ich freue mich, dass Ihr uns bei unserem Angriff auf Ahn\'Qiraj unterstützen wollt, $N. Ich kann einen schützenden Gegenstand für Euch herstellen, der Euch im Kampf gegen unseren Feind behilflich sein wird, $C.$B$BIch benötige dafür ein Tuch der Qiraji, das ich dann natürlich für Euch anpassen werde. Außerdem bräuchte ich noch Götzen und Skarabäen, die ich zu einer passenden Schnalle verarbeiten möchte.$B$BIhr werdet bestimmt nicht enttäuscht sein.",
    ["O"] = "Bringt 1 Kampftuch der Qiraji, 2 Onyxgötzen, 5 Sillberskarabäen und 5 Knochenskarabäen zu Keyl Flinkklaue in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen respektvollen Ruf beim Zirkel des Cenarius.",
    ["T"] = "Tuch der unnachgiebigen Stärke",
  },
  [8558] = {
    ["D"] = "Die Qiraji und ihre Diener besitzen Gegenstände, die aus dem seltensten und feinsten Material hergestellt werden: Knochen von riesengroßen uralten Kreaturen, Edelsteine aus den Tiefen der Erde, reines und starkes Metall.$B$BAllein der Anblick dieser Qirajiartefakte erfüllt mich mit dem Drang, mein Handwerk an ihren zerschmetterten und geschmolzenen Stücken auszuüben. Beweist dem Zirkel Eure Loyalität, $N, und ich werde aus den feinsten Qirajibestandteilen eine mächtige Waffe für Euch herstellen.",
    ["O"] = "Bringt 1 Stachelgriff der Qiraji, 2 Alabastergötzen, 5 Kristallskarabäen und 5 Steinskarabäen zu Aufseher Haro in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen ehrfürchtigen Ruf beim Zirkel des Cenarius.",
    ["T"] = "Sichel der unnachgiebigen Stärke",
  },
  [8559] = {
    ["D"] = "Qirajimagie ist sehr stark und uralt. Die mächtigsten Qiraji handhaben Gegenstände, die so machtvoll sind, dass sie sogar einem Drachen Einhalt gebieten.$B$BBringt mir die Befehlsbindungen der Qiraji sowie andere geringfügigere Bestandteile und ich werde ein Paar mächtige Stiefel für Euch herstellen.$B$BIhr werdet sie im Kampf gegen den Feind als nützlich empfinden.",
    ["O"] = "Bringt die Befehlsbindungen der Qiraji, 2 Götzen des Krieges, 5 Elfenbeinskarabäen und 5 Goldskarabäen zu Kandrostrasz in Ahn\'Qiraj. Diese Aufgabe erfordert auch einen neutralen Ruf bei der Brut Nozdormus.",
    ["T"] = "Erobererschienbeinschützer",
  },
  [8561] = {
    ["D"] = "Die Zwillingsimperatoren... ihr unaufhörliches Flüstern martert langsam meinen Verstand. Sie verhöhnen mich, wissend, dass ich nicht tiefer in ihr Reich vordringen kann, um meinen Bruder Arygos zu retten.$B$BIn meinen Visionen sehe ich ihre monströsen Fratzen, die mit mächtigen Kronen geschmückt sind. Ich werde meine Vergeltung noch bekommen... ich werde diese Symbole der Qirajimacht in solche verwandeln, die unserem Nutzen gerecht werden.$B$BBringt mir die benötigten Bestandteile, $N. Ich werde Euch dafür ein Kopfstück von unvergleichlicher Macht geben.",
    ["O"] = "Bringt Vek\'nilashs Reif, 2 Götzen der Sonne, 5 Steinskarabäen und 5 Kristallskarabäen zu Andorgos in Ahn\'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen freundlichen Ruf bei der Brut Nozdormus.",
    ["T"] = "Krone des Eroberers",
  },
  [8562] = {
    ["D"] = "Der unaussprechliche Alte Gott erwacht langsam aus seinem Schlummer unterhalb Ahn\'Qirajs. Das schreckliche Schicksal, das Azeroth erwartet, könnte in Tagen oder auch Jahren Wirklichkeit werden, Zeit spielt für Wesen seiner Macht keine Rolle.$B$BStehen die Sterne günstig, wird er furchtbaren Zorn gegen alle Lebewesen walten lassen. Ihr müsst dem zuvorkommen und einen Weg finden, um seiner Existenz ein Ende zu bereiten.$B$BTut dies und bringt mir ein Stück seiner Hülle. Ich werde Euch eine Rüstung gewähren, die noch kein Sterblicher getragen hat... Rüstung für einen Helden, der einen Gott bezwungen hat!",
    ["O"] = "Bringt den Knochenpanzer des Alten Gottes, 2 Götzen des Krieges, 5 Silberskarabäen und 5 Knochenskarabäen zu Vethsera innerhalb von Ahn\'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen wohlwollenden Ruf bei der Brut Nozdormus.",
    ["T"] = "Erobererbrustplatte",
  },
  [8563] = {
    ["D"] = "Während Ihr in Eurer Klasse weiter aufsteigt, werdet Ihr sicher die Ausbildung, die Euch der Lehrer Eurer Klasse anbietet, nutzen wollen. Der Lehrer für junge Hexenmeister hier auf der Insel der Sonnenwanderer ist Beschwörer Teli\'Larien. Sprecht mit Beschwörer Teli\'Larien, um herauszufinden, welche Ausbildung er Euch anbieten kann. Die Ausbildung hat ihren Preis, bringt also ein paar Münzen mit Euch.$B$BBeschwörer Teli\'Larien - wie alle anderen Lehrer auf der Insel der Sonnenwanderer auch - ist innerhalb des Sonnenturms, auf der unteren Ebene, anzutreffen.",
    ["O"] = "Sprecht mit Beschwörer Teli\'Larien - dem Hexenmeisterlehrer - innerhalb des Sonnenturms auf der Insel der Sonnenwanderer.",
    ["T"] = "Ausbildung zum Hexenmeister",
  },
  [8564] = {
    ["D"] = "Während Ihr in Eurer Klasse weiter aufsteigt, werdet Ihr sicher die Ausbildung, die Euch der Lehrer Eurer Klasse anbietet, nutzen wollen. Die Lehrerin für junge Priester hier auf der Insel der Sonnenwanderer ist Matrone Arena. Sprecht mit Matrone Arena, um herauszufinden, welche Ausbildung sie Euch anbieten kann. Die Ausbildung hat ihren Preis, bringt also ein paar Münzen mit Euch.$B$BMatrone Arena - wie alle anderen Lehrer auf der Insel der Sonnenwanderer auch - ist innerhalb des Sonnenturms, auf der unteren Ebene, anzutreffen.",
    ["O"] = "Sprecht mit Matrone Arena - der Priesterlehrerin - innerhalb des Sonnenturms auf der Insel der Sonnenwanderer.",
    ["T"] = "Ausbildung zum Priester",
  },
