  [8876] = {
    ["D"] = "I hold the secrets to creating Lunar Festival rockets, and I am happy to share my knowledge with the honored.$B$BIn exchange for coins of ancestry, I can offer you the recipe to make a small rocket...",
    ["O"] = "",
    ["T"] = "Kleine Raketen",
  },
  [8877] = {
    ["D"] = "Although rockets are a wonderful way to celebrate, in order to see their glory... they must be fired from a launcher.$B$BBring me coins of ancestry and I can offer you the knowledge to create rocket launchers like the ones you see in Moonglade.",
    ["O"] = "",
    ["T"] = "Zünder für Feuerwerk",
  },
  [8878] = {
    ["D"] = "The dresses and pant suits you see are traditional clothes from many years past.  They are worn in honor of old ways and loved ones gone.$B$BIf you bring me coins of ancestry, $N, then I will give you the pattern to create traditional red dresses or suits.",
    ["O"] = "",
    ["T"] = "Festtagsrezepte",
  },
  [8879] = {
    ["D"] = "Large Festival rockets light up the sky brilliantly - only the cluster rockets rival their magnificence!$B$BBring me coins of ancestry, $N, and I shall share the knowledge of their creation.",
    ["O"] = "",
    ["T"] = "Große Raketen",
  },
  [8880] = {
    ["D"] = "Cluster rockets are a collection of individual rockets, and their beauty is awesome!  Bring me coins of ancestry and I will share the knowledge of their creation to you...",
    ["O"] = "",
    ["T"] = "Raketenbündel",
  },
  [8884] = {
    ["D"] = "Seid gegrüßt, $C. Was, frage ich Euch, könnt Ihr erkennen, wenn Ihr in Richtung Küste seht? Richtig, unser Strand... unsere Stille Küste wurde vom Übel, den Murlocs, befallen.$B$BDieser Makel darf nicht bestehen bleiben!$B$BIch habe eine Aufgabe für Euch. Solltet Ihr bereit dazu sein, so würde ich ihre Köpfe haben wollen! Mal sehen, ob wir ihre Zahl derart verringern können, dass sie nicht mehr anders können, als in die dunklen Tiefen zurückzukehren, aus denen sie gekommen sind!",
    ["O"] = "Sammelt 8 Köpfe von Grimmschuppenmurlocs und bringt sie zu Hathvelion Sonnenblick im Immersangwald auf der Klippe, die die Stille Küste überblickt.",
    ["T"] = "Fischköpfe, Fischköpfe...",
  },
  [8885] = {
    ["D"] = "Die Grimmschuppenmurlocs haben sich durch Eure Taten nicht vertreiben lassen. Ich glaube aber zu wissen, was sie endlich in die Flucht schlagen wird, und wie ich mich gleichzeitig persönlich an ihnen rächen kann.$B$BSie haben einen Murlochäuptling, der sich selbst Mmmrrrggglll nennt. Er wandert entlang des Goldenen Strandes südlich der Flussmündung umher. Dies Biest hat einst einen meiner Kameraden getötet und trägt seitdem seinen Ring als offenes Zeichen der Verachtung und des Spotts.$B$BVernichtet Mmmrrrggglll und bringt mir den Ring meines Freundes wieder. Das sollte diese Monster ein für alle Mal einschüchtern!",
    ["O"] = "Erlangt Mmmrrrgggllls Ring von dem Häuptling der Grimmschuppen und kehrt damit anschließend zu Hathvelion Sonnenblick im Immersangwald auf der Klippe, die die Stille Küste überblickt, zurück.",
    ["T"] = "Mmmrrrgggllls Ring",
  },
  [8886] = {
    ["D"] = "Wie gut, dass Ihr hier seid, $C! Diese schleimigen Grimmschuppenmurlocs haben meine Fracht gestohlen! Als wenn es nicht schon schlimm genug wäre, dass die Getriebenen mir meine letzten magischen Güter geraubt haben - und jetzt das! Was ist nur aus diesem Land geworden?$B$BKönnt Ihr mir meine Fracht wiederbeschaffen? Ich erwarte zwar nicht, dass Ihr alles zurückerlangt, bin aber für jedes Stück dankbar. Es waren die Grimmschuppenmurlocs in den Hütten im Westen, südlich des Ankerplatzes am Goldenen Strand. Ich bin mir ganz sicher!",
    ["O"] = "Erlangt 6 Stücke von Kapitänin Kelisendras Fracht wieder und bringt sie zu Kapitänin Kelisendra beim befestigten Lager direkt östlich des Ankerplatzes der Sonnensegel im Immersangwald zurück.",
    ["T"] = "Piraten der Grimmschuppen!",
  },
  [8887] = {
    ["D"] = "Interessant... was Ihr hier gefunden habt, ist eine wasserdichte Lederhülse, die ein paar Seiten Pergamentpapier enthält. Nach den Kennzeichnungen zu urteilen, handelt es sich eindeutig um Seekarten. Jede dieser Karten trägt das Siegel von Kapitänin Kelisendra.$B$BIhr habt sicher schon von einem kleinen, befestigten Lager östlich des nahegelegenen Ankerplatzes der Sonnensegel gehört. Vielleicht findet Ihr die Kapitänin ja dort?",
    ["O"] = "Bringt Kapitänin Kelisendras verlorene Seekarten zu Kapitänin Kelisendra beim befestigten Lager direkt östlich des Ankerplatzes der Sonnensegel im Immersangwald zurück.",
    ["T"] = "Kapitänin Kelisendras verlorene Seekarten",
  },
  [8888] = {
    ["D"] = "$C, hört Euch mein Anliegen an. Auf Grund von Dingen, die ich jetzt nicht weiter erklären kann, habe ich beschlossen, meine nahegelegene Schule zu schließen. Vor ein paar Tagen habe ich eine meiner Schülerinnen, Lehrling Loralthalis, mit diesem Auftrag dorthin geschickt. Seitdem habe ich nichts mehr von ihr gehört.$B$BWerdet Ihr zum Nachtschimmerturm gehen und nachsehen, ob alles mit ihr in Ordnung ist? Ihr werdet den Turm finden, indem Ihr dem Pfad, der von der Zuflucht der Weltenwanderer aus nach Norden führt, folgt und dann bei der Weggabelung rechts abbiegt.",
    ["O"] = "Sprecht mit Lehrling Loralthalis nahe des Nachtschimmerturms im Immersangwald.",
    ["T"] = "Der Lehrling des Magisters",
  },
  [8889] = {
    ["D"] = "Wie Ihr wisst, hat mich Magister Nachtschimmer hierher zurückgeschickt, damit ich die Kraftquellen, die den Turm versorgen, deaktiviere. Es gibt drei davon, sie befinden sich an unterschiedlichen Stellen im Innern des Turms.$B$BUnglücklicherweise scheine ich zu spät zu sein, denn der Nachtschimmerturm wurde von seltsamen, magischen Kreaturen überrannt. Ich habe nicht das benötigte Wissen, um diese Aufgabe zu erfüllen, aber $Gein:eine:c; $C wie Ihr könnte es doch sicherlich ohne größere Probleme?",
    ["O"] = "Deaktiviert 3 Kraftquellen des Nachtschimmerturms im Nachtschimmerturm. Bringt anschließend das Deaktivierungsjuwel zu Lehrling Loralthalis im Immersangwald.",
    ["T"] = "Deaktivierung des Turms",
  },
  [8890] = {
    ["D"] = "$N, ich hätte noch eine letzte Bitte an Euch. Würdet Ihr Magister Nachtschimmer bei der Zuflucht der Weltenwanderer über die Ereignisse berichten, die hier vorgefallen sind? Sagt ihm, dass es mir gut geht, aber betont bitte auch, dass einige Lehrlinge zu Getriebenen geworden sind.$B$BVielen Dank für Eure Hilfe!",
    ["O"] = "Kehrt zu Magister Nachtschimmer bei der Zuflucht der Weltenwanderer im Immersangwald zurück.",
    ["T"] = "Nachricht vom Turm",
  },
  [8891] = {
    ["D"] = "Ihr habt ein Buch mit Untersuchungsberichten gefunden, das offenbar von Magister Nachtschimmer zurückgelassen wurde. Ihr überfliegt rasch den Inhalt und stellt mit Entsetzen fest, dass man versucht hat, verderbte magische Ströme zu filtern, was womöglich eine Erklärung für die Ereignisse hier beim Nachtschimmerturm sein könnte. Der Magister möchte dies sicherlich zurück.",
    ["O"] = "Bringt Magister Nachtschimmers Tagebuch zu Magister Nachtschimmer bei der Zuflucht der Weltenwanderer im Immersangwald zurück.",
    ["T"] = "Aufgegebene Untersuchungen",
  },
  [8892] = {
    ["D"] = "Der Ankerplatz der Sonnensegel wurde von einem Haufen Getriebener überrannt. Sobald wir Verstärkung erhalten, werden wir diesen Verbrechern schon zeigen, wer hier das Sagen hat, $N.$B$BIn der Zwischenzeit könntet Ihr uns helfen, indem Ihr ihnen etwas Disziplin beibringt. Geht zu ihnen und macht deutlich, dass man mit uns keine Spielchen treibt. Der Ankerplatz der Sonnensegel liegt genau westlich von Morgenluft.",
    ["O"] = "Tötet 5 getriebene Rohlinge und 5 getriebene Straßendiebe und erstattet Waldläufer Degolien im Immersangwald Bericht.",
    ["T"] = "Vorfall beim Ankerplatz der Sonnensegel",
  },
  [8894] = {
    ["D"] = "Ich kann Euch sagen, es ist furchtbar! Seltsame Dinge gehen im Turm vor sich, der Magister und seine Lehrlinge sind weggelaufen und wie Ihr wisst, wurde der gesamte Grund von magischen Bestien überrannt! Und wer sagte Wyllithen Bescheid? Niemand, so sieht es aus!$B$B$C, Ihr müsst mir helfen, hier wieder für Ordnung zu sorgen... ich kann nicht länger über den Grund wachen, solange diese \'Anomalien\' hier in blinder Wut herumlaufen!",
    ["O"] = "Tötet 6 Manaschlangen und 6 Ätherbestien. Kehrt anschließend zu Grundbewahrer Wyllithen beim Nachtschimmergrund im Immersangwald zurück.",
    ["T"] = "Sicherung des Grunds",
  },
  [8895] = {
    ["D"] = "Es freut mich, dass Ihr Euch zumindest in gewissen Hinsichten als fähig erweist, $N. Ich gebe Euch eine weitere Aufgabe, da Ihr so begierig scheint, Euch beweisen zu wollen.$B$BDies ist ein Brief für Leybewahrer Caidanis, der Arkanist, der für die Aufrechterhaltung des Sanktums des Nordens verantwortlich ist. Geht vom Falkenplatz Richtung Südwesten und Ihr werdet ihn finden. Er ist eine sehr wichtige Persönlichkeit, ich hoffe also, dass Ihr ihn nicht mit zu belanglosem Geschwätz belästigt.",
    ["O"] = "Überbringt den unbeschrifteten Brief an Leybewahrer Caidanis beim Sanktum des Nordens im Immersangwald.",
    ["T"] = "Lieferung an das Sanktum des Nordens",
  },
  [8896] = {
    ["D"] = "Seid Ihr sicher, dass Ihr diese Notizen bei einer Nachtelfe gefunden habt? Das meiste davon wurde in Zwergisch verfasst...$B$BAusgrabungsleiter Ambossel! Er wurde von Eisenschmiede wegen eines diplomatischen Auftrags hierher geschickt. Ich hätte es wissen müssen, sein Interesse an unseren arkanen Sankten war etwas zu groß. $N, findet den Zwerg im Sanktum des Nordens und kümmert Euch um ihn. Oh, bitte geht diskret dabei vor.",
    ["O"] = "Findet und tötet Ausgrabungsleiter Ambossel im Sanktum des Nordens und berichtet anschließend Aeldon Sonnenmal im Immersangwald.",
    ["T"] = "Der Zwergenspion",
  },
  [8900] = {
    ["D"] = "Sie war so traurig. Ich habe es genau gespürt, trotz Ihrer Versuche es zu verbergen. Ich meine, sie wirkte so traurig, versteht Ihr?$B$BDa war diese Verbindung zwischen uns beiden, als ich mit ihr sprach. Niemand hatte mich bisher verstanden, doch sie tat es auf Anhieb.$B$BAls ich es den anderen erzählte, lachten sie nur über mich und so habe ich nie wieder mit ihr gesprochen. Doch ich vermisse sie. Würdet Ihr mir einen Gefallen tun und ihr diesen Brief von mir geben? Ich will sicher sein, dass sie ihn auch erhält.",
    ["O"] = "Bringt die leicht zerknitterte Notiz zu Elenia Haydon nahe der Bank in Unterstadt.",
    ["T"] = "Liebste Elenia",
  },
  [8901] = {
    ["D"] = "Habt Ihr jemals wahre Liebe erfahren, $G junger Herr:junge Dame;? Noch vor Kurzem war dies ein Fremdwort für mich, doch dann traf ich dieses junge Mädchen aus Unterstadt, Elenia. Es ist selten für einen Angehörigen meiner Art, sich mit den anderen Völker einzulassen, ganz besonders mit den Verlassenen.$B$BDoch jetzt sind meine Gedanken Tag und Nacht von ihr erfüllt.$B$BWäre es Euch vielleicht möglich, ihr diesen Brief zu überbringen? Ich möchte, dass sie hierher nach Mulgore kommt. Hoffentlich sagt sie ja.",
    ["O"] = "Bringt den sorgfältig verfassten Brief zu Elenia Haydon nahe der Bank in Unterstadt.",
    ["T"] = "Liebste Elenia",
  },
  [8902] = {
    ["D"] = "Zu Lebzeiten hatte ich geschworen, Elenia, meine Jugendliebe, zu meiner Frau zu nehmen. Es ist schon Jahre her, seit ich das letzte Mal an sie denken musste. Doch seit einiger Zeit beherrschen mehr und mehr Gedanken an sie meinen Verstand.$B$BObwohl es eine Ewigkeit her ist, sollte es in unser beider Leben noch etwas Raum für unsere Liebe geben.$B$BIch bin sicher, auch sie wird sich verändert haben. Sei es drum! Wäre es Euch vielleicht möglich, ihr diesen Brief zu überbringen? Ich selbst bin zu nervös, um ihr selbst gegenüberzutreten.",
    ["O"] = "Bringt den liebevoll verfassten Brief zu Elenia Haydon nahe der Bank in Unterstadt.",
    ["T"] = "Liebste Elenia",
  },
  [8903] = {
    ["D"] = "Besorgt die Karte einer Wache von Sturmwind und bringt sie zu Aristan Mottar in Sturmwind.",
    ["O"] = "Besorgt die Karte einer Wache von Sturmwind und bringt sie zu Aristan Mottar in Sturmwind.",
    ["T"] = "Gefährliche Liebschaft",
  },
  [8904] = {
    ["D"] = "Diese Liebesepidemie hat etwas Unnatürliches an sich. Sie ist abscheulich und hat sie erst einmal unsere Verteidigung geschwächt, könnte dies zu einer ernsten Bedrohung für uns alle werden.$B$BSchlimm genug, dass bereits so viele unserer Leute von diesem lächerlichen Verhalten befallen sind. Ich denke, selbst unsere Wächter hat es bereits erwischt, dabei sollten sie gegen derartige Einflüsse immun sein.$B$BGeht zu einem der Wächter und seht, ob er von dieser Dummheit befallen wurde.",
    ["O"] = "Besorgt eine modrige Karte des Wächters und bringt sie zu Fenstad Argyle in Unterstadt.",
    ["T"] = "Gefährliche Liebschaft",
