pfDB["quests"]["enUS"] = {
  [2] = {
    ["T"] = "Sharptalon\'s Claw",
    ["O"] = "Bring Sharptalon\'s Claw to Senani Thunderheart at Splintertree Post, Ashenvale.",
    ["D"] = "The mighty hippogryph Sharptalon has been slain, with the claw of the felled beast serving as a testament to your victory.  Senani Thunderheart at the Splintertree Post will no doubt be interested in seeing this trophy as proof of your deeds.",
  },
  [5] = {
    ["T"] = "Jitters\' Growling Gut",
    ["O"] = "Speak with Chef Grual.",
    ["D"] = "I\'ve been stuck hiding in this ghost town for weeks, and I haven\'t eaten anything but grubs and weeds!  I need some decent food, and I\'m willing to trade well for it.$B$BBring me a feast and I\'ll pay you handsomely.$B$BI heard Chef Grual at the Scarlet Raven Tavern in Darkshire makes very good Dusky Crab Cakes...",
  },
  [6] = {
    ["T"] = "Bounty on Garrick Padfoot",
    ["O"] = "Kill Garrick Padfoot and bring his head to Deputy Willem at Northshire Abbey.",
    ["D"] = "Garrick Padfoot - a cutthroat who\'s plagued our farmers and merchants for weeks - was seen at a shack near the vineyards, which lies east of the Abbey and across the bridge.  Bring me the villain\'s head, and earn his bounty!$B$BBut be wary, $N.  Garrick has gathered a gang of thugs around him.  He will not be an easy man to reach.",
  },
  [7] = {
    ["T"] = "Kobold Camp Cleanup",
    ["O"] = "Kill 10 Kobold Vermin, then return to Marshal McBride.",
    ["D"] = "Your first task is one of cleansing, $N.  A clan of kobolds have infested the woods to the north.  Go there and fight the kobold vermin you find.  Reduce their numbers so that we may one day drive them from Northshire.",
  },
  [8] = {
    ["T"] = "A Rogue\'s Deal",
    ["O"] = "Deliver the Nondescript Letter to Innkeeper Renee in Tirisfal Glades.",
    ["D"] = "Hey, mate! Do a favor for a young man who\'s been fighting more than his fair share of mindless zombies and spiders?$B$BI got this here letter that needs to go to Brill... to an innkeeper named Renee something-or-other. Don\'t really matter none what her last name is.$B$BAnyway, it\'s a nice cozy little place full of victims of the plague trying to make their way in the world. And it be a great spot for you to rest too if the need arises. You should check it out... you do and I\'ll pay you well.",
  },
  [9] = {
    ["T"] = "The Killing Fields",
    ["O"] = "Farmer Saldean wants you to kill 20 Harvest Watchers.",
    ["D"] = "Look at what has happened to this place!  These lands were once occupied by good farm folk.  But the damned thieves have driven them all off.  Not me, though!  But it seems some Harvest Watchers have taken over the fields.$b$bIf you\'re up for the work, I\'d like you to go out and kill twenty of them.  Come back when you\'re done for your pay.  If you finish up with the ones in my field, clear them from the neighboring fields as well.",
  },
  [10] = {
    ["T"] = "The Scrimshank Redemption",
    ["O"] = "Discover the fate of Junior Surveyor Scrimshank, and bring either him or his surveying equipment to Senior Surveyor Fizzledowser in Gadgetzan.",
    ["D"] = "Oh, this is just terrible!  I sent out my top assistant, Junior Surveyor Scrimshank, into the desert southeast of here.  That\'s an area we call the Gaping Chasm.  Well, he\'s gone missing, and his last report had more of those weird bugs where he was.  Could it be that they\'ve infested two major regions of the desert now?$B$BI need you to head into the Gaping Chasm and look for Scrimshank, please!  That... or at least find his surveying equipment.  I fear that may be all that is left of the poor sod.",
  },
  [11] = {
    ["T"] = "Riverpaw Gnoll Bounty",
    ["O"] = "Bring 8 Painted Gnoll Armbands to Deputy Rainer at the Barracks.",
    ["D"] = "Gnolls, brutish creatures with no decent business in these lands, have been seen along the borders of Elwynn Forest.  A large pack of them, many more than we can handle alone, have infested the woods south of the guard tower yonder.  Another group has infested the areas near Stone Cairn Lake to the east.$B$BThe Stormwind Army will commend whomever helps kill them.  Bring me their painted gnoll armbands as proof of your deed.",
  },
  [12] = {
    ["T"] = "The People\'s Militia",
    ["O"] = "Gryan Stoutmantle wants you to kill 15 Defias Trappers and 15 Defias Smugglers then return to him on Sentinel Hill.",
    ["D"] = "The People\'s Militia has but one goal:  To defend the lands of Westfall and return peace to our surroundings.  Unfortunately, the price of peace is often blood. $b$bOne of my scouts has brought word of a band of Defias Trappers wreaking havoc nearby.  I have reports of Defias Trapper sightings near the Jangolode Mine to the Northwest as well as at the Molsen Farm and Furlbrow\'s Pumpkin Farm. If you seek to join our ranks, slay 15 Defias Trappers and 15 Defias Smugglers then return to me.",
  },
  [13] = {
    ["T"] = "The People\'s Militia",
    ["O"] = "Gryan Stoutmantle wants you to kill 15 Defias Pillagers and 15 Defias Looters and return to him on Sentinel Hill.",
    ["D"] = "A band of vicious Defias Pillagers has been seen plundering the Gold Coast Quarry, Moonbrook and the Alexston Farmstead.  The People\'s Militia will not stand for such behavior.  Dispatch immediately, $n, and make the Light\'s presence known in Westfall.$b$bThe Gold Coast Quarry is near the shore, to the West of the tower.  As the next step of your training, I want you to kill 15 of those foul Defias Pillagers and 15 Defias Looters.",
  },
  [14] = {
    ["T"] = "The People\'s Militia",
    ["O"] = "Gryan Stoutmantle wants you to kill 15 Defias Highwaymen, 5 Defias Pathstalkers and 5 Defias Knuckledusters then return to him on Sentinel Hill.",
    ["D"] = "Some Defias have eluded us.  My most trusted scout reports that these Defias have been looting and pillaging the countryside, all the way into Southern Westfall.  We believe they are hiding out in the Dagger Hills, plotting their next move.   Slay the wretches in the name of The People\'s Militia.",
  },
  [15] = {
    ["T"] = "Investigate Echo Ridge",
    ["O"] = "Kill 10 Kobold Workers, then report back to Marshal McBride.",
    ["D"] = "$N, my scouts tell me that the kobold infestation is larger than we had thought.  A group of kobold workers has camped near the Echo Ridge Mine to the north.$B$BGo to the mine and remove them.  We know there are at least 10.  Kill them, see if there are more, then report back to me.",
  },
  [16] = {
    ["T"] = "Give Gerard a Drink",
    ["O"] = "",
    ["D"] = "",
  },
  [17] = {
    ["T"] = "Uldaman Reagent Run",
    ["O"] = "Bring 12 Magenta Fungus Caps to Ghak Healtouch in Thelsamar.",
    ["D"] = "I\'ve heard about a newly discovered plant called the magenta fungus caps.  They grow in clusters at the Uldaman dig site and can be found in the early tunnels and deep within the less-explored areas.  I want to study their potential use in alchemy, and that\'s where you come in.$B$BFind the magenta cap clusters and bring me a dozen caps.  Be warned, the clusters may spew out poison spores if jostled.$B$BDo this for me, and I\'ll whip up a batch of one of my famous restorative elixirs for you!",
  },
  [18] = {
    ["T"] = "Brotherhood of Thieves",
    ["O"] = "Bring 12 Red Burlap Bandanas to Deputy Willem outside the Northshire Abbey.",
    ["D"] = "Recently, a new group of thieves has been hanging around Northshire.  They call themselves the Defias Brotherhood, and have been seen across the river to the east.$B$BI don\'t know what they\'re up to, but I\'m sure it\'s not good!  Bring me the bandanas they wear, and I\'ll reward you with a weapon.",
  },
  [19] = {
    ["T"] = "Tharil\'zun",
    ["O"] = "Bring Tharil\'zun\'s Head to Marshal Marris in Redridge.",
    ["D"] = "Blackrock Outrunners and Renegades are running ambushes between here and Stonewatch Keep.  The leader of the Outrunners is an orc named Tharil\'zun--we want this orc.  Bring me the head of Tharil\'zun!",
  },
  [20] = {
    ["T"] = "Blackrock Menace",
    ["O"] = "Bring 10 Battleworn Axes to Marshal Marris in Lakeshire.",
    ["D"] = "The Blackrock orcs have travelled from the Burning Steppes and claimed Stonewatch Keep to the north.  From there they maraud the areas north of Lakeshire and keep its citizens in town, penned like cattle.$B$BHelp rid Lakeshire of this menace.  Kill these Blackrock orcs and bring me ten of their Battleworn Axes as proof.$B$BAnd although Blackrocks threaten us from the north, we\'ve received reports of more in the southeastern pocket of Redridge.  If you find any there, then they too should be hunted.",
  },
  [21] = {
    ["T"] = "Skirmish at Echo Ridge",
    ["O"] = "Kill 12 Kobold Laborers, then return to Marshal McBride at Northshire Abbey.",
    ["D"] = "Your previous investigations are proof that the Echo Ridge Mine needs purging.  Return to the mine and help clear it of kobolds.$B$BWaste no time, $N.  The longer the kobolds are left unmolested in the mine, the deeper a foothold they gain in Northshire.",
  },
  [22] = {
    ["T"] = "Goretusk Liver Pie",
    ["O"] = "Salma Saldean needs 8 Goretusk livers to make a Goretusk Liver Pie.",
    ["D"] = "The onions are peeled.  The garlic is minced.  The rosemary is crushed.  The crust has been baked.  The dill weed is chopped.  The gravy is simmering.  Now all I need for my famous meat pie are 8 Goretusk Livers!",
  },
  [23] = {
    ["T"] = "Ursangous\'s Paw",
    ["O"] = "Bring Ursangous\'s Paw to Senani Thunderheart at Splintertree Post, Ashenvale.",
    ["D"] = "The paw of the mighty bear Ursangous is your trophy for this part of the Ashenvale Hunt.  On the paw is a small quarter-crescent emblem; the significance of the emblem remains a mystery.$B$BThe paw should be brought to Senani Thunderheart in Splintertree Post to prove that you have bested the mighty creature and completed this portion of the hunt.",
  },
  [24] = {
    ["T"] = "Shadumbra\'s Head",
    ["O"] = "Bring Shadumbra\'s Head to Senani Thunderheart at Splintertree Post, Ashenvale.",
    ["D"] = "The once swift Shadumbra now lies still as you claim victory over it.  The head of the slain nightsaber is a fitting trophy for this fight of the Ashenvale Hunt.  Senani Thunderheart at Splintertree Post will want to see such a trophy to award you credit for this part of the Ashenvale Hunt.",
  },
  [25] = {
    ["T"] = "Stonetalon Standstill",
    ["O"] = "Dispatch 12 Befouled Water Elementals at Mystral Lake, due east of the Talondeep Path and southwest of Splintertree Post.$B$BScout the gazebo on Mystral Lake that overlooks the nearby Alliance outpost.$B$BReturn to Mastok Wrilehiss at Splintertree Post, Ashenvale.",
    ["D"] = "If you want to be useful, then why don\'t you do something about the situation near Talondeep Path?$B$BThe transports between Ashenvale and Stonetalon are at a standstill because of the lake coming alive and attacking anything that gets close... which means anyone on the path.  What\'d be even more useful than just putting the hammer down on the water elementals there is someone scouting the gazebo on Mystral Lake - the one that has a solid view of the Alliance outpost there.$B$BYou up for being useful?",
  },
  [26] = {
    ["T"] = "A Lesson to Learn",
    ["O"] = "Speak with Dendrite Starblaze in the village of Nighthaven, Moonglade.",
    ["D"] = "$N, I have been following your progress with much delight.  You are most assuredly developing yourself into a valued protector of nature.$B$BThe time has come for you to learn more about one of the various animal aspects that Cenarius has blessed us with the ability to change into - one that affords you command and mastery of the water.  For this, you must travel to the village of Nighthaven in Moonglade and speak with Dendrite Starblaze of the Cenarion Circle there.  He will guide you further.",
  },
  [27] = {
    ["T"] = "A Lesson to Learn",
    ["O"] = "Speak with Dendrite Starblaze in the village of Nighthaven, Moonglade.",
    ["D"] = "$N, I have been following your progress with much delight.  You are most assuredly developing yourself into a valued protector of nature.$B$BThe time has come for you to learn more about one of the various animal aspects that Cenarius has blessed us with the ability to change into - one that affords you command and mastery of the water.  For this, you must travel to the village of Nighthaven in Moonglade and speak with Dendrite Starblaze of the Cenarion Circle there.  He will guide you further.",
  },
  [28] = {
    ["T"] = "Trial of the Lake",
    ["O"] = "Find a Shrine Bauble in Lake Elune\'ara, and take it to the Shrine of Remulos in northwestern Moonglade.  Once there, use the Shrine Bauble.$B$BYou must speak with Tajarri at the shrine afterwards in order to complete the trial.",
    ["D"] = "For your first trial, search the depths of Lake Elune\'ara to locate a Shrine Bauble.  It decays rapidly, so proceed with it to the Shrine of Remulos post haste.  Use the bauble at the shrine, and then speak with Tajarri there; she is one of Moonglade\'s most trusted wardens.$B$BYou only have a set amount of time to complete the task, and you may be competing against other druids searching for baubles themselves.  Mental and physical dexterity will be critical to your success.$B$BGood luck, $N.",
  },
  [29] = {
    ["T"] = "Trial of the Lake",
    ["O"] = "Find a Shrine Bauble in Lake Elune\'ara, and take it to the Shrine of Remulos in northwestern Moonglade.  Once there, use the Shrine Bauble.$B$BYou must speak with Tajarri at the shrine afterwards in order to complete the trial.",
    ["D"] = "For your first trial, search the depths of Lake Elune\'ara to locate a Shrine Bauble.  It decays rapidly, so proceed with it to the Shrine of Remulos post haste.  Use the bauble at the shrine, and then speak with Tajarri there; she is one of Moonglade\'s most trusted wardens.$B$BYou only have a set amount of time to complete the task, and you may be competing against other druids searching for baubles themselves.  Mental and physical dexterity will be critical to your success.$B$BGood luck, $N.",
  },
  [30] = {
    ["T"] = "Trial of the Sea Lion",
    ["O"] = "Find the Half Pendant of Aquatic Agility and the Half Pendant of Aquatic Endurance.  Speak with the residents of Moonglade to learn clues as to where these items may be located.$B$BForm the Pendant of the Sea Lion from the two pendant halves.  You need to be in proximity of the Shrine of Remulos to do this.$B$BBring the joined pendant to Dendrite Starblaze in the village of Nighthaven, Moonglade.",
    ["D"] = "For the second trial, you\'ll need the two halves that make the Pendant of the Sea Lion.  One half draws power from the agility of the sea lion aspect, gliding through water; the other draws power from the incredible endurance of the sea lion aspect.  Druids draw on both these traits equally to embody the aspect of their aquatic form.$B$BSpeak with the locals of Moonglade to learn where the parts may lie, and bring both here to join them together.  Once formed, take the pendant to Dendrite Starblaze.",
  },
  [31] = {
    ["T"] = "Aquatic Form",
    ["O"] = "Return to Thunder Bluff and show Turak Runetotem the Pendant of the Sea Lion.",
    ["D"] = "You have completed the necessary lessons all young druids are taught before they can adopt an aquatic aspect.  Go now back to your trainer, Turak Runetotem, in Thunder Bluff.  Show him your pendant and prove to him you are ready to learn what he has to teach you.  He will complete your training, allowing you to become one with the water.$B$BGoodbye, young druid.  We shall speak again.",
  },
  [32] = {
    ["T"] = "Rise of the Silithid",
    ["O"] = "Take the report to Zilzibin Drumlore in Orgrimmar.",
    ["D"] = "Here\'s your report.  I urge you to take it to someone who might have working knowledge in this kind of thing.$B$BI know this troll scholar named Zilzibin Drumlore.  He\'s spent a lot of time studying life forms that are unique to the ecology of Azeroth; he\'s a historian to boot!  If you want someone to help figure out what exactly this threat is and how to stop it, give this report to him.  We\'re going to need all the help we can get.$B$BZil lives in a house on the second tier of the Drag in Orgrimmar.",
  },
  [33] = {
    ["T"] = "Wolves Across the Border",
    ["O"] = "Bring 8 pieces of Tough Wolf Meat to Eagan Peltskinner outside Northshire Abbey.",
    ["D"] = "I hate those nasty timber wolves!  But I sure like eating wolf steaks...  Bring me tough wolf meat and I will exchange it for something you\'ll find useful.$B$BTough wolf meat is gathered from hunting the timber wolves and young wolves wandering the Northshire countryside.",
  },
  [34] = {
    ["T"] = "An Unwelcome Guest",
    ["O"] = "Martie Jainrose of Lakeshire wants you to kill Bellygrub.  Bring her his tusk as proof.",
    ["D"] = "Once again my garden was trampled and pillaged.  And I know the brute who is doing it.  It\'s that forsaken boar, the one Marshal Marris has taken to calling Bellygrub.$b$bI won\'t be able to re-plant the daffodils until next season now.  Ruined!  All ruined by that one menacing boar.  Put an end to that pest!  Show me his tusk and I\'d be happy to reward you.  He seems to spend his time foraging in the field southwest of Lakeshire but occasionally he roams over here and into my garden.",
  },
  [35] = {
    ["T"] = "Further Concerns",
    ["O"] = "Marshal Dughan wants you to speak with Guard Thomas.",
    ["D"] = "If you are concerned that the rumors of murlocs are true, then do this -- travel to the eastern Elwynn bridge and speak with Guard Thomas.  He has been stationed at the bridge for the past week and will know the state of the area.$B$BBring me his report.",
  },
  [36] = {
    ["T"] = "Westfall Stew",
    ["O"] = "Verna Furlbrow wants you to deliver her recipe for Westfall Stew to Salma Saldean.",
    ["D"] = "I never thought the day would come when I\'d leave the farm.  But the fields are overrun with thieves and it\'s far too dangerous for us here now.  As soon as Farmer Furlbrow gets the wagon fixed, we\'ll be on our way.  $b $bMaybe you could do me a favor?  Let me scribble down my recipe for Westfall stew.  Please take it to Salma Saldean over on the farm yonder.  The Saldean\'s farm is just beyond the fork in the road.",
  },
  [37] = {
    ["T"] = "Find the Lost Guards",
    ["O"] = "Guard Thomas wants you to travel north up the river and search for the two lost guards, Rolf and Malakai.",
    ["D"] = "A few days ago we sent two Guards, Rolf and Malakai, to investigate along the river, and they have not yet returned.  To complete my report, I must know what happened to those men.$B$BTravel north along the river and find the guards... or their remains.",
  },
  [38] = {
    ["T"] = "Westfall Stew",
    ["O"] = "Salma Saldean wants 3 Stringy Vulture Meat, 3 Goretusk Snouts, 3 Murloc Eyes, 3 Okra.",
    ["D"] = "Help me make some Westfall Stew!  Come back with the following ingredients: $b $b 3 Stringy Vulture Meat $b 3 Goretusk Snouts $b 3 Murloc Eyes $b 3 Okra",
  },
  [39] = {
    ["T"] = "Deliver Thomas\' Report",
    ["O"] = "Report to Marshal Dughan in Goldshire.",
    ["D"] = "Tell Marshal Dughan of Malakai and Rolf\'s deaths, and report to him that the Murlocs in eastern Elwynn cannot be contained by our current troop presence.$B$BI know we don\'t have many troops to spare, but hopefully Dughan can find someone.",
  },
  [40] = {
    ["T"] = "A Fishy Peril",
    ["O"] = "Remy \"Two Times\" wants you to speak with Marshal Dughan in Goldshire.",
    ["D"] = "$N, there\'s a new threat in Elwynn Forest!  Murlocs are swimming up the streams of eastern Elwynn, scaring away fish and attacking gentle folk!$B$BI warned Marshal Dughan, but he\'s more worried about the gnolls and the bandits.  He\'s not convinced that the murlocs are a danger.$B$BPlease, $N, speak to Dughan and persuade him to send more troops to the east!",
  },
  [45] = {
    ["T"] = "Discover Rolf\'s Fate",
    ["O"] = "Search the murloc village for Rolf, or signs of his death.",
    ["D"] = "Upon further searching the area, you find webbed footprints leading east along the shore of Stone Cairn Lake.  In the distance to the east, you can just barely see a Murloc village.$B$BPerhaps Rolf\'s fate ended there...",
  },
  [46] = {
    ["T"] = "Bounty on Murlocs",
    ["O"] = "Bring 8 Torn Murloc Fins to Guard Thomas at the east Elwynn bridge.",
    ["D"] = "The Stormwind Army has placed a bounty on murloc lurkers and foragers in Elwynn.  Slaughter them and bring me their torn murloc fins, and the Stormwind Army will reward you well.$B$BThe murlocs have built a village at Stone Cairn Lake north of here, and another to the south where the stream forks.",
  },
  [47] = {
    ["T"] = "Gold Dust Exchange",
    ["O"] = "Bring 10 Gold Dust to Remy \"Two Times\" in Goldshire.  Gold Dust is gathered from Kobolds in Elwynn Forest.",
    ["D"] = "The Kobolds in these parts sometimes carry Gold Dust on them.  I could really use the stuff - bring me a load of it and I\'ll give you the best price in town...best price in town!$B$BYou can find kobolds in the Fargodeep Mine to the south, and around the Jasperlode Mine to the northeast.",
  },
  [48] = {
    ["T"] = "Sweet Amber",
    ["O"] = "Bring Holy Spring Water to Grimbooze Thunderbrew.",
    ["D"] = "In the land to the south where vines twist and creep$bLies a hidden well where the water runs deep$bPure as the Light\'s sacred Daughter$bBring to me now some Holy Spring Water.",
  },
  [49] = {
    ["T"] = "Sweet Amber",
    ["O"] = "Grimbooze Thunderbrew wants a Sack of Barley, a Sack of Rye and a Sack of Corn.",
    ["D"] = "Amber is the hue of my life\'s longest love$bLike the last embers of dusk in the sky above$bRetrieve for me so that my love shall be born$bOne sack of each: barley, rye and corn.",
  },
  [50] = {
    ["T"] = "Sweet Amber",
    ["O"] = "Grimbooze Thunderbrew wants Truesilver.",
    ["D"] = "I\'ll mix and mix till we have a mash$bFor this will be our own private stash$bA still must be fashioned of metal strong$bBring me some Truesilver so I can finish this song.",
  },
  [51] = {
    ["T"] = "Sweet Amber",
    ["O"] = "Bring a Sycamore Branch to Grimbooze Thunderbrew.",
    ["D"] = "Not yet filtered but freshly distilled$bLike unseeded land waiting there tilled.$bNext I will fashion our pile of charcoal$bDeliver a sycamore branch, that is your next goal.",
  },
  [52] = {
    ["T"] = "Protect the Frontier",
    ["O"] = "Kill 8 Prowlers and 5 Young Forest Bears, and then return to Guard Thomas at the east Elwynn bridge.",
    ["D"] = "Hail, $N.  Wild animals are growing more and more aggressive the farther we get from Goldshire, and the Eastvale Logging Camp suffers nearly constant attacks from wolves and bears!$B$BWe could use your help out here.",
  },
  [53] = {
    ["T"] = "Sweet Amber",
    ["O"] = "Bring Grimbooze Thunderbrew a bundle of Charred Oak.",
    ["D"] = "Mellifluous liquid clear as a baby\'s tears$bTurns a lovely deep amber over the years$bBut before we proceed to get lit to the hilt$bA barrel of charred oak must be built.",
  },
  [54] = {
    ["T"] = "Report to Goldshire",
    ["O"] = "Take Marshal McBride\'s Documents to Marshal Dughan in Goldshire.",
    ["D"] = "$N, you are a $c with proven interest in the security of Northshire.  You are now tasked with the protection of the surrounding Elwynn Forest.$B$BIf you accept this duty, then I have prepared papers that must be delivered to Marshal Dughan in Goldshire.  Goldshire lies along the southern road, past the border gates.",
  },
  [55] = {
    ["T"] = "Morbent Fel",
    ["O"] = "Use Morbent\'s Bane on Morbent Fel, kill Morbent Fel, then return Morbent\'s Bane to Sven at his camp.",
    ["D"] = "Morbent Fel hides in his lair, the house perched atop the hill to the east overlooking the Raven Hill cemetery.  His time in this land is drawing to an end...$B$BUse Morbent\'s Bane against him.  It will render his protective magics powerless.$B$BSlay him.  Slay him, and save us from his wickedness.  Be the instrument of my revenge, and a hero of Duskwood!",
  },
  [56] = {
    ["T"] = "The Night Watch",
    ["O"] = "Commander Althea Ebonlocke of Darkshire wants you to kill 8 Skeletal Warriors and 6 Skeletal Mages.",
    ["D"] = "As Commander of The Night Watch it is my sworn duty to protect the citizens of Darkshire.  Stormwind has retracted their commissioned guards from their posts here, leaving us to our own devices.$b$bIf you feel worthy of the task, we need your help! Assist The Night Watch by venturing forth into Tranquil Gardens, just south of Darkshire, and slaying 8 Skeletal Warriors and 6 Skeletal Mages.  The undead threat must be thwarted!",
  },
  [57] = {
    ["T"] = "The Night Watch",
    ["O"] = "Commander Althea Ebonlocke of Darkshire wants you to kill 15 Skeletal Fiends and 15 Skeletal Horrors.",
    ["D"] = "$N, I\'m getting reports of an undead infestation in the forest!  If you\'re up to the job, journey to the Raven Hill graveyard and slay 15 of those vile Skeletal Fiends and 15 Skeletal Horrors.  There are also reports of skeletons just south of Darkshire, in and around Tranquil Gardens Cemetery.  The undead must be driven from Duskwood!",
  },
  [58] = {
    ["T"] = "The Night Watch",
    ["O"] = "Commander Althea Ebonlocke of Darkshire wants you to kill 20 Plague Spreaders in the eastern Raven Hill mausoleum.",
    ["D"] = "The situation in Raven Hill is still grim.  The safety of Darkshire is in your hands, $N.  I don\'t have enough Watchers to keep the town safe.  We need you to dispatch for Raven Hill yet again and rid the eastern mausoleum of 20 Plague Spreaders!",
  },
  [59] = {
    ["T"] = "Cloth and Leather Armor",
    ["O"] = "Give Sara Timberlain the Stormwind Armor Marker.",
    ["D"] = "For your shrewdness and valor, I have a marker here that is good for one piece of armor.  I want you to take it to Sara Timberlain at the Eastvale Logging Camp.  Give her the marker, and she will fashion the armor for you.  And after you receive it, $N, use it in the defense of Elwynn.$B$BThe Eastvale Logging Camp is beyond Guard Thomas\' post to the east.",
  },
  [60] = {
    ["T"] = "Kobold Candles",
    ["O"] = "Bring 8 Large Candles to William Pestle in Goldshire.",
    ["D"] = "Hello, $ggood sir:my lady;!  Do you have a moment?$B$BMy brother and I run an apothecary in Stormwind, and I\'m here to gather large candles for their wax.  Can you help me?$B$BYou can get large candles from kobolds, and I hear rumors that kobolds are infesting the Elwynn mines ... the Fargodeep mine to the south and Jasperlode Mine to the east.  I suggest looking for candles in one of those places.",
  },
  [61] = {
    ["T"] = "Shipment to Stormwind",
    ["O"] = "Bring William\'s Shipment to Morgan Pestle in the Stormwind Trade District.",
    ["D"] = "My brother Morgan is waiting in Stormwind for my shipment of candles.  I don\'t have enough time to make the trip myself, but if you\'d like to take him the shipment, he\'ll pay you well.$B$BI\'ve packed up the candles, and you can find Morgan in our shop, Pestle\'s Apothecary, in the Stormwind Trade District.",
  },
  [62] = {
    ["T"] = "The Fargodeep Mine",
    ["O"] = "Explore the Fargodeep Mine, then return to Marshal Dughan in Goldshire.",
    ["D"] = "The mine in Northshire isn\'t the only one with problems!  I have reports that the Fargodeep Mine in Elwynn has also become a haven for Kobolds.$B$BExplore the mine and confirm these reports, then return to me.  The mine is almost due south of Goldshire, between the Stonefield and Maclure homesteads.",
  },
  [63] = {
    ["T"] = "Call of Water (Part 6)",
    ["O"] = "Defeat the Corrupt Manifestation of Water and place the Corrupted Manifestation\'s Bracers along with the Remaining Drops of Purest Water on the Brazier of Everfount in Silverpine Forest.",
    ["D"] = "Along the coast of Silverpine, west of the Sepulcher, you will find a shrine that has become tainted by the plague. The spirits there no longer speak to our people and it will be up to you to defeat the manifestation and cleanse the pool.$B$BTake the elemental\'s bracers, along with the remaining drops of Brine\'s potion, and place them on the brazier. The ritual should be enough to summon a pure spirit for you to communicate with.$B$BBring me proof the task is done, and I will reward you with your totem.",
  },
  [64] = {
    ["T"] = "The Forgotten Heirloom",
    ["O"] = "Farmer Furlbrow wants you to retrieve his pocket watch from the wardrobe in his farmhouse at the pumpkin farm to the West.",
    ["D"] = "It was horrible!  Verna woke me when she heard a ruckus in the fields.  The fields were full of hooligans.  We left in a rush and I forgot to pack my pocket watch.  Verna\'s pa gave me that watch on our wedding day and I feel just sick knowing those thieves have it.  I left the pocket watch in the wardrobe at the farmhouse.  Look for the field of pumpkins to the West -- you can\'t miss it.  If you bring it back to me, I\'d sure be grateful!",
  },
  [65] = {
    ["T"] = "The Defias Brotherhood",
    ["O"] = "Gryan Stoutmantle wants you to talk to Wiley in Lakeshire.",
    ["D"] = "The band of wretches responsible for driving the good people of Westfall from the land call themselves The Defias Brotherhood.  I need you to infiltrate this clan of thugs.  We need to know who heads up the cartel and where they are hiding out.  In Lakeshire, in the inn, there is a rogue by the name of Wiley who owes me a favor.$b$bTravel to Lakeshire in the Redridge Mountains, east of Elwynn, and find out what you can.",
  },
  [66] = {
    ["T"] = "The Legend of Stalvan",
    ["O"] = "Seek out the Clerk Daltry in the Darkshire Town Hall.",
    ["D"] = "Last night a horrible disturbance rippled through my veins.  I sensed that my granddaughter, Alyssa, was in great danger.  I consulted the cards and Death stared up at me from the table.$b$bAfter taking a long journey through a dark trance I was able to uncover a clue to this terrifying mystery.  A name came to me, the name of Stalvan.$b$bSeek out the Clerk in the Town Hall and see if you can find out more about this character.  I fear for us all.",
  },
  [67] = {
    ["T"] = "The Legend of Stalvan",
    ["O"] = "Travel to the Moonbrook Schoolhouse and bring back any updated information about Stalvan to Clerk Daltry.",
    ["D"] = "Stalvan, eh?  Let me check the town registry.$b$bStalvan. . .Stalvan. . .let\'s see.  Ah, here we go!  I have a record of a Mr. Stalvan Mistmantle.  The last recorded address is the Moonbrook Schoolhouse.  My, talk about outdated!$b$bDo me a favor will you, friend?  If you happen to go out to Moonbrook, let me know if you get any update on this fellow.  I like to keep the records clean.",
  },
  [68] = {
    ["T"] = "The Legend of Stalvan",
    ["O"] = "Take the Dusty Unsent Letter to Clerk Daltry.",
    ["D"] = "You find a Dusty Unsent Letter in the old footlocker.",
  },
  [69] = {
    ["T"] = "The Legend of Stalvan",
    ["O"] = "Go to The Lion\'s Pride Inn to see if anyone there has heard of the name Stalvan.",
    ["D"] = "Oh my!  Must have missed this the first time.  In the registry, right beneath the first address for Stalvan there\'s another one listed, only partially scratched out.  Looks like he was headed to The Lion\'s Pride Inn over in Goldshire.  Might want to check there, $N.",
  },
  [70] = {
    ["T"] = "The Legend of Stalvan",
    ["O"] = "Retrieve Stalvan\'s Undelivered Letter from the chest and deliver it to the Canal District in Stormwind.",
    ["D"] = "The name Stalvan rings a bell.  I remember now.$b$bMany years back, on a stormy night, a messenger came in, seeking refuge for the night.  Near the stroke of midnight, the man ran down the stairs screaming, his face pale with fear.  Still wearing his bedclothes, he disappeared into the downpour.$b$bIn his haste he forgot his letters in the chest upstairs.  He never returned for them. One remains from that Stalvan fellow, intended for the Canal District in Stormwind.  Help yourself to it.",
  },
  [71] = {
    ["T"] = "Report to Thomas",
    ["O"] = "Deliver Rolf and Malakai\'s Medallions to Guard Thomas at the eastern Elwynn bridge.",
    ["D"] = "Now that you have both medallions, deliver them to Guard Thomas at the bridge so that he might know the fate of his murdered guards.",
  },
  [72] = {
    ["T"] = "The Legend of Stalvan",
    ["O"] = "Search through the packed up possessions for a clue.",
    ["D"] = "My father was the caretaker of the estate long before I was.  He had to mop the blood up after the massacre.  But that\'s neither here nor there.$b$bThe last funds of the Flintridge trust have dried up.  Now the last of the family possessions are headed for auction.  Blame the tax vultures.  I guess if you\'re really itching to learn more you\'re free to look through this junk.  Who knows what you might find.",
  },
  [74] = {
    ["T"] = "The Legend of Stalvan",
    ["O"] = "The Torn Journal page you discovered provides your next clue.",
    ["D"] = "Inside the crate you find various objects: musty heirlooms, a family portrait, a few hunting trophies and some old books.  Near the bottom, underneath a ceramic vase, you uncover A Torn Journal Page.",
  },
  [75] = {
    ["T"] = "The Legend of Stalvan",
    ["O"] = "Marshal Haggard wants you to check the chest upstairs for the Faded Journal Page.",
    ["D"] = "There was a bundle of parchments in the chest upstairs when I moved into this place.  I looked at them once when I first arrived but I gave up once the fog hazed over my peepers. $b$bDo an old, nearly-blind man a favor and check the chest upstairs for anything that might help you in your quest to discover more about this Stalvan character.  I\'m pretty sure there\'s a faded journal page that might be of interest to you.  Bring it to me and I\'ll help in anyway I can.",
  },
  [76] = {
    ["T"] = "The Jasperlode Mine",
    ["O"] = "Explore the Jasperlode Mine, then report back to Marshal Dughan in Goldshire.",
    ["D"] = "Thanks to you we know the Fargodeep Mine is infested with kobolds.  Now we need a scout to investigate the more distant Jasperlode Mine.$B$BExplore Jasperlode and confirm any kobold presence.  To reach the mine, travel east along the road until you reach the Tower of Azora.  From the tower, head north and you\'ll find the mine in the foothills.",
  },
  [77] = {
    ["T"] = "A Sticky Situation",
    ["O"] = "Recover 10 bottles of Hinterlands Honey Ripple from the crates located around the abandoned Horde base in Skulk Rock.",
    ["D"] = "I possess original packaging materials for the ripple Dran Droffers seeks.  I will assist you in packaging the ripple for Malton\'s abrasive father... for a fee of two additional bottles of said ripple.  They are located in crates found in the abandoned Horde base near Skulk Rock.$B$BIt will be an arduous task, to wit; foul slimes and oozes will be found sticking to the ruins; rumor has it they are led by some sort of master ooze.  Regardless, procure the ripple and return to me here for packaging.",
  },
  [78] = {
    ["T"] = "The Legend of Stalvan",
    ["O"] = "Take the Faded Journal Page to Tavernkeep Smitts.",
    ["D"] = "I know of someone who might be able to assist you.  Back when I was leading the Stormwind Guard, we used to get drinks at the Scarlet Raven Tavern in Darkshire.  The Innkeeper there, Smitts, was quite an expert on the local lore.  Show him this page and see what he has to say about it.",
  },
  [79] = {
    ["T"] = "The Legend of Stalvan",
    ["O"] = "Take the muddy page to Commander Ebonlocke.",
    ["D"] = "I followed the legend of that Stalvan character for years.  When those visiting nobles were slaughtered a few years back I went with Haggard to investigate.  I found these muddy pages but we were never able to link the handwriting to that crazy man in the woods.  Your trail of evidence proves his guilt.  Take this to Commander Ebonlocke immediately and fill her in on what you\'ve discovered!",
  },
  [80] = {
    ["T"] = "The Legend of Stalvan",
    ["O"] = "Show Clerk Daltry the Bloodstained Journal Page.",
    ["D"] = "I have had my eye on that creep, Stalvan, for quite sometime.  But if this page was written by the same hand it proves his guilt beyond a shadow of a doubt.$b$bIn all his days keeping the Town Registry, Clerk Daltry has become an expert at identifying handwriting.  Show him this page and see if the writing matches that of the registry signature.",
  },
  [81] = {
    ["T"] = "Ripple Delivery",
    ["O"] = "Deliver the barrel to Dran Droffers in Orgrimmar.",
    ["D"] = "I have taken the extra step of securing not only Dran\'s packaged ripple in the barrel, but also the extra bottles of ripple he is purloining.  While I may not hold Dran very highly, there is nothing worse than jostled ripple.  You\'re welcome.$B$BNow, deliver yon barrel to Dran Droffers in his salvage emporium in Orgrimmar.  Pray tell, please extend my warmest greetings to Malton; let him know that should he ever thirst for fine drink and finer drinking companions, we would be honored with his visit.",
  },
  [82] = {
    ["T"] = "Noxious Lair Investigation",
    ["O"] = "Bring five Centipaar insect parts from the Noxious Lair to Alchemist Pestlezugg in Gadgetzan.",
    ["D"] = "All right now, these reports about the bugs... what we know is that they are draining the water pools that used to be where the Noxious Lair is. What we DON\'T know is, well, everything else.$B$BLet\'s figure out who our enemy is first! I want you to head into the Noxious Lair and get some parts off of those critters. Get five from the Centipaar there and bring them to Alchemist Pestlezugg for him to dink with. With him on the case, we\'ll figure out what they are!",
  },
  [83] = {
    ["T"] = "Red Linen Goods",
    ["O"] = "Bring 6 Red Linen Bandanas to Sara Timberlain at the Eastvale Logging Camp.",
    ["D"] = "The Defias gang in Northshire wears burlap masks, but the Defias in Elwynn wear linen which I can use to make fine linen goods.$B$BBring me red linen bandanas and I\'ll use them to fashion something for you.$B$BDefias gang members have camps pocketed throughout Elwynn.",
  },
  [84] = {
    ["T"] = "Back to Billy",
    ["O"] = "Bring the Pork Belly Pie to Billy Maclure at the Maclure Vineyards.",
    ["D"] = "Here you go.  And when you give this pie to that Billy, you tell him I hope he chokes on it!",
  },
  [85] = {
    ["T"] = "Lost Necklace",
    ["O"] = "Speak with Billy Maclure.",
    ["D"] = "I lost my necklace, and think that guttersnipe Billy Maclure took it!  He\'s usually scuttling like a rat around the Maclure vineyards east of here. $B$BGet my necklace back for me, and you\'ll warm an old widow\'s heart.",
  },
  [86] = {
    ["T"] = "Pie for Billy",
    ["O"] = "Bring 4 Chunks of Boar Meat to Auntie Bernice Stonefield at the Stonefield\'s Farm.",
    ["D"] = "Maybe if I got a pie, I could tell you who has that necklace.  And you know, I think that old Bernice lady at that other farm makes great Pork Belly Pies... $B$BMaybe if you gave her some chunks of boar meat from the boars that hang around our farms, and told her what it was for, she\'d bake up a pie for you.",
  },
  [87] = {
    ["T"] = "Goldtooth",
    ["O"] = "Bring Bernice\'s Necklace to \"Auntie\" Bernice Stonefield at the Stonefield Farm.",
    ["D"] = "I was playing near the Fargodeep Mine, and I think I dropped, er...I mean I saw, the old lady\'s necklace.  Don\'t ask me how it got there...it wasn\'t me!$B$BWell anyway, I saw this big, gold-toothed kobold pick up the necklace and run into the mine.  Go find that kobold and you\'ll find the necklace, I swear!",
  },
  [88] = {
    ["T"] = "Princess Must Die!",
    ["O"] = "Kill Princess, grab her collar, then bring it back to Ma Stonefield at the Stonefield Farm.",
    ["D"] = "The Brackwells have a prize-winning pig, Princess.  The sow is HUGE, and she got that way from sneaking over here and eating my veggies!$B$BSo before she comes to our fields...Princess must die!  Bring me her collar as proof of the deed and I\'ll give you something for your time!$B$BPrincess is usually over at the Brackwell Pumpkin Patch, to the east and beyond the Maclure farm.  Get her before she gets hungry and comes back here!",
  },
  [89] = {
    ["T"] = "The Everstill Bridge",
    ["O"] = "Bring 5 Iron Pikes and 5 Iron Rivets to Foreman Oslow in Lakeshire.",
    ["D"] = "During the last orc invasion we were forced to smelt our iron down to make bullets, swords and armor.  We sent word to Stormwind for a new shipment of materials but a band of Redridge gnolls hijacked the caravan and ran off into the hills behind Lakeshire.$b$bNow we\'re coming up short on supplies to get this bridge rebuilt.  If you can bring me 5 iron pikes and 5 iron rivets, $N, I\'ll make sure you\'re rewarded. $b$bNow let\'s get to work!",
  },
  [90] = {
    ["T"] = "Seasoned Wolf Kabobs",
    ["O"] = "Gather 10 Lean Wolf Flanks and Stormwind Seasoning Herbs and return to Chef Grual in Darkshire.",
    ["D"] = "Seasoned Wolf Kabobs are a house favorite!  Sure, I can make you some.  But first I\'ll need the supplies.  Bring me 10 Lean Wolf Flanks as well as some Stormwind Seasoning Herbs.  There are so many wolves here in the forest, I am sure finding the Flanks will be no problem.  For the herbs, seek out Felicia Gump in her canal district flower shop in Stormwind.",
  },
  [91] = {
    ["T"] = "Solomon\'s Law",
    ["O"] = "Bring 10 Shadowhide Pendants to Bailiff Conacher in the Lakeshire Town Hall.",
    ["D"] = "An enemy of Stormwind has taken up residence in the eastern foothills of Redridge. This wicked villain uses the Arcane to spread terror and misery upon our town and our people. Under the name, Morganth, he conducts his evil business.$b$bMorganth now controls the Shadowhide Gnoll Clan to perform his biddings. As stated by the Law of Lakeshire, aiding a criminal of the Kingdom is punishable by death.$b$bThese vile gnolls must be killed!$b$bTurn in 10 Shadowhide Pendants to me, and you shall be rewarded.",
  },
  [92] = {
    ["T"] = "Redridge Goulash",
    ["O"] = "Chef Breanna of Lakeshire wants five pieces of Tough Condor Meat, five Great Goretusk Snouts and five helpings of Crisp Spider Meat.",
    ["D"] = "Nothing would please me more than to make you some Redridge Goulash. But there\'s a slight problem. During the recent orc uprising I fed the entire brigade of Stormwind troops. My cupboard is now bare. But if you can provide me with the ingredients, I\'d be happy to oblige.$b$bJust bring me five pieces of Tough Condor Meat, five Great Goretusk Snouts and five helpings of Crisp Spider Meat.",
  },
  [93] = {
    ["T"] = "Dusky Crab Cakes",
    ["O"] = "Gather 6 Gooey Spider Legs and bring them to Chef Grual in Darkshire.",
    ["D"] = "I\'ll let you in on a little secret - Dusky \"Crab\" Cakes are really made from spider legs!  I know it\'s a bit disgusting, but the cakes have a nice, tangy flavor and make great snacks!  Bring me Gooey Spider Legs and I\'ll whip you up a few of them.$B$BI hear Venom Web Spiders are a good source - they nest to the north, between the foothills and the river. ",
  },
  [94] = {
    ["T"] = "A Watchful Eye",
    ["O"] = "Take the Glyph of Azora to the Lion Statue near the Tower of Ilgalar in Redridge.",
    ["D"] = "My rival, Morganth, is a vicious mage of great power who lives in the Tower of Ilgalar in Redridge.  I have developed a means of spying on him, the Eye of Azora, and can now view him from afar.$B$BBut to study his thoughts ... I require further aid.$B$BIf you are willing, then take my Glyph of Azora to the Tower of Ilgalar.  Place it upon the Lion Statue near the Tower.$B$BWhen this is done, the power of Azora will grant visions of Morganth\'s future plans.",
  },
  [95] = {
    ["T"] = "Sven\'s Revenge",
    ["O"] = "Go to Sven\'s Farm and find what was buried behind the old stump.",
    ["D"] = "I used to work one of the farms to the southeast...until Dark Riders from Deadwind Pass descended upon my farm and slaughtered my family when I was away!$B$BWhen I returned I saw a shadowy figure skulking near my barn, burying something.  He fled before I could catch him, and I couldn\'t linger for I was hot on the heels of the Dark Riders.  So I never discovered what was hidden.$B$BIf you can find what that shadowy figure buried, I would be grateful.  The hiding spot is behind the old stump near my barn.",
  },
  [96] = {
    ["T"] = "Call of Water (Part 8)",
    ["O"] = "Bring the Shard of Water to Islen Waterseer in the Barrens.",
    ["D"] = "Take shard. Take shard and give life.$B$BGive life and understand. Understand water is life... corruption stops life.$B$BYou protect and give life with water\'s power.",
  },
  [97] = {
    ["T"] = "The Legend of Stalvan",
    ["O"] = "Report the news to Commander Ebonlocke.",
    ["D"] = "Let Commander Ebonlocke know immediately that the handwriting matched, $N!  Her suspicions were correct!",
  },
  [98] = {
    ["T"] = "The Legend of Stalvan",
    ["O"] = "Kill Stalvan Mistmantle and show Madame Eva his family ring.",
    ["D"] = "Stalvan Mistmantle led a life of depravity.  Innocent victims died by his hand.  Undoubtedly he is guilty of countless crimes.  Now the lunatic threatens Darkshire.  The Light only knows what sordid acts he is plotting.  Travel to his cottage just north of town, $C, and execute Stalvan, once and for all.$b$bWhen the deed is done, travel to Madame Eva\'s and show her his family ring.  After all, it was her premonition that led to this gruesome discovery.  But Darkshire is safer because of her.",
  },
  [99] = {
    ["T"] = "Arugal\'s Folly",
    ["O"] = "Bring 6 Pyrewood Shackles to Dalar Dawnweaver at the Sepulcher.",
    ["D"] = "As my understanding of Arugal\'s magic grows so does my disdain for the hapless fool.  I am close to completing my research on his so called remedy.$b$bMy knowledge will be complete when I learn what enchantment is causing the strange behavior going on in Pyrewood Village.  By day, the peasants appear to be Human.  But when the sun goes down the townsfolk turn into Moonrage Worgen.$b$bI need to draw energy from the enchanted shackles Arugal cast on them.  Bring to me six enchanted Pyrewood Shackles, $N.",
  },
  [100] = {
    ["T"] = "Call of Water (Part 7)",
    ["O"] = "Speak to the Minor Manifestation of Water in Silverpine Forest.",
    ["D"] = "Light explodes as you finish the incantation. The sounds of the water drops hitting the flame have died down and your senses are overcome as the corrupt spirits around you are destroyed.",
  },
  [101] = {
    ["T"] = "The Totem of Infliction",
    ["O"] = "Bring 10 Ghoul Fangs, 10 Skeleton Fingers and 5 Vials of Spider Venom to Madame Eva in Darkshire.",
    ["D"] = "As the mystical taint creeps through the forest, the need for self-protection is undeniable, $N.  The winds whisper to me and they speak of a great danger which waits patiently for you in the near future.$b$bIf you wish to protect yourself, noble $c, bring to me 10 ghoul fangs, 10 skeleton fingers and 5 vials of spider venom.  For you I shall enchant a Totem of Infliction which will harm those who attempt violent acts against you.",
  },
  [102] = {
    ["T"] = "Patrolling Westfall",
    ["O"] = "Bring 8 Gnoll Paws to Captain Danuvin on Sentinel Hill.",
    ["D"] = "Stormwind has abandoned us.  A foul wind of depravity rustles through the plains of Westfall.  This was my homeland and I will not turn my back on the citizens who choose to remain here.  We, the former farmers, shall make our stand.$b$bYour task, should you choose to accept, is to patrol the grasslands of Westfall.  Track down and slay the vile Gnolls that seem to be working in conjunction with the Deadmines thieves. Bring me eight Gnoll Paws and I will reward your bravery.",
  },
  [103] = {
    ["T"] = "Keeper of the Flame",
    ["O"] = "Bring 5 Flasks of Oil to Captain Grayson at the Westfall Lighthouse.",
    ["D"] = "The night the Lighthouse Keeper\'s family died was horrible.  I watched, helpless, as Old Murk-Eye led the attack.  But what\'s done is done and now my concern is for the lives of the sailors on The Great Sea whose ships come close to the perilous rocks of the coastline.  With no one to keep watch on the flame the responsibility has fallen upon me.$b$bHelp me keep the torch lit by bringing me 5 flasks of oil from the Harvest Monsters.",
  },
  [104] = {
    ["T"] = "The Coastal Menace",
    ["O"] = "Bring a scale of Old Murk-Eye to Captain Grayson at the Westfall Lighthouse.",
    ["D"] = "When my life was ended upon the rocks, I had no clue what the afterlife held for me.  The Lighthouse was black that night because Old Murk-Eye had scared the keeper\'s family off.  They returned and re-lit the flame but Old Murk-Eye coerced the weaker minded murlocs to raid the Lighthouse with him once again.  The second time the family was not so lucky and before my eyes they perished helplessly.$b$bSlay Old Murk-Eye if you see him along the shore and bring me one of his scales and I shall reward you.",
  },
  [105] = {
    ["T"] = "Alas, Andorhal",
    ["O"] = "Bring Araj\'s Phylactery Shard to High Executor Derrington at the Bulwark, Western Plaguelands.",
    ["D"] = "Alas, the time to attack Andorhal and drive out the lich that controls it is upon us!$B$BInside the ruins of the city is where the lich - Araj the Summoner - holds dominion.  He is guarded by numerous Scourge surrounding the remains of city\'s center, and his personal retinue of guardians attached to him directly is formidable.  You will need numerous allies to even stand a chance of facing him successfully.$B$BDestroy Araj, $N, and bring me a shard from his phylactery as proof!",
  },
  [106] = {
    ["T"] = "Young Lovers",
    ["O"] = "Give Maybell\'s Love Letter to Tommy Joe Stonefield.",
    ["D"] = "Oh, I\'m cursed!  My heart belongs to Tommy Joe Stonefield, but our families are bitter enemies.  So I can\'t see him, even though my eyes ache to gaze upon that handsome face!$B$BPlease, take this letter and give it to Tommy Joe.  He\'s usually at the river to the west of the Stonefield Farm, which is due west of here.",
  },
  [107] = {
    ["T"] = "Note to William",
    ["O"] = "Take Gramma Stonefield\'s Note to William Pestle.",
    ["D"] = "I bet William Pestle has a potion to unite our two young lovers!$B$BHere, take this note to William.  He\'s staying at the Lion\'s Pride Inn in Goldshire.",
  },
  [109] = {
    ["T"] = "Report to Gryan Stoutmantle",
    ["O"] = "Talk to Gryan Stoutmantle.  He usually can be found in the stone tower on Sentinel Hill, just off the road, in the middle of Westfall.",
    ["D"] = "Looks to me you\'ve seen quite a bit of combat in your time, $c.  If you haven\'t already, you should report to Gryan Stoutmantle.  He heads up the People\'s Militia, aimed at protecting the farmlands of Westfall.  I bet he could use your help.  You can usually find him in the stone tower on Sentinel Hill, just off the road in the middle of Westfall.",
  },
  [110] = {
    ["T"] = "Insect Part Analysis",
    ["O"] = "Check in Gadgetzan for Alchemist Pestlezugg and his analysis of the insect parts.",
    ["D"] = "I\'m going to spend some time taking a look at Scrimshank\'s surveying gear... as well as putting his personal effects in order.  I get the sneaking feeling behind my ears that this isn\'t the last loss we\'ll be facing against these bugs.$B$BI\'d like for you to check in with Alchemist Pestlezugg to see if he has had a chance to process the insect parts we sent him.  If they\'re ready, bring them to me.",
  },
  [111] = {
    ["T"] = "Speak with Gramma",
    ["O"] = "Speak with Gramma Stonefield.",
    ["D"] = "Please, $N, talk with my Gramma.  If anyone can find a way to bring me together with Maybell, she can.$B$BShe\'s inside our house east of here.",
  },
  [112] = {
    ["T"] = "Collecting Kelp",
    ["O"] = "Bring 4 Crystal Kelp Fronds to William Pestle in Goldshire.",
    ["D"] = "I can make an invisibility liquor for Maybell, so she can slip away from the Maclure Vineyards and go to Tommy Joe.  But to make the Liquor, I need some crystal kelp.$B$BAlthough the kelp usually grows in the ocean... sometimes murlocs collect it.  See if the murlocs near Crystal Lake have any.  Crystal Lake is just east of Goldshire.",
  },
  [113] = {
    ["T"] = "Insect Part Analysis",
    ["O"] = "Deliver the report to Senior Surveyor Fizzledowser in Gadgetzan.  Be sure he gives you a copy of the report, as Alchemist Pestlezugg has requested.",
    ["D"] = "Senior Surveyor Fizzledowser needs to see this report immediately.  Actually... everyone who can read needs to see this report.  We are facing a grave and serious threat to not only Tanaris and Gadgetzan, but if left unchecked, the entirety of southern Kalimdor.  Heck, even quite possible the whole of Azeroth; this thing has given no indication it will subside.$B$BHave Fizzledowser make a duplicate of the report for you to distribute freely to your allegiance. Everyone must know about this threat!",
  },
  [114] = {
    ["T"] = "The Escape",
    ["O"] = "Take the Invisibility Liquor to Maybell Maclure.",
    ["D"] = "Take this invisibility liquor to young Maybell.  It should last long enough for her to visit Tommy Joe.",
  },
  [115] = {
    ["T"] = "Shadow Magic",
    ["O"] = "Bring 3 Midnight Orbs to Marshal Marris in Lakeshire.",
    ["D"] = "The Blackrock orcs enlisted shadowcasters to aid their attacks in Redridge, and they have brought with them devices of dark power--midnight orbs.  These orbs have struck telling blows against Redridge\'s defenders, and it\'s imperative we remove the demon-tainted items from the conflict.$B$BFind and deliver to me midnight orbs from slain Blackrock shadowcasters.  I will then have them disposed of, for this world would be a better place without them!",
  },
  [116] = {
    ["T"] = "Dry Times",
    ["O"] = "Barkeep Daniels of Lakeshire needs a keg of Thunderbrew Lager, a cask of Merlot, a bottle of Moonshine and a skin of Sweet Rum.",
    ["D"] = "I find myself in quite a pinch here, $N.  The bottles are close to running dry.  The latest booze shipment is long overdue.  The orc invasion has been hell.$b$bPerhaps you can go to work for me?$b$bI need you to pick up a keg of Thunderbrew Lager from Grimbooze Thunderbrew in the Westfall hills, a cask of Merlot from Stormwind, a bottle of Moonshine from Darkshire and a skin of Sweet Rum from Goldshire.  Bring those back to me and I will see to it you are rewarded.",
  },
  [117] = {
    ["T"] = "Thunderbrew Lager",
    ["O"] = "Bring Grimbooze Thunderbrew 5 hops to complete his special brew.",
    ["D"] = "Hurry, my friend, move with haste $bIn order for our rich lager to taste $bMore like beer and less like stew, $bHops are needed to make the brew.",
  },
  [118] = {
    ["T"] = "The Price of Shoes",
    ["O"] = "Take Verner\'s Note to Smith Argus in Goldshire.",
    ["D"] = "I\'ve been using so much iron keeping the Stormwind Guards outfitted, I don\'t have enough to supply the stable\'s horses with shoes!$B$BTake this note to Blacksmith Argus in Goldshire.  It explains to him my problem and requests a shipment of horseshoes.",
  },
  [119] = {
    ["T"] = "Return to Verner",
    ["O"] = "Return to Verner Osgood in Redridge.  Give him the Crate of Horseshoes.",
    ["D"] = "Ok, I\'ll give him his 50 horseshoes...$B$BHere they are, all boxed up and ready to go!  Tell Verner it was a pleasure doing business, and be sure he reads the note attached to this crate!",
  },
  [120] = {
    ["T"] = "Messenger to Stormwind",
    ["O"] = "Magistrate Solomon has given you a report which must be delivered to General Marcus Jonathan in Stormwind.  The judge wants you to return to him as soon as the delivery has been made.",
    ["D"] = "These are trying times, $c.  The township is under constant siege.  Without reinforcements, we will certainly face defeat.  The message I now entrust to you is of utmost importance.  Get this report to General Marcus Jonathan of Stormwind immediately.  Once your delivery is made, return to me at once with any news, be it good or bad. $b$bNow make haste!",
  },
  [121] = {
    ["T"] = "Messenger to Stormwind",
    ["O"] = "Take General Marcus Jonathan\'s letter of response to Magistrate Solomon in Lakeshire.",
    ["D"] = "While I wait for a response from the King I want you to carry this letter to Magistrate Solomon. $b$bDismissed, $c!",
  },
  [122] = {
    ["T"] = "Underbelly Scales",
    ["O"] = "Gather 6 Underbelly Whelp Scales from Black Dragon Whelps, and bring them to Verner Osgood in Redridge.",
    ["D"] = "I need some underbelly scales from black dragon whelps to pay for the shoes Argus sent me from Goldshire.  If you can get 6 of them for me, then I can pay Argus with some... and have enough left to fashion something for you.$B$BBlack dragon whelps are often flying around south of Lakeshire, but they like to wander.  You might have to just keep an eye out and hunt them when you see them.",
  },
  [123] = {
    ["T"] = "The Collector",
    ["O"] = "Go to Marshal Dughan in Goldshire and give him The Collector\'s Schedule.",
    ["D"] = "This note is a schedule with a list of days and times when a person -- described only as \"The Collector\" -- will receive a shipment of gold from the mines in Elwynn Forest.$B$BFrom the schedule, it looks as if the Collector resides near the Brackwell Pumpkin Patch in eastern Elwynn.$B$BThis sounds important.  You should report it to Marshal Dughan in Goldshire.",
  },
  [124] = {
    ["T"] = "A Baying of Gnolls",
    ["O"] = "Kill 10 Redridge Brutes and 8 Redridge Mystics, then return to Verner Osgood.",
    ["D"] = "As if the attacking orcs weren\'t enough!  Now I have gnoll brutes and mystics prowling along the ridge north of my stable, taking my horses when they stray...$B$BIf you can get rid of those gnolls, my horses and I would be very grateful.",
  },
  [125] = {
    ["T"] = "The Lost Tools",
    ["O"] = "Foreman Oslow of Lakeshire wants you to retrieve his toolbox from the bottom of Lake Everstill.",
    ["D"] = "I could really use a hand here, $N.  With the town under siege, it\'s been hard to get supplies.  My tools were being delivered from Goldshire by wagon but the bridge was blown out.  We put the tools in a boat but the orcs hit it with a catapult.  Just my luck, my toolbox sank right to the bottom of the lake.$b$bRetrieve my toolbox, $N, and I\'ll make it worth your while.",
  },
  [126] = {
    ["T"] = "Howling in the Hills",
    ["O"] = "Bring Yowler\'s Paw to Verner Osgood in Lakeshire.",
    ["D"] = "$N, the gnolls are still out there.  I can hear their baying as they hunt in the hills above Lakeshire.  And one cry is louder than the rest - Yowler,  their leader.$B$BKill Yowler and send his pack scattering!  Bring me his paw as proof.$B$BAnd be careful, this gnoll is their leader for a reason.  He is no Mongrel...",
  },
  [127] = {
    ["T"] = "Selling Fish",
    ["O"] = "Bring 10 Spotted Sunfish to Dockmaster Baren in Lakeshire.",
    ["D"] = "Lake Everstill is famous for its spotted sunfish.  There\'s always demand for them...and I\'m running low!$B$BBring me a batch of 10 and I\'ll barter well for them.  If you can\'t fish yourself, then...maybe you can get them off the murlocs!",
  },
  [128] = {
    ["T"] = "Blackrock Bounty",
    ["O"] = "Kill 15 Blackrock Champions and Guard Howe in Lakeshire will reward you.",
    ["D"] = "The Blackrock Clan must be destroyed. The Enemy has invaded our lands. These mountains fall under the sovereign right of the King of Stormwind. Our sources tell us that the leaders of each unit within the Clan are known as Champions. You\'ll find them in their encampment to the north, just west of the road to the Burning Steppes.$b$bMagistrate Solomon wants these foul brutes dead. Slay 15 Blackrock Champions and return to me.",
  },
  [129] = {
    ["T"] = "A Free Lunch",
    ["O"] = "Bring Parker\'s lunch to Guard Parker.  He patrols the road leading to Darkshire.",
    ["D"] = "Can you do me a favor?  I\'ve prepared a lunch for Guard Parker, but he\'s out on patrol...he\'s a big, strong Stormwind guard who can defend himself, but it\'s much too dangerous out there for a townsperson like me.$B$BSo if you deliver his lunch for me, then come back here and I\'ll give you a free lunch!  Parker patrols the stretch of road leading to Duskwood.",
  },
  [130] = {
    ["T"] = "Visit the Herbalist",
    ["O"] = "Speak with the Redridge Herbalist, Martie Jainrose.",
    ["D"] = "Before you go back to Darcy, can you bring her some flowers for me?  You can get them at the herbalist shop back in Lakeshire, at the west end of town.$B$BBut when you get the flowers...don\'t tell the herbalist, Martie, whom they\'re from, or whom they\'re for...",
  },
  [131] = {
    ["T"] = "Delivering Daffodils",
    ["O"] = "Give Darcy the Daffodil Bouquet.",
    ["D"] = "Here is your bouquet.  I chose Daffodils for you.  They\'re my favorite!",
  },
  [132] = {
    ["T"] = "The Defias Brotherhood",
    ["O"] = "Take Wiley\'s Note to Gryan Stoutmantle in Westfall.",
    ["D"] = "What I am about to tell you could cost me my life.  The Defias gang is up to something big.  Last I heard they were working in conjunction with various gnolls, kobolds and even goblins.$b$bTake this note to Stoutmantle.  It explains as much as I know on the subject.",
  },
  [133] = {
    ["T"] = "Ghoulish Effigy",
    ["O"] = "Gather 7 Ghoul Ribs and bring them to Abercrombie at his shack.",
    ["D"] = "I live so far from the protection of town, it\'s a wonder the ghouls and the walking dead haven\'t eaten me!  In fact, just yesterday a pack of Bone Chewers was pounding on the walls of my house!$B$BI want to make an effigy - a kind of \"scarecrow\" for Ghouls.  To do that I need some Ghoul Ribs.  You can get them from Flesh Eaters, Bone Chewers, Brain Eaters, Rotted Ones and Plague Spreaders.$B$BGet the ribs for me, and I will repay you with some coin.",
  },
  [134] = {
    ["T"] = "Ogre Thieves",
    ["O"] = "Return Abercrombie\'s Crate to Abercrombie.",
    ["D"] = "A few weeks ago I was picking herbs far from my house, and a band of ogres attacked me!  I ran, and I was forced to leave behind a crate of precious tools and herbs.$B$BAfter they chased me off, the ogres swaggered back to the ogre mound in southern Duskwood.  I\'m sure my crate is somewhere near the mound.$B$BPlease, $N, retrieve this crate for me for I miss it sorely.",
  },
  [135] = {
    ["T"] = "The Defias Brotherhood",
    ["O"] = "Take Wiley\'s Note to Mathias Shaw in Stormwind.",
    ["D"] = "I wonder what Wiley meant when he mentioned the Stonemasons.  Perhaps that was a slip of tongue.  Could the Defias gang be related to the Stonemasons?  Only one man would know for sure:  Mathias Shaw, head of SI:7. Show him Wiley\'s Note and see if he has anything to add to this growing mystery. If you have trouble finding Shaw, check the Barracks in Old Town.",
  },
  [136] = {
    ["T"] = "Captain Sander\'s Hidden Treasure",
    ["O"] = "Find Captain Sanders\' footlocker and search it for the next clue.",
    ["D"] = "If ye be readin\' this, it means that Ol\' Captain Sanders is in a watery grave.  So my treasure is yours now, ye jest need to follow the clues. $b$bFirst ye need to find me footlocker.  It\'s probably half-buried in sand by now, along the Western Coast of Westfall near the shipwreck.  There be lots of shipwrecks but only one rusty anchor on the coast.  Find that anchor and you\'ll find me locker!  Look in there for the next clue.",
  },
  [138] = {
    ["T"] = "Captain Sander\'s Hidden Treasure (Part 2)",
    ["O"] = "Find the old barrel near the ruined chimney and search it for your next clue.",
    ["D"] = "The clue to the treasure reads: Good work, matey!  Now ye need to head due east.  East up the bluffs, east to the road.  Look for the ol\' chimney ruins near the side of the road.  There you\'ll find an old barrel with your next clue.",
  },
  [139] = {
    ["T"] = "Captain Sander\'s Hidden Treasure (Part 3)",
    ["O"] = "Search the empty jug next to the windmill for the next clue.",
    ["D"] = "Searching through the barrel you discover another piece of parchment.  This one reads:  Now from this here barrel, face ye North.  Straight as the crow flies, keep ye walkin\' till you see the empty jug next to the lone windmill on the sea bluffs.  If ye poke around that jug, ye just might find what you\'re lookin\' for.",
  },
  [140] = {
    ["T"] = "Captain Sander\'s Hidden Treasure (Part 4)",
    ["O"] = "Locate Captain Sanders\' chest and open it for your reward.",
    ["D"] = "Sure enough, deep within the Old Jug there is another clue to Sander\'s treasure.  The ink has run in some places and the paper smells like whiskey but you can make out some of the text:  Now that ye found me ol\' whiskey jug, you\'re almost to the treasure!  Just face West from the bottle and walk down to the shore.  Once ye get to the water, keep going!  Swim straight west till you find the island with me treasure chest!",
  },
  [141] = {
    ["T"] = "The Defias Brotherhood",
    ["O"] = "Take Shaw\'s report to Gryan Stoutmantle in Westfall.",
    ["D"] = "The Stonemasons\' Guild was run by a man named Edwin VanCleef.  VanCleef was responsible for rebuilding Stormwind after the orcs razed it in the First War.  Apparently, VanCleef and his men were unhappy with their treatment by the King after the reconstruction was complete.  That just might explain a thing or two.$b$bI have written a more detailed account for your Master in Westfall.  Take this to him at once!",
  },
  [142] = {
    ["T"] = "The Defias Brotherhood",
    ["O"] = "Track down the Defias Messenger in Westfall and bring his message to Stoutmantle.",
    ["D"] = "We need to discover the location of the Defias hideout.  $N, my scout reports that a Defias Messenger has been seen on the roads between Moonbrook, the Gold Coast Quarry and the Jangolode Mine.  I want you to capture him.  If he resists, kill him and bring me whatever he is carrying.",
  },
  [143] = {
    ["T"] = "Messenger to Westfall",
    ["O"] = "Magistrate Solomon wants you to take his written plea to Gryan Stoutmantle in Westfall.",
    ["D"] = "Something odd is afoot with the Stormwind Army.  They should have been here in force by now.  Time is a luxury we don\'t have, however.  I will not stand by and watch the people of Lakeshire give their lives without trying to enlist more help.  I\'ve heard word of a people\'s militia forming in Westfall.$b$bTake this plea to their leader, Gryan Stoutmantle.  Perhaps Stoutmantle\'s men can lend some support here until the King sends reserves.",
  },
  [144] = {
    ["T"] = "Messenger to Westfall",
    ["O"] = "Return to Magistrate Solomon with Gryan Stoutmantle\'s response.",
    ["D"] = "Magistrate Solomon\'s note pains me to read.  But it is obvious he knows not the war which is waged in Westfall or else he would know better than to expect aid from the Militia.  If Stormwind had not deserted us as well, we would not have the need for the Militia.$b$bTake this response to your master in Redridge, $c.  And let him know that my heart is heavy with the loss of good men.",
  },
  [145] = {
    ["T"] = "Messenger to Darkshire",
    ["O"] = "Magistrate Solomon wants you to take a letter to Lord Ebonlocke in Darkshire.",
    ["D"] = "I require your assistance once again.  I have written to Lord Ebonlocke, requesting that he sends his trained guards, the Night Watch, to help with Lakeshire\'s defense.  It is imperative this message makes it to him swiftly.  You must exercise extreme caution.  Ebonlocke is the mayor of Darkshire, in the heart of Duskwood.  Do not stray from the roads.",
  },
  [146] = {
    ["T"] = "Messenger to Darkshire",
    ["O"] = "Return to Magistrate Solomon with Lord Ebonlocke\'s letter of response.",
    ["D"] = "If I did not know Magistrate Solomon personally, I would say the man has gone mad.  Send the Night Watch to Lakeshire because Stormwind refuses aid?  The Night Watch was formed because Stormwind left us when this foul magic seeped into the land.  If I sent my Night Watch, this town would be overrun by evil before the guards even reached the forest\'s edge.$b$bTake this letter of response to your Master in Lakeshire.",
  },
  [147] = {
    ["T"] = "Manhunt",
    ["O"] = "Find and kill \"the Collector\"  then return to Marshal Dughan with The Collector\'s Ring.",
    ["D"] = "If the \"Collector\" is taking gold from our mines then he\'s stealing from the kingdom!  Bring the Collector to justice, and bring me the ring mentioned in the pickup schedule you gave me.  It may tell us whom the Collector is working for...$B$BThat Pickup Schedule says the Collector is hiding out at the Brackwell Pumpkin Patch.  You should search for him there.",
  },
  [148] = {
    ["T"] = "Supplies from Darkshire",
    ["O"] = "Speak with Madame Eva.",
    ["D"] = "Oh, what is a poor old man to do?  I need some supplies from Darkshire, but the village is so far away and I am so old and feeble...I would never make the trip.$B$BCan you go to Darkshire and bring me back some Ghost Hair Thread?  You can get it from Madame Eva at her home on the Darkshire square.",
  },
  [149] = {
    ["T"] = "Ghost Hair Thread",
    ["O"] = "Bring the Spectral Comb to Blind Mary.",
    ["D"] = "There is a poor, sad spirit, Blind Mary, who haunts an old farm house in the hills south of Darkshire.$B$BHere, take this Spectral Comb and ask Blind Mary to comb her hair.  Then bring back the comb and I will glean from it the Ghost Hair I\'ll need for the thread.$B$BAnd take care, for dark things now lurk near that house...",
  },
  [150] = {
    ["T"] = "Murloc Poachers",
    ["O"] = "Bring 8 Murloc Fins to Dockmaster Baren in Lakeshire.",
    ["D"] = "Murlocs are becoming a problem.  Larger groups of the fishmen are dropping anchor along the shores of Lake Everstill and fishing up a storm!  We need them gone before they eat all the fish in the lake!$B$BHunt murlocs!  Bring me eight of their fins and I might have something for you...",
  },
  [151] = {
    ["T"] = "Poor Old Blanchy",
    ["O"] = "Verna Furlbrow in Westfall wants you to bring her 8 Handfuls of Oats.",
    ["D"] = "Poor Old Blanchy!  Such a tired beast after all the work we put her through. I fed her before we left the farm, but we weren\'t expecting the wagon to break on us. If you could bring her a few handfuls of oats from the fields, I\'d be grateful.$B$BI bet you could find some around all of the farms in Westfall, if you can steer clear of those horrific machines that have taken over. There are several farms southwest of here.",
  },
  [152] = {
    ["T"] = "The Coast Isn\'t Clear",
    ["O"] = "Kill 7 Tidehunters, 7 Warriors, 7 Oracles and 7 Coastrunners and return to Captain Grayson at the Westfall Lighthouse.",
    ["D"] = "You probably noticed all of the shipwrecks along the coast.  The Great Sea is treacherous indeed.  The coast of Westfall needs to be kept clear, so that if sailors find their way to our beaches, they are safe.  The Murlocs are trouble though.$b$bKill 7 Tidehunters, 7 Warriors, 7 Oracles and 7 Coastrunners and I will see to it that you are rewarded.",
  },
  [153] = {
    ["T"] = "Red Leather Bandanas",
    ["O"] = "Bring 15 Red Leather Bandanas to Scout Galiaan at Sentinel Hill.",
    ["D"] = "The Defias Front is constantly shifting.  I\'ve been following their movements for quite some time now.  On a side note, I\'ve ascertained that many members of the gang can be tracked by the Red Leather Bandanas they wear.$b$bBring me 15 of these Bandanas and I\'ll see to it you are rewarded.",
  },
  [154] = {
    ["T"] = "Return the Comb",
    ["O"] = "Return the Ghost Hair Comb to Madame Eva in Darkshire.",
    ["D"] = "NO!!!!!$B$BTake it, take this comb away from me!$B$BI\'m a monster, and no one will ever think me lovely again!",
  },
  [155] = {
    ["T"] = "The Defias Brotherhood",
    ["O"] = "Escort the Defias Traitor to the secret hideout of the Defias Brotherhood.  Once the Defias Traitor shows you where VanCleef and his men are hiding out, return to Gryan Stoutmantle with the information.",
    ["D"] = "So Stoutmantle sends a scrawny $r like you to protect me?  Guess you\'ll have to do.  Better bring some friends too. $b$bYou know the deal, right?  You watch my back and I\'ll take you to the Defias hideout.  But you better be close by my side.  The Defias gang wants my head now.  If they see me with you, they\'ll try to kill me.$b$bLet me know when you and any friends you can round up are ready to go.",
  },
  [156] = {
    ["T"] = "Gather Rot Blossoms",
    ["O"] = "Gather 8 Rot Blossoms and bring them to Tavernkeep Smitts in Darkshire.",
    ["D"] = "If you bring me some rot blossoms then I can brew you up a batch of zombie juice.$B$BRot blossoms grow in strange places.  In particular, I hear you can find them inside the skulls of Skeletal Horrors and Skeletal Fiends... not that I\'ve gotten close enough to look!$B$BThe Raven Hill Cemetery is lousy with those skeletons.  You may as well get rid of them while you\'re looking for rot blossoms.  The town of Darkshire would be grateful.",
  },
  [157] = {
    ["T"] = "Deliver the Thread",
    ["O"] = "Bring the Ghost Hair Thread to Abercrombie, in his shack north of the Raven Hill Cemetery.",
    ["D"] = "Here is your thread, $N.  It has a thousand uses, from binding evil spirits to sewing life into inanimate objects.",
  },
  [158] = {
    ["T"] = "Zombie Juice",
    ["O"] = "Speak with Tavernkeep Smitts.",
    ["D"] = "I need a bottle of a certain liquor - Zombie Juice.  Tavernkeep Smitts at the Scarlet Raven Tavern in Darkshire should have some.$B$BAnd please hurry, $N.  These nights get cold, and I need that drink to put fire in my veins again!",
  },
  [159] = {
    ["T"] = "Juice Delivery",
    ["O"] = "Bring the Zombie Juice to Abercrombie at his shack.",
    ["D"] = "Here\'s your Zombie Juice...$B$B...and be careful with this stuff.  It\'s strong enough to raise the dead!",
  },
  [160] = {
    ["T"] = "Note to the Mayor",
    ["O"] = "Bring Abercrombie\'s Letter to Ello Ebonlocke, Mayor of Darkshire.",
    ["D"] = "Take this note to Ello Ebonlocke, the Mayor of Darkshire.$B$BHe and I are old friends...and when he reads it and discovers what you did for me...I\'m sure he\'ll be just as happy as I am!  Hahahaha!",
  },
  [161] = {
    ["T"] = "A Dark Threat Looms",
    ["O"] = "Chief Engineer Hinderweir wants you to take the strange smelling powder to Ashlan Stonesmirk, the explosives expert in Dun Modr.",
    ["D"] = "If my suspicions are correct, this is some type of explosive powder.  This must be analyzed by an explosives expert so we know just what we\'re dealing with here.  Ashlan Stonesmirk was assigned to the regiment guarding Dun Modr and the Thandol Span.  I need you to make a grave and perilous journey, $N.$b$bTravel through the Algaz Gate, follow the road through the Wetlands and seek out Stonesmirk in Dun Modr.  Heed my advice: stick to the roads and stop for nothing!",
  },
  [162] = {
    ["T"] = "Rise of the Silithid",
    ["O"] = "Take the Insect Analysis Report to Gracina Spiritmight in Darnassus.",
    ["D"] = "I urge you to take this report to someone who has working knowledge in this kind of thing.$B$BI know this night elf scholar named Gracina Spiritmight.  She\'s spent a lot of time studying life forms that are unique to the ecology of Azeroth; she\'s a historian to boot!  If you want someone to help figure out what exactly this threat is, and more importantly how to stop it, give her the report.  We\'ll need the help.$B$BLast I heard, she was residing in the Temple of the Moon, located in Darnassus.",
  },
  [163] = {
    ["T"] = "Raven Hill",
    ["O"] = "Find out what is haunting Raven Hill.",
    ["D"] = "Something\'s amiss at Raven Hill, $n. Calor swears that someone--or something--is haunting the buildings there. Two nights running he\'s noticed the shadows moving, and the third before that he says he saw light in one of the windows.$b$bIf, as you say, you care about the safety of Duskwood, find out what monster haunts in Raven Hill.",
  },
  [164] = {
    ["T"] = "Deliveries to Sven",
    ["O"] = "Deliver the supplies to Sven.",
    ["D"] = "Do you know Sven? Now, there is a man who does the work of the Light! He and his followers keep a camp at the northwestern edge of Duskwood.$b$bUnfortunately, because they are so few, they cannot even spare a man to fetch supplies from town. Will you deliver these supplies to him?",
  },
  [165] = {
    ["T"] = "The Hermit",
    ["O"] = "Check on the old man in the shack near Raven Hill cemetery.",
    ["D"] = "There was an old man who used to come into town to buy supplies rather frequently, but I haven\'t seen him for quite some time now. He lives out in a shack overlooking Raven Hill cemetery, if I remember correctly. Perhaps you should go see if something is amiss.",
  },
  [166] = {
    ["T"] = "The Defias Brotherhood",
    ["O"] = "Kill Edwin VanCleef and bring his head to Gryan Stoutmantle.",
    ["D"] = "There is but one task left for you to complete.  Edwin VanCleef must be assassinated.  While it saddens me to condemn any man to a death sentence, it is for the greater good of the people of Westfall that VanCleef is laid to rest once and for all.  Bring me the villain\'s head once the deed is done.",
  },
  [167] = {
    ["T"] = "Oh Brother. . .",
    ["O"] = "Bring Foreman Thistlenettle\'s Explorers\' League Badge to Wilder Thistlenettle in Stormwind.",
    ["D"] = "We were deep in a vast mine in Westfall, hidden beneath a barn in Moonbrook. No clue where these stinkin\' thieving types came from. Anyway, the mine tunnel collapsed on us. I got out but the others... well... the others haven\'t been heard from.$b$bYou look like you might have better luck in there. If you could look for my brother, I\'d be thankful.  He always carried his Explorers\' League Badge. If you can\'t find him, that badge would at least give me some peace of mind.",
  },
  [168] = {
    ["T"] = "Collecting Memories",
    ["O"] = "Retrieve 4 Miners\' Union Cards and return them to Wilder Thistlenettle in Stormwind.",
    ["D"] = "Many of my friends perished that horrible day when the mine tunnel caved in.  If your adventures happen to bring you into that wing of the mine in Moonbrook, please keep an eye out for any sign which might identify them.  If you come across any of their Miners\' Union Cards, bring them back to me and I\'ll make sure their families get some resolution from this horrible accident.",
  },
  [169] = {
    ["T"] = "Wanted: Gath\'Ilzogg",
    ["O"] = "Kill Gath\'Ilzogg and bring his head to Magistrate Solomon in Lakeshire for the reward.",
    ["D"] = "Wanted! Gath\'Ilzogg, Leader of the Blackrock attacks on Stonewatch Keep and raids against the township of Lakeshire. $b$bGath\'Ilzogg is extremely dangerous.  He is reportedly holed up in the recently conquered keep plotting the Blackrock clan\'s further crimes against humanity.  The orc infestation must be quelled at all costs.  A gracious reward will be given to whatever brave soul can deliver the head of Gath\'Ilzogg to Magistrate Solomon.",
  },
  [170] = {
    ["T"] = "A New Threat",
    ["O"] = "Balir Frosthammer wants you to kill 6 Rockjaw Troggs and 6 Burly Rockjaw Troggs.",
    ["D"] = "I hope you\'re here to lend us a hand, $c. After the last trogg attack, we could use all the help we can get.$b$bI hear the buggers have been popping up all across the lands, and it seems Coldridge Valley is no exception. They\'ve been spotted all over the hills to the southeast and near the frozen lake.$b$bAnd that\'s not all, just a few nights ago, they attacked and overran our camp to the west! $b$bWe\'re a bit shorthanded here, $g lad : lass;, and we need strong arms to help drive the troggs back.",
  },
  [171] = {
    ["T"] = "A Warden of the Alliance",
    ["O"] = "Return your ward to the Stormwind orphanage by handing in the Human Orphan Whistle to Orphan Matron Nightingale in the Cathedral District.",
    ["D"] = "You have been so very kind to me, $N.  I never dreamed that I\'d have so much fun during Children\'s Week.  Thank you.  I don\'t wanna be sad about what\'s happened to me and all - my folks always told me to rise above anything bad that happens.  You know, when I grow up I wanna be a hero... just like you.$B$BI\'m sad to say this, but it\'s time for me to go home.  Let\'s go back and I\'ll tell the matron what an awesome person you are!",
  },
  [172] = {
    ["T"] = "Children\'s Week",
    ["O"] = "Use the Orcish Orphan Whistle to talk with the child you will be looking after during Children\'s Week.",
    ["D"] = "You\'re willing to help us here at the orphanage?  Bless you, friend; your aid during Children\'s Week will be invaluable.$B$BTake this whistle.  You will only be able to use it for a short amount of time - typically not longer than Children\'s Week itself.  When you use it, you will be able to talk with the child you\'ve agreed to look after.  Using it again will dismiss the child.$B$BBy all means $N - use the whistle and meet your ward!  I\'m sure the child will be most eager to meet you.",
  },
  [173] = {
    ["T"] = "Worgen in the Woods",
    ["O"] = "Kill 6 Nightbane Shadow Weaver worgen for Calor in Darkshire.",
    ["D"] = "Darkness seems drawn inexorably to Duskwood. Master Carevin\'s quest is the expulsion of evil and heresy. Through our efforts are the people of Darkshire kept safe.$b$bYou believe yourself worthy to join us?$b$bI once thought as you. Disillusioned by the complacency of the Watch, I joined Master Carevin. If you wish to prove yourself, it will not be through words.$b$bTest your skills against the Nightbane Shadow Weaver worgen in Brightwood Grove--bright, hah!--and the Rotting Orchard.",
  },
  [174] = {
    ["T"] = "Look To The Stars",
    ["O"] = "Viktori Prism\'Antras of Darkshire wants you to bring him a bronze tube.",
    ["D"] = "My Gnomish friend, Cog, has promised to help me build a device which will let me peer towards the stars.  A truth exists, beyond magic, that can explain the strange transformation in the forest.$b$bBring to me a bronze tube made by a skilled engineer.  That is the first step, according to Cog\'s blueprint.",
  },
  [175] = {
    ["T"] = "Look To The Stars",
    ["O"] = "Locate Mary and persuade her to give up a reflective surface for Viktori\'s project.",
    ["D"] = "According to Cog\'s blueprint, what I need now is a way to reflect light.  I remember a woman by the name of Mary who became well known around these parts for always carrying a looking glass.  Perhaps you can track Mary down and see if she might lend us a reflective surface for this project?  I believe she once resided in southern Duskwood. . .",
  },
  [176] = {
    ["T"] = "Wanted:  \"Hogger\"",
    ["O"] = "Slay the gnoll Hogger and bring his Huge Gnoll Claw to Marshal Dughan.",
    ["D"] = "Wanted: Hogger$B$BA huge gnoll, Hogger, is prowling the woods in southwestern Elwynn.  He has overpowered all attempts at his capture.$B$BThe Stormwind Army has placed a generous bounty on the Gnoll.  To earn the reward, bounty hunters should bring proof of Hogger\'s demise to Marshal Dughan in Goldshire.",
  },
  [177] = {
    ["T"] = "Look To The Stars",
    ["O"] = "Retrieve Blind Mary\'s Looking Glass from the Insane Ghoul and give it to Viktori in Darkshire.",
    ["D"] = "The looking glass was one of my finest treasures until. . .the incident.  After that happened, I discarded it behind the barn.  There was an Insane Ghoul who used to roam back there.  Perhaps he picked it up.  Rumor has it the devilish beast resides in the Tranquil Gardens Cemetery.",
  },
  [178] = {
    ["T"] = "Theocritus\' Retrieval",
    ["O"] = "Bring the Faded Shadowhide Pendant to Theocritus the Mage.",
    ["D"] = "<You hear a disembodied voice...>$B$BDo not be alarmed.  I am Theocritus, High Mage of Tower Azora in Elwynn Forest.  The pendant you are holding is a method of communication between the Shadowhide Gnolls and their master, Morganth.$B$BThrough months of research, I believe I too can communicate through these pendants.  If you can hear this message, then my spell was a success.$B$BBring me this pendant and I will reward you for the service.",
  },
  [179] = {
    ["T"] = "Dwarven Outfitters",
    ["O"] = "Sten Stoutarm would like 8 pieces of Tough Wolf Meat.",
    ["D"] = "What do we have here? You look as though you might need something to keep your hands warm, hm?$b$bI\'ll tell you what would help: a pair of nice, warm gloves. And, being the kind soul that I am, I\'d be more than happy to provide you with a suitable pair. I\'ve one condition, however.$b$bI need you to go get me some wolf meat. Nice arrangement, hm? You bring me some wolf meat, and I\'ll make sure you don\'t lose any digits to frostbite. Well, what do you say?",
  },
  [180] = {
    ["T"] = "Wanted: Lieutenant Fangore",
    ["O"] = "Kill Lieutenant Fangore and return to Magistrate Solomon in Lakeshire with his paw.",
    ["D"] = "Wanted: Lieutenant Fangore$b$bKnown leader of the Shadowhide Gnoll Clan, working under the enemy of the Township and Kingdom, Morganth.  By mandate of Magistrate Solomon, this vile betrayer of Stormwind is wanted dead.  Show his paw to the Magistrate as proof of his death.",
  },
  [181] = {
    ["T"] = "Look To The Stars",
    ["O"] = "Locate Zzarc\'Vul in the southern ogre mound in Duskwood and return his monocle to Viktori in Darkshire.",
    ["D"] = "Now there is just one more item needed to complete this device.  Cog\'s blueprint calls for a lens of some sort.  The only lens I know of that would be large enough will be very difficult to acquire.$b$bThere is an ogre by the name of Zzarc\'Vul who resides in the mound in southern Duskwood.  If you can, bring to me his monocle and I will use that as our lens!",
  },
  [182] = {
    ["T"] = "The Troll Cave",
    ["O"] = "Grelin Whitebeard would like you to kill 14 Frostmane Troll Whelps.",
    ["D"] = "My brother Senir and I were sent to different parts of Dun Morogh to investigate the threat posed by the trolls. The Senate has its hands full with the troggs, so they\'ve no need for further annoyances.$b$bFrom what I\'ve seen, the trolls aren\'t well situated here in Coldridge Valley--mostly the southern cave. I\'d say that the army will not be necessary. A few strong arms should be more than enough.$b$bPerhaps you\'d like to assist in this endeavor? I have the authority to offer compensation for your help.",
  },
  [183] = {
    ["T"] = "The Boar Hunter",
    ["O"] = "Talin Keeneye would like you to kill 12 Small Crag Boars.",
    ["D"] = "Nothing like a day of boar hunting, eh?$b$bThough, here in Coldridge Valley, there are so many boars it almost takes the fun out of it. No need to get them charging. They\'re all angry and ready without any help. In fact, recently, there\'ve been so many boars in the area, it\'s become dangerous for me to do my daily hunting.$b$bLong story short, if you could help me kill some of the boars, I would appreciate it.",
  },
  [184] = {
    ["T"] = "Furlbrow\'s Deed",
    ["O"] = "Bring Furlbrow\'s Deed to Farmer Furlbrow.",
    ["D"] = "This is the deed to an expanse of farmlands within Westfall.  It is signed by a Theodore Furlbrow and cosigned by his wife, Verna.  And on the back of the deed are hastily scrawled words:$B$B\"We leaned on Furlbrow and got his deed.  Thought it might be handy if you wanted to forge one of these for your own place.$B$BThe Furlbrows won\'t give us trouble.  Last I saw them they were on their way out of Westfall, stuck with a broken wagon.\"$B$BYou think the Furlbrows might want their deed back...",
  },
  [185] = {
    ["T"] = "Tiger Mastery",
    ["O"] = "Ajeck Rouack of Nesingwary\'s Expedition wants you to kill 10 Young Stranglethorn Tigers.",
    ["D"] = "When I was but a young girl, my father trained me in the art of tiger hunting.$b$bYou\'ll find the young felines much easier to track and slay.  For this reason, we\'ll start you out easy.  One can often find young tigers near the expedition camp.$b$bTest your tracking skills and see if you can hunt some of the beasts down.",
  },
  [186] = {
    ["T"] = "Tiger Mastery",
    ["O"] = "Ajeck Rouack of Nesingwary\'s Expedition wants you to kill 10 Stranglethorn Tigers.",
    ["D"] = "You are learning, $c.  Let us test your skills with the mature cats now. Kill 10 Stranglethorn Tigers this time out.",
  },
  [187] = {
    ["T"] = "Tiger Mastery",
    ["O"] = "Ajeck Rouack of Nesingwary\'s Expedition wants you to kill 10 Elder Stranglethorn Tigers.",
    ["D"] = "Now I\'m going to up the stakes on you and truly test your skill.  Prove to me you can kill 10 Elder Stranglethorn Tigers.  If you can do that, you will be ready for your final challenge before I declare you a master tiger hunter in Master Nesingwary\'s presence.$b$bElder Stranglethorn Tigers are not only the most difficult to find, they are also the fiercest to defeat.",
  },
  [188] = {
    ["T"] = "Tiger Mastery",
    ["O"] = "Ajeck Rouack of Nesingwary\'s Expedition wants you to kill Sin\'Dall and return with her paw.",
    ["D"] = "Here is the final challenge that I will put forth.  We\'ve been tracking an elusive tiger for weeks.  We call the beast Sin\'Dall.  See if you can do what no $r has done before: hunt down and kill Sin\'Dall.  Bring me her paw as proof of your accomplishment.$b$bTracking her down will be no easy task.",
  },
  [189] = {
    ["T"] = "Bloodscalp Ears",
    ["O"] = "Acquire 15 Bloodscalp Ears and return them to Kebok in Booty Bay.",
    ["D"] = "Damn trolls! They\'ve been killing off our agents in the jungle! I\'ve already lost a good number of my best guards to skirmishes with them. The costs are piling up! How will I explain this to Baron Revilgaz?$b$bDecisive action... Think, Kebok... you weren\'t assigned to the post you have today for sitting around... I\'ve got it! You... $n, is it? You\'ll help me, won\'t you? There\'s money to be had!$b$bHere\'s the deal... you bring me the ears of fifteen Bloodscalp Trolls, and I\'ll compensate you well.",
  },
  [190] = {
    ["T"] = "Panther Mastery",
    ["O"] = "Sir S. J. Erlgadin of Nesingwary\'s Expedition wants you to kill 10 Young Panthers.",
    ["D"] = "If you want to be a part of the hunt with this crack group Hemet has assembled, you\'re going to need to prove yourself an able panther hunter. We\'ll start you out easy -- don\'t you worry. Let\'s see you kill 10 young panthers to start.  Tracking them down is only half the challenge...",
  },
  [191] = {
    ["T"] = "Panther Mastery",
    ["O"] = "Sir S. J. Erlgadin of Nesingwary\'s Expedition wants you to kill 10 Panthers.",
    ["D"] = "Now you\'re ready to turn it up a notch.  If you want to prove yourself worthy of socializing with the likes of these big game hunters, you\'ll need to prove that you can kill 10 panthers.$b$bThose big boys are tougher.  They\'re not as easy as the young ones you were killing earlier.",
  },
  [192] = {
    ["T"] = "Panther Mastery",
    ["O"] = "Sir S. J. Erlgadin of Nesingwary\'s Expedition wants you to kill 10 Shadowmaw Panthers.",
    ["D"] = "Now the hard part.  A true panther hunter can show skill by dropping Shadowmaw Panthers, the deadliest in Stranglethorn.  Prove to us that you can slay 10 of these beasts.$b$bIf you think they are hard to track, wait until you attempt to slay one....",
  },
  [193] = {
    ["T"] = "Panther Mastery",
    ["O"] = "Sir S. J. Erlgadin of Nesingwary\'s Expedition wants you to bring him the Fang of Bhag\'thera.",
    ["D"] = "You\'ve almost proven yourself to be a master hunter of panthers.  A panther by the name of Bhag\'thera has been prowling the jungle.  So far he\'s eluded our party.  See if you can use your skills to kill the great Bhag\'thera.$b$bBring me the Fang of Bhag\'thera and you\'ll have earned my respect!",
  },
  [194] = {
    ["T"] = "Raptor Mastery",
    ["O"] = "Hemet Nesingwary wants you to kill 10 Stranglethorn Raptors.",
    ["D"] = "So you think your hunting skills are in tip-top shape?  I can put them to the test in a jiffy.  Head out into that jungle and kill 10 Stranglethorn Raptors.  We\'ll see just how much of a big game hunter you are.$b$bAnd no, I won\'t tell you where you can find them!  Locating the beasts is half the challenge.",
  },
  [195] = {
    ["T"] = "Raptor Mastery",
    ["O"] = "Hemet Nesingwary wants you to kill 10 Lashtail Raptors.",
    ["D"] = "Now let\'s step up the challenge and see if you\'re up to it.  Venture into the jungle and bag yourself 10 of those bloody Lashtail Raptors.  Let\'s see what you\'ve got!",
  },
  [196] = {
    ["T"] = "Raptor Mastery",
    ["O"] = "Hemet Nesingwary wants you to kill 10 Jungle Stalkers.",
    ["D"] = "Let\'s see how you handle this next challenge.  There is a cagey breed of raptor out there.  We call them Jungle Stalkers.  They are far more difficult to track down and kill than the other breeds you\'ve been trying your luck at.  Kill 10 Jungle Stalkers and I\'ll tip you off to the best hunting yet.",
  },
  [197] = {
    ["T"] = "Raptor Mastery",
    ["O"] = "Hemet Nesingwary wants you to kill Tethis, an elusive, dangerous raptor in Stranglethorn.",
    ["D"] = "Because you proved yourself to be such a go-getter in the jungle, let me tell you about a ferocious raptor that even I have failed to kill.  Ajeck here calls this wily raptor Tethis.$b$bIf you want to prove yourself a true master, kill Tethis and bring me his talon.  It would be a feat that no other big game hunter has accomplished.",
  },
  [198] = {
    ["T"] = "Supplies to Private Thorsen",
    ["O"] = "Deliver the Miscellaneous Goblin Supplies to Private Thorsen.",
    ["D"] = "Hmm. Yes, I do have something you could do, actually. Lieutenant Doren and his followers up in the north need their regular supplies from us.$b$bThey haven\'t been delivered, and with the trolls attacking, I\'ll need someone to do it. Doren\'s camp lies northwest of the road at the entrance to Stranglethorn from Duskwood. Give the supplies to Private Thorsen, if he\'s not in the camp, then he\'ll be patrolling south of it. He\'ll take care of them.$b$bMind you, if you fail, you\'ll owe me restitution.",
  },
  [199] = {
    ["T"] = "A Dark Threat Looms",
    ["O"] = "Return to Chief Engineer Hinderweir and show him the clue that you discovered.",
    ["D"] = "You take a sample of the strange smelling powder.",
  },
  [200] = {
    ["T"] = "Bookie Herod",
    ["O"] = "Find Bookie Herod\'s Records.",
    ["D"] = "If Kurzen knows Thorsen\'s secret, then we have a spy in our midst.  We must determine how the secret was leaked!$B$BThorsen\'s contact within Kurzen\'s camp was Bookie Herod.  Herod is Kurzen\'s logistics officer and knows everything about the running of Kurzen\'s Compound.  If you can get Herod\'s notes or records, maybe we can find out how Thorsen was discovered.$B$BWhen we were still with Kurzen, Bookie\'s office was on the top floor of the camp\'s only two-story house.  His records are probably still there.",
  },
  [201] = {
    ["T"] = "Investigate the Camp",
    ["O"] = "Find the hunters\' camp and report its location to Krazek in Booty Bay.",
    ["D"] = "I\'ve gotten reports of a group of hunters that have set up camp upriver of the Venture Company mining camp on Lake Nazferiti.  Now, normally I wouldn\'t care, but improbably, I heard that Hemet Nesingwary Jr. was one of the hunters there.  He\'s certainly well-connected, if you catch my meaning, and you never can be too safe.$b$bWhy don\'t you go check out the camp, $n, see what they\'re about?",
  },
  [202] = {
    ["T"] = "Colonel Kurzen",
    ["O"] = "Kill 6 Kurzen Elite, 4 Kurzen Subchiefs, and bring Kurzen\'s Head to Lieutenant Doren at the Rebel Camp.",
    ["D"] = "You have proven yourself time and again, and your resourcefulness seems to rival even that of Kurzen himself.  You may be the salvation of our band of rebels...if you can pit yourself against Kurzen and prevail!$B$BHe commands his men from deep within the Stockpile.  You must enter that cave, brave through his defenders, and face Kurzen and his leaders.$B$BFarewell, $N.  My hope, and the fate of my men, go with you.",
  },
  [203] = {
    ["T"] = "The Second Rebellion",
    ["O"] = "Kill 15 Kurzen Jungle Fighters.$B$BReturn to Sergeant Yohwa at the Rebel Camp.",
    ["D"] = "We\'re fighting a losing battle with Colonel Kurzen.  Not only is he a brilliant tactician, he outnumbers us at least 10 to 1!  To survive, we need to use his own, covert tactics against him.$B$BEnter his camp to the east and kill his men.  Lots of them.  And get out before reinforcements can close on you.",
  },
  [204] = {
    ["T"] = "Bad Medicine",
    ["O"] = "Bring 7 Jungle Remedies and 1 Venom Fern Extract to Sergeant Yohwa at the Rebel Camp.",
    ["D"] = "To have a chance against Kurzen, we need his healing support neutralized.  He has gathered a large number of Medicine Men who heal his forces with Jungle Remedies and poison our fighters with Venom Fern Extracts.$B$BWe need those remedies and extracts for our own.  Search the Kurzen Compound - his Medicine Men and Jungle fighers will have the remedies, and the extracts are probably stowed in some of the camp\'s supply boxes.",
  },
  [205] = {
    ["T"] = "Troll Witchery",
    ["O"] = "Bring 4 Skullsplitter Fetishes to Brother Nimetz at the Rebel Camp.",
    ["D"] = "While I study those legends you acquired, gather for me implements of evil troll magic.  Enter the territory of the Skullsplitter trolls, a second tribe to the distant south, and acquire Skullsplitter Fetishes from their Witchdoctors and Mystics.",
  },
  [206] = {
    ["T"] = "Mai\'Zoth",
    ["O"] = "Bring the Mind\'s Eye to Brother Nimetz at the Rebel Camp.",
    ["D"] = "Kurzen may be dead, but...that which drove him to evil still lives!  Through my studies I have discovered this: An Ogre Mage - Mai\'Zoth - dwells in an Ogre Mound within the eastern mountains of Stranglethorn.  He is rumored to employ mind-controlling magic, and I am positive that he corrupted Kurzen.$B$BIf Mai\'Zoth is allowed to live, then his evil may influence others, and may even cause another Kurzen to surface.$B$BKill him, and bring me his Mind\'s Eye, the artifact he uses to corrupt others.",
  },
  [207] = {
    ["T"] = "Kurzen\'s Mystery",
    ["O"] = "Find the 4 tablets and bring their legends back to Brother Nimetz.",
    ["D"] = "Colonel Kurzen was once a great, noble man - I must find what caused his fall into depravity!  He may be under the sway of troll magic.  To confirm this, I must study some of their legends.$B$BThere are 4 tablets hidden within the troll ruins of Stranglethorn.  I must see what is written on these tablets!  Transcribe their markings and bring the transcriptions back to me.$B$BThe first tablet is in the Bal\'lal Ruins, the second is in the Vile Reef, and the third and fourth are in the Ruins of Zul\'Kunda.",
  },
  [208] = {
    ["T"] = "Big Game Hunter",
    ["O"] = "Hemet Nesingwary wants you to bring him the head of King Bangalash, the great white tiger.",
    ["D"] = "$N, you are a hunter worthy of our company.  From the battlefields of Lordaeron to the deepest jungle, I have rarely witnessed such prowess with weaponry as you have demonstrated.$b$bYou have killed the ferocious Sin\'Dall, the elusive Bag\'thera and the treacherous Tethis.  But the true prize is the head of King Bangalash.  That white tiger is the reason I am here.  I\'ve had my sights on him for quite some time now.  Kill King Bangalash and your hunting prowess is proven to be second to none.",
  },
  [209] = {
    ["T"] = "Skullsplitter Tusks",
    ["O"] = "Acquire 18 Skullsplitter Tusks and return them to Kebok.",
    ["D"] = "It\'s expensive and hard to kill enough tigers to support the export of tiger fangs to the islands in the South Seas. Luckily, we\'ve developed a technique that allows us to take mundane horn-like objects and turn them into undetectable forgeries.$b$bThe closest match we\'ve found, amazingly, are the tusks of the Skullsplitter trolls.$b$bHey, before you say anything, what the buyer doesn\'t know doesn\'t hurt them, am I right? Bring me a large number of them so we can get to work on the monthly shipment!",
  },
  [210] = {
    ["T"] = "Krazek\'s Cookery",
    ["O"] = "Bring Krazek\'s Crock Pot to Krazek the goblin.",
    ["D"] = "I do most of the cooking at the camp.  And it used to be a lot of work, until I bought a mechanical cooking pot from a goblin merchant in Booty Bay.  The pot is a wonder!  It saved me countless hours...until it broke!$B$BCan you take the pot back to the goblin for me so he can fix it?  His name is Krazek.",
  },
  [211] = {
    ["T"] = "Alas, Andorhal",
    ["O"] = "Bring Araj\'s Phylactery Shard to Commander Ashlam Valorfist at Chillwind Camp, Western Plaguelands.",
    ["D"] = "Alas, the time to attack Andorhal and drive out the lich that controls it is upon us!$B$BInside the ruins of the city is where the lich - Araj the Summoner - holds dominion.  He is guarded by numerous Scourge surrounding the remains of city\'s center, and his personal retinue of guardians attached to him directly is formidable.  You will need numerous allies to even stand a chance of facing him successfully.$B$BDestroy Araj, $N, and bring me a shard from his phylactery as proof!",
  },
  [212] = {
    ["T"] = "A Meal Served Cold",
    ["O"] = "Kill a Cold Eye Basilisk, get a Chilled Basilisk Haunch, and return it to Angus Stern in the Blue Recluse.",
    ["D"] = "One of my most famous dishes is Chilled Basilisk in Lime Vinaigrette.  But the problem is... the meat is impossible to keep fresh, and I must serve the dish tonight for a noble\'s birthday!$B$BSo if you can bring me a Chilled Basilisk Haunch from a freshly-killed Cold Eye Basilisk, quickly, then I will owe you a king\'s feast!$B$BCold Eye Basilisks are known to roam along the beach dunes of northwestern Stranglethorn, so you have much traveling to do.  And be swift!",
  },
  [213] = {
    ["T"] = "Hostile Takeover",
    ["O"] = "Retrieve 8 Tumbled Crystals and return them to Kebok in Booty Bay.",
    ["D"] = "He\'s done it this time! Bad enough that Gelriz\'s muscling out the moguls who were appointed by the trade princes, now he tries to cut in on the most notorious pirate!$b$bRevilgaz won\'t have it, and he\'s told me to take care of the problem in my own way.$b$bMy way? Theft. The Venture Co. geologists near Lake Nazferiti are deeply interested in those strange blue crystals they have been finding in the mines. Bring me samples of the stone from their geologists, I don\'t care what you have to do to get them.",
  },
  [214] = {
    ["T"] = "Red Silk Bandanas",
    ["O"] = "Scout Riell at the Sentinel Hill Tower wants you to bring her 10 Red Silk Bandanas.",
    ["D"] = "It\'s no secret that the trademark of the Defias Gang is their Red Bandanas.  But we\'ve learned that the material from which these bandanas are made signifies the member\'s rank.$b$bThat being said, I want you to eliminate as many high ranking Defias members as you can.  The highest ranking members will undoubtedly be found in VanCleef\'s secret hideout.  When you\'re done bring me 10 Red Silk Bandanas as proof of their deaths and I will reward you.  Good luck, $N.",
  },
  [215] = {
    ["T"] = "Jungle Secrets",
    ["O"] = "Speak with Lieutenant Doren at the Rebel Camp.",
    ["D"] = "Thank you for saving me, $N.  I know how this looks, but...allow me to explain.$B$BI was charged by Lieutenant Doren to re-enlist with Kurzen\'s men, so that I might spy on them and bring sorely needed intelligence to the rebels.  You saw here what I had thought would be my chance to infiltrate Kurzen\'s Compound.  I was wrong, and again, thanks for saving me.$B$BThis was a top secret mission, so you must tell no one.  But if you speak with Doren he will explain.",
  },
  [216] = {
    ["T"] = "Between a Rock and a Thistlefur",
    ["O"] = "Take down 8 Thistlefur Avengers and 8 Thistlefur Shaman; most are located east of Zoram Strand in Thistlefur Village.  Once completed, return to Karang Amakkar at Zoram\'gar Outpost, Ashenvale.",
    ["D"] = "The Thistlefur furbolgs are threatening our expansion!  Thistlefur Village to our east blocks the way between us here and the Splintertree Post.  It\'s a path that would circumvent the village of Astranaar; without it, we give the Alliance an extra expansion path.$B$B$N, we must not allow... an expansion path gap!$B$BProceed to Thistlefur Village and thin out the furbolgs.  You will be given compensation for this task, but more importantly you will be doing your duty for the Horde!",
  },
  [217] = {
    ["T"] = "In Defense of the King\'s Lands",
    ["O"] = "Kill the Trogg leader, Grawmug, and his two guards, Gnasher and Brawler then report back to Captain Rugelfuss in the southern guard tower.",
    ["D"] = "$N, our scouts have identified the leader of this recent Trogg incursion.  It appears that these beasts in Loch Modan are following the lead of a chieftain named Grawmug.  Grawmug is heavily guarded, however.  Two of his most elite guards, Gnasher and Brawler, never leave his side.$b$bIf you feel yourself worthy, $N, I want you to lead the assassination mission on Grawmug and his two thugs.  With the Trogg leadership dead, we just might stand a chance at pushing these beasts back into the ground.",
  },
  [218] = {
    ["T"] = "The Stolen Journal",
    ["O"] = "Grelin Whitebeard wants you to kill Grik\'nir the Cold, and retrieve his journal.",
    ["D"] = "My journal! They took it away to the cave. The one that had it... It was a big brute with some odd markings on his skin and face. I didn\'t get a much better look than that.$b$bYou\'ve had some luck with the trolls, maybe you could go get it back for me?",
  },
  [219] = {
    ["T"] = "Missing In Action",
    ["O"] = "Protect Corporal Keeshan on the journey back to Redridge.",
    ["D"] = "At last, friends of the Alliance!$b$bHelp... please... $b$bThe last thing I remember these foul Orcs overpowered our regiment.  Most the men were killed. I took a strong blow to the head and everything went black. Now I am held captive in this cave.  Help me, I am hurt badly.$b$bI\'ll need help getting back to town... please... you\'re my last hope...",
  },
  [220] = {
    ["T"] = "Call of Water (Part 5)",
    ["O"] = "Bring the Vial of Purest Water to Islen Waterseer in the Barrens.",
    ["D"] = "Islen will have one more task for you before rewarding you with your next totem. The vial of water I\'ve given you is necessary for whatever that task is.$B$BGo now and find her. We shall meet again some day.",
  },
  [221] = {
    ["T"] = "Worgen in the Woods",
    ["O"] = "Kill 12 Nightbane Dark Runner worgen for Calor in Darkshire.",
    ["D"] = "You might have noticed some larger worgen wandering around with the Shadow Weavers in the woods? From what we can tell, these Dark Runners make up the bulk of the worgen numbers.$b$bOn my rangings, I\'ve also noticed that they have overrun the Rotting Orchard southwest of town. These worgen are a bit tougher than the last you faced.$b$bBe on your guard.",
  },
  [222] = {
    ["T"] = "Worgen in the Woods",
    ["O"] = "Kill 8 Nightbane Vile Fang and 8 Nightbane Tainted One worgen for Calor in Darkshire.",
    ["D"] = "Your previous accomplishments have convinced me that you are ready to take on the toughest worgen infesting the woods.$b$bOf the worgen that have made their new home here, the Vile Fangs and the Tainted Ones have proven the most dangerous. They\'ve settled down near some of the caves and in the mine to the south.$b$bFrom far away you can even see the light from their bonfires...",
  },
  [223] = {
    ["T"] = "Worgen in the Woods",
    ["O"] = "Bring Calor\'s note to Jonathan Carevin.",
    ["D"] = "Here you go, $n. Bring this message to Master Carevin.$b$b<He quickly removes a piece of faded parchment and offers it to you.>$b$bA few more like you, and we will outnumber the Night Watch! Perhaps then we could complete the work that we few carry on today.",
  },
  [224] = {
    ["T"] = "In Defense of the King\'s Lands",
    ["O"] = "Mountaineer Cobbleflint of the southern guard tower wants you to kill 10 Stonesplinter Troggs and 10 Stonesplinter Scouts.",
    ["D"] = "We need to protect the Loch, $N!  With so many of the King\'s soldiers fighting valiantly on remote battlefields, we\'ve become overwhelmed on the home front.  Troggs are tunneling up from every crevice!  The Trogg infestation poses the largest threat to Ironforge.  These disgusting mutants must be destroyed.$b$bWe need you, brave adventurer, to venture forth and lay waste to the Trogg threat.  Slay 10 Stonesplinter Troggs and 10 Stonesplinter Scouts and report back.",
  },
  [225] = {
    ["T"] = "The Weathered Grave",
    ["O"] = "Speak with Sirra Von\'Indi in Darkshire.",
    ["D"] = "The weathered grave marker reads simply:$b$bMORGAN LADIMORE$b$b<Upon further examination, you notice that dirt over the grave has been recently disturbed, and that a good amount of the dirt seems to have been displaced...>",
  },
  [226] = {
    ["T"] = "Wolves at Our Heels",
    ["O"] = "Kill 12 Starving Dire Wolves and 8 Rabid Dire Wolves, then return to Lars at Sven\'s Camp on the western border of Duskwood.",
    ["D"] = "Sven and I have dangerous days ahead of us, what with the Necromancer to the east and all.  And out here alone as we are, we have to hunt for our own food.  It seems every time I\'m heading back to camp with some meat on me, starving or rabid dire wolves come out of the forest wanting a bite.  It goes without saying, living out here is dangerous work!$B$BBut if you can rid us of some of those wolves, we\'d have an easier time of it.  They mostly prowl north and east of here, near the river.",
  },
  [227] = {
    ["T"] = "Morgan Ladimore",
    ["O"] = "Speak with Commander Althea Ebonlocke in Darkshire.",
    ["D"] = "<He searches through the shelves and comes up with a leatherbound book.>$b$bIf you would like to know more, you might ask Althea, she\'s been the one to handle the... trouble... with him of late.",
  },
  [228] = {
    ["T"] = "Mor\'Ladim",
    ["O"] = "Kill Mor\'Ladim, then return his skull to Commander Althea Ebonlocke in Darkshire.",
    ["D"] = "From what my scouts tell me, Mor\'Ladim wanders throughout Duskwood, following a strange and meandering path through the cemetery. We buried him out past the house on the hill, you know where that is?$b$bThere isn\'t anything I can do to help you, but I wish you good luck.",
  },
  [229] = {
    ["T"] = "The Daughter Who Lived",
    ["O"] = "Speak with Watcher Sarah Ladimore in Darkshire.",
    ["D"] = "I should have told you this earlier...$b$bHow do I put this...$b$bMorgan may have believed that his family was all dead, but, in fact, his daughter Sarah Ladimore is now a Watcher. She\'s always been troubled by the... circumstances... surrounding her father\'s death. Perhaps you could go to bring the news to her.",
  },
  [230] = {
    ["T"] = "Sven\'s Camp",
    ["O"] = "Bring the Book from Sven\'s Farm to Sven.",
    ["D"] = "Digging through the mound, you find a small, dirt-stained book.$B$BThe book has no title, but...it has information Sven would like to know.",
  },
  [231] = {
    ["T"] = "A Daughter\'s Love",
    ["O"] = "Take Sarah Ladimore\'s ring to Morgan Ladimore\'s grave.",
    ["D"] = "Here, take this and lay it on his grave. Maybe... somehow, he\'ll know that I\'m okay, and that none of us hold him responsible for what happened.$b$b<There are tears in her eyes now, as she slides a ring from her right hand, and presses it into your hands.>$b$bAnd... thank you, $n.",
  },
  [232] = {
    ["T"] = "Errand for Apothecary Zinge",
    ["O"] = "Take the purchase order to Alessandro Luca in the Undercity.",
    ["D"] = "The Royal Apothecary Society needs your assistance.  Listen closely, and both of us may profit from my plan.$B$BApothecary Keever in the next room may be... touched... but his research does potentially bear fruit if it were pursued by a clever researcher.  Fortunately, I am such a researcher.  I want you to take this purchase order to Alessandro Luca, the proprietor of Blue Moon Odds and Ends; he\'ll give you a testing kit.  When you have it, bring it back to me and I\'ll outline your task in detail.",
  },
  [233] = {
    ["T"] = "Coldridge Valley Mail Delivery",
    ["O"] = "Deliver the stack of letters to Talin Keeneye.",
    ["D"] = "Hm, I don\'t suppose you\'d be willing to do a favor for me, $g lad : lass;? A stack of letters came through the pass today, but I don\'t have the time to send them along.$b$bThey\'re all addressed to Talin Keeneye. You can find him to the west down the road. He\'s set up camp next to the frozen lake.$b$bWhat do you say?",
  },
  [234] = {
    ["T"] = "Coldridge Valley Mail Delivery",
    ["O"] = "Deliver the letter to Grelin Whitebeard.",
    ["D"] = "If I remember correctly, Grelin\'s camp is down the road to the southeast. No doubt he\'ll be eager to get his mail.",
  },
  [235] = {
    ["T"] = "The Ashenvale Hunt",
    ["O"] = "Speak with Senani Thunderheart at Splintertree Post, Ashenvale.",
    ["D"] = "Attention, young adventurers!  The wilds of Ashenvale await you!$B$BThe Horde has established a strong presence in the lands north of the Barrens.  Our two outposts - Splintertree Post and the Zoram Strand Outpost - strive to bring glory to the Horde!  Those who would prove themselves should seek out guidance there.  Of note: Senani Thunderheart in Splintertree Post, directly north of the Barrens, seeks adventurers willing to take part in a great hunt of Ashenvale!",
  },
  [237] = {
    ["T"] = "In Defense of the King\'s Lands",
    ["O"] = "Mountaineer Gravelgaw in the southern guard tower wants you to kill 10 Stonesplinter Skullthumpers and 10 Stonesplinter Seers and report back to him.",
    ["D"] = "Mountaineer Cobbleflint had nothing but good things to say about you, $r.  For that reason I am going to entrust upon you a mission of utmost importance.  We need to keep pressure on the invading Trogg forces until our Dwarven brethren return from the Alliance front.$b$bSet forth into the southern hills and kill 10 Stonesplinter Skullthumpers and 10 Stonesplinter Seers.  Your attacks will buy us some time.  Report back when your mission is complete.",
  },
  [238] = {
    ["T"] = "Errand for Apothecary Zinge",
    ["O"] = "Bring the Field Sampling Kit to Apothecary Zinge in the Undercity.",
    ["D"] = "The kit is ready, $n.  No doubt that Apothecary Zinge will want this as soon as possible.  Take it to her without delay, and let her know that I will add the cost of the kit to the Society\'s tab.  Also, please extend to her my warmest salutations.$B$BWell, don\'t just stand there.  Such is the life of a simple courier, yes?",
  },
  [239] = {
    ["T"] = "Westbrook Garrison Needs Help!",
    ["O"] = "Go to the Westbrook Garrison and speak with Deputy Rainer.",
    ["D"] = "The Garrison on our western border sends word of increasing gnoll and thief activity.  They\'re requesting we send more Stormwind soldiers... but we just don\'t have any to spare!$B$BIf you can help, we could use it.  Go and speak with Deputy Rainer at the Westbrook Garrison and see what he needs done.$B$BThe Garrison is down the road to the west.  After you cross the bridge over the small brook it will be to the right.",
  },
  [240] = {
    ["T"] = "Return to Jitters",
    ["O"] = "Return to Jitters with the Dusky Crab Cakes.",
    ["D"] = "And here we are - \"Crab\" Cakes ala Grual!",
  },
  [243] = {
    ["T"] = "Into the Field",
    ["O"] = "Speak to Chief Engineer Bilgewhizzle in Gadgetzan about getting a power source for the testing kit.",
    ["D"] = "You will be headed to the Tanaris desert, testing the tissue consistency of the basilisks, scorpids, and hyena native there.  Obtain tissue samples and use them with the testing kit in tow; expect a chunk of the samples to fail.  I need eight acceptable samples of each creature type.$B$BIn addition, the goblins of Gadgetzan have a power source that must be used with this kind of modified kit.  Speak to Chief Engineer Bilgewhizzle and acquire one for the kit.$B$BReturn to me only when you are successful.",
  },
  [244] = {
    ["T"] = "Encroaching Gnolls",
    ["O"] = "Report to Deputy Feldon.",
    ["D"] = "Hail, $c.  I am charged to patrol this stretch of road.  Although the road is safe for now, I\'ve seen gnoll encampments to the north and east of here.$B$BLakeshire must know of the gathering gnoll force!  Report to Deputy Feldon in Lakeshire and tell him of the gnolls.  Do this, and I\'m sure Feldon will offer you a scout\'s wage.$B$BFeldon\'s usually inspecting the area south of the bridge to Lakeshire.",
  },
  [245] = {
    ["T"] = "Eight-Legged Menaces",
    ["O"] = "Kill 15 Pygmy Venom Web Spiders, and then report back to Watcher Dodds in Duskwood.",
    ["D"] = "Hail, $c. Perhaps you\'ve seen those spiders that have taken over the western border? The eight-legged menaces are too much for us to handle, and the Commander hasn\'t enough manpower to spare so far from Darkshire.$b$bI hate to ask this of you, but you seem like you might be able to handle them. I can\'t promise you anything of great value, but I can reward you if you can help root out the filthy bugs.$b$bOh, and a word of advice, you\'ll want to avoid their venom if you can.",
  },
  [246] = {
    ["T"] = "Assessing the Threat",
    ["O"] = "Kill 10 Redridge Mongrels and 6 Redridge Poachers, then report back to Deputy Feldon in Lakeshire.",
    ["D"] = "We don\'t have men to spare, but something must be done about those gnolls you reported to me.$B$BScout southern Redridge for gnolls.  Harry them, kill those you can and report back to me with a count of their numbers and an assessment of the threat they present.  You may find those gnolls camped all along the southern road of Redridge.$B$BDo this for the Stormwind Army, $N, and you will be rewarded.",
  },
  [247] = {
    ["T"] = "The Hunt Completed",
    ["O"] = "",
    ["D"] = "",
  },
  [248] = {
    ["T"] = "Looking Further",
    ["O"] = "Climb the intact watch tower beside Stonewatch Keep.$B$BFind a suitable container to place the Glyph of Azora.",
    ["D"] = "I have been watching the Tower of Ilgalar, and can detect that Morganth is in conflict with the Orcs in Stonewatch Keep.  I do not know the reason behind their contention, but you may aid me in unraveling this mystery.$B$BTake another Glyph of Azora, then go to the top of the intact watch tower beside Stonewatch Keep.$B$BFind a suitable object on which to place the Glyph.  A chest or a jar -- some place where the glyph will not be noticed -- will suit my purpose.",
  },
  [249] = {
    ["T"] = "Morganth",
    ["O"] = "Defeat Morganth.$B$BBring his Pendant of Shadow to Theocritus at the Tower of Azora in Elwynn Forest.",
    ["D"] = "After much observation, I have discovered Morganth\'s plans.$B$BHe searches for the Scythe of Elune...and he may have found it!  If he has, then the Redridge Mountains, and the kingdom of Stormwind, will soon feel his dark might.$B$BI am not prepared to pit myself against Morganth, but I fear he must be dealt with now.  So you must enter the Tower of Ilgalar and face Morganth.$B$BDefeat him, and bring me his Pendant of Shadow.  Do this, and the kingdom will owe you much.",
  },
  [250] = {
    ["T"] = "A Dark Threat Looms",
    ["O"] = "Investigate the area near the eastern ramp of the Dam and bring Chief Engineer Hinderweir a clue that might reveal what the Dark Iron Dwarves are up to.",
    ["D"] = "Thank the stars someone is concerned about the well-being of the Dam.  First the destruction of the Thandol Span and then the pillaging of Dun Modr!  I have no doubt that the Dam will be the target of the next Dark Iron threat.  Most of the security detail here was reassigned to the Alliance Front, including my top inspector.$b$bThat\'s why I need you, $N.  I spotted some Dark Iron Sappers milling about the eastern ramp of the Dam.   Investigate the area and bring back a clue.",
  },
  [251] = {
    ["T"] = "Translate Abercrombie\'s Note",
    ["O"] = "Bring the Letter to Ello to Sirra Von\'Indi.",
    ["D"] = "Take the note you gave me to Sirra Von\'Indi.  He\'s very learned in languages and ancient arts - if anyone here can translate this message, it\'s he.",
  },
  [252] = {
    ["T"] = "Translation to Ello",
    ["O"] = "Bring the Translated Letter to Lord Ello Ebonlocke",
    ["D"] = "Oh my!  If what is written on this note is true, then Abercrombie\'s done something...unspeakable.$B$BYou better bring the translated note back to Ello.$B$BAnd do so quickly, for I fear a doom already lumbers toward our town...",
  },
  [253] = {
    ["T"] = "Bride of the Embalmer",
    ["O"] = "Find Eliza\'s grave.  Retrieve the Embalmer\'s Heart from her, then return to Ello Ebonlocke.",
    ["D"] = "The Embalmer is a name told in one of our children\'s stories.  He was a kindly alchemist, driven mad by the death of his wife, Eliza.  To restore her, he used dark magic to place his own heart within the bosom of his dead spouse.$B$BThis revived Eliza...but it cursed her with a hunger for human flesh, forcing the Embalmer to keep her buried.$B$BHis heart is his power.  Find his wife\'s grave, retrieve his heart, and bring it to me while it still beats.  Do this and his power will be broken.",
  },
  [254] = {
    ["T"] = "Digging Through the Dirt",
    ["O"] = "",
    ["D"] = "",
  },
  [255] = {
    ["T"] = "Mercenaries",
    ["O"] = "Magistrate Bluntnose of Thelsamar has hired you to kill 4 Mo\'grosh Ogres, 4 Mo\'grosh Brutes and 4 Mo\'grosh Enforcers.",
    ["D"] = "Two months just to hear from Ironforge that we\'ll be spared a handful of soldiers for our own defense, and another two months until they arrive! Bad enough that our town goes unprotected, but the excavation and the dam--the dam!--are open to attacks as well.$b$bI\'ve no choice then, it seems. I\'ll need hired arms.$b$bHow about you? You seem a likely sort, $c. A group of ogres has set up camp on the northeast of the lake. Perhaps you will be the one to remove the threat to our town?",
  },
  [256] = {
    ["T"] = "WANTED: Chok\'sul",
    ["O"] = "Kill Chok\'sul and bring his head to Magistrate Bluntnose of Thelsamar.",
    ["D"] = "By order of the Magistrate of Thelsamar:$b$bChok\'sul, the ogre presumed to be the leader and coordinator of attacks against the town of Thelsamar, Stonewrought Dam, and the excavation site, is wanted dead through any means necessary.$b$bMonetary compensation will be provided when proof is brought to the Magistrate of Chok\'sul\'s death.$b$bChok\'sul was last seen at the ogre encampment in the northeastern part of Loch Modan.",
  },
  [257] = {
    ["T"] = "A Hunter\'s Boast",
    ["O"] = "Kill 6 Mountain Buzzards and return to Daryl the Youngling in the Farstrider Lodge within 15 minutes.",
    ["D"] = "Think you can match wits with Daryl the Bold, huh? I should think not! Of course, you\'re more than welcome to try. Here\'s a challenge that should prove to be above your abilities, so don\'t feel too bad if you can\'t face up to it.$b$bA flock of buzzards has nested here in Loch Modan. Why don\'t you try to take down some of the beasts? Tell you what, if you meet my challenge in fifteen minutes, I\'ll give you my bow or gun.$b$b<He looks you over dismissively.>$b$bIt seems you haven\'t much to lose, anyways.",
  },
  [258] = {
    ["T"] = "A Hunter\'s Challenge",
    ["O"] = "Kill 5 Elder Mountain Boars and return to Daryl the Youngling in the Farstrider Lodge within 12 minutes.",
    ["D"] = "$nath, is it? I can tell you\'re bursting with pride over completing the first test, hm? As I told you before, it\'s no large feat.$b$bYou should try your hand at boar hunting. Trust me, this is no Coldridge Valley boar hunt, so you\'d best have a care with them. I\'ll give you just twelve minutes this time.$b$bDon\'t feel bad if you fail. I\'d give you the shirt off my back if you succeeded!$b$bHave I ever related to you the story of how I received my famous scar? No? It was two years ago...",
  },
  [261] = {
    ["T"] = "Down the Scarlet Path",
    ["O"] = "Destroy 30 Undead Ravagers, then return to Brother Anton at Nijel\'s Point.",
    ["D"] = "I will be frank.  We are at war with the Scourge.  It is an evil that corrupts our people and infects our land.  It must be stopped before it overwhelms us and drags our world into shadow.  We of the Scarlet Crusade have sworn to fight the Scourge with body and soul.$B$BIf you are with us, then gather your courage and prove your allegiance--destroy the undead ravagers of Desolace, in the Valley of Bones to the south.$B$BDo this, and the Crusade will embrace you.",
  },
  [262] = {
    ["T"] = "The Shadowy Figure",
    ["O"] = "Take the book you found at Sven\'s farm and show it to townsfolk in Darkshire.",
    ["D"] = "I must know who was that skulker in the shadows I saw the night my family was killed!$B$BPlease, $N, you have shown your resourcefulness.  Please find that shadowy figure.  I must learn what secrets he holds!$B$BI do not have clues that you do not already know, but here - take the book you found at my farm.  Perhaps some of the townsfolk can link this book with its owner.",
  },
  [263] = {
    ["T"] = "In Defense of the King\'s Lands",
    ["O"] = "Mountaineer Wallbang in the southern guard tower wants you to kill 10 Stonesplinter Shaman and 10 Stonesplinter Bonesnappers.",
    ["D"] = "The Mountaineers tell me you are quite brave and capable, $N.  We need a $r like you around here.  The Trogg problem isn\'t getting any better.  The reserves have been called to the front and we\'re all alone out here.  But now that we have a seasoned $c here, let\'s see what you can do.$b$bGo out and kill 10 Stonesplinter Shaman and 10 Stonesplinter Bonesnappers.  Let\'s see if you live up to your reputation, Trogg-Slayer.",
  },
  [264] = {
    ["T"] = "Until Death Do Us Part",
    ["O"] = "Place Clarice\'s Pendant on Yuriv\'s Tomb in Silverpine.",
    ["D"] = "Filthy scum! Leaves me for his damn crusade. Because \"the Light is the most important thing we have against the threats the undead pose.\"$B$BWell, what about his children?! What about ME?! Night after night I sat patiently waiting for him... always second to his blasted duty!$B$BWell, witness what \"justice\" brings you, $N! He\'s dead and I\'m bearing the very thing he was trying to stop!$B$BTake this piece of junk and put it on his tomb at the Sepulcher. I want nothing to do with it anymore... or him!",
  },
  [265] = {
    ["T"] = "The Shadowy Search Continues",
    ["O"] = "See if other townsfolk have clues about the shadowy figure.",
    ["D"] = "Here is your book back, $N, and I bid you fortune with your search.  If you ask others in town, perhaps they can offer you more clues.$B$BAnd here is one more for you - although the author of this book is a stranger to Darkshire, the book itself was purchased here.",
  },
  [266] = {
    ["T"] = "Inquire at the Inn",
    ["O"] = "Search the inn for clues about the shadowy figure.",
    ["D"] = "Here is your book, and good luck with your inquiries.$B$BYou might want to expand your search to the inn.  If the person whom you seek spent time in town, then chances are someone saw him there.",
  },
  [267] = {
    ["T"] = "The Trogg Threat",
    ["O"] = "Bring 8 Trogg Stone Teeth to Captain Rugelfuss in the southern guard tower.",
    ["D"] = "$C, you may or may not be aware of the Trogg threat looming over Dwarven lands.  With the Ironforge Reserve called up to the Alliance Front, we are left with a fraction of the defense forces needed to keep these lands safe.  My regiment is assigned to watch over the Gate here and we cannot leave our post for fear of invasion.$b$bBut we need some pressure put on those damned Troggs lurking in the hills.  If you\'re up to the task, wage an assault on the Troggs.  Bring me back 8 Trogg Stone Teeth as proof.",
  },
  [268] = {
    ["T"] = "Return to Sven",
    ["O"] = "Return to Sven with Jitters\' completed journal.",
    ["D"] = "The book you found was only half finished.  I had to leave it at Sven\'s farm when I fled there...$B$BHere.  Take my completed journal to Sven.  It describes more fully what happened at his farm.$B$BAlthough this journal has been my only company these past weeks, if giving it up keeps me from having to face Sven and his wrath, then I do it willingly!",
  },
  [269] = {
    ["T"] = "Seeking Wisdom",
    ["O"] = "Give Jitters\' Completed Journal to Bishop Farthing in Stormwind.",
    ["D"] = "Go to the city of Stormwind and speak with Bishop Farthing in the Cathedral of Light.  Give him Jitters\' journal and tell him you mean to face Morbent Fel.$B$BAlthough Stormwind has forsaken Darkshire, Farthing is a kind soul and very wise.  He may help you on your quest to save us.$B$BYour mission is perilous, but hurry, $N.  With each passing hour, the shadows of Duskwood grow ever darker.",
  },
  [270] = {
    ["T"] = "The Doomed Fleet",
    ["O"] = "Go to Menethil Harbor in the Wetlands, and speak with Glorin Steelbrow.",
    ["D"] = "Years ago, the Third Fleet of Kul\'Tiras sunk along the coasts of The Wetlands, an expanse of marsh north of the Dwarven kingdom of Ironforge.  Aboard one ship was a load of Lightforge Iron.$B$BThis metal is precious to us, for items crafted from it strike with holy truth.  If you are to face Morbent Fel, then you will need such a weapon.$B$BGo to Menethil Harbor and speak with my dwarven colleague Glorin Steelbrow.  If that lost metal can be found, he\'ll know where to look.",
  },
  [271] = {
    ["T"] = "Vyrin\'s Revenge",
    ["O"] = "Kill Ol\' Sooty then show your handiwork to Daryl the Youngling at the Farstrider Lodge.",
    ["D"] = "I don\'t suppose you\'d be interested in a little payback for His Boldness? Daryl does have some skill, it pains me to say.$b$bA year ago, a large bear had been the target of the Farstrider Lodge, but none of the hunters had any luck with him. So, Daryl thought he\'d try his hand against him. All he had to show for his effort was that ugly scar over his eye.$b$bKill the bear, and I\'m sure even Daryl would be speechless for once.$b$bThe bear, Ol\' Sooty, can be found around his lair in the hills over Thelsamar.",
  },
  [272] = {
    ["T"] = "Trial of the Sea Lion",
    ["O"] = "Find the Half Pendant of Aquatic Agility and the Half Pendant of Aquatic Endurance.  Speak with the residents of Moonglade to learn clues as to where these items may be located.$B$BForm the Pendant of the Sea Lion from the two pendant halves.  You need to be in proximity of the Shrine of Remulos to do this.$B$BBring the joined pendant to Dendrite Starblaze in the village of Nighthaven, Moonglade.",
    ["D"] = "For the second trial, you\'ll need the two halves that make the Pendant of the Sea Lion.  One half draws power from the agility of the sea lion aspect, gliding through water; the other draws power from the incredible endurance of the sea lion aspect.  Druids draw on both these traits equally to embody the aspect of their aquatic form.$B$BSpeak with the locals of Moonglade to learn where the parts may lie, and bring both here to join them together.  Once formed, take the pendant to Dendrite Starblaze.",
  },
  [273] = {
    ["T"] = "Resupplying the Excavation",
    ["O"] = "Speak with Huldar.",
    ["D"] = "It\'s not too late though, you\'re welcome to go look for Huldar. He can\'t have made it very far, strong as they are, Miran and Saean can only move so fast laden down with those barrels.$b$bThey\'re taking the normal route to the site, leaving south out of town and following the curve of the Loch.$b$bShouldn\'t have a problem finding them unless they\'ve already gotten there.",
  },
  [274] = {
    ["T"] = "A Dark Threat Looms",
    ["O"] = "Ashlan Stonesmirk wants you to return to Chief Engineer Hinderweir and inform him of the new discovery.",
    ["D"] = "This ain\'t no ordinary blast powder.  Look at the tiny silver crystals.  And the distinct smell!  Why it\'s clear as daylight that this is Seaforium Powder.  Seaforium is harmless enough.  But once it\'s wet it could blow Ironforge out of the mountain.$b$bThe chemical reaction can be defused by mixing four components:  lurker venom, crushed Mo\'Grosh crystal, a crocolisk tear and this disarming colloid.  Now tell Hinderweir before it\'s too late!",
  },
  [275] = {
    ["T"] = "Blisters on The Land",
    ["O"] = "Kill 8 Fen Creepers, then return to Rethiel the Greenwarden in the Wetlands.",
    ["D"] = "I do not know why, but a festering grows within the Wetlands.  Fen Creepers have risen.  They are diseased, fevered blisters on the land, and for the fever to break they must be dispatched and allowed to settle back into the marshes.$B$BThe Fen Creepers shamble amidst the waterways of the Wetlands.",
  },
  [276] = {
    ["T"] = "Tramping Paws",
    ["O"] = "Kill 15 Mosshide Gnolls and 10 Mosshide Mongrels, then return to Rethiel the Greenwarden in the Wetlands.",
    ["D"] = "The Mosshide Gnolls have lived in the Wetlands peacefully for many years, but now grow in numbers.  Their feet stamp flat the fen\'s bushes and flowers, and their foul axes cut too much wood to fuel their fires.$B$BThey are no longer in balance with the land.  For the Wetlands to survive, the Gnolls must be reduced.$B$BKill 15 Mosshide Gnolls and 10 Mosshide Mongrels.  Hunt them to the south, near the Dwarven gate of Algaz.  After you do this, return to me.",
  },
  [277] = {
    ["T"] = "Fire Taboo",
    ["O"] = "Bring Rethiel the Greenwarden 9 Crude Flints.",
    ["D"] = "The Mosshides are cutting the trees of the Wetlands, using the trees\' flesh to warm themselves with fire.  I will suffer this pain no longer!$B$BRid the Mosshides of their crude flints, the tools they use to make fire.  The flints are borne by all Mosshides except the smaller Mosshide gnolls and Mosshide mongrels.",
  },
  [278] = {
    ["T"] = "A Dark Threat Looms",
    ["O"] = "Chief Engineer Hinderweir wants you to gather Lurker Venom, a Mo\'grosh Crystal, and a Crocolisk Tear.",
    ["D"] = "Begin collecting the disarming materials immediately, $N.  Lurker Venom can be found on the indigenous spiders in Loch Modan.  Crocolisk tears are found here in the Loch as well.  But a Mo\'Grosh crystal will be very difficult to procure.  The ogres to the northeast mine them but the crystals are a very rare commodity.$b$bWe need to be prepared to defuse a Dark Iron attack!  Return to me once you\'ve collected the needed items and I will prepare the mixture.",
  },
  [279] = {
    ["T"] = "Claws from the Deep",
    ["O"] = "Kill 12 Bluegill Murlocs.$B$BSlay Gobbler and take his head.$B$BBring Gobbler\'s Head to Karl Boran in Menethil Harbor.",
    ["D"] = "Murlocs are crawling out from the deep waters and building their villages on the coastline.  They are harassing our fishers and merchants and must be stopped.$B$BOne of these murlocs, Gobbler, skulks with other bluegill murlocs and harries merchants along the road, always then retreating to the safety of the nearby murloc hovels.  Our merchants are in jeopardy, and we will pay to secure them.$B$BGobbler prowls the river inlet north of here.  Find him, slay his kin, and bring me his head.",
  },
  [280] = {
    ["T"] = "A Dark Threat Looms",
    ["O"] = "Chief Engineer Hinderweir wants you to swim down to the base of the dam, locate the powder keg and stir in the Disarming Mixture to prevent an explosion.",
    ["D"] = "Earlier today I spotted some Dark Iron Insurgents swimming toward the Dam with a large keg.  No Mountaineers were on hand to stop them!  I\'m afraid they\'ve planted the Seaforium at the base of the Dam and it could blow at any minute!  Let me combine all these ingredients into the Disarming Mixture.  There we go.$b$bNow I need you to take this mixture and stir it into the keg before it\'s too late!  Hurry!",
  },
  [281] = {
    ["T"] = "Reclaiming Goods",
    ["O"] = "Search the crates and barrels at the coastal Murloc camps.",
    ["D"] = "Not long ago a merchant vessel, the Blind Princess, was raided by Murlocs.  Although the ship escaped intact, much of its cargo was dragged overboard by the creatures, including a very valuable statuette of Terenas Menethil.  We want that statuette recovered.$B$BSearch the coastal Murloc hovels for crates, barrels and other evidence of lost cargo.  Find the Menethil Statuette and return it to me.",
  },
  [282] = {
    ["T"] = "Senir\'s Observations",
    ["O"] = "Speak with Mountaineer Thalos.",
    ["D"] = "I was planning on sending my apprentice to my brother Senir with my report, but I would feel much better were it placed in more... reliable hands. That is, of course, if you wouldn\'t mind?$b$bHmm... you\'ll have to take the tunnel to get to Kharanos.$b$bSpeak with Mountaineer Thalos before going through the tunnel. It\'s completely infested with troggs now.$b$bFollow the road back to Anvilmar, then keep heading east to the tunnel, Thalos is stationed nearby.",
  },
  [283] = {
    ["T"] = "A Dark Threat Looms",
    ["O"] = "The Disarming Mixture seemed to take effect.  Return to Chief Engineer Hinderweir to report the good news.",
    ["D"] = "The keg fizzles slightly.",
  },
  [284] = {
    ["T"] = "The Search Continues",
    ["O"] = "Search for the Menethil Statuette.",
    ["D"] = "You thoroughly search this cluster of Murloc hovels, and find no trace of the Menethil Statuette.$B$BPerhaps one of the two nearby hovels to the north and northeast will have more clues.",
  },
  [285] = {
    ["T"] = "Search More Hovels",
    ["O"] = "Continue searching the Murloc hovels for the Menethil Statuette.",
    ["D"] = "This grouping of Murloc hovels, after a thorough inspection, reveals nothing.$B$B...but you\'re certain that the next clue you find will be fruitful.$B$BOne of the hovels to the north must have the Menethil Statuette!",
  },
  [286] = {
    ["T"] = "Return the Statuette",
    ["O"] = "Bring Karl Boran the Menethil Statuette.",
    ["D"] = "After pulling the barrel free from the muck and opening it, a silvery figurine is found packed in fine straw.  It depicts a young, long-haired Paladin with hammer upraised.$B$BThis must be the Menethil Statuette!  You must now return it to Karl Boran in Menethil Harbor.",
  },
  [287] = {
    ["T"] = "Frostmane Hold",
    ["O"] = "Explore Frostmane Hold, and kill 5 Frostmane Headhunters for Senir Whitebeard in Kharanos.",
    ["D"] = "I\'m behind on my report, and could use your help, $n. I just managed to find where the trolls are holed up, but I saw so many trolls, I was afraid to go in.$b$bHere\'s what I need... Go down to the cave, poke around inside, kill a few of the trolls, then come back here.$b$bTake the road north out of Kharanos, when you get to the bridge, follow the frozen river west until you reach Iceflow Lake. You\'ll find Brewnall Village on the west bank. The Hold is southwest of the village.",
  },
  [288] = {
    ["T"] = "The Third Fleet",
    ["O"] = "Buy First Mate Fitzsimmons a Flagon of Mead.",
    ["D"] = "I was once First Mate on the Wave Mistress, ship-of-the-line in the Kul Tiras Third Fleet.  The Doomed Fleet.  Doomed, for on its last voyage the fleet was attacked  by Red Dragons.  Dragons controlled by Orcs!$B$BWe didn\'t stand a chance against that dragonfire...and our fleet was burned and sunk off this here coast.  I\'m the sole survivor of those poor vessels...$B$B...Oh, my head hammers and pounds!  I\'ll need a fresh drink if I\'m to get a handle on this hangover and continue my tale.",
  },
  [289] = {
    ["T"] = "The Cursed Crew",
    ["O"] = "Kill 13 Cursed Sailors, 5 Cursed Marines and First Mate Snellig.  Bring Snellig\'s Snuffbox to First Mate Fitzsimmons in Menethil Harbor.",
    ["D"] = "<Bolstered, Fitzsimmons continues his tale...>$B$BI am the sole survivor of the ill-fated Third Fleet of Kul Tiras, wrecked off the coast to the north.  But although I\'m the only member of the fleet still breathing, I\'m not the only one still walking about!  For on one of our ships was hidden a great, and terrible treasure, and the eerie power of it keeps the souls of my brothers trapped within the ships\' rotting hulls.$B$BBut if you can free some of my brothers, then perhaps you can break their curse.",
  },
  [290] = {
    ["T"] = "Lifting the Curse",
    ["O"] = "Get the Intrepid Strongbox Key from Captain Halyndor.$B$BUse the key to open the Intrepid\'s Locked Strongbox.",
    ["D"] = "To break the curse and free all of my brethren, the...thing...stowed on my ship must be retrieved and cleansed!  I don\'t know what it was, but I do know that it was stored securely in the ship\'s strongbox, which Captain Halyndor lorded over.$B$BMy old captain is still in my dreams.  I know he walks my doomed ship, the Intrepid.  Get the key from him, and use it to open the Intrepid\'s strongbox.$B$BThen, after you have the cursed thing, bring it to the priest Glorin Steelbrow in town.",
  },
  [291] = {
    ["T"] = "The Reports",
    ["O"] = "Deliver Senir\'s report to Senator Barin Redstone in Ironforge.",
    ["D"] = "Excellent! Take my report to Senator Barin Redstone. He\'s a sour type, so don\'t let his less-than-sunny disposition get to you. He\'s in Ironforge, in the chamber where King Magni holds court.$b$bDon\'t know the way to Ironforge? Take the road out of Kharanos north, cross the bridge, then follow the road east--there are large banners flanking the road--up the mountainside.$b$bBy the way, if you might avoid mentioning how you assisted me? Can\'t let them think I\'m not working hard out here, you know?",
  },
  [292] = {
    ["T"] = "The Eye of Paleth",
    ["O"] = "Bring the Cursed Eye of Paleth to Glorin Steelbrow.",
    ["D"] = "Opening the chest reveals a blackened orb, pulsing with an eerie light.  It is the treasure of which Fitzsimmons spoke: a Cursed Eye of Paleth.$B$BTake the Cursed Eye of Paleth to the dwarven priest Glorin Steelbrow, in Menethil Harbor.  Ask him how to remove the Eye\'s curse.",
  },
  [293] = {
    ["T"] = "Cleansing the Eye",
    ["O"] = "Bring Archbishop Benedictus the Cursed Eye of Paleth.  Benedictus is in the Cathedral of Light, in the city of Stormwind.",
    ["D"] = "I can\'t lift the curse from the Eye of Paleth.  The Eyes were crafted by Human Priests, so you\'ll have to go to Stormwind to find someone who can help you.  Speak with Archbishop Benedictus.  He is head of the Cathedral of Light in Stormwind.  He can help.$B$BI hope, for your sake.",
  },
  [294] = {
    ["T"] = "Ormer\'s Revenge",
    ["O"] = "Ormer Ironbraid at the Whelgar Excavation Site wants you to kill 10 Mottled Screechers and 10 Mottled Raptors.",
    ["D"] = "The situation is severe, that much is for sure.  When we uncovered these bones it attracted the Raptors.  These filthy beasts killed my brethren and trapped me, Merrin and the poor Prospector up here.$b$bHelp clear the Wetlands of these Raptors, $N.  Mottled Raptors and Mottled Screechers are just West of the bluff there.  Kill 10 of each, if you can.$b$bThat will be a good start to the vengeance I have planned for them.",
  },
  [295] = {
    ["T"] = "Ormer\'s Revenge",
    ["O"] = "Ormer Ironbraid wants you to kill 10 Mottled Scytheclaw raptors and 10 Mottled Razormaw raptors then return to him at the Whelgar Excavation Site.",
    ["D"] = "Now it\'s time to really make those dreaded Raptors regret their blood-thirst.  Just down below there are scores of Mottled Scytheclaws and Mottled Razormaws.  Make those rotten creatures pay by slaying 10 of each!",
  },
  [296] = {
    ["T"] = "Ormer\'s Revenge",
    ["O"] = "Ormer Ironbraid at the Whelgar Excavation Site wants you to kill Sarltooth and return to him with one of his talons once the task is fulfilled.",
    ["D"] = "While you were down there I happened to notice that one of those beasts stood out from the rest.  He was bigger and more menacing.  I bet he\'s the one who led the others here to cause the disruption to the dig site.$b$bI ask of you now one final task, $N.  See to it that Sarltooth is brought to justice.  And considering the gravity of his crimes, justice in this case means death!  Bring me one of his talons as proof of his death.",
  },
  [297] = {
    ["T"] = "Gathering Idols",
    ["O"] = "Bring Magmar Fellhew 8 Carved Stone Idols.",
    ["D"] = "Recently, just before the Troggs surfaced within the site, we had uncovered a large number of strange, carved idols.  But we didn\'t have the chance to study them, for soon after their discovery the Troggs chased us away from the ruins!  And those idols have a strange effect on the Troggs.  It makes them go berserk!$B$BBring me 8 idols - I want to study them, and I want them out of Trogg hands!  You can find the idols on the Troggs infesting the site.",
  },
  [298] = {
    ["T"] = "Excavation Progress Report",
    ["O"] = "Bring Ironband\'s Progress Report to Jern Hornhelm in Thelsamar.",
    ["D"] = "I need a progress report sent to Thelsamar - a progress report, and a restatement of my request for more blastpowder!  Here\'s the report.  Take it to Jern Hornhelm.  He\'s our local contact in Thelsamar.$B$BIt\'s not a difficult task, but it must be done.",
  },
  [299] = {
    ["T"] = "Uncovering the Past",
    ["O"] = "Prospector Whelgar wants you to scour the excavation site in search of the 4 missing tablet fragments: Ados, Modr, Golm and Neru.",
    ["D"] = "Just before the invasion I uncovered a large tablet called the Goaz Stone.  The translated text breaks off in 4 places.  The text speaks of a \"divine plan\" and a \"doomed prophecy.\"$b$bI fear we are running out of time.  I am too old to go down and brave those beasts.  But you are strong.$b$bScour the excavation site and uncover the 4 missing tablet fragments: Ados, Modr, Golm and Neru.  Search for them in ancient artifacts or where the soil is loose.  Bring them to me so I can begin unlocking the mystery!",
  },
  [301] = {
    ["T"] = "Report to Ironforge",
    ["O"] = "Take Ironband\'s Progress Report to Prospector Stormpike.",
    ["D"] = "We\'ll need approval to get Ironband more blastpowder. You\'ll have to take his report to the Explorers\' League headquarters in Ironforge.$B$BHere\'s the report. Give it to Prospector Stormpike, and don\'t forget to duck after you deliver it. Stormpike is hotheaded, and he won\'t be happy to hear of Ironband\'s slow progress at the site.$B$BYou\'ll find Stormpike in the Assembly of Explorers.",
  },
  [302] = {
    ["T"] = "Powder to Ironband",
    ["O"] = "Speak with Jern Hornhelm in Thelsamar.",
    ["D"] = "If Ironband still needs blastpowder, then by Magni he\'ll have it!  I sent the approval on ahead with a messenger to Jern Hornhelm in Thelsamar.  He\'ll prepare the powder and other supplies to take to Ironband.$B$BAnd one more thing: I want you to personally see that the shipment of powder reaches its destination.  The last one didn\'t, so keep your mind sharp and your eyes open.",
  },
  [303] = {
    ["T"] = "The Dark Iron War",
    ["O"] = "Motley Garmason at Dun Modr wants you to kill 10 Dark Iron Dwarves,  5 Dark Iron Tunnelers, 5 Dark Iron Saboteurs and 5 Dark Iron Demolitionists.",
    ["D"] = "War rages both home and abroad.  While mighty Dwarven soldiers give their lives in far off territories in the name of the Alliance, our own lands fall prey to the evil deeds of the Dark Iron Dwarves.  They laid waste to the Thandol Span.  Dun Modr fell soon after.$b$bWe need you to gather force and push the front back.  If you can kill 10 Dark Iron Dwarves, 5 Tunnelers, 5 Saboteurs and 5 Demolitionists, the reserve might arrive in time to finish the drive.$b$bNow make haste!",
  },
  [304] = {
    ["T"] = "A Grim Task",
    ["O"] = "Kill Balgaras the Foul and bring his ear to Longbraid the Grim outside of Dun Modr.",
    ["D"] = "Many of my soldiers died in the battle of Dun Modr. My own brother perished when the West Bridge over the Thandol Span was destroyed.  Aye, the Dark Iron dwarves are a devilish breed.  From what Roggo was able to gather, the leader of the thugs in the Wetlands is a warlock named Balgaras the Foul. He is quite a coward, sending his soldiers to do his bidding but hides in his camp on Direforge Hill, to the southeast.$b$bIf you can find the spineless scum, slay him and bring me his ear. I will reward you, $c.",
  },
  [305] = {
    ["T"] = "In Search of The Excavation Team",
    ["O"] = "Tarrel Rockweaver wants you to travel to the Excavation Site and contact Merrin Rockweaver.",
    ["D"] = "Oh my, you must be the scout Longbraid said he would send!  You cannot imagine how worried I have been.$b$bMy wife, Merrin, has been sending me letters each week for the past month.  She is part of the Whelgar Excavation team.  But it has been nearly two weeks since her last correspondence.  Surely something is wrong!$b$bLongbraid said that he would send a scout to check up on things if we did not hear from the team soon.  I assume that scout is you, $N.  Find Merrin at the excavation site to the east!",
  },
  [306] = {
    ["T"] = "In Search of The Excavation Team",
    ["O"] = "Return to Tarrel Rockweaver with Merrin\'s note.",
    ["D"] = "It was horrible!  We were uncovering some rare artifacts and we began to discover these large bones.$b$bBefore we knew what was happening, these Mottled Raptors overwhelmed the dig site.  Many lives were lost.$b$bOrmer here -- so brave -- saved the Prospector and me.  He defended us as we ran for shelter.  Now we are trapped.  And our hard work lies in ruin.$b$bHere, take this note back to my sweet Tarrel.  It will explain the situation to him.",
  },
  [307] = {
    ["T"] = "Filthy Paws",
    ["O"] = "Go to the Silver Stream Mine and collect 4 loads of Miners\' Gear.$B$BReturn to Mountaineer Stormpike.",
    ["D"] = "The Silver Stream Mine to the east ran dry long ago.  It was converted by the Miners\' League into a storage depot, but now Kobolds have moved in, putting their filthy paws on good, Dwarven tools!$B$BWe\'ll root out those vermin soon enough, but the League wants someone to get their gear out of the mine before we warriors tromp in there and break things.  It\'ll be a tough delve - you might want cohorts at your side.$B$BThe gear is stored in Miners\' League Crates throughout the mine.  Good luck.",
  },
  [308] = {
    ["T"] = "Distracting Jarven",
    ["O"] = "",
    ["D"] = "",
  },
  [309] = {
    ["T"] = "Protecting the Shipment",
    ["O"] = "Ensure Miran and the shipment arrive at the excavation site, then inform Prospector Ironband.",
    ["D"] = "Ready to go, $n?$b$bFirst, we need to get this powder to Ironband. It\'ll be a lot for me to carry, and these parts can get dangerous -- and who knows what else the Dark Irons might have in store for me.$b$bI\'ll feel a lot better with you coming along.",
  },
  [310] = {
    ["T"] = "Bitter Rivals",
    ["O"] = "In the basement of the Thunderbrew Distillery in Kharanos, replace a barrel of Thunder Ale with a Barrel of Barleybrew Scalder.",
    ["D"] = "I have to admit, those Thunderbrews make some good drinks.  But they need to learn that theirs isn\'t the only decent brew!  Maybe you can help me teach them that lesson...$B$BHere, take this barrel of Barleybrew Scalder.  Sneak into the basement of the Thunderbrew Distillery in Kharanos and switch it with one of their barrels of Thunder Ale.  Then we\'ll see how their patrons like my brew over theirs!$B$BAnd if the barrels are guarded, then you might have to distract the guard ...",
  },
  [311] = {
    ["T"] = "Return to Marleth",
    ["O"] = "Bring the barrel of Thunder Ale to Marleth Barleybrew at the Brewnall Village.",
    ["D"] = "Having inserted a barrel of Marleth\'s Scalder in the Kharanos Distillery\'s cellar, you grab a barrel of Thunder Ale as proof of the deed...",
  },
  [312] = {
    ["T"] = "Tundra MacGrann\'s Stolen Stash",
    ["O"] = "Retrieve Tundra MacGrann\'s dried meats from the stolen meat locker in Old Icebeard\'s cave.",
    ["D"] = "I had a month\'s worth of dried meats salted and locked away for the cold season.  Kept it locked so the bears wouldn\'t get it!  But while I was off hunting deer, that beast, Old Icebeard, made off with the meat locker.  He won\'t be able to smash through that Thorium though.  But I\'ll starve if I can\'t get my meats back.$b$bYou seem a bit more stealthy than me.  Perhaps you can retrieve my stash?  I\'ll never make it in and out of that cave fast enough with this bum leg of mine.",
  },
  [313] = {
    ["T"] = "The Grizzled Den",
    ["O"] = "Gather 8 Wendigo Manes and bring them to Pilot Stonegear.",
    ["D"] = "Driving a Siege Engine isn\'t for everyone.  It takes an iron grip and nerves of steel...lucky I have both!  How about you?  How\'s your mettle?  Want to prove it to me?$B$BWell...I\'m looking to make my engine, Trollplow, a little more cozy.  How about you get me a heap of Wendigo Manes from the Grizzled Den, west of Kharanos.  Those manes would make a great rug for Trollplow\'s inside deck!",
  },
  [314] = {
    ["T"] = "Protecting the Herd",
    ["O"] = "Rudra Amberstill wants you to slay Vagash and bring his fang to her at the Ram ranch.",
    ["D"] = "We heard the cries in the middle of the night.  Then this morning, sure enough, the herd was missing two rams.  That dastardly beast known as Vagash has been preying on our livelihood.  With most of King Magni\'s army off in distant lands fighting with the Alliance, there is no one to keep Vagash at bay.$b$bPerhaps you are brave enough to seek out the beast and slay him.  Bring me one of his fangs and I will reward you.  Vagash lurks just above the ranch here, but be warned, he is deadly.",
  },
  [315] = {
    ["T"] = "The Perfect Stout",
    ["O"] = "Bring 6 Shimmerweeds to Rejold Barleybrew in the Brewnall Village.",
    ["D"] = "I\'m on a quest, a quest to make the perfect stout.  I know I can do it -- brewing\'s in my blood.  I just need to find the right recipe...$B$BThe Frostmane trolls grow a plant, shimmerweed, high up in the hills to the east.  They use it in their strange, tribal rituals.  We dwarves haven\'t found much use for it, but it has a unique taste...and I want to experiment with it in my brews!$B$BGet me some shimmerweed from Frostmane seers, or swipe it from the trolls\' shimmerweed baskets.",
  },
  [317] = {
    ["T"] = "Stocking Jetsteam",
    ["O"] = "Gather 4 Chunks of Boar Meat and 2 Thick Bear Furs, and deliver them to Pilot Bellowfiz at Steelgrill\'s Depot.",
    ["D"] = "I\'m preparing to start a mission for the Siege Brigade.  It\'s a long one, and I need to stock Jetsteam with a month\'s worth of supplies.  So while Steelgrill is working on my tank, can you do some hunting for me?$B$BI\'ll need some fur for bedding, and boar meat for food.  You can get meat from boars and fur from the bears... you can find them both in the snow fields south of the Grizzled Den.",
  },
  [318] = {
    ["T"] = "Evershine",
    ["O"] = "Get a cask of Evershine from Rejold Barleybrew in Brewnall Village.",
    ["D"] = "Rejold Barleybrew experiments with his brews.  Some of them taste good, some bad, and some...well, some will lay all but the stoutest dwarf flat.$B$BHe lives at Brewnall Village, west of Kharanos, and one of his drinks, Evershine, is what I want.  It\'ll help keep me warm while I\'m on the cold road.$B$BAnd...the blessed stuff burns so hot I can throw it in Jetsteam\'s furnace for a burst of power!  That little tactic has gotten me out of more than one tight spot.",
  },
  [319] = {
    ["T"] = "A Favor for Evershine",
    ["O"] = "Kill 6 Ice Claw Bears, 8 Elder Crag Boars, and 8 Snow Leopards, and then return to Rejold Barleybrew in Brewnall Village.",
    ["D"] = "I have a cask of Evershine handy, but...can you do me a favor?  Brewnall village could use a hand against the wild animals that wander the nearby snows.  Sometimes they wander close.$B$BWe\'re not afraid of these bears and cats and boars - no self respecting dwarf would be - but if you can take care of those animals for us, then we\'d have more time for our crafting and brewing.",
  },
  [320] = {
    ["T"] = "Return to Bellowfiz",
    ["O"] = "Give the cask of Evershine to Pilot Bellowfiz at Steelgrill\'s Depot.",
    ["D"] = "Here\'s your cask of Evershine, $N.  And again, thank you for the hunting earlier.",
  },
  [321] = {
    ["T"] = "Lightforge Iron",
    ["O"] = "Search the wreckage of The Flying Osprey in the Wetlands.",
    ["D"] = "The shipwrecks off the shore here are the doomed Third Fleet of Kul\'Tiras, burned and sunk by Red Dragons during the last Great War.$B$BThe Fleet hailed from Lordaeron, but one ship, an Elvish Destroyer: Flying Osprey, was with it.  It was shipping a load of Lightforge Iron and joined the Third Fleet for protection.  Ironic that it sunk to the same dragons...$B$BEnough history.  The Flying Osprey is wrecked to the south of Menethil Harbor.  If you find the Lightforge Iron there, bring it to me.",
  },
  [322] = {
    ["T"] = "Blessed Arm",
    ["O"] = "Take the Crate of Lightforge Ingots to Grimand Elmore in Stormwind.",
    ["D"] = "There is a dwarven weaponsmith in the Dwarven District of Stormwind who can craft with lightforge iron.  His name is Grimand Elmore.$B$BI\'ve packed the iron you found into a crate.  Take it to Grimand and tell him your tale.  I\'m sure he\'ll oblige someone on a quest such as yours, and craft for you a weapon - a weapon to give even Morbent Fel worry!",
  },
  [323] = {
    ["T"] = "Proving Your Worth",
    ["O"] = "Kill 15 Skeletal Raiders, 3 Skeletal Healers and 3 Skeletal Warders, and then return to Sven.",
    ["D"] = "Morbent Fel is a necromancer, and an ally to the dark riders.  I would pit you against the power of Morbent Fel, but I will not send you against that vile fiend without first knowing your strength.$B$BYou have already proven your bravery to me, but if you truly wish to face the necromancer, then you must now prove your skill against his minions.",
  },
  [324] = {
    ["T"] = "The Lost Ingots",
    ["O"] = "Gather 5 Lightforge Ingots, then return to Glorin Steelbrow in the Wetlands.",
    ["D"] = "Having grabbed the single lightforge ingot from the waterlogged chest, it is clear that murlocs have stolen the rest.  Hunt the murloc raiders near the Flying Osprey wreckage for the lost lightforge iron.",
  },
  [325] = {
    ["T"] = "Armed and Ready",
    ["O"] = "Bring Morbent\'s Bane to Sven in Duskwood.",
    ["D"] = "Here is the weapon against the fiend of which you spoke.  You\'re all ready, $N.  Best of luck to you, and I look forward to your tale of glory!",
  },
  [328] = {
    ["T"] = "The Hidden Key",
    ["O"] = "Use the Small Brass Key to open Bookie Herod\'s Strongbox.",
    ["D"] = "Although no mention of Private Thorsen is found in these records, hidden within the binding of one of Herod\'s ledger books is a small, brass key.$B$BAnd after further study of his notes, a few references to a \"secure strongbox\" are contained among various papers.  The box is somewhere within the Stockpile - the cavern complex at the back of Kurzen\'s Compound.$B$BIf this secure strongbox is found, and the small brass key can open it, then perhaps the mystery behind Thorsen\'s detection will be revealed.",
  },
  [329] = {
    ["T"] = "The Spy Revealed!",
    ["O"] = "Take the Muddy Note to Lieutenant Doren at the Rebel Camp.",
    ["D"] = "Among the valuables of the strongbox is a muddy, scribbled note.  Although barely legible, it looks to be an itinerary for Private Thorsen.  It details his patrol routes, who he speaks with and how he spends his time with the rebels.  It\'s signed with only one letter: -M.$B$BBut whoever wrote this note must have stayed very close to Thorsen.  Whoever wrote this note is the traitor within the rebel camp.",
  },
  [330] = {
    ["T"] = "Patrol Schedules",
    ["O"] = "Speak with Corporal Sethman.",
    ["D"] = "The note you gave me is signed with an \"-M\"...I don\'t know what that could mean.$B$BBut...the note also says this person shared patrol routes with Thorsen last week.  So whoever shared patrol with Thorsen last week must be the traitor!$B$BGo to Corporal Sethman.  He handles the patrol schedules and can tell you who was with Thorsen.",
  },
  [331] = {
    ["T"] = "Report to Doren",
    ["O"] = "Speak with Lieutenant Doren.",
    ["D"] = "Did Lieutenant Doren want you to report back to him with that patrol information?",
  },
  [332] = {
    ["T"] = "Wine Shop Advert",
    ["O"] = "Go to the Gallina Winery, and bring Suzetta Gallina the Wine Ticket for a free bottle of wine.",
    ["D"] = "If you talk to anyone about wine, then you know we Gallinas sell the best wine in Stormwind.  And we\'re not far, still in the Trade District along the city\'s lovely canals.$B$BHere, take this pamphlet.  Bring it to my sister Suzetta at our shop for a complimentary bottle of our famous pinot noir.  You won\'t regret it!",
  },
  [333] = {
    ["T"] = "Harlan Needs a Resupply",
    ["O"] = "Go to the Canal Tailor Shop and bring Rema Schneider the Cloth Request from Harlan Bagley.",
    ["D"] = "We\'ve been doing a lot of business lately.  It seems like everyone is buying armor and sturdy clothes.  Almost like they expect a cold, harsh season ahead...$B$BBut those are future worries.  My worry today is that I\'m running out of knitted clothing to sell.  I need another load from our supplier.$B$BIf you can take this request to Rema Schneider at the Canal Tailor and Fit Shop, I\'d be most grateful.",
  },
  [334] = {
    ["T"] = "Package for Thurman",
    ["O"] = "Go to the Larson Clothiers in the Stormwind Mage Quarter, and give Thurman Schneider his Sewing Kit.",
    ["D"] = "My son Thurman is an apprentice at the Larson Clothiers, in the Mage\'s Quarter.  He was in a hurry today and forgot his shears and needles.  I know a great $C like you must have important tasks at hand, but without his tools Thurman can\'t do his apprentice work!$B$BPlease, $N.  Can you take my son\'s sewing kit to him?  Larson Clothiers is one of two clothing shops in the Mage\'s Quarter - it\'s the one deeper in, near the Mage\'s Tower.",
  },
  [335] = {
    ["T"] = "A Noble Brew",
    ["O"] = "Zardeth the Black Claw wants you to bring him a Tear of Tilloa from Darkshire and a Musquash Root from the Wetlands.",
    ["D"] = "Lord Wishock is causing quite a stir amongst the House of Nobles.  He was made aware of our little \"establishment\" down here and is lobbying for a full investigation.  I have a plan to keep Wishock \"preoccupied,\" but I\'ll need your help. $b$bThere is a flower growing in the Garden of Stalvan on the outskirts of Darkshire.  This small white flower is known as the Tear of Tilloa.  I will also need a Musquash Root, found only at the very base of the falls far beneath the Stonewrought Dam in the Wetlands.",
  },
  [336] = {
    ["T"] = "A Noble Brew",
    ["O"] = "Take the Black Claw Stout to Lord Wishock in the Hall of Petitioners.",
    ["D"] = "Lord Wishock, like all men, has his weaknesses.  His happens to be a fondness for liquor.  The hypocrisy is astounding.  The trite fool spends his days trying to shut down our little \"tavern\" while his nights are spent upstairs, running up a bill he never pays.  Well, let\'s see how Wishock likes this special brew I have prepared for him:  Black Claw stout.  It\'s got quite a kick, $n.$b$bTake it to him and see how he likes it.  Most likely the old fool is milling about the Hall of Petitioners.",
  },
  [337] = {
    ["T"] = "An Old History Book",
    ["O"] = "Return the book: The History of Stormwind, to the Stormwind Library.",
    ["D"] = "Although the cover of this book is marred by scratches and caked with mud, the words \"The History of Stormwind\" can still be read along its spine.  Upon opening the book, the seal of the Stormwind Library is clearly stamped on the book\'s first page.$B$BIt looks like someone checked out this book, and never returned it...",
  },
  [338] = {
    ["T"] = "The Green Hills of Stranglethorn",
    ["O"] = "Collect the missing pages from The Green Hills of Stranglethorn manuscript.  Once all four chapters are complete, return them to Barnil.",
    ["D"] = "Master Nesingwary will be so upset if he finds out!$b$bHe trusted me to proofread the manuscript of his novel, The Green Hills of Stranglethorn.  Everything was going well, but a large gust of wind blew through the camp and scattered the pages everywhere!$b$bI was able to gather most of the pages but many are still strewn about the jungle.  Collect the pages to complete each of the four chapters.$b$bI will help you organize them.  Return the chapters to me once you have all four.",
  },
  [339] = {
    ["T"] = "Chapter I",
    ["O"] = "Bring pages 1, 4, 6, and 8 of Nesingwary\'s The Green Hills of Stranglethorn to Barnil Stonepot in order to complete Chapter I.",
    ["D"] = "Chapter I was quite riveting!  Master Nesingwary sets the backdrop for the story and makes mention of his hunting companions, including me! $b$bBut that\'s besides the point.  Let me see which pages are still missing.  It seems pages 1, 4, 6 and 8 are still out there somewhere in the jungle!",
  },
  [340] = {
    ["T"] = "Chapter II",
    ["O"] = "Bring pages 10, 11, 14 and 16 of Nesingwary\'s The Green Hills of Stranglethorn to Barnil Stonepot in order to complete Chapter II.",
    ["D"] = "Chapter II was chock full of exciting tales of panther and tiger hunting.  I could just kick myself for letting the pages blow away! $b$b It seems I was able to recover all of the pages with the exception of pages 10, 11, 14, and 16.",
  },
  [341] = {
    ["T"] = "Chapter III",
    ["O"] = "Bring pages 18, 20, 21 and 24 of Nesingwary\'s The Green Hills of Stranglethorn to Barnil Stonepot in order to complete Chapter III.",
    ["D"] = "Chapter III was my favorite.  It tells of Sir Erlgadin and Lady Rouack\'s first kill with a dwarven rifle.  And the end of Chapter III, why, such a cliffhanger!  $b$bLooks to me as though we still need pages 18, 20, 21 and 24 to finish the chapter.  Those pages are out there somewhere. . .",
  },
  [342] = {
    ["T"] = "Chapter IV",
    ["O"] = "Bring pages 25, 26, and 27 of Nesingwary\'s The Green Hills of Stranglethorn to Barnil Stonepot in order to complete Chapter IV.",
    ["D"] = "Ah Chapter IV; the big finale!  And what an ending it was! $b$bWe are only missing three pages for this chapter.  We need pages 25, 26 and 27 to put Chapter IV back together.",
  },
  [343] = {
    ["T"] = "Speaking of Fortitude",
    ["O"] = "Go to the Royal Library in Stormwind Keep and speak with Milton Sheaf.  He can find for you the book on metallurgy that Brother Kristoff needs for his speech.",
    ["D"] = "I\'m giving a speech on the Fortitude of the Spirit, and I\'d like to recite passages from a particular book on metallurgy - \"The Stresses of Iron.\"  A copy of this book should be in the Royal Library, within Stormwind Keep.  Can you retrieve it for me?  If so, then may the blessings of the Light be with you!$B$BThe Royal Library is very large, but the head librarian, Milton Sheaf, knows each shelf like it was his own son.  Speak to him, he\'ll retrieve the book for you.",
  },
  [344] = {
    ["T"] = "Brother Paxton",
    ["O"] = "Speak with Brother Paxton in the Northshire Abbey.",
    ["D"] = "The Stresses of Iron?  Yes, I know the book.  Quite a good one, written by the Dwarven miner Margulf Blaggon a few decades ago.  He spent most his time deep in the mountains of Khaz Modan, digging ore.  Smart fellow!$B$BBut where was I?  Oh yes, the Stresses of Iron!  I\'m afraid the book was moved to the Northshire Abbey.  The caretaker of the Abbey\'s library, Brother Paxton, wanted to make a copy of the book for his own shelves.$B$BBut that was months ago.  I wonder why it\'s taking him so long...",
  },
  [345] = {
    ["T"] = "Ink Supplies",
    ["O"] = "Speak with Foreman Oslow in Lakeshire and ask him for Rethban Iron Ore.",
    ["D"] = "I\'ve been meaning to finish my copy of The Stresses of Iron, but...$B$BWell you see, I make my own ink for books I copy.  It\'s my own recipe; it doesn\'t run or smear, and it dries very quickly  Unfortunately, it dries too quickly!  My last batch dried up before I could finish the copying job, and I haven\'t had the chance to make more ink!$B$BTo make more I need Rethban Iron Ore from the Redridge Mountains.  Foreman Oslow in Lakeshire is my usual supplier.",
  },
  [346] = {
    ["T"] = "Return to Kristoff",
    ["O"] = "Return to Brother Kristoff in the Cathedral Square.  Give him the book The Stresses of Iron.",
    ["D"] = "Now that I\'ve finished copying the book, here\'s the original Stresses of Iron.  Thank you for your patience, and if you see Milton please tell him I\'m sorry for the long delay.",
  },
  [347] = {
    ["T"] = "Rethban Ore",
    ["O"] = "Gather 5 loads of Rethban Ore and bring it to Brother Paxton in the Northshire Library.",
    ["D"] = "Rethban Ore is named after the man who first found it, Bart Rethban.  When smelted it\'s not as strong as iron, but its ore has a dark, flat color that some scribes use in their dyes.  It can be found in the Rethban Caverns, north of here.$B$BWe usually have some in stock, but we\'re too busy preparing for Orc attacks to gather any.  And besides...Redridge Gnolls are now living in the Rethban Caverns and their Drudgers are gathering the ore themselves.$B$BSo if you need some, you\'ll have to get it yourself.",
  },
  [348] = {
    ["T"] = "Stranglethorn Fever",
    ["O"] = "Seek out Witch Doctor Unbagwa and have him summon Mokk the Savage.  Bring the Heart of Mokk to Fin Fizracket.",
    ["D"] = "Sick...so...very s-s-sick....$b$b...have contracted Stranglethorn Fever...deadly v-v-virus...$b$b...only cure...is...is...to eat the heart of M-m-m-m-mokk the Savage...elusive white beast...$b$b...Witch doctor Unbagwa...only one who can s-s-summon....$b$b...seek Unbagwa in hidden cave on southern cape...$b$b...hurry...dying...dying....",
  },
  [349] = {
    ["T"] = "Stranglethorn Fever",
    ["O"] = "Speak with Witch Doctor Unbagwa.",
    ["D"] = "Hi, $N!$B$BSpeak with me friend Witch Doctor Unbagwa.",
  },
  [350] = {
    ["T"] = "Look to an Old Friend",
    ["O"] = "Speak to Elling Trias in Stormwind.",
    ["D"] = "That Marzon would be contacting a member of the Defias and have ties to Lescovar...$B$BHaving killed VanCleef and Thredd, it would be hard to see Lescovar brought to proper justice, as your evidence is nothing without their testimonies. That is not even considering the fact that Lescovar is a noble and well connected. It effectively makes him above the law.$B$BYou\'ll need other help. I loathe to do so, but there is someone I know that will aid us. You\'ll find Trias in the Trade District of the city.",
  },
  [351] = {
    ["T"] = "Find OOX-17/TN!",
    ["O"] = "Bring the distress beacon to Oglethorpe\'s homing robot.",
    ["D"] = "You have uncovered some sort of strange, egg-shaped device made from metal.  Fiddling with one of its knobs springs the egg to life, as it opens up into some sort of gnomish robotic contraption!  A voice from inside the robotic egg crackles to life.$B$B\"The name\'s Oglethorpe, and my homing robot has gone missing!  I will reward you for its recovery!  Please take this beacon to the robot.  Excellent, I now have computed the coordinates for you!  It crashed near the Gaping Chasm in Tanaris!\"",
  },
  [353] = {
    ["T"] = "Stormpike\'s Delivery",
    ["O"] = "Deliver the Package for Stormpike to Mountaineer Stormpike in Loch Modan.",
    ["D"] = "The Stormpikes are a respected dwarven clan, and are well known for their fine and discerning tastes.  So it\'s no wonder that Gringer Stormpike, a Mountaineer of Ironforge, commissioned me to craft him a weapon.$B$BThe weapon is finished, but... Mountaineer Stormpike is far away, in distant Loch Modan.  If you plan on traveling to the north, can you deliver this package to him?$B$BMy last message from Mountaineer Stormpike said he\'s stationed at the northern guard tower in Loch Modan.",
  },
  [354] = {
    ["T"] = "Deaths in the Family",
    ["O"] = "Bring Gregor\'s Remains, Nissa\'s Remains and Thurman\'s Remains to Coleman Farthing in Brill.",
    ["D"] = "The Agamand family was the most prosperous family in Tirisfal Glades.  I used to work their mills...before the Plague.$B$BWhen the Scourge first came, the Agamands fortified their home and convinced those in their employ to remain and help them defend.  We were fools, but at least we were loyal fools.$B$BThe Agamands, in their pride, doomed us to undeath.  And now they are minions of the Scourge!$B$BServe the Forsaken by defeating the Agamands who fell to the Plague.  Serve me by bringing me their remains.",
  },
  [355] = {
    ["T"] = "Speak with Sevren",
    ["O"] = "Speak with Magistrate Sevren in Brill.",
    ["D"] = "You have given me the remains of the Agamands, and satisfied my desire for revenge.  But the Agamand Mills hold a threat to all the Forsaken, not just to me.$B$BI have spoken to Magistrate Sevren of your exploits against the Agamands, and he wishes to speak with you.",
  },
  [356] = {
    ["T"] = "Rear Guard Patrol",
    ["O"] = "Kill 8 Bleeding Horrors and 8 Wandering Spirits, then report back to Linnea at her camp.",
    ["D"] = "The defenders on The Bulwark protecting Tirisfal from the Plaguelands are on constant alert.  But sometimes Scourge will slip past them.$B$BOur success at the Bulwark depends on a one-front battle - we cannot allow an attack from the rear, nor can we allow our defenders\' supply line to be cut.$B$BAssist the Bulwark.  Patrol east and slay any Scourge you find.  Spend particular effort at the Balnir Farmstead to the east.  It has become a haven for interloping Scourge.",
  },
  [357] = {
    ["T"] = "The Lich\'s Identity",
    ["O"] = "Bring the Lich\'s Spellbook to Bethor Iceshard in the Undercity.",
    ["D"] = "There is a lich who dwells on the island in Brightwater Lake to the north.  Although he is a free-willed Forsaken, he believes all other Undead are Scourge slaves and has his minions attack anyone who approaches him.$B$BBut because he is Forsaken, the Queen will want him.  And if he is skilled in Necromancy, then his knowledge would be...quite useful to us.$B$BTo discover his identity and his talents, I must see his spellbook.  Steal into his camp and acquire it, then return to me.",
  },
  [358] = {
    ["T"] = "Graverobbers",
    ["O"] = "Kill Rot Hide Graverobbers and Rot Hide Mongrels.  $B$BBring 8 Embalming Ichors to Magistrate Sevren in Brill.",
    ["D"] = "The Mass Graves, southwest of Garren\'s Haunt to the north, were made to accommodate the...impressive...number of deaths Tirisfal suffered when the Plague first came.  $B$BThe bodies in these graves have so far been spared an undeath, but the Scourge now send Rot Hide Gnolls to gather the corpses and use them to bolster their armies.  This cannot be allowed!$B$BYour task is twofold: slay the Rot Hides at the Mass Grave and Garren\'s Haunt, and gather from them the Embalming Ichor that gives them life.",
  },
  [359] = {
    ["T"] = "Forsaken Duties",
    ["O"] = "Speak with Deathguard Linnea.",
    ["D"] = "I need a fresh report from our Deathguard waystation to the east.$B$BI must know if more Scourge forces have slipped past The Bulwark and into Tirisfal.  Our vigilance on the border to the Plaguelands must be maintained - we can\'t let the Scourge get a stronger foothold here!$B$BTravel south along the road, then east at the fork to the waystation.  Speak with Deathguard Linnea.  She has the information I need.",
  },
  [360] = {
    ["T"] = "Return to the Magistrate",
    ["O"] = "Return to Magistrate Sevren in Brill.",
    ["D"] = "Now return to Magistrate Sevren with the information I gave you.  He\'ll want it as soon as possible, so be swift.$B$BAnd if you see any Scourge on your way back, take them down - we can\'t let them wander our lands unchallenged.",
  },
  [361] = {
    ["T"] = "A Letter Undelivered",
    ["O"] = "Find Yvette Farthing, and deliver to her the letter from Thurman Agamand.",
    ["D"] = "This is a letter written by Thurman, the eldest son of Gregor Agamand.  It is addressed to an Yvette Farthing, though it does not appear to have reached her...",
  },
  [362] = {
    ["T"] = "The Haunted Mills",
    ["O"] = "Slay Devlin Agamand, and bring Devlin\'s Remains to Coleman Farthing in Brill.",
    ["D"] = "Devlin Agamand was the younger of two sons in the Agamand family, and in life the two could not be more different.  Thurman was tall and gentle while his younger brother was weak and sharp tongued.$B$BWhen Agamand Mills fell to the Plague, I was not surprised to hear that Devlin was quick to succumb.  His mad chattering can still be heard near the road leading to the Agamand Mills.$B$BI\'m collecting the remains of the Agamands, and I want poor Devlin.  Find him, destroy him, and bring me his bones.",
  },
  [363] = {
    ["T"] = "Rude Awakening",
    ["O"] = "Speak with Shadow Priest Sarvis.",
    ["D"] = "About time you woke up. We were ready to toss you into the fire with the others, but it looks like you made it.$b$bI am Mordo, the caretaker of the crypt of Deathknell. And you are the Lich King\'s slave no more.$b$bSpeak with Shadow Priest Sarvis in the chapel at the base of the hill, he will tell you more of what you must know.",
  },
  [364] = {
    ["T"] = "The Mindless Ones",
    ["O"] = "Shadow Priest Sarvis wants you to kill 8 Mindless Zombies and 8 Wretched Zombies.",
    ["D"] = "We Forsaken are at war with the Lich King\'s army of the Scourge: necromantically raised armies of the undead, foul beasts of the north, and tormented spectres.$b$bThe northern part of the village has become overrun with the Mindless Ones, and they must be destroyed. Destroy them, show them no mercy, our former brothers and sisters as they might be. The Fallen are nothing but The Lich King\'s slaves.",
  },
  [365] = {
    ["T"] = "Fields of Grief",
    ["O"] = "Steal 10 pumpkins from the farm to the west, just north of Deathknell and take them to Apothecary Johaan in Brill.",
    ["D"] = "What have we here?  You look like a fledgling $c.  If you hope to prove yourself to The Dark Lady, you need to learn the ways of The Forsaken.$b$bTo the west you\'ll find a farm.  Humans infest the land like mold on a rotting corpse.  And worse yet, the Scarlet Crusade patrols nearby from their tower.  Teach those scum a lesson and steal 10 of their precious pumpkins.$b$bOnce you have 10, take them to Apothecary Johaan in Brill.",
  },
  [366] = {
    ["T"] = "Return the Book",
    ["O"] = "Return Gunther\'s Spellbook to him, on the island of Gunther\'s Retreat.",
    ["D"] = "Long ago Gunther and I, along with the now Scourge Necromancer Thule Ravenclaw, were friends and students within the Kirin Tor - a society of mages in Dalaran.$B$BGunther and I both fell to the Plague, but I thought he either remained dead, or was raised into the ranks of the Lich King.  It is good to know he is free.$B$BWe must have him.  I have placed a spell on his book that, when he sees it, will show him that I too am free of the Lich King.  After knowing this, it is my hope that he will join us.",
  },
  [367] = {
    ["T"] = "A New Plague",
    ["O"] = "Apothecary Johaan in the town of Brill wants you to collect 5 Vials of Darkhound Blood.",
    ["D"] = "Lady Sylvanas has called upon the Royal Apothecary Society. The Dark Lady believes our knowledge coupled with the newfound magic will provide the key to Arthas\'s demise.  She has challenged us to concoct a new plague, a plague deadlier than any ailment on Azeroth. This new disease will bring Arthas\'s Scourge Army to ruin.$b$bMy studies show that the blood of beasts might prove to be the key. Bring to me 5 vials of darkhound blood so I can test my theory. ",
  },
  [368] = {
    ["T"] = "A New Plague",
    ["O"] = "Apothecary Johaan of the town of Brill needs 5 Vile Fin Scales from Murlocs in Tirisfal Glades.",
    ["D"] = "While you were collecting samples for me, my experiments led me to realize that more reagents will be required for this new disease to spread properly. Poisoning some hapless victim is child\'s play. Plaguing an entire world proves to be a bit more complicated.$b$bI will need 5 Vile Fin Scales from Murlocs in the vicinity.  You will find the creatures along the coast to the north or to the west.",
  },
  [369] = {
    ["T"] = "A New Plague",
    ["O"] = "Apothecary Johaan in the town of Brill wants you to bring him 4 samples of venom from a Vicious Night Web Spider.",
    ["D"] = "While you were out gathering, I uncovered some old text in one of my tomes that indicates that an ancient plague wiped out thousands of innocent victims.  Later it was discovered that the deadly agent in the plague was preserved through the venom of Night Web Spiders.$b$bBring me some venom from a Vicious Night Web Spider to complete this experiment.  I want to see if the contagious element from the venom will work with my new concoction.  Rumor has it the spiders can be found in Eastern Tirisfal Glades.",
  },
  [370] = {
    ["T"] = "At War With The Scarlet Crusade",
    ["O"] = "Executor Zygand in Brill wants you to kill Captain Perrine, 3 Scarlet Zealots and 3 Scarlet Missionaries.",
    ["D"] = "The strategic documents from Executor Arren contain all the details we need to wipe the human infestation from the lands.  According to the information, The Scarlet Crusade has assigned Captain Perrine and a brigade to the ruined tower southwest of Brill.$b$bKill Perrine along with 3 Zealots and 3 Missionaries and report back to me.",
  },
  [371] = {
    ["T"] = "At War With The Scarlet Crusade",
    ["O"] = "Executor Zygand in Brill has commissioned you to slay Captain Vachon and 5 Scarlet Friars.",
    ["D"] = "We are still at war and the Scarlet Crusade grows in strength.  The report Executor Arren sent me indicates Scarlet Crusaders have been raiding from the ruined tower in southeastern Tirisfal near the Balnir Farmstead under the command of Captain Vachon.$b$bKill Vachon along with 5 Scarlet Friars. It should prove to be a devastating blow to the Crusade!",
  },
  [372] = {
    ["T"] = "At War With The Scarlet Crusade",
    ["O"] = "Executor Zygand in the town of Brill wants you to assassinate Captain Melrache and his two bodyguards.",
    ["D"] = "Scarlet Crusaders have been raiding from the ruined tower in northern Tirisfal, past Faol\'s Rest.  According to the information we have, a ruthless commander named Captain Melrache is in charge of this evil crew.$b$bI am entrusting you now with a special and dangerous mission.  Slay Melrache and his two bodyguards, in the name of The Dark Lady.",
  },
  [373] = {
    ["T"] = "The Unsent Letter",
    ["O"] = "Deliver the Letter to the City Architect to Baros Alexston in Stormwind.",
    ["D"] = "Searching Edwin VanCleef\'s person, you discover, among other things, an unsent letter. It is addressed to Baros Alexston, the City Architect of Stormwind, City Hall, Cathedral Square.$B$BIt appears to be recently written and sealed.",
  },
  [374] = {
    ["T"] = "Proof of Demise",
    ["O"] = "Bring 10 Scarlet Insignia Rings to Deathguard Burgess in Brill.",
    ["D"] = "Executor Zygand tells me you\'ve been out killing Scarlet Crusaders. Tirisfal Glades is crawling with them.$b$bVarimathras, lieutenant to The Dark Lady, is seeing to it personally that the scum of Azeroth, the Human race, gets wiped into oblivion. Here in Tirisfal Glades we face a particularly annoying breed of humans: The Scarlet Crusade.$b$bVenture forth and wage an offensive on the Scarlet Crusade, $c. Bring me back 10 Scarlet Insignia Rings as proof of your loyalty to Varimathras and The Dark Lady.",
  },
  [375] = {
    ["T"] = "The Chill of Death",
    ["O"] = "Bring five Duskbat Pelts and some Coarse Thread to Gretchen Dedmar in Brill.",
    ["D"] = "It\'s so cold, now.  The Plague of Undeath crawls through my veins like an icy serpent.  The Mindless State will be upon me soon.  But no doomed destiny will prevent me from serving our Dark Lady.  When the call arose I sewed body bags for the fallen soldiers of Sylvanas\'s mighty army.$b$bNow my hands shake from the chill.  If you would bring me five Duskbat Pelts and some Coarse Thread I could sew myself a blanket.  Help me, $N, so that I can continue to serve the cause.",
  },
  [376] = {
    ["T"] = "The Damned",
    ["O"] = "Novice Elreth requires 6 Scavenger Paws and 6 Duskbat Wings.",
    ["D"] = "My duties include tending to our wounded warriors, tailoring armor and clothes, and assisting Shadow Priest Sarvis with whatever else he might need.$b$bFrom the look of it, you\'ll be enlisted in his service also... hunting the Mindless Ones, if I know his mind. Well, if you\'d like to stay in one piece--and I\'ve no doubt you do--perhaps I can help. I\'m running out of paws and wings, and if you bring me some, I\'ll find some armor for you. You\'ll find a good number of wolves and bats to the south.",
  },
  [377] = {
    ["T"] = "Crime and Punishment",
    ["O"] = "Councilman Millstipe of Darkshire wants you to bring him the hand of Dextren Ward.",
    ["D"] = "As if the neglect for the residents of Duskwood was not bad enough, now the House of Nobles spits in the eye of the Darkshire Council with their decision to imprison Dextren Ward in Stormwind rather than behead the villain as per Lord Ebonlocke\'s sentence.$b$bWard was caught selling bodies from the cemetery to Morbent Fel, a crime punishable by death.  Yet Stormwind claims Ward as their prisoner.$b$bAssassinate Ward in The Stormwind Stockade - bring me his hand - and I shall reward you.",
  },
  [378] = {
    ["T"] = "The Fury Runs Deep",
    ["O"] = "Motley Garmason wants Kam Deepfury\'s head brought to him at Dun Modr.",
    ["D"] = "You have proven your loyalty to King Magni, $c.  And your hatred for the Dark Iron scum is as great as my own.$b$bThere is a task I wish to complete myself but I am bound by honor to stay with Longbraid.  But Roggo has gathered intelligence that proves Kam Deepfury was a conspirator in the attack on the Thandol Span.  It was by Deepfury\'s planning that Longbraid lost his kin.$b$bDeepfury is being held for political reasons in the Stormwind Stockade.  I want him dead, $N.  For Longbraid!  Bring me his head!",
  },
  [379] = {
    ["T"] = "Slake That Thirst",
    ["O"] = "Bring 5 Wastewander Water Pouches to Chief Engineer Bilgewhizzle in Gadgetzan.",
    ["D"] = "You want a power source.  I can help you with that.  Know what I want?  I want something to drink!  Bring me five Wastewander Water Pouches, and I\'ll give you the 4711-FTZ.$B$BNow... guess where you can find Wastewander Water Pouches!  Why don\'t you walk up to one of the Wastewander nomads to the east of here and ask them for one.  Let me know how that works out for you.",
  },
  [380] = {
    ["T"] = "Night Web\'s Hollow",
    ["O"] = "Executor Arren wants you to kill 10 Young Night Web Spiders and 8 Night Web Spiders.",
    ["D"] = "One of our greatest struggles is obtaining the natural resources we need to survive. Gold was scarce even in the height of the Alliance\'s power.$b$bThere is a gold mine to the northwest that has been overrun with spiders. We need gold from the mine, but we can\'t very well get it while the spiders are crawling around in there. I\'ve not much manpower to commit to the task, so we\'ll just have to do it little by little.$B$BGet up there and see what you can do for us, $n.",
  },
  [381] = {
    ["T"] = "The Scarlet Crusade",
    ["O"] = "Bring Executor Arren 12 Scarlet Armbands from Scarlet Converts and Scarlet Initiates.",
    ["D"] = "You\'ll be happy to know we appear to be making progress in the mine, thanks in no small part to your efforts.  We can now turn our eyes to other concerns.$b$bMy scouts have reported that a detachment of the Scarlet Crusade is setting up a camp southeast of here. The Scarlet Crusade is a despicable organization that hunts us, and they will not rest until every undead--Lich King\'s Scourge or no--is destroyed. We must strike first!$b$bBe careful, their unholy zeal makes them dangerous adversaries.",
  },
  [382] = {
    ["T"] = "The Red Messenger",
    ["O"] = "Kill Meven Korgal, the messenger, at the Crusader camp, then return any information you find to Executor Arren.",
    ["D"] = "Reports from my superiors in Brill indicate that an agent of the Scarlet Crusade was dispatched from the Scarlet Monastery and sent here to Deathknell. According to the reports, the messenger possesses important information on his person. I do not have further details, but whatever it is, we must have it!$b$bYou\'re the most qualified person I have to send, $n. Find that messenger and bring me whatever information you can find.",
  },
  [383] = {
    ["T"] = "Vital Intelligence",
    ["O"] = "Deliver the Scarlet Crusade Documents to Executor Zygand in Brill.",
    ["D"] = "These will need to be taken to my superior, Executor Zygand, in Brill. With this information, we\'ll be able to deal a decisive blow to the Scarlet Crusade. Your services have been useful here in Deathknell, but I need someone to deliver this to Zygand, and I believe he\'ll be able to find you more appropriate work.$b$bTake the road north out of Deathknell. Soon after entering Tirisfal proper, you\'ll hit a crossroads. Take the eastern fork, and keep going east. You\'ll pass Cold Hearth Manor along the way.",
  },
  [384] = {
    ["T"] = "Beer Basted Boar Ribs",
    ["O"] = "Ragnar Thunderbrew in Kharanos wants 6 Crag Boar Ribs and a mug of Rhapsody Malt.",
    ["D"] = "Nothing my tavern patrons enjoy more than some Beer Basted Boar Ribs!  Only problem is, the local trapper who used to bring me supplies enlisted in the King\'s Army to help with the war on the Alliance front. $b$bMaybe you can help me out?  If you bring me six crag boar ribs and a Rhapsody Malt from the tavern here, I\'ll give you the family recipe for my famous Beer Basted Boar Ribs, not to mention a free sample!  The secret\'s in the Malt!",
  },
  [385] = {
    ["T"] = "Crocolisk Hunting",
    ["O"] = "Get 5 pieces of Crocolisk Meat and 6 Crocolisk Skins for Marek Ironheart at the Farstrider Lodge.",
    ["D"] = "Many a hunter is attracted to Loch Modan to hunt our famous crocs. There are always merchants who seek out crocolisk skins for clothing items or armor, and there are also some who enjoy the taste of their meat.$b$bWe do some trade in this, but not a huge amount, as the crocolisks are ferocious and have entrenched themselves on the islands in the Loch. But don\'t let me dissuade you, it\'s quite an experience, wrestling with the jaws of the beasts.$b$bWhy, this one time...",
  },
  [386] = {
    ["T"] = "What Comes Around...",
    ["O"] = "Bring the head of Targorr the Dread to Guard Berton in Lakeshire.",
    ["D"] = "Targorr the Dread served under Gath\'Ilzogg as supreme Executioner.  His methods were ruthless, even by filthy orc standards.  Men who fought bravely to defend the Kingdom were tortured on his whim.  Now he is being held in the Stormwind Stockade, sentenced to die.  Yet something is amiss.  One of the bureaucratic nobles put a hold on his execution.  I am sure foul play is in the works.$b$bPut an end to Targorr the Dread, $N.  Travel to The Stockade and behead him before trickery is upon us.",
  },
  [387] = {
    ["T"] = "Quell The Uprising",
    ["O"] = "Warden Thelwater of Stormwind wants you to kill 10 Defias Prisoners, 8 Defias Convicts, and 8 Defias Insurgents in The Stockade.",
    ["D"] = "The uprising must be quelled!  I need your assistance.  If word gets out that the prison is overrun I will lose my job!  This is a case of foul play if I\'ve ever seen one.  But what\'s important now is forcing these prisoners into submission for the safety of Stormwind.$b$bThe punishment for insurgency is death.  Kill some of these deviants!  That ought to get things under control.",
  },
  [388] = {
    ["T"] = "The Color of Blood",
    ["O"] = "Nikova Raskol of Stormwind wants you to collect 10 Red Wool Bandanas.",
    ["D"] = "Bitterness flows through my veins like a cold river.  My grandson, Mac, was such a good boy.  A mere 14 years old.  When the army was sent off to the front, Mac took up arms to defend the city.  He was a guard in The Stockade, where the Defias gang is locked up.  Mac reprimanded some of the prisoners for tearing their wool blankets to make scarves, a symbol of their gang.  For that Mac was stabbed in the back.$b$bI want revenge.  Bring me 10 of their precious Wool Bandanas.  They are the color of blood.",
  },
  [389] = {
    ["T"] = "Bazil Thredd",
    ["O"] = "Speak with Warden Thelwater in the Stockade.",
    ["D"] = "VanCleef and I were members of the Stonemasons Guild. Our main project was to rebuild Stormwind after the War.$B$BWhen we had finished our duties, we were cheated. The nobles refused to pay us for our work. Some of the more senior of the Stonemasons were offered governmental jobs, but VanCleef refused it out of loyalty to all the Stonemasons. He led a riot and left the city, swearing revenge.$b$bHis lieutenant, Bazil Thredd, might know more about VanCleef\'s plans. He\'s being held prisoner in the Stockade.",
  },
  [391] = {
    ["T"] = "The Stockade Riots",
    ["O"] = "Kill Bazil Thredd and bring his head back to Warden Thelwater at the Stockade.",
    ["D"] = "Let\'s get one thing straight, $N: I don\'t trust you. But, with the situation as it is, you probably won\'t make a difference.$B$BSo here\'s how it\'s going to be: go down there and have your talk with Thredd. If you\'re really on our side, then kill him and bring me his head when the job\'s done. And if it turns out you\'re on his side, and don\'t come out... when we find you, you\'ll die along with the rest of the maggots.",
  },
  [392] = {
    ["T"] = "The Curious Visitor",
    ["O"] = "Bring the Sealed Description of Thredd\'s Visitor to Baros Alexston in Stormwind.",
    ["D"] = "During the first years of Thredd\'s imprisonment, he never had any visitors. I figured that he was no longer of use to the Brotherhood, so they abandoned him.$B$BAnyway, a few months ago, that all changed; he started to get regular visits... once, maybe twice a week. It was a strange man--the quiet type. I had my suspicions, but all his papers checked out.$B$BHis documents claimed his name was Maelik; here\'s his description. I don\'t think he\'ll be coming to visit Thredd anymore.",
  },
  [393] = {
    ["T"] = "Shadow of the Past",
    ["O"] = "Bring the Sealed Description of Thredd\'s Visitor to Master Mathias Shaw in Stormwind.",
    ["D"] = "I really haven\'t a clue, $N. There is someone else who might be able to help you, and it so happens that he owes me a favor for--well, let\'s not get into that. Master Mathias Shaw of SI:7 can be found in the barracks in Old Town. Shaw has his thumb on most of the shady, or underhanded dealings of the city.$B$BIf anyone knows, it\'d be him.",
  },
  [394] = {
    ["T"] = "The Head of the Beast",
    ["O"] = "Speak to Mathias Shaw in Stormwind.",
    ["D"] = "Find Shaw in Old Town. Let him know that the Lescovar threat has been dealt with and about your involvement in the Lord\'s plots against the city. I\'m sure he\'ll be proud to have another hero among Stormwind\'s citizens.$B$BHe\'ll more than likely send you off to meet with the Prince or some other noble. Don\'t let it get to your head, $N. The nobles can think they rule the city all they want, but some things remain out of their hands.",
  },
  [395] = {
    ["T"] = "Brotherhood\'s End",
    ["O"] = "Speak with Baros Alexston in Stormwind.",
    ["D"] = "Ah, I almost forgot. Alexston came while you were away, he wished to speak with you. No doubt he wishes to report the goings-on of the Defias Brotherhood to the nobles.$B$BA bit of advice, $N: if anyone should ask, I would provide all details but that it was your hand that slew Lescovar. Trust me. I am... acquainted... with the ways of such things.$B$BAnd as far as Trias goes, well, speak to no one of him... ever... if you can help it.",
  },
  [396] = {
    ["T"] = "An Audience with the King",
    ["O"] = "Deliver Baros Alexston\'s report on the Defias Brotherhood to King Varian Wrynn in Stormwind Keep.",
    ["D"] = "I have prepared a report for the King. I believe you should be the one to deliver it to him, along with an account of your findings. Do not worry, there is no doubt that he will commend you for doing your duty to the people of Stormwind.$B$BYour audience is scheduled soon, so you\'d best hurry, it\'s a bit of a ways to Stormwind Keep.",
  },
  [397] = {
    ["T"] = "You Have Served Us Well",
    ["O"] = "Speak with Zggi, the servant of Zardeth the Blackclaw, to claim your reward.",
    ["D"] = "I hear Lord Wishock enjoyed his beverage.  $b$bYou have served us well, $N.  The Warlocks of Stormwind will be able to practice our ways without disturbance, thanks to you.  For your efforts, I have prepared a special gift for you.  Speak with my servant, Zggi, and claim your reward.",
  },
  [398] = {
    ["T"] = "Wanted: Maggot Eye",
    ["O"] = "Kill Maggot Eye and return to Executor Zygand in Brill with his paw for a reward.",
    ["D"] = "Wanted: Maggot Eye!$b$bA foul beast, even by Gnoll standards, Maggot Eye has been sentenced to death for his crimes against The Forsaken.  This despicable villain has been stealing corpses for use by the Lich King\'s army.  Maggot Eye was last seen pillaging the lands near Garren\'s Haunt to the north of Brill.$b$bThe town of Brill hereby offers a reward to the capable soul who can carry out the execution.  Show Executor Zygand the villain\'s paw once the deed is done.$b$bMercy is for the weak.",
  },
  [399] = {
    ["T"] = "Humble Beginnings",
    ["O"] = "Go to Baros Alexston\'s house in Westfall and search for his compass, then return it to him in Cathedral Square of Stormwind.",
    ["D"] = "It seems an eternity since I was a boy working the farm in Westfall. They say you can never go back, and it\'s true. Doubly true in my case, my family\'s home has been burned down and taken over by thieves.$b$bI have spoken with my father about the fate of some of my possessions, including my first compass. He was unable to save them. However, he also says that they should be hidden away on the farm.$b$bYou\'ll find the Alexston Farmstead west of Sentinel Hill. Perhaps you could go and retrieve it for me?",
  },
  [400] = {
    ["T"] = "Tools for Steelgrill",
    ["O"] = "Deliver Steelgrill\'s Tools to Beldin Steelgrill.",
    ["D"] = "Beldin Steelgrill owns the local mechanic shop, and he\'s the best siege engine tech there is!  But he\'s not forgiving to his tools.  I swear he almost breaks more arclight spanners than we can supply him with!$B$BWe just filled his last order for tools.  If you deliver it to him I\'m sure he\'ll make it worth the effort.$B$BHis shop, Steelgrill\'s Depot, is just northeast of Kharanos.  And it\'s a local haunt for veteran siege engine pilots, so keep your ears open for opportunities while you\'re there.",
  },
  [401] = {
    ["T"] = "Wait for Sirra to Finish",
    ["O"] = "Wait a moment, then speak with Sirra Von\'Indi again.",
    ["D"] = "I\'m almost finished translating that note.  If you come back in a few moments I should be done.",
  },
  [403] = {
    ["T"] = "Guarded Thunderbrew Barrel",
    ["O"] = "",
    ["D"] = "",
  },
  [404] = {
    ["T"] = "A Putrid Task",
    ["O"] = "Bring 7 Putrid Claws to Deathguard Dillinger in Brill.",
    ["D"] = "Scourge have trickled into Tirisfal Glades and have infested the area west of Brill, near the old bridge.$B$BGo there and beat back the rotting dead and ravaged corpses you find.  Scatter their bones and bring back their putrid claws, and the Deathguards will reward you.",
  },
  [405] = {
    ["T"] = "The Prodigal Lich",
    ["O"] = "Present Sevren\'s Orders to Bethor Iceshard in the Undercity.",
    ["D"] = "Bethor Iceshard, a high-ranking and powerful mage in the Undercity to the south, commands me to send him an agent with proven worth against the Scourge.  You, $N, will be that agent.$B$BPresent these orders to Bethor.  He will then instruct you on your mission.  I don\'t know its details, but it deals with recruiting a wayward Lich.  And it will be dangerous.$B$BSo ready yourself, $C.  You must not fail.$B$BYou may find Bethor in the Magic Quarter of the Undercity.",
  },
  [407] = {
    ["T"] = "Fields of Grief",
    ["O"] = "Take the Laced Pumpkin to the Captured Scarlet Zealot who is being held in the cellar of the Gallow\'s End Tavern.",
    ["D"] = "Harmless pumpkins, right?  Or so it would seem.  If we are to defeat Arthas\'s advances from the North and the Human infestation from the South we need to start realizing the full potential of our gift of undeath.$b$bWith a little ingenuity a simple pumpkin becomes an agent for our Dark Lady.  This pumpkin, laced with my latest formula, will prove to be quite a treat.$b$bYet another Scarlet Zealot has been captured and is being kept in the cellar of the Gallow\'s End Tavern.  Take this pumpkin to the fool.",
  },
  [408] = {
    ["T"] = "The Family Crypt",
    ["O"] = "Kill 8 Wailing Ancestors and 8 Rotting Ancestors.$B$BKill Captain Dargol, and bring his skull to Magistrate Sevren in Brill.",
    ["D"] = "The Agamand Mills area has become a foothold for the Scourge.  Their strongest point is the Agamand Family Crypt where their local military leader, a skeleton named Captain Dargol, has headquartered.  With Necromancy he has raised the ancestors of the Agamands and plans to use them against us.$B$BThis cannot pass, and you must stop it.  Travel to the Agamand Family Crypt and defeat the raised Agamand ancestors.$B$BAnd, bring me the skull of Captain Dargol.",
  },
  [409] = {
    ["T"] = "Proving Allegiance",
    ["O"] = "Obtain a Candle of Beckoning.$B$BSummon Lillith Nefara and kill her.$B$BReturn to Gunther on his island.",
    ["D"] = "You say you are not bound by the will of the Lich King, and the enchantment Bethor placed upon my book corroborates this.  But before I believe you I must have proof that you are enemy to the Scourge.$B$BThere is an old, bloody altar just off the southern coast of this island.  it is used by a powerful Scourge agent, Lillith Nefara.$B$BTake a Candle of Beckoning from this crate here.  Light it on the altar and Lillith will awake.  Destroy Lillith, and I will believe you are the Lich King\'s foe.",
  },
  [410] = {
    ["T"] = "The Dormant Shade",
    ["O"] = "",
    ["D"] = "",
  },
  [411] = {
    ["T"] = "The Prodigal Lich Returns",
    ["O"] = "Bring the Nether Gem to Bethor Iceshard in the Undercity.",
    ["D"] = "Take this Nether Gem to my old comrade Bethor.  It will let us communicate ... and I have much to tell him.",
  },
  [412] = {
    ["T"] = "Operation Recombobulation",
    ["O"] = "Bring Razzle Sprysprocket in Kharanos 8 Restabilization Cogs and 8 Gyromechanic Gears.",
    ["D"] = "Upon further analysis of the Gnomeregan situation, it would appear as though we not only failed to eradicate the troggs but we also happened to turn most of the gnomish race into horrific, mindless, evil-doing leper gnomes.$b$bOzzie and I plan to reverse the horrible leper effect with our latest invention: The Recombobulator.  The machine is almost complete but we are in dire need of some Restabilization Cogs and Gyromechanic Gears.  Retrieve some from the leper gnomes out front of Gnomeregan for us.",
  },
  [413] = {
    ["T"] = "Shimmer Stout",
    ["O"] = "Take the Barrel of Shimmer Stout to Mountaineer Barleybrew.",
    ["D"] = "I\'d like my brother Wellart to try my new Shimmer Stout.  He\'s a Mountaineer stationed in the southern guard tower, one of two towers on the border to Loch Modan.$B$BHe may not be a brewer like the rest of us Barleybrews, but he loves his drinks and I know he\'ll like this one.$B$BCan you bring him this Barrel of Shimmer Stout for me?",
  },
  [414] = {
    ["T"] = "Stout to Kadrell",
    ["O"] = "Take the Cask of Shimmer Stout to Mountaineer Kadrell.",
    ["D"] = "I\'ve been drinking more of that Shimmer Stout my brother made - it\'s great stuff!  I want a fellow Mountaineer to try it.  His name\'s Kadrell, and he\'s usually patrolling the road through Thelsamar in Loch Modan.$B$BTo get to Thelsamar, go southeast through the tunnel, then turn northeast when the path forks into a road.  Follow that road straight into Thelsamar.$B$BGive Mountaineer Kadrell this cask of Shimmer Stout and see what he thinks!",
  },
  [415] = {
    ["T"] = "Rejold\'s New Brew",
    ["O"] = "Speak with Rejold Barleybrew.",
    ["D"] = "Rejold Barleybrew at Brewnall Village has been asking for you.$B$BHe wants to talk to you about a new drink he\'s been brewing...said something about you helping him with it.  You might want to head back to Brewnall Village and see what he has to say.$B$BThere could be some free booze in it for you...",
  },
  [416] = {
    ["T"] = "Rat Catching",
    ["O"] = "Bring 12 Tunnel Rat Ears to Mountaineer Kadrell in Thelsamar.",
    ["D"] = "We have a real infestation problem to the west.  Tunnel rat kobolds have moved into the foothills, setting up dens and fouling our land with their filth!$B$BWe want them eliminated.  Hunt them, bring me their ears and earn their bounty.$B$BYou can find tunnel rat kobolds and their dens west of Thelsamar, littered along the mountainside.",
  },
  [417] = {
    ["T"] = "A Pilot\'s Revenge",
    ["O"] = "Kill Mangeclaw.$B$BBring his Mangy Claw and Hildelve\'s Journal to Pilot Hammerfoot.",
    ["D"] = "Hildelve wrote in his journal that he was attacked by Mangeclaw, a \"huge Ice Claw Bear.\"  He drove off the beast, but not before it mauled him savagely.$B$BJudging from the state of Hildelve\'s body, Mangeclaw must have later returned and killed him.$B$BHowever, Hildelve wrote in his journal his one, final wish:$B$BRevenge against Mangeclaw.",
  },
  [418] = {
    ["T"] = "Thelsamar Blood Sausages",
    ["O"] = "Bring 3 pieces of Bear Meat, 3 Boar Intestines, and 3 Spider Ichor to Vidra Hearthstove in Thelsamar.",
    ["D"] = "There\'s never a shortage of empty bellies here in Thelsamar, kids running in and out, workers from the excavation coming in after  a hard day\'s work. We\'re famous for our blood sausages, I don\'t suppose you\'ve ever tried them?$b$bNo? Well, around here you\'ve got to work for your meals, and don\'t think just because you\'re a fancy $c, you\'ll be any exception.$b$bI\'ll need bear meat, boar intestines for the casings, and spider ichor for spice. You get me some of those, and leave the cooking to Vidra!",
  },
  [419] = {
    ["T"] = "The Lost Pilot",
    ["O"] = "Find Pilot Hildelve.",
    ["D"] = "My friend and fellow Siege Engine pilot, Mori Hildelve, is lost in the hills.  We were looking for a rare ore that\'s needed for a high-grade blastpowder, and during our search he drove his engine up a steep hill and busted it!$B$BStill convinced that ore was in these mountains, Hildelve charged me to guard our engines while he continued on foot, searching.$B$BIt\'s been days, and I\'ve heard some beastly growls in the hills at night.  Mori\'s tough as nails, but I\'m worried.$B$BPlease, $N.  Find him.",
  },
  [420] = {
    ["T"] = "Senir\'s Observations",
    ["O"] = "Deliver Grelin\'s report to Senir Whitebeard in Kharanos.",
    ["D"] = "The troggs in the tunnel are extremely hostile, and haven\'t shown any hesitation in attacking travelers through the tunnel. You seem tough enough, though, so you should probably be safe.$b$bDirections? If you\'re going to speak with Senir, you\'ll find him in Kharanos.$b$bMake your way through the tunnel, and after you come out on the other side, just keep following the road, it\'ll run right into Kharanos.",
  },
  [421] = {
    ["T"] = "Prove Your Worth",
    ["O"] = "Dalar Dawnweaver at the Sepulcher wants you to kill 5 Moonrage Whitescalps.",
    ["D"] = "Lady Sylvanas has charged Varimathras with the conquering of the human and dwarven lands to the south.$b$bBut that fool, Arugal -- charlatan of Dalaran and now cursed beast of Shadowfang Keep -- let his reckless magic wreak havoc with the strategic stronghold of Silverpine Forest.$b$bI need someone skilled in the ways of combat to help clean up Arugal\'s mess.  Prove yourself to me by killing 5 Moonrage Whitescalps.  The wretched beasts can often be found just off the road, down the hill below.",
  },
  [422] = {
    ["T"] = "Arugal\'s Folly",
    ["O"] = "Retrieve the Remedy of Arugal for Dalar Dawnweaver at the Sepulcher.",
    ["D"] = "How Arugal gained acceptance within the Kirin Tor is beyond me.  His spell-casting knowledge seemed as transparent as a blown glass bauble.$b$bWhat\'s important now is learning exactly what magic Arugal used so that I can turn it against him and secure Silverpine Forest for the Dark Lady.$b$bArugal first stayed at the wheat farm just north of the bridge.  One of the Deathstalkers reported seeing some spellbooks but could not secure them.  Retrieve for me the spell labeled Remedy of Arugal from those books.",
  },
  [423] = {
    ["T"] = "Arugal\'s Folly",
    ["O"] = "Bring 6 Glutton Shackles and 3 Darksoul Shackles to Dalar Dawnweaver at the Sepulcher.",
    ["D"] = "After examining Arugal\'s work my worst suspicions were confirmed.  The old hack was not qualified to clean chamber pots in Dalaran let alone represent the Kirin Tor in its most dire hour.  Fools!$b$bArugal used enchanted items to reinforce his weak magic.  I need to examine these items first hand.  Travel forth and slay Moonrage Gluttons and Moonrage Darksouls until you have collected enough of their enchanted shackles for my research.  The foul creatures have been seen to the north and east.",
  },
  [424] = {
    ["T"] = "Arugal\'s Folly",
    ["O"] = "Kill Grimson the Pale and bring his head to Dalar Dawnweaver at the Sepulcher.",
    ["D"] = "It will indeed take me longer than I had thought to uncover the dark secrets behind the enchantments Arugal was using.$b$bBut in the meantime I need you to take care of a slight problem our Darkstalkers have discovered.  It seems that Arugal let his magic spread to the Deep Elem Mine in the hills to the southeast.$b$bThe mine would prove to be quite a resource for Varimathras\'s advance.$b$bI want you to behead the tainted foreman of the mine, Grimson the Pale.  With his death, the mine shall be ours.",
  },
  [425] = {
    ["T"] = "Ivar the Foul",
    ["O"] = "Kill Ivar the Foul, and bring Ivar\'s Head to Rane Yorick at the Ivar Patch.",
    ["D"] = "My brother Quinn and I were tasked to recon Silverpine Forest and report to High Executor Hadrec.  But we didn\'t get far -- when moving through this farm we were attacked by the ghoul Ivar the Foul.$B$BWe escaped from Ivar, but he seriously mauled Quinn.  He\'s healing from the potion you gave him, but Ivar may return before Quinn recovers.$B$BKill Ivar so Quinn will have time to heal.  Ivar is usually in the barn yonder, waiting for my guard to drop.",
  },
  [426] = {
    ["T"] = "The Mills Overrun",
    ["O"] = "Gather 5 Notched Ribs and 3 Blackened Skulls, then return to Deathguard Dillinger in Brill.",
    ["D"] = "The Scourge are trying to form a base at the Agamand Mills.  If they do, then they can stage further attacks within Tirisfal.$B$BThe orders have been given.  The Scourge in the Mills must be destroyed.$B$BGo to the Mills.  Collect Notched Ribs from Rattlecage and Cracked Skull Soldiers, and Blackened Skulls from Darkeye Bonecasters.$B$BTo get to the Mills, follow the road west.  After crossing the bridge, take the next fork north then keep going north.  When you see windmills, the battle will begin.",
  },
  [427] = {
    ["T"] = "At War With The Scarlet Crusade",
    ["O"] = "Executor Zygand of Brill wants you to kill 10 Scarlet Warriors.",
    ["D"] = "The documents Executor Arren provided are just the break we needed in our battle with the wretched Scarlet Crusade.  We now know their exact locations throughout Tirisfal Glades.$b$bBut the Deathguard has larger concerns.  The Lich King\'s army grows in number each night.  We need someone with \"initiative\" like yourself to drive the Scarlet Crusade to the grave.$b$bProve to me you are capable of serving The Dark Lady by travelling west to the tower past the Solliden Farmstead and slay 10 Scarlet Warriors.",
  },
  [428] = {
    ["T"] = "Lost Deathstalkers",
    ["O"] = "Find the Deathstalkers Quinn and Rane Yorick.",
    ["D"] = "A couple weeks ago two of our Deathstalkers, Rane and Quinn Yorick, were sent on a reconnaissance mission through northern Silverpine.  We have not heard from them.$B$BThey may have become victim to the Scourge, though if that is true then I want it confirmed.  Find these Deathstalkers.$B$BTheir first objective was to scout the farms in northern Silverpine.  You should begin your search there.",
  },
  [429] = {
    ["T"] = "Wild Hearts",
    ["O"] = "Gather 6 discolored worg hearts and bring them to Apothecary Renferrel at the Sepulcher.",
    ["D"] = "My brother Quinn was badly hurt by the ghoul, Ivar the Foul, and I don\'t know if he will heal properly without magical aid.  Although I am not an apothecary, I do know that our apothecaries can make healing potions from the discolored hearts of worgs.$B$BGather such hearts and take them to Apothecary Renferrel at the Sepulcher to the south.  Then return here with the potion.$B$BYou can find plenty of worgs between here and Malden\'s Orchard to the east.",
  },
  [430] = {
    ["T"] = "Return to Quinn",
    ["O"] = "Bring Quinn\'s potion to Quinn Yorick at the Ivar Patch, north of the Sepulcher.",
    ["D"] = "Here is the potion to aid in Quinn Yorick\'s recovery.$B$BNow if you will excuse me, I have very important work to continue...",
  },
  [431] = {
    ["T"] = "Candles of Beckoning",
    ["O"] = "",
    ["D"] = "",
  },
  [432] = {
    ["T"] = "Those Blasted Troggs!",
    ["O"] = "Kill 6 Rockjaw Skullthumpers for Foreman Stonebrow at the Gol\'Bolar quarry.",
    ["D"] = "Those Light-blasted troggs, turning my work site into a wreck! Look at that, there goes another barrel of powder!$b$bOhhh, I wish I could go down there and wring every one of their scrawny little necks! I want to kill them all!$b$bThis can\'t be good for my health, all this stress and anger... What have they left me with? Nothing!$b$bI\'d kill them myself, but my aim\'s no good anymore! You go into the quarry and kill some of the filthy buggers! I\'ll pay you! Just hurt them, kill them!",
  },
  [433] = {
    ["T"] = "The Public Servant",
    ["O"] = "Kill 10 Rockjaw Bonesnappers for Senator Mehr Stonehallow at the Gol\'Bolar quarry.",
    ["D"] = "As a member of the Senate and the Explorers\' League, I\'ve taken it upon myself to take care of this part of the trogg infestation that has gripped our lands.$b$bThey\'ve certainly made a mess of Gol\'Bolar quarry, and for no reason. As we dug deep into the earth, they poured out, destroying our equipment and driving the miners out. There\'s not much for us to do but to exterminate the lot of them, rebuild, then get back to work.$b$bIf you help me with the troggs, I\'ll gladly recompense you for your time.",
  },
  [434] = {
    ["T"] = "The Attack!",
    ["O"] = "Remain in the garden until Lord Gregor Lescovar and Marzon the Silent Blade have had their conversation. Afterwards, kill them both before returning to Elling Trias in Stormwind.",
    ["D"] = "After I put on Spybot\'s disguise, he\'ll head in to talk to Lescovar. The guards should let him pass no problem.$B$BWhile you were getting materials for me, I took care of informing Marzon that it was time for him and Lescovar to meet again. I even dropped some false information.$B$BLescovar will dismiss his guards and wait for Marzon out here in the garden. After you catch wind of talk about the Defias, make your move. You need to kill them both before the guards return! When you\'re done, speak to Trias.",
  },
  [435] = {
    ["T"] = "Escorting Erland",
    ["O"] = "Escort Erland through the wolves, to Rane Yorick.",
    ["D"] = "The wolves outside won\'t leave, and they eye me evilly every time I step foot out that door.  They must be incredibly hungry to think I\'m worth eating.$B$BI have to report to my teammate, the Deathstalker Rane Yorick.  And to get to Rane, I\'ll need help against those wolves.$B$BPlease escort me!  And there are a lot of wolves out there, so we\'ll have a better chance if you have friends who can help...",
  },
  [436] = {
    ["T"] = "Ironband\'s Excavation",
    ["O"] = "Speak with Explorer Fellhew.",
    ["D"] = "Prospector Ironband is heading an excavation of ancient ruins east of the lake.  His progress has been slow lately, especially considering all the supplies we\'ve sent him.$B$BIronband\'s a stout, honest dwarf who values results, which has me worried that forces are at work against him.$B$BGo to Ironband\'s Excavation and speak with Magmar Fellhew.  He manages the details of the site and will know why there\'s a slowdown.$B$BTo get to the excavation site, go around the southern tip of the lake, then head east.",
  },
  [437] = {
    ["T"] = "The Dead Fields",
    ["O"] = "Kill rot hide gnolls at the Dead Fields.$B$BWhen Nightlash appears, kill her.$B$BBring the Essence of Nightlash to High Executor Hadrec at the Sepulcher.",
    ["D"] = "Our initial scouting reports of Silverpine show the rot hides have a base at an old farm to the north, the Dead Fields.  We have sent small assaults against them, but each time they are reinforced by the banshee Nightlash, who uses her magic to rout our forces.$B$BWe want Nightlash destroyed.$B$BGo to the Dead Fields and assault the rot hides.  If you don\'t see Nightlash then kill the rot hides until she appears, then defeat her.$B$BHer essence will turn to dust.  Bring me that dust as proof.",
  },
  [438] = {
    ["T"] = "The Decrepit Ferry",
    ["O"] = "Go to the Decrepit Ferry.$B$BSearch for the reason the Scourge are there.",
    ["D"] = "$N, we\'ve discovered a new hive for the Scourge: the Decrepit Ferry.  The ferry lies east of the Sepulcher, and is heavily patrolled by rot hides and other undead.$B$BThere must be a reason for the Scourge\'s occupation of the area.  Go to the Decrepit Ferry and find out why the Scourge are there.",
  },
  [439] = {
    ["T"] = "Rot Hide Clues",
    ["O"] = "Bring Deliah\'s Ring to High Executor Hadrec at the Sepulcher.",
    ["D"] = "Among the corpses a hand protrudes, unwrapped.  It is the hand of a woman, pockmarked from the plague but relatively fresh.  $B$BOn the hand is a ring.  Removing the ring and inspecting it reveals an engraving:$B$B\"For Deliah\"",
  },
  [440] = {
    ["T"] = "The Engraved Ring",
    ["O"] = "Bring Deliah\'s Ring to Magistrate Sevren in Brill.",
    ["D"] = "If you want to find the husband or beloved who gave Deliah her ring... then speak with Magistrate Sevren in Brill.  He should have records of recent deaths and, with luck, can discover who Deliah is.$B$BBut I wouldn\'t hope.  Whoever gave her the ring is probably dead.  Or if he\'s undead he probably no longer cares about Deliah.",
  },
  [441] = {
    ["T"] = "Raleigh and the Undercity",
    ["O"] = "Bring Deliah\'s Ring to Raleigh.",
    ["D"] = "Deliah had a husband, Raleigh, who now dwells in the Undercity.  If the Deliah who lived in Brill is the one who owned this ring, then Raleigh will know.$B$BRaleigh is in the Trade Quarter of the Undercity.",
  },
  [442] = {
    ["T"] = "Assault on Fenris Isle",
    ["O"] = "Bring Thule\'s Head to High Executor Hadrec at the Sepulcher.",
    ["D"] = "Now is the time to assault the Scourge\'s place of strength in Silverpine: Fenris Isle.$B$BYou must again go to the Isle and destroy Thule Ravenclaw.  I want his head!$B$BThis mission is as dangerous as it is vital, $N.  You must not fail.",
  },
  [443] = {
    ["T"] = "Rot Hide Ichor",
    ["O"] = "Bring 8 bottles of rot hide ichor to Apothecary Renferrel at the Sepulcher.",
    ["D"] = "Before we launch an assault on Fenris Isle, we must determine who is behind the rot hide gnolls.  Our apothecaries believe their origin lies within the strange fluid that flows through their bodies.$B$BGo to Fenris Isle, east of the Decrepit Ferry, and collect rot hide ichor from the gnolls you find there.  Bring it to Apothecary Renferrel, stationed outside the Sepulcher.",
  },
  [444] = {
    ["T"] = "Rot Hide Origins",
    ["O"] = "Bring the Sample Ichor to Bethor Iceshard in the Undercity.",
    ["D"] = "I have reduced the ichor you brought me and separated it into its base components.  My findings are not conclusive, but I have determined that warlock and necromantic magics were used in the ichor\'s creation.$B$BWhile I continue to study the ichor, I want you to take a sample to Bethor Iceshard in the Undercity.  Bethor is not an apothecary, but his knowledge of magic may help to unravel the mystery of the rot hides.$B$BGo.  You will find Bethor in the Magic Quarter of the Undercity.",
  },
  [445] = {
    ["T"] = "Delivery to Silverpine Forest",
    ["O"] = "Take Apothecary Johaan\'s findings to Apothecary Renferrel in Silverpine Forest.",
    ["D"] = "Time is a luxury that is not afforded to those of us under the employ of Lady Sylvanas.  Surely you know this by now.$b$bAs a member of the Royal Apothecary Society it is my duty to share my knowledge with my colleagues so that our collective efforts might one day provide The Dark Lady with the New Plague she so badly desires.$b$bTake these findings to Apothecary Renferrel who is stationed at The Sepulcher in Silverpine Forest.  Follow the roads to the south from Brill.  From the Undercity go southwest.",
  },
  [446] = {
    ["T"] = "Thule Ravenclaw",
    ["O"] = "Bring Bethor\'s Scroll to Apothecary Renferrel at the Silverpine Sepulcher.",
    ["D"] = "This Ichor was crafted by an old colleague of mine: Thule Ravenclaw.  He was a young, promising mage before the great war, but when the Lich King rose to power, he betrayed his brethren and allied with the Scourge.$B$BI did not realize he still lives, nor that he leads the Rot Hides in their forays against us!$B$BI know many of his spells, and can help concoct a potion to counter them.  Take this scroll back to Apothecary Renferrel.  It has the incantations needed for the potion.",
  },
  [447] = {
    ["T"] = "A Recipe For Death",
    ["O"] = "Collect 6 Grizzled Bear Hearts and 6 samples of Skittering Blood and deliver them to Master Apothecary Faranell in the Undercity.",
    ["D"] = "Arthas\'s numbers are overwhelming. But with a New Plague we could eradicate both the Scourge Army and the Human infestation once and for all.$b$bMy studies have proven that spider blood combined with a toxin derived from a grizzled bear heart results in a deadly elixir. Collect samples from the spiders in the Skittering Dark to the northwest and from the bears wandering throughout Silverpine Forest. Deliver the reagents to Master Apothecary Faranell of the Royal Apothecary Society in the Undercity.",
  },
  [448] = {
    ["T"] = "Report to Hadrec",
    ["O"] = "Report to High Executor Hadrec at the Sepulcher.",
    ["D"] = "Before you launch an assault on Fenris Isle, report to High Executor Hadrec.  He may have additional information or equipment you\'ll need.",
  },
  [449] = {
    ["T"] = "The Deathstalkers\' Report",
    ["O"] = "Bring the Deathstalker Report to High Executor Hadrec at the Sepulcher.",
    ["D"] = "Although Quinn\'s wounds are mending, I want to get our report to High Executor Hadrec as quickly as possible.  It has information that is vital to the Forsaken\'s occupation of Silverpine.",
  },
  [450] = {
    ["T"] = "A Recipe For Death",
    ["O"] = "Retrieve Apothecary Berard\'s journal from Pyrewood Village and take it to Apothecary Renferrel at the Sepulcher.",
    ["D"] = "You can be of further service to the Royal Apothecary Society, $N.$b$bWhen Arugal cast his wretched magic on the forest, he cursed one of our most promising members, Apothecary Berard.  Now a mindless servant of Arugal, Berard is useless to us.  But his findings might prove to be of worth.  He was studying an ancient plague that killed off all the aquatic life in Lake Lordamere ages ago.$b$bRetrieve Berard\'s Journal from his study in the Pyrewood Village Inn and take it to Apothecary Renferrel.",
  },
  [451] = {
    ["T"] = "A Recipe For Death",
    ["O"] = "Bring 6 samples of Lake Creeper Moss, 6 samples of Lake Skulker Moss and a Hardened Tumor to Master Apothecary Faranell in the Undercity.",
    ["D"] = "According to Berard\'s Journal the moss from Lake Skulkers and Creepers might prove useful for our quest to brew a new plague.  Those beasts dwell on the islands in Lake Lordamere to the east.$b$bHe also mentions a very rare Hardened Tumor that he discovered on a Vile Fin murloc.  Apparently the tumor is so rare Berard was only able to obtain one.$b$bCollect samples of the moss as well as the rare hardened tumor and take them to Master Apothecary Faranell in the Undercity.",
  },
  [452] = {
    ["T"] = "Pyrewood Ambush",
    ["O"] = "Help Deathstalker Faerleia kill the Pyrewood Council.",
    ["D"] = "You got here just in the nick of time, $N!$b$bThe Pyrewood Council will be returning any moment.  Arugal has other matters to tend to in the keep.  He uses the Council as mere puppets to keep the mindless minions of Pyrewood under his control.  But I believe that without the guidance of the Pyrewood Council, the sons of Arugal will turn against their master.$b$bI cannot handle them all by myself.  I need you to help me.  Together we can lay waste to the Council for Lady Sylvanas.",
  },
  [453] = {
    ["T"] = "Finding the Shadowy Figure",
    ["O"] = "Find the Shadowy Figure.  Your clues:$B$BHe is not native to Darkshire.$B$BHe is a nervous, jittery person.$B$BHe left Darkshire and headed west.",
    ["D"] = "Good luck, $N.  I hope you find the chap, and I hope you find him alive.$B$BIf he\'s left the safety of Darkshire then he could be prey to the beasts of the wilds.",
  },
  [454] = {
    ["T"] = "After the Ambush",
    ["O"] = "Speak with Miran.",
    ["D"] = "We\'d been hauling the barrels of powder back and forth in trips before this... business. Now we have to split up the work, Miran\'s taking the barrels and I\'m watching over the other ones.$b$bI don\'t feel safe letting Miran haul the rest of the shipment by himself. When he\'s ready to go, maybe you could escort him on his next trip?",
  },
  [455] = {
    ["T"] = "The Algaz Gauntlet",
    ["O"] = "Traverse Dun Algaz.$B$BKill 8 Dragonmaw Scouts and 6 Dragonmaw Grunts.$B$BReport to Valstag Ironjaw in Menethil Harbor.",
    ["D"] = "Orcs of the Dragonmaw clan have a haven in the Wetlands.  And now they have expanded into Dun Algaz!  We can\'t let them keep their foothold here or Loch Modan will be cut off from Menethil Harbor, our port and stronghold in the Wetlands.$B$BBreak through Dun Algaz to the Wetlands beyond, and kill some orcs for good measure!  Report to Valstag Ironjaw in Menethil Harbor.$B$BMenethil is through Dun Algaz, then north along the road, then west at the fork.$B$BWe\'re counting on you, $N.  Do not fail.",
  },
  [456] = {
    ["T"] = "The Balance of Nature",
    ["O"] = "Kill 7 Young Nightsabers and 4 Young Thistle Boars and return to Conservator Ilthalaine.",
    ["D"] = "Greetings, $N.  I am Conservator Ilthalaine.  My purpose in Shadowglen is to ensure that the balance of nature is maintained.$b$bThe spring rains were particularly heavy this year, causing some of the forest\'s beasts to flourish while others suffered.$b$bUnfortunately, the nightsaber and thistle boar populations grew too large.  Shadowglen can only produce so much food for the beasts.  Journey forth, young $c, and thin the boar and saber populations so that nature\'s harmony will be preserved.",
  },
  [457] = {
    ["T"] = "The Balance of Nature",
    ["O"] = "Conservator Ilthalaine needs you to kill 7 Mangy Nightsabers and 7 Thistle Boars.",
    ["D"] = "Thinning the younger population of creatures here in Shadowglen was a good start, $N, but there is still work to be done.$b$bThe resources of the forest will be depleted too quickly if the problem is not addressed.  Killing nature\'s beasts is a necessary evil for the good of all who share the land.  Venture into the forest and slay mangy nightsabers and thistle boars in the name of balance.",
  },
  [458] = {
    ["T"] = "The Woodland Protector",
    ["O"] = "Seek out the dryad known as Tarindrella.",
    ["D"] = "Thank goodness you are here, $c. Strange news has traveled to me through the whisperings of the forest spirits.$b$bThe mysterious woodland protector, Tarindrella, has returned to Shadowglen. The dryad\'s presence has not been felt in the forests of Kalimdor in years. Something is surely amiss if she has journeyed back to this land.$b$bSeek out Tarindrella and see what business she tends to in our grove. One of the Sentinels reported seeing her to the southwest of Aldrassil.",
  },
  [459] = {
    ["T"] = "The Woodland Protector",
    ["O"] = "Collect 8 Fel Moss and bring them to Tarindrella.",
    ["D"] = "Something evil is brewing in the forests of Teldrassil.  Look long the hills to where the peaceful furbolgs used to dwell.  They have deserted their homes and are amassing under the name of the Gnarlpine tribe.$b$bOnly the corruption of wicked Fel Moss could cause such a transformation.  The grells and grellkin have infested the area and are threatening the residents of Shadowglen.  $b$bEngage these grells and grellkin, $N, and see if they are indeed caught under the enchantment of the wicked Fel Moss.",
  },
  [460] = {
    ["T"] = "Resting in Pieces",
    ["O"] = "Bring Alaric\'s Head to his grave.",
    ["D"] = "You don\'t look like a rot hide.  Good.  I had lost hope...$B$BMy name is Alaric, and I was once Thule\'s head servant and bodyguard.  He used his magic to \"protect\" me from the Plague of Undeath, but instead his spell gave me an unlife from which I cannot escape.  And after creating the cursed rot hide gnolls, Thule beheaded me... and gave my head to the gnolls as a plaything!$B$BPlease, take me to my body.  I believe the gnolls buried it here in Fenris Keep, near the stable, in an unmarked grave.",
  },
  [461] = {
    ["T"] = "The Hidden Niche",
    ["O"] = "Take Alaric to the hidden alcove.",
    ["D"] = "I know of a hidden niche within the keep that holds an item dear to Thule, and his old colleagues.  It also holds something you might find useful.  Take me there!$B$BThe alcove is in the southwestern tower of the keep, behind a dusty shelf.$B$BTake my body, but leave my boots.  They never fit me anyway...",
  },
  [463] = {
    ["T"] = "The Greenwarden",
    ["O"] = "Find the Greenwarden.",
    ["D"] = "You\'ve heard of the Greenwarden?  You\'re not looking for him, are you?  Well I say you\'re crazy if you are, but who am I to keep a fool from $ghis:her; death?$B$BIf you are seeking that beast, then I hear he is in the marsh, east of the road where it forks to Dun Modr.  He\'s lurking there among the crocs...and worse!$B$BAnd leave your money here.  You won\'t need it where you\'re going...and you don\'t want to chip ol\' Greenie\'s tooth on your gold when he bites you in half, do you?",
  },
  [464] = {
    ["T"] = "War Banners",
    ["O"] = "Bring 8 Dragonmaw War Banners to Captain Stoutfist.",
    ["D"] = "The Dragonmaw orc clan was all but destroyed by the end of the Second War, after they lost control of their red dragon pets.  The dragons were not very happy with their old masters, and fiercely hunted the Dragonmaws.  What few remained fled from Grim Batol and into the Mountains east of Whelgar\'s Excavation.$B$BNow they\'ve gained strength, and have an encampment northeast of Menethil.$B$BWe want you to attack that encampment.  Gather and bring me their war banners as proof.",
  },
  [465] = {
    ["T"] = "Nek\'rosh\'s Gambit",
    ["O"] = "Destroy the Dragonmaw catapults.",
    ["D"] = "While you were fighting the Dragonmaws, we discovered more of their plans.$B$BNek\'rosh has built catapults and aims to lay siege to Menethil!  And with the fighting in Dun Algaz, we\'re effectively blocked from reinforcements!$B$BYou need to take out those catapults.  They\'re in the Dragonmaw encampment.$B$BHere, take this tinder.  Fight your way to the catapults and light them with the tinder.$B$BAnd after you\'ve done that, find and kill Nek\'rosh.  We need to cut this Dragonmaw beast down...at its neck.",
  },
  [466] = {
    ["T"] = "Search for Incendicite",
    ["O"] = "Bring 6 loads of Incendicite Ore to Pilot Stonegear in Dun Morogh.",
    ["D"] = "I heard that a rare ore, incendicite, was found in the Wetlands, in a cave northwest of Algaz Gate.  Incendicite is volatile stuff and hard to use without blowing yourself up, but I\'m sure if it\'s smelted the right way it could make cannon shells that pack a mighty punch!$B$BGet me incendicite ore and I\'ll find someone brave enough to experiment with it.$B$BOh, and to extract that ore from a vein you\'ll need skill in mining.  If you don\'t have it, then ... bring a friend who does to that cave!",
  },
  [467] = {
    ["T"] = "Stonegear\'s Search",
    ["O"] = "Speak with Pilot Stonegear.",
    ["D"] = "Pilot Stonegear loves his siege engine, as every pilot should!  And he\'s always eager to find new ways to improve his tank\'s firepower!$B$BHe heard rumors of a rare ore and is looking for someone to hunt it down for him.  If your boots are starting to itch, then find Stonegear at Steelgrill\'s Depot in Dun Morogh and see what he wants.",
  },
  [468] = {
    ["T"] = "Report to Mountaineer Rockgar",
    ["O"] = "Speak with Mountaineer Rockgar.",
    ["D"] = "Mountaineer Rockgar is looking for volunteers for a mission through Dun Algaz and into the Wetlands.  $B$BIf you want to get your boots wet in that soggy county to the north, then speak with Rockgar at Dun Algaz.  Dun Algaz is north up the road, beyond the guard tower.",
  },
  [469] = {
    ["T"] = "Daily Delivery",
    ["O"] = "Deliver the Bundle of Crocolisk Skins to James Halloran, the tanner, in Menethil Harbor.",
    ["D"] = "Greetings, traveler!$b$bDon\'t suppose you might be heading in the direction of Menethil, would you? If you\'re not, I\'d suggest you stop there, it\'s a good place to purchase goods and even have a pint and relax.$b$bNow, if it just so happens that you\'re heading in that direction, I don\'t suppose you\'d mind taking this bundle of crocolisk skins to James Halloran, the tanner?$b$bI\'d do it myself, but my wife\'s been in quite a mood lately, and I\'d like to put off facing her as long as possible.",
  },
  [470] = {
    ["T"] = "Digging Through the Ooze",
    ["O"] = "One of the oozes at Ironbeard\'s Tomb has Sida\'s bag, retrieve it and bring it back to her in Menethil Harbor.",
    ["D"] = "On my weekly visit to Ironbeard\'s Tomb I was attacked by dreadful, dripping oozes! Naturally, I panicked.$b$bI threw my bag at one, but it didn\'t do anything! The ooze just sucked it right up... Luckily, it did give me enough time to get away.$b$bThe bad news is that I really need to get the contents of my bag!$b$bI don\'t know which ooze I threw my bag at; they all looked the same! Ironbeard\'s Tomb is northeast of here, near Dun Modr. Thank you so much!",
  },
  [471] = {
    ["T"] = "Apprentice\'s Duties",
    ["O"] = "Collect 6 Giant Crocolisk Skins and bring them to James Halloran in Menethil Harbor.",
    ["D"] = "I sent my apprentice out into the marshes to skin some of our famous giant Wetlands crocolisks. When he didn\'t return, I went out to look for him... Let\'s just say it wasn\'t a pretty sight.$b$bMidwife says he might pull through, but in the meantime I still need my skins!$b$b...If you\'d like to kill a few more of the brutes, I wouldn\'t be opposed, after what they did. You\'ll find the crocs north up the coast, beyond the murloc hovels.",
  },
  [472] = {
    ["T"] = "Fall of Dun Modr",
    ["O"] = "Search for Longbraid near the town of Dun Modr.",
    ["D"] = "Dun Modr has fallen to the Dark Iron Dwarves!$b$bMy wounds are grave, $c.  Most of the regiment was killed!  The Dark Iron thugs attacked us before we could regroup from the Thandol Span ambush.$b$bOur leader, Longbraid, sounded the retreat horn.  As we left the town I was hit by a stray axe in the back.  All went black.$b$bI awoke here in Menethil but I fear for my fellow soldiers.  Hope still burns within me.  Perhaps Longbraid is still alive!  See if you can find him near Dun Modr, $r!",
  },
  [473] = {
    ["T"] = "Report to Captain Stoutfist",
    ["O"] = "Speak with Captain Stoutfist.",
    ["D"] = "$N.  The Dragonmaw orc clan is showing its strength in more than just Algaz.  Captain Stoutfist has asked to speak with those willing to offer aid against these orcs.  He is inside the keep, in the war room.",
  },
  [474] = {
    ["T"] = "Defeat Nek\'rosh",
    ["O"] = "Kill Chieftain Nek\'rosh$B$BBring Nek\'rosh\'s Head to Captain Stoutfist.",
    ["D"] = "Now that the catapults have been burned, find and kill Chieftain Nek\'rosh.",
  },
  [475] = {
    ["T"] = "A Troubling Breeze",
    ["O"] = "Seek out Gaerolas Talvethren in Starbreeze Village.",
    ["D"] = "A troubling breeze blows through the forest.$b$bGaerolas Talvethren serves as Great Warden to the hibernating Druids of the Talon in the Ban\'ethil Barrow Den.  His duty as the chosen protector of the Sleeping is to ensure their safety so that their pact with Ysera remains fulfilled.$b$bBut word from Gaerolas is now delayed and I grow nervous.  Travel east to Starbreeze Village and bring back a report from Gaerolas so that I can put my worries to rest, knowing my dreaming brethren slumber safely.",
  },
  [476] = {
    ["T"] = "Gnarlpine Corruption",
    ["O"] = "Return to Athridas Bearmantle in Dolanaar.",
    ["D"] = "The Gnarlpine tribe has been corrupted!$b$bThe once peaceful furbolgs have turned against the protectors of the forest.  They ambushed me as I left for the Ban\'ethil Barrow Den and proceeded to pillage Starbreeze Village.  Ursal the Mauler, their chieftain, is using the evil powers of the Fel Moss to drive them mad.$b$bI am too wounded to return to Athridas to bring him this grave news.  The task is left to you, young $c.  We can only hope that the deranged Gnarlpines have not made it to Ban\'ethil yet!",
  },
  [477] = {
    ["T"] = "Border Crossings",
    ["O"] = "Retrieve the contents of one of the Dalaran wizards\' crates. You will find Pyrewood Village to the south.",
    ["D"] = "As you may know, the town of Ambermill remains a source of human opposition, mainly through the support they receive from the wizards of Dalaran.$b$bI don\'t know what their plan is, but for them to show interest in a backwater village like Ambermill indicates that it must have some greater importance.$b$bThey\'ve been shipping crates by the wagon from Hillsbrad. Many of those supplies end up at a small camp north of Pyrewood Village. Retrieve the contents of a crate and return them to me.",
  },
  [478] = {
    ["T"] = "Maps and Runes",
    ["O"] = "Bring the strange pendant to Shadow Priest Allister at the Sepulcher.",
    ["D"] = "<You quickly remove the pendant from the crate and replace the lid.>",
  },
  [479] = {
    ["T"] = "Ambermill Investigations",
    ["O"] = "Obtain 8 Dalaran Pendants for Shadow Priest Allister at the Sepulcher.",
    ["D"] = "Dalar is attempting to locate the source of the wizards\' spellcasting. For now, we\'ll have to slow their progress in any way we can...$b$bThe conjurers, mages and protectors are no doubt carrying the pendants. Remove and retrieve them.$b$bTake the main road south and the eastern fork into Ambermill.",
  },
  [480] = {
    ["T"] = "The Weaver",
    ["O"] = "Kill the Dalaran archmage, then retrieve his staff for Shadow Priest Allister at the Sepulcher.",
    ["D"] = "An archmage of great power is overseeing the reactivation of the ley energy node. With the progress they have made, our only hope of stopping them is by killing him. It will be difficult, he is no doubt protected by many other mages and warders.$b$bYou must go, and you must dispatch him quickly. When he is dead, retrieve his staff for me. Go now.",
  },
  [481] = {
    ["T"] = "Dalar\'s Analysis",
    ["O"] = "Take the rune-inscribed pendant to Dalar Dawnweaver at the Sepulcher.",
    ["D"] = "Take the pendant to Dalar Dawnweaver, perhaps he can shed some light on the wizards\' plans.",
  },
  [482] = {
    ["T"] = "Dalaran\'s Intentions",
    ["O"] = "Speak with Shadow Priest Allister at the Sepulcher.",
    ["D"] = "Of course! How could I have forgotten?$b$bAmbermill was one of the sites earmarked by the Kirin Tor, noted because it houses a dormant ley energy node. The wizards must be planning to reactivate the node and use its energies for some greater purpose.$b$bWe cannot allow this to happen...$b$bIt will take a great amount of power to activate the area\'s ley energies, we can stall them by taking the pendants away from the wizards.$b$bRelay this information to Allister, he will decide a course of action.",
  },
  [483] = {
    ["T"] = "The Relics of Wakening",
    ["O"] = "Retrieve the Relics of Wakening and bring them to Athridas Bearmantle in Dolanaar.",
    ["D"] = "Gnarlpine invaders were seen ravaging the Ban\'ethil Barrow Den to the west.$b$bThe slumbering druids will be trapped in the Emerald Dream for eternity, unaware of their fate, unless we help. The delicate hibernation ritual cannot be broken without the Relics of Wakening.$b$bJourney to the Den and retrieve the Raven Claw Talisman, Black Feather Quill, Sapphire of Sky, and Rune of Nesting. The druids store them in sacred chests.  Return them to me and I will prepare the awakening ritual.",
  },
  [484] = {
    ["T"] = "Young Crocolisk Skins",
    ["O"] = "Obtain 4 Young Crocolisk Skins for James Halloran in Menethil Harbor.",
    ["D"] = "I\'m James Halloran, the tanner. My cured crocolisk skins are sought after all over, shipments leaving by boat to ports around the world.$b$bMet Einar? He\'s one of the hunters that brings crocolisk skins in for me.$b$bI\'m a bit short on the softer skins that come off crocolisk young.$b$bBeing that I\'m running low, I\'ll pay you top coin for a stack of the skins. You can find the young crocs in the marshes right outside of town.",
  },
  [485] = {
    ["T"] = "Find OOX-09/HL!",
    ["O"] = "Take the distress beacon to Oglethorpe\'s homing robot at the head of the river in the Hinterlands, near Skulk Rock.",
    ["D"] = "You have uncovered some sort of strange, egg-shaped device made from metal.  Fiddling with one of its knobs springs the egg to life, as it opens up into some sort of gnomish robotic contraption!  A voice from inside the robotic egg crackles to life.$B$B\"My name\'s Oglethorpe Obnoticus, and my homing robot has crashed!  I will reward you for its recovery; please take this beacon to the robot!\"$B$B\"I now have computed the coordinates of the robot for you; it crashed at the head of the river near Skulk Rock!\"",
  },
  [486] = {
    ["T"] = "Ursal the Mauler",
    ["O"] = "Kill Ursal the Mauler and return to Athridas Bearmantle in Dolanaar.",
    ["D"] = "Now the time has come to save the Druids of the Talon. If we fail now, $N, they will be forever lost in sleep.$b$bI shall prepare the Relics of Wakening and perform the ritual. For my work to take effect, the cursed beast responsible for this horrible situation must be slain.  Only then will the ritual be complete.$b$bIt was Ursal the Mauler who meddled with our brethren\'s calling and it is Ursal the Mauler who must now pay so they can be freed.  Travel to Gnarlpine Hold in the southwest and slay him.",
  },
  [487] = {
    ["T"] = "The Road to Darnassus",
    ["O"] = "Slay 6 Gnarlpine Ambushers and return to Sentinel Amara Nightwalker outside of Dolanaar.",
    ["D"] = "The road to Darnassus must be kept safe.  Travelers heading from Dolanaar to Darnassus have been reporting ruthless attacks by corrupted furbolgs from the Gnarlpine tribe.$b$bImportant news and commerce travels to and from Darnassus by way of this road daily.  We cannot afford to have a rogue band of heathens terrorizing people.$b$bTake up arms in the name of the sacred forest, $c.  Their den lies somewhere below this vantage point.  Slay 6 of these Gnarlpine ambushers and report back to me.",
  },
  [488] = {
    ["T"] = "Zenn\'s Bidding",
    ["O"] = "Bring Zenn Foulhoof outside of Dolanaar 3 Nightsaber Fangs, 3 Strigid Owl Feathers and 3 swatches of Webwood Spider Silk.",
    ["D"] = "Eager for work, I see.  Lucky for you a day never goes by that I don\'t wish I had a fledgling $c to perform my bidding.$b$bYou see, $N, I can make you very happy and provide you with things you never dreamed of having.  But in order for that to happen you must bring me certain items.$b$bMy business in the forest often requires certain. . .reagents.  Fetch for me 3 Nightsaber Fangs, 3 Strigid Owl Feathers and 3 swatches of Webwood Spider Silk.$b$bLet\'s keep this our little secret, $r.",
  },
  [489] = {
    ["T"] = "Seek Redemption!",
    ["O"] = "Collect 3 Fel Cones and give them to Zenn Foulhoof outside of Dolanaar.",
    ["D"] = "The Council of the Forest has news that you aided Zenn Foulhoof. The satyr is an enemy of the forest. As a $r, you should know better than to defile the forest by killing Nature\'s creatures.$b$bYou must redeem yourself in the eyes of the Council if you wish to remain a friend of Teldrassil.$b$bTeach Foulhoof a lesson and you shall be redeemed. Fel Cones are corrupted seeds that fall from the trees. They billow with green smoke.$b$bGive some to Foulhoof. He\'ll think you have brought him a harmless snack.",
  },
  [491] = {
    ["T"] = "Wand to Bethor",
    ["O"] = "Take the Woven Wand to Bethor Iceshard in the Magic Quarter of the Undercity.",
    ["D"] = "Thule once told me that, long ago, he and two colleagues created a magic wand, woven together from the apprentice wands each mage possessed.  This wand was a symbol of their friendship, and although Thule came to despise it he could not part with it, nor could he let the others have it.  So he stole the wand, and hid it in Fenris Keep.$B$BHere.  Take the wand to Bethor Iceshard, one of Thule\'s friends from years past.  I\'m sure he will want it.",
  },
  [492] = {
    ["T"] = "A New Plague",
    ["O"] = "Bring Johaan\'s Special Drink to the Captured Mountaineer.",
    ["D"] = "According to the Deathguard, another one of those foolish Dwarven Mountaineers has just been captured.  The Deathguard likes to use the cellar of the Gallows End Tavern as a holding cell until prisoners can be \"properly\" dealt with.$b$bWhy don\'t you go see how the Captured Mountaineer enjoys this special drink I made for him?  It contains a subtle hint of what The Dark Lady has planned for the rest of Azeroth.",
  },
  [493] = {
    ["T"] = "Journey to Hillsbrad Foothills",
    ["O"] = "Deliver Apothecary Renferrel\'s findings to Apothecary Lydon in the town of Tarren Mill in the Hillsbrad Foothills.",
    ["D"] = "The Royal Apothecary Society is under extreme pressure from the Dark Lady to develop a New Plague.  We are working diligently and have made much progress.  It is our belief that the success will be achieved much faster if we share information amongst the Society.$b$bFor that reason I want you to deliver my latest findings to Apothecary Lydon in Tarren Mill, a small town nestled in the Hillsbrad Foothills.  The journey will be a long one.$b$bHead south and stick to the roads.  Follow the signs closely!",
  },
  [494] = {
    ["T"] = "Time To Strike",
    ["O"] = "Travel to Tarren Mill to deliver Deathstalker Lesh\'s message to High Executor Darthalia.",
    ["D"] = "Be silent and pay attention, $c.$b$bI was sent to this position in order to survey the town of Hillsbrad.  My mission is one of reconnaissance.  It is imperative that you send word to High Executor Darthalia in Tarren Mill at once.  Let her know that I, Deathstalker Lesh, send the following message:$b$b\"The raven\'s cry from the west doth beckon.\"$b$bFollow the road to the east and pay close attention to the signs.  Now, hurry!  Off you go to Tarren Mill, and with urgency I might add!",
  },
  [495] = {
    ["T"] = "The Crown of Will",
    ["O"] = "Report to Melisara at Tarren Mill in the Hillsbrad Foothills.",
    ["D"] = "Mug\'Thol and his ogre warband was sent to Alterac to retrieve the Crown of Will for Lady Sylvanas. Unfortunately, instead of returning it, the idiot decided to try it on... I suppose ogres can\'t resist anything bright and shiny.$b$bHis bumbling actions have become something of a problem, however. The Crown of Will enabled him to resist the possession placed upon him to serve Lady Sylvanas.$b$bThe Dark Lady wants an example made, travel to Tarren Mill in Hillsbrad and report to Melisara, my lieutenant.",
  },
  [496] = {
    ["T"] = "Elixir of Suffering",
    ["O"] = "Apothecary Lydon of Tarren Mill wants 10 Gray Bear Tongues and some Creeper Ichor.",
    ["D"] = "Ah, another wretched day in Tarren Mill.  All of this clean air puts me in such a foul mood, $N.$b$bThe sooner we can plague the humans here, the better.  I\'ve been conducting intense studies on possible killing agents to use in my concoctions but I haven\'t the time to collect them all.$b$bIf you want to make yourself of use, procure the following items for me:  10 Gray Bear Tongues and the very rare and hard to find, Creeper Ichor.  You\'ll find both bears and creepers just outside of Tarren Mill.",
  },
  [498] = {
    ["T"] = "The Rescue",
    ["O"] = "Krusk in Tarren Mill needs you to free Drull and Tog\'thar from Durnholde Keep.",
    ["D"] = "Me and some other warriors were sent by the Warchief to retrieve an object from Durnholde and to investigate this so-called Syndicate.$b$bWe went to Durnholde to take a look around, but we were taken by surprise by a large number of the humans. We fought bravely, but their sheer numbers were enough to defeat us. They took a few of us captive, but I was able to escape.$b$bDurnholde is just southeast of here, across the river. Drull and Tog\'thar are being held there, you must go and rescue them.",
  },
  [499] = {
    ["T"] = "Elixir of Suffering",
    ["O"] = "Give Umpi the Elixir of Suffering.",
    ["D"] = "It will take but a moment for me to mix these components into what I have deemed an Elixir of Suffering.$b$bThere we go!  Ah, nothing brightens a gloomy day in these miserable foothills like bringing a little suffering into the world.$b$bLet us see if our little potion has achieved its desired effect.$b$bGive my assistant Umpi a taste of what the Dark Lady has planned for both humans and Scourge alike.",
  },
  [500] = {
    ["T"] = "Crushridge Bounty",
    ["O"] = "Gather 9 Dirty Knucklebones from Crushridge ogres in the Alterac Mountains.  Bring them to Marshal Redpath in Southshore.",
    ["D"] = "Crushridge ogres have dug an ogre mound up in the Alterac Mountains near the ruined city of Alterac.  And my scouts tell me they\'ve taken over those ruins as well.$B$BWe can\'t let them get cozy up there; if they think they\'re safe where they are, then their next step will be to move down into the foothills, which will put them right at our front door!$B$BGo north to the Alterac Mountains and hunt ogres.  Bring me the Dirty Knucklebones they carry and you will earn a nice bounty.",
  },
  [501] = {
    ["T"] = "Elixir of Pain",
    ["O"] = "Bring 10 vials of Mountain Lion Blood to Apothecary Lydon in Tarren Mill.",
    ["D"] = "Oh, $N. . .the flowers are blooming in Hillsbrad and the air is so fresh and brisk.  I can\'t help but to wallow in the misery of it all.  I look out my grimy window and long for the day when our New Plague brings this world the death it deserves.$b$bAs much as I would love to sit and brood all day, my duty to the Dark Lady calls.  Help me, would you?  My research leads me to believe I can make the most splendid death-brew with the blood of a Mountain Lion.  Bring some to me and we\'ll have a little fun.",
  },
  [502] = {
    ["T"] = "Elixir of Pain",
    ["O"] = "Feed the Elixir of Pain to Stanley.",
    ["D"] = "Ah, nothing like the smell of fresh blood in the foothills!$b$bNow we\'ll mix in a touch of this and a little of that and let the real fun begin!  What I have created with this blood, I like to call an Elixir of Pain.  If my calculations are correct this concoction could be the start of something very beautiful for Lady Sylvanas.  But what we need is a test.$b$bTake this elixir out to the northern farm in Hillsbrad Fields to the southwest.  Let\'s see how Farmer Ray\'s little dog Stanley likes this \"treat.\"",
  },
  [503] = {
    ["T"] = "Gol\'dir",
    ["O"] = "Free Gol\'dir then return to Krusk in Tarren Mill.",
    ["D"] = "From what I can tell of the documents you procured, Gol\'dir is being held in a large Syndicate base to the north, most likely in the Alterac Mountains. He has been held captive far too long and must be freed.",
  },
  [504] = {
    ["T"] = "Crushridge Warmongers",
    ["O"] = "Slay 15 Crushridge Warmongers, then return to Marshal Redpath in Southshore.",
    ["D"] = "Now that you\'ve had a taste of the Crushridge ogres, I want you to really bloody their noses...$B$BGo into the Ruins of Alterac and seek out the Crushridge Warmongers.  I want you to cut down a good number of them - that\'s the only way those brutes will learn to keep their distance from Alliance territory.",
  },
  [505] = {
    ["T"] = "Syndicate Assassins",
    ["O"] = "Kill 12 Syndicate Footpads and 8 Syndicate Thieves, then return to Magistrate Henry Maleb in Southshore.",
    ["D"] = "I am the new magistrate of Southshore, recently assigned after the assassination of the previous magistrate.  The assassins were never found, but through our investigations we\'re almost certain they were hired by the Syndicate - a group of thieves led by villainous nobles of the now fallen kingdom of Alterac.$B$BThe Syndicate has a camps in Sofera\'s Naze and Corahn\'s Dagger, north and west of the Horde-occupied Tarren Mill.  Slay the Syndicate members you find in these camps.",
  },
  [506] = {
    ["T"] = "Blackmoore\'s Legacy",
    ["O"] = "Return the information gathered by Gol\'dir to Krusk in Tarren Mill.",
    ["D"] = "A day or two ago, I was visited by the leader of this Syndicate. His name was Aliden Perenolde, and styled himself lord of this land. He confided in me his plans to take the orcs that stood against him as slaves, just as his father and his mentor, the despised Blackmoore, had planned.$b$bMore important was that he brought his mistress, a wisp of a girl named Elysa. Around her neck was the pendant the Warchief desires.$b$bNow go, I will make my own way, after I have settled my business here.",
  },
  [507] = {
    ["T"] = "Lord Aliden Perenolde",
    ["O"] = "Kill Lord Aliden Perenolde and ask his mistress, Elysa, about Taretha\'s pendant.",
    ["D"] = "It is clear that Perenolde must be killed, his foulness removed from the world. His manor lies a ways to the north of here, on the shores of Lordamere Lake. Do your duty, $n, then retrieve the pendant that the Warchief desires, and return here.",
  },
  [508] = {
    ["T"] = "Taretha\'s Gift",
    ["O"] = "Return Taretha\'s pendant to Krusk in Tarren Mill.",
    ["D"] = "The pendant? Of course you can have it... Take it! I want no memory of him... just please, please leave in peace...",
  },
  [509] = {
    ["T"] = "Elixir of Agony",
    ["O"] = "Bring 6 Mudsnout Blossoms to Apothecary Lydon in Tarren Mill.",
    ["D"] = "Another day, another elixir to be made. If it were up to me, I\'d just let the pathetic humans and Scourge fight it out.  They seem bent on killing each other as is.$b$bAlas, our Dark Lady wants to help speed up the process a tad so why not pass these morbid days by spreading a little death and disease across the land?$b$bHere\'s the plan, $N: you go out to Nethander Stead, south of Tarren Mill and east of the river and Southshore. Collect for me 6 Mudsnout Blossoms from the field for my next elixir.",
  },
  [510] = {
    ["T"] = "Foreboding Plans",
    ["O"] = "Bring the Foreboding Plans to Magistrate Maleb in Southshore.",
    ["D"] = "Among these documents is a list of requested supplies.  These include mundane items (food, weapons, clothing) as well as more exotic gear (masks, oil, poison).$B$BBut what is most notable is the urgency of the request - these supplies were needed soon in order to undertake what is described in the document as an \"important mission against Southshore.\"",
  },
  [511] = {
    ["T"] = "Encrypted Letter",
    ["O"] = "Bring the Encrypted Letter to Loremaster Dibbs in Southshore.",
    ["D"] = "One of the letters on this table is encrypted and inscrutable.",
  },
  [512] = {
    ["T"] = "Noble Deaths",
    ["O"] = "Bring 7 Alterac Signet Rings to Magistrate Maleb in Southshore.",
    ["D"] = "I fear the leadership behind the Syndicate is bent on Southshore\'s ruin.  We must strike hard at these leaders before their plans are realized.$B$BEnter the ruins of Strahnbrad, in the Alterac Mountains to the north.  We think the Syndicate is using the area as a headquarters.$B$BWe know this gang of cutthroats is run by former Alterac nobles.  They may be hiding as normal Syndicate agents, but you\'ll know you\'ve killed a noble by the signet ring he wears.$B$BBring me those signet rings.",
  },
  [513] = {
    ["T"] = "Elixir of Agony",
    ["O"] = "Take the Mudsnout Composite to Master Apothecary Faranell in the Undercity.",
    ["D"] = "Just when I thought another day was going to pass devoid of any joy, you brought me those marvelous Mudsnout Blossoms.$b$bFirst I will mix them down into a fine composite.  We\'ll then need Master Apothecary Faranell in the Undercity to apply his colloid of decay to it and then we\'ll be ready for some good fun indeed!$b$bHere, take this Mudsnout Composite and run it off to Master Faranell at once, $N.",
  },
  [514] = {
    ["T"] = "Letter to Stormpike",
    ["O"] = "Take the Cleverly Encrypted Letter to Prospector Stormpike in Ironforge.",
    ["D"] = "I have a colleague in Ironforge who may be able to help decrypt this letter.  Go to the Library in the Hall of Explorers in Ironforge and speak with Prospector Stormpike.  Tell him I sent you and show him the letter.  If there\'s anyone north of Blackrock Spire who can read this, it is he.",
  },
  [515] = {
    ["T"] = "Elixir of Agony",
    ["O"] = "Bring the Mudsnout Mixture, a Strong Troll\'s Blood Potion, 5 Daggerspine Scales and 5 Torn Fin Eyes to Apothecary Lydon in Tarren Mill.",
    ["D"] = "A mudsnout composite?  Absolutely brilliant!  Why didn\'t I think of that?$b$bLet me contribute my colloid of decay to this devilish brew.$b$bIn order to activate the contaminating agents in this mudsnout mixture, Lydon is going to need a strong troll\'s blood potion, as well as some daggerspine scales and torn fin eyes from the southern coast of Hillsbrad.  He\'ll know what to do once you\'ve gathered him all the reagents.  And my, how anxious I am to hear how his experiment goes!",
  },
  [516] = {
    ["T"] = "Beren\'s Peril",
    ["O"] = "Locate Beren\'s Peril, then kill 6 Ravenclaw Drudgers and 6 Ravenclaw Guardians, then return to Shadow Priest Allister at the Sepulcher.",
    ["D"] = "I have received reports that a group of undead are holing up to prepare for an attack against us. Armed with this information, we can turn the tables and attack them first, nipping their little plan in the bud.$b$bUnfortunately, my information is spotty, at best. They are reported to be hiding in a location known as Beren\'s Peril. The exact location is unknown, but it appears to be a cave in the hills, near a pocket of Dalaran wizards.$b$bI trust your resourcefulness. Find them, and put them down.",
  },
  [517] = {
    ["T"] = "Elixir of Agony",
    ["O"] = "Bring a keg of Shindigger Stout to Apothecary Lydon in Tarren Mill.",
    ["D"] = "This new elixir is finally ready to be tested.  But why let a perfectly good, highly contagious plague go to waste on a mere frog or dog?  Let\'s have us some true fun in the name of Lady Sylvanas.$b$bHead down to Dun Garok.  You know the barracks where those filthy little dwarves skitter about.  Steal me a keg of their Shindigger Stout.  Once I have that keg, you and I will have quite the party, $N.",
  },
  [518] = {
    ["T"] = "The Crown of Will",
    ["O"] = "Kill 10 Crushridge Maulers for Melisara in Tarren Mill.",
    ["D"] = "Mug\'Thol and his Crushridge ogres have overrun the ruins of Alterac. It will be hard to inflict any serious punishment upon him without thinning out the ogres\' numbers.$b$bAfter softening them up, we\'ll proceed.$b$bUnderstood? Good. Now go.",
  },
  [519] = {
    ["T"] = "The Crown of Will",
    ["O"] = "Kill Targ, Muckrake, and Glommus and bring their heads to Melisara in Tarren Mill.",
    ["D"] = "Targ, Muckrake, and Glommus are Mug\'Thol\'s lieutenants. They are loyal to him, though who can say what drives an ogre to loyalty.$b$bBring me their heads, $n, and then all that will remain is to remove Mug\'Thol\'s head, crown and all.",
  },
  [520] = {
    ["T"] = "The Crown of Will",
    ["O"] = "Kill Mug\'Thol, and return his head with the Crown of Will to Melisara in Tarren Mill.",
    ["D"] = "With Mug\'Thol\'s lieutenants dead, all that remains is to kill Mug\'Thol and retrieve the Crown of Will. I have no doubt he\'ll be reluctant to part with it. Most likely expecting you, too, given the... departure... of his fellows.$b$bFind Mug\'Thol, and deliver his head and the Crown to me. The Crushridge resistance will be at an end.",
  },
  [521] = {
    ["T"] = "The Crown of Will",
    ["O"] = "Deliver the Crown of Will to Sharlindra in the Undercity.",
    ["D"] = "All that remains is to deliver the Crown of Will to Sharlindra in the Undercity. She will see that it is delivered to Lady Sylvanas. I would warn you against thinking to use it for your own purposes, $n, Mug\'Thol\'s fate should be illustration enough.",
  },
  [522] = {
    ["T"] = "Assassin\'s Contract",
    ["O"] = "Bring the Assassin\'s Contract to Magistrate Maleb in Southshore.",
    ["D"] = "This is a contract for the assassination of Magistrate Henry Maleb.  It was sealed and stamped in red wax, using a distinctive signet ring.",
  },
  [523] = {
    ["T"] = "Baron\'s Demise",
    ["O"] = "Bring the Head of Baron Vardus to Magistrate Maleb in Southshore.",
    ["D"] = "The contract you recovered was signed by Baron Vardus, a noble of the former kingdom of Alterac, and one known for a cold heart and brutal practices.  I\'m not surprised someone like the Baron was behind this.$B$BFind this man.  Find him, and bring him to justice.  He must be among the Syndicate haunts - perhaps at the ruins of Strahnbrad, or perhaps within the valley north of them.",
  },
  [524] = {
    ["T"] = "Elixir of Agony",
    ["O"] = "Place the Tainted Keg on the rug for the captured farmers.",
    ["D"] = "Next door the Deathguards are holding a few captured farmers hostage.  High Executor Darthalia suspected one of the military strategists from Stormwind to be hiding out amongst them.  Apparently the military chap was found dead in the Plaguelands last night.  Darthalia, always looking out for me, decided the prisoners would be left to my disposal.$b$bI think it\'s time we threw those farmers a party.  Sounds like a grand ol\' time!$b$bPlace this tainted keg on the rug in their room.  Plagued brew for all!",
  },
  [525] = {
    ["T"] = "Further Mysteries",
    ["O"] = "Bring the Decrypted Letter to Magistrate Maleb in Southshore.",
    ["D"] = "I decrypted the letter, but I don\'t know what to make of it.  You found this among thieves in the Alterac foothills?  Very strange...$B$BYou should take this to the magistrate of Southshore.  Although this letter is cryptic, his is the closest town to Alterac; if a threat is brewing, then he should know.",
  },
  [526] = {
    ["T"] = "Lightforge Ingots",
    ["O"] = "Gather 5 lightforge ingots, then return to Glorin Steelbrow in the Wetlands.",
    ["D"] = "You might have found one lightforge ingot in that chest, but that\'s not enough to craft anything decent!$B$BYou should go back there and look for more.  Maybe the murlocs around that wrecked ship have lightforge ingots on them.",
  },
  [527] = {
    ["T"] = "Battle of Hillsbrad",
    ["O"] = "Kill 6 Hillsbrad Farmhands, 6 Hillsbrad Farmers, Farmer Ray and Farmer Getz and report back to Darthalia in Tarren Mill.",
    ["D"] = "Stand at attention while addressing me, $c.$b$bI am under direct command of Varimathras.  We are to quell the human infestation until our apothecaries can develop the new plague.$b$bOur information leads us to believe that the town of Hillsbrad is vulnerable to attack.  Your first assignment is aimed at disrupting their food supply and infrastructure.$b$bTravel west.  Raid the northernmost farms of Hillsbrad Fields.  Make sure that the landowners, Farmer Ray and Farmer Getz, are executed as well.",
  },
  [528] = {
    ["T"] = "Battle of Hillsbrad",
    ["O"] = "Kill 15 Hillsbrad Peasants and report back to Darthalia in Tarren Mill.",
    ["D"] = "Word of the attack on the northern farms has the townsfolk panicked.  Most excellent.$b$bOur Deathstalkers report peasants fleeing to the fields in southern Hillsbrad.  The fools think they can hide in the orchards and escape their doom.$b$bProve them wrong, $N.  Return to Hillsbrad Fields and seek out the cowering peasants.  Lay waste to them in the name of our Dark Lady.$b$bDismissed!",
  },
  [529] = {
    ["T"] = "Battle of Hillsbrad",
    ["O"] = "Kill Blacksmith Verringtan and 4 Hillsbrad Apprentice Blacksmiths.$b$bRetrieve a shipment of iron and report back to Darthalia in Tarren Mill.",
    ["D"] = "The humans of Hillsbrad have been putting up unexpected resistance.  The problem appears to be that the townsfolk are being armed by the local blacksmith.  We need to shut down all supply lines.$b$bYour mission will be to execute Blacksmith Verringtan.  Slay his apprentices as well.  While there, bring back a shipment of iron from their stockpile.  The supplies will come in handy when we are done with the conquering of Hillsbrad and begin razing Southshore.",
  },
  [530] = {
    ["T"] = "A Husband\'s Revenge",
    ["O"] = "Kill Valdred Moray.$B$BBring his hands to Raleigh Andrean in the Undercity.",
    ["D"] = "Valdred, once my good friend, was my wife\'s murderer.  Although I am incapable of grieving, I do yearn for revenge.  If you kill Valdred and bring me his cursed hands, then I will reward you.$B$BI last heard he was at the Greymane Wall in southern Silverpine, trying to flee into the kingdom of Gilneas.",
  },
  [531] = {
    ["T"] = "Vyrin\'s Revenge",
    ["O"] = "Bring Ol\' Sooty\'s head to Vyrin Swiftwind at the Farstrider Lodge.",
    ["D"] = "Light! Don\'t bring things like that into the Lodge... it\'s unsanitary, unspeakable, un... un... unsanitary!$b$bJust get it out of here, will you? No one wants to see your bloody little trophy, really!",
  },
  [532] = {
    ["T"] = "Battle of Hillsbrad",
    ["O"] = "Kill Magistrate Burnside and 4 Hillsbrad Councilmen.  Destroy the Hillsbrad Proclamation.  Steal the Hillsbrad Town Registry.  Report back to Darthalia in Tarren Mill afterwards.",
    ["D"] = "The town of Hillsbrad is in disarray and we believe it will soon fall.  We must, however, persist in our ruthlessness until the human scum are wiped from the foothills for eternity.$b$bDespite their overwhelming losses, the humans rally around their leadership and keep fighting.  We must eliminate their leader, Magistrate Burnside, as well as the town council.  Destroy their revered political document as well, the Hillsbrad Proclamation.  And while you\'re at it, steal the town registry.",
  },
  [533] = {
    ["T"] = "Infiltration",
    ["O"] = "Retrieve information about Gol\'dir\'s whereabouts for Krusk in Tarren Mill.",
    ["D"] = "At last we begin to hear word and rumor, find evidence of Gol\'dir\'s whereabouts. The humans have been moving him around, but we are narrowing down our search and focusing on the large Syndicate camp just north of here.$b$bGo there, $n, and bring back whatever information you can get your hands on.",
  },
  [535] = {
    ["T"] = "Valik",
    ["O"] = "Bring a mug of Southshore Stout to Valik in the Syndicate camp.",
    ["D"] = "Urrgh... Injured and dying and confronted with the enemy.$b$bNo doubt you\'ll kill me anyway, but if you bring me some Southshore stout--your kind does drink, don\'t they?--I\'ll tell you what you want to know.",
  },
  [536] = {
    ["T"] = "Down the Coast",
    ["O"] = "Kill 10 Torn Fin Tidehunters and 10 Torn Fin Oracles for Lieutenant Farren Orinelle in Southshore.",
    ["D"] = "Transferred from the Stormwind city guard to Southshore, a town that should be abandoned by the looks of it... Smell of the harbor day and night.$b$bNot to mention ol\' Redpath... Wants me to take care of the murlocs. Ha! $b$bI can barely rouse myself to go out to some of the pockets of the overgrown fish down the Western Strand. If you would go in my place and take care of them, I\'d appreciate it.",
  },
  [537] = {
    ["T"] = "Dark Council",
    ["O"] = "Kill 4 Argus Shadow Mages.$B$BBring the Head of Nagaz to Magistrate Maleb in Southshore.",
    ["D"] = "The letter you brought me was written by a being who calls himself Nagaz, a member of the Argus Wake.  I don\'t know what this Argus Wake is, but if they\'re in alliance with the Syndicate then they must be a threat.$B$BI want you to find Nagaz and the Argus Shadow Mages mentioned in that letter.  I want you to find them, and kill them.$B$BSearch in the Uplands, where the Syndicate is strongest.  It is north of Strahnbrad.$B$BWith luck, you\'ll find these envoys of the Argus Wake there.",
  },
  [538] = {
    ["T"] = "Southshore",
    ["O"] = "Speak with Loremaster Dibbs in Southshore.",
    ["D"] = "$N.  If you don\'t mind a long journey then I have a task for you, to again help Stormwind fill its library.$B$BThe old kingdom of Alterac fell after the great war, and it had a large collection of books.  It\'s a tragedy that the city is ruined, but even more tragic is all of their knowledge lost!  We must recover what we can.$B$BSouthshore is the closest town to the Ruins of Alterac.  Go there and speak with Loremaster Dibbs.  He can direct you to the ruins, and to what books may be found there.",
  },
  [539] = {
    ["T"] = "Battle of Hillsbrad",
    ["O"] = "Kill Foreman Bonds and 10 Hillsbrad Miners and report back to Darthalia in Tarren Mill.",
    ["D"] = "The Hillsbrad Town Registry indicates that the Azureload Mine is under Alliance control.  Furthermore, the mine is a prime source of iron ore for the Alliance armories.$b$bEven though we\'ve struck a decisive blow to the town of Hillsbrad, the Alliance still supports and protects the Azureload Mine fervently.$b$bThe mine lies due south of Hillsbrad.  Go there and slay the foreman, a human by the name of Bonds.  Kill his miners as well.  That should send a clear message to the Alliance.",
  },
  [540] = {
    ["T"] = "Preserving Knowledge",
    ["O"] = "Bring 5 Recovered Tomes and the Worn Leather Book containing The Arm of Gri\'lek to Loremaster Dibbs in Southshore.",
    ["D"] = "Those beastly ogres now reside within Alterac\'s ruins, and I shudder to think what they\'re doing with the precious books still there.  You must recover what you can!$B$BEnter the Ruins of Alterac and search for tomes looted by the ogres.  Get whatever you can find and bring them to me, but also seek out one book in particular: The Arm of Gri\'lek.  It contains ancient troll lore that I must learn, and the Ruins of Alterac was the last known location of an intact copy of this book.",
  },
  [541] = {
    ["T"] = "Battle of Hillsbrad",
    ["O"] = "Travel to Dun Garok and kill 8 Mountaineers, 4 Riflemen, 2 Priests and Captain Ironhill and report back to Darthalia in Tarren Mill.",
    ["D"] = "Dwarves aiding the humans?  Damn those filthy little beasts.$b$bWe shall test the strength of the so-called Alliance.  We\'ll see how anxious those little ankle-bashers are to help the humans once they feel our wrath.  We know exactly where their stronghold is in this territory.$b$bThey have established a barracks in southeastern Hillsbrad along Thoradin\'s Wall called Dun Garok.  The final mission of this battle is to lay siege on Dun Garok, $c.  Captain Ironhill, their leader, must die.",
  },
  [542] = {
    ["T"] = "Return to Milton",
    ["O"] = "Bring the Tomes of Alterac to Milton Sheaf in Stormwind.",
    ["D"] = "I need to spend some time studying the Arm of Gri\'lek book you brought me, though I\'m sure Milton is eager to get the other recovered books into Stormwind\'s collection.$B$BHere are the books you gathered from the Ruins of Alterac, packed and catalogued.  Take them back to Milton Sheaf in Stormwind and I\'m sure he\'ll be grateful.",
  },
  [543] = {
    ["T"] = "The Perenolde Tiara",
    ["O"] = "Bring the Perenolde Tiara to Remington Ridgewell in Stormwind.",
    ["D"] = "The Perenolde Tiara is an old heirloom of the Perenolde \"nobles\" - the ruling family of Alterac before that kingdom fell to ruin.  It is said to have been crafted by master jewelers in Ironforge, and fitted with the finest of emeralds.$B$BWhen Alterac was destroyed the Tiara was lost, but there are rumors that an Ogre Mage, Grel\'borg the Miser, found the tiara and now wanders the Ruins of Alterac searching for more treasures.$B$BWe would have you acquire the tiara for us.",
  },
  [544] = {
    ["T"] = "Prison Break In",
    ["O"] = "Find the traitors and recover their artifacts, then return to Magus Voidglare in Tarren Mill.",
    ["D"] = "I came to Tarren Mill to research, but now must resolve a crisis.  You see, four Forsaken fled the Undercity a few months ago.  They turned their backs on their brethren, but what\'s worse...they stole from the Dark Lady.$B$BThese thieves broke into a secure vault and stole four artifacts, items our apothecaries required in certain studies.  Sylvanas would have these artifacts returned.$B$BThe thieves fled the Undercity to Dalaran, and those wizards quarantined them in the Lordamere Internment Camp.",
  },
  [545] = {
    ["T"] = "Dalaran Patrols",
    ["O"] = "Kill 6 Dalaran Summoners and 12 Elemental Slaves, then return to Magus Voidglare in Tarren Mill.",
    ["D"] = "The wizards of Dalaran constructed a vast, magical dome around the heart of their city.  Some believe it was for protection from the violence of these times, while others say they merely wanted solitude, to study and plot.$B$BI don\'t care what the reason was.  I do care that the ruined outskirts of their city, outside the dome, may hide valuable magical treasures.$B$BBut the area is patrolled by the wizards and their elemental slaves.$B$BSo hunt them, and return when their numbers are sufficiently reduced.",
  },
  [546] = {
    ["T"] = "Souvenirs of Death",
    ["O"] = "Deathguard Samsa of Tarren Mill wants 30 Hillsbrad Human Skulls.",
    ["D"] = "I hear you were enlisted by Darthalia to wage war on the humans of Hillsbrad.  So jealous I am. . .$b$bWhile you\'re off having all the fun -- slaying humans, pillaging the town, terrorizing innocent people -- I am stuck standing guard here in Tarren Mill.$b$bPerhaps you\'ll take pity on an old Deathguard like myself?  You see I am collecting human skulls.  And you will be fighting humans for quite some time.  Over the course of your long battle, bring me 30 skulls and I will make it worth your while.",
  },
  [547] = {
    ["T"] = "Humbert\'s Sword",
    ["O"] = "Retrieve Deathguard Humbert\'s sword from Dun Garok and return it to him in Tarren Mill.",
    ["D"] = "High Executor Darthalia sent me to keep watch of Dun Garok from a distance.  I had set up camp just out of sight.  But when morning came and I had to boil water, I left to gather firewood.$b$bWhen I returned to camp, I discovered my gear was missing.$b$bApparently some dwarven scouts from Dun Garok had passed by and made off with it.  I need my sword!  If you can get it back for me I would be so grateful.  So many dwarves there...it will be quite difficult to find the dirty little thief who stole it.",
  },
  [549] = {
    ["T"] = "WANTED: Syndicate Personnel",
    ["O"] = "Kill 10 Syndicate Rogues and 10 Syndicate Watchmen. Return to High Executor Darthalia in Tarren Mill for your reward.",
    ["D"] = "By the authority of Lady Sylvanas, all members of the organization known as the Syndicate are wanted dead. Those brought into custody or taken prisoner will be summarily executed. They are currently known to be amassing in the ruins of Durnholde Keep, to the southeast of Tarren Mill.$b$bA handsome reward is offered to all who bring proof of their deeds to High Executor Darthalia.",
  },
  [550] = {
    ["T"] = "Battle of Hillsbrad",
    ["O"] = "Take Darthalia\'s Sealed Commendation to Varimathras in the Undercity.",
    ["D"] = "Because you fought with such valor and perseverance in the Battle of Hillsbrad, I have written this commendation, extolling your heroics in combat for the high command to recognize.$b$bTake this sealed commendation to Varimathras in the Undercity.  Go with pride, $c.",
  },
  [551] = {
    ["T"] = "The Ensorcelled Parchment",
    ["O"] = "Take the Ensorcelled Parchment to Loremaster Dibbs in Southshore.",
    ["D"] = "This parchment is enchanted with a spell, rendering it indecipherable.  But one word at the top of the paper can be read:$B$B$B      Nagaz$B$B",
  },
  [552] = {
    ["T"] = "Helcular\'s Revenge",
    ["O"] = "Retrieve Helcular\'s Rod from the Yeti and bring it back to Novice Thaivand in Tarren Mill.",
    ["D"] = "Master Helcular was almost complete with his transformation into a Lich when a human lynch mob discovered his ritual lair in the hills and brutally killed him.  To make matters worse they buried his corpse in the Southshore cemetery.$b$bSuch disregard for the great wizard is unacceptable.  As his apprentice I shall see to it that the rains of vengeance fall upon the town of Southshore.$b$bWe will need Helcular\'s Rod first.  Yeti overtook his ritual lair.  Undoubtedly one of the beasts has it.",
  },
  [553] = {
    ["T"] = "Helcular\'s Revenge",
    ["O"] = "Charge the Rod of Helcular with the powers of the Flame of Azel, Flame of Veraz and the Flame of Uzel.$b$bDrive the charged rod into Helcular\'s grave in Southshore.",
    ["D"] = "Here, take the Rod of Helcular. You will need it.$b$bHelcular fashioned 3 ceremonial pyres as part of his most powerful spell.  The Flame of Azel and the Flame of Veraz lie within the Foothill Caverns. The Flame of Uzel lies in a cave above the caverns, further north in the mountains.$b$bThe Rod of Helcular must be charged from each of the three flames. Once the ritual is complete, drive the rod into Helcular\'s grave in Southshore, the heavily guarded human town to the south.  Vengeance shall be ours!",
  },
  [554] = {
    ["T"] = "Stormpike\'s Deciphering",
    ["O"] = "Take the Ensorcelled Parchment to Prospector Stormpike in Ironforge.",
    ["D"] = "Prospector Stormpike is a master of decryption and deciphering.  If you take the parchment you found to him, then with luck he can make sense of it.  Stormpike is currently studying in the Prospector headquarters in Ironforge.$B$BAnd when you see him tell him you spoke with me - I\'ve been sending him quite a lot of business lately.",
  },
  [555] = {
    ["T"] = "Soothing Turtle Bisque",
    ["O"] = "Bring 10 pieces of Turtle Meat and some Soothing Spices to Chef Jessen in Southshore.",
    ["D"] = "If there\'s one thing you\'ll learn about Southshore it\'s that we have some of the best cuisine north of Stormwind!$b$bTake my secret recipe for Turtle Bisque, for example. I\'ve known folks to travel from as far as Darkshire just to enjoy a bowl.  Speaking of which, I haven\'t been able to make any lately.  I used to head up past Dalaran to Lake Lordamere myself to hunt Snapjaws but it\'s just too dangerous now. If you bring me some Turtle Meat from the Snapjaws up north and Soothing Spices, I\'ll whip some up!",
  },
  [556] = {
    ["T"] = "Stone Tokens",
    ["O"] = "Bring 10 Worn Stone Tokens to Keeper Bel\'varil in Tarren Mill.",
    ["D"] = "A notable talent of the wizards of Dalaran is their familiarity with elementals: powerful creatures from other dimensions.  The wizards use magical tokens as a means to control these elementals.  Magus Voidglare would like some of these tokens to study.$B$BEnter the territories of the Dalaran wizards, west of Hillsbrad, and gather the tokens.$B$BYou might find the tokens on any citizen of Dalaran, but I suggest hunting at the internment camp.  It\'s safer than Dalaran itself.",
  },
  [557] = {
    ["T"] = "Bracers of Binding",
    ["O"] = "Bring 4 Bracers of Earth Binding to Keeper Bel\'varil in Tarren Mill.",
    ["D"] = "The Magus will be busy studying those stone tokens you gathered, but I know he\'ll eventually want examples of their counterpart: bracers of binding.  These are set around the wrists of actual elementals, and bind them into the mage\'s service.$B$BHunt elementals in the ruins of Dalaran and gather their Bracers of Earth Binding, and the Magus will be pleased.",
  },
  [558] = {
    ["T"] = "Jaina\'s Autograph",
    ["O"] = "Travel to Theramore to see about getting Lady Jaina Proudmoore\'s autograph for your ward.",
    ["D"] = "They say that Lady Jaina Proudmoore in Theramore is one of the greatest heroes the Alliance has ever had.  When I grow up, I wanna be a hero of the Alliance too!$B$BDo you know Jaina, $N?  Could you do me a favor, please?  Could you get her autograph for me?  I dunno how to ask her, and she\'d probably be too busy for someone like me... but you!  You\'re an adventurer just like she is!  I bet she\'d give you her autograph easy!$B$BPlease?",
  },
  [559] = {
    ["T"] = "Farren\'s Proof",
    ["O"] = "Collect 10 Murloc Heads from the murlocs on the Western Strand for Lieutenant Farren Orinelle in Southshore.",
    ["D"] = "Would you believe it, $n? Redpath wasn\'t satisfied with all the murlocs we killed--oh now, don\'t be modest, you had some hand in it. Now he wants me to bring proof of the dead, hacking off cold, slimy murloc heads to submit for his approval.",
  },
  [560] = {
    ["T"] = "Farren\'s Proof",
    ["O"] = "Deliver the sack of murloc heads to Marshall Redpath in Southshore.",
    ["D"] = "You know, $n, I\'d really rather not take these heads... Since you\'ve already got a handle on them, would you mind delivering them to Redpath for me?",
  },
  [561] = {
    ["T"] = "Farren\'s Proof",
    ["O"] = "Speak with Lieutenant Farren Orinelle in Southshore.",
    ["D"] = "Please inform Lieutenant Orinelle that I would like him to refocus his efforts on the naga infesting the Eastern Strand. When he has results, tell him to report to me in person.",
  },
  [562] = {
    ["T"] = "Stormwind Ho!",
    ["O"] = "Kill 10 Daggerspine Shorehunters and 10 Daggerspine Sirens for Lieutenant Farren Orinelle in Southshore.",
    ["D"] = "My former mentor in the Stormwind city guard sent a message to me that Alliance High Command is becoming concerned with the threat posed by the naga. From what he\'s heard, there\'s good chances for promotion--or at least reassignment--for anyone who proves valuable in this regard.$b$bThere\'s just one small problem... I can\'t handle the naga, $n. Oh sure, I could break up a drunken barroom brawl, or haul off a belligerent beggar, but the naga are too much for me. You\'ve got to help me, $n!",
  },
  [563] = {
    ["T"] = "Reassignment",
    ["O"] = "Deliver Farren\'s report to Major Samuelson in Stormwind Keep.",
    ["D"] = "It shouldn\'t be a problem getting my report seen. The new captain of the guard, Major Samuelson, was my mentor while I was stationed in Stormwind. It should give him more than enough excuse to have me transferred back to the city. I\'ll need you to carry my report to him, though... wouldn\'t want ol\' Redpath to catch on.",
  },
  [564] = {
    ["T"] = "Costly Menace",
    ["O"] = "Darren Malvew of Southshore wants you to kill 8 Mountain Lions and 10 Hulking Mountain Lions.",
    ["D"] = "Ain\'t nothin\' that irks me more than losin\' these here beautiful horses.  And believe me, we\'ve lost quite a few between them pesky undead creeps, the dang Syndicate gang and all the other good-for-nothin\' scoundrels pokin\' about these parts.$b$bBut I swear on my ol\' grand-daddy\'s grave there ain\'t no bigger menace to our horses here than them Mountain Lions up in Alterac.  Always comin\' down from the hills and makin\' my life difficult!$b$bYou fancy yourself a hero?  Go and do somethin\' about it then!",
  },
  [565] = {
    ["T"] = "Bartolo\'s Yeti Fur Cloak",
    ["O"] = "Bring Bartolo Ginsetti of Southshore a Bolt of Wool, a Hillman\'s Cloak, Fine Thread and 10 clumps of Yeti Fur.",
    ["D"] = "I, the great Bartolo, shall make for you the world famous, Alliance renowned, Yeti Fur Cloak!$b$bTravel the planet and find yourself a better cloak, I dare you.  Oh wait, what is that, little $r?  \"Bartolo, there is no finer cloak!\"  Yes, yes!  That, I have heard before.$b$bBartolo requires a Bolt of Wool, a Hillman\'s Cloak made by a skilled leatherworker, Fine Thread and Yeti Fur, of course!  Bring to me these items and you shall be a happy customer!",
  },
  [566] = {
    ["T"] = "WANTED: Baron Vardus",
    ["O"] = "Kill Baron Vardus and deliver his head to High Executor Darthalia in Tarren Mill.",
    ["D"] = "By order of High Executor Darthalia, the human known as Baron Vardus is wanted for crimes against the Forsaken. Vardus is known to be a high-ranking member of the organization known as the Syndicate.$b$bHe was last spotted in the Uplands in the Alterac Mountains, north of Tarren Mill.$b$bHe is wanted dead. Bring evidence of his demise to High Executor Darthalia to claim the reward.",
  },
  [567] = {
    ["T"] = "Dangerous!",
    ["O"] = "High Executor Darthalia of Tarren Mill is offering a bounty on Clerk Horrace Whitesteed, Citizen Wilkes, Miner Hackett and Farmer Kalaba.",
    ["D"] = "Dangerous!$b$bThe following humans of Hillsbrad have been deemed dangerous and are marked for bounty by High Executor Darthalia:$b$bClerk Horrace Whitesteed.  Wanted for the murder of Deathguard Toma.$b$bCitizen Wilkes.  Wanted for the murder of Apothecary Eli.$b$bMiner Hackett.  Wanted for the murder of Deathstalker Fry.$b$bFarmer Kalaba.  Wanted for the ambush of supplies from the Undercity.$b$bAll of these enemies are hiding and will be hard to find.  A reward will be granted upon notice of their death.",
  },
  [568] = {
    ["T"] = "The Defense of Grom\'gol",
    ["O"] = "Commander Aggro\'gosh of the Grom\'gol base camp wants you to kill 15 Lashtail Raptors.",
    ["D"] = "Throm\'ka, $c!$b$bYour arrival at Grom\'gol is timely, indeed.  As commander of the Warchief\'s base camp here in the jungle I am bound by honor to ensure the safety of all members of the Horde.  Our mission to provide a safe chain of supply to Stonard is being hampered by some of the local inhabitants.$b$bI am putting you in charge of thinning out the raptor population outside of Grom\'gol.  Once you have made significant progress, report back to me for reassignment.",
  },
  [569] = {
    ["T"] = "The Defense of Grom\'gol",
    ["O"] = "Commander Aggro\'gosh of the Grom\'gol base camp wants you to kill 10 Ogre Brutes and 5 Ogre Witch Doctors.",
    ["D"] = "When we first arrived in this jungle we did not intend to wage war on the local tribes.  Our mission to supply Stonard is of the highest priority.  But complications have arisen.  A local band of ogres has occupied the Mizjah Ruins to the southeast of Grom\'gol.  Despite warnings, they continue to ambush our supply caravans.$b$bI am putting you in charge of dealing with these uncooperative ogres.$b$bTravel to the ruins and attempt to drive them from the area.  At the very least it will send a firm message.",
  },
  [570] = {
    ["T"] = "Mok\'thardin\'s Enchantment",
    ["O"] = "Far Seer Mok\'thardin at the Grom\'gol base camp wants 8 Shadowmaw Claws and a Pristine Tigress Fang.",
    ["D"] = "The power of the jungle\'s magic must be realized and then harnessed by the Horde.  My rituals have proven successful so far.  I can cast very powerful enchantments.  For you, brave $c, I shall make a special item indeed.$b$bBring to me some Shadowmaw Panther claws along with a Tigress fang.  Not just any fang will do.  It must be in pristine condition.$b$bThe beasts lurk throughout the jungle but you will find some close by, just across the river from Mizjah Ruins to the southeast.",
  },
  [571] = {
    ["T"] = "Mok\'thardin\'s Enchantment",
    ["O"] = "Bring an Aged Gorilla Sinew to Far Seer Mok\'thardin in Grom\'gol.",
    ["D"] = "What I need from you now will be very difficult to obtain.$b$bTo hold all these feathers, claws and the pristine fang in place I require a magical tether.  Only an aged gorilla sinew will work.$b$bYou will only be able to find one on an Elder Mistvale Gorilla.  They are known to forage to the east of Booty Bay.$b$bThe sinew must be perfect.  It might take quite a few kills for you to find exactly what I need but it will be well worth the hunt.",
  },
  [572] = {
    ["T"] = "Mok\'thardin\'s Enchantment",
    ["O"] = "Bring 10 Jungle Stalker Feathers to Far Seer Mok\'thardin at the Grom\'gol base camp.",
    ["D"] = "The items you gathered from the feline beasts of the Vale are only the first requirement for a powerful enchanted item.  These jungle trinkets combined with my shamanistic ritual will provide you with a very useful tool in assisting the Horde and the Warchief.$b$bBut now you must gather for me some Jungle Stalker Feathers.  The feathers are traditionally worn by the beasts on their armbands.  You will find a tribe of the raptors directly south of Grom\'gol, not far from the great arena.",
  },
  [573] = {
    ["T"] = "Mok\'thardin\'s Enchantment",
    ["O"] = "Far Seer Mok\'thardin of Grom\'gol needs Holy Spring Water.  He also wants you to kill 10 Naga Explorers.",
    ["D"] = "There is but one final reagent needed to complete the enchantment.$b$bIn the south, towards the western coast, high above the bluffs, lies a Holy Spring.  This spring is the subject of great conflict amongst religious and mystical scholars.  Every race in Azeroth claims that the spring waters were made divine for their people.$b$bMy final request of you: bring to me Holy Spring Water.  Be warned.  An expedition of naga explorers have advanced upon the spring.  They will need to be dealt with as well.",
  },
  [574] = {
    ["T"] = "Special Forces",
    ["O"] = "Kill 10 Kurzen Commandoes and 6 Kurzen Headshrinkers.$B$BReport to Lieutenant Doren at the Rebel Camp.",
    ["D"] = "Now that many of his regular troops are reduced, you must face his highly trained forces.  When they\'re not in the wilderness slaughtering our rebel soldiers, they are found deep in the back of the Kurzen Compound, in a cave they call the Stockpile.$B$BI know many of Kurzen\'s troops personally, and I know they are skilled, loyal, and cruel.  This is no easy task I set before you, $N.$B$BAfter you defeat them, report to Lieutenant Doren, my commanding officer.  He will want your report firsthand.",
  },
  [575] = {
    ["T"] = "Supply and Demand",
    ["O"] = "Bring 2 Large River Crocolisk Skins to Drizzlik in Booty Bay.",
    ["D"] = "\"The finest leathercrafter of the South Seas and the worlds old and new.\" That\'s what they call me. Me! Drizzlik! The finest lea... Ahem. Well.$b$bMy leather goods are known far and wide as the most exquisite, a connoisseur\'s choice!$b$bI\'ve just received an order from Director Riddlevox of the Tinkers\' Union for a dozen of his favorite Excelsior-line boots. Our best sellers, actually.$b$bTo start on the basic shape, I\'ll need crocolisk skins from the crocs along the river in northern Stranglethorn.",
  },
  [576] = {
    ["T"] = "Keep An Eye Out",
    ["O"] = "Dizzy One-Eye in Booty Bay wants you to retrieve his eye from the Bloodsail Buccaneers.",
    ["D"] = "Avast!  Who goes there?$b$bArgh, I am but useless without me special enchanted eye.  Bad enough to have lost me eyes in battle in the first place!  But by some stroke o\' luck we docked in Menethil and a traveling wizard enchanted a special glass eye that let me see again.$b$bLast night I ended up gettin\' jumped by some Bloodsail Buccaneers on me way out.  Took me eye, they did!  Seems those blokes have found their way to Booty Bay.$b$bHelp an old sea dog out by gettin\' me eye back?",
  },
  [577] = {
    ["T"] = "Some Assembly Required",
    ["O"] = "Bring 5 Snapjaw Crocolisks Skins to Drizzlik in Booty Bay.",
    ["D"] = "Well, I\'ve got the basic shape of the boots worked out, and they\'re coming along fine, but the skins you brought aren\'t going to be quite enough for the inner layers.$b$b\"What are we going to do, Drizzlik?\" No doubt the question on your lips, and well, isn\'t it lucky you\'re talking to Drizzlik, because he\'s going to tell you what we\'re going to do!$b$bThe inner layers will never really be seen, so we\'ll just get some of the thicker, cheaper skins from the crocs around Lake Nazferiti to fill the boots out.",
  },
  [578] = {
    ["T"] = "The Stone of the Tides",
    ["O"] = "Find the haunted island for Baron Revilgaz in Booty Bay.",
    ["D"] = "If there is indeed a goblin mage and water elementals haunting the island, it could mean the Stone of the Tides can be mine!$b$bI want you to locate this haunted island... find out what\'s going on. Oh, and if you perhaps want to find out more about the Stone of the Tides, take this scrip to the Stormwind Library, they should be able to find you a copy of the book I first came across the legend in.$b$bCould it be that Gazban actually discovered the Stone...?",
  },
  [579] = {
    ["T"] = "Stormwind Library",
    ["O"] = "",
    ["D"] = "",
  },
  [580] = {
    ["T"] = "Whiskey Slim\'s Lost Grog",
    ["O"] = "Whiskey Slim in Booty Bay wants you to bring him the bottles of Pupellyverbos Port he lost.",
    ["D"] = "We was splicin\' the mainbrace somethin\' fierce off of the Overlook Cliffs, ol\' Hinterlands just in the distance.  Thought the seas was goin\' to swallow us whole.$b$bOl\' Slim knows all about liquid courage!  Cap\'n had a crate of Pupellyverbos Port we commandeered from a privateer en route to Stormwind.$b$bSo when that storm off of Hinterlands hit, I busted into the grog.  Big wave hit and tossed them bottles overboard.  Cap\'n is goin\' to give me a taste o\' the cat if ye don\'t help me get \'em back though!",
  },
  [581] = {
    ["T"] = "Hunt for Yenniku",
    ["O"] = "Bring 9 Bloodscalp Tusks to Nimboya at the Grom\'gol Base Camp.",
    ["D"] = "A doom, there is.  A doom on my chief\'s youngest son, Yenniku.  As is a custom with our people, he was given to the Gurubashi Trolls of Zul\'Gurub, but after my tribe, the Darkspear tribe, left with the orc Thrall, poor Yenniku was lost to us.  I was sent from distant Orgrimmar, back to Stranglethorn, to search for my chief\'s son.$B$BAnd I believe the Bloodscalp tribe has him.$B$BIf you would help me, then hunt the Bloodscalps.  You may find them to the north.  Gather their tusks and return to me.",
  },
  [582] = {
    ["T"] = "Headhunting",
    ["O"] = "Bring 20 Shrunken Heads to Nimboya at the Grom\'gol Base Camp.",
    ["D"] = "We must find if Yenniku is dead.$B$BSlay the Bloodscalp Headhunters and take from them their Shrunken Heads.  You must gather many, many heads, and I will inspect them to make sure Yenniku\'s head is not among them.$B$BAnd if Yenniku was killed by the Bloodscalps, then may a 600-year curse fall on the spirit of their chief, Gan\'zulah!$B$BGo, $N.  You may find many Headhunters at the ruins of Zuuldaia and Zul\'Kunda, along the coast to the north.",
  },
  [583] = {
    ["T"] = "Welcome to the Jungle",
    ["O"] = "Speak with Hemet Nesingwary.",
    ["D"] = "Welcome to Stranglethorn!  Perhaps you\'re not aware of this, but that dwarf over there is the one and only Hemet Nesingwary, renowned war hero of the Alliance and master big game hunter. He\'s not one for welcoming strangers into his camp, but you look like you\'ve seen quite a bit of action in your day, %c.  Being a veteran of many battles himself, Master Nesingwary has a soft spot for fellow heroes.  Go and speak with him. Perhaps he can give you some hunting pointers.",
  },
  [584] = {
    ["T"] = "Bloodscalp Clan Heads",
    ["O"] = "Bring Gan\'zulah\'s Head and Nezzliok\'s Head to the bubbling cauldron at the Grom\'gol Base Camp.",
    ["D"] = "Yenniku was not among the heads, and I thank the Great Serpent for it.  But we are no closer to finding him.$B$BWe must know the truth, and I fear that only the Bloodscalp chief and his witchdoctor know it.  They are the enemies of the Darkspear tribe and will not speak freely, so we must force the truth from their dead lips!$B$BBring me the heads of Gan\'zulah and Nezzliok the Dire and place them within this cauldron.$B$BThey are deep within Zul\'Kunda.  Bring their heads...we will then make them talk.",
  },
  [585] = {
    ["T"] = "Speaking with Nezzliok",
    ["O"] = "Bring a Balia\'mah Trophy, a Ziata\'jai Trophy and a Zul\'Mamwe Trophy to the Bubbling Cauldron at the Grom\'gol Base Camp.",
    ["D"] = "<The head of Nezzliok speaks.>$B$BIt is true.  I know where the child of the Darkspears was taken.  You have bested me and bound my spirit, but I will say nothing without appeasement!$B$BThe Bloodscalps war with the Skullsplitters.  To gain my favor, steal from the Skullsplitters 3 of their trophy skulls and place them here with us, so that my spirit will have slaves in the Nether.  One skull must be from their trophy pile in Balia\'mah, another from Ziata\'jai, and the last from Zul\'Mamwe.",
  },
  [586] = {
    ["T"] = "Speaking with Gan\'zulah",
    ["O"] = "Kill 8 Skullsplitter Hunters, 6 Skullsplitter Headhunters, and 4 Skullsplitter Berserkers.$B$BBring the Broken Armor of Ana\'thek to the Bubbling Cauldron at the Grom\'gol Base Camp.",
    ["D"] = "<The head of Gan\'zulah speaks.>$B$BCurse you!  And curse your ancestors!!$B$BOnly blood and revenge can cool my rage, so if that is your wish, then... do this:$B$BThrow yourself into the heart of the Skullsplitters to the east.  Slay the fiercest among them, and if you survive...then break yourself against their chief, Ana\'thek the Cruel.  Hah!  Bring me his shattered armor if you can!$B$BMay he tear off your limbs and leave you to rot and be eaten by carrion.",
  },
  [587] = {
    ["T"] = "Up to Snuff",
    ["O"] = "Deeg in Booty Bay wants you to bring him 15 shares of Snuff.",
    ["D"] = "Ah, the sea life.  I ain\'t gonna say it\'s the good life, but it\'s my life.$b$bWhen I get into port I like to find me a good bottle of grog and some high quality snuff.  Nasty habits but what\'s a pirate without his vices, right?$b$bSo you can imagine how heartbroken I was to find out the herbalist here in town has gone and sold out of the stuff.  Says his shipment got hijacked by those damned Bloodsail Raiders.$b$bI bet a fearless $c like you could convince \'em to share the goods, eh?",
  },
  [588] = {
    ["T"] = "The Fate of Yenniku",
    ["O"] = "Speak with Kin\'weelay.",
    ["D"] = "<Nezzliok speaks.>$B$BYou have appeased myself and my chief, so I will tell you that which you seek to discover...$B$BYoung Yenniku is lost to his clan.$B$BHe was taken by the witchdoctor Zanzil the Outcast.  Now Zanzil controls him, body and soul, and only the most potent magic could set him free.$B$BA magic that you do not have, $c.$B$BPerhaps your Darkspear allies can offer a hope of Yenniku\'s salvation.  But that, I doubt.",
  },
  [589] = {
    ["T"] = "The Singing Crystals",
    ["O"] = "Bring 3 Pulsing Blue Shards to Kin\'weelay at the Grom\'gol Base Camp.",
    ["D"] = "Legends tell of a jewel, hidden deep in the heart of Stranglethorn.  We call it the Mind\'s Eye, and it has great powers.  If you gain this Eye then, perhaps, we can use it to bring lost Yenniku back from the darkness.$B$BI will consult with the spirits of the jungle to find the Mind\'s Eye.  While I do this, you enter the Crystalvein Mine, southeast of here, and gather Pulsing Blue Shards from Ironjaw Basilisks.  The shards are rare, but we will need their magic to bend the Mind\'s Eye to our will.",
  },
  [590] = {
    ["T"] = "A Rogue\'s Deal",
    ["O"] = "Defeat Calvin Montague in Deathknell.",
    ["D"] = "What?$B$BWhat?!$B$BYou want money? I don\'t owe you any money.$B$BLetter? What letter? Oh, you wanna fight then? All right, mate... let\'s do this! Owe you any money...",
  },
  [591] = {
    ["T"] = "The Mind\'s Eye",
    ["O"] = "Bring the Mind\'s Eye to Kin\'weelay at the Grom\'gol Base Camp.",
    ["D"] = "The Mind\'s Eye is held by an ogre mage, east of here and deep within the Mosh\'Ogg ogre mound.  His name is Mai\'Zoth.  I know him from when the Darkspears dwelt in Stranglethorn - he is vicious, and his magic is strong.$B$BAnd if he has the Mind\'s Eye, then his magic will be stronger still.$B$BYou must face Mai\'Zoth and wrest from him the Mind\'s Eye.  It is a perilous task, but it is the only hope we have of saving Yenniku.",
  },
  [592] = {
    ["T"] = "Saving Yenniku",
    ["O"] = "Bring the Filled Soul Gem to Nimboya.",
    ["D"] = "I have reshaped the Mind\'s Eye into a Soul Gem.  Its purpose is twofold: you must use it to stun Yenniku, and once stunned place it upon his head to entrap his soul.$B$BThen, bring the gem to Nimboya.$B$BYenniku dwells with Zanzil the Outcast, at the Ruins of Aboraz, along the Crystal Shore on the distant Cape of Stranglethorn.",
  },
  [593] = {
    ["T"] = "Filling the Soul Gem",
    ["O"] = "",
    ["D"] = "",
  },
  [594] = {
    ["T"] = "Message in a Bottle",
    ["O"] = "Find the author of the message in a bottle.",
    ["D"] = "The note reads, in fairly good handwriting:$B$BTo any that can aid me-$B$BPlease, my need for help is dire. A great Skymane holds me captive on an island in the South Seas.$B$BThe stars at night are foreign to me and obscured by the jungle trees, but on the clearest days I see Wild Shore to the northwest, and what seem to be large sailing ships to the southwest.$B$BI beg of you, my would-be savior, please help me.",
  },
  [595] = {
    ["T"] = "The Bloodsail Buccaneers",
    ["O"] = "First Mate Crazz wants you to check the cove north of Booty Bay.",
    ["D"] = "You sure seem eager to work, matey.  Not sure why some land-loving $r $c is offering help to the Blackwater Raiders, but I\'m not going to turn you away.$b$bOur ship just docked here in Booty Bay and word has it that our rivals--those damned, dirty Bloodsail Buccaneers--have been spotted all over southern Stranglethorn.$b$bAll the boys are too busy hitting the grog and looking for ladies!  What I need is a scout.$b$bCheck the cove just north of Booty Bay along the Savage Coast and see what you find.",
  },
  [596] = {
    ["T"] = "Bloody Bone Necklaces",
    ["O"] = "Bring 25 Bloody Bone Necklaces to Kin\'weelay at the Grom\'gol Base Camp.",
    ["D"] = "I was sent to Grom\'gol  to aid Nimboya on his quest, but... I too have reasons for being here.$B$BThe Bloodscalp tribe is enemy to our tribe.  Collecting trophies of our enemies gives us power over them.  You may aid our tribe by hunting our enemies.$B$BThey dwell to the north.  Slay Bloodscalp trolls and take from them their bloody bone necklaces.  Collect many, and your reward will be great.",
  },
  [597] = {
    ["T"] = "The Bloodsail Buccaneers",
    ["O"] = "Report your findings to First Mate Crazz in Booty Bay.",
    ["D"] = "The map shows Booty Bay with a red \"X\" drawn precisely at the spot at which you are standing.$b$bThe scribbled text reads:$b$b\"You men are to secure a camp at this location and keep quiet.  Wait for the signal from the southern command post before attacking.  If anyone approaches you, kill them.\"",
  },
  [598] = {
    ["T"] = "Split Bone Necklace",
    ["O"] = "Bring 25 Split Bone Necklaces to Kin\'weelay at the Grom\'gol Base camp.",
    ["D"] = "Now that you have gathered Bloodscalp trophies, I have another task for you.  The Skullsplitter tribe of trolls live to the southeast, and for years we have warred with them.  Although the Darkspears no longer dwell in Stranglethorn, we are bound to our conflict with the Skullsplitters.$B$BSo, hunt them.  Gather their Split Bone Necklaces and return to me.  Aid us in our war, and you will earn the friendship of the Darkspears.",
  },
  [599] = {
    ["T"] = "The Bloodsail Buccaneers",
    ["O"] = "Report the planned Bloodsail attack to Fleet Master Seahorn.",
    ["D"] = "Shiver me timbers!  So the Bloodsail Buccaneers are planning some sort of attack on Booty Bay?  Why, the nerve of those scoundrels.  Booty Bay will always be run by the Blackwater Raiders!$b$bThis is bigger than I thought.  Report what you have learned to Fleet Master Seahorn at once!  The ol\' sea dog is around town somewhere....",
  },
  [600] = {
    ["T"] = "Venture Company Mining",
    ["O"] = "Bring 10 Singing Blue Crystals to Crank Fizzlebub in Booty Bay.",
    ["D"] = "The Venture Company has a string of operations through Stranglethorn which keeps hard-working goblins, like me, from making honest gold!  Please, you must help me!$B$BThe Venture Company is mining near the Crystalvein Mine to the north.  They can\'t get into the mine because of all the basilisks, but they\'re still able to dig up Singing Crystals from the surrounding hills.$B$BTake their crystals from them, and show them they don\'t have the run of the jungle.$B$BAnd... um... bring me those crystals as proof!",
  },
  [601] = {
    ["T"] = "Water Elementals",
    ["O"] = "Acquire 6 Water Elemental Bracers for Baron Revilgaz in Booty Bay.",
    ["D"] = "The water elementals were no doubt summoned by Gazban, the goblin wizard rumored to have discovered the Stone of the Tides. As you may know, the Stone of the Tides curses its bearer, causing him to fade in and out of existence...$b$bPerhaps we can find a way to counteract the curse. We can have a mage of Dalaran analyze the magical residue of one of the water elemental\'s bracers. I need you to go back to the island and retrieve a few specimens.",
  },
  [602] = {
    ["T"] = "Magical Analysis",
    ["O"] = "Bring the water elemental bracers to Archmage Ansirem Runeweaver in Dalaran.",
    ["D"] = "From what I\'ve heard, the mages of Dalaran are busy with the reconstruction of their destroyed city, I\'m sure you can find one of them who will spare some time to look at the bracers. If there\'s anything I know about mages, they can\'t resist a magical puzzle.$b$bTake the bag of water elemental bracers and see if they can shed any light on the Stone\'s curse.  Look for the Archmage Ansirem Runeweaver in particular.  I\'ve dealt with him before, and his knowledge is vast.",
  },
  [603] = {
    ["T"] = "Ansirem\'s Key",
    ["O"] = "Talk to Catelyn in Booty Bay.",
    ["D"] = "The mage that is in possession of the Stone is amplifying its power through some focal point... an altar of some sort. You should be able to disrupt it by overpowering it.$b$bAs it happens, I know just the thing: an ancient dagger, another troll relic. There\'s just one... little problem.$b$bYou see, I found the dagger while researching in Stranglethorn, but I gave it to my willful daughter, Catelyn, who ran off to Booty Bay to become a pirate.",
  },
  [604] = {
    ["T"] = "The Bloodsail Buccaneers",
    ["O"] = "Fleet Master Seahorn in Booty Bay wants you to kill 10 Bloodsail Swashbucklers and bring back the Bloodsail Charts and the Bloodsail Orders.",
    ["D"] = "Crazz speaks highly of your dedication to the Blackwater Raiders.  I am going to trust you with a mission of extreme importance to the fleet.$b$bInfiltrate the so-called southern command post the Bloodsail Buccaneers have established on the Wild Shore.  Raid the encampment.  Bring me their charts along with their written orders.  We shall see exactly what those scum are up to.",
  },
  [605] = {
    ["T"] = "Singing Blue Shards",
    ["O"] = "Bring 10 Singing Crystal Shards to Crank Fizzlebub.",
    ["D"] = "The Singing Crystals are unique to Stranglethorn, and are very valuable to certain parties.  I can move those crystals, but the cursed Venture Company makes it hard for an honest entrepreneur like myself to gather any!$B$BI\'d like to hire you.$B$BThe basilisks in Stranglethorn eat the crystal.  This gives them their hardened skin, and sometimes decent quality crystal can be harvested from it.$B$BYou can get it from any basilisk, but the less nasty ones are along the shores south of Zul\'Kunda, to the north.",
  },
  [606] = {
    ["T"] = "Scaring Shaky",
    ["O"] = "Bring 5 Mistvale Giblets to \"Shaky\" Phillipe in Booty Bay.",
    ["D"] = "If there\'s one thing I can\'t stand, it\'s people who don\'t pay their gambling debts!  And there are a lot of people who owe me. Think you can help me collect?$B$BFirst, there\'s \"Shaky\" Phillipe down by the docks.  He owes me plenty after last week\'s game and I want my due!  But to get it, we\'ll have to scare him.$B$BGo out of town and hunt Elder Mistvale Gorillas.  Find a nice selection of giblets, then take them to Shaky.  Don\'t let on that they\'re from an animal; let him think the worst.",
  },
  [607] = {
    ["T"] = "Return to MacKinley",
    ["O"] = "Bring Shaky\'s Payment to \"Sea Wolf\" MacKinley.",
    ["D"] = "Here, take this pouch and give it to Sea Wolf.  And tell him I meant to pay him back!  I swear!!$B$BI just got a little drunk and lost my way, and...you know how things are.  He\'ll understand - you don\'t have to get rough!",
  },
  [608] = {
    ["T"] = "The Bloodsail Buccaneers",
    ["O"] = "Fleet-Master Seahorn in Booty Bay wants you to kill Captain Stillwater, Captain Keelhaul and Fleet Master Firallon.",
    ["D"] = "Blast the nerve of Firallon!  He couldn\'t sail a skiff through Crystal Lake let alone lead an entire fleet against us.$b$bThe Bloodsail Buccaneers will pay for their carelessness.  Now that we know their plan of attack, we can counter before they know what hit them.$b$bBut why send the entire fleet to do the work of one brave adventuring party?  $n, take some of your most skilled comrades out to Firallon\'s landing spot and assassinate the Bloodsail leadership.  Their fleet will sink soon after....",
  },
  [609] = {
    ["T"] = "Voodoo Dues",
    ["O"] = "Bring Maury\'s Clubbed Foot, Jon-Jon\'s Golden Spyglass, and Chucky\'s Huge Ring to \"Sea Wolf\" MacKinley in Booty Bay.",
    ["D"] = "My list is shortening, but there are still people who owe me.$B$BNext, we have Maury \"Club Foot\" Wilkins, Jon-Jon the Crow, and Chucky \"Ten Thumbs.\"$B$BThese scurvy dogs\' debts are months outstanding and I thought they skipped town to avoid paying up!  Later, I heard they\'re cursed and bewitched and now wander the jungle ruins.  But I don\'t care what their fate is - I want what\'s mine!$B$BThey\'re at the Ruins of Aboraz and the Ruins of Jubuwal, northeast of here.  Find them, and collect.",
  },
  [610] = {
    ["T"] = "\"Pretty Boy\" Duncan",
    ["O"] = "Get Catelyn\'s dagger from \"Pretty Boy\" Duncan.",
    ["D"] = "My FATHER sent you? I\'d have thought that old codger would have given up on me by now.$b$bIt wasn\'t really his fault I ran off, he just shouldn\'t have expected I\'d be willing to live a boring life, reading over musty books and associating with grey-haired, stiff-backed, stuffy mages!$b$bIf you need my dagger, you\'ll have to get it back from \"Pretty Boy\" Duncan, one of the Bloodsail Buccaneers. Put something \'tween his shoulders and I\'m sure he\'ll give it up.",
  },
  [611] = {
    ["T"] = "The Curse of the Tides",
    ["O"] = "Destroy the Altar of the Tides with Catelyn\'s Blade, kill Gazban and bring the Stone of the Tides and Catelyn\'s Blade back to Baron Revilgaz.",
    ["D"] = "So you\'re looking for an altar of some sort? Well, I\'m no expert on troll ruins like my father, but I have heard about a sunken troll city out near that island you\'re looking for. Maybe you should try there.$b$bYou got my knife back for me, so I guess you can borrow it... but I want it back when you\'re done!",
  },
  [613] = {
    ["T"] = "Cracking Maury\'s Foot",
    ["O"] = "Bring Maury\'s Key to \"Sea Wolf\" Mackinley in Booty Bay.",
    ["D"] = "Curses!  That swab Maury put a lock on his foot, and I can\'t open it!  His loot\'s inside the foot, and that\'s what he owes me!$B$BWe need to find the key to open the lock.  Maury was known to gamble with the Mosh\'Ogg ogres, at their mound to the northeast.  And he told me, when he was still alive, that the last time he went to the mound he was chased off after winning too much.  Maybe the Ogres there have his key.$B$BBut if they do, you might have to kill a load of them to find the right one...",
  },
  [614] = {
    ["T"] = "The Captain\'s Chest",
    ["O"] = "Bring Smotts\' Chest to Hecklebury Smotts in Booty Bay.",
    ["D"] = "I used to sail a ship from here all the way to Ratchet, in Kalimdor.  I was a successful captain with a sterling reputation.$B$BUntil...$B$BThose cursed sea giants ruined me!  They smashed my ship, killed my crew, and set me on a lifeboat to Booty Bay.  And one of the giants, Gorlash, stole my Captain\'s Chest.  He said he wanted it for a snuffbox.  The nerve!$B$BI hear Gorlash wanders the coast east of here.  Find him and get back my chest!  Do that, and you\'ll have earned a captain\'s gratitude.",
  },
  [615] = {
    ["T"] = "The Captain\'s Cutlass",
    ["O"] = "Speak with Sprogger.",
    ["D"] = "After I lost my first ship to those giants, I bought another.  Named Smotts\' Revenge, I filled it with supplies and crew, and set out to find the villains.  I found them, but...they beat me.  They smashed Smotts\' Revenge, killed my second crew and set me on another lifeboat.$B$BThis time another of the giants, Negolash, stole my cutlass.$B$BFace Negolash and bring me my cutlass!$B$BSpeak with Sprogger.  He was my cook on the Smotts\' Revenge, and survived the last attack.  He can help you find the giant.",
  },
  [616] = {
    ["T"] = "The Haunted Isle",
    ["O"] = "Speak with Baron Revilgaz in Booty Bay.",
    ["D"] = "Baron Revilgaz has had me keep an ear out for rumors of the strange and out-of-place in Booty Bay and Stranglethorn. I don\'t usually put much stock in ghost stories, but I heard some sailors in the tavern talking about a haunted island off the coast. Water elementals and a raving goblin ghost, they say.$b$bIf you\'re going to go talk to Revilgaz, could you relay that story to him?",
  },
  [617] = {
    ["T"] = "Akiris by the Bundle",
    ["O"] = "Bring 10 Bundles of Akiris Reed to Privateer Bloads in Booty Bay.",
    ["D"] = "You looking fer work, $N? I might have some if you\'re up for a little challenge.$B$BI got a friend named Groy who makes a killing selling what the naga call akiris reed: it\'s some kind of valuable ocean reed. Not too sure what it\'s for, but he pays well for bundles of the stuff.$B$BYa bring me ten stalks of the stuff, and we\'ll have business to do. The naga were last seen along the beach to the north of here.",
  },
  [618] = {
    ["T"] = "Facing Negolash",
    ["O"] = "Bring 10 Barbecued Buzzard Wings and 5 bottles of Junglevine Wine to Captain Smotts\' Lifeboat.$B$BKill Negolash, and bring Smotts\' Cutlass to Captain Smotts in Stranglethorn.",
    ["D"] = "Negolash is a hungry giant.$B$BWhen the giants destroyed our ship, Negolash went straight for my kitchen.  He wasn\'t hungry for crew like the other giants.  Negolash wanted wine...and my Barbecued Buzzard Wings.  They\'re my specialty, and I had to go all the way to the Badlands for the recipe!$B$BIf you want to lure the giant, then get some wine and a heap of buzzard wings.  Put them in our old lifeboat, southeast of here along the coast.  When Negolash smells all that food, he\'ll come for sure!",
  },
  [619] = {
    ["T"] = "Enticing Negolash",
    ["O"] = "",
    ["D"] = "",
  },
  [621] = {
    ["T"] = "Zanzil\'s Secret",
    ["O"] = "Bring 12 samples of Zanzil\'s Mixture to Crank Fizzlebub in Booty Bay.",
    ["D"] = "Zanzil the Outcast dwells with his followers in the Jubuwal and Aboraz Ruins, northeast of Booty Bay.  He is exiled from his tribe.  Why? Well...$B$BZanzil has methods of bending the minds of others, and his leaders fear him.  He uses a mixture of jungle plants on his followers to suppress their will and strengthen their bodies.$B$BHis mixture would be worth much to those who wish to control others.$B$BBring me samples of his mixture, and I will find a use for them.",
  },
  [622] = {
    ["T"] = "Return to Corporal Kaleb",
    ["O"] = "Bring Krazek\'s Fixed Pot to Corporal Kaleb at the Rebel Camp.",
    ["D"] = "While you were gone I fixed the pot you brought me.  I even added an extra steam whistle, and look!  There\'s a new chopper at the bottom that\'ll cut through leg bones!$B$BYes yes, Krazek\'s Crock Pot is a 100% cook pleaser!  I\'m sure its owner will be satisfied.",
  },
  [623] = {
    ["T"] = "Akiris by the Bundle",
    ["O"] = "Bring a Bundle of Akiris Reeds to Privateer Groy in Theramore.",
    ["D"] = "$N, since I have you here, I got a message from Groy while you were gone. He mentioned bein\' satisfied with the last shipment that arrived and has a client that wants some more of the stuff right away.$B$BI won\'t be headin\' to Theramore for a week, but if you\'re willin\' to make the journey, there\'s no reason you can\'t take the stuff for me.$B$BFeeling adventurous? You do know where Dustwallow Marsh is, doncha? I\'d be careful though, Theramore can be dangerous if you\'re the wrong kind of crowd.",
  },
  [624] = {
    ["T"] = "Cortello\'s Riddle",
    ["O"] = "Solve the riddle!",
    ["D"] = "There is an ornate, well-traveled bridge$bEast of Deadwind and south of Redridge$bUnderneath there waits something for you$bSome call it a hint, others a clue$bThere it will lie for endless tomorrows$bBiding its time in the Swamp of Sorrows",
  },
  [625] = {
    ["T"] = "Cortello\'s Riddle",
    ["O"] = "Solve the riddle!",
    ["D"] = "A far off land in Kalimdor awaits$bWhere peril has dashed hopes and fates$bEast of Barrens the weather is harsh$bGloom and danger pock Dustwallow Marsh$bBut there lies a clue for those up to the test$bWaiting to be found in a cave in the southwest",
  },
  [626] = {
    ["T"] = "Cortello\'s Riddle",
    ["O"] = "Solve the riddle!",
    ["D"] = "To Lordaeron! The great land of turmoil$bWhere brave soldiers with death do toil$bFind your way through the great unrest$bThere you will find the end to this quest$bAlong the coast of the Hinterlands, at the base of the great falls$bUnderneath the water there does your treasure call!",
  },
  [627] = {
    ["T"] = "Favor for Krazek",
    ["O"] = "Bring 4 loads of Lesser Bloodstone Ore to Krazek in Booty Bay.",
    ["D"] = "There is a mountain cave in Drywhisker Gorge, far to the north in the Arathi Highlands.  In that cave is rare ore: bloodstone.  The bloodstone in this cave is not of the highest grade, but even low-quality bloodstone is valuable... to those with the knowledge to use it.$B$BI would like some.$B$BI will fix your crock pot, but while I work on it... bring me samples of bloodstone.  Bring me enough, and I\'ll even give you a bonus.$B$BOh, and if you don\'t have the skill to mine it... find someone who does!",
  },
  [628] = {
    ["T"] = "Excelsior",
    ["O"] = "Bring an Elder Crocolisk Skin to Drizzlik in Booty Bay.",
    ["D"] = "With the inside finished, all that\'s left is to add the outer layer. For this, I use only the best elder croc skins. They are more durable and a better canvas--if you will--for adding the designs and accents that make Drizzlik\'s Excelsior line.$b$bIt\'s hard to find that perfect skin though, the only one that fits my plan is the skin of an elder saltwater crocolisk. The elders only come out to defend the other saltwater crocs, so you might need to kill a few to draw one out.",
  },
  [629] = {
    ["T"] = "The Vile Reef",
    ["O"] = "Bring a Tablet Shard to Kin\'weelay at the Grom\'gol Base Camp.",
    ["D"] = "Long ago, a great shudder of the earth sunk an old troll city beneath the waters of the Savage Coast to the northwest.  We call that place the Vile Reef, for murlocs now reside in the ruins of the city, attacking any who draw close.$B$BThere is an old tablet among those ruins that tells the ancient tale of Gri\'lek, a hero of troll legends.  The tale is sacred to the Darkspear tribe, and although the tablet is too large to move, I want a shard of it to enshrine in our new home in Orgrimmar.",
  },
  [630] = {
    ["T"] = "Message in a Bottle",
    ["O"] = "Slay King Mukla and return to Princess Poobah with the Key to free her.",
    ["D"] = "$N, I put my faith in you and anyone you can muster to help free me. The great Skymane King Mukla is my captor. He commands the beasts of this island, and they keep me here against my will with the aid of this chain. If you can slay him and find the key to this shackle, I shall be indebted to you greatly.$B$BI often hear him to the southeast side of the island, but please be careful: he is not to be trifled with.",
  },
  [631] = {
    ["T"] = "The Thandol Span",
    ["O"] = "Rhag Garmason wants you to locate Ol\' Rustlocke.",
    ["D"] = "A tremendous explosion roared through the hills.  \'Twas horrible, I tell you!$b$bGood soldiers died defending the Thandol Span.  Longbraid says we\'ll take revenge on the Dark Irons before all is said and done.$b$bWe\'re in a world of hurt though and I don\'t see any reinforcements in sight.  Longbraid sent Ol\' Rustlocke to scout the eastern bridge to see if we could secure it.$b$bThat was days ago.  We haven\'t the manpower to search for him with Dun Modr under siege.$b$bPerhaps you can find him...",
  },
  [632] = {
    ["T"] = "The Thandol Span",
    ["O"] = "Report back to Rhag Garmason and show him the parchment you found on Rustlocke\'s corpse.",
    ["D"] = "It seems as though poor Ol\' Rustlocke ran into a Dark Iron ambush.  You notice a parchment next to his clenched fist.$b$bReport the grim news to Rhag Garmason and show him what you found.",
  },
  [633] = {
    ["T"] = "The Thandol Span",
    ["O"] = "Destroy the cache of explosives.",
    ["D"] = "Heavens no!  Poor Rustlocke.  \'Tis quite a tragedy indeed.$b$bWe must not let his death pass in vain.  This parchment you discovered indicates that Kam Deepfury, the mastermind behind the original attack, arranged for a cache of explosives to be stashed just across the Thandol Span, in Arathi Highlands.$b$bIf those explosives make it to the bridge, our major supply line to the north will be severed!  The cache must be destroyed, $n!  Return to me when your mission is complete.$b$bYou\'re our only hope.",
  },
  [634] = {
    ["T"] = "Plea To The Alliance",
    ["O"] = "Seek out Captain Nials at Refuge Pointe.",
    ["D"] = "I ask of you one more small favor, $c.  We await the reserves of Ironforge.  However, Roggo has knowledge of an Alliance camp to the north in Arathi Highlands at Refuge Pointe.$b$bTravel there and seek out Captain Nials.  Ask the good Captain if the Alliance can spare some soldiers to help us reclaim Dun Modr and the Thandol Span.",
  },
  [635] = {
    ["T"] = "Crystal in the Mountains",
    ["O"] = "Find the crystal from your vision.",
    ["D"] = "This shard pulses as you hold it...$B$BYou feel a sense of pleading coming from the shard, as if someone is trying to communicate, someone who desperately needs your help.$B$BA vision appears in your mind - of the northern mountains of Arathi, and of a brilliant crystal jutting from the earth there.$B$BYou feel drawn to that place.",
  },
  [636] = {
    ["T"] = "Legends of the Earth",
    ["O"] = "Find the Shards of Myzrael.",
    ["D"] = "This book tells the tale of an elemental princess, Myzrael, and of her imprisonment deep underneath the Arathi Highlands.$B$BIt also tells of a secret outcropping of crystals in the highlands.  These crystals, the Shards of Myzrael, are a window to the princess\' prison.$B$BIf a brave soul found the Shards of Myzrael, the perhaps $ghe:she; could speak with her...",
  },
  [637] = {
    ["T"] = "Sully Balloo\'s Letter",
    ["O"] = "Deliver Sully Balloo\'s Letter to Sara Balloo.",
    ["D"] = "The waterlogged and war-torn envelope dissolves in your hands, leaving you with a few pages of a letter in your grasp, written with painstaking care.$b$bThe letter is addressed to a Mrs. Sara Balloo of Ironforge and begins, \"My dear Sara....\"",
  },
  [638] = {
    ["T"] = "Trollbane",
    ["O"] = "Travel to Hammerfall in the Arathi Highlands and speak with Zengu.",
    ["D"] = "With Thoras Trollbane dead and Stromgarde in ruins, Vol\'jin has instructed that we make every effort to spirit the legendary sword Trol\'kalar from the humans\' hands, so that we might use it in our coming battles against the trolls of Zul\'Gurub.$b$bZengu, one of our seasoned hunters, was dispatched to Hammerfall in northeastern Arathi Highlands to oversee its procurement.",
  },
  [639] = {
    ["T"] = "Sigil of Strom",
    ["O"] = "Get the Sigil of Strom for Zengu in Hammerfall.",
    ["D"] = "In ancient times, trolls fought against the Arathorian Empire and our most capable adversary, Lord Ignaeus of Strom, named Trollbane. He rode into battle wielding Trol\'kalar--troll slayer, in their ancient tongue.$b$bBut now the sons of Trollbane are weakened, their stronghold brought to ruin.$b$bThe sword is protected by several sigils that were divided among the survivors of Stromgarde, and we must retrieve them.$b$bThe first was taken by the Syndicate, one of their men in Stromgarde should have it.",
  },
  [640] = {
    ["T"] = "The Broken Sigil",
    ["O"] = "Retrieve the 5 Sigil Fragments from the defenders in Stromgarde, and bring them to Tor\'gan in Hammerfall.",
    ["D"] = "Thoras Trollbane\'s tomb is protected by three sigils--the first of which you retrieved from the Syndicate. The other two will be harder to get, as they\'re held by the firmly entrenched Stromgarde militia.$b$bThe sigil of Thoradin was divided into five pieces by Thoras\' son, Galen, and dispersed among the Stromgarde defenders. Retrieve the pieces and bring them to Tor\'gan, the troll shaman, he will be able to bind them into the complete sigil.",
  },
  [641] = {
    ["T"] = "Sigil of Thoradin",
    ["O"] = "Bring the restored Sigil of Thoradin to Zengu in Hammerfall.",
    ["D"] = "The reconstruction worked flawlessly. The sigil can now be used to unlock the seal on Trollbane\'s tomb. Return it to Zengu, no doubt he has additional tasks for you to complete.",
  },
  [642] = {
    ["T"] = "The Princess Trapped",
    ["O"] = "Gather 12 Motes of Myzrael, then bring them to the Iridescent Shards in Drywhisker Gorge.",
    ["D"] = "My name is Myzrael.  I am a princess of the earth, and my captors, the giants, have trapped me deep beneath the Arathi Highlands.  These crystal shards are the only way I can speak with the surface world.$B$BPlease help me.  Allies of the giants, the Drywhisker Kobolds, have a shard like this one in their Drywhisker Gorge, to the east.  To power the cluster, you must gather Motes of Myzrael from the kobolds and apply them to it.$B$BI beg you, $N, aid me!",
  },
  [643] = {
    ["T"] = "Sigil of Arathor",
    ["O"] = "Retrieve the Sigil of Arathor and return it to Zengu in Hammerfall.",
    ["D"] = "Though they can barely be said to control Stromgarde, let alone the whole of Arathi Highlands, the human prince trots the remnants of his cavalry over the countryside. I can\'t understand why he does it, but who understands the way the humans think, hm?$b$bWhatever his reasoning, more importantly, he has given one of the sigils to Lieutenant Valorcall, their cavalry leader. Locate him and bring his sigil back to me.",
  },
  [644] = {
    ["T"] = "Sigil of Trollbane",
    ["O"] = "Kill Prince Galen Trollbane and bring the Sigil of Trollbane to Zengu in Hammerfall.",
    ["D"] = "The final of the sigils needed to unlock the tomb of Thoras Trollbane is held by his son personally. He resides deep within Stromgarde, in a chapel in the militia-held districts. Prince Galen is well protected and no slouch of a warrior himself, so this task could prove most difficult.",
  },
  [645] = {
    ["T"] = "Trol\'kalar",
    ["O"] = "Retrieve Trol\'kalar from Trollbane\'s tomb in Stromgarde.",
    ["D"] = "What\'s this? With the four sigils brought together, they have formed together to create one sigil. There can be no doubt that this will be able to break the wardings placed upon Trollbane\'s tomb.$b$bTrollbane\'s remains are entombed in the Sanctum behind the chapel in Stromgarde. Retrieve Trol\'kalar.",
  },
  [646] = {
    ["T"] = "Trol\'kalar",
    ["O"] = "Deliver Trol\'kalar to Zengu in Hammerfall.",
    ["D"] = "The light slowly fades, the sword Trol\'kalar, held within a stone sheath has been released.",
  },
  [647] = {
    ["T"] = "MacKreel\'s Moonshine",
    ["O"] = "Take MacKreel\'s Moonshine to Brewmeister Bilger in Southshore.",
    ["D"] = "Had quite a bit of the ol\' Moonshine last night!  Longbraid would have my head if he knew that I passed out during my watch.$b$bThe noggin hurts something fierce.  Felt as though a whole brigade of siege engines passed overhead, all that rumbling.$b$bOh my, look at the time! I promised Brewmeister Bilger in Southshore that I\'d repay my debt to him by sending some Moonshine.$b$bBut there are just 15 minutes left before I\'m overdue!  Take him this batch, would you please?  And hurry!",
  },
  [648] = {
    ["T"] = "Rescue OOX-17/TN!",
    ["O"] = "Escort OOX-17/TN to Steamwheedle Port, then report to Oglethorpe Obnoticus in Booty Bay.",
    ["D"] = "The gnome\'s voice crackles once again from the robot:$B$B\"I need to move OOX-17/TN to an open, safe place so it can begin a lengthy take-off procedure.  It has built-in cloaking, but I need time on my end to make it operational again.  I need you to escort the robot from its current location to, let\'s say, Steamwheedle Port!  That should be a perfect place, and long enough, to get things on line!\"$B$B\"Escort it safely to the port, and then come talk to me in Booty Bay!  Oglethorpe Obnoticus - out!\"",
  },
  [649] = {
    ["T"] = "Ripple Recovery",
    ["O"] = "Talk to Malton Droffers in Orgrimmar.",
    ["D"] = "I\'ve got some salvage work for you, and if you\'re an aficionado of fine ripple like I am, then you\'ll love this task.  Ripple!$B$BIt\'s a drink, you dummy.$B$BAn abandoned Horde outpost has a stash of Hinterlands Honey Ripple from when they were in business.  You can\'t get fine ripple like that anymore!  One of Malton\'s dummy associates knows the shot, so you\'ll go out there and bring me a case of packaged ripple, and two for my own collection!  Talk to Malton, he\'ll fill you in on the details.",
  },
  [650] = {
    ["T"] = "Ripple Recovery",
    ["O"] = "Talk to Gilveradin Sunchaser; his camp is located in the Hinterlands.",
    ["D"] = "The elf you will need to talk to is Gilveradin Sunchaser.  He can name any wine, vintage and all, just by smell alone!  He can package up the ripple we need, so you\'ll need to talk with him.  We need six bottles for packaging, two for dad, and how ever many he\'ll want... and believe me, he\'ll want some.  When you\'ve packaged the ripple and got dad\'s two bottles, come back here!$B$BGilveradin\'s camp is near some high elf lodge in the Hinterlands, and those high elves are not good.  Trust me.",
  },
  [651] = {
    ["T"] = "Stones of Binding",
    ["O"] = "Gather the Burning Key, the Cresting Key and the Thundering Key from the Stone of West Binding, the Stone of East Binding and the Stone of Outer Binding.$B$BBring them to the Stone of Inner Binding.",
    ["D"] = "Four Stones of Binding are scattered throughout Arathi.  These stones keep me pinned beneath the earth.$B$BTo unlock them you must unlock the strongest: the Stone of Inner Binding.  And to do that, you must gather keys from the other three.$B$BThese stones are watched by guardians, but if you are clever, or lucky, you may find ways to banish them.",
  },
  [652] = {
    ["T"] = "Breaking the Keystone",
    ["O"] = "Find and kill Fozruk.  Bring the Rod of Order to the Keystone in the Arathi Highlands.",
    ["D"] = "Opening the Stones of Binding loosened much of my confinement, but a lock still holds me chained.  It is the Keystone you see among this circle of stones.  And the key is held by one of my captors, the stone watcher Fozruk.$B$BYou must obtain that key, which the stone watchers call the Rod of Order, from Fozruk and use it to open the Keystone.$B$B$N, you have done so much to aid me.  Just a little more...and my gratitude will shake the very foundations of the earth!",
  },
  [653] = {
    ["T"] = "Myzrael\'s Allies",
    ["O"] = "Speak with Gerrig Bonegrip in the Forlorn Cavern of Ironforge.",
    ["D"] = "<Myzrael speaks.>$B$BFree!  I AM FREE!  I am free to gather strength, hidden from my captors.  For if they faced me now they would surely overpower and again imprison me.$B$BBut in time I will confront the giants, and they will regret their wardship of me!$B$BYou are a noble ally, $N.  I will need your help again in time.  When I am ready, I will need you to summon me to the surface.$B$BSpeak with Gerrig Bonegrip.  He is in Ironforge, in a shop of the Forlorn Cavern.  He knows how I can be summoned.",
  },
  [654] = {
    ["T"] = "Tanaris Field Sampling",
    ["O"] = "Acquire acceptable samples for 8 basilisks, 8 hyenas, and 8 scorpions.  Bring the testing kit back to Chief Engineer Bilgewhizzle in Gadgetzan before the power source runs out.",
    ["D"] = "Activating the power source makes the field testing kit start to glow and vibrate.  It would seem to be working as planned.$B$BAccording to Chief Engineer Bilgewhizzle, you now have two hours to collect the data you need in the Tanaris desert.  If you do not collect it within two hours, all the data will be lost and you will need to buy another power source from him.$B$BReturn to him when you have completed the task.",
  },
  [655] = {
    ["T"] = "Hammerfall",
    ["O"] = "Speak with Tor\'gan in Hammerfall.",
    ["D"] = "It is still clear to me, like yesterday... So many years without hope, with wood and steel to bind me to this terrible place. Then finally, finally! There was the Warchief. Doomhammer in his midnight plate, his warhammer held high.$b$bIt was... too terrible... That the blow of a coward would strike down our greatest hero. From behind he charged on his horse, and then his lance ran him through... I fell to my knees. What hope was there? What hope is there...",
  },
  [656] = {
    ["T"] = "Summoning the Princess",
    ["O"] = "Go to the Shards of Myzrael, summon her and defeat her.$B$BGather her Eldritch Shackles and bind them to the Shards of Myzrael.",
    ["D"] = "You must defeat Myzrael before she grows in strength.  If you do not, then once she has gathered enough power she will challenge her captors!  There are some who think they can control her, but if she can be controlled then why did the giants chain her in the first place??$B$BCrazy logic, eh?$B$BTo defeat her, summon her at the Shards of Myzrael where you first spoke with her.  Defeat her and gather her Eldritch Shackles, then bind them to the Shards of Myzrael.$B$BDo that, and she will again be trapped.",
  },
  [657] = {
    ["T"] = "Hints of a New Plague?",
    ["O"] = "Speak to Kinelory.",
    ["D"] = "<Quae begins to go through the folder.>$B$BThis IS interesting. Seems Apothecary Jorell has been working on some deadly experiments here at Go\'Shek. Not sure what that has to do with Hillsbrad, but Phin wouldn\'t like it if we let it continue.$B$BWe had a run-in with some orcs while you were gone, and I\'m not in good enough shape to help Kinelory. If you\'re up for it, she\'s going to need some protection when she goes down to the farm to steal whatever research Jorell still hasn\'t sent to Tarren Mill.",
  },
  [658] = {
    ["T"] = "Hints of a New Plague?",
    ["O"] = "Find the Forsaken Courier and bring back her Sealed Folder to Quae near the Go\'Shek Farm.",
    ["D"] = "So, Phin sent you to check on us? Not surprised--he worries too much.$B$BWe\'ve been watching the farm for days, and Kin claims she saw a courier come out of that small house to the south not long ago.$B$BShe talks a lot, but I can always trust her senses when we\'re in dangerous areas.$B$BYou wouldn\'t happen to be a skilled tracker, would you? We think the courier might be carrying notes to Tarren Mill. If we could get our hands on those documents, it might shed some light on what\'s happening in Hillsbrad.",
  },
  [659] = {
    ["T"] = "Hints of a New Plague?",
    ["O"] = "Find Quae and Kinelory near the Go\'Shek Farm in the Arathi Highlands.",
    ["D"] = "Hail, and well met, $N. I lead a group of adventurers investigating odd happenings in this area.$B$BMy cousin in Hillsbrad recently put down his dog Stanley because the animal mystically grew to an immense size and savagely attacked anyone who came near him.$B$BI believe the undead are involved, and we\'re looking for signs to prove as much.$B$BTwo of my companions, Quae and Kinelory, headed to the Go\'Shek Farm in the Arathi Highlands, and haven\'t checked in for days. Find them for me? I grow concerned.",
  },
  [660] = {
    ["T"] = "Hints of a New Plague?",
    ["O"] = "Protect Kinelory until she returns to Quae.",
    ["D"] = "All right, $N. I\'m ready if you are.$B$BShall we?",
  },
  [661] = {
    ["T"] = "Hints of a New Plague?",
    ["O"] = "Find Phin Odelic in Southshore.",
    ["D"] = "I\'ve seen Kinelory look way worse after a night in the tavern than she does now. You obviously did a good job protecting her.$B$BThese items she brought back from the farm should at least let Phin get an idea of what\'s going on out here in Arathi. Just to make sure there\'s no retaliation after your attack, we\'re going to stay here for a bit longer. Would you be willing to head back to Southshore and talk to Phin?",
  },
  [662] = {
    ["T"] = "Deep Sea Salvage",
    ["O"] = "First Mate Nilzlix wants you to retrieve the Charts and Logs from the Maiden\'s Folly and the Spirit of Silverpine.",
    ["D"] = "O\'Breen led the ships into the cove.  We were certain this is where the treasure could be found.  The Harbinger docked safely but as the Maiden\'s Folly and Spirit of Silverpine approached the tides began to lower.  Strangest thing I\'ve ever seen!$b$bThose two ships ran aground into something... some sort of temple in the reef.  They sank so quickly, was quite a tragedy.$b$bWe need to retrieve their Logs as well as their Charts before we leave.  We can\'t leave that information behind for prying eyes!",
  },
  [663] = {
    ["T"] = "Land Ho!",
    ["O"] = "Talk to Captain O\'Breen in Faldir\'s Cove.",
    ["D"] = "Lolo sees a tiny $r! Always on the lookout, Lolo is!$b$bWelcome to Faldir\'s Cove.  Captain O\'Breen said we\'d only be here for a few hours.  Just long enough to gather the treasure and get back to Booty Bay.$b$bBut Lolo thinks we\'re going to be here a lot longer than that.  We lost the other two ships from our formation.  Poor Spirit of Silverpine and Maiden\'s Folly.  On the bottom of the sea they rest now!$b$bLolo suggests you talk to Captain O\'Breen if you plan on sticking around here.",
  },
  [664] = {
    ["T"] = "Drowned Sorrows",
    ["O"] = "Captain Steelgut in Faldir\'s Cove wants you to kill 10 Daggerspine Raiders and 3 Daggerspine Sorceresses.",
    ["D"] = "Argh, \'tis a horrible life. I should be in a watery grave right now, not sittin\' here by the fire, breathin\' fresh air.$b$bThe crew tried so hard to save my beautiful Maiden\'s Folly. Bless the boys, bless their hearts.$b$bO\'Breen made it to shore safely but we weren\'t so lucky. The seas just sank on us, I swear.$b$bOne second we\'re afloat and the next water is rushin\' in. And those beasts! Killed all the survivors. Horrible naga scum. I went black and ended up here, saved.$b$bI want those beasts dead.",
  },
  [665] = {
    ["T"] = "Sunken Treasure",
    ["O"] = "Escort Professor Phizzlethorpe to the cave and back.",
    ["D"] = "Now that we are full-fledged Blackwater Raiders it is our job to help Mr. O\'Breen locate the lost elven treasure.$b$bIt is next to impossible to find the gems in the dark sea without aid. The doctor has constructed some goggles that will help. He needs the goggles charged with the energy derived from the enchanted stone in the cave just up the hill.$b$bBut the cave is cursed! When we get close, we get ambushed. Defend me and I can harness the energy from the stone into the goggles.",
  },
  [666] = {
    ["T"] = "Sunken Treasure",
    ["O"] = "Doctor Draxlegauge in Faldir\'s Cove wants you to collect 10 Elven Gems and return the Goggles of Gem Hunting once you are done.",
    ["D"] = "The treasure has been on the sea floor so long that the gems have calcified into thick stone. But the power harnessed in these goggles will allow you to locate them easily.$b$bA little gnomish ingenuity goes a long way!$b$bSo borrow the Goggles of Gem Hunting, $n, and see if you can collect some of the lost treasure for Captain O\'Breen.$b$bI\'d swim down there myself but...um...well, I have important scientific business to tend to up on the safe, dry land....er, yeah.",
  },
  [667] = {
    ["T"] = "Death From Below",
    ["O"] = "Protect Shakes O\'Breen during the attack.",
    ["D"] = "This is bad. It seems the Daggerspines are amassing for an attack.$b$bNaga are known for their ruthlessness in battle. They will keep attacking until I am dead.$b$bI don\'t value my own life above that of my crew members, but you must help to defend me. The crew will not be able to make it back to Booty Bay without my knowledge of the seas.$b$bThe naga will be coming from the sea. I need you to remain up here with me to fend off the attack. Man the cannon and drive them back.$b$bAre you ready, $n?",
  },
  [668] = {
    ["T"] = "Sunken Treasure",
    ["O"] = "Take the Elven Gems to Captain O\'Breen.",
    ["D"] = "Let\'s not leave Captain O\'Breen waiting. He\'ll want to see these gems first hand. After all, that\'s why we\'re here!$b$bAnd after a few weeks of consorting with these pirates, the professor and I have realized the last thing that\'s good for our health is to be caught hanging on to their treasure.$b$bHere, $n, take these to O\'Breen.",
  },
  [669] = {
    ["T"] = "Sunken Treasure",
    ["O"] = "Take the Sample Elven Gem to Fleet Master Seahorn in Booty Bay.",
    ["D"] = "Fleet Master Seahorn will want to hear about our find at once.$b$bBut as you can see, we\'re not in much of a position to get out of here. Not only is the tide too low, but those damned creatures we disrupted are keeping a close eye on our movements.$b$bYou can be of great service to the Blackwater Raiders if you can get word to Fleet Master Seahorn in Booty Bay that we discovered the treasure and are working on extracting more.  Take him this sample as proof.",
  },
  [670] = {
    ["T"] = "Sunken Treasure",
    ["O"] = "Deliver Seahorn\'s Letter to Shakes O\'Breen in Arathi Highlands.",
    ["D"] = "Say, $n, you seem to be an adventurous type. My fleet is stuck in Booty Bay until we can restock.$b$bThere are some heavy things brewing here and abroad and I need to get some top secret correspondence to Shakes O\'Breen who is tied up off the Arathi coast.$b$bCan I trust you to deliver this message safely and in confidence?",
  },
  [671] = {
    ["T"] = "Foul Magics",
    ["O"] = "Get 10 Bloodstone Amulets for Tor\'gan in Hammerfall.",
    ["D"] = "I have felt a strangeness in the air... a feeling that simmers in my blood. I had my suspicions. I set off to find the source of my unease and discovered to the west, at Northfold Manor, the residual traces of demonic summoning. As I got close, my blood began to boil and the rage within me grew.$b$bOn the ground I spied an amulet of bloodstone, used to assist in demonic summoning. The one I found was depleted, however. I\'ll need another to examine, but I dare not return to the manor. Can you bring me some?",
  },
  [672] = {
    ["T"] = "Raising Spirits",
    ["O"] = "Acquire 10 Highland Raptor Eyes from Highland Striders and Highland Thrashers for Tor\'gan in Hammerfall.",
    ["D"] = "We tried to settle him in, cheer him up, telling him stories of our mighty city at Orgrimmar in Kalimdor, but nothing would move him.$b$bTales of our young Warchief, Thrall, who bears the armor and hammer of Orgrim... but, perhaps... Perhaps! A demonstration of our newly found shamanistic magic would give him new hope. It is worth a try!$b$bA small object we could make for him, yes... Bring me some raptor eyes, and I will see what I can do.",
  },
  [673] = {
    ["T"] = "Foul Magics",
    ["O"] = "Retrieve Marez Cowl\'s Bloodstone Orb and bring it to Tor\'gan in Hammerfall.",
    ["D"] = "There is no doubt in my mind that a powerful warlock resides within the walls of Stromgarde. From time to time, using an arcane magical relic, this warlock has summoned forth terrible demons in large numbers. We must remove the source of his energy.$b$bFind the warlock and kill him. Bring me whatever magical object you find on his person, and I will take steps to destroy it and see that its power is not used to taint this land any longer.",
  },
  [674] = {
    ["T"] = "Raising Spirits",
    ["O"] = "Give the Raptor Talon Amulet to Gor\'mul.",
    ["D"] = "Here, take this amulet to Gor\'mul, and see if the raptors\' strength can awaken the flame of the warrior that once burned through his veins.",
  },
  [675] = {
    ["T"] = "Raising Spirits",
    ["O"] = "Speak with Tor\'gan.",
    ["D"] = "<Gor\'mul tosses the amulet into the ground.>$b$bTor\'gan sent you, didn\'t he? Pah! Why he feels some mercy towards me I would not understand. Mercy was denied me when I was not allowed to die with my Warchief in battle. A cruel blow fate has dealt me...",
  },
  [676] = {
    ["T"] = "The Hammer May Fall",
    ["O"] = "Kill 8 Boulderfist Ogres and 10 Boulderfist Enforcers, then find Drum Fel in the Hammerfall outpost in Arathi Highlands.",
    ["D"] = "Weaklings... all of them. You, $c, what are you looking at? Think you\'re something special, huh? Why not prove it?$B$BThe Hammerfall outpost in Arathi\'s under attack. Unless we send them help soon, their demise is imminent.$B$BBut before you go rushing off, you may want to test your skills first. Northeast of Stromgarde in Arathi is an ogre mound inhabited by the Boulderfist tribe. When you can handle the ogres and enforcers there, then report to Drum Fel in Hammerfall.",
  },
  [677] = {
    ["T"] = "Call to Arms",
    ["O"] = "Slay 10 Witherbark Axe Throwers, 10 Headhunters and 8 Witch Doctors, and return to Drum Fel in the Hammerfall outpost.",
    ["D"] = "The Witherbark trolls and Boulderfist ogres of Arathi are working together to rid us from this land, but we shall not meet our fates at their, or anyone else\'s, hand. We will slay them and show them that we, too, have a home here. $B$BThe trolls turned their backs to us; the ogres would use us for food and bedding if we allowed them to--these things shall not be.$B$BWe start our attack on the Witherbark trolls to the south of here. Slay them in droves, and only return when their camps run thick with blood.",
  },
  [678] = {
    ["T"] = "Call to Arms",
    ["O"] = "Kill 10 Boulderfist Brutes and 4 Boulderfist Magi, and return to Drum Fel in the Hammerfall outpost.",
    ["D"] = "Your strength is evident, $N. Now to see how you fare against a real enemy.$B$BThe Boulderfists have a lair to the southwest of here towards the dwarven bridge, and, if I\'m not mistaken, the maulers and magi there shall present to you a greater challenge--they have done so to us thus far.$B$BSlay as many as you can and come to me in body when you are successful, or come to me in spirit when you fail. I shall give praise to your tenacity in battle either way.",
  },
  [679] = {
    ["T"] = "Call to Arms",
    ["O"] = "Kill 7 Boulderfist Shaman and 3 Boulderfist Lords and return to Drum Fel in the Hammerfall outpost.",
    ["D"] = "The head of the beast is ready to be taken off... with great force, $N. You have shown every sign that you are able to help us with this final mighty blow. The leaders of these attacks have made their lair within the ruins of Stromgarde, a glaring symbol of human frailty.$B$BFind and slay those Boulderfist lords and their lackey shaman who would try and drive us from these lands.",
  },
  [680] = {
    ["T"] = "The Real Threat",
    ["O"] = "Slay Or\'Kalar and bring his Head to Korin Fel in the Hammerfall outpost.",
    ["D"] = "You serve my husband well, $c, but he does not know why Hammerfall is under attack.$B$BOr\'Kalar, Mug\'thol\'s pawn in Stromgarde, has an intense hatred for my husband, and wishes nothing more than my husband\'s death for defeating him years ago.$B$BMy auguries tell me that Or\'Kalar has laid a trap for my husband, so I have hidden signs of the ogre\'s presence from him.$B$BMy husband will rush off foolishly if he learns of the ogre\'s whereabouts and I would ask for your help in slaying the creature.",
  },
  [681] = {
    ["T"] = "Northfold Manor",
    ["O"] = "Kill 10 Syndicate Highwaymen and 6 Syndicate Mercenaries.$B$BReturn to Captain Nials at Refuge Pointe.",
    ["D"] = "We are fighting a war for Stromgarde, and we are losing.  The ogres, the Syndicate and we have divided our once great city, and each faction battles the other into a bloody stalemate.$B$BBut our forces dwindle while our enemy grows.$B$BWe need help from the town of Southshore, but supplies are difficult to receive while the Syndicate holds territory between us.$B$BGo to the Syndicate base in Northfold Manor and slay them.  Then, perhaps, pressure on our supply trains will lessen and more will get through.",
  },
  [682] = {
    ["T"] = "Stromgarde Badges",
    ["O"] = "Bring 7 Stromgarde Badges to Captain Nials at Refuge Pointe.",
    ["D"] = "The Syndicate in Stromgarde do battle with our contingent of militia there. In order to urge the rabble to fight our more disciplined troops, the Syndicate leaders have placed a bounty on our soldiers, awarding gold for each Stromgarde Badge gathered off killed militiamen.$B$BWe want those badges retrieved - we cannot allow the cutthroats to take trophies from our brave fallen!$B$BEnter the Syndicate-controlled area of Stromgarde and hunt them for our badges.",
  },
  [683] = {
    ["T"] = "Sara Balloo\'s Plea",
    ["O"] = "Take Sara Balloo\'s note to King Magni Bronzebeard of Ironforge.",
    ["D"] = "$n, I have but a small request to ask of you.$b$bTake this note I have prepared to our great monarch of Ironforge, King Magni Bronzebeard. \'Tis a small task to ask of one such as yourself, someone who has shown great valor to the cause of the Alliance thus far.$b$bFor me to go before the King in my grief-ridden state would not serve the purpose of my cause.  Surely you understand.",
  },
  [684] = {
    ["T"] = "Wanted!  Marez Cowl",
    ["O"] = "Bring Marez\'s head to Captain Nials at Refuge Pointe.",
    ["D"] = "A bounty has been placed on the head of Marez Cowl, a high-level advisor and operative of the Syndicate.  She was last seen within the Syndicate controlled territory of Stromgarde.$B$BBounty may be collected from Captain Nials.$B$BBe warned: Marez is a skilled and cunning Warlock, known for her trafficking with demons.  Extreme caution is advised when hunting her.",
  },
  [685] = {
    ["T"] = "Wanted!  Otto and Falconcrest",
    ["O"] = "Bring Otto\'s Head and Falconcrest\'s Head to Captain Nials at Refuge Pointe.",
    ["D"] = "The Stromgarde Militia has placed bounties on the heads of Lord Falconcrest, and his bodyguard Otto.  Falconcrest heads the Syndicate\'s efforts in the Arathi Highlands, and his death would cause a major disruption in those efforts.$B$BHis bodyguard Otto, although not a strategic target, is a fierce opponent and has killed dozens of our defenders.$B$BTheir bounties may be collected from Captain Nials.",
  },
  [686] = {
    ["T"] = "A King\'s Tribute",
    ["O"] = "King Magni Bronzebeard wants you to speak with Grand Mason Marblesten.",
    ["D"] = "I do not expect good dwarves like Mrs. Balloo to understand why the travesties of war must be endured for the greater good of our people. The loss of her husband, and the loss of every dwarf that fights in the name of the Alliance, weighs heavy on my soul.$b$bWhile I cannot bring her husband back from the Twisting Nether, I can pay tribute to him.$b$bI am going to commission a memorial to Sully Balloo to be built by Grand Mason Marblesten.$b$bSend word to him now, $c. You shall be custodian of my bidding.",
  },
  [687] = {
    ["T"] = "Theldurin the Lost",
    ["O"] = "Find Theldurin the Lost.",
    ["D"] = "Theldurin the Lost was once a member of our order, the Twilight\'s Hammer.  But he lost his mind and scorned our teachings.  And before he fled from us, he stole the scroll of Myzrael!  We could not catch him, but rumors say he hides in the Badlands, gibbering and mad.$B$BTo bring the Lady back to us, you must find the scroll.  And to do that, you must find Theldurin.",
  },
  [688] = {
    ["T"] = "Myzrael\'s Allies",
    ["O"] = "Speak with Zaruk in Hammerfall.",
    ["D"] = "<Myzrael speaks.>$B$BFree!  I AM FREE!  I am free to gather strength, hidden from my captors.  For if they faced me now they would surely overpower and again imprison me.$B$BBut in time I will confront the giants, and they will regret their wardship of me!$B$BYou are a noble ally, $N.  I will need your help again in time.  When I am ready, I will need you to summon me to the surface$B$BSpeak with Zaruk in Hammerfall.  He knows how I can be summoned.",
  },
  [689] = {
    ["T"] = "A King\'s Tribute",
    ["O"] = "Grand Mason Marblesten of Ironforge wants 5 pieces of Alterac Granite.",
    ["D"] = "So our good King would like a memorial built in honor of a soldier named Sully Balloo?  And you say the poor chap died during the Thandol Span tragedy?$b$bBah, will there ever be peace on Azeroth?$b$bNonetheless, any commission from the King is of utmost importance. To build a truly epic and lasting piece I want to work with one of the finest stones available. Travel to Darrow Hill in Hillsbrad and gather for me some Alterac Granite.",
  },
  [690] = {
    ["T"] = "Malin\'s Request",
    ["O"] = "Find Skuerto at Refuge Pointe in the Arathi Highlands.",
    ["D"] = "Greetings, $N. You have the look of an adventurer about you, and I have a task that needs some tending to.$B$BArchmage Trelane, my original master, fled Stromgarde in the Arathi Highlands not too long ago, and he\'s asked me to help him in recovering some of the items he left behind.$B$BThe city fell under siege, and he attempted to defend the Tower of Arathor, but to no avail. I have agents in the Highlands already, but they\'ve requested I send more aid. Are you interested?",
  },
  [691] = {
    ["T"] = "Worth Its Weight in Gold",
    ["O"] = "Find 10 Witherbark Tusks, 4 Witherbark Medicine Pouches and a Shadow Hunter Knife for Apprentice Kryten at Refuge Pointe.",
    ["D"] = "Archmage Malin sent Skuerto and I here to collect reagents for him. I was at a loss as to how I was going to collect the items, but now that you\'re here, I think my problem\'s solved.$B$BI need the following: Witherbark tusks, Witherbark medicine pouches and a shadow hunter knife.$B$BCould you find those for me? The Witherbark live to the southeast of Arathi.",
  },
  [692] = {
    ["T"] = "The Lost Fragments",
    ["O"] = "Bring the Torn Scroll Fragment, the Crumpled Scroll Fragment and the Singed Scroll Fragment to Theldurin the Lost.",
    ["D"] = "The Scroll of Myzrael...the Scroll of Myzrael?!  You don\'t want that!  Its magic can pull Myzrael to the surface world and if you do that, we are in trouble!$B$BOr...maybe it\'s too late.  Maybe she already is free.  Maybe she\'s just gathering strength before unleashing herself upon us.  If so, then you\'ll NEED the scroll!$B$B...too bad it\'s destroyed.  Enraged elementals stole it and tore it up and now fight over the fragments.$B$BBut if you can gather those fragments, maybe we can fix it...",
  },
  [693] = {
    ["T"] = "Wand over Fist",
    ["O"] = "Find Trelane\'s Wand of Invocation and return it to Skuerto at Refuge Pointe.",
    ["D"] = "Hey, $N. Sorry to be so short with ye earlier... and no, that ain\'t a pun.$B$BI figured out where Trelane\'s first item ended up: Kor\'gresh has it in Boulderfist Hall. An\' by the way he was throwing down blasts of ice at his kin to keep them in line, he must be close to figurin\' out how to use all of its powers.$B$BBoulderfist Hall\'s south of here an\' then east of Thandol Span. Find the ogre an\' get back that wand.$B$BWhile you\'re gone, I\'ll figure out what we\'re gonna do about Stromgarde.",
  },
  [694] = {
    ["T"] = "Trelane\'s Defenses",
    ["O"] = "Find an Azure Agate and bring it to Apprentice Kryten at Refuge Pointe.",
    ["D"] = "All rested?$B$BHere\'s our next step: there\'re ogres all around the Tower of Arathor in Stromgarde. We\'re gonna need to activate the tower\'s defenses if you\'re gonna stand a chance against \'em an\' still make it to the items alive. To do that, we\'re gonna need some gems the Boulderfist shaman have taken a liking to. They took \'em from the tower, but they\'re not gonna stay in their possession for long.$B$BGet one of them gems an\' bring \'em to Kryten--he knows the spell to enchant \'em so they\'ll work as a key.",
  },
  [695] = {
    ["T"] = "An Apprentice\'s Enchantment",
    ["O"] = "Speak to Skuerto.",
    ["D"] = "Here you are, $N. The gem only needs to be pressed against one of the pedestals near the base of the tower to activate its defenses. Use it when you\'re ready to enter the tower and find Trelane\'s remaining items. $B$BGood luck to you. I\'ll be awaiting your return here at the camp. Oh, and speak to Skuerto before you leave. He\'ll give you the rest of the details you\'ll need.",
  },
  [696] = {
    ["T"] = "Attack on the Tower",
    ["O"] = "Find Trelane\'s Phylactery, Trelane\'s Orb, and Trelane\'s Ember Agate, and return them to Skuerto at Refuge Pointe.",
    ["D"] = "Here\'s what your lookin\' for: Trelane\'s phylactery, Trelane\'s orb, an\' Trelane\'s ember agate.$B$BAll three should be in chests within the tower an\' you shouldn\'t have a problem opening the chests on your own; you just have to be careful of the ogres.$B$BThe tower\'s defenses won\'t take care of \'em on their own; you\'ll need to have your weapons at the ready. Those ogres find out you\'re there and stealing from \'em, an\' they\'ll be sure to put the hurt on you.$B$BBe careful, an\' good luck.",
  },
  [697] = {
    ["T"] = "Malin\'s Request",
    ["O"] = "Take the Sealed Letter to Archmage Malin in the Mage Quarter of Stormwind.",
    ["D"] = "Take this letter back to Stormwind and let Malin know that Kryten an\' I\'ll be home soon. He\'ll reward you well for helping us here, $N, of that I\'ve no doubt.$B$BTravel safely.",
  },
  [698] = {
    ["T"] = "Lack of Surplus",
    ["O"] = "Bring 8 Unprepared Sawtooth Flanks to Tok\'Kar in Swamp of Sorrows.",
    ["D"] = "I\'m working on getting some supplies together for our outpost near the beach to the east--Tok\'kar does what he can, but between shifts of guard duty and cooking for his soldiers there\'s not much time left in the day.$B$BThey do enjoy cooking up some sawtooth meat when they can, but I haven\'t had time to get them another shipment. Would you mind helping them out?$B$BThe young sawtooths aren\'t as tasty as the older ones, so stay clear of those; give them a year or two to get to full size.",
  },
  [699] = {
    ["T"] = "Lack of Surplus",
    ["O"] = "Bring 6 Sawtooth Snapper Claws to Tok\'Kar in the Swamp of Sorrows.",
    ["D"] = "With the threat of murlocs along the coast, we\'re stationed out here to protect--or warn if the force is too large--of any coming threats to Stonard. It doesn\'t give us much time for relaxation, and we could use some more help if you\'re able.$B$BThe sawtooth snappers\' claws make for fine weapons when properly prepared. Bring me some of their claws and I\'ll see you\'re rewarded with one of our weapons in thanks for the fresh meat and the good company.",
  },
  [700] = {
    ["T"] = "A King\'s Tribute",
    ["O"] = "Report to King Magni Bronzebeard.",
    ["D"] = "Ah, such fine stone comes from Lordaeron.  Such a shame the land is in such shambles.$b$bBut the Memorial came out quite well! I should think the King will be most pleased with it.$b$bNow if you will, please send word to His Majesty that the Memorial is complete as requested. ",
  },
  [701] = {
    ["T"] = "Guile of the Raptor",
    ["O"] = "Acquire 12 Raptor Hearts from Highland Fleshstalkers for Tor\'gan.",
    ["D"] = "With some additional objects, I will be able to create an item that will imbue Gor\'mul with the guile of the raptor! With the power of the raptor flowing through him, Gor\'mul will know our new power and rediscover the warrior that lies dormant!$b$bI will require raptor hearts to fashion the item and spell.",
  },
  [702] = {
    ["T"] = "Guile of the Raptor",
    ["O"] = "Give Tor\'gan\'s Orb to Gor\'mul.",
    ["D"] = "Take this orb and use its energy to give Gor\'mul the essence of the raptor. Surely this will invigorate his spirit and give him new purpose in life. With him returned to normal, we can explain to him the current state of the Horde, and we will have a great warrior\'s assistance in our unceasing battles.",
  },
  [703] = {
    ["T"] = "Barbecued Buzzard Wings",
    ["O"] = "Bring 4 Buzzard Wings to Rigglefuzz.",
    ["D"] = "Oh yes, my barbecued buzzard wings are a favorite!  The sauce is rich and the meat is tangy.  And the smell will bring the hungry running from miles away!$B$BI have everything I need to cook up a batch.  Everything...except the buzzard wings!$B$BIf you want some then get me the buzzard wings.  Bring me enough, and I might even share the recipe with you!",
  },
  [704] = {
    ["T"] = "Agmond\'s Fate",
    ["O"] = "Bring 4 Carved Stone Urns to Prospector Ironband in Loch Modan.",
    ["D"] = "The last report I received from Agmond is troubling, more so now that he\'s dead!  He mentioned that his diggers uncovered \"ancient urns\" at the dig site outside Uldaman.  I fear that the urns he found at that site incited the troggs, just as did the idols I found here.$B$BI need you to get those urns for our studies.  Go to the Uldaman site, gather the urns, then return to me.",
  },
  [705] = {
    ["T"] = "Pearl Diving",
    ["O"] = "Bring 9 Blue Pearls to Rigglefuzz in the Badlands.",
    ["D"] = "The Badlands is a harsh place, filled with vicious predators and bold scavengers.  Scary, especially for a short little goblin.  To survive, I have to be tricky!$B$BI know the recipe for flash bombs.  I use those to scare away wildlife.  But I\'m running low on one of the ingredients: crushed blue pearl powder.  Get me some and I\'ll make it worth your efforts.$B$BHeh, and I hope you have good boots on.  The Blue Pearls I need are found from clams at the Vile Reef.$B$BYep, the Vile Reef in Stranglethorn!",
  },
  [706] = {
    ["T"] = "Fiery Blaze Enchantments",
    ["O"] = "Acquire a Black Drake\'s Heart for Sigrun Ironhew.",
    ["D"] = "As an apprentice to Feranor Steeltoe, I learned his special technique for imbuing weapons with fiery enchantments. Last I heard of him, he was working at a smithy with a hunting party up in Lordaeron.$b$bI can\'t tell you too many of the details about the process, but I can tell you it requires blood from a rare, still-beating black drake whelp\'s heart. I don\'t have much to do waiting for word from Ironforge. If you bring me a heart from the whelps in Lethlor Ravine, I\'ll show you my skill!",
  },
  [707] = {
    ["T"] = "Ironband Wants You!",
    ["O"] = "Speak with Prospector Ironband at Ironband\'s Excavation Site in Loch Modan.",
    ["D"] = "$N.  I received reports from Prospector Ironband at his excavation site in Loch Modan--he needs some legwork done concerning another of our excavations.$B$BI don\'t have more details than that, but if you want to stretch your legs then go speak with Ironband.",
  },
  [708] = {
    ["T"] = "The Black Box",
    ["O"] = "Take the Corroded Black Box to the Hall of Arms in Ironforge.",
    ["D"] = "The surface of this metallic black box is heavily pitted, as if it had been immersed in acid - it must have been sitting in the giant condor\'s stomach for a long time.$B$BTurning the box over, a symbol is found stamped on its bottom.  Below the symbol are the words:$B$B\"Engine #19 - Ironforge Siege Brigade\"",
  },
  [709] = {
    ["T"] = "Solution to Doom",
    ["O"] = "Bring the Tablet of Ryun\'eh to Theldurin the Lost.",
    ["D"] = "Oh my!  Oh my!  There is a doom approaching.  Mark me!  A doom!$B$BNo, I cannot speak of it...cannot utter their names or they may hear me!  But if we learn, yes, if we learn more of them...then maybe we can learn how to foil them!$B$BMaybe.$B$BGo into Uldaman.  No!  I said it!  You must find...find the tablet of...Ryun\'eh.  Yes, find it!  And don\'t make me speak that name again!$B$BIt is deep in that place, in an ancient chest.  That\'s where it hides...in a chest crafted before the works of any dwarf.",
  },
  [710] = {
    ["T"] = "Study of the Elements: Rock",
    ["O"] = "Find 10 Small Stone Shards and bring them to Lotwil Veriatus in the Badlands.",
    ["D"] = "Here in the Badlands I\'ve stopped to work on my latest project: my shackles of elemental binding.$B$BWhen complete, they will allow mages to summon even more powerful elementals for use in even greater capacities. To start things off, I need small stone shards to test some of my newest offensive spells along with a few gizmos I\'ve pieced together.$B$BThe shards are easily collected from the lesser rock elementals to the west of here. Could you bring me some?",
  },
  [711] = {
    ["T"] = "Study of the Elements: Rock",
    ["O"] = "Bring 3 Large Stone Slabs to Lotwil Veriatus in the Badlands.",
    ["D"] = "$N, you\'ve already proven more able than all my apprentices combined.$B$BThe next parts I need require a little more strength. Large stone slabs can be found on rock elementals if you\'re careful enough. Their bodies are stronger and larger, and therefore more prone to having pieces large enough for my tests. You can find them further west of here or southeast along the mountains\' edges.$B$BTake your time; I\'ll need a while to test these smaller pieces you brought me.",
  },
  [712] = {
    ["T"] = "Study of the Elements: Rock",
    ["O"] = "Bring 5 Bracers of Rock Binding to Lotwil Veriatus in the Badlands.",
    ["D"] = "$N, there\'s one more part I need from the elementals here in the Badlands: the bracers of rock binding from the greater rock elementals. By my calculations, those bracers should be enough to gauge the strength of my own shackles.$B$BMy attempts at scrying the greater rock elementals showed a group of them southwest of here, near some ogres if I recall.$B$BBring me 5 of their bracers just so I have extras. Servo\'s notorious for misplacing my reagents and research.",
  },
  [713] = {
    ["T"] = "Coolant Heads Prevail",
    ["O"] = "Find Frost Oil and bring it to Lotwil Veriatus in Badlands.",
    ["D"] = "Let me tell you something about Frost Oil: nothing can keep an engine or gears from overheating quite like it.$B$BIts cooling properties are extraordinary, and its longevity as a lubricant without equal. That\'s not all: it also aids in retaining enchantments on magical items.$B$BI need a vial of it to increase the magical capacity of my shackles and make them powerful enough to stop our summoned friend from killing us all.$B$BBro\'kin, a goblin friend of mine, can teach you to make it. He\'s in Alterac.",
  },
  [714] = {
    ["T"] = "Gyro... What?",
    ["O"] = "Bring a Gyrochronatom to Lotwil Veriatus in the Badlands.",
    ["D"] = "$N, you\'ve done your job well beyond all standards.$B$B<Lotwil\'s obviously distracted by his work.>$B$BIf we ever meet in Kharanos, feel free to come apply for work.$B$BWhoops! Quick, grab the... yes, that. Thanks.$B$BNow I just need one more thing. It\'s a small part, but rather crucial, I\'m afraid: a gyrochronatom.$B$BIf you could just go grab one and bring it to me, I should be ready to complete this.",
  },
  [715] = {
    ["T"] = "Liquid Stone",
    ["O"] = "Bring a Healing Potion and a Lesser Invisibility Potion to Lucien Tosselwrench in the Badlands.",
    ["D"] = "I\'m thinking those items you acquired for Lotwil are going to be the least of our worries, $N. On the other hand, you\'ve proven more than adept at the sciences Lotwil holds sacred, so how about helping me out?$B$BI need an invisibility potion and a healing potion; both might come in handy real soon if he pulls this off.$B$BIn return, I\'ll give you this recipe for a potion I\'ve been developing while learning from Lotwil. It\'s based on some of the research he\'s been doing here in the Badlands.",
  },
  [716] = {
    ["T"] = "Stone Is Better than Cloth",
    ["O"] = "Bring some Patterned Bronze Bracers to Lucien Tosselwrench in the Badlands.",
    ["D"] = "I know how cheap Lotwil can be, $N, so let me make you something for all your troubles. Maybe it\'ll even help protect you from the coming atrocity that is Lotwil\'s newest invention.$B$BI\'ve been working with all the extra stones you collected for him, and I can make some nice bracers that you might want to use. They\'re enchanted, of course.$B$BI\'ll just need patterned bronze bracers crafted by a blacksmith so I can complete the bracers I\'m making for you.",
  },
  [717] = {
    ["T"] = "Tremors of the Earth",
    ["O"] = "Use the Sign of the Earth to activate the Pillars of Amethyst, Opal, and Diamond and obtain the Runestones.$b$bPlace the runestones in the Seal of the Earth to free Blacklash and Hematus.$b$bSlay them and return Blacklash\'s Bindings, the Chains of Hematus, and the Sign of the Earth to Garek.",
    ["D"] = "It has become obvious that we cannot ensure that Blacklash and Hematus will remain imprisoned in Lethlor. That black dragons have taken up residence there, and that the ogres managed to steal the Sign indicates that more drastic measures will have to be taken.$b$bThe drakes\' prison is maintained by three runestones that may only be retrieved using the Sign of the Earth. Those runestones can then be used to break the seal which imprisons them.$b$b$n... assure that their dreams are eternal.",
  },
  [718] = {
    ["T"] = "Mirages",
    ["O"] = "Retrieve the Supply Crate for Sigrun Ironhew.",
    ["D"] = "In our hurry to leave the Uldaman excavation site, we were forced to abandon many wagons and crates of supplies along the way. The local ogres from Camp Kosh have been on our abandoned equipment like vultures on a rotting corpse. Don\'t smell much better, either. Than the corpses, I mean.$b$bAnyways, they took the cart that was carrying all our weapons and such... so--well, this is a bit embarrassing--we haven\'t been able to get any food.$b$bWe\'re in a bit of a pickle here, suppose you could help us out?",
  },
  [719] = {
    ["T"] = "A Dwarf and His Tools",
    ["O"] = "Find Ryedol\'s Lucky Pick and return it to Prospector Ryedol south of Hammertoe\'s Dig Site.",
    ["D"] = "Hey, $N. Be careful \'round here. There be Dark Iron dwarves all around--looked like the Shadowforge clan.$B$BThey just attacked the excavation site I was workin\' at--killed nearly everyone there, including my boss Hammertoe. I barely escaped with me life.$B$BThe site\'s just to the north of here, and I\'m tryin\' to plan a way to get some of our supplies back, especially me lucky pick.$B$BThink you\'d be up to helpin\' me out some? One of those damned Dark Iron must have my pick! Could be anyone of them.",
  },
  [720] = {
    ["T"] = "A Sign of Hope",
    ["O"] = "Find Prospector Ryedol and let him know Hammertoe Grez is alive.",
    ["D"] = "<There is text roughly scribbled in chalk on the map.>$B$B\"Ryedol, $BThey\'re taking me to Uldaman. Send help.$B-Hammertoe\"",
  },
  [721] = {
    ["T"] = "A Sign of Hope",
    ["O"] = "Find Hammertoe Grez in Uldaman.",
    ["D"] = "$N, can ye find him? Can you find Hammertoe in Uldaman?$B$BIf those dirty Shadowforge are holdin\' him hostage to figure out what he knows about the titan excavations, then all of Ironforge could be in huge trouble.$B$BFor a long time now those Dark Irons have been lookin\' for magics to make more powerful golems. Hammertoe was doin\' the same, of course, but only to stop them.$B$BThey\'re probably keepin\' him alive until they learn what he knows.Ye\'ve got to hurry if you\'re gonna save him!",
  },
  [722] = {
    ["T"] = "Amulet of Secrets",
    ["O"] = "Find Hammertoe\'s Amulet and return it to him in Uldaman.",
    ["D"] = "Ye\'re brave... <cough>... to come into Uldaman fer me, $c.$B$BIf ye\'re really here to help... ol\' Hammertoe, then... <cough>... you\'ll need to find... me amulet.$B$BDeepshadow\'s got it... Magregan Deepshadow. He\'s \'round here... in the ruins fer sure. That amulet\'ll lead \'em... <cough>...  right to the scrolls if he ain\'t stopped. It\'s up to you, $N. Please... get that amulet.$B$BThe scrolls will surely help the Dark Irons create even more powerful golems. I just know it. I can feel it in me bones.",
  },
  [723] = {
    ["T"] = "Prospect of Faith",
    ["O"] = "Take Hammertoe\'s Amulet to Prospector Ryedol in the Badlands.",
    ["D"] = "<Hammertoe holds his wounds and takes a moment to regain his composure before speaking again.>$B$BFind Ryedol, $N. Bring \'im me amulet. <cough cough> He\'ll... <cough> figure out what to do. He knows I was seeking the origins of our people, and he... <cough>... knows it was also my mission to stop the Shadowforge clan. They must not be allowed to find the scrolls... <cough>",
  },
  [724] = {
    ["T"] = "Prospect of Faith",
    ["O"] = "Take Hammertoe\'s Amulet to Historian Karnik in Ironforge.",
    ["D"] = "Okay, okay... who would know what to do?$B$BHistorian Karnik! That\'s it!$B$BHe\'s in Ironforge. He\'ll be able to help. He and Hammertoe were workin\' together to find the lost artifacts in Uldaman.$B$BGuess the Shadowforge found out the two of them knew more than they were lettin\' on.$B$BTell Karnik what\'s happened to Hammertoe and give him the amulet--he\'ll tell you what needs to be done next.",
  },
  [725] = {
    ["T"] = "Passing Word of a Threat",
    ["O"] = "Find Advisor Belgrum and give him his note.",
    ["D"] = "$N, run and find Advisor Belgrum as quickly as you can. Tell him what you\'ve witnessed and ask him how he wants us to proceed. This note will explain more to him.$B$BIn the meantime, I will take the time to speak to Hammertoe\'s spirit. Maybe he knows something that will help us put a stop to those Shadowforge cretins\' activities in the Badlands. We can only pray.",
  },
  [726] = {
    ["T"] = "Passing Word of a Threat",
    ["O"] = "Speak to Historian Karnik.",
    ["D"] = "<Advisor Belgrum reaches the end of the note, and his eyes widen for a moment.>$B$BOch, these are evil tidings.$B$B$N, please return to Historian Karnik and tell him on the king\'s authority to act on this information swiftly and decisively. He must halt the Shadowforge\'s activities in the Badlands.",
  },
  [727] = {
    ["T"] = "To Ironforge for Yagyin\'s Digest",
    ["O"] = "Give the Sigil of the Hammer to Gerrig Bonegrip in the Forlorn Cavern of Ironforge.",
    ["D"] = "The Tablet tells much.  But not enough!  Not nearly enough!$B$BThere is a book, a digest that Yagyin wrote in ages past.  The ... people I fled ... they keep this book in many places.  And their most learned study it.  It holds vast knowledge.  Knowledge to protect us!$B$BGo to Gerrig Bonegrip.  He is in a shop in Ironforge, in the Forlorn Cavern.  Show him this sigil and he\'ll think you one of them.  Ask him for Yagyin\'s Digest ... and bring it to me!",
  },
  [728] = {
    ["T"] = "To the Undercity for Yagyin\'s Digest",
    ["O"] = "Bring the Sigil of the Hammer to Keeper Bel\'dugur in the Undercity.",
    ["D"] = "The tablet tells much.  But not enough!  Not nearly enough!$B$BThere is a book, a digest that Yagyin wrote in ages past.  The... people I fled from... they keep this book in many places.  And their most learned study it.  It holds vast knowledge.  Knowledge to protect us!$B$BGo to Keeper Bel\'dugur in the Undercity of Lordaeron.  Show him this sigil and he\'ll think you one of them.  Ask him for Yagin\'s Digest...and bring it to me!",
  },
  [729] = {
    ["T"] = "The Absent Minded Prospector",
    ["O"] = "Travel south and check on Prospector Remtravel.",
    ["D"] = "What an honor it was when Master Greywhisker assigned me to work under the guidance of the great Prospector Remtravel. At the academy in Ironforge everyone knew of Remtravel\'s great discoveries.$b$bBut the prospector is rather... um... oblivious ...to his surroundings.$b$bWe had uncovered evidence of a great society. Horrible golems sprang forth from the ground and overran the site. Remtravel never seemed to notice. I ran back to Auberdine for help.$b$bPlease travel south and see if the prospector is okay!",
  },
  [730] = {
    ["T"] = "Trouble In Darkshore?",
    ["O"] = "Travel to Auberdine and look for signs of the dwarven excavation team.",
    ["D"] = "How nice it is to see a $c interested in the great archeological wonders of our world.$b$bOftentimes our work is dismissed as mere hobby by our friends in the Alliance. But what many fail to realize is that recent discoveries in Khaz Modan have proven that a great and powerful force threatens all of Azeroth, from Lordaeron to Kalimdor.$b$bI am most worried about my crew that I sent to Darkshore. They haven\'t sent word in weeks.$b$bTravel to Auberdine and look for a clue as to their whereabouts.",
  },
  [731] = {
    ["T"] = "The Absent Minded Prospector",
    ["O"] = "Protect Prospector Remtravel as he searches for the mysterious fossil, then return to Archaeologist Hollee in Auberdine.",
    ["D"] = "So then, Hollee... oh that\'s right, you are not Hollee. Where\'s Hollee?$b$bBah, that\'s beside the point. We must find that stone brush... I mean the mysterious fossil! We must find that fossil. The Explorers\' League in Darnassus will want some indication as to what I\'ve been doing.$b$bLet me know when you are ready and we will hunt down that brush.... I mean fossil! We shall find something in writing to satisfy the League.$b$bSo are you ready yet?",
  },
  [732] = {
    ["T"] = "Tremors of the Earth",
    ["O"] = "Retrieve the Sign of the Earth for Garek.",
    ["D"] = "I heard from Sigrun that you have been a great help to him. Perhaps I might enlist your help. I was sent here by my master, Krasus of the Kirin Tor, to put an end to the trouble brewing in Lethlor Ravine.$b$bI cannot divulge the exact nature of my assignment here or the tasks that I must undertake, but be assured it is of vital importance.$b$bA band of wandering ogres, sometimes seen near Camp Boff, stole an object called the Sign of the Earth; I must have it. Bring it to me, and I will tell you more.",
  },
  [733] = {
    ["T"] = "Scrounging",
    ["O"] = "Get 7 pieces of Scrap Metal for Sigrun.",
    ["D"] = "It\'s going to be tough going here if I can\'t manage to scrounge up some materials to construct some defenses and other necessities. I\'ve noticed that the ogres in the area seem to be well equipped with scrap metal. Maybe the stories of siege engines in the Badlands weren\'t so far off after all...$b$bAnyways, the Prospector\'s been getting worried about the state of our little camp, so I\'d like to get working soon if I can.$b$bThe main ogre camp is at Dustbelch Grotto, a good trek to the west of here.",
  },
  [734] = {
    ["T"] = "This Is Going to Be Hard",
    ["O"] = "Speak to Lucien Tosselwrench in the Badlands.",
    ["D"] = "Wonderful, wonderful.$B$B<Lotwil looks up at you for a moment before going back to his shackles.>$B$BGo speak to Lucien, $N. I need a cog #5. Bring it to me, will you?",
  },
  [735] = {
    ["T"] = "The Star, the Hand and the Heart",
    ["O"] = "Return the items on the Bonegrip\'s Note to Gerrig Bonegrip in Ironforge.",
    ["D"] = "Yagyin\'s Digest, eh?  Are you starting a new chapter of us, somewhere?  Never mind, it is not my place to ask.$B$BI will help you, for I have an extra copy.  We must aid each other, yes?  That is how we flourish.$B$BI require three items of power.  Bring them to me and I will give you the digest.$B$BI will not speak their names.  Ears may be on us.  But here, I will write them down.",
  },
  [736] = {
    ["T"] = "The Star, the Hand and the Heart",
    ["O"] = "Bring the items on Bel\'dugur\'s Note to Keeper Bel\'dugur in the Undercity.",
    ["D"] = "You need Yagyin\'s Digest?  Your plan, then, is to gather another flock?  Of course it is...and of course I will help you, $gbrother:sister;.$B$BBut you must help me too.$B$BI require three items of power.  Bring me these and the digest will be yours.$B$BI cannot utter the names of these items, but I will write them down.$B$BGet me the items on this list.  If you can do that, then you are truly worthy of opening your own chapter of our order.",
  },
  [737] = {
    ["T"] = "Forbidden Knowledge",
    ["O"] = "Bring Yagyin\'s Digest to Theldurin.",
    ["D"] = "I have the digest for you, $N.  I need not tell you to take great care with it.  And to keep it far from unfriendly hands.  $B$BThis book holds many truths...$B$B...And the truth can burn.",
  },
  [738] = {
    ["T"] = "Find Agmond",
    ["O"] = "Find Agmond.",
    ["D"] = "Prospector Agmond was in charge of our excavation sites in the Badlands, to the southwest.  I say \"was\" because...well, I haven\'t heard from him in weeks.  I need you to find Agmond so I can get his report.$B$BLast I heard Agmond was at the southernmost dig site in the Badlands--a tiny operation past the Angor Fortress.  I\'d start your search for him there.",
  },
  [739] = {
    ["T"] = "Murdaloc",
    ["O"] = "Slay Agmond\'s killer, Murdaloc.$B$BSlay 12 Stonevault Bonesnappers.$B$BReport to Prospector Ironband in Loch Modan.",
    ["D"] = "Agmond\'s tracks lead from the west.  It is easy to tell from the bloody smears across rock and sand that the dwarf crawled some distance.$B$BAnd here he died.$B$BUpon close inspection, the word \"Murdaloc\" is roughly scrawled in a patch of Badlands sandstone.$B$BScrawled next to Agmond\'s dead hand.",
  },
  [741] = {
    ["T"] = "The Absent Minded Prospector",
    ["O"] = "Take the mysterious fossil to Chief Archaeologist Greywhisker in Darnassus.",
    ["D"] = "So the prospector wants to send the mysterious fossil to the Explorers\' League in Darnassus? I don\'t feel right leaving Darkshore without him.$b$bHere, $n, deliver the mysterious fossil to Chief Archaeologist Greywhisker in Darnassus.",
  },
  [742] = {
    ["T"] = "The Ashenvale Hunt",
    ["O"] = "Speak with Senani Thunderheart at Splintertree Post, Ashenvale.",
    ["D"] = "Attention, young adventurers!  The wilds of Ashenvale await you!$B$BThe Horde has established a strong presence in the lands north of the Barrens.  Our two outposts - Splintertree Post and the Zoram Strand Outpost - strive to bring glory to the Horde!  Those who would prove themselves should seek out guidance there.  Of note: Senani Thunderheart in Splintertree Post, directly north of the Barrens, seeks adventurers willing to take part in a great hunt of Ashenvale!",
  },
  [743] = {
    ["T"] = "Dangers of the Windfury",
    ["O"] = "Bring 8 Windfury Talons to Ruul Eagletalon in Bloodhoof Village.",
    ["D"] = "Thank you for taking the time to speak to me, $N. I am Ruul, warrior and teacher.$B$BYou have obviously reached an age when you must prepare for your trials if you are to adventure much further out of Mulgore.$B$BIf you wish a test of your strength, start by seeking out the Windfury harpies to the southeast. They nestle along the mountain\'s edges away from the road.$B$BThey are one of our natural enemies here in Mulgore and will be a good gauge of your skill.",
  },
  [744] = {
    ["T"] = "Preparation for Ceremony",
    ["O"] = "Collect 6 Azure Feathers and 6 Bronze Feathers, and bring them to Eyahn Eagletalon in Thunder Bluff.",
    ["D"] = "$N, my brother soon stands before Chief Bloodhoof, and it is my honor to make his headdress for him.$B$BI want to ask a favor of you while I finish tanning these leather straps. There is not time for me to find enough feathers, and I was wondering, would you gather more for me?$B$BYou can find feathers of the correct size from the harpies residing far to the north and northwest of Thunder Bluff. I need only 6 azure and 6 bronze feathers to complete the design.",
  },
  [745] = {
    ["T"] = "Sharing the Land",
    ["O"] = "Kill 10 Palemane Tanners, 8 Palemane Skinners, and 5 Palemane Poachers, then return to Baine Bloodhoof in Bloodhoof Village.",
    ["D"] = "$N, there are many conflicts that wound this land. It is my hope that you will not bear witness to as many of them as I have. Yet there is one that I would ask you to seek out. It will give you some sense of how terrible, if left unchecked, even a small threat can be to the land.$B$BPalemane gnolls have settled to the south of Bloodhoof and in a cave to the west. They disrespect our attempts to communicate with them, and they slay the wildlife of Mulgore unbridled.$B$BWords are no longer the answer.",
  },
  [746] = {
    ["T"] = "Dwarven Digging",
    ["O"] = "Collect 5 Prospector\'s Picks.$B$BAt a forge, smash the Prospector\'s Picks to create Broken Tools.$B$BBring 5 Broken Tools to Baine Bloodhoof in Bloodhoof Village.",
    ["D"] = "There are dwarves preparing a dig site to the northwest.  They think secrets lie in the earth, and that is true, but hollowing and defiling the land is no way to earn its teachings.$B$BCollect the dwarven tools of digging, smash them, and return to me with the dwarves\' broken tools.$B$BYou can smash the tools at a forge.  You will find one in Thunder Bluff, but any forge -- even a forge at the dwarven camp -- will suffice.",
  },
  [747] = {
    ["T"] = "The Hunt Begins",
    ["O"] = "Grull Hawkwind in Camp Narache wants you to bring him 7 Plainstrider Feathers and 7 pieces of Plainstrider Meat.",
    ["D"] = "You have a promising air about you, and will prove yourself to the tribe. Perhaps someday soon you will be welcomed into the great city of Thunder Bluff. But before that can happen you must prove yourself to my father, Chief Hawkwind.$b$bUp here on Red Cloud Mesa we pride ourselves on keen hunting skills. Tauren hunt out of necessity and for sport. Our supply of meats has run low and we require feathers for clothing. Hunt the nearby plainstriders and prove yourself by resupplying the village.",
  },
  [748] = {
    ["T"] = "Poison Water",
    ["O"] = "Bring 6 Prairie Wolf Paws and 4 Plainstrider Talons to Mull Thunderhorn in Bloodhoof.",
    ["D"] = "Goblins and their servants have tainted our sacred water wells!  We cannot allow this.$B$BTo cleanse each well, I must create a cleansing totem, then you must bring the totem to the well and perform a cleansing ritual.$B$BFirst we will create a cleansing totem for the Winterhoof Water Well.  To do this, the land must offer its aid. Hunt prairie wolves for their paws and adult plainstriders for their talons.  They are to the southwest.$B$BReturn to me with the paws and talons, $N.",
  },
  [749] = {
    ["T"] = "The Ravaged Caravan",
    ["O"] = "Morin Cloudstalker wants you to examine the contents of a supply crate at the Ravaged Caravan.",
    ["D"] = "A few days ago, we found a Venture Co. caravan traveling under guard on the northern shore of Stonebull Lake. We\'d lost two of our number in an earlier battle, so we were quick to attack and lay waste to the caravan.$b$bWe left their supply crates, thinking that our torches would destroy anything of value, but given that Venture Co. salvagers have been spotted near the burned wagons, maybe we were wrong.$b$bPerhaps you could go to the caravan and examine the contents of the crates.",
  },
  [750] = {
    ["T"] = "The Hunt Continues",
    ["O"] = "Grull Hawkwind in Camp Narache wants you to bring him 10 Mountain Cougar Pelts.",
    ["D"] = "A tauren skilled in the ways of the hunt knows that his prey is not for mere trophy. The beasts of the plains provide us with a means of survival. You will make quite an impression on the elders if you can bring back some highly valued Mountain Cougar Pelts. You can find the beasts lurking in the hills to the south.$b$bOur children need clothing and our tents need mending.",
  },
  [751] = {
    ["T"] = "The Ravaged Caravan",
    ["O"] = "Return the Venture Co. Documents to Morin Cloudstalker near Bloodhoof Village.",
    ["D"] = "Prying open the lid reveals a tightly packed assortment of ore and minerals, more evidence of the Venture Co.\'s mining operation in Mulgore. On the top of the ore and minerals you remove a tightly bound sheaf of papers.$b$bUp in the foothills to the east, you can barely hear the sounds of work, and see smoke rising from a large fire.",
  },
  [752] = {
    ["T"] = "A Humble Task",
    ["O"] = "Chief Hawkwind wants you to search for his mother near the water well to the southeast of Camp Narache.",
    ["D"] = "All members of the tribe share in the harmony of life. We live together and work together.$b$bOur commitment to one another carries with it a high degree of responsibility. I ask of you now a humble task.$b$bMy mother set out this morning to fetch water from the well to the southeast of Narache. It has been quite some time now. Perhaps you could check on her for me while I attend to tribal matters here?",
  },
  [753] = {
    ["T"] = "A Humble Task",
    ["O"] = "Take a Water Pitcher from the water well.$b$bReturn the pitcher to Chief Hawkwind in Camp Narache which is northwest from the water well.",
    ["D"] = "I have traveled many paths through life and these old legs lack the vigor they once had. I can still perform my duties to the tribe. Sometimes it just takes an old woman a little longer to do the task.$b$bBut you look like an eager $c. Let\'s put some of that youthful vitality to the test. Take a water pitcher from the well and bring it to my son, the Chief, in Camp Narache.$b$bRemember that even the most humble task can gain the recognition of elders.",
  },
  [754] = {
    ["T"] = "Winterhoof Cleansing",
    ["O"] = "Use the Winterhoof Cleansing Totem at the Winterhoof Water Well, then return to Mull Thunderhorn.",
    ["D"] = "I have crafted the totem to cleanse the Winterhoof Water Well.  Now, you must take it to the well and perform a cleansing ritual.  You will find it to the southeast, guarded by foul goblins!$B$BThis will not be an easy task, but you must do it if we are ever to use its waters again.$B$BFortune to you, $N.",
  },
  [755] = {
    ["T"] = "Rites of the Earthmother",
    ["O"] = "Travel to Seer Graytongue who lives in the hills directly south of Camp Narache.",
    ["D"] = "Your willingness to perform a humble task for the tauren of Narache and your eagerness to learn are noble traits, $n. I believe one day you will be heralded in Thunder Bluff as a $c of greatness.$b$bBefore that you must embark on the Rites of the Earthmother, of which there are three.$b$bThe first test is the Rite of Strength. Travel to Seer Graytongue and tell him Chief Hawkwind has sent you.$b$bYou will find the seer\'s abode directly to the south of Camp Narache, tucked away in the hills.",
  },
  [756] = {
    ["T"] = "Thunderhorn Totem",
    ["O"] = "Bring 6 Stalker Claws and 6 Cougar Claws to Mull Thunderhorn.",
    ["D"] = "To create the Thunderhorn Cleansing Totem, you must gather the claws of the predators of the Mulgore plains.  Hunt prairie stalkers for their stalker claws and flatland cougars for their cougar claws, then return to me.$B$BYou will find the prairie stalkers and flatland cougars to the east and west.",
  },
  [757] = {
    ["T"] = "Rite of Strength",
    ["O"] = "Kill Bristlebacks in Brambleblade Ravine and bring 12 Bristleback Belts to Chief Hawkwind in Camp Narache.",
    ["D"] = "The Rites of the Earthmother are the steps a young tauren needs to take to gain respect in Thunder Bluff.$b$bFirst you must pass the Rite of Strength. In this test you must prove your bravery by slaying enemies of the tribe.$b$bBristlebacks of Brambleblade Ravine to the east are encroaching on our tribal lands. They ambush our hunting parties and steal from the village by dark.$b$bShow your valor by slaying these villains and return to the Chief in Camp Narache with their belts as proof of your deeds.",
  },
  [758] = {
    ["T"] = "Thunderhorn Cleansing",
    ["O"] = "Use the Thunderhorn Cleansing Totem at the Thunderhorn Water Well, then return to Mull.",
    ["D"] = "The totem is ready, $N.  And again, it is your task to perform the ritual of cleansing.$B$BGo to the Thunderhorn Water Well, north of the village, and use the Thunderhorn Cleansing Totem in your ritual.  After you do this, return to me.$B$BMay the spirits guide you.",
  },
  [759] = {
    ["T"] = "Wildmane Totem",
    ["O"] = "Bring 8 Prairie Alpha Teeth to Mull Thunderhorn in Bloodhoof Village.",
    ["D"] = "There is but one water well left to cleanse, named after the Wildmane clan.  For this well to be pure, the land must offer the teeth of a fierce predator, the prairie wolf alpha.  Find the alphas to the north, then return to me when your hunt is finished.$B$BAlphas often wander around the base of our mighty city, Thunder Bluff.",
  },
  [760] = {
    ["T"] = "Wildmane Cleansing",
    ["O"] = "Use the Wildmane Cleansing Totem at the Wildmane Water Well, then return to Mull Thunderhorn.",
    ["D"] = "Your cleansing of the Winterhoof and Thunderhorn wells is spoken among the spirits, young $N.  The Wildmane Totem is made, and your last task lies ahead.$B$BThe Wildmane Water Well lies north of Thunder Bluff.  Perform the ritual!  Heal the land of the goblins\' poisons!  Let clean water flow once again!",
  },
  [761] = {
    ["T"] = "Swoop Hunting",
    ["O"] = "Bring 8 Trophy Swoop Quills to Harken Windtotem in Bloodhoof Village.",
    ["D"] = "The swoop is a cunning bird, and difficult to find and hunt.  A collection of Swoop Quills is a badge of cleverness and determination for a hunter.$B$BIf you are willing to take up this task, then enter the plains and hunt the swoop.$B$BBring me their quills, and bring honor to your clan.$B$BThe swoops may be found anywhere in Mulgore, but your eyes must be sharp to spot them, and your hooves must be quick to catch them.",
  },
  [762] = {
    ["T"] = "An Ambassador of Evil",
    ["O"] = "Kill Ambassador Infernus in Angor Fortress and bring proof of his death to Advisor Belgrum in Ironforge.",
    ["D"] = "Ambassador Infernus was sent to Angor Fortress by Ragnaros himself to mind the Shadowforge seeking lost artifacts in Uldaman.$B$BIt has already meant tremendous casualties for our people, but it has obviously grown worse. I hesitate to guess how many artifacts they would be able to find and use against Ironforge.$B$BOur goal is simple, $c. Return to the Badlands and destroy Ambassador Infernus. Once the threat is eliminated, return to Advisor Belgrum with proof of your victory.$B$BIt is in your hands now.",
  },
  [763] = {
    ["T"] = "Rites of the Earthmother",
    ["O"] = "Take the Totem of Hawkwind to Baine Bloodhoof in Bloodhoof Village. Follow the road out of Camp Narache.",
    ["D"] = "For you to continue with the Rites of the Earthmother you must pass two more tests. It is time for you to broaden your experience, $n.$b$bTravel to Bloodhoof Village and seek out the Chief, Baine Bloodhoof, son of Cairne. There you may continue your journey and earn the acceptance of the elders of Thunder Bluff.$b$bTake this totem to Baine. He will recognize my carvings and help you on your path.$b$bFollow the road out of Camp Narache and travel with haste. Do not stray or you will lose your way.",
  },
  [764] = {
    ["T"] = "The Venture Co.",
    ["O"] = "Kill 14 Venture Co. Workers and 6 Venture Co. Supervisors for Morin Cloudstalker at Bloodhoof Village.",
    ["D"] = "This explains the buildup of Venture Co. employees and equipment we\'ve seen in Mulgore. Those goblins... their company is expanding too quickly for their own good. It\'s how they say, show a goblin something, and you can hear the scales in the background.$b$bGreedy little things, they are. Unfortunately for their business enterprise, we tauren cannot allow them to conduct their operations in our lands. Go to their mine northeast of the ravaged caravan and send them a message.",
  },
  [765] = {
    ["T"] = "Supervisor Fizsprocket",
    ["O"] = "Kill Supervisor Fizsprocket and return his clipboard to Morin Cloudstalker at Bloodhoof Village.",
    ["D"] = "According to the documents you salvaged from the caravan, the Venture Co. created many plans to attempt to drive us from our lands here, so that they could have free reign to plunder our lands.$b$bThe depths that they would sink to accomplish their goals disgusts and appalls me. I want the mastermind behind their plans killed. His name is Supervisor Fizsprocket, you will find him at the Venture Co. mine east of the ravaged caravan. Bring me his clipboard, we shall see if we find out anything useful.",
  },
  [766] = {
    ["T"] = "Mazzranache",
    ["O"] = "Bring a Prairie Wolf Heart, Flatland Cougar Femur, Plainstrider Scale and Swoop Gizzard to Maur Raincaller at Bloodhoof Village.",
    ["D"] = "My grandfather told me stories about his battles with a strider named Mazzranache. He told me of its demonic red eyes, razor-sharp talons and venomous bite.$b$bAs fortune would have it, while traveling across the plains, I encountered it, and it took a nasty bite from my shoulder. He was as terrible as my grandfather described. I will need some hard-to-find animal parts to clean the infection from his bite: a wolf heart, cougar femur, plainstrider scale and swoop gizzard.$b$bHurry, time is of the essence.",
  },
  [767] = {
    ["T"] = "Rite of Vision",
    ["O"] = "Speak with Zarlman Two-Moons in Bloodhoof Village.",
    ["D"] = "The Rite of Vision, one of the Rites of the Earthmother, will help guide you toward earning the respect of the elders of Thunder Bluff.$b$bOur people have learned that the land is our most holy provider. In order to take part in the ritualistic vision, you must speak with the spiritual leader of the village, Zarlman Two-Moons.",
  },
  [768] = {
    ["T"] = "Gathering Leather",
    ["O"] = "Bring 12 pieces of Light Leather to Veren Tallstrider in Thunder Bluff.",
    ["D"] = "Greetings, young one.  You have a gleam in your eye; I can tell you are eager to venture back to the plains for the hunt.  May you bring honor to your clan!$B$BI work with the skins of beasts, fashioning them into clothes and armor for the people of Thunder Bluff.$B$BIf you hunt beasts and bring me their skins, then I will fashion something for you.",
  },
  [769] = {
    ["T"] = "Kodo Hide Bag",
    ["O"] = "Bring 4 Light Leather and 4 Coarse Thread to Veren Tallstrider in Thunder Bluff.",
    ["D"] = "The kodo beasts of Mulgore are strong and sturdy -- traits much respected among us.  If you are skilled in leatherworking and wish the knowledge to make a Kodo Hide Bag, then bring me supplies for my trade.$B$BDo this, and I will share my knowledge with you.",
  },
  [770] = {
    ["T"] = "The Demon Scarred Cloak",
    ["O"] = "Find someone who knows of Ghost Howl.",
    ["D"] = "The ragged hide of Ghost Howl still bears a terrible wound, gained from the wolf\'s battles against the Burning Legion.$B$BPerhaps someone in Bloodhoof Village should know of Ghost Howl\'s demise....",
  },
  [771] = {
    ["T"] = "Rite of Vision",
    ["O"] = "Collect 2 Well Stones and 2 Ambercorn and bring them back to Zarlman Two-Moons in Bloodhoof Village.",
    ["D"] = "In order to pass through the Rite of Vision, you must gather the reagents needed to make the Water of the Seers.$b$bI will need well stones, which can be found around the water wells in Mulgore. With those I will combine Ambercorn, which fall beneath the mighty trees in our land.$b$bThese reagents contain magical elements, and when brewed and consumed before the tribal fire, will cause you to encounter a vision. Follow it, and it will lead you to the next step of this holy quest.",
  },
  [772] = {
    ["T"] = "Rite of Vision",
    ["O"] = "Consume the Water of the Seers in front of the tribal fire in Bloodhoof Village and follow the vision once it appears.",
    ["D"] = "I now present you with the Water of the Seers.$b$bWhen you are ready, consume the waters near the tribal fire. Once you have ingested the holy waters, it will take a few moments before the vision materializes before the fire.$b$bAt that point, it is up to you to follow it to your destiny....",
  },
  [773] = {
    ["T"] = "Rite of Wisdom",
    ["O"] = "Travel to Red Rocks east of Thunder Bluff and speak with the Ancestral Spirit.",
    ["D"] = "To gain acceptance amongst the elders of Thunder Bluff you must next complete the Rite of Wisdom.$b$bNow that you have passed the Rite of Vision, the ancestral spirits of Red Rocks will give you the blessing of our ancestors. Only those who have drunk from the Water of Seers can gain the blessing.$b$bTravel east of Thunder Bluff, to Red Rocks and seek out the Ancestral Spirit, $n.",
  },
  [775] = {
    ["T"] = "Journey into Thunder Bluff",
    ["O"] = "Speak with Cairne Bloodhoof in Thunder Bluff.",
    ["D"] = "You will now be revered within our great city. Any $r worthy of completing the Rites of the Earthmother earns that honor.$b$bFor your final quest, seek out our noble leader, Cairne Bloodhoof, who dwells atop the highest mesa in Thunder Bluff.",
  },
  [776] = {
    ["T"] = "Rites of the Earthmother",
    ["O"] = "Cairne Bloodhoof in Thunder Bluff wants you to bring him the Horn of Arra\'chea.",
    ["D"] = "You have passed the Rites of the Earthmother and earned your place in Thunder Bluff.$b$bBut in order to maintain the spot that you strived so hard to achieve, you must continue to prove your worth to your people.$b$bWe are hunters, $n. Down below in Mulgore there is a mighty kodo named Arra\'chea. Show me your skills in the ways of tracking and hunting by bringing me the Horn of Arra\'chea.",
  },
  [777] = {
    ["T"] = "This Is Going to Be Hard",
    ["O"] = "Bring Cog #5 to Lotwil Veriatus.",
    ["D"] = "Okay, here you go.$B$BI\'m going to be over here... behind cover.$B$BFeel free to join me.",
  },
  [778] = {
    ["T"] = "This Is Going to Be Hard",
    ["O"] = "Defeat the Fam\'retor Guardian and bring Lotwil\'s Shackles of Elemental Binding back to Lotwil Veriatus.",
    ["D"] = "Haha! Done! The shackles are done!$B$BIt\'s time, Lucien. It\'s time, Servo!$B$BAre you ready to bear witness to the perfect blend of alchemy, engineering, and the arcane, $N?$B$BI shall summon forth a powerful elemental now. This is a groundbreaking day for all of us.",
  },
  [779] = {
    ["T"] = "Seal of the Earth",
    ["O"] = "",
    ["D"] = "",
  },
  [780] = {
    ["T"] = "The Battleboars",
    ["O"] = "Grull Hawkwind in Camp Narache wants you to kill Battleboars and bring back 8 Battleboar Snouts and 8 Battleboar Flanks.",
    ["D"] = "The Battleboars of Brambleblade Ravine to the east are encroaching on our tribal hunting grounds. They are trained to be malicious by the Bristleback Quilboars with whom we are at war.$b$bGo and slay the vile creatures and bring back some snouts and flanks so that we can make stew for our young.",
  },
  [781] = {
    ["T"] = "Attack on Camp Narache",
    ["O"] = "Bring the Bristleback Attack Plans to Chief Hawkwind in Camp Narache.",
    ["D"] = "After carefully evaluating the map, you realize that what you discovered are the plans made by the Bristleback warchief for an extended siege on Camp Narache.$b$bSurely Chief Hawkwind could use this information!",
  },
  [782] = {
    ["T"] = "Broken Alliances",
    ["O"] = "Kill Boss Tho\'grun and bring the Sign of the Earth to Gorn in Kargath.",
    ["D"] = "From the Horde\'s alliance with the dragon Deathwing during the Second War, we know that two of his lieutenants, Blacklash and Hematus, were imprisoned within Lethlor Ravine far to the east. We must silence our depraved allies of old to restore honor to the Horde!$B$BWe hired Tho\'grun and his band of ogre mercenaries to help us obtain the Sign of the Earth, a key needed to unlock the drakes\' prison. Tho\'grun betrayed us though, overwhelming us and taking it for himself. Go to Camp Boff and take it back!",
  },
  [783] = {
    ["T"] = "A Threat Within",
    ["O"] = "Speak with Marshal McBride.",
    ["D"] = "I hope you strapped your belt on tight, young $c, because there is work to do here in Northshire.$B$BAnd I don\'t mean farming.$B$BThe Stormwind guards are hard pressed to keep the peace here, with so many of us in distant lands and so many threats pressing close.  And so we\'re enlisting the aid of anyone willing to defend their home.  And their alliance.$B$BIf you\'re here to answer the call, then speak with my superior, Marshal McBride.  He\'s inside the abbey behind me.",
  },
  [784] = {
    ["T"] = "Vanquish the Betrayers",
    ["O"] = "Kill 10 Kul Tiras Sailors, 8 Kul Tiras Marines and Lieutenant Benedict and return to Gar\'Thok in Razor Hill.",
    ["D"] = "Led by Admiral Proudmoore, the humans of Kul Tiras encroached on Durotar, violating the Warchief\'s pact made with Jaina Proudmoore in order to defeat Archimonde years ago.$b$bThe human aggression was repelled and Tiragarde Keep fell. But recently, the Admiral\'s reserves, led by Lieutenant Benedict, have retaken the keep and once again pose a threat to our homeland. These humans show no respect for diplomacy.$b$bProve your honor and travel south to Tiragarde Keep to eliminate the human invaders.",
  },
  [786] = {
    ["T"] = "Thwarting Kolkar Aggression",
    ["O"] = "Lar Prowltusk outside of Sen\'jin Village wants you to destroy the 3 sets of Attack Plans held within Kolkar Crag.",
    ["D"] = "Lower your voice, $c. The Kolkar centaurs lie just over the ridge to the west in Kolkar Crag.$b$bLast night while they were raiding, I snuck into their village and discovered that the dirty beasts have a three-tiered attack planned on the trolls and orcs of Durotar.$b$bWe mustn\'t let their invasion come to fruition. Perhaps you can muster the might needed to infiltrate Kolkar Crag and destroy their attack plans.$b$bLast I saw, they had divided them up amongst three of their leaders.",
  },
  [787] = {
    ["T"] = "The New Horde",
    ["O"] = "Report to Gornek in the Den.",
    ["D"] = "Throm\'ka, $c. I am Eitrigg, charged by Thrall to oversee the training of recruits.$b$bThe Horde has changed from what it once was. There was a time where I left the Horde, disillusioned by the growing influence of the power-hungry pawns of the Burning Legion. During my exile, I was taken prisoner by a group of humans, but was rescued by the Warchief. Hearing his vision of the Horde free of demonic influence, guided by the shamans, I returned.$b$bGornek will have further instructions for you.",
  },
  [788] = {
    ["T"] = "Cutting Teeth",
    ["O"] = "Kill 10 Mottled Boars then return to Gornek at the Den.",
    ["D"] = "The first order of business will be to put a little strength in your backbone. I could send you out to the Barrens to hunt kodo, but well, in all honesty, you\'re more useful to us alive than dead.$b$bI believe you would find a good match with the mottled boars you\'ll find to the north of here.",
  },
  [789] = {
    ["T"] = "Sting of the Scorpid",
    ["O"] = "Get 10 Scorpid Worker Tails for Gornek in the Den.",
    ["D"] = "Powerful warrior and awkward novice alike have fallen to the venomous sting of the scorpid. You will find large numbers of scorpids northwest of here. Bring me ten of their tails as proof of your prowess in battle.$b$bThe antidote for their sting is actually made from venom extracted from their stingers. We keep large quantities of antidote for scorpid venom on hand to heal young bloods just like you...$b$bBut I\'m sure you won\'t be needing any of that, will you?",
  },
  [790] = {
    ["T"] = "Sarkoth",
    ["O"] = "Kill Sarkoth and bring his claw back to Hana\'zua.",
    ["D"] = "$C! I thought I would die out here with none to know of it. While I was hunting the scorpids of the Valley, I came across a particularly vicious-looking one. Hurling myself at it, I managed to inflict a massive blow to its claw before it closed around my leg.$b$bI wasn\'t ready for its stinger though, and it sliced down and into my chest, cutting into my flesh and letting my blood. Please, you must kill the scorpid for me! My honor must be upheld! I fought it up on the plateau to the south.",
  },
  [791] = {
    ["T"] = "Carry Your Weight",
    ["O"] = "Furl Scornbrow in the Razor Hill watchtower wants 8 Canvas Scraps.",
    ["D"] = "Age has rendered me useless in battle. Now I make myself useful in other ways.$b$bFrom this vantage point I watch for invaders. As our strength here grows, I find myself blowing the signal horn less and less.$b$bTo pass the time I fashion goods to help younger, more able warriors defend our homeland.$b$bFor you, I can fashion you a bag for your belongings. If such an item would be of use to you, bring me some canvas, a material common to the humans and centaurs.",
  },
  [792] = {
    ["T"] = "Vile Familiars",
    ["O"] = "Kill 12 Vile Familiars.$B$BReturn to Zureetha Fargaze outside the Den.",
    ["D"] = "I trust the Valley of Trials will teach you much, young $c.$B$BI was sent to the Valley to guide you, but I have discovered a growing taint here...$B$BA group that calls itself the Burning Blade has a coven here in the Valley of Trials.  They are skulking in a cave to the northeast, and their Vile Familiars have spilled from its mouth to cause havoc.$B$BAs your first task against the Burning Blade, I bid you, defeat these familiars. Slay many and, if you survive, return to me.",
  },
  [793] = {
    ["T"] = "Broken Alliances",
    ["O"] = "Use the Sign of the Earth to activate the Pillars of Diamond, Opal, and Amethyst and obtain the Runestones.$b$bPlace the runestones in the Seal of the Earth to free Blacklash and Hematus.$b$bSlay them and return Blacklash\'s Bindings, the Chains of Hematus, and the Sign of the Earth to Gorn.",
    ["D"] = "The Seal of the Earth is held by three runestones, held in the Pillars of Diamond, Opal, and Amethyst in Lethlor Ravine. Using the Sign of the Earth, you will be able to remove the runestones and unlock the Seal of the Earth.$b$bBe wary though, $n, the black drakes Blacklash and Hematus are not to be trifled with. See them dead and return their bindings to me.$B$BTo reach the ravine, travel to the far eastern part of the Badlands.  Be careful though, for the ravine is home to countless drakes.",
  },
  [794] = {
    ["T"] = "Burning Blade Medallion",
    ["O"] = "Bring the Burning Blade Medallion to Zureetha Fargaze, outside The Den.",
    ["D"] = "Through my divinations, I see that an item of power hides deep within the Burning Blade Coven, guarded by beasts and black magic.$B$BIt is called the Burning Blade Medallion, and your next task is to find it and remove it from the coven.$B$BBut be wary, for the medallion may be possessed by an agent of the Burning Blade, and if so then the agent\'s power would be greater than the familiars you have already encountered.$B$BGo, $N.  You will find the coven in a cave, to the north and west.",
  },
  [795] = {
    ["T"] = "Seal of the Earth",
    ["O"] = "",
    ["D"] = "",
  },
  [804] = {
    ["T"] = "Sarkoth",
    ["O"] = "Bring the news of Hana\'zua\'s plight to Gornek at the Den.",
    ["D"] = "Seeing the deed you have done for me steels my heart. I cannot fall so easily! I must endure!$b$bBut it remains that I cannot make the trek back to the Den unassisted. Please, $n, return to the Den and tell Gornek of my situation. Perhaps he can help me.",
  },
  [805] = {
    ["T"] = "Report to Sen\'jin Village",
    ["O"] = "Speak with Master Gadrin in Sen\'jin Village.",
    ["D"] = "Your trials against the Burning Blade are finished... here in the Valley.  But I want you to report your findings.$B$BGo to the troll village of Sen\'jin and seek out Master Gadrin.  Sen\'jin Village is east out of the valley, then right at the fork.$B$BTell Gadrin of the Burning Blade, and that they have reached the Valley of Trials.  Discover from him if they have yet reached Sen\'jin.$B$BGo, $N, and be swift.  I fear the evil found in the Burning Blade Coven is but the herald of a larger threat...",
  },
  [806] = {
    ["T"] = "Dark Storms",
    ["O"] = "Bring Fizzle\'s Claw to Orgnil Soulscar in Razor Hill.",
    ["D"] = "We cannot allow the Burning Blade a foothold in Durotar!  We must destroy them before their evil festers!$B$BI have undergone my own investigations and found that a Burning Blade warlock, the goblin Fizzle Darkstorm, has camped within Thunder Ridge to the northwest.  There he and his cultist minions spread chaos.$B$BFind and defeat Fizzle, and bring me his dead claw!",
  },
  [808] = {
    ["T"] = "Minshina\'s Skull",
    ["O"] = "Retrieve Minshina\'s skull from the circle of power on the Echo Isles.$B$BBring it to Master Gadrin in Sen\'jin Village.",
    ["D"] = "I hear the voice of my brother, Minshina, calling to me in my dreams.$B$BHe was taken by Zalazane, the warlock on the Echo Isles to the east. And he is dead.$B$BBut death is not freedom for my brother. Minshina\'s spirit was trapped within his own skull by Zalazane\'s magics. In my dreams I see it with other skulls, in a circle of power on the largest Echo Isle. As long as it remains there my brother\'s soul is doomed.$B$BPlease, $N. Find the circle and retrieve Minshina\'s skull. Bring it to me.$B$BFree him!",
  },
  [809] = {
    ["T"] = "Ak\'Zeloth",
    ["O"] = "Speak with Ak\'Zeloth in the Barrens.",
    ["D"] = "Since the last great war when the Burning Legion was defeated, I have searched for sources of demonic corruption in orc society.  The collar you brought me confirms my fears.$B$BIt belongs to the Burning Blade, a cult that rallies around an item of demonic power.  It is called the Demon Seed, and it resides in the Barrens atop Dreadmist Peak.  It must be destroyed!$B$BGo to Far Watch Post, on the border of the Barrens to the west, and speak with my assistant, Ak\'Zeloth.  He will direct you further.",
  },
  [812] = {
    ["T"] = "Need for a Cure",
    ["O"] = "Find Kor\'ghan in Orgrimmar and get the Venomtail Antidote. Then bring the antidote to Rhinag near the northwestern border of Durotar.",
    ["D"] = "$N... your timing is perfect. I just hope I can compliment your haste as well.$B$BI was careless while fighting a few of the venomtails nearby, and one of them stung me deeply. I can feel its poison weakening me even as we speak. At this rate, I got maybe an hour left to live. But I\'ll need your help if I\'m to do so...$B$BKor\'ghan in Orgrimmar knows how to make the antidote. Find him... and hurry, $N. I won\'t be able to last much longer. He should be in the Cleft of Shadow.",
  },
  [813] = {
    ["T"] = "Finding the Antidote",
    ["O"] = "Bring 4 Venomtail Poison Sacs to Kor\'ghan in Orgrimmar.",
    ["D"] = "Venomtails are some of the deadliest scorpids in Durotar. It was unwise for Rhinag to hunt them without having some of the antidote with him, but right now, chastising is the last thing he needs.$B$BBring me a few venomtail poison sacs from any of the venomtails near the entrance of Orgrimmar and I\'ll make an antidote for him.$B$BMake haste, $N. One of our own needs your help.",
  },
  [815] = {
    ["T"] = "Break a Few Eggs",
    ["O"] = "Bring 3 Taillasher Eggs to Cook Torka in Razor Hill.",
    ["D"] = "Bah! I almost forgot I need to get breakfast ready.$B$B$N, get yourself movin\' and get me some taillasher eggs. I\'ll need at least three of them if I\'m going to have enough for tomorrow\'s meal.$B$BThe Bloodtalon taillashers can be fierce, and protect their eggs to the death more often than not. They sometimes bury their eggs too deep in the earth to uncover safely, but if you head to the Echo Isles to the southeast of Sen\'jin Village, you can usually find them all over those islands.",
  },
  [816] = {
    ["T"] = "Lost But Not Forgotten",
    ["O"] = "Bring a sign of Kron\'s fate to Misha Tor\'kren at the farmstead northwest of Razor Hill.",
    ["D"] = "$N, please, can you help me? My son Kron went hunting days ago, and he still hasn\'t returned. He went west towards the Barrens to hunt crocolisks along Southfury River. I fear the worst.$B$BAs strong as he was, his stubborn pride has always drawn him into trouble. I warned him that the crocolisks were powerful and vicious. He grew angry and stormed off.$B$BIf you can\'t find him, at least bring me a sign of his fate... even if you have to open up the stomach of every crocolisk along the Southfury\'s banks.",
  },
  [817] = {
    ["T"] = "Practical Prey",
    ["O"] = "Bring 4 Durotar Tiger Furs to Vel\'rin Fang in Sen\'jin Village.",
    ["D"] = "Many of the hides we use come from Durotar tigers, $N. Blankets, armor, tents: there are a great many reasons we hunt the beasts, and many reasons we let them thrive at the same time.$B$BThe time has come for us to cull the flock, so to speak. Our numbers grow, and our needs are beginning to overwhelm our stocks. I need more hides if I\'m to prepare suitable goods for our people.$B$BBring me 4 Durotar tiger furs, and I shall reward you. You can find them on the islands south of here.",
  },
  [818] = {
    ["T"] = "A Solvent Spirit",
    ["O"] = "Bring 4 Intact Makrura Eyes and 8 vials of Crawler Mucus to Master Vornal in Sen\'jin Village.",
    ["D"] = "Although my eyes fail me, I still can see clearly enough. More often I must rely on my alchemical skills to aid me in magics that once came easily. But I refuse to take on an apprentice--no troll or orc worthy enough has ever come forward.$B$BAre you worthy? Yes, of course you are... of course you think you are.$B$BI need a few things. Will you get them for me?$B$BI need intact makrura eyes, and vials of crawler mucus. You can find them on any makrura or crawler in Durotar. We shall speak again soon.",
  },
  [819] = {
    ["T"] = "Chen\'s Empty Keg",
    ["O"] = "Find someone who knows about Chen\'s Empty Keg.",
    ["D"] = "A small placard on one of the ends of the keg says,$B$B\"Chen Stormstout-$B$BMay your spirit be raised and always raise your spirits.\"",
  },
  [821] = {
    ["T"] = "Chen\'s Empty Keg",
    ["O"] = "Bring 5 Savannah Lion Tusks, 5 Plainstrider Kidneys, and 1 Thunder Lizard Horn to Brewmaster Drohn in Ratchet.",
    ["D"] = "Would you like a taste of Chen\'s namesake? Ahh, the stormstout is a mighty brew. Chen taught the recipe to my mentor, and my mentor passed it on to me. I\'ll need you to get me a few things, but I can tell you this: the kick is worth it.$B$BBring me 5 savannah lion tusks from any savannah lion, 5 plainstrider kidneys from any plainstrider, and 1 thunder lizard horn from any species of thunder lizard. That should do the trick nicely.$B$BYou can find these ingredients throughout the Barrens.",
  },
  [822] = {
    ["T"] = "Chen\'s Empty Keg",
    ["O"] = "Bring 5 Lightning Glands, 1 Thunderhawk Saliva Gland and 1 Kodo Liver to Brewmaster Drohn in Ratchet.",
    ["D"] = "Good stuff, that stormstout, huh?$B$BI have another recipe that I learned from Chen. Would you be interested? It won\'t take long to make, and it might be useful if you plan on adventuring some more.$B$BBring me 5 lightning glands from any stormhide, 1 thunderhawk saliva gland from greater thunderhawks, and a kodo liver from any of the Barrens\' kodos.$B$BLike I said, this stuff has kick; it\'s the trogg ale that I mentioned to you before.",
  },
  [823] = {
    ["T"] = "Report to Orgnil",
    ["O"] = "Speak with Orgnil Soulscar in Razor Hill.",
    ["D"] = "We at Sen\'jin Village are not without our own troubles, and we thank you for your aid.  But the news you bring regarding the Burning Blade could be trouble for everyone.$B$BThere is an orc stationed at Razor Hill, Orgnil Soulscar, who polices Durotar from evils such as the Burning Blade!  Go to Orgnil and tell him of our plight, as well as your news from the Valley of Trials.  He will want to know these things.$B$BRazor Hill lies to the north.",
  },
  [824] = {
    ["T"] = "Je\'neu of the Earthen Ring",
    ["O"] = "Bring the Befouled Water Globe to Je\'neu Sancrea at Zoram\'gar Outpost, Ashenvale.",
    ["D"] = "The Earthen Ring is a shamanistic group dedicated to studying and preserving the elements.  They have forgotten more about crazy old gods, ancient legends and lore than most scholars will ever know.$B$BLucky for you, I know someone in the Earthen Ring here in Ashenvale.  Head over to Zoram\'gar Outpost on the coast of the Zoram Strand; show Je\'neu Sancrea what you found.  If anyone can figure out that globe\'s significance, it\'d be him.",
  },
  [825] = {
    ["T"] = "From The Wreckage....",
    ["O"] = "Gar\'Thok of Razor Hill wants you to retrieve 3 Gnomish Tools from the wreckage off the coast.",
    ["D"] = "One of my most observant scouts brings back word that the wreckage of Proudmoore\'s fleet still remains off the coast of Durotar, just east of Tiragarde Keep.$b$bIt is no secret that the humans, in alliance with those foul little creatures known as gnomes, have an advanced knowledge of mechanics. We must have a complete understanding of all our potential enemies. And our people will benefit from this new knowledge as well.$b$bSwim through the wreckage, $n, and retrieve for me the tools of the Alliance.",
  },
  [826] = {
    ["T"] = "Zalazane",
    ["O"] = "Defeat Zalazane.$B$BKill 8 Voodoo Trolls and 8 Hexed Trolls.$B$BBring Zalazane\'s Head to Gadrin.",
    ["D"] = "The witchdoctor Zalazane dwells on the Echo Isles to the east.  They are the isles we once called home.$B$BFrom there he sends his trolls to the mainland, to hex our people and drag more of them under his sway.$B$BHe must be stopped.$B$BDefeat Zalazane and his minions -- former Darkspear trolls, now lost to us.  Bring me his head and I will know his reign of evil is over.",
  },
  [827] = {
    ["T"] = "Skull Rock",
    ["O"] = "Gather Searing Collars from the cultists in Skull Rock.$B$BBring them to Margoz at his camp.",
    ["D"] = "$N.  The Burning Blade has infested the cave east of Orgrimmar known as Skull Rock.  Inside that cave they perform vile rituals, and burn their own flesh with Searing Collars.$B$BBy wearing these collars, I believe the cultists attune themselves to demonic power.  But to confirm this, I must have a collection of the collars to study.$B$BGo to Skull Rock and gather Searing Collars from the cultists you find there.  Bring them to me, and I will uncover their secrets.",
  },
  [828] = {
    ["T"] = "Margoz",
    ["O"] = "Speak with Margoz.",
    ["D"] = "One of our Shamans, Margoz, knows more of the Burning Blade\'s corruption.  He speaks of a cave called Skull Rock in the mountains - just outside Orgrimmar! - that shelters a large band of Burning Blade cultists.$B$BBefore you go to Skull Rock speak with Margoz.  He is wise and his council is valued.$B$BHe is camped to the northeast, between the coast and Drygulch Ravine.$B$BFollow his advice, but whatever Margoz says, $N, I still want you to crush those cultists!",
  },
  [829] = {
    ["T"] = "Neeru Fireblade",
    ["O"] = "Bring an Example Collar to Neeru Fireblade in Orgrimmar.",
    ["D"] = "The Searing Collars you brought me are powerful, demonic implements.  Divining their origin is, I\'m afraid, beyond my skills as a shaman.  We will need a warlock to study them.$B$BTake a searing collar to Neeru Fireblade.  Although he is a skilled warlock, he professes to use his powers to thwart demons, and claims his research in the occult is benign.  Be that true or false, we may need his aid against the demonic cult in Durotar.$B$BYou may find Neeru in Orgrimmar in the Cleft of Shadow.",
  },
  [830] = {
    ["T"] = "The Admiral\'s Orders",
    ["O"] = "Take Admiral Proudmoore\'s Orders to Gar\'Thok in Razor Hill.",
    ["D"] = "You open the aged and weathered envelope and discover an official looking document. You recognize the seal of Admiral Proudmoore.$b$bThis looks important. Perhaps Gar\'Thok, the commander of Razor Hill, would be interested in having this information.",
  },
  [831] = {
    ["T"] = "The Admiral\'s Orders",
    ["O"] = "Deliver Admiral Proudmoore\'s Orders to Nazgrel in Thrall\'s chamber in Orgrimmar.",
    ["D"] = "Humans cannot be trusted. We fought alongside them with a weary heart, knowing they would betray us one day.$b$bAdmiral Proudmoore\'s death was not enough to stop his legacy of deceit. The human scum had his plans well laid out before he ever met his demise.$b$bHis reign won\'t even die with Benedict it seems. Who knows how long it will be before the next waves of Proudmoore\'s men land upon our shores.$b$bWe need to get these orders to Nazgrel in Orgrimmar immediately! He can be found in Thrall\'s chamber.",
  },
  [832] = {
    ["T"] = "Burning Shadows",
    ["O"] = "Take this eye to Neeru Fireblade.",
    ["D"] = "<Words emanate from the pendant and into your mind.>$B$BGazz\'uz?$B$BGazz\'uz...report!$B$BThere is word of your discovery in Skull Rock.  You must prepare for an attack!$B$BGazz\'uz, I order you to speak!  Speak or I will make sure Neeru Fireblade knows of your presence, and he will descend on you with swift brutality.$B$BDo not test your skills against Neeru...$B$B...Gazz\'uz, are you there...?",
  },
  [833] = {
    ["T"] = "A Sacred Burial",
    ["O"] = "Lorekeeper Raintotem wants you to kill 8 Bristleback Interlopers at Red Rocks.",
    ["D"] = "Only the most valiant tauren are laid to rest at Red Rocks, our sacred burial ground. It is an honor bestowed upon the great warriors who helped found and defend Thunder Bluff and those who have given their lives for the greater good of their tribes and chieftains.$b$bBut it appears a foul menace has made its way onto our holy land. A band of Bristleback Interlopers is ravaging the grave site and I am too old and past my prime to drive them away.$b$bThey must be driven off with force, $n.",
  },
  [834] = {
    ["T"] = "Winds in the Desert",
    ["O"] = "Retrieve 5 Sacks of Supplies and return them to Rezlak near Orgrimmar.",
    ["D"] = "I\'m Rezlak, one of Gazlowe\'s boys. Boss sent me to help the orcs here in Durotar. Things\'d been going good except for the caravans. Can\'t manage to keep them safe! Makes my job a little harder, you know?$b$bThe last shipment--they promised it\'d get through!--was snatched by the Dustwind harpies of...where was it...Razorwind Canyon?$b$bI gotta have those supplies or I\'ll never get anything done! Follow the big canyon to the south, you\'ll find the ravine cut right into the west and east sides of the walls.",
  },
  [835] = {
    ["T"] = "Securing the Lines",
    ["O"] = "Kill 12 Dustwind Savages and 8 Dustwind Storm Witches for Rezlak near Drygulch Ravine.",
    ["D"] = "Gazlowe told me once, \"Rezlak. There\'s one thing you need to know in life, and that\'s if you want something done well, do it yourself.\"$b$bI can\'t deal with losing all our caravans, so I\'m just going to take matters into my own hands. Drygulch Ravine lies through the eastern part of Razorwind Canyon. If we kill all the harpies there, the routes will be safe.$b$bOf course, there was one other thing that Gazlowe taught me, and that\'s \"You can get anything done for a price.\" So, going to help me out, $n?",
  },
  [836] = {
    ["T"] = "Rescue OOX-09/HL!",
    ["O"] = "Escort OOX-09/HL to the shoreline beyond Overlook Cliff, then report to Oglethorpe Obnoticus in Booty Bay.",
    ["D"] = "The gnome\'s voice crackles once again from the robot:$B$B\"I need to move OOX-09/HL to an open, safe place so it can begin a lengthy take-off procedure.  It has built-in cloaking, but I need time on my end to make it operational again.  Escort the robot from its current location to, let\'s say, the shoreline beyond Overlook Cliff!  That should be a perfect place, and long enough, to get things online!\"$B$B\"Escort it safely to the shore, and then come talk to me in Booty Bay!  Oglethorpe Obnoticus - out!\"",
  },
  [837] = {
    ["T"] = "Encroachment",
    ["O"] = "Kill 4 Razormane Quilboars, 4 Razormane Scouts, 4 Razormane Dustrunners and 4 Razormane Battleguards for Gar\'Thok at Razor Hill.",
    ["D"] = "When we arrived, the Razormane quilboars possessed much of the land, and proved a thorn in our sides. Through our efforts we have driven out the largest part of their numbers, but still they remain well-fortified in some areas.$b$bIt has gone on long enough, however. For our own protection, we cannot allow the Razormane any hold in our lands. Their camps can be found to the west of here. Look for the brambles and you will find them. Today we drive them from Durotar, tomorrow, perhaps from all of Kalimdor.",
  },
  [838] = {
    ["T"] = "Scholomance",
    ["O"] = "Speak with Apothecary Dithers at the Bulwark, Western Plaguelands.",
    ["D"] = "On the island of Caer Darrow lies Scholomance - a school that serves as a stronghold for the Scourge.  Our enemy dwells within, safely allowed to pursue research that strengthens their power.  A door bars the way, and try as we might no entry is to be had without a proper key.$B$BApothecary Dithers is well versed on Scholomance, and has an idea how to obtain a key.  You have proved your value to me as someone who can get things done; I now entrust this task to you.  Speak with him on this matter.",
  },
  [840] = {
    ["T"] = "Conscript of the Horde",
    ["O"] = "Follow the western road from Razor Hill to the Barrens over a bridge.$B$BStop at the orc outpost across the bridge.$B$BGive Kargal Battlescar at the Barrens outpost your recruitment letter.",
    ["D"] = "Hmm, you look pretty strong.$B$BListen, my good friend Kargal needs some new recruits for Barrens duty. I know you want to do what\'s right for the Horde. Well, this is your chance.$B$BTake this recruitment letter to Kargal and see if he\'ll sign it.",
  },
  [841] = {
    ["T"] = "Another Power Source?",
    ["O"] = "Bring 10 Wastewander Water Pouches to Chief Engineer Bilgewhizzle in Gadgetzan in exchange for another power source.",
    ["D"] = "Let me guess - you didn\'t get whatever world-ending, nefarious deed you were doing done in time.  No wait, better still... you dropped it in the sand!  Well, whatever the reason, I do have additional power sources I will sell to you.  This time, however, it will cost you ten, YES TEN Wastewander Water Pouches!$B$BTimes are tough all around, $n, and power sources don\'t grow on trees!  Hrm, but what if they did.  Maybe we could MAKE a power source tree...",
  },
  [842] = {
    ["T"] = "Crossroads Conscription",
    ["O"] = "Follow the western road from Kargal\'s Far Watch Outpost.$B$BAt the T intersection, turn left and follow the road south. $B$BFind Sergra Darkthorn at the crossing of roads within the Crossroads.",
    ["D"] = "Take this signed recruitment letter to the Crossroads.$B$BSergra Darkthorn, curse her hide, is in charge there. She\'s a nasty shaman who will talk your head in circles before she lets you do anything useful. Thrall is convinced she\'s something special, but I\'m not so sure.$B$BYou\'ll just have to decide for yourself, pup...",
  },
  [843] = {
    ["T"] = "Gann\'s Reclamation",
    ["O"] = "Gann Stonespire wants you to kill 15 Bael\'dun Excavators and 5 Bael\'dun Foremen.$b$bBring Khazgorm\'s Journal to Gann Stonespire.",
    ["D"] = "In solitude I wander these roads.$b$bThe dwarves of Bael Modan show no respect for my land. Horrendous blasts drive holes deep into the ground as noisy machines rip apart the hills. Lands which once served as home and provider to my tribe are now riddled with destructive dwarves.$b$bAttempts at reaching a diplomatic resolution have failed. Now is the time for decisive action.$b$bDrive the dwarves from the Bael Modan excavation by force and bring to me the journal of their leader, Prospector Khazgorm.",
  },
  [844] = {
    ["T"] = "Plainstrider Menace",
    ["O"] = "Collect 7 Plainstrider Beaks and return them to Sergra Darkthorn in the Crossroads.",
    ["D"] = "Your first prey will be easy.$B$BThe plainstriders to the east have been harassing our food supplies and have become a nuisance.$B$BPut down the plainstriders and return to me with their beaks.",
  },
  [845] = {
    ["T"] = "The Zhevra",
    ["O"] = "Slay Zhevra Runners to collect 4 Zhevra Hooves for Sergra Darkthorn in the Crossroads.",
    ["D"] = "The zhevra, although not the fiercest beast in the Barrens, are a shade tougher than the plainstriders.  Don\'t worry -- we\'ll send you against bigger prey in good time, but for now your teeth could use a little more cutting.$B$BHunt the zhevra to the north and south, and collect their hooves.  Bring them to me and perhaps we\'ll next send you against something tougher.",
  },
  [846] = {
    ["T"] = "Revenge of Gann",
    ["O"] = "Gann Stonespire wants you to bring him 6 vials of Nitroglycerin, 6 bundles of Wood Pulp, and 6 samples of Sodium Nitrate.",
    ["D"] = "It is clear the prospector depended on explosives and those noisy vehicles to rip the ground apart. We can expect a new foreman to take over soon.$b$bI want to give the dwarves a taste of their own medicine. According to these plans if we combine nitroglycerin with wood pulp and sodium nitrate we can create an explosive charge. The rifleman, soldiers and officers in the Keep are known to have the supplies.$b$bCollect these items and bring them to me. I will fashion a charge that the dwarves won\'t forget!",
  },
  [847] = {
    ["T"] = "Guile of the Raptor",
    ["O"] = "Speak with Tor\'gan.",
    ["D"] = "This... this power.... Is this the power of the shamans that Tor\'gan tried to show me? I must say, it is interesting. Tell Tor\'gan that he has convinced me... for the time being. I will listen to what he has to say.",
  },
  [848] = {
    ["T"] = "Fungal Spores",
    ["O"] = "Bring 4 Fungal Spores to Apothecary Helbrim at the Crossroads.",
    ["D"] = "The oases of the Barrens hide a mystery.  Life energy flows from their waters, invigorating the plants and beasts that drink it.$B$BInvigorating, and altering.$B$BThere is a mushroom that grows near these oases.  Its spores hold properties that we, the apothecaries of Lordaeron, find useful.$B$BBring me these spores and you will earn our gratitude.",
  },
  [849] = {
    ["T"] = "Revenge of Gann",
    ["O"] = "Destroy the flying machine at Bael Modan and return to Gann Stonespire.",
    ["D"] = "Following Khazgorm\'s plans it should be no problem to fashion an explosive charge$b$bAh, there we go.$b$bNow, $n, I am going to entrust you with a special task. Take this explosive stick and use it to blow up the flying machine at Bael Modan.$b$bNot only is the flying machine a key part of the site\'s infrastructure it also serves as a weapon of war. More importantly it is an icon of power and an insult to the memory of my tribe.$b$bMake me proud by completing this heroic task and you shall be rewarded.",
  },
  [850] = {
    ["T"] = "Kolkar Leaders",
    ["O"] = "Bring Barak\'s Head to Regthar Deathgate, west of the Crossroads.",
    ["D"] = "The centaurs have plagued the Tauren for years.  And recently, the Kolkar centaurs of the Barrens have grown into a real threat.  Normally they were disorganized in these lands, but new leaders have risen among them.  Rallying them.$B$BTo preserve our holdings here, these centaur leaders must be destroyed.$B$BBring me the head of Barak Kodobane.  He camps near the Forgotten Pools to the north.$B$BDefeat him, then return to me.",
  },
  [851] = {
    ["T"] = "Verog the Dervish",
    ["O"] = "Bring Verog\'s Head to Regthar Deathgate, west of the Crossroads.",
    ["D"] = "The centaur Verog the Dervish wanders the Barrens, and will be difficult to find.  But he is based at the centaur command tent at the Stagnant Oasis to the southeast.  It may be possible to draw him to you.$B$BTravel to the Stagnant Oasis to the southeast and attack centaur near the command tent.  It will be dangerous, but if you can kill enough centaur then they should raise an alarm.  And Verog will come.$B$BBring me his head and I will place it with Barak Kodobane\'s.",
  },
  [852] = {
    ["T"] = "Hezrul Bloodmark",
    ["O"] = "Bring Hezrul\'s Head to Regthar Deathgate, west of the Crossroads.",
    ["D"] = "Hezrul Bloodmark is the leader of the Kolkar centaurs in the Barrens.  He is fierce, brutal and cunning.  Defeating him would disrupt and fracture the Kolkars, greatly reducing their threat to us.$B$BSo kill him.  As with Barak and Verog, bring me Hezrul\'s Head.$B$BHe leads his people from the Lushwater Oasis, to the south.",
  },
  [853] = {
    ["T"] = "Apothecary Zamah",
    ["O"] = "Bring the Rendered Spores to Apothecary Zamah in Thunder Bluff, before the time limit is up.",
    ["D"] = "I have rendered the spores you gave me into an emulsion.  I must now send it to my associate, Apothecary Zamah.  If you were to carry this to her, she can offer you something from our stores of alchemical goods.$B$BShe is in the Pools of Vision, a cave beneath the shamans of Thunder Bluff.  The path to this cave is well hidden, but you may find it on the Spirit Rise bluff.$B$BDo hurry.  This emulsion will maintain its potency for only a short time, and must be resealed and processed before then.",
  },
  [854] = {
    ["T"] = "Journey to the Crossroads",
    ["O"] = "Speak with Thork at the Crossroads in the Barrens.",
    ["D"] = "I have foreseen your arrival, young $c. Though perhaps you are not so young anymore.$B$BI think that it is time for you to move deeper into this land. I can sense that you are destined for great things.$B$BIt is the Crossroads that you must seek out, for our lives with the orcs have become intertwined and they need assistance. Continue down this road towards the east and follow it north to the Crossroads. Find Thork within its walls, he has the Earthmother\'s blessing.",
  },
  [855] = {
    ["T"] = "Centaur Bracers",
    ["O"] = "Bring 15 Centaur Bracers to Regthar Deathgate, west of the Crossroads.",
    ["D"] = "The Kolkar are a threat to the Horde\'s claims in the Barrens; we must reduce the numbers of their troops.  And so, I have a bounty task for you.$B$BWhile in Kolkar territories, slay their warriors and collect centaur bracers.  Return when you have a heap of bracers and the Horde will reward you well.",
  },
  [857] = {
    ["T"] = "The Tear of the Moons",
    ["O"] = "Feegly the Exiled wants you to retrieve for him the Tear of the Moons.",
    ["D"] = "The power was mine! In my very hands! And I want it back! Back I say!$b$bThose dirty excavators uncovered it... so beautiful it was. Deemed the Tear of the Moons, it was the first great discovery we made at Bael Modan.$b$bIt should have been mine! Mine I say!$b$bGeneral Twinbraid grew jealous of the power it gave me. He locked it away in the Keep. Called it cursed, he did! The fool!$b$bOthers wanted it and they had to be stopped! That bastard Twinbraid drove me off.$b$bNow I want it back. Back I say!",
  },
  [858] = {
    ["T"] = "Ignition",
    ["O"] = "Get the Ignition Key and bring it to Wizzlecrank.",
    ["D"] = "I don\'t suppose Sputtervalve sent you? I\'m in a bind here. I hopped in without realizing that I need a key to unlock the shredder\'s movement column. One of the other shredder operators asked me if everything was okay, and I panicked! Instead of telling him that I was missing my key, I told him there was some sort of mechanical problem.$b$bWe need to get out of here on the double! Go up to the control room at the top of the derrick, the supervisor should have a key for this shredder.$b$bHelp me out here!",
  },
  [860] = {
    ["T"] = "Sergra Darkthorn",
    ["O"] = "Speak with Sergra Darkthorn at the Crossroads.",
    ["D"] = "If you wish to walk the hunter\'s path, then your journey leads to the Barrens.  Its beasts are strong-willed and fierce.  You will learn much by hunting there, and your spirit will grow.$B$BSpeak with Sergra Darkthorn.  She will be your first guide in the Barrens.$B$BSergra is at the Crossroads.  To reach her, travel east from Bloodhoof Village into the Barrens, then north as the road forks.  And have care as you travel--the Barrens holds great danger for the unprepared and unwary.",
  },
  [861] = {
    ["T"] = "The Hunter\'s Way",
    ["O"] = "Bring 4 Flatland Prowler Claws to Melor Stonehoof in Thunder Bluff.",
    ["D"] = "You are eager to explore, I can tell.  I too had the lust to wander, once...$B$BWander, and hunt.  For hunting is a Tauren\'s greatest honor.$B$BIf you truly wish to follow the ways of the hunter, then Melor Stonehoof can show you the path.  He is in Thunder Bluff, on the Hunter\'s Rise.$B$BAnd to show him your skill and resolve, bring him the claws of the flatland prowlers of Mulgore.  They are tough and cunning -- fitting prey for a young $c on the hunter\'s path.",
  },
  [862] = {
    ["T"] = "Dig Rat Stew",
    ["O"] = "Bring 8 Dig Rats to Grub east of the Crossroads.",
    ["D"] = "A $c like you must get pretty hungry out here in The Barrens. Me, I\'m starving all day long.$b$bTell you what, I\'ll fix you up a nice stew. Just need a few dig rats.$b$bSeems those filthy dwarves at Bael Modan are overrun with everyone\'s favorite feast. Now head on down south to Bael Modan and bring me back some dig rats.$b$bYou can\'t call yourself a seasoned adventurer until you\'ve spent some time killing rats! Haw!",
  },
  [863] = {
    ["T"] = "The Escape",
    ["O"] = "Protect Wizzlecrank and the stolen goblin shredder on the way to Sputtervalve in Ratchet.",
    ["D"] = "I suppose I\'ll learn as we go... Couldn\'t be too hard. Just some buttons here, and a lever or two... Well, are you ready to go?",
  },
  [864] = {
    ["T"] = "Return to Apothecary Zinge",
    ["O"] = "Bring the Sealed Field Testing Kit to Apothecary Zinge in the Undercity.",
    ["D"] = "\"Here you go, the power source is safely removed and the data is preserved.  Well, safe journeys and all that.  Hope the nefarious science experiment worked out for you and all.\"$B$BThe goblin turns away from you to attend to the demands of the Gadgetzan Water Company.  You secure the sealed testing kit for the journey back to the Undercity.  Apothecary Zinge waits for you and the sealed field testing kit there.",
  },
  [865] = {
    ["T"] = "Raptor Horns",
    ["O"] = "Gather 5 Intact Raptor Horns from Sunscale Scytheclaws, and bring them to Mebok Mizzyrix in Ratchet.",
    ["D"] = "The raptors of the Barrens are smarter than raptors in other lands.  And I think all that smarts is hiding in their horns!  If so, then I could grind their horns into powder and use it to make \"smart drinks.\"  I could sell them for a fortune!!!$B$BAnd you can help me.  Find me intact raptor horns from sunscale scytheclaws.  They roam in the southern Barrens, and in the northern Barrens near the border of Ashenvale Forest.",
  },
  [866] = {
    ["T"] = "Root Samples",
    ["O"] = "Bring 8 Root Samples to Mebok Mizzyrix in Ratchet.",
    ["D"] = "The herbs of the Barrens are not quite like herbs in other lands.  Their properties are mostly the same, but the oasis water here alters the plants; they are just slightly different.$B$BI want to study those differences... to see if they\'re exploitable!$B$BIf you are skilled in herbalism, then gather root samples off the herbs you find in the Barrens.  Bring back these samples so that I may compare them with herbs from other regions.",
  },
  [867] = {
    ["T"] = "Harpy Raiders",
    ["O"] = "Collect 8 Witchwing talons.$B$BReturn them to Darsok Swiftdagger at the Crossroads.",
    ["D"] = "The blasted harpies have been ravaging our caravan supplies for too long. It\'s time we slit a few of their throats and diminished their numbers.$B$BI\'ve been snooping around their nests and figured out who leads who. The most devastating attack will destroy a good section of their lower chain of command and then we can work our way to the top.$B$BI\'ll start you off slow, take out Witchwing Harpies and Witchwing Roguefeathers in the Northwest and bring me 8 Witchwing talons. That should be a good start...",
  },
  [868] = {
    ["T"] = "Egg Hunt",
    ["O"] = "Bring 12 Silithid Eggs and the Digging Claw to Korran at the Crossroads.",
    ["D"] = "Hey, $N. I been sent to the Crossroads to watch over the land and take note of its happenings for my masters in Orgrimmar.$B$BOne object of my studies are the insect-like creatures found to the south in the Field of Giants. We know little of the creatures, so I be makin\' it a point to discover more. They seem to have intelligence to them, more so than any normal animal.$B$BTake this digging claw and collect some of the creatures\' eggs from their mounds, but be careful: if alerted, they will attack you.",
  },
  [869] = {
    ["T"] = "Raptor Thieves",
    ["O"] = "Bring 12 Raptor Heads to Gazrog at the Crossroads.",
    ["D"] = "Not long ago, a shipment of silver was stolen from our guard tower.  It was meant as payroll to the Crossroads\' guards, and we want that silver back.$B$BThe strange thing is... we caught one of the thieves on the night of the theft.  And... it was a raptor!  Unbelievable!$B$BI don\'t know what raptors would want with silver.  But I don\'t care -- I want the raptors dead so they won\'t steal from us again!$B$BHunt raptors in the Barrens.  Collect for me their heads!",
  },
  [870] = {
    ["T"] = "The Forgotten Pools",
    ["O"] = "Report back to Tonga Runetotem with your findings.",
    ["D"] = "Long ago, the Barrens was a lush place, teeming with life.  But war and cataclysm raged across the land, scorching it and leaving a dry husk.  Such is the way of things, and it saddens my heart.$B$BBut within the past few years, new oases have formed in the Barrens, and life stirs.  And deep down, we druids sense a power leaking its way to the surface.$B$BWe must find its source.  Travel to the Forgotten Pools, northwest of the Crossroads.  Search its waters for a source of power, then return here.",
  },
  [871] = {
    ["T"] = "Disrupt the Attacks",
    ["O"] = "Of the Razormane tribe, kill 8 Water Seekers, 8 Thornweavers and 3 Hunters, and then return to Thork in the Crossroads.",
    ["D"] = "All the quilboars are our enemies, $N. Some just prove to be more of a nuisance than others.$B$BThe Razormane tribe has been attacking our supply lines from Durotar, causing us no end of annoyance. I have scouts seeking the leader of these raids, but until then, any losses you can inflict on the filthy pig men would aid us.$B$BStart in the northeast towards Durotar. You can always tell their dens from the huge thorny vines that come up from the earth. Seek them out, and slay them.",
  },
  [872] = {
    ["T"] = "The Disruption Ends",
    ["O"] = "Kill 8 Razormane Geomancers, 8 Razormane Defenders, and Kreenig Snarlsnout.$B$BThen bring Kreenig Snarlsnout\'s Tusk to Thork at the Crossroads.",
    ["D"] = "One of my scouts witnessed an attack on a caravan from Durotar, $N. The culprits are indeed the Razormane tribe of quilboars. One of the quilboars in particular is leading the raids: Kreenig Snarlsnout. He was seen northeast of here, just south of the road from Durotar. If the threat is to be ended, then Kreenig must die.$B$BTo be sure the attacks cease, though, I say we add insult to their injury. Kill Kreenig along with more of their tribe, and return to me when you have his tusk.",
  },
  [873] = {
    ["T"] = "Isha Awak",
    ["O"] = "Bring the Heart of Isha Awak to Mahren Skyseer.",
    ["D"] = "The grand Isha Awak is lord of these waters.  Great is his strength, and solemn his pride.  The humans on the coast fear him, for he has consumed many of their number.$B$BBut I do not fear him.  I am grateful he is here.  He is a worthy challenge, and honorable prey.$B$BIf you are ready, then swim out and search for Isha Awak, the Deep Doom.$B$BHis spirit dwells in his heart, and to hear its beat is to know your fate.",
  },
  [874] = {
    ["T"] = "Mahren Skyseer",
    ["O"] = "Speak with Mahren Skyseer.",
    ["D"] = "You have hunted the beasts of the Barrens.  Your spirit is strong.  But a hunter must always be prepared.  A true hunter can stalk prey down any path.  Mountains and swamps will not sway $ghim:her;.$B$BNot even the sea.$B$BFind my sister, Mahren.  She hunts the great water beasts along the coast.  She will be your teacher in their ways.$B$BBut be wary in your search for her, for the Barrens\' coast is held by humans.",
  },
  [875] = {
    ["T"] = "Harpy Lieutenants",
    ["O"] = "Collect 6 Harpy Lieutenant Rings from Witchwing Slayers and return them to Darsok Swiftdagger at the Crossroads.",
    ["D"] = "Hahaha, $n. Back for more? Good, I like to hear that.$B$BThis time I want you to assassinate some of the harpy lieutenants in the Dry Hills. Only Witchwing Slayers can be lieutenants, and you can tell them apart by the rings they carry. They are a nasty bunch, but they lead some of the underlings in that area. Taking out 6 of them will be a decisive strike against their matriarchy.$B$BMake sure they die painfully, $n. We want those harpy wenches to know the idiocy of picking fights with the Horde.",
  },
  [876] = {
    ["T"] = "Serena Bloodfeather",
    ["O"] = "Slay Serena Bloodfeather and return her head to Darsok Swiftdagger at the Crossroads.",
    ["D"] = "You did so well with the lieutenants and underlings that I\'d like you to do one last thing for me.$B$BSerena Bloodfeather is the sister of a harpy named Bloodfeather who was slain by Rexxar quite a while ago. Apparently, these attacks on the Horde caravans are revenge for her sister\'s death.$B$BI need you to slit her throat and bring me back her head. I want to place it on the next caravan we send out... Give those harpies something to think about.",
  },
  [877] = {
    ["T"] = "The Stagnant Oasis",
    ["O"] = "Return to Tonga at The Crossroads, after investigating the Stagnant Oasis.",
    ["D"] = "Like the one you found at the Forgotten Pools, there may be fissures at the other oases in the Barrens.  If so, then perhaps the fissures are the oases\' source of life.$B$BWe must test this.$B$BHere, take these seeds.  They are dead and infertile, but take them to the Stagnant Oasis to the southeast.  If there is a fissure there, then place the seeds within it...and observe.",
  },
  [878] = {
    ["T"] = "Tribes at War",
    ["O"] = "Kill 6 Bristleback Water Seekers, 12 Bristleback Thornweavers and 12 Bristleback Geomancers and return to Mangletooth at Camp Taurajo in the Barrens.",
    ["D"] = "You must strike my enemies. <snort> You must help Mangletooth and the Razormane by weakening the Bristlebacks. Both tribes are very spiritual--tied to the earth and its power. <snort> It gives us strength in all things... especially war.$B$BIf you want to find out who really causes Thork <snort> and the Horde such anguish, then help Mangletooth! You attack the spiritual leaders of the Bristlebacks to the north; weaken them so the Razormanes can defeat them and rule the Barrens.",
  },
  [879] = {
    ["T"] = "Betrayal from Within",
    ["O"] = "Kill Nak, Kuz, and Lok Orcbane and bring their skulls to Mangletooth at Camp Taurajo in the Barrens.",
    ["D"] = "Three of my tribe came out of the Kraul to lead the raids against the Horde, $r. <snort> They are ruthless and cunning, and if you defeat them, then your Crossroads and even most of the Barrens will learn peace... <snort> at least from the Razormane tribe. <snort>$B$BNak, Kuz, and Lok Orcbane are the ones you seek. They are far to the south  of the Barrens. One is a spell caster, another a tracker, and their leader, the one called Orcbane, <snort> a warrior. Kill them, $r, like they have killed me.",
  },
  [880] = {
    ["T"] = "Altered Beings",
    ["O"] = "Bring 8 Altered Snapjaw Shells to Tonga Runetotem at the Crossroads.",
    ["D"] = "Your findings are incredible, $N.  These oases hold properties that must come from an outside source.  Or perhaps an inside one.$B$BI want to know how these fissures are affecting the beasts who drink from the oases\' water.$B$BHunt oasis snapjaws at the Lushwater and Stagnant Oases.  Bring me their shells so I may examine them.",
  },
  [881] = {
    ["T"] = "Echeyakee",
    ["O"] = "Bring Echeyakee\'s Hide to Sergra Darkthorn at the Crossroads.",
    ["D"] = "Whitemist, Echeyakee in the Tauren tongue, is the king of the savannah cats.  He hunts with such stealth, they say he\'s like a thin, white mist on the earth.  And he kills so fast his prey have no time for fear, or pain.$B$BThe Tauren say he is both mercy and death.$B$BYou will learn if that\'s true, for I now set you on the path to hunt Echeyakee.  His lair is northeast of the Crossroads, among the bones of giant Kodo$B$BGo.  Blow this horn when you reach his lair.  Blow the horn, and he will come.",
  },
  [882] = {
    ["T"] = "Ishamuhale",
    ["O"] = "Bring Ishamuhale\'s Fang to Jorn at Camp Taurajo.",
    ["D"] = "Ishamuhale, Speartooth, is the fiercest sunscale raptor of the Barrens.  He does not hunt for sport, nor for food.  He hunts because hunting is his passion.  He kills because it is his nature to kill.$B$BAnd you will learn of his nature, $N, for your path now follows the taloned tracks of Ishamuhale.$B$BBegin the hunt.  Slay his favorite prey, a zhevra, then bring its carcass to the dead tree northwest of Ratchet.  Ishamuhale will smell the carcass and be drawn to it.$B$BBe ready when he comes.",
  },
  [883] = {
    ["T"] = "Lakota\'mani",
    ["O"] = "Bring the Hoof of Lakota\'mani to Jorn at Camp Taurajo.",
    ["D"] = "The Hoof of Lakota\'mani is a symbol of the strength of the great kodo.",
  },
  [884] = {
    ["T"] = "Owatanka",
    ["O"] = "Bring Owatanka\'s Tailspike to Jorn at Camp Taurajo.",
    ["D"] = "When touched, sparks fly from the tailspike of Owatanka.",
  },
  [885] = {
    ["T"] = "Washte Pawne",
    ["O"] = "Bring Washte Pawne\'s Feather to Jorn Skyseer at Camp Taurajo.",
    ["D"] = "The feather of Washte Pawne is painful to the touch.",
  },
  [886] = {
    ["T"] = "The Barrens Oases",
    ["O"] = "Speak with Tonga Runetotem at the Crossroads.",
    ["D"] = "The druids of Thunder Bluff sense a strange power leaking into the Barrens, east of Mulgore.$B$BTonga Runetotem was sent to discover the source of this power, but we fear he will need aid.$B$BTravel to the Barrens and speak with Tonga.  He will be at the Crossroads.  To reach there, take the road east out of Bloodhoof village.  Enter the Barrens, then turn north when the road forks.  Continue north and  you will find the Crossroads.$B$BAnd do not leave the road, $N, for the Barrens is a harsh land.",
  },
  [887] = {
    ["T"] = "Southsea Freebooters",
    ["O"] = "Kill 12 Southsea Brigands and 6 Southsea Cannoneers for Gazlowe in Ratchet.",
    ["D"] = "I sure wish Vice Admiral Grezzlik did a better job of keeping the seas safe for our trading vessels. With all the gold the trade princes have poured into the Trade Fleets, it ticks me off to see so many pirates running willy-nilly, raiding my ships and stealing my goods!$b$bI heard that the Southsea Freebooters have set up a camp just south of here. They\'ve been a real thorn in my side and if Grezzlik won\'t take care of them, well, maybe you can help me get rid of them.",
  },
  [888] = {
    ["T"] = "Stolen Booty",
    ["O"] = "Retrieve the Shipment of Boots and Telescopic Lens for Gazlowe in Ratchet.",
    ["D"] = "While I\'d like to recover all my goods, I\'m sure that\'s not possible. Still, there\'s a few things that I absolutely must have!$b$bAs you see, my observatory is ready to go, but it needs the smaller of the two lenses. The first arrived by caravan from Durotar, but the second was bound by ship, and never arrived. Also, I never got my boot shipment from Drizzlik in Booty Bay!$b$bHead down to the pirate camp and see if you can find them.",
  },
  [889] = {
    ["T"] = "Spirit of the Wind",
    ["O"] = "",
    ["D"] = "",
  },
  [890] = {
    ["T"] = "The Missing Shipment",
    ["O"] = "Bring Gazlowe\'s Ledger to Wharfmaster Dizzywig.",
    ["D"] = "I\'ve been waiting forever for my last shipment of goods from Booty Bay! I\'m pretty sure that it must have been stolen by the Freebooters, but just to make sure, will you go down to the dockside and ask Dizzywig if my goods were already put into my warehouse without my knowledge?$b$bHere, take my ledger down to Dizzywig and have him double check my inventory records against his logs.",
  },
  [891] = {
    ["T"] = "The Guns of Northwatch",
    ["O"] = "Captain Thalo\'thas Brightsun of Ratchet wants you to collect 10 Theramore Medals and slay Captain Fairmount, Cannoneer Whessan and Cannoneer Smythe.",
    ["D"] = "Yet another ship from my fleet has been lost to those overzealous humans at Northwatch Hold!$b$bThose bastards must be dealt with. Their paranoia has them in a state of frenzy. Trigger happy, reckless louts are shooting at anything in their sights.$b$bTravel south along the Merchant Coast to the Hold and lay siege to their forces. Bring me the medals of their soldiers as proof. And for the love of my fleet, slay Captain Fairmount and her overzealous cannoneers!",
  },
  [892] = {
    ["T"] = "The Missing Shipment",
    ["O"] = "Return Gazlowe\'s Ledger to Gazlowe in Ratchet.",
    ["D"] = "Everything looks square from my records, $n. Go on back and tell Gazlowe he\'s out of luck. I wouldn\'t be surprised if it was the pirates that nabbed his goodies.$b$bNothing else I can do for him. Oh... though you could tell him that I\'ve got some things from Undermine for him, when he has the time to come pick them up.",
  },
  [893] = {
    ["T"] = "Weapons of Choice",
    ["O"] = "Bring a Razormane Backstabber, a Charred Razormane Wand and a Razormane War Shield to Tatternack Steelforge at Camp Taurajo in the Barrens.",
    ["D"] = "The Razormane quilboars to the south, beyond the Field of Giants, have no skilled blacksmiths from what I am told, but they\'ve apparently started to develop sturdier weapons. I\'d like to get my hands on a few different types to learn their techniques if possible.$B$BIf you wish to help bring honor to the Horde, then bring me examples of their weapons. Find me a dagger from their stalkers, a wand from their seers, and a shield from their warfrenzy. That should do nicely!",
  },
  [894] = {
    ["T"] = "Samophlange",
    ["O"] = "Access the control console at the Venture Company research site.",
    ["D"] = "The Venture Company set up a small research facility far to the north of here, southwest of the Sludge Fen. I don\'t know very much about what they\'re doing, but did manage to discover that they\'re experimenting with something called a \"samophlange\".$b$bNow what the heck is a samophlange? Well, whatever it is, I want to examine it, so I need someone to go get it.$b$bI obtained a copy of their control system operating manual, you should be able to figure out how to disengage the samophlange from it.",
  },
  [895] = {
    ["T"] = "WANTED: Baron Longshore",
    ["O"] = "Bring the head of Baron Longshore to Gazlowe in Ratchet.",
    ["D"] = "WANTED!$b$bBaron Longshore, Captain of the Heedless$b$bCaptain Longshore of Gilneas leads vessels of the Southsea Freebooters, and is wanted on charges of piracy. Remains must be in identifiable condition!$b$b-- Gazlowe",
  },
  [896] = {
    ["T"] = "Miner\'s Fortune",
    ["O"] = "Retrieve the Cats Eye Emerald from one of the Venture Co. Overseers or Enforcers for Wharfmaster Dizzywig at Ratchet.",
    ["D"] = "Being Wharfmaster of a busy port like Ratchet, I keep my finger on the pulse of information. I know all about the exchange of goods and money between here and Booty Bay.$b$bThe latest bit of news I\'ve heard is about the Venture Company\'s Boulder Lode mine northeast of the Sludge Fen. One of the miners discovered an emerald the size of your fist. I know a few buyers who\'d be interested in getting their hands on something like that, and I\'d be willing to go half and half on its sale.",
  },
  [897] = {
    ["T"] = "The Harvester",
    ["O"] = "Bring the Harvester\'s Head to Jorn Skyseer.",
    ["D"] = "This strange, insect-like creature looks outside of the natural order, as though it does not belong in the Barrens.",
  },
  [898] = {
    ["T"] = "Free From the Hold",
    ["O"] = "Safely escort Gilthares Firebough back to Captain Brightsun in Ratchet.",
    ["D"] = "Finally! Someone to rescue me!$b$bI can\'t believe you got past the guards. These zealots from Theramore are out of their minds. They sank our ship and imprisoned me, the only survivor. I was called an enemy and threat to the Alliance.$b$bMe! A threat to their great sham of an Alliance? I was a deckhand on a moonshine transport between Ratchet and Booty Bay.$b$bEnough talk. Help me get back to Ratchet will you? Let me know when you\'re ready and we\'ll make our break.",
  },
  [899] = {
    ["T"] = "Consumed by Hatred",
    ["O"] = "Bring 60 Bristleback Quilboar Tusks to Mankrik at the Crossroads.",
    ["D"] = "Perhaps you know the pain of uncertainty, perhaps not, $c. But know this: I stand here every day, from the sun\'s rising till its decline, scouring the horizon for more of those monsters. I\'ve killed every pig man I\'ve come across since, but my thirst for their blood is far from quenched. Perhaps instead of heading north to the Crossroads I should have headed to Taurajo.$B$BYou, $c--you  could help me.$B$BKill them. Kill as many as you can. Bring me their tusks, and we will celebrate their deaths together.",
  },
  [900] = {
    ["T"] = "Samophlange",
    ["O"] = "Close off the Fuel Control Valve, the Regulator Valve and the Main Control Valve then use the control console again.",
    ["D"] = "The three control valves are currently opened. The various readings on the control panel lead you to presume that they must be shut off before the apparatus can be shut off.",
  },
  [901] = {
    ["T"] = "Samophlange",
    ["O"] = "Get the Console Key from Tinkerer Sniggles to use on the control console.",
    ["D"] = "With the apparatus deactivated, the control panel can be opened up and the samophlange removed from the console. With everything else in place, all that is left is to acquire the key to unlock the console.",
  },
  [902] = {
    ["T"] = "Samophlange",
    ["O"] = "Return the Samophlange to Sputtervalve in Ratchet.",
    ["D"] = "Pulling open the face of the control console reveals a dense jumble of wires, tubing and other strange mechanical objects--most of which seem to serve no practical purpose. Pushing them aside and digging deeper into the console, you find the samophlange. A tug removes it from its housing.",
  },
  [903] = {
    ["T"] = "Prowlers of the Barrens",
    ["O"] = "Collect 7 Prowler Claws from Savannah Prowlers for Sergra Darkthorn in the Crossroads.",
    ["D"] = "Now it\'s time to go after something that really bites.  The prowlers of the Barrens are a tough breed.  Tough, resourceful, sly... and deadly.  You\'ll find them among the tall grasses.  You\'ll find many to the southwest of the Crossroads.$B$BBe wary as you hunt them, $n, or you may find yourself no longer the predator, but instead the prey.",
  },
  [905] = {
    ["T"] = "The Angry Scytheclaws",
    ["O"] = "Kill Sunscale raptors and collect their feathers. Use the feathers on the 3 Scytheclaw nests. Return to Sergra Darkthorn in the Crossroads.",
    ["D"] = "Now, $N, the hunt deepens.  Now you must defeat your prey, and then find your way to its lair.$B$BHunt the sunscale raptors to the south.  Slay them and remove the feathers they wear.  Place the feathers on the scytheclaw nests southwest of the Stagnant Oasis. Show their brethren you do not fear them!",
  },
  [906] = {
    ["T"] = "Betrayal from Within",
    ["O"] = "Bring Lok\'s Skull to Thork at the Crossroads in the Barrens.",
    ["D"] = "Take Lok\'s head to Thork in the Crossroads, $r. <snort> He should know what has happened to my tribe without any words--ha, not that he would believe Mangletooth helped in <snort> such things. But I am sure he\'ll reward you for carrying out such a great deed. <snort>$B$BI will continue to bless you with Agamaggan\'s power for as long as I remain in this cage, $r. Until then, farewell.",
  },
  [907] = {
    ["T"] = "Enraged Thunder Lizards",
    ["O"] = "Bring 3 Thunder Lizard Blood to Jorn Skyseer at Camp Taurajo.",
    ["D"] = "Now, $N, you must face the great thunder lizards.  So large are the beasts, the ground rumbles as they stomp across the plains.  Take care as you fight them, for one false step and you may find yourself beneath their feet.$B$BYou will find the thunder lizards in the southern Barrens.  They are known as Stormsnouts, Stormhides and Thunderheads.  Return to me after you have bested them.",
  },
  [908] = {
    ["T"] = "Amongst the Ruins",
    ["O"] = "Bring the Fathom Core to Je\'neu Sancrea at Zoram\'gar Outpost, Ashenvale.",
    ["D"] = "The Twilight\'s Hammer has moved into the Moonshrine Ruins of Blackfathom Deeps.  Their presence can only serve to coerce the elements into working against us.  If left unchecked, this region will be theirs for good.$B$B$N, go into Blackfathom and find the ruin\'s fathom stone; it should be somewhere close in the water.  In it is a fathom core - a device that when properly read it will relate a history of all elemental activity.  If I have it, I and the Earthen Ring can maybe do something to stop them!",
  },
  [909] = {
    ["T"] = "Baron Aquanis",
    ["O"] = "Bring the Strange Water Globe to Je\'neu Sancrea at Zoram\'gar Outpost, Ashenvale.",
    ["D"] = "The defeated water elemental has left behind a strange water globe.  You surmise that the object somehow fed the beast energy.  Inside the globe, a putrid-looking form of water sloshes about.  The globe itself seems impervious to any sort of physical force.$B$BIf anyone can make any sense of this item, it would be Je\'neu Sancrea of the Earthen Ring.  He is located at the Horde\'s Zoram\'gar Outpost in Ashenvale.",
  },
  [910] = {
    ["T"] = "Down at the Docks",
    ["O"] = "Take your ward to the docks of Ratchet in the Barrens.",
    ["D"] = "I have heard tales of the mighty ships that sail between Ratchet and Booty Bay!  That would be so cool to sail on the ocean and fight in many great sea battles.  Maybe I will be a mighty Horde sailor when I grow up!  Yeah!$B$BWould you take me to see the Ratchet docks, please?  I promise I won\'t get into the water or cause any trouble - I just want to see the big boats and smell the salty sea water!  Yeah!",
  },
  [911] = {
    ["T"] = "Gateway to the Frontier",
    ["O"] = "Take your ward to the Mor\'shan Rampart in the northern part of the Barrens, just before entering Ashenvale.",
    ["D"] = "They say that Ashenvale is the new frontier, and that the Mor\'shan Rampart in the Barrens is the gateway to it.  That would be so cool to be a pioneer, carving everything you need out of the untamed wilds!  They say that this is where the Horde and Alliance will clash the most in the future, and I want to be there $N!$B$BWould you take me to the Mor\'shan Rampart so I can see what the frontier looks like, please?  I wanna see where the Horde is driving out those smelly night elves!",
  },
  [913] = {
    ["T"] = "Cry of the Thunderhawk",
    ["O"] = "Find and slay a Thunderhawk, return its wings to Jorn Skyseer at Camp Taurajo.",
    ["D"] = "The thunderhawk, $N, is a fierce beast.  It is time for you to face them.  You must find where it roams, and bring me its wings as proof of your successful hunt.$B$BDo this, and your time with me will near its end.",
  },
  [914] = {
    ["T"] = "Leaders of the Fang",
    ["O"] = "Bring the Gems of Cobrahn, Anacondra, Pythas and Serpentis to Nara Wildmane in Thunder Bluff.",
    ["D"] = "The druids in the Wailing Caverns, the Druids of the Fang, are an aberration.  They were part of an order of noble druids whose plan was to heal the Barrens, but now seek to remake that land to match their own, twisted dreams.$B$BThe Druids of the Fang have four leaders, and each possesses a dream gem.  Even now their faces haunt me!  Defeat the leaders and bring me their gems, and the Barrens may again know peace.$B$BGo, $N.  You will find them lurking deep within the Wailing Caverns.",
  },
  [915] = {
    ["T"] = "You Scream, I Scream...",
    ["O"] = "Get some Strawberry Ice Cream for your ward.  The lad seems to prefer Tigule and Foror\'s brand ice cream.",
    ["D"] = "ICE CREAM!  Oh please please please could you get me some ice cream?!  Strawberry is my favorite flavor, and there\'s no better strawberry ice cream in the world than Tigule and Foror\'s Strawberry Ice Cream!  It\'s my favorite ice cream in the whole wide world!$B$BI had it once a long time ago when I was at the Shimmering Flats race track, but I heard that they might be selling them in town now!  Please?  Pretty please?  With Tigule and Foror\'s Strawberry Ice Cream on top??!?!",
  },
  [916] = {
    ["T"] = "Webwood Venom",
    ["O"] = "Bring 10 Webwood Venom Sacs to Gilshalan Windwalker at Aldrassil.",
    ["D"] = "I came to Shadowglen to observe the webwood spiders that dwell in the Shadowthread Cave.  They are cousin to a much smaller variety of spider; I believe the world tree has had a profound effect on them, and I would like specimens to study to confirm this.$B$BFirst, I would like some of their venom.  Gather Webwood venom sacs from the spiders in and around the Shadowthread Cave, to the north.  I can then examine them for similarities with their smaller cousin\'s venom.",
  },
  [917] = {
    ["T"] = "Webwood Egg",
    ["O"] = "Bring a Webwood Egg to Gilshalan in Aldrassil.",
    ["D"] = "Now that I have the spiders\' venom, I\'d like some live specimens to study.  Unfortunately, capturing a living, giant spider is more than I can ask of you, young $c.  And a giant spider is more than I could handle myself!$B$BBut if you can find an unhatched egg, then delivering specimens will be much easier, and I can then arrange for the unhatched spiders to be contained.$B$BThere must be a nest deep in the Shadowthread Cave.  Please, search for an egg in the nest and return it to me.",
  },
  [918] = {
    ["T"] = "Timberling Seeds",
    ["O"] = "Bring 8 Timberling Seeds to Denelan at Lake Al\'Ameth.",
    ["D"] = "The timberlings of Teldrassil are elementals of nature.  In some ways they reflect the natural order of plants and animals on our great tree.$B$BSo it is disturbing to see how angry the timberlings have become.$B$BI believe it has something to do with the soil.  I have been working on different methods of nurturing plants and would like to try them on timberling seeds.  Please, can you gather seeds from timberlings around Lake Al\'Ameth and bring them to me?",
  },
  [919] = {
    ["T"] = "Timberling Sprouts",
    ["O"] = "Bring 12 Timberling Sprouts to Denalan at Lake Al\'Ameth.",
    ["D"] = "Small timberlings are sprouting around the waters of Lake Al\'Ameth.  I\'m afraid these sprouts are beyond help -- we should try to clear them from the land before they grow large enough to cause trouble.$B$BWhen you\'re wandering the lake, if you see any timberling sprouts please take them.  Help keep our land clean!",
  },
  [920] = {
    ["T"] = "Tenaron\'s Summons",
    ["O"] = "Speak with Tenaron Stormgrip atop Aldrassil in Shadowglen.",
    ["D"] = "$n, is it? I heard that Tenaron has been asking for you. You\'ll find him in the highest room atop Aldrassil.$b$bWe may live long lives, but it\'s best you not keep him waiting too long.",
  },
  [921] = {
    ["T"] = "Crown of the Earth",
    ["O"] = "Fill the Crystal Phial and bring it back to Tenaron Stormgrip atop Aldrassil.",
    ["D"] = "It is time for you to set out to seek your destiny, $n. But before you are ready to set out into the world beyond our enchanted forests, there is much you must learn about our recent history.$b$bMuch has changed with our people since the Battle of Mount Hyjal. Nordrassil lies a pale shadow of what it once was, its power used to defeat Archimonde and drive back the Burning Legion...$b$bThere is a task you must perform. Go to the moonwell north of Aldrassil and return me a phial of its water.",
  },
  [922] = {
    ["T"] = "Rellian Greenspyre",
    ["O"] = "Bring a Timberling Seed to Rellian Greenspyre in Darnassus.",
    ["D"] = "$N, can you take one of the seeds you brought me to my friend, Rellian Greenspyre?  He is a druid in Darnassus, and when last we spoke he revealed his interest in my work with the Timberlings.  He had some ideas himself, and he will appreciate a specimen seed to work with.$B$BThank you, $N.  You have been a great help to me.  I hope that, some day, you will see the fruit of my labors.$B$BYou will usually find Rellian walking the pathways of the Cenarian Enclave in Darnassus.",
  },
  [923] = {
    ["T"] = "Tumors",
    ["O"] = "Bring 5 Mossy Tumors to Rellian Greenspyre in Darnassus.",
    ["D"] = "There is a malevolence growing in the timberlings.  We are trying to find the source, but until we do...in order to keep Teldrassil safe we must cut down the timberlings who are beyond help.  Those that wander Wellspring Lake in northern Teldrassil are the most tainted.  They must be removed!$B$BDestroy the Timberlings you find there, and gather the mossy tumors growing upon them.  Bring the tumors to me so that they may be burned.",
  },
  [924] = {
    ["T"] = "The Demon Seed",
    ["O"] = "Grab a Flawed Power Stone.  Bring it to the Altar of Fire before the stone expires, then return to Ak\'Zeloth.",
    ["D"] = "The Demon Seed is a powerful tool for the Burning Legion. It rests in a cave on Dreadmist Peak, above a demonic altar of fire.$B$BI have prepared power stones that when placed near the Seed will disrupt its connection to the Legion.$B$BTake a stone from the table and bring it to the altar of fire atop Dreadmist Peak. Travel west, then north when the road forks. The Peak will be to your left. The safest climb to the top is on its northern face.$B$BAnd hurry--the stone\'s power will consume itself in time.",
  },
  [925] = {
    ["T"] = "Cairne\'s Hoofprint",
    ["O"] = "Travel to Thunder Bluff to see about getting Cairne Bloodhoof\'s hoofprint for your ward.",
    ["D"] = "They say that Cairne Bloodhoof is one of the greatest heroes the Horde has ever seen.  When I grow up, I wanna be a hero of the Horde too!$B$BDo you know Cairne, $N?  Could you do me a favor, please?  Could you get his hoofprint for me?  I dunno how to ask him, and he\'d probably be too busy for someone like me... but you!  You\'re an adventurer just like he is!  I bet he\'d give you his hoofprint easy!$B$BPlease?",
  },
  [926] = {
    ["T"] = "Flawed Power Stone",
    ["O"] = "",
    ["D"] = "",
  },
  [927] = {
    ["T"] = "The Moss-twined Heart",
    ["O"] = "Bring the Moss-twined Heart to Denalan at Lake Al\'Ameth.",
    ["D"] = "The heart of Blackmoss the Fetid is covered with a dark, oily moss.  In fact, the only way to tell this is a heart is from its slow, rhythmic beat... A beat that continues even now.$B$BPerhaps Denalan will want to see this heart.",
  },
  [928] = {
    ["T"] = "Crown of the Earth",
    ["O"] = "Bring the Partially Filled Vessel to Corithras Moonrage in Dolanaar.",
    ["D"] = "While there is more I could speak to you of the moonwells and of Teldrassil, I must send you along. Corithras Moonrage will be expecting you. I have poured the phial of water you brought to me into this vessel to bring to him.$b$bSeek out Corithras. You will find him at the moonwell in Dolanaar. Follow the road south from Aldrassil out of Shadowglen, and continue to follow the cobblestones as the road turns west.$b$bMind you stay on the road though, $n. There are dangerous beasts in the forests of late.",
  },
  [929] = {
    ["T"] = "Crown of the Earth",
    ["O"] = "Fill the Jade Phial and bring it back to Corithras Moonrage in Dolanaar.",
    ["D"] = "First, let me tell you more of the task you must complete. The druids in Darnassus use the water of the moonwells of Teldrassil, and their moonwell must be replenished from time to time. Using these specially crafted phials, you can collect the water of the moonwells.$b$bTake this vessel to the moonwell outside of Starbreeze Village to the east, and fill it with some of its waters, then return to me. When you have completed your task, I shall continue the story where Tenaron left off...",
  },
  [930] = {
    ["T"] = "The Glowing Fruit",
    ["O"] = "Bring the glowing fruit to Denalan at Lake Al\'Ameth.",
    ["D"] = "Hanging from beneath its fronds of this glowing plant is a large, round fruit.$B$BDenalan would want to study this fruit, you are certain.",
  },
  [931] = {
    ["T"] = "The Shimmering Frond",
    ["O"] = "Bring a Shimmering Frond to Denalan at Lake Al\'Ameth",
    ["D"] = "The fronds on this plant shimmer in the forest light, giving it a twisting, pulsing aura.$B$BYou believe that Denalan would want a specimen of this.",
  },
  [932] = {
    ["T"] = "Twisted Hatred",
    ["O"] = "Kill Lord Melenas and bring his head to Tallonkai Swiftroot in Dolanaar.",
    ["D"] = "I must warn you, $n, this matter must stay between us. The satyr are enough of an embarrassment to us already, and this one is much too close to home.$B$BHe is called Lord Melenas. He resides in the nearby cave of Fel Rock where he has gathered a large group of grell warriors. His heart is black as night, and he plots something most foul.$B$BYou must find him within his nearby cave just to the north of here, and bring me his head.",
  },
  [933] = {
    ["T"] = "Crown of the Earth",
    ["O"] = "Fill the Tourmaline Phial and bring it back to Corithras Moonrage in Dolanaar.",
    ["D"] = "There is another moonwell southeast of the entrance to Darnassus, on the shores of the Pools of Arlithrien. The Sentinels are having problems patrolling the area because of attacks and the growing ill-temperedness of the Gnarlpine furbolgs.$b$bBe wary as you seek out the well, and keep your weapons close at hand.",
  },
  [934] = {
    ["T"] = "Crown of the Earth",
    ["O"] = "Fill the Amethyst Phial and bring it back to Corithras Moonrage in Dolanaar.",
    ["D"] = "All was not well with Teldrassil, however. Staghelm\'s carefully made plans for the new World Tree had borne out as he had hoped, but there was one small problem, a problem to which many of the troubles on Teldrassil may be attributed.$b$bHowever, I will not get into that yet. You must visit the last moonwell, to the northwest in the Oracle Glade. Under the boughs of the Oracle Tree lies the first and most powerful of our wells. Retrieve a phial of its water and return to me.",
  },
  [935] = {
    ["T"] = "Crown of the Earth",
    ["O"] = "Bring the Filled Vessel to Arch Druid Fandral Staghelm in Darnassus.",
    ["D"] = "Without the blessings of Alexstrasza the Life-Binder and Nozdormu the Timeless, Teldrassil\'s growth has not been without flaw. Strange beasts have been reported arising from the very ground of the tree, and crazed furbolgs attack passing travelers.$b$bI can only hope that the solution the Arch Druid is looking for will be found quickly. I will pour all the phials you brought into this vessel, for you to deliver to Darnassus.$b$bBring it to Fandral Staghelm, you will find him in the druid grove.",
  },
  [936] = {
    ["T"] = "Assisting Arch Druid Runetotem",
    ["O"] = "Speak with Arch Druid Hamuul Runetotem in Thunder Bluff.",
    ["D"] = "Excuse me $c, but I\'d like just a moment of your time if possible.$B$BThe Cenarion Circle in Thunder Bluff is most interested in seasoned adventurers such as yourself to lend them aid in a vital research project.  While I do not know the specifics, I can inform you that none other than Hamuul Runetotem is spearheading this research.$B$BPlease - if you are interested, speak with him directly on the Elder Rise within Thunder Bluff proper.",
  },
  [937] = {
    ["T"] = "The Enchanted Glade",
    ["O"] = "Acquire 6 Bloodfeather Belts and bring them to Sentinel Arynia Cloudsbreak in the Oracle Glade.",
    ["D"] = "I was dispatched with a small group of Sentinels here to protect the Oracle Tree from the harpies that have made nests all around the glade. Little by little we are trying to push them back.$b$bWhen the Oracle Tree attempted to send a runner to Darnassus with a report, the messenger was attacked and killed by a group of the harpies.$b$bIf you feel up to the task, go to their nests, slay them, and return their belts to me as proof of your deeds.",
  },
  [938] = {
    ["T"] = "Mist",
    ["O"] = "Escort Mist to Sentinel Arynia Cloudsbreak at the moon well near the Oracle Tree.",
    ["D"] = "The sabercat\'s fur is a distinctive gray color, blending into the surrounding forest. As she lays there, you observe that she is badly wounded, with deep lacerations across her back and stomach.$b$bShe raises her head and gazes at you, bright intelligence shining in blue eyes. She seems to wish to follow you.",
  },
  [939] = {
    ["T"] = "Flute of Xavaric",
    ["O"] = "Bring the Flute of Xavaric and 5 Jadefire Felbind samples to Eridan Bluewind in southern Felwood.",
    ["D"] = "You study the simple wooden flute you picked up after defeating Xavaric.$B$BThe knotted wood feels solid, but it seems as if the spirals and patterns in the surface begin to spin and move as you turn it over in your hands. Suddenly, you feel a sense of dread...$B$BYou shake your head, sure that your mind is playing tricks on you.$B$BIt\'s possible that other satyrs carry clues as to what you have found. Bring your findings to the druid, Eridan Bluewind, that lives in southern Felwood.",
  },
  [940] = {
    ["T"] = "Teldrassil",
    ["O"] = "Deliver the Oracle Tree\'s report to Arch Druid Fandral Staghelm in Darnassus.",
    ["D"] = "The forest whispers of your feats of valor, $n. As I felt the harpies forced from their nests, a greater calm spread across the forest, its creatures once again feeling safe. I have feared sending another messenger to the Arch Druid, but I know that you will see my message safely to him.$b$bDeliver this to him and know that the forest will see you safely along its paths to the forest of stone.",
  },
  [941] = {
    ["T"] = "Planting the Heart",
    ["O"] = "Plant the Tainted Heart in Denalan\'s Planter.",
    ["D"] = "I have removed that foul moss from the heart, but it remains tainted...$B$BTry placing the heart in my planter.  It is filled with a nutritive soil that may cleanse and heal the heart.",
  },
  [942] = {
    ["T"] = "The Absent Minded Prospector",
    ["O"] = "Take the mysterious fossil to Archaeologist Flagongut in Menethil Harbor.",
    ["D"] = "This fossil bears a striking resemblance to one I once saw in Ironforge. An archaeologist by the name of Flagongut brought it to our annual Explorers\' League conference. He believed the fossil had a power which could be unlocked somehow.$b$bLast I heard Flagongut had holed up in the Deepwater Tavern in Menethil Harbor, awaiting escort to the Whelgar Excavation Site. Seek him out and show him Remtravel\'s fossil. Perhaps he can reveal the secrets of the past.$b$bThe ship to Menethil departs from Auberdine.",
  },
  [943] = {
    ["T"] = "The Absent Minded Prospector",
    ["O"] = "Archaeologist Flagongut in Menethil Harbor wants you to bring him the Stone of Relu and Flagongut\'s Fossil.",
    ["D"] = "Prospector Whelgar now holds the original fossil at his site in the Wetlands. When I discovered the Stone of Relu, I believed it to be the key to unlocking the mystery of the fossil.$b$bWhile I traveled on the road to meet Whelgar, I was attacked by raptors and the relic was lost. I don\'t know which one of the mottled beasts swallowed the relic but if you can retrieve it, I can unleash the power of these artifacts.$B$BBring me both the Stone of Relu, and the original fossil he holds.",
  },
  [944] = {
    ["T"] = "The Master\'s Glaive",
    ["O"] = "Gather information, then use the Phial of Scrying to create a Scrying Bowl.  Use the bowl to speak with Onu.",
    ["D"] = "It is strange that the naga are here, in Darkshore.  But stranger still...are the Twilight\'s Hammer.  That cult worships the old, old lords of the earth.  Lords defeated long ago.$B$BThe Master\'s Glaive, to the south, is a place where such an old lord fell.$B$BGo there and look for more Twilight\'s Hammer cultists.$B$BTake this phial of scrying.  Use it to create a scrying bowl after searching the Master\'s Glaive.  Then speak to me through the bowl.",
  },
  [945] = {
    ["T"] = "Therylune\'s Escape",
    ["O"] = "Help Therylune escape, then tell Therysil at the Shrine of Aessina that her sister is safe.",
    ["D"] = "Help!$B$BI was wandering around the Master\'s Glaive, and these dirty cultists surrounded me.  Lucky I\'m good at hiding!!$B$BCan you help me out of here?  And I need to send word to my sister Therysil that I\'m all right.  She\'s in Ashenvale to the south, at the Shrine of Aessina.",
  },
  [947] = {
    ["T"] = "Cave Mushrooms",
    ["O"] = "Bring 5 Scaber Stalks and 1 Death Cap to Barithras Moonshade in Auberdine.",
    ["D"] = "I am devising a potion that requires rare mushrooms, mushrooms that grow only in a certain cave.  The cave lies behind Cliffspring Falls, to the east and slightly north along the mountains.$B$BI would go there myself, but I was advised by the Grove of the Ancients to stay away from that place.  Our venerable allies sense that the cave is the hiding place for a new evil in Darkshore.$B$BPlease, $N, gather the mushrooms for me.  And while doing so, scout the cave to confirm the Ancients\' fears.",
  },
  [948] = {
    ["T"] = "Onu",
    ["O"] = "Speak with Onu at the Grove of the Ancients.",
    ["D"] = "Onu, an Ancient of Lore at the Grove of the Ancients, knows of your journey to Cliffwater Falls and wishes to speak with you.  The grove lies to the south, nestled near the mountains.$B$BThe Ancients are patient and wise, $N.  But if Onu seeks your counsel concerning what you saw in the falls, then I fear it is urgent.",
  },
  [949] = {
    ["T"] = "The Twilight Camp",
    ["O"] = "Find a clue in the Twilight\'s Hammer camp at the Master\'s Glaive.",
    ["D"] = "The Twilight\'s Hammer\'s purpose at the Master\'s Glaive is not known.  But it must be dark.$B$BSearch through their camp at the Glaive... Search for some thing that may teach us their intentions.$B$BFor we must learn their intentions before a plan is made.",
  },
  [950] = {
    ["T"] = "Return to Onu",
    ["O"] = "Bring Onu the recovered Insane Scribbles.",
    ["D"] = "Peering into the Twilight Tome is maddening.  Text begins to swim and dance after only a moment\'s glance, and once beautiful scrollwork twists into grotesque shapes.$B$BTearing away one page of insane scribbles is all that is possible before a swoon overtakes you.",
  },
  [951] = {
    ["T"] = "Mathystra Relics",
    ["O"] = "Bring 6 Mathystra Relics to Onu at the Grove of the Ancients.",
    ["D"] = "The parchment you found at the Master\'s Glaive is a page from an ancient text...written by elves!  It is heavily cursed and tainted by the corruption of the Twilight\'s Hammer, but it seems the cult is using ancient elven magic to bring their old masters back to our world.$B$BTravel to the ruins of Mathystra to the northeast, and search for ancient relics among the grass and broken stones.  Through their study, we may divine how the Twilight\'s Hammer intends to exploit the magic of the elves.",
  },
  [952] = {
    ["T"] = "Grove of the Ancients",
    ["O"] = "Deliver Fandral\'s message to Onu in the Grove of the Ancients in Darkshore, south of Auberdine.",
    ["D"] = "If you had enough time to run messages for the Oracle Tree, then I\'m sure that I can press you into service to deliver a message to the Grove of the Ancients in Darkshore, due south of Auberdine.$b$bYou will most likely have to secure transport on a hippogryph, but I have faith enough in you that you can manage that. Take this to Onu, the Ancient of Lore. He has been awaiting word from me, even as I waited for news from the Oracle Glade.",
  },
  [953] = {
    ["T"] = "The Fall of Ameth\'Aran",
    ["O"] = "Study the tablets which tell of Ameth\'Aran and of its fall, then return to Sentinel Tysha Moonblade in Darkshore.",
    ["D"] = "To the east you will find the ruins of Ameth\'Aran. It is now inhabited by the restless spirits of the Highborne that once dwelt within its walls, but once it was a place where the servants of Azshara freely practiced their powerful magics.$b$bI was sent to explore the ruins and came across two large tablets, scrawled with the stories of Ameth\'Aran and its fall. While I read the runes, I was accosted by the spirits, and fled.$b$bPlease, if you can, venture to the ruins and decipher the tablets in my place.",
  },
  [954] = {
    ["T"] = "Bashal\'Aran",
    ["O"] = "Find the source of the strange blue aura in the ruins of Bashal\'Aran.",
    ["D"] = "The ruins of Bashal\'Aran to the east are overrun with demonic minions. The sprites and satyrs that have taken up residence in the area feed upon the magical energies of the area, their powers growing from continued exposure.$b$bEven with that, I have noticed that there is one shrine they will not approach. On the western side of the ruins, atop a small bluff, a strange blue aura permeates... There must be an explanation to the demons\' reluctance.$b$bI would like you to investigate it.",
  },
  [955] = {
    ["T"] = "Bashal\'Aran",
    ["O"] = "Acquire 8 Grell Earrings for Asterion in Bashal\'Aran.",
    ["D"] = "If I were to relate the story of my life, I have no doubt it would surpass the limits of your patience. Let us say that mine has been a long and painful life, and this spectral form is perhaps the worst torment of all.$b$bI am held here by the means of magic. Though my words may seem disingenuous, I assure you I would be grateful beyond words if you would help me find the means of my imprisonment. A seal binds me, and by examining the earpieces of the sprites and grells, I may find a trace of it.",
  },
  [956] = {
    ["T"] = "Bashal\'Aran",
    ["O"] = "Obtain the Ancient Moonstone Seal and bring it to Asterion in Bashal\'Aran.",
    ["D"] = "If the grells have come into close contact with the seal that binds my eternal prison, I suspect I know the cause. No doubt the seal has come into the possession of the satyr that lead them.$b$bI feel strongly that this must be true, $n. One of the satyr must surely possess it. If you can obtain it, you would bring me so close to passing the bars of my prison that tears would come to my eyes.",
  },
  [957] = {
    ["T"] = "Bashal\'Aran",
    ["O"] = "Destroy the Ancient Moonstone Seal at the ancient flame in Ameth\'Aran, then return to Asterion in Bashal\'Aran.",
    ["D"] = "It was the craft of one of the most powerful of the Highborne that created the seal that formed my prison. In Ameth\'Aran, the ruins to the south that are twin to these, persists even today an ancient flame, blue in color. In this flame this seal could be destroyed.$b$bBe wary in the ruins, $n...",
  },
  [958] = {
    ["T"] = "Tools of the Highborne",
    ["O"] = "Retrieve 7 Highborne Relics for Thundris Windweaver in Auberdine.",
    ["D"] = "$C. A task I would ask of you.$b$bHave you seen the ruins of Ameth\'Aran? If not, you will find them on the eastern side of the main road, some ways to the south. Once they were the home of many powerful Highborne, now they are a testament to the destruction their dabblings produced.$b$bI have been told by the Sentinels that the spirits of the Highborne persist, and that they wield their ancient magical implements. Those relics must be appropriated, so that we might destroy them.",
  },
  [959] = {
    ["T"] = "Trouble at the Docks",
    ["O"] = "Crane Operator Bigglefuzz in Ratchet wants you to retrieve the bottle of 99-Year-Old Port from Mad Magglish who is hiding in the Wailing Caverns.",
    ["D"] = "Oops!$b$bHad a little mishap, as you can see! The morning cargo shipment from Booty Bay was huge. Huge I say! Never seen so many crates. Well, I was trying to expedite things so I started unloading a bit more than I could handle. That\'s when everything came tumbling down.$b$bBefore I knew what was going on, I saw that menace, Mad Magglish, go running off with a bottle of 99-year-old port. Chased him all the way to Wailing Caverns. No way I was going in there.$b$bMaybe you\'re brave enough to find him?",
  },
  [960] = {
    ["T"] = "Onu is meditating",
    ["O"] = "",
    ["D"] = "",
  },
  [961] = {
    ["T"] = "Onu is meditating",
    ["O"] = "",
    ["D"] = "",
  },
  [962] = {
    ["T"] = "Serpentbloom",
    ["O"] = "Apothecary Zamah in Thunder Bluff wants you to collect 10 Serpentbloom.",
    ["D"] = "The Royal Apothecary Society, based in the great Undercity of Lordaeron, has sent me here for a very specific service, $n. Perhaps you wish to aid me, and in turn The Dark Lady in our efforts to advance the Forsaken.$b$bRecently I studied a rare specimen of flora named Serpentbloom. I believe in greater quantities this herb has great potential.$b$bUnfortunately Serpentbloom can only be found in the darkest recesses of the Wailing Caverns, a dangerous cave system located in the Barrens.",
  },
  [963] = {
    ["T"] = "For Love Eternal",
    ["O"] = "Free the spirit of Anaya Dawnrunner and bring her pendant back to Cerellean Whiteclaw in Auberdine.",
    ["D"] = "In the aftermath of the battles at the Well of Eternity, I heard that Ameth\'Aran had been destroyed, its people dead, including my love, Anaya.$b$bI would never have thought, thousands of years later, that memories of Anaya would still haunt my dreams. Wandering the woods of Darkshore in a stupor, I found myself in the ruins of Ameth\'Aran... where I saw the haunted spirit of my beloved.$b$bShe must be freed, but I lack the heart to do it. Her spirit must be destroyed.",
  },
  [964] = {
    ["T"] = "Skeletal Fragments",
    ["O"] = "Bring 15 Skeletal Fragments to Apothecary Dithers at the Bulwark, Western Plaguelands.",
    ["D"] = "The key to Scholomance is called a Skeleton Key.  It must be forged from the remains of a skeleton - several actually - and hardened by only the strongest of metals within a suitable mold.  A signet of power from a being who naturally can open the portal to Scholomance will make the key ultimately function.$B$BFirst thing\'s first though, $N.  We\'ll need skeletal fragments for the key\'s forging.  Skeletons inside Andorhal should yield what we need, but the ones outside the walls might work too.",
  },
  [965] = {
    ["T"] = "The Tower of Althalaxx",
    ["O"] = "Find Balthule Shadowstrike near the Tower of Althalaxx in Darkshore.",
    ["D"] = "Hail, young $r. I am Elissa Starbreeze, and it is my charge to protect Auberdine from harm.$b$bTo this end, I sent Balthule Shadowstrike to observe the strange happenings around the Tower of Althalaxx to the northeast.$b$bIt is past time that he should have returned. I worry that he has encountered some unforeseen danger in the forest. I would be most appreciative if you would find him and see that he is doing well.",
  },
  [966] = {
    ["T"] = "The Tower of Althalaxx",
    ["O"] = "Collect 4 Worn Parchments for Balthule Shadowstrike near the Tower of Althalaxx.",
    ["D"] = "A group of warlocks has taken up residence around and inside the tower. I would have returned to Auberdine to report to Elissa earlier, but I was afraid that I might miss something while I was away.$b$bDelgren suspected such a company might be gathering at the tower, but did not know why.$b$bA few scraps of parchment that the warlocks carry have found their way to my hands, but I need more to complete the puzzle. I warn you not to venture into the tower, however, the warlocks there are much more powerful!",
  },
  [967] = {
    ["T"] = "The Tower of Althalaxx",
    ["O"] = "Deliver Balthule\'s letter to Delgren the Purifier in Ashenvale Forest.",
    ["D"] = "My master, Delgren the Purifier, is a paladin who has graciously offered his assistance in defending our forests from the forces of the demons and undead. He has taught me much about the Holy Light and the art of battle.$b$bDelgren must know of the cult\'s operations immediately.$b$bYou will find him far south of here at Maestra\'s Post in Ashenvale Forest. Be swift, I fear the Dark Strand\'s threat grows with each passing hour.",
  },
  [968] = {
    ["T"] = "The Powers Below",
    ["O"] = "Bring the Book: The Powers Below to Gerrig Bonegrip in the Forlorn Cavern, Ironforge.",
    ["D"] = "Although terribly worn and scratched, the title of this book can still be read upon its cover:$B$BThe Powers Below$B$BInside the book is a listing of unpronounceable names with foreboding titles, methods of worship, and preferred sacrifices...many of which include living, humanoid creatures.$B$BOn the inside cover of the book are the words: \"Bonegrip\'s Runes and Dooms, The Forlorn Cavern, Ironforge.  Proprietor: Gerrig Bonegrip.\"$B$BIf Bonegrip sells books like this, perhaps he\'ll buy this one back.",
  },
  [969] = {
    ["T"] = "Luck Be With You",
    ["O"] = "Collect 10 Frostmaul Shards for Witch Doctor Mau\'\'ari in Everlook.",
    ["D"] = "Ah, I can tell just by lookin\'\' at ya -- you be wantin\'\' to make a lucky charm. Mau\'\'ari always knows!$B$BTo da south. you\'\'ll find a canyon, an\'\' giants made a\'\' solid ice. The giants sometimes be carryin\'\' dem, but ya can always gather da shards yerself from da canyon. These shards have a powerful reflective quality.$B$BBring back 10 frostmaul shards, and my lucky charm will be yours.",
  },
  [970] = {
    ["T"] = "The Tower of Althalaxx",
    ["O"] = "Collect a Glowing Soul Gem and return it to Delgren the Purifier at Maestra\'s Post.",
    ["D"] = "Balthule\'s letter is dire. This Cult of the Dark Strand is a thorn in my side that must be removed. I have been dealing with some of the Dark Strand scum northeast of here at Ordil\'Aran. One of their number possesses a soul gem that I believe holds the secret to the cult\'s power.$b$bBring it to me, and I will be able to decipher the secrets held within.",
  },
  [971] = {
    ["T"] = "Knowledge in the Deeps",
    ["O"] = "Bring the Lorgalis Manuscript to Gerrig Bonegrip in the Forlorn Cavern in Ironforge.",
    ["D"] = "As you might know, I collect lore.  Old lore.  Powerful lore.  Lore that opens doorways, and lore that can awaken Those Who Sleep.$B$BThere are rumors of an old piece of lore, the Lorgalis Manuscript, at the bottom of Blackfathom Deeps in Ashenvale.  That place is the old dwelling of long-dead elves.  Elves who held great knowledge before their city fell to ruin.$B$BSearch Blackfathom Deeps for the manuscript.  Do this, and I will not forget it.  Not even after ... the end days.",
  },
  [972] = {
    ["T"] = "Water Sapta",
    ["O"] = "",
    ["D"] = "",
  },
  [973] = {
    ["T"] = "The Tower of Althalaxx",
    ["O"] = "Bring Ilkrud Magthrull\'s Tome to Delgren the Purifier at Maestra\'s Post.",
    ["D"] = "Ilkrud Magthrull. Yes, I have knowledge of him. He is a powerful orc warlock that makes his residence at the Fire Scar Shrine,  a place of great evil in southwestern Ashenvale.$b$bHe had been little more than an annoyance until now, and I had not dealt with him, but it seems that his time has come.",
  },
  [974] = {
    ["T"] = "Finding the Source",
    ["O"] = "Krakle in Un\'Goro Crater wants you to find the hottest area of Fire Plume Ridge.$B$BWhenever you find a hot spot, right click the thermometer to check the temperature. Keep looking until you find the hottest one.",
    ["D"] = "The hot springs here are a source of mystery to me. Where does the heat come from?$B$BLooking at how close the pools are to Fire Plume Ridge, I really wonder if the volcano might have something to do with it. I created a thermometer to take readings of the temperatures at the volcano, and I\'d like to find the hottest part. It gives the measurements in degrees Kraklenheit!$B$BHere, take it, and take readings of the temperatures at the ridge whenever you see a hot spot!",
  },
  [975] = {
    ["T"] = "Cache of Mau\'ari",
    ["O"] = "Speak to Witch Doctor Mau\'ari again in a moment to receive the lucky charm.",
    ["D"] = "Now, before I give ya dis charm, I better explain how ta use it.$B$BIf you carry da charm with ya, you\'ll find dat certain items will drop from creatures ya fight. If you don\'t have da charm with you, you won\'t find them. Understand?$B$BYou\'ll know these special items by the name -- they be called E\'ko. If you find any of these items, just bring dem to ol\' Mau\'ari, and she help ya out.$B$BWait here while I fix your charm.",
  },
  [976] = {
    ["T"] = "Supplies to Auberdine",
    ["O"] = "Speak with Delgren the Purifier at Maestra\'s Post after seeing Feero safely through Ashenvale Forest.",
    ["D"] = "Delgren has asked me to deliver messages and supplies to Sentinel Starbreeze in Auberdine, but I am loathe to leave on my own. Dangerous foes operate in the forest, and I do not wish to risk letting these supplies and information fall into the wrong hands.$b$bIf you and others could act as my guards, I could leave now. With your protection, I have no doubt that we can see these goods safely to Auberdine.",
  },
  [977] = {
    ["T"] = "Are We There, Yeti?",
    ["O"] = "Collect 2 Pristine Yeti Horns for Umi Rumplesnicker in Everlook.",
    ["D"] = "Now that I have what I need to cover the mechanical moving parts, I only need one other thing. Well, actually two. Two horns!$B$BThe ice thistle patriarchs and matriarchs have the largest horns, so of course, those are the ones I want. That type of yeti inhabits a cave to the southeast. There are plenty of them there; you shouldn\'t have any problem finding them.$B$BRemember, I only want the best looking ones -- no beat up or broken horns, please!",
  },
  [978] = {
    ["T"] = "Moontouched Wildkin",
    ["O"] = "Collect 10 Moontouched Feathers from Winterspring, then return to Erelas Ambersky in Rut\'theran Village.",
    ["D"] = "Wildkin feathers from the Hinterlands appear to contain traces of magical qualities. These powers... they don\'t seem to be wielded by the creatures, it\'s more like they are simply inherent to the species -- part of their very essence.$B$BDuring my studies, I heard that a large population of wildkin inhabit the snowy lands of Winterspring. Could these creatures have the same powers? Please help me find an answer, $N. Go to Winterspring and gather feathers that have dropped to the ground, then return to me.",
  },
  [979] = {
    ["T"] = "Find Ranshalla",
    ["O"] = "Find Ranshalla in eastern Winterspring.",
    ["D"] = "Yes, yes, these feathers seem to hold the same magical enchantment that I noticed previously... While I am not certain what we might find, I urge you to continue searching for answers, $N.$B$BA colleague of mine recently traveled into Winterspring to observe the wildkin there, in their own habitat. Why don\'t you catch up with her and see what she has found?",
  },
  [980] = {
    ["T"] = "The New Springs",
    ["O"] = "Travel to Winterspring and speak with Donova Snowden.",
    ["D"] = "Now, I\'ve heard about other hot springs in a place called Winterspring, far to the north of here. The strange thing is, there are no volcanoes anywhere near them. Interesting, I know.$B$BWell, if you want to find out more, you should head to Winterspring and speak to a friend of mine. Her name is Donova Snowden, and she is currently staying by the hot springs there.$B$BThanks again for helping me with my thermometer! See you later!",
  },
  [981] = {
    ["T"] = "The Tower of Althalaxx",
    ["O"] = "Talk to Delgren the Purifier at Maestra\'s Post.",
    ["D"] = "For all the work that you have done, you deserve to be well rewarded. Return to Delgren and tell him that Athrikus Narassin lies stilled by your hand, and the Cult of the Dark Strand is beaten and scattered.",
  },
  [982] = {
    ["T"] = "Deep Ocean, Vast Sea",
    ["O"] = "Recover the Silver Dawning\'s Lockbox and the Mist Veil\'s Lockbox for Gorbold Steelhand in Auberdine.  Both items should be found aboard the wreckage of the ships to the north of the village.",
    ["D"] = "Off the coast of Darkshore to our north are two wrecked ships - the Silver Dawning and the Mist Veil.  Some time ago, both ships ran afoul of the blasted murlocs as they sailed across the vast sea into Auberdine.  They now lie at the bottom of the ocean as trophies for those fiends.$B$BBoth captains didn\'t make it out that night, and their logs and other effects are still below in lockboxes.  I\'d like for you to recover them for us; it would mean a lot to the crew members still around these parts.",
  },
  [983] = {
    ["T"] = "Buzzbox 827",
    ["O"] = "Collect 6 Crawler Legs and place them in Buzzbox 827.",
    ["D"] = "Hey! Help me with my latest invention, the buzzbox. You can talk to people far away!$B$BMaybe you\'ve already seen them, they\'re boxes with levers on them. The only problem is they need constant maintenance.$B$BEach one suffers from a different problem, but I made a really smart decision. I placed each one near creatures that have the proper parts to fix that particular machine. Right now Buzzbox 827 is on the fritz. It\'s just south of Auberdine, real close. It takes 6 Crawler Legs to fix it. I\'ll pay ya...",
  },
  [984] = {
    ["T"] = "How Big a Threat?",
    ["O"] = "Find a corrupt furbolg camp in Darkshore and return to Terenthis in Auberdine.",
    ["D"] = "Some of my brethren were rescued from a corrupt furbolg in Teldrassil, and I\'ve vowed to stop any more atrocities before more of our kind are injured... or worse.$B$BI have seen a couple hints of corruption in Darkshore already, but I have yet to find any widespread signs. I think it would be logical if the investigation continued with the furbolgs. Would you find one of their camps, and return to me if you see any signs of corruption?",
  },
  [985] = {
    ["T"] = "How Big a Threat?",
    ["O"] = "Kill 8 Blackwood Pathfinders and 5 Windtalkers and return to Terenthis in Auberdine.",
    ["D"] = "You\'ve already proven adept at scouting our enemy, $N. Do you have what it takes to fight them as well?$B$BNot all adventurers prefer straightforward combat to the art of stealth and evasion.$B$BIf you think you\'re up for the task, then the furbolg camp south of Auberdine is currently the biggest threat to our people. You\'ll find some of the Blackwood tribe there. Kill 8 pathfinders and 5 windtalkers, and return to me here.",
  },
  [986] = {
    ["T"] = "A Lost Master",
    ["O"] = "Find 5 Fine Moonstalker Pelts and return them to Terenthis in Auberdine.",
    ["D"] = "$N, your skills have already helped me in my endeavor. Could I impose on you again to help Grimclaw and his master Volcor? In addition, I can craft you a magical cloak that will allow you to walk unhindered by Darkshore\'s creatures while you look for him.$B$BTo create the cloak, I\'ll need five fine moonstalker pelts from a moonstalker sire or matriarch to have enough material. The cats can be found near Wildbend River to the south, or even farther south near Ashenvale.",
  },
  [990] = {
    ["T"] = "Trek to Ashenvale",
    ["O"] = "Find Raene Wolfrunner in Ashenvale.",
    ["D"] = "$N, my mistress, Raene Wolfrunner, awaits you in the city of Astranaar within Ashenvale. With your help, perhaps we can at least ease some of the corruption that\'s taken hold there.$B$BHead south from here and stay close to the road--you\'ll find your way there without issue if you follow those directions.",
  },
  [991] = {
    ["T"] = "Raene\'s Cleansing",
    ["O"] = "Find Teronis in Ashenvale.",
    ["D"] = "$N, a longtime friend of mine is also aiding the Sentinels here in Ashenvale, but he has yet to return.$B$BHe had leads on finding an item that he thought could slow the furbolg attacks on our people--a rod created by a now-dead, evil wizard.$B$BBefore he left here, he mentioned seeking out a gem for the rod.$B$BHe mentioned the gem possibly being hidden at the shrine in Lake Falathim at the base of the mountain to the west. The gem was being held there before it was overrun.$B$BFind my friend, $N, please.",
  },
  [992] = {
    ["T"] = "Gadgetzan Water Survey",
    ["O"] = "Use the untapped dowsing widget near the pool of water by Sandsorrow Watch.  Once you have collected the sample, return the tapped dowsing widget to Senior Surveyor Fizzledowser in Gadgetzan.",
    ["D"] = "We\'ve got a position open for a temporary junior-grade surveyor in the Gadgetzan Water Company, if you\'re interested!$B$BWith the nomads seizing all our wells, we need to exploit more free sources of water!  There is water to be had in the desert, but only to those smart enough to survey them first.$B$BTake this dowsing widget and tap a sample of the water by the pool near Sandsorrow Watch.  It\'s right in sight of the trolls around there.  Bring the tapped widget back to me when you\'re done!",
  },
  [993] = {
    ["T"] = "A Lost Master",
    ["O"] = "Find Volcor in Darkshore and give him the Enchanted Moonstalker Cloak.",
    ["D"] = "The cloak is complete, $N. The time has come to find Volcor. I can only hope we are in time.$B$BThe cloak\'s magic, once invoked, will not last long; perhaps five minutes. It will be up to you to know when to use it: I would suggest waiting until you find Grimclaw.$B$BIf what he has told me is true, you will find his master in a cave south of here. Grimclaw will be alongside the road waiting for you. Signal to him by waving when you see him, and he\'ll point the way towards his master.",
  },
  [994] = {
    ["T"] = "Escape Through Force",
    ["O"] = "Protect Volcor until you reach the road, then speak to Terenthis in Auberdine.",
    ["D"] = "All right, $N, force it is. Let\'s get out of here as soon as you\'re ready. We\'ll fight our way down to the road--Grimclaw should still be there. Once there, we can part ways.$B$BTerenthis will want to hear your version of the story immediately, so head to Auberdine after we make it out of here.",
  },
  [995] = {
    ["T"] = "Escape Through Stealth",
    ["O"] = "Escape the Furbolg cave and meet Terenthis in Auberdine.",
    ["D"] = "All right then... if you\'re sure, we\'ll sneak out of here. Let us leave when you\'re ready.$B$BRemember, after you\'ve escaped, meet Terenthis back in Auberdine. I\'ll head there on my own since I\'ll probably be moving faster than you.",
  },
  [996] = {
    ["T"] = "Corrupted Windblossom",
    ["O"] = "",
    ["D"] = "",
  },
  [997] = {
    ["T"] = "Denalan\'s Earth",
    ["O"] = "Bring the package of Rare Earth to Denalan at Lake Al\'Ameth.",
    ["D"] = "Are you heading to the south?  To Lake Al\'Ameth?  If so, then I have a task I might ask of you...$B$BMy colleague Denalan has a camp along the eastern end of the lake, where he is studying and experimenting on the plant life of Teldrassil.  He requested a package of rare earths from Darnassus and it was late, only recently arriving here in Dolanaar.$B$BCan you take the package to him?",
  },
  [998] = {
    ["T"] = "Corrupted Windblossom",
    ["O"] = "",
    ["D"] = "",
  },
  [1000] = {
    ["T"] = "The New Frontier",
    ["O"] = "Speak with Arch Druid Hamuul Runetotem on the Elder Rise of Thunder Bluff about the Cenarion Circle\'s call to explore the frontiers of Kalimdor.",
    ["D"] = "Attention, heroes!$B$BThe Cenarion Circle seeks able bodied individuals to assist them in exploring the vast new frontiers of Kalimdor!  A forbidden wasteland of unimaginable dangers lies far to the west of Tanaris and Gadgetzan, and the Circle needs willing and honorable proxies to act in their stead.$B$BAll interested individuals should seek audience with the Cenarion Circle.  Speak with the Arch Druid Hamuul Runetotem on the Elder\'s Rise of Thunder Bluff for more information!",
  },
  [1001] = {
    ["T"] = "Buzzbox 411",
    ["O"] = "Collect 3 Thresher Eyes from Darkshore Threshers in the deep sea near Buzzbox 411.",
    ["D"] = "A tiny voice crackles from deep within the machine.$B$B\"Wizbang here! The next Buzzbox is north of Auberdine, on the beach$B$BSo, that Buzzbox is number 411 and it needs 3 Thresher Eyes for its repairs. They\'re just off the coast! The darkshore threshers... *Hic*... Sorry about that...$B$BJust like last time, when you give the Buzzbox the goods, it\'ll spit out your reward. *Hic*... What? No, I\'m fine!\"",
  },
  [1002] = {
    ["T"] = "Buzzbox 323",
    ["O"] = "Collect 6 Moonstalker Fangs and place them in Buzzbox 323.",
    ["D"] = "The Buzzbox gives a burst of static as you hear Wizbang begin to speak.$B$B\"The next Buzzbox is number 323. Ish north of Auberdine but... where\'s the ale? Wha? Oh, ish on the road sho ya shouldn\'t have trouble finding it near the bridge.\"$B$BWizbang mumbles something incoherent before you hear a loud gulping sound.$B$B\"Thish one needs Moonstalker Fangs... 6 of \'em.\"",
  },
  [1003] = {
    ["T"] = "Buzzbox 525",
    ["O"] = "Collect 4 Grizzled Scalps from Grizzled Thistle Bears to the south of Auberdine and place them in Buzzbox 525.",
    ["D"] = "Over the boiling noises and dripping sounds coming from the machine, you hear Wizbang\'s wavering voice call out.$B$B\"Nexsht onesh far south down the road. Shouldn\'t be hard to find... jusht off to the shide... near a fensh. I think I hid it in shum bushesh... Bushessh. Hehehe... Yesh, Buzzbox 525 takes grizzled scalps from thoshe grizzled thistle bears. Boy thash a mouthful. Grizzshled... Hehehe...\"$B$BYou hear more gulping sounds as the machine cuts out. Then, suddenly, it bursts back to life.$B$B\"4!\"",
  },
  [1004] = {
    ["T"] = "The New Frontier",
    ["O"] = "Speak with Arch Druid Hamuul Runetotem on the Elder Rise of Thunder Bluff about the Cenarion Circle\'s call to explore the frontiers of Kalimdor.",
    ["D"] = "Attention, heroes!$B$BThe Cenarion Circle seeks able bodied individuals to assist them in exploring the vast new frontiers of Kalimdor!  A forbidden wasteland of unimaginable dangers lies far to the west of Tanaris and Gadgetzan, and the Circle needs willing and honorable proxies to act in their stead.$B$BAll interested individuals should seek audience with the Cenarion Circle.  Speak with the Arch Druid Hamuul Runetotem on the Elder\'s Rise of Thunder Bluff for more information!",
  },
  [1007] = {
    ["T"] = "The Ancient Statuette",
    ["O"] = "Bring the Ancient Statuette to Talen, in his camp near the Zoram Strand.",
    ["D"] = "I would charge you with a task, $N.$B$BI was on my small vessel, skimming over the submerged ruins of Zoram, when naga attacked me, surging from the water and tearing at me with their claws!  I fled, carrying what supplies I could to make this meager camp.$B$BBut when I reached the shore and ran... my prized possession was lost.$B$BPlease, $N, find the site of my ambush, along the coast to the north, and search for an ancient statuette.  It is the reason I have braved the dangers of the Zoram Strand.",
  },
  [1008] = {
    ["T"] = "The Zoram Strand",
    ["O"] = "Bring 20 Wrathtail Heads to Shindrell Swiftfire in Astranaar.",
    ["D"] = "There is evil lurking on our northwestern coast, known as the Zoram Strand.$B$BIt is the resting place of the doomed city of Zoram, destroyed during the Sundering and submerged beneath the seas.  It has been lost to the night elves for ages.  Lost, and nearly forgotten.$B$BNow, the naga have returned, and for what reason we do not know.  But reason matters little; we must slay these fiends and throw them back to the depths!$B$BReturn to me when your mission is complete.",
  },
  [1009] = {
    ["T"] = "Ruuzel",
    ["O"] = "Bring the Ring of Zoram to Talen near the Zoram Strand.",
    ["D"] = "I fear the ancient statuette\'s secrets will remain forever hidden unless you find the key to its ancient lock.$B$BThat key is a ring, the Ring of Zoram.  It belonged to the rulers of this city before the waves of the Sundering drowned it.$B$BFor years the ring was lost, but the naga have recently found it.$B$BTheir leader, Ruuzel, dwells on an isle on the northern edge of Zoram.  Please, $N, retrieve the ring for me!",
  },
  [1010] = {
    ["T"] = "Bathran\'s Hair",
    ["O"] = "Bring 5 Bathran\'s Hair to Orendil Broadleaf in Ashenvale.",
    ["D"] = "There is a plant that once grew in the old ruins of Bathran\'s Haunt to the north.  The plant was called Bathran\'s Hair and was known to cure ailments of the spirit.$B$BThere is a sick child in the village, and I believe it is more than just a physical illness.  Will you go to Bathran\'s Haunt and search it for Bathran\'s Hair?  I may need it to properly treat the child.$B$BBathran\'s Haunt lies north of Maestra\'s Post and just south of the border to Darkshore.",
  },
  [1011] = {
    ["T"] = "Forsaken Diseases",
    ["O"] = "Bring a Bottle of Disease to Kayneth Stillwind in Forest Song.",
    ["D"] = "The Forsaken, the undead arm of the Horde, have crept into Ashenvale, lurking at our ruins and our barrows.  We believe their goal is to spread disease amongst the lands of the night elves.  We must confirm this!$B$BThe Forsaken have a camp south of here, near the Dor\'Danil Barrow Den, where they create poisons and diseases and place them in bottles.  Fight your way into that camp and steal a bottle of disease.  Return with such a bottle.  I will test it and find its purpose.",
  },
  [1012] = {
    ["T"] = "Insane Druids",
    ["O"] = "Kill the druids: Taneel Darkwood, Uthil Mooncall, Mavoris Cloudsbreak; then return to Kayneth Stillwind in Forest Song.",
    ["D"] = "When I struggled with the Forsaken\'s poisons, I had a vision.  I had a vision of the druids in the Dor\'danil Barrow Den, poisoned... murdered by the undead!  They are now errant ghosts, severed from their bodies and driven mad.$B$BSo that the druids\' spirits might some day find peace, you must enter the Dor\'danil Barrow Den and destroy its once great leaders: Taneel Darkwood, Uthil Mooncall and Mavoris Cloudsbreak.$B$BDo this, then return to me.",
  },
  [1013] = {
    ["T"] = "The Book of Ur",
    ["O"] = "Bring the Book of Ur to Keeper Bel\'dugur at the Apothecarium in the Undercity.",
    ["D"] = "$N, Shadowfang Keep holds a book, the Book of Ur, which would be much prized in my collection.  Ur was a great mage of Dalaran before the coming of the Scourge, his studies in other worlds are of much value to ... certain parties among the Forsaken.$B$BEnter Shadowfang Keep and find the book.  Bring it to me, and I will report your service to our Dark Lady...",
  },
  [1014] = {
    ["T"] = "Arugal Must Die",
    ["O"] = "Kill Arugal and bring his head to Dalar Dawnweaver at the Sepulcher.",
    ["D"] = "Arugal still resides in Shadowfang Keep. We cannot claim Silverpine as a strategic stronghold for the Dark Lady until Arugal is slain.$b$bI shall see to it that his magic is eradicated, $n. But I leave it in your hands to see that Arugal meets the death he so deserves.$b$bTravel to Shadowfang Keep and put an end to Arugal\'s foul spells once and for all. Bring to me the vile wizard\'s head!",
  },
  [1015] = {
    ["T"] = "The New Frontier",
    ["O"] = "Speak with Arch Druid Fandral Staghelm at the Cenarion Enclave of Darnassus about the Cenarion Circle\'s call to explore the frontiers of Kalimdor.",
    ["D"] = "Attention, heroes!$B$BThe Cenarion Circle seeks able bodied individuals to assist them in exploring the vast new frontiers of Kalimdor!  A forbidden wasteland of unimaginable dangers lies far to the west of Tanaris and Gadgetzan, and the Circle needs willing and honorable proxies to act in their stead.$B$BAll interested individuals should seek audience with the Cenarion Circle.  Speak with the Arch Druid in Darnassus, Fandral Staghelm, for more information!",
  },
  [1016] = {
    ["T"] = "Elemental Bracers",
    ["O"] = "Collect 5 Intact Elemental Bracers and use the Divining Scroll on them. Afterwards, bring the Divined Scroll to Sentinel Velene Starstrike at the Silverwind Refuge.",
    ["D"] = "Take this divining scroll--it can determine the creator of these elementals. But you will need something from them to use as a reagent to focus scroll\'s magic--their bracers should do nicely.$B$BA few intact bracers from the creatures should be enough. The other bracers will be useless for our purposes, but they may be worth something to a merchant if you wish to sell them. Once you have the intact bracers attempt to use the scroll. Return to me with the results and we shall proceed from there.",
  },
  [1017] = {
    ["T"] = "Mage Summoner",
    ["O"] = "Slay Sarilus Foulborne and bring his head to Sentinel Velene Starstrike in Silverwind Refuge.",
    ["D"] = "There\'s only one undead mage in the Barrens capable of defiling this many water elementals. He\'s also the only one foolish enough to try. Sarilus Foulborne is probably sitting at the top of Dreadmist Peak, laughing at me.$B$B$n, you must go into enemy territory and destroy this miscreant. I have had enough of his idiocy and we must show him what happens to those who annoy the Sentinels.$B$BDestroy him and bring me his head. Be wary though, the Horde may be watching for you.",
  },
  [1018] = {
    ["T"] = "The New Frontier",
    ["O"] = "Speak with Arch Druid Hamuul Runetotem on the Elder Rise of Thunder Bluff about the Cenarion Circle\'s call to explore the frontiers of Kalimdor.",
    ["D"] = "Attention, heroes!$B$BThe Cenarion Circle seeks able bodied individuals to assist them in exploring the vast new frontiers of Kalimdor!  A forbidden wasteland of unimaginable dangers lies far to the west of Tanaris and Gadgetzan, and the Circle needs willing and honorable proxies to act in their stead.$B$BAll interested individuals should seek audience with the Cenarion Circle.  Speak with the Arch Druid Hamuul Runetotem on the Elder\'s Rise of Thunder Bluff for more information!",
  },
  [1019] = {
    ["T"] = "The New Frontier",
    ["O"] = "Speak with Arch Druid Fandral Staghelm at the Cenarion Enclave of Darnassus about the Cenarion Circle\'s call to explore the frontiers of Kalimdor.",
    ["D"] = "Attention, heroes!$B$BThe Cenarion Circle seeks able bodied individuals to assist them in exploring the vast new frontiers of Kalimdor!  A forbidden wasteland of unimaginable dangers lies far to the west of Tanaris and Gadgetzan, and the Circle needs willing and honorable proxies to act in their stead.$B$BAll interested individuals should seek audience with the Cenarion Circle.  Speak with the Arch Druid in Darnassus, Fandral Staghelm, for more information!",
  },
  [1020] = {
    ["T"] = "Orendil\'s Cure",
    ["O"] = "Bring Orendil\'s Cure to Pelturas Whitemoon in Astranaar.",
    ["D"] = "$N, I have created a cure that I believe will help the afflicted child.  Take it to Astranaar and give it to the child\'s parent, Pelturas Whitemoon.$B$BTo reach Astranaar, follow the road south, then east.",
  },
  [1021] = {
    ["T"] = "Vile Satyr! Dryads in Danger!",
    ["O"] = "Find Anilia in Xavian.",
    ["D"] = "Listen, I don\'t know who you are, but I need your help. My sisters went to take back the Branch of Cenarius from the satyr. They said they\'d be back soon, but it\'s been two whole days!$B$B$n, you have to find them! They were looking for the ruins of a night elf temple in Xavian to the northwest. I don\'t know what to think. Look for Anilia, she was leading them on the search.",
  },
  [1022] = {
    ["T"] = "The Howling Vale",
    ["O"] = "Go to the Howling Vale and study the Tome of Mel\'Thandris, then return to Sentinel Melyria Frostshadow at Forest Song.",
    ["D"] = "Though we have put many resources and much effort into driving the remaining demons from the Felwood to the north, our successes have been few. We have been able to keep much of the demonic presence from Ashenvale.$b$bTo the north, near the Felwood border, the ruined shrine of Mel\'Thandris has been overtaken by mysterious wolf-men. Their chilling calls have led the area to be known as the Howling Vale. The Tome of Mel\'Thandris kept at the shrine may shed some light on why these wolf-men have come.",
  },
  [1023] = {
    ["T"] = "Raene\'s Cleansing",
    ["O"] = "Find the Glowing Gem and return to Raene Wolfrunner in Astranaar with Teronis\' Journal.",
    ["D"] = "As you rummage through Teronis\' belongings, you find a journal that is still in good condition.$B$BYou take the journal and leave Teronis\' corpse as is.$B$BThe only thing left to do is find the gem Teronis was seeking. Raene mentioned it being in the area. Perhaps the murlocs have it...",
  },
  [1024] = {
    ["T"] = "Raene\'s Cleansing",
    ["O"] = "Find Shael\'dryn at the moonwell to the northeast.",
    ["D"] = "You\'ll have to do the research yourself, but from what notes Teronis has made, it sounds like your next course of action is to find the other pieces of the rod Dartol created. I would start by heading to the moonwell east of Iris Lake. When you\'re ready, follow the road and turn north when you reach a small sitting area on your left.$B$BA dryad there named Shael\'dryn is better suited to guide you. Seek her out.$B$BKeep the gem with you--Shael\'dryn will need it if she\'s to help you recreate the rod.",
  },
  [1025] = {
    ["T"] = "An Aggressive Defense",
    ["O"] = "Kill 1 Den Watcher, 2 Ursas, 10 Totemics, and 12 Warriors of the Foulweald tribe, and then return to Raene Wolfrunner in Astranaar.",
    ["D"] = "Before you travel to the moonwell I have another task that will aid the people of Ashenvale if you would like to test your skills.$B$BAlong the road heading east before the Falfarren River, furbolgs have been attacking travelers. Their tortured minds feeding upon fear, and their appetites satiated by the blood of innocents.$B$BAid the Sentinels in protecting the lands by showing those monsters the same mercy they have shown our kind.$B$BI believe their camps are south of the road... be careful.",
  },
  [1026] = {
    ["T"] = "Raene\'s Cleansing",
    ["O"] = "Find the Wooden Key and a piece of Dartol\'s Rod before returning to Shael\'dryn at the moonwell.",
    ["D"] = "I wish your tidings were better.$B$BRaene is correct, I know about Dartol\'s creation and the treants in the area--at least I did once. They\'ve become corrupted now, their nature twisted.$B$BIf your goal is to find the next piece of the rod, then you\'ll have to find the key to open the chest it\'s in.$B$BThe wooden key is on one of the treants northeast of here, near Felwood. Once you have it, look for a small glade west of the road near the Felwood border... the chest should be hidden around there.",
  },
  [1027] = {
    ["T"] = "Raene\'s Cleansing",
    ["O"] = "Find the final piece of Dartol\'s Rod and return to Shael\'dryn at the moonwell.",
    ["D"] = "The final piece of the rod was given to the druids of Dor\'danil for safekeeping. I have no idea where they hid it. I\'m sorry I can\'t help you more than that.$B$BAll I can tell you is to follow the road that leads towards the Barrens. The druids took shelter to the east of that road, but they won\'t be of much help to you: they were killed recently... it\'s something I don\'t like to think about much.$B$BWhen you find the final piece, bring it to me and I will help you reconstruct the rod.",
  },
  [1028] = {
    ["T"] = "Raene\'s Cleansing",
    ["O"] = "Find the hidden shrine and re-enchant Dartol\'s Rod.",
    ["D"] = "Here is the completed rod, $N. Reconstructing it was simple... it\'s finding the shrine that has the power to recharge it that will be the hard part.$B$BWith some focus and effort it shouldn\'t be too hard for someone like you, who\'s already accomplished so much.$B$BHead southeast of here--there are very few paths, so be careful--and keep your eyes to the north. The shrine is hidden within the mountains that surround this glade.$B$BOnce there, place the rod within the shrine... the rest is magic. Hehe.",
  },
  [1029] = {
    ["T"] = "Raene\'s Cleansing",
    ["O"] = "Return to Raene Wolfrunner in Astranaar.",
    ["D"] = "I suggest you find Raene and let her know the rod is finished. I\'m sure she\'ll know what your next course should be.$B$BGood luck, $N.",
  },
  [1030] = {
    ["T"] = "Raene\'s Cleansing",
    ["O"] = "Find an uncorrupted furbolg, and using the power of Dartol\'s Rod of Transformation, speak to him.",
    ["D"] = "Alas, I\'m not completely certain what he had in mind next.$B$BIf what he said was true about the rod\'s power, then it will allow you to take on another form altogether. Which form, I do not know.$B$BPerhaps he meant to use its powers to get closer to the tainted furbolgs, but even the uncorrupted creatures would slaughter him were he not careful. Transformed by the rod, Dartol would have to speak to a furbolg who was safe from the effects of Fel Moss.$B$BI would start your search southeast of Mystral Lake.",
  },
  [1031] = {
    ["T"] = "The Branch of Cenarius",
    ["O"] = "Kill Geltharis and return the Branch of Cenarius to Illiyana at Forest Song.",
    ["D"] = "Anilia puts her hands on your shoulders and draws you close. Gasping, she points towards the arch to the north, before her eyes close and her last wheezing breath leaves her body. \"He has taken it... Geltharis...\" You know in your heart that Anilia\'s dying wish is to see the Branch of Cenarius back in dryad hands.",
  },
  [1032] = {
    ["T"] = "Satyr Slaying!",
    ["O"] = "Bring 16 Satyr Horns to Illiyana in Forest Song.",
    ["D"] = "I can\'t believe that Anilia is gone... We used to play together in the forests all the time. I almost feel... sad.$B$BI know something that might make me feel better. Please, slay the satyrs that took Anilia from me! Bring me their horns as proof of your actions and you will forever have my gratitude.",
  },
  [1033] = {
    ["T"] = "Elune\'s Tear",
    ["O"] = "Bring Elune\'s Tear to Pelturas in Astranaar.",
    ["D"] = "I had hoped Orendil\'s cure would save Relara, but I fear it only slowed the disease running through my child.  We must find more powerful medicine!  Thankfully, Orendil spoke to me of further remedies to try should his first one fail.$B$BElune\'s Tear is a type of stone that rests in only one place: the isle on Iris Lake, east of Astranaar.  Orendil said the stone will wash away corruption... please, $N, bring me Elune\'s Tear and pray it will end my child\'s sickness!",
  },
  [1034] = {
    ["T"] = "The Ruins of Stardust",
    ["O"] = "Bring 5 Handfuls of Stardust to Pelturas in Astranaar.",
    ["D"] = "Elune\'s Tear washed clear some of my daughter\'s sickness.  Her nightmares are lessened and I thank the goddess for her blessing.  But ... fever still rages within her.$B$BOrendil told me of a frosted dust found among the Ruins of Stardust.  Perhaps it will abate Relara\'s fever.  Gather some of the dust, then please return to me.$B$BThe Ruins of Stardust lie to the south, $N.  May Elune speed your journey.",
  },
  [1035] = {
    ["T"] = "Fallen Sky Lake",
    ["O"] = "Retrieve a Fallen Moonstone from the Shadethicket Oracle, then bring it to Pelturas in Astranaar.",
    ["D"] = "Relara\'s fever has passed, but she is still very weak.  I do not believe time alone will give her back the strength she has lost.  I believe it will take the power of a moonstone.$B$BWe used to gather moonstones at Fallen Sky Lake to the southeast... until the Shadethickets came.$B$BTheir leader, a Shadethicket Oracle, gathered up all the fallen moonstones and hoards them.  Now, you must retrieve a moonstone from the Oracle.$B$BAnd to do that, I fear that you must slay him.",
  },
  [1036] = {
    ["T"] = "Avast Ye, Scallywag",
    ["O"] = "Speak with Fleet Master Firallon aboard the Crimson Vale off the coast of Stranglethorn Vale.",
    ["D"] = "$N... yeah, I\'ve heard of you.  I\'ve heard that you\'re no friend of Booty Bay - just like us Bloodsail Buccaneers.  Fleet Master Firallon is looking for hale and hearty $g men : women; just like you for a special mission.$B$BIf you\'re interested, head on out to the Crimson Veil and speak with him below decks.  She\'s anchored off the Wild Coast east of here, next to the Riptide and the Damsel\'s Luck.",
  },
  [1037] = {
    ["T"] = "Velinde Starsong",
    ["O"] = "Speak with Thyn\'tel Bladeweaver at the Warrior\'s Terrace in Darnassus.",
    ["D"] = "Velinde Starsong was my predecessor here in Ashenvale Forest. At first it seemed she had the situation in Felwood under control, but little by little her efforts faltered. One day, she simply disappeared.$b$bI was sent here to continue her work. I\'m afraid I know nothing of the priestess, however. Perhaps Thyn\'tel Bladeweaver, one of the commanders of the Sentinels, knows further details of her disappearance that I was not a party to.$b$bSurely she will understand the import of such information.",
  },
  [1038] = {
    ["T"] = "Velinde\'s Effects",
    ["O"] = "Search through Velinde\'s chest for her journal, then return it along with the key to Thyn\'tel Bladeweaver in Darnassus.",
    ["D"] = "The Tome of Mel\'Thandris showed you this? I suppose there would be little harm in allowing you to examine her belongings. This key will allow you to open the chest where we stored her things in the Sentinels\' bunkhouse. She kept a journal of her duties, if there is anything to be learned, it will be from that.$b$bI should tell you, the Sentinels believe that she had her own reasons for leaving, and expect that she could return at any time. The priestess has done much in the past to earn our trust.",
  },
  [1039] = {
    ["T"] = "The Barrens Port",
    ["O"] = "Speak with Wharfmaster Dizzywig in Ratchet.",
    ["D"] = "Ratchet is the only port in the Barrens. Most likely Velinde found a trading vessel in Ratchet to take her to Blackwater Cove in Azeroth. We\'ve had limited dealings with the goblins that run the port, but the master of the dock should have information about the comings and goings of ship passengers.$b$bFollow the road southeast through Ashenvale and you will find yourself in the Barrens. Watch your step, $n, warriors of the Horde patrol the land. You will be safe at the port, though.",
  },
  [1040] = {
    ["T"] = "Passage to Booty Bay",
    ["O"] = "Take a boat to Booty Bay and speak with Caravaneer Ruzzgot.",
    ["D"] = "Ah yes, finally found it. Should have told me she passed through here that long ago. Let\'s see.$b$bVelinde. Booked passage to Booty Bay on the Black Osprey. I don\'t have anything here saying otherwise, so I\'d assume it arrived in port safely.$b$bNot much more help I can be to you, but she asked about overland travel over on that side of the world, and I mentioned Ruzzgot, a caravan driver based out of Booty Bay. Might be that this Velinde traveled with him.$b$bMove along, now. I haven\'t all day for you.",
  },
  [1041] = {
    ["T"] = "The Caravan Road",
    ["O"] = "Speak with Clerk Daltry in Darkshire.",
    ["D"] = "Turns out I was wrong about you, and that isn\'t something that happens everyday.$b$bIt just so happens that I remember this Velinde you\'re looking for. Isn\'t every day that a night elf priestess that wants to travel with a dirty old--but great for your shipping needs!--caravan like my own.$b$bWe split up on the way north, she was headed for Darkshire. The clerk there keeps all sorts of records. Might know something useful.$b$bBe careful in the jungle, it is a deadly place even at the best of times.",
  },
  [1042] = {
    ["T"] = "The Carevin Family",
    ["O"] = "Speak with Jonathan Carevin in Darkshire.",
    ["D"] = "No, I don\'t have any records of a Velinde Starsong staying in Darkshire... though, if you don\'t mind me saying, I can hardly imagine a night elf priestess taking a room in the inn, if you get my meaning?$b$bThese wolf-men you mentioned though, that\'s something I\'ve heard about. Just the other day, Calor came into town with a string of their heads. He works with the Carevin family. Hunters of demons, undead, and other monstrosities. Speak with Jonathan, he\'s the head of the household.",
  },
  [1043] = {
    ["T"] = "The Scythe of Elune",
    ["O"] = "Look for signs of the Scythe of Elune then return to Jonathan Carevin in Darkshire.",
    ["D"] = "Your story rings true... I do not entirely understand your motives, but if your business here in Duskwood involves ridding the forest of worgen in any number, then I can forgo understanding for results.$b$bThere is a mine to the south that has been overrun with worgen... They appeared out of nowhere, but from what we know, that is where they first were found.$b$bGo about your business, but I would ask that if you find anything of import, you share it with me. We will accept any aid in our war against evil.",
  },
  [1044] = {
    ["T"] = "Answered Questions",
    ["O"] = "Return to Thyn\'tel Bladeweaver in Darnassus.",
    ["D"] = "We shall rein in the worgen problem, have no worry of that. This evil that your friend introduced to our woods will be contained, and I bear her no ill will for her actions. Strange events are afoot in these times, $n, and the darkness knows no respite.$b$bI will keep you no longer. I suspect that there are others that should hear of what you have found in the mines of Duskwood.",
  },
  [1045] = {
    ["T"] = "Raene\'s Cleansing",
    ["O"] = "Kill Ran Bloodtooth and 4 Bloodtooth Guards and return to Krolg near Lake Mystral.",
    ["D"] = "The night elves are as old as time, but their arrogance destroys their very world. And now it destroys our lives too. But that can no longer be controlled...$B$BI blame the elves for much, but we are still responsible for our actions. Even beyond the corruption, we are capable of great evil.$B$BRan Bloodtooth is one such evil. Power hungry, lusting... the corruption has only made him stronger. My hatred for the elves burns, but I do not wish ill upon their children, so we must police our own.",
  },
  [1046] = {
    ["T"] = "Raene\'s Cleansing",
    ["O"] = "Bring Ran Bloodtooth\'s Skull and Dartol\'s Rod of Transformation to Raene Wolfrunner in Astranaar.",
    ["D"] = "Return to your own tribe and let them know of your victory here.$B$BShow them your trophy, and bask in the glory...",
  },
  [1047] = {
    ["T"] = "The New Frontier",
    ["O"] = "Speak with Arch Druid Fandral Staghelm at the Cenarion Enclave of Darnassus about the Cenarion Circle\'s call to explore the frontiers of Kalimdor.",
    ["D"] = "Attention, heroes!$B$BThe Cenarion Circle seeks able bodied individuals to assist them in exploring the vast new frontiers of Kalimdor!  A forbidden wasteland of unimaginable dangers lies far to the west of Tanaris and Gadgetzan, and the Circle needs willing and honorable proxies to act in their stead.$B$BAll interested individuals should seek audience with the Cenarion Circle.  Speak with the Arch Druid in Darnassus, Fandral Staghelm, for more information!",
  },
  [1048] = {
    ["T"] = "Into The Scarlet Monastery",
    ["O"] = "Kill High Inquisitor Whitemane, Scarlet Commander Mograine, Herod, the Scarlet Champion and Houndmaster Loksey and then report back to Varimathras in the Undercity.",
    ["D"] = "Whilst the Dark Lady tends to the pressing issue of the Lich King\'s advance to the north, I am seeing to it that matters closer to home are being taken care of accordingly.$b$bNo doubt, the human pests who call themselves the Scarlet Crusade are proving to be the sharpest thorn in our side domestically. Tirisfal Glades is literally crawling with the little red ants.$b$bTravel to the Monastery, $c, and lay waste to High Inquisitor Whitemane and her top lieutenants.",
  },
  [1049] = {
    ["T"] = "Compendium of the Fallen",
    ["O"] = "Retrieve the Compendium of the Fallen from the Monastery in Tirisfal Glades and return to Sage Truthseeker in Thunder Bluff.",
    ["D"] = "The Earthen Ring had pure intentions towards our plagued brethren. But who is to know the motivations of those whispering in the ears of the Elder Council?$b$bThe Forsaken whom we have allied with have a history wrought with deceit. Too hasty was our pact. Perhaps Cairne would have been wise to heed the warnings from Orgrimmar.$b$bDeep within the Scarlet Monastery lies the Compendium of the Fallen, guarded by crazed human zealots. Their method is forged from insanity but their research might prove useful.",
  },
  [1050] = {
    ["T"] = "Mythology of the Titans",
    ["O"] = "Retrieve Mythology of the Titans from the Monastery and bring it to Librarian Mae Paledust in Ironforge.",
    ["D"] = "The future is inevitable! We hurl ourselves towards it like falling raindrops to the Great Sea.$b$bBut perhaps, by studying the past, we can know what to expect and be better prepared for the unknown.$b$bBefore dire troubles fell upon Lordaeron, there was a bastion of knowledge tucked away in the Tirisfal Glades. It is rumored that a book called Mythology of the Titans was housed in the library of the old Monastery.$b$bBring it to me and perhaps together we can unlock the secrets of the past!",
  },
  [1051] = {
    ["T"] = "Vorrel\'s Revenge",
    ["O"] = "Return Vorrel Sengutz\'s wedding ring to Monika Sengutz in Tarren Mill.",
    ["D"] = "Even though my will is free, I can serve the Dark Lady no more. I feel my time on Azeroth has come to an end. My life force is shattered.$b$bInterrogator Vishas did this to me! Tortured me for weeks! Took my wedding ring and gave it to his wife. The bastard!$b$bRevenge... I want revenge!$b$bThe pompous fool bragged about his precious wife, Nancy, who lived along Lake Lordamere. Track the wench down, slay her and return my wedding ring to my wife, Monika in Tarren Mill.$b$bHeed my last wishes....",
  },
  [1052] = {
    ["T"] = "Down the Scarlet Path",
    ["O"] = "Take Brother Anton\'s Letter of Commendation to Raleigh the Devout in Southshore.",
    ["D"] = "We of the Scarlet Crusade lay claim to strongholds from Hearthglen to Tirisfal Glades. We are quite proud of our bastions of cleansing throughout Lordaeron.$b$bYou have proven yourself against the undead in Desolace. But the true threat of the plague lies in Lordaeron.$b$bTravel to the town of Southshore, in the Eastern Kingdoms. Seek out a crusader named Raleigh the Devout. Give him this letter of commendation bearing my seal and he will escort you to a place of honor in our Scarlet Monastery.",
  },
  [1053] = {
    ["T"] = "In the Name of the Light",
    ["O"] = "Kill High Inquisitor Whitemane, Scarlet Commander Mograine,  Herod, the Scarlet Champion and Houndmaster Loksey and then report back to Raleigh the Devout in Southshore.",
    ["D"] = "I once served the Scarlet Crusade with honor, loyalty and pride. I believed their cause to be a noble one: to rid Azeroth of the undead.$b$bBut as I spent time at the Monastery in Tirisfal Glades I realized that their grasp on reality was slipping. They now think everyone is plagued who doesn\'t wear the tabard of the Crusade. Innocent men and women were tortured because they were supposedly plagued. $b$bThe Scarlet Crusade must be crushed in the name of the Light. You, $n, must destroy the deranged regime.",
  },
  [1054] = {
    ["T"] = "Culling the Threat",
    ["O"] = "Bring Dal Bloodclaw\'s Skull to Raene Wolfrunner in Astranaar.",
    ["D"] = "Directly to the north of Astranaar are the Thistlefur furbolg. My scouts report that their numbers have grown greatly over the past few months. If they grow too great, they may find the courage to attack Astranaar directly.$B$BKill as many of their kind as you can, but bring me their chieftain\'s skull as proof that their efforts have been stifled... for now.$B$BI shall remain here in Astranaar and await word from you. Elune be with you.",
  },
  [1055] = {
    ["T"] = "Raene\'s Cleansing",
    ["O"] = "Return to Shael\'dryn at the moonwell.",
    ["D"] = "The rod, now complete, lies in your hands. Shael\'dryn will want to hear of your success.",
  },
  [1056] = {
    ["T"] = "Journey to Stonetalon Peak",
    ["O"] = "Travel to Keeper Albagorm on Stonetalon Peak.",
    ["D"] = "The forest spirits tell me you are brave and willing to travel.$b$bTo the south, not far from Mystral Lake, there lies a tunnel named The Talondeep Path. Through this tunnel you will come to an area known as Windshear Crag in the Stonetalon Mountains. Once there, journey to the southwest past Cragpool Lake and then north, up the steep slope until you reach Stonetalon Peak.$b$bThere awaits Keeper Albagorm. Heed his bidding, $r.$b$bThe journey will be perilous.",
  },
  [1057] = {
    ["T"] = "Reclaiming the Charred Vale",
    ["O"] = "Keeper Albagorm on Stonetalon Peak wants you to slay 7 Bloodfury Harpies, 7 Bloodfury Ambushers, 7 Bloodfury Slayers and 7 Bloodfury Roguefeathers.",
    ["D"] = "As a protector of the forest spirits you can undoubtedly guess why I am alarmed.$b$bYou have traveled through these mountains to reach this peak. Surely you have seen the travesty which has befallen the land. Greed has led to the destruction of these sacred hills.$b$bThe Charred Vale to the south was set afire by the greedy mechanized army of industrialists. Worse yet, the Bloodfuries that have encroached on the land refuse us passage in to begin regrowth.$b$bDrive the wretched harpies out, $n!",
  },
  [1058] = {
    ["T"] = "Jin\'Zil\'s Forest Magic",
    ["O"] = "Witch Doctor Jin\'Zil at Malaka\'jin wants 5 portions of Stonetalon Sap, 5 Twilight Whiskers, 30 Courser Eyes and a Fey Dragon Scale.",
    ["D"] = "So excited I am!$b$bThree little cages for three little elves! But what shall we do with our lovely tree-hugging friends? Why, let\'s give them a taste of... what do they call it... forest magic!$b$bI\'ll need a good mess of Stonetalon sap. From those horrid felines, bring me some twilight whiskers. This calls for plenty of courser eyes, of course -- can never have enough eyes. Oh, and lest we forget, a precious scale from a fey dragon.$b$bHurry to Stonetalon Peak, $n, so I can brew some forest magic!",
  },
  [1059] = {
    ["T"] = "Reclaiming the Charred Vale",
    ["O"] = "Travel to Falfindel Waywarder on the border of Thousand Needles and Feralas.",
    ["D"] = "The Bloodfuries have been subdued. But they will regroup soon enough if action is not taken.$b$bFar south of the Barrens lies a land known as Thousand Needles. If you travel west in Thousand Needles until you reach the lush forest\'s edge of Feralas, you will find a druid named Falfindel Waywarder.$b$bThis great and wise druid is capable of calling forth great forest spirits which can aid in the reclamation of the Charred Vale.$b$bSend word of our need! Seek him out at once!",
  },
  [1060] = {
    ["T"] = "Letter to Jin\'Zil",
    ["O"] = "Deliver Darsok\'s letter to Jin\'Zil within his cave in Malaka\'Jin, in Stonetalon.",
    ["D"] = "You\'ve done so well for me, $n. You\'re a regular lucky rabbit\'s foot and there\'s something I want you to see.$B$BThe harpies are not from around the Barrens. They come from a place called Stonetalon, far to the northwest of here. Though it is probably a dangerous place for you, I need this letter delivered to an old friend of mine who lives there, a witch doctor by the name of Jin\'Zil.$B$BHe\'ll be happy to know that Serena Bloodfeather is dead, and I think you\'ll find Stonetalon an interesting place.",
  },
  [1061] = {
    ["T"] = "The Spirits of Stonetalon",
    ["O"] = "Speak with Seereth Stonebreak in the Stonetalon Mountains.",
    ["D"] = "The spirits of the Stonetalon Mountains are angered, for goblins and their servants are plundering and burning the land.  So the land has cried out, and those who listen for such things... have heard.$B$BSeereth Stonebreak, a promising shaman of the Horde, was sent to Stonetalon.  She reports massive deforestation at the hands of the goblins, and requests aid.$B$BMeet Seereth in Stonetalon.  To reach her, follow the road west of the Barrens\' Crossroads.  She is camped near the Greatwood Vale.",
  },
  [1062] = {
    ["T"] = "Goblin Invaders",
    ["O"] = "Kill 15 Venture Co. Loggers, then return to Seereth Stonebreak on the border of Stonetalon and the Barrens.",
    ["D"] = "The goblin-run Venture Company moved into the Stonetalon Mountains, chopping down trees and burning huge stretches of forest.  The spirits of this place are nearly blind with pain and rage.  We must stop the Venture Company!$B$BHead northwest, beyond the Greatwood Vale and into the Windshear Crag.  You will find the goblins and their servants at work--show them the Horde will not allow the exploitation of Stonetalon.  Show them in the language they best understand...$B$BViolence.",
  },
  [1063] = {
    ["T"] = "The Elder Crone",
    ["O"] = "Speak with Magatha in Thunder Bluff.",
    ["D"] = "To truly tend the spirits of Stonetalon we must consult the tauren elders.$B$BI do not know many of their leaders\' names, but I do know of one: Magatha.  I have heard that she is very old, and she knows much.  If anyone can marshal spiritual aid from the tauren, it is she.$B$BMagatha resides in the tauren capital of Thunder Bluff, on Elder Rise -- the city\'s northeastern bluff.  Speak with Magatha, and tell her of the peril of Stonetalon Mountains.",
  },
  [1064] = {
    ["T"] = "Forsaken Aid",
    ["O"] = "Speak with Apothecary Zamah in the Pools of Vision in Thunder Bluff.",
    ["D"] = "The tauren have a bond with the land, and it pains me to hear of Stonetalon\'s plight.  But I fear that to heal the land, we must first remove the disease upon it.$B$BIs it not fortunate, then, that the Forsaken are allied with us?  They know much of disease.  I believe they can aid us, and by doing so they will strengthen the trust between our people.$B$BSpeak with Apothecary Zamah.  She is a scholar and an emissary of the Forsaken.  You will find her in the Pools of Vision below the Spirit Rise.",
  },
  [1065] = {
    ["T"] = "Journey to Tarren Mill",
    ["O"] = "Bring Zamah\'s Note to Apothecary Lydon in Tarren Mill.",
    ["D"] = "To rid Stonetalon of the Venture Company, we must take extreme measures.  Extreme, but without bloodshed.$B$BA colleague of mine may have just the tool we need.  His name is Lydon; he dwells in the town of Tarren Mill in the Hillsbrad Foothills, in the distant land of Lordaeron.$B$BTo reach Hillsbrad, travel by zeppelin to our capital, the Undercity.  Then go north to Tirisfal Glades, southwest to Silverpine, and south to Hillsbrad.$B$BSeek Lydon and bring him this note.  It details our needs to him.",
  },
  [1066] = {
    ["T"] = "Blood of Innocents",
    ["O"] = "Bring 5 Vials of Innocent Blood to Apothecary Lydon in Tarren Mill.",
    ["D"] = "I will create a toxin to bleed the violence from the Venture Company, rendering them weak and docile.  That should fix the Horde\'s problem in Stonetalon, yes?$B$BBut to create the toxin, I will need the blood of innocents, a difficult ingredient to obtain.$B$BFortunately the Syndicate, a group of human brigands in this area, employ shadow mages... and those mages collect innocent blood for their own, unsavory purposes.$B$BYou can find the Syndicate shadow mages in Durnholde Keep to the southeast.",
  },
  [1067] = {
    ["T"] = "Return to Thunder Bluff",
    ["O"] = "Bring Lydon\'s Toxin to Apothecary Zamah in Thunder Bluff.",
    ["D"] = "Here is the toxin.  If you use it against the Venture Company it should produce favorable, perhaps even contagious, results.  I must say, it is quite exciting to see my work applied in the field!$B$BBut before using it, take it to Apothecary Zamah.  She must create a delivery method for the toxin to maximize its effect.",
  },
  [1068] = {
    ["T"] = "Shredding Machines",
    ["O"] = "Kill the shredders XT:4 and XT:9, then return to Seereth Stonebreak at the border of the Stonetalon Mountains and the Barrens.",
    ["D"] = "There is a relation to the spiritual magic of shaman and the natural magic of druids.  This is why we shaman can hear the plea of the spirits of Stonetalon.  Many of their whisperings are lost to me, but one message, one vision, is clear...$B$BI saw a vision of two man-shaped machines in Windshear Crag, northwest of Greatwood Vale, felling trees with impossible speed.  These metal beasts must be stopped!$B$BIn my vision, the machines were marked with strange symbols:$B$BXT:4 and XT:9.",
  },
  [1069] = {
    ["T"] = "Deepmoss Spider Eggs",
    ["O"] = "Bring 15 Deepmoss Eggs to Mebok Mizzyrix in Ratchet.",
    ["D"] = "Spider egg omelettes are a new fad in Booty Bay!  The problem is... Booty Bay\'s got no eggs.$B$BI smell an opportunity!$B$BIn Windshear Crag and Sishir Canyon, in the Stonetalon Mountains to the northwest, lives the deepmoss spider.  Bring me its eggs and I\'ll pay a bundle!$B$BIn Windshear Crag, the eggs will be clustered under what trees remain.$B$BIn Sishir Canyon... well, if you have a weak stomach then maybe you should just collect the eggs at Windshear Crag...",
  },
  [1070] = {
    ["T"] = "On Guard in Stonetalon",
    ["O"] = "Find Kaela Shadowspear overlooking the valley entering Windshear Crag in the Stonetalon Mountains.",
    ["D"] = "Hello, $N.$B$BYou seem to be full of wanderlust by the looks of you. Have you been to the Stonetalon Mountains? It is a land plagued by the Venture Co. and other enemies of the night elves.$B$BRecently, Sentinel Wolfrunner sent my friend Kaela Shadowspear there to investigate what\'s happening to the land, but I have yet to hear word back from her. I\'m even more concerned because the Alliance sent a gnome mage along with her... a sign of trouble for sure.$B$BWould you find her and report back to me?",
  },
  [1071] = {
    ["T"] = "A Gnome\'s Respite",
    ["O"] = "Kill 10 Venture Co. Loggers, and 10 Venture Co. Deforesters and return to Gaxim Rustfizzle in Stonetalon.",
    ["D"] = "You see, $N, I\'m a member of the Enlightened Assembly of Arcanology, Alchemy and Engineering Sciences, and it\'s my goal... nay, my DUTY! to show the night elves that a blend of magic, mixtures, and mechanisms can help save their forests.$B$BBut it\'s a hard fought war, and the goblins reproduce like rabbits it seems. I haven\'t even had time to build my newest inventions!$B$BI need some time to plan and organize. But to do that, I need your help keeping the goblins at bay in Windshear Crag to the east...",
  },
  [1072] = {
    ["T"] = "An Old Colleague",
    ["O"] = "Speak to Lomac Gearstrip in Ironforge.",
    ["D"] = "The device I\'m thinking about is my most advanced version to date. But we\'ll need a special potion if it\'s to work. I\'m thinking we might as well get the good stuff since this mission could be your last.$B$BAnd for that, we\'re going to need some potent explosives: Nitromirglyceronium.$B$BThe only person who can make NG-5 is an old friend of mine: Lomac Gearstrip. Look for him in Tinker Town.$B$BYou talk him into making us some NG-5, I\'ll get to work on placement for my devices.",
  },
  [1073] = {
    ["T"] = "Ineptitude + Chemicals = Fun",
    ["O"] = "Bring 4 Minor Mana Potions and 2 Elixirs of Minor Fortitude to Lomac Gearstrip in Ironforge.",
    ["D"] = "Hahaha... you need Nitromirglyceronium to stop goblins from destroying a forest?!$B$BI don\'t care if you know Gaxim or not... I\'ll give you the stuff just to see you or them explode into itty bitty pieces.$B$BThere\'s a problem though: I\'m all out.$B$BBut I\'ll tell you what, you bring me the correct potions, and I\'ll make some up for you right away.$B$BThe least you can do is prove you\'ve got some skill as an alchemist before I just give you a sample of my greatest creation.",
  },
  [1074] = {
    ["T"] = "Ineptitude + Chemicals = Fun",
    ["O"] = "Bring the Nitromirglyceronium back to Gaxim Rustfizzle in Stonetalon.",
    ["D"] = "Here you go, stranger. Take heed with it though... it\'s volatile stuff.$B$BIf you\'re really a friend of Gaxim\'s, then tell him I say hello. If you\'re not, then have fun blowin\' yerself up.",
  },
  [1075] = {
    ["T"] = "A Scroll from Mauren",
    ["O"] = "Find Collin Mauren in Stormwind.",
    ["D"] = "Okay, now for the remote detonation part of my plan... oh, I haven\'t explained that yet, have I?$B$BOkay, so the short of it is this: with a little magic, I can make it so the explosives go off while you\'re not around. I\'m putting together a device that\'ll allow you to push just one button and have the NG-5 explode when you\'re ready.$B$BBut I need a spell from a friend in Stormwind--Collin Mauren is his name. You can find him in the mage quarter there. He\'s human, so he shouldn\'t be hard to miss.",
  },
  [1076] = {
    ["T"] = "Devils in Westfall",
    ["O"] = "Bring Dust Devil Debris to Collin Mauren in Stormwind.",
    ["D"] = "Ah, I think I have it. A messaging spell... I believe that\'s the effect he\'s looking for.$B$BI\'ll have to create a scroll with the spell impressed upon it. To do that I\'ll need debris from a dust devil.$B$BWhile I gather the rest of the components, why don\'t you head to Westfall and find one of the foul creatures? They are dangerous, but I\'m sure it\'s nothing you can\'t handle if you\'re willing to subject yourself to one of Gaxim\'s experiments.$B$BI\'ll be here when you return.",
  },
  [1077] = {
    ["T"] = "Special Delivery for Gaxim",
    ["O"] = "Bring the Scroll of Messaging to Gaxim Rustfizzle in Stonetalon.",
    ["D"] = "Here you are, $N. This should do nicely.$B$BPlease give my regards to Gaxim when next you see him. I look forward to hearing about this adventure when it sees its completion.$B$BFare well.",
  },
  [1078] = {
    ["T"] = "Retrieval for Mauren",
    ["O"] = "Bring 8 Crystalized Scales to Collin Mauren in Stormwind.",
    ["D"] = "Travelers keep asking me about the Stonetalon Mountains. It seems to be a popular place for adventure--it doesn\'t matter if you\'re seeking wyvern, elementals, or you have business with the Venture Co.$B$BWithin the Charred Vale, deep in Stonetalon, there used to be a species of basilisks whose scales, when ground to dust, made a wonderful reagent for some spells I\'ve created.$B$BIf those basilisks still live, I would love to have a few of their scales.$B$BTake your time, it is no rush, but I can pay well.",
  },
  [1079] = {
    ["T"] = "Covert Ops - Alpha",
    ["O"] = "Retrieve the Venture Co.\'s Engineering Plans from the lumber mill in Windshear Crag.",
    ["D"] = "This is it, $N. Pay close attention. The devices are done, and ready to be used.$B$BTake these pages--they\'ll explain more about the detonators and explosives, and where you should place them.$B$BRemember, the success of your mission doesn\'t depend on how much destruction you cause. Plan Alpha\'s success depends on you bringing back the Venture Co.\'s engineering plans from inside the lumber mill south of Cragpool Lake.",
  },
  [1080] = {
    ["T"] = "Covert Ops - Beta",
    ["O"] = "Retrieve the Venture Co. Letters from Windshear Mine at the east end of Windshear Crag.",
    ["D"] = "Plan Beta\'s just like Plan Alpha, $N.$B$BWe cause a distraction and then steal their precious letters.$B$BThe key is to get their letters... remember that.$B$BThe pages I gave you already will tell you where to plant the explosives. Just get back here alive and with those letters....",
  },
  [1081] = {
    ["T"] = "Reception from Tyrande",
    ["O"] = "Find Tyrande Whisperwind in Darnassus.",
    ["D"] = "$N, I am sure Tyrande Whisperwind herself would like to thank you for taking part in this mission.$B$BIt may not appear so on the surface, but what you\'ve helped start could very well strengthen our foothold in the Stonetalon Mountains.$B$BIf you consider yourself our ally against these atrocities, go see Tyrande in the Temple of the Moon in Darnassus.",
  },
  [1082] = {
    ["T"] = "Update for Sentinel Thenysil",
    ["O"] = "Return to Sentinel Thenysil in Ashenvale.",
    ["D"] = "$N, you have done a great deed by lending a hand here.$B$BThe Sentinels of Ashenvale will reward you well if you informed them that Gaxim and I are alive. Tell them we are well on our way to stopping the Venture Co. and healing the lands.$B$BFind Tara Thenysil in Astranaar; she will be pleased to see you\'ve returned with good news.",
  },
  [1083] = {
    ["T"] = "Enraged Spirits",
    ["O"] = "Bring 8 Smoldering Embers from any of the fire elementals in the Charred Vale to Kaela Shadowspear in Stonetalon.",
    ["D"] = "I\'m unsure what the Venture Co. did to the Vale, but it\'s obvious they were overpowered by the creatures there.$B$BThey set the forest aflame, and used their inventions of destruction to decimate as much of the Vale as possible.$B$BBut in their ignorance they also released something else: burning spirits, elementals of flame, stone and rage.$B$BThe land has no chance to see Elune\'s smile while these creatures roam free.$B$BBring me their smoldering embers to show you\'ve reduced their numbers.",
  },
  [1084] = {
    ["T"] = "Wounded Ancients",
    ["O"] = "Bring a piece of Corroded Shrapnel to Kaela Shadowspear in Stonetalon.",
    ["D"] = "It seems obvious to me that the protectors of the Vale--the treants--had something to do with the Venture Co. being driven back. Unfortunately, even those once great guardians have now become corrupt.$B$BGaxim thinks their corruption is somehow linked to the weapons the goblins used during their retreat.$B$BI told him I\'d collect a sample from a wounded treant and allow him to study it in order to figure out if that\'s true.$B$BBring me some of the shrapnel so the gnome can tell me more...",
  },
  [1085] = {
    ["T"] = "On Guard in Stonetalon",
    ["O"] = "Speak to Gaxim.",
    ["D"] = "The situation here in Stonetalon is obviously grave for a night elf and gnome to be working together.$B$BThe Venture Co. has devastated these lands and I\'ve spent most of my time putting an end to the destruction.$B$BDo not judge Gaxim prematurely; he understands the need for us to work together.$B$BIf you wish to aid us, then start by speaking to Gaxim.$B$BI\'m sorry I cannot send you back to Tara with good news yet. Perhaps soon.",
  },
  [1086] = {
    ["T"] = "The Flying Machine Airport",
    ["O"] = "Bring the Toxic Fogger to the Venture Co. sky port, then return to Apothecary Zamah.",
    ["D"] = "The toxin is ready.  All that remains is its delivery.  You, $N, must make that delivery.$B$BThe Venture Company must have a means of sending messages and light cargo between its headquarters and its operations in Stonetalon.  It is likely a small sky port -- not big enough for a zeppelin, but perhaps for a flying machine.  You must search the areas in Stonetalon under control of the Venture Company and find this sky port, if it exists.  If you find it, then place the toxin there.$B$BPlace it, and run.",
  },
  [1087] = {
    ["T"] = "Cenarius\' Legacy",
    ["O"] = "Kill 4 Sons of Cenarius, 4 Daughters of Cenarius and 4 Cenarion Botanists for Braelyn Firehand near Sun Rock Retreat.",
    ["D"] = "You cannot imagine the hatred I possess for this wretched land. The ilk of Cenarius and the kaldorei druids at the peak employ their pathetic powers to stifle the flow of magic, even so far south of their pathetic holdings... their beloved forests.$b$bWould that I could return to Jaedenar and exult in the flow of magic, but my master will not allow me to return until my work is done.$b$bI have no doubt you love the night elves and their allies as little as I. Aid me, and both our ends might be satisfied.",
  },
  [1088] = {
    ["T"] = "Ordanus",
    ["O"] = "Bring Ordanus\' head to Braelyn Firehand near Sun Rock Retreat.",
    ["D"] = "They thought that they could halt the flow of magic. They were mistaken. They thought that they could hide Ordanus, but I will have his head!$b$bMagic grants me true sight. Yes... there he is. A crossroads... At the top of a great tree in Ashenvale Forest. A name... Raynewood. One of the eldest sons of the forest god. They spirited him away from the peak before I could find him, but his time runs short.$b$bBring it to me and I will share with you a mighty gift.",
  },
  [1089] = {
    ["T"] = "The Den",
    ["O"] = "Travel to the Den on Stonetalon Peak. Using the Gatekeeper\'s Key, obtain the druids\' hidden items. Use these items to open the Talon Den Hoard. ",
    ["D"] = "The druids built the Talon Den on Stonetalon Peak, where they keep many powerful items.$b$bThey devised a complex scheme to keep them from falling into the wrong hands.$b$bLuckily, I managed to... extract some details from one of them.$b$bHidden in a small lantern at the entrance of the Den, you will find the Gatekeeper\'s Key--with it you will be able to open caches around the Den. The items from these caches will allow you to open their hoard, located in one of the central areas.",
  },
  [1090] = {
    ["T"] = "Gerenzo\'s Orders",
    ["O"] = "Get the mysterious ore from Piznik after he finishes mining it.",
    ["D"] = "You know, I was once the second most important person here at this site. That all changed when Gerenzo took over. Fired my boss, Ziz Fizziks, and sent me down to work in the mines. Ziz told me to stay here and gather information, though. His new boss will pay us well if we can find out what Gerenzo\'s plans are.$b$bAnyways, what I do know is that Gerenzo has ordered large amounts of this gold-green ore to be mined. I can get you a sample to bring to Ziz, but you\'ll have to watch for patrols while I get it.",
  },
  [1091] = {
    ["T"] = "Kaela\'s Update",
    ["O"] = "Bring the Venture Co. Letters to Kaela Shadowspear.",
    ["D"] = "$N, take these letters to Kaela. I\'m sure she\'ll want to know about this. Or at least the Sentinels will.$B$BMake sure you tell her how successful we were and how we couldn\'t have done it without a little bit of magic.",
  },
  [1092] = {
    ["T"] = "Gerenzo\'s Orders",
    ["O"] = "Deliver the Unidentified Ore to Ziz Fizziks in the Stonetalon Mountains.",
    ["D"] = "I guess I\'d better think about getting out of here, also. It won\'t take long for word of what happened here to spread to the higher ups, and I\'m sure they wouldn\'t take too kindly to meeting me again.$b$bI\'ll sneak out and meet with Ziz. Hopefully, he\'ll be able to find me a new job!$b$bI\'ll try and clean up this mess a little, but you should take the ore sample to Ziz right away. He told me that he\'d set up camp somewhere on the western side of Windshear Crag. Good luck.",
  },
  [1093] = {
    ["T"] = "Super Reaper 6000",
    ["O"] = "Get the Super Reaper 6000 Blueprints for Ziz Fizziks in the Stonetalon Mountains.",
    ["D"] = "Terrible what the Venture Company has done here, eh? What were they thinking? Goblin company hiring a gnome!$b$bLucky for me, Sputtervalve knows a genius when he sees one. Hired me just after I\'d been kicked out!$b$bTells me they built some enormous woodcutting machine, and Sput\' wants me to steal the plans for him. Unfortunately, Gerenzo--bloody gnome--wants me dead on sight... maybe you could steal the plans for me? You should be able to find them on one of their operators.",
  },
  [1094] = {
    ["T"] = "Further Instructions",
    ["O"] = "Deliver the Sealed Envelope to Sputtervalve in the Barrens.",
    ["D"] = "This envelope has all the information I was able to snatch as they booted me out the door... Venture Company internal documents, some manuals, technical things... Oh, and of course the Super Reaper 6000 blueprints.$b$bSputtervalve has taken up residence at Ratchet in the Barrens. Shouldn\'t have much trouble finding him, he\'s a little green fellow.",
  },
  [1095] = {
    ["T"] = "Further Instructions",
    ["O"] = "Bring the new orders to Ziz Fizziks in the Stonetalon Mountains.",
    ["D"] = "The matter of this gnome that has been hired by the Venture Company has caused a bit of a stir in Undermine. The trade princes do not approve of Razdunk\'s consorting with this Gerenzo.$b$bRiddlevox has asked me to take care of the problem, and I\'m sure he was instructed to do so by one or more of the trade princes. Of course, the Director didn\'t tell me that. Let\'s just say I have a hunch.$b$bWhatever the case, Gerenzo must be removed, and it would be a fitting test to have Fizziks take care of him.",
  },
  [1096] = {
    ["T"] = "Gerenzo Wrenchwhistle",
    ["O"] = "Bring Gerenzo Wrenchwhistle\'s Mechanical Arm to Ziz Fizziks in the Stonetalon Mountains.",
    ["D"] = "I met Gerenzo once, but let me tell you, he\'s not easy on the eyes. His flesh looks like it\'s rotting away, and he\'s grafted metal and gears onto his body, replaced some of his limbs with mechanical ones.$b$bNortheast of here, far above Cragpool Lake, up near the water wheel is where Gerenzo watches over the entire site. There\'s no way I could fight my way up there, not to mention take on Gerenzo... But you look like you might be able to make it.$b$bIf you kill him for me, I\'ll make it worth your while!",
  },
  [1097] = {
    ["T"] = "Elmore\'s Task",
    ["O"] = "Speak with Grimand Elmore.",
    ["D"] = "There\'s a dwarven weaponsmith in Stormwind, Grimand Elmore, who sent word that he needs help with a delivery.  I believe he wants a package sent to his homeland in the north.$B$BYou have a sturdy pair on you!  So if you\'re interested in some legwork then speak with Grimand.  We could use you down here, but we must also keep our ties strong with the dwarves.$B$BYou can find Grimand Elmore at the weapon shop in the Dwarven District of Stormwind, in the northeast section of town.",
  },
  [1098] = {
    ["T"] = "Deathstalkers in Shadowfang",
    ["O"] = "Find the Deathstalker Adamant and Deathstalker Vincent.",
    ["D"] = "In preparation for an assault on Shadowfang, two deathstalkers were sent to the keep to gather intelligence.  It has been many days and they have not returned, but we must receive their report if we\'re to have any hope of a successful attack!$B$BSo here is your task, $N.  Go to Shadowfang Keep to the south and find the Deathstalkers Adamant and Vincent.  Find them, and if they require aid then give it.$B$BThis mission is as dangerous as is it critical, $N.  Do not fail.",
  },
  [1100] = {
    ["T"] = "Lonebrow\'s Journal",
    ["O"] = "Read Henrig Lonebrow\'s Journal.",
    ["D"] = "You slowly open the weathered journal of Henrig Lonebrow....",
  },
  [1101] = {
    ["T"] = "The Crone of the Kraul",
    ["O"] = "Bring Razorflank\'s Medallion to Falfindel Waywarder in Thalanaar.",
    ["D"] = "Poor Heralath! And this dwarf... Lonebrow. What a brave soul he was.$b$bWe must not let their valiance go for naught. This crone... Charlga Razorflank ... must be stopped.$b$bSurely, infiltrating the Kraul will be perilous. There isn\'t time to send word to Darnassus. $n, assemble a party to slay the crone.$b$bBring me Razorflank\'s Medallion as proof of demise.",
  },
  [1102] = {
    ["T"] = "A Vengeful Fate",
    ["O"] = "Bring Razorflank\'s Heart to Auld Stonespire in Thunder Bluff.",
    ["D"] = "Cairne is a noble leader for uniting our people here on Thunder Bluff.$b$bBut I cannot forgive those who drove us from our ancestral lands as easily as some. My people inhabited the southern Barrens for decades. The land was holy to us. But we were driven off by numerous foes.$b$bA vengeful fate awaits the crone, Charlga Razorflank, who musters a foul army from within Razorfen Kraul.$b$bBring me Razorflank\'s heart and I can die in peace, $n.",
  },
  [1103] = {
    ["T"] = "Call of Water",
    ["O"] = "Bring a Water Sapta to Tiev Mordune in Silverpine Forest if you failed to speak to the Minor Manifestation of Water.",
    ["D"] = "Greetin\'s, $g brotha:sista;! Have you too come to purify the waters here and prove your understandin\' to Islen?$B$BI\'ve just defeated the corrupt water spirit meself, and I\'m just \'bout to put da bracers and remaining drops o\' water on the brazier.$B$BWhat\'s dat? Ahhh. Tiev understan\' all too well when somethin\' goes wrong. Well, Tiev can help you plenty if you be needin\' to see the pure water spirit again.$B$BBring me a water sapta from Islen as proof that you be ready, an\' I\'ll wait to turn in me parts.",
  },
  [1104] = {
    ["T"] = "Salt Flat Venom",
    ["O"] = "Bring 6 Salty Scorpid Venoms to Fizzle Brassbolts in the Shimmering Flats.",
    ["D"] = "I have so many designs for our rocket car!  So many!  And I must try them all!!$B$BI\'m working on a new type of fuel.  One that burns really really really hot!!  I haven\'t gotten the mixture perfect, but I think I know what I need...$B$BThe scorpids who wander the Shimmering Flats have a venom with lots and lots of salt in it.  It\'s very unique!  And it\'s just what I need!$B$BBring me venom and trust me -- our car will fly!!",
  },
  [1105] = {
    ["T"] = "Hardened Shells",
    ["O"] = "Bring 9 Hardened Tortoise Shells to Wizzle Brassbolts in the Shimmering Flats.",
    ["D"] = "My brother is working on a new fuel for his rocket car.  He\'s brilliant, he really is.  So brilliant that... I think that new fuel he\'s designing is strong enough to eat right through the car\'s chassis!$B$BI want to reinforce its fuel tanks before that happens.$B$BThe tortoises out on the flats have very hard shells.  I want to use them to create new tanks for our rocket car.  Tanks that can hold my brother\'s new fuel.$B$BYou look responsible enough.  Will you get me those shells?",
  },
  [1106] = {
    ["T"] = "Martek the Exiled",
    ["O"] = "Bring Fizzle\'s Letter to Martek the Exiled in the Badlands.",
    ["D"] = "I\'m developing a new engine that\'ll make the car go so fast!  Fast enough to fly, I know it!  I just need to make pistons that can handle very very heavy stress.  All my trials have failed because I can\'t make pistons hard enough!$B$BBut there is someone who might know how.$B$BHis name is Martek the Exiled.  He is a great smith and knows more about metal than anyone.  Take him this letter, I know he can help!$B$BBut he\'s far far away -- in Azeroth, in the Badlands, in a camp, with a goblin!$B$BFind him!",
  },
  [1107] = {
    ["T"] = "Encrusted Tail Fins",
    ["O"] = "Bring 10 Encrusted Tail Fins to Wizzle Brassbolts in the Shimmering Flats.",
    ["D"] = "Our rocket car is the fastest thing on the flats!  So fast that ... I\'m afraid friction is going to tear it apart!$B$BTo prevent that I need to create a composite to coat the car\'s moving parts.  It must be hard but not brittle, and must be resistant to salt corrosion, or time spent on the Shimmering Flats will eat it away.$B$BHave you heard of the Vile Reef, $N?  It\'s off the coast of Stranglethorn Vale.  Saltscale murlocs live there, and their tail fins are a crucial ingredient of my composite.",
  },
  [1108] = {
    ["T"] = "Indurium",
    ["O"] = "Bring 10 Indurium Flakes to Martek the Exiled in the Badlands.",
    ["D"] = "I\'m not out here just to stay away from people.  There is a wealth of metal in the Badlands, $N, if you have the guts to get it.$B$BThe metal\'s called indurium and as luck would have it, it is rumored to possess high resistance to heat and stress.  It might work for Fizzle\'s car.$B$BBut let\'s make sure.  Get me samples of indurium and I\'ll test its properties.$B$BTrue indurium ore lies deep in Uldaman, but the Stonevault troggs of the Badlands sometimes have flakes of it on them.  Bring me those flakes.",
  },
  [1109] = {
    ["T"] = "Going, Going, Guano!",
    ["O"] = "Bring 1 pile of Kraul Guano to Master Apothecary Faranell in the Undercity.",
    ["D"] = "As you can see, $n, we\'re up to some very.... interesting experiments at the Royal Apothecary Society.$b$bAs the Master here, it is my job to oversee the most ambitious of our alchemical attempts. So much work and so little time!$b$bYou look well traveled for a $c. Perhaps you can aid me.$b$bI\'m in need of a rare substance. It\'s foul material; it comes from a rare species of bats only found in the muck-hole known as Razorfen Kraul. Bring to me the guano from the Kraul Bats so we can begin our work...",
  },
  [1110] = {
    ["T"] = "Rocket Car Parts",
    ["O"] = "Bring 30 Rocket Car Parts to Kravel Koalbeard in the Shimmering Flats.",
    ["D"] = "Heh heh, with all the races they\'re running here, it\'s no surprise that a few mishaps happen along the way.  If you look around the Shimmering Flats then you\'ll see evidence of past crashes -- scraps of rocket car parts are littered everywhere!$B$BAnd those parts are worth money to the gnomes and goblins.  They\'re always looking for more contraptions to slap onto their cars.$B$BSo go out and get me parts.  Bring me a heap and I\'ll pay you well.",
  },
  [1111] = {
    ["T"] = "Wharfmaster Dizzywig",
    ["O"] = "Bring Kravel\'s Parts Order to Wharfmaster Dizzywig in Ratchet.",
    ["D"] = "I make it my job to get people what they need, and the gnomes over there need a parts order filled.  Their parts were shipped to Ratchet a week ago, but I haven\'t been able to make the trip.$B$BSo, are you ready for some legwork?  Go to Ratchet and give this parts order to Wharfmaster Dizzywig -- he\'ll probably be at the docks.$B$BDo this for me, and I\'ll have more jobs for you.$B$BJobs, my friend, with serious profit potential...",
  },
  [1112] = {
    ["T"] = "Parts for Kravel",
    ["O"] = "Bring Kravel\'s Parts to Kravel Koalbeard in the Shimmering Flats.",
    ["D"] = "Here\'s Kravel\'s package, $N.  And when you deliver it, tell him... if he wants more special orders, then he\'ll have to pay his tab!",
  },
  [1113] = {
    ["T"] = "Hearts of Zeal",
    ["O"] = "Master Apothecary Faranell in the Undercity wants 20 Hearts of Zeal.",
    ["D"] = "Why do I need a rare type of guano, you might ask. Well, for this experiment I want to see what effect two diametrically opposed chemical substances can have on each other, especially when both are combined with an ever-toxic agent to ensure the most... ghastly results.$b$bIn the northeastern peaks of Tirisfal Glades lies the Scarlet Monastery. There the crazed disciples of the Scarlet Crusade congregate, both inside and outside the halls. Which you slay matters not, any of them may have the heart I need.",
  },
  [1114] = {
    ["T"] = "Delivery to the Gnomes",
    ["O"] = "Bring the Delicate Car Parts to Fizzle Brassbolts in Shimmering Flats.",
    ["D"] = "Now that I have what I need, give these parts to Fizzle Brassbolts.  He\'s been waiting a long time for them -- I\'m sure he won\'t miss the little piece I removed from his order...$B$BAnd I wouldn\'t bother mentioning it to him.  It was small and worthless.$B$BTrust me.",
  },
  [1115] = {
    ["T"] = "The Rumormonger",
    ["O"] = "Talk to Krazek in Booty Bay.",
    ["D"] = "Now that we\'ve done a service for the gnomes, it\'s time to do the goblins a favor!  $B$BI hear that the goblin racer pilot, Nazz Steamboil, didn\'t always use that name.  I wonder why he changed it.  I wonder if it\'s a secret, and... I wonder if he values that secret.$B$BIf anyone knows the truth, then it\'s Krazek of Booty Bay, on the southern tip of Stranglethorn.  Speak with Krazek and ask him about our goblin pilot.$B$BIf you can get useful information from him, then we might just turn a profit from it.",
  },
  [1116] = {
    ["T"] = "Dream Dust in the Swamp",
    ["O"] = "Bring 10 Specks of Dream Dust to Krazek in Booty Bay.  Dream Dust is gathered from the dragon whelps of the Swamp of Sorrows.",
    ["D"] = "Getting the information on Nazz is quite a favor, $N.  Yes, quite a favor.  But you\'ve come all the way from the Shimmering Flats to get it... are you willing to travel some more?$B$BWhile I contact my sources, why don\'t you gather something for me, eh?$B$BHm... what will it be?  How about dream dust, from the dragon whelps of the Swamp of Sorrows.  Yes, I\'d love some dream dust for my snuff pouch!$B$BGet it for me, $N, and I\'ll give you the information you seek.",
  },
  [1117] = {
    ["T"] = "Rumors for Kravel",
    ["O"] = "Bring the Goblin Rumors to Kravel Koalbeard in the Shimmering Flats.",
    ["D"] = "I had to use some of my favors, and gave away a rare bottle of elven wine, and did some heavy deducing myself!  But I found the information you seek.$B$BHere, I wrote a letter with some very interesting bits on our friend, Nazz Steamboil.  Do with it what you will -- it\'s too dangerous for me to use.$B$BJust don\'t tell anyone where you got it!",
  },
  [1118] = {
    ["T"] = "Back to Booty Bay",
    ["O"] = "Bring Kravel\'s Scheme to Crank Fizzlebub in Booty Bay.",
    ["D"] = "$N, you\'re a $c I can trust, both to get the job done and to keep your mouth shut while doing it.$B$BI\'ve been brewing up a new scheme.  It\'s risky, but if it works then we could both make a bundle!  And I think you\'re just the person to see it through.$B$BTo start, you must go back to Booty Bay.  I apologize, good $N.  I know my last task took you there.  But this time, speak with Crank Fizzlebub.$B$BGive him this letter.  And if he requires aid, do your best to help him.  It will be worth the effort.",
  },
  [1119] = {
    ["T"] = "Zanzil\'s Mixture and a Fool\'s Stout",
    ["O"] = "Bring the Fool\'s Stout back to Kravel in the Shimmering Flats.",
    ["D"] = "I figured out how to use Zanzil\'s mixture with stout to make a powerful libation.  A Fool\'s Stout.  I think it\'ll be just what Kravel needs, but try it out and let me know how it works.$B$BAnd $N, don\'t keep this stuff too long without using it.  Zanzil\'s mixture has a weird effect on the stout\'s yeast, kicking it into high gear.  So it won\'t stay fresh forever.",
  },
  [1120] = {
    ["T"] = "Get the Gnomes Drunk",
    ["O"] = "Bring the Fool\'s Stout to the Gnome Pit Boss.",
    ["D"] = "We need to try the Fool\'s Stout on one of the pit crews.  If you want to try it on the gnomes, then here -- take the stout and give it to the gnome pit boss.$B$BAnd don\'t wait too long.  This stout will go bad eventually.",
  },
  [1121] = {
    ["T"] = "Get the Goblins Drunk",
    ["O"] = "Bring the Fool\'s Stout to the Goblin Pit Boss.",
    ["D"] = "We need to try the Fool\'s Stout on one of the pit crews.  If you want to try it on the goblins, then here -- take the stout and give it to the goblin pit boss.$B$BAnd don\'t wait too long.  This stout will go bad eventually.",
  },
  [1122] = {
    ["T"] = "Report Back to Fizzlebub",
    ["O"] = "Bring the Fool\'s Stout report to Crank Fizzlebub in Booty Bay.",
    ["D"] = "That Fool\'s Stout worked great!  I placed a bet on the next race, and... let\'s hope the crew you got drunk did a number on their car!$B$BThere\'s only one more task for you -- take this note back to Crank Fizzlebub in Booty Bay.  He\'ll want to know of the successful test.$B$BAnd hopefully we can get more of that Fool\'s Stout for future races!",
  },
  [1123] = {
    ["T"] = "Rabine Saturna",
    ["O"] = "Speak with Rabine Saturna in the village of Nighthaven, Moonglade.  Moonglade lies between Felwood and Winterspring, accessible through a path out of Timbermaw Hold.",
    ["D"] = "For this task, Keeper Remulos\' majordomo in Moonglade - Rabine Saturna - seeks aid in exploring the vast wastelands far to the west of Tanaris and Un\'Goro.  He is located in Nighthaven, the main village of Moonglade.$B$BI want to impart on you the importance of your presence.  There are growing tensions between the night elves of Darnassus and the Cenarion Circle in Moonglade.  Look beyond racial boundaries and tap into the wisdom of your ancestors in dealings with the Circle there, $N.  Good luck.",
  },
  [1124] = {
    ["T"] = "Wasteland",
    ["O"] = "Speak with Layo Starstrike near the Valor\'s Rest graveyard of Silithus, showing him Rabine\'s Letter.",
    ["D"] = "West of Un\'Goro Crater, via the northwestern ridge, is a harsh wasteland - Silithus.  Few know that an ancient war was waged there between the night elves and a malign, alien power.  The evil was sealed, but now we suspect that forces work to stir this... unspeakable horror.$B$BSeek out Layo Starstrike, one of our agents in Silithus; look for him at Valor\'s Rest, the final resting spot for the fallen of the wastes.  Give him this letter, and he will give you his trust accordingly.",
  },
  [1125] = {
    ["T"] = "The Spirits of Southwind",
    ["O"] = "Free the spirits of 8 Tortured Druids and 8 Tortured Sentinels in Southwind Village, and then return to Layo Starstrike at the Valor\'s Rest graveyard of Silithus.",
    ["D"] = "To our southwest lies a tragic site - the ruins of Southwind Village.  This is where our work should begin, $N.$B$BFrom what we\'ve learned, this village was once a night elf base of operations for Silithus.  It\'s long since overrun by the silithid hives that infest the wastes.  I can only imagine the horror of the village\'s initial sundering...$B$BThe tortured spirits of fallen druids and sentinels wander the ruins aimlessly.  Explore this locale, freeing the souls of these wretched beings as needed.",
  },
  [1126] = {
    ["T"] = "Hive in the Tower",
    ["O"] = "Scale the tower of Southwind Village and locate a means to stir the silithid hive into activity.  Bring back anything unusual you may uncover when doing so to Layo Starstrike at the Valor\'s Rest graveyard of Silithus.",
    ["D"] = "The hive needs to be addressed in Southwind Village, and I think I know a way to get at them.  It\'s been my experience that the silithid hives are very sensitive to their environment; this explains their attacks when hearing the death wails of the spirits.$B$BThe silithid are strongest in the tower - the one with the mass of wasps circling it.  Scale the tower and jostle the hive into activity.  Report back what you learn, but be careful!  If you uncover anything that might help, bring it to me.",
  },
  [1127] = {
    ["T"] = "Fool\'s Stout",
    ["O"] = "Bring 12 of Zanzil\'s Mixture to Crank Fizzlebub in Booty Bay.",
    ["D"] = "So you want some Fool\'s Stout, eh?  Thinking of heading back to the races...?$B$BAll right, bring me more of Zanzil\'s mixture and I\'ll make some more stout for you.",
  },
  [1130] = {
    ["T"] = "Melor Sends Word",
    ["O"] = "Speak with Melor Stonehoof in Thunder Bluff.",
    ["D"] = "$N, Melor Stonehoof has heard of your deeds, and has set a path before you.$B$BIf you wish to take it, then go to Thunder Bluff and speak with Melor.  You will find him on Hunter\'s Rise.",
  },
  [1131] = {
    ["T"] = "Steelsnap",
    ["O"] = "Bring Steelsnap\'s Rib to Melor Stonehoof in Thunder Bluff.",
    ["D"] = "At times we hunt for food.  At times we hunt for honor.  And at times we hunt to earn the Earthmother\'s teachings.$B$BBut the hunt I now set upon you is none of these things.  It is to slay a creature outside the Earthmother\'s blessing.$B$BThe hyena Steelsnap roams the Thousand Needles, south of the Barrens.  He is a tyrant, attracting other hyenas to him and spreading fear and bloodshed among more peaceful beasts.$B$BFind Steelsnap, $N.  Find him, and defeat him.",
  },
  [1132] = {
    ["T"] = "Fiora Longears",
    ["O"] = "Speak with Fiora Longears in the tavern at Theramore Isle in Dustwallow Marsh.",
    ["D"] = "Oh, to be at sea once again!  To feel the kiss of the wind, and to have the waves rock me like my blessed mother, long ago!$B$BOh, I wish I had your fortune, good $c, for I see the sea in your future!$B$BIt\'s my job to tell eager souls of the land of Kalimdor, the land of opportunity!  If you\'re willing to try your luck across the sea, then take a ship from here to the lovely port of Auberdine.  Speak there with my partner, the elf, Fiora Longears.$B$BShe\'ll start you on your Kalimdor adventure!",
  },
  [1133] = {
    ["T"] = "Journey to Astranaar",
    ["O"] = "Speak with Shindrell Swiftfire in Astranaar.",
    ["D"] = "If you\'re fresh off the boat from Menethil, then the first thing you should do is... go to Astranaar.  I\'m sure an eager member of the Alliance such as you can do some real good there.  Speak with Shindrell Swiftfire and offer your services.$B$BBut don\'t bother mentioning who sent you.  Shindrell does not know me...",
  },
  [1134] = {
    ["T"] = "Pridewings of Stonetalon",
    ["O"] = "Bring 12 Pridewing Venom Sacs to Shindrell Swiftfire in Astranaar.",
    ["D"] = "We are not in open war with the Horde, but our peace is fragile.  We often find their agents moving against us, but they do so covertly.$B$BOne such advance was revealed to us from hidden sources in Theramore, claiming that the orcs collect venom from the pridewings near Mirkfallon Lake, in the Stonetalon Mountains to the south.  Orc assassins then use the venom on their secret death raids against the Alliance!$B$BIf this is true, then we must stop them, $N.  We must deny them their source of venom.",
  },
  [1135] = {
    ["T"] = "Highperch Venom",
    ["O"] = "Bring 10 Highperch Venom Sacs to Fiora Longears in Theramore.",
    ["D"] = "As you may know, wyverns are often tamed by orcs for use as mounts.  But what is less known is that the venom of a wyvern, in some circles, is more valuable than the beast itself.  So if you\'re not afraid to do a little wyvern hunting, then... I have a job for you.$B$BThere is a place called Highperch in the land of a Thousand Needles, west of here.  The Highperch wyverns have a potent venom.  Potent, and valuable.",
  },
  [1136] = {
    ["T"] = "Frostmaw",
    ["O"] = "Bring Frostmaw\'s Mane to Melor Stonehoof in Thunder Bluff.",
    ["D"] = "$N, it is now time for you to travel to far-off lands.  You are ready.$B$BYou now hunt Frostmaw, a yeti in the Alterac Mountains.  He is elusive, his white coat blending into the snows of his mountain home.  He rarely shows himself.$B$BBut I will show you how to entice a creature such as he.  Here, take this scroll.  On it I have written how Frostmaw may be lured.$B$BNow go, $N.  Your journey is long and many challenges lay waiting.  When you return with Frostmaw\'s mane, only then is your task complete.",
  },
  [1137] = {
    ["T"] = "News for Fizzle",
    ["O"] = "Speak with Fizzle Brassbolts in the Shimmering Flats.",
    ["D"] = "Indurium has an amazing resistance to heat!  I\'m sure it will work for Fizzle\'s pistons.  But he\'ll need true indurium ore, not just the flakes.$B$BTo get the ore, it must be mined from deep within Uldaman, on the northern borders of the Badlands.  That is the true source of indurium.$B$BSend Fizzle Brassbolts my regards, and tell him what I told you.$B$BSafe travels, $N.  And it won\'t surprise me if we meet again -- once Fizzle learns of indurium he\'ll probably send you to Uldaman to acquire it!",
  },
  [1138] = {
    ["T"] = "Fruit of the Sea",
    ["O"] = "Collect 6 Fine Crab Chunks for Gubber Blump in Auberdine.",
    ["D"] = "I love crab.  Crabs are the fruit of the sea!  You can bake it, barbecue it, boil it, and broil it.  They\'re pan fried, deep fried, and stir fried.  There\'s crab salad, crab soup, crab stew, pepper crab, lemon crab, whipper root crab, and Ironforge surprise crab.  That\'s... that\'s about it.$B$BI\'m here fishing right now, so I can\'t get any fine crab chunks.  You can pick them out of reef crawlers and encrusted tide crawlers.  You can make a lot with it.  You can bake it, barbecue it, boil it...",
  },
  [1139] = {
    ["T"] = "The Lost Tablets of Will",
    ["O"] = "Find the Tablet of Will, and return them to Advisor Belgrum in Ironforge.",
    ["D"] = "We\'ve slowed the Shadowforge clan by cutting them off from Blackrock Spire, but Hammertoe\'s quest is still unfulfilled. His death must not be in vain. He dreamt of finding the Tablet of Will: a stone shard inscribed with magic by the titans themselves to help imbue their creations with life--making them have a will of their own, or so the legends say.$B$BWe must still recover the Tablet before the Shadowforge clan finds it.$B$BReturn to Uldaman, find the tablet, and return here immediately.",
  },
  [1140] = {
    ["T"] = "The Tower of Althalaxx",
    ["O"] = "Free the trapped Highborne souls in Night Run and Satyrnaar, then return to Delgren the Purifier at Maestra\'s Post.",
    ["D"] = "The writing of the orcs is little better than the scribblings of a child. Many of my order scoffed when I chose to learn their tongue, but I have found it beneficial to gain further understanding of my enemies.$b$bFrom Ilkrud\'s scribblings, it appears that Athrikus still possesses two soulgems, and they are being held in Night Run and Satyrnaar, satyr-held areas in northeast Ashenvale.$b$bBe careful out there, $n, the satyrs\' blood-soaked temples can be unsettling even for a seasoned $c such as yourself.",
  },
  [1141] = {
    ["T"] = "The Family and the Fishing Pole",
    ["O"] = "Catch 6 Darkshore Grouper for Gubber Blump in Auberdine.",
    ["D"] = "I love fishing.  The Blump family is known for fishing.  My name is Gubber Blump; I fish.$B$BThere\'s a kind of fish I like to catch around these parts called Darkshore grouper.  I used to go out on a boat to fish for grouper, but no boats have been going out since them mean ol\' murlocs moved in.  I bet they been eating up all the grouper too.$B$BSay, you wanna help me catch Darkshore grouper?  I\'d give you a bona fide Blump Family Fishing Pole if you did!  It\'s a real good pole to catch fish with.",
  },
  [1142] = {
    ["T"] = "Mortality Wanes",
    ["O"] = "Find and return Treshala\'s Pendant to Treshala Fallowbrook in Darnassus.",
    ["D"] = "I have not long to live... so... so... weak.$b$b...the b-b-brave dwarf, Lonebrow, has been sent to w-w-warn Falfindel...$b$bBut before I... d-d-die...$b$b...my wife, Treshala... her pendant of bonding to me... stolen... by one of the f-f-foul aggressors...$b$bFind it p-p-please... and return it to Treshala in Darnassus...$b$b...along with w-w-word of my lonely... death.",
  },
  [1143] = {
    ["T"] = "The Tower of Althalaxx part 8",
    ["O"] = "Kill Athrikus Narassin and bring his head to Balthule Shadowstrike near the Tower of Althalaxx.",
    ["D"] = "I see. Well, if what you say is true, then now is the time to strike. I believe Athrikus resides at the top of the tower. Be careful though, he is surrounded by many powerful warlocks, much more deadly than the ones you faced before.",
  },
  [1144] = {
    ["T"] = "Willix the Importer",
    ["O"] = "Escort Willix the Importer out of Razorfen Kraul.",
    ["D"] = "At last, help has arrived!$b$bYou have no clue how long I\'ve been held prisoner here. I can\'t remember the last time I saw the outside of this place.$b$bYou see, I am an entrepreneur. Always out for a copper, as they say. I caught wind that Mebok Mizzyrix was paying well for Redleaf Tuber from this here pig pen. I figured it would be an easy in, easy out kind of affair. As you can see, I was a bit wrong!$b$bAnyway, if you\'d be so kind as to help me get out of here, I\'ll make it worth your while.",
  },
  [1145] = {
    ["T"] = "The Swarm Grows",
    ["O"] = "Deliver Korran\'s Sealed Note to Belgrom Rockmaul in Orgrimmar.",
    ["D"] = "Many travelers who\'ve come through the Crossroads helped me gather eggs from the Field of Giants, and many of them have tales of how deadly the creatures are. I\'ve heard enough to consider them insects a much larger threat than my masters first thought.$B$BI need a courier to travel to Orgrimmar to meet Belgrom Rockmaul. It be no simple matter, and someone who would take the task seriously should go.$B$BBelgrom usually be found outside the warriors\' guild house.",
  },
  [1146] = {
    ["T"] = "The Swarm Grows",
    ["O"] = "Deliver Belgrom\'s Sealed Note to Moktar Krin in Thousand Needles.",
    ["D"] = "So, Korran has come to a similar conclusion about these insects--they seem to be a larger threat than we assumed.$B$BA few days ago a hunter returned to me from Thousand Needles nearly dead. He was with a raiding party sent to drive a dwarven excavation team out of the Shimmering Flats. During the attack, more of these insects erupted from a cave in the dig site and nearly killed the party.$B$BThey were led by Moktar Krin, and they\'ve taken refuge on the border of the Shimmering Flats. Bring him this note.",
  },
  [1147] = {
    ["T"] = "The Swarm Grows",
    ["O"] = "Kill 5 Silithid Searchers, 5 Silithid Hive Drones, and 5 Silithid Invaders and return to Moktar Krin in Thousand Needles.",
    ["D"] = "Hmm. Seems Belgrom thinks those monsters are a threat. He wants them pushed back into whatever depths they crawled up out of.$B$BI guess killing the dwarves sent by the Explorers\' League wasn\'t enough--now I have to send a half-dead war party into another battle. We won\'t be able to do it ourselves; not while we\'re in this condition. The dig site\'s to the south of here. There shouldn\'t be anymore dwarves to deal with. Feel up to wielding a blade or staff and doing your part to aid us?",
  },
  [1148] = {
    ["T"] = "Parts of the Swarm",
    ["O"] = "Bring 1 Silithid Heart, 5 Silithid Talons, and 3 Intact Silithid Carapaces, to Korran at the Crossroads.",
    ["D"] = "You inspect the carapace carefully. As you turn it over in your hands you realize that the chitinous plating is interlaced in such a way that makes it incredibly strong. It\'s unlike anything you\'ve ever seen. You inspect the rest of the corpse further and realize that these creatures are very powerful and highly evolved.$B$BLooking down at the carapace again, you think that Korran would enjoy studying more of these creatures. It could help him figure out what these creatures are, and where they come from.",
  },
  [1149] = {
    ["T"] = "Test of Faith",
    ["O"] = "If you have faith, leap from the planks overlooking Thousand Needles.",
    ["D"] = "So, you have sought me out, or perhaps destiny brings you to me? No matter. You are here now, and so your journey will begin anew.$B$BMany facets make up a being: the spirit, the body, the mind. Some of these can be measured by a culture\'s traditions. Others we can only hope exist within ourselves. If you agree, I shall test some of the facets within you.$B$BIf you pass, then you shall become stronger. If you fail, then you will realize the vastness of the Nether.$B$BThe first is a Test of Faith.",
  },
  [1150] = {
    ["T"] = "Test of Endurance",
    ["O"] = "Bring Grenka\'s Claw to Dorn Plainstalker in Thousand Needles.",
    ["D"] = "I\'m pleased to see your courage remains steadfast in light of what you\'ve already done. Since your faith is intact, the time has come to test your body.$B$BFirst is a test of endurance. Go to the Roguefeather Den west of Freewind Post. Make your way to the depths of their lair and destroy their foodstuffs. By then you should have provoked their fury, and if you are able to survive long enough, Grenka Bloodscreech herself will come for you--kill her and bring me her claw to complete your test.",
  },
  [1151] = {
    ["T"] = "Test of Strength",
    ["O"] = "Bring Fragments of Rok\'Alim to Dorn Plainstalker in Thousand Needles.",
    ["D"] = "During the time of the Old Gods, elementals inhabited much of this world. They worshipped the Old Gods, and the Gods, through their power, kept the elementals bound to this world. When the Old Gods were banished, the elementals faded from our world and now only return at the bidding of powerful mages.$B$BThere are exceptions though. Some elementals were powerful enough to remain here on their own. One such elemental is Rok\'Alim.$B$BTo test your strength, find Rok\'Alim in Thousand Needles and slay him.",
  },
  [1152] = {
    ["T"] = "Test of Lore",
    ["O"] = "Find Braug Dimspirit near the entrance to Talondeep Path in Stonetalon Mountains.",
    ["D"] = "Your time with me is just about finished. I am knowledgeable, but the Test of Lore is not mine to give. There are lessons to be learned and places to visit that others claim domain over.$B$BIf you are prepared, then seek out Braug Dimspirit--he is a shaman of great wisdom. He will test you further, so heed his words, $N.$B$BWhen you are ready, find the tunnel that connects the Stonetalon Mountains with Ashenvale: Braug dwells near there. And be careful, $N, the night elves may wish to impede your test.",
  },
  [1153] = {
    ["T"] = "A New Ore Sample",
    ["O"] = "Find an Unrefined Ore Sample on a Gravelsnout Digger or Surveyor and bring it to Tatternack Steelforge at Camp Taurajo in the Barrens.",
    ["D"] = "$C, you have done well in helping me in the past. The warchief is pleased thus far with what I\'ve accomplished, and asks me to continue with renewed determination. My honor dictates that I give you proper recognition to the warchief for all you have done, but first I would ask you for your help again.$B$BTravelers recently came from the south from a place called Thousand Needles. There they killed a kobold who had an unusual ore sample, but they would not sell it. I want a sample of that ore to examine.",
  },
  [1154] = {
    ["T"] = "Test of Lore",
    ["O"] = "Find the Legacy of the Aspects and return it to Braug Dimspirit near the entrance to Talondeep Path in Stonetalon Mountains.",
    ["D"] = "You have passed Dorn\'s tests, and now you seek to pass your next trial. Very good. But up until now, your challenges have been childish. It is easy to test your body--your mind is another matter completely.$B$BI shall explain it simply: find the book I name, study it, and answer my question about its contents.$B$BWhat book, you must be asking? The Legacy of the Aspects. It can be found at the bottom of Dor\'danil in southern Ashenvale, protected by the dead druids and their slayers.",
  },
  [1159] = {
    ["T"] = "Test of Lore",
    ["O"] = "Find Parqual Fintallas in Undercity.",
    ["D"] = "Well done, $N. You have passed the first portion of the Test of Lore, but there is still another question to be answered. But that question is not mine to ask.$B$BSeek out Parqual Fintallas in Lordaeron City. He was a great historian for the humans before the Plague overtook him. Now he continues to seek enlightenment, but of a different nature.$B$BHe will have another requirement of you before he asks you anything, but you have done masterfully thus far--I\'m sure you will overcome his obstacle.",
  },
  [1160] = {
    ["T"] = "Test of Lore",
    ["O"] = "Find The Beginnings of the Undead Threat, and return it to Parqual Fintallas in Undercity.",
    ["D"] = "I will tell you what you must seek, and I will even ask you my question, but not because you wish to prove yourself. Knowledge is far more important than strength. Even as others seem to pass you by in ability, your knowledge of the world will be more valuable.$B$BGo to the Monastery in the north of Tirisfal Glades. In the library there, a book called Beginnings of the Undead Threat resides. It was used by the now-corrupt paladins there to train them to fight my kind.$B$BBring it to me.",
  },
  [1164] = {
    ["T"] = "To Steal From Thieves",
    ["O"] = "Bring Kenata, Fardel, and Marcel Dabyrie\'s Heads to Genavie Callow in Undercity.",
    ["D"] = "The harlot! The swine! Kenata still lives; her family healthy and prospering while I suffer. Forsaken indeed!$B$BMy family is gone, taken by the plague. Our estate also forfeit; looted and burned during the war. And after all that, her and her lousy children had the audacity to steal the only precious belongings I had left.$B$BI don\'t care about the things they stole any more. What I want now are their heads!$B$BKill them for me! Go to the Dabyrie Farmstead in Arathi, northeast of Refuge Point.",
  },
  [1166] = {
    ["T"] = "Overlord Mok\'Morokk\'s Concern",
    ["O"] = "Overlord Mok\'Morokk in Brackenwall Village wants you to retrieve his grog, snuff and strongbox in Dustwallow Swamp.",
    ["D"] = "Bow before Overlord Mok\'Morokk!$b$bRexxar make me mighty leader of Stonemaul Clan!$b$bMe good leader. Me keep clan safe.$b$bBig dragons come and make fire at old home. Me smart ogre. Find new home for clan here.$b$bLeave old home so fast, me forget stuff. Tiny $r, go get my things. Go south and find my yummy grog, my snuff and my strongbox. Me like you after you do that. You look in Den of Flame and Stonemaul Ruins... they be there, me sure!",
  },
  [1167] = {
    ["T"] = "The Tower of Althalaxx part 7",
    ["O"] = "Speak with Balthule Shadowstrike near the Tower of Althalaxx.",
    ["D"] = "Before confronting Athrikus, it would probably be wise for you to consult with Balthule. I have no doubt he has been vigilant in his watch of the Tower, and he may have information about recent developments.",
  },
  [1168] = {
    ["T"] = "Army of the Black Dragon",
    ["O"] = "Tharg in Brackenwall Village wants you to kill 10 Firemane Scouts, 10 Firemane Ash Tails, and 5 Firemane Scalebanes.",
    ["D"] = "Mok\'Morokk was made Overlord by Rexxar. He care more for power than he care for ogres.$b$bBlack dragons kill many ogre. Kill Tharg\'s wife. Mok\'Morokk tell us to run. Make us come here.$b$bOgre should fight. Ogres not good at running. Mok\'Morokk care only about Mok\'Morokk.$b$bMe call Stonemaul Ruins home. Me like it in the south. This place not home.$b$bHelp ogres fight army of the black dragon. Me want revenge. Me want to go home.",
  },
  [1169] = {
    ["T"] = "Identifying the Brood",
    ["O"] = "Draz\'Zilb in Brackenwall Village would like you to bring him 7 Searing Tongues and 7 Searing Hearts.",
    ["D"] = "Whilst that craven fool, Mok\'Morokk, wallows in power and Tharg wrestles the demons of sorrow and vengeance and vies for leadership of the clan, I seem to be the only one concerned with identifying the source of aggression on our old home.$b$bWhy the puzzled stare, $n? Expecting me to speak like an uncouth ruffian merely because I am an ogre?$b$bNow back to business, bring to me the hearts and tongues from the whelps and hatchlings. I shall trace the root of this evil...",
  },
  [1170] = {
    ["T"] = "The Brood of Onyxia",
    ["O"] = "Speak with Overlord Mok\'Morokk in Brackenwall Village.",
    ["D"] = "Stonemaul Village was invaded by the brood of Onyxia. But why would the daughter of the black dragonlord, Deathwing, descend upon our lands?$b$bThis is most troubling. Surely, Onyxia was driven here for a purpose beyond laying siege to a small band of ogres.$b$bNotify Mok\'Morokk at once! Action must be taken.",
  },
  [1171] = {
    ["T"] = "The Brood of Onyxia",
    ["O"] = "Speak with Draz\'Zilb in Brackenwall Village.",
    ["D"] = "You leave me alone now. Go tell Draz\'Zilb we stay here. No black dragon here.",
  },
  [1172] = {
    ["T"] = "The Brood of Onyxia",
    ["O"] = "Draz\'Zilb in Brackenwall Village wants you to destroy 5 Eggs of Onyxia.",
    ["D"] = "Onyxia\'s brood has been scattered across the Dragonmurk. It is imperative that no more whelps be permitted to hatch.$b$bMake haste to Wyrmbog in the south of Dustwallow Marsh. Surely, she has made her lair there. Track down the evil dragon\'s eggs and destroy them.$b$bWe will never reclaim Stonemaul Village if the surrounding area remains a breeding ground.$b$bAs for Mok\'Morokk... I have other plans for the sod.",
  },
  [1173] = {
    ["T"] = "Challenge Overlord Mok\'Morokk",
    ["O"] = "Defeat Mok\'Morokk and report the news to Draz\'Zilb in Brackenwall Village.",
    ["D"] = "You dare challenge Overlord Mok\'Morokk?$b$bHaw!$b$bMe crush tiny $r!$b$bYou tell me when ready for good beating.",
  },
  [1175] = {
    ["T"] = "A Bump in the Road",
    ["O"] = "Kill 10 Saltstone Basilisks, 10 Saltstone Crystalhides and 6 Saltstone Gazers for Trackmaster Zherin on the Shimmering Flats.",
    ["D"] = "After the big crash with the goblins\' Red Thunder racer and a basilisk, Kronkrider hired me on to take care of the track. I don\'t know what all the animals out on the flats are feeding on to grow so big, but we have to be careful to thin them out so they won\'t wander onto the track when a race is going on.$b$bTell you what. If you want to go out and make a little profit, go out and remove the basilisks from the area.",
  },
  [1176] = {
    ["T"] = "Load Lightening",
    ["O"] = "Get 10 Hollow Vulture Bones for Pozzik in the Shimmering Flats.",
    ["D"] = "When it comes to racing, every bit counts! Whether it\'s a minor tweaking on the power output, a slight slackening on the tension coils, or lightening the weight of the frame, you have to take everything into account.$b$bWe use vulture bones for a lot of the minor structural tweaks we make to our racers, which offer the perfect balance of strength and weight. Even better, it\'s easy for us to get more, as the salt flats are littered with vultures.$b$bBring me some and I\'ll give you a good price on them.",
  },
  [1177] = {
    ["T"] = "Hungry!",
    ["O"] = "Mudcrush Durtfeet in northern Dustwallow wants 8 Mirefin Heads.",
    ["D"] = "Mud big ogre.$b$bMe hungry.$b$bGo bash fishy. Bring Mud food.$b$bYou feed Mud or Mud eat you.",
  },
  [1178] = {
    ["T"] = "Goblin Sponsorship",
    ["O"] = "Speak to Gazlowe in Ratchet.",
    ["D"] = "Our racing team is the pride of goblins everywhere. You\'d be surprised at some of the people who contribute in one way or another to our team.$b$bYou know Gazlowe? He tracks down parts from around the world for us. He even came down to work on one of our rigs once!$b$bA few weeks ago, he said he\'d send us a new prototype fuel regulator but it hasn\'t come yet. He\'s usually very prompt... Maybe you wouldn\'t mind going to Ratchet to ask him about it? I wouldn\'t be so pushy, but the gnomes are gaining on us!",
  },
  [1179] = {
    ["T"] = "The Brassbolts Brothers",
    ["O"] = "Bring the crate of crash helmets to Wizzle Brassbolts in the Shimmering Flats.",
    ["D"] = "You ever heard of the Shimmering Flats?  It\'s a stretch of salt flats way over in Kalimdor, just south of the Thousand Needles canyon.  It\'s a great place for racing because, well, it\'s so flat!$B$BA couple of gnome brothers are out there, friends of mine, trying out their new rocket car, and from what I hear it\'s fast!  Heh, so fast that their pilots keep crashing.  Giving themselves concussions.$B$BSo they asked me for some Siege Engine helmets, but I have duties here.  I can\'t make the trip!$B$BCan you?",
  },
  [1180] = {
    ["T"] = "Goblin Sponsorship",
    ["O"] = "Speak with Wharfmaster Lozgil in Booty Bay.",
    ["D"] = "Back to what you came here about. I know I promised Pozzik that I\'d deliver a new fuel regulator for him, but I still haven\'t received it yet. Revilgaz over in Booty Bay said he\'d have it shipped over, but it might be it slipped through the cracks somehow.$b$bIf that\'s true, there\'s not much we can do about it. We can at least find out if it was dispatched from Booty Bay.$b$bCheck in with Wharfmaster Lozgil over there, he should be able to check the cargo records.",
  },
  [1181] = {
    ["T"] = "Goblin Sponsorship",
    ["O"] = "Speak with Baron Revilgaz in Booty Bay.",
    ["D"] = "I\'m afraid I can\'t be of much more help to you, $n. If you\'d like to follow up on it more, I\'d suggest you go hit up the Baron himself. If there\'s anyone who knows Revilgaz\'s business, it\'s Revilgaz.$b$bYou\'ll find him at the tavern across the harbor, top floor.",
  },
  [1182] = {
    ["T"] = "Goblin Sponsorship",
    ["O"] = "Bring the Fuel Regulator Blueprints to Baron Revilgaz in Booty Bay.",
    ["D"] = "See, the Venture Company shredders out in Lake Nazferiti (in northern Stranglethorn) have been equipped with a new model fuel regulator. I was thinking that Pozzik would be able to take advantage of it, if he could get some details. Unfortunately, Cozzle, the foreman out there, appears to be a little more clever than I gave him credit for.$b$bYou seem like you might have more luck finding the blueprints though. I know he keeps them in his house, but they\'re almost certainly under lock and key.",
  },
  [1183] = {
    ["T"] = "Goblin Sponsorship",
    ["O"] = "Deliver the Fuel Regulator Blueprints to Pozzik on the Shimmering Flats.",
    ["D"] = "You should be all set to go. Give Pozzik my regards and tell him that if he ever needs anything, not to hesitate to ask. I\'ll be waiting for the news that he breaks the track record soon!",
  },
  [1184] = {
    ["T"] = "Parts of the Swarm",
    ["O"] = "Return to Belgrom Rockmaul in Orgrimmar.",
    ["D"] = "Head back to Belgrom in Orgrimmar and tell him Moktar be doing his bidding well out in the Thousand Needles. Tell him also, that me studies progress, and I hope to be havin\' learned more soon. That should please him greatly.",
  },
  [1185] = {
    ["T"] = "Under the Chitin Was...",
    ["O"] = "",
    ["D"] = "",
  },
  [1186] = {
    ["T"] = "The Eighteenth Pilot",
    ["O"] = "Speak with Razzeric on the Shimmering Flats.",
    ["D"] = "I\'m going to be busy with working on integrating this new fuel regulator to our racer designs, but if you\'d like to help out the team some more, talk to Razzeric. He\'s our pilot--well, our eighteenth pilot, to be exact--and he likes to add his own little modifications to the racers.$b$bHe\'s a little off his rocker, though, so try not to act too surprised.",
  },
  [1187] = {
    ["T"] = "Razzeric\'s Tweaking",
    ["O"] = "Retrieve the Seaforium Booster for Razzeric in the Shimmering Flats.",
    ["D"] = "Pozzik\'s a great mechanic, I know that, but he\'s afraid to just load on the firepower and grab on for the ride. Not me, though.$b$bAfter Pozzik finishes with a racer, I take a little time to put my own modifications on--you know, loosening up the controls, removing parts of the frame, and adding more juice!$b$bI ordered a seaforium booster from Shreev at the Gizmorium in Gadgetzan, but apparently the zeppelin it was being sent on crashed in Dustwallow Marsh! I gotta have it, $n!",
  },
  [1188] = {
    ["T"] = "Safety First",
    ["O"] = "Bring the Seaforium Booster to Shreev in Gadgetzan.",
    ["D"] = "But wait, what\'s this? Shreev left the safety on. That will limit the amount of power I can draw from the booster at any time. I told him I wanted it off! This is no good.$b$bYou have to take this to the Gadgetzan Gizmorium in the Tanaris Desert and have Shreev remove this.",
  },
  [1189] = {
    ["T"] = "Safety First",
    ["O"] = "Return the Modified Seaforium Booster to Razzeric on the Shimmering Flats.",
    ["D"] = "There, I did what Razzeric wanted. Just tell him to be careful when he sets this off. If he makes the slightest mistake, he won\'t be moving forward, he\'ll just be a burnt spot on the track.$b$bWould be a shame, too, if that happened. Who knows if our nineteenth driver would be any good?",
  },
  [1190] = {
    ["T"] = "Keeping Pace",
    ["O"] = "Have Zamek create a diversion, then steal Rizzle\'s plans for Pozzik on the Shimmering Flats.",
    ["D"] = "The Brassbolts brothers have discovered a new building material, that\'s the only explanation that makes sense. Their cars have been operating faster than before, but their designs, and all the other data we\'ve gathered from their runs is identical.$b$bWhat we do know, is that Rizzle Brassbolts has been focused on the plans for this new discovery almost constantly.$b$bZamek can create a diversion for you, so that you can steal those plans from them.",
  },
  [1191] = {
    ["T"] = "Zamek\'s Distraction",
    ["O"] = "",
    ["D"] = "",
  },
  [1192] = {
    ["T"] = "Indurium Ore",
    ["O"] = "",
    ["D"] = "",
  },
  [1193] = {
    ["T"] = "A Broken Trap",
    ["O"] = "",
    ["D"] = "",
  },
  [1194] = {
    ["T"] = "Rizzle\'s Schematics",
    ["O"] = "Bring the Sample of Indurium Ore to Pozzik on the Shimmering Flats.",
    ["D"] = "Rizzle\'s schematics describe the process of the refinement of indurium ore. From his scrawls, it is apparent that indurium ore is being obtained from the dwarven excavation site of Uldaman in the Badlands.$b$bMuch of the other notes relate to the heat resistant properties.$b$bUsed as a paperweight for the plans is a hunk of what appears to be unrefined indurium ore.",
  },
  [1195] = {
    ["T"] = "The Sacred Flame",
    ["O"] = "Return a Filled Etched Phial to Zangen Stonehoof in Thunder Bluff.",
    ["D"] = "The Galak centaur in the Thousand Needles are protecting an artifact from the time of the centaurs\' creation.$b$bWe would like to retrieve it, but we require a phial of water from one of the night elves\' moonwells.$b$bTo collect the water, you will first need to obtain one of the phials carried by the dryads near the Raynewood Retreat in the heart of Ashenvale Forest. There is a moonwell near the western bank of the Falfarren River, south of the main road, where you can fill the phial.",
  },
  [1196] = {
    ["T"] = "The Sacred Flame",
    ["O"] = "Deliver the Filled Etched Phial to Rau Cliffrunner at the Freewind Post.",
    ["D"] = "Bring the filled phial to Rau Cliffrunner at the Freewind Post in southeastern Thousand Needles. You can get to the Thousand Needles by taking the Great Lift at the southern edge of the Barrens.$b$bI wouldn\'t be surprised if you can help us out further.",
  },
  [1197] = {
    ["T"] = "The Sacred Flame",
    ["O"] = "Retrieve the Cloven Hoof for Rau Cliffrunner at the Freewind Post.",
    ["D"] = "Using the water you brought, you will be able to temporarily douse the flame that holds the centaur relic. It will not work permanently, however, so you will have to be quick about it.$b$bYou will find Splithoof Crag, the centaurs\' stronghold, to the north of here. The flame is kept in Splithoof Hold, a heavily guarded part of the crag.$b$bBe careful, though, the Galak centaur have great numbers here, and will be rather reluctant to surrender something so dear to them.",
  },
  [1198] = {
    ["T"] = "In Search of Thaelrid",
    ["O"] = "Seek out Argent Guard Thaelrid in Blackfathom Deeps.",
    ["D"] = "Many rumors circulate these days about the union of the Twilight\'s Hammer and the naga in Blackfathom Deeps on the coasts of Ashenvale.$b$bMy role here with the Argent Dawn is to collect as much information as I can, so we know when and where help is needed.$b$bIt has been weeks now since I sent a scout to check on the activity at Blackfathom Deeps. Argent Guard Thaelrid has not yet reported back.$b$bMy other scouts are preoccupied with pressing matters.$b$bSeek out and aid Thaelrid for me, $n.",
  },
  [1199] = {
    ["T"] = "Twilight Falls",
    ["O"] = "Bring 10 Twilight Pendants to Argent Guard Manados in Darnassus.",
    ["D"] = "Empowered by the ancient rites of the chaotic Old Gods the members of the Twilight\'s Hammer strive to bring about the return of their dark patrons and spread death and terror across the world.$b$bWe believe that the Twilight\'s Hammer has allied with the naga and are amassing in Blackfathom Deeps on the coast of Ashenvale.$b$bWhatever evil plan brews in the depths of the old temple must be thwarted.$b$bWage war on the Hammer and bring back proof of your deeds. Your efforts will be rewarded handsomely.",
  },
  [1200] = {
    ["T"] = "Blackfathom Villainy",
    ["O"] = "Bring the head of Twilight Lord Kelris to Dawnwatcher Selgorm in Darnassus.",
    ["D"] = "Strength has left me. Your help is needed!$b$bLong ago this site was a great temple of Elune. But misfortune led to ruin when the corruption of an Old God seeped up from beneath the earth and tainted the sacred Moon Well.$b$bAku\'Mai, servant of the Old God, rose from the waters.$b$bThe Twilight\'s Hammer cultists have allied with the naga to occupy these grounds. The cultists, led by Kelris, sacrifice innocents to Aku\'Mai for power.$b$bSlay Kelris and bring his head to Selgorm in Darnassus, $N, please.",
  },
  [1201] = {
    ["T"] = "Theramore Spies",
    ["O"] = "Kill 9 Theramore Infiltrators, then return to Nazeer Bloodpike in Brackenwall Village.",
    ["D"] = "The Horde and Alliance hold an uneasy truce.  Outlying skirmishes and covert actions are taken by one side against the other, and it is our duty to ensure the Horde is on top!$B$BNow is the chance to do just that.$B$BAlliance infiltrators have taken positions east of Brackenwall Village.  They spy on the village and report our movements to Theramore, which we cannot allow.$B$BHunt down the infiltrators and slay them.  Send a clear message to their superiors that we will not tolerate their spies\' presence!",
  },
  [1202] = {
    ["T"] = "The Theramore Docks",
    ["O"] = "Bring the Captain\'s Documents to Nazeer Bloodpike in Brackenwall Village.",
    ["D"] = "$N, two of our spies were sent to steal documents from a ship at the Theramore docks.  They gained the papers but were discovered, and were forced to throw the documents into the sea.$B$BOne spy later escaped, but was unable to recover the prize.  He reported his failure to me, and I now charge the task to you.$B$BThe documents are in a footlocker, in the waters somewhere beneath the Theramore docks.  Find those documents, $N, and bring them to me.",
  },
  [1203] = {
    ["T"] = "Jarl Needs a Blade",
    ["O"] = "Bring a Moonsteel Broadsword to Jarl in Dustwallow Marsh.",
    ["D"] = "I have cutting to do.  I can\'t tell you what I\'m going to cut, but... I need myself a blade.  A certain blade...$B$BSome people call it a moonsteel broadsword.  I call it a swing blade.$B$BYou help me?  Maybe you find it in town, maybe you talk to a blacksmith and he\'ll make it special.$B$BBut don\'t sleep on this.  What I\'m cutting won\'t keep forever...",
  },
  [1204] = {
    ["T"] = "Mudrock Soup and Bugs",
    ["O"] = "Bring 8 Forked Mudrock Tongues to Morgan Stern in Theramore.",
    ["D"] = "I\'m working on a dish I call \"Mudrock Soup and Bugs.\"  I still need the major ingredients, and... well, I don\'t think I\'d last long trudging through Dustwallow Marsh!$B$BSo, $N, can you do some hunting for me?  First, I need the tongues of Mudrock turtles.  You can find Mudrocks along the coast, mostly around the bay.$B$BBut I don\'t want just any tongues!  I hear that the tongues of some Mudrocks are forked...$B$BThey say the forked tongues have a distinct flavor, and I want that flavor in my dish...",
  },
  [1205] = {
    ["T"] = "Deadmire",
    ["O"] = "Bring Deadmire\'s Tooth to Melor in Thunder Bluff.",
    ["D"] = "Deadmire is an ancient crocolisk in Dustwallow marsh.  And it is his time to die.$B$BOld bones grind as he pulls his huge body through the swamp, and although he still moves with the strength and speed of youth, his aging body tortures the great spirit within it, a spirit whose flame will not waver.$B$BYet now he lives in constant, maddening pain.  You must end the life of this noble creature, $N.  You must lead Deadmire to peace.",
  },
  [1206] = {
    ["T"] = "Jarl Needs Eyes",
    ["O"] = "Bring 20 Unpopped Darkmist Eyes to \"Swamp Eye\" Jarl at the Swamplight Manor.",
    ["D"] = "Eye juice is good juice for drinking.  Too bad most critters only have two eyes.$B$BBut spiders have lots of eyes.  Lots and lots.  And Darkmist eye juice is tasty.  Smooth and salty!  Unpopped spider eyes... I taste them already.$B$BDarkmist spiders are west of here, in the Darkmist Cavern.  You figure the spiders are named after the cave... or is it the other way around?",
  },
  [1218] = {
    ["T"] = "Soothing Spices",
    ["O"] = "Bring 3 Soothing Spices to \"Swamp Eye\" Jarl in Dustwallow Marsh.",
    ["D"] = "I like cooking.  Good swamp cooking.  Fried green frog legs and the like.$B$BAnd for cooking, I need spice.  Soothing spice.  Then I\'ll make a stew and stew it up and suck it down and give you some too.$B$BDo you like stew?",
  },
  [1219] = {
    ["T"] = "The Orc Report",
    ["O"] = "Bring the Spy\'s Report to Lieutenant Aden in Theramore.",
    ["D"] = "Sifting through the loose dirt, the severed hand of an orc is found.  And clutched in the hand is a bone-carved tube.  And in the tube is a dirt-stained parchment.$B$BThe parchment is written in orcish... and it looks important.",
  },
  [1220] = {
    ["T"] = "Captain Vimes",
    ["O"] = "Bring the Spy\'s Report to Captain Garran Vimes.",
    ["D"] = "Captain Vimes should see the orc report you found, $N.  He knows the orcish language better than most.  And he should know that there are Horde spies near Theramore!$B$BThe captain is in the barracks, on the northeastern side of Theramore Isle.",
  },
  [1221] = {
    ["T"] = "Blueleaf Tubers",
    ["O"] = "Grab a Crate with Holes.$BGrab a Snufflenose Command Stick.$BGrab and read the Snufflenose Owner\'s Manual.$B$BIn Razorfen Kraul, use the Crate with Holes to summon a Snufflenose Gopher, and use the Command Stick on the gopher to make it search for Tubers.$B$BBring 6 Blueleaf Tubers, the Snufflenose Command Stick and the Crate with Holes to Mebok Mizzyrix in Ratchet.",
    ["D"] = "Blueleaf tubers are a delicacy around the world!  But they\'re rare, very rare.  The only place to find them is here in the Barrens, deep in the earth, in Razorfen Kraul.$B$BAnd even then, they\'re impossible to find unless you know just where to look!  That\'s why I\'ve trained these snufflenose gophers to find them for me.  They have great noses and can smell a tuber from fifty paces away.$B$BIt won\'t be easy, but if you get me some tubers I\'ll pay you handsomely.",
  },
  [1222] = {
    ["T"] = "Stinky\'s Escape",
    ["O"] = "Escort \"Stinky\" Ignatz, then speak with Morgan Stern in Theramore.",
    ["D"] = "Great, finally something that doesn\'t want to eat me!$B$BI need help handling the critters around here, and you fit the mold, $gbuddy:lady;.$B$BThe cook Morgan Stern is researching a new recipe, and he sent me out here to gather bogbean leaves.  I guess he thinks they\'re tasty.$B$BAnyway, while looking for a bogbean plant... I got myself surrounded!  Can you help me out of here, and watch my back as I look for the bogbean?",
  },
  [1238] = {
    ["T"] = "The Lost Report",
    ["O"] = "Bring the Spy\'s Report to Nazeer Bloodpike in Brackenwall Village.",
    ["D"] = "Sifting through the loose dirt, the severed hand of an orc is found.  And clutched in the hand is a bone-carved tube.  And in the tube is a dirt-stained parchment.$B$BThe parchment is a report made by an orc who spent weeks spying on the city of Theramore.$B$BAfter scanning the parchment, it is clear that it must be brought to the orc spymaster in Brackenwall Village.",
  },
  [1239] = {
    ["T"] = "The Severed Head",
    ["O"] = "Bring the Defiant Orc Head to Nazeer Bloodpike in Brackenwall Village.",
    ["D"] = "Vigorous digging reveals the head of a male orc, raggedly severed at the neck.  His lips are frozen in a snarl, and its dark eyes stare defiantly.",
  },
  [1240] = {
    ["T"] = "The Troll Witchdoctor",
    ["O"] = "Take the Defiant Orc Head to Kin\'weelay in the Grom\'gol Base Camp in Stranglethorn Vale.",
    ["D"] = "We must know the events surrounding Marg\'s death!  And the only way to do that, $N, is through the witch-magic of trolls.$B$BBring Marg\'s head to Kin\'weelay in Stranglethorn Vale, at the Grom\'gol Base Camp.$B$BKin\'weelay is the most powerful witchdoctor of the Darkspear trolls.  If anyone can speak to Marg Nighteye\'s spirit, it is he.",
  },
  [1241] = {
    ["T"] = "The Missing Diplomat",
    ["O"] = "Find Jorgen in Stormwind City.",
    ["D"] = "Recently a diplomat was sent to Theramore to meet with Jaina Proudmoore. That diplomat never arrived.$B$BI believe the Defias are involved in this plot, but I\'m not sure how. The diplomat\'s disappearance still eludes public attention, but it can\'t remain that way for long.$B$BAgents of the king are already scouring the city for clues, but I have my own contacts I would like involved.$B$BIn the Valley of Heroes is an old friend of mine named Jorgen. Find him, give him this note, and follow his instructions.",
  },
  [1242] = {
    ["T"] = "The Missing Diplomat",
    ["O"] = "Delivery Jorgen\'s Sealed Note to Elling Trias in Stormwind City.",
    ["D"] = "Take this here note to Elling Trias. Don\'t say nothin\' else to him. Just be polite and wait for him to respond. And don\'t worry, he will.$B$BI\'m gonna only impress this on ya once more, $N: treat this matter seriously, and keep your yapper shut.$B$BYa can find Trias at his cheese shop, Trias\' Cheese, in the Trade District. Good luck.",
  },
  [1243] = {
    ["T"] = "The Missing Diplomat",
    ["O"] = "Give the Sealed Note to Watcher Backus in Duskwood.",
    ["D"] = "Jorgen, you ol\' sonuva.... So, that\'s how it\'s going to be, huh?$B$BAll right, I can deal with that. I hope he finds the Twisting Nether that much sooner!$B$BHere\'s what\'s gonna happen, $N. I\'ll start using my contacts here, but you\'re doing most of the footwork so I save some coin.$B$BHead to Darkshire in Duskwood and find Watcher Backus--he usually patrols the north road right outside of town. Just tell him you\'re looking into any Defias activity for me and he\'ll help you out with any information he can.",
  },
  [1244] = {
    ["T"] = "The Missing Diplomat",
    ["O"] = "Find the Defias Docket and return it to Watcher Backus in Duskwood.",
    ["D"] = "Defias activity? Well, there\'s always some activity; even if they keep to themselves we consider them a threat.$B$BBut now that I think about it, there was a recent sighting that seemed odd. It must have been a couple weeks ago, but some of their agents had gathered around Addle\'s Stead. From the report, I\'d guessed something big went down.$B$BWhy don\'t you check there first and bring me back anything you find?$B$BThe farmstead is south of the road from Westfall just as you enter Duskwood.",
  },
  [1245] = {
    ["T"] = "The Missing Diplomat",
    ["O"] = "Bring the Defias Docket to Elling Trias in Stormwind.",
    ["D"] = "Whoa, this looks way too complex to be something to just lead you astray. I shouldn\'t even be looking at this stuff. Take it to Trias--he\'ll be able to make sense of it. And DON\'T let anyone get in your way. This is bigger than me. It might even be bigger than him, but we\'ll see.$B$BAnd do me a favor, $N, don\'t tell anyone I helped you. If my superiors found out I knew about any of this and didn\'t share it with them they\'d probably hang me.",
  },
  [1246] = {
    ["T"] = "The Missing Diplomat",
    ["O"] = "Speak to Dashel Stonefist in Stormwind.",
    ["D"] = "Yeah, now that I think about it... Dashel... Dashel something... What was his name? FIST! Dashel Stonefist. They call him Fist in some circles--probably on account that he likes to get in brawls.$B$BYou can find him in Old Town usually--right smack dab in the center of it. Go talk to him. If you have to, use a sword or something heavy and blunt to persuade him to open up some. And don\'t hesitate. If he\'s in this as deep as these papers suggest, then he\'s not going to give you any information willingly.",
  },
  [1247] = {
    ["T"] = "The Missing Diplomat",
    ["O"] = "Speak to Elling Trias in Stormwind.",
    ["D"] = "So yeah... um... I might know something \'bout that meeting at Addle\'s Stead. Happened a few weeks back I think. I really didn\'t have any part in it. Me part was done when plan A failed... damn adventurers.$B$BThe guy we got on the inside for plan B came out of Menethil; guy called Slim. That\'s all I know regarding their backup plan.",
  },
  [1248] = {
    ["T"] = "The Missing Diplomat",
    ["O"] = "Speak to Mikhail in the Wetlands.",
    ["D"] = "A contact of mine retired in the Wetlands at the harbor city of Menethil. He should be willing to help on your word alone if you tell him you\'re working for me. His name is Mikhail. He mentioned becoming a bartender before leaving Stormwind a few years ago.$B$BSpeak to him, find this Slim, and find out if there\'s anything else to know about this kidnapped diplomat. We need to find out where they\'ve taken him, or at least who he is. The docket doesn\'t mention anything about their plans after he\'s captured.",
  },
  [1249] = {
    ["T"] = "The Missing Diplomat",
    ["O"] = "Subdue Tapoke Jahn before he escapes, and then return to Mikhail in Menethil.",
    ["D"] = "You say Elling sent you here? Well then, the hospitality I expressed is now tenfold. Anything you need, just ask. I will do my best to aid you.$B$BHmm, Slim, you say? Not sure the name sounds familiar, but I\'ll keep my eyes out for him.$B$BDon\'t look now, $N, but the gentleman over by the exit is looking over here. He\'s listening to our conversation a little too intently if you catch my meaning? If I were you, I\'d do what you can to subdue him before he makes a break for the door.",
  },
  [1250] = {
    ["T"] = "The Missing Diplomat",
    ["O"] = "Speak to Mikhail in the Wetlands.",
    ["D"] = "The Defias didn\'t pay me well enough to die for their cause. They took the king. Yeah, the king of Stormwind, not some lowly diplomat. He was on his way to Theramore to meet with Jaina Proudmore--it was a big deal apparently, and a big secret. But it\'s not just the Defias that are involved. They\'re working with someone. I think they knew this was bigger than them.$B$BMy part was just to get their man on board the king\'s ship while it was in the harbor. The guy\'s name was Hendel. That\'s all I know. I swear!",
  },
  [1251] = {
    ["T"] = "The Black Shield",
    ["O"] = "Bring the Blackened Iron Shield to Krog in Brackenwall Village.",
    ["D"] = "An iron shield, blackened by the fire that raged through the inn, is affixed to the crumbling chimney, one of the few remaining parts of the structure that once made up the Shady Rest Inn.$b$bThe shield can be removed from the brick of the chimney.",
  },
  [1252] = {
    ["T"] = "Lieutenant Paval Reethe",
    ["O"] = "Bring Reethe\'s Badge to Captain Garran Vimes in Theramore.",
    ["D"] = "A glint of light on the ground catches your eye from underneath the rubble. Brushing away debris and ash caked onto the object reveals the insignia of a gold anchor on white enameled on the surface: the sign of Theramore.$b$bUnderneath the anchor is embossed the name \"Lieutenant Paval Reethe\".",
  },
  [1253] = {
    ["T"] = "The Black Shield",
    ["O"] = "Bring the Blackened Iron Shield to Captain Garran Vimes in Theramore.",
    ["D"] = "An iron shield, blackened by the fire that raged through the inn, is affixed to the crumbling chimney, one of the few remaining parts of the structure that once made up the Shady Rest Inn.$b$bThe shield can be removed from the brick of the chimney.",
  },
  [1258] = {
    ["T"] = "... and Bugs",
    ["O"] = "Bring 12 Pristine Crawler Legs to Morgan Stern in Theramore.",
    ["D"] = "Another component of my new recipe is the shelled leg of a giant crab.  And the crabs they have in Dustwallow -- Muckshells they call them -- just won\'t do!  I\'m afraid the only suitable crabs live far away, in the Swamp of Sorrows.$B$BThe shell of the crab leg is a key element of the dish\'s presentation so I need pristine, unscarred legs.  An adult crab might only have one or two such legs, and I need a lot in order to research my dish.$B$BSo, $N... are you willing to travel?",
  },
  [1259] = {
    ["T"] = "Lieutenant Paval Reethe",
    ["O"] = "Find out more about Reethe from Adjutant Tesoran in Theramore.",
    ["D"] = "I don\'t recognize the name of Lieutenant Paval Reethe. Your best bet would be to check with Adjutant Tesoran. He keeps track of all the records for the Theramore guard, from personnel to their equipment.$b$bHe should be able to tell you who this Reethe is with a quick check of his books.",
  },
  [1260] = {
    ["T"] = "Morgan Stern",
    ["O"] = "Speak with Morgan Stern in Theramore.",
    ["D"] = "I\'m developing a new menu for the restaurant.  It will be filled with rural dishes with a \"bayou\" theme.  I think my clientele at the Blue Recluse will love them!$B$BI sent my nephew Morgan to Theramore, a city on the edge of the Dustwallow Marsh.  He\'s there gathering recipes from the locals, and I must know what he found so far.$B$BThe trip to Theramore is long.  Too long for me.  But I bet a young $c like yourself would jump at the chance to travel, yes?",
  },
  [1261] = {
    ["T"] = "Marg Speaks",
    ["O"] = "Bring the Jeweled Pendant to Nazeer in Brackenwall Village.",
    ["D"] = "Cursed muckshell!  You can\'t kill Marg!  Ah, but I am dead.  So it must have slain me...$B$BBut my mission!  My mission!  You, $N, must complete it.$B$BWhile near Theramore, I spied a group of Alliance soldiers camped on the beach.  And one soldier wore a pendant... a pendant laden with more gems than any soldier could afford!$B$BThat night I snuck into their camp and stole the pendant, but on my return to base a muckshell crab-man killed me!$B$B$N, you must find that muckshell and recover the pendant!",
  },
  [1262] = {
    ["T"] = "Report to Zor",
    ["O"] = "Bring the Jeweled Pendant to Zor Lonetree in Orgrimmar.",
    ["D"] = "It is strange that the pendant Marg stole was in the hands of common soldiers.  Perhaps its owner was in disguise, or perhaps the soldiers themselves stole it!$B$BRegardless of which is true, this is a noble\'s pendant and we must discover its true owner if we are to unravel its mystery.$B$BTake the pendant to Zor Lonetree in Orgrimmar.  He is well schooled in Alliance politics, and will know if this pendant has a history among the nobles of Stormwind.",
  },
  [1264] = {
    ["T"] = "The Missing Diplomat",
    ["O"] = "Find Commander Samaul in Dustwallow Marsh.",
    ["D"] = "The beginning of every month the Kul Tiras Marines all get paid and usually leave Theramore for somewhere else. A lot of them come here to womanize, carouse, and just have a good time. One of those groups I\'d consider regulars here at the tavern, and I believe this Hendel is among them.$B$BCommander Samaul in Theramore would be able to help you find him if Hendel\'s still stationed there. He\'s completely loyal to Proudmoore, but if you drop Elling\'s name, he should help you out... discreetly.",
  },
  [1265] = {
    ["T"] = "The Missing Diplomat",
    ["O"] = "Search Sentry Point for any sign of Private Hendel.",
    ["D"] = "Hendel? Yes, I know the man. But why would you need one of my soldiers?$B$BElling sent you to find him? Hmm, I don\'t like the sound of that. Elling wouldn\'t have you come to me first if your business wasn\'t troubling. I understand if you can\'t tell me what this is about, but know this: I have a duty to protect these lands. If you\'re going to cause a disturbance, then I suggest you inform me of whatever you can.$B$BI believe Hendel was stationed at the first tower outside of Theramore. Be cautious.",
  },
  [1266] = {
    ["T"] = "The Missing Diplomat",
    ["O"] = "Find Private Hendel in Dustwallow Marsh.",
    ["D"] = "Private Hendel is to the west of here. He\'s currently on guard duty protecting some of Theramore\'s infiltrators while they rest. They\'ve been keeping tabs on the Horde base, Brackenwall, and looking for anything else that might threaten our borders.$B$BYou should have no problem finding him; just look for the tents along the road.$B$BI have another matter to attend to, but I shall catch up to you soon enough. You can always find me in the Lady Proudmoore\'s tower if you have any problems.",
  },
  [1267] = {
    ["T"] = "The Missing Diplomat",
    ["O"] = "",
    ["D"] = "",
  },
  [1268] = {
    ["T"] = "Suspicious Hoofprints",
    ["O"] = "Report about the suspicious prints at the Shady Rest Inn to Krog in Brackenwall Village.",
    ["D"] = "Rows of hoofprints lead away from the still-smoking skeleton of the Shady Rest Inn. There are at least a few visible impressions, with more rows of prints almost imperceptible in the soft mud of the marsh.",
  },
  [1269] = {
    ["T"] = "Lieutenant Paval Reethe",
    ["O"] = "Bring Reethe\'s Badge to Krog in Brackenwall Village.",
    ["D"] = "A glint of light on the ground catches your eye from underneath the rubble. Brushing debris and ash caked onto the object reveals the insignia of a gold anchor on white, enameled on the surface: the sign of Theramore.$b$bUnderneath the anchor is embossed the name \"Lieutenant Paval Reethe\".",
  },
  [1270] = {
    ["T"] = "Stinky\'s Escape",
    ["O"] = "Escort \"Stinky\" Ignatz, then speak with Mebok Mizzyrix in Ratchet.",
    ["D"] = "Great, finally something that doesn\'t want to eat me!!$B$BI need help handling the critters around here, and you fit the mold, $gbuddy:lady;.$B$BA goblin from Ratchet named Mebok Mizzyrix needs some bogbean leaves for a potion, or something... I honestly don\'t care why -- his money was good and I know this swamp, so here I am.$B$BToo bad I got myself surrounded before I found the bogbean!  Can you help me out of here, and watch my back as I look for Mebok\'s plant?",
  },
  [1271] = {
    ["T"] = "Feast at the Blue Recluse",
    ["O"] = "",
    ["D"] = "",
  },
  [1273] = {
    ["T"] = "Questioning Reethe",
    ["O"] = "Go with Ogron to speak with Reethe, then return to Krog in Brackenwall Village.",
    ["D"] = "It took a long time, but I found Reethe. He hide good for a human.$b$bOgron worried that he might be crazy after so much time in swamp. You come with me for we go get answers from him.",
  },
  [1274] = {
    ["T"] = "The Missing Diplomat",
    ["O"] = "Find Bishop DeLavey in Stormwind Keep.",
    ["D"] = "Excuse me, $g sir : madam;. Bishop DeLavey asked me to approach adventurers that might be able to help him with a delicate matter.$B$BIf you could quietly head to Stormwind Keep and speak to him at your earliest convenience, I\'m sure he would appreciate your help. Again, please be discreet. It is a matter of some importance.",
  },
  [1275] = {
    ["T"] = "Researching the Corruption",
    ["O"] = "Gershala Nightwhisper in Auberdine wants 8 Corrupt Brain stems.",
    ["D"] = "Foolish blood elves toil with demonic magic. Have they not witnessed what happened with the naga and with the satyrs of Kalimdor?$b$bI fear that the blood elves will meet a similar deformation. Azeroth cannot afford to give birth to another vile race of monsters.$b$bI wish to study the brain stems of the satyr and naga so that I might deduce what fate the elves are headed toward. In Blackfathom Deeps, on the coast of Ashenvale, you will find both satyr and naga.$b$b\'Tis a long journey, $n.",
  },
  [1276] = {
    ["T"] = "The Black Shield",
    ["O"] = "Show the Blackened Iron Shield to Mosarn in Thunder Bluff.",
    ["D"] = "Ah, I remember now! Mosarn is a well-respected blacksmith in Thunder Bluff. I knew I had heard his name somewhere, before.$b$bIn thanks, the Earthen Ring awarded me with a set of armor crafted by Mosarn. Very good work.$b$bPerhaps he can tell you more about the shield he crafted.$b$bIt\'s not much of a lead, but since we didn\'t get anything useful out of Reethe, it\'s all we have.",
  },
  [1282] = {
    ["T"] = "They Call Him Smiling Jim",
    ["O"] = "Speak with Captain Garran Vimes on the top floor of Foothold Citadel.",
    ["D"] = "I can\'t help it if I feel sorry for ol\' Jim there. He wasn\'t always like that. Ran an inn out in the marsh for a while, wonderful place.$b$bLook, I don\'t really want to talk about it. Bring it up with Captain Vimes if you really have to. He\'s stationed on the top floor of Foothold Citadel, the large barracks to the east of Lady Jaina\'s tower.",
  },
  [1284] = {
    ["T"] = "Suspicious Hoofprints",
    ["O"] = "Report about the suspicious prints at the Shady Rest Inn to Captain Garran Vimes in Theramore.",
    ["D"] = "Rows of hoofprints lead away from the still-smoking skeleton of the Shady Rest Inn. There are at least a few visible impressions, with more rows of prints almost imperceptible in the soft mud of the marsh.",
  },
  [1285] = {
    ["T"] = "Daelin\'s Men",
    ["O"] = "Return the information about Reethe to Captain Garran Vimes in Theramore.",
    ["D"] = "Reddon... Redpath... Ah, here it is! Lieutenant Paval Reethe.$b$bJoined the Theramore guard along with most of the recruits that came across the sea with Lady Proudmoore. Enlisted with the marines of Kul Tiras at the age of sixteen. Distinguished service, promotions...$b$bListed as missing.",
  },
  [1286] = {
    ["T"] = "The Deserters",
    ["O"] = "Get information about Reethe from Balos Jacken and the deserters in Dustwallow Marsh.",
    ["D"] = "It has been a struggle maintaining a presence in the marsh, but through strength of our resolve, we have been able to maintain several defensive watchtowers throughout the area.$b$bThe latest information and scouting reports I have received indicate that a group of deserters has squatted in our abandoned tower at Lost Point, southeast of the inn.$b$bThe local lieutenant seems to be Balos Jacken. See if you can get some information out of him.$b$bBe warned, $n. He may be a tough nut to crack.",
  },
  [1287] = {
    ["T"] = "The Deserters",
    ["O"] = "Speak with Captain Garran Vimes in Theramore about Reethe.",
    ["D"] = "Reethe? That coward?$b$bWe threw him out of camp because he insisted on raiding the supplies of the ogres up at Brackenwall Village. We\'re not so many in number that we could stand up to those mindless brutes.$b$bHe crossed the line one too many times, so we left him to fend for himself.$b$bNow, I helped you out... So, why don\'t you help us out and forget you bumped into us?",
  },
  [1288] = {
    ["T"] = "Vimes\'s Report",
    ["O"] = "Bring Vimes\'s Report to Lady Jaina Proudmoore in Theramore.",
    ["D"] = "You\'ve done some good work, $n, best I\'ve seen from an amateur.$b$bLady Proudmoore asked that I keep her apprised of this delicate situation, and I haven\'t spoken to her for some time now.$b$bI was going to deliver my report to her later, but it seems more fitting that you should be the one to speak with her about it. You can probably answer her questions better than I could.",
  },
  [1288] = {
    ["T"] = "Vimes\'s Report",
    ["O"] = "Bring Vimes\'s Report to Lady Jaina Proudmoore in Theramore.",
    ["D"] = "You\'ve done some good work, $n, best I\'ve seen from an amateur.$b$bLady Proudmoore asked that I keep her apprised of this delicate situation, and I haven\'t spoken to her for some time now.$b$bI was going to deliver my report to her later, but it seems more fitting that you should be the one to speak with her about it. You can probably answer her questions better than I could.",
  },
  [1289] = {
    ["T"] = "Vimes\'s Report",
    ["O"] = "Return to Captain Garran Vimes in Theramore.",
    ["D"] = "Well. judging from Captain Vimes\'s report. you\'ve been a huge help to his investigation. and for that I thank you.$B$BI can put my mind more at ease knowing that the matter is in such capable hands. If you would. I would request that you return to Captain Vimes and help him in getting to the bottom of this mystery.",
  },
  [1301] = {
    ["T"] = "James Hyal",
    ["O"] = "Speak with Vincent Hyal in Menethil Harbor.",
    ["D"] = "Before working here at the Blue Recluse, I was trained by one of the best chefs and innkeepers I\'d ever met. His name was James Hyal. Everything I have and everywhere I\'ve gotten in life is thanks to him.$b$bI wonder how he\'s doing. His brother Vincent and he went to work in a tavern in Menethil Harbor, but I haven\'t heard from him recently.$b$bIf you ever happen to be headed in that direction, maybe you could say hello to him for me?",
  },
  [1302] = {
    ["T"] = "James Hyal",
    ["O"] = "Speak with Clerk Lendry at Foothold Citadel in Theramore.",
    ["D"] = "My brother James went with Lady Proudmoore across the sea. Last I heard, the fighting had settled down over there, and James was setting up an inn somewhere on the continent. Wherever he is, I\'m sure he\'s doing good business. Damn fine cook he was.$b$bI haven\'t heard from him for a while now--too busy with his inn, I\'d wager--but the clerk in Theramore could probably point you to him. Look for him on the top floor of that keep near the middle of the city. What\'s it called... Foothold Citadel, that\'s it.",
  },
  [1318] = {
    ["T"] = "Unfinished Gordok Business",
    ["O"] = "Find the Gauntlet of Gordok Might and return it to Captain Kromcrush in Dire Maul.$B$BAccording to Kromcrush, the \"old timey story\" says that Tortheldrin - a \"creepy\" elf who called himself a prince - stole it from one of the Gordok kings.",
    ["D"] = "Now you da king, so maybe you get prized Gordok artifact back from creepy elf who calls himself Prince!  Da king before you failed, just like all da other kings since Tortheldrin - da creepy elf - stole it from old timey Gordok king!$B$BArtifact is called da Gauntlet of Gordok Might.  Old timey stories say only da one true king da Gordok can get it back.  Old timey story also say creepy elf is still around here - you find it and it will become mighty gauntlet you wear forever!  Yar, I show you!",
  },
  [1319] = {
    ["T"] = "The Black Shield",
    ["O"] = "Show the Blackened Iron Shield to Caz Twosprocket in Theramore.",
    ["D"] = "I\'m sorry I can\'t be of much help, $n, but I can refer you to someone who would be. Caz Twosprocket, one of our best blacksmiths, works the forge at the smithy.$b$bTake the shield to him, and see what he can tell you.",
  },
  [1320] = {
    ["T"] = "The Black Shield",
    ["O"] = "Speak with Captain Garran Vimes in Theramore.",
    ["D"] = "Ah, I almost missed this... The leather strap on the back must have burned off, but it seems like it was made to fit a large person. I\'d guess a tauren, actually.$b$bThat\'s about all I can tell you. Tell Captain Vimes I\'m sorry I couldn\'t be of more help.",
  },
  [1321] = {
    ["T"] = "The Black Shield",
    ["O"] = "Show the Blackened Iron Shield to Do\'gol in Brackenwall Village.",
    ["D"] = "Hmm... I don\'t see anything out of the ordinary with this shield. We should have Do\'gol, the blacksmith, take a look at it, and see if he can tell us more about it, but I wouldn\'t hold out hope...",
  },
  [1322] = {
    ["T"] = "The Black Shield",
    ["O"] = "Acquire 5 Acidic Venom Sacs for Do\'gol in Brackenwall Village.",
    ["D"] = "Hmmm, there\'s writing right here on the rim of the shield, but it\'s hard to read from all the burned stuff.$b$bI need acid to make it clear. Venom from the darkmist spiders would do good. The spiders have taken over an old mine, the Darkmist Cavern, northwest of the village. Get me some of their sacs and I can clean this up.",
  },
  [1323] = {
    ["T"] = "The Black Shield",
    ["O"] = "Talk with Krog in Brackenwall Village.",
    ["D"] = "Oh, I think I might have dropped too much acid onto the shield. Let me just rub a little...$b$bHm, kind of hard to read, but it looks like the name Mosarn. Never heard of him. Maybe Krog knows. Krog knows many people.",
  },
  [1324] = {
    ["T"] = "The Missing Diplomat",
    ["O"] = "Defeat Private Hendel in Dustwallow Marsh.",
    ["D"] = "What?! You come here accusing me of allying with the Defias, and think I\'ll just stand here while you insult me?! Something tells me you\'ve had this coming for a long time.",
  },
  [1338] = {
    ["T"] = "Stormpike\'s Order",
    ["O"] = "Bring Stormpike\'s Request to Furen Longbeard in Stormwind.",
    ["D"] = "There is a dwarven armorsmith whose work I admire greatly.  His name is Furen Longbeard, and his skill is unmatched.  I need a new shield, and I must have one of his!$B$BThe problem is... Furen is far to the south, in the dwarven district within Stormwind.  In human lands!  I can\'t imagine why he would live there, so far from Ironforge.  The humans must pay him a king\'s ransom to keep him there!$B$BSo, if you want to travel and don\'t mind making some money, then will you send Furen my request for a shield?",
  },
  [1339] = {
    ["T"] = "Mountaineer Stormpike\'s Task",
    ["O"] = "Speak with Mountaineer Stormpike.",
    ["D"] = "I hear Mountaineer Stormpike is looking for a runner.  Someone to do a little traveling for him.  How about it?  Are you the $r for the job?$B$BIf so, then you\'ll find Stormpike at the top of the northern guard tower.",
  },
  [1358] = {
    ["T"] = "Sample for Helbrim",
    ["O"] = "Bring the Wolf Heart Sample to Apothecary Helbrim in the Barrens.",
    ["D"] = "A colleague in Kalimdor, Apothecary Helbrim, is researching the effects of Lordaeron toxins on the beasts of Kalimdor.  He would be very interested in a sample of the wolf hearts Renferrel sent me.$B$BHelbrim is stationed in the Barrens, in a village known as the Crossroads.  To reach him, you must travel via zeppelin to the orc capital of Orgrimmar, then travel south, by foot, to Durotar.  Head west into the Barrens, and you will soon reach the Crossroads.",
  },
  [1359] = {
    ["T"] = "Zinge\'s Delivery",
    ["O"] = "Bring the Wolf Heart Samples to Apothecary Zinge in the Undercity.",
    ["D"] = "The wolf hearts you acquired possess a quality unseen in similar specimens.  There is a toxicity to them that should be deadly to the very wolves in which the hearts beat!$B$BIt\'s quite incredible, and we must study it further.  I kept a few samples of the hearts acquired.  Take them to my colleague, Apothecary Zinge.  You will find her in our headquarters in the Undercity.",
  },
  [1360] = {
    ["T"] = "Reclaimed Treasures",
    ["O"] = "Get Krom Stoutarm\'s treasured possession from his chest in the North Common Hall of Uldaman, and bring it to him in Ironforge.",
    ["D"] = "I had myself a fine job as a digger at the Uldaman excavation site in the Badlands... that is until those blasted troggs showed up.  I escaped with my hide barely intact, and nothing else.  In the chaos, I left my most treasured possession.  It\'s safe from the troggs in my chest, sure, but it\'s right in the thick of \'em.$B$BI\'d be in your debt, both in honor and coin, if you could fetch it for me!  A chest with my name on it is at the end of the North Common Hall.  That\'s the one you need to get in.",
  },
  [1361] = {
    ["T"] = "Regthar Deathgate",
    ["O"] = "Speak with Regthar Deathgate.",
    ["D"] = "A warrior of the Horde, Regthar Deathgate, is recruiting agents for a mission into the land of Desolace.  Speak with him if you wish to volunteer.  He is stationed in the Barrens, in a series of bunkers west of the Crossroads.$B$BAnd sharpen your weapons.  Desolace is a place torn by war.",
  },
  [1362] = {
    ["T"] = "The Kolkar of Desolace",
    ["O"] = "Speak with Felgur Twocuts at Ghost Walker Post in Desolace.",
    ["D"] = "The Kolkar centaur live in Desolace, and their forces have spread into the Barrens.  This is a known fact and we have Horde agents dealing with them here.$B$BBut as long as the centaur are strong in Desolace, they will always pose a threat.  It is time we rid ourselves of that threat.$B$BSpeak with Felgur Twocuts at Ghost Walker Post in Desolace.  He was sent to gather intelligence on the centaur.  He will know how to best deal with the Kolkar of Desolace.",
  },
  [1363] = {
    ["T"] = "Mazen\'s Behest",
    ["O"] = "Speak with Acolyte Dellis in Stormwind.",
    ["D"] = "As caretaker of the works here in the library of the Academy of Arcane Arts and Sciences, it is my responsibility to ensure all the magical tomes and scrolls are accounted for at all times. Who knows what folly would unfold if some of these enchanted texts were to fall into the wrong hands?$b$bWhich reminds me... my assistant, Dellis, made a horrible mistake last week. He\'s quite able in the library but incompetent when he leaves the gates of Stormwind. Be a hero, and help old Dellis out, would you?",
  },
  [1364] = {
    ["T"] = "Mazen\'s Behest",
    ["O"] = "Retrieve Khadgar\'s Essays on Dimensional Convergence and deliver them to Watcher Mahar Ba in Nethergarde Keep.",
    ["D"] = "Master Mazen\'s directions were quite clear: Deliver a very important essay to Watcher Mahar Ba in Nethergarde Keep.$b$bBut I had never been to the Blasted Lands before! The journey was frightening!$b$bWhen I got to the Swamp of Sorrows, I made a wrong turn and ended up surrounded by horrible plant-like swamp creatures. They lurched as if to attack me so I ran. But in my haste I dropped the essay. One of the beasts took it I am sure.$b$bCould you please try to retrieve it and deliver it to Mahar Ba?",
  },
  [1365] = {
    ["T"] = "Khan Dez\'hepah",
    ["O"] = "Bring Khan Dez\'hepah\'s Head to Felgur Twocuts in Desolace.",
    ["D"] = "The Kolkar are not the only centaur of Desolace.  Many clans wander these dry plains, fighting and dying against each other.  And it is good that they fight among themselves, for they are strong and brutal.  If the centaur united against us, they would be a fierce foe.$B$BWe must deal with them before they unite.$B$BYour first task is to disrupt the Kolkar clan.  Do this by killing its leader, Khan Dez\'hepah.$B$BThe Kolkar Village is northeast of here.  Go, and may fortune go with you.",
  },
  [1366] = {
    ["T"] = "Centaur Bounty",
    ["O"] = "Bring 15 Centaur Ears to Felgur Twocuts in Desolace.",
    ["D"] = "Now that you scared the centaur clans, it\'s time to bleed them.  When traveling Desolace, hunt centaur.  I don\'t care which clan you hunt.  Just gather ears.  A hoard of ears.$B$BBy weakening their numbers you will weaken their resolve, and stamp out any thoughts of expansion.$B$BGood hunting, $N.",
  },
  [1367] = {
    ["T"] = "Magram Alliance",
    ["O"] = "Gain a Friendly reputation with the Magram, then speak with Warug.",
    ["D"] = "To beat the centaur, we must use their hatred against them.  We will ally with one of the clans and through them, we will learn how to defeat all the clans.$B$BIf you choose to ally with the Magram then slay their foes, the Gelkis.  This will earn you respect among the Magram.  When you earn enough, then meet with the Magram spokesman Warug, on the outskirts of the Magram Village, in southeast Desolace.",
  },
  [1368] = {
    ["T"] = "Gelkis Alliance",
    ["O"] = "Gain a Friendly reputation with the Gelkis, then speak with Uthek the Wise.",
    ["D"] = "To beat the centaur, we must use their hatred against them.  We will ally with one of the clans and through them, we will learn how to defeat all the clans.$B$BIf you choose to ally with the Gelkis then slay their most hated rival, the Magram.  After you earn the respect of the Gelkis, meet with the Gelkis shaman Uthek the Wise, on the outskirts of the Gelkis Village, in southwest Desolace.",
  },
  [1369] = {
    ["T"] = "Broken Tears",
    ["O"] = "Maintain your reputation with the Magram, and bring 3 Broken Tears to Warug in the Magram Village.",
    ["D"] = "The Gelkis think our centaur mother, Theradras, protects us.  They are stupid.  No one protects the centaur.  The Magram do not need protection!  We are the strongest!$B$BShow the Gelkis they are stupid.  Go to the cave in their village.  They hoard crystals in that cave, the tears of Theradras.  Destroy the tears and bring me the broken pieces!",
  },
  [1370] = {
    ["T"] = "Stealing Supplies",
    ["O"] = "Bring 6 bags of Crudely Dried Meat to Uthek the Wise in the Gelkis Village.",
    ["D"] = "When we are not at war, we hunt for meat.  Meat gives us strength, and full bellies fight harder than empty ones.$B$BIt is the same with the Magram.  They store extra meat so they may fight longer between hunts.  If you raid their village to the east and take their stores of meat, then their strength will fail.",
  },
  [1371] = {
    ["T"] = "Gizmo for Warug",
    ["O"] = "Maintain your reputation with the Magram, and bring an Advanced Target Dummy to Warug in the Magram Village in Desolace.",
    ["D"] = "Long ago, a little gnome came to Desolace.  He had horses and a cart, and the cart held strange things.  Funny things.  Things that burned different colors.  Things that moved and weren\'t alive.  I killed the little gnome, but before that... he made me laugh.$B$BI kept my favorite thing from his cart.  It was a human made of wood and it waved at me until I hit it and broke it.$B$BI want a new one.  Get me a new one!",
  },
  [1372] = {
    ["T"] = "Nothing But The Truth",
    ["O"] = "Speak with Apothecary Faustin at Beggar\'s Haunt.",
    ["D"] = "Our expeditionary force was sent at Varimathras\'s behest to survey lower Azeroth.$b$bWhile out scouting I noticed a human Infiltrator spying on Beggar\'s Haunt. Before I could apprehend him, some of our orcish... brethren... captured him and took him to Stonard, their stronghold in Swamp of Sorrows.$b$bI want to... help... our orcish allies. They did not see our camp here but nonetheless I want to lend a hand.$b$bWork with Faustin here to develop a... truth serum for our friend in Stonard.",
  },
  [1373] = {
    ["T"] = "Ongeku",
    ["O"] = "Maintain your reputation with the Gelkis, and bring a Draenethyst Shard to Uthek the Wise in the Gelkis Village in Desolace.",
    ["D"] = "You have more to prove, and your next task will take you far, so for now the Gelkis will fight the Magram without you.$B$BThe earth whispers many secrets to those who listen.  One secret I hear is of a red crystal.  It is called draenethyst, and it is new to our earth.  It comes from a place where mountains fly, and where demons rule.$B$BBring me a shard of draenethyst, and it will make my magic strong!  The shard is in a place called the Swamp of Sorrows, held by the lost one Ongeku.",
  },
  [1374] = {
    ["T"] = "Khan Jehn",
    ["O"] = "Bring the Khan Jehn\'s Head to Uthek the Wise in the Gelkis Village in Desolace.",
    ["D"] = "The earth speaks to me, and it says that the Magram grow weak.  Now it is time to kill their Khan!$B$BBring me the head of the Magram Khan, Jehn!",
  },
  [1375] = {
    ["T"] = "Khan Shaka",
    ["O"] = "Maintain your reputation with the Magram, and bring the Head of Khan Shaka to Warug in the Magram Village in Desolace.",
    ["D"] = "You help the Magram, and I like you.  Now, kill the Khan of the Gelkis!  He is Khan Shaka, and I want his head!  I want to stare at his head and laugh at him!",
  },
  [1380] = {
    ["T"] = "Khan Hratha",
    ["O"] = "Maintain your reputation with the Gelkis, and bring the Maraudine Key Fragment to Uthek the Wise in the Gelkis Village.",
    ["D"] = "$N, you helped us against the Magram.  Now, help us beat the Maraudine!  Kill the head of their clan, Khan Hratha!$B$BTo face him, blow the Maraudine War Horn.  It is atop a high bluff in the Valley of Spears.$B$BBut to blow the horn, you must gain a warhorn mouthpiece.  These are rare, carried only by trusted Maraudine centaur.$B$BAfter the horn is blown, centaur will come.  Kill them until Hratha appears.$B$BKhan Hratha holds the piece of... a key.  Bring it to me...",
  },
  [1381] = {
    ["T"] = "Khan Hratha",
    ["O"] = "Maintain your Magram reputation, and bring the Maraudine Key Fragment to Warug in the Magram Village in Desolace.",
    ["D"] = "The Gelkis Khan is dead, and now the Maraudine will fall!  Kill the Maraudine Khan, Hratha!$B$BTo face him, blow the horn on the high bluff in the Valley of Spears.  But to blow the horn, you need a mouthpiece.  To get a mouthpiece, kill Maraudine centaur.  Keep killing until you find a mouthpiece.  Then blow the horn.$B$BDo this and Maraudine centaur will come to the tower.  Kill them until Khan Hratha appears.  Hratha holds the piece of a key.  Give this piece to me...",
  },
  [1382] = {
    ["T"] = "Strange Alliance",
    ["O"] = "Gain a Friendly reputation with the Gelkis, then speak with Uthek the Wise.",
    ["D"] = "We are fortunate the centaur clans fight among themselves, for if they joined into a single force then they would be unstoppable.  Yes, we are fortunate, and we should press our advantage.$B$BOne of the centaur clans, the Gelkis clan, is in brutal war with the Magram clan.  If we attack the Magram, then perhaps an alliance may be struck with the Gelkis.$B$BSpeak with Uthek the Wise, in the outskirts of the Gelkis camps in southwestern Desolace.  Through her we might strike an alliance with the Gelkis.",
  },
  [1383] = {
    ["T"] = "Nothing But The Truth",
    ["O"] = "Apothecary Faustin at Beggar\'s Haunt needs 5 Shadow Panther Hearts, Mire Lord Fungus and some Green Whelp Blood.",
    ["D"] = "I have just the right serum in mind. It will deal with the truth in precisely the way the truth should be dealt with.  For this concoction I will need several Shadow Panther hearts from the Swamp. I also require the enchanted fungus off of the Mire Lord who resides there. I am sure one as able as you, $N, can persuade him to part with some.  Now the hard part will be locating a Deepstrider tumor from far-off Desolace. Very rarely the giants there become ill and a tumor forms.  Now, off you go!",
  },
  [1384] = {
    ["T"] = "Raid on the Kolkar",
    ["O"] = "Maintain your reputation with the Gelkis, and bring 10 Crude Charms to Uthek the Wise in the Gelkis Village in Desolace.",
    ["D"] = "The Kolkar centaurs think their magic is strong.  They do not know magic!  They would not know magic if it bit them!$B$BYou will show them their magic is weak.  Go to the Kolkar  to the northeast and kill them.  Take their charms.  Bring me the charms and I will throw them in the fire and laugh!",
  },
  [1385] = {
    ["T"] = "Brutal Politics",
    ["O"] = "Gain a Friendly reputation with the Magram, then speak with Warug.",
    ["D"] = "Desolace is a land at war.  The centaurs wage war with each other, and we believe the victor among them will spill out and threaten our strongholds.  That, we cannot allow$B$BWe must learn how to beat the centaurs, and to do that... we must ally with one of the clans.  The Magram centaurs war with the Gelkis.  If you fight the Gelkis then the Magram might consider an alliance.$B$BEarn the favor of the Magrams then speak with Warug, in the Magram village in southeastern Desolace.",
  },
  [1386] = {
    ["T"] = "Assault on the Kolkar",
    ["O"] = "Maintain your reputation with the Magram. Kill 12 Kolkar Centaurs, 12 Kolkar Scouts and 6 Kolkar Maulers, then return to Warug in the Magram Village in Desolace.",
    ["D"] = "The Kolkar think they are strong, but we laugh at them!  We laugh and we kill them!  You kill them too.  Show me you can kill Kolkar like we do. Head north and slay them all!",
  },
  [1387] = {
    ["T"] = "Centaur Bounty",
    ["O"] = "Bring 15 Centaur Ears to Corporal Melkins at Nijel\'s Point in Desolace.",
    ["D"] = "Many clans make up the centaur race.  Each has its own leader and agenda, but all the clans have one thing in common.  Cruelty.$B$BIf we are to ever find peace, then the centaurs must be removed.  And so I put you on the task to do just that.$B$BWhile wandering Desolace, hunt centaur and take their ears.  Bring me many, many ears.$B$BDo this in service of the Alliance, $N, and we will be one step closer to war\'s end.",
  },
  [1388] = {
    ["T"] = "Nothing But The Truth",
    ["O"] = "Give Faustin\'s Truth Serum to Deathstalker Zraedus at Beggar\'s Haunt.",
    ["D"] = "Everything is proceeding according to plan, $n. I want you to give this truth serum to Deathstalker Zraedus and he\'ll show you how to administer it.... properly.$b$bHaha!",
  },
  [1389] = {
    ["T"] = "Draenethyst Crystals",
    ["O"] = "Bring 6 Draenethyst crystals to Magtoor at The Harborage in the Swamp of Sorrows.",
    ["D"] = "The lost ones who live in the Fallow Sanctuary look as we look, but they are monsters... driven mad by the rigors of our journey to Azeroth.  Once our brethren, they now live in the Fallow Sanctuary to the east, catching or killing any who wander near.$B$BAnd they have stolen something sacred to us--draenethyst crystals.$B$BWe lack the force to enter the Fallow Sanctuary and reclaim our stolen draenethyst from among their huts and campfires, but without those crystals... we too may lose our way.",
  },
  [1391] = {
    ["T"] = "Nothing But The Truth",
    ["O"] = "Take Zraedus\'s Brew to Infiltrator Marksen in Stonard.",
    ["D"] = "We need to operate with a certain amount of discretion. After all, it\'s not like you\'re going to waltz in there and convince the fool to drink Faustin\'s concoction here, right?$b$bBut if there\'s one thing those humans cannot deny it\'s the booze. I\'ll take Faustin\'s serum and mix it into this ale. I guarantee the poor sap will show no reluctance when it comes to chug-a-lugging a brew.$b$bMake haste to Stonard in the Swamp of Sorrows, $n. And keep our little mission nice and quiet...",
  },
  [1392] = {
    ["T"] = "Noboru the Cudgel",
    ["O"] = "Bring Noboru\'s Cudgel to the leader of The Harborage in the Swamp of Sorrows.",
    ["D"] = "Noboru has plagued the Draenei of the Harborage for months, and his cudgel is a symbol of the brutality they have suffered at the hands of the lost ones.",
  },
  [1393] = {
    ["T"] = "Galen\'s Escape",
    ["O"] = "Escort Galen out of danger, then find his camp in the Swamp of Sorrows and open his strongbox.",
    ["D"] = "Please help!  These creatures are going to eat me!$B$BHelp me out of here, and I\'ll give you a great treasure!  It\'s in my camp in the swamp north of Stonard, but it\'s locked in a strongbox that can be opened through a trick that only I know.$B$BIf you help me escape, then I\'ll teach it to you.",
  },
  [1394] = {
    ["T"] = "Final Passage",
    ["O"] = "Speak to Dorn Plainstalker in Thousand Needles.",
    ["D"] = "You have done well, $N. You have passed my test, and the tests before mine. Return to Dorn in Thousand Needles. He will sense the change within you, and see that your mind, body and spirit are strong enough that you should be rewarded for your efforts.$B$BStay true to the balance you\'ve shown throughout these trials, $N. Dorn will test you again in the future if you do.",
  },
  [1395] = {
    ["T"] = "Supplies for Nethergarde",
    ["O"] = "Bring the Crate of Powerstones to Quartermaster Lungertz, before the powerstones expire.",
    ["D"] = "This crate of powerstones needs to go to Nethergarde Keep, in the Blasted Lands.  They won\'t hold their power for long, so we can\'t ship them with the next caravan.$B$BYou must take them, and you must take them quickly.$B$BBring them to Quartermaster Lungertz in Nethergarde Keep.  To reach the keep, you must travel east through Deadwind Pass, then east through the Swamp of Sorrows, then south through the Blasted Lands.$B$BUse caution, $N.  You will travel through dangerous lands, yet you must not fail.",
  },
  [1396] = {
    ["T"] = "Encroaching Wildlife",
    ["O"] = "Kill 8 Young Sawtooth Crocolisks, 10 Sorrow Spinners and 10 Swamp Jaguars then return to Watcher Biggs in the Swamp of Sorrows.",
    ["D"] = "My caravan was destined for the eastern coast, but we did not reach our goal.  Lost Ones attacked us, killing guards and dashing supplies.$B$BI alone survived their assault.  I have sworn to protect what supplies remain, but wild creatures skulk near.  Please, help me defend my camp from the beasts!",
  },
  [1398] = {
    ["T"] = "Driftwood",
    ["O"] = "Bring 8 pieces of Sundried Driftwood to Watcher Biggs in the Swamp of Sorrows.",
    ["D"] = "I owe you greatly, $N.  An honorable heart beats within you.  And now I have another task for which I humbly ask your aid.$B$BIf I\'m ever to reach Nethergarde, then I must fix this wagon.  And for that... I will need wood.  Strong, dry wood--not the spongy, tangled wood of the swamp\'s trees.$B$B$N, go east to the coast and search for driftwood that has been dried by the sun.  Bring it to me.",
  },
  [1418] = {
    ["T"] = "Neeka Bloodscar",
    ["O"] = "Speak with Neeka Bloodscar.",
    ["D"] = "The Badlands is harsh and lies close to the Alliance-held land of Loch Modan, so communication between Stonard and the Badlands outpost of Kargath is often cut.$B$BProve yourself, $N.  Travel to the Badlands and speak with Neeka Bloodscar, head scout of the Kargath outpost.  Speak with her, and then send her report back to me.",
  },
  [1419] = {
    ["T"] = "Coyote Thieves",
    ["O"] = "Bring 30 Coyote Jawbones to Neeka Bloodscar in Kargath.",
    ["D"] = "Have you spent many days in the Badlands, $c?  Have you felt its heat? Have you faced the beasts that wander its dusty plains?  It\'s time you did.$B$BCrag coyotes are a tough breed.  Tough, and bold enough to creep into Kargath and steal from our food supplies.  Help rid the outpost of these thieves!$B$BHunt crag coyotes, $N.  Hunt them and bring me their jawbones.  Bring me enough, and maybe I\'ll believe it when you say you know the Badlands.",
  },
  [1420] = {
    ["T"] = "Report to Helgrum",
    ["O"] = "Bring Neeka\'s Report to Helgrum the Swift in Stonard.",
    ["D"] = "I wrote a fresh report for Helgrum in Stonard.  Bring it to him.$B$BAnd if you consider yourself an outrunner, then don\'t lose your way.  Or fall to an enemy.",
  },
  [1421] = {
    ["T"] = "The Lost Caravan",
    ["O"] = "Find the Caravan Chest in the Fallow Sanctuary, obtain the Wizards\' Reagents and bring to Watcher Biggs.",
    ["D"] = "I was stunned during the attack by the Lost Ones, but before my sight went black I spied one of our wagons being dragged to the Fallow Sanctuary, the lost one village to the north.$B$BA caravan chest with valuable reagents was in that wagon, reagents vital to the studies of the wizards of Nethergarde Keep.  Find the chest and get those reagents, $N.  Get them and bring them to me.",
  },
  [1422] = {
    ["T"] = "Threat From the Sea",
    ["O"] = "Speak to Katar in the Swamp of Sorrows.",
    ["D"] = "While I begin work on your weapon, why don\'t you talk to Katar? He mentioned needing help with patrols along the beach, and I\'d rather see you keep your wits and weapons prepared instead of getting drunk with some of the other louses I share this camp with.",
  },
  [1423] = {
    ["T"] = "The Lost Supplies",
    ["O"] = "Bring the Lost Supplies to Quartermaster Lungertz.",
    ["D"] = "This barrel is small yet sturdy.  Its seams are sealed with a yellow, pungent wax.  Stamped on the bottom of the barrel are the words:$B$BQUARTERMASTER LUNGERTZ$B$BNETHERGARDE KEEP",
  },
  [1424] = {
    ["T"] = "Pool of Tears",
    ["O"] = "Fel\'zerul in Stonard wants you to gather 10 Atal\'ai Artifacts.",
    ["D"] = "The Warchief sent us to investigate strange happenings at The Temple of Atal\'Hakkar.$b$bThe Atal\'ai trolls are a tribe of dark casters, known for practicing forbidden magic. You can imagine how astonished we were when we arrived here to discover the temple had been sunk.$b$bThe green dragons now guard the site, claiming to be protectors of Azeroth.$b$bWe need to discover what happened. Explore around the Pool of Tears and gather Atal\'ai artifacts. From those I can begin to piece together this puzzle.",
  },
  [1425] = {
    ["T"] = "Deliver the Shipment",
    ["O"] = "Bring the Shipment to Nethergarde to Quartermaster Lungertz in Nethergarde Keep.",
    ["D"] = "While I fix this wagon, $N, I have one last task for you.$B$BI have packed the most vital supplies saved from the ogre attacks.  Take them to Nethergarde Keep.  The keep is nestled in the northern hills of the Blasted Lands, keeping watch over the Dark Portal.$B$BTo reach Nethergard, follow the road east.  When it branches head south into the Blasted Lands, then turn left at the next fork.  Follow the road, $N, and be wary.",
  },
  [1426] = {
    ["T"] = "Threat From the Sea",
    ["O"] = "Kill 10 Marsh Murlocs, 10 Marsh Inkspewer, and 10 Marsh Flesheater, then return to Katar in the Swamp of Sorrows.",
    ["D"] = "I have no love for the sea, nor any creature I\'ve seen come from its depths. It is a powerful but blind mistress; callous and brutal. It would bring me great pleasure to slay the fish men by the hundreds if it were within my power. But I garner some pleasure from watching their deaths at the hands of any who do the Horde\'s will.$B$BWhich brings us to you, $c. How many can you slay? Would you test yourself against their most powerful kind?",
  },
  [1427] = {
    ["T"] = "Threat From the Sea",
    ["O"] = "Speak with Tok\'Kar in the Swamp of Sorrows.",
    ["D"] = "Before you take rest, speak to Tok\'Kar again. While you were gone I believe he finished a few weapons. For your help here, he would offer one of them to you. You would do well to test its strength against the fish men when you are ready... but only to please me further.",
  },
  [1428] = {
    ["T"] = "Continued Threat",
    ["O"] = "Kill 10 Marsh Inkspewers, 10 Marsh Flesheaters, and 10 Marsh Oracles, then return to Katar in the Swamp of Sorrows.",
    ["D"] = "It pleases me to see your hatred for the fish men grows with mine.$B$BFind more of their kind along the beach, and when you\'ve had your fill of combat, return to me. We shall speak then of a reward for your diligence.",
  },
  [1429] = {
    ["T"] = "The Atal\'ai Exile",
    ["O"] = "Bring the Bundle of Atal\'ai Artifacts to the Atal\'ai Exile in the Hinterlands.",
    ["D"] = "While you were braving the dangers of the Pool of Tears, one of my scouts returned with a scroll written in blood on dried flesh. It seems to be a judicial sentence against a priest of the Atal\'ai tribe.$b$bThis priest was sentenced to a fate worse than death amongst the Atal\'ai.$b$bHe was exiled to live far north in the Hinterlands, with the Witherbark trolls.$b$bI will bundle these artifacts. You are to seek out the exile and discover their true use. Return to me once your journey is complete.",
  },
  [1430] = {
    ["T"] = "Fresh Meat",
    ["O"] = "Bring 10 Monstrous Crawler Legs to Dar in the Swamp of Sorrows.",
    ["D"] = "There\'s a high demand for the succulent taste of crawler meat among my customers; especially the meat from the monstrous crawlers.$B$BIf you plan on heading to the coast, then I could easily make it worth your while to bring me back some of their legs to have transported back to Orgrimmar.$B$BThe coast is to the east from here, and you should have no problem finding the creatures scurrying along the beaches and under the sea.",
  },
  [1431] = {
    ["T"] = "Alliance Relations",
    ["O"] = "Speak to Keldran in Orgrimmar.",
    ["D"] = "Be still, $c, and allow me to share with you something important to our people, but far from obvious.$B$BWe are a proud race, and the war with the humans tried even Thrall\'s great patience. You see, our Warchief is wise and prudent, and he continues to put his people first. Ultimately, he is a diplomat, and in his time with the humans he learned much of their ways.$B$BIf the tale I\'ve started piques your interest, then find Keldran near the western exit of the city. His hovel is there.",
  },
  [1432] = {
    ["T"] = "Alliance Relations",
    ["O"] = "Speak to Takata Steelblade in Desolace.",
    ["D"] = "As the Alliance progresses deeper and deeper into Kalimdor, incorrect conclusions may be drawn about the Horde making pacts with the Burning Legion. The Warchief cannot allow that to happen. And with his blessing, I am taking control of overseeing them removed.$B$BI have agents in the area of Desolace at the Ghost Walker Post north of the great Kodo Graveyard. Head south of the Charred Vale in the Stonetalon Mountains and you shall come across it. When you arrive, seek out Takata Steelblade.",
  },
  [1433] = {
    ["T"] = "Alliance Relations",
    ["O"] = "Speak to Maurin Bonesplitter in Desolace.",
    ["D"] = "The Burning Blade have many agendas, but know that they are evil to their core, their spirits corrupted, and their destruction is the only way to thwart their objectives.$B$BKeldran sent Maurin with me because of his knowledge of the Blade and their warlock ways.$B$BHe has control of our plots against the Blade while I deal with the other demonic threats here in Desolace.$B$BSpeak to him, and he will guide you in his designs.",
  },
  [1434] = {
    ["T"] = "Befouled by Satyr",
    ["O"] = "Slay 7 Hatefury Rogues, 7 Hatefury Felsworn, 7 Hatefury Betrayers, and 7 Hatefury Hellcallers, and return to Takata Steelblade in Desolace.",
    ["D"] = "A demon infestation does not occur without other influences--something else allows evil to gain a foothold.$B$BIt may start with a weakened spirit, a needless desire for power; perhaps it\'s a thirst for revenge, or the appeal for final justice. When we are beset by pain, evil takes advantage of us--evil such as the satyr.$B$BTies have been made between them and the Burning Blade, and Thrall would have them slain. Seek them out to the northeast of here, and return to me when you\'ve driven them back.",
  },
  [1435] = {
    ["T"] = "The Burning of Spirits",
    ["O"] = "Return the Burning Gem and 15 Infused Burning Gems to Maurin Bonesplitter in Desolace.",
    ["D"] = "Death is most desirable for those despicable creatures that turn their back on their liege for such selfish reasons. But death is not enough when dealing with those that have given themselves over to the darkness.$B$BTake this gem. When one of the Burning Blade\'s near death, activate its magic. The spell shall pull the corrupted spirit out of its victim, leaving an infused gem behind.$B$BBring me these infused gems so I may dispose of them properly.$B$BGo now, and may you elude death.",
  },
  [1436] = {
    ["T"] = "Alliance Relations",
    ["O"] = "Speak to Keldran in Orgrimmar.",
    ["D"] = "The fight goes well, $N. Perhaps the Warchief will allow us to return to our homes before the seasons change if we continue to make such great strides in our battle against the demons and their underlings.$B$BReturn to Keldran and let him know what we have done. I am sure he will reward you for your efforts here. His cause is important to him, and he will take pride in knowing that the Warchief respects his concerns.",
  },
  [1437] = {
    ["T"] = "Vahlarriel\'s Search",
    ["O"] = "Find and search Tyranis and Dalinda Malem\'s wagon in Desolace.",
    ["D"] = "I am Vahlarriel, $N. I\'ve come here to discover the whereabouts of two missing nobles of a Darnassian merchant family--House Malem. The father of the house fears the worst, and I am to do everything I can to find his missing children. I have yet to start my search for Tyranis or Dalinda, but I am hopeful I shall not be here long.$B$BLast word tells of them passing through the Stonetalon Mountains a few weeks ago.$B$BIf you find their wagon, please, come find me immediately.",
  },
  [1438] = {
    ["T"] = "Vahlarriel\'s Search",
    ["O"] = "Find Dalinda Malem in Desolace.",
    ["D"] = "Find both Dalinda and Tyranis if you can and come back to me with your findings. Start your search at this fortress. I will contact my superiors in Darnassus immediately and let them know the situation.$B$B$N, there is something else I wish to impress upon you. My superiors have shown some concern over Tyranis\' and his motives for coming here. It is more important to them that Dalinda is found and brought back safely. Tyranis is to be... gauged before he is taken home.",
  },
  [1439] = {
    ["T"] = "Search for Tyranis",
    ["O"] = "Find Tyranis Malem in Desolace.",
    ["D"] = "We were brought here after the orcs slaughtered all our guards and servants. Oh, the terrible things they did to them! I thought surely they would kill us right there and then, but it became obvious they had other plans.$B$BTyranis attempted to speak to them. I think he wanted to impress them with his knowledge of the arcane... I\'m not sure why.$B$BPlease, I can\'t leave here until I know what\'s happened to my brother. Find him, then come back for me when his fate is known.",
  },
  [1440] = {
    ["T"] = "Return to Vahlarriel",
    ["O"] = "Escort Dalinda Malem from Thunder Axe Fortress and then return to Vahlarriel Demonslayer in Desolace.",
    ["D"] = "So, the time is now then? I appreciate your efforts, and if the situation does not lend itself, then allow me to thank you now for everything you\'ve done for me and my family. If you\'re ready, then there is nothing else for me here--we can leave when you feel the time is right.",
  },
  [1442] = {
    ["T"] = "Seeking the Kor Gem",
    ["O"] = "Bring a Kor Gem to Thundris Windweaver in Darkshore.",
    ["D"] = "Greetings, $N. What can I do for one who follows a holy path?$B$BAh, yes, Stilwell, the blacksmith... from long ago. I remember him well, though, I have not heard his name in some time.$B$BHe never struck me as an impudent man, so I can only assume his desire for a Kor gem is noble.$B$BIf you truly wish for one of the gems, then you must head to the northwest of Ashenvale. You will find Blackfathom Deeps there within some ancient ruins. The naga who still use the gems are down below. Do not go alone, $c.",
  },
  [1444] = {
    ["T"] = "Return to Fel\'Zerul",
    ["O"] = "Return to Fel\'Zerul in Stonard.",
    ["D"] = "Jammal\'an the Prophet sentenced me to exile from The Temple of Atal\'Hakkar. I once felt the high priest was a worthy leader.$b$bHis unyielding faith in his prophecy will bring about the destruction of the Atal\'ai. If it hasn\'t already.$b$bWhen I expressed my concerns, I was labeled an infidel and ousted from the temple.$b$bThose artifacts you brought to me are harmless. But if Jammal\'an has found the proper fetish enchantment for the summoning ritual, the world lies in great peril....",
  },
  [1445] = {
    ["T"] = "The Temple of Atal\'Hakkar",
    ["O"] = "Collect 20 Fetishes of Hakkar and bring them to Fel\'Zerul in Stonard.",
    ["D"] = "So he spoke of Jammal\'an\'s prophecy?$b$bThe Atal\'ai spiritual leader believes once Hakkar returns to Azeroth from the Nether, the god will grant the Atal\'ai tribe immortality.$b$bFoolish trickery to bring about a premature doomsday if you ask me.$b$bBut you say the exile spoke of enchanted fetishes? This concerns me greatly. If these are in fact the key to the Atal\'ai ritual which caused this mess, we must understand their intrinsic powers.$b$bVenture into the Temple, and seize the fetishes!",
  },
  [1446] = {
    ["T"] = "Jammal\'an the Prophet",
    ["O"] = "The Atal\'ai Exile in The Hinterlands wants the Head of Jammal\'an.",
    ["D"] = "My Witherbark companions don\'t take kindly to strangers around their home.$b$bMe? I don\'t have a home anymore. I was exiled from my people, the great Atal\'ai tribe of the Swamp of Sorrows.$b$bOur spiritual leader, Jammal\'an, had what he called a prophecy. He believes the summoning of the god, Hakkar will bring the Atal\'ai immortality.$b$bBut I urged caution. What if the prophecy was nothing more than manipulation?$b$bI want revenge for my exile. Bring me Jammal\'an\'s head. Maybe then my people will be free.",
  },
  [1447] = {
    ["T"] = "The Missing Diplomat",
    ["O"] = "Defeat Dashel Stonefist in Stormwind.",
    ["D"] = " Yeah, you\'re obviously not the smartest $c to come into Stormwind, but you\'re about to be one of the dumbest to never leave.$B$BHave you met me friends?",
  },
  [1448] = {
    ["T"] = "In Search of The Temple",
    ["O"] = "Brohann Caskbelly in Stormwind wants you to search for the Temple of Atal\'Hakkar.",
    ["D"] = "There was a time when I was young and full of vigor like you, $c.$b$bBut now I\'m old and unable to explore the world like I once did. Ah, the things I saw!$b$bBut there was one mystery that eluded me. For my final quest, the Explorers\' League sent me in search of The Temple of Atal\'Hakkar. It was rumored to be located in the Swamp of Sorrows.$b$bBecause of my frail state, I traveled the skies by gryphon in search of it but never found it.$b$bHelp an old dwarf out? Perhaps you will have better luck on foot.",
  },
  [1449] = {
    ["T"] = "To The Hinterlands",
    ["O"] = "Speak with High Thane Falstad Wildhammer at Aerie Peak in the Hinterlands.",
    ["D"] = "As I mentioned earlier, I attempted to scout the temple on my own from the air. Well, I wasn\'t exactly alone.$b$bI traveled to the renowned gryphon aviary at Aerie Peak in The Hinterlands. There I enlisted the aid of one of their finest pilots. The pilot fell off his mount, down into the swamp...$b$bIn my old age the poor fellow\'s name eludes me. The master of the aviary would remember, no doubt.$b$bJourney there and seek out Falstad Wildhammer.$b$bWe need to find out what happened to that pilot.",
  },
  [1450] = {
    ["T"] = "Gryphon Master Talonaxe",
    ["O"] = "Speak with Gryphon Master Talonaxe inside the Aerie Peak Aviary.",
    ["D"] = "So you\'re a glorified errand $g boy:girl; for some old Explorers\' League geezer, eh?$b$bWell you listen to me, $c. I don\'t know who your old buddy was flying around with down in Azeroth. Do I look like Gryphon Master Talonaxe to you? Do I?$b$bIf you\'re so interested in the flight assignments why don\'t you ask the gryphon master? If I catch you idling around here for much longer, I\'m going to put you to some real work.$b$bNow get a move on.",
  },
  [1451] = {
    ["T"] = "Rhapsody Shindigger",
    ["O"] = "Seek out Rhapsody Shindigger in The Hinterlands.",
    ["D"] = "Rhapsody took quite a liking to the booze. Now don\'t get me wrong -- all of us dwarves up here at the aviary love a good ale when the time is right.$b$bBut ol\' Shindigger took to the liquor like a gryphon takes to the skies. And a damned shame it was, too. No one could fly like Rhapsody.$b$bFirst sign of trouble was when he came stumbling back from that mission with your buddy, Brohann. All went down hill quickly after that. Had to let him go.$b$bLast I heard, he\'s set up a brew camp to the east of here...",
  },
  [1452] = {
    ["T"] = "Rhapsody\'s Kalimdor Kocktail",
    ["O"] = "Rhapsody Shindigger in The Hinterlands wants you to bring him 3 Roc Gizzards, 3 Groddoc Livers and 3 Ironfur Livers.",
    ["D"] = "Hiccup!$b$bSo that crazy fool Brohann sent ya? My we had quite an adventure...Hiccup!$b$bIf ya want me to tell ya what happened down in the swamp, I\'m gonna need me something stiff to drink. It wasn\'t a pretty scene, ya see. Hiccup!$b$bTell ya what... bring me what I need to whip up a Kalimdor Kocktail and I\'ll tell ya all about it.$b$bGonna need some Roc gizzards from Tanaris... and let\'s see... some Groddoc and Ironfur livers from Feralas...$b$bA nice stiff Kalimdor Kocktail... that\'ll get me talking!",
  },
  [1453] = {
    ["T"] = "Reclaimers\' Business in Desolace",
    ["O"] = "Speak to Kreldig Ungor in Desolace.",
    ["D"] = "There\'s work to be done in Desolace, $N. It\'s an atrocious place, befouled by centaur and other nasties. But if you\'re looking for a good week\'s honest work, then head there, meet with Kreldig Ungor, and tell him I sentcha.$B$BYou can find him holed up at Nijel\'s Point--it\'s a little cozy camp hidden in the mountains of northern Desolace. Just head east if you enter from the Stonetalon Mountains. It\'s easy to miss, so be on the lookout.",
  },
  [1454] = {
    ["T"] = "The Karnitol Shipwreck",
    ["O"] = "Find Karnitol\'s Chest in Desolace.",
    ["D"] = "My first order of business is for the mage Karnitol--a noble of some sort in Stormwind. Seems his ship was attacked by sea giants while off the coast in the northwest of Desolace. He and his servants tried to bring one of his more valuable chests ashore, but when the sea giants attacked in droves, he chose to abandon it and teleport himself and his servants to safety.$B$BHe wants us to find the chest and its contents, and return it to him in Stormwind.",
  },
  [1455] = {
    ["T"] = "The Karnitol Shipwreck",
    ["O"] = "Return to Kreldig Ungor in Desolace.",
    ["D"] = "You take a few moments to figure out what pried the chest open, and you notice along the sides and top of the chest, long, deep scratches that reveal that something other than a man or giant took interest in it. The marks don\'t appear as if they were caused by dagger or sword.$B$BAfter going through the chest you find it to be completely empty.$B$BNot having another course of action, you close the chest and prepare to leave. Kreldig will be disappointed.",
  },
  [1456] = {
    ["T"] = "The Karnitol Shipwreck",
    ["O"] = "Find Karnitol\'s Satchel and return it to Kreldig Ungor in Desolace.",
    ["D"] = "Intriguing.$B$BWell, we might not be without hope quite yet.$B$BAlong that same coast, but to the north near Ranazjar Isle, there\'re night elf ruins at the bottom of the ocean. A tribe of naga, the Slitherblade, make that their home. I think that\'d be our best bet.$B$BIf a scouting party went that far south, then they must\'ve been pretty powerful. If you\'re gonna find Karnitol\'s belongings, I\'d start with the more powerful naga: the tidehunters or sea witches.",
  },
  [1457] = {
    ["T"] = "The Karnitol Shipwreck",
    ["O"] = "Bring Karnitol\'s Satchel to Roetten Stonehammer in Ironforge.",
    ["D"] = "Take Karnitol\'s things back to Roetten in Ironforge and he\'ll pay you for a job well done.$B$BHaha, if you\'re not careful, he\'s gonna be so pleased with ya that he\'ll ask you to join us full time.$B$BAgain, great job, $N.$B$BBe careful on your way back, and don\'t take too much time--Karnitol\'s not the most of patient of men, but he\'s very generous nonetheless.",
  },
  [1458] = {
    ["T"] = "Reagents for Reclaimers Inc.",
    ["O"] = "Bring 10 Hatefury Claws, and 10 Hatefury Horns to Kreldig Ungor in Desolace.",
    ["D"] = "One of my tasks out here involves picking up some reagents for Leftwitch, a gnome mage in Kharanos . Roetten seems to think it\'s easy coin in the bank since we\'re out here already. He doesn\'t really care that we\'re short-handed as is.$B$BThe first few items on his list should be easy enough to get, and it\'d be a good chunk of coin for anyone helping me out.$B$BI need some horns and claws from the Hatefury satyr to the east of here. Bring \'em back to me and we\'ll get started on the rest of the list.",
  },
  [1459] = {
    ["T"] = "Reagents for Reclaimers Inc.",
    ["O"] = "Bring 7 vials of Scorpashi Venom and 3 Aged Kodo Hides to Kreldig Ungor in Desolace.",
    ["D"] = "Next on Leftwitch\'s list is... let\'s see... hmm. Oh, here it is.$B$BSimple enough, but pretty explicit in his directions. He needs venom from the scorpashi, and hide from some kodo. The fine print says both are from specific creatures: the venom\'s got to come from any of the scorpashi, and the hide\'s got to come from an aged kodo, not the dying or ancient ones.$B$BMages, I tell ya, some of the stuff they want... it\'s just weird.",
  },
  [1462] = {
    ["T"] = "Earth Sapta",
    ["O"] = "",
    ["D"] = "",
  },
  [1463] = {
    ["T"] = "Earth Sapta",
    ["O"] = "",
    ["D"] = "",
  },
  [1464] = {
    ["T"] = "Fire Sapta",
    ["O"] = "Speak to Telf Joolam for another Fire Sapta.",
    ["D"] = "",
  },
  [1465] = {
    ["T"] = "Vahlarriel\'s Search",
    ["O"] = "Return the Malem Pendant to Vahlarriel Demonslayer in Desolace.",
    ["D"] = "As you continue your search of the wagon you find not only a pendant, but also footprints leading off towards the south. In the distance you can see smoke rising from a large fort. The smoke is from a kept fire, not the burning of buildings, and your instincts tell you that the Malems, if still alive, were taken that direction.$B$BYou think Vahlarriel at Nijel\'s Point should not only see the pendant you\'ve found, but also know about the signs of life to the south.",
  },
  [1466] = {
    ["T"] = "Reagents for Reclaimers Inc.",
    ["O"] = "Bring 10 Felhound Brains, 10 Nether Wings, and 10 vials of Doomwarder Blood to Kreldig Ungor in Desolace.",
    ["D"] = "This is the last of the items, and I won\'t lie, it ain\'t pretty. I\'m also expecting this to take you a little bit longer than the other items.$B$BSo, enough with the warning, let\'s get this started. Leftwitch says he needs: ten brains from either mana eaters, mage hunters, or ley hunters. Ten nether wings from a nether maiden, sister or sorceress. And... ten vials of doomwarder blood from a doomwarder captain, or lord.$B$BSimple enough, huh? Sheesh... if you wanna pass on this one, I\'d understand.",
  },
  [1467] = {
    ["T"] = "Reagents for Reclaimers Inc.",
    ["O"] = "Bring Leftwitch\'s Package back to Roetten Stonehammer in Ironforge.",
    ["D"] = "To get paid, you\'re gonna have to get back to Ironforge and speak to Roetten himself. He\'ll love getting Leftwitch\'s package out of the way anyway. Just give him this and it should be enough to get your booty.$B$BAnd don\'t worry, I\'m still going to put in a good word for you when I get back. I shouldn\'t be in Desolace much longer.",
  },
  [1468] = {
    ["T"] = "Children\'s Week",
    ["O"] = "Use the Human Orphan Whistle to talk with the child you will be looking after during Children\'s Week.",
    ["D"] = "You\'re willing to help us here at the orphanage?  Bless you, friend; your aid during Children\'s Week will be invaluable.$B$BTake this whistle.  You will only be able to use it for a short amount of time - typically not longer than Children\'s Week itself.  When you use it, you will be able to talk with the child you\'ve agreed to look after.  Using it again will dismiss the child.$B$BBy all means $N - use the whistle and meet your ward!  I\'m sure the child will be most eager to meet you.",
  },
  [1469] = {
    ["T"] = "Rhapsody\'s Tale",
    ["O"] = "Bring the Atal\'ai Tablet Fragment to Brohann Caskbelly in Stormwind.",
    ["D"] = "Before the flight, I drank me a whole keg of stout. Hiccup!$b$bAnd what a flight it was. Zipping around with Brohann following me. Didn\'t see no temple though. Hiccup!$b$bWell, didn\'t see no temple till I got a little too tipsy and fell off my mount. I plunged right into the water and was sucked down. Found myself surrounded by a bunch of creepy trolls. Sure swam out of there fast! Even made off with this troll souvenir. Here, take it to Brohann and let him know his temple is down there, indeed. Hiccup!",
  },
  [1470] = {
    ["T"] = "Piercing the Veil",
    ["O"] = "Bring Venya Marthand 3 Rattlecage Skulls.",
    ["D"] = "Greetings, child. I can see that you are still young in the ways of the warlock, power still eluding your grasp. I can sense your eagerness to learn.$b$bAnd you will learn.$b$bThere is no strength in ignorance. Knowledge is our greatest power. Through its application, we can control the chaotic magics and beings of the void, bending them to our will.$b$bI will show you how to bind a servant to your will, but first you must bring me three skulls of the rattlecage skeletons near the abandoned smithy.",
  },
  [1471] = {
    ["T"] = "The Binding",
    ["O"] = "Using the Runes of Summoning, summon and subdue a voidwalker, then return the Runes of Summoning to Carendin Halgar in the Magic Quarter of the Undercity.",
    ["D"] = "Surely you have seen the circle of summoning below us? Only there will you be able to draw forth an unbound voidwalker.$B$BUsing these runes, you will be able to draw it forth, and then you must dominate it through strength of arms and magic.$B$BWhen it is subdued, it will be indentured to you. Come to me then, and I will elucidate the spell of summoning, so that it may serve you to whatever end you wish.",
  },
  [1472] = {
    ["T"] = "Devourer of Souls",
    ["O"] = "Speak with Godrick Farsan in the Temple of the Damned.",
    ["D"] = "A succubus is a devourer of souls, destroyer of hearts, tempter of men. A creature of profound evil and of singular mind.$b$bIt cannot be brought into our world without a stimulus.$b$bThus we use its own strength against it. With the hearts of good men, pure men, loving men, the succubus will be unable to resist, and will cross into our world. Then you can dominate it, and make it your own.$b$bSpeak with my assistant, Godrick Farsan, within the Temple. He will help you find your bait.",
  },
  [1473] = {
    ["T"] = "Creature of the Void",
    ["O"] = "Recover Egalin\'s Grimoire and bring it to Carendin Halgar in the Temple of the Damned.",
    ["D"] = "It seems the consensus that you are ready to learn to summon a voidwalker.$b$bA being created of the chaos that lies beyond, if you summon one but cannot control it, you will be torn asunder.$b$bThe first voidwalker was summoned by Egalin, and in his grimoire is the process explained.$b$bUnfortunately, it was stolen from us by an agent of the Scarlet Crusade who has it secreted away in his base at a tower to the west. Bring it to me, and I will impart the knowledge to you.",
  },
  [1474] = {
    ["T"] = "The Binding",
    ["O"] = "Using the Pure Hearts, summon and subdue a succubus, then return the Pure Hearts to Carendin Halgar in the Magic Quarter of the Undercity.",
    ["D"] = "Using these hearts, cast a spell at the summoning circle to open a doorway for the succubus to come through. Your powers are greater now, and you will need all of them to defeat the succubus. $b$bBeware its charms, $n, many a strong mind has been bound to its will. When overcome, the succubus will be returned to the void, and I will teach you the spell to summon it forth at your command.",
  },
  [1475] = {
    ["T"] = "Into The Temple of Atal\'Hakkar",
    ["O"] = "Gather 10 Atal\'ai Tablets for Brohann Caskbelly in Stormwind.",
    ["D"] = "According to legend, The Temple of Atal\'Hakkar is a holy shrine dedicated to the ancient god, Hakkar the Soulflayer. Maintained by a vicious tribe of trolls named the Atal\'ai, the temple is rumored to be impenetrable.$b$bNow sunk beneath the Pool of Tears, retrieving artifacts from the temple is even more difficult. But that\'s what I need you to do, $n.$b$bAid me by gathering the intact Atal\'ai Tablets. Bring them to me and I will return to the Explorers\' Guild a hero. Together we will share the glory.",
  },
  [1476] = {
    ["T"] = "Hearts of the Pure",
    ["O"] = "Bring the hearts of Dalin Forgewright and Comar Villard to Carendin Halgar in the Temple of the Damned.",
    ["D"] = "Men of honor and goodness? Does such a man exist?$b$bIt took much research, but through bits of overheard stories, I managed to find two men that should do.$b$bThe first is Dalin Forgewright, who has dedicated himself to the care of the Lordaeron refugees, whom are refused by their kin at the Greymane Wall.$b$bThe other is Comar Villard, a man who remains faithful to his lost love, wandering the Wetlands for a sign of his wife.$b$bDeliver their hearts to Carendin. But first, let me show you your quarry.",
  },
  [1477] = {
    ["T"] = "Vital Supplies",
    ["O"] = "Speak with Watchmaster Sorigal.",
    ["D"] = "Our mages are conducting research at Nethergarde Keep in the Blasted Lands, research vital to our understanding of the Dark Portals. They require nearly constant supplies, but our supply line to Nethergarde is tenuous.$B$BYou must help ensure the supplies make it to Nethergard.  Report to Watchmaster Sorigal in Darkshire, and do as he asks.  This task will take you into hostile lands, $N.  Prepare yourself.",
  },
  [1478] = {
    ["T"] = "Halgar\'s Summons",
    ["O"] = "Speak with Carendin Halgar in the Undercity.",
    ["D"] = "Ah, $n! I just received word from Carendin Halgar that he would like to see you for further training.$b$bYou should be proud of yourself, $n. It\'s not every day that Master Halgar personally sees fit to instruct a young warlock.$b$bYou\'ll be able to find him in the Magic Quarter of the Undercity, near the Temple of the Damned.",
  },
  [1479] = {
    ["T"] = "The Bough of the Eternals",
    ["O"] = "Take the orphan to the bank of Darnassus.  The bank itself is hollowed out of a tree known as the Bough of the Eternals.",
    ["D"] = "I\'ve heard stories about how the night elves have their bank in a tree - one that looks like a bear!  That\'s so cool!  Night elves are neat how they like nature and stuff, but bears are just totally awesome!$B$BWould you take me to see the bear-tree in Darnassus, please?  There\'s a boat that will take us close to there, and it shouldn\'t take very long.  Please, please, please!",
  },
  [1480] = {
    ["T"] = "The Corrupter",
    ["O"] = "Bring the Flayed Demon Skin to Maurin Bonesplitter in Desolace.",
    ["D"] = "The torn skin smells rotten, and is remarkably pliable for something that appears to be dry and fragile.$B$BAs you unroll it, you can clearly see the red markings of some kind of language on one side. At the bottom of the skin is a very clear symbol that you guess is a signature of some kind.$B$BPerhaps it is something Maurin could tell you more about?",
  },
  [1481] = {
    ["T"] = "The Corrupter",
    ["O"] = "Bring a Shadowstalker Scalp to Maurin Bonesplitter in Desolace.",
    ["D"] = "I believe I can find this demon lord given the proper reagents.$B$BIf he is indeed as powerful as I suspect, I will need items gathered from deadly creatures. But you act in the Warchief\'s name, so I am sure you will accept my task.$B$BFirst, I will have to break any wards he has against scrying. For this, I will need the scalp from a satyr--but not any satyr. The scalp must come from a Hatefury shadowstalker.$B$BSearch for them among the ancient ruins in the northeast of Desolace.",
  },
  [1482] = {
    ["T"] = "The Corrupter",
    ["O"] = "Bring an Oracle Crystal to Maurin Bonesplitter in Desolace.",
    ["D"] = "An oracle crystal would do nicely. They are crafted by the naga for their oracles to use.$B$BNorthwest of here, along the coast, is a vast coral reef that the naga have taken as their home when this close to the shore. You can find their oracles there. The Slitherblade are deadly and will not react kindly to your intrusions, so be careful and make haste.$B$BWhen you return, we shall see about finding this demon lord Azrethoc.",
  },
  [1483] = {
    ["T"] = "Ziz Fizziks",
    ["O"] = "Speak with Ziz Fizziks in Windshear Crag.",
    ["D"] = "The Venture Company stole many of the best goblin minds, but from time to time, we get the opportunity to steal some back.$b$bA while ago, the Venture Company outfit in the Stonetalon Mountains hired Gerenzo Wrenchwhistle--a gnome! Their other engineer, Ziz Fizziks, was so taken aback by this, he pleaded with me to find him new employment with the Tinkers\' Union. I set him on a few tasks, but he might need help.$b$bMaybe you could look in on him for me? He\'s stationed at the western edge of Windshear Crag.",
  },
  [1484] = {
    ["T"] = "The Corrupter",
    ["O"] = "Speak to Takata Steelblade in Desolace.",
    ["D"] = "This creature is far more dangerous than I had anticipated.  No doubt Takata questions my abilities to take on such a creature, and it would insult his honor if he were not informed about what just happened.$B$BSpeak to the swordsman, tell him you are willing to slay the demon if he would allow it, but do not press too hard. It is better if he thinks it his own idea.",
  },
  [1485] = {
    ["T"] = "Vile Familiars",
    ["O"] = "Bring 6 Vile Familiar Heads to Ruzan.",
    ["D"] = "Those warlocks who came before us have earned the distrust of the Horde, and while we are tolerated, we are not loved.$b$bThis has not brought the warlocks closer together, however, as many choose to live reclusively, dabbling in their magics alone. I will not seek to lead you down one path or the other, but only help you in your maturation.$b$bBefore I teach you the art of summoning, bring me six heads of the vile familiars from the cave to the northeast.",
  },
  [1486] = {
    ["T"] = "Deviate Hides",
    ["O"] = "Nalpak in the Wailing Caverns wants 20 Deviate Hides.",
    ["D"] = "As Naralex descended deeper into his nightmare, a strange breed of beasts arose from beneath the Barrens into the Wailing Caverns.$b$bThese deviate creatures have strange, otherworldly properties. While evil in nature, it is my opinion that some good can come from their existence here in Kalimdor. I believe their hides will be of particular use in the ways of leatherworking.$b$bIf you feel up to the task, venture into the caves below and retrieve some deviate hides.",
  },
  [1487] = {
    ["T"] = "Deviate Eradication",
    ["O"] = "Ebru in the Wailing Caverns wants you to kill 7 Deviate Ravagers, 7 Deviate Vipers, 7 Deviate Shamblers and 7 Deviate Dreadfangs.",
    ["D"] = "Naralex had a noble goal.$b$bOur great leader aspired to enter the Emerald Dream and help regrow these harsh lands back into the lush forest it once was. But something went terribly wrong.$b$bNaralex\'s dream turned into a nightmare and corrupt creatures began to inhabit the caverns.$b$bWhile some Disciples of Naralex seek to awake our master, my concern is with ridding these caves of the evil beasts.$b$bBrave the caverns, $n, and eradicate the deviate spawn.",
  },
  [1488] = {
    ["T"] = "The Corrupter",
    ["O"] = "Slay Lord Azrethoc and Jugkar Grim\'rod and return to Takata Steelblade in Desolace.",
    ["D"] = "Although I have no knowledge of this Lord Azrethoc, I agree with Maurin, he must be stopped; especially if he is attempting to gain influence over the Burning Blade to further his goals.$B$BYou have already shown you wish to aid the Warchief, and I would trust you to gather a party to slay the demon lord and his warlock servant.$B$BWhen you are ready, head directly south,  and return to me when the threat has been ended.",
  },
  [1489] = {
    ["T"] = "Hamuul Runetotem",
    ["O"] = "Speak with Hamuul Runetotem.",
    ["D"] = "The shells you brought me have a taint, one I have never before seen.  I am certain the water of the Oases is the cause, and I can sense the confusion they cause this land.  It is as if the land dreams and cannot awake.$B$BThe Wailing Caverns must be the source of the taint.  But before you go there, speak with Hamuul Runetotem, the elder druid.  I sent word to him of the happenings in the Barrens.  He is wise, and can offer you counsel.$B$BYou will find him in Thunder Bluff, on the Elder Rise.",
  },
  [1490] = {
    ["T"] = "Nara Wildmane",
    ["O"] = "Speak with Nara Wildmane.",
    ["D"] = "I entered the land of dreams, and dreamed of a cult of druids who dwell in the Wailing Caverns.  I saw that their goals were once noble, but they have lost their way.  They are now servants of the taint seeping through the Barrens.$B$BI am old and my strength fails.  I could not long dwell in that dreaming place and know little more of the tainted druids.  But Nara is young and her spirit is fierce.  She remained in the dreaming when I fled.$B$BSpeak with Nara.  She is in the tent, over yonder.",
  },
  [1491] = {
    ["T"] = "Smart Drinks",
    ["O"] = "Bring 6 portions of Wailing Essence to Mebok Mizzyrix in Ratchet.",
    ["D"] = "Ok, so the raptor horns didn\'t work so well.  But that\'s all right -- no one got rich on their first try!  And besides, I think I found something that\'ll work even better in my smart drinks!$B$BThere is a substance called Wailing Essence, and I think it is the cause of the strange plants and animals in the Barrens.  Get me some and I\'ll try it in my drink!$B$BI heard the essence gathers in ectoplasmic creatures in the Wailing Caverns to the west.  Hunt those creatures and gather the essence.",
  },
  [1492] = {
    ["T"] = "Wharfmaster Dizzywig",
    ["O"] = "Bring the Secure Crate to Wharfmaster Dizzywig in Ratchet.",
    ["D"] = "I have need to send a crate of potions, reagents and other items to my associates in the Old World. Little time I have to actually see them to the dock at Ratchet, and the last caravan has already left.$b$bIf you could, please take this to Wharfmaster Dizzywig. He will ensure that it is placed on the next boat to Blackwater Cove.",
  },
  [1498] = {
    ["T"] = "Path of Defense",
    ["O"] = "Bring 5 Singed Scales to Uzzek at Far Watch Post in the Barrens.",
    ["D"] = "As a warrior, one of our most crucial tasks is... protection.  We are the shield of the Horde, and we keep our weaker brethren safe.  If you are to join in our ranks, then you must prove your mettle to me.$B$BGo to Thunder Ridge in Durotar, where live the lightning hides and thunder lizards.  Hunt the beasts!  Show me you are tougher than they!  Brave their assaults and bring me a stack of their singed scales as proof.$B$BDo this, and then I will teach you.",
  },
  [1499] = {
    ["T"] = "Vile Familiars",
    ["O"] = "Speak with Zureetha Fargaze.",
    ["D"] = "Zureetha has been sending novices like yourself to help cull the numbers of the creatures in the cave near where you fought the vile familiars.$b$bI think it would be good for you to gain experience by assisting her. Be sure to tell her that you assisted with the imp problem, she\'ll probably have a reward for you.",
  },
  [1501] = {
    ["T"] = "Creature of the Void",
    ["O"] = "Retrieve the Tablet of Verga for Gan\'rul Bloodeye in Orgrimmar.",
    ["D"] = "So few warlocks remain... We risk much, but the risks are warranted. You must quickly reach maturity in your ability if you are to be of use to the Horde and to the Warchief.$b$bIt is time that you learned a new summoning spell.$b$bFirst, you must recover the Tablet of Verga, which was stolen by warlocks of the Burning Blade and is now kept at their lair in Skull Rock to the east of the city. With the tablet, I will show you the runes necessary to summoning a voidwalker.",
  },
  [1502] = {
    ["T"] = "Thun\'grim Firegaze",
    ["O"] = "Speak with Thun\'grim Firegaze in the Barrens.",
    ["D"] = "It is vital that a warrior\'s weapon is one of quality.  And if there is an orc who can craft quality weapons, then that orc is Thun\'grim Firegaze.  He is a hermit who dwells atop the hills east of the Crossroads.$B$BIf you seek a weapon, warrior, then you should seek Thun\'grim.  To find him, first travel to the Crossroads to the west, then strike east into the hills.  His camp is high on a hilltop.",
  },
  [1503] = {
    ["T"] = "Forged Steel",
    ["O"] = "Bring the Forged Steel Bars to Thun\'grim Firegaze in the Barrens.",
    ["D"] = "I will give you a weapon, $N, but first you must aid me.  The Razormanes to the northwest came into my camp a few nights past.  They would not meet me in combat, but they know my eyes are bad--a few distracted me with snorts while others stole from me!  The cowards!$B$BThey took an iron chest, and within it were some forged steel bars.  If you can recover them then I will give you the weapon you seek.$B$BSearch for the iron box within the quilboar camps, and return with the forged steel bars.",
  },
  [1504] = {
    ["T"] = "The Binding",
    ["O"] = "Using the Glyphs of Summoning, summon and subdue a voidwalker, then return the Glyphs of Summoning to Gan\'rul Bloodeye in Orgrimmar.",
    ["D"] = "Now, observe the Tablet of Verga, for it contains the glyphs you will need for the summoning. Take them to the circle in Neeru Fireblade\'s tent on the other side of the cavern. There you will be able to call for the voidwalker.$b$bYou must defeat it using whatever physical and magical means you have at your disposal. When this is completed, return to me with the glyphs of summoning and I will demonstrate how you may bind the creature to your will.",
  },
  [1505] = {
    ["T"] = "Veteran Uzzek",
    ["O"] = "Speak with Uzzek at Far Watch Post.",
    ["D"] = "The Horde honors its warriors because their strength is unquestioned.  But the truly great warriors are great students as well.$B$BSeek out Uzzek.  He is a veteran of many battles and now instructs young warriors in the ways of combat.  You will find him south of Far Watch Post, the orc garrison on the border of the Barrens and Durotar.",
  },
  [1506] = {
    ["T"] = "Gan\'rul\'s Summons",
    ["O"] = "Speak with Gan\'rul Bloodeye in Orgrimmar.",
    ["D"] = "You there. $n, right?$b$bI hope your schedule isn\'t busy, because Gan\'rul wants to see you right away. You\'ll find him with the senior warlocks in the Cleft of Shadow.$b$bBased on my past dealings with Gan\'rul, your summons is either a very good thing or a very bad thing.",
  },
  [1507] = {
    ["T"] = "Devourer of Souls",
    ["O"] = "Speak with Cazul in the Cleft of Shadow.",
    ["D"] = "The time has come for you to learn more of the summoning magics. You will learn to summon a succubus, a creature of incredible mental powers. It can lull those of weak minds into its servitude--but have no fear! I feel certain that you will be equal to the challenge.$b$bA succubus can only be summoned when baited with an item that represents great devotion and love. I leave it to you to find such an object.$b$bA suggestion? Speak with Cazul... There seems to be little the ancient warlock does not know.",
  },
  [1508] = {
    ["T"] = "Blind Cazul",
    ["O"] = "Speak with Zankaja in Orgrimmar.",
    ["D"] = "Hmm... But, of course, that is not why you have come to speak with me.$b$bIf Gan\'rul has sent you to me, it must be that you are to summon a succubus, and need an object to use for enticement.$b$bHmm... If you trust a blind man to lead you, $n, then listen. In the bluffs of Orgrimmar, west of the wind rider tower, lives Zankaja. She waits for the return of her mate.$b$bGo to her.",
  },
  [1509] = {
    ["T"] = "News of Dogran",
    ["O"] = "Speak with Gazrog at the Crossroads.",
    ["D"] = "My mate, Dogran, was recently sent to the Crossroads for duty. It was a promotion, and he was very proud.$b$bI was sad, of course, but I didn\'t let him know that. And worried, too. The Barrens is a more dangerous place than Orgrimmar, and I feared the worst.$b$bNow it\'s been a week after he should have returned, and I have received no word from him...$b$bPerhaps you could go to the Crossroads and see if they have any news? He worked with Gazrog, who should still be stationed there.",
  },
  [1510] = {
    ["T"] = "News of Dogran",
    ["O"] = "Speak with Ken\'zigla at the Malaka\'jin.",
    ["D"] = "He was stationed at Camp Taurajo, where we\'ve been trying to weed out the quilboar. He led a few grunts to one of their camps, where he was hurt bad. The others made it back and tried to rescue him. He was in bad shape, his wounds festered, so they were afraid to move him without patching him up first.$b$bThey sent a message here, requesting a healing draught from the trolls at the Malaka\'jin at the base of the Stonetalon Mountains. We sent someone to get it, but they haven\'t returned yet.",
  },
  [1511] = {
    ["T"] = "Ken\'zigla\'s Draught",
    ["O"] = "Bring Ken\'zigla\'s Draught to Grunt Logmar at Camp Taurajo.",
    ["D"] = "I figured there might be another coming to get the draught, so I prepared it anyways.$b$bHere you go, $n. Mind you, you should only give half of it to him! That will give him enough strength to move without bleeding himself dry. Wait a night, then give him the other half.$b$bAnyways, you\'ll want to bring this to Logmar at Camp Taurajo. From what the first one told me, he should have an idea where this Dogran is.",
  },
  [1512] = {
    ["T"] = "Love\'s Gift",
    ["O"] = "Bring the Dirt-caked Pendant to Gan\'rul Bloodeye in Orgrimmar.",
    ["D"] = "Zankaja is young and beautiful. To be chained to an invalid... I will not allow her to suffer that injustice.$b$bLeave me, $n. And thank you. I may end my life in glorious battle, instead of a lifeless husk.$b$bAnd please, I would ask you not to tell my mate of this... I do not want guilt on her conscience.",
  },
  [1513] = {
    ["T"] = "The Binding",
    ["O"] = "Using Dogran\'s Pendant, summon and subdue a succubus, then return Dogran\'s Pendant to Gan\'rul Bloodeye in Orgrimmar.",
    ["D"] = "Take this to the summoning circle in Neeru\'s tent, $n. Summon forth a foul succubus, then show it your mastery. I believe in the strength of your magic. As always, though, be wary, for pride and overconfidence has overcome many a promising warlock.$b$bWhen you have defeated it, return the pendant to me so that I might deliver the news to Zankaja.",
  },
  [1514] = {
    ["T"] = "Corrupted Windblossom",
    ["O"] = "",
    ["D"] = "",
  },
  [1515] = {
    ["T"] = "Dogran\'s Captivity",
    ["O"] = "Bring Ken\'zigla\'s Draught to Dogran in the Barrens.",
    ["D"] = "You\'ve made it this far, so if you want to see this through to the end, I won\'t stop you. I can\'t spare you any additional manpower though, you\'ll be on your own.$b$bDogran is being held in a quilboar camp to the north of here. Look for a group of two huts. He\'ll be heavily guarded--that is, if they haven\'t killed him yet, or he hasn\'t succumbed to his wounds.$b$bGood luck.",
  },
  [1516] = {
    ["T"] = "Call of Earth",
    ["O"] = "Bring 2 Felstalker Hooves to Canaga Earthcaller in the Valley of Trials.",
    ["D"] = "The time is now, young $c. You\'ve grown strong and your spirit endures like the earth. Following the shaman\'s path shows you have wisdom before even proving yourself.$B$BThe element of earth will guide your destiny, becoming part of you if you are ready. But you must stand before the earth itself. If you are ready, then you will see things only shaman know of.$B$BSeek out the Felstalkers to the north, and take from them two of their hooves. Return to me then, and we shall speak more of your future.",
  },
  [1517] = {
    ["T"] = "Call of Earth",
    ["O"] = "Find Spirit Rock and drink the Earth Sapta.",
    ["D"] = "A sapta is a drink created to bind our spirits to the elements. Fused with magic, the potion will allow the strong willed to see the elements as no one else can. Your spirit will be connected to the element the sapta was created for, and only a select few shaman know the recipe.$B$BThe sapta is always drunk in a holy place, and never anywhere else--remember that.$B$BSeek out Spirit Rock southwest of here--it is found at the end of the Hidden Path. Drink your sapta there, and... well, you shall see.",
  },
  [1518] = {
    ["T"] = "Call of Earth",
    ["O"] = "Bring the Rough Quartz to Canaga Earthcaller in the Valley of Trials.",
    ["D"] = "Take this rough quartz from me and bring it to Canaga Earthcaller as proof of our meeting. He will craft a totem for you, and this pebble will be the heart of it. As small as it may seem, you will come to find that size does not matter, and that even the smallest of things can outlast many others.$B$BBe patient, $N. Remain strong. And seek wisdom. This is what earth asks of you. In the end, you shall become one with the earth--respect it as it shall respect you.",
  },
  [1519] = {
    ["T"] = "Call of Earth",
    ["O"] = "Bring 2 applications of Ritual Salve to Seer Ravenfeather in Camp Narache.",
    ["D"] = "The time has come, young $c. Your body ages and grows strong, and your spirit endures like the earth. I see it within you-you have wisdom already.$B$BThe earth guides our people, and it speaks to us, as it will to you in time. But you must be tested. If you are ready, then you shall see things only our kind know of.$B$BSeek out the Bristleback shamans in their ravine to the east, and take from them a salve they use in their rituals. Return to me only when you have enough for your own potion.",
  },
  [1520] = {
    ["T"] = "Call of Earth",
    ["O"] = "Find Kodo Rock and drink the Earth Sapta.",
    ["D"] = "A sapta is a drink created to bind our spirits to the elements. Fused with magic, the potion will allow the open minded to see the elements as no one has seen them before. Your spirit will forever be tied to the element the sapta was created for, and only a select few of our kind know the secrets of its creation.$B$BThe sapta is always imbibed in a holy place, and never anywhere else.$B$BSeek out the shaman shrine to the east of here and drink of the sapta.",
  },
  [1521] = {
    ["T"] = "Call of Earth",
    ["O"] = "Bring the Rough Quartz to Seer Ravenfeather in Camp Narache.",
    ["D"] = "Take this rough quartz from me and bring it to Seer Ravenfeather as proof of our meeting. She will craft a totem for you, and this pebble will be the heart of it. As small as it may seem, you will come to find that size does not matter, and that even the smallest of things can outlast mountains.$B$BBe patient, $N. Remain strong. And seek wisdom. This is what earth asks of you. In the end, you shall become one with the earth--respect it as it shall respect you.",
  },
  [1522] = {
    ["T"] = "Call of Fire (Part 1)",
    ["O"] = "Find Kranal Fiss in the Barrens.",
    ["D"] = "Fire be witcha, mon. I see you be strong in da ways of earth already, an\' me tinkin\' you be ready to talk to Kranal.$B$BKranal live north of da Crossroads in da Barrens. He be strong in da ways of da elements and can teach ya da ways of fire... if you tink you be ready.$B$BHe be easy ta find, just don\' go too far--ya go too far an\' ya find yerself in da elves\' lands.",
  },
  [1523] = {
    ["T"] = "Call of Fire",
    ["O"] = "Find Kranal Fiss in the Barrens.",
    ["D"] = "Long has it been since you were last tested, $N. You have shown patience thus far in your studies, but the time has come for you to learn more. The time has come for you to learn about destruction... and chaos. The element of fire is yours for the understanding, if you\'re prepared to pay for it.$B$BSeek out Kranal Fiss in the Barrens. He lives in a small dwelling north of the Crossroads, but be mindful, if you\'ve reached the night elf lands, you\'ve gone too far.",
  },
  [1524] = {
    ["T"] = "Call of Fire",
    ["O"] = "Bring the Torch of the Dormant Flame to Telf Joolam in Durotar.",
    ["D"] = "You are powerful enough to wield the totem of fire, but that does not mean you are ready.$B$BAlong the Southfury River in Durotar, before the Great Sea, lies a hidden trail to one of the highest peaks in the land. Find your way to the top and you shall find one of our holy shrines watched over by Telf Joolam--the Shrine of the Dormant Flame.$B$BTake this torch to him--it marks that you attempt to tame fire. Return it to me aflame by the brazier at the shrine and I will see you are given the totem of fire.",
  },
  [1525] = {
    ["T"] = "Call of Fire",
    ["O"] = "Bring 1 Fire Tar and 1 Reagent Pouch to Telf Joolam in Durotar.",
    ["D"] = "But you did not come here for a history lesson, did you? You came for the Eternal Flame and fire totem.$B$BThe first step is to create a sapta and bind you to the flame.$B$BI can create the fire sapta for you if you bring me two items. The first is fire tar, a simple item usually carried by Razormane spellcasters in eastern Barrens.$B$BThe second item will be more dangerous: a reagent pouch from one of the cultists of the Burning Blade. You can find them in a cave above the ravines northeast of Razor Hill.",
  },
  [1526] = {
    ["T"] = "Call of Fire",
    ["O"] = "Defeat the Minor Manifestation of Fire, and place the Glowing Ember in the brazier atop the Shrine of Eternal Flame.",
    ["D"] = "The fire sapta is finished. The time has come for you to face the flame. Use the sapta at the shrine and head to the top of the mountain to face the manifestation there. He will not attack until you are ready, but he will realize you are able to see him.$B$BStrike him down swiftly, $N, for he shall only grow in power the longer you take to slay him. Once he is defeated, place his glowing embers within the brazier and light the torch.",
  },
  [1527] = {
    ["T"] = "Call of Fire",
    ["O"] = "Bring the Torch of Eternal Flame to Kranal Fiss in the Barrens.",
    ["D"] = "Torch firmly in hand, you turn away from the brazier and look out over Durotar. You feel an excitement tugging at your spirit as the wind picks up and forces the flame to dance violently.$B$BYou start off down the path back to the Barrens in search of Kranal Fiss.",
  },
  [1528] = {
    ["T"] = "Call of Water",
    ["O"] = "Find Islen Waterseer in the Barrens.",
    ["D"] = "Water be in ya future, mon. I see you be strong in da ways of fire already, an\' me tinkin\' you be ready to talk to Islen.$B$BIslen live along the coast of da Barrens. If ya head south along the Southfury River, ya canna miss her--look fer da big shark! She be strong in da ways of da elements and can teach ya da ways of water... if you tink you be ready.$B$BShe be easy ta find, just don\' go too far south--she be jus\' beyond Ratchet.",
  },
  [1529] = {
    ["T"] = "Call of Water",
    ["O"] = "Find Islen Waterseer in the Barrens.",
    ["D"] = "Long has it been since you were last tested, $N. You have shown patience thus far in your studies, but the time has come for you to learn more. The time has come for you to learn about the purity of water.$B$BSeek out Islen Waterseer in the Barrens. She is along the eastern coast, south of the Southfury River and Ratchet. You will find her there at her fishing hut. Good luck to you, $N.",
  },
  [1530] = {
    ["T"] = "Call of Water",
    ["O"] = "Find Brine in Southern Barrens.",
    ["D"] = "If there is one lesson you must learn of water, it is this: water means rebirth.$B$BIts power flows, erodes, and makes clean all manner of things. You will find its ability to heal and replenish incomparable, but only when it is pure.$B$BWhen you understand that water, when polluted, can devastate all that it touches, you will appreciate how vital a resource it is.$B$BIf you wish the water sapta, find Brine deep in Southern Barrens. Her home is continually molested by the quilboar there.",
  },
  [1531] = {
    ["T"] = "Call of Air",
    ["O"] = "Find Prate Cloudseer in Thousand Needles.",
    ["D"] = "You be makin\' headway now, mon. Your be ready to prove yerself worthy of the air totem, but first, ya must fin\' Prate Cloudseer in Thousand Needles. She be willin\' ta tell ya how to get yer air totem if ya treat her wit enough respect.$B$BHead south through the Barrens, and after ya reach Thousand Needles, head east. You\'ll find her hiddin\' in a hole wit\' her boyfriend--haha, don\' tell her I be dissin\' her none--she not take it too kindly.",
  },
  [1532] = {
    ["T"] = "Call of Air",
    ["O"] = "Find Prate Cloudseer in Thousand Needles.",
    ["D"] = "The winds of Kalimdor rush to meet you and you shall be counted among some of our most powerful $g brothers:sisters;. I am proud to see you have come this far and have such a promising future.$B$BThe element of air is a difficult one to control, but I have faith that your skills will overcome any obstacles that are placed in front of you.$B$BHead to the very south of the Barrens, and enter Thousand Needles. It is there, far to the east, that you will find Prate Cloudseer. She will teach you more.",
  },
  [1534] = {
    ["T"] = "Call of Water (Part 4)",
    ["O"] = "Fill the Empty Blue Waterskin at the Ruins of Stardust in Ashenvale and return to Brine in the Barrens.",
    ["D"] = "Take this waterskin and head deep into the lush forest the night elves call Ashenvale. It is to the far north of the Barrens.$B$BOnce you\'ve entered the forest, travel west. Along the mountains that separate Stonetalon and Ashenvale, and beyond Lake Mystral, you will find a holy place: the Ruins of Stardust. At the center of the lake you will find a small fountain.$B$BBring me a sample of the fountain\'s water. It will be the final component you\'ll need before I send you back to Islen.",
  },
  [1535] = {
    ["T"] = "Call of Water (Part 2)",
    ["O"] = "Fill the Empty Brown Waterskin at the watering hole below Brine\'s hut and return it to her in the Barrens.",
    ["D"] = "To find understanding and gain the sapta from me, you must seek out only the purest forms of water. As you fetch the samples for me, take note of your surroundings. Be aware of the creatures that rely on the water, that protect it. See how it affects forests and deserts. Witness how the very substance that sustains life will force creatures to take lives.$B$BWe shall start with a simple task: down below my hut there is a pond of the precious liquid. Fill this skin with it and return to me.",
  },
  [1536] = {
    ["T"] = "Call of Water (Part 3)",
    ["O"] = "Fill the Empty Red Waterskin at the well in Tarren Mill and return to Brine in the Barrens.",
    ["D"] = "Long ago I followed some companions into the Hillsbrad Foothills. We defended ourselves from some tenacious humans who make their home there and we found ourselves in a small town the Forsaken had taken over: Tarren Mill.$B$BIt was there I found a most peculiar thing: in the center of town was a small well of fresh spring water. I\'m not sure the plagued citizens of the town even realize its value--it\'s a small holy vestige in an unhallowed sea.$B$BBring me a sample of that water in this waterskin.",
  },
  [1558] = {
    ["T"] = "The Stonewrought Dam",
    ["O"] = "Take the orphan to the Stonewrought Dam in Loch Modan.  You should take him to the middle of the dam so he can see out over the giant waterfall.",
    ["D"] = "Ironforge is where all the dwarves and gnomes live.  Near there is a place called Loch Modan, and the dwarves built a HUGE dam there.  They say it\'s one of the biggest things anyone\'s ever made.  Wow!$B$BCould you take me to Loch Modan and see the dam there, please?  I think it is the Stone... um... Stonewrench Dam?  Stonewrought?  It\'s big - that\'s for sure.  I\'d like to look out over the big waterfall.  I promise I won\'t lean over too far!",
  },
  [1559] = {
    ["T"] = "Flash Bomb Recipe",
    ["O"] = "",
    ["D"] = "",
  },
  [1560] = {
    ["T"] = "Tooga\'s Quest",
    ["O"] = "Show Tooga the way back to his wife, Torta.",
    ["D"] = "I am so lost!$b$bMy wife, Torta, sent me out to fetch dinner and I must have taken a wrong turn somewhere.$b$bWould you be so kind as to lead me back to Torta? She\'s waiting for me just south of Steamwheedle Port.",
  },
  [1578] = {
    ["T"] = "Supplying the Front",
    ["O"] = "Bring 6 Copper Axes and 6 Copper Chain Belts to Thorvald in the southern guard tower of Loch Modan.",
    ["D"] = "The mountaineers of Loch Modan are fighting troggs, and having a busy time of it!  They need constant supplies of new armor and weapons if we\'re ever to get a handle on those troggs.$B$BThat\'s why my colleague Thorvald is there, trying to keep the mountaineers in good gear.  So if you were to make some copper axes and copper chain belts and bring them to Thorvald, he would be obliged.  He might even share his knowledge with you.$B$BThorvald is in Loch Modan\'s southern guard tower.",
  },
  [1579] = {
    ["T"] = "Gaffer Jacks",
    ["O"] = "Bring 8 Gaffer Jacks to Wizbang Cranktoggle in Auberdine.",
    ["D"] = "I came to Auberdine because of the quiet.  I want to invent my inventions where it\'s quiet, because after a night of drinking I don\'t like all the noise of Ironforge!$B$BSo I took a ship to Auberdine and had a little too much to drink on the way.  I dropped my box of gaffer jacks overboard!  I need my gaffer jacks!$B$BI don\'t know exactly where I dropped them, but if you fish for them in the ocean you might get lucky...",
  },
  [1580] = {
    ["T"] = "Electropellers",
    ["O"] = "Bring 12 Electropellers to Wizbang Cranktoggle in Auberdine.",
    ["D"] = "We know you\'re lucky, but... just how lucky are you, $N?$B$BOne night I was wandering the roads of Darkshore.  I was looking for a good river or lake to try my new self-propelled, exploding duck decoy and I think I was a little tipsy... because I passed out and awoke hours later in Auberdine.  My hair was singed and my bag of electropellers was gone!  I must have dropped them in the water!$B$BI need those electropellers!  Maybe you can fish them out of some lake or stream in Darkshore...",
  },
  [1581] = {
    ["T"] = "Elixirs for the Bladeleafs",
    ["O"] = "Bring 6 Elixirs of Lion\'s Strength and 2 Elixirs of Minor Defense to Syral Bladeleaf in Dolanaar.",
    ["D"] = "Our herb and alchemy shop is booming, but my husband spends so much time mixing potions that we never have time for each other.$B$BCan you help us?$B$BIf you can bring me a supply of pre-made elixirs, then I would be happy to trade with you.  I have some rare herbs that you might find useful.",
  },
  [1582] = {
    ["T"] = "Moonglow Vest",
    ["O"] = "Bring 1 Embossed Leather Gloves, 5 Embossed Leather Boots and 5 Embossed Leather Cloaks to Lotherias in Darnassus.",
    ["D"] = "We in Darnassus, especially we craftsman, are quite busy.  Young adventurers are eager to test themselves in the wilderness, and are in constant need of supplies.$B$BIf you can help me fill my orders of leather goods, then perhaps I can teach you an advanced leatherworking technique.",
  },
  [1598] = {
    ["T"] = "The Stolen Tome",
    ["O"] = "Retrieve the Powers of the Void for Drusilla La Salle.",
    ["D"] = "You there! $n, right?$b$bI hope you\'re sure about becoming a warlock, because if you can\'t hold your own, you\'re likely to get pulled under.$b$bI\'m to teach you, but I need to make sure you\'re strong enough first.$b$bThere was a novice studying at the Abbey who ran off to join the Defias Brotherhood. Why do I care? Well, he stole some important books.$b$bOne of those happens to be a book I\'ve wanted for myself, the Powers of the Void. Steal it for me, they have it hidden at their tent in the Vineyards.",
  },
  [1599] = {
    ["T"] = "Beginnings",
    ["O"] = "Bring 3 Feather Charms to Alamar Grimm in Anvilmar.",
    ["D"] = "You must be the new warlock everyone\'s been whispering about. Must be someone\'s taken a liking to you if they\'ve asked me to take time away from my research to start your training.$b$bDon\'t worry, I won\'t hold it against you.$b$bThe simplest of the summoning spells you will learn is that of the imp. Before I impart this to you, however, you must prove that you have the necessary magical and physical ability.$b$bBring me three feather charms from the frostmane novices from the cave to the southwest.",
  },
  [1618] = {
    ["T"] = "Gearing Redridge",
    ["O"] = "Tormus Deepforge  wants you to bring 4 Runed Copper Belts and 4 Heavy Copper Mauls to Verner Osgood in Redridge.",
    ["D"] = "We dwarves aren\'t the only ones fighting.  Our human allies have their hands full too!$B$BRedridge is under periodic attack from blackrock orcs, and their supply of equipment is always low.  If you were to help them out, I\'m sure their resident blacksmith, Verner Osgood, would teach you a trick or two.",
  },
  [1638] = {
    ["T"] = "A Warrior\'s Training",
    ["O"] = "Speak with Harry Burlguard.",
    ["D"] = "You\'ve shown yourself to be a competent warrior, $N.  But you\'re still young and have a lot to learn.$B$BSpeak with Harry Burlguard--he\'s usually in the Pig and Whistle tavern, in the Old Town of Stormwind.  Harry is an old veteran and a good teacher.  He can show you a few moves that\'ll keep you breathing longer.",
  },
  [1639] = {
    ["T"] = "Bartleby the Drunk",
    ["O"] = "Speak with Bartleby.",
    ["D"] = "Yeah, I can teach you.  But first I wanna see what moves you have already.$B$BSee that drunk over there at the end of the bar?  Bartleby?  I want you to get his mug.$B$BHah, and if you know Bartleby then you know he won\'t just give it to you.",
  },
  [1640] = {
    ["T"] = "Beat Bartleby",
    ["O"] = "Beat Bartleby, then talk to him.",
    ["D"] = "You want what??  You\'re crazy!$B$BThe only way you\'ll get this mug is if you pry it from my cold, dead fingers...",
  },
  [1641] = {
    ["T"] = "The Tome of Divinity",
    ["O"] = "",
    ["D"] = "",
  },
  [1642] = {
    ["T"] = "The Tome of Divinity",
    ["O"] = "Speak to Duthorian Rall in Stormwind.",
    ["D"] = "In all things, a paladin must reflect the Light which supplements our strength. To strive to be divine for one of our kind does not mean we strive for godhood--we strive to be good in all actions.$B$BAlthough called upon to smite evil in these harsh times, you must always remember that it\'s aiding others that will truly set you apart from the citizens of Azeroth. Compassion, patience, bravery--these things mean as much to a paladin as strength in battle.$B$BKnow this well, and never forget it.",
  },
  [1643] = {
    ["T"] = "The Tome of Divinity",
    ["O"] = "Search the Trade District of Stormwind for Stephanie Turner.",
    ["D"] = "Your task then, $N, is to find someone here in Stormwind that is in need of aid. Someone who is pure of heart and motive. Perhaps someone helping others. Perhaps someone in need of a guide. Regardless, the person must have noble intent and be relying on your kindness.$B$BDo this, and return to me when you are finished. Then, we shall discuss more of your path and what it means to be a paladin of the Light.",
  },
  [1644] = {
    ["T"] = "The Tome of Divinity",
    ["O"] = "Bring 10 Linen Cloth to Stephanie Turner in Stormwind.",
    ["D"] = "Bless their hearts, most of them have no families because of the Scourge attacks in the last decade.$B$BMy husband John collects the materials for me to make the shirts and pants for the children, but I haven\'t even had time to do that. I\'ve been too focused on keeping food on their plates, and a roof over their heads.$B$BWhat\'s that? You\'d be willing to help? Oh, thank the Light, and thank you, $N.$B$BIf you could bring me some linen cloth so I can sew clothes for the kids, that would help me out so much.",
  },
  [1645] = {
    ["T"] = "The Tome of Divinity",
    ["O"] = "",
    ["D"] = "",
  },
  [1646] = {
    ["T"] = "The Tome of Divinity",
    ["O"] = "Speak to Tiza Battleforge in Ironforge.",
    ["D"] = "In all things, a paladin must reflect the Light which supplements our strength. To strive to be divine for one of our kind does not mean we strive for godhood--we strive to be good in all actions.$B$BAlthough called upon to smite evil in these harsh times, you must always remember that it\'s aiding others that will truly set you apart from the citizens of Azeroth. Compassion, patience, bravery--these things mean as much to a paladin as strength in battle.$B$BKnow this well, and never forget it.",
  },
  [1647] = {
    ["T"] = "The Tome of Divinity",
    ["O"] = "Search the outer ring of Ironforge for John Turner.",
    ["D"] = "Your task then, $N, is to find someone here in Ironforge that is in need of aid. Someone who is pure of heart and motive. Perhaps someone helping others. Perhaps someone in need of a guide. Regardless, the person must have noble intent and be relying on your kindness.$B$BDo this, and return to me when you are finished. Then, we shall discuss more of your path and what it means to be a paladin of the Light. I will remain here in the temple for your return. Other paladins will need my guidance also.",
  },
  [1648] = {
    ["T"] = "The Tome of Divinity",
    ["O"] = "Bring 10 Linen Cloth to John Turner in Ironforge.",
    ["D"] = "My wife and I run an orphanage in Stormwind, mostly for children who have lost parents due to the attacks by the Scourge in the last decade. I come to Ironforge every month or so to see about collecting more supplies from the citizens here, and talk to the various groups to see if they can do anything to help.$B$BThis trip\'s been agonizingly slow, and not very productive.$B$BIf I could just get some more linen for my wife to make some clothes, I could finally send a shipment home.",
  },
  [1649] = {
    ["T"] = "The Tome of Valor",
    ["O"] = "Speak to Duthorian Rall in Stormwind.",
    ["D"] = "Valor, and all the virtues that go along with it, are like rare minerals: you must cherish them when you find them because of their value, but you must also take time to harvest them... refine them. When mixed together properly, a strong alloy is formed, sometimes unbreakable by even the most powerful blows.$B$BThis strength, your faith, your bravery, must be tested. You must learn to never lose faith and realize that with the power of the Light, you can overcome things that most others cannot.",
  },
  [1650] = {
    ["T"] = "The Tome of Valor",
    ["O"] = "Find Daphne Stilwell in Westfall.",
    ["D"] = "Good! Your lack of hesitation is a sure sign that you are prepared and eager to test yourself. So be it.$B$BAt the very ends of Westfall\'s Longshore, a small house overlooks the ocean. There, a couple lives, the Stilwells. Jordan is an incredible smith, and he\'s gone to Ironforge to meet with the dwarves for a while. But this has left Daphne, his wife, alone and unprotected.$B$BReports suggest that the Defias have all but taken over the area, Daphne may need some protection from the villains. Go there.",
  },
  [1651] = {
    ["T"] = "The Tome of Valor",
    ["O"] = "Defend Daphne Stilwell from the Defias attack.$B$BNeither of your spirits must be released from their mortal coils if you wish to succeed.$B$BAfter you are successful, speak to Daphne Stilwell again.",
    ["D"] = "Now, they\'ll be coming again soon, I\'d wager, so get yourself ready.$B$BI\'ve got myself a gun in the house. I\'ll grab it and be ready to help you out, but for the most part, you\'re on your own.$B$BI\'ve no idea how many they\'ll send, but I\'m sure it\'ll be more than the few they sent earlier.$B$BAsk the Light for whatever blessings you can because... did you hear that? I think they\'re coming.$B$BWe make our stand near the house!",
  },
  [1652] = {
    ["T"] = "The Tome of Valor",
    ["O"] = "Speak to Duthorian Rall in Stormwind.",
    ["D"] = "Please, return to Duthorian and let him know that I\'m safe for now. I have you and the Church to thank for that.$B$BHe\'ll send word to Ironforge to let my Jordan know that he has nothing to fear.$B$BOh, how I miss my husband. I can\'t wait till he returns.",
  },
  [1653] = {
    ["T"] = "The Test of Righteousness",
    ["O"] = "Speak to Jordan Stilwell in Ironforge",
    ["D"] = "Actually, I have no doubt that Jordan himself would like to thank you, and seeing as I need a messenger to send word that his wife is safe, I think you\'d be a perfect choice.$B$BJordan was to be a paladin of the Light before his dedication to his father\'s craft took him on another path--he\'s intimately familiar with our way of life and is considered among our most faithful.$B$BHe should still be in Ironforge--he likes to work outside the gates during the day, something about the cool mountain air.",
  },
  [1654] = {
    ["T"] = "The Test of Righteousness",
    ["O"] = "Using Jordan\'s Weapon Notes, find some Whitestone Oak Lumber, Bailor\'s Refined Ore Shipment, Jordan\'s Smithing Hammer, and a Kor Gem, and return them to Jordan Stilwell in Ironforge.",
    ["D"] = "I\'d like to reward you if you\'d be willing to accept such a thing? Don\'t misunderstand me, my generosity is dependent on how much help you can be, but I see us both benefiting greatly from such an alliance.$B$BI will craft for you a weapon worthy of what you\'ve accomplished, a symbol of paladinhood and the Light, but you must gather the items I need to craft it. I\'ve already started taking notes on the items, it just requires someone brave and skilled enough to recover them. What do you say, $c?",
  },
  [1655] = {
    ["T"] = "Bailor\'s Ore Shipment",
    ["O"] = "Bring Jordan\'s Ore Shipment to Bailor Stonehand in Loch Modan.",
    ["D"] = "Ach, ya startled me, $G man:woman;! Can\'t ya see I was lost in thought?$B$BWhat\'s that? Jordan\'s shipment... fer Ironforge. Ahhh, yeah, I know whatcha speak of. Mo\'grosh ogres... dat\'s who took me ol\' friend\'s ore shipment. Caravan was headin\' here from the Wetlands and was ambushed. The guard who survived their attack just passed away a few days ago--healer couldn\'t get to \'em fast enough.$B$BIf you want the shipment, you\'ll have to head northeast across the Loch. It\'s got to be there somewhere.",
  },
  [1656] = {
    ["T"] = "A Task Unfinished",
    ["O"] = "Bring the Bundle of Furs to Innkeeper Kauth in Bloodhoof Village.",
    ["D"] = "$N, please, I beg of you, can you help me? I have not finished my tasks here on the Mesa--I\'m in the middle of my Rite of Strength--and my father asked me to deliver these furs to the inn in Bloodhoof.$B$BI\'m far from rested enough to complete both my rite and this task for my father. Would you mind going on for me?$B$BThe inn is pleasant and a perfect place to rest during your long travels. I\'m sure you\'ll agree once you see it.",
  },
  [1657] = {
    ["T"] = "Stinking Up Southshore",
    ["O"] = "Toss three Forsaken Stink Bombs into the heart of Southshore in Hillsbrad, and return to Darkcaller Yanka at the Wickerman Festival in Tirisfal Glade.",
    ["D"] = "Celebrate Hallow\'s End in style... by bringing grief to our enemies in Southshore!$B$BTake one of these specially crafted stink bombs.  It is filled with an odiferous funk that no human - or any weak-willed beast for that matter - can stand to smell.  You\'ll need to throw it right in the heart of Southshore for it to do its job, so be prepared for a fight against the Alliance!$B$BWhen you\'ve completed this task... this tribute to our liberation, return to me!",
  },
  [1657] = {
    ["T"] = "[DISABLED] Stinking Up Southshore",
    ["O"] = "",
    ["D"] = "",
  },
  [1658] = {
    ["T"] = "Crashing the Wickerman Festival (PvP)",
    ["O"] = "Locate the Forsaken\'s Wickerman Festival in Tirisfal Glade.  Return to Sergeant Hartman in Southshore once you\'ve done so.",
    ["D"] = "I need brave individuals to head up to Tirisfal Glade and check out the Wickerman Festival.  I\'m not going to lie - heading up there will be dangerous.  The guards at the festival will be exceptionally vicious.  Still, we need to know how big the festival is this year, and how much trouble the Forsaken are going to cause us during Hallow\'s End.$B$BIf you\'re up for it, head to Tirisfal and scout out the festival.  Return to me with the information, and I\'ll make sure you\'re well compensated.",
  },
  [1661] = {
    ["T"] = "The Tome of Nobility",
    ["O"] = "Speak to Duthorian Rall in Stormwind.",
    ["D"] = "I greet you once again, $N. I am more proud to call you $g brother:sister; than ever before--your dedication is a tribute to us all.$B$BThe time has come for me to bestow a great gift upon you. But know this, for now I give you this freely, but later, when time permits, this will be a much greater challenge to all paladins who choose to undertake my quest.$B$BSpeak to me again and I shall grant you a great boon.",
  },
  [1665] = {
    ["T"] = "Bartleby\'s Mug",
    ["O"] = "Bring Bartleby\'s Mug to Burlguard",
    ["D"] = "Ok, here\'s my mug.  Wait!  Let me finish what\'s in it first... there we go.",
  },
  [1666] = {
    ["T"] = "Marshal Haggard",
    ["O"] = "Speak with Marshal Haggard.",
    ["D"] = "You\'ve learned a few moves, but you could still use a decent weapon.  I bet if you spoke with Marshal Haggard he could help you.$B$BMarshal Haggard lives in a manor in Elwynn Forest, at the Eastvale Lumber Camp.  He\'s retired now but he collected a lot of gear during his career.$B$BLet him know I sent you, and with luck he\'ll have a spare weapon to offer.",
  },
  [1667] = {
    ["T"] = "Dead-tooth Jack",
    ["O"] = "Retrieve Haggard\'s Badge from Dead-tooth Jack\'s camp, and return to Marshal Haggard.",
    ["D"] = "Burlguard sent you, did he?  Yes, I have spare weapons around here, but first I have a task for you...$B$BA while ago a Defias villain Dead-tooth Jack tricked me.  It was dark and he was wearing a Stormwind tabard and I thought he was Marshal Dughan.  He said he needed my old Stormwind Marshal\'s badge and I gave it to him... and then he laughed and ran off before I could catch him!$B$BGet back my badge!  Dead-tooth Jack probably has it stowed in his camp to the south.",
  },
  [1678] = {
    ["T"] = "Vejrek",
    ["O"] = "Bring Vejrek\'s Head to Muren Stormpike in Ironforge.",
    ["D"] = "If you want to rise in the ranks of the warriors, then you better be tough.  I can teach you new moves but before that, prove to me you can handle yourself against real foes.$B$BVejrek is the toughest troll you\'ll find in these parts, and he\'s who you have to beat.  He has a hut in the foothills just south of Frostmane Hold$B$BBring me Vejrek\'s head and I\'ll know you have mettle.",
  },
  [1679] = {
    ["T"] = "Muren Stormpike",
    ["O"] = "Speak with Muren Stormpike.",
    ["D"] = "Hello, warrior!  You show a lot of promise, but you still need to learn some tricks and Muren Stormpike is just the dwarf to teach you.$B$BYou can find Muren at the Hall of Arms in the Military Ward of Ironforge.  Talk to him when you\'re ready to learn what it is to be a warrior.",
  },
  [1680] = {
    ["T"] = "Tormus Deepforge",
    ["O"] = "Speak with Tormus Deepforge.",
    ["D"] = "Now that you\'ve learned some moves, it\'s time to get you a decent weapon.  Speak with Tormus Deepforge.  He can make you a weapon I\'m sure you\'ll like.$B$BYou\'ll find Tormus here in Ironforge, at the Great Forge.",
  },
  [1681] = {
    ["T"] = "Ironband\'s Compound",
    ["O"] = "Bring a load of Umbral Ore to Tormus Deepforge in Ironforge.",
    ["D"] = "My mentor, Sturgy Ironband, had a compound south of Helm\'s Bed Lake in eastern Dun Morogh.  There he taught blacksmithing and he was the best.$B$BBut one day, one of his students brought him a load of strange ore.  Master Ironband called it umbral ore and was eager to work with it, but soon after it arrived... a band of Dark Iron dwarves attacked his compound.  They killed everyone!$B$BEnter Ironband\'s compound, $N, and avenge my master\'s death.  And bring me the load of umbral ore that caused it.",
  },
  [1682] = {
    ["T"] = "Grey Iron Weapons",
    ["O"] = "",
    ["D"] = "",
  },
  [1683] = {
    ["T"] = "Vorlus Vilehoof",
    ["O"] = "Bring the Horn of Vorlus to Elanaria in Darnassus.",
    ["D"] = "Before I can teach you, you must show me your resolve.$B$BThe satyr Vorlus Vilehoof has found one of our remote moonwells, southeast of the Ban\'ethil Barrow Den.  He now splashes within it, defiling its pure waters with his filth.$B$BFind Vorlus at the moonwell and destroy him.  Bring me his horn as proof and I will begin your advanced training as a warrior.$B$BThe path to the moonwell is hidden well, $N, but stay focused and alert and you will find it.",
  },
  [1684] = {
    ["T"] = "Elanaria",
    ["O"] = "Speak with Elanaria.",
    ["D"] = "Greetings, warrior.  Your skill grows, but there is more to your profession than you may believe.  To further progress, you must soon find a teacher.$B$BThe warrior Elanaria dwells in Darnassus, at the Warrior\'s Terrace.  She can instruct you.",
  },
  [1685] = {
    ["T"] = "Gakin\'s Summons",
    ["O"] = "Report to Gakin the Darkbinder in the Mage Quarter of Stormwind.",
    ["D"] = "Isn\'t that how it always works? Was looking for you all day and couldn\'t find you, now when I\'m not expecting it, you come and walk right up to me.$b$bI\'ve wasted enough time trying to find you, so I won\'t delay you any longer. Master Gakin\'s summoned you for additional training. Seems to me you should seek him out as soon as possible.$b$bYou\'ll find him in the basement of the Slaughtered Lamb tavern. By the way, you might want to keep that particular location quiet. Don\'t want word spreading about it.",
  },
  [1686] = {
    ["T"] = "The Shade of Elura",
    ["O"] = "Bring 8 loads of Elunite Ore and the Medallion of Elura to Elanaria in Darnassus.",
    ["D"] = "You have learned much, $N.  Now we will craft you a weapon.  A warrior\'s weapon.$B$BTo do this, you must travel to Darkshore.$B$BLong ago a ship crashed upon the rocks near the Auberdine lighthouse, scattering many crates of elunite ore along the ocean floor.$B$BFirst, you must defeat the elunite\'s guardian, the Shade of Elura.  Once the ship\'s captain, she cursed herself to roam the deeps and protect her shipment for all time.  Defeat her, gain her medallion, and gather the crates of lost Elunite.",
  },
  [1687] = {
    ["T"] = "Spooky Lighthouse",
    ["O"] = "Take the orphan to see the lighthouse off the coast of Westfall.",
    ["D"] = "Sometimes late at night, our matrons at the orphanage will tell us spooky stories.  Those RULE!  There\'s one about a ghost that haunts a lighthouse in Westfall.  They say he was a pirate captain who had his ship taken from him by other pirates.  Wow!$B$BCould you please take me to the lighthouse in Westfall?  I wanna see if the ghost of that captain is there!  I promise to stay close to you; I won\'t run off or anything!",
  },
  [1688] = {
    ["T"] = "Surena Caledon",
    ["O"] = "Retrieve Surena\'s Choker for Gakin the Darkbinder in Stormwind.",
    ["D"] = "I had a student named Surena Caledon. She stood where you stand now, eager to learn of warlock magic, and possessing no small bit of talent. More than that she was young and pleasing to the eye. Had I seen it then, the traitorous trollop!$b$bThe thieving wench ran off with one of the Defias, Erlan Drudgemoor. While her loss is of little importance, I gifted her a bloodstone choker that I must have.$b$bHer life means nothing to me now. You will find her at the Brackwell Pumpkin Patch. Retrieve what is mine.",
  },
  [1689] = {
    ["T"] = "The Binding",
    ["O"] = "Using the Bloodstone Choker, summon and subdue a voidwalker, then return the Bloodstone Choker to Gakin the Darkbinder in Stormwind.",
    ["D"] = "By retrieving my choker and besting Surena, you\'ve proven that you\'re at least as capable as she was.$b$bWith that said, I can tell you\'re direly in need of training. You can manage the flows of magic, yes, but being a warlock is much more than that.$b$bI will show you how to command a voidwalker, but before I can, you must call one forth at a summoning circle and subdue it using whatever weapons--physical and magical--you have at your disposal. Use the magic of this choker at the summoning circle below.",
  },
  [1690] = {
    ["T"] = "Wastewander Justice",
    ["O"] = "Take down 10 Wastewander Bandits and Thieves east of Gadgetzan, then report back to Chief Engineer Bilgewhizzle.",
    ["D"] = "You there!  It\'s time for you to be a big helper to the Gadgetzan Water Company.$B$BThe Wastewander nomads out in the Tanaris desert have seized almost all the water wells!  Without access to them, our water supply will dry up in no time.  If you want to get in our good graces, then you\'ll help us bring justice to those nomads!$B$BHead east of here and bring down ten Wastewander Bandits and Thieves, then report back to me on the double.  Justice waits for no man... goblin... bah, just get on it!",
  },
  [1691] = {
    ["T"] = "More Wastewander Justice",
    ["O"] = "Take down 10 Wastewander Shadow Mages, 8 Wastewander Rogues, and 6 Wastewander Assassins for Chief Engineer Bilgewhizzle in Gadgetzan.",
    ["D"] = "We need you to take on the stronger ranks of the Wastewander nomads now.  You\'ve done a good job thus far, but if we are to take back those water wells, then it only makes sense to do it against as few nomads as possible!$B$BThis time around we need you to take down their rogues, assassins, and shadow mages.  You\'ll find them east and southeast of here.$B$BI\'m not going to lie to you - they\'re a nasty lot.  Do this for us though, and we\'ll be on our way to taking back what is rightfully ours!",
  },
  [1692] = {
    ["T"] = "Smith Mathiel",
    ["O"] = "Bring the Case of Elunite to Smith Mathiel. ",
    ["D"] = "I have placed the elunite you gathered into a case.  Take it to the blacksmith Mathiel, and he will craft for you an elunite weapon.$B$BMathiel is to the south, on the other side of this building.",
  },
  [1693] = {
    ["T"] = "Weapons of Elunite",
    ["O"] = "",
    ["D"] = "",
  },
  [1698] = {
    ["T"] = "Yorus Barleybrew",
    ["O"] = "Speak with Yorus Barleybrew.",
    ["D"] = "Yorus Barleybrew has issued a challenge to all able warriors of the Alliance.  He waits at the Lakeshire Inn, in Redridge, and pledges a grand reward to anyone able to pass his trial.",
  },
  [1699] = {
    ["T"] = "The Rethban Gauntlet",
    ["O"] = "Enter the Rethban Caverns, reach the first fork, and return to Yorus Barleybrew within the time allowed.$B$BYou must not die and release your spirit.",
    ["D"] = "I call my challenge the Rethban Gauntlet.  To pass the trial, all you must do is enter the Rethban Caverns, to the first fork, then return here.  Easy!  Except there are a few wrinkles...$B$BYou have to return within an hour.$B$BThere are whole heaps of Redridge Gnolls in the way.$B$BBefore you start, you have to take a swig of my family\'s signature drink: Barleybrew Scalder!$B$BSo what do you say?  The Rethban Caverns are in the foothills north of Lakeshire.  Are you ready to run the Rethban Gauntlet?",
  },
  [1700] = {
    ["T"] = "Grimand Elmore",
    ["O"] = "Bring Furen\'s Notes to Grimand Elmore.",
    ["D"] = "I made a copy of my notes on the techniques I learned while making your armor, and I need them sent to Grimand Elmore.  He\'s here in the Dwarven District, in the weapon shop.$B$BAnd duck when you give these to him.  He\'s fiercely proud of his skills, and won\'t like the idea of learning something from me...",
  },
  [1701] = {
    ["T"] = "Fire Hardened Mail",
    ["O"] = "Gather the materials Furen Longbeard requires, and bring them to him in Stormwind.",
    ["D"] = "So I gave you a nice shield, but we don\'t have to stop there.  I know the secrets to make extremely hard mail armor.  It\'s not easy and it takes very rare materials, but if you\'re willing to do the legwork then I\'ll make a piece for you.$B$BWhat do you say, $N?  If you\'re up for the challenge, then I\'ll give you a list of what to get and where to get them.",
  },
  [1702] = {
    ["T"] = "The Shieldsmith",
    ["O"] = "Bring the Cask of Scalder to Furen Longbeard.",
    ["D"] = "You really showed what you were made of when you ran the Rethban Gauntlet, $N.  And I\'m true to my word.  I\'ll get you the reward I promised...$B$BExcept it\'s in Stormwind.$B$BTalk to my friend, Furen Longbeard, in the Dwarven District of Stormwind.  He\'s the best shieldsmith you\'ll ever meet, and he owes me a big favor.$B$BHere, take Furen this cask of Barleybrew Scalder and ask him for a shield.  He\'ll give you a nice one.",
  },
  [1703] = {
    ["T"] = "Mathiel",
    ["O"] = "Bring Furen\'s Notes to Mathiel.",
    ["D"] = "I made a copy of my notes on the techniques I learned while making your armor, and need them sent to the blacksmith Mathiel.  He lives in Darnassus which is quite a long way off, but I\'m sure he\'ll be very grateful to learn what I\'ve learned.",
  },
  [1704] = {
    ["T"] = "Klockmort Spannerspan",
    ["O"] = "Bring Furen\'s Notes to Klockmort Spannerspan.",
    ["D"] = "I made a copy of my notes on the techniques I learned while making your armor, and need someone to take them to Klockmort Spannerspan.  He\'s a gnomish blacksmith in Ironforge and when he learns what I\'ve learned... I bet he\'ll pull off his own beard in excitement.",
  },
  [1705] = {
    ["T"] = "Burning Blood",
    ["O"] = "Bring 20 vials of Burning Blood and 1 Burning Rock to Grimand Elmore in Stormwind.",
    ["D"] = "I hate to admit that Furen might have really found something.  But to be sure I\'ll need to try his techniques on a new piece of armor.  If you help me with the materials, $N, then you can have the armor.$B$BTo make this armor, I need samples of burning blood and a burning rock.$B$BThe only place I know to get them is Duskwood.  The Nightbane worgen based in the Roland\'s Doom mine have burning blood, and their leader Gutspill will have the rock.$B$BMay fortune grant you speed, $N.",
  },
  [1706] = {
    ["T"] = "Grimand\'s Armor",
    ["O"] = "",
    ["D"] = "",
  },
  [1707] = {
    ["T"] = "Water Pouch Bounty",
    ["O"] = "Bring 5 Wastewander Water Pouches to Spigot Operator Luglunket in Gadgetzan.",
    ["D"] = "Greetings $n - the name\'s Luglunket, and we\'ve got an important job for you if you\'re up for it!  Huzzah!$B$BNothing would please me more as a seasoned spigot operator to take any Wastewander water pouches you might find out in the desert.  That\'s our water anyway - the nomads have just stolen it is all!  The Gadgetzan Water Company will offer you an official care package for every five you turn in.  Most importantly though, you\'re helping Gadgetzan help itself!",
  },
  [1708] = {
    ["T"] = "Iron Coral",
    ["O"] = "Bring 20 loads of Searing Coral to Klockmort Spannerspan in Ironforge.",
    ["D"] = "I want to try Furen Longbeard\'s new techniques on my own piece of armor.  And because you helped Furen and he helped me, you\'re welcome to the armor after I make it!$B$BTo finish the piece, I need a special coral, searing coral.  It grows in only one place--along the coast south of Menethil Harbor in the Wetlands.$B$BBring me enough searing coral and I\'ll get right to work on the armor.  And hurry, $N.  I\'m so eager to get started it\'s making my toes twist!",
  },
  [1709] = {
    ["T"] = "Klockmort\'s Creation",
    ["O"] = "",
    ["D"] = "",
  },
  [1710] = {
    ["T"] = "Sunscorched Shells",
    ["O"] = "Bring 20 Sunscorched Shells to Mathiel in Darnassus.",
    ["D"] = "My colleague, Furen Longbeard, made a discovery which I am eager to test.  I wish to try it on a new armor that I am researching, for I believe Furen\'s techniques will produce superior results.$B$BWill you help me?  If you gather what I need to make the armor, then I will make an extra piece for you.$B$BI need the sunscorched egg shells of the Highperch Wyverns who dwell at Highperch, in Thousand Needles.  Search for the shells below their nests, and return to me when you have a sufficient amount.",
  },
  [1711] = {
    ["T"] = "Mathiel\'s Armor",
    ["O"] = "",
    ["D"] = "",
  },
  [1712] = {
    ["T"] = "Cyclonian",
    ["O"] = "Bring the items on Bath\'rah\'s Parchment to Bath\'rah the Windwatcher in Alterac.",
    ["D"] = "I have spent ages watching this site.  I watch, so that one day I might see the great Cyclonian fall.  But I have not yet found a champion to defeat the wind lord.$B$BPerhaps you are that champion.$B$BProve it to me.  Gather the items I need to summon Cyclonian, and I will know you are a great warrior.  Perhaps then you can defeat him and gain his whirlwind heart.$B$BIf so, then I will infuse the heart\'s power in a weapon, and I will give that weapon to you.$B$BHere is a parchment with the items I need.",
  },
  [1713] = {
    ["T"] = "The Summoning",
    ["O"] = "Bring the Whirlwind Heart to Bath\'rah Windwatcher.",
    ["D"] = "I am ready to perform the ritual to summon Cyclonian.  When he is summoned, you must defeat him and take his whirlwind heart.  This I will use to instill a weapon with Cyclonian\'s power.$B$B$N, are you ready to face the wind lord?",
  },
  [1714] = {
    ["T"] = "Essence of the Exile",
    ["O"] = "",
    ["D"] = "",
  },
  [1715] = {
    ["T"] = "The Slaughtered Lamb",
    ["O"] = "Report to Gakin the Darkbinder in the Mage Quarter of Stormwind.",
    ["D"] = "Hmph. Times past, all warlocks in Khaz Modan were trained under the auspices of the masters of Gnomeregan, but now that we\'ve all been forced to relocate into this rat\'s hole in Ironforge, and Magni\'s decided to keep a careful watch on us, we\'re forced to send our novices to Stormwind for training.$b$bLuckily, Gakin\'s no slouch, so you\'ll still learn what you need to know, but it burns my blood!$b$bAnyways, you\'ll find Gakin in the basement of the Slaughtered Lamb in Stormwind.",
  },
  [1716] = {
    ["T"] = "Devourer of Souls",
    ["O"] = "Speak with Takar the Seer in the Barrens.",
    ["D"] = "A succubus is a devourer of souls, destroyer of hearts, and dominator of minds. It knows nothing but evil.$b$bThis will be the creature you will learn to summon and control, but it cannot be brought into our world without a stimulus.$b$bWith a symbol of love as bait, it will surely answer your call. Then you can defeat it, and make it your tool.$b$bIt will be hard to find something of this nature, though. You should speak with Takar the Seer. You will find him in the Barrens of Kalimdor near Camp Taurajo.",
  },
  [1717] = {
    ["T"] = "Gakin\'s Summons",
    ["O"] = "Report to Gakin the Darkbinder in the Mage Quarter of Stormwind.",
    ["D"] = "$n! Didn\'t expect to see you around these parts, but I was instructed to keep an eye out for you.$b$bIt\'s past time you got some more training, so you should stop in to see Gakin the next time you\'re in Stormwind. He\'ll be waiting for you.",
  },
  [1718] = {
    ["T"] = "The Islander",
    ["O"] = "Speak with Klannoc Macleod.",
    ["D"] = "Your renown as a warrior grows, $N.  Now it is time to pit yourself against your peers and see where you stand.$B$BThere is an island off the coast of the Barrens, south of Ratchet.  It is called Fray Island and it is a place where warriors meet.$B$BMeet, and fight.$B$BSpeak with the chief warrior of Fray Island, Klannoc Macleod.  If he finds you worthy, then Klannoc will lead you further down the warrior\'s path.$B$BTo reach Fray Island, search for the sand bar shallows along the Merchant Coast.",
  },
  [1719] = {
    ["T"] = "The Affray",
    ["O"] = "Kill Big Will, then speak to Klannoc Macleod on Fray Island.",
    ["D"] = "If you want to learn from me, then you must first pass The Affray.  It is a challenge we on Fray Island like to offer rising warriors who think they\'re tough, and if you can pass this test then I\'ll know you are.$B$BTo begin, walk into the middle of that crowd yonder and step on the grate.  That\'ll tell Twiggy Flathead you\'re ready, and he\'ll send challengers against you.$B$BBeat all the challengers and he\'ll call out Big Will.  Kill Big Will, and return to me in the time we allow.",
  },
  [1738] = {
    ["T"] = "Heartswood",
    ["O"] = "Retrieve the Heartswood from Ashenvale and bring it to Gakin the Darkbinder in the Mage Quarter of Stormwind.",
    ["D"] = "In preparation for your coming, I used my sight to look over the world and through the mists of time. I have found what it is you seek.$b$bIn the War of the Ancients, two lovers fell together in battle. Where they took their last breaths, a tree known as the heartswood grew, a symbol of their undying love.$b$bThat should be enough to tempt a succubus. You will find it in the ruins of Ordil\'Aran in the northwestern part of the forest of Ashenvale.",
  },
  [1739] = {
    ["T"] = "The Binding",
    ["O"] = "Using the Heartswood Core, summon and subdue a succubus, then return the Heartswood Core to Gakin the Darkbinder in the Slaughtered Lamb.",
    ["D"] = "Using the heartswood core, you will be able to cast a spell at the summoning circle in the crypt below to open a portal for the succubus to pass through.$b$bBe careful, $n, succubi have overrun even the strongest of minds.$b$bOnce you have bested and returned it to the void, return to me and I will show you how to call and control it.",
  },
  [1740] = {
    ["T"] = "The Orb of Soran\'ruk",
    ["O"] = "Find 3 Soran\'ruk Fragments and 1 Large Soran\'ruk Fragment and return them to Doan Karhan in the Barrens.",
    ["D"] = "When demons of the Legion fall out of favor, their spirits are imprisoned within orbs and distributed to their mages and warlocks.$b$bMany were carried by their servants in Azeroth. Even fragmented, they still possess the powers of the demons trapped inside.$b$bThe orb of Soran\'ruk was broken into two pieces, one was further broken to be used by the acolytes of the Twilight\'s Hammer in Blackfathom Deeps. The other, larger piece, was possessed by a wizard in Shadowfang Keep. Find them and I can reform it.",
  },
  [1758] = {
    ["T"] = "Tome of the Cabal",
    ["O"] = "Speak with Krom Stoutarm in Ironforge.",
    ["D"] = "It will take more than a simple summoning circle to bring forth a felhunter for you to challenge.$b$bMy three acolytes and I will assist in creating the magic circles. You require a copy of the Tome of the Cabal, which has been lost to us for some time. It was written by the first group of warlocks to summon a felhunter unaided.$b$bPerhaps Krom Stoutarm can help you with this. An itinerant warlock these days, he spends his days hoarding ancient texts and skulking about the library in Ironforge.",
  },
  [1778] = {
    ["T"] = "The Tome of Divinity",
    ["O"] = "Speak to Tiza Battleforge in Ironforge.",
    ["D"] = "I\'m going to see about making plans to return home soon. Hopefully, Stephanie is doing well back in Stormwind. I can\'t wait to see her again. Till next we meet, $c.",
  },
  [1779] = {
    ["T"] = "The Tome of Divinity",
    ["O"] = "Take the Symbol of Life and speak to Muiredon Battleforge in Ironforge.",
    ["D"] = "I want you to take this, $N. It is called the Symbol of Life.$B$BYou\'ve proven charitable and patient, and now I want you to prove just how adept you\'ve become in both combat, and power.$B$BMy husband Muiredon returned recently from an encounter with some Dark Iron dwarves, and he barely escaped with his life--he\'s over there having his wounds healed by one of our priests.$B$BTake the Symbol and speak to him. If you can help him, then we will speak again soon. May the Light shine upon you.",
  },
  [1780] = {
    ["T"] = "The Tome of Divinity",
    ["O"] = "Speak to Duthorian Rall in Stormwind.",
    ["D"] = "I\'ll take these to the children this very day. Please be safe, good $c. It is not often $G men:women; of your character show their faces in the city, but you\'ve proven that the paladins here in Stormwind shall never turn their backs on those in need.$B$BFarewell.",
  },
  [1781] = {
    ["T"] = "The Tome of Divinity",
    ["O"] = "Take the Symbol of Life to Gazin Tenorm in Stormwind.",
    ["D"] = "Take this, $N. It is called the Symbol of Life.$B$BYou\'ve proven charitable and patient, and now the time has come to see if you\'re powerful enough to focus one of the Light\'s greatest gifts.$B$BMy friend Gazin returned recently from an encounter with some Defias in Elwynn near Heroes\' Vigil--he barely escaped with his life. He\'s near the altar having his wounds healed by our priests.$B$BTake the Symbol and speak to him. If you can help him, then we will speak again soon. May you bask in the Light\'s glory.",
  },
  [1782] = {
    ["T"] = "Furen\'s Armor",
    ["O"] = "",
    ["D"] = "",
  },
  [1783] = {
    ["T"] = "The Tome of Divinity",
    ["O"] = "Take the Symbol of Life and resurrect Narm Faulk in Dun Morogh.",
    ["D"] = "We was spyin\' on some of the Dark Iron dwarves that\'ve bunkered down south of Helm\'s Bed Lake. We knew they was up to no good, so Narm an\' me thought we\'d take a closer look-got ta see their leader given orders to some o\' his men.$B$BNarm an\' me took the chance to kill a few of \'em, but they proved tougher than we thought, an\' more of \'em got the jump on us. Narm tol\' me to run an\' get support, so \'ere I am.$B$BNarm needs help, an\' I\'ll not rest till he gets it.$B$BC\'n ya help, $N?",
  },
  [1784] = {
    ["T"] = "The Tome of Divinity",
    ["O"] = "Retrieve a Dark Iron Script from the Dark Iron dwarves near Helm\'s Bed, and return to Muiredon Battleforge in Ironforge.",
    ["D"] = "Before I fell in battle, Muiredon and I had almost defeated one of the Dark Iron spies near that small house. We witnessed their captain passing orders to them, and sought to kill him and take the orders.$B$BSome of the council in Ironforge do not yet believe the presence of Dark Iron dwarves exists in Dun Morogh. By attaining one of those scripts, we\'ll not only have proof of their movements, but possibly an idea of what they\'re planning.$B$BGet one of those scripts and take it back to Muiredon, $N.",
  },
  [1785] = {
    ["T"] = "The Tome of Divinity",
    ["O"] = "Speak to Tiza Battleforge in Ironforge.",
    ["D"] = "I c\'n see me wife\'s o\'er there beamin\' wit pride already. Ya better get yer tuchus o\'er there pronto. I be thinkin\' she\'d have some words wit ya.$B$BLight be witcha, mighty $c. I\'ll be lookin\' fer ya on the battlefield against the Scourge and dem Dark Irons when the time comes.",
  },
  [1786] = {
    ["T"] = "The Tome of Divinity",
    ["O"] = "Take the Symbol of Life and resurrect Henze Faulk in Elwynn.",
    ["D"] = "Henze and I had made it to Heroes\' Vigil, an island in the middle of Stone Cairn Lake in northeastern Elwynn. We\'d heard the Defias had infiltrated our borders and we wanted proof to take to the king\'s advisors.$B$BWe caught sight of a couple of the scum going over some documents, and we attacked. Unfortunately, we were quickly overwhelmed by more of the mages.$B$BHenze held them off for me while I escaped... the daft fool, going and being brave.$B$BHe\'s out there now, Henze is. Can you help us, $N?",
  },
  [1787] = {
    ["T"] = "The Tome of Divinity",
    ["O"] = "Retrieve a Defias Script from the Defias Rogue Wizards in Elwynn, and return to Gazin Tenorm in Stormwind.",
    ["D"] = "We\'ll have time to talk more about the Symbol at a later time. What\'s important now is to get those papers we saw the Defias going over. If we can get our hands on them, we\'ll be able to prove the Defias have infiltrated Elwynn and hopefully gain some insight into what their plans are.$B$BI\'m in no shape for battle as of now, $N. Do you think you\'re strong enough to get those papers from the Defias here on the island?$B$BIf you\'re able to get them, bring them back to Gazin and let him know I\'m all right.",
  },
  [1788] = {
    ["T"] = "The Tome of Divinity",
    ["O"] = "Speak to Duthorian Rall in Stormwind.",
    ["D"] = "I\'ll take the script to the King\'s advisors after Duthorian\'s taken a look at them, but in the meantime, you should go speak to Duthorian now. He\'ll be wanting to pat you on the back for sure!$B$BThank you again, $N. I hope we meet again. You\'re surely an ally I\'m proud to call friend.",
  },
  [1789] = {
    ["T"] = "The Symbol of Life",
    ["O"] = "",
    ["D"] = "",
  },
  [1790] = {
    ["T"] = "The Symbol of Life",
    ["O"] = "",
    ["D"] = "",
  },
  [1791] = {
    ["T"] = "The Windwatcher",
    ["O"] = "Speak with Bath\'rah the Windwatcher.",
    ["D"] = "Now that you\'ve learned your berserker\'s stance, you\'ll want a nice weapon to use it with.$B$BTalk to Bath\'rah the Windwatcher, a troll hermit who lives between the Alterac Mountains and the Western Plaguelands.  He is a skilled weapon maker and a powerful shaman.$B$BTo find Bath\'rah, go to the river that flows along Tarren Mill\'s eastern flank.  Follow it north--Bath\'rah is camped near ruins that rest along the river\'s eastern bank.",
  },
  [1792] = {
    ["T"] = "Whirlwind Weapon",
    ["O"] = "",
    ["D"] = "",
  },
  [1793] = {
    ["T"] = "The Tome of Valor",
    ["O"] = "",
    ["D"] = "",
  },
  [1794] = {
    ["T"] = "The Tome of Valor",
    ["O"] = "",
    ["D"] = "",
  },
  [1795] = {
    ["T"] = "The Binding",
    ["O"] = "Using the Tome of the Cabal, summon and subdue a felhunter, then return the Tome of the Cabal to Strahad Farsan in Ratchet.",
    ["D"] = "If you are sufficiently prepared, then we should begin. Let me explain the process to you.$b$bI will begin by creating the basic summoning circle. When that is finished, my acolytes will use the rods of channeling to create the greater circle.$b$bOnce that is done, read the incantation from the Tome of the Cabal, and the felhunter will be pulled forth. Be careful, $n. A felhunter is a powerful foe, and I\'d like to see you keep your head on your shoulders.",
  },
  [1796] = {
    ["T"] = "Components for the Enchanted Gold Bloodrobe",
    ["O"] = "Bring Robes of the Arcana to Menara Voidrender in the Barrens.",
    ["D"] = "The first item I will aid you in creating is a robe. You will gather the items for me, and we will put them together. Afterwards, assuming you succeed, you will have the first of many items that will make you more powerful and aid you in your struggles.$B$BTo start, you must have crafted Robes of Arcana. A tailor can make this for you, or perhaps you can purchase one from someone else--I\'d be most impressed if you could make it yourself--but regardless, bring me the robes, and we shall go from there.",
  },
  [1798] = {
    ["T"] = "Seeking Strahad",
    ["O"] = "Speak with Strahad Farsan in the Barrens.",
    ["D"] = "It is time you learned the felhunter summoning, $n. I do not have the reagents and items necessary to teach you here, so you will have to seek out another, Strahad Farsan, another of our inner circle.$b$bHe makes his residence in the goblin port of Ratchet. I will send word ahead of you to inform him of your coming.",
  },
  [1799] = {
    ["T"] = "Fragments of the Orb of Orahil",
    ["O"] = "Speak to Menara\'s acolytes inside the tower above Ratchet and choose one of their paths to follow.$B$BAfterwards, bring an Infernal Orb to Tabetha in Dustwallow Marsh.",
    ["D"] = "Good, then we shall begin.$B$BThe first thing you must do is speak to my acolytes inside. Choose one of their paths to follow--they will explain more.$B$BSecond, you must travel to Desolace and find an Infernal Orb from one of the warlocks of Mannoroc Coven. It is that orb that you will cleanse with the help of a mage named Tabetha deep in southern Dustwallow Marsh. She will aid you because of our history together.$B$BAgain, do not come back until you have done both things I\'ve asked.",
  },
  [1800] = {
    ["T"] = "Lordaeron Throne Room",
    ["O"] = "Take your ward to the old Lordaeron Throne Room that lies just before descending into the Undercity.",
    ["D"] = "The orphan matrons at my home tell mighty stories of battle and lore at night if we are good and do our chores.  One story was about how Lordaeron fell, and about how the evil Arthas took his own father\'s life to become ruler.$B$BHe had no honor, $N!  When I grow up and become a mighty Horde warrior, my blade will be guided by honor - never evil!$B$BWould you take me to see the throne room, please?  It is just before where you would go down into the Undercity.",
  },
  [1801] = {
    ["T"] = "Tome of the Cabal",
    ["O"] = "Speak with Jorah Annison in the Undercity.",
    ["D"] = "It will take more than a simple summoning circle to bring forth a felhunter for you to challenge.$b$bMy three acolytes and I will assist in creating the magic circles. You require a copy of the Tome of the Cabal, which has been lost to us for some time. It was written by the first group of warlocks to summon a felhunter unaided.$b$bPerhaps Jorah Annison can help you with this. He\'s employed as a cartographer in the Undercity these days, but still knows more of ancient texts than any I know.",
  },
  [1802] = {
    ["T"] = "Tome of the Cabal",
    ["O"] = "Retrieve the Moldy Tome and Tattered Manuscript for Krom Stoutarm in Ironforge.",
    ["D"] = "It was lost, but there\'s some idea as to where it might be found.$b$bThere was only one copy ever made and it was vandalized at some point, a large portion of it ripped from the binding. Rumor has it that it somehow passed into the hands of the centaur of Thousand Needles.$b$bThe rest was lost off the coast west of Southshore, where it was to be transported to Menethil Harbor by boat. It never arrived.$b$bIn exchange for this knowledge, all I ask is that if you find it, bring it to me to see.",
  },
  [1803] = {
    ["T"] = "Tome of the Cabal",
    ["O"] = "Retrieve the Moldy Tome and Tattered Manuscript for Jorah Annison in the Undercity.",
    ["D"] = "Ah! I see that you are a warlock of similar tastes to mine. Indeed, acquiring the Tome of the Cabal is a worthy aspiration. Did you know that it was once held within the walls of Lordaeron? It was vandalized though, almost half its pages removed. Who would commit such a heinous act?$b$bThat half is lost, though rumor says that it fell into the hands of the centaur of the Thousand Needles.$b$bThe rest of the text was to be transferred to Ironforge, but was lost in transit on the coast west of Southshore.",
  },
  [1804] = {
    ["T"] = "Tome of the Cabal",
    ["O"] = "Bring the Reconstructed Tome and 3 Rods of Channeling to Strahad Farsan in Ratchet.",
    ["D"] = "Well, ah, it\'s hard to let it go. But you found it, and you have more important things to do with it, of course. Just, please, be sure to mention to Strahad that I wanted a copy.$b$bWhile you were out, he sent a message for you. He wanted me to tell you that for his acolytes to assist with the summoning, you\'ll have to acquire three rods of channeling.$b$bHe might not know where to find them, but I do! They were last used by the orcs of the Dragonmaw clan in the Wetlands. I\'d check their spellcasters!",
  },
  [1805] = {
    ["T"] = "Tome of the Cabal",
    ["O"] = "Bring the Reconstructed Tome and 3 Rods of Channeling to Strahad Farsan in Ratchet.",
    ["D"] = "This is one of the most interesting texts I have perused in the last few years. The selection here is sparse--mostly books on geography--and I have already read them all. I should hope that Strahad would send me a copy of this tome when he has the chance.$b$bSpeaking of him, he sent word to me while you were away. For your task, you must obtain three rods of channeling.$b$bFor allowing me to look at this text, I will share a secret: You can find the rods on orcs of the Dragonmaw Clan in the Wetlands.",
  },
  [1806] = {
    ["T"] = "The Test of Righteousness",
    ["O"] = "Wait for Jordan Stilwell to finish forging a weapon for you.",
    ["D"] = "Why don\'t we go over to the forge and get started on this? I\'ve been preparing for your arrival for some time, so it shouldn\'t take long at all.$B$BWhen we\'re done, you\'ll be far more prepared to defend the Light than you have been, this much I swear.$B$BI\'ll be somewhat honest, $N, seeing the results of your exploits makes me somewhat envious. But knowing that I can contribute to your adventures in my own significant way does bring me some joy.",
  },
  [1818] = {
    ["T"] = "Speak with Deathguard Dillinger",
    ["O"] = "Speak with Deathguard Dillinger in Brill.",
    ["D"] = "I was told Deathguard Dillinger has a job for someone like you.  If you\'re willing to show your value to the Dark Lady\'s forces, then report to Dillinger immediately.$B$BDeathguard Dillinger\'s post is on the west end of Brill, near the graveyard.",
  },
  [1819] = {
    ["T"] = "Ulag the Cleaver",
    ["O"] = "Kill Ulag the Cleaver, then speak with Deathguard Dillinger.",
    ["D"] = "When the Scourge swept through Tirisfal Glades, we captured one to study.  He had a free mind, and called himself Ulag the Cleaver.  But, although his will was free, he was still enthralled by the Lich King.$B$BWe hoped to find a way to break the king\'s control over Ulag, but we failed.  Now, finally, it is time to free him.  $B$BFree him, and kill him.$B$BActivate the skull stone trigger near the mausoleum to the north.  This will lift the magical seal that binds him.$B$BThen... be ready, $N.",
  },
  [1820] = {
    ["T"] = "Speak with Coleman",
    ["O"] = "Speak with Coleman Farthing.",
    ["D"] = "You have proven your value to the Dark Lady, now perhaps you can prove yourself to one of her subjects.$B$BColeman Farthing awaits a warrior who is willing to be his instrument of revenge against the Forsaken\'s enemies.$B$BYou will find him in the Gallow\'s End Tavern in eastern Brill.",
  },
  [1821] = {
    ["T"] = "Agamand Heirlooms",
    ["O"] = "Bring Coleman Farthing the Agamand Family Axe, the Agamand Family Sword, the Agamand Family Mace and the Agamand Family dagger.",
    ["D"] = "The Agamand family were my employers in life, and they were the cause of my death!  It is fitting that the Agamands are now mindless minions of the Lich King.$B$BNow I would have their last vestiges of honor.  Go into their family crypt and rob them of their family heirlooms: a sword, an axe, a dagger and a mace.  Bring them to me!$B$BThe Agamand family crypt is at the north end of the Agamand mills, protected by lurking scourge and dead Agamands.",
  },
  [1822] = {
    ["T"] = "Heirloom Weapon",
    ["O"] = "",
    ["D"] = "",
  },
  [1823] = {
    ["T"] = "Speak with Ruga",
    ["O"] = "Speak with Ruga Ragetotem.",
    ["D"] = "You are a warrior of skill and growing renown.  Perhaps you are ready for Ruga Ragetotem\'s trial?  If so, then find Ruga in Camp Taurajo in the Barrens.$B$BHe awaits those warriors who answer his call.",
  },
  [1824] = {
    ["T"] = "Trial at the Field of Giants",
    ["O"] = "Bring 5 twitching antennae to Ruga Ragetotem at Camp Taurajo",
    ["D"] = "To pass this trial, you must travel south to the Field of Giants.  There you will find a host of crawling, insect creatures.  They are new to the Barrens and I do not like what they herald.$B$BBring me their still-twitching antennae, and waste no time, $N, for harvested antennae will not twitch for long.  Bring me the antennae in the time allowed, and you will pass the trial.",
  },
  [1825] = {
    ["T"] = "Speak with Thun\'grim",
    ["O"] = "Speak with Thun\'grim Firegaze in the Barrens.",
    ["D"] = "Thun\'grim Firegaze, an orc blacksmith of renown, has heard of your continued deeds of valor in service of the Horde.  He would like to fashion for you a suit of armor, and bids you speak with him immediately.$B$BThun\'grim is camped east of the Crossroads in the Barrens.",
  },
  [1838] = {
    ["T"] = "Brutal Armor",
    ["O"] = "Bring to Thun\'grim Firegaze 15 Smoky Iron Ingots, 10 Powdered Azurite, 10 Iron Bars and a Vial of Phlogiston.",
    ["D"] = "I have heard tales of your strength and valor, and wish to make for you a suit of armor, a companion to aid you as tales of your great deeds grow.$B$BBut the fashioning of this armor will be a quest unto itself, $N.  I will need your help, and the help of other smiths of the Horde.$B$BHere, take this scroll.  I spent many nights, struggling with near-blind eyes, writing that which I need for the armor.  Bring me the items on this scroll and I will create for you the first piece of the suit.",
  },
  [1839] = {
    ["T"] = "Ula\'elek and the Brutal Gauntlets",
    ["O"] = "Speak with Ula\'elek in Durotar.",
    ["D"] = "The troll smith Ula\'elek will craft for you the brutal gauntlets.  He dwells in Durotar, in Sen\'jin Village with his Darkspear brethren, and he awaits your coming.",
  },
  [1840] = {
    ["T"] = "Orm Stonehoof and the Brutal Helm",
    ["O"] = "Speak with Orm Stonehoof in Thunder Bluff.",
    ["D"] = "Orm Stonehoof is respected greatly for his skill.  He will make for you the brutal helm.$B$BSeek him out in Thunder Bluff, near the pool by the city\'s great, central totem.",
  },
  [1841] = {
    ["T"] = "Velora Nitely and the Brutal Legguards",
    ["O"] = "Speak with Velora Nitely in the Undercity.",
    ["D"] = "It is said that, before her death, Velora Nitely was one of the finest armorsmiths in Lordaeron.  Death, it seems, has not decreased her talents.  Speak with Velora--she will make your brutal legguards.$B$BVelora plies her trade in the Undercity of Lordaeron, in the trade quarter.",
  },
  [1842] = {
    ["T"] = "Satyr Hooves",
    ["O"] = "Bring 7 Uncloven Satyr Hooves to Ula\'elek at Sen\'jin Village in Durotar.",
    ["D"] = "I can make you a pair of brutal gauntlets, and great magic will they hold.  But first, you must collect this magic and bring it to me.$B$BThe satyrs dwell in northeastern Ashenvale because of the forest\'s power.  They feed off its purity, leeching and corrupting.  You can tell which satyrs have drawn the most of Ashenvale\'s magic by their swollen, uncloven hooves.$B$BThese hooves hold great power.  I will need them for your gauntlets\' crafting.",
  },
  [1843] = {
    ["T"] = "Brutal Gauntlets",
    ["O"] = "",
    ["D"] = "",
  },
  [1844] = {
    ["T"] = "Chimaeric Horn",
    ["O"] = "Bring a Galvanized Horn to Orm Stonehoof in Thunder Bluff.",
    ["D"] = "I will make you the helm, but you must show me you are worthy of it.$B$BNestled in the northwestern reaches of the Stonetalon Mountains there lies the Charred Vale.  It was once a verdant place, but is now defiled and ashen, and what creatures remain there are mad with pain and despair.$B$BThat place is where I send you, $N.$B$BYou must go to the Charred Vale and find a chimaera matriarch.  Slay her, free her of her misery.  Bring me her galvanized horn as proof, and I shall make for you the helm. ",
  },
  [1845] = {
    ["T"] = "Brutal Helm",
    ["O"] = "",
    ["D"] = "",
  },
  [1846] = {
    ["T"] = "Dragonmaw Shinbones",
    ["O"] = "Bring 8 Sturdy Dragonmaw Shinbones to Velora Nitely in the Undercity.",
    ["D"] = "To make the brutal leg guards, I need a brace of sturdy shinbones.  The Dragonmaw orcs of the Wetlands have nice, thick shinbones.  I\'ll need theirs.$B$BBut they must be sturdy.  Be sure to test the shinbones as you collect them.  If you can break them with your bare hands, then they won\'t be sturdy enough to work with.",
  },
  [1847] = {
    ["T"] = "Brutal Legguards",
    ["O"] = "",
    ["D"] = "",
  },
  [1848] = {
    ["T"] = "Brutal Hauberk",
    ["O"] = "",
    ["D"] = "",
  },
  [1858] = {
    ["T"] = "The Shattered Hand",
    ["O"] = "Steal Tazan\'s key, then use it to open his Satchel and deliver its contents to Therzok in the Cleft of Shadow in Orgrimmar.",
    ["D"] = "I\'ll leave it to you to get Tazan\'s satchel open, and return to me with its contents. The locking mechanism is complex, so it would probably be easiest if you obtained the key.$b$bAs you didn\'t find it on Tazan\'s body, I can only assume that he left the key in the care of another... Ah, yes, that\'s right. He had an assistant named Gamon. A drunkard, you\'ll most likely find him at the tavern. Perhaps you could quietly relieve him of the key.",
  },
  [1859] = {
    ["T"] = "Therzok",
    ["O"] = "Speak with Therzok in the Cleft of Shadow in Orgrimmar.",
    ["D"] = "Lower your voice, $n. Yeah, I know your name. Was told to keep an eye out for an orc by your description.$b$bFrom what we\'ve heard about you, you can handle yourself in combat, and aren\'t opposed to a little sneakery from time to time.$b$bAm I right?$b$bAnyways, you should go see Therzok in Orgrimmar. He\'s got an offer for you to hear.",
  },
  [1860] = {
    ["T"] = "Speak with Jennea",
    ["O"] = "Speak with Jennea Cannon in Stormwind.",
    ["D"] = "We mages have a task that needs doing.  One that is vital to the safety of Stormwind and Elwynn forest.$B$BJennea Cannon has the details.  She studies in the wizard\'s sanctum, in the mage district of Stormwind.  You should make your way to her with haste.",
  },
  [1861] = {
    ["T"] = "Mirror Lake",
    ["O"] = "Bring a Mirror Lake sample to Jennea Cannon in Stormwind.",
    ["D"] = "This world has been victim to massive mystic struggles, which tore magic currents from once stable channels.  We struggle to determine the effects these changes have caused, and some of us fear the worst!$B$BBut I will reveal more of this later.  Right now, we need you to gather information.$B$BHere, take this flask.  Go to the base of the waterfall at Mirror Lake, southwest of the Stormwind gates, and retrieve a sample of the water there.  Return it to me so that I might test it for magical taints.",
  },
  [1878] = {
    ["T"] = "Water Pouch Bounty",
    ["O"] = "",
    ["D"] = "",
  },
  [1879] = {
    ["T"] = "Speak with Bink",
    ["O"] = "Speak with Bink in Ironforge.",
    ["D"] = "$N!  Bink, my gnome colleague in Ironforge, sends word.  She has a task that needs doing and won\'t rely on anyone but a mage to do it!  Are you that mage, $N?$B$BIf so, then speak with Bink.  She spends her time in the Hall of Mysteries in the Mystic Ward of Ironforge.",
  },
  [1880] = {
    ["T"] = "Mage-tastic Gizmonitor",
    ["O"] = "Bring Bink her Mage-tastic Gizmonitor.",
    ["D"] = "We mages do more than just waggle our fingers and make pretty sparks!  We control magic, the base fabric of our world!  And that takes great care, and resolve.  And knowledge... so much knowledge!$B$BOne of my devices, the mage-tastic gizmonitor, collects and stores that knowledge in huge amounts!  But it\'s lost!  When we fled our home of Gnomeregan, I left it in a chest in one of our huts outside the city, and now the place is crawling with leper gnomes!$B$BPlease, $N, retrieve my gizmonitor!",
  },
  [1881] = {
    ["T"] = "Speak with Anastasia",
    ["O"] = "Speak with Anastasia Hartwell.",
    ["D"] = "$N, Anastasia Hartwell has a task for someone of your talents.  Speak with her in the Magic Quarter of the Undercity, and prepare yourself for your first task for the mages of the Forsaken.",
  },
  [1882] = {
    ["T"] = "The Balnir Farmstead",
    ["O"] = "Bring Balnir Snapdragons to Anastasia Hartwell in the Mage Quarter of the Undercity.",
    ["D"] = "The Plaguelands to the east are an aberration.  Devised by the Lich King, they spread their magic into the soil, corrupting and killing all that live.  Although there is a certain charm to the destructive forces of the plague, we, the mages of the Forsaken, believe it is unruly and must be curtailed.$B$BBut first, we must measure how far it has spread into Tirisfal.  Go to the Balnir Farmstead in eastern Tirisfal.  In the garden are flowers--snapdragons.  Gather these and return.",
  },
  [1883] = {
    ["T"] = "Speak with Un\'thuwa",
    ["O"] = "Speak with Un\'thuwa.",
    ["D"] = "Un\'thuwa, tribal mage of Sen\'jin Village, detects a disturbance in the magic currents of Durotar and asks for aid.  Speak with Un\'thuwa and do what you can to help him.$B$BDo this, and in time further secrets will be revealed to you.",
  },
  [1884] = {
    ["T"] = "Ju-Ju Heaps",
    ["O"] = "Destroy 4 Ju-Ju Heaps, then return to Un\'thuwa in Sen\'jin Village.",
    ["D"] = "The warlock Zalazane of the Echo Isles has disrupted the normal flow of magic in Durotar.  He does this through tainted fetishes, ju-ju heaps, which he has placed among his huts on the main Echo Isle.$B$BThese heaps corrupt our wildlife and give power to the warlocks and infernal creatures that lurk in the land\'s dark corners.$B$BYou must destroy the ju-ju heaps, $N, before the flow of magic is forever altered.",
  },
  [1885] = {
    ["T"] = "Mennet Carkad",
    ["O"] = "Speak with Mennet Carkad in the Rogues\' Quarter of the Undercity.",
    ["D"] = "$n? Not quite what I was expecting, considering Carkad himself has taken an interest to you. Looks can be deceiving though, right?$b$bEither way, you should go see him as soon as possible. You wouldn\'t want to know what happened to the last few that chose to ignore his summons.",
  },
  [1886] = {
    ["T"] = "The Deathstalkers",
    ["O"] = "Get Astor\'s Letter of Introduction and return it to Mennet Carkad in the Rogues\' Quarter.",
    ["D"] = "If you wish to join the Deathstalkers, Lord Varimathras\' elite guard, you will have to prove your worth.$b$bI will not lie to you, $n, this task could mean your death.$b$bI have some things that need attending to. I require the personal effects of Astor Hadren, a messenger who travels between Silverpine and the Undercity. I will leave the method of procurement to your discretion.$b$bIt will be better for you if you know less of the nature of this business. Succeed and I shall tell you more.",
  },
  [1898] = {
    ["T"] = "The Deathstalkers",
    ["O"] = "Speak with Andron Gant in the Undercity.",
    ["D"] = "The letter of introduction should be enough to convince Andron that you are the messenger he\'s been waiting for. I\'ve met Andron before, he doesn\'t quite have the mental capacity to be that suspicious.$b$bLord Varimathras suspects that Andron has been providing aid to some other group. He wishes to know what organization this is and requires proof of it.$b$bI want you to speak with Andron, and obtain this information. You\'ll find him at his shop near the Apothecarium.",
  },
  [1899] = {
    ["T"] = "The Deathstalkers",
    ["O"] = "Bring Andron\'s Ledger to Mennet Carkad in the Rogues\' Quarter of Undercity.",
    ["D"] = "You\'ll find the ledger with the information your employers seek on the bookshelf behind me. Deliver it with my regards, and tell them that I would prefer it if they sent you, if they find enough value that they should wish to continue our arrangement.",
  },
  [1918] = {
    ["T"] = "The Befouled Element",
    ["O"] = "Bring the Befouled Water Globe to Mastok Wrilehiss at Splintertree Post, Ashenvale.",
    ["D"] = "The slain elemental has produced an intensely glowing globe of water.  The contents of the globe are, at best, disgusting.  It would be safe to assume that whatever has befouled the water of Mystral Lake is most likely present in the water of the globe.$B$BIt\'s not exactly common knowledge who might be interested in such a find; perhaps the person who originally sent you out to the lake, Mastok Wrilehiss at Splintertree Post, might have an idea.",
  },
  [1919] = {
    ["T"] = "Report to Jennea",
    ["O"] = "Speak with Jennea Cannon in Stormwind.",
    ["D"] = "Your knowledge of the craft grows, young $N.  Are you now ready to begin your true lessons?  Are you ready to take your place in the secret war we wage?$B$BIf so, then speak with Jennea Cannon, in the wizard\'s sanctum in the mage district of Stormwind.  She has a task for you which will open your first window into the invisible world.",
  },
  [1920] = {
    ["T"] = "Investigate the Blue Recluse",
    ["O"] = "Obtain a Cantation of Manifestation and a Chest of Containment coffers from behind Jennea Cannon.  Bring 3 Filled Containment Coffers to Jennea at the Wizard\'s Sanctum.",
    ["D"] = "A disturbance has been reported at the Blue Recluse tavern in the Mage Quarter.  Strange, invisible creatures now wander the tavern, and I fear a mana rift has formed there.$B$BI want you to investigate the tavern and capture those creatures.  Use the cantation of manifestation in the tavern to make the creatures visible, subdue them then use a containment coffer to capture them.  When you have enough, return to me with your filled coffers, your empty coffers and the cantation.",
  },
  [1921] = {
    ["T"] = "Gathering Materials",
    ["O"] = "Bring 10 Linen Cloth and the 6 Charged Rift Gems to Wynne Larson in Stormwind.",
    ["D"] = "You have served your fellow mages well, and now it is time to don your wizard\'s robe.  Wynne Larson can make your robe, but you must bring her the needed materials  $B$BThe robe will be made of linen which is easy to find, but you must also gain charged rift gems, and the only known location of the gems is in the Silver Stream Mine of Loch Modan, left in crates by the dwarven miners\' league.$B$BGather the linen and the gems and bring them to Wynne Larson at the Larson Clothiers in the Mage District.",
  },
  [1938] = {
    ["T"] = "Ur\'s Treatise on Shadow Magic",
    ["O"] = "Bring Ur\'s Treatise on Shadow Magic to High Sorcerer Andromath in Stormwind.",
    ["D"] = "Long ago, three mages studied in this tower until one fell to the dark arts.  He was urged to turn away from that evil magic, but he could not resist.  And so he was banished.$B$BThe fallen mage\'s new domain is the tower of Ilgalar in Redridge, and he took with him a powerful book--Ur\'s Treatise on Shadow Magic.$B$BWe, the mages of Stormwind, would have that book returned.  Enter the tower of Ilgalar, find the tome in his library, and bring it to me.",
  },
  [1939] = {
    ["T"] = "High Sorcerer Andromath",
    ["O"] = "Speak with High Sorcerer Andromath.",
    ["D"] = "High Sorcerer Andromath wishes to speak with you, $N.  He is at the top of the wizard\'s sanctum, in the mage\'s district of Stormwind.$B$BIt would be wise to speak with him, for Andromath is one of the most powerful mages not locked away in Dalaran.  Council with him is high council indeed.",
  },
  [1940] = {
    ["T"] = "Pristine Spider Silk",
    ["O"] = "Bring 8 Pristine Spider Silk to Wynne Larson in Stormwind.",
    ["D"] = "Your service has been unwavering, $N.  I think, perhaps, it is time you had a finer garment, one more suiting your station as an adept mage.  If you gather the needed materials, then we will arrange the robe made.$B$BYou must enter Duskwood and hunt carrion recluse spiders.  Extract their silk, but only the finest, most pristine silk will do.  When you have enough, take it to Wynne Larson, here in the mage district at the Larson Clothiers.  She will craft your robe.",
  },
  [1941] = {
    ["T"] = "Manaweave Robe",
    ["O"] = "",
    ["D"] = "",
  },
  [1942] = {
    ["T"] = "Astral Knot Garment",
    ["O"] = "",
    ["D"] = "",
  },
  [1943] = {
    ["T"] = "Speak with Deino",
    ["O"] = "Speak with Deino.",
    ["D"] = "The troll mage Deino has need of your assistance, $N.  She resides in Orgrimmar, in the Darkbriar Lodge.",
  },
  [1944] = {
    ["T"] = "Waters of Xavian",
    ["O"] = "Bring the Xavian Water Sample to Deino in Orgrimmar.",
    ["D"] = "The fabled city of Xavian lies nestled deep within eastern Ashenvale, and tales of its ancient knowledge are well known among the learned.  It is said that the waters of Xavian hold vast magical powers.$B$BI would have a sample of those waters, from their source.$B$BTake this flask and go to Xavian.  Gather water from the bottom of its highest waterfall, and return to me.  And use care as you move through that ancient city--satyrs now hold the ruins and do not like mages prying into their affairs.",
  },
  [1945] = {
    ["T"] = "Laughing Sisters",
    ["O"] = "Bring 12 Laughing Sister\'s Hairs to Kil\'hala at the Crossroads.",
    ["D"] = "For your services, I have arranged for the creation of your new robes, provided you can gather the needed materials...$B$BFor these robes, you must return to Ashenvale for the locks of hair from laughing sisters--dryads who gather near the Raynewood Retreat.$B$BWhen you have their hair, bring it to Kil\'hala, a troll tailor at the Crossroads in the Barrens.  He will be expecting you, and will fashion your robes.",
  },
  [1946] = {
    ["T"] = "Nether-lace Garment",
    ["O"] = "",
    ["D"] = "",
  },
  [1947] = {
    ["T"] = "Journey to the Marsh",
    ["O"] = "Speak with Tabetha.",
    ["D"] = "Now it is time for you to earn your mage\'s wand.  To begin this quest, speak with the human hermit Tabetha in Dustwallow Marsh.  You will find her shack deep in the marsh, west of Theramore.$B$BSpeak with her, for her knowledge is vast.  You will find Tabetha\'s cottage west of Theramore, and just north of the Stonemaul Ruins.",
  },
  [1948] = {
    ["T"] = "Items of Power",
    ["O"] = "Bring 1 Jade and the Bolt Charged Bramble to Tabetha in Dustwallow Marsh.",
    ["D"] = "The building of a mage\'s wand is not easy.  Rare substances are required, and a vessel must be made that can store great quantities of magical energy.  You must gather these things and bring them to me.$B$BI have written onto this parchment that which you need, and instructions on how to get it.  Bring me exactly what is on this list, and follow my written directions to the letter, for if you do not then when we make your wand things might go... badly.",
  },
  [1949] = {
    ["T"] = "Hidden Secrets",
    ["O"] = "Speak with Magus Tirth in the Shimmering Flats.",
    ["D"] = "To make your wand, there are key passages in a certain book that I must read.  The book is called Rituals of Power, written by the Magus Tirth.$B$BI do not have a copy of his book, and none of the world\'s great libraries possess it.  So you must speak with Tirth and gain it from him.$B$BGood luck.  Tirth used to be a great mage and scholar, but now he spends his time at the gnome and goblin races in the Shimmering Flats, drinking and gambling.",
  },
  [1950] = {
    ["T"] = "Get the Scoop",
    ["O"] = "Find the phrase to Tirth\'s strongbox, then return to Tirth.",
    ["D"] = "I can help you with the book, but first you have to help me!$B$BThe tome you seek is in my magically locked strongbox.  To open it a special phrase must be uttered, and I can\'t remember the phrase!  Normally my assistant helps me remember these things but he\'s nowhere to be found.$B$BIf you want my book, then find my apprentice!  Ask around the race track--if you\'re lucky then someone has seen him.  Either drag him back here, or at least get my magic phrase from him so we can open my box!",
  },
  [1951] = {
    ["T"] = "Rituals of Power",
    ["O"] = "Bring the book Rituals of Power to Tabetha in Dustwallow Marsh.",
    ["D"] = "Hm... now that I think about it, I don\'t have my book!  You see, I had some gambling debts and to pay them off I sold the last copy of Rituals of Power to a member of the clergy....$B$BA member of the Scarlet Brotherhood.  He must have taken it to their library in the Scarlet Monastery in Tirisfal Glades!$B$BIf you want that book, then you\'ll have to go to the monastery to find it.  And sorry about all the trouble.  I\'m not myself these days...",
  },
  [1952] = {
    ["T"] = "Mage\'s Wand",
    ["O"] = "Speak with Tabetha after her ritual.",
    ["D"] = "I\'ll go make the wands now.",
  },
  [1953] = {
    ["T"] = "Return to the Marsh",
    ["O"] = "Speak with Tabetha.",
    ["D"] = "It is time, $N, for you to speak with Tabetha... the hermit of Dustwallow Marsh.  She will instruct you on the creation of a powerful mage\'s implement.$B$BTabetha\'s shack lies deep in Dustwallow Marsh, west of Theramore.",
  },
  [1954] = {
    ["T"] = "The Infernal Orb",
    ["O"] = "Bring an Infernal Orb to Tabetha in Dustwallow Marsh.",
    ["D"] = "The first step is to acquire an orb with power.  The warlocks of the Burning Blade have nice, high quality orbs... it\'s too bad they\'ve bound demons into them!  But we can fix that, yes we can.$B$BGo to Desolace and snatch an infernal orb from a Burning Blade summoner.  You\'ll find summoners at the Mannoroc Coven.$B$BWhen you have the orb, return to me.",
  },
  [1955] = {
    ["T"] = "The Exorcism",
    ["O"] = "Kill the Demon of the Orb, then speak with Tabetha.",
    ["D"] = "Now that we have a summoner\'s orb, we must exorcise the demon within it.  I can do that.  That\'s the easy part.$B$BYou\'re the one who has the hard job.  You\'re the one who has to kill the demon once it\'s out.$B$BAnd you\'ll have to be quick.  If you take too long then he\'ll jump back in the orb and we\'ll have to start over!$B$BSo get yourself ready.  Prepare your strongest spells and keep your potions handy, and go get a friend if you have one.  Because this isn\'t going to be pretty.",
  },
  [1956] = {
    ["T"] = "Power in Uldaman",
    ["O"] = "Retrieve an Obsidian Power Source and bring it to Tabetha in Dustwallow Marsh.",
    ["D"] = "Now that the demon has been removed, we must gain a power source.  Unfortunately, the best power sources are usually the oldest and the hardest to get.  It\'s neat how fate likes to take a bite out of you sometimes, isn\'t it?$B$BSo you\'re going to need to go to Uldaman and defeat the obsidian sentinel there.  He\'s big, he\'s tough, and his power source packs a punch!$B$BI wish you the best of luck, $N.  And if you don\'t get lucky then, well... it was nice knowing you.",
  },
  [1957] = {
    ["T"] = "Mana Surges",
    ["O"] = "Kill 12 Mana Surges in the allowed time limit.",
    ["D"] = "We are almost finished, but this last step is the most perilous, $N.$B$BI have the empty summoner\'s orb and am ready to empower it.  We will do this by harnessing the energy of a mana rift.  I\'ll cast a spell that will temporarily open a rift, from which creatures, mana surges, will pour out.  You must defeat the mana surges and their power will be released, which I will then collect.$B$BKill enough mana surges before the rift closes and I will place all their collected energy in your orb.",
  },
  [1958] = {
    ["T"] = "Celestial Power",
    ["O"] = "",
    ["D"] = "",
  },
  [1959] = {
    ["T"] = "Report to Anastasia",
    ["O"] = "Speak with Anastasia Hartwell.",
    ["D"] = "The mages of the Undercity have noted a magical disturbance within the city, and have sent word for mages of the Horde to investigate.$B$BAnastasia Hartwell can tell you more.  Speak with her in the Magic Quarter in the Undercity.",
  },
  [1960] = {
    ["T"] = "Investigate the Alchemist Shop",
    ["O"] = "Obtain a Cantation of Manifestation and a Chest of Containment Coffers from behind Anastasia Hartwell.  Bring 3 Filled Containment Coffers, the Chest of Containment Coffers and the Cantation of Manifestation to Anastasia in the Undercity.",
    ["D"] = "Algernon\'s alchemist shop in the Apothecarium is the locus of a strange disturbance.  Invisible creatures, made solely of magic, wander the shop--there must be a reason, and we must find it!$B$BInvestigate the disturbance and capture those creatures.  Take a chest of coffers and a scroll from behind me.  Use the scroll at the shop to make the creatures visible, subdue them with your spells, then throw down a coffer to capture them.  Return with the filled coffers, the empty coffers and the cantation.",
  },
  [1961] = {
    ["T"] = "Gathering Materials",
    ["O"] = "Bring 10 Linen Cloth and 6 Dalaran Mana Gems to Josef Gregorian.",
    ["D"] = "It is time for you to don your first mage\'s robes, $N.  They are a sign of your station, and a testament to your worth.$B$BTo gain the robes, you must gather the mana gems from the Dalaran mages of Silverpine.  Their beliefs are similar to ours and so killing them is somewhat distasteful, but if they lack the strength to resist us, then they do not deserve their lives.$B$BBring the gems and a supply of linen to Josef Gregorian at the Magic Quarter\'s tailor shop.",
  },
  [1962] = {
    ["T"] = "Spellfire Robes",
    ["O"] = "",
    ["D"] = "",
  },
  [1963] = {
    ["T"] = "The Shattered Hand",
    ["O"] = "Kill Tazan and bring his Satchel to Therzok in the Cleft of Shadow in Orgrimmar.",
    ["D"] = "The Warchief is too subtle in his methods. He is cautious to unleash the might of the Horde. Once we were stronger, mighty conquerors all. That pride we must regain, and the Shattered Hand will do so.$b$bWe build strength from the shadows and strike at the exposed, secretly laying our enemies low. A task you must complete for me, to prove your worth.$b$bThe troll Tazan trades with the pirates near Ratchet. He has information I must have. Kill him and bring me his satchel.",
  },
  [1978] = {
    ["T"] = "The Deathstalkers",
    ["O"] = "Deliver Andron\'s Ledger to Varimathras in the Hall of the Dark Lady in the Undercity.",
    ["D"] = "The Syndicate? I would never have suspected. It seems the lords of Alterac have fallen far in the years since their betrayal.$b$bLord Varimathras will be pleased to learn of this, and as you were so instrumental in this discovery, it would be fitting if you were the one to deliver it to him.$b$bAh, and how could I forget?$b$bWelcome to the Deathstalkers.",
  },
  [1998] = {
    ["T"] = "Fenwick Thatros",
    ["O"] = "Kill Fenwick Thatros and bring his head back to Mennet Carkad in the Rogues\' Quarter of the Undercity.",
    ["D"] = "Lord Varimathras has just sent me a missive about a threat to the Forsaken that must be immediately removed. His name is Fenwick Thatros, one whom Lord Varimathras believes is a leader of the undead that have taken root in Silverpine Forest.$b$bYour name came to mind immediately, $n. I have faith enough in your abilities that you will carry out this mission flawlessly.$b$bThe latest reports from the Deathstalkers in Silverpine indicate that Thatros can be found near the decrepit dock on Lordamere.",
  },
  [1999] = {
    ["T"] = "Tools of the Trade",
    ["O"] = "Find the Dalaran Status Report and return it to Mennet Carkad in the Rogues\' Quarter of the Undercity.",
    ["D"] = "The wizards of the Kirin Tor at Ambermill have stored away important documents pertaining to the current state of Dalaran. This we know from the admissions of one of our prisoners. Lord Varimathras has placed the duty of recovering them in the hands of the Deathstalkers.$b$bThis task falls to you, $n. You will need some equipment, however, and the knowledge of lockpicking. Speak with Estelle Gendry, our outfitter, and ask her for a set of tools before you set out.",
  },
  [2038] = {
    ["T"] = "Bingles\' Missing Supplies",
    ["O"] = "Find and return Bingles\' supplies:$B$BBingles\' Wrench, Bingles\' Screwdriver, Bingles\' Hammer, and Bingles\' Blastencapper.",
    ["D"] = "As if crashing into the Loch wasn\'t bad enough, troggs from the nearby island attacked me and stole my tools and special Blastenheimer Blastencapper explosives. With no gyrocopter and no explosives, the movement has no chance!$B$BGet my tools back and find my Blastencapper explosives, $N. For Gnomeregan! ",
  },
  [2039] = {
    ["T"] = "Find Bingles",
    ["O"] = "Find Bingles Blastenheimer in Loch Modan.",
    ["D"] = "We\'ve lost contact with Bingles! He was sent on a reconnaissance mission over Gnomeregan and hasn\'t reported back in over a week. I just know he\'s gone and blown himself up.$B$BHe was last seen flying over the Loch. Perhaps you can investigate, $N.",
  },
  [2040] = {
    ["T"] = "Underground Assault",
    ["O"] = "Retrieve the Gnoam Sprecklesprocket from the Deadmines and return it to Shoni the Shilent in Stormwind.",
    ["D"] = "Gnomeregan has fallen under the control of those dastardly troggs! The situation is grave but perhaps you can help, $N.$B$BDeep in the Deadmines is a functional goblin shredder. Find that shredder and bring back the intact power supply. With the shredder\'s power supply, we can give our gyrodrillmatic excavationators the power they need to break through the rocky underground borders of Gnomeregan, opening the way for a gnomish assault!",
  },
  [2041] = {
    ["T"] = "Speak with Shoni",
    ["O"] = "Speak with Shoni the Shilent in Stormwind.",
    ["D"] = "Perhaps... Perhaps you can help us in the battle for Gnomeregan.$B$BIn Stormwind you will find the commander of our underground assault crew, Shoni the Shilent. Shoni needs assistance with her gyrodrillmatic excavationators.$B$BYou will probably find her amongst the dwarves in Stormwind.$B$BGood luck, $N. ",
  },
  [2078] = {
    ["T"] = "Gyromast\'s Revenge",
    ["O"] = "Find Gelkak\'s First Mate, the Threshwackonator 4100, and lead it back to Gelkak.",
    ["D"] = "Landlubber, ye needs to find me first mate.$B$BTake this\'n key, puts it in his gulliver an\' bring him back here to serve the Cap\'n.$B$BThem murlocs an\' their chief ain\'t gonna be too happy once me first mate gets a hold of em!",
  },
  [2098] = {
    ["T"] = "Gyromast\'s Retrieval",
    ["O"] = "Find and return the three pieces of Gelkak\'s Key to Gelkak Gyromast.",
    ["D"] = "A pirate\'s life ain\'t an easy one, landlubber. It ain\'t all about plunderin\' and booze! Alas, me ship be sunk just off shore thanks to them pesky Threshadons.$B$BWhat I be needin\' is me first mate back in workin\' order!$B$BWhen I washed up\'n shore, the key for me first mate broke on the rocks and was pilfered by the beasties of Darkshore. Out of the corner of me eyes I saw some of them Ragin\' Crawlers, Ferocious Foreststrider, and murloc rummaging through the wreckage....",
  },
  [2118] = {
    ["T"] = "Plagued Lands",
    ["O"] = "Capture a living Rabid Thistle Bear and bring it back to Tharnariun.$B$BShould you fail to capture a Rabid Thistle Bear and lose your trap, return to Tharnariun Treetender and request another trap.",
    ["D"] = "A sickness spreads across the Darkshore, gripping the minds and bodies of all it touches.$B$BThe Thistle Bears have been hit the hardest by this plague. The once noble beasts are now an instrument of destruction - rabid and frenzied. I may have a cure for this ailment.$B$BTake this trap to the forest and lay it on the ground. Any Rabid Thistle Bear that crosses the light shall become docile for a short time. Once the bear is docile, it shall follow you. Lead it back here, $N.",
  },
  [2138] = {
    ["T"] = "Cleansing of the Infected",
    ["O"] = "Cleanse the forest of 20 Rabid Thistle Bears and return to Tharnariun Treetender in Darkshore.",
    ["D"] = "As I had feared, the cure does not work.$B$BIt saddens me greatly to order the death of any of nature\'s beasts, but the sick and tainted thistle bears must be put down.$B$BReturn to the forest and destroy 20 rabid thistle bears, $N. It will most certainly not put a stop to the plague but it will slow down the damage the animals are doing to our forest and its inhabitants for a short time.",
  },
  [2139] = {
    ["T"] = "Tharnariun\'s Hope",
    ["O"] = "Find and kill the Den Mother.",
    ["D"] = "The survival of our people and lands comes at unfortunate costs, $N.$B$BYou have spilled the blood of nature\'s servants; many sick animals you have felled. The people of Auberdine are indebted to you, for the forest\'s pain is eased - even if only for a moment.$B$BI have one final task for you, but be warned, you will have to kill again. It is my hope that this will be the last time I call upon you, $N.$B$BIn a cave to the northeast, near Bashal\'Aran, resides the Den Mother. Slay her.",
  },
  [2158] = {
    ["T"] = "Rest and Relaxation",
    ["O"] = "Speak with Innkeeper Farley at the Lion\'s Pride Inn.",
    ["D"] = "Every adventurer should rest when exhaustion sets in - and there is no finer place to get rest and relaxation than at the Lion\'s Pride Inn!$B$BMy best friend, Innkeeper Farley, runs the Lion\'s Pride. If you tell him I sent you, he may give you the special discounted rates on food and drink.$B$BTo find the Lion\'s Pride Inn, travel south along the road from here -- you can\'t miss it!",
  },
  [2159] = {
    ["T"] = "Dolanaar Delivery",
    ["O"] = "Bring the Dolanaar Delivery to Innkeeper Keldamyr.",
    ["D"] = "Greetings, young $c.  Can you offer me aid?  I have a package of herbs that I must deliver to the town of Dolanaar.  But I still have business with the druids of Shadowglen and cannot yet leave.$B$BCan you deliver this package for me?  It must be sent to Innkeeper Keldamyr, at the Dolanaar inn.  It lies along the road, to the south.",
  },
  [2160] = {
    ["T"] = "Supplies to Tannok",
    ["O"] = "Deliver the Crate of Inn Supplies to Tannok Frosthammer in Kharanos.",
    ["D"] = "Hey! You look like a hearty adventurer. If you\'re planning on braving the pass, do you suppose you could bring a package to the inn in Kharanos?$b$bYou were planning on stopping at the inn, right? If you make it through the pass, you\'ll definitely want to take a breather there.$b$bAnyways, bring this to Tannok Frosthammer, the innkeeper\'s assistant. I can\'t get through the pass myself, and it\'ll be days until a Mountaineer escort comes through!",
  },
  [2161] = {
    ["T"] = "A Peon\'s Burden",
    ["O"] = "Bring Ukor\'s Burden to Innkeeper Grosk in Razor Hill.",
    ["D"] = "Excuse me?  Can you help me?$B$BI have a load of food here.  I walked along the road and braved scorpids and spiders and worse!  I brought the food to the Valley of Trials because I thought they needed it and I like to help out.  But they don\'t need any food!  Now I have to take it back to Razor Hill and I\'m afraid of all the beasts along the way...$B$BCan you take it for me?  I\'m just a peon, but you\'re a hero.  You fear nothing!$B$BTake it to Innkeeper Grosk at the Razor Hill inn.  Thank you!",
  },
  [2178] = {
    ["T"] = "Easy Strider Living",
    ["O"] = "Bring back 5 Strider Meat to Alanndarian Nightsong in Auberdine.",
    ["D"] = "Do you smell that delicious aroma? It\'s my secret recipe, $N--fresh strider stew. Delicious!$B$BPerhaps you would be interested in learning the recipe?$B$BBefore I teach you the recipe, you will need to prove that you can handle the beasts.$B$BNow, I don\'t expect you to go out and tame one and ride it back to me, but if you can kill a few and bring back some of their succulent meat, I may be persuaded to show you the ways of strider stew.",
  },
  [2198] = {
    ["T"] = "The Shattered Necklace",
    ["O"] = "Search for the original creator of the shattered necklace to learn of its potential value.",
    ["D"] = "You have found a tarnished necklace, though the tarnish belies intricate and magical craftsmanship.  The necklace has been shattered, as its setting appears to be missing three gems.  On the underside of the necklace is the faint imprint of the city seal for Ironforge.  Below the seal are three tiny, engraved letters: \"TdK\".$B$BIf there is anything to be gleaned from the necklace, you will need to learn about its origins.  Perhaps the seal and the initials are a start...",
  },
  [2199] = {
    ["T"] = "Lore for a Price",
    ["O"] = "Bring five silver bars to Talvash del Kissel in Ironforge.",
    ["D"] = "I am a jeweler of great renown here in Ironforge!  Well, former renown anyway... I have fallen onto hard times due to the chicanery of a competitor!  A bad reputation in this business spells financial disaster.  As a result, I find myself without a copper to my good-though-currently-sullied name.$B$BI want you to get me five silver bars to finish an order I received - the first in what seems like an eternity.$B$BYou get me those five silver bars, and I\'ll answer your questions on the necklace.",
  },
  [2200] = {
    ["T"] = "Back to Uldaman",
    ["O"] = "Search for clues as to the current disposition of Talvash\'s necklace within Uldaman.  The slain paladin he mentioned was the person who had it last.",
    ["D"] = "A competitor of mine started a rumor that my necklace cursed the paladin.  Needless to say, no one wants to buy cursed items from a jeweler.$B$B$N, I\'ll make you a deal - find the gems and I\'ll reassemble the necklace, so long as you\'ll advertise that I do NOT make cursed items!$B$BTry looking for clues in Uldaman; maybe see if the paladin\'s body is down there, since he\'s the one who we know had it last!  When you find the gems you should use this; it\'ll allow you to contact me here in Ironforge.",
  },
  [2201] = {
    ["T"] = "Find the Gems",
    ["O"] = "Find the ruby, sapphire, and topaz that are scattered throughout Uldaman.  Once acquired, contact Talvash del Kissel remotely by using the Phial of Scrying he previously gave you.$B$BFrom the journal, you know...$B* The ruby has been stashed in a barricaded Shadowforge area.$B* The topaz has been hidden in an urn in one of the Trogg areas, near some Alliance dwarves.$B$B* The sapphire has been claimed by Grimlok, the trogg leader.",
    ["D"] = "The paladin\'s journal has provided perhaps the only clues that Uldaman will yield as to the location of the necklace\'s gems.  If the paladin\'s scribing remains accurate, then the Shadowforge dwarves and the troggs that vie for control of the dig site will need to be dealt with.$B$BArmed with this knowledge, the search for the gems begins in earnest.  When all the gems have been acquired, Talvash in Ironforge should be notified via the Phial of Scrying as per his instructions.",
  },
  [2202] = {
    ["T"] = "Uldaman Reagent Run",
    ["O"] = "Bring 12 Magenta Fungus Caps to Jarkal Mossmeld in Kargath.",
    ["D"] = "I\'ve heard about a newly discovered plant called the magenta fungus caps.  They grow in clusters at the Uldaman dig site and can be found in the early tunnels and deep within the less-explored areas.  I want to study their potential use in alchemy, and that\'s where you come in.$B$BFind the magenta cap clusters and bring me a dozen caps.  Be warned, the clusters may spew out poison spores if jostled.$B$BDo this for me, and I\'ll whip up a batch of one of my famous restorative elixirs for you!",
  },
  [2203] = {
    ["T"] = "Badlands Reagent Run II",
    ["O"] = "Use the empty thaumaturgy vessels on scorched guardian dragons found in the Badlands.  Once you have them filled, bring them to Jarkal Mossmeld in Kargath.",
    ["D"] = "You have been most helpful to me, $c.  I would reward you with the very recipe for the brew I gave you earlier... but I would ask of you a task that is quite dangerous.  Hear my request.$B$BThis lockbox contains three empty thaumaturgy vessels; they are imbued with an attunement aura that will drain the blood of a scorched guardian dragon.  Using one on the beast will anger it greatly, so be warned.  Once you have all three filled, bring them to me.$B$BDo this, and the recipe will be yours.",
  },
  [2204] = {
    ["T"] = "Restoring the Necklace",
    ["O"] = "Obtain a power source from the most powerful construct you can find in Uldaman, and deliver it to Talvash del Kissel in Ironforge.",
    ["D"] = "You are going to need to find some \"oomph\" for the necklace, and Uldaman is probably the best place for it, actually.  I guess there are a lot of constructs running around in there, yeah?  Well, take out the biggest, baddest construct in that place and grab its power source!  I am pretty sure I can fuse that source into the necklace.$B$BWithout that power source, fixing the necklace will be impossible.  Come back to me here to Ironforge when you have it, and we\'ll get it all fixed up for you!",
  },
  [2205] = {
    ["T"] = "Seek out SI: 7",
    ["O"] = "Deliver Keryn Sylvius\'s package to Master Mathias Shaw at the Stormwind Barracks.",
    ["D"] = "The badge? Oh, it\'s nothing, fish - nothing you could handle anyhow. By the looks of you, I\'d say Mathias is likely to mistake you for the cheese delivery $g boy:girl; instead of a proper rogue. Regardless, I\'ve been assigned a job and I\'ve made a promise to deliver on that job.$B$BSpeaking of which, deliver this package to Mathias and he may give you a small task or two.$B$BGet cracking. ",
  },
  [2206] = {
    ["T"] = "Snatch and Grab",
    ["O"] = "Find the Defias Dockmaster and recover the Shipping Schedule for Master Mathias Shaw.",
    ["D"] = "Our operatives in the field are reporting heavy Defias activity south of Goldshire. The reports indicate that the Defias have taken over Jerod\'s Landing and are now using it as a way to smuggle large quantities of contraband from Redridge to Westfall.$B$BI need you to infiltrate the dock house and get me that shipping schedule, $N. You will probably find it buried deep in the pockets of the Dockmaster.$B$B...and $N, should you be discovered, SI:7 will disavow all knowledge of your existence.",
  },
  [2218] = {
    ["T"] = "Road to Salvation",
    ["O"] = "Speak with Hulfdan Blackbeard in Ironforge.",
    ["D"] = "Another lost soul looking for guidance, eh? I suppose you want to find a purpose - your purpose. I\'ve seen it a thousand times, another hero looking for work.$B$BWell, today\'s your lucky day, friend. Hogral\'s got the cure for your ailment.$B$BHere\'s what you do: Take this road here up to Ironforge and make your way to the Forlorn Cavern. Once there, locate the thieves\' guild and speak with Hulfdan Blackbeard. He\'ll put you on the road to salvation.",
  },
  [2238] = {
    ["T"] = "Simple Subterfugin\'",
    ["O"] = "Find Onin MacHammar by Gnomeregan.",
    ["D"] = "Ye see, the King be strip mining the quarry fer ore and gems. More riches than any one dwarf should see in ten lifetimes.$B$BThe Hidden Circle has values, $N. We value valuables. Can ye understand?$B$BNow we feels the King should be sharing some of that booty, but we don\'t want ol\' \'Stinkbeard\' to know he\'s sharin\' - if ye catch me drift.$B$BWe needs ye to speak with Onin MacHammar over by Gnomeregan and bring back a report. The crafty dwarf has hidden hisself amongst the lepers. Be careful.",
  },
  [2239] = {
    ["T"] = "Onin\'s Report",
    ["O"] = "Take Onin\'s Report back to Hulfdan in Ironforge.",
    ["D"] = "The Quarry? Yap, I see those monstrous armored steam tanks come by this post near every day.$B$BMy contacts to the east tell me that the tank starts off at Ironforge, makes a pickup at the Gol\'Bolar Quarry, stops over at Kharanos to refuel and then for some blasted reason, it takes the long way around past Gnomeregan, before reaching Ironforge to drop off the shipment.$B$BIt\'s all in the report, $r. Take it back to Hulfdan so he can plan the next step.",
  },
  [2240] = {
    ["T"] = "The Hidden Chamber",
    ["O"] = "Read Baelog\'s Journal, explore the hidden chamber, then report to Prospector Stormpike.",
    ["D"] = "Now for the task at hand.$B$BThere is a secret chamber, the Chamber of Khaz\'mul, that must be explored.  The problem is that the chamber\'s behind a huge, locked door!  It\'s a lucky thing I know how to open it.$B$BI\'ve described everything in my journal, here on the table.  My band and I are stuck here for now... so you, $N, must find the chamber of Khaz\'mul for us!$B$BRead my journal, explore the chamber, then report to Prospector Stormpike at the Explorers\' League in Ironforge.",
  },
  [2241] = {
    ["T"] = "The Apple Falls",
    ["O"] = "Take Jannok\'s Rose to Syurna in Darnassus.",
    ["D"] = "They say I\'m \'lovestruck,\' whatever that means.$B$BSure, I spend my every waking moment thinking about Syurna. Sure, my home is full of paintings and drawings of Syurna. Sure, I often go days without eating, sleeping, or drinking, while I lament about the love we could have had. Is that so wrong?$B$BNow she won\'t see me! ME! I\'ve tried sending her messages but she won\'t even talk to another person unless they are a rogue.$B$BCould you deliver this flower to her? Don\'t forget to tell her it\'s from Jannok.",
  },
  [2242] = {
    ["T"] = "Destiny Calls",
    ["O"] = "Find Sethir the Ancient and bring back any clues that you may discover to Syurna.",
    ["D"] = "We believe there is one being that may be able to help us understand what is happening to the forests of Teldrassil. Unfortunately, getting the creature to cooperate has proven disastrous.$B$BHe hides on a branch at the outskirts of the Oracle Glade. All who have so far approached the satyr, whether through force or diplomacy, have met with doom. Perhaps stealth is the answer, $N.$B$BFind what Sethir the Ancient carries in his packs and report back to me.",
  },
  [2258] = {
    ["T"] = "Badlands Reagent Run",
    ["O"] = "Bring 5 Buzzard Gizzards, 10 Crag Coyote Fangs, and  5 Rock Elemental Shards to Jarkal Mossmeld in Kargath, Badlands.",
    ["D"] = "$N, I could use your aid if you\'re up for it.  I am an alchemist of some note, and I seek your aid in acquiring some reagents found out in the Badlands.$B$BI need the following items to replenish my stocks: five buzzard gizzards, ten crag coyote fangs, and five rock elemental shards.  Obviously, you can get them off of the beasties themselves; if you\'re not the fighting type, then find friends who are.$B$BAcquire these items for me, you\'ll get some coin out of the deal.  Watcha wanna do?",
  },
  [2259] = {
    ["T"] = "Erion Shadewhisper",
    ["O"] = "Contact Erion Shadewhisper in Darnassus.",
    ["D"] = "Do not worry, $N, I am not sending you to see Syurna. Erion requires your presence at the Cenarion Enclave. Make haste!",
  },
  [2260] = {
    ["T"] = "Erion\'s Behest",
    ["O"] = "Travel to Stormwind City and seek council with Renzik \"The Shiv.\"",
    ["D"] = "Word of your deeds has spread far and wide, $N. So far, in fact, that Stormwind Intelligence has asked for you by name.$B$BThe journey is taxing and not without peril, but an opportunity to train under SI:7 should not be taken lightly.$B$BA new world awaits you, $N. Make your way to Old Town in Stormwind and speak with Renzik \"The Shiv\" at SI:7 headquarters.",
  },
  [2278] = {
    ["T"] = "The Platinum Discs",
    ["O"] = "Speak with stone watcher and learn what ancient lore it keeps.  Once you have learned what lore it has to offer, activate the Discs of Norgannon.",
    ["D"] = "The centerpieces of this treasure chamber are four huge platinum discs, each adorned with numerous sigils of an unknown origin.  Touching them makes your hand tingle.  Just after touching the discs, the visage of a large stone watcher springs forth.$B$B\"Salutations.  I am a keeper of knowledge.  The Creators\' synthesis of the Earthen has been chronicled and stored here for this world in the Discs of Norgannon.\"$B$BYou have uncovered a wealth of previously undiscovered worldly lore!",
  },
  [2279] = {
    ["T"] = "The Platinum Discs",
    ["O"] = "Take the miniature version of the Discs of Norgannon to the Explorers\' League in Ironforge.",
    ["D"] = "Touching the Discs of Norgannon once again makes your hand tingle.  This time, however, a strange grinding noise is heard from inside the discs.$B$BYour backpack suddenly becomes heavier than it was before.  Inside, you discover a miniature version of the discs!$B$BNo doubt, an item of this sort would be invaluable for someone interested in the ancient history of Azeroth.  The Explorers\' League in Ironforge is such a group, known throughout the Alliance; perhaps they can make good use of it.",
  },
  [2280] = {
    ["T"] = "The Platinum Discs",
    ["O"] = "Take the miniature version of the Discs of Norgannon to the one of the sages in Thunder Bluff.",
    ["D"] = "Touching the Discs of Norgannon once again makes your hand tingle.  This time, however, a strange grinding noise is heard from inside the discs.$B$BYour backpack suddenly becomes heavier than it was before.  Inside, you discover a miniature version of the discs!$B$BNo doubt, an item of this sort would be invaluable for someone interested in the ancient history of Azeroth.  The sages of Thunder Bluff are such historians, known throughout the Horde; perhaps they can make good use of it.",
  },
  [2281] = {
    ["T"] = "Redridge Rendezvous",
    ["O"] = "Venture forth to Lakeshire in the Redridge Mountains and speak with Lucius.",
    ["D"] = "Sending you out in the field now would be suicide. Renzik doesn\'t need another dead rogue on his hands. Don\'t fret, though, $N - Renzik is going to get you up to speed.$B$BMake your way to the Redridge Mountains and speak with an associate of SI:7 named Lucius. You will find him hanging around the docks of Lakeshire with the other shady good-for-nothings.",
  },
  [2282] = {
    ["T"] = "Alther\'s Mill",
    ["O"] = "Open Lucius\'s Lockbox, recover the Token of Thievery and return it to Lucius in Lakeshire.",
    ["D"] = "We\'ve been training thieves here for years! After all, not every rapscallion is born with the grace and finesse of old Lucius.$B$BUp off the road here you\'ll find Alther\'s Mill. I got a chest up there with something important inside. Go get it for me! Bring back that token and you\'ll receive your Certificate of Thievery!",
  },
  [2283] = {
    ["T"] = "Necklace Recovery",
    ["O"] = "Look for a valuable necklace within the Uldaman dig site and bring it back to Dran Droffers in Orgrimmar.  The necklace may be damaged.",
    ["D"] = "We\'ve got work for you, that is if you\'ve got the guts to do it!$B$BDeep in the Badlands is the Uldaman dig site.  We\'ve heard a tale about some idiot paladin who died there recently.  Why we even care is that he had a very valuable necklace on him.  It\'s supposedly got three large gems: a ruby, a sapphire, and a topaz.$B$BThe shot: we want it, and we\'re payin\' TOP SILVER to get it.  It may be damaged, but that\'s neither here nor there; we\'ll fix whatever scratches are on it.",
  },
  [2284] = {
    ["T"] = "Necklace Recovery, Take 2",
    ["O"] = "Find a clue as to the gems\' whereabouts in the depths of Uldaman.",
    ["D"] = "As sure as Malton is a dummy, those gems are still in Uldaman somewhere.  You\'re going to have to go back there and look for them.$B$BLike I said before, if the information we have is correct then there\'s a ruby, a sapphire, and a topaz that go with the necklace.  Find them and put them in the necklace.$B$BAs to where the gems are, who knows?  Maybe you\'ll get lucky and find the corpse of the Paladin.  If he\'s not been stripped clean already, then maybe he\'s got them on him still.",
  },
  [2298] = {
    ["T"] = "Kingly Shakedown",
    ["O"] = "Travel to Stormwind and seek out Renzik \"The Shiv.\"",
    ["D"] = "So there we were, about to start plottin\' out phase III when a couple o\' Magni\'s blockheads come bargin\' in here talkin\' bout some kind o\' heist and arresting some o\' the Hidden Circle.$B$BWell nobody puts the iron on old Blackbeard! The argumentations got heavy and thick and somehow yer name weres blurted out!$B$BMe advice? Take the first Gryphon out o\' Ironforge and get to Stormwind. Get yerself to Old Town and once there, talk to an old friend o\' mine named Renzik \"The Shiv.\"",
  },
  [2299] = {
    ["T"] = "To Hulfdan!",
    ["O"] = "Travel to Ironforge and speak with Hulfdan Blackbeard.",
    ["D"] = "Where have you been, $N? Hulfdan\'s been looking all over the place for you. We thought the king may have put you in irons.$B$BGet to Ironforge as soon as possible and speak with Hulfdan!",
  },
  [2300] = {
    ["T"] = "SI:7",
    ["O"] = "Travel to Stormwind and seek out Renzik \"The Shiv.\"",
    ["D"] = "Renzik was looking for you, fish.$B$BI wouldn\'t keep Mathias\'s second in command waiting if I were you.",
  },
  [2318] = {
    ["T"] = "Translating the Journal",
    ["O"] = "Find someone who can translate the paladin\'s journal.  The closest location that might have someone is Kargath, in the Badlands.",
    ["D"] = "At an impasse of sorts, you know that you will need the book translated into a language you can understand.  There certainly are a number of people in the world who could do it; the closest location that might have someone who could, however, is the Kargath outpost in the Badlands.  Perhaps that is a good place to start.",
  },
  [2338] = {
    ["T"] = "Translating the Journal",
    ["O"] = "Let Jarkal borrow the necklace.  In exchange, he will translate the journal for you.",
    ["D"] = "Droffers and Son Salvage aren\'t smart enough to restore the magic in the necklace, but I am.  All I want to do is study the necklace so I can learn to make them myself.  To do that, I will need your necklace.  Let me borrow it, and in exchange for the time I get to study it I will translate the journal for you.  While you are searching for the gems, I should have enough time to learn what I need to.$B$BIt\'s a simple deal, and no one else will be the wiser.  Whatcha wanna do?",
  },
  [2339] = {
    ["T"] = "Find the Gems and Power Source",
    ["O"] = "Recover all three gems and a power source for the necklace from Uldaman, and then bring them to Jarkal Mossmeld in Kargath.  Jarkal believes a power source might be found on the strongest construct present in Uldaman.$B$BFrom the journal, you know...$B* The ruby has been stashed in a barricaded Shadowforge area.$B* The topaz has been hidden in an urn in one of the Trogg areas, near some Alliance dwarves.$B* The sapphire has been claimed by Grimlok, the trogg leader.",
    ["D"] = "Here is the journal, translated for you to read on your own.  The crux of it says dwarves hid the ruby, the troggs hid the topaz, and a trogg named Grimlok has the sapphire.$B$BLook - you\'re going to need a power source to make the necklace work again.  Dran Droffers doesn\'t know this.  To find one, here\'s what I\'d do: go as deep as you can in Uldaman and kill whatever construct there is the strongest.  No doubt whatever that is would have it.$B$BWhen you have everything collected, come back here!",
  },
  [2340] = {
    ["T"] = "Deliver the Gems",
    ["O"] = "Deliver the Necklace and Gem Salvage to Dran Droffers in Orgrimmar.",
    ["D"] = "Any necklace setting and gem will work, so long as they are the same material.  This power source, however, is what will bring the magic back to the necklace... I hope.$B$BI\'ll gladly supply the gems needed for your version of the necklace, so long as the power source actually works.$B$BHead to Orgrimmar and keep your end of the bargain with Droffers and Son Salvage.  Just keep your mouth shut about our deal, and when you get back I might have a wonderful surprise for you.",
  },
  [2341] = {
    ["T"] = "Necklace Recovery, Take 3",
    ["O"] = "Visit Jarkal Mossmeld in Kargath to see if he was successful.",
    ["D"] = "As Dran and Malton Droffers begin to squabble over their new salvage, half of the deal is now complete.  A trip to the Badlands is now in order to finish things up, as Jarkal Mossmeld in Kargath might have a special reward waiting for you there.",
  },
  [2342] = {
    ["T"] = "Reclaimed Treasures",
    ["O"] = "Get Patrick Garrett\'s family treasure from their family chest in the South Common Hall of Uldaman, and bring it to him in the Undercity.",
    ["D"] = "Sweeping the bat pens with this broom is not befitting a Garrett!  I\'ve earned a little coin from this accursed job, and I\'m willing to spend it on you to better my station.$B$BThe Garrett family weapon of choice is locked away in our chest in the South Common Hall of Uldaman from when we Garretts toiled there as diggers.  Without it, I will be stuck here forever - literally - sweeping up after these infernal bats.$B$BReclaim my family\'s treasure and bring it to me!  Help me restore my birthright!",
  },
  [2358] = {
    ["T"] = "Horns of Nez\'ra",
    ["O"] = "Return the Horns of Nez\'ra to Arantir\'s Shadow in Lakeshire.",
    ["D"] = "Many years ago, the Horns of Nez\'ra were stolen from Ravenholdt manor. Across continents and oceans I have searched, only to discover that these insignificant beasts have had it in their possession all along.$B$BSlay every Blackrock orc you cross until the Horns of Nez\'ra are recovered. Should you recover the horns, find me in Lakeshire.$B$BDo this for me and your name shall be known to Ravenholdt, $N.",
  },
  [2359] = {
    ["T"] = "Klaven\'s Tower",
    ["O"] = "Steal the Defias Tower Key, break into the Defias Tower and uncover the contents of the Duskwood Chest. Take whatever information you find back to Mathias Shaw in Stormwind. Read Kearnen\'s Journal to gain insight about the tower.",
    ["D"] = "I\'ve been staking out this tower for weeks, $N. Recently a major shipment came through from Duskwood, and along with it came those guards.$B$BWhat are they? Your guess is as good as mine. I can tell you one thing - they aren\'t human.$B$BAnyhow, the shipment had a chest - which is what I assume those abominations are guarding. The zombie with the spikes holds a key we\'ll need.$B$BGet that key, get in the tower, and uncover whatever is in that chest. Take your findings back to Mathias.",
  },
  [2360] = {
    ["T"] = "Mathias and the Defias",
    ["O"] = "Travel to Westfall and find Agent Kearnen for your debriefing.",
    ["D"] = "Not a moment too soon, $N. SI:7 reconnaissance is reporting unusually heavy Defias activity in Westfall.$B$BIt would seem that the Defias have taken over an abandoned Alliance watch tower southeast of Sentinel Hill and are using it as a base of operations to smuggle in goods from the Redridge supply route.$B$BGet to Westfall and contact Agent Kearnen for your debriefing. You will find her hidden near the tower.$B$B...and $N, should you be discovered, SI:7 will disavow all knowledge of your existence. ",
  },
  [2361] = {
    ["T"] = "Restoring the Necklace",
    ["O"] = "",
    ["D"] = "",
  },
  [2378] = {
    ["T"] = "Find the Shattered Hand",
    ["O"] = "Journey to Orgrimmar and speak with Shenthul at the Cleft of Shadow.$B$B$B$B",
    ["D"] = "You have served the Deathstalkers well, $N, but perhaps it is time for you to venture outward. While you have shown aptitude in the ways of stealth and deception, you still require much training.$B$BThe Deathstalkers have allies within the Shattered Hand. Seek out those that would assist us - find Shenthul in Orgrimmar.",
  },
  [2379] = {
    ["T"] = "Zando\'zan",
    ["O"] = "Speak with Zando\'zan at the Cleft of Shadow.",
    ["D"] = "Your determination alone is not enough to overcome the obstacles that lay ahead of us.$B$BSpeak with Zando\'zan to begin your training as an operative of the Shattered Hand.$B$BIt is only when you have mastered the practices of stealth and thievery that you may take the next step towards your destiny.",
  },
  [2380] = {
    ["T"] = "To Orgrimmar!",
    ["O"] = "Speak with Shenthul at the Cleft of Shadow in Orgrimmar.",
    ["D"] = "A master of stealth and deception you are not, however, all of that can and will change. You\'ve obviously impressed those in charge with your meager skills, as the great Shenthul has asked for you by name.$B$BTake the road north of here to Orgrimmar and speak with Shenthul in the Cleft of Shadow.",
  },
  [2381] = {
    ["T"] = "Plundering the Plunderers",
    ["O"] = "Bring the Southsea Treasure back to Wrenix the Wretched in Ratchet. Do not forget to get an E.C.A.C. and Thieves\' Tools from Wrenix\'s Gizmotronic Apparatus. You will need both of these items to complete your mission.$B$BShould you be attacked by any unusually hostile parrots, use your E.C.A.C.!",
    ["D"] = "I don\'t mean to scare you, $N, but the last five thieves I sent to steal the southsea treasure came back to me in pieces.$B$BSouth along the coast you\'ll find Tide Razor, the southsea freebooter\'s ship. Nestled in the hold is the payoff - the southsea treasure. On the second level of that ship is the pirate\'s den, a place where they train all of their thieves.$B$BBoard that ship, steal the treasure, and bring it back to me. Use the pirate\'s den if you need to practice your thievery, but be careful!",
  },
  [2382] = {
    ["T"] = "Wrenix of Ratchet",
    ["O"] = "Travel to Ratchet and speak with Wrenix the Wretched.",
    ["D"] = "Zando\'zan has seen it! Yes, the day comes! Thrall\'s death... the dagger in his back, poison seeping through to his every vein... Zando\'zan has said too much.$B$B$N! Zando\'zan has a task for $N! $N must travel to Ratchet, east of the Crossroads, and speak with Wrenix the Wretched. Go!",
  },
  [2383] = {
    ["T"] = "Simple Parchment",
    ["O"] = "Read the Simple Parchment and speak to Frang in the Valley of Trials.",
    ["D"] = "Ah, while you were gone a parchment came for you, $N.$B$BRead it when you have time. If I\'m not mistaken, it came from the warrior trainer Frang. He would have words with you when you\'re ready. You\'ll find him outside the Den, taking cover in the shade.",
  },
  [2398] = {
    ["T"] = "The Lost Dwarves",
    ["O"] = "Find Baelog in Uldaman.",
    ["D"] = "The dig site at Uldaman wasn\'t always overrun.  We Ironforge dwarves used to control the area, but... that was before the troggs came.  They came so fast and in such numbers that not even dwarven might could stop them!$B$BIn fact, their attack was so sudden that I fear some of our people may be trapped behind enemy lines.$B$BMy friend Baelog led a small band of dwarves deep within the site just before the troggs showed up.  If he and his company live, then I want you to find them.",
  },
  [2399] = {
    ["T"] = "The Sprouted Fronds",
    ["O"] = "",
    ["D"] = "",
  },
  [2418] = {
    ["T"] = "Power Stones",
    ["O"] = "Bring 8 Dentrium Power Stones and 8 An\'Alleum Power Stones to Rigglefuzz in the Badlands.",
    ["D"] = "A while ago, I had an incident with the Shadowforge Dwarves.  They brought me to Angor Fortress to study two types of strange stones from Uldaman.  I discovered that the stones were sustained, stable power sources if treated correctly.  It\'s amazing the things one can find in that old dig site!$B$BAnyway, after I told them the secret of the stones they kicked me out of Angor Fortress and began digging for more in Uldaman.$B$BI want those stones for myself!  Bring me both types and I\'ll pay you plenty!",
  },
  [2438] = {
    ["T"] = "The Emerald Dreamcatcher",
    ["O"] = "Bring the Emerald Dreamcatcher to Tallonkai Swiftroot in Dolanaar.",
    ["D"] = "I was once given an emerald dreamcatcher from Gaerolas Talvethen, the warden of the druids in the Ban\'ethil Barrow Den. This powerful amulet is able to siphon energy from the Emerald Dream, bestowing luck upon those who carry it.$B$BSadly, I have not been able to retrieve it from my dresser in Starbreeze Village... Although Starbreeze was once a tranquil place, it has now succumbed to the corruption of the furbolg that reside there.$B$BPerhaps you would be willing to recover my dreamcatcher, $c? ",
  },
  [2439] = {
    ["T"] = "The Platinum Discs",
    ["O"] = "Take your reward voucher to Dinita Stonemantle in the Vault of Ironforge.",
    ["D"] = "This object is an incredible find.  The ancient Titans, or the Creators as these discs refer to them as, are the link to our enchanted creation, if the lore stored in here is correct!  We\'ll need to invest as much research time into this as possible.$B$BAs for you, heroic $c, take this voucher to Dinita Stonemantle at the vault.  It grants you what I think is a fitting for such a tremendous find!$B$BReturn to me directly when you are ready; there is much more to do for the Explorers\' League!",
  },
  [2440] = {
    ["T"] = "The Platinum Discs",
    ["O"] = "Take the reward voucher to Bena Winterhoof in Thunder Bluff.",
    ["D"] = "I shall focus my energies on these discs; they will reveal their true workings to me in due time.$B$BAs for you $N, I will now reward you for your diligence in bringing this to my attention.  Take this voucher to Bena Winterhoof, the alchemy trainer for Thunder Bluff.  She will give you a special item I believe you will find appealing, and she will supply you with a choice of potions from my personal stock.$B$BReturn to me soon, as I sense there is much more at hand for us to do regarding this find.",
  },
  [2458] = {
    ["T"] = "Deep Cover",
    ["O"] = "Travel to the Venture Co. Tower, north of the Sludge Fen and contact the Shattered Hand\'s covert operative, Taskmaster Fizzule.$B$BWhen the tower is in sight, use the Flare Gun to signal your arrival to Taskmaster Fizzule. Fire off TWO flares, then approach Taskmaster Fizzule and perform the Shattered /Salute.$B$BThese actions must be performed in the correct order, as Fizzule will attack and terminate any person he does not recognize.",
    ["D"] = "The Venture Company... Perhaps you have heard of them?$B$BFor one year we have worked on planting an agent deep within their ranks. Through much hard work and careful planning, we succeeded.$B$BRecently we received word that the Venture Co. are planning something on a grand scale. Go north of the Sludge Fen to the Venture Co. tower and contact our undercover agent, Taskmaster Fizzule.$B$BWhen you find the tower, use this flare gun, then approach Fizzule and show him the salute of the Shattered Hand.",
  },
  [2459] = {
    ["T"] = "Ferocitas the Dream Eater",
    ["O"] = "Tallonkai Swiftroot in Dolanaar wants you to kill 7 Gnarlpine Mystics and find the Missing Jewel.",
    ["D"] = "The emerald... It is missing! My dreamcatcher has been damaged!$B$BThere is a band of Gnarlpine mystics located to the north of Starbreeze. I have heard reports that their leader, Ferocitas the Dream Eater has been wearing a necklace that glows green in the night. Now seeing my dreamcatcher, I am sure that he has stolen my emerald... He would never realize that its power is useless to him.$B$BFind this missing jewel, $N. And, while you\'re there, clear out some of the corrupted mystics as well.",
  },
  [2460] = {
    ["T"] = "The Shattered Salute",
    ["O"] = "Perform the Shattered Salute on Shenthul.",
    ["D"] = "As a rogue of the Shattered Hand, it is imperative that you learn the Shattered Salute. It is the only way to truly identify another member of the Hand.$B$BWatch, learn, repeat.",
  },
  [2478] = {
    ["T"] = "Mission: Possible But Not Probable",
    ["O"] = "Steal Silixiz\'s Tower Key from Foreman Silixiz, then kill two Mutated Venture Co. Drones, two Venture Co. Patrollers, and two Venture Co. Lookouts.$B$BAssassinate Grand Foreman Gallywix and take his head, then pick the lock on Gallywix\'s lockbox and steal the Cache of Zanzil\'s Altered Mixture. Take the Cache and Gallywix\'s Head back to Shenthul in Orgrimmar for further instruction.",
    ["D"] = "You will need to steal the tower key from Foreman Silixiz, $N. Once you\'ve got the key in your possession, hold on to it - I may need it later.$B$BRemember, the poison has weakened everything in this tower. Use your abilities to bring chaos and destruction to its inhabitants. Slay them all -- show no mercy!$B$BOnce everything inside lays in a bloodied heap, pick Gallywix\'s lockbox and steal the cache of Zanzil\'s mixture held inside. Take both the head of Gallywix and the mixture to Shenthul in Orgrimmar.",
  },
  [2479] = {
    ["T"] = "Hinott\'s Assistance",
    ["O"] = "Travel to Tarren Mill in Hillsbrad Foothills and deliver the Sample of Zanzil\'s Mixture to Serge Hinott.$B$BTo get to Tarren Mill, take the Zeppelin to the Undercity and follow the road south through Silverpine and towards Hillsbrad. Follow the signs!",
    ["D"] = "Take a sample of the mixture to Serge Hinott in Tarren Mill.$B$BIf anyone can cure you, it\'s Serge. Live long enough and Serge will show you the ways of poison creation and use.",
  },
  [2480] = {
    ["T"] = "Hinott\'s Assistance",
    ["O"] = "Wait for Serge Hinott to complete the cure.",
    ["D"] = "I have never seen such wondrous results from a poison! Zanzil you say? Never heard of him, however, I must commend him on the creation of this concoction.$B$BSo, I am to cure you of this and then teach you how to make use of the poison?$B$BGive me a few moments to examine the toxin, $N.",
  },
  [2498] = {
    ["T"] = "Return to Denalan",
    ["O"] = "Rellian Greenspyre wants you to speak with Denalan at Lake Al\'Ameth.",
    ["D"] = "I have just received word that  Denalan at Lake Al\'Ameth has discovered what may be the cause of the tumors that plague the timberlings. Please speak with him, and tell him I sent you.$B$BMake haste, $N; he needs our help.",
  },
  [2499] = {
    ["T"] = "Oakenscowl",
    ["O"] = "Denalan at Lake Al\'Ameth wants you to collect the Gargantuan Tumor from Oakenscowl.",
    ["D"] = "In a cave along the southern bank of the lake, a timberling named Oakenscowl is spreading corruption to all the creatures that make Lake Al\'Ameth their home.$B$BI dared not get too close, but even from a distance, it is obvious: Oakenscowl is being poisoned by the largest tumor I have ever seen...  I would call it gargantuan, even.$B$B$N, you have already done much to aid my efforts, but I ask of you one more task. Hunt down Oakenscowl and collect the tumor; remove this source of corruption from my home.",
  },
  [2500] = {
    ["T"] = "Badlands Reagent Run",
    ["O"] = "Acquire the reagents Ghak Healtouch needs from the Badlands, then return to him in Thelsamar.",
    ["D"] = "$C, I would make use of your services.  I am an alchemist of some note, and I seek your aid in acquiring some reagents found out in the Badlands.$B$BI need the following items to replenish my stocks: five buzzard gizzards, ten crag coyote fangs, and five rock elemental shards.  Obviously, you can get them off of the beasties themselves; if you\'re not the fighting type, then find friends who are.$B$BAcquire these items for me, you\'ll get some coin out of the deal.$B$BWell, what say ye?",
  },
  [2501] = {
    ["T"] = "Badlands Reagent Run II",
    ["O"] = "Use the empty thaumaturgy vessels on scorched guardian dragons found in the Badlands.  Once you have them filled, bring them to Ghak Healtouch in Thelsamar.",
    ["D"] = "You have been most helpful to me, $N.  I would like to reward you with the very recipe for the brew I gave you earlier, but I must ask of you a favor that is quite dangerous.$B$BThis lockbox contains three empty thaumaturgy vessels; they are imbued with an attunement aura that will drain the blood of a scorched guardian dragon.  Using one on the beast will anger it greatly, so be careful.  Once you have all three filled, bring them to me.$B$BDo this, and the recipe will be yours.",
  },
  [2518] = {
    ["T"] = "Tears of the Moon",
    ["O"] = "Priestess A\'moora in the Temple of the Moon at Darnassus wants you to bring her Lady Sathrah\'s Silvery Spinnerets.",
    ["D"] = "Lady Sathrah was once beloved of Elune. Graceful and pure, the spider spun her silver threads through the moonlight, catching the evening mist. The silvery dew had strong healing powers and was kept here in the temple.$B$BBut of late, Sathrah has descended into madness. Her future generations are now threatened as well.$B$BFind Lady Sathrah, $N, and end her suffering. She dwells along the northern borders of Teldrassil, near Wellspring Lake. Gather her silvery spinnerets and bring them back to me.",
  },
  [2519] = {
    ["T"] = "The Temple of the Moon",
    ["O"] = "Sister Aquinne wants you to speak with Priestess A\'moora in the Temple of the Moon.",
    ["D"] = "A once great creature of the forest is in need of help, $r.$B$BThere is a difficult task that must be done.$B$BPlease seek out Priestess A\'moora; she will explain things to you. Find her to the southeast of here, inside the Temple of the Moon.",
  },
  [2520] = {
    ["T"] = "Sathrah\'s Sacrifice",
    ["O"] = "Priestess A\'moora wants you to place Lady Sathrah\'s silvery spinnerets at the fountain inside the temple, and then return to her.",
    ["D"] = "Now you must finish the task to which you have been appointed. Take the spinnerets and offer Lady Sathrah\'s sacrifice to Elune; lay the great spider to rest.$B$BInside this temple, find the central fountain. There is where you may leave the sacrifice.  The holy waters will purify and cleanse the corruption that drove Sathrah mad.$B$BWhen you have completed this, return to me.",
  },
  [2521] = {
    ["T"] = "To Serve Kum\'isha",
    ["O"] = "In your journeys throughout the Blasted Lands, should you ever come across a Flawless Draenethyst Sphere, take the item back to Kum\'isha the Collector.$B$BBe warned, this gem is one of the most rare crystals in all of Azeroth. Any creature in these lands could be holding a Flawless Draenethyst Sphere.$B$BYou will be rewarded for each Flawless Draenethyst Sphere you have collected.",
    ["D"] = "$R, can your miniscule brain comprehend the rarity of a flawless sphere of draenethyst? Draenei have spent lifetimes in search of just one of these crystals, only to meet horrible fates, their purpose unfulfilled.$B$BSo you stand before Kum\'isha, knowing what Kum\'isha has requested and still wish to assist? Very well, $r... At any time during your travels across these lands, should you find a flawless sphere of draenethyst, return it to me and I shall reward you with riches beyond your wildest dreams.  ",
  },
  [2522] = {
    ["T"] = "Kum\'isha\'s Endeavors",
    ["O"] = "Find and return a Flawless Draenethyst Sphere to Kum\'isha the Collector.",
    ["D"] = "Even though the portal conjuration was a failure, I remain hopeful that my next attempt will be successful.$B$BThe hope of the Draenei is as fervent as your greed for trinkets, $r. Go back out into these cursed lands and recover another flawless draenethyst sphere.$B$BOf course, the choice to take on this task is completely left to you *grin*.",
  },
  [2523] = {
    ["T"] = "Corrupted Songflower",
    ["O"] = "",
    ["D"] = "",
  },
  [2541] = {
    ["T"] = "The Sleeping Druid",
    ["O"] = "Bring a Shaman Voodoo Charm to Oben Rageclaw in the Ban\'ethil Barrow Den.",
    ["D"] = "The Gnarlpine shaman that inhabit this place have discovered a way of separating a sleeping druid\'s spirit from the physical body. The furbolg have animated my physical form and are using it to attack anyone that attempts to explore the Ban\'ethil Barrow Den. I am now trapped in the Emerald Dream, powerless to stop this.$B$BYou must help me. The Gnarlpine shaman carry a strange charm which is used to perform this ritual, and I\'d like to examine it. Please, $N, bring one to me.",
  },
  [2561] = {
    ["T"] = "Druid of the Claw",
    ["O"] = "Oben Rageclaw wants you to kill his soulless body, and then use the Voodoo Charm.",
    ["D"] = "After examining this charm, $N, I see now what must be done. Please take it, and do as I ask.$B$BYou are to explore the deepest areas of the Ban\'ethil Barrow Den. There, you will find my soulless body... Although I regret what I am about to tell you, I see no other way to free myself from the control of the Gnarlpine.$B$BIn order for me to escape them, you must kill my physical form.  Once that is done, use the voodoo charm on my fallen body. After you have completed this task, please return to me.",
  },
  [2561] = {
    ["T"] = "Druid of the Claw",
    ["O"] = "Oben Rageclaw wants you to kill his soulless body, and then use the Voodoo Charm.",
    ["D"] = "After examining this charm, $N, I see now what must be done. Please take it, and do as I ask.$B$BYou are to explore the deepest areas of the Ban\'ethil Barrow Den. There, you will find my soulless body... Although I regret what I am about to tell you, I see no other way to free myself from the control of the Gnarlpine.$B$BIn order for me to escape them, you must kill my physical form.  Once that is done, use the voodoo charm on my fallen body. After you have completed this task, please return to me.",
  },
  [2581] = {
    ["T"] = "Snickerfang Jowls",
    ["O"] = "Bring three Snickerfang Jowls, two Blasted Boar Lungs, and one Scorpok Pincer to Bloodmage Drazial.",
    ["D"] = "It is a well known fact that hyenas possess tremendous strength in their jowls, but the strength to snap through three inches of thorium steel? Impossible, improbable, and yet I have seen this demonstrated with my own eyes.$B$BVenture forth, into the Blasted Lands, and bring me three snickerfang jowls. As you search for the snickerfang, be on the lookout for boars and scorpoks: The initial assay also requires two blasted boar lungs and one scorpok pincer to complete.",
  },
  [2582] = {
    ["T"] = "Rage of Ages",
    ["O"] = "Bring three Snickerfang Jowls, two Blasted Boar Lungs, and one Scorpok Pincer to Bloodmage Drazial.",
    ["D"] = "Ah, $N, welcome back. It seems you enjoyed the effect of the robust, operational imbue, derived from snickerfang. Be warned, while the immediate effect is a titanic burst of strength, the long term effects on your spirit and intelligence could be hazardous.$B$BWith that said, do you require more? If so, bring me another three snickerfang jowls, two blasted boar lungs, and one scorpok pincer.",
  },
  [2583] = {
    ["T"] = "A Boar\'s Vitality",
    ["O"] = "Bring three Blasted Boar Lungs, two Scorpok Pincers, and one Basilisk Brain to Bloodmage Drazial.",
    ["D"] = "Within these blasted lands exists two species of boar: the ash-mane boar and the helboar.$B$BIt would appear that the stamina of these beasts renders them nearly immune to harm, as the other animals find them too difficult to kill and consequently, not worth the effort.$B$BWe need to perform further analysis, $N. Should you bring me three blasted boar lungs, two scorpok pincers, and one basilisk brain, I shall create a divine elixir from the residue of the assay. You will be my first test subject!",
  },
  [2584] = {
    ["T"] = "Spirit of the Boar",
    ["O"] = "Bring three Blasted Boar Lungs, two Scorpok Pincers, and one Basilisk Brain to Bloodmage Drazial.",
    ["D"] = "When I first administered the elixir, $N, I could tell that one or two applications would not satiate your appetite. Even now, I can see the hunger in your eyes. The concoction has addictive properties of which, seemingly, the most strong of will cannot resist.$B$BSo be it! Should you require more of the lung juice cocktail, venture back into the wasteland and find for me three blasted boar lungs, two scorpok pincers, and one basilisk brain.",
  },
  [2585] = {
    ["T"] = "The Decisive Striker",
    ["O"] = "Bring three Scorpok Pincers, two Vulture Gizzards, and one Blasted Boar Lung to Bloodmage Drazial.",
    ["D"] = "Have you ever watched them in battle, $N? The scorpoks, I mean. Have you ever seen the grace and agility with which they move? The decisiveness of their strikes?$B$BIf only we could somehow isolate that agility and encapsulate it, we would be wealthy beyond our... I mean... it would be a boon for the scientific community.$B$BAssist me, $N, and I shall allow you a sampling of the assay.$B$BIn order to create a stable sample, I will need three scorpok pincers, two vulture gizzards, and one blasted boar lung.",
  },
  [2586] = {
    ["T"] = "Salt of the Scorpok",
    ["O"] = "Bring three Scorpok Pincers, two Vulture Gizzards, and one Blasted Boar Lung to Bloodmage Drazial.",
    ["D"] = "You live! This is good news, indeed. I assume you are back for more of the scorpok assay?$B$BVery well, $N, back to the wasteland with you and do not return until you have collected three scorpok pincers, two vulture gizzards, and one blasted boar lung.",
  },
  [2601] = {
    ["T"] = "The Basilisk\'s Bite",
    ["O"] = "Bring ten Basilisk Brains and two Vulture Gizzards to Bloodmage Lynnore.",
    ["D"] = "These wastelands are riddled with redstone basilisk, $N. These basilisk have adapted and flourished, more so than any of the other wildlife within the region.$B$BI have watched from afar as they hunt and go about their daily activities - the beasts exhibit a thought process more complex than even some humanoids.$B$BI must study their brains!$B$BRetrieve ten of their brains for me, $N. The compound cannot be complete, however, without two vulture gizzards to form the base of the solution.",
  },
  [2602] = {
    ["T"] = "Infallible Mind",
    ["O"] = "Bring ten Basilisk Brains and two Vulture Gizzards to Bloodmage Lynnore.",
    ["D"] = "Your focused concentration has dwindled so soon? More research must be done!$B$BOh yes, the price remains the same, $N. Venture forth into the blasted lands and return to me with ten brains from the redstone and two vulture gizzards.",
  },
  [2603] = {
    ["T"] = "Vulture\'s Vigor",
    ["O"] = "Bring ten Vulture Gizzards and two Snickerfang Jowls to Bloodmage Lynnore.",
    ["D"] = "Death does indeed have wings in these blasted lands, $r. I have witnessed the black slayers and bonepickers feasting upon the corpse of many a fallen explorer - sometimes feeding upon the most vile and disease ridden of carcasses. It is a wonder, then, how their internal systems overcome the pestilence that claims these lands.$B$BAssist me in my studies, $N! Gizzards I need! Two snickerfang jowls will also be required for the base of the concoction.$B$BYou shall be rewarded two-fold for your efforts.",
  },
  [2604] = {
    ["T"] = "Spiritual Domination",
    ["O"] = "Bring ten Vulture Gizzards and two Snickerfang Jowls to Bloodmage Lynnore.",
    ["D"] = "I see that the inner fire has dwindled. Would you say the spiritual euphoria lasted an hour?$B$BIf it\'s more of the gizzard gum you desire, then more of the gizzards I require. Back to the wastelands, $N. Do not return until you have recovered ten gizzards and two jowls.",
  },
  [2605] = {
    ["T"] = "The Thirsty Goblin",
    ["O"] = "Collect a Laden Dew Gland and bring it to Marin Noggenfogger in Gadgetzan.",
    ["D"] = "I\'ve got an idea, see. I\'ve heard of some strange creatures called dew collectors that live to the southwest... and that they are able to store water in special glands.$B$BI think that if I could get one of these glands, I could create a drink that would quench my terrible thirst! Do you think you could help me, $N?$B$BLook for the cactus garden, $N; that\'s where you\'ll find the dew collectors.",
  },
  [2606] = {
    ["T"] = "In Good Taste",
    ["O"] = "Marin Noggenfogger wants you to speak with Sprinkle in Gadgetzan.",
    ["D"] = "Blech! This stuff tastes terrible! So much for that idea...$B$BWait, what\'s that? Oh, I know, I know, you think I shouldn\'t give up on this stuff. Well, okay then.$B$BUhhhh, I have an idea! Brilliant! Take this dew to my friend Sprinkle -- she\'s an excellent chef. Tell her what we\'re up to here and that we want to make it go down a little easier.$B$BI mean, if it\'s going to taste that bad, it at least should have some kick to it... And I\'m just not feeling it... ",
  },
  [2607] = {
    ["T"] = "The Touch of Zanzil",
    ["O"] = "Speak with Doc Mixilpixil in the cellar of the Barracks.",
    ["D"] = "It appears as if you\'ve brought back more information than even I expected. This certainly is a strange turn of events, $N.$B$BA cure? Hrm... Go down to the cellar and speak with Doc Mixilpixil - he may be able to help.",
  },
  [2608] = {
    ["T"] = "The Touch of Zanzil",
    ["O"] = "/Lay down to be examined by Doc Mixilpixil. How can he find the cure if he can\'t find the cause?",
    ["D"] = "Oh my, you look like death. Correction - I\'ve seen death, you look worse than death. You, $g sir:miss;, would be what death coughed up at his last physical.$B$BHah! You hear that Noarm? Write that one down!$B$B*Cough* Hrm yes, in order for me to help you, I\'ll need to draw some blood. Don\'t worry, it only hurts the first few... fifty times or so... /lay down when you\'re ready for the treatment, $N.",
  },
  [2609] = {
    ["T"] = "The Touch of Zanzil",
    ["O"] = "Bring Doc Mixilpixil one bundle of Simple Wildflowers, one Leaded Vial, one Bronze Tube, and one Spool of Light Chartreuse Silk Thread. The \'itis\' doesn\'t cure itself, young $g fella:lady;. ",
    ["D"] = "It\'s unanimous, $N. You\'ve got the \'itis.\'$B$BLuckily, I have a cure for the \'itis.\'$B$BHere\'s what I need: One bundle of simple wildflowers, one leaded vial, one bronze tube, and last but certainly not least, one spool of light chartreuse silk thread. Ol\' Emma left a spool of it for me at \'The Finest Thread.\' You can find the shop along the canals.$B$BHurry along now, $N, and try not to touch anyone until you\'re cured. Oh, and Dr. Montgomery says you need a shower -- his words, not mine.",
  },
  [2621] = {
    ["T"] = "The Disgraced One",
    ["O"] = "Speak to Dispatch Commander Ruag in Stonard.",
    ["D"] = "Regret. It is all I have now. One bad decision resulting in several horrible outcomes. If only I had waited for those orders. Maybe they would have detailed the horrors that awaited us in those cursed lands! Maybe they were to redirect us to another detail? Ultimately, it was my fault. All of it was my fault.$B$BSo here I stand, awaiting orders. I must know. I must have those orders.$B$BDispatch Commander Ruag resides in Stonard. Find him and find out what became of the orders.",
  },
  [2622] = {
    ["T"] = "The Missing Orders",
    ["O"] = "Speak to Bengor.",
    ["D"] = "The orders? What could you possibly want with the orders?$B$BWhat are you implying, $N? Of course I relayed those orders! The soldiers I sent the orders with were ambushed and brutally beaten by murlocs. Only one survived, if you can call that surviving. He can hardly speak, cannot walk, and must be fed through a tube. If you want to know what happened to those orders, speak with Bengor. I\'m sure he is around here somewhere... and, $N, be mindful of what you stick your nose into - it may get bitten off.",
  },
  [2623] = {
    ["T"] = "The Swamp Talker",
    ["O"] = "Retrieve the Warchief\'s Orders and return them to the Fallen Hero of the Horde.",
    ["D"] = "Orders were given by the dispatch commander. We took... took orders. On way to Blasted Lands, ambushed.$B$BMurlocs... Many murlocs... One named Swamp Talker. Took orders from my hands as spear thrust into my skull. East... cave....$B$B<Bengor passes out.>",
  },
  [2641] = {
    ["T"] = "Sprinkle\'s Secret Ingredient",
    ["O"] = "Sprinkle in Gadgetzan wants you to collect a Violet Tragan and return it to her.",
    ["D"] = "When I was a kid, my sister used to do all the cooking. It was horrible! I could barely stomach some of her concoctions -- until I found what I like to call my \"secret\" ingredient! Add it to anything, and... WOW! It tastes great!$B$BWhat is it, you ask? It\'s a mushroom called the Violet Tragan. If you want me to help you turn this horrid-tasting brew into something edible, head to the Hinterlands, and look for a small lake there.$B$BOh, and remember, the Violet Tragan can only be found underwater.",
  },
  [2661] = {
    ["T"] = "Delivery for Marin",
    ["O"] = "Sprinkle wants to you take the Violet Powder to Marin Noggenfogger in Gadgetzan.",
    ["D"] = "Here\'s the Violet Powder, $N. Take it to Marin and tell him he owes me a favor!",
  },
  [2662] = {
    ["T"] = "Noggenfogger Elixir",
    ["O"] = "Marin Noggenfogger wants you to speak to him again after he creates his elixir.",
    ["D"] = "Now, to mix this powder in with the dew.$B$BCome back and talk to me in a minute... after I have perfected the fabulous Noggenfogger Elixir!",
  },
  [2681] = {
    ["T"] = "The Stones That Bind Us",
    ["O"] = "Free nine Servants of Razelikh, three Servants of Sevine, three Servants of Allistarj, and three Servants of Grol. Return to the Fallen Hero when your task is complete. You must remain within close proximity of the stones or the process will fail.",
    ["D"] = "It will take more than you alone to overcome the forces of Razelikh and his subordinates.$B$BGather a suitable group of adventurers and free my men of their eternal torture! Eighteen stones you must shatter.$B$BFight hard and die with honor, $N.",
  },
  [2701] = {
    ["T"] = "Heroes of Old",
    ["O"] = "Open the chest and claim your reward.",
    ["D"] = "Please, take from this a relic of old. We no longer have a use for the item.$B$BMay it serve you well in your fight against the legion.",
  },
  [2702] = {
    ["T"] = "Heroes of Old",
    ["O"] = "Speak to Corporal Thund Splithoof.",
    ["D"] = "You have honored me with your nobility and heroism, $N.$B$BYou have also honored them...",
  },
  [2721] = {
    ["T"] = "Kirith",
    ["O"] = "Find out what became of Lieutenant Kirith.",
    ["D"] = "As one chapter of your adventure closes, another one begins, $N.$B$BOne of my men is still unaccounted for, lost somewhere in the Blasted Lands. I am talking about Lieutenant Kirith, of course.$B$BAs I had mentioned earlier, we were both imprisoned and tortured by Allistarj, but poor Kirith, his strength of will faltered. I could hear the experiments from my cage; the tortured screams rang through the halls of the cave. That was the last I saw or heard of Kirith.",
  },
  [2741] = {
    ["T"] = "The Super Egg-O-Matic",
    ["O"] = " ",
    ["D"] = "",
  },
  [2742] = {
    ["T"] = "Rin\'ji is Trapped!",
    ["O"] = "Escort Rin\'ji out of the Quel\'Danil Lodge, then find his hidden secret at the Overlook Cliffs to the east.",
    ["D"] = "Hey $r!$B$BRin\'ji lost axe and was captured! Please, help Rin\'ji escape. Me know that $r like you don\'t like Rin\'ji\'s kind, but Rin\'ji begs! Must get out of here!$B$BGot something for nice $r if $Ghe helps Rin\'ji out of here:she helps Rin\'ji out of here;...$B$BRin\'ji is hiding something secret at the Overlook Cliffs.",
  },
  [2743] = {
    ["T"] = "The Cover of Darkness",
    ["O"] = "Return to the Fallen Hero of the Horde with your findings.",
    ["D"] = "Let us not mince words, $r; my time here is limited.$B$BYou must destroy the triad of power which protects the demon lord, Razelikh. Doing so will not be as simple a task as freeing a damned soul.$B$B<Kirith grins.>$B$BYou see, each of Razelikh\'s underlings wears a third of an amulet of Razelikh\'s creation around their neck. The very same amulet you will need in order to summon the arch demon.$B$BReturn to Trebor and tell him what is required to summon Razelikh. He will be able to instruct you further.",
  },
  [2744] = {
    ["T"] = "The Demon Hunter",
    ["O"] = "Speak with Loramus Thalipedes in Azshara.",
    ["D"] = "The triad is protected by Razelikh.$B$BIn exchange for their servitude, Razelikh granted each of them nearly unlimited power within the Blasted Lands. The amulet pieces are each attuned to the lifeforce of their owner, granting them immortality. As long as they remain within the Blasted Lands, they cannot be slain, their life spans extended until the end of days.$B$BThere is one who may be able to assist you further, $N. Travel to Azshara and find the demon hunter, Loramus Thalipedes. Search the islands.",
  },
  [2745] = {
    ["T"] = "Infiltrating the Castle",
    ["O"] = "Speak to Tyrion in Stormwind Castle.",
    ["D"] = "Hmm, an interesting tale, and not one to be taken lightly. Yeah, I can help you, but what you need to do is divvy out a form of justice that\'s not... legal, if you\'re to see this through to the end.$B$BLescovar and Marzon need to be dealt with, and swiftly. Do you think you\'re capable of such a thing?$B$BYou decide. If so, then make haste to the castle and find an old friend of mine, a gnome named Tyrion. He\'s been watching Lescovar now for a couple weeks. He should be able to help you end this.",
  },
  [2746] = {
    ["T"] = "Items of Some Consequence",
    ["O"] = "Bring 3 Silk Cloth and 2 of Clara\'s Fresh Apples to Tyrion in Stormwind.",
    ["D"] = "Trias wants the timetable pushed up some, huh? Okay, we can do that, but I\'m not ready. And know this, I\'m doing the hard part, but you\'re the one that\'s going to get $g his:her; hands dirty, understand? Good.$B$BOkay, I\'ll need you to get me a few things before I can put my plan into effect. I got the perfect disguise that\'ll get Spybot past those guards.$B$BBring me some silk and a couple apples. The silk\'s up to you, but Clara\'s little farm right outside the city has the perfect sized apples.",
  },
  [2747] = {
    ["T"] = "An Extraordinary Egg",
    ["O"] = "",
    ["D"] = "",
  },
  [2748] = {
    ["T"] = "A Fine Egg",
    ["O"] = "",
    ["D"] = "",
  },
  [2749] = {
    ["T"] = "An Ordinary Egg",
    ["O"] = "",
    ["D"] = "",
  },
  [2750] = {
    ["T"] = "A Bad Egg",
    ["O"] = "",
    ["D"] = "",
  },
  [2751] = {
    ["T"] = "Barbaric Battlements",
    ["O"] = "Bring two Patterned Bronze Bracers, two Bronze Greatswords, and two Sharp Claws to Orokk Omosh in Orgrimmar.",
    ["D"] = "Twenty generations of Omosh have held this hammer, $N. A hundred thousand tons of metal we have molded. Tragically, it all ends at Orokk, failure of the Omosh: Without kin and incapable of producing offspring.$B$BI cannot let the secret recipes of Omosh die with me! Someone must continue the legacy, Omosh or not!$B$BProve to Orokk you are capable, $N. Bring for Orokk the following: Two patterned bronze bracers, two bronze greatswords, and two sharp claws. Bring them for Orokk and we shall continue.",
  },
  [2752] = {
    ["T"] = "On Iron Pauldrons",
    ["O"] = "Bring four Bronze Battleaxes and four Bronze Warhammers to Orokk Omosh in Orgrimmar.",
    ["D"] = "Orokk sees the eagerness in your eyes, $N. Like a sponge you soak in the wisdom of the Omosh.$B$BThree lessons I shall teach!$B$BReturn to the anvil, soak in the heat of the forge, create for Orokk four bronze battleaxes and four bronze warhammers. Only then will you learn of the iron pauldron.",
  },
  [2753] = {
    ["T"] = "Trampled Under Foot",
    ["O"] = "Bring four Green Iron Helms, four Green Iron Bracers, and two Green Iron Leggings to Orokk Omosh in Orgrimmar.",
    ["D"] = "Only two more lessons Orokk will teach, $N. Your muscles ache, I know, but continue on for the Omosh!$B$BBring to Orokk four green iron helms, four green iron bracers, and two green iron leggings - for this you learn and your enemies suffer!",
  },
  [2754] = {
    ["T"] = "Horns of Frenzy",
    ["O"] = "Bring two Solid Iron Mauls, two Silvered Bronze Boots, and two Silvered Bronze Gauntlets to Orokk Omosh in Orgrimmar.",
    ["D"] = "The final lesson Orokk teaches you, $N. To the sacred anvil and forge you return! Bring for Orokk two solid iron mauls, two silvered bronze boots, and two silvered bronze gauntlets.$B$BSee the fear in their eyes as you approach!",
  },
  [2755] = {
    ["T"] = "Joys of Omosh",
    ["O"] = "Watch and learn the Omosh Dance of Joy.",
    ["D"] = "Orokk is not crying, $N. Soot is in eye.$B$B<Orokk wipes a tear away.>$B$BThere is one last Omosh family secret Orokk will teach you. Orokk will die happy, thanks to $N.$B$BLearn Omosh dance of joy and then you learn fist of iron!",
  },
  [2756] = {
    ["T"] = "The Old Ways",
    ["O"] = "Bring four Steel Breastplates and four Steel Plate Helms to Aturk the Anvil in Orgrimmar.",
    ["D"] = "In the time of legends, Aturk would crush the skulls of his enemies and grind the bone into a fine powder for use in filament. Long gone are those days, $r. The Warchief calls for peace among the clans. Old enemies are now only watched with weary eyes!$B$BUnbelievable, I know, but such is the will of Thrall.$B$BEnough lamenting! You are here to learn, and learn you will! Bring me four steel breastplates and four steel helms and you will gain a taste of war!",
  },
  [2757] = {
    ["T"] = "Booty Bay or Bust!",
    ["O"] = "Speak with McGavan in Booty Bay.",
    ["D"] = "Listen, kid, you\'re not gonna learn anything hanging around here.$B$BI know a guy who knows a guy. He hangs around the smithy in Booty Bay. If you show him this insignia, he\'ll tell you all about mithril.$B$BSo whadd\'ya say? Leave this kid stuff to the babies in diapers, $N -- it\'s time to move on.$B$BIf you\'re interested, his name\'s McGavan. Show him this trinket and he\'ll recognize your commitment.",
  },
  [2758] = {
    ["T"] = "The Origins of Smithing",
    ["O"] = "Bring six Golden Scale Bracers to Hank the Hammer in Stormwind.",
    ["D"] = "When I was about your age, a dwarf by the name of Galvan took me under his wing and trained me in the ways of the armorsmith. Before he left for Booty Bay, he made me promise to train all those with the desire and determination to advance in the craft of blacksmithing -- so here I am -- all these years later.$B$BSo, now it\'s time for you to learn a little something. Make me six golden scale bracers and you will learn where smithing comes from, $N.",
  },
  [2759] = {
    ["T"] = "In Search of Galvan",
    ["O"] = "Speak with McGavan in Booty Bay.",
    ["D"] = "Although Galvan left Booty Bay many years ago, members of the order still remain in town, hoping to one day find another worthy of Galvan\'s tutelage.$B$BTake this insignia and present it to McGavan in Booty Bay.$B$BIf you actually make it that far, he\'ll know that you are worthy. I have faith, $N!",
  },
  [2760] = {
    ["T"] = "The Mithril Order",
    ["O"] = "Speak with Galvan the Ancient in Stranglethorn Vale.",
    ["D"] = "Most of our recruits never make it to Booty Bay, $N. The shine of the insignia is enough to trigger the loot lust in even the staunchest of blacksmiths. Fools they be, as the insignia is a drop in the bucket compared to the wealth of items you\'ll see!$B$BFind Galvan the Ancient and show him this pin. His residence is far to the north, between Zul Gurub and the Mosh\'Ogg ogre mound.",
  },
  [2761] = {
    ["T"] = "Smelt On, Smelt Off",
    ["O"] = "Bring forty Mithril Bars and forty Iron Bars to Galvan the Ancient in Stranglethorn.",
    ["D"] = "Many blacksmiths believe that mining and smithing are separate, independent jobs; but can there be blacksmithing without mining? Ah ha -- it is an impossibility! The accomplished blacksmith must also be an accomplished miner.$B$BShow Galvan that you are capable, $N. Bring me forty bars of iron and forty bars of mithril. For this, Galvan will teach you the ways of the ornate mithril pants!",
  },
  [2762] = {
    ["T"] = "The Great Silver Deceiver",
    ["O"] = "Bring forty Mithril Bars and five Truesilver Bars to Galvan the Ancient in Stranglethorn.",
    ["D"] = "It is said that truesilver is stronger and more durable than mithril. Galvan does not disagree with this assessment of the minerals, but contrary to its name, truesilver is not true! Mithril is a far nobler mineral! HAH! Regardless, for this lesson you will need to find the great silver deceiver, $N.$B$BBring me forty bars of mithril and five bars of truesilver. In exchange, you will be taught the secret of ornate mithril gloves and more importantly, learn that truesilver is a liar!",
  },
  [2763] = {
    ["T"] = "The Art of the Imbue",
    ["O"] = "Bring forty Mithril Bars and four Citrines to Galvan the Ancient in Stranglethorn.",
    ["D"] = "The harvesting of precious gems is of utmost importance to our craft, $N. Few gems are more precious than the citrine. The magical properties we imbue into our goods are amplified greatly by the presence of citrine.$B$BFor this lesson, we will fortify forty bars of mithril with four citrines. The result will be a better understanding of magical gemology and the knowledge to create ornate mithril shoulders.",
  },
  [2764] = {
    ["T"] = "Galvan\'s Finest Pupil",
    ["O"] = "Speak with Trenton Lighthammer in Gadgetzan.",
    ["D"] = "If you are ready to conclude your training with Galvan, you should journey to Gadgetzan.$B$BThere, you will find my finest pupil: Trenton Lighthammer. He has much to teach you in the ways of ornate mithril. Otherwise, remain here and learn!$B$BOh, Gadgetzan is in Tanaris, of course!",
  },
  [2765] = {
    ["T"] = "Expert Blacksmith!",
    ["O"] = "Claim your reward from Galvan the Ancient!",
    ["D"] = "When you first came to me, I knew that you would turn out to be one of my finest pupils! It was without hesitancy that you gave up a trinket of some power to McGavan. You did this only so that you could meet me. Such blind faith and devotion to the craft should be rewarded.$B$BYou have attained expertise over blacksmithing, $N. Reap the rewards!",
  },
  [2766] = {
    ["T"] = "Find OOX-22/FE!",
    ["O"] = "Take the distress beacon to Oglethorpe\'s homing robot in a cave near Feral Scar Vale.",
    ["D"] = "You have uncovered some sort of strange, egg-shaped device made from metal.  Fiddling with one of its knobs springs the egg to life, as it opens up into some sort of gnomish robotic contraption!  A voice from inside the robotic egg crackles to life.$B$B\"My name\'s Oglethorpe Obnoticus, and my homing robot has crashed!  I will reward you for its recovery; please take this beacon to the robot!\"$B$B\"I now have computed the coordinates of the robot for you; it is in a cave near Feral Scar Vale!\"",
  },
  [2767] = {
    ["T"] = "Rescue OOX-22/FE!",
    ["O"] = "Escort OOX-22/FE to the dock along the Forgotten Coast, then report to Oglethorpe Obnoticus in Booty Bay.",
    ["D"] = "The gnome\'s voice crackles once again from the robot:$B$B\"I need to move OOX-22/FE to an open, safe place so it can begin a lengthy take-off procedure.  It has built-in cloaking, but I need time on my end to make it operational again.  Escort the robot from its current location to, let\'s say, the dock along the Forgotten Coast!  That should be a perfect place, and long enough, to get things online!\"$B$B\"Escort it safely to the dock, and then come talk to me in Booty Bay!  Oglethorpe Obnoticus - out!\"",
  },
  [2768] = {
    ["T"] = "Divino-matic Rod",
    ["O"] = "Bring the Divino-matic Rod to Chief Engineer Bilgewhizzle in Gadgetzan.",
    ["D"] = "Sergeant Bly stole from me!  He said he\'d only borrow it, but he stole my cherished divino-matic rod!!  Without that rod how will I know where to dig new water holes??$B$BFind Bly and bring me my rod!  I heard he led his band of ill-reputed adventurers to go treasure hunting in Zul\'Farrak to the west.  I bet you\'ll find him there.$B$BLet\'s hope the trolls took care of him, because if you have to fight him for the rod then you\'re in for a serious fight.",
  },
  [2769] = {
    ["T"] = "The Brassbolts Brothers",
    ["O"] = "Speak with Wizzle Brassbolts in the Shimmering Flats.",
    ["D"] = "Have you met the Brassbolts brothers?  They\'re over in the Shimmering Flats, in Kalimdor, racing their rocket car against the goblins.  They usually win which is no surprise--the goblin car blows up so much it barely ever crosses the finish line!$B$BBut even then, the Brassbolts brothers are always looking for new ways to make their car go faster, and Wizzle told me he needs a very rare component for his next experiment.$B$BHe probably needs help getting that part.  Speak to him and find out.",
  },
  [2770] = {
    ["T"] = "Gahz\'rilla",
    ["O"] = "Bring Gahz\'rilla\'s Electrified Scale to Wizzle Brassbolts in the Shimmering Flats.",
    ["D"] = "Deep in Zul\'Farrak, the sand troll city in Tanaris, there is a sacred pool.  From that pool the trolls summon a huge beast!  Gahz\'rilla!  He\'s so fierce that even his scales crackle with energy.  It\'s that energy I want to harness for my car!$B$BBring me the electrified scale of Gahz\'rilla!$B$BBut the summoning of Gahz\'rilla is a well-kept secret of the trolls.  To face him, you must first wrest the secret from them.",
  },
  [2771] = {
    ["T"] = "A Good Head On Your Shoulders",
    ["O"] = "Bring two Mithril Coifs and one Ornate Mithril Shoulder to Trenton Lighthammer.",
    ["D"] = "It seems as if master had this planned from the start. I was not taught to create the items which you learned to create and you were not taught the items which I learned to create.$B$BThe master is wise, $N. We shall teach each other!$B$BShow me how to make a mithril coif and ornate mithril shoulders and I shall show you how to create an ornate mithril helm.",
  },
  [2772] = {
    ["T"] = "The World At Your Feet",
    ["O"] = "Bring two Heavy Mithril Boots and one Ornate Mithril Pants to Trenton Lighthammer.",
    ["D"] = "At last year\'s All-Valley Blacksmithing Championship, all those who watched saw my leg get swept from under me by a cowardly dog from the Venture Co. Foundry. What they didn\'t know, however, was the reason I was able to continue and ultimately win the tournament: That\'s right, ornate mithril boots!$B$BI will need to learn how to craft ornate mithril pants for this year\'s tournament, however, as a Venture Co. shredder is an entrant! Teach me the way of the pants and learn the way of the boot!",
  },
  [2773] = {
    ["T"] = "The Mithril Kid",
    ["O"] = "Bring two Heavy Mithril Breastplates and one Ornate Mithril Gloves to Trenton Lighthammer.",
    ["D"] = "Around these parts I am known as the Mithril Kid. I supply ornate mithril to adventurers from around the world.$B$B<Trenton whispers.>$B$BLittle do they know that I do not know the technique to fashion ornate mithril gloves. Shhhh! This will be our little secret.$B$BTeach me the technique to create ornate mithril gloves, $N, and in exchange, I will show you the resultant of the culmination of my training under Galvan: the ornate mithril breastplate.",
  },
  [2781] = {
    ["T"] = "WANTED: Caliph Scorpidsting",
    ["O"] = "Bring the head of Caliph Scorpidsting to Chief Engineer Bilgewhizzle in Gadgetzan.",
    ["D"] = "WANTED: Caliph Scorpidsting!$B$BThe Gadgetzan Water Company of Gadgetzan, Tanaris will pay a high bounty for the head of Caliph Scorpidsting, leader of the Wastewander outlaws.  His crimes include:$B$B1.  Murdering Gadgetzan Water Co. employees!$B2.Stealing Gadgetzan Water Co. resources!$B3.Seizing Gadgetzan Water Co. property!$B$BBring proof of Caliph Scorpidsting\'s demise to Chief Engineer Bilgewhizzle for an immediate reward!",
  },
  [2782] = {
    ["T"] = "Rin\'ji\'s Secret",
    ["O"] = "Oran Snakewrithe, a well known scholar on matters concerning the Witherbark trolls, may be able to decipher the tablet. She is known to reside within the Magic Quarter of the Undercity.",
    ["D"] = "The tablet is covered with Witherbark troll markings. Rin\'ji\'s secret truly is a mystery... $B$BIt\'s possible that you might have something quite valuable on your hands here. On the other hand, quite the opposite is also possible. A Witherbark troll in distress would probably do anything to find his way out of trouble...",
  },
  [2783] = {
    ["T"] = "Petty Squabbles",
    ["O"] = "Speak with the Fallen Hero of the Horde at the border of the Blasted Lands and the swamp.",
    ["D"] = "We fight the wrong enemy, $N.$B$BThe true power in these lands continues to grow in strength and number while we squabble over scorched earth with orcs.$B$BDo we continue to fight a war in these lands against the Horde, only to be routed one day by the armies of the Burning Legion? It is futile.$B$BPut your differences aside, $N, speak with the fallen hero of the Horde. He knows the truth behind the Blasted Lands.",
  },
  [2784] = {
    ["T"] = "Fall From Grace",
    ["O"] = "Listen to the Fallen Hero of the Horde tell his story.",
    ["D"] = "Hear my story, $r.",
  },
  [2801] = {
    ["T"] = "A Tale of Sorrow",
    ["O"] = "Listen to the Fallen Hero of the Horde tell his story.",
    ["D"] = "Before we may continue, you must hear my story.",
  },
  [2821] = {
    ["T"] = "The Mark of Quality",
    ["O"] = "Bring 10 Thick Yeti Hides to Pratt McGrubben in Feathermoon Stronghold.",
    ["D"] = "My philosophy on leatherworking is that the work must be the best quality possible - no compromises.  Anyone who says that quality doesn\'t matter is trying to sell you ocean-front property in Dun Morogh!$B$BIn my latest pursuit of high-quality materials, I have run across thick yeti hides.  The Yeti are native to Feralas, with many found in Feral Scar Vale.  I want some, and I\'ll give you a choice of one of my leather goods for a stack of ten.  If that sounds fair to you, then we\'re in business.",
  },
  [2822] = {
    ["T"] = "The Mark of Quality",
    ["O"] = "Bring 10 Thick Yeti Hides to Jangdor Swiftstrider in Camp Mojache.",
    ["D"] = "I take pride in my leather work, and I feel it shows; quality should never be compromised.  Anyone who says that quality doesn\'t matter would also have you believe that the earth does not keep secrets!$B$BIn my latest pursuit of high-quality materials, I have discovered thick yeti hides.  Yetis are native to Feralas, often found west of here in Feral Scar Vale.  I\'ll give you a choice of one of my leather goods for a stack of ten.  If that is agreeable to you, then we are set!",
  },
  [2841] = {
    ["T"] = "Rig Wars",
    ["O"] = "Retrieve the Rig Blueprints and Thermaplugg\'s Safe Combination from Gnomeregan and bring them to Nogg in Orgrimmar.",
    ["D"] = "Gnome technology - two years ago its most common use was as a punch line. Times certainly have changed. Now they\'ve got a new rig built that\'s better than all our shredder models combined. I need the blueprints, $N!$B$BUnfortunately, the plans are locked away in a safe near Mekgineer Thermaplugg. To make matters worse, the combination to the safe is only known by Mekgineer Thermaplugg.$B$BDo whatever you have to do to get that combination, $N. Then crack open that safe and bring back those blueprints!",
  },
  [2842] = {
    ["T"] = "Chief Engineer Scooty",
    ["O"] = "Speak with Scooty in Booty Bay.",
    ["D"] = "As soon as we got word from Kernobee about the new gnomish rig, Scooty and I began work on the Transpolyporter 6000: The fastest and most reliable way to Gnomeregan.$B$BIt\'s finally ready for passengers to board, $N. If you want to give it a try, go to Booty Bay and talk with Scooty at the wind rider roost. To get to Booty Bay, take the boat from Ratchet.$B$BOf course, you could always try and make the run to Gnomeregan through the dwarf infested mountains of Dun Morogh!",
  },
  [2843] = {
    ["T"] = "Gnomer-gooooone!",
    ["O"] = "Wait for Scooty to calibrate the Goblin Transponder.",
    ["D"] = "As long as you have the transponder in your possession, all you need to do is step on the pad and you\'ll be teleported to Gnomeregan.$B$BYou may experience some unusual side effects, but none of those should be permanent... anymore.$B$BI almost forgot the most important part - getting back. When you\'re ready to come back, just step on the transpolyporter pad in Gnomeregan!$B$BNow to calibrate the transponder to your size and weight.",
  },
  [2844] = {
    ["T"] = "The Giant Guardian",
    ["O"] = "Locate the lost night elf girl.",
    ["D"] = "I am no good, $N. I was \'spose to be watchin\' over my little friend, Shay, and somehow, she got away from me. I\'m not all that sure what happened, really, but last time I saw her, we was walkin\' through the ruins just north of here.$B$BEverythin\' was goin\' fine, but then I turned around, and... she was gone.$B$BNow I jus can\'t quite figure out what I should do here. Got any ideas, $N? I\'m awful worried.$B$BHey, how \'bout you go look fer Shay? Yer legs can travel much faster \'n mine.",
  },
  [2845] = {
    ["T"] = "Wandering Shay",
    ["O"] = "Pick up Shay\'s Bell from the chest.$B$BEscort Shay Leafrunner to Rockbiter\'s camp.",
    ["D"] = "Do you think you could help me find my way back to my friend, Rockbiter? He was supposed to be watching me... But I do have a tendency to wander off now and then. There are just so many things to see, and I get so distracted.$B$BThere is one thing that will always work to get my attention, though.$B$BInside this chest, you\'ll find a bell that is very precious to me. I just love the sound of it. You have my word, if I wander off, all you have to do is ring it and I\'ll come running back.",
  },
  [2846] = {
    ["T"] = "Tiara of the Deep",
    ["O"] = "Bring the Tiara of the Deep to Tabetha in Dustwallow Marsh.",
    ["D"] = "Long ago I possessed a beautiful piece of jewelry, the Tiara of the Deep.  And not only was it pretty--it held great power for those with the knowledge to use it.$B$BSo when word of the tiara reached the Hydromancer Velratha, she had to have it.  She sent agents to my home and they stole it while I was away.  The thieves!$B$BI want my tiara back!  Go to Zul\'Farrak, the troll city in Tanaris, find Velratha and wrench the tiara from her.  Return it to me and you\'ll earn my favor.",
  },
  [2847] = {
    ["T"] = "Wild Leather Armor",
    ["O"] = "Bring 10 Thick Leather to Pratt McGrubben in Feathermoon Stronghold.",
    ["D"] = "$n, you\'re becoming quite skilled as a leatherworker!  I know some patterns you might be interested in... Wild Leather armor!  Wild Leather taps into the potent and chaotic properties of wildvines, found in areas of lush vegetation and on some of the creatures there.  The armor made using it is second to none for people of your skill, guaranteed.$B$BI\'ll teach this to you, but you\'ll be working for me for a while.  The first cost is simply ten pieces of thick leather.  After this, we\'ll talk specifics.",
  },
  [2848] = {
    ["T"] = "Wild Leather Shoulders",
    ["O"] = "Bring 6 Thick Armor Kits and a Wildvine to Pratt McGrubben in Feathermoon Stronghold.",
    ["D"] = "Wild Leather shoulders support the wearer\'s frame extremely well, offering top notch protection of the upper arms and the shoulders themselves.  As with all Wild Leather armor, a potent but random benefit is applied to the item as the wildvine is stitched in.$B$BFor this pattern, I will require six thick armor kits and a wildvine.  That shouldn\'t be too tall of an order for you to fill.",
  },
  [2849] = {
    ["T"] = "Wild Leather Vest",
    ["O"] = "Bring 2 Turtle Scale Breastplates, 2 Turtle Scale Gloves, and a Wildvine to Pratt McGrubben in Feathermoon Stronghold.",
    ["D"] = "Wild Leather vests are the staple of any Wild Leather armor set.  The frame of the vest blends with the wildvine to produce a product that offers maximum protection of the torso while allowing for the potent magic to work its way through the garment.$B$BFor this pattern, I will require two Turtle Scale breastplates, two sets of Turtle Scale gloves, and a wildvine.  It\'s a tall order, but one you should have little difficulty in filling for me.",
  },
  [2850] = {
    ["T"] = "Wild Leather Helmet",
    ["O"] = "Bring 2 Nightscape Tunics, 2 Nightscape Headbands, and a Wildvine to Pratt McGrubben in Feathermoon Stronghold.",
    ["D"] = "A Wild Leather helmet offers the wearer superior protection to the vital areas of the head without a sacrifice in comfort.  As with all Wild Leather armor, the integrated wildvine yields a powerful but random enhancement to the frame of the helm.  Leather wearers will definitely want this piece!$B$BFor this pattern, I will need two Nightscape tunics, two Nightscape headbands, and a wildvine.  Complete this order and the pattern will be yours!",
  },
  [2851] = {
    ["T"] = "Wild Leather Boots",
    ["O"] = "Bring 2 Nightscape Pants, 2 Nightscape Boots, and 2 Wildvines to Pratt McGrubben in Feathermoon Stronghold.",
    ["D"] = "You\'ve learned quite a bit now, $n; still, there is more for you to learn.  Next are Wild Leather boots, the cornerstone of any set.$B$BThese boots are crafted to cradle the foot in comfort while bracing it against the hazards of adventuring.  The soft soles are a blessing to those seeking quiet movement in footwear.$B$BFor this pattern, I need an order of two Nightscape pants and two Nightscape boots filled.  I also will require two wildvines... not just one.  When you have all this, let me know.",
  },
  [2852] = {
    ["T"] = "Wild Leather Leggings",
    ["O"] = "Bring 2 Turtle Scale Helms, 2 Turtle Scale Bracers, and 2 Wildvines to Pratt McGrubben in Feathermoon Stronghold.",
    ["D"] = "With the basics down, you are ready to master more complex fare, $n.  Wild Leather leggings are some of the sturdiest and powerful leather gear around.  The waist, thighs, calves, and the vitals - all are given tremendous protection.  Coupled with the enhancement that the wildvine provides, adventurers who own it will thank you each time they see you.$B$BFor this pattern, I need an order of two Turtle Scale helms, two Turtle Scale bracers, and two wildvines filled.",
  },
  [2853] = {
    ["T"] = "Master of the Wild Leather",
    ["O"] = "Give Pratt\'s Letter to Telonis, the master leatherworker of Darnassus.",
    ["D"] = "Well $n, you are the definition of diligence.  There is one pattern left for Wild Leather armor; you\'ve mastered the others.  I won\'t require you to toil needlessly for the pattern.  You\'ve earned it outright, and then some... but I cannot give it to you myself.  My teacher, the one who brought me into the craft, is the one who must reward you.$B$BTake this letter to Telonis; he teaches leatherworking in Darnassus.  Show him the letter, and let him acknowledge you as an equal, as I do.",
  },
  [2854] = {
    ["T"] = "Wild Leather Armor",
    ["O"] = "Bring 10 Thick Leather to Jangdor Swiftstrider in Camp Mojache.",
    ["D"] = "$n, your skill as a leatherworker precedes you.  I possess leatherworking lore you would be wise to acquire... Wild Leather armor!  Wild Leather taps into the potent and chaotic properties of wildvines, found in areas of lush vegetation and on some of the creatures there.  Armor made using it is second to none for a crafter of your skill, guaranteed.$B$BI\'ll teach you, but you\'ll be working for me for a while.  The first cost is simply ten pieces of thick leather.  After this, we\'ll talk specifics.",
  },
  [2855] = {
    ["T"] = "Wild Leather Shoulders",
    ["O"] = "Bring 6 Thick Armor Kits and a Wildvine to Jangdor Swiftstrider in Camp Mojache.",
    ["D"] = "Wild Leather shoulders support a wearer\'s frame extremely well, offering superior protection of the upper arms as well as the shoulders.  As with all Wild Leather armor, a random yet very potent enhancement is imbued within the item as the wildvine is stitched in.$B$BFor this pattern, I will require six thick armor kits and a wildvine.  That shouldn\'t be too tall of an order for you to fill.",
  },
  [2856] = {
    ["T"] = "Wild Leather Vest",
    ["O"] = "Bring 2 Turtle Scale Breastplates, 2 Turtle Scale Gloves, and a Wildvine to Jangdor Swiftstrider in Camp Mojache.",
    ["D"] = "Wild Leather vests are the staple of any Wild Leather armor set.  The frame of the vest and the wildvine weave within it produces a product that offers maximum protection of the torso while allowing for potent magic to work its way through the garment.$B$BFor this pattern, I will require two Turtle Scale breastplates, two sets of Turtle Scale gloves, and a wildvine.  This is not an easy lesson, but it is one you certainly have the skill to complete.",
  },
  [2857] = {
    ["T"] = "Wild Leather Helmet",
    ["O"] = "Bring 2 Nightscape Tunics, 2 Nightscape Headbands, and a Wildvine to Jangdor Swiftstrider in Camp Mojache.",
    ["D"] = "A Wild Leather helmet offers the wearer superior protection to the vital areas of the head without a sacrifice in comfort.  As with all Wild Leather armor, the integrated wildvine yields a powerful but random magic imbuement to the helm\'s frame.  Leather wearers will definitely want this piece!$B$BFor this pattern, I will need two Nightscape tunics, 2 Nightscape headbands, and a wildvine.  Complete this lesson and the pattern will be yours!",
  },
  [2858] = {
    ["T"] = "Wild Leather Boots",
    ["O"] = "Bring 2 Nightscape Pants, 2 Nightscape Boots, and 2 Wildvines to Jangdor Swiftstrider in Camp Mojache.",
    ["D"] = "Your knowledge of Wild Leather armor grows, $n; still, there is more for you to learn.$B$BWild Leather boots are the cornerstone of any set.  Such boots are crafted to cradle the foot in comfort while bracing it against the hazards of adventuring.  The soft soles are a blessing to those seeking quiet movement in footwear.$B$BFor this pattern, I need two Nightscape pants and two Nightscape boots crafted.  I also will require two wildvines... not just one.  When you have these items, we will proceed.",
  },
  [2859] = {
    ["T"] = "Wild Leather Leggings",
    ["O"] = "Bring 2 Turtle Scale Helms, 2 Turtle Scale Bracers, and 2 Wildvines to Jangdor Swiftstrider in Camp Mojache.",
    ["D"] = "You are ready to master more complex lessons, $n.  Wild Leather leggings are some of the sturdiest and powerful leather gear around.  The waist, thighs, calves, and the vitals - all are given tremendous protection.  Coupled with the enhancement that the wildvine provides, adventurers who own it will thank you each time they see you.$B$BFor you to gain this pattern, I need two Turtle Scale helms, two Turtle Scale bracers, and two wildvines.",
  },
  [2860] = {
    ["T"] = "Master of the Wild Leather",
    ["O"] = "Give Jangdor\'s Letter to Una, the master leatherworker of Thunder Bluff.",
    ["D"] = "$n, you have learned all I am able to teach you.  There is, however, one remaining pattern for Wild Leather armor.  I will not require you to toil needlessly for this; you\'ve earned it outright, and then some.  This pattern, however, is one that I cannot freely share with you.$B$BMy teacher, the one who brought me into the craft, is the one who must reward you.  Take this note to Una; she teaches leatherworking in Thunder Bluff.  Show her the letter, and let her acknowledge you as an equal, as I do.",
  },
  [2861] = {
    ["T"] = "Tabetha\'s Task",
    ["O"] = "Speak with Tabetha in Dustwallow Marsh.",
    ["D"] = "Rumor is that Tabetha, a mage colleague who lives deep in Dustwallow Marsh, has a bone to pick with a certain sand troll in Zul\'Farrak.  If you speak with Tabetha she can tell you more.$B$BAnd bring some friends with you, $N.  If Tabetha has you go to Zul\'Farrak then you\'re not going to want to go alone.$B$BYou will find Tabetha\'s cottage west of Theramore, and just north of the Stonemaul Ruin.",
  },
  [2862] = {
    ["T"] = "War on the Woodpaw",
    ["O"] = "Bring 10 Woodpaw Gnoll Manes to Hadoken Swiftstrider in Camp Mojache.",
    ["D"] = "The gnolls... they are more than a nuisance.  They are a constant threat to the existence of this very camp!  It is irrelevant whether they are indigenous to Feralas or not; if we do not strengthen our resolve against them, we will find ourselves driven out of the entire region.$B$BThe gnoll attacks against the camp have become more fevered as of late.  We need your aid in thinning their numbers; bring to me ten of their manes, and I will reward you handsomely for your effort.$B$BGo!",
  },
  [2863] = {
    ["T"] = "Alpha Strike",
    ["O"] = "Kill 5 Woodpaw Alphas and return to Hadoken Swiftstrider within one hour.",
    ["D"] = "The plan is for various leaders of the gnolls - Alphas as they call themselves - to be taken down within rapid succession.  If we\'re able to execute this sort of swift and precise devastation against them, it would cause chaos in their ranks.  Perhaps it would also drive a message through their thick flea-bitten heads that we can and will destroy them!$B$BI want you to be the messenger of my will; eliminate five of their Alphas within one hour for this to work $N, or we\'ll remain at this impasse!",
  },
  [2864] = {
    ["T"] = "Tran\'rek",
    ["O"] = "Speak with Tran\'rek in Gadgetzan.",
    ["D"] = "My cousin Tran\'rek is in a tight spot.  He promised me a load of scarab shells weeks ago and he hasn\'t delivered.  He\'s my cousin, so I\'d hate to have to send someone to Gadgetzan to break something on him.$B$BCan you speak to Tran\'rek for me?  Let him know he needs to get that delivery together before I do something rash?$B$BGadgetzan is in northern Tanaris, in Kalimdor.",
  },
  [2865] = {
    ["T"] = "Scarab Shells",
    ["O"] = "Bring 5 Uncracked Scarab Shells to Tran\'rek in Gadgetzan.",
    ["D"] = "The scarabs of Tanaris have very hard shells!  Hard enough to use as a building material for lots of things.  So many things!$B$BIn fact, those shells are so useful... the scarabs were hunted all to near extinction!$B$BI know where there are more scarabs, and if you promise to bring me their shells then I\'ll tell you where they are.  Promise?$B$BOk, the scarabs have a colony in Zul\'Farrak.  I guess the trolls don\'t hunt them for their shells.$B$BBut you can!  Go to Zul\'Farrak and get me uncracked shells!",
  },
  [2866] = {
    ["T"] = "The Ruins of Solarsal ",
    ["O"] = "Explore the Ruins of Solarsal and investigate the presence of the naga there.",
    ["D"] = "To our south are the Ruins of Solarsal; this is an area that far pre-dates modern times.  We explored the ruins initially and found nothing of note; as such, we left it in peace.$B$BVery recently, the Hatecrest naga have occupied these ruins in alarming numbers.  At first, we considered it as the precursor for an attack.  Though that threat is still possible, we believe there might be something else going on.$B$BGo there, explore the ruins, and look for anything that stands out to you. Good luck, $n.",
  },
  [2867] = {
    ["T"] = "Return to Feathermoon Stronghold",
    ["O"] = "Return to Shandris Feathermoon in the Feathermoon Stronghold and report your findings.",
    ["D"] = "With the discovery of the strange gazebo in the heart of the Ruins of Solarsal, the time has come to return to Feathermoon Stronghold and report to General Shandris Feathermoon what has been discovered here.",
  },
  [2869] = {
    ["T"] = "Against the Hatecrest",
    ["O"] = "Bring 10 Hatecrest Naga Scales to Latronicus Moonspear in Feathermoon Stronghold.",
    ["D"] = "The best course of action at this point is to assume that, regardless of what the naga are up to, they will eventually move to secure the island for their own.  We need to take aggressive steps to counter that threat before it is too late.$B$BBring me ten Hatecrest Scales that are in good condition; it should be obvious to you how to acquire them.  You\'ll cut your teeth on the naga for the first time, and we\'ll have a weakened naga presence on Sardor Isle - both are prospects that play to our advantage.",
  },
  [2870] = {
    ["T"] = "Against Lord Shalzaru",
    ["O"] = "Eliminate Lord Shalzaru, and then bring the Mysterious Relic he uncovered to Latronicus Moonspear in Feathermoon Stronghold.",
    ["D"] = "$n, new scouting reports have given us a good idea on the naga chain of command.  Royalty is present amongst their rank and file, and they\'re the ones calling all the shots.$B$BSpecifically, we\'ve identified one of the crueler royals as Lord Shalzaru.  Scouts report that he himself has uncovered an odd relic during a recent naga excavation in a cave south of the main ruins, across the water on the Isle of Dread.$B$BPut a stop to it by putting a stop to him, and bring me his relic so we can study it.",
  },
  [2871] = {
    ["T"] = "Delivering the Relic",
    ["O"] = "Deliver the Mysterious Relic to Vestia Moonspear in Feathermoon Stronghold.",
    ["D"] = "If you would be so kind as to deliver the relic to my wife Vestia, I would appreciate it.  She\'s not only a fine priestess for our community, but she is one of the most well-versed scholars you\'ll find anywhere.  I couldn\'t make heads or tails of this device even if there was a wager involved.  My wife though, if anyone can figure out what this thing is, she could... and we need to know!$B$BShe\'s just around the bend, no doubt studying.  Thanks, $n.",
  },
  [2872] = {
    ["T"] = "Stoley\'s Debt",
    ["O"] = "Speak with Stoley in Steamwheedle Port.",
    ["D"] = "My old buddy Stoley owes me a very special bottle of rum.  He tends the bar at Steamwheedle Port in Tanaris, Kalimdor,  and he promised me that the rum there is stronger than anything we can find in Booty Bay.  He\'s so confident that he says if I try that rum and don\'t agree with him, then he\'ll double the money he owes me!$B$BWell, I think he\'s not as confident as he was when last we spoke, and now he\'s afraid to give me that rum.  But I don\'t back down from wagers, so why don\'t you talk to him for me...",
  },
  [2873] = {
    ["T"] = "Stoley\'s Shipment",
    ["O"] = "Bring Stoley\'s Shipment to Stoley in Steamwheedle Port.",
    ["D"] = "I owe \"Sea Wolf\" MacKinley in Booty Bay some rum, to settle a bet, but I\'m all out of rum!  Captain Cuergo stole my last shipment, and he has it stowed in that walled pirate town to the south.$B$BIf you can find my shipment of rum and return it to me, then I can prove to MacKinley that Kalimdor\'s booze beats the dirt out of Booty Bay!",
  },
  [2874] = {
    ["T"] = "Deliver to MacKinley",
    ["O"] = "Bring Stoley\'s Bottle to \"Sea Wolf\" MacKinley in Booty Bay.",
    ["D"] = "Thanks again for your help, $N.  Here\'s a bottle of Steamwheedle bilge rum.  Take it to MacKinley and he can finally put to rest his crazy beliefs about Booty Bay booze.",
  },
  [2875] = {
    ["T"] = "WANTED: Andre Firebeard",
    ["O"] = "Bring Andre\'s Head to Security Chief Bilgewhizzle in Steamwheedle Port.",
    ["D"] = "The vile pirate Andre Firebeard is wanted for crimes against Steamwheedle and Gadgetzan.$B$BBy decree of governing counsel, any who read this are hereby authorized to use lethal force during the apprehension of Andre and his abettors.$B$BBy further decree, should Andre be deceased, or otherwise indisposed, during the time of his hearing, his head may act as proxy, and deliverance of such head to Security Chief Bilgewhizzle constitutes a binding, and tenderable, contract.",
  },
  [2876] = {
    ["T"] = "Ship Schedules",
    ["O"] = "Report the Ship Schedules to an authority in Steamwheedle Port.",
    ["D"] = "This scroll contains the schedules of shipments between Steamwheedle Port and Booty Bay. Both past and future shipments are included, along with ship names, with large \'X\'s next to some ships\' journeys, and annotations like \"GREAT LOOT\" and \"HARD FIGHT\" next to others.$B$BSomeone at Steamwheedle Port would want to see this document.",
  },
  [2877] = {
    ["T"] = "Skulk Rock Clean-up",
    ["O"] = "Kill 10 Green Sludges and 10 Jade Oozes, and then report back to Fraggar Thundermantle in Aerie Peak.",
    ["D"] = "We Wildhammers remember the past, more than our brothers in Ironforge.  If you want to make friends here, then you get your hands dirty for us!$B$BThe Horde abandoned their positions in the Hinterlands around Skulk Rock; it\'s now overrun with nasty sludges and oozes.  We\'ve got plans on that area, but first we need a clean-up crew to make a hole for us.$B$BThat\'s where you come in, Sunshine!  Head down there and take out ten green sludges and ten jade oozes, then report back to me here.  Move out!",
  },
  [2878] = {
    ["T"] = "Corrupted Songflower",
    ["O"] = "",
    ["D"] = "",
  },
  [2879] = {
    ["T"] = "The Stave of Equinex",
    ["O"] = "Energize Troyas\' Stave and find the Equinex Monolith.",
    ["D"] = "This stave might be the Stave of Equinex! The Stave of Equinex is actually a key, used to unlock the Equinex Monolith in the Ruins of Ravenwind, on the mainland west of the Dream Bough.$B$BFind the four flames that still burn in those ruins: Samha, Imbel, Byltan, and Lahassa. Retrieve their essence and then while standing by the Equinex Monolith, use the essences to energize the stave. If this is truly is the Stave of Equinex, you will be able to unlock the Monolith and gather a sacred artifact from it.",
  },
  [2880] = {
    ["T"] = "Troll Necklace Bounty",
    ["O"] = "Bring 5 Troll Tribal Necklaces to Fraggar Thundermantle in Aerie Peak.",
    ["D"] = "Get this straight, $r - we are NOT part of the Alliance!  The Wildhammers are independent, relying on deeds and actions to prove one\'s worth, rather than the blather of those who use jibber-jabber to bolster their do-nothing attitudes!$B$BIf you\'re looking for friends here, then prove your worth to us first!  The foul trolls that choke the hillsides of the Hinterlands sometimes carry tribal necklaces.  Bring five of them to me, and we\'ll mark it down as a step in the right direction for you.",
  },
  [2881] = {
    ["T"] = "Troll Necklace Bounty",
    ["O"] = "",
    ["D"] = "",
  },
  [2882] = {
    ["T"] = "Cuergo\'s Gold",
    ["O"] = "Find Cuergo\'s gold!",
    ["D"] = "This map shows a stretch of beach with a southern face.  Below the map are words, elegantly written...$B$B$BSouth of Wheedle$BSouth of home$BFind a mast and flag and bones$BDig you there, if you be bold$BDig and claim Cuergo\'s gold!$BA key you\'ll need and a key you\'ll see$BThe men you face take blood as fee.",
  },
  [2902] = {
    ["T"] = "Woodpaw Investigation",
    ["O"] = "Find the gnoll battle plans somewhere in the gnoll camps to the south of Camp Mojache.",
    ["D"] = "Gnolls aren\'t the sharpest tools in the shed, but they will no doubt have some sort of documented strategy on their recent activity.  If we were to find those plans, then we\'d have a clearer idea of what is making them so worked up.$B$BI want you to head back to the Woodpaw and find anything that might resemble their battle plans.  They have to have something; their attacks have been too coordinated to be done without them.  Find them, and return to me when you have them.",
  },
  [2903] = {
    ["T"] = "The Battle Plans",
    ["O"] = "Bring the Woodpaw Battle Plans to Hadoken Swiftstrider in Camp Mojache.",
    ["D"] = "With the battle plans of the gnolls discovered, all that now remains is to bring the plans back to Hadoken Swiftstrider in Camp Mojache.  Perhaps he or someone there can make further sense of what the gnolls are up to.",
  },
  [2904] = {
    ["T"] = "A Fine Mess",
    ["O"] = "Escort Kernobee to the Clockwerk Run exit and then report to Scooty in Booty Bay.",
    ["D"] = "<Kernobee groans.> Wha.. What happened?$B$BThe last thing I remember is being captured by a group of dark iron dwarves, beaten to within an inch of my life and then thrown in here with these filthy troggs.$B$BGet me out of here, $N!$B$BHere\'s the plan: Lead me to the exit; if we make it out alive, you go to Booty Bay and let Scooty know that I went to Ratchet to warn Tinkerwiz of the dark iron\'s involvement in the making of that super rig.$B$BLet\'s get going!",
  },
  [2922] = {
    ["T"] = "Save Techbot\'s Brain!",
    ["O"] = "Bring Techbot\'s Memory Core to Tinkmaster Overspark in Ironforge.",
    ["D"] = "When our beloved city Gnomeregan fell, a rogue spark must have jolted my poor creation, Techbot.  It once aided me and my associates with its countless gizmos and limitless store of information, but its positronic brain has polarized.  Now it\'s negatronic... and Techbot\'s behaviors are reversed.  It roams Gnomeregan in a frenzy!$B$BBut perhaps it can be salvaged.  Perhaps, if you can retrieve Techbot\'s memory core and bring it to me, I can discover the cause of the polarization and fix my poor Techbot.$B$B",
  },
  [2923] = {
    ["T"] = "Tinkmaster Overspark",
    ["O"] = "Speak with Tinkmaster Overspark in Ironforge.",
    ["D"] = "Tinkmaster Overspark is very upset with the fate of his mechanical creation, Techbot.  I remember Techbot as a kind and helpful tool for the gnomes, but Overspark tells me it\'s malfunctioned.$B$BVery sad.  But with luck perhaps he can be fixed!$B$BSpeak with Tinkmaster Overspark, in Tinker Town in Ironforge.  He would welcome your help.",
  },
  [2924] = {
    ["T"] = "Essential Artificials",
    ["O"] = "Bring 12 Essential Artificials to Klockmort Spannerspan in Ironforge.",
    ["D"] = "A nigh-universal mechanical component--the essential artificial--was of great use to the tinkers and smiths of Gnomeregan.  But during the frantic exodus from our city, no one remembered to take any of these devices with them!  And I need a large supply of them for work I will soon undertake.$B$BPlease, go into Gnomeregan and get me some essential artificials.  You\'ll find them in the deeper areas of our city, held in containers called artificial extrapolators.$B$BThank you, $N, and please hurry!",
  },
  [2925] = {
    ["T"] = "Klockmort\'s Essentials",
    ["O"] = "Speak with Klockmort Spannerspan in Ironforge.",
    ["D"] = "A gnome friend, Klockmort Spannerspan, sent word to me from Ironforge.  He tells me he needs help on a dangerous, but essential, mission into Gnomeregan, the once great gnome city now contaminated and filled with mad gnomes and their devices.$B$BI must stay here, but my heart aches to help my friend.  If you can go in my stead, then I would be much indebted to you.$B$BYou may find Klockmort in Tinkertown, in Ironforge.",
  },
  [2926] = {
    ["T"] = "Gnogaine",
    ["O"] = "Use the Empty Leaden Collection Phial on Irradiated Invaders or Irradiated Pillagers to collect radioactive fallout. Once it is full, take it back to Ozzie Togglevolt in Kharanos.",
    ["D"] = "Since the accident, all Gnoarn thinks about or talks about is cleansing Gnomeregan of the trogg infestation and finding a cure for the sickness that plagues it, $N.$B$BLuckily, Razzle and I are close to some kind of cure - either that or the recipe for a really powerful rum. We won\'t know until we test! Naturally, in order to test our theory, we\'re going to need a few things; namely, fallout from the irradiated troggs in Gnomeregan. Fill up this phial with the green glow and bring it back!",
  },
  [2927] = {
    ["T"] = "The Day After",
    ["O"] = "Speak with Ozzie Togglevolt in Kharanos.",
    ["D"] = "You don\'t think I was always bald, do you? No $g sir:ma\'am;! It was the radiation leak in Gnomeregan that caused this tragic hair loss. Before that day, I had the only gnomefro in all of Azeroth.$B$B<Gnoarn shakes his fist in the air.>$B$BDamn those damned dirty troggs - damn them all!!$B$BPerhaps it\'s not too late, $N. We may still be able to save it - the city I mean! Speak to Ozzie Togglevolt in Kharanos. He and his partner Razzle are working on a solution.",
  },
  [2928] = {
    ["T"] = "Gyrodrillmatic Excavationators",
    ["O"] = "Bring twenty-four Robo-mechanical Guts to Shoni in Stormwind.",
    ["D"] = "It\'s terrible, $N. We are not going to have enough parts to get our fleet of gyrodrillmatic excavationators to Dun Morogh.$B$BWhat we need are spare parts, $N. What better place to get the spare parts for our vehicles than Gnomeregan? I hear that bots and mechs roam around that place like cattle on an open range.$B$BWhat I need you to do is go to Gnomeregan and tear apart those robots. Bring me back their robot guts. Two dozen should be sufficient.",
  },
  [2929] = {
    ["T"] = "The Grand Betrayal",
    ["O"] = "Venture to Gnomeregan and kill Mekgineer Thermaplugg. Return to High Tinker Mekkatorque when the task is complete.",
    ["D"] = "I trusted Thermaplugg, $r. Never did I expect that he would betray me and the entire gnomish people. And for what? Power? Wealth? All things that he would have had in time. Now we have been displaced from our home and that madman is in charge. The king of nothing....$B$BWe will retake Gnomeregan, $N. We will not stop until the city is back in our control. If you wish to join in our fight, a simple task I ask of you: Kill the betrayer. Destroy Mekgineer Thermaplugg.",
  },
  [2930] = {
    ["T"] = "Data Rescue",
    ["O"] = "Bring a Prismatic Punch Card to Master Mechanic Castpipe in Ironforge.",
    ["D"] = "When we fled Gnomeregan, we left so much data behind!  Vitally important data!$B$BI need the data on a prismatic punch card, from a Matrix Punchograph 3005-D high security terminal, deep in Gnomeregan.  You must access the terminal, but to do that you first need clearance... and to get that you must access lower security terminals-- models -A, -B and -C.  And to do THAT you need security punch cards, starting with white... and we left all the punch cards in the city.$B$BIt\'s a catastrophe!  Can you help?",
  },
  [2931] = {
    ["T"] = "Castpipe\'s Task",
    ["O"] = "Speak with Master Mechanic Castpipe in Ironforge.",
    ["D"] = "Master Mechanic Castpipe in Ironforge has need of adventurers!  He\'s organizing bands to reenter Gnomeregan on a data retrieval mission of top security and importance.$B$BCastpipe might not be a member of the Enlightened Assembly of Arcanology, Alchemy and Engineering Sciences like I am, but he\'s still a smart gnome so if he thinks it\'s important, then by my calculations there\'s an eighty-four percent probability that it really is important!$B$BThose are good odds!",
  },
  [2932] = {
    ["T"] = "Grim Message",
    ["O"] = "Gather Witherbark Skulls and place on Nimboya\'s Pike.  Place Nimboya\'s Laden Pike at one of the Witherbark Villages in the Hinterlands, then return to Nimboya in Stranglethorn.",
    ["D"] = "The Witherbark trolls were once allies to the Horde, but they betrayed Thrall... and that must be punished!$B$BHere, take my pike and go to the Hinterlands, far to the north.  You will find it northeast of Tarren Mill, a village held by our allies, the Forsaken.$B$BWhen you enter the Hinterlands, hunt Witherbark trolls.  Gather their skulls and drive my pike into the ground at one of their smaller villages, Hiri\'watha or Zun\'watha.$B$BShow them their vile actions are of consequence!",
  },
  [2933] = {
    ["T"] = "Venom Bottles",
    ["O"] = "Bring a Venom Bottle to an apothecary in Tarren Mill.",
    ["D"] = "These bottles hold a vile, green venom.$B$BConsidering the number of bottles in this village, the Witherbarks must deem it very important.  Perhaps an apothecary in nearby Tarren Mill would like a sample.",
  },
  [2934] = {
    ["T"] = "Undamaged Venom Sac",
    ["O"] = "Bring an Undamaged Venom Sac to Apothecary Lydon in Tarren Mill.",
    ["D"] = "I must have a fresh sample of the venom you brought me.  The spiders from which it was gathered must dwell in the Hinterlands -- hunt them until you find an undamaged venom sac and bring it to me.  You will find many spiders near the troll city of Shadra\'Alor.$B$BDo not tarry, $N, for the venom sac must be fresh when you deliver it.",
  },
  [2935] = {
    ["T"] = "Consult Master Gadrin",
    ["O"] = "Speak with Master Gadrin in Sen\'jin Village.",
    ["D"] = "The venom from the broodguards wasn\'t as potent as the venom you brought originally.  It must have come from a different spider.$B$BI want a fresh sample of that venom.  To find out where it came from, you must consult with a troll who knows the Witherbark well--Master Gadrin in Sen\'jin Village.  Travel to Durotar and speak with Gadrin.  With luck, he\'ll know where the Witherbarks get their venom.",
  },
  [2936] = {
    ["T"] = "The Spider God",
    ["O"] = "Read from the Tablet of Theka to learn the name of the Witherbark spider god, then return to Master Gadrin.",
    ["D"] = "The Witherbarks worship a spider god, and from her they collect their most potent venom.  So if you want the venom, then you must summon their god!$B$BTo do that, you must know her true name, and that name is on a tablet buried in Tanaris, in the troll city of Zul\'Farrak, in a tomb guarded by Theka the Martyr.$B$BDefeat Theka and find and read his tablet to discover the spider god\'s name.  When you know it, return to me.",
  },
  [2937] = {
    ["T"] = "Summoning Shadra",
    ["O"] = "Defeat Shadra at the altar of Shadra\'Alor, then bring her venom to Apothecary Lydon in Tarren Mill.",
    ["D"] = "Take his parchment, and read it at the altar of Shadra\'Alor, deep within the witherbark realm in the Hinterlands.  This will summon Shadra the spider god.",
  },
  [2938] = {
    ["T"] = "Venom to the Undercity",
    ["O"] = "Bring Faranell\'s Parcel to Master Apothecary Faranell in the Undercity.",
    ["D"] = "The venom you gathered from the Witherbark\'s spider god is quite a find!  I\'ve been able to kill nearly two dozen rabbits with just a drop of the stuff.  Granted, some of those rabbits were very young and small enough to fit in the palm of one\'s hand so I\'m certain their resistance to toxins was low, but it can\'t be denied that this poison is powerful!$B$BI\'d like a sample sent to my superior, Apothecary Faranell in the Apothecarium of the Undercity.  I am positive he would want to test this himself.",
  },
  [2939] = {
    ["T"] = "In Search of Knowledge",
    ["O"] = "Talk to Daryn Lightwind in Rut\'theran Village.",
    ["D"] = "While the ruins of Feralas can be quite dangerous, they have much to tell of what has happened here.$B$BSearching through the rubble to the south a few days ago, I discovered what appears to be a normal stave. However, I just can\'t shake the feeling that there is something more to it. Angelas and I have been poring over our books here, but we can\'t find a thing about it.$B$BI have a colleague in Darnassus that may be able to tell us what this is, $N. Why don\'t you go talk to her and see if she can help us?",
  },
  [2940] = {
    ["T"] = "Feralas: A History",
    ["O"] = "Ask Daryn Lightwind if you may borrow her book.",
    ["D"] = "This book looks as if no one has opened it for quite a long time. Its covers are quite worn, and its pages yellowed, but after examining it, you notice that it might be just what Troyas is looking for.$B$BYou pick it up, but realize you should probably ask before borrowing it.",
  },
  [2941] = {
    ["T"] = "The Borrower",
    ["O"] = "Take the letter to Curgle Cranklehop in Tanaris.",
    ["D"] = "I have studied many subjects in my time, and my latest fascination is with the snapjaw that occupy the beach in the Hinterlands. There\'s one in particular I\'d like to see, a giant snapjaw named Gammerita. I\'d like to go myself, but my research keeps me here. I think a picture of her would be the next best thing.$B$BTake this letter to Curgle Cranklehop in Tanaris. She has created an invention for me that can capture a picture. She called it a \"snapshot,\" I think...",
  },
  [2942] = {
    ["T"] = "The Morrow Stone",
    ["O"] = "Return the Sparkling Stone and the Stave of Equinex to Troyas Moonbreeze in Feathermoon Stronghold. ",
    ["D"] = "The moment the artifact is removed from the Monolith, you feel the energy in the Stave of Equinex begin to fade.",
  },
  [2943] = {
    ["T"] = "Return to Troyas",
    ["O"] = "Deliver the book to Troyas Moonbreeze in Feathermoon Stronghold.",
    ["D"] = "Here it is, $N. Please, take care of my book.$B$BNow, hurry along. I\'m sure Troyas is eager for your return.",
  },
  [2944] = {
    ["T"] = "The Super Snapper FX",
    ["O"] = "Use the Super Snapper FX to take a snapshot of Gammerita, then return to Daryn Lightwind in Rut\'theran Village.",
    ["D"] = "Have the first look at my new invention, $N. All you need to do is target whatever it is you\'d like to take a picture of, and push the button.$B$BWhat was that creature from the Hinterlands that you mentioned? Gammerita? Well, good luck finding her. I\'m sure Daryn will be quite pleased with the snapshot you return to her.$B$BIn any case, here\'s the Super Snapper. Have fun!",
  },
  [2945] = {
    ["T"] = "Grime-Encrusted Ring",
    ["O"] = "Figure out a way to remove the grime from the Grime-Encrusted Ring.",
    ["D"] = "You have uncovered what appears to be a ring from the corpse of one of the Dark Iron dwarves.  It is difficult to tell what kind of ring it is, however; dirt and hardened residue cake the item completely, making wearing it impossible.  You think you can make out the slight luster of precious metal and maybe even the glint of a gem under the residue.$B$BIf you are going to make use of this ring, you are going to need to find something to get the hardened residue off of it.",
  },
  [2946] = {
    ["T"] = "Seeing What Happens",
    ["O"] = "Bring the miniature platinum discs to the gates of Uldum in Tanaris and look for clues as to the connection between the two.",
    ["D"] = "We want you to take the miniature discs you found to the sealed gates of Uldum.  We... we aren\'t sure what will happen when you do this.$B$BThis sounds risky, sure, we acknowledge that.  It might, however, do nothing.  You might need to use the discs on something.  Perhaps the doors unlock when you have the discs in your possession.  Be open to try anything, and be ready for anything to happen.$B$BWhen you\'ve found out what happens, return to Ironforge.  We\'ll have a lot to analyze when you\'re done!",
  },
  [2947] = {
    ["T"] = "Return of the Ring",
    ["O"] = "You may either keep the ring, or you may find the person responsible for the imprint and engravings on the inside of the band.",
    ["D"] = "Opening the box produced by the Sparklematic 5200 reveals what you had suspected - a shiny ring!  Wearing the ring, you feel magic power surge through you body.$B$BInside the band is a small but nonetheless discernable imprint of the seal of Ironforge.  Next to that, three small initials are engraved: \"TdK\".  This may signify either the initials of the previous owner, or perhaps the initials of the ring\'s crafter.  Keep the ring... or return it to this person.  Decisions, decisions...",
  },
  [2948] = {
    ["T"] = "Gnome Improvement",
    ["O"] = "Bring the Brilliant Gold Ring, a Silver Bar, a Moss Agate, and 30 silver coins to Talvash del Kissel in Ironforge.",
    ["D"] = "Listen $n, that ring is yours, sure... but I can make you a better one!  That design, like I said, is really simple.  I am a lot more skilled now, and for the mere fee of thirty silver coins I\'ll spruce it up for you!  Well, a little more than thirty, actually...$B$BI\'m in a tight spot right now; all my material stock is all dried up.  If you bring me a bar of silver and a moss agate, I\'ll add a silver and agate lattice to the ring that will give it a little more pizzazz!$B$BTrust me!",
  },
  [2949] = {
    ["T"] = "Return of the Ring",
    ["O"] = "You may either keep the ring, or you may find the person responsible for the imprint and engravings on the inside of the band.",
    ["D"] = "Opening the box produced by the Sparklematic 5200 reveals a shiny ring!  Donning the ring, you feel magic power surge through your body.$B$BInside the band is a small but nonetheless discernable imprint of the seal of Orgrimmar.  Engraved letters are next to it, but only the first three stand out even after cleaning: \"NOG...\"$B$BThis may signify either the initials of the previous owner, or perhaps the initials of the ring\'s crafter.  Keep the ring... or find \"NOG\" - decisions, decisions...",
  },
  [2950] = {
    ["T"] = "Nogg\'s Ring Redo",
    ["O"] = "Bring the Brilliant Gold Ring, a Silver Bar, a Moss Agate, and 30 silver coins to Nogg in Orgrimmar.",
    ["D"] = "No, no, no - I\'m not all of the sudden saying that the ring is mine.  I normally don\'t do this, but your information about the Dark Irons was very useful.  I\'ll improve your ring for you, better than whomever made this simple little thing could have ever done.  Probably some gnome...$B$BIf you bring me a bar of silver, a moss agate, and thirty silver coins for my trouble, I\'ll add a silver and agate lattice to the ring that will strengthen the magic inside of it.  Interested?",
  },
  [2951] = {
    ["T"] = "The Sparklematic 5200!",
    ["O"] = "Insert a Grime-Encrusted Item into the Sparklematic 5200, and be sure to have three silver coins to start the machine.",
    ["D"] = "This contraption stands out amongst the gnomes holding out against the madness outside the room.  A small plaque on the device reads \"The Sparklematic 5200\", followed by some text:$B$B\"Grime and residue ruining your sparklies?  The Sparklematic 5200 puts the shine back in shine-tastic!  Insert the item you wish to clean and deposit three silver coins in the coin slot.  Give the Sparklematic 5200 a moment for operation, and voila!  Your valuables will emerge clean and shiny!\"",
  },
  [2952] = {
    ["T"] = "The Sparklematic 5200!",
    ["O"] = "",
    ["D"] = "",
  },
  [2953] = {
    ["T"] = "More Sparklematic Action",
    ["O"] = "",
    ["D"] = "",
  },
  [2954] = {
    ["T"] = "The Stone Watcher",
    ["O"] = "Learn the purpose of the Stone Watcher of Norgannon, and then interact with the Uldum Pedestal in the Tanaris desert.",
    ["D"] = "\"Salutations - I am a guardian of entry.  Unless you have the Plates of Uldum already integrated with your disc set, I will not allow for entry into the Uldum compound.\"$B$BThe appearance of the Stone Watcher of Norgannon must be the reaction that was hoped for in sending you out to the Tanaris desert with the platinum discs.  As instructed, you will need to find out what the story behind the Stone Watcher is before continuing on.",
  },
  [2962] = {
    ["T"] = "The Only Cure is More Green Glow",
    ["O"] = "Travel to Gnomeregan and bring back High Potency Radioactive Fallout. Be warned, the fallout is unstable and will collapse rather quickly.$B$BOzzie will also require your Heavy Leaden Collection Phial when the task is complete.",
    ["D"] = "Not only was your sample insufficient in quantity, it was also impure.$B$BWhat we require is more green glow. The troggs are obviously not irradiated sufficiently to fulfill our needs. You will have to go into Gnomeregan and bring back some radioactive fallout, straight from the tap. I am speaking of the irradiated slimes, lurkers and horrors. Be warned, the containment of this type of fallout is difficult. You may need to make several attempts with the phial until a suitable sampling is recovered.",
  },
  [2963] = {
    ["T"] = "Portents of Uldum",
    ["O"] = "Speak with Historian Karnik in the Hall of Explorers within Ironforge.",
    ["D"] = "Greetings once again, $n!  Your discovery inside Uldaman has set the Explorers\' League abuzz with excitement.  The League has been working to unlock the secrets of the miniature platinum discs you found.  Your fortuitous arrival here in the Hall allows me to share with you that we are quite possibly on to something!$B$BHistorian Karnik has uncovered some important clues as to the true origins of the Valley of the Watchers in the Tanaris desert.  He would like to speak with you as soon as possible!",
  },
  [2964] = {
    ["T"] = "A Future Task",
    ["O"] = "Speak with High Explorer Magellas in Ironforge.",
    ["D"] = "Well $n... we seem to be at a temporary dead end here.  We know what we need - the Plates of Uldum - but we have no idea where they might be.  We\'ll need to do quite a bit of research on this to come up with a solid plan of attack.  I have a couple ideas where to start looking; I have high hopes that soon, we\'ll be asking you to head out and recover them.$B$BThe High Explorer needs to be informed on this, $n; no doubt, he also wants to thank you personally for once again aiding the League.",
  },
  [2965] = {
    ["T"] = "Portents of Uldum",
    ["O"] = "Speak with Nara Wildmane on the Elder Rise in Thunder Bluff.",
    ["D"] = "Ah, $n, it is fortuitous that you have come to me again.  Your revelations at the Uldaman dig site have certainly given us sages much discuss.  I was doing just that with one of the druids, Nara Wildmane, when it dawned on me that her past adventuring experiences may have some great importance as to the next step we need to take.$B$BI would like for you to speak with her concerning her travels to the Valley of the Watchers in the Tanaris desert.  She waits for you on the Elder Rise.",
  },
  [2966] = {
    ["T"] = "Seeing What Happens",
    ["O"] = "Bring the miniature platinum discs to the gates of Uldum in Tanaris and look for clues as to the connection between the two.",
    ["D"] = "Sage Truthseeker and I would like you to take the miniature discs you found to the sealed gates of Uldum.  We... we aren\'t sure what will happen when you do this.$B$BThis sounds risky, sure, we acknowledge that.  It might, however, do nothing.  You might need to use the discs on something.  Perhaps the doors unlock when you have the discs in your possession.  Be open to try anything, and be ready for anything to happen.$B$BLearn what you can, then return to me. We will have much to discuss.",
  },
  [2967] = {
    ["T"] = "Return to Thunder Bluff",
    ["O"] = "Speak with Nara Wildmane in Thunder Bluff.",
    ["D"] = "You access the pedestal again; the platinum discs suddenly appear once more in your backpack.$B$BWith the knowledge of the stone watcher learned and the discs returned to your possession, the time has come to return to Thunder Bluff and inform the Nara Wildmane of your discovery.",
  },
  [2968] = {
    ["T"] = "A Future Task",
    ["O"] = "Speak with Sage Truthseeker in Thunder Bluff.",
    ["D"] = "Well $n... we seem to be at an unfortunate impasse.  We know what we need - the Plates of Uldum - but we have no idea where they might be.  We will research this, and then devise a solid plan of attack.  I have a couple ideas where to start looking; I have high hopes that soon, we\'ll be asking you to rejoin our efforts to discover the truth.$B$BSage Truthseeker needs to be informed on this, $n; no doubt, he also wants to thank you personally for once again heeding the call.",
  },
  [2969] = {
    ["T"] = "Freedom for All Creatures",
    ["O"] = "Using the Bamboo Cage Key, free the Sprite Darters from the pen in the Grimtotem camp. At least 6 Sprite Darters and Kindal Moonweaver must survive.$B$BYou have 6 minutes before Kindal decides it is time to retreat and you fail the quest.",
    ["D"] = "The filthy Grimtotem clan are capturing faerie dragons, $N. Jer\'kai and I have no idea what they plan on doing with them, but they\'re obviously up to no good.$B$BWe\'re not powerful enough to fight our way into the camp and free the captured dragons, but perhaps with your help...$B$BIf you\'re willing, and you have some strong friends, release the creatures from their cage with this key and make sure they escape with their lives. I\'ll be by your side the entire time. Jer\'kai will remain here.",
  },
  [2970] = {
    ["T"] = "Doling Justice",
    ["O"] = "Kill 12 Grimtotem Naturalists, 10 Grimtotem Raiders, and 6 Grimtotem Shamans before returning to Jer\'kai Moonweaver in Feralas.",
    ["D"] = "As she\'s already told you, Kindal and I will now take the fight to the Grimtotem. Together, we should still be able to pick off some of the tauren without much threat to ourselves, but we\'d love some help if you\'re willing and able.$B$BYou\'ve seen the camp below us, but there are more to the east. Using your own tactics, deal as much damage to the tauren as you can. We can all meet back here after they\'ve felt the sting of Kindal\'s arrows and your own weapons.",
  },
  [2972] = {
    ["T"] = "Doling Justice",
    ["O"] = "Take Jer\'kai\'s Signet Ring to High Priestess Tyrande Whisperwind in Darnassus.",
    ["D"] = "Out here in the wilderness, Kindal and I have meager means to reward your help, but we don\'t want you to feel unappreciated. Take my signet ring. If you ever find yourself in the boughs of Teldrassil, go to the Temple of the Moon in the heart of Darnassus. There you will find my High Priestess Tyrande Whisperwind. The ring will let her know that we are well and give credit to your story.$B$BWe both thank you, $N.",
  },
  [2973] = {
    ["T"] = "A New Cloak\'s Sheen",
    ["O"] = "Bring 10 Iridescent Sprite Darter Wings to Krueg Skullsplitter in Camp Mojache.",
    ["D"] = "Well met, $c. I\'ve traveled with my companions down to the Feralas on a mission for our Orgrimmar masters.$B$BMy life-mate Korrin finds great pleasure in new clothing, so I aim to make her a new cloak adorned with the gossamer wings of faerie dragons. Would you be able to help me? Sprite darters with gorgeous wings I want are west of Camp Mojache.",
  },
  [2974] = {
    ["T"] = "A Grim Discovery",
    ["O"] = "Bring 20 Grimtotem Horns to Krueg Skullsplitter in Camp Mojache.",
    ["D"] = "What!? You came across tauren in the area? You\'re joking! What did they look like!?$B$BThe Grimtotem clan! You found some of them? Belgrom, one of our masters, has an interest in their kind... he\'s even put a bounty on them.$B$BYou have to go back, $c. Go back and bring me their horns... piles of them if you can. The Grimtotem are not to be trifled with or underestimated. Go back and slay them; let the rivers in Feralas run red with their dark blood.",
  },
  [2975] = {
    ["T"] = "The Ogres of Feralas",
    ["O"] = "Rok Orhan in Camp Mojache wants you to kill 10 Gordunni Ogres, 10 Gordunni Ogre-Magi, and 5 Gordunni Brutes.",
    ["D"] = "The effects of the Gordunni Ogre tribe can hardly go unnoticed in this area. Look around, $N; their very presence scars this land. We must push them back before they expand even further into Feralas.$B$BFollow the river north, and you will find their outpost. Decrease their numbers, $N. We must show them that their kind is not wanted here.",
  },
  [2976] = {
    ["T"] = "A Grim Discovery",
    ["O"] = "Bring the Crate of Grimtotem Horns to Belgrom Rockmaul in Orgrimmar.",
    ["D"] = "What?!$B$BAh, so I did mention a bounty, didn\'t I? Well, that\'ll teach me to open my mouth, won\'t it?$B$BYou did the work, so I suppose it is only right you reap the greatest reward from it. Take the horns, I\'ll have them put in a crate for you. Find Belgrom Rockmaul in the Valley of Honor, he\'ll want to know what you saw and he\'ll reward you for your hard work.$B$BBe well, $c, and thank you again for everything you\'ve done.",
  },
  [2977] = {
    ["T"] = "Return to Ironforge",
    ["O"] = "Speak with Historian Karnik in Ironforge.",
    ["D"] = "You access the pedestal again; the platinum discs suddenly appear once more in your backpack.$B$BWith the knowledge of the stone watcher learned and the discs returned to your possession, the time has come to return to Ironforge and inform the Explorers\' League of your discovery.",
  },
  [2978] = {
    ["T"] = "The Gordunni Scroll",
    ["O"] = "Bring the Gordunni Scroll to Rok Orhan in Camp Mojache.",
    ["D"] = "Picking up the soiled scroll, you notice that it seems to be depicting some sort of meeting or ceremony. Rok Orhan would be interested in this scroll, and what it might mean to the Gordunni.",
  },
  [2979] = {
    ["T"] = "Dark Ceremony",
    ["O"] = "Rok Orhan in Camp Mojache wants you to find a Gordunni Orb.",
    ["D"] = "These seem to be plans for some sort of dark ceremony, $N. From what I can make out, this ritual is conducted by the Gordunni mage-lords.$B$BFind one of these mage-lords, and from him, gather an orb.$B$BThe orb of a mage contains great power, and will help us discover more about this ceremony and the magic that the Gordunni wield.",
  },
  [2980] = {
    ["T"] = "The Ogres of Feralas",
    ["O"] = "Rok Orhan in Camp Mojache wants you to kill 10 Gordunni Shaman, 10 Gordunni Warlocks, and 5 Gordunni Maulers.",
    ["D"] = "Now that you have proven that you are a true fighter, I trust that you possess the courage to continue with the task at hand.$B$BTravel to the High Wilderness, southwest of here. There lie some abandoned and destroyed structures, on land that once belonged to the night elves. Look for these ruins, and when you see the withered trees and desecrated terrain, you will find the Gordunni. Push them back.$B$BThese ogres are much stronger than the ones you faced previously, $N. Be aware.",
  },
  [2981] = {
    ["T"] = "A Threat in Feralas",
    ["O"] = "Belgrom Rockmaul wants you to speak with Rok Orhan in Camp Mojache.",
    ["D"] = "Feralas is an ideal location for an accomplished $c to prove $Ghimself:herself;. A tribe of ogres called the Gordunni are the dominant military force there, and their numbers are growing. They must be stopped before they threaten our presence in Feralas.$B$BA warrior I know of named Rok Orhan resides at Camp Mojache in Feralas; go to her. She will tell you more of the situation when you arrive.",
  },
  [2982] = {
    ["T"] = "The High Wilderness",
    ["O"] = "Angelas Moonbreeze wants you to kill 8 Gordunni Warlocks, 8 Gordunni Shaman, and 8 Gordunni Brutes.",
    ["D"] = "Although it is dangerous, Troyas and I have been investigating the ruins that cover the forests of Feralas.$B$BLately, though, Troyas has been going on his own to the High Wilderness, and I really worry about him. The Gordunni Ogres have taken over that area, and they don\'t take kindly to anyone that enters their territory.$B$BI was hoping you might be willing to travel to the ruins to the southeast and clear out some of the ogres. Make it a little safer for my Troyas, and I will be extremely grateful.",
  },
  [2983] = {
    ["T"] = "Call of Fire",
    ["O"] = "Find Kranal Fiss in the Barrens.",
    ["D"] = "It\'s been some time since you were tested last, $N. You\'ve been patient thus far in your studies, but it\'s time for you to learn about greater powers... it\'s time you learn real power. The element of fire is yours if you\'re prepared to pay for it.$B$BSeek out Kranal Fiss in the Barrens. He lives in a small dwelling north of the Crossroads, but be mindful, if you\'ve reached the blasted night elf lands, you\'ve gone way too far.",
  },
  [2984] = {
    ["T"] = "Call of Fire",
    ["O"] = "Find Kranal Fiss in the Barrens.",
    ["D"] = "Long has it been since you were last tested, $N. You have shown patience thus far in your studies, but the time has come for you to learn more. The time has come for you to learn about destruction... and chaos. The element of fire is yours for the understanding, if you\'re prepared to pay for it.$B$BSeek out Kranal Fiss in the Barrens. He lives in a small dwelling north of the Crossroads, but be mindful, if you\'ve reached the night elf lands, you\'ve gone too far.",
  },
  [2985] = {
    ["T"] = "Call of Water",
    ["O"] = "Find Islen Waterseer in the Barrens.",
    ["D"] = "You look ready for your next test, $N. You\'ve been patient in your studies, but the time has come for you to learn more. The time has come for you to learn about the purity of water.$B$BSeek out Islen Waterseer in the Barrens. She is along the eastern coast, south of the Southfury River and Ratchet. You will find her there at her fishing hut. Good luck to you, $N.",
  },
  [2986] = {
    ["T"] = "Call of Water",
    ["O"] = "Find Islen Waterseer in the Barrens.",
    ["D"] = "Long has it been since you were last tested, $N. You have shown patience thus far in your studies, but the time has come for you to learn more. The time has come for you to learn about the purity of water.$B$BSeek out Islen Waterseer in the Barrens. She is along the eastern coast, south of the Southfury River and Ratchet. You will find her there at her fishing hut. Good luck to you, $N.",
  },
  [2987] = {
    ["T"] = "Gordunni Cobalt",
    ["O"] = "Orwin Gizzmick in Camp Mojache wants 12 samples of Gordunni Cobalt.",
    ["D"] = "I just arrived here in Feralas, and I\'ve heard that the Gordunni Ogres that inhabit the wilds here have some interesting minerals. Who knows what they are up to, but they sure are protective of that stuff! They don\'t leave it just lying around; no, they bury it to protect folks like me from getting their hands on it.$B$BIf you\'d be willing, I\'ll give you my shovel. Go to the Gordunni Outpost north of here and dig me up some Gordunni cobalt. Look for the blue glow, then start digging!",
  },
  [2988] = {
    ["T"] = "Witherbark Cages",
    ["O"] = "Check the cages at the two Witherbark villages, then return to Gryphon  Master Talonaxe.",
    ["D"] = "The son of our mightiest gryphon is gone! Swiftwing\'s heir, Sharpbeak, was lost while hunting with his parents a few days ago, and we believe it was the trolls who took him.$B$BYou must aid us!  Search the cages in the two Witherbark troll villages to see if Sharpbeak is there.",
  },
  [2989] = {
    ["T"] = "The Altar of Zul",
    ["O"] = "Check the top of the Altar of Zul, then return to Gryphon Master Talonaxe.",
    ["D"] = "Sharpbeak was not with the Witherbark trolls.  Perhaps the Vilebranch trolls took him.  Go to the Altar of Zul on the western edge of Vilebranch territory and search for the gryphon child. ",
  },
  [2990] = {
    ["T"] = "Thadius Grimshade",
    ["O"] = "Bring Sharpbeak\'s Feather to Thadius Grimshade in the Blasted Lands.",
    ["D"] = "Take this feather to Thadius Grimshade, the diviner.  He left Aerie Peak years ago to pursue darker knowledge, but in his heart he is still a Wildhammer!$B$BAnd perhaps his talents can help us find Sharpbeak.$B$BThis feather was taken from the nest where the gryphon child sleeps.  If you give it to Thadius then, with luck, he can use his divining magics to find Sharpbeak.$B$BGood luck, $N.  You will find Thadius in the Blasted Lands, in the mage tower of Nethergarde Keep.",
  },
  [2991] = {
    ["T"] = "Nekrum\'s Medallion",
    ["O"] = "Bring Nekrum\'s Medallion to Thadius Grimshade in the Blasted Lands.",
    ["D"] = "I\'ll find out where your gryphon is, but first you have to do something for me.  There\'s a troll in Zul\'Farrak, Nekrum Gutchewer, who possesses a medallion I would very much like to study.  Bring me that Medallion and I\'ll use my powers to find Sharpbeak.$B$BZul\'Farrak is across the ocean to the west, in the desert of Tanaris.",
  },
  [2992] = {
    ["T"] = "The Divination",
    ["O"] = "Wait until Grimshade performs the ritual of divination.",
    ["D"] = "Give me room while I perform a ritual of divination.  I will find the connection between the feather you brought me and its owner, and follow that connection to Sharpbeak himself.$B$BThat is... I will if all goes according to plan.  If it does not, then... you better close your eyes.",
  },
  [2993] = {
    ["T"] = "Return to the Hinterlands",
    ["O"] = "Speak with Gryphon Master Talonaxe.",
    ["D"] = "Sharpbeak is held in a cage, high on a cliff at the top of Jintha\'Alor in the Hinterlands.  The key to this cage is possessed by the high priestess of the Vilebranch trolls, Vile Priestess Hexx.$B$BThere.  I have done my part.  I bid you fortune on your quest, $N.",
  },
  [2994] = {
    ["T"] = "Saving Sharpbeak",
    ["O"] = "Fight to the top of Jintha\'Alor, kill Vile Priestess Hexx for the key to Sharpbeak\'s cage, and save Sharpbeak.",
    ["D"] = "We must save Sharpbeak!$B$BFight your way to the top of Jintha\'Alor in the heart of Vilebranch territory, gain the key to Sharpbeak\'s cage from the vile Priestess Hexx, and then find and save Sharpbeak!",
  },
  [2995] = {
    ["T"] = "Lines of Communication",
    ["O"] = "Oran Snakewrithe in Undercity wants you to travel to the Hinterlands and burn the Highvale Records, Highvale Report, and Highvale Notes.",
    ["D"] = "The Quel\'Danil Lodge is a center of communication for the high elves of the Hinterlands. Their ambassadors are working to establish relationships with other races, with whom they share their knowledge of the Witherbark trolls that inhabit the area in the southern Hinterlands. All of the documents regarding the communications are kept at the lodge.$B$BWe don\'t need the Highvale building alliances with others, $N.$B$BDestroy the Highvale documents.  We need to burn their lines of communication... literally.",
  },
  [2996] = {
    ["T"] = "Seeking Strahad",
    ["O"] = "Speak to Strahad Farsan in Ratchet.",
    ["D"] = "I have taught you skills in the past, but the time has come for another to teach you. He is in Ratchet, and his name is Strahad. He is human, but do not let that fool you into thinking he is weak. He is powerful even though he may be small. He can teach you about more powerful summonings--I feel you are ready. Go to him now. He is in Ratchet... above the port town next to his tower.",
  },
  [2997] = {
    ["T"] = "Tome of Divinity",
    ["O"] = "Speak to Tiza Battleforge in Ironforge.",
    ["D"] = "Hello, $N. I know you\'re busy helping out the people of Dun Morogh, but when you have the time, you should make your way up to Ironforge to speak to one of our most respected ilk: Tiza Battleforge. She\'s the one the younger paladins go to when it\'s time they learn more about the Light and what\'s expected of ya.$B$BYou can find her upstairs in the Hall of Mysteries. If you\'ve never been there, it\'s in the Mystic Ward. Good luck to ya, $N.",
  },
  [2998] = {
    ["T"] = "Tome of Divinity",
    ["O"] = "Speak to Duthorian Rall in Stormwind.",
    ["D"] = "Greetings, $Gbrother:sister;. Duthorian Rall, one of our most influential paladins, was speaking about you recently. I overheard him talking about how it was time you be put to the test some... along with some very complimentary things about how well you\'ve performed thus far.$B$BIf I were you, I\'d head to Stormwind and speak to him when you have time. I\'m sure he\'s in the Cathedral of Light--that\'s where he spends most of his time.",
  },
  [2999] = {
    ["T"] = "Tome of Divinity",
    ["O"] = "Speak to Tiza Battleforge in Ironforge",
    ["D"] = "Hello, $N. I know you\'re busy helping out the people of Dun Morogh, but when you have the time, you should go upstairs and speak to one of our most respected ilk: Tiza Battleforge. She\'s the one the younger paladins go to when it\'s time they learn more about the Light and what\'s expected of ya.",
  },
  [3000] = {
    ["T"] = "Tome of Divinity",
    ["O"] = "Speak to Tiza Battleforge in Ironforge.",
    ["D"] = "Hello, $N. I know you\'re busy helping out the people of Azeroth, but when you have the time, you should return to Ironforge and speak to one of the most respected paladins of your home: Tiza Battleforge. She\'s the one the younger dwarven paladins go to when it is time they learn more about the Light and what\'s expected of all of us.$B$BYou can find her upstairs in the Hall of Mysteries which is located in the Mystic Ward.",
  },
  [3001] = {
    ["T"] = "Seeking Strahad",
    ["O"] = "Find Strahad Farsan in Ratchet.",
    ["D"] = "I have taught you skills in the past, but the time has come for another to teach you. He is in Ratchet, and his name is Strahad. He is human, but do not let that fool you into thinking he is weak, nor is he your enemy... at least for now. He is powerful and should be respected. He can teach you about greater summonings, summonings I feel you are prepared for. Go to him now.$B$BYou can find him in Ratchet... above the port town next to the tower there.",
  },
  [3002] = {
    ["T"] = "The Gordunni Orb",
    ["O"] = "Bring the Gordunni Orb to Uthel\'nay in the Darkbriar Lodge in Orgrimmar.",
    ["D"] = "Although I know my enemy well, I am a warrior, $N. I cannot begin to tell you what this orb is capable of.$B$BI know of a mage in Orgrimmar that will be able to tell us more about the orb. Take it to him. ",
  },
  [3022] = {
    ["T"] = "Handle With Care",
    ["O"] = "Bring the Carefully-packed Crate to Erelas Ambersky in Rut\'theran Village.",
    ["D"] = "$N, I can see you yearn to be dedicated to my worthwhile cause. Yes, of course, you can do something for me!$B$BTake this crate of eggs to Erelas Ambersky. He is a druid that cares for these eggs, helping them to hatch and then training the young hippogryphs. You will find him in Teldrassil, in a small village to the south called Rut\'theran.",
  },
  [3042] = {
    ["T"] = "Troll Temper",
    ["O"] = "Bring 20 Vials of Troll Temper to Trenton Lighthammer in Gadgetzan.",
    ["D"] = "The Sandfury trolls of Zul\'Farrak make a tempering agent from sandstones, and I can use that to temper the goods I craft, yes I can!  It is highly prized by connoisseurs, so if you bring me a good supply of the temper, then I\'ll reward you well.$B$BZul\'Farrak is northwest of Gadgetzan.  Good luck.",
  },
  [3062] = {
    ["T"] = "Dark Heart",
    ["O"] = "Talo Thornhoof in Camp Mojache wants Edana Hatetalon\'s Dark Heart.",
    ["D"] = "Edana Hatetalon is the queen of the Northspring harpies that reside in the ruins to the west. She is pure evil; I have heard that her heart is one of crystal. She is void of any emotion aside from the hate she feels towards all others. You must face Edana and bring me back her dark heart.$B$BI have heard of a horn that harpies carry -- blowing it by the Hatetalon stones, in the northern part of the ruins, will summon the queen.$B$BImagine her surprise when it is not her kin calling her, but her death.",
  },
  [3063] = {
    ["T"] = "Vengeance on the Northspring",
    ["O"] = "Talo Thornhoof wants you to kill 4 Northspring Harpies, 4 Northspring Roguefeathers, 4 Northspring Windcallers, and 4 Northspring Slayers.",
    ["D"] = "In my day, I was a seasoned hunter. There wasn\'t any creature around that I feared. Until...$B$BI was out in the forest, sharpening my blade after a tough battle, when I was ambushed. Harpies surrounded me, and I barely managed to drag myself to safety.$B$BEven now, I can still hear their cries echoing...$B$BI was never the same after that. But the hate boils inside of me -- I must get even, after all these years.$B$BThe Northspring harpies can be found to the west of here. $N, get my revenge. ",
  },
  [3065] = {
    ["T"] = "Simple Tablet",
    ["O"] = "Read the Simple Tablet and speak to Frang in the Valley of Trials.",
    ["D"] = "Ah, while you were gone a parchment came for you, $N.$B$BRead it when you have time. If I\'m not mistaken, it came from the warrior trainer Frang. He would have words with you when you\'re ready. You\'ll find him outside the Den, taking cover in the shade.",
