pfDB["quests"]["esES-tbc"] = {
  [1] = {
    ["D"] = "$Tpunk;!  Kill Kobold Vermin, 2 of em. NEW TEST AGAIN",
    ["O"] = "Matar alimaña kóbold, dos.",
    ["T"] = "Misión de Kanrethad",
  },
  [2] = {
    ["D"] = "El poderoso hipogrifo Garfafilada ha sido ejecutado, con la garfa de la bestia derribada como testimonio de tu victoria. Seguro que Senani Corazón Atronador, del Refugio Brisa de Plata, estará interesado en ver este trofeo que prueba tus actos.",
    ["O"] = "Llévale la garfa de Garfafilada a Senani Corazón Atronador en el Refugio Brisa de Plata, Vallefresno.",
    ["T"] = "Garfa de Garrafilada",
  },
  [5] = {
    ["D"] = "¡Llevo semanas escondiéndome en esta ciudad fantasma y no he comido más que larvas y hierbajos! Necesito algo de comida decente. Estoy en disposición de pagar bien.$B$BSi me traes un festín, te pagaré generosamente.$B$BHe oído que el chef Grual del Mesón del Cuervo Escarlata de Villa Oscura hace unos estupendos pasteles de cangrejo oscuro...",
    ["O"] = "Habla con el Chef Grual.",
    ["T"] = "La hambruna de Alterio",
  },
  [6] = {
    ["D"] = "Garrick Piesuaves es un maleante que lleva semanas acosando a granjeros y mercaderes. Ha sido visto en una choza cerca de los viñedos, cruzando el puente al este de la abadía. Tráeme la cabeza de ese villano y podrás quedarte con su botín.$B$BPero ve con cuidado, $N. Garrick anda con una pandilla de matones. Puede que no te sea fácil llegar hasta él.",
    ["O"] = "Mata a Garrick Piesuaves y lleva su cabeza al ayudante de alguacil Willem a la Abadía de Villanorte.",
    ["T"] = "La recompensa por Garrick Piesuaves",
  },
  [7] = {
    ["D"] = "$N, lo primero que tienes que hacer es limpiar; un clan de kóbolds ha invadido los bosques del norte. Ve y liquida a todos los kóbolds que encuentres; si reducimos sus filas podremos echarlos de Villanorte.",
    ["O"] = "Mata a 6 alimañas kóbold y ve a hablar con el alguacil McBride.",
    ["T"] = "Kobold Camp Cleanup [Programming Test]",
  },
  [8] = {
    ["D"] = "¡Eh, colega! ¿Le harías un favor a un joven hombre que ha combatido contra más zombis y arañas de los que puede contar?$B$BTengo una carta que tiene que llegar a Rémol... Es para una tabernera llamada Renee no sé qué. No creo que importe cómo se apellida, la verdad.$B$BPero el sitio es muy acogedor y está lleno de víctimas de la Plaga que intentan abrirse paso en el mundo. Es el sitio ideal para descansar... si es que necesitas hacerlo. Anímate... si vas, te pagaré bien.",
    ["O"] = "Lleva la carta anodina a Claros de Tirisfal y entrégasela a la tabernera Renee.",
    ["T"] = "Un trato de pícaro",
  },
  [9] = {
    ["D"] = "¡Mira lo que le ha ocurrido a este lugar! Estas tierras estaban antes en manos de buenos granjeros. Pero los condenados ladrones los han ahuyentado a todos. ¡Pero no a mí! Eso sí, parece que algunos vigías de la cosecha han invadido los campos.$B$BSi estás $Gpreparado:preparada; me gustaría que fueras y mataras a 20 de ellos. Vuelve cuando hayas terminado y te pagaré. Si terminas con los de mi campo, puedes limpiar también los campos vecinos.",
    ["O"] = "El granjero Saldean quiere que mates a 20 vigías de la cosecha.",
    ["T"] = "Campos de la muerte",
  },
  [10] = {
    ["D"] = "¡Oh, esto es horrible! Envié a mi mejor ayudante, el perito adjunto Patagolpe, al desierto situado al sudeste de aquí, una zona que se conoce como Sima Abierta. Bien, pues ha desaparecido y su último informe contenía más información acerca de esos extraños bichos que había visto por la zona. ¿Es posible que ya hayan infestado dos grandes regiones del desierto?$B$BPor favor, ¡necesito que vayas a Sima Abierta en busca de Patagolpe! Y si no lo encuentras a él, al menos a ver si aparece su equipo de análisis. Me temo que pueda ser lo único que quede de ese pobre chico.",
    ["O"] = "Descubre qué le ha ocurrido al perito adjunto Patagolpe y llévalo a él o su equipo de análisis ante el perito jefe Picoflojo, en Gadgetzan.",
    ["T"] = "La redención de Patagolpe",
  },
  [11] = {
    ["D"] = "En las fronteras del Bosque de Elwynn se han avistado gnolls, unas criaturas salvajes que seguramente no están allí por trabajo. Un grupo enorme de ellos, uno más numeroso del que podemos contener solos, ha infestado los bosques que hay al sur de la torre de vigilancia. Otro grupo ha infestado las áreas cercanas al Lago del Hito, al este.$B$BEl ejército de Ventormenta elogiará a quienquiera que le ayude a matarlos. Tráeme sus bandas gnoll pintadas como prueba de tu hazaña.",
    ["O"] = "Lleva 8 bandas gnoll pintadas al ayudante de alguacil Rainer al Cuartel.",
    ["T"] = "Recompensas por los gnolls Zarparrío",
  },
  [12] = {
    ["D"] = "La Milicia Popular solo tiene un objetivo: defender las tierras de los Páramos de Poniente y traer la paz a nuestro entorno. Por desgracia, el precio de la paz suele pagarse en sangre.$B$BSegún el informe de uno de mis exploradores, una banda de tramperos Defias ha sido vista cerca de la Mina de Jango al noroeste de aquí, en La Granja de Molsen y en la Plantación de Calabazas de Cejade. Si quieres unirte a nuestras filas, acaba con 15 tramperos Defias y con 15 contrabandistas Defias y vuelve a verme.",
    ["O"] = "Gryan Mantorrecio quiere que mates a 15 tramperos Defias y a 15 contrabandistas Defias. Cuando lo hayas hecho vuelve a verle en la Colina del Centinela.",
    ["T"] = "La Brigada de los Páramos de Poniente",
  },
  [13] = {
    ["D"] = "Una banda de despiadados saqueadores Defias ha sido vista saqueando la Mina de la Costa del Oro, el Arroyo de la Luna y la Hacienda de Alexston. La Milicia Popular no tolerarán ese comportamiento. Avisa inmediatamente, $N, y haz conocer la presencia de la Luz en los Páramos de Poniente.$B$BLa Mina de la Costa del Oro está cerca de la costa, al oeste de la torre. El siguiente paso de tu instrucción consistirá en acabar con 15 de esos saqueadores Defias y con 15 despojadores Defias.",
    ["O"] = "Gryan Mantorrecio quiere que mates a 15 saqueadores Defias y a 15 despojadores Defias. Cuando lo hayas hecho vuelve a verle en la Colina del Centinela.",
    ["T"] = "La Brigada de los Páramos de Poniente",
  },
  [14] = {
    ["D"] = "Algunos Defias nos han esquivado. Según informa mi mejor explorador, los Defias han estado saqueando y expoliando el campo, hasta la parte sureste de los Páramos de Poniente. Creemos que se están ocultando en Las Colinas Afiladas, planeando sus siguientes pasos. Acaba con esos miserables en nombre de La Milicia Popular",
    ["O"] = "Gryan Mantorrecio quiere que mates a 15 bandoleros Defias, a 5 acechacaminos Defias y a 5 nudillos Defias. Cuando lo hayas hecho vuelve a verle en la Colina del Centinela.",
    ["T"] = "La Brigada de los Páramos de Poniente",
  },
  [15] = {
    ["D"] = "$N, los exploradores dicen que hay más kóbolds de lo que creíamos. Un grupo de trabajadores ha acampado cerca de la Mina del Eco, al norte de aquí.$B$BVe a la mina y liquídalos; sabemos que hay al menos ocho. Comprueba que no quede ninguno más y vuelve.",
    ["O"] = "Mata a 8 trabajadores kóbold y ve a hablar con el alguacil McBride.",
    ["T"] = "Investigar la Mina del Eco",
  },
  [17] = {
    ["D"] = "Hace poco oí hablar de un organismo recién descubierto: las setas magenta. Crecen arracimadas en la excavación de Uldaman y pueden encontrarse en los túneles primitivos y en las profundidades de las zonas menos explotadas. Quiero ver qué usos potenciales pueden tener en la alquimia, por eso necesito tu ayuda.$B$BTráeme una docena de setas magenta. Es posible que se defiendan con esporas venenosas; ten cuidado.$B$B¡A cambio, te daré uno de mis famosos elixires vigorizantes!",
    ["O"] = "Lleva 12 setas magenta a Ghak Sanadón a Thelsamar.",
    ["T"] = "Componentes de Uldaman",
  },
  [18] = {
    ["D"] = "Hace poco llegó una banda de maleantes a Villanorte. Se hacen llamar la Hermandad Defias; se les ha visto cruzando el río hacia el este.$B$BNo sé qué están haciendo, ¡pero seguro que no es nada bueno! Tráeme sus pañuelos y te daré un arma como recompensa.",
    ["O"] = "Lleva 8 pañuelos de arpillera roja al ayudante de alguacil Willem cerca de la Abadía de Villanorte.",
    ["T"] = "Hermandad de ladrones",
  },
  [20] = {
    ["D"] = "Los orcos Roca Negra llegaron desde Las Estepas Ardientes y han invadido el Fuerte de Petravista, al norte de aquí. Andan merodeando por el norte de Villa del Lago y tienen a los ciudadanos encerrados como a ganado.$B$BLibra a Villa del Lago de esta amenaza. Mata a los orcos y tráeme 10 hachas desgastadas por la batalla como prueba.$B$BAunque nos amenazan desde el norte, sabemos que hay más al sureste de Crestagrana. Si los encuentras, mátalos también.",
    ["O"] = "Lleva 10 hachas desgastadas por la batalla al alguacil Marris a Villa del Lago.",
    ["T"] = "La amenaza de los Roca Negra",
  },
  [21] = {
    ["D"] = "Ha quedado demostrado que tenemos que purgar la Mina del Eco. Vuelve allí y ayuda a eliminar a los kóbolds.$B$BDate prisa, $N. Cuanto más tiempo dejemos a los kóbolds en la mina, más nos costará expulsarlos de Villanorte.",
    ["O"] = "Mata 8 obreros kóbold y ve a hablar con el alguacil McBride a la Abadía de Villanorte.",
    ["T"] = "Escaramuza en la Mina del Eco",
  },
  [22] = {
    ["D"] = "Las cebollas están peladas. El ajo machacado. La corteza está tostada. Las hierbas de eneldo picadas. ¡Lo único que me falta para mi famosa empanada de carne son 8 hígados de dentosangre!",
    ["O"] = "Salma Saldean necesita 8 hígados de dentosangre para hacer un pastel de hígado de dentosangre.",
    ["T"] = "Pastel de hígado de Dentosangre",
  },
  [23] = {
    ["D"] = "¡El poderoso oso Ursangous ya es historia!$B$BLa pequeña media luna de su pata indica que a esta bestia la entrenaron los elfos de la noche para recorrer el bosque en busca de sus enemigos. Quién sabe cuántos de tus aliados de la Horda han caído ante esta criatura.$B$BDeberías llevarle la pata a Tarkan, en el Refugio Brisa de Plata.",
    ["O"] = "Llévale la pata de Ursangous al capitán Tarkan en el Refugio Brisa de Plata.",
    ["T"] = "Pata de Ursangous",
  },
  [24] = {
    ["D"] = "El otrora veloz Shadumbra yace ahora inerte mientras tú reivindicas tu victoria sobre él.$B$BLos elfos de la noche aprecian a esta criatura legendaria. Su cabeza servirá como trofeo intimidatorio y como símbolo del fin de la Alianza en Vallefresno.$B$BEl Capitán Goggath, en la Avanzada Grito Infernal, estará encantado de recibir la cabeza del sable de la noche asesinado.",
    ["O"] = "Llévale la cabeza de Shadumbra al Capitán Goggath en la Avanzada Grito Infernal.",
    ["T"] = "Cabeza de Shadumbra",
  },
  [25] = {
    ["D"] = "¡Deberías haberlo visto, $C!$B$BLos elfos pensaban que Brisa de Plata era inexpugnable. Con sus fuertes defensas y su ejército de elementales de agua, casi tenían razón.$B$BCasi.$B$BUno de nuestros magos pudo invocar un elemental corrupto para sembrar el caos y la ira entre los demás.$B$B¡Ah, la expresión de sus rostros!$B$B<Tarkan se ríe.>$B$BPor desgracia, el mago que lo invocó cayó en combate. Ahora necesito que alguien acabe con nuestra creación.",
    ["O"] = "El capitán Tarkan, en el Refugio Brisa de Plata, quiere que acabes con Marearess y con 12 elementales de agua podrida en el Lago Mystral.",
    ["T"] = "Un jarro de agua fría",
  },
  [26] = {
    ["D"] = "$N, he seguido tus progresos con gran gozo. Sin duda te estás convirtiendo en $Gun valioso protector:una valiosa protectora; de la naturaleza.$B$BHa llegado el momento de que aprendas más sobre una de las formas animales con las que Cenarius nos ha bendecido. Una forma que podemos adoptar y que nos permite controlar el agua. Tendrás que viajar al poblado Amparo de la Noche, que está en Claro de la Luna, y hablar con Dendrita Rafagaestelar del Círculo Cenarion. Él será tu guía.",
    ["O"] = "Ve a Claro de la Luna, al poblado Amparo de la Noche y habla con Dendrita Rafagaestelar.",
    ["T"] = "Una lección que aprender",
  },
  [27] = {
    ["D"] = "$N, he seguido tus progresos con gran gozo. Sin duda te estás convirtiendo en $Gun valioso protector:una valiosa protectora; de la naturaleza.$B$BHa llegado el momento de que aprendas más sobre una de las formas animales con las que Cenarius nos ha bendecido. Una forma que podemos adoptar y que nos permite controlar el agua. Tendrás que viajar al poblado Amparo de la Noche, que está en Claro de la Luna, y hablar con Dendrita Rafagaestelar del Círculo Cenarion. Él será tu guía.",
    ["O"] = "Ve a Claro de la Luna, al poblado Amparo de la Noche y habla con Dendrita Rafagaestelar.",
    ["T"] = "Una lección que aprender",
  },
  [28] = {
    ["D"] = "En tu primera prueba, debes ir a las profundidades del Lago Elune\'ara y encontrar un adorno de altar. Se descompone rápidamente, por lo que deberás ir inmediatamente al Santuario de Remulos. Coloca el adorno en el altar y habla con Tajarri; es una de las celadoras más apreciadas de Claro de la Luna.$B$BTienes un tiempo limitado para completar la tarea y es probable que tengas que competir con otros druidas en busca de adornos. Necesitarás toda tu destreza mental y física.$B$BSuerte, $N.",
    ["O"] = "Lleva un adorno de altar del Lago Elune\'ara al Santuario de Remulos, al noroeste de Claro de la Luna. Usa el adorno en el santuario. $B$BDespués, habla con Tajarri en el santuario para completar la prueba.",
    ["T"] = "La prueba del lago",
  },
  [29] = {
    ["D"] = "En tu primera prueba, debes ir a las profundidades del Lago Elune\'ara y encontrar un adorno de altar. Se descompone rápidamente, por lo que deberás ir inmediatamente al Santuario de Remulos. Coloca el adorno en el altar y habla con Tajarri; es una de las celadoras más apreciadas de Claro de la Luna.$B$BTienes un tiempo limitado para completar la tarea y es probable que tengas que competir con otros druidas en busca de adornos. Necesitarás toda tu destreza mental y física.$B$BSuerte, $N.",
    ["O"] = "Lleva un adorno de altar del Lago Elune\'ara al Santuario de Remulos, al noroeste de Claro de la Luna. Usa el adorno en el santuario. $B$BDespués, habla con Tajarri en el santuario para completar la prueba.",
    ["T"] = "La prueba del lago",
  },
  [30] = {
    ["D"] = "En tu segunda prueba debes encontrar las dos mitades del colgante del león marino. Una obtiene su poder de la agilidad del león marino cuando se desliza en el agua, la otra lo obtiene de su increíble capacidad de resistencia. Los druidas se alimentan de ambos rasgos para asumir su forma acuática.$B$BHabla con la gente de Claro de la Luna para averiguar dónde están las mitades y tráemelas para que las unamos. Luego llevarás el colgante a Dendrita Rafagaestelar.",
    ["O"] = "Busca el colgante de Agilidad acuática y el colgante de Resistencia acuática. Habla con la gente de Claro de la Luna para conseguir pistas sobre la ubicación de los colgantes. $B$BForma el colgante del león marino con los otros dos cuando estés cerca del Santuario de Remulos. $B$BLleva el colgante completo a Dendrita Rafagaestelar a Amparo de la Noche, en Claro de la Luna.",
    ["T"] = "La prueba del león marino",
  },
  [31] = {
    ["D"] = "Has completado tu formación para la adopción de una forma acuática. Ve a ver a tu instructor, Turak Tótem de Runa, a Cima del Trueno. Enséñale el colgante y demuéstrale que puedes aprender lo que tenga que enseñarte. Él completará tu formación para que puedas fundirte con el agua.$B$BBuena suerte, joven druida. Volveremos a vernos.",
    ["O"] = "Vuelve a Cima del Trueno y enséñale el colgante del león marino a Turak Tótem de Runa.",
    ["T"] = "Forma acuática (elfo de la noche druida)",
  },
  [32] = {
    ["D"] = "Aquí tienes el informe. Te ruego que se lo entregues a alguien que tenga conocimientos de este tema.$B$BConozco a un trol erudito llamado Zilzibin Tambor Sabio que ha dedicado mucho tiempo al estudio de los seres vivos que solo se encuentran en Azeroth. ¡Es un historiador de los pies a la cabeza! Entrégale este informe si quieres que te ayude a entender a qué te enfrentas y el modo de detener esta amenaza. Vamos a necesitar toda la ayuda que podamos conseguir.$B$BZil vive en una casa en el segundo nivel de La Calle Mayor en Orgrimmar.",
    ["O"] = "Entrégale el informe a Zilzibin Tambor Sabio en Orgrimmar.",
    ["T"] = "La sublevación de los silítidos",
  },
  [33] = {
    ["D"] = "Sea lo que sea lo que está afectando a estos lobos, no se va. Cada día que pasa hay más lobos enfermos. Me puede pedirle a alguien que mate a las bestias de la naturaleza, pero debemos acabar con los lobos jóvenes malsanos antes de que esta pequeña infección se convierta en una epidemia.$B$BMata lobos jóvenes malsanos en el valle y tráeme pelambres de lobo malsano como prueba de tu hazaña.",
    ["O"] = "Lleva 8 pelambres de lobo malsano a Eagan Desollapelambres junto a la Abadía de Villanorte.",
    ["T"] = "Los lobos cruzan la frontera",
  },
  [34] = {
    ["D"] = "Han vuelto a arrasar mi jardín, y sé quién es el responsable. Es ese maldito jabalí, ese al que el alguacil Marris llama Panzallena.$B$BYa es demasiado tarde para replantar narcisos. Ese puerco ha devastado mis flores. ¡Acaba con ese bicharraco! Si me traes un colmillo suyo, te recompensaré. Dicen que anda husmeando por los campos que hay al suroeste de Villa del Lago, pero de vez en cuando viene a mi jardín.",
    ["O"] = "Martie Jainrose de Villa del Lago quiere que mates a Panzallena. Llévale un colmillo como prueba.",
    ["T"] = "Una visita poco grata",
  },
  [35] = {
    ["D"] = "Si te preocupa que los rumores sobre los múrlocs sean ciertos, viaja al puente del este de Elwynn y habla con el guardia Thomas. Esta última semana se encuentra emplazado en el puente y sabrá cuál es el estado de la situación.$B$BTengo un destrero de Ventormenta que te llevará hasta el guardia Thomas. Si quieres utilizar el caballo, dímelo.",
    ["O"] = "El alguacil Dughan quiere que hables con el guardia Thomas.",
    ["T"] = "Más preocupaciones",
  },
  [36] = {
    ["D"] = "Nunca pensé que llegaría el día en el que tuviera que dejar la granja. Pero los campos están llenos de ladrones y es demasiado peligroso que sigamos aquí. En cuanto el granjero Cejade consiga arreglar el carro nos pondremos en marcha.$B$B¿Podrías hacerme un favor? Deja que te escriba mi receta del estofado de los Páramos de Poniente. Por favor, llévasela a Salma Saldean, estará en la Finca de Saldean, nada más pasar la bifurcación del camino.",
    ["O"] = "Verna Cejade quiere que le entregues su receta del estofado de los Páramos de Poniente a Salma Saldean.",
    ["T"] = "Estofado de los Páramos de Poniente",
  },
  [38] = {
    ["D"] = "¡Ayúdame a preparar el estofado de los Páramos de Poniente! Vuelve con estos ingredientes: $B $B 3 piezas de carne fibrosa de buitre $B 3 morros de dentosangre $B 3 ojos de múrloc $B 3 okras",
    ["O"] = "Salma Saldean quiere 3 carnes fibrosas de buitre, 3 morros de dentosangre, 3 ojos de múrloc y 3 okras.",
    ["T"] = "Estofado de los Páramos de Poniente",
  },
  [39] = {
    ["D"] = "Habla al alguacil Dughan de la muerte de Malakai y Rolf, y después infórmale de que no podrá contener a los múrlocs del este de Elwynn con sus tropas.$B$BSé que no tenemos bastantes soldados, quizás Dughan pueda encontrar alguno más.",
    ["O"] = "Preséntate ante el alguacil Dughan en Villadorada.",
    ["T"] = "Entrega el informe de Thomas",
  },
  [40] = {
    ["D"] = "¡Hay una nueva amenaza en el Bosque de Elwynn, $N! ¡Los múrlocs están subiendo por los ríos al este de Elwynn, ahuyentando a los peces y atacando a las buenas gentes de allí!$B$BAvisé al alguacil Dughan, pero le preocupan más los gnolls y los bandidos. No cree que los múrlocs sean una amenaza.$B$B¡Por favor, $N! ¡Habla con Dughan y convéncele de que envíe más tropas al este!",
    ["O"] = "Remy \"Dos Veces\" quiere que hables con el alguacil Dughan de Villadorada.",
    ["T"] = "Un peligro con olor a pescado",
  },
  [46] = {
    ["D"] = "El ejército de Ventormenta ha puesto precio a la cabeza de los rondadores y batidores múrloc de Elwynn. Mátalos y llévale sus aletas al guardia Thomas. El ejército de Ventormenta te recompensará bien.$B$BLos múrlocs han construido una aldea en el Lago del Hito, al norte de aquí, y otra en el sur, donde se divide el río.",
    ["O"] = "Lleva 8 aletas de múrloc rotas al guardia Thomas al puente oriental de Elwynn.",
    ["T"] = "Recompensas por los múrlocs",
  },
  [47] = {
    ["D"] = "Los kóbolds de esa zona a veces llevan polvo de oro. Me vendría muy bien. Consígueme un montón de polvo de oro y te pagaré el mejor precio de la ciudad, ¡el mejor!$B$BPuedes encontrar kóbolds en la Mina Abisal, al sur, y también alrededor de la Cantera de Jaspe, al noreste.",
    ["O"] = "Lleva 10 unidades de polvo de oro a Remy \"Dos Veces\" a Villadorada. Puedes obtener polvo de oro de los kóbolds del Bosque de Elwynn.",
    ["T"] = "Intercambio de polvo de oro",
  },
  [48] = {
    ["D"] = "Allá en el sur donde las zarzas irritan$BHay un manantial de aguas umbrías$BPura como la sagrada Hija de la Luz$BAgua del manantial sagrado me traerás tú.",
    ["O"] = "Lleva agua del manantial sagrado a Tragoamargo Cebatruenos.",
    ["T"] = "Dulce ámbar",
  },
  [49] = {
    ["D"] = "Ámbar es el color del amor de mi vida$BComo las luces de un final del día$BRecupéralo y el amor volverá a mí$BUn saco de cebada, uno de centeno y otro de maíz.",
    ["O"] = "Tragoamargo Cebatruenos quiere un saco de cebada, un saco de centeno y un saco de maíz.",
    ["T"] = "Dulce ámbar",
  },
  [50] = {
    ["D"] = "Mezclaré y mezclaré y un puré será todo$BSerá, amigo, nuestro más querido tesoro$BHaré un alambique fuerte y resistente$BDame veraplata y no daré la lata.",
    ["O"] = "Tragoamargo Cebatruenos quiere veraplata.",
    ["T"] = "Dulce ámbar",
  },
  [51] = {
    ["D"] = "Recién destilada, aunque aún no esté filtrada$BComo tierra sin semilla esperando a ser arada$BAhora haré nuestra pila de carbón$BUna rama de sicomoro es tu próxima misión.",
    ["O"] = "Lleva una rama de sicomoro a Tragoamargo Cebatruenos.",
    ["T"] = "Dulce ámbar",
  },
  [52] = {
    ["D"] = "Saludos, $N. Los animales salvajes son más agresivos cuanto más nos alejamos de Villadorada. El Aserradero de la Vega del Este sufre constantes ataques de lobos y osos.$B$BNos vendría bien tu ayuda ahí fuera.",
    ["O"] = "Mata a 8 merodeadores o lobos grisáceos del bosque y a 5 osos del bosque jóvenes y vuelve con el guardia Thomas al puente oriental de Elwynn.",
    ["T"] = "Proteger la frontera",
  },
  [53] = {
    ["D"] = "Es un líquido meloso que se vuelve delicioso$BDulce líquido ambarino que merece mi cariño$BPero algo necesito para darle mis cuidados$BUn magnífico tonel del mejor roble carbonizado.",
    ["O"] = "Lleva un montón de roble carbonizado a Tragoamargo Cebatruenos.",
    ["T"] = "Dulce ámbar",
  },
  [54] = {
    ["D"] = "$N, eres $Gun:una:c; $C que ciertamente ha demostrado que le preocupa la seguridad de Villanorte. Tu siguiente misión será proteger el Bosque de Elwynn.$B$BSi aceptas, te daré unos papeles para el alguacil Dughan, que está en Villadorada. Para ir a Villadorada, sigue el camino hacia el sur, pasada la frontera.",
    ["O"] = "Lleva los documentos del alguacil McBride al alguacil Dughan a Villadorada, en el Bosque de Elwynn.",
    ["T"] = "Informa en Villadorada",
  },
  [55] = {
    ["D"] = "Morbent Vil se esconde en su guarida, una casa ubicada en la cima de la montaña que hay al este, da al Cementerio del Cerro del Cuervo. Su tiempo en esta tierra está llegando a su fin...$B$BUtiliza la pesadilla de Morbent contra él. Inutilizará su magia protectora.$B$BY mátalo. Mátalo y sálvanos de su maldad. ¡Sé el instrumento de mi venganza, y conviértete en $Gel héroe:la heroína; del Bosque del Ocaso!",
    ["O"] = "Utiliza la pesadilla de Morbent contra Morbent Vil, mata a Morbent Vil y luego ve al campamento de Sven y devuélvele la pesadilla de Morbent.",
    ["T"] = "Morbent Vil",
  },
  [56] = {
    ["D"] = "Como Comandante de La Guardia Nocturna, es mi deber proteger a los ciudadanos de Villa Oscura. Ventormenta ha retirado a los guardias que estaban aquí destacados. Ahora tenemos que arreglárnoslas solos.$B$BSi crees estar a la altura de la tarea, nos vendría bien tu ayuda. Ayuda a La Guardia Nocturna aventurándote en el Jardín Sereno, al sur de Villa Oscura y matando a 8 guerreros esqueléticos y a 6 magos esqueléticos. ¡Hemos de acabar con la amenaza de los no-muertos!",
    ["O"] = "La comandante Althea Cerranegro de Villa Oscura quiere que mates a 8 guerreros esqueléticos y a 6 magos esqueléticos.",
    ["T"] = "La Guardia Nocturna",
  },
  [57] = {
    ["D"] = "$N, me están llegando informes de una infestación de no-muertos en el bosque. Si estás a la altura, viaja al Cementerio del Cerro del Cuervo y mata a 15 de esos malignos esqueléticos y a 15 horrores esqueléticos. También me han llegado noticias de la presencia de esqueletos al sur de Villa Oscura, en el Cementerio del Jardín Sereno y en sus alrededores. ¡Hemos de expulsar a los no-muertos del Bosque del Ocaso!",
    ["O"] = "La comandante Althea Cerranegro de Villa Oscura quiere que mates a 15 malignos esqueléticos y a 15 horrores esqueléticos.",
    ["T"] = "La Guardia Nocturna",
  },
  [58] = {
    ["D"] = "La situación de Cerro del Cuervo sigue siendo desalentadora. La seguridad de Villa Oscura está en tus manos, $N. Yo no tengo suficientes vigías para mantener la villa a salvo. Necesitamos que ayudes a Cerro del Cuervo una vez más y que liberes el mausoleo oriental de 20 propagadores de peste.",
    ["O"] = "La comandante Althea Cerranegro de Villa Oscura quiere que mates a 20 propagadores de peste en el mausoleo oriental de Cerro del Cuervo.",
    ["T"] = "La Guardia Nocturna",
  },
  [59] = {
    ["D"] = "Por tu astucia y valor, te entrego un marcador que vale por una pieza de armadura. Quiero que se lo lleves a Sara Timberlain al Aserradero de la Vega del Este. Dale el marcador y ella te forjará una armadura. Y cuando la recibas, $N, úsala para defender Elwynn.$B$BLa Vega del Este está al este de este puesto, al otro lado del puente.",
    ["O"] = "Entrega a Sara Timberlain el marcador de armaduras de Ventormenta.",
    ["T"] = "Armadura de paño y cuero",
  },
  [60] = {
    ["D"] = "Hola, $Gchico:chica;. ¿Tienes un momento?$B$BMi hermano y yo tenemos un herbolario en Ventormenta. Yo estoy aquí para conseguir velas grandes, por la cera, ya me entiendes. ¿Puedes ayudarme?$B$BPuedes conseguir velas grandes de los kóbolds. He oído rumores de que están infestando las minas de Elwynn... La Mina Abisal del sur y la Cantera de Jaspe del este. Puedes empezar la búsqueda en uno de esos lugares.",
    ["O"] = "Lleva 8 velas grandes a William Mortero a Villadorada.",
    ["T"] = "Las velas de los kóbolds",
  },
  [62] = {
    ["D"] = "¡La mina de Villanorte no es la única que tiene problemas! Según mis informes, la Mina Abisal de Elwynn también ha sido ocupada por los kóbolds.$B$BExplora la mina y comprueba la veracidad de mis informes. Luego vuelve aquí. La mina está hacia el sur de Villadorada, entre La Granja Pedregosa y la granja Maclure.",
    ["O"] = "Explora la Mina Abisal y vuelve junto al alguacil Dughan a Villadorada.",
    ["T"] = "La Mina Abisal",
  },
  [63] = {
    ["D"] = "Por la costa del Bosque de Argénteos, al oeste de El Sepulcro, verás un altar infestado por la peste. Los espíritus de ese lugar ya no se comunican con nuestro pueblo y de ti depende acabar con esa manifestación de la Plaga y limpiar el estanque.$B$BLlévate los brazales de elemental y las gotas restantes de la poción de Salmuera y colócalos en el blandón. El ritual debería bastar para invocar a un espíritu puro con el que comunicarte.$B$BTráeme una prueba de haber completado la tarea y te recompensaré con un tótem.",
    ["O"] = "Derrota a la manifestación de agua corrupta y coloca los brazales de manifestación corrupta con las gotas restantes del agua más pura en el blandón de Siemprefuente en el Bosque de Argénteos.",
    ["T"] = "La llamada del agua",
  },
  [64] = {
    ["D"] = "¡Fue horrible! Verna me despertó cuando oyó jaleo en los campos. Estaban llenos de gamberros. Nos fuimos corriendo y olvidé recoger mi reloj de bolsillo. Me lo dio el padre de Verna el día de nuestra boda y es horrible saber que está en manos de esos ladrones. Dejé el reloj de bolsillo en el guardarropa de la granja. Busca el campo de calabazas que está al Oeste... no tiene pérdida. ¡Si me lo devuelves te estaré muy agradecido!",
    ["O"] = "El granjero Cejade quiere que recuperes el reloj de bolsillo que dejó olvidado en el guardarropa de su granja en la plantación de calabazas del Oeste.",
    ["T"] = "La reliquia olvidada",
  },
  [66] = {
    ["D"] = "La otra noche, un terrible tumulto me recorrió las venas. Sentí que mi nieta Alyssa estaba en grave peligro. Consulté las cartas y vi a la Muerte contemplándome desde la mesa.$B$BBusqué la luz en un largo y oscuro trance... intentaba encontrar algo concreto que me ayudara a calmar el espíritu. Y un nombre vino a mí. Stalvan.$B$BAcude al secretario del Concejo e intenta averiguar algo más sobre ese nombre. Temo por todos nosotros.",
    ["O"] = "Busca al secretario Daltry en el Concejo de Villa Oscura.",
    ["T"] = "La leyenda de Stalvan",
  },
  [67] = {
    ["D"] = "Stalvan, ¿eh? Deja que consulte el registro.$B$BStalvan... Stalvan... Veamos... ¡Ah! ¡Aquí está! Tengo un señor Stalvan Mantoniebla. Según consta aquí, su última dirección es la Escuela de Arroyo de la Luna. ¡Pero este dato es de hace mucho tiempo!$B$BHazme un favor, ¿quieres? Si pasas por Arroyo de la Luna, hazme saber si consigues información más reciente sobre este tipo. Me gusta llevar los registros al día.",
    ["O"] = "Viaja a la Escuela de Arroyo de la Luna y lleva cualquier información que descubras sobre Stalvan al secretario Daltry.",
    ["T"] = "La leyenda de Stalvan",
  },
  [68] = {
    ["D"] = "En el viejo arcón encuentras una carta polvorienta sin enviar.",
    ["O"] = "Lleva la carta polvorienta sin enviar al secretario Daltry.",
    ["T"] = "La leyenda de Stalvan",
  },
  [69] = {
    ["D"] = "¡Ay, ay! Esto se me debió de pasar la primera vez. En el registro, justo debajo de la primera dirección de Stalvan, hay otra dirección. Está tachada... Parece que se dirigía a la Posada Orgullo de León, en Villadorada. Quizás quieras comprobarlo, $N.",
    ["O"] = "Ve a la Posada Orgullo de León a ver si alguien ha oído el nombre de Stalvan.",
    ["T"] = "La leyenda de Stalvan",
  },
  [70] = {
    ["D"] = "Stalvan... ese nombre me suena de algo. ¡Ya sé de qué!$B$BUna noche de tormenta de hace muchos años, llegó aquí un mensajero que buscaba refugio para la noche. Casi a medianoche, el hombre se precipitó escaleras abajo gritando, pálido de terror. Desapareció en la tormenta aún en pijama.$B$BCon las prisas, se olvidó sus cartas en el cofre de arriba. Y nunca volvió a buscarlas. Una de ellas es de ese tal Stalvan. Iba dirigida al distrito de Los Canales de Ventormenta. Puedes quedártela.",
    ["O"] = "Recupera la carta sin enviar de Stalvan del cofre y entrégala en el distrito de Los Canales de Ventormenta.",
    ["T"] = "La leyenda de Stalvan",
  },
  [72] = {
    ["D"] = "Mi padre era el custodio de la propiedad mucho antes que yo. Tuvo que limpiar la sangre después de la masacre. Pero eso ahora no viene al caso.$B$BEl fondo de Cresta Sílex se ha agotado. Ahora, las últimas pertenencias de la familia serán subastadas. Es culpa de esos buitres de los recaudadores de impuestos. Como veo que te mueres por saber más, creo que puedes mirar entre toda esta basura. Quién sabe lo que puedes encontrar.",
    ["O"] = "Busca una pista entre los objetos empaquetados.",
    ["T"] = "La leyenda de Stalvan",
  },
  [73] = "_",
  [75] = {
    ["D"] = "Cuando me mudé aquí había un fajo de papiros en el cofre de arriba. Empecé a examinarlos, pero tuve que dejarlo cuando la niebla empezó a nublarme la vista.$B$BHazle un favor a un pobre anciano ciego. Sube y busca en el cofre cualquier cosa que te pueda ayudar a descubrir más sobre ese Stalvan. Sé que hay una página de un diario que, aunque está algo descolorida, podría interesarte mucho. Tráemela. Yo te ayudaré en lo que pueda.",
    ["O"] = "El mariscal Haggard quiere que busques la página del diario descolorida en el cofre que hay arriba.",
    ["T"] = "La leyenda de Stalvan",
  },
  [76] = {
    ["D"] = "Gracias a ti, ahora sabemos que la Mina Abisal está infestada de kóbolds. Ahora necesitamos un explorador que investigue la Cantera de Jaspe, que está más lejos.$B$BExplora la Cantera de Jaspe e infórmame si ves algún kóbold. Para llegar a la mina, viaja hacia el este por el camino hasta que llegues a la Torre de Azora. Cuando llegues allí, dirígete hacia el norte. Encontrarás la mina al pie de las colinas.",
    ["O"] = "Explora la Cantera de Jaspe y vuelve a informar al alguacil Dughan a Villadorada.",
    ["T"] = "La Cantera de Jaspe",
  },
  [77] = {
    ["D"] = "Tengo material de empaquetado original para el calimocho que quiere Dran Droffers. Te ayudaré a prepararlo para enviárselo a ese gruñón del padre de Malton... a cambio de dos botellas del mismo. Están en unas cajas en la base abandonada de la Horda cerca de Roca Oculta.$B$BSerá muy dificultoso; habrá babosas y mocos repugnantes por las ruinas. Dicen que su líder es una especie de jefe moco. Pero da igual; consigue el calimocho y vuelve aquí para empaquetarlo.",
    ["O"] = "Recoge 10 botellas de calimocho dulce de las Tierras del Interior de las cajas de la base abandonada de la Horda en Roca Oculta.",
    ["T"] = "Una situación peliaguda",
  },
  [78] = {
    ["D"] = "Sé de alguien que probablemente podrá ayudarte. Cuando yo comandaba la guardia de Ventormenta, iba con mis hombres a beber al Mesón del Cuervo Escarlata de Villa Oscura. El tabernero, Smitts, era todo un experto en las tradiciones de la zona. Enséñale esta página y escucha lo que te pueda decir sobre ella.",
    ["O"] = "Lleva la página de diario borrada al tabernero Smitts a Villa Oscura, en el Bosque del Ocaso.",
    ["T"] = "La leyenda de Stalvan",
  },
  [79] = {
    ["D"] = "Seguí la leyenda del tal Stalvan durante años. Hace unos años, cuando asesinaron a aquellos nobles, yo fui con Haggard a investigar el asunto. Encontré estas páginas llenas de barro, pero nunca conseguimos relacionar la caligrafía con ese lunático. Tus pruebas demuestran su culpabilidad. Lleva esto a la comandante Cerranegro de inmediato e infórmale de lo que has descubierto.",
    ["O"] = "Lleva la página embarrada a la comandante Cerranegro.",
    ["T"] = "La leyenda de Stalvan",
  },
  [80] = {
    ["D"] = "Estuve vigilando a ese asqueroso de Stalvan durante mucho tiempo. Si está página fue escrita por la misma mano... tenemos una prueba de su culpabilidad más allá de una duda razonable.$B$BEl secretario Daltry, a lo largo de sus años en el registro, se ha convertido en un experto calígrafo. Muéstrale esta página y pídele que compruebe si la caligrafía coincide con la de la firma del registro.",
    ["O"] = "Enseña la página del diario manchada de sangre al secretario Daltry.",
    ["T"] = "La leyenda de Stalvan",
  },
  [83] = {
    ["D"] = "Los matones de Elwynn llevan lino, que me vendría bien para confeccionar prendas.$B$BSi me traes pañuelos de lino rojo, los usaré para hacerte algo bonito.$B$BEncontrarás a esos criminales en campamentos repartidos por todo el Bosque de Elwynn.",
    ["O"] = "Lleva 6 pañuelos de lino rojo a Sara Timberlain al Aserradero de la Vega del Este.",
    ["T"] = "Pañuelos de lino rojo",
  },
  [86] = {
    ["D"] = "Si me das un pastel, puede que te diga quién tiene el collar. Bernice, esa anciana de la otra granja, hace unos pasteles de tripa de cerdo increíbles...$B$BSi le llevas algo de carne de jabalí tierna de los jabalíes que rondan por nuestras granjas, y le dices para qué es, seguro que te hace un pastel.",
    ["O"] = "Lleva 4 carnes de jabalí tiernas a la tía Bernice Pedregosa a La Granja Pedregosa.",
    ["T"] = "Un pastel para Billy",
  },
  [87] = {
    ["D"] = "Estaba jugando cerca de la Mina Abisal y creo que se me cayó... esto... quiero decir, creo que vi el collar de la señora. No me preguntes cómo llegó allí... ¡Yo no fui!$B$BPues eso, que vi a un enorme kóbold con dientes de oro que lo recogía y volvía corriendo a su cubil, encima de la mina. Si encuentras a ese kóbold, encontrarás el collar, ¡lo juro!",
    ["O"] = "Lleva el collar de Bernice a tía Bernice Pedregosa a La Granja Pedregosa.",
    ["T"] = "Dientes de Oro",
  },
  [88] = {
    ["D"] = "Los Brackwell tienen una cerda que ganaría cualquier concurso. Se llama Princesa. Está ENORME, ¡y es gracias a mí! ¡Porque se alimenta de mis verduras!$B$B¡Princesa debe morir antes de que llegue a nuestros campos! Tráeme su collera como prueba de que está muerta y te daré algo para recompensarte.$B$BPrincesa suele estar justo en medio de nuestro campo de sandías. ¡Ve a por ella!",
    ["O"] = "Mata a Princesa, coge su collera y llévasela a la vieja Pedregosa a La Granja Pedregosa.",
    ["T"] = "¡Princesa debe morir!",
  },
  [89] = {
    ["D"] = "Durante la última invasión orca tuvimos que usar todo el hierro para fabricar balas, espadas y armaduras. Hicimos un pedido de materia prima a Ventormenta, pero unos gnolls Crestagrana secuestraron la caravana y se ocultaron en las colinas de detrás de Villa del Lago.$B$BY ahora estamos sin material para reconstruir el puente. Te recompensaré, $N, si me traes 5 picas y 5 remaches de hierro.$B$BVenga, ¡a trabajar!",
    ["O"] = "Lleva 5 picas y 5 remaches de hierro al supervisor Olento a Villa del Lago.",
    ["T"] = "El puente sempiterno",
  },
  [90] = {
    ["D"] = "¡El kebab de lobo sazonado es el especial de la casa! ¡Claro que te puedo preparar un poco! Pero necesitaré algunos productos. Tráeme 10 ijadas magras de lobo y algunas hierbas aromatizadas de Ventormenta. En el bosque hay muchísimos lobos; encontrar ijadas no será un problema. Para las hierbas, busca a Felicia Gump. Tiene una floristería en el distrito de Los Canales de Ventormenta.",
    ["O"] = "Reúne 10 ijadas magras de lobo y hierbas aromáticas de Ventormenta y vuelve a Villa Oscura, a ver al Chef Grual.",
    ["T"] = "Kebab de lobo sazonado",
  },
  [91] = {
    ["D"] = "Se ha instalado un enemigo de Ventormenta en las laderas orientales de Crestagrana. Ese bellaco usa lo arcano para extender el terror entre nuestra gente. Se oculta bajo el nombre de Morganth.$B$BHa esclavizado al clan gnoll Pielsombra. De acuerdo con las leyes de Villa del Lago, asistir a un enemigo del Reino está penado con la muerte.$B$B¡Esos miserables gnolls deben morir!$B$BTráeme diez colgantes Pielsombra y te recompensaré.",
    ["O"] = "Lleva 10 colgantes Pielsombra a Bailiff Conacher al Concejo de Villa del Lago.",
    ["T"] = "Ley de Solomon",
  },
  [92] = {
    ["D"] = "Me encantaría prepararte un estofado Crestagrana. Pero tengo un problemita; durante los disturbios con los orcos alimenté a toda la guarnición de Ventormenta. No me queda nada en la despensa, pero si tú me traes los ingredientes, cocinaré para ti.$B$BTráeme cinco filetes de carne de cóndor dura, cinco morros de dentosangre grandes y cinco raciones de carne de araña crujiente.",
    ["O"] = "El chef Breanna de Villa del Lago quiere 5 piezas de carne de cóndor dura, 5 morros de dentosangre grandes y 5 raciones de carne de araña crujiente.",
    ["T"] = "Estofado Crestagrana",
  },
  [93] = {
    ["D"] = "Te voy a contar un secreto: ¡los pasteles de \"cangrejo\" oscuro, en realidad se hacen con patas de araña! Sé que es un poco asqueroso, pero las patas de araña son muy sabrosas y los pasteles quedan estupendos. Tráeme patas pegajosas de araña y yo te preparo algunos pasteles.$B$BHe oído que las arañas de hilo venenoso son las mejores para cocinar. Están al norte, entre las laderas de las colinas y el río.",
    ["O"] = "Reúne 6 patas pegajosas de araña y llévaselas al Chef Grual a Villa Oscura.",
    ["T"] = "Pasteles de cangrejo oscuro",
  },
  [96] = {
    ["D"] = "Llevar fragmento. Llevar fragmento y dar vida.$B$BDar vida y entender. Entender que el agua es vida... la corrupción acaba con la vida.$B$BDas vida y la proteges con el poder del agua.",
    ["O"] = "Llévale el fragmento de agua a Islen Vidente del Agua al Escalón de la Marea, en Los Baldíos.",
    ["T"] = "La llamada del agua",
  },
  [97] = {
    ["D"] = "¡Informa de inmediato a la comandante Cerranegro de que las caligrafías coinciden, $N! ¡Sus sospechas han resultado ser ciertas!",
    ["O"] = "Informa a la comandante Cerranegro.",
    ["T"] = "La leyenda de Stalvan",
  },
  [98] = {
    ["D"] = "Stalvan Mantoniebla llevó una vida de depravación. Su mano sesgó innumerables vidas inocentes. Es, sin duda alguna, culpable de incontables crímenes. Ahora ese lunático amenaza Villa Oscura. Solo la Luz sabe qué sórdidas acciones está planeando. Debes ejecutar a Stalvan, $C. Lo encontrarás en su casa, al norte de la ciudad.$B$BCuando lo hayas hecho, busca a Lady Eva y enséñale este anillo familiar. Después de todo, fue su premonición lo que nos guió a este terrible descubrimiento. Gracias a ella, Villa Oscura ahora es más segura.",
    ["O"] = "Mata a Stalvan Mantoniebla y muestra a Lady Eva su anillo familiar.",
    ["T"] = "La leyenda de Stalvan",
  },
  [99] = {
    ["D"] = "Según aumenta mi conocimiento sobre la magia de Arugal, aumenta también mi desdén por el desafortunado necio. Estoy a punto de completar la investigación de su Remedio... Bonito nombre...$B$BMi conocimiento será completo cuando descubra qué encantamiento está causando el extraño comportamiento en la Aldea Piroleña. Por el día, los aldeanos parecen ser humanos. Pero, cuando el sol desaparece, se convierten en huargen Furia Lunar.$B$BNecesito extraer energía de los grilletes encantados que Arugal utilizó con ellos. Tráeme seis grilletes de Piroleña encantados, $N.",
    ["O"] = "Lleva 6 grilletes de Piroleña a Dalar Tejealba en El Sepulcro.",
    ["T"] = "La locura de Arugal",
  },
  [100] = {
    ["D"] = "Hay un destello de luz cuando pronuncias las últimas palabras del conjuro. Se desvanece el sonido de las gotas de agua sobre la llama y la emoción embarga tus sentidos conforme se aniquilan los espíritus corruptos que te rodean.",
    ["O"] = "Habla con la manifestación de agua menor en la Cala Mareanorte, en el Bosque de Argénteos.",
    ["T"] = "La llamada del agua",
  },
  [101] = {
    ["D"] = "A medida que la corrupción mística invade el bosque, la necesidad de autoprotección se hace innegable, $N. El viento me susurra y me cuenta de grandes peligros que el futuro te depara.$B$BSi deseas protegerte, noble $C, tráeme 10 colmillos de necrófago, 10 dedos de esqueleto y 5 viales de veneno arácnido. Te encantaré un tótem de castigo que dañará a aquellos que intenten causarte daño a ti.",
    ["O"] = "Consigue 10 colmillos de necrófago, 10 dedos de esqueleto y 5 viales de veneno arácnido para Lady Eva en Villa Oscura.",
    ["T"] = "El Tótem de castigo",
  },
  [102] = {
    ["D"] = "Ventormenta nos ha abandonado. Nauseabundos aires de depravación soplan por las llanuras de los Páramos de Poniente. Este era mi hogar y no daré la espalda a los ciudadanos que decidieron permanecer aquí. Nosotros, los antiguos granjeros, ofreceremos resistencia.$B$BTu tarea, si decidieras aceptar, consistirá en patrullar las praderas de los Páramos de Poniente. Localiza y aniquila a los infames gnolls que parecen colaborar con los ladrones de Las Minas de la Muerte. Tráeme 8 garras de gnoll y sabré recompensar tu valentía.",
    ["O"] = "Lleva 8 garras de gnoll al capitán Danuvin en la Colina del Centinela.",
    ["T"] = "En patrulla por los Páramos de Poniente",
  },
  [103] = {
    ["D"] = "La noche en que murió la familia del guardián del Faro fue horrible. Contemplé, impotente, cómo el Viejo Ojosombrío lideraba el ataque. Pero lo pasado, pasado está, y lo que ahora me preocupa son las vidas de los que navegan por el Mare Magnum y se acercan demasiado a las peligrosas rocas de la costa. Como no hay nadie que vigile la llama, la responsabilidad es ahora mía.$B$BAyúdame a mantener la antorcha iluminada trayéndome 5 botellas de aceite de los monstruos de la cosecha.",
    ["O"] = "Lleva 5 botellas de aceite al capitán Grisillo en el Faro de Poniente.",
    ["T"] = "Guardián de la Llama",
  },
  [104] = {
    ["D"] = "Cuando acabó mi vida contra las rocas, no tenía ni idea de lo que me esperaba en el más allá. El Faro estaba sumido en la oscuridad aquella noche, ya que el Viejo Ojosombrío había asustado a la familia del guarda. Lograron regresar y volver a encender la llama, pero el Viejo Ojosombrío coaccionó a los múrlocs de mente más débil para que asaltaran el Faro con él de nuevo. La segunda vez, la familia no corrió la misma suerte y pereció inútilmente ante mis ojos.$B$BMata al Viejo Ojosombrío si lo ves por la costa y tráeme una de sus escamas, te recompensaré.",
    ["O"] = "Lleva una escama del Viejo Ojosombrío al capitán Grisillo en el Faro de Poniente.",
    ["T"] = "La amenaza costera",
  },
  [105] = {
    ["D"] = "¡Ay, se acerca el momento de atacar Andorhal y de hacer salir al exánime que lo controla!$B$BEs en el interior de las ruinas de la ciudad donde el exánime, Araj el Invocador, tiene sus dominios. Está custodiado por numerosos miembros de la Plaga que rodean los restos del centro de la ciudad, y su séquito personal de guardianes es realmente formidable. Necesitarás muchos aliados para tener alguna oportunidad de enfrentarte a él con éxito.$B$B¡Acaba con Araj, $N, y tráeme un trozo de su filacteria como prueba!",
    ["O"] = "Lleva el trozo de filacteria de Araj al sumo ejecutor Derrington en El Baluarte en las Tierras de la Peste del Oeste.",
    ["T"] = "Ah, Andorhal",
  },
  [106] = {
    ["D"] = "¡Oh, estoy maldita! He entregado mi corazón a Tommy Joe Pedregosa, pero nuestras familias se odian a muerte. Así que no puedo verlo, ¡aunque me duelan los ojos por no poder mirar su hermoso rostro!$B$BTe lo ruego, lleva esta carta a Tommy Joe. Suele estar en el río que está al oeste de La Granja Pedregosa, al oeste de aquí.",
    ["O"] = "Entrega a Tommy Joe Pedregosa la carta de amor de Maybell.",
    ["T"] = "Jóvenes enamorados",
  },
  [108] = "_",
  [109] = {
    ["D"] = "Parece que has visto bastantes combates en tu vida, $C. Si no has tenido suficiente, busca a Gryan Mantorrecio. Comanda la Milicia Popular que protege las granjas de los Páramos de Poniente. Seguro que le vendrá bien tu ayuda. Puedes encontrarlo en la torre de piedra de la Colina del Centinela, saliendo de la carretera que cruza los Páramos de Poniente.",
    ["O"] = "Habla con Gryan Mantorrecio. Puedes encontrarlo en la torre de piedra de la Colina del Centinela, saliendo de la carretera que cruza los Páramos de Poniente.",
    ["T"] = "Habla con Gryan Mantorrecio",
  },
  [112] = {
    ["D"] = "Puedo hacer un licor de invisibilidad para Maybell, para que pueda salir de Los Viñedos de Maclure y reunirse con Tommy Joe. Pero para hacer el licor necesito alga de cristal.$B$BAunque el alga normalmente crece en el océano, también se puede conseguir de los múrlocs. Ve a ver si los múrlocs que hay cerca del Lago de Cristal tienen algo. El Lago de Cristal está al este de Villadorada.",
    ["O"] = "Lleva 4 hojas de alga de cristal a William Mortero a Villadorada.",
    ["T"] = "La recolección de algas",
  },
  [115] = {
    ["D"] = "Los Roca Negra han traído taumaturgos oscuros a los ataques en Crestagrana. Tienen unos objetos de poder oscuro, los orbes de medianoche. Estos orbes han malherido a muchos de los que luchan en Crestagrana; tenemos que hacernos con esos objetos demoníacos.$B$BTráeme los orbes de medianoche de los cadáveres de taumaturgos umbríos Roca Negra. Me encargaré de destruirlos; el mundo estará mejor sin ellos.",
    ["O"] = "Lleva 3 orbes de medianoche al alguacil Marris a Villa del Lago.",
    ["T"] = "Magia de las Sombras",
  },
  [116] = {
    ["D"] = "Tengo un problema, $N. Nos estamos quedando sin bebida y el envío se está retrasando mucho. Esta invasión orca ha sido terrible.$B$B¿Buscas trabajo?$B$BNecesito que vayas a recoger un barril de cerveza Cebatruenos de Tragoamargo Cebatruenos, que está en las colinas de los Páramos de Poniente, uno de Merlot en Ventormenta, una botella de licor de luna en Villa Oscura y una de ron dulce en Villadorada. Tráeme todo eso y te ganarás una recompensa.",
    ["O"] = "Lleva 1 barril de cerveza Cebatruenos, 1 barril de vino dulce, 1 botella de licor de luna y 1 botella de ron dulce al posadero Daniels a Villa del Lago.",
    ["T"] = "Tiempos de sequía",
  },
  [117] = {
    ["D"] = "Rápido, $Gamigo:amiga; y date prisa$BNuestra cerveza está de risa$BMás cerveza y menos comida,$BDadnos lúpulo para hacer birra.",
    ["O"] = "Entrega a Tragoamargo Cebatruenos 5 lúpulos para que pueda terminar su cerveza especial.",
    ["T"] = "Cerveza Cebatruenos",
  },
  [118] = {
    ["D"] = "¡He necesitado tanto hierro para abastecer a la guardia de Ventormenta que no tengo bastante para hacer herraduras!$B$BVe a Villadorada y dale esta nota al herrero Argus; le cuento mi problema y le hago un pedido de herraduras.",
    ["O"] = "Lleva la nota de Verner al herrero Argus a Villadorada, en el Bosque de Elwynn.",
    ["T"] = "El precio de unas herraduras",
  },
  [119] = {
    ["D"] = "Muy bien, 50 herraduras...$B$B¡Aquí las tienes, listas para llevar! Dile a Verner que ha sido un placer y que lea la nota que he pegado a la caja.",
    ["O"] = "Vuelve a Villa del Lago, en Crestagrana, y entrega la caja de herraduras a Verner Osbien.",
    ["T"] = "Regresa junto a Verner",
  },
  [120] = {
    ["D"] = "Estamos en tiempos difíciles, $C. Vivimos bajo un asedio constante. Si no llegan refuerzos, solo nos queda la derrota. El mensaje que te confío es de la mayor importancia. Llévaselo al general Marcus Jonathan de Ventormenta inmediatamente. Después vuelve, ya sean tus noticias buenas o malas.$B$B¡Apresúrate!",
    ["O"] = "El magistrado Solomon te ha dado un informe para el general Marcus Jonathan de Ventormenta. Quiere que vuelvas en cuanto lo hayas entregado.",
    ["T"] = "Mensajero a Ventormenta",
  },
  [121] = {
    ["D"] = "Mientras espero la respuesta del Rey, llévale esta carta al magistrado Solomon.$B$BPuedes retirarte, $C.",
    ["O"] = "Lleva la respuesta del general Marcus Jonathan al magistrado Solomon a Villa del Lago.",
    ["T"] = "Mensajero a Ventormenta",
  },
  [122] = {
    ["D"] = "Necesito escamas de barriga de cachorro de dragón Negro para pagarle las herraduras que Argus me envió desde Villadorada. Si me traes 6 escamas, tendré para pagarle y también para hacerte un regalo.$B$BEsos bichos vuelan al sur de Villa del Lago, pero a menudo se alejan. Tendrás que estar ojo avizor para cazarlos en cuanto los veas.",
    ["O"] = "Lleva 6 escamas de barriga de cachorro de dragón Negro a Verner Osbien a Crestagrana.",
    ["T"] = "Escamas de barriga",
  },
  [123] = {
    ["D"] = "Esta nota es una horario de los días y las horas a las que una persona –descrita como \"el Coleccionista\"– recibirá cargamentos de oro provenientes de las minas del Bosque de Elwynn.$B$BPor lo que pone en el horario, parece que el Coleccionista vive cerca de la Plantación de Calabazas de Brackwell, al este de Elwynn.$B$BParece importante. Deberías informar al alguacil McCree en el Aserradero de la Vega del Este.",
    ["O"] = "Ve a ver al alguacil McCree en el Aserradero de la Vega del Este y dale la lista del coleccionista.",
    ["T"] = "El Coleccionista",
  },
  [124] = {
    ["D"] = "¡Como si no tuviera bastante con los orcos! Ahora tengo toscos y místicos gnolls en la cresta que hay al norte de mi establo y se llevan mis caballos cuando se pierden.$B$BSi nos libras de los gnolls, mis caballos y yo te lo agradeceremos eternamente.",
    ["O"] = "Mata a 10 toscos y 8 místicos Crestagrana y ve a ver a Verner Osbien.",
    ["T"] = "El aullido de los gnolls",
  },
  [125] = {
    ["D"] = "Échame una mano, $N. Con todo esto del asedio, es difícil conseguir suministros. Mis herramientas venían de camino de Villadorada, pero hicieron explotar el puente. Pusimos las herramientas en una barca, pero los orcos atacaron con una catapulta y todo acabó en el fondo del lago.$B$BRecupera mi caja de herramientas, $N, y te ganarás una buena recompensa.",
    ["O"] = "El supervisor Olento de Villa del Lago quiere que recojas su caja de herramientas del fondo del Lago Sempiterno.",
    ["T"] = "Las herramientas perdidas",
  },
  [126] = {
    ["D"] = "$N, los gnolls siguen ahí. Los oigo aullar mientras cazan en las colinas de Villa del Lago. El que más fuerte aúlla es Ululante, el jefe.$B$B¡Mátalo! ¡Su manada se asustará y huirá! Tráeme su garra como prueba.$B$BY ten cuidado; si es el jefe, será por algo. Ya sabes lo que dicen de los perros viejos.",
    ["O"] = "Lleva la zarpa de Ululante a Verner Osbien a Villa del Lago.",
    ["T"] = "El aullido de las colinas",
  },
  [127] = {
    ["D"] = "El Lago Sempiterno es famoso por su pez luna moteado. Tiene mucha demanda, ¡pero me estoy quedando sin existencias!$B$BTráeme 10 peces luna moteados; te pagaré bien. Y si no sabes pescar, intenta robárselo a los múrlocs.",
    ["O"] = "Lleva 10 peces luna moteados al maestro de embarcadero Baren a Villa del Lago.",
    ["T"] = "Pescado a la venta",
  },
  [128] = {
    ["D"] = "Tenemos que acabar con esos Roca Negra. El enemigo ha invadido nuestra tierra. Estas montañas pertenecen al rey de Ventormenta. Nuestras fuentes nos dicen que el jefe de cada unidad del clan se llama Campeón. Encontrarás su campamento hacia el norte, justo al oeste del camino a Las Estepas Ardientes.$B$BEl magistrado Solomon quiere que mueran. Acaba con 15 Campeones Roca Negra y vuelve a verme.",
    ["O"] = "Mata a 15 campeones Roca Negra y el guardia Howe te recompensará en Villa del Lago.",
    ["T"] = "La recompensa de Roca Negra",
  },
  [129] = {
    ["D"] = "¿Puedes hacerme un favor? Le he preparado la comida al guardia Parker, pero ha salido de patrulla. Es un guardia de Ventormenta grande y fuerte que puede defenderse, pero para mí es demasiado peligroso.$B$B¡Si le llevas la comida, cuando vuelvas te invitaré a comer! ¿Qué me dices? Está de patrulla en el camino a Bosque del Ocaso.",
    ["O"] = "Llévale su comida al guardia Parker. Está de patrulla en el camino a Villa Oscura.",
    ["T"] = "Una comida gratis",
  },
  [130] = {
    ["D"] = "¿Te importa entregarle unas flores a Darcy de mi parte? Cómpralas en el herbolario de Villa del Lago, en el extremo oeste de la ciudad.$B$BPero no vayas a decir en la tienda para quién son o quién las envía...",
    ["O"] = "Habla con Martie Jainrose, la herborista de Crestagrana.",
    ["T"] = "Una visita al herborista",
  },
  [132] = {
    ["D"] = "Lo que voy a contarte puede costarme la vida. La banda Defias está preparando algo grande. Lo último que oí fue que estaban colaborando con varios gnolls, kóbolds e incluso goblins.$B$BLlévale esta nota a Mantorrecio. Explica todo lo que sé sobre este asunto.",
    ["O"] = "Lleva la Nota de Wiley a Gryan Mantorrecio en los Páramos de Poniente.",
    ["T"] = "La hermandad de los Defias",
  },
  [133] = {
    ["D"] = "Vivo tan lejos de la protección de la ciudad que es un milagro que los necrófagos y los muertos andantes no me hayan devorado. ¡De hecho, ayer, sin ir más lejos, una manada de Mascahuesos intentó tirar abajo las paredes de mi casa!$B$BQuiero hacer un monigote... una especie de espantapájaros para necrófagos. Y para hacerlo necesito costillas de necrófago. Puedes conseguirlos de comecarnes, Mascahuesos, comesesos, podridos y propagadores de peste.$B$BConsígueme esas costillas y yo te pagaré.",
    ["O"] = "Reúne 7 costillas de necrófago y llévaselas a Abercrombie a su cabaña.",
    ["T"] = "Un espantapájaros para necrófagos",
  },
  [134] = {
    ["D"] = "Hace unas pocas semanas, mientras recogía hierbas lejos de mi casa, me atacó una manada de ogros. Huí a toda prisa y tuve que dejar atrás un cajón lleno de valiosas herramientas y hierbas.$B$BLos ogros volvieron a su túmulo del sur del Bosque del Ocaso pavoneándose de su hazaña. Tengo la seguridad de que mi cajón está cerca de ese túmulo.$B$BPor favor, $N, recupera ese cajón, lo necesito.",
    ["O"] = "Devuelve a Abercrombie el cajón de Abercrombie.",
    ["T"] = "Ladrones ogros",
  },
  [135] = {
    ["D"] = "Me pregunto qué querría decir Wiley cuando habló de los Albañiles. Quizás fue un lapsus. ¿Podría estar relacionada la banda Defias con los Albañiles? El único que podría saberlo es Mathias Shaw, líder del IV:7. Enséñale la nota de Wiley, tal vez tenga algo que añadir a este misterio. Si tienes problemas para encontrarlo, busca en el Cuartel, en el Casco Antiguo.",
    ["O"] = "Lleva la Nota de Wiley a Mathias Shaw de Ventormenta.",
    ["T"] = "La hermandad de los Defias",
  },
  [136] = {
    ["D"] = "Si estás leyendo esto, el capitán Sanders estará sepultado bajo el agua. Así que mi tesoro es tuyo, solo tienes que seguir las pistas.$B$BPrimero tienes que encontrar mi baúl. Seguramente estará enterrado en la arena, en la costa Oeste de los Páramos de Poniente, cerca de los restos de naufragios. Habrá muchos restos, pero solo un ancla oxidada en la costa. ¡Encuentra el ancla y encontrarás mi baúl! Allí encontrarás también la siguiente pista.",
    ["O"] = "Encuentra el baúl del capitán Sanders y busca la siguiente pista.",
    ["T"] = "El tesoro escondido del capitán Sanders",
  },
  [137] = "_",
  [139] = {
    ["D"] = "Buscando en el barril descubres otro fragmento de papiro. En este se puede leer: Desde este barril, ve hacia el Norte. Tan recto como vuela el cuervo, anda hasta que veas una jarra vacía junto al solitario molino de viento sobre los riscos del mar. Si buscas alrededor de la jarra encontrarás lo que estás buscando.",
    ["O"] = "Busca la jarra vacía junto al molino de viento para encontrar la siguiente pista.",
    ["T"] = "El tesoro escondido del capitán Sanders",
  },
  [140] = {
    ["D"] = "Ciertamente, dentro de la vieja jarra hay otra pista sobre el tesoro de Sanders. La tinta se ha borrado en algunas partes y el papel huele a whisky, pero aún puedes entender algo: Ahora que has encontrado mi vieja jarra de whisky ¡casi has llegado al tesoro! Dirígete hacia el Oeste desde la botella y baja hasta la costa. Cuando llegues al agua, ¡sigue adelante! ¡Nada directo hacia el Oeste hasta encontrar la isla con la arqueta!",
    ["O"] = "Localiza la arqueta del capitán Sanders y ábrela para obtener tu recompensa.",
    ["T"] = "El tesoro escondido del capitán Sanders",
  },
  [142] = {
    ["D"] = "Tenemos que descubrir la localización de la Ladronera Defias. $N, según los informes de mi explorador se ha visto a un mensajero Defias en los caminos entre el Arroyo de la Luna, la Mina de la Costa del Oro y la Mina de Jango. Quiero que le captures. Si ofrece resistencia, mátalo y tráeme lo que esté llevando.",
    ["O"] = "Localiza al mensajero Defias de los Páramos de Poniente y lleva su mensaje a Mantorrecio.",
    ["T"] = "La hermandad de los Defias",
  },
  [143] = {
    ["D"] = "En el ejército de Ventormenta pasa algo raro; ya deberían haber llegado. No tenemos tiempo que perder. No voy a quedarme a mirar cómo se sacrifica la gente de Villa del Lago sin intentar conseguir ayuda. He oído que en los Páramos de Poniente se ha formado una Milicia Popular.$B$BLleva nuestra súplica a su jefe, Gryan Mantorrecio; puede que sus hombres nos ayuden hasta que llegue la Reserva.",
    ["O"] = "El magistrado Solomon quiere que lleves su petición a Gryan Mantorrecio a los Páramos de Poniente.",
    ["T"] = "Mensajero a los Páramos de Poniente",
  },
  [144] = {
    ["D"] = "Me duele leer la nota del magistrado Solomon. Evidentemente, no sabe que hay guerra en los Páramos de Poniente, si no, no pediría ayuda a la Milicia. Si Ventormenta no nos hubiera abandonado también a nosotros, no necesitaríamos una milicia.$B$BLlévale la respuesta a tu señor en Crestagrana, $C. Dile que lamento la pérdida de tantos hombres buenos.",
    ["O"] = "Lleva la respuesta de Gryan Mantorrecio al magistrado Solomon.",
    ["T"] = "Mensajero a los Páramos de Poniente",
  },
  [145] = {
    ["D"] = "Necesito tu ayuda una vez más. He escrito a Lord Cerranegro para pedir que mande a su Guardia Nocturna como refuerzo de la defensa de Villa del Lago. Es imprescindible que reciba este mensaje cuanto antes, pero debes ser extremadamente prudente. Cerranegro es el alcalde de Villa Oscura, en pleno corazón del Bosque del Ocaso. No te salgas de los caminos.",
    ["O"] = "El magistrado Solomon quiere que lleves una carta a Lord Cerranegro a Villa Oscura.",
    ["T"] = "Mensajero a Villa Oscura",
  },
  [146] = {
    ["D"] = "Si no conociera a Solomon, diría que se ha vuelto loco. ¿Que envíe a La Guardia Nocturna a Villa del Lago porque Ventormenta no envía ayuda? La Guardia Nocturna se creó porque Ventormenta nos abandonó cuando nos infestó esta magia inmunda. Si envío a la Guardia, esta ciudad sería invadida antes de que mis guardias lleguen al Linde del Bosque.$B$BLlévale la respuesta a tu señor en Villa del Lago.",
    ["O"] = "Lleva la respuesta de Lord Cerranegro al magistrado Solomon.",
    ["T"] = "Mensajero a Villa Oscura",
  },
  [147] = {
    ["D"] = "Si el Coleccionista se está llevando oro de nuestras minas, ¡está robando al Rey! Lleva al Coleccionista ante la justicia. Y tráeme a mí el anillo que se mencionaba en la nota. Podría indicarnos para quién trabaja el Coleccionista...$B$BLa nota dice que el Coleccionista se esconde en la Plantación de Calabazas de Brackwell. Deberías buscarlo allí.\"",
    ["O"] = "Encuentra y mata al Coleccionista y vuelve junto al alguacil McCree con la sortija del coleccionista.",
    ["T"] = "La persecución",
  },
  [148] = {
    ["D"] = "Ay, ¿qué puede hacer un anciano como yo? Necesito suministros de Villa Oscura, pero está tan lejos y yo soy tan viejo y tan débil... No sobreviviría al viaje.$B$B¿Podrías ir a Villa Oscura y traerme un poco de hilo de cabello de fantasma? Puedes conseguirlo de Lady Eva. Ve a su casa en la plaza de Villa Oscura.",
    ["O"] = "Habla con Lady Eva.",
    ["T"] = "Suministros de Villa Oscura",
  },
  [149] = {
    ["D"] = "Mary la Ciega es un desgraciado y triste espíritu que mora en una vieja granja en las colinas del sur de Villa Oscura.$B$BTen, coge este peine espectral y pide a Mary la Ciega que se peine el cabello. Luego tráeme el peine y yo extraeré de él el cabello de fantasma que necesito para crear el hilo.$B$BY ten cuidado... cerca de la casa acechan seres oscuros...",
    ["O"] = "Lleva el peine espectral a Mary la Ciega.",
    ["T"] = "Hilo de cabello de fantasma",
  },
  [150] = {
    ["D"] = "Los múrlocs nos están complicando la vida. Se están instalando en las orillas del Lago Sempiterno y pescan en cantidades increíbles. ¡Tenemos que echarlos o acabarán con todos los peces del lago!$B$B¿Te apetece cazar múrlocs? Tráeme 8 aletas y te llevarás algo a cambio...",
    ["O"] = "Lleva 8 aletas múrloc al maestro de embarcadero Baren a Villa del Lago.",
    ["T"] = "Cazadores furtivos",
  },
  [151] = {
    ["D"] = "¡Pobre vieja Blanchy! La bestia está agotada después de todo el trabajo que le dimos. Le di de comer antes de dejar la granja, pero no esperábamos que se rompiera el carro. Si pudieras traerle algunos puñados de avena te estaría muy agradecida.$B$BSeguro que podrás encontrar algunos por las granjas de los Páramos de Poniente, si puedes librarte de esas horribles máquinas que los han invadido. Hay varias granjas al suroeste de aquí.",
    ["O"] = "Verna Cejade de los Páramos de Poniente quiere que le lleves 8 puñados de avena.",
    ["T"] = "La pobre y vieja Blanchy",
  },
  [152] = {
    ["D"] = "Seguramente habrás visto todos los restos de naufragios a lo largo de la costa. El Mare Magnum es traicionero. La costa de los Páramos de Poniente tiene que estar limpia, de ese modo si algún marinero llega hasta nuestras costas, no correrá ningún peligro. Pero los múrlocs son un gran problema.$B$BMata 7 cazamareas, 7 guerreros, 7 oráculos y 7 correcostas y procuraré que seas $Grecompensado:recompensada;.",
    ["O"] = "Mata 7 cazamareas, 7 guerreros, 7 oráculos y 7 correcostas y vuelve con el capitán Grisillo en el Faro de Poniente.",
    ["T"] = "Hay moros en la costa",
  },
  [153] = {
    ["D"] = "La tapadera de los Defias cambia constantemente. He estado siguiendo sus movimientos desde hace algún tiempo. Aunque, recuerda, he averiguado que muchos miembros de la banda se identifican por sus pañuelos de cuero rojo.$B$BTráeme 15 de esos pañuelos y procuraré que seas $Grecompensado:recompensada;.",
    ["O"] = "Lleva 15 pañuelos de cuero rojo al explorador Galiaan en la Colina del Centinela.",
    ["T"] = "Pañuelos rojos de cuero",
  },
  [155] = {
    ["D"] = "¿Así que Mantorrecio envía a $Gun:una; $R enclenque para protegerme? Supongo que tendrás que hacerlo. Pero será mejor que traigas algunos amigos.$B$B¿Ya conoces el trato, verdad? Tú me guardas las espaldas y yo te llevo hasta la Ladronera Defias. La banda Defias busca mi cabeza, así que si me ven contigo, intentarán matarme.$B$BAvísame cuando tú y los amigos que puedas reunir estéis listos para emprender la marcha.",
    ["O"] = "Escolta al Traidor de los Defias a la ladronera secreta de la Hermandad Defias. Cuando el Traidor de los Defias te enseñe dónde se esconden VanCleef y sus hombres, regresa con Gryan Mantorrecio con toda la información.",
    ["T"] = "La hermandad de los Defias",
  },
  [156] = {
    ["D"] = "Si me traes algunas flores podridas, podré prepararte algo de zumo de zombi.$B$BLas flores podridas crecen en lugares extraños. Se dice que se encuentran en el interior de las calaveras de los horrores esqueléticos y de los malignos esqueléticos. Claro que nunca lo he comprobado...$B$BEn el Cementerio del Cerro del Cuervo encontrarás esqueletos de esos a montones. Mientras buscas flores podridas, podrías aprovechar para deshacerte de algunos de ellos. Villa Oscura te estaría muy agradecida.",
    ["O"] = "Recoge 8 flores podridas y llévaselas al tabernero Smitts a Villa Oscura.",
    ["T"] = "Busca flores podridas",
  },
  [157] = {
    ["D"] = "Aquí tienes tu hilo, $N. Entre muchas otras cosas, sirve para atar espíritus malvados o para coser la vida en objetos inanimados.",
    ["O"] = "Lleva el hilo de cabello de fantasma a Abercrombie, a su choza al norte del Cementerio del Cerro del Cuervo.",
    ["T"] = "Devolver el pelo",
  },
  [158] = {
    ["D"] = "Necesito una botella de licor. De zumo de zombi. El tabernero Smitts, del Mesón del Cuervo Escarlata de Villa Oscura, debería tener alguna botella.$B$BPor favor, date prisa, $N. ¡Las noches empiezan a ser frías y necesito ese fuego en mis venas!",
    ["O"] = "Habla con el tabernero Smitts.",
    ["T"] = "Zumo de zombi",
  },
  [160] = {
    ["D"] = "Lleva esta nota a Ello Cerranegro, el alcalde de Villa Oscura.$B$BÉl y yo somos viejos amigos. Seguro que cuando la lea y sepa lo que has hecho por mí... ¡estará tan contento como yo! ¡Ja, ja, ja, ja!",
    ["O"] = "Lleva la carta de Abercrombie a Ello Cerranegro, el alcalde de Villa Oscura.",
    ["T"] = "Nota para el alcalde",
  },
  [161] = {
    ["D"] = "Si mis sospechas son correctas, esto es un tipo de explosivo. Debemos hacerlo analizar por un experto en explosivos para saber qué tenemos exactamente entre manos.$B$BConozco al hombre más adecuado. A Ashlan Risapétrea le asignaron vigilar a los ogros de la Fortaleza de Mo\'Grosh, al este. Ve en esa dirección y encuéntralo para que pueda decirnos qué sustancia es esta.",
    ["O"] = "El ingeniero jefe Hinderweir quiere que lleves los polvos al experto en explosivos Ashlan Risapétrea, en la Fortaleza de Mo\'Grosh.",
    ["T"] = "Se aproxima una oscura amenaza",
  },
  [163] = {
    ["D"] = "Algo ocurre en Cerro del Cuervo, $N. Calor jura que alguien –o algo– está rondando los edificios de la zona. Ha visto sombras moviéndose dos noches seguidas y dice que hace tres noches vio luz en una de las ventanas.$B$BSi, como dices, te preocupa la seguridad del Bosque del Ocaso, averigua qué monstruo acecha el Cerro del Cuervo.",
    ["O"] = "Averigua qué está acechando Cerro del Cuervo.",
    ["T"] = "Cerro del Cuervo",
  },
  [165] = {
    ["D"] = "Antes venía por aquí un anciano... venía a comprar suministros con bastante frecuencia, pero hace un tiempo que no lo veo. Vive en una choza cerca del Cementerio del Cerro del Cuervo, si no recuerdo mal. ¿Podrías ir a ver si le ha ocurrido algo?",
    ["O"] = "Ve a ver cómo está el anciano de la choza que hay cerca del Cementerio del Cerro del Cuervo.",
    ["T"] = "El Ermitaño",
  },
  [167] = {
    ["D"] = "Estábamos en las profundidades de una gran mina en los Páramos de Poniente, oculta tras un granero en el Arroyo de la Luna. No tenemos ni idea de dónde salieron esos ladrones. En fin, el túnel se derrumbó sobre nosotros. Conseguí salir, pero los otros... bueno... de los otros no sé nada.$B$BCreo que tú podrías tener más suerte ahí abajo. Si pudieras buscar a mi hermano, te lo agradecería mucho. Siempre llevaba su insignia de la Liga de Expedicionarios. Si no puedes encontrarle, al menos la insignia me daría algo de paz mental.",
    ["O"] = "Lleva la insignia de la Liga de Expedicionarios del supervisor Cardortiga a Wilder Cardortiga, en Ventormenta.",
    ["T"] = "Oh, hermano...",
  },
  [168] = {
    ["D"] = "Muchos de mis amigos murieron ese horrible día cuando se derrumbó el túnel de la mina. Si tus aventuras te llevan de casualidad a esa parte de la mina en el Arroyo de la Luna, por favor, busca alguna señal que pueda identificarles. Si te encuentras con alguna de sus Tarjetas del Sindicato Minero, tráemelas y me aseguraré de que sus familias reciban la resolución de este horrible accidente.",
    ["O"] = "Recupera 4 Tarjetas del Sindicato Minero y llévaselas a Wilder Cardortiga, en Ventormenta.",
    ["T"] = "Recolección de recuerdos",
  },
  [169] = {
    ["D"] = "Se busca a Gath\'Ilzogg por dirigir los ataques Roca Negra al Fuerte de Petravista y las incursiones en Villa del Lago.$B$BGath\'Ilzogg es extremadamente peligroso. Se encuentra en el Fuerte maquinando nuevos crímenes del clan contra la humanidad. Debemos sofocar la infestación de orcos cueste lo que cueste. Se ofrece recompensa a quien presente la cabeza de Gath\'Ilzogg al magistrado Solomon.",
    ["O"] = "Mata a Gath\'Ilzogg y lleva su cabeza al magistrado Solomon a Villa del Lago para recibir tu recompensa.",
    ["T"] = "Se busca: Gath\'Ilzogg",
  },
  [170] = {
    ["D"] = "Espero que estés aquí para echar una mano, $C. Después del último ataque de los troggs, nos vendría bien cualquier ayuda.$B$BHe oído que esos malnacidos han estado apareciendo por todas partes y parece que el Valle de Crestanevada no es una excepción. Han sido vistos en los montes del sureste y cerca del lago helado.$B$BY eso no es todo. Hace unas noches atacaron e invadieron nuestro campamento del oeste.$B$BNecesitamos ayuda, $Gmuchacho:muchacha;. Necesitamos brazos fuertes para hacer retroceder a los troggs.",
    ["O"] = "Balir Martilloescarcha quiere que mates a 6 troggs Comepiedras y a 6 troggs Comepiedras fornidos.",
    ["T"] = "Una nueva amenaza",
  },
  [171] = {
    ["D"] = "Has sido muy $Gbueno:buena; conmigo, $N. Nunca había soñado con divertirme tanto en la Semana de los Niños. Gracias.$B$BOjalá todos los huérfanos tuvieran la suerte de hacer lo que hemos hecho nosotros. ¿Quieres que vayamos a buscar a Chichón Perinola de nuevo y que le compremos un expositor para espadas de gomaespuma? Creo que todavía está empujando esa vagoneta en Los Canales, fuera del Distrito de Mercaderes. ¡Así todo el mundo en el orfanato podrá hacer algo divertido!$B$BDespués, ya será hora de que me vaya a casa.",
    ["O"] = "Compra un expositor para espadas de gomaespuma a Chichón Perinola y después habla con la supervisora de huérfanos Ruiseñor en el Distrito de la Catedral para llevar a tu pupilo de vuelta al orfanato de Ventormenta.",
    ["T"] = "Alcaide de la Alianza",
  },
  [172] = {
    ["D"] = "¿Quieres ayudarnos aquí en el orfanato? $GBendito:Bendita; seas, $Gamigo:amiga;. Tu ayuda durante la Semana de los Niños no tiene precio.$B$BCoge este silbato. Solo podrás utilizarlo durante un breve período de tiempo, básicamente, hasta que se acabe la Semana de los Niños. Cuando lo uses, podrás hablar con el niño que esté a tu cuidado. Utilízalo de nuevo para que el niño se vaya.$B$BSea como sea, $N, utiliza el silbato y conoce a tu pupilo. Seguro que el niño estará encantado de conocerte.",
    ["O"] = "Usa el silbato de huérfano orco para hablar con el niño al que cuidarás durante la Semana de los Niños.",
    ["T"] = "Semana de los Niños",
  },
  [173] = {
    ["D"] = "La oscuridad se siente inexorablemente atraída hacia el Bosque del Ocaso. El maestro Carevin lucha por erradicar el mal y la herejía. Y con nuestros esfuerzos mantenemos a salvo a las gentes de Villa Oscura.$B$B¿Te crees $Gmerecedor:merecedora; de unirte a nosotros?$B$BHace tiempo pensaba como tú. La complacencia de la Guardia me decepcionó y acabé uniéndome al maestro Carevin. Si quieres probar tu valía, no utilices las palabras.$B$BMide tus habilidades con un huargen delirante tejesombras Penumbría en la Arboleda del Destello y en El Vergel Pútrido.",
    ["O"] = "Mata a 6 huargen Tejesombras Penumbría para Calor de Villa Oscura.",
    ["T"] = "Huargen en el bosque",
  },
  [176] = {
    ["D"] = "Se busca: Hogger.$B$BUn gnoll enorme, Hogger, está al acecho en el bosque al suroeste de Elwynn. Ha sofocado todos los intentos de capturarlo.$B$BEl ejército de Ventormenta ofrece una generosa recompensa por atrapar al gnoll. Para ganarla, los cazarrecompensas tendrán que adentrarse en el bosque y eliminar a la bestia.",
    ["O"] = "Encárgate de \"la situación Hogger\" y vuelve a ver al alguacil Dughan.",
    ["T"] = "Se busca: Hogger",
  },
  [177] = {
    ["D"] = "El espejo era uno de mis tesoros más preciados hasta... el incidente. Después de lo que ocurrió, lo escondí detrás del granero. Antes venía por aquí un necrófago demente... Quizás lo cogió él. Se dice que esa bestia inmunda vive en el Cementerio del Jardín Sereno.",
    ["O"] = "Quita el espejo de Mary la Ciega al necrófago demente y llévaselo a Viktori a Villa Oscura.",
    ["T"] = "Mira las estrellas",
  },
  [178] = {
    ["D"] = "<Oyes una voz...>$B$BNo te asustes; soy Theocritus, Mago Supremo de la Torre de Azora, en el Bosque de Elwynn. El colgante es un medio de comunicación entre los gnolls Pielsombra y su señor, Morganth.$B$BHe investigado durante meses y creo que yo también puedo usar los colgantes. Si oyes este mensaje, mi hechizo ha funcionado.$B$BTráeme el colgante y te recompensaré.",
    ["O"] = "Lleva el colgante Pielsombra descolorido a Theocritus el Mago a la Torre de Azora, en el Bosque de Elwynn.",
    ["T"] = "Theocritus recupera el colgante",
  },
  [179] = {
    ["D"] = "¿Qué tenemos aquí? Tienes pinta de necesitar algo para mantener las manos calientes, ¿mm?$B$BYo te diré lo que necesitas: un par de estupendos guantes bien calentitos. Y, como soy un alma generosa, me encantaría proporcionarte ese par de guantes. Pero con una condición.$B$BNecesito que me consigas carne de lobo. Es un buen trato, ¿mm? Tú me traes un poco de carne de lobo y yo evito que pierdas los dedos por congelación. ¿Y bien? ¿Qué dices?",
    ["O"] = "Sten Brazorrecio quiere 8 piezas de carne de lobo dura.",
    ["T"] = "Los enanos modistos",
  },
  [180] = {
    ["D"] = "Se busca al teniente Fangore$B$BSe acusa al jefe del clan gnoll Pielsombra de colaborar con el enemigo del Reino Morganth. El magistrado Solomon ha decretado la pena de muerte para este traidor a Ventormenta. Su zarpa se considerará prueba de su muerte.",
    ["O"] = "Mata al teniente Fangore y lleva su zarpa al magistrado Solomon a Villa del Lago.",
    ["T"] = "¡Se busca!",
  },
  [182] = {
    ["D"] = "Los trols están agresivos. Más de lo normal, quiero decir. Ya les hemos visto crear avanzadas de guerra, pero esto... Esto es diferente.$B$BPero lo que más importa es que Yunquemar siga protegida. No pienso dejar que estos trols rodeen Yunquemar mientras los troggs la mantienen ocupada. Ve y diezma sus filas. Tienen que enterarse de que aún nos quedan defensas.",
    ["O"] = "Mata a 10 crías de trol Peloescarcha.",
    ["T"] = "La amenaza trol",
  },
  [183] = {
    ["D"] = "No hay nada como pasar un día cazando jabalíes, ¿eh?$B$BAunque con la de jabalíes que tenemos en el Valle de Crestanevada, la cosa pierde un poco de gracia. Ni siquiera hay que provocarlos. Ya están todos enfadados e inquietos, no necesitan ayuda para eso. De hecho, últimamente hay tantos jabalíes en la zona que mi caza diaria se está convirtiendo en una actividad peligrosa.$B$BPara abreviar, si pudieras ayudarme a matar algunos jabalíes, te lo agradecería mucho.",
    ["O"] = "Talin Ojo Ávido quiere que mates a 12 jabalíes del risco pequeños.",
    ["T"] = "Cazador de jabalíes",
  },
  [184] = {
    ["D"] = "Estas son las viejas escrituras de una extensión de tierras de labranza de los Páramos de Poniente. Están firmadas por un tal Theodore Cejade y por su mujer, Verna. En el reverso de las escrituras hay unas palabras garabateadas con gran prisa:$B$B\"Presionamos a Cejade y conseguimos sus escrituras. Pensé que te vendría bien si querías falsificar una para ti.$B$BLos Cejade no nos darán problemas. La última vez que los vi estaban saliendo de los Páramos de Poniente con un carro roto.\"$B$BCrees que probablemente los Cejade querrán recuperar sus escrituras...",
    ["O"] = "Lleva las escrituras de Cejade a la Finca de Jansen en Páramos de Poniente.",
    ["T"] = "Escrituras de Cejade",
  },
  [185] = {
    ["D"] = "Cuando era una niña, mi padre me enseñó a cazar tigres.$B$BVerás que los felinos jóvenes son mucho más fáciles de seguir y cazar. Es fácil encontrar tigres jóvenes cerca del campamento de expedición.$B$BPon a prueba tus habilidades de rastreo y veamos si puedes dar caza a alguna de las bestias. Comienza la caza cerca de nuestro campamento, al sur.",
    ["O"] = "Caza 10 tigres jóvenes de Tuercespina.",
    ["T"] = "Caza al tigre",
  },
  [186] = {
    ["D"] = "Los tigres jóvenes son una cosa y los felinos adultos otra. Cazar tigres de Tuercespina adultos es un reto aún mayor.$B$BLos felinos adultos suelen pasar el día al sur de la Base de Kurzen, al este del río.",
    ["O"] = "Caza 10 tigres de Tuercespina.",
    ["T"] = "Caza al tigre",
  },
  [187] = {
    ["D"] = "Los tigres de Tuercespina mayores son los más fuertes de la manada. Más de $Gun:una; $R $C ha caído frente a esos dientes afilados como cuchillas y esas feroces garras.$B$BAjeck se entusiasmará cuando sepa que los has abatido. La mayor manada de estos tigres se encuentra al sur, cerca del Cataclismo, en la frontera de El Cabo de Tuercespina.",
    ["O"] = "Caza 5 tigres de Tuercespina mayores.",
    ["T"] = "Caza al tigre",
  },
  [188] = {
    ["D"] = "Las leyendas hablan de una tigresa muy evasiva... un felino tan poderoso que hace parecer enclenques a los tigres mayores. Los cazadores de la Expedición de Nesingwary le han estado dando caza sin éxito. Muchos han caído víctimas de sus garras lacerantes; sus cuerpos se pudren en la falda de su colina, al norte de Grom\'gol.$B$BLlaman a esta bestia Sin\'Dall.$B$BDecídete a conseguir lo que $Gningún:ninguna; $R: dar caza, matar y tomar la garra de Sin\'Dall.",
    ["O"] = "Caza a Sin\'Dall y regresa con su garra.",
    ["T"] = "Caza al tigre",
  },
  [189] = {
    ["D"] = "¡Malditos trols! ¡Han estado matando a nuestros agentes en la jungla! Ya he perdido a un buen número de mis mejores guardias en esas escaramuzas. ¡Los costes se multiplican! ¿Cómo se lo voy a explicar al Barón Revilgaz?$B$BAcción decisiva... Piensa, Kebok... no te asignaron al puesto que tienes hoy por estar de brazos cruzados... ¡Lo tengo! Tú... $N, ¿verdad? Me ayudarás, ¿no? ¡Hay dinero en juego!$B$BEste es el trato: tráeme las orejas de quince trols Sangrapellejo y te compensaré generosamente.",
    ["O"] = "Consigue 15 orejas Sangrapellejo y llévaselas a Kebok en la Bahía del Botín.",
    ["T"] = "Orejas de Sangrapellejo",
  },
  [190] = {
    ["D"] = "Si quieres formar parte del grupo de caza de Hemet Jr., tendrás que demostrar que eres capaz de cazar una pantera. No te preocupes, empezaremos con algo fácil: céntrate en las panteras jóvenes por ahora. A medida que vayas mejorando y ganando experiencia como $Gcazador:cazadora;, tendrás que enfrentarte a presas más peligrosas.$B$BEmpieza por dirigirte al este. Vuelve conmigo cuando te hayas convertido en $Gun maestro cazador:una maestra cazadora; de panteras.",
    ["O"] = "Caza 10 panteras jóvenes.",
    ["T"] = "Caza la pantera",
  },
  [191] = {
    ["D"] = "Las panteras más jóvenes se rindieron fácilmente ante ti. Las panteras más adultas serán más fuertes, más rápidas y más difíciles de derrotar.$B$BSus huellas se dirigen al oeste.",
    ["O"] = "Caza 10 panteras.",
    ["T"] = "Caza la pantera",
  },
  [192] = {
    ["D"] = "Y ahora vamos con lo difícil. Cuando una pantera envejece, se vuelve más astuta, se mezcla con el entorno y es muy difícil localizarla. Estas astutas panteras se llaman \"Faucesombrías\" por su tendencia a aparecer repentinamente de entre las sombras... con una boca hambrienta llena de colmillos.$B$BSi quieres demostrar que eres $Gun verdadero cazador:una verdadera cazadora; de panteras, las Faucesombrías son el único modo de hacerlo. Acechan por el sureste, al otro lado del río.",
    ["O"] = "Caza 5 panteras Faucesombrías.",
    ["T"] = "Caza la pantera",
  },
  [193] = {
    ["D"] = "Casi has demostrado ser $Gun maestro cazador:una maestra cazadora; de panteras, pero hay una que se te ha escapado.$B$BHay canciones que hablan de una poderosa pantera llamada Bhag\'thera. Veamos si puedes usar tus habilidades para matar a la gran bestia.$B$BSi le traes el colmillo de Bhag\'thera a Erlgadin demostrarás ser $Gun maestro cazador:una maestra cazadora; de panteras.",
    ["O"] = "Consigue el colmillo de Bhag\'thera.",
    ["T"] = "Caza la pantera",
  },
  [194] = {
    ["D"] = "¿Así que crees que tus habilidades de caza están en buena forma? Veamos si es cierto. Vete a la jungla y mata 10 raptores de Tuercespina. Veremos lo $Gbuen cazador:buena cazadora; que eres.$B$B¡Y no, no te diré dónde encontrarlos! Localizar a las bestias es la mitad del desafío.$B$BVale, ve al oeste.",
    ["O"] = "Caza 10 raptores de Tuercespina.",
    ["T"] = "Caza al raptor",
  },
  [195] = {
    ["D"] = "Ahora aumentemos la dificultad del desafío. Bastará con que te adentres en la jungla y captures 10 raptores colazote. Por supuesto, serán más fuertes que los raptores a los que ya has eliminado.$B$BLos raptores colazote suelen andar al sureste de aquí, cerca de las Ruinas de Kal\'ai.",
    ["O"] = "Caza 10 raptores colazote.",
    ["T"] = "Caza al raptor",
  },
  [196] = {
    ["D"] = "Una raza muy reservada de raptores que los locales llaman \"acechadores de la selva\" merodea por los bosques del sur, al otro lado del río. Son mucho más difíciles de rastrear y matar que las otras razas que has probado hasta ahora.$B$BSi matas a estos acechadores de la selva, sin duda tendremos una buena historia que contar alrededor de la fogata.",
    ["O"] = "Caza 10 acechadores de la selva.",
    ["T"] = "Caza al raptor",
  },
  [197] = {
    ["D"] = "Antes de regresar a la Expedición de Nesingwary, tendrás que realizar lo imposible: cazar al raptor que ningún otro cazador ha conseguido matar. La bestia se llama Tethis y se sabe que tiene aterrorizada toda la esquina sureste del Norte de la Vega de Tuercespina.$B$BSi le entregas la garfa de Tethis a ese enano cazador, demostrarás que no eres $Gun:una; $R común y corriente.",
    ["O"] = "Caza a Tethis.",
    ["T"] = "Caza al raptor",
  },
  [199] = {
    ["D"] = "Coges una muestra de esas partículas con olor extraño.",
    ["O"] = "Enséñale la pista al ingeniero jefe Hinderweir.",
    ["T"] = "Se aproxima una oscura amenaza",
  },
  [200] = {
    ["D"] = "Si Kurzen sabe lo del secreto de Thorsen, significa que tenemos un espía. ¡Debemos determinar cómo se filtró el secreto!$B$BEl contacto de Thorsen en la Base de Kurzen era el corredor Herod. Herod es el oficial de logística de Kurzen y lo sabe todo sobre la Base de Kurzen. Si consigues hacerte con las notas o los registros de Herod, quizás podamos saber cómo descubrieron a Thorsen.$B$BCuando todavía estábamos con Kurzen, la oficina de Bookie estaba en la parte de arriba de la casa de dos pisos de la Base. Probablemente, sus registros sigan allí.",
    ["O"] = "Encuentra los registros del corredor Herod.",
    ["T"] = "Corredor Herod",
  },
  [201] = {
    ["D"] = "Me han informado de un grupo de cazadores acampados río arriba del campamento minero de Ventura y Cía. en Lago Nazferiti. Normalmente los ignoraría, pero dicen que uno de los cazadores es Hemet Nesingwary Jr., que tiene muy, pero que muy buenos contactos; hay que velar por su seguridad.$B$BVe a echar un vistazo al campamento, $N, a ver qué están haciendo por allí.",
    ["O"] = "Encuentra el campamento de los cazadores e informa sobre su ubicación a Krazek en la Bahía del Botín.",
    ["T"] = "Investiga el campamento",
  },
  [202] = {
    ["D"] = "Has demostrado tu valía otra vez y tus recursos parecen rivalizar con los del mismísimo Kurzen. Quizás seas la salvación de nuestro grupo de rebeldes... si consigues enfrentarte a Kurzen y vencer.$B$BLidera a sus hombres desde las profundidades de Las Reservas. Debes entrar en la cueva, derrotar a sus defensores y enfrentarte a Kurzen y a sus líderes.$B$BAdiós, $N. Mi esperanza, y el destino de mis hombres, van contigo.",
    ["O"] = "Mata 6 unidades de élite de Kurzen y 4 subjefes de Kurzen. Lleva la cabeza de Kurzen al teniente Doren en el Asentamiento Rebelde.",
    ["T"] = "Coronel Kurzen",
  },
  [203] = {
    ["D"] = "Estamos luchando una batalla perdida con el coronel Kurzen. No solo es un estratega brillante, sino que nos supera en número, ¡al menos 10 a 1! Para sobrevivir, tenemos que usar sus propias tácticas de cobertura contra él.$B$BVete a su campamento del este y mata a sus hombres. A muchos de ellos. Y sal de ahí antes de que los refuerzos te alcancen.",
    ["O"] = "Mata a 15 jíbaros de Kurzen.$B$BVuelve a ver a la sargento Yohwa en el Asentamiento Rebelde.",
    ["T"] = "La segunda rebelión",
  },
  [204] = {
    ["D"] = "Para tener una oportunidad contra Kurzen, necesitamos neutralizar su fuerza de curación. Ha reunido a un gran número de auxiliadores que curarán a sus tropas con pócimas de la selva y envenenarán a las nuestras con extractos de helecho venenoso.$B$BNecesitamos esas pócimas y extractos para nuestro propio provecho. Busca en la Base de Kurzen. Sus auxiliadores y jíbaros tendrán las pócimas y, probablemente, guardan los extractos en algunas de las cajas de suministros del campamento.",
    ["O"] = "Lleva 7 pócimas de la selva y 1 extracto de helecho venenoso a la sargento Yohwa en el Asentamiento Rebelde.",
    ["T"] = "Mala medicina",
  },
  [206] = {
    ["D"] = "Quizás Kurzen esté muerto, pero... eso que le volvió maligno sigue vivo. Gracias a mis estudios he descubierto esto: hay un ogro mago, Mai\'Zoth, que vive en un Túmulo Ogro en las montañas del este de Tuercespina. Se dice que utiliza magia de control mental y creo que fue él quien corrompió a Kurzen.$B$BSi permitimos que Mai\'Zoth siga con vida, su magia podría influenciar a otros y quizás incluso provocar que apareciese otro Kurzen.$B$BMátale y tráeme su Ojo de la mente, el artefacto que utiliza para corromper.",
    ["O"] = "Lleva el Ojo de la mente al hermano Nimetz en el Asentamiento Rebelde.",
    ["T"] = "Mai\'Zoth",
  },
  [208] = {
    ["D"] = "$N, eres $Gun cazador digno:una cazadora digna; de estar en nuestro grupo. Desde los campos de batalla de Lordaeron hasta las profundidades de la jungla, muy pocas veces he visto tal destreza con las armas como la que has demostrado tú.$B$BHas matado al feroz Sin\'Dall, a la esquiva Bag\'thera y al traicionero Tethis. Pero el verdadero trofeo es la cabeza del rey Bangalash. Ese tigre blanco es la razón por la que estoy aquí, pero sigue provocándome desde su refugio al sur. Mata al rey Bangalash y tu dominio de la cacería será insuperable.",
    ["O"] = "Caza la cabeza del rey Bangalash, el gran tigre blanco.",
    ["T"] = "Caza mayor",
  },
  [209] = {
    ["D"] = "Matar los suficientes tigres para mantener la exportación de colmillos de tigre a las islas de los Mares del Sur, es caro y complicado. Por suerte, hemos desarrollado una técnica que nos permite coger objetos normales en forma de cuerno y convertirlos en falsificaciones perfectas.$B$BLo más parecido que hemos encontrado, sorprendentemente, son los colmillos de trols Machacacráneos.$B$BEh, antes de que digas nada, ojo de comprador que no ve, corazón de tonto que no siente, ¿tengo razón? Tráeme muchos colmillos para que podamos ponernos a trabajar en el pedido del mes.",
    ["O"] = "Consigue 18 colmillos Machacacráneos y llévaselos a Kebok.",
    ["T"] = "Colmillos Machacacráneos",
  },
  [211] = {
    ["D"] = "¡Ay, se acerca el momento de atacar Andorhal y de hacer salir al exánime que lo controla!$B$BEs en el interior de las ruinas de la ciudad donde el exánime, Araj el Invocador, tiene sus dominios. Está custodiado por numerosos miembros de la Plaga que rodean los restos del centro de la ciudad, y su séquito personal de guardianes es realmente formidable. Necesitarás muchos aliados para tener alguna oportunidad de enfrentarte a él con éxito.$B$B¡Acaba con Araj, $N, y tráeme un trozo de su filacteria como prueba!",
    ["O"] = "Lleva el trozo de filacteria de Araj al comandante Ashlam Puñovalor del Campamento del Orvallo, en las Tierras de la Peste del Oeste.",
    ["T"] = "Ah, Andorhal",
  },
  [212] = {
    ["D"] = "Uno de mis platos más famosos es basilisco helado en vinagreta de lima. Pero el problema es que es imposible mantener fresca la carne y tengo que servir el plato esta noche en el cumpleaños de un noble.$B$BSi pudieras traerme un anca de basilisco helado de un Basilisco Ojogélido recién muerto y rápidamente... ¡estaré en deuda contigo!$B$BLos Basiliscos Ojogélido suelen vagar por las dunas de la playa al noroeste de la Vega de Tuercespina, así que te espera un largo viaje. ¡Y rápido!",
    ["O"] = "Mata un Basilisco Ojogélido, consigue un anca de basilisco helado y llévasela a Angus Stern en El Ermitaño Taciturno.",
    ["T"] = "Un plato bien frío",
  },
  [213] = {
    ["D"] = "¡Lo ha hecho! Ya es bastante malo que Gelriz se meta por el medio de los magnates que nombraron los príncipes mercantes como para que ahora intente meterse con el pirata más famoso.$B$BRevilgaz no lo tolerará y me ha dicho que me encargue del problema a mi manera.$B$B¿Mi manera? El robo. Los geólogos de Ventura y Cía. que están cerca del Lago Nazferiti están muy interesados en esos extraños cristales azules que han encontrado en las minas. Tráeme muestras de los cristales de los geólogos, no me importa lo que tengas que hacer para conseguirlas.",
    ["O"] = "Consigue 8 cristales caídos y llévaselos a Kebok en la Bahía del Botín.",
    ["T"] = "OPA hostil",
  },
  [214] = {
    ["D"] = "No es ningún secreto que la marca distintiva de la banda Defias son los pañuelos rojos. Pero hemos averiguado que el material del que están hechos los pañuelos sirve para identificar el rango del miembro.$B$BDicho esto, quiero que elimines tantos miembros Defias de alto rango como puedas. Podrás encontrarlos en la ladronera secreta de VanCleef. Cuando hayas terminado tráeme 10 pañuelos de seda roja y sabré recompensarte. Buena suerte, $N.",
    ["O"] = "La exploradora Riell de la Colina del Centinela quiere que le lleves 10 pañuelos de seda roja.",
    ["T"] = "Pañuelos rojos de seda",
  },
  [215] = {
    ["D"] = "Gracias por salvarme, $N. Sé lo que parece esto, pero... deja que te lo explique.$B$BEl teniente Doren me encargó alistarme con los hombres de Kurzen para que pudiera espiarles y llevar información a los rebeldes. Lo que has visto es lo que yo pensaba que sería mi oportunidad para infiltrarme en la Base de Kurzen. Me equivoqué y, otra vez, gracias por salvarme.$B$BEsto era una misión de alto secreto, así que no se lo puedes decir a nadie. Pero si hablas con Doren, te lo explicará.",
    ["O"] = "Habla con el teniente Doren en el Asentamiento Rebelde.",
    ["T"] = "Los secretos de la jungla",
  },
  [216] = {
    ["D"] = "¡Los fúrbolgs Piel de Cardo son una amenaza para nuestra expansión! El Poblado Piel de Cardo, al oeste, obstruye nuestro paso entre este lugar y el Puesto del Hachazo. Es un camino que rodearía el pueblo de Astranaar; sin él, ofrecemos a la Alianza una vía más para la expansión.$B$B$N, no podemos permitir... ¡un hueco en el camino de expansión!$B$BVe al Poblado Piel de Cardo y acaba con los fúrbolgs. Recibirás una compensación por esta tarea y lo que es más importante, ¡estarás cumpliendo con tu deber para con la Horda!",
    ["O"] = "Karang Amakkar en Avanzada Grito Infernal quiere que acabes con 15 fúrbolgs Piel de Cardo de cualquier tipo en el Poblado Piel de Cardo.",
    ["T"] = "Entre una roca y un Piel de Cardo",
  },
  [217] = {
    ["D"] = "Mi estratagema ha funcionado. Parece que estas bestias de Loch Modan siguen a un jefe llamado Gromug. Y lo tienen muy bien protegido. Siempre va acompañado de dos de sus mejores guardias, Rechín y Camorrista.$B$BSi te ves capaz, $N, quiero que dirijas el asesinato de Gromug y sus dos matones dentro de esta cueva. ¡Ve! Yo me liberaré de estas ataduras y me reuniré contigo en breve.",
    ["O"] = "Mata al jefe trogg, Gromug, y a sus dos matones, Rechín y Camorrista. Informa al capitán Rugelfuss en Thelsamar.",
    ["T"] = "Un golpe decisivo",
  },
  [218] = {
    ["D"] = "Soren acertó en enviarte en misión de reconocimiento. La información que tienes es excelente.$B$BEl problema es que, ahora, hay más trabajo que hacer. Los trols, aunque ahora mismo se encuentran bastante confusos, siguen queriendo expulsar a los enanos de Crestanevada. Y, por supuesto, no voy a permitir que un fiero demonio convierta nuestro valle nevado en un lago montañoso.$B$BEl líder trol, sus sirvientes y la criatura de fuego deberían encontrarse en la cueva que hay al sureste. Elimínalos a todos.",
    ["O"] = "Mata a Grik\'nir el Frío, a 10 sirvientes de Grik\'nir y al elemental de fuego díscolo.",
    ["T"] = "Hielo y fuego",
  },
  [219] = {
    ["D"] = "¡Al fin, ayuda para la Alianza!$B$BAyúdame...$B$BLo último que recuerdo es que esos malditos orcos nos tenían rodeados. Los mataron a casi todos. A mí me dieron un golpe en la cabeza y perdí el conocimiento, y ahora me encuentro cautivo. Ayúdame, estoy malherido.$B$BTienes que ayudarme a volver... Por favor, eres mi única esperanza.",
    ["O"] = "Protege al cabo Keeshan en el camino a Crestagrana.",
    ["T"] = "Desaparecido en combate",
  },
  [220] = {
    ["D"] = "Islen tendrá una tarea más para ti antes de recompensarte con tu siguiente tótem. Sea cual sea esa tarea, te hará falta el vial de agua que te he dado.$B$BAhora vete y encuéntrala. Volveremos a encontrarnos algún día.",
    ["O"] = "Llévale el vial del agua más pura a Islen Vidente del Agua al Escalón de la Marea, en Los Baldíos.",
    ["T"] = "La llamada del agua",
  },
  [221] = {
    ["D"] = "Quizás te hayas fijado en esos enormes huargen que deambulan por los bosques con los tejesombras... Por lo que sabemos, esos corredores oscuros son el grueso de las filas de los huargen.$B$BEn mis patrullas también he notado que han invadido El Vergel Pútrido al suroeste de la ciudad. Esos huargen son más fuertes que los últimos a los que te enfrentaste.$B$BNo bajes la guardia.",
    ["O"] = "Mata a 12 huargen sombracechadores Penumbría para Calor de Villa Oscura.",
    ["T"] = "Huargen en el bosque",
  },
  [222] = {
    ["D"] = "Tus anteriores hazañas me han hecho pensar que ya estás $Glisto:lista; para afrontar a los huargen más duros del bosque.$B$BDe todos los huargen que han establecido aquí su morada, los Colmillo Vil y los Mancillados han demostrado ser los más peligrosos. Se han instalado cerca de las cuevas y en la mina que hay al sur.$B$BEl fuego de sus hogueras puede verse desde muy lejos...",
    ["O"] = "Mata 8 Colmillovil nocturnos y 8 huargen corruptos Penumbría para Calor de Villa Oscura.",
    ["T"] = "Huargen en el bosque",
  },
  [223] = {
    ["D"] = "Ten, $N. Lleva este mensaje al maestro Carevin.$B$B<Con un rápido movimiento saca un papiro descolorido y te lo tiende.>$B$B¡Unos pocos más como tú y superaremos en número a La Guardia Nocturna! Quizás así podríamos acabar entre todos el trabajo que hacemos nosotros.",
    ["O"] = "Lleva la nota de Calor a Jonathan Carevin.",
    ["T"] = "Huargen en el bosque",
  },
  [224] = {
    ["D"] = "¡Tenemos que proteger Loch Modan, $N! Casi todas nuestras tropas están luchando en otros frentes; nos hemos quedado desprotegidos. ¡Están saliendo troggs de debajo de las piedras! Esta invasión supone la mayor amenaza para Forjaz. Tenemos que acabar con esos asquerosos mutantes.$B$BNecesitamos a alguien valiente como tú para acabar con esos seres. Acaba con seis troggs Rompecantos y seis exploradores Rompecantos. Encontrarás a la mayoría de esas alimañas en los pasos hacia las montañas del sur.",
    ["O"] = "El montaraz Pedernal, de Thelsamar, quiere que mates a 6 troggs y 6 exploradores Rompecantos.",
    ["T"] = "Un golpe decisivo",
  },
  [226] = {
    ["D"] = "A Sven y a mí nos esperan días peligrosos, con el nigromántico al este... y aquí solos... tenemos que cazar nuestra propia comida. Y cada vez que vuelvo al campamento con algo de carne, aparecen lobos hambrientos o rabiosos buscando un bocado. ¡Vivir aquí es peligroso, no hace falta que lo jure!$B$BPero si consiguieras librarnos de algunos de esos lobos, nos facilitarías mucho la cosa. Se concentran sobre todo al norte y al este de aquí, cerca del río.",
    ["O"] = "Mata a 12 lobos feroces hambrientos y a 8 lobos feroces rabiosos y vuelve al campamento de Sven en la frontera oeste de Bosque del Ocaso a buscar a Lars.",
    ["T"] = "Con los lobos en los talones",
  },
  [227] = {
    ["D"] = "<Busca entre las estanterías y vuelve con un libro encuadernado en cuero.>$B$BSi quieres saber algo más, podrías preguntar a Althea. Ella ha sido la que ha... tratado el... problema... con él.",
    ["O"] = "Ve a Villa Oscura y habla con la comandante Althea Cerranegro.",
    ["T"] = "Morgan Ladimore",
  },
  [228] = {
    ["D"] = "Por lo que me dicen los exploradores, Mor\'Ladim deambula por el Bosque del Ocaso; recorre un extraño y serpenteante camino por el cementerio. Lo enterramos pasada la casa de la colina, ¿sabes dónde digo?$B$BNo hay nada que pueda hacer para ayudarte, pero te deseo buena suerte.",
    ["O"] = "Mata a Mor\'Ladim y luego lleva su calavera a la comandante Althea Cerranegro a Villa Oscura.",
    ["T"] = "Mor\'Ladim",
  },
  [231] = {
    ["D"] = "Ten, lleva esto a su tumba. Quizás... de alguna forma, él sepa que estoy bien y que nadie le hace responsable de lo que ocurrió.$B$B<Los ojos se le llenan de lágrimas mientras se quita el anillo de la mano derecha y lo coloca en tus manos.>$B$BY... gracias, $N.",
    ["O"] = "Lleva el anillo de Sarah Ladimor a la tumba de Morgan Ladimor.",
    ["T"] = "El amor de una hija",
  },
  [232] = {
    ["D"] = "La Sociedad Real de Boticarios necesita tu ayuda. Escucha bien esto, y puede que ambos podamos beneficiarnos con mi plan.$B$BEl boticario Keever, en la sala contigua, puede que esté... un poco tocado... pero su investigación promete dar muy buenos resultados, sobre todo si la condujera un investigador más inteligente. Afortunadamente, yo soy investigadora. Quiero que lleves esta orden de compra a Alessandro Luca, el propietario de Cachivaches Luna Azul; él te dará un equipo de estudio. Cuando lo tengas, tráemelo y yo me encargaré de explicarte tu encomienda con todo detalle.",
    ["O"] = "Llévale la orden de compra a Alessandro Luca en Entrañas.",
    ["T"] = "Un recado para la boticaria Zinge",
  },
  [233] = {
    ["D"] = "Mm, oye, tú no querrás hacerme un favor... ¿o sí? Hoy ha llegado un fajo de cartas a través del paso, pero no tengo tiempo para entregarlas.$B$BEstán todas dirigidas a Talin Ojo Ávido. Lo encontrarás al oeste. Solo tienes que seguir el camino. Ha establecido su campamento en la orilla del lago helado.$B$B¿Qué respondes?",
    ["O"] = "Entrega el fajo de cartas a Talin Ojo Ávido en el Valle de Crestanevada.",
    ["T"] = "Entrega de correo en el Valle de Crestanevada",
  },
  [234] = {
    ["D"] = "Si no recuerdo mal, el campamento de Grelin está al sureste, por el camino. Estará ansioso por recibir su correo.",
    ["O"] = "Entrega la carta a Grelin Barbablanca en el Valle de Crestanevada.",
    ["T"] = "Entrega de correo en el Valle de Crestanevada",
  },
  [235] = {
    ["D"] = "¡Atención, jóvenes aventureros! ¡Los páramos de Vallefresno os esperan!$B$BLa Horda ha aumentado su presencia en las tierras al norte de Los Baldíos. Nuestras dos avanzadas, Puesto del Hachazo y La Ensenada de Zoram, se esfuerzan por traer gloria a la Horda. Aquellos que deseen demostrar su valía deben buscar ayuda allí. Nota: Senani Corazón Atronador del Puesto del Hachazo, al norte de Los Baldíos, busca a aventureros que deseen participar en la gran Caza de Vallefresno.",
    ["O"] = "Habla con Senani Corazón Atronador en Puesto del Hachazo, en Vallefresno.",
    ["T"] = "La Caza de Vallefresno",
  },
  [236] = "_",
  [237] = {
    ["D"] = "El montaraz Pedernal habla muy bien de ti, $R. Por eso he decidido confiarte una misión de extrema importancia. Tenemos que mantener a raya a los troggs invasores hasta que nuestros hermanos regresen de luchar por la Alianza.$B$BDirígete a las colinas del sur y mata a diez aplastacráneos y diez videntes Rompecantos. Eso nos hará ganar tiempo. Regresa cuando termines la misión.",
    ["O"] = "El montaraz Gravagau, de la torre de vigilancia sur, quiere que mates a 10 aplastacráneos y 10 videntes Rompecantos. Luego ve a informarle.",
    ["T"] = "Un golpe decisivo",
  },
  [238] = {
    ["D"] = "Este equipo está listo, $N. No cabe duda de que a la boticaria Zinge le gustaría recibirlo lo antes posible. Llévaselo cuanto antes, y dile que le cargaré los costes del equipo a la Sociedad. De paso, por favor, hazle llegar mi más afectuoso saludo.$B$BBueno, no te quedes ahí. Esa es la vida de $Gun simple mensajero:una simple mensajera;, ¿no?",
    ["O"] = "Llévale el equipo de prueba de campo a la boticaria Zinge en Entrañas.",
    ["T"] = "Un recado para la boticaria Zinge",
  },
  [239] = {
    ["D"] = "La plaza fuerte de la frontera occidental informa de un aumento de actividad de gnolls y ladrones. Solicitan que enviemos más soldados de Ventormenta, ¡pero no podemos prescindir de ninguno!$B$BTu ayuda nos vendría bien. Habla con el ayudante de alguacil Rainer en el Cuartel de Arroyoeste y que te diga qué necesita.$B$BLa plaza fuerte está al oeste, bajando por el camino. Encontrarás un puente sobre un arroyo. Crúzalo. La plaza fuerte estará a la derecha.",
    ["O"] = "Ve al Cuartel de Arroyoeste y habla con el ayudante de alguacil Rainer.",
    ["T"] = "El Cuartel de Arroyoeste necesita ayuda",
  },
  [240] = {
    ["D"] = "¡¡Y voilà!! ¡Croquetas de \"cangrejo\" à la Grual!",
    ["O"] = "Vuelve a donde Alterio con los pasteles de cangrejo oscuro.",
    ["T"] = "Regresa junto a Alterio",
  },
  [241] = "_",
  [242] = "_",
  [243] = {
    ["D"] = "Dirígete al Desierto de Tanaris, y allí comprueba la consistencia del tejido de los basiliscos, escórpidos y de las hienas autóctonas del lugar. Consigue muestras de tejido con el equipo de estudio que llevas; eso sí, hazte a la idea de que parte de las muestras serán inservibles. Necesito 8 muestras buenas de cada tipo de criatura.$B$BAdemás, los goblins de Gadgetzan tienen una fuente de energía que debe emplearse con este tipo de equipo modificado. Habla con el ingeniero jefe Pasaquillas y consigue una para el equipo.$B$BVuelve a hablar conmigo cuando la tengas.",
    ["O"] = "Ve a Gadgetzan y encuentra al ingeniero jefe Pasaquillas para que te explique cómo obtener una fuente de energía para el equipo de estudio.",
    ["T"] = "In situ",
  },
  [244] = {
    ["D"] = "Saludos, $C. Estoy patrullando esta parte del camino. De momento todo está en calma, pero he visto campamentos gnoll al norte y al este de aquí.$B$B¡Debes informar de esto en Villa del Lago! Preséntate ante el ayudante de alguacil Feldon en Villa del Lago y dile lo de los gnolls. Seguro que te paga sueldo de explorador.$B$BLo encontrarás al sur del puente a Villa del Lago.",
    ["O"] = "Preséntate ante el ayudante de alguacil Feldon en el puente que hay al sur de Villa del Lago, en las Montañas Crestagrana.",
    ["T"] = "Gnolls invasores",
  },
  [245] = {
    ["D"] = "Salve, $C. ¿Has visto esas arañas que han invadido la frontera occidental? Esas amenazas de ocho patas son demasiado para nosotros pero el Comandante no tiene suficientes hombres. No puede enviar a nadie tan lejos de Villa Oscura.$B$BOdio tener que pedirte esto pero, parece que tú podrías manejar a esos bichos. No puedo prometerte una gran recompensa, pero si nos ayudas a erradicar a esos arácnidos, encontraré la forma de compensarte.$B$BAh, y deja que te dé un consejo: evita su veneno a toda costa.",
    ["O"] = "Mata a 15 telarácnidas pigmeovenenosas y vuelve al Bosque del Ocaso a informar al vigía Dodds.",
    ["T"] = "Las amenazas de ocho patas",
  },
  [246] = {
    ["D"] = "Andamos escasos de tropas, pero hay que hacer algo con esos gnolls.$B$BRastrea el sur de Crestagrana en busca de gnolls. Espíalos, mata a tantos como puedas y vuelve para informarme de cuántos son y si suponen una amenaza. Sus campamentos están por todo el camino meridional de Crestagrana.$B$BEl ejército de Ventormenta te recompensará, $N.",
    ["O"] = "Mata a 10 bastardos y 6 furtivos Crestagrana e informa al ayudante de alguacil Feldon en Villa del Lago.",
    ["T"] = "Evaluar la amenaza",
  },
  [248] = {
    ["D"] = "He estado observando la Torre de Ilgalar; Morganth está en conflicto con los orcos en el Fuerte de Petravista. Desconozco los motivos, pero tú me ayudarás a descubrirlos.$B$BCoge otro Glifo de Azora y sube a la atalaya intacta que hay junto al Fuerte de Petravista.$B$BEncuentra un objeto en el que depositar el glifo. Un cofre o un tarro iría bien, un lugar en el que nadie se fije en él.",
    ["O"] = "Sube a la atalaya intacta que hay junto al Fuerte de Petravista en las Montañas Crestagrana. $B$BEncuentra un objeto en el que esconder el Glifo de Azora.",
    ["T"] = "Ver más allá",
  },
  [249] = {
    ["D"] = "Después de mucha observación, he descubierto los planes de Morganth.$B$BBusca la Guadaña de Elune, ¡y es posible que ya la haya encontrado! Si ese es el caso, pronto las Montañas Crestagrana y el Reino de Ventormenta sentirán su siniestro poder.$B$BNo estoy preparado para enfrentarme a Morganth, pero me temo que debemos ocuparnos de él cuanto antes. Debes ir a la Torre de Ilgalar y enfrentarte a Morganth.$B$BDerrótalo y tráeme su colgante de las Sombras. El reino quedará en deuda contigo.",
    ["O"] = "Derrota a Morganth. $B$BLleva el colgante de las Sombras a Theocritus a la Torre de Azora, en el Bosque de Elwynn.",
    ["T"] = "Gran mago Doane",
  },
  [250] = {
    ["D"] = "Menos mal que alguien se interesa por el bienestar de la Presa. ¡Primero la destrucción del Puente Thandol y después el saqueo de Dun Modr! No dudo que el objetivo del siguiente ataque de los Hierro Negro será la Presa. Casi todos los agentes de seguridad se han destinado a luchar con la Alianza, incluido mi mejor inspector.$B$BPor eso te necesito, $N. He visto algunos zapadores Hierro Negro en la rampa oriental de la Presa. Investiga la zona y trae una pista.",
    ["O"] = "Investiga los alrededores de la rampa este de la Presa y lleva al ingeniero jefe Hinderweir alguna pista sobre lo que traman los Hierro Negro.",
    ["T"] = "Se aproxima una oscura amenaza",
  },
  [253] = {
    ["D"] = "El Embalsamador es el nombre de un personaje de uno de nuestros cuentos para niños. Era un alquimista bueno que enloqueció tras la muerte de su esposa Eliza. Para recuperarla, utilizó magia negra y colocó su propio corazón en el pecho de su mujer.$B$BEliza resucitó... pero con un hambre insaciable de carne humana y eso obligó al Embalsamador a mantenerla enterrada.$B$BSu corazón es su poder. Encuentra la tumba de su esposa, quítale el corazón y tráemelo a mí mientras aún lata. Hazlo y romperás su poder.",
    ["O"] = "Encuentra la tumba de Eliza. Quítale el corazón del embalsamador y luego vuelve a Ello Cerranegro.",
    ["T"] = "Novia del embalsamador",
  },
  [254] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Excavar la tierra",
  },
  [255] = {
    ["D"] = "Cazar bestias salvajes se acaba haciendo aburrido. A mí me van las presas grandes... Muy grandes. Los ogros, concretamente.$B$BA los enanos les preocupa bastante la Fortaleza de Mo\'grosh al noreste, pero no tienen hombres para hacer nada al respecto. Si te apetece unirte a la caza, ponte a matar todos los ogros que encuentres. Tráeme noticias de tus hazañas y compartiré contigo la recompensa del acuerdo que tengo con el magistrado.",
    ["O"] = "Kelt Thomasin, en la Fortaleza de Mo\'grosh, te ha contratado para matar a 4 ogros, 4 toscos y 4 gorilas Mo\'grosh.",
    ["T"] = "Mercenarios",
  },
  [256] = {
    ["D"] = "Por orden del magistrado de Thelsamar:$B$BSe busca muerto a Chok\'sul, ogro culpable de dirigir los ataques contra la ciudad de Thelsamar, la Presa de las Tres Cabezas y la excavación.$B$BSe ofrece recompensa a quien presente al magistrado una prueba de la muerte de Chok\'sul.$B$BFue visto por última vez en el campamento ogro situado al noreste de Loch Modan.",
    ["O"] = "Mata a Chok\'sul y lleva su cabeza al magistrado Narizroma a Thelsamar.",
    ["T"] = "SE BUSCA: Chok\'sul",
  },
  [257] = {
    ["D"] = "Crees que estás a la altura de Daryl el Audaz, ¿eh? ¡No lo creo! Por supuesto, puedes ponerte a prueba. Te propongo un desafío que seguro estará por encima de tus posibilidades, así que no te sientas mal si no eres capaz de superarlo.$B$BUna banda de águilas ratoneras ha anidado aquí, en Loch Modan. ¿Qué tal si acabas con algunas de esas bestias? Mira, si superas mi desafío, te daré mi arco o mi pistola.$B$B<Te mira por encima del hombro.>$B$BNo parece que tengas mucho que perder de todas formas.",
    ["O"] = "Mata 8 águilas ratoneras montesas y ve a ver a Daryl el Jovenzuelo en la Cabaña del Errante.",
    ["T"] = "La fanfarronada de un cazador",
  },
  [258] = {
    ["D"] = "Así que eres $N, ¿eh? Te llena de orgullo tu destreza como $Gcazador:cazadora;, ¿verdad? Tampoco hay para tanto.$B$BA ver si te atreves a cazar un jabalí. Créeme, esta no será una cacería como las del Valle de Crestanevada, así que será mejor que tengas cuidado.$B$BNo te sientas mal si no lo consigues. ¡Si lo haces, te daré mi camisa!$B$B¿Te he contado cómo me hice mi famosa cicatriz? ¿No? Fue hace dos años...",
    ["O"] = "Mata 10 jabalíes monteses mayores y ve a ver a Daryl el Jovenzuelo en la Cabaña del Errante.",
    ["T"] = "El reto de un cazador",
  },
  [259] = "_",
  [260] = "_",
  [261] = {
    ["D"] = "Te hablaré con franqueza. Estamos en guerra con la Plaga. Es un mal que corrompe a nuestra gente e infesta nuestra tierra. Hay que detenerlo antes de que nos arrolle y arrastre a nuestro mundo a las sombras. Los miembros de la Cruzada Escarlata hemos jurado combatir a la Plaga en cuerpo y alma.$B$BSi estás con nosotros, reúne valor y prueba tu lealtad: acaba con los devastadores no-muertos de Desolace. Búscalos en el Valle de los Huesos, al sur.$B$BHazlo y la Cruzada te acogerá con los brazos abiertos.",
    ["O"] = "Aniquila 30 devastadores no-muertos y luego ve a buscar al hermano Anton a Punta de Nijel.",
    ["T"] = "El sendero Escarlata",
  },
  [262] = {
    ["D"] = "¡Debí adivinar quién era esa figura que vi en las sombras la noche en que mi familia fue asesinada!$B$BPor favor, $N, has demostrado ser $Gun hombre:una mujer; de recursos. Te lo ruego, encuentra a ese ser misterioso. ¡He de saber qué secretos guarda!$B$BNo sé nada que tú no sepas ya pero, ten, coge el libro que encontraste en mi granja. Quizás alguien del pueblo pueda decirte a quién pertenece el libro.",
    ["O"] = "Llévate el libro que encontraste en la granja de Sven a Villa Oscura y enséñaselo a sus habitantes.",
    ["T"] = "La figura en las sombras",
  },
  [263] = {
    ["D"] = "Los montaraces me dicen que eres valiente y capaz, $N. Nos hace falta $Gun:una; $R como tú por aquí. La situación con los troggs no mejora. La reserva está en combate; estamos solos. Pero con tu experiencia como $C, la cosa va a cambiar.$B$BLiquida a ocho chamanes Rompecantos y a ocho Cascahuesos Rompecantos.$B$BA ver si los troggs aprenden a temer tu reputación, mata-troggs.",
    ["O"] = "El montaraz Muroplof, de la torre de vigilancia sur, quiere que mates a 8 chamanes y 8 Cascahuesos Rompecantos.",
    ["T"] = "Un golpe decisivo",
  },
  [264] = {
    ["D"] = "¡Sucia escoria! Me abandona por su maldita cruzada porque \"la Luz es lo más importante que tenemos contra la amenaza que representan los no-muertos.\"$B$B¡¿Y sus hijos?! ¡¿Y YO?! Noche tras noche me quedaba esperando por él... ¡siempre en segundo lugar después de sus condenadas obligaciones!$B$BBueno, ¡mira lo que te depara la \"justicia\", $N! ¡Él está muerto y yo tengo precisamente lo que estaba intentando detener!$B$BCoge esto y ponlo encima de su tumba en El Sepulcro. ¡No quiero tener nunca más nada que ver con eso... ni con él!",
    ["O"] = "Coloca el colgante de Clarice sobre la tumba de Yuriv en Argénteos.",
    ["T"] = "Hasta que la muerte nos separe",
  },
  [265] = {
    ["D"] = "Te devuelvo tu libro, $N. Y te deseo mucha suerte. Si preguntas a la gente del pueblo, quizás puedas conseguir más pistas.$B$BEmpezaré por darte yo una: aunque el autor de este libro no es de Villa Oscura, el libro fue comprado aquí.",
    ["O"] = "Averigua si otros habitantes tienen pistas sobre la figura misteriosa.",
    ["T"] = "La búsqueda de la figura continúa",
  },
  [267] = {
    ["D"] = "$C, es posible que no estés al corriente de la amenaza trogg que se cierne sobre tierras enanas. Con las fuerzas de reserva de Forjaz en el frente de la Alianza, solo nos queda una fracción de las fuerzas de defensa necesarias para proteger estas tierras. Mi regimiento debe quedarse aquí protegiendo la ciudad, por lo que no podemos abandonar nuestros puestos, nos arriesgaríamos a una invasión.$B$BPero tenemos que presionar un poco a esos malditos troggs que merodean por las colinas. Si te ves capaz, lanza un ataque contra los troggs. Tráeme ocho dientes de trogg de piedra como prueba de tu acción.",
    ["O"] = "Lleva 8 dientes de trogg de piedra al capitán Rugelfuss a la torre de vigilancia sur.",
    ["T"] = "La amenaza trogg",
  },
  [269] = {
    ["D"] = "Ve a la ciudad de Ventormenta y habla con el obispo Lontananza. Búscalo en la Catedral de la Luz. Dale el diario de Alterio y dile que deseas enfrentarte a Morbent Vil.$B$BAunque Ventormenta ha abandonado a Villa Oscura, Lontananza es un alma buena y sabia. Puede que te ayude a salvarnos.$B$BTu misión es peligrosa, pero date prisa, $N. Las sombras del Bosque del Ocaso son más cada vez más oscuras.",
    ["O"] = "Entrega el diario completo de Alterio al obispo Lontananza de Ventormenta.",
    ["T"] = "En busca de sabiduría",
  },
  [270] = {
    ["D"] = "Años atrás, la Tercera Flota de Kul Tiras se hundió en la costa de Los Humedales, una expansión pantanosa al norte del reino enano de Forjaz. A bordo del barco había un envío de hierro Forjaluz.$B$BEste metal es muy valioso para nosotros, porque los objetos forjados con él, golpean con toda la verdad sagrada. Si te vas a enfrentar a Morbent Vil, necesitarás un arma así.$B$BVete al Puerto de Menethil y habla con mi colega enano Glorin Frenteacerada. Si es posible encontrar ese metal perdido, él sabrá dónde.",
    ["O"] = "Vete al Puerto de Menethil en Los Humedales y habla con Glorin Frenteacerada.",
    ["T"] = "La flota maldita",
  },
  [271] = {
    ["D"] = "Supongo que no te interesará devolvérsela a Su Señoría Osadía. Daryl tiene cierta habilidad, lo reconozco.$B$BHace un año el objetivo de la Cabaña del Errante era un gran oso, pero ni uno solo de los cazadores lo consiguió. Ni siquiera Daryl que también probó suerte con él. Lo único que sacó en claro fue esa terrible cicatriz que luce sobre el ojo.$B$BMata al oso. No me cabe duda de que eso le cerrará el pico a Daryl aunque solo sea por una vez.$B$BEl oso, el viejo Tiznado, se encuentra en las inmediaciones de su guarida, en una cueva al norte de aquí.",
    ["O"] = "Mata al viejo Tiznado y llévale una prueba a Daryl el Jovenzuelo en la Cabaña del Errante.",
    ["T"] = "La venganza de Vyrin",
  },
  [272] = {
    ["D"] = "En tu segunda prueba debes encontrar las dos mitades del colgante del león marino. Una obtiene su poder de la agilidad del león marino cuando se desliza en el agua, la otra lo obtiene de su increíble capacidad de resistencia. Los druidas se alimentan de ambos rasgos para asumir su forma acuática.$B$BHabla con la gente de Claro de la Luna para averiguar dónde están las mitades y tráemelas para que las unamos. Luego llevarás el colgante a Dendrita Rafagaestelar.",
    ["O"] = "Busca el colgante de Agilidad acuática y el colgante de Resistencia acuática. Habla con la gente de Claro de la Luna para conseguir pistas sobre la ubicación de los colgantes. $B$BForma el colgante del león marino con los otros dos cuando estés cerca del Santuario de Remulos. $B$BLleva el colgante completo a Dendrita Rafagaestelar a Amparo de la Noche, en Claro de la Luna.",
    ["T"] = "La prueba del león marino",
  },
  [274] = {
    ["D"] = "Rápido, llévale esta mezcla al jefe. Los Hierro Negro no descansan, así que nosotros tampoco podemos.",
    ["O"] = "Ashlan Risapétrea quiere que lleves la mezcla neutralizadora al ingeniero jefe Hinderweir.",
    ["T"] = "Se aproxima una oscura amenaza",
  },
  [275] = {
    ["D"] = "No sé la causa pero una auténtica podredumbre está creciendo en Los Humedales. Han aparecido trepadores del pantano. Son burbujas enfermas y febriles en el país, y para detener la fiebre deben poder a asentarse de nuevo en los pantanos.$B$BLos trepadores del pantano se arrastran por los canales de Los Humedales.",
    ["O"] = "Mata 8 trepadores del pantano y regresa con Rethiel el Guardaverde en Los Humedales.",
    ["T"] = "Ampollas en la tierra",
  },
  [277] = {
    ["D"] = "Los Pellejomusgo están talando los árboles de Los Humedales, y utilizan su carne para darse calor. ¡No seguiré sufriendo este dolor!$B$BArrebata a los Pellejomusgo los sílex rudimentarios que utilizan para hacer fuego. Todos los Pellejomusgo llevan esos sílex rudimentarios, excepto los pequeños gnolls y bastardos Pellejomusgo.",
    ["O"] = "Lleva 9 sílex rudimentarios a Rethiel el Guardaverde.",
    ["T"] = "No al fuego",
  },
  [278] = {
    ["D"] = "Ponte a buscar los ingredientes de inmediato, $N. Puedes sacar el veneno de las arañas locales cercanas. Las lágrimas de crocolisco las encontrarás en el Loch en sí. El cristal de Mo\'Grosh ya será más difícil de conseguir. Los extraen los ogros del noreste, pero son muy raros de encontrar.$B$B¡Tenemos que prepararnos para rechazar un ataque de los Hierro Negro! Vuelve cuando tengas todos los ingredientes y prepararé la mezcla.",
    ["O"] = "Ashlan Risapétrea que está en el Campamento Mo\'grosh quiere reúnas veneno de rondador, sangre frenética y 1 cristal de Mo\'grosh.",
    ["T"] = "Se aproxima una oscura amenaza",
  },
  [279] = {
    ["D"] = "Los múrlocs están emergiendo de las profundidades y construyendo sus aldeas en la costa. Están acosando a nuestros pescadores y mercaderes, y es necesario detenerlos.$B$BEngullidor, uno de esos múrlocs, anda con otros múrlocs Branquiazul y asalta a los mercaderes que pasan por el camino, regresando después a la seguridad de sus casitas. Nuestros mercaderes están amenazados y tenemos que darles seguridad.$B$BEngullidor acecha el río hacia el norte de aquí. Encuéntralo y acaba con él y sus semejantes. Tráeme su cabeza.",
    ["O"] = "Mata a 12 múrlocs Branquiazul.$B$BMata a Engullidor y coge su cabeza.$B$BLleva la cabeza de Engullidor a Karl Boran en Puerto de Menethil.",
    ["T"] = "Zarpas de las profundidades",
  },
  [280] = {
    ["D"] = "Antes he visto a unos cuantos insurgentes Hierro Negro nadando hacia la Presa con un barril grande. ¡No había ningún montaraz para detenerlos! ¡Creo que han colocado el seforio en la base de la Presa y va a explotar de un momento a otro!$B$B¡Necesito que cojas esta mezcla y la disuelvas en el barril antes de que sea demasiado tarde! ¡Date prisa!",
    ["O"] = "El ingeniero jefe Hinderweir quiere que nades hasta la base de la presa, localices el barril de pólvora y disuelvas en él la mezcla neutralizadora para evitar que explote.",
    ["T"] = "Se aproxima una oscura amenaza",
  },
  [285] = {
    ["D"] = "Este grupo de casuchas de múrloc, tras inspeccionarlo a fondo, no revela nada.$B$BPe... pero estás $Gseguro:segura; de que la próxima pista que encuentres será muy fructífera.$B$B¡La Estatuilla de Menethil debe estar en alguna de las casuchas del Norte!",
    ["O"] = "Sigue buscando la Estatuilla de Menethil en las casuchas de los múrlocs.",
    ["T"] = "Inspecciona más casuchas",
  },
  [286] = {
    ["D"] = "Después de sacar el barril de toda la mugre y abrirlo, descubres una figurilla plateada empaquetada entre paja. Representa a un joven paladín de pelo largo, con un martillo alzado.$B$B¡Tiene que ser la Estatuilla de Menethil! Debes regresar con Karl Boran en el Puerto de Menethil.",
    ["O"] = "Lleva la Estatuilla de Menethil a Karl Boran al Puerto de Menethil, en Los Humedales.",
    ["T"] = "Devuelve la Estatuilla",
  },
  [287] = {
    ["D"] = "Llevo mucho retraso con mi informe y me vendría bien tu ayuda, $N. Conseguí averiguar dónde se esconden los trols, pero vi tantos de ellos que me dio miedo entrar.$B$BPor eso... necesito que entres en la cueva, eches un vistazo y mates a algunos de los trols. Luego vuelves aquí.$B$BSigue la carretera norte que sale de Kharanos, cuando llegues al puente, sigue el río helado hacia el oeste hasta que llegues al Lago Glacial. En la orilla oeste encontrarás Las Birras. La cueva está al suroeste del poblado.",
    ["O"] = "Explora el Poblado Peloescarcha y mata a 5 rebanacabezas Peloescarcha para Senir Barbablanca de Kharanos.",
    ["T"] = "Poblado Peloescarcha",
  },
  [288] = {
    ["D"] = "En su día fui contramaestre del Dueña de las olas, una nave de alto bordo de la Tercera Flota de Kul Tiras. La flota maldita. Maldita, porque en su último viaje fue atacada por dragones Rojos. ¡Dragones controlados por orcos!$B$BNo teníamos ninguna oportunidad contra el fuego de los dragones... y nuestra flota ardió y se hundió en esta costa. Soy el único superviviente de todas esas desgraciadas naves...$B$B... ¡Oh me va a estallar la cabeza! Tengo que beber algo fresco si quiero acabar con esta resaca y seguir con mi historia.",
    ["O"] = "Compra un cántaro de hidromiel para el contramaestre Fistimos.",
    ["T"] = "La Tercera Flota",
  },
  [289] = {
    ["D"] = "<Algo recuperado Fistimos sigue contando su historia...>$B$BSoy el único superviviente de la Tercera Flota de Kul Tiras, naufragada en la costa del Norte. ¡Pero aunque soy el único miembro de la flota que sigue con vida, no soy el único que sigue por aquí! Uno de nuestros barcos ocultaba un gran y terrible tesoro y su espeluznante poder tiene atrapadas las almas de mis hermanos entre los descompuestos cascos de los barcos.$B$BSi quieres liberar a algunos de mis hermanos tal vez puedas romper su maldición.",
    ["O"] = "Mata 13 marineros malditos, 5 marinos malditos y al contramaestre Snellig. Lleva la tabaquera de Snellig al contramaestre Fistimos en Puerto de Menethil.",
    ["T"] = "La tripulación maldita",
  },
  [290] = {
    ["D"] = "Para romper la maldición y liberar a todos mis hermanos, hay que recuperar y purificar la... cosa... que guardaba mi barco. No sé lo que era, pero sé que estaba almacenada en la caja fuerte del barco. El capitán Halyndor era el único que tenía la llave.$B$BMi buen capitán sigue presente en mis sueños. Sé que sigue en el barco, El Intrépido. Hazte con la llave y utilízala para abrir la caja fuerte de El Intrépido.$B$BEntonces, cuando tengas esa maldita cosa, llévasela al sacerdote Glorin Frenteacerada en la ciudad.",
    ["O"] = "Obtén la llave de la caja fuerte de El Intrépido del capitán Halyndor.$B$BUtiliza la llave para abrir la caja fuerte de El Intrépido.",
    ["T"] = "Purga de la maldición",
  },
  [292] = {
    ["D"] = "Al abrir el cofre descubres un orbe ennegrecido, que parpadea con una luz espeluznante. Es el tesoro del que hablaba Fistimos: un Ojo maldito de Paleth.$B$BLleva el Ojo maldito de Paleth al sacerdote enano Glorin Frenteacerada, en el Puerto de Menethil. Pregúntale como eliminar la maldición del Ojo.",
    ["O"] = "Lleva el Ojo maldito de Paleth a Glorin Frenteacerada al Puerto de Menethil en Los Humedales.",
    ["T"] = "El ojo de Paleth",
  },
  [294] = {
    ["D"] = "La situación es grave, ciertamente. Cuando descubrimos estos huesos acudieron los raptores, atraídos por ellos. Esas bestias inmundas mataron a mis hermanos y nos atraparon a mí, a Merrin y al pobre prospector.$B$BLimpia Los Humedales de esos raptores, $N. Encontrarás raptores jaspeados y estridadores jaspeados al oeste de este risco. Mata 10 ejemplares de cada, si puedes.$B$BSerá un buen comienzo para la venganza que he planeado.",
    ["O"] = "Ormer Trenzaferro de la Excavación de Whelgar quiere que mates 10 estridadores jaspeados y 10 raptores jaspeados.",
    ["T"] = "La venganza de Ormer",
  },
  [296] = {
    ["D"] = "Mientras estabas ahí abajo pude observar que una de esas bestias destacaba sobre el resto. Era más grande y amenazadora. Apuesto a que es la que condujo hasta aquí al resto para atacar la excavación.$B$BTe pido una última tarea, $N. Hay que hacer justicia con ese Sarldente, y de acuerdo a la gravedad de sus crímenes, justicia no significa otra cosa que pena de muerte. Tráeme una de sus garras como prueba de su muerte.",
    ["O"] = "Ormer Trenzaferro de la Excavación de Whelgar quiere que mates a Sarldente y que le lleves una de sus garfas cuando completes la tarea.",
    ["T"] = "La venganza de Ormer",
  },
  [297] = {
    ["D"] = "Hace poco, justo antes de que los troggs emergieran en esta excavación, descubrimos una gran cantidad de extraños ídolos grabados. Pero no tuvimos oportunidad de estudiarlos, ya que poco después del descubrimiento ¡los troggs nos echaron de las ruinas! Y esos ídolos tienen un efecto extraño en los troggs. ¡Los vuelve locos!$B$BTráeme 8 ídolos: quiero estudiarlos y ¡quiero apartarlos de las manos de los troggs! Encontrarás los ídolos en los troggs que infestan la zona.",
    ["O"] = "Lleva 8 ídolos de piedra grabados a Magmar Ceñimal en la Excavación de Vetaferro.",
    ["T"] = "Recoger ídolos",
  },
  [298] = {
    ["D"] = "Tengo que enviar un informe a Thelsamar. ¡Eso, e insistir en mi pedido de pólvora! Este es el informe. Llévaselo a Jern Cornamenta, es nuestro contacto en Thelsamar.$B$BNo es un encargo difícil, pero no tengo a nadie más para hacerlo.",
    ["O"] = "Lleva el informe de Vetaferro a Jern Cornamenta a Thelsamar.",
    ["T"] = "Un informe sobre el avance de la excavación",
  },
  [299] = {
    ["D"] = "Justo antes de la invasión descubrí una tablilla, era la llamada piedra Goaz. El texto traducido se interrumpe en cuatro puntos. Se habla de un \"plan divino\" y de una \"profecía maldita.\"$B$BTemo que nos estemos quedando sin tiempo. Soy demasiado viejo para ir a enfrentarme con esas bestias. Pero tú eres fuerte.$B$BRegistra la excavación y encuentra los 4 trozos perdidos de la tablilla: Ados, Modr, Golm y Neru. Búscalos en artefactos antiguos o donde veas tierra suelta. ¡Tráemelos y podré empezar a descifrar este misterio!",
    ["O"] = "El prospector Whelgar quiere que te adentres en la excavación en busca de los 4 fragmentos de tablilla perdidos: Ados, Modr, Golm y Neru.",
    ["T"] = "Desvelando el pasado",
  },
  [301] = {
    ["D"] = "Necesitamos autorización para llevar más pólvora a Vetaferro. Lleva este informe a la oficina central de la Liga de Expedicionarios, en Forjaz.$B$BAquí tienes el informe. Dáselo al prospector Pico Tormenta y no olvides agacharte en cuanto lo hagas. Pico Tormenta tiene muy mal genio y no le va a gustar lo lento que va Vetaferro.$B$BLo encontrarás en la Asamblea de Expedicionarios.",
    ["O"] = "Lleva el informe de Vetaferro al prospector Pico Tormenta.",
    ["T"] = "Informe a Forjaz",
  },
  [303] = {
    ["D"] = "La guerra arde aquí en casa y en el extranjero. Mientras los grandes soldados enanos dan sus vidas en tierras lejanas en nombre de la Alianza, nuestras propias tierras caen víctimas de la depravación de los enanos Hierro Negro. Arrasaron el Puente Thandol y Dun Modr cayó poco después.$B$BTe necesitamos para reunir fuerzas y hacer retroceder el frente. Si puedes matar a 10 enanos Hierro Negro, 5 tuneladores, 5 saboteadores y 5 demoledores, tal vez la reserva consiga llegar a tiempo para terminar la ofensiva.$B$B¡Date prisa!",
    ["O"] = "Motley Garmason de Dun Modr quiere que mates 10 enanos Hierro Negro, 5 tuneladores Hierro Negro, 5 saboteadores Hierro Negro y 5 demoledores Hierro Negro.",
    ["T"] = "La guerra contra los Hierro Negro",
  },
  [304] = {
    ["D"] = "Muchos de mis soldados murieron en la batalla de Dun Modr. Mi propio hermano murió cuando fue destruido el Paso Oeste del Puente Thandol. Sí señor, los enanos Hierro Negro son una raza diabólica. A partir de lo que Roggo pudo reunir, el líder de matones en Los Humedales es un brujo llamado Balgaras el Hediondo. Es un auténtico cobarde, que envía a sus soldados para que cumplan sus órdenes y él se queda escondido en Cerro Fraguaferoz, al sureste.$B$BSi puedes encontrar a esa escoria escurridiza, mátalo y tráeme su oreja. Sabré recompensarte, $C.",
    ["O"] = "Mata a Balgaras el Hediondo y lleva su oreja a Largatrenza el Nefasto a las afueras de Dun Modr.",
    ["T"] = "Una misión hedionda",
  },
  [305] = {
    ["D"] = "¡Oh, debes ser $Gel explorador enviado:la exploradora enviada; por Largatrenza! No puedes imaginar lo preocupado que he estado.$B$BDurante un mes, mi mujer, Merrin, me ha estado enviando cartas cada semana. Forma parte del equipo de la Excavación de Whelgar, pero hace casi dos semanas que he dejado de recibir sus cartas. ¡Seguro que algo va mal!$B$BLargatrenza dijo que si no recibíamos noticias del equipo enviaría a un explorador para comprobar cómo va todo. Supongo que tú eres ese explorador, $N. ¡Busca a Merrin en la excavación, al Este!",
    ["O"] = "Tarrel Tejepiedra quiere que viajes a la Excavación y que contactes con Merrin Tejepiedra.",
    ["T"] = "La búsqueda del equipo de excavación",
  },
  [306] = {
    ["D"] = "¡Fue horrible! Estábamos desenterrando unos extraños artefactos cuando dimos con estos grandes huesos.$B$BAntes de poder saber qué sucedía, los raptores jaspeados inundaron la excavación. Se perdieron muchas vidas.$B$BOrmer... tan valiente... nos salvó al prospector y a mí. Nos defendió mientras corríamos a buscar refugio. Ahora estamos atrapados y todo nuestro trabajo está perdido.$B$BToma, lleva esta nota a mi querido Tarrel. En ella le explico cuál es nuestra situación.",
    ["O"] = "Lleva la nota de Merrin a Tarrel Tejepiedra al Puerto de Menethil, en Los Humedales.",
    ["T"] = "La búsqueda del equipo de excavación",
  },
  [307] = {
    ["D"] = "La Mina de Fuenteplata, que cae al este, se agotó hace tiempo. La Liga de Mineros la usaba como almacén, pero ahora se han instalado unos kóbolds allí, ¡con esas magníficas herramientas enanas a su alcance!$B$BLos expulsaremos, pero la Liga quiere que vaya alguien a recoger el material antes de que lleguemos los soldados a romperlo todo. Es muy arriesgado; será mejor que lleves compañía.$B$BEl material está en unas cajas con la marca de la Liga de Mineros a lo largo de la mina. Suerte.",
    ["O"] = "Ve a la Mina de Fuenteplata y recoge 6 cargas de equipamiento de minero. $B$BLuego ve a ver al montaraz Pico Tormenta.",
    ["T"] = "Zarpas asquerosas",
  },
  [309] = {
    ["D"] = "Tenemos que llevar esta pólvora a Vetaferro, pero este carro no va a ninguna parte. Es una carga muy pesada y esta zona es peligrosa. Además, quién sabe qué más nos tienen reservado los Hierro Negro ahora que Saean está con ellos.$B$BMe tranquilizaría bastante que me ayudases a vigilar. Miran puede descargar los barriles de pólvora en el campamento mientras tú y yo protegemos el carro.",
    ["O"] = "Protege a Huldar y la caravana Vetaferro y luego informa al prospector Vetaferro.",
    ["T"] = "Proteger el envío",
  },
  [310] = {
    ["D"] = "Tengo que reconocer que esos Cebatruenos preparan unas bebidas estupendas. ¡Pero hay que bajarles los humos! ¡La suya no es la única bebida decente del mundo! Quizás tú puedas ayudarme a darles una lección...$B$BTen, coge este barril de brebaje de Cebadiz. Cuélate en el sótano de la Destilería Cebatruenos de Kharanos y cámbialo por uno de sus barriles de Cerveza del Trueno. ¡Y ya veremos qué brebaje les gusta más a los clientes!$B$BY si encuentras guardias vigilando los barriles, intenta distraerlos...",
    ["O"] = "Ve al sótano de la Destilería Cebatruenos de Kharanos y sustituye un barril de cerveza del Trueno con uno de brebaje de Cebadiz.",
    ["T"] = "Rivales acérrimos",
  },
  [311] = {
    ["D"] = "Ya has dejado el barril del brebaje de Marleth en el almacén de la destilería de Kharanos y recoges un barril de Cerveza del Trueno como prueba de que has cumplido...",
    ["O"] = "Lleva el barril de cerveza del Trueno a Marleth Cebadiz a Las Birras.",
    ["T"] = "Regresa junto a Marleth",
  },
  [313] = {
    ["D"] = "El Cataclismo ha llevado hordas de troggs a la superficie, algo que ha obligado a los trols Peloescarcha a huir para salvar sus vidas. Por desgracia, han decidido establecerse frente a nuestro hogar y crearnos problemas.$B$BHe enviado al montaraz Dunstan y su pelotón a El Cubil Pardo, al suroeste de la ciudad, para que echen un ojo a los wendigos que viven allí, pero ahora necesito avisarlos para que vuelvan a Kharanos. Encuéntralos, diles que desplieguen los robots de observación remota que nos dieron los gnomos y que vuelvan a Kharanos.",
    ["O"] = "Comunica las órdenes del capitán Tharran al montaraz Dunstan, al montañero Lewin y al montañero Valgrum en El Cubil Pardo.",
    ["T"] = "Vigilando desde lejos",
  },
  [314] = {
    ["D"] = "Oímos los gritos en medio de la noche. Y esta mañana faltaban dos cabezas de ganado. Esa ruin bestia conocida como Vagash ha estado alimentándose de nuestro sustento. Con la mayoría del ejército de Forjaz mermado, no queda nadie para mantener a raya a Vagash.$B$BQuizás tú tengas el valor necesario para buscar a ese animal y matarlo. Tráeme uno de sus colmillos y te daré una recompensa. Vagash acecha desde encima de este rancho. Ten cuidado, es peligroso.",
    ["O"] = "Consigue el colmillo de Vagash.",
    ["T"] = "La protección del rebaño",
  },
  [315] = {
    ["D"] = "Mi misión consiste en conseguir la cerveza perfecta. Sé que puedo hacerlo, llevo la cebada en la sangre. Solo tengo que encontrar la receta adecuada...$B$BLos trols Peloescarcha cultivan una planta llamada cardaluz en lo alto de Monte Luz, al noroeste. La utilizan para rituales extraños y tribales. Nosotros los enanos casi no la usamos, pero tiene un sabor único... y quisiera experimentar con él en mis cervezas.$B$BConseguir el cardaluz puede ser peligroso, pero pagaré bien por lo que \"cojas prestado\" de las canastas del poblado trol.",
    ["O"] = "Llévale 7 cardaluz a Rejold Cebadiz en Kharanos.",
    ["T"] = "La cerveza perfecta",
  },
  [316] = "_",
  [317] = {
    ["D"] = "Me estoy preparando para empezar una misión para la Brigada de Asedio. Es una misión larga y tengo que cargar un mes de suministros en mi reactor a vapor. Así que, mientras Brasacerada trabaja en mi tanque, quizás podrías ir de caza por mí...$B$BNecesito pieles para hacerme una cama y carne para comer. Puedes cazar jabalíes para la carne y osos para las pieles... ve a los campos nevados que hay al sur de El Cubil Pardo.",
    ["O"] = "Reúne 4 trozos de carne de jabalí y 2 pelajes de oso grueso y llévaselas al piloto Bramiz al Almacén de Brasacerada.",
    ["T"] = "Los suministros del reactor a vapor",
  },
  [318] = {
    ["D"] = "Rejold Cebadiz experimenta con sus brebajes. Algunos saben bien, otros mal y otros... bueno, esos tumbarían al enano más fuerte.$B$BVive en Las Birras, al oeste de Kharanos. Y lo que quiero es una de sus bebidas: tajada. Me mantendrá caliente en los caminos más fríos.$B$BY... ¡ese bendito líquido quema tanto que también puedo usarlo para aumentar la potencia de mi reactor a vapor! Me ha sacado de un apuro más de una vez.",
    ["O"] = "Consigue un tonel de tajada de Rejold Cebadiz de Las Birras.",
    ["T"] = "La tajada",
  },
  [319] = {
    ["D"] = "Tengo un tonel de tajada, pero... ¿podrías hacerme un favor? En Las Birras vendría bien un poco de ayuda con los animales salvajes que deambulan por las nieves de los alrededores. A veces se acercan demasiado.$B$BNo es que tengamos miedo de los osos, los gatos ni los jabalíes... ningún enano que se precie lo tiene. Pero si tú pudieras ocuparte de esos animales, tendríamos más tiempo para trabajar y hacer cerveza.",
    ["O"] = "Mata a 6 osos zarpahelada, 8 jabalíes del risco mayores y 8 leopardos de la nieve y luego vuelve a Las Birras con Rejold Cebadiz.",
    ["T"] = "Un favor a cambio de un barril de tajada",
  },
  [320] = {
    ["D"] = "Aquí tienes tu tonel de tajada, $N. Y, una vez más, gracias por la caza.",
    ["O"] = "Entrega el tonel de tajada al piloto Bramiz en el Almacén de Brasacerada.",
    ["T"] = "Regresa junto a Bramiz",
  },
  [321] = {
    ["D"] = "Los restos de naufragios que puedes ver en nuestra costa son la Tercera Flota maldita de Kul Tiras, incendiada y hundida por los dragones Rojos durante la última Gran Guerra.$B$BLa flota procedía de Lordaeron, pero un destructor élfico, el Pigargo volador, estaba con ella. Transportaba un envío de hierro Forjaluz y se unió a la Tercera Flota en busca de protección. Irónicamente lo hundieron los mismos dragones...$B$BPero basta de hablar. El Pigargo volador naufragó al sur del Puerto de Menethil. Si encuentras el hierro Forjaluz, tráemelo.",
    ["O"] = "Busca los restos del Pigargo volador en Los Humedales.",
    ["T"] = "Hierro Forjaluz",
  },
  [322] = {
    ["D"] = "Hay un armero enano en el Distrito de los Enanos de Ventormenta que puede trabajar con este hierro Forjaluz. Su nombre es Grimand Elmore.$B$BHe empaquetado en un cajón el hierro que encontraste. Llévaselo a Grimand y cuéntale toda tu historia. Seguro que estará muy agradecido a alguien comprometido con una búsqueda como la tuya y te hará un arma... ¡un arma que haría temblar hasta al mismísimo Morbent Vil!",
    ["O"] = "Lleva el cajón de lingotes Forjaluz a Grimand Elmore en Ventormenta.",
    ["T"] = "Brazo bendito",
  },
  [323] = {
    ["D"] = "Morbent Vil es un nigromántico y un aliado de los Jinetes Oscuros. Te enfrentaría al poder de Morbent Vil, pero no te enviaré a luchar contra ese demonio sin conocer tu fuerza.$B$BYa me has probado tu valentía pero, si de verdad quieres enfrentarte al nigromántico, tendrás que probar primero que puedes derrotar a sus esbirros.",
    ["O"] = "Mata a 15 asaltantes esqueléticos, 3 sanadores esqueléticos y 3 depositarios esqueléticos y vuelve a donde Sven.",
    ["T"] = "Pon a prueba tu valor",
  },
  [324] = {
    ["D"] = "Después de sacar un único lingote Forjaluz del cofre anegado, está claro que los múrlocs robaron el resto. Da caza a los asaltantes múrloc que rodean los restos del Pigargo volador y busca el resto del hierro Forjaluz perdido.",
    ["O"] = "Reúne 5 lingotes de Forjaluz y vuelve junto a Glorin Frenteacerada en Los Humedales.",
    ["T"] = "Los lingotes perdidos",
  },
  [325] = {
    ["D"] = "Aquí tienes el arma para vencer al demonio ese del que me hablaste. Ya estás $Gpreparado:preparada;, $N. Te deseo mucha suerte, ¡y esperaré con ansia escuchar la leyenda de tu gloria!",
    ["O"] = "Lleva la Pesadilla de Morbent a Sven en el Bosque del Ocaso.",
    ["T"] = "Armado y listo",
  },
  [328] = {
    ["D"] = "Si bien en los registros no aparece ninguna mención al soldado Thorsen, escondida en la tapa de uno de los libros de contabilidad de Herod hay una pequeña llave de latón.$B$BY, tras estudiar en profundidad sus notas, aparecen algunas referencias entre varios papeles, a una \"caja fuerte segura\". La caja está en algún lugar de Las Reservas, el complejo de cuevas que está en la parte trasera de la Base de Kurzen.$B$BSi encontramos la caja fuerte y la pequeña llave de latón puede abrirla, quizás descubramos el misterio tras la detección de Thorsen.",
    ["O"] = "Utiliza la pequeña llave de latón para abrir la caja fuerte del corredor Herod.",
    ["T"] = "La llave oculta",
  },
  [332] = {
    ["D"] = "Si hablas sobre vino con alguien, sabrás que nosotros, los Galina, vendemos el mejor vino de Ventormenta. Y no estamos lejos; seguimos en el Distrito de Mercaderes, en los preciosos canales de la ciudad.$B$BToma, coge este panfleto. Llévaselo a mi hermana Suzetta a nuestra tienda. A cambio te dará una botella de regalo de nuestro famoso Dalaran Noir. ¡No lo lamentarás!",
    ["O"] = "Ve a la Bodega Galina y lleva a Suzetta Galina el cupón de vino para una botella gratis.",
    ["T"] = "Publicidad de la bodega",
  },
  [334] = {
    ["D"] = "Mi hijo Thurman es un aprendiz en Diseños Larson, en el Barrio de los Magos. Hoy salió con prisa y se olvidó su trasquiladora y las agujas. Sé que $Gun gran:una gran; $C como tú debe de tener importantes tareas entre manos, ¡pero sin sus herramientas, Thurman no puede hacer su trabajo de aprendiz!$B$BPor favor, $N. ¿Podrías llevar las herramientas de coser a mi hijo? Diseños Larson es una de las dos tiendas de ropa en el Barrio de los Magos, la que está más al fondo, cerca de la torre de los magos.",
    ["O"] = "Ve a Diseños Larson en el Barrio de los Magos de Ventormenta y entrega a Thurman Schneider sus herramientas de coser.",
    ["T"] = "Un paquete para Thurman",
  },
  [335] = {
    ["D"] = "Lord Wishock está causando gran agitación en la Casa de Nobles. Se ha enterado de lo de nuestro pequeño \"establecimiento\" aquí abajo y está presionando para que se realice una investigación en toda regla. Tengo un plan para mantener a Wishock \"preocupado\", pero necesito tu ayuda.$B$BEn el Jardín de Stalvan, a las afueras de Villa Oscura, crece una flor. Esta pequeña flor blanca es conocida como la lágrima de Tilloa. También necesitaré una zibética, que solo se puede encontrar en la base de las cascadas bajo la Presa de las Tres Cabezas, en Los Humedales.",
    ["O"] = "Zardeth de los Zarpanegra, quiere que le lleves una lágrima de Tilloa de Villa Oscura y una zibética de Los Humedales.",
    ["T"] = "Una noble cerveza",
  },
  [336] = {
    ["D"] = "Lord Wishock, como todos los hombres, tiene sus puntos débiles. Y, resulta que su punto más débil es el licor. La hipocresía es asombrosa. El pobre necio pasa sus días intentando cerrar nuestra pequeña \"taberna\" y las noches arriba, bebiendo y aumentando una cuenta que nunca paga. Bueno, veamos qué le parece a Wishock este brebaje especial que le he preparado: cerveza Zarpanegra. Es bastante fuerte, $N.$B$BLlévasela y veamos si le gusta. Lo más probable es que el viejo tonto esté dando vueltas por la Sala de los Ruegos.",
    ["O"] = "Lleva la cerveza Zarpanegra a Lord Wishock en la Sala de los Ruegos.",
    ["T"] = "Una noble cerveza",
  },
  [337] = {
    ["D"] = "Aunque la cubierta de este libro está llena de arañazos y de barro, aún se pueden leer las palabras del lomo: La Historia de Ventormenta. Al abrir el libro se ve claramente el sello de la biblioteca de Ventormenta en la primera página.$B$BParece que alguien sacó este libro y no lo llegó a devolver...",
    ["O"] = "Devuelve el libro: La Historia de Ventormenta a La Biblioteca de Ventormenta.",
    ["T"] = "Un viejo libro de Historia",
  },
  [338] = {
    ["D"] = "¡Oh, el maestro Nesingwary se sentirá muy disgustado si lo descubre!$B$BConfiaba en mí para revisar el manuscrito de su novela, Las verdes colinas de Tuercespina. Todo iba bien, pero un golpe de viento pasó por el campamento y las páginas quedaron dispersas por todas partes.$B$BConseguí reunir la mayor parte de las páginas, pero hay muchas todavía por la jungla. Reúne las páginas para completar los cuatro capítulos.$B$BYo te ayudaré a organizarlas. Devuélveme los capítulos cuando tengas los cuatro.",
    ["O"] = "Reúne las páginas perdidas del manuscrito Las verdes colinas de Tuercespina. Cuando los cuatro capítulos estén completos, llévaselos a Barnil.",
    ["T"] = "Las verdes colinas de Tuercespina",
  },
  [339] = {
    ["D"] = "¡El capítulo I es fascinante! El maestro Nesingwary establece el telón de fondo de la historia y menciona a sus compañeros de caza, ¡incluyéndome a mí!$B$BPero eso no viene al caso. Déjame ver las páginas que faltan. Parece ser que las páginas 1, 4, 6 y 8 siguen todavía por algún lado en la jungla.",
    ["O"] = "Lleva las páginas 1, 4, 6 y 8 de Las verdes colinas de Tuercespina de Nesingwary a Barnil Jarropetra para completar el capítulo I.",
    ["T"] = "Capítulo 1",
  },
  [340] = {
    ["D"] = "El capítulo II está lleno de historias excitantes sobre la caza de panteras y tigres. ¡Me patearía la cara por haber dejado que las páginas volaran!$B$BParece ser que conseguí recuperar todas las páginas menos las 10, 11, 14, y 16.",
    ["O"] = "Lleva las páginas 10, 11, 14 y 16 de Las verdes colinas de Tuercespina de Nesingwary a Barnil Jarropetra para completar el capítulo II.",
    ["T"] = "Capítulo 2",
  },
  [341] = {
    ["D"] = "El capítulo III es mi favorito. Habla de la primera caza de Sir Erlgadin y Lady Rouack con un rifle enano. Y, al final del capítulo III, ¡menudo suspense!$B$BMe parece que todavía necesitamos las páginas 18, 20, 21 y 24 para terminar el capítulo. Esas páginas están ahí fuera, en algún lugar...",
    ["O"] = "Lleva las páginas 18, 20, 21 y 24 de Las verdes colinas de Tuercespina de Nesingwary a Barnil Jarropetra para completar el capítulo III.",
    ["T"] = "Capítulo 3",
  },
  [342] = {
    ["D"] = "Ah, el capítulo IV, ¡el gran final! ¡Y menudo final!$B$BSolo nos faltan tres páginas de este capítulo. Necesitamos las páginas 25, 26 y 27 para finalizar el capítulo IV.",
    ["O"] = "Lleva las páginas 25, 26, y 27 de Las verdes colinas de Tuercespina de Nesingwary a Barnil Jarropetra para completar el capítulo IV.",
    ["T"] = "Capítulo 4",
  },
  [343] = {
    ["D"] = "Voy a dar un discurso sobre la fortaleza de espíritu y me gustaría recitar unos pasajes de un libro sobre metalurgia, \"La tensión del hierro\". En el Archivo Real, en el Castillo de Ventormenta debería haber una copia del libro. ¿Podrías ir a buscarlo para mí? Si lo haces, ¡que las bendiciones de la Luz te acompañen!$B$BEl Archivo Real es enorme, pero el bibliotecario jefe, Milton Gavilla, conoce todas las estanterías como la palma de su mano. Habla con él, te dará el libro.",
    ["O"] = "Ve al Archivo Real en el Castillo de Ventormenta y habla con Milton Gavilla. Podrá encontrar el libro sobre metalurgia que el hermano Kristoff necesita para su charla.",
    ["T"] = "Hablando de entereza",
  },
  [344] = {
    ["D"] = "¿La tensión del hierro? Sí, conozco el libro. Es bastante bueno, lo escribió el minero enano Margulf Blaggon hace unas décadas. Pasó la mayor parte de su vida en las profundidades de Khaz Modan, extrayendo minerales. ¡Un tipo listo!$B$B¿Por dónde iba? ¡Oh, sí, La tensión del hierro! Me temo que el libro se llevó a la Abadía de Villanorte. El custodio de la biblioteca de la Abadía, el hermano Paxton, quería hacer una copia del libro para su biblioteca.$B$BPero eso fue hace meses. Me pregunto por qué estará tardando tanto...",
    ["O"] = "Habla con el hermano Paxton en la Abadía de Villanorte.",
    ["T"] = "Hermano Paxton",
  },
  [345] = {
    ["D"] = "Quería terminar mi copia de La tensión del Hierro, pero...$B$BBueno, verás, hago mi propia tinta para los libros que copio. Es mi propia receta. No se corre ni mancha y se seca muy rápido. Por desgracia, ¡se seca demasiado rápido! La que me quedaba se secó antes de que pudiera acabar la copia y no he tenido oportunidad de hacer más tinta.$B$BPara hacer más, necesito mineral de hierro de Rethban, de las Montañas Crestagrana. El supervisor Olento, en Villa del Lago, es quien me lo suele suministrar.",
    ["O"] = "Habla con el supervisor Olento en Villa del Lago y pregúntale sobre el mineral de hierro de Rethban.",
    ["T"] = "Suministros de tinta",
  },
  [346] = {
    ["D"] = "Ahora que he terminado de copiar el libro, aquí tienes el original de La tensión del hierro. Gracias por tu paciencia y, si ves a Milton, por favor, dile que siento haberme retrasado tanto.",
    ["O"] = "Vuelve a hablar con el hermano Kristoff en la Plaza de la Catedral. Entrégale el libro La tensión del hierro.",
    ["T"] = "Regresa junto a Kristoff",
  },
  [347] = {
    ["D"] = "La mena de Rethban tiene ese nombre por el hombre que lo descubrió, Bart Rethban. Cuando se funde, no es tan fuerte como el hierro, pero tiene un color oscuro que algunos escribas utilizan en sus tintes. Se puede encontrar en las Cavernas de Rethban, al norte de aquí.$B$BGeneralmente lo tenemos almacenado, pero estamos demasiado ocupados preparándonos para los atacantes orcos que no hemos tenido tiempo de recogerlo. Y, además, los gnolls de Crestagrana viven ahora en las Cavernas de Rethban y sus esclavos se encargan de recoger el mineral.$B$BAsí que si necesitas recogerlo, tendrás que ir a por él.",
    ["O"] = "Reúne 5 montones de mineral de hierro de Rethban y llévaselos al hermano Paxton en la biblioteca de Villanorte.",
    ["T"] = "Mena de Rethban",
  },
  [352] = "_",
  [354] = {
    ["D"] = "Los Agamand eran una de las familias más prósperas de los Claros de Tirisfal. Yo solía trabajar en sus molinos... antes de la Peste.$B$BCuando la Plaga apareció por primera vez, los Agamand fortificaron su casa y convencieron a todos sus empleados para que se quedaran y les ayudaran a defenderse. Fuimos unos insensatos pero, al menos, fuimos unos insensatos leales.$B$BLos Agamand, a causa de su orgullo, nos abocaron a todos a la desgracia. ¡Y ahora son esbirros de la Plaga!$B$BSirve a los Renegados y derrota a los Agamand, que sucumbieron a la peste. Sírveme a mí y tráeme sus restos.",
    ["O"] = "Llévale los restos de Gregor, los restos de Nissa y los restos de Thurman a Coleman Farthing, que está en Rémol.",
    ["T"] = "Muertes en la familia",
  },
  [356] = {
    ["D"] = "En El Baluarte, debemos permanecer en alerta constante para proteger Tirisfal de las incursiones de la Plaga, pero, incluso a veces, consigue colarse.$B$BNuestro éxito en El Baluarte depende de una batalla de un solo frente. No podemos, pues, permitir un ataque desde la retaguardia ni dejar que corten la línea de abastecimiento de El Baluarte.$B$BPatrulla por el oeste y mata a todo miembro de la Plaga que encuentres. Céntrate sobre todo en la Hacienda Balnir, pues, parece que se ha convertido en el refugio de numerosos miembros de la Plaga.",
    ["O"] = "Mata a 8 horrores sangrantes y a 8 espíritus deambulantes.",
    ["T"] = "Patrulla de la retaguardia",
  },
  [357] = {
    ["D"] = "Hay un exánime que mora en la isla Lago Aguasclaras, en el norte. Aunque es un Renegado independiente, cree que los otros no-muertos son esclavos de la Plaga por lo que sus esbirros atacan a todo aquel que se acerca a él.$B$BPero, dado que es un Renegado, la Reina lo reclama. Y si tiene conocimientos de nigromancia, estos serán... muy útiles para nosotros.$B$BPara descubrir su identidad y talentos, debo ver su libro de hechizos. Irrumpe en su campamento, róbalo y tráemelo aquí.",
    ["O"] = "Llévale el libro de hechizos del exánime a Bethor Duroyelo en Entrañas.",
    ["T"] = "La identidad del exánime",
  },
  [358] = {
    ["D"] = "Las fosas comunes, al suroeste de la Granja de Garren situada hacia el norte de aquí, se hicieron para albergar la impresionante cantidad de cadáveres de aquellos que perecieron en Tirisfal cuando la Peste golpeó por primera vez.$B$BDe estas tumbas no había salido ni un solo no-muerto hasta el momento, pero ahora la Plaga envía a gnolls Putrepellejo a recoger cadáveres para que engrosen las filas de sus ejércitos. ¡Esto es inaceptable!$B$BTienes una doble tarea que cumplir: mata a los Putrepellejo de las fosas comunes y la Granja de Garren, y quítales el icor embalsamante que les da vida.",
    ["O"] = "Mata a los asaltatumbas Putrepellejo y a los bastardos Putrepellejo. $B$BLlévale 8 icores embalsamantes al magistrado Sevren de Rémol.",
    ["T"] = "Profanadores",
  },
  [360] = {
    ["D"] = "Ahora llévale la información que te he dado al magistrado Sevren. Le gustará recibirla lo antes posible así que date prisa.$B$BY si ves a algún miembro de la Plaga por el camino, acaba con él. No podemos permitir que vaguen por nuestras tierras a su antojo.",
    ["O"] = "Ve a ver de nuevo al magistrado Sevren de Rémol.",
    ["T"] = "Regresa junto al magistrado",
  },
  [361] = {
    ["D"] = "Esta carta la escribió Thurman, el hijo mayor de Gregor Agamand. Está dirigida a una tal Yvette Farthing, aunque parece que nunca llegó a recibirla...",
    ["O"] = "Encuentra a Yvette Farthing y entrégale la carta de Thurman Agamand.",
    ["T"] = "Una carta sin entregar",
  },
  [362] = {
    ["D"] = "Devlin Agamand era el más joven de los dos hijos de la familia Agamand y en vida ambos no podían ser más diferentes. Thurman era alto y amable mientras que su hermano pequeño era débil y tenía la lengua muy afilada.$B$BCuando la Peste llegó a los Molinos de Agamand, no me sorprendió de que Devlin sucumbiera casi de inmediato. Sus desbarres todavía pueden oírse cerca del camino que conduce a los Molinos de Agamand.$B$BEstoy recogiendo lo que queda de los Agamand y quiero al pobre Devlin. Encuéntralo, acaba con él y tráeme sus huesos.",
    ["O"] = "Acaba con Devlin Agamand y llévale los restos de Devlin a Coleman Farthing en Rémol.",
    ["T"] = "Los molinos fantasma",
  },
  [363] = {
    ["D"] = "About time you woke up. We were ready to toss you into the fire with the others, but it looks like you made it.$b$bI am Mordo, the caretaker of the crypt of Deathknell. And you are the Lich King\'s slave no more.$b$bSpeak with Shadow Priest Sarvis in the chapel at the base of the hill, he will tell you more of what you must know.",
    ["O"] = "Habla con el sacerdote de las Sombras Sarvis.",
    ["T"] = "Desvelo brutal",
  },
  [364] = {
    ["D"] = "Desde que los primeros Renegados se arrastraron fuera de sus tumbas, hemos estado en guerra con el ejército de la Plaga del Rey Exánime: ejércitos de no-muertos reanimados mediante nigromancia, asquerosas bestias del norte, y espectros atormentados.$B$BEl Rey Exánime ya no ocupa el Trono Helado, pero la parte norte del poblado sigue estando invadida por sus esclavos. Destrúyelos, no muestres piedad, aunque sean nuestros antiguos hermanos y hermanas.",
    ["O"] = "Mata a 5 zombis descerebrados y a 5 necrófagos desdichados.",
    ["T"] = "Los autómatas",
  },
  [365] = {
    ["D"] = "¿Qué tenemos aquí? Pareces un poco $Gnovato:novata;, $C. Si quieres demostrar tu valía a la Dama Oscura, tendrás que aprender los caminos de los Renegados.$B$BEncontrarás una plantación en dirección oeste. Los humanos infestan la tierra al igual que el moho un cuerpo en descomposición. Y lo que es peor, la Cruzada Escarlata vigila la zona desde su torre. Dales una lección a esos canallas y roba 10 de sus preciadas calabazas.$B$BCuando tengas 10, llévaselas al boticario Johaan a Rémol.",
    ["O"] = "Roba 10 calabazas de la plantación que encontrarás en dirección oeste, justo al norte de Camposanto, y llévaselas al boticario Johaan a Rémol.",
    ["T"] = "Campos de pena",
  },
  [366] = {
    ["D"] = "Hace tiempo, Gunther y yo, junto con el ahora nigromántico de la Plaga Thule Corvozarpa, fuimos amigos y estudiamos juntos en el Kirin Tor (una sociedad de magos de Dalaran).$B$BTanto Gunther como yo sucumbimos a la Peste pero creí que él estaría muerto o sirviendo al Rey Exánime. ¡Es estupendo saber que está libre!$B$BTenemos que conseguir que se una a nosotros. He lanzado un hechizo sobre su libro de forma que, cuando lo vea, sepa que yo también estoy libre del Rey Exánime. Cuando se entere de esto, espero que se una a nosotros.",
    ["O"] = "Devuélvele el libro de hechizos de Gunther a su dueño, que se encuentra en la isla del Refugio de Gunther.",
    ["T"] = "Devuelve el libro",
  },
  [369] = {
    ["D"] = "Mientras recogías lo que te pedí, descubrí un viejo texto en uno de mis tomos que explica cómo una antigua peste arrasó con miles de víctimas inocentes. Más tarde se descubrió que el agente mortal de dicha peste estaba presente en el veneno de las nocturácnidas.$B$BTráeme un poco de veneno de una nocturácnida sañosa para completar este experimento. Quiero comprobar si el elemento contagioso presente en el veneno servirá para mi nuevo brebaje. Se dice que hay muchas arañas en los Claros de Tirisfal del Este.",
    ["O"] = "El boticario Johaan de Rémol quiere que le lleves 4 muestras de veneno de nocturácnida sañosa.",
    ["T"] = "Una nueva Peste",
  },
  [370] = {
    ["D"] = "Los documentos del ejecutor Arren contienen exactamente todos los detalles que necesitamos para erradicar la infestación humana de nuestras tierras. De acuerdo con esta información, la Cruzada Escarlata ha enviado al capitán Perrine y a una brigada a las ruinas de la torre situada al suroeste de Rémol.$B$BMata a Perrine, a 3 zelotes y a 3 misioneros y regresa aquí.",
    ["O"] = "El ejecutor Zygand de Rémol quiere que mates al capitán Perrine, a 3 zelotes Escarlata y a 3 misioneros Escarlata.",
    ["T"] = "En guerra con la Cruzada Escarlata",
  },
  [372] = {
    ["D"] = "La Cruzada Escarlata ha estado atacando desde las ruinas de la torre situada al norte de Tirisfal, más allá de la Tumba de Faol. De acuerdo con los datos que tenemos, están bajo las órdenes del implacable capitán Melrache.$B$BTe voy a confiar una misión tan poco habitual como peligrosa. Mata a Melrache y a sus dos guardaespaldas en nombre de la Dama Oscura.",
    ["O"] = "El ejecutor Zygand de Rémol quiere que asesines al capitán Melrache y a sus dos guardaespaldas.",
    ["T"] = "En guerra con la Cruzada Escarlata",
  },
  [374] = {
    ["D"] = "El ejecutor Zygand me dice que has estado matando a miembros de la Cruzada Escarlata. Los Claros de Tirisfal están plagados de ellos.$B$BVarimathras, teniente de la Dama Oscura, se está encargando personalmente de exterminar a la escoria de Azeroth: la raza de los humanos. Aquí en los Claros de Tirisfal nos enfrentamos a un nuevo género de humanos: la Cruzada Escarlata.$B$BCorre el riesgo y lanza una ofensiva contra la Cruzada Escarlata, $C. Tráeme 10 anillos con la insignia Escarlata como muestra de tu lealtad a Varimathras y a la Dama Oscura.",
    ["O"] = "Llévale 10 anillos con la insignia escarlata al Guardia de la Muerte Burgess de Rémol.",
    ["T"] = "Prueba de defunción",
  },
  [375] = {
    ["D"] = "Tengo tanto frío... La Peste de los no-muertos corre por mis venas como una serpiente de hielo. Pronto me embargará el estado de automatismo pero nada me impedirá que sirva a nuestra Dama Oscura. En su momento yo misma cosí bolsas de cadáveres para los soldados caídos del poderoso ejército de Sylvanas.$B$BPero ahora mis manos tiemblan de frío. Si me trajeras 5 pelambres de Murciumbrío y un hilo burdo, podría coser yo mismo una manta. Ayúdame, $N, para que así pueda seguir sirviendo a la causa.",
    ["O"] = "Llévale 5 pelambres de Murciumbrío y un hilo burdo a Gretchen Dedmar, que está en Rémol.",
    ["T"] = "El frío de la muerte",
  },
  [376] = {
    ["D"] = "Mis deberes son atender a nuestros guerreros heridos, suministrar ropa y armaduras y asistir al sacerdote de las Sombras Sarvis en lo que necesite.$B$BParece que tú también vas a acabar a su servicio... dando caza a los autómatas. Seguro, sé cómo piensa. Si quieres seguir de una pieza, que seguro que sí, quizás pueda ayudarte. Me estoy quedando sin patas y alas. Si me traes algunas, te buscaré una armadura. Encontrarás muchos lobos y murciélagos hacia el sur.",
    ["O"] = "Consigue 4 garras de carroñero y 4 alas de Murciumbrío.",
    ["T"] = "Los Malditos",
  },
  [377] = {
    ["D"] = "Como si no tuviéramos suficiente con la negligencia de los residentes del Bosque del Ocaso, ahora la Casa de Nobles escupe en el ojo al Consejo de Villa Oscura con su decisión de encarcelar a Dextren Ward en Ventormenta en lugar de decapitarlo como establecía la sentencia de Lord Cerranegro.$B$BTutor fue descubierto vendiendo cadáveres del cementerio a Morbent Vil, y ese es un delito que se castiga con la muerte. Y sin embargo, Ventormenta lo reclama como prisionero.$B$BAsesina a Ward en las Mazmorras de Ventormenta. Tráeme su mano y yo te recompensaré.",
    ["O"] = "El consejero Tallolino de Villa Oscura quiere que le lleves la mano de Dextren Ward.",
    ["T"] = "Crimen y castigo",
  },
  [378] = {
    ["D"] = "Has demostrado tu lealtad al rey Magni, $C. Y tu odio por la escoria Hierro Negro es tan grande como el mío.$B$BHay una tarea que me gustaría hacer por mí mismo, pero el honor me obliga a permanecer junto a Largatrenza. Roggo ha conseguido reunir datos que demuestran que Kam Furiahonda fue miembro de la conspiración para el ataque del Puente Thandol. Fueron los planes de Furiahonda los que hicieron perder a Largatrenza a toda su familia.$B$BFuriahonda está retenido, por razones políticas, en las Mazmorras de Ventormenta. Le quiero muerto, $N. ¡Por Largatrenza! ¡Tráeme su cabeza!",
    ["O"] = "Motley Garmason quiere que le lleves la cabeza de Kam Furiahonda a Dun Modr.",
    ["T"] = "La furia mora en las profundidades",
  },
  [379] = {
    ["D"] = "Si quieres una fuente de calor, yo puedo ayudarte a conseguirla. ¿Y sabes qué quiero yo? ¡Algo de beber! Tráeme cinco faltriqueras de agua de Vagayermos y yo te daré la 4711-FTZ.$B$BYa solo te falta saber dónde puedes encontrar faltriqueras de agua de Vagayermos. ¿Por qué no te diriges al este en busca de nómadas Vagayermos y les pides una? Ya me contarás cómo resulta.",
    ["O"] = "Llévale 5 faltriqueras de agua de Vagayermos al ingeniero jefe Pasaquillas, en Gadgetzan.",
    ["T"] = "Aplacar esa sed",
  },
  [380] = {
    ["D"] = "Obtener los recursos naturales que necesitamos para sobrevivir es una de nuestras luchas más arduas. El oro escaseaba incluso en los tiempos en los que el poder de la Alianza estaba en su cumbre.$B$BHay una mina de oro al noroeste que ha sido invadida por arañas. Necesitamos el oro de esa mina, pero no podemos extraerlo mientras las arañas estén por ahí. No tengo mucho personal para la tarea, así que tendremos que encargarnos de ellas poco a poco.$B$BSube allí y a ver si puedes hacer algo por nosotros, $N.",
    ["O"] = "El ejecutor Arren quiere que mates a 8 nocturácnidas jóvenes y a 5 nocturácnidas.",
    ["T"] = "Hoya Nocturácnidas",
  },
  [381] = {
    ["D"] = "Te alegrará saber que estamos haciendo algunos progresos en la mina, gracias, en gran parte, a tus esfuerzos. Ahora podemos centrarnos en otros problemas.$B$BMis exploradores me han comunicado que un destacamento de la Cruzada Escarlata está estableciendo un campamento al sureste de aquí. La Cruzada Escarlata es una despreciable organización que nos da caza. No descansarán hasta que hayan acabado con todos los no-muertos, sean de la Plaga del Rey Exánime o no. ¡Debemos atacar primero!$B$BTen cuidado, su impuro celo los convierte en adversarios muy peligrosos.",
    ["O"] = "Lleva al ejecutor Arren 12 bandas Escarlata de conversos Escarlata y de iniciados Escarlata.",
    ["T"] = "La Cruzada Escarlata",
  },
  [382] = {
    ["D"] = "Los informes que me envían mis superiores desde Rémol indican que un agente de la Cruzada Escarlata fue enviado desde el Monasterio Escarlata hasta el Camposanto. Sabemos que ese mensajero transporta una información muy importante. No tengo más detalles pero, lleve lo que lleve, ¡debemos conseguirlo!$B$BEres la persona más cualificada a la que puedo enviar, $N. Encuentra a ese mensajero y tráeme cualquier información que encuentres.",
    ["O"] = "Mata a Meven Korgal, el mensajero, en el campamento de los Cruzados y vuelve a comunicar al ejecutor Arren cualquier información que encuentres.",
    ["T"] = "El mensajero rojo",
  },
  [384] = {
    ["D"] = "¡Nada les gusta más a mis clientes que unas ricas costillas de jabalí a la cerveza! El problema es que el trampero local que solía traerme los suministros, se ha alistado en el ejército del Rey para ayudar en el frente de la Alianza.$B$BQuizás puedas ayudarme tú... Si me traes seis costillas de jabalí montés y algo de malta rapsódica de la taberna, compartiré contigo una receta familiar: mis famosas costillas de jabalí a la cerveza. ¡Y te daré una muestra gratis! ¡El secreto está en la malta!",
    ["O"] = "Ragnar Cebatruenos de Kharanos quiere 4 costillas de jabalí tiernas y una jarra de malta rapsódica.",
    ["T"] = "Costillas de jabalí a la cerveza",
  },
  [385] = {
    ["D"] = "Vienen muchos cazadores a Loch Modan por los crocoliscos. Los mercaderes compran pieles de crocolisco para hacer ropa y armaduras, e incluso hay a quien le gusta su carne.$B$BHacemos negocio, pero no mucho, porque esos bichos son muy feroces y se esconden en las islas del lago. Pero tú no te prives, enfrentarse a esos dientes es toda una experiencia.$B$BMe acuerdo de una vez que...",
    ["O"] = "Lleva 5 piezas de carne de crocolisco y 6 pieles de crocolisco a Marek Coracero, en la Cabaña del Errante.",
    ["T"] = "La caza de crocoliscos",
  },
  [386] = {
    ["D"] = "Targorr el Pavoroso sirvió bajo las órdenes de Gath\'llzogg como Verdugo supremo. Sus métodos eran los más despiadados, incluso para los orcos. No dudó en torturar a hombres que habían servido lealmente en la defensa del Reino. Ahora se encuentra recluso en las Mazmorras de Ventormenta, a la espera de que se cumpla su sentencia de muerte. Aun así, hay algo que no encaja. Uno de los nobles burócratas ha detenido su ejecución. Seguro que se trata de una maniobra oscura.$B$BTienes que acabar con Targorr el Pavoroso, $N. Viaja a Las Mazmorras y decapítalo antes de que logre escapar a su destino.",
    ["O"] = "Lleva la cabeza de Targorr el Pavoroso al guardia Berton a Villa del Lago.",
    ["T"] = "Lo que sucede alrededor...",
  },
  [387] = {
    ["D"] = "¡Hay que detener el motín! Necesito tu ayuda. ¡Si alguien se entera de que la prisión es un caos perderé mi trabajo! Esto es juego sucio. Pero lo importante es obligar a los prisioneros a rendirse, por la seguridad de Ventormenta.$B$BEl castigo por sublevación es la muerte. Mata a alguno de esos desviados. Y verás cómo vuelven al buen camino...",
    ["O"] = "El celador Thelagua de Ventormenta quiere que mates 10 prisioneros, 8 presidiarios, y 8 insurgentes en Las Mazmorras.",
    ["T"] = "Detener el motín",
  },
  [389] = {
    ["D"] = "VanCleef y yo fuimos miembros de la Hermandad de los Albañiles. Nuestro primer proyecto después de la Guerra era reconstruir Ventormenta.$B$BCuando terminamos, nos estafaron. Los nobles se negaron a pagarnos. A algunos de los Albañiles mayores les ofrecieron trabajos gubernamentales, pero VanCleef y yo los rechazamos debido a nuestra lealtad con los Albañiles. Él lideró un motín y abandonó la ciudad, jurando venganza.$B$BEs posible que su teniente, Bazil Thredd, sepa más sobre los planes de VanCleef. Está prisionero en las Mazmorras.",
    ["O"] = "Habla con el celador Thelagua en las Mazmorras.",
    ["T"] = "Bazil Thredd",
  },
  [390] = "_",
  [391] = {
    ["D"] = "Dejemos esto claro, $N. No confío en ti. Pero en estas circunstancias, tendré que pasarlo por alto.$B$BEsto es lo que vamos a hacer: baja ahí y habla con Thredd. Si realmente estás de nuestra parte, mátale y tráeme su cabeza como prueba. Si descubrimos que estás de su parte, cuando te encontremos... morirás junto con el resto de esos gusanos.",
    ["O"] = "Mata a Bazil Thredd y lleva su cabeza al celador Thelagua en las Mazmorras.",
    ["T"] = "El motín de Las Mazmorras",
  },
  [392] = {
    ["D"] = "Durante los primeros años del encarcelamiento de Thredd, nunca recibió visitas. Supuse que ya no resultaba útil para la Hermandad y que le habían abandonado.$B$BEn fin, hace unos meses, todo cambió. Comenzó a recibir visitas regularmente... una, o quizás dos veces por semana. Era un hombre extraño... un tipo tranquilo. Tenía sospechas, pero sus papeles estaban en regla.$B$BEn los documentos venía que su nombre era Maelik; esta es su descripción. No creo que venga a visitar más a Thredd.",
    ["O"] = "Lleva la descripción del visitante de Thredd lacrada a Baros Alexston en Ventormenta.",
    ["T"] = "Extraño visitante",
  },
  [393] = {
    ["D"] = "No tengo ni idea, $N. Hay alguien más que quizás pueda ayudarte y resulta que me debe un favor por... bueno, no hace falta entrar en detalles. En los cuarteles del Casco Antiguo, puedes encontrar al maestro Mathias Shaw del IV:7. Shaw está metido en la mayor parte de los negocios sucios o turbios de la ciudad.$B$BSi alguien puede saberlo, es él.",
    ["O"] = "Lleva la descripción del visitante de Thredd lacrada al maestro Mathias Shaw en Ventormenta.",
    ["T"] = "Sombras del pasado",
  },
  [394] = {
    ["D"] = "Encuentra a Shaw en el Casco Antiguo. Dile que nos hemos encargado de la amenaza de Lescovar y cuéntale lo de tu participación en los planes del señor contra la ciudad. Seguro que se sentirá orgulloso de tener a otro héroe entre los ciudadanos de Ventormenta.$B$BLo más probable es que te envíe a ver al príncipe o a otro noble. Pero que no se te suba a la cabeza, $N. Los nobles pueden creer que gobiernan la ciudad, pero hay algunas cosas que se les escapan de las manos.",
    ["O"] = "Habla con Mathias Shaw en Ventormenta.",
    ["T"] = "La cabeza de la bestia",
  },
  [395] = {
    ["D"] = "¡Ah, casi lo olvidaba! Alexston vino cuando no estabas, quería hablar contigo. Sin duda quiere informar a los nobles de los tejemanejes de la Hermandad Defias.$B$BUn pequeño consejo, $N: si alguien te pregunta, da todos los detalles, menos que fue tu mano la que mató a Lescovar. Confía en mí. Sé cómo van esas cosas...$B$BY, con respecto a Trias, bueno, si puedes evitarlo, nunca jamás hables de él con nadie.",
    ["O"] = "Habla con Baros Alexston en Ventormenta.",
    ["T"] = "El fin de la Hermandad",
  },
  [397] = {
    ["D"] = "He oído que a Lord Wishock le gustó mucho la bebida.$B$BNos has hecho un gran servicio, $N. Los brujos de Ventormenta podremos seguir trabajando sin más interrupciones gracias a ti. Por tus esfuerzos, te he preparado un regalo especial. Habla con mi sirviente, Zggi, y pídele tu recompensa.",
    ["O"] = "Habla con Zggi, el sirviente de Zardeth de los Zarpanegra, para pedir tu recompensa.",
    ["T"] = "Nos has hecho un gran servicio",
  },
  [398] = {
    ["D"] = "Se busca: ¡Ojo de cresa!$B$BConsiderado una bestia incluso entre los propios gnolls, Ojo de cresa ha sido sentenciado a muerte por sus crímenes en contra de los Renegados. Este infame villano ha estado robando cuerpos y poniéndolos a disposición del ejército del Rey Exánime. Ojo de cresa fue visto por última vez saqueando las tierras situadas en las proximidades de la Granja de Garren, al norte de Rémol.$B$BA partir de este momento, la ciudad de Rémol ofrece una recompensa al que sea capaz de llevar a cabo su ejecución. Muéstrale al ejecutor Zygand la garra del villano una vez ejecutada la sentencia.$B$BLa clemencia queda relegada a los débiles.",
    ["O"] = "Mata a Ojo de cresa y llévale su garra al ejecutor Zygand de Rémol para obtener una recompensa.",
    ["T"] = "Se busca: Ojo de cresa",
  },
  [399] = {
    ["D"] = "Parece que hace una eternidad desde que tan solo era un chico trabajando en la granja de los Páramos de Poniente. Dicen que nunca puedes regresar, y es cierto. Doblemente cierto en mi caso, unos pasajeros quemaron y tomaron mi casa.$B$BHe hablado con mi padre para descubrir el destino de alguna de mis posesiones, incluyendo mi primera brújula. No fue capaz de salvarlas. Sin embargo, también dice que deberían estar escondidas en la granja.$B$BEncontrarás la Hacienda de Alexston al oeste de la Colina del Centinela. ¿Quizás podrías ir y recuperarla?",
    ["O"] = "Ve a la casa de Baros Alexston en los Páramos de Poniente y busca su brújula. A continuación, llévasela al Distrito de la Catedral, en Ventormenta.",
    ["T"] = "Principios humildes",
  },
  [400] = {
    ["D"] = "Beldin Brasacerada es el dueño de la tienda de mecánica local y además es el mejor técnico de máquinas de asedio. Pero es implacable con sus herramientas. ¡Te aseguro que rompe más llaves de tuerca arcoluz de las que le podemos suministrar!$B$BAcabamos de completar su último pedido. Si se lo llevas, seguro que te compensa el esfuerzo.$B$BSu tienda, el Almacén de Brasacerada, está al noreste de Kharanos. Y es un lugar en el que se reúnen los pilotos veteranos de máquinas de asedio, así que estate $Gatento:atenta; por si oyes algo interesante.",
    ["O"] = "Entrega las herramientas de Brasacerada a Beldin Brasacerada.",
    ["T"] = "Herramientas para Brasacerada",
  },
  [402] = {
    ["D"] = "Lo siento pero, tal y como están las cosas, tengo otros asuntos que atender. Intentaré acabar la traducción más tarde... Si sobrevivimos.",
    ["O"] = "Vuelve más tarde a ver si Sirra sigue aún ocupado.",
    ["T"] = "Sirra está ocupada",
  },
  [404] = {
    ["D"] = "La Plaga ha alcanzado los Claros de Tirisfal y ha infestado el área al oeste de Rémol, cerca del puente viejo.$B$BDirígete allí y enfréntate a los muertos podridos. Trae sus garras podridas y los Guardias de la Muerte te recompensarán.",
    ["O"] = "Llévale 7 garras podridas al Guardia de la Muerte Dillinger de Rémol.",
    ["T"] = "Una tarea pútrida",
  },
  [405] = {
    ["D"] = "Bethor Duroyelo, un poderoso mago de alto rango de Entrañas, que cae al sur, me ha pedido que le envíe a alguien capaz de hacer frente a la Plaga. Tú, $N, eres $Gel elegido:la elegida;.$B$BHázselo saber a Bethor y él te explicará cuál es tu misión. Yo desconozco los detalles pero sé que tiene algo que ver con reclutar a un exánime, y parece peligroso.$B$BPrepárate, $C, pues no debes fallar.$B$BBusca a Bethor en el Barrio de la Magia de Entrañas.",
    ["O"] = "Comunícale las órdenes de Sevren a Bethor Duroyelo en Entrañas.",
    ["T"] = "El exánime pródigo",
  },
  [406] = "_",
  [407] = {
    ["D"] = "¿Simples e inofensivas calabazas, verdad? Eso es lo que deben parecer... Si queremos detener el avance de Arthas por el norte y la infestación humana por el sur, tenemos que comenzar a valorar nuestro propio potencial.$B$BCon un poco de ingenio, una simple calabaza se convierte en un arma de nuestra Dama Oscura. Solo hay que echarle un poco de mi última fórmula.$B$BHan capturado a otro zelote Escarlata y está encerrado en el sótano del Mesón La Horca. Llévale a ese desgraciado esta calabaza.",
    ["O"] = "Llévale la calabaza envenenada al zelote Escarlata capturado que está encerrado en el sótano del Mesón La Horca.",
    ["T"] = "Campos de pena",
  },
  [408] = {
    ["D"] = "Los miembros de la Plaga han encontrado cobijo en los Molinos de Agamand. El punto más sólido es la Cripta de la Familia Agamand, lugar en el que su líder militar, un esqueleto llamado capitán Dargol, ha establecido su puesto de mando. Por medio de la nigromancia, ha revivido a los ancestros de los Agamand y planea utilizarlos contra nosotros.$B$BEsto no puede ocurrir y tú has de evitarlo. Viaja a la Cripta de la Familia Agamand y derrota a los ancestros de los Agamand.$B$B¡Ah! Y tráeme la calavera del capitán Dargol.",
    ["O"] = "Mata a 8 ancestros gemebundos y a 8 ancestros en podredumbre.$B$BMata al capitán Dargol y llévale su calavera al magistrado Sevren de Rémol.",
    ["T"] = "La Cripta de la Familia",
  },
  [409] = {
    ["D"] = "Afirmas que no estás $Gsometido:sometida; a la voluntad del Rey Exánime y el encantamiento que Bethor lanzó sobre mi libro lo corrobora. Sin embargo, para que pueda confiar en ti debes darme una prueba de que estás en contra de la Plaga.$B$BHay un viejo altar, a poca distancia de la costa sur de esta isla, que utiliza un poderoso agente de la Plaga llamado Lillith Nefara.$B$BCoge una vela de señalización de este cajón. Enciéndela en el altar y Lillith se despertará. Acaba con Lillith y creeré que en verdad eres $Gun enemigo:una enemiga; del Rey Exánime.",
    ["O"] = "Coge una vela de señalización.$B$BDespierta a Lillith Nefala y mátala.$B$BVuelve a ver a Gunther a su isla.",
    ["T"] = "Prueba de lealtad",
  },
  [411] = {
    ["D"] = "Dale esta gema abisal a mi viejo camarada Bethor. A través de ella podremos comunicarnos... y en verdad tengo mucho que contarle.",
    ["O"] = "Llévale la gema abisal a Bethor Duroyelo en Entrañas.",
    ["T"] = "El regreso del exánime pródigo",
  },
  [412] = {
    ["D"] = "Debería regresar a Gnomeregan y seguir buscando una cura para los gnomos irradiados.$B$BSin embargo, me he quedado en Kharanos porque es mi hogar y quería ayudar a defenderlo de los ataques de los Peloescarcha. He estado trabajando en el mantenimiento y la reparación de los tanques de vapor.$B$BPor alguna razón, los carroñeros Peloescarcha se han aficionado a los engranajes giromecánicos que necesito para completar las reparaciones. Se encuentran esparcidos por todo el Frente de Peloescarcha, al sureste de la ciudad. ¿Me ayudas a conseguir traer los engranajes de vuelta?",
    ["O"] = "Lleva 8 engranajes giromecánicos a Razzle Engranágil.",
    ["T"] = "Operación Recombobulación",
  },
  [413] = {
    ["D"] = "Me gustaría que mi hermano Wellart probara mi nueva cerveza negra Brillante. Es un montaraz. Está destacado en la torre de vigilancia del sur, en la frontera del Loch Modan.$B$BNo se dedica a hacer cerveza, como los demás Cebadiz, pero le encanta beberla y sé que esta le gustará.$B$B¿Podrías llevarle este tonel de cerveza negra Brillante de mi parte?",
    ["O"] = "Lleva el tonel de cerveza negra Brillante al montaraz Cebadiz.",
    ["T"] = "Cerveza negra Brillante",
  },
  [414] = {
    ["D"] = "He estado bebiendo la cerveza negra Brillante esa que hizo mi hermano. ¡Está buenísima! Quiero que la pruebe un compañero mío. Se llama Kadrell y normalmente patrulla por el camino de Thelsamar en Loch Modan.$B$BPara llegar a Thelsamar, ve al sureste a través del túnel y gira al noreste cuando el camino se divida. Sigue en esa dirección y llegarás $Gderecho:derecha; a Thelsamar.$B$BDale este tonel de cerveza negra Brillante al montaraz Kadrell. ¡A ver qué le parece!",
    ["O"] = "Lleva el tonel de cerveza negra Brillante al montaraz Kadrell.",
    ["T"] = "Cerveza negra para Kadrell",
  },
  [415] = {
    ["D"] = "Rejold Cebadiz de Las Birras ha estado preguntando por ti.$B$BQuiere hablar contigo sobre una nueva bebida que ha estado preparando... dijo algo de que quería que le ayudaras. Quizás quieras ir a Las Birras a ver qué necesita de ti.$B$BPodrías ganarte una cervecita...",
    ["O"] = "Habla con Rejold Cebadiz.",
    ["T"] = "La nueva bebida de Rejold",
  },
  [416] = {
    ["D"] = "Tenemos una infestación grave. Esas ratas de túnel kóbold asquerosas se han instalado en las laderas. ¡Están cavando madrigueras y ensuciándolo todo!$B$BQueremos erradicarlos. Si los cazas y me traes sus orejas, puedes quedarte con su botín.$B$BLa mayor concentración de ratas de túnel kóbold está en el interior y en los alrededores de la Mina de Fuenteplata, al este.",
    ["O"] = "Lleva 8 orejas de rata de túnel al montaraz Pico Tormenta a Thelsamar.",
    ["T"] = "Mata ratas",
  },
  [417] = {
    ["D"] = "Hildelve escribió en su diario que fue atacado por Sarnagarra, un gran oso zarpahelada. Ahuyentó a la bestia, pero no antes de que esta lo atacara salvajemente.$B$BA juzgar por el estado del cuerpo de Hildelve, Sarnagarra debió de regresar para matarlo.$B$BPero Hildelve tuvo tiempo de escribir un último deseo en su diario:$B$BSarnagarra debe pagar por esto.",
    ["O"] = "Mata a Sarnagarra.$B$BLleva su garra sarnosa y el diario de Hildelve al piloto Podomartillo en la Avanzada de la Puerta Sur.",
    ["T"] = "Vengar al piloto",
  },
  [418] = {
    ["D"] = "Aquí en Thelsamar siempre hay alguien que quiere comer, críos por todos lados, los obreros de la excavación y eso. Lo más conocido es la morcilla, ¿la has probado?$B$B¿No? Bueno, pues aquí hay que trabajar para comer, no creas que te libras porque seas $C.$B$BLo único que necesito es carne de oso, el resto está incluido en el preparado. ¡Tú trae eso y de cocinar me ocupo yo, Vidra!",
    ["O"] = "Lleva 8 trozos de carne de oso a Vidra Hogartufa, en Thelsamar.",
    ["T"] = "Morcillas de Thelsamar",
  },
  [419] = {
    ["D"] = "Mi compañero y amigo, Mori Hildelve, que es piloto de máquinas de asedio, está perdido en los montes. Buscábamos un raro mineral necesario para fabricar un potente tipo de pólvora y durante nuestra búsqueda, subió con su máquina una empinada montaña ¡y la máquina se averió!$B$BAún convencido de que el mineral estaba en estas montañas, Hildelve me encargó que custodiara las máquinas mientras él continuaba a pie.$B$BPero ya han pasado días y por las noches oigo unos estremecedores aullidos al norte del Lago de Helm, al oeste del túnel. Mori es duro como una roca, pero estoy preocupado.$B$BTe lo ruego, $N. Encuéntralo.",
    ["O"] = "El piloto Podomartillo, en la Avanzada de la Puerta Sur, quiere que encuentres al piloto Hildelve.",
    ["T"] = "El piloto perdido",
  },
  [421] = {
    ["D"] = "Lady Sylvanas ha encomendado a Varimathras la conquista de las tierras humanas y enanas del sur.$B$BPero ese necio, Arugal, charlatán de Dalaran y ahora bestia maldita del Castillo de Colmillo Oscuro, dejó que su imprudente magia causara estragos en el bastión estratégico del Bosque de Argénteos.$B$BNecesito a alguien muy diestro en combate para ayudar a arreglar el desastre de Arugal. Demuéstrame tu valía matando a 5 canosos Furia Lunar. Es fácil encontrar a esas bestias salvajes al final del camino, hacia la colina de abajo.",
    ["O"] = "Dalar Tejealba en El Sepulcro quiere que mates a 5 canosos Furia Lunar.",
    ["T"] = "Demuestra tu valía",
  },
  [422] = {
    ["D"] = "No me puedo imaginar cómo Arugal consiguió que el Kirin Tor le aceptase.$B$BLo que importa ahora es descubrir exactamente, qué magia utilizó Arugal. Así podré volverla contra él y asegurar el Bosque de Argénteos para la Dama Oscura.$B$BAl principio, Arugal se estableció en la granja de trigo que está justo al norte del puente. Uno de los mortacechadores, informó sobre algunos libros de hechizos que había visto, pero no pudo hacerse con ellos. Consigue el hechizo llamado Remedio de Arugal que está en esos libros.",
    ["O"] = "Recupera el Remedio de Arugal para Dalar Tejealba, en El Sepulcro.",
    ["T"] = "La locura de Arugal",
  },
  [423] = {
    ["D"] = "Tras examinar el trabajo de Arugal, se han confirmado mis peores sospechas. Ese viejo chiflado no estaba cualificado ni para limpiar orinales en Dalaran, y mucho menos para representar al Kirin Tor en su momento más desesperado. ¡Imbéciles!$B$BArugal utilizó objetos encantados para reforzar su débil magia. Necesito examinar esos objetos. Continúa tu viaje y mata glotones Furia Lunar y almanegras Furia Lunar hasta que hayas reunido suficientes grilletes encantados para mi investigación. Puedes encontrar a estas repugnantes criaturas hacia el norte y el este.",
    ["O"] = "Lleva 6 grilletes de glotón y 3 grilletes de almanegra a Dalar Tejealba en El Sepulcro.",
    ["T"] = "La locura de Arugal",
  },
  [424] = {
    ["D"] = "Voy a tardar mucho más de lo que había pensado en descubrir los oscuros secretos que yacen tras los encantamientos que Arugal estaba usando.$B$BPero, mientras tanto, necesito que te encargues de un pequeño problema que nuestros acechadores negros han descubierto. Parece ser que Arugal permitió que su magia se extendiera hasta la Mina de Elem, en las colinas del sureste.$B$BLa mina resultó ser un lugar estratégico para el avance de Varimathras.$B$BQuiero que decapites al supervisor contaminado de la mina, Grimson el Pálido. Tras su muerte, la mina será nuestra.",
    ["O"] = "Mata a Grimson el Pálido y lleva su cabeza a Dalar Tejealba en El Sepulcro.",
    ["T"] = "La locura de Arugal",
  },
  [426] = {
    ["D"] = "Los miembros de la Plaga están intentando establecer una base en los Molinos de Agamand. Si lo consiguen, podrán lanzar más ataques sobre Tirisfal.$B$BLas órdenes son claras: hay que detener a la Plaga en los Molinos.$B$BVe a los Molinos. Recoge costillas con cortes de los soldados armadóseos y los soldados testapartida, y calaveras ennegrecidas de los tirahuesos Ojosombra.$B$BPara llegar hasta los Molinos, sigue el camino en dirección oeste. Tras cruzar el puente, toma el siguiente desvío hacia el norte y continúa en esa dirección. Cuando veas los molinos de viento, dará comienzo la batalla.",
    ["O"] = "Recoge 5 costillas con cortes y 3 calaveras ennegrecidas, y después regresa ente el Guardia de la Muerte Dillinger de Rémol.",
    ["T"] = "La invasión de los molinos",
  },
  [427] = {
    ["D"] = "Los documentos que el ejecutor Arren nos ha proporcionado son exactamente lo que necesitábamos para dar un giro a nuestra batalla contra la desdichada Cruzada Escarlata. Ahora sabemos sus ubicaciones exactas en Claros de Tirisfal.$B$BPero la Guardia de la Muerte tiene otras preocupaciones mayores. El ejército del Rey Exánime es más numeroso cada día que pasa. Necesitamos a alguien con un poco de \"iniciativa\" como tú para que envíe a la Cruzada Escarlata a la tumba.$B$BDemuéstrame que eres capaz de servir a la Dama Oscura; para ello, viaja al oeste hasta la torre que encontrarás pasada la Hacienda Solliden y mata a 10 guerreros Escarlata.",
    ["O"] = "El ejecutor Zygand de Rémol quiere que mates a 10 guerreros Escarlata.",
    ["T"] = "En guerra con la Cruzada Escarlata",
  },
  [429] = {
    ["D"] = "El necrófago, Ivar el Hediondo, hirió de gravedad a mi hermano Quinn y no sé si podrá curarse sin ayuda mágica. Si bien yo no soy un boticario, sé que nuestros boticarios pueden preparar pociones curativas a partir de corazones de huargos descoloridos.$B$BConsigue esos corazones y llévaselos al boticario Renferrel en El Sepulcro, al sur. Después, vuelve aquí con la poción.$B$BEncontrarás muchos huargos entre aquí y la Huerta de Malden, al este.",
    ["O"] = "Consigue 6 corazones de huargo descoloridos y llévaselos al boticario Renferrel en El Sepulcro.",
    ["T"] = "Corazones salvajes",
  },
  [430] = {
    ["D"] = "Aquí está la poción para curar a Quinn Yorick.$B$BAhora, si me perdonas, tengo algo muy importante que hacer...",
    ["O"] = "Lleva la poción de Quinn a Quinn Yorick en Los Dominios de Ivar, al norte de El Sepulcro.",
    ["T"] = "Regresa junto a Quinn",
  },
  [432] = {
    ["D"] = "¡Esos malditos troggs están destrozando mi excavación! ¡Mira! ¡Ahí va otro barril de polvo!$B$B¡Ay! ¡Cómo me gustaría bajar ahí y retorcerles a todos ese pescuezo huesudo que tienen! ¡Quiero matarlos a todos!$B$BEsto no puede ser bueno para la salud. Tanta ira, tanto estrés... ¿Qué puedo hacer sin perjudicarme la salud? ¡Nada!$B$BLos mataría yo mismo, pero mi puntería no es la que era. ¡Entra tú en la cantera y mata a un puñadito de esos bichos asquerosos! Te pagaré... ¡Tú hazles daño! ¡Mátalos!",
    ["O"] = "Mata a 8 aplastacráneos Comepiedras y a 8 cascahuesos Comepiedras en la Cantera de Gol\'Bolar o en la mina.",
    ["T"] = "¡Malditos troggs!",
  },
  [433] = {
    ["D"] = "Como miembro del Senado y de la Liga de Expedicionarios, he asumido la responsabilidad de investigar las condiciones de la cantera. ¡Los troggs se han vuelto locos!$B$BHan convertido en un caos la Cantera de Gol\'Bolar. Van saliendo a medida que profundizamos en nuestra excavación, y destruyen nuestro equipo y dejan atrapados bajo tierra a nuestros mineros.$B$BTenemos que sacarlos de ahí. Algunos magos han estado haciendo horas extra para encantar estas runas de retorno. Si consigues llevárselas a los mineros atrapados, podrán regresar a la superficie a salvo.",
    ["O"] = "Reparte 7 runas de retorno a los mineros atrapados en la Cantera de Gol\'Bolar.",
    ["T"] = "El servidor público",
  },
  [436] = {
    ["D"] = "El prospector Vetaferro dirige la excavación de unas antiguas ruinas al este del lago. Últimamente está haciendo pocos progresos, a pesar de todos los suministros que le hemos enviado.$B$BVetaferro es un enano honrado y tenaz que valora los resultados. Por eso sospecho que hay otros factores en su contra.$B$BVe a la Excavación de Vetaferro y habla con Magmar Ceñimal. Él se encarga de los detalles de la excavación y sabrá a qué se debe el retraso en los trabajos.$B$BPara llegar a la excavación, rodea el extremo sur del lago y luego dirígete al este.",
    ["O"] = "Habla con el explorador Ceñimal.",
    ["T"] = "La excavación de Vetaferro",
  },
  [437] = {
    ["D"] = "Los informes iniciales de nuestros exploradores en Argénteos, muestran que los Putrepellejo tienen una base en una vieja granja del norte, El Campo Muerto. Hemos lanzado pequeños asaltos contra ellos, pero cada vez que lo intentamos, el alma en pena Latipesadilla les ayuda, utilizando su magia para diezmar nuestras tropas.$B$BQueremos acabar con Latipesadilla.$B$BVete a El Campo Muerto y ataca a los Putrepellejo. Si no ves a Latipesadilla, mata a los Putrepellejo hasta que aparezca y, después, mátala a ella.$B$BSu esencia se convertirá en polvo. Tráeme ese polvo como prueba.",
    ["O"] = "Mata gnolls Putrepellejo en El Campo Muerto.$B$BCuando aparezca Latipesadilla, mátala.$B$BLleva la esencia de Latipesadilla al sumo ejecutor Hadrec en El Sepulcro.",
    ["T"] = "El Campo Muerto",
  },
  [438] = {
    ["D"] = "$N, hemos descubierto una nueva infestación de la Plaga: El Viejo Embarcadero. El embarcadero está al este de El Sepulcro y está muy protegido por los Putrepellejo y otros no-muertos.$B$BDebe de haber una razón para la ocupación de la Plaga de esa zona. Vete a El Viejo Embarcadero y descubre por qué está allí la Plaga.",
    ["O"] = "Ve a El Viejo Embarcadero.$B$BEncuentra la razón por la que la Plaga está ahí.",
    ["T"] = "El Viejo Embarcadero",
  },
  [442] = {
    ["D"] = "Ha llegado el momento de asaltar el punto fuerte de la Plaga en Argénteos: la Isla de Fenris.$B$BDebes volver a la isla y acabar con a Thule Corvozarpa. ¡Quiero su cabeza!$B$BEsta misión es tan peligrosa como vital, $N. No puedes fallar.",
    ["O"] = "Lleva la cabeza de Thule al sumo ejecutor Hadrec en El Sepulcro.",
    ["T"] = "Asalto a la Isla de Fenris",
  },
  [444] = {
    ["D"] = "He reducido el icor que me has traído y he separado sus componentes básicos. Mis descubrimientos no son concluyentes, pero he determinado que en la creación del icor se utilizó brujería y nigromancia.$B$BMientras sigo estudiando el icor, quiero que le lleves una muestra a Bethor Duroyelo en Entrañas. Bethor no es un boticario, pero su conocimiento de la magia, puede ayudarnos a descubrir el misterio de los Putrepellejo.$B$BVete. Encontrarás a Bethor en el Barrio de la Magia de Entrañas.",
    ["O"] = "Lleva la muestra de icor a Bethor Duroyelo en Entrañas.",
    ["T"] = "El origen de los Putrepellejos",
  },
  [445] = {
    ["D"] = "El tiempo es un lujo que no nos podemos permitir quienes trabajamos para Lady Sylvanas. Pero seguro que ya te habías dado cuenta.$B$BComo miembro de la Sociedad Real de Boticarios es mi deber compartir mis conocimientos con mis colegas para que con nuestro esfuerzo colectivo logremos algún día proporcionarle a la Dama Oscura su tan ansiada Nueva Peste.$B$BLlévale estas conclusiones al boticario Renferrel que está en El Sepulcro en el Bosque de Argénteos. Sigue los caminos que parten en dirección sur desde Rémol. Desde Entrañas, dirígete hacia el suroeste.",
    ["O"] = "Llévale los descubrimientos del boticario Holland al boticario Renferrel del Bosque de Argénteos.",
    ["T"] = "Entrega a Bosque de Argénteos",
  },
  [447] = {
    ["D"] = "Arthas cuenta con innumerables tropas. Pero, con una Nueva Plaga podríamos erradicar tanto al ejército de la Plaga como a la infestación humana.$B$BMis estudios han demostrado que la sangre de araña combinada con una toxina derivada del corazón de un oso grisáceo resulta en un elixir letal. Reúne muestras de las arañas en Penumbra de las Celerácnidas, al noroeste, y de los osos que vagan por el Bosque de Argénteos. Lleva los componentes al maestro boticario Faranell de la Sociedad Real de Boticarios, en Entrañas.",
    ["O"] = "Consigue 6 corazones de oso grisáceo y 6 muestras de sangre de celerácnida y llévaselas al maestro boticario Faranell en Entrañas.",
    ["T"] = "Una fórmula mortal",
  },
  [449] = {
    ["D"] = "Si bien las heridas de Quinn se están curando, quiero entregar nuestro informe al sumo ejecutor Hadrec lo antes posible. Contiene información vital para la ocupación de los Renegados de Argénteos.",
    ["O"] = "Lleva el informe de los Mortacechadores al sumo ejecutor Hadrec en El Sepulcro.",
    ["T"] = "El informe de los Mortacechadores",
  },
  [450] = {
    ["D"] = "Puedes seguir ayudando a la Sociedad Real de Boticarios, $N.$B$BCuando Arugal utilizó su magia maligna en el bosque, maldijo a uno de nuestros miembros más prometedores, el boticario Berard. Ahora es un sirviente de Arugal, sin dominio de sí mismo. En estas condiciones, Berard no nos sirve de nada. Pero sus descubrimientos podrían servirnos. Estaba estudiando una antigua plaga que acabó con la vida acuática del Lago Lordamere años atrás.$B$BVe a buscar el Diario de Berard. Está en su estudio, el la Posada de la Aldea Piroleña, y llévaselo al boticario Renferrel.",
    ["O"] = "Recupera el Diario del boticario Berard de la Aldea Piroleña y llévaselo al boticario Renferrel en El Sepulcro.",
    ["T"] = "Una fórmula mortal",
  },
  [451] = {
    ["D"] = "Según el Diario de Berard, el musgo de los vagadores del Lago y los trepadores podría ser útil para nuestro objetivo de crear una Nueva Plaga. Esas bestias viven en las islas del Lago Lordamere, al este.$B$BTambién menciona un tumor endurecido muy extraño que descubrió en un múrloc Anca Vil. Por lo visto, el tumor es tan poco común, que Berard solo pudo obtener uno.$B$BReúne muestras del musgo y del tumor endurecido y llévaselas al maestro boticario Faranell en Entrañas.",
    ["O"] = "Lleva 6 muestras de moho de trepador del Lago, 6 muestras de moho de vagador del Lago y un tumor endurecido al maestro boticario Faranell en Entrañas.",
    ["T"] = "Una fórmula mortal",
  },
  [452] = {
    ["D"] = "¡Has llegado justo a tiempo, $N!$B$BEl Consejo Piroleña está a punto de regresar. Arugal tiene que encargarse de otras cosas en el bastión. Utiliza el Consejo como si fueran marionetas, para mantener a los ineptos esbirros de la Aldea Piroleña bajo su control. Pero creo que sin la ayuda del Consejo Piroleña, los hijos de Arugal se volverían contra su maestro.$B$BNo puedo encargarme de todo sin ayuda. Te necesito. Juntos podemos acabar con el Consejo para Lady Sylvanas.",
    ["O"] = "Ayuda a la mortacechadora Faerleia a destruir al Consejo Piroleña.",
    ["T"] = "La emboscada de Aldea Piroleña",
  },
  [453] = {
    ["D"] = "Buena suerte, $N. Espero que encuentres al tipo este... y que lo encuentres vivo.$B$BSi dejó la seguridad de Villa Oscura, podría haber sido víctima de las bestias salvajes.",
    ["O"] = "Encuentra a la figura misteriosa. Las pistas que tienes: $B$BNo es oriundo de Villa Oscura. $B$BEs una persona nerviosa, que se altera fácilmente. $B$BDejó Villa Oscura y se dirigió al oeste.",
    ["T"] = "Encuentra la figura misteriosa",
  },
  [455] = {
    ["D"] = "Los orcos del clan Faucedraco tienen un refugio en Los Humedales ¡y ahora se han adentrado en Dun Algaz! No podemos permitir que continúen afianzándose aquí o Loch Modan perderá el contacto con el Puerto de Menethil, nuestro puerto y bastión en Los Humedales.$B$BAtraviesa Dun Algaz y dirígete a Los Humedales, ¡cuando estés allí mata a tantos orcos como puedas! Informa a Valstag Quijahierro en Puerto de Menethil.$B$BMenethil está pasado Dun Algaz, siguiendo el camino hacia el Norte y al Oeste en el cruce.$B$BContamos contigo, $N. No nos falles.",
    ["O"] = "Atraviesa Dun Algaz.$B$BMata 8 exploradores Faucedraco y 6 brutos Faucedraco.$B$BInforma a Valstag Quijahierro de Puerto de Menethil.",
    ["T"] = "El asedio de Algaz",
  },
  [456] = {
    ["D"] = "Saludos, $N. Soy el conservador Ilthalaine. He venido a Cañada Umbría para asegurarme de que se mantiene el equilibrio de la naturaleza.$B$BEsta primavera ha llovido más de lo normal, lo que ha beneficiado mucho a algunas criaturas del bosque, pero ha perjudicado a otras.$B$BAhora hay demasiados sables de la noche y jabalíes Cardo; Cañada Umbría no puede alimentar a tantas bestias. Joven $C, reduce las poblaciones de jabalí Cardo y sable de la noche para mantener la armonía de la naturaleza.",
    ["O"] = "Mata a 3 sables de la noche y 3 jabalíes cardo jóvenes.",
    ["T"] = "El equilibrio de la naturaleza",
  },
  [457] = {
    ["D"] = "Acabar con los ejemplares más jóvenes de Cañada Umbría ha sido un buen comienzo, $N, pero queda mucho por hacer.$B$BSi no tomamos medidas, se agotarán los recursos del bosque. Exterminar animales es un mal necesario por el bien de todos los que comparten la tierra. Para mantener el equilibrio adéntrate en Cañada Umbría, al noreste, y acaba con los jabalíes Cardo y sables de la noche sarnosos.",
    ["O"] = "Mata a 6 sables de la noche sarnosos y a 6 jabalíes Cardo.",
    ["T"] = "El equilibrio de la naturaleza",
  },
  [458] = {
    ["D"] = "Me alegro de que hayas llegado, $C. El susurro de los espíritus del bosque me ha traído extrañas nuevas.$B$BLa misteriosa protectora de los bosques, Tarindrella, ha regresado a Cañada Umbría. La presencia de la dríade en estos bosques no se conocía desde antes de la bendición de Teldrassil. Si ha viajado de nuevo hasta estas tierras, entonces sin duda algo va mal.$B$BBusca a Tarindrella y averigua qué asuntos la han traído aquí. Uno de los centinelas la ha avistado al suroeste de Aldrassil.",
    ["O"] = "Busca a la dríade Tarindrella.",
    ["T"] = "La protectora de los bosques",
  },
  [459] = {
    ["D"] = "¡Malditos grells! Han vuelto a robar de nuestras despensas.$B$BSi no te importa echarme una mano, seguro que todo lo que han robado está esparcido por los campamentos grell del oeste. Te recompensaré si me lo traes de vuelta.",
    ["O"] = "Recupera las judías robadas de Dellylah, el agua robada de Dellylah y las bolsas robadas de Dellylah.",
    ["T"] = "La protectora de los bosques",
  },
  [460] = {
    ["D"] = "\"No pareces un Putrepellejo. Bien. Ya había perdido la esperanza...$B$BMe llamo Alaric, y fui el jefe de los sirvientes y guardaespaldas de Thule. Utilizó su magia para \"protegerme\" de la peste de no-muertos, pero su hechizo me dio una no-vida de la que no puedo escapar. Después de crear a los malvados gnolls Putrepellejo, Thule me decapitó... y dio mi cabeza a los gnolls como juguete.$B$BPor favor, llévame con mi cuerpo. Creo que los gnolls lo enterraron aquí, en el Castillo de Fenris cerca del establo, en una tumba sin nombre.\"",
    ["O"] = "Lleva la cabeza de Alaric a su tumba.",
    ["T"] = "Descansar en piezas",
  },
  [462] = "_",
  [463] = {
    ["D"] = "¿Has oído hablar del Guardaverde? ¿No lo estarás buscando, verdad? Si lo buscas es que estás $Gloco:loca;, ¿pero quién soy yo para apartar a un idiota de su muerte?$B$BSi estás buscando a esa bestia, he oído decir que está en los pantanos, al este del camino cuando se divide hacia Dun Modr. Está escondido entre los crocoliscos... ¡y cosas aún peores!$B$BDeja aquí tu dinero. No lo necesitarás allí a donde vas... no querrás que Verdín se rompa un diente con tus monedas cuando te parta en dos de un bocado, ¿verdad?",
    ["O"] = "Encuentra al Guardaverde.",
    ["T"] = "El Guardaverde",
  },
  [464] = {
    ["D"] = "El clan orco Faucedraco fue prácticamente exterminado al final de la Segunda Guerra, cuando perdieron su control sobre los dragones Rojos que eran sus mascotas. Los dragones no estaban muy contentos con sus antiguos dueños y dieron caza despiadadamente a todos los Faucedraco. Los pocos que quedaron huyeron de Grim Batol y se refugiaron en las montañas al este de la Excavación de Whelgar.$B$BAhora han ganado fuerza y tienen un campamento al noreste de Menethil.$B$BQueremos que ataques ese campamento. Recoge sus estandartes de guerra y tráemelos como prueba.",
    ["O"] = "Lleva 8 estandartes de guerra Faucedraco al capitán Puñorrecio.",
    ["T"] = "Estandartes de batalla",
  },
  [465] = {
    ["D"] = "Mientras estabas combatiendo a los Faucedraco, descubrimos más cosas sobre sus planes.$B$BNek\'rosh ha construido catapultas ¡y pretende asediar Menethil! ¡Además, debido a la lucha en Dun Algaz no podemos recibir refuerzos!$B$BTienes que acabar con esas catapultas. Están en el campamento Faucedraco.$B$BToma, coge esta yesca. Ábrete camino hasta las catapultas y préndeles fuego con la yesca.$B$BCuando hayas terminado, encuentra y mata a Nek\'rosh. Necesitamos acabar con esta bestia Faucedraco... de raíz.",
    ["O"] = "Destruye las catapultas Faucedraco.",
    ["T"] = "La estrategia de Nek\'rosh",
  },
  [466] = {
    ["D"] = "He oído decir que se encontró un extraño mineral, incendicita, en Los Humedales, en una cueva al noroeste de la Puerta de Algaz. La incendicita es un material volátil y es difícil de utilizar sin que explote, ¡pero seguro que si se funde de la forma adecuada podría hacer proyectiles de cañón que harían un daño tremendo!$B$BTráeme mineral de incendicita y encontraré a alguien que se atreva a experimentar con él.$B$BOh, una cosa más, para extraer el mineral de un filón tendrás que saber minería. ¡Si no eres $Gminero:minera; lleva uno contigo a la cueva!",
    ["O"] = "Lleva 6 piezas de mineral de incendicita al piloto Roscapiedra de Dun Morogh.",
    ["T"] = "Búsqueda de incendicita",
  },
  [467] = {
    ["D"] = "¡Al piloto Roscapiedra le encanta su máquina de asedio! ¡Y siempre anda buscando maneras de mejorar su cilindrada!$B$BHa oído hablar de un mineral muy raro y necesita a alguien que se lo consiga. Si te apetece una aventurilla, ve a hablar con él en el Almacén de Brasacerada en Dun Morogh.",
    ["O"] = "Habla con el piloto Roscapiedra.",
    ["T"] = "La búsqueda de los Roscapiedra",
  },
  [468] = {
    ["D"] = "El montaraz Piedragar está buscando voluntarios para una misión que les llevará a través de Dun Algaz hasta Los Humedales.$B$BSi quieres mojarte las botas en ese embarrado condado del Norte, habla con Piedragar en Dun Algaz, siguiendo el camino hacia el Norte, pasada la torre de vigilancia.",
    ["O"] = "Habla con el montaraz Piedragar.",
    ["T"] = "Preséntate ante el montaraz Piedragar",
  },
  [469] = {
    ["D"] = "¡Saludos $Gviajero:viajera;!$B$B¿Acaso te diriges hacia Menethil? Si no es así, te recomendaría ir y hacer un alto. Es un buen lugar para hacer unas compras o para disfrutar de una buena pinta y de algo de descanso.$B$BAhora bien, si ya te dirigías hacia allí, no creo que te importe llevarle este fardo de pieles de crocolisco a James Halloran, el curtidor.$B$BLo haría yo mismo, pero últimamente mi mujer no está de muy buen humor, me gustaría verla lo más tarde que pueda.",
    ["O"] = "Entrega el fardo de pieles de crocolisco a James Halloran, el curtidor, en Puerto de Menethil.",
    ["T"] = "Entrega cotidiana",
  },
  [472] = {
    ["D"] = "¡Dun Modr ha caído en manos de los enanos Hierro Negro!$B$BMis heridas son graves, $C. ¡Y casi todo mi regimiento fue asesinado! Los matones Hierro Negro nos atacaron antes de que pudiéramos reagruparnos tras la emboscada del Puente Thandol.$B$BNuestro líder, Largatrenza, hizo sonar el cuerno de retirada. Cuando dejamos la ciudad fui herido en la espalda con un hacha perdida. Todo se volvió negro.$B$BDesperté aquí, en Menethil, pero estoy preocupado por mis compañeros. Aún abrigo esperanzas. ¡Quizás Largatrenza siga con vida! ¡Ve a ver si puedes encontrarle cerca de Dun Modr, $R!",
    ["O"] = "Busca a Largatrenza junto a la ciudad de Dun Modr.",
    ["T"] = "La caída de Dun Modr",
  },
  [473] = {
    ["D"] = "$N. El clan orco Faucedraco está mostrando su fuerza, y no solo en Algaz. El capitán Puñorrecio ha pedido hablar con quienes quieran ofrecer su ayuda contra esos orcos. Lo encontrarás en el castillo, en la Sala de Mandos.",
    ["O"] = "Habla con el capitán Puñorrecio.",
    ["T"] = "Preséntate ante el capitán Puñorrecio",
  },
  [475] = {
    ["D"] = "Se avecinan problemas en el bosque.$B$BGaerolas Talvethren es el Gran celador de los druidas hibernantes de la Garra en Túmulo de Ban\'ethil. Su función como protector de los Adormecidos es preservar su seguridad de modo que no se rompa el pacto que tienen con Ysera. $B$BPero hace tiempo que no sé nada de él y me inquieta. Viaja al este hasta la Aldea Brisa Estelar para hablar con Gaerolas y regresa para informarme de todo. Cuando sepa que mis hermanos duermen apaciblemente, me quedaré más tranquilo.",
    ["O"] = "Busca a Gaerolas Talvethren en la Aldea Brisa Estelar.",
    ["T"] = "Una brisa problemática",
  },
  [476] = {
    ["D"] = "¡La corrupción de la tribu de los Tuercepinos ha resurgido!$B$BMe tendieron una emboscada cuando me marchaba para Túmulo de Ban\'ethil y saquearon la Aldea Brisa Estelar. Algo del bosque ha corrompido a Ursal el Violento, su jefe, que está empleando los maléficos poderes del musgovil para volverlos locos.$B$BYo estoy malherido y no puedo llevarle estas trágicas noticias a Athridas; tendrás que hacerlo tú, joven $C. ¡Esperemos que los trastornados Tuercepinos aún no hayan llegado a Ban\'ethil!",
    ["O"] = "Ve a ver a Athridas Manto de Oso a Dolanaar.",
    ["T"] = "Corrupción en Tuercepinos",
  },
  [480] = {
    ["D"] = "Un archimago de gran poder está supervisando la reactivación del nodo de energía Ley. Con el progreso que han conseguido, nuestra única esperanza de detenerles es matándole. Será difícil, estará protegido por muchos magos y depositarios.$B$BDebes ir y acabar con él rápidamente. Cuando esté muerto, tráeme su bastón. Ahora, vete.",
    ["O"] = "Mata al archimago Dalaran, recupera su bastón y llévaselo al sacerdote de las Sombras Allister en El Sepulcro.",
    ["T"] = "El Tejedor",
  },
  [481] = {
    ["D"] = "Lleva el colgante a Dalar Tejealba, quizás pueda ayudarnos a entender los planes de los magos.",
    ["O"] = "Lleva el colgante con runas grabadas a Dalar Tejealba en El Sepulcro.",
    ["T"] = "El análisis de Dalar",
  },
  [482] = {
    ["D"] = "¡Por supuesto! ¿Cómo se me ha podido olvidar?$B$BMolino Ámbar fue uno de los lugares marcados por el Kirin Tor, debido a que tiene un nodo de energía Ley inactivo. Los magos deben de estar planeando reactivar el nodo y usar sus energías para algo.$B$BNo podemos permitir que esto ocurra...$B$BPara activar las energías Ley de la zona, necesitarán una gran cantidad de poder. Podemos paralizarlo todo si conseguimos los colgantes de los magos.$B$BCuéntale esto a Allister, él decidirá lo que tenemos que hacer.",
    ["O"] = "Habla con el sacerdote de las Sombras Allister en El Sepulcro.",
    ["T"] = "Las intenciones de los Dalaran",
  },
  [483] = {
    ["D"] = "Los invasores Tuercepinos han sido vistos saqueando Túmulo de Ban\'ethil, en la colina hacia el oeste.$B$BLos druidas durmientes quedarán atrapados en el Sueño Esmeralda por toda la eternidad, ajenos a sus destinos, a menos que les ayudemos. El delicado ritual de hibernación no puede detenerse sin las Reliquias del Despertar.$B$BViaja al Túmulo y consigue el talismán de la garra del cuervo, el plumón pluma negra, el zafiro de cielo y la runa de Nidal que los druidas guardan en cofres sagrados. Tráemelos y podré preparar el ritual del despertar.",
    ["O"] = "Consigue las Reliquias del Despertar.",
    ["T"] = "Las Reliquias del Despertar",
  },
  [484] = {
    ["D"] = "Soy James Halloran, el curtidor. Mis pieles curtidas de crocolisco son las más apreciadas de todas y llegan cargamentos a todos los puertos del mundo.$B$B¿Viste a Einar? Es uno de los cazadores encargados de proporcionarme pieles de crocolisco.$B$BMe estoy quedando sin las suaves pieles de crías de crocolisco.$B$BPor eso te pagaré generosamente por una pila de esas pieles. Podrás encontrar crías de crocolisco en los pantanos que están a las mismas puertas de la ciudad.",
    ["O"] = "Consigue 4 pieles de crocoliscos jóvenes para James Halloran de Puerto de Menethil.",
    ["T"] = "Piel de crocolisco joven",
  },
  [485] = {
    ["D"] = "Has descubierto una especie de instrumento extraño de metal y con forma de huevo. Si jugueteas con uno de los botones del huevo, este toma vida. ¡Se convierte en una especie de artilugio robótico gnomo! Y del interior del huevo sale una voz.$B$B\"Mi nombre es Oglethorpe Obnoticus, ¡y mi robot mensajero se ha averiado! Te recompensaré por su rescate. Por favor, lleva este localizador hasta el robot.\"$B$B\"He procesado las coordenadas del robot: falló en la cabecera del río, cerca de Roca Oculta.\"",
    ["O"] = "Lleva el localizador al robot mensajero de Oglethorpe, en el nacimiento del río de las Tierras del Interior, cerca de Roca Oculta.",
    ["T"] = "¡Encuentra a OOX-09/TI!",
  },
  [486] = {
    ["D"] = "Ha llegado el momento de salvar a los druidas de la Garfa. Si fallamos ahora, $N, nunca podrán despertar de su sueño.$B$BPrepararé las Reliquias del Despertar para llevar a cabo el ritual. Para que mis esfuerzos surtan efecto, hay que eliminar a las malditas bestias responsables de esta horrible situación. Solo en ese momento podrá completarse el ritual.$B$BUrsal el Violento fue quien se metió con nuestros hermanos y él será el que pague las consecuencias de su liberación. Sigue el camino al noroeste y encontrarás un sendero hacia el norte que lleva a La Grieta. El cubil de Ursal estará ahí.",
    ["O"] = "Mata a Ursal el Violento.",
    ["T"] = "Ursal el Violento",
  },
  [487] = {
    ["D"] = "Hay que reforzar la seguridad en el camino que conduce a Darnassus. Unos fúrbolgs corruptos de la tribu de los Tuercepinos han estado atacando despiadadamente a los viajeros.$B$BEse camino se utiliza a diario para transporte de mercancías y es la vía de comunicación más importante de Darnassus. No podemos permitir que esa pandilla de delincuentes paganos aterrorice a la gente.$B$BLevántate en armas en nombre del bosque sagrado, $C. Su guarida se encuentra siguiendo este camino, hacia el norte. Acaba con los emboscadores Tuercepinos y después vuelve para informarme.|n",
    ["O"] = "Acaba con 8 emboscadores Tuercepinos.",
    ["T"] = "El camino a Darnassus",
  },
  [488] = {
    ["D"] = "Veo que tienes ganas de trabajar... Por suerte para ti, no hay un día que pase que no desee tener cerca a $Gun:una; inocente $C a quien proponerle mi oferta.$B$B¿Sabes, $N? Puedo hacerte muy feliz y ofrecerte cosas que jamás hubieras soñado tener, pero para ello deberás traerme una serie de objetos.$B$BPara desempeñar mi trabajo en el bosque a veces necesito ciertos... componentes. Tráeme colmillos de sable de la noche, plumas de lechuza estrígida y muestras de seda de araña Tejemadera.$B$BY que esto quede entre nosotros, $R.",
    ["O"] = "Consigue 2 colmillos de sable de la noche, 2 plumas de lechuza estrígida y 2 muestras de seda de araña Tejemadera.",
    ["T"] = "Las apuestas de Zenn",
  },
  [489] = {
    ["D"] = "El Consejo del Bosque se ha enterado de que ayudaste a Zenn Nauseapezuña, un sátiro enemigo del bosque. Como $Gbuen:buena:r; $R que eres, no deberías dedicarte a mancillar el bosque matando criaturas de la naturaleza.$B$BHas de reparar tu error ante el Consejo si deseas seguir siendo $Gamigo:amiga; de Teldrassil.$B$BDale a Nauseapezuña una lección y recibirás tu perdón. Las bellotas viles son semillas corruptas que caen de los árboles y que expulsan humo verde.$B$BDale algunas a Nauseapezuña, él creerá que se trata de un inofensivo aperitivo.",
    ["O"] = "Recoge 3 bellotas viles.",
    ["T"] = "¡Búsqueda de redención!",
  },
  [490] = "_",
  [492] = {
    ["D"] = "De acuerdo con la Guardia de la Muerte, acaban de capturar a otro de esos insensatos enanos montaraces. La Guardia de la Muerte suele encerrar a los prisioneros en el sótano del Mesón La Horca hasta que pueda encargarse \"debidamente\" de ellos.$B$B¿Por qué no vas a comprobar si al montaraz capturado le gusta la bebida especial que he preparado para él? Contiene una pequeña muestra de lo que la Dama Oscura tiene reservado para todo Azeroth.",
    ["O"] = "Llévale la bebida especial de Johaan al montaraz capturado.",
    ["T"] = "Una nueva Peste",
  },
  [493] = {
    ["D"] = "La Dama Oscura está presionando mucho a la Sociedad Real de Boticarios para que creen una Nueva Plaga. Estamos trabajando diligentemente y hemos avanzado mucho. Creemos que lo conseguiríamos antes si compartiésemos información entre la Sociedad.$B$BPor esa razón, quiero que entregues mis últimos descubrimientos al boticario Lydon en el Molino Tarren, una pequeña ciudad en las Laderas de Trabalomas. El viaje será largo.$B$BDirígete al sur y no te alejes de los caminos. ¡Sigue las señales de cerca!",
    ["O"] = "Lleva el descubrimiento del boticario Renferrel al boticario Lydon en la ciudad de Molino Tarren, en las Laderas de Trabalomas.",
    ["T"] = "Viaja a Laderas de Trabalomas",
  },
  [494] = {
    ["D"] = "Cállate y presta atención, $C.$B$BMe han enviado aquí para vigilar Trabalomas. La mía es una misión de reconocimiento. Debes enviar un mensaje a la suma ejecutora Darthalia, a Molino Tarren, de inmediato. Dile que la mortacechadora Lesh le envía este mensaje:$B$B\"Desde el oeste nos llega el llanto del cuervo.\"$B$BSigue el camino que va al este y fíjate bien en las indicaciones. ¡Apresúrate! ¡Ve a Molino Tarren a toda prisa!",
    ["O"] = "Ve a Molino Tarren para llevar el mensaje de la mortacechadora Lesh a la suma ejecutora Darthalia.",
    ["T"] = "El momento de atacar",
  },
  [495] = {
    ["D"] = "Jarra\'thol y sus ogros debían ir a Alterac en busca de la Corona de Voluntad para Lady Sylvanas. Por desgracia, en lugar de regresar con ella, el muy idiota decidió probarla... Supongo que los ogros no se pueden resistir a nada luminoso y brillante.$B$BSin embargo, su constante incompetencia se está convirtiendo en un problema. La Corona de Voluntad le permitió resistirse a la posesión que le obligaba a servir a Lady Sylvanas.$B$BLa Dama Oscura quiere darle un castigo ejemplar; viaja al Molino Tarren en Trabalomas e informa a Melisara, mi teniente.",
    ["O"] = "Preséntate ante Melisara que está en Molino Tarren, en las Laderas de Trabalomas.",
    ["T"] = "Corona de Voluntad",
  },
  [496] = {
    ["D"] = "Otro maldito día en el Molino Tarren. Todo este aire puro me pone enfermo, $N.$B$BTenemos que infestar a los humanos cuanto antes. He estado estudiando potenciales agentes letales para utilizar en mis brebajes pero no he tenido tiempo de recogerlos todos.$B$BSi quieres hacer algo útil, podrías procurarme los siguientes objetos: diez lenguas de oso gris e icor de trepador, que es muy raro y difícil de encontrar. Nada más salir del Molino Tarren encontrarás osos y trepadores.",
    ["O"] = "El boticario Lydon de Molino Tarren quiere 10 lenguas de oso gris y un poco de icor de trepador.",
    ["T"] = "Elixir de sufrimiento",
  },
  [497] = "_",
  [498] = {
    ["D"] = "El Jefe de Guerra me envió con algunos compañeros a Durnholde para recoger un objeto e investigar a esa tal Hermandad.$B$BFuimos a echar un vistazo, pero un numeroso grupo de humanos nos atacó por sorpresa. Luchamos como fieras, pero estábamos en franca minoría. Nos tomaron cautivos a algunos, pero conseguí escapar.$B$BDurnholde está al otro lado del río, hacia el sureste. Drull y Tog\'thar están retenidos allí; debes ir a rescatarlos.",
    ["O"] = "Krusk de Molino Tarren quiere que liberes a Drull y a Tog\'thar en el Castillo de Durnholde.",
    ["T"] = "El rescate",
  },
  [499] = {
    ["D"] = "Tardaré un momento en mezclar estos ingredientes para crear lo que yo llamo mi elixir de sufrimiento.$B$B¡Allá vamos! Ah, nada me alegra tanto la vida en este lugar miserable como traer padecimientos al mundo.$B$BVeamos si la poción tiene el efecto deseado.$B$BDale a probar a mi asistente Umpi lo que la Dama Oscura ha planeado tanto para los humanos como para la Plaga.",
    ["O"] = "Dale el elixir de sufrimiento a Umpi.",
    ["T"] = "Elixir de sufrimiento",
  },
  [500] = {
    ["D"] = "Los ogros Aplastacresta han construido un túmulo en las Montañas de Alterac, cerca de la ciudad arrasada de Alterac. Los exploradores indican que también han invadido las ruinas.$B$BSi dejamos que se queden ahí, su siguiente paso será bajar las colinas y llegar hasta nosotros.$B$BVe a liquidar ogros en las Montañas de Alterac, hacia el norte. Si quieres ganar un buen botín, tráeme todos los nudillos sucios que llevan.",
    ["O"] = "Recoge 9 nudillos sucios de ogros Aplastacresta en las Montañas de Alterac. Llévaselos al alguacil Rutagrana a Costasur.",
    ["T"] = "La recompensa Aplastacresta",
  },
  [501] = {
    ["D"] = "Oh, $N. En Trabalomas todo está florecido y el aire es puro y fresco. No puedo soportarlo. Miro por mi mugrienta ventana y espero el día en que nuestra Nueva Plaga traiga a este mundo la muerte que merece.$B$BAunque me gustaría perderme en mis negras reflexiones, el deber hacia la Dama Oscura me llama. ¿Quieres ayudarme? Según mis estudios, puedo obtener una estupenda poción letal con sangre de león de montaña. Tráeme un poco, verás qué bien lo pasaremos.",
    ["O"] = "Lleva 10 viales de sangre de león de montaña al boticario Lydon de Molino Tarren.",
    ["T"] = "Elixir de dolor",
  },
  [502] = {
    ["D"] = "¡No hay nada como el olor a sangre fresca!$B$BAhora añadimos un poquito de esto y una pizca de esto otro ¡y ya está! Esta poción se llama elixir de dolor. Si mis cálculos son correctos, este brebaje complacerá enormemente a Lady Sylvanas. Pero antes hay que probarlo.$B$BLlévalo a la granja norteña de Campos de Trabalomas, que está al suroeste. A ver qué le parece esta golosina a Stanley, el perrito del granjero Ray.",
    ["O"] = "Dale el elixir de dolor a Stanley.",
    ["T"] = "Elixir de dolor",
  },
  [503] = {
    ["D"] = "Según los documentos que has traído, tienen a Gol\'dir en una importante base de la Hermandad situada al norte, probablemente en las Montañas de Alterac. Ya lleva demasiado tiempo cautivo; hay que liberarlo.",
    ["O"] = "Libera a Gol\'dir e informa a Krusk en Molino Tarren.",
    ["T"] = "[DEPRECATED]Gol\'dir",
  },
  [504] = {
    ["D"] = "Ahora que has conocido a los ogros Aplastacresta, quiero que les revientes las narices...$B$BVe a las Ruinas de Alterac y busca a esos belicistas Aplastacrestas. Quiero que liquides a un buen número de ellos. Es la única forma de que esos brutos aprendan a mantenerse alejados del territorio de la Alianza.",
    ["O"] = "Mata a 10 belicistas Aplastacresta y vuelve a hablar con el alguacil Rutagrana en Costasur.",
    ["T"] = "Belicistas Aplastacresta",
  },
  [505] = {
    ["D"] = "Soy el nuevo magistrado de Costasur. Mi ilustre predecesor fue asesinado. No se ha encontrado a los asesinos, pero la investigación apunta a que eran mercenarios de la Hermandad, un grupo de ladrones liderados por unos nobles sin honor del desaparecido reino de Alterac.$B$BLa Hermandad tiene un campamento en el Saliente de Sofera y en Daga de Corahn, al norte y al oeste de Molino Tarren, territorio ocupado por la Horda. Elimina a los miembros de la Hermandad que encuentres en estos campamentos.",
    ["O"] = "Mata a 12 asaltantes de caminos y 8 ladrones de la Hermandad e informa al magistrado Henry Maleb en Costasur.",
    ["T"] = "Hermandad de asesinos",
  },
  [507] = {
    ["D"] = "Es evidente que hay que eliminar a Perenolde, erradicar su vileza. Su mansión está hacia el norte, a orillas del Lago Lordamere. $N, cumple con tu deber, recupera el colgante que anhela el Jefe de Guerra y vuelve aquí.",
    ["O"] = "Mata a Aliden Perenolde y pregunta a su querida, Elysa, por el colgante de Taretha.",
    ["T"] = "[DEPRECATED]Lord Aliden Perenolde",
  },
  [508] = {
    ["D"] = "¿El colgante? Claro que te lo puedes quedar... ¡Llévatelo! No quiero ningún recuerdo de él... tan solo, solo deja en paz...",
    ["O"] = "Devuélvele el colgante de Taretha a Krusk, que está en el Molino Tarren, en las Laderas de Trabalomas.",
    ["T"] = "Un regalo de Taretha",
  },
  [509] = {
    ["D"] = "Un día más, un elixir más. Si por mí fuera, dejaría que esos ridículos humanos y la Plaga se mataran mutuamente. De hecho, ese parece ser su único objetivo.$B$BNuestra Dama Oscura quiere acelerar el proceso, así que ¿por qué no pasar estos negros días sembrando muerte y enfermedad por el lugar?$B$BEste es el plan, $N: ve a la Granja Nethander, al sur de Molino Tarren y al este del río y de Costasur. Tráeme seis flores Morrobarro para mi próximo elixir.",
    ["O"] = "Lleva 6 flores Morrobarro al boticario Lydon de Molino Tarren.",
    ["T"] = "Elixir de agonía",
  },
  [511] = {
    ["D"] = "Una de las cartas de la mesa está cifrada y es inescrutable.",
    ["O"] = "Lleva la carta cifrada al maestro cultural Dibbs en Costasur.",
    ["T"] = "Carta cifrada",
  },
  [512] = {
    ["D"] = "Temo que la Hermandad aspire a provocar la caída de Costasur. Debemos eliminar a la cúpula para que no puedan llevar a cabo sus planes.$B$BVe a las ruinas de Strahnbrad; están al norte, en las Montañas de Alterac. Creemos que la Hermandad se concentra en esa zona.$B$BSabemos que los jefes de estos maleantes son antiguos nobles de Alterac. Puede que estén disfrazados de agentes ordinarios de la Hermandad, pero los distinguirás por los sellos que llevan.$B$BTráeme los sellos.",
    ["O"] = "Lleva 7 sellos de Alterac al magistrado Maleb a Costasur.",
    ["T"] = "Nobles muertes",
  },
  [513] = {
    ["D"] = "Justo cuando pensaba que este sería otro día triste y sin alegría, me traes estas maravillosas flores Morrobarro.$B$BEl primer paso es molerlas. Luego el maestro boticario Faranell de Entrañas aplicará su coloide de decadencia y ya estará listo.$B$BLlévale este compuesto Morrobarro al maestro boticario Faranell a toda prisa, $N.",
    ["O"] = "Lleva el compuesto Morrobarro al maestro boticario Faranell a Entrañas.",
    ["T"] = "Elixir de agonía",
  },
  [514] = {
    ["D"] = "Tengo un colega en Forjaz que quizás pueda descifrarla. Ve a la biblioteca de la Sala de los Expedicionarios, en Forjaz, y habla con el prospector Pico Tormenta. Dile que vas de mi parte y enséñale la carta. Si hay alguien que pueda descifrar esto al norte de Cumbre de Roca Negra, ese es él.",
    ["O"] = "Lleva la carta cifrada con ingenio al prospector Pico Tormenta a Forjaz.",
    ["T"] = "Una carta para Pico Tormenta",
  },
  [515] = {
    ["D"] = "¿Una mezcla Morrobarro? ¡Brillante! ¿Cómo no se me ha ocurrido a mí?$B$BVoy a añadir mi coloide de decadencia a este maléfico brebaje.$B$BPara activar los agentes contaminantes de este preparado, Lydon necesitará una poción de sangre de trol fuerte, escamas Espinadaga y ojos de aleta rasgados de la costa sur de Trabalomas. Él ya sabrá qué hacer con esos reactivos. ¡Ardo en deseos de saber el resultado del experimento!",
    ["O"] = "Lleva la mezcla Morrobarro, una poción de sangre de trol fuerte, 5 escamas Espinadaga y 5 ojos de aleta rasgados al boticario Lydon a Molino Tarren.",
    ["T"] = "Elixir de agonía",
  },
  [517] = {
    ["D"] = "El nuevo elixir ya está listo para probar. Pero sería una pena malgastar una plaga potente y altamente contagiosa en un sapo o un perro. Vamos a divertirnos en nombre de Lady Sylvanas.$B$BVete a Dun Garok. ¿Sabes las barracas en las que se hacinan esos asquerosos enanos? Tráeme un barril de cerveza Machacacanillas. Y cuando lo traigas nos pegaremos una buena fiesta, $N.",
    ["O"] = "Lleva un barril de cerveza Machacacanillas al boticario Lydon de Molino Tarren.",
    ["T"] = "Elixir de agonía",
  },
  [518] = {
    ["D"] = "Jarra\'thol y sus ogros Aplastacresta han invadido las ruinas de Alterac. Tenemos que reducir sus filas si queremos castigarlo.$B$BPrimero los debilitamos y después procedemos.$B$B¿Entendido? Bien. Adelante.",
    ["O"] = "Melisara de Molino Tarren quiere que mates a 10 aplastadores Aplastacresta.",
    ["T"] = "Corona de Voluntad",
  },
  [519] = {
    ["D"] = "Targ, Rastrazo y Glommus son los generales de Jarra\'thol. Le son leales, aunque quién sabe qué instiga la lealtad de un ogro.$B$BTráeme sus cabezas, $N, y después solo quedará separar a Jarra\'thol de la suya, corona incluida.",
    ["O"] = "Mata a Targ, Rastrazo y Glommus y lleva sus cabezas a Melisara a Molino Tarren.",
    ["T"] = "Corona de Voluntad",
  },
  [520] = {
    ["D"] = "Con los tenientes de Jarra\'thol muertos, ya solo queda matar a Jarra\'thol y hacerse con la Corona de Voluntad. No tengo dudas de que no querrá dártela voluntariamente, y menos ahora que has acabado con sus servidores.$B$BEncuentra a Jarra\'thol y tráeme su cabeza y su corona. La resistencia Aplastacresta tiene que terminar.",
    ["O"] = "Mata a Jarra\'thol y lleva su cabeza con la Corona de Voluntad a Melisara a Molino Tarren.",
    ["T"] = "Corona de Voluntad",
  },
  [521] = {
    ["D"] = "Ahora solo falta ir a Entrañas para entregarle la corona a Sharlindra. Ella se ocupará de entregársela a Lady Sylvanas. Te advierto que no debes usarla para tus propios fines, $N; recuerda lo que le ha pasado a Jarra\'thol.",
    ["O"] = "Ve a Entrañas para entregarle la Corona de Voluntad a Sharlindra.",
    ["T"] = "Corona de Voluntad",
  },
  [522] = {
    ["D"] = "Esto es un contrato para el asesinato del magistrado Henry Maleb. Estaba lacrado con cera roja y un sello muy distintivo.",
    ["O"] = "Lleva el contrato de asesinato al magistrado Maleb a Costasur.",
    ["T"] = "Contrato de asesino",
  },
  [524] = {
    ["D"] = "Los Guardias de la Muerte tienen retenidos a varios granjeros. La suma ejecutora Darthalia sospechaba que uno de los estrategas militares de Ventormenta estaba entre ellos. Al parecer fue encontrado muerto anoche en las Tierras de la Peste. Darthalia, siempre pensando en mí, decidió que yo me ocuparía de los prisioneros.$B$BCreo que ya es hora de montarles una fiesta a los granjeros. ¡Va a ser estupendo!$B$BColoca este barril contaminado en la alfombra de su cuarto. ¡Cerveza apestada para todos!",
    ["O"] = "Coloca el barril contaminado en la alfombra para los granjeros.",
    ["T"] = "Elixir de agonía",
  },
  [525] = {
    ["D"] = "He descifrado la carta, pero no sé cómo interpretarla. ¿Y la tenían unos ladrones en las laderas de Alterac? Qué raro...$B$BLlévasela al magistrado de Costasur. Aunque la carta es críptica, su ciudad es la más cercana a Alterac; si se está cociendo algo, es mejor que lo sepa.",
    ["O"] = "Lleva la carta descifrada al magistrado Maleb a Costasur.",
    ["T"] = "Más misterios",
  },
  [526] = {
    ["D"] = "Has encontrado un lingote Forjaluz en el cofre, ¡pero no es suficiente para hacer nada decente!$B$BDeberías volver y buscar más. Quizás los múrlocs que rodean los restos del naufragio tengan más lingotes Forjaluz.",
    ["O"] = "Reúne 5 lingotes de Forjaluz y vuelve junto a Glorin Frenteacerada en Los Humedales.",
    ["T"] = "Lingotes Forjaluz",
  },
  [527] = {
    ["D"] = "Stand at attention while addressing me, $C.$B$BI am under direct command of Sylvanas. We are to quell the human infestation until our apothecaries can develop the new plague.$B$BOur information leads us to believe that the town of Hillsbrad is vulnerable to attack. Your first assignment is aimed at disrupting their food supply and infrastructure.$B$BTravel west. Raid the northernmost farms of Hillsbrad Fields. Make sure that the landowners, Farmer Ray and Farmer Getz, are executed as well.",
    ["O"] = "Mata a 6 mozos de labranza de Trabalomas y 6 granjeros de Trabalomas, al granjero Ray y al granjero Getz e informa a Darthalia en Molino Tarren.",
    ["T"] = "La batalla de Trabalomas",
  },
  [528] = {
    ["D"] = "El ataque a las granjas del norte tiene aterrados a los lugareños. Excelente.$B$BNuestros mortacechadores informan que los campesinos huyen a los campos del sur de Trabalomas. Esos necios creen que pueden escapar a su destino escondiéndose en el huerto.$B$BPero se equivocan, $N. Vuelve a Campos de Trabalomas y busca a los campesinos ocultos. Aniquílalos en el nombre de nuestra Dama Oscura.$B$BPuedes retirarte.",
    ["O"] = "Mata a 15 campesinos de Trabalomas e informa a Darthalia en Molino Tarren.",
    ["T"] = "La batalla de Trabalomas",
  },
  [529] = {
    ["D"] = "Los humanos de Trabalomas han opuesto más resistencia de la esperada. Al parecer, el herrero del lugar está fabricando armas. Tenemos que interrumpir todo suministro.$B$BTu misión es ejecutar al herrero Verringtan. Elimina también a los aprendices. Y trae un envío de hierro de su almacén. Nos irá bien cuando terminemos con Trabalomas y pasemos a conquistar Costasur.",
    ["O"] = "Mata al herrero Verringtan y a 4 herreros aprendices de Trabalomas.$B$BRecupera un envío de hierro e informa a Darthalia en Molino Tarren.",
    ["T"] = "La batalla de Trabalomas",
  },
  [531] = {
    ["D"] = "¡Por la Luz! No puedes meter eso aquí, es antihigiénico, antiestético y... ¡y huele mal!$B$BLlévatelo, ¿quieres? ¡Ese trofeo sangriento no le interesa a nadie!",
    ["O"] = "Lleva la cabeza del viejo Tiznado a Vyrin Vientoveloz a la Cabaña del Errante.",
    ["T"] = "La venganza de Vyrin",
  },
  [532] = {
    ["D"] = "Trabalomas está en ruinas y creemos que a punto de caer. Pero debemos mantenernos firmes hasta erradicar a toda la chusma humana para toda la eternidad.$B$BLos humanos resisten y siguen luchando pese a sus abrumadoras pérdidas. Debemos eliminar a su líder, el magistrado Ladoquemado, además de a los consejeros. También debes acabar con su más preciado documento, la Declaración de Trabalomas. Y ya que estás, saquea el registro civil.",
    ["O"] = "Mata al magistrado Ladoquemado y a 4 consejeros de Trabalomas. Acaba con la Declaración de Trabalomas. Saquea el registro de Trabalomas. Después informa a Darthalia en Molino Tarren.",
    ["T"] = "La batalla de Trabalomas",
  },
  [533] = {
    ["D"] = "Por fin empezamos a tener noticias acerca del paradero de Gol\'dir. Los humanos lo han llevado de un lado a otro, pero hemos centrado la búsqueda en el campamento de la Hermandad que está al norte de aquí.$B$B$N, tienes que ir y conseguir toda la información que puedas.",
    ["O"] = "Consigue información sobre el paradero de Gol\'dir\'s e informa a Krusk en Molino Tarren.",
    ["T"] = "Infiltración",
  },
  [534] = "_",
  [535] = {
    ["D"] = "Urrg... Herido, agonizante y frente al enemigo.$B$BDe todos modos, seguro que me matarás, pero si me traes cerveza de Costasur, los de tu clase beben, ¿no?, te contaré lo que deseas saber.",
    ["O"] = "Llévale 1 jarra de cerveza de Costasur a Valik en el campamento de la Hermandad.",
    ["T"] = "Valik",
  },
  [536] = {
    ["D"] = "Me han transferido de la guardia de la Ciudad de Ventormenta a Costasur, un lugar que merecería ser abandonado, a juzgar por su aspecto. No me quito de encima el olor a puerto.$B$BPor no hablar de Rutagrana... Quiere que me encargue de los múrlocs. ¡Ja!$B$BApenas me animo a acercarme a esos pescados gigantescos de la Playa del Oeste. Te agradecería mucho que te ocuparas de ello en mi lugar.",
    ["O"] = "Mata a 10 cazamareas y 10 oráculos Rompeanca por orden del teniente Farren Orinelle de Costasur.",
    ["T"] = "Al lo largo de la costa",
  },
  [537] = {
    ["D"] = "La carta fue escrita por un ser que responde al nombre de Nagaz, miembro del Despertar de Argus. No sé quiénes son, pero si están aliados con la Hermandad debemos considerarlos una amenaza.$B$BQuiero que localices a Nagaz y a los magos de la Sombra Argus mencionados en la carta. Localízalos y elimínalos.$B$BExplora las Tierras Altas, el bastión de la Hermandad. Está al norte de Strahnbrad.$B$BCon un poco de suerte, encontrarás a los enviados del Despertar de Argus allí.",
    ["O"] = "Mata a 4 magos de la Sombra Argus.$B$BLleva la cabeza de Nagaz al magistrado Maleb de Costasur.",
    ["T"] = "Consejo oscuro",
  },
  [538] = {
    ["D"] = "$N. Si no te molestan los viajes largos, tengo una tarea para ti, para ayudar a llenar la biblioteca de Ventormenta.$B$BEl viejo reino de Alterac cayó tras la Gran Guerra y tenía una gran colección de libros. Es una tragedia que la ciudad esté en ruinas, pero mucho más trágico es haber perdido todo su conocimiento. Debemos recuperar todo lo que podamos.$B$BCostasur es la ciudad más próxima a las Ruinas de Alterac. Vete allí y habla con el maestro cultural Dibbs. Podrá guiarte hasta las ruinas y sabe qué libros puedes encontrar allí.",
    ["O"] = "Habla con el maestro cultural Dibbs en Costasur.",
    ["T"] = "Costasur",
  },
  [539] = {
    ["D"] = "El Registro de Trabalomas indica que la Mina Azur se encuentra bajo el control de la Alianza. Esa mina es una de las principales fuentes de hierro de la armería de la Alianza.$B$BAunque hemos asestado un golpe mortal a Trabalomas, la Alianza sigue defendiendo la Mina Azur con entusiasmo.$B$BEstá al sur de Trabalomas. Ve y asesina al supervisor, un humano llamado Bonds. Elimina también a los mineros. Eso le enseñará a la Alianza.",
    ["O"] = "Mata al supervisor Bonds y 10 mineros de Trabalomas e informa a Darthalia en Molino Tarren.",
    ["T"] = "La batalla de Trabalomas",
  },
  [540] = {
    ["D"] = "Esos bestiales ogros viven en las ruinas de Alterac. Me estremezco al pensar en lo que estarán haciendo con los valiosos libros que quedaron allí. ¡Debes recuperar tantos como puedas!$B$BVe a las Ruinas de Alterac y busca los volúmenes que hayan robado. Tráeme todos los que puedas, pero hay un libro en especial que debes buscar: El Arma de Gri\'lek. En ese libro hay ciencia trol antigua que necesito; en las Ruinas de Alterac había un ejemplar intacto, el último del que se tiene noticia.",
    ["O"] = "Lleva 5 tomos y el libro de piel gastado que contiene El Arma de Gri\'lek al maestro cultural Dibbs a Costasur.",
    ["T"] = "Proteger el conocimiento",
  },
  [541] = {
    ["D"] = "¿Enanos ayudando a los humanos? Malditas sean esas pequeñas y sucias bestias.$B$BPondremos a prueba la solidez de esa supuesta Alianza. Veremos si esos medias piernas están tan ansiosos por ayudar a los humanos cuando hayan sufrido nuestra ira. Sabemos exactamente dónde se encuentra su fortaleza en este territorio.$B$BSe han instalado en un barracón llamado Dun Garok, al sureste de Trabalomas, junto a la Muralla de Thoradin. La misión final de esta batalla es asediar Dun Garok, $C. El capitán Ferrocolina, su líder, debe morir.",
    ["O"] = "Ve a Dun Garok y mata a 8 montaraces, 4 fusileros, 2 sacerdotes y al capitán Ferrocolina e informa a Darthalia en Molino Tarren.",
    ["T"] = "La batalla de Trabalomas",
  },
  [542] = {
    ["D"] = "Voy a dedicar cierto tiempo a estudiar El Arma de Gri\'lek, pero seguro que Milton espera con impaciencia los demás libros para incluirlos en la colección de Ventormenta.$B$BAquí tienes los libros que has traído de las Ruinas de Alterac, ya catalogados y empaquetados. Ve a Ventormenta y dáselos a Milton Gavilla; seguro que te lo agradecerá.",
    ["O"] = "Lleva los Tomos de Alterac a Milton Gavilla a Ventormenta.",
    ["T"] = "Regresa junto a Milton",
  },
  [543] = {
    ["D"] = "La Tiara Perenolde es una antigua reliquia familiar de los \"nobles\" Perenolde, la familia soberana de Alterac antes de que comenzara el declive del reino. Se dice que ha sido forjada por los maestros joyeros de Forjaz y que lleva engarzadas unas esmeraldas de gran calidad.$B$BCuando Alterac fue destruido, se perdió la Tiara, pero corren rumores de que un ogro mago, Grel\'borg el Avaro, encontró la Tiara y ahora vaga por las Ruinas de Alterac en busca de más tesoros.$B$BNos gustaría que recuperases la Tiara para nosotros.",
    ["O"] = "Recupera la Tiara Perenolde.",
    ["T"] = "La Tiara Perenolde",
  },
  [544] = {
    ["D"] = "Había venido a Molino Tarren para estudiar, pero ahora debo solucionar una crisis. Verás, hace unos meses, cuatro Renegados huyeron de Entrañas. Dieron la espalda a su gente, pero lo peor es que robaron a la Dama Oscura.$B$BEntraron en una cámara de seguridad y robaron cuatro objetos que usaron nuestros boticarios en algunos proyectos. Sylvanas quiere recuperar esos objetos.$B$BLos ladrones huyeron de Entrañas a Dalaran y esos magos los pusieron en cuarentena en el Campo de Reclusión de Lordamere.",
    ["O"] = "Localiza a los traidores y recupera los objetos robados. Luego habla otra vez con el magus Vacuamirada en Molino Tarren.",
    ["T"] = "Colarse en la cárcel",
  },
  [545] = {
    ["D"] = "Los magos de Dalaran construyeron una gran cúpula mágica alrededor del corazón de la ciudad. Hay quien dice que fue un medio de protección en estos tiempos violentos, y quien cree que los magos buscaban soledad para estudiar y conspirar.$B$BLa razón no me importa. Lo que me importa es que entre las ruinas de los alrededores de su ciudad puede haber muchos tesoros mágicos.$B$BPero los magos patrullan la zona con sus esclavos elementales.$B$BTienes que eliminarlos. Vuelve cuando hayas reducido sus filas.",
    ["O"] = "Mata a 6 invocadores de Dalaran y 12 esclavos elementales. Luego vuelve a hablar con el magus Vacuamirada en Molino Tarren.",
    ["T"] = "Las patrullas de Dalaran",
  },
  [546] = {
    ["D"] = "Oí que has sido reclutado por Darthalia para luchar contra los humanos de Trabalomas. Estoy celoso.$B$BMientras tú te diviertes matando humanos, saqueando el pueblo y aterrorizando a gente inocente... yo estoy aquí clavado haciendo guardia en el Molino Tarren.$B$B¿No podrías apiadarte de un viejo Guardia de la Muerte como yo? Verás, colecciono calaveras humanas. Y tú pasarás algún tiempo combatiendo contra humanos. Reúne 30 calaveras mientras combates tu larga batalla y tráemelas. Haré que te merezca la pena.",
    ["O"] = "El Guardia de la Muerte Samsa de Molino Tarren quiere 30 calaveras humanas de Trabalomas.",
    ["T"] = "Recuerdos de muerte",
  },
  [547] = {
    ["D"] = "La suma ejecutora Darthalia me envió a vigilar Dun Garok desde cierta distancia. Acampé donde no pudieran verme. A la mañana siguiente fui a buscar leña para el fuego.$B$BY al volver descubrí que me habían robado el equipamiento.$B$BParece que unos exploradores enanos de Dun Garok pasaron por allí y se lo llevaron todo. ¡Necesito la espada! Te agradecería mucho que me la trajeras. Aquí hay tantos enanos que será difícil descubrir al ladrón.",
    ["O"] = "Recoge la espada del Guardia de la Muerte Humbert en Dun Garok y llévasela a Molino Tarren.",
    ["T"] = "Espada de Humbert",
  },
  [548] = "_",
  [549] = {
    ["D"] = "Por orden de la Lady Sylvanas, se decreta la pena de muerte para todos los miembros de la organización conocida como la Hermandad. Aquellos que sean tomados como prisioneros serán ejecutados. Se sabe que se están reuniendo en las ruinas del Castillo de Durnholde, al sureste de Molino Tarren.$B$BSe ofrece una generosa recompensa a quien pueda dar cuenta de sus actividades a la suma ejecutora Darthalia.",
    ["O"] = "Mata a 10 pícaros y 10 veladores de la Hermandad. La suma ejecutora Darthalia te dará tu recompensa en Molino Tarren.",
    ["T"] = "SE BUSCA: Personal de la Hermandad",
  },
  [550] = {
    ["D"] = "He redactado una mención de honor describiendo tu heroica actitud en combate para que el alto mando reconozca tu valor y entrega en la Batalla de Trabalomas.$B$BLleva esta mención lacrada a Bragor Puñosangre; está en Entrañas. Ve con la frente muy alta, $C.",
    ["O"] = "Lleva la mención de honor lacrada de Darthalia a Bragor Puñosangre, que está en Entrañas.",
    ["T"] = "La batalla de Trabalomas",
  },
  [551] = {
    ["D"] = "Este papiro está encantado con un hechizo que lo hace indescifrable. Aunque se puede leer una palabra en la parte de arriba:$B$BNagaz$",
    ["O"] = "Lleva el pergamino encantado al maestro cultural Dibbs a Costasur en las Laderas de Trabalomas.",
    ["T"] = "El papiro encantado",
  },
  [553] = {
    ["D"] = "Toma la Vara de Helcular; la vas a necesitar.$B$BHelcular diseñó tres piras ceremoniales como parte de su hechizo más potente. La Llama de Azel y la Llama de Veraz reposan en las cuevas de la ladera. La Llama de Uzel está en una gruta situada sobre las cuevas, hacia el norte.$B$BDebes cargar la Vara de Helcular en estas tres llamas. Cuando hayas completado el ritual, lleva la vara a la tumba de Helcular en Costasur, la muy bien protegida ciudad humana del sur. ¡Nos vengaremos!",
    ["O"] = "Carga la Vara de Helcular con el poder de la Llama de Azel, la Llama de Veraz y la Llama de Uzel.$B$BLuego llévala a la tumba de Helcular en Costasur.",
    ["T"] = "La venganza de Helcular",
  },
  [554] = {
    ["D"] = "El prospector Pico Tormenta es un maestro de la descodificación. Llévale el papiro que has encontrado; con un poco de suerte, podrá descifrarlo. Está estudiando en la oficina central de prospección, en Forjaz.$B$BDile que has hablado conmigo; últimamente le mando bastante trabajo.",
    ["O"] = "Lleva el papiro codificado al prospector Pico Tormenta a Forjaz.",
    ["T"] = "La descodificación de Pico Tormenta",
  },
  [555] = {
    ["D"] = "¡Lo que más vas a apreciar en Costasur es que tenemos la mejor cocina al norte de Ventormenta!$B$BPor ejemplo, mi sopa de tortuga. Sé de gente que ha venido incluso de Villa Oscura solo para probarla. Por cierto, hace mucho que no la hago. Antes iba pasado Dalaran hasta el Lago Lordamere para cazar quijafortes, pero ahora es demasiado peligroso. ¡Si me traes carne de tortuga de la de los quijafortes del norte te preparo una sopa!",
    ["O"] = "Lleva 10 piezas de carne de tortuga y al chef Jessen a Costasur.",
    ["T"] = "Sopa de tortuga reconfortante",
  },
  [556] = {
    ["D"] = "Los magos de Dalaran tienen una notable facilidad para relacionarse con elementales, que son poderosas criaturas de otras dimensiones. Los magos controlan a los elementales con talismanes mágicos. El mago Vacuamirada quiere analizar estos talismanes.$B$BVe a buscarlos al territorio de los magos de Dalaran, al oeste de Trabalomas.$B$BCualquier ciudadano de Dalaran puede llevar uno, pero te sugiero que busques en el campo de reclusión. Es más seguro que ir a Dalaran.",
    ["O"] = "Lleva 10 muestras de piedra desgastada al vigilante Bel\'varil de Molino Tarren.",
    ["T"] = "Muestras de piedra",
  },
  [557] = {
    ["D"] = "El mago está ocupado estudiando las muestras de piedra que has traído, pero pronto querrá ver sus opuestos: los brazales de vínculo. Cuando un elemental los lleva, queda ligado al servicio del mago.$B$BCaza elementales en las ruinas de Dalaran y recoge sus brazales de vínculo de Tierra para ganarte el favor del mago.",
    ["O"] = "Lleva 4 brazales de vínculo de Tierra al vigilante Bel\'varil de Molino Tarren.",
    ["T"] = "Brazales de vínculo",
  },
  [558] = {
    ["D"] = "Dicen que Lady Jaina Valiente de Theramore es una de las mayores heroínas de la Alianza de todos los tiempos. ¡Cuando sea mayor también seré un héroe de la Alianza!$B$B¿Conoces a Jaina, $N? ¿Me puedes hacer un favor? ¿Me conseguirás su autógrafo? No me atrevo a pedírselo, seguramente estará muy ocupada para alguien como yo... ¡pero tú! ¡Tú eres $Gun aventurero:una aventurera; como ella! ¡Seguro que te dará su autógrafo!$B$B¡¡Por favor!!",
    ["O"] = "Viaja a Theramore para intentar conseguir un autógrafo de Lady Jaina Valiente para tu pupilo.",
    ["T"] = "Autógrafo de Jaina",
  },
  [559] = {
    ["D"] = "¿Qué te parece, $N? A Rutagrana no le basta con todos los múrlocs que hemos matado. Sí, hemos, tú también has tenido algo que ver. Ahora quiere que le traiga pruebas, que me dedique a rebanar cabezas de múrloc para que las inspeccione.",
    ["O"] = "Recoge 10 cabezas de múrloc en la Playa del Oeste por orden del teniente Farren Orinelle de Costasur.",
    ["T"] = "La prueba de Farren",
  },
  [560] = {
    ["D"] = "Verás, $N, a mí esto de las cabezas me pone mal cuerpo... Ya que le has cogido el gusto a la cosa, ¿te importaría ir a llevárselas a Rutagrana por mí?",
    ["O"] = "Entrega las cabezas de múrloc al alguacil Rutagrana de Costasur.",
    ["T"] = "La prueba de Farren",
  },
  [561] = {
    ["D"] = "Informa al teniente Orinelle que debe concentrarse en los nagas que han infestado la Playa del Este. Dile que me informe personalmente del resultado de la operación.",
    ["O"] = "Habla con el teniente Farren Orinelle en Costasur.",
    ["T"] = "La prueba de Farren",
  },
  [562] = {
    ["D"] = "Mi antiguo mentor de la guardia de la Ciudad de Ventormenta me dice que el alto mando de la Alianza está preocupado por la amenaza de los nagas. Según él, quien pueda ayudar a este respecto recibirá un ascenso, o al menos un cambio de destino.$B$BSolo hay un problema... No puedo enfrentarme a los nagas, $N. Puedo intervenir en una pelea de borrachos o reducir a un indigente belicoso, pero los nagas me superan. ¡$N, tienes que ayudarme!",
    ["O"] = "Mata a 10 cazacostas y 10 sirenas Espinadaga por orden del teniente Farren Orinelle de Costasur.",
    ["T"] = "Cambio de destino",
  },
  [564] = {
    ["D"] = "Nada me duele más que perder estos hermosos caballos. Y hemos perdido unos cuantos entre los puñeteros no-muertos, la maldita Hermandad y toda la demás chusma que corre por aquí.$B$BPero te juro por mi abuelo que la amenaza más grave para los caballos son los leones de montaña de Alterac. ¡Siempre bajan de la montaña y me complican la vida!$B$B¿Te apetece una aventura? ¡Pues ve y ocúpate de ellos!",
    ["O"] = "Darren Malavista de Costasur quiere que mates 8 leones de montaña y 10 leones de montaña descomunales.",
    ["T"] = "Una amenaza costosa",
  },
  [565] = {
    ["D"] = "Yo, el gran Bartolo, te haré una de mis capas de piel de yeti, admiradas por toda la Alianza.$B$BTe desafío a recorrer el mundo en busca de una capa mejor. ¿Cómo dices, $R? \"¡Bartolo, no hay una capa mejor!\" Sí, sí. Ya lo había oído antes.$B$BBartolo necesita una madeja de lana, una capa de montañero hecha por un buen peletero, hilo refinado y, naturalmente, piel de yeti. ¡Tráeme todo eso y te haré una capa magnífica!",
    ["O"] = "Lleva una madeja de lana, una capa de montañero, hilo refinado y 10 trozos de piel de yeti a Bartolo Ginsetti a Costasur.",
    ["T"] = "Capa de piel de yeti de Bartolo",
  },
  [567] = {
    ["D"] = "¡Atención!$B$BLos siguientes humanos de Trabalomas son peligrosos. La suma ejecutora Darthalia ha designado una recompensa por su captura:$B$BFuncionario Horracio Corzalbal. Se busca por el asesinato del Guardia de la Muerte Toma.$B$BCiudadano Wilkes. Se busca por el asesinato del boticario Eli.$B$BMinero Hackett. Se busca por el asesinato del mortacechador Fry.$B$BGranjera Kalaba. Se busca por el bloqueo de suministros de Entrañas.$B$BTodos estos enemigos se encuentran fugados y su captura se hará difícil. Se recompensará a quien pueda certificar su muerte.",
    ["O"] = "La suma ejecutora Darthalia de Molino Tarren ofrece recompensa por el funcionario Horraceio Corzalbal, el ciudadano Wilkes, el minero Hackett y la granjera Kalaba.",
    ["T"] = "¡Peligrosos!",
  },
  [568] = {
    ["D"] = "¡Throm\'ka, $C!$B$BLlegas a tiempo a Grom\'gol. Como Comandante del campamento base del Jefe de Guerra aquí, en la jungla, he jurado encargarme de la seguridad de todos los miembros de la Horda. Nuestra misión de proporcionar una cadena de suministros segura hasta Rocal se ha visto obstaculizada por algunos de los habitantes locales.$B$BTe pongo al cargo de disminuir la población de raptores en las afueras de Grom\'gol. Cuando hayas conseguido un avance significativo, vuelve a informarme y te reasignaré.",
    ["O"] = "El comandante Aggro\'gosh del Campamento Grom\'gol, quiere que mates 15 raptores colazote.",
    ["T"] = "La defensa de Grom\'gol",
  },
  [570] = {
    ["D"] = "La Horda tiene que darse cuenta del poder de la magia de la jungla y, después, utilizarlo. Hasta ahora, mis rituales han tenido éxito. Puedo lanzar encantamientos muy poderosos. Para ti, valiente $C, crearé un objeto muy especial.$B$BTráeme algunas garras de panteras Faucesombría junto con un colmillo de tigresa. No valdrá cualquier colmillo. Debe estar en perfecto estado.$B$BLas bestias moran en la jungla, pero encontrarás algunas por aquí cerca, justo al otro lado del río que va desde las Ruinas de Mizjah hasta el sureste.",
    ["O"] = "El clarividente Mok\'thardin del Campamento Grom\'gol, quiere 8 garras Faucesombría y un colmillo de tigresa prístino.",
    ["T"] = "El encantamiento de Mok\'thardin",
  },
  [571] = {
    ["D"] = "Lo que necesito ahora, es bastante difícil de conseguir.$B$BPara guardar las plumas, las zarpas y el colmillo inmaculado, necesito una cadena mágica. Solo servirá un tendón de gorila mayor.$B$BEl único lugar donde podrás encontrar uno es en un gorila Velo de Bruma anciano. Suelen ir a por comida al este de la Bahía del Botín.$B$BEl tendón debe ser perfecto. Quizás necesites usar algunas de tus habilidades para encontrar exactamente lo que necesito, pero merecerá la pena.",
    ["O"] = "Lleva un tendón de gorila mayor al clarividente Mok\'thardin en Grom\'gol.",
    ["T"] = "El encantamiento de Mok\'thardin",
  },
  [572] = {
    ["D"] = "Los objetos que has conseguido de las bestias felinas del Valle son solo el primer requisito para un poderoso objeto encantado. Estas joyas de la selva combinadas con mi ritual chamanístico, te proporcionarán una herramienta muy útil para ayudar a la Horda y al Jefe de Guerra.$B$BPero ahora debes conseguirme Plumas de acechadores de la selva. Tradicionalmente, las bestias llevan las plumas en sus brazaletes. Encontrarás una tribu de raptores directamente al sur de Grom\'gol, no muy lejos de La Gran Arena.",
    ["O"] = "Lleva 10 plumas de acechadores de la selva al clarividente Mok\'thardin en el Campamento Grom\'gol.",
    ["T"] = "El encantamiento de Mok\'thardin",
  },
  [573] = {
    ["D"] = "Necesito un componente más para completar el encantamiento.$B$BAl sur, hacia la costa oeste, por encima de los riscos, hay un manantial sagrado. Este manantial es el sujeto de un gran conflicto entre eruditos religiosos y místicos. Todas las razas de Azeroth afirman que las aguas del manantial se hicieron divinas para su gente.$B$BMi última petición: tráeme agua de manantial sagrado. Ten cuidado. Una expedición de exploradores naga ha avanzado hacia el manantial. Tendrás que encargarte de ellos.",
    ["O"] = "El clarividente Mok\'thardin de Grom\'gol necesita agua de manantial sagrado. También quiere que mates 10 expedicionarios naga.",
    ["T"] = "El encantamiento de Mok\'thardin",
  },
  [574] = {
    ["D"] = "Ahora que hemos conseguido reducir algunas de sus tropas regulares, debes enfrentarte a las tropas de élite. Cuando no están fuera matando a nuestros soldados rebeldes, se retiran a las profundidades de la parte trasera de la Base de Kurzen, a una cueva a la que llaman Las Reservas.$B$BConozco personalmente a muchas de las tropas de Kurzen y sé que son habilidosas, leales y crueles. La tarea que tienes ante ti no es fácil, $N.$B$BCuando las derrotes, informa al teniente Doren, mi oficial en jefe. Quiere recibir tu informe de primera mano.",
    ["O"] = "Mata 10 comandos de Kurzen y 6 reductores de cabezas de Kurzen.$B$BPreséntate ante el teniente Doren en el Asentamiento Rebelde.",
    ["T"] = "Fuerzas especiales",
  },
  [575] = {
    ["D"] = "\"El mejor artesano peletero de los Mares del Sur, del mundo antiguo y del mundo nuevo.\" ¡Así es como me llaman! ¡A mí! ¡Drizzlik! El mejor... ejem. Bueno.$B$BMis objetos de cuero se conocen en el mundo entero como los más exquisitos, ¡la elección de los entendidos!$B$BAcabo de recibir un pedido del director Riddlevox del Gremio de Manitas, de una docena de borceguíes. El producto que más vendemos.$B$BPara el molde básico, necesitaré pieles de crocolisco de los crocoliscos que están en el río en el Norte de la Vega de Tuercespina.",
    ["O"] = "Lleva 2 pieles grandes de crocoliscos fluviales a Drizzlik en la Bahía del Botín.",
    ["T"] = "La oferta y la demanda",
  },
  [576] = {
    ["D"] = "¡Alto! ¿Quién va?$B$BAaah, no soy na sin mi ojo encantao especial. ¡De entrada ya fue bah\'tante malo perdel mi propio ojo en combate! Pero, por un golpe de sueh\'te, atracamo en Menethil y un zahorí viajante encantó un ojo especial de cristal para que pudiera ver de nuevo.$B$BAyel por la noche uno Bucaneros Velasangre me atacaron cuando salía. ¡Y cogieron mi ojo! Parece ser que esos tipos han conseguio llegal a la Bahía del Botín.$B$B¿Quieres ayudal a un viejo lobo de mar a recuperal su ojo?",
    ["O"] = "Dizzy el Tuerto, en la Bahía del Botín, quiere que recuperes su ojo de los Bucaneros Velasangre.",
    ["T"] = "¡Ojo!",
  },
  [580] = {
    ["D"] = "Estábamos ajustando la braza mayor y soplaba fuerte en Los Acantilados Dominantes, veíamos las Tierras del Interior a distancia. Creí que el mar iba a tragarnos enteros.$B$BEl viejo Slim lo sabe todo sobre el coraje líquido. El Capitán tenía una caja de oporto Pupellyverbos que le encargamos a un corsario que iba a Ventormenta.$B$BAsí que cuando cayó la tormenta de las Tierras del Interior, trinqué el grog. Una gran ola y todas las botellas cayeron por la borda. El capitán va a darme una buena paliza si no me ayudas a recuperarlas.",
    ["O"] = "Whiskey el Delgado, en la Bahía del Botín, quiere que le lleves las botellas de oporto Pupellyverbos que perdió.",
    ["T"] = "El grog de Whiskey el Delgado",
  },
  [581] = {
    ["D"] = "Es su fin, sí. El fin de Yenniku, el hijo menor de mi jefe. Como es tradición entre nuestra gente, fue entregao a los trols Gurubashi Trolls de Zul\'Gurub pero, deh\'pués de que mi tribu, los Lanza Negra, pa\'htiese con el orco Thrall, le perdimos el rastro al pobre Yenniku. Me enviaron de la distante Orgrimmar, de vuelta a Tuercespina, para buscá al hijo de mi jefe.$B$BY creo que la tribu Sangrapellejo lo tiene.$B$BSi quieres ayudarme, da caza a los Sangrapellejo. Los encontrarás al norte. Reúne sus colmillos y tráemelos.",
    ["O"] = "Lleva 9 colmillos Sangrapellejo a Nimboya en el Campamento Grom\'gol.",
    ["T"] = "Caza para Yenniku",
  },
  [582] = {
    ["D"] = "Hemos de descubrí si Yenniku eh\'tá muerto.$B$BMata a los rebanacabezas Sangrapellejo y arrebátales sus cabezas reducidas. Tienes que reuní muchas, muchas cabezas y yo las inspeccionaré para vé que la de Yenniku no eh\'té entre ellas.$B$BY, si Yenniku murió a manos de los Sangrapellejo, ¡una maldición de 600 años caerá sobre el eh\'píritu de Gan\'zulah, su jefe!$B$BVenga, $N. Encontrarás muchos rebanacabezas en la Ruinas Zuuldaia y Zul\'Kunda, a lo largo de la costa del norte.",
    ["O"] = "Lleva 20 cabezas reducidas a Nimboya en el Campamento Grom\'gol.",
    ["T"] = "Caza cabezas",
  },
  [583] = {
    ["D"] = "¡$GBienvenido:Bienvenida; a Tuercespina!$B$BPor si no lo sabes, aquel enano de allí es el hijo del mismísimo Hemet Nesingwary, famoso héroe de guerra de la Alianza y maestro de la caza mayor. Igual que a su padre, no le gusta tener a desconocidos en el campamento, pero tú tienes cara de haber guerreado bastante, $C.$B$BHabla con él; ha heredado el talento cazador de su padre, así que podrá darte buenos consejos.",
    ["O"] = "Habla con Hemet Nesingwary Jr.",
    ["T"] = "Bienvenido a la jungla",
  },
  [584] = {
    ["D"] = "Doy gracias a la Gran serpiente de que la cabeza de Yenniku no eh\'tuviese entre las que me has traído. Pero eso no nos facilita el encontrarlo.$B$BHemos de descubrir la verdad y me temo que solo el jefe de los Sangrapellejo y su médico brujo lo saben. Son los enemigos de la tribu Lanza Negra y no van a coloborá, ¡así que tenemos que sacá la verdad de sus labios muertos!$B$BTráeme las cabezas de Gan\'zulah y Nezzliok el Temible y colócalas en esta caldera.$B$BEstán en las profundidades de Zul\'Kunda. Trae sus cabezas... Las haremos hablá.",
    ["O"] = "Lleva la cabeza de Gan\'zulah y la cabeza de Nezzliok a la caldera en ebullición en el Campamento Grom\'gol.",
    ["T"] = "Las cabezas de los Sangrapellejo",
  },
  [585] = {
    ["D"] = "<La cabeza de Nezzliok habla.>$B$BEs cieh\'to. Sé dónde se llevaron al niño de los Lanza Negra. Me has vencío y has sometido mi eh\'píritu, ¡pero no diré nada si no me apaciguas!$B$BLos Sangrapellejo eh\'tán en guerra con los Machacacráneos. Para obtené mi favor, roba 3 cráneos trofeo a los Machacacráneos y colócalos aquí con nosotros, pa\' que mi eh\'píritu tenga esclavos en el Vacío Abisal. Uno de los cráneos debe procedé de su pila de trofeos en Balia\'mah, el otro debe vení de Ziata\'jai y el último de Zul\'Mamwe.",
    ["O"] = "Lleva un trofeo de Balia\'mah, un trofeo de Ziata\'jai y un trofeo de Zul\'Mamwe a la caldera en ebullición del Campamento Grom\'gol.",
    ["T"] = "Hablando con Nezzliok",
  },
  [586] = {
    ["D"] = "<La cabeza de Gan\'zulah habla.>$B$B¡Yo te maldigo! ¡Y maldigo a tus ancestros!$B$BSolo la sangre y la venganza podrán templar mi ira, así que si eso es lo que deseas... Haz esto:$B$Blánzate de pleno al corazón de los Machacacráneos, al este. Mata a los más fieros y, si sobrevives, enfréntate a su jefe, Ana\'thek el Cruel. ¡Ja! Tráeme su armadura despedazada si puedes.$B$BQue te desgarre tus miembros y deje que tu carne se pudra y sea comida de buitres.",
    ["O"] = "Mata 8 cazadores Machacacráneos, 6 rebanacabezas Machacacráneos y 4 rabiosos Machacacráneos.$B$BLleva la armadura de Ana\'thek rota a la caldera en ebullición del Campamento Grom\'gol.",
    ["T"] = "Hablando con Gan\'zulah",
  },
  [587] = {
    ["D"] = "Ah, la vida del mar. No diré que es una buena vida, pero es mi vida.$B$BCuando llego a puet-to me guh\'ta buscal una buena botella de grog y rapé de buena calidad. Unas costumbre muy mala, ¿pero qué es un pirata sin sus vicio, eh?$B$BAsí que te puede imaginal cómo me sentí cuando me enteré de que el herborih\'ta de la ciudad se había ido y lo había vendio to. Parece ser que esos maldito asaltantes Velasangre secuestraron su cargamento.$B$BApuesto a que $Gun audaz:una audaz:c; $C como tú podría convencerlos para compartil las mercancías, ¿eh?",
    ["O"] = "Deeg, en la Bahía del Botín, quiere que le lleves 15 pizcas de tabaco.",
    ["T"] = "Tabaco rapé",
  },
  [588] = {
    ["D"] = "<Nezzliok habla.>$B$BNos has complacio a mí y a mi jefe, así que te contaré aquello que deseas descubrí...$B$BEl joven Yenniku eh\'tá perdio para su clan.$B$BSe lo llevó el médico brujo Zanzil el Desterrado. Ahora Zanzil lo controla en cuerpo y alma, y solo la magia más poderosa podría liberarlo.$B$BUna magia que tú no tienes, $C.$B$BPuede que tus aliados Lanza Negra en Bambala, hacia el este, puedan ofrece\'hte una posibilidad para la salvación de Yenniku. Pero lo dudo.",
    ["O"] = "Habla con Kin\'weelay.",
    ["T"] = "El destino de Yenniku",
  },
  [589] = {
    ["D"] = "Las leyendas hablan de una joya oculta en las profundidades del corazón de Tuercespina. Llamamos a esa joya el Ojo de la mente, y tiene grandes poderes. Si consigues el Ojo, quizás podamos usarlo para traer de la oscuridad al joven Yenniku.$B$BPreguntaré a los espíritus de la jungla cómo encontrar el Ojo de la mente. Mientras lo hago, ve a la Mina de Cristal, al sureste de aquí y recoge fragmentos azul pulsante de los basiliscos Quijahierro. Los fragmentos son poco comunes, pero necesitamos su magia para conseguir dominar el Ojo de la mente.",
    ["O"] = "Lleva 3 fragmentos azul pulsante a Kin\'weelay en el Campamento Grom\'gol.",
    ["T"] = "Los cristales canto",
  },
  [590] = {
    ["D"] = "¿Qué?$B$B¡¿Qué?!$B$B¿Quieres dinero? Yo no te debo dinero.$B$B¿Una carta? ¿Qué carta? Ah, estás buscando pelea. Muy bien, colega. ¡Vamos allá! Que te debo dinero...",
    ["O"] = "Derrota a Calvin Montesco en Camposanto.",
    ["T"] = "Un trato de pícaro",
  },
  [591] = {
    ["D"] = "El Ojo de la mente eh\'tá en posesión de un ogro mago, al este de aquí, en las profundidades del Túmulo Ogro Mosh\'Ogg. Se llama Mai\'Zoth. Le conozco de cuando los Lanza Negra vivían en Tuercespina, es feroz y su magia es poderosa.$B$BY, si tiene el Ojo de la mente, entonces su magia será más fuerte todavía.$B$BDebes enfrentarte a Mai\'Zoth y conseguí el Ojo de la mente. Se trata de una misión peligrosa, pero es la única opo\'htunidad que tenemos de salvá a Yenniku.",
    ["O"] = "Lleva el Ojo de la mente a Kin\'weelay en el Campamento Grom\'gol.",
    ["T"] = "El Ojo de la mente",
  },
  [592] = {
    ["D"] = "He convertido el Ojo de la mente en una gema de alma. Tiene un doble propósito: debes usarla para aturdir a Yenniku y, cuando lo hayas hecho, colócala sobre su cabeza para atrapar su alma.$B$BA continuación, lleva la gema a Nimboya.$B$BYenniku vive con Zanzil el Desterrado, en las Ruinas de Aboraz, junto a La Costa de Cristal, en El Cabo de Tuercespina.",
    ["O"] = "Lleva la gema de alma llena a Nimboya.",
    ["T"] = "Salvar a Yenniku",
  },
  [594] = {
    ["D"] = "La nota dice, con buena caligrafía:$B$BA quien pueda ayudarme.$B$BPor favor, necesito ayuda urgentemente. Un gran Cieloleno me tiene cautivo en una isla en los Mares del Sur.$B$BLas estrellas de la noche son extrañas y los árboles de la jungla oscurecen el firmamento pero, en los días más claros, puedo ver la Orilla Salvaje al noroeste y lo que parecen grandes barcos cargueros al suroeste.$B$BTe lo suplico, ven a rescatarme.",
    ["O"] = "Encuentra al autor del mensaje en la botella.",
    ["T"] = "Mensaje en una botella",
  },
  [595] = {
    ["D"] = "Parece que tienes muchas ganas de ponerte a trabajar, colega. No tengo muy claro por qué $Gun:una; $R $C $Gmarinero:marinera; de agua dulce, se ofrece a ayudar a los Asaltantes Aguasnegras, pero no voy a rechazar tu ayuda.$B$BNuestro barco acaba de atracar aquí, en la Bahía del Botín, y corren rumores de que nuestros rivales, esos malditos y asquerosos Bucaneros Velasangre, están por todo el sur de Tuercespina.$B$BTodos los hombres están muy ocupados dándole al grog y buscando damiselas. Lo que necesitamos es un explorador.$B$BExplora la cala que está justo al norte de la Bahía del Botín en La Costa Salvaje, a ver lo que descubres.",
    ["O"] = "El contramaestre Crazz quiere que eches un vistazo a la cala norte de la Bahía del Botín.",
    ["T"] = "Bucaneros Velasangre",
  },
  [597] = {
    ["D"] = "En el mapa, la Bahía del Botín aparece marcada con una \"X\" roja, dibujada exactamente en el lugar donde estás ahora.$B$BEl texto lleno de garabatos dice:$B$B\"Debéis asegurar un campamento en esta ubicación y mantener la calma. Esperad la señal del puesto de mando del sur antes de atacar. Si alguien se aproxima, matadle.\"",
    ["O"] = "Informa sobre tus descubrimientos al contramaestre Crazz en la Bahía del Botín.",
    ["T"] = "Bucaneros Velasangre",
  },
  [598] = {
    ["D"] = "Ahora que has conseguido traerme trofeos de los Sangrapellejos, tengo otra misión para ti. La tribu de trols Machacacráneos vive al sureste y, durante años, hemos luchado con ellos. Si bien los Lanza Negra ya no estamos asentados en Tuercespina, estamos atados a nuestro conflicto con los Machacacráneos.$B$BVete a por ellos. Consigue sus collares de hueso partidos y vuelve a verme. Ayúdanos en nuestra guerra y conseguirás la amistad de los Lanza Negra.",
    ["O"] = "Lleva 25 collares de hueso partidos a Kin\'weelay en el Campamento Grom\'gol.",
    ["T"] = "Collar de hueso partido",
  },
  [602] = {
    ["D"] = "Por lo que he oído, los magos de Dalaran han conseguido reconstruir su mágica ciudad. La han movido a Rasganorte y han dejado un cráter enorme. Sin embargo, algunos magos se han quedado aquí, seguro que alguno tiene algo de tiempo para echar un vistazo a los brazales. Si algo sé de los magos es que no pueden resistirse a un rompecabezas mágico.$B$BCoge la bolsa de brazales de elemental de agua y veamos si nos pueden ayudar con lo de la maldición de la Piedra. Busca al archimago Ansirem Tejerruna. Ya he tratado antes con él y es muy sabio.",
    ["O"] = "Lleva los brazales de elementales de agua al archimago Ansirem Tejerruna en el Cráter de Dalaran.",
    ["T"] = "Análisis mágico",
  },
  [604] = {
    ["D"] = "Crazz habla muy bien de tu entrega a los Asaltantes Aguasnegras. Voy a confiarte una misión de vital importancia para la flota.$B$BInfíltrate en el puesto de mando del sur que los Bucaneros Velasangre han establecido en la Orilla Salvaje. Asalta el campamento. Tráeme sus cartas de navegación y las órdenes escritas. Veremos exactamente qué pretenden hacer esas babosas.",
    ["O"] = "El maestro de flota Cuernomarino en la Bahía del Botín quiere que mates 10 espadachines Velasangre y le lleves las cartas de navegación y las órdenes de los Velasangre.",
    ["T"] = "Bucaneros Velasangre",
  },
  [606] = {
    ["D"] = "Si hay algo que no soporto, ¡es la gente que no paga sus deudas de juego! Y hay muchos que me deben dinero. ¿Crees que podrías ayudarme?$B$BPrimero, está Phillipe el \"Trémulo\", ahí, en el muelle. Me debe mucho después de la timba de la semana pasada, ¡y quiero lo que me corresponde! Pero para conseguirlo, tendremos que asustarle.$B$BSal de la ciudad y caza gorilas Velo de Bruma ancianos. Encuentra una bonita selección de menudillos y llévaselos al Trémulo. No le digas que son de un animal. Deja que piense lo peor.",
    ["O"] = "Lleva 5 menudillos de Velo de Bruma a Phillipe el \"Trémulo\" en la Bahía del Botín.",
    ["T"] = "Asusta al Trémulo",
  },
  [607] = {
    ["D"] = "Toma, coge esta bolsa y dásela a Lobo de Mar. ¡Y dile que le iba a pagar! ¡¡Lo juro!!$B$BSolo que me emborraché un poco y me perdí... ya sabes cómo son estas cosas. Lo entenderá, ¡no hace falta que te pongas así!",
    ["O"] = "Lleva el pago del Trémulo a \"Lobo de Mar\" MacKinley.",
    ["T"] = "Regresa junto a MacKinley",
  },
  [608] = {
    ["D"] = "¡Maldito sea Firallon! No podría navegar un esquife por el Lago de Cristal y mucho menos liderar toda una flota contra nosotros.$B$BLos Bucaneros Velasangre pagarán su descuido. Ahora que sabemos su plan de ataque, podemos atacar y tomarles por sorpresa.$B$B¿Pero por qué enviar a toda la flota para hacer el trabajo de un grupo de aventureros? $N, coge a algunos de tus camaradas más capaces, id al lugar de atraque de Firallon y asesinad al líder Velasangre. Su flota se hundirá poco después...",
    ["O"] = "El maestro de flota Cuernomarino en la Bahía del Botín quiere que mates al capitán Aguasmansas, al capitán Sinquilla y al maestro de flota Firallon.",
    ["T"] = "Bucaneros Velasangre",
  },
  [609] = {
    ["D"] = "La lista empieza a menguar, pero todavía hay muchos que me deben dinero.$B$BA continuación tenemos a Maury \"Patapalo\" Wilkins, Jon-Jon el Cuervo, y Chucky \"Diez Pulgares\".$B$BLas deudas de esos malditos perros son de varios meses y creí que se habían largado de la ciudad para evitar pagarme. Pero después me enteré de que están malditos y hechizados y se dedican a vagar por las ruinas de la jungla. Pero no me importa su destino, ¡quiero lo que me pertenece!$B$BEstán en las Ruinas de Aboraz y en las Ruinas de Jubuwal, al noreste de aquí. Encuéntralos y consigue mi dinero.",
    ["O"] = "Lleva la pata de palo de Maury, el catalejo dorado de Jon-Jon y el anillo enorme de Chucky a \"Lobo de Mar\" MacKinley en la Bahía del Botín.",
    ["T"] = "El vudú debido",
  },
  [610] = {
    ["D"] = "¿Mi PADRE te ha enviado? Pensaba que ese viejo chocho ya habría dejado de intentar encontrarme.$B$BRealmente no me escapé por su culpa, es solo que él no debería haber supuesto que aceptaría tener una vida aburrida, leyendo libros polvorientos y tratando a magos canosos, estirados y acartonados.$B$BSi necesitas mi daga, tendrás que recuperarla, la tiene \"el Guapo\" Duncan, uno de los Bucaneros Velasangre. Pon algo entre sus hombros y seguro que te la da.",
    ["O"] = "Consigue la daga de Catelyn de \"El Guapo\" Duncan.",
    ["T"] = "\"El Guapo\" Duncan",
  },
  [613] = {
    ["D"] = "¡Maldición! Ese aq-queroso de Maury ha pueh\'to un candao en su pata, ¡y no consigo abril-la! Guarda el botín en el pie, ¡y es justo lo que me debe!$B$BTenemos que encontral la llave que abre el candao. Todos saben que Maury apostaba con los ogro Mosh\'Ogg, en su túmulo, al noreste. Y me dijo, cuando todavía eh\'taba vivo, que la última vez que fue al túmulo, acabó siendo perseguido porque había ganao mucho. Quizás los ogros de allí tengan su llave.$B$BSi es así, probablemente tengas que matal a muchos de ellos pa encontral al que la tiene...",
    ["O"] = "Lleva la llave de Maury a \"Lobo de Mar\" Mackinley en la Bahía del Botín.",
    ["T"] = "Abrir la pata de Maury",
  },
  [617] = {
    ["D"] = "¿Andas buscando trabajo, $N? Quizás tenga algo pa ti si t\'apetece un pequeño desafío.$B$BTengo un amigo llamado Groy que se está forrando vendiendo lo que los nagas llaman junco akiris: es un tipo de valioso junco del océano. No tengo muy claro pa qué sirve, pero paga bien por fajos de esa cosa.$B$BTráeme diez fajos de eso y haremos negocios. La última vez que vimos a los nagas fue en la playa que está al norte de aquí.",
    ["O"] = "Consigue 10 fajos de junco akiris para el corsario Bloads en Bahía del Botín.",
    ["T"] = "Tallos de juncos akiris",
  },
  [620] = {
    ["D"] = "Esta preciosa banda tiene bordadas las letras \"HS\" en el dobladillo.",
    ["O"] = "Devuelve la banda con monograma a su propietario.",
    ["T"] = "La banda con monograma",
  },
  [621] = {
    ["D"] = "Zanzil el Desterrado vive con sus seguidores en las Ruinas de Jubuwal y Aboraz, al noreste de la Bahía del Botín. Está exiliado de su tribu. ¿Por qué? Bueno...$B$BZanzil sabe cómo someter la mente de otros y sus líderes le temen. Utiliza una mezcla de plantas de la jungla con sus seguidores para eliminar su voluntad y fortalecer sus cuerpos.$B$BEsa mezcla sería muy valiosa para aquellos que deseen controlar a otros.$B$BTráeme muestras de esa mezcla y seguro que encuentro un buen uso.",
    ["O"] = "Lleva 12 muestras de la mezcla de Zanzil a Loquillo Pinchabujas en la Bahía del Botín.",
    ["T"] = "El secreto de Zanzil",
  },
  [622] = {
    ["D"] = "Mientras no estabas arreglé la olla. Incluso añadí un pitorro de vapor extra, ¡y mira! Hay una nueva hoja en el fondo que puede cortar incluso huesos de piernas.$B$BSí, sí, la olla mecánica de Krazek, es una herramienta que satisface al cocinero en un 100%. Seguro que su dueño estará satisfecho.",
    ["O"] = "Lleva la olla mecánica de Krazek arreglada al cabo Kaleb en el Asentamiento Rebelde.",
    ["T"] = "Regresa junto al cabo Kaleb",
  },
  [623] = {
    ["D"] = "$N, ya que tás aquí, he recibío un mensaje de Groy cuando no estabas. Dijo que estaba satisfecho con el último cargamento y que tiene un cliente que quiere más de esa cosa ahora mismo.$B$BYo no voy a Theramore hasta dentro de una semana, pero si tú quieres hacer el viaje, podrías llevar los juncos por mí.$B$B¿Te apetece una aventura? Sabes dónde está el Marjal Revolcafango, ¿no? Yo iría con cuidado, Theramore puede ser peligroso dependiendo de quién seas...",
    ["O"] = "Lleva un fajo de juncos akiris al corsario Groy, en Theramore.",
    ["T"] = "Tallos de juncos akiris",
  },
  [624] = {
    ["D"] = "Hay un puente ornamentado y con mucho tránsito$BAl este de Viento Muerto y al sur de Crestagrana$BBajo el puente te espera algo$BAlgunos lo llaman un consejo otros, una pista$BAllí te esperará eternamente$BViendo pasar el tiempo en el Pantano de las Penas",
    ["O"] = "¡Resuelve el enigma!",
    ["T"] = "El enigma de Cortello",
  },
  [625] = {
    ["D"] = "Te espera una tierra muy lejana, en Kalimdor$BDonde el peligro ha destruido las esperanzas y los destinos$BAl este de Los Baldíos el tiempo es severo$BLa penumbra y el peligro imperan en el Marjal Revolcafango$BPero allí hay una pista, para aquellos dispuestos a aceptar el desafío$BEsperando ser encontrada... en una cueva... al suroeste",
    ["O"] = "¡Resuelve el enigma!",
    ["T"] = "El enigma de Cortello",
  },
  [626] = {
    ["D"] = "¡A Lordaeron! La gran tierra de confusión$BDonde valientes soldados juegan con la muerte$BEncuentra tu camino a través del gran disturbio$BAllí encontrarás el final de esta misión$BJunto a la costa de las Tierras del Interior, en la base de las cataratas$BDesde las profundidades del agua, te llama tu tesoro.",
    ["O"] = "¡Resuelve el enigma!",
    ["T"] = "El enigma de Cortello",
  },
  [627] = {
    ["D"] = "Hay una cueva en la montaña, en el Cañón Mostacho Seco, al norte de las Tierras Altas de Arathi. En esa cueva hay un extraño mineral: sangrita. La sangrita de esta cueva no tiene una calidad superior, pero incluso la sangrita de baja calidad es valiosa... para aquellos que sepan usarla.$B$BMe gustaría conseguir sangrita.$B$BArreglaré tu olla mecánica, pero mientras lo hago... tráeme algunas muestras de sangrita. Si me traes las suficientes, te daré una bonificación.$B$BOh, y si no tienes la habilidad necesaria para extraerla... ¡encuentra a alguien que la tenga!",
    ["O"] = "Lleva 4 fajos de mena de sangrita inferior a Krazek en la Bahía del Botín.",
    ["T"] = "Un favor a Krazek",
  },
  [628] = {
    ["D"] = "Ahora que hemos terminado el interior, lo único que queda es añadir la capa exterior. Para esto, solo utilizo la mejor piel de crocolisco mayor. Duran más y son un lienzo perfecto para añadir los diseños y dibujos que completan el estilo borceguí de Drizzlik.$B$BPero no es fácil encontrar la piel perfecta, la única que podría servir es la piel de un crocolisco marino mayor. Los crocoliscos mayores solo salen fuera para defender a los otros crocoliscos marinos, así que quizás tengas que matar a unos cuantos para que salgan.",
    ["O"] = "Lleva una piel de crocolisco mayor a Drizzlik en la Bahía del Botín.",
    ["T"] = "Borceguí",
  },
  [629] = {
    ["D"] = "Mucho tiempo atrás, un gran temblor de tierra sumergió una antigua ciudad trol bajo las aguas de La Costa Salvaje, al noroeste. Llamamos a ese lugar El Arrecife Mortal, ya que ahora los múrlocs residen en las ruinas de la ciudad, atacando a todo el que se acerca.$B$BEn esas ruinas hay una antigua tablilla que narra la vieja fábula de Gri\'lek, un héroe de leyendas trol. La fábula es sagrada para la tribu Lanza Negra y, aunque la tablilla es muy grande como para moverla, quiero un fragmento para consagrarlo en nuestro nuevo hogar de Orgrimmar.",
    ["O"] = "Lleva un fragmento de tablilla a Kin\'weelay en el Campamento Grom\'gol.",
    ["T"] = "El Arrecife Mortal",
  },
  [632] = {
    ["D"] = "Parece que el pobre Herrumba cayó en una emboscada de los Hierro Negro. Descubres un papiro junto a su puño cerrado.$B$BLleva la desalentadora noticia a Rhag Garmason y enséñale lo que has descubierto.",
    ["O"] = "Vuelve a informar a Rhag Garmason en Dun Modr, en Los Humedales, y enséñale el papiro que encontraste en el cadáver de Herrumbra.",
    ["T"] = "El Puente Thandol",
  },
  [633] = {
    ["D"] = "¡¡¡Cielos!!! ¡¡¡No!!! Pobre Herrumbra. Es una tragedia.$B$BNo debemos permitir que su muerte haya sido en vano. El pergamino que has descubierto indica que Kam Furiahonda, el cerebro del ataque, ordenó atesorar un alijo de explosivos al otro lado del Puente Thandol, en las Tierras Altas de Arathi.$B$B¡Si esos explosivos llegan al puente, nuestra principal vía de suministros recibirá un duro golpe! ¡Hay que destruir el alijo, $N! Vuelve a verme cuando hayas terminado tu misión.$B$BEres nuestra única esperanza.",
    ["O"] = "Destruye el cajón de explosivos.",
    ["T"] = "El Puente Thandol",
  },
  [634] = {
    ["D"] = "Voy a pedirte otro pequeño favor, $C. Estamos esperando las reservas de Forjaz. Pero Roggo tiene noticias de un campamento de la Alianza al norte de las Tierras Altas de Arathi, en el Refugio de la Zaga.$B$BViaja hasta allí y busca a la capitana Nials. Pregunta a la buena capitana si la Alianza puede ofrecernos algunos soldados que nos ayuden a recuperar Dun Modr y el Puente Thandol.",
    ["O"] = "Busca a la capitana Nials en el Refugio de la Zaga, en las Tierras Altas de Arathi.",
    ["T"] = "Petición a la Alianza",
  },
  [635] = {
    ["D"] = "El fragmento palpita cuando lo agarras...$B$BSientes una especie de súplica saliendo del fragmento, como si alguien estuviera intentando comunicarse contigo, alguien que necesita tu ayuda desesperadamente.$B$BUna visión llena tu mente... son las montañas septentrionales de Arathi, y ves también un cristal brillante sobresaliendo del suelo.$B$BTe sientes $Gatraído:atraída; hacia ese lugar.",
    ["O"] = "Encuentra el cristal de tu visión.",
    ["T"] = "Cristal en las montañas",
  },
  [636] = {
    ["D"] = "El libro cuenta la historia de una princesa elemental y de su cautiverio en las profundidades de las Tierras Altas de Arathi.$B$BTambién habla de un afloramiento secreto de cristales en las Tierras Altas. Estos cristales, los Fragmentos de Myzrael, son una ventana a la prisión de la princesa.$B$BSi un alma valiente encontrara los Fragmentos de Myzrael, tal vez pudiera hablar con ella...",
    ["O"] = "Encuentra los Fragmentos de Myzrael.",
    ["T"] = "Leyendas de la tierra <NYI>",
  },
  [637] = {
    ["D"] = "El sobre empapado y desgastado se deshace entre tus manos, revelando varias páginas de una carta escrita con una cuidada caligrafía.$B$BLa carta está dirigida a Sara Balloo de Forjaz y comienza así: \"Querida Sara...\"",
    ["O"] = "Entrégale la carta de Sully Balloo a Sara Balloo.",
    ["T"] = "Carta de Sully Balloo",
  },
  [639] = {
    ["D"] = "En tiempos ancestrales, los trols lucharon contra el Imperio Arathoriano y contra nuestro más temible adversario, Lord Ignaeus de Strom, al que llamaban Aterratrols. Acudía a la batalla empuñando a Trol\'kalar, verdugo de trols en su antigua lengua.$B$BPero ahora la estirpe de Aterratrols se ha debilitado, su bastión está en ruinas.$B$BLa espada está protegida por varios sigilos que se dividieron entre los supervivientes de Stromgarde, debemos recuperarlos.$B$BLa Hermandad se llevó el primero, lo custodia uno de sus hombres en Stromgarde.",
    ["O"] = "Consigue el sigilo de Strom para Zengu en Sentencia.",
    ["T"] = "Sigilo de Strom",
  },
  [640] = {
    ["D"] = "La tumba de Thoras Aterratrols está protegida por tres sigilos, el primero de los cuales recuperaste de la Hermandad. Será más difícil conseguir los otros dos ya que la milicia de Stromgarde los tiene firmemente atrincherados.$B$BEl hijo de Thoras, Galen, dividió el sigilo de Thoradin en cinco fragmentos que dispersaría entre los defensores de Stromgarde. Recupera todos los fragmentos y llévaselos a Tor\'gan, el chamán trol, será capaz de recomponer con ellos el sigilo completo.",
    ["O"] = "Recupera los 5 trozos de sigilo de los defensores de Stromgarde y llévaselos a Tor\'gan en Sentencia.",
    ["T"] = "El sigilo roto",
  },
  [641] = {
    ["D"] = "La reconstrucción ha funcionado perfectamente. Ahora se puede usar el sigilo para desbloquear el sello de la tumba de Aterratrols. Devuélveselo a Zengu, seguro que tiene más tareas para ti.",
    ["O"] = "Llévale el sigilo de Thoradin restaurado a Zengu en Sentencia.",
    ["T"] = "Sigilo de Thoradin",
  },
  [642] = {
    ["D"] = "Me llamo Myzrael. Soy una princesa de la tierra, y mis captores, los gigantes, me han encerrado en el subsuelo de las Tierras Altas de Arathi. Estos fragmentos de cristal son mi único medio para hablar con el mundo de la superficie.$B$BPor favor, ayúdame. Los aliados de los gigantes, los kóbolds Mostacho Seco, tienen un fragmento como este en el Cañón Mostacho Seco, hacia el este. Para que ese pedazo cobre poder, debes conseguir motas de Myzrael de los kóbolds y aplicárselas.$B$BTe lo suplico, $N, ¡ayúdame!",
    ["O"] = "Recolecta 12 motas de Myzrael y llévalas a los fragmentos iridiscentes en el Cañón Mostacho Seco.",
    ["T"] = "La princesa encerrada",
  },
  [643] = {
    ["D"] = "Aunque apenas pueda afirmarse que controlan Stromgarde, y no digamos las Tierras Altas de Arathi, el príncipe humano lleva los restos de su caballería por el campo. No logro entender por qué lo hace, aunque ¿quién entiende a los humanos?$B$BSean cuales sean sus motivos, le ha dado uno de los sigilos al teniente Valeroso, el jefe de su caballería. Encuéntralo y tráeme el sigilo.",
    ["O"] = "Recupera el sigilo de Arathor y llévaselo a Zengu en Sentencia.",
    ["T"] = "Sigilo de Arathor",
  },
  [644] = {
    ["D"] = "El último sigilo necesario para desbloquear la tumba de Thoras Aterratrols está en manos de su propio hijo. Mora en lo más recóndito de Stromgarde, en una capilla en los distritos de la milicia. El príncipe Galen está bien protegido y es él mismo un guerrero respetable, así que esta tarea podría resultar de lo más difícil.",
    ["O"] = "Mata al príncipe Galen Aterratrols y llévale el sigilo de Aterratrols a Zengu en Sentencia.",
    ["T"] = "Sigilo de Aterratrols",
  },
  [647] = {
    ["D"] = "¡Anoche tomé un poco de licor de luna! Largatrenza me mataría si supiera que perdí el sentido mientras estaba de guardia.$B$BEsa bebida sí que es fuerte... Sentía como si una brigada entera de máquinas de asedio me hubiera pasado por encima.$B$B¡Oh, mira qué hora es! Le prometí al maestro cervecero Bilger de Costasur que saldaría mi deuda con él enviándole licor de luna.$B$BEl problema es que solo dispongo de 15 minutos para ello. ¿Te importaría llevárselo tú? ¡Date prisa!",
    ["O"] = "Llévale el licor de luna de MacKreel al maestro cervecero Bilger de Costasur.",
    ["T"] = "Licor de MacKreel",
  },
  [648] = {
    ["D"] = "Vuelves a oír la voz del gnomo que sale del robot:$B$B\"Tengo que llevar a OOX-17/TN a un lugar abierto y seguro en el que pueda iniciar el largo proceso de despegue. Tiene un dispositivo de invisibilidad integrado, pero necesito tiempo para que vuelva a estar operativo. Escolta al robot hasta, digamos, cerca de las Cavernas del Tiempo. Allí podré tomarme mi tiempo para hacer que todo vuelva a funcionar correctamente.\"$B$B\"¡Escóltalo para que llegue sano y salvo y después ven a verme a Bahía del Botín! Oglethorpe Obnoticus. ¡Corto y cierro!\"",
    ["O"] = "Escolta a OOX-17/TN para que llegue sano y salvo y después preséntate ante Oglethorpe Obnoticus en Bahía del Botín.",
    ["T"] = "¡Rescatar a OOX-17/TN!",
  },
  [649] = {
    ["D"] = "Tengo un trabajito de rescate para ti, y si te gusta el buen calimocho, seguro que lo aceptas. ¿Que qué es?$B$BPues una bebida, ¿qué va a ser?$B$BHay un cargamento de calimocho dulce de las Tierras del Interior en una avanzada abandonada de la Horda. ¡Ya no se hace un calimocho como ese! Uno de esos atontados con los que trabaja Malton está interesado, así que tráeme una caja de calimocho para él y un par para mí. Habla con Malton para que te dé los detalles.",
    ["O"] = "Habla con Malton Droffers en Orgrimmar.",
    ["T"] = "Recupera el calimocho",
  },
  [650] = {
    ["D"] = "El elfo con el que necesitas hablar se llama Gilveradin Cazasol. ¡Puede identificar cualquier vino, con cosecha y todo, con solo olerlo! Él puede embotellar el calimocho que necesitamos, así que tendrás que hablar con él. Necesitamos seis botellas en total: dos para papá y todas las que él quiera y, créeme, querrá alguna que otra. Cuando hayas embotellado el calimocho y tengas las dos botellas de papá, vuelve aquí.$B$BEl campamento de Gilveradin está por las Tierras del Interior, cerca de la casa de un elfo noble... y esos elfos nobles no son buena gente. Créeme.",
    ["O"] = "Habla con Gilveradin Cazasol; su campamento está en las Tierras del Interior.",
    ["T"] = "Recupera el calimocho",
  },
  [651] = {
    ["D"] = "Hay cuatro Piedras de Vínculo diseminadas por todo Arathi. Esas piedras son las que me mantienen anclada bajo la tierra.$B$BPara desbloquearlas tienes que desbloquear primero la más fuerte: la Piedra de Vínculo interior. Y para hacerlo debes reunir las llaves de las otras tres.$B$BLas piedras están custodiadas por guardias pero, con un poco de astucia o suerte, encontrarás la forma de sortearlos.",
    ["O"] = "Reúne la llave ardiente, la llave blasonada y la llave del trueno de la Piedra de Vínculo Occidental, la Piedra de Vínculo Este y la Piedra de Vínculo Exterior.$B$BLlévalas a la Piedra de Vínculo Interior.",
    ["T"] = "Piedras de Vínculo",
  },
  [652] = {
    ["D"] = "Desbloquear las Piedras de Vínculo me liberó en gran parte de mi confinamiento. Pero aún queda una cerradura por abrir. Es la piedra angular que ves en ese círculo de piedras. Y la llave la tiene uno de mis captores, Fozruk, el guardián de las piedras.$B$BDebes conseguir esa llave. Los guardianes de las piedras la llaman la vara del Orden. Arrebátasela a Fozruk y úsala para abrir la piedra angular.$B$B$N, me has ayudado mucho. Pero necesito un poco más de ayuda... ¡Mi gratitud hará temblar los mismísimos pilares de la tierra!",
    ["O"] = "Encuentra a Fozruk y mátalo. Lleva la vara del Orden a la piedra angular en las Tierras Altas de Arathi.",
    ["T"] = "Desbloquear la piedra angular",
  },
  [653] = {
    ["D"] = "<Myzrael habla.>$B$B¡Libre! ¡SOY LIBRE! Libre para recuperar la fuerza, sin que me vean mis captores. Porque si me enfrentara a ellos ahora, seguro que me vencerían y me volverían a encerrar.$B$BPero con el tiempo haré frente a los gigantes ¡y se arrepentirán de haber sido mis guardias!$B$BEres $Gun:una; noble $Galiado:aliada;, $N. Dentro de algún tiempo volveré a necesitarte. Cuando esté listo, necesitaré que me conjures para devolverme a la superficie.$B$BHabla con Gerrig Agarrahueso. Se encuentra en Forjaz, en una tienda de La Caverna Abandonada. Él sabe cómo realizar el conjuro.",
    ["O"] = "Habla con Gerrig Agarrahueso en La Caverna Abandonada de Forjaz.",
    ["T"] = "Aliados de Myzrael",
  },
  [654] = {
    ["D"] = "Al activar la fuente de energía el equipo de prueba de campo empieza a brillar y a vibrar. Parece una señal de que todo funciona correctamente.$B$BLo enviaré de inmediato.$B$BDe acuerdo con el ingeniero jefe Pasaquillas, a partir de ahora tienes algo de tiempo para recoger los datos necesarios en el desierto de Tanaris.$B$BVuelve a hablar con él cuando hayas concluido tu tarea.",
    ["O"] = "Consigue muestras no estudiadas de 8 basiliscos, 8 hienas y 8 escorpiones. Devuélvele el equipo de estudio al ingeniero jefe Pasaquillas, en Gadgetzan, antes de que se agote la fuente de energía.",
    ["T"] = "Recogida de muestras en Tanaris",
  },
  [655] = {
    ["D"] = "Lo recuerdo como si fuera ayer... Tantos años sin esperanza. Con la madera y el acero ligándome a este terrible lugar. ¡Y por fin, por fin! Ahí estaba el Jefe. Martillo Maldito, con sus agoreras placas y su martillo de guerra en alto.$B$BFue terrible... Que el golpe asestado por la mano de un cobarde acabara con nuestro héroe más admirado. Cargó por detrás con su caballo y lo atravesó con su lanza... Yo caí de rodillas. ¿Qué esperanza nos quedaba? ¿Qué esperanza nos queda...?",
    ["O"] = "Habla con Tor\'gan en Sentencia.",
    ["T"] = "Sentencia",
  },
  [656] = {
    ["D"] = "Debes derrotar a Myzrael antes de que se vuelva más fuerte. Si no lo haces, en cuanto tenga suficientes poderes ¡desafiará a sus captores! Hay quien piensa que puede controlarla, pero si así fuera, ¡¿por qué la encadenaron los gigantes?!$B$B¿A que no tiene ni pies ni cabeza?$B$BPara derrotarla, conjúrala en los fragmentos de Myzrael donde hablaste con ella la primera vez. Derrótala, coge sus grilletes tétricos y pónselos a los fragmentos de Myzrael.$B$BHazlo y volverá a estar presa.",
    ["O"] = "Ve a los fragmentos de Myzrael, invócala y derrótala.$B$BRecolecta sus grilletes de Eldritch y pónselos a los fragmentos de Myzrael.",
    ["T"] = "Invocar a la princesa",
  },
  [657] = {
    ["D"] = "<Quae empieza a examinar la carpeta.>$B$BEsto SÍ que es interesante. Al parecer, el boticario Jorell ha estado trabajando en unos experimentos letales aquí en Go\'Shek. No estoy seguro de cuál es la relación con Trabalomas pero a Phin no le gustaría que dejásemos que siga adelante.$B$BTuvimos un roce con unos orcos cuando no estabas, por lo que no estoy en condiciones de ayudar a Kinelory. Si te apetece, va a necesitar que la protejan cuando vaya a la Granja a robar lo que sea que Jorell aún no haya enviado a Molino Tarren.",
    ["O"] = "Habla con Kinelory cerca de la Granja Go\'shek, en las Tierras Altas de Arathi.",
    ["T"] = "Sospechas de una nueva Peste",
  },
  [658] = {
    ["D"] = "¿Así que Phin te ha mandado ver qué hacemos? No me sorprende. Se preocupa demasiado.$B$BHemos vigilado la granja durante días y Kin dice que hace poco ha visto a un mensajero saliendo de esa casita que hay hacia el sur.$B$BHabla mucho, pero me fío de su instinto cuando estamos en sitios peligrosos.$B$B¿No sabrás seguir rastros, por un casual? Creemos que el mensajero podría llevar notas al Molino Tarren. Si pudiéramos echar el guante a esos documentos... Seguro que nos ayudan a entender qué está pasando en Trabalomas.",
    ["O"] = "Encuentra a la mensajera Renegada y lleva su carpeta sellada a Quae cerca de la Granja Go\'shek.",
    ["T"] = "Sospechas de una nueva Peste",
  },
  [659] = {
    ["D"] = "Saludos. Te doy la bienvenida, $N. Dirijo un grupo de aventureros que investigan sucesos extraños que han tenido lugar en esta zona.$B$BMi primo de Trabalomas ha tenido que sacrificar a su perro Stanley porque el animal creció místicamente hasta alcanzar un gran tamaño y empezó a atacar a todo el que se le acercara.$B$BCreo que los no-muertos están involucrados en eso y estamos buscando indicios que lo prueben.$B$BDos compañeras mías, Quae y Kinelory, fueron a la Granja Go\'shek en las Tierras Altas de Arathi y hace días que no tenemos noticias suyas. ¿Podrías encontrarlas por mí? Empiezo a preocuparme de verdad.",
    ["O"] = "Encuentra a Quae y a Kinelory cerca de la Granja Go\'shek en las Tierras Altas de Arathi.",
    ["T"] = "Sospechas de una nueva Peste",
  },
  [660] = {
    ["D"] = "Muy bien, $N. Si tú estás $Glisto:lista;, yo también.$B$B¿Vamos?",
    ["O"] = "Protege a Kinelory hasta que haya regresado con Quae.",
    ["T"] = "Sospechas de una nueva Peste",
  },
  [661] = {
    ["D"] = "He visto a Kinelory con mucho peor aspecto después de una noche en la taberna que ahora. No hay duda de que tu protección fue de gran ayuda.$B$BCon los objetos que trajo de la Granja, Phin debería hacerse una idea de lo que está ocurriendo en Arathi. Solo para asegurarnos de que no hay represalias después de vuestra incursión, nos quedaremos aquí un poco más. ¿Te gustaría volver a Costasur y hablar con Phin?",
    ["O"] = "Encuentra a Phin Odelic en Costasur, en las Laderas de Trabalomas.",
    ["T"] = "Sospechas de una nueva Peste",
  },
  [662] = {
    ["D"] = "O\'Breen guió a las naves hasta la cala. Estábamos seguros de que ahí encontraríamos el tesoro. El Heraldo atracó sin novedad pero cuando El Capricho y el Espíritu de Argénteos se acercaban, la marea empezó a bajar. ¡Lo más extraño que haya visto nunca!$B$BLas dos naves encallaron en algo... una especie de templo que había en el arrecife. Se hundieron a gran velocidad... Fue una tragedia.$B$BNecesitamos recuperar sus cuadernos de bitácora y sus cartas de navegación antes de partir. ¡No podemos permitir que nadie vea esa información!",
    ["O"] = "El contramaestre Nizlix quiere que recuperes los cuadernos de bitácora y las cartas de navegación de El Capricho y del Espíritu de argénteos.",
    ["T"] = "Rescate en alta mar",
  },
  [663] = {
    ["D"] = "Lolo ve a un minúsculo $R! ¡Siempre al loro, Lolo está!$B$B$GBienvenido:Bienvenida; a la Cala de Faldir. El capitán O\'Breen dijo que solo estaríamos aquí durante unas horas. Lo justo para reunir el tesoro y volver a Bahía del Botín.$B$BPero Lolo piensa que vamos a estar allí más tiempo. Perdimos otros dos barcos de nuestra formación. Pobres Espíritu de argénteos y El Capricho. ¡En el fondo del mar ahora descansan!$B$BLolo sugiere que hables con el capitán O\'Breen si te vas a quedar por aquí.",
    ["O"] = "Habla con el capitán O\'Breen en la Cala de Faldir, en las Tierras Altas de Arathi.",
    ["T"] = "¡Tierra a vista!",
  },
  [664] = {
    ["D"] = "Ah, esta vida es terrible. Debería estar en una tumba de agua ahora mismo, no aquí, junto al fuego y respirando aire fresco.$B$BLa tripulación se esforzó tanto para salvar El Capricho, mi hermosa nave... Benditos sean, benditos sean sus corazones.$B$BO\'Breen llegó a la costa sano y salvo, pero nosotros no tuvimos tanta suerte. La mar nos hundió, lo juro.$B$BUn momento antes estábamos a flote y después... el agua empezó a entrar. ¡Y esas bestias! Mataron a todos los supervivientes. Esa escoria naga... Yo perdí el conocimiento y acabé aquí, a salvo.$B$BQuiero ver a esas bestias muertas.",
    ["O"] = "El capitán Tripacero de Cala de Faldir quiere que mates a 10 asaltantes Espinadaga y a 3 hechiceras Espinadaga.",
    ["T"] = "Ahogar las penas",
  },
  [665] = {
    ["D"] = "Ahora que somos Asaltantes Aguasnegras hechos y derechos, debemos ayudar a O\'Breen a encontrar el tesoro elfo perdido.$B$BEs casi imposible encontrar las gemas en el mar sin ayuda. El doctor ha creado unas gafas que ayudan a ver en la oscuridad del mar. Pero hay que cargarlas con la energía que emana una piedra encantada de la cueva que hay en la colina.$B$B¡Y la cueva está maldita! Cuando alguien se acerca, cae en una emboscada. Defiéndeme para que pueda recoger la energía de la piedra con las gafas.",
    ["O"] = "Escolta al profesor Fiseltor hasta la cueva y a su regreso.",
    ["T"] = "El tesoro sumergido",
  },
  [666] = {
    ["D"] = "El tesoro lleva tanto tiempo en el fondo del mar que las gemas se han calcificado y convertido en piedras. Pero gracias al poder de estas gafas las localizarás sin esfuerzos.$B$B¡Un inventito gnomo de gran utilidad!$B$BAsí que toma prestadas estas gafas de caza de gemas, $N, y mira a ver si puedes recolectar algo del tesoro perdido para el capitán O\'Breen.$B$BYo mismo bucearía hasta allí pero... mm... estooo, tengo importantes asuntos científicos que atender en tierra firme y seca... digo, pues eso.",
    ["O"] = "El doctor Draxlegauge de la Cala de Faldir quiere que reúnas 10 gemas de elfo y le devuelvas las gafas de caza de gemas cuando hayas acabado.",
    ["T"] = "El tesoro sumergido",
  },
  [667] = {
    ["D"] = "Vaya, malas noticias. Al parecer, los Espinadaga están amasando recursos para un ataque.$B$BLos nagas son famosos por su crueldad en el combate. Seguirán atacando hasta que me maten.$B$BNo valoro mi propia vida más que la de mis hombres, pero debes defenderme. La tripulación no será capaz de volver a Bahía del Botín sin mis conocimientos de navegación.$B$BLos nagas vendrán desde el mar. Necesito que permanezcas aquí conmigo para eludir el ataque. Controla el cañón y recházalos.$B$B¿Estás $Glisto:lista;, $N?",
    ["O"] = "Protege a Temblores O\'Breen durante el ataque.",
    ["T"] = "Muerte desde abajo",
  },
  [668] = {
    ["D"] = "No hagamos esperar al capitán O\'Breen. Querrá ver estas gemas el primero. A fin de cuentas, ¡a eso ha venido!$B$BY después de unas semanas tratando con los piratas, el profesor y yo no hemos dado cuenta de que lo peor para nuestra salud sería que nos pillaran con su tesoro en las manos.$B$BAhí va, $N, llévaselas a O\'Breen.",
    ["O"] = "Llévale las gemas de elfo al capitán O\'Breen a la Cala de Faldir, en las Tierras Altas de Arathi.",
    ["T"] = "El tesoro sumergido",
  },
  [669] = {
    ["D"] = "El maestro de flota Cuernomarino querrá enterarse de nuestro hallazgo sin demora.$B$BPero como puedes ver, no estamos precisamente en posición de salir de aquí No solo la marea es demasiado baja, sino que esas malditas criaturas a las que hemos perturbado no nos quitan ojo de encima.$B$BPuedes ser de gran ayuda para los incursores Aguasnegras si consigues llegar a Bahía del Botín y decirle al maestro de flota Cuernomarino que hemos descubierto el tesoro y estamos trabajando para extraer más. Llévate esta muestra como prueba.",
    ["O"] = "Llévale una muestra de gema de elfo al maestro de flota Cuernomarino a Bahía del Botín, en la Vega de Tuercespina.",
    ["T"] = "El tesoro sumergido",
  },
  [670] = {
    ["D"] = "Tú, $N, pareces tener un espíritu aventurero. Mi flota está anclada en Bahía del Botín hasta que podamos reabastecernos.$B$BEstán ocurriendo cosas graves aquí y en el extranjero y necesito hacer llegar una comunicación de alto secreto a Temblores O\'Breen que está amarrado en la costa de Arathi.$B$B¿Puedo confiar en que entregues este mensaje a salvo y sin traicionar su secreto?",
    ["O"] = "Llévale la carta de Cuernomarino a Temblores O\'Breen a la Cala de Faldir, en las Tierras Altas de Arathi.",
    ["T"] = "El tesoro sumergido",
  },
  [672] = {
    ["D"] = "Tratamos de tranquilizarlo, alegrarlo, contarle historias de nuestra poderosa ciudad de Orgrimmar en Kalimdor, pero nada lo motivaba.$B$BRelatos de nuestro joven Jefe de Guerra, Thrall, que lleva la armadura y el martillo de Orgrim... pero, quizás... ¡Quizás! Una prueba de nuestra recién encontrada magia chamán le devolvería la esperanza. ¡Merece la pena intentarlo!$B$BUn pequeño objeto que podríamos hacer para él, sí... Tráeme unos ojos de raptor y veré lo que puedo hacer.",
    ["O"] = "Consigue 10 ojos de raptor de las Tierras Altas de los zancudos y de los descarnadores de las Tierras Altas para Tor\'gan en Sentencia.",
    ["T"] = "El despertar de los espíritus",
  },
  [673] = {
    ["D"] = "No me cabe la menor duda de que un poderoso brujo reside entre las paredes de Stromgarde. De vez en cuando, usando una reliquia mágica arcana, este brujo ha invocado a gran número de terribles demonios. Tenemos que eliminar la fuente de su energía.$B$BEncuentra al brujo y mátalo. Tráeme cualquier objeto mágico que encuentres sobre su persona y tomaré las medidas necesarias para acabar con él y asegurarme de que su poder no se usará para contaminar esta tierra.",
    ["O"] = "Recupera el orbe de sangrita de Marez Cogulla y llévaselo a Tor\'gan en Sentencia.",
    ["T"] = "Magia demoníaca",
  },
  [674] = {
    ["D"] = "Toma, lleva este amuleto a Gor\'mul y veamos si la fuerza del raptor puede encender la llama del guerrero que otrora ardía en él.",
    ["O"] = "Dale el amuleto de garfa de raptor a Gor\'mul en Sentencia, en las Tierras Altas de Arathi.",
    ["T"] = "El despertar de los espíritus",
  },
  [675] = {
    ["D"] = "<Gor\'mul tira el amuleto al suelo.>$B$B¿Te ha enviado Tor\'gan? ¡Bah! Por qué él compadecerse de mí ahora, no alcanzo entender. Compasión ser negada mí cuando no permitir mí morir con mi Jefe de Guerra en la batalla. Cruel golpe del destino tocar mí...",
    ["O"] = "Habla con Tor\'gan en Sentencia, en las Tierras Altas de Arathi.",
    ["T"] = "El despertar de los espíritus",
  },
  [676] = {
    ["D"] = "Alfeñiques miserables... ¿Se puede saber qué miras, $C? Te crees muy especial, ¿eh? Demuéstralo.$B$BLa avanzada de Sentencia, en Arathi, está siendo atacada. La derrota es inevitable si no les enviamos ayuda de inmediato.$B$BPero antes de salir corriendo, será mejor que te pongas a prueba. Al noreste de Stromgarde, en Arathi, hay un túmulo de ogros en el que vive la tribu Puño de Roca. Cuando seas capaz de manejarte con esos ogros, preséntate ante Bombo Vil en Sentencia.",
    ["O"] = "Mata a 8 ogros y 10 déspotas Puño de Roca; luego ve a ver a Bombo Vil a la avanzada de Sentencia, en las Tierras Altas de Arathi.",
    ["T"] = "La Sentencia",
  },
  [677] = {
    ["D"] = "Los trols Secacorteza y los ogros Puño de Roca de Arathi se han unido para arrebatarnos esta tierra, pero no dejaremos nuestro sino en sus manos ni en las de nadie más. Los ejecutaremos y les demostraremos que este lugar es nuestro hogar.$B$BLos trols nos han traicionado; los ogros nos usarían de comida y de cama si les dejásemos, eso jamás ocurrirá.$B$BEmpezamos el ataque sobre los trols Secacorteza al sur de este lugar. Ejecútalos por docenas y regresa solo cuando sus campamentos estén bañados en sangre.",
    ["O"] = "Mata a 10 lanzahachas Secacorteza, 10 rebanacabezas y 8 médicos brujos y vuelve junto a Bombo Vil en la avanzada de Sentencia.",
    ["T"] = "Un llamamiento a las armas",
  },
  [678] = {
    ["D"] = "Tu fuerza es evidente, $N. Veamos ahora cómo te va frente a un enemigo real.$B$BLos Puño de Roca tienen una guarida al suroeste de este lugar hacia el puente de los enanos y, si no me equivoco, los destructores y los magis lugareños supondrán para ti un desafío aún mayor, así ha sido para nosotros hasta ahora.$B$BEjecuta a tantos como puedas y regresa ante mí con ese cuerpo cuando lo hayas conseguido, o que lo haga tu espíritu si fracasas. Sea como fuere, alabaré tu tenacidad en la batalla.",
    ["O"] = "Mata a 10 toscos Puño de Roca y 4 magi Puño de Roca y vuelve junto a Bombo Vil en la avanzada de Sentencia.",
    ["T"] = "Un llamamiento a las armas",
  },
  [679] = {
    ["D"] = "La bestia está lista para la decapitación, y con toda la fuerza, $N. Has demostrado tu valía a la hora de ayudarnos y creemos que eres la persona indicada para este golpe final. Los líderes de estos ataques han construido su guarida en las ruinas de Stromgarde, un claro símbolo de la fragilidad humana.$B$BEncuentra y mata a los señores Puño de Roca y su lacayo chamán, antes de que nos logren expulsar de estas tierras.",
    ["O"] = "Mata a 7 chamanes Puño de Roca y a 3 señores Puño de Roca y vuelve junto a Bombo Vil en la avanzada de Sentencia.",
    ["T"] = "Un llamamiento a las armas",
  },
  [680] = {
    ["D"] = "Eres de gran ayuda para mi marido, $C, pero él no sabe por qué Sentencia está siendo atacada.$B$BOr\'Kalar, el peón de Jarra\'thol\' en Stromgarde, odia visceralmente a mi marido y desea por encima de cualquier cosa su muerte por derrotarlo hace años.$B$BSegún mis augurios, Or\'Kalar le ha tendido una trampa a mi marido, así que le he ocultado signos de la presencia del ogro.$B$BMi marido se lanzaría sin más miramientos si supiera el paradero del ogro y me gustaría pedirte tu ayuda para ejecutar a la criatura.",
    ["O"] = "Mata a Or\'Kalar y lleva su cabeza a Korin Vil en la avanzada de Sentencia.",
    ["T"] = "Una auténtica amenaza",
  },
  [681] = {
    ["D"] = "Estamos librando una guerra por Stromgarde, y la estamos perdiendo. Los ogros, la Hermandad y nosotros mismos hemos dividido nuestra otrora gran ciudad, y cada facción se encuentra atrapada en un sangriento enfrentamiento.$B$BNuestras fuerzas menguan mientras las del enemigo aumentan.$B$BNecesitamos ayuda de la ciudad de Costasur pero es difícil recibir suministros mientras la Hermandad controle el territorio que hay que cruzar.$B$BVe a la base de la Hermandad en Mansión Redilnorte y ejecútalos. Quizás entonces disminuya la presión sobre los trenes de suministros y nos llegarán más.",
    ["O"] = "Mata a 10 bandoleros de la Hermandad y 6 mercenarios de la Hermandad.$B$BVuelve junto a la capitana Nials en el Refugio de la Zaga.",
    ["T"] = "Mansión Redilnorte",
  },
  [682] = {
    ["D"] = "La Hermandad de Stromgarde se resiste a nuestro contingente de la milicia allí desplazado. Para incentivar a la chusma a enfrentarse a nuestros más disciplinados soldados, los jefes de la Hermandad han puesto precio a los nuestros, prometiendo oro por cada distintivo de Stromgarde arrebatado a soldados muertos.$B$BQuiero que recuperes esos distintivos, ¡no podemos permitir que esos degolladores se lleven el trofeo de nuestros valientes caídos!$B$BPenetra en la zona de Stromgarde controlada por la Hermandad y busca nuestros distintivos.",
    ["O"] = "Llévale 7 distintivos de Stromgarde a la capitana Nials en el Refugio de la Zaga.",
    ["T"] = "Distintivos de Stromgarde",
  },
  [683] = {
    ["D"] = "$N, tengo un pequeño favor que pedirte.$B$BLlévale esta nota al gran monarca de Forjaz, el rey Magni Barbabronce. Es poca cosa para ti, que tanto has ayudado ya a la causa de la Alianza.$B$BPresentarme yo ante el Rey en mi estado de duelo resultaría contraproducente. Seguro que lo entiendes.",
    ["O"] = "Lleva la nota de Sara Balloo al rey Magni Barbabronce de Forjaz.",
    ["T"] = "Petición de Sara Balloo",
  },
  [684] = {
    ["D"] = "Le han puesto precio a la cabeza de Marez Cogulla, una consejera de alto nivel y agente de la Hermandad. La vieron por última vez en el territorio controlado por la Hermandad en Stromgarde.$B$BLa recompensa se reclama a la capitana Nials.$B$BOjo: Marez es una hábil y taimada bruja, famosa por sus trapicheos con los demonios. Se recomienda extremar la precaución en su caza.",
    ["O"] = "Llévale la cabeza de Marez a la capitana Nials en el Refugio de la Zaga.",
    ["T"] = "Se busca: Marez Cogulla",
  },
  [685] = {
    ["D"] = "La Milicia de Stromgarde ha puesto precio a la cabeza de Lord Falconcrest y a la de su guardaespaldas Otto. Falconcrest dirige a la Hermandad en las Tierras Altas de Arathi y su muerte supondría un duro golpe para ellos.$B$BSu guardaespaldas Otto, aunque no sea un objetivo estratégico, es un feroz adversario y ha acabado con docenas de nuestros defensores.$B$BLas recompensas se reclaman a la capitana Nials.",
    ["O"] = "Llévale la cabeza de Otto y la de Lord Falconcrest a la capitana Nials en el Refugio de la Zaga.",
    ["T"] = "Se busca: Otto y Lord Falconcrest",
  },
  [686] = {
    ["D"] = "No espero que una buena enana como la señora Balloo entienda que las farsas de guerra son por el bien de nuestra gente. La muerte de su marido, como la de cada enano que lucha en nombre de la Alianza, me pesa en el alma.$B$BAunque no puedo traer a su marido de vuelta de El Vacío Abisal, al menos puedo rendirle homenaje.$B$BVoy a encargar al gran mampostero Marmolez un monumento a la memoria de Sully Balloo.$B$BInfórmale, $C. Serás el custodio de mi voluntad.",
    ["O"] = "El rey Magni Barbabronce quiere que hables con el gran mampostero Marmolez.",
    ["T"] = "Un homenaje real",
  },
  [687] = {
    ["D"] = "Theldurin el Perdido fue un día miembro de nuestra orden, el Martillo Crepuscular. Pero perdió la cabeza y desdeñó nuestras enseñanzas. Antes de huir de nuestro lado, robó el Pergamino de Myzrael. No pudimos cogerle, pero hay rumores de que se oculta en las Tierras Inhóspitas, ha enloquecido y no dice más que incoherencias.$B$BPara traer de vuelta a la Dama, debemos encontrar el pergamino. Y para hacerlo, debes encontrar a Theldurin.",
    ["O"] = "Encuentra a Theldurin el Perdido.",
    ["T"] = "Theldurin el Perdido",
  },
  [688] = {
    ["D"] = "<Myzrael habla.>$B$B¡Libre! ¡SOY LIBRE! Libre para recuperar la fuerza, sin que me vean mis captores. Porque si me enfrentara a ellos ahora, seguro que me vencerían y me volverían a encerrar.$B$BPero con el tiempo haré frente a los gigantes ¡y se arrepentirán de haber sido mis guardias!$B$BEres $Gun:una; noble $Galiado:aliada;, $N. Dentro de algún tiempo volveré a necesitarte. Cuando esté lista, necesitaré que me conjures para devolverme a la superficie.$B$BHabla con Zaruk en Sentencia. Él sabe cómo realizar el conjuro.",
    ["O"] = "Habla con Zaruk en Sentencia, en las Tierras Altas de Arathi.",
    ["T"] = "Aliados de Myzrael",
  },
  [689] = {
    ["D"] = "¿Así que el Rey quiere un monumento en honor de un soldado llamado Sully Balloo? ¿Y dices que el pobre diablo murió durante la tragedia del Puente Thandol?$B$B¿Habrá alguna vez paz en Azeroth?$B$BSea como sea, todos los encargos del Rey son de la máxima importancia. Para crear una obra épica y duradera, necesito una de las mejores piedras disponibles. Tráeme granito de Alterac; lo encontrarás en las Colinas de Darrow, en Trabalomas.",
    ["O"] = "El gran mampostero Marmolez de Forjaz quiere 5 piezas de granito de Alterac.",
    ["T"] = "Un homenaje real",
  },
  [690] = {
    ["D"] = "Saludos, $N. Tienes aspecto de aventurero y tengo una tarea que requiere atención.$B$BEl archimago Trelane, mi primer maestro, huyó de Stromgarde en las Tierras Altas de Arathi no hace mucho, y me ha pedido que le ayude a recuperar unas cosas que se dejó.$B$BSitiaron la ciudad e intentó defender la Torre de Arathor pero fue en vano. Ya tengo a agentes en las Tierras Altas pero me han pedido que envíe refuerzos. ¿Te interesa?",
    ["O"] = "Encuentra a Skuerto en el Refugio de la Zaga, en las Tierras Altas de Arathi.",
    ["T"] = "Una petición de Malin",
  },
  [691] = {
    ["D"] = "El archimago Malin nos ha enviado a Skuerto y a mí a recolectar componentes para él. No sabía ni por dónde empezar pero ahora que estás aquí, creo que el problema está resuelto.$B$BNecesito lo siguiente: colmillos Secacorteza, bolsas medicinales Secacorteza y un cuchillo de cazador de las Sombras.$B$B¿Podrías encontrar estas cosas para mí? Los Secacorteza viven al sudeste de Arathi.",
    ["O"] = "Encuentra 10 colmillos Secacorteza, 4 bolsas de medicina Secacorteza y un cuchillo de cazador de las Sombras para el aprendiz Kryten en el Refugio de la Zaga.",
    ["T"] = "Valer su peso en oro",
  },
  [692] = {
    ["D"] = "El pergamino de Myzrael... ¡¿el pergamino de Myzrael?! ¡Yo que tú, ni me acercaba! Su magia puede sacar a Myzrael a la superficie del mundo y si lo haces, ¡estaremos en un lío!$B$BO... quizás ya es demasiado tarde. A lo mejor ya está libre. Quizás solo esté recobrando fuerzas antes de desatar toda su ira sobre nosotros. De ser así, entonces ¡NECESITAS el pergamino!$B$B... ¡qué mala suerte que esté en pedazos! Elementales iracundos lo robaron, lo desgarraron y luchan ahora por los fragmentos.$B$BPero puedes reunir esos trozos, quizás podamos arreglarlo...",
    ["O"] = "Lleva el trozo de pergamino roto, el trozo de pergamino arrugado y el trozo de pergamino chamuscado a Theldurin el Perdido.",
    ["T"] = "Los trozos perdidos",
  },
  [693] = {
    ["D"] = "Eh, $N. Perdona, puede que antes me quedara corto... y no, no es un chiste.$B$BM\'he enterao del lugar donde acabó el primer objeto de Trelane: Kor\'gresh lo tiene en la Sala Puño de Roca. Y, por cierto, le disparaba con hielo pa\' mantenerla firme, no debería de tardar en enterarse de pa\' qué sirven sus poderes.$B$BLa Sala Puño de Roca está al sur de donde estamos, luego tira pa\'l este de Puente Thandol. Encuentra al ogro y trae pa\'cá esa varita.$B$BMientras no estás, veré si se m\'ocurre qué hacer con Stromgarde.",
    ["O"] = "Encuentra la varita de evocación de Trelane y llévasela a Skuerto en el Refugio de la Zaga.",
    ["T"] = "Empuñar la varita",
  },
  [694] = {
    ["D"] = "¿Has descansado?$B$BEste será nuestro siguiente paso: hay ogros rodeando la Torre de Arathor en Stromgarde. Tendremos que activar las defensas de la torre pa\' tener la más mínima posibilidad de vencerles y llegar vivos hasta los objetos. Pa\' eso, necesitaremos unas gemas a las que les han tomao cariño los chamanes Puño de Roca. Las arrebataron de la torre pero pa\' mí que no les van a durar.$B$BConsigue una d\'esas gemas y llévasela a Kryten, él conoce el hechizo pa\' que funcionen de llave.",
    ["O"] = "Encuentra un ágata azur y llévasela al aprendiz Kryten en el Refugio de la Zaga.",
    ["T"] = "Las defensas de Trelane",
  },
  [695] = {
    ["D"] = "Aquí tienes, $N. Solo hay que apretar la gema contra uno de los pedestales cerca de la base de la torre para activar las defensas. Úsala cuando estés $Glisto:lista; para entrar en la torre a buscar los objetos de Trelane que faltan.$B$BQue tengas suerte. Estaré en el campamento esperando tu regreso. Ah, y habla con Skuerto antes de marcharte. Te dará el resto de detalles que necesitarás.",
    ["O"] = "Habla con Skuerto en el Refugio de la Zaga, en la Cuenca de Arathi.",
    ["T"] = "Un encantamiento de aprendiz",
  },
  [696] = {
    ["D"] = "Esto es lo que tienes que buscar: la filacteria de Trelane, el orbe de Trelane y el ágata de ascuas de Trelane.$B$BLos tres deberían encontrarse en unos baúles en el interior de la torre y no deberías tener ningún problema pa\' abrirlos $Gsolo:sola;, aunque ten cuidado con los ogros.$B$BLas defensas de la torre no bastarán pa\' detenerlos; tendrás que tener las armas preparadas. Los ogros sabrán qu\'estás allí robándoles, así que no s\'andarán con mimos si te pillan.$B$BTen cuidado y suerte.",
    ["O"] = "Encuentra la filacteria de Trelane, el orbe de Trelane y el ágata de ascuas de Trelane, y llévaselo todo a Skuerto en el Refugio de la Zaga.",
    ["T"] = "Ataque a la torre",
  },
  [697] = {
    ["D"] = "Lleva esta carta a Ventormenta y dile a Malin que Kryten y yo pronto iremos pa\'llá. Te recompensará debidamente por habernos ayudado aquí, $N, de eso no me cabe la menor duda.$B$BQue viajes sin percances.",
    ["O"] = "Lleva la carta lacrada al archimago Malin en el Barrio de los Magos de Ventormenta.",
    ["T"] = "Una petición de Malin",
  },
  [698] = {
    ["D"] = "Estoy intentando reunir algunos suministros para enviarlos a nuestra avanzada cerca de la playa del este. Tok\'Kar hace lo que puede, pero entre sus guardias y cocinar para los soldados, no le queda mucho tiempo.$B$BLes encanta cocinar carne de Denteserra cuando pueden, pero no he tenido tiempo de conseguirles otro cargamento. ¿Te importaría echarles una mano?$B$BLos Denteserra jóvenes no son tan sabrosos como los viejos, así que no los cojas. Hay que esperar un año o dos para que alcancen el tamaño ideal.",
    ["O"] = "Lleva 8 costillares de Denteserra sin preparar a Tok\'Kar en el Pantano de las Penas.",
    ["T"] = "Falta de suministros",
  },
  [699] = {
    ["D"] = "Con la amenaza de múrlocs en la costa, nos hemos quedado aquí para proteger, o advertir si no podemos hacer más, de cualquier amenaza a Rocal. No nos deja mucho tiempo para relajarnos y nos vendría bien algo de ayuda...$B$BLas garras de sacudidores Denteserra son unas armas excelentes si se preparan bien. Tráeme algunas garras y te recompensaremos con una de nuestras armas como agradecimiento por la carne fresca y la buena compañía.",
    ["O"] = "Lleva 6 garras de sacudidores Denteserra a Tok\'Kar en el Pantano de las Penas.",
    ["T"] = "Falta de suministros",
  },
  [700] = {
    ["D"] = "Ah, esta piedra tan buena es de Lordaeron. Es una lástima que la tierra esté arrasada.$B$B¡Pero el monumento ha quedado muy bien! Creo que el Rey estará satisfecho.$B$BPor favor, ve a notificar a Su Majestad que el monumento ya está terminado.",
    ["O"] = "Preséntate ante el rey Magni Barbabronce.",
    ["T"] = "Un homenaje real",
  },
  [702] = {
    ["D"] = "Toma este orbe y usa su energía para darle a Gor\'mul la esencia del raptor. Seguro que su ánimo recobrará el ímpetu y volverá a encontrarle sentido a la vida. Cuando vuelva a la normalidad, podremos explicarle el estado actual de la Horda y contaremos con la ayuda de un gran guerrero en nuestras incesantes batallas.",
    ["O"] = "Dale el orbe de Tor\'gan a Gor\'mul. Está en Sentencia, en las Tierras Altas de Arathi.",
    ["T"] = "La astucia del raptor",
  },
  [706] = {
    ["D"] = "Cuando era aprendiz de Feranor Ferrodedo, aprendí su técnica especial para imbuir armas con encantamientos ígneos. Lo último que supe de él es que estaba trabajando en una herrería para una partida de cazadores en Lordaeron.$B$BNo puedo contarte demasiados detalles del proceso pero sí puedo decirte que requiere sangre del corazón de una cría de draco negro que aún esté latiendo. No estoy demasiado ocupado ahora, espero instrucciones de Forjaz. Si me traes un corazón de las crías del Barranco Lethlor, ¡te demostraré de lo que soy capaz!",
    ["O"] = "Consigue 1 corazón de draco negro para Sigrun Extracero.",
    ["T"] = "Encantamientos Llamarada ígnea",
  },
  [707] = {
    ["D"] = "$N, he recibido unos informes del prospector Vetaferro sobre la excavación de Loch Modan; dice que necesita a alguien que estudie otra excavación.$B$BNo dice nada más, pero si te apetece estirar las piernas, ve a hablar con Vetaferro.",
    ["O"] = "Habla con el prospector Vetaferro en la Excavación de Vetaferro en Loch Modan.",
    ["T"] = "¡Vetaferro te necesita!",
  },
  [709] = {
    ["D"] = "¡Ay, ay! ¡Ay, ay! Se acerca una catástrofe. ¡Recuerda mis palabras! ¡Una catástrofe!$B$BNo, no puedo hablar de ello... no puedo pronunciar sus nombres, ¡podrían oírme! Pero si aprendemos de ellos... Sí, si aprendemos más... ¡quizás logremos frustrar sus planes!$B$BQuizás.$B$BVe a Uldaman. ¡No! ¡Ya te lo dije! Tienes que encontrar... encontrar la tablilla de... Ryun\'eh. ¡Sí, encuéntrala! ¡Y no me hagas pronunciar ese nombre otra vez!$B$BSe encuentra en las profundidades de ese lugar, en un antiguo cofre. Ahí es donde se esconde... en un cofre creado antes del tiempo de los enanos.",
    ["O"] = "Llévale la tablilla de Ryun\'eh a Theldurin el Perdido.",
    ["T"] = "La solución a la maldición",
  },
  [710] = {
    ["D"] = "Aquí en las Tierras Inhóspitas, he dejado de trabajar en mi más reciente proyecto: mis grilletes de esclavitud elemental.$B$BCuando acabe, permitirán a los magos invocar a elementales aún más poderosos para fines de más alcance. Para empezar, necesito fragmentos de piedra pequeños para probar mis más recientes hechizos ofensivos y unos aparatitos que he compuesto.$B$BEs fácil reunir tales fragmentos de los elementales de piedra inferiores hacia el oeste de este lugar. ¿Podrías traerme algunos?",
    ["O"] = "Encuentra 10 fragmentos de piedra pequeños y llévaselos a Lotwil Veriatus en las Tierras Inhóspitas.",
    ["T"] = "Estudio de los elementos: roca",
  },
  [711] = {
    ["D"] = "$N, has demostrado ya más valía que todos mis aprendices juntos.$B$BLas siguientes piezas que necesito requerirán algo más de fuerza. Si tienes cuidado, encontrarás grandes losas de piedra en los elementales de piedra. Sus cuerpos son más resistentes y voluminosos, así que tendrás más probabilidades de poder extraer piezas lo suficientemente grandes para mis pruebas. Los hallarás más hacia el oeste desde aquí o al sureste bordeando las faldas de las montañas.$B$BTómate tu tiempo; estaré ocupado probando estos fragmentos más pequeños que me has traído.",
    ["O"] = "Llévale 3 losas de piedra grandes a Lotwil Veriatus en las Tierras Inhóspitas.",
    ["T"] = "Estudio de los elementos: roca",
  },
  [712] = {
    ["D"] = "$N, necesito una pieza más de los elementales de las Tierras Inhóspitas: los brazales de vínculo de roca de los grandes elementales de piedra. Según mis cálculos, esos brazales deberían bastar para medir la fuerza de mis grilletes.$B$BMis intentos de visualizar a los grandes elementales de roca me mostraron un grupo de ellos al suroeste de aquí, cerca de unos ogros, si no me equivoco.$B$BTráeme 5 de sus brazales. Así tendré reservas. Servo es famoso por su facilidad para perder mis reactivos e investigaciones.",
    ["O"] = "Llévale 5 brazales de vínculo de roca a Lotwil Veriatus en las Tierras Inhóspitas.",
    ["T"] = "Estudio de los elementos: roca",
  },
  [713] = {
    ["D"] = "Déjame decirte algo sobre el aceite de Escarcha: nada impide que un motor o un engranaje se recalienten como ese aceite.$B$BSus propiedades de enfriamiento son extraordinarias y su longevidad como lubricante no tiene parangón. Y eso no es todo: también ayuda a retener los encantamientos de los objetos mágicos.$B$BNecesito un frasco de este aceite para aumentar la capacidad mágica de mis grilletes y hacerlos lo suficientemente poderosos para impedir que nuestros amigos invocados nos maten a todos.$B$BBro\'kin, un amigo goblin, te enseñará a confeccionarlo. Se encuentra en Alterac.",
    ["O"] = "Encuentra aceite de Escarcha y llévaselo a Lotwil Veriatus en las Tierras Inhóspitas.",
    ["T"] = "Mantener la cabeza fría",
  },
  [714] = {
    ["D"] = "$N, has hecho un trabajo más allá de toda expectativa.$B$B<Lotwil se ve claramente centrado en su trabajo.>$B$BSi por ventura nos volvemos a encontrar en Kharanos, pasa cuando quieras a por trabajo.$B$B¡Vaya! Rápido, agarra el... sí, eso. Gracias.$B$BBien, ahora solo necesito una cosa más. Es una pieza pequeña pero me temo que bastante crucial: un girocronátomo.$B$BSi pudieras hacerte con uno y traérmelo, estaré listo para completar esto.",
    ["O"] = "Llévale 1 girocronátomo a Lotwil Veriatus en las Tierras Inhóspitas.",
    ["T"] = "Un giro... ¿Qué?",
  },
  [715] = {
    ["D"] = "Creo que esos objetos que conseguiste para Lotwil van a ser la menor de nuestras preocupaciones, $N. Por otro lado, has demostrados ser más que hábil en las ciencias que Lotwil considera sagradas, así que ¿te apetece ayudarme?$B$BNecesito una poción de invisibilidad y una poción curativa; ambas podrían venir bien muy pronto si le sale esto.$B$BA cambio, te daré esta receta para una poción en la que he estado trabajando mientras aprendías cosas de Lotwil. Se basa en parte de la investigación que ha realizado aquí en las Tierras Inhóspitas.",
    ["O"] = "Llévale 1 poción de sanación y 1 poción de invisibilidad inferior a Lucien Borlallave en las Tierras Inhóspitas.",
    ["T"] = "Piedra líquida",
  },
  [716] = {
    ["D"] = "Sé lo agarrado que Lotwil puede llegar a ser, $N, así que permite que te haga algo por las molestias. A lo mejor hasta te protege de la próxima atrocidad que será el más reciente invento de Lotwil.$B$BHe estado trabajando con todas las piedras sobrantes que habías recolectado para él y puedo hacerte unos buenos brazales que podrían serte de utilidad. Tienen un encantamiento, claro.$B$BSolo necesito brazales de bronce estampados confeccionados por un herrero para poder fabricar los brazales que te estoy haciendo.",
    ["O"] = "Llévale unos brazales de bronce estampados a Lucien Borlallave en las Tierras Inhóspitas.",
    ["T"] = "Es mejor la piedra que la tela",
  },
  [717] = {
    ["D"] = "Ha quedado claro que no podemos garantizar que Latigoscuro y Hematus permanezcan encarcelados en Lethlor. Que esos dragones Negros se adueñaran del lugar y que los ogros consiguieran robar el Signo indica que se deben tomar medidas más drásticas.$B$BLa prisión de los dracos está controlada por tres piedras rúnicas que solo se pueden extraer con el Signo de la Tierra. Esas piedras rúnicas pueden entonces usarse para romper el sello que los mantiene presos.$B$B$N,... asegúrate de que su sueño es eterno.",
    ["O"] = "Usa el Signo de la Tierra para activar los pilares de amatista, de ópalo y de diamante y así obtener las piedras rúnicas.$B$BColoca las piedras rúnicas en el Sello de la Tierra para liberar a Latigoscuro y a Hematus.$B$BMátalos y llévale a Garek las ataduras de Latigoscuro, las cadenas de Hematus y el Signo de la Tierra.",
    ["T"] = "Terremotos",
  },
  [718] = {
    ["D"] = "Con las prisas por marcharnos de la excavación de Uldaman, nos obligaron a abandonar muchos carros y muchas cajas de suministros por el camino. Los ogros locales del Asentamiento Kosh se han lanzado sobre nuestro equipo abandonado como buitres sobre un cadáver en descomposición. No huele mucho mejor tampoco. Me refiero a los cadáveres.$B$BSea como fuere, se llevaron el carro en el que se transportaban todas nuestras armas y tal y tal..., bueno, esto es un poco violento, no hemos podido conseguir nada de comida.$B$BEstamos metidos en un lío, ¿nos podrías ayudar?",
    ["O"] = "Recupera el cajón de suministros para Sigrun Extracero.",
    ["T"] = "Espejismos",
  },
  [719] = {
    ["D"] = "Hola, $N. Ten cuidado por estos lares. Hay enanos Hierro Negro por todas partes, parecía del clan Forjatiniebla.$B$BAcaban de atacar la excavación donde trabajaba, se han cargado a casi todos, hasta a mi jefe Piemartillo. Me escapé por los pelos.$B$BEl sitio está justo al norte de este lugar y estoy intentando planear la forma de recuperar parte de nuestros suministros, sobre todo mi pico de la suerte.$B$B¿Te apetecería echarme una mano? ¡Uno de esos malditos Hierro Negro tendrá mi pico! Podría ser cualquiera.",
    ["O"] = "Encuentra el pico de la suerte de Ryedol y llévaselo al prospector Ryedol al sur de la excavación de Piemartillo.",
    ["T"] = "Un enano y sus herramientas",
  },
  [720] = {
    ["D"] = "<Hay texto garabateado de forma tosca con tiza en el mapa.>$B$B\"Ryedol, $BMe llevan a Uldaman. Envía ayuda.$B-Piemartillo\"",
    ["O"] = "Localiza al prospector Ryedol y dile que Grez Piemartillo está vivo.",
    ["T"] = "Un signo de esperanza",
  },
  [721] = {
    ["D"] = "$N, ¿puedes encontrarlo? ¿Puedes encontrar a Piemartillo en Uldaman?$B$BSi esos asquerosos Forjatiniebla lo han raptado para sonsacarle lo que sabe de las excavaciones de los titanes, entonces todo Forjaz podría estar en gran peligro.$B$BDurante mucho tiempo esos Hierro Negro han buscado magia para hacer gólems más poderosos. Piemartillo estaba haciendo lo mismo, claro, pero para detenerlos.$B$BProbablemente lo mantengan vivo hasta que se enteren de lo que sabe. ¡Espabila si quieres salvarlo!",
    ["O"] = "Encuentra a Grez Piemartillo en Uldaman.",
    ["T"] = "Un signo de esperanza",
  },
  [722] = {
    ["D"] = "Eres valiente... <tose>... por venir a Uldaman para mí, $C.$B$BSi realmente estás aquí para ayudar... al viejo Piemartillo, entonces... <tose>... tendrás que encontrar... mi amuleto.$B$BSombraprofunda lo tiene... Magregan Sombraprofunda. está por aquí... en las ruinas, seguro. Ese amuleto los conducirá... <tose>... derechito a los pergaminos si no lo paran. Depende de ti, $N. Por favor... recupera el amuleto.$B$BCon los pergaminos los Hierro Negro conseguirán crear gólems aún más poderosos. Lo sé. Me lo dicen los huesos.",
    ["O"] = "Encuentra el amuleto de Piemartillo y llévaselo a Uldaman.",
    ["T"] = "El amuleto de los secretos",
  },
  [723] = {
    ["D"] = "<Piemartillo se sujeta las heridas y se toma un momento para recuperarse antes de seguir hablando.>$B$BEncuentra a Ryedol, $N. Llévale mi amuleto. <Tose> Él... <tose> saber qué hacer. Sabe que estaba buscando los orígenes de nuestro pueblo, y él <tose> sabe que mi misión era detener al clan Forjatiniebla. Hay que impedir que encuentren esos pergaminos... <tose>",
    ["O"] = "Llévale el amuleto de Piemartillo al prospector Ryedol en las Tierras Inhóspitas.",
    ["T"] = "Tener fe en el porvenir",
  },
  [724] = {
    ["D"] = "Vale, vale... ¿quién sabrá qué hacer?$B$B¡Karnik el Historiador! ¡Eso es!$B$BSe encuentra en Forjaz. Podrá ayudarnos. Él y Piemartillo estaban trabajando juntos para encontrar los artefactos en Uldaman.$B$BSupongo que los Forjatiniebla se enteraron de que los dos sabían más de lo que dejaban ver.$B$BDile a Karnik lo que le ha ocurrido a Piemartillo y dale el amuleto, él te dirá qué hacer después.",
    ["O"] = "Llévale el amuleto de Piemartillo a Karnik el Historiador en Forjaz.",
    ["T"] = "Tener fe en el porvenir",
  },
  [725] = {
    ["D"] = "$N, corre y encuentra al consejero Belgrum en cuanto puedas. Cuéntale lo que has presenciado y pregúntale qué quiere que hagamos a continuación. En esta nota encontrará más detalles.$B$BMientras tanto, dedicaré un tiempo a hablar con el espíritu de Piemartillo. A lo mejor sabe algo que nos ayudará a detener las actividades de esos cretinos Forjatiniebla en las Tierras Inhóspitas. Solo nos queda rezar.",
    ["O"] = "Encuentra al consejero Belgrum y dale su nota.",
    ["T"] = "Advertir de la amenaza",
  },
  [726] = {
    ["D"] = "<El consejero Belgrum acaba de leer la nota, los ojos se le abren aún más por un momento.>$B$BVaya unas malas noticias.$B$B$N, regresa con Karnik el Historiador y dile que por la autoridad del rey actúe pronto y de forma contundente sobre la base de esta información. Tiene que detener las actividades de los Forjatiniebla en las Tierras Inhóspitas.",
    ["O"] = "Habla con Karnik el Historiador.",
    ["T"] = "Advertir de la amenaza",
  },
  [727] = {
    ["D"] = "La tablilla es muy reveladora. ¡Aunque no lo suficiente! ¡Ni de lejos!$B$BHay un cuaderno, un compendio que Yagyin escribió en otros tiempos. La... gente de los que huí... guardan este cuaderno en muchos lugares. Y sus más eruditos lo estudian. El conocimiento en él contenido es muy vasto. ¡Conocimiento para protegernos!$B$BVe a ver a Gerrig Agarrahueso. Se encuentra en una tienda de Forjaz, en La Caverna Abandonada. Muéstrale este sigilo y creerá que eres de los suyos. Pregúntale por el Compendio de Yagyin... ¡y tráemelo!",
    ["O"] = "Dale el sigilo del Martillo a Gerrig Agarrahueso en La Caverna Abandonada de Forjaz.",
    ["T"] = "Ir a Forjaz a buscar el \"Compendio\" de Yagyin",
  },
  [728] = {
    ["D"] = "La tablilla es muy reveladora. ¡Aunque no lo suficiente! ¡Ni de lejos!$B$BHay un cuaderno, un compendio que Yagyin escribió en otros tiempos. La... gente de los que huí... guardan este cuaderno en muchos lugares. Y sus más eruditos lo estudian. El conocimiento en él contenido es muy vasto. ¡Conocimiento para protegernos!$B$BVe a ver al vigilante Bel\'dugur en Entrañas, Lordaeron. Muéstrale este sigilo y creerá que eres de los suyos. Pregúntale por el Compendio de Yagyin... ¡y tráemelo!",
    ["O"] = "Llévale el sigilo del Martillo al vigilante Bel\'dugur en Entrañas.",
    ["T"] = "Ir a Entrañas a buscar el \"Compendio\" de Yagyin",
  },
  [729] = {
    ["D"] = "Fue un honor que el maestro Mostachogris me destinara a trabajar bajo la guía del gran prospector Tripirrem. En la Academia de Forjaz todo el mundo sabía de los grandes descubrimientos de Tripirrem.$B$BPero el prospector es un poco... mm... despistado... Vive en su mundo.$B$BPor ejemplo: habíamos descubierto pruebas de la existencia de una gran sociedad. Y un día, unos terribles gólems surgieron de la tierra e invadieron la excavación. Y Tripirrem ni se dio cuenta. Yo corrí a Auberdine a pedir ayuda.$B$B¡Te lo ruego, viaja hacia el sur y asegúrate de que el prospector está bien!",
    ["O"] = "Viaja al sur y asegúrate de que el prospector Tripirrem está bien.",
    ["T"] = "El prospector despistado",
  },
  [730] = {
    ["D"] = "Me agrada ver a $Gun:una; $C mostrando interés por las grandes maravillas arqueológicas de nuestro mundo.$B$BA menudo nuestro trabajo es considerado poco más que una afición por nuestros amigos de la Alianza. Pero es porque no se dan cuenta de que son los recientes descubrimientos de Khaz Modan los que han probado que una poderosa fuerza amenaza todo Azeroth, de Lordaeron a Kalimdor.$B$BEmpiezo a preocuparme de verdad por el personal que envié a Costa Oscura. Hace semanas que no tengo noticias suyas.$B$BViaja a Auberdine y busca pistas de su paradero.",
    ["O"] = "Viaja a Auberdine y busca rastros del equipo de excavación enano.",
    ["T"] = "¿Problemas en Costa Oscura?",
  },
  [731] = {
    ["D"] = "Dime, Hollee... ah, no, que no eres Hollee. ¿Dónde está Hollee?$B$BDeja, total, no es importante. Tenemos que encontrar ese cepillo de piedra... Quiero decir, el fósil misterioso. Tenemos que encontrar ese fósil. La Liga de Expedicionarios de Darnassus querrá algún informe de lo que he estado haciendo.$B$BAvísame cuando estés $Glisto:lista;. Iremos a la caza de ese cepillo. Quiero decir, ¡del fósil! Así encontraremos algo palpable para satisfacer a la Liga.$B$B¿Estás $Glisto:lista;?",
    ["O"] = "Protege al prospector Tripirrem mientras busca el fósil misterioso. Luego vuelve a Auberdine y busca a la arqueóloga Hollee.",
    ["T"] = "El prospector despistado",
  },
  [732] = {
    ["D"] = "Sigrun me ha dicho que habías sido de gran ayuda para él. Quizás te reclute para ayudarme. Mi maestro, Krasus del Kirin Tor, me envió aquí para poner fin a los disturbios que se estaban produciendo en Barranco Lethlor.$B$BNo puedo revelarte la naturaleza exacta de mi misión aquí ni de las tareas que debo acometer pero ten por seguro que son de vital importancia.$B$BUn grupo de ogros errantes, avistados en ocasiones cerca del Asentamiento Boff, robaron un objeto llamado el Signo de la Tierra; debo recuperarlo. Tráemelo y te contaré más detalles.",
    ["O"] = "Recupera el Signo de la Tierra para Garek.",
    ["T"] = "Terremotos",
  },
  [733] = {
    ["D"] = "La cosa se va a poner fea aquí si no logro gorronear algunos materiales para construir unas defensas y otras necesidades. Me he dado cuenta de que los ogros de la zona parecen estar bien surtidos de chatarra. A lo mejor las historias de máquinas de asedio en las Tierras Inhóspitas no estaban muy alejadas de la realidad después de todo...$B$BEn cualquier caso, el prospector lleva un tiempo preocupado por el estado de nuestro campamento, así que debería ponerme manos a la obra pronto si es posible.$B$BEl principal campamento de ogros está en la Gruta Rotapolvo, una buena caminata hacia el oeste desde aquí.",
    ["O"] = "Consigue 7 restos de armadura para Sigrun.",
    ["T"] = "¡A gorronear!",
  },
  [734] = {
    ["D"] = "¡Pero qué maravilla!$B$B<Lotwil te dedica una mirada breve antes de centrarse de nuevo en sus grilletes.$B$BVe a hablar con Lucien, $N. Necesito un piñón del n.º 5. ¿Me lo traerás?",
    ["O"] = "Habla con Lucien Borlallave en las Tierras Inhóspitas.",
    ["T"] = "Lo tienes crudo",
  },
  [736] = {
    ["D"] = "¿Necesitas el Compendio de Yagyin? Así que, ¿tu plan es reunir a otro grupo? Claro que sí... y por supuesto te ayudaré, $Ghermano:hermana;.$B$BPero tendrás que darme algo a cambio.$B$BNecesito tres objetos de poder. Tráemelos y el compendio será tuyo.$B$BNo puedo decir en alto sus nombres así que te los escribiré.$B$BSi me consigues los objetos de esta lista, serás verdaderamente $Gmerecedor:merecedora; de crear tu propia sección dentro de nuestra orden.",
    ["O"] = "Llévale los objetos que aparecen en la nota de Bel\'dugur al vigilante Bel\'dugur de Entrañas.",
    ["T"] = "La Estrella, la Mano y el Corazón",
  },
  [737] = {
    ["D"] = "Aquí tienes la revista, $N. Huelga decir que debes extremar el cuidado con ella. Además de mantenerla alejada de manos enemigas.$B$BEste cuaderno contiene muchas verdades...$B$B... Y la verdad puede encender chispas.",
    ["O"] = "Llévale el compendio de Yagyin a Theldurin.",
    ["T"] = "El saber prohibido",
  },
  [739] = {
    ["D"] = "La pista de Agmond arranca en el oeste. Es fácil saber por las manchas de sangre en las rocas y en la arena que el enano se arrastró cierta distancia.$B$BY murió aquí.$B$BTras una inspección más detenida, se descubre la palabra \"Murdaloc\" toscamente garabateada en un pedazo de arenisca de las Tierras Inhóspitas.$B$BGarabateada junto a la inerte mano de Agmond.",
    ["O"] = "Mata al asesino de Agmond, Murdaloc.$B$BMata a 12 Cascahuesos Petracripta.$B$BPreséntate ante el prospector Vetaferro en Loch Modan.",
    ["T"] = "Murdaloc",
  },
  [741] = {
    ["D"] = "¿Así que el prospector quiere mandar el misterioso fósil a la Liga de Expedicionarios de Darnassus? No me gusta la idea de irme de Costa Oscura sin él.$B$BTen, $N, ve a Darnassus y entrega el misterioso fósil al arqueólogo jefe Mostachogris.",
    ["O"] = "Ve a Darnassus y entrega el fósil misterioso al arqueólogo jefe Mostachogris.",
    ["T"] = "El prospector despistado",
  },
  [742] = {
    ["D"] = "¡Atención, jóvenes aventureros! ¡Los páramos de Vallefresno os esperan!$B$BLa Horda ha aumentado su presencia en las tierras al norte de Los Baldíos. Nuestras dos avanzadas, Puesto del Hachazo y La Ensenada de Zoram, se esfuerzan por traer gloria a la Horda. Aquellos que deseen demostrar su valía deben buscar ayuda allí. Nota: Senani Corazón Atronador del Puesto del Hachazo, al norte de Los Baldíos, busca a aventureros que deseen participar en la gran Caza de Vallefresno.",
    ["O"] = "Habla con Senani Corazón Atronador en Puesto del Hachazo, en Vallefresno.",
    ["T"] = "La Caza de Vallefresno",
  },
  [743] = {
    ["D"] = "Te agradezco que te pares a hablar conmigo, $N. Me llamo Ruul y soy guerrero y maestro.$B$BHas alcanzado la edad en la que debes prepararte para las pruebas que te esperan si quieres ir más allá de Mulgore.$B$BPara poner a prueba tu fuerza, ve al sureste a enfrentarte a las arpías Viento Furioso. Sus nidos están en los bordes de la montaña, alejados del camino.$B$BSon enemigas naturales de Mulgore y serán un buen indicativo de tu habilidad.",
    ["O"] = "Lleva 8 garfas de Viento Furioso a Ruul Garra de Águila al Poblado Pezuña de Sangre.",
    ["T"] = "Las Viento Furioso son un peligro",
  },
  [744] = {
    ["D"] = "$N, mi hermano pronto se personará ante el jefe Pezuña de Sangre y será un honor para mí prepararle su penacho.$B$BMe gustaría pedirte un favor mientras acabo de curtir estas cintas de piel. No he podido reunir todas las plumas que necesito y me preguntaba si tú podrías ayudarme a conseguir más.$B$BPuedes obtener las plumas del tamaño adecuado de las arpías que habitan al norte y noroeste de Cima del Trueno. Solo necesito 6 plumas azur y 6 de bronce para completar mi diseño.",
    ["O"] = "Consigue 6 plumas azur y 6 de bronce.",
    ["T"] = "Preparación para la ceremonia",
  },
  [745] = {
    ["D"] = "$N, esta tierra está herida por muchos conflictos. Espero que no tengas que presenciar tantos como yo en mi vida. Sin embargo, te pido que vayas a uno. Verás lo peligrosa que puede ser cualquier amenaza, por pequeña que sea, si no se ataja de inmediato.$B$BUnos gnolls Crines Pálidas se han instalado al sur de Pezuña de Sangre y en una cueva del oeste. No respetan nuestros intentos de establecer comunicación y matan a placer por todo Mulgore.$B$BLas palabras ya no sirven de nada.",
    ["O"] = "Mata a 10 curtidores, 8 desolladores y 5 furtivos Crines Pálidas y vuelve al Poblado Pezuña de Sangre para hablar con Baine Pezuña de Sangre.",
    ["T"] = "Compartir la tierra",
  },
  [746] = {
    ["D"] = "Al noroeste hay enanos preparando una excavación. Creen que la tierra está llena de secretos y eso es cierto, pero perforarla no es la mejor manera de descubrirlos.$B$BEncuentra las herramientas de los enanos, rómpelas y tráeme los pedazos.$B$BNecesitarás una forja para romperlas. Hay una en Cima del Trueno, pero te servirá cualquiera, incluso la del campamento enano.",
    ["O"] = "Recoge 5 picos de prospector. $B$BBusca una forja para destrozar los picos. $B$BLleva 5 trozos de herramienta rota a Baine Pezuña de Sangre al Poblado Pezuña de Sangre.",
    ["T"] = "Una excavación de los enanos",
  },
  [747] = {
    ["D"] = "Tienes un aire prometedor, demostrarás tu valía ante la tribu. Puede que pronto te acojan en la gran ciudad de Cima del Trueno. Pero antes de eso, tendrás que mostrar tu valía ante mi padre, el Jefe Viento de Halcón.$B$BAquí en Mesa de la Nube Roja estamos orgullosos de nuestra habilidad para la caza. Los tauren cazamos por necesidad y por deporte. Nos estamos quedando sin carne y necesitamos plumas para la ropa. Caza zancudos de llanura y demuestra tu valor mientras reabasteces al poblado.",
    ["O"] = "Grull Viento de Halcón quiere que le lleves 7 plumas y 7 piezas de carne de zancudo de llanura al Campamento Narache.",
    ["T"] = "Empieza la caza",
  },
  [748] = {
    ["D"] = "¡Los goblins han mancillado nuestros pozos de agua sagrada! No podemos permitirlo.$B$BPara limpiarlos necesito un tótem de limpieza; tú lo llevarás al pozo y realizarás el ritual de limpieza.$B$BEl primer tótem es para el Pozo Pezuña Invernal. Necesitamos la ayuda de la tierra. Tráeme zarpas de lobo de pradera y espolones de zancudo de llanura adulto. Los encontrarás al suroeste.$B$BSé prudente, $N.",
    ["O"] = "Lleva 6 zarpas de lobo de pradera y 4 garfas de zancudo de llanura a Mull Tronacuerno al Poblado Pezuña de Sangre.",
    ["T"] = "Pozos envenenados",
  },
  [749] = {
    ["D"] = "Hace unos días nos encontramos una caravana escoltada de Ventura y Cía. en la costa norte del Lago Toro de Piedra. En un combate anterior habíamos perdido a dos de los nuestros, así que atacamos la caravana.$B$BDejamos las cajas porque lo incendiamos todo, pero hemos visto a rescatadores de Ventura y Cía. cerca de las carretas quemadas; quizás quedó algo de valor.$B$BPodrías acercarte a ver qué hay en las cajas.",
    ["O"] = "Morin Acechanubes quiere que registres las cajas de la Caravana Devastada.",
    ["T"] = "La Caravana Devastada",
  },
  [750] = {
    ["D"] = "Un buen cazador tauren sabe que la presa no es un mero trofeo. Los animales de la llanura son nuestro medio de subsistencia. Si quieres impresionar a los ancestros, trae algunas pelambres de puma montés; son muy valiosas. Los pumas viven en esas colinas hacia el sur.$B$BLos niños necesitan ropa y tenemos que reparar las carpas.",
    ["O"] = "Grull Viento de Halcón quiere que le lleves 10 pelambres de puma montés al Campamento Narache.",
    ["T"] = "La caza sigue",
  },
  [752] = {
    ["D"] = "Todos los miembros de la tribu contribuyen a la armonía de la vida. Vivimos juntos y trabajamos juntos.$B$BCada uno de nosotros es responsable del bienestar de los demás. Quisiera pedirte un pequeño favor.$B$BMi madre se fue esta mañana a coger agua del pozo que hay al sureste de Narache. Ya ha pasado un buen rato. ¿Podrías ir a buscarla mientras yo atiendo los asuntos de la tribu?",
    ["O"] = "El Jefe Viento de Halcón quiere que vayas a buscar a su madre cerca del pozo que hay al sureste del Campamento Narache.",
    ["T"] = "Una humilde contribución",
  },
  [753] = {
    ["D"] = "He recorrido muchos caminos en la vida, mis piernas ya no son fuertes. Todavía puedo ser útil para la tribu, es solo que tardo un poco más en hacer las cosas.$B$BPero tú rebosas vitalidad, $C. Vamos a ponerla a prueba; llena un cántaro en el pozo y llévaselo a mi hijo, el jefe del Campamento Narache.$B$BRecuerda que incluso el más insignificante servicio puede valerte el respeto de los ancestros.",
    ["O"] = "Llena un cántaro de agua en el pozo. $B$BLlévaselo al Jefe Viento de Halcón al Campamento Narache, que está al noroeste del pozo.",
    ["T"] = "Una humilde contribución",
  },
  [754] = {
    ["D"] = "Este es el tótem para el Pozo Pezuña Invernal. Llévalo al pozo y lleva a cabo el ritual de limpieza. El pozo está hacia el sureste y en poder de los repugnantes goblins.$B$BEs una misión difícil, pero no podemos perder el acceso a los pozos.$B$BSuerte, $N.",
    ["O"] = "Usa el tótem de limpieza en el Pozo Pezuña Invernal y ve a ver a Mull Tronacuerno.",
    ["T"] = "La purificación de Pezuña Invernal",
  },
  [755] = {
    ["D"] = "Tu buena disposición y entusiasmo por ayudar a los tauren de Narache te ennoblecen, $N. Creo que un día se te aclamará en Cima del Trueno con grandes honores como $C.$B$BPero antes de eso, debes cumplir con los tres Ritos de la Madre Tierra.$B$BLa primera prueba es el Rito de la Fuerza. Ve a ver al vidente Lenguagris y dile que vas de parte del Jefe Viento de Halcón.$B$BSu morada está al sur del Campamento Narache, escondida entre las colinas.",
    ["O"] = "Ve a ver al vidente Lenguagris, que vive en las colinas al sur del Campamento Narache.",
    ["T"] = "Los ritos de la Madre Tierra",
  },
  [756] = {
    ["D"] = "Para crear el tótem para el Pozo Tronacuerno necesitamos las garras de los depredadores de Mulgore. Tráeme garras de acechador de pradera y puma de las estepas.$B$BLos encontrarás tanto hacia el este como hacia el oeste.",
    ["O"] = "Lleva 6 garras de acechador y 6 garras de puma a Mull Tronacuerno.",
    ["T"] = "El tótem de Tronacuerno",
  },
  [757] = {
    ["D"] = "Los Ritos de la Madre Tierra son los pasos con los que un joven tauren se gana el respeto de Cima del Trueno.$B$BEl primero que debes superar es el Rito de la Fuerza, con el que pruebas tu valor al matar enemigos de la tribu.$B$BLos Erizapúas del Barranco Cortazarza se están instalando en nuestro terreno de caza tribal. Atacan por emboscada a nuestros cazadores y roban en el poblado durante la noche.$B$BPara demostrar tu valor, acaba con esos villanos y entrega sus cinturones al jefe del Campamento Narache como prueba.",
    ["O"] = "Mata a varios Erizapúas en el Barranco Cortazarza y lleva 12 cinturones Erizapúas al Jefe Viento de Halcón al Campamento Narache.",
    ["T"] = "Rito de la fuerza",
  },
  [758] = {
    ["D"] = "$N, el tótem ya está listo. Debes llevar a cabo el ritual una vez más.$B$BLlévate el tótem al Pozo Tronacuerno, que está al norte. Vuelve aquí cuando termines.$B$BQue los espíritus te acompañen.",
    ["O"] = "Usa el tótem de limpieza en el Pozo Tronacuerno y ve a ver a Mull Tronacuerno.",
    ["T"] = "La purificación de Tronacuerno",
  },
  [759] = {
    ["D"] = "Ya solo queda por limpiar el Pozo Ferocrín. Para ello, la tierra debe ofrecernos los dientes de un depredador feroz, el lobo alfa de pradera. Los encontrarás al norte. Vuelve cuando hayas conseguido los dientes.$B$BLos alfa suelen deambular alrededor de nuestra ciudad, Cima del Trueno.",
    ["O"] = "Lleva 8 dientes de alfa de pradera a Mull Tronacuerno al Poblado Pezuña de Sangre.",
    ["T"] = "El tótem Ferocrín",
  },
  [760] = {
    ["D"] = "Los espíritus están complacidos contigo por la limpieza del Pozo Tronacuerno y el Pozo Pezuña Invernal, joven $N. El tótem de Ferocrín ya está listo y tu última tarea te espera.$B$BEl Pozo Ferocrín está al norte de Cima del Trueno. ¡Lleva a cabo el ritual! ¡Limpia la tierra del veneno de los Tótem Siniestro! Que las aguas vuelvan a fluir puras y claras.",
    ["O"] = "Usa el tótem de limpieza Ferocrín en el Pozo Ferocrín.",
    ["T"] = "La purificación de Ferocrín",
  },
  [761] = {
    ["D"] = "El aguilón es un ave muy astuta, difícil de encontrar y de cazar. Un trofeo de plumas de aguilón sería una prueba de tu talento cazador.$B$BVe a las llanuras y caza uno.$B$BTráeme las plumas y gana honor para tu clan.$B$BEstán por todo Mulgore, pero tendrás que estar ojo avizor para verlos y muy veloz para cazarlos.",
    ["O"] = "Lleva 8 péñolas trofeo de aguilón a Harken Tótem de Viento al Poblado Pezuña de Sangre.",
    ["T"] = "La caza de los aguilones",
  },
  [762] = {
    ["D"] = "El propio Ragnaros envió al embajador Infernus a la Fortaleza de Angor para controlar a los Forjatiniebla en su búsqueda de artefactos perdidos en Uldaman.$B$BHa supuesto ya gran número de bajas entre nuestra gente pero, a todas luces, ha empeorado. Dudo en adelantar una cifra respecto a cuántos artefactos habrán encontrado y usarán contra Forjaz.$B$BNuestro objetivo es sencillo, $C. Vuelve a Tierras Inhóspitas y acaba con el embajador Infernus. Una vez eliminada la amenaza, vuelve con el consejero Belgrum con una prueba de tu victoria.$B$BAhora todo depende de ti.",
    ["O"] = "Mata al embajador Infernus en la Fortaleza de Angor y llévale la prueba de su muerte al consejero Belgrum en Forjaz.",
    ["T"] = "Un embajador del mal",
  },
  [763] = {
    ["D"] = "Si quieres seguir con los Ritos de la Madre Tierra tendrás que pasar otras dos pruebas. Debes ampliar tu experiencia, $N.$B$BVe al Poblado Pezuña de Sangre y habla con el Jefe Baine Pezuña de Sangre, hijo de Cairne Pezuña de Sangre. Allí podrás seguir tu viaje y ganarte la aceptación de los ancestros de Cima del Trueno.$B$BDale a Baine Pezuña de Sangre este tótem. Reconocerá mis grabados y te ayudará.$B$BSigue el camino que sale del Campamento Narache. Viaja deprisa y no te desvíes o te perderás.",
    ["O"] = "Lleva el tótem de Alalcón a Baine Pezuña de Sangre al Poblado Pezuña de Sangre. Sigue el camino que sale del Campamento Narache.",
    ["T"] = "Los ritos de la Madre Tierra",
  },
  [764] = {
    ["D"] = "Esto explica el aumento de personal y equipamiento de Ventura y Cía. que hemos visto en Mulgore. Esos goblins... su empresa se expande demasiado rápido y contra su conveniencia. Ya conoces el dicho, enséñale algo a un goblin, y escucharás el sonido de fondo de la balanza.$B$BSon unos bichejos de lo más codiciosos. Por desgracia para sus negocios, los tauren no permitiremos que lleven a cabo sus operaciones en nuestras tierras. Dirígete a su mina, al noreste de la caravana saqueada y envíales un mensaje.",
    ["O"] = "Mata a 14 trabajadores de Ventura y Cía. y a 6 supervisores de Ventura y Cía. para Morin Acechanubes en Poblado Pezuña de Sangre.",
    ["T"] = "Los Ventura y Cía.",
  },
  [765] = {
    ["D"] = "Según los documentos que recuperaste de la caravana, Ventura y Cía. ideó varios planes para tratar de echarnos de nuestras tierras, y así tener libertad total para saquearlas.$B$BLos límites a los que estaban dispuestos a llegar para conseguir sus objetivos me asquean. Quiero ver muerto al cerebro que ideó estos planes. Se llama supervisor Saltatuercas y lo encontrarás en la Mina Ventura y Cía., al este de la caravana saqueada. Tráeme su sujetapapeles y veremos si encontramos algo útil.",
    ["O"] = "Mata al supervisor Saltatuercas y lleva su sujetapapeles a Morin Acechanubes al Poblado Pezuña de Sangre.",
    ["T"] = "Supervisor Saltatuercas",
  },
  [766] = {
    ["D"] = "Mi abuelo solía contarme historias sobre sus batallas con un zancudo llamado Mazzranache. Me hablaba de sus demoníacos ojos rojos, de sus afiladas garras y de su mordedura venenosa.$B$BLa casualidad quiso que, mientras viajaba por las llanuras, me lo encontrase; y me diese un terrible mordisco en el hombro. Era tan espantoso como mi abuelo solía describirlo. Para desinfectar la herida necesito unas cuantas cosas: un corazón de lobo, un fémur de puma, escamas de zancudo de llanura y una molleja.$B$BApresúrate, el tiempo corre.",
    ["O"] = "Lleva un corazón de lobo de pradera, un fémur de puma de las estepas, una escama de zancudo de llanura y una molleja de aguilón a Maur Clamalluvias en el Poblado Pezuña de Sangre.",
    ["T"] = "Mazzranache",
  },
  [770] = {
    ["D"] = "La piel destrozada de Aullido Fantasma muestra la terrible herida que recibió en combate contra la Legión Ardiente.$B$BHabría que avisar de su muerte en el Campamento Rasguño de Sol...",
    ["O"] = "Busca a alguien que conozca a Aullido Fantasma.",
    ["T"] = "La capa del demonio marcado",
  },
  [771] = {
    ["D"] = "Para superar el Rito de la Visión debes conseguir los componentes necesarios para crear el Agua de los Videntes.$B$BNecesito piedras de pozo, que encontrarás cerca de los pozos de Mulgore. Las mezclaré con bellota ámbar, que cae de los orgullosos árboles de nuestra tierra.$B$BEstos ingredientes son mágicos; tómate el elixir resultante junto a la hoguera tribal para tener una visión. Síguela y te llevará al siguiente paso de esta misión sagrada.",
    ["O"] = "Lleva 2 piedras de pozo y 2 bellotas ámbar a Zarlman Dos Lunas al Poblado Pezuña de Sangre.",
    ["T"] = "Rito de la Visión",
  },
  [772] = {
    ["D"] = "Aquí tienes el Agua de los Videntes.$B$BTómatela cerca de la hoguera tribal. Al cabo de un momento la visión se materializará junto a la hoguera.$B$BA partir de ahí, de ti depende el seguirla...",
    ["O"] = "Bebe el Agua de los Videntes frente a la hoguera tribal del Poblado Pezuña de Sangre y sigue a la visión cuando aparezca.",
    ["T"] = "Rito de la Visión",
  },
  [773] = {
    ["D"] = "Para que los ancestros de Cima del Trueno te acepten, tienes que superar el Rito de la Sabiduría.$B$BAhora que has superado el Rito de la Visión, los espíritus ancestrales de Roca Roja te darán las bendiciones de los ancestros. Solo quien ha bebido el Agua de los Videntes merece recibir la bendición.$B$BVe a Roca Roja, al este de Cima del Trueno, y busca al espíritu ancestral, $N.",
    ["O"] = "Ve a Roca Roja, al este de Cima del Trueno, y busca al espíritu ancestral.",
    ["T"] = "Rito de la Sabiduría",
  },
  [774] = "_",
  [775] = {
    ["D"] = "Se te venerará en nuestra gran ciudad. Te mereces tal honor, $R, pues has completado los Ritos de la Madre Tierra.$B$BTu última misión consiste en buscar a nuestro noble jefe, Cairne Pezuña de Sangre, que vive en la cima de la mesa más alta de Cima del Trueno.",
    ["O"] = "Habla con Cairne Pezuña de Sangre en Cima del Trueno.",
    ["T"] = "Viaje a Cima del Trueno",
  },
  [776] = {
    ["D"] = "Has superado los Ritos de la Madre Tierra y te has ganado un lugar en Cima del Trueno.$B$BPero no puedes dormirte en los laureles; debes probar tu valor constantemente.$B$BSomos cazadores, $N. En Mulgore vive un hermoso kodo que se llama Arra\'chea. Tráeme el cuerno de Arra\'chea para demostrarme tu habilidad para rastrear y cazar.",
    ["O"] = "Cairne Pezuña de Sangre de Cima del Trueno quiere que le lleves el cuerno de Arra\'chea.",
    ["T"] = "Los ritos de la Madre Tierra",
  },
  [778] = {
    ["D"] = "¡Ea! ¡Ya está! ¡Los grilletes están listos!$B$BEs la hora, Lucien. ¡Es la hora, Servo!$B$B¿Estás $Gpreparado:preparada; para ser testigo de la perfecta combinación de alquimia, ingeniería y lo arcano, $N?$B$BAhora voy a invocar a un poderoso elemental. Este es un día marcado por la innovación para todos nosotros.",
    ["O"] = "Derrota al guardián Fam\'retor y devuélvele los grilletes de vínculo elemental a Lotwil Veriatus.",
    ["T"] = "Lo tienes crudo",
  },
  [780] = {
    ["D"] = "Los jabaguerreros del Barranco Cortazarza se están instalando en nuestro terreno de caza tribal. Los jabaespines Erizapúas, con quienes estamos en guerra, los amaestran para aumentar su ferocidad.$B$BAcaba con esas bestias y trae algunos hocicos y costillares para hacer caldo.",
    ["O"] = "Grull Viento de Halcón quiere que le lleves 8 hocicos y 8 costillares de jabaguerrero al Campamento Narache.",
    ["T"] = "Los jabaguerreros",
  },
  [781] = {
    ["D"] = "Tras estudiar cuidadosamente el mapa, te das cuenta de que lo que has descubierto son los planes del jefe Erizapúas para un asedio prolongado sobre el Campamento Narache.$B$B¡Seguro que al jefe Viento de Halcón le hace falta esta información!",
    ["O"] = "Lleva los planes de ataque Erizapúas al jefe Viento de Halcón al Campamento Narache.",
    ["T"] = "Ataque al Campamento Narache",
  },
  [782] = {
    ["D"] = "Por la alianza de la Horda con el dragón Alamuerte durante la Segunda Guerra, sabemos que dos de sus tenientes, Latigoscuro y Hematus, estaban encarcelados en el Barranco Lethlor, un lugar remoto hacia el este. ¡Debemos acallar a nuestros depravados aliados ancestrales para restaurar el honor de la Horda!$B$BContratamos a Tho\'grun y a su grupo de ogros mercenarios para que nos ayuden a conseguir el Signo de la Tierra, una llave necesaria para desbloquear la cárcel de los dragones. Pero Tho\'grun nos traicionó, nos aplastó y se lo quedó. ¡Ve al Asentamiento Boff y recupéralo!",
    ["O"] = "Mata al jefe Tho\'grun y llévale el Signo de la Tierra a Gorn en Kargath.",
    ["T"] = "Alianzas rotas",
  },
  [783] = {
    ["D"] = "Nos encontramos en un buen aprieto, joven $C. Alamuerte y sus ejércitos del Crepúsculo han lanzado una ofensiva en Azeroth. El rey ha instado a los ciudadanos capacitados de la Alianza a unirse a la lucha.$B$BLos guardias de Ventormenta no dan abasto, ahora que muchos de nosotros están lejos y hay tantas amenazas. Por eso estamos reclutando a quien esté dispuesto a defender su tierra.$B$BSi vienes a luchar, habla con mi superior, el alguacil McBride. Está ahí atrás, en la abadía.",
    ["O"] = "Habla con el alguacil McBride.",
    ["T"] = "Una amenaza del interior",
  },
  [784] = {
    ["D"] = "Bajo el mando del almirante Valiente, los humanos de Kul Tiras invadieron Durotar, violando el pacto que el Jefe de Guerra selló con Jaina Valiente años atrás para derrotar a Archimonde.$B$BEl ataque humano fue rechazado y el Fuerte de Tiragarde cayó. Pero las reservas del Almirante, comandadas por el teniente Benedict, han retomado el fuerte y vuelven a amenazar nuestro territorio. Los humanos no tienen respeto alguno por la diplomacia.$B$BPor tu honor, viaja al sur hasta el Fuerte de Tiragarde y acaba con los invasores humanos.",
    ["O"] = "Mata a 10 navegantes Kul Tiras, 8 marineros Kul Tiras y al teniente Benedict. Luego vuelve a buscar a Gar\'Thok a Cerrotajo.",
    ["T"] = "Vencer a los traidores",
  },
  [785] = {
    ["D"] = "Thrall es un líder sabio y noble. Su tenacidad en el campo de batalla solo es comparable a su diplomacia.$B$BLos trols Lanza Negra han demostrado ser unos valiosos aliados para los orcos. Hemos sellado un pacto de protección con ellos basado en la lealtad y el honor.$B$BEl Jefe de Guerra ha dado instrucciones de que nos aseguremos que nuestros amigos Lanza Negra del Poblado Sen\'jin no están bajo amenaza directa. Uno de sus exploradores de confianza, Lar Colmillo Acechador está vigilando el flanco occidental.$B$BBusca a Lar y ayúdale en lo que necesite.",
    ["O"] = "Busca a Lar Colmillo Acechador en el flanco oeste del Poblado Sen\'jin.",
    ["T"] = "Una alianza estratégica",
  },
  [786] = {
    ["D"] = "Baja la voz, $C. Los centauros Kolkar están justo al otro lado de este promontorio, al oeste, en el Risco Kolkar.$B$BLa otra noche, cuando estaban fuera, en una de sus batidas, me colé en su poblado y descubrí que esas bestias inmundas tienen planeado un ataque en tres oleadas contra los trols y orcos de Durotar.$B$BDebemos frustrar sus planes de invasión. Quizás tú puedas reunir la fuerza necesaria para infiltrarte en el Risco Kolkar y destruir sus planes.$B$BLo último que vi es que los habían repartido entre tres de sus líderes.",
    ["O"] = "Lar Colmillo Acechador, de las afueras del Poblado Sen\'jin, quiere que destruyas los 3 planes de ataque de Risco Kolkar.",
    ["T"] = "Frustrar los planes kolkar",
  },
  [787] = {
    ["D"] = "Throm\'ka, $C. Soy Eitrigg, Thrall me encargó supervisar la instrucción de los reclutas.$B$BLa Horda ya no es lo que era. Hubo un tiempo en el que abandoné sus filas desilusionado por la creciente influencia de los peones, sedientos de poder, de la Legión Ardiente. Durante mi exilio fui hecho prisionero por un grupo de humanos pero me rescató el Jefe de Guerra. Regresé tras oír su visión de una Horda libre de influencia demoníaca y guiada por los chamanes.$B$BGornek te dará más instrucciones.",
    ["O"] = "Preséntate ante Gornek en El Cubil.",
    ["T"] = "La nueva Horda",
  },
  [788] = {
    ["D"] = "El primer punto del día será fortalecer un poco tu espalda. Te podría enviar a Los Baldíos, a cazar kodos, pero, sinceramente, nos eres más útil $Gvivo:viva; que $Gmuerto:muerta;.$B$BCreo que lo mejor será que empieces por los jabalíes jaspeados que encontrarás al norte de aquí.",
    ["O"] = "Mata a 8 jabalíes jaspeados y vuelve con Gornek en El Cubil.",
    ["T"] = "Dientes afilados",
  },
  [789] = {
    ["D"] = "El poderoso guerrero y el torpe novicio... todos han caído bajo la venenosa picadura del escórpido. Encontrarás un gran número de escórpidos al noroeste de aquí. Tráeme 8 de sus colas como prueba de tu destreza en la batalla.$B$BEl antídoto para sus picaduras se hace a base del veneno extraído de sus aguijones. Conservamos a mano grandes cantidades de antídoto para el veneno de escórpido con las que curar sangres jóvenes como la tuya...$B$BPero seguro que a ti no te hace ninguna falta, ¿no es cierto?",
    ["O"] = "Consigue 8 colas de escórpidos obreros para Gornek en El Cubil.",
    ["T"] = "Aguijón de escórpido",
  },
  [790] = {
    ["D"] = "¡$C! Creía que iba morir ahí fuera sin que nadie se enterara. Cuando estaba cazando los escórpidos del valle me crucé con uno que tenía un aspecto especialmente atroz. Me lancé contra él y conseguí asestarle un enorme golpe antes de que se enrollara en mi pierna.$B$BPero no estaba listo para su aguijón, y consiguió deslizarse en mi peto, desgarrando y penetrando en mi piel y derramando mi sangre. Por favor, ¡debes matar a ese escórpido! ¡Hay que defender mi honor! Combatí con él en la meseta que está al Sur.",
    ["O"] = "Mata a Sarkoth y llévale su zarpa a Hana\'zua.",
    ["T"] = "Sarkoth",
  },
  [792] = {
    ["D"] = "Estoy segura de que aprenderás mucho en el Valle de los Retos, joven $C.$B$BFui enviada al Valle para guiarte, pero aquí he descubierto una creciente corrupción...$B$BUn grupo que se llama a sí mismo el Filo Ardiente tiene un aquelarre aquí mismo, en el Valle de los Retos. Se esconden en una cueva al Noreste, y sus familiares viles se han esparcido desde la boca de la cueva sembrando el caos y la confusión.$B$BTe ofrezco tu primera tarea contra el Filo Ardiente: derrotar a esos familiares. Aniquila a tantos como puedas y, si sobrevives, vuelve a verme.",
    ["O"] = "Mata a 8 familiares viles.$B$BVuelve con Zureetha Vistalonga a las puertas de El Cubil.",
    ["T"] = "Familiares viles",
  },
  [793] = {
    ["D"] = "El Sello de la Tierra está controlado por tres piedras rúnicas, que se encuentran en el Pilar de diamante, el Pilar de ópalo y el Pilar de amatista en el Barranco Lethlor. Mediante el Signo de la Tierra, podrás deshacerte de las piedras rúnicas y desbloquear el Sello de la Tierra.$B$BAunque ándate con cuidado, $N, los dragones Negros Latigoscuro y Hematus son de armas tomar. Acaba con ellos y tráeme sus esclavas.$B$BPara llegar al barranco, dirígete hacia los confines orientales de las Tierras Inhóspitas. Y abre bien los ojos porque el barranco es tierra de multitud de dragones.",
    ["O"] = "Usa el Signo de la Tierra para activar el Pilar de diamante, el de ópalo y el de amatista y así obtener las piedras rúnicas.$B$BColoca las piedras rúnicas en el Sello de la Tierra para liberar a Latigoscuro y a Hematus.$B$BMátalos y llévale a Gorn las ataduras de Latigoscuro, las cadenas de Hematus y el Signo de la Tierra.",
    ["T"] = "Alianzas rotas",
  },
  [794] = {
    ["D"] = "Mis adivinaciones me traen visiones de un objeto lleno de poder, oculto en las profundidades del Aquelarre del Filo Ardiente y custodiado por bestias y magia negra.$B$BEs el llamado medallón del Filo Ardiente y tu siguiente tarea es sacarlo del Aquelarre.$B$BPero ten mucha cautela, el medallón puede estar en posesión de un agente del Filo Ardiente y su poder podría ser mayor que el del resto de los familiares con los que te hayas encontrado.$B$BVete, $N. Encontrarás el Aquelarre en una cueva, al Norte y al Oeste.",
    ["O"] = "Lleva el medallón del Filo Ardiente a Zureetha Vistalonga, a las puertas de El Cubil.",
    ["T"] = "Medallón del Filo Ardiente",
  },
  [796] = "_",
  [797] = "_",
  [798] = "_",
  [799] = "_",
  [800] = "_",
  [801] = "_",
  [802] = "_",
  [803] = "_",
  [804] = {
    ["D"] = "La hazaña que has hecho por mí llena de valor mi corazón. ¡No puedo caer tan fácilmente! ¡Debo resistir!$B$BPero lo cierto es que no puedo hacer el camino de vuelta a El Cubil sin ayuda. Por favor, $N, vuelve a El Cubil e informa a Gornek de mi situación. Quizás él pueda ayudarme.",
    ["O"] = "Informa de la situación de Hana\'zua a Gornek en El Cubil.",
    ["T"] = "Sarkoth",
  },
  [805] = {
    ["D"] = "Tus retos contra el Filo Ardiente han terminado... aquí, en el Valle. Pero quiero que informes sobre todo lo que has descubierto.$B$BVe al Poblado trol Sen\'jin y busca al maestro Gadrin. Poblado Sen\'jin está al este del valle, y a la derecha cuando llegues al cruce.$B$BInforma a Gadrin sobre el Filo Ardiente y dile que han alcanzado el Valle de los Retos. Con él, averigua si también han llegado a Sen\'jin.$B$BEn marcha, $N, y sé $Grápido:rápida;. Temo que el mal del Aquelarre del Filo Ardiente solo sea el heraldo de una mayor amenaza...",
    ["O"] = "Habla con el maestro Gadrin en el Poblado Sen\'jin.",
    ["T"] = "Preséntate en el Poblado Sen\'jin",
  },
  [806] = {
    ["D"] = "¡No podemos permitir que el Filo Ardiente se establezca en Durotar! ¡Debemos acabar con él antes de que extienda su maldad!$B$BYo conduje mi propia investigación y averigüé que un brujo del Filo Ardiente, un goblin llamado Fizzle Tormenta Oscura, ha acampado en el Monte del Trueno, al noroeste. Ahí es donde están propagando el caos él y sus esbirros.$B$BEncuentra a Fizzle y acaba con él. ¡Y tráeme una zarpa suya!",
    ["O"] = "Lleva la zarpa de Fizzle a Orgnil Escaralma a Cerrotajo.",
    ["T"] = "Oscuras tormentas",
  },
  [808] = {
    ["D"] = "En mis sueños oigo la voz de mi hermano Minshina llamándome.$B$BSe lo llevó Zalazane, el brujo de las Islas del Eco que hay al este. Y ahora está muerto.$B$BPero la muerte no lo ha liberado. Zalazane utilizó su magia para atrapar el espíritu de Minshina en su propia calavera. En mi sueño, lo veo con otras calaveras, en un círculo de poder que está en la más grande de las Islas del Eco. Mientras siga allí, el alma de mi hermano estará maldita.$B$BTe lo ruego, $N. Encuentra el círculo y recupera la calavera de Minshina. Luego tráemela.$B$B¡Libera a mi hermano!",
    ["O"] = "Recupera la calavera de Minshina del círculo de poder de las Islas del Eco.$B$BLlévasela al maestro Gadrin al Poblado Sen\'jin.",
    ["T"] = "Calavera de Minshina",
  },
  [809] = {
    ["D"] = "Desde la última Gran Guerra, aquella en la que la Legión Ardiente fue derrotada, he buscado fuentes de corrupción demoníaca entre los orcos. El collar que me has traído confirma mis temores.$B$BPertenece al Filo Ardiente, un culto que adora un objeto de gran poder demoníaco. Se llama Semilla del Demonio y se encuentra en Los Baldíos, en la Cima Calígine. ¡Debe aniquilarla!$B$BVe a la Avanzada del Puente, hacia el oeste en la frontera de Los Baldíos y habla con mi ayudante, Ak\'Zeloth. Él te guiará.",
    ["O"] = "Habla con Ak\'Zeloth en Los Baldíos.",
    ["T"] = "Ak\'Zeloth",
  },
  [810] = "_",
  [811] = "_",
  [812] = {
    ["D"] = "$N... llegas en el momento perfecto. Solo espero poder alabar tu velocidad también.$B$BTuve un descuido combatiendo con unos venocolas aquí cerca y uno de ellos me picó. Siento que su veneno me debilita por momentos. A este paso, calculo que me queda una hora de vida. Voy a necesitar tu ayuda...$B$BKor\'ghan, de Orgrimmar, sabe cómo preparar el antídoto. Encuéntralo... y deprisa, $N. No viviré mucho. Debería estar en el Circo de las Sombras.",
    ["O"] = "Encuentra a Kor\'ghan en Orgrimmar y consigue el antídoto para venocola. Luego lleva el antídoto a Rhinag. Está cerca del límite noroeste de Durotar.",
    ["T"] = "Se necesita un remedio",
  },
  [813] = {
    ["D"] = "Los venocolas son los escórpidos más mortíferos de Durotar. Rhinag no debería haber intentado cazar uno sin llevar el antídoto... pero ahora, lo último que necesita son mis reproches.$B$BTráeme unas cuantas glándulas de veneno de cualquiera de los ejemplares de venocola que encontrarás en la entrada de Orgrimmar y yo prepararé el antídoto.$B$BY date prisa, $N. Uno de los nuestros necesita nuestra ayuda.",
    ["O"] = "Lleva 4 glándulas de veneno de venocola a Kor\'ghan a Orgrimmar.",
    ["T"] = "En busca del antídoto",
  },
  [814] = "_",
  [815] = {
    ["D"] = "¡Ay! Casi me olvido de que tengo que preparar el desayuno.$B$B$N, muévete y tráeme unos huevos de colazote. Necesitaré por lo menos tres si quiero tener suficientes para la comida de mañana.$B$BLos colazotes Garrasangre son animales fieros y protegen sus huevos con la vida. A veces los entierran a gran profundidad y es difícil sacarlos sin correr peligro. Si te diriges a las Islas del Eco, al sureste del Poblado Sen\'jin, encontrarás muchos.",
    ["O"] = "Lleva 3 huevos de colazote al cocinero Torka de Cerrotajo.",
    ["T"] = "Romper huevos",
  },
  [816] = {
    ["D"] = "$N, por favor, ¿podrías ayudarme? Mi hijo Kron fue a cazar hace días y aún no ha regresado. Fue hacia el oeste, hacia Los Baldíos, a cazar crocoliscos en el Río Furia del Sur. Temo lo peor.$B$BEs muy fuerte pero su orgullo le mete siempre en líos. Yo le advertí de que los crocoliscos son fuertes y aviesos. Pero solo conseguí que se fuera enfadado.$B$BSi no puedes encontrarlo, al menos tráeme algún indicio de su suerte... aunque tengas que abrir el estómago de todos los crocoliscos de las orillas del Furia del Sur.",
    ["O"] = "Lleva un indicio del destino de Kron a Misha Tor\'kren, a la granja que hay al noroeste de Cerrotajo.",
    ["T"] = "Perdido, pero no olvidado",
  },
  [817] = {
    ["D"] = "Muchos de los pelajes que usamos provienen de tigres de Durotar, $N. Mantas, armaduras, tiendas... tenemos muchos motivos para cazar a esas bestias y muchos también para dejar que proliferen.$B$BPero ha llegado el momento de hacer una selección en la manada, por decirlo de algún modo. Nuestros números crecen y nuestras necesidades empiezan a superar nuestras reservas. Necesito más pelajes para preparar los bienes que nuestra gente necesita.$B$BTráeme 4 pelajes de tigres de Durotar. Te recompensaré. Puedes encontrarlos en las islas que hay al sur de aquí.",
    ["O"] = "Lleva 4 pelajes de tigres de Durotar a Vel\'rin Colmillo al Poblado Sen\'jin.",
    ["T"] = "Presa práctica",
  },
  [819] = {
    ["D"] = "Un cartelito en uno de los extremos del barril reza,$B$B\"Chen Cerveza de Trueno-$B$BQue tu ánimo se levante y con tu copa siempre brindes.\"",
    ["O"] = "Encuentra a alguien que sepa algo sobre el barril vacío de Chen.",
    ["T"] = "Barril vacío de Chen",
  },
  [821] = {
    ["D"] = "¿Te gustaría probar la cerveza a la que le dio nombre Chen? Ah, la Cerveza de Trueno es un poderoso brebaje. Chen le enseñó la receta a mi mentor y mi mentor, a su vez, me la transmitió. Necesitaré que me consigas unas cosas pero te lo aseguro: el subidón lo merece.$B$BTráeme 5 colmillos de leones de la sabana de cualquier león de la sabana, 5 riñones de zancudo de llanura de cualquier zancudo de llanura y un asta de truenagarto de cualquier especie de truenagarto. Eso debería irnos perfectamente.$B$BPuedes encontrar estos ingredientes por Los Baldíos.",
    ["O"] = "Llévale 5 colmillos de león de la sabana, 5 riñones de zancudo de llanura y 1 cuerno de truenagarto al maestro cervecero Drohn en Trinquete.",
    ["T"] = "Barril vacío de Chen",
  },
  [822] = {
    ["D"] = "De categoría esa Cerveza de Trueno, ¿eh?$B$BTengo otra receta que también aprendí de Chen. ¿Te interesaría? No llevará mucho tiempo prepararla y podría serte de utilidad si vas a seguir con tus aventuras.$B$BTráeme 5 glándulas de relámpago de cualquier tormentero, 1 glándula de saliva de un halcón trueno de los mayores halcones trueno y 1 hígado de kodo de cualquier kodo de Los Baldíos.$B$BComo te he dicho, este brebaje pega; es la cerveza de los troggs de la que te he hablado antes.",
    ["O"] = "Llévale 5 glándulas relampagueantes, 1 glándula salivaria de halcón trueno y 1 hígado de kodo al maestro cervecero Drohn en Trinquete.",
    ["T"] = "Barril vacío de Chen",
  },
  [823] = {
    ["D"] = "En el Poblado Sen\'jin no nos faltan problemas, pero te agradecemos tu ayuda. Claro que las noticias que traes del Filo Ardiente podrían significar más problemas para todo el mundo.$B$BEn Cerrotajo hay un orco llamado Orgnil Escaralma que se encarga de librar Durotar de males como el que representa el Filo Ardiente. Busca a Orgnil y háblale de nuestra situación. Cuéntale también las noticias que traes del Valle de los Retos. Querrá saber esas cosas.$B$BCerrotajo se encuentra al norte.",
    ["O"] = "Habla con Orgnil Escaralma de Cerrotajo.",
    ["T"] = "Informa a Orgnil",
  },
  [824] = {
    ["D"] = "El Anillo de la Tierra es un grupo de chamanes dedicados al estudio y la conservación de los elementos. Les sobra conocimiento sobre lunáticos dioses antiguos, ancestrales leyendas y tradiciones como nunca alcanzará la mayoría de los eruditos.$B$BEstás de suerte, conozco a alguien del Anillo de la Tierra aquí en Vallefresno. Dirígete a la Avanzada de Zoram\'gar, en la costa de La Ensenada de Zoram, y enséñale a Je\'neu Sancrea lo que has encontrado. Si alguien es capaz de interpretar el significado del globo, es él.",
    ["O"] = "Llévale el globo de agua contaminada a Je\'neu Sancrea en Cavernas de Brazanegra.",
    ["T"] = "Je\'neu del Anillo de la Tierra",
  },
  [825] = {
    ["D"] = "Uno de mis mejores exploradores me ha informado de que los restos de la flota naufragada de Valiente aún están en la costa de Durotar, al este del Fuerte de Tiragarde.$B$BNo es un secreto que los humanos, aliados con esas mínimas criaturas llamadas gnomos, han desarrollado sus conocimientos de mecánica. Debemos estudiar a fondo a todos nuestros enemigos potenciales. Y nosotros también nos beneficiaremos de sus conocimientos.$B$BNada entre los restos del naufragio, $N y recupera las herramientas de la Alianza.",
    ["O"] = "Gar\'Thok de Cerrotajo quiere que recuperes 3 herramientas gnómicas de los restos de naufragio de la costa.",
    ["T"] = "Los restos del naufragio...",
  },
  [826] = {
    ["D"] = "Zalazane, el curandero brujo, vive en las Islas del Eco, hacia el este. En otro tiempo, esas islas eran nuestro hogar.$B$BDesde ellas, Zalazane envía a sus trols a la península, a maldecir a nuestra gente y a esclavizarnos bajo su dominio.$B$BHay que detenerle.$B$BDerrota a Zalazane y a sus esbirros, antiguos trols Lanza Negra que hemos dado por perdidos. Tráeme su cabeza. Así sabré que su demoníaco reinado ha llegado a su fin.",
    ["O"] = "Derrota a Zalazane.$B$BMata a 8 trols vudú y 8 trols maléficos.$B$BLleva la cabeza de Zalazane a Gadrin.",
    ["T"] = "Zalazane",
  },
  [827] = {
    ["D"] = "$N, el Filo Ardiente ha infestado la cueva que hay al este de Orgrimmar, Roca del Cráneo. En esa cueva celebran sus inmundos rituales y queman su propia carne con collares abrasadores.$B$BCreo que usando esos collares, los cultores entran en sintonía con los poderes demoníacos. Pero para confirmarlo, necesito estudiar esos collares.$B$BVe a Roca del Cráneo y recoge los collares abrasadores que lleven los cultores. Tráemelos y yo descubriré su secreto.",
    ["O"] = "Recoge los collares abrasadores de los cultores de Roca del Cráneo. $B$BLlévaselos a Margoz a su campamento.",
    ["T"] = "Roca del Cráneo",
  },
  [828] = {
    ["D"] = "Uno de nuestros chamanes, Margoz, sabe más sobre la corrupción del Filo Ardiente. Habla de una cueva de las montañas llamada Roca del Cráneo... ¡justo a las afueras de Orgrimmar! Esa cueva proporciona refugio a una banda de adeptos del Filo Ardiente.$B$BAntes de ir a Roca del Cráneo, habla con Margoz. Es sabio y su consejo es valioso.$B$BEstá acampado hacia el noreste, entre la costa y el Barranco Árido.$B$BSigue su consejo pero, diga lo que diga Margoz, $N, ¡aún quiero que aplastes a esos adeptos!",
    ["O"] = "Habla con Margoz.",
    ["T"] = "Margoz",
  },
  [829] = {
    ["D"] = "Los collares abrasadores que me trajiste son potentes instrumentos demoníacos. Pero me temo que discernir su origen está más allá de mis habilidades chamánicas. Necesitamos que los estudie un brujo.$B$BLleva un collar abrasador a Neeru Hojafuego. Es un brujo muy capacitado que dice que utiliza sus poderes contra los demonios. Asegura que sus investigaciones de lo oculto son beneficiosas. Sea esto cierto o no, creo que necesitaremos su ayuda para luchar contra el culto demoníaco de Durotar.$B$BEncontrarás a Neeru en Orgrimmar, en el Circo de las Sombras.",
    ["O"] = "Lleva un collar de muestra a Neeru Hojafuego a Orgrimmar.",
    ["T"] = "Neeru Hojafuego",
  },
  [831] = {
    ["D"] = "No se puede confiar en los humanos. Luchamos junto a ellos con recelo, sabiendo que algún día nos traicionarían.$B$BLa muerte del almirante Valiente no bastó para eliminar su legado de traición; la escoria humana había preparado sus planes mucho antes de que él muriera.$B$BNi siquiera la muerte de Benedict acabará con esto; quién sabe cuándo llegará la próxima horda de hombres de Valiente.$B$BHay que llevar esta órdenes a Vol\'jin a Orgrimmar de inmediato. Lo encontrarás en la cámara de Thrall.",
    ["O"] = "Entrega las órdenes del almirante Valiente a Vol\'jin a la cámara de Thrall en Orgrimmar.",
    ["T"] = "Las órdenes del almirante",
  },
  [832] = {
    ["D"] = "<Del colgante emanan unas palabras que se introducen en tu mente.>$B$B¿Gazz\'uz?$B$BGazz\'uz... ¡informa!$B$BHay noticias sobre tu descubrimiento en la Roca del Cráneo. ¡Debes prepararte para un ataque!$B$B¡Gazz\'uz! ¡Te ordeno que hables! Habla. O me aseguraré de que llegue a Neeru Hojafuego la noticia de tu presencia y dejaré que descargue sobre ti su brutalidad.$B$BY no creo que quieras medirte con Neeru...$B$B¿Gazz\'uz? ¿Estás ahí...?",
    ["O"] = "Lleva este ojo a Neeru Hojafuego que está en Orgrimmar.",
    ["T"] = "Sombras Ardientes",
  },
  [833] = {
    ["D"] = "Solo los tauren más valientes reposan en Roca Roja, nuestro cementerio sagrado. Es un honor que concedemos a los valientes guerreros que fundaron y defendieron Cima del Trueno y a aquellos que han entregado la vida por el bien de sus tribus y sus jefes.$B$BPero una vil amenaza se cierne sobre nuestra tierra sagrada. Una banda de Erizapúas está arrasando el cementerio; yo ya estoy mayor para enfrentarme a ellos.$B$BHay que emplear la fuerza, $N.",
    ["O"] = "Mata a 8 intrusos Erizapúas en Roca Roja.",
    ["T"] = "Cementerio sagrado",
  },
  [834] = {
    ["D"] = "Soy Rezlak, uno de los chicos de Gazlowe. El jefe me manda a ayudar a los orcos de Durotar. Las cosas han ido bien... excepto para las caravanas. ¡No consigo mantenerlas al seguro! Eso hace mi trabajo un poco más difícil, ¿sabes?$B$BEl último envío –que, dicho sea de paso, prometieron que llegaría– fue robado por arpías Viento Seco de... ¿de dónde era que eran? ¿El Cañón del Ventajo?$B$B¡Necesito conseguir esos suministros! Sigue el cañón hacia el sur. Encontrarás una brecha en las paredes este y oeste.",
    ["O"] = "Recupera 5 sacos de suministros.",
    ["T"] = "Las arpías Viento Seco",
  },
  [835] = {
    ["D"] = "Gazlowe me dijo una vez, \"Rezlak. En la vida hay que saber una cosa: que si quieres algo bien hecho, tienes que hacerlo tú mismo.\"$B$BPues bien, como no me puedo permitir perder todas las caravanas, voy a tomar las riendas del asunto. Las arpías tienen invadidas nuestras rutas de suministros. Si matamos a todas las arpías, las rutas serán más seguras.$B$BY claro, Gazlowe me enseñó algo más: \"Todo tiene un precio.\" Así que, ¿cuento contigo, $N? Bien... Te recomiendo que empieces por el cañón del este.",
    ["O"] = "Mata a 12 arpías de Durotar. No importa el tipo de arpía.",
    ["T"] = "Asegurar las rutas",
  },
  [836] = {
    ["D"] = "Vuelves a oír la voz del gnomo que sale del robot:$B$B\"Tengo que llevar a OOX-09/TI a un lugar abierto y seguro en el que pueda iniciar el largo proceso de despegue. Tiene un dispositivo de invisibilidad integrado, pero necesito tiempo para que vuelva a estar operativo. ¡Escolta al robot hasta, digamos, algunas rocas donde no sea visible! Allí podré tomarme mi tiempo para hacer que todo vuelva a funcionar correctamente.\"$B$B\"¡Escóltalo para que llegue sano y salvo y después ven a verme a Bahía del Botín! Oglethorpe Obnoticus. ¡Corto y cierro!\"",
    ["O"] = "Escolta a OOX-09/TI para que llegue sano y salvo e informa a Oglethorpe Obnoticus en Bahía del Botín.",
    ["T"] = "¡Rescata a OOX-09/TI!",
  },
  [837] = {
    ["D"] = "Cuando llegamos, los jabaespines Crines de Acero poseían gran parte de la tierra... era una espinita que teníamos clavada. Con mucho esfuerzo hemos expulsado a la mayoría, pero aún quedan algunos bien fortificados en algunas zonas.$B$BPero esto ha durado ya demasiado. Por nuestro propio bien, no podemos permitir que haya Crines de Acero en nuestras tierras. Sus campamentos están al oeste de aquí. Busca las zarzas y los encontrarás. Hoy los echaremos de Durotar y mañana... puede que hasta de Kalimdor.",
    ["O"] = "Mata a 4 jabaespines Crines de Acero, 4 exploradores Crines de Acero, 4 polvorosos Crines de Acero y 4 guardias de batalla Crines de Acero para Gar\'Thok, de Cerrotajo.",
    ["T"] = "Invasión",
  },
  [839] = "_",
  [840] = {
    ["D"] = "Mmm, pareces fuerte.$B$BEscucha, mi buen amigo Kargal necesita nuevos reclutas para una misión en Los Baldíos. Sé que quieres servir bien a la Horda. Pues esta es tu oportunidad.$B$BLleva esta carta de reclutamiento a Kargal, a ver si la firma.",
    ["O"] = "Entrega tu carta de reclutamiento a Kargal Marca de Guerra, en la Avanzada de Los Baldíos.",
    ["T"] = "Nuevo recluta de la Horda",
  },
  [841] = {
    ["D"] = "Déjame adivinar... no has conseguido a tiempo lo que fuera que debías conseguir. No, mejor aún... ¡se te cayó en la arena! Bueno, sea lo que sea que ha pasado, tengo más fuentes de energía que puedo venderte. Sin embargo, esta vez te costarán diez, ¡SÍ, DIEZ faltriqueras de agua de Vagayermos cada una!$B$BCorren tiempos difíciles, $N, y las fuentes de energía no crecen en los árboles. Mmm, ¿y qué si lo hicieran? Quizás podríamos HACER un árbol de fuentes de energía...",
    ["O"] = "Llévale 10 faltriquera de agua de Vagayermos al ingeniero jefe Pasaquillas, que está en Gadgetzan, para conseguir otra fuente de alimentación.",
    ["T"] = "¿Otra fuente de alimentación?",
  },
  [842] = {
    ["D"] = "<Kargal extrae una ajada carta de reclutamiento de su bolsillo y la firma. Te la entrega con solemnidad.>$B$BBueno, $Gchico:chica;, has demostrado ser un fichaje formidable. Considérate $Greclutado:reclutada;. Necesitamos a gente como tú en El Cruce, si puedes llegar con vida. Sigue el camino que va hacia el oeste desde aquí, y luego al sur en el cruce de caminos. Si de verdad quieres ayudar a tu nueva Horda, habla con Halga para que te lleven con la siguiente caravana. Cuando llegues, dale esta carta a Sergra Espinoscura.",
    ["O"] = "Entrega tu carta de reclutamiento firmada a Sergra Espinoscura, que está en El Cruce. Si todavía no lo has hecho, puedes pedirle a Halga Ojosangre que te lleven con la siguiente caravana.",
    ["T"] = "Servicio militar en El Cruce",
  },
  [844] = {
    ["D"] = "¿Conoces a los zancudos de llanura? Son unos animales inútiles. No pueden volar. Se dejan cazar. Tienen cerebros del tamaño de mis nudillos. No tienen la sensatez de apartarse del camino de un carro. La de ejes que se me han roto por culpa de sus cráneos... A veces pienso que los jabaespines espantan bandadas de zancudos hacia nuestras caravanas intencionadamente antes de una emboscada. Haz que me sienta orgullosa, $C. Abre un paso sangriento entre esos repugnantes pajarracos para que despejen las carreteras y tráeme sus picos como prueba.",
    ["O"] = "Reúne 7 picos de zancudos de llanura.",
    ["T"] = "La amenaza de los zancudos de llanura",
  },
  [845] = {
    ["D"] = "Si quieres servir a mi amada Horda, $Gnovato:novata;, vas a tener que demostrarme tu fuerza. Puedes entrenarte cazando zhebras. Puede que no sean las bestias más fuertes de Los Baldíos, pero evita sus coces o te arrepentirás.$B$BCaza las zhebras que hay al norte y al sur y recoge sus pezuñas. Tráemelas y a lo mejor te mandamos a por algo más difícil.",
    ["O"] = "Mata a galopadores zhebras para reunir 4 pezuñas de zhebras.",
    ["T"] = "La zhebra",
  },
  [846] = {
    ["D"] = "Es evidente que el prospector dependía de los explosivos y de esos ruidosos vehículos para destrozar el suelo. Cabe esperar que pronto llegará otro Capataz para sustituirlo.$B$BQuiero darles a los enanos un poco de su propia medicina. Según estos planos, si combinamos nitroglicerina con pasta de madera y nitrato sódico, podemos crear una carga explosiva. Es bien sabido que el fusilero, los soldados y los oficiales tienen los suministros.$B$BReúne estos objetos y tráemelos. ¡Voy a preparar una carga que los enanos no olvidarán!",
    ["O"] = "Gann Picopiedra quiere que le traigas 6 viales de nitroglicerina, 6 fajos de pulpa de madera y 6 muestras de nitrato sódico.",
    ["T"] = "La venganza de Gann",
  },
  [847] = {
    ["D"] = "Este... este poder... ¿Es este el poder de los chamanes que Tor\'gan intentó enseñarme? Debo admitirlo, es interesante. Dile a Tor\'gan que me ha convencido... por ahora. Escucharé lo que tenga que decir.",
    ["O"] = "Habla con Tor\'gan en Sentencia, en las Tierras Altas de Arathi.",
    ["T"] = "La astucia del raptor",
  },
  [848] = {
    ["D"] = "Los oasis de Los Baldíos ocultan un misterio. La energía de la vida fluye por sus aguas, revitalizando a las plantas y a los animales que las beben.$B$BRevitalizando... y alterando.$B$BCerca de las Charcas del Olvido, al noroeste, crece un tipo de champiñón. Sus esporas tienen unas propiedades que nosotros, los boticarios de Lordaeron, encontramos útiles.$B$BTráeme esas esporas y te ganarás nuestra gratitud.",
    ["O"] = "Reúne 5 esporas de hongos de los hongos de Las Charcas del Olvido.",
    ["T"] = "Esporas de hongos",
  },
  [849] = {
    ["D"] = "Según los planos de Khazgorm, no debería ser un problema preparar una carga explosiva.$B$BAh, eso es.$B$BBien, $N, voy a confiarte una tarea especial. Lleva este palo explosivo y úsalo para hacer saltar la máquina voladora de Bael Modan.$B$BLa máquina voladora no es solo una parte clave de la infraestructura del sitio sino que se usa además como máquina de guerra. Es más, constituye un icono de poder y un insulto a la memoria de mi tribu.$B$BDame un motivo de orgullo completando esta tarea heroica y recibirás justa recompensa.",
    ["O"] = "Destruye la máquina voladora de Bael Modan y regresa junto a Gann Picopiedra.",
    ["T"] = "La venganza de Gann",
  },
  [850] = {
    ["D"] = "Los centauros han acosado a los tauren durante años. Y recientemente, los centauros Kolkar de Los Baldíos se han convertido en una auténtica amenaza. No solían estar organizados pero han surgido nuevos jefes entre ellos. Y se han unido.$B$BPara mantener nuestras propiedades, hay que acabar con los jefes de los centauros.$B$BTráeme la cabeza de Barak Aterrakodos. Su campamento está al oeste de Las Charcas del Olvido.$B$BDerrótalo y vuelve aquí.",
    ["O"] = "Asesina a Barak Aterrakodos y hazte con su cabeza.",
    ["T"] = "Los jefes Kolkar",
  },
  [851] = {
    ["D"] = "Quizás Verog sea difícil de encontrar, pero a juzgar por la información que has reunido, su compañera Wyneth está siempre en la tienda de mando, justo al oeste de aquí. Si la matamos, Verog saldrá al galope.$B$BRegthar nos envió para observar, pero también dijo que deberíamos aprovechar cualquier oportunidad que surja para desestabilizar la estructura de mando de los centauros. ¡Saca a Verog de su escondite y mátalo!",
    ["O"] = "Mata a Wyneth para atraer a Verog el Derviche, después asesínalo y hazte con su cabeza.",
    ["T"] = "Verog el Derviche",
  },
  [852] = {
    ["D"] = "Hezrul Marcasangre es el jefe de los centauros Kolkar en Los Baldíos. Es feroz, brutal y taimado. Si lo derrotas, sería un golpe contundente para los Kolkar y la amenaza que suponen se vería reducida en gran medida.$B$BAsí que mátalo. Tráeme su cabeza y la colgaré de un poste para que conozcan el miedo.$B$BHezrul dirige a los suyos desde el Oasis Aguaverde, hacia el sur.",
    ["O"] = "Asesina a Hezrul Marcasangre y recoge su cabeza.",
    ["T"] = "Hezrul Marcasangre",
  },
  [853] = {
    ["D"] = "He convertido las esporas que me diste en una emulsión. Debo enviársela a mi socia, la boticaria Zamah. Si se la llevas, seguro que tendrá algo para ti de nuestro fondo de productos alquímicos.$B$BSe encuentra en las Pozas de las Visiones, una caverna en el subsuelo de los chamanes de Cima del Trueno. El camino hacia esta caverna está muy bien escondido pero la encontrarás en el risco del Alto de los Espíritus.$B$BApúrate. Esta emulsión solo conservará su efectividad por poco tiempo y se debe procesar y volver a sellar antes de que eso ocurra.",
    ["O"] = "Llévale las esporas devueltas a la boticaria Zamah en Cima del Trueno antes de que se agote el límite de tiempo.",
    ["T"] = "Boticaria Zamah",
  },
  [854] = {
    ["D"] = "Había vaticinado tu llegada, joven $C. Aunque quizás ya no seas tan joven.$B$BHa llegado el momento de que te internes más en estas tierras. Tu destino es conseguir grandes cosas.$B$BTienes que ir a El Cruce; nuestra vida está ligada a la de los orcos y necesitan ayuda. Sigue este camino hacia el este y después hacia el norte hasta El Cruce. Habla con Thork, que tiene la bendición de la Madre Tierra.",
    ["O"] = "Habla con Thork en El Cruce, en Los Baldíos.",
    ["T"] = "Viaja a El Cruce",
  },
  [855] = {
    ["D"] = "El liderazgo de los centauros depende de un núcleo de guerreros leales y disciplinados que mantengan a raya a los demás. Si diezmamos sus filas, los Kolkar no tendrán fuerzas para contener su ira. De modo que tengo una misión para ti.$B$BMata maleantes y videntormentos Kolkar alrededor del Oasis Aguaverde, al sur, y coge sus brazales. Vuelve cuando tengas unos cuantos y la Horda te recompensará bien.",
    ["O"] = "Llévale 10 brazales centauro de maleantes y videntormentos Kolkar.",
    ["T"] = "Brazales centauro",
  },
  [856] = "_",
  [858] = {
    ["D"] = "¿No te mandará Petardol? Estoy en un apuro. Me metí sin darme cuenta de que necesitaba una llave para desbloquear la columna que mueve la trituradora. Uno de los operarios de la trituradora me preguntó si todo iba bien ¡y me entró el pánico! En lugar de decirle que me faltaba la llave, le dije que había algún tipo de problema mecánico.$B$BTenemos que salir de aquí ¡a paso ligero! Busca en la parte de arriba de la grúa, el Supervisor Palarrastre debería tener una llave para esta trituradora.$B$B¡Ayúdame!",
    ["O"] = "Consigue la llave de contacto del Supervisor Palarrastre.",
    ["T"] = "La llave de contacto",
  },
  [860] = {
    ["D"] = "Si deseas practicar la caza debes dirigirte a Los Baldíos. Las bestias que allí habitan son tozudas y fuertes. Si cazas allí aprenderás mucho y tu espíritu crecerá.$B$BHabla con Sergra Espinoscura pues ella será tu primera guía en Los Baldíos.$B$BSergra está en El Cruce. Para llegar hasta allí, viaja hacia el este desde el Poblado Pezuña de Sangre hasta Los Baldíos y después dirígete hacia el norte cuando el camino se bifurque. Y ten mucho cuidado por el camino pues Los Baldíos pueden ser muy peligrosos para los incautos y confiados.",
    ["O"] = "Habla con Sergra Espinoscura en El Cruce.",
    ["T"] = "Sergra Espinoscura",
  },
  [861] = {
    ["D"] = "Te veo con ganas de explorar. Yo también era así antes...$B$BMe gustaba explorar y cazar. Salir a cazar es un gran honor para los tauren.$B$BSi de verdad quieres dedicarte al arte de la caza, demuéstrame tu habilidad y determinación. Tráeme las garras de los merodeadores de las estepas de Mulgore. Son peligrosos y astutos... digna presa de $Gun:una; joven $C que sigue el camino de la caza.",
    ["O"] = "Reúne 4 garras de merodeador de las estepas.",
    ["T"] = "Los hábitos del cazador",
  },
  [862] = {
    ["D"] = "$GUn:Una; $C debe de pasar hambre por Los Baldíos. Al menos yo, me muero de hambre.$B$B¿Sabes qué? Voy a preparar un rico estofado. Solo necesito unas ratas de madriguera.$B$BAl parecer, a esos condenados enanos de Bael Modan les sobra lo mejor de lo mejor. Dirígete hacia el sur a Bael Modan y tráeme unas ratas de excavación.$B$BNo eres digno de llamarte aventurero curtido ¡hasta que has pasado un tiempo matando ratas! ¡Andando!",
    ["O"] = "Llévale 8 ratas de madriguera a Larva al este de El Cruce.",
    ["T"] = "Estofado de ratas de madriguera",
  },
  [863] = {
    ["D"] = "Supongo que aprenderé sobre la marcha... No puede ser muy difícil. Unos botones por aquí, una palanca o dos... Bueno, ¿$Glisto:lista;?",
    ["O"] = "Protege a Rabiaire y la trituradora goblin robada hasta que salgan de El Fangal.",
    ["T"] = "La fuga",
  },
  [864] = {
    ["D"] = "\"Aquí tienes, la fuente de poder se ha quitado con seguridad y la información se ha mantenido intacta. Bueno, lo de viajes seguros y todo eso. Espero que este nefasto experimento científico haya funcionado para ti y todo eso.\"$B$BEl goblin se aleja para atender sus labores con la Compañía de Aguas de Gadgetzan. Cierras bien el paquete, en preparación de tu viaje a Entrañas. La boticaria Zinge te espera allí para probar el objeto.",
    ["O"] = "Llévale el equipo de prueba de campo sellado a la boticaria Zinge, que está en Entrañas.",
    ["T"] = "Regresa junto a la boticaria Zinge",
  },
  [865] = {
    ["D"] = "Los raptores de Los Baldíos son más inteligentes que los de cualquier otro sitio, y creo que se debe precisamente a sus cuernos. Si es así, quizás podría moler algunos para fabricar \"bebidas de inteligencia\". ¡Podría amasar fortunas!$B$BAhí entras tú: quiero el cuerno del raptor más listo que hay. \"El rey Zarpagarra\" lidera toda una manada de raptores inteligentes en las colinas al norte de Trinquete. ¡Tráeme su cuerno! ¡Y no dejes que te arranquen la piel a tiras como a todo el mundo!",
    ["O"] = "Consigue el cuerno del rey Zarpagarra.",
    ["T"] = "El secreto está en el cuerno",
  },
  [866] = {
    ["D"] = "Las hierbas de Los Baldíos no son como las de otras regiones. Sus propiedades son casi idénticas pero el agua de oasis las altera por lo que hay diferencias apenas perceptibles.$B$BDeseo estudiar esas diferencias... ¡para ver si se les puede sacar partido!$B$BSi eres un entendido en herboristería, recolecta muestras de raíces de las hierbas que encuentres en Los Baldíos. Tráeme esas muestras para que las pueda comparar con las de otras regiones.",
    ["O"] = "Llévale 8 muestras de raíces de hierbas de los alrededores de Los Baldíos del Norte.",
    ["T"] = "Recoge muestras de raíces",
  },
  [867] = {
    ["D"] = "Ya que estás aquí, $C, quizás puedas ayudarme con un pequeño problema. Las malditas arpías han estado saqueando los suministros de nuestras caravanas demasiado tiempo. Es hora de rebanarles la garganta y acabar con unas cuantas.$B$BEl ataque más devastador acabará con una buena parte de su cadena inferior de mando y entonces podemos seguir hacia arriba.$B$BVe a por las arpías Alabruja de cualquier tipo hacia el noroeste y tráeme 6 garras de Alabruja. No debería estar mal para empezar...",
    ["O"] = "Reúne 6 garras de Alabruja de arpías Alabruja.",
    ["T"] = "Arpías saqueadoras",
  },
  [868] = {
    ["D"] = "Hola, $N. Mis maestros de Orgrimmar me han enviado a El Cruce para patrullar por estas tierras e informar de los acontecimientos.$B$BUno de los objetos de mis pesquisas son esos insectoides que se encuentran al sur de la Tierra de Gigantes. No sabemos mucho de estas criaturas, así que me he propuesto averiguar más. Parecen tener inteligencia, una inteligencia superior a la de los animales corrientes.$B$BToma esta garra de excavación y recolecta unos huevos de sus montículos, pero ándate con ojo: si se dan cuenta, te atacarán.",
    ["O"] = "Llévale 12 huevos de silítido y la garra de excavación a Korran en El Cruce.",
    ["T"] = "¡A cazar huevos!",
  },
  [869] = {
    ["D"] = "Hay huellas de animales por todo el lugar, huellas de garras enormes y curvadas. Parecen huellas de raptor.$B$BTu primera idea es que los jinetes raptor asaltaron la caravana, pero las huellas no son profundas y no hay ninguna pisada que no sea animal.$B$BLas huellas se dirigen al noreste de aquí, hacia el este de Colina Espinosa. La respuesta a este misterio debe estar bajo su sombra.",
    ["O"] = "Las huellas llevan al lado este de Colina Espinosa. Síguelas para ver si encuentras algo.",
    ["T"] = "El rastro del ladrón",
  },
  [870] = {
    ["D"] = "¿Has sentido el temblor, $C? Algo ha partido estas tierras en dos. Pero los druidas también percibimos temblores de otros tipos.$B$BHay vida en tierras que antes estaban secas y muertas. La vida vuelve a despertar en los oasis alrededor de El Cruce. Puedo sentir que se está filtrando una energía hacia la superficie, ¿pero de dónde? ¿Tiene algo que ver con el desastre que ha destrozado Los Baldíos?$B$BDebemos encontrar la fuente. Dirígete a Las Charcas del Olvido, al noroeste de El Cruce. Busca en sus aguas una fuente de energía y entonces vuelve aquí.",
    ["O"] = "Busca en Las Charcas del Olvido, al noroeste de El Cruce.",
    ["T"] = "Las Charcas del Olvido",
  },
  [871] = {
    ["D"] = "Todos los jabaespines son nuestros enemigos, $N. Es solo que unos son aún más molestos que otros.$B$BLa tribu de los Crines de Acero ha estado atacando nuestras líneas de suministros procedentes de Orgrimmar. Tengo a exploradores buscando al jefe de estos asaltos pero hasta que lo logren, cualquier baja que podamos infligirles a esos malditos hombres jabalí nos vendrá bien.$B$BEmpieza por el sur, en los lugares donde atacaron nuestros campamentos cerca del río. Búscalos y ejecútalos.",
    ["O"] = "Mata a 8 desvalijadores y a 3 cazadores de la tribu Crines de Acero.",
    ["T"] = "Defender la Avanzada del Puente",
  },
  [872] = {
    ["D"] = "¿Hombres jabalí sueltos por la frontera con Durotar? ¡No lo permitiré! Ya sabes lo que dicen, $N: la mejor defensa es acabar con todo lo que se interponga en tu camino.$B$BUno de mis exploradores siguió a los jabaespines tras su último ataque contra una de nuestras caravanas, así que ya sabemos dónde atacar. Kreenig Gruñucico es el responsable de los ataques a lo largo del río. Machácalo, $C. Derrótalo y dispersa sus fuerzas para que cesen los ataques contra nuestro campamento.$B$BBusca una cueva llena de jabaespines por el río, justo al sur de nuestros establos.",
    ["O"] = "Mata a 5 defensores Crines de Acero, a 5 tejespinas Crines de Acero y a Kreenig Gruñucico. Consigue el colmillo de Gruñucico.",
    ["T"] = "La ofensiva de Avanzada del Puente",
  },
  [873] = {
    ["D"] = "El gran Isha Awak es dueño y señor de estas aguas. Grande es su fuerza y solemne su orgullo. Los humanos de la costa lo temen, porque les ha infligido muchas bajas.$B$BPero no lo temo. Incluso doy las gracias por su presencia. Es un desafío digno y una presa honorable.$B$BSi estás $Glisto:lista;, nada mar adentro en busca de Isha Awak, la Condena Abisal.$B$BSu ánimo mora en su corazón y oírlo latir es conocer tu sino.",
    ["O"] = "Llévale el corazón de Isha Awak a Mahren Vidente del Cielo.",
    ["T"] = "Isha Awak",
  },
  [874] = {
    ["D"] = "Has cazado las fieras de Los Baldíos. Eso demuestra fortaleza de espíritu. Pero $Gun cazador:una cazadora; siempre debe estar $Gpreparado:preparada;. En cualquier terreno, $Gun auténtico cazador:una auténtica cazadora;, puede seguirle la pista a una presa. No se detendrá ante montañas ni ciénagas.$B$BNi siquiera ante el mar.$B$BEncuentra a mi hermana, Mahren. Se dedica a cazar a los grandes monstruos marinos de la costa. Te enseñará todo lo que hay que saber sobre ellos.$B$BPero ándate con cuidado cuando la busques, ya que la costa de Los Baldíos está en manos de los humanos.",
    ["O"] = "Habla con Mahren Vidente del Cielo.",
    ["T"] = "Mahren Vidente del Cielo",
  },
  [875] = {
    ["D"] = "La única forma de detener a esas arpías saqueadoras es destruir su cadena de mando. Las arpías siguen a intrépidas tenientes, destripadoras Alabruja. Si caen las destripadoras, el resto las seguirá.$B$BVe a Las Colina Áridas y mata a destripadoras Alabruja. Asegúrate de que sufran una muerte dolorosa, $N. Queremos que esas muchachas arpías aprendan cuán descabellado es buscar pelea con la Horda.",
    ["O"] = "Reúne 6 anillos de tenientes arpías de las destripadoras Alabruja en Los Baldíos del Norte.",
    ["T"] = "Las tenientes arpías",
  },
  [876] = {
    ["D"] = "Las arpías que mataste mencionaron a su matrona, Serena Sangrepluma. A juzgar por su encarnizado odio hacia la Horda, debe de ser algún tipo de vieja rencilla. Debes acabar con Serena Sangrepluma y zanjar este asunto de una vez por todas.",
    ["O"] = "Mata a Serena Sangrepluma y hazte con su cabeza.",
    ["T"] = "Serena Sangrepluma",
  },
  [877] = {
    ["D"] = "¿Es posible que lo que está pasando al sur pase aquí también? A juzgar por tus descubrimientos en las Charcas del Olvido, puede que estos oasis sean la clave. Hagamos un experimento.$B$BTen, toma estas semillas que he recogido de la montaña. Están muertas y no son fértiles, pero llévalas a El Oasis Estancado justo al sur. Si allí también hay una grieta, coloca las semillas en su interior... y observa. Informa de tus descubrimientos a Tonga, en El Cruce.",
    ["O"] = "Usa las semillas secas para investigar las fisuras en el Oasis Estancado.",
    ["T"] = "El Oasis Estancado",
  },
  [878] = {
    ["D"] = "Debes acabar con mis enemigos. <Gruñe.> Debes ayudar a Quebrantadientes y a los Crines de Acero a debilitar a los Erizapúas. Ambas tribus son muy espirituales, vinculadas a la tierra y a su poder. <Gruñe.> Nos da fuerza en todo... Especialmente en la guerra.$B$BSi quieres saber quién le causa realmente este suplicio a Thork <gruñe> y a la Horda, entonces ¡ayuda a Quebrantadientes! Ataca a los líderes espirituales de los Erizapúas hacia el norte; debilítalos lo suficiente para que los Crines de Acero puedan vencerlos y dominar Los Baldíos.",
    ["O"] = "Mata a 6 buscaquas Erizapúas, 12 tejespinas Erizapúas y 12 geománticas Erizapúas y vuelve junto a Quebrantadientes en el Campamento Taurajo en Los Baldíos.",
    ["T"] = "Tribus en guerra",
  },
  [879] = {
    ["D"] = "Tres miembros de mi tribu salieron del Horado para encabezar unas incursiones contra la Horda, $R. <Gruñe.> Son crueles y taimados, y si los derrotas, entonces El Cruce y la mayor parte de Los Baldíos conocerán la paz... <gruñe> al menos por lo que respecta a la tribu Crines de Acero <gruñe.>$B$BNak, Kuz y Lok Aterraorcos son a los que tienes que buscar. Se encuentran en un lugar remoto del sur de Los Baldíos. Uno lanza hechizos, otro es un rastreador y su líder, llamado Aterraorcos <gruñe> es un guerrero. Mátalos, $R, como ellos me han matado a mí.",
    ["O"] = "Mata a Nak, Kuz y Lok Aterraorcos y lleva sus calaveras a Quebrantadientes en el Campamento Taurajo en Los Baldíos.",
    ["T"] = "Traidores",
  },
  [880] = {
    ["D"] = "Es importante estudiar la vida, tanto animal como vegetal, afectada por los oasis y las energías extrañas que has descubierto. El Oasis Estancado es el hábitat de una especie de tortugas quijaforte. De un tiempo a esta parte su población se ha disparado, y lo que es peor, estos animales antes pacíficos se han vuelto agresivos, como he podido comprobar en mis carnes mientras venía hacia aquí.$B$B<Jerrik te enseña un dedo vendado.>$B$BSospecho que las ha alterado el oasis. Recoge sus caparazones y llévaselos a Tonga en El Cruce para que los estudie.",
    ["O"] = "Reúne 6 caparazones de quijafortes alterados de los quijafortes de El Oasis Estancado.",
    ["T"] = "Seres alterados",
  },
  [881] = {
    ["D"] = "Albineblina, Echeyakee en la lengua de los Tauren, es el rey de los felinos de la sabana. Es tan sigiloso al cazar que dicen que es como una neblina blanca y etérea sobre la tierra. Y tan raudo mata a sus presas que no tienen tiempo de sentir miedo ni dolor.$B$BLos tauren dicen que es muerte y misericordia a la vez.$B$B$N te encomiendo que caces a Echeyakee. Su guarida está al noroeste de aquí, en el lado norte de una pequeña colina, entre los huesos de un kodo gigante.$B$BY ahora vete. Haz sonar este cuerno cuando hayas alcanzado su guarida. Hazlo sonar y él acudirá.",
    ["O"] = "Toca el cuerno en la guarida de Echeyakee para invocarlo. Mátalo o subyúgalo.",
    ["T"] = "Echeyakee",
  },
  [882] = {
    ["D"] = "Ishamuhale, Dientarpón, es el más feroz de los raptores Solescama de Los Baldíos. No caza por deporte, ni por comida. Caza porque le apasiona la caza. Mata porque está en su naturaleza.$B$BY tendrás la oportunidad de conocer su naturaleza, $N, porque tu camino sigue ahora las huellas de las garras de Ishamuhale.$B$BEmpieza la caza. Mata a su presa favorita, una zhebra, y trae entonces sus despojos al árbol muerto al noroeste de Trinquete. Ishamuhale olerá los despojos y esto lo atraerá.$B$BPrepárate para cuando llegue.",
    ["O"] = "Llévale el colmillo de Ishamuhale a Jorn en el Campamento Taurajo.",
    ["T"] = "Ishamuhale",
  },
  [885] = {
    ["D"] = "La pluma de Washte Pawne hace daño al tocarla.",
    ["O"] = "Llévale la pluma de Washte Pawne a Jorn Vidente del Cielo en el Campamento Taurajo.",
    ["T"] = "Washte Pawne",
  },
  [886] = {
    ["D"] = "Los druidas de Cima del Trueno sentimos la presencia de un extraño poder en Los Baldíos, al este de Mulgore.$B$BEnviamos a Tonga Tótem de Runa para que descubra la fuente de dicho poder pero tememos que necesite ayuda.$B$BVe a Los Baldíos y habla con Tonga. Lo encontrarás en El Cruce. Para llegar hasta allí, sigue el camino que sale del Poblado Pezuña de Sangre en dirección este. Una vez en Los Baldíos, coge hacia el norte cuando el camino se bifurque. Continúa en dirección norte y encontrarás El Cruce.$B$BTen cuidado de no abandonar el camino, $N, pues Los Baldíos es un terreno muy abrupto.",
    ["O"] = "Habla con Tonga Tótem de Runa en El Cruce.",
    ["T"] = "El oasis de Los Baldíos",
  },
  [887] = {
    ["D"] = "Gazlowe me prometió un ascenso si le ayudo con el problema de piratas en Trinquete. El problema son los nuevos bucaneros que han venido y ocupado Isla de Batalla, al sur de aquí. No buscan solo nuestras cosas: ¡tienen agravios pendientes! La otra noche incluso fueron a por uno de nuestros galeones de guerra.$B$BTengo planes para manejar la situación, pero Gazlowe quiere resultados inmediatos. Vete a Isla de Batalla y empieza a rebanar cabezas, ¿quieres?",
    ["O"] = "Mata a 8 degolladores o corsarios de los Mares del Sur en Isla de Batalla.",
    ["T"] = "Filibusteros de los Mares del Sur",
  },
  [888] = {
    ["D"] = "Aunque me gustaría recuperar toda mi mercancía, seguro que no es posible. Sin embargo, ¡es imprescindible que recupere unas cuantas cosas!$B$BComo puedes ver, mi observatorio está listo pero necesita la más pequeña de las dos lentes. La primera llegó de Durotar por caravana pero la segunda se envió por barco... y nunca llegó a puerto. ¡Y tampoco me ha llegado el envío por mar de Drizzlik desde Bahía del Botín!$B$BDirígete al campamento pirata y a ver si los puedes encontrar.",
    ["O"] = "Recupera el envío de botas y las lentes telescópicas a Gazlowe en Trinquete.",
    ["T"] = "[DEPRICATED] El botín robado",
  },
  [890] = {
    ["D"] = "¡Llevo siglos esperando mi último cargamento de mercancías de Bahía del Botín! Pondría la mano en el fuego a que lo robaron los filibusteros pero para asegurarnos, podrías acercarte hasta el muelle y preguntarle a Vertipeluca si ya han guardado mi mercancía en el almacén sin notificármelo.$B$BToma mi libro de contabilidad y dile a Vertipeluca que coteje mis datos de inventario con sus registros.",
    ["O"] = "Llévale el libro de contabilidad de Gazlowe al maestro de muelles Vertipeluca.",
    ["T"] = "El envío perdido",
  },
  [891] = {
    ["D"] = "¡Otro barco más que ha caído ante los intransigentes humanos de Theramore!$B$BSu nuevo contraalmirante se ha asentado en la costa, cerca de aquí. Se dedica a abordar o hundir todos los barcos que no se someten a sus caprichos.$B$BViaja al sur, siguiendo la Costa Mercante, y encontrarás su campamento. Tráeme las medallas de sus marinos y sus marineros de cubierta. Ejecuta a sus tenientes principales, Pira y Terraciervo. ¡Destrózalos como ellos han destrozado mi flota!",
    ["O"] = "Consigue 10 medallas de Theramore de marinos de Theramore y marineros de cubierta de Theramore. Mata a los tenientes Pira y Terraciervo.",
    ["T"] = "La venganza de un capitán",
  },
  [892] = {
    ["D"] = "Todo parece cuadrar según mis registros, $N. Vuelve y dile a Gazlowe que no ha habido suerte. No me sorprendería nada que los piratas le hubiesen birlado ese material.$B$BNo puedo hacer nada más por él. Bueno, sí, podrías decirle que tengo unas cosas de Minahonda para él y que pase a recogerlas cuando tenga un rato.",
    ["O"] = "Llévale el libro de contabilidad de Gazlowe a Gazlowe en Trinquete.",
    ["T"] = "El envío perdido",
  },
  [893] = {
    ["D"] = "Por lo que me han contado, los jabaespines Crines de Acero hacia el sur, más allá de la Tierra de Gigantes, no cuentan con herreros cualificados pero, al parecer, han empezado a desarrollar armas resistentes. Me gustaría ponerles la mano encima a varios tipos diferentes para aprender su técnica si fuera posible.$B$BSi deseas contribuir con honor a la Horda, tráeme algunas muestras de sus armas. Encuentra una daga de sus acechadores, una varita de sus videntes y un escudo de sus frenéticos bélicos. ¡Con eso debería bastar!",
    ["O"] = "Llévale 1 puñal Crines de Acero, 1 varita Crines de Acero carbonizada y 1 escudo de guerra Crines de Acero a Jironack Forjacero en Campamento Taurajo en Los Baldíos.",
    ["T"] = "Una selección de armas",
  },
  [894] = {
    ["D"] = "Ventura y Cía. ha montado un pequeño centro de investigación al oeste, pasando El Fangal. No tengo muy claro qué hacen allí pero logré descubrir que están haciendo pruebas con algo llamado \"churumbele\".$B$BY digo yo, ¿qué leches es un churumbele? Bueno, sea lo que sea, quiero examinarlo, así que necesito que alguien me lo consiga.$B$BConseguí una copia de su manual de instrucciones del sistema de control, gracias al cual deberías poder encontrar la forma de desconectar el churumbele.",
    ["O"] = "Accede a la consola de control del laboratorio de investigación de Ventura y Cía.",
    ["T"] = "Churumbele",
  },
  [895] = {
    ["D"] = "SE BUSCA$B$B\"Capitán\" J. Garvey, capitán del Daga de Doncella.$B$BSe le han imputado cargos en todos los mares por robo, secuestro, extorsión, incendio provocado, esclavitud, etc. Garvey atracó recientemente en estas aguas con su tripulación de filibusteros de los Mares del Sur y, la última vez, se le vio atacando la Isla de Batalla. ¡Tráeme sus restos en condiciones de poder ser identificados!$B$B-- Gazlowe",
    ["O"] = "Llévale la cabeza del capitán Garvey a Gazlowe en Trinquete.",
    ["T"] = "SE BUSCA: Capitán Garvey",
  },
  [896] = {
    ["D"] = "Oye, $R, ¿te interesaría algún tesoro? Sabía que eso llamaría tu atención.$B$BPor el muelle del río se dice que uno de los mineros de Mina Pedrusco de Ventura y Cía. al noreste de aquí descubrió una esmeralda del tamaño de tu puño.$B$BSé de unos cuantos compradores que estarían interesados en ponerle las manos encima y no me importaría ir a medias en la venta. ¿Qué dices?",
    ["O"] = "Recupera la esmeralda ojo de gato de un sobrestante o un déspota de Ventura y Cía.",
    ["T"] = "La fortuna de un minero",
  },
  [897] = {
    ["D"] = "Esta extraña criatura insectoide parece de un orden natural por fuera, como si no perteneciera a Los Baldíos.",
    ["O"] = "Llévale la cabeza del Cosechador a Jorn Vidente del Cielo.",
    ["T"] = "El Cosechador",
  },
  [898] = {
    ["D"] = "¡Por fin! ¡Alguien que viene a rescatarme!$B$BNo puedo creer que hayas logrado superar a los guardias. Estos zelotes de Theramore están completamente desquiciados. Hundieron nuestro barco y a mí, el único superviviente, me encarcelaron. Me calificaron de enemigo y de amenaza para la Alianza.$B$B¡Yo! ¿Una amenaza para su gran farsa de Alianza? Solo era un marinero de un envío de licor de luna entre Trinquete y Bahía del Botín.$B$BPero basta de charla. Me ayudarás a volver a Trinquete, ¿verdad? Dime cuando estés $Glisto:lista; y saldremos para allá.",
    ["O"] = "Escolta a Gilthares Ramafuego de vuelta hasta Trinquete con el Capitán Solbrillante.",
    ["T"] = "Escapar del fuerte",
  },
  [899] = {
    ["D"] = "Asesinaron a mi esposa. Incendiaron mi hogar. Desde aquella fatídica noche he viajado por estas tierras, desde que el sol nace hasta que se pone, escudriñando el horizonte en busca de monstruos. He matado a cada hombre jabalí que se me ha cruzado pero mi sed de su sangre está lejos de estar saciada.$B$BTú, $C, tú podrías ayudarme.$B$BMátalos. Mata a tantos como puedas. Tráeme sus colmillos y celebraremos juntos su muerte.",
    ["O"] = "Reúne 30 colmillos de jabaespín.",
    ["T"] = "Consumido por el odio",
  },
  [900] = {
    ["D"] = "Ahora las tres válvulas de control están abiertas. Por los indicadores del panel de control cabe suponer que se deberían apagar antes de que se pueda apagar el dispositivo.",
    ["O"] = "Cierra la válvula de control de combustible, la válvula reguladora y la válvula de control principal y usa entonces de nuevo la consola de control.",
    ["T"] = "Churumbele",
  },
  [901] = {
    ["D"] = "Una vez apagado el aparato, se puede abrir el panel de control y extraer el churumbele de la consola. Con todo lo demás listo, solo queda conseguir la llave que desbloquea la consola.",
    ["O"] = "Consigue la llave de la consola de Manitas el Risitas.",
    ["T"] = "Churumbele",
  },
  [902] = {
    ["D"] = "Al abrir el panel frontal de la consola de control se accede al denso cableado, conducciones y otros extraños mecanismos, la mayoría de los cuales no parece tener ninguna función práctica. Si se apartan y se mira hacia el interior de la consola, se verá el churumbele. Basta con tirar de él para sacarlo de su compartimento.",
    ["O"] = "Llévale el churumbele a Petardol a la Avanzada de Ollaboquilla en Los Baldíos del Norte.",
    ["T"] = "Churumbele",
  },
  [903] = {
    ["D"] = "Si de verdad quieres demostrarme tu coraje, te enviaré a por algo de mayor calibre. Las cazadoras de la sabana de Los Baldíos son una especie dura de roer. Resistentes, con recursos, taimadas... y mortales. Han estado atacando a nuestros exploradores alrededor del pueblo, especialmente al noreste.$B$BExtrema la precaución al cazarlas, $N, o pasarás de ser el depredador a ser la presa.",
    ["O"] = "Reúne 5 garras de cazadora de las cazadoras de la sabana.",
    ["T"] = "Cazar a las cazadoras",
  },
  [904] = "_",
  [905] = {
    ["D"] = "$N, la caza se intensifica. Ahora debes encontrar y derrotar a la presa en su guarida.$B$BCaza a los raptores Solescama hacia el sur, allí han construido una madriguera entre las colinas, cerca del camino. Ejecútalos y desplúmalos. ¡Coloca las plumas en los nidos de segadores para demostrar a sus semejantes que no les temes!$B$BTen cuidado, $C. Los raptores cazan en manada y te rodearán si te descuidas.",
    ["O"] = "Mata a raptores Solescama y recoge sus plumas. Después usa las plumas en los 3 nidos de Solescama.",
    ["T"] = "En la guarida del raptor",
  },
  [906] = {
    ["D"] = "Llévale la cabeza de Lok a Thork en El Cruce, $R. <Gruñe.> Debería saber lo que le ha ocurrido a mi tribu sin más explicaciones, ¡vaya!, y no es que él se creyera que Quebrantadientes tuviera nada que ver con <gruñe> tales cosas. Pero seguro que te recompensará por llevarle tan gran trofeo <gruñe.>$B$BSeguiré bendiciéndote con el poder de Agamaggan por todo el tiempo que permanezca en esta jaula, $R. Hasta entonces, adiós.",
    ["O"] = "Llévale la calavera de Lok a Thork en El Cruce, en Los Baldíos.",
    ["T"] = "Traidores",
  },
  [907] = {
    ["D"] = "Bien, $N, ahora debes enfrentarte a los colosales truenagartos. Son fieras tan monumentales que el suelo de las llanuras tiembla bajo sus pisadas. Ten cuidado cuando luches contra ellos, un paso en falso y te aplastarían como a una hormiga.$B$BEncontrarás a los truenagartos en Los Baldíos del Sur. Se llaman morrotormenta, Tormentero y Tronatesta. Vuelve cuando los hayas vencido.",
    ["O"] = "Llévale 3 unidades de sangre de truenagarto a Jorn Vidente del Cielo en el Campamento Taurajo.",
    ["T"] = "Los truenagartos iracundos",
  },
  [908] = {
    ["D"] = "El Martillo Crepuscular se ha trasladado a las ruinas del Santuario Lunar de las Cavernas de Brazanegra. Su presencia solo puede servir para forzar a los elementos a actuar contra nosotros. Si no nos ocupamos de ella, esta región caerá en sus manos definitivamente.$B$B$N, dirígete a Brazanegra y encuentra la piedra de las profundidades en las ruinas; debería estar cerca, en el agua. En su interior hay un núcleo de las profundidades, un dispositivo que cuando se lee correctamente, revela la historia de toda actividad elemental. Si lo consigo, y con la ayuda de los miembros del Anillo de la Tierra, ¡quizás podamos detenerlos!",
    ["O"] = "Llévale el núcleo de las profundidades a Je\'neu Sancrea de la Avanzada de Zoram\'gar, Vallefresno.",
    ["T"] = "Entre ruinas",
  },
  [909] = {
    ["D"] = "El elemental de agua derrotado ha dejado tras de sí un extraño globo de agua. Supones que el objeto de alguna forma alimentaba la energía de la bestia. En el interior del globo chapotea masa líquida repulsiva. El propio globo parece impermeable a todo tipo de fuerza física.$B$BSi alguien es capaz de interpretar este artilugio, es Je\'neu Sancrea del Anillo de la Tierra.",
    ["O"] = "Llévale el extraño globo de agua a Je\'neu Sancrea en las Cavernas de Brazanegra.",
    ["T"] = "OLD Baron Aquanis",
  },
  [911] = {
    ["D"] = "Dicen que Vallefresno es la nueva frontera y que La Empalizada de Mor\'shan en Los Baldíos es su puerta de entrada. ¡Sería una pasada ser uno de los pioneros y coger todo lo que necesitas de la naturaleza virgen! Dicen que ese será el principal campo de batalla entre la Horda y la Alianza ¡y yo quiero verlo, $N!$B$B¿Querrás llevarme a La Empalizada de Mor\'shan para que pueda ver cómo es la frontera, por favor? ¡Quiero ver el sitio donde la Horda está combatiendo a esos apestosos elfos de la noche!",
    ["O"] = "Lleva a tu pupilo a La Empalizada de Mor\'shan en la zona norte de Los Baldíos, antes de la entrada a Vallefresno.",
    ["T"] = "El acceso a la frontera",
  },
  [912] = "_",
  [913] = {
    ["D"] = "$N, el halcón trueno es un animal de lo más feroz. Ha llegado la hora de que te enfrentes a uno de ellos. Debes averiguar por donde andan y traerme sus alas como prueba de haber cazado a uno.$B$BHazlo y ya casi habrás acabado tus tareas conmigo.",
    ["O"] = "Encuentra 1 halcón trueno, mátalo y llévale sus alas a Jorn Vidente del Cielo en el Campamento Taurajo.",
    ["T"] = "El chillido del halcón trueno",
  },
  [914] = {
    ["D"] = "Los druidas de las Cuevas de los Lamentos, los Druidas del colmillo, son una aberración. Fueron parte de una orden de nobles druidas que pretendían sanar Los Baldíos, pero ahora tan solo pretender cambiar esta tierra para amoldarla a sus propios y retorcidos sueños.$B$BLos Druidas del colmillo tienen cuatro líderes, y cada uno de ellos posee una gema de sueño. ¡Sus rostros aún me siguen atormentando! Derrota a los líderes y tráeme sus gemas. Así Los Baldíos recuperarán la paz.$B$BVete, $N. Los encontrarás escondidos en las profundidades de las Cuevas de los Lamentos.",
    ["O"] = "Lleva las gemas de Cobrahn, Anacondra, Pythas y Serpentis a Nara Ferocrín en Cima del Trueno.",
    ["T"] = "Líderes del Colmillo",
  },
  [916] = {
    ["D"] = "He venido a Cañada Umbría para observar a las arañas Tejemadera de la Gruta Narácnida. Me dijeron que tras la reciente bendición de Teldrassil deberían ser mucho menos agresivas. Parece que me informaron mal.$B$BHace poco obtuve algunas de sus glándulas venenosas, y me chocó encontrar una corrupción dañina en su interior. Huelga decir que me preocupa pensar lo que podría pasar si dejáramos animales corruptos como estos campar a sus anchas.$B$BQuiero que reduzcas su población para ganar tiempo.",
    ["O"] = "Mata 12 arañas Tejemadera.",
    ["T"] = "Corrupción Tejemadera",
  },
  [917] = {
    ["D"] = "Ahora que lo pienso, a lo mejor me puedes ayudar un poco más. Vine para recoger un huevo de Tejemadera del fondo de la cueva, pero las arañas no están por la labor. Si pudieras coger un huevo y llevárselo a Dirania los dos te estaríamos muy agradecidos.",
    ["O"] = "Consigue un huevo Tejemadera.",
    ["T"] = "Huevo de Tejemadera",
  },
  [918] = {
    ["D"] = "Los Brezomadera de Teldrassil son elementales de la naturaleza. En cierto modo, representan el orden natural de las plantas y los animales de nuestro gran árbol.$B$BPor eso es desagradable ver lo malhumorados que están últimamente, especialmente cuando tenemos en cuenta que la situación debería estar mejorando.$B$BQuiero ayudar a localizar el origen. He estado trabajando en diferentes métodos de nutrición de plantas y me gustaría probarlos con semillas de Brezomadera. ¿Podrías recoger semillas de Brezomadera alrededor del Lago Al\'Ameth y traérmelas?",
    ["O"] = "Recoge 6 semillas de Brezomadera.",
    ["T"] = "Semillas de Brezomadera",
  },
  [919] = {
    ["D"] = "Están brotando pequeños Brezomadera alrededor de las aguas del Lago Al\'Ameth. Me temo que ya no es posible ayudarlos por lo que deberíamos eliminarlos antes de que crezcan más y empiecen a causar problemas.$B$BCuando pases por el lago, si ves algún brote de Brezomadera, arráncalo. ¡Ayúdanos a mantener la tierra limpia!",
    ["O"] = "Reúne 10 brotes de Brezomadera.",
    ["T"] = "Brotes de Brezomadera",
  },
  [920] = {
    ["D"] = "¿Eres $N? He oído que Tenaron te estaba buscando. Está en la sala más alta de Aldrassil.$B$BAunque es mejor tomarse la vida con calma, no lo hagas esperar demasiado.",
    ["O"] = "Habla con Tenaron Tormentagarre en Aldrassil, en Cañada Umbría.",
    ["T"] = "Las invocaciones de Tenaron",
  },
  [921] = {
    ["D"] = "Es hora de que vayas a buscar tu destino, $N. Pero antes de que puedas aventurarte más allá de nuestros bosques, te queda mucho que aprender sobre nuestra historia reciente.$B$BTienes una tarea que cumplir: ve a la Poza de la Luna que hay al norte de Aldrassil y obtén una ampolla de su agua. Cuando estés allí, presenciarás una muestra de nuestra magia y aprenderás sobre nuestro pasado.$B$BCuando termines, lleva el agua a Tenaron Tormentagarre. Lo encontrarás al final de la rampa oeste, en la cima de Aldrassil.",
    ["O"] = "Llena la ampolla de cristal.",
    ["T"] = "Teldrassil: La corona de Azeroth",
  },
  [922] = {
    ["D"] = "$N, ¿puedes llevarle una de las semillas que me trajiste a mi amigo Rellian Verdeguja? Él es un druida del Cobertizo Primigenio y la última vez que hablamos me confesó su interés por mi trabajo con los Brezomadera. Incluso tenía algunas ideas así que supongo que le encantaría tener una semilla para poder experimentar con ella.$B$BGracias, $N. Espero que algún día puedas ver el fruto de mi esfuerzo.$B$BEncontrarás el Cobertizo Primigenio a lo largo del camino que sale al oeste de Dolanaar, en dirección a Darnassus.",
    ["O"] = "Llévale 1 semilla de Brezomadera a Rellian Verdeguja, en el Cobertizo Primigenio.",
    ["T"] = "Rellian Verdeguja",
  },
  [923] = {
    ["D"] = "Hay algo malvado creciendo en Brezomadera. Estamos intentando encontrar la fuente de ese mal, pero hasta que lo hagamos, debemos talar todos los Brezomadera que ya no tengan remedio. Por el bien de Teldrassil. Los que vagan por el Lago Primigenio, al norte de Teldrassil, son los más afectados. ¡Hay que eliminarlos!$B$BDestruye todos los Brezomadera que encuentres ahí y recoge los tumores musgosos que crecen en ellos. Tráelos para que podamos quemarlos.",
    ["O"] = "Consigue 5 tumores musgosos.",
    ["T"] = "Tumores musgosos",
  },
  [924] = {
    ["D"] = "La Semilla del Demonio es una poderosa herramienta para la Legión Ardiente. Se encuentra en una caverna en la Cima Calígine, sobre un altar de fuego demoníaco.$B$BHe preparado piedras de energía que cuando se colocan cerca de la semilla interfieren en su conexión con la Legión.$B$BCoge una piedra de la mesa y llévala al altar de fuego en lo más alto de la Cima Calígine. Dirígete hacia el oeste, luego hacia el norte en el cruce de caminos. Verás la montaña a tu izquierda. La cara norte es la más segura de escalar.$B$BY date prisa, la energía de la piedra se agota con el tiempo.",
    ["O"] = "Coge 1 piedra de energía imperfecta. Llévala al Altar de Fuego antes de que su energía desaparezca y vuelve junto a Ak\'Zeloth.",
    ["T"] = "La semilla del Demonio",
  },
  [925] = {
    ["D"] = "Dicen que Cairne Pezuña de Sangre es uno de las mayores héroes de la Horda de todos los tiempos. ¡Cuando sea mayor también seré un héroe de la Horda!$B$B¿Conoces a Cairne, $N? ¿Puedes hacerme un favor? ¿Me conseguirás sus huellas de pezuñas? No me atrevo a pedírselo, seguramente estará muy ocupado para alguien como yo... ¡pero tú! ¡Tú eres $Gun aventurero:una aventurera; como él! ¡Seguro que te dará su autógrafo!$B$B¡¡Por favor!!",
    ["O"] = "Viaja a Cima del Trueno para intentar conseguir las huellas de pezuñas de Cairne Pezuña de Sangre para tu pupilo.",
    ["T"] = "Marca de la pezuña de Cairne",
  },
  [926] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Piedra de energía con imperfecciones",
  },
  [928] = {
    ["D"] = "Aunque podría contarte mucho más de las Pozas de la Luna y de Teldrassil, debo dejarte ir; Corithras Furia Lunar te está esperando. He puesto el agua que has traído en esta vasija; llévasela.$B$BEstará en la Poza de la Luna de Dolanaar. Coge el camino del sur de Aldrassil al salir de Cañada Umbría y sigue los adoquines cuando el camino gira al oeste.$B$BTen cuidado, $N. Últimamente hay criaturas muy peligrosas en el bosque.",
    ["O"] = "Lleva la vasija casi llena a Corithras Furia Lunar a Dolanaar.",
    ["T"] = "Teldrassil: La corona de Azeroth",
  },
  [929] = {
    ["D"] = "Deja que te cuente más sobre el cometido que debes realizar. Los druidas de Darnassus usan el agua de las pozas de la luna de Teldrassil, que deben rellenarse de vez en cuando. Usando estas ampollas especiales puedes recoger agua de las pozas de la luna.$B$BCoge esta vasija y ve hasta la poza de la luna que está a las afueras de la Aldea Brisa Estelar, al este. Llénala de agua y tráemela de vuelta. Presta atención a las lecciones de las pozas, para evitar repetir nuestros errores.",
    ["O"] = "Llena la ampolla de jade.",
    ["T"] = "Teldrassil: La corona de Azeroth",
  },
  [931] = {
    ["D"] = "Las hojas de esta planta fulguran con la luz del bosque, creando un aura intermitente.$B$BCrees que a Denalan le gustaría tener una muestra.",
    ["O"] = "Llévale una fronda fulgurante a Denalan. Normalmente está en el Lago Al\'Amethm, pero a veces viaja hasta el Cobertizo Primigenio.",
    ["T"] = "Fronda fulgurante",
  },
  [932] = {
    ["D"] = "Debo advertirte, $N, de que este asunto debe quedar entre nosotros. Los sátiros ya son bastante vergüenza para nosotros, y este en concreto está demasiado cerca.$B$BSe llama Lord Melenas y habita en la cercana cueva de Roca Mácula, al norte, donde ha reunido a un numeroso grupo de guerreros grells. Su corazón es negro como la noche, mas lo que trama es incluso más repugnante.$B$BDebes encontrarle en su cercana cueva, al norte de aquí, y traerme su cabeza.",
    ["O"] = "Consigue la cabeza de Lord Melenas.",
    ["T"] = "Odio enraizado",
  },
  [933] = {
    ["D"] = "Quiero que visites una última poza de la luna. Está directamente al sur de aquí, a orillas de los Estanques de Arlithrien. Ándate con cuidado mientras buscas la poza y ten a mano tus armas. Percibo una corrupción creciente desde las ramas del sur de Teldrassil.$B$BCuando tengas el agua, quiero que se la lleves a una cara conocida que encontrarás en los estanques. Ella desea combatir la corrupción, y ha preguntado personalmente por ti.",
    ["O"] = "Llena la ampolla de turmalina.",
    ["T"] = "Teldrassil: La corona de Azeroth",
  },
  [934] = {
    ["D"] = "Sin embargo, no todo iba bien en Teldrassil. Corzocelada lo había planeado todo meticulosamente ya que el Árbol del Mundo había brotado según lo previsto, pero ocurrió un pequeño desajuste, desajuste al que se pueden atribuir muchos de los conflictos de Teldrassil.$B$BPero no entraré en detalles por ahora. Debes ir a la última Poza de la Luna, al noroeste de El Claro del Oráculo. Debajo de las ramas del Árbol del Oráculo se encuentra la primera y más poderosa de nuestras pozas. Llena una ampolla con agua de esta poza y tráemela.",
    ["O"] = "Llena la ampolla de amatista y devuélvesela a Corithras Furia Lunar en Dolanaar.",
    ["T"] = "La corona de la Tierra",
  },
  [935] = {
    ["D"] = "Nuestras tareas están cumplidas, por ahora. El agua ha demostrado ser de gran importancia, pero yo no soy su destinataria original. Ve y devuélvesela a Corithras Furia Lunar en el cruce, seguro que quiere que la lleves a su destino.",
    ["O"] = "Llévale la vasija llena a Corithras Furia Lunar en el cruce que hay enfrente de Darnassus.",
    ["T"] = "Teldrassil: La corona de Azeroth",
  },
  [936] = {
    ["D"] = "¿Tienes un minuto, $C?$B$BEl Círculo Cenarion de Cima del Trueno está buscando aventureros curtidos para un importante proyecto de investigación. No conozco los detalles, pero has de saber que quien dirige el proyecto es el mismísimo archidruida Hamuul Tótem de Runa.$B$BSi te interesa, habla con él directamente. Lo encontrarás en el Alto de los Ancestros, en Cima del Trueno.",
    ["O"] = "Habla con el archidruida Hamuul Tótem de Runa en Cima del Trueno.",
    ["T"] = "Ayuda al archidruida Tótem de Runa",
  },
  [937] = {
    ["D"] = "Me enviaron aquí con un pequeño grupo de centinelas para proteger el Árbol del Oráculo de las arpías que han anidado en el claro. Poco a poco estamos intentando expulsarlas.$B$BCuando el Árbol del Oráculo envió a un mensajero a Darnassus con un informe, este fue atacado y asesinado por un grupo de arpías.$B$BSi te sientes $Gcapacitado:capacitada; para esta tarea, ve a sus nidos y acaba con ellas, y después tráeme sus cinturones como prueba de ello.",
    ["O"] = "Consigue 6 cinturones Sangrepluma.",
    ["T"] = "El claro encantado",
  },
  [938] = {
    ["D"] = "La piel de la dientes de sable es de un color gris característico que armoniza con el del bosque en el que se encuentra. Mientras permanece allí tendida, observas que está malherida, con profundas laceraciones en la espalda y el estómago.$B$BLevanta la cabeza y te observa fijamente, dejando ver a través de sus azules ojos la gran inteligencia que posee. Parece que desea seguirte.",
    ["O"] = "Lleva a Niebla ante la centinela Arynia Quiebranubes que se encuentra en la Poza de la Luna situada cerca del Árbol del Oráculo.",
    ["T"] = "Niebla",
  },
  [939] = {
    ["D"] = "Estudias la sencilla flauta de madera que recogiste tras derrotar a Xavaric.$B$BLa madera parece sólida, pero cuando giras la flauta en tus manos, te da la sensación de que los dibujos y espirales de su superficie empiezan a retorcerse. Entonces te invade un repentino pavor...$B$BSacudes la cabeza $Gseguro:segura; de que tu mente te está jugando una mala pasada.$B$BPuede que otros sátiros tengan más pistas sobre lo que has encontrado. Lleva lo que has descubierto al druida Eridan Vientoazul que vive al sur de Frondavil.",
    ["O"] = "Lleva la Flauta de Xavaric y 5 muestras de lazo vil de fuego de jade a Eridan Vientoazul al Frondavil meridional.",
    ["T"] = "Flauta de Xavaric",
  },
  [940] = {
    ["D"] = "El bosque susurra tus osadas proezas, $N. Una vez expulsadas las arpías de sus nidos, el bosque vuelve a estar en calma y las criaturas que en él habitan se sienten de nuevo seguras. Temía enviar a otro mensajero al archidruida pero ahora sé que tú le harás llegar mi mensaje sin problema.$B$BEntrégaselo y el bosque te ofrecerá su protección en los caminos que conducen hasta el Bosque de Piedra.",
    ["O"] = "Llévale el informe del Árbol del Oráculo al archidruida Fandral Corzocelada de Darnassus.",
    ["T"] = "Teldrassil",
  },
  [942] = {
    ["D"] = "Este fósil tiene un parecido asombroso con uno que vi en Forjaz una vez. Un arqueólogo llamado Gargavino lo trajo a nuestra conferencia anual de la Liga de Expedicionarios. Creía que el fósil encerraba un poder y que este podría liberarse de alguna forma.$B$BLo último que supe de Gargavino es que se había refugiado en el Mesón Aguahonda, en el Puerto de Menethil y que esperaba una escolta para llegar a la Excavación de Whelgar. Búscalo y muéstrale el fósil de Tripirrem. Quizás él consiga revelar los secretos del pasado.$B$BEl barco para Menethil zarpa de Ventormenta.",
    ["O"] = "Lleva el fósil misterioso al arqueólogo Gargavino al Puerto de Menethil.",
    ["T"] = "El prospector despistado",
  },
  [943] = {
    ["D"] = "El fósil original lo tiene ahora el prospector Whelgar, en su excavación de Los Humedales. Cuando descubrí la Piedra de Relu, creía que era la clave para desvelar el misterio del fósil.$B$BCuando me dirigía a reunirme con Whelgar, fui atacado por raptores y perdí la reliquia. No sé cuál de esas bestias se tragó la reliquia pero, si la recuperas, podré liberar el poder de esos artefactos.$B$BTráeme la Piedra de Relu y el fósil original.",
    ["O"] = "El arqueólogo Gargavino, del Puerto de Menethil, quiere que le lleves la Piedra de Relu y el fósil de Gargavino.",
    ["T"] = "El prospector despistado",
  },
  [944] = {
    ["D"] = "Es extraño que haya nagas aquí, en Costa Oscura. Pero aún más extraño es... lo del Martillo Crepuscular. Ese culto adora a los antiguos Señores de la Tierra, que fueron derrotados hace mucho tiempo.$B$BUno de ellos cayó en La Espada del Maestro, al sur de aquí.$B$BVe allí y busca a más adeptos del Martillo Crepuscular.$B$BLlévate esta ampolla de adivinación. Utilízala para crear un cuenco de visión cuando encuentres La Espada del Maestro. Y luego utilízalo para hablar conmigo.",
    ["O"] = "Reúne información y luego utiliza una ampolla de adivinación para crear un cuenco de visión. Utiliza el cuenco para hablar con Onus.",
    ["T"] = "La Espada del Maestro",
  },
  [945] = {
    ["D"] = "¡Socorro!$B$BEstaba paseando por La Espada del Maestro cuando esos asquerosos del Culto me rodearon. ¡Menos mal que se me da bien esconderme!$B$B¿Puedes ayudarme a salir de aquí? También necesito avisar a mi hermana Therysil de que estoy bien. Está al sur, en Vallefresno, en el Santuario de Aessina.",
    ["O"] = "Ayuda a escapar a Therylune y luego dile a Therysil, en el Santuario de Aessina, que su hermana está a salvo.",
    ["T"] = "La fuga de Therylune",
  },
  [946] = "_",
  [947] = {
    ["D"] = "Estoy ideando una poción para la que necesito unas setas muy raras, unas que solo crecen en una cueva determinada. Esa cueva se encuentra más allá del Salto de Fonroca, al este y un poco al norte por las montañas.$B$BIría hasta allí en persona, pero en el Páramo de los Ancianos me aconsejaron que me mantuviera lejos de ese lugar. Nuestros venerables aliados creen que la cueva es el refugio de un nuevo mal de Costa Oscura.$B$BTe lo ruego, $N, tráeme algunas de esas setas. Y aprovecha para explorar la cueva y confirmar los temores de los ancianos.",
    ["O"] = "Lleva 5 boletos rudos y 1 oronja verde a Barithras Sombra Lunar a Auberdine.",
    ["T"] = "La cueva de las setas",
  },
  [948] = {
    ["D"] = "Onus, un anciano del Conocimiento del Páramo de los Ancianos, ha sabido de tu viaje a Salto de Fonroca y desea hablar contigo. El Páramo se encuentra hacia el sur, cerca de las montañas.$B$BLos ancianos son pacientes y sabios, $N. Si Onus busca tu consejo sobre lo que viste en el Salto, temo que la cosa es urgente.",
    ["O"] = "Ve al Páramo de los Ancianos y habla con Onus.",
    ["T"] = "Onu",
  },
  [949] = {
    ["D"] = "No se sabe qué motivos han llevado al Martillo Crepuscular a La Espada del Maestro. Pero sus motivos son, sin duda, oscuros.$B$BDebes ir a su campamento de La Espada. Busca algo que pueda arrojar un poco de luz sobre sus intenciones.$B$BPorque debemos saber cuáles son antes de preparar un plan.",
    ["O"] = "Encuentra una pista en el campamento del Martillo Crepuscular en La Espada del Maestro.",
    ["T"] = "El campamento del Martillo Crepuscular",
  },
  [950] = {
    ["D"] = "Mirar el Escrito Crepuscular es una locura. El texto empieza a bailar tras un momento, y la belleza se torna en formas grotescas.$B$BSolo te da tiempo a arrancar una hoja de garabatos incomprensibles antes de que te desvanezcas.",
    ["O"] = "Lleva a Onus los garabatos incomprensibles que has encontrado.",
    ["T"] = "Regresa junto a Onus",
  },
  [951] = {
    ["D"] = "El papiro que encontraste en La Espada del Maestro es una página de un antiguo texto escrito por los elfos. Está terriblemente mancillado por la corrupción del Martillo Crepuscular, pero parece que el Culto está sirviéndose de una antigua magia élfica para devolver a sus maestros a nuestro mundo$B$BViaja hacia el noreste, hasta las Ruinas de Mathystra y busca antiguas reliquias entre los hierbajos y las piedras. Estudiándolas, podríamos averiguar cómo pretende explotar el Martillo Crepuscular la magia de los elfos.",
    ["O"] = "Lleva 6 reliquias de Mathystra a Onus al Páramo de los Ancianos.",
    ["T"] = "Las reliquias de Mathystra",
  },
  [952] = {
    ["D"] = "Si tuviste tiempo para hacer de $Gmensajero:mensajera; para el Árbol del Oráculo, seguro que puedo persuadirte para que lleves un mensaje al Páramo de los Ancianos, en Costa Oscura, al sur de Auberdine.$B$BProbablemente, por motivos de seguridad, tengas que utilizar un hipogrifo como medio de transporte, pero confío plenamente en ti. Llévale esto a Onus, el anciano del Conocimiento. Ha estado a la espera de noticias mías, igual que yo he aguardado noticias de El Claro del Oráculo.",
    ["O"] = "Entrégale el mensaje de Fandral a Onus en el Páramo de los Ancianos en Costa Oscura, al sur de Auberdine.",
    ["T"] = "Páramo de los Ancianos",
  },
  [953] = {
    ["D"] = "Encontrarás las ruinas de Ameth\'Aran hacia el este. Ahora las habitan los inquietos espíritus de los Altonato que en vida moraban entre sus muros, unos muros entre los que los siervos de Azshara practicaban libremente su poderosa magia.$B$BCuando me enviaron a explorar las ruinas encontré dos grandes tablillas con grabados que relataban la historia de Ameth\'Aran y de su caída. Mientras leía las runas me abordaron los espíritus y tuve que huir.$B$BPor favor, si puedes, aventúrate entre las ruinas e intenta descifrar las tablillas en mi lugar.",
    ["O"] = "Estudia las tablillas que hablan de Ameth\'Aran y de su caída y reúnete con la centinela Tysha Filoluna en Costa Oscura.",
    ["T"] = "La caída de Ameth\'Aran",
  },
  [954] = {
    ["D"] = "Las ruinas de Bashal\'Aran, al este, están invadidas por esbirros demoníacos. Los espíritus y los sátiros que han establecido su morada en la zona se alimentan de la energía mágica del lugar. Sus poderes crecen continuamente.$B$BAun así, he notado que hay un santuario al que no se acercan jamás. En la parte occidental de las ruinas, sobre un pequeño acantilado, se ve una extraña aura azul... Debe haber una explicación a la reluctancia de los demonios.$B$BQuisiera que lo investigaras.",
    ["O"] = "Encuentra la fuente de la extraña aura azul en las Ruinas de Bashal\'Aran.",
    ["T"] = "Bashal\'Aran",
  },
  [956] = {
    ["D"] = "Si los grells han entrado en contacto directo con el sello que me ata a mi prisión eterna, sospecho cuál podría ser la causa. Sin duda el sello está en poder del sátiro que los comanda.$B$BTengo el presentimiento de que es así, $N. Debe tenerlo uno de los sátiros. Si logras hacerte con él, me acercarías tanto al otro lado de los barrotes de mi prisión que me harías llorar.",
    ["O"] = "Consigue el sello de piedra lunar antiguo y llévaselo a Asterion a Bashal\'Aran.",
    ["T"] = "Bashal\'Aran",
  },
  [957] = {
    ["D"] = "El sello que conforma mi prisión fue obra de uno de los Altonato más poderosos. En Ameth\'Aran, al sur, hay unas ruinas idénticas a estas. En ellas persiste, incluso hoy, una antigua llama, una llama azul. Esa llama podría destruir el sello.$B$BPero no bajes la guardia en las ruinas, $N...",
    ["O"] = "Destruye el sello de piedra lunar antiguo en la antigua llama de Ameth\'Aran y vuelve a reunirte con Asterion en Bashal\'Aran.",
    ["T"] = "Bashal\'Aran",
  },
  [958] = {
    ["D"] = "$C. Tengo que pedirte un favor.$B$B¿Has visto las ruinas de Ameth\'Aran? Si no las has visto, las puedes encontrar en el lado este del camino principal, viajando hacia el sur. En otro tiempo fueron el hogar de los poderosos Altonato. Hoy son testimonios de la destrucción que sus errores produjeron.$B$BLos centinelas me han dicho que los espíritus de los Altonato persisten y que aún poseen sus antiguos instrumentos mágicos. Hemos de hacernos con esas reliquias para poder destruirlas.",
    ["O"] = "Reúne 7 reliquias de los Altonato y llévaselas a Thundris Tejevientos a Auberdine.",
    ["T"] = "Las herramientas de los Altonato",
  },
  [959] = {
    ["D"] = "¡Vaya!$B$B¡He tenido un pequeño percance, como puedes ver! El envío del carguero de la mañana de Bahía del Botín era enorme. ¡Lo que se dice enorme de verdad! Nunca había visto tantas cajas. Bueno, estaba intentando acelerar las cosas, así que empecé a descargar un poco más de lo que podía manejar. Y ahí es cuando todo se vino abajo.$B$BAntes de que me pudiera dar cuenta, vi que esa amenaza de Loco Magglish salía corriendo con una botella de Oporto con 99 años de antigüedad. Lo perseguí hasta las Cuevas de los Lamentos. Pero ni hablar de meterme en ellas.$B$B¿Eres suficientemente valiente para encontrarlo?",
    ["O"] = "El operador de grúa Pelardo de Trinquete quiere que le consigas una botella de Oporto con 99 años de antigüedad de Loco Magglish, que se esconde en las Cuevas de los Lamentos.",
    ["T"] = "¡A por la botella!",
  },
  [962] = {
    ["D"] = "La Sociedad Real de Boticarios, establecida en la gran Entrañas de Lordaeron, me ha enviado aquí con un propósito muy concreto, $N. Quizás quieras ayudarme a mí y, de paso, también a la Dama Oscura en nuestro intento de mejorar la forma física de los Renegados.$B$BÚltimamente he estado estudiando una extraña planta llamada reptilia y creo que en grandes cantidades tiene un gran potencial.$B$BLamentablemente, la reptilia solo se encuentra en los recesos más oscuros de las Cuevas de los Lamentos, un sistema de cuevas muy peligroso situado en Los Baldíos.",
    ["O"] = "La boticaria Zamah de Cima del Trueno quiere que recojas 10 reptilias.",
    ["T"] = "Reptilia",
  },
  [964] = {
    ["D"] = "La llave de Scholomance es la llamada Llave esqueleto. Debe forjarse a partir de los restos de un esqueleto (varios, en realidad) y endurecerse con los metales más fuertes en el molde adecuado. Un sello de poder obtenido de un ser que pueda abrir el portal de Scholomance por medios naturales será el último componente de la llave.$B$BPero empecemos por el principio, $N. Necesitaremos trozos esqueléticos para forjar la llave. Los podrás obtener de los esqueletos que se encuentran dentro de Andorhal, aunque también deberían servirte los que están fuera de los muros de la ciudad.",
    ["O"] = "Lleva 15 trozos esqueléticos al boticario Dithers en El Baluarte, en las Tierras de la Peste del Oeste.",
    ["T"] = "Trozos esqueléticos",
  },
  [965] = {
    ["D"] = "Saludos, joven $R. Soy Elissa Brisa Estelar y soy la encargada de proteger Auberdine de todo mal.$B$BCon tal propósito envié a Balthule Tenebroso a observar los extraños sucesos de la Torre de Althalaxx, al noreste.$B$BY ya debería haber regresado. Me preocupa que haya encontrado algún peligro imprevisto en el bosque. Apreciaría sobremanera que fueras en su busca y te aseguraras de que se encuentra bien.",
    ["O"] = "Encuentra a Balthule Tenebroso cerca de La Torre de Althalaxx en Costa Oscura.",
    ["T"] = "La Torre de Althalaxx",
  },
  [966] = {
    ["D"] = "Un grupo de brujos se ha instalado alrededor y dentro de la torre. Habría regresado a Auberdine a informar a Elissa, pero tenía miedo de perderme algo.$B$BDelgren sospechaba que una compañía como esta estaba reuniéndose en la torre, pero no sabía por qué.$B$BA mis manos han llegado algunos fragmentos de los papiros que llevan los brujos, pero necesito reunir más para resolver el enigma. Deja que te dé un consejo: no entres en la torre, los brujos que hay en ella son los más poderosos.",
    ["O"] = "Reúne 4 papiros desgastados para Balthule Golpesombra acerca de La Torre de Althalaxx.",
    ["T"] = "La Torre de Althalaxx",
  },
  [968] = {
    ["D"] = "Aunque la cubierta de este libro está gastada y arañada, aún se puede leer el título:$B$BLos Poderes Subyacentes$B$BEn el interior del libro hay una lista de nombres impronunciables con títulos terribles, rituales de adoración y sacrificios preferidos... muchos de los cuales incluyen criaturas vivas, humanoides.$B$BEn la cubierta interior del libro hay unas palabras escritas: \"Runas y Maldiciones de Agarrahueso, La Caverna Abandonada, Forjaz. Propiedad de: Gerrig Agarrahueso.\"$B$BSi Agarrahueso vende libros de este tipo, quizás me vuelva a comprar este.",
    ["O"] = "Lleva el libro Los Poderes Subyacentes a Gerrig Agarrahueso a La Caverna Abandonada, en Forjaz.",
    ["T"] = "Los Poderes Subyacentes",
  },
  [969] = {
    ["D"] = "Ah, verte basta para saber que lo que quieres es un talismán de la suerte. ¡Mau\'ari todo sabe!$B$BAl Sur, encontrarás un cañón y gigantes hechos de hielo duro. A veces los gigantes las llevan, pero puedes recoger tú $Gmismo:misma; las esquirlas en el cañón. Las esquirlas tienen una poderosa cualidad reflectante.$B$BTráeme 4 esquirlas de los Machacahielo y mi talismán de la suerte será tuyo.",
    ["O"] = "Recoge 4 esquirlas de Machacahielo para la médica bruja Mau\'ari de Vista Eterna.",
    ["T"] = "Que la suerte sea contigo",
  },
  [970] = {
    ["D"] = "La carta de Balthule es espantosa. Este culto de la Facción Oscura es una espina clavada que debo eliminar. Me las he visto con algunos de los miembros de la Facción Oscura al noreste de este lugar, en Ordil\'Aran. Uno de ellos posee una gema de almas que, creo, guarda el secreto del poder del culto.$B$BTráemela y podré descifrar el secreto de su interior.",
    ["O"] = "Reúne una gema de alma resplandeciente y vuelve junto a Delgren el Purificador en la Atalaya de Maestra.",
    ["T"] = "La Torre de Althalaxx",
  },
  [971] = {
    ["D"] = "Recopilo conocimiento. Conocimiento antiguo. Conocimiento con poder. Conocimiento que abre puertas y puede despertar a los Durmientes si no se maneja con cuidado.$B$BSe dice que un antiguo documento, el manuscrito de Lorgalis, se halla en las Cavernas de Brazanegra al oeste. Es el hogar de nuestros antepasados que murieron hace mucho. Acumularon mucha sabiduría antes de que su ciudad se arruinara.$B$BBusca el manuscrito en las Cavernas de Brazanegra. Nunca olvidaré este favor. Ni siquiera después del fin de los días.",
    ["O"] = "Lleva el manuscrito de Lorgalis a la centinela practicante Issara que está en el Campamento Brazanegra.",
    ["T"] = "Conocimiento en las Cavernas",
  },
  [972] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Sapta de agua",
  },
  [973] = {
    ["D"] = "Ilkrud Magthrull. Sí, sé algo acerca de él. Es un poderoso orco brujo que mora en el Santuario de la Escara, un lugar de extrema maldad al suroeste de Vallefresno.$B$BHasta ahora no había pasado de ser una molestia menor y no he tenido que tratarlo pero, por lo visto, ha llegado su hora.",
    ["O"] = "Llévale el Libro de Ilkrud Magthrull a Delgren el Purificador en la Atalaya de Maestra.",
    ["T"] = "La Torre de Althalaxx",
  },
  [974] = {
    ["D"] = "Las fuentes termales de esta zona son un auténtico misterio. ¿De dónde viene el calor?$B$BTeniendo en cuenta su proximidad con las fuentes de la Cresta del Penacho en Llamas, me pregunto si el volcán puede tener algo que ver. He creado un termómetro para tomar lecturas de las temperaturas del volcán y me gustaría localizar la zona más caliente. ¡Mide las temperaturas en grados Kraklenheit!$B$BToma, cógelo, ¡y haz mediciones de las temperaturas en la cresta cada vez que localices una zona caliente!",
    ["O"] = "Krakle, en el Cráter de Un\'Goro, quiere que encuentres la zona más caliente de la Cresta del Penacho en Llamas.$B$BSiempre que des con un punto caliente, pulsa con el botón derecho sobre el termómetro para comprobar la temperatura. Sigue buscando hasta que encuentres el punto más caliente.",
    ["T"] = "Busca la fuente",
  },
  [975] = {
    ["D"] = "Antes del talismán darte, explico mejor cómo usarlo.$B$BSi encima lo llevas, caerán objetos de las criaturas que mates. Si encima el talismán no tienes no caerán. ¿Entiendes?$B$BLos objetos conocerás por el nombre... se llaman E\'ko. Si encuentras esos objetos, dáselos a la vieja Mau\'ari y ella te ayudará.$B$BEspera aquí, yo preparo tu talismán.",
    ["O"] = "Vuelve a hablar en un momento con la médica bruja Mau\'ari para recibir el talismán de la suerte.",
    ["T"] = "Alijo de Mau\'ari",
  },
  [976] = {
    ["D"] = "Delgren me ha pedido que le entregue varios mensajes y unos suministros a la centinela Brisa Estelar en Auberdine pero odio marcharme solo. Peligrosos enemigos acechan en el bosque y no me gustaría correr el riesgo de que esta información y estos suministros cayeran en manos malintencionadas.$B$BSi tú y alguien más pudieseis ser mis guardias, podría marcharme ahora. Con tu protección, seguro que estas cosas llegarán a buen puerto en Auberdine.",
    ["O"] = "Habla con Delgren el Purificador en la Atalaya de Maestra después ayudar a Feero a cruzar el Bosque de Vallefresno y dejarlo a buen recaudo.",
    ["T"] = "Suministros a Auberdine",
  },
  [977] = {
    ["D"] = "Ahora que tengo lo que necesito para cubrir las piezas mecánicas móviles, solo necesito una cosa más. Bueno, dos. ¡Dos cuernos!$B$BLos patriarcas y matriarcas Cardo Nevado tienen los cuernos más grandes así que, por supuesto, esos son los que quiero. Ese tipo de yeti habita una cueva al Sureste. Hay muchos de ellos, así no deberías tener ningún problema para encontrarlos.$B$BRecuerda que solo quiero los que tengan mejor aspecto... ¡no traigas cuernos rotos o arañados por favor!",
    ["O"] = "Reúne 2 cuernos prístinos de yeti para Umi Risitas en Vista Eterna.",
    ["T"] = "Yeti... ¿ya hemos llegado?",
  },
  [978] = {
    ["D"] = "Las plumas de lechúcico salvaje de las Tierras del Interior parecen tener rastros de cualidades mágicas. Estos poderes... no parece que las criaturas se valgan de ellos, es más bien algo inherente a la especie... parte de su esencia.$B$BDurante mis estudios pude oír que hay un gran número de lechúcicos salvajes en las tierras nevadas de Cuna del Invierno. ¿Tendrán esas criaturas los mismos poderes? Por favor, ayúdame a encontrar una respuesta, $N. Ve a Cuna del Invierno y recoge del suelo las plumas que se hayan caído. Cuando las tengas, vuelve a verme.",
    ["O"] = "Recoge 10 plumas Lunadón de Cuna del Invierno y regresa con Erelas Cieloámbar en Aldea Rut\'theran.",
    ["T"] = "Lechúcico Lunadón",
  },
  [979] = {
    ["D"] = "Sí, sí... estas plumas parecen albergar el mismo encantamiento mágico que ya había observado... Aunque no estoy seguro de qué es lo que podemos encontrar, te pido encarecidamente que sigas buscando respuestas, $N.$B$BUna colega mía viajó recientemente a Cuna del Invierno para observar a los lechúcicos salvajes en su propio hábitat. ¿Por qué no vas con ella y averiguas qué ha descubierto?",
    ["O"] = "Encuentra a Ranshalla en la zona oriental de Cuna del Invierno.",
    ["T"] = "Encuentra a Ranshalla",
  },
  [982] = {
    ["D"] = "En Costa Oscura, al norte de aquí, naufragaron dos barcos, el Amanecer de Plata y el Halo de Bruma. Hace un tiempo, los dos barcos fueron abordados por unos múrlocs cuando navegaban hacia Auberdine. Ahora yacen en el fondo del océano como trofeos de esos demonios.$B$BLos capitanes no sobrevivieron y sus cuadernos de bitácora y otros efectos aún siguen en sus cajas fuertes. Me gustaría que los recuperaras para nosotros. Es importante para los miembros de la tripulación que aún están por aquí.",
    ["O"] = "Recupera el arcón del Amanecer de Plata y la caja fuerte del Halo de Bruma para Gorbold Mano de Acero de Auberdine. Ambos objetos deberían estar en los cascos de los navíos naufragados al norte de la aldea.",
    ["T"] = "Profundo océano, mar inmenso",
  },
  [983] = {
    ["D"] = "¡Eh! Necesito ayuda con mi último invento, la caja mecánica. ¡Sirve para hablar con gente que está lejos!$B$BPuede que hayas visto alguna ya. Son unas cajas con muchas palancas. El problema que tienen es que necesitan un mantenimiento constante.$B$BCada una tiene un problema diferente, pero yo he hecho una cosa muy inteligente. He colocado cada caja cerca de las criaturas que tienen las piezas adecuadas para repararlas. Y ahora, hay que reparar la caja mecánica 827. Está al sur de Auberdine, muy cerca. Hacen falta 6 patas de reptador para arreglarla. Te pagaré...",
    ["O"] = "Reúne 6 patas de reptador y colócalas en la caja mecánica 827.",
    ["T"] = "Caja mecánica 827",
  },
  [984] = {
    ["D"] = "Algunos de mis hermanos fueron salvados de un fúrbolg corrupto en Teldrassil y yo he jurado impedir otras atrocidades. No permitiré que los nuestros resulten heridos... o algo peor.$B$BYa he detectado un par de indicios de corrupción en Costa Oscura, pero aún he de encontrar signos de que se haya extendido. Creo que lo lógico sería continuar la investigación con los fúrbolgs. ¿Podrías buscar alguno de sus campamentos y decirme si has visto algún signo de corrupción?",
    ["O"] = "Encuentra el campamento de fúrbolgs corruptos en Costa Oscura, vuelve a Auberdine y busca a Terenthis.",
    ["T"] = "La extensión de la amenaza",
  },
  [985] = {
    ["D"] = "Ya has demostrado que sabes rastrear a nuestros enemigos, $N. ¿Crees que también podrás enfrentarte a ellos?$B$BLo pregunto porque algunos aventureros prefieren eludir el combate directo...$B$BSi crees que estás a la altura de la tarea, puedo decirte que la mayor amenaza para nuestra gente proviene del campamento que hay al sur de Auberdine. Allí encontrarás una parte de la tribu Bosque Negro. Mata a 8 abrecaminos y a 5 soplavientos y vuelve aquí.",
    ["O"] = "Mata a 5 soplavientos y a 8 abrecaminos Bosque Negro y vuelve a Auberdine a buscar a Terenthis.",
    ["T"] = "La extensión de la amenaza",
  },
  [986] = {
    ["D"] = "$N, con tus habilidades me has ayudado mucho en mi empresa. ¿Podría abusar una vez más de tu buena disposición y pedirte que ayudes a Zarpadura y a su maestro Volcor? Puedo crear para ti una capa mágica que te permitirá caminar entre las criaturas de Costa Oscura sin ser $Gdetectado:detectada;.$B$BPero para tener suficiente material para crear la capa necesitaré 5 buenas pelambres de algún patriarca acechalunas o de una matriarca acechalunas. Puedes encontrar felinos de esos al sur, cerca del Río Culebra o cerca de Vallefresno, más al sur todavía.",
    ["O"] = "Encuentra 5 pelambres de Acechalunas refinadas y llévaselas a Terenthis a Auberdine.",
    ["T"] = "El maestro perdido",
  },
  [987] = "_",
  [990] = {
    ["D"] = "$N, mi señora, Raene Correlobo, te espera en la ciudad de Astranaar en Vallefresno. Con tu ayuda, quizás podamos reducir algo la corrupción que allí se ha extendido.$B$BDirígete al sur desde aquí y mantente cerca de la carretera, llegarás allí sin problemas si sigues estas instrucciones.",
    ["O"] = "Encuentra a Raene Correlobo en Vallefresno.",
    ["T"] = "El camino de Vallefresno",
  },
  [991] = {
    ["D"] = "Una amiga mía de Astranaar, Raene Correlobo, me pidió que enviara a un explorador al oeste, a Lago Falathim. Teronis se fue hace días, antes de que atacara la Horda.$B$BBuscaba un objeto que creía que podía detener los ataques de los fúrbolgs sobre nuestra gente: una vara creada por un difunto mago malvado.$B$BAntes de irse mencionó una gema para la vara; según él, estaría en un templo en una isla del lago.$B$BPor desgracia, ahora sabemos que una fuerza maligna ha invadido el lago. Por favor, $N, encuentra a mi amigo.|n",
    ["O"] = "Encuentra a Teronis en el Lago Falathim en Vallefresno.",
    ["T"] = "Encontrar a Teronis",
  },
  [993] = {
    ["D"] = "La capa está acabada, $N. Ha llegado el momento de ir a buscar a Volcor. Solo espero que aún estemos a tiempo.$B$BLa magia de la capa, una vez convocada, no durará mucho... solo unos cinco minutos. Tú decides cuándo es la ocasión de utilizarla: pero yo sugeriría que esperaras hasta que encuentres a Zarpadura.$B$BSi lo que me ha dicho es verdad, encontrarás a su maestro en una cueva al sur de aquí. Zarpadura te estará esperando al lado del camino. Cuando lo veas, saluda con un ademán y él te señalará el camino hacia su maestro.",
    ["O"] = "Encuentra a Volcor en Costa Oscura y dale la capa de Acechalunas encantada.",
    ["T"] = "El maestro perdido",
  },
  [994] = {
    ["D"] = "Muy bien, $N, llegó el momento. Salgamos de aquí. Cuando tú digas. Nos abriremos paso hasta el camino combatiendo. Zarpadura aún debería estar allí. Una vez allí, nos dividiremos.$B$BTerenthis querrá oír tu versión de la historia inmediatamente, así que dirígete a Auberdine en cuanto salgamos de aquí.",
    ["O"] = "Protege a Volcor hasta que lleguéis al camino y luego ve a Auberdine a hablar con Terenthis.",
    ["T"] = "Escaparse por la fuerza",
  },
  [997] = {
    ["D"] = "¿Te diriges hacia el sur? ¿Al Lago Al\'Ameth? De ser así hay algo que quiero pedirte...$B$BMi compañero Denalan tiene un campamento en el extremo oriental del lago, donde está estudiando y haciendo experimentos con la flora de Teldrassil. Encargó un paquete con tierras raras de Darnassus que llegó con retraso pues no hace mucho que lo recibimos aquí en Dolanaar.$B$B¿Podrías llevarle tú el paquete?",
    ["O"] = "Llévale el paquete con tierras raras a Denalan, que está en el Lago Al\'Ameth.",
    ["T"] = "Las tierras de Denalan",
  },
  [999] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Cuando los sueños se vuelven pesadillas",
  },
  [1000] = {
    ["D"] = "¡Atención, héroes!$B$B¡El Círculo Cenarion busca voluntarios sanos para que les ayuden a explorar las vastas nuevas fronteras de Kalimdor! Al oeste de Tanaris y Gadgetzan se extiende un páramo lleno de inimaginables peligros. El Círculo necesita voluntarios honorables para explorarlos en su lugar.$B$BTodos aquellos que estén interesados pueden solicitar audiencia con el Círculo Cenarion. ¡Hablad con el archidruida Hamuul Tótem de Runa en el Alto de los Ancestros en Cima del Trueno para más información!",
    ["O"] = "Habla con el archidruida Hamuul Tótem de Runa en el Alto de los Ancestros de Cima del Trueno acerca de la llamada del Círculo Cenarion para explorar las fronteras de Kalimdor.",
    ["T"] = "La nueva frontera",
  },
  [1001] = {
    ["D"] = "Del interior de la máquina sale una vocecita.$B$B\"¡Soy Zumbang! La siguiente caja mecánica está al norte de Auberdine, en la playa.$B$BEs la número 411 y requiere 3 ojos de trillador para ser reparada. ¡Están cerca de la costa! Los trilladores de Costa Oscura... *Hip*... Uy, perdón...$B$BComo la vez anterior, cuando le das los artículos a la caja mecánica, te escupe tu recompensa. *Hip*... ¿Qué? No, ¡estoy bien!\"",
    ["O"] = "Consigue 3 ojos de trillador de los trilladores de Costa Oscura en las profundidades del mar, cerca de la caja mecánica 411.",
    ["T"] = "Caja mecánica 411",
  },
  [1003] = {
    ["D"] = "Por encima de los ruidos que provienen de la máquina, oyes la voz de Zumbang que te llama.$B$B\"La shiguiente eshtá al shur, por el camino. No deberías tener problemas para encontrarla... jushto al lado d\'un eshcondite... cerca de un pantano. Creo que la eshcondí en unosh robushtosh... Digo, arburshtosh. Je, je, je... Shí, la caja mecánica 525 neceshita cuerosh cabelludosh grisháceohs de los Oshos Cardosh esos. ¡Tuercash! ¡Qué bocado! Grisháceosh... Je, je, je...\"$B$BLa máquina traga y traga y se desconecta. Y, de repente, vuelve a la vida.$B$B\"¡4!\"",
    ["O"] = "Consigue 4 cueros cabelludos grisáceos de los osos cardo grisáceos del sur de Auberdine y colócalos en la caja mecánica 525.",
    ["T"] = "Caja mecánica 525",
  },
  [1004] = {
    ["D"] = "¡Atención, héroes!$B$B¡El Círculo Cenarion busca voluntarios sanos para que les ayuden a explorar las vastas nuevas fronteras de Kalimdor! Al oeste de Tanaris y Gadgetzan se extiende un páramo lleno de inimaginables peligros. El Círculo necesita voluntarios honorables para explorarlos en su lugar.$B$BTodos aquellos que estén interesados pueden solicitar audiencia con el Círculo Cenarion. ¡Hablad con el archidruida Hamuul Tótem de Runa en el Alto de los Ancestros en Cima del Trueno para más información!",
    ["O"] = "Habla con el archidruida Hamuul Tótem de Runa en el Alto de los Ancestros de Cima del Trueno acerca de la llamada del Círculo Cenarion para explorar las fronteras de Kalimdor.",
    ["T"] = "La nueva frontera",
  },
  [1005] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Lo que acecha detrás",
  },
  [1006] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Lo que hay detrás",
  },
  [1007] = {
    ["D"] = "Te encargaría una tarea, $C.$B$BEstaba en mi barca, navegando sobre las sumergidas ruinas de Zoram, cuando los nagas me atacaron ¡surgiendo del agua y tratando de hacerme jirones con sus garras! Me escapé y el resto, según dicen, es historia.$B$BSoy una especie de arqueólogo, y estoy intentando recuperar algunos artefactos de la ciudad perdida.$B$B¿Irías a La Ensenada de Zoram, al suroeste, para recuperar las estatuillas antiguas que encuentres? Ellas son la razón por la que me enfrenté a los peligros de La Ensenada de Zoram.",
    ["O"] = "Consigue 10 estatuillas antiguas.",
    ["T"] = "Las antiguas estatuillas",
  },
  [1008] = {
    ["D"] = "El mal nos acecha desde la costa noroeste conocida como La Ensenada de Zoram.$B$BEs el lugar de descanso de la ciudad maldita de Zoram, destruida en el Diluvio y desde entonces sumergida bajo los mares. Se perdió para los elfos de la noche durante siglos. Perdida y casi olvidada.$B$BAhora, los nagas han regresado y en cuanto a sus motivos... los ignoramos. Pero el porqué importa poco; debemos ejecutar a estos desalmados ¡y devolverlos a los abismos!$B$BVuelve a mí cuando hayas completado tu misión.",
    ["O"] = "Llévale 20 cabezas de Colafuria a Shindrell Fuegoveloz en Astranaar.",
    ["T"] = "La Ensenada de Zoram",
  },
  [1009] = {
    ["D"] = "Temo que los secretos de la estatuilla antigua permanezcan ocultos para siempre a menos que encuentres la llave de su ancestral cerradura.$B$BEsa llave es un anillo, el Anillo de Zoram. Pertenecía a los gobernantes de esta ciudad antes de que las olas del Diluvio la inundaran.$B$BDurante años el anillo estuvo perdido pero los nagas lo encontraron recientemente.$B$BSu jefe, Ruuzel, mora en una isla en los confines norte de Zoram. Te lo ruego, $N, ¡recupera ese anillo para mí!",
    ["O"] = "Consigue el Anillo de Zoram.",
    ["T"] = "Ruuzel",
  },
  [1010] = {
    ["D"] = "Existe una planta que crece entre las Ruinas de Bathran al otro lado de la carretera, al este. La planta se llamaba cabello de Bathran y era famosa por curar enfermedades espirituales.$B$BAhora la Horda se dedica a recolectarla para sus propios planes malvados.$B$BHay una niña enferma en Astranaar y creo que se trata de algo más que una enfermedad física. Prometí a mi maestro que ayudaría a encontrar una cura, pero no soy un guerrero.$B$B¿Irás a las Ruinas de Bathran a por las plantas?",
    ["O"] = "Consigue 8 cabellos de Bathran.",
    ["T"] = "Cabello de Bathran",
  },
  [1012] = {
    ["D"] = "Los druidas han muerto a manos de sus líderes, que enloquecieron cuando la Horda corrompió el corazón del bosque. Puedo sentir la corrupción incluso desde aquí y no me atrevo a ir más allá.$B$BMientras que yo me armonizo con Vallefresno y el corazón del bosque, tú podrías... bajar hasta el túmulo y hacer lo debido. Te he dejado algunas pistas por allí.$B$BBien que me pese, debo pedirte que hagas descansar en paz a Taneel Leñoscuro, Uthil Llamaluna y Mavoris Quiebranubes, los que un día fueron grandes líderes druidas.",
    ["O"] = "Mata a los druidas enloquecidos: Taneel Leñoscuro, Uthil Llamaluna y Mavoris Quiebranubes.",
    ["T"] = "Druidas dementes",
  },
  [1013] = {
    ["D"] = "$N, en el Castillo de Colmillo Oscuro hay un libro que deseo para mi colección: el Libro de Ur. Ur era un gran mago de Dalaran antes de que llegara la Plaga; sus estudios sobre otros mundos son muy valiosos para... ciertas facciones entre los Renegados.$B$BVe al Castillo de Colmillo Oscuro y tráeme el libro. Informaré a la Dama Oscura de tus servicios...",
    ["O"] = "Lleva el Libro de Ur al vigilante Bel\'dugur al Apothecarium de Entrañas.",
    ["T"] = "El libro de Ur",
  },
  [1014] = {
    ["D"] = "Arugal sigue viviendo en el Castillo de Colmillo Oscuro. No podemos reclamar Argénteos como bastión estratégico para la Dama Oscura hasta que no hayamos matado a Arugal.$B$BMe encargaré de erradicar su magia, $N. Pero dejo en tus manos que Arugal se encuentre con la muerte que tanto merece.$B$BVe al Castillo de Colmillo Oscuro y pon fin de una vez por todas a los terribles hechizos de Arugal. ¡Tráeme la cabeza del malvado mago!",
    ["O"] = "Mata a Arugal y lleva su cabeza a Dalar Tejealba en El Sepulcro.",
    ["T"] = "Arugal debe morir",
  },
  [1016] = {
    ["D"] = "Podríamos haber defendido el Refugio Brisa de Plata si la Horda no se hubiera adueñado de nuestros elementales de agua del Lago Mystral.$B$BUno de sus chamanes invocó a Marearess, un antiguo y rencoroso espíritu del agua que contaminó a nuestros elementales y los volvió en nuestra contra. Frente al ataque del lago y de la Horda, no tuvimos más remedio que retirarnos a esta torre.$B$BVe al este. Retoma el lago para que algún día podamos recuperar el refugio.",
    ["O"] = "Mata a 12 elementales de agua podrida y marearess.",
    ["T"] = "Retomar el Lago Mystral",
  },
  [1017] = {
    ["D"] = "Solo hay un mago no-muerto en Los Baldíos capaz de envilecer a tantos elementales de agua. También es el único lo suficientemente inconsciente para intentarlo. Sarilus Fuenteviciosa probablemente se encuentre en la cumbre de Cima Calígine, riéndose de mí.$B$B$N, debes aventurarte en territorio enemigo y acabar con ese bellaco. Ya estoy cansada de su insensatez y tenemos que demostrarle lo que les ocurre a quienes irritan a las centinelas.$B$B¡Destrúyelo y tráeme su cabeza! Ve con cuidado, la Horda podría estar vigilándote.",
    ["O"] = "Mata a Sarilus Fuenteviciosa y lleva su cabeza a la centinela Velene Golpe Estelar en el Refugio Brisa de Plata.",
    ["T"] = "Mago invocador",
  },
  [1018] = {
    ["D"] = "¡Atención, héroes!$B$B¡El Círculo Cenarion busca voluntarios sanos para que les ayuden a explorar las vastas nuevas fronteras de Kalimdor! Al oeste de Tanaris y Gadgetzan se extiende un páramo lleno de inimaginables peligros. El Círculo necesita voluntarios honorables para explorarlos en su lugar.$B$BTodos aquellos que estén interesados pueden solicitar audiencia con el Círculo Cenarion. ¡Hablad con el archidruida Hamuul Tótem de Runa en el Alto de los Ancestros en Cima del Trueno para más información!",
    ["O"] = "Habla con el archidruida Hamuul Tótem de Runa en el Alto de los Ancestros de Cima del Trueno acerca de la llamada del Círculo Cenarion para explorar las fronteras de Kalimdor.",
    ["T"] = "La nueva frontera",
  },
  [1019] = {
    ["D"] = "¡Atención, héroes!$B$B¡El Círculo Cenarion busca voluntarios sanos para explorar las vastas fronteras nuevas de Kalimdor! Al oeste de Tanaris y Gadgetzan se extiende un páramo lleno de inimaginables peligros. El Círculo necesita voluntarios honorables para explorarlos en representación suya.$B$BTodos aquellos que estén interesados pueden solicitar audiencia en el Círculo Cenarion. ¡Hablad con el archidruida Fandral Corzocelada de Darnassus para más información!",
    ["O"] = "Habla con el archidruida Fandral Corzocelada en el Enclave Cenarion de Darnassus sobre la llamada del Círculo Cenarion para explorar las fronteras de Kalimdor.",
    ["T"] = "La nueva frontera",
  },
  [1020] = {
    ["D"] = "$N, ahora que está bañado en la luz sagrada de Elune, creo que el brebaje ayudará a la niña enferma. Por favor, llévalo a Astranaar y dáselo al padre de la niña, Pelturas Lunablanca.$B$BPara llegar a Astranaar, sigue el camino hacia el sur y luego al este. Si quieres llegar más rápido, habla con la centinela Avana.",
    ["O"] = "Llévale la cura de Orendil a Pelturas Lunablanca en Astranaar, en Vallefresno.",
    ["T"] = "Cura de Orendil",
  },
  [1021] = {
    ["D"] = "No sé de dónde has salido, pero necesito que me ayudes. Mis hermanas se fueron a quitarle la Rama de Cenarius al sátiro. Dijeron que volverían pronto, ¡pero hace ya dos días que se fueron!$B$B¡Encuéntralas, $N! Iban a buscar las ruinas de un templo de los elfos de la noche en Xavian, al noroeste. Busca a Anilia, era la líder del grupo.",
    ["O"] = "Encuentra a Anilia en Xavian, en Vallefresno.",
    ["T"] = "¡Vil sátiro! ¡Dríades en peligro!",
  },
  [1022] = {
    ["D"] = "Aunque hemos puesto muchos recursos y esfuerzo en expulsar hacia el norte a los demonios restantes en Frondavil, no hemos tenido mucho éxito. Hemos logrado mantener apartados de Vallefresno a casi todos los demonios.$B$BHacia el norte, cerca de los límites de Frondavil, el altar en ruinas de Mel\'Thandris ha sido tomado por unos misteriosos hombres lobo. Sus llamadas te hielan la sangre y han hecho que se conozca esa zona como la Vega del Aullido. Es posible que el Libro de Mel\'Thandris que se conserva en el altar arroje algo de luz sobre el motivo que ha traído a estos hombres lobo.",
    ["O"] = "Ve a La Vega del Aullido y estudia el Libro de Mel\'Thandris, vuelve entonces junto a la Centinela Melyria Sombrigélida en la Canción del Bosque.",
    ["T"] = "La Vega del Aullido",
  },
  [1023] = {
    ["D"] = "Mientras rebuscas en las pertenencias de Teronis, encuentras un diario bien conservado.$B$BTomas el diario y dejas el cadáver de Teronis como está.$B$BSolo falta encontrar la gema que Teronis estaba buscando. Orendil mencionó que estaba en esta zona. A lo mejor la tienen los zarcillos...",
    ["O"] = "Encuentra la gema resplandeciente.",
    ["T"] = "Encontrar a Teronis",
  },
  [1024] = {
    ["D"] = "Ah, ahí estás, $N. He leído con cautela las notas que redactó Teronis y parece que tu siguiente paso será encontrar las demás piezas de la vara de Dartol.$B$BYo empezaría por dirigirme al este por el camino que conduce a la Torre de la Algaba.$B$BUna dríade llamada Shael\'dryn podrá indicarte mejor. Búscala.$B$BLleva la gema contigo, Shael\'dryn la necesitará si te va a ayudar a recrear la vara.",
    ["O"] = "Habla con Shael\'dryn en la Torre de la Algaba en Vallefresno.",
    ["T"] = "Encontrar a Teronis",
  },
  [1025] = {
    ["D"] = "Antes de que vayas a la Poza de la Luna, tengo otra tarea que ayudará al pueblo de Vallefresno si te apetece poner a prueba tus habilidades.$B$BPor la carretera que va hacia el este antes de llegar al Río Falfarren, los fúrbolgs han estado asaltando a los viajeros. Sus torturadas mentes alimentándose del miedo y su apetito saciándose con sangre inocente.$B$BAyuda a las centinelas a proteger las tierras mostrando hacia esos monstruos la misma misericordia que ellos para con los de nuestra clase.$B$BMe parece que sus campamentos están al sur de la carretera... ve con cuidado.",
    ["O"] = "Mata a 1 vigía del cubil, 2 ursas, 10 totémicos y 12 guerreros de la tribu de los Maderiza y vuelve junto a Raene Correlobo en Astranaar.",
    ["T"] = "Una defensa agresiva",
  },
  [1026] = {
    ["D"] = "Ojalá tus nuevas fueran mejores.$B$BRaene tiene razón, he oído hablar de la creación de Dartol y de los antárboles de la región, al menos una vez. Ahora se han corrompido, su naturaleza se ha pervertido.$B$BPara encontrar la siguiente pieza de la vara, tendrás que encontrar la llave que abre el cofre en el que se encuentra.$B$BLa llave de madera está en uno de los antárboles al noreste desde aquí, en la Vega del Aullido, cerca de los límites de Frondavil. Cuando la tengas, busca un pequeño claro en la vega a través del túnel. El cofre debería estar escondido por ahí.",
    ["O"] = "Encuentra la llave de madera y úsala para conseguir un trozo de asta de hierro de la vara de Dartol.",
    ["T"] = "Encontrar a Teronis",
  },
  [1027] = {
    ["D"] = "Los druidas de la Zarpa poseen la última pieza de la vara. Cuando pedí a su líder, Oso Rajazarza, que la devolviera, ¡casi me matan sus guardias! Se les veía claramente afectados por la corrupción del corazón del bosque perpetrada por la Horda.$B$BSin embargo, tengo una idea sobre cómo puedes introducirte en la cueva de Oso y obtener la empuñadura.$B$BDesde que era un fauno, se me ha dado bastante bien hacerme el muerto. ¡Funciona de maravilla con los osos! ¡Te puedo enseñar! Dirígete hacia el este a El Rincón Lóbrego, y si intentan atraparte, utiliza mi técnica.",
    ["O"] = "Consigue el pomo de hierro, la última pieza de la vara de Dartol.",
    ["T"] = "Encontrar a Teronis",
  },
  [1028] = {
    ["D"] = "Aquí tienes la vara completada, $N. Volver a construirla ha sido sencillo... encontrar el santuario que tiene el poder para cargarla de nuevo será la parte complicada.$B$BCentrándote y con un poco de empeño, no debería ser demasiado difícil para alguien como tú, con tantos logros en tu haber.$B$BDirígete al sureste desde aquí, hay muy pocos caminos, así que ten cuidado, y mantén la vista hacia el norte. El santuario se encuentra oculto en las montañas que rodean este claro.$B$BUna vez allí, coloca la vara en el interior del santuario... el resto es magia. Je, je.",
    ["O"] = "Encuentra el Santuario Oculto y encanta de nuevo la vara de Dartol.",
    ["T"] = "Encontrar a Teronis",
  },
  [1029] = {
    ["D"] = "Te sugiero que encuentres a Raene y le digas que la vara está recompuesta. Seguro que sabrá aconsejarte sobre el siguiente paso.$B$BVe con cautela... presiento una gran fuente de poder que emana de la vara tan buena como maligna. Espero que tú y Raene sepáis lo que hacéis.$B$BBuena suerte, $N.",
    ["O"] = "Entrega la vara de Dartol a Raene Correlobo en Astranaar, en Vallefresno.",
    ["T"] = "Encontrar a Teronis",
  },
  [1030] = {
    ["D"] = "No estoy del todo segura de cuál era la intención de Teronis. Si era cierto lo que contaba del poder de la vara, entonces hasta te permitirá adoptar otra forma, probablemente la de un fúrbolg.$B$BQuizás pretendía usar sus poderes para acercarse a ellos, pero incluso la criaturas sin corromper lo asesinarían si no iba con cuidado. Transformado gracias a la vara, Dartol tendría que hablar con un fúrbolg que no estuviera corrupto.$B$BEmpezaría la búsqueda por uno al que llaman Krolg. Encontrarás su cabaña al sureste del Lago Mystral.",
    ["O"] = "Usa la vara de Dartol para comunicarte con Krolg en la Cabaña de Krolg, en Vallefresno.",
    ["T"] = "Encontrar a Teronis",
  },
  [1031] = {
    ["D"] = "Anilia te pone las manos en los hombros y te acerca. Entre jadeos, señala el arco al norte, antes de que sus ojos se cierren y el último soplo de vida salga de su boca. \"Se la ha llevado... Geltharis...\" En tu corazón sabes que el deseo póstumo de Anilia es que la Rama de Cenarius vuelva a las manos de la dríade.",
    ["O"] = "Consigue la Rama de Cenarius.",
    ["T"] = "La Rama de Cenarius",
  },
  [1032] = {
    ["D"] = "No puedo creer que Anilia ya no esté... Solíamos pasarnos los días jugando juntas en el bosque. Casi siento... tristeza.$B$BSé de algo que podría hacer que me sienta mejor. ¡Por favor, ejecuta a los sátiros que me han arrebatado a Anilia! Tráeme sus cuernos como prueba de tus actos y tendrás mi gratitud eterna.|n",
    ["O"] = "Consigue 16 cuernos de sátiro.",
    ["T"] = "¡Muerte a los sátiros!",
  },
  [1033] = {
    ["D"] = "Esperaba que la cura de Orendil salvara a Relara pero me temo que solo ha frenado su enfermedad. ¡Tenemos que encontrar una medicina más eficaz! Por suerte, Orendil me habló de otros remedios que debía intentar si el suyo no funcionaba.$B$BLa lágrima de Elune es un tipo de piedra que solo se halla en un lugar: las ruinas del Lago Iris, destruidas por la erupción del Pico del Trueno, al noreste de Astranaar. Orendil dijo que la piedra se llevaría la corrupción.$B$BPor favor, $N, ¡tráeme la lágrima de Elune y reza para que acabe con la enfermedad de mi niña!",
    ["O"] = "Consigue la lágrima de Elune.",
    ["T"] = "Lágrima de Elune",
  },
  [1034] = {
    ["D"] = "La lágrima de Elune logró curar en parte la enfermedad de mi hija. Tiene menos pesadillas y le estoy agradecido a la diosa por su bendición. Pero... sigue teniendo la fiebre muy alta.$B$BOrendil me habló de un polvo de escarcha que se encuentra en Las Ruinas del Polvo Estelar. Quizás mitigue la fiebre de Relara. Te ruego que consigas un poco de ese polvo y me lo traigas.$B$BLas Ruinas del Polvo Estelar se encuentran en el sur, $N. Que Elune haga breve tu viaje.",
    ["O"] = "Llévale 5 puñados de polvo estelar a Pelturas en Astranaar.",
    ["T"] = "Las Ruinas del Polvo Estelar",
  },
  [1036] = {
    ["D"] = "$N... sí, he oído hablar de ti. Me han dicho que no eres $Gamigo:amiga; de la Bahía del Botín, igual que nosotros, los Bucaneros Velasangre. El maestro de flota Firallon está buscando reclutas robustos y fuertes como tú para una misión especial.$B$BSi te interesa, dirígete a El Velo Carmesí y habla con él bajo cubierta. Está en La Costa Salvaje, al este de aquí, junto a Las Mareas Vivas y La Damisela Afortunada.",
    ["O"] = "Habla con el maestro de flota Firallon a bordo de El Velo Carmesí cerca de la costa de la Vega de Tuercespina.",
    ["T"] = "¡Adelante, pillín!",
  },
  [1037] = {
    ["D"] = "Velinde Cantoestelar era mi predecesora aquí en el Bosque de Vallefresno. Al principio parecía que tenía la situación en Frondavil bajo control pero poco a poco sus esfuerzos flaquearon. Un buen día desapareció sin más.$B$BMe enviaron aquí para continuar su trabajo. Aunque me temo que no sé nada de la sacerdotisa. Quizás Thyn\'tel Tejespada, una Comandante de las centinelas, sepa algo sobre su desaparición que no me haya comentado.$B$BSeguro que entenderá la trascendencia de tal información.",
    ["O"] = "Habla con Thyn\'tel Tejespada en el Bancal del Guerrero en Darnassus.",
    ["T"] = "Velinde Cantoestelar",
  },
  [1038] = {
    ["D"] = "¿El Libro de Mel\'Thandris te enseñó esto? Supongo que no hacemos daño a nadie si te permito examinar sus pertenencias. Esta llave abre el cofre en el que ella guardaba sus cosas en el barracón de centinelas. Llevaba un diario de servicio. Si hay algo que aprender por aquí, estará en su diario.$B$BDebería decirte que los centinelas creen que tenía sus propias razones para irse y esperan que vuelva en cualquier momento. La sacerdotisa ha demostrado en el pasado que merece toda nuestra confianza.",
    ["O"] = "Busca el diario de Velinde en su cofre y llévaselo, junto con la llave, a Thyn\'tel Tejespada a Darnassus.",
    ["T"] = "Las pertenencias de Velinde",
  },
  [1039] = {
    ["D"] = "Trinquete es el único puerto de Los Baldíos. Seguramente Velinde encontró un navío mercante en Trinquete que la llevó a la Cala Aguasnegras, en Azeroth. Hemos tratado poco con los goblins que llevan el puerto, pero el capataz del puerto debería tener información sobre los barcos de pasajeros.$B$BSigue el camino del sureste que atraviesa Vallefresno y te encontrarás en Los Baldíos. Pero ve con cuidado, $N. Hay guerreros de la Horda patrullando esa tierra. En el puerto estarás a salvo.",
    ["O"] = "Ve a Trinquete y habla con el maestro de muelles Vertipeluca.",
    ["T"] = "El puerto de Los Baldíos",
  },
  [1040] = {
    ["D"] = "Ah sí, por fin lo encontré. Tenía que haberme dicho que había estado por aquí hace tanto tiempo. Veamos.$B$BVelinde. Reservó un billete para Bahía del Botín en el Pigargo negro. No figura nada más, así que se supone que llegaron a puerto sanos y salvos.$B$BMe temo que no puedo ayudarte mucho más... bueno, ella preguntó sobre una expedición por tierra en esa parte del mundo y le hablé de Ruzzgot, un conductor de caravanas establecido en Bahía del Botín. Puede que la tal Velinde viajara con él.$B$BVenga, vete ya. Tengo otras cosas que hacer.",
    ["O"] = "Coge un barco a Bahía del Botín y habla con el caravanero Ruzzgot.",
    ["T"] = "Pasaje a Bahía del Botín",
  },
  [1041] = {
    ["D"] = "Parece ser que me había equivocado contigo, y eso no me suele pasar todos los días.$B$BResulta que recuerdo a esa Velinde a quien estás buscando. No todos los días una sacerdotisa elfa de la noche quiere viajar en una vieja y sucia, aunque genial para transportes, caravana como la mía.$B$BNos separamos en el norte, ella se dirigía a Villa Oscura. El secretario de allí guarda todo tipo de registros. Quizás tenga alguna información útil.$B$BTen cuidado en la jungla, es un lugar lleno de peligros mortales en todo momento.",
    ["O"] = "Habla con el secretario Daltry en Villa Oscura.",
    ["T"] = "La ruta de la caravana",
  },
  [1043] = {
    ["D"] = "Tu historia suena convincente... No acabo de entender tus motivos pero, si tus negocios en el Bosque del Ocaso implican cazar huargen en cualquier número, no necesito entender nada.$B$BHay una mina al sur que ha sido invadida por huargen. Salieron de la nada pero, por lo que sabemos, es precisamente donde fueron encontrados por vez primera.$B$BTú ocúpate de tus negocios. Y si encuentras algo importante, compártelo conmigo, si no es mucho pedir. Aceptaremos cualquier ayuda en nuestra guerra contra el mal.",
    ["O"] = "Busca signos de la Guadaña de Elune y regresa a Villa Oscura, con Jonathan Carevin.",
    ["T"] = "La Guadaña de Elune",
  },
  [1044] = {
    ["D"] = "Solucionaremos el problema de los huargen, no lo dudes. Ese mal que tu amiga trajo a nuestros bosques será contenido. Y has de saber que no le guardo rencor por sus actos. En estos tiempos se fraguan extraños acontecimientos, $N, la oscuridad no descansa.$B$BNo te entretengo más. Sospecho que hay otros que también deberían saber de lo que has encontrado en las minas del Bosque del Ocaso.",
    ["O"] = "Regresa a Darnassus y habla con Thyn\'tel Tejespada.",
    ["T"] = "La respuesta a las preguntas",
  },
  [1045] = {
    ["D"] = "Estoy seguro de que Dartol, el difunto mago, pretendía usar su vara para someternos y destruirnos igual que lo han hecho los orcos y los elfos de la noche. Ahora le daremos un uso nuevo.$B$BRan Sangradientes es el rey de todos los fúrbolgs de Vallefresno. Su tribu, los Sangradientes, son los más poderosos y se recluyen en un campamento justo al este.$B$BMátalos a todos y tráeme la calavera de Ran. Veamos si le podemos dar un buen uso.",
    ["O"] = "Consigue la calavera de Ran Sangradientes y la vara de Dartol.",
    ["T"] = "Encontrar a Teronis",
  },
  [1046] = {
    ["D"] = "Ahora, toma la vara y, mientras utilizas su poder, habla con mi hermano, Ota Wen.$B$BOta está entre los otros fúrbolgs que los orcos han capturado y encerrado en la jaula gigante del lado norte de Refugio Brisa de Plata. La vara te permitirá convencer a Ota y a los otros fúrbolgs levantarse y atacar a la Horda.$B$BEntonces podrás ir a donde te lleve tu camino. Solo recuerda que has encontrado un nuevo amigo por lo que has hecho.",
    ["O"] = "Usa la vara de Dartol, habla con Ota Wen y convéncele para que se levante contra sus opresores de la Horda.",
    ["T"] = "Encontrar a Teronis",
  },
  [1047] = {
    ["D"] = "¡Atención, héroes!$B$B¡El Círculo Cenarion busca voluntarios sanos para explorar las vastas fronteras nuevas de Kalimdor! Al oeste de Tanaris y Gadgetzan se extiende un páramo lleno de inimaginables peligros. El Círculo necesita voluntarios honorables para explorarlos en representación suya.$B$BTodos aquellos que estén interesados pueden solicitar audiencia en el Círculo Cenarion. ¡Hablad con el archidruida Fandral Corzocelada de Darnassus para más información!",
    ["O"] = "Habla con el archidruida Fandral Corzocelada en el Enclave Cenarion de Darnassus sobre la llamada del Círculo Cenarion para explorar las fronteras de Kalimdor.",
    ["T"] = "La nueva frontera",
  },
  [1048] = {
    ["D"] = "Mientras la Dama Oscura se encarga de retener el avance del Rey Exánime por el norte, yo me ocupo de los problemas que tienen lugar cerca de nuestro hogar.$B$BSin duda, los humanos que se hacen llamar la Cruzada Escarlata representan la mayor amenaza en nuestra zona. Los Claros de Tirisfal están atestados de estas pequeñas hormigas rojas.$B$BVe al Monasterio, $C, y acaba con la alta inquisidora Melenablanca y sus principales tenientes.",
    ["O"] = "Mata a la alta inquisidora Melenablanca, al comandante Escarlata Mograine, a Herod el Campeón Escarlata y al maestro de canes Loksey, y después ve a ver de nuevo a Varimathras a Entrañas.",
    ["T"] = "En el Monasterio Escarlata",
  },
  [1049] = {
    ["D"] = "Las intenciones del Anillo de la Tierra para con nuestros hermanos eran puras. ¿Pero quién sabe las motivaciones de los que confabulaban con el Consejo de los Ancianos?$B$BLa historia de los Renegados con los que nos aliamos está plagada de grandes engaños, de ahí que nuestro pacto con ellos haya sido breve. Quizás Cairne fue lo suficientemente inteligente para atender a las advertencias de Orgrimmar...$B$BEn lo más profundo del Monasterio Escarlata se encuentra el Compendio de los Caídos, custodiado por agresivos y fanáticos humanos. Su método proviene de la locura pero su trabajo de investigación puede resultar de utilidad.",
    ["O"] = "Recupera el Compendio de los Caídos del Monasterio que se encuentra en los Claros de Tirisfal y regresa ante Sabio Buscador de la Verdad, que está en Cima del Trueno.",
    ["T"] = "Compendio de los Caídos",
  },
  [1051] = {
    ["D"] = "Aunque tengo libre albedrío, no puedo seguir sirviendo a la Dama Oscura. Siento que se acaba mi tiempo en Azeroth. Mi fuerza vital está mermada.$B$B¡Es culpa del interrogador Vishas! ¡Me torturó durante semanas! Me quitó la alianza y se la dio a su mujer. ¡Maldito!$B$BMe vengaré... ¡Juro que me vengaré!$B$BEse cretino pomposo anda presumiendo de su mujer, Nancy, que vive junto al Lago Lordamere. Liquida a esa bruja y llévale la alianza a mi mujer, Monika, a Molino Tarren.$B$BCumple mi último deseo...",
    ["O"] = "Lleva la alianza de Vorrel Sengutz a Monika Sengutz de Molino Tarren.",
    ["T"] = "La venganza de Vorrel",
  },
  [1053] = {
    ["D"] = "Una vez serví a la Cruzada Escarlata con honor, lealtad y orgullo. Creí que su causa era noble: Librar Azeroth de los no-muertos.$B$BPero según pasé más y más tiempo en el Monasterio, en los Claros de Tirisfal, me di cuenta de que habían perdido la noción de la realidad. Ahora creen que todo aquel que no lleva el tabardo de la Cruzada, está apestado. Hombres y mujeres inocentes torturados porque, supuestamente, estaban apestados.$B$BHay que poner fin a la Cruzada Escarlata, en el nombre de la Luz. Tú, $N, debes destruir al enloquecido régimen.",
    ["O"] = "Mata a la alta inquisidora Melenablanca, al Comandante Escarlata Mograine, a Herod, el Campeón Escarlata y al maestro de canes Loksey. A continuación, preséntate ante Raleigh el Devoto, en Costasur.",
    ["T"] = "En el nombre de la Luz",
  },
  [1054] = {
    ["D"] = "Directamente al norte de Astranaar están los fúrbolgs Piel de Cardo. Mis exploradores me informan de que en los últimos meses han aumentado mucho sus efectivos. Si acaban siendo demasiados, podrían envalentonarse y atacar directamente Astranaar.$B$BMata a tantos de su clase como puedas pero tráeme la calavera de su jefe como prueba de que hemos reprimido el movimiento... por ahora.$B$BSeguiré aquí en Astranaar y esperaré tus noticias. Que Elune esté contigo.",
    ["O"] = "Llévale la calavera de Dal Zarpasangrante a Raene Correlobo en Astranaar.",
    ["T"] = "Matanza selectiva",
  },
  [1055] = {
    ["D"] = "La vara, ahora completa, está en tus manos. Shael\'dryn se alegrará al enterarse de tu éxito.",
    ["O"] = "Vuelve junto a Shael\'dryn en la Poza de la Luna.",
    ["T"] = "Encontrar a Teronis",
  },
  [1056] = {
    ["D"] = "Según los espíritus del bosque, eres valiente y no te importa viajar.$B$BHacia el sur, cerca del Lago Mystral, se encuentra un túnel conocido como El Paso del Espolón. Este túnel lleva a una zona llamada Risco Cortaviento en Sierra Espolón. Cuando llegues allí, prosigue tu camino hacia el suroeste hasta pasar el Lago del Peñasco, dirígete entonces hacia el norte por una cuesta empinada hasta alcanzar la Cima del Espolón.$B$BAllí aguarda el vigilante Albagorm. Presta atención a su encargo, $R.$B$BEl viaje será peligroso.",
    ["O"] = "Ve junto al vigilante Albagorm en la Cima del Espolón.",
    ["T"] = "Viaje a Cima del Espolón",
  },
  [1057] = {
    ["D"] = "Como $Gprotector:protectora; de los espíritus, sin duda sabrás por qué siento tanta inquietud.$B$BHas viajado a través de estas montañas para llegar a la cumbre. Seguro que has visto la parodia en la que se ha convertido esta tierra. La codicia ha llevado a la destrucción de estas colinas sagradas.$B$BEl ambicioso ejército de industriales mecanizados, incendió La Vega Carbonizada, al sur. Peor aún, los Furia Sangrienta que han invadido la tierra, nos niegan el paso para comenzar a reconstruir.$B$B¡Expulsa a las malvadas arpías, $N!",
    ["O"] = "El vigilante Albagorm en la Cima del Espolón quiere que mates a 7 arpías Furia Sangrienta, a 7 emboscadoras Furia Sangrienta, a 7 destripadoras Furia Sangrienta y a 7 malaplumas Furia Sangrienta.",
    ["T"] = "La reconquista de La Vega Carbonizada",
  },
  [1058] = {
    ["D"] = "¡Qué bien, qué bien!$B$B¡Tres cajitas para tres elfitos! ¿Pero qué haremos con nuestros simpáticos amiguitos \"abraza-árboles\"? Mmm... Démosles un poco de... ¿Cómo la llaman...? ¡Magia del bosque!$B$B¡Necesitaré un buen montón de savia del Espolón! Tráeme algunos mostachos Crepusculares de esos horribles felinos. Y muchos ojos de trotadores... Nunca se puede tener suficientes ojos de trotadores. Oh, y que no nos olvidemos, una preciosa escama de dragón hada.$B$B¡Vete rápidamente a la Cima del Espolón, $N! Así podré crear algo de magia del bosque.",
    ["O"] = "El médico brujo Jin\'Zil en Malaka\'jin quiere 5 porciones de savia del Espolón, 5 mostachos Crepusculares, 30 ojos de trotadores y una escama de dragón hada.",
    ["T"] = "Magia del bosque de Jin\'Zil",
  },
  [1059] = {
    ["D"] = "Hemos sometido a los Furia Sangrienta. Pero volverán a reagruparse pronto si no hacemos nada por remediarlo.$B$BAl sur de Los Baldíos hay una tierra conocida como Las Mil Agujas. Si te diriges hacia el oeste en Las Mil Agujas, hasta que llegues al Linde del Bosque de Feralas, encontrarás a un druida llamado el Díscolo Falfindel.$B$BEste sabio druida es capaz de invocar poderosos espíritus del bosque que podrán ayudarnos a reclamar La Vega Carbonizada.$B$B¡Háblale de nuestra gran necesidad! ¡Ve a buscarlo!",
    ["O"] = "Ve a ver al Díscolo Falfindel en la frontera entre Las Mil Agujas y Feralas.",
    ["T"] = "La reconquista de La Vega Carbonizada",
  },
  [1060] = {
    ["D"] = "Has hecho un gran trabajo para mí, $N. Eres como una pata de conejo de la suerte y eso es algo que siempre me alegro de ver.$B$BLas arpías no son de los alrededores de Los Baldíos. Proceden de un lugar llamado Espolón, muy lejos al noroeste desde aquí. Aunque es probablemente un lugar peligroso para ti, necesito que le entregues esta misiva a un viejo amigo que vive allí, un médico brujo que responde al nombre de Jin\'Zil.$B$BSe alegrará de saber que Serena Sangrepluma ha muerto y creo que Espolón te parecerá un lugar interesante.",
    ["O"] = "Entrégale la carta de Darsok a Jin\'Zil en su cueva de Malaka\'jin, en Espolón.",
    ["T"] = "Carta para Jin\'Zil",
  },
  [1061] = {
    ["D"] = "Los espíritus de Sierra Espolón están enojados por la intrusión de los goblins y sus siervos. Por eso la tierra se ha lamentado y aquellos que aún escuchan han oído su súplica.$B$BSe envió a Seereth Petrajada, una prometedora chamán de la Horda. Nos dijo que los goblins están deforestando la tierra a gran velocidad y pidió ayuda.$B$BVe a Sierra Espolón a hablar con ella; su campamento está cerca de la Vega del Gran Bosque. Lo mejor será que sigas el camino hacia el oeste desde El Cruce, en Los Baldíos.",
    ["O"] = "Habla con Seereth Petrajada en la frontera entre Sierra Espolón y Los Baldíos.",
    ["T"] = "Los espíritus del Espolón",
  },
  [1062] = {
    ["D"] = "La empresa goblin Ventura y Cía. llegó a Sierra Espolón, cortando árboles y quemando enormes secciones de bosque. Los espíritus del lugar están prácticamente ciegos de dolor e ira. ¡Debemos detener a Ventura y Cía.!$B$BDirígete al noroeste, más allá de la Vega del Gran Bosque hasta el Risco Cortaviento. Encontrarás a los goblins y a sus siervos en pleno trabajo. Demuéstrales que la Horda no permitirá la explotación de Espolón. Y hazlo utilizando el único idioma que comprenden...$B$BLa violencia.",
    ["O"] = "Mata a 15 leñadores de Ventura y Cía. y vuelve a ver a Seereth Petrajada, en la frontera entre Espolón y Los Baldíos.",
    ["T"] = "Los invasores goblin",
  },
  [1063] = {
    ["D"] = "Para conectar realmente con los espíritus de Espolón, debemos consultar con los ancestros tauren.$B$BNo sé el nombre de muchos de los líderes, pero sí conozco uno: Magatha. He oído que es muy vieja y muy sabia. Si hay alguien que pueda conseguir ayuda espiritual de los tauren, es ella.$B$BMagatha vive en la capital tauren de Cima del Trueno, en el Alto de los Ancestros. En el risco que queda al noreste de la ciudad. Habla con Magatha y cuéntale el peligro que corre Sierra Espolón.",
    ["O"] = "Habla con Magatha en Cima del Trueno.",
    ["T"] = "La bruja anciana",
  },
  [1064] = {
    ["D"] = "Los tauren tienen un vínculo con la tierra y me apena conocer la difícil situación de Espolón pero me temo que para salvar a la tierra primero debemos eliminar el mal que la puebla.$B$B¿No es una suerte, pues, que los Renegados sean nuestros aliados? Ellos saben mucho del mal y creo que podrán ayudarnos. De ese modo, reforzarán la confianza entre nuestros pueblos.$B$BHabla con la boticaria Zamah, una erudita y emisaria de los Renegados. La podrás encontrar en las Pozas de las Visiones, al pie del Alto de los Espíritus.",
    ["O"] = "Habla con la boticaria Zamah en las Pozas de las Visiones de Cima del Trueno.",
    ["T"] = "La ayuda de los Renegados",
  },
  [1065] = {
    ["D"] = "Para liberar a Espolón de Ventura y Cía., debemos tomar medidas extremas pero evitar el derramamiento de sangre.$B$BPuede que un colega mío tenga lo que necesitamos. Su nombre es Lydon y se encuentra en la ciudad de Molino Tarren en las Laderas de Trabalomas, en la lejana tierra de Lordaeron.$B$BPara llegar a Trabalomas viaja en zepelín hasta nuestra capital, Entrañas. Después ve en dirección norte a los Claros de Tirisfal, al sudeste hasta Argénteos y en dirección sur para llegar a Trabalomas.$B$BBusca a Lydon y entrégale esta nota en la que se detalla todo lo que necesitamos.",
    ["O"] = "Entrégale la nota de Zamah al boticario Lydon, que está en Molino Tarren.",
    ["T"] = "Viaja a Molino Tarren",
  },
  [1066] = {
    ["D"] = "Voy a crear una toxina que drene la violencia de Ventura y Cía.; así se volverán débiles y dóciles. Eso solucionará el problema de la Horda en Sierra Espolón.$B$BPero necesito sangre inocente, que es difícil de conseguir.$B$BPor suerte, la Hermandad, que es un grupo de maleantes humanos de esta zona, emplea a magos de la sombra, que recolectan sangre inocente para sus turbios propósitos.$B$BLos encontrarás en el Castillo de Durnholde, al sureste de aquí.",
    ["O"] = "Lleva 5 viales de sangre inocente al boticario Lydon de Molino Tarren.",
    ["T"] = "Sangre inocente",
  },
  [1067] = {
    ["D"] = "Aquí está la toxina. Tendrá un efecto favorable, quizás incluso contagioso, en Ventura y Cía. ¡Me emociona ver una demostración práctica de mi trabajo!$B$BPero antes de usarla, llévasela a la boticaria Zamah. Tiene que diseñar un proceso que intensifique el efecto de la toxina.",
    ["O"] = "Lleva la toxina de Lydon a la boticaria Zamah de Cima del Trueno.",
    ["T"] = "Regresa a Cima del Trueno",
  },
  [1068] = {
    ["D"] = "Existe una relación entre la magia espiritual del chamán y la magia natural del druida. Es por ello que el chamán puede escuchar la llamada de los espíritus del Espolón. Yo casi no puedo escuchar muchos de sus susurros, pero existe una visión, un mensaje, muy claro...$B$BTuve una visión... dos máquinas parecidas a dos hombres en el Risco Cortaviento, al noroeste de la Vega del Gran Bosque, derribando árboles a gran velocidad. Debemos detener a estas bestias metálicas.$B$BEn mi visión, las máquinas estaban marcadas con extraños símbolos:$B$BXT:4 y XT:9.",
    ["O"] = "Destruye las sierras XT:4 y XT:9, a continuación vuelve a ver a Seereth Petrajada, en la frontera entre Sierra Espolón y Los Baldíos.",
    ["T"] = "Las sierras XT:4 y XT:9",
  },
  [1069] = {
    ["D"] = "¡Las tortillas de huevos de araña están causando furor en Bahía del Botín! Solo hay un problema... no hay huevos en Bahía del Botín.$B$B¡Puedo oler la oportunidad!$B$BLa araña Musgondo vive en el Risco Cortaviento y en el Cañón Sishir, en Sierra Espolón hacia el noroeste. Tráeme sus huevos ¡y te pagaré un dineral!$B$BEn el Risco Cortaviento, los huevos estarán cerca de lo que quede de arboleda.$B$BEn el Cañón Sishir... bueno, si no tienes un estómago a prueba de bomba, quizás debas limitarte a recolectar los huevos en el Risco Cortaviento...",
    ["O"] = "Llévale 15 huevos de Musgondo a Mebok Mizzyrix en Trinquete.",
    ["T"] = "[DEPRICATED] Huevos de arañas Musgondo",
  },
  [1070] = {
    ["D"] = "Hola, $N.$B$BPareces tener ansias de conocer mundo. ¿Has estado en Sierra Espolón? Es una tierra apestada por Ventura y Cía. y otros enemigos de los elfos nocturnos.$B$BRecientemente, la centinela Correlobo envió a mi amiga Kaela Lanzasombra allí para investigar lo que está pasando en la tierra, pero todavía no sé nada de ella. Y me preocupa mucho, porque la Alianza envió a un mago gnomo con ella... y eso es sin duda una señal de problemas.$B$B¿Podrías encontrarla y venir a informarme?",
    ["O"] = "Encuentra a Kaela Lanzasombra contemplando la vista del valle a la entrada del Risco Cortaviento, en Sierra Espolón.",
    ["T"] = "Estar de guardia en Espolón",
  },
  [1071] = {
    ["D"] = "Verás, $N, soy miembro de la Asamblea Iluminada de Arcanología, Alquimia e Ingeniería, y mi objetivo... no, ¡mi DEBER!... es demostrar a esos elfos de la noche, que la unión de magia, combinaciones y mecanismos puede ayudarles a salvar sus bosques.$B$BPero se trata de una guerra dura, ¡y, por lo visto, esos goblins se reproducen como conejos! Ni siquiera he tenido tiempo de construir mis últimos inventos.$B$BNecesito algo de tiempo para planear y organizarme. Pero, para ello, necesito que me ayudes a mantener a raya a los goblins en el Risco Cortaviento, al este...",
    ["O"] = "Mata a 10 leñadores de Ventura y Cía. y a 10 deforestadores de Ventura y Cía. A continuación, vuelve a ver a Gaxim Silvóxido en Espolón.",
    ["T"] = "Una tregua para el gnomo",
  },
  [1072] = {
    ["D"] = "El dispositivo en el que estoy pensando, es la versión más avanzada hasta la fecha. Pero necesitaremos una poción especial para que funcione. Y creo que también podríamos intentar ir a lo grande, porque esta podría ser tu última misión.$B$BY, para eso, vamos a necesitar explosivos potentes: nitromirgliceronio.$B$BEl único que puede hacer NG-5 es un viejo amigo mío: Lomac Destrabaengranajes. Búscalo en Ciudad Manitas.$B$BConvéncelo para que nos haga NG-5. Me pondré a trabajar en mis dispositivos.",
    ["O"] = "Habla con Lomac Destrabaengranajes en Forjaz.",
    ["T"] = "Un viejo colega",
  },
  [1073] = {
    ["D"] = "¡Ja, ja, ja! ¿Necesitas nitromirgliceronio para evitar que los goblins destruyan un bosque?$B$BNo me importa si conoces a Gaxim o no... Te lo daré solo para verte a ti, o a los otros, explotar en un millón de trocitos.$B$BPero hay un problema: me he quedado sin NG-5.$B$BPero mira, si me traes las pociones correctas, te prepararé lo que necesitas ahora mismo.$B$BLo mínimo que podías hacer es demostrar tu habilidad como $Gboticario:boticaria; antes de que te dé una muestra de mi mejor creación.",
    ["O"] = "Lleva 4 pociones menores de maná y 2 elixires de entereza menor a Lomac Destrabaengranajes en Forjaz.",
    ["T"] = "Ineptitud + Productos químicos = Diversión",
  },
  [1074] = {
    ["D"] = "Aquí tienes, $Gforastero:forastera;... Pero ten muchísimo cuidado... es un material muy volátil.$B$BSi, realmente, Gaxim es tu amigo, dale recuerdos de mi parte. Si no es así, que te diviertas cuando explotes...",
    ["O"] = "Lleva el nitromirgliceronio de vuelta a Gaxim Silvóxido en Espolón.",
    ["T"] = "Ineptitud + Productos químicos = Diversión",
  },
  [1075] = {
    ["D"] = "Muy bien y, ahora, la parte de detonación remota de mi plan... Oh, eso no te lo he explicado todavía, ¿no?$B$BPues en resumen se trata de esto: con un poco de magia, puedo conseguir que los explosivos exploten cuando tú no estés cerca. Estoy creando un dispositivo que te permitirá, tan solo con pulsar un botón, hacer que el NG-5 explote.$B$BPero necesito un hechizo de un amigo de Ventormenta... Collin Mauren. Le encontrarás en el Barrio de los Magos. Es un humano, así que no deberías tener problemas para encontrarle.",
    ["O"] = "Encuentra a Collin Mauren en Ventormenta.",
    ["T"] = "Un pergamino de Mauren",
  },
  [1076] = {
    ["D"] = "Ah, creo que ya lo tengo. Un hechizo mensajero... Creo que ese es el efecto que está buscando.$B$BTendré que hacer un pergamino donde grabar el hechizo. Para ello, necesitaré restos de un demonio de polvo.$B$BMientras reúno el resto de los componentes, ¿qué te parece si vas a los Páramos de Poniente a buscar a una de esas malvadas criaturas? Los demonios de polvo son peligrosos, pero seguro que puedes encargarte de algo así si estás $Gdispuesto:dispuesta; a someterte a uno de los experimentos de Gaxim.$B$BEstaré aquí cuando vuelvas.",
    ["O"] = "Lleva restos de demonio de polvo a Collin Mauren en Ventormenta.",
    ["T"] = "Demonios en los Páramos de Poniente",
  },
  [1077] = {
    ["D"] = "Aquí estás, $N. Con esto bastará.$B$BPor favor, dale recuerdos a Gaxim la próxima vez que le veas. Me muero de ganas por saber qué tal salió todo cuando terminéis.$B$BAdiós.",
    ["O"] = "Lleva el Pergamino de mensajes de vuelta a Gaxim Silvóxido en Espolón.",
    ["T"] = "Entrega especial para Gaxim",
  },
  [1079] = {
    ["D"] = "Muy bien, $N. Presta mucha atención. Los dispositivos están terminados y listos para usar.$B$BLlévate estas páginas. Contienen más información sobre los detonadores y explosivos y dónde deberías colocarlos.$B$BRecuerda, el éxito de tu misión no depende de la destrucción que provoques. El éxito del plan A depende de que recuperes los planos de ingeniería de Ventura y Cía. que están dentro de la serrería al sur del Lago del Peñasco.",
    ["O"] = "Recupera los planos de ingeniería de Ventura y Cía. de la serrería en el Risco Cortaviento.",
    ["T"] = "Operaciones secretas: plan A",
  },
  [1080] = {
    ["D"] = "El plan Beta es como el plan Alfa, $N.$B$BCrearemos una distracción y robamos las valiosas cartas.$B$BLo más importante es conseguir sus cartas... No lo olvides.$B$BLas páginas que te he dado te indicarán dónde colocar los explosivos. Solo tienes que volver aquí con vida... y con las cartas...",
    ["O"] = "Recupera las cartas de Ventura y Cía. de la Mina Cortaviento, en el extremo este del Risco Cortaviento.",
    ["T"] = "Operaciones secretas: plan B",
  },
  [1081] = {
    ["D"] = "$N, seguro que la mismísima Tyrande Susurravientos querrá agradecerte tu participación en esta misión.$B$BQuizás no te lo parezca a primera vista, pero lo que has ayudado a empezar, podría reforzar nuestra ciudadela en Sierra Espolón.$B$BSi te consideras $Gnuestro aliado:nuestra aliada; y estás en contra de estas atrocidades, vete a ver a Tyrande en el Templo de la Luna, en Darnassus.",
    ["O"] = "Encuentra a Tyrande Susurravientos en Darnassus.",
    ["T"] = "El recibimiento de Tyrande",
  },
  [1082] = {
    ["D"] = "$N, nos has prestado un gran servicio.$B$BLas Centinelas de Vallefresno te recompensarán generosamente si les informas de que Gaxim y yo estamos vivos. Diles que vamos de camino a detener a Ventura y Cía. y curar la tierra.$B$BBusca a Tara Thenysil en Astranaar; estará encantada al verte volver con buenas noticias.",
    ["O"] = "Ve a ver a la centinela Thenysil en Vallefresno.",
    ["T"] = "Noticias para Centinela Thenysil",
  },
  [1083] = {
    ["D"] = "No tengo muy claro lo que Ventura y Cía. hizo en La Vega, pero es obvio que consiguieron dominar a las criaturas que vivían allí.$B$BIncendiaron el bosque y utilizaron sus inventos de destrucción para diezmar La Vega tanto como pudieron.$B$BPero, en su ignorancia, hicieron algo más: liberaron espíritus ardientes, elementales de fuego, piedra y furia.$B$BLa tierra no tendrá la oportunidad de ver la sonrisa de Elune mientras esas criaturas vaguen a su libre albedrío.$B$BTráeme sus ascuas ardientes para demostrarme que has conseguido reducir sus filas.",
    ["O"] = "Lleva 8 ascuas ardientes de cualquiera de los elementales de fuego de la Vega Carbonizada a Kaela Lanzasombra, en Espolón.",
    ["T"] = "Los espíritus iracundos",
  },
  [1085] = {
    ["D"] = "La situación aquí, en Espolón, es obviamente grave para que una elfa de la noche y un gnomo estén trabajando juntos.$B$BVentura y Cía. ha devastado estas tierras y yo he pasado la mayor parte de mi tiempo intentando poner fin a la destrucción.$B$BNo juzgues a Gaxim prematuramente. Comprende la necesidad que tenemos de trabajar juntos.$B$BSi quieres ayudarnos, empieza por hablar con Gaxim.$B$BSiento no poder enviarte de vuelta a ver a Tara con buenas noticias todavía. Quizás pronto...",
    ["O"] = "Habla con Gaxim en Sierra Espolón.",
    ["T"] = "Estar de guardia en Espolón",
  },
  [1086] = {
    ["D"] = "La toxina ya está lista. Solo queda entregarla. Y eso te toca a ti, $N.$B$BVentura y Cía. tiene que disponer de un sistema para enviar mensajes y paquetes ligeros entre su administración central y las operaciones de Sierra Espolón. Probablemente sea un aeropuerto, no lo bastante grande para un zepelín, pero sí para un transportador aéreo. Recorre las zonas de influencia de Ventura y Cía. en Sierra Espolón y encuentra el aeropuerto. Si lo localizas, planta la toxina.$B$BY sal de ahí inmediatamente.",
    ["O"] = "Lleva el nublador tóxico al aeropuerto de Ventura y Cía. e informa a la boticaria Zamah.",
    ["T"] = "El aeropuerto",
  },
  [1087] = {
    ["D"] = "No puedes ni imaginarte el odio que siento por esta tierra maldita. Los de Cenarius y los druidas kaldorei de la cima, utilizan sus patéticos poderes para reprimir el flujo de la magia, incluso muy lejos, al sur de sus patéticas posesiones... sus amados bosques.$B$BYo podría regresar a Jaedenar y regocijarme con el flujo de la magia, pero mi maestro no me permite volver hasta que no haya finalizado mi trabajo.$B$BNo me cabe la menor duda de que sientes tanto aprecio por los elfos de la noche y sus pequeños aliados como yo. Ayúdame y ambos quedaremos satisfechos.",
    ["O"] = "Mata a 4 Hijos de Cenarius, 4 Hijas de Cenarius y 4 botánicas Cenarion para Braelyn Manofuego, cerca del Refugio Roca del Sol.",
    ["T"] = "El legado de Cenarius",
  },
  [1089] = {
    ["D"] = "Los druidas construyeron El Cubil del Espolón, en la Cima del Espolón, y allí guardan poderosos objetos.$B$BDiseñaron un complejo sistema para mantener estos objetos alejados de manos equivocadas.$B$BPor fortuna, he logrado... extraer algunos detalles de uno al que he interrogado.$B$BEn una antorcha de la entrada a El Cubil encontrarás la llave del Guardián. Con esta podrás abrir los cofres de El Cubil. Los objetos que obtengas te permitirán abrir su tesoro, ubicado en una de las zonas centrales.",
    ["O"] = "Ve a El Cubil, en la Cima del Espolón. Utiliza la llave del Guardián y consigue los objetos ocultos del druida. Utiliza esos objetos para abrir el tesoro de El Cubil del Espolón.",
    ["T"] = "El Cubil",
  },
  [1090] = {
    ["D"] = "¿Sabes? Una vez fui la segunda persona más importante de este lugar. Pero todo cambió con la llegada de Gerenzo. Despidió a mi jefe, Ziz Fizziks, y me envió a trabajar a las minas. Sin embargo, Ziz me dijo que permaneciese aquí y reuniese algo de información. Su nuevo jefe nos pagará bien si podemos descubrir los planes de Gerenzo.$B$BDe todas formas, lo que sí sé es que Gerenzo ha ordenado excavar grandes cantidades de su mineral dorado verdoso. Puedo conseguirte una muestra para que se la lleves a Ziz, pero tendrás que vigilar por si aparecen patrullas mientras lo hago.",
    ["O"] = "Consigue el misterioso mineral de Piznik cuando termine de extraerlo.",
    ["T"] = "Órdenes de Gerenzo",
  },
  [1091] = {
    ["D"] = "$N, lleva estas cartas a Kaela. Seguro que querrá saberlo. O, al menos, las Centinelas querrán.$B$BAsegúrate de decirle que hemos tenido éxito y que no podríamos haberlo logrado sin ayuda de la magia.",
    ["O"] = "Lleva las cartas de Ventura y Cía. a Kaela Lanzasombra a Sierra Espolón.",
    ["T"] = "Noticias para Kaela",
  },
  [1092] = {
    ["D"] = "Supongo que también debería pensar en cómo salir de aquí. Las noticias de lo ocurrido aquí no tardarán en llegar a los de arriba y seguro que no van a estar muy contentos al verme de nuevo.$B$BSaldré a hurtadillas para encontrarme con Ziz. Con suerte, ¡podrá encontrarme otro trabajo!$B$BVoy a intentar arreglar un poco este desastre, pero deberías llevar la muestra del mineral a Ziz ahora mismo. Me dijo que iba a establecer su campamento en alguna parte del lado oeste del Risco Cortaviento. Buena suerte.",
    ["O"] = "Lleva la mena sin identificar a Ziz Fizziks, en Sierra Espolón.",
    ["T"] = "Órdenes de Gerenzo",
  },
  [1093] = {
    ["D"] = "Es terrible lo que Ventura y Cía. ha hecho aquí, ¿verdad? ¿En qué estaban pensando? ¡Una compañía goblin contratando a un gnomo!$B$BPor suerte para mí, Petardol puede distinguir a un genio en cuanto lo ve. ¡Me contrató justo después de que me despidieran!$B$BMe dice que han construido una enorme máquina cortaleña y Petardol quiere que robe los planos para él. El problema es que Gerenzo, maldito gnomo, me quiere muerto... Quizás tú podrías robar los planos por mí... Seguro que los lleva encima alguno de sus operadores.",
    ["O"] = "Consigue los planos de la Súper Segadora 6000 para Ziz Fizziks en Sierra Espolón.",
    ["T"] = "Súper Segadora 6000",
  },
  [1094] = {
    ["D"] = "Este sobre contiene toda la información que conseguí coger antes de que me echaran a patadas... Documentos internos de Ventura y Cía., algunos manuales, cosas técnicas... Oh y, por supuesto, los planos de la Súper Segadora 6000.$B$BPetardol vive en Trinquete, en Los Baldíos. No creo que tengas problemas para encontrarle, es pequeñito y verde.",
    ["O"] = "Lleva el sobre lacrado a Petardol a Trinquete, en Los Baldíos.",
    ["T"] = "Más instrucciones",
  },
  [1095] = {
    ["D"] = "El asunto de este gnomo contratado por Ventura y Cía. ha provocado algo de revuelo en Minahonda. Los grandes comerciantes no aprueban la asociación de Razdunk con el tal Gerenzo.$B$BRiddlevox me ha pedido que me ocupe del problema y seguro que la orden viene de los grandes comerciantes. Por supuesto, el Director no me lo ha dicho. Digamos que... tengo un presentimiento.$B$BSea como fuere, hay que apartar a Gerenzo y eso parece una prueba a la medida de Fizziks.",
    ["O"] = "Lleva las nuevas órdenes a Ziz Fizziks en Sierra Espolón.",
    ["T"] = "Más instrucciones",
  },
  [1096] = {
    ["D"] = "Una vez vi a Gerenzo, pero te diré algo, es difícil de mirar... Su carne parece podrida y tiene injertos de metal y engranajes por todo su cuerpo. Ha reemplazado algunos de sus miembros por otros mecánicos.$B$BGerenzo vigila toda la zona, desde el noreste de aquí, más arriba del Lago del Peñasco, cerca de la noria. Yo no podría llegar hasta ahí arriba, por no mencionar enfrentarme a Gerenzo... Pero tú pareces muy capaz de hacerlo.$B$BSi le matas por mí, ¡te recompensaré!",
    ["O"] = "Lleva el brazo mecánico de Gerenzo Silbillave a Ziz Fizziks, en Sierra Espolón.",
    ["T"] = "Gerenzo Silbillave",
  },
  [1097] = {
    ["D"] = "Hay un enano forjador de armas en Ventormenta, Grimand Elmore, que ha enviado un mensaje diciendo que necesita ayuda con una entrega. Creo que quiere enviar un paquete a su hogar, en el norte.$B$B¡Tienes unas piernas muy fuertes! Si te interesa ponerlas en marcha, vete a hablar con Grimand. Podrías sernos muy útil aquí, pero también debemos mantener nuestros tratos con los enanos.$B$BPuedes encontrar a Grimand Elmore en la tienda de armas del Distrito de los Enanos de Ventormenta, en la sección noreste de la ciudad.",
    ["O"] = "Habla con Grimand Elmore en Ventormenta.",
    ["T"] = "La tarea de Elmore",
  },
  [1098] = {
    ["D"] = "Estamos preparando un ataque a Colmillo Oscuro y enviamos a dos mortacechadores a investigar. De eso hace ya muchos días ¡y necesitamos su informe para preparar el ataque!$B$B$N, tienes que ir al Castillo de Colmillo Oscuro, hacia el sur, y localizar a los mortacechadores Adamant y Vincent. Ayúdalos si lo necesitan.$B$BEsta misión es tan peligrosa como crítica, $N. No falles.",
    ["O"] = "Encuentra a los mortacechadores Adamant y Vincent.",
    ["T"] = "Mortacechadores en Colmillo Oscuro",
  },
  [1099] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "¡Los goblins ganan!",
  },
  [1100] = {
    ["D"] = "Abres lentamente el desgastado diario de Henrig Soliceja...",
    ["O"] = "Lee el diario de Henrig Soliceja.",
    ["T"] = "Diario de Soliceja",
  },
  [1101] = {
    ["D"] = "¡Pobre Heralath! Y el enano Soliceja, ¡qué valiente era!$B$BNo debemos permitir que su valentía caiga en el olvido. Hay que detener a esa malvada... Charlga Filonavaja.$B$BSin duda, infiltrarse en el Horado es arriesgado pero no hay tiempo para enviar noticias a Darnassus. $N, reúne un equipo para matar a Charlga.$B$BTráeme el medallón de Filonavaja como prueba de su defunción.",
    ["O"] = "Llévale el medallón de Filonavaja a Díscolo Falfindel de Thalanaar.",
    ["T"] = "La bruja del Horado",
  },
  [1102] = {
    ["D"] = "Cairne es un líder noble que ha unido a nuestro pueblo aquí en Cima del Trueno.$B$BPero no puedo perdonar a aquellos que nos han echado de nuestras tierras ancestrales. Mi pueblo habitó en Los Baldíos del Sur durante décadas, una tierra que era sagrada para nosotros, hasta que numerosos enemigos nos expulsaron de ellas.$B$BEl destino le depara venganza a Charlga Filonavaja, que tiene a un ejército congregado en Horado Rajacieno.$B$BTráeme el corazón de Filonavaja y podré morir en paz, $N.",
    ["O"] = "Llévale el corazón de Filonavaja a Auld Picopiedra, que está en Cima del Trueno.",
    ["T"] = "Un destino vengador",
  },
  [1103] = {
    ["D"] = "¡Saluditos, $Ghermano:hermana;! ¿También has venío a purificar las aguas y a demostrarle a Islen que l\'has captao to\'?$B$BYo mesmamente m\'he cargao al espíritu del agua corrupto y estaba a punto de colocar los brazales y las gotas restantes d\'agua en el blandón.$B$B¿Qué pasha? Ah, ya. Tiev s\'entera cuando algo no anda bien. Vale, Tiev podrá ayudarte mogollón si tienes que volver a ver al espíritu del agua pura.$B$BTráeme una sapta d\'agua de Islen como prueba de qu\'estás $Glisto:lista; y esperaré pa\' cumplir mi parte.",
    ["O"] = "Llévale una sapta de agua a Tiev Mordune en el Bosque de Argénteos si no consigues hablar con la manifestación de agua menor.",
    ["T"] = "La llamada del agua",
  },
  [1104] = {
    ["D"] = "¡Tengo un montón de diseños para nuestro coche cohete! ¡Muchos! ¡Y tengo que probarlos todos!$B$BEstoy trabajando en un nuevo tipo de combustible. ¡Uno que arde a muy, muy, muy alta temperatura! Aún no he conseguido la fórmula perfecta pero creo que ya sé lo que necesito...$B$BEl veneno de los escórpidos que rondan El Desierto de Sal contiene gran cantidad de sal. ¡Es algo único y es justo lo que necesito!$B$BTráeme ese veneno y... créeme, ¡nuestro coche volará!",
    ["O"] = "Llévale 6 glándulas de veneno de escórpidos salados a Fizzle Pernolatón, que está en El Desierto de Sal.",
    ["T"] = "Veneno de El Desierto de Sal",
  },
  [1106] = {
    ["D"] = "Estoy trabajando en un nuevo motor que hará que el coche corra como el viento. ¡Podrá incluso volar, lo sé! Lo único que tengo que hacer es crear unos pistones que puedan aguantar una presión realmente alta. ¡Todos mis ensayos fueron un fracaso porque no consigo que los pistones sean lo suficientemente resistentes!$B$BAunque hay alguien que puede que sepa cómo conseguirlo.$B$BSu nombre es Martek el Exiliado. Es un gran herrero y sabe más sobre metales que ningún otro. Llévale esta carta, ¡seguro que nos puede ayudar!$B$BAunque está muy, muy lejos, en las Tierras Inhóspitas de Azeroth, en un campamento con un goblin.$B$B¡Ve y encuéntralo!",
    ["O"] = "Llévale la carta de Fizzle Pernolatón a Martek el Exiliado, que está en las Tierras Inhóspitas.",
    ["T"] = "Martek el Exiliado",
  },
  [1107] = {
    ["D"] = "¡Nuestro coche cohete es el más rápido de El Desierto de Sal! Es tan rápido que... ¡tengo miedo de que la fricción lo haga reventar en pedazos!$B$BPara evitarlo, tengo que crear un compuesto para recubrir con él las piezas móviles del coche. Tiene que ser duro pero que no se rompa, y tiene que ser resistente a la corrosión de la sal o después de un tiempo en El Desierto de Sal acabará desgastándose.$B$B¿Has oído hablar de El Arrecife Mortal, $N? Está en la costa, más allá de Vega de Tuercespina. Allí habitan múrlocs de escamas saladas, y sus aletas dorsales son el ingrediente imprescindible para mi compuesto.",
    ["O"] = "Llévale 10 aletas dorsales taraceadas a Wizzle Pernolatón, que está en El Desierto de Sal.",
    ["T"] = "Aletas dorsales taraceadas",
  },
  [1108] = {
    ["D"] = "No estoy aquí solo para estar lejos de la gente. Hay metal en abundancia en las Tierras Inhóspitas, $N, siempre que tengas las agallas para ir a por él.$B$BEl metal se llama indurio y si es cierto lo que dicen, posee una alta resistencia al calor y a la presión. Puede que sirva para el coche de Fizzle.$B$BPero tenemos que asegurarnos. Consígueme unas muestras de indurio y así podré comprobar sus propiedades.$B$BLa verdadera mena de indurio se encuentra en Uldaman, pero los troggs Grutacanto de las Tierras Inhóspitas suelen llevar encima algunas láminas de indurio. Tráeme esas láminas.",
    ["O"] = "Llévale 10 láminas de indurio a Martek el Exiliado, que está en las Tierras Inhóspitas.",
    ["T"] = "Indurio",
  },
  [1109] = {
    ["D"] = "Como puedes comprobar, $N, tenemos unos interesantes experimentos entre manos aquí en la Sociedad Real de Boticarios.$B$BDado que yo soy el maestro aquí, mi trabajo consiste en supervisar nuestros proyectos de alquimia más ambiciosos. ¡Hay tanto trabajo y tan poco tiempo!$B$BParece que tienes muchos kilómetros a tus espaldas, $C, así que quizás puedas ayudarnos.$B$BNecesito una sustancia no muy común y un tanto nauseabunda que proviene de una extraña especie de murciélagos que solo se encuentran en ese agujero apestoso conocido como Horado Rajacieno. Tráeme el guano de los murciélagos del Horado para que podamos ponernos manos a la obra.",
    ["O"] = "Llévale una pila de guano del Horado al maestro boticario Faranell en Entrañas.",
    ["T"] = "Guano del Horado",
  },
  [1110] = {
    ["D"] = "Je, je, con tantas carreras que se celebran aquí no es de extrañar que haya tantos accidentes. Si echas un vistazo por El Desierto de Sal te darás cuenta de que está plagado de restos de accidentes. ¡Hay piezas de coches cohete por todas partes!$B$BY tienen mucho valor para los gnomos y los goblins, que siempre están en busca de nuevos artilugios que instalar en sus coches.$B$BAsí que sal ahí fuera y consígueme algunas piezas. Tráeme un buen montón y te pagaré como mereces.",
    ["O"] = "Llévale 30 piezas de coche cohete a Kravel Karbarbán, que está en El Desierto de Sal.",
    ["T"] = "Piezas de coche cohete",
  },
  [1111] = {
    ["D"] = "Mi trabajo consiste en proporcionar a los demás lo que necesitan, y en este caso los gnomos me han hecho un pedido de piezas. Las piezas llegaron a Trinquete hace una semana pero yo no he podido ir a recogerlas.$B$BAsí pues, ¿te apetece ir a dar un paseo? Ve a Trinquete y entrégale este pedido de piezas al maestro de muelles Vertipeluca, que seguramente esté en el puerto.$B$BHaz esto por mí y te encargaré más trabajos.$B$BTrabajos, $Gamigo mío:amiga mía;, que te reportarán grandes beneficios...",
    ["O"] = "Llévale el pedido de piezas de Kravel al maestro de muelles Vertipeluca en Trinquete, en Los Baldíos.",
    ["T"] = "Maestro de muelles Vertipeluca",
  },
  [1112] = {
    ["D"] = "Aquí está el paquete para Kravel, $N. Cuando se lo entregues, dile que si quiere más pedidos especiales ¡tendrá que correr con los gastos!",
    ["O"] = "Llévale las piezas de Kravel a Kravel Karbarbán, que está en El Desierto de Sal en Las Mil Agujas.",
    ["T"] = "Las piezas de Kravel",
  },
  [1113] = {
    ["D"] = "Por qué necesito un tipo de guano tan poco común, te preguntarás. Bueno, para este experimento quiero ver el efecto que dos sustancias químicas diametralmente opuestas tienen entre sí, sobre todo cuando se combinan con un agente incluso más tóxico para obtener... los resultados más horripilantes.$B$BEn las cumbres más nororientales de los Claros de Tirisfal se encuentra el Monasterio Escarlata, lugar en el que se reúnen los fervientes discípulos de la Cruzada Escarlata, tanto dentro como fuera del edificio. Aunque los mates, dará igual, ninguno tiene el corazón que necesito.",
    ["O"] = "El maestro boticario Faranell de Entrañas quiere 20 corazones de fanatismo.",
    ["T"] = "Corazones de fanatismo",
  },
  [1114] = {
    ["D"] = "Ahora que tengo lo que necesito, entrégale estas piezas a Fizzle Pernolatón. Lleva mucho tiempo esperando por ellas. Seguro que no echará en falta la pieza con la que me he quedado...$B$B¡Y yo que tú no se lo mencionaría! Era pequeña e insignificante.$B$B¡Confía en mí!",
    ["O"] = "Llévale las piezas de coche delicadas a Fizzle Pernolatón, que está en El Desierto de Sal.",
    ["T"] = "Un envío para los gnomos",
  },
  [1115] = {
    ["D"] = "Ahora que ya hemos prestado servicio a los gnomos, ha llegado el momento de hacerles un favor a los goblins.$B$BHe oído que el piloto de carreras goblin, Nazz Vaporio, no siempre ha sido conocido por ese nombre. Me pregunto por qué se lo habrá cambiado, si será un secreto y si realmente valora ese secreto.$B$BSi alguien sabe la verdad de todo esto, ese es Krazek de Bahía del Botín, en el extremo meridional de Tuercespina. Habla con Krazek y pregúntale acerca del piloto goblin.$B$BSi consigues información relevante, puede que le saquemos beneficios.",
    ["O"] = "Habla con Krazek en Bahía del Botín, en la Vega de Tuercespina.",
    ["T"] = "Rumores",
  },
  [1116] = {
    ["D"] = "Si te diera información sobre Nazz te estaría haciendo un favor, $N, un gran favor. Pero has venido desde El Desierto de Sal para conseguirla... ¿estás $Gdispuesto:dispuesta; a viajar un poco más?$B$BMientras contacto con mis fuentes, ¿por qué no vas a coger algo para mí, eh?$B$BVeamos, qué podría ser... ¿Qué te parece polvo onírico de las crías de dragón que hay en el Pantano de las Penas? ¡Sí, me encantaría tener pizcas de polvo onírico para mezclarlas con mi tabaco!$B$B$N, consígueme algunas y te daré la información que andas buscando.",
    ["O"] = "Llévale 10 pizcas de polvo onírico a Krazek a Bahía del Botín. El polvo onírico se obtiene de las crías de dragón que habitan en el Pantano de las Penas.",
    ["T"] = "Polvo onírico en el pantano",
  },
  [1117] = {
    ["D"] = "¡Tuve que hacer algunos favores a cambio, regalar una botella muy especial de vino élfico y atar cabos! Pero finalmente conseguí la información que querías.$B$BAquí tienes, escribí una carta en la que explico lo más destacado de nuestro amigo, Nazz Vaporio. Haz con ella lo que creas oportuno pues es demasiado peligrosa para que yo la guarde.$B$B¡Pero no le digas a nadie de dónde la has sacado!",
    ["O"] = "Llévale los rumores goblin a Kravel Karbarbán, que está en El Desierto de Sal en Las Mil Agujas.",
    ["T"] = "Rumores para Kravel",
  },
  [1118] = {
    ["D"] = "$N, sé que eres $Gun:una; $C en quien puedo confiar, tanto para realizar un trabajo como para mantener la boca cerrada mientras lo haces.$B$BHe ideado un nuevo plan. Es arriesgado pero si sale bien... ¡podríamos ganar un dineral! Y creo que tú eres la persona adecuada para conocer todos los detalles.$B$BPara empezar, debes volver a Bahía del Botín. Lo siento, $N. Sé que para el último encargo también tuviste que ir allí pero, en esta ocasión, tendrás que hablar con Loquillo Pinchabujas.$B$BEntrégale esta carta y si necesita ayuda haz todo lo posible por echarle una mano. El esfuerzo merecerá la pena.",
    ["O"] = "Llévale el esquema de Kravel a Loquillo Pinchabujas a Bahía del Botín, en Vega de Tuercespina.",
    ["T"] = "De vuelta a Bahía del Botín",
  },
  [1119] = {
    ["D"] = "Creo que ya sé cómo preparar con la mezcla de Zanzil y un poco de cerveza un brebaje bárbaro: la cerveza de tontos. Creo que es justo lo que Kravel necesita pero quiero que la pruebes y me digas qué tal son los resultados.$B$BY $N, no estés mucho tiempo sin utilizarla pues la mezcla de Zanzil tiene un efecto muy extraño sobre la levadura y no tarda mucho en estropearse.",
    ["O"] = "Llévale la cerveza de tontos a Kravel, que está en El Desierto de Sal en Las Mil Agujas.",
    ["T"] = "La mezcla de Zanzil y la cerveza de tontos",
  },
  [1120] = {
    ["D"] = "Tenemos que probar la cerveza de tontos con uno de los equipos técnicos. Si quieres probarla con los gnomos, coge la cerveza y dásela al encargado de foso gnomo.$B$BY no tardes mucho pues la cerveza podría estropearse.",
    ["O"] = "Llévale la cerveza de tontos al encargado de foso gnomo al Circuito del Espejismo, en Las Mil Agujas.",
    ["T"] = "Emborrachar a los gnomos",
  },
  [1121] = {
    ["D"] = "Tenemos que probar la cerveza de tontos con uno de los equipos técnicos. Si quieres probarla con los goblins, coge la cerveza y dásela al encargado de foso goblin.$B$BY no tardes mucho pues la cerveza podría estropearse.",
    ["O"] = "Llévale la cerveza de tontos al encargado de foso goblin al Circuito del Espejismo, en Las Mil Agujas.",
    ["T"] = "Emborrachar a los goblins",
  },
  [1122] = {
    ["D"] = "¡Esa cerveza de tontos funciona a las mil maravillas! He apostado en la próxima carrera así que esperemos que los técnicos la hayan hecho buena con los coches.$B$BHay una cosa más que debes hacer: llévale esta nota a Loquillo Pinchabujas a Bahía del Botín. Le gustará saber que la prueba ha sido todo un éxito.$B$BCon un poco de suerte, podremos además conseguir más cerveza de tontos para otras carreras.",
    ["O"] = "Llévale el informe de cerveza de tontos a Loquillo Pinchabujas, que está en Bahía del Botín en la Vega de Tuercespina.",
    ["T"] = "Volver a informar a Pinchabujas",
  },
  [1123] = {
    ["D"] = "El mayordomo del guardián Remulos, Rabine Saturna, está en Claro de la Luna buscando ayuda para explorar los vastos páramos al oeste de Tanaris y Un\'Goro. Se encuentra en Amparo de la Noche, el poblado principal de Claro de la Luna.$B$BQuiero que seas consciente de lo importante que es que estés allí pues hay ciertas tensiones entre los elfos de la noche de Darnassus y el Círculo Cenarion de Claro de la Luna. Obvia los límites raciales y aplica la sabiduría heredada de tus ancestros a la hora de tratar con el Círculo, $N. Buena suerte.",
    ["O"] = "Ve a Claro de la Luna, al poblado Amparo de la Noche y habla con Rabine Saturna. Claro de la Luna se encuentra entre Frondavil y Cuna del Invierno; para llegar, tienes que seguir el camino que sale del Bastión Fauces de Madera.",
    ["T"] = "Rabine Saturna",
  },
  [1124] = {
    ["D"] = "Al oeste del Cráter de Un\'Goro vía la cresta noroccidental, se encuentra una tierra inhóspita: Silithus. Pocos saben que allí se libró una guerra entre los elfos de la noche y un poder maligno y extraño. El mal fue sellado, pero sospechamos que hay fuerzas que intentan despertar a este horror inimaginable.$B$BBusca a Layo Golpe Estelar, uno de nuestros agentes en Silithus; lo encontrarás en Sosiego del Valor, donde reposan los caídos en los eriales. Dale esta carta y él te entregará su confianza.",
    ["O"] = "Enséñale la carta de Rabine a Layo Golpe Estelar cerca del cementerio de Sosiego del Valor en Silithus.",
    ["T"] = "Páramo",
  },
  [1125] = {
    ["D"] = "Al suroeste de aquí hay un lugar trágico, las ruinas de la Aldea del Viento del Sur. Ahí es donde deberíamos empezar a trabajar, $N.$B$BPor lo que he descubierto, antiguamente, esa aldea era una base de operaciones de los elfos de la noche en Silithus. Mucho tiempo atrás fue tomada por las colmenas de silítidos que infestan los restos. Solo puedo imaginarme el horror de la destrucción de la aldea...$B$BLos espíritus torturados de los druidas y centinelas caídos, vagan por las ruinas sin rumbo fijo. Explora el lugar y libera las almas de esos pobres seres.",
    ["O"] = "Libera los espíritus de 8 druidas torturados y 8 centinelas torturados en la Aldea del Viento del Sur. A continuación, reúnete con Layo Golpe Estelar en el cementerio de Sosiego del Valor en Silithus.",
    ["T"] = "Los espíritus de Viento del Sur",
  },
  [1126] = {
    ["D"] = "Hay que buscar las colmenas en la Aldea del Viento del Sur y creo que sé la forma de atacarlas. Según mi experiencia, las colmenas de silítidos son muy sensibles a su entorno. Esto explica que ataquen al oír los lamentos de los espíritus.$B$BLa presencia más fuerte de silítidos está en la torre, la que tiene una masa de avispas volando alrededor. Escala la torre y consigue que la colmena se agite. Vuelve a informarme sobre tus descubrimientos, ¡pero ten cuidado! Si descubres algo que pueda ayudar, tráemelo.",
    ["O"] = "Sube a la torre de la Aldea del Viento del Sur y encuentra la forma de agitar la colmena de silítidos para que entre en actividad. Recoge cualquier cosa extraña que encuentres y llévasela a Layo Golpe Estelar en el cementerio de Sosiego del Valor en Silithus.",
    ["T"] = "Una colmena en la torre",
  },
  [1130] = {
    ["D"] = "$N, Melor Pezuña Pétrea ha oído hablar de tus hazañas y ha trazado un camino para ti.$B$BSi deseas seguirlo, ve a Cima del Trueno y habla con Melor. Lo encontrarás en el Alto de los Cazadores.",
    ["O"] = "Habla con Melor Pezuña Pétrea en Cima del Trueno.",
    ["T"] = "Melor manda saludos",
  },
  [1131] = {
    ["D"] = "A veces se caza para conseguir alimento, otras por honor e incluso se puede cazar para aprender las lecciones que enseña la Madre Tierra.$B$BPero la caza que yo te encargo no pertenece a ninguno de estos tipos. Se trata de matar a una criatura que no cuenta con la bendición de la Madre Tierra.$B$BLa hiena Metaleador vaga por Las Mil Agujas, al sur de Los Baldíos. Es un tirano que intenta atraer a otras hienas y que infunde miedo a otras criaturas más pacíficas, provocando un derramamiento de sangre.$B$BEncuentra a Metaleador, $N. Encuéntralo y derrótalo.",
    ["O"] = "Llévale la costilla de Metaleador a Melor Pezuña Pétrea, que está en Cima del Trueno.",
    ["T"] = "Metaleador",
  },
  [1132] = {
    ["D"] = "¡Ay, estar de nuevo al lado del mar! Sentir la caricia del viento y dejar que las olas me mezan como hacía mi querida madre hace tiempo...$B$B¡Ay! Ojalá estuviera en tu lugar, $C, pues veo el mar en tu futuro.$B$BMi trabajo es hablarles a las almas ambiciosas de la tierra de Kalimdor, ¡la tierra de las oportunidades! Si deseas probar suerte en el mar, coge un barco y dirígete al maravilloso puerto de Auberdine. Allí habla con mi socia, la elfa Fiora Orejona.$B$B¡Ella te ayudará a comenzar tu aventura por Kalimdor!",
    ["O"] = "Habla con Fiora Orejona en los muelles de Auberdine, en Costa Oscura.",
    ["T"] = "Fiora Orejona",
  },
  [1133] = {
    ["D"] = "$N, ve a Astranaar. Seguro que un miembro de la Alianza entusiasta como tú podrá hacer mucho bien allí. Habla con Shindrell Fuegoveloz y ofrécele tus servicios.$B$BPero no le digas quién te envía pues Shindrell no me conoce...",
    ["O"] = "Habla con Shindrell Fuegoveloz en Astranaar.",
    ["T"] = "Viaje a Astranaar",
  },
  [1134] = {
    ["D"] = "No tenemos la guerra declarada a la Horda, pero nuestra paz es frágil. A menudo encontramos a sus agentes actuando en nuestra contra, pero lo hacen encubiertamente.$B$BNos enteramos de uno de esos avances mediante unas fuentes ocultas en Theramore, decían que los orcos recogen veneno de las alaorgullo, cerca del Lago Mirkfallon en Sierra Espolón, hacia el sur. Los asesinos orcos utilizan el veneno en sus asaltos secretos contra la Alianza.$B$BSi esto es cierto, debemos detenerlos, $N. Tenemos que impedir que accedan a su fuente de veneno.",
    ["O"] = "Lleva 12 sacos de veneno de alaorgullo a Shindrell Fuegoveloz en Astranaar.",
    ["T"] = "Alaorgullo del Espolón",
  },
  [1136] = {
    ["D"] = "$N, ha llegado la hora de que viajes a lejanas tierras. Estás $Gpreparado:preparada;.$B$BAhora vas a cazar a Faucehielo, un yeti de las Montañas de Alterac. Es escurridizo, su inmaculado pelaje se confunde con la nieve de la montaña en la que mora. Rara vez se deja ver.$B$BPero te enseñaré a engatusar a una criatura como él. Aquí tienes, toma este pergamino. En él explico cómo atraer a Faucehielo.$B$BAhora vete, $N. Tu viaje es largo y te aguardan innumerables desafíos. Solo cuando vuelvas con la crin de Faucehielo habrás completado tu tarea.",
    ["O"] = "Lleva la crin de Faucehielo a Melor Pezuña Pétrea en Cima del Trueno.",
    ["T"] = "Faucehielo",
  },
  [1137] = {
    ["D"] = "¡El indurio tiene una extraordinaria resistencia al calor! Seguro que será perfecto para los pistones de Fizzle. Pero necesitará indurio de verdad, no tan solo unas láminas.$B$BPara poder acceder a él, habrá que extraerlo de las mismísimas profundidades de Uldaman, en el extremo norte de las Tierras Inhóspitas. Esa es la auténtica fuente del indurio.$B$BMándale recuerdos a Fizzle Pernolatón de mi parte y dile lo que te he contado.$B$BBuen viaje, $N. No me sorprendería que volviéramos a vernos pues cuando Fizzle compruebe las propiedades del indurio, es probable que te envíe a Uldaman a por él.",
    ["O"] = "Habla con Fizzle Pernolatón en El Desierto de Sal, en Las Mil Agujas.",
    ["T"] = "Noticias para Fizzle",
  },
  [1138] = {
    ["D"] = "Adoro los cangrejos. ¡Los cangrejos son el fruto del mar! Puedes asarlos, hervirlos, guisarlos, hacerlos a la plancha... Se hacen en sartén, en olla, en hogueras... Y también hay ensalada de cangrejo, sopa de cangrejo, guiso de cangrejo, cangrejo a la pimienta, cangrejo al limón y cangrejo-sorpresa de Forjaz. Creo que no me dejo nada.$B$BPero ahora estoy pescando. No voy a conseguir ningún cangrejo. Se pueden conseguir de reptadores de arrecife y de reptadores de marea escamados. Y se pueden hacer mil cosas con ellos. Puedes asarlos, hervirlos, guisarlos, hacerlos a la plancha...",
    ["O"] = "Reúne 6 trozos de cangrejo de gran calidad para Gubber Blump de Auberdine.",
    ["T"] = "Rico marisco",
  },
  [1140] = {
    ["D"] = "La letra de los orcos es un poco mejor que los garabatos de un niño. Muchos de los de mi orden se burlaron cuando decidí aprender su lengua pero me ha resultado de provecho tener un conocimiento más profundo de mis enemigos.$B$BPor los garabatos de Ilkrud, parece que Athrikus aún posee dos gemas de almas que se guardan en la Senda de la Noche y en Satyrnaar, áreas bajo el dominio de los sátiros al noreste de Vallefresno.$B$BVe con cuidado, $N, los templos de los sátiros bañados en sangre pueden ser perturbadores hasta para $Gun:una; $C $Gcurtido:curtida; como tú.",
    ["O"] = "Libera las almas de los Altonato atrapadas en la Senda de la Noche y en Satyrnaar y vuelve junto a Delgren el Purificador en la Atalaya de Maestra.",
    ["T"] = "La Torre de Althalaxx",
  },
  [1141] = {
    ["D"] = "Me encanta pescar. Los Blump somos una famosa familia de pescadores. Yo soy Gubber Blump. Yo también pesco.$B$BEn esta zona hay un pez que me gusta pescar: el mero de Costa Oscura. Yo solía salir en un barco a pescar meros, pero desde que aparecieron los múrlocs, ya no sale ningún barco. Además, seguro que los múrlocs se han comido todos los meros.$B$BOye, ¿quieres ayudarme a pescar un mero de Costa Oscura? Si lo haces, ¡te daré una auténtica caña de pescar de la familia Blump! Es una caña estupenda para pescar.",
    ["O"] = "Pesca 6 meros de Costa Oscura para Gubber Blump de Auberdine.",
    ["T"] = "La familia y la caña de pescar",
  },
  [1142] = {
    ["D"] = "No voy a durar mucho... Estoy débil.$B$BEl valiente enano Cimahuraña ha ido a advertir a Falfindel...$B$BAntes de morir, quisiera...$B$BSe han llevado el colgante de vínculo de mi esposa, Treshala...$B$BEncuéntralo, por favor... Llévaselo a Treshala a Darnassus...$B$BY dile que he... muerto.",
    ["O"] = "Encuentra el colgante de Treshala Arroyobarbecho y llévaselo a Darnassus.",
    ["T"] = "Decaída mortal",
  },
  [1144] = {
    ["D"] = "¡Por fin llega ayuda!$B$BNo sabes cuánto hace que me tienen aquí prisionero. Ya ni recuerdo la última vez que estuve fuera.$B$BVerás, es que soy empresario, siempre ando olfateando negocios. Oí decir que Mebok Mizzyrix pagaba bien el tubérculo hojazul para su porqueriza, y me pareció un negocio redondo y fácil. Como puedes ver, ¡calculé mal!$B$BPero si me ayudas a salir de aquí, te recompensaré.",
    ["O"] = "Escolta a Willix el Importador hasta la salida de Horado Rajacieno.",
    ["T"] = "Willix el Importador",
  },
  [1145] = {
    ["D"] = "Muchos viajeros que han pasado por El Cruce me han ayudado a recoger huevos de la Tierra de Gigantes y muchos de ellos podrán contarte historias de lo peligrosas que son esas bestias. Yo ya he oído las suficientes como para poder afirmar que esos insectos representan una amenaza mayor de lo que mis maestros pensaban.$B$BNecesito enviar a un mensajero a Orgrimmar, a ver a Belgrom Destrozarroca. No se trata de un asunto cualquiera por eso necesito que vaya alguien que se tome esta tarea en serio.$B$BBelgrom suele andar por las inmediaciones de la Casa de la Hermandad de Guerreros.",
    ["O"] = "Entrégale la nota lacrada de Korran a Belgrom Destrozarroca en Orgrimmar.",
    ["T"] = "El enjambre crece",
  },
  [1146] = {
    ["D"] = "Por lo que veo, Korran ha llegado a la misma conclusión que yo acerca de esos insectos... parece que representan una amenaza mayor de lo que yo pensaba.$B$BHace unos días, un cazador regresó agonizante de Las Mil Agujas. Formaba parte de un destacamento de asalto enviado a El Desierto de Sal para expulsar de allí a un equipo de excavación formado por enanos. Durante el ataque, empezaron a salir insectos de una cueva que formaba parte de la excavación y casi acaban con todo el destacamento.$B$BEstaban liderados por Moktar Krin y se han refugiado en la frontera de El Desierto de Sal. Llévale esta nota.",
    ["O"] = "Entrégale la nota lacrada de Belgrom a Moktar Krin en Las Mil Agujas.",
    ["T"] = "El enjambre crece",
  },
  [1147] = {
    ["D"] = "¡Vaya! Parece que Belgrom piensa que esos monstruos representan una amenaza y quiere que sean devueltos al lugar al que pertenecen.$B$BSupongo que haber matado a los enanos enviados por la Liga de Expedicionarios no ha sido suficiente y ahora tengo que enviar a un destacamento de guerra exhausto a otra batalla. No lo conseguiremos... no en estas condiciones. La excavación se encuentra al sur de aquí, y ya no debería quedar ningún enano por allí. ¿Te sientes con ánimos de blandir una espada o un bastón y echarnos una mano?",
    ["O"] = "Mata a 5 buscadores silítidos, 5 zánganos de colmena silítidos y 5 invasores silítidos, y después ve a ver de nuevo a Moktar Krin a Las Mil Agujas.",
    ["T"] = "El enjambre crece",
  },
  [1148] = {
    ["D"] = "Inspeccionas el caparazón detenidamente. A medida que lo giras, te das cuenta de que la coraza de quitina está entrelazada de tal forma que le confiere una resistencia increíble. No se parece a nada que hayas visto antes. Observas el resto del cuerpo y te das cuenta de que estas criaturas son muy poderosas y están muy evolucionadas.$B$BAl mirar de nuevo el caparazón, crees que a Korran le gustaría recibir más muestras de estas criaturas. Le resultarían útiles para descubrir qué son y de dónde proceden.",
    ["O"] = "Llévale 1 corazón de silítido, 5 garfas de silítido y 3 caparazones de silítido intactos a Korran, que está en El Cruce.",
    ["T"] = "Pedazos del enjambre",
  },
  [1149] = {
    ["D"] = "¿Me has estado buscando o quizás ha sido el destino el que te ha traído a mí? No tiene importancia... Ahora estás aquí y tu viaje encontrará un nuevo punto de partida.$B$BSon muchas las facetas que conforman un ser, tanto su espíritu como su cuerpo y su mente. Algunas de ellas son mensurables por las tradiciones de una cultura. Otras, sin embargo, solo podemos esperar que encuentren un lugar dentro de nosotros. Si no te opones, pondré a prueba algunas de tus facetas.$B$BSi superas las pruebas, te harás más fuerte. De lo contrario, descubrirás la inmensidad de El Vacío Abisal.$B$BLa primera es una prueba de fe.",
    ["O"] = "Si tienes fe, salta desde las tablas que dominan Las Mil Agujas.",
    ["T"] = "Prueba de fe",
  },
  [1150] = {
    ["D"] = "Me complace comprobar que tu valor permanece inquebrantable después de lo que acabas de hacer. Dado que tu fe está intacta, ha llegado el momento de poner a prueba tu cuerpo.$B$BEn primer lugar, harás una prueba de resistencia. Ve a Guarida Malapluma al oeste del Poblado Viento Libre. Consigue llegar hasta las profundidades de su guarida y destruye sus alimentos. Con eso provocarás su ira. Si eres capaz de aguantar con vida el tiempo suficiente, la propia Grenka Aullasangre irá a por ti. Acaba con ella y tráeme su garra para superar tu prueba.",
    ["O"] = "Llévale la garra de Grenka a Dorn Acechallanos, que está en Las Mil Agujas.",
    ["T"] = "Prueba de resistencia",
  },
  [1151] = {
    ["D"] = "En los tiempos de los dioses antiguos, los elementales ocupaban la mayor parte de este mundo. Ellos veneraban a los dioses antiguos y estos, haciendo uso de su poder, relegaron a los elementales a este mundo. Cuando los dioses antiguos desaparecieron, los elementales se desvanecieron de nuestro mundo y ahora solamente regresan cuando los poderosos magos requieren su presencia.$B$BAun así, hay algunas excepciones. Algunos elementales eran más poderosos y consiguieron permanecer aquí por su cuenta. Uno de ellos es Rok\'Alim.$B$BPara poner a prueba tu fuerza, encuentra a Rok\'Alim en Las Mil Agujas y acaba con él.",
    ["O"] = "Llévale fragmentos de Rok\'Alim a Dorn Acechallanos, que está en Las Mil Agujas.",
    ["T"] = "Prueba de fuerza",
  },
  [1152] = {
    ["D"] = "Tu tiempo conmigo está llegando a su fin. Tengo una amplia cultura mas la prueba de conocimiento no seré yo quien te la haga. Hay lecciones que aprender y lugares que visitar que corresponden a otros.$B$BSi estás $Gpreparado:preparada;, busca a Braug Espíritu Tenue, un chamán muy sabio. Él continuará poniéndote a prueba, así que sigue sus indicaciones, $N.$B$BCuando lo desees, busca el túnel que comunica Sierra Espolón con Vallefresno pues Braug anda por esa zona. Y ten cuidado, $N, a los elfos de la noche les encantaría impedir que acabaras tus pruebas.",
    ["O"] = "Encuentra a Braug Espíritu Tenue cerca de la entrada de El Paso del Espolón en Sierra Espolón.",
    ["T"] = "Prueba de conocimiento",
  },
  [1153] = {
    ["D"] = "$C, me has ayudado con eficacia en el pasado. El Jefe de Guerra está satisfecho con mis logros y quiere que continúe con renovada determinación. Según me dicta mi honor, informaré al Jefe de Guerra de tu valía para que te otorgue el reconocimiento que te mereces. Pero antes he de pedirte que me ayudes otra vez.$B$BRecientemente han llegado unos viajeros del sur, de un lugar llamado Las Mil Agujas. Estos afirman que allí mataron a un kóbold que tenía una muestra de un extraño mineral, pero no están dispuestos a vendérmelo. Quiero una muestra de ese mineral para analizarlo.",
    ["O"] = "Arrebátale una muestra bruta de mena a un cavador o a un perito Gravamorro y llévasela a Jironack Forjacero al Campamento Taurajo de Los Baldíos.",
    ["T"] = "Una nueva muestra de mineral",
  },
  [1154] = {
    ["D"] = "Has superado con éxito las pruebas de Dorn y sé que quieres hacer lo mismo con la siguiente. Muy bien, pero ten en cuenta que hasta el momento las pruebas han sido demasiado sencillas. Es fácil poner a prueba el cuerpo... pero la mente es un tema aparte.$B$BTe lo explicaré bien para que lo entiendas: encuentra el libro que yo te diga, estúdiatelo bien y responde la pregunta que te haga acerca de su contenido.$B$BQué libro, te estarás preguntando... El Legado de los Aspectos. Lo encontrarás en la parte baja de Dor\'danil, en el sur de Vallefresno, bajo la protección de los druidas muertos y sus asesinos.",
    ["O"] = "Encuentra el Legado de los Aspectos y devuélveselo a Braug Espíritu Tenue que se encuentra cerca de la entrada de El Paso del Espolón en Sierra Espolón.",
    ["T"] = "Prueba de conocimiento",
  },
  [1155] = "_",
  [1156] = "_",
  [1157] = "_",
  [1158] = "_",
  [1159] = {
    ["D"] = "Bien hecho, $N. Has superado la primera parte de la prueba de conocimiento pero aún hay otra pregunta que debes responder. Sin embargo, no seré yo quien te la haga.$B$BBusca a Parqual Fintallas en Entrañas de Lordaeron. Él era un gran historiador al servicio de los humanos antes de que la Plaga lo sorprendiera. Ahora sigue persiguiendo el conocimiento, pero de un modo distinto.$B$BAntes de formularte ninguna pregunta, te asignará otro encargo. Lo has hecho tan bien hasta el momento que no tengo ninguna duda de que superarás este obstáculo sin problemas.",
    ["O"] = "Encuentra a Parqual Fintallas en Entrañas.",
    ["T"] = "Prueba de conocimiento",
  },
  [1160] = {
    ["D"] = "Te diré lo que tienes que buscar, e incluso te formularé mi pregunta, pero no porque desees ponerte a prueba. El conocimiento va más allá de la fuerza física. Aunque otros puedan superarte en habilidad, el conocimiento del mundo te dará ventaja.$B$BDirígete al monasterio que está al norte de los Claros de Tirisfal. En la biblioteca que encontrarás allí, verás un libro titulado Comienzos de la amenaza de los no-muertos. Lo utilizaban los ahora corruptos paladines de allí para instruir a los no-muertos para luchar contra los de mi especie.$B$BTráemelo.",
    ["O"] = "Encuentra el libro Comienzos de la amenaza de los no-muertos y devuélveselo a Parqual Fintallas, que está en Entrañas.",
    ["T"] = "Prueba de conocimiento",
  },
  [1161] = "_",
  [1162] = "_",
  [1163] = "_",
  [1164] = {
    ["D"] = "¡Desgraciada! ¡Canalla! Kenata sigue viva, y su familia está sana y sigue prosperando mientras yo sufro. ¡Renegados tenían que ser!$B$BMi familia ha muerto debido a la peste. Arruinaron nuestra finca durante la guerra, la saquearon y le plantaron fuego. Y después de todo esto, ella y sus despreciables hijos tuvieron el descaro de robar las únicas posesiones de valor que me quedaban.$B$BYa no me preocupa lo que me fue robado. ¡Lo que quiero ahora son sus cabezas!$B$B¡Mátalos por mí! Ve a la Granja de los Dabyrie en Arathi, al noreste del Refugio de la Zaga.",
    ["O"] = "Llévale las cabezas de Kenata, Fardel y Marcel Dabyrie a Genavie Callow, que está en Entrañas.",
    ["T"] = "Robar a un ladrón...",
  },
  [1166] = {
    ["D"] = "¡Inclínate ante señor supremo Mok\'Morokk!$B$B¡Rexxar hacerme líder poderoso del clan Quebrantarrocas!$B$BYo buen jefe. Yo cuidar del clan.$B$BGrandes dragones vienen y hacen fuego en otra casa. Pero yo soy un ogro listo. Encontré nueva casa para el clan.$B$BClaro que dejamos casa vieja tan rápido que yo olvidé cosas. $GPequeño:Pequeña:r; $R, ve a por mis cosas. Ir al sur y encontrar mi rico grog, mi barril de tabaco y mi caja fuerte. Si haces eso, me caerás bien. Busca en Cubil de la Llama y en Ruinas Quebrantarrocas... Estar ahí, ¡yo seguro!",
    ["O"] = "El señor supremo Mok\'Morokk del Poblado Murohelecho quiere que recuperes su grog, su barril de tabaco y su caja fuerte del Pantano Revolcafango.",
    ["T"] = "Las pertenencias del señor supremo Mok\'Morokk",
  },
  [1168] = {
    ["D"] = "Mok\'Morokk fue nombrado señor supremo por Rexxar. Le preocupa más el poder que los ogros.$B$BLos dragones Negros matan a muchos ogros. Matan a la mujer de Tharg. Mok\'Morokk nos dice que huir. Nos hace venir aquí.$B$BLos ogros deben luchar. Los ogros no huir. No buenos corredores. Mok\'Morokk solo preocupa de Mok\'Morokk.$B$BYo llamo casa a las Ruinas Quebrantarrocas. A mí gustar el sur. Este lugar no casa.$B$BAyuda ogros combatir ejército de los dragones Negros. Quiero venganza. Quiero ir a casa.",
    ["O"] = "Tharg en el Poblado Murohelecho quiere que mates a 10 exploradores Pirocabello, 10 ceniazotes Pirocabello y 5 horrocrustos Pirocabello.",
    ["T"] = "El ejército de los dragones Negros",
  },
  [1169] = {
    ["D"] = "Mientras ese loco cobarde de Mok\'Morokk se revuelca en poder y Tharg lucha contra los demonios de su pena y su venganza y compite por el liderazgo del clan, solo yo me preocupo de identificar la fuente de la agresión a nuestro antiguo hogar.$B$B¿Por qué me miras así, $N? ¿Esperabas que hablara como un burdo pueblerino solo porque soy un ogro?$B$BEn fin, volvamos a nuestros asuntos. Tráeme los corazones y las lenguas de los cachorros y las crías. Yo buscaré la raíz de este mal...",
    ["O"] = "Draz\'Zilb del Poblado Murohelecho quiere que le lleves 7 lenguas abrasadoras y 7 corazones abrasadores.",
    ["T"] = "Identifica a los cachorros",
  },
  [1170] = {
    ["D"] = "El Poblado Quebrantarrocas fue invadido por el linaje de Onyxia. Pero, ¿por qué arrasaría nuestras tierras la hija de Alamuerte, el Señor de los dragones Negros?$B$BEs preocupante... Porque es evidente que Onyxia no vino aquí solo para asediar a un pequeño grupo de ogros.$B$B¡Informa a Mok\'Morokk de inmediato! Debemos actuar.",
    ["O"] = "Habla con el señor supremo Mok\'Morokk en el Poblado Murohelecho.",
    ["T"] = "El linaje de Onyxia",
  },
  [1171] = {
    ["D"] = "No me molestes. Ve a decir a Draz\'Zilb que nos quedamos aquí. Aquí no hay dragones Negros.",
    ["O"] = "Ve al Poblado Murohelecho y habla con Draz\'Zilb.",
    ["T"] = "El linaje de Onyxia",
  },
  [1172] = {
    ["D"] = "El linaje de Onyxia ha sido dispersado por todo El Pantano del Dragón. Es vital que impidamos que incube más crías.$B$BCorre hacia la Ciénaga de Fuego, al sur del Marjal Revolcafango. Seguro que ha establecido allí su guarida. Busca los huevos de ese ser maligno y destrúyelos.$B$BJamás reclamaremos el poblado Quebrantarrocas si los alrededores son un semillero de crías de dragón.$B$BY respecto a Mok\'Morokk... Tengo otros planes para ese demonio.",
    ["O"] = "Draz\'Zilb, del Poblado Murohelecho, quiere que destruyas 5 huevos de Onyxia.",
    ["T"] = "El linaje de Onyxia",
  },
  [1173] = {
    ["D"] = "¿Osas desafiar al señor supremo Mok\'Morokk?$B$B¡Ja!$B$B¡Yo aplastar insignificante $R!$B$BDime cuándo estar $Glisto:lista; para la paliza.",
    ["O"] = "Derrota a Mok\'Morokk y ve al Poblado Murohelecho a informar a Draz\'Zilb.",
    ["T"] = "Desafiar al señor supremo Mok\'Morokk",
  },
  [1174] = "_",
  [1175] = {
    ["D"] = "Tras el choque del Trueno Rojo de los goblins con un basilisco, Kronkpiloto me contrató para que me ocupe de la pista. Yo no sé lo que comerán esos animales en el desierto para crecer tanto pero tenemos que encargarnos de ellos si no queremos que deambulen por la pista durante la celebración de una carrera.$B$BTe diré lo que vamos a hacer. Si quieres ir ahí fuera y hacer algo de provecho, sal y acaba con los basiliscos que encuentres por la zona.",
    ["O"] = "El maestro rastreador Zherin quiere que mates a 10 basiliscos Piedra de sal, 10 vitropieles Piedra de sal y 6 observadores Piedra de sal en El Desierto de Sal.",
    ["T"] = "Un bache en la carretera",
  },
  [1177] = {
    ["D"] = "Fango ogro grande.$B$BYo tiene hambre.$B$BTú buscar peces. Trae comida a Fango.$B$BTú das de comer a Fango o Fango te come a ti.",
    ["O"] = "Fangolpe Piesucio del norte de Revolcafango quiere 8 cabezas de Ancalodo.",
    ["T"] = "¡Qué hambre!",
  },
  [1178] = {
    ["D"] = "Nuestro equipo de carreras es el orgullo de todos los goblins. Te sorprendería saber quiénes contribuyen de un modo u otro en esto.$B$B¿Conoces a Gazlowe? Él nos busca piezas por todo el mundo. ¡Incluso en una ocasión vino aquí para arreglar uno de estos trastos!$B$BHace unas cuantas semanas, dijo que nos enviaría un prototipo de un nuevo regulador de combustible pero todavía no nos ha llegado. Y él suele ser muy rápido... ¿Te importaría ir a Trinquete y preguntarle por el regulador? ¡No me gusta meter prisa pero los gnomos nos están ganando!",
    ["O"] = "Habla con Gazlowe en Trinquete, en Los Baldíos.",
    ["T"] = "Patrocinio goblin",
  },
  [1179] = {
    ["D"] = "¿Has oído hablar del Desierto de Sal? Es una franja de tierra salada y llana de Kalimdor, al sur del cañón de Las Mil Agujas. ¡Es un lugar ideal para echar una carrera ya que es totalmente llano!$B$BUn par de hermanos gnomos amigos míos están allí probando su nuevo coche cohete y, por lo que tengo entendido, ¡es una bala! Eh, es tan rápido que los pilotos se estrellan continuamente y sufren conmociones.$B$BPor eso me han pedido algunos cascos de máquinas de asedio pero tengo cosas que hacer aquí, ¡y no puedo ir!$B$B¿Podrías ir tú?",
    ["O"] = "Llévale el cajón de cascos de choque a Wizzle Pernolatón, que está en el Circuito del Espejismo en Las Mil Agujas.",
    ["T"] = "Los hermanos Pernolatón",
  },
  [1180] = {
    ["D"] = "Has vuelto por lo que habías venido a buscar. Sé que le prometí a Pozzik que le entregaría un nuevo regulador de combustible pero aún no lo he recibido. Revilgaz de Bahía del Botín dijo que lo había enviado pero se debe de haber perdido por algún lado.$B$BSi es cierto, no hay mucho que podamos hacer. Al menos podemos averiguar si salió realmente de Bahía del Botín.$B$BVe para allá y compruébalo con el jefe de embarcadero Lozgil. Podrá comprobarlo en los registros de cargamentos.",
    ["O"] = "Habla con el jefe de embarcadero Lozgil en Bahía del Botín.",
    ["T"] = "Patrocinio goblin",
  },
  [1181] = {
    ["D"] = "Me temo que no puedo ayudarte más, $N. Si quieres seguir investigando, te sugiero que vayas a ver al Barón. Si alguien sabe lo que está haciendo Revilgaz, es Revilgaz.$B$BLe encontrarás en la taberna al otro lado del puerto, en el piso de arriba.",
    ["O"] = "Habla con el Barón Revilgaz en la Bahía del Botín.",
    ["T"] = "Patrocinio goblin",
  },
  [1182] = {
    ["D"] = "Verás, las trituradoras de Ventura y Cía. del Lago Nazferiti (en el Norte de la Vega de Tuercespina) están equipadas con un nuevo modelo de regulador de combustible. Creo que Pozzik podría sacar ventaja de eso si pudiera conseguir algunos detalles. Por desgracia, Cozzle, el supervisor que está ahí fuera, parece más inteligente de lo que yo había pensado.$B$BQuizás tú tengas más suerte para encontrar los planos. Sé que los guarda en su casa, pero seguro que los tiene bajo llave.",
    ["O"] = "Lleva los planos de regulador de combustible al Barón Revilgaz en la Bahía del Botín.",
    ["T"] = "Patrocinio goblin",
  },
  [1183] = {
    ["D"] = "Creo que ya estás $Glisto:lista; para irte. Da recuerdos a Pozzik de mi parte y dile que, si alguna vez necesita algo, no dude en pedírmelo. ¡Estaré esperando la noticia de que ha conseguido batir el récord!",
    ["O"] = "Entrega los planos de regulador de combustible a Pozzik en El Desierto de Sal.",
    ["T"] = "Patrocinio goblin",
  },
  [1185] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Debajo de la quitina había...",
  },
  [1186] = {
    ["D"] = "Yo estaré muy ocupado intentando integrar el nuevo regulador de combustible con los diseños de nuestros coches pero si quieres seguir prestando tu ayuda al equipo, habla con Razzeric. Él es nuestro piloto, bueno... nuestro decimoctavo piloto para ser exactos, y disfruta realizando sus pequeñas modificaciones en los coches.$B$BEstá un poco chalado así que ten cuidado con lo que haces.",
    ["O"] = "Habla con Razzeric en El Desierto de Sal, en Las Mil Agujas.",
    ["T"] = "El piloto nº18",
  },
  [1187] = {
    ["D"] = "Pozzik es un gran mecánico, lo sé, pero tiene miedo de aumentar la potencia y lanzarse a la carrera. Yo no, sin embargo.$B$BCuando Pozzik acaba con un coche, yo me tomo un tiempo para realizar mis propios ajustes... ya sabes, aflojo los mandos, quito algunas piezas del cuadro y le pongo más combustible.$B$BLe encargué un propulsor de seforio a Shreev para que me lo enviara desde la Cacharrería de Gadgetzan pero parece que el zepelín en el que venía se estrelló en Marjal Revolcafango. ¡Lo necesito, $N!",
    ["O"] = "Consigue el propulsor de seforio para Razzeric, en El Desierto de Sal.",
    ["T"] = "Los ajustes de Razzeric",
  },
  [1188] = {
    ["D"] = "Pero espera, ¿qué es esto? Shreev dejó el seguro puesto y eso limitará la potencia del propulsor. ¡Le dije que se lo quitara! Esto no me gusta.$B$BTienes que llevar esto a la Cacharrería de Gadgetzan en el Desierto de Tanaris y decirle a Shreev que se lo quite.",
    ["O"] = "Llévale el propulsor de seforio a Shreev, que está en Gadgetzan en Tanaris.",
    ["T"] = "La seguridad primero",
  },
  [1189] = {
    ["D"] = "Aquí tienes, he hecho lo que Razzeric me pidió. Dile que tenga cuidado al activarlo. Si comete el más mínimo error, podría saltar por los aires.$B$BY sería una auténtica pena... ¿Quién sabe cómo sería el decimonoveno piloto?",
    ["O"] = "Devuélvele el propulsor de seforio modificado a Razzeric, que está en El Desierto de Sal en Las Mil Agujas.",
    ["T"] = "La seguridad primero",
  },
  [1190] = {
    ["D"] = "Los hermanos Pernolatón han descubierto un nuevo material de construcción, es la única explicación posible. Sus coches cada vez son más rápidos pero sus diseños y todos los datos que reunimos durante sus carreras son los de siempre.$B$BLo que sí sabemos es que Rizzle Pernolatón tiene unos diseños de ese nuevo descubrimiento.$B$BMientras Zamek los entretiene, tú roba los diseños.",
    ["O"] = "Consigue que Zamek haga una artimaña de distracción y roba los diseños de Rizzle para Pozzik en El Desierto de Sal, en Las Mil Agujas.",
    ["T"] = "Mantener la velocidad",
  },
  [1192] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Mena de indurio",
  },
  [1194] = {
    ["D"] = "Los esquemas de Rizzle describen el proceso de refinado del indurio. En sus garabatos queda claro que el indurio se obtiene de la excavación dirigida por los enanos en Uldaman, en las Tierras Inhóspitas.$B$BEl resto de notas hablan de la resistencia al calor.$B$BEncima de los diseños, a modo de pisapapeles, hay lo que aparenta ser indurio no refinado.",
    ["O"] = "Llévale la muestra de mena de indurio a Pozzik, que está en El Desierto de Sal en Las Mil Agujas.",
    ["T"] = "Los esquemas de Rizzle",
  },
  [1195] = {
    ["D"] = "Los centauros Galak de Las Mil Agujas custodian un artefacto desde el comienzo de sus tiempos.$B$BNos gustaría arrebatárselo pero para ello necesitamos una ampolla de agua de una de las Pozas de la Luna de los elfos de la noche.$B$BPara conseguir el agua, primero tendrás que hacerte con una de las ampollas que portan los dríades cerca del Refugio de la Algaba en el corazón del Bosque de Vallefresno. Hay una Poza de la Luna cerca de la orilla occidental del Río Falfarren, al sur de la carretera principal, donde podrás llenar la ampolla de agua.",
    ["O"] = "Llévale una ampolla con grabados llena a Zangen Pezuña Pétrea, que está en Cima del Trueno.",
    ["T"] = "La llama sacra",
  },
  [1196] = {
    ["D"] = "Llévale la ampolla con agua a Rau Abiscorredor en el Poblado Viento Libre en la zona sudeste de Las Mil Agujas. Podrás llegar a Las Mil Agujas cogiendo El Gran Elevador que encontrarás en el extremo sur de Los Baldíos.$B$BNo me sorprendería que pudieras seguir ayudándonos.",
    ["O"] = "Entrégale la ampolla con grabados llena a Rau Abiscorredor del Poblado Viento Libre.",
    ["T"] = "La llama sacra",
  },
  [1197] = {
    ["D"] = "Con el agua que has traído podrás apagar temporalmente la llama que protege la reliquia de los centauros. Sin embargo, su efecto no es permanente por lo que tendrás que darte prisa.$B$BEncontrarás el Risco Pezuña Quebrada, el fuerte de los centauros, en dirección norte. La llama se encuentra en el Campamento Pezuña Quebrada, un lugar fuertemente custodiado del risco.$B$BTen cuidado pues los centauros Galak tienen a muchos hombres allí y no dejarán que les arrebates algo tan preciado para ellos con facilidad.",
    ["O"] = "Consigue la pezuña hendida para Rau Abiscorredor del Poblado Viento Libre.",
    ["T"] = "La llama sacra",
  },
  [1198] = {
    ["D"] = "Estos últimos días circulan muchos rumores sobre la reunión del Martillo Crepuscular y los nagas en las Cavernas de Brazanegra al oeste.$B$BEstoy aquí para reunir toda la información que pueda para que sepamos dónde y cuándo necesitaremos ayuda.$B$BHace unos días envié a un explorador para observar la actividad de las Cavernas de Brazanegra... Pero el explorador Thaelrid aún no ha regresado.$B$BMis otros exploradores están ocupados con asuntos más urgentes.$B$BBusca a Thaelrid por mí, $N.",
    ["O"] = "Busca al explorador Thaelrid en las Cavernas de Brazanegra.",
    ["T"] = "Buscando a Thaelrid",
  },
  [1199] = {
    ["D"] = "Con el poder que les confieren los ritos de los caóticos dioses antiguos, los miembros del Martillo Crepuscular trabajan para devolver a sus oscuros Señores a este mundo y así extender la muerte y el terror por él.$B$BCreemos que el Martillo Crepuscular se ha aliado con los nagas. Se están concentrando en las Cavernas de Brazanegra, al oeste.$B$BDebemos frustrar los planes que se forjan en las profundidades del viejo santuario, sean cuales sean.$B$BEnfréntate al Martillo y tráenos pruebas de tus hazañas. Tus esfuerzos serán ampliamente recompensados.",
    ["O"] = "Lleva 10 colgantes Crepusculares a la centinela Aluwyn en el Campamento Brazanegra.",
    ["T"] = "Cascada Crepuscular",
  },
  [1200] = {
    ["D"] = "Las fuerzas me han abandonado. ¡Necesito tu ayuda!$B$BOtrora este lugar era un gran templo de Elune. Pero la desgracia lo ha llevado a la ruina cuando la corrupción de un dios antiguo empezó a calar desde el subsuelo y contaminó la sagrada Poza de la Luna.$B$BAku\'Mai, sirviente del dios antiguo, surgió de las aguas.$B$BLos adeptos del Martillo Crepuscular se han unido a los nagas para ocupar esas tierras. Esos adeptos, cuyo jefe es Kelris, sacrifican a inocentes a Aku\'Mai a cambio de poder.$B$BMata a Kelris y llévale su cabeza a Ashelan en el Campamento Brazanegra, $N, te lo ruego.",
    ["O"] = "Llévale la cabeza del señor crepuscular Kelris a Ashelan Bosquenorte en el Campamento Brazanegra.",
    ["T"] = "La vileza de Brazanegra",
  },
  [1201] = {
    ["D"] = "La tregua de Horda y la Alianza peligra. Ambas partes intentan dañar a la otra. Refriegas, acciones encubiertas... ¡Nuestro deber es asegurarnos de que gane la Horda!$B$BY esta es nuestra oportunidad de conseguirlo.$B$BHay infiltrados de la Alianza al este del Poblado Murohelecho. Espían el poblado e informan de nuestros movimientos a Theramore, cosa que no podemos permitir.$B$BDa caza a los infiltrados y mátalos. Envía un claro mensaje a sus superiores: ¡no toleraremos la presencia de sus espías!",
    ["O"] = "Mata a 9 infiltrados de Theramore y luego reúnete con Nazeer Picasangre en el Poblado Murohelecho.",
    ["T"] = "Los espías de Theramore",
  },
  [1202] = {
    ["D"] = "$N, enviamos a dos de nuestros espías a robar documentos de un navío de los puertos de Theramore. Consiguieron los papeles, pero fueron descubiertos y se vieron obligados a tirarlos al mar.$B$BUno de ellos consiguió escapar más tarde, pero no logró recuperar los documentos. Vino a mí a informar de su fracaso y ahora yo te encargo a ti la misión.$B$BLos documentos están en un baúl, en las aguas del puerto de Theramore. Encuéntralos y tráemelos aquí, $N.",
    ["O"] = "Lleva los documentos del Capitán a Nazeer Picasangre al Poblado Murohelecho.",
    ["T"] = "El puerto de Theramore",
  },
  [1203] = {
    ["D"] = "Tengo algo que cortar. No puedo decirte qué voy a cortar, pero... Necesito una hoja. Una en concreto...$B$BHay quien la llama espada. Yo prefiero llamarla hoja.$B$B¿Me ayudas? Esos hombres-pez, ya sabes, los múrlocs o como se llamen. Hay uno al que llaman Espinafilada, que lleva una gran espada y vive al norte, cerca de la playa. ¡Esa es la espada que necesito!$B$BPero no te duermas en los laureles. Lo que quiero cortar no esperará para siempre...",
    ["O"] = "Lleva la espada de Espinafilada a \"Ojolodo\" Jarl, en la Mansión Cienaluz.",
    ["T"] = "Jarl necesita una hoja",
  },
  [1204] = {
    ["D"] = "Estoy trabajando en un plato que llamaré \"sopa de Fangorroca con tropezones\". Pero necesito los ingredientes principales y... bueno, no puedo ir a buscarlos al Marjal Revolcafango, ¡no creo que durara mucho allí!$B$BQuizás podrías ir tú por mí, $N. Para empezar, necesito lenguas de tortugas Fangorroca. Puedes encontrar tortugas Fangorroca en la costa, sobre todo en la bahía.$B$B¡Pero no me sirve cualquier lengua! Sé que algunas Fangorroca tienen lenguas bífidas...$B$BY se dice que las lenguas bífidas tienen un sabor especial... Quiero ese sabor en mi plato.",
    ["O"] = "Lleva 8 lenguas Fangorroca bífidas a Morgan Stern a Theramore.",
    ["T"] = "Sopa de Fangorroca con tropezones",
  },
  [1205] = {
    ["D"] = "Lodomuerto es un anciano crocolisco del Marjal Revolcafango. Y ha llegado su hora.$B$BSus viejos huesos crujen cuando desplaza su enorme cuerpo por el pantano y aunque aún se mueve con el vigor y la velocidad de un joven, su anciano cuerpo tortura al gran espíritu que lo habita, un espíritu cuya llama no se extinguirá.$B$BSigue viviendo con constantes y enloquecedores dolores. Debes poner fin a la vida de esta noble criatura, $N. Debes dar paz a Lodomuerto.",
    ["O"] = "Lleva el Diente de Lodomuerto a Melor a Cima del Trueno.",
    ["T"] = "Lodomuerto",
  },
  [1206] = {
    ["D"] = "El zumo de ojo es una bebida estupenda. Es una pena que la mayoría de los bichos solo tengan dos ojos.$B$BPero las arañas tienen muchos. Muchos, muchos. Y el zumo de ojo de araña Niebla Negra está muy rico. ¡Suavecito y salado! Redonditos ojos de araña... Se me hace la boca agua...$B$BLas arañas Niebla Negra están al oeste de aquí, en la Cueva Niebla Negra. Está claro que las arañas llevan el nombre de la cueva en la que viven... ¿o será al revés?",
    ["O"] = "Lleva 20 ojos de Niebla Negra sin reventar a \"Ojolodo\" Jarl a la Mansión Cienaluz.",
    ["T"] = "Jarl necesita ojos",
  },
  [1218] = {
    ["D"] = "Me gusta cocinar. Sobre todo, la buena cocina del pantano. Ancas de rana verdes fritas y esas cosas.$B$BY me he quedado sin ancas. Pero, la casa está llena de ranas. Ranas de marisma gigantes. ¿Podrías traerme algunas de esas ancas? No es muy difícil, solo golpéalas en la cabeza y tráeme las ancas.$B$BLo removeré y removeré y luego me lo comeré. Y también te daré un poco a ti.$B$B¿Te gustan los guisos?",
    ["O"] = "Lleva 10 ancas de rana de marisma a \"Ojolodo\" Jarl al Marjal Revolcafango.",
    ["T"] = "Ancas de rana de marisma",
  },
  [1219] = {
    ["D"] = "Una criba de la tierra revela la mano cortada de un orco. En la mano hay un tubo de hueso. Y en el tubo, un papiro manchado de tierra.$B$BEl pergamino está escrito en lenguaje orco... y parece importante.",
    ["O"] = "Lleva el informe de espía al teniente Aden a Theramore.",
    ["T"] = "El informe orco",
  },
  [1220] = {
    ["D"] = "El capitán Vimes debería ver el informe orco que encontraste, $N. Conoce el idioma orco mejor que muchos. ¡Y debería saber que hay espías de la Horda cerca de Theramore!$B$BEl Capitán está en los barracones que hay en la parte noreste de la Isla Theramore.",
    ["O"] = "Lleva el informe de espía al capitán Garran Vimes.",
    ["T"] = "Capitán Vimes",
  },
  [1221] = {
    ["D"] = "Los tubérculos hojazul son muy apreciados en todo el mundo. Pero son muy raros. Solo se encuentran aquí, en Los Baldíos, enterrados profundamente en Horado Rajacieno.$B$B¡Y es imposible encontrarlos a no ser que sepas dónde mirar! Por eso he amaestrado husmeadores taltuza para que los busquen. Tienen un olfato extraordinario, huelen los tubérculos a una distancia de 50 pasos.$B$BNo será fácil, pero si me traes tubérculos te pagaré muy bien.",
    ["O"] = "Coge un cajón con agujeros.$BCoge una vara de mando de husmeador.$BLee el manual de propietario de husmeador. $B$BCuando llegues a Horado Rajacieno, usa el cajón con agujeros para invocar un husmeador taltuza; usa la vara para que busque tubérculos. $B$BLleva 6 tubérculos hojazul, la vara de mando de husmeador y el cajón con agujeros a Mebok Mizzyrix a Trinquete.",
    ["T"] = "Los tubérculos hojazul",
  },
  [1222] = {
    ["D"] = "¡Genial! ¡Por fin algo que no quiere comerme!$B$BNecesito ayuda con los bichos de por aquí. Y tú pareces ser justo lo que necesito, $Gchaval:señorita;.$B$BEl cocinero Morgan Stern está trabajando en una nueva receta y me ha mandado aquí a recoger hojas de cienagrana. Creo que opina que son sabrosas.$B$BEs lo mismo... el caso es que cuando buscaba una planta de cienagrana... ¡Me vi rodeado! ¿Podrías ayudarme a salir de aquí y vigilarme las espaldas mientras busco la cienagrana?",
    ["O"] = "Escolta a \"Apestoso\" Ignatz y luego ve a Theramore a hablar con Morgan Stern.",
    ["T"] = "La fuga de Apestoso",
  },
  [1238] = {
    ["D"] = "Una criba de la tierra revela la mano cortada de un orco. En la mano hay un tubo de hueso. Y en el tubo, un papiro manchado de tierra.$B$BEl pergamino es el informe de un orco que pasó semanas espiando la ciudad de Theramore.$B$BCon una rápida lectura del pergamino te das cuenta de que debes llevarlo al maestro de espías orco del Poblado Murohelecho.",
    ["O"] = "Lleva el informe de espía a Nazeer Picasangre al Poblado Murohelecho.",
    ["T"] = "El informe perdido",
  },
  [1239] = {
    ["D"] = "Una enérgica excavación revela la cabeza de un orco macho, salvajemente cercenada. Sus labios se petrificaron con un gruñido y sus ojos oscuros tienen aún una mirada desafiante.",
    ["O"] = "Lleva la cabeza de orco desafiante a Nazeer Picasangre al Poblado Murohelecho.",
    ["T"] = "La cabeza cercenada",
  },
  [1240] = {
    ["D"] = "¡Debemos conocer las circunstancias de la muerte de Marg! Y la única forma de conocerlas, $N, es a través de la magia de los trols.$B$BLleva la cabeza de Marg a Kin\'weelay. Está en el Campamento Grom\'gol, en la Vega de Tuercespina.$B$BKin\'weelay es el médico brujo de los trols Lanza Negra. Es muy poderoso. Si alguien puede hablar al espíritu de Marg Ojonocturno, ese es él.",
    ["O"] = "Lleva la cabeza de orco desafiante a Kin\'weelay al Campamento Grom\'gol de Vega de Tuercespina.",
    ["T"] = "El trol médico brujo",
  },
  [1241] = {
    ["D"] = "Recientemente, un diplomático fue enviado a Theramore para encontrarse con Jaina Valiente. Ese diplomático nunca llegó a su destino.$B$BCreo que los Defias están implicados, aunque no sé exactamente cómo. La desaparición del diplomático sigue sin ser del dominio público, pero no seguirá así mucho tiempo.$B$BHay unos agentes del rey explorando la ciudad en busca de pistas, pero tengo a mis propios contactos que me gustaría que trabajasen en esto.$B$BEn el Valle de los Héroes hay un viejo amigo mío llamado Jorgen. Encuéntrale, dale esta nota y sigue sus instrucciones.",
    ["O"] = "Encuentra a Jorgen en la Ciudad de Ventormenta.",
    ["T"] = "El diplomático desaparecido",
  },
  [1242] = {
    ["D"] = "Lleva esta nota a Elling Trias. No le digas nada. Simplemente sé amable y espera a que te responda. Y no te preocupes, lo hará.$B$BVoy a decirte esto una vez más, $N. Trata este asunto con seriedad, y mantén la boca cerrada.$B$BPodrás encontrar a Trias en su tienda de quesos, Queso de Trias, en el Distrito de Mercaderes. Buena suerte.",
    ["O"] = "Entrega la nota lacrada de Jorgen a Elling Trias en la Ciudad de Ventormenta.",
    ["T"] = "El diplomático desaparecido",
  },
  [1243] = {
    ["D"] = "Jorgen, viejo hijo de... Así que, ¿así es como va a ser, eh?$B$BPues muy bien, puedo con eso. Espero que encuentre El Vacío Abisal muy pronto.$B$BEsto es lo que va a pasar, $N. Empezaré a usar mis contactos aquí, pero tú vas a encargarte de los paseítos, no quiero perder dinero.$B$BVete a Villa Oscura en el Bosque del Ocaso y encuentra al vigía Backus. Generalmente patrulla el camino del norte justo fuera de la ciudad. Dile que estás buscando actividad Defias, que yo te lo he pedido y te ayudará dándote toda la información que pueda.",
    ["O"] = "Entrega la nota lacrada al vigía Backus en el Bosque del Ocaso.",
    ["T"] = "El diplomático desaparecido",
  },
  [1244] = {
    ["D"] = "¿Actividad Defias? Bueno, siempre hay actividad; incluso si están tranquilitos, les seguimos considerando una amenaza.$B$BPero ahora que lo pienso, hace poco tiempo vimos algo extraño. Debe de haber sido hace un par de semanas, algunos de sus agentes se habían reunido en la Granja de Addle. Según el informe, supuse que algo grande estaba pasando.$B$B¿Por qué no vas a echar un vistazo y me traes lo que encuentres?$B$BLa granja está al sur del camino de los Páramos de Poniente, justo al entrar en el Bosque del Ocaso.",
    ["O"] = "Encuentra el certificado Defias y llévaselo al vigía Backus en el Bosque del Ocaso.",
    ["T"] = "El diplomático desaparecido",
  },
  [1245] = {
    ["D"] = "¡Guau! Me parece muy complicado para ser algo que pueda llevarte por mal camino. Ni siquiera debería estar viendo esto. Llévaselo a Trias, él podrá encontrarle el significado. Y NO permitas que nadie se interponga en tu camino. Esto me supera, quizás incluso le supere a él, pero ya veremos.$B$BY hazme un favor, $N, no le digas a nadie que te he ayudado. Si mis superiores se enterasen de que sé algo de esto y no se lo cuento, probablemente me colgarían.",
    ["O"] = "Lleva el certificado Defias a Elling Trias en Ventormenta.",
    ["T"] = "El diplomático desaparecido",
  },
  [1247] = {
    ["D"] = "Eh, sí... Mmm... Quizás sepa algo sobre esa reunión en la Granja de Addle. Creo que fue hace unas semanas. Yo no participé. Acabé cuando el plan A falló... Malditos aventureros.$B$BEl tipo que teníamos dentro para el plan B, salió de Menethil; un tal Flaco. Eso es todo lo que sé sobre su plan de reserva.",
    ["O"] = "Habla con Elling Trias en Ventormenta.",
    ["T"] = "El diplomático desaparecido",
  },
  [1248] = {
    ["D"] = "Uno de mis contactos se retiró y se fue a Los Humedales, en la ciudad portuaria de Menethil. Seguro que te ayuda si le dices que estás trabajando para mí. Se llama Mikhail. Dijo algo sobre convertirse en camarero antes de irse de Ventormenta hace unos años.$B$BHabla con él, encuentra al Flaco este, y descubre si hay algo más que saber sobre el diplomático secuestrado. Tenemos que descubrir dónde le han llevado, o al menos, quién es. En los papeles no se dice nada sobre sus planes después de la captura.",
    ["O"] = "Habla con Mikhail en Los Humedales.",
    ["T"] = "El diplomático desaparecido",
  },
  [1249] = {
    ["D"] = "¿Dices que Elling te ha enviado? Bueno, pues entonces, la hospitalidad que te expresé, multiplícala por diez. Si necesitas cualquier cosa, solo tienes que pedirla. Haré lo que pueda para ayudarte.$B$BMmm, ¿el Flaco dices? No sé si le conozco, pero mantendré los ojos abiertos por si le veo.$B$BNo mires ahora, $N, pero el caballero que está allí en la salida, está mirando hacia aquí. Está escuchando nuestra conversación con demasiado interés, ya sabes lo que quiero decir... Si yo fuera tú, haría algo para detenerle antes de que salga corriendo.",
    ["O"] = "Vence a Tapoke Jahn antes de que escape y vuelve a ver a Mikhail en Menethil.",
    ["T"] = "El diplomático desaparecido",
  },
  [1250] = {
    ["D"] = "Los Defias no me pagaron lo suficiente para morir por su causa. Cogieron al rey. Sí, el rey de Ventormenta, no cualquier diplomático. Estaba de camino a Theramore para encontrarse con Jaina Valiente. Por lo visto, se trataba de algo gordo, un gran secreto. Pero no solo los Defias están implicados. Trabajan con alguien. Creo que sabían que esto era algo grande.$B$BLo único que yo tenía que hacer era subir a bordo del barco del rey a su hombre, mientras el barco estaba en el puerto. El tipo se llamaba Hendel. Eso es todo lo que sé. Lo juro.",
    ["O"] = "Habla con Mikhail en Los Humedales.",
    ["T"] = "El diplomático desaparecido",
  },
  [1252] = {
    ["D"] = "Debajo de los escombros, en el suelo, ves un destello de luz. Apartas los escombros y la ceniza y descubres una insignia de esmalte blanco con un ancla de oro: el sello de Theramore.$B$BDebajo del ancla hay un nombre grabado: \"Teniente Paval Reethe\".",
    ["O"] = "Lleva el distintivo de Reethe al capitán Garran Vimes de Theramore.",
    ["T"] = "Teniente Paval Reethe",
  },
  [1258] = {
    ["D"] = "Otro ingrediente para mi nueva receta es la pata de cangrejo gigante sin pelar. Para ello, los cangrejos del Marjal Revolcafango, esos que llaman reptadores de roca espinosos, ¡serían perfectos!$B$BLa cáscara de la pata de cangrejo es un elemento clave de la presentación del plato, así que necesito patas impolutas, sin marcas. Un cangrejo adulto solo tiene una o dos patas de esas y yo necesitaré muchas para mis pruebas.$B$BDirígete a las rocas que rodean Theramore y ¡consígueme esas patas!",
    ["O"] = "Lleva 12 patas de reptador prístinas a Morgan Stern a Theramore.",
    ["T"] = "...con tropezones",
  },
  [1259] = {
    ["D"] = "No reconozco el nombre... Teniente Paval Reethe... Será mejor que consultes al ayudante Tesoran. Él lleva todos los registros de la Guardia de Theramore, desde el personal hasta el equipamiento.$B$BÉl debería poder decirte quién es Reethe, solo tendrá que echar un vistazo a los libros.",
    ["O"] = "Averigua más sobre Reethe hablando con el ayudante Tesoran en Theramore.",
    ["T"] = "Teniente Paval Reethe",
  },
  [1260] = {
    ["D"] = "Estoy preparando un nuevo menú para el restaurante. Estará lleno de platos rurales con un mismo tema: el pantano. ¡Seguro que a mis clientes de El Ermitaño Taciturno les encanta!$B$BEnvié a mi sobrino Morgan a Theramore, una ciudad que hay en la frontera del Marjal Revolcafango. Está recogiendo recetas de sus habitantes y necesito saber qué ha conseguido hasta ahora.$B$BEl viaje a Theramore es largo. Demasiado para mí. Pero seguro que $Gun:una; $C como tú, aprovecha cualquier oportunidad para viajar.",
    ["O"] = "Habla con Morgan Stern en Theramore.",
    ["T"] = "[DEPRECATED]Morgan Stern",
  },
  [1261] = {
    ["D"] = "¡Maldito Valvafango! ¡No puedes matar a Marg! Ah... pero estoy muerto. Así que debió de matarme...$B$B¡Mi misión! ¡Mi misión! $N, debes completarla.$B$BCuando estaba cerca de Theramore, estuve espiando a un grupo de soldados de la Alianza que había acampado en la playa. Uno de esos soldados llevaba un colgante cargado de gemas. ¡Ningún soldado puede permitirse un colgante así!$B$BUna noche me colé en su campamento y robé el colgante, ¡pero cuando regresaba a la base, uno de esos hombres-cangrejo Valvafango me mató!$B$B¡$N, debes encontrar a ese Valvafango y recuperar el colgante!",
    ["O"] = "Lleva el colgante con joyas a Nazeer al Poblado Murohelecho.",
    ["T"] = "Las declaraciones de Marg",
  },
  [1262] = {
    ["D"] = "Es extraño que ese colgante que robó Marg estuviera en manos de simples soldados. Quizás su dueño estaba disfrazado... ¡o quizás los soldados lo habían robado a su vez!$B$BSea cual sea la verdad, este es el colgante de un noble. Debemos encontrar a su dueño si queremos conocer su misteriosa historia.$B$BLleva el colgante a Zor Árbol Solitario a Orgrimmar. Conoce muy bien el ámbito político de la Alianza. Él sabrá si la historia de este colgante se entrelaza en algún punto con la de los nobles de Ventormenta.",
    ["O"] = "Lleva el colgante con joyas a Zor Árbol Solitario a Orgrimmar.",
    ["T"] = "Informa a Zor",
  },
  [1263] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La posada quemada &lt;CHANGE TO GOSSIP&gt;",
  },
  [1268] = {
    ["D"] = "Hay hileras de huellas que se alejan del esqueleto, aún humeante, de la Posada Reposo Umbrío. Hay unas pocas impresiones visibles y algunas hileras de huellas casi imperceptibles en el barro blando del pantano.",
    ["O"] = "Informa a Krog en el Poblado Murohelecho sobre las huellas sospechosas de la Posada Reposo Umbrío.",
    ["T"] = "Huellas de pezuñas sospechosas",
  },
  [1269] = {
    ["D"] = "Debajo de los escombros, en el suelo, ves un destello de luz. Apartas los escombros y la ceniza y descubres una insignia de esmalte blanco con un ancla de oro: el sello de Theramore.$B$BDebajo del ancla hay un nombre grabado: \"Teniente Paval Reethe\".",
    ["O"] = "Lleva el distintivo de Reethe a Krog al Poblado Murohelecho.",
    ["T"] = "Teniente Paval Reethe",
  },
  [1270] = {
    ["D"] = "¡Genial! ¡Por fin algo que no quiere comerme!$B$BNecesito ayuda con los bichos de por aquí. Y tú pareces ser justo lo que necesito, $Gchaval:señorita;.$B$BUn goblin de Trinquete llamado Mebok Mizzyrix necesita unas hojas de cienagrana para una poción o algo así... Tampoco es que me importe mucho. Me dio un buen dinero y conozco el pantano, así que...$B$B¡Una pena que me rodearan antes de que encontrara la cienagrana! ¿Puedes ayudarme? ¿Podrías cubrirme las espaldas mientras busco la planta de Mebok?",
    ["O"] = "Escolta a \"Apestoso\" Ignatz y luego ve a Trinquete a hablar con Mebok Mizzyrix.",
    ["T"] = "La fuga de Apestoso",
  },
  [1272] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Encontrar a Reethe &lt;CHANGE INTO GOSSIP&gt;",
  },
  [1273] = {
    ["D"] = "Me llevó un buen rato, pero encontré a Reethe. Estaba bien escondido, para ser humano.$B$BOgron temía que pudiera haber enloquecido después de pasar tanto tiempo en la marisma. Ven conmigo. Tiene que darnos algunas respuestas.",
    ["O"] = "Ve con Ogron a hablar con Reethe y luego vuelve al Poblado Murohelecho a hablar con Krog.",
    ["T"] = "El interrogatorio de Reethe",
  },
  [1274] = {
    ["D"] = "Disculpa, noble $Gcaballero:gentil dama;. El Obispo DeLavey me pidió que buscara la ayuda de aventureros que quizás puedan ayudarle.$B$BSi pudieras ir discretamente al Castillo de Ventormenta a hablar con él, seguro que te lo agradecería mucho. Y, de nuevo, por favor, con mucha discreción. Se trata de un asunto muy importante.",
    ["O"] = "Encuentra al Obispo DeLavey en el Castillo de Ventormenta.",
    ["T"] = "El diplomático desaparecido",
  },
  [1275] = {
    ["D"] = "Esos insensatos elfos de sangre están estudiando la magia demoníaca. ¿Acaso no vieron lo que ocurrió con los nagas y los sátiros de Kalimdor?$B$BTemo que los elfos de sangre acabarán sufriendo una deformación similar. Azeroth no puede permitirse el surgimiento de otra raza de monstruos maléficos.$B$BQuisiera estudiar los cerebros de los sátiros y los nagas. Quizás consiga averiguar qué destino espera a los sin\'dorei. Puedes encontrar sátiros y naga en las Cavernas de Brazanegra, al oeste.",
    ["O"] = "Relwyn Sombrestrella en el Campamento Brazanegra quiere 8 bulbos raquídeos corruptos.",
    ["T"] = "Investigaciones acerca de la corrupción",
  },
  [1276] = {
    ["D"] = "¡Ah, ya lo recuerdo! Mosarn es un respetado herrero de Cima del Trueno. Sabía que había oído ese nombre antes...$B$BComo agradecimiento, el Anillo de la Tierra me recompensó con una armadura hecha por Mosarn. Es un buen trabajo.$B$BQuizás él pueda decirte más sobre el escudo que forjó.$B$BNo es gran cosa, pero como no conseguimos nada de Reethe, es todo lo que tenemos.",
    ["O"] = "Ve a Cima del Trueno y enseña el escudo de hierro ennegrecido a Mosarn.",
    ["T"] = "El escudo negro",
  },
  [1277] = "_",
  [1278] = "_",
  [1279] = "_",
  [1280] = "_",
  [1281] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La canción de Jim &lt;CHANGE TO GOSSIP&gt;",
  },
  [1282] = {
    ["D"] = "El viejo Jim me da mucha pena. Antes no era así. Llevó una posada en la marisma durante un tiempo; un lugar fantástico.$B$BMira, no me apetece mucho hablar del tema. Si realmente quieres saber qué ocurrió, habla con el capitán Vimes. Está apostado en el piso de arriba de la Ciudadela Garrida, en los grandes barracones que hay al este de la torre de Lady Jaina.",
    ["O"] = "Habla con el capitán Garran Vimes que está en el piso de arriba de la Ciudadela Garrida.",
    ["T"] = "Le llaman Jim Sonrisas",
  },
  [1283] = "_",
  [1284] = {
    ["D"] = "Hay hileras de huellas que se alejan del esqueleto, aún humeante, de la Posada Reposo Umbrío. Hay unas pocas impresiones visibles y algunas hileras de huellas casi imperceptibles en el barro blando del pantano.",
    ["O"] = "Informa al capitán Garran Vimes, en Theramore, sobre las huellas sospechosas de la Posada Reposo Umbrío.",
    ["T"] = "Huellas de pezuñas sospechosas",
  },
  [1286] = {
    ["D"] = "Mantener nuestra presencia en la marisma ha sido una lucha constante. Pero con la fuerza de nuestra determinación, hemos conseguido mantener varias atalayas defensivas en la zona.$B$BLos últimos informes que he recibido de los exploradores indican que hay un grupo de desertores en una de nuestras torres abandonadas en Punta Perdida, al sureste de la posada.$B$BEl teniente que los lidera se llama Balos Jacken. Ve a ver si consigues algo de información de él.$B$BPero ten cuidado, $N. Será duro de pelar.",
    ["O"] = "Consigue información sobre Reethe hablando con Balos Jacken y los desertores del Marjal Revolcafango.",
    ["T"] = "Los desertores",
  },
  [1288] = {
    ["D"] = "Has hecho un buen trabajo, $N, lo mejor que he visto en $Gun:una; amateur.$B$BLady Valiente me pidió que la mantuviera al corriente de esta delicada situación pero hace tiempo que no informo.$B$BIba a enviarle mi informe más tarde, pero creo que sería mejor que fueras tú quien hablara con ella. Probablemente podrás contestar mejor a sus preguntas que yo.",
    ["O"] = "Lleva el informe de Vimes a Lady Jaina Valiente a Theramore.",
    ["T"] = "El informe de Vimes",
  },
  [1289] = {
    ["D"] = "Well. judging from Captain Vimes\'s report. you\'ve been a huge help to his investigation. and for that I thank you.$B$BI can put my mind more at ease knowing that the matter is in such capable hands. If you would. I would request that you return to Captain Vimes and help him in getting to the bottom of this mystery.",
    ["O"] = "Vuelve junto al capitán Garran Vimes a Theramore.",
    ["T"] = "&lt;nyi&gt; El informe de Vimes",
  },
  [1293] = "_",
  [1294] = "_",
  [1295] = "_",
  [1296] = "_",
  [1297] = "_",
  [1299] = "_",
  [1300] = "_",
  [1301] = {
    ["D"] = "Antes de trabajar aquí, en El Ermitaño Taciturno, me formó uno de los mejores chefs y taberneros que he conocido en mi vida. Su nombre era James Hyal. Todo lo que tengo se lo debo a él. Gracias a él he llegado hasta aquí.$B$BMe pregunto cómo le va... Su hermano Vincent y él fueron a trabajar a una posada del Puerto de Menethil, pero no sé nada de él desde hace un tiempo.$B$BSi alguna vez te acercas por allí, ¿podrías saludarlo de mi parte?",
    ["O"] = "Ve al Puerto de Menethil y habla con Vincent Hyal.",
    ["T"] = "[DEPRECATED]James Hyal",
  },
  [1302] = {
    ["D"] = "Mi hermano James cruzó el mar con Lady Valiente. Lo último que oí es que la lucha había cesado en la zona y que James había abierto una posada en algún lugar del continente. Esté donde esté, seguro que el negocio le va bien. Era un cocinero estupendo.$B$BHace tiempo que no sé nada de él... estará ocupado con su posada. Seguro que el secretario de Theramore puede decirte algo más. Búscale en el piso de arriba del asentamiento que hay cerca del centro de la ciudad. ¿Cómo se llama...? Ciudadela Garrida, eso es.",
    ["O"] = "Habla con el secretario Lendry, que está en la Ciudadela Garrida en Theramore.",
    ["T"] = "[DEPRECATED]James Hyal",
  },
  [1319] = {
    ["D"] = "Siento no ser de más ayuda, $N, pero puedo remitirte a alguien que podrá serlo. Caz Dosdientes, uno de nuestros mejores herreros. Trabaja en la forja de la herrería.$B$BLlévale el escudo. A ver qué puede decirte sobre él.",
    ["O"] = "Ve a Theramore y enseña el escudo de hierro ennegrecido a Caz Dosdientes.",
    ["T"] = "El escudo negro",
  },
  [1321] = {
    ["D"] = "¿Mmm? No veo nada fuera de lo normal en este escudo. Deberíamos enseñárselo a Do\'gol, el herrero, a ver si sabe decirnos algo más, pero no me haría muchas ilusiones...",
    ["O"] = "Muestra el escudo de hierro ennegrecido a Do\'gol en el Poblado Murohelecho.",
    ["T"] = "El escudo negro",
  },
  [1322] = {
    ["D"] = "Mmmm, hay algo escrito en el borde del escudo... pero es difícil leerlo... está muy quemado.$B$BNecesito ácido para limpiar la superficie. Podría utilizar el veneno de las arañas Niebla Negra. Estas arañas han tomado una vieja mina, la Cueva de la Niebla, que se encuentra al noroeste del pueblo. Consígueme algunas de sus glándulas de veneno para que pueda limpiar esto.",
    ["O"] = "Consigue 5 glándulas de veneno ácido para Do\'gol del Poblado Murohelecho.",
    ["T"] = "El escudo negro",
  },
  [1338] = {
    ["D"] = "Hay un forjador de armaduras enano cuyo trabajo me gusta mucho. Se llama Furen Barbalarga y es el mejor de su gremio. Necesito un escudo nuevo, y quiero que me lo haga él.$B$BSolo hay un problema... Furen vive muy al sur, en el Distrito de los Enanos de Ventormenta. ¡En tierra humana! No entiendo por qué vive ahí, tan lejos de Forjaz. ¡Será que los humanos le pagan una fortuna!$B$BSi te apetece viajar y ganar algo de dinero, ve a ver a Furen para hacer mi pedido de un escudo.",
    ["O"] = "Lleva el pedido de Pico Tormenta a Furen Barbalarga a Ventormenta.",
    ["T"] = "Un pedido de Pico Tormenta",
  },
  [1339] = {
    ["D"] = "Dicen que el montaraz Pico Tormenta busca alguien que le haga de mensajero. Alguien con ganas de viajar un poco. ¿Qué te parece? ¿Te ves capaz, $R?$B$BEntonces, ve a hablar con Pico Tormenta en la cima de la torre norte.",
    ["O"] = "Habla con el montaraz Pico Tormenta.",
    ["T"] = "La tarea del montaraz Pico Tormenta",
  },
  [1359] = {
    ["D"] = "Los corazones de lobo que conseguiste, poseen una calidad nunca vista antes en especímenes similares. Tienen tal grado de toxicidad que deberían ser mortales incluso para los lobos en los que laten esos corazones.$B$BEs bastante impresionante, y debemos estudiarlo en profundidad. He guardado algunas muestras de los corazones. Llévaselas a mi colega, la boticaria Zinge. La encontrarás en nuestro puesto de mando, en Entrañas.",
    ["O"] = "Lleva las muestras de corazón de lobo a la boticaria Zinge en Entrañas.",
    ["T"] = "Una entrega de Zinge",
  },
  [1360] = {
    ["D"] = "Tenía un trabajo estupendo en la excavación de Uldaman en las Tierras Inhóspitas; todo iba bien hasta que aparecieron los malditos troggs. Escapé por los pelos y con lo puesto. Me dejé mi posesión más preciada. Está en mi cofre, a salvo de los troggs, pero claro, está allí.$B$B¡Te agradecería mucho que me lo trajeras! Está al fondo de la Sala Comunal Norte; tiene mi nombre. Anda, ve.",
    ["O"] = "Recoge la posesión más preciada de Krom Brazorrecio de su cofre, que está en la Sala Comunal Norte de Uldaman, y llévasela a Forjaz.",
    ["T"] = "Los tesoros reclamados",
  },
  [1361] = {
    ["D"] = "Un guerrero de la Horda, Regthar Portamorta, está reclutando agentes para una misión en la tierra de Desolace. Habla con él si quieres unirte a los voluntarios. Se encuentra en Los Baldíos, en una serie de búnkeres que hay al oeste de El Cruce.$B$BY afila bien tus armas. Desolace está en guerra.",
    ["O"] = "Habla con Regthar Portamorta.",
    ["T"] = "Regthar Portamorta",
  },
  [1362] = {
    ["D"] = "Los centauros Kolkar viven en Desolace y sus fuerzas se han extendido hasta Los Baldíos. Es un hecho. Por eso tenemos agentes de la Horda lidiando con ellos allí.$B$BPero mientras los centauros conserven su fuerza en Desolace, seguirán siendo una amenaza. Es hora de que nos deshagamos de esa amenaza.$B$BHabla con Felgur Doscortes. Está en el Campamento del Espíritu Errante, en Desolace. Fue enviado a reunir información sobre los centauros. Él sabrá cuál es la mejor manera de tratar con los Kolkar de Desolace.",
    ["O"] = "Habla con Felgur Doscortes. Está en el Campamento del Espíritu Errante, en Desolace.",
    ["T"] = "Los Kolkar de Desolace",
  },
  [1363] = {
    ["D"] = "Cuido de los libros que tenemos aquí, en la biblioteca de la Academia de Artes y Ciencias Arcanas y es mi deber asegurar que todos los tomos y pergaminos mágicos están registrados en todo momento. ¿Quién sabe qué podría ocurrir si alguno de estos textos encantados cayese en las manos equivocadas?$B$BLo que me recuerda... Mi ayudante, Dellis, cometió un terrible error la semana pasada. Es bastante competente en la biblioteca, pero un gran incompetente en cuanto sale por las puertas de Ventormenta. Sé $Gun héroe:una heroína; y ayuda a Dellis, ¿lo harás?",
    ["O"] = "Habla con el acólito Dellis en Ventormenta.",
    ["T"] = "La orden de Mazen",
  },
  [1366] = {
    ["D"] = "Ahora que has asustado a los clanes de centauros, es hora de sangrarlos. Cuando viajes por Desolace, caza centauros. De cualquier clan, no me importa. Pero recolecta orejas. Una horda de orejas.$B$BReduciendo su número reducirás su determinación y borrarás de su cabeza cualquier plan de expansión.$B$BBuena caza, $N.",
    ["O"] = "Lleva 15 orejas de centauro a Felgur Doscortes a Desolace.",
    ["T"] = "Colección de orejas",
  },
  [1370] = {
    ["D"] = "Cuando no estamos combatiendo, cazamos para conseguir carne. La carne nos hace fuertes. Y un estómago satisfecho combate mejor que uno vacío.$B$BLo mismo ocurre con los Magram. Almacenan carne para poder combatir más tiempo seguido sin tener que ir a cazar. Si asaltas su poblado del este y les robas su reserva de carne, les fallarán las fuerzas.",
    ["O"] = "Lleva 6 bolsas de carne secada de forma rudimentaria a Uthek la Sabia al Poblado Gelkis.",
    ["T"] = "¡A robar provisiones!",
  },
  [1371] = {
    ["D"] = "Hace mucho tiempo, llegó un pequeño gnomo a Desolace. Tenía caballos y una carreta y en la carreta llevaba cosas muy extrañas. Cosas divertidas. Cosas que se quemaban con humo de diferentes colores. Cosas que se movían aunque no estaban vivas. Yo maté al pequeño gnomo, pero antes de eso... me hizo reír.$B$BY me quedé lo que más me gustó de su carreta. Era un humano hecho de madera que me saludaba hasta que un día le pegué y se rompió.$B$BQuiero otro. ¡Consígueme uno nuevo!",
    ["O"] = "Mantén tu reputación con los Magram y lleva 1 muñeco-diana avanzado a Warug al Poblado Magram de Desolace.",
    ["T"] = "Un cacharro para Warug",
  },
  [1375] = {
    ["D"] = "Tú ayudas a los Magram. Me gustas. Ahora, ¡mata al Khan de los Gelkis! ¡Quiero la cabeza de Khan Shaka! ¡Quiero mirarle a la cara y reírme de él!",
    ["O"] = "Mantén tu reputación con los Magram y lleva la cabeza de Khan Shaka a Warug al Poblado Magram de Desolace.",
    ["T"] = "Khan Shaka",
  },
  [1380] = {
    ["D"] = "$N, nos has ayudado a combatir a los Magram. ¡Ayúdanos ahora contra los Maraudine! Mata al jefe del clan, el khan Hratha.$B$BPara enfrentarte a él, toca el cuerno de guerra Maraudine. Está en la cima de un alto risco, en el Valle de las Lanzas.$B$BPero para tocar el cuerno, debes conseguir una boquilla. Es un objeto que solo poseen los centauros Maraudine de mayor confianza.$B$BCuando hayas tocado el cuerno, los centauros irán acudiendo a su llamada. Ve matándolos hasta que aparezca Hratha.$B$BEl khan Hratha lleva algo encima... un fragmento de una llave. Tráemelo...",
    ["O"] = "Mantén tu reputación con los Gelkis y lleva el trozo de llave Maraudine a Uthek la Sabia al Poblado Gelkis.",
    ["T"] = "Khan Hratha",
  },
  [1381] = {
    ["D"] = "El khan Gelkis está muerto. ¡Y ahora caerán los Maraudine! ¡Mata a Hratha, el khan Maraudine!$B$BPara enfrentarte a él, toca el cuerno que hay en la cima de un risco del Valle de las Lanzas. Para utilizar el cuerno necesitas una boquilla. Y para conseguir la boquilla, tendrás que matar algunos centauros Maraudine. Ve matándolos hasta que encuentres uno que tenga una boquilla. Y entonces, úsala para soplar el cuerno.$B$BCuando lo hagas, los centauros Maraudine se dirigirán a la torre. Ve matándolos hasta que aparezca Khan Hratha. Hratha tiene un fragmento de una llave. Tráemelo...",
    ["O"] = "Mantén tu reputación con los Magram y lleva el trozo de llave Maraudine a Warug al Poblado Magram de Desolace.",
    ["T"] = "Khan Hratha",
  },
  [1383] = {
    ["D"] = "Conozco el suero perfecto. Trata la verdad justo como la verdad debe ser tratada.$B$BPara preparar el brebaje necesitaré varios corazones de pantera de las Sombras del pantano. También necesito unos hongos encantados del Lord fangoso que vive allí. Seguro que alguien con tu habilidad, $N, podrá convencerle de que le dé algunos hongos.$B$BEl último objeto que necesitaré para crear el suero es sangre de una cría soñadora, como las que habitan a la entrada de la cueva de Itharius, en el pantano.$B$BHala, ¡en marcha!",
    ["O"] = "El boticario Faustin del Refugio de los Mendigos necesita 5 corazones de pantera sombría, hongos de Señor del Lodo y sangre de cría verde.",
    ["T"] = "El suero de la verdad",
  },
  [1384] = {
    ["D"] = "Los centauros Kolkar creen que su magia es poderosa. ¡Pero no saben nada de magia! ¡No reconocerían la magia ni aunque llevara un cartel!$B$BTienes que demostrarles que su magia es débil. Ve al noreste y mata a los Kolkar. Coge sus talismanes. Tráemelos. ¡Yo los tiraré al fuego y me echaré unas risas!",
    ["O"] = "Mantén tu reputación con los Gelkis y lleva 10 talismanes rudimentarios a Uthek la Sabia al Poblado Gelkis de Desolace.",
    ["T"] = "Asalta a los Kolkar",
  },
  [1386] = {
    ["D"] = "Los Kolkar creen que son fuertes, ¡pero nosotros nos reímos de ellos! ¡Nos reímos y los matamos! Tú también tienes que matarlos. Demuéstrame que puedes matar a un Kolkar como nosotros. Ve al norte ¡y mátalos a todos!",
    ["O"] = "Mantén tu reputación con los Magram. Mata a 12 centauros Kolkar, 12 exploradores Kolkar y 6 destructores Kolkar y luego busca a Warug en el Poblado Magram en Desolace.",
    ["T"] = "Ataca a los Kolkar",
  },
  [1387] = {
    ["D"] = "La raza de los centauros está dividida en muchos clanes. Cada uno tiene su propio jefe y sus objetivos. Pero todos tienen algo en común: su crueldad.$B$BSi queremos conocer la paz, debemos eliminar a los centauros. Y precisamente, eso es lo que quiero que hagas.$B$BMientras viajes por Desolace, da caza a los centauros y córtales las orejas. Tráeme muchas, muchas orejas.$B$BHazlo por la Alianza, $N, y estaremos un paso más cerca del final de la guerra.",
    ["O"] = "Lleva 15 orejas de centauro al cabo Melkins que está en Punta de Nijel, en Desolace.",
    ["T"] = "Colección de orejas",
  },
  [1388] = {
    ["D"] = "Todo procede según el plan, $N. Quiero que le entregues este suero de la verdad a Zraedus el mortacechador. Él te indicará cómo administrarlo correctamente.$B$B¡Ja, ja!",
    ["O"] = "Dale el suero de la verdad de Faustin al mortacechador Zraedus en el Refugio de los Mendigos.",
    ["T"] = "El suero de la verdad",
  },
  [1389] = {
    ["D"] = "Los Perdidos que habitan en Retiro Fangoso son como nosotros en apariencia pero, en realidad, son monstruos que enloquecieron debido a los rigores sufridos durante nuestro viaje a Azeroth. En su día hermanos nuestros, viven ahora en Retiro Fangoso hacia el este, donde atrapan o matan a todo aquel que vague por la zona.$B$BAdemás, nos han robado algo sagrado para nosotros: los cristales draenetistas.$B$BCarecemos de la fuerza necesaria para aventurarnos en Retiro Fangoso y recuperar los cristales que nos han sido robados de entre sus cabañas y hogueras, pero sin ellos... quizás perdamos nosotros también nuestro camino.",
    ["O"] = "Llévale 6 cristales draenetistas a Magtoor que está en El Puerto del Pantano de las Penas.",
    ["T"] = "Cristales draenetistas",
  },
  [1390] = {
    ["D"] = "Missing text",
    ["O"] = "Missing text",
    ["T"] = "&lt;nyi&gt; Ups, les hemos matado de otra vez",
  },
  [1391] = {
    ["D"] = "Tenemos que actuar con una cierta discreción. Después de todo, no puedes entrar ahí tal cual y esperar que ese loco se tome el suero de Faustin sin más, ¿no te parece?$B$BPero si hay una cosa a la que los humanos no se resisten es... la bebida. Voy a mezclar el suero de Faustin con esta cerveza. Te garantizo que ese infeliz se beberá la cerveza sin pensárselo dos veces.$B$BCorre hasta Rocal, $N. Recuerda que está en el Pantano de las Penas. Y mantén nuestra misión en secreto...",
    ["O"] = "Lleva el brebaje de Zraedus al infiltrado Marksen a Rocal.",
    ["T"] = "El suero de la verdad",
  },
  [1392] = {
    ["D"] = "Noboru ha asediado a los draenei de El Puerto durante meses y su garrote es un símbolo de la brutalidad sufrida a manos de Los Perdidos.",
    ["O"] = "Llévale el garrote de Noboru al líder de El Puerto del Pantano de las Penas.",
    ["T"] = "Noboru el Garrote",
  },
  [1394] = {
    ["D"] = "Lo has hecho muy bien, $N. Has superado mi prueba y todas las anteriores. Vuelve a ver a Dorn a Las Mil Agujas. Podrá ver los cambios que has experimentado y comprobará que tu mente, tu cuerpo y tu espíritu son lo suficientemente fuertes, por lo que te recompensará por tus esfuerzos.$B$BPermanece fiel al equilibrio que has demostrado a lo largo de estas pruebas, $N. Dorn te pondrá a prueba más adelante si así lo haces.",
    ["O"] = "Habla con Dorn Acechallanos en Las Mil Agujas.",
    ["T"] = "La recta final",
  },
  [1395] = {
    ["D"] = "Hay que llevar esta caja de piedras de potencia al Castillo de Nethergarde, en Las Tierras Devastadas. No conservarán la potencia mucho tiempo, así que no podemos esperar a la siguiente caravana.$B$BTienes que llevarlas, y debes hacerlo si perder ni un minuto.$B$BLlévaselas al intendente Lungertz en el Castillo de Nethergarde. Para llegar al castillo, debes dirigirte al Paso de la Muerte, luego ir hacia el este a través del Pantano de las Penas, y por último cruzar Las Tierras Devastadas.$B$BTen mucha precaución, $N. Pasarás por tierras muy peligrosas y no puedes fracasar.",
    ["O"] = "Llévale caja de piedras de potencia al intendente Lungertz antes de que caduquen.",
    ["T"] = "Suministros para Nethergarde",
  },
  [1396] = {
    ["D"] = "Mi caravana iba destinada a la costa oriental pero no alcanzamos nuestro objetivo. Los Perdidos nos atacaron: mataron a los guardias y destrozaron los suministros.$B$BTan solo yo sobreviví a su ataque. He jurado proteger los suministros restantes pero hay criaturas salvajes vagando por aquí. ¡Por favor, ayúdame a defender mi campamento de las bestias!",
    ["O"] = "Mata a 8 crocoliscos Denteserra jóvenes, 10 hilapenas y 10 jaguares del pantano, y después ve a ver al vigía Biggs al Pantano de las Penas.",
    ["T"] = "Contra la fauna feroz",
  },
  [1398] = {
    ["D"] = "Te debo mucho, $N. En tu interior late un corazón noble. Y ahora tengo otra tarea para la que humildemente pido tu ayuda.$B$BSi he de llegar hasta Nethergarde, tendré que arreglar este carro. Y para ello... necesitaré madera. Madera fuerte y seca, no como la madera esponjosa de los árboles del pantano.$B$B$N, ve hacia el este hasta llegar a la costa y busca madera que haya sido arrastrada por el mar y secada por el sol. Cuando la tengas, tráemela.",
    ["O"] = "Llévale 8 piezas de madera a la deriva desecada al vigilante Biggs en el Pantano de las Penas.",
    ["T"] = "Madera a la deriva",
  },
  [1418] = {
    ["D"] = "Las Tierras Inhóspitas son escabrosas y están situadas cerca de las tierras de Loch Modan dominadas por la Alianza, por lo que la comunicación entre Rocal y la avanzada de Kargath de las Tierras Inhóspitas en ocasiones se corta.$B$BDemuestra tu valía, $N. Viaja a las Tierras Inhóspitas y habla con Neeka Marca de Sangre, la jefa de exploradores la avanzada de Kargath. Habla con ella y regresa para contarme lo que averigües.",
    ["O"] = "Habla con Neeka Marca de Sangre.",
    ["T"] = "Neeka Marca de Sangre",
  },
  [1419] = {
    ["D"] = "¿Has estado muchos días en las Tierras Inhóspitas, $C? ¿Has sentido el calor que hace allí? ¿Te has topado con las bestias que vagan por sus áridas llanuras? Pues es hora de que lo hagas.$B$BLos coyotes monteses son muy fuertes. Fuertes y lo suficientemente audaces para acercarse con sigilo a Kargath y robar las provisiones de comida. ¡Ayuda a la avanzada a deshacerse de estos ladrones!$B$BCaza a los coyotes monteses, $N, y tráeme sus mandíbulas. Si me traes bastantes quizás te crea cuando afirmas que conoces bien las Tierras Inhóspitas.",
    ["O"] = "Llévale 30 mandíbulas de coyote a Neeka Marca de Sangre, que está en Kargath.",
    ["T"] = "Ladrones coyote",
  },
  [1420] = {
    ["D"] = "He redactado un nuevo informe para Helgrum, que está en Rocal, y quiero que se lo lleves.$B$BSi te consideras $Gun buen avanzado:una buena avanzada;, entonces no te pierdas por el camino ni dejes que te atrape el enemigo.",
    ["O"] = "Llévale el informe de Neeka a Helgrum el Veloz, que está en Rocal.",
    ["T"] = "Un informe para Helgrum",
  },
  [1421] = {
    ["D"] = "Yo perdí el sentido durante el ataque de Los Perdidos pero, antes de que mi vista se nublara, vi cómo uno de nuestros carros era conducido hasta Retiro Fangoso, la ciudad perdida situada al norte.$B$BUno de los cofres de la caravana que contenía componentes muy importantes iba en ese carro, unos componentes esenciales para que los zahoríes del Castillo de Nethergarde puedan continuar con sus estudios. Encuentra ese cofre y recupera los componentes, $N. Consíguelos y tráemelos aquí.",
    ["O"] = "Encuentra el cofre de la caravana en Retiro Fangoso, consigue los componentes de zahorí y llévaselos al vigía Biggs.",
    ["T"] = "La caravana perdida",
  },
  [1424] = {
    ["D"] = "El Jefe de Guerra nos envió a investigar los extraños sucesos que están ocurriendo en El Templo de Atal\'Hakkar.$B$BLos trols Atal\'ai son una tribu de magos oscuros, famosos por practicar la magia prohibida. Te puedes imaginar lo sorprendidos que nos quedamos cuando llegamos y descubrimos que el templo estaba sumergido.$B$BAhora, los dragones Verdes protegen el lugar, erigiéndose como protectores de Azeroth.$B$BNecesitamos descubrir qué ha pasado. Explora la Charca de Lágrimas y consigue artefactos Atal\'ai. Con eso, podré empezar a unir las piezas del puzle.",
    ["O"] = "Fel\'zerul, en Rocal, quiere que consigas 5 artefactos Atal\'ai.",
    ["T"] = "Charca de Lágrimas",
  },
  [1425] = {
    ["D"] = "Mientras yo arreglo el carro, $N, tengo una última tarea que encomendarte.$B$BHe empaquetado los suministros más importantes que se salvaron del ataque de los ogros. Llévalos hasta el Castillo de Nethergarde. El castillo está enclavado en las colinas del norte de Las Tierras Devastadas y su misión es vigilar El Portal Oscuro.$B$BPara llegar a Nethergarde, sigue el camino del este. Cuando se bifurque, ve en dirección sur hacia Las Tierras Devastadas y a continuación gira a la izquierda en el siguiente cruce. Sigue el camino, $N, y ve con cuidado.",
    ["O"] = "Entrégale los suministros al intendente Lungertz del Castillo de Nethergarde.",
    ["T"] = "Envío de suministros",
  },
  [1426] = {
    ["D"] = "No siento aprecio por el mar ni por ninguna de las criaturas que he visto emerger de sus profundidades. Es una entidad poderosa pero ciega, a la vez cruel y brutal. Sería un placer para mí dar muerte a los hombres-pez de cien en cien si estuviera en mi mano pero he de conformarme con ver cómo caen ante aquellos que cumplen la voluntad de la Horda.$B$BLo cual nos lleva a ti, $C. ¿Cuántos serías capaz de matar? ¿Te atreverías a ponerte a prueba ante los más poderosos de su especie?",
    ["O"] = "Mata a 10 múrlocs de marisma, 10 vomitatintas de marisma y 10 comecarnes de marisma, y después ve a ver a Katar al Pantano de las Penas.",
    ["T"] = "La amenaza marítima",
  },
  [1427] = {
    ["D"] = "Antes de tomarte un descanso, habla de nuevo con Tok\'Kar. Durante tu ausencia creo que ha rematado algunas armas y te ofrecerá una de ellas. Harías bien en probar su fuerza contra los hombres-pez cuando sientas que estás $Gpreparado:preparada;... pero solamente si deseas complacerme aún más.",
    ["O"] = "Habla con Tok\'Kar en el Pantano de las Penas.",
    ["T"] = "La amenaza marítima",
  },
  [1428] = {
    ["D"] = "Me complace comprobar que tu odio por los hombres-pez crece con el mío.$B$BBúscalos a lo largo de la playa y cuando te hayas hartado de combatir, vuelve a verme. Entonces hablaremos de una recompensa por tu diligencia.",
    ["O"] = "Mata a 10 vomitatintas de marisma, 10 comecarnes de marisma y 10 oráculos de marisma, y después ve a ver a Katar al Pantano de las Penas.",
    ["T"] = "La amenaza continua",
  },
  [1431] = {
    ["D"] = "Guarda silencio, $C, y deja que te hable de algo que tiene mucha importancia para mi gente.$B$BSomos orgullosos y la guerra con los humanos ha impacientado incluso a Thrall. Verás, nuestro Jefe de Guerra es sabio y prudente y siempre piensa primero en su pueblo. También es diplomático y aprendió mucho de los humanos cuando estuvo con ellos.$B$BSi la historia te interesa, ve a ver a Keldran, que está cerca de la salida oeste de la ciudad.",
    ["O"] = "Habla con Keldran en Orgrimmar.",
    ["T"] = "Las relaciones de la Alianza",
  },
  [1432] = {
    ["D"] = "A medida que la Alianza avanza en Kalimdor, podría extenderse la errónea creencia de que la Horda ha pactado con la Legión Ardiente. El Jefe de Guerra no puede permitirlo y me ha autorizado a atajar estas creencias erróneas.$B$BTengo agentes en la región de Desolace, en el Campamento del Espíritu Errante que está al norte del Cementerio de Kodos. Para encontrarlo, ve hacia el sur desde La Vega Carbonizada, en Sierra Espolón. Cuando llegues, busca a Takata Hoja de Acero.",
    ["O"] = "Habla con Takata Hoja de Acero en Desolace.",
    ["T"] = "Las relaciones de la Alianza",
  },
  [1434] = {
    ["D"] = "Las infestaciones demoníacas no ocurren por sí solas; alguna otra fuerza les permite establecerse.$B$BPuede ser un espíritu debilitado, el ansia injustificada de poder, un deseo de venganza o de justicia. Cuando nos atenaza el dolor, el mal aprovecha su oportunidad con seres como el sátiro.$B$BLos sátiros han pactado con el Filo Ardiente y Thrall quiere eliminarlos. Los encontrarás hacia el noreste. Vuelve cuando los hayas expulsado.",
    ["O"] = "Mata a 7 pícaros, 7 jurapenas, 7 traidores y 7 clamainfernos Odio Feroz y ve a ver a Takata Hoja de Acero a Desolace.",
    ["T"] = "La contaminación del sátiro",
  },
  [1436] = {
    ["D"] = "Esto va bien, $N. Si la lucha contra los demonios y sus huestes sigue yendo así, puede que el Jefe de Guerra nos deje volver a casa antes de que cambie la estación.$B$BVe a ver a Keldran y dile lo que hemos hecho. Seguro que te recompensará por tu ayuda. Su causa es importante para él y seguro que se enorgullece de saber que el Jefe de Guerra lo tiene en alta estima.",
    ["O"] = "Habla con Keldran en Orgrimmar.",
    ["T"] = "Las relaciones de la Alianza",
  },
  [1437] = {
    ["D"] = "Yo soy Vahlarriel, $N. He venido aquí a descubrir el paradero de dos nobles de una familia de mercaderes de Darnassus, los Malem. El padre se teme lo peor y yo debo hacer todo lo que pueda para encontrar a sus hijos. Tengo que empezar mi búsqueda por Tyranis o Dalinda, pero espero no estar aquí demasiado.$B$BLo último que se sabe es que cruzaron la Sierra Espolón hace pocas semanas.$B$BSi encuentras su carro, por favor, ven a buscarme de inmediato.",
    ["O"] = "Busca y registra el carro de Tyranis y Dalinda Malem en Desolace.",
    ["T"] = "Buscando para Vahlarriel",
  },
  [1438] = {
    ["D"] = "Busca a Dalinda y a Tyranis y vuelve a contarme tus progresos. Empieza la búsqueda en la fortaleza. Yo me pondré inmediatamente en contacto con mis superiores en Darnassus y les informaré de la situación.$B$B$N, hay algo más que quiero transmitirte. Mis superiores han manifestado su preocupación por los motivos que pueda tener Tyranis para venir aquí. Consideran más importante encontrar a Dalinda y ponerla a salvo. Tyranis debe ser... evaluado antes de ser llevado a casa.",
    ["O"] = "Encuentra a Dalinda Malem en Desolace.",
    ["T"] = "Buscando para Vahlarriel",
  },
  [1440] = {
    ["D"] = "¿Así que ha llegado el momento? Aprecio tus esfuerzos y, por si más tarde no tengo ocasión de hacerlo, permíteme que te agradezca ahora todo lo que has hecho por mí y por mi familia. Si ya estás $Glisto:lista;... Aquí no queda nada para mí. Podemos partir en el momento que consideres adecuado.",
    ["O"] = "Escolta a Dalinda Malem fuera de la Fortaleza del Hacha de Trueno y vuelve a Desolace a hablar con Vahlarriel Destripademonios.",
    ["T"] = "Regresa junto a Vahlarriel",
  },
  [1441] = "_",
  [1442] = {
    ["D"] = "Saludos, $N. ¿Cómo puedo ayudar a quien sigue una senda sagrada?$B$BAh, sí, Fontana, el herrero... Cuánto tiempo. Lo recuerdo bien, aunque hacía tiempo que no oía hablar de él.$B$BNo lo tengo por avaricioso, luego su deseo de tener una gema Kor solo puede tener un motivo noble.$B$BPara encontrar una de esas gemas tendrás que ir hacia el noroeste de Vallefresno. Allí encontrarás unas ruinas antiguas y las Cavernas de Brazanegra. Allí viven los nagas que siguen usando esas gemas. No vayas por tu cuenta, $C.",
    ["O"] = "Lleva una gema Kor a Thundris Tejevientos a Costa Oscura.",
    ["T"] = "La búsqueda de la gema Kor",
  },
  [1444] = {
    ["D"] = "Jammal\'an el Profeta me condenó al exilio de El Templo de Atal\'Hakkar. Antiguamente creía que el sumo sacerdote era un buen líder.$B$BSu implacable fe en su profecía llevará a la destrucción de los Atal\'ai. Si es que todavía no lo ha hecho.$B$BCuando hablé sobre mis preocupaciones, fui considerado un infiel y me expulsaron del Templo.$B$BEsos artefactos que me has traído son inofensivos. Pero si Jammal\'an ha descubierto el encantamiento fetiche correcto para el ritual de invocación, el mundo corre un grave peligro...",
    ["O"] = "Vuelve a ver a Fel\'Zerul en Rocal.",
    ["T"] = "Regresa junto a Fel\'Zerul",
  },
  [1445] = {
    ["D"] = "¿Así que habló de la profecía de Jammal\'an?$B$BEl líder espiritual de los Atal\'ai cree que cuando Hakkar vuelva a Azeroth desde El Vacío Abisal, el dios concederá la inmortalidad a la tribu Atal\'ai.$B$BArtimañas estúpidas para conseguir una muerte temprana, si me lo preguntas.$B$B¿Pero dices que el exiliado habló sobre unos fetiches encantados? Eso me preocupa. Si esos fetiches son, realmente, la llave del ritual Atal\'ai que ha causado todo este caos, debemos comprender los poderes intrínsecos.$B$BVete al Templo y consigue los fetiches.",
    ["O"] = "Reúne 20 fetiches de Hakkar y llévaselos a Fel\'Zerul en Rocal.",
    ["T"] = "El Templo de Atal\'Hakkar",
  },
  [1446] = {
    ["D"] = "A mis compañeros Secacorteza no les gustan los extraños errantes.$B$B¿Yo? Yo ya no tengo hogar. Vivo en el exilio, lejos de mi gente, los Atal\'ai del Pantano de las Penas.$B$BNuestro líder espiritual, Jammal\'an, tuvo lo que él llama una visión. Cree que invocar al dios Hakkar traerá la inmortalidad a los Atal\'ai.$B$BPero yo pedí prudencia. ¿Y si la visión no es más que manipulación?$B$BQuiero vengarme por estar en el exilio. Tráeme la cabeza de Jammal\'an. A lo mejor así mi gente podrá ser libre.",
    ["O"] = "El exiliado Atal\'ai, en las Tierras del Interior, quiere la cabeza de Jammal\'an.",
    ["T"] = "Jammal\'an el Profeta",
  },
  [1447] = {
    ["D"] = "Sí, tá claro que no eres $Gel:la; $C más inteligente que ha venido a Ventormenta, pero tás a punto de ser $Guno:una; de $Glos:las; más torpes que han salido de aquí.$B$B¿Conoces a mis amigos?",
    ["O"] = "Derrota a Dashel Puñopiedra en Ventormenta.",
    ["T"] = "El diplomático desaparecido",
  },
  [1448] = {
    ["D"] = "Hace tiempo, yo era joven y rebosaba vigor como tú, $C.$B$BPero ahora soy mayor e incapaz de explorar el mundo como hice antaño. ¡Ah, las cosas que vi!$B$BPero un misterio me eludió siempre. En mi última misión, la Liga de Expedicionarios me envió a buscar El Templo de Atal\'Hakkar. Se decía que estaba situado en el Pantano de las Penas.$B$BDebido a mi delicado estado, viajé surcando los cielos sobre un grifo buscándolo, pero no conseguí encontrarlo.$B$B¿Ayudarías a un viejo enano? Quizás tengas más suerte que yo si vas a pie.",
    ["O"] = "Brohann Barriliga, en Ventormenta, quiere que busques El Templo de Atal\'Hakkar.",
    ["T"] = "En búsqueda del templo",
  },
  [1450] = {
    ["D"] = "Así que eres $Gel chico:la chica; de los recados de algún vejete de la Liga de Expedicionarios, ¿eh?$B$BBueno, pues escúchame bien, $C. No sé con quién volaba por Azeroth tu viejo amigo. ¿Acaso te parezco el maestro de grifos Garracha? ¿Eh?$B$BSi tanto te interesan las asignaciones de vuelo, ¿por qué no le preguntas al maestro de grifos? Si te pillo haciendo el vago por aquí mucho más tiempo, voy a ponerte a trabajar.$B$BY ahora, lárgate.",
    ["O"] = "Habla con el maestro de grifos Garracha dentro de la pajarera del Pico Nidal.",
    ["T"] = "Maestro de grifos Garracha",
  },
  [1452] = {
    ["D"] = "¡Hip!$B$B¿Así que te ha mandao ese loco de Brohann? Pedazo de aventura que tuvimos... ¡Hip!$B$BSi quieres que te cuente lo que pasó allí en el pantano, voy a necesitar beber algo fuerte. No fue nada bonito, ¿sabes? ¡Hip!$B$BMira... Tráeme lo que necesito pa hacer un cubata de Kalimdor y te lo contaré.$B$BVoy a necesitar mollejas de roc de Tanaris y... veamos... algunos hígados de Groddoc e hígados de Cueracero de Feralas...$B$BUn buen cubata de Kalimdor... ¡Con eso hablaré!",
    ["O"] = "Rapsodio Machacacanillas, en las Tierras del Interior, quiere que le lleves 3 mollejas de roc, 3 hígados de Groddoc y 3 hígados de Cueracero.",
    ["T"] = "El cubata de Kalimdor de Rapsodio",
  },
  [1453] = {
    ["D"] = "Hay mucho que hacer en Desolace, $N. Es un lugar atroz, mancillado por centauros y otros seres desagradables. Pero si buscas un trabajito honrado, ve, busca a Kreldig Ungor y dile que vas de mi parte.$B$BEstará en Punta de Nijel, un campamento pequeño oculto en las montañas del norte de Desolace. Si entras por la Sierra Espolón, ve hacia el este. Es difícil de ver, así que estate pendiente.",
    ["O"] = "Habla con Kreldig Ungor en Desolace.",
    ["T"] = "Los negocios de los Recuperadores en Desolace",
  },
  [1454] = {
    ["D"] = "Saludos, $Gamigo:amiga;. Siéntate...$B$BMi primer encargo es para el mago Karnitol, una especie de noble de Ventormenta. Por lo visto, su barco fue atacado por gigantes marinos cuando navegaba al noroeste de Desolace. Él y sus sirvientes intentaron llevar a la costa uno de sus cofres más valiosos, pero cuando los gigantes marinos atacaron en manada, decidió abandonarlo y teletransportarse con sus sirvientes.$B$BQuiere que encontremos su cofre y su contenido y que se lo llevemos a Ventormenta.",
    ["O"] = "Encuentra el cofre de Karnitol en Desolace.",
    ["T"] = "El naufragio de Karnitol",
  },
  [1456] = {
    ["D"] = "Intrigante.$B$BBueno, puede que aún no debamos perder la esperanza.$B$BA lo largo de esa misma costa pero hacia el norte, cerca de la Isla Ranazjar, en el fondo del mar, hay unas ruinas de los elfos de la noche. Una tribu de nagas, los Filozante, han hecho de ellas su hogar. Creo que es nuestra mejor baza.$B$BSi una partida de exploradores llegó tan al sur, debía de ser bastante poderosa. Si quieres encontrar las pertenencias de Karnitol, yo empezaría con los nagas más poderosos: los cazamareas o las brujas de mar.",
    ["O"] = "Encuentra la cartera de Karnitol y devuélvesela a Kreldig Ungor, que está en Desolace.",
    ["T"] = "El naufragio de Karnitol",
  },
  [1457] = {
    ["D"] = "Lleva las cosas de Karnitol a Forjaz. Dáselas a Roetten y él te pagará por un trabajo bien hecho.$B$BJa, ja, si no tienes cuidado, va a estar tan contento contigo que te pedirá que te unas a nosotros a jornada completa.$B$BUna vez más, buen trabajo, $N.$B$BCuidado en el camino de regreso. Y no te entretengas mucho, Karnitol no es el más paciente de los hombres, aunque sí es muy generoso.",
    ["O"] = "Lleva la cartera de Karnitol a Roetten Rocamartillo a Forjaz.",
    ["T"] = "El naufragio de Karnitol",
  },
  [1458] = {
    ["D"] = "Uno de mis cometidos es recoger componentes para Zurdibrujo, un mago gnomo de Kharanos. Roetten parece pensar que es fácil porque ya estamos aquí. Y no le preocupa que nos falte gente.$B$BLos primeros artículos de la lista deberían ser fáciles de encontrar. Y habría una buena recompensa para quien me ayudara.$B$BNecesito algunos cuernos y patas de los sátiros Odio Feroz que están al este de aquí. Tráemelos y seguiremos con el resto de la lista.",
    ["O"] = "Lleva 10 zarpas de Odio Feroz y 10 cuernos de Odio Feroz a Kreldig Ungor a Desolace.",
    ["T"] = "Componentes para Recuperadores S.A.",
  },
  [1459] = {
    ["D"] = "Lo siguiente de la lista de Zurdibrujo es... veamos... mmm. Ah, aquí está.$B$BEs simple. Suele ser muy explícito cuando da instrucciones. Necesita veneno de scorpashi y el pellejo de algún kodo. La letra pequeña dice que ambas cosas deben ser de criaturas específicas. El veneno tiene que venir de cualquiera de los scorpashi y el pellejo tiene que ser de un kodo envejecido, no de uno anciano o moribundo.$B$B¡Magos! De verdad, a veces piden unas cosas...",
    ["O"] = "Lleva 7 viales de veneno de Scorpashi y 3 pellejos de kodo envejecido a Kreldig Ungor a Desolace.",
    ["T"] = "Componentes para Recuperadores S.A.",
  },
  [1460] = "_",
  [1461] = "_",
  [1462] = {
    ["D"] = "",
    ["O"] = "Pide otra sapta de tierra a la vidente Pluma de Cuervo.",
    ["T"] = "Sapta de tierra",
  },
  [1463] = {
    ["D"] = "",
    ["O"] = "Pide a Canaga Clamatierras otra sapta de tierra.",
    ["T"] = "Sapta de tierra",
  },
  [1464] = {
    ["D"] = "",
    ["O"] = "Habla con Telf Joolam para conseguir otra sapta de fuego.",
    ["T"] = "Sapta de fuego",
  },
  [1465] = {
    ["D"] = "Cuando estudias el carro encuentras un colgante y huellas que se dirigen al sur. En la distancia puedes ver humo saliendo de un gran fuerte. El humo proviene de un fuego controlado, no de algún edificio incendiado y tu instinto te dice que, si los Malem aún están vivos, fueron llevados en esa dirección.$B$BCrees que Vahlarriel, que está en la Punta de Nijel, debería ver el colgante y también saber que has visto signos de vida al sur.",
    ["O"] = "Lleva el colgante Malem a Vahlarriel Destripademonios a Desolace.",
    ["T"] = "Buscando para Vahlarriel",
  },
  [1466] = {
    ["D"] = "Estos son los últimos objetos y, no te voy a mentir, no es un grupito agradable. Además, creo que te llevará más tiempo conseguir estos objetos que los demás.$B$BBueno, basta de charla introductoria, vamos al grano. Zurdibrujo dice que necesita: diez cerebros de devoradores de maná, de cazadores de magos o de cazadores Ley. Diez alas infernales de una doncella, hermana o hechicera abisal. Y... diez frascos de sangre de un guardiavil, que tiene que ser capitán o señor.$B$BSencillito, ¿eh? Oyee... si prefieres pasar, lo entiendo.",
    ["O"] = "Lleva 10 cerebros de can manáfago, 10 alas abisales y 10 viales de sangre de guardiavil a Kreldig Ungor a Desolace.",
    ["T"] = "Componentes para Recuperadores S.A.",
  },
  [1467] = {
    ["D"] = "Si quieres que te paguen, tendrás que volver a Forjaz y hablar con Roetten en persona. Estará encantado de quitar de en medio el paquete de Zurdibrujo. Tú dale esto. Debería bastar para conseguir tu recompensa.$B$BY no te preocupes, hablaré bien de ti cuando vuelva. No debería quedarme mucho más en Desolace.",
    ["O"] = "Lleva el paquete de Zurdibrujo a Roetten Rocamartillo a Forjaz.",
    ["T"] = "Componentes para Recuperadores S.A.",
  },
  [1468] = {
    ["D"] = "¿Quieres ayudarnos aquí en el orfanato? $GBendito:Bendita; seas, $Gamigo:amiga;. Tu ayuda durante la Semana de los Niños no tiene precio.$B$BCoge este silbato. Solo podrás utilizarlo durante un breve período de tiempo, básicamente, hasta que se acabe la Semana de los Niños. Cuando lo uses, podrás hablar con el niño que esté a tu cuidado. Utilízalo de nuevo para que el niño se vaya.$B$BSea como sea, $N, utiliza el silbato y conoce a tu pupilo. Seguro que el niño estará encantado de conocerte.",
    ["O"] = "Usa el silbato de huérfano humano para hablar con el niño al que cuidarás durante la Semana de los Niños.",
    ["T"] = "Semana de los Niños",
  },
  [1469] = {
    ["D"] = "Antes del vuelo, me bebí un barril entero de cerveza negra. ¡Hip!$B$B¡Y menudo pedazo de vuelo! De un lao pa otro con Brohann siguiéndome. Aunque no vimos ni un templo. ¡Hip!$B$BBueno, yo no vi ni un templo hasta que me puse un poco achispao y me caí de la montura. Caí justo en el agua y me arrastró pa\'bajo. Me encontré rodeao de un montón de trols muy feos. ¡Salí de allí pitando! Incluso conseguí escapar con este souvenir trol. Toma, llévaselo a Brohann y dile que su templo tá ahí abajo. ¡Hip!",
    ["O"] = "Lleva el fragmento de la tablilla Atal\'ai a Brohann Barriliga, en Ventormenta.",
    ["T"] = "El relato de Rapsodio",
  },
  [1470] = {
    ["D"] = "Buenas, criatura. Veo que aún no has desarrollado del todo los poderes de los brujos. Pero siento que tienes un gran deseo de aprender.$B$BY aprenderás.$B$BNo hay fuerza en la ignorancia. El conocimiento es nuestro mayor poder. Aplicando el conocimiento podemos controlar las magias y los seres del vacío y atarlos a nuestra voluntad.$B$BTe enseñaré a atar a un siervo a tu voluntad. Pero primero tienes que traerme tres calaveras de los esqueletos armadóseos que hay cerca de la herrería abandonada.",
    ["O"] = "Lleva 3 calaveras de armadóseos a Venya Marthand.",
    ["T"] = "Descubrir el misterio",
  },
  [1473] = {
    ["D"] = "Parece que todo apunta a que estás $Gpreparado:preparada; para aprender a invocar a un abisario.$B$BSe trata de un ser creado a partir del caos y, si lo invocas pero eres incapaz de dominarlo, será tu fin.$B$BEgalin fue el primero en invocar a un abisario, y explica el proceso en su grimorio.$B$BDesgraciadamente, nos lo robó un agente de la Cruzada Escarlata y ahora está escondido en la base que tienen en una torre hacia el oeste. Tráemelo y compartiré ese conocimiento contigo.",
    ["O"] = "Recupera el grimorio de Egalin y dáselo a Carendin Halgar, que está en el Templo de los Condenados.",
    ["T"] = "Criatura Abisal",
  },
  [1474] = {
    ["D"] = "Usando estos corazones, lanza un hechizo al Círculo de Invocación para abrir una puerta por la que el súcubo pueda entrar. Tus poderes son enormes ahora, pero los necesitarás todos para dominar al súcubo.$B$BTen cuidado con sus encantos, $N, muchas mentes regias han sucumbido a su voluntad. Cuando lo hayas dominado, el súcubo volverá al vacío, y entonces te enseñaré el hechizo para invocarlo a tu antojo en el futuro.",
    ["O"] = "Utilizando los corazones puros, invoca y domina a un súcubo, y después devuélvele los corazones puros a Carendin Halgar, que está en el Barrio de la Magia de Entrañas.",
    ["T"] = "El vínculo",
  },
  [1475] = {
    ["D"] = "Según la leyenda, El Templo de Atal\'Hakkar es un santuario sagrado dedicado al dios ancestral, Hakkar el Cazador de Almas. Está protegido por una feroz tribu de trols, los Atal\'ai, y se rumorea que el templo es impenetrable.$B$BAhora está hundido en la Charca de Lágrimas, con lo que sacar artefactos del templo es mucho más difícil. Pero eso es lo que necesito que hagas, $N.$B$BAyúdame, tráeme las tablillas de Atal\'ai intactas. Tráemelas y podré volver con los Exploradores convertido en un héroe. Juntos compartiremos la gloria.",
    ["O"] = "Consigue 10 tablillas de Atal\'ai para Brohann Barriliga, en Ventormenta.",
    ["T"] = "En El Templo de Atal\'Hakkar",
  },
  [1476] = {
    ["D"] = "¿Gente decente y de bien? ¿Existe ese tipo de personas?$B$BTras una larga investigación, recogiendo datos de historias y rumores que se oyen por ahí, he podido encontrar a dos hombres que cumplen esta condición.$B$BEl primero es Dalin Forjador, que se consagró al cuidado de los refugiados de Lordaeron, quienes fueron repudiados por los suyos en la Muralla de Cringris.$B$BEl otro es Comar Villardo, un hombre que sigue siendo fiel a su amor perdido y que vaga por Los Humedales en busca de una señal de su mujer.$B$BEntrégale sus corazones a Carendin. Pero primero, deja que te muestre una cosa.",
    ["O"] = "Llévale los corazones de Dalin Forjador y Comar Villardo a Carendin Halgar, que está en el Templo de los Condenados.",
    ["T"] = "Corazones puros",
  },
  [1477] = {
    ["D"] = "Nuestros magos están realizando una investigación en el Castillo de Nethergarde en Las Tierras Devastadas, una investigación vital para entender El Portal Oscuro. Necesitan suministros prácticamente ininterrumpidos pero nuestra línea de suministros a Nethergarde es débil.$B$BTienes que ayudarnos para que los suministros lleguen a Nethergarde. Preséntate al maestro de vigilantes Sorigal en Villa Oscura y sigue sus instrucciones. Esta tarea te llevará a tierras inhóspitas, $N. Prepárate.",
    ["O"] = "Habla con el maestro de vigilantes Sorigal en Villa Oscura, en el Bosque del Ocaso.",
    ["T"] = "Suministros de vital importancia",
  },
  [1478] = {
    ["D"] = "¡Ah, $N! Parece que Carendin Halgar desea verte para continuar con tu instrucción.$B$BDeberías sentirte muy $Gorgulloso:orgullosa;, $N. No todos los días el maestro Halgar se digna a instruir a $Gun joven brujo:una joven bruja;.$B$BLo encontrarás en el Barrio de la Magia de Entrañas, cerca del Templo de los Condenados.",
    ["O"] = "Habla con Carendin Halgar en Entrañas.",
    ["T"] = "Las invocaciones de Halgar",
  },
  [1481] = {
    ["D"] = "Creo que puedo encontrar a este señor demoníaco con los ingredientes apropiados.$B$BSi es tan poderoso como sospecho, necesitaré objetos cogidos de criaturas mortales. Pero actúas en nombre del Jefe de Guerra, así que seguro que aceptarás mi encargo.$B$BPrimero, tendré que romper las defensas que pueda tener contra la adivinación. Para ello, necesitaré la cabellera de un sátiro, pero no de cualquier sátiro. La cabellera debe proceder de un acechasombras Odio Feroz.$B$BBúscalos entre las antiguas ruinas al noreste de Desolace.",
    ["O"] = "Lleva 1 cabellera de acechasombras a Maurin Partehuesos a Desolace.",
    ["T"] = "El corruptor",
  },
  [1482] = {
    ["D"] = "Un cristal del oráculo me iría muy bien. Los fabrican los nagas para sus oráculos.$B$BA lo largo de la costa, hacia el noroeste, hay un arrecife de coral en el que se han instalado los nagas. Allí encontrarás los oráculos. Los Filozante son mortales y no van a tomarse bien tu intrusión, así que ten cuidado y date prisa.$B$BCuando vuelvas nos ocuparemos de buscar a ese Azrethoc señor demoníaco.",
    ["O"] = "Lleva 1 cristal del oráculo a Maurin Partehuesos a Desolace.",
    ["T"] = "El corruptor",
  },
  [1483] = {
    ["D"] = "Ventura y Cía. se quedó con muchos de los cerebros goblins más brillantes pero de vez en cuando, conseguimos robarle alguno.$B$BHace algún tiempo, la delegación de Ventura y Cía. en Sierra Espolón contrató a Gerenzo Silbillave, ¡un gnomo! Esto afectó tanto a su otro ingeniero, Ziz Fizziks, que me rogó que le encontrara un nuevo empleo con el sindicato de manitas. Le asigné unas tareas pero puede que necesite ayuda.$B$B¿Podrías ir a ver cómo le va por mí? Se encuentra en el extremo occidental del Risco Cortaviento.",
    ["O"] = "Habla con Ziz Fizziks en Risco Cortaviento.",
    ["T"] = "[DEPRECATED] Ziz Fizziks",
  },
  [1484] = {
    ["D"] = "Esta criatura es mucho más peligrosa de lo que esperaba. Seguro que Takata se pregunta si seré capaz de vencerla y sería un insulto a su honor el no informarle de lo que acaba de pasar.$B$BHabla con el espadachín y dile que, con su permiso, abatirás al demonio. Pero no insistas demasiado; es mejor que crea que es idea suya.",
    ["O"] = "Habla con Takata Hoja de Acero en Desolace.",
    ["T"] = "El corruptor",
  },
  [1486] = {
    ["D"] = "Cuando Naralex se hundió en su pesadilla, una extraña raza de bestias salió de debajo de Los Baldíos y se adentró en las Cuevas de los Lamentos.$B$BEstas criaturas descarriadas tienen propiedades extrañas y como de otro mundo. Aunque su naturaleza es malvada, creo que puede venir algo bueno de su presencia aquí en Kalimdor. Creo que sus pellejos serán especialmente útiles para la peletería.$B$BSi crees que estás $Gpreparado:preparada; para emprender esta tarea, aventúrate en las cuevas y hazte con algunos pellejos descarriados.",
    ["O"] = "Nalpak de las Cuevas de los Lamentos quiere 20 pellejos descarriados.",
    ["T"] = "Pellejos descarriados",
  },
  [1487] = {
    ["D"] = "Naralex tiene un fin muy noble.$B$BNuestro gran líder aspiraba a entrar en el Sueño Esmeralda y a ayudar a que estas duras tierras volvieran a convertirse en el exuberante bosque que fueron un día. Pero algo fue terriblemente mal.$B$BEl sueño de Naralex se convirtió en pesadilla y criaturas corruptas comenzaron a habitar las cavernas.$B$BMientras algunos discípulos de Naralex se esfuerzan por despertar a nuestro maestro, yo me ocupo de librar a las cuevas de esas malvadas bestias.$B$BAventúrate a las cavernas, $N, y erradica esos engendros descarriados.",
    ["O"] = "Ebru de las Cuevas de los Lamentos quiere que mates 7 devastadores descarriados, 7 víboras descarriadas, 7 arrastrapiés descarriados y 7 colminfernos descarriados.",
    ["T"] = "Erradicación de descarriados",
  },
  [1488] = {
    ["D"] = "Aunque no sé nada de ese Lord Azrethoc, coincido con Maurin en que debemos detenerlo, sobre todo si está intentando ganar influencia en Filo Ardiente.$B$BYa has demostrado tu voluntad de ayudar al Jefe de Guerra; te confío la tarea de buscar un equipo para eliminar al señor demoníaco y su siervo brujo.$B$BEncontrarás al demonio hacia el sur; vuelve cuando hayas terminado con él.",
    ["O"] = "Mata a Lord Azrethoc y a Jugkar Lug\'vara y ve a ver a Takata Hoja de Acero a Desolace.",
    ["T"] = "El corruptor",
  },
  [1489] = {
    ["D"] = "Los caparazones que me has traído están contaminados, nunca había visto algo así. Seguro que el agua de los oasis es la causa, y puedo sentir la confusión que causan en esta tierra. Es como si la tierra soñara y no pudiera despertar.$B$BLas Cuevas de los Lamentos deben de ser la fuente de la contaminación. Pero antes de ir allí habla con Hamuul Tótem de Runa, el druida anciano. Ya le hice saber lo que está ocurriendo en Los Baldíos. Es sabio y podrá ofrecerte consejo.$B$BLo encontrarás en Cima del Trueno, en el Alto de los Ancestros.",
    ["O"] = "Habla con Hamuul Tótem de Runa.",
    ["T"] = "Hamuul Tótem de Runa",
  },
  [1490] = {
    ["D"] = "Entré en el país de los sueños, y soñé con un culto de druidas que habitaba en las Cuevas de los Lamentos. Vi que sus fines fueron nobles, pero se echaron a perder. Ahora sirven a la corrupción que se está filtrando por Los Baldíos.$B$BSoy viejo y mi fuerza se desvanece. No puedo morar mucho tiempo en el país de los sueños y sé muy poco sobre los druidas corruptos. Pero Nara es joven y su espíritu implacable. Ella permaneció sumergida en la ensoñación cuando yo tuve que huir.$B$BHabla con Nara. La encontrarás en la tienda, ahí mismo.",
    ["O"] = "Habla con Nara Ferocrín.",
    ["T"] = "Nara Ferocrín",
  },
  [1491] = {
    ["D"] = "Bueno, sí, los cuernos de raptor no funcionaron muy bien. Pero no importa, ¡nadie se hace rico a la primera! Y además, ¡creo que he encontrado algo que funcionará aún mejor como ingrediente para mis bebidas de inteligencia!$B$BHay una sustancia llamada esencia de lamentos y creo que es la causa de la extraña fauna y flora de Los Baldíos. ¡Consígueme algo de esa esencia y la probaré en mi nueva bebida!$B$BHe oído que se concentra en las criaturas ectoplásmicas de las Cuevas de los Lamentos hacia el oeste. Caza a esas criaturas y recolecta la esencia.",
    ["O"] = "Reúne 6 unidades de esencia de lamentos.",
    ["T"] = "Bebidas de inteligencia",
  },
  [1492] = {
    ["D"] = "Necesito enviar una caja de pociones, componentes y otras cosas a mis socios del Viejo Mundo. Apenas tengo tiempo de llevarlos al embarcadero de Trinquete y la última caravana ya ha salido.$B$BSi puedes, lleva esto al maestro de muelles Vertipeluca. Se asegurará de que sale en el primer barco hacia Cala Aguasnegras.",
    ["O"] = "Llévale el cajón seguro al maestro de muelles Vertipeluca, en Trinquete.",
    ["T"] = "Maestro de muelles Vertipeluca",
  },
  [1498] = {
    ["D"] = "Una de las tareas más importantes de un guerrero es... la protección. Somos el escudo de la Horda y mantenemos a salvo a nuestros hermanos más débiles. Si vas a unirte a nuestras filas debes demostrarme tu entereza.$B$BVe al Monte del Trueno en Durotar, donde viven las rayopieles y los truenagartos. ¡Caza a esas bestias! ¡Demuéstrame que eres más fuerte que ellos! Resiste sus ataques y tráeme un fardo de sus escamas chamuscadas como prueba.$B$BHazlo y te instruiré.",
    ["O"] = "Lleva 5 escamas chamuscadas a Uzzek en Avanzada del Puente en Los Baldíos.",
    ["T"] = "Camino de defensa",
  },
  [1500] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Naralex ambulante",
  },
  [1501] = {
    ["D"] = "Quedan tan pocos brujos... Tienes que perfeccionar tus habilidades rápidamente si quieres ser útil a la Horda y al Jefe de Guerra.$B$BTe voy a enseñar otro hechizo de invocación.$B$BPero antes tienes que recuperar la tablilla de Vergua; la robaron unos brujos de Filo Ardiente y ahora está en su guarida de Roca del Cráneo, al este de la ciudad. La tablilla tiene las runas necesarias para invocar a un abisario.",
    ["O"] = "Lleva la tablilla de Vergua a Gan\'rul Ojosangre a Orgrimmar.",
    ["T"] = "Criatura Abisal",
  },
  [1502] = {
    ["D"] = "Es fundamental que el arma de un guerrero sea de calidad. Y si hay un orco que pueda hacer armas así es Thun\'grim Vistafuego. Es un ermitaño que vive en la cumbre de las colinas al este de El Cruce.$B$BSi buscas un arma, $Gguerrero:guerrera;, viaja hacia el Oeste, hasta El Cruce, y cuando llegues sigue hacia el Este, a las colinas. Su campamento está en la cumbre.",
    ["O"] = "Habla con Thun\'grim Vistafuego en Los Baldíos.",
    ["T"] = "Thun\'grim Vistafuego",
  },
  [1503] = {
    ["D"] = "Te daré un arma, $N, pero antes debes ayudarme. Los Crines de Acero del Noroeste, vinieron a mi campamento hace unas noches. No se enfrentarían a mí en combate, pero saben que mis ojos no son buenos... unos cuantos me distrajeron con bramidos, ¡y mientras el resto se dedicaba a robarme! ¡Cobardes!$B$BCogieron un cofre de hierro en el que había algunos lingotes de acero fundido. Si puedes recuperarlos te daré el arma que deseas.$B$BBusca la caja de hierro en los campamentos de jabaespines y vuelve con los lingotes de acero fundido.",
    ["O"] = "Lleva los lingotes de acero fundido a Thun\'grim Vistafuego en Los Baldíos.",
    ["T"] = "Acero fundido",
  },
  [1504] = {
    ["D"] = "Verás que la tablilla contiene los glifos de invocación. Llévatela al círculo de la carpa de Neeru Hojafuego, que está al otro lado de la caverna; allí podrás invocar al abisario.$B$BDerrótalo con cualquier medio físico o mágico de que dispongas. Luego devuélveme los glifos de invocación y te enseñaré cómo ligar a la criatura a tu voluntad.",
    ["O"] = "Usa los glifos de invocación para invocar y dominar a un abisario y lleva los glifos a Gan\'rul Ojosangre a Orgrimmar.",
    ["T"] = "El vínculo",
  },
  [1505] = {
    ["D"] = "La Horda honra a sus guerreros, ya que su fuerza es incuestionable. Pero los grandes guerreros también son excelentes estudiantes.$B$BBusca a Uzzek. Es veterano de muchas batallas y ahora instruye a jóvenes guerreros. Lo encontrarás al sur de la Avanzada del Puente, la guarnición de los orcos en la frontera que divide los Baldíos y Durotar.",
    ["O"] = "Habla con Uzzek en Avanzada del Puente.",
    ["T"] = "Uzzek, el veterano",
  },
  [1506] = {
    ["D"] = "¡Eh, tú! $N, ¿verdad?$B$BEspero que no tengas la agenda muy llena porque Gan\'rul quiere verte ahora mismo. Lo encontrarás con los brujos mayores en el Circo de las Sombras.$B$BSegún mi experiencia con Gan\'rul, esto de que te convoque solo puede ser muy bueno o muy malo.",
    ["O"] = "Habla con Gan\'rul Ojosangre en Orgrimmar.",
    ["T"] = "Las invocaciones de Gan\'rul",
  },
  [1508] = {
    ["D"] = "Mmm... Por supuesto, esa no es la razón por la que has venido.$B$BVienes de parte de Gan\'rul, lo que quiere decir que vas a invocar un súcubo y necesitas un objeto que lo atraiga.$B$BEn ese caso, $N, ve a los riscos de Orgrimmar, al oeste de la torre del jinete del viento.$B$BHabla con Zankaja, que espera el regreso de su compañero.",
    ["O"] = "Habla con Zankaja en Orgrimmar.",
    ["T"] = "Cazul, el ciego",
  },
  [1510] = {
    ["D"] = "Estaba destinado al Campamento Taurajo, intentando erradicar a los jabaespines. Un día los siguió hasta el campamento, donde fue atacado por esas bestias. Sus compañeros lo rescataron, pero estaba muy grave. Sus heridas tenían muy mala pinta y no se atrevían a moverlo.$B$BNos pidieron un jarabe curativo de los trols de Malaka\'jin, al pie de Sierra Espolón. Enviamos a un mensajero, pero todavía no ha vuelto.",
    ["O"] = "Habla con Ken\'zigla en Malaka\'jin.",
    ["T"] = "Noticias de Dogran",
  },
  [1511] = {
    ["D"] = "Imaginaba que vendría alguien más a por el jarabe, así que lo tengo preparado.$B$BAquí tienes, $N. Recuerda, ¡dale solo la mitad! Con eso podrán desplazarlo sin que se desangre. Al día siguiente, dale la otra mitad.$B$BLlévaselo a Logmar, que está en el Campamento Taurajo. Por lo que me dijo el primer mensajero, él sabe dónde está el tal Dogran.",
    ["O"] = "Lleva el jarabe de Ken\'zigla al bruto Logmar al Campamento Taurajo.",
    ["T"] = "Jarabe de Ken\'zigla",
  },
  [1512] = {
    ["D"] = "Zankaja es joven y guapa, no puede encadenarse a un lisiado; no permitiré que malgaste su vida.$B$BDéjame solo, $N. Y gracias. Así puedo caer honrosamente en combate, en lugar de extinguirme como un vegetal.$B$BY por favor, no le digas nada a ella, no quiero que se sienta culpable.",
    ["O"] = "Lleva el colgante cubierto de tierra a Gan\'rul Ojosangre a Orgrimmar.",
    ["T"] = "Un regalo de amor",
  },
  [1513] = {
    ["D"] = "$N, lleva eso al círculo de invocación de la carpa de Neeru. Invoca a un súcubo y demuéstrale lo que puedes hacer. Eres lo suficientemente fuerte. Pero sé prudente; la arrogancia y el exceso de confianza han sido la perdición de más de un buen brujo.$B$BCuando lo hayas derrotado, devuélveme el colgante para que pueda decirle a Zankaja lo que ha pasado.",
    ["O"] = "Usa el colgante de Dogran para invocar y dominar a un súcubo y lleva el colgante a Gan\'rul Ojosangre a Orgrimmar.",
    ["T"] = "El vínculo",
  },
  [1515] = {
    ["D"] = "Ya que has llegado hasta aquí, no seré yo quien te detenga. Pero no tengo personal que te acompañe, tendrás que ir por tu cuenta.$B$BLo tienen retenido en un campamento de jabaespines al norte de aquí; busca dos chozas juntas. Estará muy vigilado, si es que no lo han matado ya sus heridas o esos animales.$B$BSuerte.",
    ["O"] = "Lleva el jarabe de Ken\'zigla a Dogran a Los Baldíos.",
    ["T"] = "El cautiverio de Dogran",
  },
  [1516] = {
    ["D"] = "Ha llegado el momento, joven $C. Te estás haciendo fuerte y tu espíritu aguanta firme como la tierra. Al seguir la ruta del chamán has demostrado sabiduría antes incluso de haberte puesto a prueba.$B$BEl elemento tierra guiará tu destino, y pasará a formar parte de ti si estás $Gpreparado:preparada;. Pero siempre debes mostrarle tu reconocimiento. Si estás $Glisto:lista; verás cosas que solo conoce un chamán.$B$BVe a buscar a los acechadores viles, al Norte, y coge dos de sus pezuñas. Cuando las tengas vuelve aquí y seguiremos hablando de tu futuro.|n",
    ["O"] = "Lleva 2 pezuñas de acechador vil a Canaga Clamatierras en el Valle de los Retos.",
    ["T"] = "La llamada de la tierra",
  },
  [1517] = {
    ["D"] = "Una sapta es una bebida creada para unir nuestros espíritus con los elementos. Cargada de magia, la poción permite a quien tiene fuerza de voluntad ver los elementos que nadie más puede ver. Tu espíritu se conectará con los elementos para los que fue creada la sapta, y son pocos los chamanes que conocen la receta.$B$BLa sapta siempre se bebe en un lugar sagrado, y en ningún otro sitio... recuérdalo.$B$BBusca la Roca de los Espíritus al suroeste de aquí, la encontrarás al final del Sendero Oculto. Cuando llegues, bebe la sapta, y... deberías poder ver.",
    ["O"] = "Encuentra la Roca de los Espíritus y bebe la Sapta de tierra.",
    ["T"] = "La llamada de la tierra",
  },
  [1518] = {
    ["D"] = "Coge este cuarzo en bruto y llévaselo a Canaga Clamatierras como prueba de nuestro encuentro. Te hará un tótem y este guijarro será su corazón. Puede parecer muy pequeño, pero descubrirás que el tamaño no importa, y que incluso las cosas más pequeñas pueden superar a otras.$B$BTen paciencia, $N. Sigue siendo fuerte. Y busca la sabiduría. Es todo lo que te pide la tierra. Al final te harás $Guno:una; con ella... Respétala del mismo modo que ella te respeta a ti.",
    ["O"] = "Lleva el cuarzo en bruto a Canaga Clamatierras en el Valle de los Retos.",
    ["T"] = "La llamada de la tierra",
  },
  [1519] = {
    ["D"] = "$C, ha llegado el momento; tu cuerpo crece en edad y en fuerza y tu espíritu gana profundidad, como la tierra. Veo en tus ojos la sabiduría de tu alma.$B$BLa tierra guía a mi gente y nos habla, como te hablará a ti en el futuro. Pero debes ponerte a prueba. Si tu momento ha llegado, verás cosas que solo los nuestros pueden ver.$B$BBusca a los chamanes Erizapúas en el barranco que hay al este y coge un bálsamo que usan en sus rituales. Regresa cuando hayas conseguido el suficiente para crear tu propia poción.",
    ["O"] = "Lleva 2 dosis de bálsamo ritual a la vidente Pluma de Cuervo al Campamento Narache.",
    ["T"] = "La llamada de la tierra",
  },
  [1520] = {
    ["D"] = "La sapta es una bebida que nos ayuda a vincular el espíritu a los elementos. Con la magia adecuada, la poción ayuda a ver los elementos como nadie los ha visto antes. Tu espíritu quedará ligado para siempre al elemento para el que se creó la sapta. Solo unos pocos elegidos conocen la receta.$B$BSiempre debes beberte la sapta en un lugar sagrado; nunca jamás en otro sitio.$B$BVe al lugar santo de los chamanes, hacia el este, y bebe allí la sapta.",
    ["O"] = "Encuentra la Roca de los Kodos y bebe la sapta de tierra.",
    ["T"] = "La llamada de la tierra",
  },
  [1521] = {
    ["D"] = "Toma este cuarzo en bruto y llévaselo a la vidente Pluma de Cuervo como prueba de nuestro encuentro. Te hará un tótem y este guijarro será su corazón. Puede parecer muy pequeño, pero descubrirás que el tamaño no importa, y que incluso las cosas más pequeñas pueden superar montañas.$B$BTen paciencia, $N. Sigue siendo fuerte. Y busca la sabiduría. Es todo lo que te pide la tierra. Al final te harás $Guno:una; con ella... Respétala del mismo modo que ella te respeta a ti.",
    ["O"] = "Lleva el cuarzo en bruto a la vidente Pluma de Cuervo al Campamento Narache.",
    ["T"] = "La llamada de la tierra",
  },
  [1522] = {
    ["D"] = "Que el fuego te acompañe, colega. Veo que te manejas bien con la tierra. Creo que ya estás $Glisto:lista; para hablar con Kranal.$B$BKranal vive al norte de El Cruce, en Los Baldíos. Él sabe mucho de los elementos y puede enseñarte cosas del fuego... si crees que estás $Glisto:lista;.$B$BTe será fácil encontrarlo, pero no vayas muy lejos, o acabarás en las tierras de los elfos.",
    ["O"] = "Busca a Kranal Fiss en Los Baldíos.",
    ["T"] = "La llamada del fuego",
  },
  [1523] = {
    ["D"] = "Ha pasado tiempo desde tu última prueba, $N. Hasta ahora has demostrado paciencia en tus estudios, pero ha llegado el momento de que aprendas algo más. Debes aprender de la destrucción... y el caos. De un elemento: el fuego. Podrás comprenderlo, si estás $Gdispuesto:dispuesta; a pagar el precio.$B$BBusca a Kranal Fiss en Los Baldíos. Vive en una pequeña casa al norte de El Cruce. Pero cuidado: si llegas hasta las tierras de los elfos de la noche, habrás ido demasiado lejos.",
    ["O"] = "Busca a Kranal Fiss en Los Baldíos.",
    ["T"] = "La llamada del fuego",
  },
  [1524] = {
    ["D"] = "Ya tienes poder suficiente para llevar el Tótem del Fuego, pero eso no quiere decir que estés $Glisto:lista;.$B$BA lo largo del Río Furia del Sur de Durotar, antes del Mare Magnum, hay una senda oculta que lleva a uno de los picos más altos de la tierra. Ábrete camino hasta la cima. Allí, custodiado por Telf Joolam, encontrarás uno de nuestros santuarios: el Santuario de la Llama Latente.$B$BLleva esta antorcha a Joolam. Así sabrá que intentas domar el fuego. Enciéndela en el blandón del santuario y tráemela. Yo me encargaré de que te sea concedido el Tótem de Fuego.",
    ["O"] = "Lleva la antorcha de la Llama Latente a Telf Joolam a Durotar.",
    ["T"] = "La llamada del fuego",
  },
  [1525] = {
    ["D"] = "Pero tú no has venido aquí a recibir lecciones de historia, ¿verdad? Has venido aquí por la Llama Eterna y el Tótem de Fuego.$B$BEl primer paso es crear una sapta y unirte a la llama.$B$BPuedo crearte la sapta de fuego si me traes dos objetos. Primero necesito alquitrán. Un objeto sencillo que suelen llevar los taumaturgos de los Crines de Acero de la región este de Los Baldíos.$B$BEl segundo objeto será más difícil: una faltriquera de componentes de uno de los cultores del Filo Ardiente. Los encontrarás en una cueva que está sobre los barrancos del noreste de Cerrotajo.",
    ["O"] = "Lleva 1 alquitrán y 1 faltriquera de componentes a Telf Joolam a Durotar.",
    ["T"] = "La llamada del fuego",
  },
  [1526] = {
    ["D"] = "La sapta de fuego está acabada. Ha llegado el momento de que afrontes la llama. Utiliza la sapta del santuario y sube a la cima de la montaña para enfrentarte a la aparición que encontrarás allí. Él no te atacará hasta que no estés $Glisto:lista;, pero se dará cuenta de que puedes verlo.$B$BAtaca con rapidez, $N, pues cuanto más tardes en matarlo, más crecerá su poder. Una vez le hayas derrotado, coloca sus ascuas ardientes en el blandón y enciende la antorcha.",
    ["O"] = "Derrota a la Manifestación de fuego menor y coloca las ascuas ardientes sobre el altar de la llama eterna.",
    ["T"] = "La llamada del fuego",
  },
  [1528] = {
    ["D"] = "Haber agua\'n tu futuro, colega. Veo tú ser ya familiar con fuego, así qu\'estarás $Glisto:lista; pa\' hablar con Islen.$B$BIslen vivir pa\'llá por la costa de Los Baldíos. Si vas pa\'l sur por el Río Furia del Sur, no se t\'escapa, ¡buscar gran tiburón! Ella ser muy buena pa\' temas d\'agua... si creer que tú estar $Glisto:lista;.$B$B\'star tirao encontrarla, ir to\' pa\'l sur, ella estar pasao Trinquete.",
    ["O"] = "Encuentra a Islen Vidente del Agua en Los Baldíos.",
    ["T"] = "La llamada del agua",
  },
  [1529] = {
    ["D"] = "Mucho tiempo ha pasado desde que a prueba te pusieron, $N. Paciencia en tus estudios hasta ahora has mostrado pero de aprender aún más la hora ha llegado. Ha llegado la hora de que aprendas cosas sobre la pureza del agua.$B$BBusca a Islen Vidente del Agua en Los Baldíos. Se encuentra por la costa oriental, al sur del Río Furia del Sur y de Trinquete. La encontrarás en su cabaña de pesca. Que la Fortuna te sea favorable, $N.",
    ["O"] = "Encuentra a Islen Vidente del Agua en Los Baldíos.",
    ["T"] = "La llamada del agua",
  },
  [1530] = {
    ["D"] = "Si hay una lección que has de aprender acerca del agua, es esta: el agua significa renacimiento.$B$BSu energía fluye, erosiona y limpia todo tipo de cosas. Su capacidad de curación y de reconstitución te parecerá sin parangón pero solo se da cuando es pura.$B$BCuando entiendas que si el agua está contaminada, puede causar estragos en todo aquello que toca, te darás cuenta de cuán vital es este recurso.$B$BSi deseas la sapta del agua, encuentra a Salmuera en lo más recóndito de Los Baldíos del Sur. Los jabaespines la importunan constantemente en su hogar.",
    ["O"] = "Encuentra a Salmuera en la Loma Espina Negra, en Los Baldíos del Sur.",
    ["T"] = "La llamada del agua",
  },
  [1531] = {
    ["D"] = "Tú estar haciendo progresos, colega. Estar $Gpreparado:preparada; para demostrar si merecer recibir Tótem de aire, pero antes encontrar a Prate Vidente de las Nubes en Las Mil Agujas debes. Si tratas a ella con respeto, ella dirá lo que tener que hacer para conseguir tu Tótem de aire.$B$BVe al sur por Los Baldíos y al llegar a Las Mil Agujas, hacia el este. Encontrarás a ella por allí escondida con su novio... ¡Ja, ja, ja! No digas que yo dije... ¡a ella no gustar!",
    ["O"] = "Encuentra a Prate Vidente de las Nubes en Las Mil Agujas.",
    ["T"] = "La llamada del aire",
  },
  [1532] = {
    ["D"] = "Los vientos de Kalimdor se apresuran por conocerte. Tú serás $Guno:una; de $Gnuestros:nuestras; más $Gpoderosos:poderosas; $Ghermanos:hermanas;. Me llena de orgullo comprobar que has llegado hasta aquí y que cuentas con un prometedor futuro.$B$BEl aire es un elemento difícil de controlar pero confío en que tus habilidades te sirvan para superar todos los obstáculos que te encuentres.$B$BDirígete al sur de Los Baldíos y ve a Las Mil Agujas. Allí, en la zona este, encontrarás a Prate Vidente de las Nubes. Ella te podrá enseñar mejor.",
    ["O"] = "Encuentra a Prate Vidente de las Nubes en Las Mil Agujas.",
    ["T"] = "La llamada del aire",
  },
  [1534] = {
    ["D"] = "Toma este odre de agua y adéntrate en el frondoso bosque que los elfos de la noche llaman Vallefresno. Está en un lugar remoto del norte de Los Baldíos.$B$BCuando hayas penetrado en el bosque, dirígete hacia el oeste. Por las montañas que separan Espolón y Vallefresno, y más allá del Lago Mystral, encontrarás un lugar sagrado: Las Ruinas del Polvo Estelar. En el centro del lago encontrarás una fuentecilla.$B$BTráeme una muestra del agua de esa fuente. Será el último componente que necesitarás antes de que te mande de nuevo con Islen.",
    ["O"] = "Llena el odre de agua azul vacío en Las Ruinas del Polvo Estelar en Vallefresno y llévasela a Salmuera en Los Baldíos.",
    ["T"] = "La llamada del agua",
  },
  [1535] = {
    ["D"] = "Para adquirir el conocimiento y ganarte la sapta, debes buscar solo las formas más puras de agua. Conforme recoges muestras para mí, observa a tu alrededor. Ten cuidado con las criaturas que dependen del agua, que la protegen. Fíjate en cómo afecta a los bosques y a los desiertos. Presencia cómo la mismísima sustancia en la que se sustenta la vida, obliga a las criaturas a arrebatar vidas.$B$BEmpezaremos con una tarea simple: debajo de mi cabaña hay un abrevadero con el preciado líquido. Llena este odre y vuelve aquí.",
    ["O"] = "Llena el odre de agua marrón vacío en el abrevadero que está debajo de la cabaña de Salmuera y llévaselo a Los Baldíos.",
    ["T"] = "La llamada del agua",
  },
  [1536] = {
    ["D"] = "Hace mucho tiempo seguí a unos compañeros hasta las Laderas de Trabalomas. Nos defendimos de tenaces humanos que allí moran y acabamos en un pueblecito que los Renegados habían tomado: Molino Tarren.$B$BAllí fue donde encontré algo muy especial: en el centro del pueblo había un pequeño pozo con agua dulce de manantial. No sé muy bien si los lugareños apestados se daban cuenta de su valor, es un pequeño vestigio sagrado en un mar sin consagrar.$B$BTráeme una muestra de esa agua en este odre.",
    ["O"] = "Llena el odre de agua rojo vacío en el pozo de Molino Tarren y llévasela a Salmuera en Los Baldíos.",
    ["T"] = "La llamada del agua",
  },
  [1560] = {
    ["D"] = "¡Estoy totalmente perdido!$B$BMi esposa, Thorta, me envió a buscar la cena y en algún momento debí de coger el camino equivocado.$B$B¿Serías tan amable de llevarme de vuelta con mi esposa Thorta? Ella me está esperando al sur del Puerto Bonvapor.",
    ["O"] = "Muéstrale a Tooga el camino de vuelta con su esposa, Thorta.",
    ["T"] = "La misión de Tooga",
  },
  [1578] = {
    ["D"] = "Los montaraces de Loch Modan están luchando denodadamente contra los troggs. Necesitan un suministro constante de armaduras y armas.$B$BPor eso está allí mi colega Thorvald, encargándose del equipamiento de los montaraces. Seguro que te agradecerá que le fabriques unas hachas y unos cinturones de anillas de cobre. Puede incluso que comparta su sabiduría contigo.$B$BThorvald está en la torre sur de Loch Modan.",
    ["O"] = "Lleva 6 hachas y 6 cinturones de anillas de cobre a Thorvald a la torre de vigilancia sur de Loch Modan.",
    ["T"] = "Abastecer al frente",
  },
  [1580] = {
    ["D"] = "Sabemos que tienes mucha suerte, pero... ¿hasta dónde llega, $N?$B$BUna noche, estaba yo deambulando por los caminos de Costa Oscura... Iba buscando un buen río o lago para probar mi nuevo señuelo-pato autopropulsado explosivo y... creo que estaba un poco alegre porque perdí el conocimiento y me levanté horas después en Auberdine. ¡Tenía el pelo quemado y había perdido mi bolsa de electromuelles! ¡Seguro que se me cayeron al agua!$B$B¡Necesito mis electromuelles! Quizás puedas pescarlos de algún lago o río de Costa Oscura...",
    ["O"] = "Lleva 12 electromuelles a Maniveo Zumbang a Auberdine.",
    ["T"] = "Los electromuelles",
  },
  [1582] = {
    ["D"] = "En Darnassus estamos muy ocupados, sobre todo los artesanos. Los jóvenes aventureros están impacientes por probar sus habilidades en las tierras salvajes y encargan suministros constantemente.$B$BSi pudieras ayudarme a atender mis pedidos de artículos de cuero, yo podría enseñarte una técnica de peletería avanzada.",
    ["O"] = "Trae 1 par de guantes de cuero estampados, 5 botas de cuero estampadas y 5 capas de cuero estampadas para Lotherias en Darnassus.",
    ["T"] = "Jubón resplandor lunar",
  },
  [1598] = {
    ["D"] = "¡Eh, tú! Eres $N, ¿verdad?$B$BEspero que estés muy seguro de que quieres convertirte en brujo, porque es bien difícil.$B$BYo te instruiré, pero antes debo asegurarme de que tienes lo que hay que tener.$B$BTeníamos un novicio en la abadía que huyó para unirse a la Hermandad Defias. ¿Que qué más da? Pues da que robó unos libros importantes.$B$BYo quería estudiar uno de ellos, Poderes del Vacío. Tráemelo; lo tienen escondido en su carpa de los Viñedos.",
    ["O"] = "Lleva los Poderes del Vacío a Drusilla La Salle.",
    ["T"] = "El libro robado",
  },
  [1599] = {
    ["D"] = "Debes de ser $Gel nuevo brujo del:la nueva bruja de la; que habla todo el mundo. Has debido de impresionar a alguien, si no, no me pedirían que robara tiempo a mis investigaciones para instruirte.$B$BNo te preocupes, no te lo echaré en cara.$B$BEl Diablillo es el hechizo de invocación más sencillo que aprenderás. Pero antes de que te imparta tal conocimiento, debes probarme que tienes las habilidades mágicas y físicas necesarias.$B$BTráeme tres talismanes de plumas de los novicios de Peloescarcha. Los encontrarás en una cueva que hay al suroeste.",
    ["O"] = "Lleva 3 talismanes de plumas a Alamar Grimm a Yunquemar.",
    ["T"] = "Novato",
  },
  [1618] = {
    ["D"] = "Los enanos no somos los únicos que luchamos. ¡Nuestros aliados humanos también están muy ocupados!$B$BCrestagrana sufre ataques constantes de los orcos Roca Negra y siempre anda mal de equipamiento. Si decides ayudarles, seguro que su herrero, Verner Osbien, te enseñará lo que sepa.",
    ["O"] = "Tormus Forjahonda quiere que le lleves 4 cinturones rúnicos de cobre y 4 mazas grandes de cobre pesadas a Verner Osbien a Crestagrana.",
    ["T"] = "Equipar Crestagrana",
  },
  [1638] = {
    ["D"] = "Has demostrado ser $Gun guerrero:una guerrera; excelente, $N, pero todavía eres joven y tienes mucho que aprender.$B$BVe a hablar con Harry Burlguard, suele estar en la taberna El Cerdo Borracho, en el Casco Antiguo de Ventormenta. Harry es un viejo veterano y un buen instructor. Puede enseñarte algunos movimientos que te ayudarán a seguir con vida algo más tiempo.",
    ["O"] = "Habla con Harry Burlguard en Ventormenta.",
    ["T"] = "Instrucción de guerrero",
  },
  [1639] = {
    ["D"] = "Sí, puedo enseñarte. Pero primero quiero ver tus movimientos.$B$B¿Ves a aquel borracho al fondo del bar? ¿Bartleby? Quiero que cojas su jarra.$B$BJe, y si conoces a Bartleby sabrás que no va a dártela sin más.",
    ["O"] = "Habla con Bartleby.",
    ["T"] = "Bartleby, el borracho",
  },
  [1640] = {
    ["D"] = "¿¿Que quieres qué?? ¡Estás $Gloco:loca;!$B$BSolo me quitarás la jarra de mis manos frías y muertas...",
    ["O"] = "Golpea a Bartleby y habla con él.",
    ["T"] = "Vence a Bartleby",
  },
  [1641] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Escrito sobre divinidad",
  },
  [1642] = {
    ["D"] = "Un paladín debe reflejar en todas las cosas la Luz que complementa su fuerza. El esfuerzo por alcanzar la divinidad, para uno de nosotros, no significa aspirar al bien, pues aspiramos al bien en todas nuestras acciones.$B$BAunque en estos duros tiempos somos convocados para atacar al mal, debes recordar que será la ayuda que prestes a los demás lo que te distinguirá de los ciudadanos de Azeroth. La compasión, la paciencia y el valor tienen tanta importancia en un paladín como su fuerza en la batalla.$B$BAprende esto ahora y nunca lo olvides.",
    ["O"] = "Habla con Duthorian Rall en Ventormenta.",
    ["T"] = "Escrito sobre divinidad",
  },
  [1643] = {
    ["D"] = "Ahora, tu tarea, $N, es encontrar en Ventormenta a alguien que necesite ayuda. Alguien de intenciones y corazón puros. Quizás alguien que ayude a los demás. Alguien que necesite ser guiado. En cualquier caso, esa persona debe tener intenciones nobles y depender de tu bondad.$B$BVuelve a mí cuando hayas acabado. Entonces seguiremos hablando de tu camino y de lo que significa ser un paladín de la Luz.",
    ["O"] = "Busca a Stephanie Turner en el Distrito de Mercaderes de Ventormenta.",
    ["T"] = "Escrito sobre divinidad",
  },
  [1644] = {
    ["D"] = "Benditos sean, la mayoría de ellos han perdido a su familia en la última década... a manos de la Plaga.$B$BMi marido John recoge material para que yo haga camisas y pantalones para los niños, pero no he tenido tiempo de hacerlo. Mi primera preocupación ha sido ponerles comida que en el plato y procurarles un techo.$B$B¿Cómo dices? ¿Que quieres ayudar? ¡Oh, gracias a la Luz! ¡Y gracias a ti, $N!$B$BSi pudieras traerme paño de lino para que cosa ropa para los niños, me ayudarías mucho.",
    ["O"] = "Lleva 10 retales de paño de lino a Stephanie Turner a Ventormenta.",
    ["T"] = "Escrito sobre divinidad",
  },
  [1645] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Escrito sobre divinidad",
  },
  [1646] = {
    ["D"] = "Un paladín debe reflejar en todas las cosas la Luz que complementa su fuerza. El esfuerzo por alcanzar la divinidad, para uno de nosotros, no significa aspirar al bien, pues aspiramos al bien en todas nuestras acciones.$B$BAunque en estos duros tiempos somos convocados para atacar al mal, debes recordar que será la ayuda que prestes a los demás lo que te distinguirá de los ciudadanos de Azeroth. La compasión, la paciencia y el valor tienen tanta importancia en un paladín como su fuerza en la batalla.$B$BAprende esto ahora y nunca lo olvides.",
    ["O"] = "Ve a Forjaz y habla con Tiza Forjabatalla.",
    ["T"] = "Escrito sobre divinidad",
  },
  [1647] = {
    ["D"] = "Ahora, tu tarea, $N, es encontrar en Forjaz a alguien que necesite ayuda. Alguien de intenciones y corazón puros. Quizás alguien que ayude a los demás. Quizás alguien que necesite ser guiado. En cualquier caso, esa persona debe tener intenciones nobles y depender de tu bondad.$B$BVuelve a mí cuando hayas acabado. Entonces seguiremos hablando de tu camino y de lo que significa ser un paladín de la Luz. Esperaré tu regreso en el templo. Habrá otros paladines que necesiten mi guía.",
    ["O"] = "Busca a John Turner en el anillo exterior de Forjaz.",
    ["T"] = "Escrito sobre divinidad",
  },
  [1648] = {
    ["D"] = "Mi esposa y yo llevamos un orfanato en Ventormenta. En los últimos diez años hemos acogido a muchos niños que han perdido a sus padres en ataques del Azote. Yo vengo a Forjaz más o menos cada mes. Recojo suministros que me dan los habitantes y hablo con todos los grupos para ver si pueden ayudar en algo.$B$BEste viaje ha sido de una lentitud agonizante y no muy productivo.$B$BSi pudieras conseguirme más tela para que mi mujer haga algo de ropa, podría por fin hacer un envío a casa.",
    ["O"] = "Lleva 10 retales de paño de lino a John Turner a Forjaz.",
    ["T"] = "Escrito sobre divinidad",
  },
  [1649] = {
    ["D"] = "El valor y todas las virtudes que lo acompañan, son como minerales preciosos y poco comunes: debes apreciarlos cuando los encuentras porque son valiosos, pero también debes invertir tiempo en cuidarlos... refinarlos. Cuando se mezclan correctamente, se forma una fuerte aleación que, algunas veces, resulta imposible de romper, incluso con los golpes más poderosos.$B$BTodo esto debe ser puesto a prueba: la fuerza, tu fe, tu valentía. Debes aprender a no perder la fe y darte cuenta de que con el poder de la Luz, puedes conseguir cosas que otros no pueden.",
    ["O"] = "Habla con Duthorian Rall en Ventormenta.",
    ["T"] = "Escrito sobre valor",
  },
  [1650] = {
    ["D"] = "¡Bien! Que no vaciles es una señal inequívoca de que estás $Gpreparado:preparada; y $Gdispuesto:dispuesta; a ponerte a prueba. Que así sea.$B$BJusto al final de Playa Larga, en los Páramos de Poniente, hay una pequeña casa con vistas al océano. Allí vive una pareja, los Fontana. Jordan es un herrero fabuloso y ha ido a Forjaz a pasar una temporada con los enanos. Pero esto ha dejado a Daphne, su mujer, sola y desprotegida.$B$BLos informes sugieren que los Defias han tomado casi toda la zona, es posible que Daphne necesite protección. Ve allí.",
    ["O"] = "Encuentra a Daphne Fontana en los Páramos de Poniente.",
    ["T"] = "Escrito sobre valor",
  },
  [1651] = {
    ["D"] = "Apuesto a que volverán pronto, así que debes prepararte.$B$BTengo una pistola en la casa. La cogeré y me prepararé para ayudarte pero, en general, estarás $Gsolo:sola;.$B$BNo tengo ni idea de cuántos mandarán, pero seguro que serán más de los que mandaron antes.$B$BRuega a la Luz que nos bendiga porque... ¿Has oído eso? Creo que vienen ya.$B$B¡Nos colocaremos cerca de la casa!",
    ["O"] = "Defiende a Daphne Fontana del ataque de los Defias.$B$BSi quieres tener éxito, vuestros espíritus no pueden salir de sus espirales mortales.$B$BCuando lo hayas conseguido, vuelve a hablar con Daphne Fontana.",
    ["T"] = "Escrito sobre valor",
  },
  [1652] = {
    ["D"] = "Por favor, vuelve a ver a Duthorian y dile que, por ahora, estoy a salvo. Y todo gracias a ti y a la iglesia.$B$BÉl enviará el mensaje a Forjaz para que mi querido Jordan sepa que no tiene nada que temer.$B$B¡Oh, cómo echo de menos a mi marido! Qué ganas tengo de que vuelva.",
    ["O"] = "Habla con Duthorian Rall en Ventormenta.",
    ["T"] = "Escrito sobre valor",
  },
  [1654] = {
    ["D"] = "Si aceptas, te recompensaré. Entiéndeme bien, mi generosidad depende de hasta qué punto me ayudes, pero creo que una alianza nos sería beneficiosa.$B$BTe haré un arma digna de tus logros, un símbolo de los paladines y de la Luz, pero tienes que traerme los objetos que necesito para crearla. Tengo aquí la lista; solo necesito a alguien valiente que vaya a buscar los objetos. ¿Qué me dices, $C?",
    ["O"] = "Consulta la lista y llévale a Jordan Fontana de Forjaz lo siguiente: madera de roble de Piedrablanca, envío de oro refinado de Bailor, el martillo de herrero de Jordan y una gema Kor.",
    ["T"] = "La prueba de rectitud",
  },
  [1655] = {
    ["D"] = "¡Me has asustado, $Gtío:tía;! ¿No ves que estaba reflexionando?$B$B¿Qué es eso? El cargamento de Jordan para Forjaz. Vale, ya lo pillo. Los ogros de Mo\'grosh son los que afanaron los minerales de mi amigo. Atacaron la caravana cuando venía de Los Humedales. El único guardia que sobrevivió palmó hace poco; los sanadores no sabían qué hacer.$B$BSi quieres el cargamento, tendrás que cruzar Loch Modan hacia el noreste. Tiene que estar por ahí.",
    ["O"] = "Lleva el envío de Jordan a Bailor Petramano a Loch Modan.",
    ["T"] = "Envío de mena de Bailor",
  },
  [1656] = {
    ["D"] = "Por favor, $N, ¿puedes ayudarme? Estoy cumpliendo la prueba del Rito de la Fuerza; mi padre me pidió que entregara estas pieles en la posada de Pezuña de Sangre.$B$BNo puedo completar el rito y el recado de mi padre; no tengo fuerzas. ¿Puedes hacer la entrega por mí?$B$BLa posada es un sitio muy agradable para descansar de tanto viaje; seguro que me lo agradecerás.",
    ["O"] = "Lleva el fardo de pelajes al tabernero Kauth al Poblado Pezuña de Sangre.",
    ["T"] = "Una tarea por terminar",
  },
  [1657] = {
    ["D"] = "Para celebrar la fiesta de Halloween con estilo... ¡infríngeles dolor a nuestros enemigos de Costasur!$B$BCoge una de estas bombas fétidas especialmente diseñadas. Contiene una sustancia apestosa que ningún humano u otra bestia cualquiera de poca voluntad pueden soportar. Tendrás que lanzarla en el mismísimo centro de Costasur para que surta efecto, así que prepárate para una batalla contra la Alianza.$B$BCuando hayas realizado esta tarea, este tributo a nuestra liberación... ¡ven a verme!",
    ["O"] = "Lanza 3 bombas fétidas de los Renegados en el corazón de Costasur en Trabalomas, y después ve a ver a la clamasombras Yanka, que está en el Festival del Hombre de Mimbre en los Claros de Tirisfal.",
    ["T"] = "Apesta Costasur",
  },
  [1658] = {
    ["D"] = "Necesito gente valiente para ir a Claros de Tirisfal y echar un ojo al Festival del Hombre de Mimbre. No te voy a mentir: llegar hasta allí es peligroso. Los guardas del festival serán excepcionalmente brutales. Pero tenemos que saber cómo será el festival de este año y cuántos problemas nos van a dar los Renegados en Halloween.$B$BVe a Tirisfal y echa un vistazo por el festival... si te atreves. Vuelve con la información y me aseguraré de que recibes una buena recompensa.",
    ["O"] = "Localiza el Festival del Hombre de Mimbre de los Renegados en Claros de Tirisfal. Cuando acabes, ve a ver al sargento Hartman a Costasur.",
    ["T"] = "Arruinar el Festival del Hombre de Mimbre",
  },
  [1659] = "_",
  [1660] = "_",
  [1661] = {
    ["D"] = "Te saludo de nuevo, $N. Siento más orgullo que nunca por poder llamarte $Ghermano:hermana;. Tu dedicación es un honor para todos nosotros.$B$BHa llegado el momento de que te haga un gran regalo. Pero has de saber algo: por ahora, te doy esto libremente pero, más tarde, cuando sea el momento preciso, esto será un desafío mucho mayor para todos los paladines que decidan aceptar mi misión.$B$BVuelve a hablar conmigo y te concederé un gran favor.",
    ["O"] = "Habla con Duthorian Rall en Ventormenta.",
    ["T"] = "Escrito sobre nobleza",
  },
  [1665] = {
    ["D"] = "Vale, vale, toma mi jarra. Pero deja que me la acabe... Aquí tienes.",
    ["O"] = "Lleva la jarra de Bartleby a Burlguard.",
    ["T"] = "Jarra de Bartleby",
  },
  [1666] = {
    ["D"] = "Has aprendido algunos movimientos, pero te vendría bien un arma decente. Seguro que si hablaras con el mariscal Haggard podría ayudarte.$B$BEl mariscal Haggard vive en una mansión en el Bosque de Elwynn, en el Aserradero de la Vega del Este. Está retirado, pero ha reunido mucho equipo durante toda su carrera.$B$BDile que vas de mi parte y, con algo de suerte, tendrá un arma de sobra que ofrecerte.",
    ["O"] = "Habla con el mariscal Haggard.",
    ["T"] = "Mariscal Haggard",
  },
  [1667] = {
    ["D"] = "¿Te ha enviado Burlguard? Sí, tengo algunas armas de sobra, pero antes tengo una tarea para ti...$B$BHace algún tiempo, un villano Defias, Jack Dentomuerto, me engañó. Estaba oscuro y vestía un tabardo de Ventormenta, así que creí que era el alguacil Dughan. Dijo que necesitaba mi identificación de alguacil de Ventormenta, se la di y en cuanto la tuvo en sus manos salió corriendo antes de poder atraparle.$B$B¡Tráeme de vuelta mi identificación! Seguramente Jack Dentomuerto la tendrá guardada en su campamento del Sur.",
    ["O"] = "Recupera la insignia de Haggar del campamento de Jack Dentomuerto y regresa con el mariscal Haggard.",
    ["T"] = "Jack Dentomuerto",
  },
  [1678] = {
    ["D"] = "Si quieres subir en las filas de los guerreros, tendrás que ser muy fuerte. Puedo enseñarte nuevos movimientos pero, antes de nada, debes demostrarme que puedes vértelas con auténticos adversarios.$B$BVejrek es el trol más duro que encontrarás por estos lares, y es a él a quien tienes que derrotar. Tiene una cabaña en las laderas al sur de Poblado Peloescarcha.$B$BTráeme su cabeza y sabré que realmente tienes entereza.",
    ["O"] = "Lleva la Cabeza de Vejrek a Muren Pico Tormenta en Forjaz.",
    ["T"] = "Vejrek",
  },
  [1679] = {
    ["D"] = "¡Hola $Gguerrero:guerrera;! Pareces $Gcargado:cargada; de promesas, pero todavía tienes que aprender algunos trucos y Muren Pico Tormenta es exactamente el enano que necesitas.$B$BPodrás encontrarlo en la Sala de Armas, en La Sala Militar de Forjaz. Ve a hablar con él cuando estés $Glisto:lista; para aprender lo que es ser $Gun auténtico guerrero:una auténtica guerrera;.",
    ["O"] = "Habla con Muren Pico Tormenta.",
    ["T"] = "Muren Pico Tormenta",
  },
  [1681] = {
    ["D"] = "Mi mentor, Sturgy Vetaferro, tenía un complejo al sur del Lago de Helm, en la parte oriental de Dun Morogh. Allí enseñaba herrería y era el mejor.$B$BPero un día, uno de sus estudiantes le llevó una extraña mena. El maestro Vetaferro la llamó mena umbría y estaba ansioso por trabajar con ella, pero tan pronto como llegó el cargamento un grupo de enanos Hierro Negro atacaron su complejo. ¡Mataron a todo el mundo!$B$BEntra en el Complejo Vetaferro, $N, y venga la muerte de mi maestro. Tráeme también el cargamento de mena umbría que le causó la muerte.",
    ["O"] = "Lleva una pieza de mena umbría a Tormus Forjahonda en Forjaz.",
    ["T"] = "Complejo Vetaferro",
  },
  [1683] = {
    ["D"] = "Antes de poder instruirte, debes mostrarme tu determinación.$B$BEl sátiro Vorlus Pezuñavil ha encontrado una de nuestras pozas de la Luna más remotas, al sureste del Túmulo de Ban\'ethil. Ahora se pasa el día chapoteando en sus aguas y corrompiéndolas con su mugre.$B$BEncuentra a Vorlus en la Poza de la Luna y termina con él. Tráeme su cuerno como prueba y comenzaré tu instrucción avanzada como $Gguerrero:guerrera;.$B$BEl camino a la Poza de la Luna está bien escondido, $N, pero mantente $Gconcentrado:concentrada; y alerta y lo encontrarás.",
    ["O"] = "Lleva el cuerno de Vorlus a Elanaria en Darnassus.",
    ["T"] = "Vorlus Pezuñavil",
  },
  [1684] = {
    ["D"] = "Saludos, $Gguerrero:guerrera;. Tus habilidades siguen creciendo, pero hay mucho más de lo que crees en tu profesión. Para seguir progresando debes encontrar a un instructor.$B$BLa guerrera Elanaria vive en Darnassus, en el Bancal del Guerrero. Ella podrá instruirte.",
    ["O"] = "Habla con Elanaria.",
    ["T"] = "Elanaria",
  },
  [1685] = {
    ["D"] = "Si es que... siempre igual. Me he pasado el día buscándote sin encontrarte y ahora, cuando ya ni te esperaba, te plantas aquí, sin más.$B$BYa he perdido demasiado tiempo buscándote. No perdamos más. El maestro Gakin te ha convocado para instruirte. Creo que deberías ir en su busca lo antes posible.$B$BLo encontrarás en el sótano de la posada El Cordero Degollado. Por cierto, será mejor que no comentes por ahí ese detalle de la posada... No queremos que se corra la voz de dónde se encuentra...",
    ["O"] = "Preséntate ante Gakin el Presotenebra en el Barrio de los Magos de Ventormenta.",
    ["T"] = "La invocación de Gakin",
  },
  [1686] = {
    ["D"] = "Has aprendido mucho, $N. Ahora podremos fabricarte un arma. Un arma de $Gauténtico guerrero:auténtica guerrera;.$B$BPara hacerlo debes viajar a la Costa Oscura.$B$BHace mucho tiempo, un barco naufragó junto a las rocas, cerca del faro de Auberdine, esparciendo muchos cajones de mena de elunita por todo el fondo marino.$B$BPara empezar, debes derrotar a la guardiana de la elunita, la Sombra de Elura. La que fue capitana del barco se condenó a sí misma a vagar por las profundidades y a proteger el cargamento por el resto de los tiempos. Acaba con ella, hazte con su medallón y recoge los cajones de elunita perdidos.",
    ["O"] = "Lleva 8 piezas de mineral de elunita y el Medallón de Elura a Elanaria en Darnassus.",
    ["T"] = "La sombra de Elura",
  },
  [1688] = {
    ["D"] = "Tuve a una estudiante llamada Surena Caledon. Estaba justo donde estás tú ahora, dispuesta a aprender brujería y sin una pizca de talento. A pesar de eso, era joven y muy bonita. Tenía que haberlo visto venir, ¡esa mujerzuela traidora!$B$BLa ladronzuela escapó con un matón, Erlan Morosiervo. Si bien su pérdida no es muy importante, le regalé una gargantilla de sangrita que debo recuperar.$B$BAhora, su vida no significa nada para mí. La encontrarás en la Plantación de Calabazas de Brackwell. Recupera lo que me pertenece.",
    ["O"] = "Consigue la gargantilla de Surena para Gakin el Presotenebra de Ventormenta.",
    ["T"] = "Surena Caledon",
  },
  [1690] = {
    ["D"] = "¡Eh, tú! Es hora de que ayudes a la compañía de aguas de Gadgetzan.$B$B¡Los nómadas Vagayermo de Tanaris se han hecho con el poder de todos los pozos de agua! Ahora que ya no tenemos acceso a ellos, el suministro de agua se agotará en poco tiempo. Si deseas recibir nuestra gratitud, ayúdanos a impartir justicia a esos nómadas.$B$BDirígete hacia el este, captura a diez bandidos y ladrones Vagayermos y regresa aquí inmediatamente. ¡La justicia no espera a ningún hombre... goblin... lo que sea! ¡Ponte ya en marcha!",
    ["O"] = "Acaba con 10 bandidos y ladrones Vagayermos al este de Gadgetzan y después vuelve a ver al ingeniero jefe Pasaquillas.",
    ["T"] = "Justicia para los Vagayermos",
  },
  [1691] = {
    ["D"] = "Necesitamos que te encargues de las filas más fuertes de los nómadas Vagayermos. Has hecho un buen trabajo hasta el momento pero si queremos recuperar los pozos de agua, lo más inteligente es que nos enfrentemos al menor número de nómadas posible.$B$BEn esta ocasión te pedimos que acabes con sus pícaros, asesinos y magos oscuros. Los encontrarás en dirección este y sudeste.$B$BNo voy a mentirte: son unos seres repugnantes. Si finalmente haces esto por nosotros, ¡estaremos un paso más cerca de recuperar lo que nos pertenece!",
    ["O"] = "Acaba con 10 magos oscuros Vagayermos, 8 pícaros Vagayermos y 6 asesinos Vagayermos para el ingeniero jefe Pasaquillas de Gadgetzan.|n",
    ["T"] = "Más justicia para los Vagayermos",
  },
  [1699] = {
    ["D"] = "He llamado a mi desafío El guantelete de Rethban. Para superar la prueba, todo lo que tienes que hacer es entrar en las Cavernas de Rethban, hasta el primer cruce, y volver aquí. ¡Es fácil! Aunque hay algunos peros...$B$BTienes que volver en una hora.$B$BEncontrarás montones de gnolls de Crestagrana en el camino$B$BAntes de empezar tienes que tomar un trago de la bebida de mi familia: ¡brebaje de Cebadiz!$B$BBueno, ¿qué dices? Las Cavernas de Rethban están en las laderas al norte de Villa del Lago. ¿Estás $Glisto:lista; para afrontar El guantelete de Rethban?",
    ["O"] = "Entra en las Cavernas de Rethban, llega hasta la primera bifurcación y regresa junto a Yorus Cebadiz en el tiempo estipulado.$B$BNo debes morir y liberar tu espíritu.",
    ["T"] = "El guantelete de Rethban",
  },
  [1701] = {
    ["D"] = "Te he dado un buen escudo, pero la cosa no termina aquí. Conozco los secretos para hacer una armadura de malla extremadamente dura. No es fácil y se necesitan algunos materiales bastante extraños, pero si quieres hacer tú los preparativos te haré una pieza.$B$B¿Qué me dices, $N? Si estás $Glisto:lista; para el desafío, te daré una lista de lo que necesitas y dónde conseguirlo.",
    ["O"] = "Reúne los materiales que necesita Furen Barbalarga y llévaselos a Ventormenta.",
    ["T"] = "Armadura de malla endurecida con fuego",
  },
  [1702] = {
    ["D"] = "Superando El guantelete de Rethban has demostrado de qué estás $Ghecho:hecha;, $N. Y yo soy fiel a mi palabra. Te daré la recompensa prometida...$B$BEl único problema es que está en Ventormenta.$B$BVe a hablar con mi amigo, Furen Barbalarga, en el Distrito de los Enanos de Ventormenta. Es el mejor forjador de escudos que podrás encontrar, y me debe un gran favor.$B$BToma, llévale este barril de brebaje de Cebadiz a Furen y pídele un escudo. Seguro que te dará uno estupendo.",
    ["O"] = "Lleva el barril de brebaje a Furen Barbalarga.",
    ["T"] = "El forjador de escudos",
  },
  [1705] = {
    ["D"] = "Odio reconocer que Furen ha dado con algo realmente bueno. Pero para asegurarme tendré que probar sus técnicas en una nueva armadura. Si me ayudas con los materiales, $N, podrás quedarte con ella.$B$BPara hacer la armadura necesito muestras de sangre ardiente y de roca ardiente.$B$BSolo las podrás conseguir en el Bosque del Ocaso. Los huargen Penumbría que se asientan en la mina de la Condena de Roland tienen sangre ardiente y su líder, Agallas, tendrá la roca.$B$BQue la fortuna te permita ser $Grápido:rápida;, $N.",
    ["O"] = "Lleva 20 viales de sangre ardiente y 1 roca ardiente a Grimand Elmore en Ventormenta.",
    ["T"] = "Sangre ardiente",
  },
  [1707] = {
    ["D"] = "Saludos, $N. Mi nombre es Llantalunket y, si quieres, te puedo ofrecer una misión. ¡Hurra!$B$BNada me agradaría más como grifoperador que me trajeses las faltriqueras de agua de Vagayermos que encuentres por el desierto. Al fin y al cabo, es nuestra agua. Los nómadas la han robado. La compañía de aguas de Gadgetzan te ofrecerá un botiquín oficial por cada cinco faltriqueras que entregues. Y lo más importante, ¡estarás ayudando a Gadgetzan!",
    ["O"] = "Llévale 5 faltriqueras de agua de Vagayermos al grifoperador Llantalunket, que está en Gadgetzan.",
    ["T"] = "Cazador de faltriqueras de agua",
  },
  [1708] = {
    ["D"] = "Quiero probar las nuevas técnicas de Furen Barbalarga en mi propia armadura. Y como tú ayudaste a Furen y él me ayudó a mí, seguro que te gustará ver la armadura terminada.$B$BPara terminar la pieza necesito un coral muy especial, coral abrasador. Solo crece en un lugar, a lo largo de la costa sur del Puerto de Menethil, en Los Humedales.$B$BTráeme suficiente coral abrasador y estaré listo para trabajar en la armadura. Date prisa, $N. ¡Tengo tantas ganas de empezar que me estoy mordiendo las uñas!",
    ["O"] = "Lleva 20 piezas de coral férreo a Klockmort Palmalicate en Forjaz.",
    ["T"] = "Coral férreo",
  },
  [1710] = {
    ["D"] = "Mi compañero, Furen Barbalarga, ha hecho un descubrimiento que estoy ansioso por probar. Me gustaría probarlo en una nueva armadura que estoy desarrollando, porque creo que las técnicas de Furen darán resultados aún mejores.$B$B¿Me ayudarás? Si reúnes lo que necesito para hacer la armadura haré una pieza extra para ti.$B$BNecesito cáscaras asoladas de huevo del dracoleón de Nido Alto que vive en Nido Alto, en Las Mil Agujas. Busca las cáscaras bajo los nidos y vuelve a verme cuando tengas suficientes.",
    ["O"] = "Lleva 20 cáscaras asoladas a Mathiel en Darnassus.",
    ["T"] = "Cáscaras asoladas",
  },
  [1712] = {
    ["D"] = "He pasado años observando este sitio. Vigilo para poder ver un día el descenso del gran Ciclonio. Pero todavía no he encontrado a ningún campeón que pueda derrotar al Señor del viento.$B$BQuizás tú seas ese campeón.$B$BDemuéstramelo. Reúne los objetos necesarios para invocarle y sabré que eres $Gun gran guerrero:una gran guerrera;. Quizás entonces puedas derrotarle y hacerte con su corazón de espirálico.$B$BSi lo haces imbuiré el poder del corazón en un arma y te la daré a ti.$B$BEste es el papiro con los objetos que necesito.",
    ["O"] = "Lleva los objetos sobre el papiro de Bath\'rah a Bath\'rah el Vigía del viento en Alterac.",
    ["T"] = "Ciclonio",
  },
  [1713] = {
    ["D"] = "Estoy listo para el ritual de invocación de Ciclonio. Cuando haya sido invocado debes vencerle y hacerte con su corazón de espirálico. Lo utilizaré para imbuir su poder en un arma.$B$B$N, ¿estás $Glisto:lista; para enfrentarte al Señor del viento?",
    ["O"] = "Lleva el corazón de espirálico a Bath\'rah el Vigía del viento.",
    ["T"] = "La invocación",
  },
  [1715] = {
    ["D"] = "Bah. Antes, todos los brujos de Khaz Modan estudiaban con los maestros de Gnomeregan, pero ahora que hemos tenido que venir a este pueblucho que es Forjaz y que Magni ha decidido vigilarnos, tenemos que enviar a los novicios a estudiar a Ventormenta.$B$BPor suerte, con Gakin aprenderás todo lo que necesites saber, pero aun así, ¡este asunto me hace hervir la sangre!$B$BLo encontrarás en el sótano de El Cordero Degollado en Ventormenta.",
    ["O"] = "Preséntate ante Gakin el Presotenebra en el Barrio de los Magos de Ventormenta.",
    ["T"] = "El Cordero Degollado",
  },
  [1716] = {
    ["D"] = "Un súcubo es un devorador de almas, un destructor de corazones y un dominador de mentes. Lo único que sabe es hacer el mal.$B$BEsa será la criatura que aprenderás a invocar y controlar, pero no podemos traerla a nuestro mundo sin un estímulo.$B$BCon un símbolo de amor como cebo, seguramente atenderá a tu llamada. Después podrás derrotarlo y convertirlo en tu herramienta.$B$BDe todas formas, no será fácil encontrar algo así. Deberías hablar con Takar el Vidente. Le encontrarás en Los Baldíos de Kalimdor, cerca del Campamento Taurajo.",
    ["O"] = "Habla con Takar el Vidente en Los Baldíos.",
    ["T"] = "Devoradora de almas",
  },
  [1717] = {
    ["D"] = "¡$N! No esperaba verte por aquí, pero se me ha ordenado estar pendiente de tu llegada.$B$BYa es hora de proseguir tu formación; ve a ver a Gakin cuando pases por Ventormenta. Te estará esperando.",
    ["O"] = "Preséntate ante Gakin el Presotenebra en el Barrio de los Magos de Ventormenta.",
    ["T"] = "La invocación de Gakin",
  },
  [1718] = {
    ["D"] = "Tu fama como $Gguerrero:guerrera; no deja de crecer, $N. Ha llegado el momento de enfrentarte a tus pares y ver cuál es tu posición entre ellos.$B$BHay una isla en la costa de Los Baldíos, al sur de Trinquete. Es conocida como Isla de Batalla y en ella se encuentran los guerreros.$B$BSe encuentran y combaten.$B$BHabla con el jefe guerrero de Isla de Batalla, Klannoc Macleod. Si te juzga $Gdigno:digna; te guiará por la senda del guerrero.$B$BPara llegar a Isla de Batalla, busca el banco de arena que cubre La Costa Mercante.",
    ["O"] = "Habla con Klannoc Macleod.",
    ["T"] = "El Isleño",
  },
  [1719] = {
    ["D"] = "Si quieres instruirte conmigo tendrás que superar La Reyerta. Es un desafío que ofrecemos aquí, en Isla de Batalla, a los guerreros que se creen fuertes, si superas la prueba sabremos que lo eres.$B$BPara empezar dirígete al centro de esa multitud y ponte en la rejilla. Así indicarás a Twiggy Cabezaplana que estás $Gpreparado:preparada; y enviará a tus contrincantes.$B$BDerrótalos y llamará a Will el Grande. Mata a Will el Grande y vuelve a verme dentro del tiempo permitido.",
    ["O"] = "Mata a Will el Grande y habla con Klannoc Macleod en Isla de Batalla.",
    ["T"] = "La Reyerta",
  },
  [1739] = {
    ["D"] = "Utilizando el corazón del duramen, podrás lanzar un hechizo en el Círculo de Invocación de la cripta subterránea para abrir un portal y que pueda pasar el súcubo.$B$BTen cuidado, $N, algunos súcubos han dominado incluso a las mentes más fuertes.$B$BCuando hayas vencido y lo devuelvas al vacío, vuelve a verme y te enseñaré a invocarlo y controlarlo.",
    ["O"] = "Utilizando el corazón del duramen, invoca y somete a un súcubo. Después devuelve el corazón del duramen a Gakin el Presotenebra en El Cordero Degollado.",
    ["T"] = "El vínculo",
  },
  [1740] = {
    ["D"] = "Cuando los demonios de la Legión caen en desgracia, sus espíritus se encarcelan en unos orbes que se distribuyen entre sus magos y brujos.$B$BMuchos fueron llevados por sus sirvientes en Azeroth. Incluso rotos en trozos, poseen los poderes de los demonios atrapados en ellos.$B$BEl orbe de Soran\'ruk se partió en dos piezas, una de las cuales se rompió en pedazos aún más pequeños para los acólitos del Martillo Crepuscular de las Cavernas de Brazanegra. La otra, la de mayor tamaño, acabó en manos de un mago del Castillo de Colmillo Oscuro. Encuéntralas y podré reconstituirlo.",
    ["O"] = "Encuentra 3 fragmentos de Soran\'ruk y 1 fragmento grande de Soran\'ruk y llévaselos a Doan Karhan en Los Baldíos.",
    ["T"] = "El orbe de Soran\'ruk",
  },
  [1758] = {
    ["D"] = "Hará falta algo más que un simple círculo de conjuro para invocar a un manáfago al que quieres desafiar.$B$BMis tres acólitos y yo te ayudaremos a crear los círculos mágicos. Necesitas un ejemplar del libro del conciliábulo, que lleva un tiempo desaparecido. Lo escribieron los primeros brujos que invocaron a un manáfago por sus propios medios.$B$BA lo mejor Krom Brazorrecio puede ayudarnos. Hoy en día es un brujo itinerante, se pasa el tiempo acumulando textos antiguos y merodeando por la biblioteca de Forjaz.",
    ["O"] = "Habla con Krom Brazorrecio en Forjaz.",
    ["T"] = "Libro del conciliábulo",
  },
  [1778] = {
    ["D"] = "Voy a empezar a hacer planes para volver pronto a casa. Espero que a Stephanie le vaya bien en Ventormenta. Estoy deseando volver a verla. Hasta la próxima, $C.",
    ["O"] = "Ve a Forjaz y habla con Tiza Forjabatalla.",
    ["T"] = "Escrito sobre divinidad",
  },
  [1779] = {
    ["D"] = "Quiero que cojas esto, $N. Se llama Símbolo de la Vida.$B$BHas demostrado tener paciencia y caridad y ahora quiero que pruebes tus aptitudes y tu poder en combate.$B$BMi marido Muiredon tuvo hace poco un encuentro con unos enanos Hierro Negro en el que casi pierde la vida. Y ahí está... Uno de nuestros sacerdotes está curando sus heridas.$B$BCoge el Símbolo y habla con él. Si puedes ayudarle, volveremos a hablar. Pronto. Que la Luz sea contigo.",
    ["O"] = "Coge el Símbolo de la Vida y habla con Muiredon Forjabatalla. Está en Forjaz.",
    ["T"] = "Escrito sobre divinidad",
  },
  [1780] = {
    ["D"] = "Llevaré esto a los niños hoy mismo. Cuídate, por favor, $Gbuen:buena; $C. No encuentra una a menudo personas como tú en la ciudad. Has demostrado que los paladines de Ventormenta nunca vuelven la espalda a quienes los necesitan.$B$BAdiós.",
    ["O"] = "Habla con Duthorian Rall en Ventormenta.",
    ["T"] = "Escrito sobre divinidad",
  },
  [1781] = {
    ["D"] = "Coge esto, $N. Se llama Símbolo de la Vida.$B$BHas demostrado ser paciente y $Gcaritativo:caritativa; y ahora ha llegado el momento de ver si tienes poder suficiente para centrarte en uno de los mayores dones de la Luz.$B$BMi amigo Gazin tuvo hace poco un encuentro con unos Defias en Elwynn, cerca de la Vigilia de los Héroes... casi le cuesta la vida. Está cerca del altar. Uno de nuestros sacerdotes está curando sus heridas.$B$BLlévale el Símbolo y habla con él. Si puedes ayudarle, volveremos a hablar. Pronto. Que la gloria de la Luz sea contigo.",
    ["O"] = "Lleva el Símbolo de la Vida a Gazin Tenorm a Ventormenta.",
    ["T"] = "Escrito sobre divinidad",
  },
  [1783] = {
    ["D"] = "Estábamos espiando a unos enanos Hierro Negro que se habían establecido en un búnker al sur del Lago de Helm. Sabíamos que no tramaban nada bueno. Por eso Narm y yo quisimos echar un vistazo de cerca. Vimos a su líder dando órdenes a sus hombres.$B$BNarm y yo aprovechamos la ocasión para matar a algunos de ellos, pero enseguida llegaron más. Y eran más fuertes de lo que creíamos. Narm me dijo que corriera a buscar refuerzos, así que aquí estoy.$B$BNarm necesita ayuda y no descansaré hasta que la consiga.$B$B¿Puedes ayudarnos, $N?",
    ["O"] = "Coge el Símbolo de la Vida y resucita a Narm Faulk en Dun Morogh.",
    ["T"] = "Escrito sobre divinidad",
  },
  [1784] = {
    ["D"] = "Antes de que yo cayera en combate, Muiredon y yo casi habíamos derrotado a uno de los espías Hierro Negro cerca de esa casa pequeña. Vimos cómo su capitán repartía órdenes e intentamos matar a uno de ellos y hacernos con esas órdenes.$B$BEn el Consejo de Forjaz hay quien todavía no cree que en Dun Morogh haya enanos Hierro Negro. Si conseguimos esas órdenes, no solo tendremos una prueba de su presencia, sino que además, podremos hacernos una idea de lo que planean.$B$BConsigue una de esas órdenes y llévasela a Muiredon, $N.",
    ["O"] = "Consigue una misiva Hierro Negro de los enanos Hierro Negro que están cerca de Helm y luego vuelve a Forjaz y busca a Muiredon Forjabatalla.",
    ["T"] = "Escrito sobre divinidad",
  },
  [1785] = {
    ["D"] = "Ya\'stoy viendo a mi mujer hinchándose d\'orgullo. Más te vale ir yendo p\'allá ya mismo. Creo que querrá echarse unas palabras contigo.$B$BQue la Luz t\'acompañe, potente $C. Te buscaré en el campo de batalla cuando llegue el momento de luchar contra la Plaga y los Hierro Negro esos.",
    ["O"] = "Ve a Forjaz y habla con Tiza Forjabatalla.",
    ["T"] = "Escrito sobre divinidad",
  },
  [1786] = {
    ["D"] = "Henze y yo conseguimos llegar a la Vigilia de los Héroes, una isla que hay en el centro del Lago del Hito, al noreste de Elwynn. Habíamos oído que los Defias habían cruzado nuestras fronteras y queríamos conseguir pruebas para los consejeros del Rey.$B$BVimos a un par de esos tipos leyendo unos documentos y atacamos. Por desgracia, llegaron más magos y pronto nos vimos superados.$B$BHenze los contuvo mientras yo escapaba... ese tonto, haciéndose el héroe...$B$BAhora está ahí, solo... ¿Puedes ayudarnos, $N?",
    ["O"] = "Coge el Símbolo de la Vida y resucita a Henze Faulk en Elwynn.",
    ["T"] = "Escrito sobre divinidad",
  },
  [1787] = {
    ["D"] = "Ya tendremos ocasión de hablar sobre el Símbolo más adelante. Lo importante ahora es conseguir esos papeles que tenían los Defias. Si logramos hacernos con ellos, podremos probar que la banda Defias se ha infiltrado en Elwynn y, con suerte, averiguaremos cuáles son sus planes.$B$BYo ahora no estoy en disposición de combatir, $N. ¿Crees que tienes la fuerza necesaria para arrebatar esos papeles a los Defias de la isla?$B$BSi consigues hacerte con ellos, llévaselos a Gazin. Y dile también que estoy bien.",
    ["O"] = "Recupera 1 misiva Defias de los zahoríes bribones Defias de Elwynn y vuelve con Gazin Tenorm a Ventormenta.",
    ["T"] = "Escrito sobre divinidad",
  },
  [1788] = {
    ["D"] = "Cuando Duthorian haya echado un vistazo a este texto, se lo llevaré a los consejeros del Rey. Pero tú deberías ir a hablar con Duthorian ya. ¡Querrá darte una palmadita en la espalda!$B$BGracias otra vez, $N. Espero que volvamos a encontrarnos. Eres $Gun aliado al:una aliada a la; que me enorgullece considerar $Gun amigo:una amiga; también.",
    ["O"] = "Habla con Duthorian Rall en Ventormenta.",
    ["T"] = "Escrito sobre divinidad",
  },
  [1791] = {
    ["D"] = "Ahora que has aprendido la actitud rabiosa, querrás tener una buena arma.$B$BVe a hablar con Bath\'rah el Vigía del viento, un ermitaño trol que vive entre las Montañas de Alterac y las Tierras de la Peste del Oeste. Es un forjador de armas muy diestro y un poderoso chamán.$B$BPara encontrar a Bath\'rah ve hasta el río que fluye en el lado oriental del Molino Tarren. Síguelo hacia el Norte. Bath\'rah está acampado junto a las ruinas que encontrarás en la orilla oriental del río.",
    ["O"] = "Habla con Bath\'rah el Vigía del viento.",
    ["T"] = "El Vigía del viento",
  },
  [1793] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Escrito sobre valor",
  },
  [1794] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Escrito sobre valor",
  },
  [1795] = {
    ["D"] = "Si estás suficientemente $Gpreparado:preparada;, deberíamos empezar. Permite que te explique el proceso.$B$BEmpezaré por crear el círculo de conjuro básico. Cuando este esté acabado, mis acólitos usarán las varas de mediación para crear el círculo más grande.$B$BUna vez hecho esto, leerás los conjuros del libro del conciliábulo y el manáfago será invocado. Ten cuidado, $N. Un manáfago es un poderoso enemigo y me gustaría que conservaras la cabeza sobre los hombros.",
    ["O"] = "Mediante el libro del conciliábulo invoca y subyuga a un manáfago y luego devuélvele el Libro del Conciliábulo a Strahad Farsan en Trinquete.",
    ["T"] = "El vínculo",
  },
  [1796] = {
    ["D"] = "El primer objeto que te ayudaré a crear es una toga. Tú recolectarás los elementos para mí y yo los uniré. Después, y siempre que lo consigas, obtendrás el primero de muchos objetos que te harán más $Gpoderoso:poderosa; y que te ayudarán en tus esfuerzos.$B$BPara empezar, tendrás que haber confeccionado togas de Arcana. Un sastre puede hacer esto por ti, o quizás puedas comprarle una a alguien, pero estaría de lo más impresionada si la hicieras tú $Gmismo:misma; pero sea como fuere, tráeme las togas y ya veremos.",
    ["O"] = "Llévale las togas de Arcana a Menara Nadiz en Los Baldíos.",
    ["T"] = "Los componentes para la toga de sangre dorada encantada",
  },
  [1798] = {
    ["D"] = "Es hora de que aprendas a invocar a un manáfago, $N. No tengo los componentes ni los objetos necesarios para enseñarte aquí, así que tendrás que buscar a otro, Strahad Farsan, otro de nuestro círculo interno.$B$BVive en el puerto goblin de Trinquete. Enviaré un mensaje para informarle de tu llegada.",
    ["O"] = "Habla con Strahad Farsan en Los Baldíos.",
    ["T"] = "Buscando a Strahad",
  },
  [1799] = {
    ["D"] = "Bien, entonces empezaremos.$B$BPrimero debes hablar con mis acólitos de ahí adentro. Elige uno de los caminos que te ofrezcan, ellos te darán más detalles.$B$BEn segundo lugar, debes viajar hasta Desolace y encontrar un orbe infernal en manos de uno de los brujos del Aquelarre Mannoroc. Este es el orbe que limpiarás con la ayuda de una maga llamada Tabetha que se encuentra en lo más recóndito al sur de Marjal Revolcafango. Te ayudará por nuestro pasado común.$B$BInsisto, no vuelvas hasta que hayas completado las dos cosas que te he pedido.",
    ["O"] = "Habla con los acólitos de Menara que están en el interior de la torre que domina Trinquete y elige uno de sus caminos para seguirlo.$B$BDespués, llévale un orbe infernal a Tabetha en Marjal Revolcafango.",
    ["T"] = "Los trozos del orbe de Orahil",
  },
  [1800] = {
    ["D"] = "Por la noche, si éramos buenos y hacíamos los deberes, las supervisoras de huérfanos nos contaban historias impresionantes de combates y tradiciones. Una historia era la de la caída de Lordaeron y de cómo el malvado Arthas mató a su propio padre para hacerse con el poder.$B$BNo tiene ningún honor, $N. Cuando crezca y sea un poderoso guerrero de la Horda, mi espada estará guiada por el honor, ¡nunca por el mal!$B$B¿Me quieres llevar a ver la sala del trono, por favor? Está justo antes de llegar a la entrada de Entrañas.",
    ["O"] = "Lleva a tu pupilo a la antigua Sala del Trono de Lordaeron. La encontrarás justo antes de llegar al acceso a Entrañas.|n",
    ["T"] = "Sala del Trono de Lordaeron",
  },
  [1801] = {
    ["D"] = "Hará falta algo más que un simple círculo de conjuro para invocar a un manáfago al que quieres desafiar.$B$BMis tres acólitos y yo te ayudaremos a crear los círculos mágicos. Necesitas un ejemplar del libro del conciliábulo, que lleva un tiempo desaparecido. Lo escribieron los primeros brujos que invocaron a un manáfago por sus propios medios.$B$BA lo mejor Jorah Annison puede ayudarte. Ahora trabaja de cartógrafo en Entrañas pero sigue siendo el que más sabe de textos antiguos de todos mis conocidos.",
    ["O"] = "Habla con Jorah Annison en Entrañas.",
    ["T"] = "Libro del conciliábulo",
  },
  [1802] = {
    ["D"] = "Se cree perdido, pero sospecho dónde podría estar.$B$BSolo hay un ejemplar y en algún momento alguien le arrancó una gran parte. Se dice que pasó a manos de los centauros de Las Mil Agujas.$B$BEl resto se perdió en la costa al oeste de Costasur, desde donde iba a ser transportado por barco al Puerto de Menethil. Nunca llegó a su destino.$B$BLo único que te pido a cambio de esta información es que me lo traigas si lo encuentras.",
    ["O"] = "Lleva el escrito mohoso y el manuscrito maltrecho a Krom Brazorrecio a Forjaz.",
    ["T"] = "Libro del conciliábulo",
  },
  [1803] = {
    ["D"] = "¡Ah, veo que eres $Gun brujo:una bruja; con gustos similares a los míos! De hecho, la adquisición del libro del conciliábulo es una noble aspiración. ¿Sabías que una vez estuvo guardado entre los muros de Lordaeron? Pero lo destrozaron y arrancaron casi la mitad de sus páginas. ¿Quién podría estar detrás de un acto tan atroz?$B$BEsa mitad se perdió, aunque algunos rumores apuntan a que cayó en manos de los centauros de Las Mil Agujas.$B$BEl resto del texto debía ser conducido a Forjaz pero se perdió por el camino en la costa occidental de Costasur.",
    ["O"] = "Recupera el escrito mohoso y el manuscrito andrajoso para Jorah Annison de Entrañas.",
    ["T"] = "Libro del conciliábulo",
  },
  [1804] = {
    ["D"] = "Verás, me cuesta separarme de él. Pero eres tú quien lo ha encontrado y lo necesitas para cosas más importantes. Solo te pido que le digas a Strahad que quería una copia.$B$BHa enviado un mensaje mientras estabas fuera; dice que, si quieres que sus acólitos participen en la invocación, tienes que conseguir tres varas de mediación.$B$BPuede que él no sepa dónde encontrarlas, ¡pero yo sí! Fueron usadas por última vez por los orcos Faucedraco en Los Humedales. Comprobaré sus libros de hechizos.",
    ["O"] = "Lleva el tomo restaurado y las 3 varas de mediación a Strahad Farsan a Trinquete.",
    ["T"] = "Libro del conciliábulo",
  },
  [1806] = {
    ["D"] = "¿Vamos a la forja y empezamos? Llevo tiempo esperando tu llegada, así que no tardaremos mucho.$B$BY entonces podrás defender la Luz mucho mejor de lo que nunca has podido.$B$BTe diré la verdad, $N: ver el fruto de tus esfuerzos me da cierta envidia. Pero poder ofrecerte mi humilde ayuda me llena de alegría.",
    ["O"] = "Espera mientras Jordan Fontana te forja un arma.",
    ["T"] = "La prueba de rectitud",
  },
  [1819] = {
    ["D"] = "Cuando la Plaga se extendió por los Claros de Tirisfal, capturamos a uno de ellos para someterlo a estudio. Tenía una mente libre, se llamaba a sí mismo Ulag el Cuchilla. Pero, aunque era libre, seguía cautivado por el Rey Exánime.$B$BEsperábamos encontrar un modo de romper el control del rey sobre Ulag, pero fracasamos. Ahora, por fin, ha llegado el momento de liberarle.$B$BLibérale y mátale.$B$BActiva el disparador de piedra calavera junto al mausoleo del Norte. Así levantarás el sello que le mantiene atado.$B$BCuando lo hagas... estate $Gpreparado:preparada;, $N.",
    ["O"] = "Mata a Ulag el Cuchilla y ve a hablar con el Guardia de la Muerte Dillinger.",
    ["T"] = "Ulag el Cuchilla",
  },
  [1820] = {
    ["D"] = "Has demostrado tu valor a la Dama Oscura, quizás ahora puedas probarte ante uno de sus súbditos.$B$BColeman Farthing aguarda a un guerrero que quiera convertirse en su instrumento de venganza contra los enemigos de los Renegados.$B$BLo encontrarás en el Mesón La Horca, en la parte oriental de Rémol.",
    ["O"] = "Habla con Coleman Farthing.",
    ["T"] = "Habla con Coleman",
  },
  [1821] = {
    ["D"] = "Los miembros de la familia Agamand fueron mis patrones en vida, ¡y también fueron la causa de mi muerte! ¡Qué apropiado que los Agamand sean ahora unos esbirros insignificantes del Rey Exánime!$B$BMe gustaría tener los últimos vestigios de su honor. Ve a la cripta de la familia y róbales sus reliquias familiares: una espada, un hacha, una daga y una maza. ¡Tráemelos!$B$BLa Cripta de la Familia Agamand está en el extremo norte de los Molinos de Agamand, protegida por la Plaga y por los Agamand muertos.",
    ["O"] = "Lleva a Coleman Farthing el hacha de la familia Agamand, la espada de la familia Agamand, la maza de la familia Agamand y la daga de la familia Agamand.",
    ["T"] = "Reliquias Agamand",
  },
  [1823] = {
    ["D"] = "Eres $Gun guerrero:una guerrera; de fama y talento. ¿Estás $Gpreparado:preparada; para la prueba de Ruga Tótem de Ira? Si lo estás, encontrarás a Ruga en Campamento Taurajo, en Los Baldíos.$B$BEstá esperando a todo guerrero que responda a su llamada.",
    ["O"] = "Habla con Ruga Tótem de Ira.",
    ["T"] = "Habla con Ruga",
  },
  [1824] = {
    ["D"] = "Para superar la prueba debes viajar hacia el Sur, hasta Tierra de Gigantes. Allí encontrarás gran cantidad de criaturas reptantes, insectos. Son nuevas en Los Baldíos y no me gusta lo que presagian.$B$BTráeme sus antenas cuando aún sigan agitándose y no pierdas tiempo, $N, o las antenas dejarán de agitarse. Tráeme las antenas en el tiempo previsto y superarás la prueba.",
    ["O"] = "Lleva 5 antenas agitadas a Ruga Tótem de Ira de Campamento Taurajo.",
    ["T"] = "Prueba en Tierra de Gigantes",
  },
  [1838] = {
    ["D"] = "He oído hablar de tu fuerza y valor y me gustaría hacerte una armadura, una compañera que te ayudará en tus nuevas hazañas.$B$BPero la elaboración de la armadura será una auténtica misión, $N. Necesitaré tu ayuda y la de otros herreros de la Horda.$B$BToma, coge este pergamino. He pasado muchas noches luchando con mis ojos casi ciegos, escribiendo lo que necesito para la armadura. Tráeme los objetos que aparecen en el pergamino y crearé la primera pieza de la armadura.",
    ["O"] = "Lleva 15 lingotes de hierro humeantes, 10 azuritas en polvo, 10 barras de hierro y un vial de flogisto a Thun\'grim Vistafuego.",
    ["T"] = "Armadura brutal",
  },
  [1840] = {
    ["D"] = "Orm Pezuña Pétrea es muy respetado por sus habilidades. Te hará el yelmo Brutal.$B$BLo encontrarás en Cima del Trueno, cerca del estanque, junto al gran tótem central.",
    ["O"] = "Habla con Orm Pezuña Pétrea en Cima del Trueno.",
    ["T"] = "Orm Pezuña Pétrea y el yelmo brutal",
  },
  [1842] = {
    ["D"] = "Puedo hacerte un par de guanteletes brutales que tendrán una enorme fuerza mágica. Pero antes debes reunir esa magia y traérmela.$B$BLos sátiros habitan en la parte noreste de Vallefresno por el poder del bosque. Se alimentan de su pureza, corrompiéndola y exprimiéndola. Puedo decirte cuáles son los sátiros que extraen la mayor parte de la magia de Vallefresno con sus pezuñas hinchadas y unguladas.$B$BEsas pezuñas albergan un poder enorme. Las necesitaremos para elaborar tus guanteletes.",
    ["O"] = "Lleva 7 pezuñas de sátiro sin rajar a Ula\'elek de Poblado Sen\'jin en Durotar.",
    ["T"] = "Pezuñas de sátiro",
  },
  [1844] = {
    ["D"] = "Te haré el yelmo, pero debes demostrar que lo mereces.$B$BEscondida en el límite suroeste de la Sierra Espolón encontrarás La Vega Carbonizada. Fue un día un auténtico vergel, pero hoy está desolada y reducida a cenizas y las criaturas que quedaron allí están locas de terror y desesperación.$B$BAllí es a donde te envío, $N.$B$BDebes ir a La Vega Carbonizada y encontrar una matriarca quimera. Mátala, libérala de su miseria y tráeme su cuerno galvanizado como prueba. Si lo haces te haré el yelmo.",
    ["O"] = "Lleva un cuerno galvanizado a Orm Pezuña Pétrea de Cima del Trueno.",
    ["T"] = "Cuerno de quimera",
  },
  [1859] = {
    ["D"] = "Baja la voz, $N. Sé quién eres. Tenía tu descripción y te esperaba.$B$BPor lo que he oído de ti, te manejas bien en combate y no te opones a alguna incursioncilla que otra de vez en cuando.$B$B¿Me equivoco?$B$BEs lo mismo. Tienes que ir a Orgrimmar a ver a Therzok. Tiene una oferta que hacerte.",
    ["O"] = "Ve a Orgrimmar, al Circo de las Sombras, y habla con Therzok.",
    ["T"] = "Therzok",
  },
  [1860] = {
    ["D"] = "Los magos tenemos una tarea por hacer, y es vital para la seguridad de Ventormenta y del Bosque de Elwynn.$B$BJennea Cañón sabe los detalles. Estudia en el Sagrario del Mago, en el Barrio de los Magos de Ventormenta. Deberías ir a verla lo antes posible.",
    ["O"] = "Habla con Jennea Cañón en Ventormenta.",
    ["T"] = "Habla con Jennea",
  },
  [1861] = {
    ["D"] = "Este mundo ha padecido enormes luchas místicas, que desbordaron corrientes de magia de canales antes estables. Nos esforzamos en determinar los efectos que han causado esos cambios y hay quien teme lo peor.$B$BPero ya te hablaré de todo ello más adelante. Por ahora, necesitamos que reúnas información.$B$BToma, coge esta botella. Ve a la base de la cascada del Lago Espejo, al suroeste de las puertas de Ventormenta y recoge una muestra de agua. Vuelve a verme para que pueda comprobar si tiene contaminación mágica.",
    ["O"] = "Lleva una muestra del Lago Espejo a Jennea Cañón en Ventormenta.",
    ["T"] = "Lago Espejo",
  },
  [1878] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Cazador de faltriqueras de agua",
  },
  [1879] = {
    ["D"] = "¡$N! Bink, mi colega gnomo de Forjaz, ha enviado un mensaje. ¡Tiene una tarea que hacer y solo confía en un mago para hacerlo! ¿Serás tú ese mago, $N?$B$BSi es así, ve a hablar con Bink. La encontrarás en la Sala de los Misterios en La Sala Mística de Forjaz.",
    ["O"] = "Habla con Bink en Forjaz.",
    ["T"] = "Habla con Bink",
  },
  [1880] = {
    ["D"] = "¡Los magos hacemos mucho más que menear los dedos y sacar chispas! ¡Controlamos la magia, la estructura básica de nuestro mundo! Exige mucho cuidado, resolución y conocimiento... sí, ¡mucho conocimiento!$B$BUno de mis instrumentos, el trastónitor maguitrónic recoge y almacena ese conocimiento en grandes cantidades, ¡pero lo he perdido! Cuando dejamos nuestro hogar, Gnomeregan, lo dejé en un cofre en una de las cabañas a las puertas de la ciudad. ¡Y ahora el lugar está plagado de gnomos leprosos!$B$B¡Por favor, $N, recupera mi trastónitor!",
    ["O"] = "Llévale a Bink su trastónitor maguitrónic.",
    ["T"] = "Trastónitor maguitronic",
  },
  [1881] = {
    ["D"] = "$N, Anastasia Hartwell tiene una misión para alguien con tus aptitudes. Habla con ella en el Barrio de la Magia en Entrañas y prepárate para tu primera tarea como mago de los Renegados.",
    ["O"] = "Habla con Anastasia Hartwell.",
    ["T"] = "Habla con Anastasia",
  },
  [1882] = {
    ["D"] = "Las Tierras de la Peste del Este son una aberración. Ideadas por el Rey Exánime, propagan su magia a la tierra, corrompiendo y matando toda forma de vida. Aunque las fuerzas destructivas de la Peste tienen un cierto encanto, nosotros, los magos de los Renegados, creemos que es indomable y que debe ser reducida.$B$BPero antes de nada, debemos medir hasta qué punto se ha propagado en Tirisfal. Ve a la Hacienda Balnir en la parte oriental de Tirisfal. En el jardín encontrarás flores: Bocas de dragón. Recógelas y vuelve a verme.",
    ["O"] = "Lleva las Bocas de dragón de Balnir a Anastasia Hartwell en el Barrio de los Magos de Entrañas.",
    ["T"] = "La Hacienda Balnir",
  },
  [1884] = {
    ["D"] = "El brujo Zalazane de las Islas del Eco ha interrumpido el flujo normal de la magia en Durotar. Lo hace a través de fetiches corruptos, montones de Juju, que ha colocado entre sus cabañas en la Isla del Eco principal.$B$BEstos montones corrompen nuestra naturaleza y dan poder a los brujos y a las criaturas infernales que merodean las zonas más oscuras del país.$B$BDebes destruir los montones de Juju, $N, antes de que el flujo de magia quede alterado para siempre.",
    ["O"] = "Destruye 4 montones de Juju y vuelve con Un\'thuwa en Poblado Sen\'jin.",
    ["T"] = "Montones de Ju-Ju",
  },
  [1885] = {
    ["D"] = "¿$N? No eres exactamente como esperaba, teniendo en cuenta que el mismísimo Carkad se ha interesado por ti. Si bien es cierto que la primera impresión no siempre es la que cuenta, ¿no crees?$B$BEn cualquier caso, deberías ir a verlo tan pronto como puedas. No te gustaría saber lo que les ocurrió a los que hicieron caso omiso a su llamada.",
    ["O"] = "Habla con Mennet Carkad en el Barrio de los Pícaros de Entrañas.",
    ["T"] = "Mennet Carkad",
  },
  [1886] = {
    ["D"] = "Si quieres formar parte de los mortacechadores, la guardia de élite de Lord Varimathras, tendrás que demostrar tu valía.$B$BNo quiero mentirte, $N, esta misión puede suponer tu muerte.$B$BYo tengo otras cosas que requieren de mi atención. Necesito los efectos personales de Astor Hadren, un mensajero que viaja entre el Bosque de Argénteos y Entrañas. Te dejo a tu elección el método que debes utilizar.$B$BCuanto menos sepas sobre la naturaleza de su negocio, mejor para ti. Si realizas con éxito está misión, te contaré más.",
    ["O"] = "Consigue la carta de presentación de Astor y devuélvesela a Mennet Carkad, en el Barrio de los Pícaros.",
    ["T"] = "Los mortacechadores",
  },
  [1898] = {
    ["D"] = "La carta de presentación debería ser suficiente para convencer a Andron de que tú eres $Gel mensajero:la mensajera; que estaba esperando. Conozco bien a Andron y sé que no es demasiado desconfiado.$B$BLord Varimathras sospecha que Andron ha estado ayudando a otro grupo. Desea saber de qué organización se trata y quiere pruebas.$B$BQuiero que vayas a hablar con Andron y que obtengas esta información. Lo encontrarás en su tienda, cerca de El Apothecarium.",
    ["O"] = "Habla con Andron Gant en Entrañas.",
    ["T"] = "Los mortacechadores",
  },
  [1899] = {
    ["D"] = "Encontrarás el libro de contabilidad con la información que buscan los que te envían en la estantería que está a mis espaldas. Llévaselo y diles que preferiría que te enviaran a ti en caso de que deseen seguir adelante con nuestro plan.",
    ["O"] = "Llévale el libro de contabilidad de Andron a Mennet Carkad, que está en el Barrio de los Pícaros de Entrañas.",
    ["T"] = "Los mortacechadores",
  },
  [1918] = {
    ["D"] = "El elemental caído ha producido un globo de agua de brillo intenso. El contenido del globo es, cuando menos, repulsivo. No me parece descabellado suponer que lo que contaminara el Lago Mystral se encontrase casi con toda seguridad en el agua del globo.$B$BNo es fácil saber quién podría estar interesado en semejante hallazgo; quizás Mastok Bufido del Puesto del Hachazo, podría tener una idea.",
    ["O"] = "Llévale el globo de agua contaminada a Mastok Bufido en el Puesto del Hachazo, Vallefresno.",
    ["T"] = "El elemento contaminado",
  },
  [1919] = {
    ["D"] = "Tu conocimiento sobre esta disciplina no deja de crecer, joven $N. ¿Estás $Glisto:lista; para comenzar las auténticas lecciones? ¿Estás $Glisto:lista; para ocupar tu lugar en la guerra secreta en la que estamos embarcados?$B$BSi es así, ve a hablar con Jennea Cañón, en el Sagrario del Mago, en el Barrio de los Magos de Ventormenta. Tiene una tarea para ti y esa tarea te abrirá la primera ventana al mundo de lo invisible.",
    ["O"] = "Habla con Jennea Cañón en Ventormenta.",
    ["T"] = "Preséntate ante Jennea",
  },
  [1920] = {
    ["D"] = "Han llegado informes de una alteración en la taberna El Ermitaño Taciturno del Barrio de los Magos. Criaturas invisibles y extrañas vagan por la taberna y temo que se haya formado una falla de maná.$B$BQuiero que investigues la taberna y que captures a esas criaturas. Utiliza el Canto de manifestación en la taberna para hacer visibles a las criaturas, cuando las tengas dominadas utiliza un arca de contención para capturarlas. Cuando tengas bastantes vuelve a verme trayendo contigo las arcas llenas y vacías y el Canto.",
    ["O"] = "Detrás de Jennea Cañón encontrarás un Canto de manifestación y un cofre de arcas de contención. Cógelos y lleva 3 arcas de contención llenas a Jennea, en el Sagrario del Mago.",
    ["T"] = "Investiga lo que pasa en El Ermitaño Taciturno",
  },
  [1921] = {
    ["D"] = "Has servido bien a tus compañeros magos y ha llegado el momento de vestir tu túnica de mago. Wynne Larson te hará la túnica, pero debes llevarle los materiales necesarios.$B$BLa túnica lleva lino, que es muy fácil de encontrar, pero también debes traer gemas cargadas de la falla que solo encontrarás en la Mina de Fuenteplata de Loch Modan, en los cajones de la Liga de Mineros enanos.$B$BReúne el lino y las gemas y llévaselos a Wynne Larson en Diseños Larson del Barrio de los Magos.",
    ["O"] = "Consigue 10 paños de lino y 6 gemas cargadas de la falla para Wynne Larson en Ventormenta.",
    ["T"] = "Recolección de materiales",
  },
  [1938] = {
    ["D"] = "Hace mucho tiempo, tres magos estudiaban en esta torre hasta que uno de ellos sucumbió a las artes oscuras. Se le instó a apartarse de esa magia maligna, pero no pudo resistirse. Así que fue condenado al exilio.$B$BLos nuevos dominios del mago caído están en la Torre de Ilgalar en Crestagrana, a donde llevó un poderoso libro: el Tratado de Ur sobre la Magia de las Sombras.$B$BNosotros, los magos de Ventormenta, queremos recuperar el libro. Entra en la Torre de Ilgalar, encuentra el tomo en su biblioteca y tráemelo.",
    ["O"] = "Lleva el Tratado de Ur sobre la Magia de las Sombras al sumo hechicero Andromath en Ventormenta.",
    ["T"] = "Tratado de Ur sobre la magia de las Sombras",
  },
  [1939] = {
    ["D"] = "El sumo hechicero Andromath quiere hablar contigo, $N. Está en la parte superior del Sagrario del Mago, en el Barrio de los Magos de Ventormenta.$B$BDeberías ir a hablar con él, Andromath es uno de los magos más poderosos fuera de Dalaran. Su consejo es, sin duda, muy valioso.",
    ["O"] = "Habla con el sumo hechicero Andromath.",
    ["T"] = "Sumo hechicero Andromath",
  },
  [1940] = {
    ["D"] = "Tus servicios han sido inquebrantables, $N. Creo que quizás ha llegado el momento de que tengas unas prendas más delicadas, más adecuadas a tu posición como mago adepto. Si reúnes los materiales necesarios, te haremos la túnica.$B$BTendrás que adentrarte en el Bosque del Ocaso y cazar arañas pardas carroñeras. Extrae su seda, pero solo la más delicada, la seda prístina es la más indicada. Cuando tengas suficiente seda, llévasela a Wynne Larson, en el Barrio de los Magos, en Diseños Larson. Ella hará tu túnica.",
    ["O"] = "Lleva 8 sedas prístinas de araña a Wynne Larson en Ventormenta.",
    ["T"] = "Seda prístina de araña",
  },
  [1943] = {
    ["D"] = "Deino, la maga trol, necesita tu ayuda, $N. Vive en Orgrimmar, en la Cabaña de Brezo.",
    ["O"] = "Habla con Deino.",
    ["T"] = "Habla con Deino",
  },
  [1948] = {
    ["D"] = "Crear la varita de un mago no es fácil. Se necesitan sustancias extrañas y debe hacerse un recipiente que pueda almacenar grandes cantidades de energía mágica. Debes reunir estas cosas y traérmelas.$B$BHe escrito en este papiro todo lo que necesitas e instrucciones para conseguirlo. Tráeme exactamente lo que está en la lista y sigue las indicaciones al pie de la letra, si no la varita podría hacer cosas... no muy buenas.",
    ["O"] = "Lleva 1 jade y la zarza electrificada a Tabetha en el Marjal Revolcafango.",
    ["T"] = "Objetos de poder",
  },
  [1951] = {
    ["D"] = "Umm... ahora que lo pienso... ¡no tengo el libro! Verás, tenía algunas deudas de juego y para pagarlas vendí la última copia de Rituales de poder a un miembro del clero...$B$BUn miembro de la Hermandad Escarlata. ¡Debe haberlo llevado a su biblioteca en el Monasterio Escarlata de los Claros de Tirisfal!$B$BSi quieres el libro, tendrás que ir al monasterio a buscarlo. Y perdona por todo este lío... últimamente no soy yo mismo.",
    ["O"] = "Lleva el libro Rituales de poder a Tabetha en el Marjal Revolcafango.",
    ["T"] = "Rituales de Poder",
  },
  [1953] = {
    ["D"] = "$N, es hora de que vayas a hablar con Tabetha... la ermitaña del Marjal Revolcafango. Te instruirá sobre la creación de un instrumento muy poderoso para un mago.$B$BLa choza de Tabetha está en las profundidades del Marjal Revolcafango, al oeste de Theramore.",
    ["O"] = "Habla con Tabetha.",
    ["T"] = "Regresa a la Marisma",
  },
  [1954] = {
    ["D"] = "El primer paso es adquirir un orbe con poder. Los brujos del Filo Ardiente tienen orbes fantásticos, de gran calidad... ¡lo malo es que tienen atados demonios en su interior! Pero podemos arreglarlo, sí, podemos.$B$BVe a Desolace y hazte con un orbe infernal de un invocador Filo Ardiente. Encontrarás invocadores en el Aquelarre Mannoroc.$B$BCuando tengas el orbe, vuelve a verme.",
    ["O"] = "Lleva un orbe infernal a Tabetha en el Marjal Revolcafango.",
    ["T"] = "El orbe infernal",
  },
  [1955] = {
    ["D"] = "Ahora que tenemos un orbe de invocador, debemos exorcizar al demonio que hay en su interior. Podemos hacerlo. Es la parte más fácil.$B$BPero tú tienes que hacer el trabajo duro. Tendrás que matar al demonio cuando esté fuera.$B$BY tendrás que hacerlo rápido. Si te lleva demasiado tiempo volverá a meterse en el orbe y tendremos que empezar de nuevo.$B$BTendrás que estar $Gpreparado:preparada;. Ten listos tus hechizos más poderosos y ten tus pociones a mano. Trae también a un amigo, si lo tienes, esto no va a ser nada fácil.",
    ["O"] = "Mata al demonio del orbe y luego habla con Tabetha.",
    ["T"] = "El exorcismo",
  },
  [1956] = {
    ["D"] = "Ahora que hemos eliminado al demonio, debemos hacernos con una fuente de alimentación. Por desgracia, las mejores fuentes de poder suelen ser las más antiguas y las más difíciles de obtener. Los juegos y tretas del destino son fascinantes, ¿verdad?$B$BAsí que vas a tener que ir a Uldaman y vencer al centinela obsidiano. Es grande, duro ¡y su fuente de alimentación da buenos golpes!$B$BTe deseo todo lo mejor, $N. Y si no tienes suerte... ha sido un placer conocerte.",
    ["O"] = "Hazte con una fuente de poder obsidiano y llévasela a Tabetha en el Marjal Revolcafango.",
    ["T"] = "Poder en Uldaman",
  },
  [1957] = {
    ["D"] = "Ya casi hemos terminado, pero el último paso es el más peligroso, $N.$B$BTengo el orbe del invocador vacío y estoy lista para otorgarle poderes. Lo haremos aprovechando la energía de una falla de maná. Lanzaré un hechizo que abrirá temporalmente una falla de la que saldrán criaturas, oleadas de maná. Deberás vencerlas y se liberará su poder, yo lo recogeré.$B$BMata bastantes oleadas de maná antes de que se cierre la falla y pondré la energía reunida en tu orbe.",
    ["O"] = "Mata 12 oleadas de maná dentro del tiempo permitido.",
    ["T"] = "Oleadas de maná",
  },
  [1959] = {
    ["D"] = "Los magos de Entrañas han notado una perturbación mágica dentro de la ciudad y han dado la orden a toda la Horda de investigar el suceso.$B$BAnastasia Hartwell te puede contar más. Habla con ella en el Barrio de la Magia de Entrañas.",
    ["O"] = "Habla con Anastasia Hartwell.",
    ["T"] = "Preséntate ante Anastasia",
  },
  [1960] = {
    ["D"] = "La tienda de alquimia de Algernon, en El Apothecarium, es el centro de una extraña alteración. Criaturas invisibles, hechas tan solo de magia, vagan por la tienda... ¡debe haber una razón y debemos descubrirla!$B$BInvestiga la alteración y captura a esas criaturas. Coge el cofre de arcas y el pergamino que hay detrás de mí. Utiliza el pergamino en la tienda para hacer visibles a las criaturas, cuando las hayas dominado con tus hechizos, arroja un arca para capturarlas. Vuelve con las arcas llenas, el cofre y el canto.",
    ["O"] = "Detrás de Anastasia Hartwell encontrarás un Canto de manifestación y un cofre de arcas de contención. Cógelos y lleva 3 arcas de contención llenas, el cofre de arcas de contención y el canto de manifestación a Anastasia en Entrañas.",
    ["T"] = "Investiga la tienda de alquimia",
  },
  [1961] = {
    ["D"] = "Ha llegado la hora de que tengas tus primeras togas mágicas, $N. Son un símbolo de tu condición y un testimonio de tu valor.$B$BPara obtenerlas, deberás recoger gemas de maná de los magos de Dalaran, en los Argénteos. Sus creencias son similares a las nuestras, por lo que matarlos no es nada placentero, pero bueno, si no son capaces de hacernos frente, la verdad que merecen morir.$B$BTrae las gemas y un poco de lino para Josef Gregorian en la tienda de sastrería del Barrio de la Magia.",
    ["O"] = "Consigue 10 trozos de paño de lino y 6 gemas de maná de Dalaran para Josef Gregorian.",
    ["T"] = "Recolección de materiales",
  },
  [1962] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Toga de fuego de hechizo",
  },
  [1978] = {
    ["D"] = "¿La Hermandad? Nunca lo habría sospechado. Parece que la traición de los señores de Alterac comenzó hace años.$B$BLord Varimathras se complacerá de escuchar esto, y como tú has sido una pieza clave en este descubrimiento, sería perfecto si se lo hicieras llegar tú mismo.$B$BY por cierto, no me he olvidado:$B$B$Gbienvenido:bienvenida; a los mortacechadores.",
    ["O"] = "Entrégale el libro de contabilidad de Andron a Varimathras, que está en la Cámara de la Dama Oscura en Entrañas.",
    ["T"] = "Los mortacechadores",
  },
  [1998] = {
    ["D"] = "Lord Varimathras me ha enviado una misiva sobre una amenaza para los no-muertos que debe ser eliminada de inmediato. Su nombre es Fenwick Thatros, el que Lord Varimathras cree que es el líder de los no-muertos que se han asentado en el Bosque de Argénteos.$B$BTu nombre me vino a la mente rápidamente, $N. Confío en tus facultades y sé que podrás realizar esta misión a la perfección.$B$BSegún los mortacechadores del Bosque de Argénteos, puedes encontrar a Thatros cerca del embarcadero destartalado de Lordamere.",
    ["O"] = "Mata a Fenwick Thatros y llévale su cabeza a Mennet Carkad, que está en el Barrio de los Pícaros de Entrañas.",
    ["T"] = "Fenwick Thatros",
  },
  [1999] = {
    ["D"] = "Uno de nuestros prisioneros ha confesado que los zahorís del Kirin Tor en el Molino Ámbar esconden importantes documentos relativos al estado actual de Dalaran. Lord Varimathras ha encomendado a los mortacechadores la tarea de recuperarlos.$B$BAhora esta tarea te corresponde a ti, $N. Sin embargo, necesitarás algún equipamiento y saber forzar cerraduras. Habla con Estelle Gendry, nuestra modista, y pídele un juego de herramientas antes de partir.",
    ["O"] = "Encuentra el informe de estado de Dalaran y devuélveselo a Mennet Carkad, que está en el Barrio de los Pícaros de Entrañas.",
    ["T"] = "Herramientas de comercio",
  },
  [2000] = {
    ["D"] = "Tienes demasiado tiempo libre, $N; ya es hora de que aprendas algo más. Anoche encontramos a cinco de los nuestros muertos en una de las grietas de Orgrimmar. Nuestros enemigos usan nuestros propios métodos contra nosotros.$B$BAsí que he decidido que aprendas a forzar cerraduras.$B$BPor desgracia, mis manos no tienen esa habilidad. Ve a ver a Rokar, que vive en las colinas que hay cerca de Durotar. Aunque ya es mayor, seguro que sigue siendo el mejor.",
    ["O"] = "Habla con Rokar Filosombra en Durotar.",
    ["T"] = "Rokar Filosombra",
  },
  [2018] = {
    ["D"] = "¿Así que ahora Therzok me envía pupilos? Quizás pueda enseñarte un par de cosas. No es que me importe lo que estás haciendo, pero no quiero tener tu muerte sobre mi conciencia.$B$BPero antes, tienes que probar tu valía.$B$BMira, $N, a mí Ventura y Cía. me da mala espina. Esta tierra me recuerda a Draenor y no me gusta que esos goblins lo pongan todo patas arriba. Si matas al vigía de El Fangal, la operación se interrumpirá.",
    ["O"] = "Mata al vigía Glibby y llévale su silbato a Rokar Filosombra a Durotar.",
    ["T"] = "La prueba de Rokar",
  },
  [2019] = {
    ["D"] = "Ten, $N, esto te servirá.$B$BPero no te molestes en contarme tu historia, los planes de Therzok no me interesan. Solo quiero vivir en paz.$B$BSi alguna vez necesitas herramientas nuevas, ven a verme y te las daré. ¡Aun así, cuídalas bien!",
    ["O"] = "Habla con Therzok en el Circo de las Sombras de Orgrimmar.",
    ["T"] = "Herramientas de comercio",
  },
  [2038] = {
    ["D"] = "Por si no fuera bastante haberme estrellado en Loch Modan, me atacaron unos troggs de una isla cercana y me robaron las herramientas y mis cartuchos de explosivos Pimpampum. ¡Sin girocóptero y sin explosivos no hay nada que hacer!$B$BTráeme mis herramientas y mis explosivos, $N. ¡Por Gnomeregan!",
    ["O"] = "Localiza las herramientas de Bingles y devuélveselas:$B$Bllave inglesa, destornillador, martillo y cartucho de explosivos.",
    ["T"] = "Suministros de Bingles",
  },
  [2039] = {
    ["D"] = "¡Hemos perdido contacto con Bingles! Fue a una misión de reconocimiento a Gnomeregan hace una semana y no ha vuelto a dar señales de vida. Seguro que ha saltado por los aires.$B$BLa última vez que se le vio, volaba sobre Loch Modan. Quizás tú puedas enterarte de qué ha pasado, $N.",
    ["O"] = "Encuentra a Bingles Pimpampum en Loch Modan.",
    ["T"] = "Encuentra a Bingles",
  },
  [2040] = {
    ["D"] = "¡Esos malditos troggs han tomado Gnomeregan! La situación es grave, pero quizás puedas ayudar, $N.$B$BEn las profundidades de Las Minas de la Muerte hay una trituradora goblin operativa. Encuentra la trituradora y trae su batería intacta. Con la batería de la trituradora, podemos dar a nuestras excavadoras gyroagujereamáticas la energía necesaria para atravesar los rocosos límites subterráneos de Gnomeregan, ¡y nos abrirán el camino para el asalto gnomo!",
    ["O"] = "Recupera la Dentrituradora goblin de Las Minas de la Muerte y devuélveselo a Shoni la Shilenshiosha, en Ventormenta.",
    ["T"] = "Asalto subterráneo",
  },
  [2041] = {
    ["D"] = "A lo mejor... A lo mejor puedes ayudarnos en la batalla por Gnomeregan.$B$BVe a Ventormenta a hablar con Shoni la Shilenshiosha, la jefa de trabajos subterráneos. Necesita un cable con las excavadoras gyroagujereamáticas.$B$BLo más seguro es que esté con los enanos en Ventormenta.$B$BSuerte, $N.",
    ["O"] = "Habla con Shoni la Shilenshiosha en el Distrito de los Enanos en Ventormenta.",
    ["T"] = "Habla con Shoni",
  },
  [2078] = {
    ["D"] = "Tienes que encontrar a mi contramaestre, $Gmarinero:marinera; de agua dulce.$B$BCoge esta llave, pónsela en la cabeza y tráelo aquí, con su capitán.$B$BEsos múrlocs no son sus dueños... ¡ya se darán cuenta cuando mi segundo se encargue de ellos!",
    ["O"] = "Encuentra el Trillomajareitor 4100, el contramaestre de Gelkak, y condúcelo hasta él.",
    ["T"] = "La venganza de Gyromast",
  },
  [2098] = {
    ["D"] = "La vida del pirata no es fácil, te lo digo yo. ¡No es todo abordajes y ron! También hay desgracia... Mi barco se hundió cerca de la costa por culpa de esos malditos trillanodontes.$B$B¡Necesito tener conmigo a mi contramaestre! ¡Cuanto antes!$B$BCuando alcancé la costa se me cayó la llave de mi segundo de a bordo en las rocas y me la robaron esas bestias de Costa Oscura. Por el rabillo del ojo pude ver reptadores furiosos, feroces zancudos de bosque y múrlocs rebuscando entre los restos del naufragio.",
    ["O"] = "Encuentra y devuelve los tres fragmentos de la llave de Gelkak Gyromast.",
    ["T"] = "La búsqueda de Gyromast",
  },
  [2118] = {
    ["D"] = "Por Costa Oscura se extiende una enfermedad que se hace con la mente y el cuerpo de todo lo que toca.$B$BLos osos cardo han sido los más afectados por esta plaga. Eran nobles animales que se han convertido en rabiosos objetos de destrucción. Creo que yo podría tener una cura para este mal.$B$BLleva esta trampa al bosque y colócala en el suelo. Cualquier oso cardo rabioso que cruce la luz será manso por un breve período de tiempo. Y cuando esto ocurra, te seguirá. Guía uno hasta aquí, $N.",
    ["O"] = "Captura un oso cardo rabioso vivo y llévaselo a Tharnariun. $B$BSi no consigues capturar un oso cardo rabioso y pierdes la trampa, pídele otra a Tharnariun Espinarbolado.",
    ["T"] = "Tierras apestadas",
  },
  [2138] = {
    ["D"] = "Tal y como temía... la cura no funciona.$B$BMe entristece ordenar la muerte de una criatura de la naturaleza, pero estos osos enfermos deben ser sacrificados.$B$BVuelve al bosque y mata a 20 Osos Cardo rabiosos, $N. Eso no detendrá la plaga pero, al menos durante un tiempo, reducirá el daño que esos animales están haciendo al bosque y a sus habitantes.",
    ["O"] = "Libra al bosque de 20 Osos Cardo rabiosos y ve a buscar a Tharnariun Espinarbolado a Costa Oscura.",
    ["T"] = "La limpieza de la infección",
  },
  [2139] = {
    ["D"] = "El precio por la supervivencia de nuestra gente y nuestras tierras es muy alto, $N.$B$BHas derramado la sangre de muchos siervos de la naturaleza. Has derribado a muchos animales enfermos. Los habitantes de Auberdine están en deuda contigo, pues, aunque sea solo por un tiempo, has mitigado el dolor del bosque...$B$BTengo una última tarea para ti. Pero te advierto, tendrás que volver a matar. Tengo la esperanza de que esta sea la última vez que recurro a ti, $N.$B$BAl noreste hay una cueva, cerca de Bashal\'Aran. En ella reside la madre del cubil. Mátala.",
    ["O"] = "Encuentra a la madre del cubil y mátala.",
    ["T"] = "Esperanza de Tharnariun",
  },
  [2158] = {
    ["D"] = "Todos los aventureros deben descansar cuando les invade el agotamiento, ¡y no hay mejor lugar para descansar que la Posada Orgullo de León!$B$BMi mejor amigo, el tabernero Farley, lleva la Posada Orgullo de León. Si le dices que te envío yo, puede que te haga un descuento en la comida y la bebida.$B$BPara encontrar la Posada Orgullo de León, viaja al sur por el camino que sale de aquí, ¡no tiene pérdida!",
    ["O"] = "Habla con el tabernero Farley de la Posada Orgullo de León.",
    ["T"] = "Descanso y relajación",
  },
  [2159] = {
    ["D"] = "Saludos, joven $C. ¿Me podrías prestar tu ayuda? Tengo un paquete de hierbas que debo entregar en la ciudad de Dolanaar pero todavía tengo asuntos pendientes con los druidas de Cañada Umbría y no puedo irme.$B$B¿Puedes entregar este paquete por mí? Su destinatario es el tabernero Keldamyr de la posada de Dolanaar, que se encuentra un poco más adelante en dirección sur.",
    ["O"] = "Llévale el paquete de Dolanaar al tabernero Keldamyr.",
    ["T"] = "Paquete de Dolanaar",
  },
  [2160] = {
    ["D"] = "¡Eh! Tienes pinta de ser de corazón aventurero. Si vas a arriesgarte a atravesar el paso, ¿crees que podrías llevar un paquete a la posada de Kharanos?$B$BPorque pensabas hacer un alto en la posada, ¿no? Sí, seguro, si consigues cruzar el paso, necesitarás descansar un poco.$B$BY como vas a pasar por allí, no te costará nada entregar esto a Tannok Martilloescarcha, el ayudante del tabernero. Yo no puedo cruzar el paso ¡y pasarán días hasta que llegue una escolta de montaraces!",
    ["O"] = "Lleva a Kharanos el cajón de suministros del mesón y entrégaselo a Tannok Martilloescarcha.",
    ["T"] = "Suministros para Tannok",
  },
  [2161] = {
    ["D"] = "Perdón... ¿Puedes ayudarme?$B$BTengo un montón de comida aquí. ¡Y por el camino he tenido que afrontar escórpidos y arañas y cosas peores! Traje la comida al Valle de los Retos porque pensaba que la necesitarían y quería ayudar. ¡Pero no necesitan comida! Y ahora tengo que llevármela de vuelta a Cerrotajo y tengo miedo de las bestias que pueda encontrarme por el camino...$B$B¿Podrías llevarla tú por mí? Yo soy una criatura insignificante y tú, sin embargo, eres un ser aguerrido. ¡No temes a nada!$B$BLlévaselo al tabernero Grosk de la posada de Cerrotajo. ¡Gracias!",
    ["O"] = "Lleva la carga de Ukor al tabernero Grosk a Cerrotajo.",
    ["T"] = "Un cargamento de peón",
  },
  [2178] = {
    ["D"] = "¿Hueles ese delicioso aroma? Es mi receta secreta, $N: guiso de zancudo recién hecho. ¡Es una delicia!$B$BQuizás te interese aprender la receta...$B$BPues para que te la enseñe, tendrás que probarme que eres capaz de dominar a las bestias.$B$BNo espero que salgas, domestiques a un zancudo y vuelvas cabalgando en él, pero si consigues matar a unos pocos y traerme algo de su suculenta carne, me dejaría convencer para compartir mi receta.",
    ["O"] = "Llévale 5 piezas de carne de zancudo a Alanndarian Arrullanoche a Auberdine.",
    ["T"] = "Guiso de zancudo",
  },
  [2198] = {
    ["D"] = "Has encontrado un collar deslustroso, que todavía deja ver el minucioso y mágico trabajo de artesanía del que ha sido fruto. El collar está dañado y parece que faltan tres gemas. En la parte de atrás del collar hay una inscripción apenas visible con el sello de la ciudad de Forjaz. Debajo hay tres pequeñas iniciales grabadas. \"TdK\".$B$BSi este collar significa algo, tendrás que descubrir de qué se trata. El sello y las iniciales serán un buen punto de partida...",
    ["O"] = "Busca al creador del collar hecho añicos y descubre para qué sirve.",
    ["T"] = "El collar hecho añicos",
  },
  [2199] = {
    ["D"] = "¡Soy un joyero de gran renombre en Forjaz! O al menos, lo era... ¡Me veo arruinado por culpa de las argucias de un competidor! Y la mala reputación nunca ayuda en los negocios. Y así me veo en la más completa miseria.$B$BNecesito cinco lingotes de plata para acabar un encargo, el primero que recibo en ni sé cuánto tiempo.$B$BTráeme los lingotes y yo te diré lo que sepa del collar.",
    ["O"] = "Lleva cinco lingotes de plata a Talvash del Kissel a Forjaz.",
    ["T"] = "Esa información tiene un precio",
  },
  [2200] = {
    ["D"] = "Un competidor hizo correr el rumor de que mi collar tenía una maldición que cayó sobre el paladín. Evidentemente, nadie quiere comprar joyas malditas.$B$BHagamos un trato, $N: tráeme las gemas y yo arreglaré el collar, ¡siempre que hagas publicidad de que mis joyas no están malditas!$B$BBusca pistas en Uldaman; puede que encuentres el cadáver del paladín, su último dueño conocido. Usa esto cuando encuentres las gemas; así podrás comunicarte conmigo aquí en Forjaz.",
    ["O"] = "Busca pistas sobre el paradero del collar de Talvash en Uldaman. Dijo que un paladín muerto fue su último dueño.",
    ["T"] = "El regreso a Uldaman",
  },
  [2201] = {
    ["D"] = "El diario del paladín ha aportado quizás las únicas pistas que Uldaman revelará acerca del paradero de las gemas del collar. Si los garabatos del paladín son acertados, habrá que enfrentarse a los enanos Forjatiniebla y los troggs que se disputan el control de la excavación.$B$BSabiendo esto, la búsqueda de las gemas empieza de verdad. Cuando tengas todas las gemas, tendrás que notificárselo a Talvash de Forjaz utilizando la ampolla de adivinación, tal y como él te dijo.",
    ["O"] = "Encuentra el rubí, el zafiro y el topacio que están desperdigados por Uldaman. Cuando los tengas, contacta con Talvash del Kissel mediante la ampolla de adivinación que él te dio.$B$BA través del diario, sabes que...$B*El rubí está escondido en una zona de Forjatiniebla con barricadas.$B*El topacio permanece oculto en una urna en una de las zonas de los troggs, cerca de algunos enanos de la Alianza.$B$B*El zafiro obra en poder de Grimlok, el líder de los troggs, según él mismo afirma.",
    ["T"] = "Encuentra las gemas",
  },
  [2202] = {
    ["D"] = "Hace poco oí hablar de un organismo recién descubierto: las setas magenta. Crecen arracimadas en la excavación de Uldaman y pueden encontrarse en los túneles primitivos y en las profundidades de las zonas menos explotadas. Quiero ver qué usos potenciales pueden tener en la alquimia, por eso necesito tu ayuda.$B$BTráeme una docena de setas magenta. Es posible que se defiendan con esporas venenosas; ten cuidado.$B$B¡A cambio, te daré uno de mis famosos elixires vigorizantes!",
    ["O"] = "Llévale 12 setas magenta a Jarkal Musgofusión en Kargath.",
    ["T"] = "Componentes de Uldaman",
  },
  [2203] = {
    ["D"] = "Has sido de gran ayuda, $C. Te recompensaré con la mismísima receta del brebaje que te di antes... pero me gustaría hacerte un último encargo un tanto peligroso. Esta es mi petición.$B$BEste arcón contiene tres vasijas de taumaturgia vacías; están imbuidas de un aura de Armonización que absorberá la sangre de un dragón guardián agostado. Si la usas con la fiera, se enfurecerá, así que ve con mucho cuidado. Cuando tengas las tres llenas tráemelas.$B$BHazlo y la receta será tuya.",
    ["O"] = "Usa las vasijas de taumaturgia vacías con los dragones guardianes agostados de las Tierras Inhóspitas. Cuando las hayas llenado, llévaselas a Jarkal Musgofusión en Kargath.",
    ["T"] = "Componentes de Tierras Inhóspitas II",
  },
  [2204] = {
    ["D"] = "Vas a tener que darle \"vidilla\" a ese collar y Uldaman probablemente es el mejor lugar para conseguir lo que necesitas. Supongo que hay muchos ensamblajes por allí, ¿no? Bien, pues elige el más grande de todos los ensamblajes que encuentres y extrae su fuente de energía. Seguro que podré insertar esa fuente en el interior del collar.$B$BSin la fuente de energía no es posible restaurar el collar. Regresa a Forjaz cuando la tengas y podremos arreglarlo para ti.",
    ["O"] = "Obtén una fuente de energía del ensamblaje más poderoso que encuentres en Uldaman, y entrégasela a Talvash del Kissel en Forjaz.",
    ["T"] = "Restaurar el collar",
  },
  [2205] = {
    ["D"] = "¿La insignia? Oh, no es nada, pescadito. Al menos nada que tú puedas arreglar. Por la pinta que tienes, diría que Mathias, más que pensar que eres $Gun pícaro:una pícara;, te tomaría por $Gel chico:la chica; de los recados del quesero. Además, el trabajo me fue asignado a mí y fui yo quien se comprometió a hacerlo.$B$BHablando de lo cual... lleva este paquete a Mathias. Quizás te dé un par de pequeñas tareas.$B$BEn marcha. |n",
    ["O"] = "Entrega el paquete de Keryn Sylvius al maestro Mathias Shaw que está en el Cuartel de Ventormenta.",
    ["T"] = "Busca el IV:7",
  },
  [2206] = {
    ["D"] = "Nuestros operativos en el campo informan sobre una gran actividad vandálica al sur de Villadorada. Según los informes, los bandidos han tomado el Embarcadero de Jerod y lo utilizan para llevar clandestinamente grandes cantidades de contrabando desde Crestagrana hasta los Páramos de Poniente.$B$BNecesito que te infiltres en la casa del embarcadero y me traigas el calendario de envíos, $N. Probablemente esté en el bolsillo del maestro de embarcadero.$B$B... Y $N, si te descubrieran, el IV:7 negaría todo conocimiento de tu existencia.",
    ["O"] = "Encuentra al maestro de embarcadero y recupera el calendario de envíos para el maestro Mathias Shaw.|n",
    ["T"] = "El robo",
  },
  [2218] = {
    ["D"] = "Otra alma perdida buscando guía, ¿eh? Supongo que quieres encontrar un propósito en esta vida... tu propósito. Lo he visto mil veces, todos los héroes acaban buscando trabajo.$B$BPues bien, hoy es tu día de suerte. Hogral tiene la cura para tu mal.$B$BTe voy a dar algo que hacer: Sigue este sendero hasta Forjaz y ábrete camino hasta La Caverna Abandonada. Una vez en ella, localiza a la hermandad de ladrones y habla con Hulfdan Barbanegra. Él te indicará el camino hacia la salvación.|n",
    ["O"] = "Ve a Forjaz y habla con Hulfdan Barbanegra.",
    ["T"] = "El camino hacia la salvación",
  },
  [2238] = {
    ["D"] = "El Rey quiere sacar todo el mineral y las gemas de estas minas. Más tesoros de los que un enano vería en diez vidas.$B$BEl círculo oculto tiene valores, $N. Valoramos lo valioso, ¿entiendes?$B$BY pensamos que el Rey debería compartir su buena fortuna, pero sin que se entere, ¿me pillas?$B$BTienes que ir a hablar con Onin MacHammar en Gnomeregan y volver con lo que te diga. El muy ladino se camufla entre los leprosos. Ándate con ojo.",
    ["O"] = "Encuentra a Onin MacHammar cerca de Gnomeregan.",
    ["T"] = "Un simple subterfugio",
  },
  [2239] = {
    ["D"] = "¿La cantera? Sí, veo esos monstruosos tanques de vapor blindados pasar por aquí casi cada día.$B$BMis contactos en el este me dicen que el tanque sale de Forjaz, hace una recogida en la Cantera de Gol\'Bolar, para en Kharanos a repostar y luego, por no sé qué maldito motivo, da un rodeo para esquivar Gnomeregan antes de volver a Forjaz para descargar.$B$BEstá todo en el informe, $R. Llévaselo a Hulfdan para que pueda planear el siguiente paso.|n",
    ["O"] = "Lleva el informe de Onin a Hulfdan a Forjaz.",
    ["T"] = "Informe de Onin",
  },
  [2240] = {
    ["D"] = "Pasemos a la siguiente tarea.$B$BExiste una cámara secreta, la cámara de Khaz\'mul, que hay que examinar. ¡El problema es que esta cámara se encuentra tras una enorme puerta cerrada con llave! Es una suerte que sepa cómo abrirla.$B$BLo explico todo en mi diario, que está sobre la mesa. Mi equipo y yo no podemos salir de aquí... así que tú, $N, debes encontrar la cámara de Khaz\'mul por nosotros.$B$BLee mi diario, examina la cámara y ve a informar al prospector Pico Tormenta de la Liga de Expedicionarios en Forjaz.",
    ["O"] = "Lee el diario de Baelog, inspecciona la cámara oculta y ve a informar al prospector Pico Tormenta.",
    ["T"] = "La cámara oculta",
  },
  [2242] = {
    ["D"] = "Creemos que hay un ser que podría ayudarnos a entender qué está ocurriendo en los bosques de Teldrassil. Por desgracia, los intentos de convencer a la criatura para que coopere han fracasado.$B$BSe esconde en una rama a las afueras de El Claro del Oráculo. Todos los que se han acercado al sátiro, ya sea por las buenas o por las malas, han encontrado la muerte. Quizás el sigilo sea la respuesta, $N.$B$BConsigue lo que porta Sethir el Ancestro en sus talegas y vuelve a verme.|n",
    ["O"] = "Encuentra a Sethir el Ancestro y ve a contarle todo lo que descubras a Syurna.|n",
    ["T"] = "La llamada del destino",
  },
  [2258] = {
    ["D"] = "$N, no me vendría mal tu ayuda si te apetece echar una mano. Soy un alquimista de cierto renombre y necesitaría tu ayuda para conseguir unos componentes que se hallan en las Tierras Inhóspitas.$B$BMe hacen falta los siguientes objetos para reponer mis existencias: cinco mollejas de águila ratonera, diez colmillos de coyote del risco y cinco fragmentos de elemental de roca. Como es lógico, puedes conseguirlos de los propios animalitos; si no eres del tipo luchador, llévate a unos amigos que lo sean.$B$BConsígueme esos objetos y acabarás con unas monedas más en el bolsillo. ¿Qué me dices? ¿Te hace?",
    ["O"] = "Llévale 5 mollejas de águila ratonera, 10 colmillos de coyote del risco y 5 fragmentos de elemental de roca a Jarkal Musgofusión en Kargath, en las Tierras Inhóspitas.",
    ["T"] = "Componentes de Tierras Inhóspitas",
  },
  [2259] = {
    ["D"] = "No te preocupes, $N. No te voy a enviar a ver a Syurna. Erion requiere tu presencia en el Enclave Cenarion. ¡Date prisa!",
    ["O"] = "Localiza a Erion Sombrusurro en Darnassus.|n",
    ["T"] = "Erion Sombrusurro",
  },
  [2260] = {
    ["D"] = "\"Las noticias de tus hazañas han llegado lejos, $N. Tanto, que el Servicio de Inteligencia de Ventormenta ha preguntado por ti.$B$BEl viaje es agotador y no está exento de peligros, pero la oportunidad de ser entrenado por el IV:7 no debería tomarse a la ligera.$B$BUn nuevo mundo te espera, $N. Dirígete al Casco Antiguo de Ventormenta y habla con Renzik \"Chafarote\" en el puesto de mando del IV:7.",
    ["O"] = "Viaja a la Ciudad de Ventormenta y habla con Renzik \"Chafarote\".",
    ["T"] = "La orden de Erion",
  },
  [2278] = {
    ["D"] = "En el centro de esta cámara del tesoro se encuentran cuatro discos de platino gigantes, cada uno de ellos adornado con sigilos de origen desconocido. Al tocarlos sientes un cosquilleo en la mano. Tras haber tocado los discos, se muestra ante ti un enorme vigía de piedra.$B$B\"Saludos. Soy un guardián del conocimiento. La síntesis de los Creadores de los terráneos se describe y almacena para este mundo en los Discos de Norgannon.\"$B$B¡Acabas de encontrar una fuente de conocimiento nunca antes descubierta por nadie!",
    ["O"] = "Habla con el vigía de piedra y descubre qué conocimiento antiguo alberga. Cuando hayas adquirido el conocimiento que te ofrece, activa los Discos de Norgannon.",
    ["T"] = "Los discos de platino",
  },
  [2280] = {
    ["D"] = "Al tocar los Discos de Norgannon de nuevo, sientes un cosquilleo en la mano. En esta ocasión, sin embargo, se oye un extraño ruido proveniente de su interior.$B$BSientes cómo tu mochila de repente es más pesada que antes. ¡En su interior descubres una reproducción en miniatura de los discos!$B$BSin duda, un objeto de este tipo tendría un valor incalculable para alguien interesado en la historia antigua de Azeroth. ¡A alguien le va a interesar mucho!",
    ["O"] = "Lleva la reproducción en miniatura de los Discos de Norgannon a alguien que esté muy interesado.",
    ["T"] = "Los discos de platino",
  },
  [2281] = {
    ["D"] = "Enviarte ahora a la batalla sería suicida. Renzik no necesita otro pícaro muerto en sus manos. Pero no te apresures, $N, ya te meterá prisa Renzik.$B$BDirígete hacia las Montañas Crestagrana y habla con Lucius, es un miembro del IV:7. Lo encontrarás en el embarcadero de Villa del Lago, con los demás vagos y maleantes.",
    ["O"] = "Adéntrate en Villa del Lago, en las Montañas Crestagrana, y habla con Lucius.",
    ["T"] = "Cita en Crestagrana",
  },
  [2282] = {
    ["D"] = "¡Llevamos años entrenando ladrones aquí! No todos los bribones nacen con los dedos ligeros del amigo Lucius.$B$BSigue el camino y encontrarás el Molino de Alther. Dentro hay un cofre con algo importante. Tráeme lo que contiene y te ganarás tu Certificado de Ratería.",
    ["O"] = "Abre la caja fuerte de Lucius y llévale la muestra de robo a Villa del Lago.",
    ["T"] = "Molino de Alther",
  },
  [2283] = {
    ["D"] = "Tenemos un trabajillo para ti. ¿Te atreves?$B$BAl parecer, hace poco que un idiota de paladín murió en la excavación Uldaman, en medio de las Tierras Inhóspitas. Lo que nos interesa de esto es que llevaba un collar muy valioso con tres gemas: un rubí, un zafiro y un topacio.$B$BEn resumen: queremos el collar y cubriremos de plata a quien lo traiga. Aunque esté deteriorado, no importa; ya lo arreglaremos.",
    ["O"] = "Busca el collar en la excavación de Uldaman y llévaselo a Dran Droffers a Orgrimmar. Puede que el collar esté estropeado.",
    ["T"] = "La recuperación del collar",
  },
  [2298] = {
    ["D"] = "Y ahí estábamos, a punto de empezar a planear la fase III, cuando llegaron un par de esos brutos de Magni hablando de un atraco o algo así y arrestaron a gente del círculo oculto.$B$B¡Y al viejo Barbanegra no le arresta nadie! Los ánimos se caldearon y no sé cómo, salió a relucir tu nombre.$B$B¿Mi consejo? Coge el primer grifo que salga de Forjaz y ve a Ventormenta. Ve al Casco Antiguo y habla con un viejo amigo mío que se llama Renzik \"Chafarote\".",
    ["O"] = "Viaja a Ventormenta y busca a Renzik \"Chafarote\".",
    ["T"] = "Una regia sacudida",
  },
  [2299] = {
    ["D"] = "¿Dónde has estado, $N? Hulfdan te ha estado buscando por todas partes. Pensábamos que el Rey te había hecho arrestar.$B$B¡Ve a Forjaz en cuanto puedas y habla con Hulfdan!",
    ["O"] = "Viaja a Forjaz y habla con Hulfdan Barbanegra.",
    ["T"] = "¡Ve a hablar con Hulfdan!",
  },
  [2300] = {
    ["D"] = "Renzik te estaba buscando, pescadito.$B$BYo no haría esperar al segundo de Mathias si fuera tú.|n",
    ["O"] = "Viaja a Ventormenta y busca a Renzik \"Chafarote\".",
    ["T"] = "SI:7",
  },
  [2318] = {
    ["D"] = "Llegados a este punto, sabes que necesitarás la traducción del libro en un idioma que puedas comprender. Son muchos los que podrían hacerlo y el lugar más cercano en el que podrás encontrar a alguien que te ayude es, sin embargo, la Avanzada de Kargath en las Tierras Inhóspitas. Quizás sea un buen lugar para comenzar.|n",
    ["O"] = "Encuentra a alguien que pueda traducir el diario del paladín. El lugar más cercano en el que podrás encontrar a alguien es Kargath, en las Tierras Inhóspitas.",
    ["T"] = "La traducción del diario",
  },
  [2338] = {
    ["D"] = "Rescates Droffers e Hijos no son lo suficientemente listos para restaurar la magia del collar, pero yo sí. Solo quiero estudiar el collar para aprender a hacerlos yo mismo. Para ello, necesitaré tu collar. Préstamelo y, a cambio por el tiempo que me dejes estudiarlo, te traduciré el diario. Mientras buscas las gemas, debería tener tiempo suficiente para aprender lo que necesito.$B$BEs un trato sencillo y nadie se enterará. ¿Qué? ¿Te hace?",
    ["O"] = "Préstale el collar a Jarkal. A cambio, te traducirá el diario.",
    ["T"] = "La traducción del diario",
  },
  [2339] = {
    ["D"] = "Aquí tienes el diario traducido para que puedas leerlo. En esencia dice que los enanos escondieron el rubí, los troggs ocultaron el topacio y un trogg llamado Grimlok tiene el zafiro.$B$BOye, vas a necesitar una fuente de energía para que el collar vuelva a funcionar. Dran Droffers no lo sabe. Para encontrarla, esto es lo que yo haría: me adentraría lo más posible en Uldaman y acabaría con el ensamblaje más fuerte que allí hubiera. Sea lo que sea, seguro que la tendrá.$B$BCuando lo tengas todo, ¡vuelve aquí!",
    ["O"] = "Recupera las 3 gemas y una fuente de energía para el collar de Uldaman y llévalo todo a Jarkal Musgofusión en Kargath. Jarkal cree que es posible que la fuente de energía más poderosa se halle en un ensamblaje de Uldaman.$B$BPor el diario sabes...$B* Que han ocultado el rubí en una zona cerrada con barricadas en Forjatiniebla.$B* Que el topacio se ha ocultado en una urna en un área trogg, cerca de los enanos de la Alianza.$B* Que Grimlok, el jefe trogg, se ha quedado con el zafiro.",
    ["T"] = "Encuentra las gemas y la fuente de alimentación",
  },
  [2340] = {
    ["D"] = "Cualquier engarce de una gema en el collar funcionará, siempre que sean del mismo material. Esta fuente de energía, sin embargo, es lo que le devolverá la magia al collar... Espero.$B$BCon mucho gusto aportaré las gemas necesarias para tu versión del collar, siempre que la fuente de energía funcione realmente.$B$BDirígete a Orgrimmar y cumple tu parte del trato con Rescates Droffers e Hijos. Ni una palabra de nuestro trato y cuando vuelvas podría tener una maravillosa sorpresa para ti.",
    ["O"] = "Entrégale el collar y la gema recuperados a Dran Droffers en Orgrimmar.",
    ["T"] = "Entregar las gemas",
  },
  [2358] = {
    ["D"] = "Hace muchos años robaron los Cuernos de Nez\'ra de la Mansión Ravenholdt. Los he buscado por mar y tierra y resulta que han estado en poder de estas insignificantes criaturas todo este tiempo.$B$BAcaba con todos los orcos Roca Negra que veas hasta recuperar los Cuernos de Nez\'ra. Si lo consigues, ven a verme a Villa del Lago.$B$BSi me haces este favor, Ravenholdt oirá hablar de ti, $N.",
    ["O"] = "Lleva los Cuernos de Nez\'ra a Arantir a Villa del Lago.",
    ["T"] = "Cuernos de Nez\'ra",
  },
  [2359] = {
    ["D"] = "Hace semanas que vigilo esta torre, $N. Hace poco pasó un gran cargamento procedente del Bosque del Ocaso, y con él vinieron esos guardias.$B$B¿Qué son? Tu suposición es tan acertada como la mía: no son humanos.$B$BEn cualquier caso, en el cargamento había un cofre, supongo que es lo que guardaban esas abominaciones. El zombi con los pinchos tiene una llave que necesitaremos.$B$BConsigue esa llave, ve a la torre y averigua qué hay en ese cofre. Lleva a Mathias lo que descubras.",
    ["O"] = "Roba la Llave de la Torre Defias, entra en la Torre Defias y descubre qué contiene el Cofre del Bosque del Ocaso. Lleva toda la información que encuentres a Mathias Shaw de Ventormenta. Lee el diario de Kearnen para saber cómo acceder a la torre.|n",
    ["T"] = "La torre de Klaven",
  },
  [2360] = {
    ["D"] = "Justo a tiempo, $N. La división de reconocimiento del IV:7 ha informado sobre un aumento de actividad Defias en los Páramos de Poniente.$B$BParece ser que los Defias han tomado una atalaya abandonada de la Alianza al sureste de la Colina del Centinela y la están utilizando como base de operaciones para robar mercancías de la ruta de suministros de Crestagrana.$B$BVe a los Páramos de Poniente y contacta con la agente Kearnen para dar parte. La encontrarás escondida, cerca de la torre.$B$B... Y $N, si te descubrieran, el IV:7 negaría todo conocimiento de tu existencia.",
    ["O"] = "Ve a los Páramos de Poniente y encuentra a la agente Kearnen para dar parte.",
    ["T"] = "Mathias y los Defias",
  },
  [2378] = {
    ["D"] = "Has servido con diligencia a los mortacechadores, $N, pero quizás sea hora de que vayas más allá. Aunque tengas cualidades para el sigilo y el engaño, debes continuar con tu instrucción.$B$BLos mortacechadores cuentan con aliados entre los Mano Destrozada. Busca a los que nos puedan echar una mano; encuentra a Shenthul en Orgrimmar.|n",
    ["O"] = "Viaja a Orgrimmar y habla con Shenthul, que estará en el Circo de las Sombras.|n",
    ["T"] = "Encuentra a los Mano Destrozada",
  },
  [2379] = {
    ["D"] = "No conseguirás superar los obstáculos que nos esperan solo con fuerza de voluntad.$B$BHabla con Zando\'zan para empezar tu formación como miembro de Mano Destrozada.$B$BSolo cuando domines el sigilo y el robo podrás seguir tu camino hacia tu destino.|n",
    ["O"] = "Habla con Zando\'zan en el Circo de las Sombras.",
    ["T"] = "Zando\'zan",
  },
  [2380] = {
    ["D"] = "No eres $Gel rey:la reina; del sigilo pero eso puede cambiar. Y cambiará. Evidentemente, has impresionado a los de arriba con tus pobres habilidades, porque el gran Shenthul desea verte.$B$BSigue la carretera que hay al norte de aquí. Te llevará a Orgrimmar. Habla con Shenthul, estará en el Circo de las Sombras.|n",
    ["O"] = "Ve a Orgrimmar, al Circo de las Sombras, y habla con Shenthul.",
    ["T"] = "Ve a Orgrimmar",
  },
  [2381] = {
    ["D"] = "No quiero asustarte, $N, pero los cinco últimos ladrones a los que había enviado a robar el tesoro de los Mares del Sur acabaron en pedacitos.$B$BHacia el sur, bordeando la costa, encontrarás el Surcamareas, el barco de los filibusteros de los Mares del Sur. A buen recaudo en la bodega se encuentra el premio: el tesoro de los Mares del Sur. En el segundo nivel de ese barco está la guarida de los piratas, un lugar donde practican todos sus ladrones.$B$BSube a bordo de ese barco, roba el tesoro y tráemelo. Usa la guarida de los piratas si necesitas practicar cómo robar ¡pero ten cuidado!|n",
    ["O"] = "Llévale el tesoro de los Mares del Sur a Desdio el Desdichado en Trinquete. No olvides conseguir una G.E.A.Q. y herramientas de ladrones del aparato Gizmotrónico de Wrenix. Necesitarás todo eso para completar tu misión.$B$BSi te atacaran unos loros anormalmente agresivos, ¡usa la G.E.A.Q.!|n|n",
    ["T"] = "Saquear a los saqueadores",
  },
  [2382] = {
    ["D"] = "Zando\'zan ha visto que se acerca, sí, la muerte se acerca a Thrall. Será un puñal en la espalda, o un veneno que recorra su cuerpo... Zando\'zan ha dicho demasiado.$B$B¡$N! Zando\'zan tiene una misión para $N. $N tiene que ir a Trinquete, que está al este de El Cruce, y hablar con Desdio el Desdichado. Ve.|n",
    ["O"] = "Ve a Trinquete, en Los Baldíos, y habla con Desdio el Desdichado.",
    ["T"] = "Desdio de Trinquete",
  },
  [2383] = {
    ["D"] = "Ah, mientras estabas fuera llegó un papiro para ti, $N.$B$BLéelo cuando tengas tiempo, si no me equivoco viene de parte del instructor de guerreros, Frang. Querrá tener unas palabras contigo cuando estés $Glisto:lista;. Lo encontrarás a las puertas de El Cubil, al abrigo de la sombra.",
    ["O"] = "Lee el Pergamino simple y habla con Frang en el Valle de los Retos.",
    ["T"] = "Papiro simple",
  },
  [2398] = {
    ["D"] = "La excavación de Uldaman no siempre estuvo ocupada por extraños. Nosotros, los enanos de Forjaz, solíamos controlar la zona... pero eso era antes de que los troggs hicieran su aparición. ¡Llegaron tan rápido y eran tan numerosos que ni los enanos fuimos capaces de hacerles frente!$B$BDe hecho, su ataque fue tan repentino que yo mismo temí que algunos de los nuestros hubieran podido quedar atrapados tras las líneas enemigas.$B$BMi amigo Baelog estaba al frente de un pequeño grupo de enanos en las profundidades de la excavación en el momento en el que aparecieron los troggs. Si él y quienes lo acompañaban siguen con vida, quiero que los encuentres.",
    ["O"] = "Encuentra a Baelog en Uldaman.",
    ["T"] = "Los enanos desaparecidos",
  },
  [2418] = {
    ["D"] = "Hace algún tiempo, me ocurrió un incidente con los enanos de Forjatiniebla. Me trajeron a la Fortaleza de Angor para estudiar dos tipos de extrañas piedras de Uldaman. Descubrí que las piedras eran fuentes de energía estable y continua si se manipulaban correctamente. ¡Es asombroso las cosas que te puedes encontrar en esa vieja excavación!$B$BEn fin, después de compartir con ellos el secreto de las piedras, me echaron de la Fortaleza de Angor y siguieron excavando para conseguir más en Uldaman.$B$B¡Quiero esas piedras para mí! Tráeme de los dos tipos ¡y no escatimaré en gastos!",
    ["O"] = "Llévale 8 piedras de energía de dentrio y 8 piedras de energía de An\'Alleum a Aparejez en las Tierras Inhóspitas.",
    ["T"] = "Las piedras de energía",
  },
  [2438] = {
    ["D"] = "Gaerolas Talvethen, el celador de Túmulo de Ban\'ethil, me dio una vez un atrapasueños esmeralda. Este poderoso amuleto es capaz de extraer la energía del Sueño Esmeralda y dar buena suerte a quien lo porta.$B$BPor desgracia, me lo he dejado en Aldea Brisa Estelar... A pesar de que en un tiempo Brisa Estelar fue un lugar tranquilo, ahora ha sucumbido a la corrupción de los fúrbolgs que allí habitan.$B$B¿Estás $Gdispuesto:dispuesta; a recuperar mi atrapasueños, $C?",
    ["O"] = "Consigue el atrapasueños esmeralda.",
    ["T"] = "El atrapasueños esmeralda",
  },
  [2439] = {
    ["D"] = "Este objeto es un hallazgo indescriptible. ¡Si lo que aquí dice es cierto, los antiguos titanes, o Creadores según estos discos, son el enlace con nuestra creación encantada! Debemos dedicar a esto tanto tiempo como sea posible.$B$BY en agradecimiento por tu valor, $C, ten este vale y llévaselo a Dinita Mantopétreo, que está en las Arcas. ¡Con él conseguirás algo que considero pago adecuado a tu descubrimiento!$B$BVuelve después; ¡queda mucho trabajo por hacer en la Liga de Expedicionarios!",
    ["O"] = "Lleva tu vale de recompensa a Dinita Mantopétreo, que está en las Arcas de Forjaz.",
    ["T"] = "Los discos de platino",
  },
  [2440] = {
    ["D"] = "Concentraré mis energías en estos discos pues tarde o temprano me acabarán revelando su auténtico propósito.$B$BEn cuanto a ti, $N, te recompensaré por haberme informado con tanta presteza. Entrégale este vale a Bena Pezuña Invernal, la instructora de alquimia de Cima del Trueno. Ella te dará un objeto muy especial que confío te resultará tentador y te entregará una selección de pociones de mi reserva personal.$B$BVuelve pronto a mi lado pues presiento que nos quedan muchas cosas por hacer en relación a este hallazgo.",
    ["O"] = "Llévale este vale de recompensa a Bena Pezuña Invernal, que está en Cima del Trueno.",
    ["T"] = "Los discos de platino",
  },
  [2458] = {
    ["D"] = "¿Has oído hablar de Ventura y Cía.?$B$BNos ha costado un año de duros esfuerzos, pero hemos conseguido infiltrar un agente.$B$BHemos descubierto que planean algo a gran escala. Ve hacia el norte, a El Fangal; en la torre de Ventura y Cía. Encontrarás a nuestro agente, el capataz Fizzule.$B$BCuando encuentres la torre, dispara la pistola de bengalas, busca a Fizzule y dale el saludo de Mano Destrozada.|n",
    ["O"] = "Ve a la torre de Ventura y Cía., al norte de El Fangal, y localiza al capataz Fizzule, el agente de Mano Destrozada. $B$BCuando veas la torre, usa la pistola de bengalas para avisar de tu llegada al capataz Fizzule. Lanza 2 bengalas y acércate al capataz para darle el /saludo de los Mano Destrozada. $B$BTienes que hacerlo en este orden, porque Fizzule te matará si no te reconoce.",
    ["T"] = "Agente encubierto",
  },
  [2459] = {
    ["D"] = "La esmeralda... ¡ha desaparecido! ¡Mi atrapasueños está estropeado!$B$BHay un grupo de místicos Tuercepinos al norte de Brisa Estelar. He oído que su líder, Ferocitas el Comesueños, tiene un collar que brilla de noche con una luz de color verde. Por el aspecto de mi atrapasueños, seguro que él me ha robado la esmeralda... No se daría cuenta ni en mil años de que el poder de la esmeralda es inútil en sus manos.$B$BEncuentra la joya perdida, $N. Y ya de paso, mata también a algún místico corrupto.",
    ["O"] = "Mata a 7 místicos Tuercepinos y encuentra la joya perdida.",
    ["T"] = "Ferocitas el Comesueños",
  },
  [2478] = {
    ["D"] = "Tendrás que robarle la llave de la torre al supervisor Silixiz, $N. Cuando la llave esté en tus manos, quédatela, podría necesitarla más tarde.$B$BRecuerda, el veneno lo ha debilitado todo en esta torre. Usa todos tus recursos para sembrar el caos y la destrucción entre sus habitantes. Masácralos a todos ¡sin piedad!$B$BCuando todo esté bañado en sangre, toma la caja fuerte de Gallywix y roba el preparado de Zanzil ahí guardado. Lleva la cabeza de Gallywix y el preparado a Shenthul en Orgrimmar.",
    ["O"] = "Roba la llave de la torre del supervisor Silixiz, mata a 2 zánganos mutados de Ventura y Cía., 2 patrullas de Ventura y Cía. y 2 oteadores de Ventura y Cía.$B$BAsesina al gran supervisor Puzik Gallywix y quédate con su cabeza, fuerza la cerradura del arcón de Gallywix y roba la mezcla de Zanzil modificada que allí está oculta. Lleva el alijo y la cabeza de Gallywix a Shenthul en Orgrimmar, él te dará más instrucciones.|n",
    ["T"] = "Misión: Posible, pero no probable",
  },
  [2479] = {
    ["D"] = "Llévale una muestra de la mixtura a Serge Hinott, que está en Molino Tarren.$B$BSi hay alguien que pueda curarte, ese es él. Si vives lo suficiente, te enseñará a crear y usar venenos.",
    ["O"] = "Ve a Molino Tarren, en Laderas de Trabalomas, para entregar la muestra de Zanzil a Serge Hinott. $B$BPara llegar a Molino Tarren, coge el zepelín a Entrañas y sigue el camino hacia el sur por el Bosque de Argénteos hacia Trabalomas. ¡Sigue las indicaciones!|n|n",
    ["T"] = "La ayuda de Hinott",
  },
  [2480] = {
    ["D"] = "¡Jamás vi un veneno con tan extraordinario efecto! ¿Zanzil, dices? Nunca he oído hablar de él, pero admiro su genio por crear este brebaje.$B$B¿Quieres que te cure y te enseñe a usar el veneno?$B$BDame un momento para examinar la toxina, $N.",
    ["O"] = "Espera a que Serge Hinott te cure.",
    ["T"] = "La ayuda de Hinott",
  },
  [2498] = {
    ["D"] = "Me acabo de enterar de que Denalan, que está en el Lago Al\'Ameth, ha descubierto la causa de los tumores que les nacen a los Brezomadera. Por favor, habla con él y dile que vas de mi parte.$B$BDate prisa, $N, pues necesita tu ayuda.",
    ["O"] = "Rellian Verdeguja quiere que hables con Denalan, que está en el Lago Al\'Ameth.",
    ["T"] = "Regresa junto a Denalan",
  },
  [2499] = {
    ["D"] = "¡Lo he averiguado! ¡He venido corriendo para encontrarte! En realidad es muy sencillo... La corrupción se ha extendido de un Brezomadera a otro, pero solo uno es la fuente.$B$BEs Roblegüello, lo está envenenando el mayor tumor que he visto... Hasta diría que es gigantesco.$B$BRoblegüello solía vivir en una cueva cerca de mi lago, pero se ha trasladado a un escondrijo al noreste de aquí. $N, dale caza y recoge el tumor. Elimina esta fuente de corrupción de mi hogar.",
    ["O"] = "Hazte con el tumor gigantesco de Roblegüello.",
    ["T"] = "Roblegüello",
  },
  [2500] = {
    ["D"] = "$C, necesito asistencia. Soy un alquimista de cierto renombre y necesito que me ayudes a conseguir ingredientes que se encuentran en las Tierras Inhóspitas.$B$BEsto es lo que necesito: 5 mollejas de águila ratonera, 10 colmillos de coyote montés y 5 fragmentos de elemental de roca. Evidentemente, estos ingredientes están en las propias bestias; si la lucha no es lo tuyo, lleva a un par de amigos duros.$B$BSi me traes todo esto, te ganarás unas monedas.$B$BDime, ¿aceptas?",
    ["O"] = "Ve a las Tierras Inhóspitas a buscar los ingredientes que te ha pedido Ghak Sanadón y llévaselos a Thelsamar.",
    ["T"] = "Componentes de Tierras Inhóspitas",
  },
  [2501] = {
    ["D"] = "Agradezco mucho tu ayuda, $N. Quiero regalarte la receta del elixir que te di antes, pero tengo que pedirte un favor bastante peligroso.$B$BEste arcón contiene tres vasijas de taumaturgia vacías imbuidas de un aura de Armonización que absorberá la sangre de un dragón guardián agostado. Los dragones se pondrán furiosos, así que ten cuidado. Tráeme las vasijas llenas.$B$BA cambio, te daré la receta.",
    ["O"] = "Usa las vasijas de taumaturgia en los dragones guardianes agostados de las Tierras Inhóspitas. Llévaselas a Ghak Sanadón a Thelsamar.",
    ["T"] = "Componentes de Tierras Inhóspitas II",
  },
  [2518] = {
    ["D"] = "Lady Sathrah estuvo una vez en gracia de Elune. Grácil y pura, la araña tejió sus telas de plata a la luz de la luna, atrapando la niebla de la noche. El rocío plateado tenía grandes poderes de curación por lo que se guardó en el templo.$B$BPero en los últimos tiempos, Sathrah cayó en la locura y ahora sus generaciones futuras también están en peligro.$B$B$N, encuentra a Lady Sathrah y pon fin a su sufrimiento. Suele estar al noreste de aquí, cerca del río. Atrápala y tráeme sus hileras de plata.",
    ["O"] = "Recupera las hileras de plata de Lady Sathrah.",
    ["T"] = "Las lágrimas de la Luna",
  },
  [2519] = {
    ["D"] = "Una criatura del bosque, en su día grandiosa, necesita ayuda, $R.$B$BHay una difícil tarea que cumplir.$B$BPor favor, busca a la sacerdotisa A\'moora; ella te lo explicará todo. La encontrarás al sudeste de aquí, dentro del Templo de la Luna.",
    ["O"] = "La hermana Aquinne quiere que hables con la sacerdotisa A\'moora en el Templo de la Luna.",
    ["T"] = "El templo de la Luna",
  },
  [2520] = {
    ["D"] = "Ahora has de cumplir con el cometido que te ha sido asignado. Coge las hileras y ofrece el sacrificio de Lady Sathrah como ofrenda a Elune.$B$BEn el interior de este templo se encuentra la fuente central, lugar en el que debes colocar la ofrenda. Las aguas sagradas purificarán y eliminarán la corrupción que hizo enloquecer a Sathrah.$B$BCuando hayas cumplido tu cometido, vuelve a verme.",
    ["O"] = "La sacerdotisa A\'moora quiere que coloques las hileras de plata de Lady Sathrah en la fuente que hay en el interior del templo, y que después vayas a verla de nuevo.",
    ["T"] = "Sacrificio de Sathrah",
  },
  [2521] = {
    ["D"] = "$R, ¿será capaz tu cerebro de mosquito de entender cuán rara es una esfera draenetista perfecta? Generaciones de draenei han buscado uno solo de estos cristales y solo lograron un destino fatal, su objetivo por cumplir.$B$BAsí que te presentas ante Kum\'isha, sabiendo lo que Kum\'isha ha pedido ¿y aún deseas ayudar? Tú verás, $R... En el momento que sea a lo largo de tus viajes por estas tierras, en cuanto encuentres una esfera draenetista perfecta, tráemela y te recompensaré con riquezas con las que ni siquiera puedes soñar.",
    ["O"] = "En tus viajes por Las Tierras Devastadas, si te encontraras con una esfera draenetista perfecta, llévasela a Kum\'isha el Coleccionista.$B$BTen cuidado, esta gema es uno de los cristales más raros de todo Azeroth. Cualquier criatura de estas tierras podría tener una.$B$BSe te recompensará por cada esfera draenetista perfecta que consigas.",
    ["T"] = "Al servicio de Kum\'isha",
  },
  [2522] = {
    ["D"] = "Aunque el conjuro del portal fracasara, conservo la esperanza de que mi próximo intento funcione.$B$BLa esperanza de los draenei es tan ferviente como las baratijas que codicias, $R. Vuelve a esas tierras malditas y consigue otra esfera draenetista perfecta.$B$BPor supuesto, tú decides si aceptas esta tarea. <sonríe.>",
    ["O"] = "Encuentra una esfera draenetista perfecta y llévasela a Kum\'isha el Coleccionista.",
    ["T"] = "Los esfuerzos de Kum\'isha",
  },
  [2541] = {
    ["D"] = "Los chamanes Tuercepinos que habitan este lugar han descubierto un modo de separar el espíritu de un druida durmiente de su cuerpo físico. Los fúrbolgs han reanimado mi representación física y la están utilizando para atacar a todo el que intente entrar en Túmulo de Ban\'ethil. Yo estoy atrapado en el Sueño Esmeralda y no puedo hacer nada para detenerlos.$B$BTienes que ayudarme. Los chamanes Tuercepinos portan un extraño talismán para llevar a cabo el ritual y me gustaría examinarlo. Por favor $N, tráeme uno.",
    ["O"] = "Llévale un talismán de vudú chamán a Oben Garrafuria al Túmulo de Ban\'ethil.",
    ["T"] = "El druida durmiente",
  },
  [2561] = {
    ["D"] = "Tras haber estudiado el talismán, $N, veo con claridad lo que hay que hacer. Cógelo y haz lo que te digo.$B$BDebes acercarte a la puerta de la cámara cercana. Allí encontrarás mi cuerpo desalmado. Aunque me cueste aceptar lo que voy a decir, no veo otro modo de liberarme del control de los Tuercepinos.$B$BPara que pueda escapar, debes matar mi representación física. Hecho esto, utiliza el talismán vudú sobre mi cuerpo inerte. Vuelve aquí cuando hayas cumplido tu cometido.",
    ["O"] = "Mata el cuerpo desalmado de Oben Garrafuria y después utiliza el talismán vudú.",
    ["T"] = "Druida de la Zarpa",
  },
  [2581] = {
    ["D"] = "Es bien sabido que las hienas poseen una gran fuerza en sus papadas pero ¿tanta como para morder a través de 7,5 centímetros de acero de torio? Imposible, improbable, y lo cierto es que lo he visto con mis propios ojos.$B$BAdéntrate en Las Tierras Devastadas y tráeme 3 papadas de hiena risitas. Cuando busques la hiena risitas, estate $Gatento:atenta; a los jabalíes y a los scorpoks. Para la preparación inicial de polvo también hacen falta 2 pulmones de jabalí maldito y 1 pinza de scorpok.",
    ["O"] = "Llévale 3 papadas de hiena risitas, 2 pulmones de jabalí maldito y 1 pinza de scorpok al mago sangriento Drazial.",
    ["T"] = "Papadas de hiena risitas",
  },
  [2582] = {
    ["D"] = "Ah, $N, $Gbienvenido:bienvenida; de nuevo. Parece que has disfrutado del efecto de impregnación operativa y robusta que la hiena risitas produce. Ten cuidado, aunque el efecto inmediato es un impulso titánico de fuerza, los efectos a largo plazo sobre tu ánimo y tu inteligencia podrían ser peligrosos.$B$BHecha la advertencia, ¿necesitas más? Si es así, tráeme otras 3 papadas de hiena risitas, 2 pulmones de jabalí maldito y 1 pinza de scorpok.",
    ["O"] = "Llévale 3 papadas de hiena risitas, 2 pulmones de jabalí maldito y 1 pinza de scorpok al mago sangriento Drazial.",
    ["T"] = "Ira de las Eras",
  },
  [2583] = {
    ["D"] = "En Las Tierras Devastadas hay dos especies de jabalí: el cenicrín y el jabinferno.$B$BAl parecer, la fortaleza de estas bestias los vuelve prácticamente inmunes a cualquier daño, ya que los demás animales los encuentran demasiado difíciles de matar y, por lo tanto, que no merece la pena tal esfuerzo.$B$BTenemos que examinarlos más a fondo, $N. Si me traes 3 pulmones de jabalí maldito, 2 pinzas de scorpok y 1 cerebro de basilisco, con ellos crearé un elixir divino a partir de los residuos del polvo. ¡Serás mi $Gprimer:primera; cobaya!",
    ["O"] = "Llévale 3 pulmones de jabalí maldito, 2 pinzas de scorpok y 1 cerebro de basilisco al mago sangriento Drazial.|n",
    ["T"] = "La vitalidad del jabalí",
  },
  [2584] = {
    ["D"] = "La primera vez que te administré el elixir, $N, sabía que una o dos aplicaciones no bastarían para saciar tu apetito. Incluso ahora veo la ansiedad en tus ojos. Ese brebaje tiene propiedades adictivas a las que, al parecer, no se resiste ni la voluntad más férrea.$B$B¡Que así sea! Si necesitas más cóctel de zumo de pulmón, vuelve al páramo y encuéntrame 3 pulmones de jabalí maldito, 2 pinzas de scorpok y 1 cerebro de basilisco.",
    ["O"] = "Llévale 3 pulmones de jabalí maldito, 2 pinzas de scorpok y 1 cerebro de basilisco al mago sangriento Drazial.|n",
    ["T"] = "El espíritu del jabalí",
  },
  [2585] = {
    ["D"] = "¿Los has visto combatir alguna vez, $N? Me refiero a los scorpoks. ¿Has visto alguna vez la gracia y la agilidad de sus movimientos? ¿La firmeza de sus golpes?$B$BSi pudiéramos aislar de alguna manera esa agilidad y envasarla, nuestra riqueza sería más... digo... sería un tremendo impulso para la comunidad científica.$B$BAyúdame, $N, y te regalaré una muestra del polvo.$B$BPara crear una muestra estable, voy a necesitar 3 pinzas de scorpok, 2 mollejas de buitre y 1 pulmón de jabalí maldito.",
    ["O"] = "Llévale 3 pinzas de scorpok, 2 mollejas de buitre y 1 pulmón de jabalí maldito al mago sangriento Drazial.|n",
    ["T"] = "El golpe decisivo",
  },
  [2586] = {
    ["D"] = "¡Estás $Gvivo:viva;! Esto sí que es una buena noticia. Supongo que has vuelto a por más polvo de scorpok, ¿no?$B$BMuy bien, $N, vuelve al páramo y no regreses hasta que hayas recolectado 3 pinzas de scorpok, 2 mollejas de buitre y 1 pulmón de jabalí maldito.",
    ["O"] = "Llévale 3 pinzas de scorpok, 2 mollejas de buitre y 1 pulmón de jabalí maldito al mago sangriento Drazial.|n",
    ["T"] = "La sal del Scorpok",
  },
  [2601] = {
    ["D"] = "Estos páramos están plagados de basiliscos Rocarroja, $N. Estos basiliscos se han adaptado y se han multiplicado más que cualquier otra especie de la región.$B$BLos he observado a cierta distancia cuando cazan y en sus actividades cotidianas; esas bestias muestran un comportamiento reflexivo más complejo incluso que el de ciertos humanoides.$B$B¡Debo estudiar sus cerebros!$B$BConsígueme 10 cerebros, $N. Aunque el preparado no estará completo si no tengo además 2 mollejas de buitre para crear la base de la solución.",
    ["O"] = "Llévale 10 cerebros de basilisco y 2 mollejas de buitre a la maga sangrienta Lynnore.|n",
    ["T"] = "El bocado de basilisco",
  },
  [2602] = {
    ["D"] = "¿Ya has perdido la concentración? ¡Debo seguir investigando!$B$BAh, sí, el precio sigue siendo el mismo, $N. Adéntrate en Las Tierras Devastadas y tráeme 10 cerebros de basilisco Rocarroja y 2 mollejas de buitre.|n",
    ["O"] = "Llévale 10 cerebros de basilisco y 2 mollejas de buitre a la maga sangrienta Lynnore.|n",
    ["T"] = "La mente infalible",
  },
  [2603] = {
    ["D"] = "La muerte tiene alas en estas tierras devastadas, $R. Yo he visto a esos negros asesinos y carroñeros dándose festines con los cadáveres de muchos exploradores... alimentándose incluso de los cadáveres más viles y enfermos. Es un milagro que sus organismos hayan sobrevivido a la pestilencia que reclama estas tierras.$B$B¡Ayúdame con mis estudios, $N! ¡Necesito mollejas! También necesitaré dos papadas de hiena para el brebaje.$B$BSerás doblemente recompensado por tus esfuerzos.",
    ["O"] = "Llévale 10 mollejas de buitre y 2 papadas de hiena risitas a la maga sangrienta Lynnore.|n",
    ["T"] = "El vigor del buitre",
  },
  [2604] = {
    ["D"] = "Veo que el fuego interno ha menguado. ¿Dirías que la euforia espiritual ha durado una hora?$B$BSi más chicle de mollejas quieres, más mollejas necesito. De vuelta al páramo, $N. No regreses hasta que hayas recuperado 10 mollejas y 2 papadas.",
    ["O"] = "Llévale 10 mollejas de buitre y 2 papadas de hiena risitas a la maga sangrienta Lynnore.|n",
    ["T"] = "Dominación espiritual",
  },
  [2605] = {
    ["D"] = "He tenido una idea. He oído hablar de unas extrañas criaturas llamadas colectores de rocío que habitan hacia el suroeste... capaces de almacenar agua en unas glándulas especiales.$B$BCreo que si pudiera hacerme con una de esas glándulas, sería capaz de idear una bebida para saciar mi espantosa sed. ¿Crees que podrías ayudarme, $N?$B$BBusca el jardín de cactos, $N, pues allí encontrarás a los colectores de rocío.",
    ["O"] = "Consigue una glándula cargada de rocío y llévasela a Marin Tragonublo a Gadgetzan.",
    ["T"] = "El goblin sediento",
  },
  [2607] = {
    ["D"] = "Parece ser que has obtenido más información de la que me esperaba. Ciertamente, esto es un extraño giro de los acontecimientos, $N.$B$B¿Una cura? Mmm... Vete al sótano y habla con Doc Mixilpixil. Quizás pueda ayudarte.",
    ["O"] = "Habla con Doc Mixilpixil en el sótano de los Cuarteles.",
    ["T"] = "Toque de Zanzil",
  },
  [2608] = {
    ["D"] = "¡Oh! Pareces como $Gmuerto:muerta;. Corrección, he visto muchos muertos, tú tienes peor aspecto. $GCaballero:Señorita;, tú serías lo que la muerte obtuvo como resultado en su último examen médico.$B$B¡Ja! ¿Has oído eso, Noarm? ¡Escríbelo!$B$B*Tose* Mmm, sí. Para poder ayudarte, necesito sacarte sangre. No te preocupes, solo duele las primeras... cincuenta veces, o así... Túmbate cuando estés $Gpreparado:preparada; para el tratamiento, $N.",
    ["O"] = "Túmbate para que te examine Doc Mixilpixil. ¿Cómo va a encontrar la cura si no encuentra la causa?|n",
    ["T"] = "Toque de Zanzil",
  },
  [2609] = {
    ["D"] = "Es unánime, $N. Tienes la \'itis\'.$B$BPor suerte para ti, tengo la cura para la \'itis\'.$B$BEsto es lo que necesito: un ramo de flores silvestres simples, un vial emplomado, un tubo de bronce y por último, pero no menos importante, una bobina de hilo de seda verde pistacho. La vieja Emma dejó una bobina para mí en \'El Paño Fino\'. Podrás encontrar la tienda en los canales.$B$BY ahora date prisa, $N, e intenta no tocar a nadie hasta que estés $Gcurado:curada;. Oh, y el doctor Montgomery dice que necesitas una ducha... Lo dice él, no yo.",
    ["O"] = "Lleva a Doc Mixilpixil un ramo de flores silvestres simples, un vial emplomado, un tubo de bronce y una bobina de hilo de seda verde pistacho. La \'itis\' no se cura sola, $Gjovencito:jovencita;.",
    ["T"] = "Toque de Zanzil",
  },
  [2621] = {
    ["D"] = "Arrepentimiento. Eso es lo que me queda. Una mal decisión que desembocó en varias tragedias. ¡¿Por qué diablos no esperaría las órdenes?! ¡A lo mejor habrían descrito el horror que nos aguardaba en esas malditas tierras! ¿O quizás nos habrían enviado a otro destino? A fin de cuentas, fue culpa mía. Todo fue culpa mía.$B$BAsí que aquí estoy, esperando órdenes. Debo conocerlas. Debo tener esas órdenes.$B$BEl comandante de envíos Ruag vive en Rocal. Encuéntralo y averigua qué fue de esas órdenes.|n",
    ["O"] = "Habla con el comandante de envíos Ruag en Rocal, en el Pantano de las Penas.",
    ["T"] = "El deshonrado",
  },
  [2622] = {
    ["D"] = "¿Las órdenes? ¿Y qué harías tú con las órdenes?$B$B¿Insinúas algo, $N? ¡Por supuesto que transmití esas órdenes! Tendieron una emboscada a los soldados que envié con ellas, los múrlocs les dieron una brutal paliza. Solo uno sobrevivió, si se le puede llamar sobrevivir. Apenas puede hablar, es incapaz de andar y hay que alimentarlo a través de un tubo. Si quieres saber qué ocurrió con esas órdenes, habla con Bengor. Seguro que está por aquí cerca... y, $N, ten mucho cuidado con dónde metes la nariz, te la podrían arrancar de un bocado.",
    ["O"] = "Habla con Bengor en Rocal, en el Pantano de las Penas.",
    ["T"] = "Las órdenes perdidas",
  },
  [2623] = {
    ["D"] = "El Comandante de envíos dio las órdenes. Tomamos... tomamos las órdenes. Camino de Las Tierras Devastadas, emboscada.$B$BMúrlocs... Muchos múrlocs... Uno llamado voz del pantano. Me arrancó las órdenes de las manos cuando una lanza me atravesó la cabeza. Este... caverna...$B$B<Bengor fallece.>|n",
    ["O"] = "Recupera las órdenes del Jefe de Guerra y vuelve junto al héroe caído de la Horda.",
    ["T"] = "La voz del pantano",
  },
  [2661] = {
    ["D"] = "Aquí tienes las partículas violeta, $N. ¡Llévaselo a Marin y dile que me debe un favor!",
    ["O"] = "Roxio quiere que le lleves las partículas violeta a Marin Tragonublo a Gadgetzan.",
    ["T"] = "Un envío para Marin",
  },
  [2662] = {
    ["D"] = "Ahora tengo que mezclar estos polvos con el rocío.$B$BMárchate y regresa dentro de un minuto... ¡y ya tendré preparado el fabuloso Elixir de Tragonublo!",
    ["O"] = "Marin Tragonublo quiere que hables con él cuando acabe de preparar el elixir.",
    ["T"] = "Elixir de Tragonublo",
  },
  [2681] = {
    ["D"] = "No podrás vencer a las tropas de Razelikh y a sus seguidores tú $Gsolo:sola;.$B$BReúne a un grupo de aguerridos aventureros ¡y libera a mis hombres de su eterna tortura! 18 piedras has de hacer añicos.$B$BCombate con brío y muere con honor, $N.",
    ["O"] = "Libera a 9 sirvientes de Razelikh, 3 sirvientes de Sevine, 3 sirvientes de Allistarj y 3 sirvientes de Grol. Vuelve junto al Héroe Caído cuando hayas acabado la tarea. Debes permanecer cerca de las piedras o el proceso no funcionará.",
    ["T"] = "Las piedras que nos vinculan",
  },
  [2701] = {
    ["D"] = "Acepta esta ancestral reliquia. Ya no nos sirve.$B$BQue te sea de provecho en tu lucha contra la Legión.|n",
    ["O"] = "Abre el cofre y reclama tu recompensa.",
    ["T"] = "Los héroes de los antiguos",
  },
  [2702] = {
    ["D"] = "Me has honrado con tu nobleza y heroísmo, $N.$B$BY también los has honrado a ellos...",
    ["O"] = "Habla con el cabo Thund Pezuña Quebrada.",
    ["T"] = "Los héroes de los antiguos",
  },
  [2721] = {
    ["D"] = "Concluye un capítulo de tu aventura y comienza uno nuevo, $N.$B$BUno de mis hombres ha desaparecido, perdido en algún lugar de Las Tierras Devastadas. Me refiero al teniente Kirith, claro.$B$BComo he mencionado antes, Allistarj nos capturó y torturó a ambos, pero al pobre Kirith le falló la fuerza de voluntad. Desde mi jaula podía oír los experimentos; los alaridos de su voz torturada recorrían cada recoveco de la caverna. Desde entonces no he vuelto a ver ni a oír a Kirith.",
    ["O"] = "Averigua qué le ha ocurrido al teniente Kirith.",
    ["T"] = "Kirith",
  },
  [2741] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "El Superhuevomático",
  },
  [2742] = {
    ["D"] = "¡$R!$B$B¡Rin\'ji perdió el hacha y fue capturado! Ayuda a Rin\'ji a escapar, por favor. ¡Rin\'ji sabe que a $Gun:una; $R no te gusta su gente, pero por favor! ¡Quiero salir!$B$BTengo algo para amable $R si me ayudas a escapar...$B$BAlgo secreto escondido en los Acantilados Dominantes.",
    ["O"] = "Ayuda a Rin\'ji a salir de la Avanzada Quel\'Danil y encuentra su secreto oculto en los Acantilados Dominantes.",
    ["T"] = "¡Capturaron a Rin\'ji!",
  },
  [2743] = {
    ["D"] = "Dejémonos de rodeos, $R; ando escaso de tiempo.$B$BDebes destruir la tríade de poderes que protege al señor demoníaco, Razelikh. No será una tarea tan sencilla como liberar a un alma maldita.$B$B<Kirith esboza una sonrisa.>$B$BVerás, cada subordinado de Razelikh lleva colgado del cuello un amuleto, una creación de Razelikh. Y no es otro que el amuleto necesario para invocar al archidemonio.$B$BVuelve con Trebor y dile lo que hace falta para invocar a Razelikh. Él te dará entonces el resto de las instrucciones.",
    ["O"] = "Vuelve junto al héroe caído de la Horda con tus hallazgos.",
    ["T"] = "La cubierta de la Oscuridad",
  },
  [2744] = {
    ["D"] = "La tríade está protegida por Razelikh.$B$BA cambio de su servidumbre, Razelikh les concedió a cada uno poderes casi ilimitados sobre Las Tierras Devastadas. Cada pieza de amuleto está adaptada a la fuerza vital de su propietario y le confiere la inmortalidad. Mientras permanezcan en Las Tierras Devastadas no se les puede matar, su vida se extiende hasta el final de los días.$B$BSé de alguien que podrá ofrecerte más ayuda, $N. Ve a Azshara y encuentra al cazademonios, Loramus Thalimedes. Busca en las islas.",
    ["O"] = "Habla con Loramus Thalimedes en Azshara.",
    ["T"] = "El cazademonios",
  },
  [2751] = {
    ["D"] = "Este martillo ha pasado por 20 generaciones de Omosh, $N. Hemos moldeado cien mil toneladas de metal. Por desgracia, todo se acabará con Orokk, la vergüenza de los Omosh: no puedo tener descendencia.$B$B¡Las recetas secretas de Omosh no pueden morir conmigo! Alguien tiene que aceptar este legado, aunque no sea Omosh.$B$BDemuestra tu valía, $N. Tráeme dos brazales de bronce estampados, dos espadas magnas de bronce y dos garras afiladas. Entonces seguiremos.",
    ["O"] = "Lleva 2 brazales de bronce estampados, 2 espadas magnas de bronce y 2 garras afiladas a Orokk Omosh a Orgrimmar.|n",
    ["T"] = "Almenas barbáricas",
  },
  [2752] = {
    ["D"] = "Veo que tienes muchas ganas de aprender, $N. Quieres absorber toda la sabiduría Omosh.$B$BTe daré tres lecciones.$B$BVuelve al yunque, empápate del calor de la forja y crea cuatro hachas de batalla de bronce y cuatro martillos de guerra de bronce. Entonces te enseñaré a fabricar unos espaldares de hierro.",
    ["O"] = "Lleva 4 hachas de batalla de bronce y 4 martillos de guerra de bronce a Orokk Omosh a Orgrimmar.",
    ["T"] = "Espaldares de hierro",
  },
  [2753] = {
    ["D"] = "Solo te daré dos lecciones más, $N. Sé que te duelen los músculos, pero hazlo por los Omosh.$B$BTráeme cuatro yelmos, cuatro brazales y dos pares de leotardos, todo de hierro verde. ¡Así aprendes y perjudicas al enemigo!",
    ["O"] = "Lleva 4 yelmos de hierro verde, 4 brazales de hierro verdes y 2 pares de leotardos de hierro verdes a Orokk Omosh a Orgrimmar.",
    ["T"] = "¡Aplástalos!",
  },
  [2754] = {
    ["D"] = "Esta es la última lección de Orokk, $N. Regresa a la forja sagrada. Trae a Orokk dos grandes mazas de hierro sólido, dos botas de bronce bañadas en plata y dos guanteletes de bronce bañados en plata.$B$B¡Se morirán de miedo con solo verte!|n",
    ["O"] = "Lleva 2 grandes mazas de hierro sólido, 2 botas de bronce bañadas en plata y 2 guanteletes de bronce bañados en plata a Orokk Omosh en Orgrimmar.",
    ["T"] = "Cuernos de frenesí",
  },
  [2755] = {
    ["D"] = "Orokk no llora, $N.$B$B<Se seca una lágrima.>$B$BOrokk tiene otro secreto de la familia Omosh que enseñarte. Orokk morirá feliz gracias a $N.$B$BAprende la danza de la alegría de los Omosh y te enseñaré lo que sé de los puños de hierro.|n",
    ["O"] = "Mira la danza de la alegría de los Omosh.",
    ["T"] = "Las alegrías de los Omosh",
  },
  [2756] = {
    ["D"] = "En las leyendas, Aturk trituraba las calaveras de sus enemigos hasta convertirlas en un polvillo fino. Esos días ya se fueron, $R. El Jefe de Guerra pide la concordia entre los clanes. ¡Ahora solo se vigila a los enemigos de reojo!$B$BEs increíble, pero esta es la voluntad de Thrall.$B$B¡Pero basta de batallitas! Has venido a aprender y eso es lo que harás. Tráeme cuatro corazas de acero y cuatro yelmos de placas de acero y probarás el sabor de la guerra.",
    ["O"] = "Lleva 4 corazas de acero y 4 yelmos de placas de acero a Aturk el Yunque a Orgrimmar.|n",
    ["T"] = "Los viejos hábitos",
  },
  [2757] = {
    ["D"] = "Mira, no vas a aprender nada si andas perdiendo el tiempo por aquí.$B$BConozco a un tipo que suele estar por la herrería de Bahía del Botín. Enséñale esta insignia y te enseñará todo lo que hay que saber del mitril.$B$B¿Qué me dices, eh? Ya es hora de que aprendas cosas nuevas, $N.$B$BSi te animas, ve a ver a ese tipo, McGavan. Enséñale esto y así verá que vas en serio.",
    ["O"] = "Habla con McGavan en Bahía del Botín.",
    ["T"] = "¡Bahía del Botín o nada!",
  },
  [2758] = {
    ["D"] = "Cuando tenía más o menos tu edad, un enano llamado Galvan me acogió y me enseñó a forjar armaduras. Antes de partir a Bahía del Botín, me hizo prometer que instruiría a todos aquellos que sintieran una férrea determinación por aprender el oficio de la herrería. Así que, aquí estoy, muchos años después.$B$BAhora, ha llegado la hora de que aprendas algo. Hazme seis brazales de escamas doradas y aprenderás el significado de la herrería, $N.",
    ["O"] = "Lleva seis brazales de escamas doradas a Hank el Martillo en Ventormenta.",
    ["T"] = "El origen de la herrería",
  },
  [2759] = {
    ["D"] = "Si bien Galvan se marchó de la Bahía del Botín muchos años atrás, todavía quedan en la ciudad otros miembros de la orden esperando encontrar, algún día, a alguien más digno de la tutela de Galvan.$B$BLleva esta insignia y preséntasela a McGavan en la Bahía del Botín.$B$BSi consigues llegar hasta allí, sabrá que eres $Gdigno:digna;. ¡Tengo fe en ti, $N!",
    ["O"] = "Habla con McGavan en Bahía del Botín.",
    ["T"] = "Buscando a Galvan",
  },
  [2760] = {
    ["D"] = "La mayoría de nuestros reclutas nunca llegan a la Bahía del Botín, $N. El solo resplandor de la insignia es suficiente para activar el ansia de botín de incluso los herreros más devotos. Son unos necios, la insignia es tan solo una gotita comparada con la riqueza de los objetos que verás.$B$BEncuentra a Galvan el Antiguo y enséñale este alfiler. Vive al norte, entre Zul\'Gurub y el Túmulo Ogro Mosh\'Ogg.|n",
    ["O"] = "Habla con Galvan el Antiguo en la Vega de Tuercespina.|n",
    ["T"] = "La Orden del mitril",
  },
  [2761] = {
    ["D"] = "Muchos herreros creen que la minería y la herrería van por separado, son trabajos independientes. ¿Pero podría existir la herrería sin la minería? ¡Ajá, es imposible! El buen herrero debe ser también un buen minero.$B$BDemuestra a Galvan de lo que eres capaz, $N. Tráeme cuarenta barras de hierro y cuarenta de mitril. A cambio, Galvan te enseñará el secreto de los pantalones de mitril ornamentado.",
    ["O"] = "Lleva cuarenta barras de mitril y cuarenta barras de hierro a Galvan el Antiguo en Tuercespina.",
    ["T"] = "Funde o no funde",
  },
  [2762] = {
    ["D"] = "Dicen que la veraplata es más fuerte y duradera que el mitril. Galvan está de acuerdo con esta valoración de los minerales pero, al contrario de su nombre, ¡la veraplata no es verídica! ¡El mitril es un mineral mucho más noble! ¡JA! A pesar de todo, para esta lección necesitarás encontrar al mentiroso mineral, $N.$B$BTráeme cuarenta barras de mitril y cinco de veraplata. A cambio, aprenderás el secreto de los guantes de mitril ornamentado y, lo más importante, ¡aprenderás que la veraplata no tiene nada de verdad!",
    ["O"] = "Lleva cuarenta barras de mitril y cinco barras de veraplata a Galvan el Antiguo en Tuercespina.",
    ["T"] = "La veraplata no es verídica",
  },
  [2763] = {
    ["D"] = "Recoger gemas preciosas es de gran importancia para nuestro oficio, $N. Y pocas gemas son más preciosas que el citrino. Las propiedades mágicas de las que están imbuidos los objetos se amplifican enormemente con la presencia de citrino.$B$BPara esta lección, fortificaremos cuarenta barras de mitril con cuatro citrinos. El resultado será una mejor comprensión de la gemología mágica y el conocimiento para crear sobrehombros de mitril ornamentado.",
    ["O"] = "Lleva cuarenta barras de mitril y cuatro citrinos a Galvan el Antiguo en Tuercespina.",
    ["T"] = "El arte de imbuir",
  },
  [2764] = {
    ["D"] = "Si estás $Gpreparado:preparada; para finalizar tu instrucción con Galvan, deberías ir a Gadgetzan.$B$BAllí encontrarás a mi mejor alumno: Trenton Mazaligera. Puede enseñarte mucho sobre el mitril ornamentado. ¡Quédate y aprende!$B$BOh, ¡Gadgetzan está en Tanaris, por supuesto!",
    ["O"] = "Habla con Trenton Mazaligera en Gadgetzan, en Tanaris.",
    ["T"] = "El mejor alumno de Galvan",
  },
  [2765] = {
    ["D"] = "Cuando viniste por primera vez a verme, sabía que acabarías siendo uno de mis más brillantes alumnos. Le diste un abalorio de bastante poder a McGavan sin dudarlo. Y lo hiciste solo para poder conocerme. Esa fe ciega y devoción debe ser recompensada.$B$BHas conseguido gran pericia en la herrería, $N. ¡Ahora te esperan las recompensas!",
    ["O"] = "Reclama tu recompensa a Galvan el Antiguo.",
    ["T"] = "¡Experto herrero!",
  },
  [2767] = {
    ["D"] = "Vuelves a oír la voz del gnomo que sale del robot:$B$B\"Tengo que llevar a OOX-22/FE a un lugar abierto y seguro en el que pueda iniciar el largo proceso de despegue. Tiene un dispositivo de invisibilidad integrado, pero necesito tiempo para que vuelva a estar operativo. ¡Escolta al robot hasta, digamos, el camino principal! Allí podré tomarme mi tiempo para hacer que todo vuelva a funcionar correctamente.\"$B$B\"¡Escóltalo para que llegue sano y salvo y después ven a verme a Bahía del Botín! Oglethorpe Obnoticus. ¡Corto y cierro!\"",
    ["O"] = "Escolta a OOX-22/FE para que llegue sano y salvo y después preséntate ante Oglethorpe Obnoticus en Bahía del Botín.",
    ["T"] = "¡Escolta a OOX-22/FE!",
  },
  [2768] = {
    ["D"] = "¡El sargento Bly me ha robado! Dijo que solo lo cogía prestado, ¡pero me robó mi querida vara divinomática! Sin la vara, ¿cómo puedo saber dónde hacer nuevos pozos?$B$B¡Encuentra a Bly y tráeme mi vara! He oído que condujo a su panda de aventureros a buscar tesoros en Zul\'Farrak, al Oeste. Seguro que lo encontrarás allí.$B$BEsperemos que los trols se hayan ocupado de él porque si tuvieras que combatir con él por la vara sería un combate realmente duro.",
    ["O"] = "Lleva la vara divinomática al ingeniero jefe Pasaquillas en Gadgetzan.",
    ["T"] = "Vara divinomática",
  },
  [2771] = {
    ["D"] = "Parece como si el maestro lo tuviera todo planeado. A mí no me enseñó a crear los objetos que te enseñó a crear a ti, y a ti no te enseñó a crear los objetos que me enseñó a crear a mí.$B$BEl maestro es sabio, $N. ¡Podremos instruirnos el uno al otro!$B$BEnséñame a hacer un almófar de mitril y sobrehombros de mitril ornamentado y yo te enseñaré a crear un yelmo de mitril ornamentado.",
    ["O"] = "Llévale un par de almófares de mitril y unos sobrehombros de mitril ornamentado a Trenton Mazaligera.",
    ["T"] = "La cabeza sobre los hombros",
  },
  [2772] = {
    ["D"] = "Durante el Campeonato de herrería del valle del año pasado, todos los que estaban atentos vieron cómo un cobarde de la Fundición de Ventura y Cía. vino directo a mi pierna. Sin embargo, lo que no saben es la razón por la cual pude continuar y finalmente ganar el torneo. ¡Eso es, botas de mitril ornamentado!$B$BSin embargo, tengo que aprender a hacer pantalones de mitril ornamentado para el campeonato de este año pues participa un machacador de Ventura y Cía. ¡Enséñame a hacer los pantalones y yo te enseñaré a hacer las botas!",
    ["O"] = "Llévale un par de botas de mitril pesado y unos pantalones de mitril ornamentado a Trenton Mazaligera.|n",
    ["T"] = "El mundo a tus pies",
  },
  [2773] = {
    ["D"] = "Por aquí me conocen como el chico de mitril. Yo abastezco de mitril ornamentado a todos los aventureros del mundo.$B$B<Trenton susurra.>$B$BPocos saben que desconozco la técnica de creación de guantes de mitril ornamentado. ¡Sh! Este será nuestro pequeño secreto.$B$B$N, enséñame la técnica para crear guantes de mitril ornamentado y, a cambio, yo te mostraré el resultado de la culminación de mi instrucción a las órdenes de Galvan: la coraza de mitril ornamentado.",
    ["O"] = "Llévale un par de corazas de mitril pesado y unos guantes de mitril ornamentado a Trenton Mazaligera.",
    ["T"] = "El chico de mitril",
  },
  [2781] = {
    ["D"] = "SE BUSCA: Califa Escorpijón.$B$BLa compañía de aguas de Gadgetzan en Gadgetzan, Tanaris ofrece una generosa recompensa por la cabeza del Califa Escorpijón, el líder de los Vagayermos. Entre los crímenes que se le atribuyen se incluyen:$B$B1. ¡El asesinato de empleados de la compañía de aguas de Gadgetzan!$B2. ¡El robo de recursos de la compañía de aguas de Gadgetzan!$B3. ¡El allanamiento de las instalaciones de la compañía de aguas de Gadgetzan!$B$BEntrega una prueba del fallecimiento del Califa Escorpijón al ingeniero jefe Pasaquillas y recibirás una recompensa al momento.",
    ["O"] = "Llévale la cabeza de Califa Escorpijón al ingeniero jefe Pasaquillas, que está en Gadgetzan.",
    ["T"] = "SE BUSCA: Califa Escorpijón",
  },
  [2782] = {
    ["D"] = "En la tablilla se ven grabados de los trols Secacorteza. El secreto de Rin\'ji es todo un misterio...$B$BPuede que esto sea muy valioso. Por otra parte, también puede que no lo sea. Un Secacorteza en peligro probablemente haría cualquier cosa con tal de escapar.",
    ["O"] = "Oran Snakewrithe es una conocida erudita en los trols Secacorteza; quizás pueda descifrar la tablilla. Vive en el Barrio de la Magia de Entrañas.",
    ["T"] = "Secreto de Rin\'ji",
  },
  [2783] = {
    ["D"] = "Luchamos contra el enemigo equivocado, $N.$B$BEl verdadero poder de estas tierras continúa creciendo en fuerza y número mientras nos peleamos con los orcos por estos terrenos moribundos.$B$B¿No te parece que estamos librando una batalla contra la Horda para un buen día terminar exterminados por los ejércitos de la Legión Ardiente? Es fútil.$B$BDebemos dejar las diferencias de lado, $N, así que habla con el héroe caído de la Horda. Él sabe la verdad que se oculta detrás de Las Tierras Devastadas.",
    ["O"] = "Habla con el héroe caído de la Horda donde Las Tierras Devastadas lindan con El pantano.",
    ["T"] = "Peleas mezquinas",
  },
  [2784] = {
    ["D"] = "Esta es mi historia, $R.",
    ["O"] = "Escucha la historia del héroe caído de la Horda.",
    ["T"] = "Pérdida de honor",
  },
  [2801] = {
    ["D"] = "Antes de que podamos proseguir, debes oír mi historia.",
    ["O"] = "Escucha la historia del héroe caído de la Horda.",
    ["T"] = "Una historia triste",
  },
  [2821] = {
    ["D"] = "Tengo mi filosofía: en peletería, la calidad del trabajo debe ser la mejor posible, sin excusas. Cualquiera que te diga que la calidad no es importante, está intentando venderte un chalé en primera fila de playa en Dun Morogh.$B$BSiempre estoy buscando materiales de primera calidad y los pellejos de yeti son mi gran hallazgo. Los yetis son criaturas de Feralas. Se encuentran muchos en la Vega Cicatriz Feral. Quiero algunos pellejos. Te daré uno de mis artículos de cuero si me traes 10. Si te parece justo, tenemos un trato.",
    ["O"] = "Lleva 10 pellejos de yeti gruesos a Pratt McGrubben al Bastión Plumaluna.",
    ["T"] = "La marca de la calidad",
  },
  [2822] = {
    ["D"] = "Me enorgullezco de mi trabajo de peletería y creo que jamás se debería renunciar a la calidad. Cualquiera que te diga que la calidad no es importante, ¡intentará también convencerte de que la tierra no tiene secretos!$B$BSiempre estoy buscando materiales de primera calidad y en mi búsqueda he descubierto los pellejos de yeti. Los yetis son nativos de Feralas y a menudo se encuentran al oeste de aquí, en la Vega Cicatriz Feral. Te dejaré elegir uno de mis artículos de cuero si me traes 10 pellejos. Si te parece una buena oferta, ¡tenemos un trato!",
    ["O"] = "Lleva 10 pellejos de yeti gruesos a Jangdor Zanco Veloz al Campamento Mojache.",
    ["T"] = "La marca de la calidad",
  },
  [2841] = {
    ["D"] = "Hasta hace dos años, la tecnología gnómica solo servía para echarse unas risas. Las cosas han cambiado mucho; han construido una plataforma mejor que todas las nuestras juntas. ¡Quiero los planos, $N!$B$BEstán en una caja fuerte cerca del mekigeniero Termochufe. Para empeorar las cosas, él es el único que sabe la combinación.$B$BHaz lo que sea por conseguirla, $N. ¡Tienes que abrir la caja y traerme los planos!|n",
    ["O"] = "Consigue la combinación de la caja fuerte de Termochufe en Gnomeregan y lleva los planos de la plataforma a Nogg a Orgrimmar.|n",
    ["T"] = "Las guerras de la plataforma",
  },
  [2842] = {
    ["D"] = "En cuanto Kernobee nos avisó de lo de la plataforma gnómica, Scooty y yo empezamos a trabajar en el Transpoliportador 6000. Es el medio más seguro y rápido para ir a Gnomeregan.$B$BYa está a punto para despegar. Si quieres probarlo, $N, ve a Bahía del Botín y habla con Scooty en la alcándara de jinete de Viento. Para ir a Bahía del Botín tienes que coger el barco en Trinquete.$B$BPor supuesto, puedes ir a Gnomeregan cruzando las montañas de Dun Morogh, que están infestadas de enanos.",
    ["O"] = "Habla con Scooty en Bahía del Botín.",
    ["T"] = "Scooty, ingeniero jefe",
  },
  [2843] = {
    ["D"] = "Mientras tengas el transpondedor, todo lo que necesitas hacer es colocarte sobre la plataforma y serás $Gteletransportado:teletransportada; a Gnomeregan.$B$BQuizás experimentes algunos extraños efectos secundarios, pero ninguno será permanente... al menos ahora.$B$BCasi me olvido de lo más importante, la vuelta. Cuando quieras volver, colócate en la plataforma del Transpoliportador en Gnomeregan.$B$BTienes que calibrar el transpondedor según tu talla y peso.|n",
    ["O"] = "Espera a que Scooty calibre el transpondedor goblin.",
    ["T"] = "¡Gnomer-yaaaaa!",
  },
  [2844] = {
    ["D"] = "No valgo para nada, $N. Se supone que tenía que vigilar a mi amiguita Shay y, no sé cómo, se me perdió. No sé muy bien qué ocurrió, pero la última vez que la vi estábamos andando por las ruinas que hay al norte de aquí.$B$BIba todo bien. Y de repente, me giro... ¡y ella ya no estaba!$B$BNo sé qué hacer... ¿Alguna idea, $N? Estoy terriblemente preocupado.$B$B¡Espera! ¡Tú puedes ir a buscar a Shay! Con tus piernas, irás más rápido que yo.",
    ["O"] = "Localiza a la elfa de la noche perdida.",
    ["T"] = "El guardián gigante",
  },
  [2846] = {
    ["D"] = "Hace mucho tiempo tenía una bonita joya, la tiara de las profundidades. No solo era hermosa... tenía un gran poder para quien sabía usarlo.$B$BPor eso, cuando la hidromántica Velratha oyó hablar de ella quiso tenerla. Envío a sus agentes a mi hogar y me la robaron cuando estaba fuera. ¡Ladrones!$B$B¡Quiero recuperar mi tiara! Ve a Zul\'Farrak, la ciudad trol de Tanaris, encuentra a Velratha y quítale la tiara. Cuando la tengas vuelve a verme y te ganarás mi favor.",
    ["O"] = "Lleva la tiara de las profundidades a Tabetha en el Marjal Revolcafango.",
    ["T"] = "Tiara de las profundidades",
  },
  [2847] = {
    ["D"] = "¡$N, te estás convirtiendo en $Gun peletero:una peletera; bastante $Gbueno:buena;! Yo conozco algunos patrones que podrían interesarte... Por ejemplo, ¡el de la armadura de cuero salvaje! El cuero salvaje aprovecha las potentes y caóticas propiedades de la atriplex salvaje que se encuentra en áreas de exuberante vegetación y en algunas criaturas de la región. Las armaduras que se forjan con ella son las mejores para alguien de tu habilidad, te lo garantizo.$B$BTe enseñaré el patrón, pero tendrás que trabajar para mí durante un tiempo. Para empezar, tendrás que traerme 10 piezas de cuero pesado. Ya detallaremos algo más después.",
    ["O"] = "Lleva 10 cueros gruesos a Pratt McGrubben al Bastión Plumaluna.",
    ["T"] = "Armadura de cuero salvaje",
  },
  [2848] = {
    ["D"] = "Los sobrehombros de cuero salvaje ofrecen una protección de primera a la parte superior de los brazos y a los hombros de quien las lleva. Como ocurre con la armadura de cuero salvaje, cuando se cose la atriplex salvaje a las hombreras, esta les imbuye una mejora potente aleatoria.$B$BPara este patrón necesitaré 6 refuerzos para armadura gruesos y 1 atriplex salvaje. No deberías tener problemas para conseguirlos.",
    ["O"] = "Lleva 6 refuerzos para armadura gruesos y una atriplex salvaje a Pratt McGrubben al Bastión Plumaluna.",
    ["T"] = "Sobrehombros de cuero salvaje",
  },
  [2849] = {
    ["D"] = "Los jubones de cuero salvaje son la pieza principal de cualquier conjunto de armadura de cuero salvaje. La tela del jubón se amalgama con la atriplex salvaje para crear un producto que ofrece la máxima protección para el torso a la vez que permite que su potente magia se abra paso a través de ella.$B$BPara este patrón, necesitaré dos petos de caparazón de tortuga, dos pares de guantes de caparazón de tortuga y una atriplex salvaje. Son bastantes cosas, pero no debería resultarte difícil proporcionármelas.",
    ["O"] = "Lleva 2 corazas de caparazón de tortuga, 2 guantes de caparazón de tortuga y 1 atriplex salvaje a Pratt McGrubben al Bastión Plumaluna.",
    ["T"] = "Jubón de cuero salvaje",
  },
  [2850] = {
    ["D"] = "Un casco de cuero salvaje ofrece a quien lo lleva una gran protección de las áreas vitales de la cabeza sin sacrificar el confort. Como en todas las partes de las armaduras de cuero salvaje, cuando se integra la atriplex salvaje al casco, esta le imbuye una potente mejora aleatoria. ¡Quienes aprecian el cuero, sin duda desearán tener esta pieza!$B$BPara este patrón necesitaré 2 guerreras Nochefugaz, 2 cintas de pelo Nochefugaz y una atriplex salvaje. ¡Si me traes eso, el patrón será tuyo!",
    ["O"] = "Lleva 2 guerreras Nochefugaz, 2 cintas de pelo Nochefugaz y 1 atriplex salvaje a Pratt McGrubben al Bastión Plumaluna.",
    ["T"] = "Casco de cuero salvaje",
  },
  [2851] = {
    ["D"] = "Has aprendido bastante hasta ahora, $N; pero aún tienes que aprender más. Lo siguiente serán las botas de cuero salvaje, la piedra angular de cualquier conjunto de armadura.$B$BEstas botas se crean para envolver al pie en confort a la vez que lo refuerzan para protegerlo de los peligros de una vida aventurera. Sus suelas blandas son una bendición para quienes necesitan moverse con sigilo.$B$BPara este patrón necesito 2 pantalones Nochefugaz y 2 botas Nochefugaz. Y también necesito 2 atriplex salvajes, no me basta con una. Cuando tengas todo esto, házmelo saber.",
    ["O"] = "Lleva 2 pantalones Nochefugaz, 2 botas Nochefugaz y 2 atriplex salvajes a Pratt McGrubben al Bastión Plumaluna.",
    ["T"] = "Botas de cuero salvaje",
  },
  [2852] = {
    ["D"] = "Una vez asentadas las bases, ya puedes aprender algo más complejo, $N. Los leotardos de cuero salvaje son uno de los artículos de cuero de mayor solidez y potencia. Ofrecen una increíble protección a la cintura, las pantorrillas y a otros órganos vitales. Si los combinas con la mejora que proporciona la atriplex salvaje, los aventureros que los vistan te lo agradecerán cada vez que te vean.$B$BPara este patrón necesito 2 cascos de caparazón de tortuga, 2 brazales de caparazón de tortuga y 2 plantas de atriplex salvaje.",
    ["O"] = "Lleva 2 yelmos de caparazón de tortuga, 2 brazales de caparazón de tortuga y 2 atriplex salvajes a Pratt McGrubben al Bastión Plumaluna.",
    ["T"] = "Leotardos de cuero salvaje",
  },
  [2853] = {
    ["D"] = "Vaya $N, eres la diligencia personificada. Dominas casi todos los patrones de la armadura de cuero salvaje. Solo te falta uno. Y no te haré trabajar para conseguirlo. Porque ya te lo has ganado, pero... no puedo enseñártelo yo. Mi profesor, el que me inició en la peletería, es quien debe recompensarte.$B$BLleva esta carta a Telonis; enseña peletería en Darnassus. Muéstrale la carta y te tratará como a $Gun:una; igual, como hago yo.",
    ["O"] = "Entrega la carta de Pratt a Telonis, el maestro curtidor de Darnassus.",
    ["T"] = "Maestro de cuero salvaje",
  },
  [2854] = {
    ["D"] = "$N, tu habilidad como $Gpeletero:peletera; te precede. Yo poseo un conocimiento de la peletería que harías bien en aprender... Por ejemplo, ¡el de la armadura de cuero salvaje! El cuero salvaje aprovecha las potentes y caóticas propiedades de la atriplex salvaje que se encuentra en áreas de exuberante vegetación y en algunas criaturas de la región. Las armaduras que se forjan con ella son las mejores para $Gun artesano:una artesana; de tu habilidad, te lo garantizo.$B$BTe enseñaré a crearla, pero tendrás que trabajar para mí durante un tiempo. Para empezar, tendrás que traerme 10 piezas de cuero pesado. Ya detallaremos algo más después.",
    ["O"] = "Lleva 10 cueros gruesos a Jangdor Zanco Veloz al Campamento Mojache.",
    ["T"] = "Armadura de cuero salvaje",
  },
  [2855] = {
    ["D"] = "Los sobrehombros de cuero salvaje ofrecen una gran protección a la parte superior de los brazos y a los hombros de quien las lleva. Como ocurre con la armadura de cuero salvaje, cuando se cose la atriplex salvaje a las hombreras, esta les imbuye una mejora potente aleatoria.$B$BPara este patrón necesitaré seis refuerzos para armadura gruesa y una atriplex salvaje. No deberías tener problemas para conseguirlos.",
    ["O"] = "Lleva 6 refuerzos para armadura gruesos y una atriplex salvaje a Jangdor Zanco Veloz al Campamento Mojache.",
    ["T"] = "Sobrehombros de cuero salvaje",
  },
  [2856] = {
    ["D"] = "Los jubones de cuero salvaje son la pieza principal de cualquier conjunto de armadura de cuero salvaje. La tela del jubón se amalgama con la atriplex salvaje para crear un producto que ofrece la máxima protección para el torso a la vez que permite que su potente magia se abra paso a través de ella.$B$BPara este patrón, necesitaré 2 petos de caparazón de tortuga, 2 pares de guantes de caparazón de tortuga y 1 atriplex salvaje. No es una lección fácil, pero tú tienes sin duda la habilidad necesaria para aprenderla.",
    ["O"] = "Lleva 2 corazas de caparazón de tortuga, 2 guantes de caparazón de tortuga y una atriplex salvaje a Jangdor Zanco Veloz al Campamento Mojache.",
    ["T"] = "Jubón de cuero salvaje",
  },
  [2857] = {
    ["D"] = "Un casco de cuero salvaje ofrece a quien lo lleva una gran protección de las áreas vitales de la cabeza sin sacrificar el confort. Como en todas las partes de las armaduras de cuero salvaje, la atriplex salvaje integrada en el casco, le imbuye una potente mejora mágica aleatoria. ¡Quienes aprecian el cuero, sin duda desearán tener esta pieza!$B$BPara este patrón necesitaré dos guerreras Nochefugaz, 2 cintas de pelo Nochefugaz y una atriplex salvaje. ¡Si me traes eso, el patrón será tuyo!",
    ["O"] = "Lleva 2 guerreras Nochefugaz, 2 cintas de pelo Nochefugaz y una atriplex salvaje a Jangdor Zanco Veloz al Campamento Mojache.",
    ["T"] = "Casco de cuero salvaje",
  },
  [2858] = {
    ["D"] = "Tu conocimiento de la armadura de cuero salvaje crece, $N; pero aún te quedan cosas que aprender.$B$BLas botas de cuero salvaje son la piedra angular de cualquier conjunto de armadura. Estas botas se crean para envolver al pie en confort a la vez que lo refuerzan para protegerlo de los peligros de una vida aventurera. Sus suelas blandas son una bendición para quienes necesitan moverse con sigilo.$B$BPara este patrón necesito 2 pantalones Nochefugaz y 2 botas Nochefugaz. Y también necesito 2 atriplex salvajes, no me basta con una. Cuando tengas estos objetos, procederemos.",
    ["O"] = "Lleva 2 pantalones Nochefugaz, 2 botas Nochefugaz y 2 atriplex salvajes a Jangdor Zanco Veloz al Campamento Mojache.",
    ["T"] = "Botas de cuero salvaje",
  },
  [2859] = {
    ["D"] = "Ya estás $Glisto:lista; para lecciones más complejas, $N. Los leotardos de cuero salvaje son uno de los artículos de cuero de mayor solidez y potencia. Ofrecen una increíble protección a la cintura, las pantorrillas y a otros órganos vitales. Si los combinas con la mejora que proporciona la atriplex salvaje, los aventureros que los vistan te lo agradecerán cada vez que te vean.$B$BPara que te enseñe este patrón, tienes que traerme 2 cascos de caparazón de tortuga, 2 brazales de caparazón de tortuga y 2 plantas de atriplex salvaje.",
    ["O"] = "Lleva 2 yelmos de caparazón de tortuga, 2 brazales de caparazón de tortuga y 2 atriplex salvajes a Jangdor Zanco Veloz al Campamento Mojache.",
    ["T"] = "Leotardos de cuero salvaje",
  },
  [2860] = {
    ["D"] = "$N, ya has aprendido todo lo que yo puedo enseñarte. Pero te queda por aprender un último patrón de la armadura de cuero salvaje. No te pediré que trabajes más, pues ya te has ganado el derecho de aprender este patrón. Pero no es un patrón que sea libre de compartir contigo.$B$BMi profesor, el que me inició en la peletería, es quien debe enseñártelo. Lleva esta nota a Una. Enseña peletería en Cima del Trueno. Muéstrale la carta y te tratará como a $Gun:una; igual, como hago yo.",
    ["O"] = "Entrega la carta de Jangdor a Una, el maestro curtidor de Cima del Trueno.",
    ["T"] = "Maestro de cuero salvaje",
  },
  [2861] = {
    ["D"] = "Se rumorea que Tabetha, una compañera maga que vive en las profundidades del Marjal Revolcafango, tiene que arreglar cuentas con un trol de arena de Zul\'Farrak. Si hablas con Tabetha te dirá más.$B$BY lleva contigo algunos amigos, $N. Si Tabetha quiere que vayas a Zul\'Farrak no querrás ir $Gsolo:sola;.$B$BEncontrarás la cabaña de Tabetha al oeste de Theramore, al norte de las Ruinas Quebrantarrocas.",
    ["O"] = "Habla con Tabetha en el Marjal Revolcafango.",
    ["T"] = "La tarea de Tabetha",
  },
  [2862] = {
    ["D"] = "Los gnolls... son algo más que una molestia. ¡Son una amenaza constante para este campamento! Que sean o no de Feralas es irrelevante. Si no redoblamos nuestros esfuerzos por controlarlos, acabarán por expulsarnos de la región entera.$B$BLos ataques de los gnolls al campamento se han intensificado últimamente. Necesitamos que nos ayudes a reducir su número. Tráeme 10 de sus cabelleras. Recompensaré generosamente tu esfuerzo.$B$B¡Ve!",
    ["O"] = "Lleva 10 cabelleras de gnolls Zarpaleña a Hadoken Zanco Veloz al Campamento Mojache.",
    ["T"] = "En guerra con los Zarpaleña",
  },
  [2863] = {
    ["D"] = "El plan es para varios líderes de los gnolls, esos que se hacen llamar Alfas. Para acabar con ellos en rápida sucesión. Si logramos ejecutarlo con la rapidez y precisión necesarias, haríamos que cundiera el caos entre sus filas. Y quizás conseguiríamos que en esas estúpidas cabezas piojosas entrara el mensaje de que podemos destruirlos ¡y lo haremos!$B$BQuiero que seas tú $Gel mensajero:la mensajera; de mi voluntad, $N. Elimina a 5 de sus Alfas en una hora. ¡Si tardas más, el plan no funcionará y empeorarás nuestra situación!",
    ["O"] = "Mata a 5 Alfas Zarpaleña y vuelve con Hadoken Zanco Veloz en 1 hora.",
    ["T"] = "Ataca a los Alfas",
  },
  [2864] = {
    ["D"] = "Mi primo Tran\'rek está en un aprieto. Me prometió un cargamento de caparazones de escarabajo hace semanas y todavía no lo ha traído. Es mi primo, así que odio tener que enviar a alguien a Gadgetzan para que le eche la bronca.$B$B¿Puedes hablar con Tran\'rek por mí? Hazle saber que tiene que conseguir ese cargamento antes de que haga algo malo.$B$BGadgetzan está en el norte de Tanaris, en Kalimdor.",
    ["O"] = "Habla con Tran\'rek en Gadgetzan.",
    ["T"] = "Tran\'rek",
  },
  [2865] = {
    ["D"] = "¡Los escarabajos de Tanaris tienen unos caparazones muy duros! Tan duros que se pueden utilizar para construir muchas cosas. ¡Muchísimas!$B$BDe hecho, esos caparazones son tan útiles y cazamos tantos escarabajos... ¡que casi se han extinguido!$B$BSé dónde hay más escarabajos, y si me prometes traerme sus caparazones te diré dónde están. ¿Prometido?$B$BMuy bien, hay una colonia de escarabajos en Zul\'Farrak. Supongo que los trols no los cazan en busca de caparazones.$B$B¡Pero tú sí! ¡Ve a Zul\'Farrak y consígueme caparazones sin rajar!",
    ["O"] = "Lleva 5 caparazones de escarabajo sin rajar a Tran\'rek en Gadgetzan.",
    ["T"] = "Caparazones de escarabajo",
  },
  [2866] = {
    ["D"] = "Al sur de aquí están las Ruinas de Solarsal, una zona que ya era antigua antes de nuestro tiempo. Exploramos las ruinas en su momento y no encontramos nada relevante, así que no volvimos a enturbiar la paz del lugar.$B$BHace poco, los nagas Crestafuria han ocupado las ruinas en número alarmante. Al principio, lo interpretamos como el anuncio de un ataque. Aún consideramos esa posibilidad, pero además, creemos que podría estar ocurriendo alguna otra cosa.$B$BVe a explorar las ruinas y busca cualquier cosa que te llame la atención. Buena suerte, $N.",
    ["O"] = "Explora las Ruinas de Solarsal e investiga la presencia de los nagas allí.",
    ["T"] = "Las Ruinas de Solarsal",
  },
  [2867] = {
    ["D"] = "Tras descubrir la extraña glorieta en el corazón de las Ruinas de Solarsal, sabes que ha llegado el momento de regresar al Bastión Plumaluna e informar a la general Shandris Plumaluna del resultado de tu exploración.",
    ["O"] = "Vuelve al Bastión Plumaluna e informa a Shandris Plumaluna del resultado de tu exploración.",
    ["T"] = "Regresa al Bastión Plumaluna",
  },
  [2868] = "_",
  [2869] = {
    ["D"] = "En este momento, lo mejor que podemos hacer es asumir que, independientemente de lo que estén haciendo los nagas, antes o después se moverán para asegurar la isla para los suyos. Necesitamos tomar medidas agresivas para combatir esa amenaza antes de que sea demasiado tarde.$B$BTráeme 10 escamas de Crestafuria que estén en buenas condiciones. No hará falta decirte cómo conseguirlas, supongo. Tú adquirirás experiencia con los nagas y nosotros conseguiremos reducir su presencia en Isla de Sardor. Ambas cosas serán beneficiosas para nosotros.",
    ["O"] = "Consigue 10 escamas de naga Crestafuria para Latronicus Lanzaluna en el Bastión Plumaluna.",
    ["T"] = "En contra de los Crestafuria",
  },
  [2870] = {
    ["D"] = "$N, nos han llegado informes de los exploradores que nos han dado una buena idea de cómo es la cadena de mando de los nagas. Entre sus filas hay Regios, que son los que mandan.$B$BEn concreto, hemos identificado a uno de los regios más crueles: Lord Shalzaru. Según mis exploradores, él en persona ha desenterrado una extraña reliquia en una excavación naga de una cueva del sur de las ruinas principales, en la Isla del Terror.$B$BAcaba con él y tráeme esa reliquia para que podamos estudiarla.",
    ["O"] = "Elimina a Lord Shalzaru y luego lleva la reliquia misteriosa que desenterró a Latronicus Lanzaluna al Bastión Plumaluna.",
    ["T"] = "En contra de Lord Shalzaru",
  },
  [2871] = {
    ["D"] = "Si fueras tan amable de entregar la reliquia a mi esposa Vestia, te lo agradecería. No es solo una de las mejores sacerdotisas de nuestra comunidad, además es una de las estudiosas más eruditas que conocerás en tu vida. Yo no podría ni dibujar este instrumento, ¡ni aunque mi vida dependiera de ello! Pero mi mujer... Si alguien puede averiguar qué es esta cosa, esa es ella. ¡Y necesitamos saber qué es!$B$BEstá al otro lado de la curva. Estará estudiando, seguro. Gracias, $N.",
    ["O"] = "Entrega la reliquia misteriosa a Vestia Lanzaluna que está en el Bastión Plumaluna.",
    ["T"] = "Entrega la reliquia",
  },
  [2872] = {
    ["D"] = "Mi viejo amigo Stoley me debe una botella muy especial de ron. Él atiende el bar del Puerto Bonvapor en Tanaris, Kalimdor, y me aseguró que el ron allí es más fuerte que cualquier cosa que podamos encontrar en Bahía del Botín. Está tan seguro de lo que dice que prometió darme el doble de dinero del que me debe si después de probarlo no opino lo mismo.$B$BBueno, yo creo que ahora ya no está tan seguro como en su día y no quiere que pruebe el ron. Sin embargo, yo nunca me echo atrás ante una apuesta, así que por qué no vas a hablar con él...",
    ["O"] = "Habla con Stoley en el Puerto Bonvapor.",
    ["T"] = "La deuda de Stoley",
  },
  [2873] = {
    ["D"] = "Le debo algo de ron a \"Lobo de Mar\" MacKinley de Bahía del Botín por una apuesta, ¡pero no me queda ni gota! El capitán Cuergo robó mi último cargamento y lo escondió en esa ciudad pirata amurallada situada al sur.$B$BSi encuentras mi cargamento de ron y me lo devuelves, quizás pueda demostrarle a MacKinley que el ron de Bahía del Botín no le llega a este ni a la suela del zapato.",
    ["O"] = "Llévale el envío de Stoley a Stoley, que está en el Puerto Bonvapor.",
    ["T"] = "Envío de Stoley",
  },
  [2874] = {
    ["D"] = "Gracias de nuevo por tu ayuda, $N. Aquí tienes una botella de ron de la sentina Bonvapor. Llévasela a MacKinley para que pueda comprobar por fin lo que se dice del ron de Bahía del Botín.",
    ["O"] = "Llévale la botella de Stoley a \"Lobo de Mar\" MacKinley, en Bahía del Botín.",
    ["T"] = "Un envío para MacKinley",
  },
  [2875] = {
    ["D"] = "Se busca al malvado pirata Andre Barbafuego por crímenes contra Bonvapor y Gadgetzan.$B$BPor decreto del consejo de gobierno, todo el que lea esto tiene autorización expresa para utilizar fuerza letal durante la ejecución del arresto de Andre y sus cómplices.$B$BPor nuevo decreto, de producirse el fallecimiento de Andre o de sufrir este algún tipo de perjuicio durante la sesión de interrogatorio posterior a su detención, su cabeza actuará como prueba irrefutable de este hecho y su entrega ante el jefe de seguridad Pasaquillas constituye contrato vinculante.",
    ["O"] = "Llévale la cabeza de Andre al jefe de seguridad Pasaquillas, que está en el Puerto Bonvapor.",
    ["T"] = "SE BUSCA: Andre Barbafuego",
  },
  [2877] = {
    ["D"] = "Los Martillo Salvaje recordamos el pasado mejor que nuestros hermanos de Forjaz. ¡Si quieres hacer amigos aquí, tendrás que ensuciarte las manos!$B$BLa Horda ha abandonado sus puestos de Roca Oculta en las Tierras del Interior; ahora todo está infestado de babosas y mocos. Tenemos planes para la región, pero antes hay que enviar una brigada de limpieza.$B$B¡Ese es tu trabajo, hermosura! Ve allí y elimina 10 lodos verdes y 10 mocos de jade. ¡Andando!",
    ["O"] = "Mata 10 lodos verdes y 10 mocos de jade e informa a Fraggar Mantrueno en Pico Nidal.",
    ["T"] = "La limpieza de Roca Oculta",
  },
  [2879] = {
    ["D"] = "¡Esta madera curva podría ser el báculo de Equinex! El báculo de Equinex es, en realidad, una llave que abre el Monolito de Equinex de las Ruinas de Viento Azabache, las que están en la península al oeste de Rama Oniria.$B$BEncuentra las cuatro llamas que aún flamean en esas ruinas: Samha, Imbel, Byltan y Lahassa. Recoge sus esencias y luego, cuando estés en el Monolito de Equinex, utilízalas para imbuir su energía a la madera curva. Si este es realmente el báculo de Equinex, podrás abrir el Monolito y sacar un artefacto sagrado de él.",
    ["O"] = "Energiza el báculo de Troyas y encuentra el Monolito de Equinex.",
    ["T"] = "El báculo de Equinex",
  },
  [2880] = {
    ["D"] = "Que quede claro, $R: ¡NO formamos parte de la Alianza! Los Martillo Salvaje somos independientes, creemos que el valor se tiene que probar con actos y no con bonitos discursos.$B$B¡Si quieres que te aceptemos, tendrás que probar tu valía! Esos inmundos trols que ensucian las Tierras del Interior suelen llevar collares tribales. Tráeme cinco y lo consideraremos un acto de buena voluntad hacia nosotros.",
    ["O"] = "Lleva 5 collares tribales trol a Fraggar Mantrueno a Pico Nidal.",
    ["T"] = "Los collares de trols",
  },
  [2882] = {
    ["D"] = "Este mapa muestra un trecho de playa orientado hacia el sur. Debajo del mapa hay unas palabras escritas de manera elegante...$B$BMás al sur de Bonvapor$BAl sur del lar de tu corazón$BEncontrarás huesos, mástil y bandera$BExcava allí si tienes alma aventurera$BExcava y el oro de Cuergo reclama$BUna llave necesitarás y una llave encontrarás$BLos hombres a los que te enfrentarás$Ben sangre pagarás.",
    ["O"] = "¡Encuentra el oro de Cuergo!",
    ["T"] = "Oro de Cuergo",
  },
  [2903] = {
    ["D"] = "Ahora que has descubierto los planes de batalla de los gnolls, solo te queda llevárselos a Hadoken Zanco Veloz al Campamento Mojache. Quizás él o alguien de allí pueda averiguar qué están tramando los gnolls.",
    ["O"] = "Lleva los planes de batalla de los gnolls Zarpaleña a Hadoken Zanco Veloz al Campamento Mojache.",
    ["T"] = "Los planes de batalla",
  },
  [2904] = {
    ["D"] = "<Kernobee gruñe.> ¿Qué...? ¿Qué ha pasado?$B$BSolo recuerdo que me capturó un grupo de enanos Hierro Negro, me dieron una paliza de muerte y me echaron aquí, con estos asquerosos troggs.$B$B¡Sácame de aquí, $N!$B$BEste es el plan: llévame a la salida; si sobrevivimos, tú te vas a la Bahía del Botín y le dices a Scooty que he ido a Trinquete para avisar a Manitas de Oro que los Hierro Negro están metidos en lo de la superplataforma.$B$B¡Andando!",
    ["O"] = "Acompaña a Kernobee a La Rampa del Engranaje y luego ve a ver a Scooty a Bahía del Botín.",
    ["T"] = "Un buen lío",
  },
  [2922] = {
    ["D"] = "Supongo que mi pobre Tecnobot recibió algún chispazo perdido cuando cayó nuestra amada Gnomeregan. Antes nos era muy útil gracias a sus incontables funciones y su capacidad ilimitada de almacenaje de información, pero su cerebro positrónico se ha polarizado. Ahora es negatrónico y la conducta del Tecnobot se ha invertido. ¡Anda descontrolado por Gnomeregan!$B$BPero quizás podamos recuperarlo. Si me trajeras el procesador central de memoria del Tecnobot, podría descubrir la causa de la polarización y repararlo.$B$B",
    ["O"] = "Lleva el procesador central de memoria del Tecnobot al maestro manitas Sobrechispa a Forjaz.",
    ["T"] = "¡Salva el cerebro de Tecnobot!",
  },
  [2923] = {
    ["D"] = "El maestro manitas Sobrechispa está muy disgustado con lo ocurrido a su obra mecánica, el Tecnobot. Por lo que sé, el Tecnobot era una herramienta muy útil para los gnomos, pero dice que se ha averiado.$B$BLástima. ¡Aunque con suerte se podrá arreglar!$B$BVe a hablar con Sobrechispa a Ciudad Manitas, en Forjaz. Se alegrará de que vayas a ayudarlo.",
    ["O"] = "Habla con el maestro manitas Sobrechispa en Forjaz.",
    ["T"] = "Maestro manitas Sobrechispa",
  },
  [2924] = {
    ["D"] = "Los herreros y manitas de Gnomeregan usaban un componente mecánico casi universal, la esencia artificial, en su trabajo. Pero durante la huida de la ciudad, ¡nadie pensó en coger esencia! Necesitaré mucha para un trabajo que estoy a punto de empezar.$B$BTe ruego que vayas a Gnomeregan a conseguirme esencia artificial. Está en las zonas más profundas de la ciudad, en unos contenedores que se llaman extrapoladores artificiales.$B$B¡Date prisa y muchas gracias, $N!",
    ["O"] = "Lleva 12 esencias artificiales a Klockmort Palmalicate a Forjaz.",
    ["T"] = "Esencias artificiales",
  },
  [2926] = {
    ["D"] = "Desde el accidente, Gnoarn no piensa en otra cosa que en expulsar a los troggs de Gnomeregan y encontrar una cura para la plaga. $N.$B$BPor suerte, Razzle y yo estamos a punto de conseguirlo; o eso, o hemos destilado un ron muy potente. ¡No lo sabremos hasta que lo probemos! Claro que para poner a prueba nuestra teoría, necesitamos algunas cosas, como restos de los troggs radiactivos en Gnomeregan. ¡Llena este frasco de material verdoso radiactivo y tráelo!",
    ["O"] = "Usa la ampolla de plomo con invasores o saqueadores radiactivos para recoger restos radiactivos. Cuando esté llena, llévasela a Ozzie Voltiflop a Kharanos.",
    ["T"] = "Gnogaine",
  },
  [2927] = {
    ["D"] = "¿Crees que siempre he sido calvo? ¡Pues no $Gseñor:señorita;! Perdí todo el pelo por culpa de la fuga radiactiva de Gnomeregan. Hasta entonces, tenía una mata de pelognomo única en todo Azeroth.$B$B<Sacude los puños.>$B$B¡Así se pudran esos malditos troggs en las simas del averno!$B$BA lo mejor no es tarde, $N. Aún podemos salvarla. ¡A la ciudad, me refiero! Ve a hablar con Ozzie Voltiflop en Kharanos. Está buscando una solución con su colega Razzle.",
    ["O"] = "Ve a hablar con Ozzie Voltiflop en Kharanos, en Dun Morogh.",
    ["T"] = "Al día siguiente",
  },
  [2928] = {
    ["D"] = "Es terrible, $N. No vamos a tener suficientes repuestos para llevar nuestra flota de excavadoras gyroagujereamáticas a Dun Morogh.$B$BLo que necesitamos son repuestos, $N. ¿Y qué mejor lugar que Gnomeregan para conseguir repuestos para nuestros vehículos? He oído que robots y mecazancudos vagan libremente por ese lugar.$B$BNecesito que vayas a Gnomeregan y destruyas a esos robots. Tráeme sus entrañas robot. Con dos docenas tendré suficiente.|n",
    ["O"] = "Lleva veinticuatro entrañas robomecánicas a Shoni en Ventormenta.",
    ["T"] = "Excavadoras gyroagujereamáticas",
  },
  [2929] = {
    ["D"] = "Yo confiaba en Termochufe, $R. Nunca pensé que me traicionaría a mí y a todos los gnomos. ¿Y por qué? ¿Por el poder? ¿Por el dinero? Habría conseguido todo eso por sí mismo. Ahora nos han echado de nuestro hogar y ese chiflado es el que manda. Rey de pacotilla...$B$BVamos a recuperar Gnomeregan, $N. No cejaremos hasta que la ciudad vuelva a nuestras manos. Si quieres ayudarnos, tengo un trabajito para ti. Mata al traidor. Acaba con el mekigeniero Termochufe.",
    ["O"] = "Ve a Gnomeregan y mata al mekigeniero Termochufe. Ve a ver al Manitas Mayor Mekkatorque cuando hayas terminado.",
    ["T"] = "La gran traición",
  },
  [2931] = {
    ["D"] = "¡El maestro mecánico Funditubo de Forjaz busca gente aventurera! Quiere volver a Gnomeregan para recuperar datos; es una misión muy importante.$B$BAunque no sea miembro de la Asamblea Iluminada de Arcanología, alquimia e Ingeniería como yo, es un gnomo inteligente, así que si piensa que algo es importante, según mis cálculos hay un 48 por ciento de posibilidades de que, en efecto, sea importante.$B$B¡Es una probabilidad muy alta!",
    ["O"] = "Habla con el maestro mecánico Funditubo en Forjaz.",
    ["T"] = "Trabajar para Funditubo",
  },
  [2932] = {
    ["D"] = "Los trols Secacorteza fueron aliados de la Horda, ¡pero traicionaron a Thrall y deben ser castigados!$B$BToma mi pica y vete a las Tierras del Interior. Están al noreste de Molino Tarren, que está bajo control de nuestros aliados, los Renegados.$B$BCuando llegues, busca trols Secacorteza. Recoge sus cráneos y clava mi pica en el suelo en uno de sus poblados más pequeños, Hiri\'watha o Zun\'watha.$B$B¡Que aprendan que sus ruines acciones tienen consecuencias!",
    ["O"] = "Recoge cráneos Secacorteza y luego clava la pica cargada de Nimboya en uno de los poblados Secacorteza, en las Tierras del Interior. Luego ve a ver a Nimboya a Tuercespina.",
    ["T"] = "Un mensaje siniestro",
  },
  [2933] = {
    ["D"] = "En estas botellas hay un veneno verde e infame.$B$BVista la cantidad de botellas que hay en el poblado, los Secacorteza deben de considerarlo muy importante. A lo mejor algún boticario de Molino Tarren estaría interesado en esto.",
    ["O"] = "Lleva una botella de veneno a algún boticario de Molino Tarren.",
    ["T"] = "Botellas de veneno",
  },
  [2934] = {
    ["D"] = "Necesito más veneno como el que trajiste. Creo que las arañas de las que se extrae viven en las Tierras del Interior; encuentra una con la glándula de veneno intacta y tráemela. Habrá muchas cerca de la ciudad trol de Shadra\'Alor.$B$BNo te entretengas, $N. La glándula tiene que ser fresca.",
    ["O"] = "Lleva una glándula de veneno intacta al boticario Lydon de Molino Tarren.",
    ["T"] = "Glándula de veneno intacta",
  },
  [2935] = {
    ["D"] = "El veneno de las guardias del linaje no era tan potente como el que me trajiste primero. Debía de ser de otra araña.$B$BQuiero que me traigas más de ese veneno. Para averiguar de dónde viene, ve al Poblado Sen\'jin y habla con el maestro Gadrin. Es un trol que conoce bien a los Secacorteza. Ve a Durotar y habla con Gadrin. Con un poco de suerte, sabrá de dónde sacan el veneno los Secacorteza.",
    ["O"] = "Habla con el maestro Gadrin en el Poblado Sen\'jin.",
    ["T"] = "Consulta al maestro Gadrin",
  },
  [2936] = {
    ["D"] = "Los Secacorteza adoran a una diosa araña, de la cual extraen su veneno más potente. Así que, si quieres el veneno, ¡tienes que invocar a la diosa!$B$BPara ello debes conocer su verdadero nombre, inscrito en una tablilla enterrada en Tanaris, en la ciudad trol de Zul\'Farrak, en una tumba custodiada por el Mártir Theka.$B$BDerrótalo y lee el nombre en la tablilla. Después, vuelve.",
    ["O"] = "Lee el nombre de la diosa araña de los Secacorteza en la tablilla de Theka y vuelve a ver al maestro Gadrin.",
    ["T"] = "Diosa araña",
  },
  [2937] = {
    ["D"] = "Lee este papiro en el altar de Shadra\'Alor, que está escondido en el reino de los Secacorteza, en las Tierras del Interior. Así invocarás a Shadra, la diosa araña.",
    ["O"] = "Derrota a Shadra en el altar de Shadra\'Alor y lleva su veneno al boticario Lydon de Molino Tarren.",
    ["T"] = "Invocar a Shadra",
  },
  [2938] = {
    ["D"] = "¡El veneno de la diosa araña de los Secacorteza es extraordinario! Ha bastado una gota para matar a dos docenas de conejos. Es cierto que muchos eran muy pequeños y por tanto poco resistentes a las toxinas, ¡pero no puede negarse que este veneno es de lo más potente!$B$BMe gustaría enviar una muestra a mi superior, el boticario Faranell, del Apothecarium de Entrañas. Seguro que querrá hacer pruebas.",
    ["O"] = "Lleva el paquete al maestro boticario Faranell a Entrañas.",
    ["T"] = "Veneno para Entrañas",
  },
  [2939] = {
    ["D"] = "Las ruinas de Feralas pueden ser muy peligrosas, pero tienen mucho que decir acerca de lo que aquí pasó.$B$BEntre los escombros, hace unos días, descubrí lo que parecía ser un báculo normal y corriente. Sin embargo, creo que hay algo en él. Angelas y yo hemos estado leyendo buscando algo al respecto, pero no aparece nada.$B$BTengo una amiga en Darnassus que nos podría decir lo que es, $N. ¿Por qué no vas a verla y le preguntas si nos ayuda?",
    ["O"] = "Habla con Daryn Brisela de la Aldea Rut\'theran.",
    ["T"] = "La búsqueda del conocimiento",
  },
  [2942] = {
    ["D"] = "En el momento en el que extraes el artefacto del Monolito, sientes que la energía del báculo de Equinex empieza a decaer.",
    ["O"] = "Devuelve la piedra refulgente y el báculo de Equinex a Troyas Brisaluna que está en el Bastión Plumaluna.",
    ["T"] = "La Piedra del mañana",
  },
  [2943] = {
    ["D"] = "Aquí lo tienes, $N. Por favor, cuida de mi libro.$B$BY, ahora, ve. Seguro que Troyas está deseando que regreses.",
    ["O"] = "Entrega el libro a Troyas Brisaluna en el Bastión Plumaluna.",
    ["T"] = "Regresa junto a Troyas",
  },
  [2944] = {
    ["D"] = "Echa un vistazo a mi nuevo invento, $N. Lo único que hay que hacer es apuntar a lo que quieras retratar y pulsar el botón.$B$B¿Cuál era esa criatura de las Tierras del Interior que mencionaste? ¿Gammerita? Pues espero que la encuentres. Buena suerte. Estoy segura de que Daryn quedará satisfecha con la instantánea que le llevas.$B$BEn cualquier caso, ¡aquí tienes la Superinstantánea! ¡Diviértete!",
    ["O"] = "Utiliza la Superinstantánea FX para sacar una instantánea de Gammerita y luego vuelve a la Aldea Rut\'theran con Daryn Brisela.",
    ["T"] = "La Superinstantánea FX",
  },
  [2945] = {
    ["D"] = "Has encontrado algo que parece un anillo en el cadáver de un enano Hierro Negro. Está cubierto de barro y restos secos y endurecidos, por lo que no se puede llevar ni se puede ver qué tipo de anillo es. Entre el barro, te parece entrever el brillo de metal precioso y quizás una gema.$B$BSi quieres ponértelo, tendrás que encontrar la manera de limpiarlo.",
    ["O"] = "Encuentra la manera de limpiar el anillo sucio.",
    ["T"] = "Anillo con mugre incrustada",
  },
  [2946] = {
    ["D"] = "Queremos que lleves los discos en miniatura a las puertas selladas de Uldum. No estamos seguros de qué pasará, la verdad.$B$BSabemos que la operación entraña un riesgo. Aunque puede que no ocurra nada. Puede que tengas que usar los discos de algún modo. Es posible que las puertas se desbloqueen porque lleves los discos. Mantén una actitud abierta y prepárate para cualquier cosa.$B$BCuando averigües lo que pasa con los discos, vuelve a Forjaz. ¡Tendremos mucho que analizar!",
    ["O"] = "Lleva los discos de platino en miniatura a las puertas de Uldum, en Tanaris. Busca pistas sobre la conexión entre discos y puertas.",
    ["T"] = "A ver qué pasa",
  },
  [2948] = {
    ["D"] = "Oye, $N, ¡puedo hacerte un anillo mejor que el que llevas! Como decía, ese diseño es muy sencillo. Ahora soy mucho mejor, así que por 30 monedas de plata de nada mejoraré tu anillo. Bueno, en realidad, es algo más de 30...$B$BAhora mismo estoy sin material. ¡Si me traes una barra de plata y una ágata musgosa, las usaré para añadir detalle a tu anillo y darle otro aire!$B$BConfía en mí.",
    ["O"] = "Lleva tu anillo de oro luminoso, una barra de plata, una ágata musgosa y 30 monedas de plata a Talvash del Kissel a Forjaz.",
    ["T"] = "Mejora gnómica",
  },
  [2950] = {
    ["D"] = "No, no es que de pronto haya decidido que el anillo es mío. Normalmente no hago estas cosas, pero tu información sobre los Hierro Negro me ha sido muy útil. Mejoraré tu anillo de una forma que nunca se le habría ocurrido a su creador. Seguro que era un gnomo...$B$B¡Si me traes una barra de plata y una ágata musgosa, además de 30 monedas de plata, añadiré detalle a tu anillo y reforzaré su magia! ¿Te interesa la cosa?",
    ["O"] = "Lleva tu anillo de oro luminoso, una barra de plata, una ágata musgosa y 30 monedas de plata a Nogg a Orgrimmar.",
    ["T"] = "Nogg mejora el anillo",
  },
  [2951] = {
    ["D"] = "Este aparato destaca entre los gnomos que se mantienen firmes frente a la locura que acecha fuera de la sala. Tiene una plaquita que dice: \"Destellamatic 5200\" y:$B$B\"¿La suciedad arruina sus destellos? ¡El Destellamatic 5200 devuelve el brillo de forma brillante! Inserte el objeto que desea limpiar e introduzca tres monedas de plata en la ranura. Espere unos momentos, ¡y ya está! ¡Sus objetos de valor aparecerán limpios y resplandecientes!\"",
    ["O"] = "Inserta un objeto con mugre incrustada en el Destellamatic 5200 y asegúrate de tener 3 monedas de plata a mano.",
    ["T"] = "¡El Destellamatic 5200!",
  },
  [2954] = {
    ["D"] = "\"Saludos, soy un guardián de la entrada. A menos que ya tengas las Placas de Uldum integradas con los discos, no permitiré tu entrada en Uldum.\"$B$BLa aparición del vigía de piedra de Norgannon debe ser el objetivo de haberte enviado al desierto de Tanaris con los discos de platino. Para poder continuar, deberás escuchar lo que el vigía de piedra tenga que contarte.",
    ["O"] = "Averigua qué quiere el vigía de piedra de Norgannon y después interactúa con el pedestal de Uldum en el Desierto de Tanaris.",
    ["T"] = "El vigía de piedra",
  },
  [2962] = {
    ["D"] = "Tu muestra no solo era insuficiente en cuanto a cantidad, además era impura.$B$BNecesitamos más material verdoso radiactivo. Es evidente que los troggs no han sido suficientemente irradiados. Tendrás que ir a Gnomeregan y traer restos radiactivos, directamente de la fuente. Estoy hablando de babosas, rondadores y horrores irradiados. Y ten cuidado: contener este tipo de restos es difícil. Es posible que tengas que hacer varios intentos hasta que hayas reunido una muestra adecuada.",
    ["O"] = "Viaja hasta Gnomeregan y recupera los restos radiactivos de gran potencia. Pero ten cuidado, ya que es inestable y podría explotar en cualquier momento.$B$BOzzie también quiere que traigas la ampolla pesada de plomo una vez que hayas terminado el trabajo.|n",
    ["T"] = "Necesitamos más material verdoso",
  },
  [2963] = {
    ["D"] = "¡Saludos de nuevo, $N! Lo que descubriste en Uldaman ha interesado muchísimo a la Liga de Expedicionarios. Están intentando descifrar los discos de platino en miniatura que encontraste. ¡Deja que te diga que probablemente sea algo importante!$B$BKarnik el Historiador ha descubierto pistas importantes sobre los verdaderos orígenes del Valle de los Vigías en el Desierto de Tanaris. Quiere hablar contigo lo antes posible.",
    ["O"] = "Habla con Karnik el Historiador en la Sala de los Expedicionarios de Forjaz.",
    ["T"] = "Presagios de Uldum",
  },
  [2964] = {
    ["D"] = "Bueno $N... parece que hemos llegado a un punto muerto. Sabemos que necesitamos las Placas de Uldum, pero no tenemos ni idea de dónde pueden estar. Tendremos que estudiar un poco la situación para poder elaborar un buen plan de ataque. Yo tengo una ligera idea de por dónde empezar a buscar. Albergo la esperanza de que pronto te pidamos que vayas a recuperarlas.$B$BHay que informar al Alto Expedicionario de esto, $N. Sin duda, él desea una vez más agradecerte personalmente que hayas prestado tu ayuda a la Liga.",
    ["O"] = "Habla con el alto expedicionario Magellas de Forjaz.",
    ["T"] = "Una tarea futura",
  },
  [2965] = {
    ["D"] = "Ah, $N, qué casualidad que hayas vuelto a mí. Tus descubrimiento en la excavación de Uldaman nos han dado mucho de qué hablar a los sabios. De hecho, estaba discutiendo con una druida llamada Nara Ferocrín cuando caí en la cuenta de que sus antiguos viajes podrían resultar importantes para el próximo paso que hemos de dar.$B$BMe gustaría que fueras a hablar con ella acerca de sus viajes al Valle de los Vigías en el Desierto de Tanaris. Ella te está esperando en el Alto de los Ancestros.",
    ["O"] = "Habla con Nara Ferocrín en el Alto de los Ancestros de Cima del Trueno.",
    ["T"] = "Presagios de Uldum",
  },
  [2966] = {
    ["D"] = "A Sabio Buscador de la Verdad y a mí nos gustaría que llevaras los discos en miniatura que encontraste a las puertas selladas de Uldum. No estamos seguros de qué pasará, la verdad.$B$BSabemos que la operación entraña un riesgo. Aunque puede que no ocurra nada. Puede que tengas que usar los discos de algún modo. Es posible que las puertas se abran si llevas los discos. Mantén una actitud abierta y prepárate para cualquier cosa.$B$BCuando averigües lo que pasa vuelve a verme. Tendremos mucho de qué hablar.",
    ["O"] = "Lleva los discos de platino en miniatura a las puertas de Uldum, en Tanaris. Busca pistas sobre la conexión entre discos y puertas.",
    ["T"] = "A ver qué pasa",
  },
  [2967] = {
    ["D"] = "Accedes de nuevo al pedestal y los discos de platino aparecen de repente en tu mochila.$B$BCon lo que te ha enseñado el vigía de piedra y los discos de platino de nuevo en tu poder, ha llegado el momento de regresar a Cima del Trueno e informar a Nara Ferocrín de tu hallazgo.",
    ["O"] = "Habla con Nara Ferocrín en Cima del Trueno.",
    ["T"] = "Regresa a Cima del Trueno",
  },
  [2968] = {
    ["D"] = "Bueno $N... parece que hemos llegado a un punto muerto. Sabemos que necesitamos las Placas de Uldum, pero no tenemos ni idea de dónde pueden estar. Tendremos que estudiar un poco la situación para poder elaborar un buen plan de ataque. Yo tengo una ligera idea de por dónde empezar a buscar. Albergo la esperanza de que pronto te pidamos que vayas a recuperarlas.$B$BHay que informar a Sabio Buscador de la Verdad de esto, $N. Sin duda, él desea una vez más agradecerte personalmente que le hayas prestado tu ayuda.",
    ["O"] = "Habla con Sabio Buscador de la Verdad en Cima del Trueno.",
    ["T"] = "Una tarea futura",
  },
  [2969] = {
    ["D"] = "Esos asquerosos del clan Tótem Siniestro están capturando dragones feéricos, $N Jer\'kai y yo no sabemos qué están planeando hacer con ellos, pero está claro que no es nada bueno.$B$BY no podemos entrar en el campamento a liberar a los dragones a brazo partido, pero quizás, con tu ayuda...$B$BSi quieres ayudar y tienes amigos fuertes, libera a las criaturas de sus jaulas con esta llave y asegúrate de que logran escapar. Yo estaré a tu lado todo el tiempo. Jer\'kai se quedará aquí.",
    ["O"] = "Utiliza la llave de jaula de bambú para liberar a los duendes dardo del campamento Tótem Siniestro. Deben sobrevivir al menos 6 duendes dardo y Kindal Tejeluna.$B$BTienes 6 minutos antes de que Kindal decida que es hora de retirarse, entonces tu misión fracasará.",
    ["T"] = "Libertad para todas las criaturas",
  },
  [2971] = "_",
  [2972] = {
    ["D"] = "Kindal y yo no tenemos aquí los medios para recompensar tu ayuda, pero no queremos que sientas que no la apreciamos. Llévate mi sello. Si alguna vez te encuentras en Teldrassil, ve al Templo de la Luna que está en el corazón de Darnassus. Allí encontrarás a la suma sacerdotisa Tyrande Susurravientos. El sello le hará saber que nosotras estamos bien y dará crédito a tu historia.$B$BAmbas te damos las gracias, $N.",
    ["O"] = "Lleva el sello de Jer\'kai a la suma sacerdotisa Tyrande Susurravientos a Darnassus.",
    ["T"] = "La justicia del castigo",
  },
  [2973] = {
    ["D"] = "Saludos, $C. He viajado con mis compañeros hasta Feralas en una misión de nuestros maestros de Orgrimmar.$B$BMi compañera Korrin adora la ropa nueva, así que voy a hacerle una nueva capa adornada con las ligeras alas de los dragones feéricos. ¿Podrías ayudarme? Los duendes dardo con las mejores alas están al oeste del Campamento Mojache.",
    ["O"] = "Lleva 10 alas de duende dardo iridiscente a Krueg Machacacráneos al Campamento Mojache.",
    ["T"] = "El lustre de una nueva capa",
  },
  [2974] = {
    ["D"] = "¡¿Qué?! ¿Que te has cruzado con tauren en esta zona? ¡Será broma! ¡¿Qué aspecto tenían?!$B$B¡El clan Tótem Siniestro! ¿Te has encontrado con ellos? Belgrom, uno de nuestros maestros, tiene un gran interés por ellos... ¡Hasta ha ofrecido una recompensa!$B$BTienes que volver, $C. Vuelve y tráeme sus cuernos... Tráeme montones de ellos, todos los que puedas. Pero no subestimes a los Tótem Siniestro. Ve con cuidado. Vuelve y mátalos, deja que los ríos de Feralas bajen enrojecidos con su sangre oscura.",
    ["O"] = "Lleva 20 cuernos de Tótem Siniestro a Krueg Machacacráneos en el Campamento Mojache.",
    ["T"] = "Un descubrimiento siniestro",
  },
  [2975] = {
    ["D"] = "Los efectos de la invasión de la tribu Gordunni no pasan desapercibidos en la zona. Mira a tu alrededor, $N; su mera presencia marca esta tierra. Debemos hacerles retroceder antes de que empiecen a extenderse por todo Feralas.$B$BSigue el río hacia el norte y encontrarás su puesto de avanzada. Reduce su número, $N. Debemos hacerles ver que no son bienvenidos aquí.",
    ["O"] = "Rok Orhan del Campamento Mojache quiere que mates a 10 ogros Gordunni, 10 magi ogro Gordunni y 5 toscos Gordunni.",
    ["T"] = "Los ogros de Feralas",
  },
  [2976] = {
    ["D"] = "¡¿Qué?!$B$BAh, así que te hablé de la recompensa... Eso me pasa por abrir la bocaza, no aprenderé nunca.$B$BBueno, supongo que, como tú hiciste el trabajo duro, es justo que recibas la recompensa. Llévate los cuernos. Yo haré que te los metan en una caja. Busca a Belgrom Destrozarroca en el Valle del Honor. Querrá saber qué viste y te recompensará por tu duro trabajo.$B$BCuídate, $C, y gracias de nuevo por todo.",
    ["O"] = "Lleva el cajón de cuernos de Tótem Siniestro a Belgrom Destrozarroca a Orgrimmar.",
    ["T"] = "Un descubrimiento siniestro",
  },
  [2977] = {
    ["D"] = "Accedes de nuevo al pedestal y los discos de platino aparecen de repente en tu mochila.$B$BCon lo que te ha enseñado el vigía de piedra y los discos de platino de nuevo en tu poder, ha llegado el momento de regresar a Forjaz e informar a la Liga de Expedicionarios de tu hallazgo.",
    ["O"] = "Habla con Karnik el Historiador de Forjaz.",
    ["T"] = "Regresa a Forjaz",
  },
  [2979] = {
    ["D"] = "Parecen ser planes para algún tipo de ceremonia oscura, $N. Por lo que puedo entender, este es un ritual celebrado por los Señores de la Magia Gordunni.$B$BEncuentra a uno de estos Señores de la Magia y quítale su orbe.$B$BEl orbe de un mago encierra un gran poder. Nos ayudará a descubrir más sobre esta ceremonia y sobre la magia de los Gordunni.",
    ["O"] = "Rok Orhan del Campamento Mojache quiere que encuentres 1 orbe Gordunni.",
    ["T"] = "Una ceremonia oscura",
  },
  [2980] = {
    ["D"] = "Ahora que has demostrado que eres $Gun auténtico guerrero:una auténtica guerrera;, confío en que tengas el valor para continuar tu misión.$B$BViaja a las Altas Tierras Salvajes, al suroeste de aquí. Ahí encontrarás unas estructuras abandonadas cuyos restos yacen en una tierra que antaño perteneciera a los elfos de la noche. Busca esas ruinas y, cuando veas árboles marchitos y tierra profanada, encontrarás a los Gordunni. Hazlos retroceder.$B$BEsos ogros son mucho más fuertes que los que has afrontado hasta ahora, $N, ten cuidado.",
    ["O"] = "Rok Orhan del Campamento Mojache quiere que mates a 10 chamanes Gordunni, 10 brujos Gordunni y 5 aplastadores Gordunni.",
    ["T"] = "Los ogros de Feralas",
  },
  [2981] = {
    ["D"] = "Feralas es el lugar ideal para que cualquier $C ponga a prueba sus cualidades. Allí los ogros Gordunni son los amos y señores y cada día crecen en número. Hay que detenerlos antes de que pongan en peligro nuestra presencia en Feralas.$B$BConozco a una guerrera que vive en el Campamento Mojache, en Feralas. Se llama Rok Orhan. Ve a buscarla. Ella te dará más detalles del estado de la situación.",
    ["O"] = "Belgrom Destrozarroca quiere que hables con Rok Orhan, del Campamento Mojache.",
    ["T"] = "Una amenaza en Feralas",
  },
  [2982] = {
    ["D"] = "Aunque es peligroso, Troyas y yo hemos estado investigando las ruinas que cubren los bosques de Feralas.$B$BPero últimamente, Troyas ha empezado a ir solo a las Altas Tierras Salvajes y eso me preocupa mucho. Los ogros Gordunni han invadido la zona y no son muy simpáticos con los que entran en su territorio.$B$BEsperaba que pudieras viajar a las ruinas, que están al sureste, y que eliminaras a algunos de los ogros. Así el área será más segura para Troyas. Y yo te lo agradecería mucho.",
    ["O"] = "Angelas Brisaluna quiere que mates a 8 brujos Gordunni, 8 chamanes Gordunni y 8 toscos Gordunni.",
    ["T"] = "Las Altas Tierras Salvajes",
  },
  [2983] = {
    ["D"] = "Ha pasado algún tiempo desde tu última prueba, $N. Hasta el momento has sido paciente en tus estudios, pero llegó el momento de que adquieras mayores poderes... es hora de que aprendas el auténtico poder. El elemento del fuego será tuyo si estás $Gdispuesto:dispuesta; a pagar el precio.$B$BBusca a Kranal Fiss en Los Baldíos. Vive en una pequeña casa al norte de El Cruce. Pero cuidado: si llegas hasta las devastadas tierras de los elfos de la noche, habrás ido demasiado lejos.",
    ["O"] = "Busca a Kranal Fiss en Los Baldíos.",
    ["T"] = "La llamada del fuego",
  },
  [2984] = {
    ["D"] = "Ha pasado tiempo desde tu última prueba, $N. Hasta ahora has demostrado paciencia en tus estudios, pero ha llegado el momento de que aprendas algo más. Debes aprender de la destrucción... y el caos. De un elemento: el fuego. Podrás comprenderlo, si estás $Gdispuesto:dispuesta; a pagar el precio.$B$BBusca a Kranal Fiss en Los Baldíos. Vive en una pequeña casa al norte de El Cruce. Pero cuidado: si llegas hasta las tierras de los elfos de la noche, habrás ido demasiado lejos.",
    ["O"] = "Busca a Kranal Fiss en Los Baldíos.",
    ["T"] = "La llamada del fuego",
  },
  [2985] = {
    ["D"] = "$GDispuesto:Dispuesta; pareces para la prueba siguiente, $N. Paciencia en tus estudios has mostrado pero de aprender aún más la hora ha llegado. Ha llegado la hora de que aprendas cosas sobre la pureza del agua.$B$BBusca a Islen Vidente del Agua en Los Baldíos. Se encuentra por la costa oriental, al sur del Río Furia del Sur y de Trinquete. La encontrarás en su cabaña de pesca. Que la Fortuna te sea favorable, $N.",
    ["O"] = "Encuentra a Islen Vidente del Agua en Los Baldíos.",
    ["T"] = "La llamada del agua",
  },
  [2986] = {
    ["D"] = "Mucho tiempo ha pasado desde que a prueba te pusieron, $N. Paciencia en tus estudios hasta ahora has mostrado pero de aprender aún más la hora ha llegado. Ha llegado la hora de que aprendas cosas sobre la pureza del agua.$B$BBusca a Islen Vidente del Agua en Los Baldíos. Se encuentra por la costa oriental, al sur del Río Furia del Sur y de Trinquete. La encontrarás en su cabaña de pesca. Que la Fortuna te sea favorable, $N.",
    ["O"] = "Encuentra a Islen Vidente del Agua en Los Baldíos.",
    ["T"] = "La llamada del agua",
  },
  [2987] = {
    ["D"] = "Acabo de llegar a Feralas y he oído que los ogros Gordunni del norte tienen minerales interesantes. No sé qué estarán tramando, ¡pero protegen sus minerales casi de forma sospechosa! No los dejan por ahí, no, los entierran para que gente como yo no consiga echarles el guante.$B$BSi quieres ayudarme, te daré mi pala. Ve a la Avanzada Gordunni que hay al norte de aquí y desentiérrame un poco de cobalto Gordunni. Cuando veas un brillo azul, ¡empieza a cavar!",
    ["O"] = "Orwin Cacharrejos, del Campamento Mojache, quiere 12 muestras de cobalto Gordunni.",
    ["T"] = "Cobalto Gordunni",
  },
  [2990] = {
    ["D"] = "Llévale esta pluma al adivino Thadius Sombramacabra. Se fue de Pico Nidal hace años para estudiar ciencias oscuras, pero sigue siendo un Martillo Salvaje.$B$BA lo mejor puede ayudarnos a encontrar a Picoafilado.$B$BEsta pluma estaba en su nido. Con ella y un poco de suerte, Thadius usará sus poderes de adivinación para localizar al grifo.$B$BSuerte, $N. Thadius vive en Las Tierras Devastadas, en la torre de los magos del Castillo de Nethergarde.",
    ["O"] = "Lleva la pluma de Picoafilado a Thadius Sombramacabra a Las Tierras Devastadas.",
    ["T"] = "Thadius Sombramacabra",
  },
  [2991] = {
    ["D"] = "Voy a averiguar en dónde está tu grifo, pero primero haz algo por mí. Hay un trol en Zul\'Farrak, Nekrum Cometripas, que tiene un medallón que me gustaría estudiar. Tráeme ese medallón y usaré mis poderes para encontrar a Picoafilado.$B$BZul\'Farrak está al otro lado del océano, al oeste, en el desierto de Tanaris.",
    ["O"] = "Lleva el medallón de Nekrum a Thadius Sombramacabra a Las Tierras Devastadas.",
    ["T"] = "Medallón de Nekrum",
  },
  [2993] = {
    ["D"] = "Picoafilado está enjaulado en un precipicio en la cumbre de Jintha\'Alor, en las Tierras del Interior. La llave de la jaula está en poder de la sacerdotisa vil Hexx, suma sacerdotisa de los trols Vilrama.$B$BYa está. He terminado. Te deseo suerte en tu misión, $N.",
    ["O"] = "Habla con el maestro de grifos Garracha.",
    ["T"] = "Regresa a las Tierras del Interior",
  },
  [2994] = {
    ["D"] = "¡Tenemos que rescatar a Picoafilado!$B$B¡Debes llegar hasta Jintha\'Alor, en el corazón del territorio Vilrama, conseguir la llave que tiene la sacerdotisa vil Hexx, encontrar la jaula y liberar al grifo!",
    ["O"] = "Llega hasta la cima de Jintha\'Alor, mata a la sacerdotisa vil Hexx, consigue la llave de la jaula y rescata a Picoafilado.",
    ["T"] = "Salvar a Picoafilado",
  },
  [2995] = {
    ["D"] = "La Avanzada Quel\'Danil es el centro de comunicación de los elfos nobles de las Tierras del Interior. Sus embajadores están trabajando para establecer relaciones con otras razas con las que compartir sus conocimientos sobre los trols Secacorteza que viven en la zona sur de las Tierras del Interior. Todos los documentos relativos a sus comunicaciones se guardan en la avanzada.$B$BTenemos que evitar las alianzas entre Vallealto y otros pueblos, $N.$B$BDestruye los documentos de Vallealto. Tenemos que quemar sus líneas de comunicación... literalmente.",
    ["O"] = "Quema los registros de Vallealto, el informe de Vallealto y las notas de Vallealto.",
    ["T"] = "Líneas de comunicación",
  },
  [2997] = {
    ["D"] = "Hola, $N. Sé que estás $Gocupado:ocupada; ayudando a las gentes de Dun Morogh, pero cuando tengas tiempo, deberías ir a Forjaz y hablar con una persona muy respetada por aquí: Tiza Forjabatalla. Los paladines más jóvenes deben acudir a ella cuando llega el momento de que aprendan más sobre la Luz y sobre lo que se espera de ellos.$B$BLa encontrarás escaleras arriba, en la Sala de los Misterios. Por si nunca has estado allí, está en La Sala Mística. Buena suerte, $N.",
    ["O"] = "Ve a Forjaz y habla con Tiza Forjabatalla.",
    ["T"] = "Escrito sobre divinidad",
  },
  [2998] = {
    ["D"] = "Saludos, $Ghermano:hermana;. Duthorian Rall, uno de nuestros paladines más influyentes, estuvo hablando de ti hace poco. Le oí decir que era hora de ponerte a la prueba... Y bueno, también dijo algo sobre lo bien que estás yendo hasta ahora y eso.$B$BSi yo fuera tú, iría a Ventormenta y hablaría con él. Seguramente estará en la Catedral de la Luz. Ahí es donde pasa la mayor parte de su tiempo.",
    ["O"] = "Habla con Duthorian Rall en Ventormenta.",
    ["T"] = "Escrito sobre divinidad",
  },
  [2999] = {
    ["D"] = "Hola, $N. Sé que estás $Gocupado:ocupada; ayudando a las gentes de Dun Morogh, pero cuando tengas tiempo, deberías subir las escaleras y hablar con una persona que es muy respetada por aquí: Tiza Forjabatalla. Los paladines más jóvenes deben acudir a ella cuando llega el momento de que aprendan más sobre la Luz y sobre lo que se espera de ellos.",
    ["O"] = "Ve a Forjaz y habla con Tiza Forjabatalla.",
    ["T"] = "Escrito sobre divinidad",
  },
  [3000] = {
    ["D"] = "Hola, $N. Sé que estás $Gocupado:ocupada; ayudando a las gentes de Azeroth, pero cuando tengas tiempo, deberías volver a Forjaz y hablar con una de las personas más respetadas de tu casa: Tiza Forjabatalla. Los paladines enanos más jóvenes deben acudir a ella cuando llega el momento de que aprendan más sobre la Luz y sobre lo que se espera de todos nosotros.$B$BLa encontrarás escaleras arriba, en la Sala de los Misterios, que a su vez se encuentra en La Sala Mística.",
    ["O"] = "Ve a Forjaz y habla con Tiza Forjabatalla.",
    ["T"] = "Escrito sobre divinidad",
  },
  [3001] = {
    ["D"] = "Yo te he instruido hasta ahora pero ha llegado el momento de que cambies de instructor. Se llama Strahad y vive en Trinquete. Es un humano, pero no vayas a creer por eso que es débil ni que ha de ser tu enemigo... al menos de momento... Es muy poderoso y merecedor de respeto, y puede enseñarte hechizos de invocación superior, hechizos que creo que estás $Gpreparado:preparada; para conocer. Ve a buscarlo.$B$BLo encontrarás en el puerto de Trinquete, cerca de su torre.",
    ["O"] = "Habla con Strahad Farsan en Trinquete.",
    ["T"] = "Buscando a Strahad",
  },
  [3002] = {
    ["D"] = "Aunque conozco bien a mi enemigo, soy solo un guerrero, $N. No puedo ni empezar a decirte de qué es capaz este orbe.$B$BPero conozco a un mago de Orgrimmar que podrá decirnos más cosas sobre el orbe. Llévaselo a él.",
    ["O"] = "Lleva el orbe Gordunni a Uthel\'nay, que está en la Cabaña de Brezo de Orgrimmar.",
    ["T"] = "El orbe Gordunni",
  },
  [3022] = {
    ["D"] = "$N, veo que ansías dedicarte a mi valerosa causa. ¡Pues claro que puedes hacer algo por mí!$B$BLlévale esta caja de huevos a Erelas Cieloámbar. Él es un druida que los sabe cuidar muy bien pues ayuda a salir del cascarón a los pequeños hipogrifos y después los instruye. Lo encontrarás en Teldrassil, en una pequeña aldea situada al sur que se llama Rut\'theran.",
    ["O"] = "Llévale la caja cuidadosamente empaquetada a Erelas Cieloámbar, que está en Aldea Rut\'theran.",
    ["T"] = "Manejar con cuidado.",
  },
  [3042] = {
    ["D"] = "Los trols Furiarena de Zul\'Farrak utilizan arenisca para crear sus materiales de temple y yo también puedo utilizarlo para templar mis creaciones. Es muy valorado entre los expertos, así que si me traes un buen suministro de temple, te recompensaré muy bien.$B$BZul\'Farrak está al noroeste de Gadgetzan. Buena suerte.",
    ["O"] = "Lleva 20 viales de temple trol a Trenton Mazaligera en Gadgetzan.",
    ["T"] = "Temple trol",
  },
  [3062] = {
    ["D"] = "Edana Garfaodio es la reina de las arpías de Nortearroyo que viven en las ruinas del oeste. Es el mal encarnado. He oído que su corazón es de cristal. Carece de sentimientos... aparte del odio que siente por todo lo que no sea ella. Debes enfrentarte a Edana y traerme su corazón oscuro.$B$BHe oído hablar de un cuerno que llevan esas arpías... Si lo tocan en las Piedras Garfaodio, en la parte norte de las ruinas, su reina acude.$B$BImagina su sorpresa cuando vea que no son sus semejantes quienes la llaman, sino la muerte.",
    ["O"] = "Talo Pezuñahendida, del Campamento Mojache quiere el corazón oscuro de Edana Garfaodio.",
    ["T"] = "Corazón oscuro",
  },
  [3063] = {
    ["D"] = "En mis tiempos fui un avezado cazador. No había criatura a la que yo temiera. Hasta que...$B$BUn día estaba en el bosque, afilando mi espada después de una dura batalla y caí en una emboscada. Unas arpías me rodearon y... casi no lo cuento. Tuve que arrastrarme para ponerme a salvo.$B$BTodavía puedo oír el eco de sus gritos...$B$BDespués de aquello, no he vuelto a ser el mismo. Han pasado muchos años, pero el odio sigue hirviéndome en las venas. Debo desquitarme.$B$BPuedes encontrar a las arpías de Nortearroyo al oeste de aquí. Véngame, $N.",
    ["O"] = "Talo Pezuñahendida quiere que mates a 4 arpías Nortearroyo, 4 Malaplumas Nortearroyo, 4 clamavientos Nortearroyo y 4 destripadoras Nortearroyo.",
    ["T"] = "Venganza contra Nortearroyo",
  },
  [3064] = "_",
  [3065] = {
