  [7810] = {
    ["D"] = "¡Ser un maestro de la arena tiene que ser algo más que esto! ¡Quizás lo sepa el pirata que lleva el evento en Gurubashi!",
    ["O"] = "Habla con Corto John Mitril en la Arena de la Vega de Tuercespina.",
    ["T"] = "Maestro de arena",
  },
  [7811] = {
    ["D"] = "",
