  [11107] = {
    ["D"] = "Habla con el señor supremo Mor\'ghor en el Campamento Faucedraco en el Valle Sombraluna.|n",
    ["O"] = "Habla con el señor supremo Mor\'ghor en el Campamento Faucedraco en el Valle Sombraluna.|n",
    ["T"] = "Inclínate ante el Alto Señor",
  },
  [11108] = {
    ["D"] = "¡Ven, $Galto señor:alta señora; $N! ¡Illidan en persona nos espera!",
    ["O"] = "Ve al encuentro de Illidan Tempestira. Si vives para contarlo, busca a Barthamus en el Bajo Arrabal de Shattrath.",
    ["T"] = "Lord Illidan Tempestira",
  },
  [11109] = {
