pfDB["quests"]["esES"] = {
  [1] = {
    ["D"] = "$Tpunk;!  Kill Kobold Vermin, 2 of em. NEW TEST AGAIN",
    ["O"] = "Kill Kobold Vermin, 2 of em.",
    ["T"] = "The \"Chow\" Quest (123)aa",
  },
  [2] = {
    ["D"] = "El poderoso hipogrifo Garrafilada ha sido ejecutado, con la garra de la bestia derribada como testimonio de tu victoria. Seguro que Senani Truenozón, del Puesto del Hachazo, estará interesado en ver este trofeo que prueba tus actos.",
    ["O"] = "Llévale la garra de Garrafilada a Senani Truenozón en el Puesto del Hachazo, Vallefresno.",
    ["T"] = "La garra de Garrafilada",
  },
  [5] = {
    ["D"] = "¡Llevo semanas escondiéndome en esta ciudad fantasma y no he comido más que larvas y hierbajos! Necesito algo de comida decente. Estoy en disposición de pagar bien.$B$BSi me traes un festín, te pagaré generosamente.$B$BHe oído que el chef Grual del Mesón del Cuervo Escarlata de Villa Oscura hace unas estupendas croquetas de cangrejo...",
    ["O"] = "Habla con el Chef Grual.",
    ["T"] = "La hambruna de Gato",
  },
  [6] = {
    ["D"] = "Garrick Piesuaves es un maleante que lleva semanas acosando a granjeros y mercaderes. Ha sido visto en una choza cerca de los viñedos, cruzando el puente al este de la abadía. Tráeme la cabeza de ese villano y podrás quedarte con su botín.$b$bPero ve con cuidado, $N. Garrick anda con una pandilla de matones. Puede que no te sea fácil llegar hasta él.",
    ["O"] = "Mata a Garrick Piesuaves y lleva su cabeza al ayudante Willem a la Abadía de Villanorte.",
    ["T"] = "La recompensa por Garrick Piesuaves",
  },
  [7] = {
    ["D"] = "$N, lo primero que tienes que hacer es limpiar; un clan de kobolds ha invadido los bosques del norte. Ve y liquida a todos los kobolds que encuentres; si reducimos sus filas podremos echarlos de Villanorte.",
    ["O"] = "Mata 10 alimañas kobold y ve a hablar con el alguacil McBride.",
    ["T"] = "La limpieza del campamento kóbold",
  },
  [8] = {
    ["D"] = "¡Eh, colega! ¿Le harías un favor a un joven hombre que ha combatido a más zombis y arañas de los que puede contar?$B$BTengo una carta que tiene que llegar a Rémol... Es para una tabernera llamada Renee no sé qué. No creo que importe cómo se apellida, la verdad.$B$BPero el sitio es muy acogedor y está lleno de víctimas de la Plaga que intentan abrirse paso en el mundo. Es el sitio ideal para descansar... si es que necesitas hacerlo. Anímate... si vas, te pagaré bien.",
    ["O"] = "Lleva la carta indefinida a Claros de Tirisfal y entrégasela a la tabernera Renee.",
    ["T"] = "Un trato de pícaro",
  },
  [9] = {
    ["D"] = "¡Mira lo que le ha ocurrido a este lugar! Estas tierras estaban antes en manos de buenos granjeros. Pero los condenados ladrones les han ahuyentado a todos. ¡Pero no a mí! Eso sí, parece que algunos Vigilantes de la cosecha han invadido los campos.$B$BSi estás $gpreparado:preparada; me gustaría que fueras y mataras a 20 de ellos. Vuelve cuando hayas terminado y te pagaré. Si terminas con los de mi campo, puedes limpiar también los campos vecinos.",
    ["O"] = "El granjero Saldean quiere que mates 20 Vigilantes de la cosecha.",
    ["T"] = "Campos de la muerte",
  },
  [10] = {
    ["D"] = "¡Oh, esto es horrible! Envié a mi mejor ayudante, el perito adjunto Patagolpe, al desierto situado al sudeste de aquí, una zona que se conoce como Sima Abierta. Bien, pues ha desaparecido y su último informe contenía más información acerca de esos extraños bichos que había visto por la zona. ¿Es posible que ya hayan infestado dos grandes regiones del desierto?$B$BPor favor, ¡necesito que vayas a Sima Abierta en busca de Patagolpe! Y si no lo encuentras a él, al menos a ver si aparece su equipo de análisis. Me temo que pueda ser lo único que quede de ese pobre chico.",
    ["O"] = "Descubre qué le ha ocurrido al perito adjunto Patagolpe y llévalo a él o su equipo de análisis ante el perito jefe Picoflojo, que está en Gadgetzan.",
    ["T"] = "La redención de Patagolpe",
  },
  [11] = {
    ["D"] = "En las fronteras del Bosque de Elwynn se han avistado gnolls, unas criaturas salvajes que seguramente no están allí por trabajo. Un grupo enorme de ellos, uno más numeroso del que podemos contener solos, ha infestado los bosques que hay al sur de la torre de vigilancia. Otro grupo ha infestado las áreas cercanas al Lago del Hito, al este.$B$BEl ejército de Ventormenta elogiará a quienquiera que le ayude a matarlos. Tráeme sus brazaletes gnoll violáceos como prueba de tu hazaña.",
    ["O"] = "Lleva 8 brazaletes gnoll violáceos al ayudante del alguacil Rainer al Cuartel.",
    ["T"] = "Recompensas por los gnolls Zarparrío",
  },
  [12] = {
    ["D"] = "Las Milicias del Pueblo solo tienen un objetivo: defender las tierras de los Páramos de Poniente y traer la paz a nuestro entorno. Por desgracia, el precio de la paz suele pagarse en sangre.$b$bSegún el informe de uno de mis rastreadores, una banda de tramperos Defias ha sido vista cerca de la Mina de Jango al noreste de aquí, en La Granja de Molsen y en la Plantación de Calabazas de Cejade. Si quieres unirte a nuestras filas, acaba con 15 tramperos Defias y con 15 contrabandistas Defias y vuelve a verme.",
    ["O"] = "Gryan Mantorrecio quiere que mates 15 tramperos Defias y 15 contrabandistas Defias. Cuando lo hayas hecho vuelve a verle en la Colina del Centinela.",
    ["T"] = "Las Milicias del Pueblo",
  },
  [13] = {
    ["D"] = "Una banda de despiadados saqueadores Defias ha sido vista saqueando la Mina de la Costa de Oro, el Arroyo de la Luna y la Hacienda de Alexston. Las Milicias del Pueblo no tolerarán ese comportamiento. Avisa inmediatamente, $n, y haz conocer la presencia de la Luz en los Páramos de Poniente. La Mina de la Costa de Oro está cerca de la costa, al oeste de la torre. El siguiente paso de tu instrucción consistirá en acabar con 15 de esos saqueadores Defias y con 15 despojadores Defias.",
    ["O"] = "Gryan Mantorrecio quiere que mates 15 saqueadores Defias y 15 despojadores Defias. Cuando lo hayas hecho vuelve a verle en la Colina del Centinela.",
    ["T"] = "Las Milicias del Pueblo",
  },
  [14] = {
    ["D"] = "Algunos Defias nos han esquivado. Según informa mi mejor rastreador, los Defias han estado saqueando y expoliando el campo, todo el sureste de los Páramos de Poniente. Creemos que se están ocultando en Las Colinas Afiladas, planeando sus siguientes pasos. Acaba con esos miserables en nombre de Las Milicias del Pueblo.",
    ["O"] = "Gryan Mantorrecio quiere que mates 15 bandoleros Defias y 5 acechacaminos Defias. Cuando lo hayas hecho vuelve a verle en la Colina del Centinela.",
    ["T"] = "Las Milicias del Pueblo",
  },
  [15] = {
    ["D"] = "$N, los rastreadores dicen que hay más kobolds de lo que creíamos. Un grupo de obreros ha acampado cerca de la Mina del Eco, al norte de aquí.$B$BVe a la mina y liquídalos; sabemos que hay al menos diez. Comprueba que no quede ninguno más y vuelve.",
    ["O"] = "Mata 10 obreros kobold y ve a hablar con el alguacil McBride.",
    ["T"] = "Investigar la Mina del Eco",
  },
  [16] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Dale de beber a Gerard",
  },
  [17] = {
    ["D"] = "Hace poco me hablaron de un organismo recién descubierto, las setas moradas. Crecen arracimadas en lo más profundo de la excavación de Uldaman; es el único sitio del que tengo noticia. Quiero ver qué usos potenciales pueden tener en la alquimia, por eso necesito tu ayuda.$b$bTráeme una docena de setas moradas. Es posible que se defiendan con esporas venenosas; ten cuidado.$b$b¡A cambio, te daré uno de mis famosos elixires vigorizantes!",
    ["O"] = "Lleva 12 setas moradas a Ghak Sanadón a Thelsamar.",
    ["T"] = "Componentes de Uldaman",
  },
  [18] = {
    ["D"] = "Hace poco llegó una banda de maleantes a Villanorte. Se hacen llamar la Hermandad Defias; se les ha visto cruzando el río hacia el este.$b$bNo sé qué están haciendo, ¡pero seguro que no es nada bueno! Tráeme sus pañuelos y te daré un arma como recompensa.",
    ["O"] = "Lleva 12 pañuelos de tela de saco rojo al ayudante Willem cerca de la Abadía de Villanorte.",
    ["T"] = "Hermandad de ladrones",
  },
  [19] = {
    ["D"] = "Hay avanzados y renegados Roca Negra tendiendo emboscadas entre aquí y el Fuerte de Petravista. El jefe de los avanzados se llama Tharil\'zun y lo queremos muerto. ¡Tráeme su cabeza!",
    ["O"] = "Lleva la cabeza de Tharil\'zun al alguacil Marris a Crestagrana.",
    ["T"] = "Tharil\'zun",
  },
  [20] = {
    ["D"] = "Los orcos Roca Negra llegaron desde Las Estepas Ardientes y han invadido el Fuerte de Petravista, al norte de aquí. Andan merodeando por el norte de Villa del Lago y tienen a los ciudadanos encerrados como a ganado. $b$bLibra a Villa del Lago de esta amenaza. Mata a los orcos y tráeme 10 restos de hacha de guerra como prueba. $b$bAunque nos amenazan desde el norte, sabemos que hay más al sureste de Crestagrana. Si los encuentras, mátalos también.",
    ["O"] = "Lleva 10 restos de hacha de guerra al alguacil Marris a Villa del Lago.",
    ["T"] = "La amenaza de los Roca Negra",
  },
  [21] = {
    ["D"] = "Ha quedado demostrado que tenemos que purgar la Mina del Eco. Vuelve allí y ayuda a eliminar a los kobolds.$b$bDate prisa, $N. Cuanto más tiempo dejemos a los kobolds en la mina, más nos costará expulsarlos de Villanorte.",
    ["O"] = "Mata 12 peones kobold y ve a hablar con el alguacil McBride a la Abadía de Villanorte.",
    ["T"] = "Escarmuza en la Mina del eco",
  },
  [22] = {
    ["D"] = "Las cebollas están peladas. El ajo machacado. La corteza está tostada. Las hierbas de eneldo picadas. ¡Lo único que me falta para mi famosa empanada de carne son 8 hígados de dentosangre!",
    ["O"] = "Salma Saldean necesita 8 hígados de dentosangre para hacer un pastel de hígado de dentosangre.",
    ["T"] = "Pastel de hígado de dentosangre",
  },
  [23] = {
    ["D"] = "La Zarpa del poderoso oso Ursangous es tu trofeo para esta parte de la Caza de Vallefresno. La pequeña media luna de su zarpa es un emblema cuyo significado sigue siendo un misterio.$B$BDeberías llevar la zarpa a Senani Truenozón, en el Puesto del Hachazo, para demostrar que has vencido a la poderosa criatura y has completado esta parte de la caza.",
    ["O"] = "Llévale la zarpa de Ursangous a Senani Truenozón en el Puesto del Hachazo, Vallefresno.",
    ["T"] = "La zarpa de Ursangous",
  },
  [24] = {
    ["D"] = "El otrora veloz Shadumbra yace ahora inerte ya que te atribuyes esta victoria. La cabeza del sable de la noche caído es un trofeo digno para este combate de la Caza de Vallefresno. Senani Truenozón, del Puesto del Hachazo, querrá ver tal trofeo para darte lo que te corresponde por esta parte de la Caza de Vallefresno.",
    ["O"] = "Llévale la cabeza de Shadumbra a Senani Truenozón en el Puesto del Hachazo, Vallefresno.",
    ["T"] = "La cabeza de Shadumbra",
  },
  [25] = {
    ["D"] = "Si quieres ser de utilidad, ¿por qué no haces algo para remediar la situación cerca de El Paso del Espolón?$B$BEl transporte entre Vallefresno y El Espolón está paralizado porque el lago está cobrando vida y ataca a cualquiera que se acerque... lo que significa a cualquiera que utilice el paso. Lo que sería aún más útil que poner firmes a los elementales de agua allí sería tener a alguien rastreando la glorieta del Lago Mystral, la que tiene una vista ininterrumpida de la avanzada de la Alianza en ese lugar.$B$B¿Te apetece ser útil?",
    ["O"] = "Envía 12 elementales de agua podrida al Lago Mystral que esperan al este de El Paso del Espolón y al suroeste del Puesto del Hachazo.$B$BExplora la glorieta que está en el Lago Mystral y que domina la cercana avanzada de la Alianza.$B$BVuelve junto a Mastok Sibilante en el Puesto del Hachazo, Vallefresno.",
    ["T"] = "Paso del Espolón bloqueado",
  },
  [26] = {
    ["D"] = "$N, he seguido tus progresos con gran gozo. Sin duda te estás convirtiendo en $gun valioso protector:una valiosa protectora; de la naturaleza.$B$BHa llegado el momento de que aprendas más sobre una de las formas animales con las que Cenario nos ha bendecido. Una forma que podemos adoptar y nos permite controlar el agua. Tendrás que viajar al poblado Amparo de la Noche, que está en Claro de la Luna y hablar con Dendrite Rafagaestelar, del Círculo Cenarion. Él será tu guía.",
    ["O"] = "Ve a Claro de la Luna, al poblado Amparo de la Noche y habla con Dendrite Rafagaestelar.",
    ["T"] = "Una lección que aprender",
  },
  [27] = {
    ["D"] = "$N, he seguido tus progresos con gran gozo. Sin duda te estás convirtiendo en $gun valioso protector:una valiosa protectora; de la naturaleza. $B$BHa llegado el momento de que aprendas más sobre una de las formas animales con las que Cenario nos ha bendecido. Una forma que podemos adoptar y nos permite controlar el agua. Tendrás que viajar al poblado Amparo de la Noche, que está en Claro de la Luna, y hablar con Dendrite Rafagaestelar del Círculo Cenarion. Él será tu guía.",
    ["O"] = "Ve a Claro de la Luna, al poblado Amparo de la Noche y habla con Dendrite Rafagaestelar.",
    ["T"] = "Una lección que aprender",
  },
  [28] = {
    ["D"] = "En tu primera prueba, debes ir a las profundidades del Lago Elune\'ara y encontrar un adorno de altar. Se descompone rápidamente, por lo que deberás ir inmediatamente al Santuario de Rémulos. Coloca el adorno en el altar y habla con Tajarri; es una de las guardianas más apreciadas de Claro de la Luna.$b$bTienes un tiempo limitado para completar la tarea y es probable que tengas que competir con otros druidas en busca de adornos. Necesitarás toda tu destreza mental y física.$b$bSuerte, $N.",
    ["O"] = "Lleva un adorno de altar del Lago Elune\'ara al Santuario de Remulos, al noroeste de Claro de la Luna.  Usa el adorno en el santuario. $b$bDespués, habla con Tajarri en el santuario para completar la prueba.",
    ["T"] = "La prueba del lago",
  },
  [29] = {
    ["D"] = "En tu primera prueba, debes ir a las profundidades del Lago Elune\'ara y encontrar un adorno de altar. Se descompone rápidamente, por lo que deberás ir inmediatamente al Santuario de Rémulos. Coloca el adorno en el altar y habla con Tajarri; es una de las guardianas más apreciadas de Claro de la Luna.$b$bTienes un tiempo limitado para completar la tarea y es probable que tengas que competir con otros druidas en busca de adornos. Necesitarás toda tu destreza mental y física.$b$bSuerte, $N.",
    ["O"] = "Lleva un adorno de altar del Lago Elune\'ara al Santuario de Remulos, al noroeste de Claro de la Luna.  Usa el adorno en el santuario. $b$bDespués, habla con Tajarri en el santuario para completar la prueba.",
    ["T"] = "La prueba del lago",
  },
  [30] = {
    ["D"] = "En tu segunda prueba debes encontrar las dos mitades del colgante del león marino. Una obtiene su poder de la agilidad del león marino cuando se desliza en el agua, la otra lo obtiene de su increíble capacidad de resistencia. Los druidas se alimentan de ambos rasgos para asumir su forma acuática.$b$bHabla con la gente de Claro de la Luna para averiguar dónde están las mitades y tráemelas para que las unamos. Luego llevarás el colgante a Dendrite Rafagaestelar.",
    ["O"] = "Busca el colgante de Agilidad acuática y el colgante de Resistencia acuática.  Habla con la gente de Claro de la Luna para conseguir pistas sobre la ubicación de los colgantes. $b$bForma el colgante del león marino con los otros dos cuando estés cerca del Santuario de Remulos. $b$bLleva el colgante completo a Dendrite Rafagaestelar a Amparo de la Noche, en Claro de la Luna.",
    ["T"] = "La prueba del león marino",
  },
  [31] = {
    ["D"] = "Has completado tu formación para la adopción de una forma acuática. Ve a ver a tu instructor, Turak Tótem Rúnico, a Cima del Trueno. Enséñale el colgante y demuéstrale que puedes aprender lo que tenga que enseñarte. Él completará tu formación para que puedas fundirte con el agua.$b$bBuena suerte, joven druida. Volveremos a vernos.",
    ["O"] = "Vuelve a Cima del Trueno y enséñale el colgante del león marino a Turak Tótem Rúnico.",
    ["T"] = "Forma acuática",
  },
  [32] = {
    ["D"] = "Aquí tienes el informe. Te ruego que se lo entregues a alguien que tenga conocimientos de este tema.$B$BConozco a un erudito trol llamado Zilzibin Drumlore que ha dedicado mucho tiempo al estudio de los seres vivos que solo se encuentran en Azeroth. ¡Es un historiador de los pies a la cabeza! Entrégale este informe si quieres que te ayude a entender a qué te enfrentas y el modo de detener esta amenaza. Vamos a necesitar toda la ayuda que podamos conseguir.$B$BZil vive en una casa en el segundo nivel de La Calle Mayor en Orgrimmar.",
    ["O"] = "Entrégale el informe a Zilzibin Drumlore en Orgrimmar.",
    ["T"] = "La sublevación de los silítidos",
  },
  [33] = {
    ["D"] = "¡Condenados lobos Fauces de Madera! Pero me gustan los filetes de lobo, la verdad. Tráeme carne dura de lobo y te la cambiaré por algo que te será útil.$b$bConseguirás la carne de los lobos y lobeznos Fauces de Madera que andan por la campiña de Villanorte.",
    ["O"] = "Lleva 8 piezas de carne dura de lobo a Eagan Desollapieles junto a la Abadía de Villanorte.",
    ["T"] = "Los lobos cruzan la frontera",
  },
  [34] = {
    ["D"] = "Han vuelto a arrasar mi jardín, y sé quién es el responsable. Es ese maldito jabalí, ese al que el mariscal Marris llama Bellygrub. $b$bYa es demasiado tarde para replantar narcisos. Ese puerco ha devastado mis flores. ¡Acaba con ese bicharraco! Si me traes un colmillo suyo, te recompensaré. Dicen que anda husmeando por los campos que hay al suroeste de Villa del Lago, pero de vez en cuando viene a mi jardín.",
    ["O"] = "Martie Jainrose de Villa del Lago quiere que mates a Panzallena.  LLévale un colmillo como prueba.",
    ["T"] = "Una visita poco grata",
  },
  [35] = {
    ["D"] = "Si te preocupa que los rumores sobre los murlocs sean ciertos, viaja al puente del este de Elwynn y habla con el guardia Thomas. Esta última semana se encuentra emplazado en el puente y sabrá cuál es el estado de la situación.$B$BTráeme su informe.",
    ["O"] = "El alguacil Dughan quiere que hables con el guardia Thomas.",
    ["T"] = "Más preocupaciones",
  },
  [36] = {
    ["D"] = "Nunca pensé que llegaría el día en el que tuviera que dejar la granja. Pero los campos están llenos de ladrones y es demasiado peligroso que sigamos aquí. En cuanto el granjero Furlbrow consiga arreglar el carro nos pondremos en marcha.$b$b¿Podrías hacerme un favor? Deja que te escriba mi receta del estofado de los Páramos de Poniente. Por favor, llévasela a Salma Saldean, estará en su granja, nada más pasar la bifurcación del camino.",
    ["O"] = "Verna Furlbrow quiere que le entregues su receta del estofado de los Páramos de Poniente a Salma Saldean.",
    ["T"] = "Estofado de los Páramos de Poniente",
  },
  [37] = {
    ["D"] = "Hace unos días mandé a dos guardias, Rolf y Malakai, a investigar el río. Aún no han regresado. Para completar mi informe necesito saber qué les ocurrió a esos hombres.$B$BViaja al norte a lo largo del río y encuentra a mis guardias... o lo que quede de ellos.",
    ["O"] = "El guardia Thomas quiere que viajes al norte, río arriba, y busques a sus dos guardias, Rolf y Malakai.",
    ["T"] = "Encuentra a los guardias perdidos",
  },
  [38] = {
    ["D"] = "¡Ayúdame a preparar el estofado de los Páramos de Poniente! Vuelve con estos ingredientes:$b$b3 piezas de carne fibrosa de buitre$b3 morros de dentosangre$b3 ojos de múrloc$b3 okras",
    ["O"] = "Salma Saldean quiere 3 carnes fibrosas de buitre, 3 morros de dentosangre, 3 ojos de múrloc y 3 okras.",
    ["T"] = "Estofado de los Páramos de Poniente",
  },
  [39] = {
    ["D"] = "Habla al alguacil Dughan de la muerte de Malakai y Rolf y después infórmale de que no podrá contener a los murlocs del este de Elwynn con sus tropas.$B$BSé que no tenemos bastantes soldados, quizás Dughan pueda encontrar alguno más.",
    ["O"] = "Preséntate ante el alguacil Dughan en Villadorada.",
    ["T"] = "Entrega el informe de Thomas",
  },
  [40] = {
    ["D"] = "¡Hay una nueva amenaza en el Bosque de Elwynn, $N! ¡Los murlocs están subiendo por los ríos del este de Elwynn, ahuyentando a los peces y atacando a las buenas gentes de allí!$B$BAvisé al alguacil Dughan, pero le preocupan más los gnolls y los bandidos. No cree que los murlocs sean una amenaza.$B$B¡Por favor, $N! ¡Habla con Dughan y convéncele de que envíe más tropas al este!",
    ["O"] = "Remy Dos Veces quiere que hables con el alguacil Dughan de Villadorada.",
    ["T"] = "Un peligro con olor a pescado",
  },
  [45] = {
    ["D"] = "En tu exploración del área encuentras huellas palmeadas que llevan al este por la costa del Lago del Hito. Hacia el este, en la distancia, adivinas la silueta de una aldea múrloc.$B$BQuizás Rolf encontró allí su destino...",
    ["O"] = "Busca a Rolf o alguna prueba de su muerte en el poblado múrloc.",
    ["T"] = "Descubre el destino de Rolf",
  },
  [46] = {
    ["D"] = "El ejército de Ventormenta ha puesto precio a la cabeza de los rondadores y batidores múrloc de Elwynn. Mátalos y tráeme sus ancas. El ejército de Ventormenta te recompensará bien.$B$BLos murlocs han construido una aldea en el Lago del Hito, al norte de aquí, y otra en el sur, donde se divide el río.",
    ["O"] = "Lleva 8 aletas de múrloc rotas al guardia Thomas al puente oriental de Elwynn.",
    ["T"] = "Recompensas por los múrlocs",
  },
  [47] = {
    ["D"] = "Los kobolds de esa zona a veces llevan polvo de oro. Y me vendría muy bien. Consígueme un montón de polvo de oro y te pagaré el mejor precio de la ciudad, ¡el mejor!$B$BPuedes encontrar kobolds en la Mina Abisal, al sur. Y también alrededor de la Cantera de Jaspe, al noreste.",
    ["O"] = "Lleva 10 unidades de polvo de oro a Remy Dos Veces a Villadorada. Puedes obtener polvo de oro de los kobolds del Bosque de Elwynn.",
    ["T"] = "Intercambio de polvo de oro",
  },
  [48] = {
    ["D"] = "Allá en el Sur donde las zarzas irritan$bHay una fuente de aguas umbrías$bPura como la sagrada Hija de la Luz$bAgua sagrada de manantial me traerás tú.",
    ["O"] = "Lleva agua sagrada de manantial a Tragoamargo Cebatruenos.",
    ["T"] = "Dulce ámbar",
  },
  [49] = {
    ["D"] = "Ámbar es el color del amor de mi vida$bComo las luces de un final del día$bRecupéralo y el amor volverá a mí$bUn saco de cebada, uno de centeno y otro de maíz.",
    ["O"] = "Tragoamargo Cebatruenos quiere un saco de cebada, un saco de centeno y un saco de maíz.",
    ["T"] = "Dulce ámbar",
  },
  [50] = {
    ["D"] = "Mezclaré y mezclaré y un puré será todo$bSerá, amigo, nuestro más querido tesoro$bHaré un alambique fuerte y resistente$bDame veraplata y no daré la lata.",
    ["O"] = "Tragoamargo Cebatruenos quiere veraplata.",
    ["T"] = "Dulce ámbar",
  },
  [51] = {
    ["D"] = "Recién destilada, aunque aún no esté filtrada$bComo tierra sin semilla esperando a ser arada$bAhora haré nuestra pila de carbón$bUna rama de sicomoro es tu próxima misión.",
    ["O"] = "Lleva una rama de sicomoro a Tragoamargo Cebatruenos.",
    ["T"] = "Dulce ámbar",
  },
  [52] = {
    ["D"] = "Saludos, $N. Los animales salvajes son más agresivos cuanto más nos alejamos de Villadorada. El Aserradero de la Vega del Este sufre constantes ataques de lobos y osos.$B$BNos vendría bien tu ayuda ahí fuera.",
    ["O"] = "Mata a 8 merodeadores y a 5 osos del bosque jóvenes y vuelve con el guardia Thomas al puente oriental de Elwynn.",
    ["T"] = "Proteger la frontera",
  },
  [53] = {
    ["D"] = "Es un líquido meloso que se vuelve delicioso$bDulce líquido ambarino que merece mi cariño$bPero algo necesito para darle mis cuidados$bUn magnífico tonel del mejor roble quemado.",
    ["O"] = "Lleva un fajo de roble quemado a Tragoamargo Cebatruenos.",
    ["T"] = "Dulce ámbar",
  },
  [54] = {
    ["D"] = "$N, has demostrado que te preocupa la seguridad de Villanorte. Tu siguiente misión será proteger el Bosque de Elwynn.$B$BSi aceptas, te daré unos papeles para el alguacil Dughan, que está en Villadorada. Para ir, sigue el camino hacia el sur, pasada la frontera.",
    ["O"] = "Lleva los documentos del alguacil McBride al alguacil Dughan a Villadorada.",
    ["T"] = "Informa en Villadorada",
  },
  [55] = {
    ["D"] = "Morbent Vil se esconde en una casa que cuelga de la cima de la montaña que hay al este, una casa que da al Cementerio del Cerro del Cuervo. Su tiempo en esta tierra está llegando a su fin...$B$BUtiliza la pesadilla de Morbent contra él. Inutilizará su magia protectiva.$B$BY mátalo. Mátalo y sálvanos de su maldad. ¡Sé el instrumento de mi venganza, y conviértete en $gel héroe:la heroína; del Bosque del Ocaso!",
    ["O"] = "Utiliza la Pesadilla de Morbent contra Morbent Vil, mata a Morbent Vil y luego ve al campamento de Sven y devuélvele la Pesadilla de Morbent.",
    ["T"] = "Morbent Vil",
  },
  [56] = {
    ["D"] = "Como Comandante de La Guardia Nocturna, es mi deber proteger a los ciudadanos de Villa Oscura. Ventormenta ha retirado a los guardias que estaban aquí destacados. Ahora tenemos que arreglárnoslas solos.$B$BSi crees estar a la altura de la tarea, nos vendría bien tu ayuda. Ayuda a La Guardia Nocturna aventurándote en el Jardín Sereno, al sur de Villa Oscura y matando a 8 guerreros esqueléticos y a 6 magos esqueléticos. ¡Hemos de acabar con la amenaza de los no-muertos!",
    ["O"] = "La comandante Althea Cerranegro de Villa Oscura quiere que mates a 8 guerreros esqueléticos y 6 magos esqueléticos.",
    ["T"] = "Vigilancia nocturna",
  },
  [57] = {
    ["D"] = "$N, me están llegando informes de una infestación de no-muertos en el bosque. Si estás a la altura, viaja al Cementerio del Cerro del Cuervo y mata a 15 de esos demonios esqueléticos y a 15 horrores esqueléticos. También me han llegado noticias de la presencia de esqueletos al sur de Villa Oscura, en el Cementerio del Jardín Sereno y en sus alrededores. ¡Hemos de expulsar a los no-muertos del Bosque del Ocaso!",
    ["O"] = "La comandante Althea Cerranegro de Villa Oscura quiere que mates a 15 mefistos esqueléticos y 15 horrores esqueléticos.",
    ["T"] = "Vigilancia nocturna",
  },
  [58] = {
    ["D"] = "La situación de Cerro del Cuervo sigue siendo desalentadora. La seguridad de Villa Oscura está en tus manos, $N. Yo no tengo suficientes guardias para mantener la villa a salvo. Necesitamos que ayudes a Cerro del Cuervo una vez más y que liberes el mausoleo oriental de 20 propagadores de la plaga.",
    ["O"] = "La comandante Althea Cerranegro de Villa Oscura quiere que mates a 20 propagadores de la Peste en el mausoleo oriental de Cerro del Cuervo.",
    ["T"] = "Vigilancia nocturna",
  },
  [59] = {
    ["D"] = "Tengo aquí una señal, un premio a tu habilidad y tu valor. Podrás cambiarla por una buena armadura.. Quiero que se la lleves a Sara Timberlain al Aserradero de la Vega del Este. Dale la señal y ella te forjará una armadura. Y cuando la recibas, $N, úsala para defender Elwynn.$B$BEl Aserradero de la Vega del Este está más allá del puesto de vigilancia del guardia Thomas, al este.",
    ["O"] = "Entrega a Sara Timberlain el marcador de armaduras de Ventormenta.",
    ["T"] = "Armadura de tela y cuero",
  },
  [60] = {
    ["D"] = "Hola, $gchico:chica;. ¿Tienes un momento?$B$BMi hermano y yo tenemos un herbolario en Ventormenta. Yo estoy aquí para conseguir velas grandes, por la cera, ya me entiendes. ¿Puedes ayudarme?$B$BPuedes conseguir velas grandes de los kobolds. He oído rumores de que están infestando las minas de Elwynn... La Mina Abisal del sur y la Cantera de Jaspe del este. Puedes empezar la búsqueda en esos lugares.",
    ["O"] = "Lleva 8 velas grandes a William Mortero a Villadorada.",
    ["T"] = "Las velas de los kobolds",
  },
  [61] = {
    ["D"] = "Mi hermano Morgan espera mi envío de velas en Ventormenta. No tengo tiempo para hacer el viaje en persona, pero si quisieras llevarle el envío, te pagaría bien.$B$BHe empaquetado las velas. Encontrarás a Morgan en nuestra tienda, Herbolario Mortero, en el Distrito de Mercaderes de Ventormenta.",
    ["O"] = "Lleva el envío de William a Morgan Mortero al Distrito de Mercaderes de Ventormenta.",
    ["T"] = "Un envío para Ventormenta",
  },
  [62] = {
    ["D"] = "¡La mina de Villanorte no es la única que tiene problemas! Según mis informes, la Mina Abisal de Elwynn también ha sido ocupada por los kobolds.$B$BExplora la mina y comprueba la veracidad de mis informes. Luego vuelve aquí. La mina está hacia el norte de Villadorada, entre La Granja Pedregosa y la granja Maclure.",
    ["O"] = "Explora la Mina Abisal y vuelve junto al alguacil Dughan a Villadorada.",
    ["T"] = "La Mina Abisal",
  },
  [63] = {
    ["D"] = "Por la costa del Bosque de Argénteos, al oeste de El Sepulcro, verás un altar infestado por la Peste. Los espíritus de ese lugar ya no comunican con nuestro pueblo y de ti depende acabar con esa manifestación de la Plaga y limpiar el estanque.$B$BLlévate los brazales de elemental y las gotas restantes de la poción de Brine y colócalos en el blandón. El ritual debería bastar para conjurar a un espíritu puro con el que comunicar.$B$BTráeme una prueba de haber completado la tarea y te recompensaré con un tótem.",
    ["O"] = "Derrota la manifestación de agua corrupta y coloca los brazales de manifestación corrupta con las gotas restantes del agua más pura en el Blandón de Siemprefuente en el Bosque de Argénteos.",
    ["T"] = "La llamada del agua",
  },
  [64] = {
    ["D"] = "¡Fue horrible! Verna me despertó cuando oyó jaleo en los campos. Estaban llenos de gamberros. Nos fuimos corriendo y olvidé recoger mi reloj de bolsillo. Me lo dio el padre de Verna el día de nuestra boda y es horrible saber que está en manos de esos ladrones. Dejé el reloj de bolsillo en el armario de la granja. Busca el campo de calabazas que está al Oeste... no tiene pérdida. ¡Si me lo devuelves te estaré muy agradecido!",
    ["O"] = "El granjero Furlbrow quiere que recuperes el reloj de bolsillo que dejó olvidado en el armario de su granja en la plantación de calabazas del Oeste.",
    ["T"] = "La reliquia olvidada",
  },
  [65] = {
    ["D"] = "La banda de desgraciados responsable de que las gentes de los Páramos de Poniente abandonaran sus tierras se llama a sí misma la Hermandad Defias. Necesito que te infiltres en este clan de matones. Debemos averiguar quién lidera el grupo y dónde se esconden. En Villa del Lago, en la posada, encontrarás a un pícaro que responde al nombre de Wiley; me debe un favor.$B$BViaja a Villa del Lago, en las Montañas Crestagrana, al este de Elwynn, y averigua todo lo que puedas.",
    ["O"] = "Gryan Mantorrecio quiere que hables con Wiley en Villa del Lago.",
    ["T"] = "La hermandad de los Defias",
  },
  [66] = {
    ["D"] = "La otra noche, un terrible tumulto me recorrió las venas. Sentí que mi nieta Alyssa estaba en grave peligro. Consulté las cartas y vi a la Muerte contemplándome desde la mesa.$B$BBusqué la luz en un largo y oscuro trance... intentaba encontrar algo concreto que me ayudara a calmar el espíritu. Y un nombre vino a mí. Stalvan.$B$BAcude al secretario del Concejo o e intenta averiguar algo más sobre ese nombre. Temo por todos nosotros.",
    ["O"] = "Busca al dependiente Daltry en el Concejo de Villa Oscura.",
    ["T"] = "La leyenda de Stalvan",
  },
  [67] = {
    ["D"] = "Stalvan, ¿eh? Deja que consulte el registro.$B$BStalvan... Stalvan... Veamos... ¡Ah! ¡Aquí está! Tengo un señor Stalvan Mantoniebla. Según consta aquí, su última dirección es la Escuela de Arroyo de la Luna. ¡Pero este dato es de hace mucho tiempo!$B$BHazme un favor, ¿quieres? Si pasas por Arroyo de la Luna, hazme saber si consigues información más reciente sobre este tipo. Me gusta llevar los registros al día.",
    ["O"] = "Viaja a la escuela de Arroyo de la Luna y lleva cualquier información que descubras sobre Stalvan al dependiente Daltry.",
    ["T"] = "La leyenda de Stalvan",
  },
  [68] = {
    ["D"] = "En el viejo arcón encuentras una carta polvorienta sin enviar.",
    ["O"] = "Lleva la carta polvorienta sin enviar al dependiente Daltry.",
    ["T"] = "La leyenda de Stalvan",
  },
  [69] = {
    ["D"] = "¡Ay, ay! Esto se me debió de pasar la primera vez. En el registro, justo debajo de la primera dirección de Stalvan, hay otra dirección. Está tachada... Parece que se dirigía a la Posada Orgullo de León, en Villadorada. Quizás quieras comprobarlo, $n.",
    ["O"] = "Ve a la Posada Orgullo de León a ver si alguien ha oído el nombre de Stalvan.",
    ["T"] = "La leyenda de Stalvan",
  },
  [70] = {
    ["D"] = "Stalvan... ese nombre me suena de algo. ¡Ya sé de qué!$B$BUna noche de tormenta de hace muchos años, llegó aquí un mensajero que buscaba refugio para la noche. Casi a medianoche, el hombre se precipitó escaleras abajo gritando, pálido de terror. Desapareció en la tormenta aún en pijama.$B$BCon las prisas, se olvidó sus cartas en el arcón de arriba. Y nunca volvió a buscarlas. Una de ellas es de ese tal Stalvan. Iba dirigida al distrito de Los Canales de Ventormenta. Puedes quedártela.",
    ["O"] = "Recupera la carta sin enviar de Stalvan del cofre y entrégala en el distrito de Los Canales de Ventormenta.",
    ["T"] = "La leyenda de Stalvan",
  },
  [71] = {
    ["D"] = "Ahora que tienes los dos medallones, llévaselos al guardia Thomas al puente. Así sabrá que sus guardias murieron asesinados.",
    ["O"] = "Entrega los medallones de Rolf y Malakai al guardia Thomas. Lo encontrarás en el puente oriental de Elwynn.",
    ["T"] = "Informa a Thomas",
  },
  [72] = {
    ["D"] = "Mi padre era el custodio de la propiedad mucho antes que yo. Tuvo que limpiar la sangre después de la masacre. Pero eso ahora no viene al caso.$B$BEl fondo de Flintridge se ha agotado. Ahora, las últimas pertenencias de la familia serán subastadas. Es culpa de esos buitres de los recaudadores de impuestos. Como veo que te mueres por saber más, creo que puedes mirar entre toda esta basura. Quién sabe lo que puedes encontrar.",
    ["O"] = "Busca una pista entre los objetos empaquetados.",
    ["T"] = "La leyenda de Stalvan",
  },
  [73] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<TXT> No Reward",
  },
  [74] = {
    ["D"] = "Dentro de la caja encuentras varios objetos: reliquias familiares mohosas, unos trofeos de caza, un retrato de familia y algunos libros antiguos. Al fondo, debajo de un jarrón, descubres una página arrancada de un diario.",
    ["O"] = "La página arrancada del diario que descubriste es tu siguiente pista.",
    ["T"] = "La leyenda de Stalvan",
  },
  [75] = {
    ["D"] = "Cuando me mudé aquí había un fajo de pergaminos en el cofre de arriba. Empecé a examinarlos, pero tuve que dejarlo cuando la niebla empezó a nublarme la vista.$B$BHazle un favor a un pobre anciano ciego. Sube y busca en el cofre cualquier cosa que te pueda ayudar a descubrir más sobre ese Stalvan. Sé que hay una página de un diario que, aunque está algo descolorida, podría interesarte mucho. Tráemela. Yo te ayudaré en lo que pueda.",
    ["O"] = "El alguacil Haggard quiere que busques la página del diario descolorida en el cofre que hay arriba.",
    ["T"] = "La leyenda de Stalvan",
  },
  [76] = {
    ["D"] = "Gracias a ti, ahora sabemos que la Mina Abisal está infestada de kobolds. Ahora necesitamos un rastreador que investigue la Cantera de Jaspe, que está más lejos.$B$BExplora la Cantera de Jaspe e infórmame si ves algún kóbold. Para llegar a la mina, viaja hacia el este por el camino hasta que llegues a la Torre de Azora. Cuando llegues allí, dirígete hacia el norte. Encontrarás la mina al pie de las colinas.",
    ["O"] = "Explora la Cantera de Jaspe y vuelve a informar al alguacil Dughan a Villadorada.",
    ["T"] = "La Cantera de Jaspe",
  },
  [77] = {
    ["D"] = "Tengo material de empaquetado original para el calimocho que quiere Dran Droffers. Te ayudaré a prepararlo para enviárselo a ese gruñón del padre de Malton... a cambio de dos botellas del mismo. Están en unas cajas en la base abandonada de la Horda cerca de Roca Oculta.$B$BSerá muy dificultoso; habrá babosas y mocos repugnantes por las ruinas. Dicen que su líder es una especie de jefe moco. Pero da igual; consigue el calimocho y vuelve aquí para empaquetarlo.",
    ["O"] = "Recoge 10 botellas de calimocho de miel de las Tierras del Interior de las cajas de la base abandonada de la Horda en Roca Oculta.",
    ["T"] = "Una situación peliaguda",
  },
  [78] = {
    ["D"] = "Sé de alguien que probablemente podrá ayudarte. Cuando yo comandaba la guardia de Ventormenta, iba con mis hombres a beber al Mesón del Cuervo Escarlata de Villa Oscura. El posadero, Smitts, era todo un experto en las tradiciones de la zona. Enséñale esta página y escucha lo que te pueda decir sobre ella.",
    ["O"] = "Lleva la página de diario descolorida al tabernero Smitts.",
    ["T"] = "La leyenda de Stalvan",
  },
  [79] = {
    ["D"] = "Seguí la leyenda del tal Stalvan durante años. Hace unos años, cuando asesinaron a aquellos nobles, yo fui con Haggard a investigar el asunto. Encontré estas páginas llenas de barro, pero nunca conseguimos relacionar la caligrafía con ese lunático. Tus pruebas demuestran su culpabilidad. Lleva esto a la comandante Ebonlocke de inmediato e infórmale de lo que has descubierto.",
    ["O"] = "Lleva la página embarrada a la comandante Cerranegro.",
    ["T"] = "La leyenda de Stalvan",
  },
  [80] = {
    ["D"] = "Estuve vigilando a ese asqueroso de Stalvan durante mucho tiempo. Si está página fue escrita por la misma mano... tenemos una prueba de su culpabilidad más allá de una duda razonable.$B$BEl secretario Daltry, a lo largo de sus años en el registro, se ha convertido en un experto calígrafo. Muéstrale esta página y pídele que compruebe si la caligrafía coincide con la de la firma del registro.",
    ["O"] = "Enseña la página del diario manchada de sangre al dependiente Daltry.",
    ["T"] = "La leyenda de Stalvan",
  },
  [81] = {
    ["D"] = "He puesto la caja de calimocho y las otras botellas que se lleva Dran en el barril para que no se muevan. Él no me cae muy bien, pero la verdad es que no hay nada peor que el calimocho agitado.$B$BLlévale el barril a su local en Orgrimmar. Y transmite mi afectuoso saludo a Malton; dile que si alguna vez tiene sed de bebida y de buena compañía, nos honraría mucho su visita.",
    ["O"] = "Entrega el barril a Dran Droffers en Orgrimmar.",
    ["T"] = "Entrega el calimocho",
  },
  [82] = {
    ["D"] = "A ver, en cuanto a estos informes sobre los bichos... lo que sabemos es que están drenando las pozas de agua que antes estaban donde se ubica La Guarida Ponzoñosa. Lo que NO sabemos es... bueno, todo lo demás.$B$B¡Descubramos en primer lugar a quién nos enfrentamos! Quiero que vayas a La Guarida Ponzoñosa y que obtengas algunos trozos de esos bichos. Consigue 5 trozos de centipaar y llévaselos al alquimista Morterozugg para que se encargue de ellos. Con su ayuda podremos descubrir qué son.",
    ["O"] = "Llévale 5 trozos de insectos centipaar de La Guarida Ponzoñosa al alquimista Morterozugg de Gadgetzan.",
    ["T"] = "Investigación en la Guarida Ponzoñosa",
  },
  [83] = {
    ["D"] = "La banda Defias de Villanorte lleva máscaras de tela de saco, pero los Defias de Elwynn las llevan de lino... Esas me vendrían bien para confeccionar prendas.$B$BSi me traes pañuelos de lino rojo, los usaré para hacerte algo bonito.$B$BLa banda Defias tiene campamentos por todo Elwynn.",
    ["O"] = "Lleva 6 vendas de lino rojas a Sara Timberlain al Aserradero de la Vega del Este.",
    ["T"] = "Pañuelos de lino rojo",
  },
  [84] = {
    ["D"] = "Aquí tienes. Y cuando le des el pastel a Billy, ¡dile que espero que se atragante con él!",
    ["O"] = "Lleva el pastel de tripa de cerdo a Billy Maclure a Los Viñedos de Maclure.",
    ["T"] = "Vuelve con Billy",
  },
  [85] = {
    ["D"] = "He perdido mi collar... creo que me lo robó ese granuja de Billy Maclure. Normalmente pasa el día escondido como una rata en Los Viñedos de Maclure, al este de aquí.$B$BRecupera mi collar y darás una alegría al corazón de una anciana viuda.",
    ["O"] = "Habla con Billy Maclure.",
    ["T"] = "El collar perdido",
  },
  [86] = {
    ["D"] = "Si me dieras un pastel, puede que te diga quién tiene el collar. Bernice, esa anciana de la otra granja, hace unos pasteles de tripa de cerdo increíbles...$B$BSi le llevas algo de carne de jabalí de los jabalíes que rondan por nuestras granjas, y le dices para qué es, seguro que te hace un pastel.",
    ["O"] = "Lleva 4 trozos de carne de jabalí a la tía Bernice Pedregosa a La Granja Pedregosa.",
    ["T"] = "Un pastel para Billy",
  },
  [87] = {
    ["D"] = "Estaba jugando cerca de la Mina Abisal y creo que se me cayó... esto... quiero decir, creo que vi el collar de la señora. No me preguntes cómo llegó allí, ¡porque no lo sé!$B$BPues eso, que vi a un enorme kóbold con dientes de oro que lo recogía y entraba corriendo a la mina. Si encuentras a ese kóbold, encontrarás el collar, ¡lo juro!",
    ["O"] = "Lleva el collar de Bernice a tía Bernice Pedregosa a La Granja Pedregosa.",
    ["T"] = "Dientes de oro",
  },
  [88] = {
    ["D"] = "Los Brackwell tienen una cerda que ganaría cualquier concurso. Se llama Princesa. Está ENORME, ¡y es gracias a mí! ¡Porque se alimenta de mis verduras!$B$B¡Princesa debe morir antes de que llegue a nuestros campos! Tráeme su collar como prueba de que está muerta y te daré algo para recompensarte.$B$BPrincesa suele estar en la Plantación de Calabazas de Brackwell. Está al este, más allá de la granja de los Maclure. ¡Ve! ¡Antes de que le entre hambre y vuelva!",
    ["O"] = "Mata a Princesa, coge su collar y llévaselo a la vieja Pedregosa a La Granja Pedregosa.",
    ["T"] = "¡Princesa debe morir!",
  },
  [89] = {
    ["D"] = "Durante la última invasión orca tuvimos que usar todo el hierro para fabricar balas, espadas y armaduras. Hicimos un pedido de materia prima a Ventormenta, pero unos gnolls Crestagrana secuestraron la caravana y se ocultaron en las colinas de detrás de Villa del Lago. $b$bY ahora estamos sin material para reconstruir el puente. Te recompensaré si me traes cinco picas y cinco roblones de hierro. $b$bVenga, ¡a trabajar!",
    ["O"] = "Lleva 5 picas y 5 roblones de hierro al supervisor Oslow a Villa del Lago.",
    ["T"] = "La reconstrucción del puente",
  },
  [90] = {
    ["D"] = "¡El kebab de lobo salpimentado es el especial de la casa! ¡Claro que te puedo preparar un poco! Pero necesitaré algunos productos. Tráeme 10 ijadas magras de lobo y algunas hierbas aromatizadas de Ventormenta. En el bosque hay muchísimos lobos; encontrar ijadas no será un problema. Para las hierbas, busca a Felicia Gump. Tiene una floristería en el distrito de Los Canales de Ventormenta.",
    ["O"] = "Reúne 10 ijadas magras de lobo y hierbas aromáticas de Ventormenta y vuelve a Villa Oscura, a ver al Chef Grual.",
    ["T"] = "Kebab de lobo salpimentado",
  },
  [91] = {
    ["D"] = "Se ha instalado un enemigo de Ventormenta en las laderas orientales de Crestagrana. Ese bellaco usa lo arcano para extender el terror entre nuestra gente. Se oculta bajo el nombre de Morganth. $b$bHa esclavizado al clan gnol Pellejo Negro. De acuerdo con las leyes de Villa del Lago, asistir a un enemigo del Reino está penado con la muerte. $b$b¡Esos miserables gnolls deben morir! $b$bTráeme diez colgantes Pellejo Negro y te recompensaré.",
    ["O"] = "Lleva 10 colgantes Pellejo Negro a Bailiff Conacher al Concejo de Villa del Lago.",
    ["T"] = "Ley de Salomón",
  },
  [92] = {
    ["D"] = "Me encantaría prepararte un estofado de Crestagrana. Pero tengo un problemita; durante los disturbios con los orcos alimenté a toda la guarnición de Ventormenta. No me queda nada en la despensa, pero si tú me traes los ingredientes, cocinaré para ti. $b$bTráeme cinco filetes de carne dura de cóndor, cinco morros de dentosangre grandes y cinco raciones de carne de araña crujiente.",
    ["O"] = "El chef Breanna de Villa del Lago quiere 5 piezas de carne dura de cóndor, 5 morros de dentosangre grandes y 5 raciones de carne de araña crujiente.",
    ["T"] = "Estofado de Crestagrana",
  },
  [93] = {
    ["D"] = "Te voy a contar un secreto: ¡las croquetas de \"cangrejo\", en realidad se hacen con patas de araña! Sé que es un poco asqueroso, pero las patas de araña son muy sabrosas y las croquetas quedan estupendas. Tráeme patas pegajosas de araña y yo te preparo algunas croquetas.$B$BHe oído que las arañas de hilo venenoso son las mejores para cocinar. Están al norte, entre las laderas de las colinas y el río.",
    ["O"] = "Reúne 6 patas pegajosas de araña y llévaselas al Chef Grual a Villa Oscura.",
    ["T"] = "Las croquetas de cangrejo",
  },
  [94] = {
    ["D"] = "Mi rival, Morganth, es un malvado mago de gran poder que vive en la Torre de Ilgalar, en Crestagrana. He creado un dispositivo que me permitirá espiar sus movimientos: el Ojo de Azora. Ahora puedo verlo desde lejos.$B$BPero no puedo saber lo que piensa... Necesito más ayuda.$B$BSi quieres ayudarme tú, lleva mi Glifo de Azora a la Torre de Ilgalar. Colócalo sobre la Estatua Leonida, cerca de la Torre.$B$BCuando lo hayas hecho, el poder de Azora me permitirá ver los planes de Morganth para el futuro.",
    ["O"] = "Lleva el Glifo de Azora a la Estatua Leonida, cerca de la Torre de Ilgalar, en Crestagrana.",
    ["T"] = "Un ojo vigilante",
  },
  [95] = {
    ["D"] = "Yo antes trabajaba en una granja al sureste... hasta que un día que tuve que ausentarme, los Jinetes Oscuros del Paso de la Muerte arrasaron mi granja y asesinaron a mi familia.$B$BCuando regresé vi una figura misteriosa cerca de mi granero... enterrando algo. Huyó antes de que pudiera llegar a él y tampoco quería entretenerme: quería ir tras los Jinetes Oscuros. Así que nunca descubrí qué se escondía allí.$B$BTe agradecería que encontraras lo que enterró aquella figura misteriosa. El lugar en el que debes buscar está detrás del tocón que hay cerca de mi granero.",
    ["O"] = "Ve a la granja de Sven y averigua qué había enterrado detrás del viejo tocón.",
    ["T"] = "La venganza de Sven",
  },
  [96] = {
    ["D"] = "Llevar fragmento. Llevar fragmento y dar vida.$B$BDar vida y entender. Entender que el agua es vida... la corrupción acaba con la vida.$B$BDas vida y la proteges con el poder del agua.",
    ["O"] = "Llévale el fragmento de agua a Islen Vident\'aqua en Los Baldíos.",
    ["T"] = "La llamada del agua",
  },
  [97] = {
    ["D"] = "¡Informa de inmediato a la comandante Ebonlocke de que las caligrafías coinciden, $N! ¡Sus sospechas han resultado ser ciertas!",
    ["O"] = "Informa a la comandante Cerranegro.",
    ["T"] = "La leyenda de Stalvan",
  },
  [98] = {
    ["D"] = "Stalvan Mantoniebla llevó una vida de depravación. Su mano sesgó innumerables vidas inocentes. Es, sin duda alguna, culpable de incontables crímenes. Ahora ese lunático amenaza Villa Oscura. Solo la Luz sabe qué sórdidas acciones está planeando. Debes ejecutar a Stalvan, $c. Lo encontrarás en su casa, al norte de la ciudad.$B$BCuando lo hayas hecho, busca a Lady Eva y enséñale este anillo familiar. Después de todo, fue su premonición lo que nos guió a este terrible descubrimiento. Gracias a ella, Villa Oscura ahora es más segura.",
    ["O"] = "Mata a Stalvan Mantoniebla y muestra a Lady Eva su anillo familiar.",
    ["T"] = "La leyenda de Stalvan",
  },
  [99] = {
    ["D"] = "Según aumenta mi conocimiento sobre la magia de Arugal, aumenta también mi desdén por el desafortunado necio. Estoy a punto de completar la investigación de su Remedio... Bonito nombre...$B$BMi conocimiento será completo cuando descubra qué encantamiento está causando el extraño comportamiento en la Aldea Piroleña. Por el día, los aldeanos parecen ser humanos. Pero, cuando el sol desaparece, se convierten en ferocanis Furia Lunar.$B$BNecesito extraer energía de los grilletes encantados que Arugal utilizó con ellos. Tráeme seis grilletes Piroleños encantados, $N.",
    ["O"] = "Lleva 6 grilletes piroleños a Dalar Tejalba en El Sepulcro.",
    ["T"] = "La locura de Arugal",
  },
  [100] = {
    ["D"] = "Hay un destello de luz cuando pronuncias las últimas palabras del conjuro. Se desvanece el sonido de las gotas de agua sobre la llama y la emoción embarga tus sentidos conforme se destruyen los espíritus corruptos que te rodean.",
    ["O"] = "Habla con la manifestación de agua menor en el Bosque de Argénteos.",
    ["T"] = "La llamada del agua",
  },
  [101] = {
    ["D"] = "A medida que la corrupción mística invade el bosque, la necesidad de autoprotección se hace innegable, $n. El viento me susurra y me cuenta de grandes peligros que el futuro te depara.$B$BSi deseas protegerte, noble $c, tráeme 10 colmillos de necrófago, 10 dedos de esqueleto y 5 viales de veneno arácnido. Te encantaré un tótem de castigo que dañará a aquellos que intenten causarte daño a ti.",
    ["O"] = "Consigue 10 colmillos de necrófago, 10 dedos de esqueleto y 5 viales de veneno arácnido para Madame Eva en Villa Oscura.",
    ["T"] = "El Tótem de castigo",
  },
  [102] = {
    ["D"] = "Ventormenta nos ha abandonado. Nauseabundos aires de depravación soplan por las llanuras de los Páramos de Poniente. Este era mi hogar y no daré la espalda a los ciudadanos que decidieron permanecer aquí. Nosotros, los antiguos granjeros, ofreceremos resistencia.$B$BTu tarea, si decidieras aceptar, consistirá en patrullar las praderas de los Páramos de Poniente. Localiza y aniquila a los infames gnolls que parecen colaborar con los ladrones de Las Minas de la Muerte. Tráeme 8 zarpas de gnoll y sabré recompensar tu valentía.",
    ["O"] = "Lleva 8 zarpas de gnoll al capitán Danuvin en la Colina del Centinela.",
    ["T"] = "En patrulla por Páramos de Poniente",
  },
  [103] = {
    ["D"] = "La noche en que murió la familia del guardián del Faro fue horrible. Contemplé, impotente, como el Viejo Ojosombrío lideraba el ataque. Pero lo pasado pasado está y lo que ahora me preocupa son las vidas de los que navegan por el Mare Magnum y se acercan demasiado a las peligrosas rocas de la costa. Como no hay nadie que vigile la llama, la responsabilidad es ahora mía.$b$bAyúdame a mantener la antorcha iluminada trayéndome 5 botellas de aceite de los monstruos de la cosecha.",
    ["O"] = "Lleva 5 botellas de aceite al capitán Grisillo en el Faro de los Páramos de Poniente.",
    ["T"] = "Guardián de la Llama",
  },
  [104] = {
    ["D"] = "Cuando acabó mi vida contra las rocas, no tenía ni idea de lo que me esperaba en el más allá. El Faro estaba sumido en la oscuridad aquella noche, ya que el Viejo Ojosombrío había asustado a la familia del guarda. Lograron regresar y volver a encender la llama, pero el Viejo Ojosombrío coaccionó a los múrlocs de mente más débil para que asaltaran el Faro con él de nuevo. La segunda vez, la familia no corrió la misma suerte y pereció inútilmente ante mis ojos.$b$bMata al Viejo Ojosombrío si lo ves por la costa y tráeme una de sus escamas, te recompensaré.",
    ["O"] = "Lleva una escama del Viejo Ojosombrío al capitán Grisillo en el Faro de los Páramos de Poniente.",
    ["T"] = "La amenaza costera",
  },
  [105] = {
    ["D"] = "¡Ay, se acerca el tiempo de atacar Andorhal y de hacer salir al exánime que lo controla!$B$BEs en el interior de las ruinas de la ciudad donde el exánime, Araj el Invocador, tiene sus dominios. Está custodiado por numerosos miembros de la Plaga que rodean los restos del centro de la ciudad, y su séquito personal de guardianes es realmente formidable. Necesitarás muchos aliados para tener alguna oportunidad de enfrentarte a él con éxito.$B$B¡Destruye a Araj, $N, y tráeme un Fragmento de su Filacteria como prueba!",
    ["O"] = "Lleva el Fragmento de Filacteria de Araj al sumo ejecutor Derrington en El Baluarte, en las Tierras de la Peste del Oeste.",
    ["T"] = "Ah, Andorhal",
  },
  [106] = {
    ["D"] = "¡Oh, estoy maldita! He entregado mi corazón a Tommy Joe Stonefield, pero nuestras familias se odian a muerte. Así que no puedo verlo, ¡aunque me duelan los ojos por no poder mirar su hermoso rostro!$B$BTe lo ruego, lleva esta carta a Tommy Joe. Suele estar en el río que está al oeste de La Granja Pedregosa, al oeste de aquí.",
    ["O"] = "Entrega a Tommy Joe Pedregosa la carta de amor de Maybell.",
    ["T"] = "Jóvenes enamorados",
  },
  [107] = {
    ["D"] = "¡Seguro que William Mortero tiene una poción que reúna a los dos jóvenes amantes!$B$BTen, llévale esta nota a William. Está en la Posada Orgullo de León, en Villadorada.",
    ["O"] = "Lleva la nota de la Abuela Pedregosa a William Mortero.",
    ["T"] = "Una nota para William",
  },
  [108] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<TXT> Mystery Reward",
  },
  [109] = {
    ["D"] = "Parece que has visto bastantes combates en tu vida, $c. Si no has tenido suficiente, busca a Gryan Mantorrecio. Comanda las Milicias del Pueblo que protegen las granjas de los Páramos de Poniente. Seguro que le vendrá bien tu ayuda. Puedes encontrarlo en la torre de piedra de la Colina del Centinela, saliendo de la carretera que cruza los Páramos de Poniente.",
    ["O"] = "Habla con Gryan Mantorrecio. Puedes encontrarlo en la torre de piedra de la Colina del Centinela, saliendo de la carretera que cruza los Páramos de Poniente.",
    ["T"] = "Habla con Gryan Mantorrecio",
  },
  [110] = {
    ["D"] = "Voy a estudiar el equipo de análisis de Patagolpe y a ordenar sus efectos personales. Tengo la extraña sensación de que esta no es la última pérdida que sufriremos ante estos bichos.$B$BQuiero que vayas a ver al alquimista Morterozugg y compruebes si ya ha tenido oportunidad de procesar los trozos de insecto que le enviamos. Si es así, vuelve aquí.",
    ["O"] = "Busca al alquimista Morterozugg en Gadgetzan y comprueba su análisis de los trozos de insecto.",
    ["T"] = "Análisis de trozos de insecto",
  },
  [111] = {
    ["D"] = "Por favor, $N, habla con mi abuelita. Si alguien puede encontrar la forma de reunirme con Maybell, esa es ella.$B$BEstá en nuestra casa, al este de aquí.",
    ["O"] = "Habla con la Abuela Pedregosa.",
    ["T"] = "Habla con la abuelita",
  },
  [112] = {
    ["D"] = "Puedo hacer un licor de invisibilidad para Maybell, para que pueda salir de Los Viñedos de Maclure y reunirse con Tommy Joe. Pero para hacer el licor necesito kelp cristalino.$B$BAunque el kelp normalmente crece en el océano, también se puede conseguir de los murlocs. Ve a ver si los murlocs que hay cerca del Lago de Cristal tienen algo. El Lago de Cristal está al este de Villadorada.",
    ["O"] = "Lleva 4 hojas de kelp cristalino William Mortero a Villadorada.",
    ["T"] = "La recolección de Kelp",
  },
  [113] = {
    ["D"] = "El perito jefe Picoflojo tiene que ver este informe inmediatamente. En realidad... todo el que sepa leer debería echarle un vistazo. Nos enfrentamos a una grave y seria amenaza no solo para Tanaris y Gadgetzan, sino también para toda la zona meridional de Kalimdor si no le ponemos solución pronto. ¡Qué diablos! Incluso es posible que afecte a la totalidad de Azeroth pues no hay ningún indicio de que vaya a mejorar la situación.$B$BPídele a Picoflojo una copia del informe para poder distribuirlo. ¡Todos deben conocer la existencia de esta amenaza!",
    ["O"] = "Entrégale el informe al perito jefe Picoflojo de Gadgetzan. Asegúrate de que te da una copia del informe, tal y como el alquimista Morterozugg ha pedido.",
    ["T"] = "Análisis de trozos de insecto",
  },
  [114] = {
    ["D"] = "Lleva este licor de invisibilidad a la joven Maybell. Debería durar lo suficiente para que haga una visita a Tommy Joe.",
    ["O"] = "Lleva el licor de invisibilidad a Maybell Maclure.",
    ["T"] = "La fuga",
  },
  [115] = {
    ["D"] = "Los Roca Negra han traído nigromantes a los ataques en Crestagrana. Tienen unos objetos de poder oscuro, los orbes de medianoche. Estos orbes han malherido a muchos de los que luchan en Crestagrana; tenemos que hacernos con esos objetos demoníacos. $b$bTráeme los orbes de medianoche de los cadáveres de nigromantes Roca Negra. Me encargaré de destruirlos; el mundo estará mejor sin ellos.",
    ["O"] = "Lleva 3 orbes de medianoche al alguacil Marris a Villa del Lago.",
    ["T"] = "Magia de las Sombras",
  },
  [116] = {
    ["D"] = "Tengo un problema, $N. Nos estamos quedando sin bebida y el envío se está retrasando mucho. Esta invasión orca ha sido terrible. $b$b¿Buscas trabajo? $b$bNecesito que vayas a recoger un barril de cerveza Cebatruenos de Tragoamargo Cebatruenos, que está en las colinas de Páramos de Poniente, uno de Merlot en Ventormenta, una botella de licor de luna en Villa Oscura y una de ron dulce en Villadorada. Tráeme todo eso y te ganarás una recompensa.",
    ["O"] = "Lleva 1 barril de cerveza Cebatruenos, 1 barril de vino dulce, 1 botella de licor de luna y 1 botella de ron dulce al posadero Daniels a Villa del Lago.",
    ["T"] = "Tiempos de sequía",
  },
  [117] = {
    ["D"] = "Rápido, $gamigo:amiga; y date prisa$bNuestra cerveza está de risa$bMás cerveza y menos comida,$bDadnos lúpulo para hacer birra.",
    ["O"] = "Entrega a Tragoamargo Cebatruenos 5 lúpulos para que pueda terminar su cerveza especial.",
    ["T"] = "Cebatruenos",
  },
  [118] = {
    ["D"] = "¡He necesitado tanto hierro para abastecer a la guardia de Ventormenta que no tengo bastante para hacer herraduras! $b$bVe a Villadorada y dale esta nota al herrero Argus; le cuento mi problema y le hago un pedido de herraduras.",
    ["O"] = "Lleva la nota de Verner al herrero Argus a Villadorada.",
    ["T"] = "El precio de unas herraduras",
  },
  [119] = {
    ["D"] = "Muy bien, 50 herraduras... $b$b¡Aquí las tienes, listas para llevar! Dile a Verner que ha sido un placer y que lea la nota que he pegado a la caja.",
    ["O"] = "Vuelve a Crestagrana y entrega la caja de herraduras a Verner Osgood.",
    ["T"] = "Vuelve con Verner",
  },
  [120] = {
    ["D"] = "Estamos en tiempos difíciles, $c. Vivimos bajo un asedio constante. Si no llegan refuerzos, solo nos queda la derrota. El mensaje que te confío es de la mayor importancia. Llévaselo al general Marcus Jonathan de Ventormenta inmediatamente. Después vuelve, ya sean tus noticias buenas o malas. $b$b¡Apresúrate!",
    ["O"] = "El magistrado Solomon te ha dado un informe para el general Marcus de Ventormenta. Quiere que vuelvas en cuanto lo hayas entregado.",
    ["T"] = "Mensajero a Páramos de Poniente",
  },
  [121] = {
    ["D"] = "Mientras espero la respuesta del Rey, llévale esta carta al magistrado Solomon. $b$bPuedes retirarte, $c.",
    ["O"] = "Lleva la respuesta del general Marcus Jonathan al magistrado Solomon a Villa del Lago.",
    ["T"] = "Mensajero a Páramos de Poniente",
  },
  [122] = {
    ["D"] = "Necesito escamas de barriga de cachorro de dragón negro para pagarle las herraduras a Argus. Si me traes 6 escamas, tendré para pagarle y también para hacerte un regalo. $b$bEsos bichos vuelan al sur de Villa del Lago, pero a menudo se alejan. Tendrás que estar ojo avizor para cazarlos en cuanto los veas.",
    ["O"] = "Lleva 6 escamas de barriga de cachorro de dragón negro a Verner Osgood a Crestagrana.",
    ["T"] = "Escamas de barriga",
  },
  [123] = {
    ["D"] = "Esta nota es una lista de los días y las horas a las que una persona –descrita como \"el Coleccionista\"– recibirá cargamentos de oro provenientes de las minas del Bosque de Elwynn.$B$BPor lo que pone en la nota, parece que el Coleccionista vive cerca de la Plantación de Calabazas de Brackwell, al este de Elwynn.$B$BParece importante. Deberías informar al alguacil Dughan de Villadorada.",
    ["O"] = "Busca al alguacil Dughan en Villadorada y dale la lista de coleccionista.",
    ["T"] = "El Coleccionista",
  },
  [124] = {
    ["D"] = "¡Como si no tuviera bastante con los orcos! Ahora tengo brutos y místicos gnolls en la cresta que hay al norte de mi establo y se llevan mis caballos cuando se pierden. $b$bSi nos libras de los gnolls, mis caballos y yo te lo agradeceremos eternamente.",
    ["O"] = "Mata a 10 brutos y 8 místicos Crestagrana y ve a ver a Verner Osgood.",
    ["T"] = "El aullido de los gnolls",
  },
  [125] = {
    ["D"] = "Échame una mano, $N. Con todo esto del asedio, es difícil conseguir suministros. Mis herramientas venían de camino de Villadorada, pero hicieron explotar el puente. Pusimos las herramientas en una barca, pero los orcos atacaron con una catapulta y todo acabó en el fondo del lago. $b$bRecupera mi caja de herramientas; te ganarás una buena recompensa.",
    ["O"] = "El supervisor Oslow de Villa del Lago quiere que recojas su caja de herramientas del fondo del Lago Sempiterno.",
    ["T"] = "Las herramientas perdidas",
  },
  [126] = {
    ["D"] = "Los gnolls siguen ahí. Los oigo aullar mientras cazan en las colinas de Villa del Lago. El que más fuerte aúlla es Yowler, el jefe. $b$b¡Mátalo! ¡Su manada se asustará y huirá! Tráeme su zarpa como prueba. $b$bY ten cuidado; si es el jefe, será por algo. Ya sabes lo que dicen de los perros viejos.",
    ["O"] = "Lleva la zarpa de Ululante a Verner Osgood a Villa del Lago.",
    ["T"] = "El aullido de las colinas",
  },
  [127] = {
    ["D"] = "El Lago Sempiterno es famoso por su pez luna moteado. Tiene mucha demanda, ¡pero me estoy quedando sin existencias! $b$bTráeme 10 peces luna moteados; te pagaré bien. Y si no sabes pescar, intenta robárselo a los murlocs.",
    ["O"] = "Lleva 10 peces luna moteados al maestro de embarcadero Baren a Villa del Lago.",
    ["T"] = "Pescado a la venta",
  },
  [128] = {
    ["D"] = "Tenemos que acabar con esos Roca Negra. El enemigo ha invadido nuestra tierra. Estas montañas pertenecen al rey de Ventormenta. Nuestras fuentes nos dicen que el jefe de cada unidad del clan se llama campeón. Encontrarás su campamento hacia el norte, justo al oeste del camino a Las Estepas Ardientes. $b$bEl magistrado Solomon quiere que mueran. Acaba con 15 campeones Roca Negra y vuelve a verme.",
    ["O"] = "Mata a 15 campeones Roca Negra y el guardia Howe te recompensará en Villa del Lago.",
    ["T"] = "La recompensa de Roca Negra",
  },
  [129] = {
    ["D"] = "¿Puedes hacerme un favor? Le he preparado la comida al guardia Parker, pero ha salido de patrulla. Él puede salir sin miedo, pero para mí es demasiado peligroso. $b$b¡Si le llevas la comida, cuando vuelvas te invitaré a comer! ¿Qué me dices? Está de patrulla en el camino a Bosque del Ocaso.",
    ["O"] = "Llévale su comida al guardia Parker.  Está de patrulla en el camino a Villa Oscura.",
    ["T"] = "Una comida gratis",
  },
  [130] = {
    ["D"] = "¿Te importa entregarle unas flores a Darcy de mi parte? Cómpralas en el herbolario de Villa del Lago, en el extremo oeste de la ciudad. $b$bPero no vayas a decir en la tienda para quién son o quién las envía...",
    ["O"] = "Habla con Martie Jainrose, la herborista de Crestagrana.",
    ["T"] = "Una visita al herborista",
  },
  [131] = {
    ["D"] = "Aquí tienes el ramo. Lo he hecho de narcisos, mis flores favoritas.",
    ["O"] = "Dale el ramo de narcisos a Darcy.",
    ["T"] = "Un ramo de narcisos",
  },
  [132] = {
    ["D"] = "Lo que voy a contarte puede costarme la vida. La banda Defias está preparando algo grande. Lo último que oí fue que estaban colaborando con varios gnolls, kobolds e incluso goblins.$b$bLlévale esta nota a Mantorrecio. Explica todo lo que sé sobre este asunto.",
    ["O"] = "Lleva la Nota de Wiley a Gryan Mantorrecio en los Páramos de Poniente.",
    ["T"] = "La hermandad de los Defias",
  },
  [133] = {
    ["D"] = "Vivo tan lejos de la protección de la ciudad que es un milagro que los necrófagos y los muertos andantes no me hayan devorado. ¡De hecho, ayer, sin ir más lejos, una manada de masticahuesos intentó tirar abajo las paredes de mi casa!$B$BQuiero hacer un monigote... una especie de espantapájaros para necrófagos. Y para hacerlo necesito costillas de necrófago. Puedes conseguirlos de comecarnes, comehuesos, comecerebros, podridos y propagadores de la plaga.$B$BConsígueme esas costillas y yo te pagaré.",
    ["O"] = "Reúne 7 costillas de necrófago y llévaselas a Abercrombie a su cabaña.",
    ["T"] = "Un espantapájaros para necrófagos",
  },
  [134] = {
    ["D"] = "Hace unas pocas semanas, mientras recogía hierbas lejos de mi casa, me atacó una manada de ogros. Huí a toda prisa y tuve que dejar atrás una caja llena de valiosas herramientas y hierbas.$B$BLos ogros volvieron a su túmulo del sur del Bosque del Ocaso pavoneándose de su hazaña. Tengo la seguridad de que mi caja está cerca de ese túmulo.$B$BPor favor, $N, recupérame esa caja, la necesito.",
    ["O"] = "Devuelve a Abercrombie la caja de Abercrombie.",
    ["T"] = "Ladrones ogros",
  },
  [135] = {
    ["D"] = "Me pregunto qué querría decir Wiley cuando habló de los Albañiles. Quizás fue un lapsus. ¿Podría estar relacionada la banda Defias con los Albañiles? El único que podría saberlo es Mathias Shaw, líder del IS:7. Enséñale la nota de Wiley, tal vez tenga algo que añadir a este misterio. Si tienes problemas para encontrarlo, busca en el Cuartel, en el Casco Antiguo.",
    ["O"] = "Lleva la Nota de Wiley a Mathias Shaw de Ventormenta.",
    ["T"] = "La hermandad de los Defias",
  },
  [136] = {
    ["D"] = "Si estás leyendo esto, el capitán Sanders estará sepultado bajo el agua. Así que mi tesoro es tuyo, solo tienes que seguir las pistas.$b$bPrimero tienes que encontrar mi baúl. Seguramente estará enterrado en la arena, en la costa Oeste de los Páramos de Poniente, cerca de los restos de naufragios. Habrá muchos restos, pero solo un ancla oxidada en la costa. ¡Encuentra el ancla y encontrarás mi baúl! Allí encontrarás también la siguiente pista.",
    ["O"] = "Encuentra el baúl del capitán Sanders y busca la siguiente pista.",
    ["T"] = "El tesoro escondido del capitán Sanders",
  },
  [137] = {
    ["D"] = "If you earn a commendation from one of our Marshals, then bring it to me.  It will be good for one of our Lion Emblem gloves.",
    ["O"] = "General Marcus Jonathan in Stormwind will accept a commendation from Elwynn Forest.  Bringing him such a document will reward you with a choice of armor.",
    ["T"] = "<NYI> <TXT> Stormwind Commendation",
  },
  [138] = {
    ["D"] = "Esto es lo que dice la siguiente pista: ¡Buen trabajo, compañero! Ahora tienes que ir al Este. Al Este hacia los riscos, al Este hacia el camino. Busca las ruinas de chimeneas a orillas del camino. Allí encontrarás un viejo barril con la siguiente pista.",
    ["O"] = "Busca el viejo barril junto a la chimenea en ruinas y busca la siguiente pista.",
    ["T"] = "El tesoro escondido del capitán Sanders",
  },
  [139] = {
    ["D"] = "Buscando en el barril descubres otro fragmento de pergamino. En este se puede leer: Desde este barril, ve hacia el Norte. Tan recto como vuela el cuervo, anda hasta que veas una jarra vacía junto al solitario molino de viento sobre los riscos del mar. Si buscas alrededor de la jarra encontrarás lo que estás buscando.",
    ["O"] = "Busca la jarra vacía junto al molino de viento para encontrar la siguiente pista.",
    ["T"] = "El tesoro escondido del capitán Sanders",
  },
  [140] = {
    ["D"] = "Ciertamente, dentro de la Vieja jarra hay otra pista sobre el tesoro de Sander. La tinta se ha borrado en algunas partes y el papel huele a whisky, pero aún puedes entender algo: Ahora que has encontrado mi vieja jarra de whisky ¡casi has llegado al tesoro! Dirígete hacia el Oeste desde la botella y baja hasta la costa. Cuando llegues al agua, ¡sigue andando! ¡Nada directo hacia el Oeste hasta encontrar la isla con el cofre del tesoro!",
    ["O"] = "Localiza el cofre del capitán Sanders y ábrelo para obtener tu recompensa.",
    ["T"] = "El tesoro escondido del capitán Sanders",
  },
  [141] = {
    ["D"] = "La Hermandad de los Albañiles estaba dirigida por un tal Edwin VanCleef. VanCleef fue responsable de la reconstrucción de Ventormenta cuando la ciudad fue arrasada por los orcos en la Primera Guerra. Al parecer, VanCleef y sus hombres no estaban satisfechos con el tratamiento que les brindó el rey cuando terminó la reconstrucción. Pero eso solo explicaría algunas cosas.$B$BHe redactado un informe más detallado para tu maestro en los Páramos de Poniente. ¡Llévaselo de una vez!",
    ["O"] = "Lleva el informe de Shaw a Gryan Mantorrecio de los Páramos de Poniente.",
    ["T"] = "La hermandad de los Defias",
  },
  [142] = {
    ["D"] = "Tenemos que descubrir la localización de la Ladronera Defias. $N, según los informes de mi rastreador, se ha visto a un mensajero Defias en los caminos entre el Arroyo de la Luna, la Mina de la Costa de Oro y la Mina de Jango. Quiero que le captures. Si ofrece resistencia, mátalo y tráeme lo que esté llevando.",
    ["O"] = "Localiza al mensajero Defias de los Páramos de Poniente y lleva su mensaje a Mantorrecio.",
    ["T"] = "La hermandad de los Defias",
  },
  [143] = {
    ["D"] = "En el ejército de Ventormenta pasa algo raro; ya deberían haber llegado. No tenemos tiempo que perder. No voy a quedarme a mirar cómo se sacrifica la gente de Villa del Lago sin intentar conseguir ayuda. He oído que en Páramos de Poniente se ha formado una Milicia del Pueblo. $b$bLleva nuestra súplica a su jefe, Gryan Mantorrecio; puede que sus hombres nos ayuden hasta que llegue la Reserva.",
    ["O"] = "El magistrado Solomon quiere que lleves su petición a Gryan Mantorrecio a Páramos de Poniente.",
    ["T"] = "Mensajero a Páramos de Poniente",
  },
  [144] = {
    ["D"] = "Me duele leer la nota del magistrado Solomon. Evidentemente, no sabe que hay guerra en Páramos de Poniente, si no, no pediría ayuda a la Milicia. Si Ventormenta no nos hubiera abandonado también a nosotros, no necesitaríamos una milicia. $b$bLlévale la respuesta a tu señor en Crestagrana, $c. Dile que lamento la pérdida de tantos hombres buenos.",
    ["O"] = "Lleva la respuesta de Gryan Mantorrecio al magistrado Solomon.",
    ["T"] = "Mensajero a Villa Oscura",
  },
  [145] = {
    ["D"] = "Necesito tu ayuda una vez más. He escrito a Lord Ebonlocke para pedir que mande a su Guardia Nocturna como refuerzo de la defensa de Villa del Lago. Es imprescindible que reciba este mensaje cuanto antes, pero debes ser extremadamente prudente. Ebonlocke es el alcalde de Villa Oscura, en pleno corazón del Bosque del Ocaso. No te salga de los caminos.",
    ["O"] = "El magistrado Solomon quiere que lleves una carta a Lord Ebonlocke a Villa Oscura.",
    ["T"] = "Mensajero a Villa Oscura",
  },
  [146] = {
    ["D"] = "Si no conociera a Solomon, diría que se ha vuelto loco. ¿Que envíe a mi Guardia Nocturna a Villa del Lago porque Ventormenta no envía ayuda? Formé la guardia porque Ventormenta nos abandonó cuando nos infestó esta magia inmunda. Si envío a la guardia, esta ciudad sería invadida antes de que mis guardias se perdieran en el horizonte. $b$bLLévale la respuesta a tu señor en Villa del Lago.",
    ["O"] = "Lleva la respuesta de Lord Ebonlocke al magistrado Solomon.",
    ["T"] = "Mensajero a Villa Oscura",
  },
  [147] = {
    ["D"] = "Si el Coleccionista se está llevando oro de nuestras minas, ¡está robando al Rey! Lleva al Coleccionista ante la justicia. Y tráeme a mí el anillo que se mencionaba en la nota. Podría indicarnos para quién trabaja el Coleccionista...$B$BLa nota dice que el Coleccionista se esconde en la Plantación de Calabazas de Brackwell. Deberías buscarlo allí.\"",
    ["O"] = "Encuentra y mata al Coleccionista y vuelve junto al alguacil Dughan con la sortija de coleccionista.",
    ["T"] = "La persecución",
  },
  [148] = {
    ["D"] = "Ay, ¿qué puede hacer un anciano como yo? Necesito suministros de Villa Oscura, pero está tan lejos y yo soy tan viejo y tan débil... No sobreviviría al viaje.$B$B¿Podrías ir a Villa Oscura y traerme un poco de hilo de cabello de fantasma? Puedes conseguirlo de Lady Eva. Ve a su casa de Villa Oscura.",
    ["O"] = "Habla con Lady Eva.",
    ["T"] = "Suministros de Villa Oscura",
  },
  [149] = {
    ["D"] = "Mary la ciega es un desgraciado y triste espíritu que mora en una vieja granja en las colinas del sur de Villa Oscura.$B$BTen, coge este peine espectral y pide a la ciega Mary que se peine el cabello. Luego tráeme el peine y yo extraeré de él el cabello de fantasma que necesito para crear el hilo.$B$BY ten cuidado... cerca de la casa acechan seres oscuros...",
    ["O"] = "Lleva el peine espectral a Mary la Ciega.",
    ["T"] = "Hilo de pelo de fantasma",
  },
  [150] = {
    ["D"] = "Los murlocs nos están complicando la vida. Se están instalando en las orillas del Lago Sempiterno y pescan en cantidades increíbles. ¡Tenemos que echarlos o acabarán con todos los peces del lago! $b$b¿Te apetece cazar murlocs? Tráeme 8 ancas y te llevarás algo a cambio...",
    ["O"] = "Lleva 8 ancas múrloc al maestro de embarcadero Baren a Villa del Lago.",
    ["T"] = "Cazadores furtivos",
  },
  [151] = {
    ["D"] = "¡Pobre Vieja Blanchy! La bestia está agotada después de todo el trabajo que le dimos. Le di de comer antes de dejar la granja, pero no esperábamos que se rompiera el carro. Si pudieras traerle algunos puñados de avena te estaría muy agradecida.$B$BSeguro que podrás encontrar algunos por las granjas de los Páramos de Poniente, si puedes librarte de esas horribles máquinas que los han invadido. Hay varias granjas al suroeste de aquí.",
    ["O"] = "Verna Furlbrow de los Páramos de Poniente quiere que le lleves 8 puñados de avena.",
    ["T"] = "La pobre y vieja Blanchy",
  },
  [152] = {
    ["D"] = "Seguramente habrás visto todos los restos de naufragios a lo largo de la costa. El Mare Magnum es traicionero. La costa de los Páramos de Poniente tiene que estar limpia, de ese modo si algún marinero llega hasta nuestras costas, no correrá ningún peligro. Pero los murlocs son un gran problema.$b$bMata 7 cazamareas, 7 guerreros, 7 oráculos y 7 correcostas y procuraré que seas $grecompensado:recompensada;.",
    ["O"] = "Mata 7 cazamareas, 7 guerreros, 7 oráculos y 7 correcostas y vuelve con el capitán Grisillo en el Faro de los Páramos de Poniente.",
    ["T"] = "Hay moros en la costa",
  },
  [153] = {
    ["D"] = "La tapadera de los Defias cambia constantemente. He estado siguiendo sus movimientos desde hace algún tiempo. Aunque, recuerda, he averiguado que muchos miembros de la banda se identifican por sus pañuelos de cuero rojo.$b$bTráeme 15 de esos pañuelos y procuraré que seas $grecompensado:recompensada;.",
    ["O"] = "Lleva 15 pañuelos de cuero rojo al explorador Galiaan en la Colina del Centinela.",
    ["T"] = "Pañuelos rojos de cuero",
  },
  [154] = {
    ["D"] = "¡¡NO!!$B$B¡Aleja ese peine de mí!$B$B¡Soy un monstruo! ¡Nadie volverá a pensar que soy guapa!",
    ["O"] = "Lleva el peine de fantasma a Lady Eva a Villa Oscura.",
    ["T"] = "Devuelve el peine",
  },
  [155] = {
    ["D"] = "¿Así que Mantorrecio envía a un $r enclenque para protegerme? Supongo que tendrás que hacerlo. Pero será mejor que traigas algunos amigos.$b$b¿Ya conoces el trato, verdad? Tú me guardas las espaldas y yo te llevo hasta la Ladronera Defias. La banda Defias busca mi cabeza, así que si me ven contigo, intentarán matarme.$b$bAvísame cuando tú y los amigos que puedas reunir estéis listos para emprender la marcha.",
    ["O"] = "Escolta al Traidor de los Defias a la ladronera secreta de la Hermandad Defias. Cuando el Traidor de los Defias te enseñe dónde se esconden VanCleef y sus hombres, regresa con Gryan Mantorrecio con toda la información.",
    ["T"] = "La hermandad de los Defias",
  },
  [156] = {
    ["D"] = "Si me traes algunas flores podridas, podré prepararte algo de zumo de zombi.$B$BLas flores podridas crecen en lugares extraños. Se dice que se encuentran en el interior de las calaveras de los horrores esqueléticos y de los demonios esqueléticos. Claro que nunca lo he comprobado...$B$BEn el Cementerio del Cerro del Cuervo y en el Jardín Sereno encontrarás esqueletos de esos a montones. Mientras buscas flores podridas, podrías aprovechar para deshacerte de algunos de ellos. Villa Oscura te estaría muy agradecida.",
    ["O"] = "Recoge 8 flores podridas y llévaselas al tabernero Smitts a Villa Oscura.",
    ["T"] = "Busca flores podridas",
  },
  [157] = {
    ["D"] = "Aquí tienes tu hilo, $n. Entre muchas otras cosas, sirve para atar espíritus malvados o para coser la vida en objetos inanimados.",
    ["O"] = "Lleva el hilo de pelo de fantasma a Abercrombie, a su choza al norte del Cementerio del Cerro del Cuervo.",
    ["T"] = "Devolver el pelo",
  },
  [158] = {
    ["D"] = "Necesito una botella de licor. De zumo de zombi. El tabernero Smitts, del Mesón del Cuervo Escarlata de Villa Oscura, debería tener alguna botella.$B$BPor favor, date prisa, $n. ¡Las noches empiezan a ser frías y necesito ese fuego en mis venas!",
    ["O"] = "Havla con el tabernero Smitts.",
    ["T"] = "Zumo de zombi",
  },
  [159] = {
    ["D"] = "Aquí tienes tu zumo de zombi...$B$BY... ten cuidado con él. ¡Es tan fuerte que resucitaría a un muerto!",
    ["O"] = "Lleva el zumo de zombi a Abercrombie a su choza.",
    ["T"] = "Entrega de zumo",
  },
  [160] = {
    ["D"] = "Lleva esta nota a Ello Ebonlocke, el alcalde de Villa Oscura.$B$BÉl y yo somos viejos amigos. Seguro que cuando la lea y sepa lo que has hecho por mí... ¡estará tan contento como yo! ¡Ja, ja, ja, ja!",
    ["O"] = "Lleva la carta de Abercrombie a Ello Cerranegro, el alcalde de Villa Oscura.",
    ["T"] = "Nota para el alcalde",
  },
  [161] = {
    ["D"] = "Si mis sospechas son correctas, esto es un explosivo. Debemos hacerlo analizar por un experto. Ashlan Risapétrea fue asignado al regimiento que cubre Dun Modr y el Puente Thandol. Vas a tener que emprender un viaje peligroso, $N.$b$bCruza la Puerta de Algaz, sigue el camino que cruza Los Humedales y busca a Risapétrea en Dun Modr. Un consejo: ¡no te salgas del camino y no te detengas por nada!",
    ["O"] = "El ingeniero jefe Hinderweir quiere que lleves los polvos a Ashlan Risapétrea, el experto en explosivos de Dun Modr.",
    ["T"] = "Se aproxima una oscura amenaza",
  },
  [162] = {
    ["D"] = "Te ruego que le entregues este informe a alguien que tenga conocimientos de este tema.$B$BConozco a una erudita elfa de la noche llamada Gracina Poderespíritu que ha dedicado mucho tiempo al estudio de los seres vivos que solo se encuentran en Azeroth. ¡Es una historiadora de los pies a la cabeza! Entrégale este informe si quieres que te ayude a entender a qué te enfrentas y, lo más importante, el modo de detener esta amenaza. Necesitaremos ayuda.$B$BLo último que he sabido de ella es que residía en el Templo de la Luna, ubicado en Darnassus.",
    ["O"] = "Dirígete a Darnassus y entrégale el informe del análisis del insecto a Gracina Poderespíritu.",
    ["T"] = "La sublevación de los silítidos",
  },
  [163] = {
    ["D"] = "Algo ocurre en Cerro del Cuervo, $n. Calor jura que alguien –o algo– está rondando los edificios de la zona. Ha visto sombras moviéndose dos noches seguidas y dice que hace tres noches vio luz en una de las ventanas.$B$BSi, como dices, te preocupa la seguridad del Bosque del Ocaso, averigua qué monstruo acecha el Cerro del Cuervo.",
    ["O"] = "Averigua qué está acechando Cerro del Cuervo.",
    ["T"] = "Cerro del Cuervo",
  },
  [164] = {
    ["D"] = "¿Conoces a Sven? ¡Pues él sí que trabaja para la Luz! Él y sus seguidores están acampados en la linde noroccidental del Bosque del Ocaso.$B$BPor desgracia, son muy pocos, y no pueden permitirse enviar a nadie a buscar suministros a la ciudad. ¿No podrías llevarles tú estos suministros?",
    ["O"] = "Entrega los suministros a Sven.",
    ["T"] = "Entrega a Sven",
  },
  [165] = {
    ["D"] = "Antes venía por aquí un anciano... venía a comprar suministros con bastante frecuencia, pero hace un tiempo que no lo veo. Vive en una choza cerca del Cementerio del Cerro del Cuervo, si no recuerdo mal. ¿Podrías ir a ver si le ha ocurrido algo?",
    ["O"] = "Ve a ver cómo está el anciano de la choza que hay cerca del Cementerio del Cerro del Cuervo.",
    ["T"] = "El ermitaño",
  },
  [166] = {
    ["D"] = "Todavía hay una tarea que debes cumplir. Edwin VanCleef debe ser asesinado. Aunque me entristece condenar a alguien a la pena de muerte, el bien común de las gentes de los Páramos de Poniente exige que VanCleef descanse de una vez por todas. Tráeme la cabeza de ese villano cuando hayas terminado tu hazaña.",
    ["O"] = "Mata a Edwin VanCleef y lleva su cabeza a Gryan Mantorrecio.",
    ["T"] = "La hermandad de los Defias",
  },
  [167] = {
    ["D"] = "Estábamos en las profundidades de una gran mina en Páramos de Poniente, oculta tras un granero en el Arroyo de la Luna. No tenemos ni idea de dónde salieron esos ladrones. En fin, el túnel se derrumbó sobre nosotros. Conseguí salir, pero los otros... bueno... de los otros no sé nada.$B$BCreo que tú podrías tener más suerte ahí abajo. Si pudieras buscar a mi hermano, te lo agradecería mucho. Siempre llevaba su insignia de la Liga de Exploradores. Si no puedes encontrarle, al menos la insignia me daría algo de paz mental.",
    ["O"] = "Lleva la insignia de la Liga de Exploradores del supervisor Cardortiga a Wilder Cardortiga, en Ventormenta.",
    ["T"] = "Oh hermano. . .",
  },
  [168] = {
    ["D"] = "Muchos de mis amigos murieron ese horrible día cuando se derrumbó el túnel de la mina. Si tus aventuras te llevan de casualidad a esa parte de la mina en el Arroyo de la Luna, por favor, busca alguna señal que pueda identificarles. Si te encuentras con alguna de sus Tarjetas del Sindicato Minero, tráemelas y me aseguraré de que sus familias reciban la resolución de este horrible accidente.",
    ["O"] = "Recupera 4 Tarjetas del Sindicato Minero y llévaselas a Wilder Cardortiga, en Ventormenta.",
    ["T"] = "La recolección de Tarjetas del Sindicato Minero",
  },
  [169] = {
    ["D"] = "Se busca a Gath\'Ilzogg por dirigir los ataques Roca Negra al Fuerte de Petravista y las incursiones en Villa del Lago. $b$bGath\'Ilzogg es extremadamente peligroso. Se encuentra en el Fuerte maquinando nuevos crímenes del clan contra la humanidad. Debemos sofocar la infestación de orcos cueste lo que cueste. Se ofrece recompensa a quien presente la cabeza de Gath\'Ilzogg al magistrado Solomon.",
    ["O"] = "Mata a Gath\'Ilzogg y lleva su cabeza al magistrado Solomon a Villa del Lago para recibir tu recompensa.",
    ["T"] = "Se busca: Gath\'Ilzogg",
  },
  [170] = {
    ["D"] = "Espero que estés aquí para echar una mano, $c. Después del último ataque de los troggs, nos vendría bien cualquier ayuda.$B$BHe oído que esos malnacidos han estado apareciendo por todas partes y parece que el Valle de Crestanevada no es una excepción. Han sido vistos en los montes del sureste y cerca del lago helado.$B$BY eso no es todo. Hace unas noches atacaron e invadieron nuestro campamento del oeste.$B$BNecesitamos ayuda, $gmuchacho:muchacha;. Necesitamos brazos fuertes para hacer retroceder a los troggs.",
    ["O"] = "Balir Martilloescarcha quiere que mates a 6 troggs Comepiedras y a 6 troggs Comepiedras fornidos.",
    ["T"] = "Una nueva amenaza",
  },
  [171] = {
    ["D"] = "Has sido muy $gbueno:buena; conmigo, $n. Nunca había soñado con divertirme tanto en la Semana de los Niños. Gracias.$B$BOjalá todos los huérfanos tuvieran la suerte de hacer lo que hemos hecho nosotros. ¿Quieres que vayamos a buscar a Chichón Perinola de nuevo y que le compremos un expositor para espadas de gomaespuma? Creo que todavía está empujando esa vagoneta en Los Canales, fuera del Distrito de Mercaderes. ¡Así todo el mundo en el orfanato podrá hacer algo divertido!$B$BDespués, ya será hora de que me vaya a casa.",
    ["O"] = "Compra un expositor para espadas de gomaespuma a Chichón Perinola y después habla con la supervisora de huérfanos Ruiseñor en el Distrito de la Catedral para llevar a tu pupilo de vuelta al orfanato de Ventormenta.",
    ["T"] = "Alcaide de la Alianza",
  },
  [172] = {
    ["D"] = "¿Quieres ayudarnos? Muchísimas gracias, necesitamos muchas manos durante la Semana de los Niños.$b$bToma este silbato. Solo podrás usarlo durante esta semana, y cuando lo uses, podrás hablar con el niño al que estés cuidando. Si vuelves a usarlo, el niño se irá.$b$bY ahora, ¡ve a conocer a tu protegido! Seguro que lo pasaréis muy bien juntos.",
    ["O"] = "Usa el silbato de huérfano para hablar con el niño al que cuides durante la Semana de los Niños.",
    ["T"] = "Semana de los Niños",
  },
  [173] = {
    ["D"] = "La oscuridad se siente inexorablemente atraída hacia el Bosque del Ocaso. El maestro Carevin lucha por erradicar el mal y la herejía. Y con nuestros esfuerzos mantenemos a salvo a las gentes de Villa Oscura.$B$B¿Te crees $gmerecedor:merecedora; de unirte a nosotros?$B$BHace tiempo pensaba como tú. La complacencia de la Guardia me decepcionó y acabé uniéndome al maestro Carevin. Si quieres probar tu valía, no utilices las palabras.$B$BMide tus habilidades con un ferocanis delirante tejesombras en la Arboleda del Destello y en El Vergel Pútrido.",
    ["O"] = "Mata a 6 ferocanis Tejesombras nocturno para Calor de Villa Oscura.",
    ["T"] = "Ferocanis en el bosque",
  },
  [174] = {
    ["D"] = "Cog, mi amigo gnomo, me ha prometido ayuda para crear un instrumento que me servirá para ver las estrellas. Más allá de la magia hay una verdad que puede explicar las extrañas transformaciones del bosque.$B$BTráeme un tubo de bronce creado por un ingeniero cualificado. Ese es el primer paso según el manual de Cog.",
    ["O"] = "Viktori Prism\'Antras de Villa Oscura quiere que le lleves un tubo de bronce.",
    ["T"] = "Mira las estrellas",
  },
  [175] = {
    ["D"] = "Según el manual de Cog, ahora necesito encontrar algo para reflejar la luz. Me acuerdo de una mujer llamada Mary que se hizo muy famosa por aquí porque siempre llevaba un espejo consigo. Quizás puedas encontrar a Mary y pedirle que nos preste una superficie reflectante para este proyecto... Vive al sur del Bosque del Ocaso. Al menos, es donde vivía antes...",
    ["O"] = "Localiza a Mary y convéncela de que te dé una superficie reflectora para el proyecto de Viktori.",
    ["T"] = "Mira las estrellas",
  },
  [176] = {
    ["D"] = "Se busca: Hogger.$B$BUn gnoll enorme, Hogger, está al acecho en el bosque al suroeste de Elwynn. Ha sofocado todos los intentos de capturarlo.$B$BEl ejército de Ventormenta ofrece una generosa recompensa por atrapar al gnoll. Para ganarla, los caza recompensas tendrán que traer una prueba de la derrota de Hogger al alguacil Dughan de Villadorada.",
    ["O"] = "Mata al gnoll Hogger y trae su enorme garra gnoll al alguacil Dughan.",
    ["T"] = "Se busca: Hogger",
  },
  [177] = {
    ["D"] = "El espejo era uno de mis tesoros más preciados hasta... el incidente. Después de lo que ocurrió, lo escondí detrás del granero. Antes venía por aquí un necrófago... Quizás lo cogió él. Se dice que esa bestia inmunda vive en el Cementerio del Jardín Sereno.",
    ["O"] = "Quita el espejo de Mary la Ciega al necrófago demente y llévaselo a Viktori a Villa Oscura.",
    ["T"] = "Mira las estrellas",
  },
  [178] = {
    ["D"] = "<Oyes una voz...> $b$bNo te asustes; soy Theocritus, Mago Supremo de la Torre de Azora, en el Bosque de Elwynn. El colgante es un medio de comunicación entre los gnolls Pellejo Negro y su señor, Morganth. $b$bHe investigado durante meses y creo que yo también puedo usar los colgantes. Si oyes este mensaje, mi hechizo ha funcionado. $b$bTráeme el colgante y te recompensaré.",
    ["O"] = "Lleva el colgante Pellejo Negro a Theocritus el Mago.",
    ["T"] = "Theocritus recupera el colgante",
  },
  [179] = {
    ["D"] = "¿Qué tenemos aquí? Tienes pinta de necesitar algo para mantener las manos calientes, ¿mm?$B$BYo te diré lo que necesitas: un par de estupendos guantes bien calentitos. Y, como soy un alma generosa, me encantaría proporcionarte ese par de guantes. Pero con una condición.$B$BNecesito que me consigas carne de lobo. Es un buen trato, ¿mm? Tú me traes un poco de carne de lobo y yo evito que pierdas los dedos por congelación. ¿Y bien? ¿Qué dices?",
    ["O"] = "Sten Brazorrecio quiere 8 piezas de carne dura de lobo.",
    ["T"] = "Los enanos modistos",
  },
  [180] = {
    ["D"] = "Se busca al teniente Fangore $b$bSe acusa al jefe del clan gnoll Pellejo Negro de colaborar con el enemigo del Reino Morganth. El magistrado Solomon ha decretado la pena de muerte para este traidor a Ventormenta. Su zarpa se considerará prueba de su muerte.",
    ["O"] = "Mata al teniente Fangore y lleva su zarpa al magistrado Solomon a Villa del Lago.",
    ["T"] = "Se busca: teniente Fangore",
  },
  [181] = {
    ["D"] = "Necesito un objeto más para completar el instrumento. Según el manual de Cog necesito una lente de algún tipo. La única lente que conozco, aunque sería lo suficientemente grande, es difícil de conseguir.$B$BSé de un ogro que tiene un monóculo... se llama Zzarc\'Vul y vive en el montículo que hay al sur del Bosque del Ocaso. Si puedes, tráeme su monóculo y lo usaremos como lente.",
    ["O"] = "Localiza a Zzarc\'Vul en el montículo meridional del Bosque del Ocaso y llévale su monóculo a Viktori a Villa Oscura.",
    ["T"] = "Mira las estrellas",
  },
  [182] = {
    ["D"] = "Mi hermano Senir y yo fuimos enviados a diferentes zonas de Dun Morogh para investigar la amenaza de los trols. El Senado ya está muy ocupado con los troggs, no necesita más preocupaciones.$B$BPor lo que he visto, no hay muchos trols en el Valle de Crestanevada y la mayoría se concentran en la cueva del sur. No creo que vayamos a necesitar al ejército. Unos cuantos brazos fuertes serán más que suficiente.$B$BQuizás quieras ayudar en esta empresa... Tengo autoridad para ofrecerte una compensación por tu ayuda.",
    ["O"] = "Grelin Barbablanca quiere que mates a 14 crías de trol Peloescarcha.",
    ["T"] = "La cueva de los trols",
  },
  [183] = {
    ["D"] = "No hay nada como pasar un día cazando jabalíes, ¿eh?$B$BAunque con la de jabalíes que tenemos en el Valle de Crestanevada, la cosa pierde un poco de gracia. Ni siquiera hay que provocarlos. Ya están todos enfadados e inquietos, no necesitan ayuda para eso. De hecho, últimamente hay tantos jabalíes en la zona que mi caza diaria se está convirtiendo en una actividad peligrosa.$B$BPara abreviar, si pudieras ayudarme a matar algunos jabalíes, te lo agradecería mucho.",
    ["O"] = "Talin Ojoagudo quiere que mates a 12 jabalíes del risco pequeños.",
    ["T"] = "Cazador de jabalíes",
  },
  [184] = {
    ["D"] = "Esta es la escritura de una extensión de tierras de labranza de los Páramos de Poniente. Está firmada por un tal Theodore Furlbrow y por su mujer, Verna. En el reverso de la escritura hay unas palabras garabateadas con gran prisa:$B$B\"Presionamos a Furlbrow y conseguimos su escritura. Pensé que te vendría bien si querías falsificar una para ti.$B$BLos Furlbrow no nos darán problemas. La última vez que los vi estaban saliendo de los Páramos de Poniente con un carro roto.\"$B$BCrees que probablemente los Furlbrow querrán recuperar su escritura...",
    ["O"] = "Lleva al granjero Furlbrow su escritura.",
    ["T"] = "La escritura de Furlbrow",
  },
  [185] = {
    ["D"] = "Cuando yo era una niña, mi padre me enseñó a cazar tigres.$B$BVerás que los felinos jóvenes son mucho más fáciles de seguir y dar caza. Por esa razón, comenzaremos con lo fácil. Es fácil encontrar tigres jóvenes cerca del campamento de expedición.$B$BPon a prueba tus habilidades de rastreo y veamos si puedes dar caza a alguna de las bestias.",
    ["O"] = "Ajeck Rouack de la Expedición de Nesingwary, quiere que mates 10 tigres jóvenes de Tuercespina.",
    ["T"] = "Domina el tigre",
  },
  [186] = {
    ["D"] = "Estás aprendiendo, $c. Probemos ahora tus habilidades con los felinos maduros. Mata a 10 tigres de Tuercespina.",
    ["O"] = "Ajeck Rouack de la Expedición de Nesingwary, quiere que mates 10 tigres de Tuercespina.",
    ["T"] = "Domina el tigre",
  },
  [187] = {
    ["D"] = "Ahora voy a subir las apuestas y a poner a prueba realmente tu habilidad. Demuéstrame que puedes matar 10 tigres de Tuercespina mayores. Si puedes hacerlo, estarás $glisto:lista; para el desafío final antes de poder declararte Maestro Cazatigres en presencia del maestro Decautela.$B$BLos tigres de Tuercespina mayores no solo son los más difíciles de encontrar, sino también los más difíciles de vencer.",
    ["O"] = "Ajeck Rouack de la Expedición de Nesingwary, quiere que mates 10 tigres de Tuercespina mayores.",
    ["T"] = "Domina el tigre",
  },
  [188] = {
    ["D"] = "Este es el desafío final al que tienes que enfrentarte. Llevamos semanas rastreando a una tigresa muy evasiva. Llamamos a la bestia, Sin\'Dall. Veamos si puedes hacer lo que ningún $r ha logrado: dar caza y matar a Sin\'Dall. Tráeme su zarpa como prueba.$B$BRastrearla no será una tarea fácil.",
    ["O"] = "Ajeck Rouack de la Expedición de Nesingwary quiere que mates a Sin\'Dall y vuelvas con su zarpa.",
    ["T"] = "Domina el tigre",
  },
  [189] = {
    ["D"] = "¡Malditos trols! ¡Han estado matando a nuestros agentes en la jungla! Ya he perdido a un buen número de mis mejores guardias en esas escaramuzas. ¡Los costes se multiplican! ¿Cómo se lo voy a explicar al Barón Revilgaz?$B$BAcción decisiva... Piensa, Kebok... no te asignaron al puesto que tienes hoy por estar de brazos cruzados... ¡Lo tengo! Tú... $n, ¿verdad? Me ayudarás, ¿no? ¡Hay dinero en juego!$B$BEste es el trato: tráeme las orejas de quince trols Sangrapellejo y te compensaré generosamente.",
    ["O"] = "Consigue 15 orejas Sangrapellejo y llévaselas a Kebok en la Bahía del Botín.",
    ["T"] = "Orejas de Sangrapellejo",
  },
  [190] = {
    ["D"] = "Si quieres ser parte de la partida de caza tan estupenda que Hemet ha reunido, vas a tener que demostrar que se te da bien cazar panteras. Empezaremos por lo fácil, no te preocupes. Veamos, para empezar, mata 10 panteras jóvenes.$B$BRastrearlas es solo la mitad del desafío...",
    ["O"] = "Sir S. J. Erlgadin de la Expedición de Nesingwary, quiere que mates 10 panteras jóvenes.",
    ["T"] = "Domina la pantera",
  },
  [191] = {
    ["D"] = "Creo que podemos subir un poco el nivel. Si quieres demostrar que estás $gpreparado:preparada; para socializar con esos grandes cazadores, vas a tener que demostrar que puedes matar 10 panteras.$B$BSon bastante duras. No resulta tan fácil matar a esas panteras como a las más jóvenes que mataste antes.",
    ["O"] = "Sir S. J. Erlgadin de la Expedición de Nesingwary, quiere que mates 10 panteras.",
    ["T"] = "Domina la pantera",
  },
  [192] = {
    ["D"] = "Y ahora vamos con lo difícil. Un verdadero cazador de panteras puede demostrar su habilidad matando panteras Faucesombrías. Demuéstranos que puedes matar a 10 de estas bestias.$B$BSi te parece difícil rastrearlas, espera a intentar matar a una...",
    ["O"] = "Sir S. J. Erlgadin de la Expedición de Nesingwary, quiere que mates 10 panteras Faucesombrías.",
    ["T"] = "Domina la pantera",
  },
  [193] = {
    ["D"] = "Casi has demostrado ser un maestro cazador de panteras. Hay una pantera llamada Bhag\'thera merodeando por la jungla. Hasta ahora, ha conseguido esquivar a nuestro grupo. Veamos si puedes usar tus habilidades para matar a la gran Bhag\'thera.$B$B¡Tráeme el colmillo de Bhag\'thera y conseguirás mi respeto!",
    ["O"] = "Sir S. J. Erlgadin de la Expedición de Nesingwary, quiere que le lleves el colmillo de Bhag\'thera.",
    ["T"] = "Domina la pantera",
  },
  [194] = {
    ["D"] = "¿Así que crees que tus habilidades de caza están en buena forma? Veamos si es cierto. Vete a la jungla y mata 10 raptores de Tuercespina. Veremos lo $gbuen cazador:buena cazadora; que eres.$B$B¡Y no, no te diré dónde encontrarlos! Localizar a las bestias es la mitad del desafío.",
    ["O"] = "Hemet Decautela quiere que mates 10 raptores de Tuercespina.",
    ["T"] = "Domina el raptor",
  },
  [195] = {
    ["D"] = "Ahora aumentemos la dificultad del desafío y veamos si estás a la altura. Entra en la jungla y mata 10 de esos asquerosos raptores colazote. ¡Veamos si puedes!",
    ["O"] = "Hemet Decautela quiere que mates 10 raptores colazote.",
    ["T"] = "Domina el raptor",
  },
  [196] = {
    ["D"] = "Veamos qué tal lo haces en este desafío. Ahí fuera hay una raza muy reservada de raptores. Los llamamos acechadores de la selva. Son mucho más difíciles de rastrear y matar que las otras razas que has probado hasta ahora. Mata 10 acechadores de la selva y te enseñaré la mejor parte de la caza.",
    ["O"] = "Hemet Decautela quiere que mates 10 acechadores de la selva.",
    ["T"] = "Domina el raptor",
  },
  [197] = {
    ["D"] = "Como has demostrado ser invencible en la selva, te hablaré de un feroz raptor que ni siquiera yo he conseguido matar. Ajeck llama a ese astuto raptor, Tethis.$B$BSi quieres demostrar que eres un verdadero maestro, mata a Tethis y tráeme su garra. Sería un logro que ningún otro cazador ha conseguido.",
    ["O"] = "Hemet Decautela quiere que mates a Tethis, un esquivo y peligroso raptor de Tuercespina.",
    ["T"] = "Domina el raptor",
  },
  [198] = {
    ["D"] = "Mmm. Sí, de hecho, tengo algo que podrías hacer. El teniente Doren y sus seguidores que están al norte, necesitan que les enviemos sus suministros.$B$BTodavía no lo hemos podido hacer y, con el ataque de los trols, necesito que alguien lo haga. El campamento de Doren está en la parte noroeste del camino, en la entrada de Tuercespina desde el Bosque del Ocaso. Lleva los suministros al soldado Thorsen, si no está en el campamento, estará patrullando la zona sur. Él se encargará de todo.$B$BPor cierto, si fallas, me deberás una indemnización.",
    ["O"] = "Entrega los suministros goblin variados al soldado Thorsen.",
    ["T"] = "Suministros para el soldado Thorsen",
  },
  [199] = {
    ["D"] = "Coges una muestra de esos polvos de olor extraño.",
    ["O"] = "Enséñale la pista al ingeniero jefe Hinderweir.",
    ["T"] = "Se aproxima una oscura amenaza",
  },
  [200] = {
    ["D"] = "Si Kurzen sabe lo del secreto de Thorsen, significa que tenemos un espía. ¡Debemos determinar cómo se filtró el secreto!$B$BEl contacto de Thorsen en la Base de Kurzen era Bookie Herod. Herod es el oficial de logística de Kurzen y lo sabe todo sobre la Base de Kurzen. Si consigues hacerte con las notas o los registros de Herod, quizás podamos saber cómo descubrieron a Thorsen.$B$BCuando todavía estábamos con Kurzen, la oficina de Bookie estaba la parte de arriba de la casa de dos pisos de la Base. Probablemente, sus registros sigan allí.",
    ["O"] = "Encuentra los registros de Bookie Herod.",
    ["T"] = "Corredor Herod",
  },
  [201] = {
    ["D"] = "He recibido informes sobre un grupo de cazadores que ha establecido un campamento río arriba del campamento minero de Ventura y Cía., en el Lago Nazferiti. En circunstancias normales, no me importaría, pero he oído que Hemet Decautela forma parte de ese grupo. Está muy bien conectado, ya me entiendes, y nunca se puede estar demasiado seguro.$b$b¿Por qué no vas a echar un vistazo al campamento, $n, a ver qué están tramando?",
    ["O"] = "Encuentra el campamento de los cazadores e informa sobre su ubicación a Krazek en la Bahía del Botín.",
    ["T"] = "Investiga el campamento",
  },
  [202] = {
    ["D"] = "Has demostrado tu valía otra vez y tus recursos parecen rivalizar con los del mismísimo Kurzen. Quizás seas la salvación de nuestro grupo de rebeldes... si consigues enfrentarte a Kurzen y vencer.$B$BLidera a sus hombres desde las profundidades de Reservas. Debes entrar en la cueva, derrotar a sus defensores y enfrentarte a Kurzen y a sus líderes.$B$BAdiós, $N. Mi esperanza, y el destino de mis hombres, van contigo.",
    ["O"] = "Mata 6 unidades de élite Kurzen y 4 subjefes Kurzen. Lleva la cabeza de Kurzen al teniente Doren en el Asentamiento Rebelde.",
    ["T"] = "Coronel Kurzen",
  },
  [203] = {
    ["D"] = "Estamos luchando una batalla perdida con el coronel Kurzen. No solo es un estratega brillante, sino que nos supera en número, ¡al menos 10 a 1! Para sobrevivir, tenemos que usar sus propias tácticas de cobertura contra él.$B$BVete a su campamento del este y mata a sus hombres. A muchos de ellos. Y sal de ahí antes de que los refuerzos te alcancen.",
    ["O"] = "Mata 15 combatientes de la jungla Kurzen.$B$BVuelve a ver al sargento Yohwa en al Asentamiento Rebelde.",
    ["T"] = "La segunda rebelión",
  },
  [204] = {
    ["D"] = "Para tener una oportunidad contra Kurzen, necesitamos neutralizar su fuerza de curación. Ha reunido a un gran número de auxiliadores que curarán a sus tropas con remedios de la jungla y envenenarán a las nuestras con extractos de veneno de helecho.$B$BNecesitamos esos remedios para curar a los nuestros, y una muestra de esos extractos para poder estudiarla y crear nuestros propios remedios. Busca ambas cosas en la Base de Kurzen. Sus auxiliadores tendrán los remedios y, probablemente, los extractos están en algunas de las cajas de suministros del campamento.",
    ["O"] = "Lleva 7 remedios de la jungla y 1 extracto de veneno de helecho al sargento Yohwa en el Asentamiento Rebelde.",
    ["T"] = "Mala medicina",
  },
  [205] = {
    ["D"] = "Mientras estudio esas leyendas, necesito que consigas instrumentos de magia trol maligna. Entra en el territorio de los trols Machacacráneos, la segunda tribu del sur, y consigue fetiches Machacacráneos de sus médicos brujos y místicos.",
    ["O"] = "Lleva 4 fetiches Machacacráneos al hermano Nimetz en el Asentamiento Rebelde.",
    ["T"] = "Brujería trol",
  },
  [206] = {
    ["D"] = "Quizás Kurzen esté muerto, pero... eso que le volvió maligno sigue vivo. Gracias a mis estudios he descubierto esto: hay un mago ogro, Mai\'Zoth, que vive en un Túmulo Ogro en las montañas del este de Tuercespina. Se dice que utiliza magia de control mental y creo que fue él quien corrompió a Kurzen.$B$BSi permitimos que Mai\'Zoth siga con vida, su magia podría influenciar a otros y quizás incluso provocar que apareciese otro Kurzen.$B$BMátale y tráeme su Ojo de la Mente, el artefacto que utiliza para corromper.",
    ["O"] = "Lleva el Ojo de la Mente al hermano Nimetz en el Asentamiento Rebelde.",
    ["T"] = "Mai\'Zoth",
  },
  [207] = {
    ["D"] = "El coronel Kurzen fue un gran hombre de gran nobleza, ¡debo descubrir qué hizo que cayese en la depravación! Quizás está bajo el influjo de alguna magia trol. Para confirmarlo, debo estudiar algunas de sus leyendas.$B$BHay 4 tablillas escondidas en las ruinas trol de Tuercespina. ¡Necesito leer esas tablillas! Transcribe las tablillas y tráeme las transcripciones.$B$BLa primera tablilla está en las Ruinas de Bal\'lal, la segunda en El Arrecife Mortal, y la tercera y cuarta en las Ruinas de Zul\'Kunda.",
    ["O"] = "Encuentra las 4 tablillas y lleva las leyendas al hermano Nimetz.",
    ["T"] = "El misterio de Kurzen",
  },
  [208] = {
    ["D"] = "$N, eres $gun buen cazador:una buena cazadora;, mereces estar en nuestro grupo. Desde los campos de batalla de Lordaeron hasta las profundidades de la jungla, muy pocas veces he visto tal destreza con las armas como de la que has hecho gala.$B$BHas matado al feroz Sin\'Dall, a la esquiva Bag\'thera y al traicionero Tethis. Pero el verdadero premio es la cabeza del rey Bangalash. Ese tigre blanco es la razón por la que estoy aquí. Llevo bastante tiempo detrás de él. Mata al rey Bangalash y tu dominio de la cacería será insuperable.",
    ["O"] = "Hemet Decautela quiere que le lleves la cabeza del rey Bangalash, el gran tigre blanco.",
    ["T"] = "Caza mayor",
  },
  [209] = {
    ["D"] = "Matar los suficientes tigres para mantener la exportación de colmillos de tigre a las islas de los Mares del Sur, es caro y complicado. Por suerte, hemos desarrollado una técnica que nos permite coger objetos normales en forma de cuerno y convertirlos en falsificaciones perfectas.$B$BLo más parecido que hemos encontrado, sorprendentemente, son los colmillos de trols Machacacráneos.$B$BEh, antes de que digas nada, ojo de comprador que no ve, corazón de tonto que no siente, ¿tengo razón? Tráeme muchos colmillos para que podamos ponernos a trabajar en el pedido del mes.",
    ["O"] = "Consigue 18 colmillos de Machacacráneos y llévaselos a Kebok.",
    ["T"] = "Colmillos de Machacacráneos",
  },
  [210] = {
    ["D"] = "Yo me encargo de la mayor parte de la cocina del campamento. Y solía ser un montón de trabajo, hasta que le compré una olla mecánica a un mercader goblin en la Bahía del Botín. ¡Esa olla es una maravilla! Me ha ahorrado un montón de horas... ¡Pero se ha roto!$B$B¿Podrías llevar la olla al goblin para que me la pueda arreglar? Se llama Krazek.",
    ["O"] = "Lleva la olla mecánica de Krazek al goblin Krazek.",
    ["T"] = "La cocina de Krazek",
  },
  [211] = {
    ["D"] = "¡Ay, se acerca el tiempo de atacar Andorhal y de hacer salir al exánime que lo controla!$B$BEs en el interior de las ruinas de la ciudad donde el exánime, Araj el Invocador, tiene sus dominios. Está custodiado por numerosos miembros de la Plaga que rodean los restos del centro de la ciudad, y su séquito personal de guardianes es realmente formidable. Necesitarás muchos aliados para tener alguna oportunidad de enfrentarte a él con éxito.$B$B¡Destruye a Araj, $N, y tráeme un Fragmento de su Filacteria como prueba!",
    ["O"] = "Lleva el Fragmento de Filacteria de Araj al comandante Ashlam Puñovalor del Campamento del Orvallo, en las Tierras de la Peste del Oeste.",
    ["T"] = "Ah, Andorhal",
  },
  [212] = {
    ["D"] = "Uno de mis platos más famosos es basilisco helado en vinagreta de lima. Pero el problema es que es imposible mantener fresca la carne y tengo que servir el plato esta noche en el cumpleaños de un noble.$B$BSi pudieras traerme un anca de basilisco helado de un Basilisco Ojogélido recién muerto y rápidamente... ¡estaré en deuda contigo!$B$BLos Basiliscos Ojogélido suelen vagar por las dunas de la playa al noroeste de la Vega de Tuercespina, así que te espera un largo viaje. ¡Y rápido!",
    ["O"] = "Mata un Basilisco Ojogélido, consigue un anca de basilisco helado y llévasela a Angus Stern en El Ermitaño Azul.$B$B<Para tener éxito en esta misión, no puedes soltar tu espíritu.>",
    ["T"] = "Un plato bien frío",
  },
  [213] = {
    ["D"] = "¡Lo ha hecho! Ya es bastante malo que Gelriz se meta por el medio de los magnates que nombraron los príncipes mercantes como para que ahora intente meterse con el pirata más famoso.$B$BRevilgaz no lo tolerará y me ha dicho que me encargue del problema a mi manera.$B$B¿Mi manera? El robo. Los geólogos de Ventura y Cía. que están cerca del Lago Nazferiti están muy interesados en esos extraños cristales azules que han encontrado en las minas. Tráeme muestras de los cristales de los geólogos, no me importa lo que tengas que hacer para conseguirlas.",
    ["O"] = "Consigue 8 cristales girados y llévaselos a Kebok en la Bahía del Botín.",
    ["T"] = "OPA hostil",
  },
  [214] = {
    ["D"] = "No es ningún secreto que la marca distintiva de la banda Defias son los pañuelos rojos. Pero hemos averiguado que el material del que están hechos los pañuelos sirve para identificar el rango del miembro.$B$BDicho esto, quiero que elimines tantos miembros Defias de alto rango como puedas. Podrás encontrarlos en la ladronera secreta de VanCleef. Cuando hayas terminado tráeme 10 pañuelos de seda roja y sabré recompensarte. Buena suerte, $n.",
    ["O"] = "La exploradora Riell de la Colina del Centinela quiere que le lleves 10 pañuelos de seda roja.",
    ["T"] = "Pañuelos rojos de seda",
  },
  [215] = {
    ["D"] = "Gracias por salvarme, $n. Sé lo que parece esto, pero... deja que te lo explique.$B$BEl teniente Doren me encargó alistarme con los hombres de Kurzen para que pudiera espiarles y llevar información a los rebeldes. Lo que has visto es lo que yo pensaba que sería mi oportunidad para infiltrarme en la Base de Kurzen. Me equivoqué y, otra vez, gracias por salvarme.$B$BEsto era una misión de alto secreto, así que no se lo puedes decir a nadie. Pero si hablas con Doren, te lo explicará.",
    ["O"] = "Habla con el teniente Doren en el Asentamiento Rebelde.",
    ["T"] = "Los secretos de la jungla",
  },
  [216] = {
    ["D"] = "¡Los furbolgs Piel de Cardo son una amenaza para nuestra expansión! El Poblado Piel de Cardo, al este, obstruye nuestro paso entre este lugar y el Puesto del Hachazo. Es un camino que rodearía el pueblo de Astranaar; sin él, ofrecemos a la Alianza una vía más para la expansión.$B$B$N, no podemos permitir... ¡un hueco en el camino de expansión!$B$BVe al Poblado Piel de Cardo y acaba con los furbolgs. Recibirás una compensación por esta tarea y lo que es más importante, ¡estarás cumpliendo con tu deber para con la Horda!",
    ["O"] = "Ejecuta a 12 vengadores Piel de Cardo y a 12 chamanes Piel de Cardo; la mayoría se encuentran al este de La Ensenada de Zoram en el Poblado Piel de Cardo. Cuando lo hayas logrado, vuelve junto a Karang Amakkar en la Avanzada de Zoram\'gar, Vallefresno.",
    ["T"] = "Entre una roca y un Piel de Cardo",
  },
  [217] = {
    ["D"] = "$N, nuestros rasteadores han identificado al jefe de la incursión trogg. Parece que estas bestias de Loch Modan siguen a un jefe llamado Grawmug. Y lo tienen muy bien protegido. Siempre va acompañadp de dos de sus mejores guardias, Gnasher y Brawler.$b$bSi te vez capaz, $N, quiero que dirijas el asesinato de Grawmug y sus dos matones. Si hacemos caer al líder trogg, puede que tengamos una oportunidad de rechazar a estas bestias.",
    ["O"] = "Mata al jefe trol, Grawmug, y a sus dos matones, Rechín y Camorrista. Informa al capitán Rugelfuss en la torre de vigilancia sur.",
    ["T"] = "En defensa de las tierras del rey",
  },
  [218] = {
    ["D"] = "¡Mi diario! Se lo llevaron a la cueva. El que lo cogió... era una bestia enorme con unas extrañas marcas en la cara... bueno, en todo el cuerpo. No conseguí ver mucho más.$B$BA ti se te dan bien los trols, quizás podrías ir a recuperar mi diario...",
    ["O"] = "Grelin Barbablanca quiere que mates a Grik\'nir el Frío y que recuperes su diario.",
    ["T"] = "El diario robado",
  },
  [219] = {
    ["D"] = "¡Al fin, ayuda para la Alianza! $b$bAyúdame... $b$bLo último que recuerdo es que esos malditos orcos nos tenían rodeados. Los mataron a casi todos. A mí me dieron un golpe en la cabeza y perdí el conocimiento, y ahora me encuentro cautivo. Ayúdame, estoy malherido. $b$bTienes que ayudarme a volver... Por favor, eres mi única esperanza.",
    ["O"] = "Protege al cabo Keeshan en el camino a Crestagrana.",
    ["T"] = "Desaparecido en combate",
  },
  [220] = {
    ["D"] = "Islen tendrá una tarea más para ti antes de recompensarte con tu siguiente tótem. Sea cual sea esa tarea, te hará falta el vial de agua que te he dado.$B$BAhora vete y encuéntrala. Volveremos a encontrarnos algún día.",
    ["O"] = "Llévale el vial del agua más pura a Islen Vident\'aqua en Los Baldíos.",
    ["T"] = "La llamada del agua",
  },
  [221] = {
    ["D"] = "Quizás te hayas fijado en esos enormes ferocanis que deambulan por los bosques con los tejesombras... Por lo que sabemos, esos corredores oscuros son el grueso de las filas de los ferocanis.$B$BEn mis patrullas también he notado que han invadido El Vergel Pútrido al suroeste de la ciudad. Esos ferocanis son más fuertes que los últimos a los que te enfrentaste.$B$BNo bajes la guardia.",
    ["O"] = "Mata a 12 ferocanis correoscuros nocturnos para Calor de Villa Oscura.",
    ["T"] = "Ferocanis en el bosque",
  },
  [222] = {
    ["D"] = "Tus anteriores hazañas me han hecho pensar que ya estás $glisto:lista; para afrontar a los ferocanis más duros del bosque.$B$BDe todos los ferocanis que han establecido aquí su morada, los Colmillo Vil y los Mancillados han demostrado ser los más peligrosos. Se han instalado cerca de las cuevas y en la mina que hay al sur.$B$BEl fuego de sus hogueras puede verse desde muy lejos...",
    ["O"] = "Mata 8 Colmillovil nocturnos y 8 ferocanis corruptos nocturnos para Calor de Villa Oscura.",
    ["T"] = "Ferocanis en el bosque",
  },
  [223] = {
    ["D"] = "Ten, $n. Lleva este mensaje al maestro Carevin.$B$B<Con un rápido movimiento saca un pergamino descolorido y te lo tiende.>$B$B¡Unos pocos más como tú y superaremos en número a La Guardia Nocturna! Quizás así podríamos acabar entre todos el trabajo que hacemos nosotros.",
    ["O"] = "Lleva la nota de Calor a Jonathan Carevin.",
    ["T"] = "Ferocanis en el bosque",
  },
  [224] = {
    ["D"] = "¡Tenemos que proteger Loch Modan, $N! Casi todas nuestras tropas están luchando en otros frentes; nos hemos quedado desprotegidos. ¡Están saliendo trogg de debajo de las piedras! Esta invasión supone la mayor amenaza para Forjaz. Tenemos que acabar con esos asquerosos mutantes.$b$bNecesitamos a alguien valiente como tú para acabar con esos seres. Acaba con diez trogg Rompecantos y diez rastreadores Rompecantos. Después vuelve.",
    ["O"] = "El montaraz Pedernal, de la torre de vigilancia sur, quiere que mates a 10 trogg y 10 rastreadores Rompecantos.",
    ["T"] = "En defensa de las tierras del rey",
  },
  [225] = {
    ["D"] = "La lápida, desgastada por el tiempo, reza sencillamente:$B$BMORGAN LADIMOR$B$B<Cuando te acercas más, te das cuenta de que la tierra que hay sobre la tumba ha sido removida recientemente; parte de ella ha sido extraída...>",
    ["O"] = "Ve a Villa Oscura y habla con Sirra Von\'Indi.",
    ["T"] = "La lápida desgastada",
  },
  [226] = {
    ["D"] = "A Sven y a mí nos esperan días peligrosos, con el Necromántico al este... y aquí solos... tenemos que cazar nuestra propia comida. Y cada vez que vuelvo al campamento con algo de carne, aparecen lobos hambrientos o rabiosos buscando un bocado. ¡Vivir aquí es peligroso, no hace falta que lo jure!$B$BPero si consiguieras librarnos de algunos de esos lobos, nos facilitarías mucho la cosa. Se concentran sobre todo al norte y al este de aquí, cerca del río.",
    ["O"] = "Mata a 12 lobos feroces hambrientos y a 8 lobos feroces rabiosos y vuelve al campamento de Sven en la frontera oeste de Bosque del Ocaso a buscar a Lars.",
    ["T"] = "Con los lobos en los talones",
  },
  [227] = {
    ["D"] = "<Busca entre las estanterías y vuelve con un libro encuadernado en cuero.>$B$BSi quieres saber algo más, podrías preguntar a Althea. Ella ha sido la que ha... tratado el... problema... con él.",
    ["O"] = "Ve a Villa Oscura y habla con la comandante Althea Cerranegro.",
    ["T"] = "Morgan Ladimor",
  },
  [228] = {
    ["D"] = "Por lo que me dicen los rastreadores, Mor\'Ladim deambula por el Bosque del Ocaso; recorre un extraño y serpenteante camino por el cementerio. Lo enterramos pasada la casa de la colina, ¿sabes dónde digo?$B$BNo hay nada que pueda hacer para ayudarte, pero te deseo buena suerte.",
    ["O"] = "Mata a Mor\'Ladim y luego lleva su calavera a la comandante Althea Cerranegro a Villa Oscura.",
    ["T"] = "Mor\'Ladim",
  },
  [229] = {
    ["D"] = "Tendría que haberte dicho esto antes...$B$BVeamos cómo podría explicarlo...$B$BMorgan cree que toda su familia está muerta pero, de hecho, su hija Sarah Ladimor ahora es una guardia. Siempre ha estado atormentada por las... circunstancias que rodearon la muerte de su padre. Quizás podrías ir a llevarle estas noticias.",
    ["O"] = "Ve a Villa Oscura y habla con la vigilante Sarah Ladimor.",
    ["T"] = "La hija superviviente",
  },
  [230] = {
    ["D"] = "Cavando en el montículo encuentras un pequeño libro manchado de tierra.$B$BNo tiene título pero... contiene información que a Sven le gustaría tener.",
    ["O"] = "Lleva el libro de la granja de Sven a Sven.",
    ["T"] = "El campamento de Sven",
  },
  [231] = {
    ["D"] = "Ten, lleva esto a su tumba. Quizás... de alguna forma, él sepa que estoy bien y que nadie le hace responsable de lo que ocurrió.$B$B<Los ojos se le llenan de lágrimas mientras se quita el anillo de la mano derecha y lo coloca en tus manos.>$B$BY... gracias, $n.",
    ["O"] = "Lleva el anillo de Sarah Ladimor a la tumba de Morgan Ladimor.",
    ["T"] = "El amor de una hija",
  },
  [232] = {
    ["D"] = "La Sociedad Real de Boticarios necesita tu ayuda. Escucha bien esto, y puede que ambos podamos beneficiarnos con mi plan.$B$BEl boticario Keever, en la sala contigua, puede que esté... un poco tocado... pero su investigación promete dar muy buenos resultados, sobre todo si la condujera un investigador más inteligente. Afortunadamente, yo soy investigador. Quiero que lleves esta orden de compra a Alessandro Luca, el propietario de Cachivaches Luna Azul; él te dará un equipo de estudio. Cuando lo tengas, tráemelo y yo me encargaré de explicarte tu encomienda con todo detalle.",
    ["O"] = "Llévale la orden de compra a Alessandro Luca, que está en Entrañas.",
    ["T"] = "Una recado para la boticaria Zinge",
  },
  [233] = {
    ["D"] = "Mm, oye, tú no querrás hacerme un favor... ¿o sí? Hoy ha llegado un fajo de cartas a través del paso, pero no tengo tiempo para entregarlas.$B$BEstán todas dirigidas a Talin Ojoagudo. Lo encontrarás al oeste. Solo tienes que seguir el camino. Ha establecido su campamento en la orilla del lago helado.$B$B¿Qué respondes?",
    ["O"] = "Entrega el fajo de cartas a Talin Ojoagudo.",
    ["T"] = "Entrega de correo en el Valle de Crestanevada",
  },
  [234] = {
    ["D"] = "Si no recuerdo mal, el campamento de Grelin está al sureste, por el camino. Estará ansioso por recibir su correo.",
    ["O"] = "Entrega la carta a Grelin Barbablanca.",
    ["T"] = "Entrega de correo en el Valle de Crestanevada",
  },
  [235] = {
    ["D"] = "¡Amantes de la aventura, sabed que esta os espera en Vallefresno!$b$bLa Horda se está asentando en las tierras norteñas de Los Baldíos. ¡Queremos llevar la gloria a la Horda desde Puesto del Hachazo y Avanzada de la Ensanada de Zoram! Si deseáis probar vuestra valía, acudid allí. Nota: Senani Astatrueno, Puesto del Hachazo, al norte de Los Baldíos, busca amantes de la aventura para una gran cacería en Vallefresno.",
    ["O"] = "Habla con Senani Truenozón en Puesto del Hachazo, en Vallefresno.",
    ["T"] = "La Caza de Vallefresno",
  },
  [236] = {
    ["D"] = "A few weeks ago, I dispatched Watcher Callahan and a few others to the northern border of Duskwood to deal with the wolf infestation. I haven\'t heard word from him for quite some time. If he followed my instructions, he\'ll be camped out on the road north from Darkshire. $B$BIf it\'s on your way, please check up on him and inform him that I am awaiting a progress report from him.",
    ["O"] = "Speak with Watcher Callahan.",
    ["T"] = "Awaiting Word",
  },
  [237] = {
    ["D"] = "El montaraz Pedernal habla muy bien de ti, $r. Por eso he decidido confiarte una misión de extrema importancia. Tenemos que mantener a raya a los trogg invasores hasta que nuestros hermanos regresen de luchar por la Alianza.$b$bDirígete a las colinas del sur y mata a diez aplastacráneos y diez profetas Rompecantos. Eso nos hará ganar tiempo. Regresa cuando termines la misión.",
    ["O"] = "El montaraz Gravagau, de la torre de vigilancia sur, quiere que mates a 10 aplastacráneos y 10 profetas Rompecantos. Luego ve a informarle.",
    ["T"] = "En defensa de las tierras del rey",
  },
  [238] = {
    ["D"] = "Este equipo está listo, $n. No cabe duda de que a la boticaria Zinge le gustaría recibirlo lo antes posible. Llévaselo cuanto antes, y dile que le cargaré los costes del equipo a la Sociedad. De paso, por favor, hazle llegar mi más afectuoso saludo.$B$BBueno, no te quedes ahí. Esa es la vida de un simple $gmensajero:mensajera;, ¿no?",
    ["O"] = "Llévale el equipo de recogida de muestras de campo a la boticaria Zinge, que está en Entrañas.",
    ["T"] = "Una recado para la boticaria Zinge",
  },
  [239] = {
    ["D"] = "La plaza fuerte de la frontera occidental informa de un aumento de actividad de gnolls y ladrones. Solicitan que enviemos más soldados de Ventormenta, ¡pero no podemos prescindir de ninguno!$B$BTu ayuda nos vendría bien. Habla con el ayudante del alguacil Rainer en el Cuartel Arroyoeste y que te diga qué necesita.$B$BLa plaza fuerte está al oeste, bajando por el camino. Encontrarás un puente sobre un arroyo. Crúzalo. La plaza fuerte estará a la derecha.",
    ["O"] = "Ve al Cuartel de Arroyoeste y habla con el ayudante del alguacil Rainer.",
    ["T"] = "El Cuartel Arroyoeste necesita ayuda",
  },
  [240] = {
    ["D"] = "¡¡Y voilà!! ¡Croquetas de \"cangrejo\" à la Grual!",
    ["O"] = "Vuelve a donde Alterio con las croquetas de cangrejo.",
    ["T"] = "Entrega para Gato",
  },
  [241] = {
    ["D"] = "Description",
    ["O"] = "Log",
    ["T"] = "<TEST> HEY MISTER WILSON!",
  },
  [242] = {
    ["D"] = "Throm-ka, $c. There is no time to waste. Orcs of the Dragonmaw clan have fortified themselves in the valley to the south of here. $b$bThey have denied the Warchief\'s mandate, leaving us only one course of action. We will eliminate them so that they will no longer dirty the name of the Horde.",
    ["O"] = "Kill 8 Dragonmaw Raiders, 3 Dragonmaw Bonewarders, and a Dragonmaw Battlemaster.",
    ["T"] = "<UNUSED>",
  },
  [243] = {
    ["D"] = "Dirígete al Desierto de Tanaris, y allí comprueba la consistencia del tejido de los basiliscos, escórpidos y de las hienas autóctonas del lugar. Consigue muestras de tejido con el equipo de estudio que llevas; eso sí, hazte a la idea de que parte de las muestras serán inservibles. Necesito 8 muestras buenas de cada tipo de criatura.$B$BAdemás, los goblins de Gadgetzan tienen una fuente de energía que debe emplearse con este tipo de equipo modificado. Habla con el ingeniero jefe Silvaina y consigue una para el equipo.$B$BVuelve a hablar conmigo cuando la tengas.",
    ["O"] = "Ve a Gadgetzan y encuentra al ingeniero jefe Silvaina para que te explique cómo obtener una fuente de energía para el equipo de estudio.",
    ["T"] = "In situ",
  },
  [244] = {
    ["D"] = "Saludos, $c. Estoy patrullando esta parte del camino. De momento todo está en calma, pero he visto campamentos gnoll al norte y al este de aquí. $b$b¡Debes informar de esto en Villa del Lago! Preséntate ante el ayudante Feldon y dile lo de los gnolls. Seguro que te paga sueldo de rastreador. $b$bLo encontrarás al sur del puente a Villa del Lago.",
    ["O"] = "Preséntate ante el ayudante Feldon.",
    ["T"] = "Gnolls invasores",
  },
  [245] = {
    ["D"] = "Salve, $c. ¿Has visto esas arañas que han invadido la frontera occidental? Esas amenazas de ocho patas son demasiado para nosotros pero el Comandante no tiene suficientes hombres. No puede enviar a nadie tan lejos de Villa Oscura.$B$BOdio tener que pedirte esto pero, parece que tú podrías manejar a esos bichos. No puedo prometerte una gran recompensa, pero si nos ayudas a erradicar a esos arácnidos, encontraré la forma de compensarte.$B$BAh, y deja que te dé un consejo: evita su veneno a toda costa.",
    ["O"] = "Mata a 15 telarácnidas pigmeovenenosas y vuelve al Bosque del Ocaso a informar al vigilante Dodds.",
    ["T"] = "Las amenazas de ocho patas",
  },
  [246] = {
    ["D"] = "Andamos escasos de tropas, pero hay que hacer algo con esos gnolls.$b$bRastrea el sur de Crestagrana en busca de gnolls. Espíalos, mata a tantos como puedas y vuelve para informarme de cuántos son y si suponen una amenaza. Sus campamentos están por todo el camino meridional de Crestagrana. $b$bEl ejército de Ventormenta te recompensará, $N.",
    ["O"] = "Mata a 10 bastardos y 6 furtivos Crestagrana e informa al ayudante Feldon en Villa del Lago.",
    ["T"] = "Evaluar la amenaza",
  },
  [247] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "El fin de la Caza",
  },
  [248] = {
    ["D"] = "He estado observando la Torre de Ilgalar; Morganth está en conflicto con los orcos del Fuerte de Petravista. Desconozco los motivos, pero tú me ayudarás a descubrirlos.$B$BCoge otro Glifo de Azora y sube a la atalaya intacta que hay junto al fuerte.$B$BEncuentra un objeto en el que depositar el glifo. Un cofre o un tarro iría bien, un lugar en el que nadie se fije en él.",
    ["O"] = "Sube a la atalaya intacta que hay junto al Fuerte de Petravista. $b$bEncuentra un objeto en el que esconder el Glifo de Azora.",
    ["T"] = "Ver más allá",
  },
  [249] = {
    ["D"] = "Después de mucha observación, he descubierto los planes de Morganth. $b$bBusca la Guadaña de Elune, ¡y es posible que ya la haya encontrado! Si ese es el caso, pronto las Montañas Crestagrana y el Reino de Ventormenta sentirán su siniestro poder. Debes ir a la Torre de Ilgalar y enfrentarte a Morganth. $b$bDerrótalo y tráeme su colgante de la Sombra. El reino quedará en deuda contigo.",
    ["O"] = "Derrota a Morganth. $b$bLleva el colgante de la Sombra a Theocritus a la Torre de Azora, en el Bosque de Elwynn.",
    ["T"] = "Gran mago Doane",
  },
  [250] = {
    ["D"] = "Menos mal que alguien se interesa por el bienestar de la Presa. ¡Primero la destrucción del Puente Thandol y después el saqueo de Dun Modr! No dudo que el objetivo del siguiente ataque de los Hierro Negro será la Presa. Casi todos los agentes de seguridad se han destinado a luchar con la Alianza, incluido mi mejor inspector.$B$BPor eso te necesito, $n. He visto algunos zapadores Hierro Negro en la rampa oriental de la Presa. Investiga la zona y trae una pista.",
    ["O"] = "Investiga los alrededores de la rampa este de la Presa y lleva al ingeniero jefe Hinderweir alguna pista sobre lo que traman los Hierro Negro.",
    ["T"] = "Se aproxima una oscura amenaza",
  },
  [251] = {
    ["D"] = "Lleva la nota que me diste a Sirra Von\'Indi. Es un experto en idiomas y artes antiguas. Si alguien puede traducir este mensaje, ese alguien es él.",
    ["O"] = "Lleva la carta de Ello a Sirra Von\'Indi.",
    ["T"] = "Traduce la nota de Abercrombie",
  },
  [252] = {
    ["D"] = "¡Ay, ay! Si lo que dice esta nota es cierto, Abercrombie ha hecho algo... indescriptible.$B$BSerá mejor que le lleves la nota traducida a Ello.$B$BY date prisa. Temo que una maldición se acerca hacia nosotros...",
    ["O"] = "Lleva la carta traducida a Lord Ello Cerranegro.",
    ["T"] = "Una traducción para Ello",
  },
  [253] = {
    ["D"] = "El Embalsamador es el nombre de un personaje de uno de nuestros cuentos para niños. Era un alquimista bueno que enloqueció tras la muerte de su esposa Eliza. Para recuperarla, utilizó magia negra y colocó su propio corazón en el pecho de su mujer.$B$BEliza resucitó... pero con un hambre insaciable de carne humana y eso obligó al Embalsamador a mantenerla enterrada.$B$BSu corazón es su poder. Encuentra la tumba de su esposa, quítale el corazón y tráemelo a mí mientras aún lata. Hazlo y romperás su poder.",
    ["O"] = "Encuentra la tumba de Eliza.  Quítale el corazón del embalsamador y luego vuelve a Ello Cerranegro.",
    ["T"] = "La novia del embalsamador",
  },
  [254] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Escavar la tierra",
  },
  [255] = {
    ["D"] = "¡Hemos esperado dos meses a que Forjaz nos conceda unos cuantos soldados para la defensa, y tardarán otros dos meses en llegar! Ya es bastante malo que la ciudad esté desprotegida, pero la excavación y la presa son muy vulnerables.$b$bParece que no tengo elección. Necesito mercenarios.$b$b¿Qué tal tú? Creo que lo harás bien, $c. Un grupo de ogros se ha instalado al noreste del lago. ¿Qué tal si reclutas a unos cuantos tipos y os ocupáis de esos ogros?",
    ["O"] = "El magistrado Romonariz de Thelsamar te ha contratado para matar a 4 ogros, 4 brutos y 4 gorilas Mo\'grosh.",
    ["T"] = "Mercenarios",
  },
  [256] = {
    ["D"] = "Por orden del magistrado de Thelsamar:$B$BSe busca muerto a Chok\'sul, ogro culpable de dirigir los ataques contra la ciudad de Thelsamar, la Presa de las Tres Cabezas y la excavación.$B$BSe ofrece recompensa a quien presente al magistrado una prueba de la muerte de Chok\'sul.$B$BFue visto por última vez en el campamento ogro situado al noreste de Loch Modan.",
    ["O"] = "Mata a Chok\'sul y lleva su cabeza al magistrado Romonariz a Thelsamar.",
    ["T"] = "SE BUSCAN: Chok\'sul",
  },
  [257] = {
    ["D"] = "Crees que estás a la altura de Daryl el Audaz, ¿eh? ¡No lo creo! Por supuesto, puedes ponerte a prueba. Te propongo un desafío que seguro estará por encima de tus posibilidades, así que no te sientas mal si no eres capaz de superarlo. ¿Qué tal si acabas con algunas de esas bestias? Si lo consigues en 15 minutos, te daré mi arco o mi pistola.",
    ["O"] = "Mata 6 águilas ratoneras montesas y ve a ver a Daryl el Jovenzuelo a la Cabaña del Errante en menos de 15 minutos.",
    ["T"] = "La fanfarronada de un cazador",
  },
  [258] = {
    ["D"] = "Así que eres $n, ¿eh? Te llena de orgullo haber pasado la primera prueba, ¿verdad? Como dije antes, tampoco hay para tanto.$b$bA ver si te atreves a cazar un jabalí. Esta no será una cacería como las del Valle de Crestanevada. Será mejor que tengas cuidado. Esta vez te doy 12 minutos.$b$bNo te sientas mal si no lo consigues. ¡Si lo haces, te daré mi camisa!$b$b¿Te he contado cómo me hice mi famosa cicatriz? ¿No? Fue hace dos años...",
    ["O"] = "Mata 5 jabalís monteses mayores y ve a ver a Daryl el Jovenzuelo a la Cabaña del Errante en menos de 12 minutos.",
    ["T"] = "El reto de un cazador",
  },
  [259] = {
    ["D"] = "Hello there, $n. I\'ve heard much about your exploits, and must say I\'m honored to meet you. I don\'t suppose you\'ve heard the story of Ol\' Kelt? Ah, well, Ol\' Kelt--Kelt Thomasin, that is--was once considered one of the finest hunters in all the realm. There wasn\'t much he hadn\'t done, but then, one day, he became obsessed with an ancient story... a myth, really. Well, as the stories go, a huge threshadon lives in the waters of the lake. Hogwash, if you ask me. If you\'re interested, you might talk to Kelt.",
    ["O"] = "Talk to Kelt Thomasin.",
    ["T"] = "<UNUSED>",
  },
  [260] = {
    ["D"] = "Now, this will no doubt convince you that I am, as they say--and don\'t deny it, I know what they say--of an addled mind, obsessed with an impossible legend. An old dwarven legend, it is, of an enormous monster that lives in the depths of the Loch. I spent a good bit of my life trying to track it down, and I believe I got close, but alas, I fear old age has caught up to me. If you would like to try hunting down the monster of the Loch, bring me some treshadon teeth and claws.$B$B<He smiles mysteriously.>",
    ["O"] = "Collect 5 Threshadon Teeth and 5 Threshadon Claws, then bring them to Kelt Thomasin.",
    ["T"] = "<UNUSED>",
  },
  [261] = {
    ["D"] = "Te hablaré con franqueza. Estamos en guerra con la Plaga. Es un mal que corrompe a nuestra gente e infesta nuestra tierra. Hay que detenerlo antes de que nos arrolle y arrastre a nuestro mundo a las sombras. Los miembros de la Cruzada Escarlata hemos jurado combatir a la Plaga en cuerpo y alma.$B$BSi estás con nosotros, reúne valor y prueba tu lealtad: destruye a los devastadores no-muertos de Desolace. Búscalos en el Valle de los Huesos, al sur.$B$BHazlo y la Cruzada te acogerá con los brazos abiertos.",
    ["O"] = "Destruye 30 devastadores no-muertos y luego ve a buscar al hermano Antón a Punta de Nijel.",
    ["T"] = "El sendero Escarlata",
  },
  [262] = {
    ["D"] = "¡Debí adivinar quién era esa figura que vi en las sombras la noche en que mi familia fue asesinada!$B$BPor favor, $n, has demostrado ser $gun hombre:una mujer; de recursos. Te lo ruego, encuentra a ese ser misterioso. ¡He de saber qué secretos guarda!$B$BNo sé nada que tú no sepas ya pero, ten, coge el libro que encontraste en mi granja. Quizás alguien del pueblo pueda decirte a quién pertenece el libro.",
    ["O"] = "Llévate el libro que encontraste en la granja de Sven a Villa Oscura y enséñaselo a sus habitantes.",
    ["T"] = "La figura en las sombras",
  },
  [263] = {
    ["D"] = "Los montaraces me dicen que eres valiente y capaz, $N. Nos hace falta alguien así. La situación con los trogg no mejora. La reserva está en combate; estamos solos. Pero con tu experiencia como $c, la cosa va a cambiar.$b$bLiquida a diez chamanes y diez quebrantahuesos Rompecantos. A ver si los trogg aprenden a temerte.",
    ["O"] = "El montaraz Pedernal, de la torre de vigilancia sur, quiere que mates a 10 chamanes y 10 quebrantahuesos Rompecantos.",
    ["T"] = "En defensa de las tierras del rey",
  },
  [264] = {
    ["D"] = "¡Sucia escoria! Me abandona por su maldita cruzada porque \"la Luz es lo más importante que tenemos contra la amenaza que representan los no-muertos.\"$B$B¡¿Y sus hijos?! ¡¿Y YO?! Noche tras noche me quedaba esperando por él... ¡siempre en segundo lugar después de sus condenadas obligaciones!$B$BBueno, ¡mira lo que te depara la \"justicia\", $n! ¡Él está muerto y yo tengo precisamente lo que estaba intentando detener!$B$BCoge esto y ponlo encima de su tumba en El Sepulcro. ¡No quiero tener nunca más nada que ver con eso... ni con él!",
    ["O"] = "Coloca el colgante de Clarice sobre la tumba de Yuriv en Argénteos.",
    ["T"] = "Hasta que la muerte nos separe",
  },
  [265] = {
    ["D"] = "Te devuelvo tu libro, $n. Y te deseo mucha suerte. Si preguntas a la gente del pueblo, quizás puedas conseguir más pistas.$B$BEmpezaré por darte yo una: aunque el autor de este libro no es de Villa Oscura, el libro fue comprado aquí.",
    ["O"] = "Averigua si otros habitantes tienen pistas sobre la figura misteriosa.",
    ["T"] = "La búsqueda de la figura continúa",
  },
  [266] = {
    ["D"] = "Ten tu libro. Y buena suerte con tus pesquisas.$B$BQuizás quieras continuar la búsqueda en la posada. Si la persona a la que buscas pasó algo de tiempo en el pueblo, es probable que alguien le viera allí.",
    ["O"] = "Busca pistas sobre la figura misteriosa en la posada.",
    ["T"] = "Búsqueda en la posada",
  },
  [267] = {
    ["D"] = "$C, puede que sepas que los trogg amenazan las tierras de los enanos. Ahora que la Reserva de Forjaz está combatiendo por la Alianza, nos hemos quedado con una fracción de las fuerzas defensivas que necesitamos. Mi regimiento está destinado a vigilar la puerta. No podemos abandonar el puesto porque tememos una invasión.$b$bHay que ahuyentar a esos malditos trogg que acechan en las colinas. Si te ves capaz, organiza un asalto a los trogg. Tráeme ocho petrodientes de trogg como prueba.",
    ["O"] = "Lleva 8 petrodientes de trogg al capitán Rugelfuss a la torre de vigilancia sur.",
    ["T"] = "La amenaza trogg",
  },
  [268] = {
    ["D"] = "El libro que encontraste no estaba acabado. Tuve que dejarlo en la granja de Sven cuando huí...$B$BTen. Lleva mi diario completo a Sven. Describe con más detalle lo que ocurrió en su granja.$B$BAunque este diario ha sido mi única compañía todas estas semanas, si renunciando a él me evito tener que afrontar la ira de Sven... ¡te lo doy encantado!",
    ["O"] = "Lleva a Sven el diario completo de Alterio.",
    ["T"] = "Entrega para Sven",
  },
  [269] = {
    ["D"] = "Ve a la ciudad de Ventormenta y habla con el obispo Farthing. Búscalo en la Catedral de la Luz. Dale el diario del Gato y dile que deseas enfrentarte a Morbent Vil.$B$BAunque Ventormenta ha abandonado a Villa Oscura, Farthing es un alma buena y sabia. Puede que te ayude a salvarnos.$B$BTu misión es peligrosa, pero date prisa, $N. Las sombras del Bosque del Ocaso son más cada vez más oscuras.",
    ["O"] = "Entrega el diario completo de Alterio al obispo Lontananza de Ventormenta.",
    ["T"] = "En busca de sabiduría",
  },
  [270] = {
    ["D"] = "Años atrás, la Tercera Flota de Kul\'Tiras se hundió en la costa de Los Humedales, una expansión pantanosa al norte del reino enano de Forjaz. Abordo del barco había un cargamento de hierro Forjaluz.$B$BEste metal es muy valioso para nosotros, porque los objetos forjados con él, golpean con toda la verdad sagrada. Si te vas a enfrentar a Morbent Vil, necesitarás un arma así.$B$BVete al Puerto de Menethil y habla con mi colega enano Glorin Frenteacerada. Si es posible encontrar ese metal perdido, él sabrá dónde.",
    ["O"] = "Vete al Puerto de Menethil en Los Humedales y habla con Glorin Frenteacerada.",
    ["T"] = "La flota maldita",
  },
  [271] = {
    ["D"] = "Supongo que querrás vengarte de ese bocazas. Me duele admitirlo, pero Daryl tiene su talento.$b$bHace un año, toda la Cabaña del Errante andaba detrás de un oso enorme, pero nadie conseguía cazarlo. Y Daryl decidió intentarlo. Lo único que consiguió fue esa fea cicatriz.$b$bSi matas al oso dejarás a todo el mundo boquiabierto, incluido Daryl.$b$bEl oso se llama Ol\' Sooty y vive en las colinas de Thelsamar.",
    ["O"] = "Mata a Ol\' Sooty y llévale una prueba a Daryl el Jovenzuelo en la Cabaña del Errante.",
    ["T"] = "La venganza de Vyrin",
  },
  [272] = {
    ["D"] = "En tu segunda prueba debes encontrar las dos mitades del colgante del león marino. Una obtiene su poder de la agilidad del león marino cuando se desliza en el agua, la otra lo obtiene de su increíble capacidad de resistencia. Los druidas se alimentan de ambos rasgos para asumir su forma acuática.$b$bHabla con la gente de Claro de la Luna para averiguar dónde están las mitades y tráemelas para que las unamos. Luego llevarás el colgante a Dendrite Rafagaestelar.",
    ["O"] = "Busca el colgante de Agilidad acuática y el colgante de Resistencia acuática.  Habla con la gente de Claro de la Luna para conseguir pistas sobre la ubicación de los colgantes. $b$bForma el colgante del león marino con los otros dos cuando estés cerca del Santuario de Remulos. $b$bLleva el colgante completo a Dendrite Rafagaestelar a Amparo de la Noche, en Claro de la Luna.",
    ["T"] = "La prueba del león marino",
  },
  [273] = {
    ["D"] = "Pero aún hay tiempo, ayúdanos a buscar a Huldar. No puede estar muy lejos; aunque son fuertes, no creo que Miran y Saean puedan ir muy rápido cargando con esos barriles.$B$BEstán siguiendo la ruta normal, hacia el sur y siguiendo la curva de Loch Modan.$B$BSi todavía no han llegado, los encontrarás.",
    ["O"] = "Habla con Huldar.",
    ["T"] = "Aprovisionar la excavación",
  },
  [274] = {
    ["D"] = "Esto no es un explosivo normal y corriente. Mira estos cristalitos de plata. ¡Y este olor tan peculiar! Está clarísimo que esto es polvo de seforio. Es bastante inofensivo, pero si se moja, podría borrar Forjaz del mapa.$b$bPara neutralizar la reacción química necesito cuatro ingredientes: veneno de rondador, cristal de Mo\'Grosh triturado, una lágrima de crocolisco y este coloide desactivador. ¡Avisa a Hinderweir antes de que sea tarde!",
    ["O"] = "Ashlan Risapétrea quiere que informes de la naturaleza de los polvos al ingeniero jefe Hinderweir.",
    ["T"] = "Se aproxima una oscura amenaza",
  },
  [275] = {
    ["D"] = "No sé la causa pero una auténtica podredumbre está creciendo en Los Humedales. Han aparecido Trepadores del cubil. Son burbujas enfermas y febriles en el país, y para detener la fiebre deben poder a asentarse de nuevo en los pantanos.$B$BLos Trepadores del cubil se arrastran por los canales de Los Humedales.",
    ["O"] = "Mata 12 Trepadores del cubil y regresa con Rethiel el Guardaverde en Los Humedales.",
    ["T"] = "Ampollas en la tierra",
  },
  [276] = {
    ["D"] = "Los gnolls Pellejomusgo han vivido en paz en Los Humedales durante mucho tiempo, pero ahora su número está creciendo. Sus pies pisotean las flores y arbustos del pantano y sus hachas no dejan de talar árboles para alimentar sus fuegos.$B$BHan dejado de estar en equilibrio con la tierra. Para que sobrevivan Los Humedales, debemos reducir a los gnolls.$B$BMata a 15 gnolls Pellejomusgo y a 10 bastardos Pellejomusgo en el Sur, junto a la puerta enana de Algaz. Cuando lo hayas hecho vuelve aquí.",
    ["O"] = "Mata 15 gnolls Pellejomusgo y 10 bastardos Pellejomusgo y regresa con Rethiel el Guardaverde en Los Humedales.",
    ["T"] = "Patas sucias",
  },
  [277] = {
    ["D"] = "Los Pellejomusgo están talando los árboles de Los Humedales, y utilizan su carne para darse calor. ¡No seguiré sufriendo este dolor!$B$BArrebata a los Pellejomusgo los pedernales primitivos que utilizan para hacer fuego. Todos los Pellejomusgo llevan esos pedernales primitivos, excepto los pequeños gnolls y bastardos Pellejomusgo.",
    ["O"] = "Lleva 9 pedernales primitivos a Rethiel el Guardaverde.",
    ["T"] = "No al fuego",
  },
  [278] = {
    ["D"] = "Ponte a buscar los ingredientes de inmediato, $N. Puedes sacar el veneno de las arañas de Loch Modan. También encontrarás lágrimas de crocolisco allí. El cristal de Mo\'Grosh ya será más difícil de conseguir. Los extraen los ogros del noreste, pero son muy raros de encontrar.$b$b¡Tenemos que prepararnos para rechazar un ataque de los Hierro Negro! Vuelve cuando tengas todos los ingredientes.",
    ["O"] = "El ingeniero jefe Hinderweir quiere que le lleves veneno de rondador, 1 cristal de Mo\'grosh y 1 lágrima de crocolisco.",
    ["T"] = "Se aproxima una oscura amenaza",
  },
  [279] = {
    ["D"] = "Los murlocs están saliendo de las aguas profundas y construyendo sus poblados en la costa. Acosan a nuestros pescadores y mercaderes y hay que detenerlos.$B$BUno de esos murlocs, Engullidor, merodea el camino en compañía de otros murlocs Branquiazul y acosan a los mercaderes, refugiándose luego en la seguridad de las casuchas múrloc cercanas. Nuestros mercaderes están en peligro y pagaremos por su seguridad.$B$BEngullidor merodea la cala del río al norte de aquí. Da con él, mata a los suyos y tráeme su cabeza.",
    ["O"] = "Mata a 12 múrlocs Branquiazul.$B$BMata a Engullidor y coge su cabeza.$B$BLleva la cabeza de Engullidor a Karl Boran en Puerto de Menethil.",
    ["T"] = "Zarpas de las profundidades",
  },
  [280] = {
    ["D"] = "Antes he visto a unos cuantos insurgentes Hierro Negro nadando hacia la Presa con un barril grande. ¡No había ningún montaraz para detenerlos! ¡Creo que han colocado el seforio en la base de la Presa y va a explotar de un momento a otro! Voy a combinar todos los ingredientes para crear la mezcla neutralizadora. Aquí tienes.$b$b¡Ahora tienes que verter la mezcla en el barril! ¡Date prisa!",
    ["O"] = "El ingeniero jefe Hinderweir quiere que nades hasta la base de la presa, localices el barril de pólvora y disuelvas en él la mezcla neutralizadora para evitar que explote.",
    ["T"] = "Se aproxima una oscura amenaza",
  },
  [281] = {
    ["D"] = "No hace mucho los múrlocs atacaron una nave de mercaderes, La princesa ciega. Aunque el barco escapó intacto, esas inmundas criaturas sacaron gran parte de su cargamento por la borda, incluida una valiosa estatuilla de Terenas Menethil. Queremos recuperar la estatuilla.$B$BRegistra las casuchas múrloc de la costa en busca de cajones, barriles o cualquier otra evidencia del cargamento perdido. Encuentra la estatuilla y vuelve a verme.",
    ["O"] = "Busca los cajones y barriles en los campamentos múrloc de la costa.",
    ["T"] = "Recuperación de bienes",
  },
  [282] = {
    ["D"] = "Tenía intención de enviar a mi aprendiz a que entregara a mi hermano Senir mi informe, pero por mi tranquilidad de espíritu... preferiría confiarlo a manos más seguras. Si es que no te importa, claro.$B$BMmm... tendrás que atravesar el túnel para llegar a Kharanos.$B$BHabla con el montaraz Thalos antes de adentrarte en el túnel. Ahora está infestado de troggs.$B$BSigue la carretera para regresar a Yunquemar y luego ve hacia el este hasta llegar al túnel. Thalos no andará lejos.",
    ["O"] = "Habla con el montaraz Thalos.",
    ["T"] = "Las observaciones de Senir",
  },
  [283] = {
    ["D"] = "El barril crepita.",
    ["O"] = "Parece que la mezcla neutralizadora funciona. Ve a darle la buena noticia al ingeniero jefe Hinderweir.",
    ["T"] = "Se aproxima una oscura amenaza",
  },
  [284] = {
    ["D"] = "Registras a fondo el grupo de casuchas múrloc y no encuentras ni un rastro de la Estatuilla de Menethil.$B$BTal vez encuentres más pistas en las casuchas del Norte y del Noreste.",
    ["O"] = "Busca la Estatuilla de Menethil.",
    ["T"] = "La búsqueda continúa",
  },
  [285] = {
    ["D"] = "Este grupo de casuchas de múrloc, tras inspeccionarlo a fondo, no revela nada.$B$BPe... pero estás $gseguro:segura; de que la próxima pista que encuentres será muy fructífera.$B$B¡La Estatuilla de Menethil debe estar en alguna de las casuchas del Norte!",
    ["O"] = "Sigue buscando la Estatuilla de Menethil en las casuchas de los múrlocs.",
    ["T"] = "Inspecciona más casuchas",
  },
  [286] = {
    ["D"] = "Después de sacar el barril de toda la mugre y abrirlo, descubres una figurilla plateada empaquetada entre paja. Representa a un joven paladín de pelo largo, con un martillo alzado.$B$B¡Tiene que ser la Estatuilla de Menethil! Debes regresar con Karl Boran en el Puerto de Menethil.",
    ["O"] = "Lleva la Estatuilla de Menethil a Karl Boran.",
    ["T"] = "Devuelve la Estatuilla",
  },
  [287] = {
    ["D"] = "Llevo mucho retraso con mi informe y me vendría bien tu ayuda, $n. Conseguí averiguar dónde se esconden los trols, pero vi tantos de ellos que me dio miedo entrar.$B$BPor eso... necesito que entres en la cueva, eches un vistazo y mates a algunos de los trols. Luego vuelves aquí.$B$BSigue la carretera norte que sale de Kharanos, cuando llegues al puente, sigue el río helado hacia el oeste hasta que llegues al Lago Glacial. En la orilla oeste encontrarás Las Birras. La cueva está al suroeste del poblado.",
    ["O"] = "Explora el Poblado Peloescarcha y mata a 5 rebanacabezas Peloescarcha para Senir Barbablanca de Kharanos.",
    ["T"] = "Poblado Peloescarcha",
  },
  [288] = {
    ["D"] = "En su día fui el primero de abordo del Dueña de las olas, una nave de alto bordo de la Tercera Flota de Kul Tiras. La flota maldita. Maldita, porque en su último viaje fue atacada por dragones rojos. ¡Dragones controlados por orcos!$B$BNo teníamos ninguna oportunidad contra el fuego de los dragones... y nuestra flota ardió y se hundió en esta costa. Soy el único superviviente de todas esas desgraciadas naves...$B$B... ¡Oh me va a estallar la cabeza! Tengo que beber algo fresco si quiero acabar con esta resaca y seguir con mi historia.",
    ["O"] = "Compra un cántaro de hidromiel para el primer oficial Fistimos.",
    ["T"] = "La Tercera Flota",
  },
  [289] = {
    ["D"] = "<Algo recuperado Fistimos sigue contando su historia...>$B$BSoy el único superviviente de la Tercera Flota de Kul Tiras, naufragada en la costa del Norte. ¡Pero aunque soy el único miembro de la flota que sigue con vida, no soy el único que sigue por aquí! Uno de nuestros barcos ocultaba un gran y terrible tesoro y su espeluznante poder tiene atrapadas las almas de mis hermanos entre los descompuestos cascos de los barcos.$B$BSi quieres liberar a algunos de mis hermanos tal vez puedas romper su maldición.",
    ["O"] = "Mata 13 marineros malditos, 5 marinos malditos y al primer oficial Esnelim. Lleva la caja de tabaco de Enelim al primer oficial Fistimos en Puerto de Menethil.",
    ["T"] = "La tripulación maldita",
  },
  [290] = {
    ["D"] = "Para romper la maldición y liberar a todos mis hermanos, hay que recuperar y purificar la... cosa... que guardaba mi barco. No sé lo que era, pero sé que estaba almacenada en la caja fuerte del barco. El capitán Halyndor era el único que tenía la llave.$B$BMi buen capitán sigue presente en mis sueños. Sé que sigue en el barco, El Intrépido. Hazte con la llave y utilízala para abrir la caja fuerte de El Intrépido.$B$BEntonces, cuando tengas esa maldita cosa, llévasela al sacerdote Glorin Frenteacerada en la ciudad.",
    ["O"] = "Obtén la caja de la caja fuerte de El intrépido del capitán Halyndor.$B$BUtiliza la llave para abrir la caja fuerte de El intrépido.",
    ["T"] = "Purga de la maldición",
  },
  [291] = {
    ["D"] = "¡Fantástico! Lleva mi informe al senador Barin Rocarroja. Es un tipo muy agrio; procura que no te contagie su desbordante alegría. Está en Forjaz, en la cámara en la que el rey Magni celebra sus audiencias.$B$B¿Que no sabes llegar a Forjaz? Sigue la carretera norte que sale de Kharanos, cruza el puente, sigue el camino del este que verás bordeado de estandartes, y que sube por la ladera de la montaña.$B$BPor cierto... ¿podrías no mencionar eso de que me has ayudado? No me gustaría que pensaran que no trabajo duro, ya me entiendes.",
    ["O"] = "Entrega el informe de Senir al senador Barin Rocarroja de Forjaz.",
    ["T"] = "Los informes",
  },
  [292] = {
    ["D"] = "Al abrir el cofre descubres un orbe ennegrecido, que parpadea con una luz espeluznante. Es el tesoro del que hablaba Fistimos: un Ojo maldito de Paleth.$B$BLleva el Ojo maldito de Paleth al sacerdote enano Glorin Frenteacerada, en el Puerto de Menethil. Pregúntale como eliminar la maldición del Ojo.",
    ["O"] = "Lleva el Ojo maldito de Paleth a Glorin Frenteacerada.",
    ["T"] = "El ojo de Paleth",
  },
  [293] = {
    ["D"] = "No puedo borrar la maldición del ojo de Paleth. Los ojos fueron creados por sacerdotes humanos, así que tendrás que ir a Ventormenta para encontrar a alguien que pueda ayudarte. Habla con el arzobispo Benedictus. Está al frente de la Catedral de la Luz de Ventormenta. El podrá ayudarte.$B$BEso espero, por tu bien.",
    ["O"] = "Lleva el Ojo maldito de Paleth al arzobispo Benedictus. Benedictus se encuentra en la Catedral de la Luz, en la ciudad de Ventormenta.",
    ["T"] = "Limpieza del ojo",
  },
  [294] = {
    ["D"] = "La situación es grave, ciertamente. Cuando descubrimos estos huesos acudieron los raptores, atraídos por ellos. Esas bestias inmundas mataron a mis hermanos y nos atraparon a mí, a Merrin y al pobre prospector.$B$BLimpia Los Humedales de esos raptores, $n. Encontrarás raptores jaspeados y estridadores jaspeados al oeste de este risco. Mata 10 ejemplares de cada, si puedes.$B$BSerá un buen comienzo para la venganza que he planeado.",
    ["O"] = "Ormer Trenzaferro de la Excavación de Whelgar quiere que mates 10 estridadores jaspeados y 10 raptores jaspeados.",
    ["T"] = "La venganza de Ormer",
  },
  [295] = {
    ["D"] = "Ha llegado el momento de que esos pavorosos raptores lamenten su sed de sangre. Aquí debajo podrás encontrar decenas de segadores jaspeados y tajobuches jaspeados. Haz pagar todas sus deudas a esas terribles criaturas matando a 10 de cada clase.",
    ["O"] = "Ormer Trenzaferro quiere que mates 10 raptores segadores jaspeados y 10 raptores tajobuches jaspeados y que vuelvas a verle en la Excavación de Whelgar.",
    ["T"] = "La venganza de Ormer",
  },
  [296] = {
    ["D"] = "Mientras estabas ahí abajo pude observar que una de esas bestias destacaba sobre el resto. Era más grande y amenazadora. Apuesto a que es la que condujo hasta aquí al resto para atacar la excavación.$B$BTe pido una última tarea, $n. Hay que hacer justicia con ese Sarldente, y de acuerdo a la gravedad de sus crímenes, justicia no significa otra cosa que pena de muerte. Tráeme una de sus garras como prueba de su muerte.",
    ["O"] = "Ormer Trenzaferro de la Excavación de Whelgar quiere que mates a Sarldente y que le lleves una de sus garras cuando completes la tarea.",
    ["T"] = "La venganza de Ormer",
  },
  [297] = {
    ["D"] = "Justo antes de que los trogg invadieran la excavación, habíamos descubierto una gran cantidad de ídolos tallados muy extraños. Pero no pudimos estudiarlos porque los trogg nos echaron de las ruinas. Parece que los ídolos tienen un efecto extraño en los trogg. ¡Se ponen como locos!$b$bTráeme ocho ídolos; quiero estudiarlos ¡y quiero quitárselos a los trogg! Los trogg que han invadido la excavación tienen los ídolos.",
    ["O"] = "Lleva 8 ídolos de piedra tallada a Magmar Tallavil.",
    ["T"] = "Recoger ídolos",
  },
  [298] = {
    ["D"] = "Tengo que enviar un informe a Thelsamar. ¡Eso, e insistir en mi pedido de pólvora! Este es el informe. Llévaselo a Jern Cornamenta, es nuestro contacto en Thelsamar.$b$bNo es un encargo difícil, pero no tengo a nadie más para hacerlo.",
    ["O"] = "Lleva el informe de Vetaferro a Jern Cornamenta a Thelsamar.",
    ["T"] = "Un informe sobre el avance de la excavación",
  },
  [299] = {
    ["D"] = "Justo antes de la invasión descubrí una tablilla, era la llamada piedra Goaz. El texto traducido se interrumpe en cuatro puntos. Se habla de un \"plan divino\" y de una \"profecía maldita.\"$b$bTemo que nos estemos quedando sin tiempo. Soy demasiado viejo para ir a enfrentarme con esas bestias. Pero tú eres fuerte.$b$bRegistra la excavación y encuentra los 4 fragmentos perdidos de la tablilla: Ados, Modr, Golm y Neru. Búscalos en artefactos antiguos o donde veas tierra suelta. ¡Tráemelos y podré empezar a descifrar este misterio!",
    ["O"] = "El prospector Whelgar quiere que te adentres en la excavación en busca de los 4 fragmentos de tablilla perdidos: Ados, Modr, Golm y Neru.",
    ["T"] = "Desvelando el pasado",
  },
  [301] = {
    ["D"] = "Necesitamos autorización para llevar más pólvora a Vetaferro. Lleva este informe a la oficina central de la Liga de Exploradores, en Forjaz.$b$bAquí tienes el informe. Dáselo al prospector Pico Tormenta y no olvides agacharte en cuanto lo hagas. Pico Tormenta tiene muy mal genio y no le va a gustar lo lento que va Vetaferro.$b$bLo encontrarás en la Asamblea de Exploradores.",
    ["O"] = "Lleva el informe de Vetaferro al prospector Pico Tormenta.",
    ["T"] = "Informe a Forjaz",
  },
  [302] = {
    ["D"] = "¡Si Vetaferro necesita más pólvora, por Magni que la va a tener! Ya le he enviado la autorización a Jern Cornamenta a Thelsamar. Él preparará el envío para Vetaferro.$B$BY otra cosa: quiero que te ocupes personalmente de que la pólvora llega a su destino. El último envío no llegó, así que mantén los ojos abiertos.",
    ["O"] = "Habla con Jern Cornamenta en Thelsamar.",
    ["T"] = "Pólvora para Vetaferro",
  },
  [303] = {
    ["D"] = "La guerra arde aquí en casa y en el extranjero. Mientras los grandes soldados enanos dan sus vidas en tierras lejanas en nombre de la Alianza, nuestras propias tierras caen víctimas de la depravación de los enanos Hierro Negro. Arrasaron el Puente Thandol y Dun Modr pronto caerá.$b$bTe necesitamos para reunir fuerzas y hacer retroceder el frente. Si puedes matar a 15 enanos Hierro Negro, 5 tuneladores, 5 saboteadores y 5 demoledores, tal vez la reserva consiga llegar a tiempo para terminar la ofensiva.$B$B¡Date prisa!",
    ["O"] = "Motley Garmason de Dun Modr quiere que mates 15 enanos Hierro Negro, 5 tuneladores Hierro Negro, 5 saboteadores Hierro Negro y 5 demoledores Hierro Negro.",
    ["T"] = "La guerra contra los Hierro Negro",
  },
  [304] = {
    ["D"] = "Muchos de mis soldados murieron en la batalla de Dun Modr. Mi propio hermano murió cuando fue destruido el Paso Oeste del Puente Thandol. Sí señor, los enanos Hierro Negro son una raza diabólica. A partir de lo que Roggo pudo reunir, el líder de esta banda de matones en Los Humedales es un brujo llamado Balgaras el Hediondo. Pero es un auténtico cobarde. Envía a sus soldados para que cumplan sus órdenes y él se queda escondido, evitando el peligro.$b$bSi puedes encontrar a esa escoria escurridiza, mátalo y tráeme su oreja. Sabré recompensarte, $c.",
    ["O"] = "Mata a Balgaras el Hediondo y lleva su oreja a Largatrenza el Nefasto a las afueras de Dun Modr.",
    ["T"] = "Una misión hedionda",
  },
  [305] = {
    ["D"] = "¡Oh, debes ser $gel rastreador enviado:la rastreadora enviada; por Largatrenza! No puedes imaginar lo preocupado que he estado.$b$bDurante un mes, mi mujer, Merrin, me ha estado enviando cartas cada semana. Forma parte del equipo de la Excavación de Whelgar, pero hace casi dos semanas que he dejado de recibir sus cartas. ¡Seguro que algo va mal!$b$bLargatrenza dijo que si no recibíamos noticias del equipo enviaría a un rastreador para comprobar cómo va todo. Supongo que tú eres ese explorador, $N. ¡Busca a Merrin en la excavación, al Este!",
    ["O"] = "Tarrel Tejepiedra quiere que viajes a la Excavación y que contactes con Merrin Tejepiedra.",
    ["T"] = "La búsqueda del equipo de excavación",
  },
  [306] = {
    ["D"] = "¡Fue horrible! Estábamos desenterrando unos extraños artefactos cuando dimos con estos grandes huesos.$B$BAntes de poder saber qué sucedía, los raptores jaspeados inundaron la excavación. Se perdieron muchas vidas.$B$BOrmer... tan valiente... nos salvó al prospector y a mí. Nos defendió mientras corríamos a buscar refugio. Ahora estamos atrapados y todo nuestro trabajo está perdido.$B$BToma, lleva esta nota a mi querido Tarrel. En ella le explico cuál es nuestra situación.",
    ["O"] = "Lleva a Tarrel Tejepiedra la nota de Merrin.",
    ["T"] = "La búsqueda del equipo de excavación",
  },
  [307] = {
    ["D"] = "La Mina de Fuenteplata, que cae al este, se agotó hace tiempo. La Liga de Mineros la usaba como almacén, pero ahora se han instalado unos kobolds allí, ¡con esas magníficas herramientas enanas a su alcance!$b$bLos expulsaremos, pero la Liga quiere que vaya alguien a recoger el material antes de que lleguemos los soldados a romperlo todo. Es muy arriesgado; será mejor que lleves compañía.$b$bEl material está en unas cajas con la marca de la Liga de Mineros. Suerte.",
    ["O"] = "Ve a la Mina de Fuenteplata y recoge 4 cargas de equipamiento de minero. $b$bLuego ve a ver al montaraz Pico Tormenta.",
    ["T"] = "Zarpas asquerosas",
  },
  [308] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Distraer a Jarven",
  },
  [309] = {
    ["D"] = "¿Vamos, $n?$b$bLo primero es llevarle la pólvora a Vetaferro. Es una carga muy pesada y esta zona es peligrosa. Además, quién sabe qué más me tienen preparado los Hierro Negro.$b$bMe tranquiliza bastante que vengas conmigo.",
    ["O"] = "Asegúrate de que Miran y la pólvora llegan a la excavación y luego informa al prospector Vetaferro.",
    ["T"] = "Proteger el envío",
  },
  [310] = {
    ["D"] = "Tengo que reconocer que esos Cebatruenos preparan unas bebidas estupendas. ¡Pero hay que bajarles los humos! ¡La suya no es la única bebida decente del mundo! Quizás tú puedas ayudarme a darles una lección...$B$BTen, coge este barril de brebaje de Cebadiz. Cuélate en el sótano de la Destilería Cebatruenos de Kharanos y cámbialo por uno de sus barriles de Cerveza del Trueno. ¡Y ya veremos qué brebaje les gusta más a los clientes!$B$BY si encuentras guardias en Los Baldíos, intenta distraerlos...",
    ["O"] = "Ve al sótano de la Destilería Cebatruenos de Kharanos y sustituye un barril de cerveza del Trueno con uno de brebaje de Cebadiz.",
    ["T"] = "Cerveza rivales",
  },
  [311] = {
    ["D"] = "Ya has dejado el barril del brebaje de Marleth en el almacén de la destilería de Kharanos y recoges un barril de Cerveza del Trueno como prueba de que has cumplido...",
    ["O"] = "Lleva el barril de cerveza del Trueno a Marleth Cebadiz a Las Birras.",
    ["T"] = "Vuleve con Marleth",
  },
  [312] = {
    ["D"] = "Tenía carne seca para un mes, salada y bien guardada para la estación fría. ¡Estaba bajo llave para que los osos no se la comieran! Pero cuando salí a cazar ciervos, esa bestia, el Viejo Barbahielo, se las arregló para romper la cerradura. Eso sí, no podrá con el torio, seguro. Claro que yo me moriré de hambre si no recupero mi carne.$B$BSeguro que tú te mueves con más sigilo que yo. ¿No podrías ir a recuperar mi alijo? Con esta pierna coja no conseguiré entrar y salir de esa cueva con suficiente rapidez.",
    ["O"] = "Ve a la cueva del Viejo Barbahielo y recupera la carne seca de Tundra MacGrann de la caja fuerte robada.",
    ["T"] = "La reserva robada de Tundra MacGrann",
  },
  [313] = {
    ["D"] = "No todo el mundo puede conducir una máquina de asedio. Hay que tener brazos de hierro y nervios de acero. Yo, por suerte ¡tengo ambas cosas! ¿Y tú? ¿Te crees a la altura? ¿Quieres demostrármelo?$B$BBien... Estoy intentando hacer algo más cómoda mi máquina, Potenciatrol. ¿Qué tal si me consigues unas cuantas crines de wendigo de El Cubil Pardo, al oeste de Kharanos? ¡Serían una alfombrilla estupenda para mi Potenciatrol!",
    ["O"] = "Reúne 8 crines de wendigo y llévaselas al piloto Roscapiedra.",
    ["T"] = "El Cubil Pardo",
  },
  [314] = {
    ["D"] = "Oímos los gritos en medio de la noche. Y esta mañana faltan dos cabezas de ganado. Esa ruin bestia conocida como Vagash ha estado alimentándose de nuestro sustento. La mayor parte del ejército del rey Magni está en tierras lejanas, luchando con la Alianza. No queda nadie para mantener a raya a Vagash.$B$BQuizás tú tengas el valor necesario para buscar a ese animal y matarlo. Tráeme uno de sus colmillos y te daré una recompensa. Vagash acecha desde encima de este rancho. Ten cuidado, es peligroso.",
    ["O"] = "Rudra Cieloámbar quiere que mates a Vagash y que le lleves su colmillo al rancho de carneros.",
    ["T"] = "La protección del rebaño",
  },
  [315] = {
    ["D"] = "Mi misión consiste en conseguir la cerveza perfecta. Sé que puedo hacerlo, la cebada está en mi sangre. Necesito encontrar la receta adecuada...$B$BLos trols Peloescarcha cultivan una planta llamada cardaluz, en las alturas del este. La utilizan para rituales extraños y tribales. Nosotros los enanos casi no la usamos, pero tiene un sabor único... y quisiera experimentar con él en mis cervezas.$B$BTráeme un poco de cardaluz de los profetas Peloescarcha o róbala de sus canastas.",
    ["O"] = "Trae 6 cardaluz a Rejold Cebadiz en Las Birras.",
    ["T"] = "La cerveza perfecta",
  },
  [316] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [317] = {
    ["D"] = "Me estoy preparando para empezar una misión para la Brigada de Asedio. Es una misión larga y tengo que cargar un mes de suministros en mi reactor a vapor. Así que, mientras Brasacerada trabaja en mi tanque, quizás podrías ir de caza por mí...$B$BNecesito pieles para hacerme una cama y carne para comer. Puedes cazar jabalíes para la carne y osos para las pieles... ve a los campos nevados que hay al sur de El Cubil Pardo.",
    ["O"] = "Reúne 4 piezas de carne de jabalí y 2 pieles gruesas de oso y llévaselas al piloto Bramiz al Almacén de Brasacerada.",
    ["T"] = "Los suministros del reactor a vapor",
  },
  [318] = {
    ["D"] = "Rejold Barleybrew experimenta con sus brebajes. Algunos saben bien, otros mal y otros... bueno, esos tumbarían al enano más fuerte.$B$BVive en Las Birras, al oeste de Kharanos. Y lo que quiero es una de sus bebidas: tajada. Me mantendrá caliente en los caminos más fríos.$B$BY... ¡ese bendito líquido quema tanto que también puedo usarlo para aumentar la potencia de mi reactor a vapor! Me ha sacado de un apuro más de una vez.",
    ["O"] = "Consigue un barril de tajada de Rejold Cebadiz de Las Birras.",
    ["T"] = "La tajada",
  },
  [319] = {
    ["D"] = "Tengo un barril de tajada, pero... ¿podrías hacerme un favor? En Las Birras vendría bien un poco de ayuda con los animales salvajes que deambulan por las nieves de los alrededores. A veces se acercan demasiado.$B$BNo es que tengamos miedo de los osos, los gatos ni los jabalíes... ningún enano que se precie lo tiene. Pero si tú pudieras ocuparte de esos animales, tendríamos más tiempo para trabajar y hacer cerveza.",
    ["O"] = "Mata a 6 osos zarpahelada, 8 jabalíes del risco mayores y 8 leopardos de la nieve y luego vuelve a Las Birras con Rejold Cebadiz.",
    ["T"] = "Un favor a cambio de un barril de tajada",
  },
  [320] = {
    ["D"] = "Aquí tienes tu barril de tajada, $N. Y, una vez más, gracias por la caza.",
    ["O"] = "Entrega el barril de tajada al piloto Bramiz que está en el Almacén de Brasacerada.",
    ["T"] = "Vuelve con Bramiz",
  },
  [321] = {
    ["D"] = "Los restos de naufragios que puedes ver en nuestra costa son la Tercera Flota maldita de Kul Tiras, incendiada y hundida por los dragones rojos durante la última Gran Guerra.$B$BLa flota procedía de Lordaeron, pero un destructor élfico, el Pigargo volador, estaba con ella. Transportaba un cargamento de hierro Forjaluz y se unió a la Tercera Flota en busca de protección. Irónicamente lo hundieron los mismos dragones...$B$BPero basta de hablar. El Pigargo volador naufragó al sur del Puerto de Menethil. Si encuentras el hierro Forjaluz, tráemelo.",
    ["O"] = "Busca los restos del Pigargo volador en Los Humedales.",
    ["T"] = "Hierro Forjaluz",
  },
  [322] = {
    ["D"] = "Hay un armero enano en el Distrito de los Enanos de Ventormenta que puede trabajar con este hierro Forjaluz. Su nombre es Grimand Elmore.$B$BHe empaquetado en un cajón el hierro que encontraste. Llévaselo a Grimand y cuéntale toda tu historia. Estoy seguro de que estará muy agradecido a alguien comprometido con una búsqueda como la tuya y te hará un arma... ¡un arma que haría temblar hasta al mismísimo Morbent Vil!",
    ["O"] = "Lleva el cajón de lingotes Forjaluz a Grimand Elmore en Ventormenta.",
    ["T"] = "Brazo bendito",
  },
  [323] = {
    ["D"] = "Morbent Vil es un Necromántico y un aliado de los Jinetes Oscuros. Te enfrentaría al poder de Morbent Vil, pero no te enviaré a luchar contra ese demonio sin conocer tu fuerza.$B$BYa me has probado tu valentía pero, si de verdad quieres enfrentarte al Necromántico, tendrás que probar primero que puedes derrotar a sus esbirros.",
    ["O"] = "Mata a 15 asaltantes esqueléticos, 3 sanadores esqueléticos y 3 guardas esqueléticos y vuelve a donde Sven.",
    ["T"] = "Prueba de valor",
  },
  [324] = {
    ["D"] = "Después de sacar un único lingote Forjaluz del cofre anegado, está claro que los múrlocs robaron el resto. Da caza a los asaltantes múrloc que rodean los restos del Pigargo volador y busca el resto del hierro Forjaluz perdido.",
    ["O"] = "Reúne 5 lingotes Forjaluz y vuelve con Glorin Frenteacerada en Los Humedales.",
    ["T"] = "Los lingotes perdidos",
  },
  [325] = {
    ["D"] = "Aquí tienes el arma para vencer al demonio ese del que me hablaste. Ya estás $gpreparado:preparada;, $n. Te deseo mucha suerte, ¡y esperaré con ansia escuchar la leyenda de tu gloria!",
    ["O"] = "Lleva la Pesadilla de Morbent a Sven en el Bosque del Ocaso.",
    ["T"] = "Armado y listo",
  },
  [328] = {
    ["D"] = "Si bien en los registros no aparece ninguna mención al soldado Thorsen, escondida en la tapa de uno de los libros de contabilidad de Herod hay una pequeña llave de latón.$B$BY, tras estudiar en profundidad sus notas, aparecen algunas referencias entre varios papeles, a una \"caja fuerte segura\". La caja está en algún lugar de Reservas, el complejo de cuevas que está en la parte trasera de la Base de Kurzen.$B$BSi encontramos la caja fuerte y la pequeña llave de latón puede abrirla, quizás descubramos el misterio tras la detección de Thorsen.",
    ["O"] = "Utiliza la pequeña llave de latón para abrir la caja fuerte de Bookie Herod.",
    ["T"] = "La llave oculta",
  },
  [329] = {
    ["D"] = "Entre los objetos de la caja fuerte hay una nota sucia llena de garabatos. Aunque casi es imposible de leer, parece ser un itinerario para el soldado Thorsen. Detalla sus rutas de patrulla, con quién habla y cómo pasa su tiempo con los rebeldes. Está firmada con una letra: M.$B$BPero quien fuera que escribiera esta nota debe de haber estado muy cerca de Thorsen. Quien escribió esta nota es el traidor del Asentamiento Rebelde.",
    ["O"] = "Lleva la nota sucia al teniente Doren en el Asentamiento Rebelde.",
    ["T"] = "¡Espía descubierto!",
  },
  [330] = {
    ["D"] = "Esa nota que me diste está firmada con una \"M\"... No sé lo que puede significar.$B$BPero... la nota también dice que esta persona compartió las rutas de patrulla con Thorsen la semana pasada. Así que, quien compartió las rutas de patrulla con Thorsen la semana pasada es el traidor.$B$BVete a ver al cabo Sethman. Se encarga de hacer los horarios de patrulla y podrá decirte quién estaba con Thorsen.",
    ["O"] = "Habla con el cabo Sethman.",
    ["T"] = "Horarios de patrulla",
  },
  [331] = {
    ["D"] = "¿Te pidió el teniente Doren que volvieses a informarle sobre las patrullas?",
    ["O"] = "Habla con el teniente Doren.",
    ["T"] = "Informa a Doren",
  },
  [332] = {
    ["D"] = "Si hablas sobre vino con alguien, sabrás que nosotros, en Gallinas, vendemos el mejor vino de Ventormenta. Y no estamos lejos, seguimos en el Distrito de Mercaderes, en los preciosos canales de la ciudad.$B$BToma, coge este panfleto. Llévaselo a mi hermana Suzetta a nuestra tienda, a cambio te dará una botella de regalo de nuestro famoso pinot noir. ¡No lo lamentarás!",
    ["O"] = "Ve a la Bodega Gallina y lleva a Suzetta Gallina el cupón de vino para una botella gratis.",
    ["T"] = "Publicidad de la bodega",
  },
  [333] = {
    ["D"] = "Últimamente el negocio ha ido bien. Parece que todo el mundo está comprando armaduras y ropas resistentes. Es casi como si esperasen una época dura y fría...$B$BPero esas son preocupaciones futuras. Mi preocupación del día es que me estoy quedando sin ropas tejidas que vender. Necesito otro cargamento de nuestro suministrador.$B$BSi puedes llevar este pedido a Rema Schneider en Trajes y Arreglos a Medida, te lo agradecería mucho.",
    ["O"] = "Ve a Trajes y Arreglos a Medida y lleva a Rema Schneider el paño que ha pedido Harlan Bagley.",
    ["T"] = "Harlan necesita un nuevo suministro",
  },
  [334] = {
    ["D"] = "Mi hijo Thurman es un aprendiz en Diseños Larson, en el Barrio de los Magos. Hoy salió con prisa y se olvidó su trasquiladora y las agujas. Sé que un gran $C como tú debe de tener importantes tareas entre manos, ¡pero sin sus herramientas, Thurman no puede hacer su trabajo de aprendiz!$B$BPor favor, $N. ¿Podrías llevar las herramientas de coser a mi hijo? Diseños Larson es una de las dos tiendas de ropa en el Barrio de los Magos, la que está más al fondo, cerca de la torre de los magos.",
    ["O"] = "Ve a Diseños Larson en el Barrio de los Magos de Ventormenta y entrega a Thurman Schneider sus herramientas de coser.",
    ["T"] = "Un paquete para Thurman",
  },
  [335] = {
    ["D"] = "Lord Wishock está causando gran agitación en la Casa de Nobles. Se ha enterado de lo de nuestro pequeño \"establecimiento\" aquí abajo y está presionando para que se realice una investigación en toda regla. Tengo un plan para mantener a Wishock \"preocupado\", pero necesito tu ayuda.$B$BEn el Jardín de Stalvan, a las afueras de Villa Oscura, crece una flor. Esta pequeña flor blanca es conocida como la lágrima de Tilloa. También necesitaré una zibética, que solo se puede encontrar en la base de las cascadas bajo la Presa de Fraguapiedra, en Los Humedales.",
    ["O"] = "Zardeth de los Zarpanegra, quiere que le lleves una lágrima de Tilloa de Villa Oscura y una zibética de Los Humedales.",
    ["T"] = "Una noble cerveza",
  },
  [336] = {
    ["D"] = "Lord Wishock, como todos los hombres, tiene sus puntos débiles. Y, resulta que su punto más débil es el licor. La hipocresía es asombrosa. El pobre necio pasa sus días intentando cerrar nuestra pequeña \"taberna\" y las noches arriba, bebiendo y aumentando una cuenta que nunca paga. Bueno, veamos qué le parece a Wishock este brebaje especial que le he preparado: cerveza Zarpanegra. Es bastante fuerte, $n.$B$BLlévasela y veamos si le gusta. Lo más probable es que el viejo tonto esté dando vueltas por la Sala de los Ruegos.",
    ["O"] = "Lleva la cerveza Zarpanegra a Lord Wishock en la Sala de los Ruegos.",
    ["T"] = "Una noble cerveza",
  },
  [337] = {
    ["D"] = "Aunque la cubierta de este libro está llena de arañazos y de barro, aún se pueden leer las palabras del lomo: La Historia de Ventormenta. Al abrir el libro se ve claramente el sello de la biblioteca de Ventormenta en la primera página.$B$BParece que alguien sacó este libro y no lo llegó a devolver...",
    ["O"] = "Devuelve el libro: La Historia de Ventormenta a La Biblioteca de Ventormenta.",
    ["T"] = "Un libro de Historia viejo",
  },
  [338] = {
    ["D"] = "¡Oh, el maestro Decautela se sentirá muy disgustado si lo descubre!$b$bConfiaba en mí para revisar el manuscrito de su novela, Las verdes colinas de Tuercespina. Todo iba bien, pero un golpe de viento pasó por el campamento y las páginas quedaron dispersas por todas partes.$b$bConseguí reunir la mayor parte de las páginas, pero hay muchas todavía por la jungla. Reúne las páginas para completar los cuatro capítulos.$B$BYo te ayudaré a organizarlas. Devuélveme los capítulos cuando tengas los cuatro.",
    ["O"] = "Reúne las páginas perdidas del manuscrito Las verdes colinas de Tuercespina. Cuando los cuatro capítulos estén completos, llévaselos a Barnil.",
    ["T"] = "Las verdes colinas de Tuercespina",
  },
  [339] = {
    ["D"] = "¡El capítulo I es fascinante! El maestro Decautela establece el telón de fondo de la historia y menciona a sus compañeros de caza, ¡incluyéndome a mí! $b$bPero eso no viene al caso. Déjame ver las páginas que faltan. Parece ser que las páginas 1, 4, 6 y 8 siguen todavía por algún lado en la jungla.",
    ["O"] = "Lleva las páginas 1, 4, 6 y 8 de Las verdes colinas de Tuercespina de Decautela a Barnil Jarropetra para completar el capítulo I.",
    ["T"] = "Capítulo I",
  },
  [340] = {
    ["D"] = "El capítulo II está lleno de historias excitantes sobre la caza de panteras y tigres. ¡Me patearía la cara por haber dejado que las páginas volaran! $b$b Parece ser que conseguí recuperar todas las páginas menos las 10, 11, 14, y 16.",
    ["O"] = "Lleva las páginas 10, 11, 14 y 16 de Las verdes colinas de Tuercespina de Decautela a Barnil Jarropetra para completar el capítulo II.",
    ["T"] = "Capítulo II",
  },
  [341] = {
    ["D"] = "El capítulo III es mi favorito. Habla de la primera caza de Sir Erlgadin y Lady Rouack con un rifle enano. Y, al final del capítulo III, ¡menudo suspense! $b$bMe parece que todavía necesitamos las páginas 18, 20, 21 y 24 para terminar el capítulo. Esas páginas están ahí fuera, en algún lugar...",
    ["O"] = "Lleva las páginas 18, 20, 21 y 24 de Las verdes colinas de Tuercespina de Decautela a Barnil Jarropetra para completar el capítulo III.",
    ["T"] = "Capítulo III",
  },
  [342] = {
    ["D"] = "Ah, el capítulo IV, ¡el gran final! ¡Y menudo final! $b$bSolo nos faltan tres páginas de este capítulo. Necesitamos las páginas 25, 26 y 27 para finalizar el capítulo IV.",
    ["O"] = "Lleva las páginas 25, 26, y 27 de Las verdes colinas de Tuercespina de Decautela a Barnil Jarropetra para completar el capítulo IV.",
    ["T"] = "Capítulo IV",
  },
  [343] = {
    ["D"] = "Voy a dar un discurso sobre la fortaleza de espíritu y me gustaría recitar unos pasajes de un libro sobre metalurgia, \"La tensión del hierro\". En el Archivo Real, en el Castillo de Ventormenta debería haber una copia del libro. ¿Podrías ir a buscarlo para mí? Si lo haces, ¡que las bendiciones de la Luz te acompañen!$B$BEl Archivo Real es enorme, pero el bibliotecario jefe, Milton Sheaf, conoce todas las estanterías como la palma de su mano. Habla con él, te dará el libro.",
    ["O"] = "Ve al Archivo Real en el Castillo de Ventormenta y habla con Milton Gavilla. Podrá encontrar el libro sobre metalurgia que el hermano Kristoff necesita para su charla.",
    ["T"] = "Hablando de fortaleza",
  },
  [344] = {
    ["D"] = "¿La tensión del hierro? Sí, conozco el libro. Es bastante bueno, lo escribió el minero enano Margulf Blaggon hace unas décadas. Pasó la mayor parte de su vida en las profundidades de Khaz Modan, extrayendo minerales. ¡Un tipo listo!$B$B¿Por dónde iba? ¡Oh, sí, La tensión del hierro! Me temo que el libro se llevó a la Abadía de Villanorte. El encargado de la biblioteca de la Abadía, el hermano Paxton, quería hacer una copia del libro para su biblioteca.$B$BPero eso fue hace meses. Me pregunto por qué estará tardando tanto...",
    ["O"] = "Habla con el hermano Paxton en la Abadía de Villanorte.",
    ["T"] = "Hermano Paxton",
  },
  [345] = {
    ["D"] = "Quería terminar mi copia de La tensión del hierro, pero...$B$BBueno, verás, hago mi propia tinta para los libros que copio. Es mi propia receta. No se corre ni mancha y se seca muy rápido. Por desgracia, ¡se seca demasiado rápido! Lo última que me quedaba se secó antes de que pudiera acabar la copia y no he tenido oportunidad de hacer más tinta.$B$BPara hacer más, necesito mineral de hierro de Rethban, de las Montañas Crestagrana. El supervisor Oslow, en Villa del Lago, es quien me lo suele suministrar.",
    ["O"] = "Habla con el supervisor Oslow en Villa del Lago y pregúntale sobre el mineral de hierro de Rethban.",
    ["T"] = "Suministros de tinta",
  },
  [346] = {
    ["D"] = "Ahora que he terminado de copiar el libro, aquí tienes el original de La tensión del hierro. Gracias por tu paciencia y, si ves a Milton, por favor, dile que siento haberme retrasado tanto.",
    ["O"] = "Vuelve a hablar con el hermano Kristoff en la Plaza de la Catedral. Entrégale el libro La tensión del hierro.",
    ["T"] = "Vuelve con Kristoff",
  },
  [347] = {
    ["D"] = "El mineral de Rethban tiene ese nombre por el hombre que lo descubrió, Bart Rethban. Cuando se funde, no es tan fuerte como el hierro, pero tiene un color oscuro que algunos escribas utilizan en sus tintes. Se puede encontrar en las Cavernas de Rethban, al norte de aquí.$B$BGeneralmente lo tenemos almacenado, pero estamos demasiado ocupados preparándonos para los atacantes orcos que no hemos tenido tiempo de recogerlo. Y, además, los gnolls de Crestagrana viven ahora en las Cavernas de Rethban y sus esclavos se encargan de recoger el mineral.$B$BAsí que si necesitas recogerlo, tendrás que ir a por él.",
    ["O"] = "Reúne 5 montones de mineral de hierro de Rethban y llévaselos al hermano Paxton en la biblioteca de Villanorte.",
    ["T"] = "Mineral de Rethban",
  },
  [348] = {
    ["D"] = "M... me... m... muero...$B$B... T... tengo la fiebre de Tuercespina... un virus m... mortal...$B$B... La única cura... es... comer el corazón de M... mokk el Salvaje... una esquiva bestia blanca...$B$B... El médico brujo Unbagwa... es el único que p... puede invocar...$B$B... Busca a Unbagwa en una cueva oculta en el cabo... del sur...$B$B... Rápido... m... me... muero...",
    ["O"] = "Busca al médico brujo Unbagwa y haz que invoque a Mokk el Salvaje. Lleva el corazón de Mokk a Fin Fizqueta.",
    ["T"] = "La fiebre de Tuercespina",
  },
  [349] = {
    ["D"] = "temp text 02 – descripción",
    ["O"] = "temp text 02 - registro",
    ["T"] = "La fiebre de Tuercespina",
  },
  [350] = {
    ["D"] = "Ese Marzon tenía que contactar con un miembro de los Defias y llegar a un acuerdo con Lescovar...$B$BCon VanCleef y Thredd muertos, no sería fácil llevar a Lescovar ante la justicia, ya que las pruebas no sirven de nada sin sus testimonios. Y eso, sin considerar que Lescovar es un noble, y está muy bien conectado. Efectivamente, eso hace que esté por encima de la ley.$B$BVas a necesitar más ayuda. Odio hacer esto, pero conozco a alguien que nos ayudará. Busca a Trias en el Distrito de Mercaderes de la ciudad.",
    ["O"] = "Habla con Elling Trias en Ventormenta.",
    ["T"] = "Mira por un viejo amigo",
  },
  [351] = {
    ["D"] = "Has descubierto una especie de dispositivo metálico extraño con forma de huevo. Al tocar uno de sus botones el huevo cobra vida y descubres que se trata de algún tipo de artilugio robótico de invención gnómica. A continuación, una voz resuena en su interior.$B$B\"Mi nombre es Oglethorpe y he perdido a mi robot mensajero. Si lo encuentras te daré una recompensa. Por favor, llévale este localizador al robot. ¡Genial, acabo de obtener las coordenadas! ¡Parece que se estropeó cerca de Sima Abierta en Tanaris!\"",
    ["O"] = "Llévale el localizador de emergencia al robot mensajero de Oglethorpe.",
    ["T"] = "¡Encontrar a OOX-17/TN!",
  },
  [352] = {
    ["D"] = "Great Mammoda. Enormous, she was, with claws and teeth like swords. Now, I was caught off my guard, but I don\'t think being ready would have helped my cause much. One swipe and poof! Darkness. They say I\'m lucky to walk now...$b$bWell, not much enjoyment to be had in hearing that old story now, is there? Anyways, $n, if you\'d like to try yourself against her, the Lady of the Loch,",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [353] = {
    ["D"] = "Los Pico Tormenta son unos enanos muy respetados y famosos por su exigente gusto. Así que no es sorprendente que Gringer Pico Tormenta, un montaraz de Forjaz, me encargase que forjase un arma para él.$B$BHe terminado el arma, pero... el montaraz Pico Tormenta está muy lejos, en Loch Modan. Si tienes planeado viajar al norte, ¿podrías entregarle este paquete?$B$BEl último mensaje que recibí del montaraz Pico Tormenta, decía que estaba estacionado en la torre de vigilancia del norte, en Loch Modan.",
    ["O"] = "Entrega el paquete Pico Tormenta al montaraz Pico Tormenta en Loch Modan.",
    ["T"] = "Una entrega a Pico Tormenta",
  },
  [354] = {
    ["D"] = "Los Agamand eran una de las familias más prósperas de los Claros de Tirisfal. Yo solía trabajar en sus molinos... antes de la Peste.$B$BCuando la Plaga apareció por primera vez, los Agamand fortificaron su casa y convencieron a todos sus empleados para que se quedaran y les ayudaran a defenderse. Fuimos unos insensatos pero, al menos, fuimos unos insensatos leales.$B$BLos Agamand, a causa de su orgullo, nos abocaron a todos a la desgracia. ¡Y ahora son esbirros de la Plaga!$B$BSirve a los Renegados y derrota a los Agamand, que sucumbieron a la peste. Sírveme a mí y tráeme sus restos.",
    ["O"] = "Llévale los restos de Gregor, los restos de Nissa y los restos de Thurman a Coleman Lontananza, que está en Rémol.",
    ["T"] = "Muertes en la familia",
  },
  [355] = {
    ["D"] = "Me has entregado los restos de los Agamand y con ello, has satisfecho mis deseos de venganza. Pero los Molinos de Agamand encierran una amenaza para todos los Renegados, no solo para mí.$B$BHe informado al magistrado Sevren acerca de tus hazañas contra los Agamand y desea hablar contigo.",
    ["O"] = "Habla con el magistrado Sevren de Rémol.",
    ["T"] = "Habla con Sevren",
  },
  [356] = {
    ["D"] = "Los defensores de El Baluarte que protegen Tirisfal de las Tierras de la Peste están en alerta constante pero, aun así, en ocasiones la Plaga consigue colarse.$B$BNuestro éxito en El Baluarte depende de una batalla de un solo frente. No podemos, pues, permitir un ataque desde la retaguardia ni dejar que corten la línea de abastecimiento de El Baluarte.$B$BPresta ayuda en El Baluarte. Inspecciona en dirección este y mata a todo miembro de la Plaga que encuentres. Céntrate sobre todo en la Hacienda Balnir, situada al este, pues parece que se ha convertido en el refugio de numerosos miembros de la Plaga.",
    ["O"] = "Mata a 8 horrores sangrantes y a 8 espíritus deambulantes, y ve a informar a Linnea a su campamento.",
    ["T"] = "Patrulla de la retaguardia",
  },
  [357] = {
    ["D"] = "Hay un exánime que mora en la isla Lago Aguasclaras, en el norte. Aunque es un Renegado independiente, cree que los otros no-muertos son esclavos de la Plaga por lo que sus esbirros atacan a todo aquel que se acerca a él.$B$BPero, dado que es un Renegado, la Reina lo reclama. Y si tiene conocimientos de necromancia, estos serán... muy útiles para nosotros.$B$BPara descubrir su identidad y talentos, debo ver su libro de hechizos. Irrumpe en su campamento, róbalo y tráemelo aquí.",
    ["O"] = "Llévale el libro de hechizos del exánime a Bethor Duroyelo, que está en Entrañas.",
    ["T"] = "La identidad del exánime",
  },
  [358] = {
    ["D"] = "Las fosas comunes, al suroeste de la Granja de Garren situada hacia el norte de aquí, se hicieron para albergar la impresionante cantidad de cadáveres de aquellos que perecieron en Tirisfal cuando la Peste golpeó por primera vez. $B$BDe estas tumbas no había salido ni un solo no-muerto hasta el momento, pero ahora la Plaga envía a gnolls Putrepellejo a recoger cadáveres para que engrosen las filas de sus ejércitos. ¡Esto es inaceptable!$B$BTienes una doble tarea que cumplir: mata a los Putrepellejo de las fosas comunes y la Granja de Garren, y quítales el icor de embalsamar que les da vida.",
    ["O"] = "Mata a los asaltatumbas Putrepellejo y a los bastardos Putrepellejo. $B$BLlévale 8 icores de embalsamar al magistrado Sevren de Rémol.",
    ["T"] = "Profanadores",
  },
  [359] = {
    ["D"] = "Necesito conocer las últimas noticias de nuestro puesto del Guardia de la Muerte situado al este de aquí.$B$BTengo que saber si las fuerzas de la Plaga han pasado por El Baluarte y han entrado en Tirisfal. Debemos mantener la vigilancia en la frontera con las Tierras de la Peste pues no podemos permitir que la Plaga afiance su presencia aquí.$B$BViaja hacia el sur siguiendo el camino y cuando este se bifurque, continúa en dirección este hasta el puesto. Habla con la Guardia de la Muerte Linnea. Ella tiene la información que necesito.",
    ["O"] = "Habla con la guardia de la muerte Linnea.",
    ["T"] = "Las obligaciones de los Renegados",
  },
  [360] = {
    ["D"] = "Ahora llévale la información que te he dado al magistrado Sevren. Le gustará recibirla lo antes posible así que date prisa.$B$BY si ves a algún miembro de la Plaga por el camino, acaba con él. No podemos permitir que vaguen por nuestras tierras a su antojo.",
    ["O"] = "Ve a ver de nuevo al magistrado Sevren de Rémol.",
    ["T"] = "Vuelve con el magistrado",
  },
  [361] = {
    ["D"] = "Esta carta la escribió Thurman, el hijo mayor de Gregor Agamand. Está dirigida a una tal Yvette Lontananza, aunque parece que nunca llegó a recibirla...",
    ["O"] = "Encuentra a Yvette Lontananza y entrégale la carta de Thurman Agamand.",
    ["T"] = "Una carta sin entregar",
  },
  [362] = {
    ["D"] = "Devlin Agamand era el más joven de los dos hijos de la familia Agamand y en vida ambos no podían ser más diferentes. Thurman era alto y amable mientras que su hermano pequeño era débil y tenía la lengua muy afilada.$B$BCuando la Peste llegó a los Molinos de Agamand, no me sorprendió de que Devlin sucumbiera casi de inmediato. Sus desbarres todavía pueden oírse cerca del camino que conduce a los Molinos de Agamand.$B$BEstoy recogiendo lo que queda de los Agamand y quiero al pobre Devlin. Encuéntralo, destrúyelo y tráeme sus huesos.",
    ["O"] = "Acaba con Devlin Agamand y llévale los restos de Devlin a Coleman Lontananza, que está en Rémol.",
    ["T"] = "Los molinos fantasma",
  },
  [363] = {
    ["D"] = "Ya era hora de que despertaras. Estábamos a punto de arrojarte al fuego con los otros y, mira por dónde, ya no tenemos que hacerlo.$B$BSoy Mordo, el custodio de la cripta de Camposanto. Y tú ya no eres uno de los esclavos del rey Exánime.$B$BHabla con el sacerdote de las Sombras Sarvis. Está en la capilla, al pie de la colina. Él te dirá lo que debes saber.",
    ["O"] = "Habla con el sacerdote de las Sombras Sarvis.",
    ["T"] = "Desvelo brutal",
  },
  [364] = {
    ["D"] = "Los Renegados estamos en guerra con el ejército del rey Exánime, la Plaga, un ejército de no-muertos alzados con necromancia, bestias inmundas del norte y espectros atormentados.$B$BLa parte norte del poblado ha sido invadida por los Autómatas. Deben ser destruidos. Acaba con ellos. Y no tengas piedad, aunque antes fueran nuestros hermanos y hermanas. Los Caídos no son más que esclavos del rey Exánime.",
    ["O"] = "El sacerdote de las Sombras Sarvis quiere que mates a 8 zombis descerebrados y a 8 zombis desdichados.",
    ["T"] = "Los autómatas",
  },
  [365] = {
    ["D"] = "¿Qué tenemos aquí? Pareces un poco $gnovato:novata;. Si quieres demostrar tu valía a la Dama Oscura, tendrás que aprender los caminos de los Renegados.$b$bEncontrarás una plantación en dirección oeste. Los humanos infestan la tierra al igual que el moho un cuerpo en descomposición. Y lo que es peor, la Cruzada Escarlata vigila la zona desde su torre. Dales una lección a esos canallas y roba 10 de sus preciadas calabazas.$b$bCuando tengas 10, llévaselas al boticario Johaan a Rémol.",
    ["O"] = "Roba 10 calabazas de la plantación que encontrarás en dirección oeste, justo al norte de Camposanto, y llévaselas al boticario Johaan a Rémol.",
    ["T"] = "Campos de pena",
  },
  [366] = {
    ["D"] = "Hace tiempo, Gunther y yo, junto con el ahora necromántico de la Plaga Thule Corvozarpa, fuimos amigos y estudiamos juntos en el Kirin Tor (una sociedad de magos de Dalaran).$B$BTanto Gunther como yo sucumbimos a la Peste pero creí que él estaría muerto o sirviendo al rey Exánime. ¡Es estupendo saber que está libre!$B$BTenemos que conseguir que se una a nosotros. He lanzado un hechizo sobre su libro de forma que, cuando lo vea, sepa que yo también estoy libre del rey Exánime. Cuando se entere de esto, espero que se una a nosotros.",
    ["O"] = "Devuélvele el libro de hechizos de Gunther a su dueño, que se encuentra en la isla del Refugio de Gunther.",
    ["T"] = "Devuelve el libro",
  },
  [367] = {
    ["D"] = "Lady Sylvanas ha recurrido a la Sociedad Real de Boticarios. La Dama Oscura cree que nuestro conocimiento, unido a la recién descubierta magia, proporcionará la clave para derrotar a Arthas. Nos ha instado a idear una nueva peste, una peste mucho más mortífera que cualquier otra dolencia conocida en Azeroth. Esta nueva enfermedad llevará a la ruina al ejército de la Plaga de Arthas.$B$BMis estudios revelan que la clave podría estar en la sangre de las bestias. Tráeme 5 viales de sangre de can oscuro para que pueda comprobar mi teoría.",
    ["O"] = "El boticario Johaan de Rémol quiere que recojas 5 viales de sangre de can oscuro.",
    ["T"] = "Una nueva Peste",
  },
  [368] = {
    ["D"] = "Mientras recogías las muestras, me he dado cuenta gracias a mis experimentos de que serán necesarios más componentes para que esta nueva enfermedad se propague como es debido. Envenenar a unas cuantas víctimas es un juego de niños pero extender una plaga por todo un mundo es algo más complicado.$B$BNecesitaré 5 escamas de Anca Vil de los múrlocs de la zona. Encontrarás a estas criaturas en la costa hacia el norte o el oeste.",
    ["O"] = "El boticario Johaan de Rémol necesita 5 escamas de Anca Vil de los múrlocs de los Claros de Tirisfal.",
    ["T"] = "Una nueva Peste",
  },
  [369] = {
    ["D"] = "Mientras recogías lo que te pedí, descubrí un viejo texto en uno de mis tomos que explica cómo una antigua peste arrasó con miles de víctimas inocentes. Más tarde se descubrió que el agente mortal de dicha peste estaba presente en el veneno de las nocturácnidas.$b$bTráeme un poco de veneno de una nocturácnida sanguinaria para completar este experimento. Quiero comprobar si el elemento contagioso presente en el veneno servirá para mi nuevo brebaje. Se dice que hay muchas arañas en los Claros de Tirisfal del Este.",
    ["O"] = "El boticario Johaan de Rémol quiere que le lleves 4 muestras de veneno de nocturácnida sanguinaria.",
    ["T"] = "Una nueva Peste",
  },
  [370] = {
    ["D"] = "\"Los documentos del ejecutor Arren contienen exactamente todos los detalles que necesitamos para erradicar la infestación humana de nuestras tierras. De acuerdo con esta información, la Cruzada Escarlata ha enviado al capitán Perrine y a una brigada a las ruinas de la torre situada al suroeste de Rémol.$b$bMata a Perrine, a 3 fanáticos y a 3 misioneros y regresa aquí.",
    ["O"] = "El ejecutor Zygand de Rémol quiere que mates al capitán Perrine, 3 integristas Escarlata y 3 misioneros Escarlata.",
    ["T"] = "En guerra con la Cruzada Escarlata",
  },
  [371] = {
    ["D"] = "Seguimos en guerra y la Cruzada Escarlata se hace cada vez más fuerte. El informe que el ejecutor Arren me ha enviado indica que la Cruzada Escarlata ha estado atacando desde las ruinas de la torre situada al sudeste de Tirisfal, cerca de la Hacienda Balnir, bajo las órdenes del capitán Vachon.$B$BMata a Vachon y a 5 frailes Escarlata. ¡Será un duro golpe para la Cruzada!",
    ["O"] = "El ejecutor Zygand de Rémol te ha encargado que mates al capitán Vachon y a 5 frailes Escarlata.",
    ["T"] = "En guerra con la Cruzada Escarlata",
  },
  [372] = {
    ["D"] = "La Cruzada Escarlata ha estado atacando desde las ruinas de la torre situada al norte de Tirisfal, más allá Tumba de Faol. De acuerdo con los datos que tenemos, están bajo las órdenes del implacable capitán Melrache.$b$bTe voy a confiar una misión tan poco habitual como peligrosa. Mata a Melrache y a sus dos guardaespaldas en nombre de la Dama Oscura.",
    ["O"] = "El ejecutor Zygand de Rémol quiere que asesines al capitán Melrache y a sus dos guardaespaldas.",
    ["T"] = "En guerra con la Cruzada Escarlata",
  },
  [373] = {
    ["D"] = "Registrando a Edwin VanCleef descubres, entre otras cosas, una carta sin enviar. Va dirigida a Baros Alexston, el arquitecto jefe de Ventormenta, Ayuntamiento, Plaza de la Catedral.$B$BParece ser que ha sido escrita y sellada recientemente.",
    ["O"] = "Entrega la carta destinada al arquitecto jefe a Baros Alexston en Ventormenta.",
    ["T"] = "La carta sin enviar",
  },
  [374] = {
    ["D"] = "El ejecutor Zygand me dice que has estado matando a miembros de la Cruzada Escarlata. Los Claros de Tirisfal están plagados de ellos.$B$BVarimathras, teniente de la Dama Oscura, se está encargando personalmente de exterminar a la escoria de Azeroth: la raza de los humanos. Aquí en los Claros de Tirisfal nos enfrentamos a un nuevo género de humanos: la Cruzada Escarlata.$B$BCorre el riesgo y lanza una ofensiva contra la Cruzada Escarlata, $c. Tráeme 10 anillos con la insignia Escarlata como muestra de tu lealtad a Varimathras y a la Dama Oscura.",
    ["O"] = "Llévale 10 anillos con la insignia escarlata al Guardia de la Muerte Burgess de Rémol.",
    ["T"] = "Prueba de la muerte",
  },
  [375] = {
    ["D"] = "Tengo tanto frío... La Peste de los no-muertos corre por mis venas como una serpiente de hielo. Pronto me embargará el estado de automatismo pero nada me impedirá que sirva a nuestra Dama Oscura. En su momento yo mismo cosí bolsas de cadáveres para los soldados caídos del poderoso ejército de Sylvanas.$b$bPero ahora mis manos tiemblan de frío. Si me trajeras 5 pieles de murciumbrío y un poco de hilo grueso, podría coser yo mismo una manta. Ayúdame, $N, para que así pueda seguir sirviendo a la causa.",
    ["O"] = "Llévale 5 pieles de murciumbrío y algunos hilos gruesos a Gretchen Dedmar, que está en Rémol.",
    ["T"] = "El frío de la muerte",
  },
  [376] = {
    ["D"] = "Mis deberes son atender a nuestros guerreros heridos, suministrar ropa y armaduras y asistir al sacerdote de las Sombras Sarvis en lo que necesite.$B$BParece que tú también vas a acabar a su servicio... dando caza a los autómatas. Seguro, sé cómo piensa. Si quieres seguir de una pieza, que seguro que sí, quizás pueda ayudarte. Me estoy quedando sin patas y alas. Si me traes algunas, te buscaré una armadura. Encontrarás muchos lobos y murciélagos hacia el sur.",
    ["O"] = "El novicio Elreth necesita 6 patas de carroñero y 6 alas de murciumbrío.",
    ["T"] = "Los Malditos",
  },
  [377] = {
    ["D"] = "Como si no tuviéramos suficiente con la negligencia de los residentes del Bosque del Ocaso, ahora la Casa de Nobles escupe en el ojo al Consejo de Villa Oscura con su decisión de encarcelar a Dextren Tutor en Ventormenta en lugar de decapitarlo como establecía la sentencia de Lord Ebonlocke.$B$BTutor fue descubierto vendiendo cadáveres del cementerio a Morbent Vil, y ese es un delito que se castiga con la muerte. Y sin embargo, Ventormenta lo reclama como prisionero.$B$BAsesina a Tutor en las Mazmorras de Ventormenta. Tráeme su mano y yo te recompensaré.",
    ["O"] = "El consejero Tallolino de Villa Oscura quiere que le lleves la mano de Dextren Tutor.",
    ["T"] = "Crimen y castigo",
  },
  [378] = {
    ["D"] = "Has demostrado tu lealtad al rey Magni, $c. Y tu odio por la escoria Hierro Negro es tan grande como el mío.$B$BHay una tarea que me gustaría hacer por mí mismo, pero el honor me obliga a permanecer junto a Largatrenza. Roggo ha conseguido reunir datos que demuestran que Kam Furiahonda fue miembro de la conspiración para el ataque del Puente Thandol. Fueron los planes de Furiahonda los que hicieron perder a Largatrenza a toda su familia.$B$BFuriahonda está retenido, por razones políticas, en las Mazmorras de Ventormenta. Le quiero muerto, $n. ¡Por Largatrenza! ¡Tráeme su cabeza!",
    ["O"] = "Motley Garmason quiere que le lleves la cabeza de Kam Furiahonda a Dun Modr.",
    ["T"] = "La furia mora en las profundidades",
  },
  [379] = {
    ["D"] = "Si quieres una fuente de calor, yo puedo ayudarte a conseguirla. ¿Y sabes qué quiero yo? ¡Algo de beber! Tráeme cinco bolsas de agua de los Deambuladores y yo te daré la 4711-FTZ.$BYa solo te falta saber dónde puedes encontrar bolsas de agua de los Deambuladores. ¿Por qué no te diriges al este en busca de nómadas Deambuladores y les pides una? Ya me contarás cómo resulta.",
    ["O"] = "Llévale 5 bolsas de agua de los Deambuladores al ingeniero jefe Silvaina, que está en Gadgetzan.",
    ["T"] = "Aplacar esa sed",
  },
  [380] = {
    ["D"] = "Obtener los recursos naturales que necesitamos para sobrevivir es una de nuestras luchas más arduas. El oro escaseaba incluso en los tiempos en los que el poder de la Alianza estaba en su cumbre.$B$BHay una mina de oro al noroeste que ha sido invadida por arañas. Necesitamos el oro de esa mina, pero no podemos extraerlo mientras las arañas estén por ahí. No tengo mucho personal para la tarea, así que tendremos que encargarnos de ellas poco a poco.$B$BSube allí y a ver si puedes hacer algo por nosotros, $n.",
    ["O"] = "El ejecutor Arren quiere que mates a 10 nocturácnidas jóvenes y a 8 nocturácnidas.",
    ["T"] = "Hoya Nocturácnidas",
  },
  [381] = {
    ["D"] = "Te alegrará saber que estamos haciendo algunos progresos en la mina, gracias, en gran parte, a tus esfuerzos. Ahora podemos centrarnos en otros problemas.$B$BMis exploradores me han comunicado que un destacamento de la Cruzada Escarlata está estableciendo un campamento al sureste de aquí. La Cruzada Escarlata es una despreciable organización que nos da caza. No descansarán hasta que hayan destruido a todos los no-muertos, sean de la Plaga del rey Exánime o no. ¡Debemos atacar primero!$B$BTen cuidado, su impuro celo los convierte en adversarios muy peligrosos.",
    ["O"] = "Lleva al ejecutor Arren 12 brazaletes Escarlata de convertidos Escarlata y de iniciados Escarlata.",
    ["T"] = "La Cruzada Escarlata",
  },
  [382] = {
    ["D"] = "Los informes que me envían mis superiores desde Rémol indican que un agente de la Cruzada Escarlata fue enviado desde el Monasterio Escarlata hasta el Camposanto. Sabemos que ese mensajero transporta una información muy importante. No tengo más detalles pero, lleve lo que lleve, ¡debemos conseguirlo!$B$BEres la persona más cualificada a la que puedo enviar, $n. Encuentra a ese mensajero y tráeme cualquier información que encuentres.",
    ["O"] = "Mata a Meven Korgal, el mensajero, en el campamento de los Cruzados y vuelve a comunicar al ejecutor Arren cualquier información que encuentres.",
    ["T"] = "El mensajero rojo",
  },
  [383] = {
    ["D"] = "Hay que hacer llegar esto a mi superior, el ejecutor Zygand, en Rémol. Con esta información podremos asestar un golpe definitivo a la Cruzada Escarlata. Has sido muy útil en Camposanto, pero necesito que alguien lleve esto a Zygand y además, creo que él podrá asignarte tareas más adecuadas.$B$BCuando salgas de Camposanto, sigue la carretera del norte. Poco después de entrar en Tirisfal, encontrarás un cruce. Toma el sendero del este y sigue en esa dirección. En tu camino pasarás por la Mansión Fríogar.",
    ["O"] = "Ve a Rémol y entrega los documentos de la Cruzada Escarlata al ejecutor Zygand.",
    ["T"] = "Información decisiva",
  },
  [384] = {
    ["D"] = "¡Nada les gusta más a mis clientes que unas ricas costillas de jabalí a la cerveza! El problema es que el trampero local que solía traerme los suministros, se ha alistado en el ejército del Rey para ayudar en el frente de la Alianza.$B$BQuizás puedas ayudarme tú... Si me traes seis costillas de jabalí montés y algo de malta rapsódica de la taberna, compartiré contigo una receta familiar: mis famosas costillas de jabalí a la cerveza. ¡Y te daré una muestra gratis! ¡El secreto está en la malta!",
    ["O"] = "Ragnar Cebatruenos de Kharanos quiere 6 costillas de jabalí del risco y una jarra de malta rapsódica.",
    ["T"] = "Costillas de jabalí a la cerveza",
  },
  [385] = {
    ["D"] = "Vienen muchos cazadores a Loch Modan por los crocoliscos. Los mercaderes compran pieles de crocolisco para hacer ropa y armaduras, e incluso hay a quien le gusta su carne.$b$bHacemos negocio, pero no mucho, porque esos bichos son muy feroces y se esconden en las islas del lago. Pero tú no te prives, enfrentarse a esos dientes es toda una experiencia.$b$bMe acuerdo de una vez que...",
    ["O"] = "Lleva 5 piezas de carne de crocolisco y 6 pieles de crocolisco a Marek Coracero, que está en la Cabaña del Errante.",
    ["T"] = "La caza de crocoliscos",
  },
  [386] = {
    ["D"] = "Targorr el Pavoroso sirvió bajo las órdenes de Gath\'llzogg como Ejecutor supremo. Sus métodos eran los más despiadados, incluso para los orcos. No dudó en torturar a hombres que habían servido lealmente en la defensa del Reino. Ahora se encuentra recluso en las Mazmorras de Ventormenta, a la espera de que se cumpla su sentencia de muerte. Aún así, hay algo que no encaja. Uno de los nobles burócratas ha detenido su ejecución. Estoy seguro de que se trata de una maniobra oscura.$b$bTienes que acabar con Targorr el Pavoroso, $N. Viaja a las Mazmorras y decapítalo antes de que logre escapar a su destino.",
    ["O"] = "Lleva la cabeza de Targor el Horror al guardia Berton a Villa del Lago.",
    ["T"] = "Lo que sucece alrededor…",
  },
  [387] = {
    ["D"] = "¡Hay que detener el motín! Necesito tu ayuda. ¡Si alguien se entera de que la prisión es un caos perderé mi trabajo! Esto es juego sucio. Pero lo importante es obligar a los prisioneros a rendirse, por la seguridad de Ventormenta.$B$BEl castigo por sublevación es la muerte. Mata a alguno de esos desviados. Y verás cómo vuelven al buen camino...",
    ["O"] = "El alcaide Thelagua de Ventormenta quiere que mates 10 prisioneros Defias, 8 presidiarios Defias y 8 insurgentes Defias en las Mazmorras.",
    ["T"] = "Detener el motín",
  },
  [388] = {
    ["D"] = "El resentimiento corre por mis venas como una corriente fría. Mi nieto, Mac, era un chico muy bueno. 14 años recién cumplidos. Cuando el ejército fue mandado al frente, Mac cogió las armas para defender la ciudad. Fue un guardia de Las Mazmorras, donde está encerrada la banda Defias. Mac reprendió a algunos de los prisioneros por romper sus mantas de lana para hacer bufandas, un símbolo de la banda. Por hacerlo, recibió una puñalada en la espalda.$B$BQuiero venganza. Tráeme 10 de sus valiosos pañuelos de lana. Son del color de la sangre.",
    ["O"] = "Nikova Raskol, de Ventormenta, quiere que consigas 10 pañuelos de lana roja.",
    ["T"] = "El color de la sangre",
  },
  [389] = {
    ["D"] = "VanCleef y yo fuimos miembros de la Hermandad de los Albañiles. Nuestro primer proyecto después de la Guerra era reconstruir Ventormenta.$B$BCuando terminamos, nos estafaron. Los nobles se negaron a pagarnos. A algunos de los Albañiles mayores les ofrecieron trabajos gubernamentales, pero VanCleef y yo los rechazamos debido a nuestra lealtad con los Albañiles. Él lideró un motín y abandonó la ciudad, jurando venganza.$B$BEs posible que su teniente, Bazil Thredd, sepa más sobre los planes de VanCleef. Está prisionero en las Mazmorras.",
    ["O"] = "Habla con el alcaide Thelagua en las Mazmorras.",
    ["T"] = "Bazil Thredd",
  },
  [390] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [391] = {
    ["D"] = "Dejemos esto claro, $n. No confío en ti. Pero en estas circunstancias, tendré que pasarlo por alto.$B$BEsto es lo que vamos a hacer: baja ahí y habla con Thredd. Si realmente estás de nuestra parte, mátale y tráeme su cabeza como prueba. Si descubrimos que estás de su parte, cuando te encontremos... morirás junto con el resto de esos gusanos.",
    ["O"] = "Mata a Bazil Thredd y lleva su cabeza al alcaide Thelagua en las Mazmorras.",
    ["T"] = "El motín de Las Mazmorras",
  },
  [392] = {
    ["D"] = "Durante los primeros años del encarcelamiento de Thredd, nunca recibió visitas. Supuse que ya no resultaba útil para la Hermandad y que le habían abandonado.$B$BEn fin, hace unos meses, todo cambió. Comenzó a recibir visitas regularmente... una, o quizás dos veces por semana. Era un hombre extraño... un tipo tranquilo. Tenía sospechas, pero sus papeles estaban en regla.$B$BEn los documentos venía que su nombre era Maelik; esta es su descripción. No creo que venga a visitar más a Thredd.",
    ["O"] = "Lleva la descripción sellada del visitante de Thredd al Baros Alexton en Ventormenta.",
    ["T"] = "Extraño visitante",
  },
  [393] = {
    ["D"] = "No tengo ni idea, $N. Hay alguien más que quizás pueda ayudarte y resulta que me debe un favor por... bueno, no hace falta entrar en detalles. En los cuarteles del Casco Antiguo, puedes encontrar al maestro Mathias Shaw del IS:7. Shaw está metido en la mayor parte de los negocios sucios o turbios de la ciudad.$B$BSi alguien puede saberlo, es él.",
    ["O"] = "Lleva la descripción sellada del visitante de Thredd al maestro Mathias Shaw en Ventormenta.",
    ["T"] = "Sombras del pasado",
  },
  [394] = {
    ["D"] = "Encuentra a Shaw en el Casco Antiguo. Dile que nos hemos encargado de la amenaza de Lescovar y cuéntale lo de tu participación en los planes del señor contra la ciudad. Seguro que se sentirá orgulloso de tener a otro héroe entre los ciudadanos de Ventormenta.$B$BLo más probable es que te envíe a ver al príncipe o a otro noble. Pero que no se te suba a la cabeza, $n. Los nobles pueden creer que gobiernan la ciudad, pero hay algunas cosas que se les escapan de las manos.",
    ["O"] = "Habla con Mathias Shaw en Ventormenta.",
    ["T"] = "La cabeza de la bestia",
  },
  [395] = {
    ["D"] = "¡Ah, casi lo olvidaba! Alexston vino cuando no estabas, quería hablar contigo. Sin duda quiere informar a los nobles de los tejemanejes de la Hermandad Defias.$B$BUn pequeño consejo, $n: si alguien te pregunta, da todos los detalles, menos que fue tu mano la que mató a Lescovar. Confía en mí. Sé cómo van esas cosas...$B$BY, con respecto a Trias, bueno, si puedes evitarlo, nunca jamás hables de él con nadie.",
    ["O"] = "Habla con Baros Alexton en Ventormenta.",
    ["T"] = "El fin de la Hermandad",
  },
  [396] = {
    ["D"] = "He preparado un informe para el rey. Creo que tú deberías entregárselo, junto con un resumen de tus descubrimientos. No te preocupes, sin duda te elogiará por hacer tu deber con el pueblo de Ventormenta.$B$BTu audiencia comenzará pronto, así que será mejor que te des prisa, todavía te queda un buen tramo hasta el Castillo de Ventormenta.",
    ["O"] = "Lleva el informe de Baros Alexston sobre la Hermandad Defias al rey Varian Wrynn en el Castillo de Ventormenta.",
    ["T"] = "Una audiencia con el Rey",
  },
  [397] = {
    ["D"] = "He oído que a Lord Wishock le gustó mucho la bebida.$B$BNos has hecho un gran servicio, $n. Los brujos de Ventormenta podremos seguir trabajando sin más interrupciones gracias a ti. Por tus esfuerzos, te he preparado un regalo especial. Habla con mi sirviente, Zggi, y pídele tu recompensa.",
    ["O"] = "Habla con Zggi, el sirviente de Zardeth de los Zarpanegra, para pedir tu recompensa.",
    ["T"] = "Nos has hecho un gran servicio",
  },
  [398] = {
    ["D"] = "Se busca: ¡Ojo de gusano!$b$bConsiderado una bestia incluso entre los propios gnolls, Ojo de gusano ha sido sentenciado a muerte por sus crímenes en contra de los Renegados. Este infame villano ha estado robando cuerpos y poniéndolos a disposición del ejército del rey Exánime. Ojo de gusano fue visto por última vez saqueando las tierras situadas en las proximidades de la Granja de Garren, al norte de Rémol.$b$bA partir de este momento, la ciudad de Rémol ofrece una recompensa al que sea capaz de llevar a cabo su ejecución. Muéstrale al ejecutor Zygand la garra del villano una vez ejecutada la sentencia.$b$bLa clemencia queda relegada a los débiles.",
    ["O"] = "Mata a Ojo de gusano y llévale su garra al ejecutor Zygand de Rémol para obtener una recompensa.",
    ["T"] = "Se busca: Ojo de gusano",
  },
  [399] = {
    ["D"] = "Parece que hace una eternidad desde que tan solo era un chico trabajando en la granja de Páramos de Poniente. Dicen que nunca puedes regresar, y es cierto. Doblemente cierto en mi caso, unos ladrones quemaron y tomaron mi casa.$B$BHe hablado con mi padre para descubrir el destino de alguna de mis posesiones, incluyendo mi primera brújula. No fue capaz de salvarlas. Sin embargo, también dice que deberían estar escondidas en la granja.$B$BEncontrarás la Hacienda de Alexston al oeste de la Colina del Centinela. ¿Quizás podrías ir y recuperarla?",
    ["O"] = "Ve a la casa de Baros Alexston en Páramos del Poniente y busca su brújula. A continuación, llévasela al Distrito de la Catedral, en Ventormenta.",
    ["T"] = "Principios humildes",
  },
  [400] = {
    ["D"] = "Beldin Brasacerada es el dueño de la tienda de mecánica local y además es el mejor técnico de máquinas de asedio. Pero es implacable con sus herramientas. ¡Te aseguro que rompe más llaves de arclite de las que le podemos suministrar!$B$BAcabamos de completar su último pedido. Si se lo llevas, seguro que te compensa el esfuerzo.$B$BSu tienda, el Almacén de Brasacerada está al noreste de Kharanos. Y es un lugar en el que se reúnen los pilotos veteranos de máquinas de asedio, así que, $gatento:atenta;, por si oyes algo interesante.",
    ["O"] = "Entrega las herramientas de Brasacerada a Beldin Brasacerada.",
    ["T"] = "Herramientas para Brasacerada",
  },
  [401] = {
    ["D"] = "Casi he acabado de traducir esa nota. Vuelve en un rato, probablemente habré acabado.",
    ["O"] = "Espera un momento y luego vuelve a hablar con Sirra Von\'Indi.",
    ["T"] = "Espera a que Sirra acabe",
  },
  [403] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "El barril de Cebatruenos custodiado",
  },
  [404] = {
    ["D"] = "La Plaga ha alcanzado los Claros de Tirisfal y ha infestado el área al oeste de Brill, cerca del puente viejo.$B$BDirígete allí y enfréntate a los muertos podridos. Trae sus garras pútridas y los Guardias de la Muerte te recompensarán.",
    ["O"] = "Llévale 7 garras pútridas al guardia de la muerte Dillinger de Rémol.",
    ["T"] = "Una tarea pútrida",
  },
  [405] = {
    ["D"] = "Bethor Duroyelo, un poderoso mago de alto rango de Entrañas, que cae al sur, me ha pedido que le envíe a alguien capaz de hacer frente a la Plaga. Tú, $n, eres $gel elegido:la elegida;.$B$BHázselo saber a Bethor y él te explicará cuál es tu misión. Yo desconozco los detalles pero sé que tiene algo que ver con reclutar a un exánime, y parece peligroso.$B$BPrepárate, $c, pues no debes fallar.$B$BBusca a Bethor en el Barrio de la Magia de Entrañas.",
    ["O"] = "Comunícale las órdenes de Sevren a Bethor Duroyelo, que está en Entrañas.",
    ["T"] = "El exánime pródigo",
  },
  [406] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [407] = {
    ["D"] = "¿Simples e inofensivas calabazas, verdad? Eso es lo que deben parecer... Si queremos detener el avance de Arthas por el norte y la infestación humana por el sur, tenemos que comenzar a valorar nuestro propio potencial.$b$bCon un poco de ingenio, una simple calabaza se convierte en un arma de nuestra Dama Oscura. Solo hay que echarle un poco de mi última fórmula.$b$bHan capturado a otro integrista Escarlata y está encerrado en el sótano del Mesón La Horca. Llévale a ese desgraciado esta calabaza.",
    ["O"] = "Llévale la calabaza envenenada al integrista Escarlata capturado que está encerrado en el sótano del Mesón La Horca.",
    ["T"] = "Campos de pena",
  },
  [408] = {
    ["D"] = "Los miembros de la Plaga han encontrado cobijo en los Molinos de Agamand. El punto más sólido es la Cripta de la Familia Agamand, lugar en el que su líder militar, un esqueleto llamado capitán Dargol, ha establecido su puesto de mando. Por medio de la necromancia, ha revivido a los ancestros de los Agamand y planea utilizarlos contra nosotros.$B$BEsto no puede ocurrir y tú has de evitarlo. Viaja a la Cripta de la Familia Agamand y derrota a los ancestros de los Agamand.$B$B¡Ah! Y tráeme la calavera del capitán Dargol.",
    ["O"] = "Mata a 8 ancestros de los Lamentos y a 8 ancianos putrefactos.$B$BMata al capitán Dargol y llévale su calavera al magistrado Sevren de Rémol.",
    ["T"] = "La Cripta de la Familia",
  },
  [409] = {
    ["D"] = "Afirmas que no estás $gsometido:sometida; a la voluntad del rey Exánime y el encantamiento que Bethor lanzó sobre mi libro lo corrobora. Sin embargo, para que pueda confiar en ti debes darme una prueba de que estás en contra de la Plaga.$B$BHay un viejo altar, a poca distancia de la costa sur de esta isla, que utiliza un poderoso agente de la Plaga llamado Lillith Nefara.$B$BCoge una vela de señalización de este cajón. Enciéndela en el altar y Lillith se despertará. Destruye a Lillith y creeré que en verdad eres $gun enemigo:una enemiga; del rey Exánime.",
    ["O"] = "Coge una vela de señalización.$B$BDespierta a Lillith Nefala y mátala.$B$BVuelve a ver a Gunther a su isla.",
    ["T"] = "Prueba de lealtad",
  },
  [410] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Sombra durmiente",
  },
  [411] = {
    ["D"] = "Dale esta gema infernal a mi viejo camarada Bethor. A través de ella podremos comunicarnos... y en verdad tengo mucho que contarle.",
    ["O"] = "Llévale la gema infernal a Bethor Duroyelo, que está en Entrañas.",
    ["T"] = "El regreso del exánime pródigo",
  },
  [412] = {
    ["D"] = "Tras analizar bien la situación de Gnomeregan, parecía que no solo no habíamos logrado erradicar a los troggs sino que además, habíamos transformado a la mayor parte de los gnomos en unos maléficos leprosos sin conciencia.$B$BOzzie y yo planeamos invertir ese horrible efecto con nuestro último invento: el Recombobulador. La máquina está casi acabada, pero necesitamos desesperadamente algunas levas estabilizadoras y engranajes giromecánicos. Ve a Gnomeregan, trata con los gnomos leprosos y consíguenos algunos.",
    ["O"] = "Lleva a Razzle Energiodentado a Kharanos 8 levas estabilizadoras y 8 engranajes giromecánicos.",
    ["T"] = "Operación Recombobulador",
  },
  [413] = {
    ["D"] = "Me gustaría que mi hermano Wellart probara mi nueva cerveza negra Brillante. Es un montaraz. Está destacado en la torre de vigilancia del sur, en la frontera del Loch Modan.$B$BNo se dedica a hacer cerveza, como los demás Barleybrew, pero le encanta beberla y sé que esta le gustará.$B$B¿Podrías llevarle este barril de cerveza negra Brillante de mi parte?",
    ["O"] = "Lleva el barril de cerveza negra Brillante al montaraz Cebadiz.",
    ["T"] = "Cerveza negra Brillante",
  },
  [414] = {
    ["D"] = "He estado bebiendo la cerveza negra Brillante esa que hizo mi hermano. ¡Está buenísima! Quiero que la pruebe un compañero mío. Se llama Kadrell y normalmente patrulla por el camino de Thelsamar en Loch Modan.$B$BPara llegar a Thelsamar, ve al sureste a través del túnel y gira al noreste cuando el camino se divida. Sigue en esa dirección y llegarás $gderecho:derecha; a Thelsamar.$B$BDale este barril de cerveza negra Brillante al montaraz Kadrell. ¡A ver qué le parece!",
    ["O"] = "Lleva el barril de cerveza negra Brillante al montaraz Kadrell.",
    ["T"] = "Cerveza negra para Kadrell",
  },
  [415] = {
    ["D"] = "Rejold Barleybrew de Las Birras ha estado preguntando por ti.$B$BQuiere hablar contigo sobre una nueva bebida que ha estado preparando... dijo algo de que quería que le ayudaras. Quizás quieras ir a Las Birras a ver qué necesita de ti.$B$BPodrías ganarte una cervecita...",
    ["O"] = "Habla con Rejold Cebadiz.",
    ["T"] = "La nueva bebida de Rejold",
  },
  [416] = {
    ["D"] = "Tenemos una infestación grave al oeste. Esos kobolds asquerosos se han instalado en las laderas. ¡Están cavando madrigueras y ensuciándolo todo!$b$bQueremos erradicarlos. Si los cazas y me traes sus orejas, puedes quedarte con su botín.$b$bLos encontrarás por toda la ladera al oeste de Thelsamar.",
    ["O"] = "Lleva 12 orejas de kobold al montaraz Kadrell a Thelsamar.",
    ["T"] = "Mata ratas",
  },
  [417] = {
    ["D"] = "Hildelve escribió en su diario que fue atacado por Zarpasarnosa, un gran oso zarpahelada. Ahuyentó a al bestia, pero no antes de que esta lo atacara salvajemente.$B$BA juzgar por el estado del cuerpo de Hildelve, Zarpasarnosa debió de regresar para matarlo.$B$BPero Hildelve tuvo tiempo de escribir un último deseo en su diario:$B$BZarpasarnosa debe pagar por esto.",
    ["O"] = "Mata a Zarpasarnosa. $B$BLleva su zarpa sarnosa y el diario de Hildelve al piloto Podomartillo.",
    ["T"] = "Vengar al piloto",
  },
  [418] = {
    ["D"] = "Aquí en Thelsamar siempre hay alguien que quiere comer, críos por todos lados, los obreros de la excavación y eso. Lo más conocido es la morcilla, ¿la has probado?$b$b¿No? Bueno, pues aquí hay que trabajar para comer, no creas que te libras porque seas $c.$b$bNecesito carne de oso, tripa de jabalí y un poco de icor de araña para darle sabor. ¡Tú trae eso y de cocinar me ocupo yo, Vidra!",
    ["O"] = "Lleva 3 trozos de carne de oso, 3 tripas de jabalí y 3 porciones de icor de araña a Vidra Hogartufa, en Thelsamar.",
    ["T"] = "Morcillas de Thelsamar",
  },
  [419] = {
    ["D"] = "Mi compañero y amigo, Mori Hildelve, que es piloto de máquinas de asedio, está perdido en los montes. Buscábamos un raro mineral necesario para fabricar un potente tipo de pólvora y durante nuestra búsqueda, subió con su máquina una empinada montaña ¡y la máquina se averió!$B$BAún convencido de que el mineral estaba en estas montañas, Hildelve me encargó que custodiara las máquinas mientras él continuaba la búsqueda a pie.$B$BPero ya han pasado días y por las noches oigo unos estremecedores aullidos en las montañas. Mori es duro como una roca, pero estoy preocupado.$B$BTe lo ruego, $N. Encuéntralo.",
    ["O"] = "Encuentra al piloto Hildelve.",
    ["T"] = "El piloto perdido",
  },
  [420] = {
    ["D"] = "Los troggs del túnel son extremadamente hostiles y no dudan en atacar a los viajeros que lo cruzan. Pero tú pareces fuerte. No deberías tener problemas para pasar.$B$BMmm... indicaciones... Pues para hablar con Senir tienes que ir a Kharanos.$B$BÁbrete paso a través del túnel y cuando lo hayas cruzado, sigue el camino. Te llevará directamente a Kharanos.",
    ["O"] = "Lleva el informe de Grelin a Senir Barbablanca a Kharanos.",
    ["T"] = "Las observaciones de Senir",
  },
  [421] = {
    ["D"] = "Lady Sylvanas ha encomendado a Varimathras la conquista de las tierras humanas y enanas del sur.$B$BPero ese necio, Arugal, charlatán de Dalaran y ahora bestia maldita del Castillo de Colmillo Oscuro, dejó que su imprudente magia causara estragos en el bastión estratégico del Bosque de Argénteos.$B$BNecesito a alguien muy diestro en combate para ayudar a arreglar el desastre de Arugal. Demuéstrame tu valía matando a 5 canosos Furia Lunar. Es fácil encontrar a esas bestias salvajes al final del camino, hacia la colina de abajo.",
    ["O"] = "Dalar Tejalba en El Sepulcro quiere que mates 5 canosos Furia Lunar.",
    ["T"] = "Demuestra tu valía",
  },
  [422] = {
    ["D"] = "No me puedo imaginar cómo Arugal consiguió que los Kirin Tor le aceptasen.$B$BLo que importa ahora es descubrir exactamente, qué magia utilizó Arugal. Así podré volverla contra él y asegurar el Bosque de Argénteos para la Dama Oscura.$B$BAl principio, Arugal se estableció en la granja de trigo que está justo al norte del puente. Uno de los mortacechadores, informó sobre algunos libros de hechizos que había visto, pero no pudo hacerse con ellos. Consigue el hechizo llamado Remedio de Arugal que está en esos libros.",
    ["O"] = "Recupera el Remedio de Arugal para Dalar Tejalba, en El Sepulcro.",
    ["T"] = "La locura de Arugal",
  },
  [423] = {
    ["D"] = "Tras examinar el trabajo de Arugal, se han confirmado mis peores sospechas. Ese viejo chiflado no estaba cualificado ni para limpiar orinales en Dalaran, y mucho menos para representar a los Kirin Tor en su momento más desesperado. ¡Imbéciles!$b$bArugal utilizó objetos encantados para reforzar su débil magia. Necesito examinar esos objetos. Continúa tu viaje y mata glotones Furia Lunar y almanegras Furia Lunar hasta que hayas reunido suficientes grilletes encantados para mi investigación. Puedes encontrar a estas repugnantes criaturas hacia el norte y el este.",
    ["O"] = "Lleva 6 grilletes de glotón y 3 grilletes de almanegra a Dalar Tejalba en El Sepulcro.",
    ["T"] = "La locura de Arugal",
  },
  [424] = {
    ["D"] = "Voy a tardar mucho más de lo que había pensado en descubrir los oscuros secretos que yacen tras los encantamientos que Arugal estaba usando.$B$BPero, mientras tanto, necesito que te encargues de un pequeño problema que nuestros acechadores negros han descubierto. Parece ser que Arugal permitió que su magia se extendiera hasta la Mina de Elem, en las colinas del sureste.$B$BLa mina resultó ser un lugar estratégico para el avance de Varimathras.$B$BQuiero que decapites al supervisor contaminado de la mina, Grimson el Pálido. Tras su muerte, la mina será nuestra.",
    ["O"] = "Mata a Grimson el Pálido y lleva su cabeza a Dalar Tejalba en El Sepulcro.",
    ["T"] = "La locura de Arugal",
  },
  [425] = {
    ["D"] = "Mi hermano Quinn y yo teníamos que explorar el Bosque de Argénteos e informar al sumo ejecutor Hadrec. Pero no llegamos muy lejos... Cuando atravesábamos una granja, fuimos atacados por el necrófago Ivar el Hediondo.$B$BConseguimos escapar de Ivar, pero hirió gravemente a Quinn. Ahora se está recuperando gracias a la poción que le diste, pero Ivar podría volver antes de que Quinn esté totalmente bien.$B$BMata a Ivar para que Quinn tenga tiempo para curarse. Generalmente, Ivar está en el establo de allí, esperando a que baje la guardia.",
    ["O"] = "Mata a Ivar el Hediondo y lleva la cabeza de Ivar a Rane Yorick en Los Dominios de Ivar.",
    ["T"] = "Ivar el Hediondo",
  },
  [426] = {
    ["D"] = "Los miembros de la Plaga están intentando establecer una base en los Molinos de Agamand. Si lo consiguen, podrán lanzar más ataques sobre Tirisfal.$B$BLas órdenes son claras: hay que detener a la Plaga en los Molinos.$B$BVe a los Molinos. Recoge costillas dentadas de los soldados armadóseos y los soldados testapartida, y calaveras ennegrecidas de los tirahuesos Ojosombra.$B$BPara llegar hasta los Molinos, sigue el camino en dirección oeste. Tras cruzar el puente, toma el siguiente desvío hacia el norte y continúa en esa dirección. Cuando veas los molinos de viento, dará comienzo la batalla.",
    ["O"] = "Recoge 5 costillas dentadas y 3 calaveras ennegrecidas, y después regresa ente el guardia de la muerte Dillinger de Rémol.",
    ["T"] = "La invasión de los molinos",
  },
  [427] = {
    ["D"] = "Los documentos que el ejecutor Arren nos ha proporcionado son exactamente lo que necesitábamos para dar un giro a nuestra batalla contra la desdichada Cruzada Escarlata. Ahora sabemos sus ubicaciones exactas en Claros de Tirisfal.$b$bPero el guardia de la muerte tiene otras preocupaciones mayores. El ejército del rey Exánime es más numeroso cada día que pasa. Necesitamos a alguien con un poco de \"iniciativa\" como tú para que envíe a la Cruzada Escarlata a la tumba.$b$bDemuéstrame que eres capaz de servir a la Dama Oscura; para ello, viaja al oeste hasta la torre que encontrarás pasada la Hacienda Solliden y mata a 10 guerreros Escarlata.",
    ["O"] = "El ejecutor Zygand de Rémol quiere que mates a 10 guerreros Escarlata.",
    ["T"] = "En guerra con la Cruzada Escarlata",
  },
  [428] = {
    ["D"] = "Hace un par de semanas, dos de nuestros Mortacechadores, Rane y Quinn Yorick, fueron a una misión de reconocimiento por la parte norte de Argénteos. No hemos vuelto a tener noticias suyas.$B$BQuizás hayan caído en manos de la Plaga pero, si es así, necesito una confirmación. Encuentra a estos mortacechadores.$B$BSu primer objetivo era explorar las granjas al norte de Argénteos. Deberías comenzar por allí.",
    ["O"] = "Encuentra a los Mortacechadores Quinn y Rane Yorick.",
    ["T"] = "Los Mortacechadores desaparecidos",
  },
  [429] = {
    ["D"] = "El necrófago, Ivar el Hediondo, hirió de gravedad a mi hermano Quinn y no sé si podrá curarse sin ayuda mágica. Si bien yo no soy un boticario, sé que nuestros boticarios pueden preparar pociones curativas a partir de corazones de lupos decolorados.$B$BConsigue esos corazones y llévaselos al boticario Renferrel en El Sepulcro, al sur. Después, vuelve aquí con la poción.$B$BEncontrarás muchos lupos entre aquí y la Huerta de Malden, al este.",
    ["O"] = "Consigue 6 corazones de lupo decolorados y llévaselos al boticario Renferrel en El Sepulcro.",
    ["T"] = "Corazones salvajes",
  },
  [430] = {
    ["D"] = "Aquí está la poción para curar a Quinn Yorick.$B$BAhora, si me perdonas, tengo algo muy importante que hacer...",
    ["O"] = "Lleva la poción de Quinn a Quinn Yorick en Los Dominios de Ivar, al norte de El Sepulcro.",
    ["T"] = "Vuelve con Quinn",
  },
  [431] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Velas de señalización",
  },
  [432] = {
    ["D"] = "¡Esos malditos troggs están destrozando mi excavación! ¡Mira! ¡Ahí va otro barril de polvo!$B$B¡Ay! ¡Cómo me gustaría bajar ahí y retorcerles a todos ese pescuezo huesudo que tienen! ¡Quiero matarlos a todos!$B$BEsto no puede ser bueno para la salud. Tanta ira, tanto estrés... ¿Qué puedo hacer sin perjudicarme la salud? ¡Nada!$B$BLos mataría yo mismo, pero mi puntería no es la que era. ¡Entra tú en la cantera y mata a un puñadito de esos bichos asquerosos! Te pagaré... ¡Tú hazles daño! ¡Mátalos!",
    ["O"] = "Mata a 6 aplastacráneos Comepiedras para Foreman Petrocejas de la Cantera de Gol\'Bolar.",
    ["T"] = "¡Malditos troggs!",
  },
  [433] = {
    ["D"] = "Como miembro del Senado y de la Liga de Exploradores, he asumido la responsabilidad de ocuparme de la infestación trogg que ha invadido nuestras tierras.$B$BPorque, sin motivo alguno, han convertido en un caos la Cantera de Gol\'Bolar. Van saliendo a medida que profundizamos en nuestra excavación, y destruyen nuestro equipo y ahuyentan a nuestros mineros. Así que tenemos que ir exterminando a cuantos podemos, luego reconstruimos todo y volvemos al trabajo.$B$BSi me ayudas con los troggs, te recompensaré.",
    ["O"] = "Mata a 10 quebrantahuesos Comepiedras para el senador Mehr Piedrasanta de la Cantera de Gol\'Bolar.",
    ["T"] = "El servidor público",
  },
  [434] = {
    ["D"] = "Cuando le ponga el disfraz al robot espía, irá a hablar con Lescovar. Los guardias le dejarán pasar sin problemas.$B$BMientras estabas reuniendo los materiales, me encargué de informar a Marzon de que tenía que reunirse de nuevo con Lescovar. Incluso le di información falsa.$B$BLescovar dirá a sus guardias que se retiren y esperará a Marzon aquí fuera, en el jardín. En cuanto empiecen a hablar de los Defias, te toca mover ficha. Tienes que matarlos antes de que vuelvan los guardias. Cuando hayas terminado, habla con Trias.",
    ["O"] = "Quédate en el jardín hasta que Lord Gregor Lescovar y Marzon el Filo Silencioso hayan terminado de hablar. A continuación, mátalos antes de volver a reunirte con Elling Trias en Ventormenta.",
    ["T"] = "El ataque",
  },
  [435] = {
    ["D"] = "Los lobos que están fuera no se mueven y me miran de forma malvada cada vez que salgo por esa puerta. Deben de estar increíblemente hambrientos si piensan que puede merecer la pena comerme.$B$BTengo que informar a mi compañero de equipo, el Mortacechador Rane Yorick. Y, para conseguir llegar donde está Rane, necesitaré ayuda con esos lobos.$B$B¡Por favor, escóltame! Hay un montón de lobos ahí fuera, así que tendríamos más oportunidades si tienes algún amigo que pueda ayudarnos...",
    ["O"] = "Escolta a Erland a través de los lobos, hasta llegar a Rane Yorick.",
    ["T"] = "Escolta a Erland",
  },
  [436] = {
    ["D"] = "El prospector Vetaferro dirige la excavación de unas antiguas ruinas al este del lago. Últimamente está haciendo pocos progresos, a pesar de todos los suministros que le hemos enviado.$b$bVetaferro es un enano honrado y tenaz que valora los resultados. Por eso sospecho que hay otros factores en su contra.$b$bVe a la Excavación de Vetaferro y habla con Magmar Tallavil. Él se encarga de los detalles de la excavación y sabrá a qué se debe el retraso en los trabajos.$b$bPara llegar a la excavación, rodea el extremo sur del lago y luego dirígete al este.",
    ["O"] = "Habla con el explorador Tallavil.",
    ["T"] = "La excavación de Vetaferro",
  },
  [437] = {
    ["D"] = "Los informes iniciales de nuestros exploradores en Argénteos, muestran que los Putrepellejo tienen una base en una vieja granja del norte, El Campo Muerto. Hemos lanzado pequeños asaltos contra ellos, pero cada vez que lo intentamos, el alma en pena Latipesadilla les ayuda, utilizando su magia para diezmar nuestras tropas.$B$BQueremos destruir a Latipesadilla.$B$BVete a El Campo Muerto y ataca a los Putrepellejo. Si no ves a Latipesadilla, mata a los Putrepellejo hasta que aparezca y, después, mátala a ella.$B$BSu esencia se convertirá en polvo. Tráeme ese polvo como prueba.",
    ["O"] = "Mata gnolls Putrepellejo en El Campo Muerto.$B$BCuando aparezca Latipesadilla, mátala.$B$BLleva la esencia de Latipesadilla al sumo ejecutor Hadrec en El Sepulcro.",
    ["T"] = "El Campo Muerto",
  },
  [438] = {
    ["D"] = "$n, hemos descubierto una nueva infestación de la Plaga: El Viejo Embarcadero. El embarcadero está al este de El Sepulcro y está muy protegido por los Putrepellejo y otros no-muertos.$B$BDebe de haber una razón para la ocupación de la Plaga de esa zona. Vete a El Viejo Embarcadero y descubre por qué está allí la Plaga.",
    ["O"] = "Ve a El Viejo Embarcadero.$B$BEncuentra la razón por la que la Plaga está ahí.",
    ["T"] = "El Viejo Embarcadero",
  },
  [439] = {
    ["D"] = "Entre los cadáveres sobresale una mano, abierta. Es la mano de una mujer, con marcas de la peste, pero relativamente fresca.$B$BEn la mano hay un anillo. Al sacar el anillo e inspeccionarlo revela una inscripción:$B$B\"Para Deliah\"",
    ["O"] = "Lleva el anillo de Deliah al sumo ejecutor Hadrec en El Sepulcro.",
    ["T"] = "Pistas de Putrepellejos",
  },
  [440] = {
    ["D"] = "Si quieres encontrar al marido o bienamado de Deliah, quien le dio el anillo... habla con el magistrado Sevren en Rémol. Debería tener registros de los fallecimientos recientes y, con suerte, podrás descubrir quién es Deliah.$B$BPero yo no contendría el aliento. Quienquiera que le dio el anillo, está probablemente muerto. Y, si no está muerto, probablemente ya no le importa Deliah.",
    ["O"] = "Lleva el anillo de Deliah al magistrado Sevren en Rémol.",
    ["T"] = "El anillo grabado",
  },
  [441] = {
    ["D"] = "Deliah tenía un marido, Raleigh, que ahora vive en Entrañas. Si la Deliah que vivía en Rémol es la dueña de este anillo, Raleigh lo sabrá.$B$BRaleigh está en el Barrio del Comercio, en Entrañas.",
    ["O"] = "Lleva el anillo de Deliah a Raleigh.",
    ["T"] = "Raleigh y Entrañas",
  },
  [442] = {
    ["D"] = "Ha llegado el momento de asaltar el punto fuerte de la Plaga en Argénteos: la Isla de Fenris.$B$BDebes volver a la isla y destruir a Thule Corvozarpa. ¡Quiero su cabeza!$B$BEsta misión es tan peligrosa como vital, $N. No puedes fallar.",
    ["O"] = "Lleva la cabeza de Thule al sumo ejecutor Hadrec en El Sepulcro.",
    ["T"] = "Asalto a la Isla de Fenris",
  },
  [443] = {
    ["D"] = "Antes de lanzar un asalto en la Isla de Fenris, debemos determinar quién está detrás de los gnolls Putrepellejo. Nuestros boticarios creen que su origen yace en el extraño líquido que fluye por sus cuerpos.$B$BVete a la Isla de Fenris, al este de El Viejo Embarcadero y recoge icor de los gnolls que encuentres allí. Llévaselo al boticario Renferrel, está fuera de El Sepulcro.",
    ["O"] = "Lleva 8 botellas de icor de Putrepellejo al boticario Renferrel en El Sepulcro.",
    ["T"] = "Icor de Putrepellejo",
  },
  [444] = {
    ["D"] = "He reducido el icor que me has traído y he separado sus componentes básicos. Mis descubrimientos no son concluyentes, pero he determinado que en la creación del icor se utilizó brujería y necromancia.$B$BMientras sigo estudiando el icor, quiero que le lleves una muestra a Bethor Duroyelo en Entrañas. Bethor no es un boticario, pero su conocimiento de la magia, puede ayudarnos a descubrir el misterio de los Putrepellejo.$B$BVete. Encontrarás a Bethor en el Barrio de la Magia de Entrañas.",
    ["O"] = "Lleva la muestra de icor a Bethor Duroyelo en Entrañas.",
    ["T"] = "El origen de los Putrepellejos",
  },
  [445] = {
    ["D"] = "El tiempo es un lujo que no nos podemos permitir quienes trabajamos para Lady Sylvanas. Pero seguro que ya te habías dado cuenta.$B$BComo miembro de la Sociedad Real de Boticarios es mi deber compartir mis conocimientos con mis colegas para que con nuestro esfuerzo colectivo logremos algún día proporcionarle a la Dama Oscura su tan ansiada Nueva Peste.$B$BLlévale estas conclusiones al boticario Renferrel que está en El Sepulcro en el Bosque de Argénteos. Sigue los caminos que parten en dirección sur desde Rémol. Desde Entrañas, dirígete hacia el suroeste.",
    ["O"] = "Llévale las conclusiones del boticario Johaan al boticario Renferrel del Bosque de Argénteos.",
    ["T"] = "Entrega a Bosque de Argénteos",
  },
  [446] = {
    ["D"] = "Este icor fue creado por un viejo amigo mío: Thule Corvozarpa. Antes de la Gran Guerra, era un joven mago muy prometedor, pero cuando el Rey Exánime ocupó el trono, traicionó a sus hermanos y aliados con la Plaga.$B$BNo me había dado cuenta de que todavía sigue con vida, ni de que él es quien lidera a los Putrepellejo contra nosotros.$B$BConozco muchos de sus hechizos y puedo ayudar a preparar una poción para contrarrestarlos. Lleva este pergamino al boticario Renferrel. Tiene los conjuros necesarios para la poción.",
    ["O"] = "Lleva el pergamino de Bethor al boticario Renferrel en El Sepulcro de Argénteos.",
    ["T"] = "Thule Corvozarpa",
  },
  [447] = {
    ["D"] = "Arthas cuenta con innumerables tropas. Pero, con una Nueva Plaga podríamos erradicar tanto a al ejército de la Plaga como a la infestación humana.$B$BMis estudios han demostrado que la sangre de araña combinada con una toxina derivada del corazón de un grisoso, resulta en un elixir letal. Reúne muestras de las arañas en Penumbra de las Celerácnidas, al noroeste, y de los osos que vagan por el Bosque de Argénteos. Lleva los componentes al maestro boticario Faranell de la Sociedad Real de Boticarios, en Entrañas.",
    ["O"] = "Consigue 6 corazones de grisoso y 6 muestras de sangre de Celearácnida y llévaselas al maestro boticario Faranell en Entrañas.",
    ["T"] = "Una fórmula mortal",
  },
  [448] = {
    ["D"] = "Antes de iniciar un asalto a la Isla de Fenris, ve a ver al sumo ejecutor Hadrec. Es posible que tenga equipamiento o información adicional que pueda serte útil.",
    ["O"] = "Preséntate ante el sumo ejecutor Hadrec en El Sepulcro.",
    ["T"] = "Preséntate ante Hadrec",
  },
  [449] = {
    ["D"] = "Si bien las heridas de Quinn se están curando, quiero entregar nuestro informe al sumo ejecutor Hadrec tan pronto como sea posible. Contiene información vital para la ocupación de los Renegados de Argénteos.",
    ["O"] = "Lleva el informe  de los Mortacechadores al sumo ejecutor Hadrec en El Sepulcro.",
    ["T"] = "El informe de los Mortacechadores",
  },
  [450] = {
    ["D"] = "Puedes seguir ayudando a la Sociedad Real de Boticarios, $n.$B$BCuando Arugal utilizó su magia maligna en el bosque, maldijo a uno de nuestros miembros más prometedores, el boticario Berard. Ahora es un sirviente de Arugal, sin dominio de sí mismo. En estas condiciones, Berard no nos sirve de nada. Pero sus descubrimientos podrían servirnos. Estaba estudiando una antigua plaga que acabó con la vida acuática del Lago Lordamere años atrás.$B$BVe a buscar el Diario de Berard. Está en su estudio, el la Posada de la Aldea Piroleña, y llévaselo al boticario Renferrel.",
    ["O"] = "Recupera el Diario del boticario Berard de la Aldea Piroleña y llévaselo al boticario Renferrel en El Sepulcro.",
    ["T"] = "Una fórmula mortal",
  },
  [451] = {
    ["D"] = "Según el Diario de Berard, el musgo de los vigilantes del Lago y los trepadores podría ser útil para nuestro objetivo de crear una Nueva Plaga. Esas bestias viven en las islas del Lago Lordamere, al este.$B$BTambién menciona un tumor endurecido muy extraño que descubrió en un múrloc Anca Vil. Por lo visto, el tumor es tan poco común, que Berard solo pudo obtener uno.$B$BReúne muestras del musgo y del tumor endurecido y llévaselas al maestro boticario Faranell en Entrañas.",
    ["O"] = "Lleva 6 muestras de musgo de trepador del Lago, 6 muestras de musgo de vigilante del Lago y un tumor endurecido al maestro boticario Faranell en Entrañas.",
    ["T"] = "Una fórmula mortal",
  },
  [452] = {
    ["D"] = "¡Has llegado justo a tiempo, $N! $b$bEl Consejo Piroleño está a punto de regresar. Arugal tiene que encargarse de otras cosas en el bastión. Utiliza el Consejo como si fueran marionetas, para mantener a los ineptos esbirros de la Aldea Piroleña bajo su control. Pero creo que sin la ayuda del Consejo Piroleño, los hijos de Arugal se volverían contra su maestro.$B$BNo puedo encargarme de todo sin ayuda. Te necesito. Juntos podemos acabar con el Consejo para Lady Sylvanas.",
    ["O"] = "Ayuda al mortacechador Faerleia a destruir al Consejo Piroleño.",
    ["T"] = "La emboscada de Aldea Piroleña",
  },
  [453] = {
    ["D"] = "Buena suerte, $n. Espero que encuentres al tipo este... y que lo encuentres vivo.$B$BSi dejó la seguridad de Villa Oscura, podría haber sido víctima de las bestias salvajes.",
    ["O"] = "Encuentra a la figura misteriosa.  Las pistas que tienes: $B$BNo es oriundo de Villa Oscura. $B$BEs una persona nerviosa, que se altera fácilmente. $B$BDejó Villa Oscura y se dirigió al oeste.",
    ["T"] = "Encuentra a la figura",
  },
  [454] = {
    ["D"] = "Antes llevábamos juntos los barriles de pólvora. Pero ahora que ha pasado esto... Ahora tenemos que dividir el trabajo, Miran lleva los barriles y yo vigilo los otros.$B$BNo me gusta que Miran vaya por ahí solo con los barriles. ¿Te importa acompañarlo?",
    ["O"] = "Habla con Miran.",
    ["T"] = "Tras la emboscada",
  },
  [455] = {
    ["D"] = "Los orcos del clan Faucedraco tienen un refugio en Los Humedales ¡y ahora se han adentrado en Dun Algaz! No podemos permitir que continúen afianzándose aquí o Loch Modan perderá el contacto con el Puerto de Menethil, nuestro puerto y bastión en Los Humedales.$B$BAtraviesa Dun Algaz y dirígete a Los Humedales, ¡cuando estés allí mata a tantos orcos como puedas! Informa a Valstag Quijahierro en Puerto de Menethil.$B$BMenethil está pasado Dun Algaz, siguiendo el camino hacia el Norte y al Oeste en el cruce.$B$BContamos contigo, $n. No nos falles.",
    ["O"] = "Atraviesa Dun Algaz.$B$BMata 8 exploradores Faucedraco y 6 brutos Faucedraco.$B$BInforma a Valstag Quijahierro de Puerto de Menethil.",
    ["T"] = "El asedio de Algaz",
  },
  [456] = {
    ["D"] = "Saludos, $N. Soy el conservador Ilthalaine. He venido a Cañada Umbría para asegurarme de que se mantiene el equilibrio de la naturaleza. $b$bEsta primavera ha llovido más de lo normal, lo que ha beneficiado mucho a algunas criaturas del bosque, pero ha perjudicado a otras. $b$bAhora hay demasiados sables de la noche y jabalíes cardo; la zona no puede alimentar a tantas bestias. Joven $c, reduce las poblaciones de jabalí cardo y sable de la noche para mantener la armonía de la naturaleza.",
    ["O"] = "Mata 7 sables de la noche y 4 jabalíes cardo jóvenes y ve a ver al conservador Ilthalaine.",
    ["T"] = "El equilibrio de la naturaleza",
  },
  [457] = {
    ["D"] = "Acabar con los ejemplares más jóvenes de Cañada Umbría ha sido un buen comienzo, $N, pero queda mucho por hacer. $b$bSi no tomamos medidas, se agotarán los recursos del bosque. Exterminar animales es un mal necesario por el bien de todos los que comparten la tierra. Para mantener el equilibrio, ve al bosque y acaba con los jabalís cardo y sables de la noche sarnosos.",
    ["O"] = "El conservador Ilthalaine quiere que mates 7 sables de la noche sarnosos y 7 jabalíes cardo.",
    ["T"] = "El equilibrio de la naturaleza",
  },
  [458] = {
    ["D"] = "Me alegro de que hayas llegado, $c. El susurro de los espíritus del bosque me ha traído extrañas nuevas. $b$bLa misteriosa protectora de los bosques, Tarindrella, ha regresado a Cañada Umbría. Hacía años que la dríade no venía a los bosques de Kalimdor; su presencia aquí no presagia nada bueno. $b$bBusca a Tarindrella y averigua qué asuntos la han traído aquí. Uno de los centinelas la ha avistado al suroeste de Aldrassil.",
    ["O"] = "Busca a la dríade Tarindrella.",
    ["T"] = "La protectora de los bosques",
  },
  [459] = {
    ["D"] = "En los bosques de Teldrassil está creciendo algo siniestro. Investiga en las colinas en las que vivían los pacíficos furbolgs. Han abandonado la zona y se están reuniendo bajo el nombre de la tribu Tuercepinos. $b$bSolo la corrupción del musgovil puede haber causado esta transformación. La zona está infestada de grells y grellkin, que amenazan a los residentes de Cañada Umbría. $b$bEnfréntate a ellos y averigua si se hallan bajo el encantamiento del musgovil.",
    ["O"] = "Lleva 8 muestras de musgovil a Tarindrella.",
    ["T"] = "La protectora de los bosques",
  },
  [460] = {
    ["D"] = "\"No pareces un Putrepellejo. Bien. Ya había perdido la esperanza...$B$BMe llamo Alaric, y fui el jefe de los sirvientes y guardaespaldas de Thule. Utilizó su magia para \"protegerme\" de la Peste de no-muertos, pero su hechizo me dio una no-vida de la que no puedo escapar. Después de crear a los malvados gnolls Putrepellejo, Thule me decapitó... y dio mi cabeza a los gnolls como juguete.$B$BPor favor, llévame con mi cuerpo. Creo que los gnolls lo enterraron aquí, en el Castillo de Fenris cerca del establo, en una tumba sin nombre.”",
    ["O"] = "Lleva la cabeza de Alaric a su tumba.",
    ["T"] = "Descansar en piezas",
  },
  [461] = {
    ["D"] = "Conozco un nicho oculto en el castillo que contiene un objeto muy valioso para Thule y sus viejos colegas. También contiene algo que podrías encontrar útil. ¡Llévame allí!$B$BEl nicho está en la torre suroeste del castillo, detrás de una estantería polvorienta.$B$BCoge mi cuerpo, pero deja mis botas. De todas formas... nunca me quedaron bien...",
    ["O"] = "Lleva a Alaric al nicho oculto.",
    ["T"] = "El nicho oculto",
  },
  [462] = {
    ["D"] = "Other than you, we sent two scouts through Algaz.  Only one returned.  Here\'s what he discovered:$B$BThe Dragonmaws sent one of their Lieutenants, Maruk Wyrmscale, to organize a crew of orcs in Algaz and cut off the land route to Menethil Harbor!$B$BWe have to stop this, now.  Take out Maruk, and then report to Valstag Ironjaw in Menethil Harbor.$B$BMaruk is headquartered in a small cave, across a pond between the second and third tunnels of Algaz.  Menethil is through Algaz, then west along the road.",
    ["O"] = "Kill Maruk Wyrmscale$B$BReport to Valstag Ironjaw in Menethil Harbor.",
    ["T"] = "<UNUSED>",
  },
  [463] = {
    ["D"] = "¿Has oído hablar del Guardaverde? ¿No lo estarás buscando, verdad? Si lo buscas es que estás $gloco:loca;, ¿pero quién soy yo para apartar a un idiota de su muerte?$B$BSi estás buscando a esa bestia, he oído decir que está en los pantanos, al este del camino cuando se divide hacia Dun Modr. Está escondido entre los crocoliscos... ¡y cosas aún peores!$B$BDeja aquí tu dinero. No lo necesitarás allí a donde vas... no querrás que Verdín se rompa un diente con tus monedas cuando te parta en dos de un bocado, ¿verdad?",
    ["O"] = "Encuentra al Guardaverde.",
    ["T"] = "El Guardaverde",
  },
  [464] = {
    ["D"] = "El clan orco Faucedraco fue prácticamente destruido al final de la Segunda Guerra, cuando perdieron su control sobre los dragones rojos que eran sus mascotas. Los dragones no estaban muy contentos con sus antiguos dueños y dieron caza despiadadamente a todos los Faucedraco. Los pocos que quedaron huyeron de Grim Batol y se refugiaron en las montañas al este de la Excavación de Whelgar.$B$BAhora han ganado fuerza y tienen un campamento al noreste de Menethil.$B$BQueremos que ataques ese campamento. Recoge sus estandartes de guerra y tráemelos como prueba.",
    ["O"] = "Lleva 8 estandartes de guerra Faucedraco al capitán Puñorrecio.",
    ["T"] = "Estandartes de batalla",
  },
  [465] = {
    ["D"] = "Mientras estabas combatiendo a los Faucedraco, descubrimos más cosas sobre sus planes.$B$BNek\'rosh ha construido catapultas ¡y pretende asediar Menethil! ¡Además, debido a la lucha en Dun Algaz no podemos recibir refuerzos!$B$BTienes que acabar con esas catapultas. Están en el campamento Faucedraco. Toma, coge esta yesca.$B$BCuando hayas terminado, encuentra y mata a Nek\'rosh. Necesitamos acabar con esa bestia Faucedraco... de raíz.",
    ["O"] = "Destruye las catapultas Faucedraco.",
    ["T"] = "La estrategia de Nek\'rosh",
  },
  [466] = {
    ["D"] = "He oído decir que se encontró un extraño mineral, incendicita, en Los Humedales, en una cueva al noroeste de la Puerta de Algaz. La incendicita es un material volátil y es difícil de utilizar sin que explote, ¡pero estoy seguro de que si se funde de la forma adecuada podría hacer proyectiles de cañón que harían un daño tremendo!$B$BTráeme mineral de incendicita y encontraré a alguien que se atreva a experimentar con él.$B$BOh, una cosa más, para extraer el mineral de un filón tendrás que saber minería. ¡Si no eres $gminero:minera; lleva uno contigo a la cueva!",
    ["O"] = "Lleva 6 piezas de mineral de incendicita al piloto Roscapiedra de Dun Morogh.",
    ["T"] = "Búsqueda de incendicita",
  },
  [467] = {
    ["D"] = "¡Al piloto Roscapiedra le encanta su máquina de asedio! ¡Y siempre anda buscando maneras de mejorar su cilindrada!$B$BHa oído hablar de un mineral muy raro y necesita a alquien que se lo consiga. Si te apetece una aventurilla, ve a hablar con él en el Almacén de Brasacerada en Dun Morogh.",
    ["O"] = "Habla con el piloto Roscapiedra.",
    ["T"] = "La búsqueda de los Roscapiedra",
  },
  [468] = {
    ["D"] = "El montaraz Rockgar está buscando voluntarios para una misión que les llevará a través de Dun Algaz hasta Los Humedales.$B$BSi quieres mojarte las botas en ese embarrado condado del Norte, habla con Rockgar en Dun Algaz, siguiendo el camino hacia el Norte, pasada la torre de vigilancia.",
    ["O"] = "Habla con el montaraz Rockgar.",
    ["T"] = "Preséntate ante el montaraz Rockgar",
  },
  [469] = {
    ["D"] = "¡Saludos $gviajero:viajera;!$b$bNo creo que te dirijas hacia Menethil, ¿verdad? Si no lo haces, te recomendaría hacer un alto, este es un buen lugar para hacer unas compras o para disfrutar de una buena pinta y de algo de descanso.$b$bAhora bien, si estás yendo hacia allí, no creo que te importe llevarle este fardo de pieles de crocolisco a James Halloran, el peletero.$b$bLo haría yo mismo, pero últimamente mi mujer no está de muy buen humor, me gustaría verla lo más tarde que pueda.",
    ["O"] = "Entrega el fardo de pieles de crocolisco a James Halloran, el peletero, en Puerto de Menethil.",
    ["T"] = "Entrega cotidiana",
  },
  [470] = {
    ["D"] = "¡Durante mi visita semanal a la Tumba de Barbaférrea fui atacada por espantosos y pringosos mocos! Por supuesto, me entró pánico.$B$BLe lancé mi bolsa a uno de ellos, ¡pero no le hizo nada! El moco la absorbió... Por fortuna eso me dio algo de tiempo para escapar.$B$B¡La mala noticia es que necesito recuperar el contenido de mi bolsa!$B$BNo sé a qué moco le lancé la bolsa, ¡todos parecían iguales! La Tumba de Barbaférrea está al noreste de aquí, cerca de Dun Modr. ¡Muchísimas gracias!",
    ["O"] = "Uno de los mocos de la Tumba de Barbaférrea tiene la Bolsa de Shida, recupérala y devuélvesela en Puerto de Menethil.",
    ["T"] = "Abriéndose paso entre los mocos",
  },
  [471] = {
    ["D"] = "Envié a mi aprendiz a los pantanos para desollar algunos de nuestros famosos crocoliscos gigantes de Los Humedales. Cuando no regresó salí a buscarlo... Basta decir que no fue una visión muy agradable.$B$BLa matrona dice que el chico podría recuperarse, ¡pero hasta entonces sigo necesitando pieles!$B$B... Si quisieras matar unas cuantas de esas bestias no estaría en contra, después de todo lo que hicieron. Encontrarás a los crocoliscos al Norte, siguiendo la costa y pasadas las casuchas de los múrlocs.",
    ["O"] = "Reúne 6 pieles de crocolisco gigante y llévaselas a James Halloran en Puerto de Menethil.",
    ["T"] = "Deberes de aprendiz",
  },
  [472] = {
    ["D"] = "¡Dun Modr ha caído en manos de los enanos Hierro Negro!$B$BMis heridas son graves, $c. ¡Y casi todo mi regimiento fue asesinado! Los matones Hierro Negro nos atacaron antes de que pudiéramos reagruparnos tras la emboscada del Puente Thandol.$B$BNuestro líder, Largatrenza, hizo sonar el cuerno de retirada. Cuando dejamos la ciudad fui herido en la espalda con un hacha perdida. Todo se volvió negro.$B$BDesperté aquí, en Menethil, pero estoy preocupado por mis compañeros. Aún abrigo esperanzas. ¡Quizás Largatrenza siga con vida! ¡Ve a ver si puedes encontrarle cerca de Dun Modr, $r!",
    ["O"] = "Busca a Largatrenza junto a la ciudad de Dun Modr.",
    ["T"] = "La caída de Dun Modr",
  },
  [473] = {
    ["D"] = "$n. El clan orco Faucedraco está mostrando su fuerza, y no solo en Algaz. El capitán Puñorrecio ha pedido hablar con quienes quieran ofrecer su ayuda contra esos orcos. Lo encontrarás en el castillo, en la Sala de Mandos.",
    ["O"] = "Habla con el capitán Puñorrecio.",
    ["T"] = "Preséntate ante el capitán Puñorrecio",
  },
  [474] = {
    ["D"] = "Ahora que las catapultas han ardido, encuentra y mata al jefe Nek\'rosh.",
    ["O"] = "Mata al jefe Nek\'rosh.$B$BLleva la cabeza de Nek\'rosh al capitán Puñorrecio.",
    ["T"] = "Derrota a Nek\'rosh",
  },
  [475] = {
    ["D"] = "Se avecinan problemas en el bosque.$b$bGaerolas Talvethren es el Gran Alcaide de los druidas de la Garra hibernantes en Túmulo de Ban\'ethil. Su función como protector de los Adormecidos es preservar su seguridad de modo que no se rompa el pacto que tienen con Ysera. Viaja al este hasta la Aldea Brisa Estelar para hablar con Gaerolas y regresa para informarme de todo. Cuando sepa que mis hermanos duermen apaciblemente, me quedaré más tranquilo.",
    ["O"] = "Busca a Gaerolas Talvethren en la Aldea Brisa Estelar.",
    ["T"] = "Una brisa problemática",
  },
  [476] = {
    ["D"] = "¡La tribu de los Tuercepinos está sumida en la corrupción!$b$bLos antes apacibles furbolgs se han vuelto en contra de los protectores del bosque. Me tendieron una emboscada cuando me marchaba para Túmulo de Ban\'ethil y saquearon Aldea Brisa Estelar. Ursal el Violento, su jefe, está empleando los maléficos poderes del musgovil para volverlos locos.$b$bYo estoy malherido y no puedo llevarle estas trágicas noticias a Athridas por lo que tendrás que hacerlo tú, joven $c. Esperemos que los trastornados Tuercepinos no hayan llegado a Ban\'ethil.",
    ["O"] = "Ve a ver a Athridas Manturso a Dolanaar.",
    ["T"] = "Corrupción en Tuercepinos",
  },
  [477] = {
    ["D"] = "Como sabrás, la ciudad de Molino Ámbar sigue siendo una fuente de oposición humana, principalmente gracias al apoyo que reciben de los magos de Dalaran.$B$BNo sé cuál es su plan, pero que se hayan interesado por un lugar tan remoto como Molino Ámbar, indica que tiene que ser más importante de lo que pensamos.$B$BHan estado enviando cajas por carro desde Trabalomas. Muchos de esos suministros acaban en un pequeño campamento al norte de la Aldea Piroleña. Consigue los contenidos de una caja y tráemelos.",
    ["O"] = "Recupera el contenido de una de las cajas de zahoríes de Dalaran. Encontrarás la Aldea Piroleña hacia el sur.",
    ["T"] = "Pasa la frontera",
  },
  [478] = {
    ["D"] = "<Rápidamente coges el colgante de la caja y vuelves a colocar la tapa.>",
    ["O"] = "Lleva el extraño colgante al sacerdote de las Sombras Allister en El Sepulcro.",
    ["T"] = "Mapas y runas",
  },
  [479] = {
    ["D"] = "Dalar está intentando localizar la fuente de los hechizos de los magos. Por ahora, tendremos que ralentizar su avance como podamos...$B$BLos conjuradores, magos y protectores tienen, sin lugar a dudas, los colgantes. Quítaselos.$B$BSigue hacia el sur por el camino principal y después, dirígete al este, a Molino Ámbar.",
    ["O"] = "Consigue 8 colgantes de Dalaran al sacerdote de las Sombras Allister en El Sepulcro.",
    ["T"] = "Las investigaciones de Molino Ámbar",
  },
  [480] = {
    ["D"] = "Un archimago de gran poder está supervisando la reactivación del nodo de energía Ley. Con el progreso que han conseguido, nuestra única esperanza de detenerles es matándole. Será difícil, estará protegido por muchos magos y guardianes.$B$BDebes ir y acabar con él rápidamente. Cuando esté muerto, tráeme su bastón. Ahora, vete.",
    ["O"] = "Mata al archimago Dalaran, recupera su bastón y llévaselo al sacerdote de las Sombras Allister en El Sepulcro.",
    ["T"] = "El Tejedor",
  },
  [481] = {
    ["D"] = "Lleva el colgante a Dalar Tejalba, quizás pueda ayudarnos a entender los planes de los magos.",
    ["O"] = "Lleva el colgante con runas grabadas a Dalar Tejalba en El Sepulcro.",
    ["T"] = "El análisis de Dalar",
  },
  [482] = {
    ["D"] = "¡Por supuesto! ¿Cómo se me ha podido olvidar?$b$bMolino Ámbar fue uno de los lugares marcados por los Kirin Tor, debido a que tiene un nodo de energía Ley inactivo. Los magos deben de estar planeando reactivar el nodo y usar sus energías para algo.$B$BNo podemos permitir que esto ocurra...$B$BPara activar las energías Ley de la zona, necesitarán una gran cantidad de poder. Podemos paralizarlo todo si conseguimos los colgantes de los magos.$B$BCuéntale esto a Allister, él decidirá lo que tenemos que hacer.",
    ["O"] = "Habla con el sacerdote de las Sombras en El Sepulcro.",
    ["T"] = "Las intenciones de los Dalaran",
  },
  [483] = {
    ["D"] = "Los invasores Tuercepinos han sido vistos saqueando Túmulo de Ban\'ethil hacia el oeste.$b$bLos druidas durmientes quedarán encerrados en el Sueño Esmeralda por toda la eternidad, ajenos a sus destinos, a menos que les ayudemos. El delicado ritual de hibernación no puede detenerse sin las Reliquias del Despertar.$b$bViaja a El Cubil y consigue el talismán de la garra del cuervo, el plumón pluma negra, el zafiro de cielo y la runa de nidal que los druidas guardan en cofres sagrados. Tráemelos y podré preparar el ritual del despertar.",
    ["O"] = "Consigue las Reliquias del Despertar y llévaselas a Athridas Manturso a Dolanaar.",
    ["T"] = "Las Reliquias del Despertar",
  },
  [484] = {
    ["D"] = "Soy James Halloran, el peletero. Mis pieles curtidas de crocolisco son las más apreciadas de todas y llegan cargamentos a todos los puertos del mundo.$b$b¿Viste a Einar? Es uno de los cazadores encargados de proporcionarme pieles de crocolisco.$b$bMe estoy quedando sin las suaves pieles de crías de crocolisco.$b$bPor eso te pagaré generosamente por una pila de esas pieles. Podrás encontrar crías de crocolisco en los pantanos que están a las mismas puertas de la ciudad.",
    ["O"] = "Consigue 4 pieles de cría de crocolisco para James Halloran de Puerto de Menethil.",
    ["T"] = "Piel de crocolisco joven",
  },
  [485] = {
    ["D"] = "“Has descubierto una especie de instrumento extraño de metal y con forma de huevo. Si jugueteas con uno de los botones el huevo, este toma vida. ¡Se convierte en una especie de artilugio robótico gnomo! Y del interior del huevo sale una voz.$b$b\"Mi nombre es Oglethorpe Obnoticus, ¡y mi robot mensajero se ha averiado! Te recompensaré por su rescate. Por favor, lleva este localizador hasta el robot.\"$B$B\"He procesado las coordenadas del robot: falló en la cabecera del río, cerca de Roca Oculta.\"”",
    ["O"] = "Lleva el localizador al robot mensajero de Oglethorpe, que está en el nacimiento del río de las Tierras del Interior, cerca de Roca Oculta.",
    ["T"] = "¡Encuentra a OOX-09/HL!",
  },
  [486] = {
    ["D"] = "Ha llegado el momento de salvar a los druidas de la Garra. Si fallamos ahora, $N, nunca podrán despertar de su sueño.$b$bPrepararé las Reliquias del Despertar para llevar a cabo el ritual. Para que mis esfuerzos surtan efecto, hay que eliminar a las malditas bestias responsables de esta horrible situación. Solo en ese momento podrá completarse el ritual.$b$bUrsal el Violento fue quien se metió con nuestros hermanos y él será el que pague las consecuencias de su liberación. Ve a las Tierras de los Tuercepinos, en el sudeste, y mátalo.",
    ["O"] = "Mata a Ursal el Violento y ve a ver de nuevo a Athridas Manturso a Dolanaar.",
    ["T"] = "Ursal el Violento",
  },
  [487] = {
    ["D"] = "Hay que reforzar la seguridad en el camino que conduce a Darnassus. Algunos que han viajado de Dolanaar a Darnassus nos han informado de que en ese camino los corruptos furbolgs de la tribu de los Tuercepinos llevan a cabo despiadados ataques.$b$bEse camino se utiliza a diario para transporte de mercancías y es la vía de comunicación más importante de Darnassus. No podemos permitir que esa pandilla de delincuentes paganos aterrorice a la gente.$b$bLevántante en armas en nombre del bosque sagrado, $c. Su guarida se encuentra en algún lugar cerca de aquí. Acaba con 6 furbolgs Tuercepinos y después vuelve aquí.",
    ["O"] = "Acaba con 6 emboscadores Tuercepinos y después ve a ver de nuevo a Amara Andanoche a las afueras de Dolanaar.",
    ["T"] = "El camino a Darnassus",
  },
  [488] = {
    ["D"] = "Veo que tienes ganas de trabajar... Por suerte para ti, no hay un día que pase que no desee tener cerca a un inocente $c a quien proponerle mi oferta.$b$b¿Sabes, $N? Puedo hacerte muy feliz y ofrecerte cosas que jamás hubieras soñado tener, pero para ello deberás traerme una serie de objetos.$b$bPara desempeñar mi trabajo en el bosque a veces necesito ciertos... componentes. Tráeme 3 colmillos de sable de la noche, 3 plumas de lechuza estrígida y 3 muestras de seda de araña Tejemadera.$b$bY que esto quede entre nosotros, $r.",
    ["O"] = "Llévale a Zenn Nauseapezuña a las afueras de Dolanaar 3 colmillos de sable de la noche, 3 plumas de lechuza estrígida y 3 muestras de seda de araña Tejemadera.",
    ["T"] = "Las apuestas de Zenn",
  },
  [489] = {
    ["D"] = "El Consejo del Bosque se ha enterado de que ayudaste a Zenn Nauseapezuña, un sátiro enemigo del bosque. Como buen $r que eres, no deberías dedicarte a mancillar el bosque matando criaturas de la Naturaleza.$b$bHas de reparar tu error ante el Consejo si deseas seguir siendo $gamigo:amiga; de Teldrassil.$b$bDale a Nauseapezuña una lección y recibirás tu perdón. Los conos inferi son semillas corruptas que caen de los árboles y que expulsan humo verde.$b$bDale algunas a Nauseapezuña, él creerá que se trata de un inofensivo aperitivo.",
    ["O"] = "Recoge 3 conos inferi y dáselos a Zenn Nauseapezuña, que está a las afueras de Dolanaar.",
    ["T"] = "¡Búsqueda de redención!",
  },
  [490] = {
    ["D"] = "By strict orders from the Council of Darnassus, I am commissioning a bounty on the Gnarlpine Tribe. They are no longer friends of the forest. Their corruption has left them mindless threats to our people and the creatures of the glade.$b$bAs decreed by the Council, you shall be rewarded for removing the furbolg menaces from Kalidar. Bring to me their fangs as proof of your deeds.",
    ["O"] = "Shayla Nightbreeze outside of Darnassus wants you to bring her 20 Gnarlpine Fangs.",
    ["T"] = "<UNUSED>",
  },
  [491] = {
    ["D"] = "Una vez Thule me dijo que, mucho tiempo atrás, él y dos de sus colegas crearon una varita mágica, combinando las varitas de aprendiz que cada uno de los magos tenía. Esta varita fue un símbolo de su amistad y, aunque Thule llegó a despreciarla, no pudo deshacerse de ella, ni permitir que los otros la tuvieran. Así que robó la varita y la escondió en el Castillo de Fenris.$B$BAquí. Lleva la varita a Bethor Duroyelo, uno de los antiguos amigos de Thule. Seguro que la quiere.",
    ["O"] = "Lleva la varita tejida a Bethor Duroyelo en el Barrio de la Magia, en Entrañas.",
    ["T"] = "Una varita para Bethor",
  },
  [492] = {
    ["D"] = "De acuerdo con el guardia de la muerte, acaban de capturar a otro de esos insensatos enanos montaraces. El guardia de la muerte suele encerrar a los prisioneros en el sótano del Mesón La Horca hasta que pueda encargarse \"debidamente\" de ellos.$b$b¿Por qué no vas a comprobar si al montaraz capturado le gusta la bebida especial que he preparado para él? Contiene una pequeña muestra de lo que la Dama Oscura tiene reservado para todo Azeroth.",
    ["O"] = "Llévale la bebida especial de Johaan al montaraz capturado.",
    ["T"] = "Una nueva Peste",
  },
  [493] = {
    ["D"] = "La Dama Oscura está presionando mucho a la Sociedad Real de Boticarios para que creen una Nueva Plaga. Estamos trabajando diligentemente y hemos avanzado mucho. Creemos que lo conseguiríamos antes si compartiésemos información entre la Sociedad.$B$BPor esa razón, quiero que entregues mis últimos descubrimientos al boticario Lydon en el Molino Tarren, una pequeña ciudad en las Laderas de Trabalomas. El viaje será largo.$B$BDirígete al sur y no te alejes de los caminos. ¡Sigue las señales de cerca!",
    ["O"] = "Lleva los descubrimientos del boticario Renferrel al boticario Lydon en la ciudad de Molino Tarren, en las Laderas de Trabalomas.",
    ["T"] = "Viaja a Laderas de Trabalomas",
  },
  [494] = {
    ["D"] = "Cállate y presta atención, $c.$b$bMe han enviado aquí para vigilar Trabalomas. La mía es una misión de reconocimiento. Debes enviar un mensaje a la suma ejecutora Darthalia, a Molino Tarren, de inmediato. Dile que el mortacechador Lesh le envía este mensaje:$b$b\"Desde el oeste nos llega el llanto del cuervo.\"$b$bSigue el camino que va al este y fíjate bien en las indicaciones. ¡Apresúrate! ¡Ve a Molino Tarren a toda prisa!",
    ["O"] = "Ve a Molino Tarren para llevar el mensaje del mortacechador Lesh a la suma ejecutora Darthalia.",
    ["T"] = "El momento de atacar",
  },
  [495] = {
    ["D"] = "Mug\'Thol y sus ogros debían ir a Alterac en busca de la Corona de la Voluntad para Lady Sylvanas. Por desgracia, en lugar de regresar con ella, el muy idiota decidió probarla... Supongo que los ogros no se pueden resistir a nada luminoso y brillante.$b$bSin embargo, su constante incompetencia se está convirtiendo en un problema. La Corona de la Voluntad le permitió resistirse a la posesión que le obligaba a servir a Lady Sylvanas.$b$bLa Dama Oscura quiere darle un castigo ejemplar; viaja al Molino Tarren e informa a Melisara, mi teniente.",
    ["O"] = "Informa a Melisara que está en Molino Tarren, en las Laderas de Trabalomas.",
    ["T"] = "La Corona de la Voluntad",
  },
  [496] = {
    ["D"] = "Otro maldito día en el Molino Tarren. Todo este aire puro me pone enfermo, $n.$B$BTenemos que infestar a los humanos cuanto antes. He estado estudiando potenciales agentes letales para utilizar en mis brebajes pero no he tenido tiempo de recogerlos todos.$B$BSi quieres hacer algo útil, podrías procurarme los siguientes objetos: diez lenguas de oso gris e icor de trepador, que es muy raro y difícil de encontrar. Nada más salir del Molino Tarren encontrarás osos y trepadores.",
    ["O"] = "El boticario Lydon de Molino Tarren quiere 10 lenguas de oso gris y un poco de icor de trepador.",
    ["T"] = "Elixir del sufrimiento",
  },
  [497] = {
    ["D"] = "As a child, raised as a gladiator at Durnholde Keep, I was befriended by Taretha, the daughter of my wet nurse and mistress of the damned bastard Blackmoore.$b$bThe past is the past, $n, but reports of human movements in Hillsbrad reminded me of that time. I dispatched a small party to the area to investigate and to retrieve an... item... given to me by Taretha, if they could.$b$bA fruitless task, no doubt, but perhaps...$b$bThey should have returned by now, I want you to go to Tarren Mill to investigate.",
    ["O"] = "Go to Tarren Mill and find out the status of the party sent by Thrall.",
    ["T"] = "<UNUSED>",
  },
  [498] = {
    ["D"] = "El Jefe de Guerra me envió con algunos compañeros a Durnholde para recoger un objeto e investigar a esa tal Hermandad.$b$bFuimos a echar un vistazo, pero un numeroso grupo de humanos nos atacó por sorpresa. Luchamos como fieras, pero estábamos en franca minoría. Me capturaron, pero conseguí escapar.$b$bDurnholde está al otro lado del río, hacia el sureste. Drull y Tog\'thar están retenidos allí; debes ir a rescatarlos.",
    ["O"] = "Krusk de Molino Tarren quiere que liberes a Drull y a Tog\'thar en el Castillo de Durnholde.",
    ["T"] = "El rescate",
  },
  [499] = {
    ["D"] = "Tardaré un momento en mezclar estos ingredientes para crear lo que yo llamo mi elixir del sufrimiento.$b$b¡Allá vamos! Ah, nada me alegra tanto la vida en este lugar miserable como traer padecimientos al mundo.$b$bVeamos si la poción tiene el efecto deseado.$b$bDale a probar a mi asistente Umpi lo que la Dama Oscura ha planeado tanto para los humanos como para la Plaga.",
    ["O"] = "Dale el elixir del sufrimiento a Umpi.",
    ["T"] = "Elixir del sufrimiento",
  },
  [500] = {
    ["D"] = "Los ogros Aplastacresta han construido un túmulo en las Montañas de Alterac, cerca de la ciudad arrasada de Alterac. Los rastreadores indican que también han invadido las ruinas.$b$bSi dejamos que se queden ahí, su siguiente paso será bajar las colinas y llegar hasta nosotros.$b$bVe a liquidar ogros en las Montañas de Alterac, hacia el norte. Si quieres ganar un buen botín, tráeme todos los huesos sucios que llevan.",
    ["O"] = "Recoge 9 huesos sucios de orcos Aplastacresta en las Montañas de Alterac. LLévaselos al mariscal Rutagrana a Costasur.",
    ["T"] = "La recompensa Aplastacresta",
  },
  [501] = {
    ["D"] = "Oh, $N. En Trabalomas todo está florecido y el aire es puro y fresco. No puedo soportarlo. Miro por mi mugrienta ventana y espero el día en que nuestra Nueva Plaga traiga a este mundo la muerte que merece.$b$bAunque me gustaría perderme en mis negras reflexiones, el deber hacia la Dama Oscura me llama. ¿Quieres ayudarme? Según mis estudios, puedo obtener una estupenda poción letal con sangre de puma. Tráeme un poco, verás qué bien lo pasaremos.",
    ["O"] = "Lleva 10 viales de sangre de puma al boticario Lydon de Molino Tarren.",
    ["T"] = "Elixir del dolor",
  },
  [502] = {
    ["D"] = "¡No hay nada como el olor a sangre fresca!$b$bAhora añadimos un poquito de esto y una pizca de esto otro ¡y ya está! Esta poción se llama el elixir del dolor. Si mis cálculos son correctos, este brebaje complacerá enormemente a Lady Sylvanas. Pero antes hay que probarlo.$b$bLlévalo a la granja norteña de Campos de Trabalomas, que está al suroeste. A ver qué le parece esta golosina a Stanley, el perrito del granjero Ray.",
    ["O"] = "Dale el elixir del dolor a Stanley.",
    ["T"] = "Elixir del dolor",
  },
  [503] = {
    ["D"] = "Según los documentos que has traído, tienen a Gol\'dir en una importante base de la Hermandad situada al norte, probablemente en las Montañas de Alterac. Ya lleva demasiado tiempo cautivo; hay que liberarlo.",
    ["O"] = "Libera a Gol\'dir e informa a Krusk en Molino Tarren.",
    ["T"] = "Gol\'dir",
  },
  [504] = {
    ["D"] = "Ahora que has conocido a los ogros Aplastacresta, quiero que les revientes las narices...$b$bVe a las Ruinas de Alterac y busca a esos Aplastacresta pendencieros. Quiero que liquides a un buen número de ellos. Es la única forma de que esos brutos aprendan a mantenerse alejados del territorio de la Alianza.",
    ["O"] = "Mata a 15 belicistas Aplastacresta y vuelve a hablar con el mariscal Rutagrana en Costasur.",
    ["T"] = "Belicistas Aplastacresta",
  },
  [505] = {
    ["D"] = "Soy el nuevo magistrado de Costasur. Mi ilustre predecesor fue asesinado. No se ha encontrado a los asesinos, pero la investigación apunta a que eran mercenarios de la Hermandad, un grupo de ladrones liderados por unos nobles sin honor del desaparecido reino de Alterac.$b$bLas pistas señalan al campamento que tienen al norte de Molino Tarren, territorio ocupado por la Horda. Elimina a los miembros de la Hermandad que te encuentres.",
    ["O"] = "Mata a 12 asaltantes de caminos y 8 ladrones de la Hermandad e informa al magistrado Henry Maleb en Costasur.",
    ["T"] = "Hermandad de asesinos",
  },
  [506] = {
    ["D"] = "Hace uno o dos días me visitó el líder de una organización que se llama la Hermandad. Su nombre era Aliden Perenolde y se considera a sí mismo el señor de esta tierra. Me confió sus planes de convertir en esclavos a los orcos que se enfrentan a él, tal y como su padre y su mentor, el despreciado Lodonegro, habían planeado.$B$BPero lo más importante es que traía consigo a su querida, una chica menudita llamada Elysa. Que llevaba puesto el colgante que desea el Jefe.$B$BAhora ve. Yo seguiré mi camino cuando haya acabado de arreglar mis asuntos.",
    ["O"] = "Lleva la información recopilada por Gol\'dir a Krusk a Molino Tarren.",
    ["T"] = "El legado de Lodonegro",
  },
  [507] = {
    ["D"] = "Es evidente que hay que eliminar a Perenolde, erradicar su vileza. Su mansión está hacia el norte, a orillas del Lago Lordamere. $n, cumple con tu deber, recupera el colgante que anhela el Jefe de Guerra y vuelve aquí.",
    ["O"] = "Mata a Aliden Perenolde y pregunta a su querida, Elysa, por el colgante de Taretha.",
    ["T"] = "Lord Aliden Perenolde",
  },
  [508] = {
    ["D"] = "¿El colgante? Claro que te lo puedes quedar... ¡Llévatelo! No quiero ningún recuerdo de él... tan solo, solo deja en paz...",
    ["O"] = "Devuélvele el colgante de Taretha a Krusk Molino Tarren.",
    ["T"] = "Un regalo de Taretha",
  },
  [509] = {
    ["D"] = "Un día más, un elixir más. Si por mí fuera, dejaría que esos ridículos humanos y la Plaga se mataran mutuamente. De hecho, ese parece ser su único objetivo.$b$bNuestra Dama Oscura quiere acelerar el proceso, así que ¿por qué no pasar estos negros días sembrando muerte y enfermedad por el lugar?$b$bEste es el plan, $N: ve a la Granja Nethander, al sur de Molino Tarren y al este del río y de Costasur. Tráeme seis flores de morrobarro para mi próximo elixir.",
    ["O"] = "Lleva 6 flores de morrobarro al boticario Lydon de Molino Tarren.",
    ["T"] = "Elixir de agonía",
  },
  [510] = {
    ["D"] = "Entre los documentos hay un pedido de suministros. Incluye objetos de uso diario (alimentos, armas, ropas) y otros menos usuales (máscaras, combustible, veneno).$B$BPero lo más importante es la urgencia del pedido: los suministros eran necesarios para lo que se describe como \"una importante misión contra Costasur.\"",
    ["O"] = "Lleva los documentos de planificación al magistrado Maleb a Costasur.",
    ["T"] = "Documentos de planificación",
  },
  [511] = {
    ["D"] = "Una de las cartas de la mesa está cifrada y es inescrutable.",
    ["O"] = "Lleva la carta cifrada al maestro del Conocimiento Dibbs en Costasur.",
    ["T"] = "Una carta cifrada",
  },
  [512] = {
    ["D"] = "Temo que la Hermandad aspire a provocar la caída de Costasur. Debemos eliminar a la cúpula para que no puedan llevar a cabo sus planes.$b$bVe a las ruinas de Strahnbrad; están al norte, en las Montañas de Alterac. Creemos que la Hermandad se concentra en esa zona.$b$bSabemos que los jefes de estos maleantes son antiguos nobles de Alterac. Puede que estén disfrazados de agentes ordinarios de la Hermandad, pero los distinguirás por los anillos de sello que llevan.$b$bTráeme los anillos.",
    ["O"] = "Lleva 7 anillos con sello de Alterac al magistrado Maleb a Costasur.",
    ["T"] = "Nobles muertes",
  },
  [513] = {
    ["D"] = "Justo cuando pensaba que este sería otro día triste y sin alegría, me traes estas maravillosas flores de morrobarro.$b$bEl primer paso es molerlas. Luego el boticario Faranell de Entrañas aplicará su coloide de decadencia y ya estará listo.$b$bLlévale este compuesto de morrobarro al boticario a toda prisa, $N.",
    ["O"] = "Lleva el compuesto de morrobarro al maestro boticario Faranell a Entrañas.",
    ["T"] = "Elixir de agonía",
  },
  [514] = {
    ["D"] = "Tengo un colega en Forjaz que quizás pueda descifrarla. Ve a la biblioteca de la Sala de los Exploradores, en Forjaz, y habla con el prospector Pico Tormenta. Dile que vas de mi parte y enséñale la carta. Si hay alguien que pueda descifrar esto al norte de Cumbre de Roca Negra, ese es él.",
    ["O"] = "Lleva la carta muy bien cifrada al prospector Pico Tormenta a Forjaz.",
    ["T"] = "Una carta para Pico Tormenta",
  },
  [515] = {
    ["D"] = "¿Un compuesto de morrobarro? ¡Brillante! ¿Cómo no se me ha ocurrido a mí?$b$bVoy a añadir mi coloide de decadencia a este maléfico brebaje.$b$bPara activar los agentes contaminantes de este preparado, Lydon necesitará una poción fuerte de sangre de trol, escamas de dagaespinazo y ojos de rompeanca de la costa sur de Trabalomas. Él ya sabrá qué hacer con esos reactivos. ¡Ardo en deseos de saber el resultado del experimento!",
    ["O"] = "Lleva la mezcla de morrobarro, una poción fuerte de sangre de trol, 5 escamas de dagaespinazo y 5 ojos de rompeanca al boticario Lydon a Molino Tarren.",
    ["T"] = "Elixir de agonía",
  },
  [516] = {
    ["D"] = "Me han informado sobre un grupo de no-muertos que están reagrupándose para preparar un ataque contra nosotros. Con esta información, podemos hacer algo y atacar primero, poniendo fin de un manotazo a su pequeño plan.$B$BPor desgracia, la información de la que dispongo es bastante incompleta. Se supone que están escondidos en El Desafío de Beren. Se desconoce la ubicación exacta, pero parece ser una cueva en las colinas, cerca de unos magos de Dalaran.$B$BConfío en tus recursos. Encuéntrales y acaba con ellos.",
    ["O"] = "Encuentra El Desafío de Beren y, a continuación, mata 6 esclavos Corvozarpa y 6 guardianes Corvozarpa. Por último, vuelve a ver al sacerdote de las Sombras Allister en El Sepulcro.",
    ["T"] = "El Desafío de Beren",
  },
  [517] = {
    ["D"] = "El nuevo elixir ya está listo para probar. Pero sería una pena malgastar una plaga potente y altamente contagiosa en un sapo o un perro. Vamos a divertirnos en nombre de Lady Sylvanas.$B$BVete a Dun Garok. ¿Sabes las barracas en las que se hacinan esos asquerosos enanos? Tráeme un barril de cerveza Machacacanillas. Y cuando lo traigas nos pegaremos una buena fiesta, $n.",
    ["O"] = "Lleva un barril de cerveza Machacacanillas al boticario Lydon de Molino Tarren.",
    ["T"] = "Elixir de agonía",
  },
  [518] = {
    ["D"] = "Mug\'Thol y sus ogros Aplastacresta han invadido las ruinas de Alterac. Tenemos que reducir sus filas si queremos castigarlo.$b$bPrimero los debilitamos y después procedemos,$b$b¿entendido? Bien. Adelante.",
    ["O"] = "Melisara de Molino Tarren quiere que mates a 14 destructores Aplastacresta.",
    ["T"] = "Corona de la Voluntad",
  },
  [519] = {
    ["D"] = "Targ, Muckrake y Glommus son los generales de Mug\'Thol. Le son leales, aunque quién sabe qué instiga la lealtad de un ogro.$b$bTráeme sus cabezas, $n, y después solo quedará separar a Mug\'Thol de la suya, corona incluida.",
    ["O"] = "Mata a Targ, Muckrake y Glommus y lleva sus cabezas a Melisara a Molino Tarren.",
    ["T"] = "Corona de la Voluntad",
  },
  [520] = {
    ["D"] = "Con los tenientes de Mug\'Thol muertos, ya solo queda matar a Mug\'Thol y hacerse con la Corona de la Voluntad. No tengo dudas de que no querrá dártela voluntariamente, y menos ahora que has acabado con sus servidores.$b$bEncuentra a Mug\'Thol y tráeme su cabeza y su corona. La resistencia Aplastacresta tiene que terminar.",
    ["O"] = "Mata a Mug’Thol y lleva su cabeza con la Corona de la Voluntad a Melisara a Molino Tarren.",
    ["T"] = "Corona de la Voluntad",
  },
  [521] = {
    ["D"] = "Ahora solo falta ir a Entrañas para entregarle la corona a Sharlindra. Ella se ocupará de entregársela a Lady Sylvanas. Te advierto que no debes usarla para tus propios fines, $n; recuerda lo que le ha pasado a Mug\'Thol.",
    ["O"] = "Ve a Entrañas para entregarle la Corona de la Voluntad a Sharlindra.",
    ["T"] = "Corona de la Voluntad",
  },
  [522] = {
    ["D"] = "Esto es un contrato para el asesinato del magistrado Henry Maleb. Estaba sellado con cera roja y un anillo de sello muy distintivo.",
    ["O"] = "Lleva el contrato de asesinato al magistrado Maleb a Costasur.",
    ["T"] = "Contrato de asesino",
  },
  [523] = {
    ["D"] = "Está firmado por el Barón Vardus, un noble del antiguo reino de Alterac conocido por su crueldad y brutalidad. No me sorprende que el Barón sea responsable de esto.$B$BDebes buscarlo. Encuéntralo y entrégalo a las autoridades. Debe de estar en alguna guarida de la Hermandad, en las ruinas de Strahnbrad o en el valle que hay al norte de ellas.",
    ["O"] = "Lleva la cabeza del Barón Vardus al magistrado Maleb a Costasur.",
    ["T"] = "Muerte al Barón",
  },
  [524] = {
    ["D"] = "Los guardias de la muerte tienen retenidos a varios granjeros. La suma ejecutora Darthalia sospechaba que uno de los estrategas militares de Ventormenta estaba entre ellos. Al parecer fue encontrado muerto anoche en las Tierras de la Peste. Darthalia, siempre pensando en mí, decidió que yo me ocuparía de los prisioneros.$b$bCreo que ya es hora de montarles una fiesta a los granjeros. ¡Va a ser estupendo!$b$bColoca este barril contaminado en la alfombra de su cuarto. ¡Y que corra la plaga!",
    ["O"] = "Coloca el barril contaminado en la alfombra para los granjeros.",
    ["T"] = "Elixir de agonía",
  },
  [525] = {
    ["D"] = "He descifrado la carta, pero no sé cómo interpretarla. ¿Y la tenían unos ladrones en las laderas de Alterac? Qué raro…$b$bLlévasela al magistrado de Costasur. Aunque la carta es críptica, su ciudad es la más cercana a Alterac; si se está cociendo algo, es mejor que lo sepa.",
    ["O"] = "Lleva la carta descifrada al magistrado Maleb a Costasur.",
    ["T"] = "Más misterios",
  },
  [526] = {
    ["D"] = "Has encontrado un lingote Forjaluz en el cofre, ¡pero no es suficiente para hacer nada decente!$B$BDeberías volver y buscar más. Quizás los múrlocs que rodean los restos del naufragio tengan más lingotes Forjaluz.",
    ["O"] = "Reúne 5 lingotes Forjaluz y vuelve con Glorin Frenteacerada en Los Humedales.",
    ["T"] = "Lingotes Forjaluz",
  },
  [527] = {
    ["D"] = "Ponte firme cuando te dirijas a mí, $c.$b$bEstoy a las órdenes directas de Varimathras. Debemos sofocar la infestación humana hasta que nuestros boticarios desarrollen la nueva plaga.$b$bNuestros datos indican que Trabalomas es vulnerable. Tu primera misión es interrumpir el abastecimiento de alimentos y su infraestructura.$b$bVe hacia el oeste. Ataca las granjas más septentrionales de Campos de Trabalomas. Asegúrate de que los propietarios, el granjero Ray y el granjero Getz, son ejecutados.",
    ["O"] = "Mata a 6 jornaleros y 6 granjeros de Trabalomas, al granjero Ray y al granjero Getz e informa a Darthalia en Molino Tarren.",
    ["T"] = "La batalla de Trabalomas",
  },
  [528] = {
    ["D"] = "El ataque a las granjas del norte tiene aterrados a los lugareños. Excelente.$b$bNuestros mortacechadores informan que los labriegos huyen a los campos del sur de Trabalomas. Esos necios creen que pueden escapar a su destino escondiéndose en el huerto.$b$bPero se equivocan, $N. Vuelve a Campos de Trabalomas y busca a los labriegos ocultos. Aniquílalos en el nombre de nuestra Dama Oscura.$b$bPuedes retirarte.",
    ["O"] = "Mata a 15 campesinos de Trabalomas e informa a Darthalia en Molino Tarren.",
    ["T"] = "La batalla de Trabalomas",
  },
  [529] = {
    ["D"] = "Los humanos de Trabalomas han opuesto más resistencia de la esperada. Al parecer, el herrero del lugar está fabricando armas. Tenemos que interrumpir todo suministro.$b$bTu misión es ejecutar al herrero Verringtan. Elimina también a los aprendices. Y trae un cargamento de hierro de su almacén. Nos irá bien cuando terminemos con Trabalomas y pasemos a conquistar Costasur.",
    ["O"] = "Mata al herrero Verringtan y a 4 aprendices de herrero de Trabalomas.$b$bRecupera un cargamento de hierro e informa a Darthalia en Molino Tarren.",
    ["T"] = "La batalla de Trabalomas",
  },
  [530] = {
    ["D"] = "Valdred, el que una vez fue mi mejor amigo, es el asesino de mi esposa. Aunque soy incapaz de llorar su muerte, sí que anhelo venganza. Si matas a Valdred y me traes sus manos malditas, te recompensaré.$B$BLa última información de que dispongo es que se encuentra en La Muralla de Cringris, al sur del Bosque de Argénteos, con intención de escapar al reino de Gilneas.",
    ["O"] = "Mata a Valdred Moray.$B$BLlévale sus manos a Raleigh Andrean, que está en Entrañas.",
    ["T"] = "La venganza de un esposo",
  },
  [531] = {
    ["D"] = "¡Por la Luz! No puedes meter eso aquí, es antihigiénico, antiestético y... ¡y huele mal!$B$BLlévatelo, ¿quieres? ¡Ese trofeo sangriento no le interesa a nadie!",
    ["O"] = "Lleva la cabeza de Ol\' Sooty a Vyrin Vientoveloz a la Cabaña del Errante.",
    ["T"] = "La venganza de Vyrin",
  },
  [532] = {
    ["D"] = "Trabalomas está en ruinas y creemos que a punto de caer. Pero debemos mantenernos firmes hasta erradicar a toda la chusma humana para toda la eternidad.$b$bLos humanos resisten y siguen luchando pese a sus abrumadoras pérdidas. Debemos eliminar a su líder, el magistrado Ladoquemao, además de a los ediles. También debes destruir su más preciado documento, la Declaración de Trabalomas. Y ya que estás, saquea el registro civil.",
    ["O"] = "Mata al magistrado Ladoquemao y a 5 concejales de Trabalomas. Destruye la Declaración de Trabalomas. Saquea el registro civil. Después informa a Darthalia en Molino Tarren.",
    ["T"] = "La batalla de Trabalomas",
  },
  [533] = {
    ["D"] = "Por fin empezamos a tener noticias acerca del paradero de Gol\'dir. Los humanos lo han llevado de un lado a otro, pero hemos centrado la búsqueda en el campamento de la Hermandad que está al norte de aquí.$B$B$n, tienes que ir y conseguir toda la información que puedas.",
    ["O"] = "Consigue información sobre el paradero de Gol\'dir\'s e informa a Krusk en Molino Tarren.",
    ["T"] = "Infiltración",
  },
  [534] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [535] = {
    ["D"] = "Urrg... Herido, agonizante y frente al enemigo.$B$BDe todos modos, seguro que me matarás, pero si me traes cerveza negra de Costasur, los de tu clase beben, ¿no?, te contaré lo que deseas saber.",
    ["O"] = "Llévale 1 jarra de cerveza Costasur a Valik en el campamento de la Hermandad.",
    ["T"] = "Valik",
  },
  [536] = {
    ["D"] = "Me han transferido de la guardia de Ventormenta a Costasur, un lugar que merecería ser abandonado, a juzgar por su aspecto. No me quito de encima el olor a puerto.$b$bPor no hablar de Rutagrana... Quiere que me encargue de los murlocs. ¡Ja!$b$bApenas me animo a acercarme a esos pescados gigantescos de la Playa del Oeste. Te agradecería mucho que te ocuparas de ello en mi lugar.",
    ["O"] = "Mata a 10 cazamareas y 10 oráculos Rompeanca por orden del general Farren Orinelle de Costasur.",
    ["T"] = "Al lo largo de la costa",
  },
  [537] = {
    ["D"] = "La carta fue escrita por un ser que responde al nombre de Nagaz, miembro del Despertar de Argus. No sé quiénes son, pero si están aliados con la Hermandad debemos considerarlos una amenaza.$B$BQuiero que localices a Nagaz y a los magos de la Sombra Argus mencionados en la carta. Localízalos y elimínalos.$B$BExplora las Tierras Altas, el bastión de la Hermandad. Está al norte de Strahnbrad.$B$BCon un poco de suerte, encontrarás a los enviados del Despertar de Argus allí.",
    ["O"] = "Mata a 4 magos de la Sombra Argus.$b$bLleva la cabeza de Nagaz al magistrado Maleb de Costasur.",
    ["T"] = "Consejo oscuro",
  },
  [538] = {
    ["D"] = "$N. Si no te molestan los viajes largos, tengo una tarea para ti, para ayudar a llenar la biblioteca de Ventormenta.$B$BEl viejo reino de Alterac cayó tras la Gran Guerra y tenía una gran colección de libros. Es una tragedia que la ciudad esté en ruinas, pero mucho más trágico es haber perdido todo su conocimiento. Debemos recuperar todo lo que podamos.$B$BCostasur es la ciudad más próxima a las Ruinas de Alterac. Vete allí y habla con el maestro del Conocimiento Dibbs. Podrá guiarte hasta las ruinas y sabe qué libros puedes encontrar allí.",
    ["O"] = "Habla con el maestro del Conocimiento Dibbs en Costasur.",
    ["T"] = "Costasur",
  },
  [539] = {
    ["D"] = "El Registro de Trabalomas indica que la Mina Azur se encuentra bajo el control de la Alianza. Esa mina es una de las principales fuentes de hierro de la armería de la Alianza.$b$bAunque hemos asestado un golpe mortal a Trabalomas, la Alianza sigue defendiendo la Mina Azur,$b$bque está al sur de Trabalomas. Ve y asesina al supervisor, un humano llamado Bonds. Elimina también a los mineros. Eso le enseñará a la Alianza.",
    ["O"] = "Mata al supervisor Bonds y 10 mineros de Trabalomas e informa a Darthalia en Molino Tarren.",
    ["T"] = "La batalla de Trabalomas",
  },
  [540] = {
    ["D"] = "Esos bestiales ogros viven en las ruinas de Alterac. Me estremezco al pensar en lo que estarán haciendo con los valiosos libros que quedaron allí. ¡Debes recuperar tantos como puedas!$B$BVe a las Ruinas de Alterac y busca los volúmenes que hayan robado. Tráeme todos los que puedas, pero hay un libro en especial que debes buscar: El Arma de Gri\'lek. En ese libro hay ciencia trol antigua que necesito; en las Ruinas de Alterac había un ejemplar intacto, el último del que se tiene noticia.",
    ["O"] = "Lleva 5 tomos y el libro de piel gastado que contiene El Arma de Gri\'lek al maestro del Conocimiento Dibbs a Costasur.",
    ["T"] = "Proteger el conocimiento",
  },
  [541] = {
    ["D"] = "¿Enanos ayudando a los humanos? Malditas sean esas pequeñas y sucias bestias.$B$BPondremos a prueba la solidez de esa supuesta Alianza. Veremos si esos medias piernas están tan ansiosos por ayudar a los humanos cuando hayan sufrido nuestra ira. Sabemos exactamente dónde se encuentra su fortaleza en este territorio.$B$BSe han instalado en un barracón llamado Dun Garok, al sureste de Trabalomas, junto a la Muralla de Thoradin. La misión final de esta batalla es asediar Dun Garok, $c. El capitán Ferrocolina, su líder, debe morir.",
    ["O"] = "Ve a Dun Garok y mata a 10 montaraces, 8 fusileros, 4 sacerdotes y al capitán Ferrocolina e informa a Darthalia en Molino Tarren.",
    ["T"] = "La batalla de Trabalomas",
  },
  [542] = {
    ["D"] = "Voy a dedicar cierto tiempo a estudiar El Arma de Gri\'lek, pero seguro que Milton espera con impaciencia los demás libros para incluirlos en la colección de Ventormenta.$B$BAquí tienes los libros que has traído de las Ruinas de Alterac, ya catalogados y empaquetados. Ve a Ventormenta y dáselos a Milton Gavilla; seguro que te lo agradecerá.",
    ["O"] = "Lleva los Tomos de Alterac a Milton Gavilla a Ventormenta.",
    ["T"] = "Vuelve con Milton",
  },
  [543] = {
    ["D"] = "La Tiara Perenolde es una antigua reliquia familiar de los \"nobles\" Perenolde, la familia soberana de Alterac antes de que comenzara el declive del reino. Se dice que ha sido forjada por los maestros joyeros de Forjaz y que lleva engarzadas unas esmeraldas de gran calidad.$B$BCuando Alterac fue destruido, se perdió la Tiara, pero corren rumores de que un mago ogro, Grel\'borg el Avaro, encontró la Tiara y ahora vaga por las Ruinas de Alterac en busca de más tesoros.$B$BNos gustaría que recuperases la Tiara para nosotros.",
    ["O"] = "Lleva la Tiara Perenolde a Remington Crestabuena en Ventormenta.",
    ["T"] = "La Tiara Perenolde",
  },
  [544] = {
    ["D"] = "Había venido a Molino Tarren para estudiar, pero ahora debo solucionar una crisis. Verás, hace unos meses, cuatro Renegados huyeron de Entrañas. Dieron la espalda a su gente, pero lo peor es que robaron a la Dama Oscura.$B$BEntraron en una cámara de seguridad y robaron cuatro objetos que usaron nuestros boticarios en algunos proyectos. Sylvanas quiere recuperar esos objetos.$B$BLos ladrones huyeron de Entrañas a Dalaran y esos magos los pusieron en cuarentena en el Campo de Reclusión de Lordamere.",
    ["O"] = "Localiza a los traidores y recupera los objetos robados. Luego habla otra vez con el mago Vacuamirada en Molino Tarren.",
    ["T"] = "Colarse en la cárcel",
  },
  [545] = {
    ["D"] = "Los magos de Dalaran construyeron una gran cúpula mágica alrededor del corazón de la ciudad. Hay quien dice que fue un medio de protección en estos tiempos violentos, y quien cree que los magos buscaban soledad para estudiar y conspirar.$b$bLa razón no me importa. Lo que me importa es que entre las ruinas, fuera de la cúpula, puede haber muchos tesoros mágicos.$b$bPero los magos patrullan la zona con sus esclavos elementales.$b$bTienes que eliminarlos. Vuelve cuando hayans reducido sus filas.",
    ["O"] = "Mata a 6 invocadores de Dalaran y 12 esclavos elementales. Luego vuelve a hablar con el mago Vacuamirada en Molino Tarren.",
    ["T"] = "Las patrullas de Dalaran",
  },
  [546] = {
    ["D"] = "Oí que has sido reclutado por Darthalia para luchar contra los humanos de Trabalomas. Estoy celoso.$b$bMientras tú te diviertes matando humanos, saqueando el pueblo y aterrorizando a gente inocente… yo estoy aquí clavado haciendo guardia en el Molino Tarren.$b$b¿No podrías apiadarte de un viejo guardia de la muerte como yo? Verás, colecciono calaveras humanas. Y tú pasarás algún tiempo combatiendo contra humanos. Reúne 30 calaveras mientras combates tu larga batalla y tráemelas. Haré que te merezca la pena.",
    ["O"] = "El guardia de la muerte Samsa de Molino Tarren quiere 10 calaveras humanas de Trabalomas.",
    ["T"] = "Recuerdes de muerte",
  },
  [547] = {
    ["D"] = "La suma ejecutora Darthalia me envió a vigilar Dun Garok desde cierta distancia. Acampé donde no pudieran verme. A la mañana siguiente fui a buscar leña para el fuego.$b$bY al volver descubrí que me habían robado el equipamiento.$b$bParece que unos rastreadores enanos de Dun Garok pasaron por allí y se lo llevaron todo. ¡Necesito la espada! Te agradecería mucho que me la trajeras. Aquí hay tantos enanos que será difícil descubrir al ladrón.",
    ["O"] = "Recoge la espada del Guardia de la Muerte Humbert en Dun Garok y llévasela a Molino Tarren.",
    ["T"] = "La espada de Humbert",
  },
  [548] = {
    ["D"] = "This will start the Bloodstone Pendant quest line...<NYI>",
    ["O"] = "",
    ["T"] = "<NYI> <TXT> Bloodstone Pendant",
  },
  [549] = {
    ["D"] = "Por orden de la Lady Sylvanas, se decreta la pena de muerte para todos los miembros de la organización conocida como la Hermandad. Aquellos que sean capturados serán ejecutados. Se sabe que se están reuniendo en las ruinas del Castillo de Durnholde, al sureste de Molino Tarren.$b$bSe ofrece una generosa recompensa a quien pueda dar cuenta de sus actividades a la suma ejecutora Darthalia.",
    ["O"] = "Mata a 10 pícaros y 10 vigilantes de la Hermandad. La suma ejecutora Darthalia te dará tu recompensa en Molino Tarren.",
    ["T"] = "SE BUSCA: Personal de la Hermandad",
  },
  [550] = {
    ["D"] = "He redactado una mención honorífica describiendo tu heroica actitud en combate para que el alto mando reconozca tu valor y entrega en la Batalla de Trabalomas.$b$bLleva esta mención sellada a Varimathras; está en Entrañas. Ve con la frente muy alta, $c.",
    ["O"] = "Lleva la mención honorífica sellada de Darthalia a Varimathras, que está en Entrañas.",
    ["T"] = "La batalla de Trabalomas",
  },
  [551] = {
    ["D"] = "Este pergamino está encantado con un hechizo que lo hace indescifrable. Aunque se puede leer una palabra en la parte de arriba:$B$B$B Nagaz$B$B",
    ["O"] = "Lleva el pergamino encantado al maestro del Conocimiento Dibbs en Costasur.",
    ["T"] = "El pergamino encantado",
  },
  [552] = {
    ["D"] = "El maestro Helcular casi había completado su transformación en exánime cuando una multitud airada descubrió su refugio ritual en las colinas y lo asesinó brutalmente. Para empeorar las cosas, lo enterraron en el cementerio de Costasur.$B$BTamaño desprecio por tan gran mago es inaceptable. Yo, su aprendiz, me aseguraré de que el fuego de la venganza arrase Costasur.$B$BNecesitamos la Vara de Helcular. Los yeti invadieron el refugio ritual. Seguro que una de esas bestias tiene la vara.",
    ["O"] = "Róbale la Vara de Helcular a los yetis y llévasela al novicio Thaivand a Molino Tarren.",
    ["T"] = "La venganza de Helcular",
  },
  [553] = {
    ["D"] = "Toma la Vara de Helcular; la vas a necesitar.$B$BHelcular diseñó tres piras ceremoniales como parte de su hechizo más potente. La Llama de Azel y la Llama de Veraz reposan en las cuevas de la ladera. La Llama de Uzel está en una gruta situada sobre las cuevas, hacia el norte.$B$BDebes cargar la Vara de Helcular en estas tres llamas. Cuando hayas completado el ritual, lleva la vara a la tumba de Helcular en Costasur, la muy bien protegida ciudad humana del sur. ¡Nos vengaremos!",
    ["O"] = "Carga la Vara de Helcular con el poder de la Llama de Azel, la Llama de Veraz y la Llama de Uzel.$b$bLuego llévala a la tumba de Helcular en Costasur.",
    ["T"] = "La venganza de Helcular",
  },
  [554] = {
    ["D"] = "El prospector Pico Tormenta es un maestro de la descodificación. Llévale el pergamino que has encontrado; con un poco de suerte, podrá descifrarlo. Está estudiando en la oficina central de prospección, en Forjaz.$b$bDile que has hablado conmigo; últimamente le mando bastante trabajo.",
    ["O"] = "Lleva el pergamino codificado al prospector Pico Tormenta a Forjaz.",
    ["T"] = "La descodificación de Pico Tormenta",
  },
  [555] = {
    ["D"] = "¡Lo que más vas a apreciar en Costasur es que tenemos la mejor cocina al norte de Ventormenta!$b$bPor ejemplo, mi sopa de tortuga. Sé de gente que ha venido incluso de Villa Oscura solo para probarla. Por cierto, hace mucho que no la hago. Antes iba pasado Dalaran hasta el Lago Lordamere para cazar quijafortes, pero ahora es demasiado peligroso. ¡Si me traes carne de tortuga de la de los quijafortes del norte y especias calmantes, te preparo una sopa!",
    ["O"] = "Lleva 10 piezas de carne de tortuga y especias calmantes al chef Jessen a Costasur.",
    ["T"] = "Sopa de tortuga calmante",
  },
  [556] = {
    ["D"] = "Los magos de Dalaran tienen una notable facilidad para relacionarse con elementales, que son poderosas criaturas de otras dimensiones. Los magos controlan a los elementales con talismanes mágicos. El mago Vacuamirada quiere analizar estos talismanes.$b$bVe a buscarlos al territorio de los magos de Dalaran, al oeste de Trabalomas.$b$bCualquier ciudadano puede llevar uno, pero te sugiero que busques en el campo de reclusión. Es más seguro que ir a Dalaran.",
    ["O"] = "Lleva 10 muestras de piedra gastada al guardián Bel’varil de Molino Tarren.",
    ["T"] = "Muestras de piedra",
  },
  [557] = {
    ["D"] = "El mago está ocupado estudiando las muestras de piedra que has traído, pero pronto querrá ver sus opuestos: los brazales de vínculo. Cuando un elemental los lleva, queda ligado al servicio del mago.$b$bCaza elementales en las ruinas de Dalaran y recoge sus brazales de vínculo con la Tierra para ganarte el favor del mago.",
    ["O"] = "Lleva 4 brazales de vínculo con la Tierra al guardián Bel’varil de Molino Tarren.",
    ["T"] = "Brazales de vínculo",
  },
  [558] = {
    ["D"] = "Dicen que Lady Jaina Valiente de Theramore es una de las mayores heroínas de la Alianza de todos los tiempos. ¡Cuando sea mayor también seré un héroe de la Alianza!$B$B¿Conoces a Jaina, $n? ¿Me puedes hacer un favor? ¿Me conseguirás su autógrafo? No me atrevo a pedírselo, seguramente estará muy ocupada para alguien como yo... ¡pero tú! ¡Tú eres $gun aventurero:una aventurera; como ella! ¡Seguro que te dará su autógrafo!$B$B¡¡Por favor!!",
    ["O"] = "Viaja a Theramore para intentar conseguir un autógrafo de Lady Jaina Valiente para tu pupilo.",
    ["T"] = "El autógrafo de Jaina",
  },
  [559] = {
    ["D"] = "¿Qué te parece, $n? A Rutagrana no le basta con todos los múrlocs que hemos matado. Sí, hemos, tú también has tenido algo que ver. Ahora quiere que le traiga pruebas, que me dedique a rebanar cabezas de múrloc para que las inspeccione.",
    ["O"] = "Recoge 10 cabezas de murloc en la Playa del Oeste por orden del general Farren Orinelle de Costasur.",
    ["T"] = "La prueba de Farren",
  },
  [560] = {
    ["D"] = "Verás, $n, a mí esto de las cabezas me pone mal cuerpo... Ya que le has cogido el gusto a la cosa, ¿te importaría ir a llevárselas a Rutagrana por mí?",
    ["O"] = "Entrega las cabezas de murloc al mariscal Rutagrana de Costasur.",
    ["T"] = "La prueba de Farren",
  },
  [561] = {
    ["D"] = "Informa al teniente Orinelle que debe concentrarse en los nagas que han infestado la Playa del Este. Dile que me informe personalmente del resultado de la operación.",
    ["O"] = "Habla con el general Farren Orinelle en Costasur.",
    ["T"] = "La prueba de Farren",
  },
  [562] = {
    ["D"] = "Mi antiguo mentor de la guardia de Ventormenta me dice que el alto mando de la Alianza está preocupado por la amenaza de los nagas. Según él, quien pueda ayudar a este respecto recibirá un ascenso, o al menos un cambio de destino.$b$bSolo hay un problema... No puedo enfrentarme a los nagas, $n. Puedo intervenir en una pelea de borrachos o reducir a un indigente belicoso, pero los nagas me superan. ¡$n, tienes que ayudarme!",
    ["O"] = "Mata a 10 cazacostas y 10 sirenas Dagaespinazo por orden del general Farren Orinelle de Costasur.",
    ["T"] = "Cambio de destino",
  },
  [563] = {
    ["D"] = "No debería ser un problema conseguir que vea mi informe. El nuevo capitán de la guardia, el mayor Samuelson, fue mi mentor cuando estuve destinado en Ventormenta. Eso debería ser excusa suficiente para que me traslade a la ciudad de nuevo. Pero necesitaré que le lleves mi informe... no quiero que Rutagrana se entere de nada.",
    ["O"] = "Entrega el informe de Farren al mayor Samuelson en el Castillo de Ventormenta.",
    ["T"] = "Nueva asignación",
  },
  [564] = {
    ["D"] = "Nada me duele más que perder estos hermosos caballos. Y hemos perdido unos cuantos entre los puñeteros no-muertos, la maldita Hermandad y toda la demás chusma que corre por aquí.$b$bPero te juro por mi abuelo que la amenaza más grave para los caballos son los pumas de Alterac. ¡Siempre bajan de la montaña y me complican la vida!$b$b¿Te apetece una aventura? ¡Pues ve y ocúpate de ellos!",
    ["O"] = "Darren Malvew de Costasur quiere que mates 8 pumas y 10 pumas gigantes.",
    ["T"] = "Una amenaza costosa",
  },
  [565] = {
    ["D"] = "Yo, el gran Bartolo, te haré una de mis capas de piel de yeti, admiradas por toda la Alianza.$b$bTe desafío a recorrer el mundo en busca de una capa mejor. ¿Cómo dices, $r? \"¡Bartolo, no hay una capa mejor!\" Sí, sí. Ya lo había oído antes.$b$bBartolo necesita una bala de lana, una capa de montañero hecha por un buen peletero, hilo de alta calidad y, naturalmente, piel de yeti. ¡Tráeme todo eso y te haré una capa magnífica!",
    ["O"] = "LLeva una bala de lana, una capa de montañero, hilo de alta calidad y 10 trozos de piel de yeti a Bartolo Ginsetti a Costasur.",
    ["T"] = "Capa de piel de yeti de Bartolo",
  },
  [566] = {
    ["D"] = "Por orden de la suma ejecutora Darthalia, se busca al humano conocido como el Barón Vardus por crímenes contra los Renegados. Vardus es un miembro dirigente de la organización conocida como la Hermandad.$B$BFue visto por última vez en las Tierras Altas de las Montañas de Alterac, al norte de Molino Tarren.$B$BSe halla bajo pena de muerte. Se ofrece recompensa a quien presente una prueba de su muerte a la suma ejecutora Darthalia.",
    ["O"] = "Mata al Barón Vardus y entrega su cabeza a la suma ejecutora Darthalia en Molino Tarren.",
    ["T"] = "SE BUSCA: Barón Vardus",
  },
  [567] = {
    ["D"] = "¡Atención!$b$bLos siguientes humanos de Trabalomas son peligrosos. La suma ejecutora Darthalia ha designado una recompensa por su captura:$b$bSecretario Horrace Corzalbal. Se busca por el asesinato del guardia de la muerte Toma.$b$bCiudadano Wilkes. Se busca por el asesinato del boticario Eli.$b$bMinero Hackett. Se busca por el asesinato del mortacechador Fry.$b$bGranjero Kalaba. Se busca por el bloqueo de suministros de Entrañas.$b$bTodos estos enemigos se encuentran fugados y su captura se hará difícil. Se recompensará a quien pueda certificar su muerte.",
    ["O"] = "La suma ejecutora Darthalia de Molino Tarren ofrece recompensa por el secretario Horrace Corzalbal, el ciudadano Wilkes, el minero Hackett y el granjero Kalaba.",
    ["T"] = "¡Atención!",
  },
  [568] = {
    ["D"] = "¡Throm\'ka, $c!$B$BLlegas a tiempo a Grom\'gol. Como Comandante del campamento base del Jefe de Guerra aquí, en la jungla, he jurado encargarme de la seguridad de todos los miembros de la Horda. Nuestra misión de proporcionar una cadena de suministros segura hasta Rocal se ha visto obstaculizada por algunos de los habitantes locales.$B$BTe pongo al cargo de disminuir la población de raptores en las afueras de Grom\'gol. Cuando hayas conseguido un avance significativo, vuelve a informarme y te reasignaré.",
    ["O"] = "El comandante Aggro\'gosh del Campamento Grom\'gol, quiere que mates 15 raptores colazote.",
    ["T"] = "La defensa de Grom\'gol",
  },
  [569] = {
    ["D"] = "Cuando llegamos por primera vez a esta jungla, no pretendíamos tener que luchar con las tribus locales. Nuestra primera prioridad es enviar suministros a Rocal. Pero han surgido complicaciones. Una banda de ogros ha ocupado las Ruinas de Mizjah al sureste de Grom\'gol. A pesar de las advertencias, siguen preparando emboscadas a nuestras caravanas de suministros.$B$BTe pongo al cargo de tratar con esos ogros tan poco cooperativos.$B$BVete a las Ruinas e intenta expulsarlos de la zona. Al menos, con esto les mandaremos un firme mensaje.",
    ["O"] = "El comandante Aggro\'gosh del Campamento Grom\'gol, quiere que mates 10 toscos ogros y 5 médicos brujos ogros.",
    ["T"] = "La defensa de Grom\'gol",
  },
  [570] = {
    ["D"] = "La Horda tiene que darse cuenta del poder de la magia de la jungla y, después, utilizarlo. Hasta ahora, mis rituales han tenido éxito. Puedo lanzar encantamientos muy poderosos. Para ti, valiente $c, crearé un objeto muy especial.$B$BTráeme algunas zarpas de panteras Faucesombrías junto con un colmillo de tigresa. No valdrá cualquier colmillo. Debe estar en perfecto estado.$B$BLas bestias moran en la jungla, pero encontrarás algunas por aquí cerca, justo al otro lado del río que va desde las Ruinas de Mizjah hasta el sureste.",
    ["O"] = "El vidente Mok\'thardin del Campamento Grom\'gol, quiere 8 garras Faucesombrías y un colmillo de tigresa inmaculado.",
    ["T"] = "El encantamiento de Mok\'thardin",
  },
  [571] = {
    ["D"] = "Lo que necesito ahora, es bastante difícil de conseguir.$B$BPara guardar las plumas, las zarpas y el colmillo inmaculado, necesito una cadena mágica. Solo servirá un tendón de gorila maduro.$B$BEl único lugar donde podrás encontrar uno es en un gorila del Valle Velo de Bruma viejo. Suelen ir a por comida al este de la Bahía del Botín.$B$BEl tendón debe ser perfecto. Quizás necesites usar algunas de tus habilidades para encontrar exactamente lo que necesito, pero merecerá la pena.",
    ["O"] = "Lleva un tendón de gorila maduro al vidente Mok\'thardin en Grom\'gol.",
    ["T"] = "El encantamiento de Mok\'thardin",
  },
  [572] = {
    ["D"] = "Los objetos que has conseguido de las bestias felinas del Valle son solo el primer requisito para un poderoso objeto encantado. Estas joyas de la jungla combinadas con mi ritual chamanístico, te proporcionarán una herramienta muy útil para ayudar a la Horda y al Jefe de Guerra.$B$BPero ahora debes conseguirme Plumas de acechadores de la selva. Tradicionalmente, las bestias llevan las plumas en sus brazaletes. Encontrarás una tribu de raptores directamente al sur de Grom\'gol, no muy lejos de La Gran Arena.",
    ["O"] = "Lleva 10 plumas de acechadores de la jungla al vidente Mok\'thardin en el Campamento Grom\'gol.",
    ["T"] = "El encantamiento de Mok\'thardin",
  },
  [573] = {
    ["D"] = "Necesito un componente más para completar el encantamiento.$B$BAl sur, hacia la costa oeste, por encima de los riscos, hay un manantial de agua sagrada. Este manantial es el sujeto de un gran conflicto entre eruditos religiosos y místicos. Todas las razas de Azeroth afirman que el agua del manantial recibió su don por su pueblo.$B$BMi última petición: tráeme agua sagrada de manantial. Ten cuidado. Una expedición de exploradores naga ha avanzado hacia el manantial. Tendrás que encargarte de ellos.",
    ["O"] = "El vidente Mok\'thardin de Grom\'gol necesita agua sagrada de manantial. También quiere que mates 10 exploradores naga.",
    ["T"] = "El encantamiento de Mok\'thardin",
  },
  [574] = {
    ["D"] = "Ahora que hemos conseguido reducir algunas de sus tropas regulares, debes enfrentarte a las tropas de élite. Cuando no están fuera matando a nuestros soldados rebeldes, se retiran a las profundidades de la parte trasera de la Base de Kurzen, a una cueva a la que llaman Reservas.$B$BConozco personalmente a muchas de las tropas de Kurzen y sé que son habilidosas, leales y crueles. La tarea que tienes ante ti no es fácil, $N.$B$BCuando las derrotes, informa al teniente Doren, mi oficial en jefe. Quiere recibir tu informe de primera mano.",
    ["O"] = "Mata 10 comandos Kurzen y 6 reductores de cabezas Kurzen.$B$BPreséntate ante el teniente Doren en el Asentamiento Rebelde.",
    ["T"] = "Fuerzas especiales",
  },
  [575] = {
    ["D"] = "\"El mejor artesano peletero de los Mares del Sur, del mundo antiguo y del mundo nuevo.\" ¡Así es como me llaman! ¡A mí! ¡Drizzlik! El mejor... ejem. Bueno.$b$bMis objetos de cuero se conocen en el mundo entero como los más exquisitos, ¡la elección de los entendidos!$b$bAcabo de recibir un pedido del director Riddlevox del Gremio de Manitas, de una docena de borceguíes. El producto que más vendemos.$b$bPara el molde básico, necesitaré pieles de crocolisco de los crocoliscos que están en el río de la parte norte de Tuercespina.",
    ["O"] = "Lleva 2 pieles grandes de crocoliscos fluviales a Drizzlik en la Bahía del Botín.",
    ["T"] = "La oferta y la demanda",
  },
  [576] = {
    ["D"] = "¡Alto! ¿Quién va?$b$bAaah, no soy nada sin mi ojo encantado especial. ¡Ya es bastante malo haber perdido mi ojo en combate en primer lugar! Pero por un golpe de suerte atracamos en Menethil y un zahorí viajante encantó un ojo especial de cristal para que pudiera ver de nuevo.$B$BAyer por la noche unos bucaneros Velasangre me atacaron cuando salía. ¡Y cogieron mi ojo! Parece ser que esos tipos han conseguido llegar a la Bahía del Botín.$B$B¿Quieres ayudar a un viejo lobo de mar a recuperar su ojo?",
    ["O"] = "Dizzy el Tuerto, en la Bahía del Botín, quiere que recuperes su ojo de los Bucaneros Velasangre.",
    ["T"] = "¡Ojo!",
  },
  [577] = {
    ["D"] = "Bien, ya tengo el molde básico y todo va bien, pero con las pieles que has traído no tendré suficiente para las capas interiores.$B$B\"¿Qué vamos a hacer, Drizzlik?\" Sin duda esa es la pregunta que ibas a hacer y, en fin, qué suerte que estés hablando con Drizzlik, porque te va a decir lo que vamos a hacer.$B$BLas capas interiores no se ven, así que podemos coger unas pieles más gruesas y baratas de los crocoliscos que están por el Lago Nazferiti.",
    ["O"] = "Lleva 5 pieles de crocoliscos Quijaforte a Drizzlik en la Bahía del Botín.",
    ["T"] = "Hay que ensamblar",
  },
  [578] = {
    ["D"] = "Si realmente hay un mago goblin y elementales de agua en esa isla, ¡la Piedra de las Mareas podría ser mía!$B$BQuiero que encuentres esa isla encantada... descubre lo que está pasando. Oh, y si quieres saber algo más sobre la Piedra de las Mareas, lleva este manuscrito a la Biblioteca de Ventormenta, deberían poder encontrarte una copia del libro donde me encontré la leyenda por primera vez.$B$B¿Podría ser que Gazban realmente descubriese la Piedra...?",
    ["O"] = "Encuentra la isla encantada para el Barón Revilgaz en la Bahía del Botín.",
    ["T"] = "La piedra de las mareas",
  },
  [579] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La biblioteca de Ventormenta",
  },
  [580] = {
    ["D"] = "Tábamos ayustando la braza mayor y soplaba fuerte en Los Acantilados Dominantes, veíamos las Tierras del Interior a distancia. Creí que el mar iba a tragarnos enteros.$B$BEl viejo Slim lo sabe todo sobre la valentía líquida. El capitán tenía una caja de oporto Pupellyverbos que le encargamos a un corsario que iba a Ventormenta.$B$BAsí que cuando cayó la tormenta de las Tierras del Interior, trinqué el grog. Una gran ola y todas las botellas cayeron por la borda. El capitán va a darme una buena paliza si no me ayudas a recuperarlas.",
    ["O"] = "Whiskey Delgado, en la Bahía del Botín, quiere que le lleves las botellas de oporto Pupellyverbos que perdió.",
    ["T"] = "El grog de Whiskey Slim",
  },
  [581] = {
    ["D"] = "Una maldición. Ha caído una maldición sobre el hijo menor de mi jefe, Yenniku. Como es costumbre entre los nuestros, fue entregado a los trols Gurubashi de Zul\'Gurub, pero después de que mi tribu, los Lanza Negra, se fueran con el orco Thrall, perdimos al pobre Yenniku. Me enviaron desde el lejano Orgrimmar, de vuelta a Tuercespina, para buscar al hijo de mi jefe.$B$BY creo que los Sangrapellejo lo tienen.$B$BSi quieres ayudarme, ve a por los Sangrapellejo. Les encontrarás en el norte. Consigue sus colmillos y vuelve a verme.",
    ["O"] = "Lleva 9 colmillos Sangrapellejo a Nimboya en el Campamento Grom\'gol.",
    ["T"] = "Caza para Yenniku",
  },
  [582] = {
    ["D"] = "Debemos descubrir si Yenniku está muerto.$B$BMata a los rebanacabezas Sangrapellejo y coge sus cabezas reducidas. Debes recoger muchas, muchas cabezas y las inspeccionaré para estar seguros de que la cabeza de Yenniku no está entre ellas.$B$BY si los Sangrapellejo asesinaron a Yenniku, ¡que caiga una maldición de 600 años sobre el espíritu de su jefe, Gan\'zulah!$B$BVe, $N. Encontrarás muchos rebanacabezas en las ruinas de Zuuldaia y Zul\'Kunda, en la costa, hacia el norte.",
    ["O"] = "Lleva 20 cabezas reducidas a Nimboya en el Campamento Grom\'gol.",
    ["T"] = "Caza cabezas",
  },
  [583] = {
    ["D"] = "¡Te doy la bienvenida a Tuercespina!$b$bQuizás no sepas esto, pero ese enano que está allí es el único y genuino Hemet Decautela, famoso héroe de guerra de la Alianza y gran maestro cazador. No le suele gustar que vengan forasteros al campamento, pero parece que tú has visto algo de acción en tu día, $c.$B$BSiendo un veterano de muchas batallas, el maestro Decautela tiene debilidad por los héroes.$B$BVete a hablar con él. Quizás pueda darte algunos trucos para cazar.",
    ["O"] = "Habla con Hemet Decautela.",
    ["T"] = "Bienvenido a la jungla",
  },
  [584] = {
    ["D"] = "La cabeza de Yenniku no estaba, demos las gracias a la Gran serpiente. Pero no estamos más cerca de encontrarle.$B$BDebemos conocer la verdad y me temo que solo el jefe de los Sangrapellejo y su médico brujo lo saben. Son los enemigos de la tribu Lanza Negra y no hablarán por voluntad propia, así que tendremos que sacar la verdad de sus labios sin vida.$B$BTráeme las cabezas de Gan\'zulah y Nezzliok el Feroz y colócalas en esta caldera.$B$BEstán en las profundidades de Zul\'Kunda. Trae sus cabezas... las obligaremos a hablar.",
    ["O"] = "Lleva la cabeza de Gan\'zulah y la cabeza de Nezzliok a la caldera en ebullición en el Campamento Grom\'gol.",
    ["T"] = "Las cabezas de los Sangrapellejo",
  },
  [585] = {
    ["D"] = "<La cabeza de Nezzliok habla.>$B$BEs cierto. Sé dónde llevaron al chico de los Lanza Negra. Me has vencido y has atrapado mi espíritu, pero no diré nada si no recibo algo a cambio.$B$BLos Sangrapellejo están en guerra con los Machacacráneos. Para conseguir mi favor, roba 3 cráneos trofeo a los Machacacráneos y colócalos aquí con nosotros, para que mi espíritu tenga esclavos en el Vacío Abismal. Un cráneo debe ser de su pila de trofeos en Balia\'mah, otro de Ziata\'jai y el último de Zul\'Mamwe.",
    ["O"] = "Lleva un trofeo de Balia\'mah, un trofeo de Ziata\'jai y un trofeo de Zul\'Mamwe a la caldera en ebullición del Campamento Grom\'gol.",
    ["T"] = "Hablando con Nezzliok",
  },
  [586] = {
    ["D"] = "<La cabeza de Gan\'zulah habla.>$B$B¡Yo te maldigo! ¡Y maldigo a tus ancestros!$B$BSolo la sangre y la venganza podrán templar mi ira, así que si eso es lo que deseas... Haz esto:$B$Blánzate de pleno al corazón de los Machacacráneos, al este. Mata a los más fieros y, si sobrevives, enfréntate a su jefe, Ana\'thek el Cruel. ¡Ja! Tráeme su armadura despedazada si puedes.$B$BQue te desgarre tus miembros y deje que tu carne se pudra y sea comida de buitres.",
    ["O"] = "Mata 8 cazadores Machacacráneos, 6 rebanacabezas Machacacráneos y 4 rabiosos Machacacráneos.$B$BLleva la armadura despedazada de Ana\'thek a la caldera en ebullición del Campamento Grom\'gol.",
    ["T"] = "Hablando con Gan\'zulah",
  },
  [587] = {
    ["D"] = "Ah, la vida del mar. No diré que es una buena vida, pero es mi vida.$B$BCuando llego a puerto me gusta buscar una buena botella de grog y rapé de buena calidad. Unas costumbres muy malas, ¿pero qué es un pirata sin sus vicios, eh?$b$bAsí que te puedes imaginar cómo me sentí cuando me enteré de que el herborista de la ciudad se había ido y lo había vendido todo. Parece ser que esos malditos asaltantes Velasangre secuestraron su cargamento.$B$BApuesto a que un audaz $c como tú podría convencerlos para compartir las mercancías, ¿eh?",
    ["O"] = "Deeg, en la Bahía del Botín, quiere que le lleves 15 unidades de tabaco.",
    ["T"] = "Tabaco rapé",
  },
  [588] = {
    ["D"] = "<Nezzliok habla.>$B$BNos has aplacado a mi jefe y a mí, así que te diré lo que buscas...$B$BEl clan ha perdido al joven Yenniku.$B$BFue secuestrado por el médico brujo Zanzil el Desarraigado. Ahora Zanzil le controla, en cuerpo y alma, y solo la magia más poderosa podría liberarle.$B$BUna magia que tú no posees, $c.$B$BQuizás tus aliados Lanza Negra puedan ser la esperanza de la salvación de Yenniku. Pero lo dudo.",
    ["O"] = "Habla con Kin\'weelay.",
    ["T"] = "El destino de Yenniku",
  },
  [589] = {
    ["D"] = "Las leyendas hablan de una joya oculta en las profundidades del corazón de Tuercespina. Llamamos a esa joya el Ojo de la Mente, y tiene grandes poderes. Si consigues el Ojo, quizás podamos usarlo para traer de la oscuridad al joven Yenniku.$B$BPreguntaré a los espíritus de la jungla cómo encontrar el Ojo de la Mente. Mientras lo hago, ve a la Mina de Cristal, al sureste de aquí y recoge fragmentos azul intenso de los basiliscos Quijahierro. Los fragmentos son poco comunes, pero necesitamos su magia para conseguir dominar el Ojo de la Mente.",
    ["O"] = "Lleva 3 fragmentos azul intenso a Kin\'weelay en el Campamento Grom\'gol.",
    ["T"] = "Los cristales canto",
  },
  [590] = {
    ["D"] = "¿Qué?$B$B¡¿Qué?!$B$B¿Quieres dinero? Yo no te debo dinero.$B$B¿Una carta? ¿Qué carta? Ah, estás buscando pelea. Muy bien, colega. ¡Vamos allá! Que te debo dinero...",
    ["O"] = "Derrota a Calvin Montague en Camposanto.",
    ["T"] = "Un trato de pícaro",
  },
  [591] = {
    ["D"] = "El Ojo de la Mente está en posesión de un mago ogro, al este de aquí, en las profundidades del Túmulo Ogro Mosh\'Ogg. Se llama Mai\'Zoth. Le conozco de cuando los Lanza Negra vivían en Tuercespina, es vicioso y su magia es poderosa.$B$BY, si tiene el Ojo de la Mente, entonces su magia será más fuerte todavía.$B$BDebes enfrentarte a Mai\'Zoth y conseguir el Ojo de la Mente. Se trata de una misión peligrosa, pero es la única oportunidad que tenemos de salvar a Yenniku.",
    ["O"] = "Lleva el Ojo de la mente a Kin\'weelay en el Campamento Grom\'gol.",
    ["T"] = "El Ojo de la mente",
  },
  [592] = {
    ["D"] = "He convertido el Ojo de la Mente en una gema de alma. Tiene un doble propósito: debes usarla para aturdir a Yenniku y, cuando lo hayas hecho, colócala sobre su cabeza para atrapar su alma.$B$BA continuación, lleva la gema a Nimboya.$B$BYenniku vive con Zanzil el Desarraigado, en las Ruinas de Aboraz, junto a La Costa de Cristal, en El Cabo de Tuercespina.",
    ["O"] = "Lleva la gema de alma llena a Nimboya.",
    ["T"] = "Salvar a Yenniku",
  },
  [593] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Gema de alma",
  },
  [594] = {
    ["D"] = "La nota dice, con buena caligrafía:$B$BA quien pueda ayudarme.$B$BPor favor, necesito ayuda urgentemente. Un gran Cieloleno me tiene de rehén en una isla en los Mares del Sur.$B$BLas estrellas de la noche son extrañas y los árboles de la jungla oscurecen el firmamento pero, en los días más claros, puedo ver la Orilla Salvaje al noroeste y lo que parecen grandes barcos cargueros al suroeste.$B$BTe lo suplico, ven a rescatarme.",
    ["O"] = "Encuentra al autor del mensaje en la botella.",
    ["T"] = "Mensaje en una botella",
  },
  [595] = {
    ["D"] = "Parece que tienes muchas ganas de ponerte a trabajar, colega. No tengo muy claro por qué un $r $c marinero de agua dulce, se ofrece a ayudar a los asaltantes Aguasnegras, pero no voy a rechazar tu ayuda.$B$BNuestro barco acaba de atracar aquí, en la Bahía del Botín, y corren rumores de que nuestros rivales, esos malditos y asquerosos bucaneros Velasangre, están por todo el sur de Tuercespina.$B$BTodos los hombres están muy ocupados dándole al grog y buscando damiselas. Lo que necesitamos es un explorador.$B$BExplora la cala que está justo al norte de la Bahía del Botín en La Costa Salvaje, a ver lo que descubres.",
    ["O"] = "El primer oficial Crazz quiere que eches un vistazo a la cueva norte de la Bahía del Botín.",
    ["T"] = "Bucaneros Velasangre",
  },
  [596] = {
    ["D"] = "Me enviaron a Grom\'gol para ayudar a Nimboya en su misión, pero... yo también tengo razones para estar aquí.$B$BLa tribu Sangrapellejo es enemiga de mi tribu. Conseguir trofeos de nuestros enemigos nos da poder sobre ellos. Podrías ayudar a nuestra tribu dando caza a nuestros enemigos.$B$BViven al norte. Mata trols Sangrapellejo y coge sus collares de huesos sangrientos. Consigue muchos y tendrás una gran recompensa.",
    ["O"] = "Lleva 25 collares de huesos sangrientos a Kin\'weelay en el Campamento Grom\'gol.",
    ["T"] = "Collares de huesos sangrientos",
  },
  [597] = {
    ["D"] = "En el mapa, la Bahía del Botín aparece marcada con una \"X\" roja, dibujada exactamente en el lugar donde estás ahora.$B$BEl texto lleno de garabatos dice:$B$B\"Debéis asegurar un campamento en esta ubicación y mantener la calma. Esperad la señal del puesto de mando del sur antes de atacar. Si alguien se aproxima, matadle.\"",
    ["O"] = "Informa sobre tus descubrimientos al primer oficial Crazz en la Bahía del Botín.",
    ["T"] = "Bucaneros Velasangre",
  },
  [598] = {
    ["D"] = "Ahora que has conseguido traerme trofeos de los Sangrapellejos, tengo otra misión para ti. La tribu de trols Machacacráneos vive al sureste y, durante años, hemos luchado con ellos. Si bien los Lanza Negra ya no estamos asentados en Tuercespina, estamos atados a nuestro conflicto con los Machacacráneos.$B$BVete a por ellos. Consigue sus collares de huesos partidos y vuelve a verme. Ayúdanos en nuestra guerra y conseguirás la amistad de los Lanza Negra.",
    ["O"] = "Lleva 25 collares de huesos partidos a Kin\'weelay en el Campamento Grom\'gol.",
    ["T"] = "Collares de huesos partidos",
  },
  [599] = {
    ["D"] = "¡Voto a bríos! ¿Así que los Bucaneros Velasangre están planeando atacar la Bahía del Botín? ¡No me puedo creer lo de esos bribones! La Bahía del Botín siempre estará dominada por los Asaltantes Aguasnegras.$B$BEsto es más grande de lo que yo había pensado. Informa al maestro de flota Cuernomarino inmediatamente. Ese viejo lobo de mar está por la ciudad, en alguna parte...",
    ["O"] = "Informa del ataque planeado de los Velasangre al maestro de flota Cuernomarino.",
    ["T"] = "Bucaneros Velasangre",
  },
  [600] = {
    ["D"] = "Ventura y Cía. está llevando varias operaciones en Tuercespina que priva a los duros trabajadores goblins, como yo, de conseguir un dinero honrado. ¡Por favor, tienes que ayudarme!$B$BVentura y Cía. está excavando cerca de la Mina de Cristal, al norte. No pueden entrar en la Mina debido a los basiliscos, pero consiguen extraer cristales canto de las colinas de alrededor.$B$BCoge los cristales y demuéstrales que no son los amos de la jungla.$B$BY... mmm... ¡tráeme esos cristales como prueba!",
    ["O"] = "Lleva 10 cristales canto azules a Loquillo Pinchabujas en la Bahía del Botín.",
    ["T"] = "La minería de Ventura y Cía.",
  },
  [601] = {
    ["D"] = "Sin duda, Gazban, el mago goblin que se rumorea que ha descubierto la Piedra de las Mareas, invocó a los elementales de agua. Como quizás sepas, la Piedra de las Mareas maldice a su portador, provocando que entre y salga del plano existencial...$B$BQuizás podamos encontrar la forma de contraatacar la maldición. Podemos conseguir que un mago de Dalaran analice el residuo mágico de uno de los brazales de un elemental de agua. Necesito que vuelvas a la isla y consigas algunos especímenes.",
    ["O"] = "Consigue 6 brazales de elementales de agua para el Barón Revilgaz en la Bahía del Botín.",
    ["T"] = "Elementales de agua",
  },
  [602] = {
    ["D"] = "Por lo que he oído, los magos de Dalaran están ocupados con la reconstrucción de la ciudad. Seguro que puedes encontrar a uno de ellos que tenga algo de tiempo para echar un vistazo a los brazales. Si algo sé sobre magos es que no pueden resistirse a un puzzle mágico.$B$BCoge la bolsa de brazales de elementales de agua y veamos si nos pueden ayudar con lo de la maldición de la Piedra. Busca al archimago Ansirem Tejepista. Ya he tratado antes con él y es muy sabio.",
    ["O"] = "Lleva los brazales de elementales de agua al archimago Ansirem Tejepista en Dalaran.",
    ["T"] = "Análisis mágico",
  },
  [603] = {
    ["D"] = "El mago que tiene la Piedra está ampliando su poder mediante algún punto focal... un altar de algún tipo. Deberías poder detenerlo destruyéndolo.$B$BResulta que sé cómo hacerlo: hay una daga antigua, otra reliquia trol. Y solo hay un pequeño... problema.$B$BVerás, encontré la daga mientras estaba investigando en Tuercespina, pero se la di a mi testaruda hija, Catelyn, que se escapó a la Bahía del Botín para convertirse en pirata.",
    ["O"] = "Habla con Catelyn en la Bahía del Botín.",
    ["T"] = "Llave de Ansirem",
  },
  [604] = {
    ["D"] = "Crazz habla muy bien de tu entrega a los Asaltantes Aguasnegras. Voy a confiarte una misión de vital importancia para la flota.$B$BInfíltrate en el puesto de mando del sur que los Bucaneros Velasangre han establecido en la Orilla Salvaje. Asalta el campamento. Tráeme sus cartas de navegación y las órdenes escritas. Veremos exactamente qué pretenden hacer esas babosas.",
    ["O"] = "El maestro de flota Cuernomarino en la Bahía del Botín quiere que mates 10 aventureros Velasangre y le lleves las cartas de navegación y las órdenes Velasangre.",
    ["T"] = "Bucaneros Velasangre",
  },
  [605] = {
    ["D"] = "Solo hay cristales canto en Tuercespina y, para ciertos grupos, son muy valiosos. Yo puedo conseguir esos cristales, pero la maldita Ventura y Cía. ha complicado las cosas para los empresarios como yo.$B$BMe gustaría contratarte.$B$BLos basiliscos de Tuercespina comen esos cristales. Eso es lo que les da su piel endurecida y, algunas veces, se pueden conseguir cristales de calidad decente de la piel.$B$BPuedes conseguirlos de cualquier basilisco, pero los menos asquerosos están en la orilla sur de Zul\'Kunda, hacia el norte.",
    ["O"] = "Lleva 10 fragmentos de cristales canto a Loquillo Pinchabujas.",
    ["T"] = "Fragmentos de cristales canto azules",
  },
  [606] = {
    ["D"] = "Si hay algo que no soporto, ¡es la gente que no paga sus deudas de juego! Y hay muchos que me deben dinero. ¿Crees que podrías ayudarme?$B$BPrimero, está \"Trémulo\" Phillipe, ahí, en el muelle. Me debe mucho después del juego de la semana pasada, ¡y quiero lo que me corresponde! Pero para conseguirlo, tendremos que asustarle.$B$BSal de la ciudad y caza gorilas del Valle Velo de Bruma viejos. Encuentra una bonita selección de menudillos y llévaselos a Trémulo. No le digas que son de un animal. Deja que piense lo peor.",
    ["O"] = "Lleva 5 menudillos de gorila del Valle Velo de Bruma al \"Trémulo\" Phillipe en la Bahía del Botín.",
    ["T"] = "Asusta a Trémulo",
  },
  [607] = {
    ["D"] = "Toma, coge esta bolsa y dásela a Lobo de Mar. ¡Y dile que le iba a pagar! ¡¡Lo juro!!$B$BSolo que me emborraché un poco y me perdí... ya sabes cómo son estas cosas. Lo entenderá, ¡no hace falta que te pongas así!",
    ["O"] = "\"Lleva el pago de Trémulo a \"Lobo de Mar\" MacKinley\".",
    ["T"] = "Vuelve con MacKinley",
  },
  [608] = {
    ["D"] = "¡Maldito sea Firallon! No podría navegar un esquife por el Lago de Cristal y mucho menos liderar toda una flota contra nosotros.$B$BLos Bucaneros Velasangre pagarán su descuido. Ahora que sabemos su plan de ataque, podemos atacar y tomarles por sorpresa.$B$B¿Pero por qué enviar a toda la flota para hacer el trabajo de un grupo de aventureros? $n, coge a algunos de tus camaradas más capaces, id al lugar de atraque de Firallon y asesinad al líder Velasangre. Su flota se hundirá poco después...",
    ["O"] = "El maestro de flota Cuernomarino en la Bahía del Botín quiere que mates al capitán Aguasmansas, al capitán Sinquilla y al maestro de flota Firallon.",
    ["T"] = "Bucaneros Velasangre",
  },
  [609] = {
    ["D"] = "La lista empieza a menguar, pero todavía hay muchos que me deben dinero.$B$BA continuación tenemos a Maury \"Patón\" Wilkins, Jon-Jon el Cuervo, y Chucky \"Diezdedos.\"$B$BLas deudas de esos malditos perros son de varios meses y creí que se habían largado de la ciudad para evitar pagarme. Pero después me enteré de que están malditos y hechizados y se dedican a vagar por las ruinas de la jungla. Pero no me importa su destino, ¡quiero lo que me pertenece!$B$BEstán en las Ruinas de Aboraz y en las Ruinas de Jubuwal, al noreste de aquí. Encuéntralos y consigue mi dinero.",
    ["O"] = "Lleva el Pie Deforme de Maury, el catalejo dorado de Jon-Jon y el anillo enorme de Chucky a \"Lobo de Mar\" MacKinley en la Bahía del Botín.",
    ["T"] = "El vudú debido",
  },
  [610] = {
    ["D"] = "¿Mi PADRE te ha enviado? Pensaba que ese viejo chocho ya habría dejado de intentar encontrarme.$B$BRealmente no me escapé por su culpa, es solo que él no debería haber supuesto que aceptaría tener una vida aburrida, leyendo libros polvorientos y tratando a magos canosos, estirados y acartonados.$B$BSi necesitas mi daga, tendrás que recuperarla, la tiene \"el Guapo\" Duncan, uno de los Bucaneros Velasangre. Pon algo entre sus hombros y seguro que te la da.",
    ["O"] = "Consigue la daga de Catelyn de \"El Guapo\" Duncan.",
    ["T"] = "\"El Guapo\" Duncan\"",
  },
  [611] = {
    ["D"] = "¿Así que estás buscando un altar? Bueno, no soy tan experta en ruinas trol como mi padre, pero he oído hablar de una ciudad trol hundida cerca de esa isla que estás buscando. Quizás podrías probar allí.$B$BHas recuperado mi cuchillo, así que supongo que puedes usarlo... ¡Pero lo quiero de vuelta cuando hayas terminado!",
    ["O"] = "Destruye el Altar de las Mareas con la daga de Catelyn, mata a Gazban y lleva la Piedra de las Mareas y la daga de Catelyn al Barón Revilgaz.",
    ["T"] = "La maldición de las mareas",
  },
  [613] = {
    ["D"] = "¡Maldición! Ese asqueroso de Maury ha puesto un candado en su pie, ¡y no consigo abrirlo! Guarda el botín en el pie, ¡y es justo lo que me debe!$B$BTenemos que encontrar la llave que abre el candado. Todos saben que Maury apostaba con los ogros Mosh\'Ogg, en su túmulo, al noreste. Y me dijo, cuando todavía estaba vivo, que la última vez que fue al túmulo, acabó siendo perseguido porque había ganado mucho. Quizás los ogros de allí tengan su llave.$B$BSi es así, probablemente tengas que matar a muchos de ellos para encontrar al que la tiene...",
    ["O"] = "Lleva la llave de Maury a \"Lobo de Mar\" Mackinley en la Bahía del Botín.",
    ["T"] = "El pie roto de Maury",
  },
  [614] = {
    ["D"] = "Yo solía navegar en barco desde aquí hasta Trinquete, en Kalimdor. Fui un capitán de mucho éxito e inmejorable reputación.$B$BHasta que...$B$BEsos malditos gigantes marinos acabaron conmigo. Destruyeron mi barco, mataron a mi tripulación y me dejaron en un barco salvavidas con dirección a la Bahía del Botín. Y uno de esos gigantes, Gorlash, robó mi cofre del Capitán. Dijo que lo quería para usarlo como caja de rapé. ¡Increíble!$B$BHe oído que Gorlash está cerca de la costa al este de aquí. ¡Encuéntrale y recupera mi cofre! Hazlo y conseguirás la gratitud de un capitán.",
    ["O"] = "Lleva el cofre de Smotts a Hecklebury Smotts en la Bahía del Botín.",
    ["T"] = "El cofre del Capitán",
  },
  [615] = {
    ["D"] = "Cuando perdí mi primer barco por culpa de esos gigantes, compré otro. Se llamaba La Venganza de Smott, lo llené de suministros y una tripulación y partí en busca de los villanos. Les encontré... pero me vencieron. Destruyeron La venganza de Smott, mataron a mi segunda tripulación y de nuevo me dejaron en un barco salvavidas.$B$BEsta vez, otro de los gigantes, Negolash, robó mi alfanje.$B$BEnfréntate a Negolash y tráeme mi alfanje.$B$BHabla con Tardo. Fue mi cocinero en La venganza de Smott y sobrevivió al ataque. Podrá ayudarte a encontrar al gigante.",
    ["O"] = "Habla con Tardo.",
    ["T"] = "El alfanje del Capitán",
  },
  [616] = {
    ["D"] = "El Barón Revilgaz me ha pedido permanecer alerta en busca de rumores y cosas extrañas que ocurren en la Bahía del Botín y en Tuercespina. Generalmente no creo mucho en historias de fantasmas, pero oí a unos marineros en la taberna hablando sobre una isla encantada a poca distancia de la costa. Dicen que hay elementales de agua y un fantasma goblin enloquecido.$B$BSi vas a ver a Revilgaz, ¿podrías contarle la historia?",
    ["O"] = "Habla con el Barón Revilgaz en la Bahía del Botín.",
    ["T"] = "La isla fantasma",
  },
  [617] = {
    ["D"] = "¿Andas buscando trabajo, $n? Quizás tenga algo pa ti si t\'apetece un pequeño desafío.$B$BTengo un amigo llamado Groy que se está forrando vendiendo lo que los nagas llaman junco akiris: es un tipo de valioso junco del océano. No tengo muy claro pa qué sirve, pero paga bien por fajos de esa cosa.$B$BTráeme diez fajos de eso y haremos negocios. La última vez que vimos a los nagas fue en la playa que está al norte de aquí.",
    ["O"] = "Consigue 10 fajos de junco akiris para el corsario Bloads en Bahía del Botín.",
    ["T"] = "Tallos de juncos akiris",
  },
  [618] = {
    ["D"] = "Negolash es un gigante hambriento.$B$BCuando los gigantes destruyeron nuestro barco, Negolash fue directo a la cocina. No quería comerse a la tripulación, como los otros gigantes. Negolash quería vino... y mis alas de águila ratonera a la parrilla. Son mi especialidad, y he tenido que ir hasta las Tierras Inhóspitas para encontrar la receta.$B$BSi quieres atraer al gigante, consigue vino y un montón de alas de águila ratonera. Ponlo todo en nuestro viejo barco salvavidas, en la costa, al sureste de aquí. Cuando Negolash huela toda esa comida, ¡irá seguro!",
    ["O"] = "Lleva 10 Alas de águila ratonera a la parrilla y 5 botellas de vino de vid de jungla al barco salvavidas del capitán Smotts.$B$BMata a Negolash y lleva el alfanje de Smotts al capitán Smotts en Tuercespina.",
    ["T"] = "Enfréntate a Negolash",
  },
  [619] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La tentación de Negolash",
  },
  [620] = {
    ["D"] = "This fine sash has the letters \"HS\" embroidered along its hem.",
    ["O"] = "Return the Monogrammed Sash to its owner.",
    ["T"] = "The Monogrammed Sash",
  },
  [621] = {
    ["D"] = "Zanzil el Desarraigado vive con sus seguidores en las Ruinas de Jubuwai y Aboraz, al noreste de la Bahía del Botín. Está exiliado de su tribu. ¿Por qué? Bueno...$B$BZanzil sabe cómo someter la mente de otros y sus líderes le temen. Utiliza una mezcla de plantas de la jungla con sus seguidores para eliminar su voluntad y fortalecer sus cuerpos.$B$BEsa mezcla sería muy valiosa para aquellos que deseen controlar a otros.$B$BTráeme muestras de esa mezcla y seguro que encuentro un buen uso.",
    ["O"] = "Lleva 12 muestras de la mezcla de Zanzil a Loquillo Pinchabujas en la Bahía del Botín.",
    ["T"] = "El secreto de Zanzil",
  },
  [622] = {
    ["D"] = "Mientras no estabas arreglé la olla. Incluso añadí un pitorro de vapor extra, ¡y mira! Hay una nueva trituradora en el fondo que puede cortar incluso huesos de piernas.$B$BSí, sí, la olla mecánica de Krazek, es una herramienta que satisface al cocinero en un 100%. Seguro que su dueño estará satisfecho.",
    ["O"] = "Lleva la olla mecánica de Krazek arreglada al cabo Kaleb en el Asentamiento Rebelde.",
    ["T"] = "Vuelve con el cabo Kaleb",
  },
  [623] = {
    ["D"] = "$n, ya que tás aquí, he recibío un mensaje de Groy cuando no estabas. Dijo que estaba satisfecho con el último cargamento y que tiene un cliente que quiere más de esa cosa ahora mismo.$B$BYo no voy a Theramore hasta dentro de una semana, pero si tú quieres hacer el viaje, podrías llevar los juncos por mí.$B$B¿Te apetece una aventura? Sabes dónde está el Marjal Revolcafango, ¿no? Yo iría con cuidado, Theramore puede ser peligroso dependiendo de quién seas...",
    ["O"] = "Lleva un fajo de juncos akiris al corsario Groy, en Theramore.",
    ["T"] = "Tallos de juncos akiris",
  },
  [624] = {
    ["D"] = "Hay un puente ornamentado y con mucho tránsito$bAl este de Viento Muerto y al sur de Crestagrana$bBajo el puente te espera algo$bAlgunos lo llaman un consejo otros, una pista$bAllí te esperará eternamente$bViendo pasar el tiempo en el Pantano de las Penas",
    ["O"] = "¡Resuelve el enigma!",
    ["T"] = "El enigma de Cortello",
  },
  [625] = {
    ["D"] = "Te espera una tierra muy lejana, en Kalimdor$bDonde el peligro ha destruido las esperanzas y los destinos$bAl este de Los Baldíos el tiempo es severo$bLa penumbra y el peligro imperan en el Marjal Revolcafango$bPero allí hay una pista, para aquellos dispuestos a aceptar el desafío$bEsperando ser encontrada... en una cueva... al suroeste",
    ["O"] = "¡Resuelve el enigma!",
    ["T"] = "El enigma de Cortello",
  },
  [626] = {
    ["D"] = "¡A Lordaeron! La gran tierra de confusión$bDonde valientes soldados juegan con la muerte$bEncuentra tu camino a través del gran disturbio$bAllí encontrarás el final de esta misión$bJunto a la costa de las Tierras del Interior, en la base de las cataratas$bDesde las profundidades del agua, te llama tu tesoro.",
    ["O"] = "¡Resuelve el enigma!",
    ["T"] = "El enigma de Cortello",
  },
  [627] = {
    ["D"] = "Hay una cueva en la montaña, en el Cañón Mostacho Seco, al norte de las Tierras Altas de Arathi. En esa cueva hay un extraño mineral: sangrita. La sangrita de esta cueva no tiene una calidad superior, pero incluso la sangrita de baja calidad es valiosa... para aquellos que sepan usarla.$B$BMe gustaría conseguir sangrita.$B$BArreglaré tu olla mecánica, pero mientras lo hago... tráeme algunas muestras de sangrita. Si me traes las suficientes, te daré una bonificación.$B$BOh, y si no tienes la habilidad necesaria para extraerla... ¡encuentra a alguien que la tenga!",
    ["O"] = "Lleva 4 fajos de mineral de sangrita inferior a Krazek en la Bahía del Botín.",
    ["T"] = "Un favor a Krazek",
  },
  [628] = {
    ["D"] = "Ahora que hemos terminado el interior, lo único que queda es añadir la capa exterior. Para esto, solo utilizo la mejor piel de crocolisco anciano. Duran más y son un lienzo perfecto para añadir los diseños y dibujos que completan el estilo borceguí de Drizzlik.$b$bPero no es fácil encontrar la piel perfecta, la única que podría servir es la piel de un crocolisco aguasalada anciano. Los crocoliscos ancianos solo salen fuera para defender a los otros crocoliscos aguasalada, así que quizás tengas que matar a unos cuantos para que salgan.",
    ["O"] = "Lleva una piel de crocolisco maduro a Drizzlik en la Bahía del Botín.",
    ["T"] = "Borceguí",
  },
  [629] = {
    ["D"] = "Mucho tiempo atrás, un gran temblor de tierra sumergió una antigua ciudad trol bajo las aguas de La Costa Escarpada, al noroeste. Llamamos a ese lugar El Arrecife Mortal, ya que ahora los murlocs residen en las ruinas de la ciudad, atacando a todo el que se acerca.$B$BEn esas ruinas hay una antigua tablilla que narra la vieja fábula de Gri\'lek, un héroe de leyendas trol. La fábula es sagrada para la tribu Lanza Negra y, aunque la tablilla es muy grande como para moverla, quiero un fragmento para consagrarlo en nuestro nuevo hogar de Orgrimmar.",
    ["O"] = "Lleva fragmento de tablilla a Kin\'weelay en el Campamento Grom\'gol.",
    ["T"] = "El Arrecife Mortal",
  },
  [630] = {
    ["D"] = "$N, pongo mi fe en ti y en cualquiera que te ayude a liberarme. Mi secuestrador es el gran Cieloleno rey Mukla. Domina a las bestias de esta isla que me tienen aquí contra mi voluntad con ayuda de una cadena. Si puedes matarle y encuentras la llave de estos grilletes, estaré en deuda contigo eternamente.$B$BA menudo puedo oírle en la parte sureste de la isla, pero ten cuidado. Hay que tomarle muy en serio.",
    ["O"] = "Mata al rey Mukla y vuelve a por la princesa Poobah con la llave para liberarla.",
    ["T"] = "Mensaje en una botella",
  },
  [631] = {
    ["D"] = "Se oyó el estruendo de una enorme explosión por todas las colinas. \'¡Fue horrible, créeme!$B$BLos buenos soldados murieron en la defensa el Puente Thandol. Largatrenza dice que nos vengaremos de los Hierro Negro en un santiamén.$B$BPero estamos en un mundo lleno de dolor y no veo ningún refuerzo. Largatrenza envió a Herrumbra a explorar el puente del Este para ver si podemos ganarlo.$B$BEso fue hace días. Con Dun Modr bajo asedio no tenemos hombres suficientes para ir a buscarlo.$B$BQuizás tú puedas ayudarnos...",
    ["O"] = "Rhag Garmason quiere que localices a Herrumbra.",
    ["T"] = "El Puente Thandol",
  },
  [632] = {
    ["D"] = "Parece que el pobre Herrumba cayó en una emboscada de los Hierro Negro. Descubres un pergamino junto a su puño cerrado.$b$bLleva la desalentadora noticia a Rhag Garmason y enséñale lo que has descubierto.",
    ["O"] = "Vuelve a informar a Rhag Garmason y enséñale el pergamino que encontraste en el cadáver de Herrumbra.",
    ["T"] = "El Puente Thandol",
  },
  [633] = {
    ["D"] = "¡¡¡Cielos!!! ¡¡¡No!!! Pobre Herrumbra. Es una tragedia.$B$BNo debemos permitir que su muerte haya sido en vano. El pergamino que has descubierto indica que Kam Furiahonda, el cerebro del ataque, ordenó atesorar un alijo de explosivos al otro lado del Puente Thandol, en las Tierras Altas de Arathi.$B$B¡Si esos explosivos llegan al puente, nuestra principal vía de suministros recibirá un duro golpe! ¡Hay que destruir el alijo, $n! Vuelve a verme cuando hayas terminado tu misión.$B$BEres nuestra única esperanza.",
    ["O"] = "Destruye el cajón de explosivos.",
    ["T"] = "El Puente Thandol",
  },
  [634] = {
    ["D"] = "Voy a pedirte otro pequeño favor, $c. Estamos esperando las reservas de Forjaz. Pero Roggo tiene noticias de un campamento de la Alianza al norte de las Tierras Altas de Arathi, en el Refugio de la Zaga.$b$bViaja hasta allí y busca al capitán Nials. Pregunta al buen capitán si la Alianza puede ofrecernos algunos soldados que nos ayuden a recuperar Dun Modr y el Puente Thandol.",
    ["O"] = "Busca al capitán Nials en el Refugio de la Zaga.",
    ["T"] = "Petición a la Alianza",
  },
  [635] = {
    ["D"] = "El fragmento palpita cuando lo agarras...$B$BSientes una especie de súplica saliendo del fragmento, como si alguien estuviera intentando comunicarse contigo, alguien que necesita tu ayuda desesperadamente.$B$BUna visión llena tu mente... son las montañas septentrionales de Arathi, y ves también un cristal brillante sobresaliendo del suelo.$B$BTe sientes $gatraído:atraída; hacia ese lugar.",
    ["O"] = "Encuentra el cristal de tu visión.",
    ["T"] = "Cristal en las montañas",
  },
  [636] = {
    ["D"] = "El libro cuenta la historia de una princesa elemental y de su cautiverio en las profundidades de las Tierras Altas de Arathi.$B$BTambién habla de un afloramiento secreto de cristales en las Tierras Altas. Estos cristales, los Fragmentos de Myzrael, son una ventana a la prisión de la princesa.$B$BSi un alma valiente encontrara los Fragmentos de Myzrael, tal vez pudiera hablar con ella...",
    ["O"] = "Encuentra los Fragmentos de Myzrael.",
    ["T"] = "Las Leyendas de la Tierra",
  },
  [637] = {
    ["D"] = "El sobre empapado y desgastado se deshace entre tus manos, revelando varias páginas de una carta escrita con una cuidada caligrafía.$B$BLa carta está dirigida a Sara Balloo de Forjaz y comienza así: \"Querida Sara...\"",
    ["O"] = "Entrégale la carta de Sully Balloo a Sara Balloo.",
    ["T"] = "La carta de Sully Balloo",
  },
  [638] = {
    ["D"] = "Con Thoras Aterratrols muerto y Stromgarde en ruinas, Vol\'jin ha ordenado que hagamos todo lo posible por robar la legendaria espada Trol\'kalar a los humanos, para que podamos usarla en nuestras próximas batallas contra los trols de Zul\'Gurub.$B$BZengu, uno de nuestros cazadores más expertos, se ha dirigido a Sentencia, al noreste de las Tierras Altas de Arathi, para encargarse de todo.",
    ["O"] = "Viaja a Sentencia en las Tierras Altas de Arathi y habla con Zengu.",
    ["T"] = "Aterratrols",
  },
  [639] = {
    ["D"] = "En tiempos ancestrales, los trols lucharon contra el Imperio Arathoriano y contra nuestro más temible adversario Lord Ignaeus de Strom, al que llamaban Aterratrols. Acudía a la batalla empuñando a Trol\'kalar, verdugo de troles en su antigua lengua.$B$BPero ahora la estirpe de Aterratrols se ha debilitado, su bastión está en ruinas.$B$BLa espada está protegida por varios sigilos que se dividieron entre los supervivientes de Stromgarde, debemos recuperarlos.$B$BLa Hermandad se llevó el primero, lo custodia uno de sus hombres de Stromgarde.",
    ["O"] = "Consigue el sigilo de Strom para Zengu en Sentencia.",
    ["T"] = "El sigilo de Strom",
  },
  [640] = {
    ["D"] = "La tumba de Thoras Aterratrols está protegida por tres sigilos, el primero de los cuales recuperaste de la Hermandad. Será más difícil conseguir los otros dos ya que la milicia de Stromgarde los tiene firmemente atrincherados.$B$BEl hijo de Thoras, Galen, dividió el sigilo de Thoradin en once fragmentos que dispersaría entre los defensores de Stromgarde. Recupera todos los fragmentos y llévaselos a Tor\'gan, el chamán trol, será capaz de recomponer con ellos el sigilo completo.",
    ["O"] = "Recupera los 11 fragmentos de sigilo de los defensores de Stromgarde y llévaselos a Tor\'gan en Sentencia.",
    ["T"] = "El sigilo roto",
  },
  [641] = {
    ["D"] = "La reconstrucción ha funcionado perfectamente. Ahora se puede usar el sigilo para desbloquear el sello de la tumba de Aterratrols. Devuélveselo a Zengu, seguro que tiene más tareas para ti.",
    ["O"] = "Llévale el sigilo de Thoradin restaurado a Zengu en Sentencia.",
    ["T"] = "El sigilo de Thoradin",
  },
  [642] = {
    ["D"] = "Me llamo Myzrael. Soy una princesa de la tierra, y mis captores, los gigantes, me han encerrado en el subsuelo de las Tierras Altas de Arathi. Estos fragmentos de cristal son mi único medio para hablar con el mundo de la superficie.$B$BPor favor, ayúdame. Los aliados de los gigantes, los kóbolds Mostacho Seco, tienen un fragmento como este en el Cañón Mostacho Seco, hacia el este. Para que ese pedazo cobre poder, debes conseguir motas de Myzrael de los kóbolds y aplicárselas.$B$BTe lo suplico, $n, ¡ayúdame!",
    ["O"] = "Recolecta 12 motas de Myzrael y llévalas a los fragmentos iridiscentes en el Cañón Mostacho Seco.",
    ["T"] = "La princesa secuestrada",
  },
  [643] = {
    ["D"] = "Aunque apenas pueda afirmarse que controlan Stromgarde, y no digamos las Tierras Altas de Arathi, el príncipe humano lleva los restos de su caballería por el campo. No logro entender por qué lo hace, aunque ¿quién entiende a los humanos?$b$bSean cuales sean sus motivos, le ha dado uno de los sigilos al teniente Valorcall, el jefe de su caballería. Encuéntralo y tráeme el sigilo.",
    ["O"] = "Recupera el sigilo de Arathor y llévaselo a Zengu en Sentencia.",
    ["T"] = "El sigilo de Arathor",
  },
  [644] = {
    ["D"] = "El último sigilo necesario para desbloquear la tumba de Thoras Aterratrols está en manos de su propio hijo. Mora en lo más recóndito de Stromgarde, en una capilla en los distritos de la milicia. El príncipe Galen está bien protegido y es él mismo un guerrero respetable, así que esta tarea podría resultar de lo más difícil.",
    ["O"] = "Mata al príncipe Galen Aterratrols y llévale el sigilo de Aterratrols a Zengu en Sentencia.",
    ["T"] = "El sigilo de Aterratrols",
  },
  [645] = {
    ["D"] = "¿Qué es esto? Con los cuatro sigilos reunidos, han logrado recomponerlos y crear un solo sigilo. No hay duda de que con este lograrán atravesar la protección de la tumba de Aterratrols.$B$BLos restos de Aterratrols están enterrados en el Sagrario detrás de la capilla de Stromgarde. Recupera Trol\'kalar.",
    ["O"] = "Recupera la espada Trol\'kalar de la Tumba de Aterratrols en Stromgarde.",
    ["T"] = "Trol\'kalar",
  },
  [646] = {
    ["D"] = "La luz se desvanece despacio, la espada Trol\'kalar, retenida en una funda pétrea se ha liberado.",
    ["O"] = "Entrega Trol\'kalar a Zengu en Sentencia.",
    ["T"] = "Trol\'kalar",
  },
  [647] = {
    ["D"] = "¡Anoche tomé un poco de licor de luna! Largatrenza me mataría si supiera que perdí el sentido mientras estaba de guardia.$B$BEsa bebida sí que es fuerte... Sentía como si una brigada entera de máquinas de asedio me hubiera pasado por encima.$B$B¡Oh, mira qué hora es! Le prometí al maestro cervecero Bilger de Costasur que saldaría mi deuda con él enviándole licor de luna.$B$BEl problema es que solo dispongo de 15 minutos para ello. ¿Te importaría llevárselo tú? ¡Date prisa!",
    ["O"] = "Llévale el licor de luna de MacKreel al maestro cervecero Bilger de Costasur.",
    ["T"] = "Licor de luna de Mackreel",
  },
  [648] = {
    ["D"] = "Vuelves a oír la voz del gnomo que sale del robot:$b$b\"Tengo que llevar a OOX-17/TN a un lugar abierto y seguro en el que pueda iniciar el largo proceso de despegue. Tiene un posicionador de serie, pero necesito tiempo para que vuelva a estar operativo. Lleva al robot a, digamos, el Puerto Bonvapor. Allí podré tomarme mi tiempo para intentar que todo vuelva a funcionar correctamente.\"$b$b\"Llévalo con cuidado al puerto y después ven a verme a Bahía del Botín. Oglethorpe Obnoticus. ¡Corto y cierro!\"",
    ["O"] = "Lleva a OOX-17/TN hasta el Puerto Bonvapor y después informa a Oglethorpe Obnoticus, que está en Bahía del Botín.",
    ["T"] = "¡Rescatar a OOX-17/TN!",
  },
  [649] = {
    ["D"] = "Tengo un trabajito de rescate para ti, y si te gusta el buen calimocho, seguro que lo aceptas. ¿Que qué es?$b$bPues una bebida, ¿qué va a ser?$b$bHay un cargamento de calimocho de miel de las Tierras del Interior en una avanzada abandonada de la Horda. ¡Ya no se hace un calimocho como ese! Uno de esos atontados con los que trabaja Malton está interesado, así que traéme una caja de calimocho para él y un par para mí. Habla con Malton para que te dé los detalles.",
    ["O"] = "Habla con Malton Droffers en Orgrimmar.",
    ["T"] = "Recupera el calimocho",
  },
  [650] = {
    ["D"] = "El elfo con el que necesitas hablar se llama Gilveradin Cazasol. ¡Puede identificar cualquier vino, con cosecha y todo, con solo olerlo! Él puede embotellar el calimocho que necesitamos, así que tendrás que hablar con él. Necesitamos seis botellas en total: dos para papá y todas las que él quiera y, créeme, querrá alguna que otra. Cuando hayas embotellado el calimocho y tengas las dos botellas de papá, vuelve aquí.$b$bEl campamento de Gilveradin está por las Tierras del Interior, cerca de la casa de un alto elfo… y esos altos elfos no son buena gente. Créeme.",
    ["O"] = "Habla con Gilveradin Cazasol; su campamento está en las Tierras del Interior.",
    ["T"] = "Recupera el calimocho",
  },
  [651] = {
    ["D"] = "Hay cuatro Piedras de vínculo diseminadas por todo Arathi. Esas piedras son las que me mantienen anclado bajo la tierra.$b$bPara desbloquearlas tienes que desbloquear primero la más fuerte: la Piedra de vínculo interior.  Y para hacerlo debes reunir las llaves de las otras tres.$b$bLas piedras están custodiadas por guardias pero, con un poco de astucia o suerte, encontrarás la forma de sortearlos.",
    ["O"] = "Reúne la llave ardiente, la llave del emblema y la llave tronadora de la Piedra de Vínculo Occidental, la Piedra de Vínculo Oriental y la Piedra de Vínculo Exterior.$B$BLlévalas a la Piedra de Vínculo Interior.",
    ["T"] = "Piedras de Vínculo",
  },
  [652] = {
    ["D"] = "Desbloquear las Piedras de vínculo me liberó en gran parte de mi confinamiento. Pero aún queda una cerradura por abrir  Es la Piedra llave que ves en ese círculo de piedras. Y la llave la tiene uno de mis captores, Fozruk, el guardián de las piedras. $b$bDebes conseguir esa llave. Los guardianes de las piedras la llaman el Cetro del Orden. Arrebátasela a Fozruk y úsala para abrir la Piedra llave. $b$b$N, me has ayudado mucho. Pero necesito un poco más de ayuda… ¡Mi gratitud hará temblar los mismísimos pilares de la tierra!",
    ["O"] = "Encuentra a Fozruk y mátalo. Lleva la vara del Orden a la piedra angular en las Tierras Altas de Arathi.",
    ["T"] = "Desbloquear la piedra angular",
  },
  [653] = {
    ["D"] = "<Myzrael habla.>$B$B¡Libre! ¡SOY LIBRE! Libre para recuperar la fuerza, sin que me vean mis captores. Porque si me enfrentara a ellos ahora, seguro que me vencerían y me volverían a encerrar.$B$BPero con el tiempo haré frente a los gigantes ¡y se arrepentirán de haber sido mis guardias!$B$BEres $gun:una; noble $galiado:aliada;, $n. Dentro de algún tiempo volveré a necesitarte. Cuando esté listo, necesitaré que me conjures para devolverme a la superficie.$B$BHabla con Gerrig Agarrahueso. Se encuentra en Forjaz, en una tienda de La Caverna Abandonada. Él sabe cómo realizar el conjuro.",
    ["O"] = "Habla con Gerrig Agarrahueso en La Caverna Abandonada de Forjaz.",
    ["T"] = "Aliados de Myzrael",
  },
  [654] = {
    ["D"] = "Al activar la fuente de energía el equipo de estudio de campo empieza a brillar y a vibrar. Parece una señal de que todo funciona correctamente.$B$BDe acuerdo con el ingeniero jefe Silvaina, a partir de ahora tienes dos horas para recoger los datos necesarios en el desierto de Tanaris. Si no consigues hacerlo en un plazo máximo de dos horas, perderás toda la información que tenías hasta el momento y deberás comprar una nueva fuente de energía.$B$BVuelve a hablar con él cuando hayas concluido tu tarea.",
    ["O"] = "Consigue muestras aceptables de 8 basiliscos, 8 hienas y 8 escorpiones. Devuélvele el equipo de estudio al ingeniero jefe Silvaina, que está en Gadgetzan, antes de que se agote la fuente de energía.",
    ["T"] = "Recogida de muestras en Tanaris",
  },
  [655] = {
    ["D"] = "Lo recuerdo como si fuera ayer... Tantos años sin esperanza. Con la madera y el acero ligándome a este terrible lugar. ¡Y por fin, por fin! Ahí estaba el Jefe. Mazohecatombe, con sus agoreras placas y su martillo de guerra en alto.$b$bFue terrible... Que el golpe asestado por la mano de un cobarde acabara con nuestro héroe más admirado. Cargó por detrás con su caballo y lo atravesó con su lanza... Yo caí de rodillas. ¿Qué esperanza nos quedaba? ¿Qué esperanza nos queda...?",
    ["O"] = "Habla con Tor\'gan en Sentencia.",
    ["T"] = "Sentencia",
  },
  [656] = {
    ["D"] = "Debes derrotar a Myzrael antes de se vuelva más fuerte. Si no lo haces, en cuanto tenga suficientes poderes ¡desafiará a sus captores! Hay quien piensa que puede controlarla, pero si así fuera, ¡¿por qué la encadenaron los gigantes?!$B$B¿A que no tiene ni pies ni cabeza?$B$BPara derrotarla, conjúrala en los fragmentos de Myzrael donde hablaste con ella la primera vez. Derrótala, coge sus grilletes de Eldritch y pónselos a los fragmentos de Myzrael.$B$BHazlo y volverá a estar presa.",
    ["O"] = "Ve a los fragmentos de Myzrael, invócala y derrótala.$B$BRecolecta sus grilletes de Eldritch y pónselos a los fragmentos de Myzrael.",
    ["T"] = "Invocar a la princesa",
  },
  [657] = {
    ["D"] = "<Quae empieza a examinar la carpeta.>$B$BEsto SÍ que es interesante. Al parecer, el boticario Jorell ha estado trabajando en unos experimentos letales aquí en Go\'Shek. No estoy seguro de cuál es la relación con Trabalomas pero a Phin no le gustaría que dejásemos que siga adelante.$B$BTuvimos un roce con unos orcos cuando no estabas por lo que no estoy en condiciones de ayudar a Kinelory. Si te apetece, va a necesitar que la protejan cuando vaya a la Granja a robar lo que sea que Jorell aún no haya enviado a Molino Tarren.",
    ["O"] = "Habla con Kinelory.",
    ["T"] = "Sospechas de una nueva Peste",
  },
  [658] = {
    ["D"] = "¿Así que Phin te ha mandado ver qué hacemos? No me sorprende. Se preocupa demasiado.$B$BHemos vigilado la granja durante días y Kin dice que hace poco ha visto a un mensajero saliendo de esa casita que hay hacia el sur.$B$BHabla mucho, pero me fío de su instinto cuando estamos en sitios peligrosos.$B$B¿No sabrás seguir rastros, por un casual? Creemos que el mensajero podría llevar notas al Molino Tarren. Si pudiéramos echar el guante a esos documentos... Seguro que nos ayudan a entender qué está pasando en Trabalomas.",
    ["O"] = "Encuentra a la mensajera Renegada y lleva su cartera sellada a Quae cerca de la Granja Go\'shek.",
    ["T"] = "Sospechas de una nueva Peste",
  },
  [659] = {
    ["D"] = "Saludos. Te doy la bienvenida, $N. Dirijo un grupo de aventureros que investigan sucesos extraños que han tenido lugar en esta zona. $b$bMi primo de Trabalomas ha tenido que sacrificar a su perro Stanley porque el animal creció místicamente hasta alcanzar un gran tamaño y empezó a atacar a todo el que se le acercara. $b$bCreo que los no-muertos están involucrados en eso y estamos buscando indicios que lo prueben. $b$bDos compañeros míos, Quae y Kinelory, fueron a la granja de Go\'Shek en las Tierras Altas de Arathi y hace días que no tenemos noticias suyas. ¿Podrías encontrarlos por mí? Empiezo a preocuparme de verdad.",
    ["O"] = "Encuentra a Quae y a Kinelory cerca de la Granja Go\'shek en las Tierras Altas de Arathi.",
    ["T"] = "Sospechas de una nueva Peste",
  },
  [660] = {
    ["D"] = "Muy bien, $N. Si tú estás listo, yo también.$b$b¿Vamos?",
    ["O"] = "Protege a Kinelory hasta que haya regresado con Quae.",
    ["T"] = "Sospechas de una nueva Peste",
  },
  [661] = {
    ["D"] = "He visto a Kinelory con mucho peor aspecto después de una noche en la taberna que ahora. No hay duda de que tu protección fue de gran ayuda.$B$BCon los objetos que trajo de la Granja, Phin debería hacerse una idea de lo que está ocurriendo en Arathi. Solo para asegurarnos de que no hay represalias después de vuestra incursión, nos quedaremos aquí un poco más. ¿Te gustaría volver a Costasur y hablar con Phin?",
    ["O"] = "Encuentra a Phin Odelic en Costasur.",
    ["T"] = "Sospechas de una nueva Peste",
  },
  [662] = {
    ["D"] = "O’Breen guió a las naves hasta la cala. Estábamos seguros de que ahí encontraríamos el tesoro. El Heraldo atracó sin novedad pero cuando El Capricho y el Espíritu de Argénteos se acercaban, la marea empezó a bajar. ¡Lo más extraño que haya visto nunca!$b$bbLas dos naves encallaron en algo… una especie de templo que había en el arrecife. Se hundieron a gran velocidad... Fue una tragedia.$b$bNecesitamos recuperar sus cuadernos de bitácora y sus cartas de navegación antes de partir. ¡No podemos permitir que nadie vea esa información!",
    ["O"] = "El primer oficial Nizlix quiere que recuperes los cuadernos de bitácora y las cartas de navegación de El capricho y del Espíritu de argénteos.",
    ["T"] = "Rescate en alta mar",
  },
  [663] = {
    ["D"] = "Lolo ve a un minúsculo $r! ¡Siempre al loro, Lolo está!$B$B$gBienvenido:Bienvenida; a la Cala de Faldir. El capitán O’Breen dijo que solo estaríamos aquí durante unas horas. Lo justo para reunir el tesoro y volver a Bahía del Botín.$B$BPero Lolo piensa que vamos a estar allí más tiempo. Perdimos otros dos barcos de nuestra formación. Pobres Espíritu de argénteos y El Capricho. ¡En el fondo del mar ahora descansan!$B$BLolo sugiere que hables con el capitán O\'Breen si te vas a quedar por aquí.",
    ["O"] = "Habla con el capitán O\'Breen en la Cala de Faldir.",
    ["T"] = "¡Tierra a vista!",
  },
  [664] = {
    ["D"] = "Ah, esta vida es terrible. Debería estar en una tumba de agua ahora mismo, no aquí, junto al fuego y respirando aire fresco.$b$bLa tripulación se esforzó tanto para salvar El Capricho, mi hermosa nave... Benditos sean, benditos sean sus corazones.$b$bO\'Breen llegó a la costa sano y salvo, pero nosotros no tuvimos tanta suerte. La mar nos hundió, lo juro.$b$bUn momento antes estábamos a flote y después… el agua empezó a entrar. ¡Y esas bestias! Mataron a todos los supervivientes. Esa escoria naga... Yo perdí el conocimiento y acabé aquí, a salvo.$b$bQuiero ver a esas bestias muertas.",
    ["O"] = "El capitán Tripacero de Cala de Faldir quiere que mates a 10 asaltantes Espinadaga y a 3 hechiceras Espinadaga.",
    ["T"] = "Ahogar las penas",
  },
  [665] = {
    ["D"] = "Ahora que somos Asaltantes Aguasnegras hechos y derechos, debemos ayudar a O’Breen a encontrar el tesoro elfo perdido.$B$BEs casi imposible encontrar las gemas en el mar sin ayuda. El doctor ha creado unas gafas que ayudan a ver en la oscuridad del mar. Pero hay que cargarlas con la energía que emana una piedra encantada de la cueva que hay en la colina.$B$B¡Y la cueva está maldita! Cuando alguien se acerca, cae en una emboscada. Defiéndeme para que pueda recoger la energía de la piedra con las gafas.",
    ["O"] = "Escolta al profesor Fiseltor hasta la cueva y a su regreso.",
    ["T"] = "El tesoro sumergido",
  },
  [666] = {
    ["D"] = "El tesoro lleva tanto tiempo en el fondo del mar que las gemas se han calcificado y convertido en piedras. Pero gracias al poder de estas gafas protectoras las localizarás sin esfuerzos.$B$B¡Un inventito gnomo de gran utilidad!$b$bAsí que toma prestadas estas gafas de caza de gemas, $n, y mira a ver si puedes recolectar algo del tesoro perdido para el capitán O\'Breen.$B$BYo mismo bucearía hasta allí pero... mm... estooo, tengo importantes asuntos científicos que atender en tierra firme y seca… digo, pues eso.",
    ["O"] = "El doctor Draxlegauge de la Cala de Faldir quiere que reúnas 10 gemas de elfo y le devuelvas las gafas de caza de gemas cuando hayas acabado.",
    ["T"] = "El tesoro sumergido",
  },
  [667] = {
    ["D"] = "Vaya, malas noticias. Al parecer, los Dagaespinazo están amasando recursos para un ataque.$B$BLos nagas son famosos por su crueldad en el combate. Seguirán atacando hasta que me maten.$B$BNo valoro mi propia vida más que la de mis hombres, pero debes defenderme. La tripulación no será capaz de volver a Bahía del Botín sin mis conocimientos de navegación.$B$BLos nagas vendrán desde el mar. Necesito que permanezcas aquí conmigo para eludir el ataque. Controla el cañón y recházalos.$B$B¿Estás $glisto:lista;, $n?",
    ["O"] = "Protege a Temblores O\'Breen durante el ataque.",
    ["T"] = "La muerte viene de las profundidades",
  },
  [668] = {
    ["D"] = "No hagamos esperar al capitán O\'Breen. Querrá ver estas gemas el primero. A fin de cuentas, ¡a eso ha venido!$B$BY después de unas semanas tratando con los piratas, el profesor y yo no hemos dado cuenta de que lo peor para nuestra salud sería que nos pillaran con su tesoro en las manos.$B$BAhí va, $n, llévaselas a O\'Breen.",
    ["O"] = "Llévale las gemas de elfo al capitán O\'Breen.",
    ["T"] = "El tesoro sumergido",
  },
  [669] = {
    ["D"] = "El maestro de flota Cuernomarino querrá enterarse de nuestro hallazgo sin demora.$B$BPero como puedes ver, no estamos precisamente en posición de salir de aquí No solo la marea es demasiado baja, sino que esas malditas criaturas a las que hemos perturbado no nos quitan ojo de encima.$B$BPuedes ser de gran ayuda para los incursores Aguasnegras si consigues llegar a Bahía del Botín y decirle al maestro de flota Cuernomarino que hemos descubierto el tesoro y estamos trabajando para extraer más. Llévate esta muestra como prueba.",
    ["O"] = "Llévale una muestra de gema de elfo al maestro de flota Cuernomarino en Bahía del Botín.",
    ["T"] = "El tesoro sumergido",
  },
  [670] = {
    ["D"] = "Tú, $n, pareces tener un espíritu aventurero. Mi flota está anclada en Bahía del Botín hasta que podamos reabastecernos.$B$BEstán ocurriendo cosas graves aquí y en el extranjero y necesito hacer llegar una comunicación de alto secreto a Temblores O\'Breen que está amarrado en la costa de Arathi.$B$B¿Puedo confiar en que entregues este mensaje a salvo y sin traicionar su secreto?",
    ["O"] = "Llévale la carta de Cuernomarino a Temblores O\'Breen en las Tierras Altas de Arathi.",
    ["T"] = "El tesoro sumergido",
  },
  [671] = {
    ["D"] = "He notado algo extraño en el aire... un sentimiento que me hace hervir la sangre. Algo sospechaba. Me puse en camino para encontrar el origen de mi desasosiego y descubrí al oeste, en la Mansión Redilnorte, restos residuales de un conjuro demoníaco. Conforme me acercaba, me empezó a hervir la sangre y crecía mi rabia.$B$BDescubrí en el suelo un amuleto de sangrita, que se usa para conjuros demoníacos. Aunque, el que encontré estaba agotado. Necesitaré otro para poder investigar pero no me atrevo a volver a la mansión. ¿Podrías traerme alguno?",
    ["O"] = "Consigue 10 amuletos de sangrita para Tor\'gan en Sentencia.",
    ["T"] = "Magia demoníaca",
  },
  [672] = {
    ["D"] = "Tratamos de tranquilizarlo, alegrarlo, contarle historias de nuestra poderosa ciudad de Orgrimmar en Kalimdor, pero nada lo motivaba.$B$BRelatos de nuestro joven Jefe de Guerra, Thrall, que lleva la armadura y el martillo de Orgrim... pero, quizás... ¡Quizás! Una prueba de nuestra recién encontrada magia chamán le devolvería la esperanza. ¡Merece la pena intentarlo!$B$BUn pequeño objeto que podríamos hacer para él, sí... Tráeme unos ojos de raptor y veré lo que puedo hacer.",
    ["O"] = "Consigue 10 ojos de raptor de las Tierras Altas de los zancudos y de los vándalos de las Tierras Altas para Tor\'gan en Sentencia.",
    ["T"] = "El despertar de los espíritus",
  },
  [673] = {
    ["D"] = "No me cabe la menor duda de que un poderoso brujo reside entre las paredes de Stromgarde. De vez en cuando, usando una reliquia mágica arcana, este brujo ha conjurado a gran número de terribles demonios. Tenemos que eliminar la fuente de su energía.$B$BEncuentra al brujo y mátalo. Tráeme cualquier objeto mágico que encuentres sobre su persona y tomaré las medidas necesarias para destruirlo y asegurarme de que su poder no se usará para contaminar esta tierra.",
    ["O"] = "Recupera el orbe de sangrita de Marez Cowl y llévaselo a Tor\'gan en Sentencia.",
    ["T"] = "Magia demoníaca",
  },
  [674] = {
    ["D"] = "Toma, lleva este amuleto a Gor\'mul y veamos si la fuerza del raptor puede encender la llama del guerrero que otrora ardía en él.",
    ["O"] = "Dale el amuleto de espolón de raptor a Gor\'mul.",
    ["T"] = "El despertar de los espíritus",
  },
  [675] = {
    ["D"] = "<Gor\'mul tira el amuleto al suelo.>$B$B¿Te ha enviado Tor\'gan? ¡Bah! Por qué él compadecerse de mí ahora, no alcanzo entender. Compasión ser negada mí cuando no permitir mí morir con mi Jefe de Guerra en la batalla. Cruel golpe del destino tocar mí...",
    ["O"] = "Habla con Tor\'gan.",
    ["T"] = "El despertar de los espíritus",
  },
  [676] = {
    ["D"] = "Alfeñiques miserables... ¿Se puede saber qué miras, $c? Te crees muy especial, ¿eh? Demuéstralo.$B$BLa avanzada de Sentencia, en Arathi, está siendo atacada. La derrota es inevitable si no les enviamos ayuda de inmediato.$B$BPero antes de salir corriendo, será mejor que te pongas a prueba. Al noreste de Stromgarde, en Arathi, hay un túmulo de ogros en el que vive la tribu Puño de Roca. Cuando seas capaz de manejarte con esos ogros, preséntate ante Bombo Vil en Sentencia.",
    ["O"] = "Mata a 8 ogros y 10 gorilas Puño de Roca; luego ve a ver a Bombo Vil a la avanzada de Sentencia, en las Tierras Altas de Arathi.",
    ["T"] = "La Sentencia",
  },
  [677] = {
    ["D"] = "Los trols Secacorteza y los ogros Puño de Roca de Arathi se han unido para arrebatarnos esta tierra, pero no dejaremos nuestro sino en sus manos ni en las de nadie más. Los ejecutaremos y les demostraremos que este lugar es nuestro hogar.$B$BLos trols nos han traicionado; los ogros nos usarían de comida y de cama si les dejásemos, eso jamás ocurrirá.$B$BEmpezamos el ataque sobre los trols Secacorteza al sur de este lugar. Ejecútalos por docenas y regresa solo cuando sus campamentos estén bañados en sangre.",
    ["O"] = "Mata a 10 lanzahachas Secacortezas, 10 cazadores de cabezas y 8 médicos brujas y vuelve junto a Bombo Vil en la avanzada de Sentencia.",
    ["T"] = "Un llamamiento a las armas",
  },
  [678] = {
    ["D"] = "Tu fuerza es evidente, $n. Veamos ahora cómo te va frente a un enemigo real.$B$BLos Puño de Roca tienen una guarida al suroeste de este lugar hacia el puente de los enanos y, si no me equivoco, los destructores y los magis lugareños supondrán para ti un desafío aún mayor, así ha sido para nosotros hasta ahora.$B$BEjecuta a tantos como puedas y regresa ante mí con ese cuerpo cuando lo hayas conseguido, o que lo haga tu espíritu si fracasas. Sea como fuere, alabaré tu tenacidad en la batalla.",
    ["O"] = "Mata a 10 toscos Puño de Roca y 4 magos Puño de Roca y vuelve junto a Bombo Vil en la avanzada de Sentencia.",
    ["T"] = "Un llamamiento a las armas",
  },
  [679] = {
    ["D"] = "La bestia está lista para la decapitación, y con toda la fuerza, $N. Has demostrado tu valía a la hora de ayudarnos y creemos que eres la persona indicada para este golpe final. Los líderes de estos ataques han construido su guarida en las ruinas de Stromgarde, un claro símbolo de la fragilidad humana.$B$BEncuentra y mata a los Lores Puño de roca y su lacayo chamán, antes de que nos logren expulsar de estas tierras.",
    ["O"] = "Mata a 10 chamanes Puño de Roca y 15 señores Puño de Roca y vuelve junto a Bombo Vil en la avanzada de Sentencia.",
    ["T"] = "Un llamamiento a las armas",
  },
  [680] = {
    ["D"] = "Eres de gran ayuda para mi marido, $c, pero él no sabe por qué Sentencia está siendo atacada.$B$BOr\'Kalar, el péon de Mug\'thol\' en Stromgarde, odia visceralmente a mi marido y desea por encima de cualquier cosa su muerte por derrotarlo hace años.$B$BSegún mis augurios, Or\'Kalar le ha tendido una trampa a mi marido, así que le he ocultado signos de la presencia del ogro.$B$BMi marido se lanzaría sin más miramientos si supiera el paradero del ogro y me gustaría pedirte tu ayuda para ejecutar a la criatura.",
    ["O"] = "Mata a Or\'Kalar y lleva su cabeza a Korin Fel en la avanzada de Sentencia.",
    ["T"] = "Una auténtica amenaza",
  },
  [681] = {
    ["D"] = "Estamos librando una guerra por Stromgarde, y la estamos perdiendo. Los ogros, la Hermandad y nosotros mismos hemos dividido nuestra otrora gran ciudad, y cada facción se encuentra atrapada en un sangriento enfrentamiento.$B$BNuestras fuerzas menguan mientras las del enemigo aumentan.$B$BNecesitamos ayuda de la ciudad de Costasur pero es difícil recibir suministros mientras la Hermandad controle el territorio que hay que cruzar.$B$BVe a la base de la Hermandad en Mansión Redilnorte y ejecútalos. Quizás entonces disminuya la presión sobre los trenes de suministros y nos llegarán más.",
    ["O"] = "Mata a 10 bandoleros de la Hermandad y 6 mercenarios de la Hermandad.$B$BVuelve junto al capitán Nials en el Refugio de la Zaga.",
    ["T"] = "Mansión Redilnorte",
  },
  [682] = {
    ["D"] = "La Hermandad de Stromgarde se resiste a nuestro contingente de la milicia allí desplazado. Para incentivar a la chusma a enfrentarse a nuestros más disciplinados soldados, los jefes de la Hermandad han puesto precio a los nuestros, prometiendo oro por cada insignia de Stromgarde arrebatada a soldados muertos.$B$BQuiero que recuperes esas insignias, ¡no podemos permitir que esos degolladores se lleven el trofeo de nuestros valientes caídos!$B$BPenetra en la zona de Stromgarde controlada por la Hermandad y busca nuestras insignias.",
    ["O"] = "Llévale las insignias de Stromgarde al capitán Nials en el Refugio de la Zaga.",
    ["T"] = "Insignias de Stromgarde",
  },
  [683] = {
    ["D"] = "$n, tengo un pequeño favor que pedirte.$B$BLlévale esta nota al gran monarca de Forjaz, el rey Magni Barbabronce. Es poca cosa para ti, que tanto has ayudado ya a la causa de la Alianza.$B$BPresentarme yo ante el Rey en mi estado de duelo resultaría contraproducente. Seguro que lo entiendes.",
    ["O"] = "Lleva la nota de Sara Balloo al rey Magni Barbabronce de Forjaz.",
    ["T"] = "Un favor a Sara Balloo",
  },
  [684] = {
    ["D"] = "Le han puesto precio a la cabeza de Marez Cowl, una consejera de alto nivel y agente de la Hermandad. La vieron por última vez en el territorio controlado por la Hermandad en Stromgarde.$B$BLa recompensa se reclama al capitán Nials.$B$BOjo: Marez es una hábil y taimada bruja, famosa por sus trapicheos con los demonios. Se recomienda extremar la precaución en su caza.",
    ["O"] = "Llévale la cabeza de Marez al capitán Nials en el Refugio de la Zaga.",
    ["T"] = "Se busca: Marez Cowl",
  },
  [685] = {
    ["D"] = "La Milicia de Stromgarde ha puesto precio a la cabeza de Lord Cresta de Halcón y a la de su guardaespaldas Otto. Cresta de Halcón dirige a la Hermandad en las Tierras Altas de Arathi y su muerte supondría un duro golpe para ellos.$B$BSu guardaespaldas Otto, aunque no sea un objetivo estratégico, es un feroz adversario y ha acabado con docenas de nuestros defensores.$B$BLas recompensas se reclaman al capitán Nials.",
    ["O"] = "Llévale la cabeza de Otto y la de Cresta de Halcón al capitán Nials en el Refugio de la Zaga.",
    ["T"] = "Se busca: Otto y Lord Cresta de Halcón",
  },
  [686] = {
    ["D"] = "No espero que una buena enana como la señora Balloo entienda que las farsas de guerra son por el bien de nuestra gente. La muerte de su marido, como la de cada enano que lucha en nombre de la Alianza, me pesa en el alma.$b$bAunque no puedo traer a su marido de vuelta del Vacío Abisal, al menos puedo rendirle homenaje.$b$bVoy a encargar al Gran maestro Mablesten un monumento a la memoria de Sully Balloo.$b$b$c, a ti te corresponde ir a llevarle mensaje de mi encargo.",
    ["O"] = "El rey Magni Barbabronce quiere que hables con el Gran maestro Mablesten.",
    ["T"] = "Un homenaje real",
  },
  [687] = {
    ["D"] = "Theldurin el Perdido fue un día miembro de nuestra orden, el Martillo Crepuscular. Pero perdió la cabeza y desdeñó nuestras enseñanzas. Antes de huir de nuestro lado, robó el Pergamino de Myzrael. No pudimos cogerle, pero hay rumores de que se oculta en las Tierras Inhóspitas, ha enloquecido y no dice más que incoherencias.$B$BPara traer de vuelta a la Dama, debemos encontrar el pergamino. Y para hacerlo, debes encontrar a Theldurin.",
    ["O"] = "Encuentra a Theldurin el Perdido.",
    ["T"] = "Theldurin, el perdido",
  },
  [688] = {
    ["D"] = "<Myzrael habla.>$B$B¡Libre! ¡SOY LIBRE! Libre para recuperar la fuerza, sin que me vean mis captores. Porque si me enfrentara a ellos ahora, seguro que me vencerían y me volverían a encerrar.$B$BPero con el tiempo haré frente a los gigantes ¡y se arrepentirán de haber sido mis guardias!$B$BEres $gun:una; noble $galiado:aliada;, $n. Dentro de algún tiempo volveré a necesitarte. Cuando esté lista, necesitaré que me conjures para devolverme a la superficie.$B$BHabla con Zaruk en Sentencia. Él sabe cómo realizar el conjuro.",
    ["O"] = "Habla con Zaruk en Sentencia.",
    ["T"] = "Aliados de Myzrael",
  },
  [689] = {
    ["D"] = "¿Así que el Rey quiere un monumento en honor de un soldado llamado Sully Balloo? ¿Y dices que el pobre diablo murió durante la tragedia del Puente Thandol?$b$b¿Habrá alguna vez paz en Azeroth?$b$bSea como sea, todos los encargos del Rey son de la máxima importancia. Para crear una obra épica y duradera, necesito una de las mejores piedras disponibles. Tráeme granito de Alterac; lo encontrarás en las las Colinas de Darrow, en Trabalomas.",
    ["O"] = "El Gran maestro Mablesten de Forjaz quiere 5 piezas de granito de Alterac.",
    ["T"] = "Un homenaje real",
  },
  [690] = {
    ["D"] = "Saludos, $n. Tienes aspecto de aventurero y tengo una tarea que requiere atención.$B$BEl archimago Trelane, mi primer maestro, huyó de Stromgarde en las Tierras Altas de Arathi no hace mucho, y me ha pedido que le ayude a recuperar unas cosas que se dejó.$B$BSitiaron la ciudad e intentó defender la Torre de Arathor pero fue en vano. Ya tengo a agentes en las Tierras Altas pero me han pedido que envíe refuerzos. ¿Te interesa?",
    ["O"] = "Encuentra a Skuerto en el Refugio de la Zaga, en las Tierras Altas de Arathi.",
    ["T"] = "Una petición de Malin",
  },
  [691] = {
    ["D"] = "El archimago Malin nos ha enviado a Skuerto y a mí a recolectar componentes para él. No sabía ni por dónde empezar pero ahora que estás aquí, creo que el problema está resuelto.$B$BNecesito lo siguiente: colmillos Secacorteza, bolsas medicinales Secacorteza y una navaja del tipo cazador oscuro.$B$B¿Podrías encontrar estas cosas para mí? Los Secacorteza viven al sudeste de Arathi.",
    ["O"] = "Encuentra 10 colmillos Secacorteza, 4 bolsas de medicina Secacorteza y una navaja de cazador de las Sombras para el aprendiz Kryten en el Refugio de la Zaga.",
    ["T"] = "Valer su peso en oro",
  },
  [692] = {
    ["D"] = "El pergamino de Myzrael... ¡¿el pergamino de Myzrael?! ¡Yo que tú, ni me acercaba! Su magia puede sacar a Myzrael a la superficie del mundo y si lo haces, ¡estaremos en un lío!$B$BO... quizás ya es demasiado tarde. A lo mejor ya está libre. Quizás solo esté recobrando fuerzas antes de desatar toda su ira sobre nosotros. De ser así, entonces ¡NECESITAS el pergamino!$B$B... ¡qué mala suerte que esté destruido! Furiosos elementales lo robaron, lo desgarraron y luchan ahora por los fragmentos.$B$BPero puedes reunir esos fragmentos, quizás podamos arreglarlo...",
    ["O"] = "Lleva el fragmento de pergamino rasgado, el fragmento de pergamino arrugado y el fragmento de pergamino chamuscado a Theldurin el Perdido.",
    ["T"] = "Los fragmentos perdidos",
  },
  [693] = {
    ["D"] = "Eh, $N. Perdona, antes igual me quedara corto... y no, no es un chiste.$B$BM’he enterao del lugar donde acabó el primer objeto de Trelane: Kor\'gresh lo tiene en la Sala Puño de Roca. Y, por cierto, le disparaba con hielo pa’ mantenerla firme, no debería de tardar en enterarse de pa\' qué sirven sus poderes.$B$BLa Sala Puño de Roca está al sur de donde estamos, luego tira pa\'l este de Puente Thandol. Encuentra al ogro y trae pa’cá esa varita.$B$BMientras no estás, veré si se m’ocurre qué hacer con Stromgarde.",
    ["O"] = "Encuentra la varita de evocación de Trelane y llévasela a Skuerto en el Refugio de la Zaga.",
    ["T"] = "Empuñar la varita",
  },
  [694] = {
    ["D"] = "¿Habéis descansado?$B$BEste será nuestro siguiente paso: hay ogros rodeando la Torre de Arathor en Stromgarde. Tendremos que activar las defensas de la torre pa’ tener la más mínima posibilidad de vencerles y llegar vivos hasta los objetos. Pa\' eso, necesitaremos unas gemas a las que les ha tomao cariño el chamán Puño de Roca. Las arrebataron de la torre pero pa\' mí que no les va a durar.$B$BConsigue una d’esas gemas y llévasela a Kryten, él conoce el hechizo pa’ que funcionen de llave.",
    ["O"] = "Encuentra un ágata azur y llévasela al aprendiz Kryten en el Refugio de la Zaga.",
    ["T"] = "Las defensas de Trelane",
  },
  [695] = {
    ["D"] = "Aquí tienes, $n. Solo hay que apretar la gema contra uno de los pedestales cerca de la base de la torre para activar las defensas. Úsala cuando estés $glisto:lista; para entrar en la torre a buscar los objetos de Trelane que faltan.$B$BQue tengas suerte. Estaré en el campamento esperando tu regreso. Ah, y habla con Skuerto antes de marcharte. Te dará el resto de detalles que necesitarás.",
    ["O"] = "Habla con Skuerto.",
    ["T"] = "Un encantamiento de aprendiz",
  },
  [696] = {
    ["D"] = "Aquí tienes los objetos de Trelane qu’andabas buscando: la filacteria, el orbe y el ágata de brasa.$B$BLos tres deberían encontrarse en unos baúles en el interior de la torre y no deberías tener ningún problema pa’ abrirlos solo; aunque ten cuidado con los ogros.$B$BLas defensas de la torre no bastarán pa’ detenerlos; tendrás que tener las armas preparadas. Los ogros sabrán qu’estás allí robándoles, así que no s\'andarán con mimos si te pillan.$B$BTen cuidado y suerte.",
    ["O"] = "Encuentra la filacteria de Trelane, el orbe de Trelane y el ágata ámbar de Trelane, y llévaselo todo a Skuerto en el Refugio de la Zaga.",
    ["T"] = "Ataque a la torre",
  },
  [697] = {
    ["D"] = "Lleva esta carta a Ventormentay dile a Malin que Kryten y yo pronto iremos pa’llá. Te recompensará debidamente por habernos ayudado aquí, $N, de eso no me cabe la menor duda.$B$BQue viajes sin percances.",
    ["O"] = "Lleva la carta sellada al archimago Malin en el Barrio de los Magos de Ventormenta.",
    ["T"] = "Una petición de Malin",
  },
  [698] = {
    ["D"] = "Estoy intentando reunir algunos suministros para enviarlos a nuestra avanzada cerca de la playa del este. Tok\'kar hace lo que puede, pero entre sus guardias y cocinar para los soldados, no le queda mucho tiempo.$B$BLes encanta cocinar carne de denteserras cuando pueden, pero no he tenido tiempo de conseguirles otro cargamento. ¿Te importaría echarles una mano?$B$BLos denteserras jóvenes no son tan sabrosos como los viejos, así que no los cojas. Hay que esperar un año o dos para que alcancen el tamaño ideal.",
    ["O"] = "Lleva 8 costillas de denteserra sin preparar a Tok\'Kar en el Pantano de las Penas.",
    ["T"] = "Falta de suministros",
  },
  [699] = {
    ["D"] = "Con la amenaza de murlocs en la costa, nos hemos quedado aquí para proteger, o advertir si no podemos hacer más, de cualquier amenaza a Rocal. No nos deja mucho tiempo para relajarnos y nos vendría bien algo de ayuda...$B$BLas garras de pargos denteserra son unas armas excelentes si se preparan bien. Tráeme algunas garras y te recompensaremos con una de nuestras armas como agradecimiento por la carne fresca y la buena compañía.",
    ["O"] = "Lleva 6 garras de pargos denteserra a Tok\'Kar en el Pantano de las Penas.",
    ["T"] = "Falta de suministros",
  },
  [700] = {
    ["D"] = "Ah, esta piedra tan buena es de Lordaeron. Es una lástima que la tierra esté arrasada.$B$B¡Pero el monumento ha quedado muy bien! Creo que el Rey estará satisfecho.$B$BPor favor, ve a notificar a Su Majestad que el monumento ya está terminado.",
    ["O"] = "Informa al rey Magni Barbabronce.",
    ["T"] = "Un homenaje real",
  },
  [701] = {
    ["D"] = "Con algunos elementos más, poder crear un objeto que imbuir Gor\'mul ¡con la astucia del raptor! Con el poder del raptor recorriéndolo, ¡Gor\'mul conocer nuevo poder y descubrir otra vez al guerrero durmiente en é!$B$BNecesitar corazones de raptor para componer el objeto y el hechizo.",
    ["O"] = "Consigue 12 corazones de raptor de los acechacarnes de las Tierras Altas para Tor\'gan.",
    ["T"] = "La astucia del raptor",
  },
  [702] = {
    ["D"] = "Toma este orbe y usa su energía para darle a Gor\'mul la esencia del raptor. Seguro que su ánimo recobrará el ímpetu y volverá a encontrarle sentido a la vida. Cuando vuelva a la normalidad, podremos explicarle el estado actual de la Horda y contaremos con la ayuda de un gran guerrero en nuestras incesantes batallas.",
    ["O"] = "Dale el orbe de Tor\'gan a Gor\'mul.",
    ["T"] = "La astucia del raptor",
  },
  [703] = {
    ["D"] = "¡Sí señor, mis alas de águila ratonera a la parrilla son para chuparse los dedos! La salsa es cremosa y la carne, penetrante. ¡Y su aroma atraerá a los hambrientos de kilómetros a la redonda!$B$BTengo todo lo necesario para cocinar una tanda. Todo... ¡excepto las alas de águila ratonera!$B$BSi quieres algunas, tráemelas. Consígueme suficientes alas de águila ratonera ¡y hasta podría compartir la receta contigo!",
    ["O"] = "Llévale 4 alas de águila ratonera a Aparejez.",
    ["T"] = "Alas de águila ratonera a la parrilla",
  },
  [704] = {
    ["D"] = "El último informe de Agmond es preocupante, ¡sobre todo ahora que ha muerto! Mencionaba que en sus excavaciones había desenterrado unas \"urnas antiguas\" en la explotación situada en las afueras de Uldaman. Me temo que las urnas que encontró instigaron a los troggs, igual que lo hicieron los ídolos que encontré aquí.$B$BNecesito que recuperes esas urnas para nuestros estudios. Ve a la excavación de Uldaman, encuentra las urnas y tráemelas aquí.",
    ["O"] = "Llévale 4 urnas de piedra labrada al prospector Vetaferro en Loch Modan.",
    ["T"] = "El sino de Agmond",
  },
  [705] = {
    ["D"] = "Las Tierras Inhóspitas son un lugar hostil, lleno de sanguinarios depredadores y de carroñeros sin escrúpulos. Aterrador, sobre todo para un diminuto goblin. Para sobrevivir, ¡tengo que ser astuto!$B$BConozco la receta de las bombas centelleantes. Las uso para espantar a los animales. Pero se me está acabando uno de los ingredientes: polvo de perla azul triturada. Consígueme un puñado y no te arrepentirás.$B$BEh, y espero que lleves puestas unas buenas botas. Las perlas azules que necesito se encuentran en unas almejas de El Arrecife Mortal.$B$BEse mismo, ¡El Arrecife Mortal de Tuercespina!",
    ["O"] = "Llévale 9 perlas azules a Aparejez en las Tierras Inhóspitas.",
    ["T"] = "La pesca de perlas",
  },
  [706] = {
    ["D"] = "Cuando era aprendiz de Feranor Ferrodedo, aprendí su técnica especial para imbuir armas con ardientes encantamientos. Lo último que supe de él es que estaba trabajando en una herrería para una partida de cazadores en Lordaeron.$B$BNo puedo contarte demasiados detalles del proceso pero sí puedo decirte que requiere sangre del corazón de un cachorro de dragón negro que aún esté latiendo. No estoy demasiado ocupado ahora, espero instrucciones de Forjaz. Si me traes un corazón de los cachorros del Barranco Lethlor, ¡te demostraré de lo que soy capaz!",
    ["O"] = "Consigue 1 corazón de dragón negro para Sigrun Extracero.",
    ["T"] = "Encantamientos de fuego abrasador",
  },
  [707] = {
    ["D"] = "$N, he recibido unos informes del prospector Vetaferro sobre la excavación de Loch Modan; dice que necesita a alguien que estudie otra excavación. $b$No dice nada más, pero si te apetece estirar las piernas, ve a hablar con Vetaferro.",
    ["O"] = "Habla con el prospector Vetaferro en su excavación de Loch Modan.",
    ["T"] = "¡Vetaferro te necesita!",
  },
  [708] = {
    ["D"] = "La superficie de esta caja negra metálica está muy hundida, como si la hubiesen sumergido en ácido, debe de haber pasado una buena temporada en el estómago del cóndor gigante.$B$BAl girar la caja, se ve un símbolo estampado en la parte inferior. Debajo del símbolo hay inscritas unas palabras:$B$B\"Motor n.º 19 - Brigada de Asedio de Forjaz\".",
    ["O"] = "Lleva la caja negra corroída a la Sala de Armas de Forjaz.",
    ["T"] = "La caja negra",
  },
  [709] = {
    ["D"] = "¡Ay, ay! ¡Ay, ay! Se acerca una catástrofe. ¡Recuerda mis palabras! ¡Una catástrofe!$B$BBNo, no puedo hablar de ello… no puedo pronunciar sus nombres, ¡podrían oírme! Pero si aprendemos de ellos... Sí, si aprendemos más... ¡quizás logremos frustrar sus planes!$B$BBQuizás.$B$BVe a Uldaman. ¡No! ¡Ya te lo dije! Tienes que encontrar... encontrar la tablilla de... Ryun’eh. ¡Sí, encuéntrala! ¡Y no me hagas pronunciar ese nombre otra vez!$B$BBSe encuentra en las profundidades de ese lugar, en un antiguo cofre. Ahí es donde se esconde... en un cofre creado antes del tiempo de los enanos.",
    ["O"] = "Llévale la tablilla de Ryun\'eh a Theldurin el Perdido.",
    ["T"] = "La solución a la maldición",
  },
  [710] = {
    ["D"] = "Aquí en las Tierras Inhóspitas, he dejado de trabajar en mi más reciente proyecto: mis grilletes de esclavitud elemental.$B$BCuando acabe, permitirán a los magos conjurar a elementales aún más poderosos para fines de más alcance. Para empezar, necesito pequeños fragmentos de piedra para probar mis más recientes hechizos ofensivos y unos aparatitos que he compuesto.$B$BEs fácil reunir tales fragmentos de los elementales de piedra inferiores hacia el oeste de este lugar. ¿Podrías traerme algunos?",
    ["O"] = "Encuentra 10 fragmentos de piedra pequeños y llévaselos a Lotwil Veriatus en las Tierras Inhóspitas.",
    ["T"] = "Estudio de los elementos: roca",
  },
  [711] = {
    ["D"] = "$n, has demostrado ya más valía que todos mis aprendices juntos.$B$BLas siguientes piezas que necesito requerirán algo más de fuerza. Si tienes cuidado, encontrarás grandes losas de piedra en los elementales de piedra. Sus cuerpos son más resistentes y voluminosos, así que tendrás más probabilidades de poder extraer piezas lo suficientemente grandes para mis pruebas. Los hallarás más hacia el oeste desde aquí o al sureste bordeando las faldas de las montañas.$B$BTómate tu tiempo; estaré ocupado probando estos fragmentos más pequeños que me has traído.",
    ["O"] = "Llévale 3 losas de piedra grandes a Lotwil Veriatus en las Tierras Inhóspitas.",
    ["T"] = "Estudio de los elementos: roca",
  },
  [712] = {
    ["D"] = "$N, necesito una pieza más de los elementales de las Tierras Infames: los brazales de roca de los grandes elementales de piedra. Según mis cálculos, esos brazales deberían bastar para medir la fuerza de mis grilletes.$b$bMis intentos de visualizar a los grandes elementales de piedra me mostraron un grupo de ellos al suroeste de aquí, cerca de unos ogros, si no me equivoco.$b$bTráeme 5 de sus brazales. Así tendré reservas. Servo es famoso por su facilidad para perder mis reactivos e investigaciones.",
    ["O"] = "Llévale 5 brazales de vínculo de roca a Lotwil Veriatus en las Tierras Inhóspitas.",
    ["T"] = "Estudio de los elementos: roca",
  },
  [713] = {
    ["D"] = "Déjame decirte algo sobre el aceite de escarcha: nada impide que un motor o un engranaje se recalienten como ese aceite.$B$BSus propiedades de enfriamiento son extraordinarias y su longevidad como lubricante no tiene parangón. Y eso no es todo: también ayuda a retener los encantamientos de los objetos mágicos.$B$BNecesito un frasco de este aceite para aumentar la capacidad mágica de mis grilletes y hacerlos lo suficientemente poderosos para impedir que nuestros amigos conjurados nos maten a todos.$B$BBro\'kin, un amigo goblin, te enseñará a confeccionarlo. Se encuentra en Alterac.",
    ["O"] = "Encuentra aceite de Escarcha y llévaselo a Lotwil Veriatus en las Tierras Inhóspitas.",
    ["T"] = "Mantener la cabeza fría",
  },
  [714] = {
    ["D"] = "$N, has hecho un trabajo más allá de toda expectativa.$B$B<Lotwil se ve claramente centrado en su trabajo.>$B$BSi por ventura nos volvemos a encontrar en Kharanos, pasa cuando quieras por trabajo.$B$B¡Vaya! Rápido, agarra el... sí, eso. Gracias.$B$BBien, ahora solo necesito una cosa más. Es una pieza pequeña pero me temo que bastante crucial: un girocronátomo.$B$BSi pudieras hacerte con uno y traérmelo, estaré listo para completar esto.",
    ["O"] = "Llévale 1 girocronátomo a Lotwil Veriatus en las Tierras Inhóspitas.",
    ["T"] = "Un giro... ¿Qué?",
  },
  [715] = {
    ["D"] = "Creo que esos objetos que conseguiste para Lotwil van a ser la menor de nuestras preocupaciones, $n. Por otro lado, has demostrados ser más que hábil en las ciencias que Lotwil considera sagradas, así que ¿te apetece ayudarme?$B$BNecesito una poción de invisibilidad y una poción curativa; ambas podrían venir bien muy pronto si le sale esto.$B$BA cambio, te daré esta receta para una poción en la que he estado trabajando mientras aprendías cosas de Lotwil. Se basa en parte de la investigación que ha realizado aquí en las Tierras Inhóspitas.",
    ["O"] = "Llévale 1 poción de curación y 1 poción inferior de invisibilidad inferior a Lucien Tosselramienta en las Tierras Inhóspitas.",
    ["T"] = "Piedra líquida",
  },
  [716] = {
    ["D"] = "Sé lo agarrado que Lotwil puede llegar a ser, $N, así que permite que te haga algo por las molestias. A lo mejor hasta te protege de la próxima atrocidad que será el más reciente invento de Lotwil.$B$BHe estado trabajando con todas las piedras sobrantes que habías recolectado para él y puedo hacerte unos buenos brazales que podrían serte de utilidad. Tienen un encantamiento, claro.$B$BSolo necesito brazales de bronce con dibujos confeccionados por un herrero para poder fabricar los brazales que te estoy haciendo.",
    ["O"] = "Llévale unos brazales de bronce grabados a Lucien Tosselramienta en las Tierras Inhóspitas.",
    ["T"] = "Es mejor la piedra que la tela",
  },
  [717] = {
    ["D"] = "Ha quedado claro que no podemos garantizar que Cinchoscura y Hematus permanezcan presos en Lethlor. Que esos dragones negros se han adueñaran del lugar y que los ogros consiguieran robar el Signo indica que se deben tomar medidas más drásticas.$B$BLa prisión de los dragones está controlada por tres piedras rúnicas que solo se pueden extraer con el Signo de la Tierra. Esas piedras rúnicas pueden entonces usarse para romper el sello que los mantiene presos.$B$B$n,... asegúrate de que su sueño es eterno.",
    ["O"] = "Usa el Signo de la Tierra para activar el Pilar de Amatista, el de Ópalo y el de Diamante y así obtener las piedras rúnicas.$B$BColoca las piedras rúnicas en el Sello de la Tierra para liberar a Cinchoscura y a Hematus.$B$BMátalos y llévale a Garek las ataduras de Cinchoscura, las cadenas de Hematus y el Signo de la Tierra.",
    ["T"] = "Terremotos",
  },
  [718] = {
    ["D"] = "Con las prisas por marcharnos de la excavación de Uldaman, nos obligaron a abandonar muchos carros y muchas cajas de suministros por el camino. Los ogros locales del Asentamiento Kosh se han lanzado sobre nuestro equipo abandonado como buitres sobre un cadáver en descomposición. No huele mucho mejor tampoco. Me refiero a los cadáveres.$b$bSea como fuere, se llevaron el carro en el que se transportaban todas nuestras armas y tal y tal…, bueno, esto es un poco violento, no hemos podido conseguir nada de comida.$b$bEstamos metidos en un lío, ¿nos podrías ayudar?",
    ["O"] = "Recupera el cajón de provisiones para Sigrun Extracero.",
    ["T"] = "Espejismos",
  },
  [719] = {
    ["D"] = "Hola, $N. Ten cuidado por estos lares. Hay enanos Hierro Negro por to’as partes, parecía clan Forjatiniebla.$B$BAcaban d’atacar la excavación donde trabajaba, s’han cargao a casi todos, hasta a mi jefe Piemartillo. M’escapé por los pelos.$B$BEl sitio está justo al norte d’este lugar y ‘stoy intentando planear la forma de recuperar parte de nuestros suministros, sobre tó’ mi pico de la suerte.$B$B¿T\'apetecería echarme una mano? ¡Uno d’esos malditos Hierro Negro tendrá mi pico! Podría ser cualquiera.",
    ["O"] = "Encuentra el pico de la suerte de Ryedol y llévaselo al prospector Ryedol al sur de la excavación Piemartillo.",
    ["T"] = "Un enanos y sus herramientas",
  },
  [720] = {
    ["D"] = "<Hay texto garabateado de forma tosca con tiza en el mapa.>$B$B\"Ryedol, $BMe llevan a Uldaman. Envía ayuda.$B-Piemartillo\"",
    ["O"] = "Localiza al prospector Ryedol y dile que Grez Dedomaza está vivo.",
    ["T"] = "Un signo de esperanza",
  },
  [721] = {
    ["D"] = "$N, ¿puedes encontrarlo? ¿Puedes encontrar a Piemartillo en Uldaman?$B$BSi esos asquerosos Forjatiniebla l’han raptao pa’ sonsacarle lo que sabe de las excavaciones de los titanes, entonces todo Forjaz podría ‘star en gran peligro.$B$BDurante mucho tiempo esos Hierro Negro han buscao magia pa’ hacer golems más poderosos. Piemartillo ‘staba haciendo lo mismo, claro, pero pa’ detenerlos.$B$BProbablemente lo mantengan vivo hasta que s’enteren de lo que sabe. ¡‘spabila si quieres salvarlo!",
    ["O"] = "Encuentra a Grez Dedomaza en Uldaman.",
    ["T"] = "Un signo de esperanza",
  },
  [722] = {
    ["D"] = "Eres valiente... <tose>... por venir a Uldaman pa’ mí, $c.$B$BSi realmente ‘stás aquí pa’ ayudar... al viejo Piemartillo, entonces... <tose>... tendrás qu’encontrar... mi amuleto.$B$BSombraprofunda lo tiene... Magregan Sombraprofunda. ‘stá por aquí... en las ruinas, seguro. Ese amuleto los conducirá... <tose>... derechito a los pergaminos si no lo paran. Depende de ti, $N. Favor... recupera el amuleto.$B$BCon los pergaminos los Hierro Negro conseguirán crear golems aún más poderosos. Lo sé. Me lo dicen los huesos.",
    ["O"] = "Encuentra el amuleto de Piemartillo y llévaselo a Uldaman.",
    ["T"] = "El amuleto de los secretos",
  },
  [723] = {
    ["D"] = "<Piemartillo se sujeta las heridas y se toma un momento para recuperarse antes de seguir hablando.>$B$BEncuentra a Ryedol, $N. Llévale mi amuleto. <Tose> Él… <tose> saber qué hacer. Sabe qu’estaba buscando los orígenes de nuestro pueblo, y él <tose> sabe que mi misión era detener al clan Forjatiniebla. Hay qu’impedir qu’encuentren esos pergaminos... <tose>",
    ["O"] = "Llévale el amuleto de Piemartillo al prospector Ryedol en las Tierras Inhóspitas.",
    ["T"] = "Tener fe en el porvenir",
  },
  [724] = {
    ["D"] = "Vale, vale... ¿quién sabrá qué hacer?$B$B¡El historiador Karnik! ¡Eso es!$B$BSe encuentra en Forjaz. Podrá ayudarnos. Él y Piemartillo estaban trabajando juntos para encontrar los artefactos en Uldaman.$B$BSupongo que los Forjatiniebla se enteraron de que los dos sabían más de lo dejaban ver.$B$BDile a Karnik lo que le ha ocurrido a Piemartillo y dale el amuleto, él te dirá qué hacer después.",
    ["O"] = "Llévale el amuleto de Piemartillo a Karnik el Historiador en Forjaz.",
    ["T"] = "Tener fe en el porvenir",
  },
  [725] = {
    ["D"] = "$N, corre y encuentra al consejero Belgrum en cuanto puedas. Cuéntale lo que has presenciado y pregúntale qué quiere que hagamos a continuación. En esta nota encontrará más detalles.$B$BMientras tanto, dedicaré un tiempo a hablar con el espíritu de Piemartillo. A lo mejor sabe algo que nos ayudará a detener las actividades de esos cretinos Forjatiniebla en Tierras Inhóspitas. Solo nos queda rezar.",
    ["O"] = "Encuentra al consejero Belgrum y dale su nota.",
    ["T"] = "Avertir de la amenaza",
  },
  [726] = {
    ["D"] = "<El consejero Belgrum acaba de leer la nota, los ojos se le abren aún más por un momento.>$B$BVaya unas malas noticias.$B$B$N, regresa con el historiador Karnik y dile que por la autoridad del rey actúe pronto y de forma contundente en base a esta información. Tiene que detener las actividades de los Forjatiniebla en Tierras Inhóspitas.",
    ["O"] = "Habla con Karnik el Historiador.",
    ["T"] = "Avertir de la amenaza",
  },
  [727] = {
    ["D"] = "La tablilla es muy reveladora. ¡Aunque no lo suficiente! ¡Ni de lejos!$B$BHay un cuaderno, una revista que Yagyin escribió en otros tiempos. La... gente de los que huí... guardan este cuaderno en muchos lugares. Y sus más eruditos lo estudian. El conocimiento en él contenido es muy vasto. ¡Conocimiento para protegernos!$B$BVe a ver a Gerrig Bonegrip. Se encuentra en una tienda de Forjaz, en La Caverna Abandonada. Muéstrale este sigilo y creerá que eres de los suyos. Pregúntale por la revista de Yagyin... ¡y tráemela!",
    ["O"] = "Dale el sigilo del Martillo a Gerrig Agarrahueso en La Caverna Abandonada de Forjaz.",
    ["T"] = "Ir a Forjaz a buscar el \"Compendio\" de Yagyin",
  },
  [728] = {
    ["D"] = "La tablilla es muy reveladora. ¡Aunque no lo suficiente! ¡Ni de lejos!$B$BHay un cuaderno, una revista que Yagyin escribió en otros tiempos. La... gente de los que huí... guardan este cuaderno en muchos lugares. Y sus más eruditos lo estudian. El conocimiento en él contenido es muy vasto. ¡Conocimiento para protegernos!$B$BVe a ver al guardián Bel\'dugur en la ciudad de Lordaeron en Oxis. Muéstrale este sigilo y creerá que eres de los suyos. Pregúntale por la revista de Yagyin... ¡y tráemela!",
    ["O"] = "Llévale el sigilo del martillo al guardián Bel\'dugur en Entrañas.",
    ["T"] = "Ir a Entrañas a buscar el \"Compendio\" de Yagyin",
  },
  [729] = {
    ["D"] = "Fue un honor que el maestro Mostachogris me destinara a trabajar bajo la guía del gran prospector Remtravel. En la academia de Forjaz todo el mundo sabía de los grandes descubrimientos de Remtravel.$B$BPero el prospector es un poco... mm... despistado... Vive en su mundo.$B$BPor ejemplo: habíamos descubierto pruebas de la existencia de una gran sociedad. Y un día, unos terribles golems surgieron de la tierra e invadieron la excavación. Y Remtravel ni se dio cuenta. Yo corrí a Auberdine a pedir ayuda.$B$B¡Te lo ruego, viaja hacia el sur y asegúrate de que el prospector está bien!",
    ["O"] = "Viaja al sur y asegúrate de que el prospector Tripirrem está bien.",
    ["T"] = "El prospector despistado",
  },
  [730] = {
    ["D"] = "Me agrada ver a un $c mostrando interés por las grandes maravillas arqueológicas de nuestro mundo.$B$BA menudo nuestro trabajo es considerado poco más que una afición por nuestros amigos de la Alianza. Pero es porque no se dan cuenta de que son los recientes descubrimientos de Khaz Modan los que han probado que una poderosa fuerza amenaza todo Azeroth, de Lordaeron a Kalimdor.$B$BEmpiezo a preocuparme de verdad por el personal que envié a Costa Oscura. Hace semanas que no tengo noticias suyas.$B$BViaja a Auberdine y busca pistas de su paradero.",
    ["O"] = "Viaja a Auberdine y busca rastros del equipo de excavación enano.",
    ["T"] = "¿Problemas en Costa Oscura?",
  },
  [731] = {
    ["D"] = "Dime, Hollee... ah, no, que no eres Hollee. ¿Dónde está Hollee?$B$BDeja, total, no es importante. Tenemos que encontrar ese cepillo de piedra... Quiero decir, el fósil misterioso. Tenemos que encontrar ese fósil. La Liga de Exploradores de Darnassus querrá algún informe de lo que he estado haciendo.$B$BAvísame cuando estés $glisto:lista;. Iremos a la caza de ese cepillo. Quiero decir, ¡del fósil! Así encontraremos algo palpable para satisfacer a la Liga.$B$B¿Estás $glisto:lista;?",
    ["O"] = "Protege al prospector Tripirrem mientras busca el fósil misterioso. Luego vuelve a Auberdine y busca al arqueólogo Hollee.",
    ["T"] = "El prospector despistado",
  },
  [732] = {
    ["D"] = "Sigrun me ha dicho que habías sido de gran ayuda para él. Quizás te reclute para ayudarme. Mi maestro, Krasus del Kirin Tor, me envió aquí para poner fin a los disturbios que se estaban produciendo en Barranco Lethlor.$B$BNo puedo revelarte la naturaleza exacta de mi misión aquí ni de las tareas que debo acometer pero ten por seguro que son de vital importancia.$B$BUn grupo de ogros errantes, avistados en ocasiones cerca del Asentamiento Kosh, robaron un objeto llamado el Signo de la Tierra; debo recuperarlo. Tráemelo y te contaré más detalles.",
    ["O"] = "Recupera el Signo de la Tierra para Garek.",
    ["T"] = "Terremotos",
  },
  [733] = {
    ["D"] = "La cosa se va a poner fea aquí si no logro gorronear algunos materiales para construir unas defensas y otras necesidades. Me he dado cuenta de que los ogros de la zona parecen estar bien surtidos de chatarra. A lo mejor las historias de máquinas de asalto en las Tierras Inhóspitas no estaban muy alejadas de la realidad después de todo...$B$BEn cualquier caso, el prospector lleva un tiempo preocupado por el estado de nuestro campamento, así que debería ponerme manos a la obra pronto si es posible.$B$BEl principal campamento de ogros está en la Gruta Rotapolvo, una buena caminata hacia el oeste desde aquí.",
    ["O"] = "Consigue 7 piezas de restos de armadura para Sigrun.",
    ["T"] = "¡A gorronear!",
  },
  [734] = {
    ["D"] = "¡Pero qué maravilla!$B$B<Lotwil te dedica una mirada breve antes de centrarse de nuevo en sus grilletes.$B$BVe a hablar con Lucien, $n. Necesito un piñón del n.º 5. ¿Me lo traerás?",
    ["O"] = "Habla con Lucien Tosselramienta en las Tierras Inhóspitas.",
    ["T"] = "Lo tienes crudo",
  },
  [735] = {
    ["D"] = "El Compendio de Yagyin, ¿eh? ¿Se ha empezado a escribir otro capítulo sobre nosotros? Disculpa, no debería haber preguntado.$B$BTe ayudaré porque tengo dos ejemplares. Hay que ayudarse mutuamente, ¿eh? Así es como se prospera.$B$BQuiero tres objetos de poder. Tráemelos y te daré el Compendio.$B$BNo voy a decir los nombres en alto, nunca se sabe quién está escuchando. Te los voy a escribir.",
    ["O"] = "Devuelve los objetos de la lista a Gerrig Agarrahueso a Forjaz.",
    ["T"] = "La Estrella, la Mano y el Corazón",
  },
  [736] = {
    ["D"] = "¿Necesitas el Compendio de Yagyin? Así que, ¿tu plan es reunir a otro grupo? Claro que sí... y por supuesto te ayudaré, $ghermano:hermana;.$B$BPero tendrás que darme algo a cambio.$B$BNecesito tres objetos de poder. Tráemelos y el compendio será tuyo.$B$BNo puedo decir en alto sus nombres así que te los escribiré.$B$BSi me consigues los objetos de esta lista, serás verdaderamente $gmerecedor:merecedora; de crear tu propia sección dentro de nuestra orden.",
    ["O"] = "Llévale los objetos que aparecen en la nota de Bel\'dugur al vigilante Bel\'dugur de Entrañas.",
    ["T"] = "La Estrella, la Mano y el Corazón",
  },
  [737] = {
    ["D"] = "Aquí tienes la revista, $n. Huelga decir que debes extremar el cuidado con ella. Además de mantenerla alejada de manos enemigas.$B$BEste cuaderno contiene muchas verdades...$B$B... Y la verdad puede encender chispas.",
    ["O"] = "Llévale el compendio de Yagyin a Theldurin.",
    ["T"] = "El saber prohibido",
  },
  [738] = {
    ["D"] = "El prospector Agmond estaba a cargo de nuestra excavación en las Tierras Inhóspitas, hacia el suroeste. Y digo \"estaba\" porque... bueno, hace semanas que no sé nada de él. Necesito que encuentres a Agmond para poder conseguir su informe.$B$BLa última vez que oímos algo de Agmond se encontraba en la más meridional de nuestras excavaciones en las Tierras Inhóspitas, una pequeña explotación más allá de la Fortaleza de Angor. Yo empezaría a buscarlo allí.",
    ["O"] = "Encuentra a Agmond.",
    ["T"] = "Encontrar a Agmond",
  },
  [739] = {
    ["D"] = "La pista de Agmond arranca en el oeste. Es fácil saber por las manchas de sangre en las rocas y en la arena que el enano se arrastró cierta distancia.$B$BY murió aquí.$B$BTras una inspección más detenida, se descubre la palabra \"Murdaloc\" toscamente garabateada en un pedazo de arenisca de las Tierras Inhóspitas.$B$BGarabateada junto a la inerte mano de Agmond.",
    ["O"] = "Mata al asesino de Agmond, Murdaloc.$B$BMata a 12 partehuesos Petracripta.$B$BPreséntate ante el prospector Vetaferro en Loch Modan.",
    ["T"] = "Murdaloc",
  },
  [741] = {
    ["D"] = "¿Así que el prospector quiere mandar el misterioso fósil a la Liga de Exploradores de Darnassus? No me gusta la idea de irme de Costa Oscura sin él.$B$BTen, $n, ve a Darnassus y entrega el misterioso fósil al arqueólogo jefe Mostachogris.",
    ["O"] = "Ve a Darnassus y entrega el fósil misterioso al arqueólogo jefe Mostachogris.",
    ["T"] = "El prospector despistado",
  },
  [742] = {
    ["D"] = "¡Atención, jóvenes aventureros! ¡Los páramos de Vallefresno os esperan!$B$BLa Horda ha aumentado su presencia en las tierras al norte de Los Baldíos. Nuestras dos avanzadas, Puesto del Hachazo y La Ensenada de Zoram, se esfuerzan por traer gloria a la Horda. Aquellos que deseen demostrar su valía deben buscar ayuda allí. Nota: Senani Truenozón del Puesto del Hachazo, al norte de Los Baldíos, busca a aventureros que deseen participar en la gran Caza de Vallefresno.",
    ["O"] = "Habla con Senani Truenozón en Puesto del Hachazo, en Vallefresno.",
    ["T"] = "La Caza de Vallefresno",
  },
  [743] = {
    ["D"] = "Te agradezco que te pares a hablar conmigo, $n. Me llamo Ruul y soy guerrero y maestro.$B$BHas alcanzado la edad en la que debes prepararte para las pruebas que te esperan si quieres ir más allá de Mulgore.$B$BPara poner a prueba tu fuerza, ve al sureste a enfrentarte a las arpías Viento Furioso. Sus nidos están en los bordes de la montaña, alejados del camino.$B$BSon enemigas naturales de Mulgore y serán un buen indicativo de tu habilidad.",
    ["O"] = "Lleva 8 espolones de Viento Furioso a Ruul Garraguilar al Poblado Pezuña de Sangre.",
    ["T"] = "Las Viento Furioso son un peligro",
  },
  [744] = {
    ["D"] = "$n, mi hermano pronto se personará ante el jefe Pezuña de Sangre y será un honor para mí prepararle su penacho.$B$BMe gustaría pedirte un favor mientras acabo de curtir estas cintas de piel. No he podido reunir todas las plumas que necesito y me preguntaba si tú podrías ayudarme a conseguir más.$B$BPuedes obtener las plumas del tamaño adecuado de las arpías que habitan al norte y noroeste de Cima del Trueno. Solo necesito 6 plumas azur y 6 de bronce para completar mi diseño.",
    ["O"] = "Consigue 6 plumas azur y 6 de bronce, y llévaselas a Eyahn Garraguilar a Cima del Trueno.",
    ["T"] = "Preparación para la ceremonia",
  },
  [745] = {
    ["D"] = "$N, esta tierra está herida por muchos conflictos. Espero que no tengas que presenciar tantos como yo en mi vida. Sin embargo, te pido que vayas a uno. Verás lo peligrosa que puede ser cualquier amenaza, por pequeña que sea, si no se ataja de inmediato.$b$bUnos gnolls Crines Pálidos se han instalado al sur de Pezuña de Sangre y en una cueva del oeste. No respetan nuestros intentos de establecer comunicación y matan a placer por todo Mulgore.$b$bLas palabras ya no sirven de nada.",
    ["O"] = "Mata a 10 peleteros, 8 desolladores y 5 furtivos Crines Pálidos y vuelve al Poblado Pezuña de Sangre para hablar con Baine Pezuña de Sangre.",
    ["T"] = "Compartir la tierra",
  },
  [746] = {
    ["D"] = "Al noroeste hay enanos preparando una excavación. Creen que la tierra está llena de secretos y eso es cierto, pero perforarla no es la mejor manera de descubrirlos.$B$BEncuentra las herramientas de los enanos, rómpelas y tráeme los pedazos.$B$BNecesitarás una forja para romperlas. Hay una en Cima del Trueno, pero te servirá cualquiera, incluso la del campamento enano.",
    ["O"] = "Recoge 5 picos de prospector. $b$bBusca una forja para destrozar los picos. $b$bLleva 5 trozos de herramienta rota a Baine Pezuña de Sangre al Poblado Pezuña de Sangre.",
    ["T"] = "Una excavación de los enanos",
  },
  [747] = {
    ["D"] = "Tienes un aire prometedor, demostrarás tu valía ante la tribu. Puede que pronto te acojan en la gran ciudad de Cima del Trueno. Pero antes de eso, tendrás que mostrar tu valía ante mi padre, el Jefe Vientaguilar.$b$bAquí en Mesa de la Nube Roja estamos orgullosos de nuestra habilidad para la caza. Los tauren cazamos por necesidad y por deporte. Nos estamos quedando sin carne y necesitamsos plumas para la ropa. Caza zancudos de llanura y demuestra tu valor mientras reabasteces al poblado.",
    ["O"] = "Grull Alalcón quiere que le lleves 7 plumas y 7 piezas de carne de zancudo de llanura al Campamento Narache.",
    ["T"] = "Empieza la caza",
  },
  [748] = {
    ["D"] = "¡Los goblins han mancillado nuestros pozos de agua sagrada! No podemos permitirlo.$b$bPara limpiarlos necesito un tótem purificador; tú lo llevarás al pozo y realizarás el ritual de limpieza.$b$bEl primer tótem es para el Pozo de agua Pezuña Invernal. Necesitamos la ayuda de la tierra. Tráeme zarpas de lobo de pradera y espolones de zancudo de llanura adulto. Los encontrarás al suroeste.$b$bSé prudente, $N.",
    ["O"] = "Lleva 6 zarpas de lobo de pradera y 4 espolones de zancudo de llanura a Mull Astatrueno al Poblado Pezuña de Sangre.",
    ["T"] = "Pozos envenenados",
  },
  [749] = {
    ["D"] = "Hace unos días nos encontramos una caravana escoltada de Ventura y Cía. en la costa norte del Lago Toro de Piedra. En un combate anterior habíamos perdido a dos de los nuestros, así que atacamos la caravana.$B$BDejamos las cajas porque lo incendiamos todo, pero hemos visto a rescatadores de Ventura y Cía. cerca de las carretas quemadas; quizás quedó algo de valor.$B$BPodrías acercarte a ver qué hay en las cajas.",
    ["O"] = "Morin Acechanubes quiere que registres las cajas de la caravana quemada.",
    ["T"] = "La Caravana Devastada",
  },
  [750] = {
    ["D"] = "Un buen cazador tauren sabe que la presa no es un mero trofeo. Los animales de la llanura son nuestro medio de subsistencia. Si quieres impresionar a los ancestros, trae algunas pieles de puma montés; son muy valiosas. Los pumas viven en esas colinas hacia el sur.$b$bLos niños necesitan ropa y tenemos que reparar las carpas.",
    ["O"] = "Grull Alalcón quiere que le lleves 10 pieles de puma montés al Campamento Narache.",
    ["T"] = "La caza sigue",
  },
  [751] = {
    ["D"] = "Al abrir la caja ves que está llena de mineral, otra prueba de las operaciones mineras de Ventura y Cía. en Mulgore. Coges el paquete de documentos que hay encima del mineral.$B$BEn las laderas del este se oyen ruidos de actividad industrial y se ve una columna de humo.",
    ["O"] = "Lleva los documentos de Ventura y Cía. a Morin Acechanubes cerca del Poblado Pezuña de Sangre.",
    ["T"] = "La Caravana Devastada",
  },
  [752] = {
    ["D"] = "Todos los miembros de la tribu contribuyen a la armonía de la vida. Vivimos juntos y trabajamos juntos. $b$bCada uno de nosotros es responsable del bienestar de los demás. Quisiera pedirte un favor. $b$bMi madre se fue esta mañana a coger agua del pozo que hay al sureste de Narache. Empiezo a inquietarme; ¿podrías ir a buscarla mientras yo atiendo los asuntos de la tribu?",
    ["O"] = "El  Jefe Vientaguilar quiere que vayas a buscar a su madre cerca del pozo que hay al sureste del Campamento Narache.",
    ["T"] = "Una humilde contribución",
  },
  [753] = {
    ["D"] = "He recorrido muchos caminos en la vida, mis piernas ya no son fuertes. Todavía puedo ser útil para la tribu, es solo que tardo un poco más en hacer las cosas. $b$bPero tú rebosas vitalidad, $c. Vamos a ponerla a prueba; llena un cántaro en el pozo y llévaselo a mi hijo, el jefe del Campamento Narache. $b$bRecuerda que incluso el más insignificante servicio puede valerte el respeto de los ancestros.",
    ["O"] = "Llena un cántaro de agua en el pozo. $b$bLlévaselo al Jefe Vientaguilar al Campamento Narache, que está al noroeste del pozo.",
    ["T"] = "Una humilde contribución",
  },
  [754] = {
    ["D"] = "Este es el tótem para el Pozo de agua Pezuña Invernal. Llévalo al pozo y lleva a cabo el ritual de limpieza. El pozo está hacia el sureste y en poder de los repugnantes goblins.$b$bEs una misión difícil, pero no podemos perder el acceso a los pozos.$b$bSuerte, $N.",
    ["O"] = "Usa el tótem purificador en el Pozo de agua Pezuña Invernal y ve a ver a Mull Astatrueno.",
    ["T"] = "La purificación de Pezuña Invernal",
  },
  [755] = {
    ["D"] = "Tu buena disposición y entusiasmo por ayudar a los tauren de Narache te ennoblecen, $n. Creo que un día se te aclamará en Cima del Trueno con grandes honores como $c. $b$bPero antes de eso, debes cumplir con los tres Ritos de la Madre Tierra. $b$bLa primera prueba es el Rito de la Fuerza. Ve a ver al profeta Lenguagris y dile que vas de parte del Jefe Vientaguilar. $b$bSu morada está al sur del campamento, escondida entre las colinas.",
    ["O"] = "Ve a ver al profeta Lenguagris, que vive en las colinas al sur del Campamento Narache.",
    ["T"] = "Ritos de la Madre Tierra",
  },
  [756] = {
    ["D"] = "Para crear el tótem para el Pozo Tronacuerno necesitamos las garras de los depredadores de Mulgore. Tráeme garras de acechador de pradera y puma de las estepas.$B$BLos encontrarás tanto hacia el este como hacia el oeste.",
    ["O"] = "Lleva 6 garras de acechador y 6 garras de puma a Mull Astatrueno.",
    ["T"] = "El tótem de Tronacuerno",
  },
  [757] = {
    ["D"] = "Los Ritos de la Madre Tierra son los pasos con los que un joven tauren se gana el respeto de Cima del Trueno.$b$bEl primero que debes superar es el Rito de la Fuerza, con el que pruebas tu valor al matar enemigos de la tribu.$b$bLos Erizapúas de Barranco Cortazarza se están instalando en nuestro terreno de caza tribal. Atacan por emboscada a nuestros cazadores y roban en el poblado durante la noche.$b$bPara demostrar tu valor, acaba con esos villanos y entrega sus cinturones al jefe del Campamento Narache como prueba.",
    ["O"] = "Mata Erizapúas en Barranco Cortazarza y lleva 12 de sus cinturones al Jefe Vientaguilar al Campamento Narache.",
    ["T"] = "Rito de la fuerza",
  },
  [758] = {
    ["D"] = "$n, el tótem ya está listo. Debes llevar a cabo el ritual una vez más.$B$BLlévate el tótem al Pozo Tronacuerno, que está al norte. Vuelve aquí cuando termines.$B$BQue los espíritus te acompañen.",
    ["O"] = "Usa el tótem purificador en el Pozo Tronacuerno y ve a ver a Mull Astatrueno.",
    ["T"] = "La purificación de Tronacuerno",
  },
  [759] = {
    ["D"] = "Ya solo queda por limpiar el Pozo Bravacrín. Para ello, la tierra debe ofrecernos los colmillos de un depredador feroz, el lobo alfa de pradera. Los encontrarás al norte. Vuelve cuando hayas conseguido los colmillos.$b$bLos alfa suelen deambular alrededor de la ciudad.",
    ["O"] = "Lleva 8 dientes de alfa de pradera a Mull Astatrueno al Poblado Pezuña de Sangre.",
    ["T"] = "El tótem de Bravacrín",
  },
  [760] = {
    ["D"] = "Los espíritus están complacidos contigo por la limpieza de los pozos de Tronacuerno y Pezuña de Invierno, $N. El tótem de Bravacrín ya está listo; te espera tu última tarea.$b$bEl pozo está al norte de Cima del Trueno. Lleva a cabo el ritual para limpiar la tierra de la inmundicia goblin. Que las aguas vuelvan a fluir puras y claras.",
    ["O"] = "Usa el tótem purificador en el Pozo Bravacrín y ve a ver a Mull Astatrueno.",
    ["T"] = "La purificación de Bravacrín",
  },
  [761] = {
    ["D"] = "El aguilón es un ave muy astuta, difícil de encontrar y de cazar. Un trofeo de plumas de aguilón sería una prueba de tu talento cazador.$B$BVe a las llanuras y caza uno.$B$BTráeme las plumas y gana honor para tu clan.$B$BEstán por todo Mulgore, pero tendrás que estar ojo avizor para verlos y muy veloz para cazarlos.",
    ["O"] = "Lleva 8 plumas trofeo de aguilón a Harken Vientótem al Poblado Pezuña de Sangre.",
    ["T"] = "La caza de los aguilones",
  },
  [762] = {
    ["D"] = "El propio Ragnaros envió al embajador Infernus a la Fortaleza de Angor para controlar a los Forjatiniebla en su búsqueda de artefactos perdidos en Uldaman.$B$BHa supuesto ya gran número de bajas entre nuestra gente pero, a todas luces, ha empeorado. Dudo en adelantar una cifra respecto a cuántos artefactos habrán encontrado y usarán contra Forjaz.$B$BNuestro objetivo es sencillo, $c. Vuelve a Tierras Inhóspitas y destruye al embajador Infernus. Una vez eliminada la amenaza, vuelve con el consejero Belgrum con una prueba de tu victoria.$B$BAhora todo depende de ti.",
    ["O"] = "Mata al embajador Infernus en la Fortaleza de Angor llévale la prueba de su muerte al consejero Belgrum en Forjaz.",
    ["T"] = "Un embajador de mal",
  },
  [763] = {
    ["D"] = "Si quieres seguir con los Ritos de la Madre Tierra tendrás que pasar otras dos pruebas. Debes ampliar tu experiencia, $n.$b$bVe al Poblado Pezuña de Sangre y habla con el Jefe Baine, hijo de Cairne Pezuña de Sangre. Allí podrás seguir viaje y ganarte la aceptación de los ancestros de Cima del Trueno.$b$bDale a Baine este tótem. Reconocerá mis grabados y te ayudará.$b$bSigue el camino que sale del Campamento Narache. Viaja deprisa y no te desvíes o te perderás.",
    ["O"] = "Lleva el tótem de Alalcón a Baine Pezuña de Sangre al Poblado Pezuña de Sangre. Sigue el camino que sale del Campamento Narache.",
    ["T"] = "Ritos de la Madre Tierra",
  },
  [764] = {
    ["D"] = "Eso explica que Ventura y Cía. estuviera adquiriendo mano de obra y material a gran escala en Mulgore. Esa empresa goblin se está expandiendo demasiado deprisa; acabarán mal. Solo piensan en el valor monetario de las cosas.$b$bPandilla de avariciosos... Por desgracia para ellos, los tauren no podemos permitir que trabajen en nuestras tierras. Ve a la mina, que está al noreste de la caravana quemada, y dales un mensaje.",
    ["O"] = "Mata a 14 obreros y 6 supervisores de Ventura y Cía. por encargo de Morin Acechanubes, del Poblado Pezuña de Sangre.",
    ["T"] = "Los Ventura y Cía.",
  },
  [765] = {
    ["D"] = "Los documentos que recuperaste en la caravana muestran que Ventura y Cía. estaba haciendo planes para echarnos de nuestras tierras.$b$bMe enferma pensar en lo que son capaces de hacer para conseguir lo que quieren. Quiero que mates a quien ideó los métodos para echarnos. Es el supervisor Fizsprocket; está en la mina que hay al este de la caravana. Tráeme su sujetapapeles, a ver si averiguamos algo más.",
    ["O"] = "Mata al supervisor Fizsprocket y lleva su sujetapapeles a Morin Acechanubes al Poblado Pezuña de Sangre.",
    ["T"] = "Supervisor Saltatuercas",
  },
  [766] = {
    ["D"] = "Mi abuelo me contaba historias de sus batallas con un zancudo llamado Mazzranache. Me contó que tenía los ojos rojos, espolones como navajas y una mordedura venenosa.$b$bFui a dar con él mientras cruzaba las llanuras y se me llevó un pedazo de hombro. Era tan terrorífico como había dicho mi abuelo. Para desinfectar la herida necesito unas cuantas cosas: un corazón de lobo, un fémur de puma, escama de zancudo de llanura y molleja.$b$bApresúrate, el tiempo corre.",
    ["O"] = "Lleva 1 corazón de lobo de pradera, 1 fémur de puma de las estepas, escamas de zancudo de llanura y molleja de aguilón a Maur Clamalluvias al Poblado Pezuña de Sangre.",
    ["T"] = "Mazzranache",
  },
  [767] = {
    ["D"] = "El Rito de la Visión, uno de los Ritos de la Madre Tierra, te enseñará cómo ganar el respeto de los ancestros de Cima del Trueno.$B$BNuestra gente ha aprendido que la tierra es la sagrada proveedora. Para tomar parte en la visión ritual, debes hablar con el guía espiritual de la aldea, Zarlman Dos Lunas.",
    ["O"] = "Habla con Zarlman Dos Lunas en el Poblado Pezuña de Sangre.",
    ["T"] = "Rito de la Visión",
  },
  [768] = {
    ["D"] = "Saludos, joven. El brillo en tus ojos me indica que estás impaciente por continuar la caza. ¡Ojalá traigas honor a tu clan!$B$BYo trabajo con pieles y con ellas confecciono ropajes y armaduras para las gentes de Cima del Trueno.$B$BSi cazas bestias y me traes sus pieles, confeccionaré algo especialmente para ti.",
    ["O"] = "Llévale 12 pieles de cuero ligero a Veren Zancaalta a Cima del Trueno.",
    ["T"] = "Recolecta cuero",
  },
  [769] = {
    ["D"] = "Los kodos de Mulgore son fuertes y robustos, unas cualidades que apreciamos mucho. Si tienes conocimientos de peletería y deseas aprender a fabricar una bolsa de piel de kodo, tráeme suministros para mi oficio.$B$BHazlo y compartiré mis conocimientos contigo.",
    ["O"] = "Llévale 4 pellejos de cuero ligero y 4 hilos gruesos a Veren Zancaalta a Cima del Trueno.",
    ["T"] = "Bolsa de pellejo de kodo",
  },
  [770] = {
    ["D"] = "La piel destrozada de Aullido Fantasma muestra la terrible herida que recibió en combate contra la Legión Ardiente.$b$bHabría que avisar de su muerte en el Poblado Pezuña de Sangre...",
    ["O"] = "Busca a alguien que conozca a Aullido Fantasma.",
    ["T"] = "La capa del demonio marcado",
  },
  [771] = {
    ["D"] = "Para superar el Rito de la Visión debes conseguir los componentes necesarios para crear el Agua de los Visionarios.$b$bNecesito piedras de pozo, que encontrarás cerca de los pozos de Mulgore. Las mezclaré con bellota ámbar, que cae de los orgullosos árboles de nuestra tierra.$b$bEstos ingredientes son mágicos; tómate el elixir resultante junto a la hoguera tribal para tener una visión. Síguela y te llevará al siguiente paso de esta misión sagrada.",
    ["O"] = "Lleva 2 piedras de pozo y 2 bellotas ámbar a Zarlman Dos Lunas al Poblado Pezuña de Sangre.",
    ["T"] = "Rito de la Visión",
  },
  [772] = {
    ["D"] = "Aquí tienes el Agua de los Visionarios.$b$bTómatela cerca de la hoguera tribal. Al cabo de un momento la visión se materializará junto a la hoguera.$b$bA partir de ahí, de ti depende el seguirla...",
    ["O"] = "Bebe el Agua de los Visionarios frente a la hoguera tribal del Poblado Pezuña de Sangre y sigue a la visión cuando aparezca.",
    ["T"] = "Rito de la Visión",
  },
  [773] = {
    ["D"] = "Para que los ancestros de Cima del Trueno te acepten, tienes que superar el Rito de la Sabiduría.$b$bAhora que has superado el Rito de la Visión, los espíritus ancestrales de Roca Roja te darán las bendiciones de los ancestros. Solo quien ha bebido el Agua de los Visionarios merece recibir la bendición.$b$bVe a Roca Roja, al este de Cima del Trueno, y busca al espíritu ancestral, $n.",
    ["O"] = "Ve a Roca Roja, al este de Cima del Trueno, y busca al espíritu ancestral.",
    ["T"] = "Rito de la Sabiduría",
  },
  [774] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [775] = {
    ["D"] = "Se te venerará en nuestra gran ciudad. Te mereces tal honor, pues has completado los Ritos de la Madre Tierra.$b$bTu última misión consiste en buscar a nuestro noble jefe, Cairne Pezuña de Sangre, que vive en la cima de la mesa más alta de Cima del Trueno.",
    ["O"] = "Habla con Cairne Pezuña de Sangre en Cima del Trueno.",
    ["T"] = "Viaja a Cima del Trueno",
  },
  [776] = {
    ["D"] = "Has superado los Ritos de la Madre Tierra y te has ganado un lugar en Cima del Trueno.$B$BPero no puedes dormirte en los laureles; debes probar tu valor constantemente.$B$BSomos cazadores, $n. En Mulgore vive un hermoso kodo que se llama Arra\'chea. Tráeme el cuerno de Arra’chea para demostrarme tu habilidad para rastrear y cazar.",
    ["O"] = "Cairne Pezuña de Sangre de Cima del Trueno quiere que le lleves el cuerno de Arra\'chea.",
    ["T"] = "Ritos de la Madre Tierra",
  },
  [777] = {
    ["D"] = "Muy bien, aquí tienes.$B$BEstaré por aquí... a cubierto.$B$BSi quieres, puedes unirte a mí.",
    ["O"] = "Llévale un piñón del n.º 5 a Lotwil Veriatus.",
    ["T"] = "Lo tienes crudo",
  },
  [778] = {
    ["D"] = "¡Ea! ¡Ya está! ¡Los grilletes están listos!$B$BEs la hora, Lucien. ¡Es la hora, Servo!$B$B¿Estás $gpreparado:preparada; para ser testigo de la perfecta combinación de alquimia, ingeniería y lo Arcano, $N?$B$BAhora voy a conjurar a un poderoso elemental. Este es un día marcado por la innovación para todos nosotros.",
    ["O"] = "Derrota al guardián Fam\'retor y devuélvele los grilletes de vínculo elemental a Lotwil Veriatus.",
    ["T"] = "Lo tienes crudo",
  },
  [779] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "El Sello de la Tierra",
  },
  [780] = {
    ["D"] = "Los jabaguerreros de Barranco Cortazarza se están instalando en nuestro terreno de caza tribal. Los jabaespines Erizapúas, con quienes estamos en guerra, los amaestran para aumentar su ferocidad.$b$bAcaba con esas bestias y trae algunos morros y costillas para hacer caldo.",
    ["O"] = "Grull Alalcón quiere que le lleves 8 morros y 8 costillas de jabaguerrero al Campamento Narache.",
    ["T"] = "Los jabaguerreros",
  },
  [781] = {
    ["D"] = "Al estudiar el mapa, ves que contiene los planes del Jefe de Guerra Erizapúas para el asedio prolongado del Campamento Narache. $b$b¡Hay que avisar al Jefe Vientaguilar!",
    ["O"] = "Lleva los planes de ataque de los Erizapúas al Jefe Vientaguilar al Campamento Narache.",
    ["T"] = "Ataque al Campamento Narache",
  },
  [782] = {
    ["D"] = "Por la alianza de la Horda con el dragón Alamuerte durante la Segunda Guerra, sabemos que dos de sus tenientes, Cinchoscura y Hematus, estaban presos en el Barranco Lethlor, un lugar remoto hacia el este. ¡Debemos acallar a nuestros depravados aliados ancestrales para restaurar el honor de la Horda!$B$BContratamos a Tho\'grun y a su grupo de ogros mercenarios para que nos ayuden a conseguir el Signo de la Tierra, una llave necesaria para desbloquear la cárcel de los dragones. Pero Tho\'grun nos traicionó, nos aplastó y se lo quedó. Ve al Asentamiento Boff ¡y recupéralo!",
    ["O"] = "Mata al jefe Tho\'grun y llévale el Signo de la Tierra a Gorn en Kargath.",
    ["T"] = "Alianzas rotas",
  },
  [783] = {
    ["D"] = "Espero que vengas con ganas, $c, porque aquí en Villanorte hay mucho que hacer.$b$bY no me refiero a trabajar la tierra.$b$bLos guardias de Ventormenta no dan abasto, ahora que muchos de nosotros están lejos y hay tantas amenazas. Por eso estamos reclutando a quien esté dispuesto a defender su tierra. Y a su alianza.$b$bSi vienes a luchar, habla con mi superior, el mariscal McBride. Está ahí atrás, en la abadía.",
    ["O"] = "Habla con el alguacil McBride.",
    ["T"] = "Una amenaza del interior",
  },
  [784] = {
    ["D"] = "Bajo el mando del almirante Valiente, los humanos de Kul Tiras invadieron Durotar, violando el pacto que el Jefe de Guerra selló con Jaina Valiente años atrás para derrotar a Archimonde.$B$BEl ataque humano fue rechazado y el Fuerte de Tiragarde cayó. Pero las reservas del Almirante, comandadas por el teniente Benedicto, han retomado el fuerte y vuelven a amenazar nuestro territorio. Los humanos no tienen respeto alguno por la diplomacia.$B$BPor tu honor, viaja al sur hasta el Fuerte de Tiragarde y acaba con los invasores humanos.",
    ["O"] = "Mata a 10 navegantes Kul Tiras, 8 marineros Kul Tiras y al teniente Benedicto. Luego vuelve a buscar a Gar\'Thok a Cerrotajo.",
    ["T"] = "Vencer a los traidores",
  },
  [785] = {
    ["D"] = "Thrall is a most wise and noble leader. His tenacity on the battlefield is matched only by his diplomatic tact.$b$bThe Darkspear Trolls have proven to be invaluable allies to orckind. Out of loyalty and honor we have sworn a pact of protection with them.$b$bThe Warchief has sent orders to make sure our Darkspear friends in Sen\'jin Village are not under any direct threat. One of their most trusted scouts, Lar Prowltusk is keeping a close watch on their western flank.$b$bSeek out Lar and assist as needed.",
    ["O"] = "Seek out Lar Prowltusk on the western flank of Sen\'jin Village.",
    ["T"] = "A Strategic Alliance",
  },
  [786] = {
    ["D"] = "Baja la voz, $c. Los centauros Kolkar están justo al otro lado de este promontorio, al oeste, en el Risco Kolkar.$B$BLa otra noche, cuando estaban fuera, en una de sus batidas, me colé en su poblado y descubrí que esas bestias inmundas tienen planeado un ataque en tres oleadas contra los trols y orcos de Durotar.$B$BDebemos frustrar sus planes de invasión. Quizás tú puedas reunir la fuerza necesaria para infiltrarte en el Risco Kolkar y destruir sus planes.$B$BLo último que vi es que los habían repartido entre tres de sus líderes.",
    ["O"] = "Lar Prowltusk, de las afueras del Poblado Sen\'jin, quiere que destruyas los 3 planes de ataque de Risco Kolkar.",
    ["T"] = "Frustrar la los planes kolkar",
  },
  [787] = {
    ["D"] = "Throm\'ka, $c. Soy Eitrigg, Thrall me encargó supervisar la instrucción de los reclutas.$B$BLa Horda ya no es lo que era. Hubo un tiempo en el que abandoné sus filas desilusionado por la creciente influencia de los peones, sedientos de poder, de la Legión Ardiente. Durante mi exilio fui hecho prisionero por un grupo de humanos pero me rescató el Jefe de Guerra. Regresé tras oír su visión de una Horda libre de influencia demoníaca y guiada por los chamanes.$B$BGornek te dará más instrucciones.",
    ["O"] = "Informa a Gornek en El Cubil.",
    ["T"] = "La nueva Horda",
  },
  [788] = {
    ["D"] = "El primer punto del día será fortalecer un poco tu espalda. Te podría enviar a Los Baldíos, a cazar kodos, pero, sinceramente, nos eres más útil $gvivo:viva; que $gmuerto:muerta;.$B$BCreo que lo mejor será que empieces por los jabalíes jaspeados que encontrarás al norte de aquí.",
    ["O"] = "Mata a 10 jabalíes jaspeados y vuelve con Gornek en El Cubil.",
    ["T"] = "Dientes afilados",
  },
  [789] = {
    ["D"] = "El poderoso guerrero y el torpe novicio... todos han caído bajo la venenosa picadura del escórpido. Encontrarás un gran número de escórpidos al noroeste de aquí. Tráeme 10 de sus colas como prueba de tu destreza y valor en la batalla.$b$bEl antídoto para sus picaduras se hace a base del veneno extraído de sus aguijones. Conservamos a mano grandes cantidades de antídoto para el veneno de escórpido con las que curar sangres jóvenes como la tuya...$b$bPero seguro que a ti no te hace ninguna falta, ¿no es cierto?",
    ["O"] = "Consigue 10 colas de trabajador escórpido para Gornek en El Cubil.",
    ["T"] = "Aguijón de escórpido",
  },
  [790] = {
    ["D"] = "¡$C! Creía que iba morir ahí fuera sin que nadie se enterara. Cuando estaba cazando los escórpidos del Valle me crucé con uno que tenía un aspecto especialmente atroz. Me lancé contra él y conseguí asestarle un enorme golpe antes de se enrollara en mi pierna.$b$bPero no estaba listo para su aguijón, y consiguió deslizarse en mi peto, desgarrando y penetrando en mi piel y derramando mi sangre. Por favor, ¡debes matar a ese escórpido! ¡Hay que defender mi honor! Combatí con él en la meseta que está al Sur.",
    ["O"] = "Mata a Sarkoth y llévale su zarpa a Hana\'zua.",
    ["T"] = "Sarkoth",
  },
  [791] = {
    ["D"] = "El tiempo me ha inutilizado para el combate. A mi edad, busco otras formas de ser útil.$B$BDesde este punto estratégico puedo vigilar la llegada de invasores. A medida que nuestra fuerza aquí se consolida, me encuentro dando la alarma cada vez menos.$B$BPara pasar el tiempo, creo objetos para ayudar a guerreros jóvenes y capacitados para defender nuestro hogar.$B$BA ti podría hacerte una bolsa para que lleves tus pertenencias. Si crees que te vendría bien, tráeme un retal. Sabrás lo que es... es un artículo muy normal tanto para centauros como para humanos.",
    ["O"] = "Furl Ceñosprecio de la Atalaya de Cerrotajo quiere 8 retales.",
    ["T"] = "Asume tu responsabilidad",
  },
  [792] = {
    ["D"] = "Estoy segura de que aprenderás mucho en el Valle de los Retos, joven $c.$B$BFui enviada al Valle para guiarte, pero aquí he descubierto una creciente corrupción...$B$BUn grupo que se llama a sí mismo el Filo Ardiente tiene un aquelarre aquí mismo, en el Valle de los Retos. Se esconden en una cueva al Noreste, y sus familiares viles se han esparcido desde la boca de la cueva sembrando el caos y la confusión.$B$BTe ofrezco tu primera tarea contra el Filo Ardiente: derrotar a esos familiares. Aniquila a tantos como puedas y, si sobrevives, vuelve a verme.",
    ["O"] = "Mata a 12 familiares viles.$B$BVuelve con Zureetha Vistalonga a las puertas de El Cubil.",
    ["T"] = "Familiares viles",
  },
  [793] = {
    ["D"] = "El Sello de la Tierra está controlado por tres piedras rúnicas, que se encuentran en el Pilar de Diamante, el Pilar de Ópalo y el Pilar de Amatista en el Barranco Lethlor. Mediante el Signo de la Tierra, podrás deshacerte de las piedras rúnicas y desbloquear el Sello de la Tierra.$B$BAunque ándate con cuidado, $n, los dragones negros Cinchoscura y Hematus son de armas tomar. Acaba con ellos y tráeme sus esclavas.$B$BPara llegar al barranco, dirígete hacia los confines orientales de las Tierras Inhóspitas. Y abre bien los ojos porque el barranco es tierra de multitud de dragones.",
    ["O"] = "Usa el Signo de la Tierra para activar el Pilar de Diamante, el de Ópalo y el de Amatista y así obtener las piedras rúnicas.$B$BColoca las piedras rúnicas en el Sello de la Tierra para liberar a Cinchoscura y a Hematus.$B$BMátalos y llévale a Gorn las ataduras de Cinchoscura, las cadenas de Hematus y el Signo de la Tierra.",
    ["T"] = "Alianzas rotas",
  },
  [794] = {
    ["D"] = "Mis adivinaciones me traen visiones de un objeto lleno de poder, oculto en las profundidades del Aquelarre del Filo Ardiente y custodiado por bestias y magia negra.$B$BEs el llamado medallón del Filo Ardiente y tu siguiente tarea es sacarlo del Aquelarre.$B$BPero ten mucha cautela, el medallón puede estar en posesión de un agente del Filo Ardiente y su poder podría ser mayor que el del resto de los familiares con los que te hayas encontrado.$B$BVete, $n. Encontrarás el Aquelarre en una cueva, al Norte y al Oeste.",
    ["O"] = "Lleva el medallón del Filo Ardiente a Zureetha Vistalonga, a las puertas de El Cubil.",
    ["T"] = "El medallón del Filo Ardiente",
  },
  [795] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "El Sello de la Tierra",
  },
  [796] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [797] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
