pfDB["quests"]["frFR"] = {
  [1] = {
    ["D"] = "$Tpunk;!  Kill Kobold Vermin, 2 of em. NEW TEST AGAIN",
    ["O"] = "Kill Kobold Vermin, 2 of em.",
    ["T"] = "The \"Chow\" Quest (123)aa",
  },
  [2] = {
    ["D"] = "Le grand hippogryphe Serres-tranchantes a été tué, et la griffe arrachée à son cadavre témoigne de votre victoire. Senani Thunderheart, au poste de Bois-brisé, sera sans doute intéressée de voir ce trophée qui est la preuve de votre exploit.",
    ["O"] = "Apporter la griffe de Serres-tranchantes à Senani Thunderheart, au poste de Bois-brisé, en Ashenvale.",
    ["T"] = "La griffe de Serres-tranchantes",
  },
  [5] = {
    ["D"] = "Je suis resté caché dans cette ville fantôme depuis des semaines.$B$BApportez-moi de quoi festoyer et je vous payerai généreusement.$B$BJ\'ai entendu parler d\'un certain chef Grual qui fait de succulents gâteaux au crabe brun, à la taverne du Corbeau écarlate à Darkshire...",
    ["O"] = "Parler au chef Grual.",
    ["T"] = "L\'estomac qui gargouille",
  },
  [6] = {
    ["D"] = "Garrick Padfoot – un brigand qui harcèle les fermiers et les marchands de la région depuis des semaines – a été vu dans une cabane près des vignes qui se trouvent à l\'est de l\'abbaye, juste après le pont. Rapportez-moi la tête de ce mécréant, et vous empocherez la récompense !$B$BMais soyez $gprudent:prudente; $N. Garrick a rassemblé une bande de voyous autour de lui. Il ne sera pas aisé de l\'approcher.",
    ["O"] = "Tuer Garrick Padfoot et rapporter sa tête à l\'Adjoint Willem de l\'Abbaye du Northshire.",
    ["T"] = "La prime pour Garrick Padfoot",
  },
  [7] = {
    ["D"] = "Votre première tâche est un nettoyage, $N. Un clan de kobolds a envahi les bois se trouvant au nord. Allez là-bas et combattez cette vermine. Affaiblissez-la assez, pour qu\'un jour nous puissions la repousser hors du Northshire.",
    ["O"] = "Tuer 10 Vermines kobold, puis revenir voir le maréchal McBride.",
    ["T"] = "Nettoyer le camp des kobolds",
  },
  [8] = {
    ["D"] = "Hé ! Voudriez-vous faire une faveur à un homme qui a eu plus que sa part de combats contre des zombies et des araignées ?$B$BJ\'ai ici une lettre qui doit être livrée à Brill... à une aubergiste du nom de Renée quelque chose. Enfin c\'est pas important son nom de famille.$B$BBref, c\'est un joli petit coin, plein de personnes victimes de la peste qui essayent de trouver leur voie en ce bas monde. De plus, c’est un bon endroit pour se reposer. Vous devriez aller voir... D\'autant que je vous payerai bien.",
    ["O"] = "Livrer une Lettre à Renée dans les Clairières de Tirisfal.",
    ["T"] = "Un marché de voleur",
  },
  [9] = {
    ["D"] = "Regardez ce qu\'il s\'est passé ici ! Ces terres étaient autrefois habitées par de braves fermiers. Mais ces maudits voleurs les ont tous chassés. Pas moi, pour sûr ! Mais il semble que quelques Gardiens des moissons ont pris possession des champs.$b$bSi vous êtes d\'accord, j\'aimerais que vous y alliez et que vous en détruisiez une vingtaine. Revenez quand vous aurez fini pour recevoir votre dû. Une fois que vous aurez débarrassé ceux de mon champ, occupez-vous des champs alentours.",
    ["O"] = "Le Fermier Saldean veut que vous tuiez 20 Gardiens des moissons.",
    ["T"] = "Les champs de mort",
  },
  [10] = {
    ["D"] = "Oh, c\'est terrible ! J\'ai envoyé mon premier assistant, le géomètre novice Scrimshank, dans le désert au sud-est d\'ici. C\'est un endroit appelé le Gouffre béant. Et il est porté disparu. Dans son dernier rapport, il faisait allusion à ces étranges insectes près de là où il était. Serait-il possible qu\'ils aient maintenant infesté deux grandes régions du désert ?$B$BJ\'ai besoin que vous alliez retrouver Scrimshank dans le Gouffre béant ! Ou, au pire, que vous retrouviez son équipement topographique. Je crains que ce soit tout ce qu\'il reste de lui.",
    ["O"] = "Découvrir ce qu\'est devenu le géomètre Scrimshank, et rapporter son équipement topographique au grand géomètre Fizzledowser à Gadgetzan.",
    ["T"] = "La rédemption de Scrimshank",
  },
  [11] = {
    ["D"] = "Des gnolls, des animaux brutaux qui n\'ont rien à faire ici, ont été aperçus à la lisière de la forêt d\'Elwynn. C\'est un très grand groupe, bien trop nombreux pour que nous nous en occupions seuls. Ils ont envahi les bois au sud de la tour de garde. Un autre groupe à envahi les zones autour du lac du Cairn à l\'est.$B$BL\'armée de Stormwind engage quiconque pourrait en tuer. Apportez-moi des Brassards peints de gnolls pour me prouver vos actions.",
    ["O"] = "Apporter 8 Brassards peints de gnolls à l\'Adjoint Rainer à la Caserne.",
    ["T"] = "Prime pour les gnolls Rivepatte",
  },
  [12] = {
    ["D"] = "La Milice du peuple n\'a qu\'un seul objectif : défendre la marche de l\'Ouest et ramener le calme dans les environs. Malheureusement, le prix de la paix est souvent le sang.$b$bUn de mes éclaireurs rapporte qu\'une bande de Trappeurs défias sème le trouble dans le coin. Ils auraient été aperçus près de la Mine de Jangolode, au nord-ouest, ainsi que dans la Ferme des Molsen et la Ferme de potirons des Furlbrow. Si vous voulez rejoindre nos rangs, tuez 15 Trappeurs défias et 15 Contrebandiers défias, puis revenez me voir.",
    ["O"] = "Tuer 15 Trappeurs défias et 15 Contrebandiers défias pour Gryan Stoutmantle, puis retourner le voir sur la Colline des sentinelles.",
    ["T"] = "La milice du peuple",
  },
  [13] = {
    ["D"] = "Une bande de pilleurs défias a été vue en train de saccager la carrière de la Côte de l\'or ainsi que Ruisselune et la ferme des Alexston. La Milice du peuple n\'acceptera pas ce genre de comportement. Déployez-vous immédiatement, $n, et faites en sorte que l\'on sache que la Lumière est présente dans la marche de l\'Ouest.$b$bLa carrière est près de la côte, à l\'ouest de la tour. Comme étape de votre instruction, je veux que vous tuiez 15 Pilleurs défias et 15 Pillards défias.",
    ["O"] = "Tuer 15 Pilleurs défias et 15 Pillards défias pour Gryan Stoutmantle, puis retourner le voir sur la Colline des sentinelles.",
    ["T"] = "La milice du peuple",
  },
  [14] = {
    ["D"] = "Quelques défias nous ont échappé. Mon éclaireur le plus fiable fait état de pillages défias dans les campagnes, au sud de la marche de l\'Ouest. Nous pensons qu\'ils se cachent dans les collines de la Dague, fomentant leur prochain coup. Détruisez ces misérables au nom de la Milice du peuple.",
    ["O"] = "Tuer 15 Malandrins défias, 5 Eclaireurs défias et 5 Truands défias pour Gryan Stoutmantle, puis retourner le voir sur la Colline des sentinelles.",
    ["T"] = "La milice du peuple",
  },
  [15] = {
    ["D"] = "$N, mes éclaireurs me rapportent que l\'infestation kobold est d\'une plus grande envergure que nous le pensions. Un groupe d\'ouvriers kobolds a établi son campement près de la mine de la Crête aux échos au nord.$B$BAllez à la mine et faites en sorte qu\'il n\'y en ait plus. Nous savons qu\'il y en a au moins 10. Tuez-les, voyez s\'il y en a davantage, et revenez me présenter votre rapport.",
    ["O"] = "Tuer 10 Ouvriers kobolds, puis revenir faire son rapport au maréchal McBride.",
    ["T"] = "Enquête sur la crête aux échos",
  },
  [16] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Procurer de l\'eau à Gerard",
  },
  [17] = {
    ["D"] = "On a découvert récemment une nouvelle plante : les champignons magenta. Ils poussent en grappes, au plus profond des excavations d\'Uldaman ; il en existe peut-être ailleurs, mais je ne connais que cet endroit. Je veux étudier leur utilité éventuelle en alchimie, et c\'est là que vous entrez en scène.$B$BTrouvez les Champignons magenta et rapportez-m\'en une douzaine. Attention, les grappes peuvent projeter des spores vénéneux quand on les manipule !$B$BRendez-moi ce service et je sortirai pour vous un petit Elixir revigorant de derrière les fagots.",
    ["O"] = "Apporter 12 Champignons magenta à Ghak Healtouch à Thelsamar.",
    ["T"] = "Rechercher les composants à Uldaman",
  },
  [18] = {
    ["D"] = "Un groupe de voleurs traîne depuis peu autour de Northshire. Ils se nomment eux-mêmes la Confrérie défias, et ont été aperçus de l\'autre côté de la rivière à l\'est.$B$BJe ne sais pas ce qu\'ils préparent, mais il est certain que ce n\'est rien de bon ! Apportez-moi les Masques qu\'ils portent, et je vous récompenserai d\'une arme.",
    ["O"] = "Apporter 12 Masques rouges à l’Adjoint Willem à l’extérieur de l’Abbaye de Northshire.",
    ["T"] = "La confrérie des voleurs",
  },
  [19] = {
    ["D"] = "Les renégats et les estafettes Blackrock tendent des embuscades entre ici et le donjon de Guet-de-pierre. Le chef des estafettes est un orc du nom de Tharil\'zun – nous voulons qu\'il meure. Rapportez-moi la tête de Tharil\'zun !",
    ["O"] = "Rapporter la Tête de Tharil\'zun au maréchal Marris, dans les Carmines.",
    ["T"] = "Tharil\'zun",
  },
  [20] = {
    ["D"] = "Les orcs Blackrock ont voyagé depuis les Steppes ardentes et ont pris possession du donjon de Guet-de-pierre, depuis le nord. De cette base, ils patrouillent dans toutes les zones au nord de Lakeshire et forcent les citoyens à rester en ville, parqués comme du bétail.$B$BAidez à libérer Lakeshire de cette oppression. Tuez ces orcs Blackrock et rapportez-moi 10 haches détériorées comme preuve de votre réussite.$B$BBien que les orcs Blackrock nous menacent au nord, nous avons des informations disant qu’il y aurait aussi des orcs au sud-est des Carmines. Si vous en trouvez là-bas, tuez-les.",
    ["O"] = "Rapporter 10 Haches détériorées au maréchal Marris de Lakeshire.",
    ["T"] = "La menace des Blackrock",
  },
  [21] = {
    ["D"] = "Vos dernières investigations ont prouvé que la mine de la Crête aux échos devait être nettoyée. Retournez-y et chassez les kobolds.$B$BNe perdez pas de temps, $N. Plus les kobolds seront tranquilles dans la mine, plus ils renforceront leurs positions dans le Northshire.",
    ["O"] = "Tuer 12 Travailleurs kobold, puis revenir voir le maréchal McBride, à l’Abbaye de Northshire.",
    ["T"] = "La bataille de la Crête aux échos",
  },
  [22] = {
    ["D"] = "Les oignons sont pelés et l\'ail émincé. Le romarin est pilé. La pâte est cuite et le fenouil haché. La sauce finit de mijoter. Il ne me manque que 8 foies de Broche-tripes pour réussir ma fameuse tourte à la viande !",
    ["O"] = "Salma Saldean a besoin de 8 Foies de broche-tripes pour faire une Tourte au foie de broche-tripes.",
    ["T"] = "La tourte au foie de broche-tripes",
  },
  [23] = {
    ["D"] = "La patte du puissant ours Ursangous représente votre trophée pour cette partie de la Chasse d\'Ashenvale. Sur la patte figure un petit emblème en forme de croissant de lune, dont la signification reste mystérieuse.$B$BLa Patte doit être apportée à Senani Thunderheart, au Poste de Bois-brisé, comme preuve que vous avez abattu la puissante créature et terminé cette partie de la Chasse.",
    ["O"] = "Apporter la Patte d\'Ursangous à Senani Thunderheart, au Poste de Bois-brisé, à Ashenvale.",
    ["T"] = "La patte d\'Ursangous",
  },
  [24] = {
    ["D"] = "Le rapide et redoutable Shadumbra gît, immobile et silencieux, maintenant que vous l\'avez vaincu. La tête du sabre-de-nuit est un trophée de première classe, parfaitement à même de satisfaire les conditions de la Chasse d\'Ashenvale. Senani Thunderheart, au Poste de Bois-brisé, aimerait voir un tel trophée pour pouvoir valider cette partie de la Chasse d\'Ashenvale.",
    ["O"] = "Apporter la Tête de Shadumbra à Senani Thunderheart, au Poste de Bois-brisé, dans Ashenvale.",
    ["T"] = "La tête de Shadumbra",
  },
  [25] = {
    ["D"] = "Si vous voulez vous rendre utile, pourquoi n\'iriez-vous pas essayer de régler le problème près de la Perce des Serres ?$B$BLes convois entre Ashenvale et les Serres-Rocheuses sont au point mort. En effet, c\'est comme si le lac avait pris vie et attaquait tout ce qui s\'approche à proximité... c\'est à dire quiconque s\'aventure sur la Perce. Au lieu de simplement massacrer ces élémentaires d\'eau, il serait vraiment utile que quelqu\'un aille reconnaître le kiosque du lac Mystral ; celui qui offre une si belle vue sur l\'avant-poste de l\'Alliance à cet endroit.$B$BAlors, voulez-vous vous rendre utile ?",
    ["O"] = "Tuer 12 Elémentaires d’eau corrompus au lac Mystral, à l’est de la Perce des Serres, et au sud-ouest du poste de Bois-brisé.$B$BFaire une reconnaissance sur le kiosque du lac Mystral ; celui qui a vue sur l’avant-poste de l’Alliance.$B$BRetourner voir Mastok Wrilehiss, au poste de Bois-brisé, dans Ashenvale.",
    ["T"] = "Impasse aux Serres-Rocheuses",
  },
  [26] = {
    ["D"] = "$N, j\'ai suivi votre progression avec beaucoup de plaisir. Vous êtes assurément en train de devenir $gun véritable gardien et protecteur:une véritable gardienne et protectrice; de la nature.$B$BLe temps est venu pour vous d\'en apprendre plus à propos de l\'une des nombreuses formes animales que nous pouvons revêtir et dont nous gratifie Cénarius. Un aspect qui vous permettra de maîtriser l\'eau. Pour cela, vous devez voyager jusqu\'au village de Havrenuit, à Reflet-de-Lune, et parler à Dendrite Starblaze, du Cercle cénarien. Il vous guidera plus avant.",
    ["O"] = "Parler à Dendrite Starblaze, au village de Havrenuit, à Reflet-de-Lune.",
    ["T"] = "Une leçon à apprendre",
  },
  [27] = {
    ["D"] = "$N, j\'ai suivi votre progression avec beaucoup de plaisir. Vous êtes assurément en train de devenir $gun véritable gardien et protecteur:une véritable gardienne et protectrice; de la nature.$B$BLe temps est venu pour vous d\'en apprendre plus à propos de l\'une des nombreuses formes animales que nous pouvons revêtir et dont nous gratifie Cénarius. Un aspect qui vous permettra de maîtriser l\'eau. Pour cela, vous devez voyager jusqu\'au village de Havrenuit, à Reflet-de-Lune, et parler à Dendrite Starblaze, du Cercle cénarien. Il vous guidera plus avant.",
    ["O"] = "Parler à Dendrite Starblaze, au village de Havrenuit, à Reflet-de-Lune.",
    ["T"] = "Une leçon à apprendre",
  },
  [28] = {
    ["D"] = "Pour la première épreuve, vous devrez aller fouiller les profondeurs du lac Elune\'ara, et trouver un Colifichet du sanctuaire. Attention, il se corrompt rapidement ; aussi, ne tardez pas à l\'apporter au sanctuaire de Remulos. Utilisez le Colifichet sur le sanctuaire puis parlez à Tajarri ; elle est l\'un des gardiens les plus fiables de Reflet-de-Lune.$B$BVous devez réussir cette quête avant l\'expiration du temps imparti, et vous serez peut-être en concurrence avec d\'autres druides, également à la recherche de colifichets. Il vous faudra user de ruse et de dextérité pour réussir dans cette mission.$B$BBonne chance, $N.",
    ["O"] = "Trouver un Colifichet du sanctuaire dans le Lac Elune\'ara, et l’apporter au sanctuaire de Remulos, au nord-ouest de Reflet-de-Lune. Une fois sur place, utiliser le Colifichet du sanctuaire.$B$BVous devez parler à Tajarri, au sanctuaire, afin de terminer l’épreuve.",
    ["T"] = "L\'épreuve du lac",
  },
  [29] = {
    ["D"] = "Pour la première épreuve, vous devrez aller fouiller les profondeurs du lac Elune\'ara, et trouver un Colifichet du sanctuaire. Attention, il se corrompt rapidement ; aussi, ne tardez pas à l\'apporter au sanctuaire de Remulos. Utilisez le Colifichet sur le sanctuaire puis parlez à Tajarri ; elle est l\'un des gardiens les plus fiables de Reflet-de-Lune.$B$BVous devez réussir cette quête avant l\'expiration du temps imparti, et vous serez peut-être en concurrence avec d\'autres druides, également à la recherche de colifichets. Il vous faudra user de ruse et de dextérité pour réussir dans cette mission.$B$BBonne chance, $N.",
    ["O"] = "Trouver un Colifichet du sanctuaire dans le Lac Elune\'ara, et l’apporter au sanctuaire de Remulos, au nord-ouest de Reflet-de-Lune. Une fois sur place, utiliser le Colifichet du sanctuaire.$B$BVous devez parler à Tajarri, au sanctuaire, afin de terminer l’épreuve.",
    ["T"] = "L\'épreuve du lac",
  },
  [30] = {
    ["D"] = "Pour la seconde épreuve, vous aurez besoin des deux moitiés qui constitueront le Pendentif du lion de mer. L\'une des moitiés emprunte au lion de mer son agilité à glisser à travers les eaux. L\'autre moitié emprunte à l\'animal son incroyable endurance. Les druides doivent revêtir ces deux aspects pour pouvoir adopter leur forme aquatique.$B$BAllez parler aux résidents de Reflet-de-Lune pour apprendre où les deux parties se trouvent, puis rapportez-les ici, afin de pouvoir les joindre ensemble. Quand vous aurez formé le pendentif, apportez-le à Dendrite Starblaze.",
    ["O"] = "Trouver le Demi-pendentif d\'agilité aquatique et le Demi-pendentif d\'endurance aquatique. Parler aux habitants de Reflet-de-Lune pour obtenir des indices quant à l’endroit où ces objets se trouvent.$B$BReformer le Pendentif du lion de mer à partir des deux moitiés. Vous devez vous trouver à proximité du sanctuaire de Remulos pour faire cela.$B$BApporter le Pendentif reformé à Dendrite Starblaze, au village de Havrenuit, à Reflet-de-Lune.",
    ["T"] = "L\'épreuve du lion de mer",
  },
  [31] = {
    ["D"] = "Vous avez terminé les leçons nécessaires, enseignées à tous les jeunes druides avant qu\'ils ne puissent adopter un aspect aquatique. Retournez voir votre Maître, Turak Runetotem, à Thunder Bluff. Présentez-lui votre pendentif et montrez-lui que vous êtes en mesure d\'apprendre ce qu\'il est prêt à vous enseigner. Il veillera à parachever votre entraînement, vous permettant de revêtir une forme aquatique.$B$BAu revoir, jeune druide. Nous nous reparlerons plus tard.",
    ["O"] = "Retourner à Thunder Bluff et montrer le Pendentif du lion de mer à Turak Runetotem.",
    ["T"] = "Forme aquatique",
  },
  [32] = {
    ["D"] = "Voici votre rapport. Je vous demande de l\'apporter de toute urgence à quelqu\'un qui a de l\'expérience dans ce domaine.$B$BJe connais un troll érudit du nom de Zilzibin Drumlore. Il a passé beaucoup de temps à étudier les formes de vie exceptionnelles de l\'écologie d\'Azeroth ; de plus, c\'est un historien ! Si vous voulez rencontrer quelqu\'un capable de comprendre la nature de ce danger et comment l\'arrêter, apportez-lui ce rapport. Nous allons avoir besoin de toute l\'aide disponible.$B$BZil réside dans une maison sur le second tiers de la Herse, à Orgrimmar.",
    ["O"] = "Apporter le rapport à Zilzibin Drumlore à Orgrimmar.",
    ["T"] = "L\'ascension des silithides",
  },
  [33] = {
    ["D"] = "J\'ai horreur de ces maudits loups des bois ! Mais j\'aime beaucoup en déguster des steaks… Apportez-moi de la viande de loup coriace et je vous donnerai en contrepartie quelque chose que vous devriez trouver utile.$B$BOn peut trouver de la viande de loup coriace en chassant les loups des bois et les jeunes loups qui parcourent la campagne de Northshire.",
    ["O"] = "Apporter 8 pièces de Viande de loup coriace à Eagan Peltskinner, à l’extérieur de l’Abbaye de Northshire.",
    ["T"] = "Les loups derrière la frontière",
  },
  [34] = {
    ["D"] = "Mes jardins ont encore été saccagés. Et je connais le responsable. C\'est ce satané sanglier, celui que le maréchal Marris appelle Fouailleventre.$b$bJe ne vais pas pouvoir replanter mes jonquilles avant la prochaine saison maintenant. Ruinée ! Je suis complètement ruinée à cause de ce sanglier ! Mettez un terme à cette nuisance ! Montrez-moi ses défenses et je serai heureuse de vous récompenser. Il semble qu\'il passe son temps dans les champs au sud-ouest de Lakeshire, mais il vagabonde de temps en temps jusque dans mon jardin.",
    ["O"] = "Martie Jainrose de Lakeshire veut que vous tuiez Fouailleventre. Ramenez-lui ses défenses comme preuve.",
    ["T"] = "Un indésirable",
  },
  [35] = {
    ["D"] = "Si vous voulez savoir si les rumeurs concernant les murlocs sont vraies, allez donc au pont, à l\'est d’Elwynn et parlez au garde Thomas. Il y est en poste depuis une semaine, il saura de quoi il retourne.$B$BRamenez-moi son compte rendu.",
    ["O"] = "Le maréchal Dughan veut que vous parliez au garde Thomas.",
    ["T"] = "Inquiétudes supplémentaires",
  },
  [36] = {
    ["D"] = "Je n\'avais jamais pensé un jour quitter ma ferme. Mais les champs grouillent de voleurs, et cela devient vraiment trop dangereux pour nous. Dès que la charrette du Fermier Furlbrow sera réparée, nous partirons. $b $bVous pourriez peut-être me faire une faveur? Laissez-moi écrire la recette du ragoût de la marche de l\'Ouest. S\'il vous plaît, apportez-la à Salma Saldean à la ferme de l\'autre côté, là-bas. La ferme des Saldean est un peu plus loin sur la route, juste après l\'embranchement.",
    ["O"] = "Verna Furlbrow veut que vous apportiez la Recette du ragoût de la marche de l\'Ouest à Salma Saldean.",
    ["T"] = "Le ragoût de la marche de l\'Ouest",
  },
  [37] = {
    ["D"] = "Il y a quelques jours, j\'ai envoyé deux gardes, Rolf et Malakai, patrouiller le long de la rivière, et ils ne sont toujours pas revenus. Afin de finaliser mon rapport, je dois absolument savoir ce qu\'il est advenu de ces hommes.$B$BAllez vers le nord, le long de la rivière, et trouvez les gardes... ou ce qu\'il en reste.",
    ["O"] = "Le garde Thomas veut que vous alliez vers le nord, le long de la rivière, à la recherche de deux gardes disparus, Rolf et Malakai.",
    ["T"] = "Trouver les gardes disparus",
  },
  [38] = {
    ["D"] = "Aidez-moi à faire du ragoût de la marche de l\'Ouest. Ramenez les ingrédients suivants : $b $b 3 morceaux de Viande filandreuse de vautour ; $b 3 Groins de Broche-tripes ; $b 3 Yeux de murloc ; $b 3 Okras.",
    ["O"] = "Salma Saldean veut 3 morceaux de Viande filandreuse de vautour, 3 Groins de Broche-tripes, 3 Yeux de murloc et 3 Okras.",
    ["T"] = "Le ragoût de la marche de l\'Ouest",
  },
  [39] = {
    ["D"] = "Allez annoncer au maréchal Dughan la mort de Malakai et de Rolf, et prévenez-le que les murlocs à l\'est d\'Elwynn ne pourront pas être contenus par les troupes présentes actuellement.$B$BJe sais que nous n’avons pas beaucoup de troupes disponibles, mais Dughan trouvera une solution.",
    ["O"] = "Avertir le maréchal Dughan de Goldshire.",
    ["T"] = "Remettre le rapport de Thomas",
  },
  [40] = {
    ["D"] = "$N, il y a une nouvelle menace dans la forêt d\'Elwynn ! Les murlocs remontent le cours est de l\'Elwynn, effrayant les poissons et attaquant des personnes innocentes !$B$BJ\'ai averti le maréchal Dughan, mais il est plus préoccupé par les gnolls et les bandits. Il n\'est pas convaincu que les murlocs représentent un réel danger.$B$BS\'il vous plaît, $N, parlez à Dughan et persuadez-le d\'envoyer des renforts à l\'est !",
    ["O"] = "Remy \"Deux fois\" veut que vous parliez au maréchal Dughan à Goldshire.",
    ["T"] = "Péril en la rivière",
  },
  [45] = {
    ["D"] = "En fouillant un peu plus dans la zone, vous tombez sur des traces de pas palmés qui mènent vers l\'est, le long de la rive du lac du Cairn. Légèrement plus loin, vous apercevez un village murloc.$B$BPeut-être que le destin de Rolf s\'est arrêté ici...",
    ["O"] = "Fouiller le village des murlocs pour trouver Rolf ou des traces de sa mort.",
    ["T"] = "La destinée de Rolf",
  },
  [46] = {
    ["D"] = "L\'armée de Stormwind a mis à prix la tête des rôdeurs et des fourrageurs murlocs qui se trouvent à Elwynn. Tuez-les, découpez leurs ailerons et ramenez-les-moi, l\'armée de Stormwind vous récompensera.$B$BLes murlocs ont construit un village au lac du Cairn au nord d\'ici, et un autre au sud, là où la rivière se sépare.",
    ["O"] = "Ramener 8 Ailerons de murloc au garde Thomas au Pont est d\'Elwynn.",
    ["T"] = "Des primes pour les murlocs",
  },
  [47] = {
    ["D"] = "Par ici les kobolds transportent quelquefois de la poussière d\'or sur eux. Cela peut m\'être très utile : rapportez-m\'en et je vous en donnerai le meilleur prix de la ville ! Le meilleur prix de la ville !$B$BVous trouverez des kobolds dans la mine de Fargodeep, au sud, et autour de la mine de Jasperlode au nord-est.",
    ["O"] = "Rapporter 10 Poussières d\'or à Remy \"Deux fois\" à Goldshire. La Poussière d\'or se récupère sur les kobolds dans la forêt d\'Elwynn.",
    ["T"] = "L\'échange de poussière d\'or",
  },
  [48] = {
    ["D"] = "Dans la campagne du Sud, là où la vigne pousse, en rampant et se tordant$BSe cache un puits où l\'eau coule profondément$BAussi pure que la Sœur sacrée de la Lumière, elle est$BApportez-moi un peu de cette eau sacrée.",
    ["O"] = "Rapporter de l\'Eau de source sacrée à Grimbooze Thunderbrew.",
    ["T"] = "Doux whisky",
  },
  [49] = {
    ["D"] = "Le whisky, de ma vie est le plus ancien amour$bComme les étoiles qui durent pour toujours$bRapportez-moi, pour que mon amour revienne majestueux tel un aigle$bUn sac de chacune de ces céréales : orge, maïs et seigle.",
    ["O"] = "Grimbooze Thunderbrew veut un Sac d\'orge, un Sac de seigle et un Sac de maïs.",
    ["T"] = "Doux whisky",
  },
  [50] = {
    ["D"] = "Je mélangerai et mélangerai, jusqu\'à de la pulpe obtenir$bPour que ce soit notre petit cache sans faiblir$bEn métal solide, il faut que cela soit fait$bPour me permettre de finir cette chanson, du vrai-argent il faut me rapporter.",
    ["O"] = "Grimbooze Thunderbrew veut du Vrai-argent.",
    ["T"] = "Doux whisky",
  },
  [51] = {
    ["D"] = "Pas encore filtré, mais déjà distillé$BComme une terre inconnue attendant d\'être trouvée$BPuis pour faire un tas de charbon il faut être actif$BLivrez-moi une branche de sycomore, ceci est votre objectif.",
    ["O"] = "Apporter une Branche de sycomore à Grimbooze Thunderbrew.",
    ["T"] = "Doux whisky",
  },
  [52] = {
    ["D"] = "Salutations, $N. Les animaux sauvages deviennent de plus en plus agressifs à mesure que l\'on s\'éloigne de Goldshire, et le camp de bûcherons du Val d\'est subit de perpétuelles attaques de la part de loups et d\'ours !$B$BNous pourrions avoir besoin de votre aide.",
    ["O"] = "Tuer 8 Rôdeurs et 5 Jeunes ours des forêts, et revenir voir le garde Thomas au pont, à l\'est d’Elwynn.",
    ["T"] = "Protéger la frontière",
  },
  [53] = {
    ["D"] = "Douce eau aussi translucide que des larmes d\'enfant$bUne merveilleuse et profonde couleur ambre prend avec le temps$bMais avant de nous saouler$bUn fût de chêne doit être fabriqué.",
    ["O"] = "Apporter un Fagot de chêne à Grimbooze Thunderbrew.",
    ["T"] = "Doux whisky",
  },
  [54] = {
    ["D"] = "$N, vous êtes un $c indubitablement impliqué dans la sûreté de Northshire. Vous avez maintenant en charge la protection de la zone entourant la forêt d\'Elwynn.$B$BSi vous acceptez cette responsabilité, j\'ai préparé tous les documents qui doivent être apportés au maréchal Dughan à Goldshire. Goldshire se situe le long de la route sud, au-delà de la frontière.",
    ["O"] = "Apporter les documents du maréchal McBride au maréchal Dughan de Goldshire.",
    ["T"] = "Rapport pour Goldshire",
  },
  [55] = {
    ["D"] = "Morbent Fel se cache dans son repaire, la maison perchée sur la colline à l\'est, celle qui domine le cimetière de Colline-aux-Corbeaux. Son temps sur cette terre touche à sa fin...$B$BUtilisez le Fléau de Morbent contre lui. Il réduira à néant ses magies protectrices.$B$BTuez-le. Tuez-le et libérez-nous de sa cruauté. Soyez le bras armé de ma vengeance et $gle héros:l\'héroïne; du bois de la Pénombre !",
    ["O"] = "Utiliser le Fléau de Morbent sur Morbent Fel, tuer Morbent Fel, puis rapporter le Fléau de Morbent à Sven, dans son camp.",
    ["T"] = "Morbent Lagangrène",
  },
  [56] = {
    ["D"] = "En tant que commandant des Veilleurs, et de par mon serment, il est de mon devoir de protéger les citoyens de Darkshire. Stormwind a rappelé les gardes qui étaient en faction ici, nous laissant nous débrouiller par nous-mêmes.$B$BCe serait un acte noble de votre part si vous nous aidiez, car nous en avons terriblement besoin ! Aidez les Veilleurs. Aventurez-vous dans les Jardins paisibles, juste au sud de Darkshire, et tuez 8 guerriers squelettes, ainsi que 6 mages squelettes. Les morts-vivants doivent être contrecarrés !",
    ["O"] = "Le commandant Althea Ebonlocke de Darkshire veut que vous tuiez 8 Guerriers squelettes et 6 Mages squelettes.",
    ["T"] = "Les Veilleurs",
  },
  [57] = {
    ["D"] = "$N, on vient juste de me rapporter que la forêt était envahie de morts-vivants ! Si vous êtes d\'accord pour le faire, voyagez jusqu\'au cimetière de Colline-aux-Corbeaux et tuez 15 de ces infâmes démons squelettes et 15 squelettes infernaux. Mes informateurs me disent que des squelettes ont aussi été repérés au sud de Darkshire, autour et dans le cimetière des Jardins paisibles. Les morts-vivants doivent être repoussés hors du bois de la Pénombre !",
    ["O"] = "Le commandant de Darkshire, Althea Ebonlocke, veut que vous tuiez 15 Démons squelettes et 15 Squelettes infernaux.",
    ["T"] = "Les Veilleurs",
  },
  [58] = {
    ["D"] = "La situation reste préoccupante à Colline-aux-Corbeaux. La sûreté de Darkshire est entre vos mains, $N. Je n\'ai pas assez de Veilleurs pour protéger la ville. Nous avons encore besoin de vous. Allez à Colline-aux-Corbeaux pour débarrasser le mausolée est de 20 répandeurs de peste !",
    ["O"] = "Le commandant de Darkshire, Althea Ebonlocke, veut que vous tuiez 20 Répandeurs de peste dans le mausolée est de Colline-aux-Corbeaux.",
    ["T"] = "Les Veilleurs",
  },
  [59] = {
    ["D"] = "Pour récompenser votre perspicacité et votre bravoure, j\'ai ici un Bon pour une pièce d\'armure de Stormwind. Allez trouver Sara Timberlain, au Camp de bûcherons du Val d\'est. Donnez-lui ce bon et elle vous fabriquera une armure. Une fois que vous l\'aurez, $N, utilisez-la pour défendre Elwynn.$B$BLe Camp de bûcherons du Val d\'est est au-delà du poste de garde de Thomas, vers l\'est.",
    ["O"] = "Donner le Bon pour une pièce d\'armure de Stormwind à Sara Timberlain.",
    ["T"] = "Les armures de cuir et d\'étoffe",
  },
  [60] = {
    ["D"] = "Bonjour, $gcher monsieur:ma dame; ! Auriez-vous une seconde ?$B$BMon frère et moi sommes apothicaires à Stormwind, et je suis ici pour acheter des grandes bougies, afin d\'utiliser leur cire. Pourriez-vous m\'aider ?$B$BLes kobolds produisent ces grandes bougies, et j\'ai entendu dire qu’ils infesteraient les mines d\'Elwynn... La mine de Fargodeep au sud et la celle de Jasperlode à l\'est. Je vous suggère d\'aller me chercher ces bougies là où elles se trouvent.",
    ["O"] = "Rapporter 8 Grandes bougies à William Pestle de Goldshire.",
    ["T"] = "Les bougies de kobolds",
  },
  [61] = {
    ["D"] = "Mon frère Morgan attend ma cargaison de bougies à Stormwind. Je n\'ai pas le temps de faire le voyage moi-même, mais si vous livrez la cargaison à ma place, il vous payera bien.$B$BJ\'ai empaqueté les bougies, et vous pouvez trouver Morgan à notre magasin, les Apothicaires Pestle, dans le Quartier commerçant de Stormwind.",
    ["O"] = "Livrer la Cargaison de William à Morgan Pestle dans le Quartier commerçant de Stormwind.",
    ["T"] = "Une cargaison pour Stormwind",
  },
  [62] = {
    ["D"] = "Le Northshire n\'est pas la seule région à avoir des problèmes avec ses mines ! J\'ai des rapports disant que la mine de Fargodeep à Elwynn est aussi infestée de kobolds.$B$BAllez explorer la mine afin de confirmer ces rapports, puis revenez me voir. Elle se situe au sud de Goldshire, entre les propriétés des Stonefield et des Maclure.",
    ["O"] = "Explorer la mine de Fargodeep, puis revenir voir le maréchal Dughan de Goldshire.",
    ["T"] = "La mine de Fargodeep",
  },
  [63] = {
    ["D"] = "Le long de la côte de la forêt des Pins argentés, à l\'ouest du Sépulcre, vous trouverez un autel qui a été corrompu. Les esprits n\'y parlent plus aux gens. Vous devez résoudre le problème en purifiant l\'autel.$B$BPrenez ces bracelets élémentaires et la potion de Brine, placez-les sur le brasero de l’autel. Ce rituel devrait suffire à invoquer un esprit pur à qui vous pourrez parler.$B$BRamenez-moi une preuve de votre réussite, et je vous récompenserai avec un totem.",
    ["O"] = "Vaincre la Manifestation d\'eau corrompue et placer les Bracelets de manifestation corrompue avec le reste d\'eau pure sur le brasero d\'Everfount dans la forêt des Pins argentés.",
    ["T"] = "L\'appel de l\'eau",
  },
  [64] = {
    ["D"] = "C\'était horrible ! Verna m\'a réveillé quand elle a entendu du grabuge dans les champs. Ils étaient envahis par des vandales. Nous nous sommes enfuis à toutes jambes, du coup j\'ai oublié ma montre sur place. Le père de Verna me l\'avait offerte pour notre mariage et je suis malade de savoir qu\'elle est entre les mains de ces voleurs. J\'ai laissé ma montre dans la penderie à la ferme. Cherchez le champ de citrouilles à l\'ouest – vous ne pouvez pas le manquer. Si vous me la ramenez, je saurais être reconnaissant !",
    ["O"] = "Le fermier Furlbrow désire que vous retrouviez sa Montre qui se trouve dans la penderie de sa ferme près du champ de citrouilles à l\'ouest.",
    ["T"] = "Le cadeau oublié",
  },
  [65] = {
    ["D"] = "La bande de misérables, responsable de la fuite des honnêtes gens de la marche de l\'Ouest, se fait appeler la Confrérie défias. J\'ai besoin de vous pour infiltrer ces voyous. Nous devons connaître l\'identité de leur chef et où ils sont basés. À Lakeshire, il y a un voleur nommé Wiley à l\'auberge qui me doit une faveur.$b$bAllez jusqu\'à Lakeshire dans les Carmines, à l\'est d\'Elwynn, et trouvez ce que vous pouvez.",
    ["O"] = "Gryan Stoutmantle veut que vous parliez à Wiley à Lakeshire.",
    ["T"] = "La Confrérie défias",
  },
  [66] = {
    ["D"] = "La nuit dernière, j\'ai eu un mauvais pressentiment concernant ma petite-fille Alyssa. Elle serait en grand danger. J\'ai tiré les cartes et la lame de la Faucheuse est sortie.$b$bAprès une longue et sombre transe, j’ai obtenu un indice. Un nom est apparu : Stalvan.$b$bAllez voir un greffier de la mairie et trouvez plus de détails sur ce personnage. J\'ai peur pour tout le monde…",
    ["O"] = "Aller voir le Greffier Daltry à la mairie de Darkshire.",
    ["T"] = "La légende de Stalvan",
  },
  [67] = {
    ["D"] = "Stalvan, hein ? Laissez-moi vérifier dans les registres de la ville.$b$bStalvan. Stalvan…Voyons voir. Ah ! nous y voilà. J\'ai la trace d\'un certain M. Stalvan Mistmantle. Sa dernière adresse connue est l\'école de Ruisselune. Enfin, mon registre date un peu !$b$bFaites-moi une faveur, si vous allez à Ruisselune, renseignez-vous sur cette personne. Que je puisse mettre à jour mon registre.",
    ["O"] = "Voyager jusqu\'à l\'école de Ruisselune et rapporter des informations récentes sur Stalvan au Greffier Daltry.",
    ["T"] = "La légende de Stalvan",
  },
  [68] = {
    ["D"] = "Vous trouvez une lettre poussiéreuse dans la vieille caisse.",
    ["O"] = "Rapporter la Lettre poussiéreuse au greffier Daltry.",
    ["T"] = "La légende de Stalvan",
  },
  [69] = {
    ["D"] = "Oh non ! J\'ai dû manquer ça la première fois. Dans le registre, juste en dessous de l’adresse notée pour Stalvan, il y en a une autre partiellement effacée. Il semble qu’il s’agisse de l\'auberge de la Fierté du lion à Goldshire. J\'aimerais vérifier cela, $N.",
    ["O"] = "Aller à l\'Auberge de la Fierté du lion pour apprendre si quelqu\'un a entendu parler de Stalvan.",
    ["T"] = "La légende de Stalvan",
  },
  [70] = {
    ["D"] = "Oui, effectivement, je me rappelle de Stalvan !$b$bIl y a quelques années, lors d\'une nuit d\'orage, un messager est venu, à la recherche d\'un abri pour la nuit. Vers minuit, l\'homme a dévalé les escaliers en hurlant, le visage terrifié. Il était toujours en pyjama quand il a disparu en courant sous la pluie torrentielle.$b$bDans sa précipitation, il a oublié ses lettres dans le coffre à l\'étage. Il n\'est jamais revenu. Une des lettres était adressée au quartier du Canal à Stormwind. Prenez-la si vous le voulez.",
    ["O"] = "Retrouver la Vieille lettre de Stalvan dans le coffre et la remettre au quartier du Canal à Stormwind.",
    ["T"] = "La légende de Stalvan",
  },
  [71] = {
    ["D"] = "Maintenant que vous avez les deux médaillons, apportez-les au Garde Thomas au Pont est, il saura peut-être comment ces hommes ont été assassinés.",
    ["O"] = "Rapporter les Médaillons de Rolf et Malakai au garde Thomas au pont est d’Elwynn.",
    ["T"] = "Faire son rapport au garde Thomas",
  },
  [72] = {
    ["D"] = "Mon père fut le gardien de cette propriété bien avant que je ne le sois. Il a dû tout nettoyer après le massacre.$b$bLes derniers fonds des Flintridge ont fondu comme neige au soleil. Le reste des affaires de la famille va partir aux enchères. La faute à ces vautours des impôts. J\'imagine que vous êtes $gimpatient:impatiente; de voir ce qu\'il y a, vous pouvez regarder dans le tas de vieilleries. Si ça se trouve, quelque chose vous intéressera.",
    ["O"] = "Rechercher un indice dans le tas d\'affaires.",
    ["T"] = "La légende de Stalvan",
  },
  [73] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<TXT> No Reward",
  },
  [74] = {
    ["D"] = "Dans la caisse vous trouvez diverses choses : quelques objets moisis, un portrait de famille, quelques trophées de chasse et de vieux livres. Dans le fond, sous un vase en céramique, vous découvrez une Page de journal déchirée.",
    ["O"] = "La Page de journal déchirée que vous avez découverte est votre deuxième indice.",
    ["T"] = "La légende de Stalvan",
  },
  [75] = {
    ["D"] = "Quand j\'ai emménagé ici, il y avait une liasse de lettres dans le coffre en haut. Je les ai examinées en arrivant, mais j\'ai abandonné quand mes yeux ont commencé à perdre leur acuité.$b$bFaites une faveur à un vieil homme aveugle, et allez voir dans le coffre à l\'étage si vous trouvez quelque chose qui puisse vous aider à en savoir plus sur cet homme, Stalvan. Je suis presque sûr qu\'il y a une vieille page de journal qui peut avoir un intérêt pour vous. Ramenez-la-moi et je vous aiderai autant que possible.",
    ["O"] = "Le Maréchal Haggard veut que vous alliez chercher la Vieille page de journal dans le coffre à l\'étage.",
    ["T"] = "La légende de Stalvan",
  },
  [76] = {
    ["D"] = "Merci, nous savons désormais que la mine de Fargodeep est infestée de kobolds. Nous avons besoin maintenant d\'un éclaireur pour explorer la mine la plus éloignée, celle de Jasperlode.$B$BExplorez-la et confirmez la présence de kobolds. Pour parvenir à la mine, allez vers l\'est le long de la route, jusqu\'à la tour d\'Azora. De là prenez vers le nord et vous tomberez sur la mine dans les collines.",
    ["O"] = "Explorer la Mine de Jasperlode, puis faites votre rapport au maréchal Dughan de Goldshire.",
    ["T"] = "La mine de Jasperlode",
  },
  [77] = {
    ["D"] = "Je possède l\'emballage d\'origine nécessaire pour conditionner la Houle que Dran Droffers recherche. Je vais vous aider à l\'emballer pour le père de Malton, à l\'humeur si caustique... contre deux bouteilles de la susnommée Houle. Les bouteilles se trouvent dans une caisse, dans une base abandonnée de la Horde, près du rocher de l\'Affût.$B$BLa tâche sera ardue : d\'immondes gelées et même des limons infestent les ruines. On dit que ces créatures seraient commandées par une sorte de limon en chef. Quoiqu\'il en soit, procurez-vous la Houle et revenez me voir ici pour la conditionner.",
    ["O"] = "Récupérer 10 bouteilles de Houle au miel des Hinterlands dans des caisses se trouvant aux environs de la base abandonnée de la Horde, au rocher de l\'Affût.",
    ["T"] = "Une situation délicate",
  },
  [78] = {
    ["D"] = "Je connais quelqu’un qui peut peut-être vous aider. Quand je commandais les gardes de Stormwind, nous avions l\'habitude d\'aller boire des coups à la taverne du Corbeau écarlate à Darkshire. L\'aubergiste, Smitts, connaît nombre d\'histoires sur la région. Montrez-lui cette page et voyez ce qu\'il peut vous en dire.",
    ["O"] = "Prendre la Vieille page de journal et la montrer à Smitts l\'aubergiste.",
    ["T"] = "La légende de Stalvan",
  },
  [79] = {
    ["D"] = "Je m\'intéresse à l\'histoire de ce personnage, Stalvan, depuis des années. Le jour où ces nobles de passage furent massacrés, il y a quelques années, je suis venu avec Haggard pour enquêter. J\'ai trouvé ces pages boueuses, mais je n\'ai jamais pu faire le lien avec l\'écriture de cet homme fou dans les bois. Ce que vous m\'apportez là, prouve de toute évidence qu\'il est coupable. Portez immédiatement cela au commandant Ebonlocke et racontez-lui ce que vous avez découvert !",
    ["O"] = "Porter les pages au commandant Ebonlocke.",
    ["T"] = "La légende de Stalvan",
  },
  [80] = {
    ["D"] = "J\'avais déjà l\'œil sur ce sale type, Stalvan. Mais si les écritures concordent, alors cela prouve sans l\'ombre d\'un doute qu\'il est coupable.$b$bAvec toutes ces journées à tenir le registre de la ville, le Greffier Daltry est devenu un expert dans l\'identification des écritures. Montrez-lui ces pages pour vérifier si les écritures concordent avec la signature présente sur le registre.",
    ["O"] = "Montrer au Greffier Daltry la Page de journal maculée de sang.",
    ["T"] = "La légende de Stalvan",
  },
  [81] = {
    ["D"] = "J\'ai pris la peine de mettre à l\'abri dans le tonneau, en plus de la houle emballée de Dran, les bouteilles supplémentaires qu\'il veut faucher pour son propre compte. Je n\'aime pas beaucoup Dran, mais il n\'y a rien de pire que de la houle éventée. Ne vous donnez pas la peine de me remercier.$B$BAllez maintenant livrer le baril à Dran Droffers, à sa boutique d\'Orgrimmar. Et passez le bonjour à Malton ! Dites-lui que s\'il cherche d\'excellentes boissons et de sympathiques compagnons de beuverie, nous serions honorés de recevoir sa visite.",
    ["O"] = "Livrer le tonneau à Dran Droffers à Orgrimmar.",
    ["T"] = "La livraison de Houle",
  },
  [82] = {
    ["D"] = "Très bien, maintenant ces rapports à propos des insectes... Ce que nous savons, c\'est qu\'ils drainent l\'eau du réservoir qui se trouvait à l\'emplacement du Repaire nuisible. Ce que nous ne savons PAS... Tout le reste.$B$BEssayons tout d\'abord de savoir qui est notre ennemi ! Je veux que vous alliez au Repaire nuisible et rameniez des bouts de ces bestioles. Récupérez cinq morceaux de ces Centipaars et portez-les à l\'alchimiste Pestlezugg pour qu\'il voit de quoi il retourne. Avec lui sur l\'affaire, nous le saurons vite !",
    ["O"] = "Apporter cinq morceaux de Centipaars du Repaire nuisible à l\'Alchimiste Pestlezugg à Gadgetzan.",
    ["T"] = "Enquête au Repaire nuisible",
  },
  [83] = {
    ["D"] = "Les Défias du Northshire portent de simples masques, mais ceux d\'Elwynn portent des masques en lin, une belle matière avec laquelle je pourrais faire de très beaux articles.$B$BRapportez-moi des masques rouges en lin et je les utiliserai pour vous confectionner quelque chose.$B$BLes membres de la Confrérie défias ont des campements disséminés à travers tout Elwynn.",
    ["O"] = "Apporter 6 Masques rouge en lin à Sara Timberlain au Camp de bûcherons du Val d\'est.",
    ["T"] = "Articles rouges en lin",
  },
  [84] = {
    ["D"] = "Voilà. Quand vous donnerez cette tourte à Billy, dites-lui de s’étouffer avec !",
    ["O"] = "Porter la Tourte de panse de porc à Billy Maclure aux Vignes des Maclure.",
    ["T"] = "Revenir voir Billy",
  },
  [85] = {
    ["D"] = "J\'ai perdu mon collier, et je pense que c\'est ce garnement de Billy Maclure qui l\'a pris ! Généralement il vagabonde dans les Vignes des Maclure, à l\'est d\'ici.$B$BRapportez-moi mon collier, et vous réchaufferez le cœur d\'une pauvre veuve.",
    ["O"] = "Parler à Billy Maclure.",
    ["T"] = "Le collier perdu",
  },
  [86] = {
    ["D"] = "Si je mangeais une tourte, je pourrais peut-être vous dire qui a ce collier. Vous savez, je pense que cette vieille dame du nom de Bernice, dans l\'autre ferme, fait de formidables tourtes de panse de porc...$B$BSi vous lui apportiez quelques morceaux d’un des sangliers qui traînent autour de notre ferme, et si vous lui expliquiez toute l’histoire, alors elle vous préparerait une tourte.",
    ["O"] = "Apporter 4 morceaux de Viande de sanglier à Tatie Bernice Stonefield à la ferme des Stonefield.",
    ["T"] = "Une tourte pour Billy",
  },
  [87] = {
    ["D"] = "Je jouais près de la mine de Fargodeep, et je pense que j\'ai fait tomb... Euuuuh... Je veux dire que j\'ai vu le collier de la vieille dame. Ne me demandez pas comment il est arrivé là... Ce n\'est pas moi qui l\'ai pris !$B$BQuoi qu\'il en soit, j\'ai vu cet énorme kobold avec une dent en or le prendre et s\'enfuir vers la mine. Allez là-bas, trouvez le kobold et vous trouverez le collier, c\'est juré !",
    ["O"] = "Rapporter le Collier de Bernice à \"Tatie\" Bernice Stonefield à la ferme des Stonefield.",
    ["T"] = "Dent-d\'or",
  },
  [88] = {
    ["D"] = "Les Brackwell ont un cochon qui a gagné des tas de prix, Princesse. C\'est une ENORME truie, que je soupçonne de dévaster mon potager !$B$BAvant qu\'elle ne s’intéresse à mes champs... La bête doit mourir ! Rapportez-moi son collier pour preuve de son exécution, et je vous récompenserai pour votre travail !$B$BPrincesse dîne généralement du côté du Champ de potirons des Brackwell, à l\'est, après la ferme des Maclure. Chopez-la avant qu\'elle commence à avoir faim, et ne revienne ici !",
    ["O"] = "Tuer Princesse, prendre son collier, puis le porter à Maman Stonefield à la ferme des Stonefield.",
    ["T"] = "Princesse doit mourir !",
  },
  [89] = {
    ["D"] = "Lors de la dernière invasion des orcs, nous avons été obligés de fondre tout notre fer pour fabriquer des balles, des armures et des épées. Nous avons envoyé une demande de ravitaillement à Stormwind, mais une bande de gnolls des Carmines a détourné la caravane puis s\'est enfuie dans les collines au-delà de Lakeshire.$B$BNous allons bientôt être à court de matériel pour reconstruire ce pont. Si vous pouviez me rapporter 5 clous en fer et 5 rivets en fer, $N, je ferais en sorte que l\'on vous récompense.$B$BAllez, au travail !",
    ["O"] = "Apporter 5 Clous en fer et 5 Vis en fer au contremaître Oslow à Lakeshire.",
    ["T"] = "Le pont sur le lac",
  },
  [90] = {
    ["D"] = "La spécialité de la maison est le kébab de loup assaisonné. Bien sûr, je peux vous en faire ! Mais j\'ai besoin de différents ingrédients. Apportez-moi 10 Steaks de loup ainsi qu\'un peu d\'Herbes aromatiques de Stormwind. Il y a tellement de loups dans la forêt que cela ne devrait pas être un problème pour trouver la viande. Pour ce qui est des herbes, allez donc voir Felicia Gump, la fleuriste du quartier du canal à Stormwind.",
    ["O"] = "Récupérer 10 Steaks de loup et des Herbes aromatiques de Stormwind, puis retourner voir le chef Grual à Darkshire.",
    ["T"] = "Kébab de loup assaisonné",
  },
  [91] = {
    ["D"] = "Un ennemi de Stormwind s\'est installé dans les collines à l’est des Carmines. D’infâmes voleurs utilisent les Arcanes pour répandre la terreur et la misère sur notre ville et ses habitants. Derrière cela, il y a une personne, Morganth ; c\'est lui qui dirige cette entreprise maléfique.$b$bMorganth contrôle maintenant le clan des gnolls Sombrepoil, qu’il utilise pour ses exactions. De par la loi de Lakeshire, aider un ennemi du royaume est passible de la peine de mort.$b$bCes infâmes gnolls doivent être tués !$b$bRapportez-moi 10 pendentifs sombrepoil, et vous serez $grécompensé:récompensée;.",
    ["O"] = "Apporter 10 Pendentifs sombrepoil au Bailli Conacher à l\'Hôtel de ville de Lakeshire.",
    ["T"] = "La loi de Solomon",
  },
  [92] = {
    ["D"] = "Rien ne me ferait plus plaisir que de vous faire du goulash des Carmines. Mais il y a un petit problème. Lors la dernière attaque des orcs, j\'ai dû nourrir la totalité des troupes de Stormwind. Mon garde-manger est complètement vide. Mais si vous me ramenez les ingrédients, je me ferai un plaisir de vous en préparer.$b$bIl me faut seulement 5 morceaux de Viande de condor coriace, 5 Groins de grands Broche-tripes et 5 portions de Chair croustillante d\'araignée.",
    ["O"] = "Le chef Breanna de Lakeshire veut 5 morceaux de Viande de condor coriace, 5 Groins de grands Broche-tripes et 5 portions de Chair croustillante d\'araignée.",
    ["T"] = "Goulash des Carmines",
  },
  [93] = {
    ["D"] = "Je vais vous confier un petit secret : mon gâteau au \"crabe\" brun est en réalité fait de pattes d\'araignée ! C\'est vrai que cela semble un peu répugnant, mais leur goût est merveilleux ! Apportez-moi des Pattes d\'araignées fondantes et je vous en préparerai quelques-uns.$B$BVous en trouverez sur les araignées Tisse-venin qui nichent au nord, entre les collines et la rivière.",
    ["O"] = "Récupérer 6 Pattes d\'araignée fondantes et les porter au chef Grual de Darkshire.",
    ["T"] = "Gâteaux au crabe brun",
  },
  [94] = {
    ["D"] = "Mon adversaire, Morganth, est un puissant mage vicieux qui vit dans la Tour d\'Ilgalar dans les Carmines. J\'ai élaboré un moyen pour l\'espionner, l\'Œil d\'Azora, et je peux maintenant l\'observer de loin.$B$BMais pour connaître ses pensées, j\'ai besoin d\'assistance.$B$BSi vous le voulez, portez mon Glyphe d\'Azora à la Tour d\'Ilgalar. Placez-le sur la Statue de lion près de la tour.$B$BLorsque cela sera fait, j\'aurai une vision des plans futurs de Morganth.",
    ["O"] = "Placer le Glyphe d\'Azora sur la Statue de lion près de la Tour d\'Ilgalar dans les Carmines.",
    ["T"] = "L\'œil attentif",
  },
  [95] = {
    ["D"] = "Je travaillais dans une ferme, au sud-est... Des cavaliers noirs du défilé de Deuillevent sont venus dans ma ferme et ont massacré toute ma famille alors que j\'étais absent !$B$BQuand je suis rentré, j\'ai vu une silhouette indistincte rôder près de ma grange, en train d\'enterrer quelque chose. Il s\'est enfui avant que j\'aie pu l\'attraper, et je n\'ai pas pu m\'attarder, car je suis parti aussitôt à sa poursuite. Du coup, je n\'ai pas vu ce qu\'il avait caché.$B$BSi vous pouviez découvrir ce qu\'a enterré le cavalier noir, je vous en serai reconnaissant. L\'endroit exact est derrière la vieille souche d\'arbre près de la grange.",
    ["O"] = "Aller à la ferme de Sven et trouver ce qui a été enterré derrière la vieille souche d\'arbre.",
    ["T"] = "La vengeance de Sven",
  },
  [96] = {
    ["D"] = "Prendre l\'éclat. Prendre l\'éclat et donner la vie.$B$BDonner la vie et comprendre. Comprendre que l\'eau, c\'est la vie... La corruption détruit la vie.$B$BLe pouvoir de l\'eau protège et donne la vie.",
    ["O"] = "Apporter l\'Éclat d\'eau à Islen Waterseer dans les Tarides.",
    ["T"] = "L\'appel de l\'eau",
  },
  [97] = {
    ["D"] = "Prévenez immédiatement le commandant Ebonlocke que les écritures concordent, $N ! Ses soupçons étaient fondés !",
    ["O"] = "Prévenir le commandant Ebonlocke.",
    ["T"] = "La légende de Stalvan",
  },
  [98] = {
    ["D"] = "Stalvan Mistmantle est un dépravé. De nombreuses victimes ont perdu la vie entre ses mains. Il est sans aucun doute coupable d\'un nombre incalculable de crimes et de méfaits. Et maintenant ce dément menace Darkshire. Seule la Lumière sait quels actes abjects il prépare. Allez jusqu\'à sa chaumière au nord de la ville, $c, et exécutez Stalvan.$b$bLorsque ce sera fait, allez voir Madame Eva et montrez-lui son anneau familial. Après tout, c\'est elle qui a eu la prémonition qui nous a conduits jusqu\'à cette terrible découverte. Mais Darkshire est désormais plus sûr grâce à elle.",
    ["O"] = "Tuer Stalvan Mistmantle et montrer son anneau familial à Madame Eva.",
    ["T"] = "La légende de Stalvan",
  },
  [99] = {
    ["D"] = "Mon mépris pour ce malheureux fou d\'Arugal augmente à mesure que je déchiffre son enchantement. J\'ai presque fini mes recherches pour y parvenir.$b$bMa compréhension sera totale quand j\'aurai compris ce qui cause l\'étrange comportement des habitants de Bois-du-Bûcher. Le jour, les paysans sont des gens normaux. Mais la nuit venue ils se transforment en worgens Ragelune.$b$bJe dois puiser l\'énergie des chaînes qu\'a enchantées Arugal. Rapportez-moi 6 chaînes enchantées de Bois-du-Bûcher, $N.",
    ["O"] = "Rapporter 6 Chaînes enchantées de Bois-du-Bûcher à Dalar Dawnweaver au Sépulcre.",
    ["T"] = "La folie d\'Arugal",
  },
  [100] = {
    ["D"] = "À la fin de votre incantation une explosion lumineuse se produit. Le grésillement des gouttes dans les flammes s\'est arrêté, et vos sens reviennent à mesure que les Esprits corrompus autour de vous sont détruits.",
    ["O"] = "Parler à la Manifestation d\'eau inférieure dans la forêt des Pins argentés.",
    ["T"] = "L\'appel de l\'eau",
  },
  [101] = {
    ["D"] = "À mesure que la corruption mystique rampe à travers la forêt, la nécessité d’une protection devient indispensable, $N. Les vents m\'ont murmuré qu\'un grand danger vous guette dans un avenir proche.$b$bSi vous voulez vous garder de ce danger, noble $c, apportez-moi 10 Crocs de goule, 10 Doigts de Squelette et 5 Fioles de venin d\'araignée. J’enchanterai alors un Totem d\'affliction qui blessera ceux qui tenteront de vous agresser.",
    ["O"] = "Apporter 10 Crocs de goule, 10 Doigts de Squelette et 5 Fioles de venin d\'araignée à Madame Eva à Darkshire.",
    ["T"] = "Le Totem d\'affliction",
  },
  [102] = {
    ["D"] = "Stormwind nous a abandonnés. Un vent de corruption souffle sur la marche de l\'Ouest. C\'est ma terre natale et je n\'abandonnerai pas les gens qui ont choisi de rester ici. Nous, les anciens fermiers, nous résisterons.$b$bVotre mission, si vous l\'acceptez, sera de patrouiller dans les prairies de la marche. Débusquez et tuez les infâmes gnolls qui apparemment sont de connivence avec les voleurs des Mortemines. Apportez-moi 8 Pattes de gnolls et je vous récompenserai pour votre courage.",
    ["O"] = "Rapportez 8 Pattes de gnolls au capitaine Danuvin à la Colline des sentinelles.",
    ["T"] = "Patrouilles dans la marche de l\'Ouest",
  },
  [103] = {
    ["D"] = "Je me souviens de cette terrible nuit, au cours de laquelle la famille du gardien de phare a été tuée. J\'ai contemplé, impuissant, l’attaque du Vieux Troubloeil. Mais bon, ce qui est fait est fait. Ma préoccupation maintenant est la sécurité des marins et des navires qui croisent sur la Grande mer, car la zone est très dangereuse près des récifs le long de la côte. Vu qu\'il n\'y a plus personne pour s\'occuper du phare, la tâche m\'incombe à présent de surveiller sa flamme.$b$bAidez-moi à garder la flamme allumée, apportez-moi 5 Flasques d\'huile provenant des monstres des moissons.",
    ["O"] = "Apporter 5 Flasques d\'huile au capitaine Grayson, au phare de l\'Ouest",
    ["T"] = "Le gardien de la flamme",
  },
  [104] = {
    ["D"] = "Quand ma vie a pris fin sur ces rochers, je ne savais pas que je pouvais espérer quelque chose après la mort. Cette nuit-là, le phare était éteint parce que le Vieux Troubloeil avait fait fuir la famille qui s\'en occupait. Mais les gens sont revenus et ont rallumé la flamme. Alors le Vieux Troubloeil a contraint ces faibles d\'esprit de murlocs à attaquer le phare. Cette fois-ci, la famille n\'a pas eu le temps de fuir, et elle a été massacrée par ces ignobles créatures, sans que personne ne puisse intervenir.$b$bTuez le Vieux Troubloeil si vous le rencontrez le long de la plage, et rapportez-moi une de ses écailles, je vous récompenserai.",
    ["O"] = "Rapporter une Ecaille du Vieux Troubloeil au capitaine Grayson au phare de l\'Ouest.",
    ["T"] = "Menace sur la côte",
  },
  [105] = {
    ["D"] = "Hélas, le temps est venu d\'attaquer Andorhal et d\'en finir avec la liche qui la contrôle !$B$BAraj l\'Invocateur, la liche, règne sur les ruines de la cité. Il est gardé par de nombreuses créatures du Fléau qui encerclent les restes du centre de la ville, et sa garde personnelle, qui lui est directement attachée, est redoutable. Vous aurez besoin de plusieurs alliés pour avoir la moindre chance de le vaincre.$B$BExécutez Araj, $N, et apportez-moi un éclat de son phylactère comme preuve !",
    ["O"] = "Apporter l’Éclat phylactère d\'Araj au Grand exécuteur Derrington à la Barricade, dans les Maleterres de l’ouest.",
    ["T"] = "Hélas, Andorhal",
  },
  [106] = {
    ["D"] = "Je suis maudite ! Mon cœur appartient à Tommy Joe Stonefield, mais nos familles sont ennemies. Du coup, je ne peux pas le voir, et pourtant je languis d’admirer son magnifique visage !$B$BS\'il vous plaît, portez-lui cette lettre de ma part. Généralement, il va à la rivière à l\'ouest de la ferme des Stonefield, qui se trouve à l\'ouest d\'ici.",
    ["O"] = "Donner la Lettre d\'amour de Maybell à Tommy Joe Stonefield.",
    ["T"] = "Jeunes amoureux",
  },
  [107] = {
    ["D"] = "Je suis sûre que William Pestle a une potion qui permettrait de réunir ces deux amoureux !$B$BTenez, prenez cette note et portez-la à William. Il habite à l’auberge de la Fierté du lion à Goldshire.",
    ["O"] = "Porter la Note de Mamie Stonefield à William Pestle.",
    ["T"] = "Note pour William",
  },
  [108] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<TXT> Mystery Reward",
  },
  [109] = {
    ["D"] = "J\'ai l\'impression que vous êtes rompu au combat, $c. Si vous ne l\'avez pas déjà fait, vous devriez aller voir Gryan Stoutmantle. Il dirige la Milice du peuple, dont le but est de défendre les fermes de la marche de l\'Ouest. Je suis sûr qu\'il aurait besoin de vous. Il se trouve généralement à la Tour de pierre sur la Colline des sentinelles, près de la route au centre de la marche de l\'Ouest.",
    ["O"] = "Parler à Gryan Stoutmantle. Il se trouve à la Tour de pierre sur la Colline des sentinelles, près la route au centre de la marche de l\'Ouest.",
    ["T"] = "Aller voir Gryan Stoutmantle",
  },
  [110] = {
    ["D"] = "Je vais passer un peu de temps à examiner l\'équipement d\'analyse de Scrimshank... et remettre de l\'ordre dans ses effets personnels. J\'ai le sentiment que ces insectes vont nous faire subir bien d\'autres pertes.$B$BPourriez-vous aller voir si l\'Alchimiste Pestlezugg a analysé les morceaux d\'insectes que nous lui avons envoyés. S\'ils sont prêts, rapportez-les moi.",
    ["O"] = "Aller voir à Gadgetzan si l\'Alchimiste Pestlezugg a terminé d\'analyser les morceaux d\'insectes.",
    ["T"] = "Analyse des morceaux d\'insectes",
  },
  [111] = {
    ["D"] = "S\'il vous plaît, $N, allez parler à Mamie. Si quelqu’un peut trouver une solution à notre problème, c\'est bien elle.$B$BElle habite dans notre ferme à l\'est d\'ici.",
    ["O"] = "Parler à Mamie Stonefield.",
    ["T"] = "Discuter avec Mamie",
  },
  [112] = {
    ["D"] = "Je peux préparer une potion d\'invisibilité pour Maybell, qui lui permettra de s\'échapper du vignoble des Maclure et de rejoindre Tommy Joe. Mais pour préparer cette potion, j\'ai besoin de varech cristallin.$B$BCette plante pousse habituellement dans l\'océan, mais parfois les murlocs en récoltent. Il faut aller voir si les murlocs près du lac de Cristal n\'en auraient pas en ce moment. Le lac de Cristal est à l\'est de Goldshire.",
    ["O"] = "Apporter 4 Feuilles de Varech cristallin à William Pestle de Goldshire.",
    ["T"] = "À la recherche du varech",
  },
  [113] = {
    ["D"] = "Le grand géomètre Fizzledowser doit être informé immédiatement. En fait... toute personne sachant lire doit voir ce rapport. Nous sommes confrontés à une menace très sérieuse et très grave non seulement pour Tanaris et Gadgetzan, mais si on ne s\'en occupe pas, pour tout le Sud de Kalimdor. Oups, et même pour tout Azeroth ; cette menace n\'a donné aucun signe de déclin.$B$BDemandez à Fizzledowser de faire des copies du rapport pour que vous le distribuiez le plus largement possible. Tout le monde doit être informé de cette menace !",
    ["O"] = "Porter le rapport au grand géomètre Fizzledowser à Gadgetzan. Obtenir une copie de ce rapport, comme l\'alchimiste Pestlezugg l\'a demandé.",
    ["T"] = "Analyse des morceaux d\'insectes",
  },
  [114] = {
    ["D"] = "Prenez cette potion d\'invisibilité pour la jeune Maybell. Les effets devraient être suffisamment longs pour qu\'elle puisse rendre visite à Tommy Joe.",
    ["O"] = "Porter la Potion d\'invisibilité à Maybell Maclure.",
    ["T"] = "La fuite",
  },
  [115] = {
    ["D"] = "Les orcs Blackrock ont engagé des exhalombres pour les aider dans leurs attaques contre les Carmines ; ils ont emporté des orbes de minuit. Ces orbes ont infligé de graves dégâts aux défenses des Carmines et il est impératif que nous ne soyons plus confrontés à leur puissance.$B$BRetrouvez et rapportez-moi les orbes de minuit, après avoir tué les exhalombres Blackrock qui les possèdent. Je détruirai ces orbes ; le monde ne se portera que mieux sans eux !",
    ["O"] = "Apporter 3 Orbes de minuit au maréchal Marris à Lakeshire.",
    ["T"] = "Magie des Ténèbres",
  },
  [116] = {
    ["D"] = "Je suis dans une situation désespérée, $N. Mes réserves sont presque à sec. La dernière cargaison de boisson aurait dû arriver il y a bien longtemps. L\'invasion des orcs a été un enfer. $b$bPeut-être pouvez-vous travailler pour moi ?$b$bJ\'ai besoin que vous récupériez un tonneau de Thunderbrew de chez Grimbooze Thunderbrew dans les collines de la marche de l\'Ouest, un tonneau de merlot de Stormwind, une bouteille de tord-boyaux de Darkshire et une outre de rhum doux de Goldshire. Rapportez-les-moi et je ferai en sorte que vous soyez $grécompensé:récompensée;.",
    ["O"] = "Le tavernier Daniels de Lakeshire a besoin d\'un Tonneau de Thunderbrew, d\'un Tonneau de merlot, d\'une Bouteille de tord-boyaux et d\'une Outre de rhum doux.",
    ["T"] = "Régime sec",
  },
  [117] = {
    ["D"] = "Allez-donc, $gmon ami:mon amie;, sans tarder hâtez-vous !$bPour que notre breuvage, fidèle à mes dosages,$bTienne plus de la bière que d’un mauvais ragoût,$bIl me manque du houblon pour en faire le brassage.",
    ["O"] = "Apporter 5 Pieds de Houblon à Grimbooze Thunderbrew pour qu\'il prépare son breuvage spécial.",
    ["T"] = "La Tonnebière",
  },
  [118] = {
    ["D"] = "J\'ai utilisé tellement de métal pour équiper les Gardes de Stormwind que je n\'en ai même plus pour ferrer les chevaux !$B$BApportez cette note à Argus le forgeron de Goldshire. Elle explique mon problème et lui demande une livraison de fers à cheval.",
    ["O"] = "Porter la Note de Verner à Argus le forgeron de Goldshire.",
    ["T"] = "Le prix des fers",
  },
  [119] = {
    ["D"] = "D\'accord, je lui envoie 50 fers à cheval… $B$BLes voilà, tout empaquetés et prêts à emporter ! Dites à Verner que c’est un plaisir de faire affaire, et faites en sorte qu\'il trouve la facture accrochée à la caisse !",
    ["O"] = "Retourner chez Verner Osgood dans les Carmines et lui donner la caisse de Fers à cheval.",
    ["T"] = "Le retour chez Verner",
  },
  [120] = {
    ["D"] = "Nous vivons un moment crucial, $c. Le bourg est assiégé. Sans renforts, nous allons succomber. Le rapport dont je vous charge est de la plus haute importance. Remettez-le au général Marcus Jonathan de Stormwind le plus rapidement possible. Dès que vous le lui aurez remis, revenez avec sa réponse, qu\'elle soit bonne ou mauvaise. $b$bMaintenant hâtez-vous !",
    ["O"] = "Le magistrat Solomon vous a remis un rapport qui doit être transmis au général Marcus Jonathan de Stormwind. Il exige que vous reveniez vers lui aussitôt que vous aurez donné le message.",
    ["T"] = "Un message pour Stormwind",
  },
  [121] = {
    ["D"] = "Pendant que j\'attends la réponse du roi, je veux que vous portiez cette lettre au Magistrat Solomon.$b$bRompez, $c !",
    ["O"] = "Porter la réponse du général Marcus Jonathan au magistrat Solomon, à Lakeshire.",
    ["T"] = "Un message pour Stormwind",
  },
  [122] = {
    ["D"] = "J\'ai besoin d\'écailles ventrales de dragonnet noir pour payer les fers qu\'Argus m\'a envoyés de Goldshire. Si vous pouviez m\'en trouver six, je réglerais ma dette... et il m\'en resterait suffisamment pour vous façonner quelque chose.$B$BLes dragonnets noirs errent souvent au sud de Lakeshire, mais ils aiment aussi se balader. Vous devrez vous embusquer et les chasser quand vous les verrez.",
    ["O"] = "Rassembler 6 Ecailles ventrales de dragonnet noir et les apporter à Verner Osgood dans les Carmines.",
    ["T"] = "Les écailles de dragonnet",
  },
  [123] = {
    ["D"] = "Cette note est un tableau des heures et des jours auxquels une personne – décrite comme le \"Collecteur\" – doit recevoir une livraison d\'or en provenance des mines de la forêt d\'Elwynn. $B$BD\'après le tableau, il semblerait que le Collecteur habite près du Champ de potirons des Brackwell, à l\'est d\'Elwynn. $B$BC\'est une information importante. Vous devriez en faire part au maréchal Dughan de Goldshire.",
    ["O"] = "Aller voir le maréchal Dughan, à Goldshire, et lui donner le Plan de route du Collecteur.",
    ["T"] = "Le collecteur",
  },
  [124] = {
    ["D"] = "Comme si les attaques des orcs ne suffisaient pas ! Maintenant j\'ai des brutes et des mystiques gnolls qui rôdent au nord de mon écurie et qui massacrent mes chevaux dès qu\'ils s\'éloignent...$B$BSi vous pouviez nous débarrasser de ces gnolls, mes chevaux et moi vous en serions très reconnaissants.",
    ["O"] = "Tuer 10 Brutes des Carmines et 8 Mystiques des Carmines, puis retourner chez Verner Osgood.",
    ["T"] = "Le hurlement des gnolls",
  },
  [125] = {
    ["D"] = "J\'apprécierais vraiment votre aide, $N. La ville est assiégée, l\'approvisionnement devient difficile. On devait me livrer des outils par chariot depuis Goldshire, mais le pont a sauté. Nous avons alors mis les outils dans un bateau, mais les orcs l\'ont touché avec une catapulte. Évidemment, ma boîte d\'outils a coulé tout droit au fond du lac.$b$bRécupérez-la, $N, et je ferai en sorte que vous ne le regrettiez pas.",
    ["O"] = "Le contremaître Oslow de Lakeshire veut que vous récupériez sa Boîte d\'outils au fond du lac Placide.",
    ["T"] = "Les outils perdus",
  },
  [126] = {
    ["D"] = "$N, les gnolls sont toujours là. Je peux entendre leurs hurlements quand ils chassent dans les collines de Lakeshire. Et l\'un d\'eux hurle plus fort que les autres : Couineur, leur chef.$B$BTuez Couineur et dispersez sa meute ! Rapportez-moi sa patte comme preuve.$B$BMais faites attention ! Ce gnoll n\'est pas leur chef pour rien ! Ce n\'est pas un bâtard !",
    ["O"] = "Rapporter la patte de Couineur à Verner Osgood à Lakeshire.",
    ["T"] = "Les collines aux hurlements",
  },
  [127] = {
    ["D"] = "Le lac Placide est connu pour son poisson-lune. C\'est un poisson qui se vend bien et j\'ai justement besoin d\'argent !$B$BRapportez-m\'en 10 ; je paierai bien. Si vous ne savez pas pêcher, peut-être pourrez-vous en voler aux murlocs !",
    ["O"] = "Rapporter 10 Poissons-lunes au Chef des docks Baren à Lakeshire.",
    ["T"] = "Criée aux poissons",
  },
  [128] = {
    ["D"] = "Le clan Blackrock doit être démantelé. L\'Ennemi a envahi nos terres. Ces montagnes sont cependant sous le règne souverain du roi de Stormwind. Nos sources nous indiquent que les chefs de chaque unité du clan sont appelés des champions. Vous les trouverez dans leur campement au nord, juste à l\'ouest de la route menant aux Steppes Ardentes.$B$BLe magistrat Solomon veut que ces brutes abjectes soient tuées. Tuez 15 Champions Blackrock et revenez me voir.",
    ["O"] = "Tuez 15 Champions Blackrock et le garde Howe de Lakeshire vous récompensera.",
    ["T"] = "La prime Blackrock",
  },
  [129] = {
    ["D"] = "Vous pouvez me faire une faveur ? J\'ai préparé un déjeuner pour le Garde Parker, mais il est en patrouille... C\'est un homme grand et fort, un Garde de Stormwind, qui peut se défendre, mais pour une citadine comme moi, c\'est beaucoup trop dangereux dehors.$B$BSi vous lui apportez ce repas à ma place, revenez me voir, je vous offre un déjeuner gratuit ! Parker patrouille le long de la route qui mène au bois de la Pénombre.",
    ["O"] = "Apporter son déjeuner au Garde Parker. Il patrouille sur la route qui mène au Darkshire.",
    ["T"] = "Un repas gratuit",
  },
  [130] = {
    ["D"] = "Puisque vous retournez voir Darcy, pouvez-vous lui apporter des fleurs de ma part ? Vous en trouverez chez l\'herboriste, quand vous serez de retour au Lakeshire, à l\'extrémité ouest de la ville.$B$BMais quand vous prendrez les fleurs, ne dites pas à l\'herboriste, Martie, qui les achète, ni pour qui elles sont...",
    ["O"] = "Parler à Martie Jainrose, l\'Herboriste des Carmines.",
    ["T"] = "Une visite chez l\'herboriste",
  },
  [131] = {
    ["D"] = "Voilà votre bouquet. J\'ai choisi des jonquilles pour vous. Ce sont mes fleurs préférées !",
    ["O"] = "Donner à Darcy le bouquet de jonquilles.",
    ["T"] = "Bouquet de jonquilles",
  },
  [132] = {
    ["D"] = "Ce que je vais vous dire peut me coûter la vie. Le gang défias est en train de préparer un gros coup. Il paraît qu\'ils se sont acoquinés avec des gnolls, des kobolds et même des gobelins.$b$bApportez ce message à Stoutmantle. Il donne tous les détails de ce que je sais à ce sujet.",
    ["O"] = "Apporter la Note de Wiley à Gryan Stoutmantle, dans la marche de l\'Ouest",
    ["T"] = "La Confrérie défias",
  },
  [133] = {
    ["D"] = "Je vis tellement loin de la sûreté de la ville que c\'est un miracle que les morts-vivants ne m\'aient pas dévoré ! En fait, juste hier, un groupe de Mâcheurs est venu rôder près de ma maison !$B$BPour les éloigner, je voudrais faire une sorte d’\"épouvantail\" à goules. Il me faut des côtes de goules. On peut se les procurer sur les Dévoreurs, les Mâcheurs, les Mangeurs de cervelle, les Pourris et les Répandeurs de peste.$B$BTrouvez-moi des côtes et je vous donnerais une récompense.",
    ["O"] = "Trouver 7 Côtes de goule. Les apporter à Abercrombie dans sa cabane.",
    ["T"] = "L\'épouvantail à goules",
  },
  [134] = {
    ["D"] = "Il y a quelques semaines, je cueillais des herbes assez loin de chez moi et j\'ai été attaqué par une bande d\'ogres ! Je me suis enfui, en laissant derrière moi une caisse pleine d’herbes et de précieux outils.$B$BLes ogres m\'ont pourchassé un moment, puis ils sont retournés à leur tertre, au sud du bois de la Pénombre. Je suis sûr que ma caisse est là-bas, pas très loin du tertre des ogres.$B$BJe vous en prie, $N, récupérez cette caisse pour moi. Elle me fait gravement défaut.",
    ["O"] = "Rapporter sa caisse à Abercrombie.",
    ["T"] = "Les ogres voleurs",
  },
  [135] = {
    ["D"] = "Je me demande de quoi parlait Wiley quand il a mentionné les Maçons. C\'était peut-être un lapsus ? Les Défias pourraient-ils être reliés aux Maçons ? Le seul qui pourrait le savoir à coup sûr, c\'est Mathias Shaw, le chef du SI:7. Montrez-lui la note de Wiley et voyez s\'il a quelque chose à tirer de ce mystère qui s\'épaissit. Si vous avez du mal à trouver Shaw, cherchez du côté de la caserne de la vieille ville.",
    ["O"] = "Apporter la Note de Wiley à Mathias Shaw à Stormwind.",
    ["T"] = "La Confrérie défias",
  },
  [136] = {
    ["D"] = "Si vous lisez ceci, c\'est que le vieux capitaine Sanders repose sous six pieds de flotte. Mon trésor est à vous. Il vous suffit de suivre le jeu de piste.$b$bD\'abord, trouvez mon coffre. Il doit être à moitié enterré dans le sable, depuis le temps, sur la côte de la marche de l\'Ouest, à côté de l\'épave. Il y a des tas d\'épaves sur la côte, mais une seule ancre rouillée. Qui trouve cette ancre trouve mon coffre ! Et avec le coffre, l\'indice suivant !",
    ["O"] = "Trouver le Coffre du capitaine Sanders. Le fouiller pour trouver l\'indice suivant.",
    ["T"] = "Le trésor caché du capitaine Sanders",
  },
  [137] = {
    ["D"] = "If you earn a commendation from one of our Marshals, then bring it to me.  It will be good for one of our Lion Emblem gloves.",
    ["O"] = "General Marcus Jonathan in Stormwind will accept a commendation from Elwynn Forest.  Bringing him such a document will reward you with a choice of armor.",
    ["T"] = "<NYI> <TXT> Stormwind Commendation",
  },
  [138] = {
    ["D"] = "L\'indice que vous trouvez est le suivant : « Bon travail, matelot ! Maintenant il va falloir cingler vers l\'est. À l\'est, vers le haut des falaises, à l\'est vers la route. Cherchez les ruines des vieilles cheminées, au bord de la route. Là, vous trouverez un vieux tonneau qui contient l\'indice suivant. »",
    ["O"] = "Trouver le Tonneau à côté des cheminées en ruine. Le fouiller pour trouver l\'indice suivant.",
    ["T"] = "Le trésor caché du capitaine Sanders",
  },
  [139] = {
    ["D"] = "En fouillant le tonneau, vous découvrez un autre parchemin. Il y est écrit : « À partir de ce tonneau, tournez-vous vers le nord. Marchez droit comme une bande de cadets qui va au réfectoire, jusqu\'à ce que vous voyiez la cruche vide, à côté du moulin à vent solitaire, sur les falaises. Si vous farfouillez autour de cette cruche, vous pourrez bien trouver ce que vous cherchez. »",
    ["O"] = "Chercher la Cruche vide à côté du moulin à vent pour trouver le prochain indice.",
    ["T"] = "Le trésor caché du capitaine Sanders",
  },
  [140] = {
    ["D"] = "Bien vu ! Il y a un autre indice au fond de la cruche. L\'encre a coulé par endroits et le papier sent le whisky, mais vous arrivez à reconstituer une partie du texte. « Maintenant que vous avez trouvé la vieille cruche de whisky, vous êtes presque au trésor ! Tournez-vous vers l\'ouest et descendez vers le rivage. Ne vous arrêtez pas au bord de l\'eau, continuez ! Nagez vers l\'ouest jusqu\'à l\'île où se trouve mon coffre au trésor ! »",
    ["O"] = "Localiser le Coffre au trésor du capitaine Sanders et prendre votre récompense.",
    ["T"] = "Le trésor caché du capitaine Sanders",
  },
  [141] = {
    ["D"] = "La Guilde des Maçons est dirigée par un homme du nom d\'Edwin VanCleef. C\'est lui qui a rebâti Stormwind après sa destruction par les orcs au cours de la Première Guerre. Apparemment, VanCleef et ses équipes n\'ont pas apprécié la façon dont ils ont été traités par le roi à la fin de la reconstruction. Ça pourrait expliquer une ou deux petites choses.$b$bJ\'ai rédigé un rapport détaillé pour ton maître, dans la marche de l\'Ouest. Apporte-le-lui tout de suite !",
    ["O"] = "Apporter le Rapport de Shaw à Gryan Stoutmantle, dans la marche de l\'Ouest.",
    ["T"] = "La Confrérie défias",
  },
  [142] = {
    ["D"] = "Nous devons débusquer les Défias de leur cache, $N. Mes éclaireurs ont rendu compte qu\'un Messager Défias a été vu sur les routes entre Ruisselune, la carrière de la côte de l\'Or et la mine de Jangolode. Je veux que vous le capturiez. S\'il résiste tuez-le et apportez-moi tout ce que vous trouverez sur lui.",
    ["O"] = "Traquer le Messager défias dans la marche de l\'Ouest et apporter les documents qu\'il transporte à Stoutmantle.",
    ["T"] = "La Confrérie défias",
  },
  [143] = {
    ["D"] = "Il y a quelque chose d\'étrange dans le comportement de l\'armée de Stormwind. Elle devrait se trouver là en force à l\'heure qu\'il est. De toute façon, le temps est un luxe que nous ne pouvons pas nous offrir. Je ne resterai pas là à regarder couler le sang du peuple de Lakeshire sans essayer de trouver de l\'aide. J\'ai entendu dire qu\'une milice populaire s\'était formée dans la marche de l\'Ouest. $b$bApportez cette demande d\'aide à leur chef, Gryan Stoutmantle. Ses hommes pourront peut-être nous apporter leur soutien en attendant que le roi envoie ses réserves.",
    ["O"] = "Le Magistrat Solomon veut que vous portiez sa Demande d\'aide à Gryan Stoutmantle dans la marche de l\'Ouest.",
    ["T"] = "Un message pour la marche de l\'Ouest",
  },
  [144] = {
    ["D"] = "La demande du Magistrat Solomon me peine plus que je ne saurais le dire. Visiblement, il ignore que la guerre a éclaté dans la marche de l\'Ouest, sinon il saurait qu\'aucune aide n\'est à attendre de la Milice. Si Stormwind ne nous avait pas abandonnés elle aussi, nous n\'aurions pas eu besoin de la Milice.$B$BApportez cette réponse à votre maître dans les Carmines, $c. Et dites-lui que mon cœur est lourd à la pensée de la mort de tant de braves.",
    ["O"] = "Retourner voir le Magistrat Solomon avec la Réponse de Stoutmantle.",
    ["T"] = "Un message pour la marche de l\'Ouest",
  },
  [145] = {
    ["D"] = "Je sollicite votre aide une nouvelle fois. J\'ai écrit au seigneur Ebonlocke pour lui demander de m\'envoyer ses gardes les plus expérimentés, les Veilleurs, afin de renforcer les défenses de Lakeshire. Il est primordial que ce message lui soit remis rapidement. Vous devez faire preuve de la plus grande vigilance. Ebonlocke est le maire de Darkshire, au cœur du bois de la Pénombre. Ne vous écartez pas des chemins.",
    ["O"] = "Le magistrat Solomon veut que vous apportiez une lettre au seigneur Ebonlocke de Darkshire.",
    ["T"] = "Un message pour Darkshire",
  },
  [146] = {
    ["D"] = "Si je ne connaissais pas personnellement le Magistrat Solomon, je penserais qu\'il a perdu la tête ! Envoyer les Veilleurs au Lakeshire simplement parce que Stormwind lui refuse son aide ? Nous avons dû créer le corps des Veilleurs justement parce que Stormwind nous avait abandonnés au moment où cette horrible magie a commencé à se répandre. Si j\'envoie mes Veilleurs, notre région sera la proie du Mal avant même que les soldats n’aient atteint la lisière de la forêt.$B$BPortez cette lettre en réponse à votre maître au Lakeshire.",
    ["O"] = "Retourner voir le Magistrat Solomon avec la Lettre en réponse du Seigneur Ebonlocke.",
    ["T"] = "Un message pour Darkshire",
  },
  [147] = {
    ["D"] = "Si le \"Collecteur\" prend de l\'or dans nos mines, il le vole au royaume ! Traînez-le devant la justice et apportez-moi l\'anneau dont parlait le Plan de route que vous m\'avez donné. Cela pourrait bien donner une indication sur l\'employeur du Collecteur...$B$BCe plan de route indique que le Collecteur se cache près du Champ de potirons des Brackwell. Cherchez-le vers là-bas.",
    ["O"] = "Trouver et tuer le \"Collecteur\" puis rapporter son Anneau au maréchal Dughan.",
    ["T"] = "Chasse à l\'homme",
  },
  [148] = {
    ["D"] = "Oh ! Comment doit faire un pauvre vieillard ? J\'ai besoin d\'aller chercher des fournitures à Darkshire, mais le village est si loin et je suis si vieux, si faible... Je n\'y arriverai jamais. $B$BPouvez-vous aller à Darkshire et me rapporter des mèches de cheveux fantômes ? Vous les obtiendrez chez Madame Eva, qui habite la place de Darkshire.",
    ["O"] = "S’adresser à Madame Eva.",
    ["T"] = "Fournitures en provenance de Darkshire",
  },
  [149] = {
    ["D"] = "Il y a un pauvre fantôme triste, Marie l\'Aveugle, qui hante une vieille ferme, dans les collines au sud de Darkshire.$B$BPrenez ce Peigne spectral et demandez-lui de peigner ses cheveux. Ensuite, rapportez-moi le peigne et j\'en retirerai les cheveux nécessaires pour fabriquer la mèche.$B$BFaites attention, car des créatures des ténèbres sont terrées autour de la maison...",
    ["O"] = "Apporter le Peigne spectral à Marie l\'Aveugle.",
    ["T"] = "Mèche de cheveux fantômes",
  },
  [150] = {
    ["D"] = "Les murlocs sont en train de devenir gênants. De plus en plus de pêcheurs jettent l\'ancre au large des rives du lac Placide et remontent un problème dans leurs filets ! Nous devons nous débarrasser d\'eux avant qu\'ils aient dévoré jusqu\'au dernier poisson du lac !$B$BPartez à la chasse aux murlocs ! Rapportez-moi huit ailerons et je vous offrirai quelque chose...",
    ["O"] = "Apporter 8 Ailerons de murloc au Chef des docks Baren à Lakeshire",
    ["T"] = "Les braconniers murlocs",
  },
  [151] = {
    ["D"] = "Cette pauvre Blanchy ! Après tout ce que nous lui avons fait subir, elle est très fatiguée. Je l\'ai nourrie avant que nous quittions la ferme, mais nous ne nous attendions pas à ce que le chariot nous trahisse. Si vous pouviez lui apporter quelques poignées d\'avoine des champs, vous auriez ma reconnaissance.$B$BJe parie que vous pourriez en trouver un peu près des fermes de la marche de l\'Ouest, si vous parvenez à éviter ces horribles machines qui ont pris possession des lieux. Vous verrez de nombreuses fermes, au sud-ouest.",
    ["O"] = "Verna Furlbrow de la marche de l\'Ouest veut que vous lui apportiez 8 Poignées d\'avoine.",
    ["T"] = "Pauvre Vieille Blanchy !",
  },
  [152] = {
    ["D"] = "Vous avez probablement remarqué toutes les épaves le long de la côte. La Grande Mer est une maîtresse cruelle, en vérité. La côte de la marche de l\'Ouest doit être nettoyée, afin de permettre aux marins de trouver leur chemin jusqu\'à nos plages en toute sécurité. Les murlocs sont déjà assez dangereux !$b$bTuez 7 Chasse-marées, 7 Guerriers, 7 Oracles et 7 Cours-la-côte et vous recevrez une récompense.",
    ["O"] = "Tuer 7 Chasse-marées, 7 Guerriers, 7 Oracles et 7 Cours-la-côte, puis retourner voir le capitaine Grayson au Phare de l\'Ouest.",
    ["T"] = "La côte n\'est pas sûre",
  },
  [153] = {
    ["D"] = "Les Défias bougent en permanence. Ça fait un moment que je suis leurs mouvements. J\'ai remarqué que nombre des membres du gang portaient un masque en cuir rouge.$b$bApportez-moi 15 de ces masques et vous recevrez une récompense.",
    ["O"] = "Apporter 15 Masques en cuir rouge à l\'éclaireur Galiaan à la Colline des sentinelles.",
    ["T"] = "Les masques en cuir rouge",
  },
  [154] = {
    ["D"] = "NON !$B$BPrenez-le, reprenez ce peigne, éloignez-le de moi !$B$BJe suis un monstre et personne ne peut me trouver le moindre charme !",
    ["O"] = "Rapporter le Peigne spectral à Madame Eva à Darkshire.",
    ["T"] = "Rendre le peigne",
  },
  [155] = {
    ["D"] = "Ainsi Stoutmantle envoie quelqu\'un d\'aussi famélique pour me protéger ? Il faudra bien que ça aille. Tout de même, il vaudrait mieux s\'entourer de quelques alliés...$B$BVous connaissez le plan, n\'est-ce pas, $N ? Vous surveillez mes arrières et je vous mène à la cache des Défias. Vous feriez mieux de ne pas trop vous éloigner. Les Défias veulent ma tête à présent. S\'ils me voient avec vous, ils essaieront de me tuer.$B$BPrévenez-moi quand vous aurez rassemblé quelques amis et que vous serez prêts à partir.",
    ["O"] = "Escortez le Traître défias jusqu\'à la cache secrète de la Confrérie défias. Lorsque vous saurez où se cachent VanCleef et ses hommes, rapportez l\'information à Gryan Stoutmantle.",
    ["T"] = "La Confrérie défias",
  },
  [156] = {
    ["D"] = "Si vous m\'apportez des racines pourries, je pourrai vous préparer une tournée de suc de zombie. $B$BLes racines pourries poussent dans des endroits étranges. J\'ai entendu dire qu\'on pouvait les trouver à l\'intérieur des crânes des Démons squelettes et des Squelettes infernaux... Cela dit, je ne me suis jamais approché pour vérifier ! $B$BLe cimetière de Colline-aux-Corbeaux et celui des Jardins paisibles sont tous les deux pleins de ces squelettes. Vous pourriez nous en débarrasser tout en cherchant les racines pourries. Darkshire vous en serait infiniment reconnaissant.",
    ["O"] = "Rapporter 8 Racines pourries à Smitts l\'aubergiste de Darkshire.",
    ["T"] = "Cueillette de racines pourries",
  },
  [157] = {
    ["D"] = "Voici votre mèche, $N. Elle a des milliers d\'usages. Elle permet d\'attacher les esprits maléfiques, de redonner la vie aux objets inanimés, que sais-je encore ?",
    ["O"] = "Apporter la Mèche de cheveux fantômes à Abercrombie, dans sa cabane, au nord du cimetière de Colline-aux-Corbeaux.",
    ["T"] = "Apporter la mèche",
  },
  [158] = {
    ["D"] = "J\'ai besoin d\'une bouteille d\'une liqueur peu ordinaire : du Suc de zombie. Smitts, l\'aubergiste de la taverne du Corbeau écarlate à Darkshire, doit pouvoir en fournir.$B$BEt dépêchez-vous, $N. Les nuits commencent à fraîchir et j\'ai besoin de ce breuvage pour réchauffer le sang dans mes veines.",
    ["O"] = "S’adresser à Smitts l\'aubergiste.",
    ["T"] = "Suc de zombie",
  },
  [159] = {
    ["D"] = "Voici votre Suc de zombie…$B$B… Et faites attention avec ce truc. Il est assez fort pour réveiller les morts !",
    ["O"] = "Apporter le Suc de Zombie à Abercrombie, dans sa cabane.",
    ["T"] = "Livrer le Suc",
  },
  [160] = {
    ["D"] = "Apportez cette lettre à Ello Ebonlocke, le Maire de Darkshire.$B$BNous sommes de vieux amis… Et quand il lira ça et comprendra ce que vous avez fait pour moi, je suis sûr qu\'il sera tout aussi heureux que moi ! Ha ha !",
    ["O"] = "Apporter la Lettre d\'Abercrombie à Ello Ebonlocke, Maire de Darkshire.",
    ["T"] = "Une lettre pour le Maire",
  },
  [161] = {
    ["D"] = "Si mes soupçons sont justifiés, c\'est une espèce de poudre explosive. Il faut la faire analyser par un artificier pour savoir à quoi nous avons à faire exactement. Ashlan Stonesmirk a été affecté au régiment qui garde Dun Modr et le viaduc de Thandol. Vous allez devoir faire un périlleux voyage, $N.$b$bTraversez la Porte d\'Algaz, suivez la route jusqu\'aux Paluns et trouvez Stonesmirk à Dun Modr. Et suivez mon conseil : ne vous éloignez pas de la route et ne vous arrêtez pour rien au monde !",
    ["O"] = "L\'Ingénieur en chef Hinderweir veut que vous apportiez cette étrange poudre à Stonesmirk l\'Artificier à Dun Modr.",
    ["T"] = "Une sombre menace se dessine",
  },
  [162] = {
    ["D"] = "Je vous presse d\'apporter ce rapport à quelqu\'un qui a les connaissances nécessaires pour l\'étudier.$B$BJe connais une elfe de la nuit érudite, Gracina Spiritmight. Elle a passé beaucoup de temps à étudier les formes de vie exceptionnelles de l\'écologie d\'Azeroth et elle est historienne de surcroît ! Si vous cherchez quelqu\'un qui pourrait comprendre la nature de cette menace, et surtout qui serait capable de trouver une solution, alors confiez-lui ce rapport. Nous aurons besoin de son aide.$B$BLa dernière fois que j\'ai entendu parler d\'elle, elle vivait au Temple de la Lune, à Darnassus.",
    ["O"] = "Apporter le Rapport d\'analyse des insectes à Gracina Spiritmight à Darnassus.",
    ["T"] = "L\'ascension des silithides",
  },
  [163] = {
    ["D"] = "Il y a quelque chose d\'étrange à Colline-aux-Corbeaux, $n. Calor jure que quelqu\'un (ou quelque chose) hante les bâtiments là-bas. Deux nuits de suite, il a remarqué que les ombres bougeaient et la nuit d\'avant, il dit qu\'il a vu de la lumière à l\'une des fenêtres.$b$bSi, comme vous le prétendez, vous vous intéressez vraiment à la sécurité du bois de la Pénombre, trouvez le monstre qui hante Colline-aux-Corbeaux.",
    ["O"] = "Découvrir ce qui hante Colline-aux-Corbeaux.",
    ["T"] = "Colline-aux-Corbeaux",
  },
  [164] = {
    ["D"] = "Connaissez-vous Sven ? Voilà un homme fidèle à la Lumière ! Lui et ses fidèles vivent dans un camp à la lisière nord-ouest du bois de la Pénombre.$b$bMalheureusement, ils sont si peu nombreux qu\'ils ne peuvent même pas dépêcher l\'un des leurs pour aller en ville chercher du ravitaillement. Voudriez-vous lui apporter ceci ?",
    ["O"] = "Ravitailler Sven.",
    ["T"] = "Livraison à Sven",
  },
  [165] = {
    ["D"] = "Il y avait un vieil homme qui venait régulièrement en ville faire des courses, mais cela fait un moment que je ne l\'ai pas vu. Il vit dans une cabane, au-dessus du cimetière de Colline-aux-Corbeaux, si je me souviens bien. Vous devriez aller voir ce qui ne va pas.",
    ["O"] = "Aller voir le vieil homme dans sa cabane près du cimetière de Colline-aux-Corbeaux.",
    ["T"] = "L\'ermite",
  },
  [166] = {
    ["D"] = "Il vous reste une dernière mission à accomplir. Il va falloir éliminer Edwin VanCleef. Bien que cela me chagrine de condamner un homme à mort, c\'est pour le bien du peuple de la marche de l\'Ouest que VanCleef doit mourir. Apportez-moi sa tête lorsque l\'acte sera accompli.",
    ["O"] = "Tuer Edwin VanCleef et apporter sa Tête à Gryan Stoutmantle.",
    ["T"] = "La Confrérie défias",
  },
  [167] = {
    ["D"] = "Nous étions au plus profond d\'une mine dans la marche de l\'Ouest, cachés derrière un baraquement à Ruisselune. Impossible de savoir d\'où viennent ces voleurs puants... Bref, la mine s\'est effondrée sur nous. Je m\'en suis sorti mais les autres... Enfin... On n\'en a plus entendu parler. $b$bVous aurez peut-être plus de chance. Si vous arrivez à avoir des nouvelles de mon frère, je vous en serai reconnaissant. Il portait toujours son insigne de la Ligue des Explorateurs. Si vous ne le trouvez pas, mais que vous me rapportez cet insigne, cela me rendrait un peu de paix.",
    ["O"] = "Apporter l\'Insigne de la Ligue des Explorateurs du contremaître Crispechardon à Wilder Crispechardon à Stormwind.",
    ["T"] = "Adieu mon frère…",
  },
  [168] = {
    ["D"] = "Nombre de mes amis ont péri ce jour terrible de l\'effondrement de la mine. Si vos pas vous conduisent dans cette partie de la mine de Ruisselune, voyez s’il existe des indices qui permettraient de les identifier. Si vous trouvez leurs Cartes du Syndicat des Mineurs, rapportez-les-moi et je préviendrai leurs familles.",
    ["O"] = "Récupérer 4 Cartes du Syndicat des mineurs et les apporter à Wilder Crispechardon à Stormwind.",
    ["T"] = "À la recherche de Cartes du Syndicat des Mineurs",
  },
  [169] = {
    ["D"] = "On recherche ! Gath\'Ilzogg, responsable des attaques Blackrock sur le donjon de Guet-de-pierre et des raids sur la ville de Lakeshire.$B$BGath\'Ilzogg est très dangereux. On dit qu\'il prépare ses prochains crimes contre les humains depuis le Donjon récemment conquis. L\'invasion de ces orcs doit être stoppée à tout prix ! Une forte récompense est offerte au cœur vaillant qui apportera la tête de Gath\'Ilzogg au magistrat Solomon.",
    ["O"] = "Tuer Gath\'Ilzogg et apporter sa Tête au magistrat Solomon de Lakeshire pour toucher la récompense.",
    ["T"] = "On recherche : Gath\'Ilzogg",
  },
  [170] = {
    ["D"] = "J\'espère que vous êtes là pour nous donner un coup de main, $c. Après la dernière attaque des troggs, nous avons besoin de toute l\'aide possible !$b$bJe sais que ces canailles pullulent dans toutes les régions et on dirait que les Frigères ne font pas exception. On les a repérés au sud-est, partout dans les collines et à côté du lac Gelé.$b$bEt ce n\'est pas tout ! Il y a à peine quelques nuits, ils ont attaqué et détruit notre camp, à l\'ouest !$b$bNous sommes un peu à court de personnel, ici, $gmon gars:jeune fille;, et nous avons besoin de force et de bravoure pour renvoyer les troggs d\'où ils viennent.",
    ["O"] = "Balir Frosthammer veut que vous tuiez 6 Troggs Mâcheroc et 6 Troggs Mâcheroc forcenés.",
    ["T"] = "Une nouvelle menace",
  },
  [171] = {
    ["D"] = "Vous avez été très $ggentil:gentille; avec moi, $N. Je n\'aurais jamais pensé que je m\'amuserai autant pendant la Semaine des enfants. Merci. Je ne veux pas être triste, avec tout ce qui m\'est arrivé... Mes parents me disaient toujours qu\'il fallait surmonter les mauvaises choses qui nous arrivent. Vous savez, quand je serai grand, je veux être un héros... comme vous.$B$BC\'est triste de devoir revenir à l\'orphelinat. Rentrons à la maison, et je dirai à la gouvernante que vous êtes une personne géniale !",
    ["O"] = "Ramenez votre protégé à l\'orphelinat de Hurlevent, en rendant le sifflet d\'orphelin humain à la gouvernante de l\'orphelinat, Rossignol, sur la place de la Cathédrale.",
    ["T"] = "Un surveillant de l\'Alliance",
  },
  [172] = {
    ["D"] = "Vous voulez aider l\'orphelinat ? Vous faites honneur à la Horde, mon $gami:amie;. Votre aide durant la Semaine des enfants nous sera précieuse.$B$BPrenez ce sifflet. Vous ne pourrez vous en servir que durant sept jours, en fait, pendant toute la Semaine des enfants Quand vous l\'utiliserez, vous pourrez bavarder avec l\'enfant que vous avez accepté de surveiller. Si vous l\'utilisez une seconde fois, il renverra l\'enfant.$B$BJe vous en prie, $N, utilisez le sifflet pour faire la connaissance de votre protégé ! Je suis sûre que l\'enfant est très impatient de vous rencontrer.",
    ["O"] = "Utiliser le sifflet d\'orphelin orc pour parler à l\'enfant que vous protégez durant la Semaine des enfants.",
    ["T"] = "La Semaine des enfants",
  },
  [173] = {
    ["D"] = "Les ténèbres s\'étendent inexorablement sur le bois de la Pénombre. La quête de maître Carevin est l\'expulsion du mal et de l\'hérésie. Nos efforts protègent le peuple de Darkshire. $b$bVous sentez-vous digne de nous rejoindre ? $b$bJe pensais comme vous, autrefois. Mais la suffisance des Veilleurs était pénible et j\'ai rejoint maître Carevin. Si vous voulez prouver votre valeur, les mots ne suffiront pas. $b$bTestez votre habileté contre les tisseurs d\'ombre Plaie-de-nuit, des worgens qui rôdent dans le Verger pourissant et dans le bosquet de Clairbois (clair, mon oeil !).",
    ["O"] = "Tuer 6 worgens Tisseurs d\'ombre Plaie-de-nuit pour Calor de Darkshire.",
    ["T"] = "Worgens dans les bois",
  },
  [174] = {
    ["D"] = "Mon ami Cog, un gnome, a promis de m\'aider à construire un outil qui permet de voir les étoiles. Il semblerait qu\'il existe une vérité, au-delà de la magie, qui explique les étranges changements survenus dans la forêt. $b$bApportez-moi un tube en bronze fabriqué par un ingénieur habile. C\'est la première étape, si j\'en crois le plan de Cog.",
    ["O"] = "Viktori Prism\'Antras de Darkshire veut que vous lui apportiez un Tube en bronze.",
    ["T"] = "Les yeux dans les étoiles",
  },
  [175] = {
    ["D"] = "Selon le plan de Cog, il faut maintenant que je trouve un moyen de réfléchir la lumière. Je me souviens d\'une femme du nom de Marie, bien connue par ici car elle avait l\'habitude de transporter un miroir. Vous pourriez peut-être chercher Marie et voir si elle nous prêterait une surface réfléchissante pour notre entreprise ? Je crois qu\'elle habitait au sud du bois de la Pénombre...",
    ["O"] = "Trouver Marie et la persuader de prêter une surface réfléchissante pour le projet de Viktori.",
    ["T"] = "Les yeux dans les étoiles",
  },
  [176] = {
    ["D"] = "On recherche : Lardeur.$B$BUn énorme gnoll, Lardeur, rôde dans les bois en quête de proies, au sud-ouest d\'Elwynn. Nul n\'a réussi à le capturer.$B$BL\'armée de Stormwind offre une généreuse récompense à celui qui le tuera et qui apportera une preuve de sa mort au maréchal Dughan, de Goldshire.",
    ["O"] = "Liquider le gnoll Lardeur et apporter sa Grande griffe au maréchal Dughan.",
    ["T"] = "On recherche : \"Lardeur\"",
  },
  [177] = {
    ["D"] = "Le miroir était l\'un de mes plus précieux trésors jusqu\'à... l\'accident. Après cela, je l\'ai jeté derrière la grange. Il y avait une goule folle qui traînait souvent par là-bas. Elle l\'a peut-être récupéré. La rumeur dit qu\'elle vit dans le cimetière des Jardins paisibles.",
    ["O"] = "Prendre le miroir de Marie l\'Aveugle à la Goule folle et l’apporter à Viktori de Darkshire.",
    ["T"] = "Les yeux dans les étoiles",
  },
  [178] = {
    ["D"] = "<Vous entendez une voix désincarnée...> $B$BNe soyez pas $geffrayé:effrayée;. Je suis Theocritus, grand mage de la tour d\'Azora de la forêt d\'Elwynn. Le pendentif que vous avez en main est un moyen de communication entre les gnolls Sombrepoil et leur maître Morganth. $B$BAprès des mois de recherches, j\'ai cru que moi aussi je pourrais communiquer grâce à ces pendentifs. Si vous pouvez entendre ce message, c\'est que mon sortilège est un succès. $B$BApportez-moi ce pendentif et je vous récompenserai.",
    ["O"] = "Apporter l\'Ancien pendentif sombrepoil à Theocritus le Mage.",
    ["T"] = "Theocritus récupère son Pendentif",
  },
  [179] = {
    ["D"] = "Qu\'est-ce que c\'est ? On dirait que vous avez besoin de quelque chose pour tenir chaud aux mains, non ?$b$bJe vais vous dire ce qui vous serait utile : une paire de jolis gants bien chauds. Et comme je suis une âme charitable, je vous les fournirai. À une condition, cependant.$b$bJ\'ai besoin de viande de loup. Arrangement équitable, n\'est-ce pas ? Vous m\'apportez de la viande et je m\'assure qu\'on ne sera pas obligé de vous amputer d’un ou deux doigts gelés. Qu\'est-ce que vous en dites ?",
    ["O"] = "Sten Stoutarm voudrait 8 Morceaux de Viande de loup coriace.",
    ["T"] = "Tailleur chez les nains",
  },
  [180] = {
    ["D"] = "On recherche : Lieutenant Fangore.$b$bConnu comme chef du clan des gnolls Sombrepoil, sous les ordres de Morganth, ennemi de la commune et du royaume. Sur mandat du magistrat Solomon, ce traître doit mourir. Apportez sa patte au magistrat comme preuve de sa mort.",
    ["O"] = "Tuer le lieutenant Fangore et rapporter sa patte au magistrat Solomon de Lakeshire.",
    ["T"] = "On recherche : lieutenant Fangore",
  },
  [181] = {
    ["D"] = "Il ne manque plus qu\'un seul objet pour terminer cet appareil. Le plan de Cog mentionne une lentille ou quelque chose comme ça. La seule lentille qui soit assez large pour l\'appareil risque d\'être très difficile à acquérir. $b$bUn ogre du nom de Zzarc\'Vul habite dans la colline au sud du bois de la Pénombre. Si vous le pouvez, rapportez-moi son monocle et je l\'utiliserai comme lentille !",
    ["O"] = "Localiser Zzarc\'Vul dans le tertre des ogres au sud du bois de la Pénombre et rapporter son Monocle à Viktori de Darkshire.",
    ["T"] = "Les yeux dans les étoiles",
  },
  [182] = {
    ["D"] = "Mon frère Senir et moi avons voyagé dans tous les coins de Dun Morogh pour évaluer la menace posée par les trolls. Le Sénat est déjà débordé par les ennuis que causent les troggs, il n\'a pas besoin de ce problème supplémentaire.$b$bDe ce que j\'ai vu, les trolls sont bien installés dans la vallée des Frigères, principalement dans les grottes du sud. Je ne pense pas qu\'il soit nécessaire de recourir à l\'armée. Quelques hommes robustes devraient suffire.$b$bVous aimeriez peut-être vous joindre à cette campagne ? J\'ai le pouvoir de vous dédommager pour votre aide.",
    ["O"] = "Grelin Whitebeard aimerait que vous tuiez 14 Jeunes trolls Frostmane.",
    ["T"] = "La caverne des trolls",
  },
  [183] = {
    ["D"] = "Rien de tel qu\'une journée de chasse au sanglier, pas vrai ?$b$bOui, eh bien ici, dans la vallée des Frigères, il y en a tellement que ça gâche tout plaisir. Pas besoin de les pousser à attaquer ! Ils sont furieux et prêts à charger sans qu\'on les aide ! En fait, depuis quelque temps, il y a tellement de sangliers dans la vallée des Frigères que chasseur devient un métier à haut risque.$b$bPour faire court, si vous pouviez m\'aider à tuer quelques bêtes, j\'apprécierais le geste.",
    ["O"] = "Talin Keeneye aimerait que vous tuiez 12 Petits sangliers des rochers.",
    ["T"] = "Chasseur de sangliers",
  },
  [184] = {
    ["D"] = "Voici un titre de propriété concernant une ferme de la marche de l\'Ouest, signé par un certain Théodore Furlbrow et par sa femme, Verna. Au dos, il y a quelques mots hâtivement écrits : $B$B\"Nous avons fait pression sur Furlbrow et avons récupéré son titre pensant que ça pourrait nous être utile au cas où nous voudrions nous installer. $B$BIls ne nous embêteront pas. La dernière fois que je les ai vus, ils étaient sur la route de la marche, avec un chariot cassé.\"$B$BLes Furlbrow aimeraient sans doute récupérer leur titre...",
    ["O"] = "Rapporter son titre de propriété au Fermier Furlbrow.",
    ["T"] = "Titre de propriété de Furlbrow",
  },
  [185] = {
    ["D"] = "Lorsque j\'étais encore petite fille, mon père m\'a tout appris de la chasse au tigre.$b$bLes jeunes félins sont des proies plus faciles. C\'est pourquoi vous commencerez par eux. On peut trouver de jeunes tigres à côté du camp de l\'expédition.$b$bFaites la preuve de vos capacités en traquant et en chassant quelques-unes de ces bêtes.",
    ["O"] = "Ajeck Rouack de l\'Expédition Nesingwary veut que vous chassiez 10 Jeunes tigres de Strangleronce.",
    ["T"] = "Maîtrise de la chasse au tigre",
  },
  [186] = {
    ["D"] = "Vous apprenez vite, $c. Passons aux choses sérieuses à présent. Tuez 10 Tigres de Strangleronce, cette fois. Des adultes !",
    ["O"] = "Ajeck Rouack de l\'Expédition Nesingwary veut que vous chassiez 10 Tigres de Strangleronce.",
    ["T"] = "Maîtrise de la chasse au tigre",
  },
  [187] = {
    ["D"] = "Bien... Nous allons relever la barre, et voir dans quel bois on vous a taillé. Prouvez-moi que vous êtes en mesure de tuer 10 Anciens tigres de Strangleronce. Si vous y arrivez, vous serez digne d\'affronter la dernière épreuve avant d\'être nommé maître-chasseur de tigres en présence de maître Nesingwary. $b$bLes Anciens tigres ne sont pas seulement les plus difficiles à débusquer, ils sont aussi les plus féroces.",
    ["O"] = "Ajeck Rouack de l\'Expédition Nesingwary veut que vous chassiez 10 Anciens tigres de Strangleronce.",
    ["T"] = "Maîtrise de la chasse au tigre",
  },
  [188] = {
    ["D"] = "Voici votre dernier défi. Cela fait des semaines que nous traquons un tigre particulièrement malin. Nous l\'appelons Sin\'Dall. Essayez de réussir ce qu\'aucun $r n\'a réussi avant vous : débusquer et tuer Sin\'Dall. Apportez-moi sa patte comme preuve. $b$bÇa ne va pas être une tâche facile...",
    ["O"] = "Ajeck Rouack de l\'Expédition Nesingwary veut que vous tuiez Sin’Dall et que vous rapportiez sa Patte.",
    ["T"] = "Maîtrise de la chasse au tigre",
  },
  [189] = {
    ["D"] = "Saletés de trolls ! Ils massacrent tous nos agents dès qu\'ils mettent un pied dans la jungle ! J\'ai perdu beaucoup de mes meilleurs hommes dans de simples escarmouches ! Les pertes deviennent insupportables ! Comment vais-je pouvoir expliquer cela au baron Revilgaz ? $b$bIl faut une action décisive... Pense, Kebok, réfléchis... Tu n\'as pas gagné tes galons et ton poste en rêvassant... J\'y suis ! Vous... $n, c\'est ça ? Vous allez m\'aider, n\'est-ce pas ? Il y a de l\'argent à gagner. $b$bVoilà le marché : vous me rapportez les oreilles de 15 trolls Bloodscalp et vous recevrez une récompense.",
    ["O"] = "Obtenir 15 Oreilles de trolls Bloodscalp et les apporter à Kebok, à Baie-du-Butin.",
    ["T"] = "Oreilles de Bloodscalp",
  },
  [190] = {
    ["D"] = "Si vous voulez faire partie de la chasse avec ce groupe de cracks rassemblé par Hemet, il va falloir vous en montrer digne ! On va tester vos capacités de chasseur de panthères... En commençant par le plus facile, soyez tranquille. Voyons si vous êtes capable de tuer 10 jeunes panthères. Pour débuter. $b$bLes débusquer représente seulement la moitié de la difficulté...",
    ["O"] = "Le sire S. J. Erlgadin de l\'Expédition Nesingwary veut que vous tuiez 10 Jeunes panthères.",
    ["T"] = "Maîtrise de la chasse à la panthère",
  },
  [191] = {
    ["D"] = "Bon. On va pouvoir passer au cran supérieur. Si vous voulez montrer que vous êtes digne de vous mêler aux chasseurs de grands fauves, vous devrez tuer 10 panthères.$b$bLes adultes sont plus retorses. Elles ne sont pas aussi faciles à chasser que les jeunes.",
    ["O"] = "Le sire S. J. Erlgadin de l\'Expédition Nesingwary veut que vous tuiez 10 Panthères.",
    ["T"] = "Maîtrise de la chasse à la panthère",
  },
  [192] = {
    ["D"] = "Maintenant, passons aux choses sérieuses. Un vrai chasseur doit être capable de chasser une panthère Ombregueule, le fauve le plus dangereux de Strangleronce. Si vous voulez nous prouver votre valeur, tuez 10 d\'entre elles.$b$bSi vous les trouvez difficiles à débusquer, attendez de vous trouver face-à-face avec l\'une d\'elles...",
    ["O"] = "Le sire S. J. Erlgadin de l\'Expédition Nesingwary veut que vous tuiez 10 panthères Ombregueule.",
    ["T"] = "Maîtrise de la chasse à la panthère",
  },
  [193] = {
    ["D"] = "Vous avez presque atteint le niveau de maître-chasseur de panthères. Il y en a une, du nom de Bhag\'thera qui rôde dans la jungle. Jusqu\'à présent, elle a réussi à échapper à toutes nos battues. Voyons si votre habileté est suffisante pour régler son compte à Bhag\'thera.$b$bApportez-moi un Croc de Bhag\'thera et vous gagnerez mon respect !",
    ["O"] = "Le sire S. J. Erlgadin de l\'Expédition Nesingwary veut que vous lui rapportiez un Croc de Bhag\'thera.",
    ["T"] = "Maîtrise de la chasse à la panthère",
  },
  [194] = {
    ["D"] = "Vous vous imaginez être un as parmi les chasseurs ? On peut vérifier ça ! Entrez dans cette jungle et tuez 10 raptors de Strangleronce. On verra bien...$b$bEh non, je ne vous aiderai pas à découvrir comment on les débusque ! La traque fait la moitié du défi !",
    ["O"] = "Hemet Nesingwary veut que vous tuiez 10 Raptors de Strangleronce.",
    ["T"] = "Contrôler la population des raptors",
  },
  [195] = {
    ["D"] = "Commençons le défi à présent et voyons si vous êtes à la hauteur. Allez dans la jungle et tuez 10 de ces maudits raptors flagellants. Montrez-moi ce dont vous êtes capable !",
    ["O"] = "Hemet Nesingwary veut que vous tuiez 10 Raptors flagellants.",
    ["T"] = "Contrôler la population des raptors",
  },
  [196] = {
    ["D"] = "Voyons comment vous vous en sortez avec ce nouveau défi. Il existe une espèce de raptor pas loin d\'ici, une race assez méfiante de nature. Nous les appelons les Traqueurs de la jungle. Ils sont largement plus coriaces à chasser et à tuer que les proies sur lesquelles vous avez tenté votre chance jusqu\'à présent. Tuez 10 Traqueurs de la jungle et je vous indiquerai le meilleur terrain de chasse.",
    ["O"] = "Hemet Nesingwary veut que vous tuiez 10 Traqueurs de la jungle.",
    ["T"] = "Contrôler la population des raptors",
  },
  [197] = {
    ["D"] = "Puisque vous avez montré que vous êtes une telle terreur dans la jungle, laissez-moi vous parler d\'un terrible raptor que même moi je n\'ai pas réussi à vaincre. Ajeck, que vous voyez là, appelle ce raptor rusé Téthis.$b$bSi vous voulez prouver que vous maîtrisez l\'art de la chasse, ramenez-moi sa griffe. C’est un exploit que personne n\'a accompli jusqu’à présent.",
    ["O"] = "Hemet Nesingwary veut que vous tuiez Téthis, un raptor farouche et dangereux de Strangleronce.",
    ["T"] = "Contrôler la population des raptors",
  },
  [198] = {
    ["D"] = "Hum ! Oui, il y a quelque chose que vous pourriez faire. Le lieutenant Doren et ses partisans, dans le nord, ont besoin d\'être réapprovisionnés.$b$bIls n\'ont encore rien reçu, et avec les attaques des trolls, j\'aurais besoin de quelqu\'un pour le faire. Le campement de Doren se situe au nord-ouest de la route, à l\'entrée de Strangleronce depuis le bois de la Pénombre. Donnez les provisions au soldat Thorsen ; s\'il n\'est pas dans le campement, c\'est qu\'il patrouille plus au sud du camp.$b$bAttention, si vous échouez, vous devrez me rembourser.",
    ["O"] = "Remettre les marchandises des gobelins au soldat Thorsen.",
    ["T"] = "Les marchandises du soldat Thorsen",
  },
  [199] = {
    ["D"] = "Vous devez prendre un échantillon de l\'étrange poudre odorante.",
    ["O"] = "Retourner voir l\'ingénieur en chef Hinderweir et lui montrer l\'indice que vous avez découvert.",
    ["T"] = "Une sombre menace se dessine",
  },
  [200] = {
    ["D"] = "Si Kurzen connaît le secret de Thorsen, alors cela veut dire qu\'il y a un espion parmi nous. Nous devons trouver comment le secret a été éventé !$B$BLe contact de Thorsen au camp de Kurzen est Herod la Paperasse. C’est l\'officier en logistique de Kurzen et il sait tout ce qui se passe dans l\'enceinte de Kurzen. Si vous pouvez mettre la main sur les notes et les registres de Herod, nous comprendrions peut-être comment Thorsen a été découvert.$B$BLorsque nous étions du côté de Kurzen, le bureau de la Paperasse se trouvait au dernier étage de la seule maison à étages du camp. Ses registres s\'y trouvent probablement encore.",
    ["O"] = "Trouver les registres de Herod la Paperasse.",
    ["T"] = "Herod la Paperasse",
  },
  [201] = {
    ["D"] = "On m\'a rapporté qu\'un groupe de chasseurs a établi campement au bord de la rivière, au-dessus de celui de la KapitalRisk, sur le lac Nazfériti. En temps normal, je n\'y prêterais pas attention. Mais, aussi improbable que cela puisse paraître, j\'ai entendu dire que Hemet Nesingwary était l\'un de ces chasseurs ? Il semble avoir beaucoup d\'affinités, si vous voyez ce que je veux dire, et l\'on n\'est jamais trop prudent.$b$bPourquoi n\'iriez-vous pas y faire un tour, $n, et voir de quoi il retourne ?",
    ["O"] = "Trouver le camp des chasseurs et communiquer sa position à Krazek à Baie-du-Butin.",
    ["T"] = "L\'enquête au camp",
  },
  [202] = {
    ["D"] = "Vous avez montré votre valeur à maintes reprises, et vos ressources semblent même concurrencer celles de ce Kurzen en personne. Il se peut que vous représentiez le salut pour notre bande de rebelles… si vous pouvez vous mesurer à Kurzen et le vaincre !$B$BIl contrôle ses hommes depuis les profondeurs de la Réserve. Vous devez y pénétrer, déborder ses défenseurs, puis combattre Kurzen et ses officiers.$B$BAdieu, $N. Mes espoirs et la destinée de mes hommes sont entre vos mains.",
    ["O"] = "Tuer 6 Troupes d\'élite, 4 Lieutenants et rapporter la tête de Kurzen au lieutenant Doren au Camp rebelle.",
    ["T"] = "Le colonel Kurzen",
  },
  [203] = {
    ["D"] = "Nous combattons désespérément le colonel Kurzen. Non seulement c’est un habile tacticien, mais nous nous battons à 1 contre 10. Pour survivre, nous devons retourner contre lui ses propres tactiques sournoises.$B$BInvestissez son camp, à l\'est, et tuez ses hommes. Le plus possible. Et ressortez avant que les renforts ne vous attrapent.",
    ["O"] = "Tuer 15 Guerriers de la jungle.$B$BRetourner voir le sergent Yohwa au Camp rebelle.",
    ["T"] = "La seconde rébellion",
  },
  [204] = {
    ["D"] = "Si nous voulons avoir la moindre chance contre Kurzen, nous devons neutraliser ses services de soins. Il a rassemblé un nombre important de Guérisseurs qui soignent ses troupes avec des remèdes tropicaux et empoisonnent les nôtres avec des extraits de fougère vénéneuse.$B$BNous avons besoin de ces remèdes pour soigner nos gens, ainsi que d\'un échantillon de ces extraits végétaux afin de l\'étudier et d’en tirer nos propres médicaments. Fouillez la base de Kurzen et ramenez ces produits. Ses Guérisseurs auront les remèdes sur eux, quant aux extraits, ils sont probablement stockés dans une caisse d\'approvisionnement quelconque.",
    ["O"] = "Apporter 7 Remèdes tropicaux et 1 Extrait de fougère vénéneuse au sergentYohwa au Camp rebelle.",
    ["T"] = "Mauvaise médecine",
  },
  [205] = {
    ["D"] = "Tandis que j\'étudie ces légendes que vous avez apportées, rassemblez pour moi des instruments de la magie maléfique des trolls. Entrez dans le territoire des trolls Skullsplitter, une tribu mineure du Sud lointain, et obtenez de leurs Sorciers-docteurs et de leurs Mystiques certains Fétiches.",
    ["O"] = "Apporter 4 Fétiches Skullsplitter à frère Nimetz au camp rebelle.",
    ["T"] = "Sorcellerie troll",
  },
  [206] = {
    ["D"] = "Kurzen est peut-être mort, mais… ce qui l\'a conduit vers le Mal existe toujours ! Mes recherches m\'ont appris ceci : un ogre mage du nom de Mai\'Zoth vit dans les monts orientaux de Strangleronce. On dit qu\'il utilise une magie contrôlant l\'esprit, et je suis sûr qu\'il a perverti Kurzen.$B$BSi l\'on permet à Mai\'Zoth de vivre, alors ses maléfices peuvent en influencer d\'autres, et nous verrons peut-être surgir un deuxième Kurzen.$B$BTuez-le, et apportez-moi sa Vue de l\'Esprit, l\'artefact qu\'il utilise pour corrompre les gens.",
    ["O"] = "Apporter la Vue de l\'Esprit au frère Nimetz au camp rebelle.",
    ["T"] = "Mai\'Zoth",
  },
  [207] = {
    ["D"] = "Le colonel Kurzen était jadis un homme grand et noble. Je dois découvrir la cause de sa déchéance ! Il est peut-être sous l\'emprise de la magie des trolls. Pour étayer cette hypothèse, il me faut étudier certaines de leurs légendes. $B$BQuatre tablettes sont cachées dans les ruines des trolls de Strangleronce. Je dois en prendre connaissance ! Recopiez-les et ramenez vos transcriptions.$B$BLa première tablette est dans les Ruines de Bal\'lal, la deuxième dans le Récif infâme, et vous trouverez les troisième et quatrième dans les ruines de Zul\'Kunda.",
    ["O"] = "Trouver les 4 tablettes et rapporter ces légendes au frère Nimetz.",
    ["T"] = "Le mystère de Kurzen",
  },
  [208] = {
    ["D"] = "$N, vos talents à la chasse sont dignes de notre compagnie. Des champs de bataille de Lordaeron aux jungles les plus denses, j\'ai rarement été témoin de prouesses telles que les vôtres. $b$bVous avez tué le féroce Sin\'Dall, l\'insaisissable Bag\'thera et le perfide Téthis. Mais le trophée ultime reste la tête du roi Bangalash. Ce tigre blanc est la raison de ma présence ici. Il occupe mes pensées depuis longtemps déjà. Tuez le roi Bangalash et vous n\'aurez personne à envier.",
    ["O"] = "Hemet Nesingwary veut que vous lui rameniez la tête du roi Bangalash, le grand tigre blanc.",
    ["T"] = "La chasse au gros gibier",
  },
  [209] = {
    ["D"] = "Il est difficile et onéreux de tuer assez de tigres pour fournir l\'exportation de leurs crocs vers les îles des Mers du sud. Par chance, nous avons mis au point une technique pour fabriquer des faux indétectables.$b$bEtonnamment, la matière idéale pour créer ces faux crocs, ce sont des défenses de troll Skullsplitter.$b$bEt avant de vous mettre à râler, sachez que ce que le client ignore ne peut pas le déranger, hein ?$b$bApportez-moi beaucoup de ces défenses, afin que l\'on puisse fournir la livraison mensuelle !",
    ["O"] = "Acquérir 18 Défenses de trolls Skullsplitter et les apporter à Kebok.",
    ["T"] = "Les défenses Skullsplitter",
  },
  [210] = {
    ["D"] = "Presque toute la cuisine se fait au camp. Et c\'était beaucoup de travail, jusqu\'à ce que j\'achète une marmite à un marchand gobelin de Baie-du-Butin. Cette marmite est une pure merveille, le temps qu\'elle m\'a fait gagner est inimaginable… malheureusement elle est cassée ! $B$BPourriez-vous la lui ramener afin qu\'il la répare ? Son nom est Krazek.",
    ["O"] = "Apporter la Cocotte ébréchée de chez Krazeck à Krazeck le gobelin.",
    ["T"] = "La cuisine de Krazeck",
  },
  [211] = {
    ["D"] = "Hélas, le temps est venu d\'attaquer Andorhal et d\'en finir avec la liche qui la contrôle !$B$BAraj l\'Invocateur, la liche, règne sur les ruines de la cité. Il est gardé par de nombreuses créatures du Fléau qui encerclent les restes du centre de la ville, et sa garde du corps personnelle, qui lui est directement attachée, est redoutable. Vous aurez besoin de plusieurs alliés pour avoir la moindre chance de le vaincre.$B$BExécutez Araj, $N, et apportez-moi un éclat de son phylactère comme preuve !",
    ["O"] = "Apporter l’Eclat phylactère d\'Araj au commandant Ashlam Valorfist, au camp du Noroît, dans les Maleterres de l’ouest.",
    ["T"] = "Hélas, Andorhal",
  },
  [212] = {
    ["D"] = "L\'un de mes plats les plus célèbres est le basilic frappé avec sa vinaigrette citronnée. Mais le problème est qu\'il est impossible de garder la viande fraîche, et je dois servir ce plat ce soir à l\'occasion de l\'anniversaire d\'un noble !$B$BAussi si vous pouviez m\'apporter un cuissot provenant d\'un basilic Oeil-de-glace fraîchement occis, et ce dans les plus brefs délais, alors je vous serais redevable d’un festin de roi.$B$BCes créatures vagabondent souvent dans les dunes au nord-ouest de Strangleronce, vous avez donc un long chemin à parcourir. Et faites vite !",
    ["O"] = "Tuer un basilic Œil-de-glace, collecter un Cuissot de basilic glacé, et le rapporter à Angus Stern, au Solitaire bleu.$B$B<Pour réussir cette quête vous devez la terminer sans libérer votre esprit.>",
    ["T"] = "Un repas servi froid",
  },
  [213] = {
    ["D"] = "Cette fois, c\'en est trop ! Ce n\'était pas assez que ce Gelriz vire les nababs invités par les princes marchands, à présent il essaie de s\'immiscer dans les affaires du plus célèbre des pirates !$b$bRevilgaz ne les aura pas, et il m\'a demandé de régler le problème à ma manière.$b$bMa manière ? Le vol ! Les géologues de la KapitalRisk qui campent près du lac Nazfériti sont très intéressés par ces étranges cristaux bleus qu\'ils ont trouvés dans les mines. Rapportez-moi des échantillons de ces cristaux. Peu m\'importe comment vous les obtenez.",
    ["O"] = "Retrouver 8 Cristaux réduits et les apporter à Kebok à Baie-du-Butin.",
    ["T"] = "Une ponction musclée",
  },
  [214] = {
    ["D"] = "Ce n\'est un secret pour personne que le signe de reconnaissance du gang des Défias est un masque rouge. Mais nous avons appris que la qualité du tissu dont est fait le masque détermine le rang de celui qui le porte au sein du gang.$b$bCela étant dit, je veux que vous éliminiez autant de Défias haut placés que possible. Vous en trouverez à coup sûr dans la cachette de VanCleef. Lorsque vous en aurez terminé, ramenez-moi 10 masques rouges en soie pour me prouver leur mort, je vous remettrai alors une récompense. Bonne chance, $N.",
    ["O"] = "L\'Eclaireur Riell de la tour de la Colline des sentinelles veut que vous lui rameniez 10 masques rouges en soie.",
    ["T"] = "Les masques rouges en soie",
  },
  [215] = {
    ["D"] = "Merci de m\'avoir sauvé, $N. Je sais de quoi ça a l\'air, mais… permettez-moi de m\'expliquer. $B$BJ\'ai été chargé par le lieutenant Doren de rejoindre les troupes de Kurzen, pour les espionner, et fournir des informations vitales aux renseignements rebelles. Vous avez vu ce que j\'ai cru être ma chance de m\'infiltrer dans la base de Kurzen. Je m\'étais trompé et, encore une fois, merci de m\'avoir sauvé.$B$BIl s\'agissait d\'une mission secrète, aussi n\'en parlez à personne. Mais si vous en parlez à Doren, il vous expliquera.",
    ["O"] = "Parler au lieutenant Doren au Camp rebelle.",
    ["T"] = "Les secrets de la jungle",
  },
  [216] = {
    ["D"] = "Les furbolgs Crins-de-Chardon sont devenus un danger qui menace notre expansion ! Le village des Crins-de-Chardon, à l\'est, nous bloque la route menant au poste de Bois-brisé. Par ce chemin, nous pourrions contourner le village d\'Astranaar. D\'un autre côté, il offrirait à l\'Alliance une voie d\'accès supplémentaire par où poursuivre son expansion.$B$B$N, nous ne pouvons pas tolérer... que se forme ainsi un chemin facilitant leur campagne d\'expansion !$B$BAllez au village des Crins-de-Chardon et réduisez le nombre des furbolgs. Vous recevrez une récompense en échange de ce travail ; mais surtout, vous ferez votre devoir au service de la Horde !",
    ["O"] = "Tuer 12 Vengeurs Crins-de-Chardon et 12 Chamans Crins-de-Chardon ; la plupart d’entre eux se trouvent à l’est de la grève de Zoram, dans le village des Crins-de-Chardon. Retourner voir Karang Amakkar, à l’avant-poste de Zoram\'gar, à Ashenvale, une fois que vous aurez terminé.",
    ["T"] = "Entre un roc et un Crin-de-Chardon",
  },
  [217] = {
    ["D"] = "$N, nos éclaireurs ont identifié le chef des troggs qui a mené la récente incursion que nous avons subie. Il semble que ces bêtes du Loch Modan répondent aux ordres d\'un certain Grawmug. Celui-ci est bien protégé. Deux de ses meilleurs gardes, Grinceur et Bagarreur, ne le quittent pas une seconde.$b$bSi vous vous en sentez capable, $N, j\'aimerais que vous acceptiez la mission visant à assassiner Gramwug et ses deux acolytes. Une fois la direction des troggs décapitée, nous aurons peut-être une chance de renvoyer ces monstres sous terre.",
    ["O"] = "Tuer le chef des troggs, Grawmug, et ses deux gardes, Grinceur et Bagarreur, puis rendre compte de la situation au capitaine Rugelfuss à la Tour de garde méridionale.",
    ["T"] = "À la défense des terres du roi",
  },
  [218] = {
    ["D"] = "Mon journal ! Ils l\'ont emporté dans la caverne. Celui qui l\'a pris ? C\'était une grosse brute avec d\'étranges inscriptions sur la peau et le visage. Je n\'ai pas pu regarder plus précisément.$b$bVous avez déjà combattu des trolls, peut-être pourriez-vous tenter de me le rapporter ?",
    ["O"] = "Grelin Whitebeard veut que vous tuiez Grik\'nir le Froid et que vous lui rapportiez son journal.",
    ["T"] = "Le journal volé",
  },
  [219] = {
    ["D"] = "Enfin, des amis de l\'Alliance !$B$BAu secours… Je vous en prie… $B$BLa dernière chose dont je me souviens est que les orcs ont submergé nos forces. La plupart des hommes ont été tués. J\'ai reçu un coup terrible à la tête et tout s\'est assombri. Je suis à présent prisonnier dans cette caverne. Au secours, je suis gravement blessé !$B$BJ\'aurais besoin d\'aide pour revenir jusqu\'à la ville... par pitié... vous êtes mon seul espoir...",
    ["O"] = "Protéger le caporal Keeshan sur le chemin des Carmines.",
    ["T"] = "Porté disparu",
  },
  [220] = {
    ["D"] = "Islen aura une nouvelle tâche à vous confier avant de vous récompenser d\'un totem. La fiole d\'eau que je vous ai donnée est nécessaire à l\'accomplissement de cette tâche.$B$BPartez à présent et trouvez-la. Nous nous reverrons dans le futur.",
    ["O"] = "Apporter la Fiole d\'eau la plus pure à Islen Waterseer dans les Tarides.",
    ["T"] = "L\'appel de l\'eau",
  },
  [221] = {
    ["D"] = "Vous avez peut-être remarqué de grands worgens accompagnant les Tisseurs d\'ombre dans les bois ? D\'après ce que nous savons, ces Coureurs sombres renforcent considérablement les rangs de leurs alliés.$b$bLors de mes expéditions, j\'ai également découvert qu\'ils ont envahi le Verger pourrissant, au sud-ouest de la ville. Ces worgens sont plus résistants que ceux que vous avez combattus précédemment.$b$bRestez donc sur vos gardes.",
    ["O"] = "Tuer 12 worgens Messagers Plaie-de-nuit pour Calor en Darkshire.",
    ["T"] = "Worgens dans les bois",
  },
  [222] = {
    ["D"] = "Vos victoires passées m\'ont convaincu que vous êtes capable de vous mesurer aux plus forts des worgens infestant les bois. $b$bParmi ceux qui se sont établis en ces contrées, les Crocs noirs et les Contaminés se sont montrés les plus dangereux. On peut les trouver près de certaines cavernes et dans la mine, au sud.$b$bVous pourrez voir de loin la lumière des feux qu\'ils font avec les os de leurs victimes...",
    ["O"] = "Tuer 8 Crocs noirs Plaie-de-nuit et 8 Contaminés Plaie-de-nuit pour Calor en Darkshire.",
    ["T"] = "Worgens dans les bois",
  },
  [223] = {
    ["D"] = "Voilà, $n. Portez ce message à maître Carevin.$b$b<Il sort promptement un morceau de parchemin décoloré et vous le tend.>$b$bEncore d’autres comme vous, et nous pourrons vaincre les Veilleurs ! Peut-être alors serions-nous en mesure d’achever l\'œuvre à laquelle si peu d\'entre nous se sont attelés.",
    ["O"] = "Apporter la lettre de Calor à Jonathan Carevin.",
    ["T"] = "Worgens dans les bois",
  },
  [224] = {
    ["D"] = "Nous devons protéger le Loch, $N ! Avec tant de soldats du roi combattant vaillamment sur des champs de bataille lointains, les forces nous manquent à l\'intérieur. Les troggs nous envahissent depuis la plus petite brèche et constituent la menace principale qui pèse sur Ironforge. Ces répugnants mutants doivent être annihilés.$b$bNous avons besoin de vous, $gbrave aventurier:courageuse aventurière;, pour mener une expédition et amener le chaos dans leurs rangs. Tuez 10 Troggs et 10 Eclaireurs parmi les Brisepierre.",
    ["O"] = "Le Montagnard Cobbleflint de la Tour de garde méridionale veut que vous tuiez 10 Troggs et 10 Eclaireurs Brisepierre.",
    ["T"] = "À la défense des terres du roi",
  },
  [225] = {
    ["D"] = "L\'épitaphe, sur la tombe effritée, annonce simplement :$b$bMORGAN LADIMORE$b$b<Après un examen plus minutieux, vous remarquez que l\'on a touché à la couche de terre sur la tombe, et qu\'une bonne partie semble avoir été déplacée...>",
    ["O"] = "Parler à Sirra Von\'Indi à Darkshire.",
    ["T"] = "La tombe effritée",
  },
  [226] = {
    ["D"] = "Sven et moi avons de difficiles journées qui nous attendent, avec ce Nécromancien à l\'est et tout le reste. Et ici, isolés comme nous le sommes, nous devons chasser pour nous procurer de la nourriture. Il semble que chaque fois que je rentre au campement avec un peu de viande sur moi, des loups sauvages affamés ou enragés me poursuivent en quête d\'un morceau. Cela va sans dire que vivre ici est une tâche ardue !$B$BMais si vous pouviez nous débarrasser de ces loups, vous nous rendriez les choses un peu plus simples. Ils rôdent pour la plupart au nord et à l\'est d\'ici, près de la rivière.",
    ["O"] = "Tuer 12 Loups redoutables affamés et 8 Loups redoutables enragés, puis retourner auprès de Lars au camp de Sven à la frontière ouest du bois de la Pénombre.",
    ["T"] = "Des loups aux trousses",
  },
  [227] = {
    ["D"] = "<Il cherche quelques instants sur les étagères puis en sort un livre relié en cuir.>$b$bSi vous voulez en savoir plus, vous devriez parler à Althea, c\'est elle qui s\'est occupée du… problème… avec lui ces derniers temps.",
    ["O"] = "Parler au commandant Althea Ebonlocke à Darkshire.",
    ["T"] = "Morgan Ladimore",
  },
  [228] = {
    ["D"] = "D\'après ce que m\'ont rapporté mes Eclaireurs, Mor\'Ladim erre du côté du bois de la Pénombre. On le voit suivre un étrange chemin sinuant à travers le cimetière. Nous l\'avons enseveli après la maison sur la colline, vous voyez où c\'est ?$b$bIl n\'y a rien que je puisse faire pour vous venir en aide, mais je vous souhaite bonne chance.",
    ["O"] = "Tuer Mor\'Ladim, puis rapporter son crâne au commandant Althea Ebonlocke à Darkshire.",
    ["T"] = "Mor\'Ladim",
  },
  [229] = {
    ["D"] = "J\'aurais dû vous le dire plus tôt… $b$bComment puis-je m\'exprimer… $b$bIl se peut que Morgan ait cru que sa famille tout entière était morte, mais en fait sa fille Sarah est Gardien à présent. Elle a toujours été troublée par les… circonstances... entourant la mort de son père. Peut-être pourriez-vous lui donner des nouvelles.",
    ["O"] = "Parler au Gardien Sarah Ladimore à Darkshire.",
    ["T"] = "La fille qui survécut",
  },
  [230] = {
    ["D"] = "En creusant, vous trouvez un petit livre, plein de terre.$B$BIl ne porte pas de titre, mais il contient des informations que Sven aimerait connaître.",
    ["O"] = "Apporter à Sven le Livre de la ferme de Sven.",
    ["T"] = "Le camp de Sven",
  },
  [231] = {
    ["D"] = "Voilà, prenez ceci et posez-la sur sa tombe. Peut-être… je ne sais comment… comprendra-t-il que je vais bien, et que personne ne le considère responsable de ce qui est arrivé.$b$b<Les larmes perlent à ses yeux à présent, tandis qu\'elle enlève une bague de sa main droite, et la presse dans votre paume.>$b$bEt... merci, $n.",
    ["O"] = "Déposer la bague de Sarah Ladimore sur la tombe de Morgan Ladimore.",
    ["T"] = "L\'amour d\'une fille",
  },
  [232] = {
    ["D"] = "La Société royale des apothicaires a besoin de vous. Écoutez attentivement et cela sera bénéfique pour nous deux.$B$BL\'apothicaire Keever dans l\'autre salle risque d\'être... vexé. Mais ses recherches porteraient leurs fruits si elles étaient faites par un chercheur plus intelligent. Heureusement, je suis cette chercheuse. Je veux que vous apportiez ce bon de commande à Alessandro Luca, le propriétaire du bazar de la Lune bleue. Il vous donnera une trousse d\'analyse. Quand vous l\'aurez, rapportez-la moi et je vous expliquerai alors le détail de la quête.",
    ["O"] = "Apporter le bon de commande à Alessandro Luca dans Undercity.",
    ["T"] = "Course pour l\'apothicaire Zinge",
  },
  [233] = {
    ["D"] = "Hum, je ne pense pas que vous seriez $gprêt:prête; à me rendre un petit service, si ? Une pile de lettres est arrivée au tri aujourd\'hui, mais je n\'ai pas le temps de les distribuer.$b$bElles ont toutes pour destinataire un dénommé Talin Keeneye. Vous pouvez le trouver à l\'ouest, sur la route. Il a établi son camp près du lac Gelé.$b$bQu\'en dites-vous ?",
    ["O"] = "Apporter la pile de lettres à Talin Keeneye.",
    ["T"] = "La distribution du courrier de la Vallée des Frigères",
  },
  [234] = {
    ["D"] = "Si je me souviens bien, le camp de Grelin se trouve plus bas sur la route, vers le sud-est. Il ne fait aucun doute qu\'il serait ravi de recevoir ce courrier.",
    ["O"] = "Remettre la lettre à Grelin Whitebeard.",
    ["T"] = "La distribution du courrier de la Vallée des Frigères",
  },
  [235] = {
    ["D"] = "Votre attention ! Les terres sauvages d\'Ashenvale vous attendent tous !$B$BLa Horde s\'est établie dans les terres au nord des Tarides. Nos deux avant-postes : le Poste de Bois-brisé et le Poste de la Grève de Zoram s\'efforcent bravement d\'apporter la gloire à la Horde ! Ceux et celles qui veulent faire montre de leur courage devraient y trouver conseil. De plus, Senani Thunderheart, au Poste de Bois-brisé, directement au nord des Tarides, cherche en ce moment des candidats prêts à prendre part à une grande Chasse d\'Ashenvale !",
    ["O"] = "Parler à Senani Thunderheart, au Poste de Bois-brisé, à Ashenvale.",
    ["T"] = "La Chasse d\'Ashenvale",
  },
  [236] = {
    ["D"] = "A few weeks ago, I dispatched Watcher Callahan and a few others to the northern border of Duskwood to deal with the wolf infestation. I haven\'t heard word from him for quite some time. If he followed my instructions, he\'ll be camped out on the road north from Darkshire. $B$BIf it\'s on your way, please check up on him and inform him that I am awaiting a progress report from him.",
    ["O"] = "Speak with Watcher Callahan.",
    ["T"] = "Awaiting Word",
  },
  [237] = {
    ["D"] = "Le Montagnard Cobbleflint n\'a pas tari d’éloges à votre égard, $n. Aussi, je vais vous confier une mission de la plus haute importance. Nous devons maintenir la pression sur les forces d\'invasion des troggs jusqu\'à ce que les nains, nos frères, reviennent du front de l\'Alliance. $b$bMettez-vous en route pour les collines méridionales et tuez 10 Brise-crâne et 10 Prophètes Brisepierre. Vos attaques nous feront gagner un temps précieux. Revenez me voir une fois votre mission menée à bien.",
    ["O"] = "Le Montagnard Gravelgaw de la Tour de garde méridionale veut que vous tuiez 10 Brise-crâne et 10 Prophètes Brisepierre, puis que vous retourniez le voir.",
    ["T"] = "À la défense des terres du roi",
  },
  [238] = {
    ["D"] = "La trousse est prête, $n. L\'apothicaire Zinge souhaite sûrement l\'avoir le plus vite possible. Apportez-la-lui sans tarder et dites-lui que j\'ai mis cela sur le compte de la Société. Passez-lui également mes plus sincères salutations.$B$BEh bien, ne restez pas là. C\'est pourtant simple d\'être coursier, non ?",
    ["O"] = "Apportez une Trousse d\'analyse de terrain à l\'Apothicaire Zinge à Undercity.",
    ["T"] = "Course pour l\'apothicaire Zinge",
  },
  [239] = {
    ["D"] = "La garnison basée sur notre frontière à l\'ouest fait état d\'une augmentation de l\'activité des gnolls et des voleurs. Elle demande plus de soldats de Stormwind, mais nous ne pouvons nous passer d\'aucun !$B$BSi vous le pouvez, votre aide sera la bienvenue. Allez voir l’Adjoint Rainer à la garnison du ruisseau de l\'ouest. Il vous dira de quoi il s\'agit.$B$BLa garnison se trouve le long de la route, à l\'ouest. Après avoir traversé le pont au-dessus du ruisseau, ce sera sur la droite.",
    ["O"] = "Se rendre à la garnison du ruisseau de l\'ouest et parler à l’Adjoint Rainer.",
    ["T"] = "Au secours de la garnison du ruisseau de l\'ouest !",
  },
  [240] = {
    ["D"] = "Et nous y voilà. Des gâteaux au \"crabe\" façon Grual.",
    ["O"] = "Retourner voir la Tremblotte avec des Gâteaux au crabe brun.",
    ["T"] = "Retour vers la Tremblotte",
  },
  [241] = {
    ["D"] = "Description",
    ["O"] = "Log",
    ["T"] = "<TEST> HEY MISTER WILSON!",
  },
  [242] = {
    ["D"] = "Throm-ka, $c. There is no time to waste. Orcs of the Dragonmaw clan have fortified themselves in the valley to the south of here. $b$bThey have denied the Warchief\'s mandate, leaving us only one course of action. We will eliminate them so that they will no longer dirty the name of the Horde.",
    ["O"] = "Kill 8 Dragonmaw Raiders, 3 Dragonmaw Bonewarders, and a Dragonmaw Battlemaster.",
    ["T"] = "<UNUSED>",
  },
  [243] = {
    ["D"] = "Vous allez vous rendre dans le désert de Tanaris, pour tester la consistance des tissus des basilics, des scorpides et des hyènes de la région. Récupérez des échantillons de tissus et placez-les dans la trousse d\'analyse ; certains ne conviendront sans doute pas. Il me faut huit échantillons corrects pour chacun des trois types de créatures.$B$BDe plus, les gobelins de Gadgetzan ont une source d\'énergie utilisable avec ce genre de trousse modifiée. Allez parler à l\'ingénieur en chef Bilgewhizzle et récupérez-en pour la trousse.$B$BNe revenez me voir que si vous réussissez.",
    ["O"] = "Parler à l\'Ingénieur en chef Bilgewhizzle à Gadgetzan afin d\'obtenir une source d\'énergie pour la trousse d\'analyse.",
    ["T"] = "Sur le terrain",
  },
  [244] = {
    ["D"] = "Je vous salue, $c. On m\'a chargé de patrouiller sur ce bout de route. Bien qu\'elle soit sûre pour l\'heure, j\'ai vu des campements de gnolls au nord et à l\'est d\'ici.$B$BLakeshire doit être mise au courant de ce rassemblement de forces ! Allez voir l’Adjoint Feldon et informez-le. Faites-le, et je suis sûr qu’il vous offrira la prime d\'un éclaireur. $B$BD\'ordinaire, Feldon inspecte les lieux au sud du pont menant à Lakeshire.",
    ["O"] = "Informer l’Adjoint Feldon.",
    ["T"] = "Des gnolls encombrants",
  },
  [245] = {
    ["D"] = "Je vous salue, $c. Peut-être avez-vous vu ces araignées qui ont envahi la frontière ouest ? Nous n\'avons pas les forces nécessaires à une attaque, et le commandant n\'a pas suffisamment d\'hommes pour en envoyer si loin de Darkshire.$b$bCela me gêne de vous le demander, mais il semble que vous êtes de taille à vous en occuper. Je ne peux rien vous promettre de grande valeur, mais je vous récompenserai si vous parvenez à déloger ces horribles bestioles.$b$bOh, un petit conseil : évitez leur venin...",
    ["O"] = "Tuer 15 Araignées Tisse-venin pygmées, puis rendre compte à la Sentinelle Dodds au bois de la Pénombre.",
    ["T"] = "Les plaies à huit pattes",
  },
  [246] = {
    ["D"] = "Nous ne pouvons nous passer de personne, la présence de chacun est vitale ici. Pourtant, quelque chose doit être fait à propos de ces gnolls dont vous m\'avez parlé.$B$BPartez à leur recherche, au sud des Carmines. Harcelez-les, tuez tous ceux que vous pouvez et revenez m\'informer de leur nombre et du danger potentiel qu\'ils représentent. Vous trouverez le camp des gnolls le long de la route sud des Carmines.$B$BFaites-le pour l\'armée de Stormwind, $N, et vous serez $grécompensé:récompensée;.",
    ["O"] = "Tuer 10 Bâtards des Carmines et 6 Braconniers des Carmines, et aller faire un rapport à l’Adjoint Feldon à Lakeshire.",
    ["T"] = "L\'appréciation du danger",
  },
  [247] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La chasse achevée",
  },
  [248] = {
    ["D"] = "J\'ai observé la Tour d\'Ilgalar, et ai découvert que Morganth est entré en conflit avec les orcs au donjon de Guet-de-pierre. Je n’en connais pas la raison, mais vous pourriez m\'aider à lever le voile sur ce mystère.$B$BPrenez donc le Glyphe d\'Azora, et rendez-vous au sommet de la Tour de garde restée intacte, près du donjon de Guet-de-Pierre.$B$BLà, trouvez un objet dans lequel placer le glyphe. Un coffre, une jarre, qu\'importe, pourvu que le glyphe ne puisse pas être détecté.",
    ["O"] = "Monter au sommet de la Tour de garde intacte près du Donjon de Guet-de-pierre, puis trouver dans quoi placer le Glyphe d\'Azora.",
    ["T"] = "En regardant plus loin",
  },
  [249] = {
    ["D"] = "Après une longue surveillance, j\'ai découvert les plans de Morganth.$B$BIl recherche la faux d\'Elune… et il se peut même qu\'il l\'ait déjà trouvée ! S\'il se l\'est appropriée, alors les Carmines et le royaume de Stormwind tomberont bientôt sous son joug.$B$BJe ne suis pas prêt à l’affronter, mais j\'ai peur qu\'il faille s\'en occuper dès à présent. Vous devez donc pénétrer dans la tour d\'Ilgalar, et combattre Morganth.$B$BRemportez la victoire, et apportez-moi son pendentif des ombres. Si vous réussissez, le royaume vous sera grandement redevable.",
    ["O"] = "Tuer Morganth, puis rapporter son Pendentif des ombres à Théocritus, à la Tour d\'Azora dans la forêt d\'Elwynn.",
    ["T"] = "Grand magus Doane",
  },
  [250] = {
    ["D"] = "Grâce au Ciel, quelqu\'un s\'inquiète pour le barrage ! D\'abord la destruction du viaduc de Thandol, ensuite le pillage de Dun Modr ! Je suis sûr que le barrage est la prochaine cible des Sombrefer. La plus grande partie de mon détachement de sécurité a été dépêché sur le front de l\'Alliance, y compris mon meilleur inspecteur.$b$bC\'est pourquoi j\'ai besoin de vous, $N. J\'ai aperçu quelques Sapeurs Sombrefer près de la rampe est du barrage. Enquêtez sur place, et ramenez un indice.",
    ["O"] = "Enquêter près de la rampe est du barrage et rapporter à l\'ingénieur en chef Hinderweir un indice qui permettrait de deviner les intentions des nains Sombrefer.",
    ["T"] = "Une sombre menace se dessine",
  },
  [251] = {
    ["D"] = "Apportez la lettre que vous m\'avez donnée à Sirra Von\'Indi. C\'est un érudit en lettres anciennes. Si quelqu’un ici peut traduire ce message, c\'est lui !",
    ["O"] = "Apporter la lettre à Ello, à Sirra Von\'Indi.",
    ["T"] = "La traduction de la lettre d\'Abercrombie",
  },
  [252] = {
    ["D"] = "Bon sang ! Si ce qui est écrit là est vrai, alors ce qu\'a fait Abercrombie est… incroyable.$B$BVous devez la transmettre à Ello.$B$BEt faites vite, car je crains qu\'un sort funeste n\'avance déjà vers notre ville...",
    ["O"] = "Remettre la lettre traduite au seigneur Ello Ebonlocke.",
    ["T"] = "La traduction pour Ello",
  },
  [253] = {
    ["D"] = "Le nom de l’Embaumeur apparaît dans l\'une des histoires que se racontent les enfants. C\'était un alchimiste bon et généreux, rendu fou par la mort de sa femme, Eliza. Dans le but de la ressusciter, il utilisa la magie noire et plaça son propre cœur dans la poitrine de son épouse décédée.$B$BIl réussit à faire revivre Eliza... mais celle-ci était à présent désormais avide de chair humaine, et l\'Embaumeur dut se résoudre à l\'enterrer.$B$BSon cœur est son pouvoir. Trouvez la tombe d’Eliza, récupérez son cœur, et ramenez-le-moi pendant qu\'il bat encore. Procédez ainsi et son pouvoir s\'éteindra.",
    ["O"] = "Trouver la tombe d\'Eliza, retrouver le cœur de l\'Embaumeur, puis retourner voir Ello Ebonlocke.",
    ["T"] = "La fiancée de l\'Embaumeur",
  },
  [254] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "En creusant la terre",
  },
  [255] = {
    ["D"] = "Deux mois d\'attente, et pour apprendre que seule une poignée d\'hommes sera dépêchée ici, et qu\'il lui faudra deux autres mois pour arriver ! Je comprends que notre ville risquerait d’être sans protection, mais le site de fouilles et le barrage – le barrage ! – sont également l’objet d’attaques.$b$bJe n\'ai pas le choix, je vais devoir embaucher des mercenaires.$b$bQu\'en pensez-vous ? Vous semblez faire l\'affaire, $c. Un groupe d\'ogres s\'est établi au nord-est du lac. Vous pourriez peut-être recruter d\'autres mercenaires et nous débarrasser de la menace qui pèse sur notre ville?",
    ["O"] = "Le magistrat Bluntnose de Thelsamar vous a $gembauché:embauchée; pour tuer 4 Ogres, 4 Brutes et 4 Massacreurs Mo\'grosh.",
    ["T"] = "Mercenaires",
  },
  [256] = {
    ["D"] = "Par décret du Magistrat de Thelsamar : Chok\'sul, l\'ogre que l\'on croit être l\'instigateur et le chef des attaques menées sur la ville de Thelsamar, sur le barrage de Formepierre et sur les excavations, est recherché mort, et mort seulement.$b$bLa récompense sera donnée à celui qui apportera la preuve de sa mort au Magistrat.$b$bLa dernière fois que Chok\'sul a été vu, il se trouvait au camp des ogres au nord-est de Loch Modan.",
    ["O"] = "Tuer Chok\'sul et rapporter sa tête au Magistrat Bluntnose de Thelsamar.",
    ["T"] = "ON RECHERCHE : Chok\'sul",
  },
  [257] = {
    ["D"] = "Vous croyez pouvoir vous mesurer à Daryl l\'Intrépide, hein ? Si j\'étais vous, je n’y croirais pas ! Bien sûr, vous êtes libre d\'essayer. Voilà un défi qui devrait être au-dessus de vos capacités, alors ne culpabilisez pas trop si vous n\'êtes pas à la hauteur.$b$bUn groupe de Buses s\'est établi ici à Loch Modan. Pourquoi ne pas essayer d\'en désabuser certaines ? Voilà le marché : si vous parvenez à accomplir le défi en quinze minutes, je vous donne mon arc ou mon fusil.$b$b<Il vous jauge dédaigneusement.>$b$bVous n\'avez pas grand-chose à perdre, de toute façon...",
    ["O"] = "Tuer 6 Busards des montagnes et retourner auprès de Daryl le Jeune au pavillon des pérégrins en moins de 15 minutes.",
    ["T"] = "La fanfaronnade d\'un chasseur",
  },
  [258] = {
    ["D"] = "$naf, c\'est ça ? Je vois que vous exultez pour avoir réussi le premier test, pas vrai ? Comme je vous l\'ai dit, ce n\'était pas grand-chose.$b$bVous devriez vous essayer à la chasse au sanglier. Croyez-moi, cela n\'a rien à voir avec la chasse au sanglier de la Vallée des Frigères, alors prenez vos précautions. Vous n\'aurez que douze minutes cette fois-ci.$b$bEt ne soyez pas $gdéçu:déçue; en cas d’échec. Je vous donnerai ma chemise si vous réussissez !$b$bÀ propos, je vous ai déjà raconté comment j\'ai reçu ma célèbre cicatrice ? Non ? C\'était il y a deux ans...",
    ["O"] = "Tuer 5 Anciens sangliers des montagnes et retourner auprès de Daryl le Jeune au pavillon des pérégrins en 12 minutes.",
    ["T"] = "Le défi d\'un chasseur",
  },
  [259] = {
    ["D"] = "Hello there, $n. I\'ve heard much about your exploits, and must say I\'m honored to meet you. I don\'t suppose you\'ve heard the story of Ol\' Kelt? Ah, well, Ol\' Kelt--Kelt Thomasin, that is--was once considered one of the finest hunters in all the realm. There wasn\'t much he hadn\'t done, but then, one day, he became obsessed with an ancient story... a myth, really. Well, as the stories go, a huge threshadon lives in the waters of the lake. Hogwash, if you ask me. If you\'re interested, you might talk to Kelt.",
    ["O"] = "Talk to Kelt Thomasin.",
    ["T"] = "<UNUSED>",
  },
  [260] = {
    ["D"] = "Now, this will no doubt convince you that I am, as they say--and don\'t deny it, I know what they say--of an addled mind, obsessed with an impossible legend. An old dwarven legend, it is, of an enormous monster that lives in the depths of the Loch. I spent a good bit of my life trying to track it down, and I believe I got close, but alas, I fear old age has caught up to me. If you would like to try hunting down the monster of the Loch, bring me some treshadon teeth and claws.$B$B<He smiles mysteriously.>",
    ["O"] = "Collect 5 Threshadon Teeth and 5 Threshadon Claws, then bring them to Kelt Thomasin.",
    ["T"] = "<UNUSED>",
  },
  [261] = {
    ["D"] = "Je serai franc. Nous sommes en guerre contre le Fléau. C\'est un mal qui corrompt notre peuple et pollue nos terres. Il faut l\'arrêter avant qu\'il ne nous prenne de vitesse et n\'entraîne notre monde dans l\'obscurité. Nous, la Croisade écarlate, avons juré de combattre le Fléau de toutes nos forces et de toute notre âme.$B$BSi vous êtes de notre bord, faites montre de votre courage et prouvez-nous votre allégeance en détruisant les Ravageurs morts-vivants de Désolace, dans la Vallée des ossements, plus au sud.$B$BFaites cela et la Croisade vous portera en haute estime.",
    ["O"] = "Détruire 30 Ravageurs morts-vivants, puis retourner voir Frère Anton, à la Combe de Nijel.",
    ["T"] = "Sur le chemin écarlate",
  },
  [262] = {
    ["D"] = "Je dois savoir qui était ce rôdeur que j\'ai vu dans les ombres, la nuit où ma famille a été tuée !$B$BJe vous en prie, $N, vous avez montré que vous êtes $gplein:pleine; de ressources, trouvez cet individu. Je dois apprendre les secrets qu\'il détient !$B$BJe n\'ai aucun autre indice que vous ne connaissiez déjà, mais tenez, prenez le livre que vous avez trouvé à la ferme. Peut-être quelqu\'un pourra-t-il faire un lien entre ce livre et son propriétaire.",
    ["O"] = "Montrer le livre que vous avez trouvé à la ferme de Sven aux habitants de Darkshire.",
    ["T"] = "L\'obscure silhouette",
  },
  [263] = {
    ["D"] = "Les Montagnards disent que vous êtes $gcourageux:courageuse; et $gcompétent:compétente;, $N. Nous avons besoin de gens tels que vous ici. Le problème avec les troggs ne s\'arrange pas. Les réservistes ont été envoyés à la guerre et nous sommes seuls. Mais à présent que nous avons un $c expérimenté comme vous, $N, nous allons voir de quoi vous êtes capable.$b$bSortez et tuez 10 Chamans et 10 Briseurs d\'Os Brisepierre. Nous allons voir si votre réputation est fondée, $gtueur de: tueuse de; troggs.",
    ["O"] = "Le Montagnard Wallbang de la Tour de garde méridionale veut que vous tuiez 10 Chamans et 10 Briseurs d\'Os Brisepierre.",
    ["T"] = "À la défense des terres du roi",
  },
  [264] = {
    ["D"] = "Ordure abjecte ! Me laisser pour cette maudite croisade. Parce que \"la Lumière est notre bien le plus précieux contre la menace des morts-vivants.\"$B$BEt ses enfants alors ? Et MOI ? Nuit après nuit, je l\'ai attendu… à toujours passer après son fichu devoir !$B$BEh bien ! Regardez ce que la \"justice\" apporte, $N ! Il est mort et je porte au cou ce qu\'il essayait d\'arrêter !$B$BPortez ce bout de ferraille sur sa tombe au Sépulcre. Je ne veux plus rien avoir à faire avec ça... et avec lui !",
    ["O"] = "Placer le pendentif de Clarice sur la tombe de Yuriv, dans la forêt des Pins argentés.",
    ["T"] = "Jusqu\'à ce que la mort nous sépare",
  },
  [265] = {
    ["D"] = "Je vous rends votre livre, $N, et vous souhaite bonne chance dans vos recherches. Si vous enquêtez auprès d\'autres personnes en ville, elles seront peut-être en mesure de vous fournir d’autres indices.$B$BEn attendant, en voici un : bien que l\'auteur de ce livre soit étranger à Darkshire, le livre lui-même a été acheté ici.",
    ["O"] = "Voir si d\'autres habitants ont des indices concernant l\'obscure silhouette.",
    ["T"] = "L\'obscure recherche se poursuit",
  },
  [266] = {
    ["D"] = "Voici votre livre, et bonne chance dans votre quête.$B$BVous devriez aller jusqu\'à l\'auberge. Si celui que vous recherchez a passé quelque temps en ville, on l’a peut-être vu là-bas.",
    ["O"] = "Chercher à l\'auberge des indices concernant l\'obscure silhouette.",
    ["T"] = "Une enquête à l\'auberge",
  },
  [267] = {
    ["D"] = "$C, il se peut que vous ayez entendu parler de la menace que les troggs font peser sur les nains. Les réservistes ayant été appelés pour servir sur le front de l\'Alliance, nous nous retrouvons avec bien peu de forces pour assurer la sécurité de nos terres. Mon régiment a pour mission de garder la porte, ici, et nous ne pouvons quitter notre poste, nous craignons trop une invasion.$B$BMais nous devons attaquer ces maudits troggs, qui se terrent dans les collines. Si vous vous en sentez capable, lancez un assaut contre eux. Ramenez-moi 8 Dents de pierre de trogg comme preuve de votre réussite.",
    ["O"] = "Apporter 8 Dents de pierre de troggs au capitaine Rugelfuss à la Tour de garde méridionale.",
    ["T"] = "La menace trogg",
  },
  [268] = {
    ["D"] = "Le livre que vous avez trouvé n\'était écrit qu\'à moitié. J\'ai dû le laisser à l\'exploitation de Sven lorsque je me suis enfui jusqu\'ici…$B$BTenez, prenez la version complète de mon journal et donnez-la à Sven. Il décrit de manière plus exhaustive ce qu\'il s\'y est déroulé.$B$BCe journal a été ma seule compagnie durant ces dernières semaines, mais je vous le donne volontiers si cela m\'épargne d’affronter Sven et sa colère.",
    ["O"] = "Retourner voir Sven avec le journal complété de la Tremblotte.",
    ["T"] = "Retourner voir Sven",
  },
  [269] = {
    ["D"] = "Rendez-vous à la cité de Stormwind et parlez à l\'évêque Farthing dans la cathédrale de la Lumière. Donnez-lui le journal de la Tremblotte et dites-lui que vous voulez combattre Morbent Fel.$B$BBien que Stormwind ait abandonné Darkshire, Farthing est un homme aimable et empli de sagesse. Il pourrait vous aider dans votre quête pour nous sauver.$B$BVotre mission est périlleuse, mais hâtez-vous, $N. À chaque heure qui s\'écoule, le bois de la Pénombre s\'assombrit encore...",
    ["O"] = "Donner le journal complété de la Tremblotte à l\'évêque Farthing à Stormwind.",
    ["T"] = "À la recherche de la sagesse",
  },
  [270] = {
    ["D"] = "Il y a des années, la IIIe flotte de Kul\'Tiras a coulé le long des côtes des Paluns, une étendue marécageuse située au nord du royaume nain d\'Ironforge. Un vaisseau avait à son bord un chargement de fer de sancteforge.$B$BLes objets forgés dans ce métal précieux frappent avec la force de la sainte vérité. Si vous devez combattre Morbent Fel, vous aurez besoin d\'une telle arme.$B$BAllez au port de Menethil, et rencontrez mon confrère le nain Glorin Steelbrow. S\'il existe une chance de retrouver ce précieux métal, il vous indiquera où le chercher.",
    ["O"] = "Aller au port de Menethil, dans les Paluns, et parler à Glorin Steelbrow.",
    ["T"] = "La Flotte perdue",
  },
  [271] = {
    ["D"] = "Je suppose que vous ne seriez pas contre le fait de rabattre un peu le caquet de Monsieur le Brave ? Daryl a une certaine habileté, il nous le rabâche assez.$b$bIl y a un an, un grand ours était dans le collimateur du pavillon des pérégrins, mais pas un chasseur ne réussissait à le tuer. Alors Daryl s\'est dit qu\'il allait s\'en occuper. La seule chose qu\'il y a gagnée, c\'est cette vilaine cicatrice à l’œil.$b$bTuez cet ours, ça clouera le bec de Daryl, pour une fois.$b$bVous trouverez cet ours, le vieux Noiraud, près de sa tanière dans les collines, au-delà de Thelsamar.",
    ["O"] = "Tuer le Vieux Noiraud puis montrer le résultat à Daryl le Blanc-bec au pavillon des Pérégrins.",
    ["T"] = "La vengeance de Vyrin",
  },
  [272] = {
    ["D"] = "Pour la seconde épreuve, vous aurez besoin des deux moitiés qui constitueront le Pendentif du lion de mer. L\'une des moitiés emprunte au lion de mer son agilité à glisser à travers les eaux. L\'autre moitié emprunte à l\'animal son incroyable endurance. Les druides doivent revêtir ces deux aspects pour pouvoir adopter leur forme aquatique.$B$BAllez parler aux résidents de Reflet-de-Lune pour apprendre où les deux parties se trouvent, puis rapportez-les ici, afin de pouvoir les joindre ensemble. Quand vous aurez formé le pendentif, apportez-le à Dendrite Starblaze.",
    ["O"] = "Trouver le Demi-pendentif d\'agilité aquatique et le Demi-pendentif d\'endurance aquatique. Parler aux habitants de Reflet-de-Lune pour obtenir des indices quant à l’endroit où ces objets se trouvent.$B$BReformer le Pendentif du lion de mer à partir des deux moitiés. Vous devez vous trouver à proximité du sanctuaire de Remulos pour faire ceci.$B$BApporter le Pendentif reformé à Dendrite Starblaze, au village de Havrenuit, à Reflet-de-Lune.",
    ["T"] = "L\'épreuve du lion de mer",
  },
  [273] = {
    ["D"] = "Cependant, il n\'est pas trop tard, votre aide est la bienvenue pour prêter main-forte à Huldar. Il ne peut pas être très loin : aussi forts qu\'ils soient, Miran et Saean ne peuvent pas se déplacer très vite avec les barils dont ils sont chargés.$b$bIls ont pris la route habituelle pour aller au site, celle qui suit la courbe du lac au sud de la ville.$b$bÇa ne devrait pas poser de problème de les rattraper à moins qu\'ils ne soient déjà arrivés.",
    ["O"] = "Aller voir Huldar.",
    ["T"] = "Approvisionnement du site de fouilles",
  },
  [274] = {
    ["D"] = "Ce n\'est pas un explosif ordinaire. Regardez ces petits cristaux et sentez cette odeur particulière ! C\'est de la poudre d\'hydroglycérine. Cette substance est sans danger quand elle n\'est pas humidifiée ; autrement, elle pourrait faire sauter tout Ironforge en une fois.$b$bLa réaction en chaîne peut être enrayée en mêlant quatre composants : du venin de rôdeuse, du cristal Mo\'Grosh pilé, une larme de crocilisque et ce colloïde de désamorçage. Allez prévenir Hinderweir au plus vite !",
    ["O"] = "Sur ordre d\'Ashlan Stonesmirk, retourner voir l\'Ingénieur en chef Hinderweir pour l\'informer de la nouvelle découverte.",
    ["T"] = "Une sombre menace se dessine",
  },
  [275] = {
    ["D"] = "Je ne sais pas pourquoi, mais il y a une prolifération maligne dans les Paluns. Les Rampants des tourbières se manifestent. Ils sont malades et répandent leur fièvre partout. Pour circonscrire l’épidémie, ils doivent être dispersés et renvoyés dans les marécages.$B$BLes Rampants des tourbières divaguent dans les voies navigables des Paluns.",
    ["O"] = "Tuer 12 Rampants des tourbières, puis retourner voir Rethiel, le Gouverneur vert, dans les Paluns.",
    ["T"] = "Fièvre sur le pays",
  },
  [276] = {
    ["D"] = "Les gnolls Poils-moussus ont vécu paisiblement dans les Paluns pendant longtemps, mais aujourd’hui leur nombre augmente. Les buissons et les fleurs du marais ne leur suffisent plus, et pour alimenter leurs feux, leurs haches crasseuses coupent trop de bois.$B$BL\'équilibre naturel n\'est plus respecté. Pour la survie des Paluns, le nombre de gnolls doit être réduit.$B$BTuez 15 Gnolls Poils-moussus et 10 Bâtards Poils-moussus. Chassez-les au sud, près des nains de la Porte d\'Algaz. Lorsque vous aurez terminé, revenez me voir.",
    ["O"] = "Tuer 15 Gnolls Poils-moussus et 10 Bâtards Poils-moussus puis retourner voir Rethiel, le Gouverneur vert, dans les Paluns.",
    ["T"] = "Piétinements de pattes",
  },
  [277] = {
    ["D"] = "Les Poils-moussus coupent les arbres des Paluns et les brûlent pour se chauffer. Je ne le supporte plus ! $B$BAllez leur prendre leurs silex bruts, les outils qu\'ils utilisent pour faire du feu. A l\'exception des simples gnolls et des bâtards, tous les Poils-moussus portent des silex.",
    ["O"] = "Rapporter à Rethiel, le Gouverneur vert, 9 Silex bruts.",
    ["T"] = "Feu interdit",
  },
  [278] = {
    ["D"] = "Commencez à collecter les matériaux de désamorçage, $N. Le venin de rôdeuse peut être collecté sur les araignées de Loch Modan. Vous trouverez les larmes de crocilisque dans le Loch également. Mais le cristal mo\'grosh sera plus difficile à obtenir. Les ogres dans le nord-est en exploitent dans leurs mines, mais c’est un minerai très rare.$b$bNous devons être prêts à enrayer l’attaque des Sombrefer ! Revenez quand vous aurez collecté les ingrédients requis.",
    ["O"] = "Collecter du Venin de rôdeuse, un Cristal mo\'grosh et une Larme de crocilisque pour l\'Ingénieur en chef Hinderweir.",
    ["T"] = "Une sombre menace se dessine",
  },
  [279] = {
    ["D"] = "Les murlocs sortent des eaux profondes et construisent leurs villages sur le littoral. Ils harcèlent nos pêcheurs et nos marchands, et il faut les arrêter.$B$BL\'un de ces murlocs, Enfourneur, tend des embuscades aux marchands le long de la route, avec d\'autres Branchies-bleues, et va ensuite se réfugier dans les taudis voisins. Nos marchands sont en danger et nous sommes prêts à payer pour les protéger.$B$BEnfourneur rôde le long du fleuve au nord d\'ici. Trouvez-le, éliminez sa famille et apportez-moi sa tête.",
    ["O"] = "Tuer 12 Murlocs Branchies-bleues.$B$BTuer Enfourneur et prendre sa tête.$B$BL\'apporter à Karl Boran au port de Menethil.",
    ["T"] = "Griffes des profondeurs",
  },
  [280] = {
    ["D"] = "Tout à l\'heure, j\'ai découvert quelques insurgés Sombrefer qui nageaient vers le barrage avec un tonnelet. Aucun Montagnard n\'était en mesure de les arrêter ! J\'ai bien peur qu\'ils n\'aient placé de l\'hydroglycérine à la base du barrage et ça pourrait exploser à tout moment ! Laissez-moi préparer une mixture de désamorçage. Allons-y.$b$bMaintenant, j\'ai besoin de vous pour prendre ce produit et le mélanger au contenu du tonnelet avant que ce ne soit trop tard ! Vite !",
    ["O"] = "Sur ordre de l\'Ingénieur en chef Hinderweir, nager jusqu\'à la base du barrage pour localiser le tonnelet de poudre et y verser la mixture de désamorçage pour empêcher une explosion.",
    ["T"] = "Une sombre menace se dessine",
  },
  [281] = {
    ["D"] = "Il y a peu de temps, un navire marchand, le \"Princesse aveugle\" a été attaqué par les murlocs. Tout ce qui n’intéressait pas les créatures a été jeté par-dessus bord, à commencer par une statuette de très grande valeur de Terenas Menethil. Nous voulons retrouver cette statuette.$B$BRecherchez des caisses, des barils et autres éléments de la cargaison perdue le long de la côte ou dans les taudis des murlocs. Retrouvez la Statuette de Menethil et rendez-la-moi.",
    ["O"] = "Rechercher les caisses et les barils dans les camps murlocs le long de la côte.",
    ["T"] = "Récupération de marchandises",
  },
  [282] = {
    ["D"] = "Je projetais d\'envoyer mon apprenti apporter mon rapport à mon frère Senir, mais je préférerais qu\'il soit placé entre des mains plus... fiables. Enfin, si vous êtes d\'accord, bien sûr !$b$bHum... Vous devrez prendre le tunnel pour arriver à Kharanos.$b$bAllez voir le Montagnard Thalos avant de traverser le tunnel. Il est complètement infesté de troggs maintenant.$b$bRetournez vers Anvilmar, puis continuez en prenant à l\'est vers le tunnel, Thalos n\'est pas loin.",
    ["O"] = "Aller voir le Montagnard Thalos.",
    ["T"] = "Les observations de Senir",
  },
  [283] = {
    ["D"] = "Le tonnelet pétille légèrement.",
    ["O"] = "La Mixture de désamorçage semble avoir fonctionné. Retourner voir l\'Ingénieur en chef Hinderweir pour lui annoncer la bonne nouvelle.",
    ["T"] = "Une sombre menace se dessine",
  },
  [284] = {
    ["D"] = "Vous avez fouillé avec soin les taudis murlocs dans ce secteur et vous n\'avez trouvé aucune trace de la Statuette de Menethil.$B$BIl y a peut-être davantage d\'indices dans les deux taudis qui se trouvent tout près de vous au nord et au nord-est.",
    ["O"] = "Rechercher la Statuette de Menethil.",
    ["T"] = "La recherche continue",
  },
  [285] = {
    ["D"] = "L\'inspection minutieuse de ces quelques taudis murlocs n\'a rien donné.$B$BMais le prochain indice sera le bon.$B$BL\'un des taudis qui se situent au nord doit cacher la Statuette de Menethil !",
    ["O"] = "Continuer à fouiller les taudis des murlocs pour retrouver la Statuette de Menethil.",
    ["T"] = "À la recherche d\'autres taudis",
  },
  [286] = {
    ["D"] = "Une fois le baril sorti de la boue et ouvert, on y trouve une figurine argentée empaquetée avec soin dans la paille. Elle représente un jeune paladin aux cheveux longs, qui brandit un marteau.$B$BCe doit être la Statuette de Menethil ! Vous devez maintenant l’apporter à Karl Boran au Port de Menethil.",
    ["O"] = "Apporter la Statuette de Menethil à Karl Boran.",
    ["T"] = "Rapporter la statuette",
  },
  [287] = {
    ["D"] = "Je suis sur mon rapport et j\'ai besoin de votre aide, $n. Je viens juste de trouver où se cachent les trolls, mais ils sont si nombreux que j\'ai peur d\'y aller.$b$bVoici ce que j\'attends de vous... Allez à la grotte, entrez, tuez quelques trolls et revenez ici.$b$bPrenez la route au nord de Kharanos, puis quand vous arriverez au pont, longez la rivière gelée par l\'ouest jusqu\'au Lac glacial. Vous trouverez le village de Brassetout sur la rive ouest. Leur repaire est au sud-ouest du village.",
    ["O"] = "Explorer le repaire des Frostmane et tuer 5 Chasseurs de tête Frostmane pour Senir Whitebeard à Kharanos.",
    ["T"] = "Le repaire des Frostmane",
  },
  [288] = {
    ["D"] = "J\'étais autrefois le Premier Officier du « Maîtresse de l\'onde », l\'un des bateaux de la IIIe flotte de Kul Tiras. La Flotte perdue. Perdue parce qu\'à son dernier voyage, elle a été attaquée par des dragons rouges. Des dragons conduits par des orcs !$B$BNous n\'avions aucune chance contre le feu des dragons... Notre flotte a été brûlée et envoyée par le fond. Je suis le seul survivant de ces malheureux navires...$B$B... Oh ça cogne dans ma tête ! Il me faudrait un coup à boire pour supporter cette gueule de bois et continuer mon histoire.",
    ["O"] = "Acheter une Cruche d\'hydromel pour le Premier Officier Fitzsimmons.",
    ["T"] = "La IIIe flotte",
  },
  [289] = {
    ["D"] = "<Revigoré, Fitzsimmons continue son histoire...> $B$BJe suis le seul survivant de l\'infortunée IIIe flotte de Kul Tiras, détruite au large de la côte, vers le nord. Mais bien que je sois le seul membre d’équipage qui respire toujours, je ne suis pas le seul qui marche encore ! Car sur l\'un de nos bateaux a été caché un trésor épouvantable, dont le pouvoir énorme maintient prisonnières les âmes de mes frères d\'armes dans les épaves pourries de nos bateaux.$B$BMais si vous parveniez à libérer quelques-uns de mes frères, peut-être pourriez-vous briser la malédiction.",
    ["O"] = "Tuer 13 Marins maudits, 5 Soldats de marine maudits et le Premier officier Snellig. Rapporter la Tabatière de Snellig au Premier officier Fitzsimmons au port de Menethil.",
    ["T"] = "L\'équipage maudit",
  },
  [290] = {
    ["D"] = "Pour rompre la malédiction et libérer mes frères d\'armes, la... chose... qui se trouve sur mon bateau doit être récupérée et nettoyée ! Je ne sais pas ce que c\'est, mais je sais qu\'elle est en lieu sûr dans le coffre-fort du bateau, dont seul le capitaine Halyndor avait la clé.$B$BMon vieux capitaine rôde toujours dans mes rêves. Je sais qu\'il erre sur \"l\'Intrépide\", mon bateau perdu. Obtenez de lui sa clé et utilisez-la pour ouvrir le coffre de \"l\'Intrépide\".$B$BEnsuite, quand vous aurez cet objet maudit, apportez-le au prêtre Glorin Steelbrow en ville.",
    ["O"] = "Obtenir du capitaine Halyndor la Clé du coffre-fort de \"l\'Intrépide\".$B$BUtiliser la clé pour ouvrir le Coffre verrouillé de l\'Intrépide.",
    ["T"] = "Levée de la malédiction",
  },
  [291] = {
    ["D"] = "Excellent ! Apportez mon rapport au Sénateur Barin Redstone. C\'est un type plutôt aigri, alors ne vous laissez pas impressionner. Il est à Ironforge, dans la salle où se tient la cour du roi Magni.$b$bVous ne connaissez pas la route pour Ironforge ? Prenez la route au nord de Kharanos, traversez le pont, suivez ensuite la route de l\'est – il y a de grandes bannières le long de la route – jusqu\'en haut de la montagne.$b$bCela dit, si vous pouviez éviter de raconter que vous m\'avez aidé... Je ne veux pas qu\'ils pensent que je ne bosse pas assez dur ici, vous comprenez ?",
    ["O"] = "Livrer le rapport de Senir au Sénateur Barin Redstone à Ironforge.",
    ["T"] = "Les rapports",
  },
  [292] = {
    ["D"] = "En ouvrant le coffre, on trouve une sphère noircie, qui irradie une lumière à faire frémir. C\'est le trésor dont Fitzsimmons a parlé : un Oeil maudit de Paleth. $B$BApportez l\'Oeil maudit de Paleth au prêtre Glorin Steelbrow, un nain qui se trouve au port de Menethil. Demandez-lui comment lever la malédiction de l\'Oeil.",
    ["O"] = "Apporter l\'Oeil maudit de Paleth à Glorin Steelbrow.",
    ["T"] = "L\'Oeil de Paleth",
  },
  [293] = {
    ["D"] = "Je ne peux pas lever la malédiction de l\'Oeil de Paleth. Ces Yeux ont été fabriqués par des prêtres humains, vous devez donc aller à Stormwind pour trouver quelqu\'un qui pourra vous aider. Allez voir l\'archevêque Benedictus, le maître de la cathédrale de la Lumière de Stormwind. Il vous aidera.$B$BJe l\'espère pour vous.",
    ["O"] = "Apporter l\'Oeil maudit de Paleth à l\'archevêque Benedictus, à la cathédrale de la Lumière de la ville de Stormwind.",
    ["T"] = "Purifier l\'Oeil",
  },
  [294] = {
    ["D"] = "La situation est grave, c\'est certain. Les raptors ont été attirés par les ossements. Ces sales bêtes ont tué mes compagnons et nous ont coincés ici, Merrin, le pauvre Prospecteur et moi.$b$bAidez-nous à nettoyer les Paluns de ces raptors, $N. Il y a des raptors tachetés et des Hurleurs tachetés à l\'ouest. Tuez-en 10 de chaque, si vous le pouvez.$b$bCe sera un bon début à la vengeance que je leur réserve.",
    ["O"] = "Sur ordre d\'Ormer Ironbraid à l\'Excavation de Whelgar, tuer 10 Hurleurs tachetés et 10 Raptors tachetés.",
    ["T"] = "La vengeance d\'Ormer",
  },
  [295] = {
    ["D"] = "Il est vraiment temps de faire payer à ces raptors leur soif sanguinaire. Un peu plus bas, il y a d’innombrables Faucheurs tachetés et Tranchegueules tachetés. Tuez 10 Faucheurs et 10 Tranchegueules !",
    ["O"] = "Tuer 10 Faucheurs tachetés et 10 Tranchegueules tachetés pour Ormer Ironbraid, puis aller le voir à l\'excavation de Whelgar.",
    ["T"] = "La vengeance d\'Ormer",
  },
  [296] = {
    ["D"] = "Lorsque vous étiez là-bas, j\'ai remarqué qu\'un de ces monstres se tenait à l\'écart des autres. Il était plus grand et plus menaçant. Je parie que c\'est lui qui a incité les autres à venir ici interrompre les fouilles.$b$bJ\'ai une dernière chose à vous demander, $N. Faites en sorte que Grondecroc réponde de ses crimes à la justice. Et compte tenu de la gravité de ses crimes, la justice dans ce cas, c\'est la mort ! Apportez-moi une de ses serres pour preuve de sa mort.",
    ["O"] = "Sur ordre d\'Ormer Ironbraid, de l\'Excavation de Whelgar, tuer Grondecroc et revenir avec l\'une de ses serres une fois la tâche accomplie.",
    ["T"] = "La vengeance d\'Ormer",
  },
  [297] = {
    ["D"] = "Récemment, juste avant que les troggs ne débarquent sur le site, nous avons découvert un grand nombre d\'étranges idoles taillées. Mais nous n\'avons pas eu le temps de les étudier, car les troggs nous ont chassés des ruines juste après ! Et ces idoles ont un curieux effet sur les troggs : elles les rendent fous furieux !$B$BRapportez-moi 8 de ces idoles. Je veux les étudier et il ne faut pas qu\'elles restent aux mains des troggs ! Vous pouvez trouver les idoles sur les troggs qui infestent le site.",
    ["O"] = "Apporter 8 Idoles de pierre sculptée à Magmar Fellhew.",
    ["T"] = "Réunir les idoles",
  },
  [298] = {
    ["D"] = "Je dois envoyer un compte rendu d\'avancement à Thelsamar et une nouvelle demande pour avoir plus d\'explosif ! Voici le rapport. Portez-le à Jern Hornhelm. C\'est notre contact local à Thelsamar.$B$BCe n\'est pas une tâche difficile, mais ça doit être fait.",
    ["O"] = "Apporter le Compte rendu d\'avancement d\'Ironband à Jern Hornhelm à Thelsamar.",
    ["T"] = "Compte rendu d\'avancement des fouilles",
  },
  [299] = {
    ["D"] = "Avant l\'invasion, j\'ai découvert une tablette appelée la Pierre de Goaz. Elle s\'est brisée en quatre morceaux. Elle parle d\'un \"plan divin\" et d\'une \"prophétie damnée\".$b$bJe crains que nous n\'ayons pas beaucoup de temps. Je suis trop vieux pour aller affronter ces bêtes. Mais vous êtes solide.$b$bExplorez le site de fouilles et cherchez les 4 fragments : Ados, Modr, Golm et Neru. Cherchez dans les artefacts anciens, ou bien là où le sol est meuble. Apportez-les, que je puisse résoudre le mystère !",
    ["O"] = "À la demande du prospecteur Whelgar, parcourir l\'Excavation à la recherche des 4 fragments de tablette : Ados, Modr, Golm et Neru.",
    ["T"] = "Découverte du passé",
  },
  [301] = {
    ["D"] = "Nous aurons besoin d\'une autorisation pour fournir à Ironband plus d\'explosif. Vous devrez apporter son rapport au quartier général de la Ligue des Explorateurs à Ironforge.$B$BVoici ce rapport. Donnez-le au Prospecteur Stormpike et mettez-vous à l\'abri juste après ! Stormpike est impétueux et il ne sera pas ravi d\'apprendre qu\'Ironband ne progresse que lentement sur le site.$B$BVous trouverez Stormpike à l\'Assemblée des Explorateurs.",
    ["O"] = "Porter le Compte rendu d\'avancement d\'Ironband au Prospecteur Stormpike.",
    ["T"] = "Rapport à Ironforge",
  },
  [302] = {
    ["D"] = "Si Ironband a encore besoin d\'explosif, par Magni, alors il l\'aura ! J\'ai envoyé un messager à Thelsamar avec une autorisation pour Jern Hornhelm. Il préparera la poudre et du matériel à remettre à Ironband.$B$BEncore une chose : je veux que vous assuriez personnellement la livraison pour que l\'explosif arrive à bon port. Le dernier chargement n\'y est pas arrivé, alors gardez l\'esprit vif et les yeux ouverts.",
    ["O"] = "Parler à Jern Hornhelm à Thelsamar.",
    ["T"] = "De l\'explosif pour Ironband",
  },
  [303] = {
    ["D"] = "La guerre fait rage ici autant qu\'à l\'étranger. Tandis que nos valeureux nains donnent leur vie loin de chez eux au nom de l\'Alliance, nos propres terres sont la proie des nains Sombrefer. Ils ont eu raison du viaduc de Thandol. Dun Modr est tombée peu après.$b$bNous avons besoin de vous pour reprendre des forces et repousser le front. Si vous pouvez tuer 15 nains Sombrefer, 5 Tunneliers, 5 Saboteurs et 5 Démolisseurs, nos réservistes pourront être là à temps pour conduire l\'offensive.$b$bFaites vite !",
    ["O"] = "Sur ordre de Motley Garmason à Dun Modr, tuer 15 nains Sombrefer : 5 Tunneliers Sombrefer, 5 Saboteurs Sombrefer et 5 Démolisseurs Sombrefer.",
    ["T"] = "La guerre contre les Sombrefer",
  },
  [304] = {
    ["D"] = "Beaucoup de mes soldats sont morts à la bataille de Dun Modr. Mon propre frère a péri lors de la destruction du pont de l\'ouest du viaduc de Thandol. Oui, les nains Sombrefer sont une race diabolique. D\'après ce que Roggo a réussi à apprendre, le chef de la bande de nervis qui se trouve dans les Paluns est un démoniste du nom de Balgaras le Crasseux. Et c\'est un véritable lâche. Il envoie ses soldats en première ligne, mais il reste invisible et il évite le danger.$b$bSi vous parvenez à trouver cette racaille, à l\'exécuter et à m\'apporter son oreille, je vous récompenserai, $c.",
    ["O"] = "Tuer Balgaras le Crasseux et rapporter son oreille à Longbraid le Macabre à Dun Modr.",
    ["T"] = "Une tâche sinistre",
  },
  [305] = {
    ["D"] = "Oh bon sang ! Vous devez être l\'éclaireur que Longbraid a promis d\'envoyer ! Vous ne pouvez pas imaginer mon inquiétude.$b$bMa femme, Merrin, m\'envoie des lettres toutes les semaines depuis le mois dernier. Elle fait partie de l\'équipe de fouilles de Whelgar. Mais il s\'est écoulé presque deux semaines depuis sa dernière lettre. Il y a sûrement quelque chose qui ne va pas !$b$bLongbraid a dit qu\'il enverrait un éclaireur aux nouvelles si nous n\'avions pas rapidement du nouveau. Je suppose que l\'éclaireur, c\'est vous, $N. Allez voir Merrin à l\'Excavation, à l\'est !",
    ["O"] = "À la demande de Tarrel Rockweaver, aller jusqu\'à l\'Excavation et contacter Merrin Rockweaver.",
    ["T"] = "À la recherche de l\'équipe de fouilles",
  },
  [306] = {
    ["D"] = "C\'était horrible ! Nous avions découvert quelques artefacts rares et nous commencions à nous intéresser à ces grands ossements.$b$bAvant d\'avoir compris ce qui se passait, ces raptors marbrés ont déboulé sur le site. Il y a eu beaucoup de morts.$b$bC\'est Ormer – et avec quel courage ! – qui nous a sauvés, le Prospecteur et moi. Il nous a couverts pendant que nous courions nous mettre à l\'abri. Maintenant nous sommes pris au piège. Et nous avons perdu le fruit de notre dur labeur.$b$bTenez, apportez cette lettre à mon cher Tarrel pour lui expliquer la situation.",
    ["O"] = "Retourner voir Tarrel Rockweaver avec la lettre de Merrin.",
    ["T"] = "À la recherche de l\'équipe de fouilles",
  },
  [307] = {
    ["D"] = "La mine du Ru d\'argent à l\'est est épuisée depuis longtemps. Elle a été convertie en dépôt de stockage par la Ligue des Mineurs, mais aujourd’hui les kobolds s\'y sont installés, et ils mettent leurs pattes sales sur nos bons vieux outils !$B$BNous chasserons bientôt cette vermine de là, mais avant d\'envoyer des guerriers qui feront forcément un peu de casse, la Ligue veut que quelqu\'un aille chercher son équipement. Ce ne sera pas facile, vous pourriez avoir besoin de l\'aide d\'alliés.$B$BL\'équipement de mineur est stocké dans les caisses de la Ligue des Mineurs un peu partout dans la mine. Bonne chance !",
    ["O"] = "Aller à la Mine du Ru d\'argent pour prendre 4 lots d\'Equipement de mineur.$B$BRetourner voir le Montagnard Stormpike.",
    ["T"] = "Bas les pattes !",
  },
  [308] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Distraire Jarven",
  },
  [309] = {
    ["D"] = "Prêt à y aller, $n ?$b$bD\'abord, nous devons trouver de l\'explosif pour Ironband. Je ne pourrai pas le porter seul, d\'autant que ça peut être dangereux – et qui sait ce que les Sombrefer me réservent ?$b$bJe me sentirais mieux si vous m\'accompagniez.",
    ["O"] = "S\'assurer que Miran et la cargaison parviennent au Site de fouilles puis en informer le Prospecteur Ironband.",
    ["T"] = "Protection de la cargaison",
  },
  [310] = {
    ["D"] = "Je dois admettre que ces Thunderbrew font quelques bonnes boissons. Mais il faut qu’ils comprennent qu\'ils ne sont pas les seuls brasseurs ! Vous pouvez peut-être m\'aider à faire passer le message... $B$BTenez, prenez ce tonneau de Barleybrew Scalder. Glissez-vous dans le sous-sol de la distillerie Thunderbrew, à Kharanos, et échangez-le avec un de leurs tonneaux de Thunder Ale. Nous verrons bien si leurs clients préfèrent leur recette ou la mienne !$B$BBien sûr, si les tonneaux sont gardés, il faudra peut-être distraire le garde...",
    ["O"] = "Aller au sous-sol de la distillerie Thunderbrew à Kharanos et remplacer un Tonneau de Thunder Ale par un Tonneau de Barleybrew Scalder.",
    ["T"] = "Bières rivales",
  },
  [311] = {
    ["D"] = "Après avoir mis un tonneau de chez Marleth Scalder dans la cave de la Distillerie Thunderbrew, vous emportez un Tonneau de Thunder Ale pour prouver que vous l\'avez vraiment fait...",
    ["O"] = "Apporter le Tonneau de Thunder Ale à Marleth Barleybrew au Village de Brassetout.",
    ["T"] = "Retour chez Marleth",
  },
  [312] = {
    ["D"] = "J\'avais un mois de réserves de viande séchée et salée que j\'avais stockées pour la saison froide. C\'était hors d\'atteinte des ours, dans une caisse fermée ! Mais pendant que j\'étais à la chasse au daim, cette bête, le Vieux Barbeglace, est venue et repartie avec ma caisse de viande. Il ne sera pas capable de défoncer le thorium de la caisse. Mais je vais mourir de faim si je ne peux pas récupérer mes réserves de viande.$B$BVous semblez plus discret que moi. Peut-être parviendrez-vous à récupérer ma caisse ? Je n\'arriverai jamais à entrer et sortir assez vite de cette caverne avec ma jambe qui se traîne.",
    ["O"] = "Récupérer la caisse de viande séchée de Tundra MacGrann dans la Caverne du Vieux Barbeglace.",
    ["T"] = "La réserve volée de Tundra MacGrann",
  },
  [313] = {
    ["D"] = "La conduite d\'une machine de siège n\'est pas à la portée de n\'importe qui. Il faut une poigne de fer et des nerfs d\'acier... Par chance, j\'ai les deux ! Et vous ? De quel bois êtes-vous fait ? Vous voulez le prouver ?$B$BBon... Je cherche comment rendre mon engin, le \"Moissonne-trolls\", un peu plus confortable. Et si vous alliez me chercher quelques peaux de Wendigo dans l\'Antre Gris, à l\'ouest de Kharanos ? On pourrait en tapisser le pont intérieur de ma machine !",
    ["O"] = "Réunir 8 Peaux de Wendigo et les apporter au Pilote Stonegear.",
    ["T"] = "L\'antre Gris",
  },
  [314] = {
    ["D"] = "Nous avons entendu des cris au milieu de la nuit. Et ce matin, il manquait deux béliers dans le troupeau. C\'est cette bête poltronne connue sous le nom de Vagash qui s\'en est prise à notre gagne-pain. Vu que la grande partie de l\'armée du roi Magni est loin d\'ici, en train de se battre avec l\'Alliance, il n\'y a personne pour tenir Vagash à distance.$b$bVous avez peut-être assez de courage pour poursuivre la bête et la tuer. Apportez-moi un de ses crocs et je vous récompenserai. Vagash se cache juste au-dessus du ranch, mais soyez $gprudent:prudente;, il est très dangereux.",
    ["O"] = "Sur demande de Rudra Amberstill, tuer Vagash et rapporter un de ses crocs à la ferme des Amberstill.",
    ["T"] = "Protection du troupeau",
  },
  [315] = {
    ["D"] = "Je cherche à faire la bière parfaite. Je sais que je peux le faire : le brassage, j\'ai ça dans le sang. Je dois juste trouver la bonne recette...$B$BLes trolls Frostmane cultivent une plante, la chatoyante, en haut des collines à l\'est. Ils l\'utilisent dans les rituels de leur tribu. Nous les nains, nous ne lui avons pas trouvé d\'usages, mais elle a un goût unique... Et je veux essayer !$B$BApportez-moi de la chatoyante qu\'utilisent les Prophètes Frostmane, ou piquez-en dans les paniers à chatoyante des trolls.",
    ["O"] = "Apporter 6 Brins de chatoyante à Rejold Barleybrew au village de Brassetout.",
    ["T"] = "La bière parfaite",
  },
  [316] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [317] = {
    ["D"] = "Je me prépare à entamer une mission pour la brigade des Sapeurs. C\'est une longue mission et j\'ai besoin de ravitailler le Crache-vapeur avec l\'équivalent d\'un mois de provisions. Alors pendant que Steelgrill travaille sur mon réservoir, pouvez-vous aller chasser pour moi ?$B$BJ\'ai besoin de fourrure pour nos lits et de viande pour nous nourrir. Vous pouvez rapporter de la viande de sanglier et de la fourrure d\'ours... Vous trouverez l\'un et l\'autre dans les terrains enneigés au sud de l\'Antre Gris.",
    ["O"] = "Réunir 4 morceaux de Viande de sanglier et 2 Fourrures d\'ours épaisses et les livrer au pilote Bellowfiz au dépôt de Steelgrill.",
    ["T"] = "Ravitailler le Crache-vapeur",
  },
  [318] = {
    ["D"] = "Rejold Barleybrew fait des expériences de brassage. Certaines ont bon goût, d\'autres sont mauvaises et certaines... Eh bien, certaines mettraient par terre les plus vaillants des nains !$B$BCe brasseur habite à Brassetout, à l\'ouest de Kharanos, et j\'ai besoin de l\'une de ses boissons : l\'Evershine. Ça me tiendra au chaud quand je serai sur les routes glacées.$B$BEt... ce liquide béni brûle si bien que je peux le jeter dans la chaudière du Crache-vapeur pour avoir un peu plus de puissance ! Cette petite tactique m\'a servi plus d\'une fois.",
    ["O"] = "Demander un tonneau d\'Evershine à Rejold Barleybrew au village de Brassetout.",
    ["T"] = "L’Evershine",
  },
  [319] = {
    ["D"] = "J\'ai un tonneau d\'Evershine sous la main, mais... Pouvez-vous me faire une faveur ? Mon village aurait besoin d\'un coup de main pour se débarrasser des animaux sauvages qui errent dans les neiges voisines. Parfois ils rôdent tout près.$B$BNous n\'avons pas peur de ces oursons, de ces chats et de ces cochons – aucun nain digne de ce nom n\'en aurait peur – mais si vous pouviez vous occuper de ces bestioles, nous aurions plus de temps pour travailler.",
    ["O"] = "Tuer 6 Ours Griffeglace, 8 Anciens sangliers des rochers et 8 Léopards des neiges, puis retourner voir Rejold Barleybrew à Brassetout.",
    ["T"] = "Une faveur pour un tonneau d’Evershine",
  },
  [320] = {
    ["D"] = "Voici votre tonneau d\'Evershine, $N. Et merci encore pour la partie de chasse.",
    ["O"] = "Donner le Tonneau d\'Evershine au Pilote Bellowfiz au Dépôt de Steelgrill.",
    ["T"] = "Retour vers Bellowfiz",
  },
  [321] = {
    ["D"] = "Les épaves qui se trouvent près du rivage sont les débris de la Flotte perdue de Kul Tiras, brûlée et coulée par les dragons rouges pendant la dernière Grande guerre.$B$BLa flotte venait de Lordaeron, mais il y avait aussi un destroyer elfique, \"l\'Albatros\". Il transportait une cargaison de fer de sancteforge et avait rejoint la IIIe flotte pour profiter de sa protection. Ironie du sort, il a été coulé par les mêmes dragons...$B$BMais assez raconté d\'histoires. L\'épave de \"l\'Albatros\" est au sud du port de Menethil. Si vous trouvez du fer de sancteforge là-bas, apportez-le moi.",
    ["O"] = "Fouiller l\'épave de l\'Albatros, dans les Paluns.",
    ["T"] = "Le fer de sancteforge",
  },
  [322] = {
    ["D"] = "Il y a un nain, fabricant d\'armes dans le quartier des nains de Stormwind, qui sait travailler le fer de sancteforge. Il s\'appelle Grimand Elmore.$B$BJ\'ai emballé le métal que vous avez trouvé dans une caisse. Apportez-le à Grimand et racontez-lui votre histoire. Je suis sûr qu\'il est prêt à aider quiconque mène une quête comme la vôtre et il vous fabriquera une arme – une arme qui donnera du fil à retordre à Morbent Fel lui-même !",
    ["O"] = "Apporter la Caisse de lingots de sancteforge à Grimand Elmore, à Stormwind.",
    ["T"] = "Béni soit ce bras",
  },
  [323] = {
    ["D"] = "Morbent Fel est un nécromancien, allié des Cavaliers noirs. Je pourrais vous envoyer combattre Morbent Fel, mais je dois d’abord tester votre force avant que vous ne luttiez contre un tel adversaire.$B$BVous avez déjà prouvé votre bravoure, mais si vous voulez vraiment affronter le nécromancien, vous devez d’abord montrer votre valeur contre ses sbires.",
    ["O"] = "Tuer 15 Écumeurs squelettes, 3 Soigneurs squelettes et 3 Gardiens squelettes puis retourner voir Sven.",
    ["T"] = "Prouver votre valeur",
  },
  [324] = {
    ["D"] = "Puisque vous n\'avez trouvé qu\'un seul lingot de sancteforge dans le coffre, il paraît évident que les murlocs ont volé le reste. Pourchassez les écumeurs murlocs près de l\'épave de \"l\'Albatros\" pour retrouver le fer de sancteforge volé.",
    ["O"] = "Collecter 5 Lingots de sancteforge puis retourner voir Glorin Steelbrow, dans les Paluns.",
    ["T"] = "Les lingots perdus",
  },
  [325] = {
    ["D"] = "Voici l\'arme pour combattre le démon dont vous avez parlé. Vous pouvez y aller, $N. Je vous souhaite bonne chance et j\'attends avec impatience d\'entendre raconter vos exploits !",
    ["O"] = "Apporter le Fléau de Morbent à Sven, dans le bois de la Pénombre.",
    ["T"] = "En armes et prêt",
  },
  [328] = {
    ["D"] = "Bien qu\'il ne soit jamais fait mention du soldat Thorsen dans ces rapports, une petite clé en cuivre était cachée dans un des registres d\'Herod.$B$BEt en poussant plus loin l\'étude de ces notes, on trouve au milieu de divers papiers des références à un \"coffre-fort sécurisé\". Il est quelque part dans la Réserve, l\'ensemble de cavernes qui se trouve derrière la Base de Kurzen.$B$BSi nous retrouvons ce coffre-fort et que la petite clé peut l\'ouvrir, nous résoudrons peut-être le mystère de Thorsen.",
    ["O"] = "Utiliser la Petite Clé en cuivre pour ouvrir le Coffre-fort de Herod la Paperasse.",
    ["T"] = "La clé cachée",
  },
  [329] = {
    ["D"] = "Parmi les objets précieux contenus dans le coffre-fort, il y a une note griffonnée et couverte de boue. Bien qu\'elle soit à peine lisible, on dirait un itinéraire pour le soldat Thorsen. Il y a des détails sur ses itinéraires de patrouille, les gens auxquels il parle, ce qu\'il fait de son temps avec les rebelles. Elle n\'est signée que d\'une lettre : M.$B$BMais celui ou celle qui a écrit cette note doit être proche de Thorsen. Celui ou celle qui a écrit cette note est le traître du camp rebelle.",
    ["O"] = "Apporter la Note maculée de boue au lieutenant Doren, au Camp rebelle.",
    ["T"] = "L\'espion découvert !",
  },
  [330] = {
    ["D"] = "La note que vous m\'avez donnée est signée d\'un \" M \"... Je ne sais pas ce que ça signifie.$B$BMais... La note dit aussi que cette personne a partagé les tours de patrouille de Thorsen la semaine dernière. Le traître doit être l\'un de ceux qui ont patrouillé en même temps que Thorsen !$B$BAllez voir le caporal Sethman. C\'est lui qui gère la composition des patrouilles, il vous dira qui était avec Thorsen la semaine dernière.",
    ["O"] = "Parler au caporal Sethman.",
    ["T"] = "Composition des patrouilles",
  },
  [331] = {
    ["D"] = "Le Lieutenant Doren a-t-il demandé que vous retourniez le voir avec cette information concernant les patrouilles ?",
    ["O"] = "Parler au lieutenant Doren.",
    ["T"] = "Rapport au lieutenant Doren",
  },
  [332] = {
    ["D"] = "Si vous parlez à qui que ce soit de vin, vous apprendrez que nous, les Gallina, nous vendons le meilleur vin de Stormwind. Et nous ne sommes pas loin, dans le Quartier commerçant, le long des superbes canaux de la ville.$B$BTenez, prenez ce bon d’échange. Apportez-le à ma sœur Suzetta dans notre magasin, et elle vous offrira une bouteille de bienvenue de notre célèbre pinot noir. Vous ne le regretterez pas !",
    ["O"] = "Aller à la Cave à vins des Gallina et apporter à Suzetta Gallina le Bon pour une bouteille de vin gratuite.",
    ["T"] = "Publicité pour la cave à vins Gallina",
  },
  [333] = {
    ["D"] = "Nous avons fait pas mal d\'affaires dernièrement. On dirait que tout le monde achète des armures et des vêtements résistants. Comme s\'ils s\'attendaient à une saison froide et dure... $B$BMais ce sont là des soucis pour plus tard. Mon souci aujourd\'hui, c\'est que je suis à court de vêtements tricotés à vendre. J\'ai besoin d\'un autre lot de mon fournisseur.$B$BSi vous pouviez en prendre livraison auprès de Rema Schneider, chez le tailleur et équipementier du canal, je vous en serais très reconnaissant.",
    ["O"] = "Aller à la Boutique de tailleur du canal et donner à Rema Schneider la Demande de tissu, pour Harlan Bagley.",
    ["T"] = "Harlan a besoin d\'être approvisionné",
  },
  [334] = {
    ["D"] = "Mon fils Thurman est apprenti chez les Couturiers Larson, dans le Quartier des mages. Il était pressé aujourd\'hui et il a oublié ses ciseaux et ses aiguilles. Je sais qu\'un grand $c comme vous a des tâches autrement plus importantes à accomplir, mais sans ses outils, Thurman ne peut pas faire son travail d\'apprenti !$B$BS\'il vous plaît, $N, pouvez-vous rapporter à mon fils son matériel de couture ? Les Couturiers Larson tiennent l\'un des deux magasins de prêt-à-porter du Quartier des mages, c\'est le plus éloigné, près de la Tour du mage.",
    ["O"] = "Aller chez les Couturiers Larson, dans le quartier des mages de Stormwind, pour donner à Thurman Schneider son Matériel de couture.",
    ["T"] = "Un paquet pour Thurman",
  },
  [335] = {
    ["D"] = "Le seigneur Wishock sème le trouble à la Maison des Nobles. Il sait pour notre petit \"établissement\" et fait pression pour qu\'une enquête soit ouverte. J’ai un plan pour « occuper » Wishock, mais je vais avoir besoin de votre aide.$b$bIl y a une fleur qui pousse dans le jardin de Stalvan aux environs de Darkshire. On appelle cette petite fleur blanche la larme de Tilloa. J\'aurai aussi besoin d\'une racine de musquash, qu\'on ne trouve qu\'en bas des chutes du barrage de Formepierre dans les Paluns.",
    ["O"] = "Sur ordre de Zardeth de la Griffe noire, rapporter une Larme de Tilloa de Darkshire et une Racine de Musquash des Paluns.",
    ["T"] = "Un noble brassage",
  },
  [336] = {
    ["D"] = "Le seigneur Wishock est comme tous les hommes, il a ses faiblesses. Il se trouve que la sienne est un penchant prononcé pour la bouteille. Quelle sidérante hypocrisie. Ce pauvre imbécile passe ses journées à essayer de faire fermer notre petite « taverne », et ses nuits à l’étage à rallonger une ardoise qu’il ne payera jamais. Et bien nous allons voir comment Wishock apprécie la cuvée spéciale que je lui ai préparée : la bière brune de la Griffe noire. C’est du brutal, $n.$b$bApportez-lui et voyez comment il réagit. Probable que ce vieil idiot soit en train de traîner autour de la Chambre du Requérant.",
    ["O"] = "Apporter la Bière brune de la Griffe noire au seigneur Wishock dans la Chambre du requérant.",
    ["T"] = "Un noble brassage",
  },
  [337] = {
    ["D"] = "Bien que la couverture de ce livre soit éraflée et couverte de boue, on arrive à lire le titre \"L\'Histoire de Stormwind\" sur la tranche. En l’ouvrant, on voit clairement tamponné sur la première page le cachet de la Bibliothèque de Stormwind.$B$BOn dirait que quelqu\'un a emprunté ce livre et ne l\'a jamais rendu...",
    ["O"] = "Rendre le livre « l\'Histoire de Stormwind » à la Bibliothèque de Stormwind.",
    ["T"] = "Un vieux livre d\'histoire",
  },
  [338] = {
    ["D"] = "Maître Nesingwary va être tellement furieux s\'il découvre ça !$b$bIl m\'a fait confiance pour relire le manuscrit de son roman « Les vertes collines de Strangleronce ». Tout allait bien, mais une grande rafale de vent a soufflé sur le camp et a dispersé toutes les pages !$b$bJ\'ai pu en ramasser la plupart, mais certaines sont toujours perdues dans la jungle. Retrouvez les pages qui manquent pour compléter les quatre chapitres.$b$bJe vous aiderai à les remettre dans l\'ordre. Rendez-moi les chapitres une fois que vous les aurez tous les quatre.",
    ["O"] = "Rassembler les pages manquantes du manuscrit des « Vertes collines de Strangleronce ». Une fois les quatre chapitres complétés, les rendre à Barnil.",
    ["T"] = "« Les vertes collines de Strangleronce »",
  },
  [339] = {
    ["D"] = "Le Chapitre I était vraiment fascinant ! Maître Nesingwary plante le décor de l\'histoire et décrit ses compagnons de chasse, moi y compris !$b$bMais c\'est un détail. Laissez-moi voir s’il manque des pages. On dirait que les pages 1, 4, 6 et 8 sont toujours quelque part dans la jungle !",
    ["O"] = "Rapporter les pages 1, 4, 6 et 8 des Vertes collines de Strangleronce de Nesingwary à Barnil Stonepot pour achever le Chapitre I.",
    ["T"] = "Chapitre I",
  },
  [340] = {
    ["D"] = "Le Chapitre II regorgeait de passionnants récits de chasse au tigre et à la panthère. Je me donnerais des coups pour avoir laissé les pages s\'envoler !$b$bJ’ai récupéré toutes les pages à l\'exception des pages 10, 11, 14 et 16.",
    ["O"] = "Rapporter les pages 10, 11, 14 et 16 des « Vertes collines de Strangleronce » de Nesingwary à Barnil Stonepot pour achever le Chapitre II.",
    ["T"] = "Chapitre II",
  },
  [341] = {
    ["D"] = "Le chapitre III est mon préféré. Il parle de la première fois où le sire Erlgadin et dame Rouack ont tué avec un fusil des nains. Et la fin du chapitre, quel suspense !$b$bJe vois qu\'il nous manque encore les pages 18, 20, 21 et 24 pour clore le chapitre. Ces pages sont quelque part par là...",
    ["O"] = "Rapporter les pages 18, 20, 21 et 24 des « Vertes collines de Strangleronce » de Nesingwary à Barnil Stonepot pour achever le Chapitre III.",
    ["T"] = "Chapitre III",
  },
  [342] = {
    ["D"] = "Ah Le Chapitre IV : le grand final ! Et quel final !$b$bIl ne nous manque plus que trois pages, les 25, 26 et 27, pour compléter le Chapitre IV.",
    ["O"] = "Rapporter les pages 25, 26 et 27 des « Vertes collines de Strangleronce » de Nesingwary à Barnil Stonepot pour achever le Chapitre IV.",
    ["T"] = "Chapitre IV",
  },
  [343] = {
    ["D"] = "Je fais un discours sur la fermeté de l\'Esprit et je voudrais citer des passages d\'un livre sur la métallurgie : \"Les Tensions du fer\". Un exemplaire de ce livre doit se trouver à la Bibliothèque royale, dans le Donjon de Stormwind. Pouvez-vous le récupérer pour moi ? Si vous le faites, que la bénédiction de la Lumière soit sur vous !$B$BLa Bibliothèque royale est très grande, mais le responsable, Milton Sheaf, connaît chaque étagère comme sa poche. Parlez-lui, il cherchera le livre pour vous.",
    ["O"] = "Allez à la Bibliothèque royale dans le Donjon de Stormwind pour parler à Milton Sheaf. Il trouvera le livre sur la métallurgie dont Frère Kristoff a besoin pour son discours.",
    ["T"] = "En parlant de fermeté",
  },
  [344] = {
    ["D"] = "« Les Tensions du fer » ? Oui, je connais ce livre. Excellent ouvrage, écrit par le mineur nain Margulf Blaggon il y a quelques décennies. Il a passé l\'essentiel de sa vie au cœur des montagnes de Khaz Modan, à creuser pour trouver du minerai. Chic camarade !$B$BMais où en étais-je ? Ah oui ! « les Tensions du fer » ! J\'ai bien peur que le livre ait été envoyé à l\'abbaye du Northshire. Le bibliothécaire de l\'abbaye, le frère Paxton, voulait faire une copie de l’ouvrage pour sa propre collection.$B$BMais c\'était il y a plusieurs mois. Je me demande pourquoi ça lui prend si longtemps...",
    ["O"] = "Parler au frère Paxton, à l\'Abbaye du Northshire.",
    ["T"] = "Frère Paxton",
  },
  [345] = {
    ["D"] = "J\'avais l\'intention de finir ma copie des « Tensions du fer », mais...$B$BEh bien vous voyez, je fabrique ma propre encre pour les livres que je copie. C\'est une recette personnelle : elle ne coule pas, ne tache pas et sèche très rapidement, voire trop rapidement ! Mon dernier lot a séché avant la fin de mon travail de copie et je n\'ai pas eu la possibilité d\'en refaire !$B$BPour en fabriquer davantage, j\'ai besoin de minerai de rethban qui vient des Carmines. Le contremaître Oslow de Lakeshire est mon fournisseur habituel.",
    ["O"] = "Aller voir le contremaître Oslow de Lakeshire et lui demander du minerai de rethban.",
    ["T"] = "Provisions d\'encre",
  },
  [346] = {
    ["D"] = "Maintenant que j\'ai fini ma copie des « Tensions du fer », je vous rends l\'original. Merci pour votre patience et si vous voyez Milton, dites-lui que je suis désolé pour ce long retard.",
    ["O"] = "Retourner voir Frère Kristoff sur la Place de la Cathédrale. Lui donner le livre « les Tensions du fer ».",
    ["T"] = "Retourner voir Frère Kristoff",
  },
  [347] = {
    ["D"] = "Le minerai de rethban a été découvert par Bart Rethban. Lorsqu’il est fondu, ce minerai est moins dur que le fer, mais il possède une essence que certains scribes utilisent comme encre. Il peut être trouvé dans les Cavernes de Rethban, plus au nord.$B$BNous en avons normalement en réserve, mais devant nous préparer aux attaques des orcs, nous n\'avons pas eu le temps d\'en collecter dernièrement. De plus, les gnolls des Carmines occupent maintenant les cavernes de Rethban et leurs manouvriers y exploitent le minerai eux-mêmes.$B$BSi vous voulez en collecter, vous devrez le faire par vos propres moyens.",
    ["O"] = "Réunir 5 charges de Minerai de rethban et les apporter à Frère Paxton à la bibliothèque du Northshire.",
    ["T"] = "Minerai de rethban",
  },
  [348] = {
    ["D"] = "Malade... Si... Très m-m-malade...$b$b... attrapé la fièvre de Strangleronce... V-v-virus mortel...$b$bSeul remède... est... est... manger le cœur de M-m-m-m-mokk le Sauvage... Sauvage...$b$b... Le Sorcier-docteur Unbagwa... Lui seul peut me s-s-sau...$b$b... Allez chercher Unbagwa dans la caverne cachée au sud du cap...$b$b... vite... mourant... mourant...",
    ["O"] = "Chercher le Sorcier-docteur Unbagwa pour qu\'il invoque Mokk le Sauvage. Apporter le Cœur de Mokk à Fizracket.",
    ["T"] = "La fièvre de Strangleronce",
  },
  [349] = {
    ["D"] = "temp text 02 - description",
    ["O"] = "temp text 02 - log",
    ["T"] = "La fièvre de Strangleronce",
  },
  [350] = {
    ["D"] = "Ce Marzon va contacter un Défias et il est lié à Lescovar...$B$BEn ayant tué VanCleef et Thredd, il va être difficile de traîner en justice Lescovar, vu que vos preuves ne valent rien sans leurs témoignages. Sans compter que Lescovar est un noble et qu\'il a de nombreux appuis. Il est ainsi au-dessus des lois.$B$BVous avez besoin d\'une autre aide. Je répugne à le faire, mais voici quelqu\'un que je connais et qui peut nous aider. Vous trouverez Trias dans le Quartier commerçant de la cité.",
    ["O"] = "Parler à Elling Trias à Stormwind.",
    ["T"] = "Le point de vue d\'un vieil ami",
  },
  [351] = {
    ["D"] = "Vous avez découvert un étrange objet en forme d\'oeuf fait de métal. En jouant avec les petit boutons présents sur le dessus, l\'oeuf s\'anime, à mesure qu\'il s\'ouvre, il se transforme en une espèce de bidule robot gnome ! Une voix sort du robot en grésillant.$B$B\"Je suis Oglethorpe Obnoticus, et mon robot guidé a disparu ! Je vous récompenserais si vous le réparez ! Apportez cette balise au robot. Excellent, j\'ai programmé les coordonnées du robot pour vous ! Il s\'est écrasé près du Gouffre béant à Tanaris !\"",
    ["O"] = "Apporter la balise de détresse au robot guidé d\'Oglethorpe.",
    ["T"] = "Trouver OOX-17/TN !",
  },
  [352] = {
    ["D"] = "Great Mammoda. Enormous, she was, with claws and teeth like swords. Now, I was caught off my guard, but I don\'t think being ready would have helped my cause much. One swipe and poof! Darkness. They say I\'m lucky to walk now...$b$bWell, not much enjoyment to be had in hearing that old story now, is there? Anyways, $n, if you\'d like to try yourself against her, the Lady of the Loch,",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [353] = {
    ["D"] = "Les Stormpike sont un clan nain respecté et connu pour ses goûts raffinés et subtils. Ce n\'est donc pas surprenant que Gringer Stormpike, un montagnard d\'Ironforge, m\'ait demandé de lui fabriquer une arme.$B$BL\'arme est terminée, mais Gringer vit loin d\'ici, près du Loch Modan. Si vous projetez de voyager vers le nord, pouvez-vous lui livrer ce paquet ?$B$BSon dernier message disait qu\'il se trouvait à la Tour de garde nord du Loch Modan.",
    ["O"] = "Livrer le Paquet au montagnard Stormpike au Loch Modan.",
    ["T"] = "Livraison pour Stormpike",
  },
  [354] = {
    ["D"] = "Les Agamand étaient la famille la plus prospère des Clairières de Tirisfal. Je travaillais dans leurs moulins... avant la Peste.$B$BQuand le Fléau est apparu, les Agamand ont fortifié leur maison et convaincu leurs employés de rester et de les aider à se défendre. Nous étions des imbéciles, mais au moins nous étions des imbéciles loyaux.$B$BLes Agamand, à cause de leur orgueil, nous ont condamnés. Et maintenant le Fléau a fait d\'eux ses favoris !$B$BServez ceux qui ont été abandonnés en vainquant les Agamand qui sont tombés du côté de la Peste. Servez-moi en m\'apportant leurs restes.",
    ["O"] = "Rapporter les restes de Gregor, de Nissa et de Thurman à Coleman Farthing, à Brill.",
    ["T"] = "Morts dans la Famille",
  },
  [355] = {
    ["D"] = "Vous m\'avez ramené les restes des Agamand et satisfait mon désir de vengeance. Mais les Moulins d\'Agamand sont une menace pour tous les Réprouvés, pas seulement moi.$B$BJ\'ai parlé au Magistrat Sevren de vos exploits contre les Agamand et il désire vous parler.",
    ["O"] = "Parler au magistrat Sevren à Brill.",
    ["T"] = "Parler à Sevren",
  },
  [356] = {
    ["D"] = "Les défenseurs de la Barricade protègent Tirisfal des Maleterres et sont en alerte permanente. Mais parfois, le Fléau arrive à s\'infiltrer.$B$BNotre succès à la Barricade dépend d\'un front uni. Nous ne pouvons nous permettre d\'être attaqués par l\'arrière ou de voir les lignes d\'approvisionnement des défenseurs coupées.$B$BRenforcez la Barricade. Patrouillez à l\'est et détruisez toutes forces du Fléau que vous rencontrerez. Portez un effort particulier sur la ferme des Balnir. C\'est devenu un havre pour les forces du Fléau qui maraudent dans le secteur.",
    ["O"] = "Tuer 8 Horreurs sanguinolentes et 8 Esprits vagabonds, puis revenez voir Linnea, à son camp.",
    ["T"] = "Patrouille d\'arrière-garde",
  },
  [357] = {
    ["D"] = "Une liche réside dans l\'île au nord du lac Etincelant. Bien qu\'elle soit une Réprouvée sauvage, elle croit que tous les autres morts-vivants sont des esclaves du Fléau et envoie ses serviteurs attaquer quiconque s\'approche d\'elle.$B$BMais parce que c\'est une Réprouvée, la Reine la veut. Et si elle est douée dans les arts de la nécromancie, son savoir serait réellement utile à notre cause.$B$BPour connaître son identité et ses capacités, j\'ai besoin d\'étudier son livre de sorts. Infiltrez-vous dans son camp, prenez-le livre et ramenez le moi.",
    ["O"] = "Rapporter le livre de sorts de la Liche et le donner à Bethor Iceshard à Undercity.",
    ["T"] = "Le nom de la Liche",
  },
  [358] = {
    ["D"] = "Dans le nord, les fosses communes, au sud-ouest de l\'antre de Garren ont été construites pour accueillir les morts de Tirisfal lorsque la Peste frappa la première fois. $B$BLes corps qui s\'y trouvent ont pu reposer en paix jusqu\'ici mais le Fléau a envoyé des gnolls Poil-putride prendre les corps afin de les utiliser pour accroître leurs armées. Cela ne doit pas être !$B$BVotre mission comporte deux parties : tuer les Poils-putrides dans les fosses communes et dans l\'Antre de Garren et récupérer sur eux l\'ichor d\'embaumement qui leur donne vie.",
    ["O"] = "Tuer des Profanateurs Poils-putrides et des Gnolls Poils-putrides. $B$BRapporter les Ichors d\'embaumement au magistrat Sevren à Brill.",
    ["T"] = "Les Profanateurs",
  },
  [359] = {
    ["D"] = "J\'ai besoin d\'un rapport récent sur notre base de la Garde noire dans l\'est.$B$BJe dois savoir s\'il y a encore des incursions des forces du Fléau au-delà de la Barricade et dans Tirisfal. Notre vigilance à la frontière des Maleterres doit être maintenue. Nous ne pouvons pas nous permettre que les forces du Fléau s\'établissent ici ! $B$BVoyagez vers le sud en suivant la route, puis vers l\'est à l\'embranchement jusqu\'au poste. Demandez la Garde noire Linnea. Elle a les informations dont j\'ai besoin.",
    ["O"] = "Parler à la Garde noire Linnea.",
    ["T"] = "Mission des Réprouvés",
  },
  [360] = {
    ["D"] = "Maintenant, rapportez au Magistrat Sevren les informations que je vous ai données. Il en a besoin le plus vite possible, soyez rapide.$B$BEt si vous voyez des troupes du Fléau sur votre chemin, abattez-les. Nous ne pouvons leur permettre de parcourir nos contrées sans opposition.",
    ["O"] = "Retourner voir le Magistrat Sevren à Brill.",
    ["T"] = "Retourner voir le Magistrat",
  },
  [361] = {
    ["D"] = "Ceci est une lettre écrite par Thurman, l\'aîné des fils de Gregor Agamand. Elle est adressée à Yvette Farthing, bien qu\'il semble qu\'elle n\'ait pas atteint sa destinatrice…",
    ["O"] = "Trouver Yvette Farthing. Lui donner la lettre de Thurman Agamand.",
    ["T"] = "Une lettre perdue",
  },
  [362] = {
    ["D"] = "Devlin Agamand était le plus jeune des deux fils de la famille Agamand. Jamais frères ne furent aussi différents. Thurman était grand et aimable tandis que son plus jeune frère était faible et acerbe.$B$BLorsque les Moulins d\'Agamand sont tombés suite à la Peste, je n\'ai pas été surpris d\'apprendre que Devlin avait rapidement succombé. Son bavardage maladif peut toujours être entendu près de la route qui mène aux Moulins d\'Agamand.$B$BJe collectionne les restes des Agamand. Je veux ce pauvre Devlin. Trouvez-le, détruisez-le et rapportez-moi ses os.",
    ["O"] = "Tuer Devlin Agamand et ramener ses restes à Coleman Farthing à Brill.",
    ["T"] = "Les moulins hantés",
  },
  [363] = {
    ["D"] = "Il était temps de se réveiller. Nous nous apprêtions à vous jeter au feu avec les autres mais il semble que vous ayez réussi.$b$bJe suis Mordo, le fossoyeur de la crypte du Glas. Et vous n\'êtes plus l\'esclave du roi-liche.$b$bAllez parler au prêtre des ombres Sarvis dans la chapelle en bas de la colline, il vous en dira plus sur ce que vous devez savoir.",
    ["O"] = "Parler au Prêtre des ombres Sarvis.",
    ["T"] = "Réveil brutal",
  },
  [364] = {
    ["D"] = "Nous, les Réprouvés, sommes en guerre contre le roi-liche et les troupes du Fléau : leurs armées de morts-vivants animés par la nécromancie, des bêtes immondes venues du nord et des spectres tourmentés.$b$bLa partie nord du village a été submergée par les Déchus et ils doivent être détruits. Anéantissez-les, n\'ayez aucune pitié, même s\'ils étaient nos frères et soeurs par le passé. Les Déchus ne sont rien d\'autre que les esclaves du roi-liche.",
    ["O"] = "Le prêtre des ombres Sarvis veut que vous tuiez 8 Zombies lunatiques et 8 Zombies décharnés.",
    ["T"] = "Les Déchus",
  },
  [365] = {
    ["D"] = "Ça par exemple ! Vous ressemblez à un épouvantail $c. Si vous espérez faire vos preuves au service de la Dame noire, il faudra apprendre les voies des Réprouvés.$b$bÀ l\'ouest, vous trouverez une ferme. Les humains infestent cette terre comme la vermine un cadavre. Et pire, la Croisade écarlate patrouille près de leur tour. Donnez-leur une leçon et volez 10 de leurs précieux Potirons.$b$bUne fois que vous les avez, apportez-les à l\'Apothicaire Johann à Brill.",
    ["O"] = "Volez 10 Potirons dans la ferme à l\'ouest, juste au nord du Glas, et apportez-les à l\'Apothicaire Johaan à Brill.",
    ["T"] = "Les champs du Chagrin",
  },
  [366] = {
    ["D"] = "Il y a longtemps, Gunther et moi, ainsi que Thule Ravenclaw qui est aujourd\'hui Nécromancien du Fléau, étions amis et étudiants au Kirin Tor - une société de mages de Dalaran.$B$BGunther et moi sommes tombés, tués par la Peste, mais je pensais qu\'il ne s\'était pas relevé ou bien qu\'il avait rejoint les rangs du roi-liche. Il est agréable de le savoir libre.$B$BIl doit être des nôtres. J\'ai mis un sort dans son livre qui, une fois qu\'il le verra, lui montrera que je ne suis pas non plus sous la domination du roi-liche. Une fois qu\'il saura cela, j\'espère qu\'il nous rejoindra.",
    ["O"] = "Ramener son livre de sorts à Gunther, dans sa retraite sur son île.",
    ["T"] = "Ramener le livre",
  },
  [367] = {
    ["D"] = "Dame Sylvanas a fait appel à la Société royale des apothicaires. La Dame noire croit que notre savoir couplé à cette nouvelle magie nous donnera la clé de la défaite d\'Arthas. Elle nous a mis au défi de créer une Peste nouvelle, une Peste plus puissante que celle qui a ravagé Azeroth. Cette nouvelle infection amènera la ruine des forces du Fléau.$b$bMes études montrent que le sang de bêtes en pourrait être la clé. Rapportez-moi 5 fioles de sang de sombredogues, que je puisse tester ma théorie.",
    ["O"] = "Collecter 5 fioles de Sang de sombredogues pour l\'Apothicaire Johaan de la ville de Brill.",
    ["T"] = "Une Peste nouvelle",
  },
  [368] = {
    ["D"] = "Pendant que vous collectiez mes échantillons, mes expériences m\'ont permis de réaliser que des ingrédients supplémentaires seraient nécessaires pour que cette nouvelle maladie se répande proprement. Empoisonner une victime sans défense est un jeu d\'enfant. Infecter un monde entier est un peu plus compliqué.$b$bJ\'aurais besoin de 5 écailles prélevées sur les murlocs Ailerons noirs des environs. Vous trouverez ces créatures le long de la côte du nord ou de l\'ouest.",
    ["O"] = "L\'Apothicaire Johaan de la ville de Brill a besoin de 5 Ecailles d\'Aileron noir prises sur les murlocs des Clairières de Tirisfal.",
    ["T"] = "Une Peste nouvelle",
  },
  [369] = {
    ["D"] = "Tandis que votre quête vous occupait, j\'ai découvert de vieux textes dans un de mes livres qui indique qu\'une ancienne peste a balayé des milliers de victimes innocentes. Il a été compris plus tard que l\'agent infectieux de l\'épidémie avait été préservé grâce à du venin des araignées Tisse-nuit vicieuses.$b$bRapportez-moi un peu de ce venin pour compléter mes expériences. Je veux voir si le principe conservateur de ce venin agit aussi sur mes décoctions. Les rumeurs disent que les araignées peuvent être trouvées à l\'est des clairières de Tirisfal.",
    ["O"] = "L\'Apothicaire Johaan de la ville de Brill désire que vous lui rameniez 4 échantillons de Venin de Tisse-nuit vicieuse.",
    ["T"] = "Une Peste nouvelle",
  },
  [370] = {
    ["D"] = "Les documents stratégiques de l\'Exécuteur Arren contiennent tous les détails dont nous avons besoin pour balayer l\'infection humaine de ces terres. Selon nos informations, la Croisade écarlate a assigné le capitaine Perrine et une brigade à la tour en ruine au sud-ouest de Brill.$B$BTuez Perrine ainsi que 3 Zélotes et 3 Missionnaires et revenez me rendre compte.",
    ["O"] = "Tuer le capitaine Perrine ainsi que 3 Zélotes écarlates et 3 Missionnaires écarlates.",
    ["T"] = "En guerre avec la Croisade écarlate",
  },
  [371] = {
    ["D"] = "Nous sommes toujours en guerre et la Croisade écarlate croît en puissance. Le rapport que l\'Exécuteur Arren m\'a envoyé indique que la Croisade lance ses raids depuis la tour en ruines au sud-est de Tirisfal, près de la ferme des Balnir. Elle est sous le commandement du capitaine Vachon.$b$bTuez Vachon et 5 Frères de la Croisade. Cela sera un coup dévastateur pour ces Croisés !",
    ["O"] = "L\'Exécuteur Zygand de Brill vous a chargé de liquider le Capitaine Vachon et 5 Frères écarlates.",
    ["T"] = "En guerre avec la Croisade écarlate",
  },
  [372] = {
    ["D"] = "La Croisade écarlate lance des raids depuis la tour en ruine au nord-est de Tirisfal, au-delà du Repos de Faol. Selon nos informations, elle est menée par le capitaine Melrache qui a la réputation d\'être un officier impitoyable.$b$bJe vous charge cette mission, dangereuse et spéciale. Abattez Melrache et ses deux gardes du corps au nom de la Dame noire.",
    ["O"] = "Assassiner le capitaine Melrach et ses deux gardes du corps pour l\'Exécuteur Zygand de la ville de Brill.",
    ["T"] = "En guerre avec la Croisade écarlate",
  },
  [373] = {
    ["D"] = "En fouillant le corps d\'Edwin VanCleef, vous trouvez parmi d\'autres objets une lettre non envoyée. Elle est adressée à Baros Alexston, architecte de la cité de Stormwind, Hôtel de ville, place de la cathédrale.$B$BElle a l\'air récemment écrite et scellée.",
    ["O"] = "Livrer la lettre pour l\'Architecte de la Cité à Baros Alexston à Stormwind.",
    ["T"] = "La lettre non envoyée",
  },
  [374] = {
    ["D"] = "L\'Exécuteur Zygand m\'a dit que vous exterminiez des membres de la Croisade écarlate. Les Clairières de Tirisfal en sont remplies.$B$BVarimathras, lieutenant de la Dame noire, a pris personnellement en charge la mission d\'exterminer toute la vermine humaine d\'Azeroth. Ici, dans les Clairières de Tirisfal, nous faisons face à un genre particulièrement ennuyeux d\'humains, la Croisade écarlate.$B$BAllez de l\'avant et lancez une offensive sur la Croisade, $C. Ramenez moi 10 Anneaux de la Croisade écarlate comme preuve de votre loyauté à Varimathras et à la Dame noire.",
    ["O"] = "Apporter 10 Anneaux de la Croisade écarlate au Garde noir Burgess à Brill.",
    ["T"] = "Preuves de la mort",
  },
  [375] = {
    ["D"] = "Il fait si froid maintenant. Le Fléau mort-vivant brûle dans mes veines comme un serpent de glace. Mon âme sera bientôt perdue. Mais aucun destin, aussi irrévocable soit-il, ne m\'empêchera de servir la Dame noire. Lorsque l\'appel est arrivé, j\'ai cousu des sacs pour les morts de la puissante armée de Sylvanas.$b$bMaintenant, mes mains tremblent à cause du froid. Si vous me ramenez 5 Peaux de chauve-souris du crépuscule et une Bobine de fil grossier, je pourrai me coudre une couverture. Aidez-moi, $N, que je puisse continuer à servir la cause.",
    ["O"] = "Rapporter 5 Peaux de chauve-souris du crépuscule et une Bobine de fil grossier à Gretchen Dedmar à Brill.",
    ["T"] = "Le frisson de la mort",
  },
  [376] = {
    ["D"] = "Je soigne nos guerriers blessés, j\'adapte leurs armures et vêtements, et j\'assiste le prêtre des ombres Sarvis.$b$bApparemment, vous allez entrer à son service, vous aussi, pour chasser les Âmes Perdues. Si vous voulez rester en un morceau, ce dont je ne doute pas, je peux peut-être vous aider. Je manque de pattes et d’ailes ; si vous m’en apportez, je vous fournirai une armure. Vous trouverez de nombreux loups et chauves-souris au sud.",
    ["O"] = "La Novice Elreth a besoin de 6 Pattes de charognard et de 6 Ailes de chauve-souris du crépuscule.",
    ["T"] = "Les Maudits",
  },
  [377] = {
    ["D"] = "Non contente de négliger les habitants du bois de la Pénombre, la Maison des Nobles a décidé d\'humilier le Conseil de Darkshire en décidant d\'emprisonner Dextren Ward à Stormwind plutôt que de le décapiter ainsi que le Seigneur Ebonlocke l\'avait décidé.$b$bWard a été pris en train de vendre des corps du cimetière à Morbent Fel. Ce crime est passible de la peine capitale. Mais Stormwind exige que Ward soit leur prisonnier.$b$bAssassinez Ward dans la Prison de Stormwind et ramenez-moi sa main, je vous récompenserai pour cela.",
    ["O"] = "Le conseiller Millstipe de Darkshire désire que vous lui rameniez la main de Dextren Ward.",
    ["T"] = "Crime et Châtiments",
  },
  [378] = {
    ["D"] = "Vous avez prouvé votre loyauté au roi Magni, $c. Et votre haine de la vermine Sombrefer est aussi forte que la mienne.$b$bVoici une tâche que je pourrais effectuer mais je suis obligé par une dette d\'honneur de rester à Longbraid. Roggo a rassemblé un faisceau d\'informations qui prouvent que Kam Deepfury était derrière l\'attaque du viaduc de Thandol. Ce sont les plans de Deepfury qui ont amené la ruine de Longbraid.$b$bDeepfury est détenu pour des raisons politiques dans la Prison de Stormwind. Je veux qu\'il meure, $N. Pour Longbraid ! Ramenez-moi sa tête !",
    ["O"] = "Rapportez la tête de Kam Deepfury à Motley Garmason à Dun Modr.",
    ["T"] = "Fureur dans les Profondeurs",
  },
  [379] = {
    ["D"] = "Vous voulez une source d\'énergie. Je peux vous aider. Savez-vous ce que je veux moi ? Je veux quelque chose à boire ! Rapportez-moi cinq outres de Bat-le-désert, et je vous donnerais le 4711-FTZ.$B$BMaintenant... Devinez où vous pouvez trouver ces outres ? Pourquoi ne pas aller voir un de ces nomades Bat-le-désert, à l\'est d\'ici, pour lui en demander une ? Faites-moi savoir si vous y parvenez.",
    ["O"] = "Rapporter 5 Outres de Bat-le-désert à l\'ingénieur en chef Bilgewhizzle à Gadgetzan.",
    ["T"] = "Etancher cette soif",
  },
  [380] = {
    ["D"] = "Un de nos plus gros problèmes est d\'obtenir les ressources nécessaires à notre survie. L\'or était déjà rare lorsque l\'Alliance était au sommet de sa puissance.$b$bIl y a une mine d\'or au nord-ouest qui a été envahie par les araignées. Nous avons besoin de cet or, mais nous ne pouvons l\'exploiter à cause de ces créatures. Je n\'ai pas beaucoup de personnels à affecter à cette tâche, alors je vais devoir le faire par étapes.$B$BAllez-y et voyez ce que vous pouvez faire, $n.",
    ["O"] = "L’Exécuteur Arren veut que vous tuiez 10 Jeunes Tisse-nuit et 8 Araignées Tisse-nuit.",
    ["T"] = "Les grottes des Tisse-nuit",
  },
  [381] = {
    ["D"] = "Il vous plaira d’entendre que nous progressons dans la mine, et cela grâce à vous. Nous pouvons maintenant nous atteler à d’autres tâches.$b$bMes éclaireurs m’ont dit qu’un détachement de la Croisade écarlate avait établi un campement au sud-est. Cette Croisade écarlate est une organisation abjecte qui nous chasse et n’aura de cesse d\'exterminer jusqu\'au dernier mort-vivant, qu\'il soit du Fléau ou non. Nous devons frapper les premiers !$b$bAttention ce sont des adversaires zélés et dangereux.",
    ["O"] = "Apporter à l’Exécuteur Arren 12 Bracelets écarlates, que vous trouverez sur des Convertis écarlates et des Initiés écarlates.",
    ["T"] = "La Croisade écarlate",
  },
  [382] = {
    ["D"] = "Les rapports de mes supérieurs à Brill indiquent qu\'un agent de la Croisade a été détaché du Monastère écarlate et envoyé ici, au Glas. Toujours selon les rapports, ce messager possède d\'importantes informations sur lui. Je n\'ai pas plus de détails, mais qui que soit ce messager, nous devons l\'avoir !$b$bVous êtes $gle meilleur:la meilleure; que je peux trouver, $n. Trouvez ce messager et ramenez-moi toutes les informations que vous trouverez sur lui.",
    ["O"] = "Tuer Meven Korgal, le messager, au camp de la Croisade, puis ramener toutes les informations trouvées à l\'Exécuteur Arren.",
    ["T"] = "Le messager rouge",
  },
  [383] = {
    ["D"] = "Ceci doit être rapporté à mon supérieur, l\'Exécuteur Zygand à Brill. Avec ces informations, nous serons à même de porter un coup décisif à la Croisade écarlate. Vos services ont été utiles ici au Glas, mais j\'ai besoin de quelqu\'un pour porter ceci à Zygand et je pense qu\'il sera à même de vous trouver une mission plus en relation avec vos capacités.$b$bPrenez la route au nord du Glas. Une fois dans Tirisfal, vous arriverez à un croisement. Prenez la route vers l\'est et continuez. Vous passerez devant le manoir du Foyer froid sur votre route.",
    ["O"] = "Livrer les Documents de la Croisade écarlate à l\'Exécuteur Zygand à Brill.",
    ["T"] = "Informations décisives",
  },
  [384] = {
    ["D"] = "Il n\'y a rien que mes clients apprécient plus qu\'une bonne côte de sanglier à la bière ! Le seul problème est que le trappeur qui nous ravitaille s\'est engagé dans l\'armée du roi pour aller à la guerre sur le front de l\'Alliance.$b$bPeut-être pourriez-vous m\'aider ? Si vous pouvez me procurer six côtes de sangliers des rochers et une bière au malt Rhapsody de la taverne, je vous donne ma recette familiale des côtes de sanglier à la bière accompagnée bien évidemment d\'un échantillon gratuit ! Le secret est dans la bière, mais je ne vous en dis pas plus !",
    ["O"] = "Ragnar Thunderbrew à Kharanos veut 6 Côtes de sangliers des rochers et une Chope de bière au malt Rhapsody.",
    ["T"] = "Côtes de sanglier à la bière",
  },
  [385] = {
    ["D"] = "Beaucoup de chasseurs viennent au Loch Modan pour chasser nos fameux crocilisques. Il y a toujours des marchands qui payent pour avoir ces peaux servant à la confection de vêtements ou d’armures ; sans parler de ceux qui apprécient leur viande.$b$bLes crocilisques étant féroces et retranchés sur leur île, au milieu du Loch, nous manquons de peaux. Mais ne vous laissez pas effrayer : esquiver les mâchoires de ces bêtes est une expérience enrichissante.$b$bDu moins cette fois...",
    ["O"] = "Trouver 5 morceaux de Viande de cocilisque et 6 Peaux de crocilisque pour Marek Ironheart au pavillon des pérégrins.",
    ["T"] = "Chasse aux crocilisques",
  },
  [386] = {
    ["D"] = "Targorr le Terrifiant a servi sous les ordres de Gath\'llzogg en tant qu\'Exécuteur suprême. Sa méthode était impitoyable, même selon les standards orcs. Les hommes qui combattaient vaillamment étaient torturés selon ses désirs. Maintenant il est enfermé dans la Prison de Stormwind, condamné à mort. Mais quelque chose ne va pas. Un de ces nobles passionnés de procédures bureaucratiques a suspendu l\'exécution. Je suis sûr qu\'il y a des magouilles là-dessous.$b$bMettez fin à la carrière de Targorr le Terrifiant, $N. Allez jusqu\'à la Prison et décapitez-le avant que toutes ces arguties finissent par le libérer.",
    ["O"] = "Ramener la tête de Targorr le Terrifiant au Garde Berton à Lakeshire.",
    ["T"] = "Ce qui se passait ailleurs…",
  },
  [387] = {
    ["D"] = "La révolte doit être étouffée ! J\'ai besoin de votre aide. Si l\'on apprend dehors que cette prison est en rébellion, je perds mon poste ! Mais le plus important en ce moment est de forcer les prisonniers à se soumettre, pour la sécurité de Stormwind.$b$bLa punition en cas d\'émeute est la mort. Tuez quelques-uns de ces déviants ! Cela devrait ramener les choses sous contrôle.",
    ["O"] = "Tuer 10 Prisonniers défias, 8 Détenus défias et 8 Insurgés défias dans la Prison pour le Gardien Thelwater de Stormwind.",
    ["T"] = "Écraser la rébellion",
  },
  [388] = {
    ["D"] = "L\'amertume coule dans mes veines comme un poison glacé. Mon petit-fils, Mac, était un si bon garçon. À peine 14 ans. Lorsque l\'armée est partie en direction du front, Max a pris les armes pour défendre la ville. Il était garde à la Prison lorsque les Défias ont été emprisonnés. Mac a réprimandé certains prisonniers qui déchiraient leurs couvertures pour en faire des foulards, symbole de leur gang. Pour cela, ils l\'ont poignardé dans le dos.$b$bJe veux qu\'il soit vengé. Rapportez-moi 10 de leurs précieux foulards de laine. Ils sont de la couleur du sang.",
    ["O"] = "Rapporter 10 foulards en laine rouge à Nikova Raskol de Stormwind.",
    ["T"] = "La couleur du Sang",
  },
  [389] = {
    ["D"] = "VanCleef et moi étions membres de la Guilde des Maçons. Notre grand projet était de reconstruire Stormwind après la guerre.$b$bUne fois notre tâche finie, nous avons été escroqués. Les nobles ont refusé de payer. Plusieurs Contremaîtres se virent proposer des emplois dans le gouvernement, mais VanCleef refusa par loyauté envers la guilde. Il mena une émeute et quitta la cité, jurant de se venger.$b$bSon lieutenant, Bazil Thredd, doit en savoir plus sur ses plans. Il est détenu dans la prison de Stormwind.",
    ["O"] = "Parler au gardien Thelwater à la prison.",
    ["T"] = "Bazil Thredd",
  },
  [390] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [391] = {
    ["D"] = "Qu\'une chose soit bien claire, $n : je n\'ai pas confiance en vous. Mais la situation étant ce qu\'elle est, ça ne change pas grand-chose. $B$BAlors voilà comment on va faire : descendez là-bas et parlez à Thredd. Et si vous êtes vraiment de notre côté, tuez-le et ramenez-moi sa tête. Et s\'il se trouve que vous êtes de son côté, et que vous ne revenez pas... Je vous retrouve et vous périrez comme un rat. Compris ?",
    ["O"] = "Tuer Bazil Thredd et ramener sa tête au Gardien Thelwater à la Prison.",
    ["T"] = "Les Emeutes de la Prison",
  },
  [392] = {
    ["D"] = "Pendant les premières années de son emprisonnement, Thredd n\'a reçu aucun visiteur. Je pensais qu\'il n\'était plus utile à la Confrérie Défias, alors ils l\'ont abandonné.$b$bQuoi qu\'il en soit, il y a quelques mois, tout a changé ; il a commencé à recevoir des visites régulières... Une à deux fois par semaine. C\'était un type étrange, calme. J\'avais des soupçons, mais tous ses papiers étaient en règle.$b$bLe nom sur les papiers était Maelik, voici sa description. Je ne pense pas qu\'il va revenir voir Thredd.",
    ["O"] = "Apporter la description du visiteur de Thredd à Baros Alexston à Stormwind.",
    ["T"] = "Un curieux visiteur",
  },
  [393] = {
    ["D"] = "Je n\'en ai aucune idée, $N. Il y a quelqu\'un d\'autre qui pourrait vous aider, et il se trouve qu\'il me doit une faveur... bon, pas la peine de parler de ça. Vous trouverez maître Mathias Shaw, du SI:7, dans la caserne de la vieille ville. Shaw est impliqué dans la plupart des opérations louches de la cité.$B$BSi quelqu\'un a des renseignements, ce sera lui.",
    ["O"] = "Apporter la Description scellée du visiteur de Thredd au Maître Mathias Shaw à Stormwind.",
    ["T"] = "Les Ombres du Passé",
  },
  [394] = {
    ["D"] = "Retrouvez Shaw dans la vieille ville. Faites-lui savoir que la menace Lescovar a été résolue grâce à vous. Je suis sûr qu\'il sera fier d\'avoir un nouveau héros parmi les citoyens de Stormwind.$B$BJe pense qu\'il vous enverra rencontrer le Prince ou un autre noble. Ne vous laissez pas faire, $N. Les nobles peuvent penser qu\'ils règnent sur la ville autant qu\'ils le veulent, il n\'en reste pas moins que certaines choses échappent à leur contrôle.",
    ["O"] = "Parler à Mathias Shaw, à Stormwind.",
    ["T"] = "La tête de la bête",
  },
  [395] = {
    ["D"] = "Ah, j\'avais presque oublié. Alexston est passé pendant que vous n\'étiez pas là, il désirait vous parler. Il est clair qu\'il désirait rendre compte de l\'activité de la Confrérie défias au roi.$b$bUn petit conseil, $N. Si quelqu\'un le demandait, je donnerais tous les détails, mais sans préciser que c\'est votre main qui a tué Lescovar. Croyez-moi. Je suis... habitué... à ce genre de pratiques.$B$BEn ce qui concerne Trias, he bien, ne parlez de lui à personne... jamais... Si vous le pouvez.",
    ["O"] = "Parler à Baros Alexston à Stormwind.",
    ["T"] = "La fin de la Confrérie",
  },
  [396] = {
    ["D"] = "J\'ai préparé un rapport pour le roi. Je crois que vous devriez le lui remettre, en même temps qu\'une explication de vos découvertes. N\'ayez crainte, il vous récompensera sûrement pour vos actions.$B$BVotre audience est prévue pour bientôt, vous devriez vous hâter, il y a du chemin jusqu\'au donjon de Stormwind.",
    ["O"] = "Apporter le rapport de Baros Alexston sur la Confrérie Défias au Roi Varian Wrynn au Donjon de Stormwind.",
    ["T"] = "Une audience avec le roi",
  },
  [397] = {
    ["D"] = "J\'ai entendu dire que le seigneur Wishock avait apprécié ce breuvage.$b$bVous nous avez bien servi, $N. Les démonistes de Stormwind seront capables de s\'exercer sans être dérangés et ceci grâce à vous. Pour vos efforts, nous vous avons préparé un cadeau. Allez voir mon serviteur, Zggi et demandez-lui votre récompense.",
    ["O"] = "Aller demander votre récompense en parlant à Zggi, le serviteur de Zardeth de Black Claw.",
    ["T"] = "Vous nous avez bien servi",
  },
  [398] = {
    ["D"] = "On recherche : Œil-de-ver !$b$bBête affreuse, même d\'après les critères des gnolls, Œil-de-ver a été condamné à mort pour ses crimes envers les Réprouvés. Ce criminel infâme volait les corps pour que ceux-ci soient utilisés par l\'armée du roi-liche. La dernière apparition d\'Œil-de-ver a eu lieu près de l\'antre de Garren, au nord de Brill, lorsqu\'il pillait les terres.$b$bLa ville de Brill offre une récompense à la personne capable d\'exécuter la sentence. Les griffes de ce brigand doivent être rapportées à l\'Exécuteur Zygand.$b$bLa pitié est pour les faibles.",
    ["O"] = "Tuer Œil-de-ver et rapporter ses griffes à l\'Exécuteur Zygand pour toucher la récompense.",
    ["T"] = "On recherche : Œil-de-ver",
  },
  [399] = {
    ["D"] = "J\'ai l\'impression qu\'une éternité s\'est écoulée depuis que j\'étais un jeune garçon travaillant à la ferme, dans la marche de l\'Ouest. On dit que l\'on ne peut jamais revenir en arrière et c\'est vrai. C\'est d\'autant plus vrai dans mon cas que la maison de ma famille a été brûlée et détruite par des voleurs.$b$bJ\'ai parlé avec mon père de ce qu\'il était advenu de quelques-uns de mes biens, notamment de ma première boussole. Il n\'a pas pu les sauver. Il m\'a dit qu\'ils devaient être cachés dans la ferme.$b$bLa ferme des Alexston se trouve à l\'ouest de la Colline des sentinelles. Peut-être pourriez-vous y aller et rapporter mes biens ?",
    ["O"] = "Aller à la maison de Baros Alexston dans la marche de l\'Ouest pour chercher sa boussole et lui rapporter à la place de la Cathédrale de Stormwind.",
    ["T"] = "Débuts modestes",
  },
  [400] = {
    ["D"] = "Beldin Steelgrill tient un magasin de mécanique. C’est notre meilleur technicien d\'engins de siège ! Mais il n\'est pas soigneux avec ses outils. Je vous jure qu\'il casse ses clés plates aussi vite que nous les lui fournissons !$B$BNous venons juste de préparer sa dernière commande d\'outils. Si vous la lui livrez, je suis sûr qu’il vous récompensera.$B$BSon magasin, le dépôt de Steelgrill, est juste au nord-est de Kharanos. Et c\'est le repaire de nombreux pilotes d\'engins de siège vétérans. Quand vous y serez, gardez les oreilles ouvertes, il y aura peut-être des occasions à saisir.",
    ["O"] = "Rapporter les outils de SteelGrill à Beldin Steelgrill.",
    ["T"] = "Des outils pour Steelgrill",
  },
  [401] = {
    ["D"] = "J\'ai presque fini de traduire cette note. Si vous revenez dans un moment, j\'aurai fini.",
    ["O"] = "Attendre un moment puis parler de nouveau avec Sirra Von\'Indi.",
    ["T"] = "Attendre la fin des travaux de Sirra",
  },
  [403] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La garde du tonneau de Thunderbrew",
  },
  [404] = {
    ["D"] = "Le Fléau s\'est infiltré dans les clairières de Tirisfal et a infesté l\'ouest de Brill, près du vieux pont.$B$BAllez là-bas et faites reculer les morts pourrissants et les cadavres décomposés que vous trouverez. Eparpillez leurs ossements et rapportez leurs griffes putrides et les gardes noirs vous récompenseront.",
    ["O"] = "Rapporter 7 Griffes putrides au Garde noir Dillinger à Brill.",
    ["T"] = "Une tâche putride",
  },
  [405] = {
    ["D"] = "Bethor Iceshard, un puissant mage d\'Undercity, dans le sud, m\'a demandé de lui envoyer un agent efficace contre le Fléau. Vous, $N, serez cet agent.$B$BPrésentez ces ordres à Bethor. Il vous instruira du détail de votre mission. Je ne connais pas les détails de celle-ci mais je crois qu\'il s\'agit de recruter une liche. Et cela sera dangereux.$B$BAlors soyez prêt, $c. Vous ne devez pas échouer.$B$BVous trouverez Bethor dans le quartier des mages d\'Undercity.",
    ["O"] = "Présenter les ordres de Sevren à Bethor Iceshard à Undercity.",
    ["T"] = "La Liche prodigue",
  },
  [406] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [407] = {
    ["D"] = "D\'inoffensives citrouilles, n\'est-ce pas ? A ce qu\'il semble. Si nous voulons stopper l\'avance d\'Arthas du nord et l\'infestation humaine venant du sud, nous devons commencer à comprendre l\'étendue de notre potentiel de morts-vivants.$b$bAvec un peu d\'ingéniosité une simple citrouille va devenir un agent de la Dame noire. Cette citrouille, traitée avec ma dernière formule, sera une menace sérieuse.$b$bUn autre Zélote écarlate a été capturé et est gardé prisonnier dans le cellier de la Taverne des pendus. Prenez cette citrouille et donnez-la-lui.",
    ["O"] = "Donner la citrouille enchantée au Zélote écarlate capturé qui est gardé dans le cellier de la taverne des Pendus.",
    ["T"] = "Les champs du Chagrin",
  },
  [408] = {
    ["D"] = "Les moulins d\'Agamand sont devenus un avant-poste du Fléau. Leur point fort est la crypte de la famille Agamand où leur chef local, un squelette nommé le capitaine Dargol a son quartier général. A l\'aide de la nécromancie, il a relevé les ancêtres des Agamand et veut les utiliser contre nous.$B$BCela ne doit pas être et c\'est vous qui allez stopper ceci. Allez jusqu\'à la crypte de la famille Agamand et battez les ancêtres Agamand.$B$BEt ramenez-moi le crâne du capitaine Dargol.",
    ["O"] = "Tuer 8 Spectres ancestraux et 8 Ancêtres pourrissants.$B$BTuer le capitaine Dargol et ramener son crâne au magistrat Sevren à Brill.",
    ["T"] = "La Crypte Familiale",
  },
  [409] = {
    ["D"] = "Vous dites que vous n\'êtes pas $glié:liée; à la volonté du roi-liche et l\'enchantement que Bethor a mis dans mon livre corrobore ceci. Mais avant que je ne vous croie, je dois avoir des preuves que vous êtes $gun ennemi:une ennemie; du Fléau.$B$BIl y a un vieil et sanglant autel sur la côte sud de cette île. Il est utilisé par un puissant agent du Fléau, Lillith Nefara.$B$BPrenez une Bougie de signal dans cette boîte là-bas. Allumez-là sur l\'autel et Lillith se réveillera. Détruisez-la et je saurai que vous êtes l\'ennemi du Fléau.",
    ["O"] = "Obtenir une Bougie de signal.$B$BInvoquer Lillith Nefara et la tuer.$B$BRetourner voir Gunther sur son île.",
    ["T"] = "Prouver l\'allégeance",
  },
  [410] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "L\'Ombre dormante",
  },
  [411] = {
    ["D"] = "Portez cette Gemme du néant à mon vieux compagnon Bethor. Cela nous permettra de communiquer… et j\'ai tant de choses à lui dire.",
    ["O"] = "Rapporter la Gemme du Néant à Bethor Iceshard à Undercity.",
    ["T"] = "Le Retour de la Liche Prodigue",
  },
  [412] = {
    ["D"] = "Après des analyses approfondies de la situation, il semble que non seulement nous avons échoué à éradiquer les troggs mais que nous avons changé la majorité de la race gnome en horribles gnomes lépreux.$b$bOzzie et moi voulons renverser l\'effet de cette abominable lèpre grâce à notre dernière invention : le Recombobulateur. Cette machine est pratiquement complète mais nous avons un grand besoin de Rouages de Rééquilibrage et d\'Engrenages gyromécaniques. Demandez-en pour nous aux gnomes lépreux devant Gnomeregan.",
    ["O"] = "Porter 8 Rouages de Rééquilibrage et 8 Engrenages gyromécaniques à Razzle Sprysprocket à Kharanos.",
    ["T"] = "Opération Recombobulation",
  },
  [413] = {
    ["D"] = "J\'aimerais que mon frère Wellart essaye ma nouvelle bière Shimmer. C\'est un montagnard dont la garnison est la tour de garde sud, une des deux tours au bord du Loch Modan.$B$BIl n\'est peut être pas un brasseur comme le reste des Barleybrew mais il aime boire une bonne bière et je sais qu\'il appréciera celle-ci.$b$BPouvez-vous lui porter ce Tonneau de Bière Shimmer pour moi ?",
    ["O"] = "Amener le Tonneau de Bière Shimmer au Montagnard Barleybrew.",
    ["T"] = "La bière Shimmer",
  },
  [414] = {
    ["D"] = "J\'ai encore bu un peu de cette bière Shimmer faite par mon frère, c\'est vraiment une sacrée bière ! J\'aimerais qu\'un ami montagnard la goûte. Son nom est Kadrell et il patrouille en général sur la route qui passe par Thelsamar au Loch Modan.$B$BPour aller à Thelsamar, allez au sud-ouest à travers le tunnel puis allez au nord-est quand le chemin se sépare. Suivez cette route jusqu\'à Thelsamar.$B$BDonnez au montagnard Kadrell ce Tonneau de Shimmer Stout et voyez ce qu\'il en pense !",
    ["O"] = "Portez le Tonneau de Bière Shimmer au montagnard Kadrell.",
    ["T"] = "Bière à Kadrell",
  },
  [415] = {
    ["D"] = "Rejold Barleybrew du village de Brassetout vous demande.$B$BIl veut vous parler d\'une nouvelle boisson sur laquelle il travaille… Il parle d\'un coup de main que vous pourriez lui donner. Vous pourriez faire demi-tour et voir ce qu\'il a à dire.$B$BIl pourrait y avoir quelques mousses gratuites pour vous...",
    ["O"] = "Parler à Rejold Barleybrew.",
    ["T"] = "La nouvelle cuvée de Rejold",
  },
  [416] = {
    ["D"] = "Nous avons vraiment un problème de vermine à l\'ouest. Les rats des tunnels kobolds sont venus dans les collines, ont creusé leurs terriers et contaminé notre terre avec leur pestilence !$B$BNous voulons les éliminer. Chassez-les et ramenez-moi leurs oreilles pour la récompense.$B$BVous trouverez les tunnels des kobolds et leurs terriers à l\'ouest de Thelsamar, disséminés à flanc de montagne.",
    ["O"] = "Apporter 12 Oreilles de Rat des tunnels au Montagnard Kadrell à Thelsamar.",
    ["T"] = "Chasse au rat",
  },
  [417] = {
    ["D"] = "Hildelve écrit dans son journal qu\'il a été attaqué par Griffe galeuse, un \"gigantesque ours Griffeglace\". Il a repoussé la bête mais pas avant d\'avoir été gravement blessé.$B$BD\'après l\'état du corps de Hildevel, Griffe galeuse a dû revenir et le tuer.$B$BNéanmoins, Hildevel a écrit dans son journal sa dernière volonté :$B$BVengez-moi ! Tuez Griffe galeuse !",
    ["O"] = "Tuer Griffe galeuse.$B$BRapporter la griffe à laquelle il doit son nom et le Journal de Hildelve au pilote Hammerfoot.",
    ["T"] = "La revanche du pilote",
  },
  [418] = {
    ["D"] = "On n\'a jamais manqué d\'estomacs vides à Thelsamar, avec les gamins qui courent partout et les ouvriers qui reviennent de leur journée de travail après un dur labeur. Nos boudins sont réputés, vous les avez déjà essayés ?$b$bNon ? Eh bien, par ici, il faut travailler pour mériter sa pitance et ce n\'est pas parce que vous êtes bien $ghabillé:habillée; que l\'on fera une exception pour vous, $c.$b$bJe vais avoir besoin de viande d\'ours, d\'intestins de sanglier pour la cuisson et d\'ichor d\'araignée pour épicer le tout. Trouvez-moi cela et laissez la cuisine à Vidra !",
    ["O"] = "Apporter 3 morceaux de Viande d’ours, 3 Tripes de sanglier et 3 Ichors d\'araignée à Vidra Hearthstove à Thelsamar.",
    ["T"] = "Les boudins de Thelsamar",
  },
  [419] = {
    ["D"] = "Mon ami et camarade Mori Hildelve, qui est Sapeur, a disparu dans les collines. Nous étions en train de chercher un minerai rare qui est nécessaire pour une poudre explosive de grande qualité. Durant nos recherches, il a essayé de monter une pente avec son engin lorsque le moteur a explosé !$B$BConvaincu que le minerai était dans ces montagnes, Hildelve m\'a confié la garde des engins tandis qu\'il poursuivait la prospection à pied.$B$BCela fait maintenant des jours de cela et j\'ai entendu les grognements de bêtes dans la montagne la nuit. Mori est dur comme l\'acier mais je suis inquiet.$B$BS\'il vous plaît, $N. Trouvez-le.",
    ["O"] = "Trouver le pilote Hildeve.",
    ["T"] = "Le pilote perdu",
  },
  [420] = {
    ["D"] = "Les troggs qui infestent le tunnel sont extrêmement hostiles et n\'hésitent pas à attaquer tout intrus. Vous avez l\'air suffisamment solide, vous devriez donc vous en tirer.$b$bLa route à suivre ? Si vous voulez parler à Senir, vous le trouverez à Kharanos.$b$bPassez par le tunnel et une fois dehors à l\'autre extrémité, suivez la route, elle vous conduira à Kharanos.",
    ["O"] = "Apporter le rapport de Grelin à Senir Whitebeard à Kharanos.",
    ["T"] = "Les observations de Senir",
  },
  [421] = {
    ["D"] = "Dame Sylvanas a chargé Varimathras de la conquête des terres humaines et naines se trouvant vers le sud.$b$bMais Arugal, ci-devant charlatan de Dalaran et aujourd\'hui bête maudite du donjon d\'Ombrecroc, a tristement utilisé sa magie. Il a créé le chaos dans la forêt des Pins argentés qui est d\'une grande importance stratégique.$b$bJ\'ai besoin de quelqu\'un de doué dans les arts du combat pour rétablir l\'ordre. Prouvez-moi votre valeur en tuant 5 Poils-blancs Ragelune. Ces bêtes dégénérées se trouvent généralement près de la route, en bas de la colline.",
    ["O"] = "Tuer 5 Poils-blancs Ragelune pour Dalar Dawnweaver au Sépulcre.",
    ["T"] = "Prouver votre valeur",
  },
  [422] = {
    ["D"] = "Comment Arugal a-t-il pu entrer au Kirin Tor ? C\'est au-delà de ma compréhension. Son savoir semblait aussi léger qu\'une bulle de savon.$b$bCe qui importe maintenant, c\'est de savoir quelle magie Arugal a utilisée afin que je puisse la retourner contre lui et rendre la forêt des Pins argentés saine pour la Dame noire.$b$bArugal a longtemps habité dans la ferme juste au nord du pont. Retrouvez-moi le sort appelé le Remède d\'Arugal dans ses livres.",
    ["O"] = "Retrouver le Remède d\'Arugal pour Dalar Dawnweaver au Sépulcre.",
    ["T"] = "La folie d\'Arugal",
  },
  [423] = {
    ["D"] = "Après examen des travaux d\'Arugal, mes pires hypothèses se sont confirmées. Le vieux fou n\'était même pas bon à vider des pots de chambre à Dalaran. Alors, représenter le Kirin Tor dans ses heures les plus sombres...! Les fous !$b$bArugal a utilisé des reliques magiques pour renforcer ses faibles pouvoirs. J\'ai besoin d\'étudier ces objets avant toute chose. Allez au nord et tuez des Gloutons Ragelune et des Ténébreux Ragelune jusqu\'à ce que vous ayez récolté suffisamment de leurs bracelets enchantés. J\'en ai besoin pour mes recherches. Vous trouverez ces créatures infâmes au nord et à l\'est.",
    ["O"] = "Rapportez 6 Bracelets de Gloutons et 6 Bracelets de Ténébreux à Dalar Dawnweaver au Sépulcre.",
    ["T"] = "La folie d\'Arugal",
  },
  [424] = {
    ["D"] = "Découvrir les sombres secrets des enchantements d\'Arugal va me prendre plus de temps que je ne le pensais.$b$bPendant ce temps, j\'aimerais que vous vous occupiez d\'un problème que nos Traqueurs noirs ont découvert. Il semble qu\'Arugal ait laissé sa magie s\'étendre jusqu\'à la mine du gouffre d\'Elem dans les collines du sud-est.$b$bCette mine serait d\'une aide précieuse pour Varimathras.$b$bJe veux que vous décapitiez le contremaître de cette mine, Grimson le Pâle. Avec sa mort, la mine sera nôtre.",
    ["O"] = "Tuer Grimson le Pâle et ramener sa tête à Dalar Dawnweaver au Sépulcre.",
    ["T"] = "La folie d\'Arugal",
  },
  [425] = {
    ["D"] = "Mon frère Quinn et moi avions pour mission de reconnaître la forêt des Pins argentés et de rendre compte au grand exécuteur Hadrec. Mais nous ne sommes pas allés loin. En passant par cette ferme, nous avons été attaqués par la goule nommée Ivar l\'Infect.$B$BNous avons réussi à nous échapper mais Quinn a été grièvement blessé par Ivar. Il récupère en ce moment grâce à la potion que vous lui avez donnée mais Ivar pourrait revenir avant qu\'il soit guéri.$B$BTuez Ivar de manière à donner le temps à Quinn de récupérer. Ivar se trouve généralement dans la grange, attendant que je baisse ma garde.",
    ["O"] = "Tuer Ivar l\'Infect et ramener sa tête à Rane Yorick au lopin d\'Ivar.",
    ["T"] = "Ivar l\'Infect",
  },
  [426] = {
    ["D"] = "Les forces du Fléau essayent de construire une base aux moulins d\'Agamand. Si elles réussissent, elles pourront lancer d\'autres attaques sur Tirisfal.$B$BLes ordres sont clairs. Les forces du Fléau positionnées là-bas doivent être détruites.$B$BAllez aux moulins. Collectez des côtes ébréchées sur les soldats et les squelettes Cliquethorax, ainsi que des crânes noircis d\'Ossomanciens Sombroeil.$B$BPour rejoindre les moulins, suivez la route ouest. Après avoir traversé le pont, prenez la route nord au premier croisement. Lorsque vous verrez les moulins, la bataille commencera.",
    ["O"] = "Rassemblez 5 Côtes ébréchées et 3 Crânes noircis et rapportez-les au garde noir Dillinger à Brill.",
    ["T"] = "La chute des moulins",
  },
  [427] = {
    ["D"] = "Les documents que l\'Exécuteur Arren nous a apportés sont juste ce qui nous manquait pour notre bataille contre la Croisade écarlate. Nous savons maintenant où ils se trouvent dans les Clairières de Tirisfal.$b$bMais la Garde noire a d\'autres soucis. L\'armée du roi-liche s\'accroît chaque nuit. Nous avons besoin de quelqu\'un capable \"d\'initiatives\" personnelles tel que vous pour s\'occuper de la Croisade écarlate.$b$bProuvez-moi que vous êtes digne de servir la Dame noire ! Allez vers l\'ouest jusqu\'à la tour après la ferme des Solliden et tuez 10 guerriers écarlates.",
    ["O"] = "Tuer 10 Guerriers écarlates pour l\'Exécuteur Zygand.",
    ["T"] = "En guerre avec la Croisade écarlate",
  },
  [428] = {
    ["D"] = "Il y a quelques semaines, nos Traqueurs noirs, Rane et Quinn Yorick, ont été envoyés en mission de reconnaissance au nord des Pins argentés. Nul n\'a plus entendu parler d\'eux.$B$BIls ont peut-être été victimes du Fléau, mais si cela est vrai, j\'aimerais en avoir la confirmation. Trouvez ces Traqueurs.$B$BLeur premier objectif était d\'explorer les fermes au nord de la forêt. Vous devriez commencer par là.",
    ["O"] = "Trouver les Traqueurs noirs Quinn et Rane Yorick.",
    ["T"] = "Traqueurs noirs égarés",
  },
  [429] = {
    ["D"] = "Mon frère Quinn a été gravement blessé par la goule Ivar l\'Infect et je doute qu\'il puisse guérir sans l\'aide de la magie. Bien que je ne sois pas spécialiste de ces choses, je sais que nos apothicaires savent concocter des remèdes à partir de cœurs décolorés de worgs.$B$BRecueillez quelques-uns de ces cœurs et portez-les à l\'apothicaire Renferrel au Sépulcre, au sud. Revenez ensuite avec la potion.$B$BVous trouverez des meutes de worgs entre ici et le verger de Malden, plus à l\'est.",
    ["O"] = "Recueillir six Cœurs de worg et les apporter à l\'Apothicaire Renferrel au Sépulcre.",
    ["T"] = "Cœurs sauvages",
  },
  [430] = {
    ["D"] = "Voici la potion qui guérira Quinn Yorick.$B$BMaintenant, si vous voulez bien m\'excuser, j\'ai un important travail à terminer...",
    ["O"] = "Apporter le remède à Quinn Yorick au lopin d\'Ivar, au nord du Sépulcre.",
    ["T"] = "Retour vers Quinn",
  },
  [431] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Bougies de signal",
  },
  [432] = {
    ["D"] = "Ces satanés troggs sont en train de démolir mon lieu de travail, que la Lumière les brûle ! Regardez-moi ça : un nouveau baril de poudre qui part en fumée !$b$bOh ! Si seulement je pouvais y aller moi-même et tordre, l\'un après l\'autre, leurs petits cous décharnés ! J\'aimerais les tuer tous !$b$bTout ce stress, cette colère... Cela n\'est pas bon pour ma santé. Et qu\'ont-ils laissé d\'intact ? Rien !$b$bJe les massacrerais volontiers, mais je ne sais plus viser ! Allez donc dans la carrière et tuez quelques-uns de ces salopiauds ! Je vous paierai, mais faites-leur mal, détruisez-les !",
    ["O"] = "Tuer 6 Brise-crânes Mâcheroc pour le contremaître Stonebrow à la carrière de Gol\'Bolar.",
    ["T"] = "Ces satanés troggs !",
  },
  [433] = {
    ["D"] = "En tant que membre du Sénat et de la Ligue des Explorateurs, j\'ai la charge de m\'occuper des troggs qui infestent nos terres. Ils ont dévasté la carrière de Gol\'Bolar, sans raison apparente. Comme nous forions profondément, ils ont jailli, détruisant notre équipement et chassant les mineurs. Nous n\'avons d\'autre solution que d\'en exterminer un maximum, reconstruire et retourner au travail.$b$bSi vous m\'aidez à nous débarrasser de ces troggs, je vous récompenserai en conséquence.",
    ["O"] = "Tuer 10 Briseurs d\'os Mâcheroc pour le sénateur Mehr Stonehallow à la carrière de Gol\'Bolar.",
    ["T"] = "Le serviteur public",
  },
  [434] = {
    ["D"] = "Après avoir passé le déguisement à l\'Espion, il entrera pour parler à Lescovar. Les gardes devraient le laisser passer sans problème.$B$BPendant que vous cherchiez le matériel pour moi, j\'ai pris soin d\'informer Marzon qu\'il était temps de rencontrer à nouveau Lescovar. Je lui ai aussi glissé de fausses informations.$B$BLescovar congédiera ses gardes et attendra Marzon dans le jardin. Dès que vous entendrez parler des Defias, agissez. Vous devez les tuer tous les deux. C\'est compris ?$B$BQuand cela sera fait, allez parler à Trias.",
    ["O"] = "Rester dans le jardin jusqu\'à ce que le seigneur Gregor Lescovar et Marzon Lame silencieuse aient eu leur conversation. Après, les tuer tous les deux puis retourner voir Elling Trias à Stormwind.",
    ["T"] = "L\'attaque !",
  },
  [435] = {
    ["D"] = "Les loups ne partiront pas, et leurs yeux me fixent avec avidité dès que je fais un pas dehors. Ils doivent avoir une sacrée fringale pour penser que je suis comestible !$B$BJ\'ai un rapport à remettre à mon équipier, le Traqueur noir Rane Yorick, mais il faudra d\'abord se tailler une route à travers ces loups.$B$BEscortez-moi ! C\'est une véritable meute qui attend dehors ; si vous appeliez des amis en renfort, nous doublerions nos chances de réussite...",
    ["O"] = "Escorter Erland jusqu\'à Rane Yorick.",
    ["T"] = "Une escorte pour Erland",
  },
  [436] = {
    ["D"] = "Le Prospecteur Ironband dirige l’exploration de ruines à l\'est du lac. Malgré notre approvisionnement, sa progression s\'est ralentie dernièrement.$B$BIronband est un solide et honnête nain qui aime à faire du bon travail, c\'est pourquoi je crains que des forces contraires soient à l\'œuvre pour le ralentir.$B$BAllez aux fouilles d\'Ironband et parlez à Magmar Fellhew. C\'est lui qui gère le site, et il saura sûrement pourquoi le rythme de travail a décru.$B$BPour rejoindre les fouilles, contournez la pointe sud du lac, puis marchez droit vers l\'est.",
    ["O"] = "Parler à l\'Explorateur Fellhew.",
    ["T"] = "Les fouilles d\'Ironband",
  },
  [437] = {
    ["D"] = "Nos premiers rapports de reconnaissance dans la forêt des Pins argentés indiquent que les Poils-putrides ont une base dans une vieille ferme, le Champ des morts. Nous les avons attaqués à plusieurs reprises, mais à chaque fois ils reçoivent l\'assistance de la banshee Cinglenuit dont la magie désorganise et disperse nos forces.$B$BNous voulons la voir détruite.$B$BAllez dans le Champ des morts et attaquez les Poils-putrides. Si vous ne voyez pas la banshee, massacrez les Poils-putrides jusqu\'à ce qu\'elle apparaisse, puis tuez-la.$B$BSon essence même deviendra de la poussière. Rapportez-moi cette poussière comme preuve de sa destruction",
    ["O"] = "Tuer des gnolls Poils-putrides au Champ des morts.$B$BDétruire Cinglenuit quand elle apparaît.$B$BRapporter son essence au Grand exécuteur Hadrec au Sépulcre.",
    ["T"] = "Le Champ des morts",
  },
  [438] = {
    ["D"] = "$N, nous avons découvert une nouvelle infestation du Fléau : le bac délabré. Le bac se trouve à l\'est du Sépulcre, et fourmille de patrouilles de Poils-putrides et autres morts-vivants.$B$BLe Fléau a sans doute une bonne raison pour occuper la zone. Allez au bac délabré et trouvez pourquoi le Fléau est là-bas.",
    ["O"] = "Aller au bac délabré.$B$BChercher la raison de la présence du Fléau.",
    ["T"] = "Le bac délabré",
  },
  [439] = {
    ["D"] = "De la pile de corps dépasse une main de femme, marquée par la peste, mais encore relativement fraîche.$B$BUn doigt porte une bague. L\'observation de cette bague révèle une inscription :$B$B\"Pour Deliah\"",
    ["O"] = "Rapporter la Bague de Deliah au Grand exécuteur Hadrec au Sépulcre.",
    ["T"] = "Un indice vers les Poils-putrides",
  },
  [440] = {
    ["D"] = "Si vous voulez retrouver le mari ou l\'amant qui a donné cette bague à Deliah, parlez au Magistrat Sevren à Brill.$B$BMais je n\'entretiendrais pas d\'espoir à votre place : celui qui a offert la bague est certainement mort à ce jour. Et s\'il est devenu mort-vivant, il a oublié Deliah depuis longtemps.",
    ["O"] = "Ramener la Bague de Deliah au Magistrat Sevren à Brill.",
    ["T"] = "La bague ciselée",
  },
  [441] = {
    ["D"] = "Deliah avait un mari, Raleigh, qui maintenant habite à Undercity. Si la Deliah qui vivait à Brill est bien celle à qui appartient la bague, alors Raleigh le saura.$B$BRaleigh est dans le quartier des Marchands à Undercity.",
    ["O"] = "Apporter la Bague de Deliah à Raleigh.",
    ["T"] = "Raleigh et Undercity",
  },
  [442] = {
    ["D"] = "Il est maintenant temps d\'attaquer le bastion du Fléau dans les Pins argentés : l\'île de Fenris.$B$BVous devez aller sur l\'île et tuer Thule Ravenclaw. Apportez-moi sa tête !$B$BCette mission est aussi importante qu\'elle est vitale, $N. Vous ne pouvez échouer.",
    ["O"] = "Rapporter la Tête de Thule au Grand exécuteur Hadrec au Sépulcre.",
    ["T"] = "L\'attaque de l\'île de Fenris",
  },
  [443] = {
    ["D"] = "Avant que nous lancions notre offensive contre l\'île de Fenris, nous devons déterminer qui dirige les gnolls Poils-putrides. Nos Apothicaires pensent que leur origine est liée au fluide étrange qui coule dans leurs corps.$B$BAllez sur l\'île de Fenris, à l\'est du Bac délabré, et collectez de lichor de Poil-putride sur les gnolls. Portez-le à l\'apothicaire Renferrel, qui se trouve à l\'extérieur du Sépulcre",
    ["O"] = "Rapporter 8 bouteilles d\'Ichor de Poil-putride à l\'apothicaire Renferrel.",
    ["T"] = "L\'ichor de Poil-putride",
  },
  [444] = {
    ["D"] = "J\'ai réduit l\'ichor que vous m\'avez ramené et en ai séparé les principaux composants. Mes découvertes ne laissent aucun doute sur au moins un point : la magie des démonistes et des nécromanciens a été utilisée dans sa création.$B$BPendant que je pousserai mes recherches sur ce sujet, je veux que vous en apportiez un échantillon à Bethor Iceshard à Undercity. Bethor n\'est pas un apothicaire, mais sa connaissance de la magie pourrait nous aider à comprendre le mystère des Poils-putrides.$B$BPartez ! Vous trouverez Bethor dans le Quartier de la magie d\'Undercity.",
    ["O"] = "Apporter l\'Echantillon d\'ichor à Bethor Iceshard à Undercity.",
    ["T"] = "L\'origine des Poils-putrides",
  },
  [445] = {
    ["D"] = "Le temps n\'est pas un luxe que peuvent s\'offrir ceux qui travaillent pour Dame Sylvanas, mais vous le savez certainement.$b$bEn tant que membre de la Société royale des apothicaires, il est de mon devoir de partager mon savoir avec mes confrères afin que nos efforts conjoints puissent un jour offrir à la Dame noire cette Peste nouvelle qu\'elle désire si ardemment.$b$bPortez ces découvertes à l\'apothicaire Renferrel qui réside au Sépulcre dans la forêt des Pins argentés.",
    ["O"] = "Apporter les découvertes de l\'apothicaire Johaan à l\'Apothicaire Renferrel dans la forêt des Pins argentés.",
    ["T"] = "Livraison dans la forêt des Pins argentés",
  },
  [446] = {
    ["D"] = "Cet ichor a été créé par un vieux confrère : Thule Ravenclaw. C\'était un jeune homme prometteur avant la Grande Guerre, mais quand le roi-liche vint au pouvoir, il trahit ses camarades en s\'alliant au Fléau.$B$BJe n\'avais pas réalisé qu\'il était encore en vie, ni qu\'il menait les Poils-putrides contre nous !$B$BJe connais beaucoup de ses sorts, et je peux aider à concocter une potion pour les contrer. Rapportez ce parchemin à l\'apothicaire Renferrel. Il contient les incantations nécessaires à la potion.",
    ["O"] = "Porter le parchemin de Bethor à l\'Apothicaire Renferrel au Sépulcre des Pins argentés.",
    ["T"] = "Thule Serres-de-corbeau",
  },
  [447] = {
    ["D"] = "Les troupes d\'Arthas sont en nombre, mais avec une Peste nouvelle nous pourrions éradiquer à la fois l\'armée du Fléau et les humains.$b$bMes études ont prouvé que du sang d\'araignée associé à une toxine présente dans le cœur des vieux ours devient un élixir mortel. Collectez ces échantillons sur les araignées du Grouillement, au nord-ouest, et sur les ours qui arpentent la forêt des Pins argentés. Livrez ensuite ces ingrédients au maître apothicaire Faranell de la Société royale des apothicaires, à Undercity.",
    ["O"] = "Se rendre au Grouillement et y collecter 6 Cœurs de vieil ours et 6 échantillons de Sang du Grouillement, puis les apporter au maître apothicaire Faranell de la Société royale des apothicaires, à Undercity.",
    ["T"] = "Une recette mortelle",
  },
  [448] = {
    ["D"] = "Avant que vous n\'attaquiez l\'île de Fenris, faites un rapport au grand exécuteur Hadrec. Il aura sans doute les informations et l\'équipement complémentaires dont vous aurez besoin.",
    ["O"] = "Faire un rapport au Grand exécuteur Hadrec au Sépulcre.",
    ["T"] = "Un rapport pour Hadrec",
  },
  [449] = {
    ["D"] = "Bien que les blessures de Quinn soient en train de guérir, je veux que notre rapport parvienne au grand exécuteur Hadrec le plus vite possible. Il contient des informations vitales pour maintenir l\'occupation de la forêt des Pins argentés par les Réprouvés.",
    ["O"] = "Apporter le rapport du Traqueur noir au Grand exécuteur Hadrec au Sépulcre.",
    ["T"] = "Le rapport des Traqueurs noirs",
  },
  [450] = {
    ["D"] = "Vous pouvez encore être utile à la Société royale des apothicaires, $N.$b$bQuand Arugal a jeté sa misérable magie sur la forêt, il a maudit l\'un de nos membres les plus prometteurs, l\'apothicaire Berard. Devenu un serviteur décérébré d\'Arugal, Berard ne nous sert plus à rien, mais ses découvertes pourraient être utiles. Il étudiait une ancienne peste qui a décimé toute la faune aquatique du lac de Lordamere, il y a longtemps.$b$bRécupérez le journal de Berard dans son bureau à l\'auberge de Bois-du-Bûcher et rapportez-le à l\'apothicaire Renferrel.",
    ["O"] = "Récupérer le Journal de Berard à l’Auberge de Bois-du-Bûcher et le rapporter à l\'apothicaire Renferrel au Sépulcre.",
    ["T"] = "Une recette mortelle",
  },
  [451] = {
    ["D"] = "D\'après le journal de Berard, la mousse de Furtifs et de Rampants du lac pourrait intervenir dans la composition d\'une Peste nouvelle. Ces bêtes vivent sur les îles du lac de Lordamere, à l\'est.$b$bLe journal évoque aussi une rarissime Tumeur durcie qu\'il a découverte sur un murloc Aileron noir. Apparemment cette tumeur est tellement rare que Berard n\'a pu en prélever qu\'une.$b$bRecueillez des échantillons de mousse ainsi que de la Tumeur durcie et rapportez-les au Maître Apothicaire Faranell à Undercity.",
    ["O"] = "Apporter 6 Echantillons de Mousse de Rampant du lac, 6 échantillons de Mousse de Furtif du lac et une Tumeur durcie au Maître Apothicaire Faranell à Undercity.",
    ["T"] = "Une recette mortelle",
  },
  [452] = {
    ["D"] = "Vous avez fait vite, $N !$b$bLes Conseillers de Bois-du-Bûcher vont revenir dans un instant. Arugal utilise les membres du Conseil comme autant de marionnettes pour garder la main mise sur le village. Mais je pense que sans le Conseil, les partisans d\'Arugal se retourneraient contre leur maître.$b$bJe ne peux faire cela toute seule, j\'ai besoin de votre aide. Ensemble, nous dévasterons le Conseil au nom de Dame Sylvanas.",
    ["O"] = "Aider le Traqueur noir Faerleia à tuer les Membres du Conseil de Bois-du-Bûcher.",
    ["T"] = "Embuscade à Bois-du-Bûcher",
  },
  [453] = {
    ["D"] = "Bonne chance, $N. J\'espère que vous retrouverez le bonhomme, et vivant si possible.$B$BS\'il a fui la sécurité de Darkshire, il pourrait être la proie des bêtes sauvages.",
    ["O"] = "Retrouver l\'Obscure Silhouette. Indices :$B$BIl n\'est pas né à Darkshire.$B$BC\'est un individu nerveux et craintif.$B$BIl a quitté Darkshire pour faire route vers l\'ouest.",
    ["T"] = "À la recherche de l\'obscure silhouette",
  },
  [454] = {
    ["D"] = "Avant toute cette...histoire, nous étions en train de convoyer les barils de poudre. Mais maintenant nous devons répartir les tâches, Miran prenant les barils et moi veillant sur les autres.$B$BJe n\'aime pas l\'idée que Miran transporte le reste de la cargaison tout seul. Pourriez-vous l\'escorter quand il sera prêt à partir ?",
    ["O"] = "Parler à Miran.",
    ["T"] = "Après l\'embuscade",
  },
  [455] = {
    ["D"] = "Les orcs du clan Dragonmaw ont un refuge dans les Paluns. Mais voilà : ils se répandent maintenant dans Dun Algaz ! Nous ne pouvons les laisser s\'installer là, sinon le Loch Modan sera coupé du port de Menethil, notre port et bastion dans les Paluns.$B$BTraversez Dun Algaz jusqu\'aux Paluns et tuez quelques orcs pour faire bonne mesure! Faites un rapport à Valstag Ironjaw au port de Menethil.$B$BPour rejoindre Menethil, passez par Dun Algaz, puis allez au nord en longeant la route, et enfin prenez à l\'ouest à l\'embranchement.$B$BNous comptons sur vous, $N. Vous ne pouvez échouer.",
    ["O"] = "Traverser Dun Algaz.$B$BTuer 8 Eclaireurs Dragonmaw et 6 Grunts Dragonmaw.$B$BFaites votre rapport à Valstag Ironjaw au port de Menethil.",
    ["T"] = "Le défi d\'Algaz",
  },
  [456] = {
    ["D"] = "Salutations, $N. Je suis le conservateur Ilthalaine. Mon rôle à Sombrevallon est de m\'assurer que l\'équilibre naturel est maintenu.$b$bLes pluies printanières furent particulièrement abondantes cette année, et certains animaux de la forêt en ont profité pour prospérer quand d\'autres ont dépéri.$b$bMalheureusement la population des sabres-de-nuit et des sangliers des chardons s\'est trop accrue. Sombrevallon ne peut produire plus d\'une quantité donnée de nourriture pour les animaux. En route, jeune $c, réduisez la population des sabres-de-nuit et des sangliers des chardons afin de garantir l\'harmonie de la nature.",
    ["O"] = "Tuer 7 Jeunes sabres-de-nuit et 4 Jeunes sangliers des chardons, puis retournez voir le Conservateur Ilthalaine.",
    ["T"] = "L\'équilibre de la nature",
  },
  [457] = {
    ["D"] = "Réguler les populations d\'animaux à Sombrevallon est un bon début $N, mais il y a encore beaucoup à faire.$b$bLes ressources forestières seront bientôt épuisées si nous ne réglons pas rapidement le problème. Tuer les créatures de la nature est parfois un mal nécessaire pour le bien de tous ceux qui partagent cette terre. Aventurez-vous dans la forêt et éliminez les sabres-de-nuit et les sangliers des chardons malades, au nom de l\'équilibre naturel.",
    ["O"] = "Le Conservateur Ilthalaine voudrait que vous tuiez 7 Sabres-de-nuit galeux et 7 Sangliers des chardons.",
    ["T"] = "L\'Equilibre de la nature",
  },
  [458] = {
    ["D"] = "Louée soit la Nature, vous êtes là, $c ! D\'étranges rumeurs sont parvenues jusqu\'à moi par l\'entremise des esprits de la forêt.$b$bLa mystérieuse Protectrice sylvestre, Tarindrella, est rentrée à Sombrevallon. Cette dryade n’est pas venue dans les forêts de Kalimdor depuis des années. Il y a sûrement quelque chose qui cloche pour motiver son retour.$b$bCherchez Tarindrella et voyez ce qui l\'occupe en ce moment. Une des Sentinelles dit l\'avoir vue au sud-ouest d\'Aldrassil.",
    ["O"] = "Chercher la dryade connue sous le nom de Tarindrella.",
    ["T"] = "La protectrice sylvestre",
  },
  [459] = {
    ["D"] = "Quelque chose de malfaisant se développe dans les forêts de Teldrassil. Allez dans les collines où les paisibles furbolgs avaient habitude de vivre. Ils ont quitté leurs foyers et se regroupent maintenant, sous le nom de tribu des Pins-tordus.$b$bSeule la contamination par les pernicieuses gangremousses a pu provoquer ce changement. Les grells et les grellides ont infesté la zone et menacent les habitants de Sombrevallon.$b$bAttaquez ces grells et ces grellides, $N, et voyez s\'ils sont tombés sous l’influence des Mousses corrompues.",
    ["O"] = "Collecter 8 Gangremousses et les rapporter à Tarindrella.",
    ["T"] = "La protectrice sylvestre",
  },
  [460] = {
    ["D"] = "Vous n\'avez pas l\'air d\'un Poil-putride. C\'est déjà ça. J\'avais perdu espoir...$B$BMon nom est Alaric, j\'étais naguère le serviteur et garde du corps de Thule. Il a tenté d\'utiliser sa magie pour me \"protéger\" du Fléau mort-vivant, mais en fait, son sort m’a donné une forme de mort-vivant. Après avoir créé les maudits Poil-putride, Thule m’a décapité et donné ma tête aux gnolls pour qu\'ils jouent avec !$B$BJe vous supplie de me ramener près de mon corps. Je suis sûr que les gnolls l\'ont enterré ici, au donjon de Fenris, près des étables, dans une tombe anonyme.",
    ["O"] = "Apporter la tête d\'Alaric jusqu’à sa tombe.",
    ["T"] = "Reposer en… pièces",
  },
  [461] = {
    ["D"] = "Je connais une alcôve secrète dans le donjon, qui contient un objet cher à Thule et ses anciens confrères. Il y a également quelque chose qui pourrait vous servir. Amenez-moi sur place !$B$BL’alcôve se trouve dans la tour la plus au sud-ouest du donjon, derrière une étagère poussiéreuse.$B$BPrenez mon corps, mais laissez mes bottes. De toute façon elles ne m\'allaient pas...",
    ["O"] = "Amener Alaric à l’alcôve secrète.",
    ["T"] = "L’alcôve secrète",
  },
  [462] = {
    ["D"] = "Other than you, we sent two scouts through Algaz.  Only one returned.  Here\'s what he discovered:$B$BThe Dragonmaws sent one of their Lieutenants, Maruk Wyrmscale, to organize a crew of orcs in Algaz and cut off the land route to Menethil Harbor!$B$BWe have to stop this, now.  Take out Maruk, and then report to Valstag Ironjaw in Menethil Harbor.$B$BMaruk is headquartered in a small cave, across a pond between the second and third tunnels of Algaz.  Menethil is through Algaz, then west along the road.",
    ["O"] = "Kill Maruk Wyrmscale$B$BReport to Valstag Ironjaw in Menethil Harbor.",
    ["T"] = "<UNUSED>",
  },
  [463] = {
    ["D"] = "Avez-vous entendu parler du Gouverneur vert ? Vous ne le cherchez pas, n\'est-ce pas ? Je dirais que vous êtes $gfou:folle; si c\'est le cas, mais qui suis-je pour empêcher $gun illuminé:une illuminée; de courir à sa mort ?$B$BSi vous cherchez le monstre, je crois qu\'il hante les marécages à l\'est de la route où il y a l\'embranchement pour Dun Modr. Il est tapi là-bas, parmi les crocilisques et de choses pires encore !$B$BEt puis, laissez donc votre argent ici ; il ne vous servira à rien où vous allez. Vous ne voulez pas non plus que le Verdâtre s\'ébrèche les dents sur votre or quand il vous coupera en deux ?",
    ["O"] = "Trouver le Gouverneur vert.",
    ["T"] = "Le Gouverneur vert",
  },
  [464] = {
    ["D"] = "Le clan des orcs Dragonmaw fut pratiquement anéanti à la fin de la Deuxième Guerre après qu\'ils eurent perdu le contrôle de leurs dragons rouges. Les dragons n\'étaient pas contents de leurs anciens maîtres et ont sauvagement traqué les Dragonmaw. Les quelques survivants ont fui de Grim Batol dans les montagnes jusqu’à l\'est de l\'Excavation de Whelgar.$B$BMaintenant ils ont repris des forces et campent au nord-est de Menethil.$B$BNous voulons que vous attaquiez leur campement. Vous rassemblerez et me rapporterez leurs bannières de guerre comme preuve de leur destruction.",
    ["O"] = "Rapporter 8 Bannières de guerre Dragonmaw au capitaine Stoutfist.",
    ["T"] = "Bannières de guerre",
  },
  [465] = {
    ["D"] = "Pendant que vous combattiez les Dragonmaw, nous avons percé à jour certains de leurs plans.$B$BNek\'rosh a fait construire des catapultes et projette d\'assiéger Menethil ! Et avec les combats à Dun Algaz, nous sommes coupés des renforts !$B$BVous devez détruire ces catapultes. Elles sont au campement des Dragonmaw.$B$BVoici, prenez ce combustible. Taillez-vous une route à coups d\'épée jusqu\'aux catapultes, puis brûlez-les à l\'aide du combustible.$B$BAprès, trouvez et tuez Nek\'rosh. Rien ne nous plairait plus que d\'avoir la tête de cette engeance Dragonmaw au bout d\'une pique...",
    ["O"] = "Détruire les Catapultes Dragonmaw.",
    ["T"] = "La manœuvre de Nek\'rosh",
  },
  [466] = {
    ["D"] = "J\'ai appris qu\'il existait un minerai rare, l\'incendicite, dans les Paluns, dans une grotte au nord-ouest de la Porte d\'Algaz. C’est un produit assez volatil, il explose à la moindre mauvaise manipulation ! Mais je suis sûr que correctement extrait, on pourrait l\'utiliser pour donner un sacré punch aux obus de canon !$B$BApportez-moi de l\'incendicite et je trouverai bien quelqu\'un pour l\'expérimenter.$B$BAh ! Et pour extraire ce minerai vous devez avoir la Compétence de Minage. Si vous ne l\'avez pas, alors... amenez un ami qui la connaisse !",
    ["O"] = "Apporter 6 Chargements d’Incendicite au Pilote Stonegear à Dun Morogh.",
    ["T"] = "À la recherche de l\'incendicite",
  },
  [467] = {
    ["D"] = "Le pilote Stonegear adore ses engins de siège, comme il se doit ! Et il est toujours avide d\'informations lui permettant d\'apporter des améliorations techniques à ses chars !$B$BIl a entendu parler d\'un minerai rare et cherche quelqu\'un pour aller en collecter pour lui. Si vous aimez marcher, allez trouver Stonegear au dépôt de Steelgrill à Dun Morogh, et voyez ce dont il a besoin.",
    ["O"] = "Parler au Pilote Stonegear.",
    ["T"] = "La quête de Stonegear",
  },
  [468] = {
    ["D"] = "Le Montagnard Rockgar cherche des volontaires pour une mission dans Dun Algaz et les Paluns.$B$BSi vous voulez mouiller vos bottes dans cette terre détrempée au nord, allez parler à Rockgar à Dun Algaz. Dun Algaz est plus au nord sur la route, après la Tour de garde.",
    ["O"] = "Parler au Montagnard Rockgar.",
    ["T"] = "Un rapport pour le Montagnard Rockgar",
  },
  [469] = {
    ["D"] = "Bonjour !$b$bVous n’iriez pas vers Menethil, par hasard ? Sinon, vous pouvez faire halte ici, c\'est un bon endroit pour faire des achats ou prendre une pinte de bière.$b$bMais si vous allez tout de même à Menethil, vous pourriez peut-être apporter ce lot de peaux de crocilisques à James Halloran, le tanneur ?$b$bJe pourrais le faire moi-même, mais ma femme est d’une méchante humeur en ce moment, et j’aimerais autant éviter de la contrarier.",
    ["O"] = "Apporter le Lot de peaux de crocilisques à James Halloran, le tanneur, au port de Menethil.",
    ["T"] = "Livraison quotidienne",
  },
  [470] = {
    ["D"] = "A l\'occasion de ma visite hebdomadaire au Tombeau d\'Ironbeard, j\'ai été attaquée par de redoutables Limons visqueux ! Naturellement, j\'ai paniqué.$B$BJ\'ai jeté mon sac sur l\'un d\'eux, qui l\'a littéralement aspiré, ce qui m\'a laissé le temps de m\'enfuir.$B$BLa mauvaise nouvelle, c’est que j\'ai absolument besoin du contenu de mon sac !$B$BEt je ne sais pas sur quel Limon je l\'ai jeté : ils se ressemblent tous ! Le Tombeau d\'Ironbeard est au nord-est, près de Dun Modr. Merci mille fois !",
    ["O"] = "L\'un des Limons dans le Tombeau d\'Ironbeard a le sac de Sidonie. Le retrouver et le lui rapporter au Port de Menethil.",
    ["T"] = "En creusant dans la vase",
  },
  [471] = {
    ["D"] = "J\'ai envoyé mon apprenti dans les marécages pour chasser les fameux crocilisques géants des Paluns. Comme il ne revenait pas, je suis parti à sa recherche et l\'ai retrouvé. Tout ce que je peux dire c\'est que ce n\'était pas... plaisant à regarder.$b$bLa rebouteuse dit qu\'il va s\'en sortir, mais entre-temps, j\'ai toujours besoin de mes peaux !$b$b... Si vous tuiez quelques-uns de ces monstres, je n\'y ferais aucune objection ; après ce qu\'ils ont fait... Vous trouverez les crocos au nord de la côte, derrière les taudis des murlocs.",
    ["O"] = "Collecter 6 Peaux de crocilisque géant, et les apporter à James Halloran, au port de Menethil.",
    ["T"] = "Les devoirs de l\'apprenti",
  },
  [472] = {
    ["D"] = "Dun Modr est tombé aux mains des nains Sombrefer !$b$bMes blessures sont sérieuses, $c. Mon régiment a été pratiquement décimé ! Ces brutes de Sombrefer nous ont attaqués avant que nous puissions nous regrouper après l\'embuscade du viaduc de Thandol.$b$bNotre chef, Longbraid, a sonné la retraite. Comme nous quittions la ville, j’ai reçu une hache dans le dos. Un voile noir s\'est abattu devant mes yeux.$b$bJe me suis réveillé ici, en sécurité à Menethil, mais j\'ai peur pour mes compagnons. L\'espoir ne m\'a pas encore abandonné. Peut-être Longbraid est-il encore vivant ! $n, voyez si vous pouvez le retrouver, pas loin du village de Dun Modr !",
    ["O"] = "Rechercher Longbraid dans les parages du village de Dun Modr.",
    ["T"] = "La chute de Dun Modr",
  },
  [473] = {
    ["D"] = "$N. Le clan des orcs Dragonmaw montre sa puissance bien au-delà d\'Algaz. Le capitaine Stoutfist a demandé à parler à tous ceux qui sont prêts à s\'engager contre ces orcs. Il est à l\'intérieur du donjon, dans la salle de guerre.",
    ["O"] = "Parler au capitaine Stoutfist.",
    ["T"] = "Un rapport au capitaine Stoutfist",
  },
  [474] = {
    ["D"] = "Maintenant que les catapultes ont été détruites, trouvez et exécutez le Chef Nek\'rosh.",
    ["O"] = "Tuer le Chef Nek\'rosh$B$BRapporter la tête de Nek\'rosh au capitaine Stoutfist.",
    ["T"] = "Vaincre Nek\'rosh",
  },
  [475] = {
    ["D"] = "Une brise étrange souffle à travers la forêt.$b$bGaerolas Talvethren est le Grand gardien des druides de la Serre qui hibernent dans le Refuge des saisons de Ban\'ethil. Son devoir en tant que protecteur des Dormants est de veiller à leur sécurité, afin que le pacte avec Ysera ne soit pas rompu.$b$bMais je n\'ai toujours pas de nouvelles de Gaerolas. Allez à l\'est, au village de Brise-stellaire et revenez avec des nouvelles de Gaerolas, que je puisse calmer mes craintes en apprenant que mon vieux camarade est sain et sauf.",
    ["O"] = "Chercher Gaerolas Talvethren au village de Brise-stellaire.",
    ["T"] = "Une brise étrange",
  },
  [476] = {
    ["D"] = "La tribu des Pins-tordus a été corrompue !$b$bLes furbolgs, jadis paisibles, se sont retournés contre les protecteurs de la forêt. Ils m\'ont tendu une embuscade tandis que je partais pour le Refuge des saisons de Ban\'ethil, puis ils ont pillé le village de Brise-stellaire. Ursal le Mutileur, leur chef, utilise les pouvoirs malfaisants des gangremousses pour les rendre fous.$b$bJe suis trop gravement blessé pour aller voir Athridas et lui communiquer ces graves nouvelles. Cette tâche vous incombe à présent, jeune $c. Nous n\'avons plus qu\'à espérer que les Pins-tordus ne sont pas encore arrivés jusqu\'à Ban\'ethil !",
    ["O"] = "Retourner auprès d\'Athridas Bearmantle à Dolanaar.",
    ["T"] = "La corruption des Pins-tordus",
  },
  [477] = {
    ["D"] = "Comme vous devez le savoir, Moulin-de-l\'Ambre reste un bastion de l\'opposition humaine. La ville est soutenue par les Sorciers de Dalaran qui l’approvisionnent.$b$bJe ne connais pas leurs plans, mais s\'ils portent autant d\'intérêt à ce trou perdu, c\'est que cela doit être de première importance.$b$bIls transportent quantité de caisses depuis Hillsbrad. Beaucoup de ces convois ont pour terminus un petit camp au nord du village de Bois-du-Bûcher. Récupérez le contenu de l\'une de ces caisses et apportez-le moi.",
    ["O"] = "Récupérer le contenu de l’une des caisses des sorciers de Dalaran. Vous trouverez Bois-du-Bûcher au sud.",
    ["T"] = "Frontières",
  },
  [478] = {
    ["D"] = "<Vous retirez rapidement le pendentif de la caisse et rabattez le couvercle.>",
    ["O"] = "Apporter l\'étrange pendentif au prêtre des ombres Allister au Sépulcre.",
    ["T"] = "Des cartes et des runes",
  },
  [479] = {
    ["D"] = "Dalar essaie de localiser la source de la magie des sorciers. Pour l\'heure, nous devons nous efforcer de ralentir leurs progrès...$b$bIl ne fait aucun doute que les conjurateurs, les mages et les protecteurs portent ces pendentifs. Récupérez-en.$b$bSuivez la route principale au sud, puis prenez à l\'est à l\'embranchement qui conduit à Moulin-de-l\'Ambre.",
    ["O"] = "Récupérer 8 Pendentifs de Dalaran pour le prêtre des ombres Allister du Sépulcre.",
    ["T"] = "Enquête à Moulin-de-l\'Ambre",
  },
  [480] = {
    ["D"] = "Un archimage de grand pouvoir supervise la réactivation du nœud d\'énergie Ley. Vu les progrès qu\'ils ont faits, notre seule chance de les arrêter est de tuer cet archimage. Ce sera difficile, car il est certainement protégé par d\'autres mages et gardiens.$b$bVous devez partir et le neutraliser au plus vite. Quand il sera mort, récupérez son Bâton et apportez-le-moi. En avant !",
    ["O"] = "Tuer l\'archimage de Dalaran, puis récupérer son Bâton pour le prêtre des ombres Allister du Sépulcre.",
    ["T"] = "Le Tisserand",
  },
  [481] = {
    ["D"] = "Apporter le Pendentif à Dalar Dawnweaver. Peut-être pourra-t-il anticiper les plans des Sorciers.",
    ["O"] = "Apporter le Pendentif marqué de runes à Dalar Dawnweaver au Sépulcre.",
    ["T"] = "L\'analyse de Dalar",
  },
  [482] = {
    ["D"] = "Bien sûr ! Comment ai-je pu oublier ?$b$bMoulin-de-l\'Ambre était l\'un des sites de prédilection pour le Kirin Tor, célèbre pour abriter des nœuds dormants d\'énergie Ley. Les Sorciers doivent projeter de réactiver les nœuds et d\'utiliser cette énergie pour quelque grande œuvre.$b$bNous ne pouvons laisser faire...$b$bIls vont avoir besoin de beaucoup de puissance pour activer les zones d\'énergie Ley et nous pouvons enrayer le processus en volant leurs Pendentifs aux Sorciers.$b$bRapportez cette information à Allister, il décidera de la suite.",
    ["O"] = "Parler au prêtre des ombres Allister au Sépulcre.",
    ["T"] = "Les intentions de Dalaran",
  },
  [483] = {
    ["D"] = "Les Pins-tordus ont été vus ravageant le Refuge des saisons de Ban\'ethil, à l\'ouest.$b$bLes druides dormants vont être pris dans le Rêve d\'émeraude pour l\'éternité, à moins que nous n\'intervenions. Leur très délicate hibernation ne peut pas être interrompue sans les quatre Reliques d\'éveil.$b$bRendez-vous au Refuge et récupérez le Talisman Ravenclaw, la Plume noire, le Saphir des cieux et la Rune de nidification. Les druides les gardent dans des coffres sacrés. Apportez-les-moi afin que je prépare le rituel d\'éveil.",
    ["O"] = "Récupérer les Reliques d\'éveil et les rapporter à Athridas Bearmantle à Dolanaar.",
    ["T"] = "Les Reliques d\'éveil",
  },
  [484] = {
    ["D"] = "Je suis James Halloran, le tanneur. Mes peaux de crocilisque traitées sont prisées dans le monde entier.$b$bVous connaissez Einar ? C’est l’un des chasseurs qui m\'approvisionne en peaux.$b$bJe suis en ce moment à court de ces peaux soyeuses qui proviennent des jeunes crocilisques.$b$bJe vous paierais grassement si vous pouviez me rapporter quelques-unes de ces peaux. Vous trouverez les jeunes crocilisques dans les marécages, à l’extérieur de la ville.",
    ["O"] = "Collecter 4 Peaux de jeune crocilisque, pour James Halloran, au port de Menethil.",
    ["T"] = "Les peaux de jeune crocilisque",
  },
  [485] = {
    ["D"] = "Vous avez découvert un étrange objet en forme d\'oeuf fait de métal. En jouant avec les petit boutons présents sur le dessus, l\'oeuf s\'anime, à mesure qu\'il s\'ouvre, il se transforme en une espèce de bidule robot gnomique ! Une voix sort du robot en grésillant.$B$B\"Je suis Oglethorpe Obnoticus, et mon robot guidé a disparu ! Je vous récompenserais si vous le ramenez ! Amenez cette balise au robot.\"$B$B\"J\'ai programmé les coordonnées du robot pour vous. Il s\'est écrasé en haut de la rivière près du rocher de l\'Affût !\"",
    ["O"] = "Apporter la balise de détresse jusqu\'au robot guidé d\'Oglethorpe en haut de la rivière dans les Hinterlands, près du rocher de l\'Affût.",
    ["T"] = "Trouver OOX-09/HL !",
  },
  [486] = {
    ["D"] = "Le temps est venu de sauver les druides de la Serre. Si nous échouons maintenant, $N, ils resteront à jamais captifs de leur sommeil.$b$bJe pourrais préparer les Reliques d\'éveil et pratiquer le rituel, mais mon travail ne peut prendre effet tant que le responsable de cette horrible situation est en vie. Le rituel ne sera achevé que lorsqu\'il aura été terrassé.$b$bC\'est Ursal le Mutileur qui a perturbé notre cycle, c\'est Ursal le Mutileur qui doit payer pour qu\'ils puissent être libérés. Voyagez jusqu\'au camp des Pins-tordus, au sud-ouest, et exécutez-le.",
    ["O"] = "Tuer Ursal le Mutileur et retourner voir Athridas Bearmantle à Dolanaar.",
    ["T"] = "Ursal le Mutileur",
  },
  [487] = {
    ["D"] = "La route de Darnassus doit être sécurisée. Des voyageurs qui cheminaient entre Dolanaar et Darnassus y ont été témoins d\'attaques de furbolgs de la tribu Pins-tordus.$b$bD\'importantes informations et des convois commerciaux en provenance de Darnassus empruntent cette voie quotidiennement. Nous ne pouvons tolérer qu\'une bande de brigands y terrorise les gens.$b$bAu nom de la Forêt sacrée, prenez les armes, $c. Leur repaire est quelque part au-delà de ce point. Tuez 6 de ces embusqués Pins-tordus et revenez faire votre rapport.",
    ["O"] = "Tuer 6 Embusqués Pins-tordus et retourner voir la Sentinelle Amara Nightwalker à l\'extérieur de Dolanaar.",
    ["T"] = "La route de Darnassus",
  },
  [488] = {
    ["D"] = "Volontaire pour des missions, à ce que je vois. Heureusement, il n\'y a pas un jour sans que je n\'aie besoin d\'un courageux $c prêt et armé pour répondre à mes demandes.$b$bVoyez-vous, $N, je puis vous rendre riche et vous offrir ce dont vous n\'osez rêver. Mais en échange vous devez me rapporter certains objets.$b$bMon travail dans la forêt requiert souvent certains... réactifs. Rapportez-moi 3 Crocs de sabre-de-nuit, 3 Plumes de hibou Strigid et 3 échantillons de Soie d\'araignée Tissebois.$b$bEt que tout cela reste entre nous, $n.",
    ["O"] = "Rapporter 3 Crocs de sabre-de-nuit, 3 Plumes de Hibou Strigid et 3 Echantillons de Soie d\'araignée Tissebois à Zenn Foulhoof à l\'extérieur de Dolanaar.",
    ["T"] = "Les commandements de Zenn",
  },
  [489] = {
    ["D"] = "Le Conseil de la forêt a eu vent que vous aviez aidé Zenn Foulhoof. Ce satyre est un ennemi de la forêt. Un $r devrait avoir mieux à faire que de la fragiliser en tuant ses créatures.$B$BVous devez vous racheter aux yeux du Conseil si vous désirez rester $gun ami:une amie; de Teldrassil.$B$BDonnez une bonne leçon à Foulhoof et vous serez $gracheté:rachetée;. Les gangrenoix sont des graines malades qui tombent des arbres. De la fumée verte en sort.$B$BApportez-en à Foulhoof. Il croira qu’il s’agit d’un inoffensif casse-croûte.",
    ["O"] = "Collecter 3 Gangrenoix et les offrir à Zenn Foulhoof à l\'extérieur de Dolanaar.",
    ["T"] = "En quête de rédemption !",
  },
  [490] = {
    ["D"] = "By strict orders from the Council of Darnassus, I am commissioning a bounty on the Gnarlpine Tribe. They are no longer friends of the forest. Their corruption has left them mindless threats to our people and the creatures of the glade.$b$bAs decreed by the Council, you shall be rewarded for removing the furbolg menaces from Kalidar. Bring to me their fangs as proof of your deeds.",
    ["O"] = "Shayla Nightbreeze outside of Darnassus wants you to bring her 20 Gnarlpine Fangs.",
    ["T"] = "<UNUSED>",
  },
  [491] = {
    ["D"] = "Thule m\'a raconté qu\'il y a longtemps, lui et deux de ses confrères créèrent une Baguette magique, la fusion des trois Baguettes que les mages possédaient alors. Cette Baguette devint le symbole de leur amitié, et bien que Thule se mit peu à peu à la mépriser il ne put se résoudre à s\'en séparer ou à la laisser aux deux autres. Alors il vola la Baguette et la cacha dans le donjon de Fenris.$B$BVoilà. Apportez la Baguette à Bethor Iceshard, l\'un des anciens amis de Thule. Je suis sûr qu’il appréciera.",
    ["O"] = "Apporter le Bâton tressé à Bethor Iceshard dans le Quartier de la magie à Undercity.",
    ["T"] = "Une Baguette pour Bethor",
  },
  [492] = {
    ["D"] = "Selon le Garde noir, un autre de ces Montagnards nains insensés vient juste d\'être capturé. Le Garde noir aime utiliser la cave de la taverne des Pendus comme cellule, jusqu\'à ce qu\'on puisse \"correctement\" s\'occuper des prisonniers.$b$bPourquoi n\'iriez-vous pas voir comment le captif apprécie la boisson spéciale que j\'ai concocté pour lui ? Il contient un soupçon du traitement que la Dame noire a réservé au reste d\'Azeroth.",
    ["O"] = "Apporter la Boisson spéciale de Johaan au Montagnard captif.",
    ["T"] = "Une Peste nouvelle",
  },
  [493] = {
    ["D"] = "La Société royale des apothicaires est sous la constante pression de la Dame noire pour le développement de la Peste nouvelle. Nous travaillons sans relâche et avons fait de grands progrès. Nous pensons que le succès de notre entreprise serait plus rapide si nous partagions les informations au sein de la Société.$b$bPour cette raison, je veux que vous portiez mes dernières recherches à l\'apothicaire Lydon à Moulin-de-Tarren, une petite ville nichée dans les contreforts d\'Hillsbrad. Le voyage sera long.$b$bAllez au sud sans quitter la route. Suivez de près les panneaux !",
    ["O"] = "Livrer les découvertes de l\'Apothicaire Renferrel à l\'Apothicaire Lydon à Moulin-de-Tarren dans les contreforts d\'Hillsbrad.",
    ["T"] = "Voyage jusqu’aux contreforts d\'Hillsbrad",
  },
  [494] = {
    ["D"] = "Taisez-vous et écoutez, $c.$b$bJ\'ai été envoyée ici pour surveiller la ville d\'Hillsbrad. C\'est une mission de reconnaissance. Il est impératif que vous envoyiez immédiatement des nouvelles à la Grande Exécutrice Darthalia à Moulin-de-Tarren. Dites-lui que moi, le Traqueur noir Lesh, je lui transmets le message suivant :$b$b\"Le cri du corbeau retentit à l\'ouest.\"$b$bSuivez la route vers l\'est et faites bien attention aux panneaux. Allons, vite, partez pour Moulin-de-Tarren, et en toute hâte !",
    ["O"] = "Se rendre à Moulin-de-Tarren et délivrer le message du Traqueur noir Lesh à la Grande Exécutrice Darthalia.",
    ["T"] = "Prêts à frapper",
  },
  [495] = {
    ["D"] = "Mug\'Thol et sa troupe de guerre ogre ont été envoyés à Alterac afin de récupérer la Couronne de Volonté pour dame Sylvanas. Malheureusement, au lieu de la rapporter, cet idiot a décidé de l\'essayer... Je suppose que les ogres ne peuvent résister à ce qui brille.$b$bSes actes irresponsables sont de toute façon devenus un problème. La Couronne de Volonté lui a permis de résister à l\'enchantement qui le liait au service de dame Sylvanas.$b$bLa Dame noire veut faire un exemple, allez à Moulin-de-Tarren à Hillsbrad et faites un rapport à Melisara, mon lieutenant.",
    ["O"] = "Faire un rapport à Melisara à Moulin-de-Tarren dans les Contreforts d\'Hillsbrad.",
    ["T"] = "La couronne de volonté",
  },
  [496] = {
    ["D"] = "Ah ! Un autre jour misérable à Moulin-de-Tarren ! Tout cet air pur me rend fou, $N.$b$bPlus vite nous répandrons l\'épidémie parmi les humains, mieux ce sera. Je fais d\'importantes recherches pour découvrir des agents mortels à utiliser dans mes décoctions, mais je n\'ai pas eu le temps de les collecter tous.$b$bSi vous voulez vous rendre utile, trouvez-moi 10 Langues d\'ours gris et du très rare Ichor de Rampant. Vous trouverez ces ingrédients juste à l\'extérieur de Moulin-de-Tarren.",
    ["O"] = "Apporter 10 Langues d\'ours gris et de l\'Ichor de Rampant à l\'Apothicaire Lydon à Moulin-de-Tarren.",
    ["T"] = "L\'élixir de souffrance",
  },
  [497] = {
    ["D"] = "As a child, raised as a gladiator at Durnholde Keep, I was befriended by Taretha, the daughter of my wet nurse and mistress of the damned bastard Blackmoore.$b$bThe past is the past, $n, but reports of human movements in Hillsbrad reminded me of that time. I dispatched a small party to the area to investigate and to retrieve an... item... given to me by Taretha, if they could.$b$bA fruitless task, no doubt, but perhaps...$b$bThey should have returned by now, I want you to go to Tarren Mill to investigate.",
    ["O"] = "Go to Tarren Mill and find out the status of the party sent by Thrall.",
    ["T"] = "<UNUSED>",
  },
  [498] = {
    ["D"] = "Moi et d\'autres guerriers avons été envoyés par le Chef de guerre pour récupérer un objet à Durnholde et enquêter sur ce prétendu Syndicat.$b$bNous sommes allés à Durnholde pour jeter un coup d\'œil, mais nous avons été surpris par un grand nombre d\'humains. Nous nous sommes bravement battus, mais ils étaient plus nombreux. Ils se sont emparés de nous, mais je suis parvenu à m\'échapper.$b$bDurnholde est au sud-est, de l\'autre côté de la rivière. Drull et Tog\'thar sont retenus là-bas, vous devez aller leur porter secours.",
    ["O"] = "Selon les ordres de Krusk à Moulin-de-Tarren, libérer Drull et Tog\'thar du Donjon de Durnholde.",
    ["T"] = "Sauvetage",
  },
  [499] = {
    ["D"] = "Cela va prendre un petit moment d\'associer ces ingrédients pour mettre au point ce qui s\'appellera l\'élixir de souffrance.$b$bNous y voilà ! Ah ! Rien de tel pour égayer une triste journée dans ces mornes contrées que d\'insuffler un peu de souffrance dans le monde.$b$bVoyons voir si notre petite potion produit l\'effet désiré.$b$bDonnez à mon assistant Umpi un avant-goût de ce que la Dame noire a prévu pour les humains et ceux du Fléau.",
    ["O"] = "Donner l\'Elixir de souffrance à Umpi.",
    ["T"] = "L\'élixir de souffrance",
  },
  [500] = {
    ["D"] = "Les ogres Cassecrête ont installé un repaire dans les montagnes d\'Alterac, près des ruines d\'Alterac. Mes éclaireurs m\'ont appris qu\'ils occupaient également ces ruines.$B$BNous ne pouvons pas les laisser s\'installer tranquillement là-bas. S\'ils commencent à se sentir en sécurité là où ils sont, ils ne tarderont pas à faire mouvement dans les collines. Ils seront alors à nos portes !$B$BPartez au nord dans les montagnes d\'Alterac et chassez des ogres. Rapportez-moi leurs Articulations sales, je vous en donnerai une belle prime.",
    ["O"] = "Collecter 9 Articulations sales d\'ogres Cassecrête dans les montagnes d\'Alterac. Les apporter au maréchal Redpath à Southshore.",
    ["T"] = "La prime Cassecrête",
  },
  [501] = {
    ["D"] = "Oh ! $N… Les fleurs s’épanouissent à Hillsbrad et l\'air est si pur et vif... Comme cela est triste et regrettable. Je regarde par ma fenêtre crasseuse et je me languis de voir notre Peste nouvelle se répandre et porter la mort à ce monde qui le mérite tant.$b$bBien sûr, je resterais bien à ruminer mes pensées morbides toute la journée, mais mon devoir envers la Dame noire ne peut attendre. Vous m\'aiderez, n\'est-ce pas ? Mes recherches m\'ont permis de conclure que la plus mortelle des préparations s\'obtient à partir du sang d\'un lion des montagnes. Apportez m\'en et vous verrez ce que je suis capable de faire.",
    ["O"] = "Apporter 10 Fioles de sang de lion des montagnes à l\'Apothicaire Lydon à Moulin-de-Tarren.",
    ["T"] = "L\'élixir de douleur",
  },
  [502] = {
    ["D"] = "Ah ! Rien de tel que l\'odeur du sang frais dans les collines !$b$bMaintenant, nous allons mélanger tout cela et commencer à nous amuser pour de bon ! Ce que j\'ai créé à partir de ce sang, je l\'appellerai l\'élixir de douleur. Si mes calculs sont exacts, cette mixture pourrait bien être le début de la grande œuvre que je veux offrir à dame Sylvanas. Mais avant tout, nous avons besoin de faire un test.$b$bPrenez cet élixir et allez à la ferme la plus au nord dans les champs d\'Hillsbrad, au sud-ouest. Et voyons comment Stanley, le petit chien du fermier Ray, apprécie cette \"douceur\".",
    ["O"] = "Donner l’Elixir de douleur à Stanley.",
    ["T"] = "L\'élixir de douleur",
  },
  [503] = {
    ["D"] = "D\'après les documents que vous avez rapportés, Gol\'dir est retenu dans une grande base du Syndicat, plus au nord, très certainement dans les monts Alterac. Il est captif depuis trop longtemps et doit être absolument libéré.",
    ["O"] = "Libérer Gol\'dir et retourner voir Krusk à Moulin-de-Tarren.",
    ["T"] = "Gol\'dir",
  },
  [504] = {
    ["D"] = "Maintenant que vous avez eu un avant-goût des ogres Cassecrête, je veux que vous leur rentriez dedans franchement...$B$BAllez dans les ruines d\'Alterac et débusquez des Combattants Cassecrête. Je veux que vous en fauchiez le plus possible : c\'est la seule façon de faire comprendre à ces brutes qu\'il faut garder leurs distances avec le territoire de l\'Alliance.",
    ["O"] = "Tuer 15 Combattants Cassecrête, puis retourner voir le maréchal Redpath à Southshore.",
    ["T"] = "Les Combattants Cassecrête",
  },
  [505] = {
    ["D"] = "Je suis le nouveau Magistrat de Southshore, assigné ici suite à l\'assassinat du précédent Magistrat. Les tueurs n’ont jamais été retrouvés, mais nos investigations nous ont permis de conclure qu\'ils avaient été engagés par le Syndicat, un groupe de voleurs commandé par les nobles du royaume déchu d\'Alterac.$B$BLe Syndicat a un camp au nord de Moulin-de-Tarren, occupé par la Horde. D\'après nos derniers indices les pistes convergent toutes vers ce camp. Exécutez les membres du Syndicat que vous trouverez là-bas.",
    ["O"] = "Tuer 12 Détrousseurs du Syndicat et 8 Larrons du Syndicat puis retourner voir le magistrat Henry Maleb à Southshore.",
    ["T"] = "Syndicat d\'assassins",
  },
  [506] = {
    ["D"] = "Il y a quelques jours, j\'ai reçu la visite du leader de ce Syndicat. Son nom est Aliden Perenolde et il se prend pour le seigneur de ce pays. Il m\'a confié qu\'il voulait utiliser les orcs qui s\'insurgent contre lui comme esclaves, exactement comme le voulait son père et mentor, le méprisable Blackmoore.$b$bPlus important : il est venu en compagnie de sa maîtresse, une petite femme nommée Elysa, qui portait autour du cou le pendentif que le Chef de guerre désire tant.$b$bPartez maintenant, je me débrouillerai pour régler mes affaires.",
    ["O"] = "Transmettre l\'information recueillie par Gol\'dir à Krusk à Moulin-de-Tarren.",
    ["T"] = "L\'héritage de Blackmoore",
  },
  [507] = {
    ["D"] = "Il est évident que Perenolde doit être tué pour que son influence néfaste disparaisse de la surface de ce monde. Son manoir se trouve au nord, sur les rives du lac Lordamere. Faites votre devoir, $N, et récupérez le pendentif pour le Chef de guerre.",
    ["O"] = "Tuer le seigneur Aliden Perenolde et interroger sa maîtresse, Elysa, au sujet du pendentif de Taretha.",
    ["T"] = "Seigneur Aliden Perenolde",
  },
  [508] = {
    ["D"] = "Le Pendentif ? Bien sûr que vous pouvez l\'avoir, prenez-le ! Je ne veux garder de lui aucun souvenir… Laissez-moi tranquille, je ne vous demande rien d\'autre.",
    ["O"] = "Rapporter le Pendentif de Taretha à Krusk à Moulin-de-Tarren.",
    ["T"] = "Le cadeau de Taretha",
  },
  [509] = {
    ["D"] = "À chaque jour son élixir. Si cela ne tenait qu\'à moi, je laisserais bien ces humains pathétiques et le Fléau se combattre. Ceux-là semblent nés pour s\'entre-tuer.$b$bMais la Dame noire veut accélérer le processus, alors pourquoi ne pas passer ces longues et morbides journées à répandre un peu de peste et de mort à travers le pays ?$b$bVoici le plan, $N : vous irez à la ferme des Nethander, au sud de Moulin-de-Tarren et à l\'est de la rivière et de Southshore. Collectez 6 Fleurs de fangemufle dans les champs et rapportez-les-moi que je puisse achever mon prochain élixir.",
    ["O"] = "Apporter 6 Fleurs de fangemufle à l\'Apothicaire Lydon à Moulin-de-Tarren.",
    ["T"] = "L\'élixir d\'agonie",
  },
  [510] = {
    ["D"] = "Parmi ces documents se trouve une liste des marchandises commandées. Elle contient des requêtes ordinaires (nourriture, armes, vêtements), mais aussi des demandes moins conventionnelles (masques, huile, poison).$B$BMais ce qu\'il y a de plus remarquable est le caractère urgent de la commande. Ces marchandises étaient nécessaires à l\'exécution de ce qui est décrit dans le document comme une \"mission importante contre Southshore\", et l\'on en avait besoin rapidement.",
    ["O"] = "Apporter les Documents d\'annonce d\'attaque au Magistrat Maleb à Southshore.",
    ["T"] = "Documents d\'annonce d\'attaque",
  },
  [511] = {
    ["D"] = "L\'une des lettres, sur la table, est codée et ne peut être déchiffrée.",
    ["O"] = "Apporter la Lettre codée au Maître des traditions Dibbs, à Southshore.",
    ["T"] = "Lettre codée",
  },
  [512] = {
    ["D"] = "J\'ai peur que les dirigeants du Syndicat ne veuillent à tout prix la ruine de Southshore. Nous devons porter un coup décisif à ses chefs avant que leurs plans n\'aboutissent.$B$BPartez pour les Ruines de Strahnbrad, dans les monts Alterac au nord. Nous pensons que le Syndicat a établi son quartier général dans cette région.$B$BNous savons que ce gang de brigands reçoit ses ordres d\'anciens nobles d\'Alterac. Il se peut qu\'ils se camouflent parmi les agents ordinaires du Syndicat, mais vous saurez que vous avez tué un noble à la chevalière qu\'il porte.$B$BApportez-moi ces chevalières.",
    ["O"] = "Apporter 7 Chevalières d\'Alterac au Magistrat Maleb à Southshore.",
    ["T"] = "Nobles morts",
  },
  [513] = {
    ["D"] = "Alors que je me résignais à l\'idée qu\'un autre jour allait s\'achever exempt de toute joie, vous m\'apportez ces merveilleuses fleurs de fangemufle.$B$BJe vais d\'abord les réduire en une fine poudre. Nous aurons alors besoin que le Maître Apothicaire Faranell à Undercity lui applique son colloïde de virulence. Après, on devrait plutôt s\'amuser, je crois... $b$bTenez, prenez cette Poudre de fangemufle et courez immédiatement voir maître Faranell, $N.",
    ["O"] = "Apporter la Poudre de fangemufle au Maître Apothicaire Faranell à Undercity.",
    ["T"] = "L\'élixir d\'agonie",
  },
  [514] = {
    ["D"] = "J\'ai un collègue à Ironforge qui pourrait peut-être décoder cette lettre. Rendez-vous à la bibliothèque du Hall des Explorateurs à Ironforge et parlez au Prospecteur Stormpike. Dites-lui que je vous envoie et montrez-lui la lettre. S\'il y a une personne au nord du pic Blackrock à pouvoir lire cela, c\'est bien lui.",
    ["O"] = "Apporter la Lettre ingénieusement codée au Prospecteur Stormpike à Ironforge.",
    ["T"] = "Le maître des lettres",
  },
  [515] = {
    ["D"] = "Un composé à base de fangemufle ? Absolument merveilleux ! Pourquoi n\'y ai-je pas pensé ? Laissez-moi appliquer de mon colloïde de virulence à cette diabolique mixture.$B$BAfin d\'activer les agents contaminateurs de ce brouet de fangemufle, Lydon aura besoin d\'une potion de sang de troll forte, d\'écailles de Daguéchine et d\'yeux d\'Ailerons déchirés de la côte méridionale. Il saura quoi faire une fois que vous aurez réuni tous les ingrédients. Ah ! j\'ai tellement hâte d\'apprendre ce que l\'expérience va donner.",
    ["O"] = "Apporter le Brouet de fangemufle, une Potion de sang de troll forte, 5 Ecailles de Daguéchine et 5 Yeux d\'Ailerons déchirés à l\'Apothicaire Lydon à Moulin-de-Tarren.",
    ["T"] = "L\'élixir d\'agonie",
  },
  [516] = {
    ["D"] = "J\'ai reçu des rapports indiquant qu\'un groupe de morts-vivants se cache pour préparer une attaque contre nous. Armés de cette information, nous pouvons retourner la situation en les attaquant les premiers et réduire leur plan minable à un simple souvenir cocasse.$b$bMalheureusement, les informations dont nous disposons sont incomplètes, au mieux. On dit qu\'ils se cachent dans un endroit appelé Péril de Beren. La localisation exacte est inconnue, mais nous savons que c\'est une caverne dans les collines, près d\'une enclave des sorciers de Dalaran.$B$BJ\'ai confiance en vos ressources. Trouvez-les et tuez-les.",
    ["O"] = "Localiser le Péril de Beren, tuer 6 Manouvriers Ravenclaw et 6 Gardiens Ravenclaw puis retourner voir le prêtre des ombres Allister au Sépulcre.",
    ["T"] = "Le Péril de Beren",
  },
  [517] = {
    ["D"] = "Ce nouvel élixir est enfin prêt à être testé. Mais pourquoi gâcher une si bonne et si contagieuse peste sur une pauvre grenouille ou un chien famélique ? Amusons-nous un peu, au nom de Dame Sylvanas.$B$BDescendez à Dun Garok. Vous connaissez les casernes où ces sales petits nains s’agitent partout ? Volez un baril de leur Bière Shindigger pour moi. Une fois que j\'aurai ce baril, nous pourrons commencer la fête, vous et moi, $N.",
    ["O"] = "Apporter un baril de Bière Shindigger à l\'Apothicaire Lydon à Moulin-de-Tarren.",
    ["T"] = "L\'élixir d\'agonie",
  },
  [518] = {
    ["D"] = "Mug\'Thol et ses ogres Cassecrête ont envahi les ruines d\'Alterac. Il va être difficile de lui infliger une sérieuse punition sans d’abord entamer ses troupes.$b$bAprès les avoir réduites à la portion congrue, nous l’attaquerons.$b$bCompris ? Bien. Allez-y à présent.",
    ["O"] = "Tuer 14 Mutilateurs Cassecrête pour Melisara à Moulin-de-Tarren.",
    ["T"] = "La couronne de volonté",
  },
  [519] = {
    ["D"] = "Targ, Fouilleboue et Glommus sont les lieutenants de Mug\'Thol. Ils lui sont loyaux, bien que personne ne sache vraiment ce qui peut amener un ogre à la loyauté.$b$bApportez-moi leurs têtes, $n, et alors tout ce qu\'il restera à faire sera de s\'occuper de celle de Mug\'Thol, de sa couronne, et du reste.",
    ["O"] = "Tuer Targ, Fouilleboue et Glommus, puis apporter leurs têtes à Melisara à Moulin-de-Tarren.",
    ["T"] = "La couronne de volonté",
  },
  [520] = {
    ["D"] = "À présent que les lieutenants de Mug\'Thol sont morts, tout ce qu\'il reste à faire est de tuer Mug\'Thol et de récupérer la Couronne de Volonté. Je ne crois pas qu\'il veuille la donner gentiment. Et encore moins à vous, si l\'on considère la façon dont ses amis sont... partis.$b$bTrouvez Mug\'Thol, et présentez-moi sa tête et la Couronne. Le glas sonnera alors pour la résistance des Cassecrête.",
    ["O"] = "Tuer Mug\'Thol et présenter sa tête et la Couronne de volonté à Melisara, à Moulin-de-Tarren.",
    ["T"] = "La couronne de volonté",
  },
  [521] = {
    ["D"] = "Tout ce qu\'il reste à faire est de donner la Couronne de Volonté à Sharlindra, à Undercity. Elle veillera à ce que la Couronne soit remise à Dame Sylvanas. Je vous déconseille de penser à la coiffer à vos propres fins, $n. Le sort de Mug\'Thol est assez éloquent.",
    ["O"] = "Remettre la Couronne de Volonté à Sharlindra, à Undercity.",
    ["T"] = "La couronne de volonté",
  },
  [522] = {
    ["D"] = "Ceci est un contrat pour l\'assassinat du Magistrat Henry Maleb. L\'ordre a été scellé à la cire rouge, à l\'aide d\'une chevalière caractéristique.",
    ["O"] = "Apporter le Contrat de l\'assassin au Magistrat Maleb à Southshore.",
    ["T"] = "Contrat de l\'assassin",
  },
  [523] = {
    ["D"] = "Le contrat que vous avez trouvé est signé par le baron Vardus, un noble de l\'ancien royaume d\'Alterac, réputé pour son cœur de pierre et ses pratiques barbares. Je ne suis pas surpris que quelqu\'un comme le baron soit derrière tout ça.$b$bTrouvez cet homme. Trouvez-le, et amenez-le devant la justice. Il doit être avec l\'élite du Syndicat. Peut-être aux Ruines de Strahnbrad, ou dans la vallée qui est au nord.",
    ["O"] = "Apporter la tête du baron Vardus au Magistrat Maleb à Southshore.",
    ["T"] = "La mort du baron Vardus",
  },
  [524] = {
    ["D"] = "A côté, les Gardes noirs détiennent quelques fermiers pris en otage. La Grande Exécutrice Darthalia suspectait qu\'un des stratèges de Stormwind se cache parmi eux. Apparemment, le pauvre militaire a été retrouvé mort dans les Maleterres la nuit dernière. Darthalia, toujours aux petits soins pour moi, a décidé de me laisser faire ce que je voulais avec les fermiers.$b$bJe pense qu\'il est temps de leur préparer une petite fête. Comme au bon vieux temps !$b$bPlacez ce baril infecté sur le tapis, dans leur pièce. Tournée générale bière pestilentielle !",
    ["O"] = "Placer le Baril infecté sur le tapis pour les fermiers détenus comme otages.",
    ["T"] = "L\'élixir d\'agonie",
  },
  [525] = {
    ["D"] = "J\'ai déchiffré la lettre, mais je ne sais pas quoi en faire. Vous l\'avez trouvée chez des voleurs dans les collines d\'Alterac ? C\'est étrange…$b$bVous devriez la montrer au Magistrat de Southshore. Bien que cette lettre soit sibylline, Southshore est la ville la plus proche d\'Alterac. Si un danger menace, alors il doit savoir.",
    ["O"] = "Apporter la Lettre décodée au Magistrat Maleb, à Southshore.",
    ["T"] = "D\'autres mystères",
  },
  [526] = {
    ["D"] = "Vous avez peut-être découvert un lingot de sancteforge dans ce coffre, mais ce n\'est pas suffisant pour forger quoi que ce soit de décent avec !$B$BVous devriez y retourner et en chercher davantage. Les murlocs, près de l\'épave, ont peut-être des lingots sur eux.",
    ["O"] = "Collecter 5 Lingots de sancteforge, puis retourner voir Glorin Steelbrow, dans les Paluns.",
    ["T"] = "Les lingots de sancteforge",
  },
  [527] = {
    ["D"] = "Mettez-vous au garde-à-vous lorsque vous vous adressez à moi, $c ! Je suis sous le commandement direct de Varimathras. Nous devons neutraliser la prolifération des humains jusqu\'à ce que nos apothicaires développent la Peste nouvelle.$b$bNos services de renseignements pensent que la ville d\'Hillsbrad serait vulnérable à une attaque. Votre première mission consiste à perturber leur approvisionnement et le stockage de nourriture.$b$bPartez à l\'ouest. Faites une incursion dans les fermes les plus au nord des Champs d\'Hillsbrad. Assurez-vous que leurs propriétaires, les fermiers Ray et Getz, soient exécutés.",
    ["O"] = "Tuer 6 Garçons de ferme, 6 Fermiers, le Fermier Ray et le Fermier Getz, puis retourner voir Darthalia à Moulin-de-Tarren.",
    ["T"] = "La bataille d\'Hillsbrad",
  },
  [528] = {
    ["D"] = "La nouvelle de l\'attaque sur les fermes situées au nord a semé la panique chez les gens de la ville. Merveilleux !$b$bNos Traqueurs noirs rapportent que les paysans fuient vers les champs au sud d\'Hillsbrad. Ces imbéciles pensent qu\'ils peuvent s’y cacher et ainsi échapper à leur sort.$b$bMontrez-leur qu\'ils ont tort, $N. Retournez aux Champs d\'Hillsbrad et traquez les paysans qui s\'y tapissent. Apportez-leur le chaos au nom de la Dame noire.$b$bRompez !",
    ["O"] = "Tuer 15 Paysans d\'Hillsbrad et faire votre rapport à Darthalia à Moulin-de-Tarren.",
    ["T"] = "La bataille d\'Hillsbrad",
  },
  [529] = {
    ["D"] = "Les humains d\'Hillsbrad ont fait montre d\'une résistance inattendue. Le problème semble être que les gens de la ville sont équipés par le forgeron local. Nous devons couper cet approvisionnement.$b$bVotre mission consiste à exécuter le forgeron Verringtan. Tuez également ses apprentis. Pendant que vous y serez, ramenez un chargement de fer prélevé dans leur stock. Cela se révélera pratique lorsque nous aurons achevé la conquête d\'Hillsbrad et que nous entreprendrons de raser Southshore.",
    ["O"] = "Tuer le Forgeron Verringtan et 4 Apprentis Forgerons d\'Hillsbrad.$b$bRécupérer un Chargement de fer, puis retourner voir Darthalia à Moulin-de-Tarren.",
    ["T"] = "La bataille d\'Hillsbrad",
  },
  [530] = {
    ["D"] = "Valdred, autrefois un bon ami à moi, est le meurtrier de ma femme. Bien que je sois incapable de ressentir de la peine, j\'aspire à la vengeance. Si vous tuez Valdred et me rapportez ses mains maudites, alors je vous récompenserai.$b$bLa dernière fois que j\'ai entendu parler de lui, il se trouvait au Mur de Greymane, dans le sud des Pins argentés, essayant de fuir vers le royaume de Gilnéas.",
    ["O"] = "Tuer Valdred Moray.$b$bApporter ses mains à Raleigh Andrean à Undercity.",
    ["T"] = "La vengeance d\'un mari",
  },
  [531] = {
    ["D"] = "Par la Lumière ! N\'apportez pas de choses comme ça dans le pavillon… c\'est insalubre, insupportable, in… in… insalubre !$b$bSortez-moi ça de là, voulez-vous ? Personne ne veut voir votre fichu petit trophée, vraiment !",
    ["O"] = "Amener la tête du vieux Noiraud à Vyrin Swiftwind au pavillon des pérégrins.",
    ["T"] = "La vengeance de Vyrin",
  },
  [532] = {
    ["D"] = "La ville d\'Hillsbrad est en pleine confusion et nous pensons qu\'elle va bientôt tomber. Nous devons cependant persister dans notre barbarie jusqu\'à ce que ces chiens d\'humains soient balayés des collines pour l\'éternité.$b$bMalgré leurs pertes écrasantes, les humains se rassemblent autour de leurs chefs et continuent à se battre. Nous devons éliminer leur chef, le maire Burnside, de même que le conseil municipal. Détruisez également leur très précieux document politique, la Proclamation d\'Hillsbrad. Et pendant que vous y êtes, volez le Registre municipal.",
    ["O"] = "Tuer le Magistrat Burnside et 5 Conseillers d\'Hillsbrad. Détruire la Proclamation d\'Hillsbrad. Voler le Registre municipal d\'Hillsbrad. Rendre compte à Darthalia à Moulin-de-Tarren.",
    ["T"] = "La bataille d\'Hillsbrad",
  },
  [533] = {
    ["D"] = "Nous avons enfin des nouvelles et des rumeurs, des indices sur l\'endroit où pourrait être Gol\'dir. Les humains l\'ont emmené à divers endroits, mais nos recherches aboutissent au grand camp du Syndicat au nord d\'ici.$b$bAllez-y, $n, et ramenez toutes les informations sur lesquelles vous pouvez mettre la main.",
    ["O"] = "Rapporter des informations sur la localisation de Gol\'dir pour Krusk, à Moulin-de-Tarren.",
    ["T"] = "L\'épée d\'HumbertInfiltration",
  },
  [534] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [535] = {
    ["D"] = "Eurrgh… Blessé et mourant, me voilà en face de l\'ennemi.$b$bVous me tuerez de toute façon, mais si vous m\'apportez de la bière de Southshore... Vous autres ne buvez pas, n\'est-ce pas ? Je vous dirai ce que vous voulez savoir.",
    ["O"] = "Apporter une Chope de bière de Southshore à Valik, au Camp du Syndicat.",
    ["T"] = "Valik",
  },
  [536] = {
    ["D"] = "Transféré de la garde de la cité de Stormwind à Southshore, une ville visiblement abandonnée… L\'odeur du port jour et nuit.$b$bSans parler du vieux Redpath… Veut que je m\'occupe des murlocs. Ha !$b$bJ\'ai du mal à m\'imaginer allant jusqu\'à l\'une des enclaves de ces grotesques poissons mutants qui grouillent le long du Rivage occidental. Si vous pouviez y aller et vous occuper d\'eux, j\'apprécierais.",
    ["O"] = "Tuer 10 Chasse-marées et 10 Oracles Aileron déchiré pour le lieutenant Farren Orinelle à Southshore.",
    ["T"] = "Le long de la côte",
  },
  [537] = {
    ["D"] = "La lettre que vous m\'avez apportée a été écrite par un être qui se nomme lui-même Nagaz, un membre de la Veille d\'Argus. Je ne sais pas ce qu\'est cette Veille d\'Argus, mais si elle est alliée au Syndicat, alors elle constitue une menace.$b$bJe veux que vous trouviez Nagaz et les Mages de l\'ombre d\'Argus mentionnés dans cette lettre. Je veux que vous les trouviez et que vous les tuiez.$b$bCherchez dans les Hauteurs, là où le Syndicat est le plus fort.$b$bAvec un peu de chance, vous y trouverez ces représentants de la Veille d\'Argus.",
    ["O"] = "Tuer 4 Mages de l\'ombre d\'Argus.$b$bApporter la Tête de Nagaz au Magistrat Maleb à Southshore.",
    ["T"] = "Le conseil noir",
  },
  [538] = {
    ["D"] = "$N. Si un long voyage ne vous dérange pas, alors j\'ai une tâche à vous confier, pour aider à nouveau Stormwind à étoffer sa bibliothèque.$b$bL\'ancien royaume d\'Alterac s\'est effondré après la Grande Guerre. C\'est malheureux que la ville soit à présent en ruine, mais il est encore plus tragique que de grands savoirs aient disparu ! Nous devons retrouver ces importantes collections de livres.$b$bSouthshore est la ville la plus proche des Ruines d\'Alterac. Rendez-vous-y et parlez à l\'Erudit Dibbs. Il peut vous guider vers les ruines, et tous les livres que vous pourriez trouver.",
    ["O"] = "Parler à l\'Erudit Dibbs à Southshore.",
    ["T"] = "Austrivage",
  },
  [539] = {
    ["D"] = "Le Registre municipal d\'Hillsbrad indique que la Mine d\'Azureload est sous le contrôle de l\'Alliance. De plus, c’est une source importante de minerai de fer destinée aux armureries de l\'Alliance.$b$bMême si nous avons porté un coup décisif à la ville d\'Hillsbrad, l\'Alliance soutient et protège toujours la Mine d\'Azureload.$b$bElle se trouve au sud d\'Hillsbrad. Allez-y et tuez le contremaître, un humain du nom de Bonds. Tuez également ses Mineurs. Cela devrait envoyer un message fort à l\'Alliance.",
    ["O"] = "Tuer le Contremaître Bonds et 10 Mineurs d\'Hillsbrad, puis rendre compte à Darthalia, à Moulin-de-Tarren.",
    ["T"] = "La bataille d\'Hillsbrad",
  },
  [540] = {
    ["D"] = "Ces ogres répugnants résident à présent dans les ruines d\'Alterac, et je frémis à l\'idée de ce qu\'ils peuvent faire des précieux livres qui s\'y trouvent.$b$bPénétrez dans les ruines d\'Alterac et récupérez des tomes sur les ogres. Prenez tout ce que vous trouverez, mais cherchez également le livre suivant : « Le Bras de Gri\'lek ». C’est un recueil d’anciennes légendes trolls que je dois lire. Les ruines d\'Alterac sont le dernier endroit où se trouve une copie intacte de cet ouvrage.",
    ["O"] = "Apporter 5 Tomes retrouvés, ainsi que le Livre de cuir usé « Le Bras de Gri\'lek » au Maître des traditions Dibbs, à Southshore.",
    ["T"] = "L\'entretien des connaissances",
  },
  [541] = {
    ["D"] = "Des nains aidant des humains ? Que ces affreuses petites bêtes soient maudites.$b$bNous allons tester la force de cette prétendue Alliance. Nous verrons à quel point ces cogneurs de chevilles sont déterminés à aider les humains une fois que nous serons passés. Nous savons précisément où se trouve leur forteresse dans ce territoire.$b$bIls ont établi une caserne dans la partie sud-est d\'Hillsbrad, le long du Mur de Thoradin. La mission est d\'attaquer cette caserne, baptisée Dun Garok, $c. Le capitaine Ironhill, leur chef, doit mourir.",
    ["O"] = "Aller à Dun Garok et tuer 10 Montagnards, 8 Fusiliers, 4 Prêtres et le Capitaine Ironhill, puis rendre compte à Darthalia, à Moulin-de-Tarren.",
    ["T"] = "La bataille d\'Hillsbrad",
  },
  [542] = {
    ["D"] = "J\'ai besoin de temps pour étudier le livre « l\'Arme de Gri\'lek » que vous m\'avez apporté, mais je suis sûr que Milton a hâte d\'enrichir la collection des livres de Stormwind avec ceux que vous avez retrouvés aux Ruines d\'Alterac.$b$bLes voici, empaquetés et catalogués. Amenez-les à Milton Sheaf à Stormwind et je suis certain qu\'il sera reconnaissant.",
    ["O"] = "Apporter les Tomes d\'Alterac à Milton Sheaf à Stormwind.",
    ["T"] = "Les livres de Milton",
  },
  [543] = {
    ["D"] = "La Tiare de Perenolde est un ancien héritage des \"nobles\" de Perenolde, la famille régnante d\'Alterac, avant que ce royaume ne tombe en ruine. On dit qu\'elle a été réalisée par des maîtres bijoutiers à Ironforge, et qu\'elle est ornée des émeraudes les plus pures.$b$bQuand Alterac a été détruite, la Tiare fut perdue. Mais les rumeurs disent qu\'un ogre-mage, Grel\'borg l\'Avare, l\'a trouvée, et qu\'il fouille à présent les Ruines d\'Alterac à la recherche d\'autres trésors.$b$bNous aimerions que vous récupériez la Tiare pour nous.",
    ["O"] = "Apporter la Tiare de Perenolde à Remington Ridgewell, à Stormwind.",
    ["T"] = "La tiare de Perenolde",
  },
  [544] = {
    ["D"] = "Je suis venu à Moulin-de-Tarren pour effectuer des recherches, mais je dois à présent résoudre une crise. Voyez-vous, quatre Réprouvés se sont enfuis d\'Undercity il y a quelques mois. Ils ont trahi leurs semblables, mais pire encore : ils ont volé la Dame noire.$b$bCes voleurs ont forcé un coffre et se sont emparés de quatre artefacts, des objets dont nos apothicaires ont besoin pour leurs études. Dame Sylvanas veut récupérer ces artefacts.$b$bLes voleurs ont fui à Dalaran, et là-bas les sorciers les ont placés en quarantaine dans le Camp d\'internement de Lordamere.",
    ["O"] = "Trouver les traîtres et récupérer les artefacts, puis retourner voir le magus Voidglare à Moulin-de-Tarren.",
    ["T"] = "L\'invasion de la prison",
  },
  [545] = {
    ["D"] = "Les Sorciers de Dalaran ont construit un vaste dôme magique autour du cœur de leur cité. Certains croient qu\'il s\'agissait de se protéger des violences de cette époque, d\'autres pensent qu\'ils recherchaient simplement la solitude, pour étudier et comploter.$b$bJe ne m\'intéresse pas à la raison. En revanche, ce qui m\'intéresse, c’est que la périphérie de la ville en ruine, autour du dôme, puisse abriter des trésors magiques.$b$bMais les Sorciers et leurs Esclaves élémentaires patrouillent ce secteur.$b$bChassez-les donc, et revenez lorsque leur nombre sera conséquemment réduit.",
    ["O"] = "Tuer 6 Invocateurs de Dalaran et 12 Esclaves élémentaires, puis retourner voir le magus Voidglare à Moulin-de-Tarren.",
    ["T"] = "Les patrouilles de Dalaran",
  },
  [546] = {
    ["D"] = "J\'ai entendu dire que vous étiez $grecruté:recrutée; par Darthalia pour mener l\'offensive contre les humains d\'Hillsbrad. Je vous envie tellement…$b$bTandis que vous vous amuserez – à tuer des humains, à piller, à terroriser les innocents – je resterai coincé ici, à Moulin-de-Tarren, à monter la garde et à me morfondre.$b$bPeut-être prendrez-vous en pitié un vieux Garde noir comme moi ? Vous remarquez que je fais la collection de crânes. Et vous allez combattre des humains pendant quelque temps, je crois. Au gré de votre longue bataille, vous récolterez bien quelques crânes, apportez-moi-en 30 et je vous rendrai la pareille d\'une belle façon.",
    ["O"] = "Le Garde noir Samsa du Moulin-de-Tarren veut 30 Crânes d\'humains d\'Hillsbrad.",
    ["T"] = "Souvenirs de morts",
  },
  [547] = {
    ["D"] = "La Grande Exécutrice Darthalia m\'a envoyé surveiller Dun Garok de loin. J\'ai établi mon camp juste au-delà de leur champ de vision. Mais ce matin, pour faire bouillir de l\'eau, je suis allé chercher du bois.$b$bQuand je suis revenu au camp, j\'ai découvert que mon équipement avait disparu.$b$bApparamment, des éclaireurs nains de Dun Garok sont passés par là et repartis avec mon matériel. J\'ai besoin de mon épée ! Si vous pouviez la retrouver et me la rendre, je serais tellement reconnaissant. Il y a tellement de nains par ici. Ça va être très difficile de retrouver le sale petit voleur qui l\'a dérobée.",
    ["O"] = "Retrouver l\'Epée du Garde noir Humbert et la lui retourner à Moulin-de-Tarren.",
    ["T"] = "Epée d\'Humbert",
  },
  [548] = {
    ["D"] = "This will start the Bloodstone Pendant quest line...<NYI>",
    ["O"] = "",
    ["T"] = "<NYI> <TXT> Bloodstone Pendant",
  },
  [549] = {
    ["D"] = "Au nom de l\'autorité de Dame Sylvanas, tous les membres de l\'organisation connue sous le nom du Syndicat sont recherchés. Ceux conduits à la garde ou faits prisonniers seront sommairement exécutés. Nous savons qu’actuellement ils se rassemblent dans les ruines du Donjon de Durnholde, au sud-est de Moulin-de-Tarren.$b$bUne récompense est offerte à quiconque apporte la preuve de ses actions à la Grande Exécutrice Darthalia.",
    ["O"] = "Tuer 10 Voleurs et 10 Gardiens du Syndicat, puis aller voir la Grande Exécutrice Darthalia à Moulin-de-Tarren pour votre récompense.",
    ["T"] = "ON RECHERCHE : Personnel du Syndicat",
  },
  [550] = {
    ["D"] = "Comme vous avez combattu avec valeur à la bataille d\'Hillsbrad, j\'ai écrit cette recommandation exaltant vos prouesses au combat, pour que le haut commandement les apprécie.$b$bApportez cette Recommandation scellée à Varimathras à Undercity. Allez avec fierté, $c.",
    ["O"] = "Apporter la Recommandation scellée à Varimathras à Undercity.",
    ["T"] = "La bataille d\'Hillsbrad",
  },
  [551] = {
    ["D"] = "Ce parchemin est sous l’effet d’un sort qui le rend indéchiffrable. Mais un mot peut être lu, en haut du papier :$b$b$b  Nagaz$b$b",
    ["O"] = "Apporter le Parchemin ensorcelé à l\'Erudit Dibbs à Southshore.",
    ["T"] = "Le Parchemin ensorcelé",
  },
  [552] = {
    ["D"] = "Maître Helcular avait presque achevé sa transformation en liche lorsqu\'une bande d\'humains furieux a découvert son repaire dans les collines et l\'a tué. Pour rendre les choses encore pires, ils ont enterré son corps au cimetière de Southshore.$b$bUn tel manque de respect envers un si grand sorcier n\'est pas acceptable. Etant son apprenti, je veillerai à ce que la pluie de la vengeance tombe drue sur la ville de Southshore.$b$bAvant tout, nous avons besoin du Bâtonnet d\'Helcular. Les yétis ont atteint son repaire. L\'une de ces bêtes l\'a sans doute emporté.",
    ["O"] = "Reprendre le Bâtonnet d\'Helcular aux yétis et la ramener au novice Thaivand à Moulin-de-Tarren.",
    ["T"] = "La vengeance d\'Helcular",
  },
  [553] = {
    ["D"] = "Tenez, prenez le Bâtonnet d\'Helcular. Vous en aurez besoin.$b$bHelcular a fabriqué 3 bûchers cérémoniels comme composants de son sort le plus puissant. La Flamme d\'Azel et la Flamme de Veraz brûlent dans l\'une des cavernes des contreforts. La Flamme d\'Uzel éclaire une cave située plus haut et plus au nord dans les montagnes.$b$bUne fois le Bâtonnet d\'Helcular chargé avec ces trois flammes, il faut l’enfoncer dans la tombe d\'Helcular située à Southshore. La vengeance sera nôtre.",
    ["O"] = "Charger le Bâtonnet d\'Helcular des pouvoirs de la Flamme d\'Azel, de la Flamme de Veraz et de la Flamme d\'Uzel.$b$bEnfoncez le Bâtonnet chargé dans la tombe d\'Helcular à Southshore.",
    ["T"] = "La vengeance d\'Helcular",
  },
  [554] = {
    ["D"] = "Le Prospecteur Stormpike est un maître du décodage. Si vous lui amenez le parchemin que vous avez trouvé, alors, avec un peu de chance, il peut en saisir la signification. Stormpike étudie en ce moment aux quartiers généraux des Prospecteurs à Ironforge.$b$bEt quand vous le verrez dites-lui que vous m\'avez parlé. Je lui ai envoyé beaucoup de choses dernièrement.",
    ["O"] = "Apporter le Parchemin ensorcelé au Prospecteur Stormpike à Ironforge.",
    ["T"] = "Stormpike décode",
  },
  [555] = {
    ["D"] = "S\'il y a une chose que vous apprendrez à propos de Southshore, c\'est que nous avons l\'une des meilleures cuisines au nord de Stormwind !$b$bPrenez ma recette secrète de bisque de tortue, par exemple. J\'en ai connu qui ont fait le voyage depuis Darkshire juste pour en déguster une coupe. Mais il y a bien longtemps que je n’en ai pas fait. J\'allais au-delà de Dalaran, jusqu\'au lac de Lordamere, mais c\'est trop dangereux à présent. Si vous m\'apportiez de la Viande de tortue, venant du nord, et des Epices apaisantes, je vous mitonnerai un bon petit plat.",
    ["O"] = "Apporter 10 morceaux de Viande de tortue et des Epices apaisantes au Chef Jessen à Southshore.",
    ["T"] = "Bisque de tortue apaisante",
  },
  [556] = {
    ["D"] = "Un sérieux atout des sorciers de Dalaran est leur familiarité avec les élémentaires, ces créatures puissantes venant d\'autres dimensions. Les sorciers utilisent des Runes gravées pour contrôler ces élémentaires. Le magus Voidglare aimerait étudier certaines de ces runes.$B$BPénétrez sur le territoire des sorciers de Dalaran, à l\'ouest d\'Hillsbrad, et trouvez-en.$B$BIl se peut que vous les trouviez sur n\'importe quel citoyen de Dalaran, mais je suggère que vous vous les procuriez au camp d\'internement. C\'est un endroit moins dangereux que Dalaran.",
    ["O"] = "Apporter 10 Runes gravées au Conservateur Bel\'varil à Moulin-de-Tarren.",
    ["T"] = "Les Runes gravées",
  },
  [557] = {
    ["D"] = "Le magus sera très occupé à étudier ces runes gravées que vous avez apportées, mais je sais que tôt ou tard il voudra des échantillons de leurs contreparties : des brassards de liaison. Ce sont des brassards qui, passés aux poignets des élémentaires, les lient au service des mages.$b$bChassez des élémentaires dans les ruines de Dalaran et récupérez leurs brassards de liaison de terre. Le magus en sera très satisfait.",
    ["O"] = "Apporter 4 Brassards de liaison de terre au Conservateur Bel\'varil à Moulin-de-Tarren.",
    ["T"] = "Les Bracelets de liaison",
  },
  [558] = {
    ["D"] = "On dit que dame Jaina Portvaillant, à Theramore, est l\'une des plus grands héroïnes de l\'Alliance de tous les temps. Quand je serai grand, je veux aussi être un héros de l\'Alliance !$B$BVous connaissez Jaina, $N ? Vous pouvez me faire une faveur, s\'il vous plaît ? Vous pouvez m\'avoir un autographe de dame Jaina ? Je sais pas comment lui demander, et c\'est sans doute une personne bien trop occupée pour s\'occuper de quelqu\'un comme moi... Mais pas vous ! Vous êtes $gun aventurier:une aventurière;, comme elle ! Je parie qu\'elle vous donnerait facilement un autographe !$B$BS\'il vous plaît ?",
    ["O"] = "Allez à Theramore pour tenter d\'obtenir un autographe de dame Jaina Portvaillant pour votre protégé.",
    ["T"] = "L\'autographe de Jaina",
  },
  [559] = {
    ["D"] = "Le croirez-vous, $n ? Redpath n\'était pas satisfait, en dépit de tous les murlocs que nous avons tués. Ah ! ne soyez pas modeste, vous y avez participé. À présent il veut que je lui apporte la preuve de leur mort, en allant chercher les têtes refroidies de ces misérables murlocs, pour les soumettre à sa validation.",
    ["O"] = "Collecter 10 Têtes de murlocs sur les murlocs du Rivage occidental pour le lieutenant Farren Orinelle à Southshore.",
    ["T"] = "La preuve de Farren",
  },
  [560] = {
    ["D"] = "Vous savez, $n, je préférerais ne pas toucher à ces têtes… Puisque vous avez déjà les mains qui sentent le poisson, cela vous dérangerait-il de les livrer à Redpath pour moi ?",
    ["O"] = "Livrer le Sac de têtes de murlocs au maréchal Redpath à Southshore.",
    ["T"] = "La preuve de Farren",
  },
  [561] = {
    ["D"] = "Veuillez informer le lieutenant Orinelle que j\'aimerais qu\'il concentre ses efforts sur les nagas qui infestent le Rivage oriental. Quand il aura obtenu des résultats, dites-lui qu\'il vienne en personne m\'en rendre compte.",
    ["O"] = "Parler au Lieutenant Farren Orinelle à Southshore.",
    ["T"] = "La preuve de Farren",
  },
  [562] = {
    ["D"] = "Mon ancien mentor à la garde de la cité de Stormwind a envoyé un message me disant que le Haut Commandement de l\'Alliance commence à s\'inquiéter du problème posé par les nagas. D\'après ce qu\'il a entendu, il y a des chances de promotion, ou d\'une bonne affectation, pour quiconque se montrera valeureux dans la résolution de ce problème.$b$bIl y a juste quelque chose qui ne va pas... Je ne peux pas m\'occuper des nagas, $n. Oh bien sûr, je pourrais disperser une querelle d\'ivrognes, ou alpaguer un clochard vindicatif, mais les nagas c’est trop pour moi. Vous devez m\'aider, $n.",
    ["O"] = "Tuer 10 Chasseurs du Rivage et 10 Sirènes Daguéchine pour le lieutenant Farren à Southshore.",
    ["T"] = "Stormwind me voilà",
  },
  [563] = {
    ["D"] = "Que mon rapport soit lu ne devrait pas poser de problème. Le nouveau capitaine de la Garde, le major Samuelson, était mon mentor lorsque j\'étais posté à Stormwind. Cela devrait lui donner suffisamment d\'arguments pour que j\'y sois à nouveau transféré. Je vais avoir besoin de vous pour porter mon rapport. Cependant, je n\'aimerais pas que ce cher Redpath comprenne.",
    ["O"] = "Remettre le Rapport de Farren au major Samuelson au Donjon de Stormwind.",
    ["T"] = "Nouvelle affectation",
  },
  [564] = {
    ["D"] = "Y\'a rien qui m\'hérisse plus que d\'perdre ces beaux ch\'vaux, là. Et croyez-moi, on en a perdu pas mal, entre ces sales morts-vivants, c\'fichu gang du Syndicat, et toutes les aut\' crapules bonnes à rien qui traînent dans l\'secteur.$b$bMais j\'jure sur la tombe d\'mon vieux grand-père qu\'y a pas d\'danger plus grand pour les ch\'vaux que ces lions d\'montagne en haut à Alterac.$b$bIls s\'ramènent toujours des collines et m\'rendent la vie difficile !$b$bVous croyez être $gun héros:une héroïne; ? Allez-y et faites quek\'chose pour y r\'médier.",
    ["O"] = "Darren Malvew de Southshore veut que vous tuiez 8 Lions des montagnes et 10 Lions des montagnes massifs.",
    ["T"] = "Un problème très coûteux",
  },
  [565] = {
    ["D"] = "Moi, le Grand Bartolo, je confectionnerai pour vous ma fameuse cape en fourrure de yéti, célèbre dans le monde entier, et qui fait la renommée de l\'Alliance !$b$bJe vous mets au défi de parcourir le monde pour trouver meilleure cape. Oh ! attendez, qu\'est-ce que vous dites, $gpetit:petite; $N ? \"Bartolo, il n\'existe pas de meilleure cape !\" Oui, oui, je sais, j\'ai entendu ça des milliers de fois déjà.$b$bBartolo demande une pelote de laine, une cape d\'homme des collines confectionnée par un habile tanneur, du fil fin et bien sûr de la fourrure de yéti. Apportez-moi tout cela et vous serez $gun client satisfait:une cliente satisfaite;.",
    ["O"] = "Apporter à Bartolo Ginsetti de Southshore une Pelote de laine, une Cape d\'homme des collines, du Fil fin et 10 Touffes de fourrure de yéti.",
    ["T"] = "La cape en fourrure de yéti de Bartolo",
  },
  [566] = {
    ["D"] = "Par décret de la grande exécutrice Darthalia, l\'humain connu sous le nom de baron Vardus est recherché pour crime contre les Réprouvés. Nous savons que Vardus est un membre haut placé de l\'organisation dite le Syndicat.$b$bIl a été vu pour la dernière fois dans les Hauteurs des montagnes d\'Alterac, au nord de Moulin-de-Tarren.$b$bIl doit être ramené mort. Apportez une preuve de son trépas à la grande exécutrice Darthalia pour obtenir votre récompense.",
    ["O"] = "Tuer le baron Vardus et apporter sa tête à la Grande exécutrice Darthalia, au Moulin-de-Tarren.",
    ["T"] = "ON RECHERCHE : baron Vardus",
  },
  [567] = {
    ["D"] = "Dangereux !$b$bLes humains d\'Hillsbrad dont les noms suivent ont été déclarés dangereux et leurs têtes sont mises à prix par la Grande Exécutrice Darthalia :$b$bCommis Horrace Whitesteed. Recherché pour le meurtre du Garde noir Toma.$b$bCitoyen Wilkes. Recherché pour le meurtre de l\'Apothicaire Eli.$b$bMineur Hackett. Recherché pour le meurtre du Traqueur noir Fry.$b$bKalaba la fermière. Recherchée pour détournement de marchandises à Undercity.$b$bChacun de ces ennemis se cache et sera difficile à débusquer. Une récompense sera remise à la preuve de leur mort.",
    ["O"] = "La Grande Exécutrice Darthalia de Moulin-de-Tarren offre une prime pour la mort du Commis Horrace Whitesteed, du Citoyen Wilkes, du Mineur Hackett et de Kalaba la fermière.",
    ["T"] = "Dangereux !",
  },
  [568] = {
    ["D"] = "Throm\'ka, $c !$b$bVotre arrivée à Grom\'gol tombe à point nommé. Je commande ce campement camp ici dans la jungle, au nom du Chef de guerre. Je suis tenu sur l\'honneur d\'assurer la sécurité de tous les membres de la Horde. Notre mission consistant à maintenir une chaîne d\'approvisionnement jusqu’à Stonard est entravée par certaines espèces locales.$b$bJe vous charge de réduire la population des raptors autour de Grom\'gol. Une fois que vous aurez accompli des progrès significatifs, revenez me voir pour que je vous assigne une nouvelle mission.",
    ["O"] = "Le commandant Aggro\'gosh du campement Grom\'gol veut que vous tuiez 15 Raptors flagellants.",
    ["T"] = "La défense de Grom\'gol",
  },
  [569] = {
    ["D"] = "La première fois que nous sommes arrivés dans cette jungle, nous n\'avions pas l\'intention de déclarer la guerre aux tribus locales. Notre mission d’approvisionnement de Stonard est de la plus haute priorité. Mais des complications ont surgi. Un groupe d\'ogres occupe les Ruines de Mizjah au sud-est de Grom\'gol. Malgré nos avertissements, ils continuent à tendre des embuscades à nos caravanes.$b$bJe vous ordonne de vous occuper de ces ogres peu coopératifs. Allez vers les ruines et délogez-les de la région. Au moins, cela enverra un message fort.",
    ["O"] = "Le commandant Aggro\'gosh du campement Grom\'gol veut que vous tuiez 10 Brutes et 5 Sorciers ogres.",
    ["T"] = "La défense de Grom\'gol",
  },
  [570] = {
    ["D"] = "Le pouvoir de la magie de la jungle doit être compris et acquis par la Horde. Mes rituels se sont montrés efficaces jusque-là. Je peux lancer des enchantements très puissants. Pour vous, brave $c, je vais réaliser un objet très spécial.$b$bRamenez-moi quelques griffes de panthère Ombregueule et un croc de tigresse. Mais pas n\'importe quel croc. Il doit être dans un état parfait.$b$bCes bêtes vivent cachées dans la jungle, mais vous en trouverez pas loin, juste de l\'autre côté de la rivière, depuis les ruines de Mizjah au sud-est.",
    ["O"] = "Le prophète Mok\'thardin du campement Grom\'gol veut que vous trouviez 8 Griffes de panthère Ombregueule et 1 Croc de tigresse en parfait état.",
    ["T"] = "Enchantement de Mok\'thardin",
  },
  [571] = {
    ["D"] = "Ce dont j\'ai besoin va être très difficile à obtenir. Pour tenir ensemble ces plumes, griffes et ce croc parfait, il me faut une longe magique. Seul un tendon de gorille fera l\'affaire.$b$bVous ne pourrez en trouver un que sur un ancien gorille de Valbrume. On sait que ces animaux fourragent à l\'est de Baie-du-Butin.$b$bLe tendon doit être parfait. Il se peut que vous deviez en tuer plus d\'un pour trouver exactement ce dont j\'ai besoin, mais cela en vaudra la peine.",
    ["O"] = "Apporter un Tendon d\'Ancien gorille de Valbrume au Prophète Mok\'thardin à Grom\'gol.",
    ["T"] = "Enchantement de Mok\'thardin",
  },
  [572] = {
    ["D"] = "Les matériaux que vous avez collectés sur les félins de la vallée n\'étaient que les premiers ingrédients d\'un puissant objet enchanté. Ces babioles de la jungle combinées à mon rituel chamanique vous fourniront un outil très puissant, au service de la Horde et du Chef de guerre.$b$bMais pour l\'heure vous devez rassembler pour moi quelques plumes de Traqueurs de la jungle. Ces créatures portent des généralement des plumes sur leurs bras. Vous trouverez un groupe de raptors au sud de Grom\'gol, non loin de la grande arène.",
    ["O"] = "Amener 10 Plumes de Traqueurs de la jungle au Prophète Mok\'thardin au campement Grom\'gol.",
    ["T"] = "Enchantement de Mok\'thardin",
  },
  [573] = {
    ["D"] = "Il ne manque plus qu\'un dernier réactif pour compléter l\'enchantement.$b$bAu sud, vers la côte occidentale, loin au-delà des falaises, coule une source sacrée. Elle est à l\'origine d\'un conflit parmi les érudits mystiques et religieux. Chaque race d\'Azeroth proclame que cette source a été rendue divine pour ses propres populations.$b$bLa dernière demande que je vous fais : apportez-moi de l\'Eau de source sacrée. Mais attention. Les nagas ont envoyé des explorateurs qui progressent vers la source. Vous devrez également vous en occuper.",
    ["O"] = "Le Prophète Mok\'thardin de Grom\'gol a besoin d\'Eau de source sacrée. Il veut que vous tuiez aussi 10 Explorateurs nagas.",
    ["T"] = "Enchantement de Mok\'thardin",
  },
  [574] = {
    ["D"] = "Maintenant que de nombreux hommes de troupe sont morts, vous devez combattre ses forces hautement entraînées. Lorsqu\'ils ne sont pas dans la nature à massacrer nos soldats rebelles, on peut les trouver au fond de la Base de Kurzen, dans un endroit qu\'ils appellent la Réserve.$b$bJe connais personnellement beaucoup des hommes de Kurzen, et sais qu\'ils sont entraînés, loyaux et cruels. Ce n\'est pas une tâche facile que je vous confie là, $n.$b$bAprès les avoir battus, rendez compte au lieutenant Doren, mon officier commandant. Il voudra votre rapport en priorité.",
    ["O"] = "Tuer 10 Commandos et 6 Réducteurs de têtes de Kurzen. Rendre compte au Lieutenant Doren au Camp rebelle.",
    ["T"] = "Forces spéciales",
  },
  [575] = {
    ["D"] = "“Le meilleur artisan du cuir des mers du Sud et des deux mondes, Ancien et Nouveau”. C’est comme ça qu’on m’appelle. Moi ! Drizzlik ! Le meilleur artisan… Hem. Bon.$B$BMes objets en cuir sont réputés dans le monde entier pour leur perfection – le vrai choix des connaisseurs !$B$BJe viens de recevoir une commande du directeur Riddlevox du Syndicat des Artisans. Il veut une douzaine de ses bottes préférées, le modèle Excelsior (notre meilleure vente, soit dit en passant.)$B$BPour mettre en place la forme, j’ai besoin de peaux de crocilisques. Vous trouverez les meilleurs spécimens le long des rivières, dans le nord de Strangleronce.",
    ["O"] = "Apporter 2 Grandes peaux de crocilisque des rivières à Drizzlik, à Baie-du-Butin.",
    ["T"] = "L\'offre et la demande",
  },
  [576] = {
    ["D"] = "Eh, qui va là ?$B$BArgh, je ne vaux rien sans mon œil enchanté spécial. J\'aurais pas dû perdre mes yeux à la guerre pour commencer ! Mais par chance, nous avons débarqué à Menethil et un sorcier de passage a enchanté un œil de verre spécial qui me permet de voir à nouveau.$B$BLa nuit dernière, je me suis retrouvé agressé par des boucaniers de la Voile sanglante alors que je rentrais. Ils ont pris mon œil, oh oui ! On dirait que ces types se sont vite adaptés à Baie-du-Butin.$B$BVous voulez aider un vieux loup de mer à récupérer son œil ?",
    ["O"] = "Dizzy le Borgne de Baie-du-Butin veut que vous récupériez son œil chez les boucaniers de la Voile sanglante.",
    ["T"] = "Gardez un œil dehors",
  },
  [577] = {
    ["D"] = "Bien, la forme des bottes est finalisée et elles se présentent bien, mais les peaux que vous avez amenées ne suffiront pas pour l\'intérieur.$B$B\"Alors que faire, Drizzlik ?\"... La question vous brûle sûrement les lèvres, et n\'est-ce pas heureux que vous parliez à Drizzlik justement, parce qu\'il va vous dire ce que l\'on va faire !$B$BOn ne pourra jamais vraiment voir l\'intérieur, alors on va juste récupérer de la peau plus épaisse et moins onéreuse sur les crocos autour du lac Nazfériti pour ça.",
    ["O"] = "Apporter 5 Peaux de Gueule d\'acier à Drizzlik, à Baie-du-Butin.",
    ["T"] = "Un peu d\'assemblage est nécessaire",
  },
  [578] = {
    ["D"] = "Si un mage gobelin et des élémentaires d\'eau hantent effectivement l\'île, cela peut vouloir dire que la Pierre des marées peut être mienne !$b$bJe veux que vous localisiez cette île hantée… Découvrez ce qui s\'y passe. Oh ! et si vous voulez en savoir plus sur la Pierre des marées, donnez ce titre à la bibliothèque de Stormwind, ils devraient être capables de vous trouver une copie du livre dans lequel j\'ai eu vent pour la première fois de cette légende.$b$bSerait-ce possible que ce Gazban ait vraiment découvert la Pierre... ?",
    ["O"] = "Localiser l\'Ile hantée pour le baron Revilgaz de Baie-du-Butin.",
    ["T"] = "La Pierre des marées",
  },
  [579] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La Bibliothèque de Stormwind",
  },
  [580] = {
    ["D"] = "On s\'hum\'ctait l\'gosier féroc\'ment, au large des Hauts-Surplombs, c\' bonnes vieilles Hinterlands dans l\' lointain. Cru qu\'les mers allaient tous nous bouffer.$b$bL\'vieux Slim en c\'nait un bout en bravoure à 40°. L\'cap\'tain\' \'vait une caiss\' de porto Pupellyverbos réquis\'tionnée à un corsair\' s\'rendan\' ta Stormwind.$b$b\'Lors quand c\'t\' orage prè\' des Hinterlands s\'est pointé, j\'ai fait mon enquête. Une grosse vague a visité l\'endroit et a invité les bouteilles à la gaude. L\'capt\'aine va m\' faire tâter du fouet si vous m\'aidez pas à les ram\'ner.",
    ["O"] = "Whiskey Slim de Baie-du-Butin veut que vous lui rameniez les bouteilles de porto Pupellyverbos qu\'il a perdues.",
    ["T"] = "La bibine perdue de Whiskey Slim",
  },
  [581] = {
    ["D"] = "Un sort, c\'est ça. Un sort lancé sur le plus jeune fils de mon chef, Yenniku. Comme la coutume l\'exige chez notre peuple, on l\'a laissé chez les trolls Gurubashi de Zul\'Gurub, mais après que ma tribu, la tribu Darkspear, a quitté les lieux en compagnie de l\'orc Thrall, nous n\'avons plus revu ce pauvre Yenniku. On m\'a envoyé à Strangleronce depuis la lointaine Orgrimmar, pour rechercher le fils de mon chef.$b$bEt je crois que la tribu des Bloodscalp le détient.$b$bSi vous vouliez m\'aider, partez à la chasse aux Bloodscalp. Vous pouvez les trouver au nord. Prenez leurs défenses et revenez me voir.",
    ["O"] = "Amener 9 Défenses de Bloodscalp à Nimboya au campement Grom\'gol.",
    ["T"] = "La chasse pour Yenniku",
  },
  [582] = {
    ["D"] = "Nous devons découvrir si Yenniku est mort.$b$bTuez les Chasseurs de têtes Bloodscalp et prenez leurs Têtes réduites. Vous devez en réunir beaucoup, énormément, pour que je puisse m\'assurer que celle de Yenniku n\'est pas parmi elles.$b$bEt si Yenniku a été tué par les Bloodscalp, que l\'esprit de leur chef Gan\'zulah soit maudit pour les 600 ans à venir !$b$bPartez, $n. Vous pourrez trouver de nombreux Chasseurs de têtes aux ruines de Zuuldaia et à celles de Zul\'Kunda, le long de la côte au nord.",
    ["O"] = "Apporter 20 Têtes réduites à Nimboya au campement Grom\'gol.",
    ["T"] = "La chasse aux têtes",
  },
  [583] = {
    ["D"] = "Bienvenue à Strangleronce !$b$bPeut-être n\'êtes-vous pas au courant, mais ce nain, là, n\'est autre qu\'Hemet Nesingwary, le seul et l\'unique, héros de guerre renommé de l\'Alliance, et maître de la chasse au gros gibier. Il n\'est pas homme à accueillir facilement les $gétrangers:étrangères; mais vous semblez avoir vécu quelques péripéties déjà, $c.$b$bEtant un vétéran de bien nombreuses batailles, Maître Nesingwary a un point faible pour ses collègues.$b$bAllez lui parler. Il vous donnera peut-être quelques conseils de chasse.",
    ["O"] = "Parler à Hemet Nesingwary.",
    ["T"] = "Bienvenue dans la jungle",
  },
  [584] = {
    ["D"] = "La tête de Yenniku n’était pas parmi celles que vous avez apportées, et je remercie le Grand Serpent pour cela. Mais nous n\'avons pas progressé dans nos recherches pour le retrouver.$b$bNous devons connaître la vérité, et je crains que seuls le Chef du clan Bloodscalp et son Sorcier la connaissent. Ce sont les ennemis de la tribu Darkspear et ils n\'avoueront rien d\'eux-mêmes, aussi nous devons extraire la vérité de leurs lèvres mortes. $b$bApportez-moi les têtes de Gan\'zulah et de Nezzliok le Redoutable, et placez-les dans ce chaudron.$b$bIls sont loin dans les profondeurs de Zul\'Kunda. Apportez-moi leurs têtes... nous les ferons alors parler.",
    ["O"] = "Apporter la Tête de Gan\'zulah et la Tête de Nezzliok dans le Chaudron bouillonnant au campement Grom\'gol.",
    ["T"] = "Les têtes du clan Bloodscalp",
  },
  [585] = {
    ["D"] = "<La tête de Nezzliok parle.>$b$bC\'est vrai. Je sais où l\'enfant Darkspear a été emmené. Vous m\'avez battu et avez capturé mon esprit, mais je ne dirai rien sans apaisement !$b$bLes Bloodscalp sont en guerre contre les Skullsplitter. Pour obtenir mes faveurs, volez aux Skullsplitter 3 de leurs crânes trophées et apportez-les ici avec nous, pour que mon esprit ait des esclaves dans le Néant. Un crâne doit provenir de la collection de trophées de Balia\'mah, un autre de celle de Ziata\'jai, et le dernier de celle de Zul\'Mamwe.",
    ["O"] = "Apporter un Trophée de Balia\'mah, un de Ziata\'jai et un de Zul\'Mamwe dans le Chaudron bouillonnant au campement Grom\'gol.",
    ["T"] = "En parlant avec Nezzliok",
  },
  [586] = {
    ["D"] = "<La tête de Gan\'zulah parle.>$b$bSoyez maudit ! Et vos ancêtres aussi !$b$bSeuls le sang et la revanche peuvent calmer ma rage, alors si tel est votre souhait… faites ceci :$b$bJetez-vous au coeur des Skullsplitter à l\'est. Massacrez les plus féroces d\'entre eux, et si vous survivez, mesurez-vous à leur chef, Ana\'thek le Cruel. Ha ! Apportez-moi son armure brisée si vous le pouvez !$b$bPuisse-t-il vous démembrer et laisser votre cadavre pourrir sur place.",
    ["O"] = "Tuer 8 Chasseurs, 6 Chasseurs de têtes, et 4 Berserkers du clan Skullsplitter.$b$bApporter l\'Armure brisée d\'Ana\'thek au Chaudron bouillonnant au campement Grom\'gol.",
    ["T"] = "En parlant avec Gan\'zulah",
  },
  [587] = {
    ["D"] = "Ah ! La vie en mer. Je ne vais pas dire que c\'est la bonne vie, mais c\'est ma vie.$b$bLorsque j\'arrive au port, j\'aime me trouver une bonne bouteille de tafia et du tabac à priser de qualité. Des habitudes dégoûtantes, mais que serait un pirate sans ses vices, pas vrai ?$b$bAlors vous pouvez imaginer mon dépit lorsque j\'ai découvert que l\'herboriste de la ville était parti et avait vendu sa marchandise. Dire que son chargement a été détourné par ces fichus Ecumeurs de la Voile sanglante.$b$bJe parie, $c, que vous pourriez les convaincre de partager, non ?",
    ["O"] = "Deeg de Baie-du-Butin veut que vous lui apportiez 15 portions de Tabac à priser.",
    ["T"] = "Faire un tabac",
  },
  [588] = {
    ["D"] = "<Nezzliok parle.>$B$BVous nous avez apaisés moi et mon chef, aussi vais-je vous dire ce que vous souhaitez savoir…$B$BLe jeune Yenniku est perdu pour son clan.$B$BIl a été emmené par le sorcier-docteur, Zanzil le Paria. À présent Zanzil le contrôle, de corps et d\'esprit, et seule la magie la plus puissante pourrait le libérer.$B$BUne magie dont nul $c ne peut disposer.$B$BPeut-être vos alliés Darkspear peuvent vous offrir un espoir de salut pour Yenniku. Mais ça, j\'en doute.",
    ["O"] = "Parler à Kin\'weelay.",
    ["T"] = "Le sort de Yenniku",
  },
  [589] = {
    ["D"] = "Les légendes parlent d\'un bijou, caché loin au cœur de Strangleronce. Nous l\'appelons la Vue de l\'Esprit, et il détient de grands pouvoirs. Si vous trouvez ce bijou, alors, peut-être, nous pourrions ramener Yenniku des ténèbres.$b$bJe vais parler aux esprits de la jungle pour localiser la Vue de l\'Esprit. Tandis que je m\'en occupe, allez dans la Mine aux cristaux, au sud-est d\'ici, et récupérez des éclats vibrants bleus des basilics Gueule de fer. Ces éclats sont rares, mais nous aurons besoin de leur magie pour faire plier la Vue de l\'Esprit à notre volonté.",
    ["O"] = "Apporter 3 Éclats vibrants bleus à Kin\'weelay au Campement Grom\'gol.",
    ["T"] = "Les Éclats vibrants",
  },
  [590] = {
    ["D"] = "Quoi ?$b$bQuoi ?$b$bVous voulez de l\'argent ? Je ne vous dois pas un sou.$b$bUne lettre ? Quelle lettre ? Oh ! Vous voulez vous battre, alors ? Très bien, $gmon pote:ma fille;… Allons-y ! Je vous dois de l\'argent…",
    ["O"] = "Battre Calvin Montague au Glas.",
    ["T"] = "Un marché de voleur",
  },
  [591] = {
    ["D"] = "La Vue de l\'Esprit est gardée par un mage ogre, à l\'est d\'ici et loin au cœur du tertre des ogres Mosh\'Ogg. Son nom est Mai\'zoth. Je le connais de l\'époque où les Darkspear vivaient à Strangleronce. Il est vicieux, et sa magie est puissante.$b$bEt s\'il possède la Vue de l\'Esprit, alors sa magie le sera encore plus.$b$bVous devez combattre Mai\'Zoth et lui arracher la Vue de l\'Esprit. C\'est une tâche périlleuse, mais c\'est le seul espoir que nous avons de sauver Yenniku.",
    ["O"] = "Apporter la Vue de l\'Esprit à Kin\'weelay au Campement Grom\'gol.",
    ["T"] = "La Vue de l\'Esprit",
  },
  [592] = {
    ["D"] = "J\'ai façonné la Vue de l\'Esprit dans une Gemme d\'âme. Cette gemme possède deux fonctions : vous devez d\'abord l\'utiliser pour étourdir Yenniku et, une fois qu\'il est inconscient, placer la gemme sur sa tête afin d\'emprisonner son âme.$b$bPuis apportez la gemme à Nimboya.$b$bYenniku demeure en compagnie de Zanzil le Paria le long du Rivage de cristal du lointain cap Strangleronce.",
    ["O"] = "Apporter la Gemme d\'âme remplie à Nimboya.",
    ["T"] = "Il faut sauver Yenniku",
  },
  [593] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Une âme pour la gemme",
  },
  [594] = {
    ["D"] = "La note dit, d\'une écriture plutôt élégante :$B$BA quiconque peut m\'aider,$B$BJe vous en prie, j\'ai désespérément besoin d\'aide. Un grand Crin-de-ciel me détient captive sur une île des mers du Sud.$B$BLes étoiles, la nuit, me sont étrangères et sont obscurcies par les arbres de la jungle. Mais les jours les plus clairs, je distingue le Rivage cruel au nord-ouest, et ce qui semble être de grands vaisseaux au sud-ouest.$B$BJe vous en supplie, mon possible sauveur, venez m\'aider.",
    ["O"] = "Trouver la main qui a écrit ce message lancé à la mer dans une bouteille.",
    ["T"] = "Message dans une bouteille",
  },
  [595] = {
    ["D"] = "Vous avez l\'air de vouloir travailler, $gmon pote:ma belle;. J\'ignore pourquoi des $cs viendraient offrir leur aide aux Ecumeurs des Flots noirs, mais je ne vais pas la refuser.$B$BNotre bateau vient juste d\'arriver à Baie-du-Butin et on dit que nos rivaux, ces abjects boucaniers de la Voile sanglante, ont été aperçus partout au nord de Strangleronce.$B$BTous les gars sont trop occupés à boire et à courir les jupons ! J\'ai besoin que quelqu\'un aille voir, $n.$B$BAllez à la crique au nord de Baie-du-Butin, le long de la Côte sauvage et voyez ce que vous pouvez trouver.",
    ["O"] = "Le Premier officier Crazz veut que vous contrôliez la crique au nord de Baie-du-Butin.",
    ["T"] = "Les boucaniers de la Voile sanglante",
  },
  [596] = {
    ["D"] = "J\'ai été envoyé à Grom\'gol pour aider Nimboya dans sa quête, mais j\'ai aussi des raisons personnelles d\'être là.$b$bLa tribu des Bloodscalp est l\'ennemie de la nôtre. Collecter les trophées de nos ennemis nous donne du pouvoir sur eux. Vous pouvez aider notre tribu en chassant nos ennemis.$b$bIls vivent au nord. Massacrez des trolls Bloodscalp et prenez leurs Colliers d\'os ensanglantés. Trouvez-en beaucoup, et grande sera votre récompense.",
    ["O"] = "Apporter 25 Colliers d\'os ensanglantés à Kin\'weelay au Campement Grom\'gol.",
    ["T"] = "Les Colliers d\'os ensanglantés",
  },
  [597] = {
    ["D"] = "La carte montre Baie-du-Butin avec un \"X\" rouge dessiné précisément à l\'endroit où vous vous trouvez.$B$BLe texte gribouillé dit :$B$B\"Vous devez préparer un camp à cet endroit et rester tranquilles. Attendez le signal du poste de commandement du sud avant d\'attaquer. Si quiconque approche, tuez-le.\"",
    ["O"] = "Rendre compte de vos découvertes au Premier officier Crazz à Baie-du-Butin.",
    ["T"] = "Les boucaniers de la Voile sanglante",
  },
  [598] = {
    ["D"] = "À présent que vous avez collecté les trophées des Bloodscalp, j\'ai une autre tâche pour vous. La tribu des trolls Skullsplitter vit au sud-est, et nous sommes en guerre contre eux depuis des années. Bien que les Darkspear ne vivent plus à Strangleronce, nous sommes toujours en conflit avec les Skullsplitter.$b$bAussi, chassez-les. Collectez leurs Colliers d\'os brisés et revenez me voir. Aidez-nous dans notre guerre, et vous gagnerez l\'amitié des Darkspear.",
    ["O"] = "Apporter 25 Colliers d\'os brisés à Kin\'weelay au Campement Grom\'gol.",
    ["T"] = "Collier d\'os brisés",
  },
  [599] = {
    ["D"] = "Bon sang ! Ainsi les boucaniers de la Voile sanglante préparent une attaque contre Baie-du-Butin ? Eh bien ! Ces crapules ne manquent pas d\'air. Mais Baie-du-Butin restera dirigée par les Ecumeurs des Flots noirs !$b$bC\'est plus grave que je ne le croyais. Allez dire ce que vous avez appris à l\'amiral Seahorn immédiatement ! Ce vieux loup de mer est quelque part en ville...",
    ["O"] = "Rendre compte de l\'attaque que prépare la Voile sanglante à l\'amiral Seahorn.",
    ["T"] = "Les boucaniers de la Voile sanglante",
  },
  [600] = {
    ["D"] = "La KapitalRisk possède un réseau d\'opérateurs à travers Strangleronce qui empêche les gobelins durs à la tâche, comme moi, de faire de l\'or honnête ! S\'il vous plaît, vous devez m\'aider !$b$bLa KapitalRisk exploite une mine près de la mine aux cristaux, au nord. Ils ne peuvent pas entrer dans la mine à cause de nombreux basilics, mais ils peuvent toujours extraire des cristaux chantants des collines environnantes.$b$bDérobez leurs cristaux, et montrez-leur que la jungle ne leur appartient pas.$b$bEt... euh... apportez-moi ces cristaux comme preuve !",
    ["O"] = "Apporter 10 Cristaux bleus chantants à Crank Fizzlebub, à Baie-du-Butin.",
    ["T"] = "La mauvaise mine de la KapitalRisk",
  },
  [601] = {
    ["D"] = "Les élémentaires d\'eau étaient sans nul doute invoqués par Gazban, le sorcier gobelin dont la rumeur dit qu\'il a découvert la Pierre des marées. Comme il se peut que vous le sachiez, cette Pierre lance une malédiction sur son porteur, le faisant apparaître et disparaître...$b$bPeut-être pouvons-nous trouver un moyen de neutraliser cette malédiction. Nous pouvons demander à un mage de Dalaran d\'analyser le résidu magique des bracelets de l\'un des élémentaires d\'eau. J\'ai besoin que vous retourniez sur l\'île et que vous rapportiez quelques échantillons.",
    ["O"] = "Acquérir 6 Bracelets d\'élémentaires d\'eau pour le baron Revilgaz à Baie-du-Butin.",
    ["T"] = "Les élémentaires d\'eau",
  },
  [602] = {
    ["D"] = "D\'après ce que j\'ai entendu, les mages de Dalaran sont très occupés à reconstruire leur ville détruite, mais je suis sûr que vous en dénicherez un qui donnera un peu de son temps pour regarder les bracelets. S\'il y a une chose que je sais à propos des mages, c\'est qu\'ils ne peuvent résister à une énigme magique.$B$BPrenez le sac de bracelets d’élémentaire d\'eau et voyez s\'ils peuvent apporter quelque lumière sur la malédiction de la pierre des marées. Cherchez tout particulièrement à rencontrer l\'archimage Ansirem Runeweaver. J\'ai déjà eu affaire à lui par le passé, et grand est son savoir.",
    ["O"] = "Apporter les Bracelets d’élémentaire d\'eau à l\'archimage Ansirem Runeweaver à Dalaran.",
    ["T"] = "Analyse magique",
  },
  [603] = {
    ["D"] = "Le mage qui analyse la Pierre amplifie son pouvoir à travers un point focal… Un autel d\'un certain genre. Vous devriez pouvoir le mettre hors d\'état en le surchargeant.$B$BEt de fait, je sais ce qu\'il vous faut : une dague ancienne, une relique des trolls. Il n\'y a qu\'un... léger problème.$B$BVous voyez, j\'ai trouvé la dague tandis que j\'effectuais des recherches à Strangleronce, mais je l\'ai donnée à ma fille entêtée, Catelyn, qui est partie à Baie-du-Butin pour devenir pirate.",
    ["O"] = "Parler à Catelyn à Baie-du-Butin.",
    ["T"] = "La Clé d\'Ansirem",
  },
  [604] = {
    ["D"] = "Crazz parle en termes flatteurs de votre dévouement envers les Écumeurs des Flots noirs. Je vais vous charger d\'une mission très importante pour la flotte.$b$bInfiltrez le prétendu poste de commande méridional que les boucaniers de la Voile sanglante ont établi sur le Rivage cruel. Faites une incursion dans le camp. Apportez-moi leurs cartes et leurs ordres écrits. Nous verrons exactement ce que cette vermine prépare.",
    ["O"] = "L\'amiral Seahorn à Baie-du-Butin veut que vous tuiez 10 Bretteurs de la Voile sanglante et que vous rapportiez les Cartes de la Voile sanglante et les Ordres de la Voile sanglante.",
    ["T"] = "Les boucaniers de la Voile sanglante",
  },
  [605] = {
    ["D"] = "Les cristaux chantants sont uniques à Strangleronce, et très précieux pour certains. Je peux transporter les cristaux, mais cette maudite KapitalRisk fait en sorte qu\'un honnête entrepreneur comme moi ne puisse pas en collecter.$b$bJ\'aimerais vous embaucher.$b$bLes basilics, à Strangleronce, mangent les cristaux. C’est ce qui durcit leur peau, et parfois, on peut récolter sur eux du cristal de bonne qualité.$b$bVous pouvez en trouver sur n\'importe quel basilic, mais les moins terribles se trouvent le long du rivage, plus au nord.",
    ["O"] = "Apporter 10 Eclats de cristal chantant à Crank Fizzlebub.",
    ["T"] = "Les éclats bleus chantants",
  },
  [606] = {
    ["D"] = "Si quelque chose m\'énerve, c\'est bien les gens qui ne paient pas leurs dettes de jeu. Et beaucoup me doivent de l\'argent. Vous pourriez m\'aider à le récupérer ?$B$BD\'abord, il y a Phillippe le Chancelant, en bas, près des docks. Il me doit beaucoup depuis la semaine dernière et je veux mon dû. Mais pour ça, il va falloir l\'effrayer.$B$BSortez de la ville et chassez des Anciens gorilles de Valbrume. Trouvez de bons abats, et montrez-lui. Ne dites pas qu\'ils proviennent d\'animaux, laissez-le s\'imaginer le pire.",
    ["O"] = "Apporter 5 Abats de Valbrume à Phillippe le Chancelant à Baie-du-Butin.",
    ["T"] = "Effrayer Philippe le Chancelant",
  },
  [607] = {
    ["D"] = "Tenez, prenez cette bourse et donnez-la à Loup des Mers. Et dites-lui que je comptais le rembourser ! Je le jure !$b$bJ\'étais juste un peu saoul et je me suis perdu et… vous savez comment sont les choses. Il comprendra, vous n\'avez pas besoin de devenir $gbrutal:brutale;.",
    ["O"] = "Apporter le Paiement de Philippe le Chancelant à MacKinley \"Loup des mers\".",
    ["T"] = "De retour chez MacKinley",
  },
  [608] = {
    ["D"] = "Maudit soit le toupet de Firallon ! Il ne pourrait même pas diriger une yole sur le lac de Cristal, et encore moins mener une flotte entière contre nous.$b$bLes boucaniers de la Voile sanglante vont payer leur négligence. À présent que nous connaissons leur plan d\'attaque, nous pouvons les contrer avant même qu\'ils ne sachent ce qui les frappe.$b$bMais pourquoi envoyer la flotte entière faire le travail d\'un courageux groupe d\'aventuriers ? $n, dirigez certains de vos meilleurs camarades au mouillage de Firallon et assassinez les dirigeants de la Voile sanglante. Leur flotte coulera peu après.",
    ["O"] = "L\'amiral Seahorn de Baie-du-Butin veut que vous tuiez le capitaine Stillwater, le capitaine Keelhaul et l\'amiral Firallon.",
    ["T"] = "Les boucaniers de la Voile sanglante",
  },
  [609] = {
    ["D"] = "Ma liste se réduit, mais certains me doivent encore de l\'argent.$B$BAprès, il y a Maury \"Pied bot\" Wilkins, Jon-Jon le Corbeau et Chucky \"Dix pouces\".$B$BLes dettes de ces chiens remontent à des mois et je pensais qu\'ils avaient quitté la ville pour éviter de payer ! Plus tard, j\'ai appris qu\'ils étaient maudits et ensorcelés. À présent, ils courent la jungle. Mais je me fiche de leur sort, je veux récupérer ce qui m\'appartient !$B$BIls se trouvent dans les ruines d\'Aboraz et dans les ruines de Jubuwai, au nord-est. Trouvez-les, et ramenez-moi mon dû.",
    ["O"] = "Apporter le Fléau de Maury, la Longue-vue dorée de Jon-Jon, et le Grand anneau de Chucky à MacKinley \"Loup des mers\" à Baie-du-Butin.",
    ["T"] = "Gages vaudou",
  },
  [610] = {
    ["D"] = "Mon PÈRE vous envoie ? J\'aurais cru qu\'il m\'aurait totalement oubliée.$B$BCe n\'est pas vraiment sa faute si je suis partie, il ne pouvait simplement pas imaginer que je ne voudrais pas vivre une vie ennuyeuse, à lire et relire des livres moisis et à devenir un de ces vieux mages livides aux cheveux blancs !$B$BSi ma dague vous intéresse, vous devrez la récupérer chez Duncan le \"Beau garçon\", l\'un des boucaniers de la Voile sanglante. Plantez quelque chose entre ses épaules et je suis certaine qu\'il y renoncera.",
    ["O"] = "Récupérer la Dague de Catelyn auprès de Duncan le \"Beau garçon\".",
    ["T"] = "Duncan \"le Beau garçon\"",
  },
  [611] = {
    ["D"] = "Ainsi vous recherchez un autel d\'un certain genre ? Eh bien ! Je ne suis pas experte en ruines de trolls comme peut l\'être mon père, mais j\'ai entendu parler d\'une cité troll engloutie près de cette île que vous cherchez. Peut-être devriez-vous essayer là-bas ?$b$bVous avez récupéré mon couteau, alors je suppose que vous pouvez l\'emprunter... Mais je veux que vous me le rendiez quand vous aurez fini !",
    ["O"] = "Détruire l’Autel des Marées avec la Lame de Catelyn, tuer Gazban et rapporter la Pierre des Marées et la Lame de Catelyn au baron Revilgaz.",
    ["T"] = "La malédiction des marées",
  },
  [613] = {
    ["D"] = "Malédiction ! Ce malade de Maury a mis un verrou sur son pied-bot, et je ne peux pas l\'ouvrir ! Son butin est à l\'intérieur, et c\'est ce qu\'il me doit !$b$bNous devons trouver la clé pour ouvrir le verrou. Maury était connu pour jouer avec les ogres de Mosh\'Ogg, sur leur colline au nord-est. Et il m\'a dit un jour que la dernière fois qu\'il était allé là-bas, il avait été coursé après avoir trop gagné. Peut-être que ces ogres ont sa clé.$b$bMais s\'ils l\'ont, il se peut que vous deviez en tuer beaucoup avant de trouver celui qui la détient...",
    ["O"] = "Apporter la Clé de Maury à MacKinley \"Loup des mers\" à Baie-du-Butin.",
    ["T"] = "Le pied-bot de Maury",
  },
  [614] = {
    ["D"] = "Dans le passé, je pilotais un navire d\'ici jusqu\'à Ratchet, en Kalimdor. J\'étais un grand capitaine avec une solide réputation.$b$bJusqu\'à ce que…$b$bCes maudits géants des mers m\'apportent la ruine ! Ils ont détruit mon bateau, tué mon équipage, et m’ont abandonné dans un canot. Et l\'un d’eux, Gorlash, a volé mon coffre de capitaine. Il a dit qu\'il le voulait pour en faire une tabatière. Quel toupet !$b$bJe sais que Gorlash sillonne la côte à l\'est d\'ici. Trouvez-le et récupérez mon coffre ! Faites-le, et vous gagnerez la gratitude d\'un capitaine.",
    ["O"] = "Apporter le Coffre au capitaine Hecklebury Smotts à Baie-du-Butin.",
    ["T"] = "Le coffre du capitaine",
  },
  [615] = {
    ["D"] = "Après que mon premier navire a été détruit par ces géants, j\'en ai acheté un autre. Je l\'ai baptisé le Revanche de Smotts, j\'ai trouvé un nouvel équipage, j\'ai rempli les cales de marchandises, puis j\'ai appareillé pour retrouver ces misérables géants. Je les ai trouvés, et de nouveau ils m\'ont battu. Ils ont détruit le Revanche de Smotts, tué presque tout mon équipage et jeté les rescapés dans un canot.$B$BCette fois, un autre géant, Negolash, m\'a volé mon sabre.$B$BCombattez Negolash et rapportez-moi mon sabre !$B$BParlez à Sprogger. C\'était mon cuistot sur le Revanche de Smotts et il a survécu à la dernière attaque. Il peut vous aider à trouver le géant.",
    ["O"] = "Parler à Sprogger.",
    ["T"] = "Le sabre du capitaine Smott",
  },
  [616] = {
    ["D"] = "Le baron Revilgaz m\'a chargé de laisser traîner une oreille sur les rumeurs concernant les événements étranges ou surnaturels qui surviennent à Baie-du-Butin et à Strangleronce. Je ne donne généralement pas beaucoup de crédit aux histoires de fantômes, mais j\'ai entendu des marins à la taverne parler d\'une île hantée près de la côte. Des élémentaires d\'eau et un fantôme de gobelin qui divague, qu\'ils disent.$b$bSi vous allez parler à Revilgaz, pourriez-vous lui faire part de cette histoire ?",
    ["O"] = "Parler au Baron Revilgaz, à Baie-du-Butin.",
    ["T"] = "L\'île hantée",
  },
  [617] = {
    ["D"] = "Vous cherchez du boulot, $n ? J\'en ai p\'têt\' si vous vous sentez $gprêt:prête; à relever un p\'tit défi.$b$bJ\'ai un ami, nommé Groy, qui fait un malheur en vendant c\'que les nagas appellent des roseaux akiris : c\'t\'un genre de roseau marin qu\'a d\'la valeur. Je n\'sais pas trop à quoi ça sert, mais on paye cher pour des lots d\'ce truc.$b$bApportez-m\'en dix tiges, et nous ferons des affaires. Les nagas ont été vus dernièrement le long de la plage, au nord.",
    ["O"] = "Apporter 10 Tas de roseaux akiris au corsaire Bloads à Baie-du-Butin.",
    ["T"] = "De derrière les fagots",
  },
  [618] = {
    ["D"] = "Negolash est un goinfre.$B$BQuand les géants ont détruit notre navire, il est venu directement dans ma cuisine. Il n\'avait pas faim d\'hommes d\'équipage comme les autres géants. Negolash voulait du vin et mes ailes de busard grillées au barbecue. C\'est ma spécialité, et j\'ai dû me rendre jusqu\'aux Terres ingrates pour avoir la recette !$B$BSi vous voulez tendre un piège au géant, trouvez du vin et un monceau d\'ailes de busard. Placez-les dans notre vieux canot, au sud-est, le long de la côte. Quand Negolash sentira cette bonne nourriture, il rappliquera, sans aucun doute !",
    ["O"] = "Apporter 10 Ailes de busard grillées et 5 bouteilles de Vin de Junglevine dans le Canot de sauvetage du capitaine Smotts.$B$BTuer Negolash, et apporter le Coutelas de Smotts au capitaine Smotts, à Strangleronce.",
    ["T"] = "Face à Negolash",
  },
  [619] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Allécher Negolash",
  },
  [620] = {
    ["D"] = "This fine sash has the letters \"HS\" embroidered along its hem.",
    ["O"] = "Return the Monogrammed Sash to its owner.",
    ["T"] = "The Monogrammed Sash",
  },
  [621] = {
    ["D"] = "Zanzil le Paria demeure avec ses partisans dans les Ruines de Jubuwai et dans celles d\'Aboraz, au nord-est de Baie-du-Butin. Il a été banni de sa tribu. Pourquoi ? Eh bien !… $b$bZanzil a le pouvoir de soumettre la volonté des autres, et ses chefs ont peur de lui. Il utilise un mélange de plantes tropicales pour supprimer la volonté et endurcir le corps de ses partisans.$b$bCette préparation intéresse beaucoup ceux qui voudraient contrôler les autres.$b$bApportez-moi des échantillons de ce mélange, et je leur trouverai une utilité.",
    ["O"] = "Apporter 12 Echantillons de Mixture de Zanzil à Crank Fizzlebub à Baie-du-Butin.",
    ["T"] = "Le secret de Zanzil",
  },
  [622] = {
    ["D"] = "Pendant que vous étiez $gparti:partie; j\'ai réparé la cocotte que vous m\'aviez apportée. J\'y ai même ajouté un autre sifflet à vapeur, et regardez : il y a un hachoir au fond pour réduire les os de pattes en bouillie ! Oui ! La Cocotte ébréchée de Krazek ravira tout cuisinier à 100% ! Je suis sûr que son propriétaire en sera satisfait.",
    ["O"] = "Apporter la Cocotte réparée de Krazek au caporal Kaleb, au Camp rebelle.",
    ["T"] = "La cocotte du caporal Kaleb",
  },
  [623] = {
    ["D"] = "$N, puisque vous êtes là : j\'ai reçu un message de Groy pendant que vous étiez $gparti:partie;. Il dit être satisfait de la dernière livraison, mais il a un client qui en désire plus, et tout de suite.$b$bJe ne vais pas à Theramore avant une semaine, mais si vous faites le voyage, vous pourriez transporter la marchandise pour moi.$b$bUn peu d\'aventure vous tente ? Vous savez où se trouve Âprefange, n\'est-ce pas ? Je ferais attention quand même, Theramore peut être dangereuse si vous n’avez pas le bon profil.",
    ["O"] = "Apporter un Fagot de roseaux akiris au corsaire Groy à Theramore.",
    ["T"] = "De derrière les fagots",
  },
  [624] = {
    ["D"] = "Il est un pont fleuri, beaucoup emprunté$bÀ l\'est de Deuillevent et au sud des Carmines$bDessous, quelque chose vous attend$b$bCertains l\'appellent allusion, d\'autres indice$b$bIl reposera là pour d\'éternels lendemains$b$bPrenant son temps dans le marais des Chagrins.",
    ["O"] = "Résoudre l\'énigme.",
    ["T"] = "Devinette de Cortello",
  },
  [625] = {
    ["D"] = "Une terre lointaine en Kalimdor attend$bOù le péril a brisé espoirs et destinées$bÀ l\'est des Tarides le temps est sévère$bTénèbres et dangers criblent le marécage d\'Âprefange$bMais là est un indice pour ceux qui prennent le risque$bAttendant d\'être découvert dans une caverne au sud-ouest.",
    ["O"] = "Résoudre l\'énigme.",
    ["T"] = "Devinette de Cortello",
  },
  [626] = {
    ["D"] = "A Lordaeron ! La grande terre de bouleversements$bOù le soldat courageux fait de la mort son métier$bTrouvez votre chemin à travers l\'agitation$bVous trouverez le fin mot de la quête$bLe long de la côte des Hinterlands, au pied des grandes chutes$bSous l\'eau, c\'est là que votre trésor vous appelle !",
    ["O"] = "Résoudre l\'énigme.",
    ["T"] = "Devinette de Cortello",
  },
  [627] = {
    ["D"] = "Il y a une caverne dans la montagne, dans la gorge des Sèches-moustaches, loin au nord dans les hautes-terres d\'Arathi. Cette caverne abrite un minerai rare : la pierre de sang. Celle de cette caverne n\'est pas de la meilleure qualité, mais elle a quand même une certaine valeur... $b$bJ\'en aimerais un peu.$b$bJe vais réparer votre cocotte, mais pendant que j\'y travaille, allez chercher des fragments de pierre de sang. Apportez-m\'en assez, et je vous donnerai un bonus.$b$bEt si vous n\'avez pas la capacité de l\'extraire... trouvez quelqu\'un qui le peut !",
    ["O"] = "Apporter 4 chargements de Minerai de pierre de sang inférieur à Krazek à Baie-du-Butin.",
    ["T"] = "Un service pour Krazek",
  },
  [628] = {
    ["D"] = "Maintenant que l\'intérieur est terminé, tout ce qui reste à faire est la couche extérieure. Pour ça, je n\'utilise que les meilleures peaux de vieux crocis. Elles sont plus souples et solides, avec de belles écailles ; toutes les qualités qui définissent la gamme Excelsior de Drizzlik.$b$bIl est difficile de trouver des peaux parfaites, et celles qui me conviennent le mieux proviennent des vieux crocilisques marins. Les vieux ne sortent que pour défendre les autres crocis marins, alors il faudra en occire quelques-uns avant d’attirer un vieux.",
    ["O"] = "Apporter une Peau d\'ancien crocilisque à Drizzlik, à Baie-du-Butin.",
    ["T"] = "L’Excelsior de chez Drizzlik",
  },
  [629] = {
    ["D"] = "Il y a longtemps, un grand tremblement de terre engloutit une ancienne cité troll sous les eaux de la Côte sauvage au nord-ouest. Nous appelons cet endroit le Récif infâme, car ce sont les murlocs qui à présent résident dans les ruines de la cité, attaquant tous ceux qui s\'en approchent.$b$bUne vieille tablette repose parmi ces ruines, racontant l’histoire du héros troll Gri\'lek. Cette légende est sacrée pour la tribu Darkspear, et bien que la tablette soit trop lourde pour qu\'on puisse la bouger, j\'en veux un fragment pour le conserver dans notre nouvelle demeure, à Orgrimmar.",
    ["O"] = "Apporter un Fragment de tablette à Kin\'weelay au Campement Grom\'gol.",
    ["T"] = "Le Récif infâme",
  },
  [630] = {
    ["D"] = "$N, je remets mon sort entre vos mains et entre celles de tous ceux que vous pourrez réunir pour me libérer. Le roi Mukla, le grand Crin-de-ciel, est mon ravisseur. Il commande aux bêtes de l\'île et me retient contre ma volonté. Si vous pouviez le combattre et trouver la clé qui ouvre mes chaînes, je vous serais grandement redevable.$B$BJe l\'entends souvent dans la partie sud-est de l\'île, mais je vous en prie, soyez $gprudent:prudente;, il ne doit pas être abordé à la légère.",
    ["O"] = "Tuer le roi Mukla, trouver la clé et libérer la princesse Poobah.",
    ["T"] = "Message dans une bouteille",
  },
  [631] = {
    ["D"] = "Une explosion monstrueuse a grondé à travers les collines. C\'tait horrible, j\'vous l\'dis !$b$bDe bons soldats sont morts en défendant le viaduc de Thandol. Longbraid dit que nous prendrons notre revanche sur les Sombrefer avant que tout cela ne soit fini.$b$bIci, il n’y a que des blessés, et je ne vois aucun renfort arriver à l\'horizon. Longbraid a envoyé le vieux Rustlocke en reconnaissance au pont à l\'est, pour voir si nous pouvions le sécuriser.$b$bC\'était il y a plusieurs jours. Avec Dun Modr en état de siège, nous n\'avons pas les ressources d’aller le chercher.$b$bPeut-être pourriez-vous le trouver...",
    ["O"] = "Rhag Garmason veut que vous retrouviez le vieux Rustlocke.",
    ["T"] = "Le viaduc de Thandol",
  },
  [632] = {
    ["D"] = "Il semblerait que le pauvre vieux Rustlocke soit tombé dans une embuscade de Sombrefer. Vous remarquez un parchemin froissé dans son poing serré.$b$bApportez la triste nouvelle à Rhag Garmason et montrez-lui ce que vous avez trouvé.",
    ["O"] = "Rendre compte à Rhag Garmason et lui montrer le parchemin que vous avez trouvé sur le corps de Rustlocke.",
    ["T"] = "Le viaduc de Thandol",
  },
  [633] = {
    ["D"] = "Par le Ciel, non ! Pauvre Rustlocke ! C\'est une tragédie !$b$bNous ne devons pas laisser sa mort impunie. Le parchemin qu’il tenait indique que Kam Deepfury, le cerveau derrière la première attaque, a fait entreposer des explosifs dans une cache, juste de l\'autre côté du viaduc de Thandol, dans les hautes-terres d\'Arathi.$b$bSi ces explosifs atteignent le pont, notre principale ligne d\'approvisionnement vers le nord sera très compromise. La cache doit être détruite, $n. Revenez me voir lorsque votre mission sera accomplie.$b$bVous êtes notre seul espoir.",
    ["O"] = "Détruire la cache d\'explosifs.",
    ["T"] = "Le viaduc de Thandol",
  },
  [634] = {
    ["D"] = "J\'aimerais vous demander un autre petit service, $c. Nous attendons les troupes de réserve d\'Ironforge. Cependant, Roggo connaît l\'existence d\'un camp de l\'Alliance, au nord sur les hautes-terres d\'Arathi, au Refuge de l\'Ornière.$b$bAllez-y et cherchez le capitaine Nials. Demandez-lui si l\'Alliance peut nous prêter quelques soldats pour nous aider à reprendre Dun Modr et le viaduc de Thandol.",
    ["O"] = "Chercher le capitaine Nials au Refuge de l\'Ornière.",
    ["T"] = "L\'appel à l\'Alliance",
  },
  [635] = {
    ["D"] = "L\'éclat vibre tandis que vous le tenez…$b$bVous ressentez comme un appel, comme si quelqu\'un essayait de communiquer, quelqu\'un qui avait désespérément besoin de votre aide.$b$bUne vision apparaît dans votre esprit. Une vision des monts Arathi, et d\'un cristal brillant saillant de la terre, là-bas.$b$bVous vous sentez $gattiré:attirée; par cet endroit.",
    ["O"] = "Trouver le cristal de votre vision.",
    ["T"] = "Le cristal dans les montagnes",
  },
  [636] = {
    ["D"] = "Ce livre raconte l\'histoire d\'une princesse élémentaire, Myzrael, et de son emprisonnement loin sous les hautes-terres Arathies.$B$BIl évoque également un affleurement secret de cristaux, en surface. Ces cristaux, les Éclats de Myzrael, forment une fenêtre sur la prison de la princesse.$B$BSi une âme courageuse trouve les Éclats de Myzrael, alors peut-être pourra-t-elle lui parler...",
    ["O"] = "Trouver les Éclats de Myzrael.",
    ["T"] = "Légendes de la Terre",
  },
  [637] = {
    ["D"] = "L\'enveloppe imbibée d\'eau et déchirée se dissout et laisse dans vos mains quelques pages d\'une lettre, écrite avec soin.$B$BLa lettre est adressée à Mme Sara Balloo d\'Ironforge et commence par les mots : \"Ma chère Sara…\"",
    ["O"] = "Donner la Lettre de Sully Balloo à Sara Balloo.",
    ["T"] = "Lettre de Sully Balloo",
  },
  [638] = {
    ["D"] = "Thoras Trollbane étant mort, et Stromgarde en ruine, Vol\'jin a donné pour instructions que nous mettions tout en œuvre pour dérober l\'épée légendaire Trol\'kalar, qui est aux mains des humains, afin de l\'utiliser dans nos futurs combats contre les trolls de Zul\'Gurub.$b$bZengu, l\'un de nos chasseurs aguerris, a été envoyé au Trépas-d\'Orgrim, dans la partie nord-est des hautes-terres d\'Arathi, pour veiller à son obtention.",
    ["O"] = "Aller au Trépas-d\'Orgrim dans les hautes-terres d\'Arathi et parler à Zengu.",
    ["T"] = "Trollemort",
  },
  [639] = {
    ["D"] = "Dans des temps anciens, les trolls se battaient contre l\'Empire arathorien et notre plus vaillant adversaire était le seigneur Ignaeus de Strom, dit Trollbane. Il se lançait dans la bataille en brandissant Trol\'kalar, ce qui dans leur langue voulait dire \"Tueuse de trolls\".$b$bMais aujourd’hui les fils de Trollbane sont affaiblis et leur forteresse en ruine.$b$bL\'épée est protégée par de nombreux glyphes qui ont été répartis entre les survivants de Stromgarde, et nous devons les retrouver.$b$bLe premier a été pris par le Syndicat, l\'un de leurs hommes à Stromgarde devrait l\'avoir.",
    ["O"] = "Trouver le Glyphe de Strom pour Zengu au Trépas-d\'Orgrim.",
    ["T"] = "Glyphe de Strom",
  },
  [640] = {
    ["D"] = "La tombe de Thoras Trollbane est protégée par trois glyphes. Vous avez récupéré le premier auprès du Syndicat. Les deux autres seront plus difficiles à obtenir, car ils sont détenus par la milice fortement retranchée de Stromgarde.$b$bLe Glyphe de Thoradin a été brisé en onze morceaux par le fils de Thoras, Galen, et dispersé entre les défenseurs de Stromgarde. Retrouvez les morceaux et apportez-les à Tor\'gan, le chaman troll, il les assemblera pour reconstituer le Glyphe complet.",
    ["O"] = "Reprendre les 11 Fragments de Glyphe aux défenseurs de Stromgarde, puis les apporter à Tor\'gan au Trépas-d\'Orgrim.",
    ["T"] = "Le Glyphe qui fut brisé",
  },
  [641] = {
    ["D"] = "Le Glyphe a retrouvé son intégrité et peut désormais permettre d’ouvrir le sceau sur la tombe de Trollbane. Apportez-le à Zengu, il ne fait pas de doute qu\'il a d\'autres tâches à vous confier.",
    ["O"] = "Apporter le Glyphe de Thoradin reconstitué à Zengu, au Trépas-d\'Orgrim.",
    ["T"] = "Glyphe de Thoradin",
  },
  [642] = {
    ["D"] = "Mon nom est Myzrael. Je suis une Princesse de la terre et mes ravisseurs, les géants, m\'ont emprisonnée loin sous les hautes-terres d\'Arathi. Ces éclats de cristal sont le seul moyen dont je dispose pour communiquer avec le monde de la surface.$B$BPar pitié, aidez-moi. Les alliés des géants, les kobolds Sèches-moustaches, possèdent un de ces éclats à la gorge des Sèches-moustaches, à l\'est. Pour l\'activer, vous devez collecter des Poussières de Myzrael sur les kobolds, puis les y appliquer.$B$BJe vous en supplie, $N, venez à mon secours !",
    ["O"] = "Collecter 12 Poussières de Myzrael, puis les répandre sur les Eclats iridescents, à la gorge des Sèches-moustaches.",
    ["T"] = "La princesse emprisonnée",
  },
  [643] = {
    ["D"] = "Bien qu\'on puisse dire qu\'il contrôle tout juste Stromgarde, et certainement pas toutes les hautes-terres d\'Arathi, le prince humain fait courir la campagne aux restes de sa cavalerie. Je ne comprends pas pourquoi, mais qui peut comprendre la façon dont pensent les humains, hein ?$b$bLe plus important, c’est qu’il ait donné l\'un des Glyphes au lieutenant Valorcall, commandant sa cavalerie. Trouvez-le et rapportez-moi son Glyphe.",
    ["O"] = "Retrouver le Glyphe d\'Arathor et l\'apporter à Zengu, au Trépas-d\'Ogrim.",
    ["T"] = "Glyphe d\'Arathor",
  },
  [644] = {
    ["D"] = "Le dernier Glyphe dont nous avons besoin pour ouvrir la tombe de Thoras Trollbane est détenu par son fils en personne. Il vit loin à l\'intérieur de Stromgarde, dans une chapelle des quartiers contrôlés par la milice. Le prince Galen est bien protégé et n\'est pas maladroit à la guerre non plus, aussi cette tâche pourrait s\'avérer très ardue.",
    ["O"] = "Tuer le prince Galen Trollbane et apporter le Glyphe de Trollbane à Zengu, au Trépas-d\'Ogrim.",
    ["T"] = "Glyphe de Trollbane",
  },
  [645] = {
    ["D"] = "Qu\'est-ce donc ? Les quatre Glyphes une fois réunis, ils se sont rassemblés pour n\'en former qu\'un seul : le Glyphe d\'Ignaeus. Celui-ci pourra sans aucun doute désactiver la garde placée sur la tombe de Trollbane.$b$bSes restes sont ensevelis dans le Sanctuaire derrière la chapelle de Stromgarde. Rapportez Trol\'kalar.",
    ["O"] = "Retirer Trol\'kalar de la tombe de Trollbane à Stromgarde.",
    ["T"] = "Trol\'kalar",
  },
  [646] = {
    ["D"] = "La lumière s\'éteint, doucement, et l\'épée Trol\'kalar, reposant dans un fourreau de pierre, est libérée.",
    ["O"] = "Apporter Trol\'kalar à Zengu au Trépas-d\'Orgrim.",
    ["T"] = "Trol\'kalar",
  },
  [647] = {
    ["D"] = "J\'ai bu pas mal de mon bon vieux tord-boyaux la nuit dernière ! Longbraid aurait ma tête s\'il savait que je me suis endormi pendant mon tour de garde.$b$bL’alcool a frappé fort. Je me sentais comme si une brigade entière de machines de siège m\'était passée sur le crâne, avec tous ces grondements.$b$bBon sang, regardez l\'heure ! J\'ai promis au brasseur Bilger de Southshore que je lui paierai ma dette en lui envoyant du tord-boyaux.$b$bMais il ne reste que 15 minutes, après je serai en retard ! Apportez-lui ce lot, s\'il vous plaît ! Et dépêchez-vous !",
    ["O"] = "Apporter le Tord-boyaux de MacKreel au brasseur Bilger à Southshore.",
    ["T"] = "Le tord-boyaux de MacKreel",
  },
  [648] = {
    ["D"] = "Le robot émet à nouveau la voix du gnome :$B$B\"Je dois déplacer OOX-17/TN dans un endroit dégagé et sûr pour qu\'il puisse entamer sa longue procédure de décollage. Il est équipé d\'un camouflage, mais il me faut du temps pour le rendre opérationnel de mon côté. J\'ai besoin que vous l\'escortiez jusqu\'au... disons jusqu\'à PortGentepression ! Ce serait un endroit parfait, et assez long, pour que tout puisse se mettre en route !\"$B$BEscortez-le jusque là-bas, et venez me parler à Baie-du-Butin ! Oglethorpe Obnoticus - terminé !\"",
    ["O"] = "Escorter OOX-17/TN jusqu\'à Port Gentepression, puis rendre compte à Oglethorpe Obnoticus à Baie-du-Butin.",
    ["T"] = "À la rescousse de OOX-17/TN !",
  },
  [649] = {
    ["D"] = "J\'ai un travail de récupération pour vous, et si vous êtes un amateur de Houle comme moi, vous l\'apprécierez sûrement. La Houle !$B$BMais c\'est une boisson, imbécile.$B$BIl reste une réserve de Houle au miel des Hinterlands dans un avant-poste abandonné de la Horde, qui date de l\'époque où il fonctionnait encore. On ne peut plus trouver d’aussi bonne Houle, aujourd\'hui. L\'un des hommes de paille de Malton est au courant et connaît la musique. Alors, vous allez vous rendre là-bas et m\'apporter une caisse de Houle emballée, et deux bouteilles pour ma collection. Parlez à Malton, il vous donnera tous les détails nécessaires.",
    ["O"] = "Parler à Malton Droffers à Orgrimmar.",
    ["T"] = "La récupération de Houle",
  },
  [650] = {
    ["D"] = "L’elfe que vous devez consulter s\'appelle Gilveradin Sunchaser. Il peut identifier n\'importe quel vin ou cuvée, uniquement à l\'odorat ! Il peut emballer la Houle dont nous avons besoin, et c\'est lui que vous devrez voir. Nous avons besoin de six bouteilles pour la livraison, de deux pour papa, et d\'autant de bouteilles que l\'elfe en voudra... Et il en voudra, croyez-moi ! Lorsque tout sera prêt et que vous aurez deux bouteilles pour papa, revenez me voir.$B$BLe camp de Gilveradin est proche d\'un gîte de hauts-elfes dans les Hinterlands, et ils ne sont pas commodes. Croyez-moi.",
    ["O"] = "Parler à Gilveradin Sunchaser. Son camp se situe dans les Hinterlands.",
    ["T"] = "La récupération de Houle",
  },
  [651] = {
    ["D"] = "Quatre Pierres de lien sont dispersées à travers tout Arathi. Ces pierres me retiennent prisonnière sous la terre.$b$bPour me libérer vous devez ouvrir la plus puissante : la Pierre de lien intérieure. Et pour cela, vous devez rassembler les clés des trois autres.$b$bCes pierres sont sous la surveillance de gardiens, mais si vous agissez intelligemment, ou si vous avez de la chance, vous pouvez trouver un moyen de les bannir.",
    ["O"] = "Collecter la Clé ardente, la Clé armoriée et la Clé de tonnerre aux lieux suivants : la Pierre de lien occidentale, la Pierre de lien orientale et la Pierre de lien extérieure.$b$bLes apporter à la Pierre de lien intérieure.",
    ["T"] = "Les pierres de lien",
  },
  [652] = {
    ["D"] = "Ouvrir les Pierres de lien a relâché l\'étreinte de mes fers, mais quelque chose me retient toujours captive. C\'est la Pierre cruciale que vous voyez parmi ce cercle de pierres. Et sa clé est détenue par l\'un de mes geôliers, le Gardien de pierres Fozruk.$b$bVous devez prendre cette clé, que les Gardiens de pierres appellent le Bâtonnet de l\'ordre, à Fozruk, et l\'utiliser pour ouvrir la Pierre cruciale.$b$b$n, vous avez déjà tellement fait pour me venir en aide. Encore un peu... et ma gratitude fera trembler les fondations même de la terre !",
    ["O"] = "Trouver et tuer Fozruk. Apporter le Bâtonnet de l\'ordre à la Pierre cruciale dans les hautes-terres d\'Arathi.",
    ["T"] = "La Pierre cruciale",
  },
  [653] = {
    ["D"] = "<Myzrael parle.>$b$bLibre ! JE SUIS LIBRE ! Je suis libre de rassembler mes forces, à l’abri du regard de mes geôliers. Si je les affrontais maintenant ils me domineraient et m\'emprisonneraient de nouveau.$b$bMais au moment voulu, je combattrai les géants, et ils regretteront la tutelle qu\'ils m\'ont imposée !$b$bVous êtes $gun noble allié:une noble alliée;, $n. J\'aurai encore besoin de votre aide quand le temps sera venu. Bientôt, il vous faudra m\'invoquer à la surface.$b$bParlez à Gerrig Bonegrip. Il se trouve à Ironforge, dans une boutique de la caverne lugubre. Il sait comment on doit m\'invoquer.",
    ["O"] = "Parler à Gerrig Bonegrip, dans la caverne lugubre d’Ironforge.",
    ["T"] = "Les alliés de Myzrael",
  },
  [654] = {
    ["D"] = "L\'activation de la Source d\'énergie fait briller et vibrer la Trousse d\'analyse de terrain. Tout semble fonctionner comme prévu.$B$BD\'après l\'ingénieur en chef Bilgewhizzle, vos disposez à présent de deux heures pour récupérer les données dont vous avez besoin dans le Désert de Tanaris. Si vous n\'y parvenez pas en deux heures, toutes les données seront perdues et vous devrez lui acheter une autre Source d\'énergie.$B$BAllez le voir une fois votre tâche accomplie.",
    ["O"] = "Acquérir des spécimens acceptables pour 8 basilics, 8 hyènes et 8 scorpions. Rapporter la Trousse d\'analyse de terrain à l\'Ingénieur en chef Bilgewhizzle à Gadgetzan avant que la source d\'énergie ne s\'épuise.",
    ["T"] = "Les échantillons du désert de Tanaris",
  },
  [655] = {
    ["D"] = "Je m\'en souviens très bien, c\'est comme si c\'était hier... Tellement d\'années sans espoir, attaché à cet horrible endroit par du bois et par du fer. Et puis enfin, enfin ! Il y eut le Chef de guerre. Orgrim Doomhammer dans son armure étincelante, son marteau de guerre tournoyant au-dessus de la tête.$b$bCe fut... trop terrible. Que le coup d\'un misérable lâche terrasse notre plus grand héros. Il a chargé, sur son cheval, par-derrière, et sa lance l\'a transpercé. Je suis tombé à genoux. Quels espoirs restaient-ils ? Quels espoirs restent-ils ?",
    ["O"] = "Parler à Tor\'gan au Trépas-d\'Orgrim.",
    ["T"] = "Trépas-d\'Orgrim",
  },
  [656] = {
    ["D"] = "Vous devez battre Myzrael avant que sa force n\'augmente. Sinon, une fois qu\'elle aura rassemblé suffisamment de pouvoir, elle défiera ses ravisseurs ! Certains pensent pouvoir la contrôler, mais si elle peut l\'être, alors pourquoi les géants l\'ont-ils enchaînée ??$B$BLogique stupide, pas vrai ?$B$BPour la défier, invoquez-la aux Éclats de Myzrael, où vous lui avez parlé la première fois. Battez-la et prenez ses Chaînes indicibles, puis attachez-les aux Éclats de Myzrael.$B$BAgissez ainsi et elle sera de nouveau prise au piège.",
    ["O"] = "Aller aux Éclats de Myzrael, l\'invoquer et la battre.$B$BPrendre ses Chaînes indicibles et les attacher aux Éclats de Myzrael.",
    ["T"] = "L\'invocation de la Princesse",
  },
  [657] = {
    ["D"] = "<Quae commence à consulter le dossier.>$b$bC\'est effectivement intéressant. Il semblerait que l\'Apothicaire Jorell ait travaillé sur des expériences mortelles ici à Go\'shek. Je ne suis pas certaine de ce que cela a à voir avec Hillsbrad, mais Phin n\'apprécierait pas que nous laissions se poursuivre ces expériences.$b$bNous avons eu une altercation avec des orcs pendant que vous étiez $gparti:partie;, et je ne suis pas en suffisamment bonne forme pour aider Kinelory. Si vous vous sentez d\'attaque, elle va avoir besoin de protection pour aller à la ferme et voler toutes les recherches que Jorell n\'aurait pas encore envoyées à Moulin-de-Tarren.",
    ["O"] = "Parler à Kinelory.",
    ["T"] = "Les indices d\'une Peste nouvelle ?",
  },
  [658] = {
    ["D"] = "Ainsi, Phin vous a $genvoyé:envoyée; prendre de nos nouvelles ? Ça ne m\'étonne pas, il s\'inquiète toujours trop.$b$bNous avons surveillé la ferme pendant plusieurs jours, et Kin prétend qu\'elle a vu une messagère sortir de cette petite maison et se diriger vers le sud il y a peu.$b$bElle parle beaucoup, mais je fais toujours confiance à ses sens lorsque nous sommes dans des endroits dangereux.$b$bVous ne seriez pas $gun Pisteur compétent:une Pisteuse compétente;, par hasard ? Nous pensons que la messagère se rendait à Moulin-de-Tarren. Si nous pouvions mettre la main sur les documents qu’elle emportait, cela pourrait nous renseigner sur ce qui se passe à Hillsbrad.",
    ["O"] = "Trouver la Messagère des Réprouvés et ramener ses documents scellés à Quae près de la Ferme des Go\'shek.",
    ["T"] = "Les indices d\'une Peste nouvelle ?",
  },
  [659] = {
    ["D"] = "Salut et bienvenue, $n. Je mène un groupe d\'aventuriers enquêtant sur des faits étranges se produisant dans le coin.$b$bMon cousin à Hillsbrad a récemment abattu son chien Stanley parce que l\'animal avait magiquement grossi jusqu\'à une taille immense et attaquait sauvagement quiconque l\'approchait.$b$bJe pense que les morts-vivants sont impliqués, et nous cherchons des indices le prouvant.$b$bDeux de mes compagnes, Quae et Kinelory, sont parties en direction de la ferme des Go\'shek sur les hautes-terres d\'Arathi, et ne sont toujours pas revenues. Vous pourriez les trouver pour moi ? Je commence à m\'inquiéter.",
    ["O"] = "Trouver Quae et Kinelory près de la Ferme des Go\'shek dans les hautes-terres d\'Arathi.",
    ["T"] = "Les indices d\'une Peste nouvelle ?",
  },
  [660] = {
    ["D"] = "Très bien, $n. Je suis prête si vous l\'êtes.$b$bOn y va ?",
    ["O"] = "Protéger Kinelory jusqu\'à ce qu\'elle rejoigne Quae.",
    ["T"] = "Les indices d\'une Peste nouvelle ?",
  },
  [661] = {
    ["D"] = "J\'ai déjà vu Kinelory dans un état pire après toute une nuit passée dans une taverne. Vous avez bien fait de la protéger.$B$BCe qu\'elle rapporte de la ferme permettra à Phin de se faire une idée de ce qui se trame à Arathi. Nous allons rester un peu pour être sûr qu\'il n\'y aura pas de représailles suite à votre attaque. Voudriez-vous retourner à Southshore et parler à Phin ?",
    ["O"] = "Retrouver Phin Odelic à Southshore.",
    ["T"] = "Les indices d\'une Peste nouvelle ?",
  },
  [662] = {
    ["D"] = "O\'Breen a conduit les bateaux dans la crique. Nous étions certains d\'y trouver le trésor. Le « Noir messager » a accosté sans encombre, mais à mesure que la « Folie de la vierge » et « L\'Esprit des Pins argentés » approchaient, le niveau de la mer a commencé à baisser. C\'est la chose la plus étrange que j\'aie jamais vue !$b$bLes deux bateaux se sont échoués sur quelque chose... Une sorte de temple dans les récifs. Puis ils ont coulé à pic ; une vraie tragédie.$b$bNous voulons récupérer leurs cartes maritimes et leurs journaux de bord avant de partir. Nous ne voulons pas que les autres puissent les récupérer !",
    ["O"] = "Le lieutenant Nilzlix veut que vous récupériez les Cartes et les Journaux de bord de la « Folie de la vierge » et de « L\'Esprit des Pins argentés ».",
    ["T"] = "Sauvetage en haute mer",
  },
  [663] = {
    ["D"] = "Lolo voit une minuscule personne ! Toujours Lolo faire le guet !$b$bBienvenue à la crique de Faldir, $n. Capitaine O\'Breen a dit que nous resterions que quelques heures. Juste le temps de récupérer le trésor, puis nous revenir à Baie-du-Butin.$b$bMais Lolo pense que ça va durer plus longtemps. Notre groupe a perdu deux navires. Pauvres « Esprit des Pins argents » et « Folie de la vierge ». Au fond de la mer maintenant ils reposent !$b$bLolo propose de vous parler au capitaine O\'Breen si vous voulez rester ici.",
    ["O"] = "Parler au capitaine O\'Breen à la Crique de Faldir.",
    ["T"] = "Terre !",
  },
  [664] = {
    ["D"] = "Argh, quelle horrible vie ! Je devrais être mort sous l\'eau, et pas assis ici près d\'un feu, respirant de l\'air frais.$b$bL\'équipage a tout fait pour sauver mon merveilleux « Folie de la Vierge ». Loué soit-il !$b$bO\'Breen a réussi à atteindre la rive, nous on n\'a pas été aussi chanceux. La mer s\'est carrément défilée sous nous, je le jure.$b$bA un instant on naviguait, l\'instant d\'après on était échoué sur un récif. Et ces créatures ! Elles ont tué tous les survivants. Saleté de nagas ! Je me suis évanoui, puis je me suis réveillé ici.$b$bJe veux que ces créatures meurent !",
    ["O"] = "Le capitaine Steelgut de la crique de Faldir veut que vous tuiez 10 Ecumeurs Daguéchine et 3 Sorcières Daguéchine.",
    ["T"] = "Chagrin noyé",
  },
  [665] = {
    ["D"] = "Maintenant que nous sommes des Ecumeurs des Flots noirs accomplis, il est de notre devoir d\'aider M. O\'Breen à localiser le trésor perdu des elfes.$b$bDans ces eaux sombres, il est presque impossible de trouver les gemmes sans aide. Le docteur a conçu des lunettes spéciales qui pourraient nous être utiles. Il a besoin de les charger avec l’énergie magique de pierres enchantées qui se trouvent dans la grotte en haut de la colline.$b$bMais cette grotte est maudite ! Quand on s\'en approche, on se fait attaquer. Venez et protégez-moi, alors je pourrai transférer l\'énergie des pierres vers les lunettes.",
    ["O"] = "Escorter le professeur Phizzlethorpe à la grotte et revenir.",
    ["T"] = "Le trésor englouti",
  },
  [666] = {
    ["D"] = "Le trésor repose au fond de la mer depuis si longtemps que les gemmes se sont calcifiées en pierres. Mais le pouvoir contenu dans ces lunettes vous permettront de les localiser facilement.$B$BL\'ingéniosité gnome permet beaucoup !$B$BAussi, empruntez les Lunettes de chasse aux gemmes, $N, et voyez si vous pouvez récupérer une partie du trésor perdu pour le capitaine O\'Breen.$B$BJ\'y plongerais bien moi-même mais... euh... des affaires scientifiques importantes nécessitent ma présence sur un sol dur et sec... euh... voilà, c\'est ça.",
    ["O"] = "Le docteur Draxlegauge, à la crique de Faldir, veut que vous rapportiez 10 Gemmes elfiques et que vous rapportiez ensuite les Lunettes de chasse aux gemmes.",
    ["T"] = "Le trésor englouti",
  },
  [667] = {
    ["D"] = "Ça ne se présente pas bien. Il semble que les Daguéchine se regroupent pour une attaque.$B$BLes nagas ont la réputation d\'être impitoyables dans la bataille et ne cesseront d\'attaquer tant que je n\'aurai pas rencontré la mort.$B$BJe ne considère pas ma vie plus précieuse que celles des membres de mon équipage, mais vous devez aider à ma sauvegarde. L\'équipage ne pourra pas revenir à Baie-du-Butin seul et sans la connaissance que je possède des mers.$B$BLes nagas viendront de la mer. Je veux que vous restiez près de moi ici en haut pour parer leur attaque. Servez-vous du canon et repoussez-les.$B$BÊtes-vous $gprêt:prête;, $N ?",
    ["O"] = "Protéger Shakes O\'Breen lors de l\'attaque.",
    ["T"] = "La mort venue d\'en-bas",
  },
  [668] = {
    ["D"] = "Ne faites pas attendre le capitaine O\'Breen. Il veut voir les gemmes. Après tout, c\'est quand même pour ça qu\'on est là !$b$bAprès ces quelques semaines passées avec les pirates, le professeur pense que la pire des choses serait qu\'ils s\'aperçoivent que nous nous approprions leur trésor.$b$bVoilà, $n, portez ça à O\'Breen.",
    ["O"] = "Apporter les Gemmes elfique au capitaine O\'Breen.",
    ["T"] = "Le trésor englouti",
  },
  [669] = {
    ["D"] = "L\'amiral Seahorn voudrait voir le plus rapidement possible ce que vous avez trouvé.$b$bMais comme vous pouvez le constater, nous sommes un peu coincés ici. Primo la marée est trop basse, deuzio ces damnées créatures ne nous quittent pas des yeux.$b$bVous rendriez un immense service aux Ecumeurs des Flots noirs si vous pouviez prévenir l\'amiral Seahorn à Baie-du-Butin que nous avons découvert le trésor, et que nous sommes en train de le remonter. Prenez cet échantillon en guise de preuve.",
    ["O"] = "Prendre l\'échantillon de Gemme elfique et l\'apporter à l\'amiral Seahorn à Baie-du-Butin.",
    ["T"] = "Le trésor englouti",
  },
  [670] = {
    ["D"] = "Dites, $n, vous semblez aimer l\'aventure. Ma flotte est bloquée à Baie-du-Butin pour cause de ravitaillement.$b$bIl y a des choses qui se trament dans le coin, et j\'ai besoin d\'envoyer une correspondance secrète à Shakes O\'Breen, qui a jeté l’ancre près des côtes d\'Arathi.$b$bPuis-je vous faire confiance pour remettre en toute confidentialité cette missive ?",
    ["O"] = "Remettre la Lettre de Seahorn à Shakes O\'Breen dans les hautes-terres d\'Arathi.",
    ["T"] = "Le trésor englouti",
  },
  [671] = {
    ["D"] = "J\'ai senti quelque chose de bizarre dans l\'air... une chose qui m\'a fait frémir. J\'ai ma petite idée sur ce que c\'est. En essayant de trouver d\'où me venait cette sensation, j\'ai découvert dans l\'ouest, au manoir de Nordclos, des traces résiduelles d\'une invocation démoniaque. A mesure que j\'approchais, mon sang frémissait et la rage montait en moi.$b$bSur le sol, j\'ai trouvé une Amulette de Pierre de sang, qui est généralement utilisée pour les invocations démoniaques. Celle que j\'ai trouvée était déchargée. Il m\'en faut une autre pour l\'examiner, mais je ne retournerai pas dans le manoir. Pouvez-vous m\'en procurer ?",
    ["O"] = "Apporter 10 Amulettes de pierre de sang à Tor\'gan au Trépas-d\'Orgrim.",
    ["T"] = "Magies immondes",
  },
  [672] = {
    ["D"] = "Nous avons essayé de l’installer confortablement, de le dérider, de lui raconter des histoires sur notre majestueuse cité d\'Orgrimmar en Kalimdor, mais rien ne le fait réagir.$b$bLes histoires de notre jeune Chef de guerre Thrall, qui porte l\'armure et le marteau d\'Orgrim mais, peut-être... Peut-être ! Une démonstration de la nouvelle magie chamanique lui donnera de l\'espoir. Ça vaut la peine d\'essayer !$b$bNous pourrions peut-être lui fabriquer un petit quelque chose, oui... Amenez-moi quelques Yeux de raptor, et je vais voir ce que je peux faire.",
    ["O"] = "Récupérer 10 Yeux de raptors des plateaux sur les Arpenteurs des hautes-terres et les Vandales des hautes-terres pour Tor\'gan au Trépas-d\'Orgrim.",
    ["T"] = "L\'éveil de l\'esprit",
  },
  [673] = {
    ["D"] = "Je n\'ai plus aucun doute sur le fait qu\'un puissant démoniste réside dans l\'enceinte de Stromgarde. Régulièrement, en utilisant une relique de magie des arcanes, ce démoniste a invoqué un grand nombre de démons. Nous devons tarir sa source d\'énergie.$B$BTrouvez le démoniste et tuez-le. Ramenez-moi sa source d\'énergie, elle doit être sur lui, et je m\'emploierai à la détruire. Nous verrons alors si le pouvoir de cette chose continue de souiller cette terre...",
    ["O"] = "Trouver l’Orbe de Pierre de sang de Marez Cowl et l’apporter à Tor\'gan au Trépas-d\'Orgrim.",
    ["T"] = "Magies immondes",
  },
  [674] = {
    ["D"] = "Tenez, donnez cette amulette à Gor\'mul, et regardez si la force des raptors peut ranimer la flamme guerrière qui sommeille en lui.",
    ["O"] = "Donner l\'Amulette de griffe de raptor à Gor\'mul.",
    ["T"] = "L\'éveil de l\'esprit",
  },
  [675] = {
    ["D"] = "<Gor\'mul jette l\'amulette au sol.>$b$bTor\'gan vous envoie, c\'est ça ? Peuh ! Pourquoi est-il si miséricordieux avec moi. Ma propre miséricorde m\'a été enlevée le jour où je n\'ai pas pu mourir avec mon Chef de guerre au combat. Un mauvais coup du sort...",
    ["O"] = "Parler à Tor\'gan.",
    ["T"] = "L\'éveil de l\'esprit",
  },
  [676] = {
    ["D"] = "Mauviettes... Tous autant qu\'ils sont. Vous, $c, qu\'est-ce que vous regardez comme ça ? Vous pensez être quelqu\'un de spécial, hein ? Pourquoi ne le prouvez-vous pas ?$B$BL\'avant-poste de Trépas-d\'Orgrim dans les hautes-terres d’Arathi est attaqué. À moins qu\'on leur envoie des renforts rapidement, leur défaite est imminente.$B$BMais avant de vous précipiter là-bas, j\'aimerais vous faire passer un test. Au nord-est de Stromgarde, dans Arathi, il y a une colline habitée par la tribu Rochepoing. Si vous pouvez vous occuper de ces ogres et de leurs massacreurs, allez ensuite faire votre rapport à Drum Fel, à l\'avant-poste du Trépas-d\'Orgrim.",
    ["O"] = "Tuer 8 Ogres Rochepoings et 10 Massacreurs Rochepoings, puis retrouver Drum Fel à l\'avant-poste du Trépas-d\'Ogrim sur les hautes-terres d\'Arathi.",
    ["T"] = "Menace sur le Trépas-d\'Orgrim",
  },
  [677] = {
    ["D"] = "Les trolls Witherbark et les ogres Rochepoings d\'Arathi se sont alliés pour nous éradiquer de cette terre, mais nous ne mourrons pas de leurs mains, ni des mains de personne. Nous allons les tuer et leur montrer que nous aussi possédons notre demeure ici.$B$BLes trolls nous ont refusé leur aide et les ogres, si nous les laissions faire, se serviraient de nous comme nourriture et comme literie. Cela ne sera pas !$B$BNous commençons notre attaque sur les trolls Witherbark, au sud. Tuez-les en masse, et ne revenez que lorsque leur camp sera rempli de leur sang.",
    ["O"] = "Tuer 10 Lanceurs de haches Witherbark, 10 Chasseurs de tête et 8 Sorciers-docteurs, puis retourner voir Drum Fel à l\'avant-poste du Trépas-d\'Orgrim.",
    ["T"] = "L\'appel du combat",
  },
  [678] = {
    ["D"] = "Votre force ne fait aucun doute, $N. Voyons ce que vous valez contre un ennemi digne de ce nom.$B$BLes Rochepoings sont basés au sud-ouest d\'ici, vers le pont des nains, et si je ne me trompe pas, les marteleurs et les magi qui s\'y trouvent représenteront pour vous un grand défi.$B$BTuez-en un maximum et revenez me voir quand vous aurez fini. Et même si vous n\'en revenez pas, je ferai l\'éloge de vos exploits sur le champ de bataille.",
    ["O"] = "Tuer 10 Brutes Rochepoings et 4 Magi Rochepoings, puis retourner voir Drum Fel à l\'avant-poste du Trépas-d\'Orgrim.",
    ["T"] = "L\'appel du combat",
  },
  [679] = {
    ["D"] = "La tête de la bête est prête à tomber... avec grand fracas, $N. Vous avez définitivement prouvé votre valeur au cours de cette dernière bataille. Les commanditaires de ces attaques sont installés dans les ruines de Stromgarde, symbole de la faiblesse humaine.$B$BTrouvez et tuez les seigneurs Rochepoings et leurs laquais chamans, qui essayent de nous repousser hors de nos terres.",
    ["O"] = "Tuer 15 Chamans Rochepoings et 10 Seigneurs Rochepoings et revenir voir Drum Fel à l\'avant-poste du Trépas-d\'Orgrim.",
    ["T"] = "L\'appel du combat",
  },
  [680] = {
    ["D"] = "Vous avez bien servi mon mari, $c, mais il ne connaît pas la véritable cause des attaques contre le Trépas-d\'Orgrim.$B$BOr\'Kalar, le valet de Mug’Thol à Stromgarde, déteste mon mari, et souhaite sa mort depuis qu\'il a été vaincu par lui quelques années auparavant.$B$BJ\'avais le pressentiment qu\'Or\'Kalar tendait un piège à mon mari. Je lui ai donc caché les indices comme quoi il était à l\'origine de ces attaques.$B$BSinon mon mari aurait foncé tête baissée dans le piège… Je voudrais que vous tuiez cette créature et que vous me rameniez sa tête.",
    ["O"] = "Tuer Or\'Kalar et apporter sa Tête à Korin Fel à l\'avant-poste du Trépas-d\'Orgrim.",
    ["T"] = "La réelle menace",
  },
  [681] = {
    ["D"] = "Nous avons combattu pour Stromgarde, et nous avons perdu. Les ogres, le Syndicat et nous, avons divisé notre grande cité et chaque faction s\'est battue contre les autres.$B$BMais nos forces s\'épuisent tandis que celles de nos ennemis se renforcent.$B$BIl nous faut l\'aide de la ville de Southshore, mais l\'approvisionnement reste difficile, car le Syndicat contrôle le territoire entre les deux cités.$B$BAllez au camp du Syndicat situé au manoir de Nordclos et tuez les occupants. Peut-être qu\'ensuite, plus de caravanes de ravitaillement arriveront à destination.",
    ["O"] = "Tuer 10 Malandrins du Syndicat et 6 Mercenaires du Syndicat.$B$BRetourner ensuite alors le capitaine Nials au refuge de l\'Ornière.",
    ["T"] = "Le manoir de Nordclos",
  },
  [682] = {
    ["D"] = "Le Syndicat de Stromgarde est actuellement en guerre contre la milice. Pour inciter la populace à nous affronter aussi, ils ont mis à prix la tête de nos soldats, et donnent de l\'or à tous ceux qui apportent un insigne de Stromgarde pris sur un soldat mort.$B$BNous voulons récupérer ces insignes. Nous ne pouvons tolérer que ces manants gardent des trophées de nos braves soldats tombés sur le champ de bataille !$B$BAllez dans la zone contrôlée par le Syndicat et récupérez les insignes.",
    ["O"] = "Rapporter les Insignes de Stromgarde au capitaine Nials au Refuge de l\'Ornière.",
    ["T"] = "Les insignes de Stromgarde",
  },
  [683] = {
    ["D"] = "$n, j\'ai une petite faveur à vous demander.$b$bPrenez cette lettre et portez-la à notre grand monarque d\'Ironforge, le roi Magni Bronzebeard. Ce n\'est pas grand-chose pour quelqu\'un comme vous qui a tant fait pour l\'Alliance.$b$bMe présenter devant le roi dans l\'état d\'esprit qui est le mien ne servirait pas notre cause. Vous le comprendrez sûrement.",
    ["O"] = "Remettre la lettre de Sara Balloo au roi Magni Bronzebeard à Ironforge.",
    ["T"] = "Requête de Sara Balloo",
  },
  [684] = {
    ["D"] = "La tête de Marez Cowl a été mise à prix. La dernière fois que cette conseillère de haut niveau du Syndicat a été aperçue, c\'était dans la zone de Stromgarde contrôlée par le Syndicat.$B$BLa prime peut être réclamée au capitaine Nials.$B$BAttention: Marez est une démoniste rusée et très adroite, et il est de notoriété publique qu\'elle fricote avec les démons. Débusquez-la avec une extrême prudence.",
    ["O"] = "Apporter la tête de Marez au capitaine Nials, au Refuge de l\'Ornière.",
    ["T"] = "On recherche : Marez Cowl",
  },
  [685] = {
    ["D"] = "La milice de Stromgarde a mis à prix les têtes du seigneur Falconcrest et de son garde du corps Otto. Falconcrest dirige les forces du Syndicat dans les hautes-terres d\'Arathi, et sa mort sèmerait le chaos dans ces forces.$B$BSon garde du corps Otto, bien qu\'il ne constitue pas une cible stratégique, est un adversaire puissant et il a déjà tué nombre de nos défenseurs.$B$BIl faut aller voir le capitaine Nials pour récolter les primes.",
    ["O"] = "Apporter les Têtes de Falconcrest et d\'Otto au capitaine Nials au Refuge de l\'Ornière.",
    ["T"] = "On recherche : Falconcrest et Otto",
  },
  [686] = {
    ["D"] = "Je ne demande pas à ce que les nains comme Madame Balloo comprennent que nous faisons cette guerre pour leur bien. La perte de son mari ainsi que toutes les autres dans le combat au nom de l\'Alliance pèsent lourd sur mes épaules.$b$bMême si je ne peux pas le ramener du Néant distordu, je peux lui rendre hommage.$b$bJe vais faire construire un mémorial en l\'honneur de Sully Balloo, par le maître-maçon Marblesten.$b$bEnvoyez-lui un message, $c. C\'est vous qui serez en charge de faire respecter cet ordre.",
    ["O"] = "Le roi Magni Bronzebeard veut que vous parliez avec le maître-maçon Marblesten.",
    ["T"] = "Hommage royal",
  },
  [687] = {
    ["D"] = "Theldurin l\'Egaré était un membre de notre ordre, le Marteau du crépuscule. Il a perdu l\'esprit et a rejeté nos enseignements. Et avant de s\'enfuir il nous a volé le Parchemin de Myzrael ! Nous n\'avons pas pu le rattraper, mais des rumeurs font état de sa présence dans les Terres ingrates, complètement fou et baragouinant n\'importe quoi.$B$BPour ramener la Dame, vous devez absolument retrouver le parchemin. Et pour cela vous devez retrouver Theldurin.",
    ["O"] = "Trouver Theldurin l\'Egaré.",
    ["T"] = "Theldurin l’Egaré",
  },
  [688] = {
    ["D"] = "<Myzrael parle>$B$BLibre ! JE SUIS LIBRE ! Je suis libre de rassembler mes forces, hors de portée de mes ravisseurs. Si je les rencontrais maintenant, ils seraient sûrement plus forts que moi et me captureraient à nouveau.$B$BEn temps voulu, j\'affronterai les géants, et ils regretteront de m\'avoir bannie !$B$BVous êtes d’une aide précieuse, $N. J\'aurai encore besoin de vous. Quand je serai prête, j\'aurai besoin de vous pour m\'invoquer à la surface.$B$BParlez avec Zaruk au Trépas-d\'Orgrim. Il sait comment m\'invoquer.",
    ["O"] = "Parler à Zaruk au Trépas-d\'Orgrim.",
    ["T"] = "Les alliés de Myzrael",
  },
  [689] = {
    ["D"] = "Donc mon bon roi veut construire un monument en l\'honneur du soldat Sully Balloo ? Et vous me dites que le pauvre homme est mort lors de la tragédie du viaduc de Thandol ?$b$bN\'y a-t-il jamais eu la paix en Azeroth ?$b$bQuoi qu\'il en soit, les ordres du roi sont prioritaires. Pour construire un monument grandiose et durable, j\'ai besoin d\'une pierre spéciale, d\'une grande finesse. Allez sur la Colline de Darrow, dans Hillsbrad et rapportez-moi du Granit d\'Alterac.",
    ["O"] = "Le Maître Maçon Marblesten d\'Ironforge veut 5 Morceaux de Granit d\'Alterac.",
    ["T"] = "Hommage royal",
  },
  [690] = {
    ["D"] = "Bonjour, $N. Vous avez l\'air d\'être $gun aventurier:une aventurière;, et j\'ai un petit travail qui doit être fait.$B$BL\'archimage Trelane, mon ancien maître, a fui depuis peu Stromgarde dans les hautes-terres d\'Arathi, et il m\'a demandé de récupérer quelques objets qu\'il n\'avait pas pu emporter.$B$BLa cité assiégée est tombée, bien qu\'il ait essayé de la défendre. J\'ai déjà des agents sur les hautes-terres, mais ils ont besoin de renfort. Cela vous intéresse ?",
    ["O"] = "Trouver Skuerto au Refuge de l\'Ornière dans les hautes-terres d\'Arathi.",
    ["T"] = "La demande de Malin",
  },
  [691] = {
    ["D"] = "L’archimage Malin nous a envoyés, Skuerto et moi, collecter des ingrédients. Maintenant que vous êtes là, je sais que je n’aurai pas de problèmes pour trouver ces objets.$B$BJ’ai besoin des objets suivants : des défenses de Witherbark, des bourses-médecine Witherbark et un couteau de chasseur d\'ombre.$B$BPourriez-vous me rapporter ces objets ? Les Witherbark vivent au sud-est d’Arathi.",
    ["O"] = "Trouver 10 Défenses de Witherbark, 4 Bourses-médecine Witherbark et un Couteau de Chasseur d\'ombre pour l\'Apprenti Kryten au Refuge de l\'Ornière.",
    ["T"] = "Valoir son poids en or",
  },
  [692] = {
    ["D"] = "Le Parchemin de Myzrael... Le Parchemin de Myzrael ? Vous ne voulez tout de même pas ça ! Sa puissance peut faire revenir Myzrael à la surface du monde, et si vous le faites, on sera tous en danger !$B$BOu alors... il est peut-être déjà trop tard. Peut-être qu\'elle est déjà libre. Peut-être ne fait-elle qu\'attendre en rassemblant ses forces avant de les abattre sur nous. Si c\'est le cas, alors vous avez BESOIN du parchemin !$B$B... Quel dommage, il a été détruit. Un élémentaire enragé l\'a volé et déchiré en morceaux.$B$BSi vous pouvez rassembler les fragments, je pourrai, je pense, le reconstituer...",
    ["O"] = "Apporter le Fragment de Parchemin déchiré, le Fragment de Parchemin fripé et le Fragment de Parchemin noirci à Theldurin l\'Egaré.",
    ["T"] = "Les fragments perdus",
  },
  [693] = {
    ["D"] = "Hé $N ! Désolé d\'avoir été un peu bref, la dernière fois j\'étais pris de court... euh, non ce n\'est pas un jeu de mots.$B$BJ\'ai découvert où le premier objet de Trelane a échoué : c\'est Kor\'gresh qui l\'a au Hall Rochepoing. Pendant que j\'y suis : il jette des éclairs de glace sur ses troupes pour qu\'elles gardent leur position, ce qui prouve qu\'il n\'est pas loin de maîtriser tous les pouvoirs de l\'objet.$B$BVous trouverez le Hall Rochepoing au sud d\'ici puis à l\'est du viaduc de Thandol. Trouvez cet ogre et ramenez-moi la baguette.$B$BPendant que vous serez en chemin, je vais voir ce que je peux faire pour Stromgarde.",
    ["O"] = "Trouver la Baguette d\'invocation de Trelane et revenir voir Skuerto au Refuge de l\'Ornière.",
    ["T"] = "La baguette au poing",
  },
  [694] = {
    ["D"] = "Alors vous êtes d\'attaque ?$B$BVoici la prochaine étape : il y a des ogres tout autour de la Tour d\'Arathor à Stromgarde. Nous devons activer les défenses de la tour si nous voulons avoir une chance contre eux et sauvegarder les objets. Pour cela, nous avons besoin de ces gemmes que les chamans Rochepoings apprécient particulièrement. Ils les ont récupérées à la tour, mais ils ne vont pas les garder très longtemps.$B$BRécupérez une de ces gemmes, et portez-la à Kryten : il connaît le sort pour l\'enchanter afin qu\'elle serve de clef.",
    ["O"] = "Trouver une Agate d\'azur et la porter à l\'Apprenti Kryten au Refuge de l\'Ornière.",
    ["T"] = "Les défenses de Trelane",
  },
  [695] = {
    ["D"] = "Et voilà, $N. Il suffit d\'appuyer la gemme sur un des piédestaux près de la base de la tour, et ses défenses s\'activeront. Utilisez-la pour rentrer dans la tour quand vous serez $gprêt:prête; et allez chercher les autres objets de Trelane. $B$BBonne chance à vous ! Je vous attendrai au camp. Oh ! Parlez à Skuerto avant de partir. Il vous donnera tous les détails nécessaires.",
    ["O"] = "Parler à Skuerto.",
    ["T"] = "Enchantement d\'Apprenti",
  },
  [696] = {
    ["D"] = "Voici ce qu\'il faut récupérer : le Phylactère de Trelane, l\'Orbe de Trelane et l\'Agate de braise de Trelane.$B$BLes trois objets doivent se trouver dans un coffre dans la tour, vous ne devriez pas avoir de problème pour l\'ouvrir ; faites juste attention aux ogres.$B$BLes défenses de la tour ne s\'occuperont pas d\'eux ; vous devez avoir vos armes prêtes. Quand les ogres remarqueront que vous êtes là pour les voler, ils risquent de ne pas apprécier.$B$BSoyez $gprudent:prudente;, et bonne chance.",
    ["O"] = "Trouver le Phylactère de Trelane, l\'Orbe de Trelane et l\'Agathe d\'ambre de Trelane. Les rapporter à Skuerto au Refuge de l\'Ornière.",
    ["T"] = "Attaque sur la tour",
  },
  [697] = {
    ["D"] = "Apportez cette lettre à Stormwind et informez Malin que Kryten sera bientôt de retour. Il vous récompensera pour nous avoir aidés, $N, sans aucun doute.$B$BBon voyage.",
    ["O"] = "Porter la Lettre scellée à l\'archimage Malin dans le quartier des mages de Stormwind.",
    ["T"] = "La demande de Malin",
  },
  [698] = {
    ["D"] = "Je rassemble des provisions pour notre avant-poste près de la plage à l\'est. Tok\'kar fait ce qu\'il peut, mais entre les tours de garde et la cuisine pour ses soldats, ses journées sont déjà bien remplies.$B$BIls aiment cuisiner de la viande de Dents-de-scie quand ils le peuvent, mais je n\'ai pas le temps de leur en trouver une autre cargaison. Cela vous dérangerait de m’aider ?$B$BLes jeunes Dents-de-scie n\'ont pas autant de goût que ceux plus âgés, alors laissez-les en paix un an ou deux pour qu\'ils atteignent leur taille adulte.",
    ["O"] = "Apporter 8 Flancs de Dents-de-scie non préparés à Tok\'Kar dans le marais des Chagrins.",
    ["T"] = "Petit bonus du soir",
  },
  [699] = {
    ["D"] = "En raison de la menace des murlocs le long des côtes, nous sommes stationnés ici pour repousser, ou au pire alerter s\'ils sont trop nombreux, toutes les menaces sur Stonard. Nous n\'avons pas vraiment le temps de respirer, donc si vous le souhaitez, vous pouvez rejoindre nos rangs pour nous aider.$B$BLes Griffes de Mordeurs Dents-de-scie sont un excellent matériau pour fabriquer des armes. Rapportez-m\'en quelques-unes et nous vous donnerons une de nos armes, de la viande fraîche et une bonne compagnie.",
    ["O"] = "Apporter 6 Griffes de Mordeurs Dents-de-scie à Tok\'Kar dans le marais des Chagrins.",
    ["T"] = "Petit bonus du soir",
  },
  [700] = {
    ["D"] = "Ah, Lordaeron fournit une si belle pierre. Il est si dommage que cette terre connaisse une telle confusion.$B$BMais la construction du Mémorial s\'est très bien déroulée ! Je pense que cette nouvelle ravira le Roi.$B$BÀ présent, si vous le voulez bien, veuillez informer Sa Majesté que le Mémorial est prêt, comme il l\'a ordonné.",
    ["O"] = "Rendre compte au Roi Magni Bronzebeard.",
    ["T"] = "Hommage royal",
  },
  [701] = {
    ["D"] = "Avec quelques petites choses en plus, je serai capable de créer un objet qui donnera à Gor\'mul la ruse du raptor ! Avec le pouvoir du raptor en lui, Gor\'mul prendra conscience de notre puissance, et le guerrier qui sommeille en lui se réveillera !$b$bJ\'ai besoin de cœurs de raptors pour créer l\'objet et lancer le sort.",
    ["O"] = "Récupérer 12 Cœurs de raptor provenant de Charognards des hautes-terres pour Tor\'gan.",
    ["T"] = "La ruse du raptor",
  },
  [702] = {
    ["D"] = "Prenez cet orbe et utilisez sa magie pour insuffler à Gor\'mul l\'essence des raptors. Cela va revigorer son esprit et lui redonner goût à la vie. Lorsqu\'il sera comme avant, nous lui ferons un résumé de l\'activité de la Horde, et il comprendra que pour nos incessantes batailles nous avons besoin d\'un grand guerrier.",
    ["O"] = "Donner l\'Orbe de Tor\'gan à Gor\'mul.",
    ["T"] = "La ruse du raptor",
  },
  [703] = {
    ["D"] = "Oh oui ! Mes ailes de busard grillées sont les meilleures ! La sauce est savoureuse et la viande est épicée à souhait. Son odeur attirerait les gens à des kilomètres à la ronde !$B$BJ\'ai tout ce qu\'il faut pour en préparer. Tout... excepté des ailes de busard !$B$BDonc si vous en voulez, apportez-moi des ailes de busard. Apportez m\'en assez, et je vous donnerai même la recette !",
    ["O"] = "Apporter 4 Ailes de busard à Rigglefuzz.",
    ["T"] = "Ailes de busard grillées",
  },
  [704] = {
    ["D"] = "Le dernier rapport que j\'ai reçu d\'Agmond est inquiétant, d\'autant plus qu\'à présent il est mort ! Il dit que son équipe a découvert des \"urnes anciennes\" au site de fouilles en dehors d\'Uldaman. J\'ai peur que les urnes qu\'il y a découvertes n\'aient poussé les troggs à agir, de la même manière que lorsque j\'ai découvert les idoles.$B$BJ\'ai besoin que vous récupériez ces urnes afin que nous les étudions. Allez au site d\'Uldaman, récupérez-les, et revenez me voir.",
    ["O"] = "Apporter 4 Urnes de pierre gravées au Prospecteur Ironband au Loch Modan.",
    ["T"] = "Le destin d\'Agmond",
  },
  [705] = {
    ["D"] = "Les Terres ingrates sont inhospitalières. De dangereux prédateurs et de puissants charognards y pullulent. Effrayant, surtout pour un tout petit gobelin. Pour y survivre, il faut être astucieux !$B$BJe sais comment fabriquer des bombes éclairantes. Je les utilise pour éloigner les bêtes sauvages. Mais je commence à manquer d\'un des ingrédients : de la poudre de perles bleues. Apportez-en et je vous récompenserai.$B$BHé ! J\'espère que vous avez de bonnes bottes. Les perles bleues proviennent de palourdes qui vivent au Récif infâme.$B$BOuaip, le Récif infâme de Strangleronce !",
    ["O"] = "Apporter 9 Perles bleues à Rigglefuzz dans les Terres ingrates.",
    ["T"] = "Pêche aux perles",
  },
  [706] = {
    ["D"] = "En tant qu\'apprenti de Feranor Steeltoe, j\'ai appris à créer des armes dotées d\'un enchantement flamboyant. La dernière fois que j\'ai entendu parler de lui, il travaillait dans une forge à Lordaeron.$b$bJe ne peux pas vous donner beaucoup de détails quant au processus, mais je sais qu\'il requiert du sang d\'un cœur de drake noir qui bat encore. Je n\'ai pas grand-chose à faire en attendant les ordres d\'Ironforge. Si vous me rapportez un tel cœur du ravin de Lethlor, je vous montrerai mes talents !",
    ["O"] = "Récupérer un Cœur de drake noir pour Sigrun Ironhew.",
    ["T"] = "Enchantement flamboyant éclatant",
  },
  [707] = {
    ["D"] = "$N. J\'ai reçu une demande du Prospecteur Ironband : il a besoin de quelqu\'un pour un travail sur un de nos sites.$B$BJe n\'ai pas plus de détails, mais si vous allez voir Ironband, il vous en donnera.",
    ["O"] = "Parler au Prospecteur Ironband, sur le Site de fouilles de Loch Modan.",
    ["T"] = "Ironband a besoin de vous !",
  },
  [708] = {
    ["D"] = "La surface de cette boîte noire métallique est fortement grêlée, comme si elle avait été immergée dans de l\'acide. Elle a dû rester longtemps dans l\'estomac du Condor géant.$B$BAlors que vous retournez la boîte, vous découvrez un symbole gravé à sa base. Sous le symbole, on peut lire :$B$B\"Moteur n°19 - Brigade des sapeurs de Forgefer\"",
    ["O"] = "Apporter la Boîte noire corrodée au Hall des armes, à Forgefer.",
    ["T"] = "La boîte noire",
  },
  [709] = {
    ["D"] = "Oh dieux ! Une malédiction approche. Écoutez-moi ! Une malédiction !$B$BNon, je ne peux pas en parler... je ne peux pas prononcer leur nom, ils pourraient entendre ! Mais si vous voulez savoir, oui si vous voulez en savoir plus sur eux... Alors vous en apprendrez peut-être assez pour les contrecarrer !$B$BPeut-être.$B$BAllez à Uldaman. Non ! Je l\'ai dit ! Vous devez trouver... trouvez la tablette de... Ryun\'eh. Oui, trouvez-la ! Et ne me faites plus prononcer ce nom !$B$BElle est au fond de cet endroit, dans un ancien coffre. C\'est là qu\'elle est cachée... dans un coffre construit bien avant n\'importe quelle construction naine.",
    ["O"] = "Rapporter la Tablette de Ryun\'eh à Theldurin l\'Egaré.",
    ["T"] = "La solution à la malédiction",
  },
  [710] = {
    ["D"] = "Ici, dans les Terres ingrates, mes travaux sur mon dernier projet, des entraves pour élémentaires, sont bloqués.$B$BSi j’aboutis, elles permettront aux mages d\'invoquer des élémentaires très puissants. Pour les finaliser, j\'ai besoin de tester certains de mes nouveaux sorts offensifs sur quelques bidules que j\'ai sous la main. Il me faut donc de petits éclats de pierre.$B$BCes éclats peuvent facilement être récupérés sur les élémentaires de roche inférieurs, à l\'ouest d\'ici. Pouvez-vous m\'en apporter ?",
    ["O"] = "Trouver 10 Petits Eclats de Pierre et les porter à Lotwil Veriatus dans les Terres ingrates.",
    ["T"] = "Étude des éléments : la roche",
  },
  [711] = {
    ["D"] = "$N, vous avez déjà montré plus de capacités que tous mes apprentis réunis.$B$BLes prochaines parties dont j\'ai besoin nécessitent un peu plus de force. On trouve de larges plaques de pierre sur des élémentaires de roche, en étant prudent. Leurs corps sont plus forts et plus grands, et ainsi plus enclins à posséder des morceaux assez importants pour mes tests. Ils se trouvent encore plus à l\'ouest d\'ici, ou bien au sud-est le long des bords des montagnes.$B$BPrenez votre temps, j\'en aurai besoin pour tester ces plus petites parties que vous m\'avez apportées.",
    ["O"] = "Apporter 3 Grands blocs de pierre à Lotwil Veriatus dans les Terres ingrates.",
    ["T"] = "Étude des éléments : la roche",
  },
  [712] = {
    ["D"] = "$N, il y a une dernière chose dont j\'ai besoin en provenance des Terres ingrates : des brassards de liaison de pierre portés par des élémentaires de roche supérieurs. Selon mes calculs, ces brassards me suffiraient pour calibrer mes propres liaisons.$B$BMes divinations pour localiser des élémentaires de roche supérieurs m\'ont permis de trouver un groupe au sud-ouest d\'ici, à côté d\'ogres si je me souviens bien.$B$BRapportez-moi 5 de leurs brassards, comme ça j\'en aurai un peu plus que nécessaire. La fameuse légende comme quoi Servo gâche un peu trop de réactifs...",
    ["O"] = "Apporter 5 Brassards de liaison de pierre à Lotwil Veriatus dans les Terres ingrates.",
    ["T"] = "Étude des éléments : la roche",
  },
  [713] = {
    ["D"] = "Laissez-moi vous apprendre quelque chose sur l\'Huile glaciale : elle a la meilleure capacité de refroidissement pour les embrayages et les moteurs.$B$BSes propriétés sont sans égales, et en plus elle lubrifie à merveille et durablement. Et ce n\'est pas tout : elle aide à maintenir les enchantements sur les objets.$B$BJ\'ai besoin d\'une fiole de cette huile pour augmenter la capacité de mes entraves, afin qu\'elles soient assez puissantes pour protéger l’invocateur de la créature invoquée.$B$BBro\'kin, un de mes amis gobelins, peut vous apprendre à en faire. Il se trouve en Alterac.",
    ["O"] = "Trouver de l\'Huile glaciale et la porter à Lotwil Veriatus dans les Terres ingrates.",
    ["T"] = "La tête froide l\'emporte toujours",
  },
  [714] = {
    ["D"] = "$N, vous avez brillamment accompli votre travail.$B$B<Lotwil est visiblement obnubilé par son travail.>$B$BSi vous venez à Kharanos, n\'hésitez pas à me rendre visite.$B$BOups ! Vite ! Rattrapez le... oui, ça là. Merci.$B$BMaintenant, j\'ai juste besoin d\'une dernière petite chose. C\'est un petit truc, mais c\'est indispensable, j\'en ai peur : un Gyrochronatome.$B$BSi vous pouviez en récupérer un et me l\'apporter, alors je pourrais finir ceci.",
    ["O"] = "Apporter un Gyrochronatome à Lotwil Veriatus dans les Terres ingrates.",
    ["T"] = "Un gyro... quoi ?",
  },
  [715] = {
    ["D"] = "Je pense que les objets que vous avez récupérés pour Lotwil sont le dernier de nos soucis, $N. En revanche, vous avez prouvé que vous étiez $gexpert:experte; en sciences, et ça, c\'est sacré pour Lotwil, donc comment pourriez-vous m\'aider ?$B$BJ\'ai besoin d\'une Potion d\'invisibilité et d\'une Potion de soins ; elles seraient rapidement prêtes s’il daignait arrêter ce qu\'il est en train de faire.$B$BEn échange, je vous donnerai la recette d’une potion que j’ai conçue quand j\'étais l\'élève de Lotwil. Elle est basée sur les recherches qu\'il fait dans les Terres ingrates.",
    ["O"] = "Apporter une Potion d\'invisibilité inférieure et une Potion de soins à Lucien Tosselwrench dans les Terres ingrates.",
    ["T"] = "Pierre liquide",
  },
  [716] = {
    ["D"] = "Je sais à quel point Lotwil peut faire preuve de mesquinerie, $N, aussi laissez-moi vous confectionner quelque chose en échange de tous vos efforts. Cela vous protégera peut-être même de l\'atrocité imminente qu\'est la nouvelle invention de Lotwil.$B$BJ\'ai travaillé avec le surplus des pierres que vous avez trouvées pour lui, et je peux faire de bons brassards que vous pourriez porter. Des brassards enchantés, bien-sûr.$B$BJ\'aurais juste besoin qu\'un forgeron me fasse des brassards guillochés en bronze pour les terminer.",
    ["O"] = "Apporter des Brassards guillochés en bronze à Lucien Tosselwrench dans les Terres ingrates.",
    ["T"] = "Mieux est la pierre que le tissu",
  },
  [717] = {
    ["D"] = "Il est devenu évident que nous ne pouvons plus garantir que Cinglenoir et Hematus resteront prisonniers à Lethlor. Les dragons noirs qui se sont installés là-haut, et les ogres qui projettent de voler le Signe, tout cela nécessite des mesures draconiennes.$B$BLa prison des drakes est scellée grâce à trois pierres runiques qui ne peuvent être retrouvées que par le Signe de la Terre. Ces pierres runiques peuvent être utilisées pour briser le sceau qui les garde enfermés.$B$B$N, faites en sorte qu\'ils ne se réveillent jamais.",
    ["O"] = "Utiliser le Signe de la Terre pour activer les Piliers d\'Améthyste, d\'Opale et de Diamant, et obtenir les Pierres runiques.$B$BPlacer les Pierres runiques dans le Sceau de la Terre pour libérer Cinglenoir et Hematus.$B$BLes tuer, puis rapporter les Liens de Cinglenoir, les Entraves de Hematus et le Signe de la Terre à Garek.",
    ["T"] = "Tremblement de terre",
  },
  [718] = {
    ["D"] = "Dans notre précipitation pour quitter le site de fouilles d\'Uldaman, nous avons été obligés d\'abandonner de nombreux chariots et caisses de fournitures le long du chemin. Les ogres locaux du Camp Kosh se sont précipités sur notre équipement comme des vautours sur une charogne. Ils ne sentent pas meilleur d\'ailleurs. Que la charogne je veux dire !$b$bEnfin bref, ils ont pris une caisse qui contenait toutes nos armes et notre... ben, c\'est un peu embarrassant, nous n\'avons pas pris de nourriture avec nous.$b$bNous sommes dans le pétrin maintenant, pourriez-vous nous aider ?",
    ["O"] = "Retrouver la Caisse de fournitures pour Sigrun Ironhew.",
    ["T"] = "Mirages",
  },
  [719] = {
    ["D"] = "Bonjour, $N. Faites attention par ici. Il y a des nains Sombrefer partout : ils ressemblaient à ceux du clan Ombreforge.$B$BIls viennent juste d\'attaquer le site de fouilles où je travaillais... Ils ont tué pratiquement tout le monde, y compris mon chef, Hammertoe. J\'ai réussi à m\'enfuir, c’est pourquoi je suis encore en vie.$B$BLe site se trouve au nord. Je projette de récupérer nos fournitures et plus particulièrement mon piolet porte-bonheur.$B$BPouvez-vous m\'aider ? L\'un de ces maudits Sombrefer doit avoir mon piolet.",
    ["O"] = "Trouver le Piolet de chance de Ryedol et le rapporter au Prospecteur Ryedol au sud du site de fouilles de Hammertoe.",
    ["T"] = "Un nain et ses outils",
  },
  [720] = {
    ["D"] = "<Des mots sont grossièrement écrits à la craie sur la carte.>$B$B\"Ryedol, $BIls m\'emmènent à Uldaman. Envoie de l\'aide.$B-Hammertoe\"",
    ["O"] = "Trouver le Prospecteur Ryedol et lui dire que Hammertoe Grez est en vie.",
    ["T"] = "Un signe d\'espoir",
  },
  [721] = {
    ["D"] = "$N, l\'avez-vous trouvé ? Avez-vous trouvé Hammertoe à Uldaman ?$B$BSi ces sales Ombreforge le gardent pour le faire parler des anciens Titans, alors tout Ironforge court un grand danger.$B$BDepuis longtemps maintenant, les Sombrefer sont à la recherche de magies pour créer des Golems très puissants. Hammertoe faisait la même chose, mais dans le but de les contrecarrer.$B$BIls le garderont probablement en vie tant qu\'ils ne lui auront pas arraché toutes ses connaissances. Nous devons nous dépêcher de le sauver !",
    ["O"] = "Trouver Hammertoe Grez à Uldaman.",
    ["T"] = "Un signe d\'espoir",
  },
  [722] = {
    ["D"] = "Vous avez du courage… <toux>… pour venir jusqu\'à Uldaman pour moi, $c.$B$BSi vous êtes vraiment là pour aider… le vieux Hammertoe, alors… <toux>… vous devrez me trouver… une amulette.$B$BDeepshadow l’a… Magregan Deepshadow. L\'est dans l\'coin... dans les ruines, c\'est sûr. L\'amulette les mènera... <toux>... droit aux parchemins si on n\'l\'arrête pas. Ça dépend de vous, $N. Je vous en prie... récupérez-la.$B$BLes parchemins aideront sans doute les Sombrefer à créer des Golems encore plus puissants. Je l\'sais, c\'est tout. Je peux le sentir dans mes os.",
    ["O"] = "Trouver l\'Amulette de Hammertoe et lui rapporter à Uldaman.",
    ["T"] = "L\'amulette des secrets",
  },
  [723] = {
    ["D"] = "<Hammertoe tient sa blessure et attend un moment pour reprendre son souffle avant de continuer.>$B$BTrouvez Ryedol, $N. Portez-lui l\'amulette. <toux toux> Il... <toux> saura quoi en faire. Il sait que je recherchais l\'origine de notre peuple, et il... <toux>... sait que c\'était ma mission que d\'arrêter le clan Ombreforge. Ils ne doivent pas s\'emparer de ces parchemins... <toux>",
    ["O"] = "Porter l\'Amulette de Hammertoe au Prospecteur Ryedol dans les Terres ingrates.",
    ["T"] = "Espérance en la foi",
  },
  [724] = {
    ["D"] = "D\'accord, d\'accord... Qui pourrait savoir quoi en faire ?$B$BL\'historien Karnik ! Bien sûr !$B$BIl est à Ironforge. Lui pourra nous aider. Lui et Hammertoe travaillaient ensemble pour retrouver des artefacts perdus à Uldaman.$B$BJe pense que le clan Ombreforge a trouvé deux d\'entre elles et qu\'il en sait plus qu’il n’y paraît.$B$BRacontez à Karnik ce qui est arrivé et donnez-lui l\'amulette. Il vous dira quoi faire ensuite.",
    ["O"] = "Apporter l\'Amulette de Hammertoe à l\'historien Karnik à Ironforge.",
    ["T"] = "Espérance en la foi",
  },
  [725] = {
    ["D"] = "$N, courrez trouver le Conseiller Belgrum aussi vite que vous le pourrez. Dites-lui ce que vous savez et demandez-lui ce que nous devons faire. Cette note lui donnera plus d’information.$B$BPendant ce temps, je vais essayer de contacter l\'esprit de Hammertoe. Peut-être qu\'il sait quelque chose qui pourrait nous aider à mettre un terme aux stupides activités des Ombreforge dans les Terres ingrates. Il faut le souhaiter.",
    ["O"] = "Trouver le Conseiller Belgrum et lui donner la Note.",
    ["T"] = "Avertir de la menace",
  },
  [726] = {
    ["D"] = "<Le Conseiller Belgrum achève de lire la note, et ses yeux s\'élargissent un moment.>$B$BHoula, très mauvaises nouvelles !$B$B$N, Retournez voir l\'Historien Karnik et dites-lui qu\'il agit sous l\'autorité du roi, et qu\'il s\'occupe de cette affaire rapidement et avec fermeté. Il doit mettre un terme aux activités des Ombreforge dans les Terres ingrates.",
    ["O"] = "Parler à l\'Historien Karnik.",
    ["T"] = "Avertir de la menace",
  },
  [727] = {
    ["D"] = "Les tablettes en disent long. Mais pas assez ! Vraiment pas assez !$B$BIl y a un livre, que Yagyin a écrit il y a des années. Les... gens que j\'ai fuis... ils en ont toujours un exemplaire sous la main. Et ils l\'étudient presque tous. Ce livre renferme un immense savoir. Un savoir qui peut nous permettre de nous protéger !$B$BAllez voir Gerrig Bonegrip. Il se trouve dans une boutique à Ironforge, dans la caverne lugubre. Montrez-lui ce glyphe et il pensera que vous êtes un des leurs. Demandez-lui le \"Sommaire de Yagyin\"... et rapportez-le-moi !",
    ["O"] = "Donner le Glyphe du Marteau à Gerrig Bonegrip, dans la caverne lugubre d’Ironforge.",
    ["T"] = "Aller à Ironforge chercher le « Sommaire de Yagyin »",
  },
  [728] = {
    ["D"] = "Les tablettes en disent long. Mais pas assez ! Vraiment pas assez !$B$BIl y a un livre, que Yagyin a écrit il y a des années. Les... gens que j\'ai fuis... ils en ont toujours un exemplaire sous la main. Et ils l\'étudient presque tous. Il comprend un immense savoir. Un savoir qui peut nous permettre de nous protéger !$B$BAllez voir le Gardien Bel\'dugur à Undercity, Lordaeron. Montrez-lui ce glyphe et il pensera que vous êtes un des leurs. Demandez-lui le « Sommaire de Yagyin »... et rapportez-le-moi !",
    ["O"] = "Apporter le Glyphe du Marteau au Gardien Bel\'dugur à Undercity.",
    ["T"] = "Pour le « Sommaire de Yagyin »",
  },
  [729] = {
    ["D"] = "Quel honneur cela a été quand Maître Greywhisker m\'a affecté aux ordres du grand Prospecteur Remtravel. À l\'Académie d\'Ironforge tout le monde connaît les grandes découvertes de Remtravel.$b$bMais le Prospecteur est plutôt... hum... inconscient... de son environnement.$b$bNous avons découvert les traces d’une grande société. D\'horribles Golems sont sortis du sol et ont envahi le site. Remtravel n\'a pas semblé s\'en apercevoir. J\'ai fui à Auberdine pour demander de l\'aide.$b$bS\'il vous plaît, allez vers le sud pour voir si le prospecteur va bien !",
    ["O"] = "Aller vers le sud et vérifier l\'état de santé du Prospecteur Remtravel.",
    ["T"] = "Les absences du prospecteur",
  },
  [730] = {
    ["D"] = "Il est plaisant de voir qu\'un $C s\'intéresse à l\'archéologie et aux grandes merveilles de notre monde.$B$BBien souvent, notre métier est considéré comme un simple passe-temps par nos amis de l\'Alliance. Mais ce que la plupart d\'entre eux ne comprennent pas, c\'est que les récentes découvertes dans Khaz Modan ont prouvé que de grandes et mortelles puissances menacent tout Azeroth, de Lordaeron à Kalimdor.$B$BJe suis vraiment inquiet pour le groupe que j\'ai envoyé à Sombrivage. Je suis sans nouvelles de lui depuis des semaines.$B$BAllez à Auberdine et cherchez des indices sur ce qu\'il lui arrive.",
    ["O"] = "Aller à Auberdine et chercher des indices sur les nains de l\'équipe de fouilles.",
    ["T"] = "Des problèmes à Sombrivage ?",
  },
  [731] = {
    ["D"] = "Alors, Hollee... Oh c\'est vrai ! Vous n\'êtes pas Hollee. Où est Hollee ?$b$bBah, ce n\'est pas le problème ! Nous devons trouver ce pinceau à pierre... enfin je veux dire ce mystérieux fossile ! Nous devons le trouver. La Ligue des Explorateurs à Darnassus veut savoir sur quoi je travaille.$b$bDites-moi quand vous serez d\'accord et nous partirons à la recherche de ce pinceau.... je veux dire de ce fossile ! Nous trouverons peut-être quelque chose d\'intéressant à écrire à la Ligue.$b$bAlors on y va ?",
    ["O"] = "Protéger le Prospecteur Remtravel pendant qu\'il recherche le mystérieux fossile, puis revenir voir l\'Archéologue Hollee à Auberdine.",
    ["T"] = "Les absences du prospecteur",
  },
  [732] = {
    ["D"] = "Sigrun m\'a expliqué que vous l\'aviez beaucoup aidé. Vous pourrez peut-être aussi m\'aider. Je suis envoyé par mon maître, Krasus de Kirin Tor, pour mettre fin aux problèmes dans le ravin de Lethlor.$b$bJe ne peux pas divulguer la nature exacte de ma mission, ni la tâche que je dois accomplir, mais elle est de première importance.$b$bUne bande d\'ogres errants, qu\'on a vu parfois près de Camp Boff, a volé un objet appelé le Signe de la Terre, que je dois récupérer. Si vous me le rapportez, je vous en dirai plus.",
    ["O"] = "Retrouver le Signe de la Terre pour Garek.",
    ["T"] = "Tremblement de terre",
  },
  [733] = {
    ["D"] = "Cela va devenir difficile de se rendre ici si je ne peux pas me procurer du matériel pour construire des défenses et les choses nécessaires. J\'ai remarqué que les ogres de la région semblaient bien équipés en morceaux de métal. Peut-être que les légendes à propos d’engins de siège dans les Terres ingrates ne sont pas si loin de la vérité finalement...$b$bQuoi qu\'il en soit, le Prospecteur s\'inquiète de l\'état de notre petit camp, et j\'aimerais commencer à y travailler dès que possible.$b$bLe camp principal des ogres est situé dans la grotte de Crache-poussière, assez loin d\'ici en direction de l\'ouest.",
    ["O"] = "Collecter 7 Morceaux de métal pour Sigrun.",
    ["T"] = "Demandes variées",
  },
  [734] = {
    ["D"] = "Merveilleux, merveilleux.$B$B<Lotwil lève les yeux vers vous un instant avant de reprendre son travail.>$B$BAllez parler à Lucien, $N. J\'ai besoin d\'un rouage n° 5. Voulez-vous bien me l\'apporter ?",
    ["O"] = "Parler à Lucien Tosselwrench dans les Terres ingrates.",
    ["T"] = "Grandes difficultés",
  },
  [735] = {
    ["D"] = "Le \"Sommaire de Yagyin\", hein ? Vous allez établir une nouvelle branche de notre ordre, quelque part ? Laissez tomber, je n\'ai pas à le demander.$B$BJe vais vous aider, j\'en ai une copie en plus. On doit s\'entraider, n\'est-ce pas ? C\'est comme cela que nous prospérons.$B$BJ\'ai besoin de trois objets de puissance. Apportez-les-moi et je vous donnerai le livre.$B$BJe ne vais pas vous donner les noms de vive voix, les murs ont des oreilles, mais je vais vous les écrire.",
    ["O"] = "Rapporter les Objets écrits sur le mot de Bonegrip à Gerrig Bonegrip dans Ironforge.",
    ["T"] = "L\'étoile, la main et le cœur",
  },
  [736] = {
    ["D"] = "Vous avez besoin du « Sommaire de Yagyin » ? Vous projetez donc de réunir une nouvelle cellule ? Bien sûr que c\'est le cas... et bien sûr que je vais vous aider, $gfrère:sœur;.$B$BMais vous devez m\'aider aussi.$B$BJ\'ai besoin de trois objets de puissance. Apportez-les-moi et je vous donnerai le livre.$B$BJe ne peux pas prononcer leurs noms, mais je peux vous les écrire.$B$BApportez-moi les objets de la liste. Si vous pouvez me les obtenir, alors vous méritez de créer une nouvelle branche de notre ordre.",
    ["O"] = "Apporter les objets de la liste de Bel\'dugur au Gardien Bel\'dugur à Undercity.",
    ["T"] = "L\'étoile, la main et le cœur",
  },
  [737] = {
    ["D"] = "J\'ai le livre pour vous, $N. Je n\'ai pas besoin de vous prévenir de bien y faire attention. Et de le garder hors de portée de nos ennemis. $B$BCe livre contient de nombreuses vérités...$B$B... Et la vérité peut vous consumer.",
    ["O"] = "Porter le « Sommaire de Yagyin » à Theldurin.",
    ["T"] = "Le savoir perdu",
  },
  [738] = {
    ["D"] = "Le Prospecteur Agmond avait la charge de nos sites de fouilles dans les Terres ingrates, au sud-ouest. Je dis \"avait\" car… en fait, cela fait des semaines que je n\'ai pas entendu parler de lui. J\'ai besoin que vous le trouviez pour me ramener son rapport.$B$BAux dernières nouvelles, il se trouverait au site de fouilles le plus au sud des Terres ingrates, un minuscule site au-delà de la Forteresse d\'Angor. Si j\'étais vous, je commencerais mes recherches là-bas.",
    ["O"] = "Trouver Agmond.",
    ["T"] = "Trouver Agmond",
  },
  [739] = {
    ["D"] = "Les traces d\'Agmond partent de l\'ouest. Les traînées de sang sur la pierre et sur le sable vous indiquent que le nain a rampé sur une certaine distance.$B$BEt il est mort ici.$B$BUn examen plus approfondi vous fait remarquer le mot \"Murdaloc\", tracé sur un tas de sable des Terres ingrates.$B$BTracé près de la main morte d\'Agmond.",
    ["O"] = "Tuer l\'assassin d\'Agmond : Murdaloc.$B$BTuer 12 Briseurs d’os Cavepierre.$B$BEt revenir voir le Prospecteur Ironband au Loch Modan.",
    ["T"] = "Murdaloc",
  },
  [741] = {
    ["D"] = "Alors le prospecteur veut envoyer le mystérieux fossile à la Ligue des Explorateurs à Darnassus ? Quitter Sombrivage sans lui… ce n\'est pas bien.$b$bVoilà, $n, apportez le mystérieux fossile au Chef Archéologue Greywhisker à Darnassus.",
    ["O"] = "Apporter le Mystérieux fossile au Chef archéologue Greywhisker, à Darnassus.",
    ["T"] = "Les absences du prospecteur",
  },
  [742] = {
    ["D"] = "Votre attention ! Les terres sauvages d\'Ashenvale vous attendent tous !$B$BLa Horde s\'est établie dans les terres au nord des Tarides. Nos deux avant-postes : le Poste de Bois-brisé et le Poste de la grève de Zoram s\'efforcent bravement d\'apporter la gloire à la Horde ! Ceux et celles qui veulent faire montre de leur courage devraient y trouver conseil. De plus, Senani Thunderheart, au Poste de Bois-brisé, directement au nord des Tarides, cherche en ce moment des candidats prêts à prendre part à une grande Chasse d\'Ashenvale !",
    ["O"] = "Parler à Senani Thunderheart, au Poste de Bois-brisé, à Ashenvale.",
    ["T"] = "La Chasse d\'Ashenvale",
  },
  [743] = {
    ["D"] = "Merci de prendre le temps de me parler, $N. Je suis Ruul, guerrier et professeur.$B$BVous avez atteint l\'âge où il vous faut vous préparer à de dures épreuves si vous comptez vous aventurer hors de Mulgore.$B$BSi vous voulez éprouver votre force, commencez par aller chasser les harpies Furie-des-vents au sud-est. Elles nichent le long des crêtes des montagnes, loin de la route.$B$BElles sont l\'un de nos ennemis naturels, ici à Mulgore. Les affronter éprouvera vos compétences.",
    ["O"] = "Apporter 8 Serres de Furie-des-vents à Ruul Eagletalon, au village Bloodhoof.",
    ["T"] = "Le danger des Furies-des-vents",
  },
  [744] = {
    ["D"] = "$N, mon frère doit bientôt se présenter devant le chef Bloodhoof et il est de mon devoir de lui faire une coiffe digne de ce nom.$B$BJe voudrais vous demander une faveur, pendant que je finis de tanner ces lanières de cuir. Je n\'ai, hélas, pas eu le temps de collecter suffisamment de plumes et je me demandais si vous pouviez en rassembler quelques-unes pour moi ?$B$BVous pouvez trouver des plumes de bonne taille sur les harpies qui vivent loin au nord et au nord-ouest de Thunder Bluff. Je n\'ai besoin que de 6 plumes d\'azur et de 6 plumes de bronze.",
    ["O"] = "Collecter 6 Plumes d\'azur et 6 Plumes de bronze et les rapporter à Eyahn Eagletalon à Thunder Bluff.",
    ["T"] = "Préparation à la cérémonie",
  },
  [745] = {
    ["D"] = "$N, il y a beaucoup de conflits qui ont dévasté cette terre. J\'espère seulement que vous n\'aurez pas à être le témoin du même nombre de batailles que je le fus, moi-même. Il y a pourtant une chose que je voudrais que vous fassiez ; cela vous montrera comment une menace, même minime, peut blesser ce pays, si elle n\'est pas endiguée à temps.$B$BDes crins-pâles gnolls ont colonisé le sud de Bloodhoof et une grotte à l\'ouest. Ils ne répondent pas à nos tentatives de communication et massacrent les animaux sauvages de Mulgore.$B$BAvec cette engeance, la diplomatie n\'est, hélas, plus une solution satisfaisante.",
    ["O"] = "Tuer 10 Tanneurs crins-pâles, 8 Ecorcheurs crins-pâles et 5 Braconniers crins-pâles, puis retourner voir Baine Bloodhoof, au village Bloodhoof.",
    ["T"] = "Partager la terre",
  },
  [746] = {
    ["D"] = "Il y a des nains qui préparent des fouilles au nord-ouest. Ils pensent que la terre abrite des secrets et ils ont raison. Mais creuser et souiller le pays n\'est pas un bon chemin vers la sagesse.$B$BCollectez les outils de forage des nains, fracassez-les et revenez avec les outils brisés.$B$BVous ne pourrez détruire les outils que dans une forge. Vous en trouverez une à Thunder Bluff, bien que n\'importe quelle forge (même une forge dans le camp des nains) suffise à la tâche.",
    ["O"] = "Collecter 5 Piolets de prospecteur.$B$BÀ la forge, brisez les Piolets du prospecteur pour obtenir des Outils cassés.$B$BRapporter 5 Outils cassés à Baine Bloodhoof, au village Bloodhoof.",
    ["T"] = "Les fouilles des nains",
  },
  [747] = {
    ["D"] = "Vous avez un air prometteur, et vous allez avoir l\'occasion de prouver votre valeur au sein de la tribu. Peut-être le jour est-il proche où l\'on vous accueillera dans la grande ville de Thunder Bluff. Mais avant, vous devrez démontrer votre valeur à mon père, le Chef Hawkwind.$b$bIci, à la Mesa de Nuage rouge, nous sommes fiers de nos talents de chasseurs. Les taurens chassent par nécessité et par goût du sport. Notre stock de viande a diminué et nous avons besoin de plumes pour nos vêtements. Chassez les trotteurs des plaines du voisinage et faites vos preuves en réapprovisionnant le village.",
    ["O"] = "Grull Hawkwind, à Camp Narache voudrait que vous lui apportiez 7 Plumes de trotteurs des plaines et 7 morceaux de Viande de trotteur des plaines.",
    ["T"] = "La chasse commence",
  },
  [748] = {
    ["D"] = "Des gobelins et leurs serviteurs ont pollué nos puits sacrés ! Nous ne pouvons tolérer cela.$B$BPour assainir les puits, je dois créer un totem de purification que vous devrez porter au puits, afin de pratiquer le rituel de purification.$B$BD\'abord nous allons fabriquer le totem de purification pour le puits Winterhoof. Pour ce faire, le pays doit offrir son aide. Chassez des loups des prairies pour leurs pattes et des trotteurs des plaines adultes pour leurs serres. Vous les trouverez au sud-ouest.$B$BRevenez avec les pattes et les serres, $N.",
    ["O"] = "Apporter 6 Pattes de loup des prairies et 4 Serres de trotteur des plaines à Mull Thunderhorn à Bloodhoof.",
    ["T"] = "Eau empoisonnée",
  },
  [749] = {
    ["D"] = "Il y a quelques jours, nous avons intercepté une caravane de la KapitalRisk qui se déplaçait sous escorte sur les rives du lac Taureau-de-pierre. Nous avions déjà perdu deux des nôtres dans une bataille antérieure ; aussi avons-nous attaqué sans hésiter. La caravane fut dévastée.$b$bNous avons laissé leurs caisses de provisions sur place, pensant que nos torches détruiraient toute chose de valeur. Mais comme des sauveteurs de la KapitalRisk ont été repérés à côté des caisses brûlées, il se peut que nous nous soyons trompés.$b$bPeut-être pourriez-vous aller sur place et examiner le contenu de ces caisses.",
    ["O"] = "Examiner le contenu d\'une caisse de provisions à la caravane dévastée, pour Morin Cloudstalker.",
    ["T"] = "La Caravane dévastée",
  },
  [750] = {
    ["D"] = "Un tauren compétent dans l\'art de la chasse sait que ses proies ne sont pas de simples trophées. Les animaux des plaines nous fournissent ce dont nous avons besoin pour survivre. Vous ferez grosse impression sur les plus âgés si vous réussissez à revenir avec quelques-unes de ces précieuses Peaux de puma des montagnes. Vous pourrez trouver ces bêtes dans les collines plus au sud.$b$bNos enfants ont besoin de vêtements et nos tentes d\'être raccommodées.",
    ["O"] = "Grull Hawkwind, à Camp Narache, voudrait que vous lui apportiez 10 Peaux de puma des montagnes.",
    ["T"] = "La chasse continue",
  },
  [751] = {
    ["D"] = "L\'ouverture de la caisse révèle un assortiment de minerais divers, soigneusement empaquetés. Ceci est une preuve supplémentaire de l\'activité minière de la KapitalRisk dans Mulgore. Sur les minerais, vous récupérez des rouleaux de papier, étroitement ficelés.$B$BLa-haut, dans les collines, vous pouvez presque entendre les bruits des outils de forage qui fendent les roches et voir la fumée des gigantesques feux d\'alimentation.",
    ["O"] = "Rapporter les Documents de la KapitalRisk à Morin Cloudstalker, à côté du village Bloodhoof.",
    ["T"] = "La Caravane dévastée",
  },
  [752] = {
    ["D"] = "Tous les membres de la tribu prennent part à l\'harmonie naturelle de la vie. Nous vivons et travaillons tous ensemble.$b$bNotre engagement les uns pour les autres implique une grande responsabilité envers autrui. Je voudrais vous demander une humble contribution.$b$bMa mère est partie ce matin pour tirer et rapporter de l\'eau d\'un puits qui est au sud-est de Narache. Le temps passe et elle n\'est toujours pas rentrée. Peut-être pourriez-vous aller à sa rencontre ? Je suis, hélas, retenu ici par les affaires de la tribu.",
    ["O"] = "Partir chercher la mère du Chef Hawkwind, près du puits, au sud-est de Camp Narache.",
    ["T"] = "Une humble contribution",
  },
  [753] = {
    ["D"] = "J\'en ai vu et parcouru du pays, dans ma vie. Ces jambes fatiguées n\'ont plus la vigueur d\'antan, mais je peux encore servir la tribu... en prenant le temps qu\'il faut.$b$bVous avez l\'air d\'un de ces $cs enthousiastes. Voici de quoi tester votre jeunesse et votre vitalité : prenez une cruche d\'eau et apportez-la à mon fils, le Chef, à Camp Narache.$b$bRappelez-vous que même la tâche la plus humble peut parfois vous attirer la reconnaissance des aînés.",
    ["O"] = "Prendre une Cruche d\'eau du puits.$b$bApporter la cruche au Chef Hawkwind, à Camp Narache, au nord-ouest du puits.",
    ["T"] = "Une humble contribution",
  },
  [754] = {
    ["D"] = "J\'ai réalisé le totem pour purifier le puits Winterhoof. Il vous faut maintenant l\'apporter au puits et y pratiquer le Rituel de purification. Vous trouverez le puits dans le sud-est, gardé par d\'infects gobelins !$B$BCe sera tout sauf une mission facile mais vous devez réussir, si nous voulons un jour pouvoir boire de nouveau l\'eau précieuse de ce puits.$B$BBonne chance à vous, $N.",
    ["O"] = "Utiliser le Totem de purification de Winterhoof au puits Winterhoof, puis retourner voir Mull Thunderhorn.",
    ["T"] = "Purification de Winterhoof",
  },
  [755] = {
    ["D"] = "La volonté dont vous avez fait preuve en acceptant cet humble travail pour les taurens de Narache, ainsi que votre goût pour l\'apprentissage révèlent la noblesse de votre caractère, $n. Je suis sûr qu\'un jour vous serez $gconnu:connue; à Thunder Bluff comme l\'un des $cs les plus valeureux.$b$bAvant cela, vous serez mis à l\'épreuve des trois Rites de la Terre-mère.$b$bLa première épreuve est le Rite de force. Allez voir le Prophète Graytongue et dites-lui que le Chef Hawkwind vous envoie.$b$bVous trouverez la demeure du Prophète directement au sud de Camp Narache.",
    ["O"] = "Aller voir le Prophète Graytongue qui vit dans les collines au sud de Camp Narache.",
    ["T"] = "Les Rites de la Terre-mère",
  },
  [756] = {
    ["D"] = "Pour créer le Totem de purification de Thunderhorn, vous devez collecter les griffes des prédateurs qui hantent les plaines de Mugore. Chassez les traqueurs des prairies et les pumas des basses plaines pour leurs griffes, puis revenez me voir.$B$BVous trouverez les traqueurs des prairies et les pumas des basses plaines à l\'est et à l\'ouest.",
    ["O"] = "Rapporter 6 Griffes de Traqueur et 6 Griffes de puma à Mull Thunderhorn.",
    ["T"] = "Le Totem de Thunderhorn",
  },
  [757] = {
    ["D"] = "Les rites de la Terre-mère sont un passage obligé pour tout jeune tauren qui se respecte, à Thunder Bluff.$b$bLa première épreuve est le rite de Force. Vous devrez y démontrer votre bravoure en tuant des ennemis de la tribu.$b$bLes Dos-hirsute du ravin de Roncelame à l\'est, empiètent sur nos terres tribales. Ils nous tendent des embuscades lors de nos parties de chasse et volent dans les villages, à la nuit tombée.$b$bProuvez votre valeur en massacrant ces vauriens et retournez voir le Chef, à Camp Narache, avec leurs ceintures pour prouver que vous les avez tués.",
    ["O"] = "Tuer des Dos-hirsute dans le Ravin de Roncelame et apporter 12 Ceintures de Dos-hirsute au chef Hawkwind au camp Narache.",
    ["T"] = "Le Rite de force",
  },
  [758] = {
    ["D"] = "Le totem est prêt, $N. Il vous faut encore pratiquer le Rituel de purification.$B$BAllez au puits Thunderhorn, au nord du village et utilisez le Totem de purification de Thunderhorn pour le rituel. Revenez me voir quand cela sera fait.$B$BQue les esprits soient avec vous.",
    ["O"] = "Utilisez le Totem de purification de Thunderhorn au puits Thunderhorn, puis retournez voir Mull.",
    ["T"] = "Purification de Thunderhorn",
  },
  [759] = {
    ["D"] = "Il ne reste qu\'un puits à purifier, appelé puits Wildmane, d\'après le clan du même nom. Pour nettoyer ce puits, nous aurons besoin des dents d\'un redoutable prédateur : le loup alpha des prairies. Allez chasser ces loups au nord, puis revenez me voir lorsque vous en aurez fini.$B$BLes alphas rôdent souvent autour de notre majestueuse cité de Thunder Bluff.",
    ["O"] = "Apporter 8 Dents de loup alpha des prairies à Mull Thunderhorn, au village Bloodhoof.",
    ["T"] = "Le Totem de Wildmane",
  },
  [760] = {
    ["D"] = "Votre purification des puits Winterhoof et Thunderhorn est objet de discussion et de fierté parmi les esprits, jeune $N. Le totem de Wildmane est construit et le devoir vous appelle, encore une fois.$B$BLe puits Wildmane se trouve au nord de Thunder Bluff. Pratiquez-y le rituel ! Débarrassez le pays du poison des gobelins. Que l\'eau claire coule de nouveau !",
    ["O"] = "Utiliser le Totem de purification de Wildmane au puits Wildmane, puis retournez voir Mull Thunderhorn.",
    ["T"] = "Purification de Wildmane",
  },
  [761] = {
    ["D"] = "Le rapace est un oiseau rusé, difficile à trouver et à chasser, qui plus est. Une collection de plumes de rapace est signe d\'intelligence et de détermination pour un chasseur.$B$BSi vous êtes $gprêt:prête; à entreprendre ce travail, pénétrez les plaines et chassez les rapaces.$B$BRapportez-moi leurs plumes et faites honneur à votre clan.$B$BVous trouverez les rapaces partout dans Mulgore, mais vos yeux doivent être affûtés pour les localiser et vos jambes, agiles pour les attraper.",
    ["O"] = "Apporter 8 Trophées de plumes de Rapace à Harken Windtotem, au village Bloodhoof.",
    ["T"] = "Chasse aux Rapaces",
  },
  [762] = {
    ["D"] = "L\'Ambassadeur Infernus a été envoyé à la Forteresse d\'Angor par Ragnaros lui-même, pour suivre les recherches des artefacts perdus des Ombreforge à Uldaman.$B$BCela a déjà coûté d\'énormes pertes à notre peuple et la situation empire encore. Je n\'ose imaginer combien d\'artefacts ils pourraient rassembler et utiliser contre Ironforge.$B$BNotre objectif est simple, $c. Retournez dans les Terres ingrates et tuez l\'Ambassadeur Infernus. Une fois la menace éliminée, revenez voir le Conseiller Belgrum, avec les preuves de votre victoire.$B$BTout est entre vos mains, à présent.",
    ["O"] = "Tuer l\'Ambassadeur Infernus, à la Forteresse d\'Angor, et apporter la preuve de sa mort au Conseiller Belgrum à Ironforge.",
    ["T"] = "Un ambassadeur du mal",
  },
  [763] = {
    ["D"] = "Il vous faut encore subir deux épreuves pour achever les Rites de la Terre-mère. Il est temps pour vous d\'élargir votre expérience, $n.$b$bVoyagez jusqu\'au Village Bloodhoof et trouvez le Chef, Baine Bloodhoof, fils de Cairne. Là, vous devrez continuer votre périple et gagner l\'approbation des aînés de Thunder Bluff.$b$bPortez ce totem à Baine ; il reconnaîtra mes gravures et vous aidera dans votre quête.$b$bSuivez la route hors de Camp Narache et hâtez vous. Ne vagabondez pas de-ci de-là, vous vous perdriez.",
    ["O"] = "Apporter le Totem de Hawkwind à Baine Bloodhoof au village Bloodhoof. Suivre la route hors du camp Narache.",
    ["T"] = "Les Rites de la Terre-mère",
  },
  [764] = {
    ["D"] = "Ceci explique l\'accroissement des effectifs et des équipements de la KapitalRisk à Mulgore. Ces gobelins... leur nombre augmente bien trop vite. Comme on dit : quand on montre quelque chose à un gobelin, les bruits de la monnaie et du profit ne tardent pas à retentir.$b$bLes avides petites créatures... malheureusement pour eux, nous, les taurens, ne permettrons pas à leur entreprise de se développer sur nos terres. Allez à leur mine, au nord de la caravane dévastée, et portez leur un message.",
    ["O"] = "Tuer 14 Travailleurs de la KapitalRisk et 6 Superviseurs de la KapitalRisk pour Morin Cloudstalker du village Bloodhoof.",
    ["T"] = "La KapitalRisk",
  },
  [765] = {
    ["D"] = "A en croire les documents que vous avez sauvés de la Caravane, la KapitalRisk a conçu plusieurs projets pour nous chasser de nos terres afin de pouvoir les piller et les exploiter en toute liberté.$b$bJe suis épouvanté de voir à quelles extrémités ces odieuses créatures sont prêtes à en venir pour accomplir leurs desseins. Le cerveau qui est à l\'origine de leurs plans doit être tué. Son nom est Superviseur Fizsprocket et vous le trouverez à la mine de la KapitalRisk, à l\'est de la Caravane dévastée. Rapportez-moi son écritoire ; peut-être nous apprendra-t-il quelque chose.",
    ["O"] = "Tuer le Superviseur Fizsprocket et rapporter son Ecritoire à Morin Cloudstalker, au village Bloodhoof.",
    ["T"] = "Superviseur Fizsprocket",
  },
  [766] = {
    ["D"] = "Mon grand-père m\'a conté sa bataille avec la créature nommée Mazzranache. Il m\'a parlé de ses yeux rouges, démoniaques, de ses serres aiguisées comme des rasoirs et de sa morsure venimeuse.$b$bJ\'ai eu la malchance de rencontrer la bête tandis que je cheminais à travers les plaines. Elle était exactement comme mon grand-père me l\'avait décrite ; elle m\'a attaqué et m\'a profondément mordu à l\'épaule. Son poison coule à présent dans mes veines et j\'aurais besoin d\'ingrédients rares pour réaliser un antidote : un coeur de loup, un fémur de puma, une écaille de trotteur des plaines et un gésier de rapace.$b$bHâtez-vous, le temps m\'est compté.",
    ["O"] = "Apporter un Coeur de loup des plaines, un Fémur de puma des basses plaines, une Ecaille de trotteur des plaines et un Gésier de rapace à Maur Raincaller, au village Bloodhoof.",
    ["T"] = "Mazzranache",
  },
  [767] = {
    ["D"] = "Réussir le Rite de vision, un des rites de la Terre-mère, vous attirera le respect des aînés de Thunder Bluff.$b$bNotre peuple a compris que la terre était pourvoyeuse de ressources. Pour faire l\'expérience de la vision rituelle, vous devez parler au chef spirituel du village, Zarlman Deux-Lunes.",
    ["O"] = "Parler à Zarlman Deux-Lunes, au village Bloodhoof.",
    ["T"] = "Le Rite de vision",
  },
  [768] = {
    ["D"] = "Salut, $gmon:ma; jeune $gami:amie; ! Votre oeil brille d\'une flamme farouche et je vois que vous avez hâte de retourner dans les plaines pour chasser. Puissiez-vous faire l\'honneur de votre clan !$B$BJe travaille les peaux de bêtes ; j\'en fais des vêtements et des armures pour les gens de Thunder Bluff.$B$BSi vous alliez chassez des bêtes et m\'en rapportiez les peaux, je pourrais vous confectionner quelque chose.",
    ["O"] = "Apporter 12 pièces de Cuir léger à Veren Tallstrider, à Thunder Bluff.",
    ["T"] = "La chasse aux peaux",
  },
  [769] = {
    ["D"] = "Les kodos de Mulgore sont puissants et robustes, qualités que nous respectons beaucoup. Si vous savez travailler le cuir et souhaitez apprendre à confectionner un sac en peau de kodo, apportez-moi les fournitures nécessaires.$B$BFaites cela et je partagerai mes connaissances avec vous.",
    ["O"] = "Apporter 4 pièces de Cuir léger et 4 Bobines de fil grossier à Veren Tallstrider, à Thunder Bluff.",
    ["T"] = "Sac en peau de kodo",
  },
  [770] = {
    ["D"] = "Cette peau déchiquetée de Hurleur fantomatique porte toujours la terrible blessure reçue lors de la bataille des loups contre la Légion ardente.$B$BPeut-être quelqu\'un au village Bloodhoof sait-il quelque chose au sujet de la malédiction du Hurleur fantomatique...",
    ["O"] = "Trouver quelqu\'un qui puisse vous en apprendre davantage au sujet du Hurleur fantomatique.",
    ["T"] = "La Cape de démon balafrée",
  },
  [771] = {
    ["D"] = "Pour passer le Rite de vision, vous devez collecter les ingrédients nécessaires à la réalisation de l\'Eau des Prophètes.$b$bJ\'aurai besoin de Pierres de puits, qui se trouvent autour des puits, à Mulgore. Je les combinerai aux pommes de pin, qui tombent des arbres majestueux de notre pays.$b$bCes ingrédients sont porteurs de grands principes magiques ; quand vous en consommerez les décoctions devant le feu tribal, leurs essences vous donneront une vision. Suivez-la et vous accéderez à l\'étape suivante de votre quête sacrée.",
    ["O"] = "Collecter 2 Pierres de puits et 2 Pommes de pin et les rapporter à Zarlman Deux-Lunes, au village Bloodhoof.",
    ["T"] = "Le Rite de vision",
  },
  [772] = {
    ["D"] = "Vous allez maintenant être $gconfronté:confrontée; à l\'Eau des Prophètes.$B$BQuand vous vous sentirez $gprêt:prête;, consommez-la en face du feu tribal. Une fois bue l\'eau sacrée, la vision se matérialisera rapidement devant le feu.$B$BA ce moment-là, vous serez $gseul:seule; avec votre destin...",
    ["O"] = "Consommer l\'Eau des Prophètes devant le feu tribal, au village Bloodhoof et suivre la vision quand elle apparaîtra.",
    ["T"] = "Le Rite de vision",
  },
  [773] = {
    ["D"] = "Pour gagner l\'approbation des aînés de Thunder Bluff, vous devez désormais réussir le Rite de sagesse.$b$bMaintenant que vous avez achevé le Rite de vision, les esprits ancestraux des Rochers rouges vous donneront la bénédiction des ancêtres. Seuls ceux qui ont bu l\'Eau des Prophètes accèdent à cette bénediction.$b$bVoyagez à l\'est de Thunder Bluff, jusqu\'aux Rochers rouges et cherchez l\'Esprit ancestral, $n.",
    ["O"] = "Voyager aux Rochers rouges, à l\'est de Thunder Bluff et parler à Esprit ancestral.",
    ["T"] = "Le Rite de sagesse",
  },
  [774] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [775] = {
    ["D"] = "Vous allez maintenant être $grévéré:révérée; dans notre grande ville. Chaque $r qui passe avec succès les Rites de la Terre-mère a droit à cet honneur.$b$bPour votre quête finale, trouvez notre noble chef, Cairne Bloodhoof, qui demeure au sommet de la plus haute mesa de Thunder Bluff.",
    ["O"] = "Parler à Cairne Bloodhoof à Thunder Bluff.",
    ["T"] = "Voyage à Thunder Bluff",
  },
  [776] = {
    ["D"] = "Vous avez passé les rites de la Terre-mère et avez désormais votre place parmi nous, à Thunder Bluff.$b$bMais pour maintenir un prestige si durement gagné, vous devez continuer à prouver votre valeur auprès de votre peuple.$b$bNous sommes des chasseurs, $n. Plus bas, à Mulgore, vit une formidable bête kodo nommée Arra\'chea. Prouvez votre habileté à la chasse ; traquez la bête et rapportez-moi la Corne d\'Arra\'chea.",
    ["O"] = "Apporter la Corne d\'Arra\'chea à Cairne Bloodhoof, à Thunder Bluff",
    ["T"] = "Les Rites de la Terre-mère",
  },
  [777] = {
    ["D"] = "Bon.$B$BJe serai là-bas... à l\'abri.$B$BRejoignez-moi, si vous le souhaitez.",
    ["O"] = "Apporter le Rouage N°5 à Lotwil Veriatus.",
    ["T"] = "Grandes difficultés",
  },
  [778] = {
    ["D"] = "Haha ! c\'est fait ! Les entraves sont finies !$B$BIl est temps, Lucien. Il est temps, Servo !$B$BÊtes-vous $gprêt:prête; à assister à la fusion parfaite de la science alchimique et des arcanes, $N ?$B$BJe vais maintenant invoquer un puissant élémentaire. C\'est un grand jour pour nous !",
    ["O"] = "Vaincre le Gardien Fam\'retor et rapporter les entraves des élémentaires de Lotwil à Lotwil Veriatus.",
    ["T"] = "Ça promet d\'être dur",
  },
  [779] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Sceau de la Terre",
  },
  [780] = {
    ["D"] = "Les sangliers de guerre apprivoisés du ravin de Roncelame se répandent sur nos terrains de chasse. Ils sont entraînés au combat et rendus agressifs par les hurans Dos-hirsute contre lesquels nous sommes en guerre.$b$bPartez ; massacrez ces infâmes créatures et revenez avec leurs groins et leurs flancs, que nous puissions en faire un bon ragoût pour les jeunes.",
    ["O"] = "Grull Hawkwind, à Camp Narache, voudrait que vous tuiez des sangliers de guerre, et que vous rapportiez 8 Groins de sanglier de guerre et 8 Flancs de sanglier de guerre.",
    ["T"] = "Les sangliers de guerre apprivoisés",
  },
  [781] = {
    ["D"] = "Après une analyse attentive de la carte, vous comprenez que vous avez découvert les plans du chef de guerre Dos-hirsute pour assiéger le camp Narache.$b$bLe chef Hawkwind pourrait certainement tirer profit de ces informations !",
    ["O"] = "Apporter les Plans d\'attaque Dos-hirsute au chef Hawkwind, au camp Narache.",
    ["T"] = "L\'attaque du camp Narache",
  },
  [782] = {
    ["D"] = "Durant l\'alliance de la Deuxième guerre entre la Horde et le dragon Aile de mort, nous avons appris que deux de ses lieutenants, Cinglenoir et Hematus, ont été emprisonnés dans le ravin de Lethlor, loin à l\'est. Nous devons réduire au silence nos anciens alliés pervertis afin de restaurer l\'honneur de la Horde !$B$BNous avons engagé Tho\'grun et sa bande de mercenaires ogres pour obtenir le Signe de la Terre, une clé nécessaire à l\'ouverture de la prison des drakes. Mais Tho\'grun nous a trahis, nous a submergés et s\'en est emparé. Allez au Camp Boff et récupérez-le !",
    ["O"] = "Tuer Boss Tho\'grun et rapporter le Signe de la Terre à Gorn, à Kargath.",
    ["T"] = "Alliances rompues",
  },
  [783] = {
    ["D"] = "J\'espère que vous avez le coeur bien accroché, jeune $c, car il y a du travail à faire à Northshire.$B$BEt ce n\'est pas du travail de ferme.$B$BLes gardes de Stormwind ont beaucoup à faire afin de maintenir la paix en ces lieux, tandis que nombre d\'entre nous sont en des terres lointaines et que tellement de dangers nous menacent. Aussi acceptons-nous l\'aide de tous ceux qui souhaitent défendre leur demeure. Et leur alliance également.$B$BSi votre volonté est de répondre à cet appel, allez parler à mon supérieur, le maréchal McBride. Il se trouve dans l\'abbaye qui est derrière moi.",
    ["O"] = "Parler au maréchal McBride.",
    ["T"] = "Menace intérieure",
  },
  [784] = {
    ["D"] = "Guidés par l\'amiral Proudmoore, les humains de Kul Tiras prirent pied en Durotar, en violation du pacte signé des années auparavant entre le Chef de guerre et Jaina Proudmoore pour vaincre Archimonde.$b$bL\'attaque humaine fut repoussée et le donjon de Tiragarde tomba. Mais, récemment, les troupes de réserves de l\'amiral, menées par le lieutenant Benedict, ont repris le donjon et menacent nos terres, une fois de plus. Ces humains ne montrent décidément aucun respect pour la diplomatie.$b$bDémontrez votre bravoure et voyagez vers le sud, jusqu\'au donjon de Tiragarde, pour éliminer ses envahisseurs humains.",
    ["O"] = "Tuer 10 Marins de Kul Tiras, 8 Soldats de marine de Kul Tiras et le Lieutenant Benedict, puis retourner voir Gar\'Thok à Tranchecolline.",
    ["T"] = "Vaincre les traîtres",
  },
  [785] = {
    ["D"] = "Thrall is a most wise and noble leader. His tenacity on the battlefield is matched only by his diplomatic tact.$b$bThe Darkspear Trolls have proven to be invaluable allies to orckind. Out of loyalty and honor we have sworn a pact of protection with them.$b$bThe Warchief has sent orders to make sure our Darkspear friends in Sen\'jin Village are not under any direct threat. One of their most trusted scouts, Lar Prowltusk is keeping a close watch on their western flank.$b$bSeek out Lar and assist as needed.",
    ["O"] = "Seek out Lar Prowltusk on the western flank of Sen\'jin Village.",
    ["T"] = "A Strategic Alliance",
  },
  [786] = {
    ["D"] = "Parlez plus doucement, $c. Les centaures kolkar sont juste derrière la crête, à l\'ouest, dans la combe des Kolkar.$b$bLa nuit dernière, alors qu\'ils étaient en manœuvres, je me suis glissé dans leur village et j\'ai découvert que ces tristes créatures envisageaient une attaque massive contre les trolls et les orcs de Durotar.$b$bNous ne pouvons leur permettre de mener leurs desseins à terme. Peut-être avez-vous les capacités requises pour vous infiltrer dans la combe des Kolkar et y détruire leurs plans d\'attaque ?$b$bAux dernières nouvelles, ces plans ont été répartis entre trois de leurs chefs.",
    ["O"] = "Détruire 3 Jeux de Plans d\'attaque dans la combe des Kolkar, pour Lar Prowltusk à l\'extérieur du village de Sen\'jin.",
    ["T"] = "Déjouer l\'agression des Kolkar",
  },
  [787] = {
    ["D"] = "Throm\'ka, $c. Je suis Eitrigg, chargé par Thrall de superviser l\'entraînement des recrues.$b$bLa Horde n\'est plus ce qu\'elle était. Jadis, je la quittai, écoeuré par l\'influence toujours croissante des sbires avides de la Légion ardente. Durant mon exil, je fus pris par un groupe d\'humains, mais secouru ensuite par le Chef de guerre. Lorsqu\'il me fit part de sa vision de la Horde, libre de toute influence démoniaque et guidée par les chamans, je réintégrai ses rangs.$b$bGornek aura d\'autres instructions pour vous.",
    ["O"] = "Faites un rapport à Gornek, dans l\'Antre",
    ["T"] = "La nouvelle Horde",
  },
  [788] = {
    ["D"] = "La première chose à faire sera de tremper un peu ce corps trop tendre. Je pourrais vous envoyer dans les Tarides pour y chasser des kodos ; mais en toute honnêteté, vous nous êtes plus utile $gvivant que mort:vivante que morte;.$b$bEn revanche, je pense que les sangliers tachetés seront des adversaires à votre hauteur. Vous les trouverez plus au nord.",
    ["O"] = "Tuer 10 Sangliers tachetés, puis retourner voir Gornek, dans l’Antre.",
    ["T"] = "La dent tranchante",
  },
  [789] = {
    ["D"] = "De piètres novices comme de grands guerriers ont succombé au dard venimeux des scorpides. Vous trouverez un grand nombre de ces créatures au nord-ouest. Apportez moi dix de leurs queues comme preuve que vous les avez affrontées.$b$bL\'antidote à leur venin est préparé à partir de leur aiguillon. Nous conservons de grandes quantités de ce remède pour sauver de jeunes vies comme la vôtre...$b$bMais vous n\'aurez certainement aucun besoin de ce contre-poison, n\'est-ce pas ?",
    ["O"] = "Apporter 10 Queues de scorpides ouvriers à Gornek, dans l’Antre.",
    ["T"] = "L\'aiguillon du scorpide",
  },
  [790] = {
    ["D"] = "$C ! Je me voyais déjà mort ici, ignoré de tous. Alors que je chassais les scorpides de la vallée, je fus soudain confronté à un spécimen particulièrement puissant. Je parvins à lui porter un grand coup avant qu\'il ne referme ses pinces autour de ma jambe.$b$bJe fus, hélas, distrait par cette attaque et en oubliai le redoutable aiguillon, qui se ficha dans ma poitrine. Je vous en prie, tuez ce scorpide pour moi ! Mon honneur doit être lavé. J\'ai combattu la créature sur le plateau, plus au sud.",
    ["O"] = "Tuer Sarkoth et rapporter sa pince à Hana\'zua.",
    ["T"] = "Sarkoth",
  },
  [791] = {
    ["D"] = "Mon âge me rend inapte au maniement des armes, mais j\'ai appris à me rendre utile par d\'autres moyens.$B$BDe ce poste d\'observation, je guette les envahisseurs. Nos forces s\'accroissant de jour en jour, je me trouve de moins en moins souvent à devoir donner l\'alarme en soufflant dans le cor.$B$BAussi, pour passer le temps, je confectionne du matériel et aide de plus jeunes guerriers que moi à défendre leur pays.$B$BJe peux fabriquer un sac pour vos affaires. Si cela vous intéresse, apportez-moi du canevas, un matériau qu\'on trouve communément chez les humains et les centaures.",
    ["O"] = "Apporter 8 Morceaux de canevas à Furl Scornbrow, dans la la tour de guet de Tranchecolline.",
    ["T"] = "Assumer ses responsabilités",
  },
  [792] = {
    ["D"] = "Je suis sûre que la Vallée des épreuves vous enseignera beaucoup, jeune $c.$B$BJ\'ai été envoyée dans la Vallée pour vous guider, mais j\'y ai découvert une menace croissante.$B$BUn groupe qui se fait appeler la Lame ardente a un convent, ici, dans la Vallée des épreuves. Ils se cachent dans une grotte, au nord-est, d\'où leurs vils quasits partent se répandre sur les terres pour les ravager.$B$BComme première action contre la Lame ardente, je vous demande de vaincre ces quasits. Massacrez-les en nombre et, si vous y avez survécu, revenez me voir.",
    ["O"] = "Tuer 12 Vils quasits.$B$BRetourner voir Zureetha Fargaze, à l’extérieur de l’Antre.",
    ["T"] = "Les vils quasits",
  },
  [793] = {
    ["D"] = "Le Sceau de la Terre est retenu par trois Pierres runiques, placées dans les Piliers de diamant, d\'onyx, et d’améthyste dans le Ravin de Lethlor. Avec le Signe de la Terre, vous serez capable d\'enlever les Pierres runiques et de déverrouiller le Sceau de la Terre.$B$BPrudence, $N, les drakes noirs Cinglenoir et Hematus sont des ennemis à prendre au sérieux. Tuez-les et apportez-moi leurs liens.$B$BPour atteindre le ravin, allez dans la partie la plus à l\'est des Terres ingrates. Attention, car le ravin abrite de nombreux drakes.",
    ["O"] = "Utiliser le Signe de la Terre pour activer les Piliers de diamant, d\'opale et d\'améthyste, et obtenir les Pierres runiques.$b$bPlacer les Pierres runiques dans le Sceau de la Terre pour libérer Cinglenoir et Hematus.$b$bLes exécuter et rapporter les Liens de Cinglenoir, les Entraves d\'Hematus et le Signe de la Terre à Gorn.",
    ["T"] = "Alliances rompues",
  },
  [794] = {
    ["D"] = "Mes divinations me font voir qu\'un objet de puissance se trouve loin à l\'intérieur du convent de la Lame ardente, et qu\'il est gardé par des bêtes et de la magie noire.$B$BVous devez trouver cet objet, le Médaillon de la Lame ardente et le prendre au convent.$B$BAttention, il sera probablement entre les mains d\'un agent de la Lame ardente autrement plus puissant que les quasits.$B$BPartez, $N. Vous trouverez le Convent dans une caverne, au nord puis à l\'ouest.",
    ["O"] = "Rapporter le Médaillon de la Lame ardente à Zureetha Fargaze, à l\'extérieur de l\'Antre.",
    ["T"] = "Le médaillon de la Lame ardente",
  },
  [795] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Sceau de la Terre",
  },
  [796] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [797] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [798] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [799] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [800] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [801] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [802] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [803] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [804] = {
    ["D"] = "Ce que vous avez fait pour moi remplit mon cœur de courage. Je ne peux pas me laisser mourir comme ça, je dois me battre !$b$bJe ne puis, cependant, regagner l\'Antre sans assistance. S\'il vous plaît, $n, retournez à l\'Antre et informez Gornek de ma situation. Peut-être saura-t-il m\'aider.",
    ["O"] = "Informer Gornek, à l\'Antre, de la situation critique dans laquelle se trouve Hana\'zua.",
    ["T"] = "Sarkoth",
  },
  [805] = {
    ["D"] = "Vos épreuves contre la Lame ardente sont finies… ici dans la Vallée. Mais je veux que vous rendiez compte de vos découvertes.$B$BAllez au village troll de Sen\'jin et cherchez Maître Gadrin. Le village est à l\'est de la vallée, puis à droite à l\'embranchement.$B$BParlez-lui de la Lame ardente, dites-lui que ses membres ont atteint la Vallée des épreuves. Apprenez de lui s\'ils ont déjà atteint Sen\'jin.$B$BAllez, $n, et faites vite. J\'ai peur que le mal découvert au convent de la Lame ardente ne présage un plus grand danger.",
    ["O"] = "Parler à Maître Gadrin au village de Sen\'jin.",
    ["T"] = "Faire un rapport au Village de Sen\'jin",
  },
  [806] = {
    ["D"] = "Nous ne pouvons permettre à la Lame ardente de prendre pied en Durotar ! Nous devons les détruire avant que ce mal ne suppure !$B$BJ\'ai mené mes propres enquêtes et j\'ai découvert qu\'un démoniste de la Lame ardente, le gobelin Fizzle Darkstorm, s\'est établi dans les falaises du Tonnerre au nord-ouest. Là-bas, lui et ses sectateurs répandent le chaos.$B$BTrouvez Fizzle, et éliminez-le. Apportez-moi alors sa griffe !",
    ["O"] = "Apporter la Griffe de Fizzle à Orgnil Soulscar à Tranchecolline.",
    ["T"] = "Noirs orages",
  },
  [808] = {
    ["D"] = "J\'entends la voix de mon frère Minshina qui m\'appelle dans mes rêves.$B$BIl a été enlevé par Zalazane, le démoniste des îles de l\'Écho, à l\'est. Et il est mort.$B$BMais la mort ne l’a pas libéré. Car Zalazane a emprisonné son esprit dans son propre crâne. Dans mes rêves, je peux le voir parmi d\'autres crânes, dans un cercle de puissance situé sur la plus grande des îles de l\'Écho. Tant qu\'il y reste, son esprit est condamné.$B$BJe vous en prie, $N. Trouvez le cercle et le Crâne de Minshina. Rapportez-le.$B$BLibérez-le !",
    ["O"] = "Récupérer le Crâne de Minshina dans le Cercle de puissance, sur les îles de l\'Écho.$B$BLe rapporter à Maître Gadrin, au village de Sen\'jin.",
    ["T"] = "Crâne de Minshina",
  },
  [809] = {
    ["D"] = "Depuis la dernière grande guerre où la Légion ardente fut défaite, j\'ai recherché les sources de corruption démoniaque dans la société des orcs. Le collier que vous m\'avez apporté confirme mes craintes.$B$BIl appartient à la Lame ardente, un culte en possession d\'un objet de grand pouvoir maléfique qu\'on appelle la Graine démoniaque. Vous pouvez la trouver dans les Tarides, au sommet du pic de Brume-funeste. La Graine doit absolument être détruite !$B$BAllez au Poste de garde extérieur, à l\'ouest sur la frontière des Tarides et parlez avec mon assistant, Ak\'Zeloth. Il vous donnera les prochaines directives.",
    ["O"] = "Parler à Ak\'Zeloth, dans les Tarides.",
    ["T"] = "Ak\'Zeloth",
  },
  [810] = {
    ["D"] = "You would do well to learn how dangerous this land can be, $c.$B$BThe wildlife here can teach us such things... if we are wise and observant.$B$BThe scorpids are a perfect example of survival in Durotar. If you are to survive, it would be wise to take on some of their characteristics as your own. Their hardened carapaces can protect you from the harshness of the sun, or even a deadly weapon.$B$BBring me 6 Small Scorpid Carapaces from the Clattering Scorpids, $N, and I shall see about rewarding you justly.",
    ["O"] = "Bring 6 Small Scorpid Carapaces to Kor\'ghan in Sen\'jin Village.",
    ["T"] = "<UNUSED>",
  },
  [811] = {
    ["D"] = "One of the other traits of the scorpids is their fierce loyalty. They will protect one another if they are in danger--you\'ve probably already seen this behavior in your first scorpid hunt.$B$BYou would do well to heed that lesson and adapt it for yourself: we are stronger as one; we are weak when we are divided into many.$B$BBring me 8 Large Scorpid Carapaces from the Armored Scorpids and I shall reward you for learning this wisdom.",
    ["O"] = "Bring 8 Large Scorpid Carapaces to Kor\'ghan in Sen\'jin Village.",
    ["T"] = "<UNUSED>",
  },
  [812] = {
    ["D"] = "$N... votre minutage est parfait. J\'espère que je pourrai en dire autant de votre célérité.$B$BJ’ai manqué d\'attention en combattant des Queues-venin aux alentours, et j\'ai été piqué par l\'un d\'entre eux. Le poison m\'affaiblit au moment même où je parle. A ce rythme, il me reste peut-être une heure à vivre. Mais je vais avoir besoin de vous...$B$BKor\'ghan, à Orgrimmar, sait comment confectionner l\'antidote. Trouvez-le... et faites vite, $N. Je ne vais pas pouvoir tenir beaucoup plus longtemps. Il devrait se trouver dans la Faille de l\'ombre.",
    ["O"] = "Trouver Kor\'ghan à Orgrimmar et obtenir l\'Antidote du Queue-venin. L\'apporter ensuite à Rhinag près de la frontière nord-ouest de Durotar.",
    ["T"] = "Besoin d\'un remède",
  },
  [813] = {
    ["D"] = "Les scorpides Queue-venin sont parmi les plus dangereux en Durotar. Rhinag a manqué de sagesse lorsqu\'il a les a chassés sans emporter d\'antidote, mais à présent, une remontrance est vraiment la dernière chose dont il a besoin.$B$BApportez-moi des Glandes à venin de n\'importe quel Queue-venin, et je lui préparerai un antidote. Vous en trouverez près de l\'entrée d\'Orgrimmar.$B$BDépêchez-vous, $N. L\'un d\'entre nous a besoin de votre aide.",
    ["O"] = "Apporter 4 Glandes à venin de Queue-venin à Kor\'ghan à Orgrimmar.",
    ["T"] = "La recherche de l\'antidote",
  },
  [814] = {
    ["D"] = "Cook and clean--that\'s all I do!$B$BYou want to eat, you get me some more meat! I don\'t have all day to hunt and prepare food for all these louses. You got to learn to pull your own weight around here if you wanna be treated equal.$B$BGet me some Chunks of Boar Meat if you want to make yourself useful... or you don\'t want to starve to death.",
    ["O"] = "Bring 10 Chunks of Boar Meat to Cook Torka in Razor Hill.",
    ["T"] = "<UNUSED>",
  },
  [815] = {
    ["D"] = "Bah ! J\'allais presque oublier... faut qu\'je ficelle le p\'tit déjeuner$B$B$N, bougez- vous l\'train et allez m\'trouver quelques oeufs de fouette-queue. M\'en faut au moins trois si j\'veux en avoir aussi pour le déjeuner de demain.$B$BLe fouette-queue Griffesang peut être farouche et protège ses oeufs jusqu\'à la mort, ça oui. Ils les enfouissent parfois bien trop profondément pour pouvoir les déterrer ; mais, bon, si vous fouinez un peu du côté des îles de l\'Écho, au sud-est du village de Sen\'jin, vous en trouverez d\'accessibles à foison, partout sur ces îles.",
    ["O"] = "Rapporter 3 Oeufs de fouette-queue au cuisinier Torka, à Tranchecolline.",
    ["T"] = "Des oeufs pour l\'omelette",
  },
  [816] = {
    ["D"] = "$N, pouvez-vous m\'aider ? Mon fils, Kron, est parti chasser il y a quelques jours et il n\'est toujours pas revenu. Il est allé à l\'ouest, vers les Tarides, pour traquer des crocilisques, le long du fleuve Furie-du-Sud. Je crains le pire.$B$BAussi fort soit-il, sa fierté mal placée l\'a toujours conduit dans les pires situations. Lorsque je lui ai dit que les crocilisques étaient puissants et brutaux, il s\'est emporté et est parti.$B$BSi vous ne le ramenez pas, découvrez au moins ce qu\'il est devenu...Même si vous devez pour cela ouvrir le ventre de chaque crocilisque, le long des bancs de sable de la Furie-du-Sud.",
    ["O"] = "Trouver un indice du sort de Kron et en informer Misha Tor\'kren, à la ferme, au nord-ouest de Tranchecolline.",
    ["T"] = "L\'égaré",
  },
  [817] = {
    ["D"] = "La plupart des peaux que nous utilisons proviennent des tigres de Durotar, $N. Couvertures, armures, tentes, nous avons beaucoup de raisons de chasser ces bêtes, et autant de les laisser prospérer.$B$BLe temps est venu de cueillir la récolte, pour ainsi dire. Nos rangs grandissent et les besoins de mon peuple sont supérieurs à nos provisions. Il me faut plus de peaux pour subvenir à ces besoins.$B$BApportez-moi 4 Fourrures de tigres de Durotar, et je vous récompenserai. Vous pourrez les trouver sur les îles au sud d\'ici.",
    ["O"] = "Apporter 4 Peaux de tigres de Durotar à Vel\'rin Fang, au village de Sen\'jin.",
    ["T"] = "Proie nécessaire",
  },
  [818] = {
    ["D"] = "Même si mes yeux sont faibles, j\'y vois encore suffisamment clair. Je me repose souvent sur mes compétences en alchimie pour les magies qui autrefois m\'étaient aisées. Mais je refuse de prendre $gun apprenti:une apprentie;. Aucun orc ou troll assez digne ne s\'est jamais présenté.$B$BEt vous, en êtes-vous digne ? Bien sûr que vous en êtes digne... que vous croyez l\'être.$B$BJ\'ai besoin de certaines choses. Pouvez-vous les obtenir ?$B$BIl me faut des yeux de makruras intacts, et des fioles de mucosités de clampants. De n\'importe quels makruras ou clampants en Durotar. Nous nous reparlerons bientôt.",
    ["O"] = "Apporter 4 Yeux de makrura intacts et 8 Fioles de mucus de clampant à maître Vornal, au village de Sen\'jin.",
    ["T"] = "Un esprit parfaitement éduqué",
  },
  [819] = {
    ["D"] = "Un petit écriteau, placé sur l\'un des bouts du tonneau, dit :$B$B\"Chen Stormstout-$B$BQue laper règne sur la terre comme au ciel, et que les spiritueux soient avec votre esprit.\"",
    ["O"] = "Trouver quelqu\'un qui en sache davantage au sujet du Tonneau vide de Chen.",
    ["T"] = "Tonneau vide de Chen",
  },
  [821] = {
    ["D"] = "Voudriez-vous goûter à l\'homonyme de Chen ? Ahhh ! La Stormstout est une puissante mixture. Chen a appris la recette à mon mentor, et mon mentor me l\'a apprise. J\'aurais besoin que vous me trouviez certaines choses, mais je vous assure : le choc en vaut la peine.$b$bApportez-moi 5 Canines de lion des savanes, de n’importe quel lion des savanes ; 5 Rognons de trotteur des plaines, de n\'importe quel trotteur des plaines, et 1 Corne de lézard-tonnerre. Ça devrait parfaitement faire l\'affaire.$b$bVous pouvez trouver ces ingrédients dans les Tarides.",
    ["O"] = "Apporter 5 Canines de lion des savanes, 5 Rognons de trotteur des plaines, et 1 Corne de lézard-tonnerre au brasseur Drohn à Ratchet.",
    ["T"] = "Tonneau vide de Chen",
  },
  [822] = {
    ["D"] = "Sacré caractère, cette Brune d’Orage, hein ?$B$BJ’ai une autre recette que j’ai apprise de Chen. Ça vous intéresse ? Ça ne sera pas long, et ça peut même vous être utile si vous comptez poursuivre vos aventures.$B$BApportez-moi cinq glandes d’éclair d’orageux, une glande salivaire de faucon-tonnerre et un foie de kodo. Vous trouverez tout ça dans les Tarides.$B$BComme je l\'ai dit, ce breuvage décoiffe. C\'est la tête de Trogg dont je vous ai parlé.",
    ["O"] = "Apporter 5 Glandes de corne-éclair, 1 Glande salivaire de faucon-tonnerre et 1 Foie de kodo au brasseur Drohn à Ratchet.",
    ["T"] = "Tonneau vide de Chen",
  },
  [823] = {
    ["D"] = "Nous autres au village de Sen\'jin avons nos problèmes, et nous vous remercions pour votre aide. Mais les nouvelles que vous apportez à propos de la Lame ardente pourraient annoncer des ennuis pour tout le monde.$B$BUn orc nommé Orgnil Soulscar est posté à Tranchecolline. Il protège Durotar de maux tels que la Lame ardente. Allez lui parler et informez-le de notre demande. Donnez-lui également des nouvelles de la Vallée des épreuves. Il voudra savoir ces choses.$B$BTranchecolline est au nord.",
    ["O"] = "Parler à Orgnil Soulscar à Tranchecolline.",
    ["T"] = "Un rapport à Orgnil",
  },
  [824] = {
    ["D"] = "Le Cercle terrestre est un groupe de chamans dédié à l\'étude et à la préservation des éléments. Ils en savent plus sur les dieux anciens fous, les vieilles légendes et les savoirs antiques que la plupart des savants de ce monde.$B$BHeureusement pour vous, je connais quelqu\'un du Cercle terrestre, ici, à Ashenvale. Allez à l\'avant-poste de Zoram\'gar, sur la côte de la grève de Zoram. Montrez à Je\'neu Sancrea ce que nous avons trouvé. Si quelqu\'un peut découvrir quelle est la signification de ce globe, c\'est bien lui.",
    ["O"] = "Apporter le Globe d\'eau souillée à Je\'neu Sancrea, à l’Avant-poste de Zoram\'gar, dans Ashenvale.",
    ["T"] = "Je\'neu du Cercle terrestre",
  },
  [825] = {
    ["D"] = "L\'un de mes éclaireurs les plus observateurs me rapporte que les épaves de la flotte de Proudmoore sont toujours près de la côte de Durotar, juste à l\'est du donjon de Tiragarde.$b$bCe n\'est un secret pour personne que les humains, alliés à ces petites bêtes idiotes que sont les gnomes, possèdent des connaissances avancées en mécanique. Nous devons apprendre précisément les savoirs de tous nos ennemis potentiels. Et notre peuple bénéficiera de ces nouveaux savoirs, également.$b$bNagez à travers les épaves, $n, et trouvez pour moi les outils de l\'Alliance.",
    ["O"] = "Gar\'Thok de Tranchecolline veut que vous retrouviez 3 Outils gnomes parmi les épaves près de la côte.",
    ["T"] = "Des épaves",
  },
  [826] = {
    ["D"] = "Le sorcier-docteur Zalazane demeure sur les îles de l\'Écho, à l\'est. Ce sont les îles que nous appelions autrefois « maison ».$b$bDe là, il envoie ses trolls sur le continent et déchaîne ses maléfices sur notre peuple pour placer davantage des nôtres sous son emprise.$b$bIl faut l\'arrêter.$b$bBattez Zalazane et ses serviteurs, d\'anciens trolls Darkspear perdus pour notre cause. Apportez-moi sa tête, et je saurai que son règne maléfique est terminé.",
    ["O"] = "Battre Zalazane.$b$bTuer 8 Trolls vaudou et 8 Trolls maléficiés.$b$bApporter la Tête de Zalazane à Gadrin.",
    ["T"] = "Zalazane",
  },
  [827] = {
    ["D"] = "$N. La Lame ardente a infesté la caverne appelée le Rocher du crâne, à l\'est d\'Orgrimmar. Ils y accomplissent des rituels abominables, et brûlent leur propre chair avec des Colliers brûlants.$b$bEn portant ces colliers, je pense que les sectateurs s\'harmonisent avec la puissance démoniaque. Mais pour le confirmer, je dois en posséder une collection afin de les étudier.$b$bPartez pour le Rocher du crâne et prenez les Colliers brûlants que vous trouverez sur les membres du culte. Apportez-les-moi, et je dévoilerai leurs mystères.",
    ["O"] = "Prendre les Colliers brûlants des sectateurs au Rocher du crâne.$b$bLes apporter à Margoz à son camp.",
    ["T"] = "Rocher du crâne",
  },
  [828] = {
    ["D"] = "L\'un de nos chamans, Margoz, en sait plus sur la corruption de la Lame ardente. Il parle d\'une caverne appelée Rocher du crâne dans les montagnes, juste à l\'extérieur d\'Orgrimmar, qui abrite un important groupe de sectateurs.$b$bAvant que vous ne partiez pour le Rocher du crâne, parlez à Margoz. C\'est un sage et son conseil est précieux.$b$bIl campe au nord-est, entre la côte et le Ravin asséché.$b$bSuivez son avis, mais quoi qu\'il dise, $n, je veux que vous écrasiez ce culte !",
    ["O"] = "Parler à Margoz.",
    ["T"] = "Margoz",
  },
  [829] = {
    ["D"] = "Les Colliers brûlants que vous m\'avez apportés sont des instruments puissants et démoniaques. Deviner leur origine est cependant, je le crains, au-delà de mes capacités de chaman. Nous aurons besoin qu\'un démoniste les étudie.$b$bApportez un Collier brûlant à Neeru Fireblade. Bien qu\'il soit un redoutable démoniste, il professe qu\'il utilise ses pouvoirs pour contrecarrer les démons, et proclame que ses recherches sur l\'occulte sont bénignes. Que cela soit vrai ou pas, nous pouvons avoir besoin de son aide contre le culte démoniaque en Durotar. Vous trouverez Neeru à Orgrimmar, dans la Faille de l\'ombre.",
    ["O"] = "Apporter un Spécimen de collier à Neeru Fireblade à Orgrimmar.",
    ["T"] = "Neeru Lamefeu",
  },
  [830] = {
    ["D"] = "Vous ouvrez la vieille enveloppe décolorée et découvrez un document d\'apparence officielle. Vous reconnaissez le sceau de l\'amiral Proudmoore.$b$bÇa a l\'air important. Peut-être que Gar\'Thok, le commandant de Tranchecolline, serait intéressé par cette information.",
    ["O"] = "Apporter les Ordres de l\'amiral Proudmoore à Gar\'Thok de Tranchecolline.",
    ["T"] = "Les ordres de l\'amiral",
  },
  [831] = {
    ["D"] = "On ne peut pas faire confiance aux humains. Nous avons combattu à leurs côtés le cœur empli de lassitude, sachant qu\'ils nous trahiraient un jour.$b$bLa mort de l\'amiral Proudmoore n\'a pas été suffisante pour clore cette longue tradition de tromperie. Cette vermine avait bien préparé ses plans avant qu\'il ne rencontre la mort.$b$bSon influence ne s\'éteindra pas non plus avec Benedict. Qui sait combien de temps il reste avant que la prochaine vague d\'hommes de Proudmoore ne déferle sur nos rivages ?$b$bNous devons apporter ces ordres à Nazgrel à Orgrimmar immédiatement ! On peut le trouver dans les logements de Thrall.",
    ["O"] = "Porter les Ordres de l\'amiral Proudmoore à Nazgrel, dans les appartements de Thrall à Orgrimmar.",
    ["T"] = "Les ordres de l\'amiral",
  },
  [832] = {
    ["D"] = "<Des mots émanent du pendentif et dans votre esprit.>$B$BGazz\'uz ?$B$BGazz\'uz… au rapport !$B$BOn raconte que vous avez été découvert au rocher du crâne. Vous devez vous préparer à une attaque !$B$BGazz\'uz, je vous ordonne de parler ! Parlez ou je m\'assurerai que Neeru Fireblade apprenne votre présence, et il vous tombera dessus comme la misère sur le monde.$B$BNe testez pas vos capacités contre Neeru...$B$B... Gazz\'uz, vous êtes là ?",
    ["O"] = "Apporter cet Oeil à Neeru Fireblade.",
    ["T"] = "Ombres ardentes",
  },
  [833] = {
    ["D"] = "Seuls les plus vaillants taurens sont enterrés aux Rochers rouges, notre terre funéraire sacrée. C\'est un honneur accordé aux grands guerriers qui ont aidé à construire et à défendre Thunder Bluff et à ceux qui ont donné leur vie pour le bien de leur tribu et de leurs chefs.$b$bMais il apparaît qu\'un infâme danger menace notre terre sacrée. Un groupe d\'Intrus Dos-hirsute ravage le cimetière et je suis trop vieux et trop faible pour les repousser.$b$bIls doivent pourtant être chassés sans pitié, $n.",
    ["O"] = "Le Gardien du savoir Raintotem veut que vous tuiez 8 Intrus Dos-hirsute aux Rochers rouges.",
    ["T"] = "Cimetière sacré",
  },
  [834] = {
    ["D"] = "Je suis Rezlak, l\'un des gars de Gazlowe. Le patron m\'a envoyé pour donner un coup de main aux orcs, ici en Durotar. Les choses se sont bien passées, à part pour les caravanes. Je n\'arrive pas à assurer leur sécurité ! Ça rend mon boulot un peu plus difficile, vous savez ?$b$bLa dernière livraison – ils ont promis que je passerais ! – a été chipée par les harpies Terrevent au... c\'était où déjà... au canyon de Tranchevent ?$b$bFaut que je récupère ces marchandises ou je n\'arriverai jamais à rien ! Suivez le grand canyon vers le sud, vous trouverez le ravin coupant exactement les parois à l\'est et à l\'ouest.",
    ["O"] = "Retrouver 5 Sacs de marchandises et les rendre à Rezlak près d\'Orgrimmar.",
    ["T"] = "Des vents dans le désert",
  },
  [835] = {
    ["D"] = "Gazlowe m\'a dit une fois \"Rezlak, il y a une chose que tu dois savoir dans la vie : si tu veux qu\'une chose soit faite de manière correcte, fais-la toi-même.\"$B$BJe ne peux supporter la perte de nos caravanes, alors je vais m\'occuper des choses en personne. Le Ravin asséché est quelque part dans la partie est du canyon de Tranchevent. Si nous y tuons toutes les harpies, les routes seront sûres.$B$BBien sûr, il y a autre chose que Gazlowe m\'a enseigné, et c\'est \"En payant bien, tout est possible.\" Alors, vous allez m\'aider, $N ?",
    ["O"] = "Tuer 12 Terrevent farouches et 8 Sorcières des tempêtes Terrevent pour Rezlak près du Ravin asséché.",
    ["T"] = "Des lignes sécurisées",
  },
  [836] = {
    ["D"] = "Le robot émet à nouveau la voix du gnome :$B$B\"Je dois déplacer OOX-09/HL dans un endroit dégagé et sûr pour qu\'il puisse entamer sa longue procédure de décollage. Il est équipé d\'un camouflage, mais il me faut du temps pour le rendre opérationnel de mon côté. Il faut que vous l\'escortiez jusqu\'au... disons jusqu\'au littoral qui s\'étend au-delà des Haut-Surplombs l ! Ce serait un endroit parfait, et assez long, pour que tout puisse se mettre en route !\"$B$BEscortez-le jusque là-bas, et venez me parler à Baie-du-Butin ! Oglethorpe Obnoticus, terminé.\"",
    ["O"] = "Escorter OOX-09/HL jusqu\'au littoral au-delà des Haut-Surplombs, puis rendre compte à Oglethorpe Obnoticus à Baie-du-Butin.",
    ["T"] = "À la rescousse de OOX-09/HL !",
  },
  [837] = {
    ["D"] = "Quand nous sommes arrivés, les hurans Tranchecrin possédaient la majeure partie de la terre, et étaient comme une épine dans notre flanc. Par des efforts constants, nous sommes parvenus à en chasser une grande partie, mais ils restent toujours bien fortifiés dans certains secteurs.$b$bCela a suffisamment duré, cependant. Pour notre propre sécurité, nous ne pouvons tolérer la présence des Tranchecrins sur notre territoire. On peut trouver leurs camps à l\'ouest d\'ici. Cherchez les roncières et vous les trouverez. Aujourd\'hui, nous les chassons de Durotar, et demain, peut-être, de tout Kalimdor.",
    ["O"] = "Tuer 4 hurans Tranchecrin, 4 Eclaireurs Tranchecrin, 4 Lève-poussière Tranchecrin et 4 Gardes de guerre Tranchecrin pour Gar\'Thok de Tranchecolline.",
    ["T"] = "Empiètement",
  },
  [838] = {
    ["D"] = "Sur l\'île de Caer Darrow se trouve Scholomance - une école servant de forteresse au Fléau. Nos ennemis y résident, à l\'abri, et libres de poursuivre des recherches qui ne font qu\'accroître leur pouvoir. Une porte se dresse sur le chemin, et malgré tous nos efforts, elle ne peut être franchie sans que la bonne clé ne lui soit présentée.$B$BLe Pharmacien Dithers en connaît beaucoup sur Scholomance, et a une idée quant à la façon d\'obtenir cette clé. Vous m\'avez prouvé que vous êtes capable d\'atteindre des objectifs, aussi je vous confie cette tâche. Allez lui parler et entretenez-le à ce propos.",
    ["O"] = "Parler au Pharmacien Dithers à la Barricade, dans les Maleterres de l\'ouest.",
    ["T"] = "Scholomance",
  },
  [839] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [840] = {
    ["D"] = "Hum ! Vous avez l\'air plutôt $gfort:forte;.$b$bÉcoutez, mon bon ami Kargal a besoin de nouvelles recrues pour servir dans les Tarides. Je sais que vous voulez faire ce qui est bon pour la Horde. Eh bien, voici votre chance !$b$bApportez cette lettre de recrutement à Kargal et voyez s\'il la signe...",
    ["O"] = "Suivre la route de l\'ouest, celle qui part de Tranchecolline, et qui arrive aux Tarides par un pont.$b$bS\'arrêter à l\'avant-poste orc sur le pont.$b$bDonner votre lettre de recrutement à Kargal Battlescar aux Tarides.",
    ["T"] = "Sous les drapeaux de la Horde",
  },
  [841] = {
    ["D"] = "Laissez-moi deviner… vous n’avez pas accompli l\'un de vos horribles exploits à temps. Non, attendez, encore mieux, vous l\'avez laissée tomber dans le sable ! Bien, qu’importe la raison, j’ai d’autres sources d\'énergie que je peux vous vendre. Cette fois, cependant, ça vous coûtera dix, OUI DIX Outres de Bat-le-désert !$B$BLes temps sont durs par ici vous savez, $N, et les sources d\'énergie ne poussent pas sur les arbres ! Hmm, mais si c\'était le cas... peut-être que nous pourrions FABRIQUER un arbre à sources d\'énergie...",
    ["O"] = "Apporter 10 Outres de Bat-le-désert à l\'ingénieur en chef Bilgewhizzle à Gadgetzan pour qu\'il les échange contre une autre source d\'énergie.",
    ["T"] = "Une autre Source d\'énergie ?",
  },
  [842] = {
    ["D"] = "Apportez cette lettre de recrutement signée à la Croisée.$b$bSergra Darkthorn, que sa peau soit maudite, est la responsable là-bas. C\'est une affreuse chaman qui vous saoulera de paroles avant de vous laisser faire quoi que ce soit d\'utile. Thrall est convaincu qu\'elle est spéciale, mais je n\'en suis pas si sûr.$b$bVous aurez à vous faire votre propre opinion, $gmorveux:morveuse;...",
    ["O"] = "Suivre la route à l\'ouest à partir du Poste de garde extérieur de Kargal.$b$bA l\'intersection en T, tourner à gauche et suivre la route vers le sud.$b$bTrouver Sergra Darkthorn où les routes se croisent à l\'intérieur de la Croisée.",
    ["T"] = "Conscription à la Croisée",
  },
  [843] = {
    ["D"] = "Je foule ces chemins dans la solitude.$b$bLes nains de Bael Modan n\'ont aucun respect pour ma terre. D\'horribles explosions creusent profondément le sol tandis que des machines éventrent les collines. La terre qui autrefois était le foyer et la nourrice de ma tribu est aujourd\'hui grouillante de ces nains destructeurs.$b$bLes tentatives diplomatiques pour arriver à un accord ont échoué. À présent l\'heure de l\'action radicale a sonné.$b$bRepoussez par la force les nains de Bael Modan et apportez-moi le journal de leur chef, le prospecteur Khazgorm.",
    ["O"] = "Gann Stonespire veut que vous tuiez 15 Excavateurs de Bael\'dun et 5 Surveillants de Bael\'dun.$b$bApporter le Journal de Khazgorm à Gann Stonespire.",
    ["T"] = "La réclamation de Gann",
  },
  [844] = {
    ["D"] = "Votre première proie sera une proie facile.$B$BLes trotteurs des plaines, à l\'est, attaquent constamment nos réserves de nourriture et sont devenus une véritable nuisance.$B$BAbattez-les et revenez me voir avec leurs becs.",
    ["O"] = "Collecter 7 Becs de trotteurs des plaines et les apporter à Sergra Darkthorn à la Croisée.",
    ["T"] = "La menace des trotteurs des plaines",
  },
  [845] = {
    ["D"] = "Les zhévras ne sont pas les bêtes les plus redoutables des Tarides, mais ils sont un peu plus dangereux que les trotteurs des plaines. Ne vous en faites pas, nous vous enverrons combattre des proies plus conséquentes en temps utile, mais vos dents ont encore besoin d\'être aiguisées.$B$BChassez les zhévras au nord et au sud, et récupérez leurs sabots. Apportez-les-moi et peut-être que la prochaine fois nous vous indiquerons quelque chose de plus coriace.",
    ["O"] = "Tuer des Messagers zhévras et apporter 4 Sabots de zhévra à Sergra Darkthorn à la Croisée.",
    ["T"] = "Les zhévras",
  },
  [846] = {
    ["D"] = "Il est évident que le prospecteur dépendait des explosifs et des véhicules bruyants pour déchirer le sol. Nous pouvons nous attendre à ce qu\'un nouveau contremaître le remplace bientôt.$b$bJ\'aimerais faire goûter aux nains leur propre médecine. D\'après ces plans, si nous combinons de la nitroglycérine avec de la pulpe de bois et du nitrate de sodium, nous pouvons créer une charge explosive. Les fusiliers, les soldats et les officiers du donjon possèdent ces ingrédients.$b$bRécupérez ces objets et apportez-les-moi. Je confectionnerai une charge que les nains ne sont pas près d\'oublier.",
    ["O"] = "Gann Stonespire veut que vous lui apportiez 6 Fioles de Nitroglycérine, 6 Paquets de Pulpe de bois, et 6 Echantillons de Nitrate de sodium.",
    ["T"] = "La revanche de Gann",
  },
  [847] = {
    ["D"] = "Ce… Ce pouvoir… Est-ce le pouvoir des chamans que Tor\'gan a essayé de me montrer ? Je dois admettre que c\'est intéressant. Dites à Tor\'gan qu\'il a réussi à me convaincre… pour l\'instant. Je vais écouter ce qu\'il a à dire.",
    ["O"] = "Parler à Tor\'gan.",
    ["T"] = "La ruse du raptor",
  },
  [848] = {
    ["D"] = "Les oasis des Tarides abritent un mystère. L\'énergie de la vie coule dans leur eau, revigorant les plantes et les bêtes qui la boivent.$b$bRevigorante et stimulante.$b$bUn champignon pousse près de ces oasis. Ses spores détiennent des propriétés que nous, les apothicaires de Lordaeron, trouvons utiles.$b$bApportez-moi ces spores et vous obtiendrez ma gratitude.",
    ["O"] = "Apporter 4 Spores fongiques à l\'Apothicaire Helbrim à la Croisée.",
    ["T"] = "Spores fongiques",
  },
  [849] = {
    ["D"] = "En suivant les plans de Khazgorm, la fabrication d’une charge explosive ne devrait pas poser de problème.$b$bAh ! Nous y voilà.$b$bÀ présent, $n, je vais vous confier une tâche spéciale. Prenez ce bâton explosif et utilisez-le pour faire sauter la machine volante à Bael Modan.$b$bNon seulement cette machine est une partie cruciale de l’infrastructure du site, mais elle sert également de machine de guerre. Plus important encore, c\'est un symbole de pouvoir et une insulte portée à la mémoire de ma tribu.$b$bFaites ma fierté en concluant cette quête héroïque et vous serez $grécompensé:récompensée;.",
    ["O"] = "Détruire la Machine volante à Bael Modan et retourner voir Gann Stonespire.",
    ["T"] = "La revanche de Gann",
  },
  [850] = {
    ["D"] = "Les centaures nuisent aux taurens depuis des années. Et récemment, les centaures Kolkar des Tarides sont devenus un danger réel. D\'habitude, ils étaient désorganisés, mais de nouveaux chefs ont émergé. Et les ont unis.$b$bAfin de préserver nos possessions ici, ces chefs centaures doivent être éliminés.$b$bApportez-moi la tête de Barak Kodobane. Il campe près des Bassins oubliés au nord.$b$bBattez-le, puis revenez me voir.",
    ["O"] = "Apporter la Tête de Barak à Regthar Deathgate, à l\'ouest de la Croisée.",
    ["T"] = "Les chefs des Kolkar",
  },
  [851] = {
    ["D"] = "Le centaure Verog le Derviche rôde dans les Tarides, et il va être difficile à trouver. Mais il est basé à la tente de commandement des centaures, à l\'Oasis stagnante au sud-est. Il peut être possible de l\'attirer.$B$BPartez pour l\'Oasis stagnante au sud-est et attaquez les centaures près de la tente de commandement. Ce sera dangereux, mais si vous tuez suffisamment de centaures, ils sonneront l\'alarme. Et Verog viendra.$B$BApportez-moi sa tête et je la mettrai avec celle de Barak Kodobane.",
    ["O"] = "Apporter la Tête de Verog à Regthar Deathgate, à l\'ouest de la Croisée.",
    ["T"] = "Verog le Derviche",
  },
  [852] = {
    ["D"] = "Hezrul Marque-de-sang est le chef des centaures Kolkar dans les Tarides. Il est féroce, brutal et malin. L’éliminer sèmerait la confusion et briserait l’unité des Kolkar. Le danger qu\'ils représentent serait ainsi largement diminué.$B$BAussi tuez-le. Tout comme pour Barak et Verog, apportez-moi sa tête.$B$BHezrul dirige son peuple depuis l\'Oasis luxuriante, au sud.",
    ["O"] = "Apporter la Tête d\'Hezrul à Regthar Deathgate, à l\'ouest de la Croisée.",
    ["T"] = "Hezrul Marque-de-sang",
  },
  [853] = {
    ["D"] = "J\'ai fait une émulsion avec les spores que vous m\'avez apportées. Je dois à présent l\'envoyer à mon associée, l\'Apothicaire Zamah. Si vous pouviez la lui apporter, elle pourra vous offrir quelque chose de nos magasins d\'alchimie.$b$bElle est aux Bassins de la vision, une caverne qui se trouve sous les chamans de Thunder Bluff. Le chemin menant à cette caverne est bien caché, mais vous pourrez le trouver sur la Cime des Esprits.$b$bDépêchez-vous ! Cette émulsion ne restera pas puissante longtemps. Elle doit être livrée et utilisée au plus vite.",
    ["O"] = "Apporter les Spores fondues à l\'Apothicaire Zamah à Thunder Bluff, avant que la limite de temps ne soit dépassée.",
    ["T"] = "Apothicaire Zamah",
  },
  [854] = {
    ["D"] = "J\'avais prédit votre arrivée, jeune $c. Bien que peut-être vous ne soyez plus si jeune.$b$bJe crois qu\'il est temps de vous aventurer plus profondément dans ces terres. Je sens que vous êtes $gdestiné:destinée; à de grandes choses.$b$bC\'est la Croisée que vous devez chercher à présent, dans la mesure où nos vies se sont mêlées à celles des orcs et qu\'ils ont besoin de notre aide. Descendez cette route vers l\'est et suivez-la vers le nord en direction de la Croisée. Trouvez Thork dans ses murs, il est béni par la Terre-mère.",
    ["O"] = "Parler à Thork, à la Croisée des Tarides.",
    ["T"] = "Un voyage à la Croisée",
  },
  [855] = {
    ["D"] = "Les Kolkar représentent une menace pour les ambitions de la Horde dans les Tarides ; nous devons réduire leur nombre. De fait, j\'ai une mission pour vous, en échange d\'une récompense.$B$BAllez sur les terres des Kolkar, tuez leurs guerriers et récupérez des bracelets de centaures. Revenez quand vous en aurez un tas ; la Horde vous récompensera largement.",
    ["O"] = "Apporter 15 Bracelets de centaures à Regthar Deathgate, à l\'ouest de la Croisée.",
    ["T"] = "Les bracelets de centaures",
  },
  [856] = {
    ["D"] = "Though you have reached the Barrens, your journey is not complete. You must continue on to the Crossroads, where you are needed most.$B$BFollow the road as you were, and turn left where the path gives you the choice. Follow it North until you see the walls of the Crossroads.$B$BJust before you enter, you should see the Ancestral Sage, he will have words of wisdom for you.",
    ["O"] = "Continue down the road from Camp Taurajo.$B$BTurn left at the T intersection and continue North.$B$BFollow the path to the Ancestral Sage outside the Crossroads.",
    ["T"] = "<UNUSED>",
  },
  [857] = {
    ["D"] = "La puissance était mienne ! Dans mes propres mains ! Et je veux la retrouver ! La retrouver, je vous dis !$b$bCes fichus Excavateurs l\'ont découverte… Elle était tellement belle. Avoir trouvé la Larme des lunes, c\'était la première grande découverte qu\'on avait faite à Bael Modan.$b$bElle aurait dû être mienne ! Mienne je vous dis !$b$bMais le général Twinbraid devint jaloux de la puissance qu\'elle me donnait. Il l\'a enfermée à clé dans le donjon. L\'a traitée de maudite, il l\'a fait ! L\'imbécile !$b$bD\'autres la voulaient et ils devaient être arrêtés ! Ce bâtard de Twinbraid m\'a chassé.$b$bMaintenant, je la veux à nouveau ! Je la veux je vous dis !",
    ["O"] = "Feegly l’Exilé veut que vous retrouviez pour lui la Larme des lunes.",
    ["T"] = "La larme des lunes",
  },
  [858] = {
    ["D"] = "Ce n\'est sûrement pas Sputtervalve qui vous a $genvoyé:envoyée; ? Je suis coincé ici. Je suis entré sans réaliser que j\'avais besoin d\'une clé pour déverrouiller la colonne de mouvement du déchiqueteur. L\'un des autres opérateurs m\'a demandé si tout allait bien, et j\'ai paniqué ! Au lieu de lui dire que je n\'avais pas la clé, je lui ai dit qu\'il y avait un genre de problème mécanique.$b$bNous devons sortir d\'ici au plus vite ! Montez à la salle de contrôle au sommet du derrick, le superviseur devrait avoir une clé pour ce déchiqueteur.$b$bSortez-moi de là !",
    ["O"] = "Récupérer la Clé de contact et l\'apporter à Wizzlecrank.",
    ["T"] = "Contact",
  },
  [860] = {
    ["D"] = "Si vous souhaitez suivre la voie du chasseur, alors votre voyage vous mène jusqu\'aux Tarides. Ses créatures sont robustes et féroces. Vous y apprendrez beaucoup en les traquant, et votre volonté grandira.$B$BParlez à Sergra Darkthorn. Elle sera votre premier guide dans les Tarides.$B$BElle se trouve à la Croisée. Pour l\'atteindre, allez à l\'est en partant du village Bloodhoof et en vous rendant dans les Tarides, puis allez au nord lorsque le chemin se sépare. Et attention tandis que vous voyagez. Les Tarides réservent de grands dangers pour ceux qui manquent de préparation ou de prudence.",
    ["O"] = "Parler à Sergra Darkthorn, à la Croisée.",
    ["T"] = "Sergra Sombreronce",
  },
  [861] = {
    ["D"] = "Vous êtes avide d\'explorations, ça se voit. J’avais aussi le désir d\'explorer, autrefois…$B$BD\'explorer, et de chasser. Car chasser est le plus grand honneur d\'un tauren.$B$BSi vous désirez suivre cette voie, Melor Stonehoof peut vous y aider. Il est à Thunder Bluff, sur la Cime des chasseurs.$B$BPour lui prouver vos compétences et votre résolution, apportez-lui les griffes de Rôdeurs des basses plaines de Mulgore. Ils sont robustes et astucieux, une proie parfaite pour de jeunes $cs qui suivent la voie du chasseur.",
    ["O"] = "Apporter 4 Griffes de Rôdeurs des basses plaines à Melor Stonehoof à Thunder Bluff.",
    ["T"] = "La voie du chasseur",
  },
  [862] = {
    ["D"] = "Les $cs comme vous doivent plutôt avoir faim ici dans les Tarides. Moi, je crève la dalle tout au long de la journée.$b$bJe vais vous mitonner un chouette brouet. J\'ai juste besoin de quelques rats fouisseurs.$b$bIl semble que ces sales nains de Bael Modan soient submergés par le festin favori de tout un chacun. À présent descendez vers le sud, à Bael Modan, et apportez-moi quelques rats fouisseurs.$b$bVous ne pouvez pas vous prétendre $gaventurier chevronné:aventurière chevronnée; si vous n\'avez pas passé du temps à tuer des rats ! Ha !",
    ["O"] = "Apporter 8 Rats fouisseurs à Boustiff\' à l\'est de la Croisée.",
    ["T"] = "Brouet de rat",
  },
  [863] = {
    ["D"] = "Je suppose que j\'apprendrai en le faisant… Ça ne devrait pas être trop difficile. Juste quelques boutons par-ci par-là, et un levier ou deux… Alors, êtes-vous $gprêt:prête; à y aller ?",
    ["O"] = "Protéger Wizzlecrank et le Déchiqueteur gobelin volé jusqu\'à Sputtervalve à Ratchet.",
    ["T"] = "La fuite",
  },
  [864] = {
    ["D"] = "\"Et voilà, la source d\'énergie est enlevée et les données sont préservées. Bien, faites attention sur la route et tout ça. J\'espère que cette horrible expérience s\'est bien déroulée pour vous, blablabla…\"$B$BLe gobelin se détourne pour s\'occuper des demandes de la Compagnie des eaux de Gadgetzan. Vous mettez la trousse d\'analyse verrouillée à l\'abri en vue du voyage de retour vers Undercity. Là-bas, l\'apothicaire Zinge vous attend, vous et la trousse d\'analyse de terrain verrouillée.",
    ["O"] = "Apporter la Trousse d\'analyse de terrain verrouillée à l\'apothicaire Zinge à Undercity.",
    ["T"] = "De retour vers l\'apothicaire Zinge",
  },
  [865] = {
    ["D"] = "Les raptors des Tarides sont plus malins que les autres. Et je pense que cette intelligence se trouve dans leurs cornes ! Si tel est vraiment le cas, alors je pourrais réduire leurs cornes en poudre et les utiliser pour en faire une \"potion d\'intelligence\". Je pourrais en tirer une fortune !!!$B$BEt vous pouvez m\'aider. Trouvez-moi des Cornes de raptors intactes sur des Faucheurs solécailles. Ils parcourent les Tarides septentrionales, et le nord des Tarides près de la frontière avec la forêt d\'Ashenvale.",
    ["O"] = "Collecter 5 Cornes de raptors intactes prises sur des Faucheurs solécaille, et les apporter à Mebok Mizzyrix à Ratchet.",
    ["T"] = "Les cornes de raptors",
  },
  [866] = {
    ["D"] = "Les herbes qui poussent dans les Tarides ne sont pas tout à fait pareilles à celles des autres contrées. Leurs propriétés sont à peu près semblables, mais l\'eau des oasis les transforme légèrement. Elles sont juste un peu différentes.$b$bJ\'aimerais étudier ces différences... pour voir si nous pouvons les exploiter !$b$bSi vous êtes $gun herboriste compétent:une herboriste compétente;, cueillez les herbes que vous trouvez dans les Tarides. Apportez-moi ces échantillons que je puisse les comparer aux herbes des autres régions.",
    ["O"] = "Apporter 8 Echantillons de Racines à Mebok Mizzyrix, à Ratchet.",
    ["T"] = "Les échantillons de racines",
  },
  [867] = {
    ["D"] = "Ces maudites harpies massacrent nos caravanes d\'approvisionnement depuis trop longtemps déjà. Il est temps que nous tranchions quelques-unes de leurs gorges et que nous réduisions leur nombre.$b$bJ\'ai jeté des coups d\'œil discrets à leurs nids et j’ai compris leur hiérarchie. L\'attaque la plus dévastatrice devra détruire une bonne partie de leur chaîne inférieure de commandement, pour que nous puissions ensuite remonter jusqu\'au sommet.$b$bNous allons commencer en douceur. Tuez des harpies et des Volplumes Ensorçaile au nord-ouest, et apportez-moi 8 Griffes d\'Ensorçaile. Ce sera bien pour commencer...",
    ["O"] = "Collecter 8 Griffes d\'Ensorçaile.$b$bLes apporter à Darsok Swiftdagger à la Croisée.",
    ["T"] = "Les harpies pillardes",
  },
  [868] = {
    ["D"] = "Hé $n ! On m\'a fait venir à la Croisée pour surveiller la zone et noter ce qui s\'y déroule, pour mes maîtres à Orgrimmar.$b$bL\'un de mes sujets d\'étude est cette créature, ressemblant à un insecte, et que nous avons découverte au sud dans le Champ des géants. Nous savons peu de choses sur ces bêtes, alors je me fais un point d\'honneur d\'en découvrir plus. Elles semblent posséder quelque intelligence, plus que la plupart des simples animaux.$b$bPrenez cette griffe d\'excavation et récupérez quelques œufs dans les tertres où vivent ces bêtes. Mais attention, si elles sont alertées par quoi que ce soit, elles vous attaqueront.",
    ["O"] = "Apporter 12 Œufs de silithide et la Griffe d\'excavation à Korran, à la Croisée.",
    ["T"] = "La chasse aux œufs",
  },
  [869] = {
    ["D"] = "Il y a peu, une cargaison d\'argent a été volée à notre tour de garde. Cet argent était destiné à payer les gardes de la Croisée, et nous voulons le récupérer.$B$BLe point étrange est que… nous avons arrêté un voleur la nuit même du vol. Et il s\'agissait... d\'un raptor ! Incroyable !$B$BJe ne sais pas quels raptors seraient intéressés par cet argent. Mais qu’importe ! Je veux qu’ils soient tués afin qu\'ils ne nous volent pas à nouveau !$B$BPartez à leur recherche dans les Tarides et récupérez leurs têtes !",
    ["O"] = "Apporter 12 Têtes de raptor à Gazrog, à la Croisée.",
    ["T"] = "Les raptors voleurs",
  },
  [870] = {
    ["D"] = "Il y a longtemps de cela, les Tarides étaient un endroit luxuriant, fourmillant de vie. Mais la guerre et le chaos ont ravagé la terre, l\'ont brûlée et finalement laissée telle une bogue desséchée. Ainsi se sont passées les choses, et cela chagrine mon cœur.$b$bMais ces dernières années, de nouvelles oasis se sont formées dans les Tarides, et la vie frémit à nouveau. Et loin sous le sol, nous les druides sentons une puissance se frayer un chemin vers la surface.$b$bNous devons en trouver la source. Partez pour les Bassins oubliés, au nord-ouest de la Croisée. Parcourez les eaux et cherchez la source de cette puissance, puis revenez me voir.",
    ["O"] = "Rendre compte de vos découvertes à Tonga Runetotem.",
    ["T"] = "Les Bassins oubliés",
  },
  [871] = {
    ["D"] = "Tous les hurans sont nos ennemis, $N. Certains ne représentent simplement qu\'une nuisance plus importante que les autres.$B$BLa tribu Tranchecrin a attaqué nos lignes d\'approvisionnement en provenance de Durotar, nous causant des soucis sans nombre. J\'ai des éclaireurs qui recherchent le chef de ces expéditions, mais en attendant, toutes les pertes que vous pourriez leur infliger nous seraient d\'une grande aide.$B$BCommencez au nord-est, en direction de Durotar. Vous pouvez toujours repérer leurs tanières grâce aux ronces géantes qui sortent de la terre. Cherchez-les. Trouvez-les. Et tuez-les.",
    ["O"] = "Dans la tribu Tranchecrin, tuer 8 Sourciers, 8 Tisseuses d’épines et 3 Chasseurs, puis retourner voir Thork.",
    ["T"] = "Stopper les attaques des hurans",
  },
  [872] = {
    ["D"] = "L\'un de mes éclaireurs a été témoin d\'une attaque sur une caravane en provenance de Durotar, $n. Les coupables sont effectivement des hurans de la tribu Tranchecrin. L\'un d’eux en particulier dirige les attaques : Kreenig Grondegroin. On l\'a vu au nord-est d\'ici, juste au sud de la route venant de Durotar. Pour régler le problème, Kreenig doit mourir.$b$bPour s\'assurer que les attaques cessent, je suggérerais quand même que l\'on fasse un exemple.$b$bTuez Kreenig et des membres de sa tribu, puis revenez me voir lorsque vous aurez sa Défense.",
    ["O"] = "Tuer 8 Géomanciennes Tranchecrin, 8 Défenseurs Tranchecrin et Kreenig Grondegroin.$b$bPuis apporter la Défense de Kreenig Grondegroin à Thork à la Croisée.",
    ["T"] = "La perturbation cesse",
  },
  [873] = {
    ["D"] = "Le grand Isha Awak est le seigneur de ces eaux. Grande est sa force et solennelle est sa fierté. Les humains de la côte le craignent, car il en a occis beaucoup parmi eux.$B$BMais il ne me fait pas peur. Je suis reconnaissante de son existence. Il est un digne défi et une proie honorable.$B$BSi vous êtes $gprêt:prête; alors partez nager et cherchez Isha Awak, le Destin des profondeurs.$B$BSon esprit demeure dans son cœur, et l\'entendre battre, c\'est voir votre destinée.",
    ["O"] = "Apporter le Cœur d\'Isha Awak à Mahren Skyseer.",
    ["T"] = "Isha Awak",
  },
  [874] = {
    ["D"] = "Vous avez chassé les bêtes des Tarides. Votre esprit est fort. Mais un chasseur doit toujours être prêt. Un véritable chasseur doit être capable de traquer une proie sur n\'importe quelle piste. Des montagnes ou des marais ne sauraient le rebuter.$B$BEt pas même la mer.$B$BAllez trouver ma soeur, Mahren. Elle chasse les grandes bêtes maritimes le long de la côte. Ce sera votre professeur.$B$BMais soyez $gprudent:prudente; tandis que vous la recherchez, car la côte des Tarides est détenue par des humains.",
    ["O"] = "Parler à Mahren Skyseer.",
    ["T"] = "Mahren Oeil-des-Cieux",
  },
  [875] = {
    ["D"] = "Ha ha ha ! $n. Vous en voulez encore ? Bien. J\'aime entendre ça.$b$bCette fois-ci je veux que vous tuiez certaines des lieutenants harpies des Collines arides. Seules les Tueuses Ensorçaile peuvent être lieutenants, et vous pouvez les reconnaître à la bague qu\'elles portent. C\'est une vilaine bande, qui dirige beaucoup de subalternes dans le secteur. Tuer 6 d\'entre elles portera un coup décisif à leur matriarcat.$b$bFaites en sorte qu\'elles meurent dans la douleur, $n. Nous voulons montrer à ces femelles qu\'il n\'est pas sage de choisir la Horde comme adversaire.",
    ["O"] = "Rassembler 6 Anneaux de lieutenant harpie sur des Tueuses Ensorçaile et les apporter à Darsok Swiftdagger à la Croisée.",
    ["T"] = "Lieutenants harpies",
  },
  [876] = {
    ["D"] = "Vous vous en êtes tellement bien $gsorti:sortie; avec les lieutenants et autres harpies subalternes que j\'aimerais que vous fassiez une dernière chose pour moi.$B$BSerena Plumesang est la sœur d\'une harpie nommée Plumesang qui a été tuée par Rexxar il y a longtemps. Apparemment, ces attaques sur les caravanes de la Horde sont une vengeance pour la mort de sa sœur.$B$BJ\'ai besoin que vous lui tranchiez la gorge et que vous me rapportiez sa tête. Je la placerai sur la prochaine caravane que nous enverrons... Histoire de donner aux harpies un sujet de réflexion.",
    ["O"] = "Tuer Serena Plumesang et apporter sa tête à Darsok Swiftdagger à la Croisée.",
    ["T"] = "Serena Plumesang",
  },
  [877] = {
    ["D"] = "Tout comme celle que vous avez trouvée aux Bassins oubliés, il se peut qu\'il y ait des fissures dans les autres oasis des Tarides. Si tel est le cas, alors peut-être que ces fissures sont la source de vie des oasis.$b$bNous devons tester cette hypothèse.$b$bTenez, prenez ces graines. Elles sont mortes et stériles, mais emportez-les à l\'Oasis stagnante, au sud-est. S\'il y a une fissure là-bas, alors placez-y les graines... et observez.",
    ["O"] = "Retourner voir Tonga Runetotem à la Croisée, après avoir enquêté à l\'Oasis stagnante.",
    ["T"] = "L\'oasis stagnante",
  },
  [878] = {
    ["D"] = "Vous devez frapper mes ennemis <grogne>. Vous devez aider Mangletooth et les Tranchecrins en affaiblissant les Dos-hirsute Les deux tribus accordent beaucoup d\'importance à la spiritualité et sont très liées à la terre et à ses pouvoirs <grogne>. Cela nous apporte de la force en tout, et pour la guerre en particulier.$B$BSi vous voulez découvrir qui a causé à Thork <grogne> et à la Horde un tel supplice, alors aidez Mangletooth ! Attaquez les chefs spirituels des Dos-hirsute au nord. Affaiblissez-les afin que les Tranchecrins puissent les vaincre et régner sur les Tarides.",
    ["O"] = "Tuer 6 Sourciers Dos-hirsute, 12 Tisseurs d’épines Dos-hirsute et 12 Géomanciennes Dos-hirsute puis retourner auprès de Mangletooth au camp Taurajo dans les Tarides.",
    ["T"] = "Tribus en guerre",
  },
  [879] = {
    ["D"] = "Trois membres de ma tribu sont venus du Kraal pour diriger les raids contre la Horde, $n <grogne>. Ils sont brutaux et malins, et si vous parvenez à les vaincre, alors votre Croisée et même la plus grande partie des Tarides seront en paix... <grogne>.$B$BNak, Kuz et Lok Plaie-des-orcs sont ceux que vous recherchez. Ils se trouvent loin au sud des Tarides. L\'un lance des sorts, l\'autre est un pisteur, et leur chef, celui qu\'on nomme Plaie-des-orcs <grogne> est un guerrier. Tuez-les, $n, tout comme ils m\'ont tué.",
    ["O"] = "Tuer Nak, Kuz et Lok Plaie-des-orcs et apporter leurs crânes à Mangletooth au Camp Taurajo dans les Tarides.",
    ["T"] = "Une trahison de l\'intérieur",
  },
  [880] = {
    ["D"] = "Vos découvertes sont incroyables, $N. Ces oasis détiennent des propriétés qui doivent provenir d\'une source extérieure. Ou peut-être intérieure.$B$BJe veux savoir comment ces fissures affectent les bêtes qui boivent aux eaux des oasis.$B$BChassez les gueules d\'acier des oasis à l\'Oasis luxuriante et à l\'Oasis stagnante. Apportez-moi leurs carapaces pour que je les examine.",
    ["O"] = "Apporter 8 Carapaces de Gueules d\'acier altérées à Tonga Runetotem à la Croisée.",
    ["T"] = "Source de vie",
  },
  [881] = {
    ["D"] = "Blanchebrume, Echeyakee dans la langue des taurens, est le roi des lions des savanes. Il chasse si furtivement qu’il est comme une brume légère et blanche sur la terre. Et il tue si vite que ses proies n\'ont pas le temps d\'avoir peur ni de souffrir.$B$BLes taurens disent qu\'il est à la fois la miséricorde et la mort.$B$BVous apprendrez si cela est vrai, car je vous lance sur la trace d\'Echeyakee. Son repaire est au nord-est de la Croisée, parmi les os des kodos géants.$B$BRendez-vous à son repaire, soufflez dans cette corne, et il viendra.",
    ["O"] = "Apporter la Peau d\'Echeyakee à Sergra Darkthorn à la Croisée.",
    ["T"] = "Echeyakee",
  },
  [882] = {
    ["D"] = "Ishamuhale, Lancecroc, est le plus redoutable des raptors solécaille. Il ne chasse pas pour se divertir, ni pour se nourrir. Il chasse par passion. Il tue car sa nature est de tuer.$B$BEt vous allez apprendre de cette nature, $N, car votre voie suit à présent les traces griffues d\'Ishamuhale.$B$BCommencez la chasse. Tuez sa proie favorite, un zhévra, puis emmenez sa carcasse à l\'arbre mort au nord-ouest de Ratchet. Ishamuhale sera attiré par l’odeur.$B$BSoyez $gprêt:prête; lorsqu\'il arrivera.",
    ["O"] = "Apporter le Croc d\'Ishamuhale à Jorn au Camp Taurajo.",
    ["T"] = "Ishamuhale",
  },
  [883] = {
    ["D"] = "Le Sabot de Lakota\'mani est un symbole de la force du grand kodo.",
    ["O"] = "Apporter le Sabot de Lakota\'mani à Jorn au Camp Taurajo.",
    ["T"] = "Lakota\'mani",
  },
  [884] = {
    ["D"] = "Lorsqu\'on le touche, des étincelles jaillissent de l\'épine caudale d\'Owatanka.",
    ["O"] = "Apporter l\'Epine caudale d\'Owatanka à Jorn au Camp Taurajo.",
    ["T"] = "Owatanka",
  },
  [885] = {
    ["D"] = "La plume de Washte Pawne est douloureuse au toucher.",
    ["O"] = "Apporter la Plume de Washte Pawne à Jorn Skyseer au Camp Taurajo.",
    ["T"] = "Washte Pawne",
  },
  [886] = {
    ["D"] = "Les druides de Thunder Bluff sentent une étrange puissance qui fuit dans les Tarides, à l\'est de Mulgore.$b$bNous avons envoyé Tonga Runetotem pour enquêter sur la source de cette puissance, mais nous craignons qu\'il ait besoin d’aide.$b$bVoyagez jusqu\'aux Tarides et parlez à Tonga. Il sera à la Croisée. Pour atteindre cet endroit, prenez la route à l\'est en sortant du village Bloodhoof. Entrez dans les Tarides, puis tournez vers le nord lorsque la route se divise. Continuez dans cette direction et vous trouverez la Croisée.$b$bEt ne quittez pas la route, $n, car les Tarides sont hostiles.",
    ["O"] = "Parler à Tonga Runetotem à la Croisée.",
    ["T"] = "Les oasis des Tarides",
  },
  [887] = {
    ["D"] = "J\'aurais vraiment souhaité que le vice-amiral Grezzlik fasse un meilleur travail pour que nos navires marchands puissent sillonner les mers en toute sécurité. Avec tout l\'or que les princes marchands ont versé, voir autant de pirates attaquer mes navires et voler mes marchandises me rend malade !$b$bJ\'ai entendu dire que les Flibustiers des Mers du Sud ont établi un camp juste au sud d\'ici. Ils ont été une source d\'irritation constante et si Grezzlik ne s\'en occupe pas, alors, vous, peut-être, pourriez m\'en débarrasser.",
    ["O"] = "Tuer 12 Brigands et 6 Canonniers des Mers du Sud pour Gazlowe de Ratchet.",
    ["T"] = "Les flibustiers des mers du Sud",
  },
  [888] = {
    ["D"] = "J\'aimerais récupérer toutes mes marchandises. Hélas, je suis sûr que ce n\'est pas possible. Mais il y a des choses que je dois absolument récupérer !$b$bComme vous pouvez le constater, mon observatoire est prêt à fonctionner, mais il manque la plus petite des deux lentilles. La première est arrivée de Durotar par caravane, mais la deuxième a été envoyée par bateau, et elle ne m\'est jamais parvenue ! Je n\'ai pas non plus reçu mes bottes de chez Drizzlik à Baie-du-Butin !$b$bPartez pour le camp des pirates, et voyez si vous les trouvez.",
    ["O"] = "Retrouver la Cargaison de bottes et la Lentille de télescope pour Gazlowe de Ratchet.",
    ["T"] = "Course à la lentille",
  },
  [889] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "L\'esprit du vent",
  },
  [890] = {
    ["D"] = "J\'ai attendu durant une éternité l\'arrivée de mon dernier chargement de marchandises de Baie-du-Butin ! Je suis à peu près sûr qu\'il a dû être volé par les Flibustiers, mais pour que je le sois complètement, pourriez-vous descendre du côté des docks et demander à Dizzwig si mes marchandises ont déjà été livrées à mon entrepôt sans que je le sache ?$B$BTenez, descendez mon Grand livre à Dizzwig et demandez-lui de vérifier à nouveau mes inventaires par rapport à ses registres.",
    ["O"] = "Apporter le Grand Livre de Gazlowe au Maître des quais Dizzywig.",
    ["T"] = "La livraison perdue",
  },
  [891] = {
    ["D"] = "J\'ai encore perdu un navire contre ces humains trop zélés du Fort du Nord !$b$bNous devons nous occuper de ces bâtards. Leur paranoïa les met dans un état de totale frénésie. Ces gâchettes faciles, ces rustres insouciants canardent tout ce qui passe dans leur ligne de mire.$b$bPartez vers le sud, le long de la Côte des marchands, vers le Fort, et persécutez-les. Apportez les médailles de leurs soldats comme preuve. Et pour l\'amour de ma flotte marchande, tuez le capitaine Fairmount et ses canonniers trop enthousiastes.",
    ["O"] = "Le capitaine Thalo\'thas Brightsun de Ratchet veut que vous collectiez 10 Médailles de Theramore et que vous tuiez le Capitaine Fairmount, le Canonnier Whessan et le Canonnier Smythe.",
    ["T"] = "Sous le feu de l\'ennemi",
  },
  [892] = {
    ["D"] = "Tout a l\'air correct selon mes registres, $n. Retournez-y et dites à Gazlowe que sa chance a tourné. Je ne serais pas surpris que les pirates aient pris ses marchandises.$b$bJe ne peux rien faire d\'autre pour lui. Oh !… Vous pourriez lui dire quand même que j\'ai quelque chose venant d\'Undermine à son nom, s\'il peut faire un saut pour le chercher.",
    ["O"] = "Rapporter le Grand Livre de Gazlowe à Gazlowe à Ratchet.",
    ["T"] = "La livraison perdue",
  },
  [893] = {
    ["D"] = "Les hurans Tranchecrin, au sud, au-delà du Champ des géants, n\'ont pas de forgerons compétents, que je sache. Mais ils ont apparemment commencé à construire des armes plus solides. J\'aimerais mettre la main sur différents types de ces armes pour acquérir leurs techniques, si possible.$B$BSi vous souhaitez œuvrer pour l\'honneur de la Horde, apportez-moi des exemples de leurs armes. Obtenez une dague de leurs traqueurs, une baguette de leurs prophètes, et un bouclier de leurs frénétiques. Cela devrait parfaitement faire l\'affaire.",
    ["O"] = "Apporter un Poignard de Tranchecrin, une Baguette Tranchecrin carbonisée et un Bouclier Tranchecrin à Tatternack Steelforge au camp Taurajo dans les Tarides.",
    ["T"] = "Des armes de choix",
  },
  [894] = {
    ["D"] = "La KapitalRisk a construit un petit centre de recherches loin au nord, au sud-ouest de la Videfange. Je ne sais pas vraiment ce qu\'ils y font, mais j\'ai quand même réussi à découvrir qu\'ils font des expériences avec quelque chose qu\'ils appellent \"Samophlange\".$B$BÀ présent, que diable est le Samophlange ? Eh bien, quoi que ce soit, je veux l\'examiner, et j\'ai besoin que quelqu\'un le prenne.$B$BJ\'ai réussi à obtenir une copie de leur Manuel de la console de contrôle, vous devriez ainsi comprendre comment en désengager le Samophlange.",
    ["O"] = "Accéder à la Console de contrôle au Centre de recherches de la KapitalRisk.",
    ["T"] = "Samophlange",
  },
  [895] = {
    ["D"] = "ON RECHERCHE !$b$bBaron de Longrivage, capitaine de \"l\'Insouciant\".$b$bLe capitaine Longrivage de Gilnéas commande des navires pour le compte des Flibustiers des Mers du sud, il est recherché pour actes de piraterie. Ses restes doivent être identifiables !$b$bGazlowe",
    ["O"] = "Apporter la tête du baron de Longrivage à Gazlowe à Ratchet.",
    ["T"] = "ON RECHERCHE : baron de Longrivage",
  },
  [896] = {
    ["D"] = "Etant Maître des quais d\'un port plein d\'activité comme Ratchet, je prends toujours le pouls de l\'information. Je sais tout des échanges de biens et d\'argent entre ici et Baie-du-Butin.$B$BLa dernière nouvelle que j\'ai entendue concerne la mine des Pierriers de la KapitalRisk, au nord-est de la Videfange. L\'un des mineurs y a découvert une émeraude aussi grosse que votre poing. Je connais quelques acheteurs qui voudraient mettre la main sur un tel joyau, et je suis prêt à faire 50-50.",
    ["O"] = "Prendre l\'Emeraude œil-de-chat à l\'un des Surveillants ou l\'un des Massacreurs de la KapitalRisk pour le Maître des quais Dizzywig à Ratchet.",
    ["T"] = "La bonne fortune d\'un mineur pauvre",
  },
  [897] = {
    ["D"] = "Cette étrange créature, qui ressemble à un insecte, n\'a pas l\'air de faire partie de l\'ordre naturel, comme si elle n\'appartenait simplement pas aux Tarides.",
    ["O"] = "Apporter la Tête du Moissonneur à Jorn Skyseer.",
    ["T"] = "Le Moissonneur",
  },
  [898] = {
    ["D"] = "Enfin ! Quelqu\'un est venu me sauver !$B$BIl est incroyable que vous ayez passé les gardes. Ces fanatiques de Theramore sont devenus fous. Ils ont coulé notre navire et m\'ont emprisonné. Je suis le seul survivant. Ils ont dit que j\'étais leur ennemi et un danger pour l\'Alliance.$B$BMoi ! Un danger pour leur parodie d\'Alliance ? J\'étais matelot sur un transport de gnôle entre Ratchet et Baie-du-Butin.$B$BMais assez parlé. Aidez-moi à retourner à Ratchet, voulez-vous ? Dites-moi lorsque vous serez $gprêt:prête; et nous prendrons la fuite.",
    ["O"] = "Escorter en toute sécurité Gilthares Firebough jusqu\'au capitaine Brightsun à Ratchet.",
    ["T"] = "Hors du fort",
  },
  [899] = {
    ["D"] = "Connaissez-vous l’affliction du doute, $c ? Sachez seulement que je suis là toute la journée à scruter l’horizon pour repérer ces monstres. J’ai tué chaque homme-sanglier que j’ai rencontré depuis, mais ma soif de sang est loin d’être étanchée. Peut-être qu’au lieu d’aller au nord, vers la Croisée, j’aurais dû me diriger sur Taurajo.$B$BVous, $c, vous pourriez m’aider.$B$BTuez-en autant que vous pourrez. Apportez-moi leurs défenses et nous boirons ensemble à leur destruction.",
    ["O"] = "Apporter 60 Défenses de huran Dos-hirsute à Mankrik, à la Croisée.",
    ["T"] = "Consumé par la haine",
  },
  [900] = {
    ["D"] = "Les trois valves de contrôle sont à présent ouvertes. Les indications sur le panneau de contrôle vous amènent à penser qu\'elles doivent être fermées pour que l\'appareil puisse être arrêté.",
    ["O"] = "Fermer la Valve de contrôle du carburant, la Valve de réglage et la Valve de contrôle principale, puis utiliser à nouveau la console de contrôle.",
    ["T"] = "Samophlange",
  },
  [901] = {
    ["D"] = "Maintenant que l\'appareil est désactivé, le panneau de contrôle peut s\'ouvrir et on peut sortir le Samophlange de la console. À présent que tout est en place, il reste à trouver la clé qui déverrouillera la console.",
    ["O"] = "Obtenir la Clé de la console de l\'artisan Sniggles, puis l\'utiliser sur la console de contrôle.",
    ["T"] = "Samophlange",
  },
  [902] = {
    ["D"] = "Ouvrir la console de contrôle révèle des entrelacs de fils, de tubes et d\'autres objets mécaniques étranges, dont la plupart ne semblent avoir aucune utilité. En les écartant et en fouillant au fond le la console, vous découvrez le Samophlange. Vous le retirez d\'un coup sec de son réceptacle.",
    ["O"] = "Rapporter le Samophlange à Sputtervalve à Ratchet.",
    ["T"] = "Samophlange",
  },
  [903] = {
    ["D"] = "Il est temps à présent de courir après quelque chose qui mord vraiment. Les rôdeurs des Tarides sont une espèce résistante. Résistante, pleine de ressources, sournoise… et mortelle. Vous les trouverez parmi les herbes hautes. Ils sont nombreux au sud-ouest de la Croisée.$B$BSoyez $gprudent:prudente; tandis que vous les chassez, $N, ou vous ne serez plus $gle prédateur:la prédatrice;, mais bien la proie.",
    ["O"] = "Collecter 7 Griffes de Rôdeur des savanes pour Sergra Darkthorn à la Croisée.",
    ["T"] = "Les rôdeurs des Tarides",
  },
  [904] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [905] = {
    ["D"] = "À présent, $N, la chasse devient plus difficile. Vous devez battre votre proie, et trouver le chemin jusqu\'à son repaire.$B$BChassez les raptors solécaille au sud. Tuez-les et enlevez leurs plumes. Placez les plumes dans les nids des Faucheurs au sud-ouest de l\'oasis stagnante. Montrez à leurs semblables que vous ne les craignez pas !",
    ["O"] = "Tuer des raptors solécaille et récupérer leurs plumes. Placer ces plumes dans les 3 nids de Faucheurs solécaille, puis retourner voir Sergra Darkthorn à la Croisée.",
    ["T"] = "Les Faucheurs furieux",
  },
  [906] = {
    ["D"] = "Apportez la tête de Lok à Thork à la Croisée, $n <grogne>. Il devrait savoir ce qu\'il est advenu de ma tribu sans le moindre mot--ha, mais je ne m\'attends pas à ce qu\'il croie que Mangletooth y a <grogne> contribué. Mais il est certain qu\'il vous récompensera pour vous être $goccupé:occupée; de tels actes <reniflement>.$B$BJe vais continuer à vous bénir du pouvoir d\'Agammagan aussi longtemps que je resterai dans cette cage, $n. Jusque-là, adieu.",
    ["O"] = "Apporter le Crâne de Lok à Thork à la Croisée des Tarides.",
    ["T"] = "Une trahison de l\'intérieur",
  },
  [907] = {
    ["D"] = "À présent, $N, vous devez combattre les grands lézards-tonnerre. Ces bêtes sont si imposantes que le sol tremble lorsqu\'ils parcourent les plaines. Prenez garde lorsque vous les combattez, car un faux pas peut vous jeter directement sous leurs pattes.$B$BVous trouverez les lézards-tonnerre au sud des Tarides. Ils sont connus sous les noms de Groins-tempête, Orageux et Têtes-tonnerre. Revenez me voir lorsque vous les aurez vaincus.",
    ["O"] = "Apporter 3 doses de Sang de lézard-tonnerre à Jorn Skyseer, à Camp Taurajo.",
    ["T"] = "Les lézards-tonnerre enragés",
  },
  [908] = {
    ["D"] = "Le Marteau du crépuscule s\'est déplacé dans les ruines d\'Ecrin-de-Lune des Profondeurs de Brassenoire. Leur présence forcera les élémentaires à travailler contre nous. Si nous ne nous occupons pas de cette région, ils en prendront complètement le contrôle.$B$B$N, allez à Brassenoire et trouvez la pierre de la brasse. Elle devrait se trouver quelque part, à proximité, dans l\'eau. À l\'intérieur, se trouve un Noyau de la Brasse, un objet qui, s\'il est utilisé correctement, offrira un historique de toute l\'activité des élémentaires. Si je l\'avais, je pourrais, avec l\'aide du Cercle terrestre, faire quelque chose pour les arrêter !",
    ["O"] = "Apporter le Noyau de la Brasse à Je\'neu Sancrea, à l’Avant-poste de Zoram\'gar, dans Ashenvale.",
    ["T"] = "Parmi les ruines",
  },
  [909] = {
    ["D"] = "Sur l\'élémentaire d\'eau vaincu, vous trouvez un étrange globe d\'eau. Vous avez la nette impression que l\'objet a absorbé l\'énergie de la créature. À l\'intérieur du globe, vous discernez une eau aux reflets malsains. Le globe lui-même semble insensible à toute forme d\'action physique.$B$BS\'il y a une personne qui puisse comprendre ce qu\'est cet objet, cela ne peut être que Je\'neu Sancrea du Cercle terrestre. Il se trouve dans l\'avant-poste de Zoram\'gar de la Horde, à Ashenvale.",
    ["O"] = "Apporter le Globe d\'eau étrange à Je\'neu Sancrea, à l’Avant-poste de Zoram\'gar, dans Ashenvale.",
    ["T"] = "Baron Aquanis",
  },
  [910] = {
    ["D"] = "J\'ai entendu parler des grands bateaux qui naviguent entre Cabestan et Baie-du-Butin ! C\'est génial de voyager sur l\'océan et de faire de grandes batailles navales. Peut-être que je serai un puissant marin de la Horde quand je serai grand ! Ouais !$B$BVous voulez bien m\'emmener voir les quais de Cabestan, s\'il vous plaît ? Je vous promets que je sauterai pas dans l\'eau et que je ne ferai pas de bêtise. Je veux juste voir les grands bateaux et sentir l\'eau salée de la mer ! Ouais !",
    ["O"] = "Emmenez votre protégé sur les quais de Cabestan, dans les Tarides.",
    ["T"] = "Un tour sur les quais",
  },
  [911] = {
    ["D"] = "Ils disent qu\'Orneval est la nouvelle frontière, et que le Rempart de Mor\'shan dans les Tarides est à sa porte. Ça doit être génial d\'être un pionnier, de trouver tout ce dont on a besoin dans la nature sauvage ! Ils disent que c\'est là que la Horde et l\'Alliance se battront le plus dans l\'avenir, et je veux y être, $N !$B$BVous voulez bien m\'emmener au Rempart de Mor\'shan pour que je puisse voir à quoi ressemble la frontière, s\'il vous plaît ? Je veux voir l\'endroit où la Horde chasse ces sales elfes de la nuit puants !",
    ["O"] = "Emmenez votre protégé au Rempart de Mor\'shan dans la région nord des Tarides, juste à l\'entrée d\'Orneval.",
    ["T"] = "L\'accès vers la frontière",
  },
  [912] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Stonesplinter Trogg Disguise",
  },
  [913] = {
    ["D"] = "Le faucon-tonnerre, $N, est une bête féroce. Il est temps pour vous d\'en affronter. Vous devez trouver où il rôde, puis m\'apporter ses ailes comme preuve de votre chasse fructueuse.$B$BParvenez-y, et l\'heure de notre séparation sonnera bientôt.",
    ["O"] = "Trouver et tuer un faucon-tonnerre, et rapporter ses ailes à Jorn Skyseer, au Camp Taurajo.",
    ["T"] = "Le cri du faucon-tonnerre",
  },
  [914] = {
    ["D"] = "Les druides des Cavernes des lamentations, les druides du Croc, sont une aberration. Ils appartenaient à un ordre de druides nobles voulant soigner les Tarides, mais ils tentent maintenant de plier cette terre à leurs fantasmes.$B$BIls ont quatre chefs, et chacun possède une gemme de rêve. Même à présent, leurs visages me hantent ! Vous devez les tuer et m\'apporter les gemmes. Ces terres pourront peut-être ainsi connaître à nouveau la paix.$B$BAllez, $N. Vous les trouverez, tapis, au fond des Cavernes des lamentations.",
    ["O"] = "Apporter les Gemmes de Cobrahn, d\'Anacondra, de Pythas et de Serpentis à Nara Wildmane à Thunder Bluff.",
    ["T"] = "Les druides du Croc",
  },
  [915] = {
    ["D"] = "DE LA GLACE ! Oh, s\'il vous plaaaaîîîîîît, je peux avoir de la glace ?! La glace à la fraise est ma glace préférée, et la meilleure glace à la fraise du monde, c\'est celle d\'Aguilor & Dagharn ! C\'est ma glace préférée dans le monde entier !$B$BJ\'en ai mangé il y a longtemps aux courses des Salines, mais il paraît qu\'ils en vendent en ville ! S\'il vous plaît ? S\'il vous plaaaaîîîîîît ? Avec une deuxième boule sur le dessus ??!?!",
    ["O"] = "Allez chercher de la glace à la fraise pour votre protégé. Le gamin semble préférer la marque de glace Aguilor & Dagharn.",
    ["T"] = "Je veux une glaaaaace !",
  },
  [916] = {
    ["D"] = "Je suis venu à Sombrevallon pour observer les araignées Tissebois qui vivent dans la grotte de Sombrefil. Elles sont cousines d\'une variété bien plus petite. Je pense que l\'arbre-monde a eu un profond effet sur elles, et j\'aimerais des spécimens à étudier pour confirmer cette hypothèse.$b$bD\'abord, j\'aimerais un peu de leur venin. Prélevez des glandes à venin de Tissebois sur les araignées à l\'intérieur et autour de la grotte de Sombrefil, au nord. Je pourrai alors étudier les similitudes avec le venin de leurs cousines plus petites.",
    ["O"] = "Apporter 10 Glandes à venin Tissebois à Gilshalan Windwalker, à Aldrassil.",
    ["T"] = "Le venin des Tissebois",
  },
  [917] = {
    ["D"] = "À présent que j\'ai le venin de ces araignées, j\'aimerais étudier des spécimens vivants. Malheureusement, capturer une araignée géante est plus que je ne peux vous demander, jeune $c. Et je ne pourrais pas moi non plus maîtriser une araignée géante.$b$bSi vous pouvez trouver un œuf non encore éclos, alors il sera plus facile de posséder des spécimens, et je peux organiser les choses pour les enfermer avant que les araignées ne sortent.$b$bIl doit y avoir un nid, au plus profond de la grotte de Sombrefil. S\'il vous plaît, partez chercher un œuf dans ce nid et rapportez-le-moi.",
    ["O"] = "Apporter un Œuf d\'araignée Tissebois à Gilshalan à Aldrassil.",
    ["T"] = "L\'œuf d\'araignée Tissebois",
  },
  [918] = {
    ["D"] = "Les sylvains de Teldrassil sont des élémentaires de la Nature. D\'une certaine manière, ils reflètent l\'ordre naturel des plantes et des animaux sur notre Grand Arbre.$b$bAussi est-ce inquiétant de voir les sylvains devenir furieux.$b$bJe pense que cela a quelque chose à voir avec la terre. J\'ai travaillé à différentes méthodes pour alimenter les plantes et aimerais les essayer sur des graines de sylvains. S\'il vous plaît, pouvez-vous collecter des graines de sylvain autour du lac Al\'Ameth et me les apporter ?",
    ["O"] = "Apporter 8 Graines de sylvain à Denalan au lac Al\'Ameth.",
    ["T"] = "Les graines de sylvain",
  },
  [919] = {
    ["D"] = "De petits sylvains poussent près des eaux du lac Al\'Ameth. J\'ai bien peur que nous ne puissions plus rien faire pour ces jeunes sujets. Nous devons les arracher avant qu\'ils ne grandissent trop et causent des problèmes.$B$BLorsque vous serez près du lac, arrachez les pousses de sylvains que vous verrez. Aidez-nous à garder une terre propre !",
    ["O"] = "Apporter 12 Pousses de sylvain à Denalan au lac Al\'Ameth.",
    ["T"] = "Les pousses de sylvain",
  },
  [920] = {
    ["D"] = "$n, n\'est-ce pas ? J\'ai entendu dire que Tenaron vous cherchait. Vous le trouverez à l\'endroit le plus haut d\'Aldrassil.$b$bIl se peut que nous vivions longtemps encore, mais ce serait mieux de ne pas le faire attendre trop longtemps.",
    ["O"] = "Parler à Tenaron Stormgrip au sommet d\'Aldrassil à Sombrevallon.",
    ["T"] = "Les appels de Tenaron",
  },
  [921] = {
    ["D"] = "Il est temps pour vous de partir en quête de votre destinée, $n. Mais avant que vous ne soyez $gprêt:prête; à vous aventurer au-delà de nos forêts enchantées, il y a beaucoup à apprendre de notre histoire récente.$b$bNotre peuple a beaucoup changé depuis la Bataille du mont Hyjal. Nordrassil n\'est plus que l\'ombre pâle de ce qu\'il était autrefois... Son pouvoir avait battu Archimonde et repoussé la Légion ardente...$b$bIl y a une tâche que vous devez accomplir. Partez pour le Puits de lune au nord d\'Aldrassil et apportez-moi une fiole de son eau.",
    ["O"] = "Remplir la Fiole de cristal et la rapporter à Tenaron Stormgrip au sommet d\'Aldrassil.",
    ["T"] = "La couronne de la Terre",
  },
  [922] = {
    ["D"] = "$N, pouvez-vous apporter l\'une des graines que vous avez trouvées à mon ami, Rellian Greenspyre ? Il est druide à Darnassus, et la dernière fois que nous avons parlé il m\'a révélé son intérêt concernant mes travaux sur les sylvains. Il avait quelques idées de son côté et il appréciera d\'avoir une graine comme spécimen à étudier.$B$BMerci, $N. Vous m\'avez été d\'un grand secours. J\'espère qu\'un jour, vous verrez les fruits de mon labeur.$B$BRellian parcourt souvent les chemins de l\'Enclave cénarienne à Darnassus.",
    ["O"] = "Apporter une Graine de sylvain à Rellian Greenspyre à Darnassus.",
    ["T"] = "Rellian Vertecime",
  },
  [923] = {
    ["D"] = "Une maladie décime les sylvains. Nous essayons d\'en trouver la cause, mais tant que nous n’y parvenons pas nous devons arracher les sylvains contaminés, pour la sécurité de Teldrassil. Ceux qui poussent près du lac d\'Aigue-vive, au nord de Teldrassil, sont les plus atteints. Nous devons les arracher !$b$bDétruisez les sylvains que vous trouvez, et prélevez la Tumeur moussue qui se développe sur eux. Apportez-moi les tumeurs afin qu\'elles soient brûlées.",
    ["O"] = "Apporter 5 Tumeurs moussues à Rellian Greenspyre à Darnassus.",
    ["T"] = "Tumeurs",
  },
  [924] = {
    ["D"] = "La Graine démoniaque est un outil puissant au service de la Légion ardente. Elle repose dans une caverne du pic de Brume-funeste, au-dessus d\'un diabolique autel de feu.$b$bJ\'ai préparé des Pierres de puissance qui, lorsqu\'elles seront placées près de la graine, couperont sa connexion avec la Légion.$b$bPrenez une pierre sur la table et portez-la à l\'autel de feu au sommet du pic de Brume-funeste. Voyagez à l\'ouest, puis au nord lorsque la route se divise. Le pic sera sur votre gauche. L\'escalade la plus sûre s\'effectue par la face nord.$b$bEt faites vite, le pouvoir de la pierre faiblit avec le temps.",
    ["O"] = "Prendre une Pierre de puissance imparfaite. L\'apporter à l\'autel de feu avant que le pouvoir de la pierre n\'expire, puis revenir à Ak\'Zeloth.",
    ["T"] = "La Graine démoniaque",
  },
  [925] = {
    ["D"] = "On dit que Cairne Sabot-de-sang est l\'un des plus grands héros de la Horde de tous les temps. Quand je serai grand, je veux être un héros de la Horde, moi aussi !$B$BVous connaissez Cairne, $N ? Vous pouvez me faire une faveur, s\'il vous plaît ? Vous pouvez avoir son empreinte de sabot pour moi ? Je sais pas comment lui demander, et c\'est sans doute une personne bien trop occupée pour s\'occuper de quelqu\'un comme moi... Mais pas vous ! Vous êtes $gun aventurier:une aventurière;, comme lui ! Je parie qu\'il vous donnerait facilement son empreinte de sabot !$B$BS\'il vous plaît ?",
    ["O"] = "Allez aux Pitons du Tonnerre pour tenter d\'obtenir l\'empreinte de sabot de Cairne pour votre protégé.",
    ["T"] = "L\'empreinte de Cairne",
  },
  [926] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Pierre de puissance imparfaite",
  },
  [927] = {
    ["D"] = "Le cœur de Noiremousse le Fétide est couvert d\'une mousse sombre et huileuse. En fait, ce qui permet d’identifier ce cœur, c’est sa pulsation, lente et rythmée.$b$bPeut-être que Denalan voudra voir ce cœur ?",
    ["O"] = "Apporter le Cœur moussu à Denalan au lac Al\'Ameth.",
    ["T"] = "Le cœur moussu",
  },
  [928] = {
    ["D"] = "Bien que je puisse vous entretenir encore longtemps des Puits de lune et de Teldrassil, je dois vous faire partir. Corithras Moonrage va vous attendre. J\'ai versé l\'eau de la fiole que vous m\'avez apportée dans ce récipient que vous devez lui remettre.$b$bCherchez Corithras. Vous le trouverez au Puits de lune à Dolanaar. Suivez la route vers le sud à partir d\'Aldrassil et vers Sombrevallon, et continuez à suivre les pavés ronds lorsque la route se dirige vers l\'ouest.$b$bAttention, restez toujours sur la route, $n. Il y a de dangereuses bêtes, le soir, dans les forêts.",
    ["O"] = "Apporter le Récipient à moitié plein à Corithras Moonrage à Dolanaar.",
    ["T"] = "La couronne de la Terre",
  },
  [929] = {
    ["D"] = "Tout d\'abord, laissez-moi vous en dire plus à propos de la quête que vous devez accomplir. Les druides de Darnassus utilisent l\'eau des Puits de lune de Teldrassil, et leurs Puits de lune doivent être remplis de temps en temps. En utilisant ces fioles spécialement conçues, vous pouvez collecter l\'eau des Puits de lune.$b$bApportez ce récipient jusqu\'au Puits de lune à l’extérieur du village de Brise-stellaire à l\'est, et remplissez-le d\'un peu de ses eaux. Puis revenez me voir. Lorsque vous aurez accompli votre tâche, je reprendrai l\'histoire là où Tenaron s’est arrêté.",
    ["O"] = "Remplir la Fiole de jade et la rapporter à Corithras Moonrage à Dolanaar.",
    ["T"] = "La couronne de la Terre",
  },
  [930] = {
    ["D"] = "Pendu à cette plante, sous ses feuilles, se trouve un grand fruit rond.$B$BDenalan voudra étudier ce fruit, c\'est certain.",
    ["O"] = "Apporter le Fruit luminescent à Denalan au lac Al\'Ameth.",
    ["T"] = "Le fruit luminescent",
  },
  [931] = {
    ["D"] = "Les feuilles de la plante flamboient dans la lumière de la forêt, lui donnant une aura pulsante et sinueuse.$B$BVous pensez que Denalan en souhaiterait un spécimen.",
    ["O"] = "Apporter les Racines flamboyantes à Denalan au lac Al\'Ameth.",
    ["T"] = "Les racines flamboyantes",
  },
  [932] = {
    ["D"] = "Je vous avertis, $N, cette affaire doit rester entre nous. Les satyres nous embarrassent déjà suffisamment, et celui-ci est beaucoup trop proche.$B$BIl s\'appelle le seigneur Melenas. Il réside dans la caverne proche de Gangreroche où il a rassemblé de nombreux guerriers grells. Son cœur est aussi noir que la nuit, et il prépare quelque chose d\'abominable.$B$BVous devez le trouver à l\'intérieur de sa caverne proche juste au nord d\'ici, et m\'apporter sa tête.",
    ["O"] = "Tuer le seigneur Melenas et apporter sa tête à Tallonkai Swiftroot à Dolanaar.",
    ["T"] = "Haine folle",
  },
  [933] = {
    ["D"] = "Il y a un autre Puits de lune au sud-est de l\'entrée vers Darnassus, sur les rives des bassins d\'Arlithrien. Les Sentinelles ont des problèmes lors de leurs patrouilles dans ce secteur. Ils sont attaqués par les furbolgs du clan Pin-tordu, de plus en plus sauvages.$b$bSoyez $gprudent:prudente; quand vous chercherez le Puits, et gardez la main près de vos armes.",
    ["O"] = "Remplir la Fiole de tourmaline et la rapporter à Corithras Moonrage à Dolanaar.",
    ["T"] = "La couronne de la Terre",
  },
  [934] = {
    ["D"] = "Cependant, tout n\'était pas parfait sur Teldrassil. Les plans soigneusement conçus de Staghelm pour le nouvel Arbre Monde s\'étaient déroulés comme il l\'avait espéré. Mais il y eut un petit problème, un problème sans doute à l\'origine des nombreuses difficultés rencontrées sur Teldrassil.$B$B. Mais je ne vais pas m\'étendre maintenant sur ce sujet. Vous devez vous rendre au dernier puits de lune, au nord-ouest, dans la clairière de l\'Oracle. Sous les branches de l\'arbre-oracle se trouve le premier et le plus puissant de nos puits. Remplissez un flacon de son eau et revenez me voir.",
    ["O"] = "Remplir la Fiole d\'améthyste et la rapporter à Corithras Moonrage à Dolanaar.",
    ["T"] = "La couronne de la Terre",
  },
  [935] = {
    ["D"] = "Sans la bénédiction d\'Alexstrasza la Lieuse de vie et celle de Nozdormu l\'Éternel, la croissance de Teldrassil ne fut pas parfaite. On rapporte que des bêtes étranges sortaient de ses racines et que des furbolgs enragés attaquaient des voyageurs.$b$bJe ne peux qu\'espérer que l\'archidruide trouvera rapidement la solution. Je vais verser le contenu de toutes les fioles que vous m\'avez amenées dans ce récipient, pour que vous l\'apportiez à Darnassus.$b$bApportez-le à Fandral Staghelm, vous le trouverez dans le bosquet des druides.",
    ["O"] = "Apporter le Récipient plein à l\'archidruide Fandral Staghelm à Darnassus.",
    ["T"] = "La couronne de la Terre",
  },
  [936] = {
    ["D"] = "Pardonnez-moi, $c, mais j\'aimerais prendre quelques instants de votre temps, si cela est possible.$B$BLe Cercle cénarien de Thunder Bluff est très intéressé par des $gaventuriers aguerris tels:aventurières aguerries telles; que vous pour lui prêter main-forte dans un projet crucial. Bien que je n’en connaisse pas les détails, je peux vous dire que Hamuul Runetotem lui-même mène ce projet.$B$BJe vous en prie, si cela vous intéresse, allez lui parler en personne à la Cime des Anciens dans Thunder Bluff.",
    ["O"] = "Parler à l\'archidruide Hamuul Runetotem à Thunder Bluff.",
    ["T"] = "L\'aide à l\'archidruide Runetotem",
  },
  [937] = {
    ["D"] = "On m\'a envoyée ici avec un petit groupe de Sentinelles pour protéger l\'arbre-oracle des harpies qui ont construit leurs nids tout autour de la clairière. Nous essayons de les repousser petit à petit.$b$bLorsque l\'arbre-oracle a tenté d\'envoyer un messager avec un rapport, il a été attaqué par un groupe de harpies et tué.$b$bSi vous vous sentez suffisamment $gpréparé:préparée; pour cette tâche, allez à leurs nids, tuez-les et rapportez-moi leurs ceintures en guise de preuves de vos exploits.",
    ["O"] = "Acquérir 6 Ceintures plumesang et les apporter à la Sentinelle Arynia Cloudsbreak à la clairière de l\'Oracle.",
    ["T"] = "La clairière enchantée",
  },
  [938] = {
    ["D"] = "La fourrure du tigre à dents de sabre est d\'un gris particulier, tranchant avec les couleurs de la forêt environnante. Tandis qu\'elle repose là, vous remarquez qu\'elle est gravement blessée, de profondes lacérations déchirent son dos et son ventre.$b$bElle lève la tête et vous regarde, ses yeux bleus brillant d\'intelligence. Elle a l\'air de souhaiter vous suivre.",
    ["O"] = "Escorter Brume vers la Sentinelle Arynia Cloudsbreak au puits de lune, près de l\'arbre-oracle.",
    ["T"] = "Brume",
  },
  [939] = {
    ["D"] = "Vous étudiez la simple flûte de bois que vous avez récupérée après avoir vaincu Xavaric.$B$BLe bois noueux semble solide mais les spirales et les motifs de sa surface semblent se mouvoir quand vous tournez l’instrument entre vos mains. Soudain, vous êtes pris d’un sentiment d’appréhension...$B$BVous vous ébrouez, pensant que votre esprit vous joue des tours.$B$BIl est possible de trouver auprès d\'autres satyres des indices relatifs à ce que vous avez trouvé. Apportez vos découvertes au druide Eridan Bluewind qui vit dans la partie sud de Gangrebois.",
    ["O"] = "Apporter la Flûte de Xavaric et 5 échantillons de Lien maudit Jadefeu à Eridan Bluewind, dans le sud de Gangrebois.",
    ["T"] = "Flûte de Xavaric",
  },
  [940] = {
    ["D"] = "On entend murmurer la forêt qui loue vos grandes prouesses, $n. Les harpies une fois délogées de leurs nids, un grand calme s\'est répandu sur la forêt, ses créatures se sentant à nouveau en sécurité. J\'ai eu peur d\'envoyer un autre messager à l\'archidruide, mais je sais que vous, vous parviendrez à lui remettre un message.$b$bApportez-lui ceci et sachez que la forêt veillera à votre sécurité le long du chemin menant à la Forêt de pierre.",
    ["O"] = "Remettre le rapport de l\'arbre-oracle à l\'archidruide Fandral Staghelm à Darnassus.",
    ["T"] = "Teldrassil",
  },
  [941] = {
    ["D"] = "J\'ai débarrassé le cœur de cette ignoble mousse, mais il reste contaminé…$b$bEssayez de placer le cœur dans ce pot. Il est rempli d’un terreau spécial qui pourrait le nettoyer et le guérir.",
    ["O"] = "Planter le Cœur corrompu dans le Pot de Denalan.",
    ["T"] = "Une belle plante",
  },
  [942] = {
    ["D"] = "Le fossile ressemble de manière frappante à un autre que j\'ai vu une fois à Ironforge. Un Archéologue du nom de Flagongut l\'avait apporté à notre conférence annuelle de la Ligue des Explorateurs. Il croyait que le fossile détenait un pouvoir que l\'on aurait pu réveiller d\'une certaine façon.$b$bLa dernière fois que j\'ai entendu parler de lui, Flagongut s\'était installé à la taverne de l\'Eau-profonde, au Port de Menethil, attendant une escorte pour le mener à l\'Excavation de Whelgar. Recherchez-le et montrez-lui le Fossile de Remtravel. Peut-être pourra-t-il révéler les secrets du passé.$b$bLe bateau pour le Port de Menethil part d\'Auberdine.",
    ["O"] = "Apporter le mystérieux Fossile à l\'Archéologue Flagongut au Port de Menethil.",
    ["T"] = "Les absences du Prospecteur",
  },
  [943] = {
    ["D"] = "Le Prospecteur Whelgar détient à présent le fossile original, en son site des marécages. Quand j\'ai découvert la Pierre de Relu, j\'ai cru y voir la solution à l\'énigme du fossile.$b$bAlors que je taillais la route pour aller à la rencontre de Whelgar, j\'ai subi une attaque de raptors et la relique fut perdue. J\'ignore laquelle des ces créatures chamarrées a bien pu l\'avaler, mais si vous pouvez la retrouver, je peux libérer le pouvoir de ces artefacts.$B$BApportez-moi donc la Pierre de Relu, et le fossile original que Whelgar possède.",
    ["O"] = "L\'archéologue Flagongut au Port de Menethil veut que vous lui apportiez la Pierre de Relu et le Fossile de Flagongut.",
    ["T"] = "Les absences du Prospecteur",
  },
  [944] = {
    ["D"] = "C\'est étrange que des nagas soient là, à Sombrivage. Mais... la présence du Marteau du crépuscule l\'est encore plus. Ce culte vénère de très anciens seigneurs de la terre. Des seigneurs vaincus il y a longtemps de cela.$b$bLe Glaive du Maître, au sud, est un endroit où est tombé l\'un de ces anciens seigneurs.$b$bAllez-y et cherchez d\'autres sectateurs du Marteau du crépuscule.$b$bPrenez cette fiole de divination. Utilisez-la pour créer une coupe de divination après avoir enquêté au Glaive du Maître. Puis parlez-moi par l\'intermédiaire de cette coupe.",
    ["O"] = "Trouver des informations, puis utiliser la Fiole de divination pour créer une Coupe de divination. Utiliser la coupe pour parler à Onu.",
    ["T"] = "Le Glaive du Maître",
  },
  [945] = {
    ["D"] = "Au secours !$b$bJe me promenais autour du Glaive du Maître, et ces infâmes sectateurs m\'ont entourée. Encore heureux que je sois douée pour me cacher !$b$bPouvez-vous m\'aider à m\'échapper ? Et j\'aurais aussi besoin de dire à ma sœur Therysil que je vais bien. Elle est à Ashenvale au sud, au sanctuaire d\'Aessina.",
    ["O"] = "Aider Therylune à s\'échapper, puis dire à Therysil au Sanctuaire d\'Aessina que sa sœur est saine et sauve.",
    ["T"] = "L\'évasion de Therylune",
  },
  [946] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [947] = {
    ["D"] = "Je prépare une potion qui nécessite des champignons rares, des champignons qui ne poussent que dans une certaine caverne. La caverne en question se trouve au-delà des Chutes de la Bondissante, à l\'est et légèrement au nord le long des montagnes.$b$bJ\'irais bien moi-même, mais le Bosquet des Anciens m\'a conseillé de me tenir à l\'écart de cet endroit. Nos vénérables alliés pressentent que cette caverne est l\'antre d\'une nouvelle créature maléfique, ici à Sombrivage.$b$bS\'il vous plaît, $n, ramassez ces champignons pour moi. Et pendant que vous y êtes, explorez la caverne pour confirmer le pressentiment des Anciens.",
    ["O"] = "Apporter 5 Pédoncules d\'Amanite et 1 Amanite phalloïde à Barithras Moonshade à Auberdine.",
    ["T"] = "La caverne aux champignons",
  },
  [948] = {
    ["D"] = "Onu, un Ancien du savoir au Bosquet des Anciens, est au courant de votre voyage vers les Chutes de la Bondissante et souhaite vous parler. Le bosquet se trouve au sud, blotti près des montagnes.$b$bLes Anciens sont patients et sages, $n. Mais si Onu souhaite votre conseil à propos de ce que vous avez vu aux chutes, alors j\'ai peur que ce soit urgent.",
    ["O"] = "Parler à Onu au Bosquet des Anciens.",
    ["T"] = "Onu",
  },
  [949] = {
    ["D"] = "Nous ne connaissons pas le but du Marteau du crépuscule au Glaive du Maître.$b$bFouillez leur camp au Glaive… Essayez de trouver quelque chose qui puisse nous indiquer leurs intentions.$b$bCar nous devons les connaître avant d\'établir un plan.",
    ["O"] = "Trouver un indice au Camp du Marteau du crépuscule au Glaive du Maître.",
    ["T"] = "Le camp du Marteau du crépuscule",
  },
  [950] = {
    ["D"] = "Consulter le Tome du crépuscule rend fou. Au premier regard, le texte commence à flotter et danser ; images et enluminures s\'entremêlent pour créer des formes grotesques.$b$bVous arrivez tout juste à déchirer une page de ces gribouillages déments avant qu\'un vertige ne vous submerge.",
    ["O"] = "Apporter les Gribouillages déments à Onu.",
    ["T"] = "La page qui rend fou",
  },
  [951] = {
    ["D"] = "Le parchemin que vous avez découvert au Glaive du Maître est la page d\'un texte ancien… écrit par des elfes ! Il est souillé de la corruption du Marteau du crépuscule. Ainsi, le culte utilise l\'ancienne magie des elfes pour ramener ses vieux maîtres dans notre monde.$b$bVoyagez jusqu\'aux ruines de Mathystra au nord-est, et cherchez d\'anciennes reliques, dans l\'herbe et parmi les pierres brisées. En les étudiant, il se peut que nous comprenions comment le Marteau du crépuscule compte exploiter la magie des elfes.",
    ["O"] = "Apporter 6 Reliques de Mathystra à Onu, au Bosquet des Anciens.",
    ["T"] = "Les Reliques de Mathystra",
  },
  [952] = {
    ["D"] = "Si vous avez suffisamment de temps pour servir de $gmessager:messagère; pour l\'arbre-oracle, je pourrais vous demander de remettre un message au bosquet des Anciens, à Sombrivage, au sud d\'Auberdine.$b$bVous aurez probablement à effectuer le voyage en hippogryphe, mais j\'ai foi en vos compétences. Apportez ceci à Onu, l\'Ancien du savoir. Il attend un mot de ma part, tout comme j\'attendais un mot de la Clairière de l\'Oracle.",
    ["O"] = "Livrer le message de Fandral à Onu, au bosquet des Anciens, à Sombrivage, au sud d’Auberdine.",
    ["T"] = "Bosquet des Anciens",
  },
  [953] = {
    ["D"] = "Vous trouverez à l\'est les ruines d\'Ameth\'Aran. Elles sont habitées par les esprits sans repos des Bien-nés qui vivaient autrefois dans ces murs, quand les serviteurs d\'Azshara y pratiquaient librement leur puissante magie.$b$bOn m\'a envoyée pour explorer les ruines et trouver deux grandes tablettes, sur lesquels sont racontées les histoires d\'Ameth\'Aran et de sa chute. Alors que je lisais les runes, j\'ai été surprise par les esprits, et je me suis enfuie.$b$bS\'il vous plaît, si vous le pouvez, aventurez-vous dans les ruines et déchiffrez les tablettes pour moi.",
    ["O"] = "Étudier les tablettes qui parlent d\'Ameth\'Aran et de sa chute, puis retourner voir la Sentinelle Tysha Moonblade à Sombrivage.",
    ["T"] = "La chute d\'Ameth\'Aran",
  },
  [954] = {
    ["D"] = "Les ruines de Bashal\'Aran à l\'est sont infestées de serviteurs démoniaques. Les fées et les satyres qui ont élu domicile dans la région se nourrissent des énergies magiques du secteur, et leurs pouvoirs augmentent avec le temps passé sur place.$b$bMais malgré cela, j\'ai remarqué un sanctuaire dont ils ne s\'approchent jamais. A l’ouest des ruines, au sommet d\'un petit promontoire, filtre une étrange aura bleue... Il doit y avoir une explication à la répugnance des démons.$b$bJ\'aimerais que vous enquêtiez à ce propos.",
    ["O"] = "Trouver la source de l’aura bleue dans les Ruines de Bashal\'Aran.",
    ["T"] = "Bashal\'Aran",
  },
  [955] = {
    ["D"] = "Si j\'avais à raconter l\'histoire de ma vie, il ne fait pas de doute qu\'elle dépasserait les limites de votre patience. Disons que ma vie a été longue et douloureuse, et cette forme spectrale qui est la mienne à présent est peut-être le pire de tous ces tourments.$b$bJe suis retenu ici par les pouvoirs de la magie. Bien que mes mots puissent sonner faux, je vous assure que je vous serais reconnaissant au-delà de tout si vous pouviez m’aider à identifier ce qui me retient prisonnier. Un sceau fait partie de ce dispositif, et en examinant les boucles d\'oreilles des fées et des grells, je pourrais en trouver la trace.",
    ["O"] = "Acquérir 8 Boucles d\'oreilles de grell pour Asterion à Bashal\'Aran.",
    ["T"] = "Bashal\'Aran",
  },
  [956] = {
    ["D"] = "Si les grells sont entrés en contact avec le sceau qui verrouille ma prison éternelle, je crois en connaître la cause. Il ne fait pas de doute que le sceau est en la possession du satyre qui les dirige.$b$bJe ressens fortement que cela doit être vrai, $n. L\'un des satyres le possède à présent. Si vous pouvez l\'obtenir, je serais si proche de pouvoir passer les barreaux de ma prison que les larmes me monteraient aux yeux.",
    ["O"] = "Obtenir l\'Ancien sceau de pierre de lune et l\'apporter à Asterion à Bashal\'Aran.",
    ["T"] = "Bashal\'Aran",
  },
  [957] = {
    ["D"] = "C\'est le savoir de l\'un des plus puissants Bien-nés qui a créé le sceau fermant ma prison. À Ameth\'Aran, dans les ruines au sud, jumelles de celles-ci, brûle une flamme antique, de couleur bleue. Dans cette flamme, ce sceau pourrait être détruit.$b$bSoyez $gprudent:prudente; dans les ruines, $n.",
    ["O"] = "Détruire l\'Ancien sceau de pierre de lune à la flamme antique qui se trouve dans Ameth\'Aran, puis retourner voir Asterion à Bashal\'Aran.",
    ["T"] = "Bashal\'Aran",
  },
  [958] = {
    ["D"] = "$C. J\'aurais quelque chose à vous demander.$b$bConnaissez-vous les ruines d\'Ameth\'Aran ? Si ce n\'est pas le cas, vous les trouverez à l’est de la route principale, à quelque distance au sud. Alors qu\'elles abritaient de nombreux Bien-nés puissants, elles ne sont plus à présent que l’image de la destruction à laquelle leurs expériences ont abouti.$b$bLes Sentinelles disent que les esprits des Bien-nés sont toujours là, et qu\'ils manient toujours leurs instruments magiques. Nous devons nous emparer de ces reliques, afin de les détruire.",
    ["O"] = "Retrouver 7 Reliques de Bien-né pour Thundris Windweaver à Auberdine.",
    ["T"] = "Les outils des Bien-nés",
  },
  [959] = {
    ["D"] = "Oups !$b$bJ\'ai eu une petite mésaventure, comme vous pouvez le voir ! La cargaison du matin venant de Baie-du-Butin était énorme. Enorme je dirais ! J\'ai jamais vu autant de caisses. Bien, j\'essayais d\'expédier rapidement les choses alors j\'ai commencé à décharger un peu plus que je ne pouvais soutenir. C\'est à ce moment-là que tout s\'est cassé la gueule.$b$bAvant que je sache ce qu\'il se passait, j\'ai vu cette peste de Magglish le Dingue filer avec une bouteille de porto vieille de 99 ans. Je l\'ai poursuivi jusqu\'aux Cavernes des lamentations. Pas question d\'y rentrer, quand même.$b$bPeut-être êtes-vous suffisamment $gcourageux:courageuse; pour aller le retrouver ?",
    ["O"] = "Le grutier Bigglefuzz de Ratchet veut que vous repreniez la Bouteille de porto vieille de 99 ans à Magglish le Dingue, qui se cache dans les Cavernes des lamentations.",
    ["T"] = "Fuite de porto aux docks",
  },
  [960] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Onu réfléchit",
  },
  [961] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Onu réfléchit",
  },
  [962] = {
    ["D"] = "La Société royale des apothicaires, basée dans la grande ville d\'Undercity de Lordaeron, m\'a envoyée ici pour une mission très particulière, $n. Peut-être souhaitez-vous m\'aider, et ainsi aider la Dame noire, dans nos efforts pour faire progresser les Réprouvés.$b$bJ\'ai récemment étudié un spécimen rare de flore appelé Fleur de serpent. Je crois qu\'en grande quantité cette herbe a un bon potentiel.$b$bMalheureusement, elle ne pousse qu’au plus profond des Cavernes des lamentations, un réseau de cavernes situé dans les Tarides.",
    ["O"] = "L\'Apothicaire Zamah à Thunder Bluff veut que vous récoltiez 10 Fleurs de serpent.",
    ["T"] = "Fleur de serpent",
  },
  [963] = {
    ["D"] = "À la suite des batailles au Puits d\'éternité, j\'ai appris qu\'Ameth\'Aran avait été détruit, et son peuple tué, y compris mon bel amour Anaya.$b$bJe n\'aurais jamais cru, des milliers d\'années plus tard, que des souvenirs d\'Anaya hanteraient toujours mes rêves. Frappé de stupeur, j\'ai erré dans les bois de Sombrivage, et j’ai retrouvé les ruines d\'Ameth\'Aran... Là j’ai vu l\'esprit hanté de ma bien-aimée.$b$bElle doit être libérée, mais je n\'ai pas le cœur de faire cela. Son esprit doit être détruit.",
    ["O"] = "Libérer l\'esprit d\'Anaya Dawnrunner et rapporter son pendentif à Cerellean Whiteclaw à Auberdine.",
    ["T"] = "Un amour éternel",
  },
  [964] = {
    ["D"] = "La clé pour accéder à Scholomance s\'appelle une Clé squelette. Elle doit être forgée à partir des restes d\'un squelette - de plusieurs en fait - et doit être renforcée des métaux les plus robustes dans un moule particulier. Enfin, un sceau de pouvoir provenant d\'une créature naturellement capable d\'ouvrir le portail de Scholomance permettra à la clé d\'avoir la fonction désirée.$B$BMais commençons par le commencement, $N. Nous allons avoir besoin de Fragments de squelette pour forger la clé. Les squelettes d\'Andorhal devraient nous les fournir, mais ceux en dehors des murs devraient pouvoir faire l\'affaire également.",
    ["O"] = "Apporter 15 Fragments de squelette au Pharmacien Dithers à la Barricade, dans les Maleterres de l\'ouest.",
    ["T"] = "Fragments de squelette",
  },
  [965] = {
    ["D"] = "Salutations, jeune $r. Je m\'appelle Elissa Starbreeze, et il est de mon devoir de protéger Auberdine de tout mal.$b$bÀ cette fin, j\'ai envoyé Balthule Shadowstrike observer les événements étranges qui se produisent près de la Tour d\'Althalaxx au nord-est.$b$bIl aurait déjà dû revenir. J\'ai peur qu\'il ait rencontré un danger imprévu dans la forêt. J\'apprécierais beaucoup si vous pouviez le trouver et vous assurer qu\'il va bien.",
    ["O"] = "Trouver Balthule Shadowstrike près de la Tour d\'Althalaxx à Sombrivage.",
    ["T"] = "La Tour d\'Althalaxx",
  },
  [966] = {
    ["D"] = "Un groupe de démonistes s\'est établi autour et à l\'intérieur de la tour. Je serais retourné à Auberdine pour rendre compte à Elissa plus tôt, mais j\'avais peur de rater quelque chose une fois parti.$b$bDelgren suspectait qu\'une telle compagnie puisse se rassembler à la tour, mais il n’en savait pas la cause.$b$bQuelques morceaux de parchemins appartenant à ces démonistes ont atterri entre mes mains, mais j\'en ai besoin d\'autres pour compléter le puzzle. Je vous avertis cependant de ne pas vous aventurer dans la tour, les démonistes qui s\'y trouvent sont beaucoup trop puissants !",
    ["O"] = "Collecter 4 Parchemins passés pour Balthule Shadowstrike près de la Tour d\'Althalaxx.",
    ["T"] = "La Tour d\'Althalaxx",
  },
  [967] = {
    ["D"] = "Mon maître, Delgren le Purificateur, est un paladin qui a gracieusement offert son assistance pour défendre nos forêts contre les démons et les morts-vivants. Il m\'a beaucoup appris sur le Feu sacré et sur l\'art de la bataille.$b$bDelgren doit être mis au courant des opérations du culte immédiatement.$b$bVous le trouverez loin au sud, au poste de Maestra dans la forêt d\'Ashenvale. Faites vite, j\'ai peur que la menace que représente la Rive noire n\'augmente d\'heure en heure.",
    ["O"] = "Remettre la Lettre de Balthule à Delgren le Purificateur dans la forêt d\'Ashenvale.",
    ["T"] = "La Tour d\'Althalaxx",
  },
  [968] = {
    ["D"] = "Bien qu\'affreusement usé et égratigné, le titre est encore lisible sur la couverture :$B$BLes puissances d\'en bas.$B$BÀ l\'intérieur du livre écorné se trouve une liste de noms imprononçables affublés d\'horribles titres, des méthodes de culte, des sacrifices conseillés... dont beaucoup incluent des créatures humanoïdes, vivantes.$B$BAu verso de la couverture, sont inscrits les mots suivants : \"Runes et malédictions de Bonegrip, la caverne lugubre, Ironforge. Propriétaire : Gerrig Bonegrip.\"$B$BSi Bonegrip vend de tels livres, peut-être qu\'il rachètera celui-ci.",
    ["O"] = "Apporter le Livre : Les puissances d\'en bas à Gerrig Bonegrip, dans la caverne lugubre d\'Ironforge.",
    ["T"] = "Les puissances d\'en-bas",
  },
  [969] = {
    ["D"] = "Rien qu’à vous regarder, je vois que vous voulez un charme de chance. Mau\'ari sait tout !$B$BLà-bas, dans le sud du Berceau, vous trouverez un canyon où les géants collectent des éclats de glace. Vous pouvez toujours prélever vous-mêmes des éclats sur le canyon. Ils ont une puissante qualité de réflexion.$B$BRapportez 10 Eclats cognegivre, et mon charme de chance sera à vous.",
    ["O"] = "Collecter 10 Eclats cognegivre pour le sorcier-docteur Mau\'ari à Long-guet.",
    ["T"] = "Que la chance soit avec vous",
  },
  [970] = {
    ["D"] = "La lettre de Balthule est désespérée. Ce culte de la Rive noire m\'irrite au plus haut point et doit être éradiqué. J\'ai eu affaire à quelques vermines de la Rive noire au nord-est d\'ici à Ordil\'Aran. L\'un d\'entre eux possède une gemme d\'âme, qui je crois détient le secret de la puissance de ce culte.$B$BApportez-la-moi, et je serai capable de déchiffrer les secrets qu\'elle contient.",
    ["O"] = "Trouver une Gemme d\'âme luminescente et la rapporter à Delgren le Purificateur au poste de Maestra.",
    ["T"] = "La Tour d\'Althalaxx",
  },
  [971] = {
    ["D"] = "Comme il se peut que vous le sachiez, je recueille le savoir. Le savoir ancien. Le savoir puissant. Le savoir qui ouvre les portes et le savoir qui réveille Ceux qui dorment.$b$bIl court des rumeurs sur une pièce de savoir, le Manuscrit de Lorgalis, loin dans les Profondeurs de Brassenoire, dans Ashenvale. Cet endroit est la demeure d\'elfes depuis longtemps disparus. Des elfes qui avaient réuni de grandes connaissances avant que leur cité ne tombe en ruines.$b$bFouillez les Profondeurs de Brassenoire à la recherche du manuscrit. Faites-le, et je ne l\'oublierai pas. Même pas après... les derniers jours.",
    ["O"] = "Apporter le « Manuscrit de Lorgalis » à Gerrig Bonegrip à Ironforge.",
    ["T"] = "La connaissance des profondeurs",
  },
  [972] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Le sapta d\'eau",
  },
  [973] = {
    ["D"] = "Ilkrud Magthrull ? Oui, je le connais. Il s\'agit d\'un puissant démoniste orc qui s’est établi au Sanctuaire de Scarfeu, un endroit très maléfique dans le sud-ouest d\'Ashenvale.$b$bIl n\'a pas été plus qu\'un simple désagrément jusqu\'à présent, et je n\'ai pas eu affaire à lui, mais il semble que le temps soit venu.",
    ["O"] = "Apporter le Tome d\'Ilkrud Magthrull à Delgren le Purificateur au Poste de Maestra.",
    ["T"] = "La Tour d\'Althalaxx",
  },
  [974] = {
    ["D"] = "Les sources chaudes ne cesseront de m’étonner ; d’où vient cette chaleur ?$B$BVu à quel point les sources sont proches de la crête de la Fournaise, je me demande si l’influence du volcan n’est pas responsable. J’ai créé un thermomètre pour lire et enregistrer la température de différentes parties du volcan et trouver la plus chaude. La chaleur est exprimée en degrés Kraklenheit !$B$BVoici, prenez-le et faites des mesures de température sur la crête, dès que vous voyez un point chaud !",
    ["O"] = "Découvrir la zone la plus chaude de la crête de la Fournaise, pour Krakle, au cratère d\'Un\'Goro.$B$BQuand vous trouvez une zone de chaleur, cliquez avec le bouton droit de la souris sur le thermomètre afin de prendre la température. Continuez vos recherches jusqu’à avoir trouvé la zone la plus chaude.",
    ["T"] = "À la recherche de la source",
  },
  [975] = {
    ["D"] = "Avant de vous donner ce charme, je vous expliquerai comment l’utiliser.$B$BSi vous le portez sur vous, vous trouverez certains objets spéciaux sur les créatures que vous combattrez. Sans le charme, pas d’objets spéciaux, vous voyez ?$B$BCes objets s’appellent des E\'ko. Si vous trouvez n\'importe lequel de ces objets, apportez-le à la vieille Mau\'ari, qui saura vous aider.$B$BAttendez ici pendant que je prépare votre charme.",
    ["O"] = "Attendre un instant, puis reparler au sorcier-docteur Mau\'ari pour recevoir le Charme de chance.",
    ["T"] = "Cache de Mau\'ari",
  },
  [976] = {
    ["D"] = "Delgren m\'a demandé d\'apporter un message et du ravitaillement à la Sentinelle Starbreeze à Auberdine, mais je déteste partir seul. La forêt regorge de dangereux adversaires et je ne veux pas prendre le risque que le ravitaillement ou des informations capitales tombent entre de mauvaises mains.$b$bSi vous et quelques autres pouvez m\'accompagner en tant que gardes, je partirai tout de suite. Si vous nous protégez, aucun doute que ces marchandises arriveront à bon port.",
    ["O"] = "Parler à Delgren le Purificateur, au Poste de Maestra, après avoir escorté Feero à travers la Forêt d\'Ashenvale.",
    ["T"] = "De l’approvisionnement pour Auberdine",
  },
  [977] = {
    ["D"] = "Maintenant que j’ai ce dont j’ai besoin pour construire les parties mécaniques mobiles, il ne me manque qu’une seule chose. Deux choses, en fait. Deux cornes !$B$BLes patriarches et matriarches Chardon de glace sont ceux qui ont les plus grandes cornes. Ces yétis se trouvent dans les grottes, au sud-est. Il y en a un grand nombre là bas, vous n’aurez pas de problèmes à les trouver.$B$BRappelez-vous que je ne veux que les plus belles cornes, sans la moindre éraflure !",
    ["O"] = "Collecter 2 Cornes de yéti en parfait état pour Umi Rumplesnicker, à Long-guet",
    ["T"] = "Yéti ? Y es-tu ?",
  },
  [978] = {
    ["D"] = "Les plumes d’Indomptables des Hinterlands contiennent des traces de magie. Toutefois, ces créatures ne semblent pas avoir de pouvoirs particuliers. La magie semble faire partie de leur essence.$B$BPendant mes études, j’ai entendu qu’une importante population d’Indomptables résidait dans les terres enneigées du Berceau-de-l\'Hiver. Ces créatures auraient-elles les mêmes pouvoirs ? Aidez-moi à trouver les réponses à ces questions, $N. Allez au Berceau-de-l\'Hiver et collectez des plumes qui traînent au sol, puis revenez me voir.",
    ["O"] = "Collecter 10 Plumes de Chouettard lunaire, dans le Berceau-de-l\'Hiver, puis retourner voir Erelas Ambersky, à Rut\'theran.",
    ["T"] = "Les Indomptables lunaires",
  },
  [979] = {
    ["D"] = "Oui, oui, ces plumes semblent contenir le même enchantement que j\'ai remarqué précédemment… Bien que je ne sache pas vraiment ce que nous pourrions découvrir, je vous conjure de continuer à chercher des réponses, $N.$B$BL\'une de mes collègues a voyagé récemment au Berceau-de-l\'Hiver afin d\'y observer les indomptables, dans leur habitat naturel. Pourquoi ne pas essayer de la retrouver et voir ce qu\'elle a pu découvrir...",
    ["O"] = "Trouver Ranshalla dans l’est du Berceau-de-l\'Hiver.",
    ["T"] = "Trouver Ranshalla",
  },
  [980] = {
    ["D"] = "Il y a d’autres sources chaudes dans un endroit appelé le Berceau-de-l\'Hiver, loin au nord. Ce qui est étrange, c’est qu’il n’y a pas de volcan à proximité ; intéressant n’est-ce pas ?$B$BSi vous voulez en apprendre plus, vous devriez aller au Berceau-de-l\'Hiver et parler à une de mes amies. Son nom est Donova Snowden, et elle réside aux environs de ces sources.$B$BMerci de m’aider avec mon thermomètre et à plus tard !",
    ["O"] = "Se rendre au Berceau-de-l\'Hiver et parler à Donova Snowden.",
    ["T"] = "Les nouvelles sources",
  },
  [981] = {
    ["D"] = "Vous méritez bien une récompense pour tout ce que vous avez accompli. Retournez voir Delgren et dites-lui qu\'Athrikus Narassin gît à terre par vos soins et que le Culte de la Rive noire est vaincu et démantelé.",
    ["O"] = "Aller voir Delgren le Purificateur au Poste de Maestra.",
    ["T"] = "La Tour d\'Althalaxx",
  },
  [982] = {
    ["D"] = "Au large de Sombrivage, plus au nord, il y a deux épaves de bateau : l’Aube argentée et le Voile de brume. Il y a quelque temps, ces deux bateaux sont tombés sur ces maudits murlocs, alors qu’ils faisaient voile à travers la vaste mer jusqu’à Auberdine. Ils reposent maintenant au fond des eaux, comme autant de trophées au crédit de ces démons.$B$BLes deux capitaines n’ont pas survécu et leurs journaux de bord et effets personnels sont toujours dans leurs coffrets. Je voudrais que vous les récupériez ; cela serait symboliquement très important pour tous les équipages de cette contrée.",
    ["O"] = "Récupérer le Coffret de l’Aube argentée et le Coffret du Voile de brume, pour Gorbold Steelhand, à Auberdine. Ces deux objets se trouvent à bord des épaves des navires respectifs, au nord du village.",
    ["T"] = "Océan profond, mer immense",
  },
  [983] = {
    ["D"] = "Hé ! J\'ai besoin d\'aide pour ma dernière invention, la bigobox. Elle permet de parler aux gens à distance !$B$BVous en avez peut-être déjà vu, ce sont des boîtes avec un levier dessus. Le seul problème, c\'est qu\'il faut une maintenance permanente.$B$BElles ont toutes un problème différent, mais j\'ai eu une idée géniale. J\'ai placé chacune d\'elle près de la créature qui possède les éléments appropriés pour la réparer. En ce moment, la bigobox 827 est en panne. C\'est juste au sud d\'Auberdine, vraiment tout près. Il faut 6 pattes de clampants pour la réparer. Je vous payerai pour ça...",
    ["O"] = "Réunir 6 Pattes de clampants et les mettre dans la Bigobox 827.",
    ["T"] = "Bigobox 827",
  },
  [984] = {
    ["D"] = "Certains de mes frères ont été sauvés de justesse d\'un furbolg corrompu à Teldrassil, et je me suis juré d\'arrêter ces atrocités avant que d\'autres ne soient blessés... ou pire.$B$BJ\'ai déjà vu des prémices de corruption à Sombrivage, mais il faut que je cherche d\'autres signes ailleurs. Je pense qu\'il serait logique de poursuivre les investigations auprès des furbolgs. Pourriez-vous trouver un de leurs camps et me dire s’il y a des signes de corruption ?",
    ["O"] = "Trouver un camp de furbolgs corrompus à Sombrivage et retourner voir Terenthis dans Auberdine.",
    ["T"] = "Quelle est l\'ampleur de la menace ?",
  },
  [985] = {
    ["D"] = "Vous avez déjà prouvé vos qualités dans le domaine de la reconnaissance, $N. Avez-vous aussi les qualités nécessaires pour affronter l\'adversaire ?$B$BLes aventuriers ne préfèrent pas tous le combat direct à l\'art de la ruse et de l\'évasion.$B$BSi vous pensez être à la hauteur de la tâche, sachez que la plus grande menace pour notre peuple est aujourd\'hui le camp furbolg au sud d\'Auberdine. Vous y trouverez des furbolgs de la tribu Noirbois. Tuez 8 Guides et 5 Messagers du vent et revenez me voir.",
    ["O"] = "Tuer 8 Guides et 5 Messagers du vent Noirbois puis retourner voir Terenthis à Auberdine.",
    ["T"] = "Quelle est l\'ampleur de la menace ?",
  },
  [986] = {
    ["D"] = "$N, votre habileté m\'a déjà été utile. Puis-je à nouveau vous demander d\'aider Griffe-sinistre et son maître Volcor ? Je vais vous fabriquer une cape magique qui vous permettra de vous déplacer librement au milieu des créatures de Sombrivage pendant que vous le chercherez.$B$BPour faire cette cape, j\'ai besoin de cinq belles peaux de géniteur ou de matriarche traquelune. Vous trouverez ces tigres au sud de la Sinueuse ou beaucoup plus au sud, non loin d\'Ashenvale.",
    ["O"] = "Trouver 5 Belles peaux de traquelune et les apporter à Terenthis, à Auberdine.",
    ["T"] = "Un maître perdu",
  },
  [987] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [990] = {
    ["D"] = "$N, ma maîtresse, Raene Wolfrunner, vous attend à la cité d\'Astranaar dans Ashenvale. Peut-être que grâce à vous, nous pourrons réduire la corruption qui y règne.$B$BEn partant d\'ici, vous devez prendre plein sud en restant près de la route ; vous trouverez sans problème votre chemin en suivant cette direction.",
    ["O"] = "Trouver Raene Wolfrunner dans Ashenvale.",
    ["T"] = "Voyage vers Ashenvale",
  },
  [991] = {
    ["D"] = "$N, l\'un de mes vieux amis est aussi venu à Ashenvale pour aider les Sentinelles, mais il est déjà reparti.$B$BIl cherchait un objet qui pourrait retarder les attaques des furbolgs contre notre peuple : un Bâtonnet créé par un sorcier diabolique, aujourd’hui mort.$B$BAvant de partir, il a dit qu\'il cherchait une gemme pour ce Bâtonnet.$B$BIl pensait que la gemme était cachée dans le sanctuaire près du lac Falathim, au pied de la montagne à l\'ouest. La gemme était là avant l’invasion.$B$BTrouvez mon ami, je vous en prie, $N.",
    ["O"] = "Trouver Teronis dans Ashenvale.",
    ["T"] = "Le nettoyage selon Raene",
  },
  [992] = {
    ["D"] = "Nous avons un poste temporaire vacant de géomètre novice à la Compagnie des Eaux de Gadgetzan, si ça vous intéresse !$B$BComme les nomades s\'emparent de tous nos puits, nous devons exploiter tous les points d\'eau libres ! Il y en a dans le désert, mais seulement pour ceux qui les étudient en premier.$b$bPrenez cette fiole de sourcier et récoltez un échantillon d\'eau de la mare près du Guet de Tristesable. Juste sous le nez des trolls. Rapportez-moi la fiole pleine quand vous aurez fini !",
    ["O"] = "Utiliser la Fiole de sourcier vide à la mare près du Guet de Tristesable. Une fois l\'échantillon récolté, remettre la Fiole de sourcier pleine au grand géomètre Fizzledowser à Gadgetzan.",
    ["T"] = "Étude des eaux de Gadgetzan",
  },
  [993] = {
    ["D"] = "La cape est terminée, $N. Nous devons trouver Volcor à présent. J\'espère seulement que nous sommes encore dans les temps.$B$BLa magie de la cape, une fois invoquée, ne durera pas longtemps. Cinq minutes, peut-être. Ce sera à vous de choisir quand l\'utiliser. Je suggèrerais d\'attendre de trouver Griffe-sinistre.$B$BSi ce qu\'il m\'a dit est vrai, vous trouverez son maître dans une caverne au sud d\'ici. Griffe-sinistre vous attendra le long de la route. Faites-lui un signe de la main lorsque vous le verrez et il vous indiquera la direction de son maître.",
    ["O"] = "Trouver Volcor, à Sombrivage, et lui donner la Cape enchantée de traquelune.",
    ["T"] = "Un maître perdu",
  },
  [994] = {
    ["D"] = "Ça va, $N. Sortons de là. Nous nous fraierons un passage jusqu\'à la route ; Griffe-sinistre devrait encore y être. Une fois là bas, nous pourrons nous séparer.$B$BTerenthis va vouloir que vous lui racontiez immédiatement ce qui s\'est passé, vous irez donc à Auberdine dès que nous serons sortis vivants d\'ici.",
    ["O"] = "Protéger Volcor jusqu\'à la route puis aller voir Terenthis à Auberdine.",
    ["T"] = "Évasion par la force",
  },
  [995] = {
    ["D"] = "D’accord... si vous le voulez, nous nous échapperons d’ici.$B$BRappelez vous, une fois que vous aurez quitté ce lieu, allez à Auberdine et rencontrez Terenthis. C’est là que je vais me rendre aussi, et j’y serai probablement avant vous.",
    ["O"] = "S\'échapper de la grotte des furbolgs et aller voir Terenthis à Auberdine.",
    ["T"] = "Évasion par la ruse",
  },
  [996] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La fleur-de-vent corrompue",
  },
  [997] = {
    ["D"] = "Vous allez vers le sud ? Au lac Al\'Ameth ? Si c\'est le cas, j\'ai une tâche à vous confier... $B$BMon collègue Denalan campe le long du rivage oriental du lac, où il étudie la flore de Teldrassil. Il a demandé qu\'on lui envoie un paquet de Terre rare de Darnassus, mais il y a eu du retard et le paquet vient seulement d\'arriver à Dolanaar.$B$BPouvez-vous le lui apporter ?",
    ["O"] = "Rapporter le paquet de Terre rare à Denalan au lac Al\'Ameth.",
    ["T"] = "La terre de Denalan",
  },
  [998] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La fleur-de-vent corrompue",
  },
  [999] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "When Dreams Turn to Nightmares",
  },
  [1000] = {
    ["D"] = "Votre attention !$B$BLe Cercle cénarien cherche à recruter ceux qui se sentent capables pour une mission d\'exploration sur les nouveaux territoires de Kalimdor ! Des terres désolées, interdites, regorgeant de dangers, se trouvent à l\'ouest de Tanaris et de Gadgetzan, et le Cercle a besoin d\'honorables représentants pour agir en son nom.$B$BTous ceux et celles qui seraient intéressés devraient solliciter une audience auprès du Cercle cénarien. Parlez avec l\'archidruide Hamuul Runetotem, sur la Cime des Anciens de Thunder Bluff, pour plus d\'informations !",
    ["O"] = "Parler à l’archidruide Hamuul Runetotem, sur la Cime des Anciens de Thunder Bluff. Evoquer l’appel du Cercle cénarien concernant l’exploration des frontières de Kalimdor.",
    ["T"] = "La nouvelle frontière",
  },
  [1001] = {
    ["D"] = "Une petite voix crépite à l\'intérieur de la machine.$B$B\"C\'est Wizbang ! La prochaine Bigobox se trouve au nord d\'Auberdine, sur la plage !$B$BCette Bigobox porte le numéro 411 et a besoin de 3 Yeux de batteur pour être réparée. Ils sont un peu au large de la côte ! Les Frappeurs de Sombrivage... *Hic*... Pardon...$B$BTout comme pour la dernière fois, quand vous donnerez les objets à la Bigobox, elle vous crachera votre récompense. *Hic*... Quoi ? Non, je vais bien !\"",
    ["O"] = "Rassembler 3 Yeux de batteur de Sombrivage au fond de la mer, près de la Bigobox 411.",
    ["T"] = "Bigobox 411",
  },
  [1002] = {
    ["D"] = "La bigobox se tait un instant puis vous entendez Wizbang commencer à parler.$B$B\"La-la bigobox suivante est la numéro 323. L\'est au nord d\'Auberdine mais... Où est la bière ? Hein ? Oh ! C\'est sur-sur la route alo-lors ça ne devrait pas-pas être dif-difficile de la trou-trouver près du pont.\"$B$BWizbang marmonne quelque chose d\'incohérent puis vous entendez un bruit de déglutition sonore.$B$B\" Il f-f-faut des Crocs de traquelune... 6 cro-crocs.\"",
    ["O"] = "Trouver 6 Crocs de traquelune et les mettre dans la bigobox 323.",
    ["T"] = "Bigobox 323",
  },
  [1003] = {
    ["D"] = "En plus du bruit de fond et des sons écœurants qui viennent de la machine, vous entendez la voix hésitante de Wizbang appeler.$B$B\"Au-autre au s-sud en bas de l-la route. Ne devrait pa-pas trop dur à trouver... juste au b-bord... près d\'une clôture. Je p-pense que je l\'ai ca-cachée dans un beu-beu... buisson. Hé hé hé !... Ouais, la bigobox 525 a be-besoin de s-scalps vieillis de vieux ou-ours des chardons. Garçon, une autre ! Vi-vieillis... Hé hé hé !...\"$B$BVous entendez déglutir tandis que la machine s\'arrête. Puis soudain, elle éructe.$B$B\"4 !\"",
    ["O"] = "Se procurer 4 Scalps vieillis sur de Vieux ours des chardons au sud d\'Auberdine et les placer dans la Bigobox 525.",
    ["T"] = "Bigobox 525",
  },
  [1004] = {
    ["D"] = "Votre attention !$B$BLe Cercle cénarien cherche à recruter ceux qui se sentent capables pour une mission d\'exploration sur les nouveaux territoires de Kalimdor ! Des terres désolées, interdites, regorgeant de dangers, se trouvent à l\'ouest de Tanaris et de Gadgetzan, et le Cercle a besoin d\'honorables représentants pour agir en son nom.$B$BTous ceux et celles qui seraient intéressés devraient solliciter une audience auprès du Cercle cénarien. Parlez avec l\'archidruide Hamuul Runetotem, sur la Cime des Anciens de Thunder Bluff, pour plus d\'informations !",
    ["O"] = "Parler à l’archidruide Hamuul Runetotem, sur la Cime des Anciens de Thunder Bluff. Evoquer l’appel du Cercle cénarien concernant l’exploration des frontières de Kalimdor.",
    ["T"] = "La nouvelle frontière",
  },
  [1005] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "What Lurks Beyond",
  },
  [1006] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "What Lies Beyond",
  },
  [1007] = {
    ["D"] = "Je vais vous charger d\'une mission, $N.$B$BJ\'étais sur mon petit bateau, voguant sur les ruines englouties de Zoram, quand les nagas ont émergé des eaux et m\'ont attaqué en me déchirant avec leurs griffes ! Je me suis enfui, en transportant tout ce que je pouvais pour le camp.$B$BEt lorsque j\'ai atteint le rivage, je me suis mis à courir... J\'ai perdu ma précieuse cargaison.$B$BJe vous en prie, $N, retrouvez l’endroit où je suis tombé dans cette embuscade et cherchez une ancienne statuette. C\'est pour elle que j\'ai bravé les dangers de la grève de Zoram.",
    ["O"] = "Apporter l\'Ancienne Statuette à Talen, dans son camp près de la grève de Zoram.",
    ["T"] = "L\'ancienne statuette",
  },
  [1008] = {
    ["D"] = "Il y a un endroit sur notre côte nord-ouest, connu sous le nom de grève de Zoram.$B$BC\'est là que se trouve la cité perdue de Zoram, détruite pendant le Fractionnement et submergée par les eaux. Cela fait une éternité que les elfes de la nuit l\'ont perdue. Perdue et presque oubliée.$B$BAujourd’hui, les nagas y sont retournés et nous ne savons pas pourquoi. Mais la raison importe peu : nous devons tuer ces démons et les rejeter dans les profondeurs !$B$BRevenez me voir quand votre mission sera achevée.",
    ["O"] = "Apporter 20 Têtes d\'Irequeue à Shindrell Swiftfire, à Astranaar.",
    ["T"] = "La grève de Zoram",
  },
  [1009] = {
    ["D"] = "J\'ai peur que les secrets de l\'ancienne statuette ne restent un mystère tant que vous n\'aurez pas trouvé la clé qui ouvre cette vieille serrure.$B$BCette clé est un anneau, l\'Anneau de Zoram. Il a appartenu aux dirigeants de cette cité avant que les ondes du Fractionnement ne l\'aient noyée.$B$BL\'anneau a été perdu pendant des années, mais les nagas viennent de le retrouver.$B$BLeur chef, Ruuzel, se trouve sur une île, sur le côté nord de Zoram. Je vous en prie, $N, trouvez-moi cet anneau !",
    ["O"] = "Apporter l\'Anneau de Zoram à Talen près de la grève de Zoram.",
    ["T"] = "Ruuzel",
  },
  [1010] = {
    ["D"] = "Il y avait une plante qui poussait autrefois dans les vieilles ruines du repaire de Bathran au nord. On l\'appelait Cheveux de Bathran et elle était réputée guérir les maux de l\'esprit.$B$BIl y a un enfant malade dans le village et je crois qu\'il s\'agit d\'autre chose que d\'une simple maladie physique. Accepteriez-vous d\'aller au repaire de Bathran pour y chercher des Cheveux de Bathran ? Il se peut que j\'en ai besoin pour soigner cet enfant.$B$BLe repaire de Bathran se trouve au nord du Poste de Maestra, juste au sud de la frontière de Sombrivage.",
    ["O"] = "Rapporter 5 Cheveux de Bathran à Orendil Broadleaf dans Ashenvale.",
    ["T"] = "Les Cheveux de Bathran",
  },
  [1011] = {
    ["D"] = "Les Réprouvés, le bras mort-vivant de la Horde, ont rampé jusqu\'en Ashenvale, et rôdent près de nos ruines et de nos refuges des saisons. Nous pensons que leur objectif est de répandre la maladie sur les terres des elfes de la nuit. Nous devons nous en assurer !$B$BLes Réprouvés ont un camp au sud d\'ici, près du refuge des saisons de Dor\'danil. Ils y conçoivent poisons et maladies et les placent dans des bouteilles. Frayez-vous un passage jusqu\'à ce camp et volez une de ces bouteilles pleines. Revenez alors me voir. J\'expérimenterai cette maladie et découvrirai à quelles fins elle a été conçue.",
    ["O"] = "Apporter une Bouteille des maladies à Kayneth Stillwind au Chant des Forêts.",
    ["T"] = "Les maladies des Réprouvés",
  },
  [1012] = {
    ["D"] = "Lorsque je me battais avec les poisons des Réprouvés, j\'ai eu une vision. J\'ai vu les druides du Refuge des saisons de Dor\'danil empoisonnés... Assassinés par les morts-vivants ! Ils sont désormais des fantômes errants, séparés de leurs corps et se conduisant comme des fous.$B$BPour que les esprits des druides puissent retrouver la paix, vous devez entrer dans le Refuge des saisons de Dor\'danil et éliminer leurs grands chefs : Taneel Darkwood, Uthil Mooncall et Mavoris Cloudsbreak.$B$BFaites cela et revenez me voir.",
    ["O"] = "Tuer les druides : Taneel Darkwood, Uthil Mooncall, Mavoris Cloudsbreak ; puis retourner auprès de Kayneth Stillwind au Chant des Forêts.",
    ["T"] = "Les druides fous",
  },
  [1013] = {
    ["D"] = "$N, il y a un livre dans le donjon d\'Ombrecroc, le « Livre d\'Ur », qui ferait très bon effet dans ma collection. Ur était un grand mage de Dalaran avant l\'arrivée du Fléau, les études qu\'il a faites dans certains domaines ont beaucoup de valeur aux yeux de certains... parmi les Réprouvés.$B$BPénétrez dans le donjon d\'Ombrecroc et trouvez ce livre. Apportez-le-moi et je ferai part de votre action à notre Dame noire.",
    ["O"] = "Apporter le \"Livre d\'Ur\" au Gardien Bel\'dugur à l\'Apothicarium à Undercity.",
    ["T"] = "Le Livre d\'Ur",
  },
  [1014] = {
    ["D"] = "Arugal se trouve toujours dans le Donjon d\'Ombrecroc. Nous ne pouvons pas faire des Pins argentés une forteresse stratégique pour la Dame noire tant qu\'Arugal est vivant.$b$bJe vais me débrouiller pour que sa magie soit annihilée, $n. Mais je vous laisse le soin de faire en sorte qu\'Arugal rencontre la mort qu\'il sert si bien.$b$bAllez au Donjon d\'Ombrecroc pour mettre fin une fois pour toutes aux charmes crasseux d\'Arugal. Apportez-moi la tête de ce vil sorcier !",
    ["O"] = "Tuer Arugal et apporter sa Tête à Dalar Dawnweaver au Sépulcre.",
    ["T"] = "Arugal doit mourir",
  },
  [1015] = {
    ["D"] = "Votre attention !$B$BLe Cercle cénarien cherche à recruter ceux qui se sentent capables pour une mission d\'exploration sur les nouveaux territoires de Kalimdor ! Des terres désolées, interdites, regorgeant de dangers, se trouvent à l\'ouest de Tanaris et de Gadgetzan, et le Cercle a besoin d\'honorables représentants pour agir en son nom.$B$BTous ceux et celles qui seraient intéressés devraient solliciter une audience auprès du Cercle cénarien. Parlez avec l\'archidruide Fandral Staghelm, dans Darnassus, pour plus d\'informations !",
    ["O"] = "Parler à l’archidruide Fandral Staghelm, dans l\'Enclave cénarienne, à Darnassus. Evoquer l’appel du Cercle cénarien concernant l’exploration des frontières de Kalimdor.",
    ["T"] = "La nouvelle frontière",
  },
  [1016] = {
    ["D"] = "Prenez ce Parchemin de divination, il pourra nous apprendre qui a créé ces élémentaires. Mais vous aurez besoin d\'une chose qui leur appartienne et que l’on pourra utiliser comme réactif pour focaliser la magie du parchemin ; leurs bracelets, par exemple, devraient faire l’affaire.$B$BQuelques bracelets intacts suffiront. Les autres bracelets ne serviront pas nos desseins mais pourront être revendus auprès d’un marchand. Une fois que vous aurez les bracelets intacts, essayez d’utiliser le Parchemin. Revenez avec le résultat de l\'expérience et nous verrons quelle suite donner aux événements.",
    ["O"] = "Récolter 5 Bracelets élémentaires intacts et utiliser le Parchemin de divination sur eux. Ensuite, apporter le Parchemin de divination à la Sentinelle Velene Starstrike au Refuge de Vent-argent.",
    ["T"] = "Les bracelets d\'élémentaire",
  },
  [1017] = {
    ["D"] = "Il n\'y a qu\'un seul mage mort-vivant dans les Tarides capable de souiller autant d\'élémentaires d\'eau. C\'est aussi le seul suffisamment idiot pour le faire. Sarilus Foulborne est probablement assis au sommet du pic de Brume-funeste, en train de se moquer de moi.$B$B$n, vous devez pénétrer en territoire ennemi et éliminer ce scélérat. J\'en ai assez de sa stupidité et nous devons lui montrer ce qui arrive à ceux qui s\'en prennent aux Sentinelles.$B$BTuez-le et apportez-moi sa tête. Mais soyez $gprudent:prudente;, il se peut que la Horde vous surveille.",
    ["O"] = "Tuer Sarilus Foulborne et apporter sa Tête à la Sentinelle Velene Starstrike au Refuge de Vent-argent.",
    ["T"] = "Mage invocateur",
  },
  [1018] = {
    ["D"] = "Votre attention !$B$BLe Cercle cénarien cherche à recruter ceux qui se sentent capables pour une mission d\'exploration sur les nouveaux territoires de Kalimdor ! Des terres désolées, interdites, regorgeant de dangers, se trouvent à l\'ouest de Tanaris et de Gadgetzan, et le Cercle a besoin d\'honorables représentants pour agir en son nom.$B$BTous ceux et celles qui seraient intéressés devraient solliciter une audience auprès du Cercle cénarien. Parlez avec l\'archidruide, Hamuul Runetotem, sur la Cime des Anciens de Thunder Bluff, pour plus d\'informations !",
    ["O"] = "Parler à l’archidruide Hamuul Runetotem, sur la Cime des Anciens de Thunder Bluff. Evoquer l’appel du Cercle cénarien concernant l’exploration des frontières de Kalimdor.",
    ["T"] = "La nouvelle frontière",
  },
  [1019] = {
    ["D"] = "Votre attention !$B$BLe Cercle cénarien cherche à recruter ceux qui se sentent capables pour une mission d\'exploration sur les nouveaux territoires de Kalimdor ! Des terres désolées, interdites, regorgeant de dangers, se trouvent à l\'ouest de Tanaris et de Gadgetzan, et le Cercle a besoin d\'honorables représentants pour agir en son nom.$B$BTous ceux et celles qui seraient intéressés devraient solliciter une audience auprès du Cercle cénarien. Parlez avec l\'archidruide, Fandral Staghelm, dans Darnassus, pour plus d\'informations !",
    ["O"] = "Parler à l’archidruide Fandral Staghelm, dans l\'Enclave cénarienne, à Darnassus. Evoquer l’appel du Cercle cénarien concernant l’exploration des frontières de Kalimdor.",
    ["T"] = "La nouvelle frontière",
  },
  [1020] = {
    ["D"] = "$N, j\'ai mis au point un traitement qui, je pense, devrait guérir cette pauvre enfant. Apportez-le à Astranaar et donnez-le à son père, Pelturas Whitemoon.$B$BPour atteindre Astranaar, suivez la route par le sud puis par l\'est.",
    ["O"] = "Apporter la Jouvence d\'Orendil à Pelturas Whitemoon dans Astranaar.",
    ["T"] = "Jouvence d\'Orendil",
  },
  [1021] = {
    ["D"] = "Écoutez, je ne sais pas qui vous êtes, mais j\'ai besoin de votre aide. Mes sœurs sont allées reprendre la branche de Cénarius au satyre. Elles ont dit qu\'elles seraient vite de retour, mais il y a deux jours entiers qu\'elles sont parties !$B$B$n, vous devez les trouver ! Elles cherchaient les ruines d\'un temple des elfes de la nuit au nord-est de Xavian. Je ne sais que penser. Cherchez Anilia, c\'est elle qui menait la recherche.",
    ["O"] = "Trouver Anilia dans Xavian.",
    ["T"] = "Vil satyre ! dryades en danger !",
  },
  [1022] = {
    ["D"] = "Bien que nous ayons dépensé beaucoup d\'énergie pour repousser les démons encore présents de Gangrebois jusqu\'au nord, nous n\'avons pas eu beaucoup de succès. Nous avons pu éviter toute présence démoniaque à Ashenvale.$b$bAu nord, près de la frontière de Gangrebois, de mystérieux hommes-loups ont investi le sanctuaire en ruine de Mel\'Thandris. Leurs cris sont si terrifiants qu\'on a surnommé ce secteur le Val hurlant. Le « Tome de Mel\'Thandris » qui est gardé dans le sanctuaire peut faire la lumière sur la raison de la venue de ces hommes-loups.",
    ["O"] = "Se rendre au Val hurlant et étudier le « Tome de Mel\'Thandris », puis retourner voir la Sentinelle Melyria Frostshadow au Sanctuaire d\'Aessina.",
    ["T"] = "Le Val hurlant",
  },
  [1023] = {
    ["D"] = "En fouillant les affaires de Teronis, vous trouvez un journal qui est encore en bon état.$B$BVous prenez le journal et laissez le cadavre de Teronis comme il est.$B$BIl ne vous reste plus qu\'à trouver la gemme que Teronis cherchait. Raene a dit qu\'elle était dans le secteur. Peut-être que les murlocs l\'ont prise...",
    ["O"] = "Trouver la Gemme luminescente et retourner voir Raene Wolfrunner dans Astranaar avec le Journal de Teronis.",
    ["T"] = "Le nettoyage selon Raene",
  },
  [1024] = {
    ["D"] = "Vous devrez chercher par vous-même, mais d\'après les notes de Teronis, il semble que la prochaine étape soit de trouver les autres morceaux du Bâtonnet que Dartol a créé. Je commencerais par me diriger vers le puits de lune à l\'est du lac Iris. Suivez la route et prenez au nord lorsque vous atteindrez une petite zone de repos sur votre gauche.$B$BVotre meilleur guide sera une dryade du nom de Shael\'dryn. Cherchez-la.$B$BPrenez la Gemme avec vous : Shael\'dryn en aura besoin si elle doit vous aider à réparer le Bâtonnet.",
    ["O"] = "Trouver Shael\'dryn, au Puits de lune, au nord-est.",
    ["T"] = "Le nettoyage selon Raene",
  },
  [1025] = {
    ["D"] = "Avant que vous n\'alliez au Puits de lune, j\'ai une autre tâche à vous confier pour aider les habitants d\'Ashenvale, si ça vous dit de tester vos compétences.$B$BLe long de la route qui commence à l\'est, avant le fleuve Falfarren, les furbolgs ont attaqué des voyageurs. Leurs esprits tordus se nourrissent de la peur qu\'ils causent et seul le sang des innocents les rassasie.$B$BAidez les Sentinelles à protéger nos terres en rendant à ces monstres la monnaie de leur pièce.$B$BJe crois que leurs camps sont au sud de la route... Faites preuve de prudence.",
    ["O"] = "Tuer 1 Protecteur, 2 Ursas, 10 Totemiques et 12 Guerriers de la tribu Vile-sylve, puis retourner voir Raene Wolfrunner, à Astranaar.",
    ["T"] = "Une défense agressive",
  },
  [1026] = {
    ["D"] = "Je regrette que vous n\'ayez pas de meilleures nouvelles.$B$BRaene a raison, je sais ce qu\'avait créé Dartol et je connais les tréants du secteur – du moins je les connaissais. Désormais, ils sont corrompus et leur nature a changé.$B$BVotre mission est de rapporter le morceau suivant du bâtonnet, mais vous devez d’abord trouver la clé qui ouvre le coffre qui contient le morceau.$B$BLa Clé en bois est sur l\'un des tréants au nord-est, près de Gangrebois. Une fois que vous l\'aurez, cherchez une petite clairière à l\'ouest de la route près de la lisière de Gangrebois... Le coffre devrait être caché par là.",
    ["O"] = "Trouver la Clé en bois et un morceau du Bâtonnet de Dartol avant de retourner voir Shael\'dryn au Puits de lune.",
    ["T"] = "Le nettoyage selon Raene",
  },
  [1027] = {
    ["D"] = "Le dernier morceau du Bâtonnet a été donné aux druides de Dor\'danil, qui en assurent la garde. Je n\'ai aucune idée de l\'endroit où ils l\'ont caché. Je suis désolée de ne pas pouvoir mieux vous aider.$B$BTout ce que je peux vous dire, c\'est de suivre la route qui mène aux Tarides. Les druides se sont réfugiés à l\'est de cette route, mais ils ne vous seront pas très utiles : ils ont été tués récemment... C\'est une chose à laquelle j\'évite de penser.$B$BQuand vous aurez trouvé le dernier morceau, apportez-le-moi et je vous aiderai à réparer le Bâtonnet.",
    ["O"] = "Trouver le dernier morceau du Bâtonnet de Dartol et retourner voir Shael\'dryn au puits de lune.",
    ["T"] = "Le nettoyage selon Raene",
  },
  [1028] = {
    ["D"] = "Le bâtonnet est terminé, $N. Le réparer, c\'était facile... Ce qui va nous donner plus de mal, c\'est d’atteindre l\'Autel qui a le pouvoir de le recharger.$B$BAvec de la concentration et des efforts, cela ne devrait pas être trop dur pour quelqu\'un comme vous, qui a déjà tant accompli.$B$BPartez vers le sud-est, il y a très peu de chemins, et ne perdez pas le nord de vue – et faites preuve de prudence. L\'autel est caché dans les montagnes qui entourent cette prairie.$B$BUne fois arrivé, placez le bâtonnet sur l\'autel... Le reste relève de la magie.",
    ["O"] = "Trouver l\'Autel caché et recharger le Bâtonnet de Dartol.",
    ["T"] = "Le nettoyage selon Raene",
  },
  [1029] = {
    ["D"] = "Je vous suggère d\'aller trouver Raene pour lui dire que le bâtonnet est terminé. Elle saura vous dire quelle est votre prochaine mission. $B$BBonne chance, $N.",
    ["O"] = "Retourner voir Raene Wolfrunner à Astranaar.",
    ["T"] = "Le nettoyage selon Raene",
  },
  [1030] = {
    ["D"] = "Hélas, je ne sais pas ce qu\'il comptait faire ensuite.$B$BSi ce qu\'il a dit sur le pouvoir du bâtonnet est vrai, alors il devrait vous permettre de prendre une autre forme. Laquelle, je ne sais pas.$B$BPeut-être qu\'il voulait utiliser ces pouvoirs pour approcher les furbolgs infectés, mais même les créatures non corrompues l\'auraient abattu s\'il avait manqué de prudence. Transformé par le bâtonnet, Dartol aurait pu parler à un furbolg ayant échappé aux effets de la gangremousse.$B$BVous devriez commencer vos recherches au sud-est du lac Mystral.",
    ["O"] = "Trouver un furbolg non corrompu et lui parler en utilisant le pouvoir du Bâtonnet de transformation de Dartol.",
    ["T"] = "Le nettoyage selon Raene",
  },
  [1031] = {
    ["D"] = "Anilia pose ses mains sur vos épaules et vous attire vers elle. Haletante, elle indique l\'arche au nord, avant de fermer les yeux pour toujours. \" Il l\'a pris... Geltharis...\" Au fond du cœur, vous comprenez que le dernier souhait d\'Anilia est de voir la branche de Cénarius à nouveau entre les mains des dryades.",
    ["O"] = "Tuer Geltharis et rendre la Branche de Cénarius à Illiyana au Sanctuaire d\'Aessina.",
    ["T"] = "La branche de Cénarius",
  },
  [1032] = {
    ["D"] = "Je n\'arrive pas à croire qu\'Anilia ne soit plus là. Nous avions l\'habitude de jouer ensemble dans la forêt. Je me sens presque... triste.$B$BJe sais ce qui pourrait me faire me sentir mieux. Si vous pouviez massacrer les satyres qui m\'ont pris Anilia ! Rapportez-moi leurs cornes comme preuve de leur mort ; si vous faites cela, vous gagnerez mon éternelle gratitude.",
    ["O"] = "Apporter 16 Cornes de satyre à Illiyana, au Sanctuaire d\'Aessina.",
    ["T"] = "Mort aux satyres !",
  },
  [1033] = {
    ["D"] = "J\'espérais que le remède d\'Orendil sauverait Relara, mais je crains qu\'il ne puisse que ralentir la progression de la maladie qui terrasse mon enfant. Nous devons trouver des médicaments plus efficaces ! Heureusement, Orendil m\'a parlé d\'un autre remède à essayer au cas où le premier échouerait.$B$BLa Larme d\'Elune est une variété de pierre qu\'on ne trouve qu\'en un seul endroit : l\'île du lac Iris, à l\'est d\'Astranaar. Orendil a dit que cette pierre soignerait Relara... Je vous en prie, $N, apportez-moi la Larme d\'Elune et priez pour qu\'elle guérisse mon enfant !",
    ["O"] = "Apporter une Larme d\'Elune à Pelturas Whitemoon à Astranaar.",
    ["T"] = "Larme d\'Elune",
  },
  [1034] = {
    ["D"] = "La Larme d\'Elune a un peu amélioré l\'état de ma fille. Ses cauchemars ont diminué et je remercie la déesse pour sa bénédiction. Mais... la fièvre n\'a pas baissé.$B$BOrendil m\'a parlé d\'une poussière dépolie que l\'on trouve dans les Ruines de Chimétoile. Peut-être qu\'elle diminuerait la fièvre de Relara. Trouvez un peu de cette poussière, et rapportez-la-moi, je vous en prie.$B$BLes Ruines de Chimétoile se trouvent au sud, $N. Puisse Elune vous faire revenir rapidement !",
    ["O"] = "Rapporter 5 Poignées de Poussière d\'étoile à Pelturas Whitemoon à Astranaar.",
    ["T"] = "Les ruines de Chimétoile",
  },
  [1035] = {
    ["D"] = "La fièvre de Relara est tombée, mais elle est toujours très faible. Je ne crois pas que sa force reviendra avec le temps. Il va falloir le pouvoir d\'une pierre de lune.$B$BNous avions l\'habitude d\'en ramasser au lac Tombeciel au sud-est... jusqu\'à ce que les Sombretaillis s\'y installent.$B$BLeur chef, un oracle, a ramassé toutes les pierres tombées de la lune et les conserve. Vous devez maintenant lui reprendre une pierre de lune.$B$BMais pour ça, je crains que vous ne soyez dans l\'obligation de le tuer.",
    ["O"] = "Reprendre une Pierre de lune tombée à l\'Oracle Sombretaillis puis l\'apporter à Pelturas Whitemoon à Astranaar.",
    ["T"] = "Le lac Tombeciel",
  },
  [1036] = {
    ["D"] = "$N... Oui, j\'ai entendu parler de vous ; vous n\'êtes pas l\'$gami:amie; de Baie-du-Butin, exactement comme nous, les boucaniers de la Voile sanglante. L\'amiral Firallon recherche des volontaires vigoureux et enthousiastes comme vous pour une mission spéciale.$B$BSi cela vous intéresse, trouvez le Voile cramoisi et parlez avec l\'amiral, sous le pont. Le navire est ancré plus à l\'est, au large de la Côte sauvage, à proximité du Courant et de La Chance de la demoiselle.",
    ["O"] = "Parler à l\'amiral Firallon, à bord du Voile cramoisi, au large de la côte de la vallée de Strangleronce.",
    ["T"] = "En avant toute, jeune mariole !",
  },
  [1037] = {
    ["D"] = "Velinde Starsong m\'a précédé dans la forêt d\'Ashenvale. Au début, elle semblait contrôler la situation dans Gangrebois, mais peu à peu ses résultats ont faibli. Un beau jour, elle a tout simplement disparu.$b$bOn m\'a envoyé ici pour continuer sa mission. Cependant, j\'ai bien peur de ne rien savoir d\'elle. Peut-être que Thyn\'tel Bladeweaver, l\'un des commandants des Sentinelles, a du nouveau sur sa disparition, des détails que j’ignorerais.$B$BElle comprendra certainement l\'importance de ce genre d\'informations.",
    ["O"] = "Parler à Thyn\'tel Bladeweaver à la Terrasse des guerriers de Darnassus.",
    ["T"] = "Velinde Chantétoile",
  },
  [1038] = {
    ["D"] = "Le « Tome de Mel\'Thandris » vous a montré cela ? Je suppose qu\'il n\'y a pas grand mal à vous permettre d\'examiner ses affaires. Cette clé ouvrira le coffre dans lequel nous avons stocké ses effets, dans le baraquement des Sentinelles. Elle a tenu un journal de sa mission, s\'il y a quoi que ce soit à apprendre, ce sera là-dedans.$b$bJe dois ajouter que les Sentinelles croient qu\'elle avait des raisons personnelles de partir et s\'attendent à ce qu\'elle revienne à tout moment. La prêtresse a fait beaucoup dans le passé pour gagner notre confiance.",
    ["O"] = "Fouiller le coffre de Velinde pour trouver son Journal et le rendre ensuite avec la clé à Thyn\'tel Bladeweaver à Darnassus.",
    ["T"] = "Les effets personnels de Velinde",
  },
  [1039] = {
    ["D"] = "Ratchet est le seul port des Tarides. L\'hypothèse la plus probable est que Velinde a trouvé à Ratchet un navire de commerce pour la conduire à la crique des Flots noirs, en Azeroth. Nous avons des relations limitées avec les gobelins qui gèrent le port, mais le Maître des docks devrait avoir des informations sur les arrivées et les départs des passagers des bateaux.$b$bSuivez la route au sud-est d\'Ashenvale et vous arriverez dans les Tarides. Faites preuve de prudence, $n, les guerriers de la Horde patrouillent dans le secteur. Mais au port, vous serez en sûreté.",
    ["O"] = "Aller voir le Maître des docks Dizzywig à Ratchet.",
    ["T"] = "Le port des Tarides",
  },
  [1040] = {
    ["D"] = "Oh oui ! Enfin trouvé. Z\'auriez dû me dire qu\'elle était passée il y a longtemps. Voyons.$b$bVelinde Starsong. Passage réservé à Baie-du-Butin sur le « Balbuzard noir ». Je n\'ai rien d\'autre là-dedans, donc je suppose qu\'il est arrivé à bon port.$b$bJe ne peux pas faire grand\'chose de plsu pour vous. Elle a cependant demandé comment voyager par voie de terre de l\'autre côté et je lui ai indiqué Ruzzgot, un chef de caravane basé à Baie-du-Butin. Peut-être que cette Velinde a voyagé avec lui.$b$bMaintenant dégagez. Je n\'ai pas toute la journée à vous consacrer.",
    ["O"] = "Prendre un bateau pour Baie-du-Butin et aller voir le Caravanier Ruzzgot.",
    ["T"] = "Passage à Baie-du-Butin",
  },
  [1041] = {
    ["D"] = "S\'avère que j\'ai eu tort à propos de vous et que ça n\'arrive pas tous les jours.$b$bJe viens juste de me rappeler de cette Velinde que vous cherchez. Pas tous les jours qu\'une prêtresse des elfes de la nuit veut voyager dans une vieille et sale – mais parfaitement adaptée à vos transports de marchandises ! – caravane comme la mienne.$b$bNous avons pris ensemble le chemin vers le nord, elle se dirigeait vers Darkshire. L\'employé là-bas tient à jour toutes sortes d\'informations. Pourrait savoir quelque chose d\'utile.$b$bFaites preuve de prudence dans la jungle, c\'est un endroit mortel même dans les bons jours.",
    ["O"] = "Aller voir le greffier Daltry à Darkshire.",
    ["T"] = "La route de la caravane",
  },
  [1042] = {
    ["D"] = "Non, je n\'ai reçu aucun rapport concernant une Velinde Starsong résidant à Darkshire... Quoique, si vous voulez mon avis, j\'aie du mal à imaginer une prêtresse elfe de la nuit prenant une chambre à l\'auberge, si vous voyez ce que je veux dire.$b$bPour ce qui est des hommes-loups que vous mentionnez, j\'en ai entendu parler. Justement l\'autre jour, Calor est rentré en ville avec une guirlande de leurs têtes. Il travaille avec la famille Carevin. Des chasseurs de démons, de morts-vivants et autres monstres. Allez voir Jonathan, c\'est le chef de famille.",
    ["O"] = "Aller voir Jonathan Carevin à Darkshire.",
    ["T"] = "La famille Carevin",
  },
  [1043] = {
    ["D"] = "Votre histoire sonne juste... Je ne comprends pas bien vos motifs, mais si vous venez dans le bois de la Pénombre pour le débarrasser de quelques worgens, je ne vais plus chercher à comprendre.$b$bIl y a une mine au sud, que les worgens ont envahie... Ils sont apparus de nulle part, mais d\'après ce que nous savons, c\'est là qu\'on les a vus pour la première fois.$b$bFaites votre boulot, mais si vous découvrez quoi que ce soit d\'important, je vous demande de m\'en faire part. Nous acceptons l\'aide de tout le monde dans notre guerre contre le Mal.",
    ["O"] = "Chercher les signes de la Faux d\'Elune puis retourner voir Jonathan Carevin à Darkshire.",
    ["T"] = "La Faux d\'Elune",
  },
  [1044] = {
    ["D"] = "Nous maîtriserons le problème des worgens, n\'ayez aucune crainte là-dessus. Ce mal que votre amie a introduit dans nos bois sera contenu et je ne lui en veux pas pour ses actions. Des événements étranges surviennent en ce moment, $n et l\'obscurité ne connaît aucun répit.$b$bJe ne vous retiens pas plus. Je soupçonne que d\'autres ont entendu parler de ce que vous avez trouvé dans les mines du bois de la Pénombre.",
    ["O"] = "Retourner vers Thyn\'tel Bladeweaver à Darnassus.",
    ["T"] = "Réponses aux questions",
  },
  [1045] = {
    ["D"] = "Les elfes de la nuit sont aussi vieux que le temps, mais leur arrogance détruit leur monde même. Et maintenant elle détruit aussi nos vies. Mais on ne peut plus rien contrôler... $B$BJe blâme les elfes pour beaucoup de choses, mais nous sommes toujours responsables de nos actions. Même sans la corruption, nous sommes capables de mauvaises choses.$B$BRan Dent-rouge est de ce genre de créatures démoniaques. La soif de pouvoir, la luxure... La corruption n\'a fait qu\'aggraver les choses. Je brûle de haine pour les elfes, mais je n\'appelle pas la maladie sur leurs enfants, parce que nous devons maintenir l\'ordre chez les nôtres.",
    ["O"] = "Tuer Ran Dent-rouge et 4 Gardes de Dent-rouge, puis retourner voir Krolg près du lac Mystral.",
    ["T"] = "Le nettoyage selon Raene",
  },
  [1046] = {
    ["D"] = "Retournez dans votre tribu pour raconter votre victoire.$B$BMontrez-leur votre trophée et appréciez la gloire que vous venez de conquérir…",
    ["O"] = "Apporter le Crâne de Ran Dent-rouge et le Bâtonnet de transformation de Dartol à Raene Wolfrunner à Astranaar.",
    ["T"] = "Le nettoyage selon Raene",
  },
  [1047] = {
    ["D"] = "Votre attention !$B$BLe Cercle cénarien cherche à recruter ceux qui se sentent capables pour une mission d\'exploration sur les nouveaux territoires de Kalimdor ! Des terres désolées, interdites, regorgeant de dangers, se trouvent à l\'ouest de Tanaris et de Gadgetzan, et le Cercle a besoin d\'honorables représentants pour agir en son nom.$B$BTous ceux et celles qui seraient intéressés devraient solliciter une audience auprès du Cercle cénarien. Parlez avec l\'archidruide Fandral Staghelm, dans Darnassus, pour plus d\'informations !",
    ["O"] = "Parler à l’archidruide Fandral Staghelm, dans l\'Enclave cénarienne, à Darnassus. Evoquer l’appel du Cercle cénarien concernant l’exploration des frontières de Kalimdor.",
    ["T"] = "La nouvelle frontière",
  },
  [1048] = {
    ["D"] = "Pendant que la Dame noire s\'occupe du problème urgent posé par l\'avance du roi-liche au nord, je me préoccupe de ce qui se passe par ici.$b$bAucun doute, les parasites humains qui se nomment la Croisade écarlate sont l’épine à l\'intérieur même de nos terres. Les Clairières de Tirisfal grouillent littéralement de ces petites fourmis rouges.$b$bAllez au Monastère, $c, et faites disparaître le Grand Inquisiteur Whitemane et ses lieutenants les plus importants.",
    ["O"] = "Tuer le grand inquisiteur Whitemane, le commandant Mograine de la Croisade, le champion Herod et le maître-chien Loksey, et retourner faire son rapport à Varimathras à Undercity.",
    ["T"] = "Au monastère écarlate",
  },
  [1049] = {
    ["D"] = "Le Cercle terrestre avait des intentions pures envers nos frères tourmentés. Mais qui connaît les motivations de ceux qui chuchotent aux oreilles du Conseil des Anciens ?$b$bLes Réprouvés auxquels nous nous sommes alliés ont un lourd passé de duperie. Notre pacte a été trop hâtif. Peut-être Cairne aurait-il été sage de tenir compte des avertissements d\'Orgrimmar.$b$bLe cœur du Monastère écarlate abrite le « Compendium des Déchus », gardé par des humains fanatiques et fous furieux. Ils tirent leur méthode de leur folie, mais leurs recherches pourraient s\'avérer utiles.",
    ["O"] = "Trouver le « Compendium des Déchus » au Monastère dans les Clairières de Tirisfal et retourner voir le Sage Truthseeker à Thunder Bluff.",
    ["T"] = "Compendium des Déchus",
  },
  [1050] = {
    ["D"] = "L\'avenir est inévitable ! Nous nous lançons vers lui comme les gouttes de pluie tombent dans la Grande mer.$b$bMais peut-être qu\'en étudiant le passé, nous pourrons savoir à quoi nous attendre pour mieux nous préparer.$b$bAvant que Lordaeron ne connaisse ces événements sinistres, il y avait un bastion de connaissance caché dans les Clairières de Tirisfal. La rumeur disait qu\'un livre appelé « la Mythologie des Titans » avait été placé dans la bibliothèque du vieux Monastère.$b$bRetrouvez-le et peut-être qu\'ensemble nous pourrons découvrir les secrets du passé !",
    ["O"] = "Reprendre « la Mythologie des Titans » au Monastère et le rapporter au Bibliothécaire Mae Paledust à Ironforge.",
    ["T"] = "Mythologie des Titans",
  },
  [1051] = {
    ["D"] = "Bien que ma volonté soit intacte, je ne peux plus servir la Dame noire. Je sens que mes jours sur Azeroth vont prendre fin. Ma force de vie vole en éclats.$b$bC\'est l\'œuvre de l\'Interrogateur Vishas ! Il m\'a torturé pendant des semaines ! Il a pris mon anneau de mariage et l\'a donné à sa femme. Le bâtard !$b$bVengeance... Je veux ma vengeance !$b$bCe pompeux imbécile s\'est vanté de sa précieuse femme, Nancy, qui vit le long du lac de Lordamere. Traquez cette femme, assassinez-la et rendez mon anneau de mariage à ma femme Monika, à Moulin-de-Tarren.$b$bExaucez mes dernières volontés...",
    ["O"] = "Rapporter l\'anneau de mariage de Vorrel Sengutz à Monica Sengutz à Moulin-de-Tarren.",
    ["T"] = "La vengeance de Vorrel",
  },
  [1052] = {
    ["D"] = "Nous, les membres de la Croisade écarlate, nous revendiquons des forteresses de Hearthglen aux Clairières de Tirisfal. Nous sommes assez fiers des bastions de nettoyage que nous avons installés partout dans Lordaeron.$b$bVous avez fait vos preuves contre les morts-vivants de Désolace. Mais la vraie menace de cette peste se trouve à Lordaeron.$b$bFaites route jusqu\'à la ville de Southshore, dans les Royaumes de l\'est. Cherchez-y un croisé nommé Raleigh le Dévot. Remettez-lui cette lettre de recommandation portant mon cachet et il vous donnera une place d\'honneur dans notre monastère.",
    ["O"] = "Apporter la Lettre de recommandation du Frère Anton à Raleigh le Dévot à Southshore.",
    ["T"] = "Sur le chemin écarlate",
  },
  [1053] = {
    ["D"] = "J\'ai servi autrefois la Croisade écarlate avec honneur, fidélité et fierté. J\'ai cru que sa cause était noble : débarrasser Azeroth des morts-vivants.$b$bMais lorsque je suis allé au Monastère, dans les Clairières de Tirisfal, je me suis rendu compte combien les frères perdaient le sens des réalités. Aujourd\'hui, ils sont persuadés que tous ceux qui ne portent pas leur tabard sont des êtres tourmentés. Des hommes et des femmes innocents ont été torturés sous prétexte qu\'ils étaient tourmentés.$b$bLa Croisade écarlate doit être écrasée au nom de la Lumière. C\'est vous, $n, qui devez renverser ce régime fourvoyé.",
    ["O"] = "Tuer le grand inquisiteur Whitemane, le commandant Mograine de la Croisade, le champion Herod de la Croisade et le maître-chien Loksey, et retourner faire un rapport à Raleigh le Dévot à Southshore.",
    ["T"] = "Au nom de la Lumière",
  },
  [1054] = {
    ["D"] = "Les furbolgs Crins-de-Chardon sont au nord d\'Astranaar. Mes éclaireurs m\'ont appris que leur nombre a très fortement augmenté ces derniers mois. S\'ils deviennent trop nombreux, ils pourraient bien trouver le courage d\'attaquer Astranaar...$B$BTuez-en autant que possible, mais surtout apportez-moi le crâne de leur chef pour me prouver que vous les avez arrêtés... au moins pour l\'instant.$B$BJe vais rester à Astranaar pour attendre de vos nouvelles. Qu\'Elune soit avec vous !",
    ["O"] = "Apporter le Crâne de Dal Griffesang à Raene Wolfrunner à Astranaar.",
    ["T"] = "Diminution de la menace",
  },
  [1055] = {
    ["D"] = "Le Bâtonnet, maintenant achevé, repose entre vos mains. Shael\'dryn va vouloir apprendre votre réussite.",
    ["O"] = "Retourner voir Shael\'dryn au puits de lune.",
    ["T"] = "Le nettoyage selon Raene",
  },
  [1056] = {
    ["D"] = "Les Esprits de la forêt m\'ont dit que vous aviez du courage et que vous vouliez voyager.$b$bAu sud, près du lac Mystral, il y a un tunnel nommé la Perce des Serres. Par ce tunnel, vous arriverez dans un secteur connu sous le nom de la Combe des Cisailles, dans les Serres-Rocheuses. Une fois là-bas, faites route vers le sud-ouest, devant le lac de la Combe et ensuite vers le nord, en prenant la montée jusqu\'au pic des Serres-Rocheuses.$b$bLe Gardien Albagorm vous y attend. Tenez compte de ce qu\'il vous proposera, $N.$b$bLe voyage sera périlleux.",
    ["O"] = "Voyager pour aller voir le Gardien Albagorm au pic des Serres-Rocheuses.",
    ["T"] = "Voyage au pic des Serres-Rocheuses",
  },
  [1057] = {
    ["D"] = "En tant que protecteur des esprits de la forêt, vous pouvez sans aucun doute deviner pourquoi je suis alarmé.$b$bVous avez voyagé à travers ces montagnes pour atteindre ce pic, et avez sûrement vu les changements terrifiants qu\'a subi la terre. La cupidité a mené à la destruction de ces collines sacrées.$b$bLe Val calciné, au sud, a été consumée par une avide armée mécanique. Pire encore, les Rougefuries qui ont empiété sur ce territoire nous en interdisent l\'accès et nous empêchent d’y prospérer à nouveau.$b$bChassez ces affreuses harpies, $n !",
    ["O"] = "Le Gardien Albagorm du pic des Serres-Rocheuses veut que vous tuiez 7 Harpies, 7 Embusqués, 7 Tueuses et 7 Volplumes Rougefuries.",
    ["T"] = "À la reconquête du Val calciné",
  },
  [1058] = {
    ["D"] = "Je suis surexcité !$b$bTrois petites cages pour trois petits elfes ! Mais qu\'allons-nous faire de nos trois calinouilleurs d\'arbre ? Eh bien ! Faisons-leur goûter à… comment appellent-ils cela ?… La magie de la forêt !$b$bJ\'aurais besoin de pas mal de Sève des Serres-Rocheuses. Prenez également des Moustaches crépusculaires à ces affreux félins. Cela nous amène aux Yeux de coursier : on ne peut jamais en avoir trop sous la main. Et surtout n\'oublions pas : une précieuse Ecaille de dragon-faë !$b$bHâtez-vous de rejoindre le pic des Serres-Rocheuses $n, que je puisse concocter une recette magique de la forêt.",
    ["O"] = "Le sorcier-docteur Jin\'Zil à Malaka\'jin veut 5 doses de Sève des Serres-Rocheuses, 5 Moustaches crépusculaires, 30 Yeux de coursier et une Ecaille de dragon-faë.",
    ["T"] = "Magie de la forêt de Jin\'Zil",
  },
  [1059] = {
    ["D"] = "Les Rougefuries ont été maîtrisées. Mais elles se regrouperont bientôt si nous n\'agissons pas.$b$bLoin au sud des Tarides se trouve une terre connue sous le nom de Mille pointes. Si vous voyagez à l\'ouest des Mille pointes jusqu\'à la lisière de la luxuriante forêt de Feralas, vous trouverez un druide nommé Falfindel Waywarder.$b$bCe grand et sage druide est capable d\'invoquer de puissants esprits de la forêt qui pourraient aider à la reconquête du Val calciné.$b$bFaites-lui connaître notre requête ! Partez à sa recherche. Maintenant !",
    ["O"] = "Trouver Falfindel Waywarder à la frontière entre les Mille pointes et Feralas.",
    ["T"] = "À la reconquête du Val calciné",
  },
  [1060] = {
    ["D"] = "Vous avez tellement fait pour moi, $N. Vous me portez vraiment chance et j\'aimerais que vous voyiez quelque chose.$B$BLes harpies ne viennent pas des environs des Tarides. Elles viennent d\'un endroit appelé les Serres-Rocheuses, loin au nord-ouest d\'ici. Bien qu\'il s\'agisse probablement d\'un endroit dangereux pour vous, j\'ai besoin que cette lettre soit remise à l\'un de mes vieux amis qui y réside, un sorcier-docteur du nom de Jin\'Zil.$B$BIl sera content d\'apprendre que Serena Plumesang est morte, et je crois que vous trouverez les Serres-Rocheuses intéressantes.",
    ["O"] = "Remettre la lettre de Darsok à Jin\'Zil dans sa caverne des Serres-Rocheuses.",
    ["T"] = "Lettre à Jin\'Zil",
  },
  [1061] = {
    ["D"] = "Les esprits des Serres-Rocheuses sont courroucés, car les gobelins et leurs serviteurs pillent et brûlent cette région. Ainsi la Terre a appelé à grands cris, et ceux qui sont à son écoute, ont entendu.$b$bSeereth Stonebreak, un chaman prometteur de la Horde, a été envoyée dans les Serres-Rocheuses. Elle témoigne d’une large déforestation perpétrée par les gobelins, et elle demande de l\'aide.$b$bRencontrez Seereth dans les Serres-Rocheuses. Pour la trouver, suivez la route à l\'ouest de la Croisée des Tarides. Elle s\'est établie près du val de Grandbois.",
    ["O"] = "Parler à Seereth Stonebreak dans les Serres-Rocheuses.",
    ["T"] = "Les esprits des Serres-Rocheuses",
  },
  [1062] = {
    ["D"] = "La KapitalRisk, dirigée par des gobelins, s\'est installée dans les Serres-Rocheuses, où ils coupent les arbres et brûlent des secteurs entiers de la forêt. Les esprits de cette terre sont quasiment aveuglés par la douleur et par la haine. Nous devons l\'arrêter !$B$BPartez pour le nord-ouest, au-delà du Val de Grandbois et dans la combe des Cisailles. Vous trouverez les gobelins et leurs serviteurs à l\'oeuvre. Montrez-leur que la Horde ne permettra pas l\'exploitation des Serres-Rocheuses. Prouvez-le avec les arguments qu\'ils comprennent le mieux...$B$BLa violence.",
    ["O"] = "Tuer 15 Déboiseurs de la KapitalRisk, puis retourner auprès de Seereth Stonebreak à la frontière des Serres-Rocheuses et des Tarides.",
    ["T"] = "Les invasions gobelines",
  },
  [1063] = {
    ["D"] = "Pour vraiment soigner les esprits des Serres-Rocheuses, nous devons consulter les anciens esprits des taurens.$b$bLe seul chef dont j’ai entendu parler est Magatha. Elle est très vieille et ses connaissances sont grandes. Si quelqu’un peut canaliser une aide spirituelle des taurens, c\'est bien elle.$b$bMagatha réside à Thunder Bluff, la capitale des taurens, sur la Cime des Anciens, la cité au nord-est du promontoire. Parlez à Magatha, et racontez-lui les périls encourus par les Serres-Rocheuses.",
    ["O"] = "Parler à Magatha à Thunder Bluff.",
    ["T"] = "L\'Ancienne",
  },
  [1064] = {
    ["D"] = "Les taurens ont un lien avec cette terre, et apprendre la mauvaise santé des Serres-Rocheuses me peine. Mais j\'ai peur que pour soigner cette terre, nous devions d\'abord éradiquer la maladie qui l’envahit.$b$bN\'est-ce pas heureux, alors, que les Réprouvés soient nos alliés ? Ils en savent beaucoup à propos des maladies. Je pense qu\'ils peuvent nous aider, et en nous aidant ils consolideront la confiance qui existe entre nos deux peuples.$b$bParlez à l\'Apothicaire Zamah. C\'est une érudite et un émissaire des Réprouvés. Vous la trouverez aux Bassins de la vision sous la Cime des Esprits.",
    ["O"] = "Parler à l\'Apothicaire Zamah aux Bassins de la vision.",
    ["T"] = "L\'aide des Réprouvés",
  },
  [1065] = {
    ["D"] = "Pour débarrasser les Serres-Rocheuses de la KapitalRisk, nous devons employer des mesures extrêmes. Extrêmes, mais sans effusion de sang.$b$bIl se peut que l\'un de mes confrères ait exactement l\'outil dont nous avons besoin. Il se nomme Lydon, et réside dans la ville de Moulin-de-Tarren, dans les contreforts d\'Hillsbrad, dans la terre lointaine de Lordaeron.$b$bPour atteindre Hillsbrad, voyagez par zeppelin vers notre capitale, Undercity. Puis allez au nord vers les Clairières de Tirisfal, au sud-ouest vers les Pins argentés, et enfin au sud vers Hillsbrad.$b$bCherchez Lydon et donnez-lui cette note. Elle explique ce que nous attendons de lui.",
    ["O"] = "Apporter la Note de Zamah à l\'Apothicaire Lydon à Moulin-de-Tarren.",
    ["T"] = "Un voyage vers Moulin-de-Tarren",
  },
  [1066] = {
    ["D"] = "Je vais créer une toxine pour annihiler la violence de la KapitalRisk, et la rendre faible et docile. Cela devrait régler le problème de la Horde dans les Serres-Rocheuses, pas vrai ?$b$bMais pour créer la toxine, je vais avoir besoin de sang d\'innocent, un ingrédient difficile à trouver.$b$bHeureusement le Syndicat, un groupe de brigands humains, emploie des mages de l\'ombre... Et ces mages stockent du sang d\'innocent pour leurs propres desseins, peu recommandables.$b$bVous trouverez les mages de l\'ombre du Syndicat au donjon de Durnholde, au sud-est.",
    ["O"] = "Apporter 5 Fioles de Sang d\'innocent à l\'apothicaire Lydon à Moulin-de-Tarren.",
    ["T"] = "Le sang des innocents",
  },
  [1067] = {
    ["D"] = "Voici la toxine. Si vous l\'utilisez contre la KapitalRisk, cela devrait produire des effets intéressants, et peut-être même contagieux. Je dois dire que c\'est très excitant de voir mon travail appliqué sur le terrain !$b$bMais avant de l\'utiliser, apportez-la à l\'Apothicaire Zamah. Elle doit concevoir une méthode de diffusion particulière pour optimiser les effets de la toxine.",
    ["O"] = "Apporter la Toxine de Lydon à l\'Apothicaire Zamah à Thunder Bluff.",
    ["T"] = "Le retour à Thunder Bluff",
  },
  [1068] = {
    ["D"] = "Il existe une relation entre la magie spirituelle des chamans et la magie naturelle des druides. C\'est pourquoi nous, les chamans, nous pouvons entendre la complainte des esprits des Serres-Rocheuses. Beaucoup de leurs murmures m\'échappent, mais un message – une vision – est clair...$b$bJ\'ai eu la vision de deux machines à forme humaine, dans la Combe des cisailles, au nord-ouest du val de Grandbois, coupant les arbres à une vitesse incroyable. Ces bêtes de métal doivent être stoppées !$b$bDans ma vision, il y avait d\'étranges symboles gravés sur les machines :$b$bXT4 et XT9.",
    ["O"] = "Détruire les Déchiqueteurs XT4 et XT9, puis retourner voir Seereth Stonebreak à la frontière entre les Serres-Rocheuses et les Tarides.",
    ["T"] = "Les machines à déchiqueter",
  },
  [1069] = {
    ["D"] = "L’omelette aux oeufs d\'araignée est la nouvelle mode culinaire à Baie-du-Butin ! Le problème c\'est qu\'il n\'y a pas le moindre oeuf dans toute la ville.$b$bUne possibilité se profile !$b$bÀ la combe des Cisailles et dans le canyon de Sishir, dans les Serres-Rocheuses, au nord-ouest, vit l\'araignée des mousses profondes. Apportez-moi ses oeufs et je vous payerai un sacré paquet !$b$bÀ la combe des Cisailles, vous trouverez les oeufs groupés sous les arbres qui restent.$B$BEn ce qui concerne le canyon de Sishir... eh bien, si vous avez un estomac fragile, vous feriez bien de vous en tenir à la combe des Cisailles pour collecter les oeufs...",
    ["O"] = "Apporter 15 Œufs des mousses profondes à Mebok Mizzyrix à Ratchet.",
    ["T"] = "Omelette à la mousse profonde",
  },
  [1070] = {
    ["D"] = "Salutations, $n.$b$bVotre apparence semble indiquer que vous avez envie de parcourir le monde. Êtes-vous $gallé:allée; dans les Serres-Rocheuses ? C\'est une région tourmentée par la KapitalRisk et par d\'autres ennemis des elfes de la nuit.$b$bRécemment, la sentinelle Wolfrunner y a envoyé mon amie Kaela Shadowspear enquêter sur place, et je n\'ai toujours pas reçu de ses nouvelles. L\'inquiétude me ronge, d\'autant que l\'Alliance a envoyé un mage gnome avec elle... Mauvais présage, pour sûr.$b$bPourriez-vous la retrouver et revenir m\'informer ?",
    ["O"] = "Retrouver Kaela Shadowspear qui surveille la vallée à l\'entrée de la combe des Cisailles dans les Serres-Rocheuses.",
    ["T"] = "De garde dans les Serres-Rocheuses",
  },
  [1071] = {
    ["D"] = "Vous voyez, $n, je suis un membre de l\'Assemblée illuminée d\'arcanologie, d\'alchimie et de sciences d\'ingénierie, et c\'est mon but… non, il est de mon DEVOIR de montrer aux elfes de la nuit que l’association de magie, de mixtures et de mécanique peut aider à sauver leurs forêts.$b$bMais c\'est un combat acharné, et les gobelins semblent se reproduire comme des lapins. Je n\'ai même pas eu le temps de construire mes dernières inventions !$b$bJ\'ai besoin de temps pour planifier et organiser. Mais vous pouvez m’aider à retenir les gobelins à quai à la combe des Cisailles, à l\'est.",
    ["O"] = "Tuer 10 Déboiseurs de la KapitalRisk et 10 Défricheurs de la KapitalRisk, puis retourner voir Gaxim Rustfizzle dans les Serres-Rocheuses.",
    ["T"] = "Du répit pour un gnome",
  },
  [1072] = {
    ["D"] = "L\'appareil auquel je pense est à ce jour ma version la plus avancée. Mais nous aurons besoin d\'une potion spéciale si nous voulons que ça marche. Je pense que nous pourrons avoir la bonne dose également puisque cette mission sera votre dernière.$b$bEt pour ça, il nous faut un puissant explosif : le Nitromirglyceronium.$b$bLa seule personne qui puisse faire du NG-5 est un de mes vieux amis : Lomac Gearstrip. Cherchez-le à Brikabrok.$b$bDemandez-lui de nous faire du NG-5, je vais travailler au placement de mes appareils en attendant.",
    ["O"] = "Parler à Lomac Gearstrip à Ironforge.",
    ["T"] = "Un vieux collègue",
  },
  [1073] = {
    ["D"] = "Ha ha ha ! Vous avez besoin de nitromirglyceronium pour empêcher les gobelins de détruire une forêt ?$b$bSavoir si vous connaissez Gaxim ou non ne m\'intéresse pas… Je vais vous donner le matos juste histoire de vous voir ou de les voir exploser en petits confettis.$b$bIl y a un problème quand même : je n\'en ai plus.$b$bMais je vais vous dire, vous m\'apportez les bonnes potions, et je vous en fais tout de suite.$b$bLe moins que vous puissiez faire c\'est de me montrer que vous avez des talents d\'alchimiste avant que je vous donne un échantillon de ma grande œuvre.",
    ["O"] = "Apporter 4 Potions de mana mineures et 2 Elixirs de Robustesse mineure à Lomac Gearstrip à Ironforge.",
    ["T"] = "Inaptitude + Produits chimiques = Rigolo",
  },
  [1074] = {
    ["D"] = "Voilà, $gétranger:étrangère;. Mais faites attention avec ça quand même… C\'est volatil.$b$bSi vous êtes vraiment $gun ami:une amie; de Gaxim, alors transmettez-lui mes salutations. Si vous ne l\'êtes pas, alors amusez-vous bien à vous faire sauter la tronche.",
    ["O"] = "Rapporter le Nitromirglyceronium à Gaxim Rustfizzle, dans les Serres-Rocheuses.",
    ["T"] = "Inaptitude + Produits chimiques = Rigolo",
  },
  [1075] = {
    ["D"] = "OK, maintenant à propos de l’explosion à distance… Oh ! Je n\'ai pas encore expliqué cette partie de mon plan ?$b$bBien, alors voici la version courte : avec un peu de magie, on peut déclencher l’explosion sans avoir besoin de vous dans les parages. Je suis en train de peaufiner un système qui vous permettra d’appuyer simplement sur un bouton pour que le NG-5 explose, à votre commandement.$b$bMais j\'ai besoin du sort d\'un ami à Stormwind. Il s\'appelle Collin Mauren. Vous le trouverez dans le quartier des mages, là-bas. C\'est un humain, alors ça sera difficile de le rater.",
    ["O"] = "Trouver Collin Mauren à Stormwind.",
    ["T"] = "Un parchemin de Collin Mauren",
  },
  [1076] = {
    ["D"] = "Ah ! Je pense que ça vient. Un sort de contrôle… Je crois que c\'est l\'effet qu\'il recherche.$b$bJe vais devoir créer un parchemin pour y inscrire le sort. Pour cela, j’ai besoin de débris de Diable de poussière.$b$bTandis que je rassemble le reste des ingrédients, pourquoi n\'iriez-vous pas dans la marche de l\'Ouest me trouver une de ces ignobles créatures ? Elles sont dangereuses, mais je suis sûr que vous êtes $gprêt:prête; à tout pour participer à une expérimentation de Gaxim.$b$bJe serai là quand vous reviendrez.",
    ["O"] = "Apporter des Débris de Diable de poussière à Collin Mauren à Stormwind.",
    ["T"] = "Des diables dans la marche de l\'Ouest",
  },
  [1077] = {
    ["D"] = "Voilà, $n. Ça devrait faire l\'affaire.$b$bAssurez Gaxim de ma considération la prochaine fois que vous le verrez. J\'ai hâte d\'entendre la suite de cette aventure.$b$bAdieu.",
    ["O"] = "Apporter le Parchemin de contrôle à Gaxim Rustfizzle dans les Serres-Rocheuses.",
    ["T"] = "Livraison spéciale pour Gaxim",
  },
  [1078] = {
    ["D"] = "Les voyageurs m\'interrogent toujours au sujet des Serres-Rocheuses. C’est une région populaire pour qui cherche l\'aventure, qu’il s’agisse de traquer des wyverns, des élémentaires, ou du personnel de la KapitalRisk.$b$bDans le Val calciné, loin à l’intérieur des montagnes, vivait autrefois une espèce de basilic dont les écailles, une fois plantées dans la poussière, produisaient un merveilleux réactif pour certains des sorts que j\'ai créés.$b$bSi ces basilics vivent toujours, j\'aimerais avoir quelques-unes de leurs écailles.$b$bPrenez votre temps, ce n\'est pas urgent, mais je paye bien.",
    ["O"] = "Apporter 8 Ecailles de cristal à Collin Mauren à Stormwind.",
    ["T"] = "Des retrouvailles pour Mauren",
  },
  [1079] = {
    ["D"] = "Ça y est, $n. Faites très attention ! Les dispositifs sont montés et prêts à fonctionner.$b$bPrenez ces explicatifs. Ils résument tout ce qu\'il faut savoir sur les détonateurs et les explosifs, et indiquent également où vous devez les placer.$b$bRappelez-vous, le succès de votre mission ne dépend pas de la somme de destruction que vous provoquerez. Le succès du plan A dépend du fait que vous rapportiez les plans d\'ingénierie de la KapitalRisk de l\'intérieur de la scierie au sud du lac de la Combe.",
    ["O"] = "Récupérer les Plans d\'ingénierie de la KapitalRisk dans la scierie de la Combe des Cisailles.",
    ["T"] = "Opérations secrètes – Plan A",
  },
  [1080] = {
    ["D"] = "Le plan B est identique au plan A, $n.$b$bNous faisons diversion et nous volons leurs précieuses notes.$b$bLe plus important est d\'obtenir leurs notes… Rappelez-vous de ça.$b$bLes explicatifs que je vous ai déjà fournis indiquent où placer les explosifs. Revenez simplement ici en vie et avec ces notes...",
    ["O"] = "Récupérer les Notes de la KapitalRisk dans la mine des Cisailles à l\'extrémité est de la Combe des Cisailles.",
    ["T"] = "Opérations secrètes – Plan B",
  },
  [1081] = {
    ["D"] = "$N, il est sûr que Tyrande Whisperwind en personne voudrait vous remercier de prendre part à cette mission.$B$BIl n’y paraît peut-être pas, mais votre aide pourrait renforcer notre position dans les Serres-Rocheuses.$B$BSi vous vous considérez comme notre $gallié:alliée; face à ces atrocités, allez voir Tyrande au Temple de la Lune de Darnassus.",
    ["O"] = "Trouver Tyrande Whisperwind à Darnassus.",
    ["T"] = "Réception par Tyrande",
  },
  [1082] = {
    ["D"] = "$n, vous avez accompli un noble geste en nous offrant votre aide.$b$bLes Sentinelles d\'Ashenvale vous récompenseront si vous leur donnez des nouvelles de Gaxim et de moi. Dites-leur que nous sommes bien partis pour stopper la KapitalRisk et soigner la terre.$b$bTrouvez Tara Thenysil à Astranaar ; elle sera ravie de vous voir revenir avec de bonnes nouvelles.",
    ["O"] = "Retourner voir la Sentinelle Tara Thenysil en Ashenvale.",
    ["T"] = "Une mise à jour pour la Sentinelle Thenysil",
  },
  [1083] = {
    ["D"] = "Je ne sais pas exactement comment la KapitalRisk s’est comportée vis-à-vis de la vallée, mais une chose est sûre : elle a été submergée par des créatures locales.$b$bSes gens ont mis le feu à la forêt et utilisé leurs engins de destruction pour ravager la vallée.$b$bMais dans leur ignorance ils ont libéré des esprits ardents et des élémentaires de flamme, de roche et de rage.$b$bLa Terre n\'a aucune chance de voir le sourire d\'Elune tant que ces créatures la fouleront librement.$b$bApportez-moi leurs Braises ardentes pour me montrer que vous avez réduit leur nombre.",
    ["O"] = "Apporter 8 Braises ardentes provenant de n\'importe quel élémentaire de feu du Val calciné à Kaela Shadowspear, dans les Serres-Rocheuses.",
    ["T"] = "Esprits enragés",
  },
  [1084] = {
    ["D"] = "Il me semble évident que les protecteurs de la vallée, les tréants, ont quelque chose à voir avec le retrait de la KapitalRisk. Malheureusement, même ceux qui étaient autrefois de grands gardiens sont aujourd\'hui corrompus.$b$bGaxim pense que leur corruption est d\'une certaine manière liée aux armes utilisées par les gobelins pendant le combat.$b$bJe lui ai dit que je collecterais un résidu sur un tréant blessé pour qu’il l\'étudie et découvre l’origine du mal.$b$bApportez-moi un morceau de shrapnel pour que le gnome puisse m\'en dire plus...",
    ["O"] = "Apporter un morceau de Shrapnel abîmé à Kaela Shadowspear, dans les Serres-Rocheuses.",
    ["T"] = "Les Anciens blessés",
  },
  [1085] = {
    ["D"] = "La situation ici dans les Serres-Rocheuses est suffisamment grave pour qu\'une elfe de la nuit et un gnome travaillent ensemble.$b$bLa KapitalRisk a dévasté cette région et j\'ai passé la majeure partie de mon temps à mettre un terme à cette destruction.$b$bNe jugez pas Gaxim trop vite, il comprend la nécessité de notre travail commun.$b$bSi vous souhaitez nous aider, alors commencez par parler à Gaxim.$b$bJe suis désolée de ne pas encore pouvoir vous renvoyer à Tara avec de bonnes nouvelles. Bientôt peut-être…",
    ["O"] = "Parler à Gaxim.",
    ["T"] = "De garde dans les Serres-Rocheuses",
  },
  [1086] = {
    ["D"] = "La toxine est prête. Tout ce qu\'il reste à faire est de la livrer. Vous devez vous en occuper, $n.$b$bLa KapitalRisk doit avoir un moyen d\'envoyer des messages et de petites cargaisons entre ses quartiers généraux et ses sites d\'opérations dans les Serres-Rocheuses. Il s\'agit probablement d\'un petit aéroport, pas suffisamment important pour un zeppelin, mais assez pour une machine volante. Cherchez dans les Serres-Rocheuses les secteurs sous contrôle de la KapitalRisk et trouvez cet aéroport, s\'il existe. Placez-y alors la toxine.$b$bPlacez-la, et courez !",
    ["O"] = "Apporter le Brouillard toxique à l\'aéroport de la KapitalRisk, puis retourner voir l\'Apothicaire Zamah.",
    ["T"] = "L\'aéroport de la machine volante",
  },
  [1087] = {
    ["D"] = "Vous ne pouvez pas imaginer la haine que je ressens pour cette terre misérable. Au pic, les semblables de Cénarius et les druides kaldorei emploient leurs pouvoirs minables pour étouffer le flot de magie, même jusqu\'au sud de leurs possessions pathétiques... leurs forêts bien-aimées.$b$bJe voudrais retourner à Jaedenar et exulter dans le flot de magie, mais mon maître ne le permettra pas tant que mon travail ici ne sera pas achevé.$b$bIl ne fait aucun doute que vous aimez les elfes de la nuit et leurs alliés aussi peu que je les aime. Aidez-moi, et notre but commun pourra être atteint.",
    ["O"] = "Tuer 4 Fils de Cénarius, 4 Filles de Cénarius et 4 Botanistes cénariens pour Braelyn Firehand, près de la retraite de Roche-Soleil.",
    ["T"] = "L\'héritage de Cénarius",
  },
  [1088] = {
    ["D"] = "Ils ont cru qu\'ils pourraient arrêter le flot de magie. Ils se sont trompés. Ils ont cru pouvoir cacher Ordanus, mais j\'aurai sa tête !$b$bLa magie m\'accorde la vision véritable. Oui… il est là. Un croisement… Au sommet d\'un grand arbre dans la forêt d\'Ashenvale. Un nom... Raynewood. L\'un des fils aînés du dieu de la Forêt. Ils l\'ont fait disparaître du pic avant que je puisse le trouver, mais son temps est compté.$b$bApportez-le-moi et je vous offrirai un puissant cadeau.",
    ["O"] = "Apporter la Tête d\'Ordanus à Braelyn Firehand, près de la Retraite de Roche-Soleil.",
    ["T"] = "Ordanus",
  },
  [1089] = {
    ["D"] = "Les druides ont construit l\'Antre des Serres dans le pic des Serres-Rocheuses. Ils y conservent de nombreux objets puissants.$b$bIls ont conçu un plan compliqué pour que ces derniers ne tombent pas entre de mauvaises mains.$b$bPar chance, j\'ai réussi à… extraire quelques informations de l\'un d\'entre eux.$b$bCachée dans une petite lanterne, à l\'entrée de l\'Antre, vous trouverez la Clé du gardien ; elle vous permettra d\'ouvrir des cachettes autour de l\'Antre. Les objets extraits de ces cachettes donnent accès au trésor, situé dans l\'un des secteurs centraux.",
    ["O"] = "Aller dans l’Antre, sur le Pic des Serres-Rocheuses. En utilisant la Clé du gardien, obtenir les objets cachés des druides. Utiliser ces objets pour ouvrir le Trésor de l\'Antre des Serres.",
    ["T"] = "L\'Antre",
  },
  [1090] = {
    ["D"] = "Vous savez, avant j\'étais la seconde personne la plus importante ici sur ce site. Tout a changé quand Gerenzo est arrivé. Il a viré mon patron, Ziz Fizziks, et m\'a envoyé bosser dans les mines. Ziz m\'a dit de rester ici et de rassembler des renseignements. Son nouveau patron nous paiera bien si nous pouvons découvrir les plans de Gerenzo.$b$bMais bon, ce que je sais c’est que Gerenzo a ordonné que des quantités importantes de ce minerai or-vert soient extraites. Je peux vous trouver un échantillon pour que vous l\'apportiez à Ziz, mais faites attention aux patrouilles le temps que j’aille en chercher.",
    ["O"] = "Obtenir le minerai mystérieux de Piznik après qu\'il l’aura extrait.",
    ["T"] = "Les ordres de Gerenzo",
  },
  [1091] = {
    ["D"] = "$n, apportez ces notes à Kaela. Il est certain qu\'elle voudra en savoir plus à ce propos. Ou du moins ce sera le cas des Sentinelles.$b$bDites-lui à quel point notre victoire a été éclatante, et à quel point nous n\'aurions pu y parvenir sans l\'aide d\'un peu de magie.",
    ["O"] = "Apporter les Notes de la KapitalRisk à Kaela Shadowspear.",
    ["T"] = "Des nouvelles pour Kaela",
  },
  [1092] = {
    ["D"] = "Je pense que je ferais mieux d’envisager de ficher le camp d\'ici, aussi. Ça ne va pas prendre beaucoup de temps avant que la nouvelle de ce qui s\'est passé ici se répande au-dessus, et je suis sûr qu\'ils ne seraient pas ravis de me revoir.$b$bJe vais dégager d’ici et aller voir Ziz. J\'espère qu\'il pourra me trouver du boulot !$b$bJe vais essayer de nettoyer ce bordel, mais vous devez apporter l\'échantillon de minerai à Ziz tout de suite. Il m\'a dit qu\'il établirait un camp quelque part du côté ouest de la Combe des Cisailles. Bonne chance.",
    ["O"] = "Remettre le Minerai non identifié à Ziz Fizziks dans les Serres-Rocheuses.",
    ["T"] = "Les ordres de Gerenzo",
  },
  [1093] = {
    ["D"] = "C\'est terrible ce que la KapitalRisk a fait ici, hein ? Mais à quoi donc pensaient-ils ? Une compagnie de gobelins qui embauche un gnome !$b$bHeureusement pour moi, Sputtervalve reconnaît un génie lorsqu\'il en voit un. M\'a embauché juste après que je me suis fait virer !$b$bIl me dit qu\'ils construisent une énorme machine pour découper le bois, et Sputt veut que je vole les plans pour lui. Malheureusement, Gerenzo – maudit gnome – veut ma peau... Peut-être que vous pourriez voler les plans pour moi ? Vous devriez les trouver sur l\'un de leurs opérateurs.",
    ["O"] = "Trouver le Plan du Super-Découpeur 6000 pour Ziz Fizziks dans les Serres-Rocheuses.",
    ["T"] = "Super-Découpeur 6000",
  },
  [1094] = {
    ["D"] = "Cette enveloppe contient toutes les informations que j\'ai pu rassembler avant qu\'ils me flanquent à la porte : des documents internes de la KapitalRisk, quelques manuels, des trucs techniques… Et bien entendu, le plan du Super-Découpeur 6000.$b$bSputtervalve a élu résidence à Ratchet dans les Tarides. Vous ne devriez pas avoir trop de mal à le trouver, c\'est un type un peu vert.",
    ["O"] = "Remettre l\'Enveloppe scellée à Sputtervalve dans les Tarides.",
    ["T"] = "D\'autres instructions",
  },
  [1095] = {
    ["D"] = "L’embauche de ce gnome par la KapitalRisk a créé quelques remous à Undermine. Les princes marchands n\'approuvent pas que Razdunk fasse des affaires avec ce Gerenzo.$b$bRiddlevox m\'a demandé de m\'occuper du problème, et il est certain qu\'il a reçu, d\'un ou de plusieurs princes marchands, les mêmes instructions. Bien sûr, le directeur ne me l\'a pas dit. Disons que j\'ai simplement une intuition.$b$bQuoi qu\'il en soit, nous devons faire en sorte de supprimer Gerenzo, et ce serait le test parfait de voir Fizziks s\'en occuper.",
    ["O"] = "Apporter les nouveaux ordres à Ziz Fizziks dans les Serres-Rocheuses.",
    ["T"] = "D\'autres instructions",
  },
  [1096] = {
    ["D"] = "J\'ai rencontré Gerenzo une fois, mais laissez-moi vous dire qu\'il n\'est pas très beau à voir. On dirait que sa chair est en train de pourrir, et il s\'est fait greffer du métal et des accessoires sur le corps. Il a remplacé certains membres naturels par des membres mécaniques.$b$bAu nord-est d\'ici, loin au-dessus du lac de la Combe, près de la roue hydraulique, Gerenzo surveille le site entier.$b$bJe ne pourrais pas monter là-haut, je ne survivrais pas aux combats. Sans parler de me charger de Gerenzo... Mais vous semblez être capable de réussir.$b$bSi vous le tuez pour moi, je ferai en sorte que ça en vaille le coup !",
    ["O"] = "Apporter le Bras mécanique de Gerenzo Wrenchwhistle à Ziz Fizziks dans les Serres-Rocheuses.",
    ["T"] = "Gerenzo Siffleclef",
  },
  [1097] = {
    ["D"] = "Il y a un fabricant d\'armes à Stormwind, un nain du nom de Grimand Elmore, qui nous a envoyé un mot disant qu\'il avait besoin d\'aide avec une livraison. Je crois qu\'il veut qu\'un paquet soit envoyé dans son pays d’origine, dans le nord.$b$bVous avez des jambes robustes ! Si vous êtes $gintéressé:intéressée; par un peu de marche à pied, alors parlez à Grimand. Nous pourrions vous utiliser ici, mais nous devons également entretenir nos bonnes relations avec les nains.$b$bVous trouverez Grimand Elmore au magasin d\'armes dans le quartier des nains de Stormwind, dans la partie nord-est de la ville.",
    ["O"] = "Parler à Grimand Elmore.",
    ["T"] = "La quête d\'Elmore",
  },
  [1098] = {
    ["D"] = "En préparation d\'un assaut sur Ombrecroc, deux Traqueurs noirs ont été envoyés au donjon pour réunir des renseignements. Plusieurs jours ont passé, et ils ne sont toujours pas revenus. Nous devons cependant prendre connaissance de leur rapport si nous voulons que notre attaque ait la moindre chance de réussir !$b$bAussi voici votre tâche, $n. Partez pour le donjon d\'Ombrecroc au sud et trouvez les Traqueurs noirs Adamant et Vincent. Trouvez-les, et s\'ils ont besoin d’aide, aidez-les !$b$bCette mission est aussi dangereuse qu\'elle est critique, $n. N\'échouez pas.",
    ["O"] = "Trouver les Traqueurs noirs Adamant et Vincent.",
    ["T"] = "Des Traqueurs noirs à Ombrecroc",
  },
  [1099] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Goblins Win!",
  },
  [1100] = {
    ["D"] = "Vous ouvrez lentement le journal décoloré de Henrig Lonebrow…",
    ["O"] = "Lire le Journal de Henrig Lonebrow.",
    ["T"] = "Journal de Lonebrow",
  },
  [1101] = {
    ["D"] = "Pauvre Heralath ! Et ce nain... Lonebrow. Quel esprit courageux il avait.$b$bNous ne devons pas laisser leur vaillance périr pour rien. Cette mégère… Charlga Trancheflanc… doit être arrêtée.$b$bIl ne fait pas de doute qu\'infiltrer le Kraal sera périlleux. Nous n\'avons pas le temps d\'envoyer un mot à Darnassus. $n, rassemblez un groupe et tuez la mégère.$b$bApportez-moi le médaillon de Trancheflanc comme preuve de son décès.",
    ["O"] = "Apporter le Médaillon de Trancheflanc à Falfindel Waywarder à Thalanaar.",
    ["T"] = "La mégère du Kraal",
  },
  [1102] = {
    ["D"] = "Cairne est un noble chef qui a unifié notre peuple, ici, à Thunder Bluff.$b$bMais je ne peux pas pardonner aussi facilement à ceux qui nous ont éloignés de nos terres ancestrales. Mon peuple a habité les Tarides du sud pendant des décennies. Cette terre est sacrée pour nous. Mais nous en avons été chassés par de nombreux ennemis.$b$bUn destin funeste attend la sinistre mégère, Charlga Trancheflanc, qui rameute une ignoble armée depuis le Kraal de Tranchebauge.$b$bApportez-moi le coeur de Trancheflanc et je pourrai mourir en paix, $n.",
    ["O"] = "Apporter le Cœur de Trancheflanc à Stonespire le Vieil, à Thunder Bluff.",
    ["T"] = "Un destin funeste",
  },
  [1103] = {
    ["D"] = "Bienvenue, $gmon pote:m\'dame; ! Vous êtes ici pour purifier l\'eau et prouver votre compréhension de cet élément à Islen ? J\'viens juste de vaincre l\'esprit corrompu de l\'eau, ouais, moi-même, et j\'m\'apprêtais à placer les bracelets et les gouttes d\'eau restantes dans le brasero.$B$BQu\'est-ce que c\'est que ce machin ? Ahhh. Tiev comprend très bien quand quelque chose tourne mal. Eh bien, Tiev peut beaucoup vous aider si vous voulez voir de nouveau l\'esprit pur de l\'eau.$b$bApportez-moi un sapta d\'eau d\'Islen comme preuve de votre bonne préparation, et j\'vous attendrai pour faire ma part du travail.",
    ["O"] = "Apporter un sapta d\'eau à Tiev Mordune dans la forêt des Pins argentés, si vous n\'arrivez pas à parler à la Manifestation d\'eau mineure.",
    ["T"] = "L\'appel de l\'eau",
  },
  [1104] = {
    ["D"] = "J\'ai tellement de modèles pour notre chariot explosif ! Tellement ! Et je dois tous les essayer !$b$bJe travaille à un nouveau type de carburant. Un qui met vraiment vraiment vraiment le feu ! Je n\'ai pas encore trouvé le mélange exact, mais je pense savoir ce dont j\'ai besoin... Les scorpides qui vadrouillent dans les Salines ont un venin qui contient des tas et des tas d’acides. C\'est tout à fait unique ! Et c\'est exactement ce qu\'il me faut !$b$bApportez-moi de ce venin et croyez-moi, notre chariot va voler !",
    ["O"] = "Apportez 6 doses de Venin de scorpide acide à Fizzle Brassbolts, dans les Salines.",
    ["T"] = "Le venin acide des salines",
  },
  [1105] = {
    ["D"] = "Mon frère travaille sur un nouveau carburant pour son chariot explosif. Il est brillant, vraiment. Tellement brillant que… Je pense que ce nouveau carburant va être tellement corrosif qu’il risque de dissoudre le châssis.$b$bJe veux renforcer ses réservoirs avant que ça n\'arrive.$b$bLes tortues des Salines ont une carapace très résistante. J\'avais l\'idée de les utiliser pour créer de nouveaux réservoirs sur notre chariot explosif. Des réservoirs capables de contenir le nouveau carburant de mon frère.$b$bVous avez l\'air suffisamment responsable. Vous pourriez m\'obtenir ces carapaces ?",
    ["O"] = "Apporter 9 Carapaces de tortue durcies à Wizzle Brassbolts dans les Salines.",
    ["T"] = "Les carapaces durcies",
  },
  [1106] = {
    ["D"] = "Je suis en train de développer un nouveau moteur. Il rendra le chariot si rapide ! Suffisamment rapide pour voler, je le sais ! J\'ai juste besoin de faire des pistons qui peuvent supporter de très très grandes pressions. Tous mes essais ont échoué, car je n\'arrive pas à faire des pistons suffisamment forts !$b$bMais il y a quelqu\'un qui pourrait m’aider.$b$bSon nom est Martek l\'Exilé. C\'est un grand forgeron et il en connaît plus sur les métaux que n\'importe qui d\'autre. Apportez-lui cette lettre, je sais qu\'il peut m\'aider !$b$bMais il est très très loin. En Azeroth, dans les Terres ingrates, dans un camp, avec un gobelin !$b$bTrouvez-le !",
    ["O"] = "Apporter la Lettre de Fizzle à Martek l\'Exilé dans les Terres ingrates.",
    ["T"] = "Martek l\'Exilé",
  },
  [1107] = {
    ["D"] = "Notre chariot explosif est ce qu\'il y a de plus rapide sur les plateaux. Tellement rapide que… j\'ai peur que les frottements ne le détruisent complètement.$b$bPour empêcher cela, je dois créer un composite pour enduire les parties mobiles du chariot. Il doit être dur, mais pas friable, et pouvoir résister à la corrosion du sel, ou le temps passé dans les Salines le grignotera petit à petit.$b$bAvez-vous entendu parler du Récif infâme, $n ? C\'est près de la côte de la vallée de Strangleronce. Des murlocs Selécaille y vivent, et leurs nageoires sont un élément crucial de mon composite.",
    ["O"] = "Apporter 10 Nageoires incrustées à Wizzle Brassbolts dans les Salines.",
    ["T"] = "Les nageoires incrustées",
  },
  [1108] = {
    ["D"] = "Je ne suis pas là simplement pour rester à l’écart des gens. Il y a une fortune en métal dans les Terres ingrates, $n, si vous avez les tripes pour aller la chercher.$b$bLe métal s\'appelle l\'indurium, et une de ses qualités est sa très haute résistance à la chaleur et à la pression.$b$bÇa pourrait faire l\'affaire pour le chariot de Fizzle.$b$bMais nous devons nous en assurer. Trouvez-moi des échantillons d\'indurium et je testerai ses propriétés.$b$bLe véritable indurium se trouve dans les profondeurs d\'Uldaman, mais les troggs Cavepierre des Terres ingrates en ont parfois des flocons sur eux. Apportez-moi ces flocons.",
    ["O"] = "Apporter 10 Flocons d\'indurium à Martek l\'Exilé dans les Terres ingrates.",
    ["T"] = "Indurium",
  },
  [1109] = {
    ["D"] = "Comme vous pouvez le constater, $N, nous préparons de très… intéressantes expérimentations à la Société royale des apothicaires.$B$BEn tant que Maître, mon travail est de superviser les plus ambitieuses de nos tentatives alchimiques. Tellement de travail et si peu de temps !$B$BVous avez l\'air d\'avoir beaucoup voyagé, $c. Peut-être pourriez-vous m\'aider.$B$BJ\'ai besoin d\'une substance rare. C\'est un matériau infect, qui provient d\'une espèce rare de chauves-souris que l\'on trouve dans la fosse connue sous le nom de Kraal de Tranchebauge. Apportez-moi du guano de chauve-souris du kraal afin que nous puissions nous mettre au travail...",
    ["O"] = "Apporter 1 tas de Guano du kraal au Maître Apothicaire Faranell à Undercity.",
    ["T"] = "Corvée de guano",
  },
  [1110] = {
    ["D"] = "Eh ! Avec toutes les courses qui ont lieu ici, il n\'est pas surprenant que quelques incidents surviennent le long du chemin.$b$bSi vous regardez tout autour des Salines, vous verrez des traces d’accidents. On trouve des pièces de chariots explosifs partout !$b$bEt ces pièces valent de l\'argent. Elles intéressent les gnomes et les gobelins. Ils cherchent toujours des pièces à monter sur leurs chariots.$b$bAlors allez-y et trouvez-moi des pièces. Apportez-m\'en un tas et je vous paierai bien.",
    ["O"] = "Apporter 30 Pièces de Chariot explosif à Kravel Koalbeard des Salines.",
    ["T"] = "Pièces de chariot explosif",
  },
  [1111] = {
    ["D"] = "Mon boulot, c\'est que les gens obtiennent ce dont ils ont besoin, et les gnomes par-là ont besoin qu\'une commande soit passée. Leurs pièces sont arrivées à Ratchet il y a une semaine, mais je n\'ai pas pu faire le voyage.$b$bAlors, êtes-vous $gprêt:prête; à marcher un peu ? Allez à Ratchet, et donnez cette commande de pièces au Maître des quais Dizzywig. Il se trouvera probablement sur les docks.$b$bFaites cela pour moi, et j\'aurai plus de boulots à vous confier.$b$bDes boulots, mon $gami:amie; avec de sérieux bénéfices à la clé...",
    ["O"] = "Apporter la Commande de Kravel au Maître des quais Dizzywig à Ratchet.",
    ["T"] = "Maître des quais Dizzywig",
  },
  [1112] = {
    ["D"] = "Voici le paquet de Kravel, $n. Et lorsque vous lui remettrez, dites-lui que s\'il veut plus de commandes spéciales, alors il doit payer l\'addition !",
    ["O"] = "Apporter les Pièces détachées de Kravel à Kravel Koalbeard dans les Salines.",
    ["T"] = "Les pièces détachées de Kravel",
  },
  [1113] = {
    ["D"] = "Vous pourriez vous demander pourquoi j\'ai besoin d\'un type rare de guano. Eh bien, pour cette expérience, je veux voir quels effets deux substances chimiques diamétralement opposées peuvent avoir l\'une sur l\'autre, et spécialement lorsqu\'elles sont combinées à un agent extrêmement toxique pour assurer les résultats les plus... effrayants.$B$BDans les pics au nord-est des Clairières de Tirisfal, vous trouverez le Monastère écarlate. Là, les disciples fanatiques de la Croisade se réunissent. Tuez-les et apportez-moi leur coeurs zélés.$B$BNotre mélange sera particulièrement diabolique !",
    ["O"] = "Le maître apothicaire Faranell, à Undercity, veut 20 Cœurs zélés.",
    ["T"] = "Des cœurs zélés",
  },
  [1114] = {
    ["D"] = "À présent que j\'ai ce dont j\'ai besoin, apportez ces pièces à Fizzle Brassbolts. Cela fait longtemps qu\'il les attend. Je suis sûr que la pièce que j\'ai prélevée sur sa commande ne lui manquera pas…$b$bEt ça ne vaut même pas le coup de lui dire. C\'est une toute petite pièce complètement inutile, de rien du tout. Minuscule...$b$bFaites-moi confiance.",
    ["O"] = "Apporter les Pièces de chariot fragiles à Fizzle Brassbolts dans les Salines.",
    ["T"] = "Une livraison pour les gnomes",
  },
  [1115] = {
    ["D"] = "Maintenant que nous avons rendu un service aux gnomes, il est temps de faire une faveur aux gobelins !$b$bJe crois savoir que le pilote de course gobelin, Nazz Steamboil, n\'a pas toujours porté ce nom. Je me demande pourquoi il en a changé. Je me demande s\'il s\'agit d\'un secret, et je me demande s\'il accorde de la valeur à ce secret.$b$bSi quelqu’un connaît la vérité, alors c\'est Krazek à Baie-du-Butin, sur la pointe sud de Strangleronce. Parlez à Krazek et interrogez-le sur notre pilote gobelin.$b$bSi vous parvenez à en tirer une information utile, alors nous pourrions peut-être en tirer profit...",
    ["O"] = "Parler à Krazek à Baie-du-Butin.",
    ["T"] = "Le marchand de rumeurs",
  },
  [1116] = {
    ["D"] = "Vous obtenir des informations sur Nazz est une grande faveur que je vous fais, $n. Oui, une grande faveur ! Mais vous avez fait tout le chemin depuis les Salines pour en avoir… Voulez-vous voyager encore un peu ?$b$bPendant que je contacte mes sources, pourquoi ne chercheriez-vous pas quelque chose pour moi, hein ?$b$bHmm... Qu\'est-ce que ce sera ?... Et que diriez-vous de Poussière de rêve ? On en trouve chez les dragonnets et les wyrmides du marais des Chagrins... Oui, j\'aimerais de la Poussière de rêve pour ma blague à tabac !$b$bTrouvez-m\'en, $n, et je vous fournirai l\'information que vous recherchez.",
    ["O"] = "Apporter 10 Grains de poussière de rêve à Krazek à Baie-du-Butin. La Poussière de rêve se récupère sur les dragonnets du marais des Chagrins.",
    ["T"] = "De la poussière de rêve dans les marécages",
  },
  [1117] = {
    ["D"] = "J\'ai dû utiliser une faveur qu’on me devait et abandonner une bouteille de vin elfique rare. J\'ai même dû faire des déductions difficiles moi-même. Mais j\'ai trouvé l\'information que vous cherchez.$b$bTenez, j\'ai écrit une lettre avec des passages très croustillants au sujet de notre ami, Nazz Steamboil. Faites-en ce que vous voulez, c\'est trop dangereux pour que moi je l\'utilise.$b$bMais ne dites à personne qui vous a informé !",
    ["O"] = "Apporter les Rumeurs gobelines à Kravel Koalbeard dans les Salines.",
    ["T"] = "Des rumeurs pour Kravel",
  },
  [1118] = {
    ["D"] = "$n, vous êtes quelqu\'un en qui je peux avoir confiance, pour faire le boulot, mais aussi pour la fermer tout en le faisant.$b$bJ\'ai échafaudé un nouveau plan, $c. C\'est risqué, mais si ça marche, on peut tous les deux se faire un paquet ! Et je pense que vous êtes la bonne personne pour ne pas se laisser abuser.$b$bPour commencer, vous devez retourner à Baie-du-Butin. Désolé, $gcher:chère; $n. Je sais que ma dernière quête vous y a $gamené:amenée;. Mais cette fois-ci, parlez à Crank Fizzlebub.$b$bDonnez-lui cette lettre. Et s\'il demande de l\'aide, faites de votre mieux pour lui apporter. Cela en vaudra la peine.",
    ["O"] = "Apporter le Plan de Kravel à Crank Fizzlebub à Baie-du-Butin.",
    ["T"] = "Retour à Baie-du-Butin",
  },
  [1119] = {
    ["D"] = "J\'ai trouvé comment mélanger la Mixture de Zanzil avec de la bière pour créer une boisson qui décape. Une Bière du sot. Je pense que c’est exactement ce dont Kravel a besoin, mais essayez-la et dites-moi comment ça se passe.$b$bEt $n, ne gardez pas trop longtemps ce truc sans l\'utiliser. La Mixture de Zanzil a un effet bizarre sur les levures de la bière, elle les booste un max. Mais elle ne va pas rester fraîche éternellement.",
    ["O"] = "Rapporter la Bière du sot à Kravel, aux Salines.",
    ["T"] = "La bière du sot",
  },
  [1120] = {
    ["D"] = "Nous devons essayer cette bière du Sot sur l\'une des équipes de mécanos. Si vous voulez l\'essayer sur l\'équipe des gnomes, alors tenez, prenez la bière et donnez-la au chef des mécaniciens gnomes.$b$bEt n\'attendez pas trop. Cette bière va se gâter après un temps.",
    ["O"] = "Apporter la bière du Sot au Chef-mécanicien gnome.",
    ["T"] = "Saoulez les gnomes",
  },
  [1121] = {
    ["D"] = "Nous devons essayer cette bière du Sot sur l\'une des équipes de mécanos. Si vous voulez l\'essayer sur l\'équipe des gobelins, alors tenez, prenez la bière et donnez-la au chef des mécaniciens gobelins.$b$bEt n\'attendez pas trop. Cette bière va se gâter après un temps.",
    ["O"] = "Apporter la bière du sot au Chef-mécanicien gobelin.",
    ["T"] = "Saoulez les gobelins",
  },
  [1122] = {
    ["D"] = "Cette Bière du sot a marché à merveille ! J\'ai parié sur la course suivante… Vous pensez bien que l\'équipe que vous avez éméchée a fait son numéro sur le chariot !$b$bIl n\'y a plus qu\'une chose à faire : apportez cette note à Crank Fizzlebub à Baie-du-Butin. Il voudra connaître les résultats du test.$b$bEt avec un peu de chance, nous aurons plus de cette Bière du sot pour les courses futures !",
    ["O"] = "Apporter le Rapport sur la Bière du sot à Crank Fizzlebub à Baie-du-Butin.",
    ["T"] = "Le compte rendu à Fizzlebub",
  },
  [1123] = {
    ["D"] = "Pour cette tâche, vous devrez assister et aider le majordome du Gardien Remulos, Rabine Saturna, à Reflet-de-Lune. Il vous faudra explorer les terres désolées qui se trouvent loin à l’ouest de Tanaris et d’Un\'Goro. Il habite à Havrenuit, le plus grand village de Reflet-de-Lune.$B$BJe voudrais que vous compreniez à quel point votre présence y est importante. En effet, les tensions ne cessent de croître entre les elfes de la nuit de Darnassus et le Cercle cénarien de Reflet-de-Lune. Essayez de vous départir de tout préjugé racial et appelez-en à la sagesse de vos ancêtres qui savaient comment traiter avec le Cercle cénarien, $N. Bonne chance.",
    ["O"] = "Parler à Rabine Saturna, au village de Havrenuit, à Reflet-de-Lune. Reflet-de-Lune se trouve entre Gangrebois et le Berceau-de-l\'Hiver. Le chemin qui y mène traverse le Repaire des Grumegueules.",
    ["T"] = "Rabine Saturna",
  },
  [1124] = {
    ["D"] = "A l’ouest du cratère d’Un\'Goro, par la crête du nord-ouest, se trouvent d’arides terres désolées du nom de Silithus. Peu nombreux sont ceux qui savent qu’une guerre y eut lieu, opposant les elfes de la nuit à une force extérieure maléfique. Le mal put être contenu, mais maintenant nous pensons que des forces sont à l’ouvrage pour réanimer cette inimaginable... horreur.$B$BAllez voir Layo Starstrike, l’un de nos agents dans Silithus ; vous le trouverez au cimetière du Repos des vaillants, là où reposent tous ceux qui sont tombés. Donnez-lui cette lettre, afin qu’il vous accorde sa confiance.",
    ["O"] = "Parler à Layo Starstrike, à proximité du Repos des vaillants de Silithus, et lui montrer la Lettre de Rabine.",
    ["T"] = "La dévastation",
  },
  [1125] = {
    ["D"] = "Au sud-ouest se trouve un endroit terrible : les ruines du village de Sudevent. C’est là que nous devons commencer à travailler, $N.$B$BD’après ce que j’ai appris, ce village fut jadis une base des elfes de la nuit en Silithus. Il est maintenant envahi par des ruches de silithides. Je ne peux penser sans la plus grande horreur comment le village fut jadis emporté et submergé...$B$BLes esprits torturés des druides et des Sentinelles hantent les ruines, sans but. Explorez cet endroit et libérez les âmes de ces malheureux.",
    ["O"] = "Libérer l’esprit de 8 Druides torturés et de 8 Sentinelles torturées, dans le village de Sudevent, puis revenir voir Layo Starstrike, au cimetière du Repos des Vaillants de Silithus.",
    ["T"] = "Les esprits de Sudevent",
  },
  [1126] = {
    ["D"] = "La ruche doit être traitée au village de Sudevent, et je pense que je sais comment les atteindre. Mon expérience m’a appris que les ruches des silithides étaient extrêmement sensibles à leur environnement. Ce qui explique leurs attaques dès qu\'ils entendent les gémissements des esprits.$B$BLes silithides sont plus nombreux dans la tour ; celle qui est entourée d’un nuage de guêpes. Escaladez la tour et forcez la ruche à entrer en activité. Revenez faire votre rapport sur ce que vous avez appris, mais faites attention ! Si vous découvrez quoi que ce soit qui vous semble utile, rapportez-le-moi.",
    ["O"] = "Escalader la Tour du village de Sudevent, et trouver un moyen de mettre la ruche des silithides en activité. Toute chose inhabituelle doit être rapportée à Layo Starstrike, au cimetière du Repos des vaillants de Silithus.",
    ["T"] = "Une ruche dans la tour",
  },
  [1127] = {
    ["D"] = "Ainsi, vous voulez de la Bière du sot, hein ? Vous pensez retourner aux courses ?$b$bTrès bien, apportez-moi de la Mixture de Zanzil et je préparerai davantage de bière pour vous.",
    ["O"] = "Apporter 12 Mixtures de Zanzil à Crank Fizzlebub à Baie-du-Butin.",
    ["T"] = "Bière du sot",
  },
  [1130] = {
    ["D"] = "$n, Melor Stonehoof a entendu parler de vos exploits, et vous a préparé une voie à suivre.$b$bSi vous souhaitez l\'emprunter, alors partez pour Thunder Bluff et parlez à Melor. Vous le trouverez sur la Cime des chasseurs.",
    ["O"] = "Parler à Melor Stonehoof à Thunder Bluff.",
    ["T"] = "Melor envoie un message",
  },
  [1131] = {
    ["D"] = "Parfois, nous chassons pour vivre. Parfois, nous chassons pour l\'honneur. Et parfois, nous chassons pour mériter le savoir de la Terre-mère. $B$BMais la chasse qui vous attend n\'est pas de celles-là. Il s\'agit de détruire une créature qui refuse les bénédictions de la Terre-mère. $B$BLes Mille pointes, au sud des Tarides, sont le terrain de chasse de la hyène Broie-l\'acier. C\'est un tyran, qui attire à lui les autres hyènes, répand la terreur et massacre les bêtes plus paisibles. $B$BTrouvez Broie-l\'acier, $N. Trouvez-le et tuez-le.",
    ["O"] = "Apportez 1 Côte de Broie-l\'acier à Melor Stonehoof à Thunder Bluff.",
    ["T"] = "Broie-l\'acier",
  },
  [1132] = {
    ["D"] = "Ah ! Pouvoir prendre la mer une fois encore ! Sentir le baiser du vent, être bercé par les vagues comme un enfant par sa mère !$B$BJ\'aimerais avoir votre chance, cher $c, car je vois la mer dans votre avenir proche !$B$BIl est de mon devoir de raconter aux âmes vaillantes la terre de Kalimdor, la terre de toutes les possibilités ! Si vous voulez chercher fortune sur la mer, prenez ici un bateau qui vous mènera au joli port de Theramore. Adressez-vous alors à mon amie, l\'elfe Fiora Longears.$B$BElle vous aidera à commencer l\'aventure de Kalimdor !",
    ["O"] = "S\'adresser à Fiora Longears à Theramore.",
    ["T"] = "Fiora Grandes-oreilles",
  },
  [1133] = {
    ["D"] = "Si vous débarquez tout juste du bateau qui vient de Menethil, la première chose à faire est d\'aller à Astranaar. Je suis sûr $gqu\'un:qu\'une; fidèle membre de l\'Alliance comme vous peut faire beaucoup de bien là-bas. Allez offrir vos services à Shindrell Switfire. $B$BInutile de mentionner le nom de la personne qui vous envoie, car Shindrell ne me connaît pas...",
    ["O"] = "Parler à Shindrell Switfire à Astranaar.",
    ["T"] = "Le voyage vers Astranaar",
  },
  [1134] = {
    ["D"] = "Nous ne sommes pas en guerre ouverte avec la Horde, mais la paix est fragile. Leurs agents agissent clandestinement contre nous. $B$BLeurs incursions nous ont été révélées par des sources secrètes à Theramore : il semble que des orcs collectent du venin des wyvernes Ailes-fières du lac Mirkfallon, dans le sud des Serres-Rocheuses. Un venin utilisé ensuite par leurs assassins lors de raids secrets contre l\'Alliance ! $B$BS\'il s\'agit de la vérité, nous devons les stopper, $N ! Nous devons anéantir leur source d\'approvisionnement en venin.",
    ["O"] = "Apporter 12 Glandes de venin d\'Aile-fière à Shindrell Swiftfire à Astranaar.",
    ["T"] = "Les Ailes-fières des Serres-Rocheuses",
  },
  [1135] = {
    ["D"] = "Comme vous le savez, les wyvernes sont souvent domestiquées par les orcs, qui les utilisent comme montures. Ce que l\'on sait moins, c\'est que, dans certains cercles, le venin de wyverne est plus recherché que la bête elle-même. Si vous n\'avez pas peur de chasser la wyverne, j\'ai un travail pour vous.$B$BIl existe dans la région des Mille pointes, à l\'ouest, un lieu nommé Haut-perchoir. Les wyvernes qui y nichent produisent un puissant venin. Très puissant et largement monnayable.",
    ["O"] = "Apporter 10 Glandes à venin de Haut-perchoir à Fiora Longears à Theramore.",
    ["T"] = "Le venin de Haut-perchoir",
  },
  [1136] = {
    ["D"] = "$N, il est temps pour vous de voyager vers de lointaines contrées. Le moment est venu. $B$BVous devez partir à la poursuite de Gueule-glacée, un yéti des montagnes d\'Alterac. Il est très difficile à trouver, car sa fourrure blanche se confond avec la neige de ses montagnes maternelles. $B$BJe vais vous montrer comment attirer une telle créature à découvert. Prenez ce parchemin. Il vous indiquera la manière de débusquer Gueule-glacée. $B$BAllez, maintenant, $N. Votre voyage sera long et plein d\'embûches. Votre quête sera accomplie lorsque vous rapporterez la crinière de Gueule-glacée.",
    ["O"] = "Rapporter la Crinière de Gueule-glacée à Melor Stonehoof à Thunder Bluff.",
    ["T"] = "Gueule-glacée",
  },
  [1137] = {
    ["D"] = "L\'Indurium est étonnamment résistant à la chaleur. Je suis certain que ce métal serait idéal pour les pistons de Fizzle. Mais il lui faudra du minerai d\'Indurium naturel, pas uniquement quelques paillettes. $B$BOn ne trouve ce minerai que très profondément enfoui dans les terres d\'Uldaman, en bordure septentrionale des Terres ingrates. C\'est là que se trouve le véritable Indurium. $B$BEnvoyez mes amitiés à Fizzle Brassbolts et répétez-lui ce que je viens de vous dire. $B$BQue votre voyage soit paisible, $N. Je ne serais pas étonné que nous soyons appelés à nous rencontrer de nouveau... Dès que Fizzle recevra mon message au sujet de l\'Indurium, il vous enverra certainement à Uldaman pour en chercher !",
    ["O"] = "Parler à Fizzle Brassbolts dans les Salines.",
    ["T"] = "Des nouvelles pour Fizzle",
  },
  [1138] = {
    ["D"] = "J’adore le crabe, ce fruit de mer par excellence ! Vous pouvez le faire griller, l’ébouillanter, le braiser, en faire des beignets frits, des acras, le déguster en salade ou le consommer en soupe ; voilà tout ce qu’on peut faire avec.$B$BJe suis ici en train de pêcher en ce moment, et je ne peux donc pas me procurer les morceaux de belle chair de crabe dont j\'ai besoin. Vous pouvez en trouver sur les clampants des récifs et sur les clampants des marées incrustés. Vous pouvez en faire plein de choses. Vous pouvez le faire griller, l’ébouillanter, le braiser...",
    ["O"] = "Collecter 6 morceaux de Belle chair de crabe pour Gubber Blump, à Auberdine.",
    ["T"] = "Fruit de la mer",
  },
  [1139] = {
    ["D"] = "Nous avons ralenti le clan Ombreforge en le coupant du pic Blackrock, mais la quête d\'Hammertoe n\'est toujours pas accomplie. Sa mort ne doit pas rester vaine. Il voulait trouver la Tablette de Volonté : un morceau de pierre que les Titans avaient gravé de caractères magiques pour les aider à donner la vie à leurs créations, leur communiquer une volonté propre. C\'est ce que dit la légende en tout cas. $B$BIl nous faut retrouver cette tablette avant ceux du clan Ombreforge !$B$BRepartez pour Uldaman, trouvez la Tablette et revenez ici immédiatement.",
    ["O"] = "Trouver la Tablette de Volonté et la rapporter au Conseiller Belgrum à Ironforge.",
    ["T"] = "La tablette de volonté",
  },
  [1140] = {
    ["D"] = "L\'écriture des orcs est à peine plus lisible que le gribouillage d\'un enfant. J\'ai essuyé bien des moqueries lorsque j\'ai décidé d\'apprendre leur langue, mais il est toujours utile de connaître l\'ennemi. $b$bSi j\'en crois les griffonnages d\'Ilkrud, il semble qu\'Athrikus possède toujours deux gemmes d\'âme captives, au défilé de la Nuit et à Satyrnaar, des régions tenues par les satyres au nord-est d\'Ashenvale. $B$BFaites attention, $n, les temples baignés de sang des satyres peuvent être effrayants, même pour des $cs aguerris !",
    ["O"] = "Libérer les Âmes de Bien-nés captives au défilé de la Nuit et à Satyrnaar, puis retourner voir Delgren le Purificateur au poste de Maestra.",
    ["T"] = "La Tour d\'Althalaxx",
  },
  [1141] = {
    ["D"] = "J’adore pêcher. La famille Blump est connue comme une grande famille de pêcheurs. Je m’appelle Gubber Blump ; je pêche.$B$BIl y a un poisson que j’aime attraper dans le coin, le mérou de Sombrivage. J’avais l’habitude d’aller en bateau pour pêcher Ie mérou, mais les bateaux ne sortent plus depuis que ces maudits murlocs sont arrivés. Je suppose qu’ils ont aussi dévoré tous les mérous.$B$BVoulez-vous m’aider à attraper des mérous de Sombrivage ? Si vous m’aidez, je vous donnerai une authentique canne à pêche de la famille Blump ! C’est une excellente canne pour attraper le poisson.",
    ["O"] = "Attraper 6 Mérous de Sombrivage pour Gubber Blump, à Auberdine.",
    ["T"] = "La famille à la canne à pêche",
  },
  [1142] = {
    ["D"] = "Il ne me reste plus beaucoup de temps... Tellement... Tellement faible... $b$bLe brave Lonebrow a été envoyé pour avertir Falfindel... $b$bMais avant que je ne m-m-meure... $b$b... Ma femme, Treshala... Son pendentif de mariage avec moi... V-v-volé... par un de ces sauv-v-vages agresseurs... $b$bTrouvez-le, j-j-je vous en prie... Et rapportez-le à Treshala, à Darnassus...$b$b... Et racontez-lui ma mort... solitaire.",
    ["O"] = "Trouver et rapporter le Pendentif de Treshala à Treshala Fallowbrook à Darnassus.",
    ["T"] = "Le déclin et la mort",
  },
  [1143] = {
    ["D"] = "Je vois. Si ce que vous affirmez est vrai, alors c\'est le moment de frapper. Je sais qu\'Athrikus réside en haut de la tour. Faites attention, car il est entouré de nombreux démonistes, bien plus dangereux que ceux que vous avez déjà combattus.",
    ["O"] = "Tuer Athrikus Narassin et apporter sa Tête à Balthule Shadowstrike près de la Tour d\'Althalaxx.",
    ["T"] = "La Tour d\'Althalaxx",
  },
  [1144] = {
    ["D"] = "Enfin de l\'aide ! $b$bVous n\'avez pas idée du temps que j\'ai passé prisonnier ici ! Je n\'arrive plus à me rappeler quand j\'ai vu le monde extérieur pour la dernière fois. $b$bVous voyez, je suis un entrepreneur. Prêt à tout en échange d\'un sou, si vous voyez ce que je veux dire. J\'avais entendu dire que Mizzyrix payait bien pour les Racines de Feuillerouge locales, de ce trou à rats ! Je pensais que ce serait de l\'argent facile. Je me trompais et pas qu\'un peu ! $b$bEnfin... Merci de m\'avoir sorti de là, je vous le revaudrai...",
    ["O"] = "Escorter Willix l\'Importateur hors du Kraal de Tranchebauge.",
    ["T"] = "Willix l’Importateur",
  },
  [1145] = {
    ["D"] = "Bien des voyageurs qui sont passés par la Croisée m\'ont aidé à ramasser des oeufs dans le Champ des géants, et nombreux sont ceux qui ont rapporté des récits effrayants sur les créatures. J\'en ai assez entendu pour considérer ces insectes comme plus dangereux que ne l\'avaient cru mes maîtres de prime abord. $B$BJ\'ai besoin d\'un courrier pour aller à Orgrimmar trouver Belgrom Rockmaul. Cela ne sera pas simple et celui qui ira devra prendre cette tâche au sérieux. $B$BOn trouve généralement Belgrom devant la maison des guerriers.",
    ["O"] = "Remettre la Note scellée de Korran à Belgrom Rockmaul à Orgrimmar.",
    ["T"] = "L\'essaim s\'agrandit",
  },
  [1146] = {
    ["D"] = "Ainsi, Korran est arrivé aux mêmes conclusions que nous sur ces insectes... On dirait qu\'il s\'agit d\'une menace plus grande que nous ne le pensions au départ. $B$BIl y a quelques jours, un chasseur est rentré à moitié mort des Mille pointes. Il accompagnait un groupe chargé de déloger une équipe de fouille naine hors des Salines. Une nuée de ces insectes a jailli d\'une grotte pendant l\'attaque et les a presque anéantis.$B$BIls étaient sous les ordres de Moktar Krin et ils se sont réfugiés à la lisière des Salines. Apportez-lui cette note.",
    ["O"] = "Apporter la Note scellée de Belgrom à Moktar Krin aux Mille pointes.",
    ["T"] = "L\'essaim s\'agrandit",
  },
  [1147] = {
    ["D"] = "Hum... On dirait que Belgrom pense que ces monstres sont une menace. Il veut que nous les repoussions jusque dans les profondeurs abjectes d\'où ils sont sortis. $B$BOn dirait que tuer les nains de la Ligue des Explorateurs ne leur a pas suffi... Et maintenant il va falloir que j\'envoie une section à moitié anéantie mener une nouvelle bataille. Nous ne pourrons pas réaliser cette mission par nous-mêmes. Pas tant que nous serons dans cet état. Le site de fouilles est là-bas vers le sud. Envie de manier l\'épée et de nous donner un coup de main ?",
    ["O"] = "Tuer 5 Chercheurs silithides, 5 Bourdons silithides et 5 Envahisseurs silithides, puis retourner voir Moktar Krin aux Mille pointes.",
    ["T"] = "L\'essaim s\'agrandit",
  },
  [1148] = {
    ["D"] = "Vous inspectez la carapace avec soin. Comme vous la tournez, vous comprenez que le placage est entrecroisé de telle manière qu\'elle lui donne une dureté incroyable. Cela ne ressemble à rien que vous connaissiez. Vous inspectez le reste du corps et vous réalisez que ces créatures sont très puissantes et très évoluées. $B$BEn regardant de nouveau la carapace, vous vous dites que Korran apprécierait sûrement d\'étudier ces créatures d\'un peu plus près. Cela l\'aiderait à découvrir ce qu\'elles sont et d\'où elles viennent.",
    ["O"] = "Apporter 1 Coeur de silithide, 5 Serres de silithide et 3 Carapaces de silithide intactes à Korran, à la Croisée.",
    ["T"] = "Des parties de l\'essaim",
  },
  [1149] = {
    ["D"] = "Vous me cherchiez, ou peut-être est-ce le destin qui vous mène à moi ? Aucune importance. Vous voilà et un nouveau voyage commence pour vous. $B$BIl y a différents aspects dans un être : l\'esprit, le corps, l\'âme. Certains peuvent être améliorés par les traditions d\'une culture. Pour d\'autres... Nous pouvons simplement espérer qu\'elles existent en nous. Si vous êtes d\'accord, je vais mettre à l\'épreuve quelques-unes de vos qualités. $B$BSi vous réussissez, vous y gagnerez en force. Si vous échouez, vous comprendrez l\'immensité du Néant. $B$BLa première épreuve est l\'Epreuve de la Foi.",
    ["O"] = "Si vous avez la Foi, sautez des planches qui dominent les Mille pointes.",
    ["T"] = "L\'épreuve de la Foi",
  },
  [1150] = {
    ["D"] = "Il me plaît de constater que votre courage reste entier à la lumière de ce que vous avez déjà réalisé. Puisque votre foi est grande, il est temps de mettre les capacités de votre corps à l\'épreuve. Allez avec des amis jusqu\'à la tanière des Volplumes, à l\'ouest du poste de Librevent. Plongez au cœur de leur tanière et détruisez leurs réserves de nourriture. Cela devrait provoquer leur colère, et si vous êtes capable de survivre assez longtemps à leur furie, Grenka Hurlesang elle-même s\'attaquera à vous... Tuez-la et apportez-moi sa griffe en accomplissement de votre épreuve.",
    ["O"] = "Apporter la Griffe de Grenka au traqueur des plaines Dorn, aux Mille pointes.",
    ["T"] = "L’épreuve de l’Endurance",
  },
  [1151] = {
    ["D"] = "Au temps des Dieux très anciens, les élémentaires peuplaient ce monde. Ils adoraient les Dieux très anciens, et le pouvoir de ces derniers maintenait les élémentaires attachés à ce monde. Lorsque les Dieux très anciens furent bannis, les élémentaires disparurent de notre monde et ils n’y reviennent que s\'ils sont appelés par de puissants mages. $B$BIl y a pourtant des exceptions. Certains élémentaires étaient suffisamment puissants pour rester. L\'un d\'eux est Rok\'Alim. $B$BPour mettre votre force à l\'épreuve, trouvez Rok\'Alim dans les Mille pointes et tuez-le.",
    ["O"] = "Apporter des Fragments de Rok\'Alim au traqueur des plaines Dorn dans les Mille pointes.",
    ["T"] = "L\'épreuve de la Force",
  },
  [1152] = {
    ["D"] = "Vous avez presque terminé votre apprentissage avec moi, mais l\'Epreuve de la Connaissance n\'est pas de mon ressort. Il y a des leçons que l\'on apprend et des lieux que l\'on visite pour lesquels il faut un autre guide que moi-même. $B$BSi vous vous sentez apte, cherchez Braug Dimspirit... C\'est un chaman d\'une grande sagesse. Il vous mettra à l\'épreuve, alors prêtez attention à ses paroles, $N. $B$BLorsque vous vous estimerez apte, trouvez le tunnel qui relie les Serres-Rocheuses et Ashenvale : Braug habite près de là. Mais soyez $gprudent:prudente;, $N. Les elfes de la nuit pourraient vouloir vous empêcher d\'accomplir votre épreuve.",
    ["O"] = "Trouver Braug Dimspirit près de l’entrée de la Perce des Serres dans les Serres-Rocheuses.",
    ["T"] = "L\'épreuve de la Connaissance",
  },
  [1153] = {
    ["D"] = "Vous m\'avez apporté une aide précieuse par le passé, $c. Le Chef de guerre apprécie, au moins jusque-là, ce que j\'ai accompli, et me demande de continuer avec une nouvelle détermination. Mon honneur me commande de vous envoyer au Chef de guerre avec une recommandation pour tout ce que vous avez fait, mais avant je voudrais vous demander votre aide une nouvelle fois. $B$BDes voyageurs, récemment revenus d\'un endroit appelé les Mille pointes, ont tué un kobold porteur d\'un échantillon d\'un minerai inhabituel. Ils n\'ont pas voulu le vendre. Je voudrais que vous me rapportiez un échantillon de ce minerai.",
    ["O"] = "Trouver un échantillon de minerai brut sur un Mineur ou un Géomètre Mufle-de-gravier et l’apporter à Tatternack Steelforge, à Camp Taurajo dans les Tarides.",
    ["T"] = "Un nouveau minerai",
  },
  [1154] = {
    ["D"] = "Vous avez réussi les épreuves de Dorn et vous cherchez à présent le défi suivant. Ce que vous avez réussi, un enfant aurait pu le réussir. Il est facile de mettre son corps à l\'épreuve. Pour l\'esprit, c\'est tout autre chose. $B$BJe vais m\'expliquer simplement : trouvez le livre dont je vais vous parler, étudiez-le et répondez à mes questions sur son contenu. $B$BQuel livre ? « L\'Héritage des aspects ». On peut le trouver au pied de Dor\'danil au sud d\'Ashenvale, protégé par les druides morts et leurs assassins.",
    ["O"] = "Trouver l’Héritage des Aspects et l’apporter à Braug Dimspirit, à côté de l’entrée de la Perce des Serres, dans les Serres-Rocheuses.",
    ["T"] = "L\'épreuve de la Connaissance",
  },
  [1155] = {
    ["D"] = "x",
    ["O"] = "x",
    ["T"] = "<NYI> <TXT> bug crystal side quest",
  },
  [1156] = {
    ["D"] = "x",
    ["O"] = "x",
    ["T"] = "<NYI> <TXT> speak to alchemist pestlezugg",
  },
  [1157] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<NYI> <TXT> pestlezugg needs items",
  },
  [1158] = {
    ["D"] = "x",
    ["O"] = "x",
    ["T"] = "<NYI> <TXT> speak to rabine saturna",
  },
  [1159] = {
    ["D"] = "Bien joué, $N. Vous avez passé la première partie de l\'Épreuve de la Connaissance, mais il reste une question à laquelle vous devez répondre. Cette question, je ne puis la poser.$B$BCherchez Parqual Fintallas dans la cité de Lordaeron. C’était un grand historien parmi les humains, avant d\'être emporté par la Peste. Il continue sa quête du savoir, mais un savoir d\'une nature différente.$B$BIl exigera de vous une certaine tâche avant d’accepter de vous tester, mais vous avez superbement mené à bien les épreuves jusqu\'ici... Je suis sûr que vous surmonterez cet obstacle.",
    ["O"] = "Trouver Parqual Fintallas à Undercity.",
    ["T"] = "L\'épreuve de la Connaissance",
  },
  [1160] = {
    ["D"] = "Je vous dirai ce que vous devrez chercher, et je vous poserai même ma question, mais certainement pas parce que vous cherchez à vous prouver quelque chose à vous-même. La Connaissance est bien plus importante que la force. Même si d\'autres vous surpassent en habileté, votre connaissance du monde vous sera précieuse. $B$BRendez-vous au Monastère au nord des Clairières de Tirisfal. Dans la bibliothèque, vous trouverez un livre intitulé « Les commencements de la menace des morts-vivants ». Il était utilisé par les paladins, désormais corrompus, pour leur entraînement contre ceux de ma race. $B$BApportez-le-moi.",
    ["O"] = "Trouver « Les commencements de la menace des morts-vivants » et le rapporter à Parqual Fintallas à Undercity.",
    ["T"] = "L\'épreuve de la Connaissance",
  },
  [1161] = {
    ["D"] = "x",
    ["O"] = "x",
    ["T"] = "<NYI> <TXT> gossip shade of ambermoon",
  },
  [1162] = {
    ["D"] = "x",
    ["O"] = "x",
    ["T"] = "<NYI> <TXT> speak to hamuul runetotem",
  },
  [1163] = {
    ["D"] = "x",
    ["O"] = "x",
    ["T"] = "<NYI> <TXT> speak to tyrande whisperwind",
  },
  [1164] = {
    ["D"] = "La traînée ! La truie ! Kenata vit toujours ; sa famille est riche et prospère tandis que je souffre, comme une Réprouvée que je suis !$B$BMa famille a disparu, emportée par la peste. Notre domaine a disparu, lui aussi, détruit et brûlé pendant la guerre. Et malgré cela, aidée de ses misérables enfants, elle a l\'audace de voler le seul bien qui me reste.$B$BJe n\'attache plus d\'importance aux choses qui m\'ont été volées. Ce que je veux à présent c\'est leurs têtes !$B$BTuez-les pour moi ! Rendez-vous à la ferme des Dabyrie en Arathi, au nord-est du Refuge de l\'Ornière.",
    ["O"] = "Apporter la Tête de Kenata, Fardel et Marcel Dabyrie à Genavie Callow à Undercity.",
    ["T"] = "Le voleur volé",
  },
  [1166] = {
    ["D"] = "Inclinez-vous devant le seigneur Mok\'Morokk !$b$bRexxar faire de moi le puissant chef du clan Cognepierre !$b$bMoi bon chef. Moi garder clan en sûreté.$b$bGrands dragons venir et faire le feu avec anciennes maisons. Mais moi ogre malin. Trouver nouveau foyer pour clan ici.$b$bQuitté ancienne maison trop vite. Oublié truc. Minuscule $r, aller trouver mes trucs. Aller sud et trouver mon grog délicieux, mon tabac à priser et mon coffre. Moi apprécier vous après ça. Chercher dans Antre des flammes et dans ruines Cognepierres... Mes trucs là-bas, moi sûr !",
    ["O"] = "Le seigneur Mok\'Morokk de Mur-de-Fougères veut que vous retrouviez son grog, son tabac à priser et son coffre dans le marécage d\'Âprefange.",
    ["T"] = "Les soucis du Seigneur Mok\'Morokk",
  },
  [1167] = {
    ["D"] = "Il serait sage de vous entretenir avec Balthule avant d\'affronter Athrikus. Il a certainement observé la Tour avec une grande vigilance et il pourrait avoir des renseignements intéressants.",
    ["O"] = "S\'entretenir avec Balthule Shadowstrike près de la Tour d\'Althalaxx.",
    ["T"] = "La Tour d\'Althalaxx",
  },
  [1168] = {
    ["D"] = "Mok\'Morokk fait seigneur par Rexxar. Lui faire attention plus pouvoir que ogres. $b$bDragons noirs tuer beaucoup ogres. Tuer femme de Tharg. Mok\'Morokk dire s\'enfuir. Venir ici. $b$bOgres devoir combattre. Ogres pas bons pour fuite. Mok\'Morokk seulement intéresse Mok\'Morokk. $b$bMoi appeler maison \"ruines Cognepierres\". Moi mieux au sud. Ici pas maison. $b$bAider ogres à combattre armée des dragons noirs. Moi vouloir vengeance. Moi vouloir rentrer maison.",
    ["O"] = "Tharg de Mur-de-Fougères veut que vous tuiez 15 Eclaireurs Crins-de-feu, 10 Queues-cendrées Crins-de-feu et 5 Gardes Crins-de-feu.",
    ["T"] = "L\'armée des dragons noirs",
  },
  [1169] = {
    ["D"] = "Voyez ce vieux fou de Mok\'Morokk, qui se complaît dans son pouvoir et Tharg, qui combat les démons du désespoir et de la vengeance tout en espérant prendre la direction du clan. On dirait que je suis le seul qui cherche à connaître la source de l\'agression qui a détruit notre ancien foyer.$B$BPourquoi ce regard ahuri, $N ? Vous vous attendiez à m\'entendre parler comme un forban mal dégrossi parce que je suis un ogre ?$B$BBon ! Au travail, maintenant ! Apportez-moi des cœurs et des langues de dragonnets incendiaires et de jeunes incendiaires. Je pourrai alors chercher la source du mal.",
    ["O"] = "Draz\'Zilb de Mur-de-Fougères souhaite que vous lui apportiez 15 Langues d\'incendiaire et 15 Cœurs d\'incendiaire.",
    ["T"] = "Identification de la portée",
  },
  [1170] = {
    ["D"] = "Le village des Cognepierres a été envahi par la portée d\'Onyxia. Mais pourquoi la fille d\'Aile de mort, le seigneur du vol noir, est-elle descendue sur nos champs ?$b$bC\'est très troublant. Onyxia a sûrement un autre but que de faire le siège d\'un petit village ogre.$b$bRendez immédiatement compte à Mok\'Morokk ! Il faut agir !",
    ["O"] = "Parler au seigneur Mok\'Morokk au village de Mur-de-Fougères.",
    ["T"] = "La portée d\'Onyxia",
  },
  [1171] = {
    ["D"] = "Vous me laisser maintenant. Aller dire à Draz\'Zilb nous rester ici. Pas de dragon noir ici.",
    ["O"] = "Parler à Draz\'Zilb à Mur-de-Fougères.",
    ["T"] = "La portée d\'Onyxia",
  },
  [1172] = {
    ["D"] = "L\'engeance d\'Onyxia s\'est répandue dans tout le Cloaque aux dragons. Il est impératif d\'empêcher les dragonnets d\'éclore. $b$bHâtez-vous de rejoindre la tourbière du Ver, au sud du marécage d\'Âprefange. Il est sûr qu\'elle a laissé ses œufs là-bas. Trouvez-les tous et détruisez-les. $b$bNous ne pourrons jamais rebâtir le village des Cognepierres si les environs servent de nid aux dragons ! $b$bQuant à Mok\'Morokk, j\'ai d\'autres plans pour lui…",
    ["O"] = "Draz’Zilb, de Mur-de-Fougères, veut que vous détruisiez 5 des Œufs d\'Onyxia.",
    ["T"] = "La portée d\'Onyxia",
  },
  [1173] = {
    ["D"] = "Toi oser défier le Seigneur Mok\'Morokk ? $b$bMorbleu ! $b$bMoi écraser $r comme un insecte ! $b$bToi me dire quand vouloir bonne raclée.",
    ["O"] = "Défaire Mok\'Morokk en combat singulier et rapporter la nouvelle à Draz’Zilb au village de Mur-de-Fougères.",
    ["T"] = "Défier le Seigneur Mok\'Morokk",
  },
  [1174] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Gnomes Win!",
  },
  [1175] = {
    ["D"] = "Après le gros accrochage entre le Tonnerre rouge gobelin et un basilic, Kronkrider m\'a embauché pour surveiller la piste. Je ne sais pas ce que mangent les animaux d’ici pour grossir comme ça, mais il faut réduire leur nombre si nous voulons éviter qu\'ils ne se baladent sur la piste pendant une course. $b$bJe vais vous dire : si vous voulez gagner un peu d\'argent sortez d\'ici et faites dégager les basilics de la zone.",
    ["O"] = "Tuer 10 Basilics du sel gemme, 10 Peaux de cristal du sel gemme et 10 Contemplateurs du sel gemme pour le maître pisteur Zherin des Salines.",
    ["T"] = "Les cahots de la piste",
  },
  [1176] = {
    ["D"] = "Quand on veut vraiment courir, chaque détail compte. Il faut tout prendre en compte depuis la plus petite chute de puissance jusqu\'au plus léger défaut dans la tension des fils des bobines, en passant par un trop grand allégement du châssis… $b$bNous utilisons des os de vautour pour fabriquer une grande partie des structures mineures de nos voitures, qui sont des modèles d\'équilibre entre légèreté et solidité. Cela pourrait être encore amélioré si nous avions plus de ces os ! Ils sont faciles à obtenir : les plateaux salés sont littéralement infestés de vautours. $b$bRapportez-moi des os de vautour et vous recevrez une récompense en échange.",
    ["O"] = "Se procurer 10 Os creux de vautour pour Pozzik dans les Salines.",
    ["T"] = "Allégement de la structure",
  },
  [1177] = {
    ["D"] = "Pil\'bou grand ogre.$b$bMoi faim.$b$bAller taper poisson. Apporter manger pour Pil\'bou.$b$bVous nourrir Pil\'bou sinon Pil\'bou manger vous.",
    ["O"] = "Pilebou Piessale, dans le nord d\'Âprefange, veut 12 Têtes d\'Aileron boueux.",
    ["T"] = "Faim !",
  },
  [1178] = {
    ["D"] = "Notre équipe de course est la fierté de tous les gobelins ! Vous seriez surpris si je vous disais les noms de tous ceux qui y participent, d\'une manière ou d\'une autre ! $b$bVous connaissez Gazlowe ? Il voyage dans le monde entier pour nous rapporter nos pièces détachées. Il a même participé une fois à la construction d\'un des engins ! $b$bIl y a quelques semaines, il a dit qu\'il nous enverrait un nouveau prototype de régulateur de carburant, mais il n\'est pas encore arrivé. D\'habitude, il est plus rapide que ça... Peut-être accepteriez-vous d\'aller à Ratchet pour le lui demander ? Je sais que j\'exagère un peu en vous le demandant, mais les gnomes sont en train de nous prendre de vitesse !",
    ["O"] = "Parler à Gazlowe à Ratchet.",
    ["T"] = "Mécénat gobelin",
  },
  [1179] = {
    ["D"] = "Vous avez déjà entendu parler des Salines ? C\'est une étendue plate et salée au-delà de Kalimdor, juste au sud du canyon des Mille pointes. C\'est un endroit fabuleux pour les courses ; forcément, c\'est complètement plat ! $B$BUne paire de gnomes, des frères, des amis à moi, sont là-bas pour essayer leur nouvelle voiture à réaction. D\'après ce qu\'on m\'a dit, elle dépote ! Elle dépote tellement que les pilotes se crashent tous l\'un après l\'autre. Et finissent avec le crâne en miettes. $B$BAlors, évidemment, ils m\'ont demandé de fabriquer des casques, mais j\'ai des contraintes ici ! Je ne peux pas faire le voyage ! $B$BVous pouvez ?",
    ["O"] = "Apporter la caisse de casques anti-accidents à Wizzle Brassbolts, dans les Salines.",
    ["T"] = "Les frères Brassbolts",
  },
  [1180] = {
    ["D"] = "Bon, revenons à la raison de votre voyage. Je sais que j\'ai promis à Pozzik un nouveau régulateur de carburant, mais je ne l\'ai pas encore reçu. Revilgaz, à Baie-du-Butin, dit qu\'il l\'a envoyé par bateau, mais il s\'est peut-être perdu en route. $b$bSi c\'est le cas, nous ne pouvons pas faire grand-chose. Mais nous pouvons tout de même vérifier qu\'il est bien parti de Baie-du-Butin. $b$bVérifiez avec Maître Lozgil, sur les quais, là-bas, il devrait être en mesure de vérifier les registres de chargement.",
    ["O"] = "S\'adresser à Maître Lozgil à Baie-du-Butin.",
    ["T"] = "Mécénat gobelin",
  },
  [1181] = {
    ["D"] = "Désolé, je ne vais pas être d\'un grand secours, $n ! Si vous voulez d’autres informations, je vous suggère d\'aller trouver le baron lui-même. S\'il y a quelqu\'un qui est en mesure de vous renseigner sur les affaires de Revilgaz, c\'est Revilgaz lui-même. $b$bVous le trouverez à l’étage de la Taverne, de l\'autre côté du port.",
    ["O"] = "Parler au baron Revilgaz à Baie-du-Butin.",
    ["T"] = "Mécénat gobelin",
  },
  [1182] = {
    ["D"] = "Vous voyez, les déchiqueteurs de la KapitalRisk du lac Nazfériti (au nord de Strangleronce) sont équipés d\'un nouveau régulateur de carburant. Je pensais que Pozzik serait en mesure de l\'adapter s\'il avait quelques détails précis à son sujet. Malheureusement, j\'ai sous-estimé l\'intelligence de Cozzle, le Surveillant du chantier. $b$bPeut-être aurez-vous plus de chance dans la recherche des plans. Je sais qu\'il les garde chez lui, mais ils sont certainement sous clé.",
    ["O"] = "Apporter les Plans du régulateur de fuel au baron Revilgaz à Baie-du-Butin.",
    ["T"] = "Mécénat gobelin",
  },
  [1183] = {
    ["D"] = "Vous devriez être sur le départ. Transmettez mes salutations à Pozzik et dites-lui que s\'il a besoin de quoi que ce soit, qu’il n\'hésite pas à demander. J\'attends les nouvelles de sa victoire avec impatience !",
    ["O"] = "Apporter les Plans du Régulateur de carburant à Pozzik dans les Salines.",
    ["T"] = "Mécénat gobelin",
  },
  [1184] = {
    ["D"] = "Retournez voir Belgrom à Orgrimmar pour lui dire que Moktar fait tout ce qu\'il peut aux Mille pointes. Dites-lui aussi que mes études progressent et que je pourrai lui donner des détails assez vite. Cela devrait le satisfaire pleinement.",
    ["O"] = "Retour à Orgrimmar pour voir Belgrom Rockmaul.",
    ["T"] = "Des parties de l\'Essaim",
  },
  [1185] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Sous la chitine...",
  },
  [1186] = {
    ["D"] = "Je vais être très occupé avec l\'intégration de ce nouveau Régulateur de carburant sur la structure de nos voitures de course, mais si vous voulez apporter une aide supplémentaire à l\'équipe, adressez-vous à Razzeric. C\'est notre pilote. En fait, c\'est notre dix-huitième pilote... Et il aime bien apporter ses petites modifications personnelles à sa voiture. $b$bJe vous préviens, il est un peu à côté de ses pompes alors tâchez de ne pas manifester de surprise quand vous lui parlerez !",
    ["O"] = "S\'adresser à Razzeric, dans les Salines.",
    ["T"] = "Le dix-huitième pilote",
  },
  [1187] = {
    ["D"] = "Pozzik est un grand mécanicien, je le sais, mais il tergiverse trop lorsqu\'il s\'agit de puissance et de se lancer dans l\'action. Ce n\'est pas mon cas.$B$BLorsque Pozzik en a fini avec une machine, je prends quelques instants pour apporter mes petites modifications. Vous savez ce que c\'est, je rends la direction un peu plus souple, j\'enlève des morceaux de carrosserie inutiles, et j\'y mets un peu plus de patate...$B$BJ\'ai commandé un lance-hydroglycérine à Shreev au Bidulium de Gadgetzan, mais apparemment le zeppelin qui le transportait s\'est écrasé dans le marécage d\'Âprefange ! Il faut que je le récupère, $N !",
    ["O"] = "Récupérer le Lance-hydroglycérine pour Razzeric dans les Salines.",
    ["T"] = "Les trucs de Razzeric",
  },
  [1188] = {
    ["D"] = "Mais qu\'est-ce que c\'est que ça ? Shreev a laissé la sûreté enclenchée. Ça limite l\'énergie que je peux en tirer à chaque fois. Je lui avais dit de l\'enlever ! Ça ne va pas ! $b$bVous devez rapporter ça au Bidulium de Gadgetzan dans le désert de Tanaris, et faire enlever ce truc par Shreev.",
    ["O"] = "Apporter le Lance-hydroglycérine à Shreev à Gadgetzan.",
    ["T"] = "La sécurité d\'abord",
  },
  [1189] = {
    ["D"] = "Voilà ! J\'ai fait selon les recommandations de Razzeric. Dites-lui simplement d\'être prudent quand il installera ce truc. À la moindre erreur, il n\'avancera pas : il explosera sur place.$b$bCe serait dommage, non ? Qui sait ce que vaudra notre dix-neuvième pilote !",
    ["O"] = "Rapporter le Lance-hydroglycérine modifié à Razzeric, dans les Salines.",
    ["T"] = "La sécurité d\'abord",
  },
  [1190] = {
    ["D"] = "Les frères Brassbolts ont utilisé un nouveau matériau de construction, c\'est la seule explication plausible. Leurs voitures avancent plus rapidement qu\'avant, mais leur aspect et toutes les données que nous avons recueillies sur leurs courses restent identiques.$b$bCe que nous savons, c\'est que Rizzle Brassbolts est branché à temps plein sur les plans de sa nouvelle découverte. $b$bZamek peut créer une diversion pour vous, ainsi vous pourrez dérober ces plans.",
    ["O"] = "Utiliser Zamek pour faire diversion, dérober les plans de Rizzle et les apporter à Pozzik dans les Salines.",
    ["T"] = "Rester dans la course",
  },
  [1191] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La diversion de Zamek",
  },
  [1192] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Minerai d\'indurium",
  },
  [1193] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Un piège cassé",
  },
  [1194] = {
    ["D"] = "Les schémas de Rizzle montrent le processus de raffinage de l\'Indurium. D\'après ses gribouillages, il apparaît que le minerai provient de la mine des nains d\'Uldaman dans les Terres ingrates.$b$bLa plupart des autres notes évoquent ses propriétés de résistance à la chaleur. $b$bCe qui sert de presse-papiers pour les plans est vraisemblablement un gros morceau d\'Indurium non raffiné.",
    ["O"] = "Apporter un Échantillon de minerai d\'indurium à Pozzik dans les Salines.",
    ["T"] = "Les schémas de Rizzle",
  },
  [1195] = {
    ["D"] = "Les centaures du clan Galak, dans les Mille pointes, protègent un artefact du temps de la création de leur espèce.$B$BNous aimerions le voir de plus près, mais il nous faut d’abord une fiole d\'eau provenant d’un des puits de lune des elfes de la nuit.$B$BPour puiser l\'eau, vous aurez besoin d\'une des fioles que transportent les dryades près de la retraite de Raynewood, au cœur de la forêt d\'Ashenvale. Il existe un puits de lune près de la rive ouest de la rivière Falfarren, au sud de la route principale. Vous pourrez y remplir la fiole.",
    ["O"] = "Rapporter une Fiole d\'Eau forte remplie à Zangen Stonehoof à Thunder Bluff.",
    ["T"] = "La flamme sacrée",
  },
  [1196] = {
    ["D"] = "Apportez la Fiole remplie à Rau Cliffrunner au Poste de Librevent dans le sud-est des Mille pointes. Vous pouvez atteindre les Mille pointes en empruntant la Grande élévation à la limite sud des Tarides. $b$bJe ne serais pas surpris que vous puissiez nous aider davantage.",
    ["O"] = "Remettre la Fiole d\'Eau forte remplie à Rau Cliffrunner au Poste de Librevent.",
    ["T"] = "La flamme sacrée",
  },
  [1197] = {
    ["D"] = "En l\'aspergeant avec l’eau que vous avez apportée, vous pourrez temporairement éteindre la flamme qui protège la relique des centaures. L\'effet sera provisoire, vous devrez faire vite.$b$bVous trouverez la combe du Sabot fendu, la ville fortifiée des centaures, plus au nord. La flamme est gardée dans le bastion du Sabot fendu, une zone très solidement gardée.$b$bFaites attention, les centaures du clan Galak sont très nombreux à cet endroit et ils combattront férocement pour protéger une chose aussi importante à leurs yeux.",
    ["O"] = "Retrouver le Sabot fendu pour Rau Cliffrunner du Poste de Librevent.",
    ["T"] = "La flamme sacrée",
  },
  [1198] = {
    ["D"] = "De nombreuses rumeurs circulent ces jours-ci sur l\'union entre le Marteau du crépuscule et les nagas dans les Profondeurs de Brassenoire sur les côtes d\'Ashenvale.$B$BMon rôle ici avec l\'Aube d\'argent est de collecter autant d\'informations que possible, afin que nous sachions où et quand on a besoin d\'aide.$B$BCela fait des semaines à présent que j\'ai envoyé un éclaireur espionner les activités menées dans les Profondeurs de Brassenoire. Le Garde d’argent Thaelrid n\'est toujours pas revenu.$B$BMes autres éclaireurs sont très pris par d\'autres tâches pressantes.$B$BRecherchez et venez en aide à Thaelrid pour moi, $N.",
    ["O"] = "Aller chercher le Garde d\'argent Thaelrid dans les Profondeurs de Brassenoire.",
    ["T"] = "À la recherche de Thaelrid",
  },
  [1199] = {
    ["D"] = "Les membres du Marteau du crépuscule, se servant des vieux rites des chaotiques Dieux très anciens, se battent pour les ramener en ce monde et pour y répandre la mort et la terreur.$B$BNous pensons que le Marteau du crépuscule s\'est allié aux nagas et se regroupe en masse dans les Profondeurs de Brassenoire sur la côte d\'Ashenvale.$B$BQuels que soient les plans maléfiques qu\'ils préparent dans les profondeurs du temple ancien, ils doivent être contrecarrés.$B$BLancez un assaut sur le Marteau et rapportez la preuve de vos actes. Vos efforts se verront récompensés de grandiose manière.",
    ["O"] = "Apporter 10 Pendentifs du Crépuscule au Garde d\'argent Manados à Darnassus.",
    ["T"] = "Le crépuscule descend",
  },
  [1200] = {
    ["D"] = "Mes forces me quittent. J\'ai besoin de votre aide ! $b$bIl y a longtemps, ce site était un grand temple d\'Elune. Mais la malchance l\'a conduit à sa ruine, quand un Dieu très ancien, en contaminant la terre, a corrompu le Puits de lune sacré. $b$bAku\'Mai, serviteur du Dieu très ancien, est sorti des eaux.$b$bLes sectateurs du Marteau du crépuscule se sont alliés aux nagas pour occuper ces terres. Les sectateurs, conduits par Kelris, sacrifient des innocents à Aku\'Mai pour augmenter son pouvoir. $b$bTuez Kelris et apportez sa tête à Selgorm, à Darnassus, je vous en prie, $N.",
    ["O"] = "Apporter la Tête du Seigneur du crépuscule Kelris au Veilleur de l\'aube Selgorm, à Darnassus.",
    ["T"] = "L\'infamie de Brassenoire",
  },
  [1201] = {
    ["D"] = "La trêve entre la Horde et l\'Alliance n\'est pas toujours respectée. Des escarmouches et des actions secrètes sont menées par chacun des deux camps, et il est de notre devoir de nous assurer que la Horde garde l’avantage ! $B$BNous avons maintenant l\'occasion de démasquer les infiltrateurs de l\'Alliance. $B$BIls sont en position à l\'est du village de Mur-de-Fougères. Ils nous espionnent et rapportent nos mouvements à Theramore, ce que nous ne pouvons permettre. $B$BTraquez-les et tuez-les. Envoyons un message clair à leurs chefs : nous ne supporterons pas leur présence sur notre sol !",
    ["O"] = "Tuer 9 Infiltrateurs de Theramore, puis rendre compte à Nazeer Bloodpike à Mur-de-Fougères.",
    ["T"] = "Les espions de Theramore",
  },
  [1202] = {
    ["D"] = "$N, deux de nos espions ont été envoyés pour dérober des documents sur un bateau dans les docks de Theramore. Ils ont trouvé les papiers, mais ont été découverts et ils ont dû jeter les documents à la mer. $B$BL\'un des deux a réussi à s\'échapper par la suite, mais il n’a pas pu récupérer le paquet. Il a rendu compte de son échec et maintenant je vous confie cette mission. $B$BLes documents sont dans un coffret, quelque part dans les eaux des docks de Theramore. Trouvez ces documents, $N, et apportez-les-moi.",
    ["O"] = "Apporter les Documents du capitaine à Nazeer Bloodpike au village de Mur-de-Fougères.",
    ["T"] = "Les docks de Theramore",
  },
  [1203] = {
    ["D"] = "Je dois découper. Je ne peux pas vous dire quel type de découpage je vais faire, mais j\'ai besoin d\'une lame… Mais pas n\'importe quelle lame !$B$BIl y en a qui l\'appellent Epée d\'acier lunaire. Moi je l\'appelle Epée à double tranchant.$B$BAllez-vous m\'aider ? Peut-être que vous la trouverez en ville, peut-être que vous pourrez la faire faire chez un forgeron.$B$BMais ne traînez pas. Ce que je dois découper n\'attendra pas des années.",
    ["O"] = "Apporter une Epée en acier lunaire à Jarl, dans le marécage d\'Âprefange.",
    ["T"] = "Jarl a besoin d\'une épée",
  },
  [1204] = {
    ["D"] = "Je prépare un plat que je nomme \"Soupe et insectes rochefange\". Il me manque encore les ingrédients principaux… Eh bien, je pense que je ne ferais pas long feu à patauger tout seul dans le marécage d\'Âprefange !$B$BAussi, $N, cela vous dérangerait-il de faire une petite partie de chasse pour moi ? D\'abord, il me faut des langues de tortues Rochefange. Vous pouvez en trouver le long de la côte, près de la baie.$B$BMais il ne me faut pas n\'importe quelles langues ! J\'entends par là que certaines tortues ont la langue fourchue...$B$BIl paraît que ces langues fourchues ont un goût particulier, et je veux cet ingrédient-là dans ma recette...",
    ["O"] = "Apporter 8 Langues fourchues de Rochefange à Morgan Stern, à Theramore.",
    ["T"] = "Soupe de Rochefange aux insectes",
  },
  [1205] = {
    ["D"] = "Mortebourbe est un ancien crocilisque du marécage d\'Âprefange. Son heure est arrivée. $B$BSes vieux os grincent quand il extrait son énorme corps du marais, et pourtant il évolue avec la force et la vitesse de ses jeunes années. Son corps fatigué torture le grand esprit qui l\'habite, un esprit dont la flamme ne faiblit pas. $B$BPourtant, il vit maintenant marqué par une douleur cruelle et permanente. Vous devez mettre un terme à la vie de cette noble créature, $N. Vous devez conduire Mortebourbe à la paix.",
    ["O"] = "Apporter la Dent de Mortebourbe à Melor à Thunder Bluff.",
    ["T"] = "Mortebourbe",
  },
  [1206] = {
    ["D"] = "Le jus d’œil est bon à boire. Malheureusement la plupart des monstres n\'ont que deux yeux. $B$BMais les araignées, elles, ont beaucoup d\'yeux. Plus que beaucoup. Et le jus d\'oeil de Sombrebrume est fameux. Doux et salé ! Des yeux d\'araignée entiers... J\'ai l\'impression de les savourer. $B$BLes araignées Sombrebrume se trouvent à l\'ouest d\'ici, dans la caverne de Sombrebrume. A votre avis, c\'est cette grotte qui a donné son nom aux araignées, ou l\'inverse ?",
    ["O"] = "Apporter 40 Yeux de Sombrebrume à Jarl \"Œil-des-marais\", au Manoir des Flammeroles.",
    ["T"] = "Jarl a besoin d’yeux",
  },
  [1218] = {
    ["D"] = "J\'aime la cuisine. La bonne cuisine du marais. Les cuisses de grenouille verte grillées par exemple. $B$BEt pour cuisiner j\'ai besoin d\'épices. Des épices apaisantes. Après je ferai un ragoût qui mijotera et qui mijotera et je le mangerai. Mais je vous en donnerai un peu aussi. $B$BVous aimez le ragoût ?",
    ["O"] = "Apporter 3 Epices apaisantes à Jarl \"Œil-des-marais\", dans le marécage d\'Âprefange.",
    ["T"] = "Epices apaisantes",
  },
  [1219] = {
    ["D"] = "En remuant la poussière, on trouve la main coupée d\'un orc. Une main qui agrippe encore un tube en os gravé. Le tube contient un parchemin taché.$B$BLe parchemin est écrit en orc... Et ça semble important.",
    ["O"] = "Communiquer le Rapport de l\'Espion à un Lieutenant de Theramore.",
    ["T"] = "Le rapport orc",
  },
  [1220] = {
    ["D"] = "Le capitaine Vimaire désire voir le rapport orc que vous avez trouvé, $N. C’est lui qui connaît le mieux la langue des orcs. Et il doit savoir qu\'il y a des espions de la Horde près de Theramore !$B$BLe capitaine est dans la Caserne, au nord-est de Theramore.",
    ["O"] = "Apporter le Rapport orc au capitaine Garran Vimaire.",
    ["T"] = "Le capitaine Vimaire",
  },
  [1221] = {
    ["D"] = "Les racines de feuillebleue sont une friandise universellement appréciée ! Mais elles sont rares, très rares. On ne peut les trouver qu\'ici, dans les Tarides, profondément enterrées au kraal de Tranchebauge.$B$BEt même ici, vous ne pourriez pas les trouver à moins de savoir comment les chercher ! C\'est pourquoi j\'ai entraîné ces Ecureuils Sniffetarin à les trouver. Ils ont un grand nez et peuvent sentir une racine à cinquante pas.$B$BCela ne sera pas facile, mais si vous me ramenez quelques racines, je vous paierai grassement.",
    ["O"] = "Prendre une Caisse percée.$BPrendre un Bâton de commandement de Sniffetarin.$BLire le Manuel d\'utilisateur de Sniffetarin.$B$BAu Kraal de Tranchebauge, utiliser la Caisse pour invoquer un Ecureuil Sniffetarin et utiliser le Bâton de commandement pour ordonner à l\'Ecureuil de chercher les racines.$B$BRamener 6 Racines de Feuillebleue, le Bâton de commandement de Sniffetarin et la Caisse percée à Mebok Mizzyrix, à Ratchet.",
    ["T"] = "Racines de Feuillebleue",
  },
  [1222] = {
    ["D"] = "Génial, enfin quelque chose qui ne veut pas me dévorer !$B$BJ\'ai besoin d\'aide pour occuper ces monstres autour de moi et vous m\'avez l\'air de pouvoir le faire.$B$BLe chef cuisinier Morgan Stern travaille sur une nouvelle recette et il m\'a envoyé ici pour cueillir des feuilles de haricots des marais. Je pense qu\'il les trouve savoureux.$B$BQuoi qu\'il en soit, pendant que je cherchais un plant de haricot, je me suis retrouvé cerné ! Pouvez-vous m\'aider à sortir de là et surveiller les alentours pendant que je cherche des plants de haricots ?",
    ["O"] = "Escorter Ignatz \"Puant\" puis discuter avec Morgan Stern à Theramore.",
    ["T"] = "Sauvetage du Puant",
  },
  [1238] = {
    ["D"] = "Au milieu de la poussière, une main d\'orc, seule. Et dans cette main, un tube creusé dans de l\'os. Dans le tube, un parchemin sale.$B$BCe parchemin est un rapport établi par un orc qui a passé des semaines à espionner la ville de Theramore.$B$BAprès examen du rapport, il est clair qu\'il doit être apporté au Maître Espion, au village de Mur-de-Fougères.",
    ["O"] = "Apporter le Rapport de l\'espion à Nazeer Bloodpike à Mur-de-Fougères.",
    ["T"] = "Le rapport perdu",
  },
  [1239] = {
    ["D"] = "Une fouille approfondie permet de découvrir une tête d\'orc mâle. Les lèvres sont figées dans un rictus et les yeux noirs semblent vous fixer avec défi.",
    ["O"] = "Apporter la Tête d\'orc défiant à Nazeer Bloodpike à Mur-de-Fougères.",
    ["T"] = "La tête coupée",
  },
  [1240] = {
    ["D"] = "Nous devons connaître les faits concernant la mort de Marg ! Et il n\'y a qu\'une seule manière de savoir, $N. C\'est d\'utiliser la sorcellerie des trolls.$B$BApportez la tête de Marg à Kin\'weelay dans la vallée de Strangleronce au Campement Grom\'gol.$B$BKin\'weelay est le plus puissant des Sorciers-docteurs des trolls Darkspear. Si quelqu\'un peut parler à l\'esprit de Marg Nighteye, c\'est lui.",
    ["O"] = "Apporter la Tête d\'orc défiant à Kin\'weelay au Campement Grom\'gol dans la vallée de Strangleronce.",
    ["T"] = "Le Sorcier-docteur troll",
  },
  [1241] = {
    ["D"] = "Récemment, un diplomate a été envoyé à Theramore pour rencontrer Jaina Proudmoore. Ce diplomate n\'est jamais arrivé.$B$BJe pense que les Défias sont impliqués dans ce complot, mais je ne vois pas de quelle manière. La disparition du diplomate n\'a pas encore attiré l\'attention du public, mais cette situation ne durera pas.$B$BDes agents du roi sont déjà en train de fouiller la ville à la recherche d\'indices, mais j\'aimerais consulter mes propres contacts.$B$BDans la Vallée des héros vit un vieil ami à moi qui s\'appelle Jorgen. Trouvez-le, donnez-lui cette note et suivez ses instructions.",
    ["O"] = "Trouver Jorgen dans la cité de Stormwind.",
    ["T"] = "Un diplomate a disparu",
  },
  [1242] = {
    ["D"] = "Portez cette note à Elling Trias. Ne lui dites rien d\'autre. Contentez-vous d\'être $gpoli:polie; et attendez la réponse. Et ne vous inquiétez pas, il répondra.$B$BQue ce soit bien clair, $N : c\'est une affaire sérieuse, alors gardez votre clapet fermé.$B$BVous trouverez Trias dans son magasin de fromages, « Aux Fromages de Trias », dans le Quartier commerçant. Bonne chance !",
    ["O"] = "Apporter la Note scellée de Jorgen à Elling Trias dans la cité de Stormwind.",
    ["T"] = "Un diplomate a disparu",
  },
  [1243] = {
    ["D"] = "Jorgen, vieux fils de… Alors c\'est comme ça que ça va se passer, hein ?$B$BTrès bien, je peux m\'en accommoder. J\'espère qu\'il trouvera le Néant distordu au plus tôt !$B$BVoilà comment nous allons procéder, $N. Je commence à utiliser mes contacts ici, mais comme c\'est vous qui faites le plus gros du travail de terrain, je vous prête un peu d\'argent.$B$BAllez à Darkshire, dans le bois de la Pénombre, et trouvez le gardien Backus. Il patrouille généralement sur la route nord juste en dehors de la ville. Dites-lui simplement que vous êtes là pour surveiller, pour moi, l\'activité des Défias, et il vous aidera en vous donnant toutes les informations qu\'il possède.",
    ["O"] = "Donner la Note scellée au Gardien Backus, au bois de la Pénombre.",
    ["T"] = "Un diplomate a disparu",
  },
  [1244] = {
    ["D"] = "Les activités des Défias ? Il y a toujours de l\'activité, même s\'ils ne se vantent pas que nous les considérions comme une menace.$B$BMais pendant que j\'y pense, il y a eu récemment quelque chose de bizarre. Cela s\'est passé il y a environ deux semaines : certains de leurs agents se sont réunis près de la ferme des Addle. D\'après le rapport, j\'en ai déduit que quelque chose d\'important était en préparation.$B$BPourquoi ne commenceriez-vous pas par vérifier cela et me rapporter tout ce que vous trouverez ?$B$BLa ferme est au sud de la route qui vient de la marche de l\'Ouest, juste à l\'endroit où vous rentrez dans le bois de la Pénombre.",
    ["O"] = "Trouver le Registre des Défias et le ramener au Gardien Backus au bois de la Pénombre.",
    ["T"] = "Un diplomate a disparu",
  },
  [1245] = {
    ["D"] = "Ouais ! Cela semble bien trop compliqué pour quelque chose qui serait juste destiné à vous induire en erreur. Je ne devrais même pas être en train de regarder tout ça. Portez-le à Trias. Il sera capable de comprendre tout cela. Et ne laissez PERSONNE se mettre en travers de votre chemin. Ceci est trop gros pour moi. C\'est peut-être même trop gros pour lui, mais il verra.$B$BEt faites-moi une faveur, $N, ne dites à personne que je vous ai fourni de l\'aide. Si mes supérieurs apprennent que je savais quelque chose sur cette affaire et que je ne les ai pas prévenus, ils me pendront.",
    ["O"] = "Ramener le Registre des Défias à Elling Trias à Stormwind.",
    ["T"] = "Un diplomate a disparu",
  },
  [1246] = {
    ["D"] = "Ouais, maintenant que j\'y pense… Dashel… Dashel… quelque chose… Son nom c\'était quoi déjà ? FIST ! Dashel Stonefist. Ils l\'appelaient Fist dans certains milieux, probablement parce qu\'il aimait se battre.$B$BVous le trouverez habituellement dans la vieille ville, pile au centre. Allez donc lui parler. Et n\'hésitez pas à utiliser une épée ou quelque chose de bien lourd et contondant pour l’engager à discuter. N\'y allez pas de main morte, s\'il est aussi impliqué que ces papiers le laissent à penser, il n\'est pas prêt à parler de son plein gré.",
    ["O"] = "Parler à Dashel Stonefist à Stormwind.",
    ["T"] = "Un diplomate a disparu",
  },
  [1247] = {
    ["D"] = "Alors ouais… Hum !… Il se pourrait que je sache quelque chose à propos de cette rencontre à la ferme des Addle. Ça s\'est passé il y a quelques semaines je crois. Je n\'y ai pas participé. J\'avais terminé ma part du travail lorsque le plan A a échoué… Maudits aventuriers !$B$BLa taupe que nous avions pour le plan B venait de Menethil, un type appelé Slim. C\'est tout ce que je sais sur leur plan de rechange.",
    ["O"] = "Parler à Elling Trias à Stormwind.",
    ["T"] = "Un diplomate a disparu",
  },
  [1248] = {
    ["D"] = "Un de mes contacts a pris sa retraite dans les Paluns, dans le port de la cité de Menethil pour être précis. Il sera d\'accord pour vous aider si vous lui dites que vous travaillez pour moi. Son nom est Mikhail. Avant de quitter Stormwind, il y a quelques années, il disait qu\'il voulait s’occuper d’un bar.$B$BParlez-lui, trouvez ce Slim et trouvez tout ce qui est possible sur ce diplomate disparu. Nous avons besoin de savoir où ils l\'ont emmené ou au moins qui il est. Le registre ne dit rien concernant leurs projets après sa capture.",
    ["O"] = "Parler à Mikhail dans les Paluns.",
    ["T"] = "Un diplomate a disparu",
  },
  [1249] = {
    ["D"] = "Vous dites que c\'est Elling qui vous envoie ? Eh bien l\'hospitalité que je vous dois est encore plus grande. Quels que soient vos besoins, demandez. Je ferai de mon mieux pour vous aider.$B$BHmm, Slim vous dites ? Ça ne me dit rien, mais je garderai un oeil ouvert au cas où.$B$BNe vous retournez pas maintenant, $N, mais le gentilhomme là-bas près de la sortie regarde par ici. Il écoute notre conversation un peu trop attentivement, si vous voyez ce que je veux dire. Si j\'étais vous, je l\'intercepterais avant qu\'il ne puisse atteindre la porte et disparaître.",
    ["O"] = "Soumettre Tapoke Jahn avant qu\'il ne s\'échappe et ensuite retourner voir Mikhail à Menethil.",
    ["T"] = "Un diplomate a disparu",
  },
  [1250] = {
    ["D"] = "Les Défias ne me payent pas suffisamment pour que je meure pour leur cause. Ils ont pris le roi. Ouais, le roi de Stormwind, pas un petit diplomate de rien du tout. Il était en route pour rencontrer Jaina Proudmoore. C\'était un gros coup apparemment et un grand secret. Mais il n\'y avait pas que les Défias qui y participaient. Ils travaillaient pour quelqu\'un. Je pense qu\'ils savaient que c\'était quelque chose de trop gros pour eux.$B$BMon travail consistait à introduire l\'un de leurs hommes sur le bateau du roi lorsqu\'il était à quai. Le nom de ce type est Hendel. C\'est tout ce que je sais. Je le jure !",
    ["O"] = "Parler à Mikhail dans les Paluns.",
    ["T"] = "Un diplomate a disparu",
  },
  [1251] = {
    ["D"] = "Un bouclier en fer, noirci par le feu qui a fait rage, est fixé à la cheminée branlante, une des rares structures encore debout de ce qui a été l\'auberge du Repos ombragé.$b$bLe bouclier peut être détaché du mur en brique de la cheminée.",
    ["O"] = "Rapporter le Bouclier en fer noirci à Krog, à Mur-de-Fougères.",
    ["T"] = "Le bouclier noir",
  },
  [1252] = {
    ["D"] = "Un éclat de lumière attire votre attention sur les gravats. Ecartant les débris et la poussière, vous trouvez un insigne émaillé représentant une ancre dorée sur fond blanc : le symbole de Theramore.$b$bSous l\'ancre est gravé : \"Lieutenant Paval Reethe\".",
    ["O"] = "Rapporter le badge de Reethe au capitaine Garran Vimaire à Theramore.",
    ["T"] = "Le lieutenant Paval Reethe",
  },
  [1253] = {
    ["D"] = "Un bouclier en fer, noirci par le feu qui a fait rage, est fixé à la cheminée branlante, une des rares structures encore debout de ce qui a été l\'auberge du Repos ombragé.$b$bLe bouclier peut être détaché du mur en brique de la cheminée.",
    ["O"] = "Rapporter le Bouclier en fer noirci au capitaine Garran Vimaire à Theramore.",
    ["T"] = "Le Bouclier noir",
  },
  [1258] = {
    ["D"] = "Un autre ingrédient de ma nouvelle recette est la patte de crabe géant. Et les crabes qu\'ils ont à Âprefange, ceux qu\'on appelle les Bourbecoques, ne conviennent pas ! J\'ai bien peur que les seuls crabes valables ne vivent loin, dans le marais des Chagrins.$B$BLa carapace des pattes de crabe est un des éléments clés de la présentation de ce plat. Cela veut dire que j\'ai besoin de pattes parfaites, sans trace ni éraflure. Un crabe adulte n\'a généralement qu’une ou deux pattes de cette qualité et il m\'en faut beaucoup pour mettre au point ce plat.$B$BAlors, $N... est-ce qu\'un voyage vous tente ?",
    ["O"] = "Apporter 12 Pattes de Clampant en parfait état à Morgan Stern à Theramore.",
    ["T"] = "... Et des bestioles",
  },
  [1259] = {
    ["D"] = "Le nom du lieutenant Paval Reethe ne me dit rien. Votre meilleure chance est de vérifier auprès de l\'adjudant Tesoran. Il conserve tous les dossiers de la garde de Theramore, du personnel jusqu\'à l\'équipement.$b$bIl devrait être capable de vous dire qui est ce Reethe après un rapide coup d\'oeil dans ses registres.",
    ["O"] = "Trouver plus de renseignements sur Paval Reethe auprès de l\'adjudant Tesoran à Theramore.",
    ["T"] = "Le lieutenant Paval Reethe",
  },
  [1260] = {
    ["D"] = "Je suis en train de créer une nouvelle carte pour le restaurant. Elle sera composée de plats typiques sur un thème \"bayou\". Je pense que ma clientèle au Solitaire Bleu va l\'adorer !$B$BJ\'ai envoyé mon neveu Morgan à Theramore, une ville au bord du marécage d\'Âprefange. Il collecte des recettes locales et j\'ai besoin de savoir ce qu\'il a déjà trouvé.$B$BLe voyage pour Theramore est long. Trop long pour moi. Mais je parie que quelqu\'un d\'aussi jeune que vous, $c, sauterait sur une occasion de voyager, non ?",
    ["O"] = "Parler à Morgan Stern à Theramore.",
    ["T"] = "Morgan Stern",
  },
  [1261] = {
    ["D"] = "Maudit Bourbecoque ! Tu ne peux pas tuer Marg ! Ah, mais je suis mort. Alors il doit m\'avoir tué...$B$BMais ma mission ! Ma mission ! Vous, $N, devez achever ma mission.$B$BAlors que j\'étais près de Theramore, j\'ai espionné un groupe de soldats de l\'Alliance qui campaient sur la plage. Un de ces soldats portait un pendentif... Un pendentif avec plus de pierres précieuses que dans les rêves d\'un soldat !$B$BCette nuit-là, je me suis glissé dans leur camp et j\'ai volé le pendentif, mais au retour à la base, un homme-crabe Bourbecoque m\'a tué !$B$B$N, vous devez le retrouver et récupérer le pendentif !",
    ["O"] = "Apporter le Pendentif orné à Nazeer à Mur-de-Fougères.",
    ["T"] = "Marg parle",
  },
  [1262] = {
    ["D"] = "Il est étrange que le pendentif que Marg a volé était en possession de simples soldats. Peut-être que son propriétaire était déguisé, ou que les soldats l’avaient eux-mêmes volé !$B$BQuoi qu\'il en soit, il s\'agit d\'un pendentif de noble et nous devons découvrir son véritable propriétaire si nous voulons percer ce mystère.$B$BApportez-le à Zor Lonetree à Orgrimmar. Il en connaît beaucoup sur les personnages politiques de l\'Alliance et saura dire si ce pendentif est connu parmi les nobles de Stormwind.",
    ["O"] = "Apporter le Pendentif orné à Zor Lonetree à Orgrimmar.",
    ["T"] = "Rapport à Zor",
  },
  [1263] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "The Burning Inn <CHANGE TO GOSSIP>",
  },
  [1264] = {
    ["D"] = "Au début de chaque mois, les soldats de marine de Kul Tiras reçoivent leur paye et quittent généralement Theramore pour un autre endroit. Un grand nombre d\'entre eux viennent ici pour les femmes, faire la bringue et passer un bon moment. L\'un de ces groupes vient régulièrement à cette taverne et je crois que ce Hendel en fait partie.$B$BLe commandant Samaul à Theramore devrait vous aider à le trouver si Hendel est toujours par ici. Il est loyal à Proudmoore, mais si vous citez négligemment le nom de Elling, il devrait vous aider... discrètement.",
    ["O"] = "Trouver le commandant Samaul, dans le marécage d\'Âprefange.",
    ["T"] = "Un diplomate a disparu",
  },
  [1265] = {
    ["D"] = "Hendel ? Oui, je le connais. Mais pourquoi avez-vous besoin d\'un de mes soldats ?$B$BElling vous a demandé de le trouver ? Je n\'aime pas cela. Elling ne vous aurait pas $genvoyé:envoyée; directement à moi si cela n\'était pas pour des problèmes. Je comprends que vous ne puissiez pas me dire de quoi il s\'agit, mais sachez ceci : j\'ai pour mission de protéger ce pays. Si vous pensez créer des problèmes, alors je vous suggère de m\'en informer.$B$BJe crois que Hendel est cantonné à la première tour en dehors de Theramore.",
    ["O"] = "Chercher le soldat Hendel à la halte de la Vigie.",
    ["T"] = "Un diplomate a disparu",
  },
  [1266] = {
    ["D"] = "Le soldat Hendel se trouve à l\'ouest d\'ici. Il est en ce moment de garde et protège des infiltrateurs de Theramore tandis qu\'ils se reposent. Ils ont tenu à l\'œil la base de la Horde, Mur-de-Fougères, et tout ce qui pourrait menacer nos frontières.$B$BVous ne devriez rencontrer aucune difficulté pour le trouver. Cherchez simplement des tentes le long de la route.$B$BUne autre affaire nécessite mon attention, mais je vous rejoindrai bientôt. Vous pourrez toujours me rencontrer dans la tour de dame Proudmoore si vous avez le moindre problème.",
    ["O"] = "Trouver le soldat Hendel dans le marécage d\'Âprefange.",
    ["T"] = "Un diplomate a disparu",
  },
  [1267] = {
    ["D"] = "",
    ["O"] = "Parler à Jaina Proudmoore au marécage d\'Âprefange.",
    ["T"] = "Un diplomate a disparu",
  },
  [1268] = {
    ["D"] = "Des lignes d\'empreintes de sabots partent des ruines encore fumantes de l\'auberge du Repos ombragé. Il y a au moins quelques traces visibles avec d\'autres empreintes peu marquées dans la boue du marais.",
    ["O"] = "Faire votre rapport sur les empreintes suspectes de l\'auberge du repos ombragé à Krog, au village de Mur-de-Fougères.",
    ["T"] = "Des empreintes de sabots suspectes",
  },
  [1269] = {
    ["D"] = "Un éclat de lumière attire votre attention sur les gravats. Ecartant les débris et la poussière, vous trouvez un insigne émaillé représentant une ancre dorée sur fond blanc : le symbole de Theramore.$b$bSous l\'ancre est gravé : \"Lieutenant Paval Reethe\".",
    ["O"] = "Rapporter le Badge de Reethe à Krog à Mur-de-Fougères.",
    ["T"] = "Le lieutenant Paval Reethe",
  },
  [1270] = {
    ["D"] = "Enfin quelque chose qui ne semble pas vouloir me manger !$B$BJ\'ai besoin d\'aide pour repousser ces monstres et vous me semblez convenir.$B$BUn Goblin de Ratchet, du nom de Mebok Mizzyrix, a besoin de feuilles de haricots des marais pour une potion ou je ne sais quoi... J\'avoue que je me fiche complètement de la raison, son argent est bienvenu et je connais ce marais, donc me voilà.$B$BDommage que je me sois fait encercler avant d\'avoir trouvé les haricots ! Pourriez-vous m\'aider à sortir de là et surveiller mes arrières tandis que je cherche les plantes pour Mebok ?",
    ["O"] = "Escorter Ignatz \"Puant\" puis discuter avec Mebok Mizzyrix à Ratchet.",
    ["T"] = "Sauvetage du Puant",
  },
  [1271] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Fête au Solitaire Bleu",
  },
  [1273] = {
    ["D"] = "Cela a pris du temps, mais j\'ai trouvé Reethe. Il se cache bien pour un humain.$b$bOgron se demande s’il n\'est pas devenu fou après tout ce temps passé dans le marais. Vous venez avec moi pour lui tirer les vers du nez.",
    ["O"] = "Aller avec Ogron pour parler à Reethe puis retourner voir Krog à Mur-de-Fougères.",
    ["T"] = "Questionner Reethe",
  },
  [1274] = {
    ["D"] = "Excusez-moi, $gmonsieur:madame;, l\'évêque DeLavey m\'a demandé de prendre contact avec des $gaventuriers:aventurières; qui pourraient être capables de l\'aider avec un problème délicat.$B$BSi vous pouviez vous rendre discrètement au Donjon de Stormwind pour lui parler dès que cela vous est possible, il est certain qu\'il apprécierait grandement votre secours. A nouveau, soyez $gdiscret:discrète;. Il s\'agit d\'une affaire d\'une certaine importance.",
    ["O"] = "Trouver l\'évêque DeLavey au Donjon de Stormwind.",
    ["T"] = "Un diplomate a disparu",
  },
  [1275] = {
    ["D"] = "Ces imbéciles d\'elfes de sang peinent avec la magie démoniaque. N\'ont-ils pas vu ce qui est arrivé aux nagas et aux satyres de Kalimdor ?$B$BJe crains que les elfes de sang ne rencontrent pareille déformation. Azeroth ne supporterait pas la naissance d\'une autre race de monstres infects.$B$BJe souhaite étudier les Souches de cerveaux des satyres et des nagas afin d\'en déduire la voie que prennent les elfes. Vous trouverez des satyres et des nagas dans les Profondeurs de Brassenoire, sur la côte d\'Ashenvale.$B$BC\'est un long voyage, $N.",
    ["O"] = "Gershala Nightwhisper d\'Auberdine veut 8 Souches de cerveau corrompu.",
    ["T"] = "Recherches sur la corruption",
  },
  [1276] = {
    ["D"] = "Je me souviens ! Mosarn est un forgeron respecté de Thunder Bluff. Je savais que j\'avais entendu son nom quelque part !$b$bEn remerciement, le Cercle terrestre m\'a offert un équipement complet forgé par Mosarn. Du très beau travail !$b$bPeut-être pourrait-il vous en dire plus sur ce bouclier qu\'il a forgé.$b$bCe n\'est pas une grosse piste, mais c’est la seule et c’est Reethe qui l’a donnée.",
    ["O"] = "Montrer le Bouclier en fer noirci à Mosarn à Thunder Bluff.",
    ["T"] = "Le Bouclier noir",
  },
  [1277] = {
    ["D"] = "Kagoro has revealed that the centaur hoofprints belong to some chumps in Mauradon.",
    ["O"] = "Beat down.",
    ["T"] = "<nyi> <TXT> The Centaur Hoofprints",
  },
  [1278] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<nyi> <TXT> The Grim Totem Clan",
  },
  [1279] = {
    ["D"] = "Finish one of the three.",
    ["O"] = "",
    ["T"] = "<nyi> <TXT>The Centaur Hoofprints",
  },
  [1280] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<nyi> <TXT>The Centaur Hoofprints",
  },
  [1281] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Jim\'s Song <CHANGE TO GOSSIP>",
  },
  [1282] = {
    ["D"] = "Je ne peux pas aider tant que je m\'apitoierai sur le vieux Jim là-bas. Il n\'a pas toujours été comme cela. Il a tenu une auberge en dehors des marais pendant un moment, un endroit superbe.$b$bÉcoutez, je ne veux pas vraiment en parler. Allez voir le capitaine Vimaire si vous avez besoin de quelque chose.",
    ["O"] = "Discuter avec le capitaine Garran Vimaire à Theramore.",
    ["T"] = "Ils l\'appellent Jim le Souriant",
  },
  [1283] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Fire at the Shady Rest <CHANGE TO GOSSIP>",
  },
  [1284] = {
    ["D"] = "Des lignes d\'empreintes de sabots partent des ruines encore fumantes de l\'auberge du Repos ombragé. Il y a au moins quelques traces visibles avec d\'autres empreintes peu marquées dans la boue du marais.",
    ["O"] = "Rapporter la présence des empreintes suspectes à l’auberge du Repos ombragé au capitaine Vimaire à Theramore.",
    ["T"] = "Des empreintes de sabots suspectes",
  },
  [1285] = {
    ["D"] = "Reddon… Redpath… Ah voilà ! Lieutenant Paval Reethe.$b$bA rejoint la garde de Theramore en même temps que la plupart des recrues ayant fait la traversée avec dame Proudmoore. Engagé dans les soldats de marine de Kul Tiras à seize ans. Etats de service brillants, promu... $b$bPorté disparu.",
    ["O"] = "Ramener l\'information concernant Reethe au capitaine Vimaire à Theramore.",
    ["T"] = "Les hommes de Daelin",
  },
  [1286] = {
    ["D"] = "Nous avons lutté pour maintenir notre présence dans les marais, et grâce à notre détermination, nous avons pu conserver plusieurs tours de garde défensives dans le secteur.$B$BLes dernières informations et rapports d\'éclaireurs que j\'ai reçus indiquent qu\'un groupe de déserteurs squatte une tour abandonnée à la Halte perdue, au sud-est de l\'auberge.$B$BLeur chef se nomme Jacken. Voyez si vous pouvez lui soutirer des informations.$B$BSoyez sur vos gardes, $N. Il risque d\'être réticent à collaborer.",
    ["O"] = "Rapporter l\'information de Balos Jacken sur Reethe et les déserteurs dans le marécage d\'Âprefange.",
    ["T"] = "Les déserteurs",
  },
  [1287] = {
    ["D"] = "Reethe ? Ce lâche ?$b$bNous l\'avons éjecté du camp, car il insistait pour aller piller le ravitaillement des ogres de Mur-de-Fougères. Nous ne sommes pas suffisamment nombreux pour faire face à ces brutes sans cervelle.$b$bIl a franchi trop de fois les limites, alors nous l\'avons envoyé paître.$b$bBon je vous ai aidé... Alors aidez-moi à votre tour en oubliant que vous nous avez trouvés !",
    ["O"] = "Discuter avec le capitaine Garran Vimaire à Theramore à propos de Reethe.",
    ["T"] = "Les déserteurs",
  },
  [1288] = {
    ["D"] = "Vous avez fait du bon travail, $n, le meilleur que j\'ai vu de la part de quelqu\'un qui n\'est pas de chez nous.$b$bDame Proudmoore a demandé que je la tienne informée de cette situation délicate et je ne lui ai pas parlé depuis longtemps.$b$bJe m\'apprêtais à lui envoyer mon rapport, mais il me semble plus approprié que cela soit vous qui lui parliez. Vous répondrez mieux à ses questions que moi.",
    ["O"] = "Donner le Rapport de Vimaire à Dame Proudmoore à Theramore.",
    ["T"] = "Le rapport de Vimaire",
  },
  [1289] = {
    ["D"] = "Bien, d\'après ce que je déduis du rapport du capitaine Vimaire, vous avez été d\'une aide capitale dans ces investigations, et de cela je vous remercie.$B$BJe peux avoir l\'esprit en paix sachant que vous vous occupez de ce problème. Si vous acceptez, je vous suggère de retourner voir le capitaine Vimaire et de l\'aider à résoudre l\'affaire.",
    ["O"] = "Retourner voir le capitaine Garran Vimaire à Theramore.",
    ["T"] = "<nyi> Rapport de Vimes",
  },
  [1293] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<nyi> <TXT> Centaur Hoofprints",
  },
  [1294] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<nyi> <TXT>Centaur Sympathies",
  },
  [1295] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<nyi> <TXT> Course of Action",
  },
  [1296] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<nyi> <TXT> Course of Action",
  },
  [1297] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<nyi> <TXT> Course of Action",
  },
  [1299] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<nyi> <TXT> Thrall\'s Dirty Work",
  },
  [1300] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<nyi> <TXT> Lorn Grim Totem",
  },
  [1301] = {
    ["D"] = "Avant de travailler ici, au Solitaire bleu, j\'ai été formé par le meilleur chef cuisinier et gérant d\'auberge que je connaisse. Son nom était James Hyal. Tout ce que j\'ai et tout ce que j\'ai appris, je le lui dois.$b$bJe me demande ce qu\'il devient. Son frère Vincent et lui sont allés travailler dans une auberge du Port de Menethil, mais je suis sans nouvelles depuis longtemps.$b$bSi vous passez dans le coin, donnez-lui le bonjour de ma part, d\'accord ?",
    ["O"] = "Discuter avec Vincent Hyal au port de Menethil.",
    ["T"] = "James Hyal",
  },
  [1302] = {
    ["D"] = "Mon frère James a accompagné Dame Proudmoore lorsqu\'elle a traversé les océans. Les dernières nouvelles disaient que le calme était revenu là-bas et que James avait monté une auberge quelque part sur le continent. Où qu\'il soit, je suis sûr qu\'il fait de bonnes affaires. C\'est un excellent chef cuisinier !$b$bJe suis sans nouvelles de lui depuis un bon moment. Trop de travail avec son auberge, je suppose. Mais le greffier à Theramore a peut-être plus de renseignements.",
    ["O"] = "Parler au greffier Lendry à Theramore.",
    ["T"] = "James Hyal",
  },
  [1318] = {
    ["D"] = "Maintenant qu\'z\'êtes le roi, p\'têt\' qu\'vous pourrez récupérez l\'objet précieux des Gordok que le sale elfe qui se dit Prince nous a pris. L\'roi avant vous a pas réussi, et celui d\'avant non plus, et aucun roi depuis que Tortheldrin – c\'est le sale elfe – l\'a volé à un vieux roi gordok d\'il y a longtemps !$B$BC\'est le Gantelet de puissance Gordok. Les vieux, ils disent que seul le vrai roi des Gordok pourra le ramener. Et puis, les vieux, ils disent aussi que le sale elfe est toujours là – si vous le trouvez, vous pourrez porter le gantelet de puissance pour toujours ! Ouais ! Je vais vous montrer !",
    ["O"] = "Trouvez le Gantelet de Puissance Gordok et rendez-le au capitaine Kromcrush à Hache-tripes.$B$BSelon Kromcrush, les histoires des « vieux » racontent que Tortheldrin – un « vilain » elfe qui se fait appeler prince – l\'a volé à l\'un des rois gordok.",
    ["T"] = "Affaires inachevées chez les Gordok",
  },
  [1319] = {
    ["D"] = "Je suis désolé de ne pouvoir vous en dire plus, $N, mais je sais qui peut vous aider. Caz Twosprocket, un de nos meilleurs forgerons. Portez-lui le bouclier et voyez ce qu\'il peut vous dire.",
    ["O"] = "Montrer le Bouclier en fer noirci à Caz Twosprocket à Theramore.",
    ["T"] = "Le Bouclier noir",
  },
  [1320] = {
    ["D"] = "Ah ! J\'ai failli manquer ceci… Cette lanière de cuir au dos aurait dû brûler, et elle a été faite pour un grand gabarit. Je dirais même un tauren.$b$bC\'est tout ce que je peux dire. Dites au capitaine Vimaire que je suis désolé de n\'avoir pas pu l’aider plus.",
    ["O"] = "Discuter avec le capitaine Garran Vimaire à Theramore.",
    ["T"] = "Le Bouclier noir",
  },
  [1321] = {
    ["D"] = "Hmmm… Je ne vois rien qui sorte de l\'ordinaire à propos de ce bouclier. Demandons à Do\'gol, le forgeron, de l\'examiner, puis écoutons ce qu\'il peut nous dire, mais n\'espérons pas trop…",
    ["O"] = "Montrer le Bouclier en fer noirci à Do\'gol au village de Mur-de-Fougères.",
    ["T"] = "Le Bouclier noir",
  },
  [1322] = {
    ["D"] = "Hmmm ! Il y a quelque chose d\'écrit sur le bord du bouclier, c\'est dur à lire avec toutes ces traces de suie.$b$bJ\'ai besoin d\'acide pour le faire apparaître. Du venin d\'araignée Sombrecroc des marais serait parfait. Rapportez-moi quelques glandes et je vous nettoie cela.",
    ["O"] = "Trouver 6 Sacs de Venin acide pour Do\'gol de Mur-de-Fougères.",
    ["T"] = "Le Bouclier noir",
  },
  [1323] = {
    ["D"] = "Oh ! Je crois que j\'ai mis trop d\'acide sur le bouclier. Frottons un peu pour voir…$b$bHmm, difficile à lire, mais il me semble qu\'il s\'agit d\'un nom : Mosarn. Jamais entendu parler de lui. Peut-être Krog le sait. Krog connaît tellement de monde.",
    ["O"] = "Parler à Krog au village de Mur-de-Fougères.",
    ["T"] = "Le Bouclier noir",
  },
  [1324] = {
    ["D"] = "Quoi ? Vous venez m\'accuser d\'être allié aux Défias et vous pensez que je vais rester là tranquillement alors que vous êtes en train de m\'insulter ? Quelque chose me dit que vous pensez à cela depuis longtemps.",
    ["O"] = "Battre le soldat Hendel dans le marécage d\'Âprefange.",
    ["T"] = "Un diplomate a disparu",
  },
  [1338] = {
    ["D"] = "Il y a un forgeron nain dont j\'admire beaucoup le travail. Il s\'appelle Furen Longbeard et ses compétences sont inégalables. J\'ai besoin d\'un nouveau bouclier et je veux l\'un des siens !$B$BLe problème… Furen est loin dans le sud, dans le quartier des nains de Stormwind. Chez les humains ! Je ne comprends pas pourquoi il vit là-bas, si loin d\'Ironforge. Les humains doivent lui faire un pont d’or pour qu\'il accepte de rester !$B$BSi vous avez envie de voyager, et ne pensez pas à demander une récompense, alors pourquoi ne porteriez-vous pas ma commande de bouclier à Furen ?",
    ["O"] = "Porter la commande de Stormpike à Furen Longbeard à Stormwind.",
    ["T"] = "La mission de Stormpike",
  },
  [1339] = {
    ["D"] = "J\'ai entendu dire que le montagnard Stormpike a besoin d\'un coursier. Quelqu\'un pour voyager à sa place. Qu\'en pensez-vous, $N ? Êtes-vous capable de ce travail ?$B$BSi c\'est le cas, vous trouverez Stormpike au sommet de la tour de garde la plus au nord.",
    ["O"] = "Parler au Montagnard Stormpike.",
    ["T"] = "La mission du Montagnard Stormpike",
  },
  [1358] = {
    ["D"] = "Un collègue de Kalimdor, l\'apothicaire Helbrim, étudie les effets des toxines de Lordaeron sur les animaux de Kalimdor. Il serait très intéressé par un échantillon de coeurs de loups que Renferrel m\'a envoyés.$B$BHelbrim se trouve dans les Tarides, dans un village connu sous le nom de la Croisée. Pour le rejoindre, vous voyagerez par zeppelin jusqu\'à Orgrimmar, la capitale des orcs, puis à pied vers le sud en Durotar. Allez à l\'ouest, dans les Tarides, et vous trouverez rapidement la Croisée.",
    ["O"] = "Apporter l\'échantillon de coeur de loup à l\'Apothicaire Helbrim dans les Tarides.",
    ["T"] = "Un échantillon pour Helbrim",
  },
  [1359] = {
    ["D"] = "Les Coeurs de loups que vous avez là possèdent une qualité inouïe pour ces spécimens. Ils possèdent une toxicité telle qu\'elle tuerait net les loups vivants !$B$BC\'est fascinant, il faut approfondir l\'étude. J\'ai gardé quelques échantillons de ces coeurs. Apportez-les à ma collègue, l\'Apothicaire Zinge. Vous la trouverez dans notre quartier général à Undercity.",
    ["O"] = "Apporter les Echantillons de coeurs de loups à l\'apothicaire Zinge à Undercity.",
    ["T"] = "Une livraison pour Zinge",
  },
  [1360] = {
    ["D"] = "J\'avais un bon travail de terrassier à l\'excavation d\'Uldaman dans les Terres ingrates… jusqu\'à ce que ces sacrés troggs arrivent. J\'ai à peine réussi à sauver ma peau, rien d\'autre. Dans la panique, j\'ai laissé mon plus précieux bien. C\'est dans mon coffre donc à l\'abris des troggs, mais au beau milieu d\'eux.$B$BVous auriez toute ma gratitude, en honneur et en or, si vous pouviez me le ramener. Il y a un coffre portant mon nom au bout du hall commun nord. C\'est là qu\'il faut aller.",
    ["O"] = "Récupérer les biens de Krom Stoutarm dans son coffre dans le hall commun nord d\'Uldaman et les lui rapporter à Ironforge.",
    ["T"] = "Des Trésors à recouvrer",
  },
  [1361] = {
    ["D"] = "Un guerrier de la Horde, Regthar Deathgate, recrute des agents pour une mission dans les terres de Désolace. Allez lui parler si vous souhaitez vous porter volontaire. Il se trouve dans les Tarides, dans une série de fortins situés à l\'ouest de la Croisée.$B$BEt aiguisez vos armes. Désolace est ravagée par la guerre.",
    ["O"] = "Discuter avec Regthar Deathgate.",
    ["T"] = "Regthar Seuil-de-mort",
  },
  [1362] = {
    ["D"] = "Les centaures Kolkar vivent en Désolace et leurs forces sont disséminées dans toutes les Tarides. Il est de notoriété publique que des agents de la Horde s\'occupent d\'eux.$B$BMais tant que les centaures seront puissants en Désolace, ils resteront une menace. Il est temps de nous en débarrasser.$B$BParlez à Felgur Twocuts au Poste de Rôdeur-fantôme en Désolace. Il y a été envoyé pour réunir des informations sur les centaures. Il saura comment faire au mieux avec les Kolkar de Désolace.",
    ["O"] = "Discuter avec Felgur Twocuts au poste de Rôdeur-fantôme en Désolace.",
    ["T"] = "Les Kolkar de Désolace",
  },
  [1363] = {
    ["D"] = "En tant que conservateur de la bibliothèque de l\'Académie des arts et sciences des arcanes, il est de ma responsabilité de m\'assurer que tous les livres magiques et les parchemins sont bien présents. Qui sait quelle folie pourrait survenir si l\'un de ces textes enchantés tombait entre de mauvaises mains ?$b$bCela me fait penser : mon assistant, Dellis, a fait une terrible erreur la semaine dernière. Il est parfaitement à l\'aise dans la bibliothèque, mais parfaitement incompétent dès qu\'il franchit les portes de Stormwind. Soyez un exemple de ce temps et allez aider le vieux Dellis, voulez-vous ?",
    ["O"] = "Parler à l\'acolyte Dellis à Stormwind.",
    ["T"] = "Les ordres de Mazen",
  },
  [1364] = {
    ["D"] = "Les ordres de maître Mazen sont très clairs : porter d\'importants essais au Gardien Mahar Ba au Rempart-du-Néant.$b$bMais je n\'ai jamais été dans les Terres foudroyées avant ! C’est terrifiant !$b$bDans le marais des Chagrins, j\'ai pris un mauvais chemin et j\'ai fini cerné par d\'horribles créatures à moitié végétales. Elles bougeaient comme si elles voulaient m\'attaquer, alors j\'ai fui. Mais dans ma hâte, j\'ai laissé tomber les papiers. Un des monstres les a pris, j\'en suis sûr.$b$bPourriez-vous les retrouver s\'il vous plaît et les porter à Mahar Ba ?",
    ["O"] = "Retrouver les Essais de Khadgar sur la convergence dimensionnelle et les porter au Gardien Mahar Ba au Rempart-du-Néant.",
    ["T"] = "Les ordres de Mazen",
  },
  [1365] = {
    ["D"] = "Les Kolkar ne sont pas les seuls centaures de Désolace. De nombreux clans parcourent ces plaines sèches, combattant et mourant. Il est heureux qu’ils se battent entre eux, car ils sont forts et brutaux. Si les centaures s\'unissaient contre nous, ils seraient un adversaire redoutable.$B$BNous devons nous occuper d\'eux avant qu\'ils ne s\'allient.$B$BVotre première mission est de désorganiser le clan Kolkar. Vous y parviendrez en tuant son chef, le khan Dez\'hepah.$B$BLe village des Kolkar est au nord-est. Allez et que les dieux soient avec vous !",
    ["O"] = "Rapporter la Tête du khan Dez\'hepah à Felgur Twocuts en Désolace.",
    ["T"] = "Khan Dez\'hepah",
  },
  [1366] = {
    ["D"] = "Maintenant que vous avez terrifié les clans centaures, il est temps de les saigner. Lorsque vous voyagerez à travers Désolace, chassez-les. Je me moque de savoir à quel clan ils appartiennent. Vous devez juste collectionner les oreilles. Je veux un monceau d\'oreilles !$B$BEn diminuant leur nombre vous porterez un coup à leur résolution et sonnerez le glas de leurs envies d\'expansion.$B$BBonne chasse, $N.",
    ["O"] = "Apporter 15 Oreilles de centaure à Felgur Twocuts, en Désolace.",
    ["T"] = "La prime aux centaures",
  },
  [1367] = {
    ["D"] = "Pour battre les centaures, nous allons utiliser leur haine contre eux. Nous allons nous allier à l\'un des clans, et par ce biais nous apprendrons comment battre les autres clans.$B$BSi vous décidez de vous allier aux Magram, alors tuez leurs ennemis, les Gelkis. Vous gagnerez ainsi le respect des Magram. Lorsque cela sera fait, alors allez rencontrer Warug, le porte-parole des Magram, dans les faubourgs du village des Magram au sud-est de Désolace.",
    ["O"] = "Faire en sorte que les Magram soient Amicaux envers vous, puis aller parler à Warug.",
    ["T"] = "L\'alliance avec les Magram",
  },
  [1368] = {
    ["D"] = "Pour battre les centaures, nous allons utiliser contre eux leur haine fratricide. Nous allons nous allier à l\'un des clans, et par ce biais, nous apprendrons comment vaincre les autres clans.$B$BSi vous décidez de vous allier aux Gelkis, alors tuez leurs pires ennemis, les Magram. Après avoir gagné le respect des Gelkis, allez rencontrer le chaman Uthek la Sage dans les environs du village des Gelkis, au sud-ouest de Désolace.",
    ["O"] = "Faire en sorte que les Gelkis deviennent Amicaux envers vous, puis aller parler à Uthek la Sage.",
    ["T"] = "L\'alliance avec les Gelkis",
  },
  [1369] = {
    ["D"] = "Les Gelkis pensent que Theradras, la mère des centaures, nous protège. Ils sont idiots. Personne ne protège les centaures. Les centaures n\'ont pas besoin de protection ! Les Magram n\'ont pas besoin de protection ! Nous sommes les plus forts !$B$BMontrez aux Gelkis qu\'ils sont stupides. Allez dans la caverne de leur village. Ils y possèdent des cristaux, les Larmes de Theradras. Détruisez ces cristaux et rapportez-moi les morceaux.",
    ["O"] = "Maintenir votre réputation auprès des Magram et ramener 3 Larmes brisées à Warug au village des Magram.",
    ["T"] = "Larmes brisées",
  },
  [1370] = {
    ["D"] = "Lorsque nous ne sommes pas en guerre, nous chassons pour trouver de la viande. La viande nous donne la force et des ventres remplis se battent mieux que des ventres vides.$B$BC\'est la même chose chez les Magram. Ils gardent la viande en trop afin de se battre plus longtemps entre leurs chasses. Si vous attaquez leur village par l\'est et prenez leurs réserves de viande, leur force va diminuer.",
    ["O"] = "Rapporter 6 Sacs de viande grossièrement séchée à Uthek la Sage au village des Gelkis.",
    ["T"] = "Voleur de viande",
  },
  [1371] = {
    ["D"] = "Il y a longtemps, un petit gnome est venu en Désolace. Il avait des chevaux et une carriole qui contenait des choses étranges. De drôles de choses. Des choses qui brûlaient de différentes couleurs. Des choses qui bougeaient, mais n\'étaient pas vivantes. J\'ai tué le petit gnome mais avant cela... Il m\'a fait rire.$B$BJ\'ai gardé ma chose préférée de sa carriole. C\'était un humain en bois qui me faisait signe jusqu\'à ce que je le tape et que je le casse.$B$BJ\'en veux un nouveau !",
    ["O"] = "Maintenir votre réputation auprès des Magram et rapporter un Leurre perfectionné à Warug au village des Magram en Désolace.",
    ["T"] = "Un bidule pour Warug",
  },
  [1372] = {
    ["D"] = "Notre force expéditionnaire a été envoyée sur les ordres de Varimathras pour surveiller le bas-Azeroth.$b$bPendant une reconnaissance, j\'ai remarqué un infiltrateur humain qui espionnait le Repaire des mendiants. Avant que je ne puisse l\'attraper, certains de nos... frères orcs l\'ont capturé et emmené à Stonard, leur forteresse dans le marais des Chagrins.$b$bJe veux… aider… nos alliés, les orcs. Ils n\'ont pas vu notre camp, mais malgré cela, je veux les aider.$b$bTravaillez avec Faustin pour développer… un sérum de vérité pour notre ami, à Stonard.",
    ["O"] = "Parler à l’Apothicaire Faustin, au Repaire des mendiants.",
    ["T"] = "La vérité, rien que la vérité",
  },
  [1373] = {
    ["D"] = "Vous avez encore à prouver des choses et votre prochaine mission vous emmènera loin, car maintenant les Gelkis vont combattre les Magram sans vous.$B$BLa terre chuchote bien des secrets à ceux qui savent écouter. Un des secrets que j\'ai appris est qu\'il existe un cristal rouge appelé draenéthyste, nouvellement arrivé sur notre terre. Il vient d\'un endroit où les montagnes volent et où les démons règnent.$B$BApportez-moi un éclat de draenéthyste et ma magie grandira ! Le cristal se cache dans un endroit appelé le marais des Chagrins, et appartient à un Perdu, Ongeku.",
    ["O"] = "Maintenir votre réputation auprès des Gelkis et rapporter un Éclat de draenéthyste à Uthek la Sage au village des Gelkis, en Désolace.",
    ["T"] = "Ongeku",
  },
  [1374] = {
    ["D"] = "La terre me parle et me dit que les Magram sont de plus en plus faibles. Il est maintenant temps de tuer leur khan !$B$BRapportez-moi la tête de Jehn, le khan des Magram !",
    ["O"] = "Rapporter la Tête du khan Jehn à Uthek la Sage au village des Gelkis, en Désolace.",
    ["T"] = "Khan Jehn",
  },
  [1375] = {
    ["D"] = "Vous avez aidé les Magram et je vous apprécie. Maintenant tuez le khan des Gelkis ! Il s\'appelle Shaka et je veux sa tête ! Je veux la regarder et me moquer d\'elle !",
    ["O"] = "Maintenir votre réputation parmi les Magram et rapporter la Tête du khan Shaka à Warug au village des Magram en Désolace.",
    ["T"] = "Khan Shaka",
  },
  [1380] = {
    ["D"] = "Vous nous avez aidés contre les Magram, $n. À présent, aidez-nous à battre le clan des Maraudine ! Tuez leur chef, le khan Hratha !$b$bPour le combattre, soufflez dans la corne de guerre maraudine. Elle se trouve au sommet d\'un grand promontoire, dans la Vallée des lances.$b$bMais pour souffler dans la corne, vous devez d’abord obtenir une embouchure de corne de guerre. Elles sont rares, et portées uniquement par des Maraudine de confiance.$b$bQuand vous aurez soufflé dans la corne, des centaures arriveront. Tuez-en jusqu\'à ce que Hratha apparaisse.$b$bLe khan Hratha porte un fragment de... clé. Apportez-le-moi...",
    ["O"] = "Maintenir votre réputation auprès des Gelkis, et apporter le Fragment de clé des Maraudine à Uthek la Sage au village des Gelkis.",
    ["T"] = "Khan Hratha",
  },
  [1381] = {
    ["D"] = "Le khan des Gelkis est mort, et à présent le clan Maraudine va tomber ! Tuez le khan des Maraudine, Hratha.$b$bPour le combattre, soufflez dans la Corne sur le grand promontoire de la Vallée des lances. Mais pour y souffler, vous aurez besoin d\'une Embouchure. Pour l\'obtenir, tuez des centaures Maraudine. Tuez jusqu\'à ce que vous la trouviez. Puis soufflez dans la corne.$b$bAgissez ainsi et les centaures Maraudine viendront à la tour. Tuez-en jusqu\'à ce que le khan Hratha apparaisse. Il détient un fragment de clé. Apportez-moi ce fragment...",
    ["O"] = "Maintenir votre réputation auprès des Magram, et apporter le Fragment de clé des Maraudine à Warug au village des Magram en Désolace.",
    ["T"] = "Khan Hratha",
  },
  [1382] = {
    ["D"] = "Nous avons de la chance que les clans de centaures se battent entre eux, car s\'ils unissaient leurs forces, rien ne pourrait les arrêter. Oui, nous sommes chanceux, et nous devrions mettre à profit cet avantage.$B$BL\'un des clans de centaures, le clan Gelkis, mène une guerre acharnée au clan Magram. Si nous attaquons ce second clan, alors nous pourrions peut-être conclure une alliance avec les Gelkis.$B$BAllez parler à Uthek la Sage, aux approches des campements gelkis, au sud-ouest de Désolace. Grâce à elle, vous pourrez peut-être conclure une alliance avec le clan Gelkis.",
    ["O"] = "Obtenir que les Gelkis se montrent Amicaux, puis parler à Uthek la Sage.",
    ["T"] = "Une étrange alliance",
  },
  [1383] = {
    ["D"] = "J\'ai tout simplement en tête le bon sérum. Il s\'occupera de la vérité exactement comme il convient. Pour cette préparation, je vais avoir besoin de beaucoup de cœurs de panthères des ombres, en provenance du marais. J\'aurai également besoin d\'un champignon de boueux enchanté, de la réserve personnelle du seigneur du bourbier local. Il est certain que quelqu\'un d\'aussi talentueux que vous, $n, peut le persuader de s\'en séparer.$b$bLe plus difficile sera de trouver une tumeur d\'Arpentefonds dans la lointaine Désolace. Il est très rare que les géants de là-bas tombent malades et forment des tumeurs.$b$bAllez-y à présent !",
    ["O"] = "L\'apothicaire Faustin, du repaire des mendiants, a besoin de 5 Cœurs de panthère des ombres, d\'un Champignon de Boueux et d\'une Tumeur d\'Arpentefonds.",
    ["T"] = "La vérité, rien que la vérité",
  },
  [1384] = {
    ["D"] = "Les Kolkar estiment que leur magie est puissante. Ils ne connaissent rien à la magie ! Ils ne reconnaîtraient même pas la magie si elle les frappait !$b$bVous allez leur montrer que leur magie est faible. Allez chez les centaures Kolkar au nord-est, et tuez-les. Détruisez leurs camps. Apportez-moi leurs charmes, que je les jette au feu et que je rie !",
    ["O"] = "Maintenir votre réputation auprès des Gelkis, et apporter 10 Charmes bruts à Uthek la Sage dans le village des Gelkis, en Désolace.",
    ["T"] = "Raid sur les Kolkar",
  },
  [1385] = {
    ["D"] = "Désolace est une terre en guerre. Les centaures se font la guerre les uns les autres. Nous pensons que les vainqueurs vont surgir et menacer nos forteresses, ce que nous ne pouvons permettre.$b$bNous devons apprendre comment battre les centaures, et pour cela nous devons nous allier à l\'un des clans. Les centaures Magram sont en guerre contre les Gelkis. Si vous combattez les centaures Gelkis, alors les Magram pourraient envisager une alliance.$b$bGagnez les faveurs des Magram, puis parlez à Warug, au village des Magram, au sud-est de Désolace.",
    ["O"] = "Faire en sorte que les Magram soient Amicaux à votre égard, puis aller parler à Warug.",
    ["T"] = "Une violente politique",
  },
  [1386] = {
    ["D"] = "Les centaures Kolkar pensent qu\'ils sont forts, mais nous rions de leur force ! Nous rions d\'eux et nous les tuons ! Vous les tuez aussi. Montrez-moi que vous pouvez tuer des centaures Kolkar comme nous les tuons. Allez vers le nord et massacrez-les tous !",
    ["O"] = "Maintenir votre réputation auprès des Magram, tuer 12 Centaures kolkar, 12 Eclaireurs kolkar, 6 Marteleurs kolkar, puis retourner voir Warug, dans le village des Magram, en Désolace.",
    ["T"] = "Assaut sur les Kolkar",
  },
  [1387] = {
    ["D"] = "Le peuple des centaures est divisé en nombreux clans. Chaque clan a son chef et ses propres buts, mais ils ont tous une « qualité » en commun : la cruauté.$b$bSi nous voulons avoir une chance de construire la paix, alors les centaures doivent être éradiqués. C\'est exactement la tâche que je vous confie.$b$bPendant que vous serez en Désolace, chassez des centaures et prenez leurs oreilles. Apportez-moi de nombreuses, très nombreuses oreilles.$b$bŒuvrez au service de l\'Alliance, $n, et nous nous rapprocherons de la fin de la guerre.",
    ["O"] = "Apporter 15 Oreilles de centaures au caporal Melkins, à la combe de Nijel, en Désolace.",
    ["T"] = "La prime aux centaures",
  },
  [1388] = {
    ["D"] = "Tout se déroule selon le plan, $n. Je veux que vous remettiez ce sérum de vérité au traqueur noir Zraedus et il vous montrera comment l\'administrer… proprement.$b$bHa ! Ha !",
    ["O"] = "Remettre le Sérum de vérité de Faustin au traqueur noir Zraedus au Repaire des mendiants.",
    ["T"] = "La vérité, rien que la vérité",
  },
  [1389] = {
    ["D"] = "Les Perdus qui vivent dans le Sanctuaire des friches nous ressemblent physiquement, mais ce sont des monstres… rendus fous par les rigueurs de notre voyage vers Azeroth. Nos anciens frères attrapent et tuent quiconque s’approche du Sanctuaire des friches, à l\'est.$b$bEt ils nous ont volé quelque chose de sacré à nos yeux : des cristaux de draenéthyste.$b$bNous n\'avons pas la force suffisante pour pénétrer dans le Sanctuaire des friches et reprendre nos draenéthystes volées dans leurs huttes et près des feux de camp. Mais sans ces cristaux... nous pourrions également perdre la voie, tout comme eux.",
    ["O"] = "Apporter 6 Cristaux de draenéthyste à Magtoor au Havre boueux du marais des Chagrins.",
    ["T"] = "Les cristaux de draenéthyste",
  },
  [1390] = {
    ["D"] = "Missing text",
    ["O"] = "Missing text",
    ["T"] = "Oops, We Killed Them Again.",
  },
  [1391] = {
    ["D"] = "Nous devons procéder avec une certaine discrétion. Après tout, ce n\'est pas comme si vous alliez danser la valse là-bas et convaincre un imbécile de boire la préparation de Faustin, pas vrai ?$b$bMais il y a une chose à laquelle les humains ne savent pas dire non, c\'est l\'alcool. Je prendrai le sérum de Faustin et le verserai dans de la bière. Je peux garantir que le pauvre type n’hésitera pas à la siffler d\'un trait.$b$bHâtez-vous vers Stonard, dans le marais des Chagrins, $n.$b$bEt faites en sorte que notre petite mission reste secrète et sans accroc...",
    ["O"] = "Apporter la Bière de Zraedus à l\'Infiltrateur Marksen à Stonard.",
    ["T"] = "La vérité, rien que la vérité",
  },
  [1392] = {
    ["D"] = "Noboru a harcelé les draeneï du Havre boueux pendant des mois, et son gourdin est le symbole de la brutalité qu’ils ont subi de la part de ceux qu\'ils ont perdus.",
    ["O"] = "Apporter le Gourdin de Noboru au Chef du Havre boueux dans le marais des Chagrins.",
    ["T"] = "Noboru le Gourdin",
  },
  [1393] = {
    ["D"] = "Au secours ! Ces créatures vont me manger !$b$bSortez-moi de là, et je vous donnerai un grand trésor ! Il est dans mon camp, dans le marais au nord de Stonard, mais il est enfermé dans un coffre qui ne peut être ouvert que grâce à un truc que moi seul connais.$b$bSi vous m\'aidez à m\'en sortir, alors je vous l’enseignerai.",
    ["O"] = "Escorter Galen jusqu\'à ce qu\'il soit hors de danger, puis trouver son camp dans le marais des Chagrins et ouvrir son coffre.",
    ["T"] = "L\'évasion de Galen",
  },
  [1394] = {
    ["D"] = "Vous avez bien œuvré, $N. Vous avez réussi mon test, et les tests d’avant. Retournez voir Dom aux Mille pointes. Il ressentira le changement qui s\'est opéré en vous, et constatera que votre cerveau, votre corps et votre esprit sont suffisamment forts pour que vous soyez $grécompensé:récompensée; de vos efforts.$B$BRestez fidèle à l\'équilibre que vous avez montré durant ces épreuves, $N. Dorn vous testera à nouveau dans l\'avenir si vous le faites.",
    ["O"] = "Parler au traqueur des plaines Dorn aux Mille pointes.",
    ["T"] = "Le dernier voyage",
  },
  [1395] = {
    ["D"] = "Cette caisse de pierres de puissance doit être livrée au Rempart-du-Néant, dans les Terres foudroyées. Elles ne vont pas conserver leur pouvoir longtemps, aussi nous ne pouvons pas les envoyer par la prochaine caravane.$b$bVous devez les emporter, et vous devez les emporter rapidement.$b$bApportez-les à l\'intendant Lungertz au Rempart-du-Néant. Pour atteindre le donjon, vous devez voyager vers l\'est, à travers le défilé de Deuillevent, puis à l\'est à travers le marais des Chagrins, et enfin vers le sud à travers les Terres foudroyées.$b$bFaites attention, $n. Vous allez traverser des endroits dangereux, mais vous n\'avez pas le droit d\'échouer.",
    ["O"] = "Apporter la Caisse de Pierres de puissance à l\'Intendant Lungertz, avant que le pouvoir des pierres n\'expire.",
    ["T"] = "Fournitures pour Rempart-du-Néant",
  },
  [1396] = {
    ["D"] = "Ma caravane avait pour destination la côte est. Mais nous ne l\'avons pas atteinte. Les Perdus nous ont attaqués, tuant les gardes et volant des marchandises.$B$BMoi seul ai survécu à l\'assaut. J\'ai juré de protéger les marchandises restantes, mais des créatures sauvages rôdent dans les alentours. Je vous en prie, aidez-moi à défendre mon campement contre ces bêtes !",
    ["O"] = "Tuer 8 Jeunes crocilisques Dents-de-scie, 10 Tisseuses des pleurs et 10 Jaguars du marécage, puis retourner voir le Gardien Biggs, dans le marais des Chagrins.",
    ["T"] = "Des créatures sauvages rôdent",
  },
  [1398] = {
    ["D"] = "Je vous dois beaucoup, $N. Un cœur noble bat en vous. J\'ai à présent une autre tâche pour laquelle je demanderai humblement votre aide.$b$bSi je veux avoir la moindre chance d\'atteindre Rempart-du-Néant, alors je dois réparer mon chariot. Et pour cela, j\'ai besoin de bois. Du bois fort, du bois sec, pas de ce bois spongieux et noueux des arbres du marais.$b$b$N, partez à l\'est pour la côte et cherchez du bois qui a séché au soleil. Et apportez-le-moi.",
    ["O"] = "Apporter 8 Morceaux de Bois séché au soleil au Gardien Biggs dans le marais des Chagrins.",
    ["T"] = "Bois flotté",
  },
  [1418] = {
    ["D"] = "Les Terres ingrates sont une région difficile, en bordure du Loch Modan, un territoire aux mains de l\'Alliance. Aussi, la liaison entre Stonard et Kargath, l\'avant-poste des Terres ingrates, est-elle souvent interrompue.$b$bMontrez votre valeur, $n. Partez pour les Terres ingrates et parlez à Neeka Bloodscar, Eclaireur principal de l\'avant-poste de Kargath. Parlez-lui, puis envoyez-moi son rapport.",
    ["O"] = "Parler à Neeka Bloodscar.",
    ["T"] = "Neeka Balafre-sanglante",
  },
  [1419] = {
    ["D"] = "Avez-vous passé beaucoup de temps dans les Terres ingrates, $c ? Avez-vous ressenti la chaleur ? Avez-vous affronté les bêtes qui rôdent dans les plaines poussiéreuses ? Il est temps que vous le fassiez.$b$bLes coyotes des rochers sont une espèce robuste. Robuste, et suffisamment intrépide pour s\'aventurer jusqu\'à Kargath et chaparder de la nourriture. Aidez-nous à débarrasser l\'avant-poste de ces pique-assiette !$b$bChassez les coyotes des rochers, $n. Chassez-les et apportez-moi leurs mâchoires. Apportez-en assez, et peut-être que je vous croirai lorsque vous dites que vous connaissez les Terres ingrates.",
    ["O"] = "Apporter 30 Mâchoires de coyotes à Neeka Bloodmark à Kargath.",
    ["T"] = "Les coyotes voleurs",
  },
  [1420] = {
    ["D"] = "J\'ai écrit un rapport tout frais pour Helgrum à Stonard. Remettez-le-lui.$b$bEt si vous vous considérez comme une bonne estafette, alors ne vous perdez pas. Et ne tombez pas aux mains de l’ennemi.",
    ["O"] = "Apporter le Rapport de Neeka à Helgrum l\'Agile à Stonard.",
    ["T"] = "Le rapport à Helgrum",
  },
  [1421] = {
    ["D"] = "J\'ai perdu connaissance durant l\'attaque des Perdus, mais avant que tout ne s\'obscurcisse, j\'ai vu l\'un de nos chariots en train d\'être traîné vers le Sanctuaire des friches, le village des Perdus, au nord.$B$BUn coffre plein d\'ingrédients de valeur se trouvait dans ce chariot. Ces ingrédients sont d\'un intérêt scientifique vital pour les sorciers du Rempart-du-Néant. Retrouvez ce coffre et mettez la main sur ces ingrédients, $N. Une fois que vous les aurez en votre possession, rapportez-les-moi.",
    ["O"] = "Trouver le Coffre de la Caravane, dans le Sanctuaire des friches, obtenir les Composants pour les sorciers, et les apporter au Gardien Biggs.",
    ["T"] = "Le chariot perdu",
  },
  [1422] = {
    ["D"] = "Pendant que je commence à travailler sur votre arme, pourquoi n\'iriez-vous pas parler à Katar ? Il a mentionné qu\'il aurait besoin d\'aide pour patrouiller le long de la plage, et je vous imagine mieux gardant vos esprits et vos armes en alerte que vous saoulant en compagnie des autres poux avec qui je partage ce camp.",
    ["O"] = "Parler à Katar, dans le marais des Chagrin.",
    ["T"] = "Le danger venu des flots",
  },
  [1423] = {
    ["D"] = "Ce tonneau, bien que petit, est robuste. Il est scellé avec une cire jaune à l’odeur âcre. On peut lire, tamponné au cul du tonneau :$b$bINTENDANT LUNGERTZ$b$bREMPART-DU-NEANT.",
    ["O"] = "Apporter les Marchandises perdues à l\'Intendant Lungertz.",
    ["T"] = "Les marchandises perdues",
  },
  [1424] = {
    ["D"] = "Le chef de guerre nous a envoyés enquêter sur des événements étranges survenus au temple d\'Atal\'Hakkar.$b$bLes trolls Atal\'ai sont une tribu d\'ensorceleurs noirs connus pour utiliser des magies interdites. Vous pouvez imaginer notre étonnement lorsque nous sommes arrivés pour découvrir que le temple avait été englouti.$b$bÀ présent, les dragons verts gardent le site, et ils se disent protecteurs d\'Azeroth.$b$bNous devons découvrir ce qui s\'est passé. Explorez les lieux autour du Bassin des larmes et rassemblez des artefacts atal\'ai. Ils me permettront de reconstituer une partie du puzzle.",
    ["O"] = "Fel’zerul à Stonard veut que vous rassembliez 10 Artefacts atal\'ai.",
    ["T"] = "Le bassin des larmes",
  },
  [1425] = {
    ["D"] = "Pendant que je répare ce chariot, $n, j\'ai une dernière tâche à vous confier.$b$bJ\'ai empaqueté les marchandises les plus importantes rescapées des attaques des ogres. Apportez-les au Rempart-du-Néant. Le donjon est blotti dans les collines au nord des Terres foudroyées, pour surveiller la Porte des ténèbres.$b$bPour atteindre Rempart-du-Néant, suivez la route est. A l\'embranchement, allez vers le sud dans les Terres foudroyées, puis tournez à gauche au second. Continuez à suivre la route, $n, et soyez $gprudent:prudente;.",
    ["O"] = "Apporter la Cargaison pour Rempart-du-Néant à l\'Intendant Lungertz au Rempart-du-Néant.",
    ["T"] = "La remise du chargement",
  },
  [1426] = {
    ["D"] = "Je n\'ai aucun amour pour la mer, ni pour aucune des créatures que j\'ai vu sortir de ses profondeurs. C\'est une maîtresse puissante, mais aveugle. Insensible et brutale. Cela m\'apporterait un grand plaisir de massacrer les hommes-poissons par centaines si c\'était en mon pouvoir. Mais je ressens également du plaisir à les regarder mourir de la main de quiconque obéit à la volonté de la Horde.$b$bCe qui nous amène à vous, $c. Combien pouvez-vous en massacrer ? Voudriez-vous vous mesurer à leur plus puissante espèce ?",
    ["O"] = "Tuer 10 Murlocs des marais, 10 Cracheurs des marais, 10 Dévoreurs des marais, puis retourner voir Katar dans le marais des Chagrins.",
    ["T"] = "Le danger venu des flots",
  },
  [1427] = {
    ["D"] = "Avant que vous ne vous reposiez, parlez à nouveau à Tok\'Kar. Pendant que vous étiez $gparti:partie;, il a terminé quelques armes. Pour récompenser l\'aide que vous nous avez apportée, il devrait vous en offrir une. Vous devriez tester sa force contre les hommes-poissons quand vous vous sentirez $gprêt:prête;... mais seulement pour me faire plaisir.",
    ["O"] = "Parler à Tok\'Kar dans le marais des Chagrins.",
    ["T"] = "Le danger venu des flots",
  },
  [1428] = {
    ["D"] = "Il me plaît de voir que votre haine pour les hommes-poissons grandit en même temps que la mienne.$b$bTrouvez-en d\'autres, le long de la plage, et lorsque vous serez $gfatigué:fatiguée; de vous battre, revenez me voir. Nous parlerons alors d\'une récompense pour vos efforts assidus.",
    ["O"] = "Tuer 10 Cracheurs des marais, 10 Dévoreurs des marais, 10 Oracles des marais, puis retourner voir Katar dans le marais des Chagrins.",
    ["T"] = "Le danger permanent",
  },
  [1429] = {
    ["D"] = "Tandis que vous braviez les dangers du Bassin des larmes, l\'un de mes éclaireurs a trouvé un vélin en peau séché, avec un texte écrit avec du sang. Cela semble être une sentence prononcée à l’encontre d’un prêtre de la tribu Atal\'ai.$b$bLe prêtre a été condamné à un sort pire que la mort, pour un Atal\'ai.$b$bIl a été exilé, obligé de vivre loin au nord, dans les Hinterlands, avec les trolls Witherbark.$b$bJe vais faire un paquet de ces artefacts. Vous allez trouver l\'exilé et lui demander d’identifier ces objets. Revenez me voir lorsque vous aurez achevé votre voyage.",
    ["O"] = "Apporter les Artefacts atal\'ai à l\'Exilé atal\'ai dans les Hinterlands.",
    ["T"] = "L\'Exilé atal\'ai",
  },
  [1430] = {
    ["D"] = "Le goût succulent de la chair de clampant est très prisé parmi mes clients. Et particulièrement celui des clampants monstrueux.$B$BSi vous comptez vous rendre sur la côte prochainement, je peux rentabiliser votre voyage : rapportez-moi quelques-unes de leurs pattes et je les expédierai à Orgrimmar.$B$BLa côte se trouve à l\'est, et vous y trouverez facilement ces créatures, détalant de-ci de-là le long des plages et dans la mer.",
    ["O"] = "Apporter 10 Pattes de clampants monstrueux à Dar dans le marais des Chagrins.",
    ["T"] = "Viande fraîche",
  },
  [1431] = {
    ["D"] = "Tenez-vous tranquille, $c, et permettez-moi de partager avec vous quelque chose d\'important pour notre peuple, mais loin d\'être évident.$b$bNous sommes une race fière, et la guerre contre les humains a mis à rude épreuve même la grande patience de Thrall. Vous voyez, notre Chef de guerre est sage et prudent, et il continue à placer son peuple en premier. Il est, en fin de compte, un diplomate, et le temps qu\'il a passé avec les humains lui a appris leur façon d\'être.$b$bSi l\'histoire que je viens de commencer éveille votre intérêt, alors trouvez Keldran près de la sortie ouest de la ville. Sa masure s\'y trouve.",
    ["O"] = "Parler à Keldran à Orgrimmar.",
    ["T"] = "Les relations de l\'Alliance",
  },
  [1432] = {
    ["D"] = "Tandis que l\'Alliance progresse plus profondément en Kalimdor, des conclusions hâtives pourraient être faites à propos d\'hypothétiques accords entre la Horde et la Légion ardente. Le Chef de guerre ne peut le permettre. Et avec sa bénédiction, je prends le contrôle pour superviser leur anéantissement.$b$bJ\'ai des agents dans le secteur de Désolace, au Poste de Rôdeur-fantôme, au nord du grand Cimetière des kodos. Allez au sud du Val Calciné, dans les Serres-Rocheuses, vous devriez tomber dessus. Une fois sur place, cherchez Takata Steelblade.",
    ["O"] = "Parler à Takata Steelblade en Désolace.",
    ["T"] = "Les relations de l\'Alliance",
  },
  [1433] = {
    ["D"] = "Les sectateurs de la Lame ardente élaborent de nombreux plans, mais sachez qu\'ils sont foncièrement maléfiques, que leur esprit est corrompu, et que la destruction est le seul moyen de mettre un terme à leurs objectifs.$b$bKeldran a envoyé Maurin avec moi car il connaît la Lame et les méthodes des démonistes.$b$bIl supervise nos actions contre la Lame, tandis que je m\'occupe des autres dangers démoniaques ici en Désolace.$b$bParlez-lui, et il vous expliquera ses projets.",
    ["O"] = "Parler à Maurin Bonesplitter en Désolace.",
    ["T"] = "Les relations de l\'Alliance",
  },
  [1434] = {
    ["D"] = "Une infestation démoniaque n\'arrive pas sans une aide extérieure. Quelque chose permet au Mal de prendre pied.$b$bCela peut être un esprit affaibli, tenaillé par un désir vain de pouvoir. Peut-être est-ce une soif de vengeance, ou l\'appel de la justice finale. Lorsque nous sommes assaillis par la douleur, le démon devient plus fort que nous. Un démon tel que les satyres.$b$bDes liens ont été établis entre les satyres et la Lame ardente, et Thrall souhaiterait les voir massacrés. Cherchez-les au nord-est d\'ici, et revenez me voir lorsque vous les aurez vaincus.",
    ["O"] = "Massacrer 7 Voleurs Fielfurie, 7 Liges de la corruption Fielfurie, 7 Traîtres Fielfurie et 7 Implorateurs de l’enfer Fielfurie, puis retourner voir Takata Steelblade en Désolace.",
    ["T"] = "Souillé par les satyres",
  },
  [1435] = {
    ["D"] = "La mort est très désirable pour ces dégoûtantes créatures qui quittent les ordres de leur seigneur pour des raisons si égoïstes. Mais la mort n\'est pas suffisante quand on a affaire à ceux qui se sont donnés aux ténèbres.$b$bPrenez cette gemme. Lorsque un membre de la Lame ardente approchera de la mort, activez sa magie. Le sort attirera l\'esprit corrompu de sa victime, laisssant une gemme ardente canalisée.$b$bApportez-moi ces gemmes ardentes, que je m\'en occupe comme il convient.$b$bPartez à présent, et puissiez-vous échapper à la mort.",
    ["O"] = "Apporter une Gemme ardente et 15 Gemmes ardente canalisées à Maurin Bonesplitter en Désolace.",
    ["T"] = "Esprits consumés",
  },
  [1436] = {
    ["D"] = "La lutte se déroule bien, $N. Peut-être que le Chef de guerre nous permettra de rentrer dans nos foyers avant la fin de la saison si nous continuons à frapper de si grands coups dans notre bataille contre les démons et leurs servants.$B$BRetournez voir Keldran et faites-lui savoir ce que nous avons accompli. Il est certain qu\'il récompensera vos efforts ici. Sa cause est importante à ses yeux, et il sera fier de savoir que le Chef de guerre la respecte.",
    ["O"] = "Parler à Keldran à Orgrimmar.",
    ["T"] = "Les relations de l\'Alliance",
  },
  [1437] = {
    ["D"] = "Je me nomme Vahlarriel, $n. Je suis ici pour retrouver deux nobles disparus, issus d\'une famille marchande de Darnassus : la famille Malem. Le père craint le pire, et je dois tout tenter pour retrouver ses enfants disparus. J\'ai commencé mes recherches pour retrouver Tyranis ou Dalinda, mais j\'ai bon espoir que je ne serai pas ici longtemps.$b$bAux dernières nouvelles, ils passaient par les Serres-Rocheuses il y a quelques semaines.$b$bSi vous trouvez leur chariot, s\'il vous plaît, veuillez revenir ici pour m\'en informer.",
    ["O"] = "Rechercher le chariot de Tyranis et de Dalinda Malem en Désolace.",
    ["T"] = "La recherche de Vahlarriel",
  },
  [1438] = {
    ["D"] = "Trouvez Dalinda et Tyranis si vous le pouvez, et revenez m\'informer de vos progrès. Commencez vos recherches dans cette forteresse. Je vais immédiatement contacter mes supérieurs à Darnassus et les informer de la situation.$b$b$n, il y a quelque chose que je souhaite vous dire. Mes supérieurs ont montré quelque inquiétude à propos de Tyranis Malem et de ses raisons de venir ici. Le plus important pour eux est que Dalinda Malem soit retrouvée et ramenée en bonne santé. Nous devons... jauger Tyranis avant qu\'il ne rentre chez lui.",
    ["O"] = "Trouver Dalinda Malem en Désolace.",
    ["T"] = "La recherche de Vahlarriel",
  },
  [1439] = {
    ["D"] = "Nous avons été emmenés ici après le massacre de nos gardes et de nos serviteurs par les orcs. Oh ! Ils leur ont fait des choses si horribles ! J\'ai cru qu\'ils allaient également nous tuer, sur place, mais ils avaient visiblement d\'autres plans.$b$bTyranis a essayé de leur parler. Je pense qu\'il voulait les impressionner par sa connaissance des arcanes... Je ne suis pas sûre de savoir pourquoi.$b$bJe vous en prie, je ne peux pas quitter les lieux tant que j\'ignore ce qu\'il est advenu de mon frère. Retrouvez-le, et venez me dire quel a été son sort.",
    ["O"] = "Trouver Tyranis Malem en Désolace.",
    ["T"] = "À la recherche de Tyranis",
  },
  [1440] = {
    ["D"] = "Ainsi, le temps est venu ? J\'apprécie vos efforts, et si la situation ne se résout pas, alors permettez-moi de vous remercier dès à présent pour tout ce que vous avez fait pour moi et ma famille. Si vous êtes $gprêt:prête;, rien ne me retient ici. Nous pouvons partir quand vous estimerez le bon moment arrivé.",
    ["O"] = "Escorter Dalinda Malem depuis la forteresse de Hache-Tonnerre et retourner voir Vahlarriel Demonslayer, en Désolace.",
    ["T"] = "Le retour vers Vahlarriel",
  },
  [1441] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [1442] = {
    ["D"] = "Salutations, $n. Que puis-je faire pour quelqu\'un qui suit la voie sacrée ?$b$bAh oui ! Stilwell, le forgeron… d\'il y a longtemps. Je me souviens bien de lui, mais cela fait quelque temps que je n\'ai pas entendu parler de lui.$b$bIl ne m\'a jamais paru impudent, aussi je ne peux que supposer que son désir d\'une gemme de Kor est noble.$b$bSi vous souhaitez réellement l\'une de ces gemmes, alors vous devez vous diriger vers le nord-ouest d\'Ashenvale. Vous y trouverez les Profondeurs de Brassenoire à l\'intérieur de ruines anciennes. Les nagas qui utilisent toujours ces gemmes se trouvent en bas. Ne partez pas $gseul:seule;, $c.",
    ["O"] = "Apporter une Gemme de Kor à Thundris Windweaver à Sombrivage.",
    ["T"] = "À la recherche de la gemme de Kor",
  },
  [1444] = {
    ["D"] = "Jammal\'an le Prophète m\'a condamné à être exilé du temple d\'Atal\'Hakkar. Je considérais autrefois que le grand prêtre était un chef digne.$b$bSa foi inflexible dans sa prophétie apportera la destruction aux Atal\'ai. Si ce n\'est pas déjà fait.$b$bLorsque j\'ai exprimé des réserves, j\'ai été traité d’infidèle et évincé du temple.$b$bLes artefacts que vous avez apportés sont inoffensifs. Mais si Jammal\'an a découvert le bon fétiche d\'enchantement pour le rituel d\'invocation, alors le monde court un grand péril...",
    ["O"] = "Retourner voir Fel’Zerul à Stonard.",
    ["T"] = "De retour vers Fel’Zerul",
  },
  [1445] = {
    ["D"] = "Ainsi il a parlé de la prophétie de Jammal\'an ?$b$bLe chef spirituel des Atal\'ai croit qu\'une fois Hakkar revenu du Néant pour Azeroth, le dieu accordera l\'immortalité à sa tribu.$b$bUne excuse idiote pour provoquer une fin du monde prématurée, si vous voulez mon avis.$b$bMais vous dites que l\'Exilé a parlé de fétiches enchantés ? Cela m\'inquiète profondément. S\'ils constituent la clé du rituel atal\'ai et qu\'ils sont à l\'origine de ce chaos, nous devons comprendre leurs pouvoirs intrinsèques.$b$bAventurez-vous dans le temple, et emparez-vous des fétiches.",
    ["O"] = "Rassembler 20 Fétiches d\'Hakkar et les apporter à Fel’Zerul à Stonard.",
    ["T"] = "Le temple d\'Atal\'Hakkar",
  },
  [1446] = {
    ["D"] = "Mes compagnons Witherbark n\'apprécient pas beaucoup les étrangers qui rôdent près de chez eux.$b$bMoi ? Je n\'ai plus de maison. J\'ai été exilé par mon peuple, la grande tribu Atal\'ai du marais des Chagrins.$b$bNotre chef spirituel, Jammal\'an, avait ce qu\'il appelait une prophétie. Il pense que l\'invocation du dieu Hakkar apportera l\'immortalité à la tribu Atal\'ai.$b$bMais j\'ai prôné la prudence. Et si cette prophétie n\'était rien d\'autre que de la manipulation ?$b$bJe veux une revanche pour mon exil. Apportez-moi la tête de Jammal\'an. Peut-être qu’ensuite mon peuple retrouvera son libre arbitre.",
    ["O"] = "L\'Exilé atal\'ai des Hinterlands veut la Tête de Jammal\'an.",
    ["T"] = "Jammal\'an le Prophète",
  },
  [1447] = {
    ["D"] = " Ouais ! De toute évidence, vous n\'êtes pas le $c le plus malin à venir à Stormwind, mais vous êtes sur le point d\'être l\'un des plus stupides à n\'en jamais repartir.$b$bJe vous ai présenté mes amis ?",
    ["O"] = "Battre Dashel Stonefist à Stormwind.",
    ["T"] = "Un diplomate a disparu",
  },
  [1448] = {
    ["D"] = "Fut un temps, j\'étais jeune et plein de vigueur comme vous, $c.$b$bMais à présent je suis vieux et incapable d\'explorer le monde comme je le faisais autrefois. Ah, les choses que j\'ai vues !$b$bMais il reste un mystère qui m\'a toujours échappé. Pour ma dernière quête, la Ligue des Explorateurs m\'a envoyé chercher le temple d\'Atal\'Hakkar. La rumeur veut qu\'il se trouve dans le marais des Chagrins.$b$bEn raison de mon état fragile, j\'ai parcouru les cieux à dos de gryphon, mais je ne l\'ai jamais trouvé.$b$bPourriez-vous venir en aide à un vieux nain ? Peut-être aurez-vous plus de chance à pied.",
    ["O"] = "Brohann Caskbelly à Stormwind veut que vous recherchiez le temple d\'Atal\'Hakkar.",
    ["T"] = "À la recherche du temple",
  },
  [1449] = {
    ["D"] = "Comme j\'en ai déjà parlé plus tôt, j\'ai moi-même tenté une reconnaissance du temple depuis les airs. Mais en fait, je n\'étais pas exactement seul.$b$bJ\'ai voyagé sur un gryphon de la fameuse volière du Nid-de-l\'Aigle dans les Hinterlands. Là, j’avais acheté les services d’un de leurs meilleurs pilotes. Mais il est tombé de sa monture, et directement dans le marais...$b$bMon vieil âge m\'empêche de me souvenir du nom de ce pauvre type. Le maître de la volière devrait s\'en souvenir, très certainement.$b$bPartez-y et cherchez Falstad Wildhammer.$b$bNous devons découvrir ce qui est arrivé à ce pilote.",
    ["O"] = "Parler au haut-thane Falstad Wildhammer, au Nid-de-l\'Aigle des Hinterlands.",
    ["T"] = "Vers les Hinterlands",
  },
  [1450] = {
    ["D"] = "Ainsi, vous êtes un des fameux coursiers au service des vieux schnocks de la Ligue des Explorateurs, hein ?$b$bBien, écoutez-moi, $c. Je ne sais pas avec qui votre vieux pote faisait des ronds dans le ciel d’Azeroth. Est-ce que j\'ai la tête du Maître des gryphons Talonaxe ? Hein ?$b$bSi vous êtes tellement $gintéressé:intéressée; par les aléas du trafic aérien pourquoi n\'allez-vous pas lui parler directement ? Si je vous prends à glandouiller dans le coin un peu plus longtemps, je vais vraiment vous faire transpirer.$b$bÀ présent, bougez de là !",
    ["O"] = "Parler au Maître des gryphons Talonaxe dans la Volière de Nid-de-l\'Aigle",
    ["T"] = "Le Maître des gryphons Talonaxe",
  },
  [1451] = {
    ["D"] = "Rhapsody commençait vraiment à apprécier le goulot. Mais ne vous méprenez pas : tous les nains ici à la volière savent apprécier une bonne bière quand le bon moment arrive.$b$bMais le vieux Shindigger s\'est habitué à la boisson comme le jour s\'est habitué à la nuit. Quel dommage ! Personne ne savait voler comme lui.$b$bLes premiers signes du problème sont apparus quand il est revenu en titubant de cette mission avec votre pote, Brohann. Tout s\'est écroulé peu de temps après. Nous avons dû le laisser partir.$b$bLa dernière fois que j\'ai entendu parler de lui, il avait monté un camp de brassage à l\'est d\'ici...",
    ["O"] = "Chercher Rhapsody Shindigger dans les Hinterlands.",
    ["T"] = "Rhapsody Croquejarret",
  },
  [1452] = {
    ["D"] = "Hic !$b$bAlors c\'est ce vieux fou de Brohann qui vous envoie ? Bon sang ! On a eu une de ces aventures… Hic !$b$bSi vous voulez que je vous raconte ce qui s\'est passé au marais, je vais avoir besoin de quelque chose de costaud à boire. C\'était pas beau à voir, vous comprenez ? Hic !$b$bJe vais vous dire... Apportez-moi de quoi concocter un Kocktail Kalimdor et je vous en parlerai.$b$bJe vais avoir besoin de gésiers de Roc de Tanaris... et voyons... de foies de Groddoc et de Ferpoil de Feralas...$b$bUn bon Kocktail Kalimdor bien costaud... ça m\'f\'ra causer !",
    ["O"] = "Rhapsody Shindigger dans les Hinterlands veut que vous lui rameniez 3 Gésiers de roc, 3 Foies de Groddoc et 3 Foies de Ferpoil.",
    ["T"] = "Le Kocktail Kalimdor de Rhapsody",
  },
  [1453] = {
    ["D"] = "Il y a beaucoup de travail à faire en Désolace, $n. C\'est un endroit atroce, souillé par les centaures et autres monstruosités. Mais si vous voulez un bon petit travail honnête à la semaine, alors allez-y, parlez à Kreldig Ungor, et dites-lui que je vous envoie.$b$bVous le trouverez planqué à la Combe de Nijel, un petit camp plutôt douillet niché dans les montagnes dans la partie nord de Désolace. Allez simplement à l\'est si vous venez des Serres-Rocheuses. C\'est facile à rater, alors faites attention.",
    ["O"] = "Parler à Kreldig Ungor en Désolace.",
    ["T"] = "Les affaires de Récupérateurs SA en Désolace",
  },
  [1454] = {
    ["D"] = "Ma première commande concerne le mage Karnitol, une sorte de noble de Stormwind. Il semblerait que son bateau ait été attaqué par des géants des mers tandis qu\'il naviguait au large de la côte, dans le nord-ouest de Désolace. Ses serviteurs ont essayé de ramener sur la rive l\'un de ses coffres les plus précieux, mais lorsque les géants ont attaqué en masse, il a préféré abandonner le coffre et se téléporter, avec ses serviteurs, en sécurité.$b$bIl veut que nous retrouvions le coffre et son contenu, et que nous le lui rapportions à Stormwind.",
    ["O"] = "Retrouver le Coffre de Karnitol en Désolace.",
    ["T"] = "L\'épave de Karnitol",
  },
  [1455] = {
    ["D"] = "Vous prenez un moment pour comprendre comment le coffre a été ouvert, et vous remarquez, sur les côtés et le dessus du coffre, de longues et profondes entailles. Elles révèlent que quelque chose d\'autre qu\'un humain ou qu\'un géant s\'y est intéressé. Les marques ne semblent être celles d\'une épée ou d\'une dague.$b$bAprès avoir fouillé le coffre, vous constatez qu\'il est complètement vide.$b$bN\'ayant plus rien d\'autre à faire, vous refermez le coffre et vous préparez à partir. Kredlig va être déçu.",
    ["O"] = "Retourner voir Kreldig Ungor en Désolace.",
    ["T"] = "L\'épave de Karnitol",
  },
  [1456] = {
    ["D"] = "Intriguant.$b$bBien, il se peut que tout espoir ne soit pas perdu.$b$bLe long de cette même côte, mais plus au nord, près de l\'île de Ranazjar, se trouvent des ruines sous-marines datant des elfes de la nuit. Une tribu de nagas, les Ondulame, s\'y est établie ! Je pense que c\'est ce notre meilleure chance.$b$bSi un groupe de leurs éclaireurs a été envoyé aussi loin au sud, alors cette tribu doit être puissante. Pour retrouver les possessions de Karnitol, je m’attaquerais en priorité aux nagas les plus forts : les chasse-marées et les sorcières des mers.",
    ["O"] = "Retrouver la Sacoche de Karnitol et la rapporter à Kreldig Ungor en Désolace.",
    ["T"] = "L\'épave de Karnitol",
  },
  [1457] = {
    ["D"] = "Rapportez les biens de Karnitol à Roetten à Ironforge, et il vous paiera pour ce travail bien accompli.$b$bHa ! Ha ! Si vous ne faites pas attention, il sera tellement satisfait de vous qu\'il vous demandera de nous rejoindre à plein temps.$b$bMais à nouveau, bon travail, $n.$b$bSoyez $gprudent:prudente; sur le chemin du retour, et ne prenez pas tout votre temps ; Karnitol n\'est pas le plus patient des hommes, mais il reste très généreux néanmoins.",
    ["O"] = "Apporter la Sacoche de Karnitol à Roetten Stonehammer à Ironforge.",
    ["T"] = "L\'épave de Karnitol",
  },
  [1458] = {
    ["D"] = "L\'une de mes tâches ici consiste à collecter des réactifs pour Leftwitch, un mage gnome de Kharanos. Roetten semble penser que c\'est de l\'argent facilement gagné puisque nous sommes déjà sur place. Il se soucie peu du fait que nous sommes si démunis.$b$bLes premiers objets de sa liste devraient être assez faciles à trouver, et ce sera de l\'argent gagné pour quiconque m\'aidera.$b$bJ\'ai besoin de cornes et de griffes des satyres Fielfurie qui vivent à l\'est d\'ici. Rapportez-m\'en et nous poursuivrons la liste.",
    ["O"] = "Apporter 10 Griffes de Fielfurie et 10 Cornes de Fielfurie à Kreldig Ungor en Désolace.",
    ["T"] = "Des réactifs pour les Récupérateurs",
  },
  [1459] = {
    ["D"] = "Ensuite, sur la liste de Leftwitch, on trouve… on trouve… Oh !$b$bRelativement facile, mais il faut décrypter ses directives. Il a besoin de venin de scorpashi et de peau de kodo. Mais il est précisé que le venin doit provenir de n\'importe quel scorpashi, et que la peau doit être celle d\'un vieux kodo, pas d\'un kodo mourant ou d\'un ancien kodo.$b$bLes mages, je vous dis pas, les trucs qu\'ils veulent parfois... c\'est tout simplement bizarre.",
    ["O"] = "Apporter 7 Fioles de venin de scorpashi et 3 Peaux de vieux kodo à Kreldig Ungor, en Désolace.",
    ["T"] = "Des réactifs pour les Récupérateurs",
  },
  [1460] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [1461] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [1462] = {
    ["D"] = "",
    ["O"] = "Parler à la Prophétesse Ravenfeather pour obtenir un autre Sapta de terre.",
    ["T"] = "Le sapta de terre",
  },
  [1463] = {
    ["D"] = "",
    ["O"] = "Parler à Canaga Earthcaller pour obtenir un autre Sapta de terre.",
    ["T"] = "Le sapta de terre",
  },
  [1464] = {
    ["D"] = "",
    ["O"] = "Parler à Telf Joolam pour obtenir un autre Sapta de feu.",
    ["T"] = "Le sapta de feu",
  },
  [1465] = {
    ["D"] = "Tandis que vous continuez à fouiller le chariot, vous découvrez non seulement un pendentif mais également des traces de pas qui se dirigent vers le sud. Au loin, vous distinguez de la fumée s\'élevant d\'une grande forteresse. La fumée provient d\'un feu entretenu, il ne s\'agit pas de bâtiments qui brûlent, et votre instinct vous dit que les Malem, si tant est qu\'ils soient toujours en vie, ont été emmenés dans cette direction.$B$BVous estimez que Vahlarriel, à la Combe de Nijel, devrait voir le pendentif et savoir qu\'il se trouve des signes de vie au sud.",
    ["O"] = "Rapporter le Pendentif de Malem à Vahlarriel Demonslayer en Désolace.",
    ["T"] = "La recherche de Vahlarriel",
  },
  [1466] = {
    ["D"] = "Ce sont les derniers objets à rassembler. Cela vous prendra aussi un peu plus de temps que pour les précédents.$b$bBien, assez parlé, commençons. Leftwitch dit qu\'il a besoin de : dix cervelles de mange-mana, de chasseur de mage ou de chasseur ley ; dix ailes du néant prélevées sur une vierge du néant, une soeur du néant ou une ensorceleuse du néant. Et... dix fioles de sang de garde funeste provenant d\'un capitaine ou seigneur de la garde funeste.$b$bC\'est assez simple, non ? Non, sérieusement, si vous laissez tomber cette quête, je comprendrai.",
    ["O"] = "Apporter 10 Cervelles de gangrechiens, 10 Ailes du Néant et 10 Fioles de Sang de garde funeste à Kreldig Ungor, en Désolace.",
    ["T"] = "Des réactifs pour les Récupérateurs",
  },
  [1467] = {
    ["D"] = "Pour être $gpayé:payée;, vous devez retourner à Ironforge et parler à Roetten en personne. Il aimera être débarrassé de la caisse de Leftwitch, de toute façon. Remettez-lui simplement ceci et ça devrait suffire pour avoir votre récompense.$b$bEt ne vous en faites pas, je ferai passer un mot positif à votre sujet lorsque je reviendrai. Je ne devrais plus rester longtemps en Désolace.",
    ["O"] = "Rapporter la Caisse de Leftwitch à Roetten Stonehammer à Ironforge.",
    ["T"] = "Des réactifs pour Récupérateurs SA",
  },
  [1468] = {
    ["D"] = "Vous voulez aider l\'orphelinat ? Soyez $gbéni, mon ami:bénie; mon amie;. Votre aide durant la Semaine des enfants sera inestimable.$B$BPrenez ce sifflet. Vous ne pourrez vous en servir que durant sept jours, en fait, pendant toute la Semaine des enfants. Quand vous l\'utiliserez, vous pourrez bavarder avec l\'enfant que vous avez accepté de surveiller. Si vous l\'utilisez une seconde fois, il renverra l\'enfant.$B$BJe vous en prie, $N, utilisez le sifflet pour faire la connaissance de votre protégé ! Je suis sûre que l\'enfant est très impatient de vous rencontrer.",
    ["O"] = "Utilisez le Sifflet d\'orphelin humain pour parler à l\'enfant que vous protégerez pendant la Semaine des enfants.",
    ["T"] = "La Semaine des enfants",
  },
  [1469] = {
    ["D"] = "Avant le vol, je me suis bu un plein tonneau de bière. Hic !$b$bEt ça a été un de ces vols ! Volé de-ci de-là avec Brohann qui me suivait. Mais pas vu de temple en tout cas. Hic !$b$bJe veux dire, j\'ai pas vu de temple, jusqu\'à ce que je sois trop parti et que je tombe de ma monture. J\'ai plongé directement dans la flotte et j’ai été aspiré. Me suis trouvé entouré par un tas de trolls à la mine patibulaire. Vous pensez bien que j\'ai nagé très vite pour partir ! J\'ai quand même ramassé un souvenir troll. Tenez, apportez ça à Brohann et dites-lui que son temple est là-bas, en bas, de fait. Hic !",
    ["O"] = "Apporter le Fragment de tablette atal\'ai à Brohann Caskbelly à Stormwind.",
    ["T"] = "L\'histoire de Rhapsody",
  },
  [1470] = {
    ["D"] = "Salutations, enfant. Je peux voir que vous êtes encore novice dans les arts du démoniste, le pouvoir échappant toujours à votre contrôle. Je peux aussi sentir votre désir d\'apprendre.$B$BEt vous apprendrez.$B$BLa force ne réside pas dans l\'ignorance. La connaissance est notre pouvoir le plus puissant. Par l\'application du savoir, nous pouvons contrôler les magies et les êtres chaotiques du vide, et les soumettre à notre volonté.$B$BJe peux vous montrer comment soumettre un servant, mais d\'abord vous devez m\'apporter trois crânes pris sur les squelettes Cliquethorax qui rôdent près de la forge abandonnée.",
    ["O"] = "Apporter 3 Crânes de Cliquethorax à Venya Marthand.",
    ["T"] = "Lever le voile",
  },
  [1471] = {
    ["D"] = "Vous avez sûrement vu le cercle d\'invocation en dessous. C\'est le seul endroit d’où vous pourrez tirer un marcheur du Vide libre.$B$BEn utilisant ces runes, vous pourrez l\'attirer ici. Vous devrez alors le dominer par la force des armes et de la magie. Lorsqu\'il sera assujetti, il sera lié à vous. Venez alors me voir, et je vous expliquerai le sort d\'invocation, pour que vous puissiez vous en servir, pour quelque objectif que ce soit.",
    ["O"] = "Utiliser les Runes d\'invocation pour invoquer et soumettre un marcheur du Vide, puis rapporter les Runes d’invocation à Carendin Halgar, au Temple des Damnés.",
    ["T"] = "Le lien",
  },
  [1472] = {
    ["D"] = "Une succube est une dévoreuse d\'âmes, une destructrice de cœurs et une tentatrice d\'hommes. Une créature machiavélique à l’esprit très singulier.$B$BElle ne peut pas être amenée dans notre monde sans une impulsion.$B$BAussi nous utilisons sa propre force contre elle. Face à des cœurs d\'hommes bons, d\'hommes purs, d\'hommes aimants, la succube est incapable de résister, et elle viendra jusqu\'à notre monde. Alors vous pourrez la dominer, et l’utiliser.$B$BParlez à mon assistant, Godrick Farsan, à l\'intérieur du temple. Il vous aidera à trouver un appât.",
    ["O"] = "Parler à Godrick Farsan au Temple des damnés.",
    ["T"] = "La dévoreuse d\'âmes",
  },
  [1473] = {
    ["D"] = "Il semble acquis que vous êtes $gprêt:prête; à apprendre à invoquer un marcheur du Vide.$B$BUn être créé par le chaos qui l\'entoure. Si vous en invoquez un, mais que vous ne pouvez pas le contrôler, vous serez $gannihilé:annihilée;.$B$BLe premier marcheur du Vide a été invoqué par Egalin, et le procédé est expliqué dans son grimoire.$B$BMalheureusement, il nous a été volé par un agent de la Croisade écarlate, qui l\'a caché dans sa base, dans une tour de l\'ouest. Rapportez-le-moi, et je vous transmettrai cette connaissance.",
    ["O"] = "Retrouver le Grimoire d\'Egalin et l\'apporter à Carendin Halgar au Temple des damnés.",
    ["T"] = "La créature du Vide",
  },
  [1474] = {
    ["D"] = "Lancez un sort avec ces cœurs au cercle d\'invocation. Vous ouvrirez ainsi le chemin à une succube. Vous avez de grands pouvoirs à présent, et vous en aurez besoin pour la soumettre.$B$BAttention à ses charmes, $N, beaucoup d\'esprits forts ont cédé devant eux. Une fois qu\'elle sera battue, la succube retournera au Néant, et je vous apprendrai le sort pour l\'invoquer à volonté.",
    ["O"] = "Utiliser les Coeurs purs pour invoquer et soumettre une succube, puis rapporter les Coeurs purs à Carendin Halgar, au Temple des Damnés.",
    ["T"] = "Le lien",
  },
  [1475] = {
    ["D"] = "D\'après la légende, le temple d\'Atal\'Hakkar est un lieu saint dédié au dieu ancien, Hakkar l\'Écorcheur d\'âmes. Habité par une ignoble tribu de trolls, les Atal\'ai, on dit qu\'il est impossible d\'y entrer.$B$BCe temple est à présent sous les eaux du Bassin des larmes, et récupérer les artefacts sera d\'autant plus difficile. Mais c\'est pourtant ce que j\'ai besoin que vous fassiez, $N.$B$BAidez-moi, en récupérant les Tablettes atal\'ai intactes. Apportez-les-moi, et je deviendrai un héros aux yeux de la Ligue des Explorateurs. Nous partagerons ensemble cette gloire.",
    ["O"] = "Rassembler 10 Tablettes atal\'ai pour Brohann Caskbelly à Stormwind.",
    ["T"] = "Dans le temple d\'Atal\'Hakkar",
  },
  [1476] = {
    ["D"] = "Des hommes d\'honneur et de bonté ? Est-ce qu\'un tel homme existe seulement ?$b$bCela a nécessité une recherche assez longue, mais grâce à quelques fragments d’histoires entendues par-ci par-là, je suis parvenu à trouver deux hommes qui devraient faire l\'affaire.$b$bLe premier se nomme Dalin Forgewright. Il a consacré sa vie aux réfugiés de Lordaeron, ceux qui sont rejetés par leurs pairs au mur de Greymane.$b$bL\'autre s\'appelle Comar Villard, un homme qui reste fidèle à son premier amour, et qui court les Paluns à la recherche de sa femme.$b$bRemettez leurs cœurs à Carendin. Mais auparavant, laissez-moi vous montrer votre gibier.",
    ["O"] = "Apporter les Cœurs de Dalin Forgewright et de Comar Villard à Carendin Halgar dans le Temple des Damnés.",
    ["T"] = "Des cœurs purs",
  },
  [1477] = {
    ["D"] = "Les mages conduisent des recherches au Rempart-du-Néant dans les Terres foudroyées, des recherches vitales à notre compréhension des Portes des ténèbres. Ils ont besoin presque constamment de marchandises, mais notre ligne d\'approvisionnement vers Rempart-du-Néant est fragile.$b$bVous devez vous assurer que les provisions parviennent bien au Rempart-du-Néant. Allez voir le maître de guet Sorigal à Darkshire, et faites ce qu\'il demande. Cette quête vous conduira dans des terres hostiles, $n. Soyez $gprêt:prête;.",
    ["O"] = "Parler au Maître de guet Sorigal.",
    ["T"] = "Fournitures vitales",
  },
  [1478] = {
    ["D"] = "Ah, $n ! Je viens juste de recevoir un mot de Carendin Halgar disant qu\'il voulait vous voir pour continuer l\'entraînement.$b$bVous devriez être $gfier:fière; de vous, $n. Ce n\'est pas tous les jours que Maître Halgar daigne instruire personnellement un jeune démoniste.$b$bVous le trouverez dans le Quartier de la magie à Undercity, près du Temple des damnés.",
    ["O"] = "Parler à Carendin Halgar à Undercity.",
    ["T"] = "Les invocations de Halgar",
  },
  [1479] = {
    ["D"] = "J\'ai entendu parler d\'histoire sur les elfes de la nuit, qui ont leur banque dans un arbre, mais qui ressemble à un ours ! C\'est génial ! C\'est chouette, il paraît que les elfes de la nuit aiment la nature et ce genre de choses. Et les ours, c\'est trop terrible !$B$BVous voulez bien m\'emmener voir l\'arbre-ours à Darnassus, s\'il-vous-plaît ? Y\'a un bateau qui peut nous amener juste à côté, ça ne devrait pas prendre trop de temps. S\'il vous plaaaaaaaaaaaît !",
    ["O"] = "Conduire l\'orphelin à la banque de Darnassus. La banque est creusée dans le tronc d\'un arbre connu sous le nom du Rameau des Éternels.",
    ["T"] = "Le Rameau des Éternels",
  },
  [1480] = {
    ["D"] = "La peau déchirée sent le pourri. Malgré son aspect sec et fragile, elle est remarquablement souple.$b$bEn la déroulant, vous voyez clairement une sorte de langage écrit en rouge sur l\'un des côtés. Un symbole est apposé en évidence en bas de la peau ; vous supposez qu’il s’agit d’une signature.$b$bPeut-être Maurin pourrait-il vous en dire plus ?",
    ["O"] = "Apporter une Peau de démon écorché à Maurin Bonesplitter en Désolace.",
    ["T"] = "Le corrupteur",
  },
  [1481] = {
    ["D"] = "Je pense pouvoir trouver ce démon si j\'ai les réactifs adéquats.$b$bS\'il est effectivement aussi puissant que je le suspecte, je vais avoir besoin de certains objets disponibles sur des créatures mortelles. Mais vous agissez au nom du Chef de guerre, aussi je suis certain que vous accepterez ma quête.$b$bD\'abord, je vais devoir briser toutes les protections qu\'il aurait contre les visions. Pour cela, j\'aurais besoin d\'un scalp de satyre. Mais pas de n\'importe quel satyre. Il doit provenir d\'un Traqueur des ténèbres Fielfurie.$b$bCherchez cette tribu dans les ruines anciennes au nord-est de Désolace.",
    ["O"] = "Apporter un Scalp de Traqueur des ténèbres à Maurin Bonesplitter en Désolace.",
    ["T"] = "Le corrupteur",
  },
  [1482] = {
    ["D"] = "Un cristal d\'oracle ferait parfaitement l\'affaire. Ils sont conçus par les nagas et utilisés par leurs oracles.$b$bAu nord-ouest d\'ici, le long de la côte, se trouve un vaste récif de corail que les nagas considèrent comme leur demeure, lorsqu\'ils viennent près du rivage. Vous trouverez leurs oracles là-bas. Les Ondulames sont dangereux et ne réagiront pas amicalement à votre intrusion : soyez $gprudent:prudente; et hâtez-vous.$b$bQuand vous reviendrez, nous verrons à localiser le seigneur-démon Azrethoc.",
    ["O"] = "Apporter un Cristal d\'oracle à Maurin Bonesplitter, en Désolace.",
    ["T"] = "Le corrupteur",
  },
  [1483] = {
    ["D"] = "La KapitalRisk a détourné un grand nombre de savants gobelins, mais de temps en temps, nous avons l’occasion de les leur reprendre.$b$bIl y a quelque temps, l\'équipe de la KapitalRisk des Serres-Rocheuses a embauché Gerenzo Wrenchwhistle. Un gnome ! Leur autre ingénieur, Ziz Fizziks, en a été tellement dégoûté qu\'il a essayé de trouver un nouveau travail auprès du Syndicat des Artisans. Je lui ai confié quelques travaux à faire, mais il a peut-être besoin de renfort.$b$bPeut-être que vous pourriez voir ça pour moi ? On peut le trouver à la lisière ouest de la combe des Cisailles.",
    ["O"] = "Parler à Ziz Fizziks dans la Combe des Cisailles.",
    ["T"] = "Ziz Fizzik",
  },
  [1484] = {
    ["D"] = "Cette créature est beaucoup plus dangereuse que je ne l\'avais cru. Il est certain que Takata remet en cause mes capacités à battre une telle créature, et ce serait insulter son honneur que de ne pas l\'informer de ce qui vient juste de se passer.$b$bParlez à l\'homme d\'épée, dites-lui que vous voulez tuer le démon s\'il l’autorise, mais n\'insistez pas trop. Ce serait mieux qu\'il croit que c\'est son idée.",
    ["O"] = "Parler à Takata Steelblade en Désolace.",
    ["T"] = "Le corrupteur",
  },
  [1485] = {
    ["D"] = "Ces démonistes qui sont passés avant nous ont engendré la méfiance parmi la Horde, et bien que nous soyons tolérés, nous ne sommes pas aimés.$B$BCela n\'a pas poussé les démonistes à s’unir pour autant, et beaucoup choisissent de vivre reclus, marinant seuls dans leur magie. Je ne vais pas tenter de vous faire choisir un chemin ou l\'autre, mais seulement vous aider pendant votre progression.$B$BAvant que je vous apprenne l\'art de l\'Invocation, apportez-moi six têtes de vils quasits. Vous en trouverez dans la grotte au nord-est.",
    ["O"] = "Apporter 6 Têtes de vils quasits à Ruzan.",
    ["T"] = "Les vils quasits",
  },
  [1486] = {
    ["D"] = "Tandis que Naralex s\'enfonçait toujours plus profondément dans son cauchemar, des bêtes étranges émergèrent de sous les Tarides, jusqu\'aux Cavernes des lamentations.$b$bCes créatures perverties possèdent des propriétés étranges, comme venues d\'un autre monde. Bien qu\'elles soient maléfiques de nature, je pense qu’il y a des avantages à tirer de leur présence ici. Leur peau peut certainement être travaillée.$b$bSi vous vous considérez à la hauteur, partez dans les cavernes, en dessous, et trouvez des écailles communes.",
    ["O"] = "Napalk dans les Cavernes des lamentations veut 20 Peaux communes.",
    ["T"] = "Les peaux communes",
  },
  [1487] = {
    ["D"] = "Naralex poursuivait un noble but.$b$bNotre grand chef aspirait à entrer dans le Rêve d\'émeraude pour faire reverdir ces terres arides, qui étaient autrefois une forêt luxuriante. Mais quelque chose s\'est mal passé.$b$bLe rêve de Naralex s\'est transformé en cauchemar et des créatures perverties ont commencé à envahir les cavernes.$b$bDes Disciples de Naralex tentent de le réveiller, mais mon souci est de débarrasser les cavernes de ces bêtes maléfiques.$b$bBravez les cavernes, $n, et éradiquez cette vermine étrangère.",
    ["O"] = "Ebru, des Cavernes des lamentations, veut que vous tuiez 7 Ravageurs Déviant, 7 Vipères Déviant, 7 Glacials Déviant et 7 Crocs-d\'effroi Déviant.",
    ["T"] = "L\'éradication des déviants",
  },
  [1488] = {
    ["D"] = "Bien que je ne sache rien de ce seigneur Azrethoc, je suis d\'accord avec Maurin. Il faut l\'arrêter, surtout s\'il tente de gagner du pouvoir au sein de la Lame ardente pour faire avancer ses objectifs.$b$bVous nous avez déjà exprimé votre souhait d’aider le Chef de guerre, et j\'aimerais vous charger de réunir un groupe pour aller tuer ce seigneur-démon et son serviteur démoniste.$b$bLorsque vous serez $gprêt:prête;, partez pour le sud, et revenez me voir lorsque ce danger sera éliminé.",
    ["O"] = "Tuer le seigneur Azrethoc et Jugkar Grim\'rod puis retourner voir Takata Steelblade en Désolace.",
    ["T"] = "Le corrupteur",
  },
  [1489] = {
    ["D"] = "Les carapaces que vous m\'avez apportées portent une souillure que je n\'ai jamais vue auparavant. Je pense que l\'eau des oasis en est la cause, et je peux ressentir le désordre qu\'ils causent à cette terre. C\'est comme si la terre rêvait et ne pouvait s\'éveiller.$b$bLes Cavernes des lamentations doivent être à l’origine de cette souillure. Mais avant de vous y rendre, allez parler à Hamuul Runetotem, le druide ancien. Je lui ai envoyé un message décrivant les événements dans les Tarides. C\'est un sage, et il peut vous donner de bons conseils.$b$bVous le trouverez à Thunder Bluff, sur la Cime des Anciens.",
    ["O"] = "Parler à Hamuul Runetotem.",
    ["T"] = "Hamuul Totem-runique",
  },
  [1490] = {
    ["D"] = "J\'ai pénétré sur la terre des rêves et j’ai vu un groupe de druides dans les Cavernes des lamentations. J\'ai vu qu’autrefois leurs buts étaient nobles, mais qu\'ils ont perdu le chemin depuis. Ils sont à présent serviteurs de la souillure qui sourd dans les Tarides.$B$BJe ne suis plus jeune et les forces me manquent. Je ne pourrai pas rester plus longtemps sur cette terre des rêves pour en apprendre davantage sur ces druides souillés. Mais Nara est jeune et son esprit est féroce. Elle est restée à rêver tandis que je me retirais.$B$BParlez-lui. Elle est dans la tente, là-bas.",
    ["O"] = "Parler à Nara Wildmane.",
    ["T"] = "Nara Crin-Sauvage",
  },
  [1491] = {
    ["D"] = "Ainsi les cornes de raptors n\'ont pas si bien marché que cela ! Mais ce n\'est pas grave, personne ne devient riche au premier essai ! Et de plus, je pense avoir trouvé encore mieux pour mes potions d\'intelligence !$b$bIl existe une substance appelée essence de lamentation, qui est sûrement responsable de l’état de certaines plantes et bêtes des Tarides. Trouvez-en, et je l\'essaierai dans mes boissons !$b$bJ\'ai entendu dire que cette essence apparaît sur des créatures à l\'état d\'ectoplasme vivant dans les Cavernes des lamentations, à l\'ouest. Chassez ces créatures et récupérez l\'essence.",
    ["O"] = "Apporter 6 doses d\'Essence de lamentation à Mebok Mizzyrix, à Ratchet.",
    ["T"] = "Les potions d\'intelligence",
  },
  [1492] = {
    ["D"] = "J\'ai besoin d\'envoyer une caisse de potions, de réactifs et d’objets divers à mes associés dans l\'Ancien Monde. J\'ai en fait assez peu de temps et je ne peux pas vérifier moi-même qu\'elle est bien embarquée aux docks de Ratchet. De plus, la dernière caravane est déjà partie.$b$bSi vous pouvez, apportez-la au Maître des quais Dizzywig. Il veillera à ce qu\'elle soit chargée sur le prochain bateau en partance pour la crique des Flots noirs.",
    ["O"] = "Apporter la Caisse sûre au Maître des quais Dizzywig à Ratchet.",
    ["T"] = "Maître des quais Dizzywig",
  },
  [1498] = {
    ["D"] = "En tant que guerrier, l\'une de nos tâches les plus cruciales est… la protection. Nous sommes le bouclier de la Horde, et nous gardons nos semblables les plus faibles à l\'arrière. Si vous voulez rejoindre nos rangs, alors vous devez me prouver votre ardeur.$b$bPartez pour les falaises du Tonnerre en Durotar, où vivent les foudroyants et les lézards-tonnerre. Chassez ces bêtes ! Montrez-moi que vous êtes plus $gfort:forte; qu\'eux ! Affrontez leurs assauts et apportez-moi un tas d\'écailles roussies comme preuve.$b$bFaites cela, et alors je deviendrai votre maître.",
    ["O"] = "Apporter 5 Ecailles roussies à Uzzek au Poste de garde extérieur dans les Tarides.",
    ["T"] = "La voie de la défense",
  },
  [1499] = {
    ["D"] = "Zureetha envoie des novices comme vous réduire le nombre des créatures de la caverne près de laquelle vous avez combattu les vils quasits.$B$BJe pense qu\'il serait bon pour vous de l\'aider et ainsi de gagner de l\'expérience. Dites-lui également que vous avez participé à la résolution du problème posé par ces diablotins, elle aura probablement une récompense pour vous.",
    ["O"] = "Parler à Zureetha Fargaze.",
    ["T"] = "Les vils quasits",
  },
  [1500] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Waking Naralex",
  },
  [1501] = {
    ["D"] = "Il reste si peu de démonistes… Nous risquons beaucoup, mais cela en vaut la peine. Vous devez rapidement atteindre la pleine possession de vos capacités pour être utile à la Horde et au Chef de guerre.$B$BIl est temps pour vous d\'apprendre un nouveau sort d\'invocation.$B$BD\'abord, vous devez récupérer la Tablette de Verga, qui a été volée par des démonistes de la Lame ardente et qui est à présent détenue dans leur antre du Rocher du crâne à l\'est de la ville. Une fois que j\'aurai la tablette, je vous montrerai les runes nécessaires à l\'invocation d\'un marcheur du Vide.",
    ["O"] = "Récupérer la Tablette de Verga pour Gan\'rul Bloodeye à Orgrimmar.",
    ["T"] = "La créature du Vide",
  },
  [1502] = {
    ["D"] = "Il est vital que l\'arme d\'un guerrier soit de qualité. Et s\'il y a bien un orc capable de concevoir des armes de qualité, c’est Thun\'grim Firegaze. C\'est un ermite qui vit au sommet des collines à l\'est de la Croisée.$b$bSi vous cherchez une arme, $gguerrier:guerrière;, alors cherchez Thun\'grim. Pour le trouver, voyagez d\'abord vers la Croisée, vers l\'ouest. Puis tirez droit à l\'est, dans les collines. Son camp est au sommet d\'une d\'entre elles.",
    ["O"] = "Parler à Thun\'grim Firegaze dans les Tarides.",
    ["T"] = "Thun\'grim Mirefeu",
  },
  [1503] = {
    ["D"] = "Je vais vous donner une arme, $n, mais d\'abord vous devez me venir en aide. Les Tranchecrins du nord-ouest sont venus dans mon camp il y a plusieurs nuits. Ils ne venaient pas m’affronter, mais ils savaient que ma vue est mauvaise. Quelques-uns m\'ont distrait, en grognant, tandis que les autres me dévalisaient ! Les lâches !$b$bIls ont emporté un coffre de fer contenant des barres d\'acier forgé. Si vous pouvez les récupérer, alors je vous donnerai l\'arme idéale.$b$bTrouvez le coffre en fer dans le camp des hurans et rapportez-moi les barres d\'acier forgé.",
    ["O"] = "Apporter les Barres d\'Acier forgé à Thun\'grim Firegaze dans les Tarides.",
    ["T"] = "Acier forgé",
  },
  [1504] = {
    ["D"] = "À présent, observez la Tablette de Verga, car elle contient les glyphes nécessaires à l\'invocation. Apportez-les au cercle sous la tente de Neeru Fireblade de l\'autre côté de la grotte. Vous serez alors capable d\'invoquer un marcheur du Vide.$b$bVous devez le battre en utilisant tous les moyens physiques et magiques que vous avez à votre disposition. Une fois qu\'il sera battu, revenez me voir avec les glyphes d\'invocation et je vous apprendrai comment soumettre la créature.",
    ["O"] = "Utiliser les Glyphes d\'invocation pour invoquer et soumettre un marcheur du Vide, puis rapporter les Glyphes d\'invocation à Gan\'rul Bloodeye, à Orgrimmar.",
    ["T"] = "Le lien",
  },
  [1505] = {
    ["D"] = "La Horde honore ses guerriers, car leur force n\'est pas remise en question. Mais les grands guerriers sont également de grands étudiants.$b$bCherchez Uzzek. C\'est le vétéran de maintes batailles et il enseigne à présent l\'art du combat à de jeunes guerriers. Vous le trouverez au sud du Poste de garde extérieur, la garnison orc à la frontière entre les Tarides et Durotar à l\'est.",
    ["O"] = "Parler à Uzzek au Poste de garde extérieur.",
    ["T"] = "Le vétéran Uzzek",
  },
  [1506] = {
    ["D"] = "Vous là ! $n, c\'est ça ?$b$bJ\'espère que votre programme n\'est pas trop chargé, car Gan\'rul veut vous voir tout de suite. Vous le trouverez avec les démonistes seniors à la Faille de l\'ombre.$b$bD\'après mes souvenirs de Gan\'rul, le fait qu\'il vous appelle est soit une très bonne chose, soit une très mauvaise.",
    ["O"] = "Parler à Gan\'rul Bloodeye à Orgrimmar.",
    ["T"] = "Les invocations de Gan\'rul",
  },
  [1507] = {
    ["D"] = "Il est temps pour vous d\'en apprendre plus sur les magies d\'invocation. Vous allez apprendre à invoquer une succube, une créature dotée de pouvoirs mentaux incroyables. Elle peut apaiser les esprits faibles jusqu\'à ce qu\'ils tombent en son pouvoir. Mais ne craignez rien ! Je sais que vous êtes capable de relever le défi.$b$bUne succube peut être invoquée que si elle est appâtée avec un objet qui représente une grande dévotion et un grand amour.$b$bUne suggestion ? Parlez à Cazul... Il semble qu\'il n\'y ait pas grand-chose que le vieux démoniste ignore.",
    ["O"] = "Parlez à Cazul à la Faille de l\'ombre.",
    ["T"] = "La dévoreuse d\'âmes",
  },
  [1508] = {
    ["D"] = "Hmm… mais bien sûr, ce n\'est pas la raison pour laquelle vous êtes $gvenu:venue; me parler.$B$BSi Gan\'rul vous envoie, ce doit être parce que vous devez invoquer une succube, et que vous avez besoin d\'un objet pour la séduire.$B$BHmm… si vous pouvez faire confiance à un homme aveugle pour vous diriger, $N, alors écoutez. Zankaja vit à l\'ouest de la tour des Cavaliers célestes, dans les hauteurs d\'Orgrimmar. Elle attend le retour de son compagnon.$B$BAllez la voir.",
    ["O"] = "Parler à Zankaja à Orgrimmar.",
    ["T"] = "Cazul l\'aveugle",
  },
  [1509] = {
    ["D"] = "Mon compagnon, Dogran, a été récemment envoyé à la Croisée pour y accomplir son devoir. Il avait obtenu une promotion, et il était très fier.$b$bMoi, j\'étais triste, mais je n\'en ai rien laissé paraître. Et j\'étais inquiète, aussi. Les Tarides sont une région bien plus dangereuse qu\'Orgrimmar, et je craignais le pire.$b$bÀ présent, cela fait une semaine qu\'il aurait dû renter, et je n\'ai toujours reçu aucune nouvelle...$b$bPeut-être pourriez-vous vous rendre à la Croisée et voir s\'ils ont des nouvelles de lui ? Il travaillait avec Gazrog, qui devrait toujours y être assigné.",
    ["O"] = "Parler à Gazrog à la Croisée.",
    ["T"] = "Des nouvelles de Dogran",
  },
  [1510] = {
    ["D"] = "Il était en poste au Camp Taurajo, où nous essayions de nous débarrasser des hurans. Il a dirigé quelques grunts sur l\'un de leurs campements, mais il a été alors grièvement blessé. Les autres sont revenus et ont essayé de lui porter secours. Mais il allait très mal, ses blessures saignaient et ils hésitaient à le déplacer avant de le panser correctement.$B$BIls ont envoyé un message ici, demandant un breuvage de soin des trolls de Malaka\'jin, au pied des Serres-Rocheuses. Nous avons envoyé quelqu\'un pour le chercher, mais personne n\'est encore revenu.",
    ["O"] = "Parler à Ken\'zigla, au village de Malaka\'jin.",
    ["T"] = "Des nouvelles de Dogran",
  },
  [1511] = {
    ["D"] = "Je pensais bien que quelqu\'un d\'autre viendrait prendre le breuvage, alors je l\'ai préparé à toutes fins utiles.$b$bTenez, prenez, $n. N’oubliez pas : ne lui donnez que la moitié ! Cela lui donnera suffisamment de force pour qu\'il puisse bouger sans perdre tout son sang. Laissez passer une nuit, puis donnez-lui l\'autre moitié.$b$bVoilà, vous feriez bien de l\'apporter à Logmar au Camp Taurajo. D\'après ce que m\'a dit l\'autre qui est passé avant, il devrait avoir une idée de l\'endroit où Dogran se trouve.",
    ["O"] = "Apporter le Breuvage de Ken\'zigla au grunt Logmar, à Camp Taurajo.",
    ["T"] = "Breuvage de Ken\'zigla",
  },
  [1512] = {
    ["D"] = "Zankaja est jeune et belle. Etre enchaînée à un invalide… Je ne permettrai pas qu\'elle souffre de cette injustice.$b$bLaissez-moi, $n. Et merci. Je peux ainsi terminer ma vie dans une glorieuse bataille, et non comme un légume.$b$bEt je vous en prie, je souhaiterais que vous ne disiez rien à ma compagne... Je ne veux pas quelle se sente coupable.",
    ["O"] = "Rapporter le Pendentif de poussière à Gan\'rul Bloodeye à Orgrimmar.",
    ["T"] = "Le cadeau de l\'amour",
  },
  [1513] = {
    ["D"] = "Apportez cela au cercle d\'invocation dans la tente de Neeru, $N. Invoquez une ignoble succube, et dominez-la. Comme toujours cependant, soyez $gprudent:prudente;, car la fierté et une trop grande confiance en soi ont terrassé de nombreux démonistes prometteurs.$B$BUne fois que vous l\'aurez vaincue, venez me rendre le pendentif, pour que je puisse donner des nouvelles à Zankaja.",
    ["O"] = "Utiliser le Pendentif de Dogran pour invoquer et soumettre une succube, puis rapporter le Pendentif de Dogran à Gan\'rul Bloodeye, à Orgrimmar.",
    ["T"] = "Le lien",
  },
  [1514] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La fleur-de-vent corrompue",
  },
  [1515] = {
    ["D"] = "Vous êtes $garrivé:arrivée; jusque-là, alors si vous voulez voir la fin, je ne vais pas vous arrêter. Cependant, je ne peux pas vous fournir de forces supplémentaires, vous êtes $gseul:seule;.$B$BDogran est prisonnier dans un camp de hurans au nord d\'ici. Cherchez une paire de huttes. Il sera surveillé de près... À condition qu’ils ne l\'aient pas encore tué et qu’il n’ait pas succombé à ses blessures.$B$BBonne chance.",
    ["O"] = "Apporter le Breuvage de Ken\'zigla à Dogran dans les Tarides.",
    ["T"] = "La captivité de Dogran",
  },
  [1516] = {
    ["D"] = "Le temps est venu, jeune $c. Vous êtes $gdevenu plus fort:devenue plus forte; et votre esprit est aussi endurant que la terre. Votre choix de suivre la voie du chaman montre votre sagesse, avant même de devoir faire vos preuves.$b$bL\'élément de la terre guidera votre destinée, et fera partie intégrante de votre être, si vous y êtes $gprêt:prête;. Mais vous devez vous présenter devant la terre elle-même. Si vous êtes $gprêt:prête;, vous verrez des choses dont seuls les chamans ont connaissance.$b$bCherchez les traqueurs corrompus au nord, et prenez-leur deux de leurs sabots. Revenez alors me voir, et nous parlerons de votre avenir.",
    ["O"] = "Apporter 2 Sabots de traqueur corrompu à Canaga Earthcaller dans la Vallée des épreuves.",
    ["T"] = "L\'appel de la terre",
  },
  [1517] = {
    ["D"] = "Un sapta est une boisson qui lie notre esprit aux éléments. Imprégnée de magie, cette potion permet aux esprits forts de percevoir les éléments comme nul autre ne le peut. Votre esprit contactera l\'élément pour lequel le sapta a été créé, que seuls de rares chamans peuvent confectionner.$B$BIl faut toujours boire le sapta dans un endroit sacré, jamais ailleurs, ne l’oubliez pas.$B$BCherchez le rocher des esprits au sud-ouest d\'ici, au bout du chemin secret. Buvez-y votre sapta et... et vous verrez.",
    ["O"] = "Trouver le Rocher des esprits et boire le Sapta de terre.",
    ["T"] = "L\'appel de la terre",
  },
  [1518] = {
    ["D"] = "Prenez ce quartz brut et apportez-le à Canaga Earthcaller. Il attestera de notre rencontre. Il créera un totem pour vous, et ce caillou en sera le cœur. Aussi petit qu\'il puisse paraître, vous comprendrez avec le temps que la taille importe peu, et que même la plus petite des choses peut survivre à bien d\'autres.$b$bSoyez $gpatient:patiente;, $n. Restez $gfort:forte;. Et recherchez la sagesse. C\'est ce que la terre attend de vous. À la fin, vous ne ferez plus qu\'un avec elle. Respectez-la comme elle vous respecte.",
    ["O"] = "Apporter le Quartz brut à Canaga Earthcaller dans la Vallée des épreuves.",
    ["T"] = "L\'appel de la terre",
  },
  [1519] = {
    ["D"] = "Il est temps à présent, jeune $C. Votre corps s’est aguerri et votre esprit est aussi endurant que la terre. Je le vois, vous possédez déjà la sagesse.$B$BLa terre guide notre peuple, et nous parle, de la même manière qu\'elle vous parlera lorsque le temps sera venu. Mais vous devez être $gmis à l\'épreuve:mise à l\'épreuve;. Si vous êtes $gprêt:prête;, alors vous verrez des choses que nous seuls connaissons.$B$BPartez à la recherche des chamans Dos-hirsute dans leur ravin à l\'est, et procurez-vous le baume qu\'ils utilisent lors de leurs rituels. Ne revenez me voir que lorsque vous en aurez suffisamment pour votre propre potion.",
    ["O"] = "Apporter 2 Applications de Baume rituel au Prophète Ravenfeather au Camp Narache.",
    ["T"] = "L\'appel de la terre",
  },
  [1520] = {
    ["D"] = "Un sapta est une boisson qui lie notre esprit aux éléments. Imprégnée de magie, cette potion permet aux esprits ouverts de voir les éléments comme personne d’autre ne peut les voir. Votre esprit sera lié à jamais à l\'élément pour lequel le sapta a été créé, que seuls de rares chamans peuvent confectionner.$B$BIl faut toujours boire le sapta dans un endroit sacré, jamais ailleurs.$B$BCherchez l\'autel du chaman à l\'est d\'ici et buvez-y le sapta.",
    ["O"] = "Trouver le Rocher des kodos et boire le Sapta de terre.",
    ["T"] = "L\'appel de la terre",
  },
  [1521] = {
    ["D"] = "Prenez ce quartz brut et apportez-le à la prophétesse Ravenfeather. Il attestera de notre rencontre. Elle concevra un totem pour vous, et ce caillou en sera le cœur. Aussi petit qu\'il puisse paraître, vous comprendrez avec le temps que la taille importe peu, et que même la plus petite des choses peut survivre aux montagnes.$b$bSoyez $gpatient:patiente;, $n. Restez $gfort: forte;. Et recherchez la sagesse. C\'est ce que la terre attend de vous. À la fin, vous ne ferez plus qu\'un avec elle. Respectez-la comme elle vous respecte.",
    ["O"] = "Apporter le Quartz brut à la prophétesse Ravenfeather au Camp Narache.",
    ["T"] = "L\'appel de la terre",
  },
  [1522] = {
    ["D"] = "Qu\'le feu soit avec vous, $gm\'sieur:m\'dame;. Je vois que vous êtes balèze déjà dans les arts de la terre, et j\'pense que vous êtes $gprêt:prête; à parler à Kranal.$B$BIl vit au nord de la Croisée des Tarides. Il s\'y connaît dans les éléments et peut vous apprendre les méthodes du feu... si vous pensez être $gprêt:prête;.$B$BL\'est facile à trouver, n\'allez pas trop loin, c\'est tout, sinon vous vous retrouvez direct chez les elfes de la nuit.",
    ["O"] = "Trouver Kranal Fiss dans les Tarides.",
    ["T"] = "L\'appel du feu",
  },
  [1523] = {
    ["D"] = "Cela fait longtemps que vous n\'avez pas été $gmis à l\'épreuve:mise à l\'épreuve;, $N. Vous avez montré de la patience jusque-là dans vos études, mais le temps est à présent venu d\'apprendre la destruction et le chaos. L\'élément du feu peut vous appartenir, si vous êtes $gprêt:prête; à en payer le prix.$B$BRecherchez Kranal Fiss dans les Tarides. Il vit dans une petite demeure au nord de la Croisée, mais prenez garde. Si vous atteignez les terres des elfes de la nuit, c\'est que vous êtes $gallé:allée; trop loin.",
    ["O"] = "Trouver Kranal Fiss dans les Tarides.",
    ["T"] = "L\'appel du feu",
  },
  [1524] = {
    ["D"] = "Vous êtes suffisamment $gpuissant:puissante; pour manier le totem de feu, mais cela ne signifie pas que vous êtes $gprêt:prête;.$b$bLe long de la Furie-du-Sud en Durotar, avant la Grande Mer, se trouve un sentier caché menant aux hauteurs les plus élevées de cette terre. Trouvez le chemin menant au sommet et vous découvrirez l\'un de nos autels sacrés, surveillé par Telf Joolam : l\'Autel de la flamme dormante.$b$bApportez-lui cette torche, cela voudra dire que vous tentez d\'apprivoiser le feu. Rapportez-la-moi lorsqu\'elle sera enflammée au brasero de l’autel et je veillerai à ce que le totem de feu vous soit remis.",
    ["O"] = "Apporter la Torche de la flamme dormante à Telf Joolam en Durotar.",
    ["T"] = "L\'appel du feu",
  },
  [1525] = {
    ["D"] = "Mais vous n\'êtes pas là pour recevoir un cours d\'histoire, n\'est-ce pas ? Vous êtes $gvenu:venue; pour la flamme éternelle et le totem de feu.$B$BLa première étape consiste à créer un sapta et à vous lier à la flamme.$B$BJe peux m\'en occuper si vous m\'apportez deux choses. La première est du goudron de feu, un objet simple à se procurer et généralement porté par les lanceurs de sorts Tranchecrin dans les Tarides de l\'est.$B$BIl sera plus dangereux de se procurer la seconde : une bourse de réactifs que vous trouverez sur l\'un des sectateurs de la Lame ardente. Ils sont dans une caverne au-dessus des ravins, au nord-est de Tranchecolline.",
    ["O"] = "Apporter 1 Goudron de feu et 1 Bourse de réactifs à Telf Joolam en Durotar.",
    ["T"] = "L\'appel du feu",
  },
  [1526] = {
    ["D"] = "Le sapta de feu est terminé. Il est temps pour vous de vous confronter à la flamme. Utilisez le sapta à l\'autel et rendez-vous au sommet de la montagne pour affronter la manifestation. Elle n\'attaquera pas tant que vous ne serez pas $gprêt:prête;, mais elle sait que vous pouvez la voir.$B$BFrappez bien, et frappez vite, $N, car sa puissance grandira si vous tardez à la tuer. Quand vous l\'aurez vaincue, placez ses braises dans le brasero et allumez la torche.",
    ["O"] = "Vaincre la Manifestation de feu mineure, et placer sa Braise rougeoyante dans le brasero, en haut de l\'Autel de la flamme éternelle.",
    ["T"] = "L\'appel du feu",
  },
  [1527] = {
    ["D"] = "Tenant fermement la torche, vous vous détournez du brasero et embrassez Durotar du regard. Vous sentez la fièvre battre dans votre esprit tandis que le vent avive les flammes qui dansent furieusement.$B$BVous prenez le chemin du retour vers les Tarides, pour trouver Kranal Fiss.",
    ["O"] = "Apporter la Torche de la flamme éternelle à Kranal Fiss dans les Tarides.",
    ["T"] = "L\'appel du feu",
  },
  [1528] = {
    ["D"] = "L\'eau est dans votre avenir, $gmon pote:m\'dame;. Je vois qu\'vous vous en sortez déjà bien avec les arts du feu, et je pense que vous êtes $gprêt:prête; à parler à Islen.$b$bElle vit le long de la côte des Tarides. Si vous suivez le fleuve Furie-du-Sud en direction du sud, vous pouvez pas la rater – cherchez l\'grand requin ! Elle maîtrise les arts des éléments et peut vous apprendre ceux de l\'eau... si vous pensez être $gprêt:prête;.$b$bElle est facile à trouver, n’allez pas trop loin, c\'est tout. Elle se trouve juste après Ratchet.",
    ["O"] = "Trouver Islen Waterseer dans les Tarides.",
    ["T"] = "L\'appel de l\'eau",
  },
  [1529] = {
    ["D"] = "Cela fait longtemps que vous n\'avez pas été $gmis à l\'épreuve:mise à l\'épreuve;, $n. Vous avez montré de la patience jusque-là dans vos études, mais le temps est à présent veu pour vous d\'apprendre la pureté de l\'eau.$b$bCherchez Islen Waterseer dans les Tarides. Elle se trouve le long de la côte est, au sud du fleuve Furie-du-Sud et de Ratchet. Vous la trouverez dans une hutte de pêcheur. Bonne chance à vous, $n.",
    ["O"] = "Trouver Islen Waterseer dans les Tarides.",
    ["T"] = "L\'appel de l\'eau",
  },
  [1530] = {
    ["D"] = "S\'il y a une leçon à apprendre de l\'eau, la voici : l\'eau est la renaissance.$b$bSon pouvoir coule, érode et rend propre toute chose. Ses propriétés de guérison et de ressourcement sont incomparables, mais seulement lorsqu\'elle est pure.$b$bLorsque vous comprendrez que l\'eau, quand elle est polluée, peut dévaster tout ce qu\'elle touche, vous apprécierez à quel point cette ressource est vitale.$b$bSi vous souhaitez le sapta de l’eau, trouvez Brine loin dans les Tarides du sud. Sa maison est constamment la cible des hurans.",
    ["O"] = "Trouver Brine dans les Tarides du sud.",
    ["T"] = "L\'appel de l\'eau",
  },
  [1531] = {
    ["D"] = "Y faut que vous y alliez, là. Préparez-vous à montrer qu\'vous êtes digne de r\'cevoir vot\' totem d\'air. Mais déjà, vous d\'vez trouver Prate Cloudseer aux Mille pointes. Elle vous dira comment obtenir vot\' totem d\'air.$b$bPassez au sud par les Tarides, puis aux Mille pointes, à l\'est. Vous la trouverez cachée dans un trou, avec son p\'tit copain. Haha, ne lui dites pas qu\'je la salue pas, elle aimerait pas trop.",
    ["O"] = "Trouver Prate Cloudseer aux Mille pointes.",
    ["T"] = "L\'appel de l\'air",
  },
  [1532] = {
    ["D"] = "Les vents de Kalimdor se hâtent pour vous rencontrer et vous serez bientôt $gcompté:comptée; parmi nos plus puissants frères et sœurs. La fierté m\'emplit de vous voir arriver à ce stade, et de savoir votre avenir si prometteur.$b$bL\'air est difficile à contrôler, mais j\'ai foi en vos capacités à surmonter tous les obstacles.$b$bPartez pour l\'extrême sud des Tarides et entrez aux Mille pointes. C\'est là, loin à l\'est, que vous trouverez Prate Windseer. Elle vous enseignera.",
    ["O"] = "Trouver Prate Cloudseer aux Mille pointes.",
    ["T"] = "L\'appel de l\'air",
  },
  [1534] = {
    ["D"] = "Prenez cette outre et enfoncez-vous au cœur de la forêt luxuriante que les elfes de la nuit appellent Ashenvale. C\'est à l\'extrême nord des Tarides.$b$bUne fois dans la forêt, allez à l\'ouest. Le long des montagnes qui séparent les Serres-Rocheuses d\'Ashenvale, au-delà du lac Mystral, vous trouverez un lieu sacré : les ruines de Chimétoile. Une petite fontaine jaillit au centre du lac.$b$bApportez-moi un échantillon de l\'eau de cette fontaine. Ce sera le dernier ingrédient que vous devez obtenir avant que je ne vous renvoie à Islen.",
    ["O"] = "Remplir l\'Outre bleue vide aux Ruines de Chimétoile à Ashenvale et la rapporter à Brine dans les Tarides.",
    ["T"] = "L\'appel de l\'eau",
  },
  [1535] = {
    ["D"] = "Pour trouver la compréhension et obtenir le sapta, vous devez trouver les eaux les plus pures. Tandis que vous rassemblerez les échantillons pour moi, observez les lieux qui vous entourent. Soyez $gattentif:attentive; aux créatures qui ont besoin d\'eau, et qui la protègent. Voyez comment elle affecte les forêts et les déserts. Soyez témoin de la manière dont la substance même qui maintient la vie force les créatures à ôter la vie.$b$bNous commencerons par une tâche simple : en bas de ma hutte, vous trouverez un bassin de ce précieux liquide. Remplissez-en cette outre et revenez me voir.",
    ["O"] = "Remplir l\'Outre marron vide au trou d’eau, derrière la hutte de Brine, puis rapportez-lui la, dans les Tarides.",
    ["T"] = "L\'appel de l\'eau",
  },
  [1536] = {
    ["D"] = "Il y a longtemps, j\'ai suivi quelques compagnons jusqu\'aux contreforts d\'Hillsbrad. Nous nous sommes défendus contre des humains tenaces qui s\'y sont établis et nous nous sommes retrouvés dans une petite ville occupée par les Réprouvés : Moulin-de-Tarren.$b$bC\'est là que j\'ai découvert une chose des plus étranges : en pleine ville, un petit puits offrait une belle eau fraîche. Je ne crois même pas que ces pestiférés comprennent sa valeur. C\'est une goutte de pureté au cœur d’une mer de malédiction.$b$bRemplissez cette outre avec cette eau et rapportez-la-moi.",
    ["O"] = "Remplir l\'Outre rouge vide au puits de Moulin-de-Tarren et retourner voir Brine dans les Tarides.",
    ["T"] = "L\'appel de l\'eau",
  },
  [1558] = {
    ["D"] = "Forgefer, c\'est là où vivent tous les nains et les gnomes. Pas loin, il y a un endroit qui s\'appelle le Loch Modan. Les nains y ont construit un ÉNORME barrage. Ils disent que c\'est l\'une des choses les plus grandes jamais construites. Ouah !$B$BVous pouvez m\'emmener au Loch Modan pour voir le barrage, s\'il vous plaît ? Je crois que c\'est le barrage de Forme... de Formepierre. Il est énorme, c\'est sûr. J\'aimerais bien regarder par-dessus la grande chute d\'eau. Je promets que je ne me pencherai pas trop au-dessus de la rambarde !",
    ["O"] = "Conduire l\'orphelin au barrage de Formepierre au Loch Modan. Vous devez le conduire au milieu du barrage pour qu\'il puisse voir par-dessus l\'immense chute d\'eau.",
    ["T"] = "Le barrage de Formepierre",
  },
  [1559] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La recette de la bombe éclairante",
  },
  [1560] = {
    ["D"] = "Je suis complètement perdu !$b$bMa femme, Torta, m\'a envoyé chercher le dîner et j\'ai dû prendre le mauvais chemin quelque part.$b$bAuriez-vous l\'amabilité de me guider vers Torta ? Elle m\'attend au sud de Port Gentepression.",
    ["O"] = "Montrer à Tooga le chemin du retour vers sa femme, Torta.",
    ["T"] = "La quête de Tooga",
  },
  [1578] = {
    ["D"] = "Les montagnards de Loch Modan combattent les troggs, et cela les occupe beaucoup. Ils ont constamment besoin d\'être approvisionnés en armures et en nouvelles armes.$B$BC\'est pourquoi mon collègue Thorvald est ici, pour maintenir l\'équipement des montagnards en bon état. Aussi, si vous pouviez fabriquer des haches en cuivre et des ceintures en anneaux de cuivre, puis les apporter à Thorvald, il vous en serait reconnaissant. Il se peut même qu\'il partage une partie de son savoir avec vous.$B$BThorvald est basé dans la tour de garde méridionale de Loch Modan.",
    ["O"] = "Apporter 6 Haches en cuivre et 6 Ceintures en anneaux de cuivre à Thorvald dans la Tour de garde méridionale du Loch Modan.",
    ["T"] = "Rééquiper le front",
  },
  [1579] = {
    ["D"] = "Je suis venu à Auberdine parce que c\'est calme. Je veux créer dans le calme. Ironforge est décidément trop bruyante après une nuit passée à boire !$b$bAlors j\'ai pris un bateau pour Auberdine et j’ai bu un petit peu trop pendant le voyage. J\'ai laissé tomber ma boîte de rouages électriques par-dessus bord ! J\'en ai besoin !$b$bJe ne sais pas exactement où je l’ai laissée tomber, mais si vous pêchez quelque part dans l\'océan, il se peut que vous ayez de la chance...",
    ["O"] = "Apporter 8 Rouages électriques à Wizbang Cranktoggle à Auberdine.",
    ["T"] = "Rouages électriques",
  },
  [1580] = {
    ["D"] = "Nous savons que vous êtes $gchanceux:chanceuse;, mais à quel point l\'êtes-vous vraiment, $n ?$b$bUne nuit, je marchais sur les routes de Sombrivage. J\'essayais de trouver une bonne rivière ou un bon lac pour tester mon nouvel appeau à canard explosif et à propulsion automatique, et je pense que j\'étais un peu... parti. Je me suis endormi et je me suis réveillé des heures plus tard à Auberdine. Mes cheveux étaient roussis et je ne trouvais plus mon sac d\'Electropellers ! J\'avais dû le laisser tomber dans l\'eau !$b$bIl me faut ces Electropellers ! Peut-être pourriez-vous les pêcher quelque part dans un lac ou dans un cours d\'eau quelconque à Sombrivage...",
    ["O"] = "Apporter 12 Electropellers à Wizbang Cranktoggle à Auberdine.",
    ["T"] = "Pêche aux Electropellers",
  },
  [1581] = {
    ["D"] = "Notre magasin d\'herbes et d\'alchimie est un succès, mais mon mari passe tellement de temps à préparer ses potions qu\'il ne nous en reste plus pour nous deux.$B$BPouvez-vous nous aider ?$B$BSi vous m\'apportiez une provision d\'élixirs déjà faits, alors je serais heureuse de commercer avec vous. J\'ai ici quelques herbes rares qui pourraient vous intéresser.",
    ["O"] = "Apporter 6 Elixirs de force du lion et 2 Elixirs de défense mineure à Syral Bladeleaf à Dolanaar.",
    ["T"] = "Les élixirs pour les Bladeleaf",
  },
  [1582] = {
    ["D"] = "Nous autres à Darnassus sommes très occupés, et tout particulièrement les artisans. Les jeunes aventuriers souhaitant affronter les dangers de la nature ont constamment besoin d\'équipement.$B$BSi vous pouviez m\'aider à honorer mes commandes de cuir, alors je pourrais peut-être vous apprendre une technique avancée d\'artisanat du cuir.",
    ["O"] = "Apporter 1 Gant en cuir estampé, 5 Bottes en cuir estampé et 5 Capes en cuir estampé à Lotherias à Darnassus.",
    ["T"] = "Veste de Moonglow",
  },
  [1598] = {
    ["D"] = "Vous ici ! $n, c\'est ça ?$b$bÊtes-vous $gsûr:sûre; de votre vocation de démoniste, car si vous ne pouvez pas maîtriser ce que vous invoquez, vous finirez $gaspiré:aspirée; dans le Néant.$b$bJe dois vous enseigner les arts du démoniste, mais je dois d\'abord m\'assurer que vous êtes suffisamment $gfort:forte;.$b$bUn novice qui étudiait à l\'abbaye s\'est enfui pour rejoindre la Confrérie défias. Pourquoi est-ce que cela m\'intéresse ? Eh bien, il a volé certains livres importants.$b$bL\'un d\'entre eux se trouve être un livre que je voulais pour mon usage personnel : « la Puissance du Vide ». Volez-le pour moi, il est caché dans sa tente dans les vignes.",
    ["O"] = "Retrouver la Puissance du Vide pour Drusilla La Salle.",
    ["T"] = "Le Tome dérobé",
  },
  [1599] = {
    ["D"] = "Vous devez être $gle nouveau:la nouvelle; démoniste dont tout le monde parle. Quelqu\'un doit vous apprécier pour qu’on m\'arrache à mes recherches et me demande de vous enseigner.$b$bNe vous inquiétez pas, je vais vous consacrer du temps.$b$bLe plus simple des sorts d\'invocation est celui du diablotin. Mais avant que je vous le livre, vous devez prouver que vous avez les capacités physiques et mentales nécessaires.$b$bApportez-moi trois Charmes de plume que portent les novices Frostmane de la caverne au sud-ouest.",
    ["O"] = "Apporter 3 Charmes de plume à Alamar Grimm à Anvilmar.",
    ["T"] = "Les débuts",
  },
  [1618] = {
    ["D"] = "Nous les nains ne sommes pas les seuls à nous battre. Nos alliés humains sont très occupés également.$b$bLes Carmines subissent régulièrement des attaques des orcs Blackrock, et leur réserve d\'équipement est constamment très basse. Si vous vouliez leur venir en aide, je suis sûr que leur forgeron permanent, Verner Osgood, vous apprendrait un ou deux trucs.",
    ["O"] = "Tormus Deepforge veut que vous apportiez 4 Ceintures runiques en cuivre et 4 Grands maillets en cuivre à Verner Osgood, dans les Carmines.",
    ["T"] = "Le rééquipement des Carmines",
  },
  [1638] = {
    ["D"] = "Vous avez montré que vous êtes $gun guerrier:une guerrière; capable, $n. Mais vous êtes encore jeune et avez beaucoup à apprendre.$b$bParlez à Harry Burlguard, il est généralement à la taverne du « Cochon siffleur », dans la vieille ville de Stormwind. Harry est un vétéran et un excellent Maître. Il pourrait vous montrer quelques passes d’armes qui vous aideraient à vivre plus vieux.",
    ["O"] = "Parler à Harry Burlguard.",
    ["T"] = "L\'entraînement du guerrier",
  },
  [1639] = {
    ["D"] = "Ouais, je peux vous enseigner ! Mais d\'abord je veux voir ce que vous savez déjà.$b$bVous voyez ce poivrot là-bas au bout du bar ? Bartleby ? Je veux que vous lui preniez sa chope.$b$bHa ! Et si vous connaissez Bartleby, alors vous savez qu\'il ne vous la donnera pas gentiment.",
    ["O"] = "Parler à Bartleby.",
    ["T"] = "Bartleby le poivrot",
  },
  [1640] = {
    ["D"] = "Vous voulez quoi ? Vous êtes $gfou:folle; !$b$bLa seule façon pour vous d\'obtenir cette chope serait de la prendre entre mes doigts froids et morts…",
    ["O"] = "Battre Bartleby, puis lui parler.",
    ["T"] = "Battre Bartleby",
  },
  [1641] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Le Tome de divinité",
  },
  [1642] = {
    ["D"] = "En toutes choses, un paladin doit réfléchir la Lumière qui nourrit notre force. Tendre vers la pureté, se battre pour l\'un des nôtres, ne veut pas dire que nous nous battons pour une déité. Nous nous battons pour être bons dans toutes nos actions.$B$BBien que nous soyons appelés pour frapper le Mal en ces temps difficiles, vous ne devez jamais oublier que c\'est en aidant les autres que vous vous démarquerez véritablement des citoyens d\'Azeroth. La compassion, la patience, la bravoure, ces valeurs sont aussi importantes pour un paladin que la force dans la bataille.$B$BPrenez-en conscience, et ne l\'oubliez pas.",
    ["O"] = "Parler à Duthorian Rall à Stormwind.",
    ["T"] = "Le Tome de divinité",
  },
  [1643] = {
    ["D"] = "Votre tâche, à présent, $n, est de trouver quelqu\'un ici, à Stormwind, qui a besoin de votre aide. Quelqu\'un ayant un cœur et des motivations purs. Peut-être une personne dévouée aux autres. Peut-être quelqu\'un ayant besoin d\'un guide. En tout cas, cette personne doit être pétrie de bonnes intentions et doit vouloir compter sur votre gentillesse.$b$bAgissez au mieux et revenez me voir après. Alors, nous discuterons de la voie du paladin et de la Lumière.",
    ["O"] = "Chercher Stephanie Turner dans le Quartier commerçant de Stormwind.",
    ["T"] = "Le Tome de divinité",
  },
  [1644] = {
    ["D"] = "Qu’ils soient bénis. La plupart ont perdu leur famille dans les attaques du Fléau, ces dernières années.$B$BMon mari, John, collecte des matériaux qui me servent à confectionner des chemises et des pantalons pour les enfants, ce que je n’ai malheureusement pas eu le temps de faire dernièrement.$B$BMais peut-être voudriez-vous m\'aider ? Oh, merci mille fois, $N.$B$BSi vous pouviez m’apporter de l’étoffe de lin pour que je puisse en faire des vêtements pour les enfants, je vous en serai reconnaissante.",
    ["O"] = "Apporter 10 morceaux d\'étoffe de lin à Stephanie Turner à Stormwind.",
    ["T"] = "Le Tome de divinité",
  },
  [1645] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Le Tome de divinité",
  },
  [1646] = {
    ["D"] = "En toutes choses, un paladin doit réfléchir la Lumière qui nourrit notre force. Tendre vers la pureté, se battre pour l\'un des nôtres, ne veut pas dire que nous nous battons pour une déité. Nous nous battons pour être bons dans toutes nos actions.$b$bBien que nous soyons appelés pour frapper le Mal en ces temps difficiles, vous ne devez jamais oublier que c\'est en aidant les autres que vous vous démarquerez véritablement des citoyens d\'Azeroth. La compassion, la patience, la bravoure, ces valeurs sont aussi importantes pour un paladin que la force dans la bataille.$b$bPrenez-en conscience, et ne l\'oubliez pas.",
    ["O"] = "Parler à Tiza Battleforge à Ironforge.",
    ["T"] = "Le Tome de divinité",
  },
  [1647] = {
    ["D"] = "Votre tâche, $n, consiste à trouver quelqu\'un ici à Ironforge qui ait besoin d\'aide. Quelqu\'un ayant un cœur et des motivations purs. Peut-être une personne dévouée aux autres. Peut-être quelqu\'un ayant besoin d\'un guide. En tout cas, cette personne doit être pétrie de bonnes intentions et doit vouloir compter sur votre gentillesse.$b$bAgissez au mieux et revenez me voir après. Alors, nous discuterons de la voie du paladin et de la Lumière. Je resterai ici, au temple, dans l\'attente de votre retour. D\'autres paladins auront besoin que je les guide, également.",
    ["O"] = "Chercher John Turner à la périphérie d\'Ironforge.",
    ["T"] = "Le Tome de divinité",
  },
  [1648] = {
    ["D"] = "Ma femme et moi nous nous occupons d\'un orphelinat à Stormwind, principalement pour des enfants qui ont perdu leurs parents au cours des attaques du Fléau cette dernière décennie. Je passe à Ironforge à peu près tous les mois afin de collecter des vivres offerts par des citoyens d’ici, et j’essaie de convaincre certaines associations de soutenir notre action vis-à-vis des enfants.$b$bCe voyage a été horriblement lent, et pas très productif.$b$bSi je pouvais simplement trouver des pièces de lin pour que ma femme puisse coudre quelques vêtements, je pourrais enfin envoyer quelque chose à la maison.",
    ["O"] = "Apporter 10 morceaux d\'étoffe de lin à John Turner à Ironforge.",
    ["T"] = "Le Tome de divinité",
  },
  [1649] = {
    ["D"] = "La bravoure et toutes les vertus qui vont avec sont comme les minerais rares : pour leur valeur vous devez les chérir, mais vous devez aussi prendre le temps de les entretenir, de les polir. Quand vous les associez judicieusement, vous obtenez un alliage résistant, qui peut résister même aux coups les plus violents.$B$BCette force, votre foi, votre bravoure doivent être éprouvées. Vous devez apprendre à ne jamais perdre la foi et comprendre qu\'avec le pouvoir de la Lumière à vos côtés, vous pourrez surmonter plus d’obstacles que la plupart des gens.",
    ["O"] = "Parler à Duthorian Rall à Stormwind.",
    ["T"] = "Le Tome de la bravoure",
  },
  [1650] = {
    ["D"] = "Bien ! Votre engouement est le signe que vous êtes $gprêt:prête; et montre votre enthousiasme à commencer le test. Ainsi soit-il ! $B$BA l\'extrémité de Longrivage, dans la marche de l\'Ouest, une petite maison donne sur l\'océan. C’est la demeure du couple Stilwell. Jordan, un très bon forgeron, est parti à Ironforge voir les nains. Mais il a laissé Daphne, sa femme, seule et sans protection.$B$BDes rapports disent que les Défias ont envahi cette zone : Daphne aura peut-être besoin de protection. Allez là-bas.",
    ["O"] = "Trouver Daphne Stilwell dans la marche de l\'Ouest",
    ["T"] = "Le Tome de la bravoure",
  },
  [1651] = {
    ["D"] = "Je suis sûre qu\'ils seront là dans peu de temps maintenant, préparez-vous.$B$BJ\'ai une arme à la maison. Je vais la chercher et je reviens vous donner un coup de main, mais vous allez devoir faire le plus gros du travail j\'en ai peur.$B$BJe n\'ai aucune idée de leur nombre, mais pour sûr ils seront plus nombreux que la dernière fois.$B$BPriez la Lumière de vous accorder ses faveurs parce que... Vous avez entendu ça ? Je pense qu\'ils arrivent.$B$BNous allons nous replier près de la maison !",
    ["O"] = "Défendez Daphne Stilwell contre les attaques des Défias.$B$BAucun de vous deux ne doit mourir, si vous voulez réussir la quête.$B$BQuand vous aurez réussi, reparlez à Daphne Stilwell.",
    ["T"] = "Le Tome de la bravoure",
  },
  [1652] = {
    ["D"] = "S\'il vous plaît, retournez voir Duthorian et dites-lui que je suis saine et sauve. Et c\'est grâce à vous et à l\'Eglise.$B$BJe vais envoyer un mot à Jordan à Ironforge pour lui dire de ne pas s\'inquiéter. Oh qu\'il me manque ! J\'ai hâte qu\'il revienne.",
    ["O"] = "Parler à Duthorian Rall à Stormwind.",
    ["T"] = "Le Tome de la bravoure",
  },
  [1653] = {
    ["D"] = "Je pense que Jordan voudra vous remercier lui-même, et comme j\'ai besoin de quelqu\'un pour lui porter le message disant que sa femme est en sécurité, ce serait bien que vous le lui apportiez.$B$BJordan a été un paladin de la Lumière avant que son dévouement au métier de son père le fasse changer de voie. Il connaît nos coutumes et il est considéré parmi nos meilleurs fidèles.$B$BIl doit être encore à Ironforge. Dans la journée, il aime travailler hors de l\'enceinte de la ville, sans doute pour profiter du grand air de la montagne.",
    ["O"] = "Parler à Jordan Stilwell à Ironforge",
    ["T"] = "Le test de droiture",
  },
  [1654] = {
    ["D"] = "J\'aimerais vous récompenser, si vous voulez bien l\'accepter ? Ne vous y trompez pas, ma générosité sera tributaire de votre contribution, mais je sens que cette association sera bénéfique pour nous deux.$B$BJe vous fabriquerai une arme à la hauteur de vos actions, un symbole des paladins et de la Lumière, mais vous devez me procurer les composants dont j\'ai besoin pour la forger. J\'ai déjà fait la liste de ce qui est nécessaire, il manquait juste une personne de qualité comme vous pour me les procurer. Qu\'en pensez-vous, $c ?",
    ["O"] = "Utiliser les Notes sur les armes de Jordan, trouver du Bois de chêne de blanchepierre, la Cargaison de Minerai raffiné de Bailor, le Marteau de forge de Jordan et une Gemme de Kor, puis revenir voir Jordan Stilwell à Ironforge.",
    ["T"] = "Le test de droiture",
  },
  [1655] = {
    ["D"] = "Ach, vous m\'avez fait peur $ghomme:femme; ! Vous ne pouviez pas remarquer que j\'étais perdu dans mes pensées ?$B$BQu\'est-ce que c\'est ? La cargaison de Jordan... pour Ironforge. Ahhh, ouais, j\'vois de quoi vous parlez ! Les ogres Mo\'grosh... z\'ont volé la cargaison de minerai d\'mon vieil ami. La caravane venant des Paluns a été attaquée. Le garde qui avait survécu est mort il y a quelques jours : le soigneur n\'est pas arrivé à temps.$B$BSi vous voulez la cargaison, faut que vous alliez au nord-est à travers le Loch. Elle est quelque part par là.",
    ["O"] = "Apporter la Cargaison de minerai de Jordan à Bailor Stonehand au Loch Modan.",
    ["T"] = "Cargaison de minerai de Bailor",
  },
  [1656] = {
    ["D"] = "$N, s\'il vous plaît, je vous en supplie, pouvez-vous m\'aider ? Je n\'ai pas fini ce que j\'ai à faire ici sur le Plateau – je suis au milieu de mon rite de force – et mon père veut que je livre ces fourrures à l\'auberge de Bloodhoof.$B$BJe ne suis pas suffisamment reposée pour finir mon rite et faire ce que mon père m\'a demandé. Cela vous embêterait-il d\'y aller pour moi ?$B$BL\'auberge est agréable et c\'est l\'endroit parfait pour se reposer après un long voyage. Vous en conviendrez lorsque vous l\'aurez vue.",
    ["O"] = "Livrer le Ballot de fourrures à l\'aubergiste Kauth au village Bloodhoof.",
    ["T"] = "Une tâche inachevée",
  },
  [1657] = {
    ["D"] = "Nous allons célébrer la Sanssaint en grande pompe… en en faisant voir de toutes les couleurs à nos ennemis de Southshore !$B$BPrenez l’une de ces boules puantes conçues spécialement pour l’occasion. Elle est remplie d’une substance nauséabonde dont aucun humain - ou aucune bête à l’esprit simple, d’ailleurs - ne peut supporter l’odeur. Il faut la jeter au beau milieu de Southshore pour qu’elle agisse correctement, donc attendez-vous à combattre l’Alliance !$B$BQuand vous aurez accompli cette tâche… cet hommage à notre libération, revenez me voir !",
    ["O"] = "Lancer trois Boules puantes des Réprouvés au milieu de Southshore en Hillsbrad, puis revenez voir la sombre imploratrice Yanka à la Fête de l’homme d’osier, aux Clairières de Tirisfal.",
    ["T"] = "Empuantir Southshore",
  },
  [1658] = {
    ["D"] = "J’ai besoin de personnes courageuses pour se rendre aux clairières de Tirisfal, et voir comment se déroule la Fête de l’homme d’osier. Je ne vais pas vous mentir, aller là-bas sera dangereux : les gardes de faction lors de la fête seront exceptionnellement brutaux. Cependant, nous devons absolument connaître l’ampleur des festivités, et la gravité des problèmes que les Réprouvés nous causeront durant la Sanssaint.$B$BSi vous vous sentez d’attaque, dirigez-vous vers Tirisfal et espionnez les Réprouvés. Revenez me voir avec des informations, et je m’assurerai que vous recevrez une compensation convenable.",
    ["O"] = "Allez voir la Fête de l\'homme d\'osier des Réprouvés dans les clairières de Tirisfal. Revenez voir le sergent Hartman à Southshore quand ce sera fait.",
    ["T"] = "Un intrus à la Fête de l\'homme d\'osier",
  },
  [1659] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [1660] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [1661] = {
    ["D"] = "Encore bonjour, $N. Je suis encore plus fier de vous appeler $gfrère:soeur; qu’avant ; votre dévouement est une bénédiction pour nous tous.$B$BLe temps est venu de vous accorder une grande récompense. Sachez que je vous donne cela gratuitement, mais que plus tard, les défis qu’auront à accomplir les paladins qui voudront réaliser mes quêtes seront autrement plus exigeants.$B$BReparlez-moi plus tard et je vous accorderai une grande faveur.",
    ["O"] = "Parler à Duthorian Rall, à Stormwind.",
    ["T"] = "Le Tome de noblesse",
  },
  [1665] = {
    ["D"] = "Ok, voilà ma tasse. Attendez ! Laissez-moi finir avant... Ok, allez-y !",
    ["O"] = "Apporter la Tasse de Bartleby à Burlguard.",
    ["T"] = "Tasse de Bartleby",
  },
  [1666] = {
    ["D"] = "Vous avez appris quelques passes d\'armes, mais il faudrait utiliser une arme décente. Je parie que si vous parliez au maréchal Haggard il pourrait vous aider.$B$BLe maréchal Haggard habite dans un manoir dans la forêt d\'Elwynn, au Camp de bûcherons du Val d\'est. Il est à la retraite, mais il a accumulé beaucoup d\'équipements quand il était en activité.$B$BDites-lui que vous venez de ma part, et avec un peu de chance il aura une arme en trop à vous offrir.",
    ["O"] = "Parler au maréchal Haggard.",
    ["T"] = "Le maréchal Haggard",
  },
  [1667] = {
    ["D"] = "Burlguard vous a dit de passer ? Oui, j\'ai des armes en trop ici, mais avant vous devez faire quelque chose pour moi...$B$BIl y a quelque temps un scélérat défias appelé Jack Dent-Morte m\'a escroqué. Il faisait sombre et il portait un tabard de Stormwind, j\'ai cru que c\'était le maréchal Dughan. Il m\'a dit qu\'il avait besoin de mon ancienne plaque de Maréchal de Stormwind et je lui ai donnée... Alors il a éclaté de rire et s\'est enfui avant que je puisse l\'attraper !$B$BRapportez-moi ma plaque ! Jack Dent-Morte a probablement un camp secret dans le sud.",
    ["O"] = "Retrouver la Plaque du maréchal Haggard au Camp de Jack Dent-Morte, et la rapporter au maréchal Haggard.",
    ["T"] = "Jack Dent-Morte",
  },
  [1678] = {
    ["D"] = "Si vous voulez rejoindre les rangs des guerriers, vous avez intérêt à être robuste. Je peux vous apprendre de nouvelles passes d\'armes, mais avant prouvez-moi que vous pouvez vous débrouiller contre des adversaires réels.$B$BVejrek est le plus coriace des trolls du coin ; c\'est lui que vous devez affronter. Il a une hutte dans les collines au sud du repaire des Frostmane$B$BRapportez-moi sa tête et je saurai que vous avez du courage.",
    ["O"] = "Apporter la Tête de Vejrek à Muren Stormpike à Ironforge.",
    ["T"] = "Vejrek",
  },
  [1679] = {
    ["D"] = "Bonjour guerrier ! Vous êtes $gvoué:vouée; à un grand avenir, mais il vous reste quelques petits trucs à apprendre et Muren Stormpike est le nain qu\'il vous faut pour vous les enseigner.$B$BVous trouverez Muren dans le Hall des Armes à la Garde militaire d\'Ironforge. Parlez-lui quand vous serez $gprêt:prête; à apprendre ce qu\'est un vrai guerrier.",
    ["O"] = "Parler à Muren Stormpike.",
    ["T"] = "Muren Foudrepique",
  },
  [1680] = {
    ["D"] = "Maintenant que vous avez appris de nouvelles passes, il est temps pour vous d\'avoir une arme digne de ce nom. Parlez à Tormus Deepforge. Il peut vous fabriquer une arme dont vous me donnerez des nouvelles.$B$BVous trouverez Tormus ici, à Ironforge, à la Grande Forge.",
    ["O"] = "Parler à Tormus Deepforge.",
    ["T"] = "Tormus Forge-profonde",
  },
  [1681] = {
    ["D"] = "Mon mentor, Sturgy Ironband, a une base au sud du lac du Lit d\'Helm, à l\'est de Dun Morogh. Il est maître forgeron, et c\'est le meilleur.$B$BMais un jour, un de ses apprentis lui a apporté un étrange minerai. Le maître Ironband l\'a baptisé \"minerai d\'Umbral\" et s\'est empressé de le travailler. Mais au moment où il était sur le point d’aboutir, une bande de nains Sombrefer a attaqué sa base. Ils ont tué tout le monde !$B$BAllez à la Base d’Ironband, $N, vengez la mort du maître, et rapportez-moi une cargaison de Minerai d\'Umbral.",
    ["O"] = "Rapporter du Minerai d\'Umbral à Tormus Deepforge à Ironforge.",
    ["T"] = "Base d\'Ironband",
  },
  [1682] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Armes en fer gris",
  },
  [1683] = {
    ["D"] = "Avant de vous apprendre quoi que ce soit, vous devez me prouver votre résolution.$B$BLe satyre Vorlus Vilehoof a découvert un de nos lointains Puits de lune, au sud-est du Refuge des saisons de Ban\'ethil. Il est en train d’y barboter, souillant l\'eau pure avec sa crasse.$B$BTrouvez Vorlus au Puits de lune et détruisez-le. Ramenez-moi sa corne comme preuve, et je vous entraînerais.$B$BLe chemin vers le Puits de lune est bien caché, $N, restez $gconcentré:concentrée; et alerte, et vous le trouverez.",
    ["O"] = "Rapporter la Corne de Vorlus Vilehoof à Elanaria à Darnassus.",
    ["T"] = "Vorlus Vilsabot",
  },
  [1684] = {
    ["D"] = "Bonjour, $gguerrier:guerrière; ! Vos compétences progressent, mais il y a plus à savoir sur votre métier que vous ne le pensez. Pour continuer à progresser il vous faudra bientôt trouver un Maître.$B$BLa guerrière Elanaria réside à Darnassus, à la Terrasse des guerriers. Elle peut vous former.",
    ["O"] = "Parler à Elanaria.",
    ["T"] = "Elanaria",
  },
  [1685] = {
    ["D"] = "N\'est-ce pas toujours ainsi ? Je vous ai $gcherché:cherchée; partout aujourd\'hui et je n\'ai pas pu vous trouver, et maintenant que je n\'ai plus besoin de vous, vous venez me voir. !$b$bJ\'ai perdu assez de temps à vous chercher, je ne vais donc pas vous faire attendre plus longtemps. Maître Gakin vous convoque pour une autre leçon. Il me semble que vous devriez y aller immédiatement.$b$bVous le trouverez au sous-sol de « l\'Agneau assassiné ». Au fait, pas un mot : je préférerai que l’endroit reste tranquille.",
    ["O"] = "Aller voir Gakin le Tisse-noir dans le quartier des mages de Stormwind.",
    ["T"] = "Convocation de Gakin",
  },
  [1686] = {
    ["D"] = "Vous avez appris beaucoup, $N. Maintenant nous allons vous fabriquer une arme. Une arme de $gguerrier:guerrière;.$B$BPour cela, vous devez aller à Sombrivage.$B$BIl y a bien longtemps, un bateau s\'est brisé sur les rochers près du phare d\'Auberdine, éparpillant ses caisses de minerai d\'Elunite au fond de l\'océan.$B$BTout d\'abord, vous devez vaincre la gardienne de l\'Elunite : l\'Ombre d\'Elura. C\'était le capitaine du bateau, elle s\'est condamnée elle-même à errer au fond de l\'océan pour protéger la cargaison à jamais. Combattez-la, récupérez son médaillon, et remonter toutes les caisses de minerai.",
    ["O"] = "Apporter 8 Caisses de minerai d\'élunite et le Médaillon d\'Elura à Elanaria à Darnassus.",
    ["T"] = "L\'Ombre d\'Elura",
  },
  [1687] = {
    ["D"] = "Des fois, tard le soir, les gouvernantes de l\'orphelinat nous racontent des histoires qui font peur. C\'est GÉNIAL ! Il y en a une à propos d\'un fantôme qui hante un phare dans la marche de l\'Ouest. C\'était un pirate à qui d\'autres pirates ont pris son bateau. Waou !$B$BVous pourriez m\'emmener au phare de la marche de l\'Ouest ? Je veux voir le fantôme du capitaine ! Je resterai près de vous, c\'est promis, je ne m\'enfuirai pas ni rien...",
    ["O"] = "Emmenez l\'orphelin voir le phare sur la côte de la marche de l\'Ouest.",
    ["T"] = "Le phare hanté",
  },
  [1688] = {
    ["D"] = "J\'ai eu une étudiante du nom de Surena Caledon. Elle était là où vous êtes en ce moment, enthousiaste à apprendre la magie des démonistes, et talentueuse. En plus, elle était jeune et belle. Quand j\'y repense, quelle traînée !$b$bCette voleuse s\'est enfuie avec un Defias, Erlan Drudgemoor. Sa perte est négligeable, mais je lui avais offert une Echarpe de Pierre de sang que je veux absolument récupérer.$b$bSa vie m\'importe peu maintenant. Vous la trouverez au Champ de Potirons des Brackwell. Rapportez-moi ce qui m\'appartient.",
    ["O"] = "Retrouver l’Écharpe de Surena pour Gakin le Tisse-noir à Stormwind.",
    ["T"] = "Surena Caledon",
  },
  [1689] = {
    ["D"] = "En retrouvant mon écharpe et en éliminant Surena, vous avez prouvé que vous étiez au moins aussi $gtalentueux:talentueuse; qu\'elle.$B$BCela dit, vous avez terriblement besoin d\'entraînement. Vous pouvez manier les flux magiques, bien sûr, mais être démoniste, c\'est bien plus que cela.$B$BJe vais vous montrer comment contrôler un marcheur du Vide, mais avant, vous devez en invoquer un dans le cercle d\'invocation et le maîtriser avec les armes – physiques et magiques – dont vous disposez. Utilisez la magie que contient l\'écharpe sur le cercle d\'invocation en bas.",
    ["O"] = "Utiliser l’Echarpe de Pierre de sang pour invoquer et soumettre un marcheur du Vide, puis la rapporter à Gakin le Tisse-noir, à Stormwind.",
    ["T"] = "Le lien",
  },
  [1690] = {
    ["D"] = "Vous là-bas ! Il est temps d\'aider la Compagnie des Eaux de Gadgetzan.$B$BLes nomades Bat-le-désert de Tanaris se sont emparés de presque tous les puits ! Si nous ne pouvons pas y avoir accès, notre réserve d\'eau va s\'assécher en un clin d\'oeil. Si vous voulez vous faire bien voir, aidez-nous à faire la loi contre ces nomades !$B$BAllez à l\'est d\'ici et abattez dix bandits et dix larrons Bat-le-désert, puis revenez immédiatement me faire votre rapport. La justice n\'attend pour aucun homme... gobelin... bon, enfin bref, allez-y !",
    ["O"] = "Abattre 10 Bandits et 10 Larrons Bat-le-désert à l\'est de Gadgetzan, puis revenir au rapport auprès de l\'ingénieur en chef Bilgewhizzle.",
    ["T"] = "Châtier les Bat-le-désert",
  },
  [1691] = {
    ["D"] = "Nous voulons que vous attaquiez les rangs les plus forts des nomades Bat-le-désert. Vous avez fait du bon travail jusque-là, mais pour reprendre ces puits, il faut affronter le moins possible de ces nomades !$B$BCette fois, il faut que vous éliminiez des voleurs, des assassins et des mages de l\'ombre. Ils se trouvent à l\'est et au sud-est d\'ici.$B$BJe ne vais pas vous mentir, ils sont coriaces. Faites cela pour nous, et nous pourrons bientôt reprendre ce qui est nôtre.",
    ["O"] = "Tuer 10 Mages de l\'ombre Bat-le-désert, 8 Voleurs Bat-le-désert et 6 Assassins Bat-le-désert pour l\'ingénieur en chef Bilgewhizzle à Gadgetzan.",
    ["T"] = "Continuer à châtier les Bat-le-désert",
  },
  [1692] = {
    ["D"] = "J\'ai mis l\'Elunite que vous avez récupérée dans une caisse. Portez-la au Forgeron Mathiel, il fabriquera votre arme avec.$B$BMathiel est au sud, de l\'autre côté de ce bâtiment.",
    ["O"] = "Apporter la Caisse d\'élunite au Forgeron Mathiel.",
    ["T"] = "Forgeron Mathiel",
  },
  [1693] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Armes en élunite",
  },
  [1698] = {
    ["D"] = "Yorus Barleybrew a lancé un défi à tous les guerriers de l\'Alliance. Il attend à l\'Auberge de Lakeshire, dans les Carmines, et promet une grande récompense à celui qui passera l\'épreuve.",
    ["O"] = "Parler à Yorus Barleybrew.",
    ["T"] = "Yorus Brasselorge",
  },
  [1699] = {
    ["D"] = "J\'ai appelé cette épreuve le \"défi de Rethban\". Pour passer l\'épreuve, tout ce que vous avez à faire c\'est d\'entrer dans les Cavernes de Rethban, jusqu\'à la première fourche. Facile ! Sauf qu\'il y a certaines conditions...$B$BVous devez être de retour dans moins d\'une heure.$B$BEt il y a un tas de gnolls des Carmines sur le chemin.$B$BAvant de commencer, il faut que vous preniez une lampée de ma boisson personnelle : du Barleybrew Scalder !$B$BAlors qu\'en dites-vous ? Les Cavernes de Rethban sont dans les collines, au nord de Lakeshire. Êtes-vous $gprêt:prête; à relever le défi de Rethban ?",
    ["O"] = "Entrer dans les Cavernes de Rethban jusqu\'à la première fourche, et revenir voir Barleybrew dans le temps imparti.$B$BVous ne devez pas mourir.",
    ["T"] = "Le défi de Rethban",
  },
  [1700] = {
    ["D"] = "J\'ai pris des notes sur les techniques que j\'ai mises au point en faisant votre armure, et je dois en faire parvenir une copie à Grimand Elmore. Il est ici dans le quartier des nains, au Magasin d\'armes.$B$BEt attention quand vous lui donnerez mes notes. Il est extrêmement fier de ses compétences, et il n\'aimera pas l\'idée que je puisse lui apprendre quelque chose...",
    ["O"] = "Porter les Notes de Furen à Grimand Elmore.",
    ["T"] = "Grimand Elmore",
  },
  [1701] = {
    ["D"] = "Alors je vous ai donné un bon bouclier, mais on ne va pas s\'arrêter là. Je connais le secret pour fabriquer une Cotte de mailles extrêmement résistante. C\'est difficile à faire et demande des matériaux très rares, mais si vous voulez faire le travail sur le terrain, alors je vous en fabriquerais une.$B$BQu’en dites-vous, $N ? Vous relevez le défi ? Je vais donc vous donner la liste des éléments dont j\'ai besoin et où les trouver.",
    ["O"] = "Réunir les matériaux dont Furen Longbeard a besoin et lui apporter à Stormwind.",
    ["T"] = "Une cotte de mailles en acier trempé",
  },
  [1702] = {
    ["D"] = "Vous avez montré vos talents en relevant le défi de Rethban, $N. Et je respecterai ma parole. Je vais vous donner votre récompense...$B$BSauf qu\'elle est à Stormwind.$B$BParlez à mon ami, Furen Longbeard, dans le quartier des nains de Stormwind. C\'est le meilleur fabriquant de boucliers que vous rencontrerez, et il me doit une faveur.$B$BTenez, apportez-lui ce tonneau de Barleybrew Scalder et demandez-lui un bouclier. Il vous en donnera un bon.",
    ["O"] = "Apporter le Tonneau de Scalder à Furen Longbeard.",
    ["T"] = "Fabricant de bouclier",
  },
  [1703] = {
    ["D"] = "J\'ai pris des notes sur les techniques que j\'ai mises au point en faisant votre armure, et je dois en faire parvenir une copie au Forgeron Mathiel. Il vit à Darnassus, assez loin d\'ici, mais je suis sûr qu\'il sera reconnaissant de pouvoir apprendre ce que j\'ai appris.",
    ["O"] = "Apporter les Notes de Furen à Mathiel.",
    ["T"] = "Mathiel",
  },
  [1704] = {
    ["D"] = "J\'ai pris des notes sur les techniques que j\'ai mises au point en faisant votre armure, et je dois en faire parvenir une copie à Klockmort Spannerspan. C\'est un Forgeron gnome d’Ironforge et quand il lira mes découvertes, je parie qu\'il s\'arrachera la barbe.",
    ["O"] = "Apporter les Notes de Furen à Klockmort Spannerspan.",
    ["T"] = "Pléthorloge Cléventail",
  },
  [1705] = {
    ["D"] = "Je déteste l\'admettre, mais Furen a fait une découverte impressionnante. Mais pour être sûr je dois l\'essayer moi-même sur une armure. Si vous me procurez le matériel, $N, alors vous pourrez en avoir une.$B$BPour faire cette armure, il me faut des échantillons de sang ardent et du roc ardent.$B$BLe seul endroit où je sais en trouver est le bois de la Pénombre. Les worgens Plaie-de-nuit basés dans la mine du Destin de Roland ont du sang ardent, et leur chef, Videssac, devrait avoir le Roc.$B$BQue la chance soit avec vous, $N !",
    ["O"] = "Rapporter 20 Fioles de sang ardent et 1 Roc ardent à Grimand Elmore à Stormwind.",
    ["T"] = "Le sang ardent",
  },
  [1706] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "L\'armure de Grimand",
  },
  [1707] = {
    ["D"] = "Salutations, $N. Mon nom est Luglunket, et nous avons un travail important à vous confier si vous vous sentez d\'attaque ! Houzza !$B$BIl n\'y a rien qui me ferait plus plaisir, en tant qu\'Opérateur du robinet expérimenté, que de récupérer les outres de Bat-le-désert que vous trouverez dans le désert. C\'est notre eau de toute façon : les nomades l\'ont juste volée ! La Compagnie des Eaux de Gadgetzan vous offre un paquet pour cinq outres que vous ramenez. Encore plus important : venir en aide à Gadgetzan, c\'est l\'aider à se venir en aide !",
    ["O"] = "Apporter 5 Outres de Bat-le-désert à l\'Opérateur de robinet Luglunket à Gadgetzan.",
    ["T"] = "Prime de la poche à eau",
  },
  [1708] = {
    ["D"] = "Je veux essayer la nouvelle technique de Furen Longbeard sur une de mes pièces d\'armure. Et parce que vous avez aidé Furen et qu\'il m\'a aidé, je vous offrirai l\'armure que j’aurai fabriquée !$B$BPour faire la pièce, j\'ai besoin d\'un corail spécial, du corail torride. Il pousse à un seul endroit : le long des côtes au sud du port de Menethil dans les Paluns.$B$BApportez-moi suffisamment de corail torride et je pourrai fabriquer l\'armure. Et dépêchez-vous, $N. Je suis impatient de commencer !",
    ["O"] = "Apporter 20 Coraux torrides à Klockmort Spannerspan à Ironforge.",
    ["T"] = "Le corail de fer",
  },
  [1709] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La création de Klockmort",
  },
  [1710] = {
    ["D"] = "Mon collègue, Furen Longbeard, a fait une découverte que je suis impatient d\'essayer. Je veux la tester sur une de mes nouvelles armures, car je pense que les techniques de Furen me permettraient d’améliorer la qualité de mes produits.$B$BVoulez-vous m\'aider ? Si vous me ramenez de quoi fabriquer l\'armure, alors j\'en ferai une pour vous.$B$BJ\'ai besoin de coquilles d\'œuf desséchées au soleil des wyvernes Aile-fière qui résident au Haut-perchoir, dans les Mille pointes. Cherchez les coquilles au pied de leurs nids, et revenez quand vous en aurez assez.",
    ["O"] = "Apporter 20 Coquilles desséchées au soleil à Mathiel à Darnassus.",
    ["T"] = "Coquilles desséchées au soleil",
  },
  [1711] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "L\'armure de Mathiel",
  },
  [1712] = {
    ["D"] = "J\'ai passé un temps considérable à observer ce site. J\'observe… Je verrai peut-être tomber le Grand Cyclonien ! Mais je n\'ai toujours pas trouvé un champion pour combattre le Seigneur des vents.$B$BPeut-être est-ce vous ?$B$BProuvez-le moi. Rassemblez les éléments dont j\'ai besoin pour invoquer un Cyclonien, et je saurai que vous êtes $gun grand guerrier:une grande guerrière;. Peut-être pouvez-vous le battre et récupérer son cœur de tourbillon.$B$BSi c\'est le cas, alors je transmettrai le pouvoir du cœur dans une arme que je vous donnerai.$B$BVoilà le parchemin qui liste les objets dont j\'ai besoin.",
    ["O"] = "Apporter les objets du Parchemin de Bath\'rah à Bath\'rah la Vigie des vents à Alterac.",
    ["T"] = "Cyclonien",
  },
  [1713] = {
    ["D"] = "Je suis prêt à faire mon rituel pour invoquer un Cyclonien. Quand il apparaîtra, vous devez l\'abattre et lui prendre son Cœur de tourbillon. Je l\'utiliserai pour faire une arme qui possédera les pouvoirs du Cyclonien.$B$B$N, êtes-vous $gprêt:prête; à affronter le Maître des vents ?",
    ["O"] = "Rapporter le Cœur de tourbillon à Bath\'rah la Vigie des vents.",
    ["T"] = "L\'invocation",
  },
  [1714] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Essence d\'exil",
  },
  [1715] = {
    ["D"] = "Hum ! Dans le temps, tous les démonistes de Khaz Modan étaient entraînés par les maîtres de Gnomeregan, mais maintenant que nous avons été forcés de nous réfugier dans ce trou à rats qu\'est Ironforge, et que Magni a décidé de nous surveiller, nous sommes forcés d\'envoyer les novices étudier à Stormwind.$b$bHeureusement, Gakin n\'est pas un empoté, vous pourrez donc continuer à recevoir un enseignement, mais cela m\'exaspère !$b$bEnfin, vous trouverez Gakin au sous-sol de « l\'Agneau assassiné » à Stormwind.",
    ["O"] = "Aller voir Gakin le Tisse-noir dans le quartier des mages de Stormwind.",
    ["T"] = "L\'Agneau assassiné",
  },
  [1716] = {
    ["D"] = "Une succube est une dévoreuse d\'âmes, une destructrice de cœurs et une dominatrice d\'esprits. Elle ne connaît rien d\'autre que le Mal.$B$BC\'est cette créature que vous allez apprendre à invoquer et à contrôler, mais on ne peut pas l\'amener dans notre monde sans un appât.$B$BAvec un symbole d\'amour comme appât, elle répondra sûrement à votre appel. Alors vous pourrez la maîtriser, et en faire votre esclave.$B$BIl va cependant être difficile de trouver quelque chose de cette nature. Vous devriez parler à Takar le Prophète. Vous le trouverez dans les Tarides de Kalimdor près du Camp Taurajo.",
    ["O"] = "Parler à Takar le Prophète dans les Tarides.",
    ["T"] = "La dévoreuse d\'âmes",
  },
  [1717] = {
    ["D"] = "$n ! Je ne pensais pas vous trouver ici, mais on m\'a demandé de vous chercher.$b$bIl est temps pour vous de reprendre l\'entraînement, vous devez aller voir Gakin la prochaine fois que vous passerez à Stormwind. Il vous attend.",
    ["O"] = "Aller voir Gakin le Tisse-noir dans le quartier des mages de Stormwind.",
    ["T"] = "Convocation de Gakin",
  },
  [1718] = {
    ["D"] = "Votre renommée en tant que $gguerrier:guerrière; s\'accroît, $N. Il est temps de vous mesurer à l\'un de vos pairs.$B$BIl existe une île près des côtes des Tarides, au sud de Ratchet : l\'île de la Dispute. Les guerriers s’y retrouvent.$B$BS’y retrouvent et s’affrontent.$B$BParlez au chef des guerriers de l\'île, Klannoc Macleod. S’il pense que vous le méritez, alors il vous guidera plus avant sur la voie du guerrier.$B$BPour atteindre l\'île de la Dispute, cherchez des bancs de sable à faible profondeur le long de la Côte des marchands.",
    ["O"] = "Parler à Klannoc Macleod.",
    ["T"] = "L\'île mortelle",
  },
  [1719] = {
    ["D"] = "Si vous voulez que je sois votre Maître, vous devez d\'abord passer la Dispute. C\'est une épreuve que nous, sur l\'île de la Dispute, aimons faire passer aux guerriers qui pensent être forts, et si vous réussissez ce test alors nous saurons que vous l\'êtes.$B$BPour commencer, marchez à travers cette foule là-bas, et allez jusqu\'à l\'âtre. Dites à Twiggy Flathead que vous êtes $gprêt:prête;, il vous enverra des partenaires de combat.$B$BBattez-les tous, et il appellera le Grand Will. Tuez le Grand Will, et revenez me voir.",
    ["O"] = "Tuer le Grand Will et parler à Klannoc Macleod sur l\'île de la Dispute.",
    ["T"] = "La Dispute",
  },
  [1738] = {
    ["D"] = "En prévision de votre arrivée, j\'ai utilisé ma vision pour regarder à travers le monde et à travers le temps. J\'ai trouvé ce que vous cherchez.$b$bLors de la guerre des Anciens, deux amoureux sont tombés ensemble lors d\'une bataille. Là où ils sont morts, un arbre connu sous le nom de cœur du bois poussa, symbole de leur amour éternel.$b$bCe sera assez pour attirer une succube. Vous le trouverez dans les ruines d\'Ordil\'Aran, dans la partie nord-ouest de la forêt d\'Ashenvale.",
    ["O"] = "Trouver le Cœur du bois en Ashenvale et le porter à Gakin le Tisse-noir dans le quartier des mages de Stormwind.",
    ["T"] = "Le cœur du bois",
  },
  [1739] = {
    ["D"] = "En utilisant le noyau du cœur du bois, vous serez en mesure de jeter le sort dans le cercle d\'invocation qui se trouve dans la crypte en bas afin d\'ouvrir un portail d\'où la succube arrivera.$B$BAttention, $N, les succubes arrivent à contrôler les esprits les plus forts.$B$BUne fois que vous l\'aurez vaincue et renvoyée dans le néant, revenez me voir pour que je vous apprenne comment l\'appeler et la contrôler.",
    ["O"] = "Utiliser le Noyau du cœur du bois pour invoquer et soumettre une succube, puis rapporter le Noyau de cœur du bois à Gakin le Tisse-noir, à l\'Agneau assassiné.",
    ["T"] = "Le lien",
  },
  [1740] = {
    ["D"] = "Quand les démons de la Légion sont tombés, leurs esprits furent emprisonnés dans des orbes et distribués à leurs mages et leurs démonistes.$B$BBeaucoup furent apportés en Azeroth par leurs serviteurs. Même fragmentés, ils gardent les pouvoirs du démon emprisonné à l\'intérieur.$B$BL\'orbe de Soran\'ruk a été brisé en deux morceaux, l\'un a été fragmenté pour être utilisé par les acolytes du Marteau du crépuscule dans les Profondeurs de Brassenoire. L\'autre, le plus gros morceau, est en possession d\'un sorcier dans le donjon d\'Ombrecroc. Retrouvez les fragments et je pourrai réparer l\'orbe.",
    ["O"] = "Trouver 3 Fragments de Soran\'ruk et 1 Grand Fragment de Soran\'ruk et les rapporter à Doan Karhan dans les Tarides.",
    ["T"] = "L\'Orbe de Soran\'ruk",
  },
  [1758] = {
    ["D"] = "Il faudra plus qu\'un simple cercle d\'invocation pour attirer un chasseur corrompu et le défier.$b$bMes trois acolytes et moi-même allons vous aider à créer les cercles magiques. Vous aurez besoin d\'un exemplaire du Tome de la Cabale, qui a été perdu depuis bien des années. L’ouvrage a été écrit par le premier groupe de démonistes pour invoquer seul un chasseur corrompu.$b$bKrom Stoutarm peut peut-être vous aider. Ce démoniste nomade passe son temps à accumuler des textes anciens et traîne parfois dans la bibliothèque à Ironforge.",
    ["O"] = "Parler à Krom Stoutarm à Ironforge.",
    ["T"] = "Tome de la Cabale",
  },
  [1778] = {
    ["D"] = "Je vais devoir rentrer chez moi le plus rapidement possible. Heureusement, Stephanie fait de même pour rentrer à Stormwind. J\'ai hâte de la revoir. A bientôt, $c.",
    ["O"] = "Parler à Tiza Battleforge à Ironforge.",
    ["T"] = "Le Tome de divinité",
  },
  [1779] = {
    ["D"] = "Je veux que vous preniez ceci, $N. Cela s\'appelle le Symbole de vie.$B$BVous avez montré que vous étiez charitable et $gpatient:patiente;, maintenant je veux que vous me montriez votre pouvoir et vos talents au combat en tant qu\'adepte.$B$BMuiredon, mon mari, a rencontré récemment des nains Sombrefer, et il s\'en est sorti de justesse. Il est à Ironforge, aux bons soins d’un de nos prêtres.$B$BPrenez le Symbole et parlez-lui. Si vous pouvez l\'aider, alors nous nous reparlerons bientôt. Que la Lumière soit avec vous !",
    ["O"] = "Prendre le Symbole de vie et parler à Muiredon Battleforge à Ironforge.",
    ["T"] = "Le Tome de divinité",
  },
  [1780] = {
    ["D"] = "J\'amènerai ça aux enfants dès aujourd\'hui. Merci beaucoup, $c. Il est rare de nos jours qu\'$gun homme:une femme; de votre trempe se montre dans cette ville, mais vous avez prouvé que les paladins à Stormwind ne tourneront jamais le dos à ceux qui sont dans le besoin.$B$BAdieu.",
    ["O"] = "Parler à Duthorian Rall à Stormwind.",
    ["T"] = "Le Tome de divinité",
  },
  [1781] = {
    ["D"] = "Je veux que vous preniez ceci, $N. Cela s\'appelle un Symbole de vie.$B$BVous avez montré que vous étiez charitable et $gpatient:patiente;, et maintenant il est temps de savoir si vous avez la puissance nécessaire pour contrôler un des plus grands dons de la Lumière.$B$BMon ami Gazin a croisé récemment des Défias à Elwynn près de la Veillée des héros – il s\'en est sorti de justesse. Il est près de l\'autel en train de se faire soigner par un de nos prêtres.$B$BPrenez le Symbole et parlez-lui. Si vous pouvez l\'aider, alors nous nous reparlerons bientôt. Que votre chemin soit éclairé par la Lumière !",
    ["O"] = "Porter le Symbole de vie à Gazin Tenorm à Stormwind.",
    ["T"] = "Le Tome de divinité",
  },
  [1782] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "L\'armure de Furen",
  },
  [1783] = {
    ["D"] = "J\'espionnais des nains Sombrefer qui se terrent au sud du lac du Lit d\'Helm. Nous savions qu\'ils n\'étaient pas là pour rien, alors Narm et moi nous nous sommes rapprochés – assez près pour voir leur chef donner des ordres à ses hommes.$B$BNarm et moi nous avons tenté d\'en tuer quelques-uns, mais ils se sont révélés plus résistants que nous le pensions, et beaucoup plus nombreux. Narm m\'a dit de partir et de ramener du renfort, donc voilà pourquoi je suis là.$B$BNarm a besoin d\'aide, et je ne me reposerai pas tant qu\'il n\'en n\'aura pas trouvé.$B$BAllez-vous m\'aider, $N ?",
    ["O"] = "Prendre le Symbole de vie et ressusciter Narm Faulk dans Dun Morogh.",
    ["T"] = "Le Tome de divinité",
  },
  [1784] = {
    ["D"] = "Avant de tomber, Muiredon et moi avions presque vaincu un des espions Sombrefer près de cette petite maison. Nous avons vu le capitaine leur passer des ordres, et nous avons cherché à le tuer pour récupérer ces ordres.$B$BCertaines personnes au conseil d\'Ironforge ne croient pas à la présence des Sombrefer à Dun Morogh. En récupérant un de ces scripts, nous aurions non seulement la preuve de leur présence ici, mais nous aurions aussi une idée de ce qu\'ils trament.$B$BRécupérez un de ces scripts et rapportez-le à Muiredon, $N.",
    ["O"] = "Récupérer un Script des nains Sombrefer près du lac du Lit d\'Helm et le rapporter à Muiredon Battleforge à Ironforge.",
    ["T"] = "Le Tome de divinité",
  },
  [1785] = {
    ["D"] = "J\'peux déjà voir l\'visage d\'ma femme s\'illuminer de fierté. Vous devriez bouger vot\'postérieur tout de suite et aller la voir. J\'pense qu\'elle aura des choses à vous dire.$B$BLa Lumière soit avec vous, $c ! Je vous reverrai sur l\'champ de bataille contre le Fléau et ces damnés Sombrefer quand le temps viendra.",
    ["O"] = "Parler à Tiza Battleforge à Ironforge.",
    ["T"] = "Le Tome de divinité",
  },
  [1786] = {
    ["D"] = "Henze et moi sommes allés à la Veillée des héros, une île au milieu du lac du Cairn au nord-est d\'Elwynn. Nous avions entendu dire que des Défias avaient infiltré nos frontières et nous voulions le prouver aux conseillers du roi.$B$BNous en avons surpris en possession de documents, et nous les avons attaqués. Malheureusement, nous avons été rapidement submergés par des mages.$B$BHenze les a retenus pendant que je m\'enfuyais... C’était cinglé de sa part, mais il était brave.$B$BIl est mort à présent. Pourriez-vous nous aider, $N ?",
    ["O"] = "Apporter le Symbole de vie et ressusciter Henze Faulk à Elwynn.",
    ["T"] = "Le Tome de divinité",
  },
  [1787] = {
    ["D"] = "Nous aurons le temps de parler du Symbole plus tard. Ce qui importe c\'est de récupérer les papiers que les Défias avaient sur eux. Si nous pouvons mettre la main dessus, nous serons en mesure de prouver que la Confrérie a infiltré Elwynn et nous aurons une idée de ce qu\'elle trame.$B$BJe ne suis pas assez remis pour combattre maintenant, $N. Pensez-vous pouvoir récupérer $gseul:seule; ces papiers dans l\'île ?$B$BSi vous pouvez les récupérer, apportez-les à Gazin et dites-lui que je vais bien.",
    ["O"] = "Retrouver un Script Défias sur les Sorciers-voleurs défias d\'Elwynn et le rapporter à Gazin Tenorm à Stormwind.",
    ["T"] = "Le Tome de divinité",
  },
  [1788] = {
    ["D"] = "Je vais porter le Script aux conseillers du roi après que Duthorian y aura jeté un coup d\'oeil, ensuite allez parler à Duthorian. Il voudra sûrement vous féliciter !$B$BMerci encore, $N. J\'espère que l\'on se reverra. Je suis fier de vous avoir comme $gallié:alliée;... et comme $gami:amie;.",
    ["O"] = "Parler à Duthorian Rall à Stormwind.",
    ["T"] = "Le Tome de divinité",
  },
  [1789] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Le Symbole de vie",
  },
  [1790] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Le Symbole de vie",
  },
  [1791] = {
    ["D"] = "À présent que vous avez appris la posture berserker, vous aurez besoin d\'une belle arme avec laquelle l\'utiliser.$B$BAllez parler à Bath\'rah la Vigie des vents, un troll ermite qui vit entre les montagnes d\'Alterac et les Maleterres de l\'est. C\'est un fabricant d\'armes très versé dans son métier et un puissant chaman.$B$BPour le trouver, allez à la rivière qui coule le long du côté est de Moulin-de-Tarren. Remontez-la vers le nord. Bath\'rah campe près des ruines jonchant la rive est de la rivière.",
    ["O"] = "Parler à Bath\'rah la Vigie des vents.",
    ["T"] = "La vigie des vents",
  },
  [1792] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Arme Tourbillon",
  },
  [1793] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Le Tome de la bravoure",
  },
  [1794] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Le Tome de la bravoure",
  },
  [1795] = {
    ["D"] = "Si vous êtes suffisamment $gpréparé:préparée;, alors nous pouvons commencer. Laissez-moi vous expliquer la procédure.$B$BJe vais commencer par créer un simple cercle d\'invocation. Quand j\'aurai fini, mes acolytes vont utiliser leurs bâtonnets de canalisation pour créer un cercle majeur.$B$BLisez alors l\'incantation du Tome de la Cabale, et le chasseur corrompu apparaîtra. Faites attention, $N. Un chasseur corrompu est un ennemi puissant, et j\'aimerais que vous gardiez la tête sur les épaules.",
    ["O"] = "Utiliser le Tome de la Cabale pour invoquer et soumettre un Chasseur corrompu, puis rapporter le Tome de la Cabale à Strahad Farsan, à Ratchet.",
    ["T"] = "Le lien",
  },
  [1796] = {
    ["D"] = "Le premier objet que je vais vous aider à confectionner est une Robe. Vous rassemblerez ses composants pour moi, et nous les assemblerons ensemble. Après quoi, si tant est que vous aurez réussi, vous aurez les premiers des nombreux objets qui vous aideront dans vos luttes.$B$BMais pour commencer, vous devez avoir des Robes d\'Arcana confectionnées. Un tailleur peut vous les faire, ou peut-être que vous pourriez en acheter à quelqu\'un d\'autre, mais cela m\'impressionnerait que vous les confectionniez vous même. Quelle que soit la manière dont vous vous les procurerez, apportez-moi les robes, et nous commencerons.",
    ["O"] = "Apporter des Robes d’Arcana à Menara Voidrender dans les Tarides.",
    ["T"] = "Les composants de la robe sanglante dorée enchantée",
  },
  [1798] = {
    ["D"] = "Il est temps pour vous d\'apprendre à invoquer un chasseur corrompu, $n. Je n\'ai pas ni composants ni les objets nécessaires ici pour vous l\'enseigner, donc adressez-vous à Strahad Farsan, un membre de notre communauté.$b$bIl réside dans le port gobelin de Ratchet. Je vais lui envoyer un message pour lui annoncer votre venue.",
    ["O"] = "Parler à Strahad Farsan dans les Tarides.",
    ["T"] = "À la recherche de Strahad",
  },
  [1799] = {
    ["D"] = "Bien, alors commençons.$B$BAllez d\'abord parler à mes acolytes à l’intérieur et choisissez l’une des voies qu’ils vous proposeront de suivre... Ils vous en diront davantage eux-mêmes.$B$BEnsuite, vous devrez voyager en Désolace et prendre un Orbe infernal sur l\'un des démonistes du convent de Mannoroc. C’est cet orbe que vous purifierez avec l’aide d’un mage du nom de Tabetha, au sud du marécage d\'Âprefange. Elle vous aidera en souvenir de notre histoire commune.$B$BNe revenez pas avant d’avoir accompli les deux tâches que j’ai requises auprès de vous.",
    ["O"] = "Parler aux acolytes de Menara dans la tour qui domine Ratchet et choisir de suivre l’une de leurs voies.$B$BEnsuite, apporter un Orbe infernal à Tabetha, dans le marécage d\'Âprefange.",
    ["T"] = "Fragments de l\'Orbe d\'Orahil",
  },
  [1800] = {
    ["D"] = "La gouvernante de l\'orphelinat, à la maison, nous raconte de grandes histoires de bataille et de sagesse, si nous sommes gentils et que nous avons fait nos corvées. Une histoire raconte la chute de Lordaeron, et comment le maléfique Arthas a assassiné son père pour devenir le chef.$B$BIl n\'avait pas d\'honneur, $N ! Quand je serai grand, je serai un puissant guerrier de la Horde, et ma lame sera guidée par l\'honneur - jamais par le mal !$B$BVous voulez bien m\'emmener voir la salle du trône, s\'il vous plaît ? C\'est juste avant l\'endroit où il faut descendre pour aller à Fossoyeuse.",
    ["O"] = "Emmenez votre protégé à la vieille salle du trône de Lordaeron, qui se trouve juste avant l\'endroit où il faut descendre pour aller à Fossoyeuse.",
    ["T"] = "La salle du trône de Lordaeron",
  },
  [1801] = {
    ["D"] = "Il va falloir plus qu\'un simple cercle d\'invocation pour appeler le chasseur corrompu que vous allez affronter.$b$bMes trois acolytes et moi allons vous assister pour créer les cercles magiques. Vous avez besoin d’un exemplaire du Tome de la Cabale, qui a été perdu depuis bien des années. Il a été écrit par le premier groupe de démonistes pour invoquer seul un chasseur corrompu.$b$bPeut-être que Jorah Annison peut vous aider. En ce moment, il est employé comme cartographe à Undercity, mais il en connaît plus que quiconque sur les textes anciens.",
    ["O"] = "Parler à Jorah Annison à Undercity.",
    ["T"] = "Tome de la Cabale",
  },
  [1802] = {
    ["D"] = "Il a été perdu, mais j\'ai une petite idée d’où il se trouve.$b$bIl n’en existe qu’un seul exemplaire, en très mauvais état : déchiré à plusieurs endroits et en grande partie arraché de la reliure. La rumeur dit que cette partie serait entre les mains des centaures des Mille pointes.$b$bL’autre partie a été perdue sur la côte ouest de Southshore, alors qu’elle était envoyée par bateau au port de Menethil. Elle n\'est jamais arrivée à destination.$b$bEn échange de ces informations, tout ce que je demande c\'est que vous le trouviez, et que vous me l\'apportiez afin de le voir.",
    ["O"] = "Retrouver le Tome moisi et le Manuscrit en lambeaux pour Krom Stoutarm à Ironforge.",
    ["T"] = "Tome de la Cabale",
  },
  [1803] = {
    ["D"] = "Ah ! Je vois que vous avez les mêmes goûts de démoniste que moi. Vraiment, vouloir récupérer le Tome de la Cabale est une noble intention. Saviez-vous qu\'il était autrefois à Lordaeron ? Mais il a été abîmé, presque la moitié des pages ont été arrachées. Qui a pu faire une chose pareille ?$b$bUne rumeur circule à propos de cette moitié perdue : on dit qu’elle serait entre les mains des centaures des Mille pointes.$b$bLe reste a été envoyé à Ironforge, mais a été perdu lors de son transport sur la côte ouest de Southshore.",
    ["O"] = "Retrouver le Tome moisi et le Manuscrit en lambeaux pour Jorah Annison à Undercity.",
    ["T"] = "Tome de la Cabale",
  },
  [1804] = {
    ["D"] = "Ah ! C\'est difficile de le laisser partir. Mais vous l\'avez trouvé et vous avez des choses importantes à faire avec. S’il vous plaît, dites bien à Strahad que j\'en veux une copie !$B$BPendant que vous étiez $gparti:partie;, il vous a envoyé un message. Les acolytes qui vont participer à l\'invocation ont besoin de trois Bâtonnets de canalisation, vous devez les trouver.$B$BIl ne sait peut-être pas où les chercher, mais moi si ! Les derniers à en avoir utilisé étaient des orcs du clan Dragonmaw dans les Paluns, leurs magiciens doivent en posséder…",
    ["O"] = "Apporter le Tome recomposé et 3 Bâtonnet de canalisation à Strahad Farsan à Ratchet.",
    ["T"] = "Tome de la Cabale",
  },
  [1805] = {
    ["D"] = "C\'est l\'un des textes les plus intéressants que j\'ai parcouru au cours de ces dernières années. Ici, il n\'y a pas grand-chose, à part des livres de géographie, que j\'ai déjà tous lus... Strahad m\'en enverra peut-être une copie, quand il le pourra... $b$bÀ propos, il vous fait dire que pour remplir votre mission, il vous faudra trois bâtonnets de canalisation. $b$bPour vous remercier de m\'avoir laissé regarder ce texte, laissez-moi vous confier un secret : vous trouverez ces bâtonnets sur les orcs du clan Dragonmaw dans les Paluns.",
    ["O"] = "Apporter le Tome recomposé et 3 Bâtonnets de canalisation à Strahad Farsan à Ratchet.",
    ["T"] = "Tome de la Cabale",
  },
  [1806] = {
    ["D"] = "Pourquoi n\'irions-nous pas à la forge pour commencer ? J\'ai préparé votre arrivée depuis quelque temps déjà et tout devrait être prêt.$B$BAprès cela, vous serez mieux $gpréparé:préparée; que jamais pour défendre la Lumière, je vous le promets.$B$BJe dois vous avouer, $N, que je suis un peu envieux de votre réussite. Mais sachez que de pouvoir participer à ma manière à cette réussite me remplit d\'aise.",
    ["O"] = "Attendre que Jordan Stilwell finisse de forger votre arme.",
    ["T"] = "Le test de droiture",
  },
  [1818] = {
    ["D"] = "J\'ai entendu dire que Dillinger le Garde noir avait un travail pour quelqu\'un comme vous. Si vous voulez prouver votre valeur aux forces de la Dame noire, alors présentez-vous devant Dillinger immédiatement.$B$BLe poste de Dillinger le Garde noir est à la frontière ouest de Brill, près du cimetière.",
    ["O"] = "Parler au Garde noir Dillinger à Brill.",
    ["T"] = "Parler à Dillinger",
  },
  [1819] = {
    ["D"] = "Lorsque le Fléau s\'est abattu sur les Clairières de Tirisfal, nous avons capturé l\'un de ses serviteurs pour l\'étudier. C\'était un esprit libre, qui se nommait Ulag le Fendeur. Pourtant, malgré sa libre conscience, le roi-liche avait gardé son emprise sur lui.$B$BNous espérions trouver un moyen de le délivrer de cette influence, mais nous avons échoué. Il est temps de le libérer, maintenant... $B$BLibérez-le et tuez-le.$B$BEn actionnant le déclencheur de la Pierre du Crâne devant le Mausolée qui se trouve au nord, vous briserez le sceau magique qui le lie.$B$BAprès... Il faudra frapper vite et fort, $N !",
    ["O"] = "Tuer Ulag le Fendeur, puis parler au Garde noire Dillinger.",
    ["T"] = "Ulag le Fendeur",
  },
  [1820] = {
    ["D"] = "Vous avez prouvé votre valeur à la Dame noire, à présent vous pourrez peut-être la prouver à l\'un de ses sujets.$B$BColeman Farthing cherche un guerrier pour être son bras vengeur contre les ennemis des Réprouvés.$B$BVous le trouverez à « la Taverne des Pendus » dans l\'est de Brill.",
    ["O"] = "Parler à Coleman Farthing.",
    ["T"] = "Parler à Coleman",
  },
  [1821] = {
    ["D"] = "J\'ai servi la famille Agamand tout le long de ma vie et ils ont été la cause de ma mort ! S\'ils sont maintenant les esclaves du roi-liche, ce n\'est que justice ! $B$BÀ présent, je dois m\'approprier les derniers vestiges de leur honneur perdu. Dans leur crypte, je veux que vous voliez leur héritage familial : une épée, une hache, une dague et une masse, et que vous me les apportiez. $B$BLa Crypte de la famille Agamand se trouve à la lisière nord des Moulins d\'Agamand, sous la protection d\'un Fléau errant et des Agamands morts.",
    ["O"] = "Rapporter à Coleman Farthing la Hache de la famille Agamand, l\'Epée de la famille Agamand, la Masse de la famille Agamand et la Dague de la famille Agamand.",
    ["T"] = "L\'héritage des Agamand",
  },
  [1822] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Une arme en héritage",
  },
  [1823] = {
    ["D"] = "Vous êtes un guerrier compétant à la renommée croissante. Peut-être êtes-vous $gprêt:prête; pour l\'épreuve de Ruga Ragetotem ? Si c\'est le cas, alors allez trouver Ruga au Camp Taurajo dans les Tarides.$B$BIl cherche des guerriers qui accepteraient de passer son épreuve.",
    ["O"] = "Parler à Ruga Ragetotem.",
    ["T"] = "Parler à Ruga",
  },
  [1824] = {
    ["D"] = "Pour passer l\'épreuve, vous devez voyager vers le sud jusqu\'au Champ des géants. Là-bas, vous trouverez un grand nombre d\'insectes rampants. Ils sont récemment arrivés dans les Tarides et je pense que cela ne présage rien de bon.$B$BApportez-moi leurs antennes mobiles, et ne perdez pas de temps, $N. Ces antennes ne remuent pas longtemps. Apportez-les-moi dans le temps alloué, et vous aurez passé l\'épreuve.",
    ["O"] = "Apporter 5 Antennes mobiles à Ruga Ragetotem au Camp Taurajo.",
    ["T"] = "L\'épreuve du Champ des géants",
  },
  [1825] = {
    ["D"] = "Thun\'grim Firegaze, un forgeron orc de grande renommée, a entendu parler de vos exploits au service de la Horde. Il voudrait vous confectionner un équipement d\'armure et vous propose d\'aller en discuter immédiatement avec lui.$B$BThun\'grim campe à l\'est de la Croisée dans les Tarides.",
    ["O"] = "Parler à Thun\'grim Firegaze dans les Tarides.",
    ["T"] = "Parler à Thun\'grim",
  },
  [1838] = {
    ["D"] = "J\'ai entendu parler de votre force et de votre courage, et je voudrais faire pour vous une armure complète qui vous aidera à mesure que vos exploits passeront à la postérité.$B$BMais faire une armure est une véritable quête en soi $N, et je vais avoir besoin de votre aide et de celle d\'autres forgerons de la Horde.$B$BTenez, prenez ce parchemin. J\'ai passé de nombreuses nuits à lutter avec mes yeux presque aveugles pour écrire tout ce dont j\'ai besoin pour l\'armure. Apportez-moi ce qui est noté sur ce parchemin et je créerai pour vous la première pièce de l’ensemble.",
    ["O"] = "Apporter à Thun\'grim Firegaze 15 Lingots de Fer cendré, 10 Azurites en poudre, 10 Barres de Fer et une Fiole de Phlogiston.",
    ["T"] = "Armure de brutalité",
  },
  [1839] = {
    ["D"] = "Ula\'elek, le forgeron troll, va façonner pour vous les gantelets de brutalité. Il demeure en Durotar, au Village de Sen\'jin, avec ses frères Darkspear, et il attend votre arrivée.",
    ["O"] = "Parler à Ula\'elek en Durotar.",
    ["T"] = "Ula\'elek et les Gantelets de brutalité",
  },
  [1840] = {
    ["D"] = "Orm Stonehoof est très respecté pour son habileté. Il va fabriquer pour vous le Camail de brutalité.$B$BCherchez-le à Thunder Bluff, près du bassin du grand totem central de la cité.",
    ["O"] = "Parler à Orm Stonehoof à Thunder Bluff.",
    ["T"] = "Orm Stonehoof et le Camail de brutalité",
  },
  [1841] = {
    ["D"] = "On dit qu\'avant de mourir, Velora Nitely était l\'un des plus grands forgerons d\'armures de Lordaeron. La mort, semble-t-il, n\'a pas affecté ses talents. Allez voir Velora : elle fabriquera vos Jambières de brutalité.$B$BVelora officie à Undercity de Lordaeron, dans le quartier des Marchands.",
    ["O"] = "Parler à Velora Nitely à Undercity.",
    ["T"] = "Velora Nitely et les Jambières de brutalité",
  },
  [1842] = {
    ["D"] = "Je peux vous faire une paire de gantelets de brutalité, et ils seront porteurs d\'une grande magie, mais pour cela, vous devez d\'abord m\'apporter cette magie. $B$BLes satyres parcourent le nord-est d\'Ashenvale à cause du pouvoir de la forêt. Ils se nourrissent de sa pureté, ils aspirent son innocence comme des sangsues et la corrompent. Ceux d\'entre eux ont des sabots fendus et abîmés. Ils ont volé plus de magie à la forêt que les autres. $B$BCes sabots détiennent un grand pouvoir. J\'en aurai besoin pour façonner vos gantelets.",
    ["O"] = "Rapporter 7 Sabots fendus de satyre à Ula\'elek au village de Sen\'jin en Durotar.",
    ["T"] = "Les sabots de satyre",
  },
  [1843] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Gantelets de brutalité",
  },
  [1844] = {
    ["D"] = "Je veux bien vous faire un casque, mais vous devez d\'abord me montrer que vous en êtes digne.$B$BBlottie sur les flancs nord-ouest des Serres-Rocheuses se trouve le Val calciné. C\'était autrefois un endroit verdoyant, mais il est désormais souillé et étouffé sous la cendre. Les créatures qui y vivent encore en sont folles de douleur et de désespoir.$B$BC\'est là que je vous envoie, $N.$B$BVous devez aller au Val calciné et trouver une matriarche chimère. Tuez-la, libérez-la de sa misère. Apportez-moi sa corne galvanisée comme preuve et je vous ferai votre casque.",
    ["O"] = "Apporter une Corne galvanisée à Orm Stonehoof à Thunder Bluff.",
    ["T"] = "La corne de chimère",
  },
  [1845] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Camail de brutalité",
  },
  [1846] = {
    ["D"] = "Pour faire les Jambières de brutalité, j\'ai besoin de quelques solides tibias. Les orcs Dragonmaw des Paluns ont de jolis tibias bien épais. Je vais en avoir besoin.$B$BMais ils doivent être vraiment solides. Prenez soin de tester les tibias que vous ramasserez. Si vous pouvez les casser à mains nues, c\'est qu\'ils ne sont pas assez solides pour que je puisse travailler avec.",
    ["O"] = "Apporter 8 Solides tibias de Dragonmaw à Velora Nitely, dans Undercity.",
    ["T"] = "Les tibias de Dragonmaw",
  },
  [1847] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Jambières de brutalité",
  },
  [1848] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Le haubert de brutalité",
  },
  [1858] = {
    ["D"] = "Je vous le laisse pour ouvrir la sacoche de Tazan et me rapporter son contenu. Le mécanisme de verrouillage est complexe, ce serait sans doute plus facile si vous trouviez la clé.$B$BComme elle n\'était pas sur le corps de Tazan, je ne peux que supposer qu\'il a confié la clé à quelqu\'un... Oh oui ! C\'est ça ! Il avait un aide nommé Gamon. Un ivrogne, vous le trouverez probablement à la taverne. Peut-être que vous pourrez lui prendre la clé en douceur.",
    ["O"] = "Voler la Clé de Tazan puis l\'utiliser pour ouvrir sa Sacoche et livrer son contenu à Therzok dans la Faille de l\'ombre à Orgrimmar.",
    ["T"] = "La Main brisée",
  },
  [1859] = {
    ["D"] = "Parlez moins fort, $n ! Ouais, je sais qui vous êtes ! On m\'a fait un topo.$b$bD\'après ce qu\'on entend dire de vous, vous vous débrouillez au combat et vous employez la ruse à l\'occasion.$b$bJ\'ai raison ?$b$bDe toute façon, vous devriez aller voir Therzok à Orgrimmar. Il a quelque chose à vous proposer.",
    ["O"] = "Aller voir Therzok dans la Faille de l\'ombre à Orgrimmar.",
    ["T"] = "Therzok",
  },
  [1860] = {
    ["D"] = "Nous, les mages, avons une tâche à accomplir. Une tâche essentielle pour la sécurité de Stormwind et de la forêt.$B$BJennea Cannon vous donnera plus de détails. Elle étudie dans le Sanctuaire du Sorcier, dans le quartier des mages de Stormwind. Hâtez-vous d\'aller la voir.",
    ["O"] = "Parler à Jennea Cannon à Stormwind.",
    ["T"] = "Parler à Jennea",
  },
  [1861] = {
    ["D"] = "Les importantes luttes mystiques que le monde a vécues ont fait déborder les courants magiques de leurs canaux autrefois stables. Nous essayons de déterminer quels effets ces changements ont provoqué et certains d\'entre nous craignent le pire !$B$BMais je vous en dirai davantage plus tard. Pour l\'instant, nous avons besoin de vous pour recueillir des informations.$B$BTenez, prenez cette fiole. Allez au bas de la chute d\'eau du lac Miroir, au sud-ouest des portes de Stormwind, et prélevez un échantillon de cette eau. Apportez-le-moi pour que je puisse vérifier si l\'eau est contaminée par la magie.",
    ["O"] = "Apporter un Echantillon d\'Eau du lac Miroir à Jennea Cannon à Stormwind.",
    ["T"] = "Le lac Miroir",
  },
  [1878] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Prime de la poche à eau",
  },
  [1879] = {
    ["D"] = "$N ! Bink, ma collègue d\'Ironforge, vous salue. Elle a une mission qu\'elle ne veut confier qu\'à un mage ! Avez-vous cette qualification, $N ?$B$BDans ce cas, adressez-vous à elle. Elle passe son temps dans le Hall des Mystères de la Garde mystique d\'Ironforge.",
    ["O"] = "Parler à Bink à Ironforge.",
    ["T"] = "Parler à Bink",
  },
  [1880] = {
    ["D"] = "Nous, les mages, nous faisons plus qu\'agiter les doigts et faire quelques jolies étincelles ! Nous contrôlons la magie, le tissu de base de notre monde ! Et cela nécessite beaucoup de soin et de résolution. Et de connaissances... tant de connaissances !$B$BL\'une de mes inventions, le Bidulotron de Youplala, permet de rassembler et de stocker d\'énormes quantités d\'informations. Mais je l\'ai perdu ! Quand nous nous sommes enfuis de notre maison de Gnomeregan, je l\'ai oublié dans un coffre d\'une de nos huttes, à l\'extérieur de la ville, et maintenant, la place est envahie par des gnomes lépreux !$B$BJe vous en prie, $N, récupérez mon Bidulotron !",
    ["O"] = "Rapporter à Bink son Bidulotron de Youplala.",
    ["T"] = "Le Bidulotron de youplala",
  },
  [1881] = {
    ["D"] = "$N, Anastasia Hartwell a une tâche à proposer à quelqu\'un de votre valeur. Allez la voir dans le Quartier de la magie d\'Undercity et préparez-vous à accomplir votre première tâche pour les mages des Réprouvés.",
    ["O"] = "Aller voir Anastasia Hartwell.",
    ["T"] = "Parler à Anastasia",
  },
  [1882] = {
    ["D"] = "Les Maleterres de l\'est sont une aberration. Engendrées par le roi-liche, elles étendent leur magie dans le sol, corrompant et tuant tout ce qui vit. Bien que les forces destructrices de la corruption aient un certain charme, nous, les mages des Réprouvés, pensons que cette façon de faire est chaotique et doit être arrêtée.$B$BMais d\'abord, nous devons mesurer leur étendue dans Tirisfal. Allez à la ferme des Balnir, à l\'est de Tirisfal. Il y a des fleurs qui poussent dans le jardin, des gueules-de-loup. Cueillez-les et revenez me voir.",
    ["O"] = "Apporter des Gueules-de-loup du jardin des Balnir à Anastasia Hartwell dans le Quartier de la magie d\'Undercity.",
    ["T"] = "La ferme des Balnir",
  },
  [1883] = {
    ["D"] = "Un\'thuwa, le mage tribal du village de Sen\'jin, a détecté une perturbation dans les courants magiques de Durotar et demande de l\'aide. Allez voir Un\'thuwa et faites ce que vous pouvez pour l\'aider.$B$BEn retour, de nouveaux secrets vous seront révélés.",
    ["O"] = "Aller voir Un\'thuwa.",
    ["T"] = "Parler à Un\'thuwa",
  },
  [1884] = {
    ["D"] = "Le démoniste Zalazane des îles de l\'Écho a perturbé le flux magique en Durotar. Il l\'a fait par l’intermédiaire de fétiches infectés, des tas de Ju-ju, qu\'il a placés dans ses huttes sur l\'île principale d\'Écho.$B$BCes tas perturbent la faune et la flore et accroissent le pouvoir des sorciers et des créatures infernales qui se cachent dans les coins sombres du pays.$B$BVous devez détruire les tas de Ju-ju, $N, avant que le flux de magie ne soit altéré pour toujours.",
    ["O"] = "Détruire 4 Tas de Ju-Ju, puis retourner voir Un\'thuwa au village de Sen\'jin.",
    ["T"] = "Les Tas de Ju-Ju",
  },
  [1885] = {
    ["D"] = "$n ? Pas vraiment ce à quoi je m\'attendais, compte tenu de l\'intérêt que Carkad lui-même manifeste pour vous ! Comme quoi les apparences sont parfois trompeuses, non ?$b$bPeu importe, vous devez aller le voir dès que possible. Autant que vous ne sachiez pas ce qui est arrivé au dernier qui a ignoré ses sommations.",
    ["O"] = "Aller voir Mennet Carkad dans le Quartier des voleurs d\'Undercity.",
    ["T"] = "Mennet Carkad",
  },
  [1886] = {
    ["D"] = "Si vous voulez rejoindre les Traqueurs noirs, la garde d\'élite du Seigneur Varimathras, vous devez prouver votre valeur.$b$bJe ne vais pas vous mentir, $n, cette tâche pourrait bien signifier votre mort.$b$bIl y a certaines choses auxquelles il faut prêter attention. J\'ai besoin des effets personnels d\'Astor Hadren, un messager qui voyage entre les Pins argentés et Undercity. Je vous laisse le choix de la méthode.$b$bMoins vous en saurez, mieux ça vaudra pour vous. Réussissez et je vous en dirai plus.",
    ["O"] = "Trouver la Lettre de recommandation d\'Astor et l\'apporter à Mennet Carkad dans le Quartier des voleurs.",
    ["T"] = "Les Traqueurs noirs",
  },
  [1898] = {
    ["D"] = "La lettre de recommandation devrait être suffisante pour convaincre Andron que vous êtes le messager qu\'il attendait. J\'ai déjà rencontré Andron, il n\'est pas assez intelligent pour être soupçonneux.$B$BLe seigneur Varimathras suspecte Andron d\'avoir fourni de l\'aide à un autre groupe. Il exige de savoir lequel et en veut la preuve.$B$BJe veux que vous alliez voir Andron pour obtenir cette information. Vous le trouverez à son magasin près de l\'Apothicarium.",
    ["O"] = "Aller voir Andron Gant à Undercity.",
    ["T"] = "Les Traqueurs noirs",
  },
  [1899] = {
    ["D"] = "Vous trouverez les informations qui intéressent vos employeurs dans le grand livre sur l\'étagère derrière moi. Apportez-leur avec mes respects et dites-leur que je préférerais que ce soit vous qu\'ils envoient, si ce qu\'ils trouvent leur semble avoir assez de valeur, pour continuer notre petit accord.",
    ["O"] = "Apporter le Grand Livre d\'Astor à Mennet Carkad dans le Quartier des voleurs d\'Undercity.",
    ["T"] = "Les Traqueurs noirs",
  },
  [1918] = {
    ["D"] = "Sur l\'élémentaire vaincu, vous trouvez un globe d\'eau, formidablement lumineux, dont le contenu semble proprement répugnant. Apparemment, l\'essence qui a souillé les eaux du lac Mystral est également présente dans les eaux du globe. $B$BCe genre de découverte n\'est pas en mesure d\'intéresser le commun des mortels, et sans doute la personne qui vous a demandé de vous rendre au lac, Mastok Wrilehiss, au poste de Bois-brisé, pourrait bien avoir une idée plus précise à ce sujet.",
    ["O"] = "Apporter le Globe d\'eau souillée à Mastok Wrilehiss, au Poste de Bois-brisé, à Ashenvale.",
    ["T"] = "Les éléments souillés",
  },
  [1919] = {
    ["D"] = "Vous connaissez de mieux en mieux le métier, jeune $N. Vous vous sentez d\'attaque pour commencer vos vraies leçons maintenant ? Pour prendre part à la guerre secrète que nous menons ?$B$BSi oui, allez voir Jennea Cannon au Sanctuaire du Sorcier, dans le quartier des mages de Stormwind. Elle vous confiera une tâche qui sera votre première fenêtre sur le monde invisible.",
    ["O"] = "Parler à Jennea Cannon à Stormwind.",
    ["T"] = "Rendre compte à Jennea",
  },
  [1920] = {
    ["D"] = "On nous a rapporté un incident qui est survenu à la taverne du « Solitaire bleu » dans le quartier des mages. Des créatures étranges et invisibles rôdent dans la taverne et je crains qu\'une faille de mana ne se soit formée là-bas.$B$BIl faut que vous y alliez pour neutraliser ces créatures. Sur place, utilisez l\'incantation de manifestation pour les rendre visibles, puis capturez-les avec un coffre de retenue. Quand vous en aurez suffisamment, revenez me voir avec les coffres remplis, les coffres vides et l\'incantation.",
    ["O"] = "Obtenir une Incantation de manifestation et une Caisse de coffres de retenue placée derrière Jennea Cannon. Apporter 3 Coffres de retenue remplis à Jennea Cannon au Sanctuaire du sorcier.",
    ["T"] = "Trouver le Solitaire bleu",
  },
  [1921] = {
    ["D"] = "Vous avez bien servi les mages, et il est temps de vous attribuer votre robe de sorcier. Wynne Larson peut la réaliser pour vous, mais il faudra lui fournir les matériaux nécessaires. $B$BLa robe sera faite de lin, ce qui ne sera pas difficile à trouver, mais vous devrez aussi fournir des gemmes chargées de la faille. On ne peut les trouver que dans la mine du Ru d\'argent à Loch Modan, dans les caisses laissés par la Ligue des mineurs nains. $B$BRassemblez le lin et les gemmes et apportez-les à Wynne Larson, chez les couturiers Larson, dans le quartier des mages.",
    ["O"] = "Apporter 10 unités de Lin et 6 Gemmes chargées de la faille à Wynne Larson à Stormwind.",
    ["T"] = "Rassembler les matériaux",
  },
  [1938] = {
    ["D"] = "Autrefois, trois mages étudiaient dans cette tour, jusqu\'à ce que l\'un d\'entre eux tombe dans les travers de la magie noire. On l\'a pressé de se détacher de ses travaux maléfiques, mais il n\'a pas pu résister. Il fut alors banni. $B$BLe mage renégat a élu domicile dans la tour d\'Ilgalar, dans les Carmines, et il a emporté avec lui un livre très puissant : le \"Traité d\'Ur sur la Magie de l\'Ombre\". $B$BNous, mages de Stormwind, voulons récupérer ce livre. Volez-le dans la tour d\'Ilgalar et rapportez-le.",
    ["O"] = "Apporter le « Traité d\'Ur sur la magie de l\'Ombre » au Grand Sorcier Andromath à Stormwind.",
    ["T"] = "Traité d\'Ur sur la Magie des Ombres",
  },
  [1939] = {
    ["D"] = "Le Grand Sorcier Andromath souhaite vous parler, $N. Il est en haut du Sanctuaire du Sorcier, dans le quartier des mages de Stormwind.$B$BIl serait sage d\'aller le voir, car Andromath est l\'un des mages les plus puissants de Dalaran. Avec lui, le Conseil est en effet un Haut Conseil.",
    ["O"] = "Aller voir le Grand Sorcier Andromath.",
    ["T"] = "Grand sorcier Andromath",
  },
  [1940] = {
    ["D"] = "Vous avez servi sans faille, $N. Je pense qu\'il est temps pour vous de revêtir un vêtement plus adapté à votre statut. Si vous allez chercher les matériaux nécessaires, nous ferons faire la robe qui convient.$B$BVous devez entrer dans le bois de la Pénombre et chasser les araignées qui s\'y cachent. Prélevez leur soie, mais uniquement la plus fine et la plus pure. Quand vous en aurez suffisamment, apportez-la à Wynne Larson, des Couturiers Larson, dans le quartier des mages. Elle fera votre robe.",
    ["O"] = "Apporter 8 Soies d\'araignée en parfait état à Wynne Larson à Stormwind.",
    ["T"] = "Soie d\'araignée en parfait état",
  },
  [1941] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La robe de tisse-mana",
  },
  [1942] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "L\'Habit du noeud astral",
  },
  [1943] = {
    ["D"] = "La mage troll Deino a besoin de votre aide, $N. Elle réside à Orgrimmar, au pavillon de Buissombre.",
    ["O"] = "Aller voir Deino.",
    ["T"] = "Parler à Deino",
  },
  [1944] = {
    ["D"] = "La ville légendaire de Xavian est enfouie au plus profond des terres orientales d\'Ashenvale et les initiés connaissent bien son histoire. Il est dit que les eaux de Xavian ont des pouvoirs magiques énormes.$B$BJe voudrais un échantillon de ces eaux, pris à la source.$B$BPrenez cette fiole et allez à Xavian. Recueillez de l\'eau en bas de la chute la plus haute et rapportez-la-moi. Et prenez garde en vous déplaçant dans la cité ancienne : les satyres se sont installés dans les ruines et ils n\'aiment pas que les mages viennent fourrer leur nez dans leurs affaires.",
    ["O"] = "Apporter l\'Échantillon d\'Eau de Xavian à Deino à Orgrimmar.",
    ["T"] = "Les Eaux de Xavian",
  },
  [1945] = {
    ["D"] = "En remerciement de vos services, j\'ai pris des dispositions pour vous faire confectionner de nouvelles robes, à condition que vous alliez chercher les matériaux nécessaires... $B$BPour ces robes, vous devez vous rendre à Ashenvale et prélever des boucles de cheveux sur les sœurs rieuses, les dryades qui se réunissent près de la retraite de Raynewood.$B$BUne fois en possession de leurs cheveux, apportez-les à Kil\'hala, un tailleur troll qui se trouve à la Croisée, dans les Tarides. Il vous attend et coudra vos robes.",
    ["O"] = "Apporter 12 Cheveux de sœurs rieuses à Kil\'hala à la Croisée.",
    ["T"] = "Les sœurs rieuses",
  },
  [1946] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "L\'Habit du néant",
  },
  [1947] = {
    ["D"] = "Maintenant est venu le temps d\'obtenir votre Baguette de mage. Tout d\'abord, allez voir Tabetha, l\'ermite humaine du marécage d\'Âprefange. Sa cabane se trouve au fin fond du marais, à l\'ouest de Theramore.$B$BAdressez-vous à elle, car son savoir est immense. Vous trouverez sa cabane à l\'ouest de Theramore, et juste au nord des ruines Cognepierres.",
    ["O"] = "Aller voir Tabetha.",
    ["T"] = "Voyager vers le Marais",
  },
  [1948] = {
    ["D"] = "La fabrication d\'une Baguette de mage n\'est pas aisée. Il faut des substances rares et il faut fabriquer un récipient pour recueillir de grandes quantités d\'énergie magique. Vous devez me rapporter tout ça.$B$BJ\'ai écrit sur ce parchemin ce dont vous aurez besoin et des instructions sur la façon de les obtenir. Apportez-moi exactement ce qui est sur cette liste et suivez mes instructions à la lettre, sinon ça pourrait mal tourner au moment de la fabrication de votre Baguette magique.",
    ["O"] = "Apporter un Jade et le Roncier de foudre à Tabetha, dans le marécage d\'Âprefange.",
    ["T"] = "Les Objets de Puissance",
  },
  [1949] = {
    ["D"] = "Pour faire votre baguette magique, je dois lire des passages d\'un certain livre. Ce livre, intitulé « les Rituels du pouvoir », a été écrit par le magus Tirth.$B$BJe n\'ai pas d’exemplaire de ce livre et aucune des grandes bibliothèques de ce monde n\'en possède un. Vous devez donc aller voir Tirth et l’obtenir de lui.$B$BBonne chance ! Tirth était un grand mage et un grand savant, mais maintenant il passe tout son temps aux courses des gnomes et des gobelins des Salines, à boire et à parier.",
    ["O"] = "Parler au magus Tirth dans les Salines.",
    ["T"] = "Des secrets cachés",
  },
  [1950] = {
    ["D"] = "Je peux vous aider pour le livre, mais d\'abord vous devez m\'aider !$B$BL’ouvrage que vous cherchez est dans mon coffre-fort, fermé magiquement. Pour l\'ouvrir, il faut prononcer une incantation spéciale et je ne me souviens plus de l\'incantation ! D’habitude mon assistant m\'aide à me rappeler de ces choses, mais je ne le trouve nulle part.$B$BSi vous voulez mon livre, trouvez d\'abord mon apprenti ! Demandez aux gens le long de la piste. Avec un peu de chance, quelqu\'un l\'aura vu. Ramenez-le ici, ou au moins qu\'il vous donne l\'incantation pour ouvrir le coffre !",
    ["O"] = "Trouver l\'Incantation pour ouvrir le coffre-fort de Tirth puis retourner voir Tirth.",
    ["T"] = "Trouver le Scoop",
  },
  [1951] = {
    ["D"] = "Hum... Maintenant que j\'y pense, je n\'ai plus ce livre ! Vous voyez, j\'avais quelques dettes de jeu et pour les régler, j\'ai vendu ma dernière copie des « Rituels du pouvoir » à un membre du clergé...$B$BUn membre de la Croisade écarlate. Il a dû le mettre dans la bibliothèque du Monastère écarlate dans les Clairières de Tirisfal !$B$BSi vous voulez ce livre, il va falloir que vous alliez le chercher au monastère. Et désolé pour tout ce tracas. Je ne suis plus moi-même ces derniers temps...",
    ["O"] = "Apporter le livre des « Rituels du pouvoir » à Tabetha dans le marécage d\'Âprefange.",
    ["T"] = "Rituels du Pouvoir",
  },
  [1952] = {
    ["D"] = "Je vais aller fabriquer les Baguettes magiques maintenant.",
    ["O"] = "Aller voir Tabetha après son rituel.",
    ["T"] = "La baguette du mage",
  },
  [1953] = {
    ["D"] = "Il est temps pour vous de parler à l\'ermite Tabetha, $N. Elle vous donnera des indications sur la fabrication de l\'un des objets les plus importants pour les mages. $B$BLa cabane de Tabetha est loin dans le marécage d\'Âprefange à l\'ouest de Theramore.",
    ["O"] = "Aller voir Tabetha.",
    ["T"] = "Retourner dans le Marais",
  },
  [1954] = {
    ["D"] = "D\'abord, il faut acquérir un orbe puissant. Les démonistes de la Lame ardente ont des orbes splendides, de qualité... Dommage qu\'ils soient liés à des démons ! Mais ça peut s\'arranger. $B$BRendez-vous en Désolace et prenez un orbe infernal à un Invocateur démoniste. Ils se retrouvent au convent de Mannoroc. $B$BLorsque vous aurez cet orbe, rapportez-le.",
    ["O"] = "Apporter un Orbe infernal à Tabetha dans le marécage d\'Âprefange.",
    ["T"] = "L\'orbe infernal",
  },
  [1955] = {
    ["D"] = "Maintenant que nous avons l\'Orbe de l\'Invocateur, nous devons exorciser le démon qu\'elle contient. Je peux y arriver. C\'est même la partie facile.$B$BVous, vous allez avoir à faire le plus dur : tuer le démon quand il va sortir.$B$BEt il va falloir faire vite, sans ça il se réfugiera dans l\'Orbe et il faudra tout recommencer !$B$BPréparez-vous. Apportez vos sorts les plus puissants et gardez vos potions à portée de la main. Et amenez un ami, si vous en avez un. Parce que ça ne va pas être joli...",
    ["O"] = "Tuer le démon de l\'Orbe puis parler à Tabetha.",
    ["T"] = "L\'Exorcisme",
  },
  [1956] = {
    ["D"] = "Le démon a été enlevé, et maintenant il nous faut une source d\'énergie. Malheureusement, les plus puissantes sont les plus anciennes et les plus difficiles à acquérir ! Le destin n\'est pas tendre avec nous, hein ? $B$BIl va falloir que vous alliez à Uldaman pour y vaincre la sentinelle d\'obsidienne. Il est grand, il est fort, et sa source d\'énergie est pleine... d\'énergie ! $B$BJe vous souhaite beaucoup de chance, $N. Et si vous n\'avez pas cette chance, et bien... C\'était un plaisir d\'avoir pu faire votre connaissance.",
    ["O"] = "Récupérer une Source d\'énergie en obsidienne et la rapporter à Tabetha dans le marécage d\'Âprefange.",
    ["T"] = "L\'énergie d\'Uldaman",
  },
  [1957] = {
    ["D"] = "Nous avons presque fini, mais la dernière étape est la plus périlleuse, $N.$B$BJ\'ai l\'orbe d\'invocateur vide, prêt à être ensorcelé. Nous le ferons en canalisant l\'énergie d\'une faille de mana. Je jetterai un sort qui va en ouvrir une pour un court moment. Des créatures issues du mana en surgiront. Vous devrez combattre ces éruptions de mana pour extraire leur puissance, dont je m’emparerai.$B$BTuez suffisamment d\'éruptions de mana avant que la faille ne se referme et je canaliserai toute leur énergie dans votre orbe.",
    ["O"] = "Tuer 12 Eruptions de mana dans la limite de temps imposée.",
    ["T"] = "Les éruptions de mana",
  },
  [1958] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "L\'énergie céleste",
  },
  [1959] = {
    ["D"] = "Les mages d\'Undercity ont noté une perturbation magique dans la ville et ont demandé à tous les mages de la Horde d’enquêter.$B$BAnastasia Hartwell vous en dira plus. Allez la voir dans le quartier des mages de Undercity.",
    ["O"] = "Aller voir Anastasia Hartwell.",
    ["T"] = "Rendre compte à Anastasia",
  },
  [1960] = {
    ["D"] = "L\'échoppe d\'alchimiste d\'Algernon, à l\'Apothicarium, est l\'objet de curieux troubles. Des créatures magiques, faites seulement de magie, la parcourent. Il doit bien y avoir une raison, et nous devons la découvrir !$B$BEnquêtez à ce propos et capturez ces créatures. Prenez des coffres de stockage et un parchemin, derrière moi. Utilisez-le à l\'échoppe pour rendre les créatures visibles, assujettissez-les à l\'aide de vos sorts, puis capturez-les dans les coffres. Rapportez les coffres pleins, ceux qui sont vides et l\'incantation.",
    ["O"] = "Obtenir une Incantation de Manifestation et une Caisse de Coffres de retenue placée derrière Anastasia Hartwell. Apporter 3 Coffres de retenue remplis, la Caisse de Coffres de retenue et l\'Incantation de Manifestation à Anastasia Hartwell à Undercity.",
    ["T"] = "Enquêter dans le magasin de l\'Alchimiste",
  },
  [1961] = {
    ["D"] = "Il est temps pour vous de porter vos premières robes de mage, $N. Elles sont le signe de votre statut et un témoignage de votre valeur.$B$BPour gagner ces robes, il vous faudra aller dans la forêt des Pins argentés et prendre des gemmes de mana aux mages de Dalaran. Leurs croyances sont semblables aux nôtres, il est donc assez désagréable de les assassiner, mais s\'ils ne sont pas assez forts pour résister, alors ils ne méritent pas de vivre.$B$BApportez les gemmes et une longueur de lin à Josef Gregorian, à sa boutique de tailleur, dans le Quartier de la magie.",
    ["O"] = "Apporter 10 pièces de lin et 6 Gemmes de mana de Dalaran à Josef Gregorian.",
    ["T"] = "Rassembler les matériaux",
  },
  [1962] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Robe du feu-sorcier",
  },
  [1963] = {
    ["D"] = "Le Chef de guerre utilise des méthodes trop subtiles. Par prudence, il veut éviter de déchaîner le pouvoir de la Horde. Pourtant, nous avons été plus forts, nous avons été de fiers conquérants ! Nous devons retrouver notre gloire passée et la Main brisée va la reconquérir.$B$BNous tirons notre force de l\'ombre et frappons nos ennemis en secret. Une tâche que vous devez accomplir pour prouver votre valeur.$B$BLe troll Tazan est en affaires avec les pirates près de Ratchet. Il détient des informations dont j\'ai besoin. Tuez-le et apportez-moi sa sacoche.",
    ["O"] = "Tuer Tazan et apporter sa Sacoche à Therzok, à la Faille de l\'ombre à Orgrimmar.",
    ["T"] = "La Main brisée",
  },
  [1978] = {
    ["D"] = "Le Syndicat ? Je ne m\'en serais jamais douté. Il semble que ces messieurs d\'Alterac soient tombés bien bas depuis leur trahison.$b$bLe Seigneur Varimathras aura plaisir à apprendre ça et comme vous avez contribué à cette découverte, il convient que ce soit vous qui la lui annonciez.$b$bAh comment pouvais-je oublier !$b$bBienvenue chez les Traqueurs noirs…",
    ["O"] = "Livrer le Grand Livre d\'Andron à Varimathras au Hall de la Dame noire à Undercity.",
    ["T"] = "Les Traqueurs noirs",
  },
  [1998] = {
    ["D"] = "Le Seigneur Varimathras vient juste de m\'envoyer une missive au sujet d\'une menace concernant les Réprouvés, menace qui doit être immédiatement supprimée. Son nom est Fenwick Thatros, et le Seigneur Varimathras pense que c\'est l\'un des chefs morts-vivants qui ont pris pied dans la forêt des Pins argentés.$b$bJ\'ai immédiatement pensé à vous, $n. J\'ai assez confiance dans vos capacités pour penser que vous réussirez cette mission.$b$bLes derniers rapports des Traqueurs noirs dans la forêt indiquent que Thatros doit se trouver près du vieux port abandonné sur Lordamere.",
    ["O"] = "Tuer Fenwick Thatros et rapporter sa Tête à Mennet Carkad dans le Quartier des voleurs d\'Undercity.",
    ["T"] = "Fenwick Thatros",
  },
  [1999] = {
    ["D"] = "Les magiciens de Kirin Tor à Moulin-de-l\'Ambre ont stocké d\'importants documents se rapportant à l\'état actuel de Dalaran. Nous tenons cela d\'un de nos prisonniers. Le Seigneur Varimathras a ordonné de remettre ces documents aux Traqueurs noirs.$b$bCette tâche vous incombe, $n. Cependant, vous aurez besoin de certains équipements, et de la maîtrise de la technique du Crochetage. Allez voir Estelle Gendry, notre spécialiste, et demandez-lui un jeu d\'outils avant de partir.",
    ["O"] = "Trouver le Rapport de situation de Dalaran et le remettre à Mennet Carkad dans le Quartier des voleurs d\'Undercity.",
    ["T"] = "Les outils du métier",
  },
  [2038] = {
    ["D"] = "Comme si le crash dans le Loch n\'avait pas suffi, des troggs de l\'île voisine m\'ont attaqué et ont volé mes outils et mes capsuleurs soufflants Blastenheimer spéciaux ! Sans gyrocoptère et sans explosifs, le mouvement n\'a aucune chance ! $B$BRapportez-moi mes outils et mes capsuleurs soufflants, $N. Pour Gnomeregan !",
    ["O"] = "Trouver et rapporter à Bingles son matériel :$B$BLa Clé de Bingles, le Tournevis de Bingles, le Marteau de Bingles et le Capsuleur soufflant de Bingles.",
    ["T"] = "Les fournitures perdues de Bingles",
  },
  [2039] = {
    ["D"] = "Nous avons perdu contact avec Bingles ! On l\'a envoyé en mission de reconnaissance sur Gnomeregan et ça fait une semaine qu\'il n\'a pas envoyé de rapport. Je sais juste qu\'il est parti et s\'est envolé.$B$BLa dernière fois qu\'on a vu son gyrocoptère, c\'était au-dessus du Loch. Peut-être que vous pourriez enquêter, $N.",
    ["O"] = "Trouver Bingles Blastenheimer, au Loch Modan.",
    ["T"] = "Trouvez Bingles",
  },
  [2040] = {
    ["D"] = "Gnomeregan est tombé sous les contrôle de ces troggs maudits ! La situation est grave, mais vous pouvez peut-être nous aider, $N. $B$BAu plus profond des mortemines, il y a un Déchiqueteur gobelin en état de fonctionnement. Trouvez-le et rapportez sa source d\'énergie intacte. Elle permettra à nos Excavateurs gyrodrilmatiques de percer les murailles pour l\'assaut !",
    ["O"] = "Récupérer l\'Indicateur Gnoam dans les mortemines et le ramener à Shoni la Silencieuse à Stormwind.",
    ["T"] = "Assaut souterrain",
  },
  [2041] = {
    ["D"] = "Peut-être... Peut-être que vous pouvez nous aider dans la bataille de Gnomeregan.$B$BVous trouverez à Stormwind le commandant de notre équipe d\'assaut souterraine, Shoni la Silencieuse. Shoni a besoin d\'aide pour ses excavateurs gyrodrilmatiques.$B$BVous la trouverez probablement parmi les nains de Stormwind.$B$BBonne chance, $N.",
    ["O"] = "Aller voir Shoni la Silencieuse à Stormwind.",
    ["T"] = "Allez voir Shoni",
  },
  [2078] = {
    ["D"] = "Bouseux de terrien ! Tu dois me dégoter mon Premier Officier.$B$BPrends c\'te clé, mets-y dans le bon trou, et ramèn\' le ici pour servir le cap\'taine.$B$BCes murlocs et leur chef vont pas beaucoup aimer quand mon Premier Officier va leur mettre l\'grappin dessus !",
    ["O"] = "Trouver le Premier officier de Gelkak, le Destructomembreur 4100, et le ramener à Gelkak.",
    ["T"] = "La vengeance de Gyromast",
  },
  [2098] = {
    ["D"] = "La vie d\'un pirate n\'est pas facile, bouseux de terrien. C\'est pas pillage et tafia à volonté ! Hélas ! Mon navire est à la baille, tout près du bord, à cause de ces saletés de battrodons.$B$BJ\'ai b\'soin que mon Premier Officier rapplique en ordre de marche !$B$BQuand j\'me suis ramassé sur le rivage, la clé de contact de mon Premier Officier est tombée sur les rochers et a été chapardée par des bestioles du cru. Du coin de l\'oeil, j\'ai vu des clampants enragés, des trotteurs des forêts géants et des murlocs fouiller l\'épave...",
    ["O"] = "Trouver et rapporter les 3 Morceaux de la clé de Gelkak à Gelkak Gyromast.",
    ["T"] = "La recherche de Gyromast",
  },
  [2118] = {
    ["D"] = "Il y a une maladie qui s\'étend à travers tout Sombrivage, contaminant les esprits et les organismes qu\'elle touche.$B$BCe sont les ours des chardons qui ont été les plus touchés. Ces animaux, autrefois si nobles, sont devenus des instruments de destruction, enragés et frénétiques. J\'ai peut-être trouvé un remède contre ce mal.$B$BApportez ce piège dans la forêt et placez-le au sol. N\'importe quel ours des chardons enragé qui traversera cette lumière deviendra docile pour un court moment. Une fois que l\'ours sera docile, il vous suivra. Ramenez-le ici, $N.",
    ["O"] = "Capturer vivant un Ours des chardons enragé, puis le ramener à Tharnariun.",
    ["T"] = "Terres malades",
  },
  [2138] = {
    ["D"] = "Comme je le craignais, le remède ne marche pas.$B$BCela m\'attriste énormément de devoir ordonner la mort de certaines créatures de la nature, mais les ours des chardons malades et infectés doivent être éliminés.$B$BRetournez dans la forêt et détruisez 20 ours des chardons enragés, $N. Cela ne suffira sans doute pas à éradiquer la contamination, mais ça permettra de limiter pendant quelque temps les dégâts que ces animaux causent à notre forêt et à ses habitants.",
    ["O"] = "Débarrasser la forêt de 20 Ours des chardons enragés et retourner voir Tharnariun Treetender, à Sombrivage.",
    ["T"] = "Nettoyage de l\'infection",
  },
  [2139] = {
    ["D"] = "La survie de notre peuple et de nos terres a exigé des sacrifices, $N.$B$BHélas, vous avez dû verser le sang des serviteurs de la nature et bien des animaux sont tombés sous vos coups. Le peuple d\'Auberdine vous doit beaucoup, et la souffrance de la forêt est apaisée, au moins pour le moment.$B$BJ\'ai une dernière tâche à vous confier mais sachez qu\'il vous faudra tuer de nouveau. J\'espère de tout coeur que ce sera la dernière fois, $N.$B$BÀ côté de Bashal\'Aran, dans une caverne au nord-est d\'ici, habite la Mère de la tanière. Faites-la disparaître.",
    ["O"] = "Trouver et tuer la Mère de la tanière.",
    ["T"] = "Espoir de Tharnariun",
  },
  [2158] = {
    ["D"] = "Tous les aventuriers doivent prendre du repos s\'ils veulent éviter l\'épuisement... Et pour cela, le meilleur endroit, c\'est bien l\'Auberge de la Fierté du lion ! $B$BMon meilleur ami, l\'aubergiste Farley, tient cette auberge. Si vous venez de ma part, il vous fera sûrement des réductions sur le prix de la nourriture et de la boisson ! $B$BSuivez cette route vers le sud, vous ne pouvez pas la rater !",
    ["O"] = "Parler à l\'aubergiste Farley, à l\'auberge de la Fierté du lion.",
    ["T"] = "Repos et détente",
  },
  [2159] = {
    ["D"] = "Salutations, jeune $c. Pouvez-vous m\'aider ? Je dois livrer ce paquet d\'herbes dans la ville de Dolanaar, mais ne puis partir d\'ici car j\'ai une tâche à terminer avec les druides de Sombrevallon.$B$BPouvez-vous livrer ce paquet pour moi ? Il doit être remis à l\'aubergiste Keldamyr, à l\'auberge de Dolanaar. Elle se trouve sur cette route, au sud.",
    ["O"] = "Apporter la Livraison de Dolanaar à l\'aubergiste Keldamyr.",
    ["T"] = "Livraison à Dolanaar",
  },
  [2160] = {
    ["D"] = "Hé ! Vous avez l\'air d\'être un cœur généreux. Si vous projetez de prendre le Passage, pourriez-vous apporter un paquet à l\'auberge de Kharanos ?$b$bVous vouliez vous arrêter à l\'auberge, non ? Si vous prenez le Passage, vous aurez certainement envie de vous y reposer.$b$bQuoi qu\'il en soit, apportez ceci à Tannok Frosthammer, l\'assistant de l\'aubergiste. Je ne peux pas passer le Passage moi-même et il faudra des jours avant qu\'une escorte de Montagnards ne passe par ici !",
    ["O"] = "Livrer la Caisse de ravitaillement à l\'auberge de Tannok Frosthammer à Kharanos.",
    ["T"] = "Des provisions pour Tannok",
  },
  [2161] = {
    ["D"] = "Excusez-moi... Pouvez-vous m\'aider ? $B$BVous voyez ce chargement ? J\'ai suivi la route, j\'ai bravé les scorpides, les araignées, que sais-je encore, tout ça pour apporter ces vivres à la vallée des Epreuves. Je pensais qu\'ils en auraient besoin... Mais non, ils n\'en ont pas voulu ! Maintenant, il ne me reste plus qu\'à rapporter tout ça à Tranchecolline et ça me terrorise, à cause des bêtes... $B$BVous pourriez vous en charger pour moi ? Je ne suis qu\'un malheureux péon mais vous... Vous n\'avez peur de rien, vous avez l\'habitude d\'être héroïque ! $B$BRapportez ce paquet à l\'aubergiste Grosk à l\'auberge de Tranchecolline. Merci !",
    ["O"] = "Apporter le Fardeau d\'Ukor à l\'aubergiste Grosk à Tranchecolline.",
    ["T"] = "Un Fardeau de Péon",
  },
  [2178] = {
    ["D"] = "Sentez-vous cet arôme délicieux ? C\'est ma recette secrète, $N : le brouet de trotteur frais. Délicieux !$B$BLa recette vous intéresse peut-être ?$B$BAvant de vous l\'apprendre, je veux voir comment vous vous débrouillez avec les bêtes.$B$BJe ne m\'attends pas à ce que vous sortiez en dompter un, mais si vous pouvez en abattre quelques-uns et rapporter leur succulente viande, ça suffira à me convaincre que je peux vous montrer comment cuisiner mon brouet de trotteur.",
    ["O"] = "Apporter 5 morceaux de Viande de trotteur à Alanndarian Nightsong, à Auberdine.",
    ["T"] = "Brouet de trotteur",
  },
  [2198] = {
    ["D"] = "Vous avez trouvé un Collier terni mais sur lequel on devine encore des entrelacs compliqués et des signes magiques. Il a été brisé et on voit qu\'il manque trois Gemmes. Sous le collier, on peut reconnaître le sceau de la ville d\'Ironforge et, sous le sceau, trois toutes petites lettres : \"TdK\". $B$BS\'il y a quelque chose à tirer de ce Collier, il va falloir revenir à ses origines. Le sceau et les initiales sont déjà un début...",
    ["O"] = "Chercher celui qui a fabriqué le Collier brisé pour découvrir sa valeur potentielle.",
    ["T"] = "Le Collier brisé",
  },
  [2199] = {
    ["D"] = "Je suis un joaillier de renom, ici, à Ironforge. Enfin, je l\'étais... Je suis la victime d\'un concurrent déloyal ! Dans ce métier, c\'est la réputation qui fait tout... Et résultat, je me retrouve sans un sous, uniquement propriétaire d\'un nom traîné dans la boue ! $B$BJe veux que vous me rapportiez cinq Barres d\'argent pour terminer une commande qui m\'a été faite - la première commande depuis des lustres ! $B$BVous me rapportez ces barres d\'argent et je répondrai à vos questions au sujet du Collier.",
    ["O"] = "Apporter 5 Barres d\'Argent à Talvash del Kissel, à Ironforge.",
    ["T"] = "Le Prix de la Connaissance",
  },
  [2200] = {
    ["D"] = "Un de mes concurrents a fait courir la rumeur que le collier avait maudit le paladin. Inutile de dire que personne ne vient acheter des bijoux maudits à un joaillier !$B$B$N, je vous propose un marché : trouvez les gemmes et je réparerai le collier, à condition que vous affirmiez partout que je ne fabrique PAS de bijoux maudits !$B$BCherchez des indices à Uldaman ; voyez si le corps du paladin est là-bas ; après tout c\'est lui qui en était le dernier propriétaire ! Quand vous aurez les gemmes, utilisez ceci pour me contacter, ici à Ironforge.",
    ["O"] = "Chercher des indices sur le Collier de Talvash à Uldaman. Le paladin dont il a parlé est la dernière personne à l\'avoir porté.",
    ["T"] = "Retour à Uldaman",
  },
  [2201] = {
    ["D"] = "Le journal du paladin est sans doute le seul indice dans Uldaman qui puisse vous conduire aux gemmes. Si ce qu\'il prétend est vrai, il va falloir traiter avec les nains Ombreforge et les troggs qui combattent pour le contrôle de l\'excavation. $B$BLa traque des gemmes doit commencer immédiatement. Lorsqu\'elles seront récupérées, il faudra prendre les instructions de Talvash à Ironforge en utilisant la Fiole de Divination.",
    ["O"] = "Trouvez le rubis, le saphir et la topaze qui sont éparpillés à travers Uldaman. Une fois que vous les aurez, contactez Talvash del Kissel en utilisant la Fiole de divination qu’il vous a donnée précédemment.$B$BC\'est dans le journal, vous savez...$B* Le rubis a été caché dans une zone barricadée des Ombreforge.$B* La topaze a été dissimulée dans une urne, dans l’une des zones trogg, pas très loin de nains de Alliance.$B* Le saphir est en possession du chef trogg, Grimlok.",
    ["T"] = "Trouver les gemmes",
  },
  [2202] = {
    ["D"] = "On a découvert récemment une nouvelle plante : le champignon magenta. Ils poussent en grappes, au plus profond des excavations d\'Uldaman ; il en existe peut-être ailleurs, mais je ne connais que cet endroit. Je veux étudier leur utilité éventuelle en alchimie, et c\'est là que vous entrez en scène.$B$BTrouvez les champignons magenta et rapportez-m\'en une douzaine. Attention, les grappes peuvent projeter des spores vénéneux quand on les manipule !$B$BRendez-moi ce service et je sortirai pour vous un petit élixir revigorant de derrière les fagots.",
    ["O"] = "Apporter 12 Champignons magenta à Jarkal Mossmeld à Kargath.",
    ["T"] = "Rechercher les composants à Uldaman",
  },
  [2203] = {
    ["D"] = "Votre aide m\'a été précieuse, merci, $c. Je vous récompenserai en vous donnant la recette de l\'Elixir que vous avez testé un peu plus tôt... mais je vais d\'abord vous confier une mission dangereuse. Écoutez bien. $B$BCe coffret contient trois Récipients thaumaturgiques vides. Ils sont entourés d\'une Aura d\'harmonie qui attire le Sang de Gardien dragon brûlé. Attention ! Si vous n\'en utilisez qu\'un, la bête deviendra enragée. Lorsque vous aurez rempli les trois, rapportez-les-moi. $B$BVous aurez la recette de mon Elixir en échange.",
    ["O"] = "Utiliser les 3 Récipients thaumaturgiques vides sur des dragons gardiens des terres brûlées dans les Terres ingrates. Une fois remplis, les rapporter à Jarkal Mossmeld à Kargath.",
    ["T"] = "Les réactifs des Terres ingrates II",
  },
  [2204] = {
    ["D"] = "Vous allez avoir besoin de \"pep\'s\" pour le collier, et l\'endroit idéal pour en trouver, c\'est Uldaman... Je suppose qu\'il y a des tas de constructions qui se baladent par là-bas, non ? Il suffit de choisir la plus vile et la plus grande et de lui piquer sa Source d\'énergie. Je suis presque sûr de pouvoir m\'en servir pour le collier !$B$BSans source d\'énergie, de toute façon, la réparation est impossible. Revenez-me voir à Ironforge quand vous l\'aurez, et on verra ce qu\'on peut faire !",
    ["O"] = "Obtenir une Source d\'énergie de la construction la plus puissante que vous pouvez trouver à Uldaman, puis la remettre à Talvash del Kissel à Ironforge.",
    ["T"] = "Réparer le Collier",
  },
  [2205] = {
    ["D"] = "L\'insigne ? Rien... Rien qui soit pour vous... Si j\'en juge par votre apparence, j\'ai peur que Mathias vous prenne pour le livreur de fromage, mon enfant, au lieu du malandrin attendu. Enfin, on m\'a donné un travail et j\'ai promis de livrer ce travail. $B$BTiens, à propos, vous apporterez ce paquet à Mathias, et peut-être qu\'il vous donnera un ou deux petits boulots à accomplir. $B$BAllez, du vent.",
    ["O"] = "Remettre le colis de Keryn Sylvius à maître Mathias Shaw à la Caserne de Stormwind.",
    ["T"] = "Chercher le SI:7",
  },
  [2206] = {
    ["D"] = "Nos espions sur le terrain rapportent que les Défias sont très actifs dans le sud de Goldshire. Il semble qu\'ils aient pris le Débarcadère de Jerod et qu\'ils l\'utilisent pour passer en contrebande de grandes quantités de produits entre les Carmines et la marche de l\'Ouest.$B$BIl faut que vous vous introduisiez dans les docks et que vous me rapportiez le programme d\'embarquement des navires, $N. Vous le trouverez probablement au profond des poches de l\'Intendant des docks.$B$B... Et $N, si vous étiez découvert, sachez que le SI:7 nierait vous avoir jamais $gconnu:connue;.",
    ["O"] = "Trouver l\'Intendant des docks défias et récupérer le programme d\'embarquement, pour le compte de maître Mathias Shaw.",
    ["T"] = "Effraction",
  },
  [2218] = {
    ["D"] = "Une autre âme perdue en quête de conseil, hein ?Je suppose que vous cherchez une voie, votre voie. J\'ai déjà vu ça mille fois, encore un héros désœuvré qui cherche du travail.$B$BEh bien, c\'est aujourd\'hui votre jour de chance, mon $gami:amie;. Hogral connaît la solution à votre tourment.$B$BVoici ce qu\'il vous faut faire : prenez cette route jusqu\'à Ironforge et cherchez-y la caverne lugubre. Une fois sur place, localisez la guilde des voleurs et demandez entretien à Hulfdan Blackbeard. Il vous guidera vers votre salut.",
    ["O"] = "Parler à Hulfdan Blackbeard à Ironforge.",
    ["T"] = "La voie du salut",
  },
  [2238] = {
    ["D"] = "Vous voyez, le roi a épuisé les carrières en extrayant le minerai de fer et les gemmes. Cela fait plus de richesses que n\'en pourrait amasser un nain en vivant dix vies.$B$BLe Cercle secret a des valeurs, $N. Nous accordons de la valeur a ce qui a de la valeur. Vous comprenez ?$B$BNous pensons que le roi doit partager ce butin, mais nous ne voulons pas que \"Barbe-puante\" sache qu\'il est en train de partager, pigé ?$B$BAdressez-vous à Onin MacHammar à Gnomeregan et rendez moi compte. Il est habile, il se cache parmi les lépreux. Faites attention.",
    ["O"] = "Trouver Onin MacHammar à Gnomeregan.",
    ["T"] = "Simple subterfuge",
  },
  [2239] = {
    ["D"] = "La carrière ? Ouaip, je vois ces monstrueux chars à vapeur qui passent près de là, presque tous les jours.$B$BMes contacts, à l\'est, me disent que le char quitte Ironforge, charge sa cargaison à la carrière de Gol\'Bolar, se ravitaille à Kharanos et après, pour une raison bizarre, emprunte le long chemin qui contourne Gnomeregan avant d\'atteindre Ironforge où il décharge.$B$BTout est dans le rapport, $n. Portez-le à Hulfdan, qu\'il puisse réfléchir à la suite du plan.",
    ["O"] = "Porter le rapport d\'Onin à Hulfdan à Ironforge.",
    ["T"] = "Rapport d\'Onin",
  },
  [2240] = {
    ["D"] = "Bon... Pour la tâche en cours...$B$BIl va falloir explorer une chambre secrète, la Chambre de Khaz\'mul. Le problème, c\'est qu\'elle se trouve derrière une lourde porte fermée ! Il est heureux que je sache comment l\'ouvrir ! $B$BJ\'ai tout écrit dans ce journal, là, sur la table. Pour l\'instant nous sommes coincés ici. Trouvez la Chambre de Khaz\'mul pour nous, $N !$B$BLisez mon journal, explorez la Chambre, puis rendez compte au Prospecteur Stormpike de la Ligue des Explorateurs à Ironforge.",
    ["O"] = "Lire le journal de Baelog, explorer la Chambre secrète, puis rendre compte au Prospecteur Stormpike.",
    ["T"] = "La Chambre secrète",
  },
  [2241] = {
    ["D"] = "Ils disent que je suis fleur bleue... quoi que cela puisse vouloir dire.$B$BC\'est vrai qu\'à tout instant je pense à Syurna. C\'est vrai, ma maison est tapissée de peintures et de dessins de Syurna. C\'est vrai, je peux passer des jours sans manger, boire ou dormir en me languissant de l\'amour que nous aurions pu connaître. Mais est-ce mal ?$B$BDésormais, elle ne me verra plus ! MOI ! J\'ai essayé de lui envoyer des messages, mais elle ne parlera même pas à quelqu’un qui n’est pas un voleur.$B$BPourriez-vous lui porter cette fleur ? Et n\'oubliez pas de dire que c\'est de la part de Jannok.",
    ["O"] = "Porter la fleur de Jannok à Syurna à Darnassus.",
    ["T"] = "Pomme d\'amour",
  },
  [2242] = {
    ["D"] = "Nous pensons qu\'il existe un être qui pourrait nous aider à comprendre ce qui arrive aux forêts de Teldrassil. Malheureusement, toutes les tentatives pour amener la créature à coopérer se sont soldées par un désastre.$B$BIl se cache sur une branche, sur le rebord de la clairière de l\'Oracle. À ce jour, tous ceux qui ont approché le satyre, par la force ou la diplomatie, ont été anéantis. Le camouflage est peut-être la solution, $N.$B$BTrouvez ce que Sethir l\'Ancien cache dans ses poches et faites-moi un rapport.",
    ["O"] = "Trouver Sethir l’Ancien et rapporter tout indice découvert à Syurna.",
    ["T"] = "L\'appel du destin",
  },
  [2258] = {
    ["D"] = "$N, je pourrais avoir l\'usage de vos capacités. Je suis un alchimiste de quelque renom et j\'ai besoin de votre aide pour acquérir des composants que l\'on ne peut trouver que dans les Terres ingrates. $B$BJ\'ai besoin des objets suivants pour réapprovisionner mon stock : cinq Gésiers de busards, dix Crocs de coyote des rochers et cinq Fragments d\'élémentaire de roche. A prendre sur les bêtes elles-mêmes, bien entendu... Si vous n\'êtes pas du genre batailleur, trouvez quelqu\'un qui le soit ! $B$BTrouvez-moi ces objets, et vous aurez quelques pièces en échange, ça vous va ?",
    ["O"] = "Apporter 5 Gésiers de busard, 10 Crocs de coyote des rochers et 5 Fragments d\'élémentaire de roche à Jarkal Mossmeld, à Kargath, dans les Terres ingrates.",
    ["T"] = "Les réactifs des Terres ingrates",
  },
  [2259] = {
    ["D"] = "Ne vous inquiétez pas, $N, je ne vous envoie pas voir Syurna. Erion veut que vous le rejoigniez dans l\'Enclave de Cenarion. Faites vite !",
    ["O"] = "Contacter Erion Shadewhisper à Darnassus.",
    ["T"] = "Erion Murmombre",
  },
  [2260] = {
    ["D"] = "Le Service de Renseignement de Stormwind (SI:7) a entendu parler de vos exploits, $N, et il veut vous recruter. $B$BLe voyage est coûteux et périlleux, mais on ne rejette pas l\'opportunité de s\'entraîner pour le SI:7 !$B$BUn nouveau monde s\'ouvre à vous, $N. Rendez-vous dans la vieille ville de Stormwind et adressez-vous à Renzik \"le Kriss\" \" au QG du SI:7.",
    ["O"] = "Voyager vers Stormwind pour y rencontrer Renzik \"le Kriss\".",
    ["T"] = "L\'ordre d\'Erion",
  },
  [2278] = {
    ["D"] = "Les pièces centrales de cette chambre au trésor sont quatre larges disques de platine ornés de sceaux d\'origine inconnue. Lorsque vous les touchez, vous sentez un frémissement. Le visage d\'un grand Gardien des pierres apparaît alors. $B$B\"Salut à vous. Je suis le Gardien du Savoir. La synthèse des Créateurs de la Terre a été gravée et conservée dans les Disques de Norgannon.\"$B$BVous avez découvert une manne de savoir inconnu du monde entier !",
    ["O"] = "Parler au Gardien des pierres pour connaître le Savoir qu\'il défend. Une fois découvert ce qu\'ils peuvent offrir, activer les Disques de Norgannon.",
    ["T"] = "Les Disques de platine",
  },
  [2279] = {
    ["D"] = "Votre main vibre de nouveau au contact des Disques de Norgannon. Cette fois, pourtant, c\'est un étrange grincement qui se fait entendre. $B$BSoudain, votre sac paraît plus lourd. Vous y trouvez une version miniature des Disques ! $B$BVoilà qui sera utile à tous ceux qui s\'intéressent à l\'histoire ancienne d\'Azeroth. La Ligue des Explorateurs d\'Ironforge est connue dans toute l\'Alliance pour ses recherches sur le sujet ; ils devraient pouvoir faire bon usage de tout ceci.",
    ["O"] = "Apporter la version miniature des Disques de Norgannon à la Ligue des Explorateurs à Ironforge.",
    ["T"] = "Les Disques de platine",
  },
  [2280] = {
    ["D"] = "Votre main vibre de nouveau au contact des Disques de Norgannon. Cette fois, pourtant, c\'est un étrange grincement qui émane des disques. $B$BSoudain, votre sac paraît plus lourd. Vous y trouvez une version miniature des Disques ! $B$BVoilà qui sera utile à tous ceux qui s\'intéressent à l\'histoire ancienne d\'Azeroth. Les Sages de Thunderbluff sont connus dans toute la Horde pour leurs recherches sur le sujet ; ils devraient pouvoir faire bon usage de tout ceci.",
    ["O"] = "Apporter la version miniature des Disques de Norgannon à l\'un des Sages de Thunder Bluff.",
    ["T"] = "Les Disques de platine",
  },
  [2281] = {
    ["D"] = "Vous jeter maintenant sur le champ de bataille serait pur suicide. Renzik ne veut pas d\'un autre cadavre de voleur dans son équipe. Mais pas d\'inquiétude, $N : Renzik vous mettra à niveau.$B$BDirigez-vous vers les Carmines et discutez avec Lucius, un associé du SI:7. Vous le trouverez facilement, il traîne généralement autour des docks de Lakeshire avec d\'autres bons à rien.",
    ["O"] = "S\'aventurer vers Lakeshire dans les Carmines et parler à Lucius.",
    ["T"] = "Le Rendez-vous des Carmines",
  },
  [2282] = {
    ["D"] = "J\'entraîne des voleurs depuis des années ! Après tout, c\'est pas n\'importe quel vaurien qu\'est né avec la grâce et la finesse du vieux Lucius.$B$BUn peu plus loin, z\'allez trouver la scierie d\'Alther. J\'y ai un coffre fermé qui contient que\'q\' chose d\'important. Allez le récupérer pour moi. Ramenez-moi ce qui s\'y trouve, et vous recevrez vot\' Certificat de voleur !",
    ["O"] = "Ouvrir le Coffret de Lucius, récupérer le Gage de voleur et le rapporter à Lucius à Lakeshire.",
    ["T"] = "La scierie d\'Alther",
  },
  [2283] = {
    ["D"] = "Nous avons du travail pour vous, si vous avez du cran...$B$BAu plus profond de l\'excavation d\'Uldaman, dans les Terres ingrates, il paraît qu\'il y a les restes d\'un crétin de paladin qui est mort par là il y a pas longtemps. Ça nous intéresse pas, sauf qu\'il avait un collier de prix avec lui. On dit qu\'il y avait trois Gemmes dessus : un Rubis, un Saphir et une Topaze.$B$BBon alors voilà : on le paiera très cher. Il est peut-être abîmé mais on s\'en moque. On le réparera.",
    ["O"] = "Rechercher dans l\'excavation d\'Uldaman un Collier de prix et le rapporter à Dran Droffers à Orgrimmar. Ce Collier peut être endommagé.",
    ["T"] = "Réparer le Collier",
  },
  [2284] = {
    ["D"] = "Aussi vrai que Malton est un crétin, ces gemmes sont encore quêk part dans Uldaman. Va falloir y r\'tourner. $B$BComme j\'l\'ai d\'jà dit, si les informations que nous avons sont justes, il y a un rubis, un saphir et une topaze. $B$BOù elles sont, comment je l\'saurais ? Si vous avez du bol et qu\'vous trouvez l\'corps du paladin, et qu\'il a pas encore été nettoyé, elles s\'ront p\'têt encore là.",
    ["O"] = "Trouver, dans les profondeurs d\'Uldaman, des indices sur la localisation des Gemmes.",
    ["T"] = "Réparer le Collier, deuxième prise",
  },
  [2298] = {
    ["D"] = "Alors on était là, en train de comploter pour la phase III quand d\'un coup, deux crétins de Magni se pointent en parlant de je ne sais quel coup, et d\'arrêter ceux du Cercle secret.$B$BPersonne ne porte la main sur le vieux Blackbeard ! La discussion s\'envenime, blabla, paf, et soudain on prononce vot\' nom !$B$BV\'voulez un conseil ? Prenez le premier Griffon pour Stormwind. Une fois là-bas, allez voir Renzik \"le Kriss\" dans la vieille ville. C\'est un vieil ami à moi.",
    ["O"] = "Aller à Stormwind et chercher Renzik \"le Kriss\".",
    ["T"] = "Extorsion royale",
  },
  [2299] = {
    ["D"] = "Où donc étiez-vous, $N ? Hulfdan vous cherche partout. On craignait que le roi ne vous ait fait mettre aux fers.$B$BAllez à Ironforge dès que possible et parlez à Hulfdan !",
    ["O"] = "Se rendre à Ironforge et parler à Hulfdan Blackbeard.",
    ["T"] = "Cap sur Hulfdan !",
  },
  [2300] = {
    ["D"] = "Renzik vous cherchait, l\'ami.$B$BJe ne ferais pas attendre le bras droit de Mathias à votre place.",
    ["O"] = "Aller à Stormwind et chercher Renzik \"le Kriss\".",
    ["T"] = "SI:7",
  },
  [2318] = {
    ["D"] = "Vous êtes dans l\'impasse si vous n\'arrivez pas à faire traduire le journal dans une langue que vous comprenez. Il doit bien y avoir des gens qui en sont capables, non ? L\'endroit le plus proche où vous pourriez trouver un traducteur, c\'est l\'avant-poste de Kargath dans les Terres ingrates. C\'est le mieux pour commencer !",
    ["O"] = "Trouver quelqu\'un qui puisse traduire le journal du paladin. L\'endroit le plus proche est Kargath, dans les Terres ingrates.",
    ["T"] = "La Traduction du Journal",
  },
  [2338] = {
    ["D"] = "Ceux de la Récupération Droffers et fils sont trop bêtes pour restaurer la magie du Collier. Moi, je peux le faire. Je veux l\'étudier pour apprendre à le confectionner moi-même. Pour ce faire, j\'aurai besoin de votre collier. Laissez-le-moi et en échange je vous traduirai le journal. Le temps que vous trouviez les Gemmes, j\'aurai le loisir d\'apprendre ce qui m\'intéresse. $B$BUn marché simple et sans duperie... Alors, qu\'en pensez-vous ?",
    ["O"] = "Laisser Jarkal emprunter le Collier en échange de la traduction du Journal.",
    ["T"] = "La traduction du Journal",
  },
  [2339] = {
    ["D"] = "Voici la traduction du journal, pour que vous puissiez le lire de votre côté.$B$BIl est crucial de savoir que ce sont les nains qui ont caché le rubis, les troggs la topaze et que c\'est un trogg nommé Grimlok qui a le saphir.$B$BBon. Ensuite, vous aurez besoin d\'une source d\'énergie pour que le Collier fonctionne de nouveau. Et ça, Dran Droffers ne le sait pas. Pour en trouver une, moi, j\'irais au plus profond d\'Uldaman et je détruirais les machines les plus puissantes. Elles doivent en disposer !$B$BQuand vous aurez tout ça, revenez me voir.",
    ["O"] = "Récupérer, dans Uldaman, les trois gemmes et une source d’énergie pour le collier, puis les rapporter à Jarkal Mossmeld, à Kargath. Jarkal pense que vous pouvez trouver une source d’énergie sur la machine la plus puissante d’Uldaman.$B$BC\'est dans le journal, vous savez...$B* Le rubis a été caché dans une zone barricadée des Ombreforge.$B* La topaze a été dissimulée dans une urne, dans l’une des zones trogg, pas très loin de nains de Alliance.$B* Le saphir est possession du chef trogg, Grimlok.",
    ["T"] = "Trouver les gemmes et la source d\'énergie",
  },
  [2340] = {
    ["D"] = "Un collier et des gemmes ordinaires marcheront aussi bien, du moment qu\'ils sont faits du même matériau. Cette Source d\'énergie, en revanche, c\'est elle qui va donner sa magie au Collier... Enfin j\'espère ! $B$BJe fournirai volontiers les gemmes nécessaires pour la confection de votre version du Collier, si toutefois cette Source d\'énergie fonctionne. $B$BRetournez à Orgrimmar, et terminez votre marché avec Droffers et Fils. Ne leur dites rien de notre rencontre et lorsque vous reviendrez, je pourrais bien avoir un cadeau pour vous.",
    ["O"] = "Apporter les Gemmes et le Collier récupérés à Dran Droffers, à Orgrimmar.",
    ["T"] = "Livrer les Gemmes",
  },
  [2341] = {
    ["D"] = "Pendant que Dran et Malton Droffers se disputent la prise, la moitié de l\'affaire est maintenant close. Il vous reste à retourner dans les Terres ingrates pour finir tout ça. Jarkal Mossmeld, à Kargath, pourrait avoir une récompense spéciale à vous donner.",
    ["O"] = "Rendre visite à Jarkal Mossmeld à Kargath pour constater (ou non) son succès.",
    ["T"] = "Réparer le collier, troisième prise",
  },
  [2342] = {
    ["D"] = "Balayer les enclos à chauve-souris avec ce balai, ce n\'est pas digne d\'un Garrett ! J\'ai fait quelques économies avec ce boulot maudit, et je suis prêt à les dépenser pour améliorer mes conditions de travail. $B$BL\'arme familiale des Garrett est restée enfermée dans notre Coffre, dans le Hall commun sud d\'Uldaman, où nous avons trimé comme mineurs. Sans elle, je suis coincé ici pour toujours, à balayer derrière ces immondes chauve-souris ! $B$BRapportez-moi ce trésor de famille ! Aidez-moi à retrouver ma dignité !",
    ["O"] = "Rapporter à Patrick Garrett, à Undercity, son Trésor de famille, qui se trouve dans le Coffre de famille dans le Hall commun sud d\'Uldaman.",
    ["T"] = "Des Trésors à recouvrer",
  },
  [2358] = {
    ["D"] = "Il y a bien des lustres, les Cornes de Nez\'ra ont été volées au manoir de Ravenholdt. Je les ai cherchées par le monde entier, sur terre comme sur mer. Et tout ce temps, ce sont ces bêtes immondes les avaient !$B$BExterminez tous les orcs Rochenoire que vous croiserez jusqu\'à ce que vous retrouviez les Cornes de Nez\'ra.$B$BFaites ceci pour moi, $N, et votre renommée atteindra Ravenholdt.",
    ["O"] = "Rapporter les Cornes de Nez\'ra à l\'Ombre d\'Arantir, à Comté-du-lac.",
    ["T"] = "Cornes de Nez\'ra",
  },
  [2359] = {
    ["D"] = "Je suis là, à surveiller cette tour depuis des semaines, $N. Récemment, un chargement important est arrivé du bois de la Pénombre, protégé par ces gardes. $B$BQue sont-ils ? Aucune idée. La seule chose que je puisse affirmer, c\'est qu\'ils ne sont pas humains. $B$BQuoiqu\'il en soit, dans cette cargaison repose un coffre, et je suppose que ces abominations sont commises à sa garde. Le gros avec les piques, là, possède une clé dont nous aurons besoin.$B$BRécupérez cette clé, entrez dans la Tour et découvrez ce que contient ce coffre. Rapportez à Mathias le produit de votre recherche.",
    ["O"] = "Voler la Clé de la Tour défias, pénétrer dans la Tour et trouver ce que contient le Coffre du bois de la Pénombre. Rapporter les informations à Mathias Shaw à Stormwind. Lire le journal de Kearnen pour savoir ce qui se passe dans la Tour.",
    ["T"] = "La Tour de Klaven",
  },
  [2360] = {
    ["D"] = "Pas trop tôt, $N. Les éléments de reconnaissance du SI:7 nous apprennent les Défias sont inhabituellement actifs dans la marche de l\'Ouest.$B$BOn dirait qu\'ils ont pris une ancienne Tour de guet de l\'Alliance abandonnée au sud-est de la Colline des sentinelles, et qu\'ils s\'en servent comme base d\'opérations pour leurs trafics à partir de la route d\'approvisionnement des Carmines.$B$BAllez dans la marche et contactez l\'Agent Kearnen. Elle est cachée à proximité de la tour. $B$B... Oh, $N, si vous veniez à être $gcapturé:capturée;, le SI:7 nierait avoir eu connaissance, etc.",
    ["O"] = "Se rendre dans la marche de l\'Ouest et trouver l\'Agent Kearnen pour un débriefing.",
    ["T"] = "Mathias et les Défias",
  },
  [2361] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Réparer le Collier",
  },
  [2378] = {
    ["D"] = "Vous avez bien servi les Traqueurs de ténèbres, $N, mais il est temps d\'étendre votre expérience. Vous avez montré vos capacités en matière de ruse et de traîtrise, mais votre entraînement n\'est pas terminé. $B$BLes Traqueurs de ténèbres ont des alliés au sein de la Main brisée. Cherchez ceux qui viennent à notre aide : trouvez Shenthul à Orgrimmar.",
    ["O"] = "Voyager vers Orgrimmar pour y parler à Shenthul, dans la Faille de l\'ombre.",
    ["T"] = "Trouver la Main brisée",
  },
  [2379] = {
    ["D"] = "Votre seule détermination ne suffira pas pour affronter les obstacles qui nous attendent. $B$BAdressez-vous à Zando\'zan pour commencer votre entraînement comme opérationnel de la Main brisée. $B$BLorsque vous maîtriserez le noble art du vol et de la tricherie vous pourrez faire un pas de plus vers votre destinée.",
    ["O"] = "Parler à Zando\'zan à la Faille de l\'ombre.",
    ["T"] = "Zando\'zan",
  },
  [2380] = {
    ["D"] = "Spécialiste de la forfaiture et de la tricherie, vous ne l\'êtes pas, mais ça peut changer. Il semble que vous ayez impressionné les chefs avec vos maigres talents, puisque le grand Shenthul lui-même vous réclame. $B$BPrenez la route vers le nord jusqu\'à Orgrimmar et adressez-vous à Shenthul à la Faille de l\'ombre.",
    ["O"] = "S\'adresser à Shenthul à la Faille de l\'ombre à Orgrimmar.",
    ["T"] = "Cap sur Orgrimmar !",
  },
  [2381] = {
    ["D"] = "Sans vouloir vous effrayer, $N, les cinq derniers qui ont essayé de voler le trésor des Mers du sud ont été mis en pièces.$B$BLe long de la côte, au sud, vous trouverez le \"Tranche-les-vagues\", le navire des Flibustiers des Mers du sud. Le trésor se trouve dans la cale. Au deuxième niveau se trouve la salle des pirates, une salle d\'entraînement pour leurs voleurs.$B$BMontez à bord, volez le trésor, et rapportez-le-moi. Utilisez la salle des pirates si vous voulez vous entraîner, mais attention : prudence !",
    ["O"] = "Rapporter le Trésor des Mers du sud à Wrenix le Misérable à Ratchet. Ne pas oublier d’obtenir un B.G.A.C. et des Outils de voleur de l’Appareil bidulotronique de Wreni. Ces deux éléments sont nécessaires pour accomplir la mission.$B$BEn cas d’attaque par des perroquets particulièrement hostiles, utiliser le B.G.A.C.!",
    ["T"] = "Piller les pilleurs",
  },
  [2382] = {
    ["D"] = "Zando\'zan l\'a vu ! Oui, le jour vient ! La mort de l\'esclave... Le poignard dans le dos, le poison suintant de chaque veine... Zando\'zan en a trop dit.$B$B$N ! Zando\'zan a une tâche pour $N ! $N doit aller à Ratchet, à l\'est de la Croisée, pour rencontrer Wrenix le Misérable. Allez !",
    ["O"] = "Aller jusqu\'à Ratchet pour voir Wrenix le Misérable.",
    ["T"] = "Wrenix de Ratchet",
  },
  [2383] = {
    ["D"] = "Ah, pendant que vous n’étiez pas là un parchemin est arrivé pour vous, $N.$B$BLisez-le quand vous en aurez le temps. Si je ne me trompe pas, il provient du Maître des guerriers Frang. Il souhaiterait s’entretenir avec vous, quand vous jugerez le moment venu. Vous le trouverez à l’extérieur de l’Antre, dissimulé dans l’ombre.",
    ["O"] = "Lire le Simple parchemin, et parler à Frang, dans la Vallée des épreuves.",
    ["T"] = "Parchemin simple",
  },
  [2398] = {
    ["D"] = "Le site de fouilles d\'Uldaman n\'a pas toujours été mal fréquenté. Nous, les nains d\'Ironforge, contrôlions le secteur... jusqu’à la venue des troggs. Ils sont arrivés si vite et si nombreux que même des nains n\'ont pas pu les arrêter !$B$BLeur attaque a été si soudaine que je crains que certains des nôtres soient pris au piège derrière les lignes ennemies.$B$BMon ami Baelog a conduit une petite troupe assez loin dans le site avant d\'être repéré. S\'ils sont vivants, je veux que vous les trouviez.",
    ["O"] = "Trouver Baelog dans Uldaman.",
    ["T"] = "Les nains perdus",
  },
  [2399] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Les racines ont poussé",
  },
  [2418] = {
    ["D"] = "Je viens d\'avoir un problème avec les nains Ombreforge. Ils m\'ont amené à la forteresse d\'Angor pour étudier deux sortes de pierres étranges venant d\'Uldaman. J\'ai découvert que ces pierres, à condition d’être bien utilisées, étaient des sources d\'énergie stables. C\'est étonnant tout ce qu\'on peut trouver sur ce vieux site !$B$BBref, après leur avoir appris le secret des pierres, ils m\'ont chassé de la forteresse et ont commencé les fouilles.$B$BJe veux ces pierres ! Apportez-moi ces deux sortes de pierres et je vous paierai bien !",
    ["O"] = "Apporter 8 Pierres de puissance Dentrium et 8 Pierres de puissance An\'Alleum à Rigglefuzz dans les Terres ingrates.",
    ["T"] = "Les pierres de puissance",
  },
  [2438] = {
    ["D"] = "Un jour, Gaerolas Talvethren, le Gardien des druides du Refuge des saisons de Ban\'ethil, m\'a donné un Capturêve d’émeraude. Cette puissante amulette est capable d\'aspirer l\'énergie du Rêve d\'émeraude pour donner de la chance à ceux qui la portent.$B$BHélas, je n\'ai pas pu la récupérer dans ma commode au village de Brise-stellaire... Autrefois, Brise-stellaire était un endroit tranquille, mais il a succombé à la corruption des furbolgs qui y résident.$B$BVoudriez-vous récupérer mon Capturêve, $c ?",
    ["O"] = "Apporter le Capturêve d\'émeraude à Tallonkai Swiftroot à Dolanaar.",
    ["T"] = "Le Capturêve d\'émeraude",
  },
  [2439] = {
    ["D"] = "Cet objet est incroyable. Si ce qu\'il dit est vrai, les anciens Titans, ou les Créateurs comme ces disques les nomment, sont le lien avec notre création enchantée ! Nous devons investir autant de temps que possible dans cette recherche.$B$BPour vous, héroïque $c, apportez ce bon à Dinita Stonemantle à la Banque. Il vous donnera une récompense à la hauteur de cette magnifique trouvaille !$B$BRevenez directement me voir quand vous serez $gprêt:prête;, il y a encore beaucoup à faire pour la Ligue !",
    ["O"] = "Apporter le Bon de récompense à Dinita Stonemantle à la Chambre forte d\'Ironforge.",
    ["T"] = "Les Disques de platine",
  },
  [2440] = {
    ["D"] = "Je vais concentrer mes travaux sur ces disques ; ils me révéleront bientôt leur raison d’être.$B$B$N, je vais vous récompenser pour la diligence avec laquelle vous m\'avez apporté ceci. Portez ce bon à Bena Winterhoof, le maître des Alchimistes de Thunder Bluff. Elle vous offrira un objet spécial que vous apprécierez sûrement et elle vous proposera un choix de breuvages magiques de ma réserve personnelle.$B$BRevenez vite me voir, car je sens qu\'il y a du pain sur la planche pour étudier cette trouvaille.",
    ["O"] = "Apporter le Bon de récompense à Bena Winterhoof à Thunder Bluff.",
    ["T"] = "Les Disques de platine",
  },
  [2458] = {
    ["D"] = "La KapitalRisk... Vous avez entendu parler d\'eux ?$B$BDepuis un an, nous essayons d\'y introduire un de nos agents. Nous avons travaillé dur, mais prudemment, et nous avons réussi.$B$BNous venons d\'apprendre que la KapitalRisk projette une opération de grande envergure. Allez au nord de la Videfange, à la tour de la KapitalRisk, et contactez notre agent, le sous-chef Fizzule.$B$BUne fois sur place, utilisez ce lance-fusées, approchez Fizzule et faites-lui le salut de la Main brisée.",
    ["O"] = "Aller à la tour de la KapitalRisk, au nord de la Videfange, et contacter l\'agent secret de la Main brisée, le sous-chef Fizzule.$B$BUne fois la tour en vue, utiliser le lance-fusées pour se signaler au sous-chef Fizzule. Tirer DEUX fusées, puis s\'approcher du sous-chef Fizzule et faire le Salut brisé (/salut).$B$BRespecter l\'ordre de ces actions, car Fizzule attaque et tue toute personne qu\'il ne peut pas identifier.",
    ["T"] = "Sous le couvert de la nuit",
  },
  [2459] = {
    ["D"] = "L\'émeraude... Elle a disparu ! Mon capturêve a été endommagé !$B$BUne bande de Mystiques Pins-tordus rôde au nord de Brise-stellaire. J\'ai entendu dire que leur chef, Ferocitas le Mangeur de rêves, porte un collier qui émet des lueurs vertes pendant la nuit. Vu l\'état de mon capturêve, il est certain qu\'il a volé mon émeraude... Il ne comprendra jamais que sa puissance lui est inutile.$B$BTrouvez mon bijou disparu, $N. Et tant que vous y êtes, profitez-en pour nous débarrasser de quelques Mystiques corrompus.",
    ["O"] = "Sur ordre de Tallonkai Swiftroot de Dolanaar, tuer 7 Mystiques Pins-tordus et retrouver le Bijou disparu.",
    ["T"] = "Ferocitas le Mangeur de rêves",
  },
  [2460] = {
    ["D"] = "En tant que voleur de la Main brisée, il est impératif que vous appreniez le Salut brisé. C\'est la seule façon d\'identifier un autre membre de la Main.$B$BRegardez, apprenez, répétez.",
    ["O"] = "Faire le Salut brisé à Shenthul.",
    ["T"] = "Le Salut brisé",
  },
  [2478] = {
    ["D"] = "Vous devez voler la clé de la tour au contremaître Silixiz, $N. Une fois la clé en votre possession, gardez-la soigneusement, je pourrais en avoir besoin plus tard.$B$BProfitez de l\'état d\'affaiblissement des habitants empoisonnés, et laissez donc votre tendance au chaos et à la destruction s\'exprimer librement. Tuez-les tous ! Ne montrez aucune pitié !$B$BLorsque tous baigneront dans leur sang, ouvrez le coffre de Gallywix et volez la Mixture de Zanzil. Apportez la tête de Gallywix ainsi que la Mixture à Shenthul à Orgrimmar.",
    ["O"] = "Voler la Clé de la tour de Silixiz au contremaître Silixiz, puis tuer deux Pantins mutants de la KapitalRisk, deux Patrouilleurs de la KapitalRisk, et deux Sentinelles de la KapitalRisk.$B$BAssassiner le grand contremaître Gallywix et prendre sa tête. Ouvrir le coffre de Gallywix et y voler la Mixture altérée de Zanzil. Apporter la Mixture et la tête de Gallywix à Shenthul à Orgrimmar, et attendre de nouvelles instructions.",
    ["T"] = "Mission : possible mais non probable",
  },
  [2479] = {
    ["D"] = "Apportez un échantillon du mélange à Serge Hinott à Moulin-de-Tarren.$B$BSi quelqu\'un peut vous guérir, c\'est bien Serge. Si vous vivez suffisamment longtemps, il vous montrera comment créer un poison et comment l\'utiliser.",
    ["O"] = "Aller à Moulin-de-Tarren dans les contreforts d\'Hillsbrad et livrer l\'Échantillon de la mixture de Zanzil à Serge Hinott.$B$BPour rejoindre Moulin-de-Tarren, prenez le zeppelin à Undercity et suivez la route du sud, à travers les Pins argentés et vers Hillsbrad. Suivez les panneaux !",
    ["T"] = "L\'aide d\'Hinott",
  },
  [2480] = {
    ["D"] = "Je n\'ai jamais vu d’aussi merveilleux résultats pour un poison ! Zanzil, vous dites ? Jamais entendu parler de lui, mais je m\'incline devant le mélange qu\'il a créé.$B$BBon, vous voulez que je vous guérisse et que je vous apprenne ensuite à utiliser le poison ?$B$BLaissez-moi un peu de temps pour examiner la toxine, $N.",
    ["O"] = "Attendre Serge Hinott pour achever le remède.",
    ["T"] = "L\'aide d\'Hinott",
  },
  [2498] = {
    ["D"] = "Je viens de recevoir un mot de Denalan au lac Al\'Ameth, disant qu\'il a peut-être découvert la cause les tumeurs chez les sylvains. Allez le voir et dites-lui que je vous envoie.$B$BFaites vite, $N, il a besoin de nous.",
    ["O"] = "Sur ordre de Rellian Greenspyre, aller voir Denalan au lac Al\'Ameth.",
    ["T"] = "Retour vers Denalan",
  },
  [2499] = {
    ["D"] = "Dans une caverne le long de la rive sud du lac, un sylvain nommé Froncechêne propage la corruption parmi les créatures qui vivent au lac Al\'Ameth.$B$BJe n\'ai pas osé m\'approcher, mais c’est visible à distance : il est parasité par la plus grande tumeur que j\'ai jamais vue... On pourrait même dire gargantuesque.$B$B$N, vous m’avez déjà été d\'un grand secours, mais je vous demande encore cela : pourchassez-le et débarrassez-le de sa tumeur, éliminez cette source de contamination de chez moi.",
    ["O"] = "Sur ordre de Denalan du lac Al\'Ameth, ôter la Tumeur gargantuesque de Froncechêne.",
    ["T"] = "Froncechêne",
  },
  [2500] = {
    ["D"] = "$C, vos services vont m’être utiles. Je suis alchimiste et j\'ai besoin de votre aide pour trouver certains réactifs dans les Terres ingrates.$B$BJ\'ai besoin de ceci pour ma réserve : 5 Gésiers de busard, 10 Crocs de coyote des rochers et 5 Fragments d\'élémentaire de roche. Évidemment, vous pouvez trouver les réactifs sur les créatures elles-mêmes. Si vous n\'êtes pas du genre battant, trouvez donc des amis qui le soient.$B$BApportez-moi tout ça et je vous récompenserai.$B$BQu\'en dites-vous ?",
    ["O"] = "Aller chercher les réactifs dont Ghak Healtouch a besoin dans les Terres ingrates, puis les lui rapporter à Thelsamar.",
    ["T"] = "Les réactifs des Terres ingrates",
  },
  [2501] = {
    ["D"] = "Vous m\'avez été des plus utiles, $N. Votre récompense sera la recette de la boisson que vous avez goûtée tout à l\'heure... Mais j\'ai une tâche très dangereuse pour vous.$B$BCette boîte fermée contient trois Récipients vides ; ils sont imprégnés d\'un fluide magique qui drainera le sang d\'un dragon gardien des terres brûlées. Je vous préviens : le fait de les utiliser irrite beaucoup les bêtes. Une fois que vous aurez rempli les trois, apportez-les-moi.$B$BEnsuite la recette sera à vous !",
    ["O"] = "Utiliser les Récipients thaumaturgiques vides sur des dragons gardiens des terres brûlées dans les Terres ingrates. Une fois remplis, les rapporter à Ghak Healtouch à Thelsamar.",
    ["T"] = "Les réactifs des Terres ingrates II",
  },
  [2518] = {
    ["D"] = "Autrefois, dame Sathrah était aimée d\'Elune. Gracieuse et pure, l\'araignée tissait ses toiles d\'argent au clair de lune pour recueillir la brume du soir. La rosée argentée avait de grands pouvoirs de guérison et on la conservait au temple.$B$BMais récemment, dame Sathrah a plongé dans la folie. Sa descendance à venir est également menacée.$B$BTrouvez dame Sathrah, $N, et mettez fin à ses souffrances. Elle demeure le long des frontières, au nord de Teldrassil, près du lac d\'Aigue-vive. Prenez ses filières d\'argent et apportez-les-moi.",
    ["O"] = "Apporter les Filières d\'argent de dame Sathrah à la prêtresse A\'moora, au temple de la Lune, à Darnassus.",
    ["T"] = "Les larmes de la Lune",
  },
  [2519] = {
    ["D"] = "Une créature de la forêt, autrefois l\'une des plus grandes, a besoin de votre aide, $N.$B$BC\'est une tâche difficile, mais qui doit être accomplie.$B$BS\'il vous plaît, allez voir la prêtresse A\'moora, elle vous expliquera de quoi il s\'agit. Elle est au sud-est, dans le Temple de la Lune.",
    ["O"] = "Sœur Aquinne veut que vous parliez à la prêtresse A\'moora, au temple de la Lune.",
    ["T"] = "Le Temple de la Lune",
  },
  [2520] = {
    ["D"] = "Maintenant vous devez finir la tâche qui vous a été confiée. Prenez les Filières d\'argent et offrez le sacrifice de dame Sathrah à Elune. Permettez à la Grande araignée de trouver le repos.$B$BDans le temple, trouvez la fontaine centrale. C\'est là que vous déposerez le sacrifice. Les eaux saintes vont nettoyer et purifier la corruption qui a conduit Sathrah à la folie.$B$BQuand vous aurez fini, revenez me voir.",
    ["O"] = "Sur ordre de la prêtresse A\'moora, placer les Filières d\'argent de dame Sathrah dans la fontaine du Temple, puis retourner la voir.",
    ["T"] = "Sacrifice de Sathrah",
  },
  [2521] = {
    ["D"] = "$N, votre cerveau étriqué peut-il comprendre la rareté d\'une sphère de draenéthyste sans défaut ? Les draeneï ont consacré des vies entières à chercher un seul de ces cristaux et ils n\'ont rencontré que des destins horribles.$B$BEt vous voilà devant Kum\'isha, sachant ce qu\'il veut et souhaitant toujours l\'aider ? Très bien, $N... Si en parcourant ces terres, vous trouvez une sphère de draenéthyste sans défaut, apportez-la-moi et je vous récompenserai au-delà de vos rêves les plus fous.",
    ["O"] = "Si vous trouvez une sphère de draenéthyste sans défaut au hasard des voyages dans les Terres foudroyées, apportez-la à Kum\'isha le Collecteur.$B$BCette gemme, l\'une des plus rares de tout Azeroth, peut être détenue par n\'importe qui dans le pays.$B$BUne récompense sera donnée pour chaque sphère de draenéthyste sans défaut trouvée et rapportée.",
    ["T"] = "Au service de Kum\'isha",
  },
  [2522] = {
    ["D"] = "Bien que l\'invocation de portail ait échoué, j\'ai bon espoir que la prochaine tentative sera la bonne.$B$BL\'espoir des draeneï est aussi fort que votre soif de bibelots, $N. Retournez dans ces terres maudites et trouvez une autre sphère de draenéthyste sans défaut.$B$BBien sûr, vous êtes libre d\'accepter ou non cette tâche *grand sourire*.",
    ["O"] = "Trouver une Sphère de draenéthyste sans défaut et l\'apporter à Kum\'isha le Collecteur",
    ["T"] = "Les efforts de Kum\'isha",
  },
  [2523] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La fleur-de-chant corrompue",
  },
  [2541] = {
    ["D"] = "Les chamans Pins-tordus qui habitent cet endroit ont découvert comment séparer l\'esprit d\'un druide dormant de son corps physique. Les furbolgs ont ranimé ma forme physique et l\'utilisent pour attaquer ceux qui essaient d\'explorer le Refuge des saisons de Ban\'ethil. Je suis impuissant, pris au piège dans le Rêve d\'émeraude.$B$BVous devez m\'aider. Les chamans Pins-tordus portent un charme étrange qu\'ils utilisent pour exécuter ce rituel et je voudrais l\'examiner. Je vous en prie, $N, il m’en faut un.",
    ["O"] = "Rapporter un Charme vaudou de chaman à Oben Grifferage dans le Refuge des saisons de Ban\'ethil.",
    ["T"] = "Le druide dormant",
  },
  [2561] = {
    ["D"] = "J\'ai examiné ce charme, $N, et je vois maintenant ce qu\'il faut faire. Prenez-le et suivez mes instructions.$B$BVous devez explorer les zones les plus reculées du Refuge des saisons de Ban\'ethil pour trouver mon corps sans âme... Je regrette de devoir en arriver à cette extrémité, mais je ne vois pas d\'autre moyen de me libérer du contrôle des Pins-tordus.$B$BVous devez tuer ma forme physique. Ensuite, utilisez le Charme vaudou sur mon corps. Lorsque vous aurez accompli cette tâche,revenez me voir.",
    ["O"] = "Sur ordre d\'Oben Grifferage, tuer son Corps privé d\'âme et utiliser ensuite le Charme vaudou.",
    ["T"] = "Le druide de la Griffe",
  },
  [2581] = {
    ["D"] = "Tout le monde sait que la mâchoire des hyènes possède une force énorme, mais jusqu’à quel point ? Au point de mordre dans trois pouces d\'acier au thorium ? Impossible ! Improbable ! Et pourtant je l\'ai vu de mes propres yeux.$B$BAllez dans les Terres foudroyées et apportez-moi trois joues de Raillecroc. En cherchant les Raillecroc, trouvez aussi les sangliers et les scorpoks : la préparation initiale exige deux Poumons de sanglier éclatés et une Pince de scorpok.",
    ["O"] = "Apporter 3 Joues de Raillecroc, 2 Poumons de sanglier éclatés et 1 Pince de scorpok au Mage de sang Drazial.",
    ["T"] = "Joues de Raillecroc",
  },
  [2582] = {
    ["D"] = "Oh $N ! Bienvenue. On dirait que vous avez apprécié l\'effet de ce mélange tiré des Raillecroc. Prenez garde, si l\'effet immédiat est un fantastique regain de force, à long terme les effets pourraient être dangereux pour votre esprit et votre intelligence.$B$BSachant cela, en voulez-vous encore ? Dans ce cas, apportez-moi trois autres joues de Raillecroc, deux poumons de sanglier éclatés et une pince de scorpok.",
    ["O"] = "Apporter 3 Joues de Raillecroc, 2 Poumons de sanglier éclatés et 1 Pince de scorpok au Mage de sang Drazial.",
    ["T"] = "La colère des âges",
  },
  [2583] = {
    ["D"] = "Sur ces terres désolées vivent deux espèces de sanglier : le sanglier cendré et le sanglier infernal.$B$BL\'endurance de ces bêtes les rend presque invulnérables, et même les autres prédateurs les évitent.$B$BNous devons faire de nouvelles analyses, $N. Si vous m\'apportez trois Poumons de sanglier éclatés, deux Pinces de scorpok et un Cerveau de basilic, je créerai un élixir divin. Vous serez mon premier cobaye !",
    ["O"] = "Apporter 3 Poumons de sanglier éclatés, 2 Pinces de scorpok et 1 Cerveau de basilic au Mage de sang Drazial.",
    ["T"] = "La vitalité du sanglier",
  },
  [2584] = {
    ["D"] = "Quand je vous ai administré l\'élixir, $N, je savais qu\'une ou deux applications ne rassasieraient pas votre appétit. Et maintenant, je peux voir la faim dans vos yeux. Le mélange provoque une dépendance, à laquelle même les plus forts ne résistent pas.$B$BQu\'il en soit ainsi ! Si vous voulez plus de Jus de poumon, retournez dans le désert et trouvez-moi trois poumons de sanglier éclatés, deux pinces de scorpok et un cerveau de basilic.",
    ["O"] = "Apporter 3 Poumons de sanglier éclatés, 2 Pinces de scorpok et 1 Cerveau de basilic au Mage de sang Drazial.",
    ["T"] = "L\'esprit du sanglier",
  },
  [2585] = {
    ["D"] = "Avez-vous observé les scorpoks au combat, $N ? Avez-vous vu la grâce et l\'agilité avec lesquelles ils se déplacent ?$B$BSi seulement nous pouvions isoler cette agilité et l\'encapsuler, nous serions riches au-delà de... je veux dire... ce serait bien pour la communauté scientifique.$B$BAidez-moi, $N et je vous permettrai d\'essayer le résultat.$B$BPour faire un échantillon stable, j\'ai besoin de trois Pinces de scorpok, de deux Gésiers de vautour et d\'un Poumon de sanglier éclaté.",
    ["O"] = "Apporter 3 Pinces de scorpok, 2 Gésiers de vautour et 1 Poumon de sanglier éclaté au mage de sang Drazial.",
    ["T"] = "Le coup décisif",
  },
  [2586] = {
    ["D"] = "Vous êtes en vie ! C\'est une bonne nouvelle, vraiment. Je suppose que vous revenez pour avoir plus de préparation de scorpok ?$B$BTrès bien, $N, retournez dans le désert et ne revenez pas avant d\'avoir prélevé trois pinces de scorpok, deux gésiers de vautour et un poumon de sanglier éclaté.",
    ["O"] = "Apporter 3 Pinces de scorpok, 2 Gésiers de vautour et 1 Poumon de sanglier éclaté au mage de sang Drazial.",
    ["T"] = "Le sel de scorpok",
  },
  [2601] = {
    ["D"] = "Ces terres désolées sont envahies par les basilics Rougepierre, $N. Ils se sont adaptés et multipliés plus que n\'importe quelle autre espèce.$B$BJ\'ai observé leur technique de chasse et leur comportement : leur processus de pensée est plus complexe que celui de certains humanoïdes.$B$BJe veux étudier leur intelligence !$B$BRapportez-moi dix cerveaux, $N. Cependant, le composé ne sera pas complet sans deux gésiers de vautour pour former la base de la solution.",
    ["O"] = "Apporter 10 Cerveaux de basilic et 2 Gésiers de vautour au Mage de sang Lynnore.",
    ["T"] = "La morsure du basilic",
  },
  [2602] = {
    ["D"] = "Votre concentration est déjà retombée ? Il faut faire d\'autres recherches !$B$BOh oui ! Le prix est le même, $N. Retournez dans les Terres foudroyées et revenez me voir avec dix cerveaux de basilic Rougepierre et deux gésiers de vautour.",
    ["O"] = "Apporter 10 Cerveaux de basilic et 2 Gésiers de vautour au Mage de sang Lynnore.",
    ["T"] = "Un esprit infaillible",
  },
  [2603] = {
    ["D"] = "La mort a réellement des ailes dans les Terres foudroyées, $r. J\'ai vu les Pourfendeurs noirs et les Racleur-d\'os festoyer sur de nombreux cadavres d\'explorateurs et déchiqueter même les carcasses les plus atteintes par la maladie. Je me demande comment leur système neutralise la peste qui sévit sur ces terres.$B$BAidez-moi dans mes études, $N ! J\'ai besoin de gésiers ! Egalement de deux joues de Raillecroc pour la base du mélange.$B$BLa récompense de vos efforts sera double.",
    ["O"] = "Apporter 10 Gésiers de vautour et 2 Joues de Raillecroc au Mage de sang Lynnore.",
    ["T"] = "La vigueur du vautour",
  },
  [2604] = {
    ["D"] = "Je vois que votre feu intérieur a diminué. Cela voudrait-il dire que l\'euphorie n\'a duré qu\'une heure ?$B$BSi vous voulez plus de Gomme de gésier, alors j\'ai besoin de plus de gésiers. Retournez dans le désert, $N. Et ne revenez pas avant d\'avoir récupéré dix gésiers et deux joues.",
    ["O"] = "Apporter 10 Gésiers de vautour et 2 Joues de Raillecroc au Mage de sang Lynnore.",
    ["T"] = "Domination spirituelle",
  },
  [2605] = {
    ["D"] = "J\'ai une idée… J\'ai entendu parler de créatures étranges appelées collecteurs de rosée, vivant au sud-ouest... Elles sont capables de stocker de l\'eau dans des glandes spéciales.$B$BJe pense que si je pouvais me procurer l\'une de ces glandes, je pourrais mettre au point une boisson qui étancherait ma terrible soif ! Pensez-vous que vous pourriez m\'aider, $N ?$B$BAllez au jardin des cactus, $N, c\'est là que vous trouverez les collecteurs de rosée.",
    ["O"] = "Trouver une Glande de rosée remplie et l\'apporter à Marin Noggenfogger à Gadgetzan.",
    ["T"] = "Le gobelin assoiffé",
  },
  [2606] = {
    ["D"] = "Beurk ! Cette substance a un goût épouvantable ! Trop pour mon idée... $B$BAttendez, qu\'est-ce que c\'est que ça ? Oh, je sais ! Vous pensez que je dois continuer de croire en cette substance. Bon alors...$B$BJ\'ai une idée brillante ! Apportez cette rosée à mon amie Sprinkle, c\'est un excellent chef. Dites-lui où nous en sommes et ce que nous voulons faire.$B$BJe veux dire, si c\'est aussi mauvais, il y a sûrement quelque chose à en faire... C\'est juste que moi, je ne le sens pas...",
    ["O"] = "Sur ordre de Marin Noggenfogger, aller voir Sprinkle à Gadgetzan.",
    ["T"] = "De bon goût",
  },
  [2607] = {
    ["D"] = "On dirait que vous avez rapporté plus d\'informations que je n\'en attendais. Les événements prennent une tournure étrange, $N.$B$BUn remède ? Hum... Rendez-vous à la Cave et allez voir le docteur Mixilpixil, il pourra peut-être vous aider.",
    ["O"] = "Aller voir le docteur Mixilpixil dans la Cave de la caserne.",
    ["T"] = "Le toucher de Zanzil",
  },
  [2608] = {
    ["D"] = "Oh Ciel ! Vous ressemblez à la mort. Je corrige : j\'ai vu la mort, vous semblez en pire état. Vous ressemblez à ce que serait la mort à son dernier instant.$B$BAh ! Vous entendez ce Noarm ? Notez-le !$B$B*soupir* Hum ! Oui, pour vous aider, je dois prendre un peu de votre sang. Ne vous inquiétez pas, ça ne fait mal que les… cinquante premières fois. Allongez-vous (/coucher) quand vous serez $gprêt:prête; pour le traitement, $N.",
    ["O"] = "S\'allonger (/coucher) pour être $gexaminé:examinée; par le docteur Mixilpixil. Comment peut-il trouver le remède s\'il ne peut pas trouver la cause ?",
    ["T"] = "Le toucher de Zanzil",
  },
  [2609] = {
    ["D"] = "Le diagnostic est unanime, $N. Vous êtes $gatteint:atteinte; de “itis”. $B$BHeureusement, j\'ai un remède.$B$BVoilà ce dont j\'ai besoin : une brassée de fleurs sauvages simples, une fiole plombée, un tube en bronze et une bobine de fil de soie chartreuse. La vieille Emma en a laissé pour moi au \"Fil le plus fin”. Le magasin se trouve le long des canaux.$B$BDépêchez-vous $N, et ne touchez personne jusqu\'à votre guérison. Oh ! Montgomery dit que vous devriez prendre une douche – c\'est lui qui le dit, pas moi.",
    ["O"] = "Apporter au docteur Mixilpixil une Brassée de Fleurs sauvages simples, une Fiole plombée, un Tube en Bronze et une Bobine de Fil de soie chartreuse. Le itis ne guérit pas tout seul.",
    ["T"] = "Le toucher de Zanzil",
  },
  [2621] = {
    ["D"] = "Désolé. C\'est tout ce que j\'ai pour l\'instant. Une mauvaise décision aux conséquences horribles. Si seulement j\'avais attendu ces ordres. Peut-être détaillaient-ils les horreurs qui nous attendaient ! Peut-être devaient-ils nous guider autrement ? En fin de compte, c\'est ma faute. Tout cela est ma faute.$B$BJe suis là et j\'attends les ordres. Je dois savoir. Je dois avoir ces ordres.$B$BLe commandant d\'ordonnance Ruag réside à Stonard. Trouvez-le et découvrez ce qui est arrivé aux ordres.",
    ["O"] = "Aller voir le commandant d\'ordonnance Ruag dans Stonard.",
    ["T"] = "Le déshonoré",
  },
  [2622] = {
    ["D"] = "Les ordres ? Que voulez-vous faire avec ?$B$BPourquoi vous impliquez-vous, $N ? Bien sûr que j\'ai relayé ces ordres ! Les soldats auxquels j\'ai envoyé ces ordres ont été pris en embuscade par les murlocs. Un seul s\'en est sorti, si on peut dire. Il peut à peine parler, ne peut pas marcher et on l\'alimente par un tube. Si vous voulez savoir où sont ces ordres, allez voir Bengor. Il est quelque part par là... et, $N, méfiez-vous des endroits où vous fourrez votre nez : il pourrait bien être mordu.",
    ["O"] = "Aller voir Bengor.",
    ["T"] = "Les ordres disparus",
  },
  [2623] = {
    ["D"] = "Le commandant d\'ordonnance a donné des ordres. Nous avons pris... pris des ordres. Sur le chemin des Terres foudroyées, pris en embuscade.$B$BLes murlocs... Beaucoup de murlocs... Un qui s\'appelle le Parleur des marécages. À pris les ordres de mes mains avant de me frapper le crâne avec sa lance. À l\'est... la caverne...$B$B<Bengor trépasse.>",
    ["O"] = "Retrouver les Ordres du Chef de guerre et les rendre au Héros déchu de la Horde.",
    ["T"] = "Le Parleur des marécages",
  },
  [2641] = {
    ["D"] = "Quand j\'étais gosse, ma sœur faisait à manger. C\'était horrible ! Mon estomac ne supportait pas sa cuisine, jusqu\'à ce que je trouve ce que j\'aime à appeler mon ingrédient \"secret\" ! Ajoutez-le à n\'importe quoi et... ouah ! le goût devient génial !$B$BDe quoi je parle ? D\'un champignon, le tragan violet. Si vous voulez que je vous aide à rendre buvable ce breuvage écœurant, allez aux Hinterlands et cherchez un petit lac.$B$BLe tragan violet ne vit que sous l\'eau.",
    ["O"] = "Collecter un Tragan violet et l\'apporter à Sprinkle, à Gadgetzan.",
    ["T"] = "L\'ingrédient secret de Sprinkle",
  },
  [2661] = {
    ["D"] = "Voilà la Poudre violette, $N. Portez-la à Marin et dites-lui qu\'il me doit une faveur !",
    ["O"] = "Sur ordre de Sprinkle, apporter la Poudre violette à Marin Noggenfogger à Gadgetzan.",
    ["T"] = "Livraison pour Marin",
  },
  [2662] = {
    ["D"] = "Maintenant, il faut mélanger cette poudre avec la rosée.$B$BPartez et revenez me voir un peu plus tard... Quand j\'aurai perfectionné le fabuleux Élixir Noggenfogger !",
    ["O"] = "Retourner voir Marin Noggenfogger une fois qu\'il aura créé son élixir.",
    ["T"] = "Elixir Noggenfogger",
  },
  [2681] = {
    ["D"] = "Vous ne pourrez pas à vous $gseul:seule; affronter les forces de Razelikh et de ses subordonnés.$B$BRassemblez un groupe d\'aventuriers pour libérer mes hommes de leur torture éternelle ! Vous devez briser dix-huit pierres.$B$BBattez-vous durement et mourez avec honneur, $N.",
    ["O"] = "Libérer 9 Serviteurs de Razelikh, 3 Serviteurs de Sevine, 3 Serviteurs d\'Allistarj et 3 Serviteurs de Grol. Retourner voir le Héros déchu une fois la tâche terminée. Il faut rester à proximité des pierres ou le processus échouera.",
    ["T"] = "Les pierres qui nous lient",
  },
  [2701] = {
    ["D"] = "Je vous en prie, prenez cette Relique des Anciens. Nous n\'en avons plus besoin.$B$BPuisse-t-elle bien vous servir dans votre combat contre la Légion.",
    ["O"] = "Ouvrir le coffre et prendre la récompense.",
    ["T"] = "Les héros des Anciens",
  },
  [2702] = {
    ["D"] = "Votre noblesse et votre héroïsme m\'ont honoré, $N.$B$BVous les avez honorés aussi…",
    ["O"] = "Aller voir le caporal Thund Splithoof.",
    ["T"] = "Les héros des Anciens",
  },
  [2721] = {
    ["D"] = "Un chapitre de vos aventures se termine, un autre commence, $N.$B$BL\'un de mes hommes manque toujours à l\'appel, il est perdu quelque part dans ces terres. Je parle du lieutenant Kirith, bien sûr.$B$BComme je l\'ai déjà dit, nous avons été tous deux capturés et torturés par Allistarj mais, pauvre Kirith, sa force a vacillé. Depuis ma cage, je pouvais entendre ce qu’il subissait, ses cris résonnaient dans toute la caverne. Je ne l’ai plus vu depuis.",
    ["O"] = "Découvrir ce qui est arrivé au Lieutenant Kirith.",
    ["T"] = "Kirith",
  },
  [2741] = {
    ["D"] = "",
    ["O"] = "Le Super-Oeuf-O-Matic est la dernière des inventions de Curgle Cranklehop. C\'est une machinerie très impressionnante.$B$BLe Super-Oeuf-O-Matic bafouille quelque chose lorsque vous vous approchez du panneau de commande.",
    ["T"] = "Le Super-Oeuf-O-Matic",
  },
  [2742] = {
    ["D"] = "Hé $r !$B$BS\'il plaît à vous, aidez Rin\'ji à s\'évader. Moi sais que $r pas aimer race de Rin\'ji, mais Rin\'ji demande pitié ! Rin\'ji doit sortir ici !$B$BCadeau agréable pour $r si $gil:elle; aider Rin\'ji à sortir...$B$BRin\'ji cache quelque chose de secret sur les Hauts-Surplombs.",
    ["O"] = "Escorter Rin\'ji hors du Gîte de Quel\'Danil, puis découvrir son secret caché sur les Hauts-Surplombs, à l’est.",
    ["T"] = "Rin\'ji est pris au piège !",
  },
  [2743] = {
    ["D"] = "Faisons vite, $N, mon temps ici est compté.$B$BIl faut détruire la triade de pouvoir qui protège le seigneur-démon Razelikh. Ce ne sera pas aussi facile que de libérer une âme damnée.$B$B<Kirith sourit.>$B$BVous voyez, chacun des trois subalternes de Razelikh porte autour du cou le tiers d\'une amulette créée par Razelikh. L\'amulette dont vous aurez besoin pour appeler le démon.$B$BRetournez voir Trebor et dites-lui comment appeler Razelikh. Il pourra vous aider à aller plus loin.",
    ["O"] = "Retourner voir le Héros déchu de la Horde avec ces découvertes.",
    ["T"] = "Le couvert de l\'obscurité",
  },
  [2744] = {
    ["D"] = "La triade est protégée par Razelikh.$B$BEn échange de leur servitude, Razelikh a accordé à chacun d\'eux un pouvoir presque illimité dans les Terres foudroyées, ainsi qu\'une amulette leur conférant l\'immortalité. Tant qu\'ils restent dans leurs terres, ils ne peuvent pas être tués.$B$BQuelqu\'un peut vous aider à poursuivre, $N. Allez à Azshara et trouvez le chasseur de démons, Loramus Thalipedes. Cherchez-le dans les îles.",
    ["O"] = "Aller voir Loramus Thalipedes à Azshara.",
    ["T"] = "Le chasseur de démons",
  },
  [2745] = {
    ["D"] = "Hum ! Une histoire intéressante à ne pas prendre à la légère. Ouais, je peux vous aider, mais ce que vous devez faire n\'est pas vraiment une justice... légale, si vous allez jusqu\'au bout.$B$BOn doit d’abord s\'occuper de Lescovar et de Marzon, et vite. Pensez-vous en être capable ?$B$BA vous de décider. Si oui, rendez-vous rapidement au château pour y rencontrer un vieil ami à moi, un gnome nommé Tyrion. Il observe Lescovar depuis maintenant quelques semaines. Il devrait pouvoir vous aider à en finir.",
    ["O"] = "Aller voir Tyrion au Château de Stormwind.",
    ["T"] = "En infiltrant le château",
  },
  [2746] = {
    ["D"] = "Trias veut accélérer les choses, hein ? Bien, nous pouvons le faire, mais je ne suis pas prêt. Je vais faire la partie difficile du boulot, mais c\'est vous qui allez vous salir les mains, d’accord ? Bon.$B$BIl faut que vous m\'obteniez certaines choses pour mettre mon plan à exécution. J\'ai le déguisement parfait pour que l\'Espion passe ces gardes.$B$BApportez-moi de la soie et deux pommes. Pour la soie, débrouillez-vous, mais vous trouverez des pommes parfaites à la petite ferme de Clara à l\'extérieur de la ville.",
    ["O"] = "Apporter 3 Etoffes de soie et 2 Pommes fraîches de Clara à Tyrion à Stormwind.",
    ["T"] = "Des objets d’une certaine importance",
  },
  [2747] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Un œuf extraordinaire",
  },
  [2748] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Un œuf de premier choix",
  },
  [2749] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Un œuf ordinaire",
  },
  [2750] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Un mauvais œuf",
  },
  [2751] = {
    ["D"] = "Vingt générations d\'Omosh ont tenu ce marteau, $N. Nous avons façonné cent mille tonnes de métal. Tragiquement, tout prend fin avec Orokk, l\'échec des Omosh : sans famille et sans héritier.$B$BJe ne peux pas laisser les savoir-faire secrets des Omosh mourir avec moi ! Quelqu\'un doit recueillir cet héritage, qu’il soit ou non un Omosh !$B$BProuvez à Orokk que vous êtes celui-là, $N. Apportez deux brassards guillochés en bronze, deux glaives en bronze et deux griffes aiguisées. Apportez-les à Orokk et nous pourrons continuer.",
    ["O"] = "Apporter 2 Brassards guillochés en bronze, 2 Glaives en bronze et 2 Griffes aiguisées à Orokk Omosh à Orgrimmar.",
    ["T"] = "Remparts barbares",
  },
  [2752] = {
    ["D"] = "Orokk voit l\'ardeur dans vos yeux, $N. Comme une éponge, vous absorbez la sagesse des leçons des Omosh.$B$BTrois leçons encore !$B$BRetournez à l\'enclume, absorbez-vous dans la chaleur de la forge, créez pour Orokk quatre marteaux de guerre en bronze et quatre haches de guerre en bronze. Ensuite seulement vous apprendrez le secret des Epaulettes en fer.",
    ["O"] = "Apporter 4 Haches de guerre en bronze et 4 Marteaux de guerre en bronze à Orokk Omosh à Orgrimmar.",
    ["T"] = "Épaulettes en fer",
  },
  [2753] = {
    ["D"] = "Deux leçons encore vous enseignera Orokk, $N. Vos muscles font mal, je sais, mais continuez pour les Omosh !$B$BApportez à Orokk quatre heaumes en fer émeraude, quatre bracelets en fer émeraude et deux jambières en fer émeraude – pour votre apprentissage et la souffrance de vos ennemis !",
    ["O"] = "Apporter 4 Heaumes en fer émeraude, 4 Bracelets en fer émeraude et 2 Jambières en fer émeraude à Orokk Omosh à Orgrimmar.",
    ["T"] = "Piétinés",
  },
  [2754] = {
    ["D"] = "Voici la dernière leçon d\'Orokk, $N. A l\'enclume sacrée et à la forge, vous devez retourner ! Apportez à Orokk deux solides maillets en fer, deux bottes en bronze argenté et deux gantelets en bronze argenté.$B$BRegardez la crainte grandir dans leurs yeux à votre approche !",
    ["O"] = "Apporter 2 Solides maillets en fer, 2 Bottes en bronze argenté et 2 Gantelets en bronze argenté à Orokk Omosh à Orgrimmar.",
    ["T"] = "Les cornes de Frénésie",
  },
  [2755] = {
    ["D"] = "Orokk ne pleure pas, $N. Il a une poussière dans l\'œil.$B$B<Orokk essuie une larme.>$B$BIl y a un dernier secret de famille Omosh qu\'Orokk va transmettre. Orokk mourra heureux, grâce à $N.$B$BApprenez la danse de joie des Omosh et vous apprendrez ensuite le secret des Poings de fer !",
    ["O"] = "Regarder et apprendre la Danse de joie des Omosh.",
    ["T"] = "Les joies des Omosh",
  },
  [2756] = {
    ["D"] = "Aux temps légendaires, Aturk aurait écrasé les crânes de ses ennemis et réduit leurs os en poudre comme combustible de lampe. Ces jours sont loin, $n. Le chef de guerre appelle à la paix parmi les clans. Les vieux ennemis ne sont plus observés que par des yeux fatigués ! $B$BC\'est incroyable, je sais, mais telle est la volonté de Thrall.$B$BAssez ! Vous êtes ici pour apprendre et vous allez apprendre ! Apportez-moi quatre cuirasses en acier et quatre heaumes en plaques d\'acier et vous aurez un avant-goût de la guerre !",
    ["O"] = "Apporter 4 Cuirasses en acier et 4 Heaumes en plaques d\'acier à Aturk l\'Enclume à Orgrimmar.",
    ["T"] = "Les vieilles voies",
  },
  [2757] = {
    ["D"] = "Écoutez, $ggamin:gamine; : vous n\'apprendrez plus rien ici.$B$BJe connais un type qui connaît un autre type. Il tourne autour de la forge à Baie-du-Butin. Montrez-lui cet insigne, il vous dira tout du mithril.$B$BAlors, qu\'en dites-vous ? Laissez cette matière aux bébés dans leurs langes, $N. Il est temps de progresser.$B$BSi ça vous intéresse, ce gars s\'appelle McGavan. Montrez-lui ce bibelot et il reconnaîtra votre engagement.",
    ["O"] = "Aller voir McGavan à Baie-du-Butin.",
    ["T"] = "Baie-du-Butin ou la mort !",
  },
  [2758] = {
    ["D"] = "Quand j\'avais votre âge, un nain du nom de Galvan m\'a pris sous son aile et m\'a tout appris du métier de Forgeron d\'armures. Avant de partir pour Baie-du-Butin, il m\'a fait promettre de former tous ceux qui auraient le désir et la détermination de devenir forgerons.$B$BBien, il est temps pour vous d\'apprendre quelque chose. Faites-moi six brassards en écailles dorées et vous comprendrez comment on devient forgeron, $N.",
    ["O"] = "Apporter 6 Brassards en écailles dorées à Hank le Marteau à Stormwind.",
    ["T"] = "Les origines de la forge",
  },
  [2759] = {
    ["D"] = "Galvan a quitté Baie-du-Butin depuis longtemps, mais les membres de l\'ordre sont restés en ville, espérant trouver un jour une autre personne digne de la tutelle de Galvan.$B$BPortez cet insigne à McGavan à Baie-du-Butin.$B$BSi vous avez réussi à aller aussi loin, il saura que vous êtes digne de lui. J\'ai foi en vous, $N !",
    ["O"] = "Aller voir McGavan à Baie-du-Butin.",
    ["T"] = "À la recherche de Galvan",
  },
  [2760] = {
    ["D"] = "La plupart de nos recrues ne vont jamais jusqu\'à Baie-du-Butin, $N. L\'éclat de l\'insigne est suffisant pour déclencher la convoitise, même chez le plus loyal des forgerons. Les imbéciles ! Cet insigne est une goutte d\'eau en comparaison de l\'océan des richesses que vous verrez !$B$BAllez trouver Galvan l\'Ancien et montrez-lui cette épingle. Il habite loin au nord, entre Zul Gurub et le tertre des ogres Mosh\'Ogg.",
    ["O"] = "Aller voir Galvan l\'Ancien dans la vallée de Strangleronce.",
    ["T"] = "L\'ordre du Mithril",
  },
  [2761] = {
    ["D"] = "Beaucoup de forgerons pensent qu\'extraire le minerai et le travailler sont des tâches séparées, indépendantes, mais qui peut être forgeron sans être mineur ? C\'est impossible ! Un forgeron accompli doit aussi être un mineur accompli.$B$BMontrez donc à Galvan ce dont vous êtes capable, $N. Apportez-moi quarante barres de fer et quarante barres de mithril. En échange, Galvan vous apprendra comment réaliser des jambières ornées en mithril !",
    ["O"] = "Apporter 40 Barres de mithril et 40 Barres de fer à Galvan l\'Ancien à Strangleronce.",
    ["T"] = "Ça fond, ça ne fond pas",
  },
  [2762] = {
    ["D"] = "On dit que le vrai-argent est plus solide et plus résistant que le mithril. Galvan n\'est pas contre les comparaisons, mais contrairement à ce que son nom laisse croire, le vrai-argent n\'est pas de l\'argent ! Le mithril est un minerai beaucoup plus noble ! Il faut comprendre cela par soi-même, $N.$B$BApportez-moi quarante barres de mithril et cinq barres de vrai-argent. En échange, vous saurez le secret des Gants ornés en mithril et mieux, vous comprendrez que le vrai-argent ne tient pas ses promesses !",
    ["O"] = "Apporter 40 Barres de mithril et 5 Barres de vrai-argent à Galvan l\'Ancien à Strangleronce.",
    ["T"] = "Le vif-argent n’est pas de l\'argent",
  },
  [2763] = {
    ["D"] = "Les pierres précieuses sont de la plus grande importance pour notre art, $N. Peu de gemmes sont plus précieuses que la citrine. Elle amplifie les propriétés magiques que nous insufflons à nos produits.$B$BPour cette leçon, nous allons renforcer quarante barres de mithril avec quatre citrines. Cela vous permettra de mieux comprendre la gemmologie magique et comment créer des épaulières ornées en mithril.",
    ["O"] = "Apporter 40 Barres de mithril et 4 Citrines à Galvan l\'Ancien à Strangleronce.",
    ["T"] = "L\'art de l\'imprégnation",
  },
  [2764] = {
    ["D"] = "Si vous êtes sur le point de terminer votre formation avec Galvan, vous devez aller à Gadgetzan.$B$BLà, vous trouverez mon meilleur élève : Trenton Lighthammer. Il a beaucoup à vous apprendre sur le mithril orné. Sinon restez ici et apprenez !$B$BOh bien sûr Gadgetzan est à Tanaris !",
    ["O"] = "Parler à Trenton Lighthammer à Gadgetzan.",
    ["T"] = "Le meilleur disciple de Galvan",
  },
  [2765] = {
    ["D"] = "J\'ai su, dès que je vous ai vu, que vous deviendriez un de mes meilleurs disciples ! Vous n’avez pas hésité à donner une relique à McGavan. Dans le seul but de le rencontrer. Une telle foi et une telle dévotion pour l\'art doivent être récompensées.$B$BVous avez obtenu une expertise dans l\'art de la forge, $N. Cueillez le fruit de vos efforts !",
    ["O"] = "Réclamez votre récompense à Galvan l\'Ancien !",
    ["T"] = "Maître Forgeron !",
  },
  [2766] = {
    ["D"] = "Vous découvrez une sorte d\'engin métallique en forme d\'œuf. Il revient à la vie lorsque vous tripotez l\'un de ses boutons, apparaissant comme un machin robotique gnome ! Une voix grésille.$B$B\"Mon nom est Oglethorpe Obnoticus, et mon robot guidé s\'est perdu ! Je vous récompenserai si vous le retrouvez. S\'il vous plaît allez placer cette balise dans le robot.\"$B$B\"J\'ai calculé la position de mon robot égaré : il est dans une caverne près du val des Griffes farouches !\"",
    ["O"] = "Placer la balise de détresse dans le robot d\'Oglethorpe qui se trouve dans une caverne près du val des Griffes farouches.",
    ["T"] = "Trouvez OOX-22/FE !",
  },
  [2767] = {
    ["D"] = "Le robot émet à nouveau la voix du gnome :$B$B\"Je dois déplacer OOX-22/FE dans un endroit dégagé et sûr pour qu\'il puisse entamer sa longue procédure de décollage. Il est équipé d\'un camouflage, mais il me faut du temps pour le rendre opérationnel. J\'ai besoin que vous escortiez le robot jusqu\'à... disons la Côte oubliée ! Ce serait un endroit parfait et assez vaste pour que tout puisse se mettre en route !\"$B$BEscortez-le jusque là-bas, et venez me parler à Baie-du-Butin ! Oglethorpe Obnoticus - terminé !\"",
    ["O"] = "Escorter OOX-22/FE au dock le long de la Côte oubliée, puis faire votre rapport à Oglethorpe Obnoticus à Baie-du-Butin.",
    ["T"] = "Sauvez OOX-22/FE !",
  },
  [2768] = {
    ["D"] = "Le sergent Bly m\'a volé ! Il a dit qu\'il ne faisait que l\'emprunter, mais il a volé mon bâtonnet divino-matic ! Sans lui, comment savoir où creuser de nouveaux puits ?$B$BTrouvez Bly et reprenez mon bâtonnet ! J\'ai entendu qu\'il emmenait une bande d\'aventuriers mal famés chasser les trésors à l\'ouest, à Zul\'Farrak. Je parie que vous le trouverez là-bas.$B$BEspérons que les trolls se seront déjà occupés de lui, car si vous devez l’affronter pour reprendre le bâtonnet, le combat sera difficile.",
    ["O"] = "Rapporter le Bâtonnet divino-matic à l\'Ingénieur en chef Bilgewhizzle, à Gadgetzan.",
    ["T"] = "Le bâtonnet divino-matic",
  },
  [2769] = {
    ["D"] = "Avez-vous rencontré les frères Brassbolt ? Ils sont dans les Salines en Kalimdor. Ils font des courses de voitures à fusée contre les gobelins. Ils gagnent régulièrement. Il faut dire que les voitures des gobelins explosent si souvent qu\'elles passent rarement la ligne d\'arrivée !$B$BMalgré cela, les frères Brassbolt cherchent toujours à avoir une voiture plus rapide. Wizzle m\'a dit qu\'il cherchait un composant très rare pour sa nouvelle expérience.$B$BIl a besoin d\'aide pour trouver ce composant. Allez l\'aider.",
    ["O"] = "Parler à Wizzle Brassbolts dans les Salines.",
    ["T"] = "Les frères Brassbolts",
  },
  [2770] = {
    ["D"] = "Au coeur de Zul\'Farrak, la cité des trolls des sables à Tanaris, il y a un bassin sacré. De ce bassin, les trolls invoquent une bête énorme ! Gahz\'rilla ! Elle est tellement puissante que même ses écailles crépitent d\'énergie. C\'est cette énergie que je convoite pour ma voiture !$B$BRapportez-moi les écailles électrifiées de Gahz\'rilla !$B$BMais l\'invocation de Gahz\'rilla est l’un des secrets les mieux gardés des trolls. Avant d’affronter le monstre, vous devez apprendre le secret des trolls.",
    ["O"] = "Rapporter les Ecailles électrifiées de Gahz\'rilla à Wizzle Brassbolts dans les Salines.",
    ["T"] = "Gahz\'rilla",
  },
  [2771] = {
    ["D"] = "Il semble que le maître ait planifié tout cela depuis le début. Je n\'ai pas appris à créer le genre d\'objet que vous savez créer, de même que vous ne savez pas créer ce que je sais faire.$B$BLe maître est sage, $N. Chacun de nous doit apprendre de l\'autre !$B$BMontrez-moi comment faire un camail en mithril et des épaulières ornées en mithril et je vous montrerai comment faire un heaume orné en mithril.",
    ["O"] = "Rapporter deux Camails en mithril et une paire d\'Epaulières ornées en mithril à Trenton Lighthammer.",
    ["T"] = "Une tête bien faite sur les épaules",
  },
  [2772] = {
    ["D"] = "Au dernier championnat intervallée des forgerons, tous les spectateurs ont vu un de ces chiens de la mine de la KapitalRisk m\'attaquer la jambe. Mais ils ne savent pas ce qui m\'a permis de continuer et de gagner ce championnat. Eh oui ! Des bottes ornées en mithril !$B$BJe vais avoir besoin d\'apprendre à faire des jambières ornées en mithril cette année, car la KapitalRisk participe encore au championnat ! Apprenez-moi comment faire les jambières et je vous apprendrai à faire les bottes !",
    ["O"] = "Rapporter deux paires de Bottes en mithril lourd et des Jambières ornées en mithril à Trenton Lighthammer.",
    ["T"] = "Le monde à vos pieds",
  },
  [2773] = {
    ["D"] = "Dans cette région, on m\'appelle le gars du mithril. Je fournis des équipements ornés en mithril aux aventuriers du monde entier.$B$B<Trenton chuchote.>$B$BPeu savent que je ne connais pas la technique des gants ornés en mithril. Mais chut ! Cela sera notre petit secret.$B$BEnseignez-moi cette technique, $N, et en échange je vous montre le point culminant de mon apprentissage chez Galvan : la cuirasse ornée en mithril.",
    ["O"] = "Rapporter deux Cuirasses lourdes en mithril et une paire de Gants ornés en mithril à Trenton Lighthammer.",
    ["T"] = "Le gars du mithril",
  },
  [2781] = {
    ["D"] = "ON RECHERCHE : Caliph Dard-de-scorpide !$B$BLa Compagnie des Eaux de Gadgetzan de Tanaris paiera une forte récompense pour la tête de Caliph Dard-de-scorpide, chef des hors-la-loi Bat-le-désert. Ses crimes sont :$B$B1) Assassinat d\'employés de la Compagnie des Eaux de Gadgetzan ;$B2) Vol des ressources de la Compagnie des Eaux de Gadgetzan ;$B3) Vol des biens de la Compagnie des Eaux de Gadgetzan.$BRapportez les preuves de la mort de Caliph Dard-de-scorpide à l\'ingénieur en chef Bilgewizzle pour toucher immédiatement votre récompense.",
    ["O"] = "Apporter la tête de Caliph Dard-de-scorpide à l\'Ingénieur en chef Bilgewizzle à Gadgetzan.",
    ["T"] = "ON RECHERCHE : Caliph Dard-de-scorpide",
  },
  [2782] = {
    ["D"] = "La tablette est couverte de lettres gravées par des trolls Witherbark. Le secret de Rin\'ji est réellement un mystère…$B$BIl est possible que vous ayez quelque chose de réellement précieux entre les mains. Mais l\'inverse est également possible. Un troll Witherbark en détresse ferait probablement n\'importe quoi pour s\'en sortir...",
    ["O"] = "Oran Snakewrithe, une érudite réputée pour sa connaissance des us et coutumes des trolls Witherbark, pourrait peut-être déchiffrer la tablette. Elle habite le Quartier de la magie à Undercity.",
    ["T"] = "Secret de Rin\'ji",
  },
  [2783] = {
    ["D"] = "Nous avons combattu le mauvais ennemi, $N.$B$BLa vraie puissance de cette région continue de croître alors que nous nous chamaillons avec les orcs sur cette terre aride.$B$BDevons-nous continuer à nous battre contre la Horde, pour finir écrasés un jour par les armées de la Légion ardente ? C\'est futile.$B$BMettez vos différences de côté, $N, parlez au Héros déchu de la Horde. Il sait ce qui se passe dans les Terres foudroyées.",
    ["O"] = "Parler au Héros déchu de la Horde à la frontière des Terres foudroyées et du marais.",
    ["T"] = "Querelles mesquines",
  },
  [2784] = {
    ["D"] = "Écoutez mon histoire, $N.",
    ["O"] = "Écouter le Héros déchu de la Horde raconter son histoire.",
    ["T"] = "Perte d\'honneur",
  },
  [2801] = {
    ["D"] = "Avant que nous continuions, vous devez écouter mon histoire.",
    ["O"] = "Écouter le Héros déchu de la Horde raconter son histoire.",
    ["T"] = "Une histoire de chagrin",
  },
  [2821] = {
    ["D"] = "Ma philosophie, concernant le travail du cuir, c’est que mon travail doit être le meilleur possible : pas de compromis. Quelqu’un disant que la qualité ne compte pas essaye de vous escroquer !$B$BDans ma recherche de matières premières de qualité, rien ne vaut la peau de yéti épaisse. Elle provient des yétis de Feralas, et il y en a beaucoup au val des Griffes farouches. J\'en ai besoin et j’échangerai l\'un de mes objets en cuir contre un ballot de dix peaux. Si cela vous semble équitable, le marché est conclu.",
    ["O"] = "Rapporter 10 Peaux de yéti épaisses à Pratt McGrubben au bastion de Feathermoon.",
    ["T"] = "La marque de la qualité",
  },
  [2822] = {
    ["D"] = "Je suis fier de mon travail et je sais que cela se voit : la qualité ne doit jamais être dévalorisée. Quiconque dit que la qualité n’est pas primordiale ferait également croire que les abysses sont lumineux.$B$BDans ma recherche de matières premières de qualité, rien ne vaut la peau de yéti épaisse. Elle provient des yétis de Feralas, et il y en a beaucoup au val des Griffes farouches, à l\'ouest d\'ici. Je vous laisserai choisir un objet parmi mes créations contre un ballot de dix peaux. Si cela vous convient, nous faisons affaire !",
    ["O"] = "Apporter 10 Peaux de yéti épaisses à Jangdor Swiftstrider au Camp Mojache.",
    ["T"] = "La marque de la qualité",
  },
  [2841] = {
    ["D"] = "Technologie gnome… Ce terme était autrefois brandi comme un slogan. Les temps ont changé. Ils ont désormais une nouvelle plate-forme, supérieure à tous nos modèles de Déchiqueteurs combinés. J\'ai besoin des plans de cette plateforme, $N !$B$BMalheureusement ceux-ci sont enfermés dans le coffre du mekgénieur Thermaplugg. Et pour compliquer les choses, la combinaison du coffre n\'est connue que de lui seul.$B$BFaites ce qu’il faut pour obtenir cette combinaison, $N. Puis ouvrez le coffre et ramenez-moi ces plans !",
    ["O"] = "Obtenir la Combinaison du coffre de Thermaplugg, prendre les Plans de la plate-forme et les apporter à Nogg à Orgrimmar.",
    ["T"] = "Guerre des plates-formes",
  },
  [2842] = {
    ["D"] = "Dès que nous aurons des nouvelles de Kernobee à propos de la nouvelle plate-forme des gnomes, Scooty et moi commencerons à travailler sur le Transpolyporteur 6000 : le moyen le plus rapide et le plus sûr d’aller à Gnomeregan.$B$BIl est enfin prêt à recevoir des passagers, $N. Si vous désirez l\'essayer, allez à Baie-du-Butin et parlez avec Scooty au perchoir des Cavaliers célestes. Pour aller à Baie-du-Butin, prenez le bateau depuis Ratchet.$B$BVous pouvez aussi traverser les montagnes de Dun Morogh, mais elles sont infestées de nains…",
    ["O"] = "Parler à Scooty à Baie-du-Butin.",
    ["T"] = "L\'ingénieur en chef Scooty",
  },
  [2843] = {
    ["D"] = "Aussi longtemps que vous êtes en possession du transpondeur des gobelins, tout ce que vous avez à faire est de traverser le portail et vous vous retrouverez à Gnomeregan.$B$BIl y aura peut-être des effets secondaires bizarres, mais aucun ne devrait être permanent... plus maintenant.$B$BJ\'oubliais de parler du plus important : le retour. Lorsque vous serez $gprêt:prête; à revenir, allez au portail de multitéléportation de Gnomeregan !$B$BBon, il faut maintenant régler le transpondeur selon votre poids et votre taille.",
    ["O"] = "Attendre que Scooty calibre le transpondeur des gobelins.",
    ["T"] = "Gnomer-paaarti !",
  },
  [2844] = {
    ["D"] = "Je suis vraiment bon à rien, $N. J\'étais supposé surveiller mon amie Shay, et je l’ai perdue. Je ne suis pas sûr de ce qui s\'est passé, mais la dernière fois que je l\'ai vue, nous étions en train de marcher dans les ruines au nord d\'ici.$B$BTout allait bien, mais lorsque je me suis retourné vers elle... elle avait disparu.$B$BMaintenant je ne sais plus quoi faire. Vous avez une idée, $N ? Je suis affreusement inquiet.$B$BEt si vous alliez la chercher ? Vos jambes vous permettent d\'aller plus vite que moi.",
    ["O"] = "Retrouver la jeune elfe de la nuit perdue.",
    ["T"] = "Le garde géant",
  },
  [2845] = {
    ["D"] = "Pouvez-vous m\'aider à retrouver mon chemin jusqu\'à mon ami, Croque-roc ? Il était supposé me surveiller… Mais j\'ai tendance à folâtrer de-ci de-là. Il y a tant de choses à voir et je me laisse facilement distraire.$B$BIl y a toutefois une chose qui me ramène toujours.$B$BDans ce coffre, vous trouverez une cloche qui m\'est précieuse. J\'adore sa sonorité. Si je m\'égare, vous n\'avez qu\'à la faire sonner, je reviendrai immédiatement.",
    ["O"] = "Prendre la Cloche de Shay dans le coffre.$B$BEscorter Shay Leafrunner au Camp de Croque-roc.",
    ["T"] = "Shay l\'égarée",
  },
  [2846] = {
    ["D"] = "Il y a longtemps, je possédais une splendide pièce de joaillerie, la tiare des abysses. Elle n\'était pas seulement belle, elle détenait un grand pouvoir, pour ceux qui savaient l\'utiliser.$B$BQuand l\'hydromancienne Velratha apprit son existence, elle l’a convoitée. Elle a envoyé des agents chez moi pour la voler pendant mon absence. Voleurs !$B$BJe veux ma tiare ! Allez à Zul\'Farrak, la cité des trolls à Tanaris, trouvez Velratha et reprenez-lui la tiare. Rapportez-la et vous gagnerez mes faveurs.",
    ["O"] = "Rapporter la Tiare des abysses à Tabetha au marécage d\'Âprefange.",
    ["T"] = "La tiare des abysses",
  },
  [2847] = {
    ["D"] = "$n, vous avez acquis une bonne connaissance du travail du cuir ! Mais je sais des choses qui pourraient vous intéresser… L\'armure en cuir sauvage ! L’intérêt du cuir sauvage tient aux propriétés chaotiques des sauvageonnes et de quelques autres créatures d\'ici. L\'armure que vous réaliserez sera ce qu’un artisan de votre compétence peut fabriquer de mieux.$B$BJe vous apprendrai à la réaliser si vous acceptez de travailler pour moi pendant un moment. Le coût initial est juste de dix pièces de cuir épais. Après nous parlerons technique.",
    ["O"] = "Rapporter 10 Cuirs épais à Pratt McGrubben au bastion de Feathermoon.",
    ["T"] = "L\'armure en cuir sauvage",
  },
  [2848] = {
    ["D"] = "Les épaulières en cuir sauvage protègent efficacement le haut des bras et les épaules. Comme l\'ensemble des pièces d\'armure en cuir sauvage, elles bénéficient d\'une qualité supplémentaire, mais aléatoire, en raison de l\'utilisation des sauvageonnes.$B$BPour ce patron, j’ai besoin de six renforts d\'armure épais et d’une sauvageonne. Cela ne devrait pas vous poser de problèmes.",
    ["O"] = "Apporter 6 Renforts d\'armure épais et 1 Sauvageonne à Pratt McGrubben au bastion de Feathermoon.",
    ["T"] = "Les épaulières en cuir sauvage",
  },
  [2849] = {
    ["D"] = "Le gilet en cuir sauvage est la pièce maîtresse de toute armure en cuir sauvage. Il procure une protection maximale du torse tout en permettant à la puissante magie des sauvageonnes de se diffuser dans l’armure.$B$BPour ce modèle, je demande deux paires de gants en écailles de tortue, deux cuirasses en écailles de tortue et une sauvageonne. C\'est une grosse commande, mais vous ne devriez pas avoir de problème à me la fournir.",
    ["O"] = "Rapporter 2 Cuirasses en écailles de tortue, 2 paires de Gants en écailles de tortue et 1 Sauvageonne à Pratt McGrubben au bastion de Feathermoon.",
    ["T"] = "Le gilet en cuir sauvage",
  },
  [2850] = {
    ["D"] = "Un casque en cuir sauvage offre une protection optimale sans sacrifier au confort. Comme pour l\'armure en cuir sauvage, les sauvageonnes lui apportent une amélioration puissante, mais aléatoire. Les porteurs d\'armures de cuir raffolent de cet article !$B$BPour ce modèle, j\'ai besoin de deux tuniques de la nuit, deux bandeaux de la nuit et d\'une sauvageonne. Complétez cette commande et le patron est à vous !",
    ["O"] = "Rapporter 2 Tuniques de la nuit, 2 Bandeaux de la nuit et 1 Sauvageonne à Pratt McGrubben au bastion de Feathermoon.",
    ["T"] = "Le casque en cuir sauvage",
  },
  [2851] = {
    ["D"] = "Vous avez déjà beaucoup appris, $N. Mais ce n’est pas fini. Après viennent les bottes en cuir sauvage, la pierre angulaire de tout l\'équipement.$B$BCes bottes protègent bien les pieds tout en étant très confortables. Les semelles souples sont une bénédiction pour ceux qui se déplacent discrètement.$B$BPour ces bottes, j\'ai besoin de deux pantalons de la nuit, de deux paires de bottes de la nuit, ainsi que de deux sauvageonnes. Lorsque vous aurez tout cela, faites-le-moi savoir.",
    ["O"] = "Rapporter 2 Pantalons de la nuit, 2 paires de Bottes de la nuit et 2 Sauvageonnes à Pratt McGrubben au bastion de Feathermoon.",
    ["T"] = "Les bottes en cuir sauvage",
  },
  [2852] = {
    ["D"] = "Vous pouvez maintenant apprendre des techniques plus complexes, $N. Les jambières en cuir sauvage sont un des équipements en cuir les plus résistants et les plus puissants. La taille, les hanches et les parties vitales sont particulièrement bien protégées. Les aventuriers qui l\'utiliseront apprécieront grandement cette protection combinant l\'amélioration apportée par la sauvageonne.$B$BPour ce modèle, j\'ai besoin de deux casques en écailles de tortue, de deux brassards en écailles de tortue et de deux sauvageonnes.",
    ["O"] = "Rapporter 2 Casques en écailles de tortue, 2 Brassards en écailles de tortue et 2 Sauvageonnes à Pratt McGrubben au bastion de Feathermoon.",
    ["T"] = "Les jambières en cuir sauvage",
  },
  [2853] = {
    ["D"] = "Eh bien, $n ! Vous êtes un modèle de rapidité. Vous connaissez tous les patrons de l\'armure en cuir sauvage, sauf un. Je ne vous demanderai rien pour celui-là, vous le méritez… mais je ne peux pas vous l’apprendre moi-même. Mon maître est le seul qui puisse vous transmettre ce savoir.$B$BPortez cette lettre à Telonis, il enseigne le travail du cuir à Darnassus. Montrez-lui la lettre et laissez-le vous reconnaître comme son égal, ainsi que je l\'ai fait.",
    ["O"] = "Donner la Lettre de Pratt à Telonis, le Maître tanneur de Darnassus.",
    ["T"] = "Maître du cuir sauvage",
  },
  [2854] = {
    ["D"] = "$n, la renommée de vos talents vous précède. Je connais quelque chose qui pourrait vous intéresser... l\'armure en cuir sauvage ! Le cuir sauvage tire ses propriétés des sauvageonnes que l\'on trouve dans la végétation luxuriante et sur quelques créatures. Cela ne devrait pas être compliqué pour quelqu\'un de votre niveau.$B$BJe vous enseignerai, mais en échange vous travaillerez pour moi pendant un moment. La première chose consiste à me rapporter dix pièces de cuir épais. Après nous discuterons technique.",
    ["O"] = "Rapporter 10 Cuirs épais à Jangdor Swiftstrider au Camp Mojache.",
    ["T"] = "L\'armure en cuir sauvage",
  },
  [2855] = {
    ["D"] = "Les épaulières en cuir sauvage protègent efficacement le haut des bras et les épaules. Comme toutes les pièces d\'armure en cuir sauvage, elles bénéficient d\'une amélioration puissante, mais aléatoire, grâce aux sauvageonnes.$B$BPour ce modèle, je demande six renforts d\'armure épais et une sauvageonne. Cela ne devrait pas vous poser trop de problèmes.",
    ["O"] = "Apporter 6 Renforts d\'armure épais et 1 Sauvageonne à Jangdor Swiftstrider au Camp Mojache.",
    ["T"] = "Les épaulières en cuir sauvage",
  },
  [2856] = {
    ["D"] = "Le gilet en cuir sauvage est la pièce maîtresse de toute armure en cuir sauvage. Il procure une protection maximale du torse tout en permettant à la puissante magie des sauvageonnes de se diffuser dans l’armure.$B$BPour ce patron, il me faut deux cuirasses et deux paires de gants en écailles de tortue, et une sauvageonne. Ce n\'est pas une leçon facile, mais vous disposez clairement des talents pour la mener à bien.",
    ["O"] = "Rapporter 2 Cuirasses en écailles de tortue, 2 paires de Gants en écailles de tortue et une Sauvageonne à Jangdor Swiftstrider au Camp Mojache.",
    ["T"] = "Le gilet en cuir sauvage",
  },
  [2857] = {
    ["D"] = "Un casque en cuir sauvage offre au porteur une protection optimale sur les parties vitales sans sacrifier au confort. Comme pour l\'armure en cuir sauvage, les sauvageonnes lui confèrent une amélioration puissante et aléatoire. Les porteurs d\'armure en cuir sauvage souhaiteront avoir cette pièce d\'équipement !$B$BPour ce modèle, j\'ai besoin de deux tuniques de la nuit, deux bandeaux de la nuit et d\'une sauvageonne. Rapportez-les et le patron est à vous !",
    ["O"] = "Rapporter 2 Tuniques de la nuit, 2 Bandeaux de la nuit et 1 Sauvageonne à Jangdor Swiftstrider au Camp Mojache.",
    ["T"] = "Le casque en cuir sauvage",
  },
  [2858] = {
    ["D"] = "Vous avez déjà beaucoup appris, $N. Mais ce n’est pas tout. L\'étape suivante concerne les bottes en cuir sauvage.$B$BElles protègent bien les pieds tout en restant confortables. Les semelles souples sont une bénédiction pour ceux qui aiment se déplacer discrètement.$B$BPour ces bottes, j\'ai besoin de deux pantalons de la nuit et deux paires de bottes de la nuit, ainsi que de deux sauvageonnes. Lorsque vous aurez tout cela, nous continuerons.",
    ["O"] = "Rapporter 2 Pantalons de la nuit, 2 paires de Bottes de la nuit et 2 Sauvageonnes à Jangdor Swiftstrider au Camp Mojache.",
    ["T"] = "Les bottes en cuir sauvage",
  },
  [2859] = {
    ["D"] = "Vous pouvez maintenant apprendre des techniques plus complexes, $N. Les jambières en cuir sauvage sont un des équipements en cuir les plus résistants et les plus puissants. La taille, les hanches et les parties vitales sont particulièrement bien protégées. Les aventuriers qui l\'utiliseront apprécieront grandement cette protection combinant l\'amélioration apportée par la sauvageonne.$B$BPour ce modèle, j\'ai besoin de deux casques en écailles de tortue, de deux brassards en écailles de tortue et de deux sauvageonnes.",
    ["O"] = "Rapporter 2 Casques en écailles de tortue, 2 Brassards en écailles de tortue et 2 Sauvageonnes à Jangdor Swiftstrider au Camp Mojache.",
    ["T"] = "Les jambières en cuir sauvage",
  },
  [2860] = {
    ["D"] = "$n, vous avez appris tout ce que je pouvais vous enseigner. Il reste néanmoins un dernier élément de l’armure en cuir sauvage à apprendre. Je ne vous demanderai rien pour celui-là, vous l\'avez mérité. Mais je ne peux vous le donner.$B$BMon maître est la seule personne à pouvoir vous l’enseigner. Donnez cette lettre à Una, elle enseigne le travail du cuir à Thunder Bluff. Montrez-lui la lettre et laissez-la vous reconnaître comme son égal, ainsi que je l\'ai fait.",
    ["O"] = "Donner la Lettre de Jangdor à Una, le Maître tanneur de Thunder Bluff.",
    ["T"] = "Maître du cuir sauvage",
  },
  [2861] = {
    ["D"] = "La rumeur dit que Tabetha, une collègue mage qui vit loin dans le marécage d\'Âprefange, a un compte à régler avec un certain troll des sables à Zul\'Farrak. Si vous parlez à Tabetha, elle pourra vous en dire davantage.$B$BEt invitez quelques amis, $N. Si Tabetha vous envoie à Zul\'Farrak, il ne faut pas y aller $gtout seul:toute seule;.$B$BVous trouverez le cottage de Tabetha à l\'ouest de Theramore, juste au nord des ruines Cognepierres.",
    ["O"] = "Parler à Tabetha dans le marécage d\'Âprefange.",
    ["T"] = "La mission de Tabetha",
  },
  [2862] = {
    ["D"] = "Les gnolls… Ils sont plus qu\'une nuisance. Ils sont une menace constante pour ce campement ! Qu\'ils soient natifs de Feralas ou non n\'a aucune importance, si nous ne prenons pas bientôt des mesures appropriées contre eux, nous serons balayés de la région.$B$BLes attaques contre le camp sont de pire en pire. Nous avons besoin de votre aide pour réduire leur nombre. Ramenez-moi dix crinières et je vous récompenserai de vos efforts.$B$BAllez !",
    ["O"] = "Rapporter 10 Crinières de gnolls Griffebois à Hadoken Swiftstrider au Camp Mojache.",
    ["T"] = "La guerre avec les Griffebois",
  },
  [2863] = {
    ["D"] = "Le plan concerne divers chefs gnolls - les Alphas pour reprendre leur propre appellation - et leur élimination successive en un minimum de temps. Si nous pouvions exécuter ce genre d\'éradication rapide et précise contre eux, cela amènerait le chaos dans leurs rangs. Et peut-être que le message passerait même à l\'intérieur de leurs têtes épaisses et infestées de puces que nous pouvons et allons les détruire !$B$BJe veux que vous soyez $gle messager:la messagère; de ma volonté. Eliminez cinq de leurs Alphas en une heure pour cette mission, $N, ou nous resterons dans l\'impasse.",
    ["O"] = "Tuer 5 Alphas Griffebois et retourner voir Hadoken Swiftstrider en moins d\'une heure.",
    ["T"] = "Frappe sur les Alphas",
  },
  [2864] = {
    ["D"] = "Mon cousin Tran\'rek me pose un problème. Il m\'a promis une cargaison de carapaces de Scarabées il y a quelques semaines, et je n\'ai toujours rien reçu. C’est mon cousin, je n\'aimerais donc pas le rappeler méchamment à l’ordre.$B$BPouvez-vous lui parler avant que je ne commette un acte regrettable ? Dites-lui que j\'ai vraiment besoin de cette cargaison.$B$BGadgetzan est dans le nord de Tanaris, en Kalimdor.",
    ["O"] = "Parler à Tran\'rek à Gadgetzan.",
    ["T"] = "Tran\'rek",
  },
  [2865] = {
    ["D"] = "Les scarabées de Tanaris ont une carapace très dure ! Si dure qu’elle est utilisée dans de nombreux domaines.$B$BEn fait ces carapaces sont si utiles que les scarabées ont presque disparu !$B$BJe sais où trouver des scarabées, mais vous devez me promettre de ramener leurs carapaces. Promis ?$B$BOK, les scarabées ont une colonie à Zul\'Farrak. Je pense que les trolls ne les chassent pas pour leurs carapaces.$B$BMais vous si ! Allez à Zul\'Farrak et trouvez-moi ces carapaces intactes !",
    ["O"] = "Rapporter 5 Carapaces de scarabée intactes à Tran\'rek à Gadgetzan.",
    ["T"] = "Les carapaces de scarabées",
  },
  [2866] = {
    ["D"] = "Au sud se trouvent les ruines de Solarsal, un site bien antérieur à l\'ère moderne. Nous avons exploré ces ruines et rien trouvé, aussi les avons-nous délaissées.$B$BTrès récemment, les nagas Hainecrête se sont installés en nombre dans les ruines. Au début, nous pensions à une attaque. Bien que cela soit toujours possible, nous croyons qu\'il s\'agit d\'autre chose.$B$BAllez là-bas, explorez les ruines et rendez compte de tout ce qui vous paraîtra inhabituel. Bonne chance, $n.",
    ["O"] = "Explorer les ruines de Solarsal et enquêter sur la présence des nagas dans le secteur.",
    ["T"] = "Les ruines de Solarsal",
  },
  [2867] = {
    ["D"] = "Après la découverte du belvédère étrange au cœur des ruines de Solarsal, il est temps de retourner au bastion de Feathermoon et faire votre rapport au général Shandris Feathermoon.",
    ["O"] = "Retourner voir Shandris Feathermoon au bastion de Feathermoon et lui rendre compte de vos découvertes.",
    ["T"] = "Retourner au bastion de Feathermoon",
  },
  [2868] = {
    ["D"] = "Turn this badboy into some troll tomb in the Sunken Temple, and get something cool!",
    ["O"] = "",
    ["T"] = "<TXT> The Shriveled Heart",
  },
  [2869] = {
    ["D"] = "La meilleure approche est de penser, indépendamment du projet réel des nagas, qu\'ils tentent de prendre possession de l\'île. Nous devons réagir énergiquement avant qu\'il ne soit trop tard.$B$BRapportez-moi dix écailles de Hainecrête en bon état ; pas besoin de vous expliquer comment vous les procurer. Vous diminuerez leur puissance et affaiblirez leur présence sur l\'île de Sardor, ce qui joue en notre faveur.",
    ["O"] = "Rapporter 10 Ecailles de naga Hainecrête à Latronicus Moonspear au bastion de Feathermoon.",
    ["T"] = "Contre les Hainecrête",
  },
  [2870] = {
    ["D"] = "$N, de nouveaux rapports nous donnent une bonne idée de la chaîne de commandement des nagas. Parmi le tout-venant se trouve des individus de sang royal, et ce sont eux qui prennent toutes les décisions.$B$BNous avons identifié l\'un de ces nobles, le seigneur Shalzaru. Nos agents disent qu\'il a découvert une relique étrange au cours de fouilles récentes qu’il a menées dans une caverne au sud des ruines principales, sur l\'île de l\'Effroi.$B$BArrêtez les autres en neutralisant Shalzaru et rapportez-moi sa relique afin que nous puissions l\'étudier.",
    ["O"] = "Eliminer le seigneur Shalzaru et rapporter la mystérieuse relique qu\'il a découverte à Latronicus Moonspear au bastion de Feathermoon.",
    ["T"] = "Contre le seigneur Shalzaru",
  },
  [2871] = {
    ["D"] = "Si vous étiez assez aimable de porter la relique à ma femme Vestia, je vous en serai très reconnaissant. Elle n\'est pas seulement une excellente prêtresse, elle fait également partie des meilleurs érudits que vous puissiez trouver. Je n\'arrive pas à comprendre l’usage de cet objet. Si quelqu\'un peut le savoir, c\'est ma femme... et nous avons besoin de savoir de quoi il s\'agit !$B$BElle est juste derrière le virage, sûrement en train d\'étudier. Merci, $n.",
    ["O"] = "Porter la Relique mystérieuse à Vestia Moonspear au bastion de Feathermoon.",
    ["T"] = "Livraison de la relique",
  },
  [2872] = {
    ["D"] = "Mon vieux copain Stoley me doit une bouteille d’un rhum très spécial. Il tient un bar à Port Gentepression. C\'est dans Tanaris, en Kalimdor. Il soutient que ce rhum est plus fort que tout ce que l\'on peut trouver à Baie-du-Butin. Il y croit dur comme fer. À tel point que si je ne suis pas de son avis après avoir goûté cet alcool, je double ce qu\'il me doit !$B$BJe pense que depuis la dernière fois, il a perdu confiance, car il ne m’a toujours pas envoyé son fameux rhum à goûter. Un pari est un pari, je ne reviendrai pas dessus. Pourquoi n\'iriez-vous pas lui parler un peu ?",
    ["O"] = "Parler à Stoley, à Port Gentepression.",
    ["T"] = "Les dettes de Stoley",
  },
  [2873] = {
    ["D"] = "Je dois du rhum à MacKinley \"Loup des mers\" pour payer une dette de jeu, mais je n\'en ai plus ! Le capitaine Cuergo a volé ma dernière caisse et il l\'a entreposée dans cette ville de pirates fortifiée, au sud.$B$BSi vous pouviez rapatrier ma cargaison de rhum, alors je pourrais prouver à MacKinley que la gnôle de Kalimdor est autrement plus musclée que le petit-lait qu\'ils boivent à Baie-du-Butin.",
    ["O"] = "Apporter le Chargement de Stoley à Port Gentepression et remettez-le à Stoley.",
    ["T"] = "Chargement de Stoley",
  },
  [2874] = {
    ["D"] = "Merci de votre aide, $N. Voici une bouteille de rhum de cale Gentepression. Portez-la à MacKinley et il pourra remballer ses préjugés à propos des alcools de Baie-du-Butin.",
    ["O"] = "Porter la Bouteille de Stoley à MacKinley \"Loup des mers\" à Baie-du-Butin.",
    ["T"] = "Livraison à MacKinley",
  },
  [2875] = {
    ["D"] = "Le vil pirate André Barbe-en-feu est recherché pour crimes contre Gentepression et Gadgetzan.$B$BPar décret du conseil gouvernemental, quiconque lit ceci est autorisé à utiliser la force, et à tuer si nécessaire, pour procéder à l\'arrestation de André et de ses complices.$B$BPar un autre décret, si André était décédé ou indisposé pendant son interrogatoire, sa tête servirait de preuve et la présentation de ladite tête au Chef de la sécurité Bilgewizzle constitue un contrat valable et inaliénable.",
    ["O"] = "Rapporter la tête d\'André au Chef de la sécurité Bilgewhizzle à Port Gentepression.",
    ["T"] = "ON RECHERCHE : André Barbe-en-feu",
  },
  [2876] = {
    ["D"] = "Ce parchemin répertorie les cargaisons envoyées entre Port Gentepression et Baie-du-Butin. La liste contient le nom des navires et des dates, passées ou à venir. Certaines lignes sont cochées d’un grand X. D\'autres sont annotées avec \"Bon butin\" ou \"Dur combat\".$B$BQuelqu\'un à Port Gentepression apprécierait certainement de voir ce document.",
    ["O"] = "Rapporter le Planning des expéditions aux autorités de Port Gentepression.",
    ["T"] = "Horaires des bateaux",
  },
  [2877] = {
    ["D"] = "Nous, les Wildhammer, nous souvenons du passé, mieux que nos frères d\'Ironforge. Si vous désirez vous faire des amis, alors travaillez pour nous !$B$BLa Horde a abandonné ses positions dans les Hinterlands, autour du rocher de l\'Affût qui est maintenant envahi par des Limaces et des Limons. Nous avons des plans pour ce secteur, mais nous avons besoin de quelqu\'un pour faire du nettoyage.$B$BC\'est là que vous intervenez ! Allez là-bas et liquidez dix Limaces vertes et dix Limons de jade puis revenez me faire votre rapport ici. Exécution !",
    ["O"] = "Tuer 10 Limaces vertes et 10 Limons de jade puis faire votre rapport à Fraggar Thundermantle au Nid-de-l\'Aigle",
    ["T"] = "Nettoyer le rocher de l\'Affût",
  },
  [2878] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La fleur-de-chant corrompue",
  },
  [2879] = {
    ["D"] = "Il semble que ce bâton soit le Bâton d\'Equinex. C\'est une clé qui permet d\'ouvrir le Monolithe d\'Equinex qui est érigé dans les ruines de Vent-du-Corbeau, sur le continent, à l\'ouest du Bosquet du rêve.$B$BLocalisez les quatre flammes qui brûlent toujours dans les ruines: Samha, Imbel, Byltan et Lahassa. Là, retrouvez leur essence et pendant que vous êtes près du Monolithe, utilisez cette essence pour dynamiser le Bâton d\'Equinex. Vous pourrez alors ouvrir le Monolithe et prendre l\'artefact sacré qui s\'y trouve.",
    ["O"] = "Dynamiser le Bâton de Troyas et trouver le Monolithe d\'Equinex.",
    ["T"] = "Le Bâton d\'Equinex",
  },
  [2880] = {
    ["D"] = "Comprenez bien ceci, $N. Nous ne faisons PAS partie de l\'Alliance ! Les Wildhammer sont indépendants, ils prouvent leur valeur par l\'action et leurs actes et non en bavardant, contrairement à ceux qui parlent afin de camoufler leur passivité !$B$BSi vous cherchez des amis par ici, commencez par prouver votre valeur ! Ces idiots de trolls qui remplissent les Hinterlands portent parfois des colliers tribaux. Rapportez-en cinq et nous considérerons que vous avez fait un pas dans la bonne direction.",
    ["O"] = "Rapporter 5 Colliers tribaux trolls à Fraggar Thundermantle au Nid-de-l\'Aigle",
    ["T"] = "Chasse au collier troll",
  },
  [2881] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Chasse aux colliers trolls",
  },
  [2882] = {
    ["D"] = "Cette carte montre une plage orientée au sud. Sous la carte, des mots élégamment tracés…$B$BAu sud de Gentepression, avec raison$B$BAu sud de la maison$BTrouvez un drapeau, un mât et des os$BCreusez ici, si vous l\'osez$BCreusez et gagnez l\'or de Cuergo !$BUne clé il faut et une clé vous verrez$BLes hommes d\'en face il faudra tuer.",
    ["O"] = "Trouvez l\'or de Cuergo !",
    ["T"] = "L\'or de Cuergo",
  },
  [2902] = {
    ["D"] = "Les gnolls ne sont pas des lumières, mais ils ont certainement des documents concernant leurs récents agissements. Si nous trouvions des plans, alors nous aurions une meilleure idée de ce qui les motive.$B$BJe veux que vous retourniez chez les Griffebois et que vous rapportiez tout ce qui ressemble à un plan de bataille. Ils doivent avoir quelque chose, leurs attaques sont trop coordonnées. Trouvez les plans de bataille et rapportez-les-moi.",
    ["O"] = "Trouver les Plans de bataille des gnolls dans les camps gnolls du sud du Camp Mojache.",
    ["T"] = "Enquête chez les Griffebois",
  },
  [2903] = {
    ["D"] = "Une fois trouvés les plans des gnolls, il reste à les porter à Hadoken Swiftstrider au Camp Mojache. Peut-être comprendra-t-il les projets des gnolls…",
    ["O"] = "Rapporter les Plans de bataille des Griffebois à Hadoken Swiftstrider au Camp Mojache.",
    ["T"] = "Les plans de bataille",
  },
  [2904] = {
    ["D"] = "<Kernobee grogne>Qu… Que s\'est-il passé ?$B$BLa dernière chose dont je me souviens est d\'avoir été capturé par un groupe de nains Sombrefer, d\'avoir été battu comme plâtre et jeté ici avec ces troggs crasseux.$B$BSortez-moi de là, $N !$B$BVoici le plan : vous me conduisez à la sortie, si nous survivons, vous allez à Baie-du-Butin prévenir Scooty que je suis allé à Ratchet pour informer Tinkerwiz de l\'implication des Sombrefer dans la conception de cette super plate-forme.$B$BAllons-y !",
    ["O"] = "Escorter Kernobee jusqu’à la sortie de la Fuite du temps, puis faire un rapport à Scooty, à Baie-du-Butin.",
    ["T"] = "Une jolie pagaille",
  },
  [2922] = {
    ["D"] = "Lorsque notre cité bien-aimée de Gnomeregan est tombée, une étincelle a fait disjoncter ma pauvre création, le Techbot. Il nous aidait bien, moi et mes associés, avec ses nombreux gadgets et ses banques d\'informations illimitées, mais son cerveau positronique a été polarisé. Techbot est maintenant négatronique et son comportement s\'est inversé. Il erre, enragé, dans Gnomeregan !$B$BMais peut-être peut-il être sauvé. Si vous retrouviez sa mémoire principale et me la rapportiez, je pourrais découvrir la cause de la polarisation et réparer mon pauvre Techbot.$B$B",
    ["O"] = "Rapporter la Mémoire principale de Techbot au maître-artisan Overspark à Ironforge.",
    ["T"] = "Sauver le cerveau de Techbot !",
  },
  [2923] = {
    ["D"] = "Le maître-artisan Overspark est très contrarié par la perte de sa création mécanique, le Techbot. Je me souviens du Techbot comme d’une machine gentille et serviable, très utile aux gnomes, mais Overspark dit qu\'il a eu des problèmes de fonctionnement.$B$BVraiment dommage, mais avec de la chance, cela pourrait être réparé !$B$BParlez avec maître Overspark, à Brikabrok, le quartier gnome d\'Ironforge. Votre aide sera la bienvenue.",
    ["O"] = "Parler au maître-artisan Overspark à Ironforge.",
    ["T"] = "Maître-artisan Overspark",
  },
  [2924] = {
    ["D"] = "Un composant pratiquement universel, le cerveau mécanique, est d\'une grande utilité pour les artisans et les forgerons de Gnomeregan. Mais durant notre exode, personne n\'a pensé à en emporter ! Et j\'en ai besoin d\'un grand nombre pour le travail que je vais bientôt accomplir.$B$BS\'il vous plaît, allez à Gnomeregan et rapportez-en. Vous les trouverez dans les profondeurs de notre cité, dans des conteneurs appelés des extrapolateurs artificiels.$B$BMerci, $N et dépêchez-vous !",
    ["O"] = "Rapporter 12 Cerveaux mécaniques à Klockmort Spannerspan à Ironforge.",
    ["T"] = "Les cerveaux mécaniques",
  },
  [2925] = {
    ["D"] = "Un ami gnome, Klockmort Spannerspan m\'a écrit depuis Ironforge. Il me dit qu\'il a besoin d\'aide pour une mission dangereuse, mais vitale à Gnomeregan, l\'ancienne et puissante cité gnome, aujourd’hui contaminée et envahie de gnomes fous et de leurs inventions.$B$BJe dois rester ici, mais je dois aider mon ami. Si vous pouviez y aller à ma place, je serais votre débiteur.$B$BVous pouvez trouver Klockmort à Brikabrok, le quartier gnome d\'Ironforge.",
    ["O"] = "Parler à Klockmort Spannerspan à Ironforge.",
    ["T"] = "Les cerveaux mécaniques de Klockmort",
  },
  [2926] = {
    ["D"] = "Depuis l\'accident, Gnoarn discute et palabre sur le fait de nettoyer Gnomeregan de l\'infestation des troggs et de trouver un remède à la maladie, $N.$B$BPar chance, Razzle et moi sommes près de trouver quelque chose, un remède ou une recette de rhum particulièrement corsée, nous ne sommes pas sûrs… Nous le saurons après les tests ! Nous avons bien évidemment besoin de quelques ingrédients pour tester notre produit, notamment des retombées radioactives collectées sur des troggs irradiés de Gnomeregan. Remplissez cette fiole avec la Lueur verte et rapportez-la.",
    ["O"] = "Utiliser la Fiole de collecte plombée vide sur les Envahisseurs ou les Pilleurs irradiés pour recueillir des retombées radioactives. Une fois pleine, rapportez-la à Ozzie Togglevolt à Kharanos.",
    ["T"] = "Gnogaine",
  },
  [2927] = {
    ["D"] = "Vous ne pensiez quand même pas que j’aie toujours été chauve ? Non $gm’sieur:ma\'am; ! C\'est la fuite radioactive à Gnomeregan qui a causé cette tragique perte de cheveux. Avant cela, j\'avais la seule coiffure afrognome de tout Azeroth.$B$B<Gnoarn brandit le poing vers le ciel>$B$BMaudits soient ces troggs crasseux, qu\'ils soient tous maudits !$B$BPeut-être qu\'il n\'est pas trop tard, $N. Nous pouvons peut-être encore la sauver, je parle de la ville ! Allez voir Ozzie Togglevolt à Kharanos. Lui et Razzle, son partenaire, travaillent à résoudre ce problème.",
    ["O"] = "Parler à Ozzie Togglevolt à Kharanos.",
    ["T"] = "Le jour d\'après",
  },
  [2928] = {
    ["D"] = "C\'est affreux, $N. Nous n\'avons pas suffisamment de pièces détachées pour notre flotte d\'excavateurs gyrodrilmatiques à Dun Morogh.$B$BNous avons besoin de ces pièces, $N. Le meilleur endroit pour en trouver est Gnomeregan, mais j\'ai entendu dire que les bots et les mechs grouillaient là-bas, comme les puces sur le dos d\'un chien.$B$BVous devez aller à Gnomeregan et mettre en pièces quelques robots. Ramenez-moi des entrailles mécaniques. Deux douzaines devraient suffire.",
    ["O"] = "Rapporter 24 Entrailles mécaniques de robot à Shoni à Stormwind.",
    ["T"] = "Excavateurs gyrodrilmatiques",
  },
  [2929] = {
    ["D"] = "J\'avais confiance en Thermaplugg, $N. Je ne pensais pas qu\'il me trahirait, moi et tout le peuple gnome. Et pourquoi ? La puissance ? L\'argent ? Toute chose qu\'il aurait eu de toute façon. Maintenant nous avons été jetés hors de notre pays et ce malade est à sa tête. Le roi du vide...$B$BNous reprendrons Gnomeregan, $N. Nous n\'arrêterons pas tant que la ville ne sera pas nôtre à nouveau. Si vous désirez vous joindre à nous, j\'ai une mission simple à vous confier : tuer le traître. Détruisez le mekgénieur Thermaplugg.",
    ["O"] = "Aller à Gnomeregan et tuer le mekgénieur Thermaplugg. Puis retourner voir le Grand Bricoleur Mekkatorque.",
    ["T"] = "La grande trahison",
  },
  [2930] = {
    ["D"] = "Quand nous avons fui Gnomeregan, nous avons dû abandonner tant de données importantes !$B$BJ\'ai besoin des données de la carte perforée prismatique d\'une Matrice d\'encodage 3005-D, dans Gnomeregan. C\'est votre destination ; mais pour cela, vous devez d\'abord accéder aux terminaux de modèles -A, -B et -C. Et ceci n\'est possible qu\'avec les cartes prismatiques, en commençant par les blanches... Et nous avons laissé toutes nos cartes dans la ville.$B$BPouvez-vous nous aider ?",
    ["O"] = "Apporter une Carte perforée prismatique au Maître mécanicien Castpipe, à Ironforge.",
    ["T"] = "Sauvetage de données",
  },
  [2931] = {
    ["D"] = "Le maître-mécanicien Castpipe à Ironforge a besoin d\'aventuriers ! Il organise des groupes pour pénétrer à nouveau dans Gnomeregan afin d\'effectuer une mission de récupération de première importance.$B$BIl se peut que Castpipe ne soit pas membre des grandes Assemblées d\'arcanologie, d\'alchimie ou de sciences d\'ingénieries comme je le suis, mais il reste un gnome intelligent. S\'il considère que c\'est important, alors mes calculs indiquent qu\'il y a quatre-vingt pour cent de chance que ce soit véritablement important !$B$BIl s\'agit là des probabilités les plus optimistes en ce qui nous concerne !",
    ["O"] = "Parler au Maître mécanicien Castpipe, à Ironforge.",
    ["T"] = "Du travail pour Castpipe",
  },
  [2932] = {
    ["D"] = "Les trolls Witherbark étaient jadis des alliés de la Horde, mais ils ont trahi Thrall... ce qui ne peut être toléré !$B$BVoici, prenez ma pique et allez dans les Hinterlands, loin au nord. Vous trouverez l\'endroit en question au nord-est du Moulin-de-Tarren, un village tenu par nos alliés, les Réprouvés.$B$BQuand vous entrerez sur les terres des Hinterlands, chassez les trolls Witherbark. Collectez leurs crânes, puis plantez ma pique dans le sol de l\'un de leurs petits villages, Hiri\'watha ou Zun\'watha.$B$BMontrez-leur que tout se paie en ce bas monde !",
    ["O"] = "Collecter les Crânes de Witherbark, et les mettre en place sur la Pique de Nimboyae. Placer la Pique de Nimboya préparée dans l’un des Villages Witherbark, dans les Hinterlands, puis retourner voir Nimboya, à Strangleronce.",
    ["T"] = "Sinistre message",
  },
  [2933] = {
    ["D"] = "Ces bouteilles contiennent un terrible venin.$B$BVu le nombre de bouteilles dans ce village, les Witherbark doivent projeter quelque chose d\'important avec elles ; peut-être qu\'un apothicaire, près du Moulin-de-Tarren, serait interéssé par l\'étude d\'un échantillon.",
    ["O"] = "Apporter une Bouteille de venin à un apothicaire, au Moulin-de-Tarren.",
    ["T"] = "Bouteilles de venin",
  },
  [2934] = {
    ["D"] = "Il me faut un échantillon frais du venin que vous m\'avez apporté. Les araignées dont il provient hantent les Hinterlands. Chassez-les jusqu\'à ce que vous trouviez une glande à venin intacte, puis rapportez-la-moi. Vous trouverez beaucoup d\'araignées près de la ville des trolls de Shadra\'Alor.$B$BNe tardez pas, $N, la glande à venin doit être fraîche lorsque vous l\'apporterez.",
    ["O"] = "Rapporter une Glande à venin intacte à l\'Apothicaire Lydon, au Moulin-de-Tarren.",
    ["T"] = "Glande à venin intacte",
  },
  [2935] = {
    ["D"] = "Le venin des gardes du clan n\'était pas le même que celui que vous m\'avez rapporté. Ils proviennent sans doute d\'araignées différentes.$B$BJe veux un échantillon de venin frais. Pour savoir où en trouver, vous devez consulter un troll qui connaît bien les Witherbark : maître Gadrin, au village de Sen\'jin. Allez en Durotar et parlez à Gadrin. Avec un peu de chance, il saura de quelle araignée les Witherbark tirent leur venin.",
    ["O"] = "Parler à Maître Gadrin au village de Sen\'jin.",
    ["T"] = "Consulter Maître Gadrin",
  },
  [2936] = {
    ["D"] = "Les Witherbark adorent un dieu-araignée dont ils obtiennent ce venin extrêmement toxique. Ainsi, si vous voulez en collecter un peu, il vous faudra invoquer leur dieu !$B$BPour réussir l\'invocation, vous devez connaître le vrai nom de cette divinité. Il est écrit sur une tablette, enterrée à Tanaris, dans la cité troll de Zul\'Farrak. Dans une tombe gardée par Theka le Martyr, pour être plus exact.$B$BTuez Theka, trouvez et lisez la tablette pour connaître le véritable nom du dieu-araignée, puis revenez me voir.",
    ["O"] = "Lire la Tablette de Theka pour connaître le véritable nom du dieu-araignée des Witherbark, puis retourner voir Maître Gadrin.",
    ["T"] = "Le dieu-araignée",
  },
  [2937] = {
    ["D"] = "Prenez ce parchemin et lisez-le devant l\'autel de Shadra\'Alor, loin à l\'intérieur des terres des Witherbark, dans les Hinterlands. Shadra, le dieu-araignée sera invoqué et paraîtra.",
    ["O"] = "Terrasser Shadra, à l\'autel de Shadra\'Alor, puis rapporter son venin à l\'Apothicaire Lydon, au Moulin-de-Tarren.",
    ["T"] = "L\'invocation de Shadra",
  },
  [2938] = {
    ["D"] = "Le venin que vous avez récupéré sur le dieu araignée des Witherbark est une grande découverte ! J\'ai pu tuer près de deux douzaines de lapins avec une seule goutte. Il est juste que certains de ces lapins étaient très jeunes et suffisamment petits pour tenir dans la paume de la main. Leur résistance aux toxines devait être très basse. Néanmoins, il s\'agit là d\'un puissant poison !$B$BJ\'aimerais en envoyer un échantillon à mon supérieur, l\'apothicaire Faranell à l\'Apothicarium d\'Undercity. Il ne fait aucun doute qu\'il aimerait conduire ses propres expériences.",
    ["O"] = "Apporter le Colis de Faranell au maître apothicaire Faranell, à Undercity.",
    ["T"] = "Du venin pour Undercity",
  },
  [2939] = {
    ["D"] = "Bien que les Ruines de Feralas puissent être très dangereuses, elles ont beaucoup à raconter sur ce qui s\'est passé ici.$B$BIl y a quelques jours, tandis que je fouillais les décombres, j\'ai découvert ce qui semble être une douve classique. Je ne peux cependant pas me défaire de l\'idée que cela cache quelque chose... Angelas et moi avons fait des recherches dans nos livres, mais nous n’avons rien découvert la concernant.$B$BJe connais une collègue à Darnassus qui pourrait peut-être nous dire de quoi il s\'agit, $N. Pourquoi n\'iriez-vous pas lui parler et voir si elle peut nous aider ?",
    ["O"] = "Parler à Daryn Lightwind, au village de Rut\'theran.",
    ["T"] = "En savoir plus",
  },
  [2940] = {
    ["D"] = "Ce livre à l\'air de ne pas avoir été ouvert depuis des années. Sa couverture est passée et ses pages jaunies ; mais en l\'examinant de plus près, vous remarquez que c\'est exactement ce que Troyas devait rechercher.$B$BVous le prenez, puis réalisez que vous devriez d\'abord demander la permission avant de l\'emprunter.",
    ["O"] = "Demander à Daryn Lightwind si vous pouvez emprunter son livre.",
    ["T"] = "Feralas : une histoire",
  },
  [2941] = {
    ["D"] = "J\'ai étudié beaucoup de thèmes et de sujets, et ma dernière étude porte sur la créature appelée Gueule d\'acier qui vit sur les plages, dans les Hinterlands. Il y a en particulier un spécimen que j\'aimerais voir, nommé Gammerita. Mes recherches me retiennent ici, hélas. Je pense qu\'une image de la bête suffirait à son étude.$B$BPortez cette lettre à Curgle Cranklehop, à Tanaris. Elle a inventé une machine qui prend des images. Elle appelle cela : \"photographie\", je crois...",
    ["O"] = "Apporter la lettre à Curgle Cranklehop, à Tanaris.",
    ["T"] = "Le prêt",
  },
  [2942] = {
    ["D"] = "Lorsque vous retirez l\'artefact du Monolithe, vous sentez que l\'énergie du Bâton d\'Equinex commence à décliner.",
    ["O"] = "Rapporter la Pierre étincelante et le Bâton d\'Equinex à Troyas Moonbreeze, dans le bastion de Feathermoon.",
    ["T"] = "La Pierre du matin",
  },
  [2943] = {
    ["D"] = "Voilà, $N. Je vous en prie, prenez soin de mon livre.$B$BPartez, à présent. Je suis sûre que Troyas attend votre retour avec impatience.",
    ["O"] = "Apporter le livre à Troyas Moonbreeze, dans le bastion de Feathermoon.",
    ["T"] = "Troyas",
  },
  [2944] = {
    ["D"] = "Regardez ma dernière invention, $N. Tout ce que vous avez à faire est de viser puis d\'appuyer sur ce bouton.$B$BQuelle était la créature des Hinterlands que vous avez mentionnée ? Gammerita ? Eh bien, bonne chance. Je suis sûre que Daryn sera contente du cliché que vous lui en ferez.$B$BVoici le Preneur d\'images FX, faites-en bon usage et amusez-vous bien !",
    ["O"] = "Utiliser le Preneur d\'images FX pour prendre un cliché de Gammerita, puis retourner voir Daryn Lightwind à Rut\'theran.",
    ["T"] = "Le Preneur d\'images FX",
  },
  [2945] = {
    ["D"] = "Vous avez découvert, sur le corps d\'un des nains Sombrefer, ce qui semble être un anneau. La crasse le recouvre entièrement, rendant difficile son identification et impossible son port. Il vous semble pouvoir discerner, sous les résidus, comme le chatoiement d\'un métal précieux et l\'éclat d\'une gemme.$B$BSi vous voulez utiliser cet anneau, il vous faudra trouver un moyen de nettoyer la saleté qui le souille.",
    ["O"] = "Trouver un moyen de nettoyer l\'Anneau sali.",
    ["T"] = "Anneau sali",
  },
  [2946] = {
    ["D"] = "Nous voulons que vous portiez les disques que vous avez trouvés, aux portes scellées d\'Uldum. Nous... ne sommes pas sûrs de ce qui va se passer quand vous ferez cela.$B$BCela pourrait être risqué, aussi bien que parfaitement inefficace. Vous devrez utiliser les disques, d\'une manière ou d\'une autre... Peut-être les portes s\'ouvriront-elles toutes seules à l\'approche des disques... allez savoir. Essayez ce qui vous semblera bon et attendez-vous à tout.$B$BRevenez ensuite à Ironforge. Du travail nous attend !",
    ["O"] = "Porter les Disques de platine miniatures aux Portes d\'Uldum, à Tanaris et chercher les indices de la connexion qui existe entre eux.",
    ["T"] = "Questions sans réponses",
  },
  [2947] = {
    ["D"] = "En ouvrant la boîte, vous trouvez ce à quoi vous vous attendiez : un anneau scintillant, comme flambant neuf ! Dès que vous l\'enfilez, vous sentez une vague de puissance magique traverser votre corps.$B$BDans la face intérieure de l\'anneau, vous voyez le poinçon d\'Ironforge, accompagné de trois petites initiales gravées : \"TdK\". Ce peuvent être celles du précédent propriétaire de l\'anneau ou celles de son artisan. Garder l\'anneau... ou le rendre à qui il appartient. Difficiles décisions, en vérité...",
    ["O"] = "Garder l\'anneau ou retrouver la personne qui y a gravé les lettres.",
    ["T"] = "Le retour de l\'anneau",
  },
  [2948] = {
    ["D"] = "Écoutez, $n, cet anneau vous appartient mais je peux l\'améliorer. Sa forme est très simple et j\'ai beaucoup progressé depuis. Pour la modique somme de trente pièces d\'argent, je lui donnerai de nouveaux atours... bon, un peu plus de trente pièces, car je suis un peu gêné financièrement, en ce moment.$B$BTous mes stocks de matériaux sont épuisés ; si vous m\'apportez une barre d\'argent et une agate mousse, je pourrai ajouter un treillis de perles et d\'argent qui donnera à cet anneau un sacré coup de fouet !$B$BFaites-moi confiance !",
    ["O"] = "Apporter l’Anneau d\'or brillant, une Barre d\'argent, une Agate mousse et 30 pièces d’argent à Talvash del Kissel, à Ironforge.",
    ["T"] = "Amélioration gnomique",
  },
  [2949] = {
    ["D"] = "En ouvrant la boîte produite par le Décapeur 5200, vous découvrez un anneau scintillant. Quand vous le passez à votre doigt, vous sentez une incroyable vague de puissance magique traverser votre corps.$B$BDans la face intérieure de l\'anneau, vous pouvez voir le petit poinçon d\'Orgrimmar à côté duquel les trois premières lettres gravées d\'un mot sont visibles : \"NOG...\"$B$BCe sont peut-être les initiales de l\'ancien propriétaire de l\'anneau ou de son fabricant. Garder l\'anneau... ou retrouver \"NOG\" - difficile décision, en vérité...",
    ["O"] = "Garder l\'anneau ou retrouver la personne qui y a gravé les lettres.",
    ["T"] = "Le retour de l\'anneau",
  },
  [2950] = {
    ["D"] = "Non, non, non, je ne suis pas en train de dire que cet anneau est à moi. Cependant, votre information sur les Sombrefer fut très utile. J\'améliorerai cet anneau pour vous, bien mieux que ne le pourrait celui qui a forgé cette pauvre petite chose... sans doute un gnome, d\'ailleurs...$B$BSi vous m\'apportez une barre d\'argent, une agate mousse et trente pièces d\'argent, je pourrai ajouter un treillis de perles et d\'argent qui donnera une ampleur toute nouvelle à la magie contenue dans cet anneau. Cela vous intéresse ?",
    ["O"] = "Apporter l’Anneau d\'or brillant, une Barre d\'argent, une Agate mousse et 30 pièces d’argent à Nogg, à Orgrimmar.",
    ["T"] = "Nogg améliore l\'anneau",
  },
  [2951] = {
    ["D"] = "L\'étrange objet est là, au milieu des gnomes. Une petite plaque sur la machine indique : \"Décapeur 5200\", suivit d\'un texte :$B$B\"La crasse et les résidus ruinent vos objets ? Le Décapeur 5200 est l\'outil qu\'il vous faut pour restaurer toute la brillance et l\'éclat qu\'ils méritent. Veuillez placer l\'objet à décaper dans la machine, ainsi que trois pièces d\'argent dans la fente prévue à cet effet et attendez un petit moment : vos objets de valeur en sortiront comme neufs\"",
    ["O"] = "Insérer un Objet sali dans le Décapeur 5200, sans oublier de jeter trois pièces d\'argent dans la fente, pour démarrer la machine.",
    ["T"] = "Le Décapeur 5200 !",
  },
  [2952] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Le Décapeur 5200 !",
  },
  [2953] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Le Décapeur décape encore",
  },
  [2954] = {
    ["D"] = "Bonjour, je garde l’entrée. Si vous n’avez pas les Plaques d\'Uldum intégrées à votre jeu de disques je ne vous laisserai pas entrer dans le complexe d’Uldum.$B$BL’apparition du Gardien des pierres de Norgannon est la réaction qui était espérée en vous envoyant dans le Désert de Tanaris avec les disques de platine. Vous devez maintenant découvrir l’histoire du Gardien des pierres avant de continuer.",
    ["O"] = "Apprendre le but du Gardien des pierres de Norgannon, puis interagir avec le Socle d\'Uldum, dans le désert de Tanaris.",
    ["T"] = "Le Gardien des pierres",
  },
  [2962] = {
    ["D"] = "Votre échantillon n\'était pas seulement insuffisant, mais impur.$B$BNous avons besoin de plus de Lueur verte. Les troggs ne sont apparemment pas encore assez irradiés. Vous devrez aller à Gnomeregan et rapporter de la matière radioactive, prélevée à la source. Je parle bien sûr de toutes ces gelées, rôdeurs et autres horreurs irradiées qui errent là-bas. Attention, la collecte et le conditionnement de telles matières est difficile ; il vous faudra faire plusieurs tentatives avant qu\'un échantillon valable soit prélevé.",
    ["O"] = "Voyager à Gnomeregan et rapporter de la Matière radioactive à haut potentiel. Attention, la matière radioactive est instable et se désactive rapidement.$B$BAprès le travail, conserver la Flasque plombée lourde, pour Ozzie.",
    ["T"] = "Encore plus de Lueur verte !",
  },
  [2963] = {
    ["D"] = "Ah, $n ! La nouvelle de votre découverte dans Uldaman a mis en joie la Ligue des explorateurs. La Ligue a beaucoup travaillé pour découvrir le secret des petits disques de platine que vous avez trouvés. Je puis vous dire que nous sommes au seuil de quelque chose d\'important !$B$BL\'Historien Karnik a quelques hypothèses très intéressantes sur l\'origine de la vallée des Guetteurs dans le désert de Tanaris. Il voudrait vous parler dès que possible.",
    ["O"] = "Parler à l\'Historien Karnik dans le Hall des explorateurs, à Ironforge.",
    ["T"] = "Prodiges d\'Uldum",
  },
  [2964] = {
    ["D"] = "Bien, $n... Il semblerait que nous soyons dans l\'impasse. Nous avons besoin des Plaques d\'Uldum, mais nous ne savons pas où elles se trouvent. Nous devons réfléchir avant d\'avoir un plan pertinent. J\'ai bien quelques petites idées et j\'espère que nous vous enverrons bientôt pour les récupérer.$B$BLe Grand Explorateur doit être informé de tout cela, $n ; il est à parier qu\'il veut aussi vous remercier pour l\'aide que vous avez apportée à la Ligue.",
    ["O"] = "Parler au Grand Explorateur Magellas, à Ironforge.",
    ["T"] = "Travail imminent",
  },
  [2965] = {
    ["D"] = "Ah, $n, vous voilà de nouveau. Vos révélations quant au site de fouilles d\'Uldaman ont motivé bien des discussions parmi nous. J\'en parlais justement avec mon ami, le druide Nara Wildmane et il m\'apparut que son expérience passée pouvait orienter la suite de notre action.$B$BAllez la voir et écoutez l\'histoire de ses voyages dans la vallée des Guetteurs du désert de Tanaris. Elle vous attend à la Cime des Anciens.",
    ["O"] = "Aller à la Cime des Anciens, dans Thunder Bluff et parler à Nara Wildmane.",
    ["T"] = "Prodiges d\'Uldum",
  },
  [2966] = {
    ["D"] = "Le Sage Truthseeker et moi-même voulons que vous portiez les disques que vous avez découverts jusqu\'aux portes scellées d\'Uldum. Nous... ne sommes pas sûrs de ce qui va arriver quand vous serez là-bas.$B$BCela pourrait être risqué, aussi bien que parfaitement inefficace. Vous devrez utiliser les disques, d\'une manière ou d\'une autre... peut-être les portes s\'ouvriront-elles toutes seules à l\'approche des disques... allez savoir. Essayez ce qui vous semblera bon et attendez-vous à tout.$B$BApprenez ce que vous pourrez, puis revenez me voir ; nous aurons beaucoup à nous dire.",
    ["O"] = "Porter les Disques de platine miniatures aux Portes d\'Uldum, à Tanaris et chercher les indices de la connexion qui existe entre eux.",
    ["T"] = "Questions sans réponses",
  },
  [2967] = {
    ["D"] = "Vous accédez au socle, à nouveau ; les disques de platine réapparaissent une nouvelle fois dans votre sac à dos.$B$BAvec ce que vous avez appris du Gardien des pierres et les disques en votre possession, vous pouvez à présent retourner à Thunder Bluff et informer Nara Wildmane de votre découverte.",
    ["O"] = "Parler à Nara Wildmane, dans Thunder Bluff.",
    ["T"] = "Le retour à Thunder Bluff",
  },
  [2968] = {
    ["D"] = "Bien, $n... Il semble que nous soyons dans l\'impasse. Nous avons besoin des Plaques d\'Uldum, mais nous ne savons pas où elles se trouvent. Nous devons faire des recherches à ce sujet. J\'ai bien quelques petites idées et j\'espère que vous irez bientôt les récupérer. Nous vous demanderons bientôt de vous joindre à nous pour découvrir la vérité.$B$BLe Sage Truthseeker doit être informé de cela, $n ; il voudra aussi vous remercier d\'avoir, une fois de plus, répondu à l\'appel.",
    ["O"] = "Parler au Sage Truthseeker, à Thunder Bluff.",
    ["T"] = "Travail imminent",
  },
  [2969] = {
    ["D"] = "L\'odieux clan du Totem sinistre capture des créatures féeriques, $N. Jer\'kai et moi ne savons pas quels desseins ils poursuivent en faisant cela, mais ce n\'est certainement rien de bon.$B$BNous ne sommes pas assez puissants pour combattre le Totem sinistre dans son camp et libérer les créatures, mais peut-être qu\'avec votre aide...$B$BSi vous êtes d\'accord et avez quelques amis robustes qui voudraient se joindre à vous, allez libérer les créatures de leurs cages grâce à cette clé et assurez-vous qu\'elles restent en vie. Je serai à vos côtés tout le temps ; Jer\'kai, pour sa part, restera ici, au cas où...",
    ["O"] = "A l’aide de la Clé de la cage en bambou, libérer les Fées fléchetteuses de l’enclos dans le Camp du Totem sinistre. Au moins 6 Fées fléchetteuses et Kindal Moonweaver doivent survivre.$B$BVous avez 6 minutes avant que Kindal décide qu’il est temps de battre en retraite et que vous échouiez dans votre quête.",
    ["T"] = "Liberté pour toutes les créatures",
  },
  [2970] = {
    ["D"] = "Comme vous l\'a déjà dit Kindal, nous allons désormais combattre le clan du Totem sinistre. Ensemble, nous devrions pouvoir affronter quelques taurens, mais nous apprécierions votre aide, si vous êtes d\'accord.$B$BVous avez vu l\'un des camps, en aval ; il y en a encore d\'autres à l\'est. Faites selon vos compétences et techniques mais infligez autant de dégâts possibles aux taurens. Quand ils auront goûté à la piqûre des flèches de Kindal et à vos propres armes, nous nous retrouverons tous ici, pour faire le point.",
    ["O"] = "Tuer 12 Naturalistes du Totem sinistre, 10 Ecumeurs du Totem sinistre et 6 Chamans du Totem sinistre avant de retourner voir Jer\'kai Moonweaver, en Feralas.",
    ["T"] = "Justice de châtiment",
  },
  [2971] = {
    ["D"] = "[PH] Description",
    ["O"] = "[PH] Log Description",
    ["T"] = "<UNUSED>",
  },
  [2972] = {
    ["D"] = "Ici, dans les terres sauvages, Kindal et moi n’avons pas grand-chose à vous offrir, mais nous voulons récompenser votre aide. Prenez ma chevalière. Si un jour vous êtes dans les environs de Teldrassil, allez au Temple de la Lune au cœur de Darnassus. Là, vous trouverez ma Haute prêtresse Tyrande Whisperwind. L\'anneau lui dira que nous allons bien et que votre histoire est vraie.$B$BNous vous remercions tous les deux, $N.",
    ["O"] = "Apporter la Chevalière de Jer\'kai\'s à la Grande prêtresse Tyrande Whisperwind, à Darnassus.",
    ["T"] = "Justice de châtiment",
  },
  [2973] = {
    ["D"] = "Ah, vous voici, $c. Lors d\'une mission pour nos maîtres d\'Orgrimmar, j\'ai voyagé loin en direction de Feralas.$B$BMon amie Korrin adore les vêtements, j\'ai donc l\'intention de lui confectionner une nouvelle cape ornée des ailes légères des créatures féériques. M\'aiderez-vous ? Les Fées fléchetteuses aux ailes sublimes se trouvent à l\'ouest de Camp Mojache.",
    ["O"] = "Rapporter 10 Ailes iridescentes de Fées fléchetteuses à Krueg Skullsplitter à Camp Mojache.",
    ["T"] = "Une nouvelle cape",
  },
  [2974] = {
    ["D"] = "Quoi ! Vous avez rencontré des taurens dans le secteur ? Vous plaisantez ! A quoi ressemblaient-ils !?$B$BLe clan Totem sinistre ! Vous en avez vus ? Belgrom, l\'un de nos maîtres, s\'intéresse à eux… Il offre même une prime à leur égard.$B$BVous devez repartir, $c. Retournez-y et apportez-moi leurs cornes. Des montagnes si vous pouvez. Ils ne doivent pas être traités à la légère ou sous-estimés. Retournez-y et massacrez-les. Que les rivières de Feralas rougissent de leur sombre sang.",
    ["O"] = "Apporter 20 Cornes de Totem sinistre à Krueg Skullsplitter à Camp Mojache.",
    ["T"] = "Une découverte sinistre",
  },
  [2975] = {
    ["D"] = "La tribu des ogres Gordunni ne peut pas passer inaperçue, dans cette région ; regardez autour de vous : le pays est comme blessé par leur simple présence, $N. Nous devons les repousser avant qu\'ils ne se répandent plus avant dans Feralas.$B$BSuivez la rivière au nord, vous tomberez sur leur avant-poste. Portez la mort et la désolation parmi eux, $N. Nous devons leur montrer à quel point nous les trouvons indésirables.",
    ["O"] = "Tuer 10 Ogres gordunni, 10 Ogre-magi gordunni et 5 Brutes gordunni pour Rok Orhan, à Camp Mojache.",
    ["T"] = "Les ogres de Féralas",
  },
  [2976] = {
    ["D"] = "Comment ?$B$BAh, j\'ai parlé d\'une récompense, n\'est-ce pas ? Bon... ça m\'apprendra à ouvrir la bouche !$B$BVous avez fait le travail et je suppose que je vous dois ce que je vous avais promis. Prenez les cornes, je les ai mises dans une caisse pour vous et portez-les à Belgrom Rockmaul, dans la Vallée de l\'honneur. Il souhaite entendre votre rapport et vous récompenser.$B$BQue les vents vous portent, $c, et merci encore pour tout.",
    ["O"] = "Apporter la Caisse de Cornes de Totem sinistre à Belgrom Rockmaul, Orgrimmar.",
    ["T"] = "Une découverte sinistre",
  },
  [2977] = {
    ["D"] = "Comme vous accédez de nouveau au socle, les disques de platine apparaissent une fois de plus dans votre sac à dos.$B$BAvec ce que vous avez appris du Gardien des pierres et les disques de nouveau en votre possession, vous pouvez maintenant retourner à Ironforge et informer la Ligue des explorateurs de votre découverte.",
    ["O"] = "Parler à l\'Historien Karnik, à Ironforge.",
    ["T"] = "Retour à Ironforge",
  },
  [2978] = {
    ["D"] = "En ramassant les parchemins salis, vous remarquez qu\'ils représentent quelque cérémonie ou rassemblement. Rok Orhan aimerait sûrement jeter un oeil sur ce parchemin et savoir quel est son rapport avec les Gordunni.",
    ["O"] = "Apporter le Parchemin Gordunni à Rok Orhan, à Camp Mojache.",
    ["T"] = "Le parchemin gordunni",
  },
  [2979] = {
    ["D"] = "On dirait bien des plans pour quelque sombre cérémonie, $N. D\'après ce que je vois, cette assemblée est tenue par les seigneurs-mages Gordunni.$B$BTrouvez l\'un de ces seigneurs-mages et prenez-lui un orbe.$B$BL\'orbe d\'un mage contient beaucoup de pouvoirs et nous aidera à en savoir plus sur cette cérémonie secrète des Gordunni.",
    ["O"] = "Rapporter un Orbe Gordunni à Rok Orhan, à Camp Mojache.",
    ["T"] = "Sombre cérémonie",
  },
  [2980] = {
    ["D"] = "Vous avez prouvé que vous saviez vous battre et je sais que vous avez le courage de continuer la mission.$B$BAllez dans les Plateaux sauvages, au sud-ouest. Vous y verrez des structures détruites et abandonnées sur les terres qui, jadis, appartenaient aux elfes de la nuit. Dirigez-vous sur ces ruines et quand vous traverserez des terres profanées aux arbres flétris, vous trouverez les Gordunni. Repoussez-les.$B$BCes ogres sont beaucoup plus forts et dangereux que ceux que vous avez précédemment combattus, $N. Soyez $gprudent:prudente;.",
    ["O"] = "Tuer 10 Chamans Gordunni, 10 Démonistes Gordunni et 5 Marteleurs Gordunni pour Rok Orhan, à Camp Mojache.",
    ["T"] = "Les ogres de Féralas",
  },
  [2981] = {
    ["D"] = "Feralas est un lieu parfait pour faire ses preuves, $c. Une tribu d\'ogres appelées les Gordunni domine militairement l\'endroit et leur nombre, sans cesse, s\'accroît. Ils doivent être arrêtés avant de devenir une véritable menace pour Feralas.$B$BJe connais une certaine Rok Orhan qui habite à Camp Mojache, dans Feralas ; allez la trouver, elle vous en dira plus sur la situation.",
    ["O"] = "Comme le veut Belgrom Rockmaul, aller parler à Rok Orhan, à Camp Mojache.",
    ["T"] = "Menace sur Féralas",
  },
  [2982] = {
    ["D"] = "Bien que ce soit risqué, Troyas et moi avons visité les ruines dans les forêts de Feralas.$B$BCes derniers temps, Troyas à pris l\'habitude de se rendre seul sur les Plateaux sauvages, et je m\'inquiète pour lui. Les ogres Gordunni contrôlent la zone et ils ne traitent pas avec douceur ceux qui s\'aventurent dans leur domaine.$B$BAccepteriez-vous d\'aller dans les ruines, au sud-est, et de tuer quelques ogres afin de rendre la zone un peu plus sûre pour Troyas. Je vous en remercie par avance.",
    ["O"] = "Tuer 8 Démonistes Gordunni, 8 Chamans Gordunni et 8 Brutes Gordunni pour Angelas Moonbreeze.",
    ["T"] = "Les Plateaux sauvages",
  },
  [2983] = {
    ["D"] = "Cela fait quelque temps que vous n\'avez pas été $gmis à l\'épreuve:mise à l\'épreuve;, $N. Vous avez fait preuve de patience dans vos études jusque-là, mais il est temps pour vous d\'apprendre de plus grands pouvoirs… il est temps d\'apprendre le réel pouvoir. L\'élément de feu vous appartient si vous êtes $gprêt:prête; à payer pour cela.$B$BRecherchez Kranal Fiss dans les Tarides. Il réside dans une petite demeure au nord de la Croisée, mais faites attention ; si vous atteignez les terres de ces maudits elfes, c\'est que vous êtes $gallé:allée; trop loin.",
    ["O"] = "Trouver Kranal Fiss dans les Tarides.",
    ["T"] = "L\'appel du feu",
  },
  [2984] = {
    ["D"] = "Cela fait longtemps que vous n\'avez pas été $gmis à l\'épreuve:mise à l\'épreuve;, $N. Vous avez montré de la patience jusque-là dans vos études, mais le temps est à présent arrivé d\'apprendre la destruction et le chaos. L\'élément Feu peut vous appartenir, si vous êtes $gprêt:prête; à en payer le prix.$B$BRecherchez Kranal Fiss dans les Tarides. Il vit dans une petite demeure au nord de la Croisée, mais faites attention, si vous atteignez les terres des elfes de la nuit, c\'est que vous êtes $gallé:allée; trop loin.",
    ["O"] = "Trouver Kranal Fiss dans les Tarides.",
    ["T"] = "L\'appel du feu",
  },
  [2985] = {
    ["D"] = "Vous êtes capable d\'affronter la prochaine épreuve, $N. Vous avez su monter de la patience et avez progressé dans vos études, mais il est maintenant temps d\'apprendre plus encore et de connaître la pureté de l\'eau.$B$BCherchez Islen Waterseer, dans les Tarides. Elle vit le long de la côte est, au sud du fleuve Furie-du-Sud et de Ratchet. Vous la trouverez dans sa cabane de pêcheur. Bonne chance, $N.",
    ["O"] = "Trouver Islen Waterseer dans les Tarides.",
    ["T"] = "L\'appel de l\'eau",
  },
  [2986] = {
    ["D"] = "Cela fait longtemps que vous n\'avez pas été $gmis à l\'épreuve:mise à l\'épreuve;, $n. Vous avez montré de la patience jusque-là dans vos études, mais le temps est à présent arrivé pour vous d\'apprendre la pureté de l\'Eau.$b$bCherchez Islen Waterseer dans les Tarides. Elle se trouve le long de la côte est, au sud du fleuve Furie-du-Sud et de Ratchet. Vous la trouverez dans une hutte de pêcheur. Bonne chance à vous, $n.",
    ["O"] = "Trouver Islen Waterseer dans les Tarides.",
    ["T"] = "L\'appel de l\'eau",
  },
  [2987] = {
    ["D"] = "Je viens juste d\'arriver dans Feralas et j\'ai appris que les ogres Gordunni qui y résident gardent d\'intéressants minéraux. Impossible de savoir ce qu\'ils projettent, mais ils protègent leurs stocks farouchement et les enterrent pour empêcher qu\'un fou comme moi ne mette la main dessus.$B$BSi vous êtes d\'accord, voici ma pelle : allez dans l\'avant-poste des Gordunni, plus au nord, et extrayez-moi un peu de cobalt Gordunni. Cherchez la lueur bleue, puis commencez à creuser !",
    ["O"] = "Collecter 12 échantillons de Cobalt Gordunni pour Orwin Gizzmick, à Camp Mojache.",
    ["T"] = "Cobalt Gordunni",
  },
  [2988] = {
    ["D"] = "Le fils de notre plus puissant gryphon est parti ! L\'héritier d\'Aile-vive, Bec-tranchant, a été perdu tandis qu\'il chassait avec ses parents il y a quelques jours, et nous pensons que ce sont les trolls qui l\'ont enlevé.$B$BVous devez nous aider. Passez en revue les cages dans les deux villages de trolls Witherbark pour savoir si Bec-tranchant s\'y trouve.",
    ["O"] = "Inspecter les cages dans les deux villages Witherbark, puis retourner auprès du Maître des gryphons Talonaxe.",
    ["T"] = "Les cages Witherbark",
  },
  [2989] = {
    ["D"] = "Bec-tranchant ne se trouvait pas avec les trolls Witherbark. Peut-être les trolls du clan Vilebranch l\'ont-ils enlevé ? Partez pour l\'autel de Zul sur le bord ouest du territoire Vilebranch et cherchez le jeune gryphon.",
    ["O"] = "Regarder sur le dessus de l\'autel de Zul, puis retourner auprès du Maître des gryphons Talonaxe.",
    ["T"] = "L\'autel de Zul",
  },
  [2990] = {
    ["D"] = "Apportez cette plume à Thadius Grimshade, le devin. Il a quitté le Nid-de-l\'Aigle il y a des années pour engranger un savoir plus sombre, mais dans son cœur il reste un Wildhammer !$B$BPeut-être que ses talents peuvent nous aider à retrouver Bec-tranchant.$B$BCette plume provient du nid où dormait le jeune gryphon. Donnez-la à Thadius qui, avec un peu de chance, pourra utiliser ses talents divinatoires pour localiser Bec-tranchant.$B$BBonne chance, $N. Vous trouverez Thadius dans les Terres foudroyées, dans la tour des mages du Rempart-du-Néant.",
    ["O"] = "Apporter la Plume de Bec-tranchant à Thadius Grimshade, dans les Terres foudroyées.",
    ["T"] = "Thadius Sinissombre",
  },
  [2991] = {
    ["D"] = "Si vous voulez que je localise votre gryphon, vous devrez faire quelque chose pour moi. Il y a un troll dans Zul\'Farrak, Nekrum Mâchetripes, qui possède un médaillon que j\'aimerais beaucoup étudier. Apportez-le-moi et j\'utiliserai mes pouvoirs pour retrouver Bec-tranchant.$B$BZul\'Farrak est de l\'autre côté de l\'océan, à l\'est, dans le désert de Tanaris.",
    ["O"] = "Apporter le Médaillon de Nekrum à Thadius Grimshade, dans les Terres foudroyées.",
    ["T"] = "Médaillon de Nekrum",
  },
  [2992] = {
    ["D"] = "Laissez-moi un peu de place pour me permettre de pratiquer le rituel de divination. Je vais connecter la plume à son propriétaire, ce qui me donnera accès à Bec-tranchant lui-même.$B$BSi tout se déroule comme prévu, évidemment... Sinon, hé bien, vous feriez mieux de ne pas regarder.",
    ["O"] = "Attendre que Grimshade ait fini de pratiquer son rituel de divination.",
    ["T"] = "La Divination",
  },
  [2993] = {
    ["D"] = "Bec-tranchant est enfermé dans une cage, placée haute sur une falaise au sommet de Jintha\'Alor dans les Hinterlands. La clé de cette cage est en possession de la grande prêtresse des trolls du clan Vilebranch, la vile prêtresse maléfique Maléficia.$B$BVoilà, j\'ai fait ma part. Je vous souhaite bonne chance dans votre quête, $N.",
    ["O"] = "Parler au Maître des gryphons Talonaxe.",
    ["T"] = "Retour dans les Hinterlands",
  },
  [2994] = {
    ["D"] = "Bec-tranchant doit être secouru !$B$BFrayez-vous un chemin, à coups d\'épée s\'il le faut, jusqu\'au sommet de Jinth\'Alor, au coeur du territoire des Vilebranch et arrachez la clé de la cage à la vile prêtresse maléfique Maléficia. Enfin, retrouvez et libérez Bec-tranchant !",
    ["O"] = "Combattre jusqu’au sommet de Jintha\'Alor, tuer la vile prêtresse Maléficia pour obtenir la clé de la cage de Bec-tranchant, et sauver Bec-tranchant.",
    ["T"] = "Sauver Bec-tranchant",
  },
  [2995] = {
    ["D"] = "Le gîte de Quel\'Danil est un centre de communication pour les elfes des Hinterlands. Leurs ambassadeurs essaient d\'entrer en contact avec les races partageant leur connaissance des trolls Witherbark, qui habitent dans le sud des Hinterlands. Tous les documents concernant les communications sont gardés dans le gîte.$B$BNous ne voulons pas que les gens du Haut-val tissent des alliances, $N.$B$BDétruisez les documents du Haut-val. Nous devons brûler leurs lignes de communication... au sens propre comme au sens figuré.",
    ["O"] = "Voyager dans les Hinterlands et brûler les Registres du Haut-val, le Rapport du Haut-val et les Notes du Haut-val, pour Oran Snakewrithe, à Undercity",
    ["T"] = "Lignes de communication",
  },
  [2996] = {
    ["D"] = "Je vous ai beaucoup appris et maintenant je dois passer la main à un autre professeur. Il s\'appelle Strahad et habite à Ratchet. C\'est un humain, mais que cela ne vous incline pas à croire qu\'il est faible. Il est de petite taille, c\'est vrai, mais très puissant et pourra vous apprendre des invocations beaucoup plus importantes. Allez le voir, maintenant, vous le trouverez à Ratchet... A côté de la tour qui surplombe le port.",
    ["O"] = "Parler à Strahad Farsan, à Ratchet.",
    ["T"] = "À la recherche de Strahad",
  },
  [2997] = {
    ["D"] = "Bonjour, $N. Je sais que vous êtes très $goccupé:occupée; à aider les gens de Dun Morogh, mais si vous avez le temps, allez jusqu\'à Ironforge pour parler à la vénérable Tiza Battleforge. Les plus jeunes de nos paladins vont la voir lorsqu\'ils veulent en apprendre plus sur la Lumière et sur ce que l\'on attend d\'eux.$B$BVous la trouverez à l\'étage du Hall des Mystères qui se trouve dans la Garde mystique. Bonne chance, $N.",
    ["O"] = "Parler à Tiza Battleforge à Ironforge.",
    ["T"] = "Le Tome de divinité",
  },
  [2998] = {
    ["D"] = "Salutations ! Duthorian Rall, l\'un de nos paladins les plus influents, parlait de vous récemment. Je l\'ai entendu dire qu\'il était temps que vous soyez $gmis:mise; à l\'épreuve... Ainsi que des compliments vous concernant.$B$BJe vous conseille de vous rendre à Stormwind et de lui parler. Je pense que vous le trouverez dans la cathédrale de la Lumière, où il passe le plus clair de son temps.",
    ["O"] = "Parler à Duthorian Rall à Stormwind.",
    ["T"] = "Le Tome de divinité",
  },
  [2999] = {
    ["D"] = "Bonjour, $N. Je sais que vous êtes très $goccupé:occupée; à aider les gens de Dun Morogh, mais si vous avez le temps, allez jusqu\'à Ironforge pour parler à la vénérable Tiza Battleforge. Les plus jeunes de nos paladins vont la voir lorsqu\'ils veulent en apprendre plus sur la Lumière et sur leurs obligations.",
    ["O"] = "Parler à Tiza Battleforge, à Ironforge.",
    ["T"] = "Le Tome de divinité",
  },
  [3000] = {
    ["D"] = "Bonjour, $N. Je sais que vous êtes très $goccupé:occupée; à aider les gens d\'Azeroth, mais si vous avez le temps, retournez à Ironforge et parlez à l\'une de nos paladins les plus respectés, Tiza Battleforge. Nos jeunes paladins nains vont la consulter quand ils veulent en apprendre plus au sujet de la Lumière et sur ce que l\'on attend d\'eux.$B$BVous la trouverez à l\'étage du Hall des Mystères qui se trouve dans la Garde mystique.",
    ["O"] = "Parler à Tiza Battleforge à Ironforge.",
    ["T"] = "Le Tome de divinité",
  },
  [3001] = {
    ["D"] = "Je vous ai appris des sorts, mais l\'heure est venue pour d\'autres leçons que je ne puis plus prodiguer. Votre nouveau professeur habite à Ratchet ; il s\'appelle Strahad. C\'est un humain, mais ne vous fiez pas à cela : il n\'est ni faible ni hostile et ses pouvoirs sont grands. Il pourra vous enseigner les grandes invocations que vous avez maintenant les moyens d\'apprendre.$B$BVous le trouverez à Ratchet... à côté de la tour qui surplombe le port.",
    ["O"] = "Trouver Strahad Farsan, à Ratchet.",
    ["T"] = "À la recherche de Strahad",
  },
  [3002] = {
    ["D"] = "Je connais bien mon ennemi, $N, mais je ne suis qu\'un guerrier. Je ne peux pas vous dire à quoi peut servir cet orbe.$B$BMais je connais un mage à Orgrimmar qui saura nous en dire plus à ce sujet. Montrez-lui l\'objet.",
    ["O"] = "Apporter l\'Orbe Gordunni à Uthel\'nay, au pavillon de Buissombre, à Orgrimmar.",
    ["T"] = "L\'Orbe Gordunni",
  },
  [3022] = {
    ["D"] = "$N, je vois que vous aspirez à embrasser ma louable cause. Bien entendu, vous pouvez faire quelque chose pour moi !$B$BApportez cette caisse d\'oeufs à Erelas Ambersky. Ce druide les met en couveuse puis entraîne les jeunes hippogryphes. Vous le trouverez dans Teldrassil, dans un petit village appelé Rut\'theran.",
    ["O"] = "Apporter la Caisse soigneusement empaquetée à Erelas Ambersky, au village de Rut\'theran.",
    ["T"] = "À manipuler avec soin",
  },
  [3042] = {
    ["D"] = "Les trolls Sandfury de Zul\'Farrak fabriquent un agent durcissant à partir du grès, que j\'utilise aussi pour tremper mes créations. Cette matière est très prisée, et si vous m\'en rapportez une bonne quantité, vous serez largement $grétribué:rétribuée;.$B$BZul\'Farrak est au nord-ouest de Gadgetzan. Bonne chance.",
    ["O"] = "Apporter 20 Fioles d\'agent durcissant troll à Trenton Lighthammer, dans Gadgetzan.",
    ["T"] = "Agent durcissant troll",
  },
  [3062] = {
    ["D"] = "Edana Serrehaine est la reine des harpies Ruissenord, qui vivent dans les ruines, à l\'ouest. Cette créature est le mal à l\'état brut ; il paraît que son coeur est de cristal froid, vide de toute émotion sauf de haine pure. Je vous demande de me rapporter le coeur d\'Edana, après l\'avoir tuée.$B$BUn cor est en possession des harpies ; et s\'il est utilisé au lieu-dit des Pierres de Serrehaine, dans la partie nord des ruines, la reine est invoquée.$B$BImaginez sa surprise quand elle se rendra compte que ce n\'est pas sa chère famille, mais sa mort qui l\'a appelée.",
    ["O"] = "Rapporter le Coeur noir d\'Edana Serrehaine à Talo Thornhoof, à Camp Mojache.",
    ["T"] = "Coeur noir",
  },
  [3063] = {
    ["D"] = "Jadis, j\'étais un chasseur de renom et ne craignais aucune espèce de créature. Jusqu\'à ce que...$B$BUn jour que j\'aiguisais mon épée dans la forêt, après une rude bataille, des harpies m\'ont encerclé et attaqué. Je ne sais comment je réussis à en sortir vivant.$B$BEncore maintenant, je peux entendre leurs affreux cris de crécelle...$B$BDepuis cette embuscade, je ne suis plus le même tauren ; j\'ai peur et mon âme est emplie de haine.$B$BLes harpies Ruissenord vivent à l\'ouest. $N, soyez le bras armé de ma colère, vengez-moi !",
    ["O"] = "Tuer 4 Harpies Ruissenord, 4 Volplumes Ruissenord, 4 Imploratrices célestes Ruissenord et 4 Pourfendeuses Ruissenord pour Talo Thornhoof.",
    ["T"] = "Se venger des Ruissenord",
  },
  [3064] = {
    ["D"] = "I need pirate hats for my shop.  Get me lots!",
    ["O"] = "Bring 20 Untorn Pirate Hats to Yorba Screwspigot.",
    ["T"] = "<NYI> <TXT> Pirate Hats",
  },
  [3065] = {
    ["D"] = "Ah, pendant que vous n’étiez pas là un parchemin est arrivé pour vous, $N.$B$BLisez-le quand vous en aurez le temps. Si je ne me trompe pas, il provient du Maître des guerriers Frang. Il souhaiterait s’entretenir avec vous, quand vous jugerez le moment venu. Vous le trouverez à l’extérieur de l’Antre, dissimulé dans l’ombre.",
    ["O"] = "Lire la Tablette simple, et parler à Frang, dans la Vallée des épreuves.",
    ["T"] = "Tablette simple",
  },
