  },
  [3110] = {
    ["D"] = "Pendant que vous m\'aidiez, on m\'a donné cette rune pour vous. Prenez le temps de la lire. Je pense qu\'elle provient du Maître des prêtres, Branstock. Jetez-y un coup d\'oeil et allez le retrouver, à l\'intérieur d\'Anvilmar, quand vous en aurez le temps.",
    ["O"] = "Lire la Rune bénie, et parler à Branstock Khalder, à Anvilmar.",
    ["T"] = "Rune bénie",
  },
  [3111] = {
    ["D"] = "Long ago high elves taught us the secrets of magic along with our human allies. They preached to us about rules and how magic can make ya go mad! But don\'t believe it. We\'re not like the elves; we don\'t have the same weaknesses. Just keep yourself on the right path and you\'ll find magic is as powerful a tool as it is a weapon.$B$BWhen you\'re ready, come find me inside Anvilmar. I\'ll be waiting for ya!",
    ["O"] = "Speak to Marryk Nurribit inside Anvilmar.",
    ["T"] = "Glyphic Rune",
  },
  [3112] = {
    ["D"] = "Pendant que vous m\'aidiez, on m\'a donné cette note de service pour vous. Prenez le temps de la lire. Je pense qu\'elle provient du Maître des guerriers, Thran. Jetez-y un coup d\'oeil et allez le retrouver, à l\'intérieur d\'Anvilmar, quand vous en aurez le temps.",
    ["O"] = "Lire la Note de service simple, et parler à Thran Khorman, dans la Vallée des Frigères.",
    ["T"] = "Note de service simple",
  },
  [3113] = {
    ["D"] = "Pendant que vous m\'aidiez, on m\'a donné cette note de service pour vous. Prenez le temps de la lire. Je pense qu\'elle provient du Maître des voleurs, Solm. Jetez-y un coup d\'oeil et allez le retrouver, à l\'intérieur d\'Anvilmar, quand vous en aurez le temps.",
    ["O"] = "Lire la Note de service codée, et parler à Solm Hargrin, dans la Vallée des Frigères.",
    ["T"] = "Note de service codée",
  },
  [3114] = {
    ["D"] = "Pendant que vous m\'aidiez, on m\'a donné cette note de service pour vous. Prenez le temps de la lire. Je pense qu\'elle provient du Maître des mages, Marryk. Jetez-y un coup d\'oeil et allez le retrouver, à l\'intérieur d\'Anvilmar, quand vous en aurez le temps.",
    ["O"] = "Lire la Note de service glyphée, et parler à Marryk Nurribit, dans Anvilmar, au-dessus de la Vallée des Frigères.",
    ["T"] = "Note de service glyphée",
  },
  [3115] = {
    ["D"] = "Pendant que vous m\'aidiez, on m\'a donné cette note de service pour vous. Prenez le temps de la lire. Je pense qu\'elle provient du Maître des démonistes, Alamar. Jetez-y un coup d\'oeil et allez le retrouver, à l\'intérieur d\'Anvilmar, quand vous en aurez le temps.$B$BEt faites attention, $N, on n\'apprécie pas trop votre espèce, sur ces terres.",
    ["O"] = "Lire la Note de service contaminée, et parler à Alamar Grimm, dans Anvilmar, au-dessus de la Vallée des Frigères.",
    ["T"] = "Note de service contaminée",
  },
  [3116] = {
    ["D"] = "Ce sceau m\'a été donné par un messager de notre Maître des guerriers, Alyissia. Elle souhaiterait vous parler, dès que vous aurez un moment à lui accorder. Lisez-le et rapportez-le-lui.",
    ["O"] = "Lire le Sceau simple, et parler à Alyissia, à Sombrevallon.",
    ["T"] = "Sceau simple",
  },
  [3117] = {
    ["D"] = "Ce sceau m\'a été donné par un messager de notre Maître des chasseurs, Ayanna. Elle souhaiterait vous parler, dès que vous aurez un moment à lui accorder. Lisez-le et rapportez-le-lui.",
    ["O"] = "Lire le Sceau gravé, et parler à Ayanna Everstride, au sommet d\'Aldrassil, dans Sombrevallon.",
    ["T"] = "Sceau gravé",
  },
  [3118] = {
    ["D"] = "Ce sceau m\'a été donné par un messager de notre Maître des voleurs, Frahun. Il souhaiterait vous parler, dès que vous aurez un moment à lui accorder. Lisez-le et rapportez-le-lui.",
    ["O"] = "Lire le Sceau codé et parler à Frahun Shadewhisper, à Sombrevallon.",
    ["T"] = "Sceau codé",
  },
  [3119] = {
    ["D"] = "Ce sceau m\'a été donné par un messager de notre maître des prêtres, Shanda. Elle souhaiterait vous parler, dès que vous aurez un moment à lui accorder. Lisez-le et rapportez-le-lui.",
    ["O"] = "Lire le Sceau béni, et parler à Shanda, à Aldrassil.",
    ["T"] = "Sceau béni",
  },
  [3120] = {
    ["D"] = "Ce sceau m\'a été donné par un messager de notre maître des druides, Mardant. Il souhaiterait vous parler, dès que vous aurez un moment à lui accorder. Lisez-le et rapportez-le-lui.",
    ["O"] = "Lire le Sceau verdoyant, et parler à Mardant Strongoak, dans l\'arbre Aldrassil de Sombrevallon.",
    ["T"] = "Sceau verdoyant",
  },
  [3121] = {
    ["D"] = "Neeru Fireblade à Orgrimmar a ce qu\'il me faut pour travailler ici ! Il a préparé un mélange dont j\'ai besoin pour... Enfin vous verrez.$B$BMettez-vous en chemin pour Orgrimmar. Tenez, prenez ceci. C\'est une tête réduite que j\'ai promise à Neeru en échange des herbes. C\'est la tête d\'un pauvre nain qui a croisé ma route dans la forêt. Je suis certain que Neeru appréciera.$B$BVous trouverez Neeru dans la Faille de l\'ombre.",
    ["O"] = "Porter la tête réduite à Neeru Fireblade à Orgrimmar.",
    ["T"] = "Une Requête étrange",
  },
  [3122] = {
    ["D"] = "Voici les herbes, $N. Portez-les au sorcier-docteur Uzer\'i.$B$BDites-lui que je suis très content de la tête réduite.",
    ["O"] = "Livrer une Poche d\'herbes de Neeru au Sorcier docteur Uzer\'i, en Feralas.",
    ["T"] = "Retourner voir le sorcier-docteur Uzer\'i",
  },
  [3123] = {
    ["D"] = "Il va s\'agir d\'un test, qui mettra à l\'épreuve le rituel que j\'ai accompli comme vos capacités. Prenez ce récipient, $N. Avec cela, vous pourrez réduire la taille des créatures et les y capturer.$B$BPartez pour les Hinterlands et cherchez des bêtes connues sous le nom d\'Indomptables. C’étaient autrefois les animaux de compagnie de la déesse des elfes de la nuit, Elune. Vos cibles sont les Chouettards, des Indomptables vicieux, primitifs et sauvages, $N. Tuez-en 10, puis utilisez le Récipient de muisek pour les faire rétrécir et les capturer avant que leurs esprits ne s\'échappent.",
    ["O"] = "Partez pour les Hinterlands et localisez les Indomptables. Tuez-en 10, puis utilisez le Récipient de muisek pour faire rétrécir et capturer les Indomptables vaincus.$B$BRamenez 10 Indomptables miniaturisés et le Récipient d\'Indomptables miniaturisés au sorcier-docteur Uzer\'i, en Féralas.",
    ["T"] = "Test du récipient",
  },
  [3124] = {
    ["D"] = "Puisque vous avez montré que vous êtes à la fois digne de confiance et $gdoué:douée; pour utiliser le Vaisseau de muisek, je souhaite à présent que vous capturiez une autre créature.$B$BLes hippogryphes ont été les compagnons loyaux des elfes de la nuit durant plusieurs années. Leur esprit est résolu et ferme.$B$BVotre tâche consiste à tuer 10 hippogryphes Aigreplume et de rapidement les faire rétrécir et les capturer avec le vaisseau, afin que leur muisek soit préservé. Vous trouverez les hippogryphes dans les Plateaux sauvages au sud d\'ici.",
    ["O"] = "Tuer 10 Hippogryphes Aigreplume de n\'importe quel type, puis utiliser le Vaisseau de muisek pour les faire rétrécir et les capturer.",
    ["T"] = "Hippogryphe miniaturisé",
  },
  [3125] = {
    ["D"] = "À présent, $N, je souhaite que vous capturiez une créature fragile en apparence, mais dont l\'esprit puissant nous intéresse. Je veux parler des dragons féeriques. Ils ont été les alliés des elfes de la nuit. Nous devons en capturer afin que la Horde puisse également bénéficier de cette force spirituelle.$B$BVoici le Vaisseau, $N. Tuez 8 Fées fléchetteuses ou Petits dragons féeriques, et capturez leur muisek. On peut en trouver à l\'ouest d\'ici.",
    ["O"] = "Tuer 8 Fées fléchetteuses ou Petits dragons féeriques. Utiliser le Vaisseau de muisek pour faire rétrécir et capturer les dragons féeriques vaincus.",
    ["T"] = "Dragon féerique miniaturisé",
  },
  [3126] = {
    ["D"] = "Les marcheurs des bois errants que l\'on peut voir vagabonder sous les arbres sont nos prochains objectifs, $N. Pour combattre la Horde, les elfes de la nuit ont enrôlé des tréants comme alliés. Ils ont beau être lents, leur rôle de protecteurs n\'est pas négligeable.$B$BNous pourrons bientôt utiliser cette puissance à notre profit.$B$BTrouvez trois Marcheurs des bois errants. Tuez-les, puis utilisez le Vaisseau de muisek pour les réduire et les capturer.",
    ["O"] = "Tuer 3 Marcheurs des bois errants. Utiliser le Vaisseau de muisek pour faire rétrécir et pour capturer les tréants vaincus.",
    ["T"] = "Tréant miniaturisé",
  },
  [3127] = {
    ["D"] = "La dernière chose que je vous demande est de partir vers le nord-ouest pour trouver l\'ennemi final.$B$BLes géants des montagnes ont quitté leurs montagnes pour aider les elfes de la nuit quand la Légion ardente est apparue. Vous les reconnaîtrez à leur stature massive ainsi qu’aux vibrations du sol à mesure que vous avancerez dans leur contrée rocailleuse.$B$BJ\'ai à cœur de maîtriser une puissance d\'une telle taille. Tuez 7 géants des montagnes puis utilisez le Récipient pour capturer leur puissant muisek.",
    ["O"] = "Tuer 7 Marcheurs des terres ou Géants des falaises. Utiliser le Récipient de muisek pour faire rétrécir et pour capturer les Géants des montagnes.",
    ["T"] = "Géant des montagnes miniaturisé",
  },
  [3128] = {
    ["D"] = "Je vais avoir besoin d\'autres choses pour finir ce que j\'ai commencé... Vous devrez me rendre ce service avec d\'autres choses que je vous donnerai à faire.$B$BJ\'aurai besoin de bûches fendues de tréant de Feralas. Du minerai incrusté, que l\'on peut trouver sur les géants des montagnes ou les dragons féeriques, à l\'ouest. Aussi, trouvez-moi des tendons résistants des chouettards des Hinterlands ou des hippogryphes de Feralas. Enfin, il me faudrait une grande quantité de fragments métalliques de chacune des créatures mentionnées.",
    ["O"] = "Apporter 2 Bûches fendues, 6 Minerais incrustés, 20 Morceaux de tendons résistants et 40 Fragments métalliques au sorcier-docteur Uzer\'i au Camp Mojache.",
    ["T"] = "Matériaux naturels",
  },
  [3129] = {
    ["D"] = "Imprégner les armes de l\'esprit des miniatures est un processus difficile. Maintenant que nous avons les matériaux nécessaires et les miniatures des créatures que vous avez capturées, je peux commencer. Attendez ; je reviens dans un moment.",
    ["O"] = "Le sorcier-docteur Uzer\'i veut que vous patientez tandis qu\'il prépare les armes.",
    ["T"] = "Armes de l\'Esprit",
  },
  [3130] = {
    ["D"] = "À présent, je vais vous diriger vers mon second, Latronicus. Il a gardé un oeil attentif sur les forces des nagas jusqu\'alors. J\'estimerai la situation générale ici selon les nagas. Non pas que je veuille envoyer une force compacte les combattre, laissant ainsi la forteresse ouverte à une attaque directe de la Horde, ou pire...$B$BMais passons, Latronicus attend. Vous avez fait du bon travail jusque là, $N. Continuez comme ça.$B$BVous pouvez disposer.",
    ["O"] = "Se présenter devant Latronicus Moonspear au bastion de Feathermoon.",
    ["T"] = "Contre les Hainecrête",
  },
  [3141] = {
    ["D"] = "Donc vous souhaitez vous battre contre des démons ? Vous feriez bien d\'écouter mon histoire.",
    ["O"] = "Écouter l\'histoire de Loramus Thalipedes.",
    ["T"] = "Loramus",
  },
  [3161] = {
    ["D"] = "$N, je suis un scientifique mais au fond de moi je suis aussi un chasseur de trésor !$B$BIl y a longtemps, quand les trolls occupaient ces terres, ils ont créé de nombreux ornements à l\'image de Gahz\'rilla, une hydre qu\'ils adorent comme divinité. Ces ornements sont sculptés à partir d\'une matière que j\'appelle \"Gahz\'ridian,\" d\'après le nom du dieu hydre.$B$BJ\'ai en déjà trouvé un peu moi-même, mais j\'aimerais en avoir plus à étudier ; peut-être pourrez-vous m\'en trouver ?$B$BJ\'ai même inventé un détecteur qui rend la recherche de Gahz\'ridian un jeu d\'enfant ! Il vous suffit de le porter et la recherche peut commencer !",
    ["O"] = "Marvon Rivetseeker à Tanaris veut que vous collectiez 30 Ornements Gahz\'ridian.",
    ["T"] = "Gahz\'ridienne",
  },
  [3181] = {
    ["D"] = "La corne gigantesque est couverte d\'égratignures et de fissures. Un objet de métal inhabituel s\'est logé sous la surface dure.$B$BAprès de plus amples observations, vous remarquez qu\'il s\'agit d\'une lame de hache brisée. Sur le métal sont gravés les mots suivants :$B$BPropriété de la Montagnarde Pebblebitty.$B$BQuand vous essayez de déloger la lame, elle s\'effrite en mille morceaux.$B$BOups !",
    ["O"] = "Porter la Corne Gigantesque de Margol à la Montagnarde Pebblebitty.",
    ["T"] = "La corne de la bête",
  },
  [3182] = {
    ["D"] = "Et vous pensez que je vais avaler une histoire pareille ? Le fer de ma hache se serait brisé et serait coincé dans la véritable corne, s\'il s\'agissait bien d\'elle.$B$BÉcoutez, apportez ce faux grossier au conservateur en chef Thorius à Ironforge. S\'il est capable de valider vos dires, vous recevrez un certificat de haut fait. Rapportez-moi cette preuve et je vous remettrai la clé de la gorge des Vents brûlants.$B$BAllez-y !",
    ["O"] = "Porter la Corne gigantesque de Margol au conservateur en chef Thorius à Ironforge.",
    ["T"] = "Titre de propriété",
  },
  [3201] = {
    ["D"] = "Je n\'arrive pas à croire que Pebblebitty ait pu douter de l\'authenticité de la corne. Entre vous et moi, je pense qu\'elle est seulement jalouse. Je sais qu\'elle tenait vraiment à tuer elle-même cette bête, à cause de \"l\'incident\" et tout.$B$BDonc vous avez besoin d\'un document comme preuve ? Après la contribution que vous venez de faire au musée, ce ne sera pas un problème.$B$B<Le conservateur en chef Thorius commence à remplir un grand document>$B$BEt voilà ! Vous pouvez présenter ceci à Pebblebitty.",
    ["O"] = "Apporter le document de preuve à la Montagnarde Pebblebitty.",
    ["T"] = "Enfin !",
  },
  [3221] = {
    ["D"] = "Tandis que vous étiez en train d\'aider nos Traqueurs noirs, l\'apothicaire Renferrel vous a fait appeler. Il n\'a rien précisé mais a dit qu\'il voulait vous parler à propos des coeurs de loup que vous lui aviez donnés.",
    ["O"] = "Parler à l\'apothicaire Renferrel au Sépulcre.",
    ["T"] = "Parler à Renferrel",
  },
  [3241] = {
    ["D"] = "Collect the totems from Dreadmist Peak, because it\'s icky there now and no place for shiny clean totems.",
    ["O"] = "Bring them to Tonga Runetotem.",
    ["T"] = "<NYI> <TXT><redux> Dreadmist Peak",
  },
  [3261] = {
    ["D"] = "Nos routes se séparent, $N. Pour en apprendre plus, vous devrez aller au Camp Taurajo, vers le sud et parler à Jorn Skyseer. Il continuera de vous guider.",
    ["O"] = "Parler à Jorn Skyseer, à Camp Taurajo.",
    ["T"] = "Jorn Oeil-des-Cieux",
  },
  [3281] = {
    ["D"] = "Vous avez peut-être tué les raptors, mais l\'argent qu\'ils ont volé doit être récupéré ! Des rapports indiquent que les raptors ont un important site de nidification au sud de Ratchet. Cet endroit est connu sous le nom de Terres des raptors. Il est probable qu\'ils y aient apporté notre argent volé.$B$BAllez dans ce repaire de raptors et cherchez-le. Quand vous l\'aurez trouvé, rapportez-le-moi.",
    ["O"] = "Rapportez l\'Argent volé à Gazrog à la Croisée.",
    ["T"] = "Argent volé",
  },
  [3301] = {
    ["D"] = "Ma camarade de clan, Mura Runetotem, est partie pour les Pins argentés afin d\'aider nos alliés morts-vivants. C\'est une forêt qui dépérit doucement, et elle avait l\'espoir d\'y insuffler la vie à nouveau. Peut-être que la vie insolite que nous trouvons ici dans les Tarides l\'aiderait à réaliser ses souhaits pour les Pins argentés ?$B$BPrenez avec vous une des carapaces que vous avez apportées, et donnez-la à Mura Runetotem aux Pins argentés. Elle séjourne au Sépulcre. Avec un peu de chance, l\'étude de cette carapace l’aidera à trouver une solution pour guérir cette forêt à l\'agonie.",
    ["O"] = "Parler à Mura Runetotem au Sépulcre.",
    ["T"] = "Mura Totem-Runique",
  },
  [3321] = {
    ["D"] = "Vous avez beaucoup fait pour l\'Ordre, $N. Le total dévouement nécessaire pour parvenir jusqu\'ici aurait rebuté toute personne ordinaire. Il ne fait aucun doute dans mon esprit, ni dans celui de Galvan, que vous faites partie des nôtres.$B$BC\'est la raison pour laquelle j\'estime que vous méritez une petite récompense. Ce n\'est pas grand chose en fait, un simple colifichet. Il se peut que vous soyez $gfamilier:familière; avec ses propriétés, car je crois que c\'est ce qui vous a $gamené:amenée; à nous en premier lieu.",
    ["O"] = "Attendre que Trenton ait terminé son travail.",
    ["T"] = "Est-ce que ceci est à vous ?",
  },
  [3341] = {
    ["D"] = "Il se trouve dans les Tarides un horrible fouillis de ronces entremêlées, les Souilles de Tranchebauge. Bien que nos premières observations à cet endroit n\'aient révélé que peu de dangers, nos découvertes récentes sont beaucoup plus alarmantes…$B$BLes hurans des Souilles de Tranchebauge se sont alliés au Fléau. Une liche du nom de Amnennar, dite le Porte-froid, les contrôle à présent et utilise le pouvoir de son imposante connaissance pour diriger chacun de leurs mouvements.$B$BAmnennar est liée à Ner\'zhul par la télépathie ; nous devons trancher ce lien, $N. Il faut éliminer le Porte-froid.",
    ["O"] = "Andrew Brownell veut que vous tuiez Amnennar le Porte-froid et que vous lui rameniez son crâne.",
    ["T"] = "L\'anéantissement",
  },
  [3361] = {
    ["D"] = "Nous avons chassé les troggs de Gnomeregan, mais c\'est devenu catastrophique depuis ! Maintenant notre maison est complètement irradiée et nous, les gnomes, nous avons été dispersés dans tout Dun Morogh.$B$BDans ma hâte de fuir les radiations, j\'ai perdu toutes mes affaires personnelles et mes outils. Ce sont les trolls qui les ont pris. Ils ont volé mon coffre, ma boîte et mon seau de boulons ! Ils les ont emportés dans leur camp au sud-ouest d\'Anvilmar.$B$BJe ne suis pas un aventurier… Pourriez-vous retrouver mes affaires et me les rapporter ici, s\'il vous plaît ?",
    ["O"] = "Apporter la Boîte de Felix, le Coffre de Felix et le Seau de boulons de Felix à Felix Whindlebolt, à Anvilmar.",
    ["T"] = "Les malheurs d\'un réfugié",
  },
  [3362] = {
    ["D"] = "Avez-vous déjà vu la vallée des Chardonniers ? C’est un endroit dangereux, $N.$B$BIl y a d’étranges bêtes là-bas, imprévisibles ! Je crois qu’on les appelle les Chardonniers noueux et les Chardonniers taille-racine...$B$BJe veux accéder à la vallée, mais avec tous ces Chardonniers dans les parages c’est bien trop risqué. Si vous vouliez bien aller en tuer quelques-uns, je pourrais envisager d’y retourner. Qu’en pensez-vous, $N ? M’aiderez-vous ?",
    ["O"] = "Tran\'rek, à Gadgetzan, voudrait que vous tuiez 8 Chardonniers noueux et 8 Chardonniers taille-racines.",
    ["T"] = "La vallée des Chardonniers",
  },
  [3363] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La fleur-de-chant corrompue",
  },
  [3364] = {
    ["D"] = "Ah ! Je devais apporter un Thé brûlant à Durnan Furcutter, à Anvilmar, mais je dois d’abord en porter un à Grelin. Je n\'arriverai jamais à Anvilmar avant qu\'il ne refroidisse !$B$BVous m’avez l\'air rapide. Vous pouvez le faire. Cette boisson ne restera chaude que 5 minutes et Durnan n\'a pas commandé un Thé \"glacé\", alors partez tout de suite. Anvilmar est un village dans la montagne, au nord-est.$B$BMerci, $N, et n\'oubliez pas de me rapporter ma tasse !",
    ["O"] = "Apporter un Thé brûlant à Durnan Furcutter, dans Anvilmar, dans les cinq minutes imparties avant qu\'il ne refroidisse !",
    ["T"] = "Une livraison de thé brûlant",
  },
  [3365] = {
    ["D"] = "Ah ! Vous êtes $garrivé:arrivée; à point nommé ! Rien de tel qu\'un bon thé chaud, non, BRULANT, par une froide journée d\'hiver pour réchauffer les méandres de votre esprit.$B$BTenez, $N, soyez assez aimable pour rapporter cette tasse vide à Nori pour moi, voulez-vous ?",
    ["O"] = "Apporter la Chope de Nori à Nori Pridedrift.",
    ["T"] = "Rapporter la Tasse",
  },
  [3366] = {
    ["D"] = "L\'éclat est d\'un vert brillant, et bien que compact, ce qui ressemble à de la fumée tourbillonne juste au-dessous de sa surface. Vous ne pouvez vous empêcher de contempler les profondeurs du cristal, bien qu\'en même temps, vous sentiez qu\'il y réside quelque chose de très inquiétant.$B$BPeut-être devriez-vous voir si quelqu\'un à Ratchet pourrait vous en apprendre plus à propos de cet étrange éclat...",
    ["O"] = "Aller à Ratchet pour trouver la raison d’être de l’Eclat de cauchemar.",
    ["T"] = "L\'Éclat luminescent",
  },
  [3367] = {
    ["D"] = "<Dorius gémit.>$B$BJe suis grièvement blessé, $n. Je me sens… la tête toute légère… tout… tournoie… je saigne… à l\'intérieur.$B$BJe dois retourner à Ironforge. Aidez-moi, $N.",
    ["O"] = "Aider Dorius à retourner à Ironforge.",
    ["T"] = "Les pierres de Suntara",
  },
  [3368] = {
    ["D"] = "Mon frère,$B$BSi vous lisez cette lettre, c\'est que je n\'existe plus. Comment vous est-elle parvenue, je l\'ignore. Mais remerciez et récompensez ceux qui ont fait en sorte que vous l\'ayez reçue.$B$BÀ propos des pierres : elles existent, les Sombrefer les ont en leur possession. Leur chef, Lathoric le Noir, utilise le pouvoir de ces pierres pour asservir mon équipe d\'archéologues. Ils ont été emmenés au Chaudron. Leurs esprits sont à présent asservis et ils travaillent nuit et jour pour achever une chose qui répond au nom d\'Obsidion.$B$BSecourez-les. Il s\'agit là de la dernière volonté d\'un homme mort.$B$B-Dorius",
    ["O"] = "Apporter la Lettre roussie à Thorius à Ironforge. Il se trouve dans le Hall des Explorateurs.",
    ["T"] = "Les pierres de Suntara",
  },
  [3369] = {
    ["D"] = "Cet éclat détient de grands secrets. Il s\'agit de l\'essence pure du Rêve d\'émeraude. Ce que je vois dans cet éclat n\'a rien d\'un rêve cependant. On pourrait même appeler cela une vision de cauchemar.$B$BMon but n\'est pas de vous alarmer, $N, mais je crois qu\'il serait mieux pour vous de remettre cet éclat à un grand druide, et ce le plus tôt possible.$B$BLa personne que vous devriez rencontrer à la Cime des Anciens se nomme Hamuul Runetotem. Partez pour Thunder Bluff. Je ne peux pas vous en dire plus.",
    ["O"] = "Apporter l\'Eclat de cauchemar à Hamuul Runetotem à la Cime des Anciens.",
    ["T"] = "Au coeur des cauchemars",
  },
  [3370] = {
    ["D"] = "Cet éclat détient de grands secrets. Il s\'agit de l\'essence pure du Rêve d\'émeraude. Ce que je vois dans cet éclat n\'a rien d\'un rêve cependant. On pourrait même appeler ce genre de vision un cauchemar.$B$BMon souhait n\'est pas de vous alarmer, $N, mais je crois qu\'il serait mieux pour vous de remettre cet éclat aux mains d\'un grand druide, et ce dès que vous le pouvez.$B$BLa personne que vous devez trouver au Cercle cénarien se nomme Mathrengyl Bearwalker. Partez pour Darnassus. Je ne peux vous en dire plus.",
    ["O"] = "Apporter l\'Eclat de cauchemar à Mathrengyl Bearwalker à Darnassus.",
    ["T"] = "Au coeur des cauchemars",
  },
  [3371] = {
    ["D"] = "Il y a, à présent, un creux vide de sens, là où se trouvait autrefois mon coeur, $N. Je ne me remettrai pas de la perte de Dorius. Je dois faire en sorte que sa dernière volonté soit accomplie. Justice doit être rendue contre ceux qui ont commis ce meurtre de sang-froid.$B$BRetournez à la Gorge des Vents brûlants et respectez sa dernière volonté : libérez ses hommes.",
    ["O"] = "Retourner à la Gorge des Vents brûlants et trouver l\'Equipe d\'archéologues de Dorius.",
    ["T"] = "La justice des nains",
  },
  [3372] = {
    ["D"] = "Libérez-les, $N. Leurs corps sont morts, mais la puissance de Suntara les oblige à achever leur travail.$B$BL\'Autel de Suntara est la clé pour les libérer. Allez au nord, dans les collines au-dessus de la caverne… Le Marteau du crépuscule y garde une relique. Emparez-vous-en et placez-la sur l\'Autel de Suntara.$B$B<L\'archéologue mourant s\'effondre.>",
    ["O"] = "Trouver l\'Artefact mystérieux gardé par le Marteau du crépuscule au nord.",
    ["T"] = "La libération",
  },
  [3373] = {
    ["D"] = "Alors que vous touchez la gemme, une voix grave résonne dans votre esprit.$B$B\"$gMortel:Mortelle;, considérez ma requête. Je suis Eranikus des dragons verts, gardien de ce temple maudit. J\'avais pour charge de m\'assurer que les trolls n\'amènent plus en ces lieux leurs dieux abominables. Mais j\'ai échoué. Alors que j\'imprègne cette gemme d\'une partie de mon essence, je sens que mon étreinte sur le Rêve s\'évanouit vers le néant. Je vous en prie, placez cette gemme dans le Réceptacle d\'essence de mon antre, et que notre magie en ces lieux purge l\'infection de mon esprit et me permette de reposer.\"",
    ["O"] = "Placer l\'Essence d\'Eranikus dans le Réceptacle d\'essence situé dans son antre dans le Temple englouti.",
    ["T"] = "L\'Essence d\'Eranikus",
  },
  [3374] = {
    ["D"] = "Vous activez la Pierre de vœu et Itharius parle directement à votre esprit à travers elle.$B$B\"En acceptant la charge contenue dans cette Pierre de vœu, vous vous engagez à soutenir ma cause et celle des dragons d\'Ysera, qui est de racheter l\'esprit corrompu d\'Eranikus. Le premier sacrifice, parmi les nombreux que vous et moi ferons sans aucun doute, va être de m\'abandonner la gemme qui a enchaîné ce qu\'il reste de volonté dans l\'esprit d\'Eranikus.\"$B$B\"Faites-le, $N, et nous œuvrerons de concert au rachat d\'une âme digne de rédemption.\"",
    ["O"] = "Apporter la Pierre de voeu des dragons d\'Ysera et l\'Essence d\'Eranikus enchaînée à Itharius au marais des Chagrins. C\'est là que vous choisirez d\'aider ou non les dragons d\'Ysera.",
    ["T"] = "L\'Essence d\'Eranikus",
  },
  [3375] = {
    ["D"] = "Hum, vous avez besoin d\'une autre Fiole de divination ? C\'est une plaisanterie, n\'est ce pas ? Qu\'avez-vous fait de la première ?! Beaucoup de travail nous attend, $N. Ces fioles ne poussent pas sur les arbres. En fait, j\'aimerais bien que ce soit le cas, car je m\'y rendrais tout simplement et en cueillerais une, puisqu\'il ne m\'en reste plus !!$B$BPour que je puisse vous la préparer, j\'ai besoin des choses suivantes : une Potion de mana, un morceau de Charbon, et du Nectar sucré. Ne me demandez pas pourquoi, amenez-moi simplement tout ceci et je vous en ferai une autre.",
    ["O"] = "Apporter une Potion de mana, du Charbon, et du Nectar sucré à Talvash del Kissel à Ironforge pour remplacer la Fiole de divination de Talvash.",
    ["T"] = "La fiole de remplacement",
  },
  [3376] = {
    ["D"] = "$N, j\'ai entendu parler de vous. Peut-être saurez-vous nous venir en aide là où d\'autres ont échoué.$B$BNous, taurens, avons fait de cette terre notre foyer. Mais cela n\'a pas été sans pertes. Les hurans Dos-hirsute, du ravin de Roncelame, menés par leur chef Brochaigu Mantépine, nous accablent sans cesse. Je vous demande de me rapporter la tête de leur chef !$B$BVous pourrez le trouver dans le ravin, à l\'est, dans leur village de fortune.",
    ["O"] = "Apporter la Tête du chef Brochaigu Mantépine à Brave Windfeather à la mesa de Nuage rouge.",
    ["T"] = "La fin de Brochaigu !",
  },
  [3377] = {
    ["D"] = "Me viendrez-vous en aide, $gmortel:mortelle; ? Écoutez mon récit.",
    ["O"] = "Écouter le récit de Zamael.",
    ["T"] = "Prière à Elune",
  },
  [3378] = {
    ["D"] = "Me viendrez-vous en aide, $gmortel:mortelle; ? Trouverez-vous la Prière à Elune ?$B$BComme je l\'ai dit, cette prière a été gravée sur une tablette en or. Elle doit se trouver sur l\'une de ces crapules vénérant des idoles.$B$BSi vous parvenez à retrouver ma prière, apportez-la à Astarii Starseeker à Darnassus. Il importe peu que ma foi ait été ravivée après les innombrables actions malveillantes que j\'ai infligées à ce monde et à ses habitants, mais ma prière doit être entendue. La dernière chose que je demande est l\'absolution.",
    ["O"] = "Trouver la Prière à Elune et l\'apporter à Astarii Starseeker à Darnassus.",
    ["T"] = "Prière à Elune",
  },
  [3379] = {
    ["D"] = "Les araignées de la gorge des Vents brûlants sont une source inestimable de Soie des ténèbres. Comme vous le savez, la Soie des ténèbres est très recherchée par les artisans de notre métier.$B$BMalheureusement, un groupe de braconniers a récemment empiété sur ces terres, et massacre en masse les araignées sans se soucier des effets à long terme de leurs actions. Aussi, la première chose que je vous demanderai sera assez simple : trouvez-les, et... tuez-les.$B$BCommencez vos recherches au nord.$B$BSi vous avez besoin d\'assistance, parlez avec Raze.",
    ["O"] = "Tuer le groupe de braconniers Ombresoie qui rôde dans la gorge des Vents brûlants. Nilith a de bonnes raisons de croire qu\'ils doivent être exterminés tous les cinq. Retourner voir Nilith dans la gorge des Vents brûlants lorsque vous aurez terminé.",
    ["T"] = "Les tisseuses d\'ombre",
  },
  [3380] = {
    ["D"] = "Il existe en ce monde des esprits qui ne connaissent pas le repos, $N. Il existe des esprits viciés par leur solitude, liés à un mal antique…$B$BJ\'ai entendu parler d\'un endroit se trouvant profondément sous la surface de la terre, banni en ce lieu par des puissances plus grandes que je n\'aie jamais rencontrées.$B$BSi vous souhaitez en apprendre davantage sur cet endroit, le Temple englouti, partez à la recherche d\'un gobelin nommé Marvon Rivetseeker. Il a étudié de nombreuses parties du monde, et aux dernières nouvelles, il a quitté sa demeure à Ratchet pour étudier les ruines des trolls à Tanaris.",
    ["O"] = "Trouver Marvon Rivetseeker à Tanaris.",
    ["T"] = "Le Temple englouti",
  },
  [3381] = {
    ["D"] = "Ah, a young $r come to speak to Sanath. What a wonderful day! I am so honored to have you grace me with your presence. Thank you!$B$BPlease... do not be so arrogant to assume my sarcasm was a compliment. Let us get one thing straight: I don\'t like you. But this little conversation doesn\'t have anything to do with us--it has to do with my master.$B$BBring me a couple hippogryph feathers for my arrows and I\'ll give you leave to see him. He\'s asked that I send adventurers to him if they proved worthy enough.",
    ["O"] = "Apporter 2 Plumes d\'hippogryphe sans défaut à Sanath Lim-yo à Azshara, si vous souhaitez parler à son maître.",
    ["T"] = "Une rencontre avec le maître",
  },
  [3382] = {
    ["D"] = "Nous étions près de la côte et cherchions un endroit sûr où jeter l\'ancre. Le soleil ne s\'était pas couché depuis plus d\'une heure lorsqu\'ils ont attaqué… Ces maudits nagas !$B$BJe ne sais pas ce qu\'ils cherchaient, mais il est clair qu\'ils ne l\'ont pas trouvé. Ils sont déjà revenus une fois. Deux de mes éclaireurs sont de garde à l\'entrée de la vallée pour nous avertir lorsque la prochaine attaque aura lieu.$B$BCela me désole que vous arriviez à ce moment, $N, mais je n\'y peux rien.$B$BÊtes-vous $gprêt:prête; pour le combat de votre vie ?",
    ["O"] = "Défendre la capitaine Vanessa Beltis, son équipage et les passagers de “l’Horizon” contre l\'attaque des nagas. La capitaine Vanessa Beltis doit survivre.",
    ["T"] = "Un équipage sous le feu ennemi",
  },
  [3383] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [3384] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [3385] = {
    ["D"] = "Ils se sont donnés le nom de Sous-marché : des commerçants sans vergogne qui vendront, intimideront, commerceront, ou feront à peu près n\'importe quoi contre la bonne somme d\'argent.$B$BMais les choses empirent, $N. Ils ont établi une base d\'opérations ici dans la gorge des Vents brûlants. Kovic dirige les opérations et entretient des liens étroits avec les nains Sombrefer dans la région.$B$BNous devons frapper fort et envoyer un message clair à leurs dirigeants. Tuez Kovic, ainsi que son agent Clonk. Apportez-moi sa Sacoche du marchand lorsque vous en aurez terminé.$B$BPartez pour le Chaudron à présent !",
    ["O"] = "Tuer le Maître d\'artisanat Kovic et son agent Clonk. Récupérer sur son corps la Sacoche du marchand et l\'apporter à Nilith.",
    ["T"] = "Le Sous-marché",
  },
  [3401] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "<UNUSED>",
  },
  [3402] = {
    ["D"] = "Je veux que vous informiez personnellement Vizzklick à Gadgetzan de la défaite de Kovic. Il sera si heureux d\'apprendre sa mort qu\'il vous remettra sûrement une récompense.",
    ["O"] = "Parler à Vizzklick à Gadgetzan.",
    ["T"] = "Le Sous-marché",
  },
