    ["T"] = "Poisson rare : le tassergal à dos rayé",
  },
  [8227] = {
    ["D"] = "Au milieu d’un tas de lignes et de moulinets se trouve un mètre ruban en soie, auquel est attaché une étiquette :$B$B« Nat Pagle, marécage d’Âprefange ».",
    ["O"] = "Rendre le Mètre ruban de Nat à Nat Pagle, au marécage d’Âprefange.",
    ["T"] = "Le mètre ruban de Nat",
  },
  [8228] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Puis-je avoir un prospectus sur la pêche ?",
  },
  [8229] = {
    ["D"] = "",
