  [8409] = {
    ["D"] = "Les œufs ont été placés à l\'intérieur du tonneau. Les prochaines cuvées de bière de l\'auberge d\'Austrivage seront de toute évidence gâtées !$B$BVous devriez maintenant retourner voir la sombre imploratrice Yanka à la Fête de l\'homme d\'osier, et lui annoncer votre succès.",
    ["O"] = "Retournez voir la sombre imploratrice Yanka à la Fête de l\'homme d\'osier dans les clairières de Tirisfal.",
    ["T"] = "Des tonneaux gâtés",
  },
  [8410] = {
    ["D"] = "Bath’rah la Vigie des Vents a demandé qu’un chaman aussi sage que vous vienne le voir. Il demande que vous lui apportiez un échantillon de chacun des éléments, pour lui montrer que vous êtes $gprêt:prête;.$B$BPour trouver Bath’rah, allez jusqu’à la rivière à l’est de Moulin-de-Tarren. Suivez la rivière vers le nord, jusqu’à ce que vous arriviez aux ruines où vit Bath’rah.",
    ["O"] = "Rassembler un échantillon d\'air, de feu, de terre et d\'eau pour Bath\'rah la Vigie des Vents.",
    ["T"] = "La maîtrise des éléments",
  },
  [8411] = {
    ["D"] = "Bienvenue, $C. Apportez-moi les éléments et ensuite on causera.$B$BLe feu, l\'air, l\'eau et la terre qu\'il me faut avant de faire confiance, l\'$Gami:amie;. À c\'qui paraît, vous vous y connaissez.",
    ["O"] = "Apportez les éléments terre, air, feu et eau à Bath\'rah la Vigie des vents.",
    ["T"] = "Maîtriser les éléments",
  },
  [8412] = {
    ["D"] = "Les esprits élémentaires de cet endroit ne m’aiment plus.$B$BVous savez ce qu’il en est des totems mieux qu\'aucun autre et j’ai besoin d’un totem des esprits pour me protéger. Nous avons déjà les éléments, il nous faut à présent des choses plus en rapport avec la nature. Plus primitives.$B$BDans les Maleterres de l’ouest, tuez des ours pour prendre leurs griffes et des araignées pour leurs yeux. Les charognards mangent de tout, ils pourraient avoir l\'un ou l\'autre.",
    ["O"] = "Apportez les différents morceaux du Totem des esprits à Bath’rah la Vigie des Vents.",
    ["T"] = "Le totem des esprits",
  },
  [8413] = {
    ["D"] = "Le totem des esprits ne suffit pas. Nous allons avoir besoin de magie vaudou pour qu’il fonctionne correctement.$B$BLes trolls du temple englouti possèdent des plumes fortement imprégnées de magie vaudou. Allez là-bas et tuez Balafreur, Mijan, Zolo, Hukku, Zul\'lor et Loro. Nous aurons alors assez de magie pour un vrai totem des esprits.",
    ["O"] = "Apporter les Plumes vaudou à Bath\'rah la Vigie des vents.",
    ["T"] = "Vaudou !",
  },
  [8414] = {
    ["D"] = "Salutations, $C. Des croisés comme vous sont les bienvenus dans ces étendues désolées.$B$BVotre force sacrée est nécessaire pour nous aider à combattre les morts-vivants innombrables qui hantent cette région. Nous avons besoin de vous pour rassembler un grand nombre de pierres du Fléau et les apporter au grand prêtre Thel\'danis. Le grand prêtre garde la tombe d’Uther à la colline des Chagrins, non loin d’ici.$B$BSeule une personne détentrice d’un brevet de l’Aube d’argent peut espérer trouver des pierres du Fléau, il vaudrait donc mieux vous entretenir avec l’officier Purecarde, si vous ne l’avez pas déjà fait.",
    ["O"] = "Récupérez 20 Pierres du Fléau des serviteurs et apportez-les au grand prêtre Thel\'danis dans les Maleterres de l\'ouest.",
    ["T"] = "La dissipation du mal",
  },
  [8415] = {
    ["D"] = "Le commandant Ashlam Vaillepoing a demandé qu’un paladin de votre pureté et de votre valeur vienne le voir, $N. Vous pourrez le trouver au camp du Noroît, dans la partie méridionale des Maleterres de l’ouest. Allez le trouver et servez sa cause, au nom de la Lumière !",
    ["O"] = "Parler au commandant Ashlam Vaillepoing au Camp du Noroît.",
    ["T"] = "La pointe du Noroît",
  },
  [8416] = {
    ["D"] = "Le mal a été purgé des pierres, $N.$B$BJe connais les plans du commandant Ashlam, et il n’a besoin que d’une seule pierre inerte. Apportez-la lui pour que nous puissions plonger le Fléau dans le feu justicier.",
    ["O"] = "Apporter la Pierre du Fléau inerte au commandant Ashlam Vaillepoing à la pointe du Noroît.",
    ["T"] = "Pierres du Fléau inertes",
  },
  [8417] = {
    ["D"] = "Dans les Terres foudroyées se trouve un esprit troublé qui fut autrefois un grand héros de la Horde. L\'honneur d\'un guerrier ne meurt pas avec son corps. Vous feriez bien d\'aller lui parler.",
    ["O"] = "Parler au Héros de la Horde tombé au combat à l\'entrée des Terres foudroyées.",
    ["T"] = "Un esprit troublé",
  },
  [8418] = {
    ["D"] = "Nous devons à présent combiner les pierres du Fléau inertes et créer une arme capable de briser les rangs des morts-vivants. Ce ne sera guère une tâche facile, $n. Seuls les trolls du temple englouti détiennent la magie rare qui permet de retourner le mal contenu en une chose contre le mal lui-même.$b$bÀ l\'intérieur du temple, les trolls sont connus sous les noms de Balafreur, Mijan, Zolo, Hukku, Zul\'lor et Loro. N\'éprouvez pas de remords envers leurs âmes corrompues... Ce sont des êtres malfaisants.",
    ["O"] = "Apportez les plumes vaudou à Ashlam Vaillepoing.",
    ["T"] = "Forger la pierre de pouvoir",
  },
  [8419] = {
    ["D"] = "$N, un diablotin de Gangrebois nommé Démonet a exigé votre présence. Sa requête est assez spéciale, car il a demandé que vous lui apportiez un morceau de gangrétoffe en gage de « bonne volonté ». Vous êtes un démoniste déjà très puissant et je suppose que vous savez que les satyres de Gangrebois portent de cette étoffe.$B$BVous pourrez trouver Démonet juste au nord des chutes de la Vénéneuse qui, d’après ce que j’ai entendu dire, sont assez belles en cette période de l’année.",
    ["O"] = "Apportez un morceau de Gangrétoffe à Démonet à Gangrebois.",
    ["T"] = "La demande d\'un diablotin",
  },
  [8420] = {
    ["D"] = "Peut-être que cela vous semblera bizarre, venant d’un diablotin, mais j’aimerais bien avoir un familier rien qu’à moi. Vraiment. Ce n’est pas juste… Niby m’a moi, et moi, je n’ai personne !$B$BEn fait, il n’est même pas nécessaire qu’il soit vivant. Pour commencer à en fabriquer un, il me faudrait quelque chose comme de la gangrétoffe. Les satyres ici à Gangrebois en ont parfois, il suffirait donc que vous en tuiez assez jusqu\'à ce que vous en trouviez. Voilà qui ne devrait pas vous donner trop de mal...",
    ["O"] = "Apportez un morceau de gangrétoffe à Démonet à Gangrebois.",
    ["T"] = "Chaud et piquant",
  },
  [8421] = {
    ["D"] = "Maintenant que j’ai la gangrétoffe, je peux enfin commencer à fabriquer *mon* familier.$B$BMais… je crois que je vais avoir besoin d’yeux. Peut-être que vous pourriez arracher un peu de bois pourri aux gens d’Arbrefer, pour que je puisse lui sculpter des yeux adorables. Ils sont au nord, dans le bois d\'Arbrefer.$B$BJ’aurai aussi besoin d’essence de Vénéneuse pour lui donner cette nouvelle odeur démoniaque. Les limons corrompus en possèdent, et on en trouve juste à côté, près de Niby et moi.",
    ["O"] = "Apporter 10 morceaux de Bois pourri et 4 Essences de Vénéneuse à Démonet, en Gangrebois.",
    ["T"] = "L\'anti-étoffe des héros",
  },
  [8422] = {
    ["D"] = "Mon familier est pratiquement terminé. Il ne me manque plus que quelque chose pour le rembourrer.$B$BSeules les plumes vaudou des trolls du Temple englouti conviendront. Ces trolls s’appellent Balafreur, Mijan, Zolo, Hukku, Zul\'lor, et Loro. Tuez-les tous.$B$BJe sais que ce n’est pas une tâche facile, $N, alors je vous récompenserai en vous donnant un petit quelque chose de Niby… Il n’en a probablement plus besoin, maintenant.",
    ["O"] = "Apporter 6 Plumes vaudou des trolls du Temple englouti.",
    ["T"] = "Les trolls d\'un même plumage…",
  },
  [8423] = {
    ["D"] = "<Le Héros vous examine.>$b$bVous êtes $gun:une:c; $c, cela ne fait pas de doute. J\'aimerais vous confier une tâche honorable, mais je dois d\'abord éprouver votre force.$b$bOn dit que Rexxar mesurait le tranchant de ses lames en tuant un sanglier infernal d\'un seul coup rapide. Je vais à présent mesurer votre force en vous demandant d\'en tuer.",
    ["O"] = "Tuer 7 Sangliers infernaux dans les Terres foudroyées et revenir voir le Héros de la Horde tombé au combat.",
    ["T"] = "Fraternité entre guerriers",
  },
  [8424] = {
    ["D"] = "La présence continuelle des liges d’ombre dans les Terres foudroyées est pour moi une terrible source de souffrance. Leurs rituels et leurs sorts m\'infligent des tourments pires que tout ce que j\'ai pu connaître de mon vivant.$b$bÀ travers vous, je peux envoyer un message de douleur. Allez et faites-leur sentir mon courroux.",
    ["O"] = "Tuez les Liges d\'ombre dans les Terres foudroyées et revenez voir le Héros de la Horde tombé au combat.",
    ["T"] = "La guerre contre les liges d\'ombre",
  },
  [8425] = {
    ["D"] = "Votre honneur m’a apporté un peu de paix. Il est juste que je vous offre quelque chose en échange.$B$BMes armes et mon armure étaient splendides quand j’étais en vie, mais elles ne me sont d’aucune utilité aujourd’hui. Elles seraient bien mieux placées entre vos mains.$B$BRendez-vous au temple englouti et détruisez les trolls gardiens Balafreur, Mijan, Zolo, Hukku, Zul\'lor, et Loro. Ils possèdent des plumes magiques qui peuvent rematérialiser mon corps physique suffisamment longtemps pour que je vous accorde votre récompense.",
    ["O"] = "Apportez les Plumes vaudou des trolls du Temple englouti au Héros de la Horde tombé au combat.",
    ["T"] = "Plumes vaudou",
  },
  [8426] = {
    ["D"] = "La bataille dans le goulet des Chanteguerres contre les Sentinelles d\'Aile-argent est d\'une grande importance. Sous le prétexte de protéger une forêt qui ne lui appartient pas, l\'Alliance cherche à refuser à la Horde l\'une de ses plus grandes ressources en bois.$B$BNe la laissez pas faire, $N ! Revenez me voir quand vous aurez la preuve que vous avez bien servi la Horde !",
    ["O"] = "Apportez 3 Marques d\'honneur du goulet des Chanteguerres à l\'un des porteguerres de la Horde, devant les champs de bataille.",
    ["T"] = "La bataille du goulet des Chanteguerres",
  },
