  [8570] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Efforts passés dans le goulet des Warsong",
  },
  [8572] = {
    ["D"] = "Les insignes que vous récupérez à la fin de vos missions tactiques, logistiques et de combat pour le compte du Fort cénarien ne sont pas seulement destinés à faire joli, $N.$B$BConservez-les soigneusement. Au fur et à mesure que vous ferez la preuve de votre loyauté envers notre organisation, vous pourrez les échanger contre le matériel que nous réservons à nos alliés les plus proches.",
    ["O"] = "Ramener 7 Badges cénariens de combat, 4 Badges cénariens de logistique et 4 Badges cénariens de tactique à Vargus, au Fort cénarien en Silithus. Vous devrez aussi être $gHonoré:Honorée; par le Cercle cénarien pour terminer cette quête.",
    ["T"] = "La tenue de combat de vétéran",
  },
  [8573] = {
    ["D"] = "Les insignes que vous récupérez à la fin de vos missions tactiques, logistiques et de combat pour le compte du Fort cénarien ne sont pas seulement destinés à faire joli, $N.$B$BConservez-les soigneusement. Au fur et à mesure que vous ferez la preuve de votre loyauté envers notre organisation, vous pourrez les échanger contre le matériel que nous réservons à nos alliés les plus proches.",
    ["O"] = "Ramener 15 Badges cénariens de combat, 20 Badges cénariens de logistique, 20 Badges cénariens de tactique et 1 Marque de Cénarius à Vargus, au Fort cénarien en Silithus. Vous devrez aussi être $gExalté:Exaltée; par le Cercle cénarien pour terminer cette quête.",
    ["T"] = "Equipement de guerre de champion",
  },
  [8574] = {
    ["D"] = "Les insignes que vous récupérez à la fin de vos missions tactiques, logistiques et de combat pour le compte de l’Imploratrice céleste Corne-fière ne sont pas seulement destinés à faire joli, $N.$B$BConservez-les soigneusement. Au fur et à mesure que vous ferez la preuve de votre loyauté envers notre organisation, vous pourrez les échanger contre le matériel que nous réservons à nos alliés les plus proches.",
    ["O"] = "Ramener 15 Badges cénariens de combat, 20 Badges cénariens de logistique, 17 Badges cénariens de tactique et 1 Marque de Remulos à Vargus, au Fort cénarien en Silithus. Vous devrez aussi être $gRévéré:Révérée; par le Cercle cénarien pour terminer cette quête.",
    ["T"] = "Equipement de guerre de fidèle",
  },
  [8575] = {
    ["D"] = "Vous ne comprenez pas le moindre mot de ce qui est écrit dans ce livre. Vous ne savez même pas dans quelle langue il est rédigé !",
    ["O"] = "Apporter le Grand livre magique d\'Azuregos à Narain Divinambolesque, en Tanaris.",
    ["T"] = "Le grand livre magique d\'Azuregos",
  },
  [8576] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La traduction du grand livre",
  },
  [8577] = {
    ["D"] = "Si je dois traduire ces textes, je vais avoir besoin de mes lunettes de divination. Malheureusement, elles m’ont été volées il y a quelque temps par mon ex-meilleur ami pour la vie, Stewvul. Il m’a brisé le cœur en cinq morceaux et il m’a abandonné ici.$B$BÀ l’aide de ma fidèle boule de cristal, j’ai à peu près réussi à le localiser. Il se trouve soit au mur de Grisetête dans la forêt des Pins argentés, soit de ce qui ressemble à un monde portail en Outreterre, actuellement envahi par la Légion ardente. Vous allez devoir retrouver sa trace et me rapporter mes lunettes, $N !",
    ["O"] = "Narain Divinambolesque veut que vous retrouviez son ex-meilleur ami pour la vie (M.A.P.V.), Stewvul, et que vous repreniez les lunettes de divination que Stewvul lui a volé.",
    ["T"] = "Stewvul, ex-M.A.P.V.",
  },
  [8578] = {
    ["D"] = "<Vous entendez un soupir de soulagement s’échapper de la caisse.>$B$BVous voulez juste ses lunettes de divination ? Génial ! Y’a pas de problème… Laissez-moi juste une minute pour… Hé ! Où le… Cornegidouille !$B$BBon, ben… J’ai une mauvaise nouvelle… et une très mauvaise nouvelle. La mauvaise nouvelle est que j’ai perdu les lunettes. La très mauvaise est que je les ai perdues quand je me suis promené dans le Cœur du Magma le mois dernier. Ouais, le Cœur du Magma… J’suppose qu’on a fini de faire affaire, maintenant ! Salut !",
    ["O"] = "Retrouver les Lunettes de divination de Narain et les rapporter à Narain Divinambolesque en Tanaris.",
    ["T"] = "Des lunettes d\'observation ? Aucun problème !",
  },
  [8579] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Champions mortels",
  },
  [8580] = {
    ["D"] = "Hep, le $c ! Oui, vous ! Dites donc voir, la Horde a très beaucoup besoin d’un gros tas de fleur de feu pour cet effort de guerre d’Ahn’Qiraj. Pour les mettre dans des jolies choses qui font boum, p’têt bien. Ou p’têt pour filer aux chars à vapeurs de l’Alliance ? Qui sait ? Pas Pele’keiki, pour sûr !$B$BMais Pele’keiki sait un autre truc… que vous allez l’aider et lui ramener plein de fleurs de feu. Pas vrai ?",
    ["O"] = "Ramener 20 fleurs de feu au chevaucheur de chauve-souris Pele’keiki dans la Vallée des esprits d\'Orgrimmar.",
    ["T"] = "La Horde a besoin de fleur de feu !",
  },
  [8581] = {
    ["D"] = "Pele’keiki savait que vous reviendriez aider. Ramener encore plus plein de fleurs de feu et lui faire très plaisir. Pele’keiki sait faire de grosses bombes avec fleurs de feu. Alors, quand la guerre commencera, Pele’keiki prendra sa chauve-souris, s’envolera très haut et lâchera les bombes sur les insectes. Mais avant, $c, Pele’keiki a besoin de plus de fleurs de feu !$B$BVous ramenez des fleurs de feu à Pele’keiki encore plus fissa. Au moins autant que la dernière fois, et Pele’keiki sera très content.",
    ["O"] = "Pele’keiki savait que vous reviendriez aider. Ramener encore plus plein de fleurs de feu et lui faire très plaisir. Pele’keiki sait faire de grosses bombes avec fleurs de feu. Alors, quand la guerre commencera, Pele’keiki prendra sa chauve-souris, s’envolera très haut et lâchera les bombes sur les insectes. Mais avant, $c, Pele’keiki a besoin de plus de fleurs de feu !$B$BVous ramenez des fleurs de feu à Pele’keiki encore plus fissa. Au moins autant que la dernière fois, et Pele’keiki sera très content.",
    ["T"] = "La Horde a besoin de plus de fleur de feu !",
  },
  [8582] = {
    ["D"] = "Le lotus pourpre sert dans très nombreuses potions, élixirs et huiles, $c. Certaines nous seront utiles à Ahn’Qiraj, et nous allons en fabriquer des quantités gigantesques pour l’effort de guerre. Je compte aussi procéder à des expériences pour trouver de nouveaux usages à cette plante. Pour cela, il m’en faut de grandes quantités.$B$BC’est là que vous intervenez. Allez sur le terrain, et ramassez tout le lotus pourpre que vous pourrez. Cette proposition vous convient-elle ?",
    ["O"] = "Ramener 20 lotus pourpres à l’apothicaire Jezel dans la Vallée des esprits d\'Orgrimmar.",
    ["T"] = "La Horde a besoin de lotus pourpre !",
  },
  [8583] = {
    ["D"] = "Il semble que nous ayons effectivement besoin de davantage de lotus pourpre, $c. Mes propres recherches n’ont pas encore porté leurs fruits, mais nous pouvons toujours nous rabattre sur des techniques connues et éprouvées.$B$BJ’ai besoin que vous retourniez sur le terrain et que vous récoltiez au moins vingt échantillons de lotus pourpre. Je vous attends ici.",
    ["O"] = "Il semble que nous ayons effectivement besoin de davantage de lotus pourpre, $c. Mes propres recherches n’ont pas encore porté leurs fruits, mais nous pouvons toujours nous rabattre sur des techniques connues et éprouvées.$B$BJ’ai besoin que vous retourniez sur le terrain et que vous récoltiez au moins vingt échantillons de lotus pourpre. Je vous attends ici.",
    ["T"] = "La Horde a besoin de plus de lotus pourpre !",
  },
  [8584] = {
    ["D"] = "Vous devez savoir quelque chose de très important à mon sujet : on ne parle JAMAIS boulot avec moi ! Je m’occupe de choses que vous êtes tout bonnement incapable de comprendre. Du genre : savez-vous ce qu’est un dégobulloneur pyschotronique ? Non, hein ? C’est pour ça que je m’occupe de tracer des plans, et que vous faites les courses.$B$BBon, comme je le disais, pour que ça marche, il nous faut un poulet de 500 livres.$B$BAllez à Gadgetzan et parlez à Dirge Hachillico, il vous mettra sur la piste d’un spécimen convenable.",
    ["O"] = "Narain Divinambolesque de Tanaris veut que vous parliez à Dirge Hachillico à Gadgetzan.",
    ["T"] = "On ne parle jamais de mon boulot",
  },
  [8585] = {
    ["D"] = "D’après les légendes, les chimaeroks de l’île de l’effroi, en Féralas, ont la chair la plus succulente qui soit au monde ! Or, il se trouve que leur papa, convenablement préparé, pourrait bien ressembler à un poulet de 500 livres – ou pas.$B$BAllez sur l’île de l’effroi, tuez le seigneur Lakmaeran et ramenez-moi sa carcasse. Ça, c’est pour le gnome. Quant à moi, je veux quelques filets de chimaeroks.$B$BQuand vous aurez fini, vous aurez un poulet de 500 livres et un petit extra.",
    ["O"] = "Récupérer la Carcasse de Lakmaeran et 20 Filets de chimaerok pour Dirge Hachillico, en Tanaris.",
    ["T"] = "L\'île de l\'effroi !",
  },
  [8586] = {
    ["D"] = "Ma recette est au point ! Je m’occupe des épices, et vous m’avez déjà fourni la viande. J’ai juste besoin de carburant pour fusée et de sel de Fonderoc.$B$BLorsque vous m’aurez ramené tout ça, le poulet sera prêt pour Narain.",
    ["O"] = "Dirge Hachillico, de Gadgetzan, veut que vous lui rameniez 20 doses de Carburant de fusée gobelin et 20 doses de Sel de Fonderoc.",
    ["T"] = "Pyro-côtelettes de chimaerok à la Dirge",
  },
  [8587] = {
    ["D"] = "Je suis un gobelin de parole, $gmon petit:ma petite;. Voilà votre poulet de 500 livres.$B$BQuant à ce qu’un gnome veut faire d’un poulet de 500 livres, je suppose que je ne le saurai jamais…$B$B<Dirge bougonne quelque chose d’inintelligible…>$B$BDormir dedans, sans doute… Gnomes dégoûtants…",
    ["O"] = "Remettre le Poulet de 500 livres à Narain Divinambolesque à Tanaris.",
    ["T"] = "Retour vers Narain",
  },
  [8588] = {
    ["D"] = "Tant qu’à se préparer pour la guerre, mieux vaut avoir tout ce qu’il faut avant, n’est-ce pas $gl’ami:ma grande; ? Le cuir lourd est l’une des choses dont nous allons avoir le plus besoin pendant nos préparatifs. Nous allons l’utiliser dans une foule de domaines, pour fabriquer des armures, pour les renforcer… et tout bêtement pour en faire des bâches.$B$BAutant dire qu’il nous en faut beaucoup. Et ça, ce sera votre job, $C. Aiguisez votre couteau et ramenez-m’en un paquet. Nous continuerons cette discussion après.",
    ["O"] = "Ramener 10 cuirs lourds à l’Ecorcheuse Jamani dans la Vallée des esprits d\'Orgrimmar.",
    ["T"] = "La Horde a besoin de cuir lourd !",
  },
  [8589] = {
    ["D"] = "Effectivement, $c, il me faut encore du cuir lourd. Les requêtes des généraux et des intendants s’accumulent à toute vitesse. Et ils sont modestes par rapport à ce que demandent les maîtres des zeppelins !$B$BJ’ai besoin que mon quota soit collecté très vite. $n, ramenez-moi de nouvelles piles de cuir épais aussi vite que possible !",
    ["O"] = "Effectivement, $c, il me faut encore du cuir lourd. Les requêtes des généraux et des intendants s’accumulent à toute vitesse. Et ils sont modestes par rapport à ce que demandent les maîtres des zeppelins !$B$BJ’ai besoin que mon quota soit collecté très vite. $n, ramenez-moi de nouvelles piles de cuir épais aussi vite que possible !",
    ["T"] = "La Horde a besoin de plus de cuir lourd !",
  },
  [8590] = {
    ["D"] = "Je suis contente que vous ayez décidé de venir me voir, $c. On dirait bien que les préparatifs de la Horde pour la guerre d’Ahn’Qiraj atteignent leur point culminant. Mais nous devons encore jouer notre rôle, vous et moi.$B$BJ’ai été chargée de collecter un grand nombre de morceaux de cuir épais, qui servira à fabriquer du matériel. J’ai besoin que vous alliez m’en ramasser. Vous y gagnerez de l’honneur et la gratitude de la Horde. Je compte sur vous ?",
    ["O"] = "Ramener 10 morceaux de cuir épais au sergent Umala dans la Vallée des esprits d\'Orgrimmar.",
    ["T"] = "La Horde a besoin de cuir épais !",
  },
  [8591] = {
    ["D"] = "Comme vous pouvez le voir, il nous davantage de cuir épais, $c. Je vous demande votre aide une fois encore. Je vous promets que si vous venez à bout de cette tâche, vos efforts seront reconnus.$B$BLe temps presse ! Revenez vite me voir avec du cuir épais ! Plus tôt vous reviendrez, plus tôt nos préparatifs seront terminés, et plus tôt nous pourrons partir en guerre, héros !",
    ["O"] = "Comme vous pouvez le voir, il nous davantage de cuir épais, $c. Je vous demande votre aide une fois encore. Je vous promets que si vous venez à bout de cette tâche, vos efforts seront reconnus.$B$BLe temps presse ! Revenez vite me voir avec du cuir épais ! Plus tôt vous reviendrez, plus tôt nos préparatifs seront terminés, et plus tôt nous pourrons partir en guerre, héros !",
    ["T"] = "La Horde a besoin de plus de cuir épais !",
  },
  [8592] = {
    ["D"] = "Les Empereurs jumeaux… Leurs murmures incessants usent peu à peu ma santé mentale. Ils se moquent de moi. Ils savent que je ne peux pas pénétrer plus avant dans leur domaine pour sauver mon frère Arygos.$B$BDans mes visions, leurs visages monstrueux sont surmontés d\'impressionnantes couronnes. Mais j\'aurai ma revanche… Je transformerai ces symboles de la puissance des Qiraji en quelque chose de plus adapté à notre lutte.$B$BRamenez-moi les composants dont j\'ai besoin, $N, et je vous donnerai une coiffure sans égale.",
    ["O"] = "Ramener le Diadème de Vek\'Nilash, 2 Idoles du sage, 5 Scarabées d\'argent et 5 Scarabées d\'os à Andorgos, à Ahn\'Qiraj. Pour terminer cette quête, il faut également que la Progéniture de Nozdormu soit au moins Amicale à votre égard.",
    ["T"] = "La tiare de l\'Oracle",
  },
  [8593] = {
    ["D"] = "Vous souhaitez prouver votre valeur aux yeux de mon espèce, $gmortel:mortelle; ? Très bien, peut-être pouvons-nous nous aider mutuellement.$B$BIl existe un très ancien ver des sables d’une puissance légendaire, nommé Ouro. On dit qu’il a été créé par les Dieux très anciens eux-mêmes pour parodier la vie.$B$BSa peau n’a rien de naturel… épaisse et presque impossible à transpercer, mais souple et lui permettant de se déplacer aussi vite et gracieusement que des créatures beaucoup plus petites.$B$BRamenez-moi un échantillon de cette peau et quelques autres composants mineurs, et je vous fabriquerai des chausses d’un grand pouvoir.",
    ["O"] = "Ramener la Peau du grand ver des sables, 2 Idoles de la renaissance, 5 Scarabées d\'or et 5 Scarabées d\'argile à Kandrostrasz, à l\'intérieur d\'Ahn\'Qiraj. Pour terminer cette quête, il faut aussi que la Progéniture de Nozdormu soit au moins Amicale à votre égard.",
    ["T"] = "Les chausses de l\'Oracle",
  },
  [8594] = {
    ["D"] = "Nous pensons qu\'à l\'origine, les épaulières ornées que portent les races mortelles étaient une tentative pour imiter les ailes des dragons.$B$BRamenez-moi les manchettes portées par les plus hauts dignitaires qiraji, et je les façonnerai pour en faire des espauliers plus redoutables que les ailes de Nefarian lui-même !",
    ["O"] = "Ramener des Manchettes de commandement qiraji, 2 Idoles de la renaissance, 5 Scarabées d\'argent et 5 Scarabées d\'ivoire à Andorgos, à Ahn\'Qiraj. Pour terminer cette quête, il faut aussi que la Progéniture de Nozdormu soit au moins Neutre à votre égard.",
    ["T"] = "Le mantelet de l\'Oracle",
  },
  [8595] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Champions mortels",
  },
  [8596] = {
    ["D"] = "La magie qiraji est puissante et ancienne. Les Qiraji les plus redoutables manient des objets si terribles qu’ils feraient hésiter un dragon. Si vous me ramenez des manchettes de commandement qiraji et quelques autres composants mineurs, je vous fabriquerai des bottillons exceptionnels.$B$BIls vous seront très utiles pour affronter l’ennemi.",
    ["O"] = "Ramener des Manchettes de commandement qiraji, 2 Idoles de la mort, 5 Scarabées en bronze et 5 Scarabées d’or à Kandrostrasz, à Ahn\'Qiraj. Pour terminer cette quête, il faut aussi que la Progéniture de Nozdormu soit au moins Neutre à votre égard.",
    ["T"] = "Les bottillons de l\'Oracle",
  },
  [8597] = {
    ["D"] = "Je savais bien que ce livre finirait par me servir ! Heureusement, je suis prévoyant, et j’ai eu le bon sens de le ranger dans un endroit sûr.$B$BNe vous en faites pas, là où il est, personne ne risque de le trouver ! La première fois, il m’a fallu un gyrocoptère pour y aller. Malheureusement, au retour, je me suis écrasé sur la plage. C’est grâce à cet accident que j’ai gagné mes super-pouvoirs psychiques gnomes !$B$BAllez à la plage du Bout-du-Monde, cherchez l’épave de mon gyrocoptère, et nagez vers le sud ! Vous ne pouvez rater l’endroit !",
    ["O"] = "Retrouver le livre de Narain Divinambolesque, enfoui sur une île des mers du Sud.",
    ["T"] = "Le draconique pour les nuls",
  },
  [8598] = {
    ["D"] = "<Ce texte est composé de caractères d’imprimerie collés sur un parchemin.>$B$BNous tenons ton livre bien-aimé, gnome. Si tu veux le revoir, tu vas faire tout ce que nous demandons.$B$B<Suivent trois pages d’instructions détaillées.>$B$BAmène l’argent et viens seul !",
    ["O"] = "Ramener la Demande de rançon a Narain Divinambolesque, en Tanaris.",
    ["T"] = "rANçOn !",
  },
  [8599] = {
    ["D"] = "J’ai très rarement des visiteurs. En fait, je n’avais vu personne depuis la fois où j’ai sauvé ce cher, cher gnome de l’épave de sa machine volante.$B$B<Meredith se plonge dans ses souvenirs, et son regard se perd dans le lointain.>$B$BLe connaissez-vous ? Narain ? Narain Divinambolesque ? J’aimerais beaucoup que quelqu’un lui porte un message. Il faut qu’il sache ce que je ressens pour lui ! C’est de l’amour ! J’en suis sûre !$B$BPourriez-vous me servir de messager ? Je vous en serai éternellement reconnaissante !",
    ["O"] = "Transmettre la Lettre d’amour de Meredith à Narain Divinambolesque, en Tanaris.",
    ["T"] = "Une chanson d\'amour pour Narain",
  },
  [8600] = {
    ["D"] = "A mon avis, la manière dont vous tenez le couteau est plus importante que son tranchant. Même très émoussée, une lame peut toujours couper si vous appuyez suffisamment. Mais nous n’avons pas ce problème, vous et moi, car nous connaissons la valeur d’une lame aiguisée, n’est-ce pas, $c ?$B$BOh, aucune importance. J’ai du travail pour vous. Pour servir la Horde, et pour jouer votre rôle dans l’effort de guerre d’Ahn’Qiraj, j’ai besoin que vous me rameniez un certain nombre de morceaux de cuir robuste. Est-ce dans vos cordes ?",
    ["O"] = "Ramener 10 morceaux de cuir robuste au docteur Serratus dans la Vallée des esprits d\'Orgrimmar.",
    ["T"] = "La Horde a besoin de cuir robuste !",
  },
  [8601] = {
    ["D"] = "Nos préparatifs pour la future guerre d’Ahn’Qiraj se poursuivent, $c. Toutefois, afin que nous ne prenions pas de retard, ou au moins pour faire en sorte que le retard ne vienne pas de moi, j’aimerai que vous me rameniez une autre pile de morceaux de cuir robuste. Votre promptitude sera dûment appréciée, $n",
    ["O"] = "Nos préparatifs pour la future guerre d’Ahn’Qiraj se poursuivent, $c. Toutefois, afin que nous ne prenions pas de retard, ou au moins pour faire en sorte que le retard ne vienne pas de moi, j’aimerai que vous me rameniez une autre pile de morceaux de cuir robuste. Votre promptitude sera dûment appréciée, $n",
    ["T"] = "La Horde a besoin de plus de cuir robuste !",
  },
  [8602] = {
    ["D"] = "Nous pensons qu\'à l\'origine, les épaulières ornées que portent les races mortelles étaient une tentative pour imiter les ailes des dragons.$B$BRamenez-moi les manchettes portées par les plus hauts dignitaires qiraji, et je les façonnerai pour en faire des espauliers plus redoutables que les ailes de Nefarian lui-même !",
    ["O"] = "Rapporter des Manchettes de domination qiraji, 2 Idoles de la vie, 5 Scarabées d\'or et 5 Scarabées de cristal à Andorgos, à Ahn\'Qiraj. Pour terminer cette quête, il faut aussi que la Progéniture de Nozdormu soit au moins Neutre à votre égard.",
    ["T"] = "Les espauliers de l\'implorateur des tempêtes",
  },
  [8603] = {
    ["D"] = "L\'indicible Dieu très ancien se réveille lentement de son sommeil sous Ahn\'Qiraj. Le destin atroce qui attend Azeroth peut s\'abattre sur nous dans des jours, ou des années, car le temps n\'a pas vraiment de sens pour lui.$B$BMais lorsque les étoiles seront propices, il se déchaînera, et son effroyable courroux s\'abattra sur toutes les créatures vivantes. Trouvez-le avant qu\'il ne soit trop tard, et mettez un terme à son existence, d\'une manière ou d\'une autre.$B$BSi vous y parvenez, ramenez-moi un morceau de sa carcasse, et je vous en ferai une armure qui n\'a encore jamais été portée par aucun mortel… une armure digne $gd\'un tueur:d\'une tueuse; de dieu !",
    ["O"] = "Ramemer la Carcasse du Dieu très ancien, 2 Idoles de la mort, 5 Scarabées de pierre et 5 Scarabées de cristal à Vethsera, à l\'intérieur d\'Ahn\'Qiraj. Pour terminer cette quête, vous devez être $gHonoré:Honorée; par la Progéniture de Nozdormu.",
    ["T"] = "Les habits de l\'Oracle",
  },
  [8604] = {
    ["D"] = "Je vous salue, $c. Je coordonne les efforts de la Horde pour collecter des bandages en laine, en préparation de la toute prochaine guerre à Ahn’Qiraj. Une fois sur le terrain, nos soldats n’ont pas toujours le luxe d’attendre l’intervention d’un guérisseur pour rester en vie. Croyez-moi, je sais de quoi je parle. Nous leur préparons donc  toutes ces trousses de premiers soins.$B$BSi vous avez le temps, sachez que j’ai besoin de nombreux bandages en laine. Si vous m’en ramenez, je veillerai à ce que vous soyez $grécompensé:récompensée;.",
    ["O"] = "Ramenez 20 bandages en laine au guérisseur Longrunner dans la Vallée des esprits d\'Orgrimmar.",
    ["T"] = "La Horde a besoin de bandages en laine !",
  },
  [8605] = {
    ["D"] = "C’est bon de vous revoir, $c. Nous continuons à préparer l’effort de guerre, et j’ai toujours besoin de l’assistance que vous pourriez m’apporter dans la collecte de bandages en laine. Toute aide, même minime, sera grandement appréciée. J’espère que nos efforts seront suffisants. J’ai déjà vu trop d’âmes nobles disparaître sur les champs de bataille. Je ferai l’impossible pour que cela ne se produise plus.",
    ["O"] = "C’est bon de vous revoir, $c. Nous continuons à préparer l’effort de guerre, et j’ai toujours besoin de l’assistance que vous pourriez m’apporter dans la collecte de bandages en laine. Toute aide, même minime, sera grandement appréciée. J’espère que nos efforts seront suffisants. J’ai déjà vu trop d’âmes nobles disparaître sur les champs de bataille. Je ferai l’impossible pour que cela ne se produise plus.",
    ["T"] = "La Horde a besoin de plus de bandages en laine !",
  },
  [8606] = {
    ["D"] = "Eurêka ! Je sais ce que nous allons faire. Je vais remplir un sac de pierres, et vous allez vous charger de le remettre. Je sais que je suis censé le faire moi-même… et bien c’est ce qui va se passer. En quelque sorte.$B$B<Narain vous examine des pieds à la tête.>$B$BBon bon bon, ça ira. Prenez cette robe, ce turban et ce sac de cailloux, et filez au lieu du rendez-vous, au Berceau-de-l’hiver.$B$BJ’ai mis tout ce dont vous avez besoin dans cette panoplie spéciale. Lorsqu’ils se montreront pour récupérer l’argent… crac, vous leur tombez dessus ! Ça leur apprendra à s’en prendre à Narain !",
    ["O"] = "Narain Divinambolesque, en Tanaris, veut que vous vous rendiez au Berceau-de-l’hiver et que vous posiez le Sac d\'or à l’endroit indiqué par les voleurs de livres.",
    ["T"] = "Un leurre !",
  },
  [8607] = {
    ["D"] = "J’imagine que si vous venez me voir, c’est parce que vous comptez m’aider dans ma collecte ? Très bien. Il faut que vous me rameniez un grand nombre de bandages en tisse-mage. Utilisez tous les moyens, je dis bien tous les moyens, pour vous les procurer. Il serait préférable qu’ils soient frais, mais ce n’est pas indispensable. Ramenez-les-moi ici, $c, et je veillerai à ce que vous soyez $grécompensé:récompensée; comme il convient.",
    ["O"] = "Ramenez 20 bandages en tisse-mage à dame Callow dans la Vallée des esprits d\'Orgrimmar.",
    ["T"] = "La Horde a besoin de bandages en tisse-mage !",
  },
  [8608] = {
    ["D"] = "Vous revoilà ? Intéressant. Comme vous pouvez le voir, je travaille toujours à ma collecte pour “l’effort de guerre” à Ahn’Qiraj. Autrement dit, j’ai besoin que vous alliez me chercher un autre paquet de bandages en tisse-mage. J’attendrai votre retour avec impatience, $c.",
    ["O"] = "Vous revoilà ? Intéressant. Comme vous pouvez le voir, je travaille toujours à ma collecte pour “l’effort de guerre” à Ahn’Qiraj. Autrement dit, j’ai besoin que vous alliez me chercher un autre paquet de bandages en tisse-mage. J’attendrai votre retour avec impatience, $c.",
    ["T"] = "La Horde a besoin de plus de bandages en tisse-mage !",
  },
  [8609] = {
    ["D"] = "On pourrait croire que la Horde et l’Alliance se préparent à s’étriper pour Ahn’Qiraj. Mais en réalité, l’affrontement ne les opposera pas. Elles ont des ennemis communs, les silithides... et la force qui les contrôle. Les deux camps font de leur mieux pour récolter les ressources nécessaires à une guerre victorieuse.$B$BJe collecte des bandages en étoffe runique pour les distribuer aux soldats. Mais sans aide - sans votre aide, je n’y arriverai pas. Seriez-vous prêt à m’aider, $C ?",
    ["O"] = "Ramener 20 bandages en étoffe runique à la Garde de pierre Clayhoof dans la Vallée des esprits d\'Orgrimmar.",
    ["T"] = "La Horde a besoin de bandages en étoffe runique !",
  },
  [8610] = {
    ["D"] = "Chaque instant qui passe nous rapproche d’un embrasement généralisé à Ahn’Qiraj, $c. Mais auparavant, nous devons rassembler nos forces et nous préparer afin de survivre à l’offensive des insectes et de leurs maîtres.$B$BSi vous repassez dans la région dans un tout proche avenir, n’hésitez pas à contribuer à nouveau à l’effort de guerre en m’apportant des bandages en étoffe runique.",
    ["O"] = "Chaque instant qui passe nous rapproche d’un embrasement généralisé à Ahn’Qiraj, $c. Mais auparavant, nous devons rassembler nos forces et nous préparer afin de survivre à l’offensive des insectes et de leurs maîtres.$B$BSi vous repassez dans la région dans un tout proche avenir, n’hésitez pas à contribuer à nouveau à l’effort de guerre en m’apportant des bandages en étoffe runique.",
    ["T"] = "La Horde a besoin de plus de bandages en étoffe runique !",
  },
  [8611] = {
    ["D"] = "Je n’suis pas là pour bronzer, $c. Nous avons une guerre à préparer ! Si nous voulons écraser ces insectes à Ahn’Qiraj, nous devons être prêts, nourriture comprise !$B$BIls m’ont demandé de collecter et de saler des steaks de loup pour les soldats. Mais comme j’suis pas mal occupée ici, j’vous demande de partir, de m’tuer des loups, d’faire cuire leur viande et de m’ramener les steaks. Compris ?",
    ["O"] = "Ramener 20 steaks de loup à la garde de sang Rawtar dans la Vallée des esprits d\'Orgrimmar.",
    ["T"] = "La Horde a besoin de steaks de loup !",
  },
  [8612] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La Horde a besoin de plus de steaks de loup !",
  },
  [8613] = {
    ["D"] = "Ça va être une sacrée bonne journée pour pêcher, s’pas $gl’ami:ma grande ? Et c’est une sacrée bonne chose, passqu’on dirait ben que j’vais avoir besoin de plein de poiscaille ! La Horde, elle m’a demandé d’me charger de la collecte de tonnes et de tonnes de jaunes-queues tachetés ! Y paraît qu’elle va en avoir besoin pour la guerre qu’elle prépare à Ahn’Qiraj. Moi, j’y ai dit qu’j’aillais faire d’mon mieux. Mais j’vais avoir besoin d’aide. Vous allez m’aider, $c ?",
    ["O"] = "Ramener 20 jaunes-queues tachetés au pêcheur Lin’do dans la Vallée des esprits d\'Orgrimmar.",
    ["T"] = "La Horde a besoin de jaunes-queues tachetés !",
  },
  [8614] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La Horde a besoin de plus de jaunes-queues tachetés !",
  },
  [8615] = {
    ["D"] = "L’effort de guerre pour Ahn’Qiraj est une entreprise colossale, $c. Tout ce qui est collecté ici est de la plus haute importance, qu’il s’agisse de métal pour les armes ou de nourriture. Car lorsque vous n’avez pas mangé depuis plusieurs jours et que vous êtes faible comme un nouveau-né, votre hache ne vous sert plus à grand-chose.$B$BEt donc, nous collectons autant de nourriture que possible. Je m’occupe du pain de saumon, un plat à la fois nourrissant et facile à conserver. M’aiderez-vous dans cette tâche, $c ?",
    ["O"] = "Ramener 20 pains de saumon au chef Sharpclaw dans la Vallée des esprits d\'Orgrimmar.",
    ["T"] = "La Horde a besoin de pains de saumon !",
  },
  [8616] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La Horde a besoin de plus de pains de saumon !",
  },
  [8617] = {
    ["D"] = "The time has come for you to contribute to the Ahn\'Qiraj war effort, $c. Gone are the halcyon days of whiling away your time in leisure. Now it is time to serve me and the Horde, and I assure you that I am not as forgiving as the Warchief.$B$BYou will gather your forces, a guild if you have one, and invade the Molten Core. Inside you will find that the denizens of the place have singed corestones. Bring these back to me in large quantities.",
    ["O"] = "Bring 50 singed corestones to General Zog in Durotar.",
    ["T"] = "The Horde Needs Singed Corestones!",
  },
  [8619] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "L\'Ancien Morndeep",
  },
  [8620] = {
    ["D"] = "J’aurais dû m’en douter ! Mon ennemi juré, le docteur Dwenfer, a récidivé ! Et maintenant, à cause de votre échec au Berceau-de-l’hiver, le docteur Dwenfer a détruit mon livre ! Comment allez-vous sauver le monde, maintenant ?$B$BD’après le message que j’ai reçu, l’unique exemplaire connu du \"Draconique pour les nuls, volume II\" a été divisé en huit morceaux et dispersé aux quatre vents ! Si par miracle vous parvenez à les récupérer, utilisez cette reliure magique pour les réunir et ramenez-les-moi.",
    ["O"] = "Retrouver les 8 chapitres perdus du \"Draconique pour les nuls\" et les combiner avec la Reliure magique, puis ramener l’exemplaire réparé du \"Draconique pour les nuls, volume II\" à Narain Divinambolesque, en Tanaris.",
    ["T"] = "Le seul remède",
  },
  [8621] = {
    ["D"] = "La magie qiraji est puissante et ancienne. Les Qiraji les plus redoutables manient des objets si terribles qu\'ils feraient hésiter un dragon. Si vous me ramenez des manchettes de domination qiraji et quelques autres composants mineurs, je vous fabriquerai des bottes exceptionnelles.$B$BVous les trouverez utiles pour affronter l\'ennemi.",
    ["O"] = "Rapporter des Manchettes de domination qiraji, 2 Idoles du sage, 5 Scarabées en bronze et 5 Scarabées d\'argile à Kandrostrasz, à Ahn\'Qiraj. Pour terminer cette quête, il faut aussi que la Progéniture de Nozdormu soit au moins Neutre à votre égard.",
    ["T"] = "Les bottes d\'implorateur de tempête",
  },
  [8622] = {
    ["D"] = "L\'indicible Dieu très ancien se réveille lentement de son sommeil sous Ahn\'Qiraj. Le destin atroce qui attend Azeroth peut s\'abattre sur nous dans des jours, ou des années, car le temps n\'a pas vraiment de sens pour lui.$B$BMais lorsque les étoiles seront propices, il se déchaînera, et son effroyable courroux s\'abattra sur toutes les créatures vivantes. Trouvez-le avant qu\'il ne soit trop tard, et mettez un terme à son existence, d\'une manière ou d\'une autre.$B$BSi vous y parvenez, ramenez-moi un morceau de sa carcasse, et je vous en ferai une armure qui n\'a encore jamais été portée par aucun mortel… une armure digne $gd\'un tueur:d\'une tueuse; de dieu !",
    ["O"] = "Apporter la Carapace du Dieu très ancien, 2 Idoles du sage, 5 Scarabées d\'argent et 5 Scarabées d\'os à Vethsera à l\'intérieur d\'Ahn\'Qiraj. Pour terminer cette quête, vous devez être $gHonoré:Honorée; par la Progéniture de Nozdormu.",
    ["T"] = "Le haubert d\'implorateur de tempête",
  },
  [8623] = {
    ["D"] = "Les Empereurs jumeaux… Leurs murmures incessants usent peu à peu ma santé mentale. Ils se moquent de moi. Ils savent que je ne peux pas pénétrer plus avant dans leur domaine pour sauver mon frère Arygos.$B$BDans mes visions, leurs visages monstrueux sont surmontés d’impressionnantes couronnes. Mais j’aurai ma revanche… Je transformerai ces symboles de la puissance des Qiraji en quelque chose de plus adapté à notre lutte.$B$BRamenez-moi les composants dont j’ai besoin, $N, et je vous donnerai une coiffure sans égale.",
    ["O"] = "Apporter le Diadème de Vek\'lor, 2 Idoles de la renaissance, 5 Scarabées de pierre et 5 Scarabées de cristal à Andorgos à Ahn\'Qiraj. Pour terminer cette quête, il faut aussi que la Progéniture de Nozdormu soit au moins Amicale à votre égard.",
    ["T"] = "Le diadème d\'implorateur de tempête",
  },
  [8624] = {
    ["D"] = "Vous souhaitez prouver votre valeur aux yeux de mon espèce, $gmortel:mortelle; ? Très bien, peut-être pouvons-nous nous aider mutuellement.$B$BIl existe un très ancien ver des sables d\'une puissance légendaire, nommé Ouro. On dit qu\'il a été créé par les Dieux très anciens eux-mêmes pour parodier la vie.$B$BSa peau n\'a rien de naturel… épaisse et presque impossible à transpercer, mais souple et lui permettant de se déplacer aussi vite et gracieusement que des créatures beaucoup plus petites.$B$BRamenez-moi un échantillon de cette peau et quelques autres composants mineurs, et je vous fabriquerai des jambières d\'un grand pouvoir.",
    ["O"] = "Rapporter la Peau du Grand ver des sables, 2 Idoles de la lutte, 5 Scarabées en bronze et 5 Scarabées d\'ivoire à Kandrostrasz, à Ahn\'Qiraj. Pour terminer cette quête, il faut aussi que la Progéniture de Nozdormu soit au moins Amicale à votre égard.",
    ["T"] = "Les jambières d\'implorateur de tempête",
  },
  [8625] = {
    ["D"] = "Nous pensons qu\'à l\'origine, les épaulières ornées que portent les races mortelles étaient une tentative pour imiter les ailes des dragons.$B$BRamenez-moi les manchettes portées par les plus hauts dignitaires qiraji, et je les façonnerai pour en faire des espauliers plus redoutables que les ailes de Nefarian lui-même !",
    ["O"] = "Rapporter des Manchettes de commandement qiraji, 2 Idoles de la renaissance, 5 Scarabées d\'argent et 5 Scarabées d\'ivoire à Andorgos, à Ahn\'Qiraj. Pour terminer cette quête, il faut aussi que la Progéniture de Nozdormu soit au moins Neutre à votre égard.",
    ["T"] = "Les protège-épaules de l\'énigme",
  },
  [8626] = {
    ["D"] = "La magie qiraji est puissante et ancienne. Les Qiraji les plus redoutables manient des objets si terribles qu’ils feraient hésiter un dragon.$B$BSi vous me ramenez des manchettes de commandement qiraji et quelques autres composants mineurs, je vous fabriquerai des protège-tibias exceptionnels.$B$BIls vous seront utiles pour affronter l’ennemi.",
    ["O"] = "Rapporter des Manchettes de commandement qiraji, 2 Idoles de la vie, 5 Scarabées de pierre et 5 Scarabées d’os à Kandrostrasz, à Ahn\'Qiraj. Pour terminer cette quête, il faut aussi que la Progéniture de Nozdormu soit au moins Neutre à votre égard.",
    ["T"] = "Les protège-tibias du Frappeur",
  },
  [8627] = {
    ["D"] = "L’indicible Dieu très ancien se réveille lentement de son sommeil sous Ahn’Qiraj. Le destin atroce qui attend Azeroth peut s’abattre sur nous dans des jours, ou des années, car le temps n’a pas vraiment de sens pour lui.$B$BMais lorsque les étoiles seront propices, il se déchaînera, et son effroyable courroux s’abattra sur toutes les créatures vivantes. Trouvez-le avant qu’il ne soit trop tard, et mettez un terme à son existence, d’une manière ou d’une autre.$B$BSi vous y parvenez, ramenez-moi un morceau de sa carcasse, et je vous en ferai une armure qui n’a encore jamais été portée par aucun mortel… une armure digne $gd’un tueur:d’une tueuse; de dieu !",
    ["O"] = "Apporter la Carapace du Dieu très ancien, 2 Idoles du sage, 5 Scarabées d\'argent et 5 Scarabées d\'os à Vethsera à l\'intérieur d\'Ahn\'Qiraj. Pour terminer cette quête, vous devez être $gHonoré:Honorée; par la Progéniture de Nozdormu.",
    ["T"] = "La cuirasse du Vengeur",
  },
  [8628] = {
    ["D"] = "Les Empereurs jumeaux… Leurs murmures incessants usent peu à peu ma santé mentale. Ils se moquent de moi. Ils savent que je ne peux pas pénétrer plus avant dans leur domaine pour sauver mon frère Arygos.$B$BDans mes visions, leurs visages monstrueux sont surmontés d’impressionnantes couronnes. Mais j’aurai ma revanche… Je transformerai ces symboles de la puissance des Qiraji en quelque chose de plus adapté à notre lutte.$B$BRamenez-moi les composants dont j’ai besoin, $N, et je vous donnerai une coiffure sans égale.",
    ["O"] = "Apporter le Diadème de Vek\'lor, 2 Idoles de la renaissance, 5 Scarabées de pierre et 5 Scarabées de cristal à Andorgos à Ahn\'Qiraj. Pour terminer cette quête, il faut aussi que la Progéniture de Nozdormu soit au moins Amicale à votre égard.",
    ["T"] = "La couronne du Vengeur",
  },
  [8629] = {
    ["D"] = "Vous souhaitez prouver votre valeur aux yeux de mon espèce, $gmortel:mortelle; ? Très bien, peut-être pouvons-nous nous aider mutuellement.$B$BIl existe un très ancien ver des sables d’une puissance légendaire, nommé Ouro. On dit qu’il a été créé par les Dieux très anciens eux-mêmes pour parodier la vie.$B$BSa peau n’a rien de naturel… épaisse et presque impossible à transpercer, mais souple et lui permettant de se déplacer aussi vite et gracieusement que des créatures beaucoup plus petites.$B$BRamenez-moi un échantillon de cette peau et quelques autres composants mineurs, et je vous fabriquerai des jambières d’un grand pouvoir.",
    ["O"] = "Rapporter la Peau du Grand ver des sables, 2 Idoles de la lutte, 5 Scarabées en bronze et 5 Scarabées d\'ivoire à Kandrostrasz, à Ahn\'Qiraj. Pour terminer cette quête, il faut aussi que la Progéniture de Nozdormu soit au moins Amicale à votre égard.",
    ["T"] = "Les garde-jambes du Vengeur",
  },
  [8630] = {
    ["D"] = "Nous pensons qu’à l’origine, les épaulières ornées que portent les races mortelles étaient une tentative pour imiter les ailes des dragons.$B$BRamenez-moi les manchettes portées par les plus hauts dignitaires qiraji, et je les façonnerai pour en faire des espauliers plus redoutables que les ailes de Nefarian lui-même !",
