  [8665] = {
    ["D"] = "La magie qiraji est puissante et ancienne. Les Qiraji les plus redoutables manient des objets si terribles qu\'ils feraient hésiter un dragon. Si vous me ramenez des manchettes de domination qiraji et quelques autres composants mineurs, je vous fabriquerai des bottes exceptionnelles.$B$BVous les trouverez utiles pour affronter l\'ennemi.",
    ["O"] = "Rapporter des Manchettes de domination qiraji, 2 Idoles de la renaissance, 5 Scarabées de pierre et 5 Scarabées d\'argent à Kandrostrasz, à Ahn\'Qiraj. Pour terminer cette quête, il faut aussi que la Progéniture de Nozdormu soit au moins Neutre à votre égard.",
    ["T"] = "Les bottes de la genèse",
  },
  [8666] = {
    ["D"] = "L\'indicible Dieu très ancien se réveille lentement de son sommeil sous Ahn\'Qiraj. Le destin atroce qui attend Azeroth peut s\'abattre sur nous dans des jours, ou des années, car le temps n\'a pas vraiment de sens pour lui.$B$BMais lorsque les étoiles seront propices, il se déchaînera, et son effroyable courroux s\'abattra sur toutes les créatures vivantes. Trouvez-le avant qu\'il ne soit trop tard, et mettez un terme à son existence, d\'une manière ou d\'une autre.$B$BSi vous y parvenez, ramenez-moi un morceau de sa carcasse, et je vous en ferai une armure qui n\'a encore jamais été portée par aucun mortel… une armure digne $gd\'un tueur:d\'une tueuse; de dieu !",
    ["O"] = "Apporter la Carcasse du Dieu très ancien, 2 Idoles de la renaissance, 5 Scarabées en bronze et 5 Scarabées d\'ivoire à Vethsera à l\'intérieur d\'Ahn\'Qiraj. Pour terminer cette quête, vous devez être $gHonoré:Honorée; par la Progéniture de Nozdormu.",
    ["T"] = "Le gilet de la genèse",
  },
  [8667] = {
    ["D"] = "Les Empereurs jumeaux… Leurs murmures incessants usent peu à peu ma santé mentale. Ils se moquent de moi. Ils savent que je ne peux pas pénétrer plus avant dans leur domaine pour sauver mon frère Arygos.$B$BDans mes visions, leurs visages monstrueux sont surmontés d\'impressionnantes couronnes. Mais j\'aurai ma revanche… Je transformerai ces symboles de la puissance des Qiraji en quelque chose de plus adapté à notre lutte.$B$BRamenez-moi les composants dont j\'ai besoin, $N, et je vous donnerai une coiffure sans égale.",
    ["O"] = "Apporter le Diadème de Vek\'lor, 2 Idoles de la vie, 5 Scarabées d\'or et 5 Scarabées d\'argile à Andorgos à Ahn\'Qiraj. Pour terminer cette quête, il faut aussi que la Progéniture de Nozdormu soit au moins Amicale à votre égard.",
    ["T"] = "Le casque de la genèse",
  },
  [8668] = {
    ["D"] = "Vous souhaitez prouver votre valeur aux yeux de mon espèce, $gmortel:mortelle; ? Très bien, peut-être pouvons-nous nous aider mutuellement.$B$BIl existe un très ancien ver des sables d\'une puissance légendaire, nommé Ouro. On dit qu\'il a été créé par les Dieux très anciens eux-mêmes pour parodier la vie.$B$BSa peau n\'a rien de naturel… épaisse et presque impossible à transpercer, mais souple et lui permettant de se déplacer aussi vite et gracieusement que des créatures beaucoup plus petites.$B$BRamenez-moi un échantillon de cette peau et quelques autres composants mineurs, et je vous fabriquerai des chausses d\'un grand pouvoir.",
    ["O"] = "Rapporter la Peau du Grand ver des sables, 2 Idoles de la guerre, 5 Scarabées de pierre et 5 Scarabées de cristal à Kandrostrasz, à l\'intérieur d\'Ahn\'Qiraj. Pour terminer cette quête, il faut aussi que la Progéniture de Nozdormu soit au moins Amicale à votre égard.",
    ["T"] = "Les chausses de la genèse",
  },
  [8669] = {
    ["D"] = "Nous pensons qu\'à l\'origine, les épaulières ornées que portent les races mortelles étaient une tentative pour imiter les ailes des dragons.$B$BRamenez-moi les manchettes portées par les plus hauts dignitaires qiraji, et je les façonnerai pour en faire des espauliers plus redoutables que les ailes de Nefarian lui-même !",
    ["O"] = "Rapporter des Manchettes de domination qiraji, 2 Idoles de la lutte, 5 Scarabées d\'or et 5 Scarabées d\'os à Andorgos, à Ahn\'Qiraj. Pour terminer cette quête, il faut aussi que la Progéniture de Nozdormu soit au moins Neutre à votre égard.",
    ["T"] = "Le protège-épaules de la genèse",
  },
