  },
  [8770] = {
    ["D"] = "L\'effort de guerre progresse, mais la présence des silithides dans la région représente une menace de plus en plus importante pour nos opérations. Vous avez été $gchoisi:choisie; pour participer à l\'assaut sur la Ruche\'Ashi. Vos cibles prioritaires seront les défenseurs de la Ruche\'Ashi.$B$BÀ la fin de la mission, présentez-vous au rapport devant le commandant Mar\'alith.",
    ["O"] = "Tuez 30 Défenseurs de la Ruche\'Ashi, et revenez faire votre rapport au commandant Mar\'alith au Fort cénarien, en Silithus. Vous devez aussi apporter le Briefing de combat I pour terminer cette quête.",
    ["T"] = "Objectif : les défenseurs de la Ruche\'Ashi",
  },
  [8771] = {
    ["D"] = "L’effort de guerre progresse, mais la présence des silithides dans la région représente une menace de plus en plus importante pour nos opérations. Vous avez été $gchoisi:choisie; pour participer à l’assaut sur la Ruche’Ashi. Vos cibles prioritaires seront les traqueuses des sables de la Ruche’Ashi.$B$BÀ la fin de la mission, présentez-vous au rapport devant le commandant Mar’alith.",
    ["O"] = "Tuez 30 Traqueuses des sables de la Ruche’Ashi, et revenez faire votre rapport au commandant Mar\'alith au Fort cénarien, en Silithus. Vous devez aussi apporter le Briefing de combat II pour terminer cette quête.",
    ["T"] = "Objectif : les traqueuses des sables de la Ruche\'Ashi",
  },
  [8772] = {
    ["D"] = "Nous avons demandé à ce que des renforts rejoignent l’assaut sur la Ruche’Zora. Vous avez été $gchoisi:choisie; pour affronter les patrouilleuses de la Ruche\'Zora. À la fin de la mission, présentez-vous au rapport devant le commandant Mar’alith.",
    ["O"] = "Tuez 30 Patrouilleuses de la Ruche’Zora, puis présentez-vous au rapport devant le commandant Mar’alith au Fort cénarien en Silithus. Pour terminer cette quête, vous devez aussi apporter le Briefing de combat VI.",
    ["T"] = "Objectif : les patrouilleuses de la Ruche\'Zora",
  },
  [8773] = {
    ["D"] = "Nous avons demandé à ce que des renforts rejoignent l’assaut sur la Ruche’Zora. Vous avez été $gchoisi:choisie; pour affronter les saccageuses de la Ruche\'Zora. À la fin de la mission, présentez-vous au rapport devant le commandant Mar’alith.",
    ["O"] = "Tuez 30 Saccageuses de la Ruche’Zora, et revenez faire votre rapport au commandant Mar\'alith au Fort cénarien, en Silithus. Vous devez aussi apporter le Briefing de combat IV pour terminer cette quête.",
    ["T"] = "Objectif : les saccageuses de la Ruche\'Zora",
  },
  [8774] = {
    ["D"] = "L’effort de guerre progresse, mais la présence des silithides dans la région représente une menace de plus en plus importante pour nos opérations. Vous avez été $gchoisi:choisie; pour participer à l’assaut sur la Ruche’Regal. Vos cibles prioritaires seront les embusquées de la Ruche’Regal.$B$BÀ la fin de la mission, présentez-vous au rapport devant le commandant Mar’alith.",
    ["O"] = "Tuez 30 Embusquées de la Ruche’Regal, puis présentez-vous au rapport devant le commandant Mar’alith au Fort cénarien en Silithus. Pour terminer cette quête, vous devez aussi apporter le Briefing de combat VIII.",
    ["T"] = "Objectif : les embusquées de la Ruche\'Regal",
  },
  [8775] = {
    ["D"] = "L’effort de guerre progresse, mais la présence des silithides dans la région représente une menace de plus en plus importante pour nos opérations. Vous avez été $gchoisi:choisie; pour participer à l’assaut sur la Ruche’Regal. Vos cibles prioritaires seront aériennes : les crache-feu de la Ruche’Ashi.$B$BÀ la fin de la mission, présentez-vous au rapport devant le commandant Mar’alith.",
    ["O"] = "Tuez 30 Crache-feu de la Ruche’Regal, puis présentez-vous au rapport devant le commandant Mar’alith au Fort cénarien en Silithus. Pour terminer cette quête, vous devez aussi apporter le Briefing de combat IX.",
    ["T"] = "Objectif : les crache-feu de la Ruche\'Regal",
  },
  [8776] = {
    ["D"] = "L’effort de guerre progresse, mais la présence des silithides dans la région représente une menace de plus en plus importante pour nos opérations. Vous avez été $gchoisi:choisie; pour participer à l’assaut sur la Ruche’Regal. Vos cibles prioritaires seront les esclavagistes silithides.$B$BÀ la fin de la mission, présentez-vous au rapport devant le commandant Mar’alith.",
    ["O"] = "Tuez 30 Esclavagistes de la Ruche’Regal, puis présentez-vous au rapport devant le commandant Mar’alith au Fort cénarien en Silithus. Pour terminer cette quête, vous devez aussi apporter le Briefing de combat X.",
    ["T"] = "Objectif : les esclavagistes de la Ruche\'Regal",
  },
  [8777] = {
    ["D"] = "L’effort de guerre progresse, mais la présence des silithides dans la région représente une menace de plus en plus importante pour nos opérations. Vous avez été $gchoisi:choisie; pour participer à l’assaut sur la Ruche’Regal. Vos cibles prioritaires seront les les fouisseuses de la Ruche’Regal.$B$BÀ la fin de la mission, présentez-vous au rapport devant le commandant Mar’alith.",
    ["O"] = "Tuez 30 Fouisseuses de la Ruche’Regal, puis présentez-vous au rapport devant le commandant Mar’alith au Fort cénarien en Silithus. Pour terminer cette quête, vous devez aussi apporter le Briefing de combat XI.",
    ["T"] = "Objectif : les fouisseuses de la Ruche\'Regal",
  },
  [8778] = {
    ["D"] = "La brigade de Forgefer a déposé une requête inhabituelle pour des composants extrêmement… volatils. Rassemblez-les et remettez-les à M. Tuyèraressort, de la brigade de Forgefer.",
    ["O"] = "Apporter 6 Huiles d’immolation, 5 doses de Carburant de fusée gobelin et 10 doses de Poudre d’explosion dense à l’arcaniste Tuyèraressort, près de la Ruche’Zora en Silithus. Vous devez aussi apporter le Briefing logistique IV pour terminer cette quête.",
    ["T"] = "La brigade de Forgefer a besoin d\'explosifs !",
  },
  [8779] = {
    ["D"] = "Les études de la géologue Fablemort ont rendu d’immenses services à nos opérations en Silithus. Elle a récemment demandé du matériel de divination pour ses recherches. Procurez-vous le nécessaire, et allez le lui livrer personnellement.",
    ["O"] = "Apporter un 1 Grand éclat brillant, 1 Gros éclat irradiant et 1 Enorme émeraude à la géologue Fablemort, au Fort cénarien en Silithus. Vous devez aussi apporter le Briefing logistique V pour terminer cette quête.",
    ["T"] = "Le matériel de divination",
  },
  [8780] = {
    ["D"] = "Les réserves de renforts d\'armures de la brigade de Forgefer sont dangereusement basses. Procurez-vous-en, et livrez-les à Janela Rudemartel, à l\'avant-poste de la brigade d\'Ironfoge, près de la Ruche\'Zora.",
    ["O"] = "Apportez 8 Renforts d\'armure robustes et 8 Renforts d\'armure lourds à Janela Rudemartel, à l\'avant-poste de la brigade d\'Ironfoge, près de la Ruche\'Zora. Vous devez aussi apporter le Briefing logistique VII pour terminer cette quête.",
    ["T"] = "Des renforts d\'armure pour le champ de bataille",
  },
  [8781] = {
    ["D"] = "La brigade de Forgefer a joué un grand rôle dans nos combats en Silithus. Malheureusement, une bonne partie de son matériel s’est égarée au cours du transport. Procurez-vous des armes et livrez-les à Janela Rudemartel, à l’avant-poste de la brigade de Forgefer, près de la Ruche’Zora.",
    ["O"] = "Apportez 2 Epées d’acier lunaire à Janela Rudemartel, à l’avant-poste de la brigade de Forgefer, près de la Ruche’Zora. Vous devez aussi apporter le Briefing logistique VI pour terminer cette quête.",
    ["T"] = "Des armes pour le champ de bataille",
  },
  [8782] = {
    ["D"] = "Le dernier afflux de volontaires nous laisse à court d’uniformes. Procurez-vous de quoi en fabriquer une série et présentez-vous à l’Imploratrice céleste Corne-fière.",
    ["O"] = "Remettre 1 Etoffe lunaire, 2 Rouleaux d’étoffe runique et 1 Soie d’araignée toilacier à l’Imploratrice céleste Corne-fière au Fort cénarien en Silithus. Vous devez aussi apporter le Briefing logistique VIII pour terminer cette quête.",
    ["T"] = "La question des uniformes",
  },
  [8783] = {
    ["D"] = "Vargus, le forgeron du Fort cénarien, a demandé des matériaux enchantés. Il compte s’en servir pour forger des armes redoutables à nos troupes. Procurez-vous ces matériaux et remettez-les directement à Vargus, afin que l’équipement des forces du Fort cénarien reste performant.",
    ["O"] = "Remettre 2 Barres de thorium enchanté et 2 Cuirs enchantés à Vargus au Fort cénarien en Silithus. Vous devez aussi apporter le Briefing logistique IX pour terminer cette quête.",
    ["T"] = "Des matériaux extraordinaires",
  },
  [8784] = {
    ["D"] = "Vous avez découvert un antique artefact qiraji ! L’un des dragons qui se trouve près de l’entrée du temple saura peut-être quoi en faire.",
    ["O"] = "Remettre l’Ancien artefact qiraji aux dragons qui se cachent près de l’entrée du temple.",
    ["T"] = "Les secrets des Qiraji",
  },
  [8785] = {
    ["D"] = "La légion d\'Orgrimmar demande plusieurs ingrédients inhabituels. Vu la manière dont ils se comportent en Silithus, nous aimerions leur donner satisfaction aussi rapidement que possible. Récupérez ces objets et remettez-les à la trollesse, Shai.",
    ["O"] = "Apporter 6 Mojos puissants, 6 Flacons de grand mojo et 8 Huiles d\'immolation à la Prêtresse des ombres Shai, près de la Ruche\'Regal en Silithus. Pour terminer cette quête, vous devez aussi apporter le Briefing logistique IV.",
    ["T"] = "La légion d\'Orgrimmar a besoin de mojo !",
  },
  [8786] = {
    ["D"] = "La légion d\'Orgrimmar a présenté une requête pour des armes supplémentaires, que nous sommes incapables d\'honorer pour l\'instant. Obtenez les armes demandées et apportez-les à Merok Long-pas, à l\'extérieur de la Ruche\'Regal.",
    ["O"] = "Remettre 3 Gigantesques haches en fer à Merok Long-pas, au camp de la légion d\'Orgrimmar, à l\'extérieur de la Ruche\'Regal. Pour terminer cette quête, vous devez aussi apporter le Briefing logistique VI.",
    ["T"] = "Des armes pour le champ de bataille",
  },
  [8787] = {
    ["D"] = "Les vaillants volontaires de la légion d\'Orgrimmar sont venus en Silithus pour nous aider. Malheureusement, leur réserve de renforts d\'armure s\'est perdue au cours du voyage. Procurez-vous-en, et remettez-les à Merok Long-pas, au camp de la légion d\'Orgrimmar.",
    ["O"] = "Apportez 8 Renforts d\'armure robustes et 8 Renforts d\'armure lourds à Merok Long-pas près de la Ruche\'Regal. Pour terminer cette quête, vous devez aussi apporter le Briefing logistique VII.",
    ["T"] = "Des renforts d\'armure pour le champ de bataille",
  },
  [8788] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Un cadeau secoué doucement",
  },
  [8789] = {
    ["D"] = "Nous ne sommes pas restés inactifs pendant un millénaire, $N.$B$BNous avons vu ces bêtes dévorer ceux des leurs qui ne leur semblaient plus utiles. Nous les avons écoutées alors qu’elles préparaient la destruction de notre monde. Et nous avons appris qu’elles connaissaient la trahison.$B$BDes faiblesses existent dans leurs rangs. Nous saurons comment les utiliser à notre avantage.$B$BAllez et trouvez-moi leurs armes de cérémonie. Combinées avec des éléments créés grâce à leur dieu volatil, elles deviendront une force de justice.",
    ["O"] = "Arygos, dans le temple d’Ahn’Qiraj, vous fabriquera des armes imprégnées d’élémentium si vous lui procurez des Armes impériales qiraji et 3 Minerais d’élémentium.",
    ["T"] = "Les armes impériales qiraji",
  },
  [8790] = {
    ["D"] = "À en croire les théories d’Arygos, l’élément qui naît des conflits entre les Dieux très anciens et leurs séides peut devenir redoutable pour eux lorsque toutes les conditions sont réunies. Je parle de l’élémentium, $gmortel:mortelle;.$B$BTrouvez-moi un Equipement impérial qiraji et cet élément toxique. Je vous fabriquerai des armes d’un grand pouvoir.",
    ["O"] = "Merithra du Rêve, au temple d’Ahn’Qiraj, vous fabriquera des armes imprégnées d’élémentium si vous lui ramenez un Equipement impérial qiraji et 3 Minerais d’élémentium.",
    ["T"] = "L\'équipement impérial qiraji",
  },
  [8791] = {
    ["D"] = "Une terrible menace est enfin neutralisée. Ossirian le cruel a été vaincu.$B$BLes habitants de Kalimdor seront soulagés lorsqu’ils auront connaissance de cette victoire. Prenez la tête d’Ossirian et présentez-la au commandant Mar’alith du Fort cénarien.",
    ["O"] = "Remettez la Tête d’Ossirian l’Intouché au commandant Mar’alith, au Fort cénarien en Silithus.",
    ["T"] = "La chute d\'Ossirian",
  },
  [8792] = {
    ["D"] = "Bonjour. Que vous ayez accepté de m’écouter est déjà une bonne chose. La Horde a besoin de toute l’aide possible pour se préparer à la guerre d’Ahn’Qiraj. Cela veut aussi dire votre aide ! Au moment où je vous parle, des collecteurs officiels récoltent tout ce qui sera nécessaire pendant la bataille, mais sans votre aide, $N, nous n’atteindrons pas nos objectifs ! Vous devriez aller parler au responsable, le seigneur de guerre Gorchuk. Qu’en pensez-vous, $C ? Nous aiderez-vous dans ces préparatifs capitaux ?",
    ["O"] = "Parler au seigneur de guerre Gorchuk, dans la vallée des Esprits d’Orgrimmar.",
    ["T"] = "La Horde a besoin de votre aide !",
  },
  [8793] = {
    ["D"] = "Bonjour. Que vous ayez accepté de m’écouter est déjà une bonne chose. La Horde a besoin de toute l’aide possible pour se préparer à la guerre d’Ahn’Qiraj. Cela veut aussi dire votre aide ! Au moment où je vous parle, des collecteurs officiels récoltent tout ce qui sera nécessaire pendant la bataille, mais sans votre aide, $N, nous n’atteindrons pas nos objectifs ! Vous devriez aller parler au responsable, le seigneur de guerre Gorchuk. Qu’en pensez-vous, $C ? Nous aiderez-vous dans ces préparatifs capitaux ?",
    ["O"] = "Parler au seigneur de guerre Gorchuk, dans la vallée des Esprits d’Orgrimmar.",
    ["T"] = "La Horde a besoin de votre aide !",
  },
  [8794] = {
    ["D"] = "Bonjour. Que vous ayez accepté de m’écouter est déjà une bonne chose. La Horde a besoin de toute l’aide possible pour se préparer à la guerre d’Ahn’Qiraj. Cela veut aussi dire votre aide ! Au moment où je vous parle, des collecteurs officiels récoltent tout ce qui sera nécessaire pendant la bataille, mais sans votre aide, $N, nous n’atteindrons pas nos objectifs ! Vous devriez aller parler au responsable, le seigneur de guerre Gorchuk. Qu’en pensez-vous, $C ? Nous aiderez-vous dans ces préparatifs capitaux ?",
    ["O"] = "Parler au seigneur de guerre Gorchuk, dans la vallée des Esprits d’Orgrimmar.",
    ["T"] = "La Horde a besoin de votre aide !",
  },
  [8795] = {
    ["D"] = "Bonjour. Que vous ayez accepté de m’écouter est déjà une bonne chose. L’Alliance a besoin de toute l’aide possible pour se préparer à la guerre d’Ahn’Qiraj. Cela veut aussi dire votre aide ! Au moment où je vous parle, des collecteurs officiels récoltent tout ce qui sera nécessaire pendant la bataille, mais sans votre aide, $N, nous n’atteindrons pas nos objectifs ! Vous devriez aller parler au responsable, le grand maréchal Snowfall. Qu’en pensez-vous, $c ? Nous aiderez-vous dans ces préparatifs capitaux ?",
    ["O"] = "Parler au grand maréchal Snowfall dans la Garde militaire d’Ironforge.",
    ["T"] = "L\'Alliance a besoin de votre aide !",
  },
  [8796] = {
    ["D"] = "Bonjour. Que vous ayez accepté de m’écouter est déjà une bonne chose. L’Alliance a besoin de toute l’aide possible pour se préparer à la guerre d’Ahn’Qiraj. Cela veut aussi dire votre aide ! Au moment où je vous parle, des collecteurs officiels récoltent tout ce qui sera nécessaire pendant la bataille, mais sans votre aide, $n, nous n’atteindrons pas nos objectifs ! Vous devriez aller parler au responsable, le grand maréchal Snowfall. Qu’en pensez-vous, $c ? Nous aiderez-vous dans ces préparatifs capitaux ?",
    ["O"] = "Parler au grand maréchal Tombeneige dans la Garde militaire de Forgefer.",
    ["T"] = "L\'Alliance a besoin de votre aide !",
  },
  [8797] = {
    ["D"] = "Bonjour. Que vous ayez accepté de m’écouter est déjà une bonne chose. L’Alliance a besoin de toute l’aide possible pour se préparer à la guerre d’Ahn’Qiraj. Cela veut aussi dire votre aide ! Au moment où je vous parle, des collecteurs officiels récoltent tout ce qui sera nécessaire pendant la bataille, mais sans votre aide, $n, nous n’atteindrons pas nos objectifs ! Vous devriez aller parler au responsable, le grand maréchal Snowfall. Qu’en pensez-vous, $c ? Nous aiderez-vous dans ces préparatifs capitaux ?",
    ["O"] = "Parler au grand maréchal Tombeneige dans la Garde militaire de Forgefer.",
    ["T"] = "L\'Alliance a besoin de votre aide !",
  },
  [8798] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Un yéti rien que pour vous",
  },
  [8799] = {
    ["D"] = "Écoutez… Je ne suis pas vraiment censé vous dire cela, mais comme vous êtes le héros du jour et tout ça…$B$BIl y a du houx frais partout à cette époque de l’année, mais il ne dure jamais bien longtemps. Nous avons une machine qui préserve le houx - il ne se fane jamais ! Nous l’utilisons pour pimenter les fêtes… et faire de bons profits durant l’été !$B$BJe vous laisserai utiliser le préservateur, mais seulement si vous êtes un maître cuisinier. Vous aurez besoin d’un peu de sel de Fonderoc et de deux, trois, allez, disons cinq pièces d’or. Il vous fabriquera une belle fournée que vous pourrez garder pour vous.",
    ["O"] = "Placer 1 Sel de Fonderoc et 5 pièces d’or dans un Préservateur de houx, et vous recevrez 5 Houx préservés.",
    ["T"] = "Le héros du jour",
  },
  [8800] = {
    ["D"] = "Lorsque vous aurez terminé les tâches qui vous sont assignées, allez voir Vargus, le forgeron. Montrez-lui les insignes que vous aurez reçus. Il vous fournira du matériel à la hauteur de votre dévotion à notre cause.",
    ["O"] = "Parlez à Vargus, au Fort cénarien en Silithus.",
    ["T"] = "La tenue de combat cénarienne",
  },
  [8801] = {
    ["D"] = "Les murs d\'Ahn\'Qiraj tremblent. Une force maléfique plus vieille que ce monde a été détruite.$B$BAlors que vous contemplez les restes de cette abomination colossale, votre sang se glace. Même après sa mort, l\'héritage de C\'Thun reste perceptible dans le mal qui vous entoure.$B$BVous avez accompli l\'impossible.$B$BVous vous emparez d\'un œil pédonculé et le glissez dans votre sac à dos.$B$BApportez-le aux dragons qui se trouvent dans la pièce voisine.",
    ["O"] = "Remettre l\'Oeil de C\'Thun a Caelastrasz, dans le temple d\'Ahn\'Qiraj.",
    ["T"] = "L\'héritage de C\'Thun",
  },
  [8802] = {
    ["D"] = "Rendez-vous dans les grottes du Temps, $gmonseigneur:dame; $N. Anachronos attend votre retour. Donnez-lui l\'œil de C\'Thun. Il lui donnera certainement une place d\'honneur dans la collection de son maître.",
    ["O"] = "Apporter l\'Oeil de C\'Thun à Anachronos dans les grottes du Temps.",
    ["T"] = "Le sauveur de Kalimdor",
  },
  [8803] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Un cadeau de fête",
  },
  [8804] = {
    ["D"] = "L\'afflux de nouvelles recrues au Fort cénarien nous pose un problème. Comment les garder en vie dans le désert ? Les volontaires inexpérimentés risquent la déshydratation et l\'empoisonnement – le sable grouille de créatures venimeuses. Rassemblez les matériaux nécessaires à la constitution de paquetages de survie dans le désert, et remettez-les à Calandrath au Fort cénarien.",
    ["O"] = "Ramener 4 Globes d\'eau, 4 Anti-venins puissants et 4 Boulettes du désert fumées à Calandrath, à l\'auberge du Fort cénarien en Silithus. Pour terminer cette quête, vous devez aussi apporter le Briefing logistique I.",
    ["T"] = "Survivre dans le désert",
  },
  [8805] = {
    ["D"] = "La division de cavalerie de la garde du Fort cénarien manque de bottes ornées en mithril. Procurez-vous en un stock et livrez-le immédiatement au capitaine Vish Kozus au Fort cénarien.",
    ["O"] = "Remettre 3 Bottes ornées en mithril à Vish Kozus, capitaine de la garde du Fort cénarien en Silithus. Pour terminer cette quête, vous devez aussi apporter le Briefing logistique II.",
    ["T"] = "Des bottes pour la garde",
  },
  [8806] = {
    ["D"] = "La garde du Fort cénarien est presque à court de silex à aiguiser. Apportez-en un assortiment au capitaine Vish Kozus, du Fort cénarien.",
    ["O"] = "Apporter 10 Silex à aiguiser denses, 10 Silex à aiguiser solides et 10 Silex à aiguiser lourds à Vish Kozus, capitaine de la garde du Fort cénarien en Silithus. Pour terminer cette quête, vous devez aussi apporter le Briefing logistique III.",
    ["T"] = "Des silex à aiguiser pour la Garde",
  },
  [8807] = {
    ["D"] = "Les études de la géologue Fablemort ont rendu d\'immenses services à nos opérations en Silithus. Elle a récemment demandé du matériel de divination pour ses recherches. Procurez-vous le nécessaire, et allez le lui livrer personnellement.",
    ["O"] = "Apporter un 1 Grand éclat brillant, 1 Gros éclat irradiant et 1 Enorme émeraude à la géologue Fablemort, au Fort cénarien en Silithus. Pour terminer cette quête, vous devez aussi apporter le Briefing logistique V.",
    ["T"] = "Le matériel de divination",
  },
  [8808] = {
    ["D"] = "Le dernier afflux de volontaires nous laisse à court d\'uniformes. Procurez-vous de quoi en fabriquer une série et présentez-vous à l\'Imploratrice céleste Corne-fière.",
    ["O"] = "Remettre 1 Etoffe lunaire, 2 Rouleaux d\'étoffe runique et 1 Soie d\'araignée toilacier à l\'Imploratrice céleste Corne-fière au Fort cénarien en Silithus. Vous devez aussi apporter le Briefing logistique VIII pour terminer cette quête.",
    ["T"] = "La question des uniformes",
