  [8856] = {
    ["D"] = "With the arrival of fresh recruits to Cenarion Hold comes the problem of keeping them alive in the desert.  Dehydration as well as numerous poisonous creatures present a serious threat to inexperienced volunteers.  Gather the supplies necessary for a desert survival kit and bring them to Calandrath in Cenarion Hold.",
    ["O"] = "Bring 4 Globes of Water, 4 Powerful Anti-Venom and 4 Baked Salmon to Calandrath at the inn in Cenarion Hold in Silithus.",
    ["T"] = "Desert Survival Kits",
  },
  [8857] = {
    ["D"] = "Je suis à la recherche d\'aventuriers endurcis, désireux de remettre un échantillon prélevé sur le Colosse d\'Ahsi à Oglethorpe, qui vit à Baie-du-Butin. Non non, pas question de vous approcher du cadavre de cette chose. Prenez l\'un de mes échantillons et surtout, faites vite. Une fois pourri, ce truc ne lui servira plus à rien.",
    ["O"] = "Remettre la Carcasse du colosse d\'Ashi à Oglethorpe Obnoticus, à Baie-du-Butin.",
    ["T"] = "Les secrets du Colosse - Ashi",
  },
  [8858] = {
    ["D"] = "Franchement, j\'aimerais mieux balancer cette carcasse de cafard dans la sale gueule d\'Oilfist… Sauf que j\'suis coincé là à ramasser des bouts du bestiau. Vu sa taille, j\'suis là jusqu\'aux p\'tites heures du matin.$B$BPrenez cet échantillon et portez-le à Oilfist de ma part. Et vite, $C. Il paye BIEN !$B$BOuais, l\'est à la halte du Thorium, dans la gorge des Vents brûlants.",
    ["O"] = "Apporter la Carcasse du colosse de Regal au surveillant Oilfist, dans la gorge des Vents brûlants.",
    ["T"] = "Les secrets du Colosse - Regal",
  },
  [8859] = {
    ["D"] = "Mes serviteurs ont failli se faire tuer en récupérant ce prélèvement. Vous devez absolument vous dépêcher ! Le seigneur Maxwell Tyrosus attend impatiemment un échantillon frais de la carcasse. Il estime que les secrets de ce colosse pourraient nous aidera dans notre lutte contre le Fléau !$B$BComment, vous êtes encore là ?Allez, en route pour l\'Espoir de lumière ! Oui, oui, vous serez généreusement $grécompensé:récompensée; pour vos efforts…",
    ["O"] = "Apporter la Carcasse du colosse de Zora au seigneur Maxwell Tyrosus, à la chapelle de l\'Espoir de lumière, dans les Maleterres de l\'est.",
    ["T"] = "Les secrets du Colosse - Zora",
  },
  [8860] = {
    ["D"] = "Hurlevent organise une fête pour le nouvel an ! De l\'alcool sera distribué gratuitement toute la journée, et la nuit, il y aura des feux d\'artifice ! Ce sera sans doute une jolie fête ! Mais Baie-du-Butin ne sera pas en reste et aura sa propre fête ; et je parie qu\'elle sera bien plus belle !$B$BSinon, l\'aubergiste Allison, à Hurlevent, a besoin de marchandises des Gourmandises Fumebois pour nourrir tous les fêtards. Vous pouvez me faire une faveur, et la livrer à ma place ?$B$BMerci, $N ! C\'est pour l\'aubergiste Allison de la Rose dorée, à Hurlevent.",
    ["O"] = "Apporter les marchandises des Gourmandises Fumebois à l\'aubergiste Allison, à Hurlevent.",
    ["T"] = "Les fêtes du Nouvel an !",
  },
  [8861] = {
    ["D"] = "Thunder Bluff organise une fête pour le nouvel an ! De l’alcool sera distribué gratuitement toute la journée, et la nuit, il y aura des feux d’artifice ! Ce sera sans doute une jolie fête ! Mais Baie-du-Butin ne sera pas en reste et aura sa propre fête ; et je parie qu’elle sera bien plus belle !$B$BSinon, l’aubergiste Pala, à Thunder Bluff, a besoin de marchandises des Gourmandises Fumebois pour nourrir tous les fêtards.  Vous pouvez me faire une faveur, et la livrer à ma place ?$B$BMerci, $N ! C’est pour l’aubergiste Pala, dont l’établissement est situé près du pont qui mène à la Cime des chasseurs.",
    ["O"] = "Apporter les Marchandises des Gourmandises Fumebois à l\'aubergiste Pala, à Thunder Bluff.",
    ["T"] = "Les fêtes du Nouvel an !",
  },
