  [9229] = {
    ["D"] = "Ramaladni était un guerrier de la Confrérie, et un élève du généralissime Mograine avant même la création de l’Aube ! Quand il a appris la nouvelle de la mort de Mograine, il a été submergé par la colère et la haine.$B$BComprenez-moi bien, $N, quand on laisse la rage et la vengeance prendre les rênes, on cesse d’être un être humain. On devient la personnification des émotions qui nous contrôlent. Et c’est ainsi que Ramaladni, esprit du châtiment, est entré dans Naxxramas pour satisfaire sa vengeance... et n’est jamais revenu.$B$BAllez dans Naxxramas et découvrez ce qui est arrivé à Ramaldani.",
    ["O"] = "Entrez dans Naxxramas et découvrez le Destin de Ramaladni.",
    ["T"] = "Le destin de Ramaladni",
  },
  [9230] = {
    ["D"] = "Réparer cet anneau pourrait bien être un vrai défi, $N. Il a été fabriqué à partir des runes que l’on trouve sur les bâtiments du Fléau, comme Naxxramas. D’autres objets sont nécessaires, mais votre premier souci doit être de récupérer ces runes de givre. Si vous me fournissez ce dont j’ai besoin, cet anneau retrouvera sa gloire première.",
    ["O"] = "Korfax, à la chapelle de l’Espoir de Lumière dans les Maleterres de l’est, veut que vous lui rameniez 1 Rune de givre, 1 Saphir bleu et 1 Barre d’arcanite.",
    ["T"] = "L\'étreinte de glace de Ramaladni",
  },
  [9232] = {
    ["D"] = "Vous vous imaginez être $gun héros:une héroïne; de l’Aube, pas vrai ? J’ai vu nos plus grands soldats submergés par la racaille de Kel’Thuzad ! Après ça, combien pensez-vous que je vais parier sur $gun ahuri fraîchement débarqué:une ahurie fraîchement débarquée; ?$B$B<Wilheim soupire.>$B$BM’enfin, c’est pas comme si j’avais le choix. Y’a personne d’autre, alors autant vous aider.$B$BOmarion m’a laissé avec un seul patron. C’est tout ce que j’ai à mon répertoire... Je peux faire quelques variations sur ce thème de pantalon, il faudra juste que vous me trouviez les matériaux.",
    ["O"] = "L’artisan Wilhelm de la chapelle de l’Espoir de Lumière, dans les Maleterres de l’est, veut 2 Runes de givre, 2 Essences d\'eau, 2 Saphirs bleus et 30 pièces d’or.",
    ["T"] = "C\'est tout ce que j\'ai à mon répertoire...",
  },
  [9233] = {
    ["D"] = "Wilhelm,$B$BSi vous lisez ceci, c’est que je suis enfin mort. Je tiens à ce que vous sachiez que mes dernières pensées ont été pour vous et pour votre complète incompétence en matière d’artisanat. Malheureusement, $gle porteur:la porteuse; de ce recueil est encore pire que vous dans ce domaine.$B$BCe livre contient le reste de mes recettes. Fabriquez ce $gqu’il:qu’elle; vous demandera. Je suis certain que vous allez lui faire payer une fortune parce qu’au fond de vous, vous n’êtes qu’un misérable.$B$BAvec tout mon mépris,$B$BOmarion",
    ["O"] = "Remettre le Recueil d’Omarion à l’artisan Wilhelm à la chapelle de l’Espoir de Lumière dans les Maleterres de l’est.",
