  [9304] = {
    ["D"] = "Vous avez trouvé cette lettre sur l\'un des nombreux serviteurs du Fléau que vous avez tués. À en juger par son apparence et son odeur, elle a longtemps été en la possession de morts-vivants. Peut-être que quelqu\'un à la chapelle de l\'Espoir de Lumière souhaiterait l\'examiner plus attentivement...",
    ["O"] = "Apportez le Document taché au gardien des registres à la chapelle de l\'Espoir de Lumière dans les Maleterres de l\'est.",
    ["T"] = "Un document du front",
  },
  [9310] = {
    ["D"] = "Sur le cadavre de la créature, vous découvrez un cristal qui émet faiblement de l\'énergie nécrotique.",
    ["O"] = "Apportez le Cristal nécrotique voilé au lieutenant Rukag dans la cour d\'Undercity.",
