  [9431] = {
    ["D"] = "             ?$B$B .      .             .$B$B       .                     .",
    ["O"] = "      2        .",
    ["T"] = "   ",
  },
  [9432] = {
    ["D"] = "I am glad that you are here to help.  In fact, there is something that you can do.$B$BAs part of our inclusion within the Alliance, I would like for you to travel to the night elf town, Astranaar.  It is located in Ashenvale, a large forest that is south of Darkshore on the continent of Kalimdor.$B$BPlease speak with Vindicator Palais when you arrive to see if there\'s anything that you can do to help the inhabitants.",
    ["O"] = "Travel to Astranaar in Ashenvale and speak with Vindicator Palais.",
    ["T"] = "Travel to Astranaar",
  },
  [9433] = {
    ["D"] = "Stop looking at me like that! I didn\'t say I was going to send you to Thalanaar\'s moonwell out in the open with dipper and a flask!$B$BI\'ve been, err... tinkering with a product I was transporting for a client. In fact, I\'ve already set up a portable control console for the Robotron out near Thalanaar. You\'ll need to bring this control unit with you and use it in the vicinity of my concealed console.$B$BIf my client ever heard that I so much as removed Robotron from its packaging, he\'d hang me by my ears!",
    ["O"] = "Use the Robotron Control while standing near the Concealed Command Console hidden in a small cluster of bushes on the rim of the needle closest to Thalanaar to take command of a Robotron 3000. Pilot the robot to the western edge of Thousand Needles where the Alliance outpost of Thalanaar is located. Use the robot to collect a sample of the moonwell\'s water and return it to Wizlo Bearingshiner at Whitereach Post.",
    ["T"] = "A Dip in the Moonwell",
  },
  [9434] = {
    ["D"] = "<Wizlo retrieves the water sample from the robot and places it in a crate with the boulderkin earth and several other items.>$B$BThere you go, that\'s everything on Magistrix Elosai\'s list. Tell her that it\'s been a pleasure working with her and that I wish her luck in her research.",
    ["O"] = "Deliver the Crate of Reagents to Magistrix Elosai at Freewind Post.",
    ["T"] = "Testing the Tonic",
  },
  [9435] = {
    ["D"] = "If there\'s one great truth about Hillsbrad, it\'s that this region is destined to live under the shadow of war. Even something as innocuous as a cartload of stone is a target.$B$BNormally the loss of building materials is no great setback, but the most recent shipment from the Azurelode mine contained a crate of rare crystals I was eager to study. Forsaken raiders intercepted the cart and drove it northeast to a tower they\'re attempting to rebuild. It\'s imperative that we retrieve those crystals, $c!",
    ["O"] = "Retrieve the Shipment of Rare Crystals from the Forsaken and return it to Huraan in Southshore.",
    ["T"] = " ",
  },
  [9436] = {
    ["D"] = "$C|1;;,  .              .               .$B$B,               .           . ,      .",
    ["O"] = "           .",
    ["T"] = "    ",
  },
  [9437] = {
    ["D"] = "   ,  ,      .               .              .$B$B  $R            .",
