  [9710] = {
    ["D"] = "I\'ve got everything prepared, $N. Very soon you will have a weapon befitting your new status.",
    ["O"] = "Wait while Bemarrin forges your weapon.",
    ["T"] = "The Blood-Tempered Ranseur",
  },
  [9711] = {
    ["D"] = "Matis will be brought to justice, $N; and I want you to be the one to find him and bring him back to the Triumvirate.$B$BScour the island for this bastard. Should you find him, use this flare gun. When my trackers see the flare, they\'ll come and assist in capturing Matis. Return to me after he\'s been captured.",
    ["O"] = "         .",
    ["T"] = " ",
  },
  [9712] = {
    ["D"] = "You have distinguished yourself in the service of the order, $N. Your skill in wielding the Light energies granted by Magister Astalor and his fellows has proven you worthy to receive a new kind of knowledge.$B$BWith the Light at your command, it is possible to summon a fearsome warhorse that will serve as your faithful mount.$B$BContinue to serve with dedication and honor, $N, and even greater boons await you.",
    ["O"] = "Talk to Knight-Lord Bloodvalor to learn how to summon your Thalassian Warhorse.",
