  [10219] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Walk the Dog",
  },
  [10220] = {
    ["D"] = "난 불굴의 영혼들과 접촉을 시도하기 위해 이곳에 파견됐네. 한때 자랑스러운 얼라이언스 병사들이었던 이들은 오래전 전쟁에서 패배하고 저주받은 영혼이 되어 서쪽에 있는 폐허를 떠돌고 있지.$B$B그들이 우리에게 합류한다면 강력한 동맹을 얻게 되는 것일세. 한번 상상해 보게나. 불멸의 전사로 이루어진 군단을 적에게 보내는 광경을! 하지만, 이 언데드들은 증오로 미쳐 있기 때문에 내 말을 들으려고도 하지 않을 거야.$B$B하지만, 무력을 사용하면 합류할 수도 있겠지.",
    ["O"] = "몰디바가 해골망치 초소의 서쪽에 있는 원정대 무기고의 불굴의 보병 12명, 불굴의 기사 8명, 불굴의 마술사 6명을 해치워 달라고 했습니다.",
    ["T"] = "설득",
  },
  [10221] = {
    ["D"] = "휴... 전에 내 수련생으로 있었던 스파키 우버스러스터는 이곳으로 내려올 때 황천 보호모를 쓰지 않았었기에 미쳐버렸지. 그래서 그가 오늘날의 그 악명 높은 붐 박사로 변했지 뭔가!$B$B그러고는 우리 장비를 전부 다 가지고 북동쪽으로 도망가 버렸다네! 그 이후로 그와 그의 폭탄 로봇들은 52번 구역에 위협이 되어 왔지.$B$B마을 바로 바깥에 있는 붐의 야영지로 가서 그 친구를 끝장내 버려주게! 붐의 파멸 폭탄을 사용하여 그를 처치하게나.$B$B그리고 절대 붐 박사나 그 녀석의 폭탄 로봇에 가까이 다가가선 안 되네!",
    ["O"] = "붐 박사를 처치한 후에 황천의 폭풍의 52번 구역에 있는 선임공병 블라스트피즐에게 승전보를 전해야 합니다.",
    ["T"] = "붐 박사!",
  },
  [10222] = {
    ["D"] = "캘타스 왕자의 군대는 마을을 파괴하고 대마법사님을 보랏빛 탑 안에 가둔 후 북동쪽에 있는 성난태양 주둔지로 철수했소이다. 그들은 우리 마을을 파괴한 다음에는 그 \'대단한 승리\'를 축하하기 위해 우리 동료를 죽였다오. 하지만 우리는 죽은 친구들의 원수를 갚기에는 역부족이군.$B$B이런 범죄에 대해 아무런 처벌도 하지 않는다는 건 있을 수 없는 일이오! 우리가 친구들의 복수를 할 수 있도록 도와주시오. 성난태양 주둔지로 가서 이 범죄에 책임이 있는 자들의 목숨을 빼앗아 돌아오시오.",
    ["O"] = "키린바르 마을에 있는 관리인 디워스가 성난태양 궁수 10명을 처치해 달라고 했습니다.",
    ["T"] = "성난태양 주둔지",
  },
  [10223] = {
    ["D"] = "나는 절대 그 공격을 주도한 블러드 엘프를 잊지 못할 것이오. 그자는 진홍빛 갑옷에 생전 처음 본 장치가 달린 휘장을 두르고 있었소이다.$B$B그자는 포악하고 무자비했으며 다른 블러드 엘프 장교들이 한 명도 보이지 않는 전방에서 공격을 지휘했었소. 그 엘프가 바로 그날 많은 키린 토 마법사들의 죽음으로 이끈 장본인이지만 성난태양 주둔지에 오래 머물러 있지 않았소이다. 캘타스가 그에게 듀로 마나괴철로를 점령하는 작전을 도우라 명했기 때문이지. 그를 찾아서 정의를 위해 그의 활 소리를 잠재워 주시오.",
    ["O"] = "키린바르 마을에 있는 관리인 디워스가 기사대장 댈리스 돈스트라이크를 처치해 달라고 했습니다.",
    ["T"] = "댈리스 처치",
  },
  [10224] = {
    ["D"] = "난 X-52의 선임 기술자로서 황천 로켓의 주 엔진이 최상의 상태에 있는지 확인해야 한다네.$B$B유감스럽게도 아직 그 엔진을 만들지는 못했지만 말이야.$B$B이봐 $C 친구, 난 지금 엄청난 곤경에 빠져 있다고! 로켓에 필요한 모든 부품이 있지만 딱 하나 없는 게 엔진 동력핵이라네.$B$B다행인 건 말이야... 이 지역의 마나 망령의 정수를 사용하면 엔진에 급속 시동을 걸 수 있을 거 같거든.$B$B마나 망령들은 섬의 북쪽 지역에 있는 모든 오싹한 엄폐지 주변에서 튀어나오니까 쉽게 찾을 수 있을 걸세. 아 물론 이곳 52번 구역만 빼고 말이지.",
    ["O"] = "마나 망령의 정수 7개를 수집한 후 황천의 폭풍의 52번 구역에 있는 선임기술자 트렙에게 가져가야 합니다.",
    ["T"] = "엔진을 위한 정수",
  },
  [10225] = {
    ["D"] = "$C 친구! 이 에테리시움 맥석 수정을 모두 내... 음, 내 선임기술자인 트렙에게 가져다줬으면 하는데 말이야.$B$B그 친구가 이걸 가지고 뭘 해야 할지 아마 잘 알 걸세. 그 친구가 혹시 모른다면 알아내게 해줘야 한다고!",
    ["O"] = "황천의 폭풍의 52번 구역에 있는 선임기술자 트렙에게 에테리시움 맥석 수정을 가져가야 합니다.",
    ["T"] = "선임기술자에게 보고",
  },
  [10226] = {
    ["D"] = "젠장, 그러면 안 되는데!$B$B그래, 내게 또 좋은 수가 있어! 내가 자이롤을 시켜서 정령의 마력 추출기를 만들었지. 정령의 마력을 정확히 어디에 사용하는지는 잘 모르지만 내가 쓸 데가 있을 것 같아.$B$B남쪽 부서진 잔해의 절벽에 사는 분리의 우레정령들과 왜곡의 돌연변이들이 엔진을 돌릴 힘을 갖고 있다네!$B$B추출기를 사용하면 놈들의 힘을 고체로 바꿀 수 있지. 그런 다음에 놈들을 처치하고 정령의 마력을 주워만 오라고.",
    ["O"] = "정령의 마력 5개를 추출해서 황천의 폭풍의 52번 구역에 있는 선임기술자 트렙에게 가져가야 합니다.",
    ["T"] = "정령의 마력 추출",
  },
  [10227] = {
    ["D"] = "난 죽은 드레나이를 볼 수 있어요. 우리 주변에는 어디에나 그들이 있죠.$B$B쳇, 그런데 어린 아이가 뭘 알겠느냐면서 아무도 제 말을 믿어주지 않는다니까요. 심지어는 하나밖에 없는 우리 엄마도요.$B$B<람도르를 가리키는 할라이>$B$B하지만, 람도르만큼은 내 말을 믿어줘요... 나랑 똑같이 죽은 드레나이를 보거든요. 다른 아이들은 람도르가 그들 때문에 몹시 화가 났다고들 해요!$B$B어른들은 참 웃기죠. 내가 유령이 보인다고 하면 웃으면서 머리를 쓰다듬고 저를 달래보려고 애를 쓰는데 람도르가 유령이 보인다고 말하니까 미쳤다며 손가락질을 해요. 그래서 난 평생 아이로만 살았으면 좋겠어요!$B$B안으로 들어가기 전에 람도르와 대화하는 게 좋을 걸요. 그는 한때 죽음의 사제였죠.",
    ["O"] = "아키나이 납골당 바깥에서 뼈의 산 위에 서 있는 광기의 람도르와 대화해야 합니다.",
    ["T"] = "죽은 드레나이가 보여요",
  },
  [10228] = {
    ["D"] = "I believe that the dead, which we have been caring for these last 500 odd years, should be left alone. Maladaar has other plans. He has retreated to the Crypt of Remembrance to start his experiments... Necromancy.$B$BI hope you weren\'t thinking about going in there... Not before helping me out so that these damned spirits will finally leave me alone. I know where they keep their treasures, $r. Got your attention, eh?$B$BBring me the book of the dead, held by the Aldor, Ezekiel of Shattrath.",
    ["O"] = "샤트라스에 있는 에제키엘을 찾아 대화해야 합니다.",
    ["T"] = "에제키엘",
  },
  [10229] = {
    ["D"] = "이 고서는 얼라이언스의 비밀 암호로 쓰인 역사책 같습니다. 해골망치 초소에 있는 역사가 알센은 오랫동안 불굴의 영혼들에 대해 연구했기 때문에 이 책에 쓰인 얼라이언스의 암호를 해독할 수 있을지도 모릅니다.",
    ["O"] = "해골망치 초소에 있는 역사가 알센에게 불가사의한 고서를 가져가야 합니다.",
    ["T"] = "고서 판독",
  },
  [10230] = {
    ["D"] = "불굴의 군대에 대한 우리의 접근 방식이 틀렸던 것 같군.$B$B이 책을 보면 아주 오래전 오크족과 죽음의 기사들이 얼라이언스의 원정대 무기고를 파괴했다는군. 불굴의 군대는 원한을 품은 영혼이 되어 타락한 오크 부족과 끝이 없는 전쟁을 하며 이승에 남아 있는 것일세.$B$B그들은 오로지 복수에 대한 생각뿐일세. 우리가 그들이 가장 증오하는 적에게 복수할 수 있게 도와주자고. 다시 한 번 원정대 무기고로 들어가서 부사령관 탈보스를 찾게. 그는 불굴의 군대를 소환할 수 있는 전투 뿔피리를 지니고 다닌다네. 그걸 가져와.",
    ["O"] = "부사령관 탈보스로부터 불굴의 전투 뿔피리를 빼앗아서 해골망치 초소에 있는 역사가 알센에게 가져가야 합니다.",
    ["T"] = "전투 뿔피리",
  },
  [10231] = {
    ["D"] = "Ramdor? Hrm, let me think... Ramdor... Oh yes, the death priest. What does he want with the book? Well it doesn\'t matter. Yes I lied to you and no I don\'t have the book. Not anymore, anyway. It was stolen. Probably by one of those filthy refugees.$B$BBesides, Auchindoun is now defunct. We have no use for the book. If you find it, you can keep it.$B$BLike I said, I saw some of those ruffians snooping around the terrace last week. Start your search with them. They\'re probably playing dice in the slums.",
    ["O"] = "Ezekiel said the book has been stolen. Your best bet is to find the Ruffians and see if they have any information.",
    ["T"] = "무슨 책 말이지? 책이라곤 못 봤는데...",
  },
  [10232] = {
    ["D"] = "불타는 군단이 길을 내려가 동쪽 다리 건너에 있는 우리의 고철 지대에 압력을 가하고 있네.$B$B정말 큰일이 아닐 수 없다고! 알클론 폐허에 있는 불타는 군단 놈들만으로 모자라서 이젠 고철 지대까지 걱정해야 한다니, 이곳 52번 구역에서의 로켓 개발 사업에 지장이 있을 것 같다네.$B$B거기로 가서 그 악마들을 처치해 주게나! 내가 하고 싶지만 난 한 여자의 남편이지 싸움꾼이 아니라서 말이야.$B$B그렇네, 힘든 임무가 될 거야. 난 느낄 수 있다고!",
    ["O"] = "모아그 파멸의 대장장이 5명과 간아그 기술자 15명을 처치한 후 황천의 폭풍의 52번 구역에 있는 휠러 아저씨에게 가야 합니다.",
    ["T"] = "불타는 군단과의 전투",
  },
  [10233] = {
    ["D"] = "블러드 엘프들이 성난태양 주둔지를 다시 점거하는 걸 막으려면 무기고를 파괴하는 것만으로는 부족합니다. 캘타스의 추종자들은 열정적이고 끈기가 넘치는 자들이지요. 성난태양 주둔지에 녀석들이 쓸만한 무언가를 남겨두면 녀석들을 그곳으로 돌아오게 하는 효과적인 유인물이 될 겁니다.$B$B아니야... 그럼 안 됩니다. 다 파괴해 버려야 합니다. 녀석들의 천막과 대포가 불에 타서 재가 되는 광경을 지켜보십시오!$B$B<주먹을 꽉 쥐는 유령>$B$B성난태양 불꽃지기에게서 횃불을 훔쳐 이번 임무를 수행하십시오.",
    ["O"] = "불타오르는 횃불을 사용하여 성난태양 쇠뇌 4개, 성난태양 막사 4개를 파괴하고 키린바르 마을에 있는 마술사 부관 모란에게 가야 합니다.",
    ["T"] = "성난태양 주둔지 방화",
  },
  [10234] = {
