  [4361] = {
    ["D"] = "Tu tens que levar essas informações para o Rei Magni, $N. Ele saberá como proceder!",
    ["O"] = "Volte a Altaforja e dê as más notícias ao Rei Magni Barbabronze.",
    ["T"] = "Informante das más notícias",
