  [5803] = {
    ["D"] = "No coração em ruínas da cidade, Araj, o Evocador reina como o senhor de Andorhal. Sabemos que ele era o líder da Scolomântia antes de tomar a cidade. Para terminar a chave, precisaremos do sinete dele, o chamado escaravelho.$B$BAgora que ele foi atraído para campo aberto, siga para o âmago da cidade e rompa o jugo de Araj sob a cidade. Depois de matá-lo, pegue o escaravelho no filactério dele!$B$BA Chave para Scolomântia está à sua espera, $N!",
    ["O"] = "Destrua Araj, o Evocador e leve o Escaravelho de Araj para o Alquimista Arvignon no Morro do Ventogelante, que fica nas Terras Pestilentas Ocidentais.",
    ["T"] = "O escaravelho de Araj",
  },
  [5804] = {
    ["D"] = "No coração em ruínas da cidade, Araj, o Evocador reina como o senhor de Andorhal. Sabemos que ele era o líder da Scolomântia antes de tomar a cidade. Para terminar a chave, precisaremos do sinete dele, o chamado escaravelho.$B$BAgora que ele foi atraído para campo aberto, siga para o âmago da cidade e rompa o jugo de Araj sob a cidade. Depois de matá-lo, pegue o escaravelho no filactério dele!$B$BA Chave para Scolomântia está à sua espera, $N!",
    ["O"] = "Destrua Araj, o Evocador e leve o Escaravelho de Araj para o Boticário Trépido no Baluarte, que fica nas Terras Pestilentas Ocidentais.",
    ["T"] = "O escaravelho de Araj",
  },
  [5805] = {
    ["D"] = "$GBem-vindo:Bem-vinda; ao World of Warcraft!$B$BComo agradecimento especial pela compra do World of Warcraft Collector\'s Edition, você receberá um presente. Entregue este vale-brinde para Melissa Calmafonte, no Vale de Vila Norte, e ganhe uma mascote que vai $gacompanhá-lo:acompanhá-la; em sua busca por aventura e glória.$B$BObrigado novamente e aproveite sua estadia no World of Warcraft!",
    ["O"] = "Leve o Vale-brinde da Vila Norte para Melissa Calmafonte.",
    ["T"] = "Boas-vindas!",
  },
  [5821] = {
    ["D"] = "Em circunstâncias normais, eu e o Armador passaríamos com os kodos pela aldeia dos centauros Kolkar. De vez em quando dá para ouvir os tambores de guerra deles, e quando isso acontece a gente volta. Morrer não é nada bom para os negócios!$B$BO lance é o seguinte, $c: se você nos ajudar a passar pela aldeia Kolkar, garantimos que a recompensa será à altura.$B$BÉ um percurso pequeno, e basta você proteger a caravana para receber a recompensa do Smid Rabiscafuso, que está mais à frente, na estrada. A gente é sócio em uns negocinhos.",
    ["O"] = "Escolte a Caravana Gizelton pela aldeia dos Centauros Kolkar. Fale com o Smid no Acampamento do Rabiscafuso para receber a recompensa.",
    ["T"] = "Procura-se guarda-costas ",
  },
  [5841] = {
    ["D"] = "$GBem-vindo:Bem-vinda; ao World of Warcraft!$B$BComo agradecimento especial pela compra do World of Warcraft Collector\'s Edition, você receberá um presente. Entregue este vale-brinde para Yori Rachelmo, em Vale Cristálgida, e ganhe uma mascote que vai acompanhá-l$go:a; em sua busca por aventura e glória.$B$BObrigado novamente e aproveite sua estadia no World of Warcraft!",
