    ["D"] = "",
    ["O"] = "",
    ["T"] = "Restoring Fiery Flux Supplies via Kingsblood",
  },
  [7737] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Gaining Acceptance",
  },
  [7741] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Praise from the Emerald Circle <NYI> <TXT>",
