  [8149] = {
    ["D"] = "During the Harvest Festival of Azeroth, we give thanks to heroes for the sacrifices - in some cases ultimate sacrifices - they have given on our behalf.  The Alliance acknowledges many heroes, but none weave a more tragic tale than that of Uther Lightbringer.$B$BHelp us honor Uther by taking this offering to his tomb in the Western Plaguelands.  Place it at his statue, and then return to me.  I\'ll only be here through the end of the festival, so waste no time in giving Uther his tribute.",
    ["O"] = "Travel to Uther\'s Tomb in Western Plaguelands and use Uther\'s Tribute at his statue, and then return to Wagner Hammerstrike outside Ironforge before the Harvest Festival is over.",
    ["T"] = "Honoring a Hero",
  },
  [8166] = {
    ["D"] = "$N, the battle for Arathi Basin is a battle for resources and territory.  Key points in the area must be held and operated in order to supply our forces with much needed raw materials and supplies.  I charge you to assault the points farthest from our Arathi Basin base.$B$BAssault the farm, the mine, the lumber mill and the blacksmith when they are controlled by the enemy.  Find the enemy\'s banner and rip it from its flag post!$B$BWhen you have done this to all four points, return to me.",
    ["O"] = "Assault the mine, the lumber mill, the blacksmith and the farm, then return to Field Marshal Oslight in Refuge Pointe.",
    ["T"] = "The Battle for Arathi Basin!",
  },
  [8167] = {
    ["D"] = "$N, the battle for Arathi Basin is a battle for resources and territory.  Key points in the area must be held and operated in order to supply our forces with much needed raw materials and supplies.  I charge you to assault the points farthest from our Arathi Basin base.$B$BAssault the farm, the mine, the lumber mill and the blacksmith when they are controlled by the enemy.  Find the enemy\'s banner and rip it from its flag post!$B$BWhen you have done this to all four points, return to me.",
    ["O"] = "Assault the mine, the lumber mill, the blacksmith and the farm, then return to Field Marshal Oslight in Refuge Pointe.",
    ["T"] = "The Battle for Arathi Basin!",
  },
  [8170] = {
    ["D"] = "As you no doubt have heard, the League of Arathor is sending large numbers of troops into Arathi Basin.  Eager for food and supplies, these Alliance fools are intent to take and control the rich resources there.  We must show them that Arathi will never again be a home for humans!$B$BGo to Arathi Basin and assault the mine, the lumber mill, the blacksmith and the stables.  Pull down the enemies\' banners, declaring those territories for the Horde.$B$BGo, $N.  Report back to me when this task is complete.",
    ["O"] = "Assault the Arathi Basin mine, lumber mill, blacksmith and stable, and then return to Deathmaster Dwire in Hammerfall.",
    ["T"] = "The Battle for Arathi Basin!",
  },
  [8182] = {
    ["D"] = "$N, our worst fears have come.  Legend speaks of the Soulflayer ruling the ancient troll world through evil and terror, and his power was absolute.  If he cannot be stopped now, then his new reign may spread across all of Azeroth!$B$BThere is a contingent of Zandalarian trolls who may help us stem this tide of darkness.  They have camped on Yojamba Isle, off the coast of Stranglethorn, west of the ruins of Zul\'Kunda.  Speak with their leader, Molthor, and pray he knows how to defeat Hakkar.",
    ["O"] = "Speak with Molthor in Stranglethorn.",
    ["T"] = "The Hand of Rastakhan",
  },
  [8183] = {
    ["D"] = "The Heart of Hakkar burns from within.  Although the Soulflayer is defeated, the kernel of his power yet remains in this cursed organ.",
    ["O"] = "Bring the Heart of Hakkar to Molthor on Yojamba Isle.",
    ["T"] = "The Heart of Hakkar",
  },
  [8184] = {
