  [12492] = {
    ["D"] = "Coren Direbrew\'s corpse clutched this small cask tightly to its gut. This must be his infamous Dire Brew!",
    ["O"] = "Give Direbrew\'s Dire Brew to Tapper Swindlekeg at the Brewfest camp near Orgrimmar.",
